INSERT INTO `recipe` VALUES (952, 101, '蛋花丝瓜汤', '(1)丝瓜洗净，去皮切片；香菜择洗干净切段。\r\n(2)油锅烧热，放入葱花炝锅，下丝瓜煸炒，加适量清水、盐煮沸后，倒入蛋花液，加香菜、味精即成。', NULL, NULL, '味精,盐,植物油', '蛋黄1个30克,丝瓜100克');
INSERT INTO `recipe` VALUES (953, 101, '馄饨', '(1)虾仁洗净控干水，剁碎，加葱、姜、盐、味精、淀粉浆、蛋清、料酒、植物油搅打成馅。\r\n(2)取皮包制馄饨，煮熟。\r\n(3)碗中置虾皮、紫菜末，连汤盛入馄饨。', NULL, NULL, '盐,味精,淀粉,料酒,植物油', '馄饨皮6个50克,鲜虾仁80克,蛋清30克');
INSERT INTO `recipe` VALUES (954, 101, '海蜇汤菜', '(1)黄瓜洗净，切片；熟炎腿切片。\r\n(2)海蜇以沸水泡发后洗涤3遍，待其软嫩后，放凉开水中浸泡；再以烧沸鸡汤浸烫后捞出，盛入碗中。\r\n(3)将鸡蛋和蛋清搅打，加约100克鸡汤、盐搅匀，上锅蒸熟取出。\r\n(4)鸡汤加盐味精烧沸后倒入放海蜇的碗中，用勺将蒸好的鸡蛋一勺勺舀入碗中，加熟火腿片和黄瓜片即成。', NULL, NULL, '味精,盐,鸡汤', '海蜇300克,鸡蛋1个80克');
INSERT INTO `recipe` VALUES (955, 101, '三色鲜贝', '(1)油菜叶择洗干净，入沸水锅中略焯，待用。\r\n(2)鲜贝洗净，一半切片，一半放碗中捣成泥状，加蛋清、盐搅匀后制成小丸子。\r\n(3)油锅烧热，将鲜贝片入锅，炸好捞出。\r\n(4)锅内留少许油，加料酒、水、盐、白糖、番茄酱、味精烧开，投入鲜贝丸子烧至入味，盛盘中。\r\n(5)另起油锅烧热，入鲜贝片，加盐、味精，烧至入味后盛在鲜贝丸子周围，放油菜叶点缀即成。', NULL, NULL, '白糖,味精,淀粉,醋,植物油', '鲜贝150克,蛋清1个30克,油菜叶50克');
INSERT INTO `recipe` VALUES (956, 101, '夹馅黄瓜', '(1)黄瓜洗净，切成5厘米长的段，每段上切一十字花刀（两端不切断），在盐水中浸1小时。\r\n(2)韭菜洗净切段；火腿肠切丁。\r\n(3)将葱、姜、蒜、韭菜、火腿肠、白糖、盐等拌匀做馅。\r\n(4)黄瓜挤去水，将馅塞进行以手压紧、放在容器中，将容器以塑料膜封好，15℃以下放置冰箱中1-2天后即可食用。食用时撕成长条。', NULL, NULL, '盐,白糖', '黄瓜300克,韭菜60克,火腿肠50克');
INSERT INTO `recipe` VALUES (957, 101, '萝卜丝糕', '(1)萝卜洗净，擦成细丝，放适量盐稍腌，挤去部分水份。\r\n(2)面粉放盘中，加葱、姜、萝卜丝、盐、味精搅成糊。\r\n(3)酒盅数个，内壁抹油，将萝卜丝面糊置酒盅里抹平，放锅中稍蒸后扣出成萝卜面糕。\r\n(4)油锅烧热，下萝卜面糕，炸黄即可。', NULL, NULL, '味精,香油', '面粉500克,萝卜600克');
INSERT INTO `recipe` VALUES (958, 101, '鸽蛋龙须面', '(1)蔬菜择洗干净，生菜切小块，香菜切末。\r\n(2)油锅烧热，入葱花炒香，加适量水，放海米烧开，下龙须面，磕入鸽蛋，快熟时下生菜、盐、味精，淋少许醋，撒得菜末即可。', NULL, NULL, '醋,味精,盐', '鸽蛋6个40克,龙须面200克,生菜100克,海米20克,香菜20克');
INSERT INTO `recipe` VALUES (959, 101, '熘黄瓜', '(1)蔬菜择洗干净，黄瓜、胡萝卜、香菇切片，葱、姜切丝，蒜有刀拍碎切末。\r\n(2)油锅烧至七成熟，放入葱、姜、蒜煸炒出味，下香菇、胡萝卜、黄瓜片煸炒，加酱油、糖、盐炒匀，以湿淀粉勾芡，淋香油即可。', NULL, NULL, '白糖,淀粉,酱油,盐,植物油', '嫩黄瓜300克');
INSERT INTO `recipe` VALUES (960, 101, '熘莲鱼丁', '(1)以葱、姜、盐、湿淀粉和适量水调成滋味汁。\r\n(2)鱼剖洗干净剔去骨刺切成丁，用蛋清、料酒、盐、湿淀粉浆好。\r\n(3)油锅烧至六成热，下鱼丁、莲子滑炒，倒入调好的汁翻炒至熟，淋香油即可。\r\n注：如无鱼莲子可用干莲子上锅蒸熟后代替。', '软嫩清香', NULL, '湿淀粉,料酒,香油,盐', '鲜莲子200克,青鱼400克');
INSERT INTO `recipe` VALUES (961, 101, '香菇烧茭白', '(1)蔬菜择洗干净，均切片。\r\n(2)油锅烧至五成热，先下茭白、柿子椒滑炒后盛出。\r\n(3)再起油锅烧热，下葱姜炒香，先放入香菇略炒，再倒入滑炒后的茭白、柿子椒炒匀，放入料酒、糖、盐烧开，以水淀粉勾芡即成。', '清淡适口', NULL, '料酒,白糖,盐,植物油', '香菇100克,茭白400克');
INSERT INTO `recipe` VALUES (962, 101, '莼菜汤', '(1)火腿切丝。\r\n(2)莼菜洗净。\r\n(3)锅内加适量清水烧沸，加莼菜略氽捞出，控去水。\r\n(4)锅内放入适量水烧开，加火腿丝、盐、莼菜、味精即可。', '滑嫩清香,色彩鲜明', NULL, '味精,盐', '莼菜100克');
INSERT INTO `recipe` VALUES (963, 101, '炸鸭脯', '(1)蛋清搅打至起泡，加淀粉、面粉搅成蛋清糊。\r\n(2)鸭脯肉切片，以料酒、酱油、盐、葱、姜丝拌匀腌制一小时。\r\n(3)油锅烧热，将鸭脯肉挂蛋糊后逐片入锅炸熟，捞出控油盛盘，食时蘸以少许甜面酱。', NULL, NULL, '酱油,料酒,盐,甜面酱,植物油', '鸭脯肉150克');
INSERT INTO `recipe` VALUES (964, 101, '三丝炒豆芽', '(1)蔬菜择洗干净，柿子椒切细丝。\r\n(2)水锅烧沸，下绿豆芽稍氽，捞出控水。\r\n(3)油锅烧热，煸炒葱姜出味，下柿子椒丝炒匀，放入豆芽，加糖、盐翻炒至熟，淋香油即成。', NULL, NULL, '白糖,盐,香油', '青红黄柿子90克,绿豆芽250克');
INSERT INTO `recipe` VALUES (966, 101, '皮蛋瘦肉粥', '(1)肉洗净，用盐将表面涂匀，淹渍过夜。\r\n(2)皮蛋用壳切细丁，腐竹洗净切细丁。\r\n(3)大米洗净用适量盐、油20克拌成油盐米。\r\n(4)锅中置水烧沸，下油盐米、腐竹丁，搅煮20分钟，再放入洗净的咸肉、一半皮蛋丁、麦片和余下的油煮15分钟，改用微火再煮1小时。\r\n(5)捞出肉，撕成肉丝放回锅中，再倒入剩余的皮蛋丁煮沸，加精盐、味精即可。', '绵糯可火，咸香味美', NULL, '盐,味精,植物油30克', '皮蛋3个240克,瘦肉250克,水发腐竹80克,大米180克,麦片25克');
INSERT INTO `recipe` VALUES (967, 101, '枣卷', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (968, 101, '珍珠丸子', '(1)糯米用水浸泡5小时，用前控水。\r\n(2)黑木耳泡发后洗净剁碎。\r\n(3)将肉馅、黑木耳、鸡蛋及各种调料搅打成馅。\r\n(4)将馅做成丸子，在丸子外面滚一层浸泡好的糯米，置盘中，放锅中隔水蒸25分钟即可。', '粘糯鲜香', NULL, '黄酒,酱油,葱,姜,盐,味精', '猪肉馅250克,黑木耳25克,鸡蛋1个80克,糯米120克,淀粉25克');
INSERT INTO `recipe` VALUES (969, 101, '香菇豆腐汤', '(1)豆腐捣碎，加盐、蛋清搅拌均匀；取碗一个，在碗内涂一层食油，放入豆腐。\r\n(2)各样蔬菜择洗干净切丁，摆于豆腐之上，上锅隔水蒸10分钟。\r\n(3)用适量的水烧开，取出蒸好的豆腐，轻置于水中，加葱、姜末、盐、味精烧沸即可。', '色味俱佳', NULL, '盐,味精,植物油', '鲜豆腐300克,香菇90克,黑木耳20克,西红柿50克,黄瓜30克,蛋清1个30克');
INSERT INTO `recipe` VALUES (970, 101, '绿豆饭', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (971, 101, '蒸茄条', '(1)茄子去蒂洗净，上锅隔水蒸熟，晾凉后撕成细条摆盘中。\r\n(2)炒锅放适量油烧热，将熟油倒入碗中，加入酱油、香油、糖、味精搅匀，做成调料汁。\r\n(3)炒锅放适量油烧热，甜面酱放油锅中翻炒出香味，盛于碟中。\r\n(4)食用时用茄条蘸酱、汁即可。', '软烂清口', NULL, '甜面酱,植物油,糖,味精', '长茄子150克');
INSERT INTO `recipe` VALUES (972, 101, '糖醋鱿鱼', '(1)鱿鱼撕去皮、膜，去骨，洗净劈开，先切花刀，再切块。\r\n(2)黄瓜洗净切象眼片。\r\n(3)木耳泡发后去泥沙，洗净撕成小朵。\r\n(4)将料酒、醋、糖、盐、味精、湿淀粉调成芡汁。\r\n(5)油锅炒至七成热，下鱿鱼块，待鱿鱼卷起，捞出沥油。\r\n(6)将油锅留少量底油，放入葱、姜丝煸炒出味，再将鱿鱼、木耳、黄瓜下锅，倒入芡汁，翻炒均匀即可。', '滑嫩清口', NULL, '料酒,醋,糖,盐,味精,植物油', '鱿鱼300克');
INSERT INTO `recipe` VALUES (973, 101, '荠菜蛋花汤', '(1)荠菜洗净切碎，鸡蛋磕入碗中打匀。\r\n(2)水锅煮沸，下荠菜再煮沸。\r\n(3)倒入蛋液，加盐、味精即可。', '清淡爽口', NULL, '香油,味精,盐', '荠菜50克,鸡蛋1个70克');
INSERT INTO `recipe` VALUES (974, 101, '烤茄子', '(1)茄子在火上烤至皮焦、内熟，剥皮后放小盆中。\r\n(2)将茄肉搅成泥，加葱、姜、蒜末、香菜末、酱油、醋、香油拌匀即成。', NULL, NULL, '盐,味精,酱油,醋,香油', '茄子300克');
INSERT INTO `recipe` VALUES (975, 101, '鸡丝炒豇豆', '(1)豇豆择洗干净，切寸段。\r\n(2)鸡肉洗净切丝。\r\n(3)油锅烧热，煸炒葱姜，下鸡肉炒至发白，下豇豆，加盐炒熟后，淋香油，加味精翻匀即成。', NULL, NULL, '盐,味精,植物油,香油', '鸡脯肉50克,豇豆300克');
INSERT INTO `recipe` VALUES (976, 101, '鸽蛋银耳羹', '(1)银耳用开水泡发，除去根蒂，洗净撕成小瓣。\r\n(2)红枣洗净去核。\r\n(3)鸽蛋煮熟去皮。\r\n(4)沙锅中加适量水，先放入银耳煮沸，加枣、糖，中火将银耳煮软烂，再加入鸽蛋煮片刻即可。', NULL, NULL, NULL, '鸽蛋9个80克');
INSERT INTO `recipe` VALUES (977, 101, '香芹腐竹', '(1)将发好的腐竹入沸水略焯，捞出待凉后切斜条，以少许盐稍渍。\r\n(2)芹菜择洗干净，切斜丝，入沸入略焯，以冷开水过凉，控水，以少许盐腌片刻。\r\n(3)将芹菜、腐竹用盐、香油拌匀即成。', NULL, NULL, '盐,香油', '嫩芹菜300克,水发腐竹50克');
INSERT INTO `recipe` VALUES (978, 101, '素炒彩丁', '(1)蔬菜择洗干净，蕃茄去皮、籽；胡萝卜去根；土豆去皮；黄瓜去尾部；洋葱去干皮；皆切为小丁。\r\n(2)油锅烧热，下葱、姜丝炒香，放入土豆丁煎黄，依次下洋葱、胡萝卜、蕃茄，加糖、醋、盐及少许水，开后下黄瓜丁，翻炒均匀，以淀粉勾芡，装盘即可。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (979, 101, '香菇鸡肝汤', '(1)鸡肝洗净切片。\r\n(2)香菇洗净泥沙，去蒂切丝。\r\n(3)水锅烧开，依次下鸡肝、料酒、香菇烧开，加盐，撒葱末、淋香油即成。', NULL, NULL, '料酒,香油', '鸡肝200克,泡发香菇100克');
INSERT INTO `recipe` VALUES (980, 101, '麻酱米粉', '(1)以香油、盐、味精调制麻酱。\r\n(2)水锅烧沸，煮熟炎粉，盛碗中，浇芝麻酱拌交，撒葱花即可。', NULL, NULL, '麻酱,味精,盐', '宽条米粉350克');
INSERT INTO `recipe` VALUES (981, 101, '五香兔肉', '(1)用葱、姜、八角、桂皮、花椒及适量水熬制五香水。\r\n(2)兔肉洗净切大块，放入五香水中腌制过夜。\r\n(3)将兔肉从五香水中捞出，再用酱油拌匀。\r\n(4)油锅烧热，放兔肉炸黄，捞出控油。\r\n(5)将兔肉放砂锅中，加水浸没，加粮、盐、花椒、大料、葱、姜、酱油、料酒，大火煮沸后改小火炖约一小时，以中火收汤，捞出后淋香油，切片盛盘即成。', NULL, NULL, '盐,花椒,八角,桂皮,白糖,料酒,香油,植物油', '兔肉600克');
INSERT INTO `recipe` VALUES (982, 101, '青豆茄片', '(1)青豆洗净；茄子洗净，去蒂、皮，切片。\r\n(2)水锅烧开，加适量盐，放入青豆煮熟。\r\n(3)油锅烧热，炒香葱、姜、蒜末，下茄片炒黄，加料酒，把茄片拨向锅边放入甜面酱、青豆]白糖、盐，翻炒均匀即成。', NULL, NULL, '白糖,盐,甜面酱,料酒,植物油', '长茄子500克,鲜青豆150克');
INSERT INTO `recipe` VALUES (983, 101, '乌梅枣粥', '(1)锅中加适量水，下乌梅煮开，再用水火煮20分钟。\r\n(2)大枣洗净去核，与淘好的米一起放入乌梅水中，煮至米烂，加白糖即可。', NULL, NULL, '白糖', '乌梅10克,大枣15克,大米50克');
INSERT INTO `recipe` VALUES (984, 101, '排骨冻', '(1)肉皮去毛洗净，切块。\r\n(2)排骨洗净剁成小段。\r\n(3)将肉皮、排骨放水锅中，加葱、姜、八角、桂皮煮至七成熟，再加白糖、盐、酱油煮熟，将排骨捞至碗中。\r\n(4)将八角、桂皮、葱、姜、肉皮捞出，把汤滤至盛排骨的碗中，上屉蒸15分钟取出，晾成冻即可。', NULL, NULL, '八角,桂皮,白糖,盐,酱油', '猪肉皮100克,猪排骨500克');
INSERT INTO `recipe` VALUES (985, 101, '拌茭白', '(1)茭白洗净去皮，用刀拍松。\r\n(2)将茭白入沸入中焯10分钟合捞出，冷却后撕成条，放盘中加各种调料拌匀即可。', NULL, NULL, '白糖,酱油,香油', '嫩茭白250克');
INSERT INTO `recipe` VALUES (986, 101, '芝麻肉条', '(1)猪肉洗净切条，加葱姜末、料酒、盐腌渍两小时。\r\n(2)将肉条拍上面粉，沾上鸡蛋液，沾芝麻。\r\n(3)油锅烧至七成热，下肉条炸至金黄色熟透即成。', NULL, NULL, '姜,盐,料酒,植物油', '猪肉250克,鸡蛋3个250克,面粉60克,芝麻120克');
INSERT INTO `recipe` VALUES (987, 101, '烩鸡翅', '(1)将鸡翅洗净，清炖至熟，拆去骨，将翅肉切段。\r\n(2)蔬菜择洗干净，油菜去根剖开，香菇切片。\r\n(3)油锅烧热，煸炒油菜心至断生，盛出。\r\n(4)锅中加适量鸡汤，放鸡翅段，料酒、香菇片、油菜心、盐烧开，以淀粉浆勾芡即成。', NULL, NULL, '淀粉,盐,料酒,植物油', '鸡翅15个400克');
INSERT INTO `recipe` VALUES (988, 101, '西红柿生菜汤', '(1)西红柿洗净，去皮切块；生菜叶洗净撕成块。\r\n(2)豆腐洗净切片。\r\n(3)鸡汤中下葱姜丝、豆腐煮沸，撇去浮沫，下生菜、西红柿、盐、胡椒粉、醋搅匀即可。', NULL, NULL, '胡椒粉,盐,醋,鸡汤', '西红柿3个300克,生菜100克,豆腐50克');
INSERT INTO `recipe` VALUES (989, 101, '鳜鱼卷', '(1)蔬菜择洗干净，玉兰片、香菇、胡萝卜切丝，以适量盐、料酒拌匀。\r\n(2)用蛋清、湿淀粉、盐、料酒制芡浆。\r\n(3)鳜鱼剖洗干净，分成两片，剔去骨刺，将带皮的一面贴菜板，片连刀片（一刀断皮，一刀不断皮），用制好的浆浆好。\r\n(4)在浆好的鱼片中，放入蔬菜丝、葱姜丝卷成鱼卷，放盘中上屉蒸十五分钟取出。\r\n(5)油锅烧热，放入蕃茄沙司炒透，浇在鱼卷上，将生菜和小西红柿点缀在盘边上即成。', NULL, NULL, '盐,淀粉,蕃茄沙司,料酒,植物油', '鳜鱼1条400克');
INSERT INTO `recipe` VALUES (990, 101, '素炒花丁', '(1)蔬菜择洗干净，菜花掰成小朵，其余切丁。\r\n(2)油锅烧热，先将土豆丁煎至微黄，再下其余蔬菜，加料酒、盐翻炒匀即成。', NULL, NULL, '盐,料酒,植物油', '花菜300克,莴苣50克,胡萝卜50克,土豆50克');
INSERT INTO `recipe` VALUES (991, 101, '夹馅面筋', '(1)白菜叶洗净，撕成块。\r\n(2)猪肉洗净剁成茸，加葱、姜末、料酒、酱油、白糖、淀粉拌匀成馅。\r\n(3)将面筋泡开个口，把里面捣碎，将制好的肉馅塞入其中。\r\n(4)烧热油锅，将塞好肉馅的面筋开口朝下略煎，再加盐和适量水烧10分钏，下白菜叶，烧至菜叶入味即成。', '软嫩味鲜', NULL, '白糖,酱油,淀粉,料酒,盐', '面筋泡15个300克,猪肉300克,白菜叶150克');
INSERT INTO `recipe` VALUES (992, 101, '拌胡萝卜丝', '(1)蔬菜择洗干净，萝卜去皮切细丝，加少量盐腌渍水分。\r\n(2)蒜苗切细丝，香菜切段。\r\n(3)将腌过的胡萝卜以清水漂洗控去水份，加入蒜苗、香菜、盐、糖、醋、香油拌匀即成。', '色泽美观', NULL, '白糖,醋,盐,香油', '胡萝卜150克,蒜苗10克');
INSERT INTO `recipe` VALUES (993, 101, '虾仁烩碗豆', '(1)鲜碗豆洗净，控干水。\r\n(2)虾仁洗净，放入六成热的温油锅里划散，捞出控油。\r\n(3)别起油锅烧热，煸炒葱闰，下碗豆、虾仁炒交，加盐和少许水，待碗豆熟后，以湿粉勾芡即可。', '滑嫩可口', NULL, '淀粉,盐,植物油', '鲜碗豆200克,虾仁80克');
INSERT INTO `recipe` VALUES (994, 101, '三丝汤', '(1)油锅烧热，将打匀的蛋共同液倒入锅中摊成蛋皮，取出切丝。\r\n(2)熟火腿切丝。\r\n(3)西红柿洗净去皮、籽切丝。\r\n(4)以葱花炝锅，放入适量水下三丝烧开，加盐、紫菜即可。', NULL, NULL, '盐', '熟火腿30克,蛋黄4个150克');
INSERT INTO `recipe` VALUES (995, 101, '香肠焖饭', '(1)土豆洗净，去皮，切小丁。\r\n(2)香肠切圆片。\r\n(3)大米淘洗干净，加入土豆丁与适量水，以均匀火力焖至快熟时加入香肠片焖熟即可。', NULL, NULL, NULL, '香肠1根100克,土豆1个80克,大米200克');
INSERT INTO `recipe` VALUES (996, 101, '锅贴', '(1)以温水和面，放置待醒。\r\n(2)白菜洗净剁碎。\r\n(3)猪肉洗净剁成末，虾切成小丁。\r\n(4)将剁碎的白菜猪肉末、虾肉丁放盆中，加葱姜末、盐、酱油、料酒，搅打成馅。\r\n(5)将醒好的面制成锅贴皮，包入馅后将中间捏严，两头稍捏。\r\n(6)平底油锅烧热，摆好锅贴，加入适量油水（油：水=1：10）盖盖，听里面响时，开盖再浇此油水，盖盖焖熟即可。', '上部软嫩,下部焦脆,其中鲜香', NULL, '料酒,酱油,盐,香油,植物油', '面粉250克,猪肉150克,虾肉80克,白菜80克,鸡蛋1个80克');
INSERT INTO `recipe` VALUES (997, 101, '生菜豆腐汤', '(1)豆腐以沸水略汆，切成条；海米以开水加少量料酒浸发。\r\n(2)生菜洗净切条。\r\n(3)油锅烧热，煸炒葱、姜出味后加入，下豆腐及海米煮沸，下生菜、盐、醋即可。', NULL, NULL, '醋,盐', '生菜150克,豆腐150克,海米5克');
INSERT INTO `recipe` VALUES (998, 101, '炸藕夹', '(1)猪肉洗净剁成茸，与洗净切好的香菇丁、葱姜末、盐、白糖、料酒搅与馅。\r\n(2)藕去皮洗净，切成每两片之间相连的连刀片。\r\n(3)用鸡蛋、面粉、适量水调成蛋糊。\r\n(4)油锅烧热，下藕夹炸至金黄色即可。', '色泽金黄,外焦里嫩', NULL, '料酒,白糖,盐', '鲜藕300克,猪肉150克,香菇59克,鸡蛋1个70克,面粉100克');
INSERT INTO `recipe` VALUES (999, 101, '肉末烧菜花', '(1)肉洗净剁成末；菜花洗净切成小朵，入开水锅略烫，捞出控水。\r\n(2)油锅烧热，先放入葱姜末煸出香味，放肉末炒散，加酱油、料酒炒匀，入菜花同炒，加盐炒透即成。', '鲜香味美', NULL, '酱油,盐,料酒,葱,姜,植物油', '瘦猪肉100克,菜花400克');
INSERT INTO `recipe` VALUES (1000, 101, '五彩鸡肉卷', '(1)用鸡蛋清、面粉、水、盐制成蛋糊。\r\n(2)胡萝卜洗净切丁；嫩豌豆洗净。\r\n(3)咸鸭蛋黄、山楂糕切丁。\r\n(4)鸡胸肉洗净切片，将松仁铺在鸡肉片上，卷也卷儿，挂上蛋糊，下六成热油锅炸成黄色，捞出控油。\r\n(5)别起油锅烧热，煸炒豌豆、胡萝卜丁和鸭蛋黄丁，加盐翻炒出锅，放在鸡肉卷周围，撒上山楂糕丁即可。', NULL, NULL, '盐,植物油', '鸡胸肉100克,松仁30克,咸鸭蛋黄140克,鸡蛋清1个30克,嫩豌豆,胡萝卜半根');
INSERT INTO `recipe` VALUES (1001, 101, '烧三冬', '(1)虾仁洗净。\r\n(2)冬菇洗净去蒂；冬笋去皮洗净切块，入沸水锅中焯透捞出；冬瓜洗净去皮、瓤切块。\r\n(3)油锅烧至七成热，先放入虾仁滑炒，再下冬笋、冬菇翻炒，加盐、糖、料酒和适量水烧开，下冬瓜烧至软件烂，起锅时以湿淀粉勾芡，淋香油即可。', NULL, NULL, '盐,糖,香油,湿淀粉,料酒,植物油', '冬菇150克,冬笋100克,冬瓜100克,虾仁50克');
INSERT INTO `recipe` VALUES (1002, 101, '芦笋海带汤', '(1)海带丝洗净。\r\n(2)芦笋洗净切丝。\r\n(3)水锅烧开，下芦笋、海带丝，加料酒、酱油、盐、香油适量。', '清鲜适口', NULL, '酱油,盐,料酒,香油', '芦笋100克,海带丝100克');
INSERT INTO `recipe` VALUES (1003, 101, '三鲜水饺', '(1)面粉加水和成面稍醒。\r\n(2)藕洗净去皮，切片，用开水焯透，控水剁碎。\r\n(3)香菇洗净去蒂，剁碎。\r\n(4)猪肉洗净剁碎，加酱油、葱、姜末搅匀，加入藕末、香菇末、盐、香油，搅成馅。\r\n(5)取醒好的面制成饺子皮，包好饺子。\r\n(6)沸水加少许盐，煮熟饺子。', NULL, NULL, '酱油,盐,香油', '嫩鲜藕200克,香菇100克,猪肉100克,鸡蛋3个180克');
INSERT INTO `recipe` VALUES (1004, 65, '白果烧鸡', '1.将白果去壳、心芽，炒锅置旺火上，下猪油烧至六成热，放白果炸至进皮时捞起。胡萝卜切成小梅花状，焯熟。 \r\n2.净鸡放入沸水煮熟后捞起，置清水中漂洗干净。\r\n3.清汤加姜、葱、料酒、胡椒粉和鸡，烧沸换微火，鸡烧至八成熟时加白果和精盐，白果烧烂时将鸡盛入盘中，将白果围于鸡的周围；锅内汤汁置旺火上，捞去姜葱，勾入湿淀粉，汤浓下味精，鸡油和匀起锅，浇在鸡身上，胡萝卜小梅花置鸡周围即成。', '味醇鲜，鸡肉细嫩，白果回甜。', NULL, NULL, '净仔母鸡1250克，白果250克，清汤750克，猪化油500克，鸡化油20克，姜、大蒜、料酒各50克，胡椒粉、胡萝卜各2克，盐5克，味精1克，湿淀粉25克。');
INSERT INTO `recipe` VALUES (1005, 87, '扁豆沙拉', '1.将扁豆去筋洗净，切成菱形，放入滚沸水肉烫熟（水内放少许碱可优化绿色），控干。\r\n2.将洋葱洗净，切成末。\r\n3.待扁豆凉后放上洋葱末、生菜油、醋精、胡椒粉、盐拌匀即可。', NULL, NULL, NULL, '扁豆350克、洋葱40克、精盐适量，生菜油20克、胡椒粉少许。');
INSERT INTO `recipe` VALUES (1006, 87, '玻璃蛋糕', '1.蛋黄加砂糖搅松，加入玉米淀粉和吉士粉搅匀，冲入热牛奶及琼脂液（水:琼脂=5:3:0.1)。在火上加热至糊状，趁热使用。 \r\n2.将需要大小的糕胚圈洗净，四周围一圈玻璃纸，嵌入一层S.P海绵蛋糕胚，倒入热的奶黄淇淋裱菱形网络。然后将水:琼脂:砂糖=4:0.1:1比例熬煮成透明琼液，待稍冷倒于表面。 \r\n3.凝固后脱模，用水果装饰。', NULL, NULL, NULL, '牛奶、蛋黄、砂糖、玉米淀粉、白脱、琼脂、吉士粉。制奶黄淇淋材料比例：牛奶:蛋黄:砂糖:玉米淀粉:白脱:琼脂:吉士粉=6:3:2:1:0.5:0.04:0.02。糕胚:奶黄淇淋:透明琼脂=2:5:3。');
INSERT INTO `recipe` VALUES (1007, 87, '菠菜色拉', '1.制作脆面包片：将烤箱烧至200度（煤气炉6度）。用面包刀将法国长面包横切成5毫米的薄块。在面包的两面涂上橄榄油，然后放在烤板上，放入烤箱内烘7-10分钟，直至面包变脆及呈金黄色。在烤脆的过程中，要将面包反转一次。用厨刀身轻压蒜头，去皮后将蒜头切为两份。将蒜头的表面在烘脆的面包片上涂擦，然后将蒜头弃掉，面包放在一边待用。\r\n2.准备色拉调料：将鸡蛋煮熟去壳。在煮鸡蛋的同时，摘去菠菜上的硬梗和主脉，以冷水冲洗菠菜。将菠菜叶撕成大片，然后放在毛巾上或以色拉滤干器弄开。轻轻将蛋白取出切碎，将蛋黄放在筛子中以大', NULL, '待煎锅稍为冷却后才放入陈醋，以避免陈醋溅出。将锅中的油醋汁液淋在菠菜及熏肉上，然后均匀地搅拌。', NULL, '鸡蛋2个，新鲜菠菜500克，熏肉250克，陈醋75毫升；脆面包1/2条，法国长面包，橄榄油3汤匙，蒜头一粒。');
INSERT INTO `recipe` VALUES (1008, 87, '菠萝蛋糕', '1.砂糖加液体葡萄糖和水加热至118度，拌入杏仁粉，迅速搅进蛋白，揉成柔软的杏仁糖团。 \r\n2.糕胚用S.P海绵蛋糕修成菠萝形。 \r\n3.用白脱油调成菠萝黄色，0.4cm左右的花齿裱花头裱不规则点于表面，尖头用巧克力白脱油点上。杏仁糖团掺少量绿色素制叶子装饰。 \r\n4.将糕胚去皮，横剖三层，中间嵌白脱油，顶面及垂直边刮抹白脱油。四周用杏仁糖团搓成的长条围边一圈，用花钳钳花，中央放上菠萝，裱花装饰。糕胚:白脱油=4:6。', NULL, NULL, NULL, '砂糖、液体葡萄糖、蛋白、水、杏仁粉、白脱油。制杏仁糖团：砂糖:液体葡萄糖:蛋白:水:杏仁粉=1:0.15:0.15:0.3:0.6。');
INSERT INTO `recipe` VALUES (1009, 87, '菠萝酥', '1.用清酥面团制底胚修成菠萝形，余料作为叶子装饰。 \r\n2.菠萝加水、糖煮沸、放入白脱，搅拌至白脱熔化，用水淀粉钩芡。 \r\n3.成型：在菠萝形底胚上放入菠萝馅，用酥面条贴成网络状格，边缘捏紧，装上叶，表面刷蛋。 \r\n4.烘烤：炉温220℃，约20分钟左右，表面金黄。', NULL, NULL, NULL, '菠萝、糖、白脱、淀粉。净制菠萝馅：菠萝丁:糖:白脱:淀粉=1:0.2:0.5:0.05。');
INSERT INTO `recipe` VALUES (1010, 87, '菠萝珍宝饭', '1.香菇切丝，素火腿切丁，比其压碎，将这些材料和豌豆、玉米粒混合。\r\n2.用橄榄油热锅，放入咖哩及刚刚混合好的配料，炒好后放入糯米饭炒均匀最后放入切成丁的菠萝炒匀后，淋上椰子乳提味，起锅放入挖空的菠萝中，同时用竹签刺穿菠萝皮，放入烤箱烤约12分钟，完成。', NULL, NULL, NULL, '菠萝红、萝卜、糯米饭、玉米粒、香菇、比其、素火腿、豌豆、椰子乳、橄榄油、盐、咖哩。');
INSERT INTO `recipe` VALUES (1011, 87, '薄荷及香菜酱', '1.混合薄荷香菜及洋葱，加入辣椒，胡椒及酸奶；\r\n2.将蜂蜜在醋中融化，并搅入酸奶混合物；\r\n3.冷藏1小时以后使用。', NULL, NULL, NULL, '薄荷叶，碎5克；香菜鲜碎5克；洋葱细丁100克；辣椒粉2克；黑胡椒2克；盐2克；摆酸奶60毫升；蜂蜜10毫升；醋10毫升。');
INSERT INTO `recipe` VALUES (1012, 87, '苍鹰搏兔', '1.用牛奶浸泡兔一晚在冷库中，第二天排放掉多余的牛奶，这是为了减少肝的氧化；\r\n2.另外浸泡下列混合物：兔子，鸭，猪及猪油，一半的香料，一半的白兰地及白酒，根据需要加入调料；\r\n3.在油锅中炒肝、洋葱、大蒜和剩余的香料，用一半的白兰地及白酒烹制3-4分钟，取下；\r\n4.当肝及洋葱等冷却好时，用手混合猪肉及其它淹泡物，搅拌两遍，并用手混合；\r\n5.在长方形的肉坯模具中排好咸肉，然后填入2厘米深的肉馅，放上李子，再填上肉馅，放入火腿及肥油，然后重复上述步骤直至5-8厘米厚；\r\n6.叠上咸肉使其盖在肉坯表面，并', NULL, NULL, NULL, '兔，去骨，净的，切成块750克、鸭子，去骨和皮，切成块500克、兔肝250克、白兰地50毫升、黑胡椒，碎3克、盐2克、百里香，鲜碎3克；猪肘，去骨，净的，切成块400克、猪背油，块400克、油3毫升、洋葱100克、大蒜，酱20克、混合(四)：酱料，豆蔻，丁香及桂皮3克、白酒50毫升、香叶1个、鸡蛋1个、法香，碎3克、李子，去壳的500克、火腿，肥的300克、猪油300克、牛奶60毫升、鸡汤150毫');
INSERT INTO `recipe` VALUES (1013, 87, '草莓奶油卷', '1.糕胚中间刮草莓奶油（人造奶油拌入草莓泥或草莓酱）向前推卷，表面用草莓奶油装饰、切片（约1.2cm宽）。糕胚：草莓:奶油=1:0.5. \r\n2.烘烤：炉温190度，烘烤约8-10分钟，出炉后覆于纸或布上，先空卷定型，冷却后表面刮上各种材料。', NULL, NULL, NULL, '草莓酱、草莓泥。糕胚制法：蛋:面粉:糖:水:色拉油:乳化剂=1:0.5:0.1:0.15:0.06。');
INSERT INTO `recipe` VALUES (1014, 87, '炒带子西兰花', '1.用毛巾或纸将带子多余的汁液吸干；\r\n2.热好一个锅，加入一点油，并均匀的沾满锅边；\r\n3.加入一些带子，炒至棕黄，搅翻取出放在边上备用，重复同样步骤，直至所有带子棕黄，保持温度；\r\n4.倒入其余并轻炒姜及大蒜，加入洋葱，西兰花，胡萝卜，蘑菇，继续直至西兰花半熟，取出保温；\r\n5.擦一下锅并重新加热，放入鱼汤及豆酱，鹰栗粉和蚝油汁，加入蔬菜，煮开并搅动；\r\n6.最后加入带子，调味，并根据要求摆入盘中，并配以米饭。', NULL, NULL, NULL, '冻带子130克、花生油15毫升、鲜姜3克、蒜，碎10克、洋葱片30克、西兰花，焯过的30克、胡萝卜条15克、芹菜，片丝15克、蘑菇，1/4的10克、鱼汤45毫升、辣豆腐1克、红豆腐1克、鹰栗粉0.5克、蚝油汁3毫升、白米饭1包、小葱细碎2克、芝麻籽黑烤的3克。');
INSERT INTO `recipe` VALUES (1015, 87, '炒面条配蔬菜豆腐', '1.将豆腐水分排干，切为两厘米块，在酱油中腌30分钟，在带有烤纸的烤盘中烤10分钟(220度)，取出置冷；\r\n2.在锅中加热油，炒洋葱姜蒜胡萝卜及红椒，加入豆腐，将豆腐炒至微黄。但避免炒糊；\r\n3.加入紫菜，橙味汁，及酱油，然后放入河粉，并与其他配料混和好；\r\n4.放在盘中装饰。', NULL, NULL, NULL, '豆腐300克、酱油30毫升、大蒜，碎10克、姜，鲜碎2克、芝麻，烤的4克、紫菜皮0.25个、橙味汁60毫升、芝麻油20毫升、河粉，焯冷125克、胡萝卜丝60克、红甜椒，丝40克、豆芽40克、小春葱片30克、香菜，碎3克。');
INSERT INTO `recipe` VALUES (1016, 87, '脆皮烤肉', '1.肉块洗净控干，在肉皮面顺着短轴方向切数刀，切透肉皮进入肥肉约1厘米深。刀距1.5-2厘米。\r\n2.将肉块放入烤盘中，肉皮面朝下，加水约2厘米深。\r\n3.烤箱预热至160度，放入烤盘，水开后15分钟取出肉块。也可用大锅将肉块皮朝下放入锅内，加水煮15分钟后取出肉块。\r\n4.在肉块四面均匀地抹上盐和胡椒末，肉皮缝内插入月桂叶。\r\n5.烤盘上放一烤架，将肉块皮面朝上置于烤架上，使肉块不与烤盘内的汤汁接触，在烘烤过程中流出的肉汗可收集在烤盘内。将烤盘放入烤箱最下层（温度仍为160度）。\r\n6.烤半小时后，往肉', '烤肉皮香脆，肉软嫩，汤汁味浓，配以酸甜的苹果和果酱，更能开胃增加食欲。此菜是北欧传统的圣诞节晚餐主菜。', '配食：清水煮土豆或炸土豆。吃法：将烤肉沿肉皮上的刀痕切成约1厘米厚的片，摆入大盘中，浇汁盛在碗中，土豆盛在瓷盆内，同时上桌。食时可配苹果片和少量果酱如黑加仑子酱。', NULL, '带皮猪腿肉（长方形整块肉）1000克，水1公升，洋葱3个，月桂叶3-4片，面粉1-1.5大勺，精盐、胡椒末适量。');
INSERT INTO `recipe` VALUES (1017, 87, '大米泥子汤', '1.把鸡蛋黄和牛奶或奶油放在碗里搅匀。\r\n2.将葱头和40克黄油放到锅里，用微火将其炒出香味，不要炒上色。\r\n3.把面粉和剩下的油放到另一个锅里，用微火把面粉炒熟。\r\n4.将清汤、大米、炒好的葱头放在一起，上火见沸后改用小火，米煮烂后过粗萝，再用勺子背将米和葱一起压挤到汤里，然后将汤逐渐地倒在炒好的面粉里，边倒边搅，用微火将其煮到沸起，调好味，离开火，晾一会儿倒入鸡蛋黄和牛奶混合物搅匀即可。', NULL, NULL, NULL, '大米250克，清汤1500亳升，葱头200克（切碎），黄油或植物油120克，面粉100克，鸡蛋黄2个，牛奶或奶油50克，盐和胡椒粉适量。');
INSERT INTO `recipe` VALUES (1018, 87, '大米咸猪肉汤', '把汤或水放在锅里，将锅置中火上，沸起后放入猪肉，改用小火煨1小时，放入蒜，土豆和大米，煮熟后用盐和胡椒粉调好味，倒在汤盘里，撒上香菜即可。', NULL, NULL, NULL, '大米100克，猪肉100克（切丁），土豆300克（去皮，切丁），汤或水1500毫升，大蒜8克（切碎），盐，胡椒粉适量，香菜10克（切碎）。');
INSERT INTO `recipe` VALUES (1019, 87, '大虾色拉', '1.制腌味汁：将番红花放在一中碗内，然后加入沸水，浸5分钟后待用。将2个柠檬榨汁，汁液约有90毫升。用厨刀将蒜头拍碎，除去蒜头的皮并将它粗略地切碎。将蒜头、白米醋、盐、胡椒、柠檬汁、橄榄油及水瓜钮加入盛番红花的碗中，再把所有的腌味料搅拌，轻轻将水瓜钮压向碗边，以便吸取更浓的香味。\r\n2.腌制青瓜及大虾：清洗青瓜，切去首尾两端，用刀将每个青瓜直切成2块。分别将2块青瓜平放在砧板上，横切成厚5毫米的薄片。将青瓜片平放在一只浅碟中，并用匙羹淋上2/3的腌味汁，搅拌好后盖好，放入电冰箱内腌制3-4小时，以让香味渗', NULL, NULL, NULL, '1大撮番红花、2汤匙热水、3个柠檬、6粒蒜头、4汤匙白米醋、少许盐和胡椒、250毫升橄榄油(可准备多些，作烘烤用)、4汤匙隔去水分的水瓜钮、500克青瓜、18只新鲜连壳大虾。');
INSERT INTO `recipe` VALUES (1020, 87, '丹麦大肉丸', '1.洋葱切细，与其它原料一起放入大盆内，用力搅拌3-5分钟，直至肉馅均匀“有劲”为止。\r\n2.将拌好的肉馅放入冰箱冷藏柜（4度）内，至少15分钟。时间长些更好。\r\n3.取平锅烧热下油，用一大勺子将肉馅做成直径约为4厘米的大肉丸子（如果肉馅粘勺，可将勺子先在热油中蘸一下就可以防粘）。作好一个即放入锅中，并在表面上淋一遍热油，待肉丸放满后，将火调小，炸4-5分钟。将肉丸翻面，同时调大火炸1分钟，即铲起沥干油出锅。', '此菜肉质松软，味美可口。是典型的丹麦风味菜。历史悠久，目前仍享有很高的盛誉，是家庭中最常见的一种主菜。', '配食：热土豆沙拉、酸奶土豆沙拉。吃法：每盘盛肉丸2-3个，配热土豆沙拉或酸奶土豆沙拉3-4勺。用刀叉进餐。', NULL, '（4份）肉末（猪、牛肉各半）500克，面粉100毫升，牛奶或冷肉汤200毫升，鸡蛋1个，猪油或菜油30克，洋葱1个，精盐2小勺，胡椒粉1/4小勺。');
INSERT INTO `recipe` VALUES (1021, 87, '蛋黄椰蓉塔', '1.用混酥面团制底胚，擀成厚度约0.5cm的大片，用7.5cm圆印模切割后，装入菊花模内。 \r\n2.调制椰蓉馅：蛋黄:砂糖:椰蓉:蛋清:全蛋=1:0.5:0.5:0.4:0.1。将其中2/3砂糖和蛋清拌匀，拌入椰蓉，将椰蓉馅舀在底胚上（容量约占3/4），再将蛋黄和1/3砂糖搅打稠厚，用裱花袋裱于椰蓉表面。 \r\n3.烘烤：炉温200度，约20分钟左右。待烘透，表面呈金黄色为佳。出炉后表面如有塌陷现象是烘烤不透的缘故。', NULL, NULL, NULL, '蛋黄、砂糖、椰蓉、蛋清、全蛋。调味料：蛋黄:砂糖:椰蓉:蛋清:全蛋=1:0.5:0.5:0.4:0.1。');
INSERT INTO `recipe` VALUES (1022, 87, '蛋汁吐司', '1.将B料打匀成蛋汁，涂在吐司上。\r\n2.适量的油倒入平底锅，将涂有蛋汁的吐司放在平底锅内，两边煎至金黄色夹出。\r\n3.趁热将奶油涂抹在吐司上。', NULL, '也可以适量地在B料中加入一些香草精，香草精可以在药房或食品材料行购得，但也可以不加，或改用肉桂粉，别有风味。', NULL, 'A：厚片吐司4片、奶油1匙。B：蛋1个、蜂蜜1匙。');
INSERT INTO `recipe` VALUES (1023, 87, '德国薯仔沙拉', '1.将熟土豆去皮切片，与洋葱丝一起放入容皿内，调以精盐、白胡椒粉、醋、芥未酱、沙拉油和马油乃司，制成沙拉。\r\n2.炸锅内倒入沙拉油，把切好的熏肉片放入锅内，炸至微黄酥脆时取出，再用刀将肉剁碎成粒状。\r\n3.食用前，将沙拉和熏肉碎粒混合拌匀，装在垫有生菜的盘内，呈丘形。', NULL, NULL, NULL, '熟土豆150克、生菜60克、洋葱15克、芥未酱4克、精盐3克、白胡椒粉2克、马油奶司40克、熏肉25克、醋2克、沙拉油100克。');
INSERT INTO `recipe` VALUES (1024, 87, '德式黄瓜土豆沙拉', '1.将土豆洗净煮熟去皮，切小片。\r\n2.将黄瓜洗净去皮、籽、切小片。\r\n3.将洋葱洗净，擦成末，与土豆、黄瓜一起放入容皿内，加盐，胡椒粉、醋拌均即可。', NULL, NULL, NULL, '黄瓜500克、土豆350克、醋5克、精盐5克、洋葱125克、胡椒粉少许。');
INSERT INTO `recipe` VALUES (1025, 87, '德式土豆饼', '1.将土豆洗净去皮，放入锅中加适量水，上火煮约40分钟，煮到软烂，滗出水，把土豆捣碎成泥。放上鸡蛋，盐、胡椒面，面粉25克，并混合均匀。\r\n2.将葱切成末，放在黄油里炒黄，倒入土豆泥中，再混合均匀。\r\n3.在一面板上撒上面粉，把土豆泥分成三份，全滚上面粉，用刀按成两头尖，中间宽的椭圆饼形，用刀在中间按一条纹，在边上再交替的安几个短纹；呈树叶状。\r\n4.将煎盘上火，放入少许植物油烧热，把土豆饼下入，煎成金黄色即可。然后码放在一个煎盘里、入炉烤几分钟，待土豆饼豉起、铲入盘中，即成。食用时可淋上酸奶油或少许黄油', NULL, NULL, NULL, '豆饼250克、鸡蛋1个，面粉50克、细葱末15克、植物油100克、盐、胡椒面各适量、黄油少许。');
INSERT INTO `recipe` VALUES (1026, 87, '炖小羊腿', '将煎上色的澳洲小羊腿和各种新鲜时蔬炖制4小时，使蔬菜的营养、味道充分被小羊腿吸收。将阿拉伯小米蒸熟和时蔬搅拌作配饭。', NULL, NULL, NULL, '澳洲小羊腿、阿拉伯小米、芒果酱。');
INSERT INTO `recipe` VALUES (1027, 87, '番茄大米色拉', '1.将大米淘洗干净后，放入沸水中煮至成饭粒，再焖15分钟取出用冷开水过凉后控干；番茄洗净后，用沸水烫后剥皮切丁；葱头洗净切末，青椒洗净切丁；备用。\r\n2.将番茄、米饭、葱头末、青椒丁、香菜末、色拉油、醋精放在一起拌匀，再加入精盐、胡椒粉、食糖调好口味即可食用。', '酸咸清口。', NULL, NULL, '番茄110克，大米75克，葱头15克，青椒50克。调料：色拉油25克，香菜末5克，精盐、胡椒粉各适量、醋精、食糖各少许。');
INSERT INTO `recipe` VALUES (1028, 87, '番茄芦笋凯撒色拉', '照主食谱的方法制成面包粒。\r\n1.用剥皮刨将375克芦笋的坚韧外皮削去，并除去木质的尾端，用厨用绳将它们捆成2-3束。将一大锅盐水煮沸，然后加入芦笋，用慢火煮5-7分钟，直至芦笋变软，能以刀尖插入即成。隔去水，以冷水清洗后再滤干水待用。\r\n2.将芦笋顶部约长5厘米切出待用，余下的部分切成长2厘米的条块。将1千克长形番茄的蒂部切去，然后横切成厚5毫米的片块。撇去银鱼柳不用。滤清60克油浸干番茄的油分，并将它们切成细粒。照主食谱的方法准备色拉，并加入取代银鱼柳的番茄粒。将芦笋粒、生菜、2/3的芝士以及1/2的', NULL, NULL, NULL, '面包粒、芦笋、番茄、芝士。');
INSERT INTO `recipe` VALUES (1029, 87, '蕃茄洋芋饼', '1.土豆去皮切片，洋葱切成小丁；\r\n2.油入锅将绞肉炒香，加入1的材料、水及调味料煮一下；\r\n3.用烤盘装入2的材料，上面撒些起司；\r\n4.入200度烤箱烤5分钟，即是香浓的焗风料理。', NULL, '若再撒上葱花色泽就更加娇艳诱人了。', NULL, '起司1/3杯，土豆2个，绞肉2两，意大利蕃茄酱1罐、洋葱1/2杯，水2杯。调味料：盐1/2小匙、糖1/2小匙，胡椒粉1/6。');
INSERT INTO `recipe` VALUES (1030, 87, '方便油饭', '1.米泡一晚，水滤干。\r\n2.料炒好。\r\n3.米与料拌匀，放入康宁锅，加水二杯，微波6分钟后拿出搅匀，再微波6分钟即可。', NULL, NULL, NULL, '糯米三柸(电饭锅柸)、香菇、猪肉丝、虾米、红葱头。');
INSERT INTO `recipe` VALUES (1031, 87, '凤梨海鲜盅', '1.凤梨去皮，并去头尾各1/3，保留中段，挖空成碗状。\r\n2.虾仁及鲜干贝氽烫后泡冰水放凉；凤梨肉切成菱形块。\r\n3.将凤梨块、虾仁及鲜贝放入盅内，上淋以所有调味料调匀的千岛酱汁即可。', NULL, NULL, NULL, '凤梨1个、鲜干贝12个、虾仁200克、千岛酱汁：沙拉酱1大匙、番茄酱1大匙、碎酸黄瓜1/2茶匙、碎洋葱1/2茶匙、碎白煮蛋1/2粒、辣酱1/3茶匙、拧檬汁1/3匙、去皮籽之青椒丁1/2茶匙、白兰地酒1/4茶匙、盐、胡椒适量。');
INSERT INTO `recipe` VALUES (1032, 87, '干笋杏仁鲑吐司', '把鲑鱼肉、胡萝卜做成泥，加味后抹在面包片上，拌鸡蛋糊，再沾杏仁片，下油锅炸成金黄色，出锅切条装盘即可。', NULL, NULL, NULL, '加州杏仁片、鲑鱼肉、干笋（胡萝卜）、面包片、蛋糊、盐、味精、鸡精、淀粉。');
INSERT INTO `recipe` VALUES (1033, 87, '高尔夫', '1.用S.P海绵蛋糕胚修成长方形，剖三层，中间嵌白脱油，顶面、垂直边刮抹白脱油，撒绿椰丝于表面及四周。\r\n2.用薄荷奶油以及调制好的巧克力裱高尔夫运动员形象作装饰。', NULL, NULL, NULL, '白脱油、绿椰丝、薄荷奶油。调味料：糕胚:白脱油=1:1。');
INSERT INTO `recipe` VALUES (1034, 87, '格司布丁', '1.蛋和砂糖搅打均匀，牛奶加热后倒入蛋中，过筛，倒入抹过油的玻璃盅内，放入盛一半水的烤盘内。\r\n2.烘烤：炉温180度，约20分钟。', NULL, NULL, NULL, '牛奶、蛋、糖。调味料：制胚：牛奶:蛋:糖=1:0.23:0.1。');
INSERT INTO `recipe` VALUES (1035, 87, '管壮面', '1.在锅中放入3/4的淡盐水，上面并放一点橄榄油，煮开，小火；\r\n2.加入面条并搅动，避免面沾在一起成为团状，煮大约10分钟直至8成熟；\r\n3.从炉上取下锅并过滤并用流动的冷水冷却，盖上并冷藏备用；\r\n4.根据要求加热汁，并在热水使面加热大约20-30秒，过滤并轻炒；\r\n5.加热一个小煎锅，溶化黄油直至其开始变棕，加入面并搅动，加入汁搅匀；\r\n6.放入盘中，并照常装饰及配料。', NULL, NULL, NULL, '管壮面500克；火腿丁200克；奶酪汁500毫升；番茄汁500毫升；奶油汁500毫升；黄油20克；小葱(细丁)10克。');
INSERT INTO `recipe` VALUES (1036, 87, '罐烤面包葱丝汤', '1.将葱头洗净切丝后，把锅烧热后放入50克黄油，待溶化后放入葱头丝炒至微黄，备用。\r\n2.把锅烧热后放入50克黄油，待溶化后撒入面粉炒至黄色后，倒入滚沸的牛肉汤调匀，放入葱头丝，加盐调好口味，盛于罐内，上放干酪，面包，放入烤箱烤至黄色即可食用。', '清香适口。', NULL, NULL, '葱头300克，干酪50克，面包100克，牛肉清汤1000克。调料：黄油100克，面粉30克，精盐6克。');
INSERT INTO `recipe` VALUES (1037, 87, '罐烤土豆腊肠板肉', '1.将腊肠（去皮）、板肉洗净切片；土豆洗净煮熟切片；鸡蛋煮老切片；备用。把锅烧热后倒入食油，待油温6成热时，放入板肉片炒至上色，备用。\r\n2.罐内码入一层土豆片，一层板肉、一层鸡蛋片，一层腊肠片，按此次序再码放一层，最上面再放一层土豆片，加入精盐、奶油、鸡蛋、清汤混和液，放进烤箱烤熟即可。', '清香味鲜。', NULL, NULL, '土豆600克，腊肠250克，板肉125克，鸡蛋250克。调料：食油25克，奶油100克，精盐适量。');
INSERT INTO `recipe` VALUES (1038, 72, '八宝青蟹饭', '1.将糯米淘清，加适量水蒸成米饭；白果和花生都去壳去衣，也一起蒸熟。\r\n2.将糯米饭、白果、花生、冬菇丁、冬笋片、火腿丁、开洋、猪油（约45克）和盐、酒各少许拌和，放在碗里。\r\n3.将青蟹去壳，去小腿，每只片成6片，覆在糯米饭上面，加葱、姜、用壳扣盖上面，再上笼将青蟹肉蒸熟后，取去葱、姜、倒上热清汤即好。', '此菜呈黄白色，适宜于夏季。', NULL, NULL, '2只700克、糯米165克、火腿丁45克、开洋20克、白果45克、花生75克、冬菇丁、冬笋丁、葱段、姜片、黄酒、盐、清汤（300克）。');
INSERT INTO `recipe` VALUES (1039, 72, '白拌黄螺', '1.先将黄螺的肉取出，方法有二种：一种是先将黄螺外壳敲开，将肉取出，另一种是先将带壳的黄螺用水煮约十分钟，取出，将黄螺的盖子一只一只地揭开，顺着壳的螺旋纹将肉慢慢转出。为了保持螺肉的完整，以用后一种方法为好。\r\n2.将整只黄螺肉洗清，去污质（不要剪去尾部），再放入开水里焯一下，以拔除腥味和黏液，然后取出，揩干水分。\r\n3.将酒、白酱油、糖、葱放在碗里调和，放黄螺肉下去浸约二十分钟，再与佐料一起倒入盘中，浇上麻油即好。', '能保持黄螺肉的本色，鲜嫩清口，是夏季的好菜。', NULL, NULL, '黄螺肉500克、葱段6.5克、黄酒8.5克、白糖少许、白酱油40克、麻油12.5克。');
INSERT INTO `recipe` VALUES (1040, 72, '白炒响螺', '1.将响螺壳敲开，取出肉，除去尾部，用少许盐搓一搓，去尽响螺肉的黏液，再用清水洗净，然后片成薄片，越薄越好，否则炒不嫩。\r\n2.开旺火热猪油（30克）、锅，将响螺片放下去一拉，倒出，滤去油。\r\n3.在锅内另加少许猪油烧热，将葱段，冬笋片、冬菇片一起放入略炒，再加盐、酒、菱粉、清汤勾好芡，随即将拉过的响螺片倒入，一起迅速炒几下即好。', '色白，鲜而清口，夏季最宜。', NULL, NULL, '响螺肉500克、冬笋片75克、冬菇片4片、葱段45克、黄酒75克、盐少许、菱粉少许、清汤少许。');
INSERT INTO `recipe` VALUES (1041, 72, '白蜜黄螺', '1.黄螺洗净，在80度的热水锅中氽一下，取出用竹签挑出螺肉，洗净污渍（特别螺头部泥渍）。\r\n2.清水烧沸，将洗净的螺肉下锅氽一下放碗中，加蒜米及各种调料拌匀，装盘即成。', '螺肉脆嫩，原汁原味，鲜美可口。', NULL, NULL, '鲜活黄螺1000克。蒜米、生油、白糖适量，味精、香油少许。');
INSERT INTO `recipe` VALUES (1042, 72, '爆糟排骨', '1.将排骨切成条形块；糖、盐和红糟调在小碗里，将排骨放入拌一拌，再放在用蛋白、菱粉调和糊内拌一拌。\r\n2.开温火热猪油（500克）锅，将排骨放入炸约三分钟，倒出滤去油。\r\n3.将大蒜头用刀拍碎，放在油锅里炒出香味后，加麻油和葱。再将排骨放入，一起颠炒几下即好。', '色红，有糟香味。', NULL, NULL, '猪大排骨500克、红糟75克、鸡蛋白2只、葱末少许、大蒜头5瓣、白糖30克、盐少许、菱粉45克、麻油20克。');
INSERT INTO `recipe` VALUES (1043, 72, '菜包', '1.青菜洗净，用开水烫后捞出，放冷水浸冷后挤去水分，斩成细末待用。金针菜、笋、冬菇用温水浸发洗净。亦斩成细末；\r\n2.炒锅上火，放油烧至七成热时，放金针菜、笋、冬菇，加精盐、白糖、味精和少量鲜汤，炒透后出锅，加青菜末、姜末拌和，浇麻油少许，即成素菜馅心；\r\n3.将鲜酵母加温水拌，调成糊状，倒入面粉，再加湿水拌和揉透，静置2小时左右；\r\n4.将面团搓条，摘8克左右的坯子，再揿成中间厚、周围转薄的圆形皮子，将素菜馅心包在皮子内；\r\n5.包子上笼后搁置2-3分钟，用旺火沸水蒸一刻钟左右即可。', NULL, NULL, NULL, '青菜1250克，金针菜25克，笋25克，精盐、味精、白糖、姜末、生油、麻油各适量，鲜酵母半块，面粉1000克。');
INSERT INTO `recipe` VALUES (1044, 72, '炒海瓜子', '1.海瓜子洗净；\r\n2.锅内放油，烧热放入葱花、海瓜子，海瓜子八成熟时，放入青辣椒翻炒，开口即熟，放入味精，花椒油和香油出锅即可。', NULL, NULL, NULL, '海瓜子300克，青辣椒，味精，油，葱花花椒油，香油各适量。');
INSERT INTO `recipe` VALUES (1045, 72, '炒面线', '1．锅置旺火上，下花生油烧六成热时，将面线投入炸成金黄色时捞起，放入沸水锅回软，去盐味和油渍，捞起待用。\r\n2.精肉、冬笋、香菇、红萝卜、干扁鱼、葱白切丝，鲜虾洗净剥壳另用，虾肉也切成丝。虾壳洗净加上汤100克烧成虾壳汤，过滤待用。\r\n3.锅置旺火上，放入少许花生油，烧热后入肉丝、冬笋、香菇、红萝卜丝稍炒后加入虾壳汤、上汤，投入过油的线面，炒几下，加葱白、煸鱼丝，淋上香油、胡椒粉即成。', NULL, NULL, NULL, '面线（又称线面）500克，精肉50克，鲜虾100克，干扁鱼30克。熟冬笋50克，香菇5朵，红萝卜半根，葱白2根，精盐、味精、香油少许，上汤400克，花生油500克（约耗150克）。');
INSERT INTO `recipe` VALUES (1046, 72, '炒蜇血', '1.将蜇血切成块子，放入冷水内浸约一天，以拔去咸味，取出，压干水分。\r\n2.开旺火大热猪油锅，将蜇血放入锅内拉一下，倒出，滤去油。\r\n3.将黄酒、糖、醋、酱调和，倒入热锅，再将拉过的蜇血倒入略炒，立即加湿菱粉和葱段略炒，浇上麻油，翻一下起锅即好。', '呈咖啡色，味脆嫩，为春秋季的时令菜之一。', NULL, NULL, '海蜇血700克、葱段45克、黄酒6.5克、 白糖125克、酱油25克、醋40克、菱粉45克）、麻油6.5克。');
INSERT INTO `recipe` VALUES (1047, 72, '蛏溜奇', '1.将蛏洗清，用开水氽一下，剥去壳，剔去黑的污质；将熟猪肥膘切成小粒；葱切成2—3分长的细末；鸡蛋打碎。\r\n2.开热猪油（250克）锅，先将蒜头拍碎，下锅炒香，再将打发的鸡蛋、肥膘粒、面粉、盐、葱末、蛏肉拌和，倒入锅内，炒至金黄色时，随即浇上麻油一翻炒即成。', '色金黄，鸡蛋松而不老，有肥膘香、蒜香、葱香、麻油香。', NULL, NULL, '蛏肉300克、熟猪肥膘40克、鸡蛋3只、葱末少许、大蒜头5瓣、盐少许、面粉150克、麻油12.5克。');
INSERT INTO `recipe` VALUES (1048, 72, '葱爆肉丝', '1.瘦肉切丝，加入调味料拌匀，泡嫩油待用。\r\n2.葱洗净，切斜段。红椒洗净，去籽切丝。\r\n3.烧热锅，下油两汤匙爆香葱白，放入肉丝，酒，加入余下葱段、芡汁料及红椒丝，兜匀便可上碟。', '是一款很惹味的福建小菜，喜欢吃葱的朋友一定不能放过。', NULL, NULL, '瘦肉、葱（各四两）、红椒（半只）、生抽、酒（各一茶匙）、生粉（半茶匙）、油（半汤匙），盐、糖、生抽、生粉、清水、麻油适量。');
INSERT INTO `recipe` VALUES (1049, 65, '白汁菜心', '1. 将干贝、冬笋片、鲜蘑、姜、葱、精盐放在一起烧至入味，再把鸽蛋做熟放入盘中。\r\n2. 菜心加汤烧至入味时捞出，覆盖在盘上，用湿淀粉勾芡淋在盘上。', '四川家常菜，此菜色泽翠绿，清香脆嫩，滑润爽口。', NULL, NULL, '油菜心250克，干贝150克，油、冬笋片各50克，鲜蘑30克，鸽蛋1个，姜15克，葱20克，精盐5克。');
INSERT INTO `recipe` VALUES (1050, 72, '葱油饼', '1.将面粉、水拌和均匀，揉成面团，搓成长条；\r\n2.摘下生坯，擀成围片，刷上油，将葱花、盐、油、花椒粉拌和，均匀地撒在圆片上，卷好擀圆，上平底锅烙烙至金黄色出锅。', NULL, NULL, NULL, '面粉500克、葱花l00克、盐、油、花椒粉各适量。');
INSERT INTO `recipe` VALUES (1051, 72, '蛋拌豆腐', '1.将熟鸡蛋剥去外壳用冷水洗净；\r\n2.和嫩豆腐一起放到碗内，加入精盐、酱油、味精、蒜泥、菊花和麻油调好口味用竹筷将鸡蛋和豆腐搅拌匀即可。', NULL, NULL, NULL, '熟鸡蛋2只，嫩豆腐l盒，精盐、酱油、味精、蒜泥、葱花和麻油备少许。');
INSERT INTO `recipe` VALUES (1052, 72, '佛跳墙', '1.将水发鱼翅去沙，剔整排在竹箅上，放进沸水锅中加葱段30克、姜片15克、绍酒100克煮10分钟，去其腥味取出，拣去葱、姜，汁不用，将箅拿出放进碗里，鱼翅上摆放猪肥膘肉，加绍酒50克，上笼屉用旺火蒸2小时取出，拣去肥膘肉，滗去蒸汁。\r\n2.鱼唇切成长2厘米、宽4.5厘米的块，放进沸水锅中，加葱段30克、绍酒100克、姜片15克煮10分钟去腥捞出，拣去葱、姜。\r\n3.金钱鲍放进笼屉，用旺火蒸取烂取出，洗净后每个片成两片，剞上十字花刀，盛入小盆，加骨汤250克、绍酒15克，放进笼屉旺火蒸30分钟取出，滗去蒸汁', '此菜用料多海鲜珍品，制作方法独特，食味浓香，非一般佳肴所能比拟，且营养丰富，中外驰名。', '泡发干贝：将干贝洗净，装入碗内，加少许高汤和葱、姜，放入笼屉蒸烂即可。花冬菇：即可末春初所产的香菇，面有菊花纹。最后各种原料放入坛内，一定要上小火煨制，不可急燥，否则达不到效果。', NULL, '水发鱼翅500克，净鸭肫6个，水发刺参250克，鸽蛋12个，净肥母鸡1只，水发花冬菇200克，水发猪蹄筋250克，猪肥膘肉95克，大个猪肚1个，姜片75克，羊肘500克，葱段95克，净火腿腱肉150克，桂皮10克，炊发干贝125克，绍酒2500克，净冬笋500克，味精10克，水发鱼唇250克，冰糖75克，鲂肚125克，上等酱油75克，金钱鲍1000克，猪骨汤1000克，猪蹄尖1000克，熟猪油10');
INSERT INTO `recipe` VALUES (1053, 72, '芙蓉干贝', '1.将水发干贝盛入盆里，加入葱、姜、绍酒5克、清水250克，上笼屉用旺火蒸30分钟取出，滗去蒸汁，拣去葱、姜。\r\n2.鸡蛋清放在碗里，顺一定方向打散，加精盐2.5克、味精7.5克、牛奶调匀，倒入汤盘，上笼屉用中火蒸5分钟取出，不待芙蓉面结硬，将干贝整齐地插于面上，再上笼屉蒸5分钟取出。\r\n3.锅置旺火上，下高汤烧沸，加入精盐。在用味精、绍酒调匀，淋于芙蓉干贝上即成。', '风味清淡、鲜美，既无浓妆艳饰，也无繁杂搭配。菜品软嫩甘美，清鲜适口。', '水发干贝加料上笼屉用旺火蒸30分钟后，滗出蒸汁。这时应注意干贝的整粒形态。水发干贝即是将干贝边上的老肉去掉，用清水洗净，去沙土、霉味，上笼屉用中蒸2小时取出。', NULL, '水发干贝300克，绍酒7.5克，鸡蛋清8个，精盐4.5克，葱8克，味精10克，姜3片，白汤250克，鲜牛奶200克。');
INSERT INTO `recipe` VALUES (1054, 72, '干贝水晶鸡', '将鸡脯开花刀，再切成方块，先用盐抓一抓，再用打散调匀的蛋白抓一抓。然后将鸡块一块块地放入开水锅内氽一氽，使鸡块稍微结实一些， 再将鸡块放入大碗，加清水（也可用清汤，但蒸好后较混浊）、干贝、酒、盐、上笼蒸熟（约五十分钟）即好。', '汤明如水晶，味极清口。', NULL, NULL, '鸡脯500克、干贝75克、鸡蛋白3只、黄酒20克、盐6.5克、菱粉65克。');
INSERT INTO `recipe` VALUES (1055, 72, '干烧牛肉片', '1.将牛肉切成薄片；\r\n2.上旺火煸炒，加姜丝、豆瓣酱、辣椒粉略炒至油变红，加料酒、味精、白糖、芹菜翻炒几下，淋少许醋，装盘，撒花椒粉即可。', NULL, NULL, NULL, '牛肉，芹菜，姜丝，豆瓣酱，辣椒粉，味精，料酒，醋，糖。');
INSERT INTO `recipe` VALUES (1056, 72, '干烧虾', '1.炒锅上火留底油，油热放入大虾、肉丁、木耳丁、葱节煸炒。\r\n2.放入豆瓣酱、调料烧入味即可。', NULL, NULL, NULL, '大虾，木耳丁，葱节豆瓣酱，调料。');
INSERT INTO `recipe` VALUES (1057, 72, '干烧鱼', '1.将鲤鱼两面各剡数刀，以川盐、料酒腌入味；\r\n2.锅内油烧至八成热，将鱼炸至金黄色；\r\n3.锅内留油，下猪肉末、白糖、川盐、料酒、胡椒面和鲜汤、姜、蒜、辣椒、酱油，烧至汁干，起锅装盘。', NULL, NULL, NULL, '鲤鱼一条，川盐，料酒，猪肥瘦肉末，姜，蒜，泡辣椒，酱油，白糖，胡椒面，葱备适量。');
INSERT INTO `recipe` VALUES (1058, 72, '蛤蜊鲫鱼', '1.将蛤蜊在冷水里泡约十二小时，使它吐净污物，再洗清。\r\n2.将鲫鱼剖开，去鳞，去内脏，洗清，放入热猪油锅略爆，滗去油，加清汤、酒、葱、姜，再用旺火烧，后用小温火烧。\r\n3.鲫鱼汤烧至浓白时，将蛤蜊一只只分别放在鲫鱼旁边，加盐同烧，待蛤蜊壳一张开，立即取去葱、姜，起锅推入盘中，撒上少许胡椒粉即好。', NULL, NULL, NULL, '蛤蜊500克、鲫鱼1条，600克、葱1根、姜片1片、胡椒粉少许、黄酒75克、盐4克、清汤700克。');
INSERT INTO `recipe` VALUES (1059, 72, '豉椒鸡丁', '1.鸡脯肉开花刀，切成块形小丁，加鸡蛋白、干淀粉、精盐调拌均匀，放入旺猪油锅稍煎一下，将油沥干；\r\n2.将干椒切成小丁形和豆豉放入旺油锅煎，煎至呈金黄色，将鸡丁放入一起炒10分钟；\r\n3.将葱段、黄酒、酱油、糖、醋、湿淀粉调和，倒入锅内炒拌，最后加些辣椒油起锅即可。', NULL, NULL, NULL, '嫩鸡脯150克，鸡蛋一只，干淀粉6克，葱段、辣油、白糖、酱油、湿淀粉、味精、精盐、黄酒、干辣椒、豆豉、香醋适量。');
INSERT INTO `recipe` VALUES (1060, 72, '豉椒牛排', '1.烧锅放油，油烧至两成半熟，将牛排放入热油至仅熟，倒出滤油；\r\n2.锅内留油，将料头放入爆香，放入辣椒块抛匀，放入些清水，再加入牛排，下入绍酒，用芡汤七成，淡汤三成，加入深色酱油调成金红色，用湿淀粉调匀为芡，加些包尾油、麻油和匀上碟便成，装点荷兰豆等时令蔬菜。', NULL, NULL, NULL, '腌好牛排125克，辣椒块100克，以蒜茸、葱段、豆豉泥为料头，绍酒10克，油味料等适量。');
INSERT INTO `recipe` VALUES (1061, 72, '豉椒肉丝', '1.将熟猪肉切成细丝锅上火，下猪油烧热，放入火腿片略炸倒入鸡汤，下肉丝、黄酒烧沸片刻；\r\n2.加入精盐、菠菜心烧入味，用水淀粉勾薄芡，起锅装盘即可。', NULL, NULL, NULL, '熟猪肉50克，火腿片25克，菠菜心25克，黄酒15克，精盐3克，猪油50克，豆豉20克，花椒5克，淀粉l0克，鸡汤250克。');
INSERT INTO `recipe` VALUES (1062, 72, '豉椒虾', '1.将虾剪去须脚用清水流净，沥干水分；\r\n2.烧锅落油，待油烧至三成半热，将虾放入拉油至仅熟倒在爪篱里。利用锅中余油，将料头放在锅中爆香加进辣椒段抛匀倒入些滚水，将虾放入锅里再抛匀倒入绍酒，用芡汤、葱段、湿淀粉调成金黄色为佳，加些包尾油、麻油和匀上碟即可。', NULL, NULL, NULL, '虾120克，辣椒段100克，以蒜茸、葱段、豆豉泥为料头，油味料等适量。');
INSERT INTO `recipe` VALUES (1063, 72, '海参羹', '1.将海参切成小方丁，投入放有葱、姜的开水锅内焯一下，以拔去海腥味；冬菇、冬笋都切成丁；鸡蛋打碎。\r\n2.将猪肉切成末，放入热猪油锅内炒熟，加少许清汤、酱油、使呈红色。再将冬菇丁、冬笋丁与海参丁一起放入肉锅，并加上剩下的清汤和酒，用旺火烧熟，下菱粉勾好薄芡，然后将打发的鸡蛋倒入搅匀，再撒上胡椒粉，浇上醋和麻油即好。', NULL, NULL, NULL, '水发涨参700克、猪肉末160克、冬菇、冬笋、鸡蛋2只、葱、姜片、胡椒粉、黄酒、酱油20克、盐油、盐、醋、 菱粉、麻油、鸡蛋600克。');
INSERT INTO `recipe` VALUES (1064, 72, '海鲜豆腐羹', '1.将豆腐去掉外部表皮，切成长4厘米、宽0.5厘米、厚0.5厘米的条，用开水焯过备用。牛肉切成丝。\r\n2.将牛肉汤倒入炒锅中，开锅后去掉泡沫，加入料酒、盐、味精，然后将豆腐放入，开锅后加入牛肉丝，西红柿、香菇、木耳、笋丝，拌匀后，盛入场碗中，撒葱丝即可。', NULL, NULL, NULL, '豆腐150克，牛肉50克，西红柿50克香菇少许，葱15克，木耳、笋丝各少许，牛肉汤500克，盐3克，味精3克，料酒2克。');
INSERT INTO `recipe` VALUES (1065, 72, '红豆沙包', '1.将鲜酵母加温水搅，调成糊状，倒入面粉，再加水拌和揉透，静置2小时；\r\n2.将面团搓条，摘成坯子，再揿成中间厚、周转薄的圆形皮子，将红豆沙馅心包在皮子内同时放一小块糖板油丁；\r\n3.沙包上笼后搁置23分钟，用旺火沸水蒸一刻钟左右即可。', NULL, NULL, NULL, '面粉l000克，鲜酵母半块，红豆沙馅心600克，糖板油丁100克。');
INSERT INTO `recipe` VALUES (1066, 72, '红烧狮子头', '1.猪肉分割成肥肉及瘦肉两部分。\r\n2.肥肉切粒，瘦肉切幼粒，再剁碎，一同放入大碗中，加入调味料拌匀至起胶，搓成六个肉丸。\r\n3.小棠菜洗净切段，以油锅略炒，盛起一半于瓦锅底，放入肉丸，再将其余菜放在肉丸上，加入上汤、老抽、糖煮至滚，改慢火焖约一小时，即可原煲上桌。', '具有浓厚的家乡风味，狮子头啖肉，甘香美味，加上原煲上桌，最适宜送饭。', NULL, NULL, '半肥瘦猪肉、小棠菜（各十二两）、上汤（半杯）、老抽、糖（各一茶匙）、盐、生抽（一茶匙）、姜汁、葱汁、生粉、糖、酒（两茶匙）、清水（两汤匙）。');
INSERT INTO `recipe` VALUES (1067, 72, '鸿图鸭', '1.将鸭从尾部剖开，去头，去脚，去内脏，洗清，然后加酒、葱、姜上笼蒸约一小时半。\r\n2.将鸭子取出，拆去骨头后切成小片，放在碗里，加火腿片、冬笋片、鲜菇片、盐、酒、清汤、上笼将鸭片等蒸熟（约十分钟）。\r\n3.将鸡蛋白加清汤和盐打发，上笼蒸约三分钟后取出，放在盘中。\r\n4.将蒸熟的鸭片，火腿片等倒入锅内，加上清汤，下湿菱粉勾薄芡后起锅，推在蛋白上面即好。', NULL, NULL, NULL, '光鸭1只（1400克）、火腿片75克、冬笋片75克、 鲜菇片75克、干贝、鸡蛋白6克、葱、姜片、黄酒、盐、菱粉、鸡蛋300克。');
INSERT INTO `recipe` VALUES (1068, 72, '荤罗汉', '1.将鱼翅、鲍鱼片、海参块、蹄子块、鸡块洗干净，放入开水锅，加姜片川一下，拔去腥味。\r\n2.用酒坛一只，将鱼翅、鲍鱼片、海参块、鸡块、蹄子块、天胎、干贝、冬菇放入，加酒、白酱油、将坛口密封。用小温火慢慢烧烂。\r\n3.用菜心炒熟，加入坛里烧开，盛在大碗里，将菜心放在上面即好。 附注：“天胎”即猪口内的上颚肉。', '用料几乎遍及鸡、鸭、鱼、肉、鱼翅、海参等，应有尽有。芬香扑鼻，味浓而鲜。', NULL, NULL, '水发板鱼翅300克、水发鲍鱼片160克、水发海参块160克，鸡肉块、猪蹄子块、干贝、天胎10条、水发冬菇、菜心、黄酒、姜片、白酱油。');
INSERT INTO `recipe` VALUES (1069, 72, '鸡茸金丝笋', '1.将冬笋切成4、5厘米长的段，再切成纸一般薄的片，然后分别切成细丝。鸡脯肉和猪肥膘肉用刀剁成茸。鸡蛋磕开，放入碗里打散，加入精盐，味精、湿淀粉搅匀后，将鸡肉茸放入，再搅拌至鸡茸糊。火腿肉切成米粒状。\r\n2. 炒锅旺火烧热，下猪油烧到八成热时，将笋丝下锅过油一分钟，用漏勺捞起沥干油，用沸水冲去油腻，与鸡汤一并下锅，微火煨二十分钟至鸡汤全部被笋丝吸收，然后取出，装入鸡茸糊中拌匀。\r\n3. 炒锅旺火烧热，下猪油100克烧到八成热时，倒下拌匀的笋丝鸡茸糊，这时火越旺越好，炒三分钟后装盘，再撒上火腿即成。', '色泽金黄，细如金丝的冬笋丝与鸡茸融为一体，笋嫩脆，鸡茸松软，鲜润爽口，芳香扑鼻。', NULL, NULL, '将鸡脯肉125克、净冬笋100克、猪肥膘肉25克、鸡蛋4个、熟火腿25克、精盐1.5克、味精0.5克、湿淀粉10克、鸡汤250克、熟猪油500克（实耗125克）。');
INSERT INTO `recipe` VALUES (1070, 72, '鸡汤氽海蚌', '1.将母鸡宰杀洗净后，割下鸡脯肉待用。其余鸡肉切四块，牛肉、猪里脊内切大牍一料同装一盆中，加清水1500克，上笼屉用旺火蒸3小时取出，去肉取汤，撇浮沫，滤去杂质。鸡脯肉剁成茸，加适量清水与精盐抓匀，捏成几个小圆球，与鸡汤下锅煮5分钟，捞出鸡茸球，鸡汤用净纱布过滤成精美鸡汤，取900克待用。 \r\n2.海蚌尖每只片成2片，洗净后盛入漏勺内，放入沸水锅中氽至六成熟取出，剔净蚌膜，装在碗中，加绍酒抓匀腌渍后沥干酒汁，盛入汤碗，加入鸡汤150克稍浸，再将汤汁滗净。\r\n3.将鸡汤下锅煮开，加入白酱油、味精调匀，浇入汤', '海蚌是我国海产品中珍品，肉质脆嫩，色白透明，蚌壳略呈三角形，壳很薄，仅在淡咸水交汇处的海水细沙中生长，以长乐漳港所产的质量最佳。本品成菜后，色泽洁白透明，蚌肉清鲜脆嫩。', '鸡脯内剁成茸，加盐及清水后捏成小圆球，与鸡汤同煮，是因为鸡茸球可以吸附汤中杂质，使汤更清。洗净后的海蚌，味较清淡，用精美的鸡汤烹制可使蚌肉渗入鸡汤而醇香味美。', NULL, '鲜海蚌尖400克，绍酒25克，老母鸡1只，白酱油25克，鲜牛肉750克，精盐1.5克，猪里脊肉750克，味精5克。');
INSERT INTO `recipe` VALUES (1071, 72, '吉利虾', '1.对虾洗净，去壳留尾，每只虾肉均从背脊割透1刀，剔去沙线，提起虾尾穿过割透的缝隙，拉成虾球生坯，用精盐、味精0.5克、绍酒浆匀稍腌。\r\n2.辣椒去蒂后洗净，与香菇、冬笋、洋葱、胡萝卜、葱白均切成丝。蒜瓣剁末。骨汤、酱油、桔汁、味精1.5克、乌醋、白醋、白糖、芝麻油、湿淀粉和匀兑成卤汁，鸭蛋放在碗里，打散。\r\n3.炒锅置旺火，下入熟猪油，烧至五成热，将虾球生坯先沾匀面粉，再蘸匀蛋液，而后滚匀面包末，下锅炸至色泽金黄时，倒进漏勺沥去油，装入盘中。\r\n4.在炸虾球的同时，取另1只锅置火上，下熟猪油50克烤热，', '虾球色泽金黄，外酥里嫩。芡汁五彩缤纷，色艳甘鲜，上菜时才浇汁，炙热爽口，饶有食趣。', '炸虾球与烹味汁要同时进行，迅速上席，将芡汁淋在虾球上，趁热食用。炸虾球，先用旺火热油，然后转中火，翻匀炸透，里外一致，外脆里嫩。', NULL, '鲜对虾500克，净胡萝卜15克， 水发香菇15克，葱白10克，净冬笋10克，鲜辣椒1只，净洋葱10克，白糖10克，精盐1克，湿淀粉10克，乌醋5克，桔汁10克，白醋5克，绍酒10克，酱油5克，味精2克，鸭蛋1个，猪骨汤75克，面粉25克，芝麻油0.5克，蒜瓣15克，熟猪油500克，面包末150克（约耗100克）。');
INSERT INTO `recipe` VALUES (1072, 72, '家常烤鸡', '1.将鸡入沸水煮5分钟，捞出沥干；\r\n2.用昧精、盐、酒搽匀鸡身，腌半小时取出晾干，再用麦芽糖均匀地涂在鸡身上；\r\n3.鸡装入烤盘后进烤箱烘烤，胸向上25分钟，背向上15分钟取出淋上香油即可。', NULL, NULL, NULL, '鲜嫩鸡1只，味精3克，盐15克，酒l0克，花椒、葱、姜、麦芽糖少许。');
INSERT INTO `recipe` VALUES (1073, 72, '煎明虾段', '1.将每只明虾带切成3段，除去黑肠，放入用少许白酱油、酒、蛋白、菱粉调和的薄糊里拌一下。\r\n2.开温火热猪油锅，将明虾放入锅内煎透后，加白酱油、糖、酒、葱、姜，再下少许清汤、菱粉勾成薄芡，浇上麻油，翻转即好。', NULL, NULL, NULL, '明虾6只、鸡蛋白2只、葱白20克、姜米20克、黄酒20克、白糖6.5克、白酱油12克、菱粉12克、麻油6.5克、清汤。');
INSERT INTO `recipe` VALUES (1074, 72, '金钱肉', '1.将猪腿肉片成24片薄片，每2片要留一边不要片断，使2片能连在一起。\r\n2.将猪肥膘切成12块像银元大小的圆块子，一起放入用酱油、糖、五香粉、酒调成的卤内滚一滚，再放着腌约二十分钟。\r\n3.将猪肥膘一块块地夹入没有片断的腿肉片中，并用银签将夹好的肉穿成一串再抹上干菱粉。\r\n4.开热猪油锅，将穿好的肉片放入锅内炸熟，再一片片地从银签上取下来，整齐地装在盘中即好。热炒和冷盘两用。', NULL, NULL, NULL, '猪腿肉700克、猪肥膘165克、黄酒45克、白糖85克、酱油40克、五香粉少许，菱粉少许。');
INSERT INTO `recipe` VALUES (1075, 72, '桔烧巴', '1.将猪肥膘片成长1寸半，宽7分的长方片，放在用红糟、糖、盐拌和的卤内抓一抓，并放着腌一天。腌的时间不要过久或过短，过久了太咸，过短则不入味。\r\n2.将面粉、红粉、发粉放在碗里，加水调成薄糊。\r\n3.开温猪油500克，将猪肥膘一片片地放在薄糊里拖一拖，随即放入油锅，用旺火炸酥，捞出后浇上麻油，倒在盘里即好。', '呈红黄色，味咸甜而酥脆，形如一瓤瓤的桔子，因此称为“桔烧巴”。', NULL, NULL, '猪肥膘150克、红糟45克、白糖65克、盐少许、红粉少许、发粉少许、面粉130克、麻油少许。');
INSERT INTO `recipe` VALUES (1076, 72, '桔烧鳗', '1.将鳗鱼的肚皮剖开，去肚肠，洗清，取其当中的一段，约500克，再一剖为二，片成长1寸半、宽7分的长方片12片，每片再片成2片，但要留一边不能片断；猪肥膘切成长1寸3分、宽6分的长方片12片；红糟、糖、酒、盐调和在小碗里。\r\n2.将鳗鱼片放入用盐和酒调和料内浸一下；反膘片放在小碗调料里抓一抓，一片一片地夹在鳗鱼片的当中，鱼片四周抹上蛋白与面粉调和的糊，封住隙缝。\r\n3.开温火热猪油700克，将鳗鱼片放下去炸酥（约五分钟），泌去油，加麻油略炒即好。', NULL, NULL, NULL, '海鳗鱼肉500克、猪肥膘75克、鸡蛋白4只、红糟45克、黄酒20克、白糖20克、盐少许、面粉75克、麻油45克。');
INSERT INTO `recipe` VALUES (1077, 72, '咖哩鸡', '1.将公鸡斩成块。用胡椒、盐入味放入油锅煎至金黄后，移入另一锅中；\r\n2.将碎洋葱、碎大蒜、咖哩酱炒至棕黄，加水、月桂树叶、糖搅匀，随即倒入鸡块于锅内调好口味，去皮水果切成细末投入锅内，烧沸出锅即可。', NULL, NULL, NULL, '公鸡一只，盐、胡椒粉、洋葱、大蒜头、咖哩酱、月桂树叶、白糖、水果各适量。');
INSERT INTO `recipe` VALUES (1078, 72, '咖哩牛肉', '1.将牛肉切成方块撒上盐，用油炸成黄色，放入焖锅里，加水、干红葡萄酒、酱油、葱头丝、胡萝卜片、香叶、胡椒粒、盐用小火焖2-3小时至牛肉酥烂；\r\n2.用煎锅将油烧热放葱头丁、芹菜段、香叶、蒜泥略炒，加面粉炒出香味，再加咖哩粉炒香，放少许辣椒面做成咖哩酱盛出。\r\n3.煎锅里放鸡汤烧开，放入咖哩酱、盐、味精调匀，再把熟牛肉倒入，稍焖，出锅装盘即成。', NULL, NULL, NULL, '牛腿肉500克，葱头、胡萝卜、芹菜各50克，咖哩粉15克，盐3克，干红葡萄酒、酱油备5克，面粉25克，鸡汤l00克，油50克，辣椒面、味精、香叶、蒜泥、胡椒粒各少许。');
INSERT INTO `recipe` VALUES (1079, 72, '扛糟鸡', '1.将鸡从背部剖开，去头，去脚，去内脏，洗清，再切成１寸半的方块。\r\n2.开热猪油（500克）锅，将鸡块放入锅内拉一下（约两分钟）后倒出，滤去油。\r\n3.将红糟、糖放入油锅内略炒，再将鸡块放入炒一下，随即将葱、姜片放入略炒，加上清汤、黄酒、盐，将鸡块烧熟，取去葱，姜，再下菱粉勾芡即好。', NULL, NULL, NULL, '嫩母鸡1只700克、红糟75克、葱1根、姜片1片、黄酒165克、白糖65克、盐、菱粉少许、 清汤600克。');
INSERT INTO `recipe` VALUES (1080, 72, '扛糟羊肉', '1.将羊肉切成长1寸半，宽1寸的块子。在开水锅内放葱、姜，将羊肉放入氽一氽，以拔除膻味，取出再洗净。\r\n2.开热猪油（300克）锅，将羊肉放入锅内氽一下，即倒入漏勺，滤去油，放好。\r\n3.另开热猪油锅，将红糟，糖，酒放入锅内略炒，再将羊肉放入略炒，然后加清汤，盐烧酥，即下湿菱粉勾薄芡，起锅装盘即好。', NULL, NULL, NULL, '羊肉700克、红糟75克、葱1根、姜片1片、黄酒165克、白糖45克、盐少许、菱粉少许、清汤700克。');
INSERT INTO `recipe` VALUES (1081, 72, '拉糟鱼块', '1.将鲜鱼洗净，切下头尾，待用。 \r\n2.用平刀从鱼背脊进刀片切取鱼肉2片（去脊骨），切去鱼鳍，去鱼皮，切成6×3×2厘米的鱼块。 \r\n3.红糟在砧板上用刀剁细放在大碗里，加料酒、白糖、葱姜末、五香粉调匀。 \r\n4.把切好的鱼块和调好的调汁拌匀腌渍15分钟。 \r\n5.用蛋清和干淀粉制成蛋清浆，将腌渍的鱼块上浆。 \r\n6.油锅置旺火上烧热，划油后加入花生油，五成熟时将上浆的鱼块逐块下油锅，炸熟后起锅，沥干油，装盘。鱼头尾下油锅炸熟置鱼盘两头，成整鱼形，淋上香油即成。', '糟香扑鼻，外红里白，肉嫩可口。', NULL, NULL, '鲜黄鱼1000克（其他肉嫩少骨带鳞鲜鱼亦可）。红糟25克，蛋清1个，干淀粉100克，花椒盐10克，白糖20克，味精、料酒、香油、五香粉、葱姜末少许，花生油1000克（约耗120克）。');
INSERT INTO `recipe` VALUES (1082, 72, '辣酱面', '1.炒锅烧热，油八成热时，放葱末、辣椒面，炸出香味，再放入猪肉丁煸炒片刻，加黄酱、黄酒煸炒，炒至猪肉熟，待肉与酱分离时，加白糖、清汤少许，再续炒片刻，加味精，淋上麻油即成辣酱；\r\n2.用开水锅下面条，面条熟后盛入大汤碗内，加辣酱即可。', NULL, NULL, NULL, '面条250克，猪肉丁150克，黄酱40克，辣椒面、葱末、黄酒、麻油、猪油、白糖、酱油、味精各适量。');
INSERT INTO `recipe` VALUES (1083, 72, '荔枝肉', '1.猪瘦肉切块，剞斜十字花刀，再切成斜形块，与荸荠片用湿淀粉抓匀。 \r\n2.清汤加调料调成卤汁。肉块与荸荠下锅炸2分钟，呈荔枝状捞出。', NULL, NULL, NULL, '猪瘦肉约300克，净荸荠片100克，蒜末3克，葱段15克，番茄汁50克。');
INSERT INTO `recipe` VALUES (1084, 72, '莲子鸡丁', '1.将莲子用热水氽熟，鸡脯肉切成斜丁，葱姜切片，鸡丁放入碗中，加入盐、料酒、味精、蛋清、淀粉搅匀上浆；\r\n2.将葱姜放入另一碗中，加料酒、盐、味精、水淀粉调成汁，待用；\r\n3.炒勺上火，入油放鸡丁煸炒，八成熟后，入胡萝卜丁，四季豆小段及莲子，翻炒均匀至鸡丁成熟，将碗汁迅速倒入锅中翻炒，使汁均匀挂在原料上即可。', NULL, NULL, NULL, '鸡脯肉250克，油50克，鸡蛋2只，白莲子，胡萝卜，四季豆，白糖，料酒，盐，味精，葱姜各适量。淀粉少许。');
INSERT INTO `recipe` VALUES (1085, 72, '溜鸽松', '1.将鸽肉切成小丁，放入用盐、酒调和卤内拌一拌，再放入打散调匀的蛋白中拌过，再用菱粉拌一拌。\r\n2.开温火热猪油（500克）锅，将鸽肉放入锅内拉一拉，倒入漏勺，滤去油。\r\n3.将冬笋、冬菇、地栗都分别切成小丁，与葱一起下原油锅略炒，随即将清汤加盐和菱粉调薄下锅勾芡，再将鸽肉小丁放入，迅速颠炒几下即好。', NULL, NULL, NULL, '鸽肉250克、冬笋75克、水发冬菇2片、地栗160克、鸡蛋白1只、葱末85克、黄酒20克、盐、菱粉、清汤少许。');
INSERT INTO `recipe` VALUES (1086, 72, '龙凤腿', '1.将地栗去皮后剁成细末，挤干水分；虾仁和鸡肉一起剁成茸；用蛋白一只打发，与地栗末、虾仁鸡肉茸放在一起，加少许盐拌和。\r\n2.将剩下的一只蛋白打发，加面料调成薄糊。\r\n3.将网油一块块地摊开，分别撒上一层干菱粉，再抹上鸡蛋面粉糊，糊上放虾仁鸡肉茸，然后分别卷成一只只像鸡腿的样子，每只腿的一端插一根鸭脚骨，作为腿骨，放入大热猪油锅内炸酥即好。', NULL, NULL, NULL, '虾仁250克、鸡肉250克、猪网油12块、鸡蛋2只、鸭脚骨12根、地栗300克、盐、面粉45克、菱粉20克。');
INSERT INTO `recipe` VALUES (1087, 72, '龙须燕丸', '1.将虾仁、腿肉、开洋一起剁成细末；地栗也剁成细末，挤开水分，与虾仁、腿肉、开洋末放在一起，加盐和少许清汤拌匀，做成40只丸子。\r\n2.将燕皮用少许清水洒一洒，再用手将它卷软，切成细丝，分别黏在丸子外面。\r\n3.用盘子一只，盘底抹上一层薄油，以防黏底。然后将做好的丸子排在盘中，上笼用旺火蒸熟（约五分钟），取出，用少许热汤冲一冲，使丸子不致过于干燥。4.用大碗盛好热清汤，将丸子推入碗内，撒上芹菜末即好。', '鲜嫩清口，洁白透明。用料“燕皮”是福建的特产。', NULL, NULL, '燕皮175克、虾仁175克、猪腿肉175克、开洋20克、地栗275克、芹菜末少许、盐7克、清汤750克。');
INSERT INTO `recipe` VALUES (1088, 72, '罗汉鸭', '1.将鸭子从背部剖开，去内脏，去脚爪，洗净，放入温火热猪油（1000克）锅一拉，倒去油，加酱油、盐、葱、姜、糖、清汤、盖好锅盖，烧约十分钟，取出鸭，放在盘中，再上笼蒸约五十分钟，取出，拆去大小骨头，放好。\r\n2.将冬菇片、菜心、栗子片、白果肉、冬笋片、扁尖笋段、腐竹放入锅内，加少许盐烧约五分钟，取出，塞在鸭肚里。\r\n3.将鸭仰放在盘中，再蒸约十五分钟，取出，覆在另一只盘中，背部朝上，用少许原鸭汤、菱粉下锅勾好芡，倒在鸭的上面，再浇上一些麻油即好。', NULL, NULL, NULL, '鸭子1只1400克、冬笋片165克、水发冬菇片、扁尖笋段、栗子片、腐竹、白果肉、菜心、葱段、姜片、黄酒、白糖、盐、酱油、菱粉、麻油、清汤适量。');
INSERT INTO `recipe` VALUES (1089, 72, '罗汉鱼首', '1.将鱼头去鳃，洗净切开（不要切断）。\r\n2.将豆腐，冬菇、鲜莲子、冬笋、扁尖笋、川冬菜都切成小粒，加盐拌和，塞入鱼头内部。\r\n3.开热猪油锅，将鱼头放入，炸至五成熟时取出。另将葱、姜、酱油、盐、酒、糖放入锅内爆一下，再将鱼头放入，加清汤，盖好锅盖，然后先起旺火，再转温火烧熟，取去葱，姜，再下湿菱粉勾薄芡，浇上麻油即好。', NULL, NULL, NULL, '花鲢鱼头（1000克）、豆腐1块、水发冬菇、鲜莲子、扁尖笋、冬笋、川冬菜、葱段、姜片、黄酒、酱油、白糖、盐、菱粉、麻油、清汤适量。');
INSERT INTO `recipe` VALUES (1090, 72, '马蹄鸡丁', '1.鸡肉切丁放碗里加鸡蛋、淀粉、面，抓成匀糊；\r\n2.勺坐油，炸马蹄捞出，下鸡丁划开片刻倒出。原勺放油、面酱、白糖、酱油、料酒、盐、味精，炒匀，倒入鸡丁、果仁，黄瓜片，红辣椒段，勾点芡，淋香油即可。', NULL, NULL, NULL, '鸡肉250克，马蹄75克，油1500克，面酱l0克，料酒15克，酱油15克，白糖40克，盐2克，味精5克，鸡蛋25克，水淀粉50克，面少许，香油l0克，小黄瓜一根，红辣椒2个。');
INSERT INTO `recipe` VALUES (1091, 72, '闽生果', '1.将花生米放在开水里泡一泡后取出，剥去皮，再放入热猪油锅内炸酥，然后取出冷却，但不要炸得过久过老，以免发苦。\r\n2.将五香粉、糖、椒盐放在一起拌和，冻猪油与花生米拌和，最后再将这些材料混合拌匀即好。', NULL, NULL, NULL, '花生米300克、冻猪油60克、白糖40克、五香粉少许、椒盐少许。');
INSERT INTO `recipe` VALUES (1092, 72, '牛筋煲', '1.牛筋用慢火炸透捞出，浸入清水少放许，然后用滚水提透待用；\r\n2.下油三汤匙，爆香姜，放入调味、牛筋、红枣、甘笋后煮开，再加入火腿慢火煮十分钟，勾薄芡。原煲上桌。', NULL, NULL, NULL, '牛筋l00克，火腿片，红枣六粒去核，甘笋、姜少量，上汤、蚝油、生抽、糖、盐、酒备适量，生粉、老抽、麻油、古月粉少许。');
INSERT INTO `recipe` VALUES (1093, 72, '七星鱼丸汤', '1.海鳗鱼杀好，和小尖刀刮取鱼绒，盛入盆中，清水500克加精盐4克，分5次添入鱼绒中，每加一次，用手往一个方向使劲搅动，使鱼肉泡涨起劲，加入淀粉调匀，至鱼绒捏成小丸放入水中能浮起即成。 \r\n2.虾仁洗净后制泥，猪肉剁成茸，同放一碗中，加精盐2克、味精1.5克搅匀，分捏成一个个1厘米的馅丸。 \r\n3.炒锅放中火上，盛入清水，用左手抓起鱼绒摊在掌心，右手挑入一个馅心，随后用左手的拇指和食指捏压，鱼绒包裹馅心，从指缝中压出包心鱼丸，用小匙舀入锅中，这样制成一个个浮于水面的鱼丸。锅改用旺火烧沸，把丸氽熟捞入汤碗中', '“七星鱼丸汤”是福建的名汤之一。成菜后，汤清如镜，鱼丸洁白，漂浮在汤碗面上，形似星头罗列，故名。', '制作鱼丸的鱼肉，一定要是新鲜的。鱼丸的成败主要由鱼的新鲜度和鱼肉的纤维所决定的。掌握好鱼肉、盐、水三者的比例。盐和水过少，鱼绒无粘性，汤易浑浊，反之，鱼丸易变硬沉底浮不起来。用干淀粉搅拌，鱼丸韧而有弹性，如用水淀粉搅拌，软而滑嫩，弹性稍差。', NULL, '净海鳗鱼肉600克，香油10克，鲜虾仁30克，精盐10克，干淀粉60克，高汤800克，五花猪肉60克，胡椒粉2克，味精5克，芥菜末5克。');
INSERT INTO `recipe` VALUES (1094, 72, '炝糟鸡脯', '1.将鸡脯去皮去筋，洗净，切成长1寸半、宽5分的薄片，先用盐抓，加上打匀的蛋白抓一抓，再加上菱粉抓一抓。\r\n2.开温火热猪油（500克）锅，将鸡片放入拉一拉，倒出， 滤去油。\r\n3.将红糟放入原油锅用旺火略煸加上白糖、酒一起煸一下，然后将清汤、菱粉、鸡片、蘑菇片迅速倒入。', '红黑绿数色相映成彩，鸡片外脆里嫩，有浓厚的红糟味，适宜于四季。', NULL, NULL, '鸡脯250克、红糟、鲜蘑菇片、鸡蛋白1只、鸡蛋黄2只、熟洋山芋、黄酒、黄瓜、盐、白糖、清汤、 菱粉。');
INSERT INTO `recipe` VALUES (1095, 72, '茄汁鸡肉', '1.将鸡肉横切成2厘米长的薄片，放入碗内，加小苏打、酱油l0克、胡椒粉、淀粉，绍酒、姜末和清水l00克，浸l0分钟后加入花生油，腌l小时；\r\n2.炒锅上火，油烧至六成热，放入牛肉片，拌和，待牛肉色白时，倒入漏勺沥油；\r\n3.炒锅内放少许油，将番茄酱、精盐、黄酒、白糖、味精、少许汤，用淀粉勾芡加麻油炒好淋在肉上即可。', NULL, NULL, NULL, '嫩鸡肉300克，番茄酱50克，绍酒20克，酱油20克，白糖l0克，小苏打5克，淀粉20克，胡椒粉l克，葱姜片20克，姜末2克，花生油500克，黄酒味精少许。');
INSERT INTO `recipe` VALUES (1096, 72, '茄汁牛排', '1.将猪排斩成15厘米宽、厚薄均匀的条，每条都要均匀地带上骨头；\r\n2.将猪骨条放碗里加入绍酒、酱油、味精、干淀粉和鸡蛋液，用手捏和均匀；\r\n3.炒锅上火，下油烧至八成热，投入猪骨炸制。猪骨放入时要四面分散以防粘连。全部投入后用漏勺翻动炸至呈金黄色，排骨浮起，用漏勺捞出，放入另一锅内加入番茄汁翻炒上浆，稍时令蔬菜出锅。', NULL, NULL, NULL, '猪排500克，绍酒、花椒盐备l0克，酱油20克，味精l克，干淀粉15克，鸡蛋1个，猪油l000克，番茄汁适量。');
INSERT INTO `recipe` VALUES (1097, 72, '茄汁猪排', '1.将猪排斩成15厘米宽、厚薄均匀的条，每条都要均匀地带上骨头；\r\n2.将猪骨条放碗里加入绍酒、酱油、味精、干淀粉和鸡蛋液，用手捏和均匀；\r\n3.炒锅上火，下油烧至八成热，投入猪骨炸制。猪骨放入时要四面分散以防粘连。全部投入后用漏勺翻动炸至呈金黄色、排骨浮起。', NULL, NULL, NULL, '猪排500克，绍酒、花椒盐备l0克，酱油20克，味精l克，干淀粉15克，鸡蛋1个，猪油l000克，番茄汁适量。');
INSERT INTO `recipe` VALUES (1098, 72, '青菜汤', '1.将菜心洗净并从中间剖开，把它切成长短一样的原料。葱切段，姜切块用刀略拍。\r\n2.锅中加入鸡、清水，再下葱、姜烧开后将白菜心放入，烧至八成熟取出，再将汤去掉放入鸡汤、盐、味精、料酒，烧开后将菜心、胡萝卜丝放入，再开锅即离火。', NULL, NULL, NULL, '嫩白菜心250克，胡萝卜丝l00克，好鸡汤l000克，盐5克，味精2克，葱、姜各5克，料酒5克。');
INSERT INTO `recipe` VALUES (1099, 72, '清炒海蚌', '1.将海蚌壳挖开，取出蚌肉，一切二片，去掉污质；将香菇撕成叶子形的块子；胡萝卜雕成蝙蝠形，再切成片子。\r\n2.用旺火热猪油（500克）锅，将蚌肉放入爆至八成熟，倒出，滤去油。再将青菜心、香菇放回原油锅，加上盐、酒迅速一炒，再加上清汤、菱粉勾芡，随即放进蚌肉，起大火再一炒（约一二分钟），起锅推在盘中即好。(不要炒过头，否则蚌肉一出水，就会发韧而嚼不烂。)', '红绿黑白四色，味极鲜嫩松脆，为夏天的当令菜之一。', NULL, NULL, '海蚌肉6只700克、青菜心125克、水发香菇30克、胡萝卜2只、盐少许、黄酒65克、菱粉少许、清汤少许。');
INSERT INTO `recipe` VALUES (1100, 72, '清炒芦笋', '1.将芦笋洗净，切成段，备用。\r\n2.炒锅内放底油，加入葱粒煸炒，并放入姜、料酒、醋、盐和味精，加入笋段，不停地翻炒，待笋段熟后加入溶于水的淀粉收汁，即可装盘。', NULL, NULL, NULL, '芦笋200克，葱粒l00克，盐适量，淀粉、料酒、醋少许。');
INSERT INTO `recipe` VALUES (1101, 72, '清炒虾仁', '1.将虾仁洗净，挑去虾线，加料酒、盐、淀粉拌匀。\r\n2.炒锅上火，舀入花生油，烧至五成热，放入虾仁略炸后用漏勺捞起沥油。炒锅再次上火，舀入花生油25克，投入胡萝卜丁、青豆略炒，再放入精盐、味精、豆芽汤烧沸，用水淀粉勾芡倒入虾仁中，淋上麻油颠匀，起锅装盘即成。', NULL, NULL, NULL, '虾仁300克，熟胡萝卜丁25克，青豆50克，花生油500克，精盐3克，味精l克，干淀粉50克，豆芽汤25克，水淀粉l0克，麻油15克。');
INSERT INTO `recipe` VALUES (1102, 72, '清炖全鸡', '鸡宰杀褪毛，从背部剖开，掏出内脏，洗净，在沸水锅中烫过，鸡腹部向上，头盘向身旁，脚剁去爪尖，屈于内侧，放入炖钵内，背上放香菇，加入精盐、味精、清水500克与少许绍酒，用棉纸或牛皮纸将炖盅封严，上蒸笼用旺火烧20分钟后改中火蒸2小时取出，移入汤碗上席。', '清炖全鸡是泉州传统名菜，泉州习俗结婚喜筵必有，象征完整，这一习俗到现在仍然沿袭。鸡肉的营养高于猪肉，特别是含脂肪较少，肌肉纤维较细，味道特别鲜美，历来作为滋补食品。清蒸全鸡，原汤原汁，不掺其它味道，鸡肉软烂爽滑，汤清汁甘，原味一点不漏，不油不腻，香醇鲜美。', '中火久蒸，使鸡入味。炖盅封严，保持原汁原味。', NULL, '肥嫩母鸡1只1000克，精盐6克，味精3克，水发香菇15克，姜片2克，绍酒20克。');
INSERT INTO `recipe` VALUES (1103, 72, '清蒸桂鱼', '1.将桂鱼杀好，去浮皮，去内脏，洗清，放入开水锅内川一下，取出，刮干净，鱼身两面开花刀，放在盘中。\r\n2.将冬菇片，冬笋片、开洋、葱、姜、猪油（45克）倒在鱼上面，再加盐、糖、酒、酱油、清汤，上笼蒸熟，取去葱、姜即好。', NULL, NULL, NULL, '桂鱼1条500克、开洋6只、水发冬菇片2片、冬笋片45克、葱、姜片、黄酒12.5克、白糖12.5克、酱油、盐、清汤165克。');
INSERT INTO `recipe` VALUES (1104, 72, '肉米鱼唇', '1.将鱼唇切成长1寸半、宽1寸的块子，放入开水锅内，加葱、姜、酒川一下，以拔除腥味；猪排也切成块，放入开水锅内氽一下，以拔除血水；将猪腿肉去皮，切块，剁成肉末。\r\n2.将鱼唇放在碗中，上面放猪排，上笼蒸约一小时后取下，猪排取去不要。\r\n3.开热猪油锅，将肉末放入锅内炒一下，随即将鱼唇，酱油，清汤，盐放入，再下湿菱粉勾薄芡，撒上胡椒粉，浇上麻油和醋即好。', NULL, NULL, NULL, '水发鱼唇600克、猪腿肉160克、猪排600克、葱1根、姜片、胡椒粉、黄酒、酱油、盐、醋、菱粉45克、麻油、清汤600克。');
INSERT INTO `recipe` VALUES (1105, 72, '肉松', '1.将猪腿肉去皮去筋，切成小块，放入开水里川一下，拔去血水后取出放好。\r\n2.将红糟、酒、糖放入热猪油锅略炒，然后将肉块放下一同炒透后，加上清汤，用温火慢慢地烧。待肉烧成浆糊状后（越烂越好），再用极小的温火边烧边炒，直至汤汁完全收干，肉料泡起发松，取出。', NULL, NULL, NULL, '瘦猪腿肉300克、红糟75克、黄酒75克、白糖45克、清汤700克。');
INSERT INTO `recipe` VALUES (1106, 72, '沙茶焖鸭块', '1.将鸭宰杀褪毛，去内脏洗净，放入沸水锅中稍氽取出，盛入砂锅，加清水1000克，酒10克、葱段、姜片，旺火煮沸，十五分钟后捞出。\r\n2.将鸭头、颈、翅膀、尾、脚掌剁下待用，鸭身剖成两片，分别切成3.5厘米长、0.8厘米宽的块。马铃薯去皮洗净，用刀修削成均匀的桔子状12个，下油锅炸三分钟捞起，再上笼旺火蒸十分钟至熟取出。\r\n3.炒锅微火烧热，先将蒜泥、辣椒粉、沙茶酱、酱油、白糖、味精、酒15克下锅稍炒，再放入鸭块翻炒五分钟，然后加猪骨汤、鸭头、颈、翅膀、尾、脚掌焖一小时三十分钟，最后放入香菇，再焖十分钟起锅', '色泽金黄，形状美观，沙茶酱独特的醇香，鸭块肉质软嫩芳香，吃时倍感味醇鲜美，甜辣爽口。', NULL, NULL, '鸭1只（1250克左右），沙茶酱125克、葱段15克、水发香菇25克、马铃薯12个、姜片5克、蒜泥25克、辣椒粉10克、绍酒25克、白糖15克、酱油50克、味精10克、湿淀粉50克、猪骨汤750克、熟鸡油10克、熟猪油750克（实耗100克）。');
INSERT INTO `recipe` VALUES (1107, 72, '沙茶牛肉', '1.将牛肉去筋络切成薄片，加小苏打、酱油、胡椒粉、水淀粉、绍酒、姜末和清水l00克，浸20分钟后，加花生油，腌l小时；\r\n2.炒锅入油，先将沙茶酱等配料下锅稍炒，再放入香菇勾芡，浇在牛肉上，淋上熟鸡油即成。', NULL, NULL, NULL, '牛肉300克，沙茶酱125克，香菇25克，蒜泥、绍酒备25克，酱油20克，花生油50克，白糖15克，酱油、湿淀粉味精、辣椒粉、鸡油备l0克，小苏打、胡椒粉l0克。');
INSERT INTO `recipe` VALUES (1108, 72, '沙锅鳝鱼', '1.冬菇浸软去脚烧腩切块。蒜肉用将滚之油炸黄捞起。鳝鱼洗净抹水斩块，加腌料腌二十分钟；沾满生粉用滚油大火炸黄捞起；约二分钟后重炸至金黄色捞起。生菜洗净，切短段放在煲仔内；\r\n2.下油一汤匙，爆香姜、蒜，下冬菇下酒半汤匙，加入调味煮滚，勾芡下烧肉、鳝鱼兜匀，倒到放有生菜之煲内，加盖大火煮滚，下葱，加油二汤匙即可。', NULL, NULL, NULL, '鳝鱼八两，烧腩二两，冬菇四只，蒜肉六粒，姜数片葱二条切丝，生粉酌量，生菜六两麻油，古月粉，盐。');
INSERT INTO `recipe` VALUES (1109, 72, '沙锅鱿鱼', '1.冬菇浸软去脚烧腩切块。蒜肉用将滚之油炸黄捞起。将水发鱿鱼剥净皮膜，切去头尾皮朝下平铺砧板上切成长方形的块，待用。生菜洗净，切短段，放在砂锅内；\r\n2.下油一汤匙，爆香姜、蒜，下冬菇下酒半汤匙，加入调味煮滚，勾芡下烧肉、鱿鱼块兜匀，倒落到已放有生菜的砂锅内，加盖大火煮滚，下葱加油二汤匙即可。', NULL, NULL, NULL, '鱿鱼八两，烧腩二两，冬菇四只，蒜肉六粒，姜数片葱二条切丝，生粉酌量，生菜六两麻油、胡椒粉，盐各适量。');
INSERT INTO `recipe` VALUES (1110, 72, '什锦鸡菜煲', '1.山根放入滚水煮软，捞起滴干水每只切开二件。云耳用清水浸软洗净放滚水中煮五分钟捞起滴干水，葱切二寸长，玉米笋、菜胆、胡萝卜切丝。\r\n2.鸡去皮，洗净抹干水斩块， 加腌料腌十分钟，泡油；下油二汤匙爆香姜、葱，下鸡兜匀，下酒二茶匙，熄火。\r\n3.在煲内下油一汤匙，下云耳、山根、玉米笋、菜胆、胡萝卜丝，加入调味煮滚，再煮片刻勾芡。下鸡及姜葱兜匀煮滚。\r\n4.用另一煲煲饭，熟后倒入鸡煲，再稍煲片刻，原煲上席。', NULL, NULL, NULL, '鸡十二两，葱十五条，姜十数片，云耳半两，山根十只，玉米笋、菜胆、胡萝卜，红枣，盐糖，生抽，老抽姜汁，酒，生粉，蚝油备适量。');
INSERT INTO `recipe` VALUES (1111, 72, '素炒鸡丁', '1.鸡肉用刀背捶后斩成蚕豆大的丁放入盘内，加入适量黄酒、味精、精盐、蛋清、干淀粉抓拌上浆待用。青椒切成蚕豆大的丁。\r\n2.炒锅放油，烧至五、六成热，倒入鸡丁，搅散，待鸡丁呈白色，捞出沥干油待用；\r\n3.炒锅内放油，油热，放入青椒略煸炒，倒入鸡丁，加入适量精盐、味精、鸡汤，用湿淀粉勾芡，翻炒几下即可。', NULL, NULL, NULL, '净鸡肉250克，鸡蛋1只，青椒50克，植物油、淀粉、精盐、黄酒、味精适量。');
INSERT INTO `recipe` VALUES (1112, 72, '酸菜牛', '1.用芡汤、糖、醋与湿淀粉调为芡；\r\n2.烧锅入油，牛肉放入，拌炒至仅熟放入漏勺里；\r\n3.锅中留油，将配料、酸菜片放入锅中炒香，放入牛肉炒匀，淋入绍酒随即把碗芡倒入炒匀，淋入麻油上碟即可。', NULL, NULL, NULL, '腌牛肉250克，酸菜茎薄片200克，以葱自、辣椒、蒜茸为配料，油味料等适量。');
INSERT INTO `recipe` VALUES (1113, 72, '台湾炒饭', '1.用油起锅，将鸡蛋放在锅中，加入精盐、味精、白饭炒透；\r\n2.把叉烧细丁、熟虾肉、葱花、冬菇丁等放入炒匀上碟即可。', NULL, NULL, NULL, '熟虾肉40克，叉烧细丁80克，冬菇丁20克，葱花l0克，净蛋80克，油20克，精盐2克，味精2克，白饭300克。');
INSERT INTO `recipe` VALUES (1114, 72, '豌豆鸡丝', '1.鸡肉切丝放碗里加蛋清、淀粉，抓匀糊。豌豆焯一下；\r\n2.勺坐油，等油三四成热下鸡丝，划开后倒出；\r\n3.坐勺放料酒、汤、盐、糖、鸡、豌豆、味精，开后除沫，勾芡，盛在场碗里即可。', NULL, NULL, NULL, '鸡脯肉200克，豌豆50克，油1500克，蛋清30克，料酒25克，盐2克，味精5克，白糖30克，水淀粉60克，汤500克。');
INSERT INTO `recipe` VALUES (1115, 72, '五彩虾松', '1.鲜虾洗净去壳、去肠泥、冬菇去蒂、红萝卜去皮。 \r\n2.虾肉用白毛巾轻轻压干水份，切成三粒米大小的粒；冬菇、冬笋、红萝卜、青椒、葱白切成与虾粒相等的丁。蒜头剁成末。 \r\n3.虾粒与鸡蛋清、味精、干淀粉拌匀腌渍待用；冬菇丁、冬笋丁、红萝卜丁、青椒丁、蒜米、精盐、味精、白糖、料酒、胡椒粉、骨汤、湿淀粉调匀待用。 \r\n4.炒锅放旺火上，下生油五成热时将腌渍的虾粒下锅，用筷子拨散。待虾成白色，倒进漏勺，炒锅留余油，倒入调匀的调配，再入虾粒，颠翻几下，淋上香油、装盘。', NULL, NULL, NULL, '主辅料：鲜虾250克，净冬笋25克，葱白1根，水发冬菇1朵，青椒半个，红萝卜25克。调配料：鸡蛋清半个，蒜头2瓣，干淀粉10克，湿淀粉5克，白糖、精盐适量，味精、料酒、胡椒粉少许，骨汤75个，生油500克（耗75克）。');
INSERT INTO `recipe` VALUES (1116, 72, '西瓜盅', '1.将西瓜洗净，在四分之一处打一圈人字花刀，将顶端取下，挖出瓜瓤在瓜皮上刻成花纹。将西瓜瓤去籽切成l厘米见方的丁。菠萝、荔枝也切成l厘米见方的丁；\r\n2.铝锅上火，放入清水，加入白糖煮开，撇去浮沫，下入桂花，晾凉，放入冰箱。\r\n3.将西瓜丁、菠萝丁、桔子瓣、荔枝丁装在西瓜壳内浇上冰凉的白糖水即成西瓜盘，放在用西瓜刻好的座上即可。', NULL, NULL, NULL, '西瓜，菠萝、荔枝88克，清水800克，白糖280克，桂花3克，桔子瓣88克。');
INSERT INTO `recipe` VALUES (1117, 72, '虾仁饭煲', '1.先用饭煲煲米饭；\r\n2.下油二汤匙，爆香姜，葱，下虾仁及油菜段、火腿片，加入调味煮滚，铲起放在煲好的米饭上。将虾、蔬菜等与米饭搅拌后，稍煲片刻，煮滚原煲上台即可。', NULL, NULL, NULL, '大米3两，虾仁3两，油菜二棵，火腿适量，酒一汤匙，磨鼓半汤匙，姜数片，生抽、盐、古月粉少许生粉半茶匙，葱少许。');
INSERT INTO `recipe` VALUES (1118, 72, '虾仁汤河粉', '1.把河粉发松，银芽洗净，滴干水；\r\n2.下油，放入银芽炒两下，加入河粉炒匀下鸭蛋炒熟，加入调味炒匀，最后加入虾仁、韭菜、盐炒匀上碟即可。', NULL, NULL, NULL, '河粉一斤，虾仁三两，韭菜三两，切短段，银芽二两，鸭蛋二只打匀，油三汤匙，盐适量。');
INSERT INTO `recipe` VALUES (1119, 72, '香油石鳞', '石鳞洗净，留腿取出骨，加味精、葱、姜腌制，再挂上干淀粉待用，锅烧热加入香油，待油热后放入石鳞腿炸熟，取出装盘、青椒切叶形，与红绿樱桃一起做点缀。', '外脆内鲜嫩，有香油味，是福建风味菜。', NULL, NULL, '石鳞20只，干淀粉10克，青椒、红、绿樱桃各2只，香油适且，味糟、葱 、姜各少许。');
INSERT INTO `recipe` VALUES (1120, 72, '一品鲳', '1.将鲳鱼剖腹去内脏洗净，鱼身两侧分剞十字花刀，中间横拉一刀。用酱油5克、绍酒10克腌渍一下，再滚匀干淀粉。\r\n2.锅置旺火上，注入花生油烧至八成热，将鲳鱼下锅炸成金黄以，倒进漏勺沥去油。 \r\n3.炒锅留余油15克旺火上烧热，下蒜瓣煸香，再加入骨汤、香醋、味精、白糖、咖喱粉、胡椒粉、芝麻油、酱油、绍酒煮沸，然后放入过油鲳鱼，改用微火煨至汁粘，装盘即成。盘边可饰配番茄片或萝卜丝、葱丝佐食。', '鲳鱼为优质鱼类之一。其鳞细、肉嫩，特别是骨软而香。鲳鱼有黑白两种，颜色灰黑而本略肥者称黑鲳；颜色银白而体略扁者称白鲳，烹制一品鲳宜用黑鲳。福州菜凡是形圆者，多称“一品”。“一品鲳”系采用整条鲳鱼煨制而成，其扁而略圆，质细嫩，味香鲜甘美。', '鱼在宰杀时，鲳鱼尾为了美观，切忌修得光秃。炸鲳鱼时，要注意两个问题。一是干淀粉定要滚匀，否则会出现爆油现象，易伤人。二是鱼炸的软硬要适度，炸的软，煨制后不易取出；炸的硬，煨制后，鱼还较挺，在不到菜品的要求。', NULL, '鲳鱼750克，干淀粉15克，白糖20克，胡椒粉0.5克，酱油15克，猪骨汤100克，花生油500克，蒜瓣10克，绍酒20克，咖喱粉0.5克，香醋20克，味精5克，芝麻油1克。');
INSERT INTO `recipe` VALUES (1121, 72, '银耳川鸭', '1.银耳用水浸一小时，去蒂及剪成小朵，烧滚水，放入姜、葱及银耳煮五分钟盛起，控干水份放于深碟中。\r\n2.鸭 洗兆，放入姜，葱水内煮两分钟，除去 衣，以一汤匙酒拌匀腌片刻，飞水盛起，滴干水份放于银耳上，加入上汤，隔水蒸三十分钟，倾出汤不要。\r\n3.下少许油炒红萝卜花，加入芡汁料煮滚，淋在鸭及银耳上即成。', '是一款美观精巧，味道清鲜的菜式。', NULL, NULL, '银耳半两、鸭六两、红萝卜花数片、姜、葱、上汤上杯、芡汁料：盐、糖、老抽、生抽、生粉、上汤、麻油。');
INSERT INTO `recipe` VALUES (1122, 72, '银杏芋泥', '1.银杏去壳、去衣，洗净，加入冰糖、清水慢火煮至一杯，取起银杏，糖水留用。\r\n2.槟榔芋去皮洗净，切大件，隔水蒸粘，趁热压成芋茸。\r\n3.烧热锅，下油四汤匙爆香葱弃去，放入芋茸，逐少加入糖水，炒至芋茸软滑盛起，排上银杏即成，可放进切柜雪冻或趁热食用。', '用料简单经济，香甜美味，最适宜作为饭后甜品。', NULL, NULL, '银杏二两、槟榔芋约1.5斤、葱三棵、冰糖六两、清水两杯。');
INSERT INTO `recipe` VALUES (1123, 72, '油条西舌', '1.将西舌肉一只一只地片成2片；油条切成小斜块。\r\n2.开温火热猪油（500克）锅，将油条放下炸酥，捞出，放在盘中打底。将西舌片投入旺火大猪油锅一拉，滗去油，随即将酒冲入，并用勺轻轻压干西舌的水分，接着将葱放入略炒加少许清汤、盐、酒、再加菱粉勾好薄芡，起锅倒在油条上面即好。', '味甘脆，但因水分较多，只宜烹制炒菜和汤菜，不宜炸，炸后肉易萎缩。', NULL, NULL, '西舌肉700克、油条2条、葱段20克、黄酒75克、盐少许、菱粉少许、清汤少许。');
INSERT INTO `recipe` VALUES (1124, 72, '鱼腩煲', '1.南乳磨鼓加水2汤匙搅匀。鱼脯放入滚水中煲滚，慢火煲，约需一小时至二小时后取起，待完全冷后切块煲鱼脯汤待用；\r\n2.下油三汤匙，爆香姜、蒜、南乳、磨鼓加入鱼脯，再爆片刻，下酒加调味慢火焖至入味勾芡，下葱、香菜原煲上台即可。', NULL, NULL, NULL, '鱼脯一斤，萝卜一斤，姜数片，蒜茸一汤匙，南乳半汤匙，磨鼓一汤匙，葱三条切段，酒2汤匙，香菜2棵，豆腐，盐，糖，生抽，老抽，果皮八角，生粉各适量。');
INSERT INTO `recipe` VALUES (1125, 72, '糟片鸭', '1.将熟鸭去头、脚和大骨，切成4大块。锅置中火上，下花生油（25克）烧热，先将姜末、香糟下锅煸香，再加入鸭块、白糖、五香粉、绍酒、酱油，改用微火焖20分钟后，调以味精拌匀，起锅装入盆中，滗去汁，加入干淀粉拌匀。\r\n2.锅置旺火上，下花生油烧至六成热时，将浆粉的鸭块下锅翻炸至酥，倒进漏勺沥去油，淋上芝麻油，然后切成5厘米长、0.33厘米宽的薄片装盘。香菜择洗干净，饰配盘边即成。', '色泽淡红，皮酥肉嫩，糟味馥郁芳香，甘鲜爽口。', NULL, NULL, '熟肥嫩鸭1只（约重500克），香菜50，姜末1克，干淀粉50克，香糟50克，五香粉0.5克，绍酒15克，白糖15克，白酱油15克，味精0.5克，芝麻油0.5克，花生油750克（约耗60克）。');
INSERT INTO `recipe` VALUES (1126, 72, '炸八块', '1.将鸡杀好，去毛，去内脏，再将它的胸脯起下，斩成４块，两只腿也斩成四块，其他不要。然后将鸡块放入用糖、酒、酱油、葱、姜调和的卤内拌一拌，放着腌鸡二十分钟，再加菱粉拌一拌。\r\n2.开温火热猪油（500克）锅，将鸡块放入锅内炸呈金黄色后，泌去油，即将酱油、糖、麻油、酒放入一滚即好。', NULL, NULL, NULL, '嫩母鸡1只，500克、葱末少许、姜米少许、黄酒20克、白糖6.5克、酱油少许、菱粉6.5克、麻油20克。');
INSERT INTO `recipe` VALUES (1127, 72, '炸鸡排', '1.在鸡腿肉上直划一刀，除去腿骨使腿肉分开，平摊在案板上，去鸡皮两面用刀背轻轻拍几下，用胡椒粉、精盐拌和一下两面再拍上面粉。鸡蛋磕在碗里，用竹筷搅拌后，均匀地涂抹在鸡肉上再拍上面包屑；\r\n2.炒锅上火烧热油烧至六成热，将鸡排放入炸至酥脆漏勺捞起，改成菱形块装盘即可。', NULL, NULL, NULL, '鸡大腿4只，鸡蛋1只，干面粉20克，精盐3克，面包屑50克，胡椒粉2克，熟猪油500克。');
INSERT INTO `recipe` VALUES (1128, 72, '芝麻豆腐', '1.将豆腐切成l厘米见方的小丁，用温开水略焯。牛肉剁成末。蒜苗切成l厘米长的小段；\r\n2.炒锅加油烧热下牛肉末炒散，至颜色发黄时，加精盐、酱油同炒，再放鲜汤，下豆腐块最后放入蒜苗用湿淀粉勾芡，浇少许熟油，出锅装盘，撒上芝麻即成。', NULL, NULL, NULL, '黄豆嫩豆腐300克，黄牛肉、菜油、蒜苗各l00克，芝麻、花椒面各15克，酱油、湿淀粉备20克，精盐l0克，鲜汤200克。');
INSERT INTO `recipe` VALUES (1129, 72, '猪排汤河粉', '1.把河粉发松。炸好的猪排滴净油，切宽丝，银芽洗净，滴干水；\r\n2.下油三汤匙，放入银芽炒两下，加入河粉炒匀，下鸭蛋炒熟加入调味炒匀，最后加入韭菜、猪排丝、盐炒匀上碟即可。', NULL, NULL, NULL, '河粉一斤，瘦猪肉三两，韭菜三两切短段，银芽二两，鸭蛋二只打匀，油三汤匙盐适量。');
INSERT INTO `recipe` VALUES (1130, 72, '紫盖肉', '1.将五花肉修齐，理清，放入开水锅里氽一下，以拔去血水，取出，抹上酱油。2.开大热猪油锅，将肉放入锅内拉一下，滤去油，加上清汤、酱油、糖、酒、用小温火将肉烧透（约一小时），取出，抹上菱粉，再抹上用鸡蛋、面粉调和的薄糊。\r\n3.另开温火热猪油锅，将肉放入炸酥并呈紫色后取出，切片装盘即好。', NULL, NULL, NULL, '五花猪肉1块，500克、鸡蛋2只、黄酒125克、白糖975克、酱油165克、菱粉少许、面粉25克、清汤25克。');
INSERT INTO `recipe` VALUES (1131, 72, '醉排骨', '1.将排骨切成20多块长方块。\r\n2.将菱粉、酒、盐、蛋白各少许调和在一只小碗里；再将芝麻酱、番茄酱、辣酱油、咖喱粉、芥末、糖、酒、盐、胡椒粉、麻油、酱油、醋调和在另一只碗里。\r\n3.开温火热猪油（500克）锅，将排骨放入菱粉、蛋白糊里抓一抓，再放入油锅氽熟，然后将排骨取出，滤去油，盛在盘中。锅里的油也同时倒出。\r\n4.将大蒜头拍碎，下原热锅炒香，倒入调和盛有芝麻酱、番茄酱等调料的小碗里，一起浇在排骨上即好。色金黄，味香甜酸辣俱全。', '色金黄，味香甜酸辣俱全。', NULL, NULL, '猪大排骨500克、大蒜头、鸡蛋白2只、咖喱粉、芥末、胡椒粉、黄酒、白糖、酱油、辣酱油、盐、芝麻酱、番茄酱、醋、菱粉、麻油。');
INSERT INTO `recipe` VALUES (1132, 87, '3个蛋的蛋卷', '1.当制作番茄蘑菇或香草蛋卷时，炒好在蛋锅中，溶化黄油或油，加入葱，慢炒至开始蒸发，避免变色及糊；\r\n2.加入蘑菇、番茄或细香草（根据需要）并继续使之蒸发，加入调味品，从锅中取出，并放在边上备用；\r\n3.将锅用纸或布擦净，加入多一些黄油或油，加热，然后倒入蛋液，摆动并继续搅动锅，使其成为一个平蛋形饼；\r\n4.当混合物开始凝固时，加入备好的调料，由六点种至十二点种方向处折叠蛋饼，使边角沾好，并使表面成为金黄色；\r\n5.将其转入盘中，放入沙拉装饰及薯条。', NULL, NULL, NULL, '鸡蛋打好的3个、火腿、细丁50克、奶酪粉50克、蘑菇细丁50克、熏三文鱼，细丁50克、番茄，去皮，去籽50克、混和鲜香草丁，并炒过的10克、洋葱，去皮细丁，炒过25克、黄油或菜油20克、薯条1包、沙拉装饰1个、面包2个、份装黄油20克、盐调至够味、白胡椒调至够味。');
INSERT INTO `recipe` VALUES (1133, 87, '奥地利式烤牛肉扒', '将牛外脊切成10段，每段折成1.2厘米厚，撒盐，胡椒粉用油煎上色，平码在烤盘中备用。用黄油炒鲜蘑片及芹菜末，加入少量的清汤，盐、胡椒粉调剂口味在煎好的肉扒上，上面摆放西红柿片，再撒上计司末，入炉烤熟即可。', NULL, NULL, NULL, '净牛外脊1700克，油150克，黄油50克，鲜西红柿500克，芹菜100克，鲜蘑100克，计司100克，盐15克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1134, 87, '扒海鲜串配香草黄油', '1.将所有配料(海鲜)一起放入腌料过一晚，制成串，开始并用虾串，并串上甜椒，洋葱(两串一份包好)，肉房将完成以上步骤；\r\n2.将串放在小火的扒炉上，均匀扒好，避免糊，但两面要金黄，根据要求加入调味品；\r\n3.将辣番茄酱汁在炒锅中加热，另外单独加热红花米饭然后放在边上；\r\n4.将扒好的海鲜串放在盘中的番茄酱汁上，然后放一些青柠肉及香草黄油，放入米饭，蔬菜，装饰及其他料。', NULL, NULL, NULL, '三文鱼柳，块45克；雪鱼柳，块45克；大虾，去皮，无头，带尾45克；带子，冻45克；洋葱，丁15克；混合甜椒丁15克；柠檬香草腌汁30毫升；青柠香草黄油30克；红花米饭1包；时令蔬菜1包；时令配菜1个；番茄(辣)酱汁50毫升；盐调至够味；白胡椒粉调至够味；青柠半个；沙拉装饰1个；面包2个；份装黄油20克。');
INSERT INTO `recipe` VALUES (1135, 87, '扒龙虾配榛子油', '1.由龙虾壳上取出龙虾肉并去掉肠，小心保持龙虾形的完整将其用汁浸泡5-10分钟；\r\n2.洗净虾壳，洗掉所有多余水分，然后在内侧轻刷上榛子肉，将腌好的虾放回，用腌汁浇在上面；\r\n3.将肉放在上面，肉向上放入160度－170度的烤箱15-20分钟，不断在上面烧汁，并在最后10分钟时浇上榛子油；\r\n4.将其取出，并摆好烩饭，装饰及配料。', NULL, NULL, NULL, '半只龙虾，800克-1公斤，去肠，稍微浸泡5-10分钟、洋葱细碎100克、大蒜细碎30克、黄油100克、榛子油150克、洋子苏细碎10克、香菜，细碎10克、小葱细碎5克、辣红粉7克、切割的西柚肉及汁120克、喔彻斯特汁5毫升、干雪利酒50毫升、西柚皮屑，漂白10克、装饰、榛子，细碎2克、烩饭（辣椒和蘑菇）1包、时令沙拉1个、沙拉汁40毫升、沙拉装饰1个、面包卷2个、简装黄油20克。');
INSERT INTO `recipe` VALUES (1136, 87, '扒美国肉眼', '1.用盐及胡椒调味牛肉，并涂少许橄榄油，柠檬汁和屑，香草，浸泡6小时左右；\r\n2.根据要求，使牛排扒上条纹，(炖五成或全熟)并用油及柠檬汁不断刷在肉上；\r\n3.另外，将土豆、蔬菜及扒番茄准备好，与装饰配料摆在盘中。', NULL, NULL, NULL, '肉眼排，带骨修净375克、绿胡椒胡椒、黄油30克、柠檬汁20毫升、柠檬屑，焯过的2克、橄榄油20毫升鲜萝丝玛林，碎1克、百里香，碎香1克、洋紫苏，鲜1克、阿里根农，鲜碎1克、大蒜细碎5克、意大利炸土豆1包、时令蔬菜1包、香草黄油汁40毫升、面包2个、份装黄油20克、扒制番茄1个、盐调至够味、黑胡椒，碎调至够味、沙拉装饰1个。');
INSERT INTO `recipe` VALUES (1137, 87, '扒酿馅小牛排', '1.用刀将牛排切为一个袋，大约深3厘米，长4厘米，填入少许调味品及蘑菇酱；\r\n2.在肉表面涂少许酱，胡椒，柠檬屑，香草，大蒜，油及芥末，浸泡20分钟；\r\n3.根据要求将其两面扒纹并制成所需火侯；\r\n4.用另外一只锅炒土豆；然后放在餐盘中央，小牛排放在土豆上，小心地用勺将汁淋上，同样地淋上香草黄油并用明火烤稍微上色；\r\n5.放上熏肉及洋紫苏，摆上装饰及配料。', NULL, NULL, NULL, '小牛柳排225克、野蘑菇酱30克、瘦的熏肉片（意大利）25克、干番茄及热的洋紫苏汁50毫升、土豆饼（15厘米）1个、时令沙拉1个、沙拉汁40毫升、法香汁1个、香草黄油汁25毫升、面包2个、份装黄油20克、盐调至够味、黑胡椒粉调至够味、焯的柠檬屑2克、混合香草1克、橄榄油20毫升、英国芥末20克、柠檬汁20毫升、大蒜酱5克。');
INSERT INTO `recipe` VALUES (1138, 87, '扒新西兰羊排配黄油及冷番茄酱', '1.用盐、胡椒、芥末、萝丝马林、百里香及马左林调味羊排，并挤上柠檬汁；\r\n2.将羊排放在扒条炉上并两面印上交叉条纹，加热至五成熟(或稍长3-4分钟，如果需要全熟的)；\r\n3.肉制好后，排在冷番茄酱上，并在每片肉上各放一片辣黄油，排好其余配料。', NULL, NULL, NULL, '新西兰羊排(4片)320克，辣黄油30克，冷番茄酱50毫升，盐调至够味，黑胡椒粉调至够味，地壮或英式芥末20克，蒜酱10克，萝丝马林（干或鲜）1克，百里香（干或鲜）1克，马左林（干或鲜）1克，橄榄油15毫升，柠檬1/2个，薯条或其他土豆1包，时令蔬菜1包，时令配沙拉1个，扒番茄1个，沙拉装饰1个，沙拉汁40毫升，面包2个，份装黄油20克。');
INSERT INTO `recipe` VALUES (1139, 87, '扒鸭胸配青柠花生汁', '1.把鸭胸先煎一下，然后翻过来放在热锅上加少许油炒1分钟；\r\n2.把鸭胸放在烤盘上，放入口袋中150度的烤箱中等凉后拿下来，使内部带一层粉红色；\r\n3.分别把沙拉和装饰品放在盘子中；\r\n4.冷却，在40度-45度时可以切开，在盘里放些生菜，小心的用勺把青柠汁和花生汁放到肉上；\r\n5.酸柠和花生酱的做法：把所有的料放到打碎机打碎；可以放到冰箱里保存24小时，用时在打碎机里搅拌一下。', NULL, NULL, NULL, '鸭胸120克、日本汁50毫升、绿色蔬菜色拉50克、小西红柿30克、香料5克、青柠花生汁40毫升、花生料汁0.6升、花生汁300毫升、青柠汁100毫升、柠檬汁50毫升、黄油30克、醋50毫升、蜂蜜毫升、小洋葱45克、红胡椒粒5克、香料5克、小葱碎5克、香菜碎5克、蒜蓉10克、盐调至够味、白胡椒调至够味。');
INSERT INTO `recipe` VALUES (1140, 87, '扒猪排配香草', '1.在羊排上调味并均匀的涂上盐及胡椒，芥末，萝丝马林，百里香，阿里哏农，杨紫苏，辣椒及茴香籽，加入油及柠檬汁，几分钟后放在扒炉上。\r\n2.将两面扒上交叉痕后，将其放在一小烤盘上，烤箱(160度)，烤10-15分钟。\r\n3.当制作肉时，边上将蔬菜扒好，并印上条纹，小心不要过火。\r\n4.另外，在锅中加热意大利汁酱，放在盘中使其成为池状，将肉放在中央，将蔬菜(包括意大利炸土豆)装饰排好在盘中。', NULL, NULL, NULL, '猪排一块，带一块骨200克、橄榄油10毫升、萝丝马林，干的1克、百里香干或鲜1克、阿里哏农干或鲜1克、杨紫苏，干或鲜花克、辣椒籽，干或鲜1克、茴香籽，干或鲜1克、芥末英式20克、蒜酱10克、盐调至够味、黑胡椒粉调至够味、意大利炸土豆(角，皮)1包、时令蔬菜1包、时令配蔬菜1个、沙拉装饰40毫升、意大利酱汁50毫升、面包2个、黄油20克、沙拉装饰1个、柠檬(浓缩汁)1/2个。');
INSERT INTO `recipe` VALUES (1141, 87, '白法西红柿', '将西红柿洗净、切片，黄瓜切菱形片。炒锅上火，加沙拉油烧热，投入葱头末、姜末煸出香味，放西红柿片炒熟，加盐、味精黄瓜片炒匀，淋水淀粉、炒匀盛入盘内，撒上熟芹菜末即成。', NULL, NULL, NULL, '西红柿400克、黄瓜150克、葱头未、姜末、熟芹菜末、盐、味精、水淀粉、沙拉油各适量。');
INSERT INTO `recipe` VALUES (1142, 87, '白毫牛腩', '白毫乌龙茶以600克热开水泡开，取茶汤；素火腿、猴头菇分别煎至香味溢出，然后将素火腿排放在碗中央，猴头菇排两旁。霉干菜洗兆切碎炒香，加入泡好的300克茶汤和调味料炒匀至入味，倒入碗中。上笼蒸40分种，取出扣入盘中。炒锅把辣油和面粉炒香后，加剩下的300克茶汤和调味料，（辣油和面粉不再加入），勾兑芡汁淋在盘中，青菜心烫好围边即可。', NULL, NULL, NULL, '主材料：白毫乌龙茶3克、猴头菇250克。配料：霉干菜60克、素火腿8片、青茶心8棵。调味料：酱油1茶匙、糖1汤匙、姜未2克、辣油1汤匙、盐2/3茶匙、糖1汤匙、面粉2/3茶匙、淀粉2/3茶匙。');
INSERT INTO `recipe` VALUES (1143, 87, '白毫乌龙烤青蟹', '先将青蟹洗净处理好，切成数块；洋葱切成条状，大蒜头切片备用。以2大匙苦茶油和白毫乌龙茶叶用小火熬出茶油。将锡箔纸摊平，泮葱丝铺在上面，再放大蒜头片，接着将盐、酒、黑胡椒加入。最后淋上熬好的茶叶和苦茶油。将锡箔纸包起来进烤箱烘烤，以350度烘烤20分钟即可。', NULL, NULL, NULL, '主材料：青蟹2只、白毫乌龙菜叶1克。配料：大蒜头4瓣、洋葱1个、锡箔纸2张。调味料：苦茶油2大匙、盐2/3茶匙、酒1茶匙、黑胡椒少许。');
INSERT INTO `recipe` VALUES (1144, 87, '白脱柠檬布丁', '柠檬刨皮炸汁。糖粉加油脂搅松，渐渐加入蛋搅匀，拌入牛奶、过筛的面粉和发粉、柠檬汁和柠檬皮拌匀，放入抹过油的串心模内，表面用防油纸封盖，旺火蒸45分钟，出笼，覆于银盘上浇柠檬沙司即可。', NULL, NULL, NULL, '低筋粉、糖粉、油脂、蛋、牛奶、发粉、吉士粉、蛋黄。制胚：低筋粉:糖粉:油脂:蛋:牛奶:发粉=1:0.6:0.6:0.6:0.25:0.06；制柠檬沙司：牛奶:糖:玉米淀粉:吉士粉:蛋黄=1:0.2:0.1:0.03:0.3；将玉米淀粉、吉士粉及砂糖搅匀冲入煮沸的牛奶，搅匀后在火上烧至薄糊状，加入柠檬汁、柠檬皮。');
INSERT INTO `recipe` VALUES (1145, 87, '柏林猪酸白菜', '1.将白菜切粗丝，用15克盐腌后挤干水分。取葱头100克切丝用油炒黄。放白菜丝一起炒至半熟，放入不锈钢锅内加适量清汤、香叶l片及茵香籽炯之。当白菜炯熟时放糖、醋精调剂口味，并擦上生土豆末，微沸调剂浓度。待用。\r\n2.将猪腿肉切方块(每块250克)，放锅内加适量水，放盐15克、香叶4片、切碎的葱头250克、胡萝卜50克，煮至熟软改温火(必须加盖免得肉皮变色)。起菜时配以焖酸菜。', '量大实惠，味香不腻。', NULL, NULL, '带皮猪腿肉2500克，胡萝卜50克，葱头350克，芹菜100克，白菜500克，净土豆100克，香叶5片，糠25克，醋精10克，盐25克，茴香籽5克，油100克，清汤适量。');
INSERT INTO `recipe` VALUES (1146, 87, '板肉鸡蛋面片', '1.将1/2鸡蛋、面粉、盐、适量水放在一起拌匀成面团，擀成约1毫米厚的鸡蛋面片，再切成正方块面片，放入沸水煮熟取出控干；把板肉洗净切小片煮熟，备用。\r\n2.把板肉片、鸡蛋面片放入烤盘，浇上另1/2鸡蛋、牛奶，煮板肉汤混合成汁，加盐调好口味，撒上干酪末，浇上热黄油，放进210度的烤箱烤至上色即可食用。', '鲜香味浓。', NULL, NULL, '鸡蛋250克，面粉350克，牛奶200克，板肉250克。调料：黄油15克，干酪末25克，精盐适量。');
INSERT INTO `recipe` VALUES (1147, 87, '拌萝卜丝', '1.将洋葱洗净，切成丝，用生菜油炒至香脆、油黄为止，即为葱油少司。\r\n2.将萝卜洗净，切成细丝，加盐、糖、醋精、胡椒粉拌均，装入盘中央，成丘形。食用时，浇上葱油少司即可。', NULL, NULL, NULL, '萝卜175克、精盐5克、生菜油40克、洋葱25克、胡椒粉少许、醋精少许、糖5克。');
INSERT INTO `recipe` VALUES (1148, 87, '海虹虾配红花汁', '1.炒洋葱，大蒜，红花丝及小葱在一半橄榄油和黄油中直至炒出汁液，浓缩掉四分之一，然后加入鱼汤并小火煮至2-3分钟；\r\n2.加入香料酱，橄榄，海虹，盖盖直至壳打开，取出海虹肉将其余炒至30-60秒，过滤并保温；\r\n3.浓缩汁液三分之一，加入香菜、柠檬汁、调味品（包括炒面条的汁）；\r\n4.另外，在一只炒锅中加热其他油，并炒面条、海虹，搅好加入调味品；\r\n5.将在半只壳中的海虹放上，并撒上帕玛山奶酪，稍微烤一下，配以装饰及配料摆在盘中。', NULL, NULL, NULL, '洋葱细丁50克；大蒜细丁5克；橄榄油25毫升；红花丝4个；黄油10克；白酒50毫升；鱼汤60毫升；带壳海虹洗涤好并除去杂须的100克；虾，带尾去皮及肠、头的70克；番茄一只，去壳切为四分之一60克；黑胡椒，去核切成四分之一；香料酱20克；柠檬汁5毫升；香菜，碎2克；混合鲜面条160克；盐，碎调至够味；黑胡椒，碎调至够味；帕玛山奶酪40毫升；小葱，细碎2克；黄油份装20克；时令沙拉1个；沙拉汁40毫');
INSERT INTO `recipe` VALUES (1149, 87, '海绵拼花', '1.调制海绵卷：用S.P海绵蛋糕去调制糕胚，约0.02cm厚，表面刮一层苹果酱，向前推卷，切片。\r\n2.调制奶油胶冻：用清全利冻配料拌入打发的鲜奶油。\r\n3.将海绵卷整齐地铺满模子壁，再倒入奶油胶冻至模子口，进冰箱使其凝固，凝固后边缘修齐，覆于平盘上即可。', NULL, NULL, NULL, '苹果酱、清全利冻。');
INSERT INTO `recipe` VALUES (1150, 87, '海鲜汤', '1.黄油炒海鲜壳，洋葱及混合香草；\r\n2.加入大蒜，红粉和番茄酱，并炒一会，慢慢的搅入面粉并煮5分钟，不要使其焦糊；\r\n3.烹入白兰地，煮至几乎干掉；\r\n4.加入鱼汤并小火煮45分钟，用一细萝或粗布将其过滤；\r\n5.重新加热并加入浓奶油；\r\n6.黄油炒海鲜丁（虾，带子和蟹肉），加入汤小火煮5分钟，用盐，胡椒，喔彻斯特汁和辣椒籽汁加入雪利酒，使用时撒上雪利酒在汤杯中，倒入汤的3/4，放上一些小葱。', NULL, NULL, NULL, '海鲜壳（虾，龙虾或蟹）700克、洋葱碎450克、黄油60克、蒜碎50克、红粉50克、番茄酱60克、白兰地90毫升、面粉80克、鱼汤31/2升、浓奶油，温1升、带子，净，煮250克、虾，净煮250克、蟹肉，冻丁250克、混合香草30克、干雪利酒120毫升、喔彻斯特汁3毫升、辣椒籽汁3毫升、盐调至够味、白胡椒调至够味、小葱细丁3克。');
INSERT INTO `recipe` VALUES (1151, 87, '黑豆饼配炒蟹肉玉米', '1.在鸡汤中小火煮豆，火腿，香料包，洋葱，煮大约一个半小时，待豆软，排掉液体及香料包；\r\n2.将豆打为酱，加入鸡蛋面粉，加入盐，胡椒调料，并制为饼状；\r\n3.在清黄油中炒蟹肉及玉米粒，至热透，加入调味品及香菜，保温；\r\n4.在橄榄油中炒豆饼至热透，放在一个番茄酱汁上，上面配以蟹肉玉米，用香菜做装饰。', NULL, NULL, NULL, '黑豆浸泡340克、火腿关节熏的1.5公斤、香料包1个、洋葱细的115克、鸡汤3升、鸡蛋打的2个、面粉30克、盐2克、白胡椒1克、蟹肉，精选285克、玉米粒285克、橄榄油120毫升、鲜香菜，细碎2克、番茄酱汁450毫升。');
INSERT INTO `recipe` VALUES (1152, 87, '黑人鱼沙拉', '1.将蒸熟的蟹肉去骨取肉，放入碗内备用。\r\n2.煮熟的大虾凉透后，剥去皮壳和头尾，在脊背处割切开，取出沙线，并沿脊背部横切成两片，浸泡洗净后，调以精盐、白胡椒粉、洋葱和柠檬汁，也可用少许醋拌匀。\r\n3.土豆煮熟去皮、切丁、番茄去皮、籽切丁，同放碗内，加青豆和口蘑片，调以马油奶司、精盐、白胡椒粉，制成沙拉。\r\n4.在盘内垫上生菜，先将沙拉堆放在盘中央呈丘形，大虾片围贴在四周，挤上网形马油奶司，然后再在沙拉顶部均匀地铺上碎蟹肉，再用鲜橙、红樱桃等拼摆装饰即可。', NULL, NULL, NULL, '蟹肉50克、大虾100克、马油乃司25克、生菜50克、青豆15克、番茄15克、土豆15克、洋葱5克、口蘑15克、鲜橙（或白兰瓜）半只、精盐4克、白胡椒粉2克、柠檬汁5克、红樱桃2只。');
INSERT INTO `recipe` VALUES (1153, 87, '黑森林马铃薯色拉', '红皮马铃薯1、4千克；盐、胡椒少许；韦斯特菲火腿薄片150克；香芹菜7-10束；芫荽子酱汁；小红洋葱1个；陈醋3汤匙；酸奶油3汤匙；芥末酱2汤匙；芫荽子2茶匙；红花子油250毫升。\r\n1.煮熟马铃薯及调制酱汁\r\n（1）在流的冷水下用小刷清洗马铃薯，但不要刨去它的外皮。若用较大的马铃薯，则可将它切开2-4份再清洗。\r\n（2）将马铃薯放入一大锅加了盐的冷水中，待水煮沸后，再以慢火煮15-20分钟直至马铃薯变变软为止。与此同时，调制酱汁。\r\n（3）将红洋葱去皮，保留一小部分根部，然后将洋葱直切为2块，平放在砧板', NULL, NULL, NULL, '红皮马铃薯1、4千克；盐、胡椒少许；韦斯特菲火腿薄片150克；香芹菜7-10束；芫荽子酱汁；小红洋葱1个；陈醋3汤匙；酸奶油3汤匙；芥末酱2汤匙；芫荽子2茶匙；红花子油250毫升。');
INSERT INTO `recipe` VALUES (1154, 87, '红白卷心菜丝色拉', '撇去胡萝卜不用，照主食谱的方法，将白卷心菜刨成细丝。\r\n1.将1/2个红卷心菜(约重750克)照主食谱的方法刨成细丝。将一锅盐水煮沸后加入红卷心菜，待再煮沸后，改慢火煮1分钟。\r\n2.隔去红卷心菜的水分，趁卷心菜还热的时候，淋上4汤匙的陈醋，拌匀后放在滤盆中滤干。另照主食谱的方法将洋葱切碎。\r\n3.撇去苹果醋不用。将3个柠檬榨汁，汁液应有125毫升。以柠檬汁取代苹果醋，照主食谱的方法调制酱汁。将红白卷心菜丝放入一大碗内混匀。\r\n4.将酱汁倒入卷心菜丝中，搅匀后试味，照主食谱的方法将色拉盖好，放入电冰箱内冷', NULL, NULL, NULL, '白卷心菜、红卷心菜、陈醋、柠檬汁。');
INSERT INTO `recipe` VALUES (1155, 87, '红菜头沙拉', '将红菜头烤熟烤香后切块，淋上红酒醋汁及黑胡椒碎，配上羊奶酪及脆口的花叶生菜。', NULL, NULL, NULL, '红菜头、花叶生菜、羊奶酪、红酒醋汁。');
INSERT INTO `recipe` VALUES (1156, 87, '红茶煎腓力', '将宽5厘米的腓力圆块，用生培根围起，再用牙签固定住，以平底锅煎至约六分熟，摆盘。红茶以1碗热开水冲泡5分钟，将茶汤滤出备用；马铃薯蒸好并压成泥。大红辣椒、洋葱、洋茱、碎牛肉入锅炒香。加入2000克水及1大匙红酒煮至汤汁剩1/3时，将材料滤出，加入红茶汤。最后加鲜奶油和马铃薯泥与其他调味料同煮，拌匀调味后淋在煎好的腓力上即可。', NULL, NULL, NULL, '主材料：红茶2克、腓力600克。配料：培根6片、大红辣椒2只、洋葱1/2个、洋芹2根碎牛肉200克、马铃薯1个。调味料：红酒1大匙、鲜奶油1茶匙、酱油1茶匙白胡椒粉少许、糖1/2茶匙、盐1/2茶匙。');
INSERT INTO `recipe` VALUES (1157, 87, '红烩菜花', '将菜花拆成小朵，用盐水浸泡5-10分钟（杀死幼虫），然后洗净，用沸水煮烫5分钟左右，捞出并控去水分，将胡萝卜，芹菜，洋葱洗净，分别切成片，段，丝，先用热油炒洋葱丝，炒到微黄时放香叶，胡椒粒，干辣椒，胡萝卜片，番茄酱，再继续炒到油呈红色时，放鸡汤或水调匀，再放入煮烫过的菜花和芹菜段，沸后放盐，糖醋精，蒜调好口味，移文火上再微沸10分钟，倒耐酸器皿内，凉后即可食用。', NULL, NULL, NULL, '菜花500克，胡萝卜150克，西红柿150克，蒜25克，醋精10克，精盐5克，番茄酱125克，胡椒粒10粒，洋葱75克，香叶1片，芹菜50克，干辣椒20克，糖50克。');
INSERT INTO `recipe` VALUES (1158, 87, '红酒烩牛肚', '1.将牛肚用滚沸水烫片刻后，刮净粘液、黑膜，再用温水洗净切成菱形块；把芹菜、大蒜洗净切末；葱头、土豆、胡萝卜洗净切块，备用。\r\n2.将牛肚、香叶、芹菜末、精盐、红酒、胡椒粉放入焖锅拌匀倒入适量牛肉清汤，用文火焖至5成熟。把锅烧热后倒入食油，待油温6成时，放入葱头块炒至微黄，放入番茄酱炒至油呈红色时，盛入焖至5成熟的牛肚锅内，焖至8成熟时放入土豆块，胡萝卜焖至牛肚熟软时，放入炒黄的大蒜末，油炒面粉调匀，加盐调好口味微沸即可食用。', NULL, NULL, NULL, '牛肚1250克，芹菜10克，胡萝卜120克，葱头75克，土豆500克。调料：食油75克，红酒200克，香叶1片，大蒜25克，番茄75克，油炒面粉25面，精盐、胡椒粉适量。');
INSERT INTO `recipe` VALUES (1159, 65, '棒棒鸡', '1.用特制的小木棒将鸡脯肉、鸡腿肉轻轻拍松，撕成丝入盘，外围以葱白丝。\r\n2.将调料和匀调成味汁，浇在鸡丝上即成。', '“棒棒鸡”原为四川乐山地区的传统风味小吃，二十年代传入成都，已入名馔冷碟之列。因乐山古称“嘉定”，故此菜多以“嘉定棒棒鸡”闻名于世。棒棒鸡美味的烹制确别有技巧。首先妙在煮鸡，煮前要用麻绳缠上腿翅，肉厚处用竹扦打眼，使汤水充分渗透，以微火徐徐煮沸；二是以特制的木棒将煮熟的鸡肉拍松，撕成粗丝入盘，利于调料入味；三是以众多调料调成的味汁，浇于鸡丝上，使鸡丝分外鲜美香嫩，有浓郁的香甜、麻辣味。', NULL, NULL, '嫩鸡一只、芝麻酱、红油辣椒、糖、麻酱油、花椒粉、葱白丝适量。');
INSERT INTO `recipe` VALUES (1160, 87, '红腰豆甜粟辣椒色拉', '1.准备红腰豆\r\n（1）将红腰豆放进大碗内，并加入大量的清水浸上最少8小时。(另一种浸透红腰豆的方法，要将它放入一注满水的深锅内，煮沸，再以慢火煮30分钟，将水滤去，以冷水冲洗后再滤干。)\r\n（2）将洋葱去皮，把丁香粒钉在洋葱上。将已浸透的红腰豆放入煮锅内，放入香草束、洋葱和胡椒以及大量的清水，大火煮沸后再煮10分钟，改以慢火煮1-1.5小时直至红腰豆松软。在烹煮的中段可放适当的盐调味。从煮锅内取出数颗红腰豆，待冷却后用食指及拇指挤夹它，煮好的红腰豆应是松软的。将洋葱及香草束拿走，滤干水分，再用流动冷水清', NULL, NULL, NULL, '375克干红腰豆、1个洋葱、2粒丁香粒、1束香草束（包括5-6棵香芹菜梗，2-3根百里香及1片月桂叶）、少许盐、胡椒、4个新鲜粟米、1个红甜椒、1个青甜椒、1个黄甜椒、750克番茄、6片墨西哥玉米饼、1/4茶匙辣椒粉、辛辣油醋汁、1束新鲜芫荽、125毫升苹果醋、1/2茶匙小茴香粉、3个小青辣椒、125毫升红花子油（用于玉米饼及烤盘）。');
INSERT INTO `recipe` VALUES (1161, 87, '花式巧克力酥', '1.用混酥面团制底胚。\r\n2.调制巧克力：巧克力熔化，隔冷水调质至29度。\r\n3.两片底胚中间夹巧克力，表面被覆巧克力，在巧克力未完全干时，用纸卷装白脱奶油(或调质白巧克力)，裱圆或直线于表面，并迅速用尖签拉出花纹。', NULL, NULL, NULL, '巧克力、白脱奶油。');
INSERT INTO `recipe` VALUES (1162, 87, '欢喜饭团', '在半片烤紫外紫菜上铺上一层饭，芝麻和一点点椒盐，卷成圆桶状即可。', NULL, NULL, NULL, '半片烤紫菜饭芝麻或芝麻粉（或花生粉）。调味料：胡椒盐。');
INSERT INTO `recipe` VALUES (1163, 87, '皇式清汤', '1.将熟鸡肉，熟火腿切丁；鸡蛋煮老切丁；备用。 \r\n2.将鸡清汤倒入锅内煮沸后，加入精盐、胡椒粉调好口味，再放入鸡丁、火腿丁、鸡蛋丁稍煮。食用前倒入雪梨酒，撒上炸面包丁即可。', '清香味鲜。', NULL, NULL, '熟鸡脯肉150克，熟火腿肉150克，鸡蛋200克，炸面包丁少许。调料：雪梨酒100克，鸡清汤、精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (1164, 87, '黄玫瑰', '1.白脱油拌入柠檬汁，调成淡黄色。 \r\n2.糕胚用S.P海绵蛋糕，修成蛋形，横剖三层，中间嵌柠檬白脱油，顶面及垂直面刮柠檬白脱油，要挺直。糕胚:白脱油=4:6。  \r\n3.再用本色裱花装饰、点缀。', NULL, NULL, NULL, '白脱油、柠檬汁。');
INSERT INTO `recipe` VALUES (1165, 87, '茴香核桃香梨色拉', '1.烘脆核桃及调制酱汁\r\n（1）先将烤箱烧热至180度（350℉，煤气炉4度）。把核桃放在烤板上烘5-8分钟至松脆。烤时应不时将核桃搅动。\r\n（2）切去蓝芝士外面的干酩皮，用手指将芝士捏碎；若太松软，可用叉将它压碎。\r\n（3）将2/3的蓝芝士跟陈醋、盐和胡椒放入碗内，用打蛋器均匀地搅拌。\r\n（4）将橄榄油逐渐加入芝士酱汁内搅匀，令酱汁浓化呈乳状。另将余下的芝士放酱汁内，不要搅拌，以留下较大块的芝士碎片。酱汁试味后加盖放入电冰箱内冷藏。这时可预备色拉。\r\n2.准备及组成色拉\r\n（1）清理茴香的梗、根部及坚硬', NULL, NULL, NULL, '60克核桃粒，375克大茴香，1个625克成熟的梨，1个柠檬，高根疏拿蓝芝士酱汁125克，高根疏拿蓝芝士60毫升，陈醋、盐、胡椒少许，75毫升橄榄油。');
INSERT INTO `recipe` VALUES (1166, 87, '惠灵顿羊排', '1.先将整蒜放入油内煮熟，蒜肉用水煮透，10克做泥，另10克流着待用。\r\n2.把羊排略煎，抹上蒜泥和法式粒状芥末，包上酥皮入烤箱约10分钟左右。\r\n3.把青椒、红椒、白萝卜、胡萝卜均切成细丝待用，牛奶煮沸加入土豆粉成土豆泥。\r\n4.用煮好的蒜泥略炒，加入白葡萄酒和迷迭香，再放入羊肉汁煮沸即可。\r\n5.放上略热醋油汁及预先拌成的各色细丝即可。', NULL, NULL, NULL, '羊排200克、酥皮100克、牛奶200克、土豆粉120克、大蒜肉20克、整蒜一只、青椒、红椒、白萝卜、胡萝卜各10克，醋油汁15克、法式粒状芥末15克、橄榄油100克（蚝油20克）。');
INSERT INTO `recipe` VALUES (1167, 87, '火腿蛋卷', '鸡蛋液搅匀，火腿切丝用黄油煸炒出香味备用；煎锅烧热擦上黄油，倒入蛋液吊成厚蛋皮，放上火腿丝包成扇面形状，两面煎成金黄，抹上黄油出锅，配上拌西兰花即可。', NULL, NULL, NULL, '鸡蛋2枚，方火腿80克，黄油20克，西兰花100克。');
INSERT INTO `recipe` VALUES (1168, 87, '火腿杏仁糯米粽', '将糯米洗净，用水泡30分钟。杏仁泡透去皮，切成粒、火腿亦切粒。苇叶开水烫过。（取不锈钢盒放入糯米、火腿、杏仁、盐少许拌匀。用苇叶包成“小脚”粽子入水煮熟。捞起装盘便成。）', NULL, NULL, NULL, '糯米750克、加州杏200克、金华火腿100克、盐、苇叶。');
INSERT INTO `recipe` VALUES (1169, 87, '鸡蛋青椒沙拉', '1.葱头切末加生菜油，盐，醋精，胡椒粉，芥末酱，用适量的冷开水调匀成少司。\r\n2.青椒去蒂，籽洗净，消毒切丝放入盘中央，老鸡蛋去皮切丁，撒在青椒上面，浇上少司即可。', NULL, NULL, NULL, '青椒1000克，鸡蛋400克，葱头100克，生菜油50克，盐15克，芥末酱15克，胡椒粉10克，醋精10克。');
INSERT INTO `recipe` VALUES (1170, 87, '基础色拉', '1.土豆、胡萝卜洗净，放入锅内加水（水没过实物）煮熟，取出晾凉；土豆去皮，与胡萝卜一起切成小片或丁。 \r\n2.鸡蛋洗净，放水中煮熟，取出，用凉水冲凉，去皮，先一切两半，再切成小片或丁。 \r\n3.鲜黄瓜洗净，去皮去籽，切成小片或丁。\r\n4.以上各料和附料一起搅拌均匀，装入容器，放入冰箱即可。', NULL, NULL, NULL, '土豆750克，胡萝卜75克，鸡蛋150克，鲜黄瓜150克，豌豆50克； 附料 马奶司75克，奶油50克，盐适量，辣酱油10克，胡椒粉、味精少许；（其实用色拉酱就行了）');
INSERT INTO `recipe` VALUES (1171, 87, '忌司酥', '1.用清酥面团制底胚，擀成厚0.3cm的大片，表面刷蛋，将忌司在筛网上压成条形细粒，撒于表面，然后切割成各种形状，可以切成7cm×7cm方形，在每一边中点用夹子从中心夹成叶状，也可以切成7cm×7cm方形，在每一边中点用夹子从中心夹成叶状，也可以切成7cm×10cm的长方形，中央1/3处开一口，一边从中穿出成结状。 \r\n2.烤炉：炉温220度，约15分钟，呈金黄色。', NULL, NULL, NULL, '蛋，干乳酪。');
INSERT INTO `recipe` VALUES (1172, 87, '家庭拌素菜', '将土豆，胡萝卜，红菜头洗净，煮熟，去皮，切丁；将黄瓜洗净去皮，籽，切丁；将酸黄瓜去皮，籽，切丁；将洋葱洗净切丁；将鸡蛋洗净煮熟去壳，切丁；将以上各丁按不同颜色分另单独成堆放入盆内，浇上用奶油，盐，胡椒粉调好的沙司即可。', NULL, NULL, NULL, '土豆500克，鲜黄瓜200克，胡萝卜250克，酸黄瓜500克，红菜头250克，洋葱100克，鸡蛋250克，精盐15克，奶油250克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1173, 87, '煎扳鱼', '1.把板鱼去皮去骨，将鱼肉包成长方形，裹以打碎搅匀的鸡蛋浆和面粉，撒上盐、胡椒粉待用。\r\n2.取煎锅加入色拉油，用热油将裹好的鱼块煎至色黄后放人烤箱中烤熟，再取另一只煎锅把黄油溶化并着色，然后浇在鱼上面即可食用。', NULL, NULL, NULL, '板鱼l条，面粉50克，鸡蛋2只，盐、胡椒粉少许，黄油50克，色拉油少许。');
INSERT INTO `recipe` VALUES (1174, 87, '煎法式面包', '鸡蛋液加入牛奶少许搅匀，将面包放入蛋液中略浸泡待用；煎锅烧热放入食油适量，把面包片放入锅煎至两面金黄，涂上黄油少许出锅，配上事先消毒的生菜即可。若再抹上糖浆或果酱其味更佳。', NULL, NULL, NULL, '法式面包4片，鸡蛋2枚，牛奶少许，黄油20克，生菜叶两片。');
INSERT INTO `recipe` VALUES (1175, 87, '煎红酒丁骨牛排', '1.丁骨牛排先适当的调味； \r\n2.平底锅加热将丁骨牛排煎至喜欢的生熟度后，取出并置于主菜盘内； \r\n3.锅内炒洋葱、培根丝，软化后倒进红葡萄酒； \r\n4.酒精成份蒸发后加进少许黄奶油，调成肉汁。并适当的调味，使用时淋于丁骨牛排上即可。', NULL, NULL, NULL, '美国丁骨牛排一块400公克、(切丝)个30公克、红葡萄酒1/2杯。调味料：橄榄油1大匙，盐、胡椒适量，黄奶油少许。');
INSERT INTO `recipe` VALUES (1176, 87, '煎牛排', '1.将牛排切成10克重的小块，逐块用刀拍成1寸半直径的圆薄片，随即用刀斩几下，再把肉修圆，把牛排逐片放入用葱头汁、黄酒等调成的卤汁内拖一拖，取出腌五分钟，再用蛋糊涂拖；\r\n2.下热油锅两面煎成金黄色，烹上辣酱油和蕃茄沙司，翻几下出锅装盆，配以黄瓜片、土豆条，或生菜和蕃茄片上席即可。', NULL, NULL, NULL, '牛排，葱头汁，黄酒，鸡蛋，辣酱油，蕃茄沙司，黄瓜片、土豆条或生菜和蕃茄片。');
INSERT INTO `recipe` VALUES (1177, 87, '煎猪排', '1.将杏仁粒马蹄粒，用卡夫奇妙酱拌匀，将冬瓜切成四方薄片做成马蹄莲的花纹。\r\n2.将用卡夫奇妙酱拌匀的杏仁粒和马蹄粒装入作冬瓜做好的马蹄莲花坟里，然后再放入拌入味的胡萝卜条做花心。\r\n3.将做好的马蹄莲花放入用韭菜点缀好的盘中，即可。', NULL, NULL, NULL, '杏仁粒、鲜马蹄粒。配料：卡夫奇妙酱、冬瓜、韭菜、拌入味的胡萝卜条。');
INSERT INTO `recipe` VALUES (1178, 87, '姜丝田鸡', '1.将田鸡腿洗净，剁块，放鸡蛋清，白葡萄酒，盐和淀粉拌匀，逐块入四成热的油锅中炸透捞出，姜切片，蒜去皮切茸。\r\n2.炒锅上火，加植物油烧热，放入蒜茸，青椒块，姜片，砂糖，盐炒匀，下入田鸡腿，白葡萄酒烧开，淋水淀粉勾芡即成。', NULL, NULL, NULL, '田鸡腿400克，嫩姜100克，大蒜20克，鸡蛋清50克，青椒10克，白葡萄酒50克，植物油，淀粉，白砂糖，盐各适量。');
INSERT INTO `recipe` VALUES (1179, 87, '椒盐牛仔骨', '将牛小排直接放入预热的烤箱内，以200度的温度烤20分钟取出，放入盘中，周围配上蔬菜，食用时撒些椒盐及柠檬汁。', NULL, NULL, NULL, '美国带骨牛小排(含1根肋骨)450公克、柠檬1个、甜豆3荚、玉米笋3支、胡萝卜3片。调味料：椒盐1小匙。');
INSERT INTO `recipe` VALUES (1180, 87, '芥兰牛肉', '1.将肉切成大片，拍松肉质，加入少许黄酒、葱花、味精和盐，拌和后，将切碎的芥兰与面粉拌匀； \r\n2.将锅烧热，油五成熟时，将肉片放入，炸至金黄并浮起捞出； \r\n3.原锅油留少许，下葱花，煸出香味，下肉及配料，烹上黄酒、麻油，翻几下，出锅即可。', NULL, NULL, NULL, '牛五花肉300克，面粉50克，芥兰100克，味精、白酒、葱花、椒盐、麻油各适量。');
INSERT INTO `recipe` VALUES (1181, 87, '芥兰沙拉', '1.将芥兰洗净，用热水焯一下，取出后用清水冷却，控干水份待用。\r\n2.将焯好的芥兰切成长段，或整核码放在盘内，淋色拉油少许，挤上沙拉酱即可。', NULL, NULL, NULL, '新鲜芥兰300克，沙拉酱或沙拉调料适量。色拉油少许。');
INSERT INTO `recipe` VALUES (1182, 87, '金枪鱼它它', '将腌制后的金枪鱼肉切碎，与鸭梨丁和焙制好的松子仁围成3个圆。', NULL, NULL, NULL, '新鲜生金枪鱼肉、鸭梨、松子仁、干葱、鹌鹑蛋。');
INSERT INTO `recipe` VALUES (1183, 87, '金萱炸三蔬', '1.新鲜香菇切去蒂；洋葱切头尾去外皮，切成宽0、5厘米的洋葱圈；将新鲜金针切去老根。金萱茶末用热开水100克冲泡1分钟，滤出茶汤，放凉后加入所有调味料拌成面糊。\r\n2.加热油锅，约八分钟热时将所有蔬菜一片片上面糊，下锅炸至金黄酥脆，即可捞起排入盘中。', NULL, NULL, NULL, '主材料：金萱茶末2克。配料：新鲜香菇6个、小洋葱1个、新鲜金针150克。调味料：中筋面粉1/2碗、脆酥粉1/2碗、吉士粉(干酪粉)2大匙、鸡蛋2个、沙拉油1茶匙。');
INSERT INTO `recipe` VALUES (1184, 87, '卷心菜热狗', '1.将面包横切开并烤好，涂上蛋黄酱，在底层上放上生菜，番茄及甜黄瓜；\r\n2.焯一个肠，再放在扒炉上；\r\n3.好后取下肠放在底层面包上，转入盘中；\r\n4.将洋葱炸至金黄放在肠上；\r\n5.将菜丝沙拉放在生菜叶上，用法香放在上面并配以薯条。', NULL, NULL, NULL, '热狗或俄尔纳肠60克、热狗包1个、生菜15克、番茄片50克、甜黄瓜片10克、菜丝沙拉70克、薯条1包、洋葱圈，沾面包糠，炸(4--5个)60克、菜油20毫升、盐调至够味、胡椒调至够味、法香枝1个、蛋黄酱30克。');
INSERT INTO `recipe` VALUES (1185, 87, '咖喱油烟虾段', '1.将胡萝卜、葱头、姜去皮、芹菜去筋，洗净，均切成片。\r\n2.往煎盘上放入200克生菜油以及香叶、胡椒粒、干辣椒，上火烧热、再把胡萝卜、葱头、芹菜和姜下入，煸炒至葱头成黄色时，下入咖喱粉，将咖喱粉炒出香味，下入番茄酱，待番茄酱中的水分炒去，倒入鸡清汤和5克精盐、味精，改小火上把汁去一半，撤火，过箩成咖喱油汁。\r\n3.再将大虾去头、去尾、去屎线，洗净，从中间一切成两段，放上5克精盐和奶油拌匀。\r\n4.把生菜油下锅，中火烧热，把大虾下锅炸成金黄色，捞出，放入小焖锅，再把咖喱油放入装大虾的焖锅，上火焖3分钟，待', NULL, NULL, NULL, '大虾(约l公斤左右)16只、咖喱粉40克、葱头150克、生菜油750克（实耗200克）、姜15克、胡椒粒5克、干辣椒1个、香叶2片、芹菜150克、胡萝卜150克、味精10克、番茄酱20克、鸡清汤500毫升、奶油20克、精盐10克。');
INSERT INTO `recipe` VALUES (1186, 87, '咖哩粉丝金银虾煲', '将鲜虾和虾干过油备用，用咖哩酱起锅，将鲜虾和粉下锅焖3到4分钟，配上椒丝和丝瓜丝。', NULL, NULL, NULL, '鲜虾、虾干、咖哩酱椒丝、丝瓜丝。');
INSERT INTO `recipe` VALUES (1187, 87, '烤比目鱼', '1.将比目鱼除肠杂洗净去头尾，切成150克一块，撒上精盐、白醋、胡椒粉、雪利酒腌制约15分钟，沾上一层面粉，再沾上一层鸡蛋液，备用。\r\n2.把锅烧热后倒入食油，待油温5成时，逐块放入鱼块炸至金黄香熟，即可食用。', '香嫩味鲜。', NULL, NULL, '鲜比目鱼1500克，鸡蛋100克，面粉100克。调料：食油250克，雪利酒50克，白醋、精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (1188, 87, '烤蕃茄菲力牛排', '1.先将菲力牛排煎至喜爱的生熟度后置放于主菜盘内； \r\n2.取出少许橄榄油炒洋葱末和磨菇片，待磨菇软化后，倒进少许白葡萄酒； \r\n3.酒精成份蒸发后加进鲜奶油； \r\n4.浓缩成半稠程度，并适当的调味成汁，淋于牛排上即可食用。', NULL, NULL, NULL, '美国菲力牛排一块160公克、红蕃茄碎(罐装)2粒、九层塔(切碎)2-3只、大蒜(切末)2粒、橄榄油1大匙。');
INSERT INTO `recipe` VALUES (1189, 87, '烤腹脊牛肉', '1.将腹脊肉排适当的调味，并把烤箱预热约220度； \r\n2.先将牛肉周边用大火煎至金黄，并什锦蔬菜、香料炒过； \r\n3.将蔬菜香料倒进考盘内，牛肉置于蔬菜上，放进烤箱内； \r\n4.烤约12-15分钟呈半生熟度取出； \r\n5.将剩余蔬菜香料加进高汤或水，煮出味道后，将菜与汁分离，于汤汁中加入少许辣酱油，成调味肉汁； \r\n6.牛肉切片搭配蔬菜，淋上调味肉汁，并可加芥末酱佐料。', NULL, NULL, NULL, '美国腹脊牛排一块约1公斤、什锦蔬菜、香料（芹菜、香菜、红萝卜、月桂冠、青蒜）约200公克。调味料：盐、胡椒适量，辣酱油少许。');
INSERT INTO `recipe` VALUES (1190, 87, '烤牛排', '1.牛排用刀拍，用盐、糖、胡椒等调味品腌半小时；\r\n2.牛排放在烤架上入箱烘烤，先烤25分钟，再翻转烤10分钟即可。', NULL, NULL, NULL, '净牛排500克，盐、糖、胡椒、酱油、酒、味精适量。');
INSERT INTO `recipe` VALUES (1191, 87, '烤双肉蛋饼', '1.将牛、猪肉洗净切块，用水将面包泡软后挤去水分，一起放入绞肉机绞末；葱头洗净切末；备用。 \r\n2.将牛、猪肉末、50克葱头末、50克鸡蛋、牛奶、精盐、胡椒粉放在一起用力拌匀成肉饼馅；备用。 \r\n3.把锅烧热后倒入25克食油，待油温6成热时，放入葱头未炒至黄色，倒入打散的100克鸡蛋炒至小碎团成鸡蛋葱头馅；备用。 \r\n4.将肉饼馅分成10团，压扁中间包上鸡蛋葱头馅，沾上面包粉制成椭圆装饼备用。把煎锅烧热后倒入食油，待油温6成热时，放入肉饼煎至两面黄色，码入烤盘内，放进烤箱烤熟。起菜时浇上番茄调味汁，配上土', '鲜嫩适口，清香味浓。', NULL, NULL, '牛肉150克，猪肉350克，面包60克，鸡蛋150克，葱头125克，面包粉100克，土豆泥600克。调料：食油125克，牛奶100克，番茄调味汁150克，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (1192, 87, '烤笋瓜蕃茄', '1.将笋瓜、西红柿切成零点五公分厚之圆切片。\r\n2.将笋瓜片、西红柿片交错排列在抹油的圆形烤盘中，撒上蒜头末、盐、胡椒粉，再淋入橄榄油。放入已预热至摄氏两百度的烤箱中烤十五分钟。', NULL, NULL, NULL, '意大利笋瓜一条、大西红柿一个、蒜头末一汤匙、盐、胡椒粉各适量、橄榄油三十公克。');
INSERT INTO `recipe` VALUES (1193, 87, '烤鯷鱼纽约克牛排', '1.纽约克牛排一片适当的调味；\r\n2.烤架预热后，将牛排置于烤架上，两面烘烤至喜爱的生熟度；\r\n3.烤好之牛派上面摆上3-4尾油渍鯷鱼，和已切片的青橄榄；\r\n4.食用时沥上些香菜末。', NULL, NULL, NULL, '纽约克牛排一块240公克、油渍G鱼(罐装)3-4尾、青橄榄(切片)5粒、美国香菜(切末)1大匙。调味料：盐、胡椒适量。');
INSERT INTO `recipe` VALUES (1194, 87, '烤羊脊配他力根汁', '1.将肉用盐胡椒大蒜，香草油及芥末调料，涂上大量的香草及大蒜在上面，放在小烤盘上；\r\n2.放入170度的烤箱中20分钟，根据具体要求火候控制时间；\r\n3.另外在一汁锅中炒洋葱及1/2的它力根及剩余（10毫升）油（另外浸煮透它力根及醋，备用）不要糊，放入醋，倒入酒并浓缩一半；\r\n4.加入烧汁，开转小火浓缩掉一半，加入烤羊出来的汁增加味道；\r\n5.用滤器或粗布过滤，再用小火煮5分钟，加入备用的它力根及黄油（在最终时）；\r\n6.在盘中浇上汁，将羊肉放在中央，并用勺小心的浇上少许汁，排上其他装饰配料摆在盘中。', NULL, NULL, NULL, '羊脊，净的带一只排骨（1份）285克；混合香草4克；地装芥末3克；大蒜碎3克；橄榄油30毫升；盐调至够味；黑胡椒碎调至够味；洋葱细碎30克；红酒30毫升；烧汁30毫升；黄油25克；鲜他力根碎4克；红酒醋10毫升；土豆（软糖）1包；菠菜炒的1包；烩蔬菜1包；扒番茄1个；沙拉装饰1个；面包2个；份装黄油20克。');
INSERT INTO `recipe` VALUES (1195, 87, '辣味红酒烤羊排', '1.将材料均匀混合，腌渍羊肋排，大约半天或一天。\r\n2.滚水煮熟尖管通心粉，待凉备用。\r\n3.青芦笋整支滚水烫一下，待凉备用。橄榄油热锅煎羊肋排，加入红酒，然后以中火煎3分钟，烤箱预热，将煎五分熟的羊肋排送进220℃的烤箱烤15-20分钟，利用煎羊肋排的油锅加热腌酱，以中火收干汁液，使其浓稠，取出羊肋排，将通心粉与清芦笋置于盘缘并淋上酱汁与辣椒粉即可食用。', NULL, NULL, NULL, '特选羊肋排1公斤，义大利尖管通心粉（PENNE ）20克，青芦笋6支，Abasco辣椒酱4滴，辣椒粉2茶匙，红糖粉2大匙，红酒醋1.5大匙，番茄酱2大匙，盐2茶匙，清水1大杯，橄榄油1大匙，橄榄油，红酒1大杯。');
INSERT INTO `recipe` VALUES (1196, 87, '辣味金枪鱼', '1.用卡真料将金枪鱼两面涂好，腌至2小时，用柠檬汁香油盐胡椒；\r\n2.用平底锅煎金枪鱼20至30秒，取出，不要过火；\r\n3.从平底锅取出拿走走锅，用锋利的刀做成角度的方向顺着切入等长的鱼；\r\n4.放在盘子中央成扇形展开。', NULL, NULL, NULL, '金枪鱼110克；混合卡真粉25克；绿柠檬汁20亳升；日式芥末20克；日式酱油10毫升；蔬菜色拉装饰30克；香油10毫升；大块盐1克；黑胡椒碎1克。');
INSERT INTO `recipe` VALUES (1197, 87, '辣味沙拉浇汁', '将所有的原料合在一起，充分搅拌均匀即成。如无甜辣酱、辣椒酱掺入一勺白糖，拌匀代替。', NULL, NULL, NULL, '（4-6份） 酸奶300毫升，洋葱末1大勺，甜辣酱3大勺，精盐1/2小勺，胡椒粉适量。');
INSERT INTO `recipe` VALUES (1198, 87, '冷豆腐', '1.将豆腐切成若干块、放入净水、再放入几根红绿海草点缀。\r\n2.上桌时将冰块放入净水中，以保持豆腐的温度。', NULL, '在小碗中盛入特制冷豆腐酱油，用漏勺将豆腐舀入酱油中，再根据个人口味将柴鱼丝、葱、海苔、生姜泥适量放入，即可享受到味道鲜美、清清爽爽的豆腐美味了。', NULL, '绢豆腐1盒250克、红绿海草少许。配料：柴鱼丝5克、葱5克、海苔丝少许、生姜泥5克、冷豆腐酱油。');
INSERT INTO `recipe` VALUES (1199, 87, '莲白沙拉', '1.莲白洗理净切成丝；苹果去皮去核，切成约1厘米厚的片；榛子去壳去仁切碎。\r\n2.浓牛奶搅打至起泡，加入适量的白糖和柠檬汁调口味，拌匀成牛奶柠檬浇汁。', '色淡味美，酸甜可口。尤其使用于同肉、禽类主菜配食。', NULL, NULL, '（4-6份）莲花白500克，苹果（中等大小）2个，榛子25克，浓牛奶100毫升，柠檬汁1-2大勺，白糖适量。');
INSERT INTO `recipe` VALUES (1200, 87, '绿色的梦', '用全利冻调制法制的清全利冻，冻内加入少量薄荷酒或橙皮酒，按照冻凝固的时间，掌握速度使其色泽有层次。', NULL, NULL, NULL, '牛奶、蛋、糖、明胶。调味料：比例：牛奶：蛋：糖：明胶=1：0.25：0.3：0.06。（蛋和糖打发，冲入加热煮沸的牛奶，放在微火上加热，并不断搅拌至稠，放入已经溶解的明胶液搅匀、过筛）。');
INSERT INTO `recipe` VALUES (1201, 87, '罗宋汤', '将牛肉丁加水用漫火炖至七成熟，此时在牛肉汤中先放入不易熟的土豆丁、胡萝卜、番茄丁一起煮到土豆出沙，番茄在汤中泛红为止。再放入磨碎的白胡椒粒及盐便可以喝了，还可在汤熬好后往锅中再加一碗熟牛奶，则成为西餐馆提供的那种奶茸汤。', NULL, NULL, NULL, '不肥不瘦牛肉1斤切丁，土豆、胡萝卜、番茄， 芹菜。以上四种蔬菜皆切成丁。洋白菜、白胡椒粒、盐。');
INSERT INTO `recipe` VALUES (1202, 87, '麻酱香桃蜜糖烤鸭胸', '将糖在锅中烧成金黄色，倒入红酒醋，然后加入烧汁、酱油与柠檬汁，烧开后调味，搁在一旁待用。用少许牛油煎鸭胸，鸭皮向下，煎二至三分钟。在鸭面扫上蜜糖。用焗炉以摄氏280度中火烘焙7至8分钟。所有蔬菜去水，用牛油拌匀，加盐与胡椒。桃子一开四，放在平底锅内慢慢地煎。鸭胸片放在蔬菜面，淋上汁料，撒上炒好的芝麻。以桃和新鲜芫荽作装饰。', NULL, NULL, NULL, '600g鸭胸肉、20g蜜糖、80g小白菜、80g白菜、80g小胡萝卜、80g土豆、60g 青豆、6g芫荽、10g牛油、80g桃子、盐和胡椒粉适量。调味料：5cl红酒醋、20g糖、3cl烧汁、2spoon酱油、2cl柠檬汁、5g炒好的芝麻。');
INSERT INTO `recipe` VALUES (1203, 87, '马介休烟肉炒薯丝', '将马介休、烟肉、马铃薯切丝，然后将马介休、烟肉拉油，薯丝过水，再加入青红椒丝一起炒。', NULL, NULL, NULL, '马介休、烟肉、马铃薯、青红椒丝');
INSERT INTO `recipe` VALUES (1204, 87, '马乃司', '蛋黄放入干净的碗内，加精盐、胡椒粉、芥末和一半白醋，用打蛋棒用力抽打，并徐徐注入色拉油（约四分之一）；当汁逐渐稠厚时，再加些色拉油，边抽打边加油，随时检查汁的混合情况；油加完后，加入剩下的白醋，搅至浆糊状即可；约出料250克。其是调制色拉的主要汁种，被称为冷汁汁种。', NULL, NULL, NULL, '蛋黄2个，精盐1.5克，白胡椒粉少许，芥末1.5克，白醋（或柠檬汁）5克，色拉油250克。');
INSERT INTO `recipe` VALUES (1205, 87, '茅菜色拉', '撇去脆蒜味面包及鸡蛋不用，以750克的茅菜取代菠菜。摘去菜的根部及表面已凋谢的叶，分开菜叶，然后将它浸于冷水中洗净，除去坚硬的梗部，将大快的叶撕开，并用毛巾或色拉滤干器将菜叶弄干。用削皮刨将1块约重125克的巴麻臣芝士刨片，依照主食谱的方法，煎香熏肉，以漏匙隔去油后将熏肉放到茅菜上。待锅中的油冷却后放入30克的松子，炒约30-60秒，直至变成金黄色及香脆为止。将松子及油加入茅菜内用力搅拌，淋入用陈醋和油组成的油醋汁液，依照主食谱的方法组成色拉。将色拉分为6碟，放上巴麻臣芝士片，趁色拉热时享用。', NULL, NULL, NULL, '茅菜、熏肉、巴麻臣芝士、松子。');
INSERT INTO `recipe` VALUES (1206, 87, '美国牛排', '1.将牛排两面撒上胡椒粉后，直接放入烧热的平底不沾锅内，不必添加任何油脂（一般平底锅则加少许油），先以大火略煎两面，再改为中小火，两面各煎3分钟即可（可依个人偏爱之熟度增减烹调时间）；\r\n2.将玉米筍、甜豆川汤后，置于牛排旁。', NULL, NULL, NULL, '美国沙朗肋眼牛排肉300公克、玉米G、甜豆各3支。调味料：盐、胡椒粉各1/3小匙。');
INSERT INTO `recipe` VALUES (1207, 65, '爆炒腰花', '1.猪腰中间片开去腰臊，剞麦穗花刀，再切成块。\r\n2.木耳洗净，冬笋切成略小于腰花的片。 \r\n3.碗中放入汤、酱油、料酒、姜汁、味精、蒜片、淀粉对成芡汁。\r\n4.先将腰花木耳分别用开水焯后控水。 \r\n5.炒锅上火放油烧至7-8成热，投入浆好的腰花，稍滑迅速控油，勺留底油，下入葱花、煸香、腰花、木耳、冬笋，倒芡汁旺火急炒，淋明油出锅即可。', '形似麦穗，色泽红润油亮，脆嫩爽口。', NULL, NULL, '猪腰400克，木耳10克，冬笋片25克，油75克，酱油10克，葱5克，姜汁5克，料酒8克，味精3克，蒜片5克，汤、淀粉各适量。');
INSERT INTO `recipe` VALUES (1208, 87, '美国牛肉沙爹串', '1.将美国牛肉切成一寸长、半寸阔薄长条，放入调味拌匀，待用。\r\n2.青瓜、洋葱洗净切小块，椰汁糯米饭压实切成小方块。\r\n3.将美国牛肉腌后用竹签穿成串，用烤炉烤熟或油锅炸熟，上碟。\r\n4.青瓜、洋葱、椰汁糯米饭拌碟，配合沙爹酱共食。', NULL, NULL, NULL, '美国牛肉（西冷或牛柳）150克，青瓜、洋葱各适量，椰汁糯米饭少许，竹签适量，沙爹酱1/4杯。调味料：生抽、老抽各1/2汤匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1209, 87, '摩菜鸡', '1.将鸡除毛、内脏洗净去头脚切块；番茄洗净用沸水烫后剥皮；\r\n2.青椒去蒂籽切片，把番茄、青椒、芝麻、杏仁、大蒜、巧克力放入绞肉机绞末，加入精盐、丁香粉、茴香粉、桂皮粉、香菜籽粉，红辣椒粉拌匀，倒入适量鸡清汤调匀成调味汁，备用。\r\n3.把锅烧热后放入黄油，待熔化后放入鸡块煎至上色，盛入焖锅内，倒入调味汁，加盖煮沸后，改用小火焖熟。食用时可配大米饭。', NULL, NULL, NULL, '鸡一只（约1500克），番茄100克，青椒50克，杏仁120克。调料：黄油75克，红辣椒粉10克，大蒜10克，丁香粉、茴香粉、桂皮粉、香菜籽粉各少许，芝麻10克，无味巧克力1小块，精盐适量。');
INSERT INTO `recipe` VALUES (1210, 87, '蘑菇大虾色拉', '照主食谱的方法准备腌味汁，但要撇去番红花不用。挑去连壳大虾背上的黑肠，将大虾切开成蝴蝶状。用1/3的腌味汁浸润大虾。撇去青瓜不用。用湿润的厨纸将375克蘑菇抹净，切去过长的蒂部。有需要的话，可将大蘑菇切成4份，放在一锅有盐的沸水中，以慢火煮5-7分钟，直至体软为止，隔去水分后用冷水过滤，沥干水，将干透的蘑菇放在余下的腌味汁中，搅拌后盖好，放入电冰箱内腌制3-4小时。用有孔匙羹将蘑菇分别放在6只碟子上，腌味汁弃掉不用。将烤箱烧热，照主食谱的方法烧烤大虾。大虾烧好，放于碟上便可立即食用。喜欢的话，可用柠檬角块', NULL, NULL, NULL, '大虾、蘑菇。');
INSERT INTO `recipe` VALUES (1211, 87, '蘑菇蛋糕', '1.调制蛋白蘑菇：砂糖:蛋白=2:1。蛋白打发，边加砂糖边打至细腻。使用1cm左右无齿裱花头，在垫白纸并撒少量面粉的烤盘上，裱蘑菇状，表面撒少量可可粉。烘烤炉温120度约40分钟左右。\r\n2.调制巧克力奶油：可可粉加色拉油调匀拌入白脱油（浓稠根据需要而定）。\r\n3.用S.P海绵蛋糕胚，去皮，剖三层，中间嵌巧克力奶油，顶面、垂直边刮抹巧克力奶油，中央刮白脱油小圆，用蘑菇裱花装饰。', NULL, NULL, NULL, '砂糖、蛋白、可可粉、色拉油、白脱油。\r\n调味料：砂糖和鸡蛋清比例为2比1，糕胚、巧克力和白脱油比例为4比5比1。');
INSERT INTO `recipe` VALUES (1212, 87, '莫斯科红菜汤', '先将红菜，葱头，胡萝卜全都切成丝，加盐，糖醋精腌1小时左右，放上牛油，香味，胡椒粒，干辣椒焖到六成熟，放上番茄酱焖至油呈红色，放洗净切丝的洋白菜，加入牛肉汤煮沸用油炒面调剂浓度，加盐，糖，醋精调剂口味放上西红柿块和大蒜末把牛肉切片，火腿切片，小肠半根用牛肉汤煨之起汤时在盘内放牛肉，火腿各1片，半根小肠，盛上汤，浇上奶油，撒上小茴香末，如果色不够红，可加一些煮红菜头皮加醋精红滤的红汤即可。', NULL, NULL, NULL, '熟牛肉400克，牛肉汤2500克，红菜头400克，葱头150克，胡萝卜150克，洋白菜400克，糖75克，醋精20克，盐20克，干辣椒1克，香味2片，胡椒粒2.5克，油炒面25克，牛油250克，番茄酱200克，奶油150克，小茴香25克，大蒜25克，鲜西红柿15克，小泥肠15克，火腿15克。');
INSERT INTO `recipe` VALUES (1213, 87, '墨西哥水饺片', '1.将水饺皮用波浪刀全部切成半圆形备用。\r\n2.热油锅，等油温在160摄氏度左右时，将切好的水饺皮入锅炸至呈金黄色，约需3-5分钟，再捞起将油沥干，摆盘后蘸调味料食用。', '风味独特，香脆爽口，有些酸辣。', NULL, NULL, '水饺皮10张。调味料：番茄丁3大匙、洋葱丁2大匙、葱末1大匙半、香菜末1/3大匙、辣椒末2小匙、白醋1大匙、糖1/2大匙、香油2小匙。');
INSERT INTO `recipe` VALUES (1214, 87, '奶酪蛋卷', '鸡蛋液加胡椒粉搅匀备用；煎锅烧热控上黄油，蛋液倒入锅内吊成蛋皮，放入奶酪1片四边包严，形似春卷，煎至金黄涂上些黄油出锅，配上些酸黄瓜片食用。', NULL, NULL, NULL, '鸡蛋2枚，奶酪2片，胡椒粉少许，黄油20克，酸黄瓜1根。');
INSERT INTO `recipe` VALUES (1215, 87, '奶酪汉堡包', '1.将面包分为两半，并将中心面朝上排在一起，将生菜番茄片洋葱片及时萝酸菜片，放在烤好的底层面包上。注意：涂上黄油并烤好即刻用；\r\n2.将肉饼放在扒炉上煎至所需要的程度（嫩，五成或全部等）制作奶酪汉堡，用明火烤炉使其溶化；\r\n3.将肉饼放在有蔬菜的面包上，上片面包放在上面，将其放在盘中，单独用菜丝沙拉，橄榄及法香枝做装饰，最后放上薯条。', NULL, NULL, NULL, '汉堡面包1个、牛脊（本地），并弄成圆筒状180克、生菜30克、番茄片40克、时萝酸菜片25克、洋葱片，去皮40克、菜丝沙拉30克、椰榄5克、薯条1包、瑞士奶酪40克、黄油20克、盐调至够味、白胡椒调至够味、法香枝1个。');
INSERT INTO `recipe` VALUES (1216, 87, '奶酪火腿蛋卷', '鸡蛋打散，火腿切丝用黄油煸炒待用，煎锅烧热擦上黄油，把鸡蛋倒入吊成厚蛋皮，放上奶酪、火腿丝包成三角形，两面煎制金黄，涂上黄油出锅。', NULL, NULL, NULL, '鸡蛋2枚，奶酪2片，方火腿30克，黄油20克。');
INSERT INTO `recipe` VALUES (1217, 87, '奶油菠菜汤', '1.将菠菜洗净用沸水烫软，挤干切末；鸡蛋煮老切片；备用。\r\n2.将鸡清汤倒入锅内煮沸，放入油面粉调匀，加入精盐、豆蔻粉、胡椒粉调好口味后，倒入菠菜末煮至微沸，放入奶油调匀即可。食用时，先在汤盘内放上鸡蛋片，然后盛汤即可。', '清香味鲜。', NULL, NULL, '菠菜500克，鸡蛋75克，油面粉25克。调料：奶油50克，鸡清汤750克，精盐适量，豆蔻粉、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (1218, 87, '奶油长生卷', '将糕胚中间夹白脱花生酱（花生酱:白脱=1:0.5，熔化的白脱油加盐和花生酱拌和），向前推卷，表面刮一层薄的白脱油，沾上烘熟切粒的花生，切斜形块。（糕胚:花生:白脱花生酱:白脱油=1:0.5:0.3:0.2）。', NULL, NULL, NULL, '花生酱、白脱油。糕胚材料比例：蛋:面粉:糖:水:色拉油:乳化剂=1:0.5:0.1:0.15:0.06。');
INSERT INTO `recipe` VALUES (1219, 87, '南美洲炒饭', '1.先将鸡肉、烟脯肉、猪肉肠切成片，洋葱、干辣椒、罗勒切末，蕃茄青椒切成丁待用。\r\n2.取平底不粘锅加入黄油、色拉油略加热，然后先加入鸡片、烟脯肉片、猪肉肠片、虾仁炒一下，再加入洋葱末、青椒丁、罗勒末一起炒熟。\r\n3.倒入米饭翻炒一下，再加进番茄丁、干辣椒末、红椒粉一起炒。根据口味适当加些盐。', '郁香微辣，色泽艳丽。', NULL, NULL, '鸡肉35克，烟脯肉30克，猪肉肠30克，虾仁40克，洋葱半只，番茄半只，青椒适量，干辣椒少许，红椒粉少许，罗勒少许，米饭45O克，黄油少许，盐少许，色拉油适量。');
INSERT INTO `recipe` VALUES (1220, 87, '柠檬蜂蜜鲑鱼排', '1.混合所有调味料(鲑鱼除外)做成腌汁。\r\n2.腌30分钟左右，勿腌制过久，入味即可，因为柠檬汁会“煮熟”鱼肉，太久会老。\r\n3.不沾锅中放入少许油，鱼排先煎一面，略呈金黄后，再换面煎。\r\n4.或可将鱼排包在锡箔纸中，放入烤箱约烤6至8分钟。\r\n5.将腌料加热，煮沸，煎或烤熟的鱼排置于已热过的碟中，淋上少许煮汁，一旁置少许煮过的蔬菜或生菜即可。', NULL, NULL, NULL, '一斤鲑鱼(600公克)切成四片、六大匙蜂蜜、三大匙酱油、一个柠檬榨汁、半大匙麻油、四分之一小匙辣椒粉、黑胡椒粉少许。');
INSERT INTO `recipe` VALUES (1221, 87, '牛柳配奶油蘑菇汁', '1.加热一只小炒盆，使黄油至金黄色加入猪柳，并将面条炒好，取出。\r\n2.加入洋葱、大蒜、金针菇、蘑菇红粉及香草炒，不要上色，当汁开始跑出时，加入酒并浓缩一半。\r\n3.倒入烧汁并浓缩至四分之一，将肉放回汁中，浓缩掉一半，再从火上取下。\r\n4.在锅中加热剩余黄油，加入面条搅动，加入调味品及一些细法香，放入盘中。\r\n5.用一些法香装饰并单独配上沙拉摆好其他装饰及配料放在盘中。', NULL, NULL, NULL, '牛柳丝150克、洋葱细丁35克、金针菇20克、白蘑菇20克、大蒜细碎3克、红粉2克、混合鲜香草1克、黄油20克、白酒45毫升、烧汁50毫升、浓奶油35毫升、盐调至够味、白胡椒粉调至够味、面条烫熟，时令配沙拉1包、面包2个、黄油份装20克。');
INSERT INTO `recipe` VALUES (1222, 87, '牛肉盖饭', '1.牛腩、红萝卜剁小块，放入高汤内煮沸。 \r\n2.捞掉肉沫，转小火，煮到肉烂，再倒入香菇。 \r\n3.将蛋打散后淋下，芹菜切小段入锅，熄火。 \r\n4.饭盛在碗内，淋上配料。', NULL, NULL, NULL, '牛腩半斤、红萝卜少许、蛋4个、香菇6朵、高汤8杯、白饭4碗。调味料：酱油4大匙、盐、冰糖各少许、芹菜适量。');
INSERT INTO `recipe` VALUES (1223, 87, '牛油果西柚火腿色拉', '1.事前准备：西柚的片块及皮丝，可于食用前一天预备好，加盖放入电冰箱内。你可于一天前调制酱汁，在食用前的30分钟内才组成色拉。在牛油果上淋一些西柚汁，可防止变色。\r\n2.调制罂粟子油醋汁：用擦碎器将1/2个洋葱擦成碎丝放入碗中。将陈醋、蜜糖、芥末粉、姜粉、盐及胡椒加入洋葱内搅拌，随后逐渐加入搅拌，直至油醋汁浓稠呈乳状。加入罂粟子，将它与油醋汁搅匀，试味。\r\n3.准备色拉用料：用削皮刨刨出1/2个西柚的外皮，用厨师刀将西柚皮切成细丝。烧沸1/2锅水，加入西柚皮，以慢火煮2分钟，滤干水分后待用。切去西柚的顶及', NULL, NULL, NULL, '4个西柚、125克巴马火腿薄片、175克紫花南芥、2个牛油果、罂粟子油醋汁、1/2个小洋葱、3汤匙陈醋、1汤匙蜜糖、1/2茶匙芥末粉、1/4茶匙姜粉、少许盐、胡椒、150毫升植物油、1汤匙罂粟子。');
INSERT INTO `recipe` VALUES (1224, 87, '牛油果西柚熏三文鱼色拉', '摘下5条新鲜的莳萝，将它们在砧板上用刀切碎。调制油醋汁，加入莳萝。选用4个白肉西柚去皮，切片块。将175克的熏三文鱼片切成长5厘米的条状。预备90克的红生菜的根部切出，摘去凋萎的菜叶，将菜叶分开，以大量的冷水清洗，用色拉滤干器或茶巾将菜叶的水分吸干。以油醋汁替色拉调味，并将它分放在4只碟子中，将西柚块及牛油果片以旋转的车轮状排放在叶上，将三文鱼块摺成玫瑰花状放在碟中间，喜欢的话可用莳萝装饰。', NULL, NULL, NULL, '莳萝、白肉西柚、熏三文鱼、红生菜、牛油果。');
INSERT INTO `recipe` VALUES (1225, 87, '牛脂油布丁', '1.苹果洗净去皮、去核，切丁。面包去皮切碎后搓成细末。牛脂油择去皮、筋，用刀剁成碎末加牛奶拌匀待用。\r\n2.鸡蛋内加糖、香草粉，用蛋袖子抽至膨松体，再放入拌好的苹果、面包、牛脂油，调匀后装在抹油撒面粉的模子里，蒸30分钟。\r\n3.食用时将布丁扣在点心盘内，浇以黄沙司。（黄沙司的制作：用料：牛奶500克，蛋黄100克，砂糖150克，玉米淀粉25克，香草香料片l片。制法：把蛋黄、糖、玉米粉、香草粉放入锅内加适量牛奶搅勾，将滚沸牛奶冲入，在文火上搅拌微沸，过滤备用）。', '香甜可口，松软棕黄。', NULL, NULL, '净牛脂油250克，苹果500克，面包450克，鸡蛋250克，砂糖250克，牛奶300克，香草香料片l片， 黄沙司350克。');
INSERT INTO `recipe` VALUES (1226, 87, '纽约牛排', '1.在油，盐，胡椒的混合物中浸泡牛排一晚，刷少许柠檬汁，屑大蒜及香草，至少要用6个小时。\r\n2.根据需要，首先扒上条纹，在制成需要的火候，在这个锅中一定要小心保证达到要求。\r\n3.另外准备好意大利炸土豆，时令蔬菜，扒番茄，并配以装饰及配料。', NULL, NULL, NULL, '牛排，中部修净的250克、柠檬汁20毫升、橄榄油20毫升、绿胡椒及辣椒黄油50克、扒番茄(80克)1个、鲜萝丝玛林1克、百里香，碎1个、洋紫苏，鲜1个、阿里根农，鲜碎1个、柠檬屑，焯2个、盐调至够味、黑胡椒，碎调至够味、大蒜细碎5克、意大利炸土豆1包、时令沙拉1包、香草黄油汁40毫升、沙拉装饰1个、面包2个、份装黄油20个。');
INSERT INTO `recipe` VALUES (1227, 87, '挪威水果布丁', '1.将李子脯加冷水300克浸泡过夜，次日在锅中煮20分钟，去核留汁留脯待用。\r\n2.用另一锅加300克冷水，放糖盐及肉桂粉煮沸，然后将少许冷水与玉米粉拌合成浆，徐徐倒入煮沸的水中，不停地搅拌均匀。\r\n3.将玉米糊与果脯汁，果脯肉，柠檬汁混合在一起，装入碗中摊凉后送进冰箱。\r\n4.食用时不用将碗中物倒出，直接取用。', '甜香可口。', NULL, NULL, '黑李子脯150克，白砂糖100克，玉米粉50克，柠檬汁20克，食盐，肉桂粉少许。无黑李脯时可用其他果脯代替。');
INSERT INTO `recipe` VALUES (1228, 87, '泡葡萄', '1.选择新鲜（带白霜）成串的葡萄洗净，摘掉成熟过度的粒小的葡萄粒，小心放入木桶或瓦缸内。\r\n2.将水煮沸放入桂皮，丁香煮约10分钟再放入糖，溶化后放醋精调剂口味，凉后注入装葡萄的木桶或瓦缸内移至零上3-5度处存放3-5天后即可食用。', NULL, NULL, NULL, '葡萄10公斤、水10公斤、糖1.5公斤、醋精150克、桂皮10克、丁香15克。');
INSERT INTO `recipe` VALUES (1229, 87, '啤酒焖牛肉', '1.油炒面粉制法：先用锅将黄油熔化，然后放入过箩的面粉，用木制搅板搅匀，放在温炉板上炒之，随炒随搅，以免糊底，炒到面粉微黄且呈翻砂颗粒状，并发出面香味时即可，备用。\r\n2.将牛肉(每块500克-750克)撒盐、胡椒粉腌约l小时，然后用热油煎上色，放入烟锅内加啤酒、鲜西红柿块、牛肉汤和用油炒黄的葱头丝、胡萝卜片、芹菜段，在旺火上煮沸，沸后改文火微沸，至牛肉熟软时将其取出。\r\n3.原汁用油炒面粉调剂浓度，放盐、糖调剂口味，过滤成沙司。\r\n4.食用时将中肉每份切2-3片，浇沙司即可。', NULL, NULL, NULL, '牛后腿肉1750克，啤酒750克，牛肉汤750克，油100克，鲜西红柿500克，葱头150克，胡萝卜100克，芹菜50克，盐15克，油炒面粉50克，糠50克，胡椒粉少许，黄油50克。');
INSERT INTO `recipe` VALUES (1230, 87, '啤酒汤', '将1/2啤酒、桂皮、砂糖、柠檬汁放入锅内煮沸，把另1/2啤酒与土豆粉调成稀糊状，缓缓倒入煮沸的啤酒汤内再煮沸，取出桂皮，加盐、胡椒粉调好口味即可。', '酸香味美，别具风味。', NULL, NULL, '啤酒1瓶，土豆粉50克。调料：柠檬汁50克，砂糖20克，桂皮一块，精盐、胡椒粉适量。');
INSERT INTO `recipe` VALUES (1231, 87, '苹果菠萝卷心菜丝色拉', '撇去胡萝卜、洋葱及酸奶油酱汁不用。照主食谱的方法将卷心菜刨成细丝。将菠萝粒挤汁。将4个青苹果去皮，然后放在1个大碗内。将菠萝加入苹果内搅拌，直至菠萝的汁液遍布在苹果上，这样可避免苹果变铁锈色。将150毫升的苹果醋、250毫升的乳酷(Buttermilk)、250毫升的瓶装白色拉酱、2茶匙的芥末粉以及胡椒、盐放入一小碗内，搅匀后试味。将卷心菜丝放入盛有苹果及菠萝的大碗内搅拌。将酱汁加入色拉内搅匀后试味，然后照主食谱的方法，将色拉盖好放入电冰箱内冷藏。喜欢的话，可用苹果雕花作为装饰。', NULL, NULL, NULL, '菠萝粒、苹果、苹果醋。');
INSERT INTO `recipe` VALUES (1232, 87, '苹果茴香色拉', '撇去核桃粒，高根疏拿蓝芝士酱汁及香梨不用。可先调制酱汁：准备1块约重125克的罗佛芝士，切去它外面的干酷皮后切成大块，连同2汤匙的陈醋放入食物搅拌机中搅匀，加入175毫升的奶油继续搅合。但是切勿过分搅拌，否则酱汁会凝结。将一个圆生菜的根部切去，分开菜叶，以冷水冲洗，并去掉硬梗。洗净后将菜叶放入色拉滤干器或用茶巾将水吸干。如主食谱所示的方法清理茴香及将它片，加入酱汁调味，试味后持用。用挖圆器挖出3个苹果心。在每个苹果的一侧切去一薄片，将它能平稳地放于砧板上，再横切成直径为5毫米的圆环，用柠檬挤汁并滴在苹果环', NULL, NULL, NULL, '苹果、茴香、圆生菜、罗佛芝士。');
INSERT INTO `recipe` VALUES (1233, 87, '葡式薯泥焗生蚝', '调味薯泥，镶入生蚝里，焗至金黄色，铺上香茜碎。', NULL, NULL, NULL, '生蚝、调味薯泥。');
INSERT INTO `recipe` VALUES (1234, 87, '葡萄干布丁', '1.将葡萄干去蒂洗净，用温水泡软待用。\r\n2.将面粉过箩，放入缸内，加砂糖（200克），蛋黄（100克），牛奶（150克），用木铲搅匀，再将化软的黄油倒入，加少许发酵粉及少许香草粉，一块搅匀。 \r\n3.将蛋清150克，用抽条抽起，与和好的面糊混合，加入泡软的葡萄干搅匀，装入事先抹好黄油的布丁模子，装八成满，上锅蒸约半小时取出（不要在中途掀锅）。\r\n4.将其余的砂糖，牛奶入锅上火煮开，把玉米粉，蛋黄（80克）放少许凉水解开，与香草粉倒入锅中搅成汁，上台时，将布丁扣入杯内，浇上汁即成。', '色黄，味甜香，宜热食。', NULL, NULL, '黄油150克，牛奶400克，鸡蛋400克，面粉200克，玉米粉20克，砂糖300克，葡萄干75克，香草粉，发酵粉各少许。');
INSERT INTO `recipe` VALUES (1235, 87, '葡汁奄列斋', '用咖哩煮斋（云耳，木耳，黄耳，如耳），然后用蛋饼包裹，加葡汁、水榄，焗3分。', NULL, NULL, NULL, '云耳，木耳，黄耳，如耳，蛋饼，葡汁，水榄。');
INSERT INTO `recipe` VALUES (1236, 87, '普罗旺斯大蒜西红柿面', '1.20颗大蒜剥皮切片。西红柿在热水中略烫过，以方便去皮去籽，然后再切成小丁。\r\n2.在锅中混合橄榄油和蒜片，置炉上开小火，熬三、四十分钟，直到蒜片变软且呈金黄色。接着鲜奶油入锅，搅拌一下，把火转至最小保温，勿使沸腾，洒少许普罗旺斯混合香草。即成大蒜奶油汁。\r\n3.大锅中加四、五公升水，沸腾后加1大匙盐，把意大利面入锅，煮至软硬适中的弹牙程度，即可捞起，沥去水分。\r\n4.把大蒜奶油汁浇在面上，搅拌后，分置四只温过的盘子上，洒上干酪粉，再把西红柿丁和九层塔丝平均铺在面上，食用时由各人自行搅拌均匀。', NULL, NULL, NULL, '20颗蒜头、四五个成熟红西红柿。1/4杯橄榄油、1杯鲜奶油。1大匙盐、1包500公克的意大利面、研磨意大利干酪粉、九层塔丝、少许普罗旺斯香草（可省略）。');
INSERT INTO `recipe` VALUES (1237, 87, '钱杏仁冬瓜', '将冬瓜雕刻成金钱形，腌渍入味，拍粉拖蛋，沾杏仁片，入油锅炸至金黄，做成两个钱串。用胡萝卜和青笋做成小金钱炒熟，放在两个钱串的中间。', NULL, NULL, NULL, '杏仁片、冬瓜、胡萝卜、青笋。');
INSERT INTO `recipe` VALUES (1238, 87, '茄子玉米粥', '1.首先将玉米面，牛奶及豆蔻混和在汁锅中并煮开，继续小火煮，并搅动10分钟，最后搅入牛奶，取下。\r\n2.稍冷却，然后搅入蛋黄，然后放在有油印的烤盘上，1厘米高，20×30cm，备用。\r\n3.盐腌茄子，并放在滤锅中30分钟，用流动水冲洗，浸入牛奶10分钟，放入35毫升热橄榄油煎至金黄，排去多余油脂，放在边上。\r\n4.将玉米饼切为与茄子大小相同的圆片，将茄子与玉米饼轮流排在涂有橄榄油的烤盘中，并将玉米饼的两面涂上酱汁，堆为3个茄子及两个玉米饼高，撒上一些鲜洋紫苏，在最上面放上一些奶酪，在180度烤箱中烤10钟', NULL, NULL, NULL, '大茄子片(1厘米厚)带皮500克、牛奶100毫升、橄榄油、酱汁100毫升、酱汁料单160毫升、洋紫苏碎1克、红绿椒丁1.5厘米方形100克、阿里根农鲜丁0.5克、玉米粒75克、奶酪片100克、洋紫苏汁，炸脆的(装饰用)12个、玉米面细100克、牛奶200毫升、大蒜酱10克、帕玛山奶酪25克、蛋黄4个、豆蔻粉0.5克。');
INSERT INTO `recipe` VALUES (1239, 87, '青豆色拉', '撇去熏三文鱼及芦笋不用。如主食谱的方法将米煮熟及调制油醋汁。在一小锅有盐分的沸水中加入125克新鲜有壳青豆，以慢火煮3-5分钟直至青豆变软，隔去青豆的水分并以冷水清洗，沥干。可改用同样分量的雪藏青豆，依包装上的说明将它煮熟。预备2条熏鳟鱼（每条约重250克），并以小刀剥去鱼皮，切出鱼柳块，弃掉鱼骨，以叉将熏鱼块分开成碎块。将375克的小番茄洗净，以厨用纸巾抹干，切去番茄蒂，将番茄剪开成两半作装饰。如主食谱的方法组成色拉，试味后放入碗内，以番茄装饰。', NULL, NULL, NULL, '青豆、番茄、熏鳟鱼。');
INSERT INTO `recipe` VALUES (1240, 87, '倾心', '1.制心形装饰箱：杏仁糖团或胶糖团，调制淡玫瑰红色，按照预制的样板，切割成形，加以固定1-2天，然后边上裱细花点缀。\r\n2.用S.P海绵蛋糕胚，修成心形，去皮，剖三层，中间夹人造鲜奶油刮抹、装饰，并用心形装饰箱点缀，以示母亲对子女的爱。', NULL, NULL, NULL, '杏仁糖团或胶糖团、S.P海绵蛋糕胚、鲜奶油。调味料：糕胚:人造奶油=1:1。');
INSERT INTO `recipe` VALUES (1241, 87, '软鸡胸配小茴香', '1.用腌汁将鸡胸腌一晚，并冷藏于冷库中；\r\n2.将鸡胸扒上条纹，先扒有皮的一面备用，放在烤盘中，置入170度的烤箱中盖上盖烤12-15分钟或汁出，并在烤炙过程中不断涂上腌肉汁，上餐时再次淋上热汁；\r\n3.放入装有米饭的餐盒中，配以蔬菜装饰。', NULL, '步骤1在有热油的锅中炒洋葱，大蒜辣椒几分钟，加入米并撑几分钟；\r\n步骤2根据前面的要求配用。', NULL, '鸡胸(12片)1.5千克，大蒜细碎30克，马佐林细碎5克，小茴香细碎3克，鼠尾草细碎1克，柠檬汁30毫升，橄榄油60毫升，洋葱酱25克，红胡椒碎3克，胡椒调至够味，盐调至够味。调料：橄榄油45毫升，红洋葱细丁125克，大蒜细碎20克，墨西哥辣椒（去籽的）2克，意大利米350克，香菜细碎4克，盐调至够味，黑胡椒细碎调至够味，鸡汤400毫升，柠檬3个，时令配沙拉6个，面包12个，份装黄油250克，沙拉');
INSERT INTO `recipe` VALUES (1242, 87, '瑞士牛肉扒', '1.土豆去皮切成1厘米宽厚、4厘米长，用冷水洗净，沥干水分。\r\n2.炸锅放植物油，加热至七、八成时，将土豆条放入，以旺火炸至土豆发硬时，移微火，将熟透时再上旺火，使其上色，当完全熟透并呈黄色时，取出沥干余油，撒盐备用。\r\n3.将牛外脊切20片，用肉拍子拍成薄片，用刀断其筋，剁成圆形，撒盐、胡椒粉，沾面粉，用热油煎成黄色，抹在烤盘内。\r\n4.将葱头切丝、胡萝卜切片，和香叶一起用油炒黄，再放上番茄酱，炒至油呈红色时放牛肉汤、西红柿块，沸后放盐调剂口味，浇在肉扒上，入烤箱烤熟为止。起菜时配炸土豆条共食。', '红褐色，郁香适口。', NULL, NULL, '牛外脊1750克，土豆500克，植物油100克，胡萝卜150克，葱头150克，鲜西红柿200克，面粉50克，番茄酱15O克，香叶2片，盐20克，胡椒粉少许，油150克。');
INSERT INTO `recipe` VALUES (1243, 87, '三明治', '1.将两片面包的各一面涂上蛋黄酱，将它们排在底层，放上腌菜及生菜叶及一半的番茄片，重复同样步骤放上第二片。\r\n2.将填充料放在底层面包上，根据需要调味。\r\n3.将上片面包盖在底层面包上，使其成为三明治，切去四边，并用两根牙签固定，一根在右上角，一根在左下角，每根距离边2-3厘米，从左上角到右下角切为两半，橄榄分别装饰两根牙签的顶部。\r\n4.将菜丝沙拉放在一片生菜上，并放在盘子的边上，并在菜丝沙拉上淋一些纯橄榄油，放上法香的装饰。', NULL, NULL, NULL, '白面包片，全麦或黑麦2个。下列各项平均加入共75克：火腿，咸肉，烤牛肉，鸡肉沙拉，金枪鱼沙拉，熏三文鱼，意大利肠。奶酪及番茄、生菜25克、菜丝沙拉30克、橄榄3克、番茄片45克、蛋黄酱35毫升、薯条1包、混和腌菜10克、盐调至够味、白胡椒调至够味。');
INSERT INTO `recipe` VALUES (1244, 87, '三文鱼芦笋蘑菇面条', '1.在柠檬汁橄榄油及盐胡椒混合物腌制大约20分钟；\r\n2.在一个大炒盘中加热其余油脂，然后加入洋葱大蒜，芦笋蘑菇，炒至汁液出来；\r\n3.倒入白酒，浓缩掉1/2，加入汤煮开，并小火煮3分钟左右，然后加入三文鱼，一半百里香及香料仪器煮1-2分钟，搅入其余原料，包括加热的面条；\r\n4.在放入餐盘前，再次将其搅匀，并用剩余的百里香放在上面作装饰。', NULL, NULL, NULL, '三文鱼，丁500克、柠檬1个(汁)、橄榄油50毫升、盐，磨碎的调至够味、黑胡椒蘑碎的调至够味、洋葱，细碎150克、大蒜细碎20克、本地黄蘼菇（金针菇）100克、芦笋尖，去皮，净的，切为3厘米长500克、白酒60毫升、淡蔬菜汤130毫升、意大利长面500克、鲜梨，去皮，核，成熟的1个；香草酱100克；鲜百里香枝5克；柠檬角切为片状1个。');
INSERT INTO `recipe` VALUES (1245, 87, '烧BAR－B－Q牛肋条', '1.将牛肋条做适当的调味； \r\n2.其余材料全部混合成为巴比Q渍汁； \r\n3.将牛肋条浸泡在巴比Q渍汁里； \r\n4.将烤箱以大火预热； \r\n5.取出浸泡过的牛肋条放进烤箱内，烤至金黄色即可。', NULL, NULL, NULL, '美国牛肋条6条、洋葱(碎)一大匙、大蒜(碎)一小匙。调味料：蕃茄酱1杯、芥末酱1大匙、辣椒酱数滴、辣椒油数滴、蜂蜜适量6、柳橙汁50CC、白醋15CC、盐、胡椒少许。');
INSERT INTO `recipe` VALUES (1246, 87, '烧烤美国嫩肋条', '1.美国牛肋条切段，约一寸长，将切好之牛肋以串烤方式串起；\r\n2.撒上准备好之综合调味粉，不需腌制，完成的肉串马上用碳烤或烤箱（175度）烘烤，约5-10分钟，熟度可依个人喜好增减加热时间，但建议以全熟烘烤；\r\n3.如能串上凤梨、青椒与胡萝卜一起烘烤，色香味美，口味更佳。', NULL, NULL, NULL, '美国牛肋条400公克（2人份）。调味料：1.细黑胡椒粉3大匙。2.香蒜粉3大匙。3.红辣椒粉1/2小匙。4.细盐3/4小匙。');
INSERT INTO `recipe` VALUES (1247, 87, '生牛肉配黑红椒', '1.在长柄浅锅中，加热橄榄油快煎上色，从锅中取出；\r\n2.混合酱油汁和芥末，并涂在牛肉表面；\r\n3.将牛柳滚上混合香草，将其用保鲜纸卷为长圆拉形，保持一晚；\r\n4.将生菜心洗好，去掉所有破碎叶或硬的部分，不要擦破，小心弄干；\r\n5.将冻好的牛肉切为2-3毫米厚的片，取下保鲜纸，排3-4片在盘上(每份)将用油醋汁拌好的菜叶放在盘中；\r\n6.用碎的海盐和混合物将牛肉调料及红胡椒和黑胡椒，1小勺橄榄油和黑香醋淋在肉的周围，排好鲜香草和沙拉。', NULL, NULL, NULL, '牛柳(本地)，净500克、橄榄油50毫升、酱油汁60毫升、地壮芥末60克、法香细碎50克、胡荽细碎50克、时萝细碎50克、小葱细碎50克、盐3克、红胡椒3克、黑胡椒3克、沙拉，橡树叶300克、菊苣叶300克、野箭生菜75克、油醋汁150毫升、混和鲜香草100克、橄榄油80毫升、黑香醋80毫升、黑胡椒碎调至够味、红胡椒碎调至够味。');
INSERT INTO `recipe` VALUES (1248, 87, '什锦饭布丁', '1.将葡萄干洗净，苹果，香蕉去皮后切成小丁放置备用。 \r\n2.将大米用温水浸泡2小时，然后与葡萄干，水果丁，鸡蛋混合一起，再加少许清水放入高压锅中蒸熟。放凉后装碗送入冰箱。 \r\n3.将白糖在锅中熬成焦糖浆，食用时将碗中布丁倒扣在盘中，然后将熬好的糖均匀地淋在表面即可。', '香甜可口。', NULL, NULL, '大米150克，葡萄50克，新鲜苹果50克，香蕉50克，白糖100克，鸡蛋150克。');
INSERT INTO `recipe` VALUES (1249, 87, '什锦盖饭', '1.以1大匙炒1/2量的蒜头、葱。倒入鸡肉，再加盐、胡椒粉。虾子与少许盐同炒。1碗水煮沸，将鸡肉煮熟、撕碎。\r\n2.热油1大匙炒葱、蒜，再加4碗水、高汤块、盐、胡椒粉、酱油同煮。\r\n3.将各种材料铺在白饭上，最后淋上制作的汤汁。', NULL, NULL, NULL, '鸡胸肉1块、虾仁半斤、白饭4碗、蛋皮切细。调味料：葱6根、蒜头4片、高汤块2个、盐、胡椒粉、酱油、水、色拉油、炸洋葱。');
INSERT INTO `recipe` VALUES (1250, 87, '时菜沙拉', '将生菜掰成不规则片状，黄瓜切成椭圆形片，番茄切成角、洋葱切成圈，工米笋切段，口蘑切片，青红椒去籽切成圈，然后拼摆在透明的玻璃盛皿内。把沙拉油和醋混合，倒入盛皿内，再放精盐、白胡椒粉调匀，制成油醋汁。食用时，将油醋汁摇匀、浇在时菜沙拉上即可。', NULL, NULL, NULL, '生菜40克、黄瓜25克、番茄25克、洋葱20克、玉米笋20克、口磨25克、青红椒20克、香菜5克、樱桃萝卜5克、精盐3克、白胡椒粉2克、沙拉油25克、醋25克。');
INSERT INTO `recipe` VALUES (1251, 87, '蔬菜春卷', '1.热煎盘，将芹菜、胡萝卜、蒜泥、蘑菇炒至汁出来；\r\n2.加入咖喱粉、糖、酱油、香油炒1-2分钟，不能糊，调味，放在容器里待凉后用；\r\n3.包春卷步骤：\r\n（1）将春卷皮放在台面上；\r\n（2）在春卷边上刷上蛋液；\r\n（3）将两勺馅均匀放在春卷皮中，将一边的皮折至中间；\r\n（4）然后将两边折至中间；\r\n（5）翻转成卷，沾点水封口，放入长盘，表面盖保鲜纸放入冰箱至使用时。', NULL, NULL, NULL, '芹菜丝175克、胡萝卜丝175克、蒜泥20克、豆芽450克、蘑菇丝10克、香油3毫升、酱油5毫升、咖喱粉1克、糖1克、菜油5毫升、春卷皮18个、鸡蛋1个。');
INSERT INTO `recipe` VALUES (1252, 87, '蔬菜汉堡', '1.混和胡萝卜、芹菜、洋葱、白胡椒，压去多余液体，加入核桃、蘑菇、小春葱、鸡蛋、辣椒籽汁及调味品。\r\n2.加入足够的饼干碎使其坚固，形成圆馅饼。\r\n3.如需要压入多些饼干碎，煎至金黄，放入125度的烤箱中大约15分钟，根据需要转动，放上奶酪，使其溶化。\r\n4.烤面包的两边，将菜饼放在底上，并用生菜洋葱片番茄和酸黄瓜，取另一片生菜，将菜丝沙拉放在上面，配以薯条。', NULL, NULL, NULL, '胡萝卜去皮切碎为680克、芹菜去皮碎115克、洋葱，去皮，碎115克、红椒，碎60克、绿椒，碎60克、核桃粉85克、蘑菇细碎225克、小春葱，细碎225克、胡荽，细碎30克、鸡蛋2个、饼干碎60克、香油3毫升、辣椒籽汁1毫升、盐调至够味、白胡椒调至够味、汉堡面包12个、洋葱片180克、时萝片180克、番茄片180克、生菜100克、薯条12包、奶酪片300克、菜丝沙拉250克。');
INSERT INTO `recipe` VALUES (1253, 87, '蔬菜角', '1.在汁锅中加热油，并加入小茴香粉，芥末粉咖哩或香叶及干辣椒，炒2分钟，加入大蒜；\r\n2.当蒜变为金黄时，加入土豆，盐，黄姜粉，水，盖上锅，使其蒸发，5分钟左右；\r\n3.加入鲜辣椒，在最后加入香菜，用小刀试土豆，熟了后冷却，并拍掉多余水。', NULL, NULL, NULL, '土豆丁2.5cm，煮的并冷却900克、植物油120毫升、小茴香籽3克、芥末籽1克、咖哩或香叶5个、干红辣椒粉2克、蒜蓉25克、盐5克、香菜鲜3克、黄姜粉1克、水30毫升、辣椒鲜细丁2个。');
INSERT INTO `recipe` VALUES (1254, 87, '蔬菜角配香菜、薄荷', '1.过滤面粉及苏打粉到一个盆中，加入盐，胡椒及小茴香，并搅好；\r\n2.揉入人造黄油并搅入酸奶，直至成为团状，面团应湿润且硬，盖上置入冷库1小时，最好放一晚；\r\n3.弄为直径为2.5厘米的球状，在有面粉的板或大理石上将其压为2毫米厚的圆形，一片片叠起，但每两片间撒好面粉；\r\n4.将堆好的圆切为一半，将会得到15-20片半圆面皮，将长边弄湿，从左下角向右边叠好，并沾好，弄成个袋状，小心的用勺填入蔬菜，然后盖上沾好；\r\n5.在锅中加热油，然后一个一个的投入面饺，轧至金黄色，并松脆，取出；用纸巾沾干，并根据要求加', NULL, NULL, NULL, '面粉100克、苏打粉1克、黑胡椒粉1克、盐1克、小茴香籽1克、植物黄油50克、白酸奶30毫升、植物油100毫升。');
INSERT INTO `recipe` VALUES (1255, 66, '甩袖汤', '1.把熟肉切成丝。菠菜择洗净切成小段。木耳切成小块。玉兰片，胡萝卜都切成丝。鸡蛋打在碗内用筷子搅匀。 \r\n2.勺内添汤，汤烧开后把肉丝、菠菜、玉兰片、胡萝卜、木耳都放入汤内，加酱油、花椒水、味精、绍酒，汤再开时用水淀粉勾米汤芡，随后将碗内的鸡蛋甩在汤内，撇去浮沫，加点香油，出勺装碗即成。', '色鲜艳，味鲜美，菜样多，是经济实惠的家常汤菜。', NULL, NULL, '肥瘦熟猪肉50克、鸡蛋1个、水发木耳10克、水发玉兰片25克、菠菜50克、胡萝卜5克、水淀粉、酱油、精盐、味精、花椒水、绍酒、肉汤、香油。');
INSERT INTO `recipe` VALUES (1256, 87, '双耳布丁', '1.将黑白木耳用温水浸泡，洗净泥沙，然后用鸡汤煮烂；\r\n2.将淀粉，盐，胡椒粉，五香粉放入煮烂的木耳中搅拌均匀，再度在火上烧开；\r\n3.放凉后置入冰箱，食用时取出用刀划成数块装盘。', '咸鲜可口，晶莹透亮，黑白分明，营养丰富。', NULL, NULL, '白木耳5克，黑木耳5克，鸡汤500克，淀粉200克，盐，胡椒粉，五香粉各少许。');
INSERT INTO `recipe` VALUES (1257, 87, '苏比兹汤', '洋葱切细末，把鸡肉汤加热。在锅内把黄油溶化，加进洋葱和盐，炒成黄色，把米撒入锅中，把热好的汤倒进去，煮25分钟，然后用箩过滤，把做好的奶油汤加热倒在鸡汤中，搅拌均匀，用盐，胡椒粉，豆蔻粉撒匀。', NULL, NULL, NULL, '洋葱900克，鸡汤500毫升，黄油60克，西米50克，做好的奶油汤250升，豆蔻，盐，胡椒粉各少许。');
INSERT INTO `recipe` VALUES (1258, 87, '苏格兰焗洋芋蔬菜', '1.洋芋切成厚片，包心菜切片，洋葱切碎。\r\n2.洋芋在加了盐的滚水中煮熟，捞出后捣成泥。\r\n3.包心菜也在盐水中烫一下。\r\n4.将奶油烧溶后，炒香洋葱屑，再加入洋芋泥与包心菜、两至三匙鲜奶油、葱屑及盐与胡椒，边煮边搅和。煮好的芋泥，置于烤盘(或碗)中，洒上乳酪丝，烤至金黄色即可。', NULL, NULL, NULL, '一斤洋芋、一斤包心菜、一粒洋葱、四分之三条奶油、液态奶油少许、乳酪(CheddarCheese)丝60克、盐与胡椒少许、葱屑(亦可不加)。');
INSERT INTO `recipe` VALUES (1259, 87, '苏格兰羊肉薏米粥', '1.将薏米洗净后浸泡4小时；把羊肉、葱头、萝卜、大蒜、芹菜、胡萝卜洗净；备用。\r\n2.将羊肉、薏米、葱米、萝卜、大蒜、芹菜、胡萝卜、适量水放在一起煮至羊肉熟透，边煮边除去浮沫杂物，再捞出大蒜、芹菜不用，取出羊肉、萝卜、胡萝卜切成小丁，放回锅内，加盐、胡椒粉、豌豆调好口味煮沸即可。', '鲜美可口，别有风味。', NULL, NULL, '羊肉500克，薏米150克，葱头50克，萝卜150克，胡萝卜150克，豌豆60克，芹菜50克。调料：大蒜25克，精盐、胡椒粉适量。');
INSERT INTO `recipe` VALUES (1260, 87, '酸白菜', '将白菜洗净，切5毫米左右粗的丝。将胡萝卜洗净，去皮，切3毫米粗的丝。将苹果洗净，削皮，切成花瓣形。将白菜丝与胡萝卜丝混台，撮上盐，加香叶、胡椒粒、茴香籽、干辣椒拌匀，装入耐酸器皿内，上面码一层苹果，再放一层白菜压实，上面加盖压上重物。器皿口加盖，放置38-40度处，使其发酵到有泡沫时，移于5度处存放。', NULL, NULL, NULL, '白菜2500克、香叶2片、苹果150克、胡椒粒少许、胡萝卜100克、茴香籽少许、精盐75克、干辣椒少许。');
INSERT INTO `recipe` VALUES (1261, 87, '酸黄瓜', '1.将黄瓜洗净，芹菜、茴香择净洗过、蒜洗净横切开。将洗净的黄瓜在耐酸器皿内码放一层，撒上一层芹菜、茴香、蒜、香叶、胡椒粒、干辣椒、青椒，上面再码放一层黄瓜。\r\n2.锅内放2500克水，煮沸后加盐搅匀，待盐融化后注入装有黄瓜的器皿内，器皿内加一盖用重物压之。器皿口加盖，置于38度左右处，使其发酵。待发酵至有沫时，移至5度处保存，冷透后即可食用。', NULL, NULL, NULL, '黄瓜2500克、青椒50克、茴香50克、芹菜50克、精盐100克、香叶2片、蒜50克、干辣椒2个、胡椒粒少许。');
INSERT INTO `recipe` VALUES (1262, 87, '酸奶沙司', '1.先将牛奶和酸奶混合均匀，再加入葱、蒜末拌匀。\r\n2.最后加入盐和胡椒粉，调好口味。', '奶油色，味酸辣鲜香醇厚。适用于蔬菜沙拉、土豆沙拉。', NULL, NULL, '（4-6份）酸奶500毫升，牛奶3大勺，葱末3大勺，大蒜（切成碎末）2瓣，精盐1小勺，胡椒粉适量。');
INSERT INTO `recipe` VALUES (1263, 87, '酸奶猪肉', '1.将猪肉洗净切成30块，猪蹄每只剁成4块，抹上盐腌片刻，把板肉、葱头洗净切丁，番茄、青椒洗净切块；备用。\r\n2.把锅烧热后倒入食油，待油温5成时，放入猪肉块煎至上色后，捞出放入焖锅，加适量水用文火焖煮。把锅烧热后放入黄油待熔化时，放入板肉丁炒至黄色，放入葱头丁炒透，加甜红椒粉略炒后，放入焖肉锅内；待焖至9成熟时放入青椒、番茄、酸牛奶拌匀用小火焖熟，即可食用。', '鲜酸辣嫩，独具风味。', NULL, NULL, '猪肉500克、猪蹄3只（约500克）、番茄250克，葱头、青椒各125克，板肉100克。调料：酸牛奶400克，食油75克，黄油30克，精盐5克，甜红椒粉15克。');
INSERT INTO `recipe` VALUES (1264, 87, '蒜味猪排', '1.猪排用刀背略拍松，洒少许盐，薄薄沾取一些面粉。\r\n2.在平底锅中烧油，将猪排入锅，煎至两面呈金黄色。\r\n3.蒜头用刀背拍碎去皮，入锅同煎至金黄，洒上迷迭香。\r\n4.倒出锅中太多的油，加入酒醋同煮，约5分钟后，加酒再煮5分钟。\r\n5.加1杯鸡汤，盖上锅盖用小火煮5分钟；其间需翻面两三次，若汤汁变干，可酌加鸡汤。\r\n6.猪排移置已热过的盘内，再用大火略收干汤汁，淋在猪排上即完成。', NULL, NULL, NULL, '4片厚猪排、各约2公分厚、盐及面粉少许、3大匙橄榄油、8粒蒜头、1小匙干的迷迭香（Rosemary）、2大匙白酒醋、1/4杯不甜的白酒、1又1/4杯罐头鸡汤。');
INSERT INTO `recipe` VALUES (1265, 87, '糖醋海蛎子', '1.将海蛎子洗净后，用沸水烫1分钟捞出控干，放至凉透，生菜叶洗净，备用。\r\n2.将木鱼汤倒入锅内煮沸后，加砂糖、酱油、味精、醋精拌匀成糖醋汁，待凉后放入海蛎子拌匀盛盘，配上生菜叶即可食用。', '酸甜爽口。', '木鱼汤：木鱼100克，海带50克，清水1000克，精盐适量．将海带洗净切成佛手状，放入锅内，倒入清水煮沸后，捞出海带（另用）、放入木鱼煮沸约2分钟加精盐调好口味过箩，即成木鱼汤。如买不到木鱼可用海蜒代替。', NULL, '海蛎子500克，生菜叶适量。调料：酱油50克，砂糖150克，醋精15克，味精5克，木鱼汤100克。');
INSERT INTO `recipe` VALUES (1266, 87, '桃仁布丁', '1.将鸡蛋黄与蛋白分开。蛋黄加4大勺白糖充分搅拌，直至白糖晶体完全溶化。\r\n2.将牛奶烧开，慢慢地加入蛋黄糖浆内，要边加边搅动，直到搅拌成均匀的酱状，然后置小火上稍加热。注意不能烧开。\r\n3.将琼脂粉用1大勺冷水发软，拌入热蛋黄酱中，搅拌至琼脂粉完全溶化，放置冷却，即为布丁坯料。\r\n4.从核桃仁中挑出8粒完整的留用，其余的切碎为细粒。\r\n5.将蛋白抽打成形如“奶油”状，和碎核桃仁拌入布丁坯料，拌匀后盛入大碗中，待其凝结即为桃仁布丁。\r\n6.取平锅置小火上，放入3大勺白糖，熔化为糖浆，加入整核桃仁，翻拌几下', '奶黄色，甜肥有核桃香味。', NULL, NULL, '鸡蛋3个，白糖7大勺，牛奶100毫升，琼脂粉3-4小勺，核桃仁75克。');
INSERT INTO `recipe` VALUES (1267, 87, '甜椒肉果', '1.将甜椒蒂部横切下一小块（留作帽），掏出椒籽和瓤，将另一头作为底，稍削平使能立稳，但要保持椒底不漏。\r\n2.牛肉末加鸡蛋和适量的水，搅拌至上劲，再加入切碎的洋葱，大蒜和蘑菇，搅拌成肉馅。\r\n3.将肉馅填进甜椒内，边填边压紧，一直填到肉馅冒出甜椒口，形成一尖顶为止\r\n4.在顶上撒一层干面包屑，盖一小块猪油，再扣上甜椒帽，然后放入中等大小的烤盘中。番茄切成块，放在周围，番茄汁倒入烤盘，撒上盐和胡椒粉调好味。\r\n5.烤箱预热至200度，烤盘置中层烤30-45分钟。', '味鲜美，制作简单，是比较流行的家常便餐。', NULL, NULL, '大绿甜椒4个，牛肉末400克，鸡蛋1个，洋葱半个，大蒜3-4瓣，蘑茹150克，干面包屑1-2大勺，猪油25克，番茄罐头500克，精盐、胡椒粉适量。');
INSERT INTO `recipe` VALUES (1268, 87, '甜酒炖洋梨', '1.梨削皮，保留蒂，从底部尽量挖取核。\r\n2.梨置汤锅中，底朝下，红酒倒入锅。\r\n3.柳橙、柠檬取皮切细丝，白色皮肉部份不要，果肉挤汁备用。\r\n4.皮丝、果汁与糖同置锅中，加盖用小火慢炖至梨软熟。\r\n5.整锅冷却浸泡2小时以上，使更入味。\r\n6.先捞取梨置于一旁，在锅中用大火烧煮汁；不时翻搅以免变焦苦，至汁收干至一半，淋在已置碟的梨上。', NULL, NULL, NULL, '4个西洋梨、2杯半甜的红酒、1个柳橙、1个柠檬、半杯细糖。');
INSERT INTO `recipe` VALUES (1269, 87, '通心粉沙拉', '1.通心粉3/4杯煮熟，并放置十分钟待凉。\r\n2.鸡胸肉用水煮熟后，取出并加葱、姜、酒调味。\r\n3.胡萝卜切丁，放水中煮熟、取出。\r\n4.蛋二个煮熟、取出、切丁。\r\n5.将以上四种材料加火腿丁、调味料混合拌匀。取生菜垫底，把拌过的通心粉沙拉倒在上面，即可食用。', NULL, NULL, NULL, '调味料：沙拉酱适量，盐、黑胡椒少量，糖二匙。');
INSERT INTO `recipe` VALUES (1270, 87, '土豆煎饼', '土豆放过滤网内先用清水冲再挤出水分。平底不沾锅放3大匙沙拉油加热，土豆丝约1杯，铺成一片圆形状，中火煎至金黄色。洒上1/4茶匙的盐，半边薄饼铺上西红柿2-3片，再铺菠菜和奶酪片，然后对折成一个馅饼继续加热至奶酪和菠菜软化即可。', NULL, NULL, NULL, '土豆1000克刨丝，菠菜600克切碎，西红柿4个切圆片，奶酪片250克，油少量，盐酌量。');
INSERT INTO `recipe` VALUES (1271, 87, '五彩什锦饭', '先把米清洗干净，再加入一定量定的水泡30分钟。把配料切成骨牌块，和调味料一起放入米中，一起用电饭煲蒸，然后将青豆色泽鲜地煮，等饭蒸好后加入青豆搅拌一下，就可以了。', NULL, NULL, NULL, '米2杯、水(与米同容量)2杯、鸡肉80克、胡萝卜1/2根、煮竹笋40克、干蘑菇2片、青豆(冷冻)10克、酒1大匙、酱油1/2大匙。');
INSERT INTO `recipe` VALUES (1272, 87, '西班牙式煎鱼', '1.将净鲜鱼肉切斜片，撒上盐、胡椒粉，沾面粉，用热油将两面煎黄控去油，烹白酸葡萄酒，放入牛奶以微火候之。\r\n2.大蒜切末，葱头切丝，用生菜油炒至微黄，放咖喱粉，炒出香味时加适量鸡汤调匀，青、红椒撇切丝放入，煮沸后加糖、盐调剂口味成沙司。\r\n3.将沙司浇在鱼上，在微火上煮至微沸。起菜时配煮土豆。', '咸鲜适口，清香味美。', NULL, NULL, '净鲜鱼肉1000克，煮土豆1000克，青、红椒250克，葱头100克，大蒜25克，牛奶250克，面粉250克，盐15克，糖10克，胡椒粉少许，咖喱粉5克，白酸葡萄酒100克，生菜油150克，鸡汤1000克。');
INSERT INTO `recipe` VALUES (1273, 87, '咸肉鸡蛋烤豆及薯条', '1.将咸肉番茄排在扒板上扒至微黄，翻面取下放在纸巾上，沾干多余油脂，保湿；\r\n2.用煎锅加热油并放入两只鸡蛋煎好后，小心转到盘中；\r\n3.另外用一只热锅放入烤豆及番茄汁，根据要求调味；\r\n4.炸薯条至微金黄色，并抖掉多余油脂，撒入盐并搅匀放入盘中，小心沾干多余油脂；\r\n5.排好配料装饰料摆在盘中。', NULL, NULL, NULL, '瘦咸肉50克；煎蛋2个；烤豆80克；薯条1包；扒番茄1个；盐调至够味；白胡椒调至够味。');
INSERT INTO `recipe` VALUES (1274, 87, '咸肉蔬菜三明治', '1.将两片烤好的各一面涂上蛋黄酱，另一片两面涂上蛋黄酱，将一片的蛋黄酱面朝上，两面都有酱的放在中央，另一片蛋黄酱向下盖在上面；\r\n2.将一片生菜放在第一片面包上，并放上一半的番茄片及肉，并轻微调味。重复上述步骤放在中央片上；\r\n3.将中央片放在第一片上，并将第三片面包蛋黄酱面朝下盖在中央片上，并使其成为三层三明治；\r\n4.用四根牙签分别在12点，3点，6点，9点并距边1厘米，用米使三明治更坚固，并在牙签上端放上橄榄，切去四边，并去掉屑；\r\n5.将三明治切为4个大小相等的角，并配以薯条，菜丝沙拉，及装饰。', NULL, NULL, NULL, '面包片（白或全麦）3个；番茄片40克；咸肉，熏的扒过90克；生菜25克；蛋黄酱25毫升；薯条1包；菜丝沙拉30克；橄榄3克；法香枝1个。');
INSERT INTO `recipe` VALUES (1275, 87, '乡村蔬菜鸡汤', '1.将鸡与汤煮开，转小火煮2小时，并去除浮物；\r\n2.加入番茄及杂菜，小火半小时；\r\n3.加入大蒜及香料，小火半小时；\r\n4.去除油脂，取出鸡并过滤；\r\n5.加入盐胡椒调料，并装饰。', NULL, NULL, NULL, '鸡1.5公斤以上，2只3公斤、鸡汤5.75升、杂菜丁500克、番茄，碎225克、大蒜，去皮100克、香料包1个、盐调至够味、白胡椒粉调至够味、装饰：杂菜切为菱形并焯过的500克、鸡肉熟的丁状500克、小葱细丁3克。');
INSERT INTO `recipe` VALUES (1276, 87, '香草美国牛扒', '1.美国牛扒用调味腌匀，待用。 \r\n2.意大利青瓜、干筍切片，煮熟拌碟。（可酌量加入少许植物牛油、盐拌匀）。 \r\n3.将美国牛扒用平底锅煎至所需熟度放上碟。 \r\n4.蕃茜碎加入植物牛油中拌匀，雪至凝固，切成小块，放上已煎好的美国牛扒面，即可供食。', NULL, NULL, NULL, '美国牛扒（肉眼、西冷或T骨）1件约重250克，意大利青瓜、干G各适量，蕃茜碎1茶匙，植物牛油约2汤匙。调味料：蒜盐、黑椒粉各少许。');
INSERT INTO `recipe` VALUES (1277, 87, '香肠沙拉', '香肠用开水煮两分钟左右，斜着切片。圆白菜切块，柠檬切成6等份。把香肠和圆白菜倒入容器，加入沙拉酱再略撒胡椒粉拌匀。', NULL, NULL, NULL, '香肠6根，胡椒少许，圆白菜1/4根，丘比沙拉酱5大匙，柠檬两片。');
INSERT INTO `recipe` VALUES (1278, 87, '香滑卷心菜丝色拉', '1.准备色拉用料：将胡萝卜洗净去皮，用擦碎板擦成丝。留下1个待用。用削皮刨将余下的1个胡萝卜刨成卷曲的片块(中心的部分弃掉不用)，置于一碗冰水中，然后再将它放入电冰箱内保存，以令它保持爽脆。将卷心菜洗净，并除去表面的萎叶，然后切成4份，弃掉根梗不用，用手动刨片器卷心菜刨成细丝(也可用食物加工机处理卷心菜)。洋葱去皮，留下少许根茎部分，然后直切为两半，平放于砧板上后，先用厨师刀向根部横切成片后再直切(注意切片时不要切开根部)，最后将洋葱切成粒。\r\n2.调制酸奶油及组成色拉：将糖、盐、胡椒、酸奶油及苹果醋放入', NULL, NULL, NULL, '中型胡萝卜500克、1.4千克白卷心菜1棵、中型洋葱1个、酸奶油酱汁，2汤匙糖、少许盐、胡椒、250毫升酸奶油、175毫升苹果醋、2茶匙芥末粉、2茶匙芫荽子、250毫升瓶装白色拉酱。');
INSERT INTO `recipe` VALUES (1279, 87, '香煎鳕鱼', '1.在汤碗中打散蛋与牛奶，使之均匀。\r\n2.在另一个碗中混合面粉、甜椒粉、胡椒、盐与洋香菜。\r\n3.将鳕鱼排逐片先沾蛋汁，再将两面沾取面粉，并甩去多余粉状物。\r\n4.油锅烧至八分热后，火力从大火改成中大火；将鳕鱼逐片入锅，或煎或炸，每面煎炸约2分钟，至表皮香酥，鱼肉已熟即可。食用时，可挤上柠檬汁，增添香味。', NULL, NULL, NULL, '4片鳕鱼（每片约180公克）、2个蛋、1/4杯牛奶、1杯面粉、1小匙甜椒粉（paprika）、1小匙新鲜洋香菜屑（parsley）、1/4小匙新鲜黑胡椒粉、1小匙盐、炸油。');
INSERT INTO `recipe` VALUES (1280, 87, '香蔬烤鸡腿', '1.烤箱预热至摄氏230度（华氏450度左右）。\r\n2.鸡腿一只分切成两块，用纸巾拭干表面；青、红椒分切成约1寸宽的长条；洋葱切片。\r\n3.在可直接受热的大烤盘中置鸡腿、青红椒与洋葱，洒橄榄油、百里香、盐与胡椒，混合至鸡块与蔬菜皆均匀沾到油与调味料。\r\n4.把鸡块与蔬菜在烤盘上尽量铺平，鸡皮朝上；入烤箱中烤约30分钟左右；或至鸡呈金黄色并已熟透即可。\r\n5.将烤好的鸡与蔬菜置于大碗中，加白酒至烤盘中，与鸡汁、余油混合，以中火加热搅拌1分钟成酱汁。将酱汁倒入碗中鸡块上，略微搅拌后即成。', NULL, NULL, NULL, '4只鸡腿、2个甜红椒（Bellpepper）、1个青椒、2颗洋葱、2大匙橄榄油、1小匙干的百里香（Thyme）半杯不甜的白酒、盐与胡椒适量。');
INSERT INTO `recipe` VALUES (1281, 87, '香蒜小牛排', '1.平底锅热后煎牛小排。\r\n2.两面煎至8-9分熟度后取出。\r\n3.锅内炒大蒜末，加进香菜末和数滴辣油。\r\n4.淋在已煎好的牛排上即可。', NULL, NULL, NULL, '美国带骨牛小排(每片约120公克)2片 、大蒜末1小匙、美国香菜叶末少许。调味料：辣酱油数滴、橄榄油1小匙、盐、胡椒适量。');
INSERT INTO `recipe` VALUES (1282, 87, '香杏马蹄沙拉', '1.将杏仁粒马蹄粒，用卡夫奇妙酱拌匀，将冬瓜切成四方薄片做成马蹄莲的花纹。\r\n2.将用卡夫奇妙酱拌匀的杏仁粒和马蹄粒装入作冬瓜做好的马蹄莲花纹里，然后再放入拌入味的胡萝卜条做花心。\r\n3.将做好的马蹄莲花放入用韭菜点缀好的盘中，即可。', NULL, NULL, NULL, '杏仁粒、鲜马蹄粒。配料：卡夫奇妙酱、冬瓜、韭菜、拌入味的胡萝卜条。');
INSERT INTO `recipe` VALUES (1283, 87, '小牛肝配奶油汁', '1.将咸肉炒至脆，去掉多余油脂，备用；\r\n2.将一半的咸肉在锅中加热，加入洋葱，蘑菇，香草及番茄，热的时候加入调味品慢慢炒，然后烹入雪利酒并浓缩一半；\r\n3.倒入烧汁，并浓缩一半，加入奶油放在一边备用；\r\n4.将肝用盐，胡椒调味；\r\n5.在橄榄油中炒小牛肝至四成熟并且两面棕色，放入盘中，倒上高质量奶油汁(60毫升)，将装饰及配料排在盘中。', NULL, NULL, NULL, '小牛肝，净的，片状180克；咸肉丁60克；洋葱细丁30克；混合香草3克；雪利酒(干)30毫升；蘑菇片20克；番茄去皮切成丁15克；橄榄油10毫升；烧汁20毫升；浓奶油100毫升；盐调至够味；黑胡椒粉，调至够味；米及豆1包；时令蔬菜1包；沙拉装饰1个；面包2个；份装黄油20克。');
INSERT INTO `recipe` VALUES (1284, 87, '蟹肉沙拉', '把蟹肉撕碎，除去骨，二分之一拌入沙拉内，放入盘中央呈丘形，再把其余二分之一贴在沙拉上，顶端放柠檬花，周围用鲜西红角、黄瓜花等装饰点缀，上面挤上马油奶司即可。', NULL, NULL, NULL, '蟹肉400克、蔬菜沙拉1500克、柠檬150克、蔬菜（点缀用）250克、马油乃司15克。');
INSERT INTO `recipe` VALUES (1285, 87, '辛香咖哩蛋', '1.将蛋黄和蛋白对半分开后，把蛋黄部份取出放入碗中，并用叉子碾碎。 \r\n2.将咖哩粉加入碾碎的蛋黄中加以混合。加入美乃滋搅拌，尽量搅拌均匀至里面大的颗粒都消失为止。如有必要，可以搅拌器处理上述程序，可以使成份充份混合成滑顺的浆状物。 \r\n3.加入切碎的韭菜或美国香菜末并搅拌均匀。以汤匙舀出搅拌好的混合物填回蛋白中，在顶端洒上些许香菜末装饰即可食用。', NULL, NULL, NULL, '白煮蛋6个，美乃滋3汤匙，咖哩粉2茶匙（视口味添加），盐、胡椒、韭菜或美国香菜末2茶匙（视口味添加）。');
INSERT INTO `recipe` VALUES (1286, 87, '新英格兰蛤汤', '1.在盖盖的锅中蒸蛤，到壳打开；\r\n2.用粗布或过滤器将汤过滤备用；\r\n3.取出蛤肉备用；\r\n4.将咸肉放入汤锅中，加入洋葱，芹菜炒至透明；\r\n5.加入面粉，制成淡面酱，逐渐加入汤及热奶，避免形成任何面团；\r\n6.小火煮30分钟，根据要求去除表面杂质，加入土豆直至嫩熟，加入蛤肉并搅入牛奶放入盐，胡椒，喔彻斯特汁，辣椒籽汁；\r\n7.根据配方配以装饰及配料。', NULL, NULL, NULL, '小号，去壳的蛤，拥税冲洗300克；鱼汤1升；咸肉，馅155克；芹菜，细丁115克；洋葱，细丁115克；面粉100克；土豆，0、5厘米丁340克；牛奶，烫的1升；盐调至够味；白胡椒调至够味；喔彻斯特汁2毫升；辣椒籽汁1毫升；浓奶油（与蛋黄拌匀）360毫升；蛋黄6个；小葱(细丁)5个。');
INSERT INTO `recipe` VALUES (1287, 87, '熏腌三文鱼', '1.熏三文鱼及腌三文鱼，分别切为薄片；\r\n2.小番茄切为一半，并挖净核，籽，填入辣根汁；\r\n3.在盘上排好每样食品，撒上鲜碎胡椒在两种三文鱼上。', NULL, NULL, NULL, '熏三文鱼55克、腌三文鱼55克、辣根汁15毫升、柠檬角(1/4个)30克、鲜混合沙拉25克、选择汁10毫升、烤面包片2个、份装黄油20克、小番茄(10-15)2个。');
INSERT INTO `recipe` VALUES (1288, 87, '鸭肝卷配蕃茄酱汁及烤法包', '1.把鸭肝用干净布擦干，抹干水分；\r\n2.把鸭肝用盐，白胡椒，白兰地两面调味；\r\n3.把鸭肝放入冰箱一晚；\r\n4.把鸭肝从冰箱中拿出在室温下放一小时；\r\n5.把烤箱定到120度；\r\n6.把准备好的食物放入模中，培根码在模子内层；\r\n7.把鸭肝填满模子；\r\n8.最后把培根盖在鸭肝上；\r\n9.把模子放在装热水的盘里，水温在70度-73度1个小时，恒温在55度-58度；\r\n10.把熟的卷放在冷水盘里10-15分钟，使之冷下来；\r\n11.用锡纸包上，用一片薄本片压在肝上面，盖好盖，放在冰箱2-3天；\r\n12.把盖', NULL, NULL, NULL, '鸭肝900克，盐30克，白胡椒3克，国产白兰地50亳升，咸肉450克。');
INSERT INTO `recipe` VALUES (1289, 87, '烟熏素烧鹅', '1.将调味料调匀备用。\r\n2.热油锅，放入3大匙油，炒香香菇丝、笋丝、红萝卜丝，放入1/2的调味料拌炒，熄火置一旁备用。\r\n3.取刀将整叠腐皮对半裁开，每半边约比一个汤锅盖大一点（太大就再对半切开），将所有腐皮均分二等份（或四等份，视素烧鹅包的大小而定），腐皮的形状不一定很规则。\r\n4.取刷子沾调味料在一张腐皮上刷均匀，再取下一张放在前一张刷好的腐皮上再刷（这样腐皮的两面都可以沾到调味料），将一份腐皮都刷完后，再中间放上炒好的馅料，将腐皮卷起后，将下层再刷上调味料，用保鲜膜包好，一旁备用。\r\n5.重复上一', NULL, NULL, NULL, '新鲜腐皮1包、香菇丝1/2杯、红萝卜丝一碗、熟笋丝1/2杯。调味料：酱油6大匙、糖2大匙、香油4大匙、甘草水（或香菇水）1杯。');
INSERT INTO `recipe` VALUES (1290, 87, '羊膝批同烩羊臀肉', '1.羊膝烩熟去骨起肉，备用。\r\n2.番茄、日式萝卜切成条备用。\r\n3.将咖喱片溶于羊清汤内。\r\n4.苋菜灼熟，摊放在保鲜纸上，再连保鲜纸一起放进模内。\r\n5.将羊膝肉、番茄及萝卜平均放进模内。\r\n6.把羊清汤慢慢倒进模内。\r\n7.用剩余的苋菜铺面，存放于雪柜约3-4小时，直至变成喱状，成羊喱批，切片备用。\r\n8.羊臀肉放在卤水汁内烩煮3小时至腍，切片，连羊膝批上碟拌以沙律菜小麦片、沙律汁及油醋汁即可。', NULL, NULL, NULL, '羊膝2只、羊臀片60克、苋菜1公斤、番茄10只、日式萝卜1条、羊清汤500毫升、喱片15块、沙律菜30克、脆薄片2块。调味料：芥末籽醋汁2汤匙、沙律汁2汤匙卤水汁：老抽半公升、姜100克、香茅8支、干50克、蒜头50克、鸡汤3公升。');
INSERT INTO `recipe` VALUES (1291, 87, '洋白菜沙拉', '将洋白菜洗净切丝，青红椒去籽切丝、胡萝卜去皮切丝，一同放入盛皿内，加马油奶司，调以精盐、白胡椒粉调匀。在盘内垫上生菜，将洋白菜丝倒入中央呈丘形，四周围摆玉米笋、香菜和樱桃萝卜。', NULL, NULL, NULL, '洋白菜120克、青红椒25克、胡萝卜25克、生菜50克、精盐3克、白胡椒粉2克、醋8克、马油奶司35克、玉米笋10克、香菜5克、樱桃萝卜5克。');
INSERT INTO `recipe` VALUES (1292, 87, '洋葱烤饭', '1.洋葱切成碎丁状，米洗净沥干备用。\r\n2.以奶油炒香洋葱，加入米以中火炒至半透明，放入烤盘中。再倒入滚水，加盐、胡椒粉、蒜头、以锡箔纸将烤盘完全覆盖。\r\n3.将烤盘放入已预热至摄氏一百八十度的烤箱中烤二十分钟后，继续置于烤箱中焖二十分钟。\r\n4.取出烤盘，挑除蒜头，将米饭拌松，放入烤箱中保温，待食用时再取出。', NULL, NULL, NULL, '洋葱一个、米五杯（电饭锅量杯）、滚水五杯（电饭锅量杯）、奶油50公克、蒜头三粒、盐、胡椒粉适量、锡箔纸一张。');
INSERT INTO `recipe` VALUES (1293, 87, '椰奶及玉米片炸大虾配蒜汁', '1.混合鸡蛋及牛奶成为蛋液备用；\r\n2.另外，混合椰奶粉及玉米片，搅好；\r\n3.虾沾面粉然后蛋液，再玉米片，椰奶粉；\r\n4.再重复上述步骤一次，将虾放在盘上；\r\n5.放入热油中，一个一个的炸，直至脆的程度到金黄，除去多余的油并轻微用盐胡椒调味；\r\n6.转每份入盘中，配以常规装饰及配料。', NULL, NULL, NULL, '大虾带尾去头去皮150克；玉米片，碎的100克；椰奶粉25克；面粉100克；鸡蛋打好的2个；牛奶30毫升；盐调至够味；胡椒调至够味；柠檬1个；法香枝2个；沙拉装饰2个；辣椒蒜蓉汁100毫升；菜油（炸用）150毫升。');
INSERT INTO `recipe` VALUES (1294, 87, '椰肉焖鸡块', '1.将鸡洗净去头脚剁成方块，放入沸水煮至5成熟捞出控干，椰肉切丝；葱头、蒜瓣洗净切片，辣椒去蒂籽与杏仁一起捣泥；备用。\r\n2.把锅烧热后倒入食油，待油温5成热时，放入葱头片、蒜片、椰丝炒金黄色，盛入焖锅，放入鸡块，辣椒、杏仁泥、香菜籽粉，小茴香籽粉、椰汁拌匀煮沸后，改用温火焖至熟软。汤浓时，加精盐调好口味，撒上香菜末即可。', '郁香味浓。', NULL, NULL, '鸡一只（约1250克），椰肉300克，葱头100克，杏仁5克。调料：食油75克，蒜瓣10克，香菜10克，辣椒10克，香菜籽粉、小茴香粉各5克，椰汁、精盐各适量。');
INSERT INTO `recipe` VALUES (1295, 87, '义式香煎肋眼牛排', '1.在生牛排上加进盐、胡椒和香料。\r\n2.将2-3汤匙的橄榄油倒进已加热的煎锅中。\r\n3.大蒜切片并丢入煎锅，煎至焦黄后取出，再倒入少许橄榄油并将肋眼牛排放入煎锅，每面约煎3-5分钟，时间长短视个人喜好牛排的熟度而定，起锅前加入已煎过的蒜片即可。', NULL, NULL, NULL, '1/2-1厘米厚的肋眼牛排1份，橄榄油1/2杯，大蒜6-8颗，迷迭香少许（选择性），盐胡椒，红酒1/2杯（选择性）。');
INSERT INTO `recipe` VALUES (1296, 87, '印度咖喱菜花', '菜花洗净去梗切小块，胡萝卜去皮洗净刻成花形切圆片。先将菜花放入沸水内烫约2分钟，再放入胡萝卜片烫1分钟，捞在盆内趁热放入咖喱粉拌匀，约2分钟后再用凉水冲洗去咖喱渣，控去水分放入瓷缸内。糖、水、干椒放锅内煮沸，离火凉后放醋精调好口味倒入菜花中，放零上1-3度处浸泡24小时即可。', NULL, NULL, NULL, '净菜花100克、胡萝卜500克、糖1000克、醋精30克、咖喱粉15克、干椒少许，水3000克。');
INSERT INTO `recipe` VALUES (1297, 87, '英国薄煎饼', '1.先将酸牛奶、面粉、水、芝麻和盐调匀（透明杯装清水，将调好的面糊用筷子沾一点，滴入水中面糊不发散，不混浊为适当浓度，象厚一点的稀饭）将平底不沾锅以小火热锅后（可倒入少许油或奶油亦可），放入1大瓢面糊，转动锅使面糊薄薄散开，等饼边呈金黄色即可翻另一面，再煎一会儿即可取出装盘。 \r\n2.在煎饼上涂奶油，然后洒上糖，挤上柠檬汁，再卷起。（也可涂果酱、蜂蜜、奶酪、腐乳等）甜、咸皆可。', NULL, NULL, NULL, '酸牛奶1杯，或牛奶1杯加柠檬一些白面粉或全麦粉、水各适量。盐1/2茶匙，白或黑芝麻粉2茶匙，柠檬切块（挤汁用）果糖、奶油各适量。');
INSERT INTO `recipe` VALUES (1298, 87, '油饭', '1.圆糯米加白米洗净，加水1又1/2杯泡3小时。\r\n2.肉丝加酱油、太白粉，腌10分钟。\r\n3.香菇切丝。\r\n4.锅中加油，中火炒香菇丝、肉丝，不要太熟。\r\n5.加1/2柸香菇水、油酥、五香粉、酱油及米，炒至水吸干。\r\n6.再加1又1/2柸米水（米泡时加盖时间以汤匙轻拌，炒前米水倒出），炒至水快干移至康宁锅，微波7分钟，取出搅拌，若太干再加些香菇水，再微波5分钟（若火力不够，米还未熟，可再续微波3分钟，视家里的微波炉不一），取出搅拌。', NULL, NULL, NULL, '圆糯米2柸、白米1柸、肉丝适量、香菇4朵、油葱酥适量、五香粉少许、酱油3大匙。');
INSERT INTO `recipe` VALUES (1299, 87, '鱿鱼拌芭乐', '芭乐去籽、切细丝，泡盐水1分钟后洗净沥干，洋葱末拌盐腌5分钟后压干水分，将鱿鱼、洋葱与沙拉酱、黑胡椒粒、番茄酱拌搅均匀，以芭乐丝铺盘底，并将拌匀的鱿鱼酱捕于其上即可。待食用时，再将所有材料一齐拌匀食用。', NULL, NULL, NULL, '芭乐2粒、鱿鱼罐头200克、洋葱末1/4粒、盐1/4茶匙、沙拉酱3大匙、黑胡椒粒1/4茶匙、番茄酱1茶匙。');
INSERT INTO `recipe` VALUES (1300, 87, '有氧蔬果菜餐', '1.先将苦瓜、甜椒、苹果、小黄瓜、西洋芹榨成汁。 \r\n2.剩余的残渣与蛋混合，并加入盐。\r\n3.再将蛋放入锅中煎熟即可食用。', NULL, NULL, NULL, '苦瓜半条、甜椒1个、盐1/2小匙、苹果1个、小黄瓜1/4个、西洋芹2片、蛋2个。');
INSERT INTO `recipe` VALUES (1301, 87, '鱼肉汉堡', '1.炸鱼饼直到金黄，两面要均匀；\r\n2.切开并烤面包，然后涂好黄油，放上生菜番茄及甜黄瓜；\r\n3.将制熟的鱼肉饼放在底面包上，转入盘中，根据要求调味，配以它它汁；\r\n4.将菜丝沙拉放在生菜叶上，上面放上法香枝，将这些放入盘中并配以薯条。', NULL, NULL, NULL, '鱼肉汉堡1个、面包糠100克、面粉80克、鸡蛋2个、生菜15克、番茄片50克、甜黄瓜片10克、它它汁40毫升、汉堡包1个、黄油30克、薯条1包、盐调至够味、白胡椒调至够味。');
INSERT INTO `recipe` VALUES (1302, 87, '鱼肉沙拉', '1.先将净鱼肉放在锅内加上切碎的胡萝卜、葱头、芹菜及香叶、盐、醋、柠檬片加水煮熟，凉透后切成长方形片。\r\n2.把蔬菜沙拉放入盘中呈丘形，将鱼片贴在沙拉上，顶端放柠檬花，周围用酸黄瓜、鲜西红柿角、青叶等点缀、上面挤上马油奶司即可。', NULL, NULL, NULL, '净鱼肉500克、蔬菜沙拉1500克、柠檬150克、马油奶司15克、蔬菜（点缀用）250克、胡萝卜、元葱头、芹菜各25克、香叶1片、盐10克、醋5克。');
INSERT INTO `recipe` VALUES (1303, 87, '杂果布丁', '1.将琼脂用凉水泡软，放入锅内，加清水适量，上火微沸后过箩稍凉待用。\r\n2.将水果洗净，去皮，籽（核），切碎，加入白糖，再加入琼脂液及打成泡沫的蛋清，搅匀成布丁料。\r\n3.取盆，周围抹上葡萄酒，内放布丁料，入冰箱冷冻。\r\n4.食时，将鲜奶油打成泡沫，将布丁取出，奶油泡沫浇在上面即可。', '果香浓郁，苦甜可口。', NULL, NULL, '杂果（苹果，红瓤西瓜，杏，桃）250克，白糖40克，鸡蛋600克，鲜奶油，琼脂少许，葡萄酒适量。');
INSERT INTO `recipe` VALUES (1304, 87, '炸黄油鸡卷', '1.把带骨鸡胸肉撒上盐和胡椒粉后，用手揉成圆按形，再裹上面粉、鸡蛋、面包粉待用。\r\n2.用旺火把色拉油加至五、六成热后，改用微火，把裹好的鸡肉放入锅中炸熟，然后改用旺火炸至色黄，取出沥干后放入盘中，浇上热黄油即可食用。', '鲜嫩清香，呈金黄色。', NULL, NULL, '带骨鸡胸肉200克，面包粉50克，鸡蛋2只，盐、胡椒粉少许，面包粉100克，黄油30克，色拉油少许。');
INSERT INTO `recipe` VALUES (1305, 65, '玻璃鱿鱼', '鱿鱼用温水泡1小时，淘洗净，去掉头须、蒙皮，用快刀片成长约9厘米、宽约3.5厘米完整、均匀的薄片，放入碗内用温水淘洗一次，沥干水加白碱拌匀，加开水盖严，焖至水不烫手时，将水滗去，再加开水盖焖，如此反复至鱿鱼色白、透明、体软，再用清水漂起待用。菠菜心洗净，入沸水锅中烫熟，捞起放汤碗中热底矿锅内烧清汤，沸后放入鱿鱼片，煨两次后，捞出盖在菠菜心上。锅内另用清汤烧沸，加胡椒粉、盐、味精、料酒吃味后，灌入汤碗内即成。', '汤色清澈，鱿鱼色白透明如同玻璃，配以碧绿菜心，色清爽，入口滑嫩，汤味清鲜。', NULL, NULL, '干鱿鱼一张、约150克、菠菜心50克。清汤500克、胡椒粉1克、盐2克、味精0.5克、料酒10克。');
INSERT INTO `recipe` VALUES (1306, 87, '炸鸡翅配斑兰叶', '1.取24个鸡翅，并翻出肉，使骨露出并修净，放入混合盒中，备用；\r\n2.将洋葱、大蒜、蜂蜜、红甜汁、大料粉、喔彻斯特汁、HP汁、酸梅汁、菜油及香油搅至滑细酱，加入调味品；\r\n3.将混合调料倒入鸡翅中，并搅好，盖好冷藏一晚（最好）或至少一晚；\r\n4.用斑蓝叶包扎好鸡翅（简单的节），在鸡翅尾部留出大约15厘米与鸡骨在一起，并留出突出的斑蓝叶朝向客人一头；\r\n5.在热油中炸大约5分钟，或直至鸡翅内部熟透，转入盘中，6个一份；\r\n6.排好常用装饰和配料。', NULL, NULL, NULL, '鸡翅1千克、蒜酱40克、洋葱酱125克、蜂蜜汁90毫升、红甜汁30毫升、大料粉5克、喔彻斯特汁45克、HP汁15毫升、酸梅汁30毫升、油60毫升、香油3毫升、斑蓝叶（包裹用）24个、油（炸）150毫升、盐调至够味、黑胡椒粉调至够味。');
INSERT INTO `recipe` VALUES (1307, 87, '芝士牛油焗蟹饼', '将瘦肉、肥肉粒、膏蟹的蟹膏、咸蛋黄搅拌做成肉饼，再加鸡蛋蒸8分钟，然后将切成块的蟹放在底部，焗3分钟。', NULL, NULL, NULL, '瘦肉、肥肉粒、膏蟹的蟹膏、咸蛋黄、鸡蛋。');
INSERT INTO `recipe` VALUES (1308, 87, '芝士威化色拉', '1.照主食谱所示方法，做好芝士威化面团后放入电冰箱内冷藏；同时依所示方法预备生菜、菠菜及香芹菜，然后放入大碗内。撇去树莓及无花果不用。将4汤匙的香油醋及2茶匙的芥末酱以及盐和胡椒搅拌，再逐渐加入175毫升的橄揽油搅匀，以令油醋汁浓化成乳状。\r\n2.烘烤3只红椒：先将烤箱烧热，然后把红椒放在烤架上烤10-12分钟，直至红椒皮变黑及起泡为止。烤架与热源的距离约为10厘米。烤好后，立刻将红椒放入一胶袋内，并将它封闭，待它们冷却后，扯出椒蒂，将它切为2份，去子后，以流动的清水冲洗、抹干，最后切成细条。\r\n3.隔去', NULL, NULL, NULL, '雅芝竹(Artichoke)烤红椒是杂菜色拉中一对美好的搭配。松子令色拉有质感，而以香油醋(BalsamicVinegar)调制的酱汁更为色拉带来无比的香气。');
INSERT INTO `recipe` VALUES (1309, 87, '煮芋头鸡肉丸子', '1.先将上汤和调料汁煮开，再把冻丸子放进去。待再次煮开以后，将芋头加进去，煮至变软。最后把摘掉筋的扁豆放进去一起煮。\r\n2.如果采用生芋头，应先去皮，用盐水揉去表面粘液，然后水洗，再用冷水煮，煮开之后即厅使用。削皮时先切掉两头，再从大头向细小的方向削，要纵向用刀，削好的芋头的侧面呈六面形，这样味道易于渗入，也能防止煮破。', NULL, '将鸡肉丸放进汤里，待煮开后，再放入冻芋头。用竹签扎芋头，在很容易穿透的柔软程度时，放入扁豆。冻的蔬菜与新鲜的相比，味道多少欠缺一些，可以调节气味时偏重一点，或添加一些诸如柚子的香味料。', NULL, '（2-3人量）冷冻芋头200克，鸡肉丸子200克，扁豆50克，日式上汤一杯半，柚子少许。调料汁：砂糖3汤匙，酒2汤匙，味淋（日式料酒）1汤匙，酱油3汤匙。');
INSERT INTO `recipe` VALUES (1310, 83, '菜肉卷天妇罗', '1.金针菇切掉根部，略微拆散。葱配合猪肉的宽度切开，姜削皮后切丝，泡水后隔水。\r\n2.1－2片猪肉垂直摊平，撒少许盐和胡椒粉。盛上金针菇、葱和生姜各1/8份量于肉片上，卷起并以牙签固定。\r\n3.在碗內把蛋打散，加入冷水混合，把面粉撒入混合。\r\n4.油烧热至中温（170度），肉卷沾上脆衣后入油中炸透。拔掉牙签，把长度切2－3段后即可。可依喜好挤上柠檬汁。', NULL, NULL, NULL, '猪的肩里脊肉薄片300克、金针菇2袋、葱1/3束、生酱适量、盐适量、胡椒粉适量、炸油适量、脆衣、蛋1个、冷水3/4杯、面粉1杯。');
INSERT INTO `recipe` VALUES (1311, 83, '茶壶蒸海鲜', '1.鸡肉，鱼肉洗净切片，柠檬分切4片，贝芽菜切段，蛤蜊、鲜香菇洗净备妥。 \r\n2.将鸡肉用1/8小匙盐腌制，鱼肉、蛤蜊分别以开水川烫后捞出。 \r\n3.茶壶内放入鸡肉、鱼肉、蛤蜊、香菇及煮开的料，盖上盖子移入蒸笼，大火蒸3分钟取出，撒上贝芽菜并立即盖好，即可趁热食用。食时淋上柠檬汁，先倒汁品尝，再吃壶内材料。', NULL, NULL, NULL, '鸡肉40克、鱼肉40克、柠檬（小）1/2个、贝芽菜（萝卜樱）适量、蛤蜊40克、鲜香菇（小）4朵、煮出汁3杯、盐2/3小匙、淡口酱油少许。');
INSERT INTO `recipe` VALUES (1312, 83, '茶巾寿司', '1.将葫芦条、香菇煮好备用。 \r\n2.将鸡蛋打开，用平底锅摊成薄饼。\r\n3.把藕片与煮好的葫芦条、香菇一起切碎，与寿司饭拌匀备用。\r\n4.把拌好的寿司饭放在鸡蛋皮中包好，用煮好的菠菜梗系好。\r\n5.小心地把蟹子堆在寿司上即可。', NULL, NULL, NULL, '寿司饭、藕片、葫芦条、香菇、蟹子、菠菜、鸡蛋。');
INSERT INTO `recipe` VALUES (1313, 83, '大福袋', '1.米洗净放入电锅內，用水浸30分钟，海带抹净，与米一起煮，煮好的饭与合成醋混合，吹干成寿司饭。\r\n2.虾洗净，去肠泥，用盐水烫熟，晾干，冬菇洗净浸水去蒂，切成薄片状。\r\n3.鸡蛋搅拌均匀，加砂糖1汤匙及盐调味，放入倒有沙律油的煎锅內，煎成蛋块，最后切成丝状。\r\n4.油豆腐先用一只筷子搓松表皮，使两边容易撕开，制成袋状。\r\n5.把寿司饭捏成小块，塞入豆腐袋中，表面依次加入冬菇、虾、蛋丝，最后排列上碟。\r\n6.入油锅，以中火炸至金黄色即可。', NULL, NULL, NULL, '米1杯、水1杯、海带10厘米、合成醋（米醋1/3杯、糖2汤匙、盐1茶匙）、油豆腐皮8个、冬菇4个、大虾8只、鸡蛋3个。');
INSERT INTO `recipe` VALUES (1314, 83, '豆腐皮寿司', '1.米饭趁热拌上白糖、白醋、黑芝麻，放凉备用。 \r\n2.豆腐皮剥开，装入米饭即可。', NULL, NULL, NULL, '豆腐皮1包，米饭4碗，黑芝麻少许，白糖、白醋各3大匙。');
INSERT INTO `recipe` VALUES (1315, 83, '豆腐沙拉', '1.铺上苜蓿。\r\n2.洒上干燥海苔丝、小鱼朝。\r\n3.淋上调味酱油。', NULL, NULL, NULL, '嫩豆腐1盆，山芹菜1支，香菜1颗，小黄瓜半条，西洋芹1支，苜蓿5克，干燥海苔丝5克，小鱼朝10支，调味酱油2汤匙。');
INSERT INTO `recipe` VALUES (1316, 83, '豆沙白玉团子', '1.白玉粉加入水搓匀，然后将之分成10粒的粉团子，用手指轻按压扁它。 \r\n2.将水煲沸后，放入粉团子，1－2分钟后，将浮起来的小团子放入冰水中。 \r\n3.3分钟后将白玉团子捞起，盛起，然后再加红豆茸伴食。', NULL, NULL, NULL, '白玉粉200克、水约1杯、红豆茸适量。');
INSERT INTO `recipe` VALUES (1317, 83, '鲑鱼饭团', '1.取三角饭团的模子，铺上白饭，中间当下空位放入鲑鱼，再填补上少许白饭，成一完整白饭团。 \r\n2.依饭烤的宽度切一段海苔片，围绕贴上白饭团。 \r\n3.在饭团两侧撒上芝麻海苔即成。', NULL, NULL, NULL, '白饭2碗，鲑鱼60克，海苔（半张），芝麻海苔酌量。');
INSERT INTO `recipe` VALUES (1318, 83, '鲑鱼生鱼片沙拉', '1.鲑鱼用厚质的卫生纸将鱼肉表面的水吸干，均匀抹上盐和糖，然后撕一段保鲜膜包裹，放入冰箱腌入味，待1小时后取出。 \r\n2.在腌鱼肉的空档，将柠檬切片，洋葱切丝、西洋芹切段、胡萝卜切片、巴西里分切成小朵，置入平浅的盘中，倒入葵花油在盘中淹过蔬菜为准，腌1小时。 \r\n3.鱼肉去保鲜膜，且清水洗净，放入蔬菜腌盘中，腌6小时。 \r\n4.山芹菜、香菜切碎，和紫苏昆布丝一起置碗。\r\n5.鱼肉取出切片，铺在碗中上桌；食时，再和山芹菜、香菜、紫苏昆布丝拌匀。', NULL, NULL, NULL, '鲑鱼肉块2块，糖3匙 ，盐2匙，柠檬1个，洋葱半个，西洋芹1支，胡萝卜半个，巴西里30克，葵花油1碗，山芹菜1支。');
INSERT INTO `recipe` VALUES (1319, 83, '海鳗鸡骨汤', '1.制作鸡骨汤：把鸡骨焯至发白。用水洗去多余脂肪、血污。把鸡骨、昆布、水放入锅，用旺火煮。煮沸，改用微火。撇出汤末，去除怪味。使汤保留滚的状态，熬到二成（水熬去一半），用法兰绒布过滤。\r\n2.各种材料的处理：海鳗——连皮带骨刺切成肉片，放入170－175度油中，炸至表面变色。牛蒡——用棕刷刷净表面，纵向切刻刀纹；放菜板上，像削铅笔那样，边用手转动牛蒡，边削竹片（竹叶状）；用醋漂洗，去除涩味，中间换几次水，漂洗后将水沥干。鲜冬菇——切去硬蒂，再切成薄片。独活——切成3厘米长段，厚削皮，旋削带状薄片；叠起切成', NULL, NULL, NULL, '海鳗400克、牛蒡1/2根、鲜冬菇4朵、红萝卜1/4根、独活5厘米、西芹1/4根、三叶芹1束、鸡汤600毫升（鸡骨5只份量、水5升、瓜状昆布2片）、黑胡椒适量、淡口酱油2茶匙、盐1/2茶匙、酒1茶匙、醋、炸油。');
INSERT INTO `recipe` VALUES (1320, 83, '海鲜刺身', '1.把萝卜丝码在盘子底下，把裙带菜码一些在萝卜丝上。 \r\n2.把海鲜切成长约5cm、厚约1cm左右的细条，铺在萝卜丝上。', NULL, NULL, NULL, '新鲜的加吉鱼、三文鱼、金枪鱼、北极贝、裙带菜、白萝卜、绿芥末、日本酱油。');
INSERT INTO `recipe` VALUES (1321, 83, '海鲜煎饼', '1.做外皮：面粉加适量水（比例3比1调和）。\r\n2.做内馅儿：主要配料——猪肉、虾仁、章鱼、墨鱼、蛋1个。\r\n3.调好的面粉加入高丽菜、葱花搅拌后，一半先下铁板，而后将配料放入，再将剩余的面粉放入配料。\r\n4.用中火煎至略带金黃色后，翻面，时间约为3-4分钟。\r\n5.翻面后，同样以3－4分钟煎成金黄色，起锅。\r\n6.准备好柴鱼、沙拉酱、海苔和调味酱，煎饼就算完成了。', NULL, NULL, NULL, '面粉、水、猪肉、虾仁、章鱼、墨鱼、蛋1个、高丽菜、葱花、柴鱼、沙拉酱、海苔和调味酱。');
INSERT INTO `recipe` VALUES (1322, 83, '海鲜小火锅', '1.熬制汤精可用猪骨、鸡骨、牛骨熬，家庭使用汤精须1副猪骨熬20个小时，鸡骨熬的时间可短些。\r\n2.汤精熬好后存放在冰箱，使用时舀一勺，对水和昆布（海带）一起煮制高汤。\r\n3.依个人受好将食物下锅。玉米、白菜、芋头、香菇、番茄愈煮汤越甜，可以早下锅。', NULL, NULL, NULL, '生豪1粒，蚵仔8颗，蛤蜊6颗，鱼片3片，透抽1大片，大草虾2尾，白菜1/4颗，金针菇20克，鱼板1片，豆腐1块，福州丸1颗，芋头1小块，玉米1/3棵，香菇1朵，番茄1片，高汤1锅，冬粉或面条。');
INSERT INTO `recipe` VALUES (1323, 83, '荷兰豆烧麦', '1.荷兰豆去筋切成粗丝，分成12份。 \r\n2.长葱切成末，沾上山慈姑粉，与肉末混合，搅拌成肉酱，分成12份。 \r\n3.将1中切好的荷兰豆用2中做好的肉酱包好，放入涂了色拉油的耐热容器中（中间空开，呈放射状）。 \r\n4.将3中的耐热容器松松的包上一层保鲜膜，放入微波炉加热7至8分钟。根据个人喜好添加芥末、酱油即可。', NULL, NULL, NULL, '荷兰豆40节（100克），肉末（猪肉末300克），长葱半根，山慈姑粉1大匙，酒1大匙，砂糖1小匙，香油1小匙，盐半小匙，芥末酱，酱油各少许。');
INSERT INTO `recipe` VALUES (1324, 83, '金枪鱼调味酱', '1.黄瓜丝用水冲洗后沥干水分。 \r\n2.金枪鱼剁成酱，加入泡菜、葱、酱油和香油，搅拌均匀。 \r\n3.将2中处理好的酱盖在黄瓜丝上，酱中央挖出一个小凹坑，把鹌鹑蛋整个儿打在里面，淋上香油即可。', NULL, NULL, NULL, '金枪鱼红肉150克，泡菜（切成较粗的丝）80克，黄瓜（也切成较粗的丝）1条，万能葱（切成圈）6根，酱油、香油各半小匙，鹌鹑蛋4个，白芝麻少许。');
INSERT INTO `recipe` VALUES (1325, 83, '栗子铜锣烧', '1.低筋面粉加泡打粉过筛。\r\n2.蛋加砂糖打发加入1中拌均加蜂蜜。\r\n3.平底锅加少许油，将生料加入，煎至成金黄色。\r\n4.将3中间加入蜜红豆和切碎的栗子即可。', NULL, NULL, NULL, '低筋面粉180克、泡打粉2小匙、沙律油少许、蛋3个、蜂蜜3大匙、砂糖90克、蜜红豆1罐、栗子4至5颗、咖喱粉2大匙、高汤精半大匙、酱油1大匙。');
INSERT INTO `recipe` VALUES (1326, 83, '绿意通心粉', '1.准备材料：虾抽筋剥壳；切去菜花根部的硬块；在煮沸的开水中放入少许盐，将油菜花和小圆豆置于笊篱中，放到热水中煮一下，然后将其取出，放入冷水中。 \r\n2.将腊肉切成2厘米宽的形状，放入炒锅中用大火翻炒。\r\n3.煮通心粉：将水煮沸，加入少许盐，一边煮一边将通心粉拨开拌松，直至水分收干。 \r\n4.在煮通心粉的同时，将橄榄油加入中华锅中加热，用大火煸炒大蒜末和切成小段的红辣椒，再将腊肉、虾、油菜花（除去装饰用部分）和小圆豆依次放入锅中一起煸炒，然后倒入葡萄酒和酱油。 \r\n5.将通心粉和炒好的佐料倒在一起，放入盐', NULL, NULL, NULL, '通心粉（细面条）300克，虾12尾，油菜花1棵，小圆豆30节，腊肉（咸猪肉）100克，大蒜末少许，红辣椒1只，橄榄油3大匙，红葡萄酒3大匙，酱油1大匙半，盐、胡椒少许。');
INSERT INTO `recipe` VALUES (1327, 83, '麻婆锅', '1.在煎锅中倒入油，将牛肉与油豆腐块一起煸炒。 \r\n2.在色拉中加入白菜、椎茸、长葱一起炒。 \r\n3.在土锅中倒入2中炒好的料，另加水400cc和“麻婆豆腐素”2袋，煮4－5分钟。 \r\n4.将生粉放入30cc的水中溶解，倒入锅中，充分调匀即可。', NULL, NULL, NULL, '丸美屋“麻婆豆腐素”2袋、生粉1袋、牛肉200g、油豆腐块1枚、白菜1/4个（切成4-5cm宽）、长葱2本（斜切）、椎茸3个（切成5mm宽）。');
INSERT INTO `recipe` VALUES (1328, 83, '面豉烧龙虾及生豪伴海胆', '1.首先在龙虾及海胆肉上淋鱼汤三次。 \r\n2.然后把混有日本紫苏叶的海胆肉撒在龙虾内。\r\n3.而生豪则以日本清酒、柚子肉等调味；\r\n4.最后将龙虾及生豪烤5分钟便可。', NULL, NULL, NULL, '龙虾300克，海胆50克，姜20克，日本紫苏叶20克，鱼汤30毫升，生豪150克，面豉100克，柚子12克，清酒。');
INSERT INTO `recipe` VALUES (1329, 83, '漆匠萝卜', '1.熬红面豉制作：红面豉、白面豉、酒、砂糖、蛋黄放入锅中，隔水加热，熬至与原有的酱同样柔软程度。快完成时，放入木鱼干，轻轻地搅拌，从火上拿下冷却。过滤，使之更光滑。 \r\n2.漆匠萝卜制作：萝卜切成2厘米厚，削去皮，切成半月形，削掉棱角，再用水冲洗。放进淘米水，加火，煮制成用金属能穿透为止。放进水里仔细冲洗，去除淘米水的异味。将昆布出汁，腊肉、萝卜放进土锅，加火煮。从冷水起煮20－30分钟。 \r\n3.柚子葱的制作：把青葱切成葱花，用布巾包好，在水中轻轻揉洗。削掉柚子外皮，取内侧白色部分，切成细小碎块，用温水', NULL, NULL, NULL, '萝卜1根、腊肉150克、昆布出汁1升、青葱1/2棵、柚子1/2个、红面豉100克、白面豉50克、酒150毫升、砂糖50克、蛋黄1个、木鱼干1个、淘米水。');
INSERT INTO `recipe` VALUES (1330, 83, '巧克力铜锣烧饼', '1.蛋3个打匀，加入糖80g一起打匀。\r\n2.加入各式口味的糖浆3大匙一起打匀。\r\n3.中筋面粉或低筋面粉120g，BP1大匙，BS1小匙分別过筛放入拌匀。\r\n4.可可粉70g过筛放入拌匀。\r\n5.用不沾锅煎会比较好。\r\n6.煎好的饼夹馅即可。', NULL, NULL, NULL, '蛋3个，糖80g，糖浆3大匙，中筋面粉或低筋面粉120g，BP1大匙，BS1小匙，可可粉70g，番薯泥，马铃薯泥加2:1或不先加奶油的芋泥馅。');
INSERT INTO `recipe` VALUES (1331, 83, '日本红豆饭', '1.红豆洗净，加入4杯水煮开后，调成中火煮10分钟后熄火待凉。 \r\n2.长糯米洗净沥干后与作法1的红豆连汤（已经凉的）一起混合泡1小时左右，再用饭锅焖熟。 \r\n3.但如果没有时间的话不用泡1小时也可以（泡1小时色泽会比较漂亮）。 \r\n4.食用时将少许盐和黑芝麻撒在饭上。', NULL, NULL, NULL, '红豆1/2杯（90公克）、水4杯、长糯米3杯（500公克）、盐、炒香黑芝麻少许。');
INSERT INTO `recipe` VALUES (1332, 83, '日本紫菜卷寿司', '1.将紫菜平铺在展开的竹帘上，把米饭握在手中捏软，迅速将米饭均匀地平摊在紫菜上（米饭厚度大约0.5cm），压实，并空出上端2cm左右的紫菜。再将人造蟹肉、日本黄萝卜咸菜、芦笋、三文鱼条放置于米饭的中央。\r\n2.用竹帘裹住紫菜和米饭，卷紧，然后撤走竹帘。\r\n3.把卷好的寿司切成段装盘。', NULL, '蒸米饭之前要将米浸泡一二个小时，米饭要蒸得软硬适中。要等到米饭温度略低于手温时再铺在紫菜上。铺米饭的过程，尽量快速完成，不然紫菜会因吸收过多水分而变软，无法成型。包裹的时候，米饭不要外露，松紧要适中。制作寿司的过程中可以在身边摆放一盆水，随时用清水蘸蘸就不会粘手了。', NULL, '糖醋米饭：米最好用日本产的或与日本合资的，如东北五常米（松粳二号），如果买不到，用天津小站米代替也可。糖醋是用日本醋加糖熬至粘稠而成的，口味可以依据个人爱好而定，如买不到日本醋可用白醋代替。把熬好的糖醋汁均匀拌在米饭中，做成糖醋米饭。紫菜：最好用专门制作寿司的紫菜。人造蟹肉、日本黄萝卜咸菜、芦笋、三文鱼切成长条状。调味品：青芥末、日本酱油。');
INSERT INTO `recipe` VALUES (1333, 83, '日式冰品', '热红茶，加上果汁牛奶和冰块即可。', NULL, NULL, NULL, '热红茶，果汁牛奶，冰块。');
INSERT INTO `recipe` VALUES (1334, 83, '日式豆沙馒头', '1.先将低筋面粉加苏打粉过筛加糖粉。\r\n2.将1加蛋混匀加炼乳充分混匀成一面团，擀成一份一份的面皮。 \r\n3.将面皮包入白豆沙馅，焗约15－20分钟，上火180度、下火150度。', NULL, NULL, NULL, '炼乳95克、糖粉45克、蛋1个、苏打粉2.5克、低筋面粉188克。');
INSERT INTO `recipe` VALUES (1335, 83, '日式海鲜炒面', '1.把鱼肉、青椒、洋葱、京葱、目鱼切成细条，蘑菇切片，大蒜切末。用热水把面条煮10分钟，控去水分。\r\n2.在炒锅中加入色拉油、鱼肉、虾仁、目鱼炒熟后，加入青椒、洋葱、京葱、蘑菇、大蒜，炒熟散发香味后，加入面条炒匀后，浇入酱油、盐，炒匀即可食用。', NULL, NULL, NULL, '鱼肉80克、青椒30克、洋葱20克、葱10克、蘑菇10克、大蒜1只、虾仁30克、目鱼30克、色拉油少许、酱油少许、盐少许、意大利干面条120克。');
INSERT INTO `recipe` VALUES (1336, 83, '日式冷素面', '1.虾只先用水灼熟，去壳、去肠及开边。另将鸡蛋发匀，用平底镬以慢火煎，之后切丝备用。\r\n2.冬菇加入热水浸软，然后加入少许日本酱油、味醂及砂糖烩2小时后切丝。\r\n3.将水煲沸，加入素面1包，煮约1－2分钟，期间边煮边搅，以免面条互相黏住。\r\n4.然后加入冷水浸约1分钟过冷水，捞起；木鱼精加入开水后，将味醂及淡口酱油搅匀成素汁，淋在冷面上，将配料虾、蛋丝、冬菇丝等铺于素面上即成。', NULL, NULL, NULL, '素面1包、虾2只、鸡蛋1只、冬菇2只、木鱼精2粒、味G1/2茶匙、淡口酱油1/2茶匙、水1碗。');
INSERT INTO `recipe` VALUES (1337, 83, '日式凉面', '1.先将水，酱油，沙糖，味素少许（面汁）煮滚放凉，冰在冰箱备用。 \r\n2.清水滚后下面，面再滚后关小火，约煮10－12分钟左右。面煮好时冲冰水冷却为止。将山葵酱少许、海苔丝放进冰箱，吃时再拿出来。\r\n3.吃时面汁以个人口味放适量山葵酱，海苔丝。\r\n4.面粘上调好的凉面汁即可食用。', NULL, NULL, NULL, '日本进口绿藻面1包，山葵酱少许，海苔丝少许，水5杯，酱油1杯，沙糖少许，味素少许。');
INSERT INTO `recipe` VALUES (1338, 83, '日式馒头', '1.先将低筋面粉加苏打粉过筛加糖粉。\r\n2.将水加红豆沙加洋菜煮开。\r\n3.再加蛋混匀加炼乳充分混匀成一面团，擀成一份一份的面皮。\r\n4.将白豆沙及栗子混合均匀。\r\n5.将面皮包入馅20克，上面刷上蛋水焗约15－20分钟，上火180度、下火150度。', NULL, NULL, NULL, '炼乳90公克、糖粉45公克、蛋1个、苏打粉2.2公克、低筋面粉188公克、白豆沙250克、栗子250克。');
INSERT INTO `recipe` VALUES (1339, 83, '日式乌冬面', '1.肉片用薄盐酱油稍腌。\r\n2.肉片放入高汤內煮滚，放入乌冬面，放入鱼板、竹轮煮熟。\r\n3.打入蛋（保持完整）。\r\n4.加入盐，放入葱段即可。', NULL, NULL, NULL, '乌东面1包、日式高汤、竹轮2块、鱼板2片、蛋1颗、肉片蛋1颗、肉片1片、葱段适量、薄盐酱油少许、盐少许。');
INSERT INTO `recipe` VALUES (1340, 83, '日式蟹柳扒鲜草菇', '1.撕碎蟹柳切段，鲜草菇冲净，韭黄切段，蛋白泉水搅匀。\r\n2.烧滚半镬水，将草菇飞水1分钟，取出过冷水，沥干水。\r\n3.烧2汤匙油，爆香姜片，加草菇兜炒，倒下调味料炒至干，取出盛碟。\r\n4.烧热1汤匙油，略炒韭黄，加芡汁煮滚，加入蟹柳丝，拌入蛋白煮熟，淋于草菇上。', NULL, NULL, NULL, '日式蟹柳6条，鲜草菇12两（480克），韭黄6条，姜4片，蛋白1只量，水4汤匙，糖、盐、麻油各1/2茶匙，生粉1茶匙，清鸡汤1杯，水1/2杯，盐1/4茶匙，糖1/2茶匙，麻油及胡椒粉少许，生粉1汤匙。');
INSERT INTO `recipe` VALUES (1341, 83, '日式羊羹', '1.将水加红豆沙加洋菜煮开。\r\n2.再将另500公克水加白豆沙、洋菜煮开。\r\n3.将1、2分别倒入模型，待凉倒出即可。', NULL, NULL, NULL, '（红豆）500公克水、5公克洋菜、（抹茶）500公克水、5公克洋菜、（红豆沙）红豆沙300公克、红豆粒、（白豆沙）300公克、抹茶粉10公克。');
INSERT INTO `recipe` VALUES (1342, 83, '日式炸豆腐', '1.先把酱油、清水、味淋、姜汁、沙糖煮滚备用（豆腐汁）。\r\n2.火锅豆腐（1块）切成8小块沾上低筋面粉。\r\n3.油热至140度左右，把沾好面粉的豆腐下油锅炸至金黄色起锅，放在皿中。\r\n4.豆腐上放少许萝萄泥，淋上调好的豆腐汁，加少许海苔丝即可食用。', NULL, NULL, NULL, '板豆腐或中华火锅豆腐、酱油1杯、日本味琳半杯、清水4杯、白细砂糖半杯、生姜（磨成汁）少许、白萝萄（用果汁机打成泥）、海苔丝少许、低筋面粉少许。');
INSERT INTO `recipe` VALUES (1343, 83, '烧竹简及鸭片伴寿司', '1.首先将竹简切半，挖出简肉待用。\r\n2.鸭肉以鱼汤调味，并微烤一会儿。\r\n3.将简肉与鸭肉切成半寸厚的片，然后在挖空的竹简内。\r\n4.而鳗鱼肉，蘑菇和寿司饭团则置于另一半竹简内。\r\n5.最后把整个竹简烧5分钟便可。', NULL, NULL, NULL, '竹简300克，鸭肉40克，烧鳗鱼30克，蘑菇15克，鱼汤30毫升，寿司50克。');
INSERT INTO `recipe` VALUES (1344, 83, '生火腿干酪卷色拉', '1.将萝卜去皮，切成半圆形薄片，加盐腌一腌，绞干水分。将洗干净的新鲜生菜和水芹切成便于食用的小段，同萝卜混合，置于冰箱内。\r\n2.将干酪切成厚厚的四块，再纵向一切为二，用生火腿裹成卷，放入冰箱。\r\n3.再将生火腿纵向一切为二，与1中所制蔬菜混合，淋上调味汁即可。', NULL, NULL, NULL, '萝卜一段（10厘米），盐少许，新鲜生菜2－3棵，水芹1棵，乳脂干酪100克，生火腿薄片100克，万能调味汁5大匙。');
INSERT INTO `recipe` VALUES (1345, 83, '生鱼四喜饭', '1.将鱼洗净去头尾切小片，抹上芥末备用。\r\n2.将四喜饭分成10小团，手沾过盐水。\r\n3.再将每1小团的1/2放入盛四喜饭的木盒内摊平，放上生鱼片。\r\n4.用木盒盖压一下，取出每个切为四块，即可装盘。', NULL, NULL, NULL, '新鲜活鱼一条（约750克）、四喜饭2000克、日本芥末30克（如没有芥末可用鲜姜代替）。');
INSERT INTO `recipe` VALUES (1346, 83, '什锦寿司卷', '1.葫芦条酱油1勺（珍极酱油）、适量的糖、水4勺，开锅后小火煮20分钟即可。\r\n2.香菇酱油1勺（珍极酱油）、适量的糖、2勺泡香菇的水，开锅后小火煮20分钟即可。\r\n3.将各种配料切成细条状备用。\r\n4.将寿司饭均匀铺在紫菜上（注意紫菜上下两边留出约一指宽的紫菜边），然后将各式备料放在饭的中间。\r\n5.用帘子将其卷成圆形，切片即可。', NULL, NULL, NULL, '寿司饭、黄瓜、紫菜、黄咸菜、豆腐皮、鳗鱼藉片、虾、蟹柳、芝麻、梅肉、鸡蛋、菠菜、香茹、葫芦条等（也可根据个人爱好有所调整）。');
INSERT INTO `recipe` VALUES (1347, 83, '手握寿司', '1.将新鲜的虾在水里焯一下，然后去壳剖开备用。\r\n2.将扇贝劈为花刀备用。\r\n3.将寿司饭用手握成饭团，放在苏子叶上面。\r\n4.将备好的扇贝放在饭团上即可。', NULL, NULL, NULL, '寿司饭、虾、扇贝、赤贝、北极贝、苏子叶。');
INSERT INTO `recipe` VALUES (1348, 83, '寿司饭', '1.寿司米2杯洗净，加水一杯半，先浸泡20分钟再煮成饭。\r\n2.趁热拌入寿司醋半杯、糖一大匙，弄散放凉。\r\n3.份量要多可按比例增加，內馅随自己喜好。卷长形、三角形皆可。', NULL, NULL, NULL, '寿司米2杯，水1/2 杯，寿司醋1/2杯，糖，馅料。');
INSERT INTO `recipe` VALUES (1349, 83, '蔬果寿司', '1.黄秋葵烫过漂凉。\r\n2.寿司醋先煮开待凉后，加入热的白饭中拌匀再捏成小饭团，抹少许芥末，再铺上各类蔬果材料，容易滑动的就用海苔卷起来固定。', NULL, '鱼虾类胆固醇太高，变换一下材料，健康又多样化。水果可依自己喜欢的口味来挑选。', NULL, '白饭适量、雪里红叶子2片、卤香菇4朵（大的2朵）、白萝卜片2片、黄秋葵2支、水果适量、海苔1/2张、醋姜少许、寿司醋、米醋1/3杯、糖2大匙、盐1大匙、酱油、绿芥末各适量。');
INSERT INTO `recipe` VALUES (1350, 83, '水滴寿司', '1.米饭趁热拌上白糖、白醋，放凉备用。\r\n2.蛋打散，加少许盐调味，入平底锅中煎成蛋皮后，切成长方型，再切作长条。\r\n3.菠菜放进加盐的热水中烫熟，漂凉后挤干水分。\r\n4.取竹帘垫底，上铺紫菜，米饭铺至3/4处，中间放进所有内馅，并包卷起来，卷到前端时，倾斜下压，缝口用米饭沾粘，成水滴状。　\r\n5.以利刀将水滴寿司切薄片，排列于盘中。', NULL, NULL, NULL, '米饭4碗，紫菜4张，肉松1/2碗，菠菜1/2斤，蛋3个，胡萝卜4长条，竹帘1张，白糖，白醋各3大匙，盐少许。');
INSERT INTO `recipe` VALUES (1351, 83, '糖饴烧', '1.将砂糖及水放入勺子后放在火上（瓦斯炉）焗，并用卫生筷搅拌均匀，使其沸腾。\r\n2.等砂糖完全溶化，放入苏打粉。\r\n3.待起泡膨胀固定，再在火上烘一下子（让它溶化得比较好拿起来）。', NULL, NULL, NULL, '2大匙砂糖、苏打粉2小匙、2小匙水。');
INSERT INTO `recipe` VALUES (1352, 83, '天妇罗大虾面', '1.将天妇罗粉和水以1：1.5的比例打糊，也可用白面、鸡蛋和水打糊。\r\n2.把大虾、芦笋、洋葱片、香菇放在天妇罗粉糊中沾裹，再依次放入滚开的油锅中煎炸，至金黄色即可捞出。\r\n3.把面条煮熟，拌上日本酱油，再把炸好的大虾盖在面条上。', NULL, NULL, NULL, '天妇罗粉(也可用少许白面加一个鸡蛋、一碗水代替)、煮熟的大虾、芦笋、洋葱片、香菇、面条一碗。');
INSERT INTO `recipe` VALUES (1353, 83, '铜锣烧', '1.蛋加糖打至八分发（不用太发，差不多就好了）。\r\n2.加入低粉、小苏打、泡打粉、果糖、奶水拌匀（一次加一样东西，一点点加）。\r\n3.舀一匙在平底锅成圆饼状煎熟（锅要预热，倒油）。\r\n4.冷却后加入红豆沙。', NULL, NULL, NULL, '蛋两个，糖九十克，低筋粉一百四十公克，小苏打两公克，泡打粉两公克，果糖十五公克（一定要用果糖），奶水三十五公克。');
INSERT INTO `recipe` VALUES (1354, 83, '土豆可乐饼', '1.取三个大土豆，连皮放在水里煮40分钟左右，用竹签戳戳看，如果能戳透，就取出放在手巾上，趁热剥去皮。\r\n2.趁热将土豆捣碎。要注意的是，土豆冷却后就会起粘，难以捣碎。\r\n3.取洋葱一个，切碎。在平底锅里加入一大匙食油，将切好的洋葱放入炒。炒透后，加入牛肉末200克和一片月桂树叶一起炒。用盐、胡椒调味，加入土豆并搀混。然后，加入一大匙鲜奶，掺匀并使之冷却。\r\n4.等其凉下来以后，在手掌上抹上食油，取适量调理好的土豆泥放在左手上，用右手搓成整洁的圆柱形。\r\n5.在两个瓷盘上分别撒上适量的小麦粉和面包粉，同时', NULL, NULL, NULL, '大土豆三个，洋葱一个，牛肉末200克，月桂树叶一片，鸡蛋一只，鲜奶一汤匙，小麦粉、面粉、食用油、盐、胡椒适量。');
INSERT INTO `recipe` VALUES (1355, 83, '土豆奶汁烤菜', '1.将新土豆去皮切成薄片，包上保鲜膜，放入微波炉加热6分钟。\r\n2.将白色酱汁倒入锅中，用牛奶稀释溶解并煮至温热，洒入盐和胡椒。\r\n3.在耐热容器内壁涂上黄油，顺次铺上新土豆、橄榄油沙丁鱼、番茄以及2中制成的沙司，重复叠放两层后，浇上奶酪，在220度的烤炉中烤20分钟。\r\n4.烤至表面呈金黄时即可。', NULL, NULL, NULL, '新土豆4个，番茄2个，白色酱汁一罐（290克），牛奶1杯半，橄榄油沙丁鱼2罐（120克），溶解干酪120克，盐、胡椒、黄油各少许。');
INSERT INTO `recipe` VALUES (1356, 83, '土豆蟹鱼糕', '1.将土豆去皮切成1cm宽的丝，浸在清水中。\r\n2.在热水中放入少许盐，将1中切好的土豆丝放入，一起置于锅内煮至水分收干。\r\n3.蟹鱼糕纵向切成细丝，万能葱切成4cm长的小段。\r\n4.将土豆丝和蟹鱼糕盛于碗内，依次撒上盐、香油、胡椒粉和辣椒粉即可。', NULL, NULL, NULL, '新土豆（难以煮烂的品种为佳）2－3个（250克），蟹鱼糕3块，万能葱4根，盐1/4小匙，香油1/2大匙，胡椒粉，辣椒粉少许。');
INSERT INTO `recipe` VALUES (1357, 83, '味噌海鲜汤', '1.鱼切小块，淋上酒去腥味，蛤蜊泡盐水。\r\n2.花枝去皮切小块，用开水烫过。\r\n3.虾剪去长须，用开水烫过。\r\n4.豆腐切长方块。\r\n5.将水与味噌同时煮热，放入鱼、虾、花枝煮滚。\r\n6.放入蛤蜊、豆腐， 用柴鱼粉拌一拌， 蛤蜊打开就好了。\r\n7.放入葱花即可。', NULL, NULL, NULL, '鱼1条、虾10个、蛤蜊1碗、豆腐1块、花枝1只、葱花适量、姜2－3片、酒1－2大匙、味噌2大匙、水6－7碗、柴鱼粉1包。');
INSERT INTO `recipe` VALUES (1358, 65, '菠菜鱼肚', '1. 墨鱼经初步加工后，剖开为两片，头相连，两边各留尾巴一半，剔去脊骨，在鱼身的两面用直刀下，平刀进的刀法（为肉的三分之二的深度）。剖六至七刀，然后用精盐、绍酒抹遍墨鱼的全身。将葱白先切成7厘米长的段，再切成丝，漂入清水中，香油豆瓣切细。\r\n2. 炒锅烧热，下油烧至八成热时，将鱼全身沾满干淀粉，提起鱼尾，用炒勺舀油淋于刀口处，待刀口翻起定形后，将鱼腹贴锅放入油里，炸至呈金黄色时，捞出装盆。炒锅留油50克，加猪油50克，下葱、姜、香油、豆瓣炒熟后，下肉汤、白糖、酱油，用湿淀粉勾成薄芡，撒上葱花，然后烹入醋，', '色泽红亮，皮酥肉嫩，甜酸中略带香辣。', NULL, NULL, '鲜墨鱼1条（约750克左右）、麻油50克、豆瓣50克、葱花15克、葱白1根、姜末和蒜末各10克、醋40克、绍酒15克、干淀粉7克、湿淀粉50克、精盐1.5克、酱油25克、熟猪油50克、熟菜油1500克（实耗150克）。');
INSERT INTO `recipe` VALUES (1359, 83, '味噌烤肉酱', '1.将味噌，味淋，糖和水仔细调均。\r\n2.再加入蒜末，淋上葱花和香菜末即可。', NULL, '如果还觉得不够咸的话可以加入适量的酱油。如果买不到味淋，可以用少许白醋代替。', NULL, '味噌（须挑选盐份较低的）1大匙、味淋（即日本酒醋）1小匙、糖1大匙、水1大匙、蒜末1小匙、香菜末1大匙、葱花1大匙。');
INSERT INTO `recipe` VALUES (1360, 83, '味噌汤', '1.鲷鱼（或其它新鲜鱼骨）切块，入开水川烫捞出，再用清水洗净。\r\n2.锅内入水1/3杯烧开，将红、白萝卜丝煮软，续入鱼骨煮滚，去除泡沬，将味噌、 糖、味精置小漏勺内，以木棍(或汤匙)拌匀，立即熄火盛碗，并撒上葱花即成。若将鱼骨改用豆腐、海带芽，水则改用鱼干汁（小鱼干300公克去头及内脏，加水5杯以小火煮20分钟即成）较理想。', NULL, NULL, NULL, '鲷骨（赤宗鱼骨）300公克，红、白萝卜丝各1/2，味噌80公克，糖、味精各1/8小匙，葱花2大匙。');
INSERT INTO `recipe` VALUES (1361, 83, '味噌海带芽汤', '1.海带芽洗净泡水，切段。\r\n2.水和姜丝煮开，放入海带芽煮熟。\r\n3.味噌加少许水调匀倒入2，加入糖、味精拌均，撒入芹菜末即可。', NULL, NULL, NULL, '味噌适量、糖少许、味精3两、姜丝适量、海带芽3两、芹菜末少许。');
INSERT INTO `recipe` VALUES (1362, 83, '五彩炒饭', '1.鸡肝切成丁，用酱油腌一下待用。\r\n2.把所有蔬菜均切成末或细丁待用。\r\n3.在锅中加入色拉油加热，煸炒胡萝卜和葱，加入盐；将饭倒入其中煸炒，至饭粒松散开时，依次加入小松菜和鸡肝翻炒。\r\n4.与此同时，将调匀的鸡蛋沿锅壁缓缓浇下，撒上盐和胡椒调味即可。', NULL, NULL, NULL, '煮熟的鸡肝80－150克，酱油2小匙，胡萝卜小半根，长葱1/4 根，小松菜3－4棵，色拉油2大匙，米饭（温热）2茶碗，鸡蛋1个（调匀），盐、胡椒各少许。');
INSERT INTO `recipe` VALUES (1363, 83, '洋葱金枪鱼炒蛋', '1.将洋葱去皮，纵向切成1.5厘米宽的梳子形。将金枪鱼罐头中的鱼和橄榄油汁分开。豆苗去根冲洗。 \r\n2.在锅中加入橄榄油汁1大匙加热，鸡蛋敲碎后倒入锅中迅速翻炒，然后盛起。 \r\n3.将锅冲洗一下置于火上，加1大匙橄榄油汁，放入1中切好的洋葱煸炒。 \r\n4.在3中再加入金枪鱼翻炒，均匀洒上牡蛎沙司、酱油和豆瓣酱，再将2中炒好的鸡蛋和1中洗好的豆苗放入锅中，一齐煸炒，至熟即可。', NULL, NULL, NULL, '新洋葱2个，橄榄油金枪鱼1大罐，鸡蛋3个，豆苗少许，牡蛎沙司半大匙，酱油半大匙，豆瓣酱1小匙。');
INSERT INTO `recipe` VALUES (1364, 83, '鱿鱼拌菜', '1.鱿鱼、泡菜、绿紫苏叶均切成丝。\r\n2.将鱿鱼丝和泡菜丝混合拌匀，盖上紫苏丝，淋上香油即可。', NULL, NULL, NULL, '鱿鱼肉段150克，泡菜70克，绿紫苏叶4片，香油半小匙。');
INSERT INTO `recipe` VALUES (1365, 83, '炸牛排', '1.用菜刀将厚牛排从中间剖开（不剖断），留出空间，敷入盐和胡椒，再涂上芥末，将之闭合。在牛排上依次涂上小麦粉、鸡蛋（已加水调开）、剁碎的百里香、面包粉，做成外衣，放入冰箱待用。\r\n2.做番茄沙司：番茄用热水烫过后去蒂取子，切成小块去汁。用橄榄油以中火翻炒，放入意大利香料，煮2-3分钟，然后放入盐、胡椒调味。\r\n3.将牛排从冰箱内取出，放入180度的热油中煎炸。\r\n4.将熟牛排放至盘中，旁边铺上番茄沙司，再用意大利香料修边装饰即可。', NULL, NULL, NULL, '牛肉（作牛排用）4块（约480克），盐、胡椒各少许，芥末2大匙，小麦粉2－3大匙，鸡蛋1个，面包粉1杯半，百里香、油各少许，番茄沙司（熟透的大番茄2个），橄榄油2大匙，意大利香料少许。');
INSERT INTO `recipe` VALUES (1366, 83, '紫菜卷寿司', '1.蒸米饭之前要将米浸泡一二个小时，米饭要蒸得软硬适中。\r\n2.要等到米饭温度略低于手温时再铺在紫菜上。\r\n3.铺米饭的过程，尽量快速完成，不然紫菜会因吸收过多水分而变软，无法成型。\r\n4.包裹的时候，米饭不要外露，松紧要适中。\r\n5.制作寿司的过程中可以在身边摆放一盆水，随时用清水蘸蘸就不会粘手了。\r\n6.将紫菜平铺在展开的竹帘上，把米饭握在手中捏软，迅速将米饭均匀地平摊在紫菜上（米饭厚度大约0.5cm），压瓷实，并空出上端2cm左右的紫菜。再将人造蟹肉、日本黄萝咸菜、芦笋、三文鱼条放置于米饭的中央。\r', NULL, NULL, NULL, '糖醋米饭：糖醋是用醋加糖熬至粘稠而成的，口味可以依据个人爱好而定，把熬好的糖醋汁均匀拌在米饭中，做成糖醋米饭。紫菜：最好用专门制作寿司的紫菜。人造蟹肉、日本黄萝卜咸菜、芦笋、三文鱼，切成长条状、青芥末、日本酱油。');
INSERT INTO `recipe` VALUES (1367, 80, '遍地锦装鳖', '1.将团鱼宰杀后，放净血，放入沸水中。水开后即出锅，刮掉外层黑衣，在鱼肚上开一个十字刀口，取出内脏，用清水漂洗干净。\r\n2.将洗净的团鱼放入沸水锅中再煮，煮至五成熟，捞出取掉鱼盖，其余剁成3.3厘米见方的大块，每块连有裙边。\r\n3.将炒锅放在火上，加熟猪油，中火煸炒大蒜、葱段、姜片。待香味略出，将团鱼带盖与水口蘑、水玉兰片放入炒锅，投酱油、精盐、绍酒、冰糖、八角、胡椒粉、鸡清汤，煸炒数分钟后，移到小火焖烧，待原汁收浓，团鱼成熟后，取出团鱼盖备用，再入味精，即可出锅，出锅后，放在大条盘内，拣出葱、姜、八角。', '本品是西安市近年来烹制的“仿唐菜”之一。西安曾是唐代帝都，膳食业非常发达，烹调技术达到了相当水平，据唐人韦巨源《烧尾食单》约略统计，便有58种之多，真可谓“玉液珍馐，水陆杂陈”。现今的西安名菜仍可找到唐菜的历史渊源。这款仿唐菜，以团鱼为原料，配以羊肉油和咸鸭蛋黄，采用唐代常用的烧蒸方法烹制。不但没有腥膻气味，而且鲜香四溢，基本上保持了唐代的固有风味，使食者领略到古人以“鱼”、“羊”两字构成的“鲜”的真正含义。', '宰杀团鱼时，将团鱼腹部朝上，盖朝下，待它将头伸出时，迅速用刀斩下，再将其头腔朝下，控净血，放入八成开的水中烫几分钟（时间长短视团鱼老嫩程度而定），再放入温水中用小刀刮去团鱼裙边和腿部的黑膜（下刀要轻，不可划破裙边），并联刮去腹部的白膜，再用小刀沿着锯齿形的盖将团鱼切开，撬开盖，取出内脏，剁去爪尖，用凉水冲洗干净即可。蒸团鱼的时间不能过长，15分钟即可，以免鱼肉老而发柴，口感不佳。', NULL, '团鱼1只约1000克，熟火腿25克，酱油75克，水口磨100克，绍酒50克，熟咸鸭蛋黄3个，芝麻油25克，姜片25克，味精1克，水玉兰片25克，湿淀粉15克，八角2个，葱段25克，鸡清汤500克，菜心25克，胡椒粉15克，熟猪油25克，羊网油250克，精盐7.5克，大蒜200克，冰糖25克。');
INSERT INTO `recipe` VALUES (1368, 80, '贡米酿藕', '将藕洗净去皮，一头切开。把泡洗好的黑米薄在藕孔里，上笼蒸约40分钟取出，晾凉后切成斜刀厚片，摆在盘里，撒上白糖即成。', '汉中洋县盛产黑米，相传为西汉张骞所培育，其色黑柴，其味芳香。《本草纲目》说黑米有“滋阴初肾，健身暖肝，明目活血”之效，并主“走马喉痹，调中气，主骨节风瘫痪不遂，常年白发”，因珍稀名贵自西汉以来，一直是历代皇室的贡品。故称“贡米”。“贡米酿藕”色泽悦目，滋补健身，是陕西汉中地区甜菜名品。', '黑米洗净，用清水泡12小时，沥干水分，薄在藕孔里，越实越好。大火气路，蒸半小时以上，软糯为上品。', NULL, '黑米100克，白糖30克，鲜藕1000克。');
INSERT INTO `recipe` VALUES (1369, 80, '红焖鳝鱼', '1、将鳝鱼宰杀洗净后，切成5厘米长的段。葱打成结，生姜拍松，三种大料用纱布包好，葱拍成末。五花肉在开水锅中泖授捞出，切成5厘米长、3.5厘米宽、6.5毫米厚的片，水发兰片切成片。\r\n2、锅中加油少许，放入白糖用小火炒成糖色，加水50克化开，放入五花肉煸炒上色，加水1000克，加酱油、大料、葱结、生姜、盐、料酒，用小火烧熟。\r\n3、锅中加猪油500克烧热时，将鳝鱼炸熟捞出，放在五花肉中焖酥烂，拣去葱、姜、大料，用湿淀粉勾芡，盛在大盘中。\r\n4、锅中加香油，蒜末炸出香味，浇在菜上面即成。', '鳝鱼，古代叫“鱼旦”、“鱼单”，俗名有黄鳝、善鱼、长鱼等。甘肃陇南有少量生产。据分析，鳝鱼肉100克中含蛋白质18.8克，脂肪0.9克，钙38毫克，磷150毫克，铁16毫克，VB10.02毫克，VB20.05毫克，尼克酸31毫克，营养丰富，肉质细嫩，味道鲜美，刺少肉厚。中医认为，其味甘性湿，有补虚损，祛风湿，强筋骨，壮肾阳之功，民间有“小暑黄鳝赛人参”之说。', '鳝鱼初加工一般有三法：一为活杀，即先将鳝鱼用力掼晕，然后用刀剖腹，去内脏，剔其脊骨，二为先用白酒一勺倒入装鳝鱼的容器中，迅速盖上，约数分钟，待其醉后剔骨取肉，三为烫熟剔骨，较为常见的是将鳝鱼倒入沸水锅中，加盖浸焐，待鳝鱼卷缩口张，将其置清水中，然后用刀划去鳝骨，取肉供用。出骨后的生鳝肉适宜爆、熘，如名菜“火攻马鞍桥”。熟鳝肉适合炒、炮、炸等。如名菜 “清炒鳝糊”。一般应以烧、炖、爆炒等法为佳。', NULL, '鳝鱼750克，蒜30克，猪五花肉500克，葱30克，水发兰片50克，香油40克，酱油50克，料酒40克，白糖4克，盐3克，生姜3克，湿淀粉40克，山奈3片，食油500克，大香3个，桂皮1片。');
INSERT INTO `recipe` VALUES (1370, 80, '红烧猴头', '1.猴头用水发透，去老根，拣去杂质并剪掉针刺毛尖后洗净，沥干水分，切成0.3厘米厚的片，放入碗中，加鲜汤100克、酱油、绍酒、白糖、精盐、葱段10克、姜片3克、花椒。\r\n2.鸡肉、猪肉切片用开水焯一下放入猴头碗中，将碗置笼中蒸半小时出笼，从碗中取出鸡、猪肉另作别用，拣出葱、姜、花椒等佐品不要。\r\n3.将香菇、冬笋切片；姜2克、葱10克切末状。炒锅置中蘑菇、冬笋片，炒几下后，把蒸好的猴头连同原汤汁推入，再加鲜汤300克、味精等调味品，调好口味，稍焖一下，再开锅撇净浮沫，用温淀粉色芡，淋入熟猪油及芝麻油搅拌即', '猴头即猴头菌，又名刺猬菌、对脸蘑，因形似猴头而得名。猴头与海参、燕窝、熊掌齐名，并称中国四大名菜。中医认为，猴头味甘性平，有健脾和胃，强壮抗衰，补益五脏的功效。现代医学认为，猴头菌水煎提取物制成的浸膏片，对消化道恶性肿瘤及胃、十二指肠溃疡、慢性胃炎有较好的疗效。', '猴头发制：干猴头用温水洗净，放开水中浸泡，渗透后入另一开水盆中，加盖焖涨，焖软后捞出削去根及外皮，顺毛片切开，根据要求切片，装碗中，添头汤，加葱、姜，旺火气足，蒸15分钟，取出即可使用。', NULL, '水发海参400克，水发冬菇25克，生鸡脯肉100克，鸡蛋清5个，鱼肉50克，小白菜心12棵，猪肥膘肉25克，葱结15克，姜片15克，干淀粉10克，精盐5克，湿淀粉10克，味精1克，鸡汤250克，绍酒20克，熟鸡油25克，胡椒粉0.5克，熟猪油25克。');
INSERT INTO `recipe` VALUES (1371, 80, '葫芦鸡', '1.将鸡粗加工后洗净，放水中漂30分钟，除净血污，剁去脚爪，然后投入沸水锅中煮约30分钟。取出后，割断腿骨上的筋，放入蒸盆，注入肉汤，以淹没鸡身为度，加入绍酒、酱油、精盐，将葱、姜、桂皮、八角放鸡肉上，入笼用旺火蒸约2小时取出，捡去葱、姜、桂皮和八角，沥干水，顺脊椎骨将鸡剖开。\r\n2.炒锅放入菜籽油，用旺火烧至八成热。将鸡背向下推入锅内，用手勺拨动，炸至金黄色时，捞入漏勺内沥油。将鸡的胸部向上，用手掬拢，呈葫芦形，放入扒盘。上桌时另带花椒盐小碟。', '据《酉阳杂俎》记载：葫芦鸡创始于唐礼部尚书韦陟的家厨。韦陟穷奢极欲，对膳食极为讲究，命家厨烹制酥嫩的鸡肉。第一个厨师采用先煮后油炸，韦陟尝后，嫌肉质太老，命家丁将这个厨师鞭打五十致死；第二个厨师采用先煮后蒸，再油炸的方法，鸡虽酥嫩，但鸡肉脱骨成碎块，韦陟嫌不成形，便将这个厨师活活打死；第三个厨师吸取前二者的经验教训，把鸡捆扎起来，而后烹制形如葫芦，香醇酥嫩，颇得韦陟赏识。后来人们就把这样烹制的鸡称为“葫芦鸡”，一直流传至今。“葫芦鸡”历史悠久，已流传千年以上，号称长安第一味。色泽金黄，形似葫芦，皮酥肉嫩，', '炸鸡前，应沥干水分，防止入油爆炸。炸鸡时，开始用热油炸，以保持主料的完整形态。中途改用温油炸，以使油逐渐渗入主料内，炸成外焦里嫩。', NULL, '肥母鸡1只，酱油100克，葱段15克，绍酒25克，姜块10克，精盐10克，桂皮7.5克，花椒盐25克，八角4个，肉汤1500克，菜籽油1500克。');
INSERT INTO `recipe` VALUES (1372, 80, '黄焖狗肉', '1.用凉水将30-40天的小狗呛死，放入盆内，浇沸开水烫洗去毛，从肚月襄下一壁两半，除去内脏，入清水盆中漂洗干净。\r\n2.锅坐火上，加清水烧开，将狗拉展，入开水锅中焯过，抹上白酱油，叉到铁叉上。用木炭生火，视烟走后，将铁叉就火翻烤至金黄色待用。\r\n3.将锅坐火上，加熟猪油烧热，投入花椒，炸出香味，再将花椒粒捞出，然后加入各种调料、肉汤和烤好的狗肉，加锅盖改大火焖约30分钟，加淀粉翻搅两下，盛入大盘内，用力划开皮即可食用。', '狗肉不但味美，而且营养丰富，它具有含蛋白质多，脂肪少，胆固醇低的特点。祖国医学认为，狗肉性味咸甘酸、热，归胃、肾二经，具有温补强壮之功。食用狗肉，据《食疗本草》载：“狗肉补五劳七伤、益阳事、补血脉、厚肠胃、实下焦、填精髓。”《日华子本草》注曰：“狗肉补胃气、壮阳道、暖腰膝、益气力。”隆冬时节，正吃狗肉。食后周身发热，驱寒壮体，大有补益。', '大火烧开，小火焖酥，约1小时左右，风味是一格。', NULL, '小狗1只，熟猪油100克，淀粉100克，肉汤750克，酱油25克，料酒100克，大葱50克，蒜瓣50克，辣椒25克，鲜姜25克，香油25克，花椒5克，食盐10克，味精3克，胡椒粉10克。');
INSERT INTO `recipe` VALUES (1373, 80, '烤小猪', '1.先将小乳猪宰杀，用85度开水烫洗去毛，剖腹除去内脏，撕下板油，然后剔除前肋骨三根，再把猪身放平，用水冲洗干净。接着用开水把猪皮烫破，揩干水分，乘热在全身涂上蜂蜜，蜂蜜抹均匀后，把猪的后小腿、爪及其筋皮，折装有猪软肋部，铁叉叉平，放在燃烧的木炭火上翻烤，使其均匀地受热。烤至猪皮起红黄色小泡时取出，用长针在猪身上均匀地刺很多小孔，使水分较快蒸发。视孔不出油时，用笤帚将油涂遍猪身，以防猪皮烤焦，而且能使猪皮更加光亮鲜艳，整个烤制过程约1小时左右。然后再将香油抹遍猪身再烤15分钟即可。\r\n2.将烤好的小猪放在', '此菜自周朝之后，一直流传至今，且遍及南北各地。北魏《齐民要术》中载有“炙豕屯豚法”：“用乳下，豕页 牛字 具得，治一如洗去。揩净、乔、削食极净。小开腹，去五脏，又洗净，以茅茹腹令满。榨木穿，缓火遥炙，急转勿住，转使周币，不币则偏火焦 也。清酒数涂，以发色，色足便止。取新猪膏极白净者，涂拭勿住。若无新猪膏，净麻油亦得。色同琥珀，又类真金，入口则肖，壮若凌雪，含浆膏润，特异凡常也。”清袁枚《随园食单》记载了当时杭州的“烧小猪”：“小猪一个，六七斤者，钳毛去秽，叉上炭火炙之。要四面齐到，以深黄色为度。皮上慢慢以', '小乳猪以成长30-40天者为佳，太大或者太小皆不可取。烤至猪皮见有黄色小泡时，必须用长针扎眼放气，使水分较快蒸发，再涂上植物油继续烘烤，可使猪皮酥脆且油亮。', NULL, '小乳猪1头，蜂蜜50克，嫩绿萝卜条250克，面酱250克，大葱250克，香油100克，椒盐面50克。');
INSERT INTO `recipe` VALUES (1374, 80, '烤羊腿', '1.将羊腿冲洗干净，用铁钎子均匀地遍戳小孔，置容器里，加精盐、花椒水腌4-6小时。\r\n2.烤盘内加桂皮、八角、草果、姜、番茄酱和羊汤，温度至180-200度左右，将烤盘推进烤箱内，每小时翻动一次，约3-4小时，汤汁烤干，羊腿烂熟，取出装盘，以香菜点缀即成。', '“烤羊腿”是蒙族名菜，流传广远，西北各地，皆有制作。此菜羊腿为主料，经腌制再加调料烘烤而成。成菜羊腿形整，颜色红润，酥烂醇香，滋味鲜美，回味悠长。', '每小时翻动一次，将烤盘里汤汁浇在羊肉上，反复3-4次，不但入味，且色油润。', NULL, '羊后腿1只，芹菜200克，番茄酱1000克，番茄250克，精盐30克，花椒水1000克，桂皮5克，八角10克，草果2个，姜20克，葱头500克，羊肉汤500克。');
INSERT INTO `recipe` VALUES (1375, 80, '玛瑙海参', '1.从猪肺喉管灌进清水，冲洗净血污，放汤锅内煮烂，去皮筋，剁成直径2厘米大的小圆球。\r\n2.将海参顺长片成磨刀片，用开水氽过捞出。\r\n3.锅内加猪油50克烧热，下入葱段、姜片、蒜片，炸出香味，加入料酒、鸡汤500克，捞去葱，倒进海参，加酱油、胡椒面、盐、味精、猪肺烧5分钟，淋鸡油，盛入汤盘内，撒火腿末即成。', '玛瑙为美玉的一种，红黄润泽，宝色生辉，历代用以雕刻器皿或装饰品。菜品以玛瑙为名，喻意高雅美观，稀有珍贵。“玛瑙海参”汁红质滑，味美醇厚，是甘肃传统名菜。', '水发海参：将干海参放开水盆内，涨发回软，放入冷水锅内，用小火烧开，盖严盖，放保温处，使水温保持在60-70度左右，把海参发透，捞到温水盆中，剖开腹部，清除肠肚及里皮，用净温水洗净，放入冷水锅中，用小火烧开，倒入盆内加盖，保温涨发。如此反复涨发，直到海参柔软光滑，手捏有韧性，即为发好，注意，泡海参的水不要被油脂污染。', NULL, '水发海参500克，盐14克，猪肺1个，鸡油40克，味精0.3克，熟火腿末50克，蒜10克，料酒30克，胡椒面0.3克，葱段30克，生姜3克，食油50克，酱油15克。');
INSERT INTO `recipe` VALUES (1376, 80, '三皮丝', '1.海蜇皮先用清水浸泡，洗去沙后，撕去血膜，用水洗净，切成块，用七成熟水烫一下，捞出，用冷水泡至脆嫩无腥味即成。\r\n2.将鸡皮、猪皮、鸡肉，分别放入锅中煮熟捞出。猪皮片成薄片，连同鸡皮、海蜇皮分别切成5厘米的细丝。选好带皮的酱肘花，将皮片成薄片，连同精瘦部分及鸡肉分别切成细丝，作装盘垫底菜。\r\n3.将葱切成丝，放入碗内，浇上热花椒油，再放入鸡肉丝、肘花丝加盐、酱油、醋搅拌均匀。放入盘中，摆成三角形，然后将鸡皮丝、海蜇皮丝、猪皮丝分别覆盖在肘花丝和鸡肉丝上面。\r\n4.将芝麻酱加盐，用香油搅拌融合，浇在三丝上', '“三皮丝”是由唐代韦巨源《烧尾宴》食单中的“羊皮花丝”演变而来的一道长安古菜。此菜皮脆肉嫩，清爽利口，风味独特。在《烧尾宴》食帐中，“羊皮花丝”条下只有“长及寸”三个字，没有记载烹制方法。相传此菜原称“剥豹皮”，是唐代京城长安厨师为发泄对王旭、李嵩、李铰的不满而创制的。王旭为中唐时左台侍御史，李嵩、李铰为临察御史。这三个贪脏枉法，作恶多端，长安人民百姓愤恨之极，时称王旭为“黑豹”，李嵩为“赤黧豹”，李铰为“白额豹”。长安有个姓吕的厨师，将乌鸡皮、海蜇皮、猪皮三种不同颜色的皮制菜佐酒，意为活剥“三豹”皮，以', '鸡皮、鸡肉煮的不要太老。要先将热花椒油浇在葱丝上面以后，再放鸡肉丝和肘花丝，目的是为了让其香味浓郁。', NULL, '鸡皮75克，猪皮100克，海蜇皮75克，鸡肉75克，酱肘花100克，葱15克，香油2.5克，花椒油25克，芝麻酱10克，精盐3克，酱油10克，醋15克。');
INSERT INTO `recipe` VALUES (1377, 80, '三套鸭', '1.用刀从光鸭宰口处将颈骨斩断，在颈与翅膀相连划一刀，划破鸭皮，抽出颈骨，用手翻开鸭皮，边播边用刀割开，使骨肉分离，一直割到大腿末端，斩断骱骨，后去腿骨，切去鸭臊，再将鸭皮翻转，恢复原状。肝去胆，肫去皮污，和鸭一起洗净，同入沸水锅焯后洗净。\r\n2.野鸭与鸽用上述方法整体出骨，再洗净。\r\n3.将鸽子由野鸭刀口处套入野鸭腹内，并将冬菇10克、火腿片50克、笋片50克填入野鸭腹腔之内，再将野鸭由鸭子刀口处套入家鸭腹内，并在鸭腹中填入冬菇15克，火腿片50克，笋片50克，将光鸭刀口合好成套鸭，放入沸水锅略烫一下。', '《礼记·内则》记载：“弗食舒凫翠”，这是说不食鸭尾臊。在长沙出土的“马王堆”一只墓的遗骨里，考古学家找到了鸭骨。在汉墓的器里，常常看到，楼阁下的池子里，有家鸭遨游，水里配有鱼鳖。这说明，我国在两千多年前不仅饲养家鸭，在宫廷膳食中，用鸭制的菜肴已十分讲究。“三套鸭”，用家鸭、野鸭、菜鸽整料出骨，套制成餐。三禽相会，技艺高超，制作新奇。此菜最初出现在扬州。清朝乾、嘉之后，流传到山东济宁、济南等地，后及北方一些城镇，今为大西北鸭馔名肴。', '此为工艺菜，禽出骨，不可破皮，连环相套，注意形整，美观。此菜重火功，大火烧开，小火慢火靠，酥烂粑软，方成美味。', NULL, '肥光鸭1只2500克，野光鸭1只750克，光鸽1只250克，水发冬菇50克，熟火腿片150克，笋片10克，绍酒100克，食盐10克，姜块5克，葱结10克。');
INSERT INTO `recipe` VALUES (1378, 80, '商芝肉', '1.将肉刮洗干净，入汤锅煮至六成熟捞出，趁热用蜂蜜、醋涂抹肉皮。\r\n2.炒锅内放熟猪油，用旺火烧至八成熟，即将肉块皮朝下投入，炸至成金黄色时，捞入凉肉汤锅中泡软，再放在案板上，切成10厘米长、0.7厘米厚的片，皮仍朝下，整齐地装入蒸碗内。\r\n3.将5克大葱切成3厘米长的段，另5克切成3厘米长的斜形片。姜去皮洗净，3克切成片，1克切成末。摊鸡蛋皮切成3厘米长的象眼片。\r\n4.商芝入沸水锅中煮软捞出，去掉老茎、杂质，淘洗干净，切成3厘米长的段，放入锅中，加酱油5克、精盐1克、猪油10克拌匀，盖在肉片上。另将鸡', '商芝，又名柴萁，属蕨类，嫩叶可食。西汉初，“四皓”志行高洁，隐居今陕西省内商山，采蕨而食，当地因而称蕨菜为商芝。《本草纲目》谓：“四皓食芝而寿。”故本品有补益、抗衰老的作用。此菜色泽红润，质地软糯，肥而不腻，入口可化，有浓郁的商芝香味，是陕西商县特有的风味菜。', '猪五花肉必须带皮、去骨，烹制后色泽金黄，质地绵润，才不失地道风味。蒸的时间宁长勿短，成菜软糯，入口稍咀即化，风味始佳。可先蒸制好芝肉备用，临上桌时，再蒸热浇汁。', NULL, '猪五花肉500克，八角3个，商芝50克，醋5克，摊鸡蛋皮15克，绍酒15克，鸡汤200克，味精1.5克，熟猪油2000克，酱油10克，蜂蜜15克，精盐1.5克，葱10克，麻油10克，姜5克。');
INSERT INTO `recipe` VALUES (1379, 80, '五丝驼峰', '1.将驼峰用水泡6小时，修去杂物，冲洗干净，放入锅内煮3小时，捞出再用温水洗一次，切成8厘米长的细丝，锅内加水，放入驼丝，加米酒25克氽两次捞出，沥干不分。\r\n2.火腿、玉兰片、冬菇、鸡脯均切成6厘米长的细丝。\r\n3.韭黄摘洗干净，切成寸段。\r\n4.锅坐火上，滑锅后加油烧热，放葱、姜、蒜炝锅，加驼丝煸炒，喷入料酒，随即加入火腿等各种细丝，再加味精、盐、酱油和醋，炒匀，用水淀粉勾汁，颠匀即成。', '“五丝驼峰”是河西历史悠久的传统佳肴，其源可追溯到唐朝之前，据《明皇杂录》记载：明皇每年十月幸华清宫，杨国忠姐妹五家扈从。出发前会忠府第，大摆筵席，“翟釜”红烧“紫驼峰”，水晶盘进“赤尾鲤”，琼浆玉液，水陆杂陈。明皇还担心他们吃不好，专差太监送来“御厨八珍”。主客举“犀箸”端“玉杯”，美人从旁捧壶劝酒，乐队吹打助兴，极尽豪奢。唐朝大诗人杜甫之诗，号称诗史，他在《丽人行》中写道：“紫驼之峰出翠釜，水晶之盘行素鲜，犀筋饮久未下，鸾刀缕切空纷纶。”意在讽刺虢国夫人与杨国忠等的奢侈淫乱，却也历史地记录了驼峰作为珍', '本品为高级宴会菜式之一，要求刀工精细。先将原料切成片，然后切丝，丝的直径在0.5厘米左右。主料驼蜂丝长8厘米，辅料火腿、玉兰片、冬菇、鸡脯丝长6厘米，粗细均匀。长短不一，成菜后主料突出，辅料为衬。韭黄切3厘米长，较辅料丝为短，宜后下，在火腿等五种丝炒入味时放入，颠匀即可出锅，以保持其色鲜脆嫩。本品为熟炒菜式，主料辅料之丝皆不可挂糊上浆，要求旺火，热锅、热油，用水淀粉勾汁，使炒菜汁浓味厚，汁紧紧抱着主料辅料，有特殊的芳香味。', NULL, '驼峰725克，熟火腿50克，水发玉兰片50克，水发冬菇50克，韭黄150克，熟鸡脯肉50克，湿淀粉25克，熟猪油150克，料酒100克，味精5克，葱丝2.5克，姜丝10克，蒜末25克，食盐4克，酱油25克，醋15克。');
INSERT INTO `recipe` VALUES (1380, 80, '夏河蹄筋', '1.将蹄筋放在柴火上撩去毛，然后把碱面放清水盆中，视溶解后，将蹄筋投水中刷洗干净，沥水晾干。\r\n2.锅坐火上烧热，加精油400克，改微火，乘凉将蹄筋加入油锅，浸泡约20分钟，使油慢慢浸入筋内，油温至八成熟时，将蹄筋捞入清水锅中，用微火边烧边泡，视水烧开后，捞出用刀刮净，将蹄筋顺长剖二，切成3厘米长的小段，整齐地码在碗中，加食盐和其它调料，浇鸡汤半斤，上笼蒸约20分钟，滗汤，翻扣盘中。\r\n3.将木耳和黄花水发，摘洗干净。将玉兰片、木耳撕碎，黄花切成小段，葱切段，蒜切片，然后把炒勺坐在火上，加清水100克，用', '蹄筋向来为筵席上品，食用历史悠久。在古代食谱中，曾有将“参、翅、骨、肚、窝、膜、筋、唇”列为“八珍”之说。古人所谓的蹄筋，多指鹿筋，并非猪蹄筋、牛蹄筋、羊蹄筋。至清代，考《随园食单》中所载的“筋”，仍是鹿筋，这大概是袁枚祖籍杭州，嗜食江浙风味，牛、羊筋“非南人家常时有之物”的原故，是证牛、羊蹄筋入馔确乎晚于鹿筋。夏河地区位于甘南藏族自治州的西北部，地处甘南高原和黄土高原的过渡地带，海拨较高，大部地区在3000-4000米，是大西北主要的畜牧业基地之一所产羊筋，质地上乘，量甲天下，用以入馔，为秦陇名肴。传说', '蹄筋在火上撩去毛时，注意不要烧焦。可将蹄筋先用凉水泡一夜，泡透后放大容器内用宽水煮，小火微开，待煮软时即可放在温水里洗净，放入原来的容器内，加葱、姜、料酒，上火煮约1小时，捞出在凉水中冲漂，以去腥膻味。存放时用凉水泡上。蹄筋制作方法众多，名馔佳肴亦多，自古至今鸡汤不能缺少。鸡汤的质量直接影响到菜肴的质量，故除蹄筋发好制好外，吊好鸡汤极为重要。', NULL, '羊蹄筋150克，木耳15克，黄花15克，生姜2.5克，清油500克，胡椒粉2.5克，碱面100克，花椒粉2.5克，水淀粉40克，食盐2.5克，鸡汤500克，味精0.5克，葱白25克，香油10克，苹果1枚，蒜瓣5克。');
INSERT INTO `recipe` VALUES (1381, 80, '炸野鸡卷', '1.将野鸡脯肉洗净，切成细丝。葱5克切成丝，15克切成葱段。生姜切成丝。网油洗净，晾干水分。韭菜洗净，面酱加白糖搅匀。鸡蛋清用竹筷打成泡沫状，加面粉10克和淀粉15克搅匀，制成蛋泡糊。25克淀粉加少许水制成糊。\r\n2.鸡肉丝放碗内加盐、料酒、葱、姜丝、香油5克拌匀。网油铺在砧墩上，抹淀粉糊，将鸡丝放在网油一边，顺长摆成一条，中间放1根腌韭菜，卷成4分粗的卷，如此卷完，上笼蒸透取出。\r\n3.锅内加油烧至四成熟，将野鸡卷挂上蛋泡糊下锅，炸至外部凝固捞出。待油温升至六成热时，重炸野鸡卷至金黄色，捞出沥油切成3厘', '“炸野鸡卷”始于黄谏旧居“黄家园”，色泽金黄，外酥里嫩，风味特别，流传市肆，遂为兰州名肴。黄谏，兰州人，曾任翰林院侍读学士，著名《经书集解》和《兰坡集》，是明代陇上知名学者。黄谏及第前，多在黄家楼（今兰州中央广场东侧）读书，曾收留一胡姓女子为妾。胡才貌出众，善烹饪，深得黄谏宠爱及近邻称赞，呼为“胡姐姐”。不幸胡氏早殁，葬于华林寺后，当地农民把这圹称为“姐姐沟”。相传，胡氏殁后，谏常思之，一日梦胡氏烹野鸡卷，味香扑鼻，风味绝佳。梦醒，谏步至“姐姐沟”胡氏墓前，遂见一狐狸逐野鸡，疑之为狐仙。狐去，谏得鸡，家厨', '蛋泡糊，又称雪衣糊。把蛋清抽打至硬稠，以能立住筷子为度，然后再加淀粉、面粉调合而成。配料比例一般是1个蛋清加面粉15克。荤素菜肴均可使用蛋泡糊，挂这种糊的菜肴，经炸制后雪白如霜，涨发饱满而松软。蛋泡糊本身膨胀性很大，故鸡卷挂糊要厚薄适当，挂糊太厚则主料膨胀过大过厚，挂糊太薄，则不酥松。鸡卷挂糊下油锅炸时，须待糊定型时方可用手勺不停地推动、翻转，以免炸出双色来。鸡卷炸透之后，捞出热油重炸一次，才能使其外酥松，里脆嫩，颜色一致。', NULL, '野鸡脯肉100克，猪网油250克，鸡蛋清3个，淀粉40克，葱20克，香油20克，料酒5克，椒盐15克，面粉10克，腌韭菜5根，白糖2克，植物油500克，生姜2克，食盐2.5克，面酱15克。');
INSERT INTO `recipe` VALUES (1382, 73, '八宝甜饭', '糯米洗净，浸泡，上笼蒸熟，拌入绵白糖、熟猪油。碗内抹上猪油。将八味果料拼摆成图形，装入拌好的饭，中间留一小坑，填上豆沙，再用饭封平即成。食时将饭蒸熟扣在盘中，上面浇一层用糖、淀粉、桂花勾成的薄芡即可。', '甜糯，香肥，润口，美观。', NULL, NULL, '糯米300克、豆沙50克、蜜枣15克、桂圆肉10克、莲子15克、金桔饼10克、青梅5克、瓜仁5克、松子仁5克。熟猪油15克、绵白糖30克、桂花1克。');
INSERT INTO `recipe` VALUES (1383, 73, '扒方肉', '1.先将五花肉皮的毛污刮洗干净，去掉杂质，切成15厘米见方的肉块，用火将皮面烧呈焦黑色时，放入冷水中泡软，然后取出，将焦黑灰刮去，使之成红黄色，洗干净。 \r\n2.将砂锅底部用竹篦垫好，把肉块的皮朝上置于篦子上，加入料酒、盐、酱油、冰糖、葱和适量的水，盖好盖，用小火炖至8成熟。再将肉块翻转使皮向下炖至全烂。然后转旺火将汁收浓，取出装盆，食用时，跟热荷叶夹一起上桌即可。', '色泽金红，香甜味浓。', NULL, NULL, '猪五花肉1.5公斤，酱油10克，盐5克，料酒100克，冰糖150克，净葱25克，荷叶夹12只。');
INSERT INTO `recipe` VALUES (1384, 73, '鲃肺汤', '1.将鲃鱼脊背向外，放在砧板上，左手捏住鱼腹的边皮，用刀把鱼皮划破，向外平推除去鱼皮，取出鱼肝（俗称鱼肺），摘去胆洗净。再挖去鱼的内脏，去骨取下两片鱼肉，放清水中撕去粘膜，洗净血污。将鱼肉、鱼肝分别片成两片，放入碗中，加精盐2克、葱末、绍酒5克拌匀稍腌。\r\n2.炒锅置旺火上，加鸡汤烧沸，将鱼片、鱼肝放入，加绍酒20克、精盐5克烧沸，撇去浮沫，放火腿片、笋片、香菇片、豌豆苗，加味精，烧沸后倒入汤碗，淋熟猪油少许，撒上胡椒粉即成。', '鱼肝肥嫩，浮于场面，鱼肉细腻，汤清味美，是夏秋季节的时菜。', '烹制时，必须先将鸡汤烧沸，再放鱼肉、鱼肝，以保持其质地鲜嫩。', NULL, '活鲃鱼500克，熟火腿片、熟猪油各15克，水发香菇片、熟春笋片各10克，豌豆苗5克，绍酒25克，精盐7克，味精、白胡椒粉各二克，葱末5克，鸡清汤750克。');
INSERT INTO `recipe` VALUES (1385, 73, '白炒刀鱼丝', '刀鱼洗净，批下两面鱼肉，皮向下平放砧板上，用刀背轻捶鱼肉，致鱼刺粘在皮上，再用刀刮下鱼肉略斩成茸，加入绍酒、葱姜汁、精盐、蛋清和水淀粉调拌，灌入用牛皮纸做的漏斗内。炒锅上火烧热，放熟猪油，烧至三成热（约66度）时，将漏斗内的鱼茸从小孔中慢慢挤入油锅，至熟时捞出，改成4厘米长的段。将配料切成丝，放入原锅中煸炒，绍酒、精盐、葱姜汁、鸡汤和味精，倒入鱼丝，晃动炒锅，用水淀粉勾芡，淋熟猪油翻锅装盘即成。', '鱼丝洁白，加以三丝调和，色彩悦目，软嫩润滑，味美可口，为初春佳肴。', NULL, NULL, '刀鱼300克、水发冬菇10克、绿叶菜100克、蛋清20克、火腿15克。绍酒20克、葱姜汁10克、精盐2克、水淀粉15克、味精1.5克、猪油500克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1386, 73, '白酥鸡', '将鸡洗净，取鸡腿、脯肉四块，剔尽胸腿骨，用刀排剁拍松，加入绍酒、精盐、葱、姜汁腌渍。将虾仁、猪肥膘分别斩成茸。加入虾子等调料搅匀成虾馅。将鸡肉上拍一层干淀粉，抹上虾馅，用刀轻轻细排，将表面抹平，放入盘中连同肫肝一道上笼蒸熟，取出酥鸡，滗去卤汁，肫肝切片。净酥鸡切成长片，与肫肝及其它配料一道扣入碗内，倒入蒸鸡卤汁。上笼蒸30分钟。净绿叶菜炒熟辅入凹盆中，取出蒸好的酥鸡翻扣入盆中，原汁滗出。锅置火上放熟猪油，投入葱姜炸香捞出，倒入卤汁，放入白酱油，虾子烧沸着芡，淋熟鸡油，浇在酥鸡上即成。', '以绿叶菜衬底，白绿相映，酥鸡细嫩，味鲜而美，清淡爽口，深受食者喜爱。', NULL, NULL, '当年仔母鸡鸡脯肉300克、大白虾150克、熟火腿片25克、冬笋尖片25克、香菇片15克、猪肥膘50克、虾子5克、绿叶菜25克、淀粉25克、绍酒30克、精盐7.5克、葱25克、姜15克、白酱油25克、猪油35克、鸡油15克。');
INSERT INTO `recipe` VALUES (1387, 73, '白汁乳狗', '将乳狗宰杀后，煺毛，去掉内脏、头、脚和光脊，洗净斩成4厘米见方的块，放入水锅中出水。火腿、香菇切片。炒锅置火上烧热，放花生油烧至二成（约50度）热，投入葱姜炸出香味，放入狗肉略煸，加绍酒、肉汤烧沸后移小火烧至酥烂。再加精盐转旺火收浓汤汁，放入胡椒粉、味精，淋芝麻油装盘。将火腿片、香菇片，青菜心、冬笋等，用锅中留下的汆汁略烩后浇在盘中的狗肉上即成。', '汤汁浓白、味厚，狗肉异香酥嫩。是冬令时节之佳肴。', NULL, NULL, '乳狗肉750克、熟火腿片25克、水发香菇35克、冬笋150克、青菜心250克、精盐15克、花生油100克、味精12克、葱姜各15克、绍酒35克、胡椒粉7.5克、芝麻油5克。');
INSERT INTO `recipe` VALUES (1388, 73, '白汁鼋量', '将甲鱼宰杀，入沸水锅中略烫刮去表皮黑衣，去内脏洗净，斩成块状。将甲鱼蛋放入碗中，加葱姜、绍酒、精盐，上笼蒸熟取出。锅中放入竹箅，将鱼块排入锅内，上放肥膘片。另取锅上火放油，投入葱姜炸黄发香时，捞出葱姜放肥膘肉片上，将油倒入甲鱼锅中，加绍酒，加盖上旺火略烧，加猪肉汤，烧沸后移至中火烧30分钟，加精盐、蒜瓣再烧。然后加冰糖屑、熟猪油，用微火焖烧2小时至酥烂，拣去葱姜，用旺火收稠汤汁，离火。将甲鱼块扣入到几个碗中，把卤汁均匀的分浇在碗内。将锅置旺火烧热，放入熟猪油。投入葱段炸香，放入笋块、山药块、木耳、甲鱼蛋，', '鱼肉酥烂脱骨，裙边透明光亮，汁粘如胶，入口咸中带甜。并有滋阴凉血，健脾补肺，固肾益精之功效。', NULL, NULL, '活甲鱼750克、猪肥膘50克、熟山药50克、熟冬笋30克、水发木耳20克、葱25克、姜15克、绍酒30克、盐10克、猪肉汤100克、蒜10克、熟猪油35克、冰糖7.5克、白糖5克，芝麻油25克、淀粉25克。');
INSERT INTO `recipe` VALUES (1389, 73, '百花酒焖肉', '猪肋条肉洗刮干净，用洁布吸去水分，用烤叉插入肉块中，肉皮朝下，在中火上烤至皮色焦黑，其目的是起香增色，离火抽出烤叉，将肉入温水中泡软，刮去皮上焦污并洗净。然后修去肉的左角，切成大小均等的12个方块，再在每块肉皮上剞芦席形花刀。取砂锅一只，内垫竹血箅，放入葱姜，把肉块皮朝上排放入锅，加百花酒、白糖、精盐、置旺火上烧沸，再加清水、酱油、盖上锅盖，用微火焖1小时至酥烂，再移至旺火收浓汤汁，拣去葱姜装盘即成。', '酒香浓郁，肉酥入味，甜咸可口，肥而不腻，别有风味。', NULL, NULL, '去骨肋条肉一块1000克、百花酒50克、精盐10克。味精7.5克、白糖50克、酱油30克、葱姜各15克。');
INSERT INTO `recipe` VALUES (1390, 73, '拌脆鳝', '1.将鳝鱼丝放入烧至9成熟的油炒勺内炸半分钟即捞出，之后放入热水中稍泡一下，再放热油勺中炸脆，捞出后沥去油盛盘。 \r\n2.用酱油、香油、味精调成汁，浇在炸好的鳝鱼丝即成。', '色泽黄黑，鲜香酥脆。', NULL, NULL, '净鲜鱼丝250克，植物油800克(实耗约60克)，味清2克，香油、油各15克。');
INSERT INTO `recipe` VALUES (1391, 73, '鲍鱼鸡翼', '1.鸡翼剁去两端留中间一节洗净，加入酱油拌匀后，用7成热油炸上色后捞出，放入砂锅中，加进料酒、葱、姜、糖、酱油、盐和适量的水，用小火焖烂。拆除骨头再放入原汤，用大火收浓汁入盘。\r\n2.将鲍鱼花纹四边撕去，切成麻花刀放入炒勺中，加进浓汤、料酒、盐、味精，烧开后用湿淀粉勾芡，取出与鸡翼装入一个盘内，各占一边。\r\n3.将炒熟的菜心加盐和味精，围在四周即成。', NULL, NULL, NULL, '原汤鲍鱼500克，鸡翼24克，小菜芯12棵。生油800克(实耗约70克)，酱油15克，盐5克，白糖、味精、湿淀粉各10克，料酒35克，葱、姜各10克，浓汤150克。');
INSERT INTO `recipe` VALUES (1392, 73, '爆氽', '将青鱼段斜片成1.5厘米的鱼块。用绍酒、酱油浸渍5分钟。炒锅置旺火上，放入豆油，烧至八成热(约200度）时，放入鱼块炸至深黄色时捞出。另取锅上火，加入鸡清汤，投入爆鱼块、笋片、水发冬姑、绍酒、葱姜，烧5分钟，再放入精盐、味精即成。', '鱼块鲜嫩，酥香，汤汁浓厚，鲜肥，佐酒下饭兼优，尤宜老人食用。', NULL, NULL, '净青鱼肉500克，笋片25克，水发冬菇15克，鸡清汤450克，绍酒25克，酱油15克，豆油450克（实耗油75克），葱姜各10克，精盐2克，味精1.5克。');
INSERT INTO `recipe` VALUES (1393, 73, '扁大枯酥', '将猪肋条肉、生荸荠分别剁成米粒大小，同放碗内，加蛋黄、米粉、调料搅拌匀，做成直径8厘米的圆饼5块。锅置火上烧热，舀入清油烧至七成热（约175度）时，将肉饼逐个放锅内煎炸成淡黄色捞出，待油烧至八成热（约200度）时，再放入肉饼煎至金黄色时，锅离火几分钟，然后再置火上煎约2分钟，直至肉饼成枯黄色，沥去油，装入长盘中。在煎炸肉圆的同时，用芝麻油将菊花叶加盐、味精、白糖炒至翠绿色，围装在长盘两头。锅置火上放鸡清汤，加酱油、白糖、绍酒烧沸，着芡，放醋，淋入芝麻油，起锅浇在肉饼上即成。', '色呈枯黄，香气扑鼻，酸甜可口，进餐时佐以嫩菊花叶，清香爽口，别具一格。', NULL, NULL, '猪肋条肉750克，生荸荠250克，蛋黄50克，菊花叶15克，粳米粉75克，芝麻油15克，湿淀粉20克，花生油500克，精盐15克，味精15克，酱油25克，白糖20克，绍酒25克，湿淀粉25克，醋25克，清油500克，芝麻油10克。');
INSERT INTO `recipe` VALUES (1394, 73, '冰糖排马面', '1.将猪头放入盛器，加温开水泡一下，用刀刮净毛根和皮上污物，在下额处顺长劈开成两片，放入开水锅里出水，除去血沫，捞出用清水洗净。 \r\n2.取大铁锅一只，锅里放竹垫，将猪头皮朝下放在竹垫上，加水（以淹没猪头为度），下葱结、姜片、绍酒，烧开后改用小火，焖1.5小时左右，至猪头七八成熟时捞出，拆去头骨，改刀切成大块，仍按两片猪头形状放在竹垫上，放入炒锅里，加醋、绍酒、冰糖、酱油、桂皮、葱结、姜片，加煮猪头的汤，在旺火上烧开后，移至微火上，焖1.5小时左右，至猪头肉像豆腐一样酥嫩，即可出锅，拣去葱、姜、桂皮，将竹', '色泽深红，卤汁浓厚，肉质酥烂，味香浓郁，甜中带咸，形似马面。', NULL, NULL, '猪头1个（重4000克左右），冰糖、醋各300克，绍酒75克，酱油175克，桂皮25克，葱结4克，姜20克，绿叶蔬菜少许。');
INSERT INTO `recipe` VALUES (1395, 73, '饼子野鸭', '将野鸭干煺去毛，洗净，放入清水中漂净血水。将野鸭和猪肥膘肉一起放入水锅中烧沸，撇去浮沫，捞出洗净，用绳紧扎三圈，鸭胸朝下放入内有竹箅垫底的砂锅中，肥膘肉放在野鸭两旁。原汤锅中加酱油、白糖、精盐、绍酒烧沸，倒入砂锅中，加葱结、姜块，用一只平盘压住鸭身、加盖上中火烧沸后，移至小火1小时离火，拣去葱姜。将猪五花肉斩成米粒状，放入碗内加调料拌匀，做成长圆形肉饼。将锅置火上烧热，放油，投入肉饼煎成金黄色，加绍酒、酱油、白糖、猪肉汤烧沸，焖3分钟后。倒入盛有野鸭的砂锅中，再将砂锅置火上烧焖1小时，至野鸭酥烂离火，取出', '色泽棕黄，鸭肉酥烂脱骨，肉饼松香，汤汁鲜浓。', NULL, NULL, '野鸭一只50克、去皮猪五花肉75克、带皮猪肥膘50克、熟冬笋片30克、水发冬菇25克、葱结10克、姜块10克、酱油25克、白糖7.5克、精盐2.5克、绍酒20克、芝麻油10克、花生油45克。');
INSERT INTO `recipe` VALUES (1396, 73, '叉烤鳜鱼', '活鳜鱼宰杀后放砧板上，刮去鳞，掏除鳃，从鳃部用筷子绞出内脏，使腹部保持完整无破损，洗净后晾干水分。猪网油洗净，用葱、姜汁浸渍。京冬菜择去杂物洗净。鸡蛋磕碗内，加葱、椒盐、干淀粉，调成蛋糊。炒锅置中火上烧热，加入熟猪油，投入姜、葱丝略煸，再将肉丝放入煸炒，放入京冬菜、笋丝，加入绍酒、精盐、味精，炒熟成馅。把洗净的鱼放在砧板上，用刀在鱼两面剞上花刀（刀深至内，不能划破鱼腹），然后用葱、姜汁、绍酒、精盐擦抹鱼身，腌渍2小时后，将馅从鱼口中填入鱼腹。把猪网油摊平，上涂蛋糊，放上鳜鱼包好。取铁丝烙一只，放上鳜鱼，鱼', '外脆里嫩，色泽金黄，肉质鲜香，馅料软糯，佐以姜醋，回味无穷。', NULL, NULL, '活鳜鱼750克、京冬菜15克、笋丝15克、猪肉丝30克、鸡蛋一个（约50克）、猪网油250克，生姜10克、葱6克、绍酒40克、精盐10克、味精1克、香醋50克、芝麻油15克、熟猪油40克。');
INSERT INTO `recipe` VALUES (1397, 73, '叉烤酥方', '1.将正中7根肋骨的方肉放在砧板上，用刀从肋骨中间处斩断（不能斩断肋骨），并将四边修齐成长约30厘米、宽约20厘米的长方块，再用削尖的竹筷在肉面戳许多小眼，以便透热排气。\r\n2.用铁叉双齿从肉块第二根与第六根肋骨之间，顺骨缝叉入，叉到7厘米处，翘起叉夹，隔7厘米再叉入，最后叉尖从另一边叉出，再用两根两头削尖的竹筷横叉在肋条肉的两边，别在叉齿上。\r\n3.当炉膛内芦柴烧至无火苗、无烟时，再把肉块（皮朝下）伸进炉里烤20分钟，至肉上水分烤干，肉皮呈黑釉色时离火，用湿布润湿肉皮，刮去皮上焦污。再用此法烘刮一次，抹', '皮面松脆异常，肉质干香酥烂，油而不腻。', NULL, NULL, '猪肋条肉3000克、空心饽饽（面制的一种饼饵）、麻油25克、绵白糖200克、甜酱250克、精盐10克、葱15克。');
INSERT INTO `recipe` VALUES (1398, 73, '叉烤野鸡片', '将野鸡剥去皮洗净，剔下鸡肉批切成薄片，放入碗内，加绍酒、白酱油、白糖浸渍等用。将虾仁、肥膘肉分别斩成茸，放入盘内，加入野鸡片、葱姜汁、精盐、白胡椒粉、水淀粉用劲搅匀成糊状。蛋清加葱姜末调匀，涂抹在猪网油上，再将野鸡片均匀地铺在网油上面，叠成长约25厘米，宽约20厘米的长方块。然后夹进铁丝网夹内，上叉，在烤炉内反复熏烤，至色黄皮酥后出炉，抹上芝麻油，改刀菱形装盘。上桌时，配以甜成酱、花椒盐、青蒜花及蝴蝶夹，风味独特。', '外脆里嫩，油润爽口。', NULL, NULL, '雄野鸡一只（重约450克）、虾仁50克、肥膘肉50克、猪网油75克、绍酒30克、白酱油20克、白糖10克、葱姜汁15克。精盐7克、白胡椒粉2克、水淀粉25克、蛋清25克、葱姜末各5克、芝麻油15克、甜面酱10克、花椒盐5克、青蒜花15克。');
INSERT INTO `recipe` VALUES (1399, 73, '叉烧野鸡', '野鸡去毛洗净，剖腹去内脏，用葱姜、绍酒、酱油腌渍2小时取出，擦干。将锅置火上烧热，放入油烧至六成热（约150度）时，投入肉丝煸炒，再投入京冬菜、笋丝炒匀，加酱油、绍酒、白糖，鸡汤烧沸，用水淀粉勾芡，起锅装入盘中凉透，倒入葱丝拌匀，装入野鸡腹内。将网油洗净晾干水分，平铺在砧板上，抹上带花椒盐的蛋薄糊，将野鸡包入网油中，放在瓷盘。上下放葱姜。然后放进烤箱，烤至外表色呈金黄、香味四溢时取出，去掉葱姜、网油，将野鸡腿肉、脯肉撕成片状，与腹内馅拌匀，浇上芝麻油，装入盘中即成。', '食用时以手撕成片，与馅料拌和，干香油润，别具情趣。', NULL, NULL, '野鸡一只（重约500克）、京冬菜25克、冬笋丝25克、猪肉丝25克、猪网油75克、葱25克、姜25克、绍酒30克、酱油15克、白糖10克、水淀粉30克、花椒盐10克、芝麻油15克、花生油25克。');
INSERT INTO `recipe` VALUES (1400, 65, '参麦团鱼', '将团鱼斩去头颈，沥净血水，放在盆内加沸水烫3分钟后取出，用小刀刮去背部和裙边上的黑膜，再剥去四脚上的白衣，斩去爪、尾及腹壳。取出内脏，铣净。炒锅置火上，放入清水和团鱼，烧沸后用炆火煮约半小时捞出，放入温水中，撕去黄油，剔去背壳、腹甲、甲肢粗骨，洗净切成3厘米见方的块入放入碗中。将火腿肉切成小片，生板油切成丁，盖在团鱼上面。将调料的一半（味精暂不用）、姜米、清汤适量一并放入碗中。将浮麦、获菩用布包好扎口，放入汤中。人参打成细粉撒在上面，用湿棉纸封好碗口，上笼蒸3小时至团鱼酥烂。团鱼出笼后拣去葱节，翻碗，将原', '鱼肉软烂，焾滑利口，汤汁浓厚，蛋味浓郁，并具有滋阴补血，益气健脾之功效。', NULL, NULL, '活团鱼一只（约500-1000克）、人参5克、获菩10克、浮麦20克、鸡蛋一个、火腿肉50克、姜15克、葱15克、食盐3克、料酒15克、味精1克、鸡汤500克、生板油50克。');
INSERT INTO `recipe` VALUES (1401, 73, '拆冻鲫鱼', '1.鲫鱼去鳞、去鳃、去内脏，洗净沥干，用绍酒少许、酱油10克浸渍20分钟取出。\r\n2.炒锅上旺火，用油滑锅后，放入菜油烧热，下鲫鱼煎至两面呈黄色，放葱、姜爆香，加绍酒、精盐、酱油、白糖、味精和清水200克左右，盖上锅盖，先在旺火上烧开，再端到小火上烧六七分钟，至鱼肉成熟。\r\n3.用锅铲将鲫鱼盛出，鱼汤倒入碗里待用，用刀将鱼头切下移开，再将鱼尾（长约5厘米左右）切下移开，再用刀尖伸入鱼肚内将鱼剖开，把上面一片鱼肉翻开，先拆去脊背大骨，再抽去肋骨，并将脊背小骨拣净，然后再将上面一片鱼肉合拢呈原状，仍将鱼头鱼尾', '形态美观，鱼肉鲜嫩，清凉适口，夏令佳肴。', '要掌握好火候。在鱼肉断生时即取出拆骨，以保持鱼体原状。烧得过熟，鱼酥烂，不易整条出骨。原汤卤汁要浇匀，使鱼肉都浇到，以便入味。', NULL, '河鲫鱼2条（重约600克），绍酒15克，葱结1克，姜片1克，酱油60克，白糖25克，味精2克，精盐5克，菜油75克。');
INSERT INTO `recipe` VALUES (1402, 73, '拆烩鲢鱼头', '将鱼头劈成两片，去鳃洗净，放入清水锅中，置旺火烧沸，后移小火焖至鱼肉离骨时，捞起拆去骨，再将鱼头肉下水锅，加葱姜、绍酒出水。青菜心洗净，削成橄榄形，投入四成热（约88度）的油中划至菜色翠绿时，捞出沥油。锅至火上放油，烧至五成热（约1l0度）时，投入姜片、葱炸香后，捞出，放入蟹肉、虾子略炒，加绍酒、鸡汤、精盐、白糖，再放入笋片，熟火腿片、熟鸡肫、熟鸡肉片、水发香菇、虾子等原料，盖上盖，烧10分钟，加入菜心、味精，烧沸后，用水淀粉勾芡，淋入白醋、熟猪油，起锅装盘，撒上白胡椒粉，放上火腿片即成。', '鱼头无骨，鱼肉肥嫩，汤汁稠浓，口味鲜美，营养丰富，为冬令佳肴。', NULL, NULL, '鲢鱼头一个、蟹肉35克、青菜心250克、笋片50克、熟火腿片25克、熟鸡肫40克、熟鸡肉75克、水发冬菇15克、虾子5克、葱25克、姜15克、绍酒25克、鸡汤250克、盐2.5克、白糖7.5克、味精2克、湿淀粉25克、白醋5克、熟猪油75克、白胡椒粉2克。');
INSERT INTO `recipe` VALUES (1403, 73, '拆烩野鸭', '1.将野鸭剥皮留胸脯肉(其它留作它用)，洗净，放入开水中氽一下后取出。炒勺上火，加入清水、料酒、葱、姜，将鸭煮成8成熟捞出(其余均不要)，拆去鸭骨，鸭肉撕成块。冬笋用刀切成块下勺煮熟捞出。小菜芯洗净，氽一下，出水沥干。 \r\n2.炒勺内放鸡油25克，料酒25克，鸡汤、冬笋、盐和鸭块烩透。然后再放味精、小菜芯，有湿淀粉勾芡(稠米汤芡)，淋上鸡油即成。', '色泽白绿美观，清淡鲜香适口。', NULL, NULL, '净肥野鸭2只(约1.5公斤)，净冬笋100克，水菜芯12棵，鸡油100克，葱、姜各12克，料酒100克，盐7克，味精5克，湿淀粉50克，鸡汤400克。');
INSERT INTO `recipe` VALUES (1404, 73, '拆烩鱼头', '1.将鲢鱼头一劈两半，掏去两鳃后洗净，放入炒锅内，加入清水、料酒、12克姜、25克葱，先用旺火烧开后，改小火焖至能拆取鱼骨时捞出。而后拆去鱼头骨(注意保持原形整齐)，为除去腥味，用温水漂洗几次。\r\n2.将炒锅烧热放入熟大油，葱(25克)，姜(13克)，炸香后取出，加入浓汤、鱼头肉、料酒、盐，用小火烩10分钟左右，然后用湿淀粉勾芡。再改为大火将汤收浓，撒入胡椒粉即成。', '色泽金黄，鱼头肉质肥嫩，味鲜香浓。', NULL, NULL, '鲢鱼头一个，熟大油50克，盐、味精各10克，葱50克，姜25克，料酒100克，湿淀粉5克，浓汤500克。');
INSERT INTO `recipe` VALUES (1405, 73, '长寿菜', '1.香菇去蒂，用清水反复洗干净。冬笋切成4厘米长的薄片。\r\n2.炒锅上火，下油烧至六七成热，放入冬笋片煸炒，然后下香菇，加酱油、白糖、味精、鲜汤150克，旺火烧开，移小火上焖煮15分钟左右，至香菇软熟吸入卤汁，香菇发胖时，移旺火上收汁，用湿淀粉勾芡，颠炒几下，淋上麻油，出锅装盘即成。', '香菇软熟可口，滋味鲜美清香。', '香菇选中等肉厚的为佳，要浸软发透。烹制要用小火焖煮，使其吸足卤汁，食时鲜香可口。', NULL, '水发香菇500克，净冬笋50克，酱油20克，白糖5克，味精1克，花生油30克，麻油15克，湿淀粉3克，鲜汤150克。');
INSERT INTO `recipe` VALUES (1406, 73, '炒素什锦', '1.鲜蘑去蒂，刀切进1/2深，用手按一下成扇状。香菇切梅花状。黄瓜、胡萝卜均切2厘米段，削边成蝶状。西红柿去皮切菱形片。姜去皮切锯齿片。西兰花掰成小朵。玉米笋切段。马蹄、窝笋、紫菜头均削成球状。 \r\n2.全部主配料用开水焯一遍。\r\n3.起锅放少许油，烧热，投入全部主配料，加入鸡汤及调料翻炒，用水淀粉勾芡，淋明油出锅。', '颜色美观，口味清香微辣。', NULL, NULL, '鲜蘑40克，香菇40克，黄瓜40克，胡萝卜40克，西红柿40克，姜40克，西兰花40克，玉米笋40克，清水马蹄40克，窝笋40克，紫菜头40克，盐、味精、姜汁、水淀粉各适量，胡椒粉3克，油50克，鸡汤适量。');
INSERT INTO `recipe` VALUES (1407, 73, '炒虾蟹', '1.将大虾洗净，沥干水后放在碗中，先用味精、盐拌一拌，再用蛋清、湿淀粉挂浆，然后放入5成热的大油炒勺中划熟倒出。 \r\n2.将大油、葱姜末倒入热油勺中煸炒后，把蟹黄、蟹肉下入，而后加入酱油、味精、盐和料酒，炒透后倒入虾仁，翻炒几下，取出盛盘。盘底放有香醋，撒上胡椒粉，盆两侧放上香菜末即成。', '色泽金黄，味特鲜美，宴客佳肴，遐迩驰名。', NULL, NULL, '大虾仁250克，蟹黄、蟹肉各100克，鸡蛋清30克，香菜末2克，熟大油700克(实耗约50克)，料酒25克，盐3克，味精5克，葱、姜末各10克，湿淀粉6克，胡椒粉1克，酱油少许，香醋2克。');
INSERT INTO `recipe` VALUES (1408, 73, '炒血糯', '1.将血糯和白糯米淘洗干净，放入清水中浸一二小时，捞入容器中，加水50克左右，上笼蒸30分钟至熟，取出冷却。也可以入锅加水适量，烧成硬饭（饭粒要松而硬结，切勿糊烂），取出冷却。\r\n2.炒锅洗净上火，放熟猪油75克，倒入开水50克左右，加白糖溶化，下湿淀粉勾薄芡，将血糯米饭倒入炒透，最后放入青梅、红绿瓜丝、蜜枣、桂圆肉、甜桂花，炒和均匀，浇上熟猪油25克，起锅倒入汤盘内即成。', '色泽鲜艳，血糯韧硬，白糯软润，甜美细腻，香味浓郁。', '常熟血糯属籼型稻，糯性不足，必须与白糯米相配烹制，使之软糯适中。在下锅烹制前，先将糖油汁烧浓，再下糯米炒透，加配料出锅。注意切勿粘锅炒焦。', NULL, '血糯100克，白糯米、绵白糖各150克，青梅干、红绿瓜丝各10克，蜜枣、桂圆肉、湿淀粉各15克，熟猪油100克，甜桂花少许。');
INSERT INTO `recipe` VALUES (1409, 73, '虫草炖牛鞭', '1.将水发牛鞭用刀带雕成若干朵菊花状，用牛清汤反复焯，至牛鞭无腥味。\r\n2.将牛鞭放入小盆内，倒入清汤，上笼炖1小时，再加入虫草、枸杞和葱、姜、绍酒，再炖1小时，至牛鞭绵软、虫草糯化、枸杞味溢，倒出汤汁，将牛鞭取出倒入小汤盘中。\r\n3.炒锅上火，注入牛肉清汤，加盐、味精、胡椒粉、香油，调好味，倒入汤盘即成。', '汤汁清爽鲜醇，牛鞭松绵软喏，虫草润肤健体，枸杞明目状元，营养上乘，滋补佳品。', NULL, NULL, '水发牛鞭500克，牛肉清汤1000克，虫草20克，枸杞子10克，精盐2克，味精2克，葱、姜各20克，绍酒20克，香油10克，白胡椒粉少许。');
INSERT INTO `recipe` VALUES (1410, 73, '春笋白拌鸡', '将鸡洗净，放入肉清汤中，加绍酒、葱姜、煮至八成熟，捞出将其肉批成薄片。春笋去底根，放入汤中煮熟。捞出切成薄片。将酱油、醋、芝麻油、味精、精盐、白糖调和成卤汁待用。将笋片入肉汤中烫3分钟，捞出沥水，再倒入鸡片略烫，趁热用卤汁拌好笋片，置于盘中，上放鸡片，浇上卤汁即成。', '笋嫩清香。鸡肉鲜美，口味鲜咸，佐酒下饭皆优。', NULL, NULL, '仔母鸡一只（重约500克）、春笋75克、绍酒20克、葱20克、姜10克、酱油、20克、醋15克、芝麻油20克、味精2克、精盐5克、白糖7.5克。');
INSERT INTO `recipe` VALUES (1411, 73, '刺猬圆子', '1.把荸荠、猪肉切成如绿豆粒大小，然后稍剁数刀，使荸荠和肉混合均匀后盛入碗中。将料酒、盐、葱、姜汁、味精和湿淀粉，与适量水拌匀上劲捏成约40克重1个的圆子。 \r\n2.糯米淘洗干净后，用清水泡15分钟左右，沥去水放入钵中，加料酒、味精拌匀，腌10分钟左右，沥干汁水。再把捏好的圆子滚上糯米，放入事先抹上油的盆内，上屉用旺火蒸20分钟即成。', '色泽乳白，形似刺猬，外糯里嫩，香甜可口。', NULL, NULL, '猪五花肉500克，糯米150克，荸荠100克，料酒100克，湿淀粉50克，盐、味精各5克，葱、姜汁15克。');
INSERT INTO `recipe` VALUES (1412, 73, '大烧马鞍桥', '将鳝鱼宰杀，去内脏，斩去头、尾洗净，切成五厘米长的段，每段横剞二刀，后放入沸水锅中加盐、香醋略烫，捞出洗净。猪肉切成长方形片。锅置旺火烧热，舀入熟猪油，烧至五成热（约110度）时，放入葱姜炸香，再倒入猪肉片煽炒，加入绍酒、酱油和清水。加盖烧沸后移小火焖30分钟。另取锅置旺火烧热，放油烧至五成热（约110度）时，投入蒜瓣炸香，离火焖3分钟，捞出蒜瓣，锅再上火，放入葱姜略煽，投入鳝段翻炒，加入绍酒、香醋，再加入酱油、精盐、糖色和清水，烧沸后离火。将肉片、鳝段同入带有竹垫的砂锅中。倒入汤汁，加入白糖，加盖用旺火', '色泽酱红、鳝段酥香。汤汁稠粘，口味咸甜适中。', NULL, NULL, '粗活鳝鱼350克、猪腿肉150克、蒜瓣40克、香醋10克、葱15克、姜10克、绍酒20克、酱油20克、盐3克、糖色5克、白糖15克、芝麻油5克、胡椒粉1克、青蒜丝10克、熟猪油750克（实耗油150克）。');
INSERT INTO `recipe` VALUES (1413, 73, '淡菜皱纹肉', '淡菜用热水浸泡，发涨后去毛洗净备用。猪肉出水至断血后取出洗净，放入冷水锅中加葱姜煮至六成熟，再放入骨汤锅中，加绍酒、酱油、白糖走红。锅置旺火烧热，放清油烧至九成热（约225度），将走红的肋条肉趁热皮朝下投入油锅，盖上锅盖，待锅中迸爆声消失后揭盖，将肉翻身再炸片刻，捞起沥油。将过了油炸的肉放入原红汤锅内，置火中煮至肉皮起皱纹，捞出用旋刀将肉片切成长条扣入碗内，放入淡菜，加白糖、浇上煮肉红汤，上笼蒸透后取出，将肉汤滗入锅中，取出淡菜。另取碗豆苗加调味炒好，取2/3填入肉碗，将肉翻扣凹盆中，再将豌豆苗、淡菜围边', '皮似琥珀，内如镶玉，肥肉糯而不腻，瘦肉酥而不柴，口味咸中带甜。', NULL, NULL, '带皮去骨猪肋条肉750克，淡菜25克，精盐12克，酱油15克，白糖20克，绍酒25克，味精15克，葱姜各10克，湿淀粉25克，清油750克（实耗油75克）。');
INSERT INTO `recipe` VALUES (1414, 73, '东坡狗肉', '将狗肉剔去骨，放铁板上置火上将肉皮烘至金黄色，再泡入水中刮去焦膜，洗净。然后放入水锅中出水断血，捞出洗净，将狗肉切成四方块。放入砂锅内，加精盐、绍酒、酱油、白糖及香料，再把狗肉汤倒入，加盖烧沸，移至小火焖2小时，然后取出扣入碗内，再上笼蒸1小时，出笼翻扣盘中，撒上胡椒粉、青蒜末即成。', '色泽红润，滋味异香，狗肉酥烂，风味独特。', NULL, NULL, '小狗肉750克。精盐15克、绍酒30克、酱油25克、白糖20克、八角5克、桂皮5克、豆蔻5克、青蒜25克、胡椒粉5克。');
INSERT INTO `recipe` VALUES (1415, 73, '东坡绣球', '1.虾仁抽去肠泥，以盐抓洗，沥干水分，剁成泥状，香菇泡软切细末，荸荠拍碎；全部与绞肉一同拌匀，加入调味料充分搅拌至有粘性，即为馅。\r\n2.蛋打散，用平底锅小火煎成蛋皮，切成细丝。\r\n3.肉馅捏成球状，滚上蛋皮丝，入蒸具中以大火蒸7-9分钟。\r\n4.菜心入滚水中氽烫一下，取出漂凉、沥干水分，再入油锅中加少许盐味精同炒，盛出、排边。\r\n5.将蒸出来的汤汁倒于锅中（若不够再加水1/2碗）烧开，加少许盐、味精、胡椒粉调味，再以太白粉水勾芡成薄汁淋在蛋球上。', NULL, NULL, NULL, '绞肉1/2斤、虾仁4两、香菇3朵、荸荠数粒、蛋4个、菜心1/2斤。盐1小匙、味精、白胡椒粉、麻油各少许、太白粉1大匙、水4大匙。');
INSERT INTO `recipe` VALUES (1416, 73, '冬菇笋炖老豆腐', '将生鸡肉、鸡骨、肉骨入沸水锅中氽水。捞出洗净。豆腐放冷水锅中，加精盐烧沸，移中火煮至豆腐起孔，浮在水面上时捞入清水中，泡去豆腥味，捞出沥水，片去老皮，切成块放入盘中。取砂锅一个。放入鸡肉、火腿、鸡骨、肉骨垫底，将老豆腐铺在上面，舀入肉清汤，加入绍酒、精盐、酱油，上旺火烧沸后移至微火上，加入熟猪油，烧至汤汁浓稠时离火，捞出鸡肉、火腿和骨头，将豆腐连同卤汁倒入另一砂锅内，上放冬笋片、冬菇，再置旺火上烧沸，淋入熟猪油，稍炖即成。', '色泽金红，食之豆腐味浓含卤，质韧而嫩，笋菇脆嫩，口味鲜咸醇香。', NULL, NULL, '豆腐200克、冬笋20克、水发冬菇15克、生鸡肉50克、熟火腿15克、盐7克、绍酒10克、酱油15克、熟猪油20克。');
INSERT INTO `recipe` VALUES (1417, 73, '豆茸酿枇杷', '削去枇杷顶端，剥去皮，挖去核及内膜，口朝上放入盘中。将糖猪板油掺入豆沙中拌和，分别酿入枇杷内，再在每只枇杷口的周围插上松子仁5粒，中间缀以红樱桃末，上笼旺火蒸15分钟，取出整齐排入另一盘中。锅上火放清水，加白糖，糖桂花烧沸，用水淀粉勾芡，浇在枇杷上即成。', '形似梅花，口味香甜，枇杷汁多细腻，豆沙油润味美。', NULL, NULL, '枇杷300克、甜豆沙150克、糖猪板油30克、松子仁10克、红樱桃7.5克、白糖75克、糖桂花5克、淀粉15克。');
INSERT INTO `recipe` VALUES (1418, 73, '炖菜核', '1.将青菜洗净（不能弄散），菜头削成橄榄形，剖十字形刀纹，切去菜叶，取7厘米长的菜心。\r\n2.把鸡脯肉片成约长5厘米、宽1厘米的柳叶片，放入碗中，加鸡蛋黄、干淀粉拌匀。\r\n3.炒锅上火，下熟猪油，烧至四成热，放入菜心，用铁勺翻动，至翠绿色时捞出沥油。接着将鸡脯肉下锅过油后，取出沥油。\r\n4.取炒锅一只，先用部分菜心垫底，再将其余菜心头朝外，沿炒锅底边排成圆形，放在垫底的菜心上面（露出菜头），中心缀以虾仁、鸡脯肉、冬笋片。火腿片、冬菇片，加精盐、绍酒、味精、鸡清汤，置旺火上烧沸后，移至微火上炖约15分钟，淋', NULL, '烹制时先将洗净的菜心用温油汆至色泽碧绿取出，再与其它配料一起入锅炖煮，这样既可保持菜心的嫩度，又容易入味。', NULL, '“矮脚黄”青菜心600克，鸡脯肉60克，虾仁25克，冬笋片、熟火腿片各30克，水发冬菇、熟鸡油各15克，鸡蛋1个，绍酒10克，精盐、干淀粉各3克，味精1.5克，鸡清汤500克，熟猪油750克（约耗100克）。');
INSERT INTO `recipe` VALUES (1419, 73, '粉蒸回鱼', '将回鱼洗净，取下两边鱼肉，切成小长方块状（约四厘米）。锅置火上，放入清水浇沸，加入绍酒、精盐、葱姜，再把鱼块倒入锅内略烫，捞出放清水浸泡片刻并洗净。将豆瓣酱捣碎放入碗内，加入绍酒、酱油、糖和葱姜末调成汁。将回鱼捞出，沥干水分后放入盛汁的碗内拌和腌泡，然后拌入炒米粉，浇上麻油待用。将荷叶剪成圆形后放入沸水锅中略烫，然后捞出摊在小笼内，再把腌过的回鱼放进笼内用旺火蒸5分钟，取出连笼上桌即成。', '选用荷叶包蒸，清香四溢，鱼肉肥嫩，为夏令时菜。并有补中益气，健脾开胃，开发清阴之功效。', NULL, NULL, '回鱼750克、炒粳米粉100克、荷叶1张、绍酒20克、酱油20克、豆瓣酱6克、盐1.5克、糖10克、葱15克、姜10克、麻油10克。');
INSERT INTO `recipe` VALUES (1420, 73, '风鸡斩肉', '风鸡摘毛，斩去头爪，放入冷水中浸泡2小时。洗净，斩成大方块，再入冷水中浸泡1小时，捞入沸水锅中氽水。炒锅上火，放油，投入风鸡块偏炒，加入绍酒、葱姜汁，倒入砂锅内，加入酱油、白糖、清水，放大火上烧沸，移小火炖焖。猪五花肉细切粗斩成茸，加入绍酒、葱姜汁、酱油、精盐、白糖、清水适量，搅拌上劲，做成肉圆下油锅煎至两面金黄，然后放入风鸡砂锅，焖炖2小时即成。', '原汁原味，鸡酥肉嫩，为春节期间席上佳肴。', NULL, NULL, '风鸡一只（重约500克）、猪五花肉200克、绍酒40克、葱25克、姜25克、酱油15克、白糖10克、盐5克。');
INSERT INTO `recipe` VALUES (1421, 73, '风蹄', '将刮洗干净的猪蹄剖开主骨，斩下猪爪，一起放入锅中，舀入肉汤烧沸，撇去浮沫，捞出蹄子洗净，再放入原锅中，加绍酒、精盐、葱姜烧沸，移至小火烧至六成熟，捞出剔去火骨，用酱油抹在蹄皮上。将风鱼切成条块，放入碗中，将猪蹄皮朝下放在鱼块上，再放入猪爪，加绍酒、酱油、味精、白糖，舀入锅内原肉汤，用圆盘盖在碗上，上笼用旺火蒸约2小时至蹄膀酥烂，取出滗出汤汁，翻扣入盘中，浇上汤汁即成。', '蹄膀皮糯肉烂，凤鱼味鲜，酒香四溢，风味别具一格。', NULL, NULL, '猪前蹄750克，风鱼150克，精盐20克，酱油40克，白糖25克，绍酒50克，味精15克，葱姜各15克。');
INSERT INTO `recipe` VALUES (1422, 73, '佛手海蜇皮', '1.先用水将海蜇皮浸泡，而后洗净去掉泥沙，用刀切成3厘米宽的长条。然后沿长条纵向每切4刀连刀（切断第5刀，即成五指手掌形状的条），切好后用清水泡上。 \r\n2.上桌前，把加工好的海蜇皮，放入开水锅中烫一下，即成佛手状，迅速去水分装盘，浇上用香油、酱油、味精、盐、香醋等调料调匀的卤汁即可。', '色泽淡黄，鲜脆爽口。', NULL, NULL, '海蜇皮250克，香油25克，味精3克，酱油10克，香醋5克，盐3克。');
INSERT INTO `recipe` VALUES (1423, 73, '腐皮肉卷', '1.将猪肉洗净，用刀剁成细茸，盛入碗内，加适量的清水，放入精盐、味精和姜末，搅拌成肉馅。将面粉加适量水调成稀面糊。\r\n2.将豆腐皮铺在菜板上，撕去毛边，放入肉馅成粗条形，酱豆腐皮由里向外卷起，边缘用稀面糊封口。\r\n3.炒锅上火，放入花生油，烧至七成热，将肉卷放入炸至表皮起亮，倒入漏勺沥油。\r\n4.原锅留少量花生油坐火上，放入葱段、姜汤，用小火烧至汤汁收浓，在加入芝麻油，然后起锅，将肉卷切成小段，整齐的放入盘中即成。', '造型美观，色泽金黄，腐嫩肉鲜，口味醇和。', NULL, NULL, '豆腐皮3张，鲜嫩瘦肉300克，酱油75克，精盐5克，味精1克，白糖6克，2厘米长的葱白4段，生姜4片，肉清汤50克，绍酒、姜末、面粉、芝麻油、花生油各适量。');
INSERT INTO `recipe` VALUES (1424, 73, '腐乳汁肉', '将肋条肉切成5厘米见方块，氽水后捞出，把红曲米研成末，用水调和。在锅内放入竹箅，加葱姜、桂皮、八角，再加入猪肋条肉，在肉的上面放肥鸭，然后倒入红曲米水，加绍酒、精盐、酱油、冰糖和清水。将锅置旺火烧沸，移小火煨焖一个半小时，加冰糖收稠卤汁，起锅将肉皮朝下扣入碗内，上笼蒸透，吃时将肉翻扣入汤盆里，肉汁用旺火烧稠，浇在肉上即成。', '肥而不腻，酥烂入味，甜中带咸，色泽红亮。', NULL, NULL, '去骨肋条肉1000克、肥鸭750克、精盐15克、桂皮5克、八角5克、冰糖10克、红曲米适量、绍酒25克、酱油15克、葱姜各15克。');
INSERT INTO `recipe` VALUES (1425, 73, '贡淡炖鸭', '光鸭从脊背开刀，取出内脏，洗净，放入水锅中出水至断血，捞出洗净。贡淡用水泡软后剪去内毛或老肉，洗净待用。鸡肉与肥膘一同切成细米粒状，放入葱姜末，再加精盐、绍酒、蛋清拌成糊状，嵌在贡淡上待用。将鸭子放入砂锅（脯朝上），加精盐、绍酒、葱姜、贡淡、原鸭汤，加盖烧沸后移至小火炖至酥烂，食时加入火腿片稍炖即成。', '汤汁醇厚，鸭肉酥烂脱骨，淡菜鲜美。', NULL, NULL, '光鸭一只（重约2500克）、鸡肉100克、肥膘50克、熟火腿15克、葱30克、姜20克、味精4克、精盐15克、绍酒40克。');
INSERT INTO `recipe` VALUES (1426, 73, '瓜姜鱼丝', '1.将鱼宰杀，去鳞、去鳃、去内脏，洗净，剁去头、尾，去皮、去骨，切成鱼丝。酱瓜、酱姜切成丝。\r\n2.将鱼丝放入碗内，加鸡蛋清、盐、味精少许，干淀粉拌和上浆。\r\n3.炒锅上火，用油滑锅，下油烧至五成热，将鱼下锅滑熟取出。锅内留油少许，下葱段稍煸，加鲜汤50克、绍酒、盐、味精烧开，用湿淀粉少许勾芡，放入鱼丝和瓜姜丝炒和，颠翻几下，淋麻油，出锅装盘即成。', '色泽洁白，鱼丝细嫩，瓜姜香脆，鲜咸适口。', NULL, NULL, '青鱼或鳜鱼1条（重约600克），酱姜、酱瓜各25克，绍酒15克，葱段、味精各1克，精盐2克，熟猪油400克（约耗75克），干湿淀粉各5克，麻油5克，鸡蛋1个，鲜汤50克。');
INSERT INTO `recipe` VALUES (1427, 73, '锅煎鳜鱼', '将净鳃鱼肉洗净，批成四厘米长片，放入碗内加葱、椒盐、绍酒、味精略腌，再放入鸡蛋黄拌匀。网油洗净沥干，平铺砧板上将边修齐，拍上干淀粉，将鱼片均匀地逐片铺在网油中心，再四面对折成18厘米见方的网油鱼块。炒锅上火，放油烧至四成热（约100度）时，将网油鱼块投入锅内，煎至两面呈金黄色离火，撇去汆油，再上火略煎，倒入漏勺沥油。将煎好的鱼块放砧板上改成菱形块装盘，盘的两头放花椒盐、番茄酱蘸食。', '色泽金黄，外酥里香，鱼肉鲜嫩，佐酒最佳。', NULL, NULL, '鲜鳜鱼肉400克、猪网油150克、生鸡蛋黄25克、葱15克、椒盐5克、绍酒25克、味精105克、番茄酱15克、干淀粉20克、菜油75克。');
INSERT INTO `recipe` VALUES (1428, 73, '锅烧野鸭', '1.将野鸭沿着脊背剖开，洗干净，放在开水锅内过水后，取出放入砂锅中，加入味精、葱、料酒、酱油、盐、糖、大米、桂皮和适量水，用小火焖烂后，放于大盘中，拆骨摊平，头留下，用鸡蛋、湿淀粉一起调糊，倒在野鸭肉上。 \r\n2.将炒勺烧热，放入熟大油，油热后，把野鸭放进勺中两面均煎黄。再加入大油将鸭炸至金黄色时捞出，将油沥去，切成宽约2厘米、长约5厘米的长条，再按整鸭形状摆盘，鸭头一劈两半。上桌时与椒盐、甜面酱各一碟同上即可。', '色美金黄，香脆可口。', NULL, NULL, '净肥野鸭2只(约1.5公斤)，鸡蛋4个。熟大油800克(实耗约60克)，盐、味精各5克，酱油40克，葱、姜各10克，料酒70克，大料2棵，桂皮1小块，湿淀粉35克，白糖10克。');
INSERT INTO `recipe` VALUES (1429, 73, '海底松银肺', '海蜇头撕去衣膜，洗净，入沸水中烫泡10分钟，使其酥软，捞出入清水中漂洗干净。将火腿改刀成马牙块，加入绍酒，上笼蒸酥烂。猪肺用清水灌拍，抽去肺内气管筋络（应保持完整），入开水锅中氽水洗净，装入砂锅，放葱姜、绍酒、鸡汤，用小火炖3-4小时，使其酥烂。将海蜇头下沸水中略烫，再用沸鸡汤套过一次，捞出连同火腿一起放入砂锅内，炖至沸，加精盐、味精略焖即成。', '蜇头形似松枝，猪肺色白如银，酥烂如豆腐，入口即化。', NULL, NULL, '海蜇头150克、猪肺750克、火腿35克、绍酒40克、葱25克、姜25克、盐12克、味精3克、鸡汤150克。');
INSERT INTO `recipe` VALUES (1430, 73, '荷包鲫鱼', '1.鲫鱼去鳞、去鳃，从背脊上剖开，取出内脏，洗干净。 \r\n2.猪五花肉斩成肉末，放入碗中，加盐、味精，葱姜汁拌和成馅，塞入鱼腹中。在鱼身上略剞几刀，然后抹上酱油稍腌。\r\n3.炒锅上火，下猪油50克，烧至七八成热，将鲫鱼放入锅内，两面煎至发黄时，下葱结、姜片，煎出香味，再下绍酒、酱油、白糖、盐、清水250克，用旺火烧开，盖上锅盖，改用小火焖烧20分钟左右，再用旺火将卤汁收浓，用湿淀粉少许勾芡，浇上熟猪油25克，撒上葱段，起锅装盘即成。', '色泽深红，鱼肉鲜嫩，非常入味。', NULL, NULL, '大活鲫鱼1条（重约350克），净猪五花肉200克，绍酒25克，酱油35克，白糖20克，精盐2克，味精1克，熟猪油75克，葱段2克，姜2片，葱结1只，葱姜汁（由葱花、姜未、清水调成）15克，湿淀粉10克。');
INSERT INTO `recipe` VALUES (1431, 73, '荷花集锦炖', '鱼肚用温水泡软，洗净挤干，放入锅中加鸡汤、绍酒、葱姜烧沸，捞出放入品锅中。鸡肫剞菊花纹，猪腰剞麦穗花纹，鳜鱼肉批成片，冬菇批成片，连同虾仁分别放入沸鸡汤锅中汆熟，捞出。将火腿、冬笋及虾茸蛋卷、鸡脯肉均切成片，排齐成刀面在鱼肚上摆成“十”字形，四角空隙处摆上鸡肫、猪腰、鳜鱼片及冬菇。葱汁姜水加绍酒调和后，分别洒在肫花、猪腰、鱼片上，菜心剖开，头朝里摆放在四角的衬料上。熟鸡蛋去壳，用小刀在鸡蛋的小头处雕刻成花瓣形，火腿、冬笋均切成花瓣形的片，分别插入蛋黄中，中间缀以虾仁，撤上葱末成荷花蛋，用一个荷花蛋摆放在菜', '造型美观，原汁原味，鲜美可口，为民间筵席的上等肴馔。', NULL, NULL, '熟鸡脯肉150克、熟虾茸蛋卷100克、熟火腿25克、熟冬笋25克、净鳜鱼肉50克、鸡脓50克、猪腰50克、油发鱼肚50克、浆虾仁50克、水发冬菇25克。熟鸡蛋150克、青菜心30克。葱30克，姜25克、绍酒40克、熟鸡油15克、盐12克、味精4克、鸡汤150克。');
INSERT INTO `recipe` VALUES (1432, 73, '荷花铁雀', '铁雀捋去毛，从尾剖开一小口，挤出内脏，再从脊背部开剔去胸骨，拍断脊骨、腿骨，斩去嘴尖，挖出双眼，洗净。将治净后的铁雀放入钵内，加绍酒、精盐、葱姜汁拌匀腌渍10分钟，取出，拍上糯米粉。锅置火上，舀入花生油烧至七成热（约175度）时，放入铁雀炸透捞出，稍凉，再将铁雀重油炸至酥脆，捞入盘中。原锅上火，放油，投入蒜末炸香，加入醋、白糖。清水熬稠，淋芝麻油，起锅浇在铁雀上即成。', '色泽金黄，骨酥肉香，风味独特。', NULL, NULL, '铁雀450克、糯米粉25克、绍酒20克、精盐2.5克、葱姜汁15克、蒜末7.5克、醋25克、白糖50克、芝麻油15克、花生油750克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1433, 73, '红烧沙光鱼', '将沙光鱼治净，沥干水分，用绍酒、酱油、葱姜腌渍5分钟。锅置火上烧热，放油烧至六成热（约150度）时，投入沙光鱼煎至两面金黄，倒出。原锅上火，放油，投入葱段、姜片炸香，放入沙光鱼，烹入绍酒略焖，加入酱油、白糖、精盐和清水，加盖焖烧10分钟，用旺火收稠汤汁，烹入香醋，淋芝麻油即成。', '色泽酱红，肉质鲜嫩，口味咸中带酸，味美爽。', NULL, NULL, '沙光鱼一尾（重约1000克）。葱段25克、姜片20克、绍酒35克、酱油15克、白糖25克、精盐7.5克、香醋15克、芝麻油15克、花生油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1434, 73, '红松鳜鱼', '鳜鱼治净，切下鱼头，然后用刀沿脊骨两侧平片至鱼尾、斩去脊骨，剔去胸刺，再在鱼肉面直剞石榴米花。将蛋清放碗中，加绍酒、精盐、味精、干淀粉，调成蛋清糊。将猪腿肉斩成米粒大小的肉末。炒锅置火上烧热，放油烧至五成热（约125度）时，将鱼肉抹匀蛋清糊，鱼皮朝里翻卷，放在漏匀内入油锅，至鱼断生取出，平放在盘中。锅仍置火上，舀入鸡汤，加绍酒、精盐、味精烧沸，用水淀粉勾芡，淋油起锅烧在鱼肉上。再将锅置中火烧热，放油，投入肉末炒散，加绍酒、番茄酱略煸，加入鸡清汤、精盐、白糖，烧沸后勾芡，起锅围放在鱼肉四周，缀以香菜叶即成。', '鱼肉色白咸鲜，猪肉末色红质松，一菜双味。', NULL, NULL, '鲜活鳜鱼一条（重约750克）、猪腿肉50克、鸡蛋清25克、香菜叶5克、绍酒25克、精盐2克、味精1.5克、白糖10克、鸡汤50克、番茄酱1.5克、干淀粉15克、水淀粉25克、花生油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1435, 73, '葫芦虾蟹', '将大虾去壳留尾剥成“凤尾虾”连同虾仁用精盐，干淀粉，鸡蛋清上浆，将锅置火上，舀入少许熟猪油烧热，投入姜末略炸，放入蟹肉加绍酒，鸡清汤，精盐煸烩入味，用水淀粉勾芡起锅晾凉，取蟹黄放入碗中另用，撒上胡椒粉，葱末，加虾仁拌匀成馅，用葱椒盐，鸡蛋调成葱椒盐浆，再用鸡蛋，干淀粉调成全蛋浆；将猪网油两面逐张拍上干淀粉，铺上在案板上抹上葱椒盐蛋浆，在每张网油的一角安上凤尾虾，并将馅心分成十等份，放在网油上包成圆锥型，外抹全蛋浆，沾上面包屑，在中间用绿笋丝扎成葫芦虾蟹坯；将锅置火上舀入花生油，烧至六成热时，投入葫芦虾蟹生', '形态逼真，色泽金黄。', NULL, NULL, '虾仁，熟蟹肉，净猪网油，鲜大虾，面包屑，鸡蛋，龙虾片，香菜，水发绿笋丝。');
INSERT INTO `recipe` VALUES (1436, 73, '还丝汤', '将鳝鱼洗净，去头、尾和脊骨，切成七厘米的段。锅置旺火上烧热，舀入豆油，烧至八成热（约200度），投入鳝段，炸2分钟捞出。原锅上火，倒入鳝段，舀入猪肉汤，放入其它配料，加入绍酒、葱结、姜片烧沸、撇去浮沫。移小火炖10分钟，至汤呈乳白色时，再移旺火上拣去葱姜，加入精盐。味精，起锅装入大汤碗中，撒上白胡椒粉即成。', '汤色乳白，清香味浓，口味鲜咸，鲜肉酥软，为夏令应时佳肴。', NULL, NULL, '活鳝鱼350克。虾仁50克、熟火腿20克、猪腰片50克、水发冬菇20克，水发虾米10克、熟笋片30克、虾子5克。猪肉汤750克、绍酒15克、葱15克、姜7.5克、盐5克、味精3克、白胡椒粉1.5克。');
INSERT INTO `recipe` VALUES (1437, 65, '炒鸡什件', '1.把鸡胗两面白色的皮去掉，切成片，肝片成片。随之将鸡胗、肝用料油、酱油和盐腌好，浆些湿淀粉，再拌些油。木耳用水发透择洗干净，青菜叶切段，茎切成片。葱、姜、蒜均切片。\r\n2.用酱油、料酒、味精、湿淀粉和葱姜蒜、汤在一起兑成汁。\r\n3.用炒勺将油烧热后，随下胗、肝翻炒，至将要熟时，把木耳、青菜倒入再继续翻炒，并将对好的汁倾入勺中，并翻炒均匀，滴几滴醋，起锅将菜盛入盘中即成。', '特点味鲜醇香，胗肝脆嫩。', NULL, NULL, '鸡胗肝净350克，干木耳10克，青菜100克，大油80克，姜20克，葱25克，蒜20克，湿淀粉30克，料酒25克，酱油20克，盐5克，味精5克，醋3克，汤适量。');
INSERT INTO `recipe` VALUES (1438, 73, '黄焖着甲', '将着甲肉放入锅中，加清水烧沸，移小火焖至半熟捞出除去沙粒及甲骨，切成长条块洗净沥去水，取砂锅放入竹箅垫底，将着甲肉整齐放在上面。将肥膘肉切成薄片，山药切成滚刀块，明骨洗净，放入砂锅内加绍酒和清水，加盖上火焐4小时取出，剔去硬骨，切成小块。锅置火上，加入菜油，投放葱白、姜片煸香，倒在着甲肉上，随后将着甲肉锅置火上，加绍酒，盖上锅盖烧透，加精盐、酱油、冰糖、蒜头及猪肉汤烧沸，移小火烧2小时左右，拣去葱姜。炒锅上火放油，放入葱白煸香，将山药、冬菇，冬笋及明骨放入，加绍酒焖透后将着甲连汤一起加入，烧至汤稠着芡，淋', '香醇鲜嫩，卤汁明亮，甜咸适中。并有温中补虚，固肾益精之功效。', NULL, NULL, '着甲肉450克、明骨75克、猪肥膘50克、熟冬笋25克、冬菇6克、熟山药35克、蒜头15克。绍酒40克、葱25克、姜25克、酱油25克、盐7.5克、冰糖5克、淀粉25克、麻油15克。');
INSERT INTO `recipe` VALUES (1439, 73, '火腿酥腰', '1.将猪腰洗净，逐只剖开，下清水锅内烧开，取出洗净，再入锅加清水、葱、姜、绍酒，烧滚后移小火上烧30分钟取出。将萝卜（或冬瓜）切削成若干小球状，经开水焯后取出，用清水洗净，去除萝卜辣味。\r\n2.火腿切成薄片，放在汤碗中，猪腰去臊切成斜片，放在火腿两边，萝卜球放在上面，加葱结、绍酒、少量鸡汤（或鲜汤），上笼蒸上小时，取出倒去汤汁，将火腿与腰子反扣在盆中。\r\n3.炒锅上火，下猪油25克烧热，放葱结、姜片稍煸，另加鸡汤150克，放盐和味精，烧滚后用湿淀粉少许勾薄芡，淋入少量熟猪油，出锅浇在火腿上即成。', '火腿咸鲜，腰子酥烂，汤味鲜美。尤宜老年人食用。', '腰子要洗净，去除异味，蒸酥。要将火腿与腰子合蒸的原汁滗出，因火腿味咸，腰子有臊味，故其汁须弃，要重新用鸡汤烹制，这样即可保证成菜鲜味浓纯而无异味。', NULL, '熟火腿200克，猪腰3只（重约300克），萝卜（夏天用冬瓜）100克，葱、姜、湿淀粉各10克，味精、精盐各1克，绍酒15克，熟猪油35克，鸡汤（或鲜汤）300克。');
INSERT INTO `recipe` VALUES (1440, 73, '鸡茸干贝', '1.将干贝老肉剥去洗净盛碗，加入料酒、葱、姜和适量的水，上屉蒸烂后捣碎。将鸡胸脯肉去筋皮，剁成泥放入碗中，加料酒、鸡蛋清、湿淀粉、盐、味精、适量水搅匀，调成鸡茸。 \r\n2.将炒勺烧热，放入鸡汤、料酒、盐、味精、干贝和蒸干贝的汤汁，烧开后用湿淀粉勾芡，然后把调成的鸡茸倒入勺内搅匀，待鸡茸见稠时放入大油搅匀后，盛入碗中即成。', '色泽乳白，鲜嫩味美。', NULL, NULL, '大干贝150克，鸡胸脯肉150克，鸡蛋清100克，熟大油150克，料酒25克，味精5克，盐3克，湿淀粉50克，鸡汤800克。');
INSERT INTO `recipe` VALUES (1441, 73, '将军过桥', '将黑鱼洗净，斩下鱼头，从脊背剖开，去除内脏，鱼肠留用，取下鱼肉，片成0.6厘米厚的片，放入碗内加精盐。绍酒、蛋清、水淀粉拌匀上浆。将鱼肠放入清水中，用剪刀刺破，去净污物，用精盐抓去粘液，洗净。再将鱼骨斩成块，鱼头劈成两片。菜心洗净，头部修成橄榄形。将炒锅置旺火上烧热，舀入熟猪油烧至四成热（约88度）时，放入鱼片划至乳白色，倒出沥油。原锅上火，放油，投入葱白段、笋片，冬菇片煽炒，加绍酒、精盐、白糖、鸡清汤少许、味精烧沸，着芡，倒入鱼片，淋芝麻油，翻锅盛入盘中（盘中先放醋），即成炒菜。将鱼头、骨、肠一起入沸水', '一鱼两吃，有菜有汤，鱼片洁白、滑嫩，鱼汤浓白香醇，可谓经济实惠又美味可口的大众化佳馔。', NULL, NULL, '黑鱼500克、熟冬笋20克、青菜心75克、水发冬菇15克、熟火腿10克、蛋清20克、虾子5克、盐5克、绍酒25克、蛋清25克、淀粉20克、熟猪油750克（实耗油150克）、葱10克、白糖5克、味精2.5克、芝麻油7.5克、醋5克、鸡清汤25克、姜5克。');
INSERT INTO `recipe` VALUES (1442, 73, '酱方', '五花肉刮洗干净，修成正方形，用竹签在精肉一面戳些小孔，再用盐擦透放入钵中腌制一日。将腌制的方块肉取出放入锅中出水，洗净后将肉皮朝下放入垫有竹箅的砂锅内，倒入原汤，加入酱油、绍酒、少量冰糖屑、葱、姜，用盆将肉压紧，加盖用旺火烧沸后，改用小火焖至酥烂。然后将肉取出，皮朝下扣入碗内，再放少许冰糖屑，食时先上笼略蒸，取出扣入盘中，再把原汁倒入锅中烧沸着芡，淋浇盘内，另将绿叶菜加盐炒熟饰盘边即成。', '口味咸中带甜，食而不腻，入口即化，富有苏帮风味。', NULL, NULL, '猪五花肉750克、绿叶菜500克。酱油20克、绍酒25克、冰糖屑10克、盐10克、大葱15克、姜15克、水淀粉25克、味精12克。');
INSERT INTO `recipe` VALUES (1443, 73, '酱汁排骨', '将腌好的排骨下水锅出水，洗净。锅内放入竹箅垫底，将排骨整齐放入，加绍酒、八角、桂皮、葱姜、清水，用旺火烧沸后，再加入白糖、酱油盖好锅盖，用中火烧至汁稠。食时改刀装盘，浇上原汁即成。', '色泽酱红，肉质酥烂，芳香扑鼻，咸中带甜，既是宴席上的美味，也是馈赠亲友的佳品。', NULL, NULL, '猪肋排骨肉1000克，酱油30克，白糖50克，绍酒25克，味精10克，精盐10克，八角5克，桂皮5克，葱姜各10克。');
INSERT INTO `recipe` VALUES (1444, 73, '酱汁肉', '将猪肋条肉、蹄膀、猪爪分别洗刮干净，将肋条肉切成100克重的方块，连同蹄膀、猪爪放入大锅内出水至断血，取出后洗净。将原汤浮沫撇去，加入精盐，放入竹箅垫底，先将猪爪、蹄膀放在下面，把肉块皮朝上排放在上面，加入绍酒、葱姜、八角、盖上锅盖，用中火烧约1小时，然后加入糖色、红曲粉，用小火焖至酥烂，加冰糖、白糖。待卤汁浓稠时，锅离火，取出酱汁肉（蹄、爪另用），皮朝上放在大瓷盘中，食时切块浇上卤汁即成。', '色泽酱红光亮，肉酥烂肥醇，入口先甜后咸，香味浓郁，风味独特。', NULL, NULL, '猪肋条肉1000克、猪蹄膀500克、猪爪500克、冰糖15克、白糖15克、糖色10克、红曲粉5克、八角5克、绍酒25克、精盐10克、味精12克、酱油20克、葱姜各15克。');
INSERT INTO `recipe` VALUES (1445, 73, '椒雪肉片', '1.将里脊肉切成厚0.5厘米、长5厘米的小薄片，用刀脊把肉片轻轻捶拍一遍。之后将肉片放入用料酒、葱姜汁、盐、糖、葱椒调成的卤汁中腌30分钟入味，再放在面粉中滚一下。 \r\n2.用蛋清和湿淀粉沾满芝麻放入热油炒勺中炸脆，装入盆中。 \r\n3.再用烧至8成热的油将洗净切成粗条的雪菜叶炸脆，围在盘内里脊肉的周围即成。', '色美金黄，外脆里嫩。', NULL, NULL, '猪里脊肉400克，芝麻100克，雪菜叶50克，鸡蛋清60克，熟大油800克(实耗约80克)，白糖15克，葱姜汁10克，盐4克，料酒5克，味精5克，湿淀粉30克，葱椒1克，面粉50克。');
INSERT INTO `recipe` VALUES (1446, 73, '金陵烤鸭', '将鸭宰杀洗净，在右翅下开一小口取出内脏，洗净沥干。把青菜叶、花椒填入鸭腹内。葱叶塞入鸭子肛门，使肚膛饱满。将烤叉从鸭腿下裆插入，穿过胸骨，至肩骨颈皮穿口3.5厘米处戳通，将鸭头由左向右方向弯转，从眼睛下面戳通，卡住叉尖。水锅上火烧沸，将鸭头先入水内，舀沸水浇烫鸭身，使鸭皮收缩绷紧，出现毛孔，然后用饴糖抹匀鸭身，放在通风处吹干。烤池放入木炭，点火烧燃后执鸭叉烤。先烤两侧，再烤腿、尾部、背部，最后烤胸部。待鸭皮吱吱作响，欲冒油花时，再以麻油涂之，并烤至金红色时离火退叉，片下鸭肉装盘，嵌上头尾，配以薄饼、甜酱、', '鸭皮金红、香脆酥松，油润光亮，肉嫩鲜香，食之满口留香。堪为色、香、味三绝。', NULL, NULL, '南郊麻鸭一只（重约2000克）、青菜叶150克、花椒5克、葱30克、饴糖25克、麻油15克。');
INSERT INTO `recipe` VALUES (1447, 73, '金陵圆子', '将猪肉细切，初斩成米粒状，掺入斩碎的虾米，加调料搅拌均匀，制成大小一致的扁形肉圆10只，用水淀粉在肉圆上抹匀，下油锅煎至两面发黄时取出。取砂锅一个，肋排用沸水略烫洗净，放入砂锅中，水发蹄筋切成两段放在肋排上，舀入猪肉汤，加葱、姜、绍酒，置于火上，将肉圆铺在蹄筋上面，再加精盐、味精，盖上青菜叶。加锅盖烧沸，移至微火焖2小时，然后揭去青菜叶，撇去浮油即成。', '肉圆酥嫩鲜香，蹄筋软糯醇美，汤汁稠浓味厚。', NULL, NULL, '肥四瘦六的优质猪肉500克。水发蹄筋250克、肋排500克、虾米50克、精盐15克、味精50克、绍酒100克、青菜叶250克。');
INSERT INTO `recipe` VALUES (1448, 73, '金腿脊梅炖腰酥', '将熟火腿及里脊肉切成厚块，猪腰撕去外皮，在两面直划三、四刀，萝卜削成核桃大小的圆球。将里脊肉、猪腰放入沸水锅内烫去血污，捞出洗净，连同火腿及葱姜放入炒锅，加满清水，置旺火烧沸后，移至小火炖约2小时，将猪腰取出横切成片，再放入砂锅中。将萝卜球放沸水中烫一下，捞起放入砂锅，加精盐、味精、绍酒，炖至萝卜球熟即成。', '汤汁清澄、原汁原味、猪腿香、猪肉烂、猪腰酥、味道醇浓鲜美。', NULL, NULL, '金华火腿75克、猪里脊肉250克、猪腰500克、萝卜250克、精盐25克、味精15克、绍酒75克、葱姜各15克。');
INSERT INTO `recipe` VALUES (1449, 73, '京葱炆牛方', '将牛脯肉两面剞上刀纹，用酱油涂沫，锅置旺火，放入花生油，放入牛脯煎至两面呈金黄色时，放入笋片、京葱段、姜片、丁香、酱油、绍酒及白糖，加清水，盖上锅盖，烧沸后移到小火上焖3个小时至牛肉酥烂，待卤汁收稠时，拣去丁香，加味精，淋入芝麻油，取锅装盘即成。', '卤汁稠粘入味，牛肉鲜香酥烂，尤以秋冬季食用为佳。', NULL, NULL, '小牛脯肉750克、冬笋片150克、精盐15克、京葱25克、花生油75克、酱油25克、丁香7.5克、绍酒50克、味精12克、芝麻油5克、姜片10克、白糖25克、花生油250克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1450, 73, '镜箱豆腐', '将猪肉斩成茸，加绍酒、葱姜汁、精盐拌和上劲。豆腐切成长5厘米、宽3厘米、厚3厘米的长方块，下八成热（约200度）的油锅中，炸至豆腐外表起软壳、色呈金黄色时，捞出沥油，待冷却后在每块豆腐中间挖去嫩豆腐，然后装入肉馅，再在肉馅上面横嵌一只大虾仁。炒锅上火烧热，舀入豆油，放入葱末煸香，再放入冬菇、笋片略煸，将镜箱豆腐肉面朝下排入锅中，加入绍酒、酱油、白糖、番茄酱、猪肉汤、精盐，烧沸后加盖，移小火上烧6分钟，再置旺火上加味精，用水淀粉勾芡，淋入熟猪油和芝麻油，将虾仁朝上装入盘中即成。', '色泽桔红光亮，豆腐细腻味醇，肉馅鲜嫩，荤素兼优，老少皆宜。', NULL, NULL, '小箱豆腐350克、瘦猪肉150克、大虾仁50克、冬笋25克、水发冬菇15克、绍酒25克、葱15克、姜10克、盐7.5克、酱油15克、白糖5克、番茄酱10克、猪肉汤100克、淀粉15克、熟猪油15克、芝麻油15克、豆油450克（实耗油75克）。');
INSERT INTO `recipe` VALUES (1451, 73, '酒酿金腿', '将熟火腿去皮并修净油头，切成薄片。取10片火腿；逐片卷入酒酿使其呈喇叭形，置小盘中。将火腿片整齐排在碗内，加白糖、绍酒，并以酒酿垫底，用保鲜纸封口，与火腿卷同时上笼蒸20分钟。将蒸好的火腿片反扣在盘中，原汁入锅烧热勾芡，用火腿卷围边，浇上原汁即成。', '香甜不腻，甜中趋咸，如配以鲜莲同蒸更妙。', NULL, NULL, '金华熟火腿中峰一块750克、酒酿250克，白糖50克、绍酒75克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (1452, 73, '酒蒸全鸡汤', '1.将活母鸡宰后煺毛，开膛取出内脏，剁去鸡翅，再从背部剖开，用开水氽烫将血污除净。 \r\n2.使鸡双腿朝天，腹部向上，放入汤斗内，加入花雕酒、葱、姜、味精、盐和适量清水，上屉蒸烂后取出，去掉葱、姜即成。', '色泽银白，味香汤清。', NULL, NULL, '活母鸡1只，花雕酒150克，葱、姜各15克，味精5克，盐20克。');
INSERT INTO `recipe` VALUES (1453, 73, '菊花青鱼', '1.将鱼皮朝下，用刀斜片至鱼皮，每片4刀切断鱼皮，共切成10块，再将鱼块直割到鱼皮（刀距约0.7厘米，不能破皮），粘上干淀粉，抖去余粉，成菊花鱼生坯。将醋、白糖、精盐、番茄酱、猪肉汤、水淀粉一起放入碗中，搅和成调味料。 \r\n2.将锅置旺火上烧热，舀入熟猪油，烧至七成热时，将菊花鱼生坯抖散，皮朝下放入油锅内，炸至黄色时，捞出装盘。另取炒锅置旺火上烧热，舀入熟猪油25克，投入葱、蒜末炒香后，倒入调味汁搅匀，在淋入熟猪油15克、芝麻油拌和，浇在菊花鱼上即成。', '色泽逼真，色泽枣红，酸甜适口，佐酒极佳。', NULL, NULL, '带皮青鱼肉1段（重约350克），精盐6克，白糖150克，番茄酱100克，香醋7.5克，葱末10克，蒜末10克，干淀粉80克，猪肉汤75克，水淀粉40克，芝麻油10克，熟猪油1500克（约耗200克）。');
INSERT INTO `recipe` VALUES (1454, 73, '烤炖鸭子', '将烧鸭斩下鸭头、鸭颈，放入大砂锅中垫底，上放鸭子（脯朝上），再将火腿、香菇、笋片整齐地排放在鸭胸脯上，加绍酒、精盐、味精、葱结、姜片、猪肉汤，盖上锅盖，上笼用旺火蒸约2小时至酥烂，取出，拣出姜、葱即成。', '酥烂醇厚，鲜香肥嫩，味美可口，老少皆宜。', NULL, NULL, '烧鸭一只（重约1500克）、熟火腿片25克、熟冬笋片30克、水发冬菇15克、绍酒30克、精盐7.5克、味精25克、葱结1个、姜片25克、猪肉汤200克。');
INSERT INTO `recipe` VALUES (1455, 73, '葵花圆子', '1.将猪肉切成米粒大小的末，再粗斩几刀，将松子仁5克剁碎同肉末一起放碗中。磕入鸡蛋1个，加绍酒10克、葱姜末各2克、精盐1.5克、味精1克、湿淀粉5克，搅拌均匀，做成直径约18厘米、中间厚3.5厘米、边厚1.5厘米的葵花圆子生坯。\r\n2.取大圆盘一只倒入花生油20克。将鸡蛋黄80克倒入碗内搅匀，加面粉搅成蛋糊，用一半蛋糊均匀的涂在圆子上面。将其余的松子仁尖朝上，均匀的直插在肉圆上。\r\n3.炒锅上火，放熟煮油，烧至六成热，将肉圆放入，炸至两面起壳捞出，放入汤盘内，上笼蒸熟。炒锅上火，放花生油15克，烧热，倒', '色泽金黄，形似葵花，清香油润，酥软可口。', NULL, NULL, '净猪肉（肥三瘦七）500克，松子仁25克，鸡蛋黄80克，面粉75克，绿菜叶200克，鸡蛋1个，湿淀粉5克，精盐4.5克，味精2克，绍酒20克，葱、姜末各2克，绵白糖6克，熟煮油750克（约耗75克），花生油35克，鸡清汤200克。');
INSERT INTO `recipe` VALUES (1456, 73, '梁溪脆鳝', '锅内清水烧沸，加盐、香醋、葱姜，投入活长鱼，速加盖，煮至鱼嘴张开，捞入清水中漂清。鱼腹朝里放案板上，左手捏住鱼头，右手持竹片从长鱼下巴处插入，沿脊骨直划至尾，再沿脊骨两侧划下整条鳝肉，洗净沥水，锅置旺火烧热，舀入豆油烧至七成热（约175度）时，放入鳝鱼肉炸3分钟捞出，待油温升至八成热（约200度）时，再放入鱼肉复炸4分钟，炸至松脆捞出。另用锅放少许油，加入葱姜末煸香，加绍酒，酱油、白糖、烧沸成卤汁，放入炸脆的鳝鱼肉颠翻，淋芝麻油，起锅装盘，撒上姜丝即成。', '鳝肉松脆香酥，呈酱褐色，卤汁甜中带咸，佐酒佳肴。', NULL, NULL, '活大鳝鱼750克、盐2克、醋5克、葱25克、姜15克、绍酒15克、酱油15克、白糖3克、芝麻油10克、豆油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1457, 73, '金钱肉', '1.从里脊肉顶头切成小块，每块重约10克，用刀逐块拍成小薄圆片，直径约5厘米。把切好的肉片逐片放入用料酒、盐、味精和葱、姜末调成的卤汁内拖一下，取出腌5分钟。 \r\n2.将腌好入味的小肉片，再放入用鸡蛋清加湿淀粉调成的蛋糊中，再拖一拖放在盘中。 \r\n3.炒勺上火，放大油，把里脊煎成金黄色，烹入辣酱油，翻炒均匀即成。', '色泽黄白，味鲜脆嫩。', NULL, NULL, '猪里脊肉500克，鸡蛋清120克，熟大油50克，料酒50克，盐3克，味精5克，湿淀粉30克，辣酱油10克，葱姜末各8克。');
INSERT INTO `recipe` VALUES (1458, 73, '龙凤腿', '将鸡丝、虾仁同放碗内，加鸡蛋、白糖、辣酱油、精盐、绍酒，味精、葱末和胡椒粉拌和待用。猪网油漂洗干净，切成10厘米见方的块，将鸡丝、虾仁分别放在网油上，鸡腿骨摆放在网油的一端包成鸡腿形。鸡蛋加干淀粉调和。油锅上火，烧至七成热（约175度）时，将龙凤腿坯逐个沾满干淀粉，再拖蛋糊，滚上面包粉，下入油锅炸至淡黄色，将油锅移至小火上炸1分钟至熟，用漏勺捞出，整齐摆入盘中即成。', '造形美观，色泽褐黄，外松脆、里鲜嫩，口味香咸。', NULL, NULL, '鸡丝450克、虾仁100克、猪肉油150克、鸡腿骨10克、鸡蛋50克、白糖10克、辣酱油7.5克、盐7.5克、绍酒20克、味精2克、葱20克、胡椒粉3克、淀粉20克、花生油1250克（实耗油150克）。');
INSERT INTO `recipe` VALUES (1459, 73, '龙肝凤脑', '将鱼肝洗净放入水锅中，加葱、姜、绍酒出水。取出漂入清水中。将鸡头煮熟劈开，取出鸡脑。将火腿切成柳叶片，菜心洗净，削成橄榄形。锅置火上烧热，放油滑锅，加入高汤、鱼肝、鸡脑、火腿，用旺火烧沸后移小火略炆。另取锅上火放油烧热，下菜心划油至翠绿色时，倒出沥油，将菜心放入鱼肝锅中，并移至旺火加入精盐、味精推匀，用水淀粉勾芡，淋入熟鸡油，撒上胡椒粉。', '鱼肝鲜绵，鸡脑软糯，汤汁鲜香，别具风味。', NULL, NULL, '鲤鱼肝200克、鸡脑150克、熟火腿15克、菜心50克、葱15克、姜10克、绍酒20克、高汤250克、淀粉15克、熟鸡油10克、胡椒粉1克、精盐2克、味精1.5克。');
INSERT INTO `recipe` VALUES (1460, 73, '麻花野鸡', '将野鸡脯肉、熟肥膘肉分别批成长7厘米、宽3厘米的薄片，雪菜叶漂洗后切成同样大小的片。虾仁斩成茸，加入蛋清、干淀粉、精盐、绍酒搅匀成糊。将肥膘肉片两面拍上干淀粉，放入盘中，将虾糊涂在肥膘上，再盖上鸭脯片，在鸭脯片上抹一层鸡蛋清，贴上雪菜叶。然后在中间划3刀（每刀约3.5厘米长的口子），两头相连，用手捏住一头，将另一头从中间刀口中反穿过来，拉成麻花形，再逐个抹上鸡蛋清，将蛋皮、冬菇、青菜末撒在麻花野鸭上。锅置火上烧热，放入油烧至四成热（约100度）时，锅离火，将麻花野鸭逐一放入油中；至鸭肉色呈乳白，倒出沥油。', '形如麻花，柔软鲜嫩，底壳香脆。', NULL, NULL, '野鸡脯肉450克、熟猪肥膘45克、虾仁50克、咸雪菜叶15克、蛋皮10克、水发冬菇10克、青菜叶10克、蛋清25克、花生油750克、干淀粉15克、精盐7.5克、绍酒30克、味精2克。');
INSERT INTO `recipe` VALUES (1461, 73, '美人肝', '将鸭胰白放入沸水锅内烫约十分钟后取出，入冷水中冷却，撕去臊筋，放入盘内，将鸡脯丝也放入鸭胰盘内，用鸡蛋清、湿淀粉拌匀，水发冬菇，冬笋切成薄片，鸭油烧至四成热，将鸭胰白，鸡脯丝，冬笋丝，冬菇丝放入，用手勺推动，见油起沫时起锅，倒入漏勺沥油，炒锅置火上，舀入鸡清汤，加精盐，料酒，味精，用湿淀粉勾芡，将鸭胰白，鸡脯，冬菇，冬笋倒入，颠翻炒锅，淋上鸡油即成。', '色泽乳白，光润鲜嫩，味美爽口。', NULL, NULL, '鸭胰，鸡脯丝，鸡蛋清，湿淀粉，水发冬菇，冬笋，鸡汤，精盐，料酒，味精，鸡油。');
INSERT INTO `recipe` VALUES (1462, 73, '美炸眉卷', '1.把猪里脊肉剁成茸，鸡肝切成碎末混合一起，加入葱、姜汁，湿淀粉、盐、味精、葱椒末、料酒、白糖、鸡蛋1个拌匀。然后用凉开水把网油洗净一分为二平摊在案板上，放少许糯米粉，将里脊茸抹平，卷成直径约3厘米、长6厘米的长卷。摆在盘中，上屉蒸10分钟后，取出待用。 \r\n2.用2个鸡蛋加糯米粉调成蛋糊，把里脊卷在蛋糊中拖一下，放在热油炒勺中炸酥。每卷切成斜角6段摆盘，撒上椒盐即成。', '色泽金黄，外脆里嫩，味香可口。', NULL, NULL, '猪里脊肉250克，母鸡肝100克，猪网油2张，鸡蛋3个，生大油800克(实耗约70克)，葱、姜汁60克，白糖2克，盐5克，糯米粉200克，料酒30克，椒盐3克，葱椒末3克，味精5克，湿淀粉25克。');
INSERT INTO `recipe` VALUES (1463, 73, '米花鸡丁', '粳米洗净上笼蒸熟，晾干待用。鸡脯肉切成细丁状，用绍酒、蛋清、精盐、水淀粉浆制。炒锅上火烧热，放熟猪油至四成热（约88度）时，放下鸡丁划油，至熟倒出沥油，炒锅再上火放熟猪油，放入鸡丁、牛奶、青豆、绍酒、精盐、味精略煸，勾芡淋熟猪油15克。在烹制鸡丁的同时，将晾干的粳米下花生油锅炸至松脆，装入盘中，将炒好的鸡丁装在米花上即成。', '外形美观，脆中有软、酥中有嫩，鲜美异常。', NULL, NULL, '净鸡胸肉300克、粳米50克、鲜牛奶100克、熟青豆25克。绍酒15克、蛋清15克、精盐5克、水淀粉20克、味精2克、熟猪油500克（实耗油50克）、花生油50克。');
INSERT INTO `recipe` VALUES (1464, 73, '蜜汁火方', '将火腿修切成大方块，皮朝下放砧板上，用刀剞成小方块，深度至肥膘一半，但要皮肉相连。皮朝下放入碗内，加清水上笼蒸2小时30分钟取出，换清汤，加冰糖再上笼蒸1小时取出，放入莲子再蒸30分钟，取出滗去卤汁，装入盘中。锅上火烧热，放清油烧至五成热（约125度），投入松子仁略炸至金黄色，取出待用。锅再置火上，倒入卤汁，加蜂蜜烧沸，用水淀粉勾芡，放入糖桂花搅和，浇在火方上面，再撒上松子仁即成。', '色呈枣红，蜜汁芬芳，味甜而咸香，火腿酥烂甘鲜，“其香隔户便至”。', NULL, NULL, '熟南腿中峰一块（约750克）、白糖莲子50克、松子仁50克、糖桂花5克、蜂蜜20克、冰糖15克、清油75克（实耗袖25克）、精盐15克、味精12克、水淀粉10克。');
INSERT INTO `recipe` VALUES (1465, 73, '母油船鸭', '将光鸭从背颈处划约7厘米的口，然后按整鸭出骨操作要求将其出骨。将糯米淘洗干净，同笋丁、香菇丁、鸭肫丁、猪肉丁、莲子、芡实、白果、栗子一起放入碗中，加酱油、精盐、绍酒拌和，从刀口处填入鸭腹内。用鸭颈皮塞住刀口，成八宝鸭，将八宝鸭、鸭骨架、猪骨、猪肥膘一起放入水锅中略煮后，捞出洗净，将八宝鸭腹朝下放入有竹箅垫底的砂锅中，两旁分别放入肥膘、猪骨、鸭骨，再放入葱姜、绍酒、酱油、白糖、精盐，倒入原汤，加盖烧沸后，移微火焖3小时至酥烂。揭开盖，去掉葱姜、骨头、肥膘、竹箅垫，将鸭翻身，放入笋片、香菇、青菜心，再放入经油', '鸭形完整，色棕黄有光，鸭皮肥嫩，鸭肉酥烂，汤浓味醇，馅心香鲜。具有滋阴补虚，益肾固精之功效。', NULL, NULL, '光嫩母鸭一只（重约2500克）、糯米100克、净莲子50克、白果肉50克、栗子肉30克、芡实米25克、带皮猪肥膘100克、猪骨头250克、笋丁30克、香菇丁15克、酱油50克、精盐2克、绍酒15克、白糖5克、葱25克、姜25克、芝麻油25克。');
INSERT INTO `recipe` VALUES (1466, 73, '南林香鸭', '1.将鸭子洗净，放入热水锅中，烫去血水，捞出洗净置盘中，用精盐（14克）、绍酒（45克）擦匀鸭身，加入葱末、姜片、花椒、丁香、八角、桂皮、草果、玉果，上笼蒸约2小时至酥烂，取出待用。 \r\n2.将鸭子平剖开，去骨后置盘中。把虾仁、猪肥膘剁成膘茸，加绍酒（5克）、精盐（1克）、味精搅匀，涂抹在鸭肉面上。另取盘1只，抹上花生油，将（皮朝上）放入，同时将酵种加清水调成糊状，加入花生油搅匀，涂在鸭肉面上。 \r\n3.将锅置中火上，舀入花生油，浇至七成热时，放入鸭炸至酵糊呈金黄色时离火，捞出切成长方块，排放在盘中，再将', '香味浓郁，松脆鲜酥。', NULL, NULL, '光肥嫩鸭1只（约重1500克），河虾仁100克，猪肥膘50克，味精1克，花椒5克，桂皮15克，草果10克，八角2克，丁香2克，玉果1克，蒜头末25克，葱末50克，姜片25克，花生油1500克（约耗150克），芝麻油50克。');
INSERT INTO `recipe` VALUES (1467, 73, '泥煨鸡', '1.将活鸡宰杀，煺尽毛，在翅膀腋下开一个长约3厘米的小口，挖去内脏，切除肛门，洗净晾干，再用刀背拍断鸡脚、翅膀、腿骨、颈骨，放入钵里，加酱油50克、绍酒25克、精盐5克、葱末和姜汁各少许，腌30分钟。\r\n2.将鸡肫肝切成片。炒锅上火，放猪油50克，烧至五成热，先下葱段、姜片炒香，再将肫片、虾仁、冬菜末、猪肉丁、火腿丁、香菇丁、大虾米等配料倒入炒和，加酒、酱油各25克，放味精1克、白糖适量，至配料刚熟时起锅倒入碗内。将配料从鸡翅下刀口放入腹内，然后将鸡头塞进开口处。再将葱切成细茸，拌上少许猪油，涂遍鸡身，并', '皮色黄亮，鸡肉酥嫩，上筷骨肉即离，香气扑鼻，原汁原味，滋味异常鲜美。', '必须用鲜活嫩草鸡烹制，忌用老母鸡与肉鸡。配料必须先加调味炒熟，再装入鸡腹内，并用网油、荷叶包好，再用酒坛黄泥裹严。重用火功煨制。要先后用旺火、中火和小火慢慢煨熟，其味才佳。', NULL, '活新嫩三黄（黄嘴、黄脚、黄毛）母鸡1只（重约1250克），虾仁、京冬菜、白糖各50克，猪肉丁150克，火腿丁、香菇丁、葱、姜各25克，鸡肫、绍酒、酱油各100克，大虾米16克，味精1克，盐200克，猪网油250克，鲜荷叶4张，熟猪油80克，芝麻油、芝麻酱、葱白段各少许。');
INSERT INTO `recipe` VALUES (1468, 73, '藕粉圆子', '将红绿丝、金桔、蜜枣、桃仁、松子仁碾碎，加上芝麻粉、绵白糖和糖油丁和成馅心，搓成弹子大的馅心待用。藕粉碾碎过筛放入小筐内。再放入馅心滚上一层藕粉，取出装入爪篱中入沸水锅中烫过，再放入小筐滚一层藕粉，依次反复多次，使之成为大的圆子。锅里加清水，下入圆子。煮熟捞入放有糖桂花的原汁汤中，即成。', '形似鸽蛋，色泽棕红，质地软糯，入口甜爽。', NULL, NULL, '藕粉400克、红绿丝5克、金桔5克、蜜枣15克、桃仁10克、松子仁5克、麻粉7.5克、糖油丁50克、绵白糖75克、桂花1克。');
INSERT INTO `recipe` VALUES (1469, 73, '沛公狗肉', '将狗和甲鱼分别宰杀洗净，狗肉切成小块，加酒，精盐，葱，姜腌渍二小时，甲鱼入沸水锅略焯洗净，炒锅烧热，放花生油烧热，先下葱姜煸香，再放狗肉和甲鱼块，略炒后加酒，酱油，精盐，白糖，八角，花椒加清水焖烧至半熟，倒入垫有竹箅的大砂锅内烧沸，转用小火炖至肉质酥烂，卤汁浓醇即成。', '色泽深红，香味浓郁，狗肉酥烂入味。', NULL, NULL, '狗肉，甲鱼，酒，精盐，葱，姜，酱油，白糖，八角，花椒，清水。');
INSERT INTO `recipe` VALUES (1470, 73, '彭城鱼丸', '将鲤鱼治净。斩下头尾，取净鱼肉，洗净后同猪肥膘一起斩成茸，放入钵内，加绍酒、葱姜汁、鸡蛋清、鸡清汤、精盐搅拌上劲后，加入斩碎的粉丝和干淀粉，调成糊状。将鱼头尾洗净，下水锅焯水后加精盐、绍酒、葱姜上笼蒸熟。将锅置火上，放入冷水，将鱼糊挤成大桂圆形状的丸子放入水锅中，待水沸时，鱼丸即熟，捞出。锅置火上放熟猪油，放入葱姜炸黄出香时捞出，加入鸡清汤，倒入鱼丸、菜心、火腿片、冬菇片，加入绍酒、精盐。味精，烧沸后勾芡，淋入芝麻油，装入长腰盘中，将蒸熟的鱼头尾饰在盘子两头即成。', '鱼丸清淡洁白，滑嫩爽口，尤宜老人食用。', NULL, NULL, '活鲤鱼一尾（重约1000克）、猪肥膘50克、水发粉丝100克、熟菜心25克、熟火腿片15克、鸡蛋清25克、水发冬菇10克、绍酒25克、葱15克、姜10克、盐3.5克、淀粉25克、味精2克、芝麻油15克、鸡清汤500克。');
INSERT INTO `recipe` VALUES (1471, 73, '青鱼甩水', '1.将青鱼尾段洗净，顺长切3至5刀，尾鳍相连。 \r\n2.炒锅置旺火烧热，放入熟猪油，投入葱段煸黄出香后捞出，放入鱼尾煎至两面发黄，烹入绍酒稍焖，再加入姜末、酱油、白糖及清水，盖上锅盖，烧沸后移小火焖8分钟，再转旺火收稠卤汁，淋入芝麻油，起锅将鱼尾整齐地装入盘中即成。', '色泽酱红，咸中带甜，肉质肥嫩。', NULL, NULL, '青鱼350克、熟猪油75克、葱15克、姜10克、绍酒25克、油20克、白糖15克、芝麻油7.5克。');
INSERT INTO `recipe` VALUES (1472, 73, '清炖鸡孚', '将猪肉剁成米粒状，放碗内，加调料拌匀。鸡腿去骨皮朝下，平摊在砧板上，用刀轻轻横竖排剁，拍一层干淀粉，将肉茸均匀地平铺在鸡肉上，仍用刀排剁几遍，使肉茸与鸡肉粘合，再将其切成3厘米宽的菱形状。将蛋清打成发蛋，加干淀粉拌匀成发蛋糊，再净鸡块放入滚蘸。将锅上火烧热放油，烧至五成热（约125度）时，将鸡块下油中炸约1分钟，待鸡块稍起软壳，呈白色时，用漏勺捞出沥油。然后将其放入砂锅内，加鸡清汤、火腿片、绍酒、精盐，加盖上旺火烧沸后，移微火上焖半小时至酥烂，放入冬菇，稍焖即成。', '汤汁清澈纯厚，入口酥烂，鲜咸可口。鸡香肉鲜质酥烂，清汤味醇色洁白。', NULL, NULL, '鸡腿肉二只（重约400克）、蛋清25克、净猪肉100克、熟火腿片25克、水发冬菇15克，干淀粉30克、绍酒25克、精盐7.5克、花生油750克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1473, 73, '清炖狼山鸡', '1.将鸡宰杀治净，氽水待用。\r\n2.火腿脚爪汆水，刮洗干净，把鸡、火腿、脚爪放入垫有竹箅的砂锅中，舀入清水，加入葱姜、盖上砂锅盖，先置旺火上烧沸，加绍酒，撇去浮沫，再移至微火炖约2小时，取去火腿、脚爪、葱姜，加入精盐，烧至微沸，离火即成。', '汤汁清澈见底，皮白有光，鲜味浓郁，微兼辣香，汤菜兼优。', NULL, NULL, '活新母鸡一只（重约1250克）、火腿脚爪25克、葱25克、姜25克、绍酒30克、精盐12克。');
INSERT INTO `recipe` VALUES (1474, 65, '成都蛋', '1. 将鸡蛋去壳，放入碗内用力调匀。木耳洗净。\r\n2. 汤锅置火上，放入猪油烧热，鸡蛋入锅，煎至两面微黄，当蛋质松软时，用手勺将鸡蛋捣散，加入汤，再下精盐、木耳、菜心、味精烧开，入味后淋上猪油即成。', '汁色奶白，味浓鲜香，蛋质酥软，木耳脆嫩，可作四季汤菜，酒饭均宜。', NULL, NULL, '鸡蛋4个，水发木耳50克，菜心100克，精盐4克，猪油75克，味精2克，浓白汤1000克。');
INSERT INTO `recipe` VALUES (1475, 73, '清炖硕鸭', '将鸭宰杀洗净。放入冷水锅中煮沸至断血，捞出洗净。将煮过的光鸭放入砂锅中，再把煮鸭原汤用筛滤入，放入火腿片、冬菇片，加精盐、绍酒、葱姜，上笼用旺火蒸约2小时，取出拣去葱姜，加味精即成。', '汤清见底，味道醇厚，鸭肉酥烂，鸭形完整，堪为一道形美、味美的鸭馔佳品。', NULL, NULL, '光肥鸭一只（重约2500克）、火腿25克、水发冬菇片15克、精盐12克、绍酒30克、葱25克、姜25克、味精3克。');
INSERT INTO `recipe` VALUES (1476, 73, '清炖兔子', '兔肉洗净，切成3厘米见方的块，入清水中浸泡4小时，捞出放盆内，用精盐、绍酒、葱姜、花椒腌制4小时，再用清水洗净。五花肉切成小丁待用。将兔肉放水锅中出水，捞出洗净。将兔肉、猪肉丁、鸡清汤、葱姜、绍酒放入砂锅，上火烧沸，撇去浮沫，加盖移至微火炖至兔肉酥烂，拣去葱姜，加入冬菇、冬笋、青菜心烧沸，放入味精、胡椒粉，淋上芝麻油即成。若配蒜泥蘸食，更别具风味。', '兔肉酥烂，鲜香肥嫩，风味独特，深受食者喜爱。', NULL, NULL, '鲜兔肉1500克、水发冬菇25克、冬笋50克、净猪五花肉350克、青菜心75克、精盐12克、味精7.5克、绍酒25克、花椒5克、胡椒粉1克、芝麻油3克、葱姜各15克。');
INSERT INTO `recipe` VALUES (1477, 73, '清烩鲈鱼片', '将鱼肉切成长方片，加绍酒、精盐、鸡蛋清、湿淀粉拌匀上浆，炒锅上烧热，放油烧至四成热（约88度），放入鱼片划油，至鱼片呈乳白色时倒出沥油。原锅仍置火上，留底油，放入葱姜末煸香，再放入韭黄段及其它配料煸炒，加入鲈鱼骨浓汤，加绍酒、精盐烧沸，倒入鱼片，用水淀粉勾芡，淋入芝麻油，起锅装入凹盘内，撒上胡椒粉，放上香菜叶即成。上桌时，带姜醋碟。', '鱼片白如雪，肉质软嫩滑，汤汁似牛乳，清谈又爽口，原汁原味浓。', NULL, NULL, '鲈鱼肉200克、荸荠片100克、水发木耳10克、韭黄段25克、鸡蛋清15克。绍酒15克、精盐3克、湿淀粉15克、葱10克、姜5克、胡椒粉1.5克、芝麻油15克、熟猪油450克（实耗油45克）。');
INSERT INTO `recipe` VALUES (1478, 73, '清汤火方', '将火腿及脚爪放清水中浸泡，刮洗干净。母鸡去内脏洗净，用一只母鸡和火腿爪一起放入锅中调汤，将另只鸡脯斩成茸，谓“白馅”。鸡腿斩成茸，谓“红馅”。鸡骨斩成茸，谓“骨馅”。在炖好的鸡汤中再经三种“馅”分别调制，从而汤清见底。将火方放入垫有竹箅的砂锅中，加绍酒、葱结、姜片、清水，上火焖至七成熟。待凉后在肉面上剖成3厘米见方的方格，肉皮面剞同样的方格，然后将皮朝下放入汤盘中，加清水上笼蒸30分钟，取出后滗去汤汁，换入清水复蒸一次，再用鸡汤套蒸二次至酥烂，蒸火方的同时，将冬菇、冬笋片放入盘中上笼蒸熟。从笼内取出火方，', '汤清见底，味鲜汁醇，火腿酥香而脍炙入口。', NULL, NULL, '火腿腰峰500克、火腿脚爪75克、净母鸡一只（重约600克）、水发冬菇25克、冬笋片150克、精盐25克、绍酒75克、葱结20克、味精15克、姜片25克。');
INSERT INTO `recipe` VALUES (1479, 73, '清汤金钱鱿鱼', '1.将鱿鱼切成圆形，放入清水浸泡一夜后捞出。 \r\n2.用净炒勺放入石碱鱿鱼块和50克清水，而后用小火炒至鱿鱼全部沾上碱时，加入清水，用大火烧开后，再放入小火焖15分钟，倒去污水，再以同样方法焖10分钟，第3次焖5分钟，然后用开水漂洗2-3次，使鱿鱼呈嫩白色，碱全部被洗掉、香菜放入。 \r\n3.炒勺上旺火，放入鸡汤和云腿片，再加入味精、盐、料酒，汤烧开后倒入盛鱿鱼的汤斗中即成。', '汤色白，味清鲜，鱼香嫩。', NULL, NULL, '鱿鱼5条，熟云腿12片，料酒20克，盐、味精、香菜各5克，胡椒粉1克，鸡汤1公斤。');
INSERT INTO `recipe` VALUES (1480, 73, '清蒸刀鱼', '1.将刀鱼刮去鱼鳞，用两支竹筷从鱼鳃处插入鱼肚里，卷出内脏和鳃，用清水洗干净，放入八成热的水锅里烫一下捞出，用刀轻轻刮去鱼身上粘液（不要刮破鱼皮），再用清水洗净，用刀在鱼身的2/3处切下鱼尾待用。\r\n2.将刀鱼整齐地摆放在汤盆里，鱼上先放笋片铺平，火腿片放在笋片上，再放上冬菇、猪板油丁、葱结、姜片，加盐、酱油、绍酒，上笼用旺火菜10分钟左右，鱼熟立即出笼，拣去葱结、姜片，将卤汁滗入锅内，加鸡汤50克，烧滚后倒入鱼盆里即成。', '色泽洁白，鱼肉细嫩，汤汁微红，滋味鲜美。', '必须用鲜活的刀鱼，切勿用不新鲜或冰冻多时的鱼。因为此菜应是肉嫩清鲜之美味，原料鲜活才能做到，陈鱼或久冻之鱼色泽灰暗，鲜味不足，并有异味。火候要恰当。一般上笼旺火蒸10分钟左右即可，如系清明前刚上市的刀鱼，其肉质极嫩，只需蒸8分钟即熟，应视鱼的大小、质地恰当用火，不要蒸得过熟，失去其鲜嫩特点。', NULL, '刀鱼2条（共重400克左右），熟火腿片5克，笋片25克，水发冬菇4只，生猪板油丁50克，绍酒20克，精盐5克，酱油、葱结、姜片各1克，鸡汤50克。');
INSERT INTO `recipe` VALUES (1481, 73, '人参珠鸡', '珍珠鸡宰杀治净，斩去爪，将鸡头插入翼下，两腿斩去大骨插入腹部，放入水锅中，加绍酒、葱姜，烧沸后撇去浮沫，将鸡捞出洗净，汤用筛过滤待用。人参加冷水略泡，轻轻洗去泥沙，直切成10条，用火腿丝捆成原形，放碗内加高汤蒸之。将珍珠鸡放入品锅内，加盐、绍酒、原汤，再加入人参及原汁，用保鲜纸封口加盖，用旺火上笼蒸2小时即成。', '汤清见底，鸡肉鲜美。食之，可明目益智，延年益寿，尤宜老入冬季食用。并有补精添髓，补脾益肺。', NULL, NULL, '珍珠鸡一只（重约500克）、人参10克、生火腿35克、盐5克、绍酒25克。');
INSERT INTO `recipe` VALUES (1482, 73, '如意笋', '1.用开水先把冬笋煮熟，然后用滚刀切成约20厘米长的薄片。鸡胸脯肉剁成鸡茸，加入味精、盐、蛋清、料酒和葱姜汁，搅拌均匀。把青椒挖去籽洗净，切成与火腿条一样粗(筷子粗)的长条。 \r\n2.把笋片摊平，抹上干淀粉和一层鸡茸，然后把两根火腿条放在笋片的一端，把两根青椒条放在另一端，由两端向中间卷起。其它按同法去做，卷好后上笼屉蒸熟取出，淋上香油。冷却后把两头切去，并切成0.5厘米厚的片装盘即成。', '色白、脆嫩。', NULL, NULL, '净冬笋400克，青椒20克，鸡蛋清30克，鸡胸脯肉100克，火腿条25克。调料:料酒5克，盐4克，味精5克，葱姜汁25克，干淀粉3克。');
INSERT INTO `recipe` VALUES (1483, 73, '三套鸭', '1.活鸭宰杀治净，切断颈骨，在近背部的颈处划破鸭皮，抽出颈骨，将鸭皮肉用力向下翻剥，割断翅骨、骱骨和筋，抽去翅骨，继续向下翻剥，露出腿骨，割断骨散和筋，敲断腿膝骨，抽去腿骨，再向下翻剥至肛门，割断肠，使鸭肉与骨架分离，挖去内脏，放入沸水锅中烫去血污，清水洗净，再把鸭翻回成原状。野鸭和鸽用上述同样方法出骨，入开水锅中焯后洗净。\r\n2.将鸽子从野鸭刀口处塞入野鸭腹中，同时放入冬菇10克、火腿片100克，再将野鸭从家鸭刀口处塞入家鸭腹中，并将冬菇15克、火腿片100克、冬笋片50克塞入鸭腹内，成三套鸭生坯。\r\n', '制作精细；形状饱满完整，家鸭肥美，野鸭酥香，鸽子鲜嫩，三味溶合，滋味极佳。', '选料要新鲜，最好取用活鸭。三禽出骨后要保持形状完整。重用文火煨酥。', NULL, '活嫩母鸭1只（重2000克左右），光野鸭1只（重750克左右），光鸽子1只（重250克左右），熟火腿片250克，水发冬菇50克，冬笋片、绍酒各100克，精盐6克，葱结35克，姜块25克。');
INSERT INTO `recipe` VALUES (1484, 73, '三鲜脱骨鱼', '将鲤鱼治净，平放砧板上，在肛门处横切一刀，刀断脊骨。用长薄形尖刀从两刀口处伸进，沿脊骨轻轻将骨肉割离，从鳃口处取出脊骨和内脏，洗净沥干。将猪肉斩成茸，放入碗加绍酒、精盐、葱姜汁、味精拌匀，再放入冬菇丁、冬笋丁、鸡脓丁、虾仁，伴和后从鱼鳃盖下刀口处填入鱼腹。再用酱油抹遍鱼身。锅置火上烧热，舀入熟猪油，烧至七成热（约154度）时，投入鱼煎至两面金黄，加入绍酒、酱油、精盐，葱姜、白糖和适量清水，加盖烧沸，移小火烧20分钟，淋入熟猪油，移至旺火收稠汤汁，起锅装盘即成。', '鱼形完整，鱼肉鲜嫩，腹藏三鲜，别具风格。', NULL, NULL, '活鲤鱼一条（重约750克）、猪夹心肉75克、虾仁25克、鸡肫30克、冬笋25克、水发冬菇15克、绍酒、20克、盐2克、葱15克、姜10克、味精2克、酱油15克、白糖7.5克、熟猪油75克。');
INSERT INTO `recipe` VALUES (1485, 73, '拔丝楂糕', '将山楂糕切成4.5厘米长、1.2厘米宽的条。鸡蛋加水淀粉、面粉、清水调成蛋糊。锅置火上，加花生油，烧至六成热（约150度）时，将楂糕条逐一先滚沾干淀粉，再拖满蛋糊放入油中，炸至金黄色时捞出。在炸楂糕条的同时，另取锅上火，放花生油、白糖和清水，用小火熬至糖液出丝时，放入桂花酱和炸好的楂糕条颠翻炒锅，撒上芝麻仁，装盘即成。', '植糕外皮甜脆，内软嫩，酸甜爽口。', NULL, NULL, '山楂糕350克、芝麻仁10克、鸡蛋25克、冰淀粉15克、面粉30克、花生油500克（实耗油75克），白糖75克、桂花酱5克。');
INSERT INTO `recipe` VALUES (1486, 73, '山鸡塌', '将野鸡脯肉剔去筋膜，批片后放入清水中漂除血水，捞出沥干，与生肥膘肉分别斩成茸，同放碗内，加鸡蛋清、绍酒、味精2克、葱姜汁15克、精盐拌匀成糊状。将熟肥膘肉先批成片，再修成直径3.5厘米的圆片。将蛋清磕入碗中，加干淀粉、精盐搅成蛋清糊。将肥膘圆片一面沾干淀粉，另一面抹上蛋糊，有粉的一面朝下放入盘中，将野鸡糊挤成3厘米直径的圆球，放在肥膘上，再缀以火腿末、香菜叶。炒锅上火烧热，放花生油烧至六成热（约150度）时，将山鸡塌生坯放入锅内，晃动炒锅，盖上锅盖，煎约3分钟至熟，倒入漏勺沥油。装盘后淋芝麻油即成。', '色泽鲜艳，造形美观，鸡肉软嫩，煎面酥脆，食之鲜香油润。', NULL, NULL, '野鸡脯肉450克、生猪肥膘肉25克、熟猪肥膘肉25克、熟火腿末20克、香菜5克、鸡蛋清20克、绍酒20克、味精1.5克、葱姜汁15克、精盐5克、干淀粉15克、芝麻油10克、花生油75克。');
INSERT INTO `recipe` VALUES (1487, 73, '生麸肉圆', '将水面筋搓成长条，剪成50个小块，每块捏成圆子，另将猪腿肉斩碎，加调料搅拌成50个馅心。再把水面筋圆子拉开，包进肉馅捏紧。锅放水烧沸，加精盐，肉圆随做随下锅，但水不能沸，适时加放冷水，免使沸水激破肉馅，至圆子全部浮在水面时，再加入绍酒、味精，起锅装汤碗即成。', '此菜呈白色小圆球，柔软皮薄，肉圆汁多鲜嫩，食前须咬鼓圆之薄皮吸吮卤汁，其味甜中带咸，深受老年人的喜爱。', NULL, NULL, '猪腿肉500克、生麸（即水面筋）250克、精盐12克、味精7.5克、绍酒25克、白糖25克、葱姜各10克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (1488, 73, '生煎皈鱼', '将皈鱼剥皮治净。用刀将鱼轻拍一下，再在鱼肉两面剞上刀纹，用绍酒、酱油、精盐、胡椒粉、葱姜腌浸5分钟。在鸡蛋中加入面粉、水淀粉、葱末、精盐调成糊。锅置火上烧热，放油烧至六成热（约150度）时，将鱼挂糊后入锅煎至两面金黄，倒出。原锅上火，放芝麻油烧热，投入葱花炸香，放入煎熟的饭鱼，撒入花椒盐，翻锅，取出改刀装盘即成。', '色泽金黄，外脆里嫩，口味鲜美，为秋令佳肴。', NULL, NULL, '皈鱼400克、鸡蛋25克、面粉15克。绍酒30克、酱油20克、精盐4克、胡椒粉1.5克、葱20克、姜15克、水淀粉25克、芝麻油15克、花椒盐5克、花生油150克。');
INSERT INTO `recipe` VALUES (1489, 73, '双皮刀鱼', '将刀鱼刮鳞，去鳃，鳍，在肛门外横划一刀，用竹筷从鳃口插入鱼腹，绞去内脏切掉鱼尾尖洗净，逐条在鱼背外用刀沿脊骨两侧剖开，去掉脊骨，鱼皮朝下平铺在砧板上，用刀背轻捶鱼肉使细刺粘在鱼皮上，再用刀面沾水刮下两面鱼肉剁成鱼茸，放入碗中，加猪熟肥膘茸，鸡蛋清，精盐，味精，绍酒和适量清水搅匀分成四份，平铺在四条刀鱼皮的肉面上，再将另一面鱼皮合上成鱼原状，在合口处沾上火腿末香菜末，放入盘中，将火腿片，春笋片，冬菇片相间铺在鱼身上，再放上葱结，姜片，加绍酒，精盐上笼蒸熟取出，去葱，姜，滗去汤汁；将锅置旺火上，舀入鸡清汤，再', '鱼形完整，食之无刺。', NULL, NULL, '刀鱼，猪熟肥膘茸，熟火腿片，熟火腿末，春笋片，水发冬菇，鸡蛋清，香菜末，绍酒，精盐，味精，葱结，姜片，鸡清汤，水淀粉，熟猪油。');
INSERT INTO `recipe` VALUES (1490, 73, '水晶肴蹄', '猪蹄剔去骨，用铁扦在肉面上戳些小孔，每只蹄均匀洒上硝水，再用盐揉擦，待肉色变红后取出泡入水中，漂去涩味，刮净皮上污物，用温水洗净。锅上火放入清水，将猪蹄放入煮至断血后捞出洗净。另取锅倒入老卤，略加清水，将花椒、八角、葱姜装入小布袋内扎紧袋口，放入锅中再加精盐、绍酒，以旺火烧沸后用盘压紧蹄肉，移至小火煮约3小时，使蹄肉酥烂出锅。将煮好的蹄肉皮朝下放入方盘内，摊平压紧，将锅内老卤烧沸，撇去浮油，放入明矾，加少许清水，再撇去浮油。然后将汤卤舀入蹄盆，淹满肉面，放阴凉处冷却“凝冻”，即成水晶肴蹄。上桌时，改刀装盘', '具有香、酥、鲜、嫩四大特点，瘦肉香酥，肥肉不腻，食不桠齿。', NULL, NULL, '猪前蹄750克、花椒7克、八角5克、葱姜各12克、精盐15克、味精12克、香醋15克、绍酒15克、硝水适量、明矾0.5克。');
INSERT INTO `recipe` VALUES (1491, 73, '松鼠桂鱼', '将桂鱼去鳞及鳃，剖腹去内脏洗净，鱼皮朝下在鱼肉上先直剞，再斜剞，深至鱼皮，成菱形刀纹。用绍酒、精盐调匀，抹在鱼头和鱼肉上，再滚上干淀粉，用手拎鱼尾抖去余粉，将番茄放入碗内加鲜汤、糖、香醋、酒、盐、湿淀粉拌成调味汁，炒锅用大火烧热下猪油，烧至八成热时，先将两片鱼肉翻卷，翅起鱼尾，放入油锅稍炸使其成形，再将鱼全部放入油锅炸，至金黄色捞起，放入盘中，装上鱼头拼成松鼠形，锅内留油少许，放葱段煸香捞出加蒜瓣末，笋丁，香菇丁，豌豆炒熟，下调味汁用大火烧浓后，放猪油和虾仁拌和，淋上麻油，起锅浇在鱼身上即成。', '外松脆，内软嫩，卤汁酸甜适口，滋味鲜美。', NULL, NULL, '桂鱼，干淀粉，番茄酱，鲜汤，糖，香醋，酒，盐，蒜瓣末，笋丁，香菇，豌豆，猪油，虾仁，麻油。');
INSERT INTO `recipe` VALUES (1492, 73, '松鼠鳜鱼', '将鱼治净，齐胸鳍斜切下鱼头，在鱼头下巴处剖开，用刀轻轻拍平，再用刀沿脊骨两侧平片至尾部，鱼尾部不断，斩去脊骨剔去胸刺，在鱼肉面用刀先直剞后斜奇成菱形刀纹，深至鱼皮。把绍酒和精盐放入碗内调匀抹在鱼头及鱼肉上，再蘸上干淀粉。油锅上火烧至七成（约17度），将鱼提起抖去汆粉，再将鱼肉翻卷翘起鱼尾成松鼠形，然后一手提着鱼尾，一手持筷夹住另一端，放入油锅，炸约20秒钟，使其成形，然后将鱼脱手放入油锅中，同时投下鱼头，炸至淡黄色时捞起。待油温升至八成热（约200度）时，投入鱼，复炸至金黄色捞出，装入长腰盘中，装上鱼头，', '鱼色金黄，外脆内松，卤汁金红，甜中带酸，鲜香可口。', NULL, NULL, '鲜活整条净鳜鱼1500克、河虾仁5克、熟笋10克、水发冬菇10克、青豌豆10克，番茄酱15克、精盐10克、白糖40克、绍酒30克、猪肉汤150克、蒜末15克、淀粉100克、香醋60克、热油1500克。');
INSERT INTO `recipe` VALUES (1493, 73, '松子肉', '将肋条肉放在砧板上，用刀将其修成长方形，肉面批平，将批下的碎肉与虾仁分别斩成茸，加调料搅匀成虾肉茸。将肉块上烤叉，放在炭火上烤至肉皮焦黑时，取出放入冷水中刮去焦屑洗净，在肉的一面剞小方格，在皮的一面剞斜方格，然后在肉面上抹一层蛋糊，再把虾肉茸均匀铺在上面，用刀轻排几下。松子仁下油锅划油后，均匀地铺在肉茸上，再轻轻排斩，表面再抹一层蛋糊，然后肉皮朝上放入。锅中煎至金黄色。取砂锅一只，内垫竹血型，将肉块皮朝下放入，加酱油、绍酒、白糖、葱、姜和清水，上中火烧沸，撇去浮沫，加盖移小火上焖2小时至酥烂，待汤汁稠浓时', '肉嫩如豆腐，肥而不腻，虾仁鲜美，松子芳香。', NULL, NULL, '去骨肋条肉一块2000克。虾仁50克、松子仁75克、豌豆苗150克、虾子15克、精盐10克）酱油25克、绍酒40克、八角5克、味精10克、白糖30克、葱姜各10克。');
INSERT INTO `recipe` VALUES (1494, 73, '松子熏肉', '五花肉刮洗干净，用精盐、绍酒，花椒腌制，然后洗净吸干水分，用铁叉插入肉内，皮向下在旺火上烘烤至皮呈焦黄色，离火放入清水中泡刮、洗净。大砂锅垫入竹箅，放入方肉，加入酱油、绍酒、冰糖、精盐、丁香、葱、姜，加水烧沸，移小火焖至酥烂。铁丝烙垫入荷叶，将方肉放上（皮朝上），在锅内投入松叶、茶叶、糖及锅粑，再将铁丝烙放入锅内，加盖上火，以旺火烧2-3分钟，离火取出，切块装盘，再涂上麻油。松子仁入油锅内划油，撒在肉上即成。', '色泽金黄，肉酥味香，肥而不腻，食之爽口。', NULL, NULL, '鲜猪五花肉一块750克、松子仁50克、荷叶1张、精盐15克、绍酒25克、花椒5克、麻油5克、酱油30克、冰糖50克、丁香3克、葱姜各5克、花生油200克。');
INSERT INTO `recipe` VALUES (1495, 73, '松子鸭颈', '将烤鸭片下，切成5厘米宽，6厘米长的块待用。虾仁、肥膘分别斩成茸，放入碗内加精盐、绍酒。葱姜汁、味精及蛋清搅拌成虾馅。将松子下四成热（约100度）的油锅拉油，变色时倒出沥油，放入虾馅内拌匀。将鸭皮（皮面朝下）摊在砧板上，拍上干淀粉，放上虾馅，卷成5厘米长的鸭颈，锅上火烧热，倒入花生油，烧至五成热（约125度）时，将鸭卷徐徐投入锅中炸至皮脆色金红，然后捞出装盘，以香菜衬托即成。', '形象逼真，外酥里嫩，香而不腻，兼有松子之香。', NULL, NULL, '烤鸭皮350克、香菜7.5克、松子25克、虾肉50克、精盐2.5克、绍酒25克、葱姜汁10克、味精2克、蛋清25克、干淀粉15克、花生油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1496, 73, '松子鱼米', '鱼肉切成绿豆大小的鱼米，松子用温油炸熟；锅上火放油，待油温，下入鱼米滑开；原锅上火，注入花生油，放姜末煸炒后放鱼米与松子炒均，调味芡汁，熟透装盘即成。', '鱼肉鲜嫩，松子酥香。', NULL, NULL, '桂鱼，松子，柿子椒，红泡椒，高汤，盐，料酒，蛋清，淀粉，味精，胡椒粉，油，糖，姜末。');
INSERT INTO `recipe` VALUES (1497, 73, '苏式熏刀鱼', '1.将鲜刀鱼支鳃、内脏洗净，斩成10厘米长的块，用葱、姜、料酒、精盐略腌。\r\n2.将酱油、醋、白糖、精盐、料酒、八角、味精同放锅内，烧开成卤汁备用。\r\n3.炒锅内加植物油烧至八成热，将鱼块拍上干淀粉后放入，炸至呈金黄色时捞出，待油温又升至八成热时再放入鱼块炸酥，捞出放入调好的卤汁中浸泡，再浇上芝麻油即成。', '酸甜咸酥香，滋味鲜美。', NULL, NULL, '鲜刀鱼300克，葱1根，生姜5克，八角2个，酱油、醋各5克，白糖5克，盐、料酒、干淀粉、味精各适量，植物油500克（约耗50克），芝麻油10克。');
INSERT INTO `recipe` VALUES (1498, 73, '酥桔元', '将金桔饼、猪肥膘肉切成细末，加入蛋黄、面粉及适量清水调和成糊。锅置火上、放入熟猪油，烧至六成热（约132度）时，将肥膘糊挤成核桃大小的丸子，下油锅炸至起壳时，将锅端离火口，焐至熟透，再上火炸至金黄色时倒出沥油。将锅复置火上，放入适量清水，加入白糖、桂花卤，熬至白糖溶解、卤汁翻泡，离火，速将炸好的丸子倒入，并用竹筷在锅内轻轻翻拌，至糖液与桔元裹匀，待冷却后结成白霜，装入盘内，撤上红绿丝即成。', '形似金桔，金体玉衣，桔香浓郁，肥甜而不腻，香酥可口，经济实惠，老少皆宜。', NULL, NULL, '生猪肥膘肉300克、面粉75克、鸡蛋黄50克、红绿丝5克、金桔饼15克、桂花卤5克、白糖35克、熟猪油500克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1499, 73, '素熘鹅皮', '油面筋用刀一切两开，翻转外面向里。取碗一个，放入糖、酱油、醋、水淀粉和清水适量调成糖醋汁。炒锅上火，放入花生油，投入生姜末略煸，放入冬菇、笋片、红辣椒片、香菜末倒入糖醋汁烧沸，加芝麻油烧至八成热（约200度）时，投入面筋炸至褐黄色，捞起装盘，浇热花生油和卤汁即成。', '形似鹅皮，酥松香脆，口味酸甜，为一道佐酒佳肴。', NULL, NULL, '油面筋350克、水发冬菇15克、鲜笋片25克、香菜7.5克、糖40克、酱油15克、醋20克、淀粉20克、姜10克、芝麻油15克、花生油500克（实耗油75克）。');
INSERT INTO `recipe` VALUES (1500, 73, '宿迁猪头肉', '猪头泡入水中洗净，镊去细毛，割下双耳，去掉猪眼圈、嘴唇、耳圈、鼻子、脸劈成两块，下巴劈成3块，再放入水中浸泡，漂去血水。下水锅煮半小时，捞出洗净切成块。取锅上火放油，下甜面酱炒成甜酱色，加入卤汁，放入肉块、茴香、桂皮、酱油、精盐及清水，先用旺火烧沸，再用炆火煮约3小时至肉酥烂即成。', '肥肉酥烂，精肉鲜香，味纯而嫩，香气芬芳。', NULL, NULL, '猪头一个（约2500克）、精盐30克、茴香7.5克、桂皮7.5克、酱油40克、甜面酱50克。');
INSERT INTO `recipe` VALUES (1501, 73, '太湖银鱼', '将银鱼摘去头尾，用清水洗净沥水，鸡蛋磕入碗中，加盐调散，笋丝入开水锅中焯一下捞出，木耳清水洗净，用开水泡发沥干；炒锅上火，用油滑锅，放猪油烧热，下银鱼煸炒几下，倒入蛋液中搅和，炒锅内再加猪油烧沸，倒入银鱼和蛋液，待蛋液涨发，一面煎黄后，端起炒锅翻身，再煎另一面，煎熟后用铁勺将蛋块拉成四大块。加入绍酒、酱油、精盐、白糖、味精、白汤，倒入笋丝、木耳加盖，用小火焖烧二三分钟，再旺火收汁，放入韭菜，再加猪油出锅装盘即成。', '色泽金黄，肥鲜香嫩。', NULL, NULL, '银鱼，鸡蛋，笋丝，韭芽，酱油，水发木耳，绍酒，精盐，猪油，白汤，白糖，味精。');
INSERT INTO `recipe` VALUES (1502, 73, '桃仁鸭方', '光鸭去内脏洗净，沥干水分后放入盘中，用精盐、绍酒、葱姜、丁香腌制20分钟，然后上笼蒸至酥烂，取出。将鸭子骨头剔除，保持整形，皮朝下放入盘中待用。桃仁用沸水浸泡，水中略加点精盐，然后剥去皮，切成粗粒。虾仁洗净斩成茸，加调料搅拌成糊。在鸭肉上拍一层干淀粉后铺上虾糊，并塌平，撒上桃仁。锅上火烧热，放入花生油，烧至六成热（约150度）时投入鸭，煎炸至金黄酥脆，起锅沥油，改刀装盘即成。', '色泽金灿，香酥透味。', NULL, NULL, '光鸭一只（重约2000克）、虾仁50克、核桃仁15克，精盐15克、绍酒35克、葱25克、姜25克、丁香1克、干淀粉25克、花生油500克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1503, 73, '跳竹蛏', '1.将竹蛏洗去泥沙，剥去壳取出蛏肉，撕去韧带，分别将水管、蛏体剖开，刮去内脏，洗净泥沙，放入绍酒（30克）、姜末、精盐（2克），浸渍待用。 \r\n2.将锅置旺火上烧热，舀入熟猪油（50克），烧至九成热时，将蛏肉投入，快速煸炒至呈玉色时，迅即盛起待用。 \r\n3.将锅置火上烧热，舀入熟猪油（50克），投入葱段，笋片煸炒，放绍酒（20克）、精盐（3克），滗入熟蛏汁烧沸，用水淀粉勾芡，投入蛏肉，颠锅淋入芝麻油装盘，撒上白胡椒粉即成。', '色若白玉，蛏肉腴滑柔嫩，蛏鼻脆嫩爽口。', NULL, NULL, '活鲜大竹蛏1000克，熟竹笋片150克，绍酒50克，精盐5克，葱段25克，姜末5克，白胡椒粉0.5克，水淀粉25克，芝麻油15克，熟猪油100克。');
INSERT INTO `recipe` VALUES (1504, 73, '氽脊脑', '将猪脑和脊髓放在盆中用清水浸泡，并轻轻去掉血衣、血筋，把猪脑切成四块，脊髓切成几段。炒锅上火，放入鸡清汤烧沸即投入脑、髓烧透，撇去浮油沫，移小火焖2分钟，再移至旺火，放入配料，加精盐、味精、绍酒，起锅装入碗内，淋上熟鸡油即成。', '汤汁清澈，脑、髓乳白，鲜嫩细腻，风味独特。', NULL, NULL, '鲜猪脑子250克，猪脊髓250克，熟火腿片50克，笋片150克，香菇25克，精盐15克，味精12克，绍酒25克，熟鸡油20克。');
INSERT INTO `recipe` VALUES (1505, 73, '脱壳鳜鱼', '将鳜鱼治净，沿脊骨顺长片入，取出内脏，在鱼身带骨面横剖三刀，再直剞一刀。用精盐在鱼身面擦匀，抹上香糟，腌渍3小时后去鳃洗净，将葱姜塞入鱼腹内。网油洗净沥干，平铺砧板上，撒上干淀粉，将鳅鱼用绍酒涂抹后放网油上包好。另将鸡蛋清放碗内，加干淀粉、粳米粉和清水调成蛋糊。锅置火上，放油烧至五成热（约125度）时，将鳃鱼挂满蛋糊，下油锅炸熟捞出，待油温升至八成热（约200度）时，再将鱼投入油锅炸至皮脆，捞出装盘，淋芝麻油，撒上胡椒粉、椒盐即成。', '外包薄壳，酥脆鲜香，壳内鱼肉清醇细嫩，糟香扑鼻，风味独特。', NULL, NULL, '活鳜鱼一条（约750克）、蛋清35克、粳米粉15克、精盐2.5克、干淀粉25克、胡椒粉1克、椒盐5克、生姜10克、葱7.5克、绍酒35克、猪网油150克、芝麻油5克、菜子油1000克（实耗油75克）、香糟15克。');
INSERT INTO `recipe` VALUES (1506, 73, '网包鳜鱼', '将鳜鱼治净，用刀片下鱼肉，修切成12个圆形块，再分别剖斜、直刀纹，然后和鱼头、鱼尾共放碗中，加绍酒、精盐、葱姜汁腌渍。网油洗净沥干，改切成方块，每块网油中间放鱼肉一块，面上再放冬菇一只，包成圆形。将鱼头、尾分放在长腰盘的两端，中间排放包好的鱼块，上笼旺火蒸熟取下，将盘内汤汁滗到炒锅中，置火上烧沸，加味精，用水淀粉勾芡。烧在鱼身上，撒上熟火腿末即成。', '鱼肉鲜嫩腴美，火腿腊香。色彩悦目。', NULL, NULL, '鲜活鳜鱼750克、冬菇15克、网油175克、熟火腿末10克、绍酒35克、精盐2.5克、姜汁15克、葱10克、味精1.75克、水淀粉25克。');
INSERT INTO `recipe` VALUES (1507, 73, '网油卷', '将猪网油用温水漂洗干净，晾干，切成长30厘米、宽12厘米的长方块，将甜枣泥放在网油上，卷成长圆条状，再切成3厘米长的段。鸡蛋清打成发蛋，加入干淀粉调成蛋糊，锅置旺火上烧热，舀入熟猪油，烧至六成热（约132度）时，离火，将肉油卷滚上干淀粉，再挂满发蛋糊放入油锅中。然后将锅置在中火上，待网油卷炸至米黄色时捞出，沥去油装入盘中，撒上白糖即成。', '色泽金黄，外形饱满，内里松软，口味香甜。', NULL, NULL, '猪网油100克、甜枣泥150克、鸡蛋清20克、淀粉20克、白糖25克。');
INSERT INTO `recipe` VALUES (1508, 73, '文思豆腐', '将豆腐切成豆腐丝，入沸水锅中略焯，去除豆腥气，使豆腐成丝条，炒锅内加清鸡汤，外加清汤，放豆腐丝，笋丝，香菇丝，烧沸后撇去浮沫，加盐，味精，火腿丝和菜丝稍烩，出锅倒入汤碗内，淋上鸡油即成。', '色泽白绿相间，豆腐细嫩，汤汁鲜美。', NULL, NULL, '豆腐，清鸡汤，笋丝，香菇丝，盐，味精，火腿丝，菜丝，鸡油。');
INSERT INTO `recipe` VALUES (1509, 73, '无锡肉骨头', '1.将排骨斩成小块，用硝末、红米、食盐拌匀，入缸腌10小时左右。取出放入锅内，加清水烧沸，捞出洗净。\r\n2.将锅洗净，放入排骨和方肉，加绍酒、葱、姜、茴香、桂皮、加清水1750克，盖上锅盖，用旺火烧沸，加酱油、白糖，再盖好锅盖，用中小火焖烧1小时，至排骨酥烂、汤汁浓香即可。食用时取出，改刀装盘，浇上卤汁。', '浓油赤酱，酥烂脱骨，汁浓味鲜，咸中带甜，香气浓郁。', '烹制前必须将排骨腌透，使其吸入咸味。烹制时重用小火焖烧、窝酥，使汁浓入味。', NULL, '猪助条排骨2000克，精肥方肉500克，酱油300克，白糖175克，绍酒100克，姜、葱、桂皮各25克，茴香15克，硝末5克，食盐适量，红米少许。');
INSERT INTO `recipe` VALUES (1510, 73, '五丁鱼圆', '将火腿肉、冬菇、笋、蛋糕各留少许切成片，其汆切成细丁，加入去皮青豆、精盐、味精，同熟猪油进行搅拌，然后做成若干个直径约3厘米的丸子。将鱼肉用刀拍松，去除鱼刺，切成块状，用清水漂净，沥干后放在猪皮上面，斩成茸状。将鱼肉放入容器内，加入鸡蛋清、绍酒、葱姜汁、水淀粉，随即顺着一个方向搅拌，边搅边加清水，同时加入精盐搅至上劲。用左手抓鱼茸，使鱼茸从大拇指和食指中间挤出，随即掐入一只五丁小丸，将鱼茸回入手中，再用拇指在上部刮一下，挤出圆子，用右手勾入冷水锅中，做好后，用小火加热，至鱼圆成白玉色时，捞入汤碗内。将鸡清', '鱼圆色白如玉，味美爽口，具有“三形”特色，即鱼圆在汤中呈圆形，夹在筷上显长形，放在盘内似扁形。', NULL, NULL, '咸鱼肉500克、熟火腿15克、水发冬菇15克、鲜笋25克、黄蛋糕15克、鸡蛋清25克、熟青豆10克、盐2.5克、味精2克、熟猪油30克、绍酒20克、葱10克、姜7.5克、淀粉25克、鸡清汤500克、熟鸡油15克。');
INSERT INTO `recipe` VALUES (1511, 65, '虫草鸭舌', '1. 用根条粗壮质地好的虫草，用温水清洗泥沙，去根足，浸泡回软。\r\n2. 将鸭子、排骨洗净，斩成块，入沸水中去净血污装汤盘，加姜（5克）、葱（5克）、绍酒（8克）、川盐（2克），清水上笼蒸约2小时，制成特制鸭汤约1000克。 \r\n3. 将莲子泡涨，除去皮、心，蒸粑。 \r\n4. 选完整鸭舌，洗净煮熟去骨。将虫草逐一放入鸭舌内摆入碗中，加姜（5克）、葱（5克）、绍酒（7克）、鸭汤250克、川盐（2克），上笼蒸熟定型。 \r\n5. 把已蒸好的虫草鸭舌翻扣入大盘内，熟莲子镶在鸭舌四周，倒入特制鸭汤（鸭子、排骨作它用', '鲜醇浓香，菜形美观。', NULL, NULL, '鸭舌500克（100根），虫草60克（100根），白莲米50克，鸭子1只约1250克，猪排骨750克，姜10克，葱10克，川盐4克，绍酒15克，胡椒粉1克，味精1克。');
INSERT INTO `recipe` VALUES (1512, 73, '五子蒸鸡', '活母鸡宰杀治净，放入锅中出水，加葱姜、绍酒，沸后撇去浮沫，煮至断血时捞出，将原汤吊清待用。莲子去皮心，其汆“四子”洗净。将鸡从脊背剖开，斩去大骨（长三角形），扣入品锅中，放入“五子”，倒入原汤，加精盐，用保鲜纸封口加盖。将品锅上笼用旺火蒸3小时，至酥烂取出，上席后去盖，揭掉封纸即成。', '汤色银红，清而见底，肉质鲜嫩酥烂，口味香咸。并有明目降压，养血安神之功效。', NULL, NULL, '活嫩母鸡一只（重约750克），莲子20粒、枸杞子10粒、红枣10粒、松子10粒、五味子10粒、绍酒30克、葱25克、姜25克、精盐7.5克。');
INSERT INTO `recipe` VALUES (1513, 73, '香菜梗炒鳝丝', '1.将活鳝鱼杀后，把内脏、背骨除去，用刀顶头切成细丝，加入盐2克和湿淀粉，拌匀。香菜梗洗净，切成3厘米长段。用碗放入料酒、白糖、味精、盐和温淀粉调成卤汁。 \r\n2.炒勺内放熟大油，烧7成热时，倒入鳝丝，速划熟，后倒出沥油。 \r\n3.用原勺将大蒜泥煸一下，再倒进卤汁，放入鳝丝、香菜梗，稍翻炒几下，淋入香油，装盆，撒上胡椒粉即成。', '色金黄，味香脆。', NULL, NULL, '活鳝鱼800克，香菜梗50克。熟大油900克，(实耗约80克)，蒜泥10克，酱油10克，料酒15克，盐4克白糖2克，胡椒粉1克，味精4克，湿淀粉20克，香油30克。');
INSERT INTO `recipe` VALUES (1514, 73, '香蕉果炸', '1.鸡蛋加入白糖、面粉、干淀粉调和均匀，再加清水调成稀面糊。\r\n2.锅置火上，舀入清水，将稀面糊徐徐倒入，边倒边用铁勺搅动，打熟成厚糊状，见锅内四面发出气泡时离火，加香蕉精搅和，放入葡萄干、蜜冬瓜丁搅搓匀，然后倒入用芝麻油抹过的盘中，冷却后撒上干淀粉，切成长5厘米、宽3厘米、厚1厘米的长方块，再用干淀粉拌和。\r\n3.锅上旺火烧热，放入花生油。烧至七成（约175度）时，放入果炸块，炸至浮于油面，色呈淡黄时，捞出装盘，撒上白糖即成。', '色呈淡黄，香蕉味浓，外脆里嫩，软润香甜，菜点兼优。', NULL, NULL, '面粉200克、干淀粉20克、鸡蛋50克、蜜冬瓜丁25克、葡萄干25克、香蕉精0.5克。白糖75克、芝麻油入5克、花生油750克（实耗油150克）。');
INSERT INTO `recipe` VALUES (1515, 73, '香芋烧竹鸡', '将竹鸡焖死治净，斩成长方块，用酱油浸渍。香芋去皮洗净，切成块。炒锅上火，舀入花生油烧至八成热（约200度）时，分别将鸡块、香芋块投入油锅划油。原锅置火上，放油，加葱姜略煸，放入鸡块、绍酒、酱油、白糖、精盐及适量清水，烧至鸡块六成熟时，投入香芋同烧15分钟，再用水淀粉勾芡即成。', '色泽金黄，竹鸡鲜美，香芋栗香，入口酥松。', NULL, NULL, '竹鸡450克、香芋150克、葱25克、姜25克、绍酒25克、酱油15克、白糖5克、精盐3.5克、水淀粉20克、花生油450克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1516, 73, '香炸银鱼', '将银鱼摘去头，抽去肠，用清水漂洗干净，沥干，加精盐、绍酒、胡椒粉、葱姜汁略腌后，放入鸡蛋、干淀粉拌匀，再滚上一层面包屑。锅置旺火烧热，放花生油烧至六成热（约150度）时，将银鱼徐徐下锅炸至金黄色时捞出沥油。原锅仍上火放麻油，投葱末略炸，倒入银鱼翻锅装盘即成。', '色泽金黄，外脆里嫩，香味诱人。', NULL, NULL, '银鱼350克、鸡蛋25克、面包屑55克、精盐2克、绍酒25克、胡椒粉1.5克、葱姜汁15克、干淀粉20克、麻油5克、葱10克、花生油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1517, 73, '香炸云雾', '虾仁洗净沥干，斩成虾茸，加入绍酒、精盐、味精、干淀粉搅匀成虾糊。云雾茶尖放入碗中，用沸水浸泡30秒钟，取出沥干。将鸡蛋清打成发蛋，先取1/4与茶尖、虾茸拌匀，再将汆下的发蛋连同斩碎的松子仁一起放入，搅成糊状。锅置火上烧热，舀入熟猪油，烧至二成热（约44度）时，用汤匙将云雾虾茸糊一匙一匙舀入锅内，待成玉白色时，轻轻翻身，再氽1分钟，捞出。待油温升至四成热（约88度）时，倒入云雾团略汆，捞出装盘，佐以番茄酱即成。', '色泽青绿，酥脆干香，茶味馥郁。', NULL, NULL, '云雾茶尖350克、虾仁150克、松子仁30克、鸡蛋50克、绍酒15克、盐5克、精盐2克、淀粉20克、番茄酱15克、熟猪油750克（实耗100克）。');
INSERT INTO `recipe` VALUES (1518, 73, '响铃球', '将猪板油撕去膜，斩成茸，加入白糖拌匀。锅置火上，舀入熟猪油，烧至六成热（约132度）时，投入核桃仁炸至酥脆取出，去掉外衣，将核桃仁包入板油中，逐一搓成圆形，再滚上菱粉。鸡蛋清打成发蛋，加入菱粉调成发蛋糊，放入油锅炸至起壳发亮，用牙签在两端穿一小孔，让壳内溶油排出，炸至摇动发出响声时，捞出装盘，撒上白糖和红绿丝即成。', '形似圆球，光滑洁白，香酥松脆可口。', NULL, NULL, '猪板油150克、核桃仁100克、鸡蛋清25克、菱粉25克、红绿丝5克、白糖50克、熟猪油450克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1519, 73, '蟹肉扒翅', '1.水发之鱼翅盛在小锅内，加入葱2条、姜2片及鸡汤3杯，用小火煮30分钟，至鱼翅够软为止，捞出留用。 \r\n2.烧热锅，下猪油煎黄葱条与姜片，倒下蟹肉略炒，再淋入酒爆炒后加入鸡汤和酱油、盐、糖、胡椒粉，待煮滚后捞出葱姜，放下鱼翅，改用小火烧煮5分钟左右。 \r\n3.将生粉用水调溶，慢慢淋入锅内勾芡，熄火后盛入大碟内，滴下鸡油即可。', NULL, NULL, NULL, '水发鱼翅300克，葱4条，姜4片，新鲜蟹肉100克，酒2汤匙，鸡汤9杯，酱油（淡色）1汤匙，糖1/4茶匙，胡椒粉1/4茶匙，生粉4汤匙，鸡油、猪油各适量。');
INSERT INTO `recipe` VALUES (1520, 73, '雪花豆腐', '豆腐片去老皮、批成薄片、再切碎放入碗中，用热水烫去黄腥味，将水香蕈与蘑菇等配料均切成屑，虾仁用调料拌和上浆。炒锅上火，舀入鸡清汤，投入各种配料屑，再把豆腐沥水后倒入，加入精盐、绍酒、味精、烧沸后用水淀粉勾芡，淋入熟猪油烧沸、起锅盛入碗中。炒锅上火烧热，放油烧至四成热（约88度），投入虾仁拉油，至乳白色时倒出，撒入豆腐中即成。', '形似雪花，鲜香独特，营养丰富，堪称豆腐菜中珍品。', NULL, NULL, '嫩豆腐200克、水香蕈15克、蘑菇25克、松子仁5克、瓜子仁5克、熟火腿15克、熟鸡脯肉50克、虾仁5克。鸡清汤200克、绍酒20克、精盐10克、味精2克、淀粉15克、熟猪油75克。');
INSERT INTO `recipe` VALUES (1521, 73, '盐水虾', '将虾去须、脚，洗净后下入开水锅内。在烧开之前，锅内先加入葱、姜味精、料酒、盐、花椒等调料，烧开后将浮沫撇去，约3分钟左右虾即可煮熟。而后连汤一起离火装入碗中，冷却后盛入盆内即可。', '鲜红美观，鲜嫩清口。', NULL, NULL, '新鲜河虾500克，盐15克，花椒4粒，姜2片，葱3段，料酒50克，味精5克。');
INSERT INTO `recipe` VALUES (1522, 73, '扬州煮干丝', '1.将豆腐干先批成厚约0.15厘米的薄片，再切成细丝，放入盛器，加沸水和少量盐浸泡两次，捞出沥干，以除去豆腥味，使干丝软韧色白。开洋洗净，放在小碗内加酒，上笼或入锅隔水蒸透。\r\n2.炒锅烧热，放熟猪油40克，加鸡汤和肉汤，下干丝、开洋，旺火烧沸，加精盐、绍酒，移小火上烩煮10分钟，使干丝涨胖，吸足鲜味。出锅前再用旺火烧开，下豆苗，放味精，淋熟猪油10克，将干丝连汤倒在汤盆里，撒上鸡丝和火腿丝，豆苗放四周即成。', '色泽美观，干丝洁白，质地绵软，汤汁浓厚，味鲜可口。', NULL, NULL, '黄豆制成的白色豆腐干400克，熟鸡丝、熟猪油各50克，熟火腿丝、开洋、豌豆苗各25克，精盐2.5克，绍酒10克，味精1克，鸡汤、肉汤（或肉骨汤）各250克。');
INSERT INTO `recipe` VALUES (1523, 73, '羊方藏鱼', '羊肉用花椒、精盐、绍酒、葱姜搓抹，腌6小时，再下水锅中氽水，洗净。将鲫鱼宰杀治净，在鱼面两侧剞上花刀，下水锅分水洗净，抹上精盐和绍酒。用刀从羊肉侧面剖开，将鱼藏入。放入锅中，加清水、精盐、绍酒、葱姜、花椒，烧沸后移小火炖至羊肉酥烂，加入味精，淋芝麻油即成。', '羊肉酥烂味香，内藏鱼肉鲜嫩。', NULL, NULL, '僻羊肉750克、活鲫鱼500克、花椒3克、盐7.5克、绍酒20克、葱20克、姜15克、味精2克、芝麻油15克。');
INSERT INTO `recipe` VALUES (1524, 73, '银芽黄鱼素翅', '粉丝用沸水泡软，洗净，加入酱油、味精、干淀粉拌匀。虾仁斩成茸，加入蛋清、精盐、味精、水淀粉拌匀。将粉丝放在涂在麻油的长盘中，中间摊上宽约7厘米的虾茸，再铺上一层粉丝，两头修齐，用手抹平，使上下两层都粘住虾茸，上笼蒸3分钟，取出用刀从中间切成两段，在两段有虾茸粘住的地方直划9刀成10条，在没有虾茸的地方，用手撕开，即成形似鱼翅的针须，锅上火放油，放入绿豆芽，加盐、味精炒透，装入长盘。净黄鱼肉切成条，加入盐、味精、水淀粉上浆，放入六成热（约150度）的油中划油，倒出沥油，放在银芽上。炒锅上火，舀入鸡清汤，加酱', '素菜荤做，形态逼真。银芽香脆，鱼条鲜嫩，清爽适口，夏季食用最佳。', NULL, NULL, '绿豆芽300克、净黄鱼肉200克、绿豆细粉丝100克、虾仁50克、蛋清25克、酱油20克、味精2克、淀粉25克、盐5克、麻油15克、白糖7.5克、绍酒20克、熟猪油15克、鸡清汤150克、花生油500克（实耗油50克）。');
INSERT INTO `recipe` VALUES (1525, 73, '元蹄焖乌参', '1.将猪脚洗净，加调料焖煮至烂熟，起锅，每只对剖成4块。乌参洗净，对剖斜切成4块，备用。 \r\n2.烧热油锅，放葱姜爆香，倒入猪脚，加味精、糖、酱油、香油、汤，翻炒焖煮3分钟后，加入乌参焖煮2分钟，装碟前用生粉水勾芡即可。', NULL, NULL, NULL, '猪脚2只，乌参300克，葱2条，姜末少许，酱油1茶匙，糖1/2茶匙，香油少许，生粉适量，上汤2杯，用糖3大匙，味精1大匙，水6杯，八角6颗，桂皮少许，甘草少许，葱2根，姜1小段做成调料。');
INSERT INTO `recipe` VALUES (1526, 73, '糟扣肉', '将已出水的五花肉皮朝下，放入垫有竹箅的砂锅中，加酱油、白糖、绍酒、葱姜、猪肉汤，用圆盘压住，盖上锅盖，置中火上烧沸后移至小火焖半小时，将肉取出凉透，取碗一只，内倒少许酱油，将肉切成长9厘米、厚0.9厘米的片，皮朝上入碗内。把香糟用焖的原汁100克调匀，浇在碗内肉面上，用玻璃纸封口，上笼蒸1小时，取出，将肉翻扣在盘中即成。', '色泽酱红，酥烂入味，糟香诱人，肉肥而不腻，入口即化，几经复蒸而食则香味更足，口感更糯。', NULL, NULL, '猪五花肉750克、香糟45克，精盐12克、20、味精15克、白糖50克，绍酒25克、葱姜、各20克。');
INSERT INTO `recipe` VALUES (1527, 73, '枣方肉', '肋条肉刮洗干净，放入锅中出水至断血，捞出洗净，在肉皮一面剞上花刀。取砂锅1只，内垫竹箅，将肉皮朝下放入砂锅内。加猪肉汤、绍酒、酱油、冰糖和葱姜等调味品，用一个圆盘将肉压住，再盖上锅盖，置火上烧沸，后移至小火焖约1小时，将肉取出，皮朝下扣入碗中，倒入原焖肉卤汁。将红枣洗净煮烂，去掉枣皮和核，用刀面砸成枣泥，炒锅上火烧热，放油，投入枣泥、白糖炒匀起锅，把枣泥铺放在肉面上，用玻璃纸封口，上笼蒸约1小时取出，去掉封口纸。将肉翻扣在盘中即成。', '色红光亮，肉质肥烂，甜中带咸，枣泥香甜油润，食而不腻，风味别致。', NULL, NULL, '猪肋条肉一块（约1500克）、红枣50克、精盐10克、味精7.5克，绍酒25克、酱油30克、冰糖25克、葱姜各10克。');
INSERT INTO `recipe` VALUES (1528, 73, '掌上明珠', '鸭掌去爪尖，放入清水锅中煮至八成熟，捞出剔去掌骨，掌背撒上干淀粉，排放于盘中。将虾仁斩成茸，加精盐、味精、绍酒、鸡蛋清、干淀粉拌和成糊。将虾糊挤成圆球分别放在鸭掌上，虾球上缀以火腿末，冬菇末、青菜叶末、上笼蒸2分钟，取出。炒锅上火，舀入鸡汤，加精盐、味精烧沸，用水淀粉勾芡，淋入熟猪油，起锅浇在鸭掌上即成。', '色泽鲜艳，掌肉软韧，虾球鲜嫩适口。', NULL, NULL, '鸭掌450克、虾仁35克、熟火腿25克、水发冬菇15克、青菜叶35克、鸡蛋清15克、淀粉15克、盐5克、味精2克、绍酒25克、鸡汤150克、熟猪油15克。');
INSERT INTO `recipe` VALUES (1529, 73, '芝麻条', '1.将面粉放在案板上，中间扒一个凹窝，将鸡蛋打入窝中，放进芝麻、白糖、拌匀揉透。将揉好的面团用擀面杖擀成薄片(0.5厘米厚)，注意边擀边撒干淀粉，以免粘住。然后将擀好的薄片，切成5厘米长，0.5厘米宽的条待用。 \r\n2.将炒勺烧热注油，待油达7-8成热时，把芝麻条放入，炸至金黄色、松脆时捞出，冷透装盘即可。', '色泽金黄，味香酥脆。', NULL, NULL, '精白面粉250克，芝麻50克，鸡蛋3个，植物油900克(实耗约80克)，干淀粉50克，绵白糖150克。');
INSERT INTO `recipe` VALUES (1530, 73, '煮糟青鱼', '将鱼段皮朝下，在脊骨上横切三刀（骨断肉连），用精盐擦抹鱼段，腌6小时。香糟用绍酒调匀，涂抹在鱼肉上，然后放在钵中，上放重物压紧鱼段，腌12小时后，取出洗净。锅置火上放清水。放入“糟鱼”，加葱姜烧沸，撇去浮沫，加绍酒、精盐，加盖移微火焐10至15分钟，将鱼捞出，皮朝上装入汤锅中，原汤拣去葱姜，加入火腿片、冬菇片、笋片、菠菜，烧沸后加味精倒入鱼碗中，将配料排放在鱼段上，淋入熟猪油即成。', '糟香扑鼻，汤清味鲜，鱼肉细嫩，汤菜兼优。', NULL, NULL, '青鱼750克、熟火腿15克、水发香菇15克、熟冬笋片25克、菠菜30克，香糟15克、盐1.5克、葱10克、姜7.5克、绍酒25克、味精1.2克、熟猪油35克。');
INSERT INTO `recipe` VALUES (1531, 73, '紫菜鸭卷', '1.光鸭去内脏洗净，剔去脊骨，斩去鸭头、鸭腿，在剔去骨的肉内侧剞上交叉刀纹，加料酒、精盐、花椒搓腌2小时。 \r\n2.腌渍鸭肉上放紫菜、香菜梗，顺长卷起，用麻线捆扎紧，下入有花椒、葱段、姜片、料酒、精盐、味精、清汤制的卤水锅内，水沸后用小火焖2小时。 \r\n3.捞出沥去汤卤，周身抹上芝麻油，顶刀切成薄片，排入盘内。', '咸鲜软嫩，清香利口，佐酒佳肴。', NULL, NULL, '光鸭1只（约1200克），紫菜30克，香菜梗10克，葱、姜各10克，料酒30克，精盐5克，味精2克，花椒3克，芝麻油10克。');
INSERT INTO `recipe` VALUES (1532, 74, '白沙咸蛋卷', '先用冰肉把咸蛋黄卷成圆卷，撒上生粉，淋上鸡蛋液，再上面包糠，待炸，下油500克烧至90度，下咸蛋卷，炸熟斩件装盘，即成。', '酥香，入口就化，特色小吃。', NULL, NULL, '咸蛋黄50克，切好冰肉2张，生粉50克，面包糠50克，鸡蛋2个，色拉500克（实耗20克）。');
INSERT INTO `recipe` VALUES (1533, 74, '板栗煨鸡', '1.将净鸡剔去粗骨，剁成长、宽约3厘米的方块。板栗肉洗净沥干。葱切成3厘米的段。姜切成长、宽1厘米的薄片。\r\n2.炒锅内放入猪油，烧至六成热时，放入板栗肉炸呈金黄色，倒入漏勺沥油。\r\n3.炒锅内放入猪油，置旺火烧至八成热，下入鸡块煸炒，至水干，即烹入料酒，放入姜片、精盐、酱油、肉清汤，焖3分钟左右。\r\n4.用大瓦钵1只，放竹箅子垫底将炒锅内的鸡块连汤一齐倒入，置小火上煨至八成烂时，加入炸过的板栗肉，继续煨至软烂，再倒入炒锅，加味精、葱段，撒上胡椒粉，烧沸，用湿淀粉调稀勾芡，淋入香油，出锅装盘即成。', '熟板栗每100克约含蛋白质4.8克，碳水化合物44.8克，维生素C36毫克。中医认为其味甘性温，具有养胃健脾、补肾强筋、活血止血之功效。板栗煨鸡是湖南湘西地区的风味名菜。湘西出产的油板栗，经济价值高，营养较一般板栗丰富，有“中国甘栗”之美称，用以煨鸡更是锦上添花。鸡肉鲜嫩，板栗粉糯，鲜美可口。不过煨鸡的板栗必须先经油炸，否则松散成糊，大为失色。', NULL, NULL, '带骨鸡肉750克，板栗肉150克，熟猪油250克（实耗100克），精盐5克，味精1克，胡椒粉1克，料酒50克，葱15克，姜15克，酱油30克，湿淀粉25克，肉清汤750克，香油5克。');
INSERT INTO `recipe` VALUES (1534, 74, '爆炒血鸭', '1.将鸭宰杀后去净毛，鸭血入碗中，开膛取出内脏洗净，将鸭头取下，去嘴尖斩成3块（下颔至颈部1块，鸭头一劈两半），斩断鸭脚，去掉爪尖，取下鸭翅，去掉尾臊，再将鸭肉斩成2厘米见方的小块。\r\n2.将仔姜洗净。蒜瓣去皮洗净。芋荷切成2厘米长、0.5厘米粗的条。小红辣椒切末。 \r\n3.炒锅置中火上，放入猪油烧至七成热，先下入鸭头、翅、脚炸熟捞出。锅内留油，再下鸭肉煸炒收干水，倒入鸭血炒匀，然后，加入料酒、精盐炒至五成熟，依次放入酱油、肉清汤、仔姜、蒜瓣、芋荷、红椒末炒匀，焖5分钟，加味精，用湿淀粉调稀勾芡收浓汁，盛', '鸭肉丰满细嫩，肥而不腻，皮薄香鲜，且有药用价值。据《随息居饮食谱》载：鸭肉能滋五脏之阴，清虚劳之热，补血行水，养胃生津，止咳息惊，消螺蛳积。爆炒血鸭是以湖南著名大型鸭种－临武鸭精制而成。这种鸭体型大，生长发育快，肉质好，产蛋多。此菜以鸭血与鸭肉同炒，并辅以酸芋荷，色泽金黄，酸辣鲜香，是湖南郴州地区久负盛名的传统风味名菜。', NULL, NULL, '临武子母鸭1只（重约1250克），酸芋荷100克，小红辣椒50克，蒜瓣50克，仔姜100克，熟猪油100克，精盐5克，味精1.5克，料酒15克，酱油10克，湿淀粉15克，肉清汤200克。');
INSERT INTO `recipe` VALUES (1535, 74, '冰糖湘莲', '1.将白莲和纯碱下入冷水锅烧沸，用洗锅的刷把倾斜搓擦，到莲子皮易于褪去时，将碱水倒掉，换入温水，用双手捧莲子搓擦，再用清水冲洗2次，将莲子皮洗净为止。然后放冷水将去皮莲子烧沸捞出，装入碗内，用沸水泡上，使碱味全部吐出，用牙签从莲子下部将莲心抵出，再用沸水冲洗1次，装入碗内，加入温水，上笼蒸发至软（蒸的时间不宜太久，否则蒸溶了会不成颗粒）。桂圆肉用温水洗净，浸泡5分钟，将水滗去，待用。\r\n2.炒锅置中火上，放入清水500克，下入冰糖溶化，端锅离火，用箩筛滤去糖渣，再将冰糖水倒回锅内，加青豆、樱桃、桂圆肉，上', '湘莲营养丰富，莲肉每100克含蛋白质17克，脂肪2克，碳水化合物66.8克，粗纤维2.8克，钙100毫克，磷220毫克，铁5.6毫克。李时珍的《本草纲目》介绍：“莲子，补中养神，益气力，除百病。久服轻身耐老，不饥延年……”。中医认为，莲子味甘涩，性平，入心、肾、脾三经，有养心、益肾、补脾、涩肠之效。在挖掘长沙马王堆汉墓时，发现过两千多年前当地人就食用过莲心。莲子作菜，在明清以前就比较盛行，不过当时制作比较简单，最早称为“糖莲心”，到近代因取用冰糖制作莲心，故称“冰糖湘莲”。此菜汤清莲白，甜糯清香，而且莲子', NULL, NULL, '湘白莲200克，青豆25克，红樱桃25克，桂圆肉15克，冰糖300克，纯碱15克。');
INSERT INTO `recipe` VALUES (1536, 74, '玻璃鲜墨', '1.将鲜墨鱼改刀为长约10厘米、宽5厘米的块，从1/2处剞十字花刀，其余部分改为夹刀片后，顺刀切成鱼尾状。\r\n2.将加工好的鱼片用开水焯好码在盘中。\r\n3.起锅放清油、鸡汤、姜汁、盐、味精、胡椒粉烧开，少许水淀粉勾玻璃芡，淋明油，将汁浇在码好的墨鱼片上，临少许鸡油即可。（盘中可作点缀 ，主料用水焯时稍稍起卷即出勺，剞刀使进刀要深。', '味道鲜美，咸香适口。', NULL, NULL, '鲜墨鱼300克，盐2克，味精2克，胡椒粉1克，水淀粉、姜汁各少许，鸡汤、清油、鸡油各适量。');
INSERT INTO `recipe` VALUES (1537, 74, '朝珠八宝水鱼', '1.葱、姜拍破。蒜瓣剥去皮洗净。白菜苞洗净。五花肉切成片，下入沸水锅氽过，洗净血沫。海参切成小指头大的丁，下入汤锅加精盐、料酒烧沸捞出。胡萝卜削去皮切成短筒，再削成圆球形，下入油锅焖炸熟待用。\r\n2.将鸡脯肉、火腿、冬笋、冬菇、金钩都切成海参一样大小的丁，下入油锅煸炒出香味，烹料酒，加精盐、味精、胡椒粉、海参丁、莲子拌成馅。\r\n3.将水鱼宰杀，随后将尾向上，使血液流尽，用沸水烫一下，褪出黄膜衣，再用沸水烫一次，刮净裙边和背壳上的黑膜，用刀取下背壳待用，去内脏和喉管、气管、脚爪，再下入沸水锅氽过捞出，洗净血', '朝珠八宝水鱼，是湖南省委接待处蓉园宾馆特一级烹调师聂厚忠，在借鉴传统名菜“红煨八宝鸡”的基础上研制的创新菜，曾在第二届全国烹饪技术比赛中获铜牌奖。此菜不仅造型美观，而且原料丰富。在水鱼肚内灌入海参、鸡肉、火腿、冬笋、金钩、冬菇、莲子等调制的馅料，并在蒸制的过程中，使各种原料互相调和融汇，集多种营养成分和多种口味于一体，很受宾客的赞赏。', NULL, NULL, '活水鱼1只，重约1500克，猪五花肉500克，鸡脯肉50克，火腿50克，水发金钩30克，水发冬菇50克，净冬笋50克，水发海参50克，蒸发莲子50克，胡萝卜250克，蒜瓣50克，小白菜苞16个，熟猪油125克，料酒50克，精盐8克，味精2克，胡椒粉5克，葱15克，湿淀粉40克，鸡清汤250克，鸡油10克。');
INSERT INTO `recipe` VALUES (1538, 74, '潮州冻花芥', '先将大花蚧腌死（不脱壳，以保持原味），用生姜、葱、味精、盐、绍酒在大花蚧身上腌制30分钟，放上蒸笼蒸熟，捡去姜、葱，再放入冰箱冻60分钟，取出，装盘，上菜时配姜米浙醋2碟。', '是夏令佳肴，配上酱碟，别有风味。', NULL, NULL, '大花蚧母2只（每只约600克），生姜1块，葱2根，味精、盐、绍酒各适量。');
INSERT INTO `recipe` VALUES (1539, 74, '炒素什锦', '1.鲜蘑去蒂，刀切进1/2深，用手按一下成扇状。香菇切梅花状。黄瓜、胡萝卜均切2厘米段，削边成蝶状。西红柿去皮切菱形片。姜去皮切锯齿片。西兰花掰成小朵。玉米笋切段。马蹄、窝笋、紫菜头均削成球状。 \r\n2.全部主配料用开水焯一遍。\r\n3.起锅放少许油，烧热，投入全部主配料，加入鸡汤及调料翻炒，用水淀粉勾芡，淋明油出锅。', '颜色美观，口味清香微辣。', NULL, NULL, '鲜蘑40克，香菇40克，黄瓜40克，胡萝卜40克，西红柿40克，姜40克，西兰花40克，玉米笋40克，清水马蹄40克，莴笋40克，紫菜头40克，盐、味精、姜汁、水淀粉各适量，胡椒粉3克，油50克，鸡汤适量。');
INSERT INTO `recipe` VALUES (1540, 74, '豉椒划水', '1.划水用盐腌约10分钟，用湿淀粉、鸡蛋液上浆。\r\n2.起锅放油烧至八成热，下入划水炸至金黄色，捞出。\r\n3.锅留底油，放干辣椒爆香，放豆豉、划水、配料、调料煸炒，放鸡汤用微火焖透，淋水淀粉够芡出锅装盘。', '豆豉味香浓，色泽洪亮。', NULL, NULL, '草鱼或鲤鱼，划水300克，豆豉10克，干辣椒2克，笋片10克，青蒜适量，盐2克，酱油4克，醋2克，白糖3克，味精2克，料酒4克，淀粉10克，油80克，鸡蛋2个，鸡汤适量。');
INSERT INTO `recipe` VALUES (1541, 74, '醋焖鸡三件', '1.将鸡肫片切开成2块，撕去内筋，洗净，每块在肉厚的部位横剞几刀，再切开成2小块。将鸡翅从中间骨节处砍开成2段；鸡脚爪去皮、去爪尖，一齐投入沸水中氽过，除去腥味，再投入冷水中清洗1次，沥去水，与鸡肫一起盛入瓦钵中。\r\n2.将干红辣椒切成细末，放入盛鸡三件的瓦钵内，加入黄醋、料酒、精盐、姜末，入笼干蒸1小时，至质地柔软时，取出。\r\n3.炒锅置中火上，放入熟猪油，烧至六成热，倒入鸡三件和原汁，焖2分钟，再放入味精、葱花，用湿淀粉勾芡，出锅装盘即成。', '醋焖鸡三件，是湖南的一道传统风味菜。鸡三件，即鸡肫、鸡爪和鸡翅，是鸡身上风味各异的部位，最宜佐酒。以三件醋焖，鸡肫质韧脆嫩，鸡爪柔爽滑糯，鸡翅软烂浓厚，且酸辣味突出，富有地方风味。', NULL, NULL, '生鸡肫250克，生鸡脚爪200克，生鸡翅膀250克，熟猪油75克，料酒50克，精盐3克，味精1克，葱花10克，姜末10克，干红辣椒2.5克，黄醋50克，湿淀粉25克。');
INSERT INTO `recipe` VALUES (1542, 74, '翠竹粉蒸鮰鱼', '1.取直径10厘米、长25厘米、两端竹节的翠竹筒1节，离竹筒两端约4厘米处，横锯2条、再破成宽10厘米的口，破下的竹片作筒盖。\r\n2.将鮰鱼从腹部剖开，去内脏，洗净沥干。切成5厘米长，3厘米宽、2厘米厚的长方形块，再用水清洗一闪，沥干水放入大碗。加原汁酱油、豆瓣酱、胡椒 粉、五香粉、甜面酱、花椒粉、精盐、白糖、白醋、绍酒、味精、芝麻油、辣椒油、葱、姜末拌匀，然后加入米粉、熟猪油拌匀，腌5分钟，再将腌好的鱼放入竹筒，盖上筒盖，上笼蒸20分钟取出。用托盘竹上席，揭去盖即成。', '鮰鱼，又名肥坨黄鲟鱼，亦称肥坨鱼，背侧灰黄色，无鳞片，盛产于洞庭湖区，以岳阳一带居多。传说鮰鱼原为天上监管鱼族之神，下凡，被班次皇大帝夺在长江大石之下。有一天，黄鹤戏掠江面，听江中呼救遂潜江底，见鮰鱼被压十分同情，便向玉帝准奏，免去鮰鱼的苦役。鮰鱼入馔历史悠久，苏东坡《戏作鱼回鱼一绝》云：“粉红石首仍无骨，雪白河豚不药人，寄与天公与河伯，又见鮰鱼上市时。”的诗句。此菜选用新鲜翠竹筒，盛鱼后密蒸之。既保留了粉蒸鮰鱼的传统风味，又增加了翠竹本身的淡淡清香。菜型别具一格，回味悠长。', '选用洞庭未产子的鮰鱼，2500克左右为佳。鮰鱼名贵，上颌和尾部同时选用，以免顾客误认为蛇鱼代替。鮰鱼处理后，挤出涎窝，斩去边鳍，连同头尾一起剁块。', NULL, '母G鱼1尾，白醋5克，熟米粉100克，绍酒5克，五香粉10克，味精1克，原汁酱油15克，葱5克，豆瓣酱25克，姜5克，甜面酱15克，精盐1克，胡椒粉1克，芝麻油30克，花椒粉1克，辣椒油30克，白糖1.5克，熟猪油40克。');
INSERT INTO `recipe` VALUES (1543, 74, '大边炉', '1.将猪腰片剖开，剔去腰臊，鸡脯肉、猪瘦肉、桂鱼肉分别切成4.5厘米长、3厘米宽、0.2厘米厚的薄片。油条从中分开，切成6厘米的段。馓子分散。鸡蛋洗净。冬笋切成薄片。香菇去蒂洗净。排冬菜洗净切碎。将鸡脯肉，猪瘦肉、桂鱼肉、猪腰分别盛入四个盘里。\r\n2.炒锅置旺火上，下熟猪油，烧至六成热时，将油条、馓子、粉丝分别下锅，炸至焦脆捞出分别盛入三个盘里。鸡蛋放入一只大瓷盘里。味精2克、胡椒粉和精盐2.5克、香蒜、熟猪油50克等调味均分别盛在各小碟中。\r\n3.炒锅旺火烧热，下熟猪油50克、烧至六成热时，先下冬笋片炒', '此菜多料多味，荤素俱全，食物鲜嫩，汤烫味鲜，别有风味。', NULL, NULL, '鸡脯肉、桂鱼肉、猪瘦肉、猪腰、鱼丸各100克，鸡蛋12个，水发香姑、冬笋各50克，油条2根，油炸馓子2个，湘粉丝25克，菠菜、芽白菜、冬苋菜、白菜心各100克，排冬菜25克，青蒜丝25克，熟猪油250克（实耗125克），酱油25克，味精1.5克，胡椒粉1克，精盐6克，鸡清汤1250克。');
INSERT INTO `recipe` VALUES (1544, 74, '丁香烤羊腿', '1.羊腿洗净，用竹扦在两面戳10个孔，放入精盐、味精、胡椒粉、葱姜揉透，腌渍1小时。\r\n2.将羊腿放入烤盘，丁香镶入肉孔内，再放入花生油和清水共250克，置烤盘于烤箱内，用温水烤至汤干，肉熟呈金黄色，取出刷上芝麻油，全腿上桌。亮相之后，切片装盘，在盘两端用黄瓜片点缀，跟烧饵、面酱碟、葱白段碟上桌。', '云南回族自元代大规模迁入后，定居农耕，食俗起了明显变化。烤羊腿是北方烤制的基础上，经过回族厨师增用丁香、花生油和水，入烤炉烹制而成。此菜色泽金黄，异香扑鼻，酥烂软嫩，用烧饵夹入烤羊腿肉，既有北方风味，又有南方特点。', '烧饵：是用大米煮熟为饭，取出趁热捣为泥，捏成团，擀成直径15厘米、厚0.5厘米的圆饼即为饵。放在栗炭火上烘烤起泡取下，抹上芝麻花生酱、辣椒油、甜酱油即食。亦可夹上烤羊肉或卤牛肉、油条，则风味更佳。', NULL, '山羊后腿1只3000克，姜末30克，甜面酱20克，黄瓜片50克，葱白段80克，烧饵12个，胡椒5克，盐20克，丁香10颗味精10克，花生油200克，葱末30克，芝麻油20克。');
INSERT INTO `recipe` VALUES (1545, 74, '东安子鸡', '1.宰杀母鸡，拨除鸡毛，用干稻草火焚净细小绒毛，在鸡的食袋旁切口，拉出食袋，在鸡的肛门切4.5厘米口，掏出内脏，清洗干净。\r\n2.将净鸡放入汤锅内煮约8至10分钟，达七成熟捞出，剁去头、颈、脚爪作它用。 \r\n3.将粗细骨全部剔除，把鸡胸、鸡腿分开，顺肉纹切成5厘米长、1厘米宽的长条。', NULL, NULL, NULL, '嫩母鸡1只，姜25克，干红辣椒10克，清汤100克，黄醋50克，湿淀粉25克，绍酒25克，味精1克，葱5克，熟猪油100克， 香油2.5克。');
INSERT INTO `recipe` VALUES (1546, 74, '洞庭金龟', '1.将龟肉下开水烫过，除去薄膜，剁去爪尖，洗净滤干，切成3厘米长、2厘米宽的块，猪五花肉切成3厘米长、1厘米宽、0.2厘米厚的片，冬笋切成梳形片，香菇去蒂洗净，大的切开，葱打结，姜去皮、拍破，香菜洗净。\r\n2.炒锅置旺火，放入熟猪油，下入葱、姜煸出香味，随即下入龟肉、五花肉煸炒，烹入绍酒、酱油，放入桂皮、八角、干红椒、精盐、白糖、适量清水烧开，撇开泡沫，倒入炒锅，移到小火上煨1小时至龟肉软烂，再加入笋片、香菇、味精，撒上胡椒粉，淋入芝麻油，盛入汤盆中，香菜盛入小碟同时上桌。', '“洞庭金龟”是岳阳“味腴酒家”的传统名肴。龟品种多样，有水龟、金线龟、泥龟元绪、金头龟。要求个大、肉肥的活体为佳。其性甘、平、咸，龟肉滋阴降火、补血健胃，对虚脱虚喘、崩漏失血、子宫脱垂、小儿遗尿有益。此菜咸鲜香辣汤稠红肉，醇厚浓郁，悠长隽永，是滋补佳品。', '金锅腹部朝上、盖朝下，待它将头伸出时，迅速用刀将头斩下，再使头腔朝下，控净血，两侧用铁捶打破，先用刀尖割下龟肉，去掉底板，再去上壳和内脏，洗一遍。此菜煨制而成，加热时间较长，要将盖盖严，中途不可再加汤和调料，熟时再打加锅盖，食其原汁原味，砂锅底放一竹箅子以防糊底。选用湖南洞庭产金龟，其腹部呈金黄，色彩斑斓，肉质鲜美细嫩，营养价值高于其它龟类。', NULL, '金龟1000克，八角1克，猪五花肉150克，干红椒5克，冬笋50克，葱15克，水发香菇25克，姜15克，香菜50克，白糖1克，酱油25克，味精1克，绍酒25克，芝麻油20克，精盐1克，胡椒粉0.5克，桂皮2克，熟猪油50克。');
INSERT INTO `recipe` VALUES (1547, 74, '洞庭野鸭', '1.冬笋用刀滚切成块。地菜摘洗干净。葱白切成段，余下的葱、姜拍破。\r\n2.野鸭干拔净粗毛，剁去头和脚爪，放在火上烧尽绒毛，用温水泡上刮洗干净，由背脊骨开膛去内脏，去净骨，剁成4厘米大的方块，放入冷水锅内煮熟捞出，清洗2次，以除去血腥异味，然后用漏勺沥干水分。\r\n3.将花生油烧沸，放入葱、姜煸炒，随下入野鸭块煸出香味，烹料酒，再下入冬笋块、精盐、白糖、酱油和汤，烧沸后装入砂锅内，用小火煨1小时左右，至酥烂为止。\r\n4.食用时，在锅内放入油，烧至六成热时，下入地菜加精盐炒一下，用来拼边，然后将野鸭肉和冬笋倒入', '野鸭体内含有丰富的蛋白质、碳水化合物、无机盐和多种维生素。中医认为，野鸭味甘性晾，入脾、胃、肺、肾经。具有补中益气、清食和胃、利水解素之功效。野鸭是一种迁徙性候鸟，一般是春夏在北方繁殖，秋冬在南方越冬。湖南洞庭湖一带即是野鸭常来栖息越冬的地方，人们捕而食之遂得名。此菜是湘北地区和洞庭湖区的风味名肴，色泽酱红，质地酥烂，味道香鲜。尤其野鸭肉与地菜（即荠菜）拌吃，更是别有风味。', NULL, NULL, '鲜野鸭1只（重约1500克），净冬笋200克，地菜150克，花生油150克，料酒30克，精盐8克，味精2克，胡椒粉1克，葱15克，姜15克，桂皮10克，酱油25克，白糖5克，湿淀粉25克，普汤500克，香油15克。');
INSERT INTO `recipe` VALUES (1548, 74, '洞庭玉珠水鱼', '1.将水鱼宰杀，斩去头和爪尖，用沸水烫一下，刮去薄膜，下冷水锅中煮至六成熟取出，去掉背壳、底板和内脏，剔除腿上粗骨，清洗干净。将水鱼肉剁成长宽约3厘米的块，裙边切成3厘米的条，盛入瓦钵中，用料酒、精盐腌10分钟，再用清水洗净，沥干水，然后将水鱼肉整齐放入瓦钵内，上面放水鱼裙边，依次将白胡椒、精盐、蒜瓣、肥膘肉、葱结、姜片放在水鱼上面，再烹入料酒、熟猪油，入笼蒸1小时至软烂。\r\n2.将鸡脯肉切成约2厘米见方的丁，用精盐抓匀，腌5分钟。将鸡蛋清磕入碗内，用力搅发呈泡沫状，放入百合粉调匀成泡糊，再放鸡丁拌匀。炒', '洞庭玉珠水鱼，是涉外三星级宾馆湘江宾馆特一烹调师周伯安研制的创新菜，是将湖南的传统名菜原蒸水鱼和清汤滑鸡球两菜加以取舍组合而成。此菜可谓色味形俱佳。一是装盘美观：水鱼半圆居中，菜苞中圈簇拥，外围众星拱月。二是色菜柔和：水鱼红褐，菜苞碧绿，鸡球玉白，三色相间，交相辉映。三是质味丰富：鸡球滑嫩柔软，落口消溶；水鱼肉质细腻，腴肥鲜美；菜苞脆嫩鲜软，清淡爽口。一境外客人食之称曰：新的“霸王别姬”也。', NULL, NULL, '活水鱼1只（重约1000克），鸡脯肉100克，猪肥膘肉100克，白菜苞20个，鸡蛋清2个，熟猪油750克（实耗50克），料酒25克，精盐5克，味精1克，葱结15克，姜片15克，蒜瓣15克，胡椒粉1克，白胡椒12粒，百合粉50克，湿淀粉10克，鸡清汤250克。');
INSERT INTO `recipe` VALUES (1549, 74, '独鱼腐', '1.将刀鱼肉剁成细泥，加清水50克调匀，用细萝过掉渣滓。再加入清水100克和精盐，朝一个方向搅成较稠的鱼肉糊。把鸡蛋清打散，与熟鸡油15克一起放入鱼肉糊里搅匀。然后，把鱼肉类有陆续舀在碟子边，拨成直径3.3厘米、长8.3厘米的贺形长条，轻轻推到三成热的温水中，上火加热即成鱼腐。待水烧到八九成开（刚翻起小泡）时，将鱼腐翻个身，端下锅，用锅里的热水浸泡待用。\r\n2.将熟鸡丝25克放入炒锅内，置于旺火上烧到六成热时，下入葱丝、姜丝，炸成金黄色，再烹入绍酒和酱油，倒入鸡汤，熬20秒种，捞去葱丝、姜丝，把鱼腐从热水', '清朝末年，天津“鸿宾楼”饭庄创办开业，其精美菜肴“火笃鱼白”等，引得津门豪绅巨富纷纷前来，一快朵颐。但鱼白数量委少，有时从一大堆鱼中仅能找到几条雄鱼取鱼白，难以应市。该店名厨们屡经试制终于想出新招：将刀鱼剥皮去骨剁成鱼泥，制成牛舌状，代而烹之，名曰独鱼腐。结果，不仅其外形极似鱼白，而且色泽亦假乱真。因其质地柔嫩犹如豆腐，遂定名为“鱼腐”。此物入口后，用舌尖一抿即散开，尤宜老年人进食。', '烹制时，用微火将鱼火靠熟，并使各种调料味道浸入鱼中，碾要色呈酱红，有光泽，又软又嫩，有似豆腐。', NULL, '鲜河刀鱼肉250克，酱油10克，鸡蛋清1个，葱丝5克，糖色10克，绍酒15克，湿淀粉10克，精盐1.5克，鸡汤100克，味精1.5克，姜丝2.5克，熟鸡油65克。');
INSERT INTO `recipe` VALUES (1550, 74, '发丝百叶', '1.将生牛百叶分割成5块，放入桶内，倒入沸水浸没，用木棍不停地搅动3分钟，捞出放在案板上，用力搓去上面的黑膜，以清水漂洗干净，下冷水锅煮1小时，至七成烂捞出。\r\n2.将牛百叶逐块平铺在钻板上，剔去外壁，切成约5厘米长的细丝盛入碗中，用黄醋10克、精盐1克拌匀，用力抓揉去掉腥味，然后用冷水漂洗干净，挤干水分。\r\n3.玉兰片切成略短于牛百叶的细丝。葱切成2厘米长的段取小碗1只，加牛清汤、味精、芝麻油、黄醋10克、葱段和温淀粉兑成芡。\r\n4.炒锅置旺火，放入茶油，烧至八成熟，先把玉兰片丝和干椒末下锅炒几下，随下', '长沙百年老店李合盛以烹制牛肉菜闻名。其中红烧牛蹄筋，烩牛脑髓和发丝百叶尤为出色，誉为“牛中三杰”。发丝百叶是三杰中的佼佼者。百叶，是牛肚内壁中皱褶的部位，经切丝争火爆炒，具有脆嫩、鲜香的独特风味。今李合盛老店虽已歇业，但发丝百叶一菜则在长沙市各大餐馆广为流传，以曲园酒家制作最佳。', '本品注重刀工，百叶切得愈细愈好。火旺油热，迅速煸炒，烹汁后颠翻几下，立即出锅。', NULL, '生牛百叶750克，湿淀粉15克，水发玉兰片50克，味精1克，干红椒末1.5克，精盐3克，牛清汤50克，芝麻渍2.5克，葱段10克，熟茶油100克，黄醋20克。');
INSERT INTO `recipe` VALUES (1551, 74, '芙蓉脆皮乳鸽', '1.姜切末。葱切花。干辣椒切末。蒜瓣捶成茸。饴糖兑成汁。\r\n2.将乳鸽宰杀去毛、去内脏，清洗干净，放入汤锅中烫熟取出，擦干水分，抹上饴糖汁，再将乳鸽挂起晾干。\r\n3.炒锅内放油，烧至八成热，下入乳鸽吞炸至色泽红亮、皮脆、肉香、熟透捞出。再将炸好的乳鸽改切成直径为3厘米大小的块，在盘中拼摆成乳鸽形，周围摆上香菜叶。\r\n4.炒锅置旺火上，放油烧至七成热时，下入姜末、蒜茸、红椒末、喼汁、生抽、沙司、蚝油、美极鲜酱油、白糖、米醋、精盐、味精、鸡汤、葱花、香油烧沸，调成汁，装入2只小碟内，随乳鸽上席即成。', '鸽肉营养丰富，易于消化，每100克含蛋白质22.14克，脂肪仅含1克，属高蛋白、低脂肪食品，其所含微量元素和维生素亦高于鸡肉，且有滋肾益气、祛风解毒之功能，故民间有“一鸽当三鸡”的说法。芙蓉脆皮乳鸽系由湖南省涉外旅游宾馆—芙蓉宾馆特二级烹调师冯海泉，在传统名菜“油淋庄鸡”的基础上精心烹制而成。乳鸽经蒸制、吞炸后，香气四溢，酥润鲜美，另以16种调料烹制成浓汁佐食，味感丰富，风味别致。', NULL, NULL, '肥嫩乳鸽2只，重约600克，饴糖5克，香菜叶5克，蒜瓣5克，花生油1000克（实耗50克），精盐5克，味精1克，姜2克，葱3克，干红辣椒5克，米醋5克，白糖10克，喼汁5克，生抽5克，沙司10克，蚝油10克，美极鲜酱油5克，鸡汤50克，香油5克。');
INSERT INTO `recipe` VALUES (1552, 74, '芙蓉鸡片', '1.将鸡柳条肉剔去筋，用刀和刀背捶剁成极细的鸡茸，用凉鸡汤解散，加入鸡蛋清、精盐和湿淀粉搅拌成浓汁。\r\n2.冬菇去蒂洗净，解切成片。菜苞摘洗干净。蕃茄用沸水烫过，撕去皮，切开去籽切成片。冬笋切成薄片。\r\n3.炒锅洗净置火上烧热，放入猪油来回晃动，使整个锅底淌上油，待油热时，用炒瓢舀鸡茸汁，沿锅周围倒入，待鸡汁形成滑嫩的大鸡片时取出，装盘摊开晾凉，改成菱形片待用。\r\n4.食用时，将炒锅内猪油烧至六成热时，下入冬笋煸炒，随后下入冬菇、蕃茄片、白菜苞、鸡汤、精盐、味精和滑熟的鸡片，轻轻推动几下，用湿淀粉调稀勾芡', '湖南素称“芙蓉国”，毛泽东主席曾有“芙蓉国里尽朝晖”的赞誉。厨师以蛋清象征白芙蓉，取之与鸡茸调匀，在油锅中烫成大鸡片，再改切成菱形，与冬笋、冬菇、白菜苞同烹，制成富有地方色彩的芙蓉鸡片这一美味。此菜色泽鲜艳，形状美观，软滑鲜嫩，清淡爽口，极易吸收消化，老人尤为适宜。芙蓉鸡片制作的关键，在于掌握烫鸡片的火候：时间长了鸡片易老，不白不嫩，黯然寡味；时间短了成品松散，不整不齐，难圆片型。', NULL, NULL, '鸡柳条肉250克，净冬笋50克，水发冬菇50克，红蕃茄200克，白菜苞10个，鸡蛋6个，熟猪油200克，料酒25克，精盐5克，味精2克，胡椒粉0.5克，湿淀粉50克，凉鸡汤200克，鸡油15克。');
INSERT INTO `recipe` VALUES (1553, 74, '芙蓉鲫鱼', '1.鲫鱼去鳞、鳃、内脏，洗净，斜切下鲫鱼的头和尾，同鱼身一起装入盘中，加绍酒和拍破的葱姜，上笼蒸10分钟取出，头尾和原汤不动，用小刀剔下鱼肉。\r\n2.将蛋清打散后，放入鱼肉、鸡汤、鱼肉原汤，加入精盐、味精、胡椒粉搅匀，将一半装入汤碗，上笼蒸至半熟取出，另一半倒在上面，上笼蒸熟，即为芙蓉鲫鱼，同时把鱼头、鱼尾蒸熟。\r\n3.将芙蓉鲫鱼和鱼头鱼尾取出，头、尾分别摆放芙蓉鲫鱼两头，拼成鱼形，撒上火腿末、葱花，淋入鸡油即成。', '鲫鱼是一种小型广适应鱼类，除青藏高原外，广布于全国各水系水体中，喜栖在水草丛生的浅水河湾湖泊中。在不同的地区生长速度不同，长江中下游的鲫鱼，一般在250克左右，大的可达1250克。鲫鱼四季皆产，以春、冬两季肉质最佳。洞庭湖区盛产荷包鲫鱼，肥胖丰腴，形似荷包，质地细嫩，甜润鲜美，是鱼类中的上品。此菜即以荷包鲫鱼为主料，配以蛋清同蒸，味道鲜嫩，落口即溶，是湖南传统名菜。', '鲫鱼不可久蒸，以10分钟为度，蒸的时间过长，肉死刺软，不易分离，鲜味尽失。', NULL, '鲜鲫鱼2尾750克，胡椒粉0.5克，鸡蛋清5个，葱25克，熟瘦火腿15克，姜15克，绍酒50克，鸡清5克，鸡油15克，味精2克。');
INSERT INTO `recipe` VALUES (1554, 74, '芙蓉鱼排', '1.将才鱼肉片成薄片，长3厘米、宽3厘米。葱、姜捣烂用料酒取汁，加精盐、白糖、味精和胡椒粉，把鱼片腌10分钟。\r\n2.面包切成鱼肉一样大小、数量的片。鸡蛋取清用筷子打起来发泡，加入干淀粉搅拌成雪花蛋糊。香菜洗净。火腿切末。\r\n3.面包摊开放在木板上，把鱼片逐一裹上雪花糊，贴在面包上，再贴上香菜、火腿末。\r\n4.炒锅置旺火上，将油烧至五成热，把贴好的鱼片、面包朝下下入油锅。面包炸焦酥呈金黄色时鱼片已熟，翻炸一下即捞出，淋香油，装盘即成。', '才鱼，学名乌鳢，又称乌鱼、生鱼，其肉质厚实紧密，成熟后色白而较嫩，少刺。其肉每100克约含蛋白质18.8－19.8克，脂肪0.8－1.4克。中医认为其味甘性寒，具有健脾利水、益气补血、通乳等功效，对创伤愈合亦有良好疗效。此菜造型美观，色彩艳丽，有焦脆鲜嫩、咸香等多种味道，真是既好看，又好吃的创新佳肴。', NULL, NULL, '净鱼肉（可用桂鱼或草鱼）300克，咸面包200克，熟火腿15克，香菜叶50克，鸡蛋4个，花生油1000克（实耗100克），料酒25克，精盐2.5克，味精1.5克，胡椒粉0.5克，葱15克，姜15克，白糖1.5克，干淀粉50克，香油15克。');
INSERT INTO `recipe` VALUES (1555, 65, '参杞羊头', '将党参、山药分别洗净后，焖软切片。枸杞去杂质。将羊头皮面燎去绒毛，放入温水中刮洗干净，砍成两半，取出羊脑，洗净血水，放入锅内，加清水和鸡蛋壳，煮至羊头熟，取出洗净。再将羊头放入锅内，加清水适量。下陈皮、火腿、荸荠，武火烧沸，撇去浮沫，移炆火上炖熟，取出羊头、拆骨后切成条块。将荸荠、火腿切片，放入盆内。羊肉块放在荸荠上面。党参、枸杞、山药洗净后，放在羊头肉上面，加入原汤，加盖上笼蒸1小时左右取出，用食盐、味精调味即成。', '头肉肥烂，荸参软糯，汤汁清鲜，并具有补脾益肾之功效。', NULL, NULL, '羊头一只（约400克）、党参18克、枸杞10克、陈皮10克、山药24克、荸荠60克、火腿30克、鸡蛋壳3个、上汤500克、食盐5克、味精1克。');
INSERT INTO `recipe` VALUES (1556, 74, '腐乳冬笋', '冬笋切片，起锅放少量油烧热，倒入腐乳汁，投入冬笋，加调料翻炒，淋明油出锅即成。', '脆爽适口，腐乳香味浓郁。', NULL, NULL, '冬笋200克，腐乳汁10克，味精2克，盐2克，油少许。');
INSERT INTO `recipe` VALUES (1557, 74, '干蒸湘莲', '1.干莲子180克用温水稍泡2－3分钟。捞出放入锅中，添清水烧开，加食用碱少许，用刷子反复搓洗，去掉莲子红皮。然后用温水换洗几次，去净碱味，洗净捞出，用小刀切去两头的尖，捅去莲心，用开水氽煮一下，捞出放碗内，加白糖和开水 适量，上笼蒸至六成烂取出晾冷备用。\r\n2.糯米120克淘洗干净，用开水略氽煮，捞出，再用旺火蒸透。\r\n3.扣碗内抹上油，将莲子孔向下码入碗内，把冰糖120克撒在莲子上。另外把糯料饭加熟油、白糖、桂花拌匀，取大部分放在莲子上摊平，中间稍凹，放入豆沙馅，将剩余小部分放在莲子上，入蒸笼1小时，', '具有补肾健脾、养心安神之功能。适用于失眠、心悸、遗精、健忘等到患者食用。', NULL, NULL, '干莲子、糯米、豆沙馅。');
INSERT INTO `recipe` VALUES (1558, 74, '红炖鱼翅', '1.用炖钵（瓦缶掌）先落竹筷，加竹箅垫底使用。\r\n2.用锅下沸水2500克，加入绍酒50克、精盐10克、姜20克、葱25克，投入翅针一起滚5分钟，捡去姜、葱不用，把翅捞起装入炖钵内。\r\n3.猪手、花肉、排骨、各斩开与猪皮起锅炒香，溅绍酒50克，加入二汤、精盐10克、糖色，然后倒入鱼翅钵内，加姜25克，葱25克、香菜头、火腿脚。加盖，用木炭炉以武火火靠3个钟头，捡去猪手、花肉、猪皮、排骨、火腿脚和姜、葱、香菜头，然后将光老母鸡开两边盖在翅面，转为文火火靠炖1个半钟头，最后转为慢火火靠炖1个半钟头，然后捡去老', '翅针软滑，香味浓郁。', '潮州红炖鱼翅，净火靠炖时间要6个小时，6个小时分为三个阶段：前3个小时武火，是因鱼翅胶质不浓，中1个半小时文火，是因鱼翅开始有胶质，后1个半小时火力更弱，是因鱼翅胶质大，将近收汁，保持火路似滚不滚的状态，这样才能保证既够火，又不烧焦。泡发鱼翅过程是，先将鱼翅的翅尾用剪刀剪掉3毫米，然后再用清水浸6个小时，后用沸水慢火煲，到翅皮的沙能洗得出为度。把鱼翅连原水倾倒于盆中泡浸，候水冷至可以下手时用小刀刮洗干净。然后再用沸水慢火把鱼翅再煲，至可以脱骨时，把鱼翅捞起落清水中浸之。然后拆去鱼翅的硬软骨，和翅脚的皮臭肉', NULL, '洗净翅针100克，光老母鸡1250克，花肉750克，排骨500克，猪手750克，猪皮250克，绍酒100克，糖色15克，精盐20克，酱油5克，生葱50克，香油50克，生姜50克，香菜50克，银针100克，火腿丝25克，火腿脚50克，麻油10克，胡椒粉1克，浙醋2小碟，上汤1000克，二汤3000克， 味精10克。');
INSERT INTO `recipe` VALUES (1559, 74, '红椒酿肉', '猪肉剁成泥，虾、香菇洗净剁碎，加肉泥、鸡蛋、味精、盐调淀粉成馅；泡红椒在蒂部切口去瓤，填入肉馅，用湿淀粉封口，炸至8成熟捞出；底朝下码入碗内，撒上蒜瓣上笼蒸透，滗出原汁翻扣在盘中。原汁加调料勾芡淋在红椒上即成。', NULL, NULL, NULL, '泡红鲜椒500克，猪肉300克，金钓虾、水发香菇各15克，鸡蛋1个，蒜瓣50克。');
INSERT INTO `recipe` VALUES (1560, 74, '红烧龟肉', '1. 将龟放入盆中，加热水（约40度），使其排尽尿，然后剁去其头、足，剖开，去龟壳、内脏，洗净，将龟肉切块。\r\n2. 锅中加菜油，烧热后，放入龟肉块，反复翻炒，再加生姜、葱、花椒、冰糖等调料，烹以酱油、黄酒、加适量清水，用文火煨炖，至龟肉烂为止。', '滋阴补血，适用于阴虚或血虚患者所出现的低热、咯血、便血等症。', NULL, NULL, '龟1只（250－500克），菜油60克，黄酒20克，生姜、葱、花椒、冰糖、酱油各适量。');
INSERT INTO `recipe` VALUES (1561, 74, '红烧寒菌', '1.将寒菌摘去蒂，在清水中浸泡15分钟后，用清水连续洗3－4遍，洗净泥沙，沥干水分。 \r\n2.将猪肉切成片。葱、姜拍破。青蒜切成2厘米长的段。\r\n3.炒锅内放入猪油烧沸，下入葱姜煸锅，继下入肉片、寒菌煸炒，加入精盐和普汤，烧沸后倒入砂锅内，用小火煨半小时，然后去掉猪肉、葱姜。\r\n4.食用时，将寒菌倒入锅内，收浓汁，放入蒜段、味精和胡椒粉，用湿淀粉调稀勾芡，放香油，装盘即成。', '寒菌，学名松乳菇，又称松菌、雁鹅菌，为世界性美味食用菌，湖南山丘之地多有出产。寒菌味很鲜美，质地柔脆，营养丰富，含蛋白质18%，碳水化合物80%左右。每500克可提供热量1500千卡，核黄素（维生素B2）含量也高于许多蔬菜。中医认为其味甘淡微酸，可治眼目不明，能泻肝经之火，散热舒气。此菜以寒菌配猪肉同煨，味道更为鲜香爽嫩，系湖南的地方风味名菜。', NULL, NULL, '鲜寒菌1000克，肥瘦猪肉250克，青蒜50克，熟猪油100克，精盐5克，味精1.5克，胡椒粉1.5克，葱15克，姜15克，湿淀粉25克，普汤500克，香油15克。');
INSERT INTO `recipe` VALUES (1562, 74, '红烧鳝片', '1.将鳝鱼摔昏，用锥子将鳝鱼钉在长条木板上，左手理直鳝鱼，右手持厚背小刀从颔下切开（不切断骨），从肉厚的脊背上划下来，直至鳝尾，再从颔下切断脊骨（不切断肉），从骨边下刀，划至鳝尾，脊骨与鳝肉即分离，用小刀将脊骨和内脏挑出，再从尾部下刀，将鳝肉切成4厘米长的片至颔下止，洗净。将玉兰片切成4厘米长、2厘米宽、0.3厘米厚的片。紫苏叶切碎。蒜瓣切小薄片。姜切细丝。\r\n2.炒锅置旺火上，放入花生油，烧至六成热，将鳝片下锅煸炒，至表面略焦，倒入漏勺沥油。\r\n3.炒锅置旺火上，放入猪油，烧至六成热，下蒜片略炸一下，再', '红烧鳝片以黄鳝为主料。黄鳝，又名鳝鱼，俗称长鱼、无鳞公子。湖南为主要产区之一，以洞庭湖区生产最多。鳝鱼每100克含蛋白质17.2－18.8克，脂肪0.9－1.2克，此外，还含有钙、磷、铁等矿物质。中医认为其味甘，性温，有补虚损、去风湿、强筋骨、壮肾阳之功效。《本草纲目》载：鳝血可“治耳痛、治鼻衄”。鳝鱼肉质细嫩，刺少肉厚，每年5至6月鳝鱼最为肥壮，以红烧法烹之，鳝片鲜嫩清润，醇香柔软。民间还有“小暑黄鳝赛人参”之说。此菜系湖南的传统名菜。', NULL, NULL, '活黄鳝1000克，水发玉兰片50克，熟猪油50克，花生油50克，料酒50克，精盐3克，味精1克，姜15克，蒜瓣50克，漆醋15克，酱油20克，鲜紫苏叶5克，胡椒粉0.5克，湿淀粉50克，肉清汤200克，香油5克。');
INSERT INTO `recipe` VALUES (1563, 74, '红煨八宝鸡', '1.鸡宰杀去净骨，取完整鸡皮。鸡肉切成小指头大小的丁。冬菇去蒂洗净。金钩泡发。肥膘肉、冬笋、火腿都切成丁。苡米洗一遍。大葱剖开，切成6厘米长的段。葱、姜拍破。 \r\n2.炒锅置旺火上，放入猪油烧至六成热，下鸡肉丁和肥膘肉、冬笋、金钩、火腿、冬菇煸炒出香味，烹料酒，加酱油、精盐炒几下，再加白莲、苡米、味精和胡椒粉拌成馅，灌入鸡腹内，缝好开口处擦干水，抹上甜酒汁。\r\n3.在炒锅内放入猪油，烧至六成热，下鸡炸呈浅红色捞出，放入垫有竹箅的砂钵内，放入酱油、料酒、白糖，拍破的葱姜和水（以没过鸡为准），用盘盖上，在旺火', '红煨八宝鸡不但选料精当，而且制作考究。它将传统红煨和油炸的方法相结合，先炸后煨，充分体现了湘菜长于调味的特点。成菜色泽红亮，体态丰满，原料丰富，多味融汇，醇厚浓郁，油润鲜美，是湖南的传统名菜。', NULL, NULL, '肥母鸡1只（重约1750克），猪肥膘肉100克，熟火腿50克，金钩25克，水发冬菇50克，净冬笋50克，白莲50克，苡米25克，大葱500克 ，熟猪油1000克（实耗100克），料酒50克，精盐6克，味精1.5克，胡椒粉1克，酱油50克，白糖15克，葱15克，姜15克，甜酒汁25克，湿淀粉25克，香油15克。');
INSERT INTO `recipe` VALUES (1564, 74, '红煨白鳝', '1.将白鳝去掉头，鱼身用七成热的水烫一下，再用稻草、煤灰擦去白鳝表面的绒毛，用筷子从切口处插入，绞出肠肚后洗净，剪掉须边，再清洗一次，放在砧板上，切成5厘米长的筒状，洗净盛入盘中待用。将肥膘肉切成17厘米长、2厘米宽、1厘米厚的长条。\r\n2.取大瓦钵1只，用竹箅子垫底，将肥膘肉条平放在竹箅子上，依次放入大蒜瓣、葱结、姜片、白鳝、酱油、料酒、漆醋、精盐、冰糖、熟猪油，再加冷水500克，上面用瓷盘盖好，置旺火烧沸，移至小火上煨30分钟离火，将白鳝盛入盘中，整齐排好，去掉葱姜和肥膘肉条。将瓦钵内的原汁烧沸成浓汁', '白鳝，即河鳗，生长在淡水中，肉质细嫩而鲜美，富含脂肪（达8%），属上等食用鱼。中医认为其肉味甘，性平，可补虚赢，祛风湿、杀虫。', NULL, NULL, '活白鳝1000克，肥膘肉250克 ，熟猪油50克，精盐3克，味精1克，料酒250克，葱结15克，姜片15克，胡椒粉1克，蒜瓣100克，酱油50克，漆醋50克，冰糖25克，香油5克。');
INSERT INTO `recipe` VALUES (1565, 74, '红煨水鱼裙边', '1.水鱼裙边改成5厘米长、4厘米宽的块，冼净。水发冬菇去蒂洗净。葱、姜一半拍破，余下葱白切段，姜切米。\r\n2.水鱼肉、鸡肉和五花肉都砍成大块，放入沸水锅氽过洗净，再放入垫有底折的砂锅内，加入拍破的葱、姜以及料酒、精盐、酱油、黄醋、冰糖和清水（没过以上食物为准），用盘盖上，放在大火上烧沸，撇去泡沫后移用小火煨至五成烂时，再下入水鱼裙边和冬菇，煨至裙边软糯为止，将水鱼肉、鸡肉、五花肉、葱和姜挑出不用。\r\n3.蒜瓣剥皮洗净，下入油锅炸熟。小白菜摘去边叶，留苞洗净待用。\r\n4.食用时，将猪油烧沸并下白菜苞，加精盐', '鳖滋味肥厚，营养丰富，是一种珍贵的补品，每100克肉中含蛋白质17.3克，脂肪4克，钙15毫克，磷94毫克，铁2.5毫克，还含有多种维生素。中医认为其肉味甘性平，可滋阴凉血，平肝息风。民间用作滋补食品。此菜色如琥珀，汁如浓胶，裙边软滑，腴肥鲜美，系湖南的传统名菜。', NULL, NULL, '鲜水鱼裙边1000克，水鱼肉500克，母鸡肉500克，猪五花肉250克，水发冬菇50克，蒜瓣50克，小白菜500克，熟猪油100克，料酒50克，精盐5克，味精2克，胡椒粉1克，葱20克，姜20克，酱油50克，黄醋50克，湿淀粉20克，香油15克，冰糖25克。');
INSERT INTO `recipe` VALUES (1566, 74, '蝴蝶过河', '1.才鱼肉洗净，顺纹路用斜刀片成薄片，盛入碗中，加精盐0.5克、葱、姜5克、绍酒攥出的汁腌约十分钟取出，盛入两个瓷盘内，摆成蝴蝶形。大白菜心，豆苗洗净，各用一盘盛上，余下姜切成丝，与醋、辣椒油、胡椒粉各盛入小碟，冬笋切成梳形片，火腿，香菇片成片，香菜洗净。\r\n2.炒锅置旺火上，放入鸡清汤，精盐1.5克、味精、熟猪油烧开，下入火腿、冬笋、香菇煮开，倒入不锈钢汤锅内，连同小酒精炉，才鱼片，豆苗、菜心、香菜盘，姜、醋、辣椒油，胡椒粉碟一同上桌。', '鱼肉细嫩，汤汁鲜美，现烫现吃，非常适口。', NULL, NULL, '净鱼肉500克，小白菜20棵、香菇25克、冬笋25克、火腿肉25克、大白菜心100克、香菜100克、豆苗尖250克、鸡清汤1250克、精盐3克、味精2克、胡椒粉0.5克、葱5克、姜25克、绍酒10克、醋25克、辣椒油15克、熟猪油25克。');
INSERT INTO `recipe` VALUES (1567, 74, '花菇无黄蛋', '1.将鸡蛋洗净，在每个蛋的大圆头顶端磕一小圆孔，逐个将蛋清倒入1大碗内（蛋黄另放作他用）。蛋壳内灌入清水，洗净沥干。用筷子将蛋清搅匀（不要起泡沫），加入熟猪油、精盐、味精、鸡清汤调匀，然后均匀地灌入12个蛋壳内，有薄纸封闭圆孔。取大瓷盘1个，上面平铺一层米饭，将蛋壳逐个竖立在米饭上（圆孔朝上），入笼蒸到上大气时，将蒸笼揭开一会，降低气压，避免蛋清从圆孔溢出，再加盖蒸3分钟熟后取出，放入冷水中浸泡2分钟，再剥去蛋壳（要完整无缺），并用大碗装上。\r\n2.花菇去蒂洗净。白菜苞洗净。\r\n3.食用时，将无黄蛋上笼蒸', '花菇系香菇中的上品，是湖南名产，因其菌上有淡色斑纹而得名。质嫩、肉厚，味醇香鲜美，厨师喜欢用它作为配料烹制各种佳肴。花菇无黄蛋就是其中有名的一种。花菇无黄蛋是长沙市的传统名菜。特一级烹调师蔡海云早在30年代就以擅作此菜而闻名。成菜要求蛋面光滑不破，质地异常鲜嫩，其制作关键在于掌握火候，既要蒸熟，保持鲜嫩，又不能让蛋清冲出，破坏造型。蔡老师傅制作此菜的技艺更是炉火纯青，得心应手。 花菇无黄蛋这道名菜，不少外宾品尝过，总是疑惑不解，必询问服务小姐：“为何蛋中无黄？”有的服务小姐也来个风趣的回答：“我们中国有一', NULL, NULL, '鲜鸡蛋12个，水发花菇75克，小白菜苞12个，熟猪油90克，精盐5克，味精1克，酱油10克，湿淀粉25克，鸡清汤250克，香油2.5克。');
INSERT INTO `recipe` VALUES (1568, 74, '淮杞炖鹌鹑', '将鹌鹑宰杀洗净后，每只剁成4块，共12块，放入沸水锅中氽2分钟捞出，用冷水冲凉，装入盖碗中。淮山、枸杞洗净，泡在碗里15分钟，放在鹌鹑块上，再加姜片。锅置 中火上，下高汤烧沸，加精盐、味精、鸡精、绍酒搅匀，冲入 盖碗中，上笼用旺火蒸20分钟，拾去姜片即成。', '味道清鲜，滋阴补肾。', NULL, NULL, '鹌鹑3只（每只约重250克），淮山50克，枸杞50克，精盐10克，味精5克，鸡精2克，绍酒2克，姜片2克，高汤750克。');
INSERT INTO `recipe` VALUES (1569, 74, '黄焖子铜鹅', '1.将去骨子鹅肉洗净，切成3厘米见方的块。嫩姜洗净去皮，切成0.7厘米厚菱形片。鲜红椒洗净，去蒂去籽，切成长宽各2厘米的薄片。\r\n2.炒锅置旺火上，放入猪油，烧至八成热时，下入嫩姜片炒几下，再下入子鹅肉煸炒，待煸干水时，烹入料酒，继续煸炒2分钟，放入酱油、精盐炒匀，再加入蒜瓣、杂骨汤，焖15分钟，鹅肉柔软后盛入大碗。\r\n3.炒锅内放入猪油，烧至六成热时，放入鲜红椒片、精盐炒熟，再倒入鹅肉，放入味精，用湿淀粉调稀勾芡，一齐炒匀，出锅装盘，淋入香油即成。', '鹅肉结缔组织少，肉纤维较细，易消化吸收；加之鹅肉含水解氨基酸数量多，因而具较多鲜味。中医认为，鹅肉味甘，性平，有益气补虚、和胃止渴的作用。铜鹅肉味鲜美，能够烹制出红烧鹅、清蒸鹅、烙天鹅、米粉鹅等风味名菜，黄焖子铜鹅则是其中的一种。此菜嫩如鸡肉，但比鸡肉油润；状如鸭肉，但比鸭肉丰腴，是一道具有独特风味的地方名菜。', NULL, NULL, '去骨子鹅肉500克，嫩姜50克，鲜红辣椒100克，蒜瓣25克，熟猪油100克，料酒50克，精盐6克，味精1克，酱油15克，湿淀粉25克，杂骨汤500克，香油5克。');
INSERT INTO `recipe` VALUES (1570, 74, '尖椒炒腊肉', '1.将整条腊肉去皮，切成片。红绿尖辣椒切成段。\r\n2.起锅放开水将腊肉焯一下，捞出。另起锅放底油，投入豆豉、干辣椒、尖椒爆香，放腊肉、料酒、酱油、味精、鸡汤烧开后用微火焖10分钟，收干汁出盘。', '浓香鲜美，风味独特。', NULL, NULL, '腊肉250克，红、绿尖辣椒各50克，料酒4克，酱油3克，味精2克，干辣椒2克，豆豉3克，油70克，鸡汤适量。');
INSERT INTO `recipe` VALUES (1571, 74, '煎连壳蟹', '1.将青蟹洗净，除去背壳、蟹鳃和下腹脐部，刮净脚下的毛。再将青蟹切成2厘米的长方块，留下大小腿，去掉关节和脚爪，然后底板朝下摊开平放一瓷盘里，撒上面粉待用。\r\n2.把葱花、姜末盛入小碗，再放入酱油、醋、绍酒、味精、湿淀粉、精盐和肉清汤25克兑成汁子。\r\n3.炒锅置旺火，放入熟猪油，烧至七成热，下蟹块，炸呈红色后，连油倒入漏勺，滤去油。\r\n4.炒锅放入熟猪油75克，烧至六成熟，下入炸好的蟹块将汁子搅匀倒入持锅颠翻几下，淋入芝麻油装盘，香菜拼放盘边即成。', '我国食蟹已有几千年历史。两周时已有蟹酱、蟹膏之制。文人雅士咏蟹诗词，不胜枚举。“一腹金相玉质，两螯明月秋江”、“蟹肥暂擘、馋涎堕，酒绿初倾老眼明”，《红楼梦》中写有宝黛在藕香榭食蟹的故事，赏桂咏菊，煞是快哉！秋高气爽，河蟹大量上市。蟹肉营养丰富，味道鲜美。用以煎制成连壳蟹，即焦脆，又鲜嫩，风味独特。', '蟹肉上席百味淡，此菜登筵，次序宜后。', NULL, '青蟹500克，黄醋25克，净香菜50克，酱油15克，肉清汤25克，湿淀粉25克，面粉25克，味精1克，绍酒50克，精盐1克，姜末10克，熟猪油500克， 葱花10克，芝麻油5克。');
INSERT INTO `recipe` VALUES (1572, 74, '煎紫米藕夹', '1.紫米蒸熟为饭。藕洗净刮去皮煮熟，切为3毫米厚的片。鸡茸入碗，加入盐、味精、鸡蛋清1个、湿淀粉300克，搅拌均匀，加入紫米饭拌匀。用小刀均匀地将紫米饭涂抹在藕片上，合二为一，成为藕夹。把鸡蛋清2个与湿淀粉30克混合为糊。\r\n2.炒锅上旺火，烧热，注入花生油，烧至七成热，将藕夹沾蛋糊后下锅煎至两面金黄出锅。\r\n3.荷叶洗净，焯水入盘，将藕夹复原为全藕状放在上面，跟椒盐碟上桌。', '紫米是云南特产，分紫粳、紫糯两种。紫米颗粒均匀，颜色紫黑，食味香甜，甜而不腻，民间视为补品。而藕，生食能清热生津，凉血止血，熟食能补益脾胃，上泻，益血，生肌。煎紫米藕尖，是用紫米夹入藕中，煎制而成。香糯可口，酥脆宜人，是云南回族的待客佳肴。', '《随息居饮食谱》载：“藕以肥白纯甘者良。生食宜鲜嫩，煮食宜壮老。”', NULL, '鲜藕500克，味精2克，紫糯米100克，椒盐4克，鸡茸50克，鸡蛋清3个，鲜荷叶1张，湿淀粉60克，精盐80克，花生油100克。');
INSERT INTO `recipe` VALUES (1573, 74, '酱汁肘子', '1.将猪前肘去尽骨质，刀工成半圆形。用烙铁烙尽肘皮上的余毛，置清水中浸泡10分钟后，刮洗干净。再在肘子肉的一面剞上刀。\r\n2.锅内放冷水1000克，将肘子煮至四成熟取出，移入卤锅内，用中火煮至八成烂。\r\n3.将煮烂的肘子扣入钵内，再取卤锅里的卤汁50克，加精盐搅匀，淋在肘子上，入笼蒸烂即成。', '色泽红亮，肉烂味香，微咸带甜，肥而不腻。', NULL, NULL, '猪前肘子1个（重约750克），卤汁50克，精盐1.5克。');
INSERT INTO `recipe` VALUES (1574, 74, '焦盐兔片', '1.将鲜兔肉洗净，剔去筋膜，斜片成3厘米长、2厘米宽、0.3厘米厚的薄片置碗中，加料酒、味精、精盐抓匀腌3分钟。\r\n2.将面粉、鸡蛋、湿淀粉放入大碗中调匀制成糊，倒入腌好的免肉片均匀挂糊。\r\n3.炒锅置旺火上，放入熟猪油，烧至六成热，将挂糊的免肉片，逐片下油锅，待表面炸呈淡黄色，倒入漏勺沥去油。 \r\n4.炒锅内留少许油置旺火上，下兔肉片急炒，撒上葱花、花椒粉，淋入香油，持锅颠几下，装盘即成。', '成菜色泽淡黄，外焦内软，香酥麻辣。', NULL, NULL, '鲜兔肉300克，鸡蛋2个，面粉40克，熟猪油1000克（实耗75克），精盐5克，味精0.5克，料酒25克，葱花5克，花椒粉0.5克，湿淀粉25克，香油5克。');
INSERT INTO `recipe` VALUES (1575, 74, '焦炸鳅鱼', '1.鳅鱼用清水洗净，沥干水分，装入陶器内，用料酒、精盐腌死（腌时要盖严），用漏勺沥去水分。\r\n2.小红椒、葱、姜、蒜瓣和紫苏都切成末。用普汤、白糖、味精、白醋、葱、香油和湿淀粉兑成汁。香菜摘洗干净。\r\n3.炒锅倒入油烧至七成热时，下入鳅鱼炸熟，捞出后放在砧板上，用小刀切去头尾，并除去内脏。\r\n4.食用时，将锅内的油烧至六成热，下入鳅鱼，用温火炸焦酥透捞出。锅内留油50克，下入花椒粉和葱、姜、蒜、紫苏末，并加精盐炒一下，再下入鳅鱼，并随倒入兑汁，颠炒几下，装入盘内，拼上香菜即成。', '鳅鱼，又称泥鳅，每100克可食部约含蛋白质18.8－22.6克，脂肪2.2克，还含钙、磷等矿物质。中医认为，鳅鱼味甘性平，有暖中益气、祛湿邪之功效。鳅鱼补而味清，诸病不忌，是肝病、糖尿病、泌尿系统的食疗上品。焦炸鳅鱼，系湖南民间风味名菜，麻辣甜酸，酥香焦脆，风味独特，酒饭皆宜。', NULL, NULL, '小指头大的活鳅鱼500克，香菜100克，紫苏5克，小红辣椒15克，花生油1000克（实耗100克），料酒50克，精盐5克，味精1克，葱10克，姜10克，花椒0.5克，白糖2.5克，白醋15克，蒜瓣10克，湿淀粉10克，普汤50克，香油15克。');
INSERT INTO `recipe` VALUES (1576, 74, '金鱼戏莲', '1.将干鱿鱼去须，碱发好，漂洗干净，在正面的一边剞上十字花刀，另一边切0.3厘米粗的丝，不要切断，再切成4厘米的片，即成金鱼形，置于盘中，加精盐0.5克、干淀粉25克拌匀。将鲜红椒、泡菜、蒜瓣、水发香菇切成米粒丁；味精、干淀粉25克、醋、清水10克兑成汁。\r\n2.鸡蛋清搅匀，拌入虾料子内。取小酒杯12个，逐个抹上熟猪油，将鸡蛋清、虾料子放入杯内，周围镶入5粒青豆，中间放一粒青豆，上笼蒸2分钟，即成莲蓬，入笼内保温。\r\n3.炒锅置旺火，放入熟猪油烧至八成热，下鱿鱼氽一下，滑熘至剞刀处卷起捞出。锅内留油50克', '“金鱼戏莲”以鱿鱼为主料巧制而成，鱿鱼卷似金鱼，嬉戏于以鸡蛋、虾料子和青豆精制的群莲中，菜名即由此而得。此菜刀工精湛，酸辣突鲜，鱿鱼脆嫩，莲蓬滑润，是长沙市玉楼东酒店的看店名菜。', '鱿鱼卷过油，一氽即出，以剞处卷起为度，时间一长，老硬难嚼。', NULL, '干鱿鱼200克，虾料子100克，鲜红椒50克，醋15克，泡菜25克，鸡蛋清3个，肉末50克，精盐5克，水发香菇25克，味精1克，香菜25克，干淀粉50克，青豆5克，蒜瓣5克，芝麻渍10克，熟猪油750克。');
INSERT INTO `recipe` VALUES (1577, 74, '开屏柴把桂鱼', '1.将鱼肉切成0.5厘米米粗、7厘米长的丝，冬笋、火腿切成3厘米长的丝，葱75克在开水中氽过。另将剩余葱姜拍破，加绍酒、精盐1克，拌匀攥出汁，加入味精，淋入鱼丝中腌约8分钟。然后，将鸡蛋清、干淀粉调匀。放入鱼丝上浆。\r\n2.将樱桃一切两半，鹌鹑蛋逐个搁入抹了熟猪油的12只调羹里，香菜摘洗净。然后，在盛有鹌鹑蛋调羹里嵌半粒樱桃，拼几片香菜叶，成雀屏花形，上笼蒸10分钟即熟，放在笼中保温。杂骨汤、芝麻油、湿演粉兑成汁。\r\n3.将氽水好的葱根理直摆在盘中，上面横放火腿丝2根，鱼丝2根，冬笋丝2根，按柴把形捆成2', '开屏柴把桂鱼，由传统菜：“柴把鸡”、“柴把鸭”演化而来。看上去似孔雀开屏冷盘，实际上是热菜，整个形状完全突破了热菜不能拼摆的局限，采用象形拼盘制法，以各种不同性质、不同制法、不同形状的成品拼成，造型逼真，栩栩如生。此菜颜色多样、花而不乱。有鱼肉的鲜嫩，火腿的咸香，鹑蛋的清嫩，香菇的清香，冬笋的脆嫩，味感丰富，鲜嫩适口。', '鱼丝要切得粗一些，上浆时，手法要轻，以防把鱼丝断破。蒸鹌鹑蛋时放气蒸，过火会出现蜂窝眼，以丰满光滑为准。制雀头：将蛋黄打发，加入适量的调料，倒在铝盆中蒸熟，待凉后取出，即成蛋黄糕，蒸时也采用放气蒸。雕琢雀头需半小时以上，可事先制好，加热保温，待雀屏、雀身都烹调好后，再放入盘内。', NULL, '净桂鱼肉50克，鹌鹑蛋12个，熟火腿100克，杂骨汤50克，冬笋100克，鸡蛋清2个，菠菜心20个，姜15克，红绿樱桃各3粒，葱100克，香菜100克，绍酒5克，干淀粉20克，精盐2克，湿淀粉10克，芝麻油5克，味精1克，熟猪油750克。');
INSERT INTO `recipe` VALUES (1578, 74, '口蘑烩鸽蛋', '1.将鸽蛋洗净，放入冷水锅煮沸捞出，用冷水浸凉，再剥去壳待用。火腿切成小长方片，装入碗内。豆苗摘嫩苞洗净。\r\n2.口蘑去蒂洗净，用沸水浸泡焖透（原水滗去保留待用），用少许精盐轻揉呈白色，片成片，用清水泡上。\r\n3.食用前5分钟，将鸽蛋、火腿片放入鸡汤上笼蒸热取出。同时，将炒锅置旺火上，放入猪油烧至六成热时，下入豆苗，加精盐煸炒入味，装入盘的周围，中间放鸽蛋和火腿片。锅内另放油烧热，下入口蘑炒一下，烹料酒，加入鸡汤和口蘑原水，精盐、味精烧沸，调好味，用湿淀粉调稀勾薄芡，加入胡椒粉，浇盖在鸽蛋、火腿上，淋鸡油', '鸽蛋熟后质细嫩，色晶莹，是高档的菜肴原料之一。中医认为鸽蛋味甘咸，性平，可解疮、痘毒。口蘑，嫩滑爽口。菌香浓郁，为筵席上品。医学家认为它具抗癌作用。此菜原料上乘，色泽淡雅，质地细嫩滑爽，口味鲜香清淡，堪称色、香、味、形的佳品。', NULL, NULL, '鲜鸽蛋20个，口蘑30克，熟瘦火腿30克，豆苗250克 ，熟猪油150克，料酒15克，精盐6克，味精2克，胡椒粉1克，湿淀粉25克，鸡清汤500克，鸡油15克。');
INSERT INTO `recipe` VALUES (1579, 74, '葵花虾饼', '1.将肥膘肉、荸荠均切成米粒状。葱、姜捣烂加料酒取汁。鸡蛋磕开，蛋清、蛋黄分别盛入碗中。蛋清打成雪花糊。蛋黄加入精盐、湿淀粉和清水搅匀，烫成蛋皮，用模型刀片切成葵花瓣形。将香菜摘洗干净。\r\n2.将虾仁捶剁成茸，加入葱姜酒汁、肥膘肉末、荸荠末、精盐、味精、湿淀粉搅拌成馅。将面包切成直径为4厘米的圆片计20片，摊放案板上，上面均匀地铺上一层虾仁馅，将葵花瓣形的蛋皮贴在虾仁馅的周围，再盖上一层雪花糊，然后在雪花糊上插瓜子仁，即成葵花虾饼生坯。\r\n3.炒锅置旺火上，放入猪油烧至五成热，将葵花虾饼生坯放在漏板上一同', '虾仁每100克含蛋白质17克，脂肪2克，并含有丰富的钙、磷、铁等矿物质，有补肾壮阳的功效。葵花虾饼是湖南省委接待处厨师们研制的创新佳肴。此菜既注意主配料之间的搭配，又注重工艺造型的精细，因而色彩鲜艳，造型精巧，葵花朵朵，栩栩如生，焦脆香酥，味美可口。', NULL, NULL, '虾仁250克，熟猪肥膘肉50克，削皮荸荠50克，咸面包100克，瓜子仁50克，香菜100克，鸡蛋4个，熟猪油1000克（实耗100克），料酒25克，精盐4克，味精1.5克，胡椒粉1克，葱15克，姜15克，白糖2克，干淀粉20克，湿淀粉30克，香油15克。');
INSERT INTO `recipe` VALUES (1580, 74, '腊味合蒸', '1.将腊肉、腊鸡、腊鱼用温水洗净，盛入钵瓦内上笼蒸熟取出。腊鸡去骨，腊肉去皮，腊鱼去鳞；腊肉切4厘米长、0.7厘米厚的片，腊鸡、腊鱼切成大小略同的条。\r\n2.取瓷菜碗一只，将腊肉、腊鸡、腊鱼分别皮朝下整齐排放碗内，再放入熟猪油、白糖和调好味的肉清汤上笼蒸烂，取出翻扣在大瓷盘中即成。', NULL, NULL, NULL, '腊猪肉200克，肉清汤25克，腊鸡肉200克，味精0.5克，腊鲤鱼200克，熟猪油25克，白糖15克。');
INSERT INTO `recipe` VALUES (1581, 74, '老姜鸡', '1.鸡腿剁成块，用开水焯好。\r\n2.起锅放底油，投入鸡块煸炒，放木耳、调料、鸡汤微火焖15分钟，水淀粉勾芡，淋明油、香油出锅。', '肉质细嫩，姜香浓郁。', NULL, NULL, '鸡腿500克，木耳10克，盐6克，胡椒粉2克，味精2克，姜片20克，油、香油、水淀粉、鸡汤各适量。');
INSERT INTO `recipe` VALUES (1582, 74, '熘野鸡片', '1.将野鸡脯肉洗净，切成3厘米长、2厘米宽、0.3厘米厚的片。再用蛋清、湿淀粉、精盐调匀上浆。\r\n2.冬笋切成薄片，冬菇去蒂洗净泥纱，切成薄片。姜切小片。青蒜切斜段。\r\n3.炒锅置旺火上，放入猪油烧至六成热，将浆好的野鸡片下入油锅走油、扒散，熟后倒入漏勺沥油。锅内留油，下入冬笋、冬菇片煸炒，加入精盐和鸡清汤合炒，再下入野鸡片，另加入姜片、蒜段、精盐、味精，在锅内颠簸几下，用湿淀粉调稀勾芡，淋入香油，撒上胡椒粉，出锅装盘即成。', '野鸡脯肉鲜嫩无比，肉的持水性和爽口性均较好，且营养丰富，滋补力强，药膳效果好。每100克野鸡肉约含蛋白质24.4克，磷263毫毛，以及丰富的维生素和矿物质等。中医认为野鸡肉味甘酸，性温，具有补中益气之功，能治痰气止喘，还有益肝和血的作用。此菜以白色为主，缀以绿、褐两色，清新悦目，鸡片鲜嫩，冬笋脆嫩，冬菇滑嫩，“三嫩”相汇，香鲜无比。', NULL, NULL, '野鸡脯肉250克，净冬笋100克，水发冬菇50克，鸡蛋1个，青蒜15克，熟猪油500克（实耗100克）精盐5克，味精1克，胡椒粉0.5克，姜10克，湿淀粉50克，鸡清汤50克，香油5克。');
INSERT INTO `recipe` VALUES (1583, 74, '龙凤葡萄珠', '1.鸡肉，草鱼肉分别制泥，分别加入鸡蛋液，淀粉，葱，姜汁，盐，味精调味。\r\n2.油菜取绿汁，加入鱼泥中搅匀。鸡泥加老抽酱油搅匀。\r\n3.起锅放水烧开后，将两种泥分别氽成栗子般大小的丸子，漂起时捞出。\r\n4.锅上火倒入菜汁，加少许盐，用水淀粉勾成玻璃芡，下鱼丸，淋明油出锅。\r\n5.再起锅入少许油烧热，下入番茄酱，加白糖，白醋，水淀粉勾玻璃芡，下入 鸡丸，淋明油即可。\r\n6.将做好的两种丸子分别码在盘中成葡萄串状。', '色形悦目，味道鲜美。', NULL, NULL, '鸡胸肉250克，草鱼肉250克，油菜叶300克，葱汁，姜汁各20克，鸡蛋1个，盐，味精，老抽酱油，绿菜汁各少许，白醋适量，淀粉50克，番茄酱20克，白糖50克，油100克。');
INSERT INTO `recipe` VALUES (1584, 74, '龙虾焗伊面', '热鼎烧油至120度，大青龙折净肉斩件上生粉，走油沥干，热鼎倒入上汤、生抽，放姜、葱、香菜头，倒入青龙净肉块，先武火后文火，约焗10分钟，装盘，原汁勾芡，下胡椒粉、麻油、色拉油淋在大青龙肉块上，撒上芹菜珠、红椒米，即成。', '原形生动，美味可口，色、香、味俱全。', NULL, NULL, '大青龙1只（约1200克），伊面120克，上汤100克，芹菜珠5克，红椒米5克，盐3克，姜1块，葱2根，香菜2棵，色拉油1000克（实用50克），生抽5克，味精3钱，胡椒粉3钱，麻油3钱，生粉5克。');
INSERT INTO `recipe` VALUES (1585, 74, '麻辣狗肉', '1.净狗肉用白水煮八成熟，晾凉后切2厘米左右见方的块。尖椒切大象眼片。\r\n2.起锅放少许油加入蒜片、干辣椒爆香，投入狗肉，加明油、料酒、酱油、味精、鸡汤小火炖烂，大火收汁，淋明油、料酒，放花椒末，出锅。\r\n3.另起锅放少许油，煸炒建交，加盐、味精出锅装在盘中心即可。', '色泽油亮，味微麻辣，肉质软烂。', NULL, NULL, '净狗肉500克，尖椒50克，花椒3克，大蒜20克，油50克，酱油25克，盐、味精各少许，料酒15克，干辣椒5克，鸡汤适量。');
INSERT INTO `recipe` VALUES (1586, 74, '麻辣田鸡腿', '1.用右手持刀在田鸡头部横刺一刀，用左手拉着皮往左扯去，然后撕破腹部，去内脏并洗净，在背疹骨紧连后腿处斩下两腿，用刀背敲断腿骨，再将再腿砍开，装盘待用。\r\n2.红辣椒去蒂去籽，洗净后切成斜方块。大蒜切斜段。用酱油、醋、味精、香油、湿淀粉和少许汤兑成汁。\r\n3.田鸡腿用少许盐和酱油拌匀，再用湿淀粉浆好。将花生油烧沸，下入田腿脚炸一下即捞出，待油内水分烧干时，再下入田鸡腿重炸焦酥呈金黄色，倒漏勺滤油。锅内留一两油，下入红椒后加盐炒一下，再放入花椒粉、大蒜、田鸡腿，倒入兑汁颠几下，装入盘内即成。', '此为长沙家常名菜，麻辣香酥，味鲜可口，宜于下酒。', '亦可将葱、姜、蒜和红辣椒均切成末，加花椒粉烹制叫“椒麻田鸡腿”。', NULL, '活田鸡1500克，酱油25克，小红辣椒50克，醋10克，大蒜50克，味精2克，花生油1000克，花椒粉1克，湿淀粉50克，料酒50克，香油10克，盐5克。');
INSERT INTO `recipe` VALUES (1587, 74, '麻辣子鸡', '1.鸡宰杀去净毛，由背脊开膛去内脏洗净，剔除全部粗细骨，砍成2厘米见方的丁，放入精盐、酱油拌匀，用湿淀粉浆好。\r\n2.将鲜红辣椒去蒂去籽洗净，切成1厘米见方的块。青蒜切斜段。花椒拍碎。用酱油、醋、味精、香油、清汤和湿淀粉兑成汁。\r\n3.炒锅置旺火上，放入花生油，烧至七成热，将鸡丁下锅，用手勺推散，约20秒钟，即用漏勺捞起，待油温回升至七成热时，再将鸡丁下锅，炸呈金黄色，连油倒入漏勺沥油。 \r\n4.锅内留油50克，烧至六成热时下入红椒块、青蒜段、花椒，加精盐煸炒，再放炸过的鸡丁合炒，随冲下兑汁，颠炒几下，装', '湖南气候潮湿，易患风湿症，因而形成了爱吃辣椒、生姜的习惯。湖南鸡产量多，且营养丰富，每100克鸡肉含蛋白质24.4克，脂肪2.8克，另外还有钙、磷、铁等矿物质和维生素。中医认为鸡肉味甘性温，具有温中益气、补精添髓的功效。', NULL, NULL, '嫩子鸡2只，重约750克，鲜红辣椒50克，青蒜50克，花生油1000克（实耗100克），料酒15克，精盐5克，味精1.5克，花椒1克，酱油15克，醋15克，湿淀粉50克，香油15克，清汤50克。');
INSERT INTO `recipe` VALUES (1588, 74, '麻仁酥鸭', '1.将笔鱼宰杀冼净，沥干，切成4厘米长、2厘米宽的骨牌块。 \r\n2.红椒去籽，和姜一起分别切成丝。葱白切成段，葱叶切成葱花。 \r\n3.炒锅上旺火，下猪油60克，烧至八成热，放入鱼块翻炒几下，加红椒、姜、葱白、绍酒、精 盐、酱油，煸炒一下，放入鲜汤，焖烧两三分钟，至汤汁收紧，再加入味精、猪油30克、葱花，用湿淀粉勾芡，淋入麻油，撒上胡椒粉即成。', '肉质细嫩，甘甜可口，油而不腻，浓香四溢。', NULL, NULL, '鲜笔鱼肝油条（重约1000克），姜15克、红辣椒30克、葱20克，酱油25克，绍酒5克，精盐1.5克，味精1克，湿淀粉10克，鲜汤250克，胡椒粉1.5克，芝麻油10克，熟猪油100克。');
INSERT INTO `recipe` VALUES (1589, 74, '麻仁香酥鸡', '1.葱、姜拍破。火腿切米。肥膘肉下入汤锅煮熟捞出，切成细丝。鸡蛋磕在碗内放入面粉、干淀粉和水制成蛋糊。香菜摘洗干净。\r\n2.鸭宰杀去净毛，开膛去内脏洗净，用拍破的葱姜、料酒、精盐、白糖、花椒子腌约2小时，上笼蒸八成烂取出晾凉，先取下头、翅、脚，鸭身折净骨，将腿、脯肉厚的部分剔下，切成丝，在鸭皮面抹上蛋糊，放在有油的平盘上，将肥膘肉丝和鸭丝放入余下的蛋糊内，加入味精拌匀，铺平在带皮的鸭肉上，下入油锅炸酥呈黄色捞出，用盘装上。\r\n3.鸡蛋去黄用清，用筷子打起发泡，放入干淀粉调成糊，铺满一层在炸酥的鸭肉上，表面', '鸭肉每100克约含蛋白质13.1－16.5克，脂肪6－7.5克，还含有多种维生素。中医认为，鸭肉味甘咸，性平，具有滋阴、养胃、利水消肿的作用。麻仁香酥鸭是湖南菜中研制较早，推广比较普遍的创新菜。此菜讲求原料的配合和内涵的精当，注意工艺造型，色调柔和美观，集松泡、酥脆、软嫩、鲜香于一体，深受四方宾客嘉许。如将麻仁香酥鸭，夹在薄饼中与葱段、甜面酱佐食，更是别有风味。', NULL, NULL, '肥鸭1只，重约1250克，猪肥膘肉50克，熟火腿15克，面粉50克，芝麻仁50克，鸡蛋4个，香菜100克，花生油1000克（实耗100克），料酒20克，精盐6克，味精1.5克，葱15克，姜15克，白糖10克，花椒子20粒，花椒粉1克，干淀粉50克，香油15克。');
INSERT INTO `recipe` VALUES (1590, 74, '麻仁香酥鸭', '1.将净鸭用绍酒、精盐、白糖、花椒和拍破的葱姜腌约2小时，上笼蒸至八成烂，取出晾凉，先切下头、翅、掌，再将鸭身剔净骨，从腿、脯肉厚的部位剔下肉切成丝。火腿切成末。肥膘肉切成细丝。鸡蛋在碗内，放入面粉、干淀粉10克、清水50克，调制成糊。香菜摘洗干净。\r\n2.将鸭皮表面抹一层蛋糊。摊放在抹过油的平盘中，把肥膘肉丝和鸭肉丝放在余下的蛋糊内，加入味精拌匀，平铺在鸭皮内面，下入油锅炸呈金黄色捞出，盛入平盘里。\r\n3.将蛋清打起发泡，加入干淀粉40克，调匀成雪花糊。铺在鸭肉面上，撒上芝麻和火腿末。炒锅内放入花生油，', '麻仁香酥鸭是湖南创新名菜。注重工艺造型，讲究原料配合，形态美观，色调柔和，集松泡、酥脆、软嫩、鲜香于一体，深受四方宾客欢迎。', '盛大蛋清的容器要净洁，不能有油、盐、碱和生水，用抽子顺一个方向搅打，中途别停，一气呵成，打成雪花状，以插入筷子立定不倒为度。', NULL, '肥鸭1只2000克，味精1.5克，芝麻50克，花椒20粒，熟猪肥膘肉50克，猪皮250克，熟瘦火腿10克，葱15克，香菜100克，姜15克，鸡蛋1个，干淀粉50克，鸡蛋清3个，面粉50克，绍酒25克，芝麻油10克，精盐8克，花生油1000克，白糖5克。');
INSERT INTO `recipe` VALUES (1591, 74, '米切糕', '1.将稻草烧成灰，泡水，制成灰碱水，将大米放入浸泡后洗净，磨成粉浆，上笼蒸30分钟，切成一分厚的糕片。\r\n2.清水烧沸，倒入糕片，加精盐、味精、胡椒粉、辣椒粉，稍煮后连汤舀入碗中，撒上葱花即成。', '湖南著名特色风味小吃。具有颜色微黄，糕质柔软，汤味微辣的特色。', NULL, NULL, '大米，葱花，味精，白胡椒粉，辣椒粉，精盐，稻草。');
INSERT INTO `recipe` VALUES (1592, 74, '面包鸡排', '1.将鸡胸肉从中间切开，两面剞花刀切片，用盐、料酒、葱姜片盐约30分钟，逐片沾鸡蛋液、干淀粉、面包渣，按实。\r\n2.起锅放油烧至八成热，下入加工好的鸡排，炸至金黄色捞出装盘。', '外酥里嫩，咸香适口。', NULL, NULL, '鸡胸肉250克，面包渣300克，料酒2克，盐3克，葱、姜各2克，鸡蛋液6克，油80克，干淀粉食粮。');
INSERT INTO `recipe` VALUES (1593, 74, '牛肉米粉', '1.将牛肉放水中浸漂，洗净切块，水煮30分钟，捞出； \r\n2.将小茴香、桂皮、冰糖、绍酒、精盐、酱油等放入锅中，然后投入牛肉块， \r\n加清水用小火将肉煨烂； \r\n3.把大米淘洗干净，带水磨成细滑的粉浆，再制成米粉；\r\n4.把酱油、精盐、熟猪油、味精、葱花放碗中，再放入骨清汤将米粉煮好，捞入碗中，再在上面浇上煨好的牛肉码好即成。', '湖南著名小吃。具有柔韧，光洁，色红，质醇烂，味鲜香而略甜的特色。', NULL, NULL, '早稻米，熟米饭，牛肉，酱油，精盐，味精，冰糖，绍酒，桂皮，葱花，小茴香，牛骨清汤，熟猪油。');
INSERT INTO `recipe` VALUES (1594, 74, '祁阳笔鱼', '1.将笔鱼剖腹去内脏洗净（不去鳞），沥干水，改切成4厘米长、2厘米宽的骨牌形状。红椒、姜切丝。葱白切段，余下切葱花。\r\n2.炒锅置旺火上，放入猪油烧至八成热，下入鱼块翻炒几下，加红椒、姜丝、葱白段、精盐、料酒、酱油，再煸炒1分钟，放入杂骨沁焖2分钟，至汤汁稍干，再加入味精、猪油、葱花，用湿淀粉勾芡，淋入香油，撒上胡椒粉即成。', '笔鱼是湖南祁阳县浯溪的著名特产。相传宋代文豪苏东坡有次路过祁阳，祁阳知县便邀他夜泛浯溪，并在船上设宴款待，东坡先生为此地山水秀美所吸引，正要挥笔作诗时，阵风吹来，笔落浯溪水中，立即变成了无数条形似毛笔、颜色鲜艳游鱼。古时曾有人诗曰：“天意东坡不留字，神笔化作席上珍。”祁阳笔鱼便由此而得名。笔鱼学名铜鱼，因其色黄体圆，状似竹竿，又称竹鲆。煎焖而成的笔鱼，具有桂鱼的细嫩，青鱼的甘甜，鳝鱼的油润，鳅鱼的鲜香，是当地的传统名菜。', NULL, NULL, '鲜笔鱼1条，重约1000克，红辣椒30克，熟猪油100克，料酒40克，精盐3克，胡椒粉1.5克，葱40克，姜15克，酱油25克，湿淀粉10克，杂骨汤250克，香油10克。');
INSERT INTO `recipe` VALUES (1595, 65, '叉烧鱼', '鲤鱼去鳞、鳃，剖腹去内脏，切去头尖、尾梢，揩干水分，在鱼身两面划上梯形块，用拌匀的料酒、酱油、盐、姜、葱等调料腌渍5分钟，取出揩干待用。猪肉、芽菜、泡辣椒（去籽）剁细，下锅用猪油炒成馅，填入鱼腹，用竹签锁住鱼腹。网油洗净晾干，平铺在案板上，先将鱼包裹一层，然后将剩下的网油抹上蛋清豆粉，再将鱼包裹三、四层，然后用一小叉从鱼腹部刺入，鱼背穿出，放木炭火上烤制约30分钟。烤时不断翻转，至鱼表面呈金黄色时下叉。从鱼背处划破网油，刷上香油，抽出竹筷和竹签，网油除最内一层不用外，切成约6厘米长、3厘米宽的片，镶于鱼侧', '油皮酥香，鱼鲜味浓，风味独特，佐酒尤佳。', NULL, NULL, '鲜活鲤鱼一条（约750克）、 猪肥瘦肉100克、芽菜50克、泡辣椒15克、猪网油500克、生菜50克、料酒30克、酱油10克、盐5克、姜10克、葱10克、猪油25克、蛋清50克、豆粉30克、香油15克。');
INSERT INTO `recipe` VALUES (1596, 74, '青炒双银', '1.将鲜银鱼洗净后，用精盐、葱姜汁腌5分钟。\r\n2.绿豆芽摘去两头，留约6厘米长，用清水漂上。葱白切段。\r\n3.炒锅置旺火上，放猪油烧至四成热，将银鱼挤干水。用鸡蛋清1个，加入味精和干淀粉抓匀，将银鱼上浆，下入油锅滑散，倒入漏勺中沥油。\r\n4.锅内留油50克，下入葱、姜煸炒，再倒入豆芽煸炒，加鸡汤、精盐、味精入味后，即倒入漏勺沥干水，然后会同滑好的银鱼翻炒入味，淋醋，勾薄芡，淋香油，出锅装盘即成。', '“双银”，即银鱼、银芽之谓也。以两银同炒，故名。银鱼每100克食部约含蛋白质8.2克，脂肪0.3克，钙258毫米。中医认为，银鱼味甘性平，善补脾胃，且可益肺利水。豆芽营养丰富，含多种维生素、粗纤维。中医认为，豆芽味甘性寒凉，能清热解毒。此菜色泽洁白如银，口味鲜美清爽，银鱼软嫩，银芽香脆。系岳阳宾馆研制的创新菜。', NULL, NULL, '鲜银鱼250克，绿豆芽250克，鸡蛋清1个，熟猪油1000克（实耗100克），精盐2克，味精1克，醋1克，糖1克，葱15克，姜15克，干淀粉5克，湿淀粉15克，鸡清汤50克。');
INSERT INTO `recipe` VALUES (1597, 74, '青韭鱿鱼丝', '1.鱿鱼切丝。青椒切段。\r\n2.鱿鱼丝用水氽透去碱。\r\n3.起锅放油烧热，投入鱿鱼、青韭、调料煸炒入味即可出锅。', '入口脆鲜，有浓郁的韭香味。', NULL, NULL, '水发鱿鱼200克，青韭100克，料酒5克，盐3克，味精2克，胡椒粉1克，油适量。');
INSERT INTO `recipe` VALUES (1598, 74, '清汤柴把鸭', '1.将鲜鸭肉煮熟，剔去粗细骨，切成5厘米长、0.7厘米见方的条，水发大香茹去蒂洗净。与熟火腿、玉兰片均切成5厘米长、0.3厘米见方的丝。水发青笋切成粗丝。 \r\n2.取鸭条4根、火腿、玉兰片、香茹丝各2根，共计10根，用青笋丝从中间缚紧，捆成小柴把形状，共24把，整齐码入瓦钵内，加入熟猪油、精盐1.5克、鸡清汤250克，再加入剔出的鸭骨，入笼蒸40分钟取出，去掉鸭骨，原汤滗入炒锅，鸭子翻扣在大汤碗里。 \r\n3.在盛鸭原汤的炒锅内，再加入鸡清汤250克烧开，撇去泡沫，放入精盐0.5克、味精、葱段，倒在大汤碗里', '清汤柴把鸭是长沙地区久负盛名的汤菜，营养丰富，清润滋补，清香浓郁，因鸭肉成捆，状如乡村农家的柴把，故名。', '蒸制柴把鸭，大火气足，蒸约40分钟，以软烂为佳。', NULL, '鲜鸭肉1000克、葱段5克、熟火腿75克、胡椒粉0.5克、水发玉兰片75克、味精1克、水发大香茹75克、精盐2克、水发青笋50克、鸡油5克、鸡清汤500克、熟猪油25克。');
INSERT INTO `recipe` VALUES (1599, 74, '清汤滑鸡球', '1.将鸡肉切成2厘米见方的丁。葱、姜捣烂用料酒取汁，加精盐和白糖，将鸡丁腌约半小时。\r\n2.鸡蛋去黄留清，用筷子打起发泡，加入干淀粉，调成雪花蛋糊。将口蘑、火腿切成薄片。小菜苞下入沸水锅内氽过，用冷水过凉。\r\n3.将猪油烧至五成热时，即将油锅端离火位，把鸡丁逐一裹上雪花糊，下入油锅吞炸，待鸡丁呈球形表面凝固时捞起，再在温水中氽过捞出，装入大碗内，加入鸡清汤和精盐，上笼蒸2小时左右，至鸡肉软烂时取出。\r\n4.食用前30分钟，将滑鸡球中加入火腿片、口蘑片、味精上笼蒸热。同时将鸡汤、白菜苞和精盐放入锅中烧沸，捞', '鸡肉在肉类中以味道鲜美著称。由于鸡肉结构组织少，肌纤维又较柔细，故肉的硬度较低，易为人体消化吸收，因此，用鸡肉作菜的品种特别多。清汤滑鸡球是湖南的传统风味名菜。玉白的鸡球半浮于汤面，极似香甜软糯的汤圆，滑嫩柔软，落口消溶，引人入胜。此菜尤重做工，滑鸡球时，油温保持在四成热，动作要轻巧，让鸡球在油中轻轻滑过。冷则滑不起，热则不成形。', NULL, NULL, '去骨母鸡肉300克，熟火腿50克，口蘑15克，鸡蛋5个，小白菜苞12个，熟猪油750克（实耗50克），料酒25克，精盐5克，味精1.5克，胡椒粉0.5克，白糖2.5克，干淀粉50克，鸡清汤500克。');
INSERT INTO `recipe` VALUES (1600, 74, '沙律海鲜卷', '1.鲜贝、虾仁洗净用开水焯好，同蟹柳切成丁，加盐、味精、姜汁、卡夫奇妙酱搅拌成馅，用威化纸包卷，粘鸡蛋液，滚粘面包渣。\r\n2.起锅放油烧六成热，投入海鲜卷，炸至微黄捞出控油，码盘即可。', NULL, NULL, NULL, '鲜贝100克，虾仁100克，蟹柳100克，面包渣50克，鸡蛋2个，卡夫奇妙酱50克，盐、味精、姜汁各少许，威化纸12张，油150克。');
INSERT INTO `recipe` VALUES (1601, 74, '上汤焗鲍翅', '把大鲍鱼清洗干净，热锅里倒入色拉油约1000克，加热至油温150度时，放入大鲍鱼，溜过把油沥干，大沙煲一个，用竹篾垫底放入花肉、老鸡、生姜、香菜头、葱、大红椒、生抽10克、盐10克、胡椒粒加水1000克，倒入大鲍鱼，加盖，先武火，后文火约三小时至大鲍鱼靠鲶身为止，起盖，收汤汁，捞起大鲍鱼，摆盘，原汁勾芡上麻油，淋在大鲍鱼上面即成。', '宴会高档菜肴，浓香入味。', NULL, NULL, '澳鲍5只（每只约2斤），花肉600克，老鸡1000克，李锦记生抽10克，生姜5克，大红椒5克，香菜头10克，生葱2根、胡椒粒、小麻油精5克、色拉油1000克（实用50克）、生粉5克、盐10克、味精10克。');
INSERT INTO `recipe` VALUES (1602, 74, '酸辣狗肉', '1.将狗肉去骨，用温水浸泡并刮洗干净，下入冷水锅内煮过捞出，用清水洗2遍，放入砂锅内，加入拍破的葱、姜、桂皮、干红椒、绍酒25克和清水，煮至五成烂时，切成5厘米长、2厘米宽的条。将泡菜、冬笋、小红辣椒切末，青蒜切花，香菜洗净。 \r\n2.炒锅置旺火上，放入熟油50克，烧至八成热时入狗肉爆出香味，烹绍酒，加入酱油、精盐和原汤，烧开后倒在砂锅内，用小火煨至酥烂，收干汁，盛入盘内。 \r\n3.炒锅内放入熟猪油，烧至八成热，下入冬笋、泡菜和红辣椒煸几下，倒入狗肉原汤烧开，放入味精、青蒜，用温淀粉调衡勾芡，淋入芝麻油和', '此菜香味浓郁，肉质鲜嫩软烂，芡汁稠浓，辣中带酸。', '选用膘肥体壮、健康无病的狗为上。 狗肉有干腥味，刚宰杀时用温水洗去腥味。煨制时用小火，中途不可加汤，时间2小时左右。酸泡菜制洗：将水坛洗净抹干，放入温开水2500克，再加精盐150克、花椒25克、八角5克、白酒100克、白糖50克、黄醋50克，然后将需要浸泡的蔬菜洗净晾干，盛入坛内密封浸泡一二天，即成泡菜。', NULL, '鲜狗肉1500克，香菜200克，泡菜100克，干红椒5只，冬笋50克，绍酒50克，小红辣椒15克，精盐5克，青蒜50克，酱油25克，味精1.5克，醋15 克，胡椒粉1克，湿淀粉25克，桂皮10克，芝麻油15克，葱15克，熟猪油100克，姜15克。');
INSERT INTO `recipe` VALUES (1603, 74, '双色鱿鱼卷', '1.鲜墨鱼、水发鱿鱼去头尾，直刀、斜刀交叉切（不可切透），在改刀为菱形块，用加盐、料酒的沸水烫起卷，捞出控水。油菜心用开水焯后码在盘中。\r\n2.起锅放油烧热，下入姜汁、泡椒汁、番茄酱、盐、料酒、白糖、蒜汁，水淀粉勾玻璃芡，投入鱿鱼卷翻炒均匀，淋明油出锅，装盘。\r\n3.另起锅放少许油，下入姜蒜汁、盐、味精、胡椒粉、鸡汤，水淀粉勾芡，下入墨鱼翻匀，淋明油出锅与鱿鱼同装盘中即可。', '红、白相间，菜色美观，同时可品尝两种口味。', NULL, NULL, '鲜墨鱼300克，水发鱿鱼300克，油菜心8棵，泡椒汁5克，姜汁5克，蒜汁5克，白糖3克，白醋2克，盐、味精各适量，料酒20克，油100克，胡椒粉3克，番茄酱25克，水淀粉、鸡汤各适量。');
INSERT INTO `recipe` VALUES (1604, 74, '酸辣鸡丁', '1.将鸡腿肉去骨剞上花刀后切丁，用蛋清液、淀粉上浆。柿子椒切块。起锅放油烧热，投入鸡丁滑散，捞出控油。\r\n2.锅留底油，放干辣椒爆香，投入鸡丁及调料翻炒，淋入水淀粉、明油、香油处锅。', '鲜咸醇香，酸辣适口。', NULL, NULL, '鸡腿肉250克，柿子椒50克，干辣椒2克，盐2克，酱油3克，醋4克，味精2克，料酒3克，淀粉10克，鸡蛋清20克，清油80克，香油少许。');
INSERT INTO `recipe` VALUES (1605, 74, '酸辣鱿鱼', '鱿鱼剞十字花刀，切成长方形的片，在70度水中汆成笔筒形，放碱水中浸30分钟捞出，漂去碱味，加调料、湿淀粉入味后下8成热油中汆熟捞出。 锅留底油，下肉末、泡辣椒炒出香味，下鱿鱼，加酱油、黄醋、味精合炒，再加清汤烧开勾芡装盘即成。', NULL, NULL, NULL, '水发鱿鱼300克，瘦猪肉50克，泡菜25克，泡辣椒适量。');
INSERT INTO `recipe` VALUES (1606, 74, '汤泡菊花鱼', '1.葱和姜捣烂用料酒和水取汁。火腿切成末。凤尾菌剔去根部冼净，下入沸水锅内氽过待用。香菜冼净留叶。\r\n2.桂鱼肉切成10厘米长的段，片成4厘米宽的薄片，3厘米宽处切成丝，1厘米连着，成为梳子形状，用葱姜酒汁和精盐腌好后，两面沾上干淀粉，由一端滚成卷，用手捏住连着的鱼肉，摇摆一下使鱼丝散开，放在抹油有柄的漏板上，中心放点黄色鱼茸，按点火腿末，形似盛开的菊花。\r\n3.锅内放入鸡汤、凤尾菌、精盐、味精烧沸，调好味，撇去泡沫，加入香菜叶、胡椒粉装入汤碗内。另用锅放入沸水，将漏板上的菊花鱼下入沸水锅氽熟，取出放入汤', '桂鱼菜馔，花样繁多，论其造型，此菜可谓独放异彩。洁白的菊花鱼飘浮于汤面，中间点缀红黄花蕊，好似花朵盛开，争奇斗艳，引人入胜。汤泡菊花鱼，刀工细腻，制作精巧，桂鱼肉鲜软滑嫩，凤尾菌质脆味鲜，鸡肉汤清澈鲜美，香菜叶缕缕清香，堪称创新菜之佼佼者。', NULL, NULL, '净桂鱼肉400克，嫩凤尾菌250克，黄色鱼茸50克，熟瘦火腿15克，香菜100克，料酒10克，精盐5克，味精1克，胡椒粉1克，葱10克，姜10克，干淀粉60克，鸡清汤700克，鸡油10克。');
INSERT INTO `recipe` VALUES (1607, 74, '桃源铜锤鸡腿', '1.葱、姜捣烂用料酒取汁。鸡蛋去黄用清。香菜摘洗干净。蕃茄洗净。\r\n2.将鸡腿上残毛挟去，去净骨（腿筒子骨保持待用）。鸡腿肉用刀和刀背捶松，砸断筋络，砍成5厘米宽的大块，用葱姜酒汁、精盐、白糖、花椒子、胡椒粉和味精腌约1小时，然后挑去花椒子，用鸡蛋清1个、干淀粉10克调匀上浆，再用筒子骨穿入鸡肉做成锤形。 \r\n3.鸡蛋清（5个）用筷子打起泡，加入干淀粉40克，调制成雪花蛋糊。\r\n4.将花生油烧至六成热时端锅离火，把穿好的鸡腿逐个裹上雪花蛋糊，下入油锅，再置中火上炸熟，呈浅黄色捞出，即成铜锤鸡腿。\r\n5.将', '此菜为湖南的传统名菜，松、脆、香、酥，味感细腻，为宴席中的大菜。', NULL, NULL, '桃源产嫩子鸡腿10个（重约900克），鸡蛋6个，大蕃茄2个，香菜200克，花生油1000克（实耗150克），料酒50克，精盐5克，味精2克，胡椒粉0.5克，葱15克，姜15克，白糖5克，花椒子20粒，干淀粉50克，香油15克。');
INSERT INTO `recipe` VALUES (1608, 74, '五彩鲜贝', '1.将胡箩卜球，黄瓜球连同草菇，香菇用开水焯一遍，鲜贝用淀粉上浆。\r\n2.起锅放油烧热，将鲜贝滑透。 \r\n3.锅留底油，放入全部主配料及盐，味精，料酒。胡椒粉煸炒，淋少许水淀粉勾芡，淋明油出锅。', '色泽美观，味道鲜美。', NULL, NULL, '鲜贝300克，胡萝卜球20克，黄瓜球20克，草菇20克，水发香菇5克，盐2克，味精3克，料酒5克，胡椒粉2克，淀粉，油适量。');
INSERT INTO `recipe` VALUES (1609, 74, '五元神仙鸡', '1.将鸡宰杀去毛，开膛取出内脏，洗净。入开水锅中焯一焯，捞出洗净，去嘴尖、脚爪、切掉下凳和尾臊，砸断大腿骨待用。\r\n2.桂圆、荔枝去壳，莲心肉（去皮去芯）。红枣洗净，与整鸡同时放入瓦钵内、加冰糖、精盐、清水750克、上笼蒸约两小时，再放入洗净的枸杞子，蒸五分钟取出，用手勺将整鸡翻过身背朝天，撒上胡椒粉即成。', '色泽淡红，肉嫩味鲜，咸甜适口，富有营养，具有较强的滋补功能。', NULL, NULL, '嫩母鸡1只（1250克左右）、桂圆、荔枝、红枣、各15粒、莲子肉25克、枸杞子15克、冰糖50克、胡椒粉1克、精盐3克。');
INSERT INTO `recipe` VALUES (1610, 74, '香桃鹌鹑蛋', '1.将肥膘肉、荸荠切成米粒状。葱、姜捣烂加料酒取汁。青椒洗净去蒂、去籽。切成小菱形片。香菜摘洗干净。\r\n2.将虾仁洗净沥干，捶剁成细茸，放入精盐、味精、胡椒粉、葱姜酒汁、荸荠米、肥膘米、湿淀粉，搅拌成馅。\r\n3.将鹌鹑蛋下入冷水锅煮熟捞出，剥壳洗净，切成两半，在截面沾上干淀粉。\r\n4.将面包切成桃形薄片20片，摊放木板上，逐一铺一层虾仁馅，再贴上半边鹌鹑蛋，尖的一端贴上火腿末，圆的一端插上两片青椒，即成鲜桃形，然后摆在有柄的漏板上。\r\n5.炒锅置旺火上，放入猪油烧至五成热，将摆放香桃鹌鹑蛋的漏板，轻轻放入', '鹌鹑蛋每100克含蛋白质13－15克，脂肪11－15克，还含有丰富的多种维生素。\r\n鲜桃是人们喜爱的水果，民间有“寿桃”之说。向老人祝寿，多献寿桃以示庆贺。此菜即仿鲜桃之形而制。色泽明艳，焦酥鲜嫩，口感特佳，既富于营养，又寓有吉庆之意。', NULL, NULL, '新鲜鹌鹑蛋12个，虾仁150克，猪肥膘肉50克，熟瘦火腿25克，削皮荸荠70克，咸面包150克，青椒50克，香菜100克，鸡蛋3个，熟猪油1000克（实耗100克），料酒25克，精盐5克，味精1克，胡椒粉1克，葱15克，姜15克，白糖5克，干淀粉5克，湿淀粉30克，香油10克。');
INSERT INTO `recipe` VALUES (1611, 74, '金鱼系莲', '将干鱿鱼去须，碱发好，漂洗干净，在正面的一边剞上十字花刀，另一边切0.3厘米粗的丝，不要切断，再切成4厘米的片，即成金鱼形，加精盐0.5克、干淀粉25克拌匀。将鲜红椒、泡菜、蒜瓣、水发香菇切成米粒丁。味精、干淀粉25克、醋、清水10克兑成汁。 鸡蛋清搅匀，拌入虾料子内。取小酒杯12个，逐个抹上熟猪油，将鸡 蛋清、虾料子放入杯内，周围镶入5粒青豆，中间放一粒青豆，上笼蒸2分钟，即成。', NULL, NULL, NULL, '干鱿鱼200克，虾料子100克，鲜红椒50克，醋15克，泡菜25克，鸡蛋清3个，肉末50克，精盐5克，水发香菇5克，味精1克，香菜25克，干淀粉50克，青豆15克，蒜瓣15克，芝麻油10克，熟猪油750克（实耗150克）。');
INSERT INTO `recipe` VALUES (1612, 74, '潇湘三味鸡', '1.葱姜一半捣烂，用料酒取汁，余下一半切成米。冬菇去蒂洗净，与冬笋同切米粒状，下锅炒熟入味。咸面包切成直径3厘米、厚0.4厘米的圆片13片。白菜苞洗净，下锅烫熟入味。火腿切成兰花形状。香菜摘叶留茎洗净。\r\n2.将鸡翅切成排骨形12个，用葱姜酒汁、精盐、味精稍腌，另加鸡汤上火煨至酥烂，取出翅骨。蛋清用筷子打起发泡，加入干淀粉搅拌调制成蛋糊，然后将鸡翅逐一在蛋糊中拖过，下入油锅炸呈金黄色捞出，摆放在盘中的雀巢内。锅内放油，下入蕃茄酱、白糖、米醋、湿淀粉、鸡汤，推动手勺，收成浓汁浇盖在鸡翅上。\r\n3.将鸡肫洗净', '潇湘三味鸡是湖南近年来的创新佳肴，由铜锤鸡翅、菊花鸡肫、芙蓉鸡塔三个部分组成。此菜选用了鸡身上三种不同质地的原料，即鸡翅、鸡肫和鸡脯；调配了三种不同的颜色，一为金黄，二为深红，三为洁白；精制了三种不同的形状，即铜锤、菊花和鸡塔；烹出了三种不同的质感和味道，一是酸甜酥烂，一是麻辣脆爽，一是咸鲜软嫩。成菜色彩艳丽，装盘美观，制作精细，味别多样，真可谓匠心独运。此菜曾获第三届全国烹饪技术比赛金牌奖。', NULL, NULL, '鸡翅8个，鸡肫8个，鸡脯肉250克，猪肥膘肉50克，水发冬菇50克，净冬笋50克，火腿20克，白菜苞30个，香菜20克，鸡蛋4个，咸面包250克，香芋雀巢1个，花生油1500克（实耗100克），料酒30克，精盐12克，味精2克，胡椒粉2克，葱20克，姜20克，蕃茄酱30克，白糖10克，米醋10克，干淀粉30克，湿淀粉20克，鸡清汤200克，辣椒油10克，花椒油10克。');
INSERT INTO `recipe` VALUES (1613, 74, '银针鸡汁鱼片', '1.将桂鱼肉洗净，片成3厘米长、1厘米宽、0.3厘米厚的片，用蛋清、精盐、湿淀粉调匀上浆。火腿切成2厘米长、1厘米宽、0.3厘米厚的片。冬笋切梳形片。口蘑片成片。银针茶盛入透明小玻璃杯中。\r\n2.炒锅置旺火上，放入鸡清汤，加精盐、味精烧沸，倒入汤碗中。再将杂骨汤烧沸，下入鱼片、冬笋、口蘑、菜苞氽熟捞出，放入鸡汤碗内。然后，在盛有银针茶的玻璃杯内，冲入沸水，置汤碗中，汤中撒上胡椒粉，淋入鸡油，盖上碗盖端上桌，待银针茶泡发竖立水中时，揭开盖，再将银针茶倒入汤碗中即成。', '银针鸡汁鱼片是巴陵全鱼席中的著名汤菜。相传“巴陵全鱼席”之名系清乾隆皇帝御赐，现经改进后，包括鱼菜、鱼点、鱼拼、鱼汤等20多种菜肴。君山银针是有1000多年历史的传统名茶，此菜以鱼片、银针、鸡汁配合，白绿相间，分外淡雅，鱼片鲜嫩，鸡汁味美，银针飘香，是一独具地方风味特色的创新佳肴。', NULL, NULL, '净桂鱼肉200克，瘦火腿肉25克，净冬笋25克，水发口蘑10克，小白菜苞12个，君山银针茶1克，鸡蛋清1个，鸡油10克，精盐2克，味精1克，胡椒粉0.5克，湿淀粉5克，鸡清汤750克，杂骨汤500克。');
INSERT INTO `recipe` VALUES (1614, 74, '油淋糯米鸡', '1.鸡宰杀去净毛、去净骨，鸡皮保持完整，取下部分鸡肉待用，用拍破的葱、姜、花椒子、精盐、酱油、料酒和白糖腌约1小时。香菜择洗干净。\r\n2.将鸡肉、肥膘肉、火腿、泡发的金钩、去蒂洗净的冬菇都切成小指头大小的方丁。 \r\n3.糯米淘洗干净，放入沸水锅氽过，用冷水过晾，沥干水分，加入鸡肉及上述配料，用精盐、味精、胡椒粉拌匀制成馅。再将馅从鸡的颈皮开口处灌入，用针线缝好，并用手轻轻压平，上笼蒸2小时左右，至皮软馅熟为准，取出后去掉葱、姜、花椒子。\r\n4.食用时，将花生油烧七成热，在鸡身上抹一层干淀粉，下入油锅，炸焦', '糯米每100克约含蛋白质5.1－8.1克，碳水化含物75.6－85克，可提供热量343－363千卡。中医认为其味甘性温，有补中益气功效。油淋糯米鸡系湖南的传统名菜。以母鸡肉和糯米为主料，配以火腿、金钩、冬菇等多种鲜料，灌入鸡腹以保持鸡形，采取先蒸制后炸煎而成。成品油润软糯，鲜香可口。', NULL, NULL, '肥母鸡1只（重约1250克），糯米200克，猪肥膘肉100克，火腿50克，金钩25克，水发冬菇50克，香菜150克，花生油1000克（实耗100克），料酒50克，精盐5克，味精1.5克，胡椒粉1克，葱15克，姜15克，白糖10克，酱油10克，花椒粉0.5克，花椒子20粒，干淀粉25克，香油15克。');
INSERT INTO `recipe` VALUES (1615, 74, '油淋庄鸡', '1.将净鸡沥去水，葱姜拍破，加精盐、白糖、花椒子拌匀，涂抹鸡身内外，盛入瓦钵内腌约1小时，去掉葱姜。\r\n2.取大瓦钵1只，用竹箅子垫底，将鸡放入，再放入花椒子、酱油、料酒，冰糖和清水，置旺火上烧沸，然后移小火上煨至软烂为止，取出沥干。\r\n3.炒锅置旺火上，放入花生油，烧至八成热，把煨好的整鸡用铁钩子钩住翅膀，手持钩柄悬置油面，用手勺舀沸油淋在鸡身上，先淋鸡身、鸡腿，后淋鸡背、鸡头，肉厚处要多淋几次，至鸡皮起酥，呈深红色为止。\r\n4.将鸡置砧板上，剔去胸骨、脊骨和腿、翅粗骨，剁去脚爪，将鸡头、鸡颈从中劈开，', '此菜，鸡肉香酥软烂，鲜美绝伦，配以4小碟佐食，更是锦上添花，味道更美。', NULL, NULL, '肥嫩母鸡1只（重约1500克），花生油1500克（实耗100克），料酒50克，精盐8克，花椒子1.5克，葱25克，姜25克，白糖5克，冰糖10克，原汁酱油50克。');
INSERT INTO `recipe` VALUES (1616, 74, '油焖火焙鱼', '1.用小刀挖去嫩子鱼的鳃，去内脏，洗净，沥干水，用炒锅焙成两面呈金黄色时取出。葱切花。小红椒、姜、蒜和紫苏叶均切末。酱油、醋、味精、杂骨汤、料酒、葱花加香油兑成汁。\r\n2.炒锅置旺火上，放入花生油，烧至八成热，下火焙鱼炸一下，迅速移至小火上煎至酥香，下入小红椒、姜、蒜、紫苏叶末，加精盐炒几下，随即倒入兑好的汁子，稍焖软，收干汁，盛入盘内即成。', '湖南河网密布，港汊纵横，池塘众多，沟渠通达，水产资源非常丰富。特别是广大农村，人们早出晚归，捞捉鱼虾，习以为常，自成乐趣，因此，民间常以鱼虾为菜。油焖火焙鱼即是其中的一种，是湖南民间的传统家常菜，现已登上宾馆、酒家的大雅之堂。火焙鱼是用铁锅焙干的小鱼，成品色泽焦黄，质地酥软，口感香辣，连骨而吃，软中带脆，别有风味。特别是长沙饭店特一级烹调师谭添三所烹制的油焖火焙鱼，令人胃口大开，回味无穷。', NULL, NULL, '小嫩子鱼500克，小红辣椒15克，蒜瓣15克，紫苏叶5克，花生油150克，料酒25克，精盐5克，味精1克，酱油10克，醋10克，葱10克，姜10克，杂骨汤100克，香油15克。');
INSERT INTO `recipe` VALUES (1617, 74, '鱿鱼肉丝', '1.鱿鱼切丝，用开水焯好，猪肉丝用淀粉上浆。\r\n2.起锅放油烧热，下猪肉丝滑散，控油。\r\n3.锅留底油，下入鱿鱼丝，猪肉丝，加配料，调料翻炒，用水淀粉勾芡，淋明油，香油出锅。', '色泽清新，味道鲜美。', NULL, NULL, '鱿鱼150克，猪肉丝100克，柿子椒丝30克，冬笋丝30克，盐2克，味精2克，酱油3克，料酒5克，油30克，水淀粉，香油各适量。');
INSERT INTO `recipe` VALUES (1618, 74, '玉麟香腰', '1.将生芋艿削去皮，切成0.5厘米厚的菱形片，用绍酒25克、精盐1克拌匀，腌十分钟，沥去水，放入六成热的油锅里炸呈金黄色时捞出，放在大碗里垫底。\r\n2.将带皮五花肉洗净，切成7厘米长、0.7厘米厚的片盛入碗内，用酱油5克，绍酒25克、精盐0.1克腌两分钟，上笼蒸熟，连同原汁倒入大碗，将五花肉排列在芋艿上。\r\n3.将荸荠洗净去皮，切成细末，肥肉（50克）剁成肉泥、加八角粉0.5克、精盐1克、面粉50克调匀、挤成直径0.5厘米的荸荠丸，放入七成热的油锅里，炸熟成金黄色，捞出，排放在大碗的周围（五花肉上面）。\r', '形似宝塔，用料众多，量多丰满，味道多样。', NULL, NULL, '猪腰100克、猪肥瘦肉（各半）400克、猪肥膘肉100克、带皮猪五花肉250克、净桂鱼肉100克、芋艿500克、荸荠300克。水发玉兰片50克、水发香菇25克。面粉100克、干淀粉55克、湿淀粉20克、鸡蛋3个，鸡蛋清2个、绍酒75克、八角粉1克、葱段15克、姜片10克、酱油25克、味精1.5克、胡椒粉0.5克、精盐7克、肉清汤300克、芝麻油1克、熟猪油1000克（实耗165克）。');
INSERT INTO `recipe` VALUES (1619, 74, '原汁武陵水鱼', '1.将五花肉洗净，切成1厘米厚的块，下沸水锅内氽过洗净。火腿切成边长为3厘米的方片。蘑洗净片成片。蒜瓣洗净。葱白切段，姜一半切米粒，加醋兑成汁，余下的葱、姜拍破。\r\n2.将水鱼宰杀洗净，下沸水锅中烫一下取出，搓掉薄膜，再从腹部剖开去掉内脏，剔去骨、剁去头、爪尖，改切成边长4厘米的块，置碗中，加料酒、精盐腌约15分钟取出，挤干水。\r\n3.砂锅内垫入竹箅子，放入拍破的葱姜、五花肉、蒜瓣、火腿片、口蘑片和水鱼块，再加入料酒、胡椒粉、精盐和清水，用盘盖上，在旺火上烧沸，撇去泡沫，移至小火上煨约30分钟，至水鱼软烂', NULL, NULL, NULL, '武陵活水鱼2只（重约2000克），猪五花肉300克，熟火腿50克，口蘑25克 ，熟猪油50克，精盐5克，味精2克，料酒100克，胡椒粉2.5克，黄醋50克，葱25克，姜25克，蒜瓣50克，鸡油15克。');
INSERT INTO `recipe` VALUES (1620, 74, '云托八鲜', '1.将鱿鱼、海参、鱼皮、香菇用开水焯好。虾肉、鲜贝用淀粉上浆。\r\n2.黄瓜切滚刀块，南豆腐用水稍煮。\r\n3.起锅放油烧热，将鲜贝、虾肉入锅滑散捞出。\r\n4.锅留底油将主、配料投入锅中，放调料、鸡汤焖约5分钟，同时将南豆腐放入沙锅，国内“八仙”用湿淀粉勾芡，加黄瓜倒入沙锅即可。', '多料多味，营养丰富。', NULL, NULL, '鱿鱼、海参、香菇、虾肉、鲜贝、蟹肉、鱼皮各30克，黄瓜50克，南豆腐1盒，盐2克，味精2克，酱油3克，胡椒粉2克，料酒2克，鸡汤、淀粉、油各适量。');
INSERT INTO `recipe` VALUES (1621, 74, '炸八块', '1.花生米用盐炒熟去皮剁碎，葱一半切成花，余下葱和姜一起拍破。 \r\n2.将鸡肉去骨后，用刀背捶松，砍成4.5厘米见方的肉块，用料酒、盐、糖、葱姜、花椒、味精腌约一小时后，挑去花椒、葱和姜，再用蛋清、湿淀粉浆好，粘上碎花生米。 \r\n3.锅内放花生油烧沸，逐块下入油锅炸一下即捞出，待油锅中水分烧干时，再下入油锅炸焦酥呈金黄色，滗去油，撒花椒粉、葱花，淋香油，摆入盘中，香菜拼边即成。', '此菜传统名肴，选用子鸡，炸成八大块，故名。颜色金黄，外酥里嫩，咸鲜味美，佐酒最宜。', '鸡宰杀去尽毛，开膛去内脏洗净后，左手拿住鸡翅膀，头朝右前方，按放在砧板上，右手持刀将鸡背部从颈部至尾部划一刀，破开皮肉。用刀将翅膀筋骨割断，再割开鸡胸肉，刀要割到胸骨。右手用刀按住鸡骨骼，左手捏住割断筋骨的翅膀向后拉，肃下一边鸡肉。翻边照上述方法剥下另一边鸡肉，使两边鸡肉与骨骼脱离。在鸡身骨骼取出后，腿骨仍在鸡肉中，然后将刀在鸡腿肉上靠骨处划开刮一刀，使鸡骨露出，这时将大小腿中部关节筋骨割断，并在鸡腿与鸡脚杆关节骨连节处确断，左手拿住鸡腿，翻转一折，右手将刀按住骨头，左手将鸡腿肉往后拉，把两节腿骨去掉（鸡', NULL, '嫩子鸡3只（2000克）、料酒50克、盐5克、花生米100克、味精2克、鸡蛋2个、糖10克、香菜100克、香油25克、花生油1000克（约耗100克）、花椒20粒、姜15克、花椒粉2克、湿淀粉50克、葱15克。');
INSERT INTO `recipe` VALUES (1622, 74, '子龙脱袍', '1.将鳝鱼放在砧板上一刀划开皮，然后用刀按住肉，迅速一撕，扒下皮来。将鳝鱼肉在沸水中氽一下，剔去刺，再切5厘米长、0.3厘米粗的丝。青辣椒洗净，与玉兰片、水发冬菇均切成长4厘米的细丝。鲜紫苏叶切碎。\r\n2.将鸡蛋清磕入碗内，搅发起泡沫后，放入干淀粉、精盐调匀，再放鳝丝抓匀上浆。 \r\n3.炒锅置中火上，放入猪油，烧至五成热，下鳝丝，用筷子划散，约30秒钟，倒入漏勺沥油。\r\n4.炒锅内留油50克，烧至八成热，下玉兰片、青辣椒、冬菇丝、精盐煸炒一会，再下鳝丝，烹料酒合炒。接着将黄醋、紫苏叶、湿淀粉、味精、肉清汤', '此菜制作精细，鳝鱼去皮、切丝需很高的刀工技巧。在烹调中，火候要适当，才能保其鲜嫩。成品以白色为主，绿、褐两色衬托其间，菜色调和。鳝鱼丝滑嫩香辣，是湖南的传统名菜。', NULL, NULL, '活鳝鱼750克（选用中号的），水发玉兰片50克，水发冬菇25克，鲜青辣椒50克，香菜25克，鲜紫苏叶10克，鸡蛋清1个，熟猪油500克（实耗100克），料酒25克，精盐5克，味精1克，胡椒粉0.5克，黄醋2.5克，干淀粉25克，湿淀粉25克，肉清汤25克，香油10克。');
INSERT INTO `recipe` VALUES (1623, 74, '紫龙脱袍', '1.将鳝鱼去皮、骨、头，净膛洗净切6厘米长丝，用鸡蛋液、淀粉上浆。\r\n2.起锅放油烧热，下入鳝鱼丝滑散，捞出控油。冬笋丝、柿子椒丝、香菇丝过油。\r\n3.锅留底油，投入葱姜丝爆香，放入鳝鱼、冬笋、香菇、柿子椒丝、盐、味精及料酒，翻炒均匀，撒入胡椒粉，淋香油，放香菜即可。', '鲜香味美，诱人食欲。', NULL, NULL, '鳝鱼500克，冬笋丝50克，红柿子椒丝30克，香菇丝10克，葱、姜丝各10克，香菜3克，鸡蛋液30克，盐2克，味精2克，淀粉30克，料酒30克，胡椒粉、香油、油各适量。');
INSERT INTO `recipe` VALUES (1624, 74, '组庵鱼翅', '1.将鸡宰杀去净毛，开膛去内脏洗净，砍成大块，猪肘肉刮洗干净后砍成块，一起下入开水锅内煮过捞出，用清不洗净血沫。葱、姜拍破。干贝掰去边上老筋，洗净后放入葱、姜、料酒和水，上笼蒸发待用。\r\n2.取大瓦钵一只，用竹箅子垫底，放入用白稀纱布包好的鱼翅。用鸡汤，加入料酒和葱、姜，在旺火上烧开后移到小火煨约半小时。从锅内取出鱼翅，这道汤倒掉不要，铺上猪肘肉、葱结、姜片、重新产入鱼翅、鸡块，再加入干贝汤、绍酒、精盐、清水1500克用盘盖上，在旺火上烧开，再移至小火上煨约4小时，直至鱼翅软烂、浓香、柔软。然后离火去掉鸡', '此菜是清末湖南督军谭延凯家宴名菜，谭延凯字组庵，是一位有名的美食家，他的家厨曹敬臣，跟随谭先生多年，摸透了谭的食好，经常花样翻新，他将红煨鱼翅的方法改为鸡肉、五花肉与鱼翅同煨，成菜风味独特，备爱谭延凯赞赏。组庵先生无论自己请客或别人请他吃饭，都按他的要求制作此菜，后来人们称为组庵大菜，饮誉三湘。此菜颜色淡黄、汁明油亮、软糯柔滑、鲜咸味美、醇香适口。', '鱼翅选用玉结鱼翅，玉结鱼翅是鱼翅中的上品。水发时将干鱼翅沿翅尖剪去边须，下冷水锅烧开后离火，静置数小时，待水凉后，将鱼翅捞入盛有清水的木盆内，用刀刮去沙粒和污迹，漂洗干净。锅内垫上竹箅，放入鱼翅和清水，上面压盖铁箅子，上火煮开后离火，涨泡数小时，捞入木盆，去掉翅骨和腐肉，清洗干净，逐个整齐放在锅内的竹箅上，换清水煮开离火，加盖涨泡数小时即成。煨制注意掌握火候，长时间煨制4小时左右，使味汁逐渐渗入主料。检验的方法是：用筷子挟翅针的中间，两头下垂即可。', NULL, '水发玉结鱼翅2000克，精盐8克，味精2.5克，干贝50克，胡椒盐1克，肥母鸡肉1500克，葱结50克，猪肘肉1000克，姜片50克，绍酒150克，熟鸡油25克。');
INSERT INTO `recipe` VALUES (1625, 86, '比萨饼', '1.准备好生面团。同时，备好馅。在一个大杯里，放入火鸡、食盐、胡椒粉、罗勒、欧芹。 \r\n2.用保鲜膜包好，在微波炉里高档加热5分钟。然后放在一边冷却。 \r\n3.当准备完毕，把比萨面团分成均等的两份并做成两个10英寸的卷。预热至425度。 \r\n4.把西红柿浆涂抹在面团上，然后在面团上撒上烹饪好的火鸡的混合物，并吸出多余的水。 \r\n5.在上面加上泡软的长条蘑菇并加上尽量多的新鲜罗勒和奶酪。 \r\n6.用面团将馅包起来。把边用水弄湿并用一个叉子封好，送到烤箱里。 \r\n7.在上部刷上鸡蛋，并用小刀切一细长口为蒸气的', NULL, NULL, NULL, '1份比萨生面团、1茶匙精盐、1茶匙切好的新鲜罗勒、1/4茶匙辣椒、8盎司奶酪、2-3个干蘑菇（在半杯水里浸湿30分钟）、1磅火鸡、1/2匙新鲜的白胡椒粉、1-2大汤匙切好的意大利欧芹、2大汤匙西红柿糊、1个鸡蛋。');
INSERT INTO `recipe` VALUES (1626, 86, '比萨通心粉沙司', '把通心粉煮到沸腾，加入带有橄榄油的盐水。去水并放入碗里。放在电冰箱里冷藏。并加入调味品，装盘时用干酪加以装饰。', NULL, NULL, NULL, '1磅烹调好的意大利通心粉、橄榄油、食盐、未经加工的蔬菜或西红柿、葡萄或别的粒状水果、芹菜、橄榄、色拉调味品、干酪。');
INSERT INTO `recipe` VALUES (1627, 86, '番茄青蚝汤', '1.Marinara汁材料：蒜头（切碎）1粒 、橄榄油少许、银鱼柳茸10克、茄膏（罐头）。\r\n2.2汤匙Marinara汁做法：橄榄油加热后炒香蒜茸及银鱼柳茸；倒入茄膏，慢火煮约10分钟。\r\n3.汤做法：拍松蒜头，放橄榄油中煎至金黄色，转猛火加入青蚝；加入番茄粒和白酒，锅盖煮至所有青蚝打开；加入Marinara汁，以慢火煮至汤浓，洒上少许橄榄油，伴以芫茜即可。\r\n4.烹调时间：25分钟。', NULL, NULL, NULL, '（4人分量）连壳青蚝1千克、去衣蒜头3-4粒、干辣椒（切碎）1克 、白酒3汤匙、番茄（切粒）120克、意大利芫茜（切碎）10克、Marinara汁2汤匙、橄榄油少许、盐及胡椒各少许。');
INSERT INTO `recipe` VALUES (1628, 86, '番茄酸豆角鱼面', '1.用橄榄油将洋葱、大蒜8分钟小火爆香。\r\n2.加入墨鱼，淋上白酒拌炒。\r\n3.加入酸豆、番茄继续炒。\r\n4.最后再加煮熟的蔬菜面一起拌炒即成。', NULL, '备妥处理好的墨鱼。墨鱼快炒熟时再加入酸豆和番茄（1颗大番茄约等于4、5颗小番茄的量，可互相取代）。蔬菜面同样须先煮熟后再加入拌炒。', NULL, '酸豆50克，大番茄1颗，花枝50克，洋葱1/4颗，大蒜3瓣，白酒30毫升，意大利蔬菜面3球。');
INSERT INTO `recipe` VALUES (1629, 86, '干酪通心粉', '1.烹调通心粉，同时，把调料放在一个平底锅里。文火加热1-2分钟使其融化并混合在一起。 \r\n2.加入1-2汤匙水，使其具有粘性。 \r\n3.在上菜的时候，加入切好的洋葱、虾仁和红胡椒粉。', NULL, NULL, NULL, '/4-1/3磅干酪、4汤匙新鲜黄油、少许白胡椒粉。');
INSERT INTO `recipe` VALUES (1630, 86, '烤鸡配圆辣椒橄榄', '1.用刀把鸡一分为二。 \r\n2.锅烧热，加入橄榄油，再放入已用盐和胡椒调味的鸡炸至金黄色。在这时，加入大蒜煎成金色，再放入黑橄榄、红黄两种圆椒和迷迭香。 \r\n3.把所有的原料一起烧几分钟，倒些白葡萄酒，让其蒸发一会儿，放入番茄和鸡汤。再放入175度的烤箱烤20-25分钟，等熟了以后取出。 \r\n4.从沙司中取出鸡，把鸡保温，同时把沙司调味后放入烤箱加热。 \r\n5.把每块鸡胸切成4至5块，分别装入4只盆内，上面浇上沙司并根据个人喜好放一些装饰，通常是放些蔬菜。四人份的意大利风味菜就做成了。', NULL, NULL, NULL, '（四人份）鸡2只（只算胸和翅膀大约1.6公斤），红圆椒1只，切丝，黄圆椒1只，切丝，迷迭香2只，大蒜4个，白葡萄酒200毫升，黑橄榄20个，橄榄油150毫升，番茄150/200克，去皮切碎，鸡汤0.5公升，盐和胡椒。');
INSERT INTO `recipe` VALUES (1631, 86, '米兰式通心粉', '蘑菇在加有西红柿汁的奶油中煮熟，拌以熔融奶油和粉状干酪，并盛在盘中，通心粉中夹填上蘑菇。另外送上西红柿。', NULL, NULL, NULL, '通心粉60克，磨菇30克，粉状干酪15克，奶油20克，西红柿汁80克。');
INSERT INTO `recipe` VALUES (1632, 86, '磨坊主妇小龙虾─加莉娜风味', '1.把虾头切掉，再用剪刀剪开虾壳的边，把虾壳剥掉，将虾肉纵向切成两半，摘除虾肠。 \r\n2.在虾肉上涂抹盐和胡椒粉，然后撒放高筋面粉，用漏勺把虾肉捞起来，筛掉多余的高筋面粉。 \r\n3.将橄榄油和牛油倒进炒锅中加热，当油脂被充分烧热后把虾肉倒进炒锅中煎，带两面煎好之后，把虾肉捞出来控净油。 \r\n4.把煎好的小龙虾肉摆在盘子的中间，在虾肉上撒些芫荽碎叶、盐和胡椒粉，然后把番茄汁洒在虾肉上，最后从上方倒入银鱼风味的牛油汁。', NULL, NULL, NULL, '小龙虾16条、橄榄油30毫克、牛油20克。银鱼风味的牛油汁：牛油100克、银鱼1条、蒜1/2瓣、水瓜钮（醋渍）2汤匙。番芫荽（切碎）1汤匙、番茄汁100毫升、高筋面粉、盐、胡椒粉。');
INSERT INTO `recipe` VALUES (1633, 86, '蘑菇炒香肠', '把比萨面团放在烤箱里，并加热到500度。在一个油煎平底锅里加热油，并加入大蒜。洋葱和蘑菇。用中火烹调15分钟，直到洋葱和蘑菇变成褐色。这时加入香肠，香菜，西红柿浆，食盐和胡椒粉。把这些混合物撒在比萨面包皮上，再在上面撒上干酪。加热到400度并焙烤15-20分钟，直到干酪变成褐色。', NULL, NULL, NULL, '2茶匙精制橄榄油、2瓣切好的大蒜、1片切好的洋葱片、1磅蘑菇片、1-2磅瘦肉香肠、2茶匙新鲜的意大利香菜、2茶匙西红柿浆、食盐和胡椒粉、1/2杯干酪、比萨生面团。');
INSERT INTO `recipe` VALUES (1634, 86, '蘑菇烩牛肋排', '1.放入所有海鲜，炒至半熟时加入酱汁、鱼上汤及番茄粒慢火煮。 \r\n2.将螺丝粉加少许盐、油在水中煮熟备用。\r\n3.最后放入螺丝粉煮约2分钟即成。\r\n4.烹调时间：120分钟。', NULL, NULL, NULL, '（4人分量）牛肋骨约1千克、甘笋（切块）2条、洋葱（切块）1个、西芹（切块）2条、牛油50克、白酒1杯、鸡汤1杯、番茄（罐头）425毫升、蘑菇（切片） 150克、车厘茄 4个、薯茸200克。调味料：盐及黑椒少量、迷迭香1/2茶匙。');
INSERT INTO `recipe` VALUES (1635, 86, '奶油干酪沙司', '在细面条里加入橄榄油。用碗装好放有微波炉里，加热干酪，直到上面的乳融化。调好细香葱，食盐和胡椒粉。把它们倒入细面条中。装盘时用罗勒叶子装饰。', NULL, NULL, NULL, '1茶匙特制橄榄油、1/2杯干酪、切好的新鲜细香葱、调味用的盐和胡椒粉、罗勒叶子、烹调好的细面条。');
INSERT INTO `recipe` VALUES (1636, 86, '牛肉意大利面', '1.蕃茄剥开放入热水中使外皮分开。\r\n2.起油锅爆香牛肉，炒熟后起锅备用；将烫好的蕃茄去皮切成块备用。\r\n3.起油锅，放入洋葱炒软后加入大蒜爆香，加入红萝卜、芹菜拌匀后加入切好的蕃茄拌匀后加入炒好的牛肉。\r\n4.再加入意大利肉酱及迷迭香、月桂叶及红酒小火慢熬，等汤汁稍干即可。\r\n5.将煮好的酱料淋在煮好的意大利面上即可上桌。', NULL, NULL, NULL, '意大利面、牛绞肉、红蕃茄4个、意大利肉酱、洋葱、西洋芹菜、红萝卜、大蒜、月桂叶、G蔻粉、调味料、红酒。');
INSERT INTO `recipe` VALUES (1637, 86, '色拉', '把通心粉煮到沸腾，加入带有橄榄油的盐水。去水并放入碗里。放在电冰箱里冷藏。并加入调味品，装盘时用干酪加以装饰。', NULL, NULL, NULL, '1磅烹调好的意大利通心粉、橄榄油、食盐、未经加工的蔬菜或西红柿、葡萄或别的粒状水果、芹菜、橄榄、色拉调味品、干酪。');
INSERT INTO `recipe` VALUES (1638, 86, '烧牛扒', '1.先用青柠汁、蒜头、黑椒粉及油腌牛扒，于雪柜冷藏最少两小时，中间将牛扒反转让整块牛扒能均匀地腌好。 \r\n2.于烧烤前半小时取出牛扒解冻。 \r\n3.把烤架烧热，将牛扒的面向火放于烤架上烧。肉与肉之间预留一些空间，方便待会儿把肉反转。（通常烧烤时会把肉反转一次。较生的牛扒可烧七分钟便反转，半生熟的牛扒可烧五分钟，全熟的牛扒则需七分钟才反转。反转后将牛扒放于干净的烤架上烧。）', NULL, NULL, NULL, '牛扒4块各8安士重、青柠汁1/2杯、黑椒粉1茶匙、蒜头压碎2粒、油2汤匙。');
INSERT INTO `recipe` VALUES (1639, 86, '威尼斯海鲜面', '1.用橄榄油将洋葱、大蒜8分钟小火爆香。\r\n2.将海鲜料放入炒锅，并淋上白酒拌炒至熟。\r\n3.将炒好的海鲜料置入海鲜高汤中炖煮。\r\n4.放入九层塔和芹菜。\r\n5.最后加入煮熟的意大利面即成。', NULL, NULL, NULL, '贻贝2粒，虾仁50克，鱼肉50克，花枝50克，蟹脚肉50克，兰花蚌50克，蟹身1双，草虾1尾，洋葱1/4颗，大蒜3瓣，白酒30毫升，九层塔2枝，芹菜1枝，意大利面50克，海鲜高汤500毫升。');
INSERT INTO `recipe` VALUES (1640, 86, '鲜茄海鲜幼面', '1.25克酱汁做法：用橄榄油将牛油煮溶，加入鱼柳炒熟，倒入白兰地和鱼上汤，煮至剩下一半；加入番茄，用慢火炆20分钟后熄火备用。\r\n2.主菜做法：烧热油后炸蒜头至金黄色，取走蒜头后加入干辣椒；放入所有海鲜，炒至半熟时加入酱汁、鱼上汤及番茄粒慢火煮；用盐水以猛火烚意大利幼面约7分钟，沥干水分后放入锅炒；加入酱汁拌匀，略煮一会后即可上碟，撒上芫荽碎即成；\r\n3.烹调时间：25分钟。', NULL, NULL, NULL, '（4人分量）意大利幼面条（Linguine）320克、新鲜蚬250克、带子8只、青蚝8只、蒜头1粒、橄榄油1汤匙、意大利芫荽（切碎）5克、干辣椒（切碎）少量、鱼上汤2汤匙、番茄（切粒）60克。酱汁材料：（4人分量）虾50克、鱼柳50克 、白兰地1/2汤匙、罐头番茄100克、鱼上汤1汤匙、橄榄油少量、牛油。');
INSERT INTO `recipe` VALUES (1641, 86, '蚬肉周打汤', '1.烧油少许爆洋葱、西芹及烟肉粒，加入青红椒略兜盛起备用。\r\n2.牛油炒面粉，徐徐注入上汤，边加边搅透直至汤水加完为止，最后加入花奶及适量盐调味。\r\n3.将爆好之材料及蚬肉倾下待再煮沸即可上桌。', NULL, NULL, NULL, '蚬肉1/2杯(飞水过冷)、西芹粒 1/4杯、洋葱粒1/4杯、青淑粒1/4杯、红椒1只(切粒)、烟肉粒1片、忌廉汤料：牛油40克、面粉1/4杯、花奶1/4杯、上汤4杯。');
INSERT INTO `recipe` VALUES (1642, 86, '香火鸡馅饼', '1.准备好生面团。同时，备好馅。在一个打杯里，放入火鸡、食盐、胡椒粉、罗勒、欧芹。 \r\n2.用保鲜膜包好，在微波炉里高档加热5分钟，然后放在一边冷却。 \r\n3.当准备完毕，把比萨面团分成均等的两份并做成两个10英寸的卷。预热至425度。 \r\n4.把西红柿浆涂抹在面团上，然后在面团上撒上烹饪好的火鸡的混合物，并吸出多余的水。 \r\n5.在上面加上泡软的长条蘑菇并加上尽量多的新鲜罗勒和奶酪。 \r\n6.用面团将馅包起来。把边用水弄湿并用一个叉子封好，送到烤箱里。 \r\n7.在上部刷上鸡蛋，并用小刀切一细长口为蒸气的', NULL, NULL, NULL, '1份比萨生面团、1茶匙精盐、1茶匙切好的新鲜罗勒、1/4茶匙辣椒、8盎司奶酪、2-3个干蘑菇，在半杯水里浸湿30分钟、1磅火鸡、1/2匙新鲜的白胡椒粉、1-2大汤匙切好的意大利欧芹、2大汤匙西红柿糊、1个鸡蛋。');
INSERT INTO `recipe` VALUES (1643, 65, '川西肉豆腐', '1.肥瘦肉剁细，香菇、冬笋切小丁，鸡蛋摊皮，青笋切滚刀块，黄花、木耳洗净，泡椒、香葱切粒，花椒部分剁细，香辣酱加糖，蒜末调成味碟。\r\n2.肥瘦肉加香菇、冬笋丁、鸡蛋、豆粉、水、花椒粉、盐调成肉馅，用蛋皮裹成长条上笼蒸熟，凉后切片。\r\n3.用大碗把切好的青笋、黄花、木耳、粉丝装入碗内，把肉片码放在上面。\r\n4.锅内加少许油烧热后，用花椒、姜、葱炝锅，烧水煮沸后装进碗里，上笼蒸热后撒上香葱花、泡辣椒粒，配上香辣味碟即成。\r\n注意：\r\n制作肉卷时，鸡蛋、豆粉的比例要掌握好，不然会影响成形及口感。', '味道厚重、香醇、鲜美。', NULL, NULL, '猪肥瘦肉，香菇、冬笋、鸡蛋、香辣酱、糖、花椒、盐、香葱、姜、味精、青笋、黄花、木耳、苕粉丝、泡椒、蒜末、豆粉等。');
INSERT INTO `recipe` VALUES (1644, 86, '洋葱比萨', '在平底锅里将黄油融化，并加入洋葱用文火烹调30分钟直到变成褐色。调配好盐和胡椒粉，放在一边备用。预热到475度。在面包皮上加入洋葱，干酪和胡桃。如果需要的话，加入香草。转移到烤箱里加热，直到皮变成金黄色（这个过程大约需要12分钟。）用橄榄油轻轻地涂抹在面包皮的边缘。', NULL, NULL, NULL, '1大块比萨硬外皮、4茶匙黄油、3磅切好的洋葱片(大约3个大洋葱)、食盐和新鲜的胡椒粉、1/2杯胡桃、迷迭香或新鲜的香草、1杯蓝色的干酪、橄榄油。');
INSERT INTO `recipe` VALUES (1645, 86, '义式香脂醋酱拌田园沙拉', '1.将莴苣剥开清洗并弄干。 \r\n2.大蒜压碎后再细切。 \r\n3.将红椒、洋葱、芹菜、磨菇切细（切得非常细），小黄瓜切片。 \r\n4.将材料搅拌后并洒上柠檬汁。\r\n5.将拌好的沙拉放入冰箱约十分钟后，淋上义式香脂醋和橄榄油。', NULL, NULL, NULL, '生菜莴苣1束、长菜莴苣1束（选择性）、熟蕃茄2个、小黄瓜3条、红椒1个、洋葱1个、西洋芹菜1把、大小适中的磨菇15个、酱料、橄榄油、义式香脂醋、盐、胡椒、芥末粉、大蒜、柠檬。');
INSERT INTO `recipe` VALUES (1646, 86, '意大利炒饭', '1.鲜贝洗净，鸡脯肉洗净，切片后放于碗中加入精盐、胡椒粉及淀粉腌拌，待用。 \r\n2.小黄瓜洗净去头尾，切片；西兰花撕去老筋，洗净；番茄去蒂洗净，切块；鸡蛋打入碗中，打成蛋汁，待用。 \r\n3.炒锅置旺火上，倒入橄榄油烧热，放入鲜贝、鸡脯肉滑炒后即倒入蛋汁炒散，加入米饭及番茄酱、精盐、黑胡椒粉炒匀，接着加入小黄瓜片，西兰花及番茄块略炒一下，即成。', '色泽鲜艳，光亮油润，软糯鲜香。', NULL, NULL, '米饭300克，鸡脯肉30克，鲜贝30克，鸡蛋1只，小黄瓜、西兰花、番茄、胡椒粉、精盐、淀粉、番茄酱、橄榄油、黑胡椒粉。');
INSERT INTO `recipe` VALUES (1647, 86, '意大利奶酪包', '1.在面包的两面上少许油，在明火烤炉下上色。 \r\n2.涂上太帕纳得汁放上番茄，奶酪糠，用鲜胡椒粉调味。\r\n3.在明火炉下使其融化。\r\n4.在用前淋上一些油，用沙拉装饰。', NULL, NULL, NULL, '意大利面包片2个，橄榄油10毫升，太帕纳得汁30克，番茄粒50克，混合香草，鲜细碎1克，锅干左拉奶酪30克，黑胡椒碎调至够味，沙拉装饰1个。');
INSERT INTO `recipe` VALUES (1648, 86, '意大利式米饭', '大米拣净，洗涤，倒入锅中，加入少量清汤，边煮边用叉搅拌并逐渐浇入清汤。煮熟的米饭中加入奶油、碎干酪并仔细地搅匀，趁热食用。', NULL, NULL, NULL, '大米50克，清汤100克，奶油20克，干酪30克，盐适量。 大米50克，清汤100克，奶油20克，干酪30克，盐适量。');
INSERT INTO `recipe` VALUES (1649, 86, '意大利式沙拉浇汁', '1.大蒜、辣椒洗理净、切成碎末。\r\n2.将柠檬汁、盐和胡椒粉放入深容器中拌匀，慢慢加入菜油，边加边搅拌均匀，再加入冷开水，继续搅拌成乳糜状。\r\n3.最后加入大蒜末和辣椒末拌匀即成。', '浅黄色，味鲜香辣微酸，爽口解腻。常用于蔬菜沙拉。', NULL, NULL, '（6份）柠檬汁1大勺，熟菜油50毫升，大蒜3瓣，尖角红辣椒1个，精盐1/2小勺，胡椒粉适量，冷开水1大勺。');
INSERT INTO `recipe` VALUES (1650, 86, '意大利式虾仁', '1.虾仁去泥肠、大蒜、西洋芹切碎。 　　\r\n2.锅烧热，放奶油、蒜末炒香，加入虾仁、炒至虾仁变色后，加入a料煮至汤汁收干。\r\n3.烧半锅水放少许盐、将意大利面煮熟后捞出。\r\n4.煮熟的面条上、淋上虾仁酱汁、并撒些起司粉、芹菜末即可。', NULL, NULL, NULL, '意大利面半包，虾仁200克，大蒜3瓣，奶油半杯，西洋芹半根。a料：白酒半杯、番茄酱半杯、盐半茶匙、胡椒粉少许。b料：起司粉3大匙。');
INSERT INTO `recipe` VALUES (1651, 86, '意大利细面条', '1.把面条煮熟，西红柿切成块，焯好的豆角切成3cm长。\r\n2.茄子、柿子椒，切成片，葱头切细丝，大蒜切碎，香肠切成斜块。\r\n3.锅中放油加热，把2倒入锅中炒。\r\n4.将1和3混和用丘比千岛酱调制。', NULL, NULL, NULL, '细面条480g，香肠65g，茄子80g，西红柿65g，柿子椒30g，豆角30g，葱头65g，色拉油适量，大蒜65g，丘比千岛酱80g。');
INSERT INTO `recipe` VALUES (1652, 86, '意国野菇培根面', '1.野茹泡开后切成丝。\r\n2.难高汤放入野茹炖煮4至5小时。\r\n3.另起一锅用橄榄油将洋葱、大蒜碎末8分钟小火爆香。加入番茄续炒。\r\n4.在野茹难高汤中倒入鲜奶油，加热浓缩汤汁。\r\n5.将做法3、4和煮熟的意大利面一起拌炒。\r\n6.置盘时，撒上烤好的培根即成。', NULL, NULL, NULL, '野茹30克，培根25克，大番茄1颗，鲜奶油120毫升，难高汤500毫升，洋葱1/4颗，大蒜3瓣，意大利面50克。');
INSERT INTO `recipe` VALUES (1653, 86, '意大利细面条', '1.把面条煮熟，西红柿切成块，焯好的豆角切成3cm长。\r\n2.茄子、柿子椒，切成片，葱头切细丝，大蒜切碎，香肠切成斜块。\r\n3.锅中放油加热，把2倒入锅中炒。\r\n4.将1和3混和用丘比千岛酱调制。', NULL, NULL, NULL, '细面条480g，香肠65g，茄子80g，西红柿65g，柿子椒30g，豆角30g，葱头65g，色拉油适量，大蒜65g，丘比千岛酱80g。');
INSERT INTO `recipe` VALUES (1654, 86, '意国野菇培根面', '1.野茹泡开后切成丝。\r\n2.难高汤放入野茹炖煮4至5小时。\r\n3.另起一锅用橄榄油将洋葱、大蒜碎末8分钟小火爆香。加入番茄续炒。\r\n4.在野茹难高汤中倒入鲜奶油，加热浓缩汤汁。\r\n5.将做法3、4和煮熟的意大利面一起拌炒。\r\n6.置盘时，撒上烤好的培根即成。', NULL, NULL, NULL, '野茹30克，培根25克，大番茄1颗，鲜奶油120毫升，难高汤500毫升，洋葱1/4颗，大蒜3瓣，意大利面50克。');
INSERT INTO `recipe` VALUES (1655, 86, '意式烩大虾', '1.意大利面条煮熟，用凉水浸控干； \r\n2.大虾肉切段用油煎熟，葱头切丁用黄油炒黄，加番茄酱炒到油呈红色时，放盐，柠檬汁调好口味，把虾段放入烩之； \r\n3.意大利面条用黄油炒透，放盘中间成凹形将烩虾放入，上面浇奶油少司（稠些），再在奶油少司上贴青菜叶及削成花形的青，红椒片点缀。', NULL, NULL, NULL, '大虾肉1000克，意大利面条750克，奶油少司1000克，黄油150克，油100克，柠檬50克，盐15克，青椒150克，番茄酱150克。');
INSERT INTO `recipe` VALUES (1656, 86, '意式咖啡奶酪布丁', '1.预备烤制蛋糕\r\n（1）烤箱预热至180度。用涂刷把蛋糕盘四周涂上牛油，贴上牛油纸，再涂上牛油于纸面，加入2汤匙面粉，让它均匀地黏在蛋糕盘四周：倒出剩余的面粉。用涂刷轻轻擦上一层簿簿的牛油。\r\n（2）把面粉和盐一起筛过。用电动搅拌器把蛋搅匀，加砂糖，继续快速拌打蛋白至粘稠变浅色，拿起搅拌器会滴下长长似尾巴的蛋液时停止。整个过程大约需要5分钟。 \r\n（3）把三分之一的面粉筛在蛋液中，一只手用刮铲在中间由上而下的搅拌：另一只手握住碗不停地朝反时针方向转动，再把其他面粉分两次加入拌匀。\r\n（4）把拌匀的蛋粉混', NULL, NULL, NULL, '150毫升意大利浓咖啡，4汤匙牙买加咖啡，舌甜酒或咖啡酒（K8h1ua〕4汤匙，白兰地酒1汤匙，可可粉、蛋糕适量，牛油90克，面粉、盐适量，3个鸡蛋，90克细砂糖，玛上加邦奶酪混和物，6个蛋黄，60克细砂糖，500克玛士加邦奶酪，1茶匙云呢拿香油（Vanilla essence）。');
INSERT INTO `recipe` VALUES (1657, 86, '意式肉肠通心粉', '1.把焗炉预热至华氏425度。 \r\n2.把红椒粒、洋葱、蒜茸混和一茶匙橄榄油于焗炉碟上，放入焗炉焗二十五分钟，搅匀。 \r\n3.把香肠的外皮撕去，弄成肉碎，然后放在锅内煮至香肠转为粉红色为止，煮好的香肠备用。 \r\n4.把牛肉及煮好的香肠同炒。 \r\n5.把通心粉煮好，留下1/2 杯意粉水备用。 \r\n6.把焗好的蔬菜取出，加入炒好的牛肉和香肠，再加入紫苏、通心粉、芝士等材料，再放入焗炉内焗十分钟，如果太干的话，可把之前备用的意粉水放入同焗。', NULL, NULL, NULL, '意式通心粉1磅、香肠15安士、牛肉1/2杯、芝士1/2杯、红甜椒粒2杯、洋葱粒1杯、蒜头4粒剁碎、紫苏1/2杯、橄榄油1茶匙。');
INSERT INTO `recipe` VALUES (1658, 86, '意式松仁猪排', '1.将肉排整形加盐、胡椒、白兰地、洋葱等腌制。\r\n2.芥末调汁抹在猪排上，把松仁碎及核桃碎沾在肉排表面。\r\n3.放油把猪排放入并成琥珀色即可。加两种调料装饰盘边即可。', '外酥里嫩、清香适口、有一种耳目一新的感觉。', NULL, NULL, '排骨。配料：腌肉、松仁、核桃仁、白兰地、辣椒仔、蕃茄、香葱等。');
INSERT INTO `recipe` VALUES (1659, 86, '云呢拿忌廉布甸', '1.鱼胶粉用4倍水开匀。 \r\n2.将忌廉、鲜奶及糖用慢火加热2分钟后离火，加入呢拿香油拌匀。 \r\n3.加入鱼胶粉溶液，用慢火煮至完全溶解。熄火待凉后，加入甜酒。 \r\n4.将混合液倒入杯中，放入雪柜冻至凝固即成。', NULL, NULL, NULL, '忌廉1公升、鲜奶7汤匙、糖180克、呢拿香油数滴、鱼胶粉16克、杏仁甜酒4汤匙。');
INSERT INTO `recipe` VALUES (1660, 86, '芝士焗蟹盖', '1.将蟹蒸熟，拆肉加胡椒粉少许捞匀。\r\n2.烧红镬加油1汤匙，爆洋葱、烟肉及磨菇，落盐1/2茶匙兜匀盛起。\r\n3.用牛油炒面粉，徐徐加入上汤至稀糊状，再加入鲜奶及鸡粉搅匀成厚忌廉。\r\n4.把已爆好之材料及蟹肉加入忌廉料中搅匀。\r\n5.将以上材料填入蟹盖内，洒上芝士粉，再加些面饱糠，放入焗炉，以中火焗至金黄便可（约15分钟）。', NULL, NULL, NULL, '蟹盖8个、蟹肉5只、洋葱1/2个（切粒）、磨菇6个（切粒）、烟肉3片（切粒）、盐1/2茶匙、面饱糠适量、芝士粉适量、忌廉料、牛油2安士、面粉7汤匙、鲜奶5汤匙、鸡粉1/2茶匙、上汤1杯。');
INSERT INTO `recipe` VALUES (1661, 75, '爆三脆', '1.将洗净的生肚头放墩子上，从一边开口、伸展，把上面的皮和白盘去净，立刀解成十字花纹，改成1.7厘米大的块。鸡胗破开挖去里外皮，立刀解成十字花纹。海蜇切成佛手片。把蛋清、粉芡放碗里，用手叠上劲，将鸡胗、肚头放入叠匀。 \r\n2.玉兰片切成雪花片，香菇片成片，同豌豆放在一起，加入汤、味精、料酒、盐水、姜花、跟头蒜苗和少许粉芡调成汁。 \r\n3.锅放在旺火上，热锅凉油下入肚头、鸡胗，用勺蹚开。见肚头、鸡胗花纹爆开时出锅滗油。锅内留少许油，随将锅放火上，下入配料和料汁，汁沸下入肚头、鸡胗、海蜇，簸翻两次，出锅即成。', '色泽乳白，脆嫩鲜香，爽口不腻。', NULL, NULL, '鸡肋100克，肚头100克，水发海蜇150克，蛋清1个，水粉芡15克，水发玉兰片25克，水发香菇25克，水发豌豆15克，姜花、跟斗蒜苗各5克，盐水5克，料酒10克，味精1.5克，大油300克（约耗100克），清汤适量。');
INSERT INTO `recipe` VALUES (1662, 75, '炒鸡仁', '1.鸡脯肉去筋，切成0.7厘米见方的条，再切成1厘米长的菱形块，在清水中泡一下，捞出搌干水分，放入鸡蛋清和湿淀粉搅成的糊内拌匀。荸荠去皮煮透，切成小菱形块，备用。 \r\n2.炒锅置旺火上，添入熟猪油，五六成热时下入鸡块，蹚开，鸡块收缩发白时捞出沥油。锅内留油少许，随将青豆、荸荠块放入煸炒，再下入清汤、精盐、绍酒、味精。汁沸，放入鸡块，翻几个身后装盘即成。', '“炒鸡仁”是河南传统的火功菜。其成品青白相映，质嫩味鲜。', NULL, NULL, '鸡脯肉300克，青豆10克，荸荠100克，鸡蛋清1个，湿淀粉20克，精盐2克，绍酒10克，味精2克，清汤75克，熟猪油500克（约耗75克）。');
INSERT INTO `recipe` VALUES (1663, 75, '红袍莲子', '1.红枣洗净，截去两头，用捅枣棒将枣核捅出，再把红枣放入50度的温水中，浸泡30分钟，捞出后用刀在枣的中间横切一刀，但不要切断。\r\n2.将初步加工过的莲子装在碗中，加入适量清水和25克大油，上笼蒸熟，取出滗去水分。\r\n3.取净碗一个，用大油10克将碗的内壁抹匀。将莲子逐个塞入红枣内，整齐地竖直排列在碗内，与碗口排平，将白糖50克撒入红枣上，用一张麻纸将碗盖住，上笼蒸15分钟，取出揭掉麻纸，扣在圆盘的中心处，红樱桃、桔子瓣排放在红枣周围。\r\n4.将蒸红枣的汁滗入炒锅内，放中火上，加白糖100克、大油15克炒', '工艺细致，浓甜适口。', NULL, NULL, '大红枣200克，水发莲子100克，桔子瓣100克，红樱桃25克，白糖150克，大油50克。');
INSERT INTO `recipe` VALUES (1664, 75, '红烧豆腐棍', '1.将豆腐棍用温水泡软，切成3.3厘米长的段，用布搌干水分，放入鸡蛋、湿淀粉搅成的糊内抄匀。 \r\n2.炒锅置旺火上，放入熟猪油，烧至七成热，放入豆腐棍，炸至柿黄色捞出。 \r\n3.炒锅放旺火上，添入清汤，放入豆腐棍、玉兰片、虾仁、木耳，精盐、味精、酱油、姜汁、加盖烧制，至汁收浓，淋花椒油盛入汤碗内即成。', '陈留豆腐棍香筋利口，别具风味，可凉拌、烧、炸、烩、能制成甜、咸、荤、素多种菜肴。“红烧豆腐棍”经炸、烧烹制而成，色泽微黄，筋香鲜嫩。', NULL, NULL, '陈留豆腐棍10根（约50克），水发玉兰片50克，鲜虾仁25克，水发木耳25克，鸡蛋1个，湿淀粉25克，精盐3克，味精2.5克，酱油5克，姜汁2克，清汤1000克，花椒油25克，熟猪肉1000克（约耗50克）。');
INSERT INTO `recipe` VALUES (1665, 75, '琥珀冬瓜', '1.冬瓜去皮复里，制成佛手形、石榴形、桃形等果子形，放在锅垫上。 \r\n2.锅放火上，添水400克，放入白糖、冰糖、糖色、大油，沸起放入冬瓜。再沸起，移至小文火上靠汁，待冬瓜块缩小、汁浓、呈琥珀色，合入扒盘。撒上山楂糕片，把汁浇在上边即成。', '嫩甜筋香，色如琥珀。', NULL, NULL, '冬瓜1500克，山楂糕15克，白糖25克，冰糖100克，大油15克，糖色10克。');
INSERT INTO `recipe` VALUES (1666, 75, '煎扒狮子头', '1.把肉去骨去皮切剁成碎丁，放在盆里，对入鸡蛋（1个）、淀粉（25克）以及白糖、味精、绍酒和葱姜水，搅上劲。分成7份，团成圆球状的狮子头。鸡蛋（1个）、干淀粉（25克），搅成的稀糊。冬笋切片，备用。 \r\n2.炒锅放旺火上，添入熟猪油。把狮子头逐个挂上稀糊，摁扁放在锅里，两面煎黄后，放在锅垫上，把肉皮盖在上面。 \r\n3.将装好的锅垫，放在锅里，添入头汤，下入冬笋片、酱油、精盐，用旺火烧开后，改小火扒制。至狮子头浓烂起锅，揭掉肉皮，盛入盘内即成。', '通过煎、扒烹制，原汁原味，软香浓烂。老人食用，尤为适宜。', NULL, NULL, '带皮猪肋条肉1000克，净冬笋20克，味精5克，绍酒15克，精盐7.5克，鸡蛋2个，干淀粉50克，酱油25克，白糖10 克，葱姜水50克，头汤500克，熟猪油250克（约耗100克）。');
INSERT INTO `recipe` VALUES (1667, 75, '煎鸡饼', '1.鸡脯肉去筋膜，同猪肥膘肉一起剁砸成泥，放盆中加入鸡蛋清、湿淀粉、荸荠丁、糯米泥、绍酒、味精搅匀，制成馅料。 \r\n2.炒锅放中火上，添少许花生油，将馅料挤成18个核桃般大的丸子，在锅内摆成里七外十一的圆形。再添油煎制，边煎边用勺背轻轻将丸子摁扁，油热至五成将锅端离火口。顿火2次，待一面煎成柿黄色时，将余油滗出。鸡饼翻身，重添油煎制另一面。两面均成柿黄色时，把葱姜丝撒上，炸出香味，滗出余油，保持原形体装盘。上桌时外带花椒盐。', '成菜必须不离不散，保持原来的形状，具有酥、嫩、鲜、香之特色。', NULL, NULL, '净鸡脯肉150克，熟糯米泥50克，猪肥肉膘150克，荸荠丁50克，鸡蛋清1个，湿淀粉100克，花椒盐3克，葱丝2.5克，姜丝2，5克，精盐3克，绍酒10克，味精1克，花生油250克（约耗100克）。');
INSERT INTO `recipe` VALUES (1668, 75, '煎藕饼', '1.把藕洗净，截去藕节，去皮擦成茸，用净布挤去水分，放在盘里。猪肥肉膘剁成茸，用糯米面、藕茸，搅成软硬适度的糊，分成18份，捺成直径3.5厘米的圆饼，按里七外十一放在抹过油的盘里。 \r\n2.炒锅放中火上，擦净，下入花生油，烧至三成热时，将藕饼逐个放入锅内，一面煎成微黄时，翻身煎另一面，两面煎透后，盛在盘内。全部煎完后，外带白糖两碟上桌即可。', '成菜外形似鼓，色泽柿黄，外焦里浓。蘸以白糖，香甜可口。', NULL, NULL, '鲜藕1000克，糯米面100克，猪肥肉膘75克，豆沙馅100克，花生油150克，白糖100克。');
INSERT INTO `recipe` VALUES (1669, 75, '姜豉', '1.猪肘子肉洗净，沥干水分，撒上白糖，待溶化后再撒上精盐揉匀，放盆内腌3天，每天需揉一至两次。 \r\n2.把腌好的肘子放开水内浸透，捞在温水中刮洗干净，偷刀片成大片（皮不片开），放容器中。加入葱段、姜块、陈皮、绍酒，清汤，最上面放上用开水浸透的鸡腿，上笼蒸烂（越烂越好）。 \r\n3.将蒸烂的肘子拣去葱段、姜块、陈皮不用，撇净油沫，皮面向下捞在海碗内，汤汁过滤倒汤碗中，放冰箱凝冻。 \r\n4.香菜、韭黄洗净切成1.5厘米长的小段，分装碟内；冻好的姜豉取出切成长条装盘，浇上醋、豉汁、姜汁对成的汁。外带香菜、韭黄各一', '“姜豉”成菜后，晶莹透明，红白相间，食时浇香醋、姜汁，配以香菜、韭黄调拌，入口香嫩利口，为佐酒佳肴。', NULL, NULL, '带皮猪肘子肉1000克，韭黄10克，香菜10克，鸡腿4只，姜块35克，葱段20克，陈皮5克，精盐45克，白糖25克，绍酒10克，醋30克，豉汁10克，姜汁15克，清汤150克。');
INSERT INTO `recipe` VALUES (1670, 75, '洛阳燕菜', '1.将白萝卜洗净去皮，选用中段，切成2毫米粗、6厘米长的细丝。放入冷水中浸泡20分钟，捞出沥干水分，放入干淀粉中拌匀，摊在笼布上上笼蒸5分钟。取出晾凉，再放入冷水中抖开，捞出沥干水分，撒上精盐（3克）拌匀，然后再上笼蒸5分钟即成素燕菜。下笼后放在大品锅内。 \r\n2.将海参、鱿鱼、玉兰片、蹄筋和熟鸡肉都片成约5厘米长、2厘米宽的长方形薄片，分别放入沸水中焯一下。然后把火腿也片成长方形片。将海米与片好的配料，分别间隔相对地码在品锅内的素燕菜上。 \r\n3.将生鸡脯肉剁砸成泥，放入蛋清、湿淀粉、精盐（1克）、打上', '“洛阳燕菜”又名“假燕菜”、“牡丹燕菜”，是一道具有豫西地方特色的佳肴。历来被崇为洛阳地区民间筵席“洛阳水席”的首菜。', NULL, NULL, '大白萝卜1个（约1000克） ，水发海参250克，水发鱿鱼250克，熟鸡肉250克，熟火腿25克，水发蹄筋15克，水发玉兰片15克，生鸡脯肉100克，水发海米15克，红老蛋糕100克，绿老蛋糕100，鸡蛋清2个，湿淀粉15克，绿豆干淀粉250克，精盐7克，味精2克，绍酒2克，酱油1克，清汤1000克，熟猪油15克。');
INSERT INTO `recipe` VALUES (1671, 75, '琵琶鱼肚', '1.把鱼肚杀一下，用布挤干水分，片成片、再裁成12个琵琶形，放在盘内。 \r\n2.把鸡糊抹在琵琶托上，火腿切成细丝；香菇用开汤杀一下，挤干水分，切成细丝；鱿鱼切成长1.5厘米长的棒48根。把火腿丝、香菇丝隔一行摆一行摆成琵琶弦，鱿鱼棒安在琵琶头处，一面安两个弦轴，把口蘑片放在琵琶弦下面，上笼哈透，取出，活动一下。锅放火上，添入大油，烧热，倒入清汤，对入作料；汤沸，勾入流水芡，将汁收浓，浇到琵琶鱼肚上即成。', '鲜嫩适口，形似琵琶。', NULL, NULL, '水发钱肚250克，鸡糊400克，水粉芡10克，火腿25克，水发香菇25克，口蘑片12个，水发鱿鱼50克，大油50克，味精5克，料酒10克，盐水25克，姜汁5克，清汤200克。');
INSERT INTO `recipe` VALUES (1672, 75, '炝鱿鱼卷', '1.鱿鱼卷的发制方法同“熘鱿鱼卷”。将葱椒、盐水、料酒、酱油、味精、糖、汤对成汁。 \r\n2.锅放旺火上，油热七成，将鱿鱼下入，鱿鱼卷成卷，花纹裂开时，出锅滗油。锅放火上，将对好的汁下入锅内；汁沸，下入鱿鱼卷，淋入小磨油，簸锅两次，盛入盘内即可。', '色泽柿黄，鲜脆可口。', NULL, NULL, '鱿鱼400克，葱姜丝各10克，葱椒5克，盐水10克，酱油15克，料酒10克，味精1克，糖15克，花生油500克（约耗40克），小磨油10克，汤50克。');
INSERT INTO `recipe` VALUES (1673, 75, '清汤绣球皮丝', '1.皮丝切成3厘米长的段，火腿和冬笋切成3厘米长的丝。以1／3的皮丝与火腿丝、冬笋丝拌匀，待用。 \r\n2.将鸡脯肉砸成泥，虾仁淘净剁成泥，一起放盆中，放入鸡蛋清、湿淀粉（15克）、精盐（3克）、味精（0.5克）和葱姜水（150克），搅上劲，再放熟猪油拌成糊。 \r\n3.用糊和剩余2/3的皮丝拌在一起，挤成核桃形丸子；放在拌好的冬笋丝、火腿丝和皮丝上滚一滚，使其粘上三种丝成绣球形，上笼蒸5分钟。 \r\n4.炒锅内添清汤，下入剩余的精盐（2克）、味精（0.5克）和绍酒，再将蒸好的绣球皮丝下锅，旺火煮开即成。', '质地脆嫩，味道鲜美。', NULL, NULL, '发好皮丝250克，鲜虾仁100克，鸡脯肉100克，火腿25克，净冬笋25克  ，精盐5克，味精1克，绍酒10克，湿淀粉15克，鸡蛋清1个，葱姜水150克，清汤1000克，熟猪油25克，花生油500克（约耗50克）。');
INSERT INTO `recipe` VALUES (1674, 75, '清蒸白鳝', '1.用刀背猛砸鳝鱼头部一下，由颈下转圈划一刀，用手指抠住皮向下翻，将皮去掉，剪去鳍，再用筷子从颈下插入腹内，绞出内脏，将鱼冲洗干净。切成2.5厘米长的段，在脊背上解成斜十字花刀。葱一破两面三刀半，切成3厘米长的段，铺在盘上，鳝鱼整齐地在盘内摆成圆形。把火腿、冬笋、香菇都切成片，和姜花、板油丁均匀地摆在鱼上。 \r\n2.碗内添入清汤，放入盐水、料酒、味精兑成汁，浇在鱼上面，上笼蒸熟取出。走菜时外带姜米、醋。', '味鲜肉嫩。', NULL, NULL, '白鳝鱼1000克，火腿15克，水发香菇10克，板油丁20克，姜花10克，葱段100克，水发冬笋15克，姜米10克，味精2.5克，料酒15克，盐水10克，醋2.5克，清汤200克。');
INSERT INTO `recipe` VALUES (1675, 75, '清蒸丸子', '1.净肉去皮剁碎，荸荠切成丁。玉兰片以4/5切成2毫米见方的丁，1/5切成片。白菜心切成块，面筋泡一破两半，口蘑切片。 \r\n2.碎肉、荸荠丁、玉兰片丁放碗内，加清汤、绍酒、酱油、味精、精盐打上劲成馅。 \r\n3.白菜心、面筋泡用开水焯一下，与水粉丝一起铺在盘中。将馅挤成18个丸子，里七外十一放在盘中配料上，再撒上口蘑片，玉兰片，上笼蒸熟即成。', '成菜色呈柿黄，鲜嫩清香，老幼皆宜。', NULL, NULL, '净猪肉500克，水发玉兰片150克，去皮荸荠100克，水发口蘑10克，白菜心150克，面筋泡50克，水粉丝100克，绍酒5克，酱油30克，味精5克，精盐3克，清汤250克。');
INSERT INTO `recipe` VALUES (1676, 75, '三鲜封芹', '1.将鸡胗破开，去外皮洗净，立刀解成十字花纹；猪腰子去外皮破开，挖去腰臊洗净，以立刀解成十字花纹，再改成象牙块；虾仁洗净，沥干水分。将以上三种原料，用鸡蛋清、干淀粉拌匀。封芹摘叶、去根、掐梢，洗净，改刀成3厘米的段。用小碗一个，放入绍酒、姜末 、精盐、清汤，对成调味汁。 \r\n2.炒锅放旺火上烧热，添熟猪油，热锅凉油下入鸡胗、虾仁、猪腰子，用勺摊开，随下入芹菜，翻身，立即出锅滗油。锅内留油少许，下入对好的调味汁，汁沸后，放入原料，再沸，放湿淀粉勾流水芡，下味精，淋明油，簸翻两三下出锅即成。', '“三鲜封芹”是以封芹为主料，配以鸡胗、虾仁、猪腰子烹制而成。成菜色调青翠，脆嫩爽口，芹香浓郁，为佐酒佳肴。', NULL, NULL, '封芹150克，鸡胗50克，虾仁50克，猪腰子50克，鸡蛋清1个，干淀粉10克，湿淀粉5克，精盐3克，绍酒5克，味精2克，姜末5克，清汤25克，熟猪油250克，（实耗100克）。');
INSERT INTO `recipe` VALUES (1677, 75, '蹄莲豆腐', '1.先将豆腐用开水氽透，切成1厘米厚、2厘米见方的块24块，放碗内添入清汤，加入精盐（2克）、味精（2克），上笼蒸透取出。 \r\n2.海参、鱿鱼、鱼肚、海米、荸荠、香菇、冬笋和猪肥瘦肉分别切成米粒状，加精盐（3克）、味精（2.5克）、绍酒、芝麻油调成八宝馅，用鱼糊拌匀 。豆腐块搌干水分，拍上干淀粉，将2/3的八宝馅均匀抹在12块豆腐块上面，上面各错角平放一块豆腐块，再用馅抹平，上面点缀上香菜叶和樱桃，上笼蒸透，取出放盘中间。 \r\n3.剩余的1/3八宝馅加花椒粉、葱花、姜末和匀。鸡蛋磕碗里打匀，在锅中摊成蛋皮', '成菜构思巧妙，造型优美，软嫩适口，乃素料荤做之佳品，最宜老年人食用。', NULL, NULL, '豆腐150克，水发海参5克，水发鱿鱼5克，水发鱼肚5克，水发海米3克，熟荸荠3克，水发香菇3克，净冬笋3克，猪肥瘦肉20克，鱼糊100克，鸡蛋6个，香菜叶5克，红樱桃5个，菜心20克，咸面包渣100克，干淀粉100克，精盐6克，味精6 克，绍酒20克，葱花20克，姜末20克，花椒粉5克，清汤250克，芝麻油20克，花生油500克（约耗40克）。');
INSERT INTO `recipe` VALUES (1678, 75, '烧酿鸽子嘴', '1.菠菜根洗净，用小刀将菠菜根由大头到小头刻成鸽嘴状，当中破开，刀深达菠菜根长度的3/4，用开水焯一下，捞出晾凉待用。香菇、冬笋切片，备用。\r\n2.将鸡脯肉洗净，剁砸成细泥，加鸡蛋清、绍酒（3克）、精盐（2克），搅匀成鸡茸。把鸡茸酿在菠菜根中，置入盘中，上笼蒸10分钟左右，取出待用。 \r\n3.炒锅放旺火上，加鸡油，六成热时，下姜末、香菇、冬笋、青豆、精盐（5克）、绍酒（7克）、味精、清汤，再将蒸好的菠菜根下入锅内，汤开加湿淀粉勾芡，翻匀盛入盘中即成。', '清淡利爽。', NULL, NULL, '菠菜根250克，鸡脯肉100克，水发香菇50克，净鲜冬笋30克，青豆10克，鸡蛋清1个，精盐3克，味精1克，绍酒10克，湿淀粉5克，姜末3克，清汤150克，鸡油50克。');
INSERT INTO `recipe` VALUES (1679, 75, '西瓜盅', '1.选用30厘米长、黑皮、红瓤、体型好的西瓜，由上到下分成四段，两头雕刻成图案，中间雕刻成龙凤或其他图案。将蒂部一段切掉作盖，用勺将瓜瓤挖出。另一个西瓜的蒂部一段，刻成锯齿形，雕成花纹，做西瓜盅底座。 \r\n2.冰糖、白糖放在锅内，添水上火熬成糖水，放入冰箱内冻凉。各种配料切成1.5厘米见方的丁，与冻凉的糖水一齐放入西瓜盅内，盖上盖，放在西瓜盅座上，再放在大盘内，上桌食用。', '造型美观，凉甜利口。', NULL, NULL, '西瓜2个（每个约4000克），罐头猕猴桃150克，罐头菠萝100克，罐头桔子150克，甜瓜100克，荸荠100克。苹果100克，罐头樱桃150克，冰糖150克，白糖300克。');
INSERT INTO `recipe` VALUES (1680, 76, '扒素子双菇', '1.将冬菇炖熟（要选头小的，好的），将草菇放入开水中川熟，然后一起落油锅爆一下（去水分）即捞起。\r\n2.将虾子和葱姜用猪油熬热，将双菇一并倒入，加鸡汤和调味（盐、糖、酒、胡椒粉）烧透，最后用地栗粉勾薄芡，下葱、姜起锅，装盘。', '色黑中带白，味鲜甘，爽口，夏季最宜。', NULL, NULL, '冬菇175克，鲜草菇175克，虾子7.5克，葱姜少许，精盐7.5克，白糖少许，黄酒少许，胡椒粉少许，地栗粉少许，味精4克。');
INSERT INTO `recipe` VALUES (1681, 76, '白果鸭煲', '1.白果去壳，放滚水中煮五分钟，洗净滴干水。鸭洗净，下滚水中煮熟，取出滴干水，切块。\r\n2.下油二汤匙，爆透白果，然后下鸭爆片刻，加调味，白果焖熟，大约二十分钟，勾芡熄火。\r\n3.芽白洗净，切短段。煮熟放在煲仔内，把鸭放在黄芽白上煲滚，放上芫絮。原煲上台即可。', NULL, NULL, NULL, '光鸭一只，白果4两，黄芽白八两，芫絮二棵，胡萝卜、磨豉鼓、蚝油、糖、鸡粉、生粉、酒、盐、老抽、果皮各适量。');
INSERT INTO `recipe` VALUES (1682, 76, '白云猪手', '1.将猪腿对剖切成两片，剖成长条状，清水洗净，再用热水煮5分钟，洗净沥干待用；\r\n2.另用半锅水加葱、姜放入剁成小块的猪脚，中火煮30分钟后取出，马上用冰水冰凉，冷却后捞出，投入料酒、高汤、精盐、味精浸腌6小时即可。', '著名广州传统菜。以猪手为主料，沸水煮至软烂，捞出晾凉后加调料凉拌，即可食用。其特点是骨肉易离，皮爽肉滑，是佐酒佳肴。有美容、养颜的功效。', NULL, NULL, '猪脚500克，葱15克，姜20克，冰水400克，料酒40克，高汤200克，精盐10克，味精3克。');
INSERT INTO `recipe` VALUES (1683, 76, '白斩鸡', '1.葱、姜、精盐分盛二小蝶，拌匀。用中火烧热炒锅，下油烧至微沸，取出，分别淋在二小碟上，供佐膳用；\r\n2.将鸡洗净，放水中净煮，中间提出两次，倒出腔中的水，以保持内外温度一致。约浸十五分钟至熟，用铁钩勾起，再放在冷开水中浸没冷却，并洗去绒毛、黄衣，随即捞起。晾干表皮，浇上熟花生油，斩成小块，盛入碟中，摆成鸡形。吃时佐以姜茸、葱丝。', NULL, NULL, NULL, '嫩公鸡一只，姜茸5克，葱白丝5克，精盐0.5克，花生油6克。');
INSERT INTO `recipe` VALUES (1684, 76, '白灼响螺片', '将大螺壳敲开剔出肉，洗净后切掉螺肉的边缘，用横切的方法把螺肉片成灯盏形。\r\n1.起猪油锅把姜、葱倒入稍爆，再加清水烧透，取出葱姜，加白醋３滴，立即将螺片放入锅内，略烫三秒钟即取出，沥干水分（用洁毛巾将螺肉水吸干）。\r\n2.另用油锅，加少许猪油烧热后，将螺肉倒下略炒几下，即加黄酒少许翻炒几下，便离火装盘。', '色奶白，脆嫩爽口。附注：放白醋，主要是为了吸收螺肉的水分，使保持鲜嫩。', NULL, NULL, '大螺肉2000克，姜2片，葱2条，黄酒20克，白醋3滴。');
INSERT INTO `recipe` VALUES (1685, 76, '百鸟朝凤凰', '1.先将鸡脯肉50克煮熟，切成8块柳叶式，将200克火腿切成小片柳叶式，芥兰菜叶也切成8片柳叶式，这三种拼成凤凰的翅膀。\r\n2.将200克鸡脯肉、100克白膘、200克鲜虾斩成茸，加糖、味精、盐、胡椒粉、菱粉、鸡蛋白2只拌和在一起，分成两堆。将鸡皮切成鹅蛋状，上面放一堆鸡虾茸，再放蟹黄、香菜、撒些火腿末，上笼蒸十五分钟，取出切成24片，拼成凤凰的身体。\r\n3.将鸡蛋4只打开，蛋白、蛋黄分开，分别煎成蛋皮共两张，一黄一白，上面放些火腿末、芥兰菜梗、红萝卜片点缀一下，一起切成若干块三角形，再将鸡肝两副，加酱油', '味鲜，色香俱全，呈五彩。', NULL, NULL, '鸡脯肉350克，鸡蛋6只，瘦火腿275克，鸡皮8寸平方1件，鲜虾肉275克，蟹黄75克，公鸡头1只，鸡汤150克，鸡肝2副，鹌鹑蛋6个，凤尾虾12只，禾花雀2只，精盐少许，大红萝卜1只，胡椒粉少许，白花芥兰菜1根，味精4克，菱粉40克，麻油少许，香菜40克，白膘60克，白糖4克。');
INSERT INTO `recipe` VALUES (1686, 76, '爆羊肉', '1.羊肉洗净，放入滚水中煮五分钟盛起，横切大薄片。\r\n2.姜、干葱洗净切片，京葱切段。姜片及调味料，放入羊肉爆炒片刻，加酒，加入清水及胡椒粉拌匀，加盖以中火煮约八分钟，下芡汁料、麻油及京葱拌匀即可上碟。', '北京菜的烹调法据说有五十多种，最常用是爆，也分为油瀑、汤瀑、盐爆、水爆、酱爆、葱爆等。爆羊肉是采用葱爆的方法，特点是无汤汁、味嫩而香。', NULL, NULL, '半肥瘦羊肉八两，姜半两，干葱头一两，京葱四两，酒两汤匙，麻油、胡椒粉各1/4茶匙，清水1/3杯，南乳、磨鼓酱、糖各两茶匙，芡汁料：生粉一茶匙，清水两汤匙。');
INSERT INTO `recipe` VALUES (1687, 76, '菠菜鸡煲', '1.菠菜洗净，切短段放在煲仔内。干葱撕去红衣，洗净滴干水。冬菇净软去脚，抹干水。鸡洗净抹干水，斩块，加腌料腌十分钟，泡油；\r\n2.下油二汤匙，爆香干葱、姜，加入鸡、冬菇及蚝油再爆片刻，下料酒，下调味及甘笋，不停炒动，煮至鸡熟，铲起放在菠菜上，煲滚即可。', NULL, NULL, NULL, '鸡半只，冬菇仔半两，干葱六两，姜数片，菠菜五两，甘笋数片，蚝油一汤匙半，生抽一汤匙，糖半茶匙，生粉半汤匙，油一汤匙，盐适量。');
INSERT INTO `recipe` VALUES (1688, 65, '醋溜黄鱼', '1. 黄花鱼去鳞及内脏，洗净抹干，以配料涂匀鱼内外，腌三十分钟。葱洗净切粒。\r\n2. 烧滚油，鱼沾上粉，放入油中炸至金黄色盛起上碟。\r\n3. 烧热镬，下油一汤匙爆香姜茸，倒入芡汁料煮滚，放入葱粒兜匀，淋在鱼上即成。', '四川菜有酸、辣、甜、苦、咸、香、麻七种味道，醋溜黄鱼是四川菜的大台菜式，食味酸甜醒胃，黄花鱼有“蒜子肉”之称，因其肉质嫩滑鲜美，易于消化，更可作为病后食疗品。', NULL, NULL, '黄花鱼一条、重约1.25斤、葱一棵、姜茸半茶匙、生粉适量、盐3/4茶匙、酒两茶匙、胡椒粉少许、茄汁两汤匙、糖一汤匙、洋醋两茶匙、生粉半茶匙、盐1/3茶匙、清水四汤匙。');
INSERT INTO `recipe` VALUES (1689, 76, '叉烧炒蛋', '1.将鸡蛋打入碗内，用筷子搅打。\r\n2.将叉烧肉切成0.5厘米见方的小丁，放入鸡蛋碗内，加入胡椒粉、盐、味精调均匀。\r\n3.炒勺内倒入大油，上火烧至7－8成热，下入葱花，倒入调好的鸡蛋，用文火煎至两面呈金黄色，至熟即成。', '色泽金黄，味道鲜美。', NULL, NULL, '鸡蛋4个，叉烧肉100克，豆瓣葱30克，大油100克，胡椒粉少许，盐3克，味精3克。');
INSERT INTO `recipe` VALUES (1690, 76, '叉烧炒面', '1.将叉烧肉切成丝状，待用；\r\n2.将面条放入油锅中，当面条有五六成熟时，放入叉烧肉条，葱丝，香菜，盐，稍炒片刻即可。', NULL, NULL, NULL, '面条300克，叉烧肉100克，葱、香菜、酱油、盐各适量。');
INSERT INTO `recipe` VALUES (1691, 76, '炒红云雪影', '1.把榆耳片成薄片，放入开水内川一下，把蛋白、湿菱粉、味精和牛奶125克打匀，把蟹黄下油锅拖一拖，即取出，同以上两种原料混和，再倒入温的大生油锅内，用勺子慢慢推动，一等泡起，即用汤勺捞出，滤去油，同时将锅内的油倒出。\r\n2.另将牛奶125克、味精、盐、糖、胡椒粉加菱粉勾芡，跟着再将捞出的榆耳、蟹黄倒入芡内一翻即好。', '色红白分明，味鲜嫩滑润。', NULL, NULL, '鸡蛋白8只，牛奶250克，水发榆耳65克，蟹黄净65克，湿菱粉少许，味精少许，精盐少许，白糖少许，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1692, 76, '炒乌鱼片', '1.将乌鱼除骨，切成薄片，和精盐、鸡蛋白、胡椒粉、麻油少许、地栗粉搅拌均匀，放入温生油锅，用勺搅动，以免粘住，并可保持鱼的鲜嫩，拌到七成熟后捞出。\r\n2.另起锅将菜心放入锅内，加油少许、酒、精盐、麻油、胡椒粉、糖调和，再加少许鸡汤炒好，然后将乌鱼放入，一同翻几下，最后下地栗粉勾薄芡起锅。', '味鲜美而嫩，夏季适用。', NULL, NULL, '乌鱼肉300克，菜心150克，鸡蛋白1只，精盐少许，地栗粉少许，麻油少许，胡椒粉少许，黄酒少许，白糖少许，鸡汤少许。');
INSERT INTO `recipe` VALUES (1693, 76, '炊石榴鸡', '1.将鸡皮开成24片，每片呈直径5厘米大的圆形。将鸡蛋清15克、干淀粉、精盐调配成糊，涂在鸡皮内侧。 \r\n2.把芹菜梗用开水氽过，再浸凉水，捞出晾干，每根梗撕成4条丝当绳用。 \r\n3.将鸡胸肉和虾、冬笋、冬菇、猪肥肉分别剁成小粒，混合拌匀，再加上胡椒粉、味精、精盐10克、芝麻油、蛋黄、湿淀粉20克拌匀成馅，分成24份，待用。每份用鸡皮一片做皮，包成石榴形，以芹菜梗扎口，用蟹黄点缀其上，盛在盘中，入蒸笼蒸8分钟，取出，用鸡的原汁加湿淀粉调成白汁芡，淋上即成。', '“炊石榴鸡”是潮汕风味名菜，已有数十年的历史。它的外形酷似开花的石榴，主料是鸡肉，经蒸制而成故名。传统的作法是用鸡皮或鸡胸肉作皮的，创新者则改用煎鸡蛋的。这一改，做起来虽较方便，也有特点。但比传统潮汕风味大为逊色。', '火大气足，蒸8分钟即可，久蒸成形不佳，鲜味走失。', NULL, '鸡胸肉175克，虾仁100克，鸡皮300克，猪肥肉25克，熟冬笋100克，芹菜梗6克，火腿末25克，蟹黄10克，水发冬菇50克，鸡蛋清30克，精盐11克，湿淀粉25克，味精2.5克，干淀粉5克，胡椒粉0.5克，芝麻油2克。');
INSERT INTO `recipe` VALUES (1694, 76, '脆皮鸡', '1.将鸡去掉眼球，然后放入煮沸的白卤水中浸煮至熟，取出揩干；\r\n2.将麦芽糖、生粉、白醋放在碗里，调成浆状，均匀地涂在鸡的外皮，挂在风凉处晾干；\r\n3.将花生油放入锅中，旺火烧至七成热，然后用笊将鸡托着放入热油中，用勺舀油不断浇灌入鸡腔内，至变色，继续浇全身，直浇至大红色时为熟；\r\n4.将熟鸡取出，斩成块，拼砌在盘上成鸡形；用炸虾片围在鸡的四周；食时以酱油淮盐为佐料。', '皮色大红，肉质鲜美。', NULL, NULL, '鸡1只约80O克，麦芽糖4克，生粉50克，白醋100克，白卤水2500克，炸虾片25克，花生油200克。');
INSERT INTO `recipe` VALUES (1695, 76, '脆皮乳鸽', '1.将乳鸽除去内脏洗净。另将各种香料放入鸡汤内，上锅烧约一小时，即成白卤水，再将乳鸽放入白卤水内，即停火，浸至一小时后取出。\r\n2.用饴糖、白醋调成糊，涂在乳鸽皮上，挂在风凉处吹三小时，等乳鸽皮吹干，即入生油锅炸至金黄色，便切块装盘，盘边加椒盐即好。', '色金黄，皮脆嫩，秋季最宜。', NULL, NULL, '肥嫩乳鸽2只，桂皮少许，甘草少许，八角少许，黄酒325克，葱花165克，姜80克，白酱油80克，鸡汤2500克，精盐80克，饴糖少许，白醋少许，丁香4克。');
INSERT INTO `recipe` VALUES (1696, 76, '脆皮炸鸡', '1.将光鸡（鸡要嫩）除去内脏杂物，揩净血水，放在准备好的白卤水内浸透（两小时）。卤水是用盐、八角、桂皮、草果、陈皮、水煮成，将鸡取出揩净，用饴糖、白醋、地栗粉调匀，遍擦鸡身，经过风干，再涂上用地栗粉、酒、醋调的糊。\r\n2.另起锅，将猪油烧到六成熟时，一面把鸡头放入炸，一面用油从鸡的肛门浇入肚内，等头呈金黄色，肚也烧热，再一面把腿放入炸，一面用油浇全身，看皮呈现金黄色，取出解刀，先把腿、翅膀切下，再从腰部进刀切成两片，最后分别切成小块，仍按整鸡形状，分两排装在盘中，中间放鸡脯肉，两边放腿肉，前面放鸡头即好。', NULL, NULL, NULL, '光油鸡1只（约750克），黄酒4克，地栗粉5克，饴糖4克，白醋4克。');
INSERT INTO `recipe` VALUES (1697, 76, '脆皮炸双鸽', '1.将乳鸽放入沸水内滚约半分钟，取出洗净，去掉绒毛、黄衣、污物、沥去水。\r\n2.锅内下白卤水，烧至微沸，放入乳鸽，立刻端离火口，浸泡至九成熟取出；用铁钩勾挂上糖浆后，吊于通风处晾干。\r\n3.用中火烧热炒锅，下油烧至五成熟，放入虾片炸至酥脆浮起后捞出。将锅端离火口，放入乳鸽，端回炉上，用网勺托着，边炸边翻边，炸到皮脆、呈大红色时，取出网勺沥去油。\r\n4.将炒锅放回炉上，下蒜、葱、辣椒、糖醋、麻油，用湿淀粉5克调稀勾成糖醋芡，分盛二碟。\r\n5.每只乳鸽竖切成两半，每边切6块，共切24块，在碟上砌成双鸽原形，用', '鸽红虾白，皮脆肉香，色、形、味皆佳，佐酒最宜。', NULL, NULL, '乳鸽2只（约重700克），虾片15克，葱末1.5克，蒜泥1.5克，辣椒末2克，芝麻油0.5克，糖醋100克，糖浆（麦芽糖30克、绍酒15克、浙醋10克、湿淀粉10克调和）100克，白卤水（八角、丁香、甘草、桂皮、草果、干沙姜、花椒以及精盐制成），湿淀粉5克，花生油1500克（实耗75克）。');
INSERT INTO `recipe` VALUES (1698, 76, '脆皮纸包鸡', '1.先将加工好的光鸡放在汤锅中煮熟后取出来，剔肉去骨，切制成骨牌片。\r\n2.炒勺加油上火，下入核桃仁，炸熟剁成末，香菜、葱也同时剁成末，然后加入甜酱、胡椒粉、白糖、盐、味精等调料搅拌均匀，将鸡拌好入味。\r\n3.将云米纸改裁成方块，将入味好的鸡包好，随即将鸡蛋清、粉浆均匀地涂抹于鸡包上。\r\n4.炒勺内倒入油，在旺火上烧开，下入包好的鸡，炸至纸起金黄色即熟。', '颜色油黄，皮肉酥脆，味鲜香甜，别具一格。', NULL, NULL, '嫩母鸡1只（净重700－800克为宜），核桃仁40克，鸡蛋清80克，云米纸2大张。调料：植物油600克（实耗约60克），甜酱50克，胡椒粉少许，白糖25克，盐8克，味精6克，葱50克。');
INSERT INTO `recipe` VALUES (1699, 76, '脆炸蟹钳', '1.将蟹宰杀取螯，用沸水氽约10分钟至刚熟，取其前节，用刀轻拍螯壳，拆出肉，留螯壳末端壳及螯内扇骨。将虾胶同蟹肉拌匀，瓤在扇骨上（每只约瓤12.5克），恢复蟹螯的形状，盛在盘中，入蒸笼用旺火蒸约3分钟至熟，取出。\r\n2.用中火烧热炒锅，下花生油烧至七成熟，端离火口，手持螯壳末端，沾上脆浆，逐个垂直放入油里，然后把锅放回炉上，炸至浅金黄色、酥脆，取出，上盘排成圆形（蟹螯末端指向盘边）便成。', '本品为象形菜，是用蟹肉、螯肉和虾胶拌和作成蟹钳头，沾上有胀发性的浆粉，油炸而成。色泽金黄，外层栈脆，有密密麻麻的细眼，馅心软嫩，滋味鲜香。', '脆浆制法：将酵面75克、面粉375克、干淀粉65克、荸荠粉60克、精盐10克放在550克清水中，边放边搅拌，然后静置发酵约4小时。使用前加入花生油160克、食用碱水搅拌，静置20分钟后便成。', NULL, '肉蟹6只1800克，蟹肉50克，虾胶100克，脆浆100克，花生油1000克。');
INSERT INTO `recipe` VALUES (1700, 76, '大良炒鲜奶', '1.火腿切成约1.5公分见方的小粒。鸡肝切成长、宽各2厘米的片。\r\n2.将鸡肝放入沸水锅滚至刚熟，倒入漏勺沥去水。用中火烧热炒锅，下油250克，烧至四成热，放入虾、鸡肝过油至熟，倒入笊篱沥去油。\r\n3.用中火烧热炒锅，下牛奶，烧至微沸盛起，将已用牛奶调匀的干淀粉、鸡蛋清、鸡肝、虾仁、蟹肉、火腿一并倒入拌匀。\r\n4.用中火烧热炒锅、下油搪锅后倒回油盆，再下油25克，放入已拌料的牛奶，边炒边翻动，边加油2次（每次20克），炒成糊状，再放入榄仁，淋油一钱，炒匀上蝶。', '本品首创于顺德县大良镇，故名。大良附近多土阜山丘，岗草茂盛，所养的本地水牛，产奶虽少，但质量高，水分少，油脂大，特别香浓，把它制成牛乳饼、双皮奶等饮食品，一向为食客推崇。尤其是大良炒鲜奶以其特有的风味而饮誉中外。大良炒鲜奶系软炒法的典型菜例。牛奶乃液体，用于炒特别讲究技法，使之不泻不焦，成品似山形，呈乳白色，鲜明油亮，裹在奶皮中的各种馅料均可见，奶芡嫩滑，各料鲜爽软韧皆备。', '先用牛奶与干淀粉调匀，避免干淀粉粒状。炒奶时要顺一方向搅动，下油份量适中，否侧不能保持光亮润滑。宜用中火、火过，则易泻水，装盘不能堆成山形，影响美观及口感。', NULL, '鲜牛奶250克，鸡肝25克，蟹肉25克，腌虾仁25克，炸榄仁25克，熟瘦火腿15克，鸡蛋清250克，味精3.5克，精盐4克，干淀粉2克，熟猪油500克。');
INSERT INTO `recipe` VALUES (1701, 76, '蛋塔', '1.将鸡蛋磕入碗中，加黄酒、精盐少许搅匀；\r\n2.将鸡蛋与面粉搅拌匀，加入砂糖与黄酒，搅拌后擀成圆饼状，将边缘捏成内卷花形，放入烤箱烤10分钟左右。取出后，将打好的蛋黄液倒入即可。', NULL, NULL, NULL, '鸡蛋5只，面粉500克，精盐适量，砂糖100克，熟猪油10克，黄油10克，米酒适量。');
INSERT INTO `recipe` VALUES (1702, 76, '鼎湖上素', '1.榆耳、黄耳用冷水浸约八小时，使内外发透，刮去榆耳细毛，涮去附于黄耳上的泥沙，分别漂洗干净，然后切成片，放入沸水锅中焯约一分钟捞起，和雪耳、桂花耳一道分别用清水浸泡待用。香菇、蘑菇用冷水浸约二十分钟，去蒂洗净，用油25克拌匀，加清水100克、精盐1.5克、味精1.5克，上笼蒸约十分钟取出。鲜草菇蕾削去蒂部泥沙和污物，用小刀在蒂部端垂直拉十字纹，用水反复洗净，然后入开水锅中焯约半分钟捞出，入凉水中冷却。鲜莲去壳，入锅加开水500克，加碱水2.5克。用中火煮约一二分钟捞出，擦去外衣、洗净。再入锅煮约一分钟，', '用料精细、色调雅丽，层次分明，食时鲜嫩滑爽，清香溢口。', NULL, NULL, '浸发雪耳（白木耳）80克，桂花耳15克，干榆耳25克，干黄耳10克，干香菇50克，干蘑菇50克，鲜草菇蕾150克，浸发竹荪125克，鲜莲子100克，罐头白菌100克，银针100克，熟笋花100克，菜远100克，精盐15克，味精20克，白糖5克，芝麻油1.5克，深酱油5克，淡酱油10克，绍酒40克，花生油200克，湿淀粉20克，素上汤1250克。');
INSERT INTO `recipe` VALUES (1703, 76, '东江瓤豆腐', '1.将豆腐切成长5公分、宽4公分、高2.5公分的小块，共三十块，把猪肉、鱼肉分别剁成黄豆粒大小，虾米切成细粒。\r\n2.把猪肉、鱼肉放在盆内，下精盐10克、味精0.5克，拌至有胶，再下虾米、清水、干淀粉、葱10克、左口鱼末5克，拌约2分钟成肉馅。\r\n3.在每块豆腐中间挖一个长2.5公分、宽1.5公分、宽1.5公分的小洞，然后每块豆腐瓤入肉馅20克。\r\n4.炒锅用中火烧热，下油25克，把瓤豆腐逐块放入，边煎边加油二次（每次约25克），煎至两面金黄色，取出入砂锅，加入二汤、精盐、味精，加盖，用中火焖2分钟至熟，', '中原风俗，过年尚食饺子，客藉汉人初到东江时，仍保留这一风俗习惯，但当时岭南不产小麦，而且“广人以面热不以为饭，……水面外，若干饼，蓑衣油饼，水晶包，卷蒸之属，广人亦多不食”（见清·屈大均《广东新语》），过年吃饺子可望而不可及。后来，人们就把饼子的馅料填进豆腐以代，这样食法虽不如吃饺子那样有情趣，但总可以得到一点慰藉。故很快就传遍客家地区，以后又经历代名师高厨改进而成现在的瓤豆腐，此菜原锅上桌，香浓软滑，有左口鱼的特色鲜味，为东江地区冬令常菜。流传至今，兴梅地区客家人的团年饭上，仍必备此菜。', '左口鱼末制法：左口鱼干为广东特产，将左口鱼干剥去头、皮骨，取净肉，用中火烧热炒锅，下花生油至五成热下入鱼干炸至金黄色，松化。晾凉后，碾成末。搅馅时用力要均匀，顺一个方向搅动。煎时要随时转动锅，避免糊底。', NULL, '去皮猪肉25克，浸皮海米50克，左口鱼末10克，豆腐600克，盐12.5克，胡椒粉0.5克，干淀粉20克，淡二汤750克，花生油500克，葱15克，味精7.5克，深色酱油5克，湿淀粉10克，清水50克。');
INSERT INTO `recipe` VALUES (1704, 76, '东江盐局鸡', '1.用小火烧热炒锅，下精盐4克，烧热后，放入沙姜末拌匀取出，分成三小碟，每碟加入猪油15克供佐食用。将猪油75克、精盐5克和麻油、味精调成味汁。把纱纸一张刷上花生油待用。\r\n2.将活鸡宰杀，去毛去内脏洗净，吊起晾干水分后，去掉趾尖和嘴上的硬壳，在翼膊两边各划一刀，在颈骨上剁一刀，然后精盐3.5克擦匀鸡腔内，加姜、葱、八角末，先用未刷油的纱纸将鸡裹好，再包上已刷油的纱纸。\r\n3.用旺火烧热炒锅，下粗盐炒至高温（盐呈红色）时，取出四分之一盐放入砂锅内，把鸡放在盐中，然后将余下的四分之三的盐覆盖在鸡上面加上锅盖', '此菜制法独特，味香浓郁，皮爽滑，以沙姜油盐佐食，风味极佳。', NULL, NULL, '1500克左右的肥嫩项鸡1只（毛黄、嘴黄、脚黄、下过蛋的母鸡俗称“项鸡”），姜片、葱条各10克，香菜25克，精盐10克，味精7.5克，八角末2.5克，芝麻油1克，沙姜末2.5克，熟猪油120克，花生油15克，纱纸2张。');
INSERT INTO `recipe` VALUES (1705, 76, '冬瓜干贝炖田鸡', '1.将冬瓜去皮切成棋子形，田鸡去皮切成块，用温水洗净，取出将水沥干。干贝煮滚，除去异味，将水倒掉。\r\n2.另备一盅开水，将冬瓜、田鸡、干贝、姜、盐一起下入，上笼约蒸两小时即好。', '色淡青带淡黄，为夏令佳菜，清口去暑。', '蒸时盅口要先用荷叶封口，再用生皮纸封口，才能保持原汁。此菜应清淡爽口，不宜用鸡汤，否则浓厚不适口。', NULL, '冬瓜500克，干贝150克，田鸡500克，精盐15克，姜少许。');
INSERT INTO `recipe` VALUES (1706, 76, '凤梨烩排骨', '将猪排骨用酱油腌一下沥干，再用热油略炸使其表面变色；油加热后爆香葱、姜，放入酱油、糖、料酒、番茄酱、味精和水拌炒一下，然后倒入小排骨用小火煮，煮到肉酥时，将凤梨块倒入，炒烂即可。', '成菜色泽艳丽，甜咸带酸，果香浓郁，风味独特。', NULL, NULL, '猪排骨500克，凤梨150克，葱、姜各25克，酱油30克，糖、料酒各20克，番茄酱40克，味精1克、油50克。');
INSERT INTO `recipe` VALUES (1707, 76, '咕噜肉', '1.将猪肉切成6分的四方厚块，用刀背轻轻捶松，用少许盐、白酱油、地栗粉拌一拌腌一下，再顺序放入湿地栗粉、干地栗粉内滚过（抹粉时要松，不要捏紧）。另将笋切滚刀角形，青椒、红辣椒切成片。\r\n2.将抹过粉的猪肉放入旺火油锅，炸到八成熟取出，隔一二分钟，再投入炸透倒出。即加入笋、青椒、红辣椒、糖醋，用地栗勾好芡，再将炸好的猪肉倒入翻几番，另外浇上一些生油起锅装盘。', '味酸甜香酥，红色，宜作四季小吃。', NULL, NULL, '夹心猪肉200克，笋少许，青椒少许，红辣椒少许，糖醋150克，地栗粉少许，白酱油少许，精盐少许。');
INSERT INTO `recipe` VALUES (1708, 76, '古老肉', '1.将猪肉片成0.7厘米厚的片，在上面斜着刀轻轻刻上横竖花纹，然后切成2.5厘米宽的条，再斜切成菱形块，每块约重0.5克。笋和辣椒也都切成同样大小的菱形块。\r\n2.肉块用精盐、汾酒拌匀，约腌15分钟，加入鸡蛋液和湿淀粉30克搅匀，再粘上干淀粉。\r\n3.用中火烧热炒锅，下油烧至五成熟，把肉块逐件放入，约炸3分钟端离火口，炸浸约2分钟捞起。把锅放回炉上，烧至五成熟，将已炸过的肉块和笋块一起下锅，再炸约2分钟呈金黄色至熟，出锅沥去油。炒锅放回炉上，投入蒜、辣椒，爆至有香味，加葱、糖醋，烧至微沸，用湿淀粉10克调', NULL, NULL, NULL, '去皮半肥瘦猪肉300克，熟鲜笋肉150克，去壳鸡蛋30克，辣椒25克，葱段5克，蒜泥0.5克，精盐1.5克，芝麻油0.5克，糖醋卤（用糖、白醋、精盐、茄汁制成）250克，汾酒7.5克，湿淀粉40克，干淀粉75克，花生油750克（实耗50克）。');
INSERT INTO `recipe` VALUES (1709, 76, '灌汤龙凤球', '1.把虾肉剁成茸，加胡椒粉、味精、盐拌和，分成12垛。把鸡脯肉也剁成茸，加味精、盐、葱末、糖、胡椒粉、菱粉（12.5克）和水50克拌和，分成12垛。把猪皮酱加白兰地酒，放在冰箱里冻硬，取出，切成12块。\r\n2.包成12只圆球，包时先用虾茸放在手心揿开作外皮，鸡茸放在第二层，冻猪上块放在当中作馅心，包好后，一个个滚满芝麻末和面包末，放入温热大生油锅内炸，一见圆球浮上油面，即捞出装盘。', '色黄红，味甘香鲜脆。', NULL, NULL, '鸡脯肉175克，虾肉175克，猪皮酱40克，芝麻末80克，面包末40克，菱粉13.5克，白兰地酒13.5克，葱末少许，味精少许，精盐少许，白糖少许，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1710, 76, '广东烧鸡', '1.鸡洗净，抹干水，斩块，加老抽一茶匙捞匀。泡油；\r\n2.笋虾用清水洗数次，抹干水，放入滚水中煮十分后捞起，再用清水洗一洗，擦干水，切成适当的长短或大小；\r\n3.下油四汤匙，爆葱、蒜、蕃茄酱，下笋虾炒片刻，加入调味煮滚，慢火焖二十分钟，加入鸡兜匀，再焖十五分钟，试味勾芡，铲起放入煲内煮滚，放上葱，原煲上台。', NULL, NULL, NULL, '鸡半只，笋虾3两，蒜茸2茶匙，姜数片，葱三条切段，蕃茄1个，水一杯，酒、生抽、鸡粉、糖、八角、生粉、盐各适量。');
INSERT INTO `recipe` VALUES (1711, 76, '广东虾饺', '1.将澄粉、淀粉加盐拌匀，用开水冲搅，加盖焖5分钟，取出搓擦匀透，再加猪油揉匀成团，待用； \r\n2.生虾肉洗净吸干水分，用刀背剁成细茸，放入盆内；熟虾肉切小粒；猪肥肉用开水稍烫，冷水浸透切成小粒；干笋丝发好，用水漂清，加些猪油、胡椒粉拌匀； \r\n3.在虾茸中加点盐，用力搅拌，放入熟虾肉粒、肥肉粒、葱丝、味精、白糖、麻油等拌匀，放入冰箱内冷冻； \r\n4.将澄面团摘胚、制皮，包入虾陷，捏成水饺形，上旺火笼内蒸熟或下油锅煎熟即可。', NULL, NULL, NULL, '澄粉450克，淀粉50克，虾肉125克，干笋丝125克，猪油90克，盐、味精、白糖、麻油、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (1712, 76, '广式烧填鸭', '1.将鸭放砧板上，从翅膀下开膛掏出内脏，洗干净后，再用开水烫一下使鸭皮收紧，然后把稀糖均匀地涂抹在鸭身上，吊起来晾干。\r\n2.将白糖、料酒、花椒粉、盐、味精、酱油、葱、姜、蒜末等调料放在碗中，混合均匀后，注入鸭腹中。\r\n3.将鸭上炉烤烧，至鸭皮坚脆，色泽均匀即熟。\r\n4.将烧烤好的鸭切成块，摆成原鸭形状，浇上鸭腹中的卤汁即成。', '用长鱼盘盛装，造型美观大方，色泽金黄悦目，食之皮脆肉香。', NULL, NULL, '光鸭1只（重约2.5公斤左右为宜），白糖15克，料酒50克，花椒粉10克，味精5克，稀糖10克，酱油100克，葱、姜、蒜适量。');
INSERT INTO `recipe` VALUES (1713, 76, '蚝油生菜', '1.把生菜老叶去掉，清洗干净。坐锅放水，加盐、糖、油，开后放生菜，翻个倒出，压干水分倒盘里；\r\n2.坐勺放油，加蒜炒一炒，加蚝油、料酒、胡椒面、糖、味精、酱油、汤，开后勾芡，淋香油，浇在生菜上即可。', NULL, NULL, NULL, '生菜600克，蚝油30克，清油60克，酱油10克，白糖10克，料酒20克，胡椒面1克，盐1克，水淀粉10克，味精3克，香油5克，汤适量，蒜末3克。');
INSERT INTO `recipe` VALUES (1714, 76, '蚝油鸭掌', '1.将鸭掌剪去甲趾，先放入滚水内滚一滚，取出，剥去外皮，再洗净。\r\n2.将陈皮切成小块，八角切成小粒，与蚝油、鸭掌放入锅内，加适量鸡汤，用文火焖一小时左右，再放味精。临起锅前，除去陈皮、八角不要，将鸭掌取出，装在菜盘内。再用锅内卤汁调少许的栗粉勾芡，淋在鸭掌上面，另外浇上一点生油即好。', '味脆而鲜，作小吃最好。', NULL, NULL, '鸭掌500克，陈皮少许，八角少许，味精少许，生油少许，蚝油12.5克，地栗粉少许，鸡汤适量。');
INSERT INTO `recipe` VALUES (1715, 76, '红烧鸡包翅', '1.先将翅用滚水滚约半小时，浸入冷水，将上面的沙洗净，再用竹笪（粗竹席）将翅排好，放在正中煲（即用温火煮）三小时，取出翅，用清水煲三小时，再取出翅，放入清水约浸十余分钟，再煮六小时去骨，又浸十个钟头，用清水煲三个钟头，再换清水浸两小时，臭味才能去尽。\r\n2.再用葱、姜、黄酒、鸡汤煮三十分钟，然后将翅放入瓷锅，其他都不要，加鸡汤、光鸡、瘦肉、火腿、味精和酱油、地栗粉适量，上笼蒸至四小时取出（瘦肉、火腿以及翅汤均不要），便将鸡骨去掉，将肉放在鱼翅底，再用鸡汤烧滚，下地栗粉及味精勾玻璃芡淋上，然后撒些火腿即好。', '味香浓软滑，椒酱色，补血养身，四季皆宜。', NULL, NULL, '包翅500克，光鸡1只1000克，瘦肉325克，鸡汤1400克，火腿17.5克，味精75克，酱油少许，黄油13克，地栗粉少许，葱、姜少许。');
INSERT INTO `recipe` VALUES (1716, 76, '猴脑汤', '1.先将活猴击昏，割断喉管放血，经开水烫泡去毛后，割下猴头，撬开头盖骨取出猴脑，放入碗内，先加水上笼蒸熟。猴头与鹧鸪、猫头鹰等肉一起炖，炖到骨肉分离时为止，这为猴头汤。\r\n2.取出的猴脑置于碗中，将瘦肉、火腿、鸡肉、姜片、味精、糖、盐等加鲜汤，上火煮熬，将盛猴脑之小碗，放在汤上蒸，待熟后再把猴脑放入猴头里置于汤中，再与猴头汤合在一起炖后即成。上席时用瓷锅盛之。', '汤呈金黄色，味鲜、甜、爽、滑，营养丰富，风味宜人。', NULL, NULL, '活猴1只（约2500克），瘦猪肉150克，火腿100克，鸡肉100克，姜2块，味精、糖、盐、绍酒适量，鲜汤2500克。');
INSERT INTO `recipe` VALUES (1717, 76, '护国菜', '1.将番薯叶去掉筋络洗净，用2500克开水加小苏打粉（或碱水少量），下番薯叶烫二分钟捞起，清水过四次，然后榨干水分，除去苦水，用横刀切几下使用。\r\n2.草菇洗净后入鸡油、火腿、鸡汤、精盐2.5克，蒸二十分钟取出。去掉火腿，草菇和原汁汤备用。\r\n3.炒锅烧热下猪油75克，将番薯叶略炒，投入草菇及原汁，加鸡汤900克、精盐2.5克，烧开后，用湿生粉勾芡，加熟猪油75克、麻油10克，八成倒入汤碗内，二成留锅内，再加鸡汤200克和火腿，淋在菜汤上面即可。', '色泽碧绿如翡翠、清香味美、软滑可口，是潮州汤菜之上品。', NULL, NULL, '新鲜番薯叶（苋菜、菠菜、通菜、君达菜叶皆可）500克，湿草菇片150克，火腿片25克，猪油150克，鸡油50克，精盐、苏打粉、味精适量，鸡汤700克，生粉30克，麻油少许。');
INSERT INTO `recipe` VALUES (1718, 76, '花雕鸡', '1.将鸡宰净，放入沸水锅中氽约2分钟，取出去净绒毛污物，再加重滚，捞出，晾至皮干。先涂蚝油，再涂蜂蜜于皮上。猪肥肉切成薄片，将剩下的蚝油、蜂蜜、味精及二汤放在碗中，调成料汁。\r\n2.炒锅用旺火烧热，贴猪肥肉片于锅底，煎至有油脂分泌，即将鸡蛋侧放在肉面上。煎至两面腿、胁微黄，加姜、葱拌匀，续加入花雕酒，略煎，下料汁，加盖用旺火烧开，即端离火口；待温度稍降再回炉上。如此反复8次，其中把鸡身转3次，即每焖一面，需端离火口两次；如此反复焖，约需12分钟，最后1次端离火口，需待3分钟后，才揭盖，去掉姜、葱、油渣，将', '“花雕鸡”广州八大鸡之一，为广州市北园酒家的看店名菜。它以砂锅为炊具，利用煎猪肥肉分泌出来的油脂传热，再加少量料汁、花雕酒助烹，将一只整鸡焖熟。制法独特，色如琥珀，鲜美可口，闻名遐尔。', '花雕鸡反复焖烤，经过8次之多，约需12分钟，入味甚佳，而肉质脆嫩，方为正宗。', NULL, '母鸡1只1250克，猪肥肉75克，姜块35克，葱条35克，味精5克，蜂蜜30克，蚝油50克，花雕酒100克，淡二汤125克。');
INSERT INTO `recipe` VALUES (1719, 76, '淮杞炖羊肉', '1.将羊肉放入水内煮熟切块，再放入锅内加姜片炒透。\r\n2.将羊肉放入瓦盅，加淮山药、枸杞子、鸡汤、味精及精盐等，上笼蒸两小时即好。为冬令补品，鲜美入味，淡酱色。', NULL, NULL, NULL, '羊肉500克，姜片16.5克，枸杞子20克，鸡汤650克，味精13克，精盐少许，淮山药40克。');
INSERT INTO `recipe` VALUES (1720, 76, '火局禾花雀', '将禾花雀除去内脏杂物，用酱油、胡椒粉腌三十分钟后取出。用另锅放少许生油，将黄酒、辣酱油、番茄汁、白糖、麻油放入搅拌，再放禾花雀翻拌几下即好。色淡红，味甘鲜肥美，滋补，秋季适用。', NULL, NULL, NULL, '禾花雀12只，辣酱油40克，番茄汁20克，白糖20克，黄酒20克，麻油3.5克，白酱油20克、胡椒粉少许。');
INSERT INTO `recipe` VALUES (1721, 76, '火焰醉虾', '1.将鲜活虾剪去须与脚，用清水洗净、沥干，中药材也用清水洗净。\r\n2.取用耐高温玻璃（或电饭煲）一只，将河虾放入锅内，加米酒500克，加盖，将虾醉约5分钟左右，放入中药材，另取一只碗倒入250克米酒（或玫瑰酒），用火柴点燃后，揭开盖倒入锅内，便火焰四起，约10－15分钟后，待虾体变红，肉质饱满成熟，就可捞起，由食者边剥边蘸着调料吃。', '色泽深红，肉质鲜嫩，别具风味。', NULL, NULL, '鲜活基围虾（或者鲜活青壳河虾）500克，广东米酒750克，杞子20克，川归5克，熟酱油30克。');
INSERT INTO `recipe` VALUES (1722, 76, '椒盐虾', '1.中虾用水洗净，先剪虾须、虾枪，剔出枪下的杂物，剪清虾爪及桡足，晾干。辣椒切成米粒状。\r\n2.炒锅用旺火烧热，放入花生油，烧至五成热，下虾泡油至八成熟，连油一起笊篱沥去油。\r\n3.炒锅回火上，放入已泡油的中虾略煎片刻，加淮盐和辅椒粒，炒至熟，装盘，堆成山形，四周伴以香菜即成。', '此品选鲜活中虾，不必去壳。经油泡后，再用椒、盐炒；椒、盐等味料附在壳外而不入肉内，获得外焦香感辣，肉软嫩鲜美的特色。', '淮盐：炒锅用中火烧热，放入精盐500克，炒至烫手而有响声时，端离火口，倒入五香粉20克，拌匀即成。', NULL, '鲜活中虾500克，辣椒25克，净香菜25克，淮盐15克，花生油1000克。');
INSERT INTO `recipe` VALUES (1723, 76, '金华玉树鸡', '三黄鸡用油汤浸熟，去骨起肉切块，把火腿切成片，一片夹一片码放在盘中。芥兰焯水，过油后在鸡块旁围边。锅中放上汤，加入盐、味精、蚝油、点老抽少许，最后用水淀粉勾芡，淋在鸡上即可。', NULL, NULL, NULL, '三黄鸡一只，金华火腿70克，芥兰300克，北菇，蚝油，味精，精盐，绍酒，淀粉，老抽上汤等。');
INSERT INTO `recipe` VALUES (1724, 76, '金钱明虾', '1.将明虾去壳、火腿修齐，都切成金钱圆形，白膘也切成金钱圆形。用玫瑰酒、糖、精盐、味精、蚝油调匀，将白膘放入腌十分钟后取出。\r\n2.将虾放入胡椒粉、麻油、地栗粉、味精、蛋白调的卤内拖一拖，然后用白膘、明虾、火腿各１块叠成３层，白膘作底，火腿作面，明虾夹在当中，放入旺火生油锅，再转文火煎五分钟即好。', '色金黄，味甘，香脆，宜下油，四季皆宜。', NULL, NULL, '明虾4只，瘦火腿125克，玫瑰酒20克，麻油少许，精盐4克，鸡蛋白1只，蚝油4克，白膘175克，胡椒粉少许，白糖4克，地栗粉少许，味精4克。');
INSERT INTO `recipe` VALUES (1725, 76, '金钱牛排', '1.将牛里脊修齐，切成金钱形状块，每块约200克，用刀背拍松，加酒、小苏打粉、盐、蛋黄、地栗粉和水50克拌和，腌约一小时。\r\n2.将洋山芋切成条子（较一指略大），用盐水煮约三分钟，再入生油锅炸成金黄色，起锅装在盘的一边。\r\n3.再将牛里脊入旺油锅，煎成两面黄（约八成熟），沥干油，加入番茄汁、辣酱油、糖，慢慢烧至汤汁收干起锅，镶在盘的另一边即好。', '色金黄带红，味脆嫩香甜，作四季皆宜。', NULL, NULL, '牛里脊（净）500克，洋山芋150克，辣酱油25克，番茄汁15克，白糖少许，小苏打粉3克，地栗粉3.5克，黄油少许，鸡蛋黄1只，精盐少许。');
INSERT INTO `recipe` VALUES (1726, 76, '京都排骨', '1.排骨切成块，加盐、味精、胡椒粉、三奈粉、咖哩粉、面粉、太白粉、吉士粉腌至30分钟左右；\r\n2.油加热，将排骨炸至金黄色捞出；\r\n3.番茄酱、辣椒油倒入锅中烧热，用太白粉、水勾芡，煮开后，将排骨放入锅中炒数下即可盛盘。', NULL, NULL, NULL, '猪腩排280克，盐、味精各半匙，胡椒粉、三奈粉各1匙，咖哩粉、面粉各半匙，太白粉半匙，吉士粉半匙，鹅蛋半个，酒1小匙，番茄酱1小匙，辣椒油半匙，油4杯。');
INSERT INTO `recipe` VALUES (1727, 76, '局酿禾花雀', '1.禾花雀去头、脚、翅膀，剖开尾部，取去内脏，洗净，沥干水分，放入碗内，加酱油、胡椒粉拌匀后腌三十分钟取出。\r\n2.锅内放生油烧热，将禾花雀放入，起大火炸至九成熟后全部倒出。随即趁热锅，加适量汤和黄酒、辣酱油、番茄汁、白糖、麻油，再将禾花雀放入翻拌几下，收干汤汁，起锅装盘即好。', '色泽淡红，甘香味浓，仍保持禾花雀体形。', NULL, NULL, '禾花雀12只，辣酱油30克，番茄汁15克，白糖15克，黄酒15克，白酱油15克，胡椒粉少许，麻油25克，生油500克（实耗50克）。');
INSERT INTO `recipe` VALUES (1728, 76, '咖喱牛肉', '1.牛肉切片，加入鸡蛋、盐、味精、胡椒粉、太白粉、苏打粉拌匀腌至1小时左右，加油拌匀，将洋葱、红辣椒、青椒、蒜切碎；\r\n2.锅中油加热，放牛肉炒8分熟捞出；\r\n3.锅中油加热放洋葱、红辣椒、青椒、蒜用大火拌匀，加高汤，再加牛肉、盐、味精、糖、咖喱粉一起炒，加太白粉、清水勾芡，淋下油、奶水，炒匀即可盛盘，也可用洋香菜、红萝卜片加以装饰。', NULL, NULL, NULL, '牛后腿肉280克，洋葱半个，红辣椒1支，青椒半个，蒜1瓣，鸡蛋半个，盐半匙，味精半匙，胡椒粉少许，太白粉1小匙，苏打粉半匙，高汤1大匙，糖半匙，咖喱粉1匙，油1匙，奶水1小匙。');
INSERT INTO `recipe` VALUES (1729, 76, '口蘑焖豆腐', '1.将豆腐先切成大方块，再一块块对开，最后片成一分厚的三角片，口蘑用开水泡过，将汤泌出放好；口蘑取出洗净，片成厚片。\r\n2.用猪油炝锅，先下葱丝和姜丝一煸，再下黄酒一烹，跟着将糖、酱油、味精、清汤、口蘑原汤（适量）、口蘑片、豆腐块放进，继续用温火焖熟，见豆腐由硬见软，汤将收干即好，深黄色，冷盘热炒适用。', NULL, '这里“烹”的含义与一般烹调方法中的“烹”不同。除上述例子外，凡在材料将近熟时，浇上一些酒，即统称为“烹”。如光用酒烹，对蔬菜可起松脆作用，对肉类、鱼鲜既可去腥味又增酒香；如用酒、盐、酱油、糖等调味同烹，可使菜五味调和。', NULL, '豆腐650克，口蘑75克，京葱丝40克，姜丝2大片，黄酒少许，白糖少许，酱油少许，味精少许，清汤200克。');
INSERT INTO `recipe` VALUES (1730, 76, '扣环球上汤', '1.把鸡肉剁成茸，加湿菱粉和盐，做成10只圆球，把鱼肉、猪肉也分别剁成茸，加盐、胡椒粉、麻油、干菱粉，各做成圆球10只。把鸡肫、海参、鱿鱼都各开成10块，把小冬菇洗净，剪去老蒂。\r\n2.这些原料一起下开水锅，下酒、猪油川熟，捞出装在小碗中，再加味精、盐、胡椒粉拌好，上笼蒸熟取出，倒入大汤碗内，加鸡汤（1250克）烧开，就原汤锅上桌。', '此是汤菜，花色多，色调美观，味鲜嫩。', NULL, NULL, '海蜇皮、蟹柳、西芹、五香豆腐干、醋、生抽、麻油。');
INSERT INTO `recipe` VALUES (1731, 76, '兰度鸽脯', '兰度芥兰切段，两次剞十字花刀放在水中泡卷待用。乳鸽起肉去肥油，片成鸽脯，用盐、味精翻炒，码在盘中。鸽脯在4成热的油中滑熟。锅中留底油，放入蒜茸、姜花、甘笋花，在放入鸽脯烹料酒加芡汤、味粉、蚝油、香油、胡椒粉、水淀粉，最后点明油出锅，倒在芥兰段上即成。', NULL, NULL, NULL, '乳鸽1只，芥兰段200克，蒜茸、姜花、甘笋花适量。');
INSERT INTO `recipe` VALUES (1732, 76, '荔枝虾球', '把鲜荔枝去皮和核，红辣椒切成小块待用；大虾肉在背上切一刀，深三分之二，然后加盐、味精、淀粉腌20分钟；油锅烧至四成热，将虾球滑熟；锅中留底油少许，放入蒜茸、甘笋花，然后把荔枝、红椒块、虾球倒入锅中，烹少许上汤，加盐、味精、糖，最后用一点水淀粉打芡，盛入盘中即可。', NULL, NULL, NULL, '鲜荔枝250克，大虾肉250克，红辣椒2个，味精，胡椒粉，精盐，绍酒，淀粉等。');
INSERT INTO `recipe` VALUES (1733, 76, '栗子百果羹', '1.将栗子、红瓜、青梅都切成与白果一样大小，然后将栗子和白果上笼蒸约四十五分钟。\r\n2.将栗子和白果取出，与红瓜、青梅一起放入锅内，加入600克烧开后，再加上糖的湿菱粉，调成羹（湿菱粉不要放得过多），然后将桂花糖放入，调匀后起锅。', '色白，味香甜，适宜于秋冬二季。', NULL, NULL, '栗子肉200克，白果肉200克，红瓜40克，青梅40克，白糖165克，桂花糖少许，菱粉40克。');
INSERT INTO `recipe` VALUES (1734, 76, '凉瓜排骨', '1.猪肉排切成块，用太白粉拌匀，苦瓜切成丝，加盐、水抓匀，腌至30分钟后，放在清水中，洗去盐分；\r\n2.葱切成段，姜、蒜、豆豉切碎；将油加热，放姜、豆豉、蒜同炒，再放排骨、苦瓜、用中火炒4－5分钟，加半杯水和盐、味精、酱油、糖、蚝油、胡椒粉拌炒，再用慢火焖煮20分钟左右，再用太白粉、水、勾芡，放葱，即可盛盘。', NULL, NULL, NULL, '猪肉排280克，葱2根，苦瓜580克，姜2片，蒜头1瓣，豆豉35克，太白粉半匙，盐1小匙，味精半匙，酱油1匙，糖1匙，蚝油1小匙，胡椒粉少许，油7大匙。');
INSERT INTO `recipe` VALUES (1735, 65, '灯影牛肉', '1.选用牛后腿肉去浮皮边角，片成大薄片，均匀地撒上调料，晾呈鲜红色，入烘炉烘干。\r\n2.将烘干后的牛肉改小片，上笼蒸透。\r\n3.将蒸好的牛肉片置油锅中，以文火慢慢炸透，烹入绍酒调料颠翻均匀后晾凉即成。', '牛肉片薄如纸，色红亮，味麻辣鲜脆，细嚼之，回味无穷。', NULL, NULL, '黄牛肉约480克，盐、糖、花椒粉、辣椒粉、五香粉各适量。');
INSERT INTO `recipe` VALUES (1736, 76, '岭南椰子盅', '1.将大椰子剥去皮，用锯子将椰子帽顶锯开，去掉心子；将鸡肉用开\r\n水川一下，切成10块，洗净后放在椰子内。\r\n2.将鲍鱼切成10块，与口蘑、火腿、燕窝、黄酒、盐一起放在椰子内，装满开水，帽盖好，上笼约蒸四小时取出（先试味，如不够鲜，再加些味精和盐），将奶油倒入椰子即好。（椰子盅下面可配以适当的座架）。', '味鲜，清香，壳外咖啡色，营养丰富，疗肺，养身，补神。', NULL, NULL, '大椰子1只，发好口蘑40克，鸡腿肉250克，瘦火腿40克，发好鲍鱼75克，发好燕窝75克，黄酒40克，奶油20克，精盐少许。');
INSERT INTO `recipe` VALUES (1737, 76, '龙宫照宝鸭', '1．将每只明虾去壳，片成两片，洗净，滤干，放入碱和小苏打泡的水内约腌一小时，再用清水徐徐注入，把碱水的涩味漾净，一直漾到虾片浮起成透明为止。一般夏天要漾一小时，冬天要漾两小时半，虾片才能发亮，涩味才能漾净。\r\n2．起旺热生油锅，用大火烧开，再将虾片放入炸，连油倒出，再利用原油锅的余油，将虾片捞出倒入，加鸡汤和胡椒粉、盐、糖、菱粉、葱姜末一翻起锅，装在盘的中央。另用挂炉鸭（要现做的）片下皮围边（肉不要），另外跟上一个蚝油葱酱即好。', '色红白分明，味鲜香甘脆。', NULL, NULL, '挂炉烤鸭1只1250克，新鲜明虾15只，鸡油80克，碱水20克，小苏打水13.5克， 葱花少许，姜末少许，精盐少许，胡椒粉少许，菱粉少许，白糖少许。');
INSERT INTO `recipe` VALUES (1738, 76, '龙虎斗', '1.将活蛇宰杀，去头尾、皮和内脏，洗净后“蛇壳”（带骨蛇肉）入砂锅内煮熟，取出拆出蛇肉。猫或豹狸肉入沸水锅中氽一分钟后捞起，用火燎去毛，放入清水盆中刮去污物，取出沥干水，入砂锅内加清水、姜汁、白酒、葱煮熟，取出拆肉。\r\n2.将拆出的蛇肉、豹狸肉撕成细丝，用姜、葱、精盐、绍酒煨好。鸡丝先用蛋清、干淀粉少许拌匀上浆，然后炒锅烧热，下熟猪油250克，至四五成热时，放入鸡丝过油至断生取出，沥干油，将姜丝放入沸水锅中煮约五分钟捞起，放入清水漂净，去净姜丝辣味。\r\n3.将蛇肉、猫或豹狸肉、鸡丝等原料放入炒锅，加鸡汤7', '配料多样，肉嫩香滑，味鲜物异，味鲜特异，同薄脆、柠檬叶丝和菊花瓣佐食，风味尤为特殊，秋冬食之最宜。蛇不仅肉味鲜美，而且营养丰富，具有治病功能。据《本草纲目》记载，蛇肉能祛风活血，除疾去湿，补中益气，明目滋阴。', NULL, NULL, '三蛇肉（眼镜蛇、金环蛇、过树榕蛇）250克，猫或豹狸肉150克，鸡丝100克，水发鱼肚50克，冬菇75克，木耳丝75克，姜丝50克，猪油250克（实耗25克），麻油、陈皮、精盐、绍酒、白酒少许，生粉15克，原蛇汤1000克，柠檬叶丝15克，白菊花30克，薄脆100克。');
INSERT INTO `recipe` VALUES (1739, 76, '罗定鸡排', '1.鸡肉切块，用太白粉、玉米粉拌匀，腌至5分钟，葱切断，红葱头稍拍，蒜切碎；\r\n2.锅中油加热，放红葱头炸5分钟，放入鸡块，再加葱、蒜、豆豉炒4分钟后，再加入盐、味精、酱油、蚝油、糖、水蒸4分钟左右，即可盛盘食用。', NULL, NULL, NULL, '鸡胸肉280克，红葱头100克，葱1根，蒜头3瓣，姜4片，豆豉35克，油4大匙，味精半匙，酱油1小匙，太白粉1匙，玉米粉少许，盐半匙，蚝油1小匙，糖半匙。');
INSERT INTO `recipe` VALUES (1740, 76, '满坛香', '1.狗肉、鸡肉和鸭肉切成小块，鱼块、鱼唇（浸发后）切成长3.5公分，宽2公分的条块，青蒜切成4公分长的段，生菜洗净分两碟盛起。炒锅烧热，下狗肉炒干水分取出，姜块放入开水锅中焯三分钟取出。\r\n2.炒锅烧热，下猪油50克，放入姜块，蒜段，爆一分钟后取出。再下油25克，放入豆酱、豆腐乳略炒后下狗肉、姜块、蒜段爆约一分钟，加绍酒25克，加淡鲜汤1000克、白糖10克、精盐5克和陈皮，烧开后倒入砂锅，加盖，放在木炭炉上中火煲约九十分钟至软烂。\r\n3.炒锅烧热，下油750克，烧至五成热，放入用湿淀粉拌匀的鸡、鸭肉氽至', '此菜用料广泛，汤汁芬香浓郁，揭盖时香味四溢，食物异常鲜美，在隆冬季节，围着坛边煮边吃，炉热身暖，别具风味。', NULL, NULL, '带骨狗肉500克，带骨鸡鸭肉各500克，瘦猪肉片150克，浸发鱼肚150克，干鱼唇150克，生菜500克，浸发香菇150克，熟陈皮细粒2.5克，青蒜100克，葱5克，姜块50克，姜片5克，精盐15克，味精2.5克，豆酱50克，豆腐乳25克，白糖25克，蚝油100克，芝麻油5克，深色酱油15克，绍酒50克，姜汁酒10克，湿淀粉25克，淡鲜汤1500克，熟猪油750克（实耗100克）。');
INSERT INTO `recipe` VALUES (1741, 76, '蜜桃炒虾仁', '1.虾仁去壳及剔走黑肠，洗净抹干，备用。\r\n2.烧溶牛油，放入虾仁煎熟后盛起。\r\n3.将蜜桃沥干汁，用煎过虾的牛油略炒。\r\n4.加进调好的蜜桃柠檬汁，放回虾仁煮滚，即可上碟。', '香味没法挡。用牛油来炒虾仁，香味会不期然弥漫在整个厨房内，当再加入了蜜桃及汁料后，那阵浓郁的蜜桃味更加厉害，未吃已经可以猜到它的味道将会如何。由于加入了柠檬汁的关系，令蜜桃的甜味中和了不少，亦消除了鲜虾的腥味。', '想菜式味道更酸甜，可随个人喜好多加一些柠檬汁，味道将更香口。 可用新鲜蜜桃代替罐装蜜桃，但要先去皮及核。蜜桃易碎，炒时切勿太大力。罐头蜜桃汁可用上汤代替，但要加一茶匙糖。', NULL, '虾仁500克，蒜茸少许，牛油1汤匙，罐装蜜桃1罐，盐少许，柠檬汁1汤匙，罐头蜜桃汁半罐。');
INSERT INTO `recipe` VALUES (1742, 76, '明炉烤乳猪', '1.将乳猪放在砧板上（胸朝上），从嘴巴开始颈部至脊背骨尾部，沿胸骨中线劈开（表皮勿破），挖出内脏，内外洗净沥干，使猪壳成平板形。挖出猪脑，把两边牙关节各劈一刀，使上下分离，取出第三条肋骨，划开扇骨关节，取出扇骨，并将扇骨部位的厚肉和臀肉轻轻划上几刀。\r\n2.将五香盐均匀地涂在猪腔内，用铁钩挂起，腌约三十分钟，晾干水份，然后再把豆酱、腐乳、芝麻酱、汾酒、蒜泥、白糖25克拌匀，涂在猪腔内，二十分钟后，用特制的烧叉从臀部插入，跨穿一到扇骨关节，最后穿至腮部。上叉后将猪头向上斜放，用清水冲洗皮上的油污，再用沸水淋', '乳猪色泽大红，油光明亮，皮脆酥香，肉嫩鲜美，风味独特。', NULL, NULL, '净乳猪1只（约5千克重），五香盐（由五香粉1克、八角末1只、精盐35克、白糖15克拌匀即成），烤乳猪糖醋150克（用饴糖50克、白醋75克、糯米酒10克调匀，加热烧浓即成），白糖65克，豆酱100克，红腐乳25克，千层饼125克，酸甜菜150克，葱球150克，蒜泥5克，芝麻酱25克，甜酱100克，汾酒7.5克，花生油25克，木炭7.5千克左右。');
INSERT INTO `recipe` VALUES (1743, 76, '明炉全乳猪', '1.将乳猪放血后，用七成滚开水，三成冷水，将小猪放入泡十几秒钟，取出，去毛，去内脏。再从猪的嘴唇处开始，经肚腹到肛门，用刀剖开，肚腹分两边扒开放平，用刀在脊柱骨、臀骨和每根肋条骨上轻轻划一条缝，用乳腐、五香粉、白糖、蒜泥、盐混和调匀，倒在肉面上全面搽开，使它渗入内部。再把小猪拎起，在猪皮上用滚开水淋透，涂以酒、醋、饴糖调的卤，然后悬空挂起，风干两小时。\r\n2.接着上铁叉。上铁叉前，先用一根根竹片横着，将两边猪肉撑住，再将铁叉从臀骨开始，直插到项颈，铁叉要一上一下地通过每根竹片，插得既要牢，又要平，同时不能', '味香脆爽滑，别具风味。', NULL, NULL, '毛乳猪1只，3500克至4000克，腐乳1块，麻油175克，五香粉17.5克，白糖70克，蒜头40克，精盐85克，香醋135克，饴糖40克，黄酒40克。');
INSERT INTO `recipe` VALUES (1744, 76, '柠檬鸡', '1.鸡脯肉洗净，切成长8厘米、横截面0.6厘米的条，入碗，用川盐、料酒、胡椒粉、姜、葱码味；柠檬（一半）取汁，取碗一个，放入面粉、细干淀粉、川盐、胡椒粉、鸡蛋、化猪油等调成糊状待用。 \r\n2.用萝卜雕刻成一只雄鸡，作鸣叫状；余下的柠檬、番茄洗净，切成月牙片。\r\n3.取圆盘1个，将雄鸡头朝向盘内摆放在盘子的一边，圆盘四周用柠檬、番茄、香菜、樱桃围边。\r\n4.锅置火上，放精炼油烧至五成热时，将鸡条放入面糊内，均匀地裹上一层后，入锅炸至定形捞出，整齐地堆放在盘子正中。 \r\n5.锅置火上，掺鲜汤，放川盐、白糖、柠', '风味独特，有解暑和增进食欲之功效。色泽协调，美观大方，质地外酥内嫩，咸鲜、酸、甜兼备，风味别具。', NULL, NULL, '鸡脯肉300克，面粉100克，细干淀粉30克，鲜柠檬250克，白糖30克，番茄100克，白萝卜500克，蜜樱桃4粒，川盐、料酒、泡打粉、姜、葱、香菜、胡椒粉、鲜汤、湿淀粉、精炼油、化猪油各适量。');
INSERT INTO `recipe` VALUES (1745, 76, '糯米纸包虾', '1.将明虾每只片成3片，共24片，同猪肥膘末、鸡蛋、辣椒末等所有调料调和。\r\n2.把糯米纸一张张摊开，将拌好调料的虾片在每张纸上放1片，然后包成24个长方形，放入滚开的大生油锅炸熟即好。', '味香脆爽滑，别具风味。', NULL, NULL, '鸡蛋1/4只，明虾8只（去壳）， 糯米纸24张（每张6寸见方），猪肥膘末40克，葱末少许，红泡辣椒末少许，红火腿末少许，冬菇末少许，麻油少许，胡椒粉少许，白糖少许，味精少许，菱粉少许，黄酒少许，精盐少许。');
INSERT INTO `recipe` VALUES (1746, 76, '麒麟大桂鱼', '1.将桂鱼的肉拆下，头尾留好。鱼肉切成20块枣块（零碎边角留下），用味精、盐、糖、胡椒拌和腌一下。\r\n2.将冬菇、火腿、笋切成小花片（冬菇、火腿各留下一半切成丝留着），然后将1块鱼、1片火腿、1簇冬菇、1片笋片依次在盆内装成像鱼身一样，放入笼内蒸十五分钟。\r\n3.将鸡蛋打碎，去掉蛋白，用菱粉与蛋黄拌成糊状，涂在鱼的头尾上，放入生油锅内氽一下，再将肉丝、冬菇丝、葱姜丝加酱油、糖、味精红烧后，放在鱼头上做麒麟须。\r\n4.将蒸好的鱼身装在头尾中间，再用鸡汤加些糖、味精、盐、黄酒、麻油、菱粉烧成沙司，浇在鱼身上即', '味美，色五彩。', NULL, NULL, '桂鱼1条，1250克净、水发冬菇75克，火腿75克，鸡蛋2只，肉丝40克，麻油少许，姜丝少许，味精少许，笋50克，胡椒少许，葱丝少许，黄酒少许，萝卜少许，菱粉少许，白糖少许，精盐少许，酱油少许。');
INSERT INTO `recipe` VALUES (1747, 76, '青蟹粉丝煲', '1.先将青蟹整理干净，腹部切成5块，葱切花，蒜切茸，粉丝泡10分钟，剪半捞出；\r\n2.将葱放在锅底，放2匙油，粉丝放开水中，加入盐、味精、酱油、油煮1分钟，倒进砂锅；\r\n3.锅中油烧开，将蟹放进锅中炸1分钟后加水，煮2分钟，再放入盐、糖、蚝油、葱花、蒜茸炒匀，放入粉丝，再放半匙酒即可。', NULL, NULL, NULL, '青蟹1支，葱3根，蒜3瓣，粉丝145克，盐、味精、酱油各半匙，油1大匙，糖半匙，蚝油1小匙，酒半匙。');
INSERT INTO `recipe` VALUES (1748, 76, '清蒸滑鸡', '1.将鸡肉切块和地栗粉、白糖、白酱油、黄油、味精等调拌均匀，放在盘内。\r\n2.将冬菇切片，同姜片放在鸡块上面，用旺火蒸约十五分钟可熟，不要蒸得过老，蒸熟后取出，将生油烧热，浇上即好。', '味甘香鲜美，色淡黄，宜作四季小吃。', NULL, NULL, '嫩鸡肉275克，水发冬菇20克，地栗粉5克，白糖少许，黄酒少许，味精少许，姜片少许，白酱油少许，生油少许。');
INSERT INTO `recipe` VALUES (1749, 76, '清蒸鲈鱼', '1.将鲈鱼宰好，除内脏，洗净。用盐、麻油、味精等拌匀，浇入鲈鱼肚内。用葱二三条放在碟底，葱上放鲈鱼。\r\n2.再用猪肉丝、冬菇丝、姜丝和（少许）热盐、酱油、地栗粉搅匀，涂在鱼身上，隔水猛火蒸十分钟，熟后取出原汁的一半，加生葱丝及胡椒粉放于鱼上，再烧滚猪油淋上，略加适量酱酒即好。', '白色，味鲜香，四季皆宜。', NULL, NULL, '鲈鱼1条（700克），猪肉丝50克，水发冬菇丝20克，精盐少许，猪油40克，麻油少许，白酱油少许，姜丝15克，胡椒粉少许，葱2条，地栗粉少许，味精少许。');
INSERT INTO `recipe` VALUES (1750, 76, '清蒸石斑鱼', '先将鱼整理干净，用盐抹遍鱼身，葱切长丝，把鱼、姜、葱放在盘中，淋下酱油、油，再用大火蒸10分钟，取出，倒去汤汁和姜片，再浇上加热过的酱油、花生油即可。', NULL, NULL, NULL, '石斑鱼1条，葱3根，姜3片，樱桃1棵，盐半匙，油半杯，酱油半杯，花生油1小杯。');
INSERT INTO `recipe` VALUES (1751, 76, '三色龙虾', '鲜龙虾用竹签于尾部插入至肛门处放尿，再在头部插入使其至死，放入冰箱冷冻，取出解冻，把龙虾肉整条从肚部取出，切成两半后，再切成粗粒，头、尾留用。将鲜笋、甘笋、芹菜梗切成榄形；将腰果仁用油炸至松脆；龙虾头、尾滚熟，摆在盘的两端，把炒好的西兰花围上，砌成龙虾形。用上汤、精盐、味精、白糖、淀粉调成碗荧，将笋粒、甘笋粒、芹菜粒等加盐滚过；烧锅下油，把龙虾过油至仅熟，取出沥去油，再下姜片及全部原料（腰果仁除外），溅绍酒，下碗芡炒匀后再放入腰果仁，加包尾油炒匀，倒入由西兰花围的中间便成。', '龙虾是珍贵的海产品之一，按此法烹制风味极佳。味鲜，松脆，爽嫩，色泽鲜艳， 是近年新创的菜式。', NULL, NULL, '龙虾800克，笋肉100克，腰果仁150克，芹菜50克，甘笋50克，西兰花300克，上汤、精盐、白糖、味精、花生油、姜片、绍酒、芝麻油、干淀粉各适量。');
INSERT INTO `recipe` VALUES (1752, 76, '沙锅鸭块', '1.将鸭肉切成小块；\r\n2.炒锅上火，油至五成热，下入猪肉片和香菇煸炒，放入葱、姜、蒜炒出味，然后放鸭块，加高汤和盐、酱油、醋、料酒、味精，开锅后一起倒入砂锅，用微火炖30分钟左右；\r\n3.另起锅放底油，下辣酱和蒜片煸炒出味，倒入砂锅加盖上桌即可。', NULL, NULL, NULL, '鸭肉、肥瘦猪肉片l00克，水发香菇50克，油、盐、酱油、醋、味精、料酒、葱段、姜片、蒜片、辣酱、高汤各适量。');
INSERT INTO `recipe` VALUES (1753, 76, '沙拉酱炒鱿鱼卷', '1.鱿鱼切1cm宽，3cm长的块。 \r\n2.洋葱切丝，玉米罐头去掉水分。 \r\n3.将沙拉酱、酱油、蚝油、豆瓣酱、芝麻油和在一起，再和1拌在一起，放15分钟左右。 \r\n4.将3倒入锅内炒，最后倒入玉米粒。', '鲜咸润滑，口感适宜。', NULL, NULL, '鱿鱼400克，芝麻油1匙，葱头1个，蚝油2匙，玉米罐头8匙，豆瓣酱1匙，酱油1匙，丘比沙拉酱6匙。');
INSERT INTO `recipe` VALUES (1754, 76, '上汤虾丸', '1.将虾去头去尾去壳，用盐洗净，放在干净的白布上包起，挤去水分。再用干净的纸铺在台上，将虾肉放入，把未干的水分再挤一下，加入味精、盐、面粉，搓揉到松软，放入冰箱，冰冻一小时取出（不冻不脆）。\r\n2.将虾肉制成圆子，放入烧滚的清水里煮（煮时水不能滚起泡，否则太老），煮到虾圆浮起时，即捞起，放入盛鲜汤的碗内即好。', '白色，味鲜嫩爽脆，四季皆宜。', '煮虾圆时，用清水，不要放其他调味。', NULL, '鲜虾275克，味精40克，面粉40克，精盐少许。');
INSERT INTO `recipe` VALUES (1755, 76, '生炒甲鱼片', '1.把甲鱼肉洗净，切成薄片，和少许白酱油、鸡蛋白及地栗粉、盐、麻油、胡椒、味精拌匀，入文火大温生油锅（油要250克），用勺慢慢推开，至六七成熟即一起倒出。\r\n2.将葱姜片、蒜泥、笋放入锅内稍炒，再把甲鱼片与调味（胡椒粉、酒、味精等）放入同炒一下，再下地栗粉勾芡，同时浇上生油，起锅装盘，再撒上大地鱼末即好。', '味鲜，淡酱色，四季皆宜。', NULL, NULL, '甲鱼肉150克，笋片200克，鸡蛋白1只，大地鱼末5克，葱5克，姜片3片，味精少许，麻油少许，烧酒少许，白酱油少许，地栗粉少许，胡椒粉少许，蒜泥少许，精盐少许。');
INSERT INTO `recipe` VALUES (1756, 76, '生炊龙虾', '1.先将原只龙虾洗干净，斩去虾脚，然后斩段。头部开边，外壳和虾肋去净，后斩件摆落在虾脚上面。尾部开边，捡去虾屎，连壳斩件摆在脚头上面。\r\n2.用小碗盛白酒，加入精盐开匀后撒落虾肉上，虾的上面放姜、葱，进蒸笼猛火蒸8分钟即熟。取出后捡去姜、葱，淋上白猪油，碟边伴香菜，跟桔油一碟上席。', '龙虾个大体粗，身长肉厚，甲坚硬多棘，肉味鲜美，是名贵的海鲜品。我国东南海所产的锦乡龙虾，每只重达数公斤，龙虾为高级筵席上的珍馐。本品为潮州传统名菜。炊，古代作蒸。宋仁宗时，讳其赵祯之名，凡蒸的都改作炊；元明以后，许多地方复改炊为蒸但潮汕方言却一直作炊不变。“生炊龙虾”用猛火蒸至恰熟，肉质脆嫩，味道鲜美，蘸上桔油，开胃醒酒。', '宰虾时，必须去净腮和虾屎，使蒸出的虾鲜美而无异味。放进蒸笼必须猛火蒸（蒸气直上，遇微风不摇摆，即猛火气足），掌握好时间，以不超过10分钟为度。这样蒸制的龙虾肉质脆嫩。', NULL, '活龙虾750克，精盐5克，生姜3克，香菜25克，潮州桔油2碟，白猪油75克，白酒15克，生葱25克。');
INSERT INTO `recipe` VALUES (1757, 76, '生炊麒麟鱼', '1.将生鱼起肉去皮，鱼骨不用，鱼尾留用。把鱼肉片成二片连在一起，共24片，用碗盛起。\r\n2.火腿、冬菇、肥肉也各切成24片，然后将肥肉片投入鱼片内加蛋白、白酒、盐25克、味精25克、葱、姜等腌几分钟。\r\n3.将鱼碟的碟底抹上薄猪油，然后将肥肉、火腿、冬菇夹在每片鱼片中间，摆放在碟上，放进蒸笼蒸约7分钟取出，鱼头、鱼尾要提早蒸熟，摆在碟头尾。\r\n4.将鱼的原汁下锅，加上汤150克、精盐2.5克、味精2.5克，打芡，最后把麻油、猪油淋上即成。', '麒麟，古代传说中的一种动物，形状象鹿，全身有鳞甲，古人以其象征祥瑞。此菜是以其形似麒麟皮甲而命名。乃是将几种用料切成片状拼配而成，颜色不同，均匀有序，犹如麒麟身披的鳞甲，悦目诱人，其菜鱼肉纯白，肉质爽滑，造型大方，味鲜无比。', '此菜亦可选用桂鱼，名曰“麒麟桂鱼”，此菜入笼蒸的时间不宜过长，肉刚熟为最佳，约需时10分钟左右。', NULL, '生鱼750克，火腿40克，湿冬菇50克，肥肉50克，味精5克，精盐5克，姜10克，葱10克，白酒10克，麻油5克，生汤5克，蛋白15克，上汤150克，尾油25克。');
INSERT INTO `recipe` VALUES (1758, 76, '水晶明虾球', '1.将明虾剥壳去头尾，再将表面的黑皮用刀片去，每只明虾片成3片，腌放碱水中（每500克明虾用碱水50克），两小时以后取出，再以冷水冲一小时。\r\n2.另用旺火温生油锅，将明虾放入，稍爆即捞出，再用清水烧开，将明虾放入一川捞出。另起旺火油锅，将明虾放入，一推即倒出。\r\n3.利用原油锅，把姜丝、葱丝放入煸一下，随即将明虾倒入，迅速划开，同时加入麻油、味精、胡椒粉、精盐、油勾芡，马上倒入漏勺，滤去地栗粉装盘。食时跟蚝油、虾酱各一小碟。', '色雪白透明，吃来爽脆，夏季最宜。', NULL, NULL, '明虾10只，味精12.5克，精盐少许，地栗粉少许，麻油少许，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1759, 76, '太爷鸡', '1.活鸡经宰杀、开膛，洗净，先入开水锅中略焯，取出洗净，再放入微沸的精卤水锅中，旺火煮约三十分钟左右，至八成熟时取出。\r\n2.铁镬置上，镬内铺上锡纸。将香片茶叶、广东土制的片糖屑100克、米饭100克置入镬内，再将鸡架于镬架上，盖镬密封，加大火烧至冒黄烟片刻。取出熏成的“太爷鸡”，斩成条块，装盘时配以菜蔬、红椒丝，朱碧相映，再淋上麻油，冷热均可上桌食用。', '色泽枣红，肉嫩味醇，有浓厚的茶叶清香。', NULL, NULL, '信丰母鸡1只（1500克重），香茶叶50克，片糖屑100克，花生油150克，精卤水2000克（将八角75克、桂皮100克、甘草100克、草果25克、丁香25克、沙姜25克、陈皮25克、罗汉果一个放入布袋，用绳子扎紧口袋，加酱油500克、冰糖100克、绍酒100克、精盐7克、味精1.5克、生油100克、水1500克煮成）。');
INSERT INTO `recipe` VALUES (1760, 76, '糖醋咕噜肉', '1.将猪肉片成菱形块，用精盐、酒腌入味，裹上鸡蛋液和淀粉，油烧至六成熟，把肉炸至五成熟，倒入漏勺沥油；\r\n2.再炒蒜、辣椒，爆出香味，加葱、番茄酱，随即倒入肉片和笋块拌炒、勾芡，装入盘中即可食用。', NULL, NULL, NULL, '去皮五花猪肉500克，鸡蛋清50克，辣椒20克，新鲜竹笋100克，葱段、蒜泥、芝麻油各0.5克，精盐1.5克，咸番茄酱200克，酒7克，淀粉90克，花生油750克。');
INSERT INTO `recipe` VALUES (1761, 76, '天麻鱼头汤', '1.用清水洗净2个大鱼头和100克天麻，先除去鱼鳃内污物并切为两边，天麻沥干水备用。\r\n2.烧红镬，加入油，爆香姜片，放少许酒，倒入鱼头，封煎去除鱼腥，约1－2分钟后取出，放在吸油纸上，吸去多余油分待用。\r\n3.注8碗清水于炖盅内，先放鱼头于盅底，之后放入天麻和100克云腿，隔水炖至水沸时，改用中至慢火，炖两至三小时，再放入适量盐便成。', '天麻胶质重，味甘甜而带苦涩，具有益气定惊、镇痛养肝、祛风湿、强筋骨等效用。专治神经衰弱、眩晕头痛、宁神定惊、益气养肝、利腰膝。', '据中医师指出，由于天麻属名贵中药材，故市面上亦有出现伪品，需小心识别，记紧留意天麻质地是否坚硬而紧密，呈半透明状，表面带黄白或浅黄棕色，通体晶莹丰满，个大结实则为上选。', NULL, NULL);
INSERT INTO `recipe` VALUES (1762, 76, '网油凤肝卷', '1.先将清水烧开，把鸡肝放入烫七成熟，取出，切成指甲片，把鲜菇、火腿、鲜笋也都切成指甲片，和味精、麻油、蚝油、胡椒粉、精盐等调匀，把鸡肝放入拖一拖。\r\n2.再将网油摊开，撒上一些地粟粉，将鸡肝等放在网油上面卷成4寸长、2寸圆围后，在卷子外面抹上一层薄薄的湿地栗粉，再撒上一层干地栗粉，投入滚热的生油锅炸一下，再转文火炸成金黄色，取出，切成棋子形即可。', '味甘香脆嫩，色金黄，四季皆宜。', NULL, NULL, '鸡肝175克，火腿40克，鲜菇40克，网油20克，鲜笋40克，麻油少许，味精4克，精盐少许，地栗粉40克，胡椒粉少许，蚝油8克。');
INSERT INTO `recipe` VALUES (1763, 76, '西拧煎软鸡', '1.鸡肉切块，用盐、味精、古士粉、鸡蛋、水搅拌均匀，腌至10分钟，柠檬横切成片；\r\n2.锅中油加热，将鸡肉放入锅中，用中火煎，大约5分钟后即可盛盘；\r\n3.将糖、白醋、水、盐、柠檬放入油锅中，再用古士粉、水、勾芡，炒匀后盛起，淋在鸡块上，再放上柠檬片即可。', NULL, NULL, NULL, '鸡胸肉350克，柠檬半个，盐、味精各半匙，古士粉3小匙，鸡蛋半个，糖6大匙，白醋半小杯，油半杯。');
INSERT INTO `recipe` VALUES (1764, 76, '虾胶瓤鱼肚', '1.半干炸鱼肚用清水浸泡约2小时，反复换水搓洗三次，去掉异味，取出沥去水分，剪去皮、筋，切成直径4厘米的圆形片12块，放入沸水锅内氽30秒钟，捞出沥去水。炒锅用中火烧热，下花生油15克，放姜、葱爆至有香味，烹绍酒，下二汤250克、精盐1克，烧沸后下鱼肚煨约1分钟，取出，去掉姜、葱，用洁净毛巾吸干水分，盛在盘中，晾凉后，在每块鱼肚的一面撒上干淀粉。 \r\n2.虾胶分成12颗小丸，放在干淀粉一面的鱼肚上，用鸡蛋清把虾胶抹平。香菜叶、火腿末分别放在虾胶两端，入蒸笼用旺火蒸约5分钟至熟，取出，转放在另一盘中，排成2', '鱼肚，是鱼鳔的干制品。鱼肚的品种很多，常见者有黄鱼肚、鲴鱼肚、广肚、毛常肚等。鱼肚是海八珍之一含蛋白质、脂肪及粘胶物。中医认为，鱼肚甘、咸、平，有养血止血、补肾固精的食疗效果。此菜是选用黄鱼肚，油炸涨发，再水浸至软，然后切成小块，瓤上虾蒸熟而成。颜色洁白，质地爽软。口味清鲜，四季皆宜。', '干炸鱼肚：用清水将干鱼肚500克浸泡约6小时后剪开，刮去内膜，洗出外筋。平铺在竹箅子上晒干后砍段（长的分三段、短的分两段）。将深口铁锅在中火柴炉上，下花生油1000克，烧至六成热，放入鱼肚，边炸边翻动，至鱼肚膨胀时，改用微火，用特制铁丝箅将鱼肚压至半浮沉，继续浸炸。见鱼肚升起的气泡由大、快、密变为小、慢、疏时，取出铁丝箅，捞起冷却。炸时要掌握好火候、油温，如炸的时间不够，水发后，发韧而不膨胀；过之，则捞起易碎；只有恰到好处，才能捞起能折断，闻之有响声。虾胶：把猪肉75克切成细料，放入冰箱。将吸干水分的虾仁', NULL, '干炸鱼肚50克，姜片10克，虾胶180克，忽条10克，菜芜150克，精盐2克，熟火腿末2.5克，味精1克，香菜叶5克，鸡蛋清10克，胡椒粉0.5克，二汤350克，姜汁酒10克，芡汤10克，干淀粉25克，芝麻油0.5克，湿淀粉7.5克，花生油60克，上汤125克。');
INSERT INTO `recipe` VALUES (1765, 76, '咸蛋蒸肉饼', '将猪肉剁烂，加盐、味精、咸蛋清、干淀粉搅至起胶，再加少许花生油拌匀，然后放盘中压扁，把感蛋黄压扁，放肉饼上，用中火蒸熟，取出，用适量汤水、浅色酱油调匀，浇在肉饼上便成。', '鸭蛋经盐腌一段日子，具有特别的咸香味，与猪肉糜拌在一起，蒸成肉饼，是广东各地家庭常用菜肴。', '猪肉选肥三瘦七者，去皮剁烂，成菜酥软油润。', NULL, '猪肉300克，胡椒面3克，咸蛋2个，浅色酱油25克，干淀粉25克，花生油25克，味精2克。');
INSERT INTO `recipe` VALUES (1766, 76, '蟹黄大生翅', '1.将锅烧热，加猪油，再加黄酒、鸡汤、鱼翅、味精、酱油、胡椒粉，以慢火煮，但不可太滚（使保持嫩滑）。\r\n2.用蟹黄、地栗粉勾芡烧在翅上，再在翅面上撒火腿茸。', '色黄白，味鲜美嫩滑，秋冬季最宜。', NULL, NULL, '鲜汤烩好鱼翅400克，胡椒粉少许，鸡汤1000克，黄酒少许，地栗粉40克，火腿茸少许，白酱油26.5克，味精少许，蟹黄13.5克。');
INSERT INTO `recipe` VALUES (1767, 76, '杏元鸡脚炖海狗', '1.将海狗放在砧板上，迅速在头部横砍一刀（不要砍断）放血后，用九十度热水浸烫，刮洗表皮粘液，再从肚部剖开，去内脏洗净，切块（每块重约25克），取用750克，其余另作他用。\r\n2.将火腿切成五块，猪肉切六块。鸡脚用沸水略烫后，剥去外衣，砍去趾甲，敲断脚骨，洗净。将杏仁放入碗内洗净。\r\n3.将猪肉、鸡脚放入开水锅内煮熟，下火腿后一起捞出，放入炖盅骨，海狗放入开水锅中略焯，取出洗净。用中火烧热炒锅，下油15克，加姜、葱各10克，放入海狗肉爆炒一下，烹姜汁酒，下淡鲜汤，煨一分钟，倒入漏勺沥去水，去姜葱，放入炖盅内', NULL, NULL, NULL, '海狗1只（约2500克左右），嫩鸡脚6对，熟火腿25克，瘦猪肉100克，南杏25克，桂圆10克，葱15克，精盐5克，姜汁酒25克，绍酒25克，鸡汤750克，淡汤250克，白开水750克，花生油30克。');
INSERT INTO `recipe` VALUES (1768, 76, '盐擦鸡', '1.将鸡杀好，去内脏及脚并洗净，用干洁布揩干，用针在鸡腿上、鸡脯上均匀地刺上一些小孔，全身抹上盐和花椒，放在钵头内压约十二小时。\r\n2.做卤汁：将鸡汤加酒、味精调好味，放入锅里烧开，倒在钵头里，等冷却后，将压过的鸡放入浸约五六小时，取出，放在蒸器内，上面放葱、姜，用温火蒸约二十五分钟，即可切长方条食用。也有不用蒸，而放在鸡汤里烧的，但滋味不如蒸的鲜美入味。适宜于冬季。', '此菜是上海菜中最具特色的品种之一，它的烹制方法是先浸后蒸。浸过的卤汁可以继续使用，但因每浸一次，卤汁的鲜味和咸味都会增加，而分量却会一次比一次减少，故在继续使用时，一般可不再加咸味，只要加些鸡汤即可。', NULL, NULL, '肥母鸡1只约1900克，葱段42克，姜块42克，花椒13.5克，黄酒330克，盐85克，味精少许，鸡汤2100克。');
INSERT INTO `recipe` VALUES (1769, 76, '耶盅海皇', '椰子去皮洗净，穿透椰眼，控出椰汁后从蹄部锯开做成椰盅；冰糖置碗内稍蒸后倒入椰盅，上笼蒸1小时后再倒入发好的银耳和椰奶、白糖，蒸熟，撒上桂花即成。', NULL, NULL, NULL, '大椰子1个，水发银耳100克，冰糖100克，桂花、白糖等。');
INSERT INTO `recipe` VALUES (1770, 76, '夜香冬瓜盅', '1.将冬瓜蒂部分切下25公分成冬瓜盅。将盅口的瓜皮刨成斜边后，把盅口四周改成锯齿形挖去瓜瓤，在皮上刻上图案。然后放入开水锅内煮约十分钟取出，清水洗净，放入大炖盅内。用旺火烧热炒锅，放淡鲜汤1250克、精盐7.5克、味精5克、绍酒10克烧开后倒入冬瓜盅内，入笼中火蒸约一小时，至软烂取出。\r\n2.炒锅放开水500克、精盐2.5克、下鲜菇丁和去皮去芯的鲜莲子，焯约一分钟，取出沥干水，田鸡肉加湿淀粉5克拌匀，炒锅换开水500克，先放田鸡肉、虾仁、肫丁，各煮约半分钟捞起。\r\n3.取出冬瓜盅内的鸡骨、田鸡骨、猪骨及汤', '形态美观，色泽碧绿，汤香味醇，瓜肉软烂，鲜滑爽脆，清淡解暑，是传统夏令佳品。', NULL, NULL, '冬瓜1只（约3500－4000克重），鸭肉150克，烤鸭肉75克，瘦猪肉100克，田鸡肉100克，虾仁75克，鸭肫丁75克，熟火腿丁25克，鸡骨、田鸡骨、猪骨共400克，鲜莲子100克，鲜草菇100克，夜来香花25克，姜片5克，精盐10克，味精、绍酒少许，花生油25克，湿淀粉10克，淡鲜汤1000克，鸡汤1250克。');
INSERT INTO `recipe` VALUES (1771, 76, '鱼香茄子煲', '1.选长身茄瓜削去皮，去掉头、尾，一开为4件，长约12厘米。 \r\n2.烧热锅，下油，烧至约六成滚，投入茄瓜条炸约1分钟至熟，倒起沥去油分。 \r\n3.趁锅内余油，下蒜茸、芹菜粒、椒粒、煲仔酱爆香，接着下鸡粒炒匀，加入绍酒、上汤及茄瓜条和以上调味料和炸香咸鱼茸，略焖片刻，调入少许生粉，加入包尾油，转盛入瓦煲里，加热至滚，下香油、葱花，加盖上桌即可。', NULL, NULL, NULL, '茄瓜500克，鸡粒100克，炸香咸鱼茸10克，煲仔酱30克，西芹菜粒50克，青、红椒粒20克，上汤1杯，绍酒、蒜茸、葱花、深色酱油、香油各适量。');
INSERT INTO `recipe` VALUES (1772, 76, '粤笑口常开', '1.青口壳洗净抹干，用滚水灼过备用。\r\n2.蚬肉入水，过冷沥干。\r\n3.烧牛油下蒜茸，爆香青、黄椒及甘笋、冬菇粒，加入蚬肉及调味，下味淋炒透便可以青口壳盛载奉客。', '可不要小看蚬蛤，这可是一种上乘的中药材，蚬有白、黑、黄三种，三种蚬蛤都含丰富的蛋白质和铁质，将蚬壳洗净晒干，研成粉末，即为蛤粉，其功能有助清热利显，化痰定咳，对补肺极有功效。', '选购：最好挑选巨型，色带黄的，壳薄而肉肥味鲜，一般人称黄沙大蚬，市面售蚬多用水浸着，新鲜的蚬水清而壳作开合或紧闭状。', NULL, '青口壳12只，蚬肉1/2斤，青椒粒1/4杯，黄甜椒粒1/4杯，甘笋粒1/4杯，冬菇粒1/4杯，蒜蓉1茶匙，牛油2汤匙，味淋1茶匙，味椒盐1茶匙，日本酱油1茶匙，酱油1茶匙。');
INSERT INTO `recipe` VALUES (1773, 76, '炸子鸡', '1.鸡洗净，以配料擦匀鸡腔。\r\n2.浸鸡料以布袋盛着，加入清水十杯慢火煲二十分钟，取起布袋，加入盐、绍酒及鸡，慢火煮至鸡皮凸起取出。\r\n3.拌匀糖醋料，隔水炖至溶盛起，涂于鸡身，调味料涂匀鸡腔，将鸡挂在当风处，鸡皮略干再涂糖醋料，重复做四至五次，最后将鸡吹至干透。\r\n4.烧滚多量油，将鸡放入炸至金黄色，见鸡腿肉收缩即熟，斩件上碟即成。', '炸子鸡可说是广东菜的招牌鸡，皮脆肉嫩，寿筵喜宴不可缺少。', '原来烹调鸡有一个诀窍，鸡在屠宰时，因挣扎而使肌肉收紧，若即时泡制便会因肌肉过份紧张而未能入味，只要将鸡背向下，两手分别拿着脾，用力向下压三至四次便能将鸡的肌肉放松。', NULL, '光鸡1只（重约750克），盐两茶匙，绍酒三汤匙，盐、姜汁各一茶匙，花椒一茶匙，八角一粒，陈皮1/4个，桂皮、姜各一片，草果一个，蜜糖两汤匙，白醋、浙醋各三汤匙，生粉、马蹄粉、白酒各一汤匙，盐、花椒各半茶匙，五香粉1/4茶匙。');
INSERT INTO `recipe` VALUES (1774, 76, '植物扒四宝', '1.把笋尖、冬菇、蘑菇、竹荪放入温油锅内，用温火炸烤一下，再加鸡汤300克、鸡油15克和蚝油、味精、糖各少许约烩五分钟可熟，每样分开放在盘子中央成花瓣形。\r\n2.临吃时，一面就原盘上笼约蒸十分钟，一面把青菜心下热猪油锅，加适量调味品，用温火烧熟，取出围边。最后泌出少量烩笋尖等的原汤，加适量菱粉、鸡油15克勾芡，浇上即好。', '色淡黄，味鲜香脆，四季皆宜。', NULL, NULL, '竹笋尖100克，水发冬菇65克，鲜蘑菇65克，青菜心14块，干竹荪20克，蚝油13克，鸡油40克，味精少许，菱粉少许，精盐少许，白糖少许，鸡汤400克。');
INSERT INTO `recipe` VALUES (1775, 76, '子孙满堂', '1.咸蛋黄隔水蒸熟，每个蛋黄切成4小块待用；\r\n2.虾仁剁成虾泥加入绞肉及精盐、胡椒粉、蛋白、味精、淀粉、水仔细拌匀，然后挤成16个小丸子，中间镶入小块蛋黄，然后沾水搓圆滚上瓜子仁待用； \r\n3.温油投入沾满瓜子仁的丸子用小火炸，炸至呈金黄色即可捞出排入生菜铺底的盘中。', '以鸡蛋、猪肉末、鲜虾肉泥、瓜子仁为主料，搅拌成丸状，炸制而成。此菜制法独特，用料新颖，菜形美观别致，鲜、香、脆、嫩。', NULL, NULL, '咸蛋黄4个，绞肉300克，瓜子仁200克，虾仁、生菜各100克，精盐5克，胡椒粉1克，蛋白1个，味精2克，淀粉15克，水50克，油1000克。');
INSERT INTO `recipe` VALUES (1776, 77, '白炒三七花田鸡', '1.将田鸡剥皮割下后腿剔去骨，洗净入碗；再加葱10克、姜5克、料酒10克腌渍15分钟捞出晾干水分。 　　\r\n2.田鸡肉入碗，加盐5克、鸡蛋清、水淀粉20克上浆。 　　\r\n3.三七花放入淡明矾水中浸泡30分钟，捞出漂洗干净。 　　\r\n4.炒勺上火烧热，注入油，下菜芯过油，捞入漏勺内，锅上火，注入鸡汤200毫升，放盐，倒入菜芯，沸后，捞出摆在菜盘外沿。 　　\r\n5.将熟萝卜雕刻成小花，摆放在菜芯里面一圈。 　　\r\n6.炒勺上火，烧热注入油，放入葱20克、姜15克，下田鸡肉，滑散呈白色，倒入漏勺，捡出葱、姜。 ', '白炒三七花田鸡，白绿相映，三七花清香浓郁，田鸡肉细嫩鲜美，是一款造型漂亮，又有营养的佳品。', '田鸡肉用调料腌渍15分钟，使其入底味，便于后面的烹调。', NULL, '活田鸡600克，料酒20克，鲜三七花10克，精盐15克，油菜芯200克，味精3克，熟红萝卜200克，麻油3克，鸡蛋清1个，胡椒粉2克，鸡汤400毫升，水淀粉30克，葱丝30克，猪油1000克，姜丝20克，明矾水10克。');
INSERT INTO `recipe` VALUES (1777, 77, '菠萝鸡丁', '1.鸡腿肉拍松、切丁后用酱油、洒、湿淀粉、糖腌。　　\r\n2.油多许，将鸡肉过油捞出。 \r\n3.留底油，炒葱姜，放入菠萝块、青椒，后将鸡丁倒入翻炒，淋上料。', NULL, NULL, NULL, '鸡腿肉、菠萝、酱油、酒、湿淀粉、糖、葱段、姜片、柿子椒一个。');
INSERT INTO `recipe` VALUES (1778, 77, '丁香烤羊腿', '1.羊腿洗净，用竹扦在两面戳10个孔，放入精盐、味精、胡椒粉、葱姜揉透，腌渍1小时。 \r\n2.将羊腿放入烤盘，丁香镶入肉孔内，再放入花生油和清水共250克，置烤盘于烤箱内，用温水烤至汤干，肉熟呈金黄色，取出刷上芝麻油，全腿上桌。亮相之后，切片装盘，在盘两端用黄瓜片点缀，跟烧饵、面酱碟、葱白段碟上桌。', '此菜色泽金黄，异香扑鼻，酥烂软嫩。用烧饵夹入烤羊腿肉，既有北方风味，又有南方特色。', '烧饵是用大米煮熟为饭，取出趁热捣为泥，捏成团，擀成直径15厘米、厚0.5厘米的圆饼即为饵。把它放在栗炭火上烘烤起泡取下，抹上芝麻花生酱、辣椒油、甜酱油即食。亦可夹上烤羊肉或卤牛肉、油条，则风味更佳。', NULL, '山羊后腿1只3000克，姜末30克，甜面酱20克，黄瓜片50克，葱白段80克，烧饵12个，胡椒5克，盐20克，丁香10颗，味精10克，花生油200克，葱末30克，芝麻油20克。');
INSERT INTO `recipe` VALUES (1779, 77, '锅贴乳饼', '把饼切成长方块，鸡肉、肥膘捶成茸泥后打上劲，加肥膘泥、鸡蛋清4个打匀，加葱姜汁、味精、淀粉30克拌匀，分2－3次放入鸡清汤，打至发亮成鸡泥。', NULL, NULL, NULL, '乳饼300克、鸡肉200克、猪肥膘、火腿各100克、黄瓜10克。');
INSERT INTO `recipe` VALUES (1780, 77, '过桥米线', '1.将鸡鸭去内脏洗净，同洗净的猪骨一起入开水锅中略焯，去除血污，然后入锅，加水2000克，焖烧3小时左右，至汤呈乳白色时，捞出鸡鸭（鸡鸭不宜煮得过烂，另作别用），取汤待用。 \r\n2.将生鸡脯肉、猪脊肉分别切成薄至透明的片放在盘中。乌鱼（或鱿鱼）肉切成薄片，用沸水稍煮后取出装盘。豆腐皮用冷水浸软切成丝，在沸水中烫2分钟后，漂在冷水中待用。韭菜洗净，用沸水烫熟，取出改刀待用）葱头、芫荽用水洗净，切成0.5厘米长的小段，分别盛在小盘中。 \r\n3.稻米经浸泡、磨成细粉、蒸熟，压成粉丝。再用沸水烫二三分钟成形，最后', '汤烫味美，肉片鲜嫩，口味清香，别具风味。', NULL, NULL, '光肥母鸡半只（约750克），光老鸭半只（约750克），猪筒子骨3根，猪脊肉、嫩鸡脯肉、乌鱼（黑鱼）肉或水发鱿鱼各50克，豆腐皮1张，韭莱25克，葱头10克，味精1克，芝麻油5克，猪油或鸡鸭油50克，芝麻辣椒油25克，精盐1.5克，优质稻米400克，胡椒粉、芫荽、葱花各少许。');
INSERT INTO `recipe` VALUES (1781, 77, '煎紫米藕夹', '1.紫米蒸熟为饭。藕洗净刮去皮煮熟，切为3毫米厚的片。鸡茸入碗，加入盐、味精、鸡蛋清1个、湿淀粉300克，搅拌均匀，加入紫米饭拌匀。用小刀均匀地将紫米饭涂抹在藕片上，合二为一，成为藕夹。把鸡蛋清2个与湿淀粉30克混合为糊。 \r\n2.炒锅上旺火，烧热，注入花生油，烧至七成热，将藕夹沾蛋糊后下锅煎至两面金黄出锅。 \r\n3.荷叶洗净，焯水入盘，将藕夹复原为全藕状放在上面，跟椒盐碟上桌。', '紫米是云南特产，分紫粳、紫糯两种。紫米颗粒均匀，颜色紫黑，食味香甜，甜而不腻，民间视为补品。而藕，生食能清热生津，凉血止血，熟食能补益脾胃，止泻，益血，生肌。煎紫米藕尖，是用紫米夹入藕中，煎制而成。香糯可口，酥脆宜人，是云南回族的待客佳肴。', NULL, NULL, '鲜藕500克，味精2克，紫糯100克，椒盐4克，鸡茸50克，鸡蛋清3个，鲜荷叶1张，湿淀粉60克，精盐80克，花生油100克。');
INSERT INTO `recipe` VALUES (1782, 77, '节瓜茸羮', '1.节瓜洗净，去皮，磨茸。草菇洗净切粒。虾仁去肠洗净。葱切粒。\r\n2.上汤、节瓜茸、草菇、虾仁、蟹肉、姜片同放深碗内，盖上盖子，高火煮5分锺。\r\n3.加入调味料搅拌後，高火再煮3分锺。\r\n4.取出搅入蛋白，撒上葱粒，便可供食。', NULL, NULL, NULL, '节瓜600克（约1斤），草菇50克（约1两半），虾仁100克（约2两半），蟹肉75克（约2两），蛋白1只，上汤400毫升，姜1片，葱1条，盐1茶匙，糖1/4茶匙，粟粉、酒各1茶匙，水1汤匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (1783, 65, '东坡肘子', '猪肘刮洗干净，顺骨缝划一刀，放入汤锅煮透，捞出剔去肘骨，放入垫有猪骨的砂锅内，下入煮肉原汤，一次加足，放葱节、姜、绍酒在旺火上烧开；雪豆洗净，下入开沸的砂锅中盖严，移微火上煨炖约3小时，直至用筷轻轻一戳肉皮即烂为止，吃时放川盐，连汤带豆舀入碗中上席，蘸以酱油味汁食之。', '汤汁乳白，雪豆粉白，猪肘烂软适口，原汁原味，香气四溢。', NULL, NULL, '猪肘子，雪山大豆，葱节，绍酒，姜，川盐。');
INSERT INTO `recipe` VALUES (1784, 77, '酿雪梨', '1.选大小一样的雪梨，削去皮，从梨把下切一片为盖子，挖去梨核，梨盖还原插上竹签，用清水漂着。 \r\n2.冰糖碾成末。大枣去核切碎。桂元、火腿、柿饼切丁。松子去膜。白菜去筋，切丝。莲子去皮、去心，煮熟。糯米洗净，蒸成饭。将以上配料和调料入盆，上笼蒸熟，取出加洗沙拌匀成馅。 \r\n3.炒锅上火，注入猪油，烧至五成热，下梨炸至色黄，取出沥净油。取下梨盖，装入馅，盖上盖，再插上竹签，上笼蒸透，取出梨，装入盘内。 \r\n4.锅上火，将蒸梨的原汁滗入锅内，下白糖、水，至白糖溶化，用湿淀粉勾芡，芡汁倒在梨上即成。', '酿雪梨，是白族人民的传统甜菜。此菜雪白晶莹，细线路鲜甜，梨香浓郁。雪梨独产洱海之滨，洁白似雪，滑糯蜜香，用于做菜胜过鸭梨。', '雪梨去皮、心后，用清水漂，可避免变色。蒸雪梨，大火气足，约蒸1小时左右。', NULL, '雪梨10个1500克，火腿80克，糯米50克，卷心白菜80克，松子仁50克，白糖100克，莲子80克，冰糖100克，大枣60克，湿淀粉40克，桂元肉60克，熟猪油1000克，洗沙60克(约耗50克），柿饼60克。');
INSERT INTO `recipe` VALUES (1785, 77, '汽锅鸡', '1.母鸡切成3厘米见方块，鸡切片，整葱切两段，用刀背拍松； \r\n2.鸡块入锅氽过洗净，装入汽锅内，将出水的汤烧开，将漂洗鸡块余血水倒入，清除汤中的浮物，加精盐、味精后灌入汽锅内淹过鸡块； \r\n3.放入葱节、姜片，加盖，汽锅置事先已加水的蒸锅，盖口交接处用布或白棉纸封严，再将面粉少许用清水调成面糊，糊在棉纸上，勿漏气。 \r\n4.在文火上约蒸四、五小时，拣去姜片、葱段、放入胡椒面、味精即可。', '以肥嫩母鸡为主料，用特制的汽锅蒸制而成，故得此名。此菜制法独特，鲜美异常，并可作为家庭便餐，广受欢迎。', NULL, NULL, '肥母鸡2000克，精盐8克，姜20克，葱15克，味精1克，胡椒面2克。');
INSERT INTO `recipe` VALUES (1786, 77, '沙爹鲜鱿', '1.鲜鱿洗净，在肚的部位划花。\r\n2.水、姜片及葱同放碗内，用保鲜纸盖着，留一开口处疏气，高火煮4分锺。\r\n3.加入鲜鱿，高火煮1分锺，取出，沥清水分。 \r\n4.鲜鱿用调味料腌15分锺，用保鲜纸盖著，留一开口处疏气，高火煮2分锺，便可供食。', NULL, NULL, NULL, '鲜鱿300克（约半斤），葱1条，姜2片，水300毫升，蒜盐、糖、麻油各1茶匙，胡椒粉1/4茶匙，沙爹酱1茶匙，酒1茶匙。');
INSERT INTO `recipe` VALUES (1787, 77, '砂锅焖狗肉', '1.将狗肉切成4厘米见方块，用清水漂洗两次，控干水分。薄荷洗净，摘取嫩尖。 2.炒锅上火，注入菜籽油，烧至七成热，下草果、大料、花椒、白糖、盐、干辣椒段、葱、姜、酱油，下狗肉煸炒20分钟，注入肉汤，烧开后倒入砂锅。将砂锅上火，把白酒分两次加入，改用小火，焖约1小时，汤汁收稠时，放上薄荷，连同砂锅上桌。', '砂锅焖狗肉，是云南文山苗族的风味名菜。此菜色泽金红，香味扑鼻，汁浓味醇，酥烂醇鲜。', '狗肉用清水拔去血污，然后漂洗干净，熟后膻味大减，肉香四溢。砂锅炉焖狗肉，大火烧开，小火慢，以狗肉酥烂为度。薄荷后下，一烫即可，若经煮沸，香味尽失。', NULL, '狗后腿肉500克，薄荷100克，白糖10克，干辣椒25克，白酒10克，葱10克，姜10克，大料6克，草果10克，花椒粒2克，精盐20克，菜籽油120克，酱油20克，肉汤150克。');
INSERT INTO `recipe` VALUES (1788, 77, '烧云腿', '1.云腿刮洗干净，用温碱水洗去污物和哈喇味，再用清水洗两遍，放入汤锅中加水煮1小时，至熟而不烊，捞起撕去皮子。 \r\n2.鸡蛋磕入碗内打散，先放入富强粉调匀，再放入蚕豆水粉搅成稠蛋清糊。 \r\n3.火盆内燃粟炭火。云腿穿在铁叉上，肥肉面向火，烤热时用毛刷蘸蛋清糊刷在云腿上面，待火苗将蛋清糊烤黄后，再刷第二遍蛋清糊烤黄，接着再刷第三遍蛋清糊，待烤黄后，即用刀将云腿连同蛋糊片为2.5厘米厚的片。然后再将云腿如前述方法刷三遍蛋清糊，每遍都要在刷后烤黄，然后片下，如此反复，至云腿和蛋糊用尽为止。将片下的云腿片改刀为4', '色泽淡黄，皮酥肉嫩，肥而不腻，人口即化，鲜香浓郁。', NULL, NULL, '云腿尖1500克，鸡蛋6个，富强粉75克，蚕豆水粉250克，食碱少许。');
INSERT INTO `recipe` VALUES (1789, 77, '炸洋葱', '1.菠菜切丝炸成菜松，将洋葱切片后拖蛋糊，再粘面包渣。 \r\n2.逐个下入5成热油中，炸至金黄色装盘，用菠菜松、香菜、樱桃围边即成。', NULL, NULL, NULL, '洋葱400克、菠菜叶80克、樱桃10粒、鸡蛋2个、面粉80克、面包渣100克。');
INSERT INTO `recipe` VALUES (1790, 78, '白羊肾羹', '1.将白羊肾、羊脂洗净；将肉苁蓉、陈皮、荜茇、草果、胡椒装入纱布袋内，扎住口后，与白羊肾、羊脂一同放入铝锅内，加水适量。 \r\n2.将铝锅置灶上，用武火烧沸，文火炖熬，待羊肾熟透时，放入葱、姜、酵母面、酱油，如常法制羹。', '壮肾，暖脾胃。适用于肾虚阳痿、腰膝无力、脾虚食少、胃寒腹痛等症。', NULL, NULL, '肉苁蓉50克、荜茇10克、青果10克、陈皮5克、胡椒10克、白羊肾2对、羊脂200克、食盐、葱、酱油、酵母面、姜各适量。');
INSERT INTO `recipe` VALUES (1791, 78, '白汁全鱼', '将鱼剖洗干净，劈成雌雄两片，斩去鱼牙。将火腿、鸡肉、蛋糕、香菇等均切成指甲丁待用。锅内放清水1000克左右，旺火烧沸，将鱼片放入，先放雄片，再放雌片，鱼头对齐，鱼皮朝上，使胸鳍翅起，放入葱结、姜片，盖上锅盖，沸时起盖，撇去浮沫，转动锅，继续用旺火烧煮3分钟左右即熟。去掉葱、姜，将鱼捞出，沥去水，装入长腰盘中。再另用锅一只，烧热下猪油，将葱段放入煸出香味，加绍酒、放入氽鱼原汤200克，捞去葱，将火腿、鸡丁、蛋糕、香菇、碗豆等佐料倒入锅中，加精盐、味精，用湿淀粉调稀勾薄芡，淋上鸡油，浇在鱼身上即成，上桌时带姜', '汁白明亮，色泽清丽，鲜咸嫩滑，有蟹肉滋味。', NULL, NULL, '草鱼一条（重约750克），熟鸡脯肉25克，水发香菇15克，熟火腿25克，蛋黄糕25克，熟豌豆15克，姜片5克，葱结1个，葱段2.5克，精盐2.5克，味精1.5克，胡椒粉1克，湿淀粉10克，熟猪油15克，熟鸡油10克，姜末醋一小碟。');
INSERT INTO `recipe` VALUES (1792, 78, '白煮肉', '去骨的猪五花肉（或通脊肉）横割成三、四条，每条宽约13厘米，再切成20厘米长的块，刮洗净，肉皮朝上放入锅内，倒入清水（以淹没肉并高出10厘米为度），盖上锅盖，用旺火烧开，再改用微火煮（要保持微沸状态，中途不得添水）煮2小时左右。', '汁浓味厚，入口滚烫。', NULL, NULL, '猪五花肉（或通脊肉）、酱油、蒜泥、腌韭菜花、酱豆腐汁、辣椒油。');
INSERT INTO `recipe` VALUES (1793, 78, '白字焖肉', '将猪五花肋肉皮刮洗干净，切成大小相同的10块，入沸水中汆过。取小砂锅一只，用葱白、姜片垫底把肉块放在砂锅里，放进各种调料和白汤，加盖焖烧30分钟，然后另取一小碗，将肉平放碗内盖上平盖，上笼蒸1.5小时，食用时，盖上盖滗出原汁勾芡，用菠菜围边即成。', '香醇浓郁，汤汁稠和，肉质酥烂。', NULL, NULL, '猪五花肋肉400克、菠菜150克、精盐5克、白糖15克、白字酒50克、红槽汁50克、酱油40克，湿淀粉25克、葱白50克、姜片5克。');
INSERT INTO `recipe` VALUES (1794, 78, '本土炸酱鸡', '1.首先将鸡洗净切片，加入1汤匙生粉和1汤匙油，捞匀。压碎葱头仔，蒜米和姜。 \r\n2.番茄切粒香茅拍扁，切段。 \r\n3.烧热3汤匙油在锅中，爆香葱头仔，蒜米，加入番茄，香茅和羌花，最后加入调味料，煮1分钟，倒进鸡块，捞匀煮5至8分钟，即可洒上小辣椒，放在生菜上。', '原味醇香，滋味鲜美。', NULL, NULL, '鸡1只、番茄2粒、葱头仔2汤匙、蒜米1汤匙、羌花1支、香茅2支、生菜几片、小辣椒12条、酱2汤匙半、海鲜酱2汤匙半、辣豆瓜酱2汤匙半、油4汤匙、生粉1汤匙、水2汤匙、胡椒粉1茶匙。');
INSERT INTO `recipe` VALUES (1795, 78, '薄片火腿', '1.火腿先用碱水洗去污物，清水过清，放在锅里，皮朝上加水（以淹没火腿为度）中火煮一个半小时，至千斤骨与筒骨脱开时捞出晾十分钟左右。然后片去表面污肥边肉，除去筋和骨，翻过来（皮朝上）平放在盘内，用重物压实，使肉质粘实勿松。\r\n2.食用时，将压过的火腿切去雄片，四边修齐后，切成宽5厘米、厚2厘米（瘦肉1.5厘米厚，肥膘0.5厘米厚）的块，再片成长5厘米、厚0.1厘米的薄片48片。取用圆盘一只，先取8片火腿和修下来的碎火腿片放入垫底，再取16片贴放在两边，另24片用刀口托放在上面，砌成拱桥形，桥洞两侧放上洗净的', '色泽红润似火，片薄油亮，香味浓郁，咸鲜适口，富有回味。据《本草纲目》记载，火腿具有益肾，生津，壮腰，固骨髓，健足力，愈伤口等功效。', NULL, NULL, '金华火腿一只，香菜叶少许。');
INSERT INTO `recipe` VALUES (1796, 78, '彩色肉丝', '里脊肉丝加盐、酒、鸡蛋清及淀粉上浆，青红椒切丝。油锅烧至四成热，先下肉丝，后下椒丝滑熟沥出。原锅留油少许，下葱段炒香捞去，放进肉丝加调料勾芡炒匀出锅装盘即成。 \r\n特点：刀工讲究，丝丝均匀，色呈彩色，味鲜滑嫩。', NULL, NULL, NULL, '猪里脊肉400克，青红椒50克，鸡蛋清2只，葱段10克，精盐3克，绍酒15克，味精2克，湿淀粉40克，熟猪油1000克（实耗50克）。');
INSERT INTO `recipe` VALUES (1797, 78, '菜远卤牛肉', '1.在沸水中加入1汤匙油，放入蔬菜灼熟，沥干，上碟。\r\n2.用2汤匙油爆香蒜蓉，加入牛肉炒至半熟，拌入芡汁煮热透，淋在蔬菜上即成。', '色泽呈黄色，有绿色相衬，酸甜带咸。', NULL, NULL, '牛肉150克(切片)，芥兰/绿色蔬菜300克(切段)，蒜蓉1小勺。芡汁：李锦记卤水汁2汤匙，水4汤匙，生粉3/2茶匙，糖/1汤匙，李锦记芝麻油1茶匙。');
INSERT INTO `recipe` VALUES (1798, 78, '潮州烧雁鹅', '1.先将光鹅洗干净晾干后，放入卤水中浸熟。\r\n2.取出来冻上后起肉，用湿淀粉60克糊在鹅皮上，将鹅骨用刀剁成件，上湿淀粉40克。\r\n3.炒勺上旺火，倒入油烧开，将已上粉的鹅肉、骨一同炸成红白色，骨身硬后取出沥油。\r\n4.骨装盘垫底，而后用斜刀把鹅肉切件盖在骨上，再把大油、味精、胡椒粉拌均匀，淋在鹅肉上，芫荽放在鹅边即成。另跟酸菜150克(酸姜或青瓜)、汕头梅膏酱100克。', '色泽红白兼艳，骨质酥脆味浓，肉鲜香嫩可口，宴客别具风味。', NULL, NULL, '光鹅1只(重约2公斤为宜)。卤水用料：生抽500克，老抽100克，清水200克，料酒75克，汕头南姜100克，川椒5克，桂皮5克，大料5克。调料：植物油800克(实耗约70克)，芫荽少许，胡椒粉少许，大油25克，湿淀粉100克，味精5克。');
INSERT INTO `recipe` VALUES (1799, 78, '炒里脊丝', '肉丝用蛋清、精盐1.5克、湿淀粉20克上浆拌匀。炒锅置中火下油烧至三成热（约66度）时，把里脊丝入锅中划开，呈白玉色时倒入漏勺沥油。锅内留底油15克，放葱姜煸至出香味，再将笋丝入锅，加入绍酒、白酒、味精和精盐1克，用湿淀粉5克勾芡，放入里脊丝炒匀，淋上猪油15克出锅即成。', '肉丝洁白鲜嫩，笋丝鲜嫩清香，滑爽利口。', NULL, NULL, '猪里脊丝250克，熟笋丝25克，味精2克，绍酒10克，葱段5克，精盐2.5克，姜0.5克，湿淀粉25克，蛋清1个（重约25克），熟猪油500克（实耗油75克），白汤25克。');
INSERT INTO `recipe` VALUES (1800, 78, '炒米粉', '1.将调味料1用果汁机打成泥状（可加入少许色拉油）。油豆腐切片备用。 \r\n2.将锅烧热，加入少许油，将调味料1炒香，再将调味料2加入，转中火，要一直搅拌汤料，避免烧焦。煮沸后放入草虾、油豆腐片、鱼丸煮熟，再加入米粉同煮，熟后盛入碗中。 \r\n3.将豆芽菜烫至熟，放入碗中，将1/4个水煮蛋放入，最后撒上红葱头酥、九层塔及辣椒片即完成。', '质地软嫩，味道鲜美。', NULL, NULL, '草虾3尾，油豆腐2个，鱼丸20克，豆芽菜20克，水煮蛋1/4个，米粉100克，红葱头酥少许，九层塔叶2片，辣椒片3片。调味料: 1. 虾米30克，栗子3个，红葱头6个，大蒜4个，姜1小块，干香茅1/2大勺，红辣椒4根，咖喱粉2大勺，虾酱2小勺。 2. 鸡汤2杯，椰奶1/2杯，盐、胡椒粉少许。');
INSERT INTO `recipe` VALUES (1801, 78, '炒山药泥', '1.将山药刷干净，放在笼屉内，用旺火蒸熟（约二十分钟），取出晾凉后剥去外皮，用刀碾成细泥，金糕切成菱形小片或其它花样。 \r\n2.把炒勺放在旺火上，放入花生油、山药泥，加白糖炒透装盘，码上金糕片即成。', '质地细腻，味道香甜，洁白如玉的山药泥上，缀以红色金糕，美观大方。', NULL, NULL, '山药500克，金糕50克，熟花生油60克，白糖150克。');
INSERT INTO `recipe` VALUES (1802, 78, '春笋炒步鱼', '将步鱼宰杀、洗净，切去鱼嘴和鱼鳍，斩齐鱼尾，批成两片，用精盐1克、湿淀粉35克上浆待用。笋切成瓦鱼块，略小的切滚料块，将酱油、白糖、绍酒、味精和湿淀粉15克、汤水25克放入小碗中调成芡汁待用。烧锅置火上烧热，滑锅后下猪油至三成热（约66度）时倒入笋块炸约15分钟，用漏勺捞起待油温升至五成热（约110度）时，复炸20秒钟起锅倒入漏勺，锅内留油25克放入葱段煸出香味，即下鱼块和笋块，把调好的芡汁淋入锅内，轻轻颠翻炒锅，以防鱼肉散碎。待芡汁包住鱼块时，淋上芝麻油即可。吃时根据食者爱好可加适量胡椒粉。', '鱼嫩味鲜，笋脆爽口，色泽油亮，咸鲜馥郁。', NULL, NULL, '鲜活步鱼一条（约500克），嫩春笋肉100克，酱油20克，绍酒10克，精盐1克，葱段10克，白糖5克，熟猪油500克，湿淀粉50克，芝麻油5克，味精25克，胡椒粉1克。');
INSERT INTO `recipe` VALUES (1803, 78, '葱扒鸭子', '将鸭宰杀，退毛，洗净，在尾背上部横切一刀，取出内脏，控去鸭臊，用清水冲净，鸭背部用刀尖直划一刀（长约6厘米），入沸水锅氽约3分钟，取出洗净血污。京葱洗净，对剖开切成5厘米长的段待用。炒锅置旺火上烧热，下猪油，把鸭先放入温水锅烫热，揩干水，用酱油15克涂抹鸭的全身，待油温八成热（约200度）时，把鸭子入锅炸至金黄色，用漏勺捞出。再取炒锅一只，放入鸭子，加绍酒、白糖，葱结、姜片、酱油60克、水1000克，用旺火煮沸后，将锅移至小火上炆，待鸭酥后捞出，肚朝上放在大腰盘中，拣去锅中葱、姜，原汁盛起待用。锅内下猪油', '鸭形完整，原汁原味，色泽红亮，鸭肉香酥，葱香微甜。', NULL, NULL, '嫩鸭一只（重约2000克），京葱150克，葱结1个，姜片5克，绍酒100克，酱油75克，湿淀粉50克，白糖35克，芝麻油10克，味精2.5克，熟猪油1500克（约耗90克）。');
INSERT INTO `recipe` VALUES (1804, 78, '葱油核桃鱼卷', '将草鱼肉片成连刀蝴蝶片20片，拍干粉备用。把椒盐核桃仁，生猪板油切成粒。葱切成末置于碗内，加入味精、盐拌匀成馅后，挤成20个丸子，然后逐个放在鱼片上，紧卷成蚕茧形鱼卷，放在盘中，待用。把鸡蛋磕入碗内打散，加干淀粉调成薄糊，放入鱼卷挂匀，再逐个滚上面包渣，入热油中逐个炸，呈深黄色时捞出，装盘，带花椒盐，上席即成。', '鱼卷外壳爽脆，色泽金黄，馅料酥香油润。', NULL, NULL, '净草鱼背脊肉250克、生猪板油75克、椒盐核桃仁125克、鸡蛋一个（重约50克）、精盐5克、味精1克、绍酒10克、面包渣20克、干淀粉20克、花椒盐1碟、熟菜油1000克（实耗油70克）、葱50克。');
INSERT INTO `recipe` VALUES (1805, 78, '葱油黄鱼', '将鱼宰杀，洗净，在鱼身两侧肉厚处，每隔3厘米剖一刀。将炒锅置旺火上，舀入清水烧沸，放入鱼浸没，加葱结、姜片，绍酒煮沸后，盖上锅盖改用微火。保持微沸。鱼嫩熟时，捞起装盘。把姜丝、精盐、绍酒、酱油、白糖、胡椒粉、味精及煮鱼原汤100克放在小碗中调匀，浇在鱼身上，撒上葱丝。炒锅中下入花生油，旺火烧至九成热（约225度），淋浇在葱丝上即成。', '鱼肉鲜嫩，葱香浓郁。', NULL, NULL, '大黄鱼一条750克，精盐1.5克，味精4克，绍酒30克，花生油80克，胡椒粉2克，白糖5克，酱油20克，姜片20克，葱丝40克，姜丝10克，葱结30克。');
INSERT INTO `recipe` VALUES (1806, 78, '醋椒鱼', '1.将活鱼宰杀，去鳞、鳃和内脏，洗净，剞上柳叶刀，然后用开水氽一下，去净血水。\r\n2.锅上火，放猪油、高汤、料酒、姜汁、盐，烧开后放入氽过的鱼，煮过5分钟左右，加味精、白醋、胡椒粉，随后把鱼捞出放入汤碗，上面放葱丝、香菜。\r\n3.把鱼汤过罗，倒入汤碗中即成。', '鱼鲜嫩，汤清香，味酸辣，能解腻，适合家庭聚餐作最后一道汤菜用。用草鱼、鳜鱼做原料，味道同样鲜美。', NULL, NULL, '活鲤鱼1条（约500-750克），料酒10克，味精2克，盐5克，葱丝、姜汁、胡椒粉、白醋、猪油、香菜各适量，高汤500克。');
INSERT INTO `recipe` VALUES (1807, 78, '脆瓜里脊丝', '猪里脊肉切丝用蛋清、精盐、湿淀粉搅匀上劲，黄瓜、笋肉切丝。炒锅置中火上烧热，下猪油烧至三成热（约66度）时，将肉丝入锅划开，呈白色时捞出沥油。原锅留油15克。放入葱煸出香味，下笋丝、肉丝略颠炒，将里脊丝入锅炒匀。加绍酒、清汤、味精、淋猪油15克，起锅。即成。', '瓜爽脆，肉鲜嫩，绿白相间，色彩淡雅。', NULL, NULL, '猪里脊肉250克、黄瓜25克、蛋清1个、熟笋肉25克、味精2克、绍酒10克、葱段0.5克、清汤50克、熟猪油500克（约耗75克）、淀粉20克、精盐2克。');
INSERT INTO `recipe` VALUES (1808, 78, '大枣冬菇汤', '先将干冬菇洗净泥沙；红枣洗净，去核；然后将清水、冬菇、红枣、食盐、味精、料酒、姜片、熟花生油少许，一起放入蒸碗内，盖严，上笼蒸60～90分钟，出笼即成。', '益气、开胃。适用于治疗各种虚症、食少、高血压、冠心病、癌症及胃、十二指肠溃疡等病症。', NULL, NULL, '红枣15枚，干冬菇15个，生姜、熟花生油、料酒、食盐、味精各适量。');
INSERT INTO `recipe` VALUES (1809, 78, '蛋松', '1.鸡蛋中加入盐、黄酒、味精，打匀。\r\n2.锅中加油烧至4成热（油多些），手握细眼筛子对准油锅，四面均匀的淋入打匀的蛋液，使之逐渐淋入油中受热成丝并浮起时，用筷把它翻过来，略松一下捞出，放在小箩中尽量压干油份。 \r\n3.用干净的包装纸放入压干的蛋丝，卷起，轻轻的推搓，纸潮即换纸，反复3-4次，使蛋丝成为干而膨松的蛋松即可。', '营养丰富，适合做儿童食品。', NULL, NULL, '鸡蛋5只，精盐1.5克，黄酒5克，味精1.5克。');
INSERT INTO `recipe` VALUES (1810, 78, '东坡肉', '1.将猪五花肋肉刮洗干净，切成10块正方形的肉块，放在沸水锅内煮5分钟，取出洗净。 \r\n2.取大砂锅一只，用竹箅子垫底，先铺上葱，放入姜块，再将猪肉皮面朝下整齐地排在上面，加入白糖、酱油、绍酒。', NULL, NULL, NULL, '猪五花肋肉1500克，葱100克，白糖100克，绍酒250克，姜块（拍松）50克，酱油150克。');
INSERT INTO `recipe` VALUES (1811, 78, '冬虫夏草烩番茄', '1.将豌豆、东虫夏草汆烫捞起备用。\r\n2.将番茄削皮取其果肉。\r\n3.于锅中加入清水一碗，放入豌豆、东虫夏草、番茄果肉及辣椒，小火煮沸。\r\n4.加入蔬菜调味即可。', '养生蔬菜，多吃番茄能提高免疫力。', NULL, NULL, '豌豆30克，生鲜冬虫夏草30克，番茄1个(大),辣椒少许(4条),蔬菜调味粉5克。');
INSERT INTO `recipe` VALUES (1812, 78, '冬瓜盅', '1.将鸡腿肉洗净切成丁，摘净虾仁的沙肠，洗净待用，将香菇和冬笋切成小丁。\r\n2.将鸡肉丁和虾仁加少许盐和淀粉拌匀，入开水锅中焯1分钟，捞出沥干水分待用。 \r\n3.将冬瓜洗净，从带蒂的一头下刀，将冬瓜切成一大一小的两部分，小的带蒂的这头自然是冬瓜盅的盖子，另一部分就是冬瓜盅的雏形了，将瓜瓤用勺子掏了去，洗净了就有了冬瓜盅。 \r\n4.炒锅中加入5汤匙油，烧热后倒入焯好的鸡丁和虾仁，略为翻炒后下香菇丁和冬笋丁，翻炒均匀后烹入少许绍酒，加入半杯水和1茶匙鸡粉，煮开后盛入冬瓜盅内，放入火腿片。\r\n5.将冬瓜盅放入高', '汁浓味鲜，瓜嫩爽滑。', NULL, NULL, '新鲜的小冬瓜1个，水发香菇若干，冬笋肉50克，鸡腿肉（猪肉也可）50克，鲜虾仁80克，宣威火腿2小片（金华的也可），盐、绍酒、胡椒粉少许，鸡粉1茶匙，淀粉适量。');
INSERT INTO `recipe` VALUES (1813, 78, '冬笋雪菜黄鱼汤', '1.先将黄鱼去鳞，除内脏，洗净，冬笋发好，切片，把雪菜洗净，切碎；猪肉洗净，切片备用。\r\n2.将花生油下锅烧热，放入鱼两面各煎片刻；然后锅中加入清汤，放入冬笋、雪菜、肉片、黄鱼和佐料，先用武火烧开，后改用文火烧15分钟，再改用武火烧开，拣去葱、姜，撒上味精、胡椒面，淋上香油即成。', NULL, NULL, NULL, '冬笋、雪菜、肥肉各30克，黄鱼1条（约500克)，葱、姜、花生油、香油、清汤、料酒、胡椒面、食盐、味精各适量。');
INSERT INTO `recipe` VALUES (1814, 78, '番茄腰柳', '1.将猪里脊肉横切成四段，各段正面均切成斜刀口，切口相距0.5厘米许，深度为肉厚一半即可，背面切直刀口，距离同正面。然后沾上一层面粉。 \r\n2.把黄瓜、玉兰片、火腿分别切成小丁。在番茄酱内加入鸡汤、料酒、醋、白糖、盐、味精、湿淀粉和清水，调和成芡汁。\r\n3.鸡蛋打入炒勺内，放文火上烧至快冒烟时，放入里脊肉炸3分钟左右。捞出后切成片，整齐地码入盘中。\r\n4.把炒勺放旺火上烧热后，倒入熟大油，热后放入黄瓜、玉兰片、火腿丁，翻炒几下，倒入芡汁，待熟后，倒在盘中码好的里脊肉片上即可。', '汁显红色，小丁各色争艳，十分美观夺目，肉质软嫩，味道酸甜。', NULL, NULL, '猪里脊肉400克，水发玉兰片30克，熟火腿30克，黄瓜30克，鸡蛋2个，番茄酱200克，熟大油70克，植物油1公斤(实耗约70克)，面粉40克，白糖60克，湿淀粉15克，鸡汤30克，醋15克，料酒20克，味精少许。');
INSERT INTO `recipe` VALUES (1815, 78, '丰收龙舟', '将桂鱼去鳞、腮洗净，从背部剖开，剔除脊骨，取出内脏，洗净盛入盆里，加精盐3克、绍酒50克、味精1克、葱姜丝腌渍15分钟。肥膘肉批成0.2厘米厚、7厘米长、3.5厘米宽的片10张，拍上少许干淀粉、将鱼茸分成十份，放在肥膘肉上，卷成“蚕茧”状待用。取鸡蛋二个磕入碗内，加精盐1克打散，取一半在净锅内摊成黄色蛋皮一张，另一半加入少许红曲粉。摊成红色蛋皮一张。黄蛋皮刻成“麦穗”四支，红蛋皮切成长22厘米、宽2.5厘米的彩带二条和直径5厘米的菱形片1片。黄蛋皮切细条拼成“丰”字，贴在菱形片上。取大锅一只，置旺火上，加', '鱼身深黄，肉质松脆，滋味鲜香。蛋、茧软滑，鲜咸适口，造型别致。', NULL, NULL, '桂鱼一条重1500克，鸡蛋200克，鱼茸125克，猪肥膘肉100克，精盐5克，味精3克，葱、姜丝各15克，绍酒50克，番茄沙司50克，红曲粉0.5克，干淀粉400克，熟猪油500克（实耗油50克），熟菜油5000克（实耗油150克）。');
INSERT INTO `recipe` VALUES (1816, 78, '奉化芋头', '芋头去皮，切花瓣形，上笼蒸至酥软装盘。海参、香菇、火腿、鸡肉、胡萝卜切丁，加青豆下锅中加清汤烧开，调好味后勾芡淋在芋头上即成。', NULL, NULL, NULL, '芋头650克、水发海参25克、水发香菇25克、火腿25克、熟鸡脯肉25克、青豆25克、胡萝卜5克。');
INSERT INTO `recipe` VALUES (1817, 78, '干菜焖肉', '1.先将肋条肉切成2厘米见方的小块，放在沸水中氽一分钟，去掉血水，用清水洗净。干菜切成1厘米长。\r\n2.炒锅内放清水250克，加酱油后，放入肉块旺火煮十分钟，再放糖、干菜煮五分钟，加入味精，旺火收紧卤汁后取出。\r\n3.取扣碗一只，先放10克煮过的干菜垫底，然后将小方肉（皮朝下）排放在干菜上，再将剩下的干菜盖上肉块上，烹入酒，上笼旺火蒸二小时左右，至肉酥糯时便扣入盘中即成。', '干菜色呈乌黑，鲜嫩清香，略带甜味，肉色红亮，越蒸越糯，富有粘汁，肥而不腻，是绍兴地区人们颇为喜欢的一道菜肴。', NULL, NULL, '带皮猪肋肉500克、干芥菜60克、绍酒5克、酱油25克、白糖40克、味精1.5克。');
INSERT INTO `recipe` VALUES (1818, 78, '高丽鱼条', '将鱼肉批片，切成长4厘米、厚0.8厘米的条，加精盐、味精、料酒、胡椒粉、姜汁水拌渍。蛋清打起泡待用。炒锅置中火上，下入猪油，在此同时将湿淀粉与干淀粉逐步加入蛋泡中搅成蛋泡糊，待油温至四成热（约88度）时，将鱼条拖上糊，逐条下油锅炸至鱼条硬，并保持温度平稳，用手勺不断翻动，浸炸至鱼条呈奶黄色至熟，捞出淋去油，装盘即成。食用时可蘸食甜面酱和花椒盐。', '鱼条奶黄，外壳松酥，肉质鲜嫩，酥脆焦香。', NULL, NULL, '净草鱼肉150克，鸡蛋清125克，精盐2.5克，味精1.5克，料酒25克，姜汁水25克，胡椒粉5克，干淀粉25克，湿淀粉25克，甜面酱30克，椒盐10克。');
INSERT INTO `recipe` VALUES (1819, 78, '蛤蜊黄鱼羹', '1.用清水1000克加盐10克搅匀，把洗净的蛤蜊放入盐水中养二小时左右，使它吐净泥沙，取出，再用清水洗净。炒锅内放水1000克，烧沸后把蛤蜊倒入，用炒勺推拌一下，待蛤蜊壳略张开时，即用漏勺捞出，剥壳取肉。\r\n2.将黄鱼肉切成丁。鸡蛋打成蛋液。\r\n3.炒锅在旺火上烧热，放入熟猪油50克，烧至五成热时，将葱末10克下锅爆至有香味，再下鱼丁煸一下，随即放入酒，盐（5克）和肉汤。汤沸后、撇去浮沫，加入味精，用湿淀粉调稀勾芡，放入蛤蜊肉，用炒勺轻轻搅一下，再均匀地倒入鸡蛋液，淋上熟猪油50克，用炒勺轻轻推一下，出锅', '此菜质地鲜嫩，汤汁稠浓，口味鲜美，色泽美观。', NULL, NULL, '蛤蜊500克、黄鱼肉250克、熟火腿末10克、鸡蛋1个、葱末25克、猪肉汤400克、绍酒25克、精盐15克、味精2.5克、醋15克、湿淀粉75克、熟猪油100克。');
INSERT INTO `recipe` VALUES (1820, 78, '蛤蜊鲫鱼', '1.将净鲫鱼在背肉上剞几刀。下清水锅中加调料和葱结、姜片、笋片烧开、再改用小火煮熟。 \r\n2.将蛤蜊下清水锅中烧开，烹入绍酒，待其外壳张开后捞出置汤盆中。 \r\n3.将煮蛤蜊的汤倒入鲫鱼汤内，加入香菇、熟火腿片、绿叶菜和调料烧开，注入蛤蜊汤盆内，放入煮好的鲫鱼，其上排放笋片、熟火腿片和香菇即成。', '滋味鲜美，原味醇香。', NULL, NULL, '活鲫鱼1尾约6两，活蛤蜊6两，熟火腿、笋肉、水发香菇、绿叶菜等各适量，绍酒、胡椒粉、精盐等各适量。');
INSERT INTO `recipe` VALUES (1821, 65, '冬菜肉末', '1.把猪肉剁成末，冬菜去根洗净和姜均切成末，葱切葱花。\r\n2.炒勺烧热注油。油热后先放入肉末煸炒至水分殆尽，再加入葱、姜稍炒后，即下料酒、酱油、糖和味精炒匀，最后下冬菜翻炒几下即成。', '鲜香适口，下饭便菜。', NULL, NULL, '猪肉（肥与瘦肉为3比7）200克，川冬菜75克，大油75克，酱油20克，料酒13克，白糖、姜各10克，味精、葱各3克。');
INSERT INTO `recipe` VALUES (1822, 78, '枸杞肉丝', '1.将猪肉洗净去筋，切成2寸长的丝，青笋切成同样的丝，枸杞洗净。\r\n2.炒锅上火，将猪油烧热，再将肉丝、笋丝同时下锅炒散，下料酒、白糖、酱油、盐、味精搅匀，投入枸杞，翻炒几下，淋麻油即成。', '滋阴补肾。适于体弱乏力、肾虚目眩、视觉模糊等症。', NULL, NULL, '枸杞、瘦猪肉、青笋、精盐、白糖、味精、料酒、酱油、淀粉、猪油、麻油。');
INSERT INTO `recipe` VALUES (1823, 78, '罐焖核桃鸡块', '1.将鸡洗净切成20块，抹上精盐腌片刻，核桃仁用沸水泡后剥皮，葱头洗净切块与核桃仁一起放入绞肉机内绞末，备用。\r\n2.将绞末、番茄酱放入锅内加适量鸡汤拌匀煮沸，放入鸡块煮沸后用文火焖熟，加入精盐、糖调好口味装入罐内，放在炉板上用文火煮至微沸即可。', '郁香味浓。', NULL, NULL, '净鸡肉1000克，核桃仁250克，葱头125克，番茄酱100克，砂糖40克，精盐适量。');
INSERT INTO `recipe` VALUES (1824, 78, '归参山药猪腰', '1.将猪腰切开，剔去筋膜、洗净，放入锅内。\r\n2.将当归、党参、山药装入纱布袋内，扎紧口，放入同一锅内。\r\n3.在锅内加适量水，炖至猪腰熟透，捞出猪腰，冷却后切成片，放入盘内。再将酱油、醋、姜丝、蒜末、香油与猪腰片拌匀即成。', '养血、益气、补肾。适于心悸、气短、腰酸痛、失眠、盗汗等症。', NULL, NULL, '当归、党参、山药、猪腰、酱油、醋、姜丝、蒜末、香油。');
INSERT INTO `recipe` VALUES (1825, 78, '桂花鲜栗羹', '1.鲜栗肉洗净后切成薄片。炒锅置旺火上，放入清水400克，烧沸，倒入栗子肉和白糖，再沸时，撇去浮沫。\r\n2.用小火烩煮，另将干藕粉用水25克调匀，均匀地倒入锅内，调成羹状时出锅，盛入汤盆碗内。把青梅切成薄片放在羹上面，再撒上糖桂花和玫瑰花瓣即成。', '此菜红黄绿白，色彩绚丽，栗子脆嫩，羹汁稠浓，桂花芳香，清甜适口。', NULL, NULL, '鲜栗子肉100克、干藕粉25克、蜜饯青梅半颗、糖桂花（用糖腌制）1.5克、玫瑰花2瓣、白糖150克。');
INSERT INTO `recipe` VALUES (1826, 78, '锅烧鳗', '1.将河鳗剪断喉管，在肛门处剪一小口，用沸水泡去周身的粘液，然后用两只竹筷从咽喉部插入腹中，一手捏住鳗身，一手捏紧竹筷顺一个方向绞卷几下，把肠子等卷出来，斩去头、尾，切成6厘米长的段，洗净。 　　\r\n2.将炒锅置于是旺火，下入熟猪油25克，烧热，用葱段铺底，将鳗鱼段排列其上，铺盖猪板油丁、姜片、笋块、桂皮，放入绍酒和清水500克。煮沸后，改用小火焖煮1小时，至汤汁约剩1/4，鳗肉酥烂时，拣去姜片、葱段、桂皮，转入另一只炒锅中，放入酱油、白糖、醋，改用旺火烧至汤汁稠浓，淋上熟猪油25克，用手勺推匀，把锅转动', '成菜鳗鱼肉酥烂，鲜美可口。“锅烧鳗”营养价值高，对病后滋补、增强体质有一定疗效。', '“锅烧鳗”选料严格，以取用冬季背黑肚白、嘴小、头大，重约650克的河鳗为佳。最后一道工序，一定用旺火，时间不可过长，至汤汁稠浓即可。', NULL, '活河鳗2条750克，桂皮5克，猪板油丁25克，绍酒25克，笋块75块，酱油60克，葱段100克，醋5克，姜片10克，芝麻油5克，白糖25克，熟猪油50克。');
INSERT INTO `recipe` VALUES (1827, 78, '海米冬瓜', '1.将冬瓜削去外皮，去瓤、籽，洗净切成片，用少许盐腌10分钟左右，沥干水分待用，将海米用温水泡软。\r\n2.炒锅置旺火上，放油烧至六成热，倒入冬瓜片，待冬瓜皮色翠绿时捞出沥干油待用。\r\n3.炒锅留少许底油，烧热，爆香葱末、姜末，加入半杯水、鸡粉、绍酒、盐和海米，烧开后放入冬瓜片，用旺火烧开，转用小火焖烧，冬瓜熟透且入味后，下水淀粉勾芡，炒匀即可出锅。', '汁浓味鲜，瓜嫩爽滑。', NULL, NULL, '冬瓜500克，海米50克，绍酒1茶匙，水淀粉1茶匙，葱末、姜末各1茶匙，盐、鸡粉各适量。');
INSERT INTO `recipe` VALUES (1828, 78, '杭三鲜', '将肉皮切成5厘米长，1.7厘米宽的菱角片，鸡肉、猪肚笋均切成10厘米，长4厘米宽的片。将猪肉末加精盐0.5克，做成5颗肉丸子，上笼蒸熟，把鱼肉做成5颗丸子，放在冷水锅中，用小火汆熟。炒锅置旺火上烧热，下猪油25克，放葱把肉皮、鸡、肚、笋、虾和肉丸一起倒入锅中，加入绍酒、酱油、白糖、白汤和精盐1克，烧沸片刻，放入味精用湿淀粉勾芡，淋上猪油，撒上3克葱段出锅装盘，最后把鱼丸放在四周，火腿片盖在上面即成。', '肉皮柔软，丸子鲜嫩，滋味多样，清香爽滑。', NULL, NULL, '水发肉皮150克、鱼茸75克、熟鸡肉75克、熟猪肚75克、白汤150克、绍酒10克、白糖2克、精盐1.5克、味精1.5克、湿淀粉15克、熟猪油50克。');
INSERT INTO `recipe` VALUES (1829, 78, '杭式炒年糕', '1.猪肉切丝，用盐拌过，再加蛋白和太白粉浆过，30分钟。木耳泡水至发涨，摘去硬梗部份，清洗干净，切成丝。 \r\n2.白菜洗净切丝，韭黄择干净切段备用。年糕也切成0.3公分厚的片。 \r\n3.油热至80度左右，放下年糕片稍微炸一下使之定型，捞出。 \r\n4.另用油炒肉丝、木耳和大白菜，加入高汤焖至大白菜够软酥但仍有汤汁，放下年糕拌炒，加入虾油和糖炒匀，关火后再放下韭黄段拌一下即可装盘。', '年糕是糯米制成，黏性强，所以切片时不要切太厚，不易入味，但太薄则太软，没有咬劲，所以年糕要炒得好吃，年糕片的大小厚薄有很大的影响。因为要使它有较好的口感且不黏在一起，所以先在油里炸一下，不妨试试。韭黄太早下锅会变得软烂，一定要在最后下锅才能保持脆性。炒年糕加虾油颜色较漂亮，不似酱油是黑红色，且虾油的味道是越陈越有味，不似一般的味精或高鲜味精，只是入口时感觉到暂时的鲜味，吞咽后反而会有口干口渴的感觉。', NULL, NULL, '瘦肉80克（也可用鸡肉），大白菜150克，韭黄100克，木耳5克，白年糕300克，虾油5克，糖2克。');
INSERT INTO `recipe` VALUES (1830, 78, '蚝油扒冬瓜', '1.将冬瓜削去外皮，去瓤、籽，洗净切成半厘米厚的大片，瓤面向上，逐片整齐排列在平盘内。\r\n2.炒锅上火，放油烧热，放入葱末、姜末煸出香味，再勾入鲜汤（用鸡粉加水代替，以刚能淹没冬瓜为宜）、蚝油、绍酒、白糖、盐，将冬瓜整齐地推入锅中，旺火烧至入味，用水淀粉勾芡，出锅装盘即可。', '汁浓味鲜，瓜嫩爽滑。', NULL, NULL, '冬瓜500克，蚝油2汤匙，绍酒、白糖、鸡粉、盐、水淀粉、葱末、姜末各适量。');
INSERT INTO `recipe` VALUES (1831, 78, '荷叶粉蒸肉', '1.将粳米和籼米淘净，沥干晒燥。把八角、山柰、丁香、桂皮同米一起入锅内，用小火炒拌至呈黄色（不能炒焦），冷却后磨成粉（不宜磨得过细）。\r\n2.刮去肉皮上的细毛，清水洗净，切成8块，每块长7厘米、宽2厘米重约60克，再在每块肉中各直切一刀，不要切破皮。然后，将肉放入盛器，加甜酱、酱油、糖、酒、葱段、姜丝，拌和腌渍一小时，使卤汁入肉内，再加米粉搅匀，再在中间的刀口处嵌入米粉。\r\n3.荷叶用沸水烫一下，每张切成4小张，每张上面放肉一块，包成小方块，上笼用旺火蒸二小时左右至肉酥烂，荷叶冒出香味即成。', '肉质酥烂，肥而不腻，荷叶香味浓郁。', NULL, NULL, '猪五花肉500克、鲜荷叶（直径60厘米左右）2张、粳米和籼米各75克、葱段30克、姜丝30克、山柰（中药材）1克、桂皮1.5克、八角1.5克、丁香1克、甜酱50克、绍酒40克、酱油75克、白糖15克。');
INSERT INTO `recipe` VALUES (1832, 78, '黑豆焖猪蹄', '1.将黑豆洗净用水浸泡3小时左右，把猪蹄洗净竖劈两爿，猪耳、猪尾、猪皮、猪肥膘洗净切成小块，番茄洗净切块，葱头洗净切末，大米洗净控干，备用。\r\n2. 把盐、黑豆、猪蹄、猪耳、猪尾、猪皮、猪肥膘放在一起拌匀后，放入锅内用大火煮沸后，改用文火焖至熟透，加入少许蒜炼油调好口味；备用。\r\n3.把锅烧热后倒入蒜炼油待油温6成时，放入葱头末炒至黄色后，加入番茄块炒透后，盛入锅内倒入清水煮沸。再把锅烧热后倒入食油待油温5成时，放入大米炒至黄色后，盛入盛有番茄的焖锅加盐用大火煮沸后，改用小火焖熟。食用时，盛上黑豆焖猪蹄，', '营养丰富，味道香醇。', NULL, NULL, '黑豆400克，猪蹄750克，猪耳125克，猪尾125克，猪皮75克，猪肥膘100克，番茄125克，葱头75克，大米250，食油75克，蒜炼油100克，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (1833, 78, '红枣烧肉', '1.五花猪肉300克洗净，切小方块。 \r\n2.炒锅烧热放少许花生油，下葱姜片，煸炒放五花猪肉、葡萄酒、酱油、适量鸡汤，烧开小火煨五成熟后，放入洗净红枣100克，待熟透，入盘。', '滋阴补血、酥烂甜香，易于消化和吸收。', NULL, NULL, '五花猪肉300克、花生油、葱姜片、葡萄酒、红枣100克、酱油。');
INSERT INTO `recipe` VALUES (1834, 78, '黄鱼羹', '1.黄鱼肉片成长4厘米、宽2厘米、厚1厘米，猪肥膘切成指甲片，嫩笋、熟火腿均切成末，鸡蛋敲在碗内打散。\r\n2.炒锅置中火，下入熟猪油25克，投入葱段、姜末，煸出香味，将鱼片落锅，放放绍酒、姜汁水、清汤、笋末、精盐、猪肥膘片，烧沸后撇去浮沫，加入味精、湿淀粉勾芡，淋入鸡蛋液和熟猪油50克，用手勺推匀，盛在汤盘中，撒上葱末、熟火腿末，即可上桌。', NULL, NULL, NULL, '净黄鱼肉200克，葱末3克，嫩笋50克，葱段5克，熟猪肥膘25克，绍酒15克，熟火腿10克，精盐4克，鸡蛋1个，味精3克，姜汁水10克，清汤450克，姜末3克，湿淀粉60克，熟猪油75克。');
INSERT INTO `recipe` VALUES (1835, 78, '回锅鱼', '1.郫县豆瓣、豆豉分别剁细待用，青蒜切段。\r\n2.草鱼宰杀洗净，取肉去骨后切成大厚片，用料酒、盐码底味待用。\r\n3.鸡蛋、豆粉调成蛋豆粉。\r\n4.锅内烧食用油，七成熟时，把裹上蛋豆粉的鱼肉片放入油锅里炸变色后捞出，锅内留少许油，下豆瓣，炒香，下豆豉、甜酱、少许汤、糖、味精，鱼片炒至上色放入青蒜，亮油起锅装盘，造形即成。', '有四川头菜之称的回锅肉，其色泽红亮、肉片柔香、肥而不腻、咸、鲜、微辣、鲜、香。', '炸鱼时，油温不可太高，肉质会老，油温过低，成形不好，形状应似灯盏。', NULL, '草鱼、郫县豆瓣、豆豉、甜酱、酱油、料酒、味精、鸡蛋、豆粉、青蒜、盐、食用油、糖等。');
INSERT INTO `recipe` VALUES (1836, 78, '火腿冬瓜汤', '1.火腿批成薄片10片。冬瓜削皮去籽，洗净，切成5厘米长0.3厘米厚的片。　\r\n2.炒锅放入清汤，置旺火上烧沸，放入火腿骨和皮，沸滚5分钟左右，加入冬瓜，烧至呈玉白色时，把火腿和皮捞出，撇去泡沫，加精盐、味精，出锅盛入荷叶碗，整齐地放上火腿片即成。', '火腿浓香，冬瓜细软，汤浓味纯，是杭州夏令时菜。', '用火腿骨及皮再吊清汤，鲜味大增，清香浓郁。', NULL, '净冬瓜500克，精盐3克，熟火腿50克，味精2.5克，火腿骨和皮100克，清汤750克。');
INSERT INTO `recipe` VALUES (1837, 78, '火踵鱼翅', '1.将鱼翅在沸水锅中氽一下，盛在大碗内，加入绍酒50克、葱结、清汤350克，盖上生猪肥膘，上蒸笼用旺火蒸至柔软，取出鱼翅，拣去葱结、猪肥膘，滗去汤水。\r\n2.将火腿踵切成长方块，放在碗内，加进绍酒15克，上蒸笼用旺火蒸30分钟，滗出卤汁待用，将火腿踵留2块，其余装入大盘中心。\r\n3.炒锅置旺火，下熟猪油75克，烧至七成热，投入葱段煸出香味，加入绍酒35克和清汤50克，稍后，拣去葱段，放入鱼翅，加姜汁水、火腿踵原汁，烧沸后，改用小火煮5分钟左右，再转为旺火，放入精盐和味精，把湿淀粉调稀，边淋边轻轻转动炒锅，', '“火踵鱼翅”是以名贵的鱼翅与浙江著名特产金华火腿相集于一菜的风味肴馔。“火踵鱼翅”以火腿醇香浓郁，于翅绵糯滋润而为宴会中头菜。食之，回味甘醇清雅。', '火踵即火腿的蹄膀，皮薄、肥膘少，烧制时忌用酱油、辣椒、茴香等调味品，否则就会失去特有风味。用清汤浸鱼翅时，以浸没于翅为度。于翅要蒸至极软。', NULL, '水发玉节鱼翅400克，姜汁25克，绍酒100克，加特级去骨熟火腿踵150克，精盐5克，味精5克，熟猪油100克，生猪肥膘100克，清汤400克，豌豆苗10克，湿淀粉30克，葱段10克，熟鸡油25克，葱结20克。');
INSERT INTO `recipe` VALUES (1838, 78, '鸡汁燕窝', '将燕窝用温水浸涨后，拣去燕毛杂质，用温水漂洗干净，放进品锅加沸水焖5分钟，滗去水后，再用沸水焖约10分钟，连续3至4次，使燕窝浸涨至绵糯，然后放在容器内放上火腿丝和焯熟的蔬菜。将炒锅置于火上，放入高汤，加精盐、味精，定味后倒入装有燕窝的容器内，最后淋鸡油即成。', '燕窝洁白，汤清菜绿，鲜脆可口。', NULL, NULL, '燕窝25克、熟火腿丝15克、绿色蔬菜25克、精盐2克、味精1.5克、高级清汤500克、熟鸡油5克。');
INSERT INTO `recipe` VALUES (1839, 78, '鲫鱼汆白玉', '1.鲫鱼杀洗干净，两面鱼背肉上用刀切成横刀纹，豆腐切成小方块，用凉水冲干净备用。\r\n2.炒锅放旺火上，下熟猪油烧热，投入鲫鱼略煎，即刻翻身再稍煎另一面，烹入绍酒，加锅盖焖一下，加入生姜片、开水（750克），盖严锅盖用旺火烧2分钟，改用中火烧6分钟，下豆腐块、盐、味精，同烧2分钟至豆腐块浮上汤面发胀时，大碗内撒入胡椒粉，先把鲫鱼捞入碗内，豆腐锅内放青蒜叶粒用勺推一下，立即舀入鲫鱼碗内即可。操作关键：鲫鱼必须活杀，煎鱼时鱼皮不宜煎黄，加水初烧时必须用旺火，使猪油乳化。', '美，豆腐白嫩，香辣开胃。', NULL, NULL, '活河鲫鱼（300-400克）1尾，白玉豆腐1盒，生姜片3片，青蒜叶切粒10克，绍酒10克，精制盐4克，味精1克，胡椒粉1克，熟猪油50克。');
INSERT INTO `recipe` VALUES (1840, 78, '酱爆肉酥饼', '将水皮料和油皮料分别搓成面团后，水皮包油皮，待用。把鸡肉切成小细丁，加腌鸡料爆香在热油中，加入调味和芝麻，炒匀，待凉。面团压薄，折三折，压薄包入馅料，面上涂上蛋黄、芝麻、芫茜叶，用摄氏180度烤至金黄色即可。', '原味醇香，滋味鲜美，脆嫩柔滑。', NULL, NULL, '水皮料：中筋面粉150克、白油1.5汤匙、水半杯、糖1汤匙；油皮料：面粉130克、白油2汤匙、白兰他油2汤匙、鸡肉200克、芝麻2汤匙、芫茜叶1棵、蛋黄1粒；腌鸡料：调味素1茶匙、生粉1茶匙、油1茶匙；调味料：海鲜酱1.5汤匙、汀粉1汤匙、生抽1汤匙、胡椒粉1/4汤匙、水2汤匙。');
INSERT INTO `recipe` VALUES (1841, 78, '酱烧冬瓜条', '1.将冬瓜削去外皮，去瓤、籽，洗净切成条待用。\r\n2.炒锅置旺火上，放油3汤匙烧至六成热，下葱末爆香，倒入冬瓜条煸炒至断生，加入盐、酱油、甜面酱、糖、鸡粉和3汤匙水，用旺火烧至熟烂，用水淀粉勾芡，炒匀即可出锅。', '汁浓味鲜，瓜嫩爽滑。', NULL, NULL, '冬瓜500克，糖1汤匙，酱油半茶匙，葱末1茶匙，甜面酱2汤匙，盐、鸡粉各适量。');
INSERT INTO `recipe` VALUES (1842, 78, '酱烧整步鱼', '将步鱼斩去嘴和尾，鱼身两侧各剞一刀，用酱油5克拌匀排齐，肥膘肉切丁，豆瓣酱剁细，姜切小圆片，大葱洗净切成段，炒锅置中火上，用油划锅后，下猪油75克至七成热（约150度）时，把步鱼推入锅内，两面煎黄放入葱段10克、酱油10克和膘肉丁、姜块，加绍酒、豆瓣酱、白糖，加汤150克，约炖3分钟再加味精，用湿淀粉勾芡，沿锅淋猪油25克，转动炒锅大翻身，撒上葱段3克，出锅即成。', '鱼色酱红，酱香入味，肉质鲜嫩，油而不腻。', NULL, NULL, '净步鱼500克，熟猪肥膘肉25克，葱段15克，白糖5克，生姜5克，味精1.5克，豆瓣酱15克，湿淀粉25克，绍酒15克，熟猪油100克，酱油15克。');
INSERT INTO `recipe` VALUES (1843, 78, '酱汁活鱼', '1.将活鱼去鳃、鳞、鳍，开膛取出内脏，洗干净后，在鱼身的两面每隔0.5-0.7厘米横切一刀(不切透，到鱼骨为止)。\r\n2.用手提着鱼尾在开水锅里稍烫一下(约2-3分钟)，烫开刀口除掉腥味。\r\n3.炒勺放旺火上，加入大油甜面酱、白糖、清水(100克左右)调拌均匀，再加入清水1000-1200克，烧开后，把烫好的鱼放入。\r\n4.把汤理烧开后，移入文火上15-20分钟，再转入旺火上烧到大开。把鱼捞出来沥汤，放在盘里。\r\n5.把尚有部分汤汁的炒勺在旺火上继续轻轻搅动，浓后浇在鱼上，再撒上姜末即成。', '鱼表深红色，光亮油润美观，鱼肉鲜嫩可口，汁味甘甜醇香。', NULL, NULL, '活鲤鱼(或活青鱼)1条(约700-800克)，熟大油120克，甜面酱100克，白糖120克，姜末16克。');
INSERT INTO `recipe` VALUES (1844, 78, '椒麻鸡', '1.鸡的加工处理与棒棒鸡作法相同。 \r\n2.将葱、花椒合在一起剁碎，加上述其它调料对成汁。\r\n3.将煮熟抹好香油的鸡去骨剁成块盛盘，浇上兑好的汁，吃时拌匀即可。', '有葱椒香味，清淡适口。', NULL, NULL, '公鸡1只(鸡500克左右为好)，花椒20粒，味精、盐各5克，香油、白糖各10克，葱、酱油、汤各25克。');
INSERT INTO `recipe` VALUES (1845, 78, '焦溜肥肠', '1.生面筋压成0.5厘米厚的薄片，然后切成3厘米宽的长条，均匀缠在抹了油的木棍(直径约1厘米)上。注意由上而下缠紧，不使松散。放置 5～6分钟后，连木棍一同放入开水锅中，用旺火烧开后改用文火煮30分钟左右，捞出来放在凉水中，慢慢地捋下面筋，即成为“肥肠”。\r\n2.将“肥肠”切成0.6厘米宽的斜段，放入大碗中，加入酱油(25克)拌均匀浸好。而后稍挤一下，蘸上一层干淀粉，使其均匀附着。\r\n3.将水发木耳择洗干净，把大块的切小一点。把冬笋洗干净后切长5厘米、宽1厘米、厚0.2厘米的薄片，与豌豆一起用开水焯熟待用', '颜色金黄，形似肥肠，外焦内嫩，咸甜味香，老幼欢迎，常吃不厌。', NULL, NULL, '生面筋260克，鲜豌豆50克，净冬笋50克，水发木耳30克，香油600克(实耗约90克)，口蘑汤60克，白糖2克，干淀粉50克，湿淀粉6克，味精3克，姜末2克，酱油50克。');
INSERT INTO `recipe` VALUES (1846, 78, '金华白玉卷', '1.冬瓜1500克先去皮，切片，用开水烫1分钟，然后把金华火腿、冬菇、胡萝卜、西芹、放冬瓜中间，把它卷起来成一个圆形。\r\n2.将圆圈放蒸锅里面蒸15分钟，然后把菜心烫熟排在面上。', NULL, NULL, NULL, '冬瓜1500克、金华火腿8条、冬菇8条、胡萝卜8条、西芹8条、菜心6条。');
INSERT INTO `recipe` VALUES (1847, 78, '金银蹄', '将火踵、猪蹄膀刮净褪毛，放在沸水中煮3分钟，取出洗净。取砂锅一只，用蒸架垫底，放入火踵，加入绍酒、下清水1000克，先置旺火上烧沸，再移微火上炖，待火踵成熟时，将猪蹄膀放入，加葱姜至七成熟时，去掉葱姜，捞出火踵剔去骨，再放入锅中烧10分钟，取出冷却后，对剖开横切成约1厘米的片，放入盆中压实，上笼蒸酥熟待用。待猪蹄膀煮至酥糟时，在汤汁中加入盐、味精，出锅盛入荷叶碗中，缀上烫熟的绿蔬菜。盖上火踵片即成。', '色泽红润，肉质酥糯，鲜咸浓香，汁稠味醇，是冬令菜肴佳品。', NULL, NULL, '猪蹄膀750克，火踵250克，绿蔬菜50克，葱2条，绍酒10克，精盐2克，味精1克，姜1块。');
INSERT INTO `recipe` VALUES (1848, 78, '锦绣鱼丝', '1.将黑鱼宰杀，洗净，片去皮，取净油肉切成9厘米长的丝，放在碗中，加入精盐2克、鸡蛋清、绍酒10克、湿淀粉20克，搅拌上浆；柿子椒、香菇、蛋黄糕均切细丝。 　　\r\n2.将精盐2克、绍酒5克、味精、胡椒粉、葱丝、白汤和湿淀粉20克放在碗中，调成芡汁，炒锅置旺火，下入熟猪油，烧至四成热，鱼丝落锅划开，放入香菇丝、下芡汁推稠浓，倒入鱼丝及配料，轻轻翻炒均匀，装盘即成。', '“锦绣鱼丝”刀工精细，9厘米长的鱼丝，条条均匀洁白，色彩天然鲜艳，口味滑嫩新鲜。', '柿子椒选红、绿各一，增添菜肴色彩。用湿油划鱼丝，不要弄碎，保持鱼丝完整，是此菜成功的关键。', NULL, '大黑鱼1条1500克，胡椒粉1克，柿子椒2只，绍酒15克，葱丝15克，精盐4克，姜丝10克，味精3克，水发香菇30克，湿淀粉40克，蛋黄糕30克，熟猪油750克，白汤60克（约耗75克），鸡蛋清2个。');
INSERT INTO `recipe` VALUES (1849, 78, '苦中作乐', '1.将番茄挖空备用。 \r\n2.将苦瓜切片、梅干切成汆汤捞起备用。 \r\n3.将作法2苦瓜、梅干塞入作法1之番茄中。 \r\n4.将面包屑、红海苔、豆豉酱均匀搅拌，撒于番茄馅上。 \r\n5.将莴苣放于瓷盘上铺底，并放上做好的番茄，上摆法国香菜装饰点缀即成。', '夏季最佳凉菜，苦中带甜，有清热解毒之功效。', NULL, NULL, '番茄（大）3 个，苦瓜半条，梅干50克，面包屑10克，红海苔5克，豆豉酱5克，法国香菜3朵，莴苣5片。');
INSERT INTO `recipe` VALUES (1850, 78, '辣味肉皮冻', '1.葱洗净切丝，肉皮冻切片，放入盘中备用。\r\n2.调味料加入碗中调拌均匀，淋在肉皮冻上，撒上少许葱丝即可。', '质感筋斗，有口感肥而不腻。', NULL, NULL, '肉皮冻250克，葱1支，辣椒油、酱油各1大匙，麻油、糖、花椒粉各1小匙。');
INSERT INTO `recipe` VALUES (1851, 78, '辣汁茄丝', '1.将茄子去蒂洗净，切成长4公分的细丝，将红辣椒和干辣椒切成细丝。 \r\n2.炒锅中放入油4汤匙，小火烧至微热，下干辣椒丝炸出辣椒油，撇去干辣椒丝，红油留用。\r\n3.将红油继续加热，炒香葱丝、姜丝和红辣椒丝，倒入茄丝炒熟，加入绍酒、酱油、糖、蒜泥和少量水，用旺火将汁收浓出锅即可。', '味道独特，有些辣。', '炸辣椒油时要用小火，以免将干辣椒丝炸焦；炒菜时要用旺火。', NULL, '茄子500克，红辣椒2个，干辣椒10克，蒜泥1茶匙，酱油2汤匙，糖2茶匙，绍酒少许，葱丝、姜丝各少许。');
INSERT INTO `recipe` VALUES (1852, 78, '辣子肉丁', '将鸡肉洗净，切块，腌好10分钟。青瓜切块，和腌好的鸡块放入热肉中泡油半分钟，捞起，待用。红辣椒和葱白切末。烧热锅，倒入油，将姜、葱白和红辣椒，炒香，加入调味料，鸡肉和青瓜块，快炒，拌匀，即成。', '原味醇香，滋味鲜美，有些辣。', NULL, NULL, '鸡肉400克、青瓜300克、红辣椒2条、葱白2汤匙、姜1汤匙、调味素1茶匙、生粉1茶匙、油1茶匙、辣豆瓜酱1汤匙、盐1茶匙、生抽1/2汤匙、糖1茶匙、白醋2茶匙、麻油1茶匙、鸡精1茶匙、水1茶匙。');
INSERT INTO `recipe` VALUES (1853, 78, '凉拌三丝', '1.粉丝泡水以热水将粉丝泡至呈透明状，再以冰水浸泡，将之冷却，最后将水分沥干，切适当长度备用。 \r\n2.小黄瓜切丝先将瓜蒂去掉，再切丝备用。 \r\n3.火腿切丝火腿片对切后再切成丝。 \r\n4.煎薄蛋皮将蛋打散、加盐调味，煎成薄蛋皮；待冷却后，切丝备用。 \r\n5.淋调味料拌菜将调味料的材料拌均，把作法1-4处理好的材料加入一起拌匀，盛盘后将番茄切成半圆形，和香菜一同摆盘装饰即可。', '清淡爽口，香辣清鲜。', NULL, NULL, '粉丝(干)40克、小黄瓜3条、火腿(切薄片)4片、蛋1个、盐少许、沙拉油、番茄、香菜适量。调味料：高汤、醋各4大勺、酱油3大勺、砂糖、芝麻油各1大勺。');
INSERT INTO `recipe` VALUES (1854, 78, '龙井虾仁', '1.将虾去壳，挤出虾仁，换水再洗。这样反复洗三次，把虾仁洗得雪白取出，沥干水分（或用洁净干毛巾吸水），放入碗内，加盐、味精和蛋清，用筷子搅拌至有粘性时，放入干淀粉拌和上浆。 \r\n2.取茶杯一个，放上茶叶，用沸水50克泡开（不要加盖），放1分钟，滤出40克茶汁，剩下的茶叶和汁待用。 \r\n3.炒锅上火，用油滑锅后，下熟猪油，烧至四五成热，放入虾仁，并迅速用筷子划散，约15秒钟后取出，倒入漏勺沥油。 \r\n4.炒锅内留油少许置火上，将虾仁倒入锅中，并迅速倒入茶叶和茶汁，烹酒，加盐和味精，颠炒几下，即可出锅装盘。', '色泽洁白碧绿，茶叶清香，虾仁鲜嫩，滋味独特。', '要用鲜活河虾仁为原料，冷冻虾仁和海虾仁均不宜烹制。虾仁要温油滑熟，以保持其色白质嫩。茶叶经开水略泡后，随虾仁人锅颠炒几下即出锅，勿久煮。', NULL, '活大河虾1000克，龙井新茶1.5克，鸡蛋1个，绍酒1.5克，精盐3克。味精2.5克，淀粉40克，熟猪油1000克（约耗75克）。');
INSERT INTO `recipe` VALUES (1855, 78, '龙珠芋香饭', '芋头挖空后，芋头切粒待用。芋头粒，虾米，腊肠和叉烧炒熟后，拌合香米饭炒熟倒入芋头内蒸上10分钟即可上桌。', '原味醇香，滋味鲜美。', NULL, NULL, '300克香米（煮熟待用），150克芋头，100克虾米切粒，100克叉烧，切粒50克腊肠，盐、胡椒粉、生抽少许。');
INSERT INTO `recipe` VALUES (1856, 78, '卤瓜氽黄鱼', '将黄鱼刮去鳞，去腮和剖腹，去内脏洗净，从鱼肛门处切成两段，上半段各斜批三刀（批至脊骨），下半段各批一刀。卤瓜切片。炒锅置中火上，用油划锅，下猪油烧至六成热（约132度）时，黄鱼先经沸水氽过，再下入油锅两面略煎，烹入绍酒，加沸水1250克，放入葱结、姜块（拍松），盖紧锅盖，约烧5分钟，揭盖拣去葱、姜，将黄鱼用漏勺捞出放入口锅，把卤瓜片放在黄鱼上。锅内放入笋片，加卤瓜汁，沸起后捞出笋片放入品锅。原汤撇去浮沫，加味精调准口味，用纱筛过滤，浇入品锅即成。', '鱼肉鲜嫩，汤汁浓白，卤瓜碧绿，爽脆可口。', NULL, NULL, '黄鱼一条（重约750克）、卤瓜25克、嫩笋片25克、卤瓜汁25克、葱结一个（重约15克）、姜块15克、绍酒25克、味精2.5克、熟猪油75克。');
INSERT INTO `recipe` VALUES (1857, 78, '萝卜氽鲫鱼', '将鲫鱼宰杀，在脊背两边各剞一长刀、萝卜切长丝，用水氽熟。炒锅洗净置火上烧热，用油滑锅后放入猪油，鲫鱼先用沸水稍烫，然后下入四成热（约88度）的油锅中略煎，迅速翻身，放入绍酒、葱结、姜块及沸水1500克，加盖氽约4分钟至熟。用漏勺捞起装入品锅，去掉葱姜。锅中的汤汁放入精盐、萝卜丝及味精，沸起即可把萝卜丝捞出放在鱼的上方，浇入汤汁，淋上熟鸡油即成。食用时带姜末醋碟。', '鱼肉鲜嫩，萝卜软糯，汤汁似奶，鲜香浓郁。', NULL, NULL, '鲫鱼一条（重约500克）、萝卜150克、葱结一个、姜一块、精盐5克、绍酒25克、味精4克、熟鸡油15克、熟猪油25克、姜末醋一碟。');
INSERT INTO `recipe` VALUES (1858, 78, '麻辣冬瓜', '1.将冬瓜削去外皮，去瓤、籽，洗净切成小片待用；将干辣椒去籽、去蒂待用。\r\n2.将冬瓜片投入开水锅中煮3-4分钟至熟后捞出，沥干水分，加入盐、酱油、糖和花椒末。\r\n3.炒锅中倒入香油，烧至七分热时，放入干辣椒，炸香后捞出干辣椒，将炸出的红油趁热淋在冬瓜片上，拌匀后即可食用。', '汁浓味鲜，麻辣飘香，瓜嫩爽滑。', NULL, NULL, '冬瓜500克，干辣椒若干，花椒末适量，香油2茶匙，盐、酱油、糖各半茶匙。');
INSERT INTO `recipe` VALUES (1859, 65, '川味凉面', '1.将所有调味料调和均匀，即为酱汁。 \r\n2.将猪肉丝、小黄瓜丝、胡萝卜丝和银芽分别用沸水氽烫至熟备用。 \r\n3.将细油面放在盘子上，再将食材铺在面条上，淋上做酱汁，吃的时候拌匀即可。', NULL, NULL, NULL, '细油面（熟）200克，猪肉丝30克，蛋皮丝30克，小黄瓜丝30克，胡萝卜丝30克，榨菜丝、银芽各30克，香菜少许，台式凉面酱3大匙，花生粉1大匙，辣油1大匙。');
INSERT INTO `recipe` VALUES (1860, 78, '玛瑙鸡块', '1.鸡脯肉片成柳叶片，放在碗中，加入鸡蛋清和精盐搅上劲，用湿淀粉5克浆匀。荸荠、香菇切成薄片，豆腐皮撕去边筋，叠齐，切成6份。 \r\n2.炒锅置中火，下入熟猪油250克，烧至三成熟，将鸡片落锅划散，呈玉白色时；倒入漏勺，沥去油。炒锅留底油10克，用葱白炝锅，舀入清汤，加入绍酒、精盐15克和酱油，放入香姑片和荸荠片。沸后撇去浮沫，加味精，用湿淀汾15克勾薄芡，倒入鸡片和绿色蔬菜，淋上熟鸡油，和匀后装入碗中。 \r\n3.炒锅置中火，下入熟猪油750克，烧至五成热，将豆腐皮投入油锅，炸至金黄色，用漏勺捞起，装入荷叶', '“玛瑙鸡片”用脯肉与豆腐皮同烹，因炸制后的豆腐皮色如玛瑙鸡片，故名。此菜是把鸡脯片烩制成汤，与炸制的豆腐皮同时上桌，趁热将汤冲入，即出吱吱的响声，随之香气腾起，颇有情趣，旧时称曰响堂菜。此时迅即食用，腐皮香酥，鸡片鲜嫩，味美可口。', '炸豆腐皮，将其抖松，再投入油锅，炸至金黄色，不要炸糊。荷叶碗底先放热油少许，再装上油炸豆腐皮，因保温好，冲入汤汁后吱吱作响。', NULL, '鸡脯肉100克，精盐2克，豆腐皮6张，味精25克，水发香菇15克，清汤300克，去皮荸荠2个，鸡蛋清1个，绿色蔬菜15克，湿淀粉20G，葱白1克，熟鸡油10克，绍酒25克，熟猪油50克， 酱油5克。');
INSERT INTO `recipe` VALUES (1861, 78, '玛瑙鸡片', '1.鸡脯肉片成柳叶片，放在碗中，加入鸡蛋清和精盐搅上劲，用湿淀粉5克浆匀。荸荠、香菇切成薄片，豆腐皮撕去边筋，叠齐，切成6份。\r\n2.炒锅置中火，下入熟猪油250克，烧至三成熟，将鸡片落锅划散，呈玉白色时；倒入漏勺，沥去油。炒锅留底油10克，用葱白炝锅，舀入清汤，加入绍酒、精盐1.5克和酱油，放入香姑片和荸荠片。沸后撇去浮沫，加味精，用湿淀汾15克勾薄芡，倒入鸡片和绿色蔬菜，淋上熟鸡油，和匀后装入碗中。\r\n3.炒锅置中火，下入熟猪油750克，烧至五成热，将豆腐皮投入油锅，炸至金黄色，用漏勺捞起，装入荷叶碗', '“玛瑙鸡片”用脯肉与豆腐皮同烹，因炸制后的豆腐皮色如玛瑙鸡片，故名。此菜是把鸡脯片烩制成汤，与炸制的豆腐皮同时上桌，趁热将汤冲入，即出吱吱的响声，随之香气腾起，颇有情趣，旧时称曰响堂菜。此时迅即食用，腐皮香酥，鸡片鲜嫩，味美可口。', '炸豆腐皮，将其抖松，再投入油锅，炸至金黄色，不要炸糊。荷叶碗底先放热油少许，再装上油炸豆腐皮，因保温好，冲入汤汁后吱吱作响。', NULL, '鸡脯肉100克，精盐2克，豆腐皮6张，味精2.5克，水发香菇15克，清汤300克，去皮荸荠2个，鸡蛋清1个，绿色蔬菜15克，湿淀粉20个，葱白1克，熟鸡油10克，绍酒2.5克，熟猪油750克，酱油5克。');
INSERT INTO `recipe` VALUES (1862, 78, '梅林里脊', '里脊肉批成0.5厘米的片，用刀平拍后再用虚刀来回排剁，然后切成直长2厘米、横长3厘米的菱形块，入碗用绍酒10克、精盐、味精拌匀腌渍，加蛋清、湿淀粉20克搅匀后，再加面粉拌匀待用。把番茄沙司、酱油、白糖、醋和绍酒5克、湿淀粉5克、清汤15克调成汁待用。锅置火上烧热，加猪油至五成热（约110度）时，把肉块下锅，炸至结硬捞出，待油温升到七成热（约154度）时，复炸，捞出沥油。锅留底油倒入调好的芡汁至稠浓时，放入炸好的里脊块，翻拌均匀，淋油出锅即可。', '肉质酥嫩，色泽桃红，酸甜适口。', NULL, NULL, '猪里脊肉200克、蛋清2个、梅林番茄沙司150克、味精1克、酱油2克、白糖5克、盐2克、绍酒15克、醋5克、淀粉25克、面粉20克、熟猪油1000克（约耗75克）。');
INSERT INTO `recipe` VALUES (1863, 78, '梅子肉', '猪肥瘦肉切细剁成末，放入碗中，加绍酒10克、盐、蛋清、姜汁水、胡椒粉搅上劲，将荸荠沸水氽过拍碎剁细倒入，加湿淀粉10克搅匀制馅待用。将网油摊平，硬筋敲平、裁成宽约8厘米的长条2条，撤上干淀粉，将肉馅放在网油的一侧，中间放入葱一根，卷成直径之厘米粗如筒形，用刀切成2厘米长的段，使中间葱梗一头略突出油面，放入撒有干淀粉的盘中，拌成肉九。取碗一只，放白糖、醋，酱油、绍酒15克、湿淀粉15克，加汤水25克，调成芡汁待用。锅置中火上，下猪油升温至六成热（约132度）时，将肉丸下锅炸约1分钟捞出，待油温升高时复炸一次', '外脆里嫩，酸甜适口。', NULL, NULL, '猪精肉300克，荸荠25克，肥膘50克，蛋清一个（约20克），白糖15克，猪网油50克，酱油25克，绍酒25克，醋25克，盐3克，姜汁水5克，葱5克，干淀粉15克，芝麻油10克，湿淀粉25克，猪油750克，胡椒粉1克。');
INSERT INTO `recipe` VALUES (1864, 78, '米苋黄鱼羹', '黄鱼肉洗净取皮，切成2厘米宽的长条，斜刀批成0.3厘米厚的片，再切成1厘米见方的丁，用精盐1.5克、蛋清、绍酒5克，充分搅匀，加湿淀粉10克拌匀上浆。熟火腿切成小指甲片。米苋洗净。用油滑锅后，下猪油至四成热（约88度）时，将鱼丁入锅划散，约10秒钟后倒入漏勺沥去油。锅内留油25克，放入葱段略炒，烹入绍酒10克，加白汤烧沸后取出葱段，加精盐1.5克、味精2克、用湿淀粉15克调稀勾薄芡，放入鱼丁及米苋，边转炒锅，用勺推动均匀，起锅盛入碗内，撒上火腿丝，淋上熟鸡油即成。', '鱼羹鲜嫩，色泽悦目，米克白洁，筋软清香。', NULL, NULL, '黄鱼肉200克、米苋100克。辅料：熟火腿10克、蛋清1个、鸡油15克、熟猪油250克（约耗50克）、精盐3克、味精2.5克、绍酒15克、白汤200克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (1865, 78, '蜜汁火方', '1.火腿肉洗净切成12个小方块（皮不切断），加料酒、冰糖、清水上笼蒸1小时去汤水取出，按上法再加料酒再蒸1小时取出。 \r\n2.再加料酒，冰糖各25克和已蒸酥的莲子50克，加水蒸90分钟，取出皮朝上置于汤盆中，用莲子围边，缀上青梅，樱桃。 \r\n3.砂锅加水50克，冰糖25克及滤去杂质的原汁，勾芡淋在火腿方上即成。', NULL, NULL, NULL, '金华火腿上方一块约400克，去芯莲子50克，蜜饯青梅1颗，冰糖樱桃5颗，糖桂花2克，冰糖150克。');
INSERT INTO `recipe` VALUES (1866, 78, '南腐肉', '将猪五花肉表皮刮净，然后切成小方块约24块，在沸水中汆过，捞出洗净。将肉块与葱姜、绍酒、酱油、白糖、红腐乳卤、精盐和清水同放烧沸后，改为小火焖热30分钟，加入红曲粉继续焖烧30分钟，然后肉片朝下排在扣碗内，上笼屉用旺火蒸至酥烂。将蒸好的肉放入腰盘中，其卤汁诺入炒锅内收浓，浇于肉上。另取炒锅置中火上，下入熟猪油，烧至四成热（约88度），将绿色青菜煽熟，调入精盐、味精定味起锅，置于肉的两侧即成。', '菜品油润红亮，乳香味醇，入口即化。', NULL, NULL, '猪五花肋肉400克、青菜心100克、精盐之克、味精0.5克、白糖20克、红腐乳卤25克、红曲粉2克、绍酒15克、酱油10克、葱5克、姜5克、熟猪油15克。');
INSERT INTO `recipe` VALUES (1867, 78, '南肉春笋', '咸肉斜刀切成小长方块，春笋用清水洗净，切斜刀块。往炒锅内放清水400克，倒入咸肉原汤，用旺火烧沸后，把咸肉和笋块同时下锅，加绍酒，改用微火煮10分钟，待笋块熟后，放入味精和青菜，最后淋明油，出锅即成。', '爽嫩香脆，滋味和润。', NULL, NULL, '熟猪五花咸肉200克，青菜10克，净嫩春笋250克，咸肉原汤100克，味精2.5克，绍酒10克，熟鸡油10克。');
INSERT INTO `recipe` VALUES (1868, 78, '南乳肉', '将条肉刮净毛，洗净后切成2厘米见方的块，在沸水中稍汆，捞出再洗净，与葱结、姜块、绍酒、酱油、白糖、红乳卤一同入锅，加水200克，小火焖烧约半小时，捞出葱姜，再将红曲粉用少许水调汁入锅。继续烧约半小时，起锅入碗内，加盖上笼用旺火蒸烂为止。将南乳肉从笼中取出，滗去卤汁，覆在腰盘中间。热锅加猪油，放入绿菜，加盐，味精，炒熟起锅，沥干水放在肉的两边，再将南乳肉汁入锅收浓淋在肉上即成。', '色泽鲜艳，乳香酥糯米，蔬菜碧绿，乳肉红润，别具特色。', NULL, NULL, '猪五花条肉400克、绿叶菜200克、红曲粉1.5克、葱结5克、姜块2.5克、绍酒2.5克、酱油5克、盐1克、白糖20克、味精0.5克、熟猪油15克。');
INSERT INTO `recipe` VALUES (1869, 78, '嫩姜子鸭片', '把鸭肉批成薄片，加绍酒5克和精盐，蛋清打匀，再加湿淀粉20克上浆待用。香菇批片。把酱油、味精、和绍酒10克、湿淀粉5克放入碗中调成芡汁待用。炒锅置中火上烧热，下猪油至四成热（约88度）时，放入鸭片划散，至成熟，倒入漏勺卜锅内留油15克，放入葱段，姜片略煸，下鸭片炒匀，即将调好的芡汁加白汤搅匀倒入锅内，翻炒均匀，淋入芝麻油，出锅装盘即成。', '鸭片鲜嫩，葱姜清鲜，香辣可口。', NULL, NULL, '生净出骨去皮子鸭肉300克，水发香菇25克，蛋清1个，嫩姜片25克，白汤50克，绍酒15克，酱油5克，葱段5克，精盐1.5克，味精l.5克，湿淀粉25克，芝麻油15克，熟猪油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (1870, 78, '酿青椒', '选用7厘米长的青椒24只，用剪刀通开，去尽子，椒内撒干淀粉。将里脊肉、肥膘、虾仁剁成泥，加水、蛋清、精盐、味精、姜汁水、葱白末用手抓上劲，拌以湿淀粉，再加芝麻油5克搅匀，分成24份，酿入青椒，抹平。炒锅置小火烧热，下猪油烧室三成热（约66度）时，把青椒入锅煎熟，漠出多汆油，烹入绍酒，加酱油、白糖、醋，转动炒锅，淋上芝麻油5克，排齐装入盘中即可。', '形状完整，略带甜味，咸甜适口。', NULL, NULL, '嫩羊角青椒24只，猪脊肉100克，猪肥膘肉25克，浆虾仁50克，精盐1克，味精1.5克，酱油10克，醋10克，绍酒10克，白糖10只，蛋清1个，葱白末5克，姜汁水10克，干淀粉25克，湿淀粉5克，芝麻油10克，熟猪油40克。');
INSERT INTO `recipe` VALUES (1871, 78, '宁波摇蚶', '1.先将蚶子放在钵中，倒入清水（浸没蚶子为度），用竹帚洗刷（要连续刷洗，不能中断，防止吸入泥水）至壳发白，倒掉泥水后，再用清水淘洗干净。　　　\r\n2.将蚶子放入沸水中略烫（不要烫得太熟，以免肉色发紫，没有鲜味），随即取出，如壳不易剥开，可将蚶子翻动几下，再放入沸水略烫（烫时动作要迅速）。\r\n3.将烫好的蚶子剥去半边壳，放入盘中，撒上姜末、葱末、淋入酱油、酒、芝麻油即成。', '蚶肉鲜嫩，风味独特。', NULL, NULL, '蚶子750克、葱末10克、姜末15克、绍酒10克、酱油15克、芝麻油10克。');
INSERT INTO `recipe` VALUES (1872, 78, '宁式鳝丝', '姜、韭菜切断，鳝丝、姜丝下6成热油中煸炒，加料酒、葱姜水、酱油，盖盖稍焖。再下笋丝、清汤、韭菜、葱丝烧开，勾芡后淋香油，撒胡椒粉即成。', NULL, NULL, NULL, '熟鳝丝300克、姜丝5克、熟笋丝100克、韭菜50克、葱段5克。');
INSERT INTO `recipe` VALUES (1873, 78, '牛肉酿鲜鱿', '1.鱿鱼洗净，去头及内脏，保持圆筒状。\r\n2.牛肉、姜茸同拌匀，加调味料拌匀，挞至带胶粘状，便成馅料。\r\n3.在鱿鱼筒内侧，抹上薄博的一层干粉，酿入馅料，末端用牙签穿牢，隔水蒸熟。\r\n4.烧热油，排入酿鲜鱿，煎至成微焦黄即成。横切厚件供食用。', '造型奇特，味美可口。', NULL, NULL, '鲜鱿鱼（大）1只，剁碎牛肉240克，姜茸1汤匙，干粟粉少许。调味料：生抽、粟粉各1汤匙，绍酒1/2汤匙。');
INSERT INTO `recipe` VALUES (1874, 78, '皮蛋粥', '1.将淡菜用热水浸泡发软，剪洗干净，放入碗内，加料酒，上笼蒸至烂熟为度。 \r\n2.松花蛋去壳，切成碎米粒状。\r\n3.粳米淘洗干净，入锅加清水上火烧开，再入淡菜、皮蛋、精盐，熬煮成粥后，入小麻油即可。', '口感润滑，鲜咸适口，越喝越香。', NULL, NULL, '松花蛋二个、粳米二两、淡菜一两、精盐一钱、清水二斤、麻油三钱、料酒一钱。');
INSERT INTO `recipe` VALUES (1875, 78, '平湖糟蛋', '1.选新鲜整齐的鸭蛋，洗净，放入小坛内。加上醋，封好坛口。约一个星期后，蛋壳变软，小心取出，用冷水洗去醋味，沥干。\r\n2.将小坛内的醋倒掉，洗净小坛，再将鸭蛋轻轻放入，倒入香糟卤，并将坛口密封。约一个星期后即可食用，随吃随蒸即成。', '“糟蛋”蛋白呈乳白色，柔韧细嫩，蛋黄呈半凝固状，桔红色，绚丽悦目，气味浓香，沙甜爽口，回味绵绵。', '鸭蛋要鹇整齐，如用鸡蛋方法同上。腌制时间，不能提前，否则将影响其独特风味。', NULL, '鸭蛋10克，香糟卤500克，醋400克。');
INSERT INTO `recipe` VALUES (1876, 78, '芪烧活鱼', '1.将活鱼去鳞、鳃、鳍后，剖腹去内脏，洗净，在鱼身上切成十字花刀，水发香菇切开。\r\n2.炒锅上火，放油，将鱼炸成金黄色，捞出，滗出油。\r\n3.再将炒锅上火，放油、白糖、炒成枣红色，放入炸好的鱼，党参、黄芪片同时下锅，加水，烧开后，用文火煨出浓汤，将鱼捞出装盘，拣去党参、黄芪片，再放入笋片、香菇，加味精，烧开后，加淀粉勾芡，淋上香油，浇在鱼上即成。', '益气健脾，利水消肿。适于肺脾气虚所致的水肿胀满、咳嗽、气喘等症。', NULL, NULL, '主料：黄芪、党参、活锂鱼1条；配料：水发香菇、冬笋片、猪油；调料：白糖、料酒、盐、酱油、葱、蒜、味精、淀粉、姜汁、香油。');
INSERT INTO `recipe` VALUES (1877, 78, '钱江鲈鱼', '鲈鱼宰杀治净，放入沸水锅中略烫，取出刮去外层黑衣，冲洗干净，鱼身每侧各斜批5刀，每刀深度要一致，刀距要相等，装入盘中，笋片排列在鱼的两边，鱼身均匀地撒上肥膘肉丁，中间相隔放上火腿、香菇，再放葱结、姜片，加入绍酒、猪油及精盐1克、清水50克，上蒸笼用旺火蒸15分钟。取碗一只，放入味精、精盐及葱段，将蒸好的鱼取出。将汁滗入调味料的碗内，调好味。将蒸熟的鲈鱼放入大腰盘中，拣去葱、姜，淋上调好的味汁即成，食用时佐以姜末、香醋。', '鱼形完整美观，肉质洁白鲜嫩，滋味鲜香醇厚。', NULL, NULL, '鲈鱼一条（重约750克）、熟火腿10克、熟笋片25克、水发香菇25克、姜片15克、葱结1克、葱段5克、绍酒25克、精盐7.5克、味精5.5克、姜末15克、香醋25克。');
INSERT INTO `recipe` VALUES (1878, 78, '炝青瓜条', '1.将青瓜洗净，切成中指大小的条.葱、姜分别去皮洗净，改刀切成末。\r\n2.锅置火上，放少许油，下葱、姜末炸出香味，随后放入瓜条，适量精盐，少许味精、香油，1匙红醋，翻匀出锅，装盘上席。', '用简单的原料制作相当可口的大众菜肴是不易的，需要相当的烹技才能如愿。', '青瓜也叫黄瓜。', NULL, '主料：青瓜300克。调料：精盐、味精、红醋、香油、葱、姜。');
INSERT INTO `recipe` VALUES (1879, 78, '茄汤焗香鸡', '1.鸡洗净，切大件。 \r\n2.烧该油，放入鸡件炸至金黄色，取出鸡件，爆香洋葱2分钟，加蒜茸再爆2分钟，放回鸡件，加入番茄汤及配料，拌匀，盖好。 \r\n3.将2.放炉内，以180度火，l.5小时即成。', '滋味鲜香，香味扑鼻。', NULL, NULL, '鸡1斤（约600克），洋葱2个沏片），蒜2瓣（切茸），西芹1棵（切片），番茄汤1罐，橄榄油1/3杯。配料：香草1/2茶匙，香叶2片，白酒1/3杯。');
INSERT INTO `recipe` VALUES (1880, 78, '芹菜牛肉丝', '牛里脊肉去净牛筋，按肌纹的横斜面切丝，放入碗中，加绍酒、精盐、蛋清搅匀，加湿淀粉搅上劲，加芝麻油10克，拌匀待用。芹菜去掉根叶，切成4厘米长的段，用沸水焯至六成熟，过凉待用。葱切成段。炒锅置火上烧热，用油滑锅后下菜油，至六成热（约150度）时，投入牛肉丝，划散至玉白色时捞出沥油，锅内留底油，投入葱段略煸，放入牛肉丝、芹菜、烹入绍酒，加酱油、味精煸炒均匀，淋油即可。', '肉丝酱红鲜嫩，芹菜碧绿爽口，清鲜味美。', NULL, NULL, '牛里脊肉150 、芹菜100克、蛋清1个（重约25克）、绍酒15克、味精1.5克、酱油20克、芝麻油25克、菜油750克（实耗油75克）、精盐1.5克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (1881, 78, '清炖鱼翅', '1.将鱼翅放入肉汤内略煮，除去海咸味。把鸡去头去脚，洗净，一开两片。火腿洗净沥干。 \r\n2.用瓷质一品锅，将鱼翅放入锅底，用1/2只鸡盖在鱼翅上，再将火腿放在鸡面，加葱、姜、酒、盐和适当的水，隔水蒸约5、6小时，至鱼翅酥烂时，除去葱、姜、火腿、鸡，只留鱼翅和汤汁。 \r\n3.将剩下的老母鸡放入砂锅，用小火炖汁（约一大碗即可），除去鸡肉不要，把汤撇清，倒入装鱼翅的一品锅内。 \r\n4.另外炒一小碟绿豆牙菜，与鱼翅同时食用即可。', NULL, NULL, NULL, '水发鱼翅500克，光老母鸡1只，火腿300克，料酒300克，葱、姜少许。');
INSERT INTO `recipe` VALUES (1882, 78, '清汤鱼圆', '鱼肉茸泥加入精盐、味精，搅打起泡后加入清水，继续打至起粘性，然后静置5分钟，起锅加清水，放进鱼丸，改用旺火烧沸，再改为小火“养熟”。稍后，加盐、味精及豌豆苗，定味后盛入品锅，摆上熟火腿片和熟笋片，摆成三角形，中间摆上熟香菇朵，四周以豌豆苗点缀，淋上熟鸡油即成。', '鱼丸软嫩。汤清味鲜，爽滑适口。', NULL, NULL, '净鲢鱼肉泥200克，熟火腿3片，熟笋3片，水发熟香菇15克，豌豆苗25克，精盐17克，味精2.5克，熟鸡油2.5克。');
INSERT INTO `recipe` VALUES (1883, 78, '清蒸鳊鱼', '将鳊鱼宰杀去鳞、腮后，剖腹去内脏洗净，然后入沸水锅中氽一下，捞出，刮去黑膜，冲净后在鱼身脊背部位直剞十字花刀，将鱼摆在深盘中，以次将笋片、香菇、板油丁、火腿片放在鱼身上，加精盐、绍酒、清汤，再放上姜片、葱结、入蒸笼用旺火蒸10分钟，待鱼眼珠突出即成熟，起笼去掉葱姜，将原汁滗入碗中另用。鱼装入长腰盘中。将碗中的原汁汤放入精盐、味精和沸清汤70克，调准味，而后淋在鱼身上即成。食用时随带姜末、醋蘸食。', '鱼形完整，鱼肉软嫩，鲜香味美，汤清味醇。', NULL, NULL, '鲜鳊鱼一条（净重约750克）、熟火腿片15克、生笋片2.5克、水发香菇25克、板油丁10克、姜片4克、姜米3克、葱结5克，香醋5克、精盐4克、味精3克、绍酒2克、葱段2克、清汤250克。');
INSERT INTO `recipe` VALUES (1884, 78, '清蒸石斑鱼', '将石斑鱼宰杀，洗净。在鱼身两侧剞上5刀，将猪板油切成10片。在鱼的每个刀口处塞进猪板油，姜片各1片及葱段。再取杯子一只。放入精盐、酱油和绍酒，连同鱼一起上蒸笼，用旺火蒸至鱼刀纹露骨即可。拣去葱、姜、猪板油片，撒上味精，带上蒸过的酱油味料即可食用。', '鱼形完整，鱼肉鲜嫩，肥美细滑，精香馥郁。', NULL, NULL, '石斑鱼一条（重约1000克）、猪板油50克、精盐5克、味精0.5克、绍酒15克、葱段5克、酱油25克、姜片10克。');
INSERT INTO `recipe` VALUES (1885, 78, '肉片焖扁豆', '1.羊肉切成片。扁豆择洗干净，撅成寸段。葱顺长切成长1寸、宽1分的条，姜去皮切成极细的细末，蒜去皮用刀拍了切成碎末，淀粉用水泡上。\r\n2.把炒勺放在旺炎上，放入花生油，待油烧热时，放入肉片煸炒，边炒边放入葱条、姜末、酱油、绍酒、扁豆段，加少许汤。移至微火上烤透，然后再移至旺炎上收汤，将水淀粉（10克淀粉加10克水）淋水，放些香油、蒜末，再翻炒几下即成。', '油润光泽，质地软嫩。', NULL, NULL, '主料：瘦羊肉100克，扁豆150克。调料：花生油50克，酱油30克，绍酒5克，精葱10克，鲜姜5克，大蒜10克，淀粉10克，香油10克。');
INSERT INTO `recipe` VALUES (1886, 78, '肉片烧茄子', '1.将茄子去皮去把、切大丁块、肥瘦肉切3.6厘米长、2厘米宽、0.3厘米厚的火镰片。 \r\n2.锅内倒入植物油，烧热后、把茄子炸成金黄色，捞出控净油。 \r\n3.炒锅放入猪油，先炸大料瓣，下入肉片煸炒，加入面酱搅炒几下后，再加入葱末，蒜末，料酒，酱油，白糖，清水，将茄子下锅，放入味精，用盐找口，盖严锅盖，茄块胀起后、勾芡淋上花椒油即成。', '质厚肉嫩，味道香美。', NULL, NULL, '茄子500克，肥瘦肉100克，植物油500克（实耗65克），花椒油10克，酱油30克，精盐4克，白糖15克，料酒5克，味精2克，大料两瓣，葱末10克，蒜末10克，水团粉50克，猪油25克，清水100克。');
INSERT INTO `recipe` VALUES (1887, 78, '肉丝炒蛋', '鸡蛋磕在碗里打散，加精盐、味精搅匀、炒锅置中火上烧热，用油滑锅后，下猪油60克，倒入鸡蛋。用手勺推炒成嫩蛋皮，倒入漏勺。原锅再下猪油25克，将肉丝入锅煸熟，加绍酒、酱油、葱段和蛋，加水少许，淋入猪油、15克炒匀，出锅装盘即成。', '鸡蛋柔软黄亮，肉丝鲜嫩洁白。', NULL, NULL, '猪肉丝100克，鸡蛋5个（重约200克），绍酒25克，酱油10克，精盐1.5克，味精1.5克，熟猪油90克，葱段10克。');
INSERT INTO `recipe` VALUES (1888, 78, '赛蟹羹', '1.将鱼去鳞去鳃，剖腹去内脏，洗净，斩去头尾，用平刀从头至尾沿脊背骨片成两片，放入盆中，鱼皮朝下，加葱段10克、姜片10克、酒15克、盐1克，上笼用旺火蒸六分钟左右至熟。出笼，去掉葱姜，滗去卤汁待用，用竹筷拨碎鱼肉，取出皮骨，再倒回原汁卤。\r\n2.将火腿、笋、香菇均切成五分长的丝。蛋黄打散。\r\n3.炒锅旺火烧热，下熟猪油15克，放入葱段15克，煸至有香味，加入鸡汤煮沸后，烹入酒15克，捞出葱段，放入笋、香菇，再煮沸后，将鱼肉连同原汁入锅，加酱油、盐1.5克、味精煮沸，用水淀粉调稀勾薄芡，然后将蛋黄液倒入锅', NULL, NULL, NULL, '桂鱼（或鲈鱼）1条（600克左右）、熟火腿25克、熟竹笋肉25克、水发香菇25克、鸡蛋黄2个、葱段25克、姜片10克、姜末1克、绍酒25克、酱油25克、精盐2、5克、味精3克、醋25克、鸡汤250克、熟猪油50克、湿淀粉30克。');
INSERT INTO `recipe` VALUES (1889, 78, '三色鱼丸汤', '1.鱼去皮、骨、刺、砸成细鱼泥，去筋后，用少量葱、姜水散，再加水调成稀粥状，加盐、料酒，用劲搅动至发亮，再加少许味精、胡椒粉、大油、蛋清、搅匀分成三分。一分加已捣碎后挤出的菠菜汁搅成绿色；一分加蕃茄酱搅成红色；一分原色，即成为绿、红、白三色的泥子。\r\n2.把三色泥子分别挤成丸子，入炒勺内凉水中，然后上火逐渐加热，水开(勿大开)后，用手勺背面轻轻按压，使鱼丸慢慢熟透，捞出泡于汤中。\r\n3.烧开清汤吃好味，将鱼丸放入汤内再烧开即成。', '鱼丸绵软、鲜嫩、汤清香、爽口。', NULL, NULL, '主料：黄占鱼（或白鱼、桂鱼也可）500克，菠菜叶200克，蕃茄酱100克，鸡蛋3个。调料：大油80克，盐8克，葱30克，姜20克，料酒20克，味精5克，胡椒粉、清汤、清水适量。');
INSERT INTO `recipe` VALUES (1890, 78, '三丝拌蛏', '1.将蛏子洗净，在咸水里养3小时左右，使它吐净泥沙，捞出用清水洗净。\r\n2.炒锅中放水1.5公斤，用旺火烧沸，放入蛏子，氽至开口时用漏勺捞出，汤待用，冷却后剥去壳，剪去须尖和泥肠，用冷开水洗净。\r\n3.韭菜去根，切成3.3厘米段，放入开水中焯一下，香菇切成线，将韭菜及香菇丝一起放入氽蛏子的原汤中浸渍2分钟，捞出冷却。\r\n4.将蛏肉、韭菜、香菇丝、味精、绍酒、盐2.5克放入碗中拌匀，盛入盘中，四周镶上用黄瓜做成的花，淋入芝麻油，撒上火腿丝，上桌时，外带姜末、醋、酱油一碟蘸食即可。', '此菜蛏肉洁白，火腿丝、姜黄，菇褐，韭绿，五彩缤纷，平嫩清爽，带酸辣，是宁汉地区的冷盆名菜。', '在饿养蛏子激光器，每千克清水加10克盐为宜。在饿养蛏子时，注意将不吐须的死蛏挑出，死蛏不能食。氽蛏子时间不能过长，开口即可，否则肉质易老。', NULL, '大蛏子750克，绍酒15克，熟火腿丝25克，姜末10克，熟香菇75克，酱油25克，净黄瓜50克，精盐13克，韭菜250克，味精1克。');
INSERT INTO `recipe` VALUES (1891, 78, '三丝敲鱼', '1.鲜鱼洗净剔骨去刺片成片，沾干湿淀粉后用木槌轻击，敲成鱼片，下开水锅中汆熟后，过凉切条； \r\n2.火腿、香菇切丝，菜心汆一下捞出沥水； \r\n3.锅中加清汤500克，下鱼条、菜心、料酒、盐，用中火烧开，撇去浮沫，放香菇丝、火腿丝、鸡脯肉丝、调料等，倒入汤盆即成。', NULL, NULL, NULL, '鳕鱼1尾750克，菜心100克，熟鸡脯肉丝50克，火腿25克，水发香菇50克。');
INSERT INTO `recipe` VALUES (1892, 78, '三丝鱼卷', '将草鱼宰杀洗净，沿脊骨片出两片鱼肉，取一片放砧板上，斜刀片成连刀蝴蝶片12片，皮朝上平摊在砧板上，洒上绍酒5克和姜汁水。再逐片拍上干淀粉。把火腿、熟笋、香菇切成比鱼片宽度略长的丝。葱切成同样长的段，整齐地放在鱼片上，紧紧卷成鱼卷。取腰盘一只，抹上一层熟猪油，加几滴水，把鱼卷整齐的排在盘中，上蒸笼用旺火蒸熟取出，滗去原汁待用。砂锅置中火上，下熟猪油烧至五成热（约110度）时，投入葱段、姜片，煸出香味，倒入原汁和清汤烧沸，拣去葱、姜、添加精盐、绍酒、味精，用湿淀粉勾玻璃芡，下入熟猪油推匀，浇在鱼卷上即成。', '鱼卷整齐，肉质鲜嫩，滑爽利口，醇香馥郁。', NULL, NULL, '净草鱼肉250克，水发香菇50克，熟火腿35克，熟笋肉50克，精盐2克，味精1克，绍酒10克，熟猪油10克，葱10克，姜片5克，姜汁水5克，干淀粉5克，湿淀粉5克。');
INSERT INTO `recipe` VALUES (1893, 78, '三鲜砂锅', '将河虾剪须洗净。鸡肉、蛋糕、冬笋均切成长条，分别排齐。大白菜切成6.5厘米长、2厘米宽的段，用沸水汆熟，沥干水分，粉丝切成长段，肉皮切成与大白菜相同大小的块，净水漂过待用。取砂锅一只，放入大白菜，辅上粉丝、肉皮，把猪肚、鸡肉、蛋糕，笋肉、肉圆、河虾分别按色、形不同，整齐地放在肉皮上面，鱼圆居中结顶，盖上火腿片，加绍酒、精盐、味精、清汤、猪油，置小火上炖酥烂，离火。用盘衬托上桌即成。', '用料多样，鱼、虾、禽、蛋软糯鲜香，原汁原味，香醇味浓。', NULL, NULL, '主料：熟鸡肉50克、鲜河虾50克、肉丸6颗、鱼圆6颗、猪肚50克。辅料：熟火腿15克、熟蛋糕25克、水发肉皮100克、水发粉丝100克、熟笋肉25克、大白菜250克、精盐7克、味精5克、清汤750克、熟猪油50克。');
INSERT INTO `recipe` VALUES (1894, 78, '砂锅鱼头豆腐', '1.将鲢鱼头去鳞除鳃洗净，用刀在近头部背肉段的两面各深剞两刀，放在盛器里，头部剖面涂上切碎的豆瓣酱，加酱油15克稍腌渍一下，使咸味渗入整个鱼头。豆腐切成3.5厘米长、0.5厘米厚的片，用沸水焯腥味。\r\n2.炒锅旺火烧热，用油滑锅后，下菜油烧至八成热时，将鱼头正面下锅煎黄，将鱼头翻身稍煎，烹入酒和姜汁，加盖稍焖即开锅，加酱油、糖、鲜汤1000克，加盖焖烧至八成熟时，放入豆腐、笋片、香菇和味精。烧沸后，起锅倒入大砂锅内，放在微火炉上煨煮五分钟，再移到中火上烧约二、三分钟，撇去浮沫，加入青蒜、味精，淋上猪油，原', '色泽素雅，汤纯味厚，鱼头肥嫩鲜美，清香四溢。', NULL, NULL, '花鲢鱼头1只（1250克）、嫩豆腐500克、熟竹笋片75克、水发香菇25克、豆瓣酱25克、绍酒25克、姜末1.5克、青蒜25克、酱油100克、白糖20克、味精3.5克、熟菜油250克（实耗100克）、熟猪油。');
INSERT INTO `recipe` VALUES (1895, 78, '山楂肉干', '1.将猪肉洗净，山楂去杂质，姜切片，葱切段。\r\n2.将一半山楂放入锅内，加水（量稍多），在火上烧沸后，放入猪肉，煮熬至六成熟捞出，晾凉后，切成约长2寸、宽5分的条，用油、姜、葱、料酒、花椒等调料拌匀，腌1小时，去水分。\r\n3.将炒锅上火，放油，油七成热，投入肉条。至微黄时用漏勺捞起；再将油倒出，投入另一半山楂，略炸后，再将肉干倒入锅中，反复翻炒，至熟，装盘时淋上香油，撒上味精、白糖，拌匀即可。', '滋阴润燥，化食消积。', NULL, NULL, '山楂、瘦猪肉、姜、葱、花椒、料酒、白糖、味精、香油。');
INSERT INTO `recipe` VALUES (1896, 78, '珊瑚鱼丁', '海蜇头除尽杂质，反复漂洗后切成4厘米见方的块，草鱼肉切成1.5厘米见方的丁，用绍酒、精盐和鸡蛋清搅上劲，再加入湿淀粉搅匀。然后用绍酒、盐、白糖、味精、清汤、湿淀粉调成芡汁备用。将炒锅置旺火上，添水烧沸，放入海蜇头，沥去水。然后起锅搭油，划出鱼丁，呈白色时捞出沥油，炒锅留底油，回置火上，投入葱段煸出香味，烹入调好的芡汁，迅即倒入海蜇头块、鱼丁同炒，最后淋明油即可。', '蜇头爽脆，鱼丁鲜嫩。', NULL, NULL, '净草鱼肉150克、海蜇头250克、精盐5克、味精2克、绍酒13克、白糖3克、葱段20克、鸡蛋清1个、湿淀粉25克、熟猪油500克。');
INSERT INTO `recipe` VALUES (1897, 78, '绍式虾球', '1.将鸡蛋打入碗内，放入生粉、盐、味精，用筷子搅透，倒入浆虾仁拌匀。葱洗净，切1寸长的段，段白留用。 \r\n2.烧热锅，下猪油，至七成热时，一边用铁筷在油锅中溜划，一边慢慢地将虾仁蛋糊倒入油锅，至起丝后，迅速用漏勺捞起沥去油，用筷拨松装碟，围上洗净的芫荽即可。用葱白段、甜面酱各一碟蘸食。', NULL, NULL, NULL, '浆虾仁100克，鸡蛋4只，葱3条，甜面酱20克，生粉、芫荽各适量。');
INSERT INTO `recipe` VALUES (1898, 78, '薯丁培根蛋', '1.将薯条切成1公分小段；培根丁先爆香后，再把切好的薯条丁一起拌炒至熟，盛起备用。\r\n2.将蛋打散，加入炒好的薯条丁及培根丁，并加入盐，搅拌均匀备用。\r\n3.锅子预热后加入少许油，倒入作法（2）的材料，炒至蛋液熟后，即可盛起放入盘中食用。', '营养丰富，色泽鲜亮。', NULL, NULL, '薯条125克、培根（火腿肉片）丁3大匙、蛋2个。调味料：盐1/2小匙。');
INSERT INTO `recipe` VALUES (1899, 78, '四生火锅', '1.鸡脯肉、鸡肫、猪腰分别片成薄片，整齐地排放在三只圆盘中。河虾仁放入另一只圆盘内摊平。取洗净的菜叶4张，用沸水掉一下，过凉，盖在四生荤料盘上。\r\n2.将大白菜丝、豌豆苗、油条、油炸粉丝、葱丝、姜丝分别装入盘内。\r\n3.炒锅置旺火，舀入清汤100克，加入冬笋片、雪里蕻和精盐，烧沸后撇去浮沫，加入味精倒入火锅内。\r\n4.将“四生荤料”盘上的菜叶揭去分别淋上绍酒，连同大白菜丝、豌豆苗、油条、粉丝和火锅及调料一起上桌。火锅底加入酒精，临食点燃，待汤沸后，随意涮食。', NULL, NULL, NULL, '鸡脯肉100克，姜丝5克，鸡肫100克，绍酒20克，河虾仁100克，精盐3克，猪腰100克，味精2克，大白菜丝150克，辣酱油1小碟，豌豆苗150克，虾油卤1小碟，油条2根，芝麻酱1小碟，油炸粉丝50克，香菜叶1小碟，冬笋片150克，熟猪油5克，雪里蕻50克，清汤1000克，葱丝10克，胡椒粉2克。');
INSERT INTO `recipe` VALUES (1900, 65, '川味牛肉', '1.牛脯切成l寸见方的块，用铁板烤透。\r\n2.锅内倒油，放入干辣椒、料酒、红糖、笋块、盐、麻油、烤牛脯，炖透收干汁即可。', NULL, NULL, NULL, '牛脯，干辣椒，料酒红糖，笋块，盐麻油。');
INSERT INTO `recipe` VALUES (1901, 78, '四喜丸子', '猪腿肉批片，斩成粗末，放入碗中，加鸡蛋、绍酒5克、葱末、姜汁水、精盐、酱油各5克搅上劲，加湿淀粉30克搅上劲，做成四个大丸子。菜心对开（大的四开）切成5厘米长的段。炒锅置中火上烧热，用油滑锅后，下猪油至四成热（约88度）时，把肉丸子下锅，煎至两面金黄，倒入漏勺，锅内留油少许，把菜心稍煸后下肉丸子，放入绍酒5克、酱油20克、白糖2克、加浓白汤烧沸，移至小火上炆15分钟，至汤水稠浓时，用湿淀粉10克调稀勾芡，起锅时先把菜心放入盘底，上面放肉丸子，再浇上原汁即成。', '原料精肥适宜，丸子松嫩油润，菜心鲜糯，汤汁原味。', NULL, NULL, '猪腿肉（肥三瘦七）500克，生净菜心250克，鸡蛋1个（重约50克），姜汁水10克，白糖2克，酱油25克，精盐2克，绍酒10克，葱末1克，浓白汤500克，湿淀粉40克，熟猪油250克。');
INSERT INTO `recipe` VALUES (1902, 78, '松鼠鳜鱼', '1.鳜鱼用料酒、精盐调匀，蘸上干淀粉；\r\n2.番茄酱、猪肉汤、绵白糖、香醋、料酒、湿淀粉、精盐调成味汁；\r\n3.将两片鱼肉翻卷，翘起鱼尾成松鼠形，提起鱼尾放入油锅，炸二十秒钟定型，整鱼入油锅炸至淡黄色捞起，盛入盘中拼成松鼠形；\r\n4.熟猪油旺火加热，放虾仁炸熟后捞出沥油，放入葱白段炸至葱黄发香，捞出，蒜末、笋丁、香菇丁、青豌豆炒熟，加味汁搅匀，加熟猪油、香油搅匀后浇在鱼上，撒上熟虾仁即可。', '以活鳜鱼为主料，采用荔枝花刀工艺，经炸、浇汁制成。成菜色泽黄红，外脆里松，酸甜适口，装盘头尾昂翅，形似松鼠，浇汁时吱吱有声，犹如松鼠鸣叫，故得此名。', NULL, NULL, '鲜活鳜鱼750克，虾仁30克，熟春笋丁、水发香菇丁各20克，青豌豆15粒，精盐12克，香醋50克，干淀粉60克，湿淀粉35克，料酒25克，绵白糖、鲜汤、番茄酱各100克，葱白段10克，蒜末5克，熟猪油1500克，香油15克。');
INSERT INTO `recipe` VALUES (1903, 78, '粟米粥', '1.红枣去核，与桔饼一同切成碎米状。\r\n2.粟米簸去糠皮杂物，用清水淘洗干净，放入锅内，加清水，上火烧开后转用小火熬煮。待米粒开花时，加入红枣、桔饼、白糖熬煮成粥即成。', '益肾滋阴，清热解毒，陈粟米则具止痢、除烦之功。', NULL, NULL, '粟米三两、红枣八个、桔饼五钱、白糖二两、清水二斤。');
INSERT INTO `recipe` VALUES (1904, 78, '酸辣凤爪', '1.凤爪洗净煮烂，葱头、洋葱切薄片。\r\n2.凤爪、葱头、洋葱、朝天椒加入调料拌匀，放进冰箱冷冻后便可。', NULL, NULL, NULL, '去骨凤爪300克，葱头6个，洋葱半个，朝天椒2个，青露、青柠汁、糖各2汤匙，盐少许，辣椒粒半匙，开水2汤匙。');
INSERT INTO `recipe` VALUES (1905, 78, '蒜苗炒虾', '1.大虾切成段，清蒜苗切成小段。\r\n2.锅内加油烧热，放入虾炸至八成熟时，倒出一部分油，再放入葱、姜末、蒜苗等调料，翻炒一下，勾芡，稍翻几下即成。', '壮阳，益肾，强精，祛痰。', '皮肤湿疹、癣症、皮炎、疮毒等皮肤发痒者及阴虚火旺者勿多食。', NULL, '大虾500克，食油100克，味精、食盐、酱油、黄酒、醋、姜末、葱、蒜苗各适量，豆粉1茶匙。');
INSERT INTO `recipe` VALUES (1906, 78, '苔菜拖黄鱼', '1.将黄鱼肉洗净，切成5厘米长、1厘米宽的条，放入碗里，加入绍酒、精盐、葱花（3.5克）、胡椒粉捏匀。\r\n2.把面粉放在碗里，加入苔菜末、发酵粉、清水（200克左右）调匀成厚糊（不要使劲调，以危急粘性太大）。\r\n3.炒锅置旺火上，放入花生油，烧至五成热时，端到微火上，将黄鱼条沾满苔菜面粉糊，逐条放入油锅中，一边炸，一边把皮已结硬的鱼条捞出（以免老嫩不一致）。待全部炸完后，再将鱼条一起放入油锅，炸重叠呈深绿色时，捞起。把锅端回旺火上，待油烧至六成热时，再将鱼全部倒入，复炸到外皮酥脆时，倒入漏勺沥出油。随即将', '色泽呈墨绿，香味浓郁，入口酥脆鲜嫩。\r\n　　', NULL, NULL, '大黄鱼肉（去骨）200克、苔菜末15克、面粉200克、发酵粉9克、绍酒15克、精盐7.5克、葱花5克、胡椒粉0.5克、五香粉0.3克、芝麻油10克、花生油1500克（约耗100克）。');
INSERT INTO `recipe` VALUES (1907, 78, '走油肉', '将猪肉刮洗净，入锅加水煮到八成熟出锅，擦干后抹上醋及酱油15克，用九成（约225度）旺油锅，把肉放入炸约1分钟，至肉皮起泡有皱纹时当即捞出。热锅中放入酱油20克和葱结、绍酒、白糖、八角、桂皮、肉汤，把肉入锅烧1分钟，取出冷却后切成12片，皮朝下扣在放有姜丝的碗中，倒入原汤、上蒸笼用旺火蒸酥为止。青菜切成5厘米长的段，在沸水锅汆熟后放在肉上面，上桌时将肉覆在汤盘中揭去扣碗即可。', '皮起皱纹、色泽红润，酥烂鲜香，酥而不腻，青菜爽口，下饭最宜。', NULL, NULL, '猪五花条肉500克。青菜150克。葱结15克、桂皮0、5克、姜丝25克、醋2克、八角0.5克、绍酒15克、白糖5克、肉汤50克、酱油35克、菜油1000克（实耗油100克）。');
INSERT INTO `recipe` VALUES (1908, 78, '苔菜小方烤', '将猪五花肋肉刮洗干净，煮至八成熟，抽去肋骨切成3厘米的方块，原汤待用。将炒锅置旺火上，下入熟菜油25克烧至七成热（约175度）时，投入葱段略煸，即将肉块放入锅内，投入调料倒入肉原汤，煮沸后改用小火焖至酥烂，转旺火收浓卤汁，淋上熟猪油，装盘待用。将干苔菜拣去杂质，扯松切成3厘米长的段，炒锅置旺火上、下入熟菜油烧至五成热（约125度）时，投入苔菜速炸一下即捞出装盘，撒上白糖即可。', '卤汁稠浓，肉块酥糯，苔菜清香。', NULL, NULL, '猪五花肋肉6m克、干苔菜25克、精盐2克、白糖40克、红腐乳卤25克、绍酒25克、酱油25克、熟菜油500克、熟猪油25克、葱段5克。');
INSERT INTO `recipe` VALUES (1909, 78, '糖醋里脊', '将肉批成0.5厘米厚的大片，用刀轻轻排剁一下，改成骨牌块入碗，放绍酒5克和精盐捏匀，用湿淀粉25克、面粉10克拌匀待用。酱油、白糖、绍酒、醋、湿淀粉15克、水25克成糖醋汁待用。炒锅置中火烧热，下菜油烧至六成热（约150度）时，将挂好糊的肉块入锅炸1分钟捞出，待油温升至七成热（约175度）时，复炸1分钟，捞出沥油，锅内留底油，放入葱段，煸出香味，肉块下锅，迅速将调好的汁冲入锅内，颠翻炒锅，待芡汁均匀地包住肉块时淋麻油出锅装盘即可。', '色泽红亮，酸甜味美，外酥里嫩。', NULL, NULL, '猪里脊肉250克，白糖25克，面粉10克，绍酒15克，芝麻油10克，酱油25克，醋25克，葱段5克，熟菜油750克（约耗50克），精盐1克。');
INSERT INTO `recipe` VALUES (1910, 78, '糖醋排骨', '将猪子排斩成2.5厘米长的块，用绍酒10克和精盐捏匀，加淀粉25克、面粉和水50克搅拌挂糊。把酱油、白糖、醋和绍酒20克、湿淀粉25克放在碗中，加水50克调成汁待用。炒锅置中火上烧热，下猪油至六成热（约132度）时，把猪子排逐块放入油锅炸至结壳捞出，油温升至七成热（154度）时，复炸捞出沥油。锅内留底油，放入葱段煸炒出香味后捞出，下排骨，迅速冲入调好的芡汁，颠翻几下，淋麻油出锅装盘。', '酱红油亮，外松脆，里鲜嫩，甜酸味美。', NULL, NULL, '猪子排250克，湿淀粉50克，面粉25克，葱段5克，精盐2.5克，酱油25克，白糖45克，醋40克，绍酒30克，芝麻油15克，熟猪油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (1911, 78, '同心协力', '将红萝卜切成圆形12粒，马蹄去皮，芥兰切成二寸长形，然后一起蒸45分钟备用。将以上4种材料摆在圆碟上蒸10分钟即可。把一汤更生油倒进锅里烧热，把调味料倒入锅内煮滚，把茨粉淋在碟上的材料上即可。', '玉白映翠绿，爽脆清香。', NULL, NULL, '用料：红萝卜2条、马蹄12粒、干贝12粒、芥兰12棵；调味料：蚝油1汤匙、酱油1汤匙、生抽1汤匙、鸡精粉1/2茶匙、糖1/2茶匙、麻油少许、上汤200克、茨粉2汤匙、3汤匙清水。');
INSERT INTO `recipe` VALUES (1912, 78, '豌豆黄', '1.将白豌豆去皮碾碎，红枣洗净上锅煮烂制成枣汁待用。 \r\n2.火上坐砂锅或铝锅，注水约3000克，下入白豌豆粒、碱面，开锅后用小火煮约一个半小时，成稀糊状时，过细罗使白豌豆成 细泥状。 \r\n3.铝锅上火，将豌豆泥加入白糖、红枣汁搅拌均匀，倒入铝锅内，翻炒至起稠，倒入不锈钢盘子里，晾凉，上面盖湿布放入冰箱，吃时用刀切成小块即可。', '滋味鲜美。', NULL, NULL, '白豌豆1000克，白糖500克， 红枣150克，食用碱2克。');
INSERT INTO `recipe` VALUES (1913, 78, '煨烤桂鱼', '桂鱼去鳞，从背部剖开，去掉脊髓骨和内脏，洗净后用洁净的布擦干，用精盐2克、味精1克、绍酒10克、姜丝、山奈，调匀擦遍鱼体内部，腌渍待用。热锅放入猪油，至七成热（约154度）时，将肉丝下锅煸开，放入葱丝，加酱油5克、绍酒2克、味精0.5克、白糖2.5克、将肉丝煸炒成熟，起锅装入盘中，凉后放入桂鱼的腹部，用网油（或者用批薄的膘肉油片）将桂鱼包好，外层再用鲜荷叶全裹（不要包破，以防汁水外漏），然后用麻绳扎紧。将酒坛泥敲碎，用水调成泥团，加精盐50克搅匀，使泥团增加韧性，然后将泥团放入布上，抹成厚约2厘米的泥层，', '鱼肉鲜香，油润滑嫩，保持原汁原味，制作别致。', NULL, NULL, '桂鱼一条（重约750克）、新鲜荷叶一张、麻绳一根、酒坛泥适量、猪精肉75克、猪网油75克、葱丝5克、姜丝1克、熟猪油25克、山奈0.1克、白糖2.5克、酱油5克、味精2.5克、精盐2克、粗盐50克。');
INSERT INTO `recipe` VALUES (1914, 78, '莴笋拌白肉', '1.将莴笋去叶、去皮洗净，切成薄片，用少许盐拌匀，腌制15分钟，挤干水分，平铺在盘底待用；将猪肉洗净，在开水锅中烫一下，捞出洗净。\r\n2.将蒜泥放在碗中，加酱油、醋、盐、味精和香油，勾成调味汁待用。 \r\n3.把猪肉放在锅里，加水、绍酒、葱段、姜片烧开，改用小火煮至熟烂，捞出切成薄片，放在莴笋片上，倒入调味汁，吃时拌匀即可。\r\n注意：猪肉要新鲜，一定要收拾干净；切猪肉时，尽量切得薄一点，趁热浇上调味汁更入味。', '猪肉不腻，味道鲜美。', NULL, NULL, '猪肉（后臀尖）250克，莴笋100克，蒜泥1汤匙，绍酒1汤匙，酱油1汤匙，盐、醋、味精各适量，葱段、姜片各适量，香油少许。');
INSERT INTO `recipe` VALUES (1915, 78, '五夫醉鸡', '1.将鸡宰杀，去毛去内脏，冲洗干净。 \r\n2.烧好半锅开水，放入葱结、姜片后，将鸡放入锅内白煮，直到用筷子可穿透鸡肉时，取出鸡。在鸡央内外薄薄薄抹一层盐。等鸡晾凉后斩成块、码入干净瓷盆，倒上黄酒，以淹没鸡块为准。盖严盆盖，腌两天即成。', '该菜菜色微黄，质感鲜嫩，酒香扑鼻，别有风味，有增进食欲之功。', '此菜品要求另工时，不准加油，不准加配料，白水煮鸡，只放葱姜，不放盐，成熟后抹盐于鸡内外。煮鸡时，先用大火煮沸，而后改小火煮。', NULL, '嫩肥鸡1000克，葱结1只，黄酒500克，姜片2片，精盐10克。');
INSERT INTO `recipe` VALUES (1916, 78, '五谷蛋包饭', '1.以鸡蛋1个、冰糖2大匙、苹果醋1大匙、盐少许及沙拉油250－300毫升、放入果汁机或以搅拌器均匀搅拌2分钟，制成沙拉酱备用。 \r\n2.将莴苣切碎备用。 \r\n3.以毛豆200克打成泥，加入面粉1大匙、清水半碗、盐、胡椒少许及鸡蛋2个，放入烤箱约烤30分钟，制成蛋包饭表皮。 \r\n4.将五谷饭、沙拉酱、莴苣放于表皮上卷成蛋卷。 \r\n5.将新鲜番茄削皮取其果肉磨成泥状，加入红扁豆 、香菜淋于蛋包饭上即成。', '鲜咸适口，好吃好看不油腻。', NULL, NULL, '五谷饭1碗，鸡蛋3个，冰糖2大勺，苹果醋1大勺，盐少许，沙拉油260－300毫升，莴苣1片，毛豆200克，面粉1大匙，盐、胡椒少许，番茄（大）2粒，香菜适量，红扁豆10克。');
INSERT INTO `recipe` VALUES (1917, 78, '五色鱼丁', '鱼肉去皮切成0.5厘米厚、1厘米见方的丁，加入蛋清、精盐1克渍一下，用湿淀粉15克拌匀上浆。把火腿、蛋糕、香菇均切成0.2厘米厚如指甲片。嫩姜切指甲片。炒锅置中火上，滑锅后下猪油至四成热（约88度）时下鱼丁，用筷子滑散，约10秒钟至熟，倒入漏勺，沥去油。锅内留油25克，下葱段、姜片略煸，即加入香菇、蛋糕、青豆、火腿等料煸炒，放入鱼丁，烹入绍酒，加清汤50克、精盐1.5克，用湿淀粉10克勾芡，沿锅边淋入熟猪油15克，起锅即成。', '鱼肉柔滑鲜嫩，配料色彩缤纷，口味鲜香味美。', NULL, NULL, '桂鱼肉（或黄鱼肉）200克，火腿25克，蛋黄糕25克，水发香菇25克，熟青豆25克，蛋清25克，味精1.5克，精盐2.5克，葱段15克，绍酒15克，嫩姜3克，湿淀粉25克，熟猪油500克（约耗72克）。');
INSERT INTO `recipe` VALUES (1918, 78, '五味白肉', '1.将猪肉刮洗干净，加水煮至六成熟，捞出沥干，晾凉后切成薄片，码在盘中待用。\r\n2.将辣椒油、酱油、糖、醋、熟芝麻末、花椒末、葱末、姜末、蒜泥放在碗中勾成调味汁，浇在肉片上，食用时拌匀即可。', '猪肉不腻，味道可口。', '猪肉要新鲜，一定要收拾干净；煮肉的水要一次加够，中间不要往锅中加凉水；当猪肉煮到可用筷子戳穿时，说明已经熟了，可捞出切片，煮得太烂会影响口感；尽量切得薄一点。', NULL, '猪肉（后臀尖）500克，辣椒油1汤匙，酱油2茶匙，糖1汤匙，醋、熟芝麻末、花椒末、葱末、姜末各1茶匙，蒜泥1汤匙。');
INSERT INTO `recipe` VALUES (1919, 78, '五香花生', '1.花生仁放碗中，倒入沸水泡焖片刻，剥去外衣，投三成热油锅中慢慢炸至松脆捞起待用。\r\n2.待花生仁冷却后，将炒锅置小火上，放白糖50克，精盐、五香粉，待糖化后炒匀，将花生仁倒入，使糖包裹住花生仁并炒干，将锅端离火口，装盘即成。', '滋味鲜美，原味醇香。', NULL, NULL, '花生仁150克，白糖、精盐、五香粉。');
INSERT INTO `recipe` VALUES (1920, 78, '西湖莼菜汤', '1.将莼菜去茎、去净老叶洗净，放入开水锅里焯熟，用漏勺捞起盛入汤碗里。\r\n2.将鸡丝放在锅里，加鸡汤750克左右及食盐、味精，烧开后，撇去浮沫，倒入莼菜碗里，加熟火腿丝，淋上猪油少许即成。', '菜色碧绿，鸡丝洁白，火腿鲜红，莼菜鲜嫩，汤清味美，营养丰富。', NULL, NULL, '莼菜（净）250克、熟鸡丝150克、熟火腿丝50克、食盐15克、味精1.5克、猪油少许。');
INSERT INTO `recipe` VALUES (1921, 78, '西湖醋鱼', '1.先将活草鱼放入盛水的大盆内饿养一两天，使鱼肉结实，消除泥土气味。\r\n2.宰杀时，将鱼背朝外，从尾部入刀，用平刀沿着背脊骨批至鱼颌下，同时将鱼头对劈开，使之成为脱骨相连的两爿，斩去鱼齿。 　　\r\n3.烹饪时，在锅内放清水1000克，旺火烧沸，将鱼摊开，背面朝下放入，再烧开，即将锅端上小火煮约3分钟，至鱼的划水鳍竖起、眼珠突出，即用漏勺捞出，沥干汤水，鱼皮朝上，平摊在盆里。\r\n4.另用净锅上火，放入煮鱼的原汤250克，加绍酒、酱油、白糖、姜末烧开，加醋，用湿淀粉着腻，搅成浓汁，淋上麻油，浇在鱼身上即成。', NULL, '必须用活草鱼烹制，入开水锅中汆至断生捞出，保持整条不碎，肉质不糊烂。浇鱼卤汁要薄而浓，其味才美。', NULL, '活草鱼一条（重约700克），绍酒25克，姜末1.5克，酱油75克，白糖60克，醋50克，湿淀粉50克，麻油少许。');
INSERT INTO `recipe` VALUES (1922, 78, '西兰花', '1.鸡蛋、奶酪粉、冰淇淋粉混合搅拌均匀。\r\n2.将西兰花切成一口大小煮熟；热水中加少许盐将意大利面煮熟。\r\n3.锅中到入少许油，将火腿肉片、西兰花、面一起略炒。\r\n4.将炒好后的面放入作法1.中混匀即可。', '营养丰富，好吃好看。', NULL, NULL, '西兰花1株、火腿肉片100克、意大利面300克、鸡蛋2个、奶酪粉5大勺、冰淇淋粉1/2杯。');
INSERT INTO `recipe` VALUES (1923, 78, '西洋烩鸡', '1.将鸡洗净切成块，撒盐、胡椒粉、味精、面粉，煎盘上炉，加入色拉油烧至160度左右，将鸡放入，煎至两面金黄熟透，加酒稍烧后入盘。 \r\n2.将煎盘加油至热，洋葱切丝放入煎盘内炒香，青椒丝加入炒熟；火腿切丝，磨茹切片稍炒，最后加入蕃茄汁烧至出油为止，再加入鸡汤汁、盐、胡椒粉味精、白糖等调口味，烧在鸡上即可。', '汁红不腻，甜酸适口。', NULL, NULL, '材料鸡、洋葱、蘑菇、番茄汁、青椒丝、火腿。');
INSERT INTO `recipe` VALUES (1924, 78, '虾子面筋', '1.姜、韭菜切断，鳝丝、姜丝下6成热油中煸炒，加料酒、葱姜水、酱油，盖盖稍焖。 \r\n2.再下笋丝、清汤、韭菜、葱丝烧开，勾芡后淋香油，撒胡椒粉即成。', NULL, NULL, NULL, '熟鳝丝300克、姜丝5克、熟笋丝100克、韭菜50克、葱段5克。');
INSERT INTO `recipe` VALUES (1925, 78, '鲜果银耳', '银耳用水浸发，加水300克，上笼蒸至柔糯为止。鲜果切成指甲大小的片待用。锅内放水40克，加白糖，用小火略煮，使糖溶解。撇去浮沫，放入银耳与鲜果，煮沸，用湿淀粉调稀勾薄芡、盛入荷叶碗内，撒上糖桂花即成。', '汁浓菜糯，香甜滑润，营养丰富。', NULL, NULL, '银耳10克，鲜果150克，白糖150克，糖桂花1.5克，湿淀粉25克。');
INSERT INTO `recipe` VALUES (1926, 78, '鲜杞炒里脊片', '里脊肉切片，用盐1克、湿淀粉5克和绍酒，拌匀上劲。加蛋清搅匀上浆，鲜妃嫩头洗净后在沸水中焯一下，过凉待用。炒锅置火上烧热油，至四成热（约88度）时，将肉片入锅，划散起色时捞出沥油。锅内留底油，倒入鲜杞稍煸后，加精盐1克、味精、湿淀粉5克调稀勾薄芡，倒入里脊片淋油，略炸后出锅装盘。', '鲜杞碧绿清香，里脊洁白滑嫩，清鲜味美。', NULL, NULL, '猪里脊肉200克，嫩杞子100克，蛋清1个，味精1.5克，绍酒2克，清汤100克，湿淀粉10克，熟猪油750克（耗80克），精盐1克。');
INSERT INTO `recipe` VALUES (1927, 78, '咸菜肉丝毛豆', '1.将毛豆洗净去豆荚，咸菜洗净后，切成1厘米的小段，猪瘦肉洗净，切成丝。 \r\n2.炒锅放置火上，烧热后放入油25克，待油热后放肉丝炒至断生，倒入咸菜翻炒，加入少许水，炒至水分收干离火盛出。 \r\n3.炒锅烧热，加油25克，热后下毛豆煸炒一会儿，加少许盐，白糖再炒，放咸菜，肉丝，味精，翻炒均匀即可出锅。', NULL, NULL, NULL, '咸菜、肉丝、毛豆。');
INSERT INTO `recipe` VALUES (1928, 78, '香菇里脊', '将香菇洗净，大片切小。猪里脊用顶刀切成薄片，用精盐、鸡蛋清抓渍后用淀粉上浆。将炒锅置火上，下入熟猪油，烧至三成热（约66度）时，放入里脊肉片划散沥油待用。原锅留底油，投入葱段稍煽，放香菇、白汤、精盐和里脊片，加入绍酒、葱段稍炒勾芡，淋明油颠翻出锅装盘即成。', '将香菇洗净，大片切小。猪里脊用顶刀切成薄片，用精盐、鸡蛋清抓渍后用淀粉上浆。将炒锅置火上，下入熟猪油，烧至三成热（约66度）时，放入里脊肉片划散沥油待用。原锅留底油，投入葱段稍煽，放香菇、白汤、精盐和里脊片，加入绍酒、葱段稍炒勾芡，淋明油颠翻出锅装盘即成。', NULL, NULL, '猪里脊肉200克，水发香菇100克，精盐3克，味精1.5克，绍酒15克，葱段4克，鸡蛋清25克，芝麻油10克，熟猪油750克（实耗油70克），湿淀粉25克。');
INSERT INTO `recipe` VALUES (1929, 78, '香辣咖哩面包', '首先把酵母和温水搅溶，倒入外皮用料中，拌匀，搓成面团后，存放15分。将鸡肉粒用馅料腌10分钟，洋葱切粒。烧热油在锅中，爆香蒜米和洋葱头，加入腌好的鸡肉粒，杂豆和香辣即煮料，抄香，加入薯泥，捞匀。把面团分成小球，每份包入1 汤匙馅料， 搓合，湿水，滚上面包粉。烧热油，把面包炸至金黄色，捞起即可吃。', NULL, NULL, NULL, '外皮用料：中筋面粉300克，糖1汤匙，菜油1汤匙，发粉1汤匙，酵母（干）半汤匙温水1杯 面包粉适量。馅料：鸡肉(粒)150克，洋葱头1粒，蒜米2茶匙，杂豆150克，香辣即煮料1包，薯仔泥200，油2汤匙腌鸡。调味料：调味素2茶匙、胡椒粉1/4茶匙、玉粟粉2茶匙、油1/2汤匙。');
INSERT INTO `recipe` VALUES (1930, 78, '蟹镶橙', '将每个鲜橙在上部四分之一处截一片顶，顶留用，将橙瓤挖出，留一部分橙肉，姜切末；猪肥膘肉汆熟，切削顶，净荸荠切成小丁，蟹肉，肥膘丁、荸荠丁加鸡蛋液、姜末、胡椒粉、精盐、味精、白酒拌匀，分10份装入鲜橙内，将原来截起的橙片盖顶；将盖好的橙放盘中，蒸熟即可。', '以蟹黄、蟹肉为主料煸炒后镶入橙子中蒸制而成。成菜色艳形美，橙香。', NULL, NULL, '净蟹膏肉200克，鸡蛋2个，鲜橙10个，猪肥膘肉、净荸荠各25克，精盐3克，白酒、姜各5克，味精2克，胡椒粉1克。');
INSERT INTO `recipe` VALUES (1931, 78, '新风鳗鲞', '1.将海鳗去除鳗涎，洗净，再从海鳗背脊，从头到尾剖开，去内脏、血筋，用洁净干布揩出血水，然后用盐在鱼肉上擦匀，使鱼肉吸收盐分，放入盛器内腌二三小时。\r\n2.将腌鳗取出，用竹片将鳗体交叉撑开，悬阴凉通风处晾干（忌日光晒），约七天左右，待肉质坚实硬结即可食用。\r\n3.食用时，先将风干的鲜鲞切下一块，亦可将鳗鲞切成小块，与熟五花肉共煮，即成“鲞鱼烤肉”，美味可口。在盛器内，为葱、姜、酒，蒸熟，撕碎装盘即成。', NULL, NULL, NULL, '新鲜粗减海鳗1条（约2000克左右），盐100克，葱、姜各5克，酒10克。');
INSERT INTO `recipe` VALUES (1932, 78, '杏仁猪肺汤', '1.将猪肺洗净，甜杏仁用温水净泡2小时，去皮，捣烂，取出同生姜汁、蜂蜜一起拌匀，塞入猪肺管内，扎好管口备用。\r\n2.将猪肺放入砂锅，加水适量，先用武火烧沸，后改用文火炖150分钟即可。', NULL, NULL, NULL, '新鲜猪肺1个，生姜汁60毫升，甜杏仁60玫，蜂蜜250克。');
INSERT INTO `recipe` VALUES (1933, 78, '盐水鸭亲', '鸭洗净，用洁布揩干，割去尾臊，从背部对剖开，用花椒、精盐拌匀，擦遍鸭身内外，皮朝下，头弯进，放入大品锅内，浇入绍酒，加葱结，姜片渍约15分钟后，上笼屉用旺火焖蒸约半小时，出笼，拣去花椒、葱结、姜，滗出原汁待用。待鸭略冷后，把鸭身拆骨肋，鸭颈斩成小块垫在盘底，鸭肉切成约6厘米长、1厘米宽的条，整齐地摆放成鸭子整形，将原汁撇去浮油，滤去沉渣，加入味精，调好口味，淋在鸭身上，缀上洗烫过的香菜即成。', '鸭于鲜嫩，原汁香醇，为夏令佳肴。', NULL, NULL, '生净嫩鸭一只（约重1250克）、花椒20粒、葱结1个、姜片5片、香菜25克、绍酒25克、精盐10克、味精15克。');
INSERT INTO `recipe` VALUES (1934, 78, '杨梅肉丸', '1.河虾和肥膘茸加入姜汁、葱花、黄酒、盐、湿生粉拌上劲。\r\n2.锅上火，放油烧至五成热，将虾茸挤成杨梅状的丸子，滚上火腿末入油锅炸熟，沥油。\r\n3.锅上大火，留少许油，放入番茄酱、糖、汤，煸炒，下入杨梅虾丸，湿生粉勾芡，颠匀即可。', '状似杨梅、外脆里嫩，酸甜适口。', NULL, NULL, '主料：河虾茸300克，肥膘茸75克，火腿末35克。辅料：黄酒20克，盐10克，番茄酱、糖各35克，湿生粉20克，精制油750克，上汤25克，葱花、姜汁各少许。');
INSERT INTO `recipe` VALUES (1935, 78, '养生菜根', '1.红、白萝卜、小黄瓜切5公分细丝泡水。\r\n2.白芝麻切碎，与其他调味料一起拌匀。\r\n3.将作法（1）的材料捞起，沥干水分放入盘子，淋上作法（2）中的酱料即可食用。', '补虚，润肺，滑肠。适用于中老年及体弱早衰、产后体虚、头晕目眩、肺燥咳嗽咳血、慢性便秘等症。', NULL, NULL, '白萝卜（中）1/2根、胡萝卜（中）1/2根、小黄瓜1条、黑木耳（生的）2片、不甜花生酱60公克、炒香白芝麻4大匙、酱油21/2大匙、糖3大匙、高汤1/2杯、米醋3大匙。');
INSERT INTO `recipe` VALUES (1936, 78, '樱桃肉', '条肉刮净细毛洗净，置沸水锅中米至五成熟，去血污，滗出肉汤待用，肉捞出待凉后放在砧板上，用刀在皮面上纵横切成樱桃大小的块（瘦肉不可切断）。炒锅内铺上葱姜，将肉皮朝下放在姜葱上，加绍酒、白糖、酱油、精盐l.5克、肉汤200克，加盖用旺火煮沸，置小火上烧约半小时，加入红曲米粉，使肉呈深红色，捞出装在碗内，加原汁汤，上蒸笼旺火蒸酥为止。热锅加猪油，投入绿蔬菜，加精盐0.5克和味精，炒熟出锅，沥于待用。把肉从蒸笼中取出，覆在盘中，卤汁滗入锅内，收浓，淋在肉上，绿蔬菜围在四周即成。', '肉皮红润，青菜碧绿，色泽鲜艳，肉酥汁浓，香甜味美。', NULL, NULL, '猪五花条肉400克、绿叶菜200克、姜5克、红曲米粉2.5克、绍酒25克、大葱10克、酱油1.5克、白糖25克、精盐之克、味精0.5克、猪油15克。');
INSERT INTO `recipe` VALUES (1937, 78, '油爆大虾', '1.将虾剪去钳、须脚，洗净沥干水。\r\n2.炒锅下菜油，旺火烧至九成热，将虾入锅用手勺断推动，约5秒钏即用漏勺捞起，待油温回升到八成热，再将虾倒入复炸10秒钟，使肉与壳脱开，用漏勺带捞出。\r\n3.将锅内油倒出，放入葱段略煸，倒入虾烹入绍酒，加酱油、白糖及少许水，颠动炒锅烹入醋，出锅装盘即成。', NULL, NULL, NULL, '活大河虾350克，酱油20克，葱段2克，醋15克，白糖25克，熟菜油500克，绍酒15克。');
INSERT INTO `recipe` VALUES (1938, 65, '川味肉丁', '1.将葱切成葱节，姜、蒜切成片备用。将切好的肉丁用酱油、料酒、盐、味精腌制入味，再加入蛋清、水淀粉，用手抓匀。用酱油、糖、醋、汤、料酒、盐、味精、水淀粉兑成碗芡。\r\n2.勺上火，注入油烧热后将上好浆的肉丁与豆瓣酱同时下锅煸炒，待肉热，豆瓣酱炒出香味与红油后，下入葱节、姜片、蒜片翻炒，然后放入配料，再倒入兑好的碗芡，等芡汁熟透后翻炒，芡汁均匀地将原料裹起来即可出勺。', NULL, NULL, NULL, '瘦嫩猪肉150克，油l00克，酱油20克，料酒20克，盐0.5克，糖0.3克，醋0.2克，鸡蛋清半个，水淀粉20克，豆瓣酱15克，葱、姜、蒜、毛汤各15克。');
INSERT INTO `recipe` VALUES (1939, 78, '油煎茄片', '1.将嫩茄子洗净后，带皮竖切为厚约5毫米的长形片、放入盒内，加沸水2000克，烫泡约半小时，捞出沥水待用。 \r\n2.将平锅烧热后，加入花生油50克、将茄片排锅内、煎至两面金黄即出锅。 \r\n3.将炒锅放中火上、加油15克、热后投入葱丝、蒜末、姜末、肉末、精盐、白糖、米醋、炒勾后即下入煎茄片、轻轻拌炒，炒勾即可出锅。', '紫黄相映，光亮悦目、五味俱全、风味独特。', NULL, NULL, '嫩茄子400克、花生油65克、肉末50克、葱丝5克、蒜末5克、姜末3克、精盐7克、白糖10克、米醋5克。');
INSERT INTO `recipe` VALUES (1940, 78, '油焖白菜', '1.将白菜洗净，取菜心待用，鲜蘑冼净，倒入盆内，加汤、盐稍煨。 \r\n2.炒勺上火，注入植物油烧至四五成熟时，倒入白菜心，随即加大火力，油焖至八成熟，控去油，再加入汤，将菜心焖熟，沥干汤汁，整齐把菜放在盘中。 \r\n3.炒勺洗净，放入汤、鲜蘑、盐、味精、胡椒粉烧开，淋入水淀粉勾芡，然后起勺，浇在白菜上即成。', '油润光亮，入味咸鲜。', NULL, NULL, '白菜500克，鲜蘑250克，植物油300克，盐、味精、胡椒粉、水淀粉、汤各适量。');
INSERT INTO `recipe` VALUES (1941, 78, '油焖大虾', '1.将对虾冲洗，剪去虾须、虾腿，由头部虾枪处剪一小口，取出沙包，再将虾背剪开，抽出沙腺。\r\n2.炒勺上火，放入花生油，烧热，投入大料、葱段、姜片煸炒，放入大虾煸炒出虾油，烹入料酒，加入精盐、白糖、清汤烧开，盖上盖，用微火焖烤透，再将勺移正火，收汁微浓时放入味精，淋入香油即成。', '滋味清淡，鲜咸宜人', NULL, NULL, '主料：对虾10个。调料：料酒25克，精盐适量，白糖30克，味精5克，花生油100克，香油25克，大料2克，葱段75克，姜片50克，清汤适量。');
INSERT INTO `recipe` VALUES (1942, 78, '鱿鱼烩杨桃', '1.将材料分别洗净、切好备用。\r\n2.淀粉加水搅拌均匀。\r\n3.药材用清水冲洗净，加3杯水煮20分钟后取1杯汤汁。\r\n4.锅预热加入少许油，放入葱、蒜、姜爆香。\r\n5.接着放入鱿鱼、杨桃、胡萝卜略炒1－2分钟。\r\n6.加入汤汁、调味料及碗豆荚、黄甜椒片一起翻炒。\r\n7. 再淋入淀粉水勾芡，拌炒一会即可。', '养阴润肺、清心去烦燥、补肾养心、改善神精衰弱。', NULL, NULL, '材料花枝4两、碗豆荚10支、黄甜椒1/2颗、杨桃1杯、葱2支、蒜头1粒、姜3片、胡萝卜少许。调味料：香油1/3大匙、砂糖1/2大匙、淀粉1/2大匙、水1大匙、盐1小匙。');
INSERT INTO `recipe` VALUES (1943, 78, '鱼片蒸蛋', '1.将鱼片加入精盐5克，油10克拌匀。 \r\n2.鸡蛋搅拌成蛋液，放精盐、味精搅匀，倒入盘中。 \r\n3.烧沸蒸锅，放入蛋用慢火蒸约7分钟再加入鱼片、葱粒铺放在面，续蒸3分钟，利用余热焗2分钟取出，淋酱油和油，撒上胡椒粉便成。', '嫩滑清香。', NULL, NULL, '原料去壳鸡蛋200克，鲜鱼片200克，葱粒10克，精盐10克，味精1克，浅色酱油15克，胡椒粉0.1克，熟植物油40克。');
INSERT INTO `recipe` VALUES (1944, 78, '鱼香菜心', '1.油菜洗净、切成3厘米长的段。葱、姜、蒜切成末，豆瓣酱剁细。\r\n2.用白糖、米醋、酱油、味精、精盐、水淀粉兑汁待用。\r\n3.锅中放油20克烧热、油菜下锅，稍炒，倒在盘中。锅中再放15克油，把豆瓣酱和葱、姜、蒜一同下锅，煸炒，待出香味，烹入兑好的汁炒熟，油菜下锅炒匀即成。', '油菜脆嫩、酸甜微辣。', NULL, NULL, '嫩油菜500克、花生油35克、四川郫县豆瓣酱15克、白糖25克、米醋10克、酱油10克，味精2克、精盐2克、水淀粉20克、葱、姜、蒜共50克。');
INSERT INTO `recipe` VALUES (1945, 78, '鱼香肉片', '1.肉切成薄片（宽2.5厘注、长3.5厘米）青菜带叶切成3厘米长的段，茎切片。木耳用水发透洗净，葱、姜、蒜切成末，将泡辣椒剁碎。将肉片用少量酱油、料酒、盐拌匀加湿淀粉浆好再拌些油。\r\n2.用酱油、料酒、味精、糖蒜、葱、姜、醋、湿淀粉、汤兑成汁。\r\n3.把炒勺烧热注油，油热后下肉片，边下边用手勺推动，使肉片散开，再投入辣椒，炒出味以后下木耳，青菜翻炒几下，然后倒上兑好的汁，汁开后翻几下即成。', '有似鱼的香味（鱼香是川菜中的名味）。', NULL, NULL, '主料：瘦猪肉400克，净青菜50克，木耳3克。调料：混合油75克，酱油20克，白糖、料酒、葱、泡辣椒各13克，醋2克，蒜姜各8克，味精3克，盐5克，湿淀粉13克，汤适量。');
INSERT INTO `recipe` VALUES (1946, 78, '玉兰明虾', '1.虾去脚爪，除砂肠，留壳，以姜汁、酒1小匙、酱油拌匀腌10分钟，备用。\r\n2.锅内热油炒香蒜头，放虾炒成红色，加一半的酒、高汤、糖、盐，将汁炒干后，加番茄酱和醋。\r\n3.芥兰洗净切段，在滚水中稍煮，再在油锅内炒，加剩下的一半酒、高汤、糖、盐所调成的调味料，取出，在大虾上即可。', '益精，补脊髓，强化神经。', NULL, NULL, '大虾12只，芥兰菜250克，姜汁1匙，酒2小匙，酱油1大匙，高汤半小碗，糖1中匙，盐半小匙，番茄酱2大匙，醋半小匙。');
INSERT INTO `recipe` VALUES (1947, 78, '玉兔五彩丝', '1.肉切10厘米长的丝，用清水反复搓洗浸泡，去净血水，控干，加盐，蛋清，淀粉上浆 。\r\n2.柿子椒，香菇，胡萝卜切成略细于肉丝的丝，油菜叶焯熟码在盘四周，将料酒，姜汁，盐，味精，水淀粉兑成汁芡。\r\n3.锅内放油烧至4－5成熟，下入浆好的肉丝滑至八成熟，倒入漏勺控油，锅留底油，投入肉丝及其他菜丝煸炒，加入对好的汁芡颠勺，淋明油出勺装入盘中间，鹌鹑蛋煮熟剥皮加工成玉兔状摆在油菜叶上。\r\n4.炒勺上火放入少量清汤，加盐，料酒，姜汁，味精烧开，用水淀粉勾芡，淋明油，将汁浇在玉兔上即可。', '造型美观，鲜咸爽口。', NULL, NULL, '猪通脊内200克，鹌鹑蛋10个，绿柿椒，胡萝卜，香菇各20克，油菜叶10片，盐3克，料酒8克，姜汁5克，味精5克，鸡蛋清1个，清汤、淀粉各适量，油100克。');
INSERT INTO `recipe` VALUES (1948, 78, '糟烩鞭笋', '将笋切成5厘米长的段，对剖开，用刀拍松。香糟放入碗中，加水100克，搅散，捏匀，用细筛子滤去渣子，留下糟汁待用。炒锅烧热下菜油至五成热（约110度）时，将鞭笋倒入锅中略煸，加水300克，烧5分钟左右，放味精，倒入香糟汁，用湿淀粉勾芡，淋麻油出锅装盘即成。', '糟香浓郁，鲜嫩爽口，色泽照亮，是夏季时令菜之一。', NULL, NULL, '嫩鞭笋肉300克，香糟50克，湿淀粉25克，熟菜油25克，精盐5克，味精2.5克，麻油10克。');
INSERT INTO `recipe` VALUES (1949, 78, '糟熘鱼片', '鱼肉批成长4－5厘米、宽2厘米的薄片，用精盐1.5克、绍酒5克腌渍入味，加蛋清搅打上劲，再加湿淀粉25克拌匀，香糟用清水50克捏碎拌匀，用纱布滤去糟渣、取糟汁加绍酒10克、湿淀粉10克、精盐、味精、湿淀粉、姜汁水调成汁待用。用中火将锅烧沸，下猪油烧至五成热（约110度）时，将鱼片下入划散成玉白色，倒入漏勺，沥去油，锅内留油25克，将葱段略炒，倒入鱼片，调汁加入白汤搅匀后迅速倒入锅内勾芡，转动炒锅至汤汁稠浓与鱼片融化时，浇上猪油10克，出锅即成。', '鱼片鲜嫩，糟香味醇。', NULL, NULL, '净草鱼肉300克、绍酒15克、精盐2.5克、味精1.5克、香糟25克、白汤100克、葱段5克、蛋清1个、姜汁水15克、湿淀粉35克；熟猪油750克（实耗油75克）。');
INSERT INTO `recipe` VALUES (1950, 78, '榨菜炒鸡柳', '1.鸡柳肉除筋，切成厂条，加盐、胡椒粉拌匀，再加酒、蛋白及栗粉拌匀，最后加油拌匀。\r\n2.榨菜洗净，切薄片。\r\n3.红辣椒去籽，切长片。\r\n4.烧热3汤匙油，一次下鸡肉、毛豆、红椒轻轻炒匀，至鸡肉变色时取出。\r\n5.原锅加2汤匙油，下榨菜、葱段、姜片，炒匀，加入（4）的材料及调味料炒匀，至材料熟透即成。', '质感爽口，色美，味鲜齿颊留香。', NULL, NULL, '材料：鸡柳肉、毛豆仁各4两（约160克），榨菜2两（约80克），红辣椒2只，葱1/2条（切段），姜数片，油5汤匙。鸡调味料：盐、胡椒粉各1/8茶匙，酒2茶匙，鸡蛋白一只分量，栗粉1.5汤匙，水3汤匙。');
INSERT INTO `recipe` VALUES (1951, 78, '斩鱼圆', '将鱼剖洗净，从尾部沿着脊背批取两半，去腹部鱼肉，剔下鱼皮，剥去红筋，取用净鱼肉约425克，切薄片洗净，放砧板上用双刀排制成绿豆大的粒，放入钵内，加清水500克、精盐9克，向同一方向搅拌，至鱼肉有粘性并见细泡时，放在阴凉处发涨，火腿15克切薄片，5克斩末。把涨好的鱼茸加入料酒、熟猪油、姜汁水、火腿末，味精1.5克拌匀。取炒锅一只，放入半锅冷水，然后用左手抓起鱼茸轻轻握拳，使鱼茸从虎口挤出，用右手逐个下入锅内，成直径4厘米左右的鱼圆。然后把锅移至中火上，渐渐加热，如水稍沸，即加入冷水，并随时撇去浮沫，用手勺背', '鱼肉颗大松嫩，香鲜油润爽滑。', NULL, NULL, '活草鱼一条（重约1250克），熟火腿20克，水发冬菇一朵，精盐11克，味精2.5克，绍酒15克，姜汁水15克，葱段5克，熟鸡油10克，熟猪油20克。');
INSERT INTO `recipe` VALUES (1952, 78, '之江鲈莼羹', '净鱼肉去皮和血筋，切成6厘米长的丝，加精盐1.5克、蛋清、绍酒5克、味精0.5克，捏上劲，放入湿淀粉10克拌匀上浆，莼菜用沸水焯一下，即倒入漏勺沥干水，盛入碗中待用。炒锅置水上烧热，滑锅后下熟猪油，至四五成热（约88－110度）时，把浆好的鱼丝倒入锅内，用筷轻轻划散，呈玉白色时，倒入漏勺沥油。锅留油少许放入葱段略煸，加绍酒10克、精盐2.5克、清汤及水250克，沸起取出葱段，放入味精及姜汁水，用湿淀粉勾薄芡，再放入鱼丝及莼菜，转动炒锅，加入火腿丝、鸡丝、葱丝推匀，淋上鸡油，起锅盛入汤碗、撒上陈皮丝、胡椒粉', '药菜清香，鱼丝鲜嫩，味美滑润，色泽悦目。', NULL, NULL, '鲈鱼肉150克，药菜200克，熟火腿丝10克，熟鸡丝25克，鸡蛋清1个，陈皮丝5克，熟猪油250克，清汤200克，姜汁水5克，葱丝5克，绍酒15克，精盐4克，味精2.5克，湿淀粉25克，熟鸡油10克，胡椒粉3克。');
INSERT INTO `recipe` VALUES (1953, 78, '芝麻桂鱼', '1.桂鱼去鳞及内脏洗净，起肉去骨，鱼头及鱼尾留用。\r\n2.鱼肉抹干，划菱形花纹后切件，加入配料拌匀配三十分钟。\r\n3.拌匀粉浆料，鱼肉沾上粉浆，再沾上芝麻，放入五成滚热油中炸三分钟盛起。\r\n4.再烧滚油，鱼肉回镬炸至金黄色上碟，鱼头及鱼尾也沾上粉浆炸熟，放在鱼肉两边，即可。', '桂鱼肉质嫩滑，味道鲜甜，将之起肉沾上芝麻炸香，甘香松脆，配以浙醋或汁同吃，滋味无穷，最适宜作为宴客菜。', NULL, NULL, '1只桂鱼约1.25斤、白芝麻二两。配料：盐3/4茶匙、姜汁、酒各两茶匙、葱段一棵；粉浆料：生粉两茶匙、鸡蛋白两只。');
INSERT INTO `recipe` VALUES (1954, 78, '竹荪鸽蛋汤', '1.将竹荪先用凉水清洗，再用温水泡胀去泥沙，剖成两半，把头尾两端的尖切去，改切3厘米长节。再用开水氽一下。然后将鸡蛋打入盛有凉水的大碗中。\r\n2.炒勺上旺火将水烧开后，离火，随将鸽蛋倒入，用文火煮热捞出。 \r\n3.另用锅将清汤烧开，放入盐、胡椒粉、味精、竹荪调好味。再把鸽蛋放入汤中，将汤倒进去即成。', '竹荪鲜脆，命蛋软嫩，汤清淡味美。', NULL, NULL, '主料：命蛋20个，竹荪50克。调料：盐10克，味精6克，胡椒粉1克，清汤适量。');
INSERT INTO `recipe` VALUES (1955, 78, '状元卤味', '1.将材料分别洗净、切好备用。\r\n2.锅预热加入少许油，放入葱、蒜末、辣椒、姜片爆香。 \r\n3.放入香菇片拌炒均匀，加入3杯水煮沸。 \r\n4.依序加入所有调味科与药材熬煮10－15分钟。\r\n5.最后加入鸡肝、小豆干一起卤。\r\n6.等豆干入味，即可随时享用。', '有些微辣，香味扑鼻。', NULL, NULL, '材料鸡肝1折、豆干6雨、香菇6朵、葱2根、蒜头5个、辣椒半个、姜1片。调味料：酱油半杯、沙拉油2大匙、红砂糖1大匙、酒1大匙、香油1/2小匙。');
INSERT INTO `recipe` VALUES (1956, 78, '子鱼勒丸子汤', '将子鱼勒洗净放入碗，加绍酒、葱结、姜片，上笼蒸熟，拣去葱结，姜片盛入汤碗中。肉斩末，加水50克、盐1克，搅拌上劲，再加蛋清和味精搅匀，锅加水，把肉末挤成肉丸10个，放入锅置中火上，待肉丸外皮结壳时，用手勺轻轻推动，沸起后加冷水，移至小火上煮熟，滗入入子鱼勒的原汁，沸后撇去浮沫，加盐、味精，撒入葱段，淋入猪油。出锅放入盛子鱼勒的汤碗中即成。', '子鱼勒鲜香，肉丸松软，汤汁可口。', NULL, NULL, '猪夹心肉150克、子鱼勒100克、蛋清一只（约重25克）、精盐2克、味精2.5克、绍酒5克、葱2克、姜片2.5克。');
INSERT INTO `recipe` VALUES (1957, 78, '孜然羊肉', '1.羊肉切片放碗里，加鸡蛋、淀粉、面，抓匀糊。\r\n2.勺坐油，待油五成熟，下肉片划开后，放笋片，一起倒出。原勺留油，加辣糊、盐、糖、味精、料酒、孜然、汤、倒入肉片、笋，颠勺，拢点芡，淋香油，出勺即成。', '质地软嫩，鲜辣咸香，孜然味浓。', NULL, NULL, '羊肉300克，孜然15克，笋片50克，油1500克（约耗75克），料酒20克，盐1克，白糖15克，鸡蛋30克，味精7克，辣糊30克，水淀粉50克，香油10克，汤、面各少许。');
INSERT INTO `recipe` VALUES (1958, 65, '粉蒸牛肉', '1.大米炒黄磨成粗粉。葱切成葱花。豆豉剁细。姜捣烂后用少许泡之。香菜洗净切碎。\r\n2.牛肉切成薄片(长4厘米、宽2.5厘米)，用油、酱油、姜水豆豉、豆瓣酱、胡椒粉、大米粉等拌匀，放入碗中上屉蒸熟，取出翻扣盘中撒上葱花。另用小碟盛香菜、花椒粉、胡椒粉上桌。', '鲜嫩醇香、麻辣适口。', NULL, NULL, '瘦牛肉370克，大米75克，植物油50克，酱油30克，花椒、胡椒粉各3克，辣椒粉2克，葱、姜各8克，料酒13克，豆瓣酱30克，四川豆豉5克，香菜少许。');
INSERT INTO `recipe` VALUES (1959, 65, '粉蒸排骨', '1.排骨斩成节，加炒酥的豆瓣、盐、姜末、葱末、花椒、豆腐乳汁、胡椒粉、味精拌匀，再加入米粉，菜油拌匀。 \r\n2.在蒸笼上垫一片荷叶，装入排骨，上笼蒸40分钟至熟，取出，放入蒜末、芫荽叶，入盘即成。', '咸辣鲜香，排骨粑软。', NULL, NULL, '排骨、米粉、芫荽、豆瓣、豆腐乳汁、花椒、姜、葱、蒜、盐、味精、胡椒粉、菜油。');
INSERT INTO `recipe` VALUES (1960, 65, '粉蒸肉', '大米和花椒放入锅中，微火干炒至熟，磨成粗粉。猪肉刮洗干净，切成长约10厘米。宽4厘米、厚0.4厘米的片，入盆内，加盐、酱油、红糖汁、醪糟汁、辣豆瓣、姜末、豆腐乳汁拌和均匀，再加米粉拌匀，稍置腌渍后，将肉片摆入蒸碗内成“一封书”形。豌豆洗净入盆，加盐、米粉、冷汤拌匀，装于肉上。然后上笼用旺火蒸粑（约两小时），取出翻扣于盘内，撒上葱花即成。', '色泽红亮，咸鲜微辣，软糯适口。', NULL, NULL, '猪肉500克、鲜豌豆50克、大米粉75克、辣豆瓣15克、酱油15克、醪糟汁15克、盐2克、花椒2克、豆腐乳汁15克、姜10克、红糖20克、葱10克、冷汤25克。');
INSERT INTO `recipe` VALUES (1961, 65, '复元汤', '将羊脊骨砍成数块洗净，羊肉洗净，一起入沸水锅内氽去血水，再洗净。将淮山药等药物用纱布装好扎口，姜、葱拍破，羊肉切成条块。将以上食物和药物同时下入砂锅内，加清水适量，置武火上烧沸，打去浮沫，放入花椒、八角、料酒，移炆火上炖肥。将肉汤装碗，加胡椒粉、盐调味即成。', '羊肉鲜嫩，滑爽利口，汤汁清鲜，并具有温中暖下之功效。', NULL, NULL, '瘦羊肉500克、粳米100克、淮山药50克、肉苁蓉20克、菟丝子10克、羊脊骨一具、胡桃仁2个、葱白3根、姜10克、料酒10克、八角3克、花椒2克、盐3克、胡椒粉2克。');
INSERT INTO `recipe` VALUES (1962, 65, '干煸肉丝', '猪肉切成长约6厘米的二粗丝。鲜笋切成长约5厘米的二粗丝。葱、干辣椒分别切成细丝。锅置中火上，下素油烧热（约120度），放入干辣椒丝炸成棕红色捞起，再下肉丝，煸干水气，加入料酒、盐、酱油、笋丝继续煸炒，煸至干香亮油时，下辣椒丝、味精炒匀，起锅装盘，撒上葱丝即成。', '色泽棕红，干香酥软，咸鲜味浓。', NULL, NULL, '猪肉350克、鲜冬笋50克、干辣椒5克、葱10克、素油50克、料酒10克、盐3克、酱油10克、味精0.5克。');
INSERT INTO `recipe` VALUES (1963, 65, '参蒸鳝段', '将鳝鱼剖腹，去内脏和骨后洗净，入沸水锅氽一下捞出，刮去粘液，去头尾，切成6厘米长的段。熟火腿切成片。生姜及葱洗净，姜切片，葱切段。锅内注入清水，下姜葱和料酒，待水沸后，把鳝鱼段放沸水中烫一下捞出，码在盆内，上面放火腿片、当归、党参、姜片、葱段、料酒、胡椒粉、盐、注入清鸡汤，加盖，用棉纸封口，上笼蒸1小时左右，取出启封，拣去姜、葱，加味精即成。', '鳝肉软烂鲜香，汤汁清爽味酽，并具有补中益气，生津润燥之功效。', NULL, NULL, '鳝鱼1000克、当归5克、党参10克、熟火腿150克、盐3克、料酒15克、胡椒粉3克、葱20克、姜20克、味精1克、鸡汤100克。');
INSERT INTO `recipe` VALUES (1964, 65, '醋溜黄瓜', '1.将黄瓜挖去心，切成梳子薄片，放少许精盐调拌后，挤去汁水。\r\n2.起麻油锅，烧到滚热后，将花椒、干辣椒放入炒红，再放黄瓜，随即将葱姜、醋、酱油、湿菱粉调好倒入，炒几下即好。', '味甜、酸，绿色，四季皆宜。', NULL, NULL, '嫩黄瓜300克、白糖6克、香醋7.5克、葱姜4克、 精盐4克、干辣椒少许、酱油少许、湿菱粉少许、整花椒少许。');
INSERT INTO `recipe` VALUES (1965, 65, '脆皮桂鱼', '1. 将桂鱼去鳞去内脏，洗清，脊背两面开月牙形花刀，在鱼身抹上胡椒、盐、酒、次抹湿菱粉，再抹干菱粉，入旺猪油锅炸，约十多分钟可透，起锅。\r\n2. 另将葱、姜、番茄沙司、糖、醋、味精、油、盐入滚油锅炒好，装在小碗内，跟鱼同上桌。', '色黄中带红，四季皆宜。', NULL, NULL, '桂鱼1条(约1000克)、味精少许、干菱粉75克、黄酒4克、精盐12.5克、湿菱粉20克、葱花3克、姜2.5克、番茄沙司60克、白糖60克、香醋60克、胡椒粉少许。');
INSERT INTO `recipe` VALUES (1966, 65, '东坡肉', '猪肉洗净，与鸡骨共入沸水锅内，去血水捞出，揩干水，抹糖色，入油锅中炸至猪皮呈黄色时捞出。姜拍松、葱挽结、花椒用纱布包好；罐内放鸡骨垫底，放入调料包，加鲜汤，将炸好的肉放入，再加入料酒、盐、糖色，用小火煨至肉粑，捞出装盘，原汁收浓淋于盘中即成。', '色红亮，粑软酥香，咸鲜微甜，肥而不腻。', NULL, NULL, '猪肉750克、姜15克、葱20克、花椒2克、料酒15克、盐3克、糖色20克、素油250克、鲜汤750克。');
INSERT INTO `recipe` VALUES (1967, 65, '冬菜扣肉', '1.猪肉用白水煮熟，捞出用净布擦去肉皮上的油和水，抹上些酱油。冬菜洗净切粒状，泡辣椒切短节，姜、葱切片。\r\n2.炒勺烧热，注少许油，油将开时把肉皮向下放入，炸至焦黄色为度，晾凉后把肉切成7厘米长的薄片。\r\n3.皮向下把肉按鱼鳞状排列摆在碗底，浇洒料酒、酱油，加入盐，再放入5粒左右豆豉和2-3节辣椒以及冬菜，上屉蒸2小时即可。食用时翻扣于盘中。', '香鲜可口，味浓不腻。', NULL, NULL, '五花肉250克，四川冬菜100克，泡辣椒25克，植物油25克，酱油25克，盐2克，四川豆豉、姜、蒜各8克。');
INSERT INTO `recipe` VALUES (1968, 65, '豆瓣鲫鱼', '1.将鱼洗净，在鱼身两面各剞两刀（深度接近鱼骨），抹上绍酒、细盐稍腌。 \r\n2.炒锅上旺火，下油烧至七成热，下鱼稍炸捞起。锅内留油75克，放郸县豆瓣酱、姜、蒜炒至油呈红色，放鱼、肉汤，移至小火上，再加酱油、糖、细盐，将鱼烧熟，盛入盘中。 \r\n3.原锅置旺火上，用湿淀粉勾芡，淋醋，撒葱花，浇在鱼身上即成。\r\n注意:必须用新鲜鲫鱼或鳜鱼为原料，烹制时卤汁要浓厚，使鱼粘匀卤汁而入味。', '颜色红亮，肉质细嫩，滋味鲜浓，微带甜酸。', NULL, NULL, '活鲫鱼2条或鳜鱼1条（重约600克），蒜末30克，葱花50克，姜末、酱油、糖、醋各10克，绍酒25克，湿淀粉15克，细盐2克，郫县豆瓣酱40克，肉汤300克，熟菜油500克（约耗150克）。');
INSERT INTO `recipe` VALUES (1969, 65, '豆豉鱼', '鲜鱼肉洗净，切成长5厘米、宽1.2厘米的条形，用姜片、葱节、盐、料酒、胡椒粉拌匀腌渍入味。豆豉剁细粒，葱切成长约3厘米的节，蒜切成圆片。锅置火上，下素油烧至约200度，放入鱼条，炸至呈金黄色时捞起，倒去锅内油，另放少量干净素油烧热，下豆豉炒干水分，下葱节、蒜片稍炒，倒入鱼条；加料酒、盐、酱油、鲜汤、胡椒粉炒匀略烧，改用小火收至汁浓将干时，加入香油，起锅装盘晾凉。食用时，将鱼条摆在盘内，去掉葱、蒜，原汁淋在鱼上即成。', '鲜香酥软，豆豉味浓，风味别致，是佐酒佳肴。', NULL, NULL, '鲜鱼肉450克、潼川豆豉50克、素油500克、葱15克、蒜15克、料酒10克、盐5克、酱油10克、鲜汤50克、胡椒2克、香油10克、姜10克。');
INSERT INTO `recipe` VALUES (1970, 65, '炖鸡汁', '母鸡宰杀褪毛，掏去内脏，洗净，斩去鸡的头脚，入锅加清水置旺火上烧开，撇去浮沫，加入老姜、枸杞（用白纱布包上），移焖炉内，用微火炖4小时（嫩鸡3小时），取出，鸡肉用筷子拨开，去掉脊背骨，拣去老姜，取出枸杞即成。食用时，将鸡汁和鸡肉丝盛于碗内，加味精、精盐、胡椒粉，并酌加鲜嫩小菜即可食用。', '汤鲜肉嫩，回味纯正。', NULL, NULL, '鲜肥母鸡一只（约1500克）、枸杞100克、老姜20克、味精0.5克、胡椒粉1克、精盐2克。');
INSERT INTO `recipe` VALUES (1971, 65, '豆瓣肘子', '猪肘洗净，与鸡骨入锅出水，捞出冲洗净。用刀将肘剞成大方块，将鸡骨架垫于锅内，肘子皮向下放于鸡骨上。炒锅置火上，下猪化油烧热，放剁细的豆瓣炒出红色，加鲜汤，沥去豆瓣渣，下盐、料酒、糖色，倒入装肘子的锅中。将锅置大火上烧沸，去掉浮沫，移至小火煨至汁浓肉粑时，将肘子捞出。锅下油烧热，下青蒜苗、盐、料酒，稍炒，起锅垫于盘底，肘子皮向上放于蒜苗上，煨肘于厚汁收浓，加水豆粉勾芡，收汁，亮油时，起锅淋于肘子上即成。', '色润红亮，肘子糍糯，肥而不腻，微辣鲜香。', NULL, NULL, '猪肘（去骨）500克、青蒜苗100克、郸县豆瓣25克、猪化油50克、鲜汤500克、盐3克、料酒15克、糖色15克、素油30克、水豆粉15克。');
INSERT INTO `recipe` VALUES (1972, 65, '豆渣猪头', '猪头洗净，去尽毛、骨渣，入清水锅用旺火煮五分钟捞出，用清水冲洗后，改切大菱形块。姜、葱拍松，用干净纱布将姜葱、花椒、胡椒、八角、草果包好。在大口砂锅中，放清汤，加料酒、醒糟、冰糖汁、盐、酱油和香料包，再放入猪头骨，猪头骨上放改切好的猪头肉，用旺火烧开，然后将锅口用草纸封严烧约4小时。磨细的豆渣，上笼蒸10分钟取出晾冷，用净布包起，挤去水分。锅置火上，下猪油烧热，放入豆渣用微火炒至豆渣酥香起锅。揭去砂锅封口草纸，捞猪头肉装盘，将烧肉原汁滗入炒锅中，放入炒好的豆渣和味精，拌匀淋于猪头肉上即成。', '色泽棕红，汁浓味醇，肉质糍糯，豆渣酥香。', NULL, NULL, '猪头肉750克、豆渣200克、姜、葱各20克、花椒3克、胡椒2克、八角5克、草果3克、料酒25克、醪糟15克、冰糖汁25克、盐3克、酱油20克、猪油50克、味精1克、清汤500克。');
INSERT INTO `recipe` VALUES (1973, 65, '鹅黄肉', '荸荠洗净，去皮，与猪肉分别剁细，装碗加盐、胡椒粉、味精、鸡蛋豆粉、葱花、姜末拌匀成馅。鸡蛋调散，入炙好的锅摊成蛋皮，将蛋皮铺案上，抹蛋清豆粉，中放馅料，然后将蛋皮卷成宽约5厘米、厚约0.7厘米的长方形，再用刀切成“佛手”形。炒锅置旺火上，放油烧热（约150度），放入佛手卷入锅炸熟呈黄色时捞出，装盘。下油烧热，将泡辣椒丝炒熟，下酱油、醋、糖烹成鱼香滋汁，淋于佛手卷上即成。', '色泽金黄，形如佛手；外酥内嫩，汁浓味香。', NULL, NULL, '猪肉250克、鸡蛋4个，荸荠75克、盐3克、胡椒粉2克、味精1克、鸡蛋豆粉35克、葱10克、姜10克、油500克、泡辣椒10克、酱油15克、醋10克、糖20克。');
INSERT INTO `recipe` VALUES (1974, 65, '锅巴肉片', '1.猪肉切大片。锅巴掰块。葱切成马耳葱。蒜、姜各切片。肉片用黄酒、味精、盐腌制入味，再用水淀粉上浆抓均匀待用。\r\n2.炒锅上火，注入底油，热时下入浆好的肉片煸炒，待肉片断生炒香后下入葱姜蒜、泡辣椒炒一炒，再下入酱油、料酒、味精、糖、醋，汤开后下配料，勾芡倒入碗内。\r\n3.油锅上火烧热至七、八成热时下入锅巴，将锅巴炸酥后捞入盘中，将炸锅巴的热油用手勺盛半勺倒在锅巴上，上席时将碗内的肉片连汁浇在锅巴上即可。', NULL, NULL, NULL, '瘦嫩猪肉150克、锅巴250克、冬笋50克、鲜蘑50克、泡辣椒少许、白糖50克、醋25克、盐l0克、酱油15克、黄酒20克、毛汤500克、水团粉25克、葱、姜、蒜适量。');
INSERT INTO `recipe` VALUES (1975, 65, '合川肉片', '猪肉切成长约4厘米、宽4厘米、厚0.3厘米的片，加盐、料酒、鸡蛋、豆粉拌匀。水发玉兰片切成薄片，泡辣椒去籽切成菱形，姜、蒜切片，葱切成马耳朵形，用酱油、糖、醋、味精、水豆粉、鲜汤兑成芡汁。炒锅置旺火上，放油烧热（约120度），将肉片理平入锅，煎至呈金黄色时翻面，待两面都呈金黄色后，将肉片拨至一边，下泡辣椒、姜、蒜、木耳、玉兰片、菜心、葱迅速炒几下，然后与肉片炒匀，烹入芡汁，迅速翻簸起锅，装盘即成。', '颜色金黄，外酥内嫩，酸甜鲜香。', NULL, NULL, '猪腿肉400克、 水发玉兰片100克、水发木耳30克、鲜菜心50克、泡辣椒10克、姜、蒜、葱各10克、盐3克、酱油10克、醋10克、糖15克、味精1克、料酒10克、鲜汤40克、豆粉25克、鸡蛋25克、素油150克。');
INSERT INTO `recipe` VALUES (1976, 65, '荷包鱿鱼', '碱发鱿鱼片用开水泡去碱味沥干入锅，加汤、料酒、胡椒粉浸透，捞出晾凉后，用桃形模将鱿鱼片按成桃形，搌干水。鱼肉（去皮和带血肉）、猪肥膘分别用刀背捶成细茸，去净茸肉和筋膜，再用刀口排剁一遍，分别入碗。葱、姜拍破用清水泡上。将鱼茸用泡葱、姜的水调成糊状，下盐、胡椒粉。料酒。猪肉茸用力向一个方向搅动，再加入蛋清、水豆粉搅至发亮成为鱼糁。将蛋黄搅匀摊成蛋皮后，切成细丝。樱桃切成细牙。菜帮氽后，一半切成薄的小菱形块，另一半切成短丝。薄海带切成长约1厘米的细丝。用蛋清加干豆粉调成蛋清豆粉。将桃形鱿鱼片抹上蛋清豆粉，鱼糁', '形美色艳，汤清味鲜，质软嫩滑。', NULL, NULL, '水发鳅鱼300克、鱼肉50克、猪肥膘肉50克、蛋黄10克、樱桃10克、绿色菜帮25克、海带10克、发菜10克、料酒25克、胡椒粉3克、盐4克、蛋清30克、豆粉20克、味精0.5克、葱、姜各15克、清汤750克。');
INSERT INTO `recipe` VALUES (1977, 65, '干煸鳝背', '1.鳝鱼去骨后切成块，下油锅煸干，加泡辣椒、豆瓣酱、花椒、酒酿、糖、味精、酱油等作料。\r\n2.再放些汤、等它慢慢收干，加葱、姜、蒜片、最后用湿菱粉一收即好。', '味鲜香酥嫩，金黄色。', NULL, NULL, '鳝鱼500克、酒酿20克、葱姜、白糖3克、蒜片、味精、泡辣椒25克、酱油4.5克、豆瓣酱少许、 湿菱粉6克、花椒粉。');
INSERT INTO `recipe` VALUES (1978, 65, '干烧鸡翅', '1.将鸡翅用精盐、料酒、胡椒面码味，鸡蛋、干豆粉调成糊，将鸡翅拌匀。板油去皮切成粒，用热油炒熟。 \r\n2.锅内油烧至六成热，将鸡翅逐条放入，炸至呈金黄色捞出。\r\n3.倒去锅中余油，将豆瓣炒出红色，加汤稍煮，捞去豆瓣渣，放入油丁、姜、蒜、葱，加酱油、料酒、白糖，烧透入味，加味精，随即将汁收浓，装盘即成。', '以鸡翅为主料，干烧制成，成菜色泽深红，鸡翅香嫩，味咸鲜辣。', NULL, NULL, '鸡翅250克，猪板油75克，花生油50克，姜粒，蒜粒各15克，豆瓣30克，酱油、精盐各5克，味精2克，胡椒面1克，粗葱花、料酒各25克，白糖10克，鸡蛋2个，干豆粉40克，清汤150克。');
INSERT INTO `recipe` VALUES (1979, 65, '干烧鱼翅', '1.将干鱼翅加清水，放在小火上焖四小时捞起，擦去翅面上的沙，再浸入开水，用微火焖四小时，翅可发透，捞出拆去骨，放入旺火锅内，再加清水（没过鱼翅）、葱姜、酒、用微火焖透，将原汤倒掉，这样连换连焖四五次，鱼翅腥臭味可以去净。然后用纱布将鱼翅包起，放在鸡汁汤内炖烂（鸡汤要没过鱼翅）。\r\n2.取去包，仍将鱼翅放在原鸡汁中，加酱油、味精、酒、糖、盐、葱、姜、猪油、胡椒粉等调味，最后下菱粉，烧到汁干时，再滴少许鸡油即好。', '味鲜脆，颇名贵，一向列为珍馐之一。', NULL, NULL, '干鱼翅500克、鸡汤适量、猪油125克、湿菱粉32.5克、味精、白糖、葱花、姜、黄酒、鸡油、酱油、精盐、胡椒粉。');
INSERT INTO `recipe` VALUES (1980, 65, '宫保鸡丁', '1.将鸡脯肉剞十字花刀，切成丁，装碗加酱油、盐、料酒调味后，用水淀粉拌匀。葱、姜、蒜切片。用酱油、料酒、盐、白糖、醋、味精、清汤、水淀粉调成芡汁。干红辣椒去蒂去籽，切成节。花生米用温水泡涨去皮用油炸脆。\r\n2.锅内油烧至五成热，下干辣椒、花椒，将其炸成棕红色，将鸡丁放入炒散，放姜、葱、蒜、辣椒面炒匀，烹入芡汁，快速翻炒，加花生米翻两下，起锅装盘即成。', NULL, NULL, NULL, '鸡肉500克，花生米l00克，葱50克，姜30克，蒜20克，干辣椒20克，花椒5克，辣椒面5克，酱油50克，醋l0克，盐5克，白糖20克，料酒50克，味精5克，湿淀粉50克，花生油150克，汤20克。');
INSERT INTO `recipe` VALUES (1981, 65, '宫保牛肉', '1.牛肉切成方丁，加盐、酱油、料酒淀粉拌匀稍腌。\r\n2.碗内放入白糖、盐、酱油、醋、料酒、味精、肉汤、湿淀粉，拌成汁。\r\n3.炒锅内放入油烧至六成热，放入干红辣椒末，炸至呈棕红色，加入花椒，稍后倒入牛肉丁炒散，再放葱、姜、蒜炒出香味，倒入兑好的味汁，边倒边翻炒，最后放花生米炒匀，出锅装盘即成。', NULL, NULL, NULL, '牛肉250克，油炸花生仁、干红辣末、适量，酱油20克，白糖10克，花椒少许，葱末15克，姜片、蒜片、醋各5克，盐、味精、料酒、湿淀粉各少许，肉汤50克，熟菜油适量。');
INSERT INTO `recipe` VALUES (1982, 65, '宫保肉丁', '1.将猪肉切成丁放入酱油、料酒、鸡蛋、淀粉，上浆抓匀。葱剖开，切成段。姜、蒜切成片。\r\n2.炒锅上火，加油将花生米放入，用中火炒至成熟且脆香，放入盘中待用。\r\n3.将葱、姜、蒜、酱油、料酒、醋、盐、味精、糖、淀粉放入碗中，调成汁待用。\r\n4.炒锅上火，放油，放入花椒、辣椒煸炒，至花椒黑、辣椒紫时，将肉丁放入煸炒，再加入辣椒面同炒，炒出红油，待肉成熟后将汁倒入，翻炒均匀，随即放入花生米，炒匀后即可。', NULL, NULL, NULL, '猪瘦肉150克、花生米l00克、酱油l0克、醋3克、味精2克、盐3克、料酒3克、糖l0克、辣椒20克、花椒l0克、辣椒面2克、淀粉5克、鸡蛋半个、葱3克、姜3克、蒜2克、油30克。');
INSERT INTO `recipe` VALUES (1983, 65, '豉椒鸡片', '1.锅上火，下猪油烧热，放入火腿片略炸，倒入鸡汤，下鸡片、黄酒、豆豉、花椒烧沸片刻。\r\n2.加入精盐、菠菜心烧入味，用水淀粉勾薄芡，起锅装盘即可。', NULL, NULL, NULL, '熟鸡片50克，火腿片25克，菠菜心25克，黄酒25克，精盐3克，猪油50克，豆豉20克，花椒5克，淀粉10克，鸡汤250克。');
INSERT INTO `recipe` VALUES (1984, 65, '锅贴鸡片', '1.把冬笋和生鸡胸脯肉切成宽2.5厘米、长4厘米的薄片。肥膘肉同样大小的片，在片上扎一些小眼。葱、姜均切片。火腿肉切细末。圆白菜洗净后切细丝。\r\n2.用蛋清和湿淀粉调成稀糊。再把葱、姜、料酒，味精、盐合在一起，将鸡片腌一下入味。用净布揩去肥膘肉上的油脂，继而将蛋糊抹在肥膘肉上，放上一片冬笋，再撒上火腿末。此时，把腌鸡片用的葱、姜挑去，把蛋糊与鸡片拌均匀后，放在笋片上。\r\n3.烧锅注油，将鸡肉、肥膘肉面朝下一片一片地放入热油锅底，加火煎至底部发黄、鸡肉已熟的程度，即可取出，盛盘放在一端。圆白菜丝用番茄酱、糖', '鲜香嫩脆，形态美观。', NULL, NULL, '鸡胸脯肉250克，猪肥膘肉200克，冬笋200克，熟火腿70克，圆白菜200克，鸡蛋1个，香油20克，番茄酱30克，醋10克，白糖20克，椒盐25克，湿淀粉30克。');
INSERT INTO `recipe` VALUES (1985, 65, '荷包豆腐', '1.将豆腐表面粗皮去掉，过细箩成泥。火腿剁成细末。菠菜芯洗净。 \r\n2.取蛋清、5克盐、3克味精、胡椒粉，10克料酒、20克湿淀粉与豆腐泥对一起搅匀。\r\n3.用小勺，抹上大油把豆腐泥挤成丸子，然后用手按平，上面撒上火腿末，上屉蒸5分钟，取出用汤漂上。\r\n4.将鸡汤烧开加入菠菜芯，稍煮后即加入豆腐丸子，用火煮透勾芡盛盘，再淋上鸡油便成。', '鲜嫩软滑，味美适口。', NULL, NULL, '南豆腐2块，菠菜芯50克，鸡蛋清50克，熟瘦火腿20克、大油10克，鸡油20克，盐5克，味清5克，胡椒粉1克，料酒20克，湿淀粉30克，鸡汤200克。');
INSERT INTO `recipe` VALUES (1986, 65, '红枣煨肘', '猪肘洗净，入沸水去血腥味，捞出冲洗净，红枣去核洗净。冰糖砸碎，一部分炒成糖汁。罐内放鸡骨垫底，上放猪肘，加入鲜汤、姜（拍松）、葱（挽结）、盐、糖汁，用旺火烧沸，撇去浮沫，移小火煨热，拣去葱、姜，下红枣、冰糖继续煨至肘子软糯汁浓，起锅装于大圆盘内（皮向上），原汁淋于肘子上即成。', '色红亮，肘子软糯，甜咸适口。', NULL, NULL, '猪肘750克、红枣50克、冰糖25克、姜10克、葱15克、盐3克、鲜汤750克。');
INSERT INTO `recipe` VALUES (1987, 65, '烩鸭四宝', '1.将鸭胰、鸭掌、鸭舌、鸭腰(切除腰臊)洗干净，放开水锅里用旺水煮，烧开后撇去浮沫，改文火煮熟后，沥汤待用。\r\n2.把鸭胰切成段，鸭掌切成条，鸭腰去皮剥开，鸭舌洗干净，然后都倒入开水里焯一下，捞出控净水。 \r\n3.把鸡汤倒入炒勺，放旺火上，再加入料酒、酱油、盐、毛姜水、味精，烧开后撇去浮沫，将汤调成金黄色，淋入湿淀粉，勾成稀稀的乳芡汤。\r\n4.再把鸭料(四宝)倒入，轻轻搅拌均匀，滴入葱姜油和香油，盛碗后，撒上胡椒粉即成。', '颜色金黄，汤汁清爽，质地脆嫩，味道鲜美。', NULL, NULL, '鸭胰100克，鸭掌50克，鸭舌50克，鸭腰30克，香油5克，葱姜油10克，鸡汤300克，胡椒粉少许，毛姜水适量，湿淀粉302克，料酒15克，酱油10克，盐2克，味精2克。');
INSERT INTO `recipe` VALUES (1988, 65, '金钱口蘑汤', '1.将口蘑洗净，片成片；再将鸡脯斩成茸，与菱粉、鸡蛋白、胡椒粉一起调匀。\r\n2.将鸡茸贴在各个金钱口蘑片上，再放上4小片火腿，中间放绿叶菜。上笼约蒸五分钟。\r\n3.将鸡汤烧开，稍加一些味精和盐，汤沸后，将蒸好的口蘑倒入汤内即好。', '味鲜美，色红青带白黑。', NULL, NULL, '发好的口蘑片20片（每只金钱大小）、鸡脯肉125克、鸡蛋白30克、火腿80片、鸡汤1碗、菱粉少许、绿叶菜、精盐、味精、胡椒粉。');
INSERT INTO `recipe` VALUES (1989, 65, '菊花榨菜鱼卷', '1.鱼肉洗净，横片成片切成二粗丝，入碗，入川盐、料酒、蛋清、白糖、麻油等拌匀。榨菜淘洗干净切成二粗丝。再将鱼丝、榨菜丝、姜丝、葱丝和匀成馅料。蛋清和细干淀粉调成蛋清淀粉糊，备用。 \r\n2.鸡蛋磕在碗内搅匀，入锅摊成蛋皮，再抹上蛋清，放入馅料，卷成直径约1.5厘米的卷，两端用蛋清淀粉糊封口，放入六成热的油锅里中浸炸至呈金黄色，外酥内熟时起锅稍晾，改切成菊花瓣状待用。 \r\n3.将鱼卷入盘堆摆成菊花形，再用黄瓜切刻成菊花叶片，安放在菊花下端，即成。', '成形美观大方，鱼卷皮酥馅嫩，榨菜鲜香浓郁，佐餐助酒佳。', NULL, NULL, '净草鱼肉350克，涪陵榨菜200克，鸡蛋8个，姜丝5克，葱丝5克，黄瓜300克，川盐，料酒，细干淀粉，胡椒粉，白糖，麻油，菜油，蛋清适量。');
INSERT INTO `recipe` VALUES (1990, 65, '烤扁担肉', '1.将肉用竹扦等尖锐物扎许多小眼(以便进味和烤时熟得快些)，用盐、味精揉搓，放于容器内，加入料酒、葱、姜、腌2小时左右。 \r\n2.将腌好的肉放于烤盘中用烤箱烤，随时注意防止烤焦。如烤时出现色深浅不均时，可用菜叶盖住深色处再烤，待快熟时刷香油，直烤至颜色一致熟透为止。吃时切片摆入盘中即成。', '味鲜香，肉细嫩。', NULL, NULL, '去骨扁担肉400克，香油25克，盐7克，料酒、葱各25克，净姜15克，味精5克。');
INSERT INTO `recipe` VALUES (1991, 65, '辣白菜', '1.将白菜洗净，撕成长条（或切成节），放入盐水里泡两小时后捞起来，再将白菜的水分挤干，放入盆中，上覆姜丝、泡辣椒丝。\r\n2.将麻油和干椒粉、花椒等烧滚，淋在菜上面，再加糖、醋，用锅盖盖数分钟即好。', '味辣脆酸甜，色白带红，四季皆宜。', NULL, NULL, '天津白菜（或黄芽菜）250克、白糖60克、麻油5.25克、香醋60克、花椒粉少许、干辣椒少许、姜丝少许、精盐少许、泡辣椒丝少许。');
INSERT INTO `recipe` VALUES (1992, 65, '荷叶蒸肉', '猪肋肉刮洗干净，切成长约5厘米、宽3.5厘米、厚0.5厘米的片。瘦肉切成与肋肉大小相等、厚3厘米的片。同放入盆中，加盐、酱油、胡椒粉、味精、醪糟汁、郸县豆瓣（剁细）、白糖、糖色、豆腐乳汁、姜末、葱花混合拌匀，腌渍入味。大米、花椒、八角（铡烂）混合后，用小火于炒成黄色，磨成粗米粉。泡辣椒去籽，切成斜刀块。青豆洗净沥干。鲜荷叶洗净，入沸水中烫一下，取出切成约15厘米长的等边三角形（共20片）。将米粉与腌渍好的肉片拌匀。然后取荷叶一片，放肋肉一片，再放青豆数粒，泡辣椒一节，上面再放瘦肉一片，包上，共做20个。排', '外绿内黄，质鲜酥烂，荷叶清香，咸鲜略甜，风味别具。', NULL, NULL, '猪肋肉400克、猪瘦肉350克、荷叶五张、大米粉20克、泡辣椒20克、青豆50克、盐3克、酱油15克、胡椒粉2克、味精1克、醪糟汁5克、郸县豆瓣20克、白糖50克、糖色10克、豆腐乳汁15克、姜10克、葱15克、花椒2克、八角3克。');
INSERT INTO `recipe` VALUES (1993, 65, '红辣椒爆炒鳝片', '1.鳝鱼先开膛，去掉内脏，清洗干净，如果按传统做法，鳝鱼去掉内脏即可，不必清洗。然后用刀侧把鳝鱼拍平，再切成一公分一小段，用盐、料酒腌制约5分钟。 \r\n2.食用油起镬，先把鳝鱼用温油滑一次，捞出一旁再起镬，将姜丝、花椒、蒜末置入镬中，煸出香味后，投入鲜红辣椒并炒成五成熟，这时再加入刚才滑出的鳝鱼段，接着加入调料、高汤，痛痛快快爆炒两分钟，即可出镬上碟。', '鳝鱼肉嫩而味鲜美，据古方记载，它还可以去湿毒，并有点补血作用。', '这菜到不到家，就要看炒的功夫如何跟鳝鱼段的大小巧妙配合。有些人喜欢吃较大段的鳝鱼，所以要切成两公分宽也是可以的，但爆炒时就要注意时间控制了。', NULL, '鳝鱼一条(稍微大一点)400克，鲜红辣椒约150克，姜丝约10克，蒜末10克，花椒5粒，料酒8克，胡椒少许，高汤10克，糖、盐、酱、酒等调料随意。');
INSERT INTO `recipe` VALUES (1994, 65, '红烧卷筒鸡', '鸡脯肉片成长约4.5厘米、宽约2.7厘米的薄片24张。火腿、冬笋、冬菇均切成长约2.5厘米的二粗丝各24根。鸡片摊开，将火腿、冬笋、冬菇丝各一根放于片的一端，顺裹成卷形，卷尾处抹上蛋清豆粉交口。整个卷再裹一层蛋清豆粉。炒锅置旺火上、下素油烧热（约200度），将鸡卷逐个顺锅边放入，炸至呈黄色捞出。烧热锅放鸡骨垫底，加好汤、红酱油、葱、姜、料酒、酱油、盐吃味上色，然后放入鸡卷，大火烧开后改用小慢火烧（约30分钟），取出鸡卷，摆放于蒸碗中（定成“三叠水”），将烧鸡原汁入碗，上笼蒸约10分钟取出，将原汁滗入锅中，', '色泽棕红，肉质细嫩，味极鲜美。', NULL, NULL, '鸡脯肉350克、熟火腿50克、冬笋50克、冬菇50克、蛋清50克、豆粉40克、素油500克、好汤750克、红酱油10克、葱、姜各10克、料酒10克、盐2克、酱油5克。');
INSERT INTO `recipe` VALUES (1995, 65, '红烧蹄筋', '1.将生蹄筋用水煮烂，而后捞出用凉水冲漂去胶汁，切成两段。葱剖成两半切断，姜切片。冬菇用水发透去腿，玉兰片用开水氽透后均洗净切成条形，火腿切成条片。\r\n2.用炒勺将油烧热后，先下葱、姜，再下冬菇、玉兰片煽炒，而后再下酱油、蹄筋、火腿、盐、料酒、汤，汤开后撇去浮沫用中火炖至入味时加味精，用湿淀粉勾芡，浇明油少许即成。', '味鲜美，入口糯，色红亮。', NULL, NULL, '鲜猪蹄筋250克，冬菇6克，玉兰片、火腿各50克，酱油、料酒、葱、淀粉各13克，姜8克，盐、味精各3克，汤250克。');
INSERT INTO `recipe` VALUES (1996, 65, '红烧猪蹄', '1.将猪蹄刮毛洗净，剁去爪尖，劈成两半，用水煮透后放入凉水中。姜、葱拍破待用。\r\n2.用炒勺将少许香油烧热，放入冰糖炸成紫色时放汤调至浅红色为度。\r\n3.加入猪蹄、料酒、葱、姜、盐、花椒，汤烧开后除去浮沫，用大火烧至猪蹄上色后，移至小火炖烂，收浓汁即成。', '味浓适口，肥而不腻。', NULL, NULL, '猪蹄750克，盐、葱各13克，姜8克，香油、料酒各25克，花椒5粒，冰糖50克，汤1300克。');
INSERT INTO `recipe` VALUES (1997, 65, '红油耳片', '1.取新鲜、体大的猪耳洗净，放入沸水锅中加热，煮至刚熟，取出，用一重物压平猪耳，自然晾凉。\r\n2.凉透的猪耳切成薄片，碗中加入盐、白糖、味精、红油辣椒、香油调成味汁。\r\n3.将耳片与调好的味汁、葱丝拌匀，装盘即成。', '香辣微甜，质地脆嫩。', NULL, NULL, '猪耳、红油辣椒、葱白、盐、味精、白糖、香油。');
INSERT INTO `recipe` VALUES (1998, 65, '回锅肉', '1.将肉切成4厘米宽的条，用开水煮熟改切成片，青蒜切成寸段。\r\n2.将白肉先下入热油中煸炒至肉出油卷起，即加入豆瓣酱、面酱炸出味后下青蒜和其它各种调料，再翻炒几下即成。', '鲜香，家庭口味(此菜在四川不用糖和酱油，而用甜红酱油)。', NULL, NULL, '猪后腿的二刀肉370克，青蒜(青椒、黄蒜薹亦可)70克，大油25克，面酱12克，酱油，料酒各12克，白糖5克，豆瓣酱、葱各5克，味精3克。');
INSERT INTO `recipe` VALUES (1999, 65, '火爆肚头', '肚头洗净去尽油筋，剞十字花刀，切成边长约2厘米的菱形块。兰片、香菌片成片。精盐、味精、胡椒粉、水豆粉、鲜汤兑成芡汁。炒锅置旺火上，下猪化油烧热（约200度），肚头加精盐、料酒、豆粉拌匀后，入锅爆炒，散籽翻花后，迅即下兰片、香菌、姜、葱、蒜、泡辣椒炒匀，烹入芡汁，颠匀起锅装盘即成。', '色形美观，紧汁亮油，质地脆嫩，咸鲜微辣。', NULL, NULL, '猪肚头400克、水发兰片50克、水发香菌30克、猪化油50克、精盐3克、料酒15克、豆粉30克、姜10克、蒜10克、葱15克、泡辣椒10克、胡椒粉2克、味精1克、鲜汤35克。');
INSERT INTO `recipe` VALUES (2000, 65, '火爆荔枝腰', '猪腰撕去膜，平片成两片，去尽腰臊洗净，先用斜刀，后用交叉剞成十字花纹，然后改成2.5厘米见方的块，入碗加盐、料酒、水豆粉拌匀。木耳洗净，冬笋切薄片，泡辣椒去籽，切斜刀块。姜、蒜切片，葱切马耳朵形。盐、酱油、胡椒粉、料酒、醋、白糖、味精、水豆粉、鲜汤入碗兑成芡汁。炒锅置旺火上，猪油烧热（约220度），下腰花爆炒推散，将配料一齐放入炒匀，烹入芡汁，推匀起锅装盘即成。', '形如荔枝，脆嫩鲜香，味美爽口。', NULL, NULL, '猪腰400克、水发木耳30克、冬笋50克、泡辣椒15克、豌豆苗50克。盐3克、料酒20克、豆粉30克、姜、蒜各10克、葱15克、酱油10克、胡椒粉2克、醋15克、糖30克、味精1克、鲜汤50克、猪化油50克。');
INSERT INTO `recipe` VALUES (2001, 65, '火爆腰花', '1.猪腰子平片成两块，去净油皮和腰臊，先反刀斜剞，再直刀剞成三刀一断的眉毛形，盐、湿淀粉码味上浆。黄瓜洗净，去瓤心，切成条。用盐、白糖、味精、酱油、醋、料酒、湿淀粉、鲜汤兑成滋汁。\r\n2.锅中放油烧熟，放入腰花炒散，加姜、蒜、葱、泡红辣椒、黄瓜条炒匀，烹汁收汁，起锅装盘。', '质地细嫩，咸鲜醇厚。', NULL, NULL, '猪腰子、黄瓜、姜、葱、蒜、泡红辣椒、盐、味精、酱油、白糖、醋、湿淀粉、鲜汤、熟菜油。');
INSERT INTO `recipe` VALUES (2002, 65, '鸡包鱼翅', '仔母鸡宰杀去毛后，从翅根开口去掉内脏，洗净后取出腿骨和颈骨待用。水发鱼翅洗净入沸水锅汆两次，去掉腥味。再加姜葱、料酒入沸水锅中烧沸，下鱼翅连汆两次捞出待用。火腿、冬笋切成二粗丝，与鱼翅拌和匀，装入鸡腹内，再用一块干净纱布把鸡包好放入盘内，开口处向上。猪肉洗净后切成3.3厘米长的条块，再将鸡腿骨、颈骨斩成3.3厘米长的段。炒锅在火上，加猪油烧至五成热（约110度），倒入猪肉、鸡骨，加酱油、胡椒、盐、料酒、姜葱和二汤，烧开后去掉泡沫倒入烧锅，再将包好的鸡放入锅中，待沸后，用小火煨至鸡粑翅烂时捞出，去掉纱布，将', '颜色鲜艳，形态美观，鱼翅浓香，鸡鲜酥烂。适于冬季食用。', NULL, NULL, '水发鱼翅250克、仔母鸡一只约750克、火腿25克、冬笋25克、猪肉100克、菠菜心25克、猪油25克、酱油10克、胡椒1克、盐5克、料酒15克、姜葱各15克、鸡油10克、二汤25克。');
INSERT INTO `recipe` VALUES (2003, 65, '鸡丝米粉', '1.先制出米油浆。即将米反复换水揉搓洗净，用凉水泡胀后再冲洗两次，去水后另加凉水600克，磨成细浆，再放入植物油搅动，使油浆混为一体。将细浆舀一勺放在抹好油的瓷盘内晃平上屉，用旺火烧开水蒸熟成薄片，揭下晾凉再切成韭菜叶宽的条形。鸡肉切丝。小白菜抽出筋洗净待用。 \r\n2.烧开鸡汤下入味精、盐、胡椒粉调好味。于此同时，烧开一锅水下入白菜和米粉条。小白菜烫熟后，把菜和米粉条挑入5个碗中，撒上鸡丝、注入汤便成。', '细软鲜嫩，老少皆宜。', NULL, NULL, '籼米(无粘性大米)500克，鸡丝150克，小白菜100克，植物油40克，鸡油25克，盐12克，味精8克，胡椒粉1克，鸡汤适量。');
INSERT INTO `recipe` VALUES (2004, 65, '夹沙肉', '猪肉刮洗干净，入锅煮至刚熟捞出晾冷，用刀切成长约8厘米、宽4.5厘米、厚0.7厘米的片（16片），再将肉片逢中片一刀，使成皮相连的两张薄片。锅内放红糖炒化，加洗沙、猪油炒匀，加玫瑰捣匀。然后将馅塞于肉片夹层中，压成扁形，逐片摆于蒸碗中成圆形。糯米淘洗后用清水浸泡半小时，用净布包好，上笼蒸20分钟，取出用清水浸一次，再入笼蒸至把软，取出加红糖、猪油拌匀，放于摆好肉片的蒸碗中，上笼蒸夹沙肉（约2小时），取出扣于盘中，撒上白糖即成。', '甜香粑糯，腴而不腻。', NULL, NULL, '猪肥膘肉500克、洗沙75克、玫瑰25克、糯米100克。红糖50克、猪油50克、白糖25克。');
INSERT INTO `recipe` VALUES (2005, 65, '家常海参', '海参片成上厚下薄的斧楞片，用好汤煨两次捞起。猪肉剁细。青蒜苗切成大粗花。黄豆芽去净根脚。豆瓣剁细。炒锅置火上，下猪油烧至四成热时下猪肉，放料酒、盐肥散，盛盘。再将锅洗净入下猪油烧至六成热时，下豆瓣炒出香味呈红色时，加入鲜汤烧沸，将豆瓣渣捞出，放入海参及肥好的猪肉、红酱油、料酒推转，将锅移至小火上煨，待煨至亮油时，勾薄芡，下蒜苗、味精、麻油推转。另用一锅烧猪油，将黄豆芽炒熟，装入盘中垫底，然后将海参连汁淋于豆芽上即成。', '汁色棕红，咸鲜微辣，海参粑糯，肉馅软酥。', NULL, NULL, '水发海参300克、猪肥瘦肉50克、黄豆芽100克、青蒜苗20克、郸县豆瓣20克、猪油65克、料酒20克、盐5克、鲜汤300克、红酱油10克、味精0.5克、麻油10克、豆粉10克。');
INSERT INTO `recipe` VALUES (2006, 65, '姜丝牛肉', '1.姜切丝。牛肉切薄片，加小苏打、酱油、胡椒粉、淀粉、绍酒、姜末、花生油和清水l00克，腌1小时。\r\n2.炒锅上火，油烧至六成热，放牛肉片，拌炒，待牛肉色白，倒出沥油。\r\n3.锅内留油复上火，放葱姜片、白糖、酱油、味精、清水少许，烧沸后用水淀粉勾芡，放入牛肉片、姜丝拌匀，起锅装盘。', NULL, NULL, NULL, '嫩牛肉300克、嫩姜150克、绍酒40克、酱油20克、白糖l0克、小苏打5克、水淀粉20克、胡椒粉l克、葱姜片20克、姜末2克、花生油500克、味精少许。');
INSERT INTO `recipe` VALUES (2007, 65, '酱爆肉', '1.将肉放入滚水煮七成熟捞出，切成长2寸左右，厚2分的薄片，放入旺猪油锅炒透（至肉片发卷）。\r\n2.再放葱、姜、酱油、甜面酱、黄酒、糖、最后加芥菜、蒜苗，炒拌二十秒钟起锅。', '香带酱味，呈深酱色，四季皆宜。', NULL, NULL, '坐臀猪肉200克、芥菜65克、白糖4克、酱油4克、甜面酱10克、黄酒6.5克。');
INSERT INTO `recipe` VALUES (2008, 65, '酱爆鸭块', '1.将烧鸭切成长1寸左右，宽4分的长方块，先放入旺火油锅内，把水分爆干取出。\r\n2.取笋块下锅，加甜面酱、酒、姜、蒜末等炒拌，再将鸭块，葱节、糖和鸡汤放入，再翻几下把汤收干，起锅时再放麻油。此菜须火力，先用旺火，后用微火，使鸭块酥透入味。', '味酥而香，色酱红。', NULL, NULL, '烧鸭块300克、笋块40克、甜面酱20克、黄酒3克、蒜末3克、姜20克、麻油3克、鸡汤1匙、白糖少许、葱节30克。');
INSERT INTO `recipe` VALUES (2009, 65, '酱酥桃仁', '桃仁置容器中，加开水稍焖（约2-3分钟），待桃仁皮起皱，捞出撕去皮衣。炒锅置中火上，下猪化油烧热（约120度），下桃仁炸至金黄色时捞出。锅洗净置火上，加清水、白糖不断搅动，至糖化，糖汁翻起小泡时，加入甜酱，搅拌均匀，待再翻大泡时，将锅端离火口，把炸好的桃仁倒入，轻轻搅匀，使糖汁均匀粘裹在桃仁上，冷却后装盘即成。', '香酥化渣，酱香味浓，甜味突出，回味带咸。', NULL, NULL, '干核桃仁500克、猪化油500克、白糖100克、甜酱50克。');
INSERT INTO `recipe` VALUES (2010, 65, '椒盐八宝鸡', '母鸡宰杀洗净后，整料出骨。洗净后，抹料酒、盐腌渍备用。鲜豌豆米入沸水中氽过，去壳后漂于清水中。糯米洗净入锅煮至过心。莲米褪衣去心后和苡仁、芡实淘洗泡胀，入碗加水淹没，上笼蒸烂。金钩泡胀，与火腿、香菌均切成细粒。然后将以上各料混合，加盐和匀，由鸡颈刀口处瓤入鸡腹中，以竹签锁住开口，先入沸汤中煮几分钟捞出，将鸡翅翻到背上盘好，夹住鸡头，再装入蒸碗中，上笼蒸约两小时（以用竹筷能将鸡翅戮破为度）。取出晾凉，搌干水气，抹上酱油、蛋清、豆粉。炒锅置旺火上，放油烧热（180度），将鸡放入炸呈金黄色时捞起，淋香油，去掉竹', '颜色金黄光亮，美观大方，皮酥肉嫩，馅味鲜美。', NULL, NULL, '肥嫩母鸡一只（约1000克）、糯米25克、鲜豌豆25克、莲米25克、芭仁25克、芡实25克、金钧10克、火腿15克、香菌15克、料酒15克、盐5克、酱油10克、蛋清15克、豆粉15克、素油500克、香油10克、椒盐15克。');
INSERT INTO `recipe` VALUES (2011, 65, '椒盐蹄膀', '1.将蹄膀洗净剖开，加黄酒、酱油、盐、葱、姜，上笼蒸熟取出，去骨。\r\n2.用湿菱粉调匀，涂在蹄膀肉上，放入猪油锅内，两面煎黄，再放入旺油锅，炸至发脆，捞出切成小方块，再撒上椒盐即好（花椒末及盐合炒熟）。', '味香脆，适用于秋冬季节。', NULL, NULL, '蹄膀1只、干菱粉65克、酱油6.5克、椒盐6.5克、葱花4克、姜4克、黄酒6.5克、精盐2.5克。');
INSERT INTO `recipe` VALUES (2012, 65, '金钱海参', '水发灰刺参洗净后，用清汤煨入味，取出搌干表面水分，鸡脯与肥膘肉剁细制成鸡糁。蛋清加豆粉调成蛋清豆粉。火腿切成二粗丝，黄秧白心焯熟。先在海参腹内逐一抹上蛋清豆粉，再酿入鸡糁，火腿丝顺放鸡糁中，然后将海参摆成圆形，上笼用大火蒸熟取出，晾冷后横切成厚1.5厘米的片，平铺于蒸碗内，上面放焯好的黄秧白入笼馏热。另在锅内烧开清汤，加盐、味精、料酒、胡椒粉等调料，勾二流芡，取出笼内海参扣入大盘中，将勾好芡的滋汁淋于盘中，再淋上些许鸡油即成。', '形似古钱，汁色乳白，质地软糯。适于春冬食用。', NULL, NULL, '水发灰刺参400克、鸡脯50克、肥膘肉50克、火腿25克、黄秧白心50克、蛋清25克、豆粉25克、料酒10克、清汤500克、盐2克、味精0.5克、胡椒粉1克、鸡油10克。');
INSERT INTO `recipe` VALUES (2013, 65, '金钱鸡塔', '鸡脯肉洗净去筋，与生猪肥膘肉分别用刀背捶茸后混合，加清水、蛋清、盐制成鸡糁。另将煮熟的猪肥膘肉切成直径约4厘米、厚约0.4厘米的圆形片，共24片。熟火腿剁成细片。韭菜切成长约1厘米的段，漂入清水中。蛋清加干豆粉调匀。将圆形猪肉片铺于盘中，用热布搌干表面油质，抹上一层蛋清豆粉，然后将鸡糁做成直径约为2厘米的圆珠，放于肉片上，并抹平，再取少许火腿末放在圆珠上粘稳，即是金钱鸡塔坯。炒锅置火上烧热，取鸡塔坯肥膘向下贴于锅中，烙至肥膘呈金黄色时起锅，装于条盘中，将韭菜滤干水，用盐、醋、香油拌匀，摆于盘的两端即成。', '形似金钱，颜色鲜艳，入口酥香，脆嫩味美，为佐酒佳肴。', NULL, NULL, '鸡脯200克、猪肥膘肉100克、熟瘦火腿50克、白头韭菜50克、蛋清50克、盐3克、醋10克、香油10克。');
INSERT INTO `recipe` VALUES (2014, 65, '龙眼咸烧白', '猪肉刮洗净，入汤锅煮熟，捞起搌干，在皮上抹红酱油少许，入油锅内烙皮，至呈褐红色时捞出，入汤锅中浸至皮显皱纹时捞出，片成长约8厘米、宽4厘米、厚0.4厘米的片。泡红辣椒切成短节。肉片每片裹辣椒一节，豆豉二、三粒，呈卷筒状，立装于蒸碗内，再放芽菜、红酱油、盐，上笼蒸熟取出，翻扣于盘中即成。', '肥而不腻，粑而不烂，味咸鲜香。', NULL, NULL, '猪肉750克、芽菜100克、泡红辣椒25克、豆豉25克、红酱油35克、盐2克、素油150克、汤750克。');
INSERT INTO `recipe` VALUES (2015, 65, '芦笋牛肉', '1.芦笋切菱形片。牛肉去筋络，切成薄片，放入碗内加小苏打、酱油、胡椒粉、淀粉、绍酒、姜末和清水腌l0分钟，加入花生油，再腌l小时。\r\n2.炒锅内放油，烧至六成热，放入牛肉片，拌炒，色白时，倒入漏勺沥油。\r\n3.锅内留油，放入葱姜片、白糖、酱油、味精、清水少许，烧沸后，用水淀粉勾芡，放入牛肉片、芦笋段，拌匀，起锅装盘。', NULL, NULL, NULL, '牛肉四两、芦笋150克、绍酒40克、酱油20克、白糖、小苏打、胡椒粉少许、水淀粉、葱姜片各20克、姜末2克、花生油500克、味精少许。');
INSERT INTO `recipe` VALUES (2016, 65, '麻酱凤尾', '离笋尖去皮，修整齐后，在粗端改成四瓣（切开部分为莴笋尖长度的3/5），放入沸水锅内焯至断生捞出，撒上少许细盐拌匀摊开，整齐摆放于盘内，淋上由芝麻酱、酱油、味精、芝麻油调成的味汁即成。', '质地脆嫩，酱香浓郁，鲜美可口，清爽宜人。', NULL, NULL, '嫩莴笋尖400克、细盐3克、芝麻酱10克、酱油10克、味精0.5克、芝麻油10克。');
INSERT INTO `recipe` VALUES (2017, 65, '麻辣豆腐', '1.牛肉剁碎，豆豉剁细末，葱、姜切末，青蒜剖开切段，豆腐切1.5厘米见方的丁块，用开水泡上。 \r\n2.炒勺注油烧热，先下牛肉，煽炒去水分后，将豆瓣酱、葱、姜末和豆豉下勺炒酥，再下入辣椒粉，炒变色时注汤、酱油和料酒，再下入豆腐，用小火炖透入味，再放入味精后，用湿淀粉勾芡，撒上青蒜。花椒粉成盘即成。', '味麻辣香，为四川便菜。', NULL, NULL, '主料：南豆腐2块，瘦牛肉100克，青蒜50克。\r\n调料：植物油100克，豆瓣酱50克，辣椒粉、酱油各8克，料酒20克，四川豆豉20克，味精8克，湿淀粉20克，汤15克，花椒粉1克，葱、姜各10克。');
INSERT INTO `recipe` VALUES (2018, 65, '金钱芝麻虾', '鲜虾仁、猪肥膘肉分别洗净捶茸，加鸡蛋清、盐、味精、胡椒粉、干豆粉混合搅成糁。芝麻洗净晾干。土司修成直径约3.3厘米、厚0.6厘米的片，共24片。蛋清加干豆粉搅匀成蛋清豆粉。生菜洗净用白糖、香油、醋拌好待用。将土司片平铺在案板上，先抹上蛋清豆粉，再将糁糊抹上，然后撒上芝麻（用手稍拍）。锅置火上，下菜油烧热（约1500度），将土司粘有芝麻一面向下，投入锅中炸至呈金黄色时捞起，装于盘的一端，另一端摆上拌好的生菜即成。', '酥脆香嫩，咸鲜适口。', NULL, NULL, '鲜虾仁400克、猪肥膘肉400克、芝麻50克、土司100克、鸡蛋50克、生菜25克、菜油500克、豆粉50克、盐5克、味精0.5克，胡椒粉2克、白糖5克、香油5克、醋5克。');
INSERT INTO `recipe` VALUES (2019, 65, '开水白菜', '黄秧白菜心，抽去筋，洗净，入沸水锅中焯至刚断生，捞出立即入冷开水中漂凉。然后取出修整齐，理好放入汤碗中，加料酒、味精、川盐、胡椒粉和清汤，上笼用旺火蒸烫约2分钟，取出，滗去汤，再用沸清汤过一次，最后将烧沸的特制清汤撇去浮沫，灌入汤碗即成。', '汤清澈，菜鲜嫩，清香淡雅。', NULL, NULL, '黄秧白菜心500克、清汤1000克、精盐3克、胡椒粉2克、味精1克、料酒10克。');
INSERT INTO `recipe` VALUES (2020, 65, '口袋豆腐', '将豆腐去皮，切成6厘米长、2厘米见方的条，共30条。冬笋切成骨牌片。菜心洗净。用炒锅两口，分置于两个火炉上，其中上锅放入沸水500克，加食用碱保持微沸。另一锅放熟菜油烧至七成热（约175度），将豆腐条分次放入，炸呈金黄色捞出，放入碱水锅内泡约4分钟，捞起放入清水中退碱，然后再第二次放入碱水锅中泡约5分钟后，用清水再漂。将炸泡好的豆腐再在沸水中过一次，并用肉汤汆2次。将奶汤入锅中烧沸，加冬笋、胡椒粉、料酒、川盐烧沸后，下豆腐条、菜心、味精，起锅盛入汤碗即成。', '汤白菜绿，味咸鲜而醇香。', NULL, NULL, '豆腐750克、冬笋50克、菜心50克、食用碱10克、熟菜油500克、肉汤500克、奶汤500克、胡椒粉2克、料酒10克、川盐3克、味精1克。');
INSERT INTO `recipe` VALUES (2021, 65, '辣子脆肠', '1.将乳肠洗净，划成破刀，切成小段，用碱、盐码味，入沸水锅中氽至断生，捞起。\r\n2.锅置火上，下油烧热，放干辣椒炒至棕红色，下花椒炒香，再下乳肠、辣椒粉炒至上色，烹入料酒，加入姜、蒜、葱、盐、味精、白糖簸匀即成。', '菜色泽棕红发亮，质地脆嫩爽口，麻辣味浓，咸鲜醇香，略带回甜。', NULL, NULL, '乳肠、干辣椒、花椒、辣椒粉、马耳朵葱、盐、味精、胡椒、料酒、姜蒜片、白糖、醋、碱、色拉油各适量。');
INSERT INTO `recipe` VALUES (2022, 65, '辣子鸡丁', '1.鸡肉切成块形小丁，加入鸡蛋清、干淀粉、精盐调拌均匀，连同配料荸荠丁放入热油锅内炒，l0秒钟后，沥去油，放入泡辣椒、葱、姜等同炒。\r\n2.将糖、黄酒、酱油和湿淀粉、醋等调和，乘热倒入锅内炒几下即可。', NULL, NULL, NULL, '嫩鸡肉150克，干淀粉6克，泡辣椒6克，酱油、白糖、精盐、菊花、姜、湿淀粉、黄酒、香醋、味精适量，鸡蛋1只，荸荠丁。');
INSERT INTO `recipe` VALUES (2023, 65, '栗子白菜', '1.将大白菜芯削去帮皮、抽筋顺切成条，清洗干净，用开水氽透后捞出冲凉，修成长短一致的条并理顺，整齐地放在盘内，撒上3克盐，注入清汤250克上屉蒸5分钟，栗子煮软去壳和内皮，稍微炒一下捞出来放在碗里，加些汤上屉蒸烂。\r\n2.将炒勺烧热注鸡油45克，把白菜稍炒几下即加入鸡汤、料酒、盐、栗子(去汁)用小火烧一下，将白菜整齐地摆入盘内，再把汁调好味，加上味精用湿淀粉勾成稀芡浇在白菜上，淋上鸡油即成。', '色泽鲜艳，味香适口。', NULL, NULL, '大白菜芯900克，栗子500克，大油800克(实耗约50克)，鸡油60克，盐、味精各7克，料酒25克，鸡汤15克，湿淀粉10克。');
INSERT INTO `recipe` VALUES (2024, 65, '凉拌怪味鸡', '1.番茄、青瓜洗净，切片排于碟边。芝麻以白镬炒香备用。\r\n2.鸡去皮洗净，放入滚水中以慢火浸约十二分钟至鸡熟，切块放在碟中。\r\n3.将怪味汁料混合，淋在鸡肉上，洒上白芝麻即可进食。', '怪味是四川人最喜爱的调味，是用生抽、醋、辣椒油、糖、芝麻酱、蒜茸、花椒粉拌匀而成，包括甜酸咸辣麻五味俱全，凉拌怪味鸡最适宜作为下酒小食。', NULL, NULL, '鸡1/4只、番茄两个、青瓜半个、白芝麻一汤匙。怪味汁料：生抽、洋葱各一汤匙、芝麻酱一汤匙、花椒粉、蒜茸、姜茸各1/4茶匙、辣椒油、酱各半茶匙。');
INSERT INTO `recipe` VALUES (2025, 65, '麻婆豆腐', '1.先将嫩豆腐切成3分斜方块，用滚水煮二分钟，以去除石膏味，沥干水分。\r\n2.另起旺猪油锅，将牛肉末和豆瓣酱一起炒，再放辣椒粉、酱油、豆豉、辣油、黄酒、盐、蒜末，炒到入味，再放豆腐和鸡汤100克，用小火焖成浓汁，再加菱粉收一下，放葱末、花椒粉、味精即好。', '味麻辣，四季皆宜。', NULL, NULL, '牛肉末（或猪肉末）65克、葱末4克、辣油4克、豆豉10克、花椒粉、大蒜末、酱油、嫩豆腐200克、辣椒粉、精盐、鸡汤130克、菱粉、黄酒。');
INSERT INTO `recipe` VALUES (2026, 66, '板栗烧鸡', '1.将鸡宰杀、拔毛、剖腹去内脏洗净，把鸡头、翅膀和脚至胫部切下，然后将鸡对剖开，将鸡肉斩成长3厘米、宽2厘米的长方块，把鸡头、翅膀和脚也斩成3厘米的段。\r\n2.锅置旺火上，下100克菜油烧热，然后将鸡块入热油锅中爆炒，待鸡肉变硬时，加入料酒及姜块、豆瓣、花椒，炒至水分渐干、溢出香味时，即掺入适量水，放入少量盐、酱油和白糖、八角等。\r\n3.加盖焖烧至六七成熟时，再加入板栗同烧15分钟左右即可。起锅时加入葱段及味精，有少量汤汁为宜。', '鸡肉酥烂，板栗香甜，时令佳肴，美味可口。', NULL, NULL, '活雌鸡或阉鸡1只，约重1500-2000克，菜油（或化猪油）100克，豆瓣25克，老姜50克，大葱10克，白糖或冰糖25克，花椒、料酒、酱油、精盐、味精、八角等适量。');
INSERT INTO `recipe` VALUES (2027, 65, '冷酥方', '此菜完全用干烤方法制成，先将肋条肉加以修整，成为整齐而又方正的肉块，然后照以下方法烤制：\r\n1.先在肋条这面用铁签戳些小孔，烤时，使肉油能从小孔滴出，但深度不能及皮，以免裂开。再在皮的这面搽满麻油，把整块肉平叉在烤叉上，叉时不要触及肥膘，要求尽量靠近肋条的地方。再拿肉皮这一面在大火上来回烤，先烤四周，再烤中间，至皮起泡，呈现黑壳状，取出用干净湿布盖好。\r\n2.等肉皮回软，揭去湿布，将皮上黑壳部分用刀剖净，再在皮上用麻油全面涂一涂，然后移在钢炭（木炭）烧的半武火上烤，先烤肉的这一面，至皮色又一次变为黑灰状，', '皮香脆，肉油而不腻，并有麻辣味，四季皆宜。', NULL, NULL, '上肋条肉1块约2750克、花椒末少许、麻油75克、酱油少许、细葱少许、花椒盐少许。');
INSERT INTO `recipe` VALUES (2028, 65, '连锅汤', '1.肉用开水煮透后捞出，切成大薄片，萝卜切0.2厘米厚、3厘米宽、4厘米长的片，姜拍破，整葱。\r\n2.用酱油、辣椒油、花椒粉、豆瓣酱和汤对成汁。\r\n3.将萝卜下入原汤加花椒和葱，大火煮烂后下入肉片，稍煮后下入料酒、味精、胡椒粉、盐即可。食时蘸汁。', '汤鲜可口，肉嫩。', NULL, NULL, '猪后腿肉200克，白萝卜75克(圆白菜、大白菜均可)，辣椒油、豆瓣酱、葱各13克，胡椒粉、花椒粉少许，料酒13克，酱油50克，味精3克，花椒5粒，姜8克。');
INSERT INTO `recipe` VALUES (2029, 65, '龙眼甜烧白', '红枣在炭火上将皮烧得微微带焦，用清水浸泡20分钟，去掉皮核，卷上洗沙。猪肥膘肉煮熟，捞起晾冷，切成长约7厘米、宽2厘米、厚0.3厘米的片，每片裹上一个枣卷，成圆筒，立放于蒸碗中。糯米经淘洗浸泡后，上笼蒸肥，取出加红糖、猪油拌匀，装入放有肉卷的蒸碗中作底，上笼蒸粑（约半小时）。炒锅内放白糖、清水少许，勾芡成水晶滋汁，取出蒸好的烧白，翻扣于圆盘中，淋上滋汁即成。', '外形美观，甜香肥糯。', NULL, NULL, '猪肥膘肉500克、 红枣75克、洗沙50克、糯米100克、红糖15克、猪油10克、白糖20克、水豆粉10克。');
INSERT INTO `recipe` VALUES (2030, 65, '芪蒸鹌鹑', '将鹌鹑杀后，去毛、内脏和爪洗净，入沸水中汆，1分钟捞出待用。将黄蔑用湿布擦净，切成薄片，分别装入鹌鹑腹用，放入蒸碗，注入清汤，用湿棉纸封口，上笼蒸约30分钟，出笼揭去棉纸，滗出原汁，加盐、胡椒粉等调好味，再将鹌鹑扣入碗内，灌入原汁即成。', '清鲜软烂，汤汁鲜香滑爽，并具益气补脾、利水消肿之功效。', NULL, NULL, '鹌鹑2只、黄蔑10克、生姜10克、葱10克、胡椒粉2克、盐3克、清汤100克。');
INSERT INTO `recipe` VALUES (2031, 65, '芹黄鱼丝', '活鲤鱼宰杀，去鳞、鳃、内脏后洗净，擦干水分，对剖剔骨后，将鱼净肉切成长约7厘米、宽厚各0.4厘米的丝，蛋清和干豆粉搅匀，加料酒、盐和匀，与鱼丝拌匀。芹黄切成长约7厘米的节。泡红辣椒去籽后切成丝。姜、蒜切细粒。酱油、白糖、醋、味精、香油、水豆粉加好汤兑成芡汁。炒锅置旺火上，下猪油烧热（约120一150度），下码好味的鱼丝，随即将锅端离火口，用竹筷将鱼丝拨散，鱼丝变白后，抹去一部分油，锅内留汆油约50克，再置火口上，鱼丝推至锅边，将锅稍倾斜，放入姜、蒜粒及泡红辣椒和芹黄煽炒出香味后，与鱼丝炒匀，烹入芡汁，翻簸', '鱼肉洁白鲜嫩，芹黄嫩脆适口，口味淡爽清雅。', NULL, NULL, '鲜活鲤鱼一条（约750克）、芹黄200克、泡红、辣椒30克、蛋清25克、豆粉30克、料酒10克、盐3克、姜、蒜各10克、酱油10克、醋10克、白糖20克、香油10克、味精0.5克、熟猪油150克、上汤30克。');
INSERT INTO `recipe` VALUES (2032, 65, '清蒸甲鱼', '甲鱼去膛洗净，连同鸡翅一起放入碗内，放入糖、精盐、味精、胡椒粉、葱、姜上锅蒸烂；蒸烂后，拣出葱、姜、鸡翅不要，即可食用。', '四川传统名菜。以甲鱼为原料，用沙锅蒸制而成。甲鱼是滋补佳品，肉质鲜美，营养丰富，有较高的食疗养生价值。', NULL, NULL, '甲鱼750克、鸡翅30克、清汤500克、精盐5克、味精2克、胡椒粉1克、葱、姜各15克。');
INSERT INTO `recipe` VALUES (2033, 65, '沙参心肺汤', '将沙参、玉竹择净后用清水漂洗，再用纱布包好备用。心肺用清水冲洗干净，挤尽血水，同沙参、玉竹一起下入砂锅，葱洗净入锅，加清水适量，先用武火烧沸，移炆火炖1个半小时。待心肺熟透，加味精、盐调味即成。', '心肺软烂，咸鲜适口，汤汁味浓；并具有润肺止咳，养胃生津之功效。', NULL, NULL, '猪心肺1副、沙参15克、玉竹15克、盐3克、味精1克、葱25克。');
INSERT INTO `recipe` VALUES (2034, 65, '麻油鸡', '1.将鸡腿切成2厘米块，姜切成2厘米薄片，葱拍碎。 \r\n2.炒锅加油置旺火上，放入姜、蒜炒香，在加入鸡块、酱油、香油、料酒、糖一同翻炒，至汤汁干时即可。', '以鸡腿为主料，用麻油烹制而成。成菜色泽深红，鸡肉酥软，汁浓味厚，开人胃口。是佐酒、下饭佳肴。', NULL, NULL, '鸡腿400克、姜10克、葱15克、料酒、酱油各125克、糖25克、香油20克、油80克。');
INSERT INTO `recipe` VALUES (2035, 65, '蚂蚁上树', '1.瘦肉洗净抹干，剁碎。红椒去籽，与葱分别洗净及切粒。\r\n2.粉丝放入滚油中炸脆即盛起，不要炸至金黄色。\r\n3.烧热镬，下油1.5汤匙爆香姜茸，放入肉碎及红椒粒炒片刻，加入调味料、上汤及粉丝，慢火煮至汁收干，再加入葱粒兜匀即可上碟。', '蚂蚁上树是四川一道名菜，材料中没有蚂蚁，不用害怕。蚂蚁上树又叫肉末粉丝，材料主要是粉丝、肉碎，四川风味当然少不了辣椒，吃时肉碎黏着粉丝，就如蚂蚁一样，但十分有味。', NULL, NULL, '粉丝三两、瘦肉二两、红椒一只、葱一棵、姜茸半茶匙、上汤半杯。调味料：生抽两茶匙、豆瓣酱、酒各一茶匙、盐、糖各1/8茶匙、酒糟一茶匙。');
INSERT INTO `recipe` VALUES (2036, 65, '南卤醉虾', '鲜活虾用清水洗净泥沙，剪去虾枪、须、脚，放于盘内，淋上曲酒。葱白切成约3.5厘米的段，均匀地摆在虾的上面，扣上碗即是醉虾。豆腐乳汁、酱油、味精、香油调匀，即成南卤，随醉虾同上桌。食时揭开扣碗，将醉虾蘸卤汁就葱白同食。', '鲜虾醉态可掬，入口鲜美醇香。', NULL, NULL, '鲜活虾500克（每只长3一5厘米为佳）、葱白100克、豆腐乳汁50克、酱油10克、味精0.5克、香油1克。');
INSERT INTO `recipe` VALUES (2037, 65, '酿青椒', '1.将青椒挖去心子，在青椒内部涂一层干菱粉（使青椒不易裂开），再将猪肉、火腿捣成肉泥，同鸡蛋、湿菱粉、地栗（去皮切成末）、开洋末、葱姜末及盐、胡椒粉、黄酒、味精调拌，塞在青椒内，放入猪油锅煎熟，再加些鸡汤上笼蒸。\r\n2.蒸好后，将汤倒入另一碗，加酱油、盐、糖和干菱粉收一下，浇在青椒上即好。', '味鲜香，稍带辣，呈绿色，秋季较宜。', NULL, NULL, '青椒15只、猪肉250克、地栗40克、开洋末、火腿或鸡肉12.5克、鸡蛋1只、菱粉、味精、精盐、酱油、葱姜末、胡椒粉、黄酒、白糖、鸡汤。');
INSERT INTO `recipe` VALUES (2038, 65, '牛肉馅饼', '1.面粉加开水揉和揉透后，摊开冷却，然后搓成长条，揿扁擀成长方形薄皮坯子。将牛肉剁成碎末。\r\n2.将葱、盐、油、花椒粉和牛肉末拌和均匀地撒在坯子上卷好，擀圆，上平底锅烙，烙至金黄色即可。', NULL, NULL, NULL, '面粉500克、牛肉适量、麻油l00克。');
INSERT INTO `recipe` VALUES (2039, 65, '泡菜炒肉末', '1.将泡菜挤去水分同猪肉分别剁成末，辣椒切短节。 \r\n2.把花椒投入热油中，炸黄后挑出，再下辣椒炸成黑紫色后加入肉末，煸炒将熟，加入糖、盐、味精、料酒和泡菜，翻炒均匀即成。', '味辣、鲜脆，家庭便菜。', NULL, NULL, '猪肉(肥与瘦以3比7为宜)100克，四川泡菜200克，植物油50克，干辣椒8克，盐3克，料酒13克，糖、味精各3克，花椒10粒。');
INSERT INTO `recipe` VALUES (2040, 65, '芪烧活鱼', '鲤鱼去鳃、鳞、鳍后，剖腹去内脏，洗净。在鱼身上两面刻十字花刀。水发香菇一切两半。党参、黄蔑洗润后，切成2厘米厚的片，姜、葱、蒜洗净，切成蒜片、葱丝，姜做成姜汁备用。炒锅烧热，放入菜油烧至六成热（约150度）时，下鲤鱼炸至金黄色捞出，沥去油。炒锅置火上，注入猪油，放白糖，炒成枣红色时，加清汤，下入炸好的鲤鱼、党参片、黄蔑片，烧沸后移炆火煨至熟透入味，将鱼入盘，拣去党参和黄芪。把笋片、香菇片放入汤锅内，调入味精，烧沸后，撇去浮沫，用水豆粉勾芡、淋上猪油，浇在鱼上即成。', '鱼肉褐红，肉质鲜嫩，芡汁浓郁，鲜香味醇，并具有益气健脾，利水清肿之功效。', NULL, NULL, '活鲤鱼1尾（约750克）、入黄蔑10克、党参6克、水发香菇15克、冬笋片15克、白糖30克、料酒10克、盐3克、酱油10克、葱10克、姜15克、蒜10克、味精1克、水豆粉20克、菜油500克、猪油50克、清汤500克。');
INSERT INTO `recipe` VALUES (2041, 65, '砂锅鱿鱼', '盆内放清水2000克，生石灰50克加干鱿鱼浸泡12小时，其间搅和二次，使鱿鱼胀发均匀捞出，用清水冲洗干净；将发制好的鱿鱼切成四厘米长，一厘米宽的粗丝，鸡皮，南荠，冬菇，火腿均切细丝，葱姜洗净拍松；把铁钎烧红烫猪脚尖缝的毛，然后放在温水中泡至半小时刮去烫糊的皮和毛洗净；砂锅中放上清水1000克加葱，姜，料酒，猪蹄放火上烧开撇去浮沫，移至微火上炖约90分钟成浓汤；锅内放鸡汤250克，将鱿鱼丝氽烫一遍；从砂锅内取出猪蹄加鸡汤，鱿鱼丝及各种配料再炖半小时以汤白色浓为佳，加盐和胡椒面调味即熟。', '色泽鲜艳，汤味香浓，保热时间长，宜冬季食用。', NULL, NULL, '干鱿鱼，熟鸡皮，冬菇，南荠，火腿，猪蹄，鸡汤，精盐，料酒，姜，葱，胡椒面。');
INSERT INTO `recipe` VALUES (2042, 65, '苕菜狮子头', '金钩泡涨，荸荠去皮，与猪肉、火腿均切成约3厘米的小粒，装盆中，加盐、胡椒粉、料酒。蛋清豆粉，拌匀后做成四个相等的扁圆形肉团。苕菜洗净。锅置旺火上，下猪油烧热（约150度），放入肉团，炸成呈浅黄色时捞起，放入罐内，加鲜汤、盐、料酒、姜（拍松）、葱（挽结），烧沸撇去浮沫，改用小火煨粑，加入胡椒粉，下苕菜烧入味，放入味精，捞起肉团放盘中，苕菜镶四周，原汁用水豆粉勾薄芡；淋入熟鸡油，浇于盘中即成。', '肉质细嫩，鲜爽可口，苕菜清香，是春季时令佳肴。', NULL, NULL, '猪肉500克、蓄菜100克、荸荠100克、金钩30克、火腿50克、盐3克、胡椒粉3克、料酒20克、蛋清豆粉各40克、猪油500克、姜10克、葱15克、鲜汤500克、味精1克、水豆粉15克、熟鸡油10克。');
INSERT INTO `recipe` VALUES (2043, 65, '生爆盐煎肉', '先将猪肉去皮切成30毫米长，25毫米宽，2毫米厚的簿片，姜去皮切成薄片，青蒜斜着切成10毫米长的段，鲜红辣椒切成长宽各10毫米的块；炒锅放在旺火上烧热，放入花生油，烧到起浓烟时，先投入肉片炸1分钟，炸到肉片卷起略成黄色时，加入精盐，姜片炒几下依次放进豆豉，豆瓣酱，鲜红辣椒，酱油，料酒，白糖，青蒜拌炒即成。', '味香辣，色呈枣红。', NULL, NULL, '生猪肉或后腿的肥瘦肉，鲜红辣椒，花生油，青蒜，四川郫县豆瓣酱，豆豉，酱油，料酒，白糖，姜，精盐。');
INSERT INTO `recipe` VALUES (2044, 65, '生烧筋尾舌', '猪尾刮洗净，切成长约3厘米的段，猪舌入沸水锅中煮10分钟。捞出冲凉，刮去舌上粗皮，改成长约5厘米、宽2厘米、厚约0.5厘米的片。蹄筋洗净，切成长约5厘米的段。姜拍松，葱挽结。炒锅置旺火上，下猪化油烧热，放入舌、尾、蹄筋、姜、葱稍炒，待水气稍干，加料酒、冰糖、酱油、盐、好汤烧沸，撇去浮沫，倒入罐内用微火煨粑。菜心汆熟，垫于盘底（或镶于盘边），拣去罐内葱、姜，舀起装于盘中即成。', '色泽金黄，软糯鲜美，味浓可口。', NULL, NULL, '猪舌100克、猪尾100克、猪蹄筋100克、菜心50克、猪化油50克、姜10克、葱10克、料酒10克、冰糖15克、酱油10克、盐2克、好汤300克。');
INSERT INTO `recipe` VALUES (2045, 65, '什锦海鲜煲', '1.将原料洗净，鲜鱿鱼剞十字花刀，刺参、海螺切片，海肠切成段，入开水锅略烫捞起备用。猪肉切粒，泡椒剁细。 \r\n2.锅置旺火上，加油烧至四成热，下肉粒炒散，烹入料酒，加入泡椒炒香至油呈红色时，加鲜汤、精盐、味精、酱油烧沸，加入原料微烧，下湿淀粉勾芡收汁，起锅装盘即成。决窍：海鲜氽烫时应注意掌握火候，要保持鲜嫩。', '色泽红亮，香辣味鲜。', NULL, NULL, '鲜鱿鱼100克，水发刺参100克，海肠100克，海螺100克，菜心100克，猪肥肉75克，泡红辣椒25克，精盐3克，味精2克，酱油3克，葱花18克，料酒20克，湿淀粉20克，鲜汤500克，植物油100克。');
INSERT INTO `recipe` VALUES (2046, 65, '炝黄瓜', '黄瓜洗净去蒂，切成约4厘米长、l厘米粗的条，码盐少许，葱切成马耳朵形。炒锅置旺火上，加菜油烧至五成热（约125度），放入干辣椒节炒至呈棕褐色时，下花椒炒出香味，再放黄瓜快速炒匀。加入精盐、味精、葱炒至断生，淋芝麻油起锅即成。', '质地脆嫩，香辣微麻。', NULL, NULL, '黄瓜400克、盐3克、菜油40克、干辣椒5克、花椒2克、葱10克、味精0.5克、芝麻油5克。');
INSERT INTO `recipe` VALUES (2047, 65, '清汤燕菜', '1.将燕窝放在没有油腻、干净的瓷器中，用温水泡透发涨后，捞在干净盘内。最好用镊子钳净燕毛和腐烂变色部分，用凉水清洗两遍后，用开水冲泡。\r\n2.用开水将燕窝过一遍后，再用开汤过一遍，分装在小碗内，将盐、胡椒粉、味精料酒、加进清汤中，把味调好冲进小碗即成。', '营养丰富，最珍贵佳肴，汤清菜白。', NULL, NULL, '燕窝25克，清汤适量，料酒25克，盐10克，味精5克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (2048, 65, '软炸虾糕', '鲜虾淘后挤仁洗净，剁成细粒。鸡脯肉、猪肥膘肉分别捶茸，加蛋清、水豆粉、盐、冷汤制成鸡糁后，加入料酒、胡椒面和剁细的虾仁拌匀，装入盘内抹平，上笼用中火蒸至断生（约5分钟），取出晾冷后，用刀切成宽条，裹上一层细干豆粉，锅置火上，下熟猪油烧热（约150－180度），将虾糕条放入，炸至过心刚变色即捞起，淋香油装盘，镶上生菜、葱酱，另配椒盐碟即成。', '外酥内嫩，味美鲜香。', NULL, NULL, '鲜虾500克、鸡脯肉50克、猪肥膘肉100克、生菜50克、大葱10克、鸡蛋清25克、豆粉50克、精盐5克、椒盐25克、胡椒粉1克、面酱10克、芝麻油1克。');
INSERT INTO `recipe` VALUES (2049, 65, '双色玫瑰鱼', '1.鱼肉洗净捶茸，去尽筋络、细刺，分为两份：一份入碗，加川盐、化猪油、面粉、细干淀粉、姜葱水、料酒等搅匀成鱼糁；另一份入碗，加川盐、细干淀粉、鸡蛋清、胡椒粉、化猪油、姜葱水搅匀成鱼糁。 \r\n2.用两只标花口袋，放入花嘴，将两种糁料分别装入，各标成玫瑰花形。当用糁（1）标成的玫瑰花入冰箱速冻定型后，取出入五成热的油锅中炸至呈金黄色；而对用糁（2）标成的玫瑰花，则入笼蒸熟定型待用。 \r\n3.将老南瓜洗净去皮，雕刻成半边花篮形状，入沸水锅中略烫捞出搌干水；棒菜梗洗净去尽筋络，修切成花叶，入沸水锅中焯断生，捞出，', '双色玫瑰鱼是用蛋糕标花的手法，以鱼糁为原料造型而成创新菜。这里巧用烹制之法使成菜为双色，装点成型，素雅美观，可为筵席增色。造型美观，一菜双色双味，质鲜嫩爽口，多用于筵席。', NULL, NULL, '净鱼肉500克，老南瓜1块（约750克），棒菜埂300克，面粉25 克，细干淀粉40克，鸡蛋清75克，川盐、味精、料酒、番茄酱、白糖、胡椒粉、姜葱水、湿淀粉、麻油、化猪油、精炼油各适量。');
INSERT INTO `recipe` VALUES (2050, 65, '水浒肉', '1.将里脊肉切成薄片，青蒜切成节。里脊片用水川一下，再用蛋白，菱粉拌和。\r\n2.将豌豆苗下油锅加盐、味精炒熟，放在盘底。\r\n3.另起猪油锅，将干辣椒、花椒放入爆一下，取出剁碎，放在另一碗中。\r\n4.利用原油锅，加汤、胡椒、酱油、味精、糖、盐爆一下，马上将里脊片入锅，炒至七八成熟时，连汁倒在豌豆苗上，将爆好的干辣椒，花椒倒在里脊片上面。再烧一点热油浇上即好。', NULL, NULL, NULL, '里脊肉200克、酱油4克、青蒜40克、味精2克、豌豆苗80克、白糖少许、花椒8克、菱粉32.5克、 干辣椒13克、鸡蛋1只、精盐2.5克。');
INSERT INTO `recipe` VALUES (2051, 65, '水晶南瓜', '1.将南瓜洗净后，在瓜皮四周雕上各种图案，瓜顶也要刻些花纹。再将瓜顶切下作为瓜帽，去掉瓜瓤，放在微火素油锅内氽七分钟，使瓜内水分氽去而有光彩；\r\n2.将鸡切成1寸左右的厚片，做成粉蒸鸡，用料一部分即为做此菜用。\r\n3.将粉蒸鸡镶入南瓜，吃前再上笼蒸二十分钟，取出放在圆盘内，盘四周用经过油炸过的芥兰菜叶丝做边即好。', '味辣甜清香，色黄。', NULL, NULL, '南瓜1只、约1700克、大肥鸡200克、甜面酱6克、豆瓣酱、酱油、白糖、麻油、酒酿露、葱姜末、米粉、猪油、花椒、芥兰菜叶、黄酒。');
INSERT INTO `recipe` VALUES (2052, 65, '水煮牛肉', '1.将牛肉片成薄片装入碗内。莴笋成切成6厘米左右长的簿片。芹菜、蒜苗切成5厘米长的节。\r\n2.炒锅置旺火上，放少许油烧热，将干辣椒及花椒炸成棕红色，捞出铡细，放入碗内。\r\n3.牛肉片放少许盐略腌渍，再放入水豆粉拌匀。\r\n4.炒锅置旺火上，放50克油烧热，将芹菜、莴笋尖、蒜苗炒至断生再装入碗内。\r\n5.炒锅内再放油烧热，放郫县豆瓣炒香，掺入鲜汤、加味精、酱油烧沸后，将肉片放入，用锅铲推散。待汤汁浓稠，再沸时，连同肉片盛入装莴笋尖等菜的碗内，撒上辣椒、花椒粉，然后淋上热菜油50克即成。', '麻辣味厚，刺激性强，是典型的川菜。', NULL, NULL, '主料：牛肉，芹菜，莴笋尖，蒜苗。配料：郫县豆瓣20克，花椒2克，酱油12克，水豆粉40克，熟菜油120克，干红辣椒8克，盐3克，味精适量，鲜汤600克。');
INSERT INTO `recipe` VALUES (2053, 65, '酸辣海参', '水发海参洗净，片成薄片，在沸水锅中煮后再用清汤煨一、二次，沥干待用。鸡蛋煮熟，取蛋白切成薄片。熟冬笋、蘑菇切成薄片。番茄去皮、籽，切成片。豌豆苗洗净，葱切成细花，姜切细粒。将切好的鸡蛋、冬笋、蘑菇、番茄放入汤碗中，加适量胡椒粉、醋、特制清汤在锅中烧沸，加味精，下海参、姜粒，加水豆粉勾成玻璃芡，下豌豆苗，待沸，加入香油，起锅舀入汤碗，撒上葱花即成。', '汤清色彩纷呈，海参软糯，酸辣味醇，夏令食用尤佳。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (2054, 65, '酸辣臊子蹄筋', '猪蹄筋油发后去尽油质，改成长约3厘米的节，用鲜汤煨汆一次，猪瘦肉剁成细粒，葱切花，姜切细末。炒锅置火上，下猪化油烧热，下猪肉火南酥，加酱油、姜、料酒稍炒，放入盐、胡椒粉、酱油。加好汤，下蹄筋烩入味后，用水豆粉勾二流芡，下醋、葱花、香油推匀，起锅装碗即成。', '质地肥糯，酥辣而香。', NULL, NULL, '发好的猪蹄筋300克、猪肉50克、葱10克、姜10克、猪化油50克、酱油20克、料酒10克、胡椒粉3克、醋30克、香油10克、水豆粉15克、好汤500克。');
INSERT INTO `recipe` VALUES (2055, 65, '蒜泥白肉', '1.猪肉洗净，入汤锅煮熟，再用原汤浸泡至温热，捞出搌干水分，片成长约10厘米、宽约5厘米的薄片装盘。\r\n2.大蒜捶茸，加盐、冷汤调成稀糊状，成蒜泥。上等酱油加红糖、香料在小火上熬制成浓稠状，加味精即成复制酱油。\r\n3.将蒜泥、复制酱油、红油兑成味汁淋在肉片上即成。', '肉白汁红，蒜香味浓，香辣爽口。', NULL, NULL, '猪臀肉500克、大蒜50克、上等酱油50克、红油10克、盐2克、冷汤50克、红糖10克、香料3克、味精1克。');
INSERT INTO `recipe` VALUES (2056, 65, '坛子肉', '猪肉、鸡肉、猪骨入沸水锅中煮几分钟捞出，猪肉切成7厘米见方的块，鸡肉切块。鸡蛋煮熟，去壳，裹上细干豆粉，入猪油锅炸成黄色捞出。冬笋切成滚刀块。火腿切粗条，金钩、墨鱼水涨发后洗净。在陶质小坛内垫放猪骨，将猪肉、鸡肉、墨鱼、金钩、火腿、冬笋、鸡蛋、猪肉丸等放入坛内，加精盐、酱油、醪糟汁、冰糖汁和纱布袋装好的姜（拍破）、葱（挽结））胡椒（拍碎）、口蘑（涨发），并掺入鲜汤，然后用纸（润湿）封严坛口，将坛置谷糠壳火上煨约五六小时后揭去封纸，取出装姜、葱、口蘑的纱布袋，装入盘中即成。', '原料丰富，形态丰腴，色泽棕红，味道浓厚，鲜香可口。', NULL, NULL, '猪带皮五花肉500克、油炸猪肉丸子75克、鸡蛋200克、鸡肉50克、火腿25克、墨鱼50克、冬笋25克、 蘑菇25克、金钩10克，姜10克、葱15克、胡椒2克、鲜汤500克、猪油250克、细干豆粉25克、精盐3克、酱油15克、醪糟汁20克、冰糖汁25克。');
INSERT INTO `recipe` VALUES (2057, 65, '糖醋鸡圆', '1.鸡脯肉剁细成茸，加入清水、精盐、鸡蛋液、胡椒粉、水豆粉搅打一体，再加入剁细的鲜虾仁颗粒，搅匀备用。\r\n2.锅置旺火上，烧油至六成热，用手将鸡肉馅挤成鸡圆，下锅炸定型后，捞出备用。待油温回升至七成热时，将鸡圆回锅炸酥、发黄，捞出装入盘内。 \r\n3.锅中留油少许，油温三成，下姜蒜米，炒香，烹入酱油、醋、白糖、精盐、鲜汤、水豆粉调成的芡汁，收汁起锅放入鱼眼葱，淋在鸡圆上即成。', '外酥内嫩，甜酸味浓，鲜香爽口。', NULL, NULL, '鸡脯肉200克、鲜虾仁50克、鸡蛋2个、姜米5克、蒜米8克、鱼眼葱10克、精盐少许、白糖12克、醋15克、酱油10克、胡椒粉1克、鲜汤100克、水豆粉40克、精炼油1000克(约耗75克)。');
INSERT INTO `recipe` VALUES (2058, 65, '糖醋排骨', '猪排骨斩成长约5厘米的节，入沸水内出水，捞出装入蒸盆中，加盐、花椒、料酒、姜、葱、鲜汤入笼蒸至肉离骨时，取出排骨。锅置旺火上，下油烧热至180度放入排骨炸呈金黄色捞出，下素油烧热，炒糖汁，加鲜汤，下排骨、白糖用微火收汁，汤汁将干时，加醋，待亮油起锅，淋上香油，装盘晾凉，撒上芝麻拌匀即成。', '色泽红亮，干香滋润，甜酸味醇。', NULL, NULL, '猪排骨400克、熟芝麻25克、盐2克、花椒2克、料酒15克、姜10克、葱10克、素油500克、鲜汤150克、醋50克、白糖100克、香油10克。');
INSERT INTO `recipe` VALUES (2059, 65, '糖粘羊尾', '猪肥膘肉刮洗干净，入沸水锅内煮熟，捞出，去肉皮，晾冷切成长约4厘米、宽厚各1厘米的条，用沸水冲洗去油腻，用热毛巾搌干水分。鸡蛋打散与干豆粉调成蛋糊，将肉条放入蛋糊碗内拌匀。炒锅置旺火上，下菜油烧至120度，将肉条逐一放入锅中炸定型捞出，待油温升至约150度时，再将肉条放入炸至外酥、色金黄时捞出，滗去炸油。洗净锅，置火上，加清水、白糖，熬至糖液起大泡时，将锅端离火口，放入炸好的肉条，用小铲不断翻炒，使均匀粘上糖液，起锅装盘晾冷即成。', '色泽金黄，外酥内嫩，肥而不腻。', NULL, NULL, '猪肥膘肉400克、鸡蛋50克、豆粉25克、菜油500克、自糖100克。');
INSERT INTO `recipe` VALUES (2060, 65, '桃酥鸡糕', '桃仁用温水泡10分钟捞起，撕去皮，沥干，放入五成热油（约125度）调锅中炸酥，捞出剁细。鸡脯、肥膘分别捶茸，去筋后与蛋清制成鸡糁，并加料酒、盐、味精和桃仁拌匀，装入盘内刮平呈方形（厚约1.4厘米），上笼蒸约10分钟即成鸡糕。取出晾冷后，改成一字条，裹上细干豆粉。炒锅置火上，下油烧至180度。放入鸡糕，炸至呈浅黄色皮酥时，捞出装盘，淋上香油，再配以糖、醋、香油拌匀的生菜即成。', '酥香鲜嫩，宜于佐酒。', NULL, NULL, '鸡脯肉300克、桃仁50克、猪肥膘50克、蛋清30克、生菜50克、素油500克、料酒10克、盐3克、味精0.5克、香油20克、糖10克、醋5克，干豆粉15克。');
INSERT INTO `recipe` VALUES (2061, 65, '甜椒肉丝', '猪肉切成二粗丝（长约5厘米、粗0.3厘米），入碗加水豆粉、盐、料酒拌匀。甜椒去蒂、籽，切成二粗丝，嫩姜切细丝，蒜苗切3厘米的节。酱油、料酒、水豆粉、味精、鲜汤装碗内调匀成芡汁。炒锅置火上，下油少许烧热，放入甜椒炒断生起锅。炒锅洗净置旺火上，下油烧热（约150度），放肉丝炒散，加甜酱炒香，下甜椒、姜丝、青蒜苗合炒，烹入芡汁，炒匀起锅装盘即成。', '红、黄、白、绿相互映衬，脆嫩鲜香，咸鲜微辣。', NULL, NULL, '猪肥瘦肉350克、甜椒100克、青蒜苗50克、嫩姜20克、水豆粉50克、盐3克、料酒20克、酱油10克、味精1克、鲜汤25克、素油50克、甜酱20克。');
INSERT INTO `recipe` VALUES (2062, 65, '网油腰卷', '猪腰去膜，片成两片，去尽腰臊，洗净，与猪肉、兰片分别切成细丝，入碗加盐、料酒、胡椒粉、蛋清豆粉拌匀成馅。猪网油洗净晾干，平铺于案上，修成长方形（长约20厘米，宽约10厘米），抹一层蛋清豆粉，将馅放于网油一边，呈一字形，两头包起，裹成卷条（直径约2厘米），并用竹签或刀尖在卷上扎些小孔，粘上干豆粉。锅置旺火上，下素油烧热（约150℃），下卷炸至外酥内熟，呈金黄色时捞出，抹上香油，斜刀切成长约3厘米的段，叠摆于盘的一端，另一端配糖醋生菜，并椒盐碟上桌。', '色泽金黄，皮酥内嫩，配蘸椒盐和生菜，麻香可口。', NULL, NULL, '猪腰250克。猪网油200克。猪肥肉100克，水发兰片100克、生菜100克。盐2克、料酒10克、蛋清豆粉40克、素油300克、香油25克、干豆粉15克、胡椒粉2克、糖10克、醋5克、椒盐10克。');
INSERT INTO `recipe` VALUES (2063, 65, '乌发汤', '将中药及黑豆、黑芝麻、胡桃肉等用纱布袋装好，扎口。将羊肉、羊头（打破）、羊骨用清水洗净。羊肉剔去筋膜，入沸水锅内去血水，同羊头、羊骨、药物袋同时下锅，加入清水，放入生姜、葱白、胡椒，先用武火将汤烧开，打去浮沫，捞出羊肉切片，再放入锅中，用炆火炖1小时30分钟，待羊肉酥烂即成。药袋捞出不用。以上药物可分作100份食用。吃时可加味精，食盐调味。', '羊肉酥烂，爽滑利口，汤汁香醇，并具有补养气血，乌须黑发之功效。', NULL, NULL, '羊肉1000克、羊头一只、羊骨1000克、熟地黄30克、淮山药30克、丹皮15克、枣皮15克、泽泻15克，何首乌50克、当归6克、红花6克、菟丝子230克、天麻15克、侧伯叶10克、黑豆60克、黑芝麻50克、胡桃肉50克。味精5克、盐10克、姜50克、葱白50克、胡椒15克。');
INSERT INTO `recipe` VALUES (2064, 65, '五彩蟹盒', '1.蟹盒洗净，当入油锅中经炸制呈橙红色时，捞出沥尽油，待用。 \r\n2.鲜鱼肉捶制成茸，入碗，先用清水解散，加入川盐、料酒、鸡蛋清、细干淀粉、姜葱水等搅匀成鱼浆，再将鱼倒入漏瓢内，漏入三成热的油锅中，待浮起捞出，即成鱼米。 \r\n3.老黄蛋糕、红辣椒、冬笋、香菇分别切成豌豆大的丁；豌豆去壳掰成两半。红辣椒、冬笋、香菇、豌豆均入沸水锅中氽断捞出待用。\r\n4.锅置火上，放精炼油烧至五成熟时，下各种丁料翻炒至熟，接着放入鱼米，加川盐、味精推匀，用湿淀粉勾薄芡炒匀起锅，分装入蟹盒内。 \r\n5.萝卜刻成一座假山，黄瓜切', '五彩蟹盒是以蟹盒为盛器，将红、黄、绿、白各色原料，均切成黄豆大的丁，再经炒制而成的一款新菜。装盘新颖，色泽协调，质地柔软，味咸鲜喷香。', NULL, NULL, '活蟹盒10个，净鲜鱼肉200克，嫩豌豆30克，老蛋黄糕50克，冬笋30克，香菇30克，红辣椒30克，白萝卜1000克，胡萝卜500克，黄瓜200克，紫白菜250克，鸡蛋清100克，川盐、料酒、味精、湿淀粉、姜、葱、精炼油各适量。');
INSERT INTO `recipe` VALUES (2065, 65, '鲜花豆腐', '豆腐揭去表皮后用箩筛制茸，用纱布搌干水分后置盆中。猪肥膘肉和鸡脯肉去筋膜捶茸，与豆腐同置一盆中，加葱姜水搅散后，再加盐和鸡蛋清制成糁。在扇形与蝶形模具上抹一层猪化油，分别制出10个扇形、2个蝴蝶形的豆腐糁，将胡萝卜、大甜椒、香菌等片刻成不同花卉图案，分别嵌于豆腐糁上，上笼蒸熟备用。炒锅置旺火上，加清汤烧沸，加胡椒粉，放入豌豆苗烫熟，舀入汤盆内，将蒸好的豆腐糁滑入汤内即成。', '造型美观，色彩协调，汤清味美，质细嫩。', NULL, NULL, '嫩豆腐500克、肥膘肉50克、鸡脯肉50克、胡萝卜30克、大甜椒20克、香菌15克、豌豆苗50克、葱姜水20克、盐3克、鸡蛋清25克、清汤500克、胡椒粉2克、猪化油30克。');
INSERT INTO `recipe` VALUES (2066, 65, '香椿白肉丝', '猪肉刮洗干净，入汤锅煮熟捞出，放入热汤中浸泡10分钟，捞起搌干水分，切成长约6厘米的二粗丝，装盘。椿芽洗净，入碗，用开水稍焖，捞出去蒂柄，切成细粒。酱油、白糖、辣椒油、香油、味精、蒜泥、精盐等入碗调匀成味汁，淋于肉丝上，再撒上椿芽粒即成。', '色红味浓，咸鲜微辣，芳香适口。', NULL, NULL, '猪后腿肉（带皮）500克、椿芽100克、上等酱油20克、白糖15克、辣椒油10克、香油5克、味精1克、精盐2克、蒜泥10克。');
INSERT INTO `recipe` VALUES (2067, 65, '香酥山药', '将鲜山药洗净，上笼蒸熟后取出去皮，切成3厘米长的段，再一剖两片，用刀拍扁。锅烧热后倒入菜油，待油烧至七成热（约175度）时，投入山药，炸至发黄时捞出。另烧热锅放入炸好的山药，加入糖和两勺水，用炆火烧3－5分钟后即用武火，加醋、味精，用水豆粉勾芡，淋上熟油起锅装盘即成。', '山药酥烂，软糯香甜，并具有健脾胃，补肺肾之功效。', NULL, NULL, '山药500克、白糖125克、豆粉100克、菜油750克、醋30克、味精1克。');
INSERT INTO `recipe` VALUES (2068, 65, '香酥鸭', '1.除去鸭子的内脏、翅膀、鸭脚、鸭骚、再把鸭脯处的胸骨压平，将盐抹遍全身，加各种香料如桂皮、八角、小茴香、葱、姜等，上笼约蒸三小时后取出，晾干。\r\n2.起油锅，用武火烧热，将鸭子放入锅内，炸后拿起，在鸭皮上抹些黄酒和酱油，再入锅炸至呈金黄色即好。食用时，可蘸花椒盐吃。', '味香酥，色金黄。', NULL, NULL, '光鸭1500克、葱花80克、粗盐65克、姜130克、花椒8克、黄酒少许、桂皮20克、酱油少许、八角8克、小茴香4克。');
INSERT INTO `recipe` VALUES (2069, 65, '杏仁豆腐', '1.用热水把杏仁泡胀，剥掉外衣，剁碎，放入大容器内，加入凉水1000克磨成极细的浓汁，再用细纱布滤过，渣子弃掉，用细浆。\r\n2.取钢精锅，倒入杏仁浆，加入牛奶500克、白糖260克，用旺火煮开。', '形似豆腐，凉甜爽口，消暑解热，夏食佳品。', NULL, NULL, '甜杏仁260克，白糖260克，牛奶500克，洋粉60克，罐头水果30克，杏仁精少许。');
INSERT INTO `recipe` VALUES (2070, 65, '杏仁银肺汤', '1.将肺叶、气管用水冲洗干净，不带血液，呈全白色，控去水分。用开水加料酒和拍破的葱、姜，把肺炖烂，捞出切厚片。 \r\n2.杏仁用开水泡涨后去皮，另装入容器中加水，并上屉用旺火蒸烂。\r\n3.将鸡汤注入锅内再把猪肺片和杏仁同杏仁汁一并倒入，加入上述调料，烧开去掉浮沫即成。', '汤料呈乳白色，味鲜美适口。', NULL, NULL, '完整猪肺1副，甜杏仁75克，葱13克，姜、盐各8克，味精3克，胡椒粉0.5克，料酒适量，鸡汤适量。');
INSERT INTO `recipe` VALUES (2071, 65, '绣球鱼翅', '翅针先入沸水中氽几次，然后装入蒸碗，加煮沸的清汤（或沸水）上笼蒸半小时取出，再放入料酒、盐、好汤没一次后，捞起晾干水气。火腿、丝瓜皮、蛋皮等均切成约3厘米长的细丝。与鱼翅搅匀待用。鸡脯、肥膘、蛋清打成鸡糁，挤成直径约1.5厘米的圆丸。盘中铺上鱼翅等丝，放上圆丸，再覆上鱼翅等丝，然后将圆丸和鱼翅等丝逐个团成绣球形，放入方盘中，上笼蒸5分钟取出，放入碗内加清汤、胡椒、料酒，待上菜前泡热，沥去蒸汤，绣球入碗，另加烧好的特级清汤即成。', '形色美观，汤清味鲜。适于作夏季筵宴头菜。', NULL, NULL, '水发鱼翅250克、鸡脯肉50克、肥膘肉50克、火腿、丝瓜、蛋皮、蛋清各25克、料酒30克、盐3克、上汤500克、胡椒1克、清汤250克。');
INSERT INTO `recipe` VALUES (2072, 65, '雪花鸡淖', '鸡脯肉去筋，用刀背捶茸，去尽茸中筋络，装入碗内，先用冷鲜汤调散，再加入水豆粉、盐、味精、胡椒粉搅匀，最后加入蛋清打成的蛋泡搅匀。炒锅置旺火上，下猪油烧热（约180度），倒入鸡浆，炒熟起锅，盛盘撒上火腿末即成。', '状如云朵，似积雪堆叠，入口柔软滑嫩，诚然是“食鸡不见鸡”的妙品。', NULL, NULL, '鸡脯肉150克、熟火腿末31克、鸡蛋清50克、鲜汤350克、砣状水豆粉50克、盐5克、猪油50克、胡椒粉1克、味精0.5克。');
INSERT INTO `recipe` VALUES (2073, 65, '鸭掌包', '1.将面粉加盐拌匀用开水冲搅，加盖焖5分钟，取出搓擦匀透，再加猪油15克揉匀成团待用。\r\n2.将去骨鸭掌用盐水洗净，切成细末待用。金针菜、冬菇，用温水浸发洗净，切成细末，与各种调料拌匀，制成馅。\r\n3.将面粉团摘坯、制皮，包入馅，捏成水饺形，上锅煎至金黄即可。', NULL, NULL, NULL, '面粉l000克、鲜酵母半块、鸭掌600克、金针菜l00克、冬菇50克、精盐、味精、白糖、姜末、生油、麻油各适量。');
INSERT INTO `recipe` VALUES (2074, 65, '网油鱼包', '1.鱼去掉皮、骨、刺，切成粗丝(长5厘米、宽2厘米)。冬菇水发透，去腿洗净切丝。冬笋去壳和内皮煮熟，切成丝。韭菜留白的部分切断。肥膘肉切丝。生菜叶洗净消毒。葱、姜切片。网油洗净控去水。湿淀粉用蛋清调成稀糊。 \r\n2.将鱼丝、冬笋、冬菇、肥膘肉、韭菜用料酒、葱、姜、味精、胡椒粉、盐拌匀。 \r\n3.把网油改切成8厘米见方的块，铺平抹上蛋糊，把拌好的鱼丝包成方形，用刀尖扎小眼。滚上玉米粉下入烧开的油锅内，炸至皮黄内熟捞出，围上生菜叶即成。', '外焦里嫩，香脆适口。', NULL, NULL, '桂鱼1.5公斤，网油500克，冬笋250克，冬菇25克，韭菜50克，猪肥膘肉150克，鸡蛋2个，生菜叶适量，植物油800克(实耗约100克)，葱、姜各75克，湿淀粉75克，盐7克，料酒20克，味精5克，胡椒粉、玉米粉、椒盐适量。');
INSERT INTO `recipe` VALUES (2075, 65, '盐煎肉', '猪肉切成长约5厘米、宽3厘米、厚0.3厘米的片。郸县豆瓣剁细。蒜苗切成长约2.5厘米的节。炒锅置旺火上，下油烧热（约120度），放肉片略炒，加盐炒至肉吐油，下豆瓣、豆豉炒至香味上色，再放酱油、白糖炒匀，下蒜苗炒至断生，出香味，起锅装盘即可。', '色泽棕红，香气浓郁，咸鲜微辣，川味突出。', NULL, NULL, '猪腿肉400克、蒜苗50克、郸县豆瓣20克、豆豉15克、酱油10克、白糖25克、素油50克、盐2克。');
INSERT INTO `recipe` VALUES (2076, 65, '羊耳鸡塔', '鸡脯肉去筋，与猪肥膘肉、火腿、冬笋、冬菇茨菇均切成细丝，葱、姜切成细末。蛋清加干豆粉调成糊状。将切好的各料加盐、胡椒粉、味精、料酒、香油、葱、姜和适量蛋清糊拌匀成馅。猪网油洗净，挤干水分，切成约20厘米见方的方形，抹上蛋糊，将馅料放上，两端包严，卷成扁形（宽约4厘米，厚约1厘米），交口处粘牢，即为鸡塔坯。炒锅置旺火上，下素油烧热（约150度），下鸡塔坯炸至熟透，色呈金黄时捞起，抹上香油，用斜刀法将鸡塔切成羊耳形，盛于盘内。另将生菜，拌上白糖、醋、香油，镶于盘中，随椒盐碟上席即成。', '形如羊耳，色黄酥香，多味鲜美，宜于佐酒。', NULL, NULL, '鸡脯肉300克、猪肥膘肉50克、火腿25克、冬笋25克、茨菇25克、冬菇25克、猪网油15Q克、生菜50克、葱姜各10克、盐2克、胡椒粉1克、味精0.5克、料酒10克、香油35克、蛋清50克、干豆粉30克、素油500克、白糖10克、醋10克、椒盐10克。');
INSERT INTO `recipe` VALUES (2077, 65, '一品豆腐汤', '1.将豆腐搅烂成茸，用细筛滤过；鸡肉用刀背捶成鸡茸，放少许水使之化开，捞去鸡筋，并入豆腐内；将鸡蛋除去蛋白打散。\r\n2.将以上用料拌和，加盐，味精，胡椒粉，倒入盆内铺平（盆上先抹些油，以免豆腐与鸡茸粘住），面上用些有色彩的荤素用料（可自行酌定），做些花草，树木或其他图案加以点缀，然后在食用前七分钟上笼蒸。 \r\n3.在热的鸡汤内加上竹荪、绿叶菜，再将蒸好的豆腐倒入其中即好。', '味鲜嫩，色黄白。', NULL, NULL, '嫩豆腐250克、绿叶菜少许、鸡肉60克、味精少许、鸡蛋3只、精盐少许、水发竹荪40克、胡椒粉少许、鸡汤1碗。');
INSERT INTO `recipe` VALUES (2078, 65, '一品海参', '将整只乌参先在微火上燎两分钟，待粗皮烧成小泡后用刀刮尽，然后将海参剖为两片，入开水发泡两天，取出洗净后，放入加料酒的沸水锅中汆一次，再换水汆第二次。氽后用布将参捞干，用刀在参腔内划旗子块花纹，再用清汤煨一下待用。洗净的猪肥瘦肉、冬笋、口蘑、火腿、干贝等切成小方丁，入烧热的猪油锅中炒成馅，炒时先下猪肘丁，次下冬笋等丁和酱油、料酒。将准备好的参腹腔内填上馅料，装入蒸碗，碗内加汤、盐、味精，然后封上碗口上笼蒸一小时。食时漠去原汁后，翻扣盘中，以原汁加水豆粉调匀，淋于海参上即成。', '形整大方，海参软糯，馅味鲜香，多为海参席头菜。', NULL, NULL, '整开乌参一只250克、猪肥瘦肉50克、冬笋25克、口蘑5克、火腿25克、干贝25克、猪油50克、酱油10克、料酒30克、盐2克、味精0.5克、二汤50克、豆粉10克、清汤50克。');
INSERT INTO `recipe` VALUES (2079, 65, '一品酥方', '猪肉刮洗干净，修整齐，用竹签在肋骨缝隙间刺上若干气孔，擦干水分，用铁质双股烤叉由方肉中部平穿过去。干柴放炉内，火苗燎出炉口约30－40厘米，手持叉柄，将肉方的皮向着火燎，着重燎肉方的四周和四角，并不断左右转动铁叉，待肉皮上一层很薄的黑焦皮自行脱落（俗称漂方），遂将肉方离开炉火，擦净叉尖，取下肉方放入温水中冲洗，并用小刀轻轻刮去焦皮，搌干水分，抹上用料酒浸泡的姜、葱、盐、花椒汁，用干净纱布捂半小时。火池里放入烧红的木炭，将肉方重上叉，皮向上，左右侧动，慢慢烤背脊肉厚部位，烤至肉熟（俗称吊膛）。将池中木炭拨至', NULL, NULL, NULL, '带肋骨硬边猪肉一方约7.5千克、双麻酥饼750克\\生姜15克、大葱15克、精盐10克、花椒5克、绍酒100克、香油25克、蒜片5克、甜面酱20克、白糖10克、清汤750克。');
INSERT INTO `recipe` VALUES (2080, 65, '盐水肫花', '鸡剜去内金洗净，去鸡肫外表皮的白肋膜，改成四瓣，再用直刀刻成十字花刀（刀距为2毫米，深度达4/5），然后装入蒸碗，加精盐、姜、葱、料酒、味精、鲜汤、花椒入笼用旺火蒸至熟透，取出晾冷，装盘，原汁加香油淋干肫花上即成。此菜也可用煮的方式，如蒸时加五香粉或装盘后淋上椒盐汁，则为五香腕花。', '形如花朵，质地脆嫩，清淡爽口。', NULL, NULL, '鸡肫250克、精盐3克、姜、葱各10克、料酒10克、花椒2克、鲜汤50克、香油10克、味精0.5克。');
INSERT INTO `recipe` VALUES (2081, 65, '芝麻兔', '将黑芝麻淘洗干净，炒香。兔宰杀后去皮、爪，掏去内脏洗净，放入锅内，加水适量，烧沸打去浮沫，放入姜片、葱节、花椒、盐，将兔煮至七成熟，捞出稍凉，再放入卤水锅在火上卤2小时左右，捞出晾凉，切配装盘。将味精、香油调匀，淋入盘内，撒上黑芝麻，拌匀即成。', '兔肉褐红，芝麻黑亮，肉质软烂，麻香味浓，具有补血润燥、补中益气之功效。', NULL, NULL, '兔一只（约1000克）、黑芝麻30克、生姜10克、葱10克、花椒2克、香油15克、味精5克、卤汁500克、盐3克。');
INSERT INTO `recipe` VALUES (2082, 84, '泰国姜味烤鱼', '1.鱼两面撒少许盐，用炭火或烤箱烤热，也可用煎或微波炉煮熟。\r\n2.油2大匙烧热，炒香A料，入B料略炒后，再加调匀的C料勾汁，浇在烤好的鱼肉上即成。', NULL, NULL, NULL, '鱼肉2片8两；\r\nA：蒜末，辣椒末各1/2大匙、豆办酱一大匙。\r\nB：姜丝，葱丝各1/4杯。\r\nC：白酱油1/2大匙、糖，醋，太白粉各1小匙、高汤（或水）5大匙。');
INSERT INTO `recipe` VALUES (2083, 84, '泰国辣味烤魚', '1.鱼两面撒少许盐，用炭火或烤箱烤熟，也可用油煎或微波炉煮熟。\r\n2.油2大匙烧热，炒香A料，随入B料烧开，浇在烤好的鱼肉上即成。', NULL, NULL, NULL, '鱼肉8两；\r\nA：蒜，辣椒，芫荽茎（切碎）各1/2大匙、洋葱末4大匙。\r\nB：鱼露，酸子汁各2大匙、糖1大匙、高汤4大匙。');
INSERT INTO `recipe` VALUES (2084, 65, '银杏蒸鸭', '银杏拍破去壳，在开水内煮熟，撕去皮膜，切去两头，除去心，再用开水焯去苦水，在猪油锅内炸一下，捞出待用。将鸭洗净，宰去头脚，用盐、胡椒粉、料酒抹匀入盆，加入姜葱、花椒，上笼蒸约1小时取出。拣去姜葱、花椒，用刀从背脊骨剁开，去净骨头，盛入碗内，齐碗口修圆。将剩下的鸭肉切成丁，与银杏混合放于鸭脯上，将原汁倒入，加上汤，蒸笼蒸约半小时至鸭肉烂，出笼翻入盘内。锅内掺清汤，加鸡油。料酒、盐、味精、胡椒粉、豆粉勾兑，放猪油，浇于鸭肉上即成。', '鸭肉色白油润，肉质酥烂鲜香。并具有滋阴养胃，利水消肿，定喘止咳之功效。', NULL, NULL, '鸭一只（约1000克）、 银杏200克、熟猪油500克、胡椒2克、料酒15克、鸡油10克、清汤200克、姜10克、葱10克、食盐3克、花椒2克、味精2克、汤150克、豆粉15克。');
INSERT INTO `recipe` VALUES (2085, 65, '鱼香荷包蛋', '1.用上述调料(除泡辣椒和油)兑成汁。葱切葱花。姜、蒜均切末。辣椒剁碎。\r\n2.将炒勺烧热注油，待油热后将鸡蛋分次打入，两面煎成黄色，取出放入盘中。勺内留底油少许，把辣椒下入稍炒，倒入好的汁，汁开时把明油浇在鸡蛋上即成。', '形似荷包，色泽金黄，味道鲜浓，带有鱼香。', NULL, NULL, '鸡蛋500克，大油50克，净葱、湿淀粉各15克，净姜5克，蒜瓣5克，泡辣椒10克，酱油35克，白糖20克，料酒25克，味精5克，汤50克，醋15克。');
INSERT INTO `recipe` VALUES (2086, 65, '鱼香茄子', '1.将茄子去皮、去蒂，切成小块。葱、姜、蒜均切成末。\r\n2.将酱油、醋、糖、料酒、淀粉、味精、葱、姜、蒜放入碗中，调成汁。\r\n3.炒锅内放入油烧热后放入茄块、豆瓣辣酱，不停地煸炒，待茄块炒软且成熟后，放入碗内的汁，迅速翻炒使汁拌匀后出锅即可。', NULL, NULL, NULL, '茄子500克、酱油15克、醋l0克、糖l0克、淀粉3克、料酒5克、葱5克、姜3克、蒜3克、豆瓣辣酱4克、油50克、味精l克。');
INSERT INTO `recipe` VALUES (2087, 65, '鱼香肉片', '1.肉切成薄片(宽2.5厘注、长3.5厘米)，青菜带叶切成3厘米长的段，茎切片。木耳用水发透洗净，葱、姜、蒜切成末，将泡辣椒剁碎。将肉片用少量酱油、料酒、盐拌匀，加湿淀粉浆好再拌些油。 \r\n2.用酱油、料酒、味精、糖蒜、葱、姜、醋、湿淀粉、汤对成汁。 \r\n3.把炒勺烧热注油，油热后下肉片，边下边用手勺推动，使肉片散开，再投入辣椒，炒出味以后下木耳，青菜翻炒几下，然后倒上兑好的汁，汁开后翻几下即成。', '有似鱼的香味。', NULL, NULL, '瘦猪肉400克，净青菜50克，木耳3克，混合油75克，酱油20克，白糖、料酒、葱、泡辣椒各13克，醋2克，蒜、姜各8克，味精3克，盐5克，湿淀粉13克，汤适量。');
INSERT INTO `recipe` VALUES (2088, 65, '鱼香肉丝', '1.将泡辣椒切成碎末待用。\r\n2.小碗内加入姜末、葱末、蒜末、精盐、白糖、醋、酱油、湿淀粉，调和均匀待用。\r\n3.花生油烧至六成热，煸炒泡辣椒待出红油后，撒入辣椒粉，放入肉丝煸炒，菜肴将熟时，把小碗内兑好的汁倒入锅内翻拌均匀，淋入香油即可。', '以细猪肉为主料，配以冬菇、木耳，以四川特有鱼香调料软炒烹制而成。此菜色泽深红，咸甜酸辣兼备，鱼香鲜味浓郁，口味十分别致，深受食客称赞，享誉全国。', NULL, NULL, '瘦猪肉200克、水发木耳、水发玉兰片各25克、泡辣椒40克、酱油、白糖、蒜末各10克、葱花、料酒各15克、醋8克、精盐3克、味精1克、淀粉50克、油125克、清汤50克。');
INSERT INTO `recipe` VALUES (2089, 65, '鱼香碎滑肉', '1.猪肉切成指甲大小的薄片，加盐、酱油、料酒、水豆粉拌匀码味。木耳、兰笋洗净切小片，并入开水中泡透沥干。泡辣椒去籽剁细。姜、蒜切细，葱切花。\r\n2.用盐、酱油、料酒、味精，白糖、醋、水豆粉、汤合并入碗配成芡汁。锅置火上，下油烧热，放入肉片炒散，下泡辣椒、姜、蒜稍炒几下，再下木耳、兰笋、葱花炒匀，烹入芡汁，推匀起锅，装盘即成。', '色润红亮，质鲜滑嫩，鱼香味浓。', NULL, NULL, '猪肉350克、水发木耳50克、水发兰笋50克、泡辣椒15克、盐3克、酱油20克、料酒20克、姜10克、蒜10克、葱15克、白糖30克、醋15克、味精1克、豆粉30克、汤30克、清油50克。');
INSERT INTO `recipe` VALUES (2090, 65, '鱼香腰花', '1.先将腰子切半，取掉腰心杂物洗净，打梳形花刀，切成小条，撒少许精盐、酒、胡椒粉抹一下，用干净布挤干，放入干菱粉内翻滚，再放入旺猪油锅一溜捞出（把握时间，保持鲜嫩）。\r\n2.泡辣椒丁、冬菇片、笋片放入油锅内炒拌，同时将准备好的鱼香味（即姜、葱、酱油、胡椒粉、辣油、味精、蒜泥、糖、醋、湿菱粉）和腰子倒入一炒即好，味嫩脆、鱼香，呈深酱色。', '味嫩脆、鱼香，呈深酱色。', NULL, NULL, '猪腰200克、笋片40克、冬菇片20克、香醋8克、辣油、泡辣椒13克、干菱粉、白糖、葱、姜、胡椒粉、酱油、味精、蒜泥、花椒粉、湿菱粉、黄酒、精盐。');
INSERT INTO `recipe` VALUES (2091, 65, '玉兔葵菜尖', '1.将鸡肉、火腿、冬笋均剁成极细粒。炒锅置火上，下猪油15克，烧至六成热，下鸡肉、火腿、冬笋炒匀，加入川盐1克、味精1克、胡椒份0.5克，炒匀后起锅成三鲜馅。\r\n2.将葵菜入沸水锅焯熟捞起修整齐，放入清水中漂冷。澄面粉加川盐0.5克，倒入沸水烫熟，揉成团后做成12个剂子，分别包入三鲜馅，捏成兔子形。\r\n3.炒锅置旺火上，下猪油烧至五成热，下姜、葱炒香掺入奶汤、川盐、胡椒粉烧沸后取去姜、葱。锅内下葵菜尖烧入味，取出放入大圆盘中摆放整齐。锅内下味精、湿淀粉勾成清二流芡，淋入鸡油，起锅浇淋在菜心上。\r\n4.在烧', '鲜咸清香适口，葵菜鲜嫩宜人。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (2092, 65, '原笼玉簪', '大米入锅干炒至呈微黄色，铲出，磨成粗粉备用。猪排骨洗净后，从肉缝处划开，斩成约7厘米长的段，并将两端的肉修去一部分，使骨头整齐地露出。姜洗净去皮拍松，与葱、花椒同用刀铡成细末。修整好的排骨装盆中。将酱油、豆腐乳汁、红糖汁、豆瓣、醪糟汁、川盐。姜葱、花椒末等料放于碗中调匀后，倒入排骨盆中和匀，再放入米粉拌匀。红苕去皮洗净后，切成不规则的块，放小竹蒸笼中，再将拌匀的排骨整齐地码在红苕上，盖好笼盖，上笼锅蒸至排骨肉酥软，取出揭去笼盖，放上几片洗净的芹菜嫩叶即成。', '形如玉簪，肉质酥软，口味浓香，乡土气息浓郁。', NULL, NULL, '猪正肋骨750克、大米100克、红苕150克、芹菜50克、姜15克、葱15克、花椒2克、酱油10克、豆腐乳汁10克、红糖汁20克、豆瓣10克、醪糟汁15克、盐2克。');
INSERT INTO `recipe` VALUES (2093, 65, '炸班指', '肥肠头切去两端，洗净后入沸水锅中稍煮（约一刻钟），捞出切成两段，装入蒸碗中，加清水、盐、葱白、料酒、花椒、姜（拍松），上蒸笼用旺火蒸粑，取出，沥干水分。葱、姜、蒜分别切细末，和香油、料酒、冷汤、水豆粉、白糖、醋、酱油入碗，调匀成糖醋汁。锅置火上，下素油烧热，将蒸好的肥肠头放入，炸呈金黄色时，捞出切成长约1.5厘米的短节，淋上香油，装于盘中，另配糖醋生菜。糖醋汁入锅煎热。装于汤杯中同上。', '色泽金黄，皮酥肉肥，鲜香味美。因其形似旧时射箭者带在手指上的“班指”而得名。', NULL, NULL, '肥肠头300克、生菜50克。盐3克、料酒20克、花椒2克、姜15克、葱20克、蒜10克、香油15克、酱油15克、醋25克、白糖50克、素油500克、冷汤50克。');
INSERT INTO `recipe` VALUES (2094, 65, '炸花仁腰块', '猪腰去膜，平片成两片，去尽腰臊，洗净，用直刀剖交叉十字花纹后，改成约2.5厘米见方的块。花生仁用开水浸泡，去掉皮衣，剁成细粒。葱切短节，姜切片。生菜切成细丝，加白糖、白醋腌渍。蛋清加干豆粉调成稀糊。将腰块用料酒、胡椒粉、味精、姜片、葱节拌匀，腌渍入味。去掉姜、葱，加蛋清豆粉上浆。炒锅置旺火上，下素油烧热（约150度），将上好浆的腰块，沾上花生仁粒入锅，炸至熟透呈黄色起锅，淋上香油，盛于盘的一端，另一端将用糖醋拌匀的生菜摆上，并配椒盐碟上桌。', '色形美观，质鲜嫩脆，花仁酥香，别具风味。', NULL, NULL, '猪腰400克、花生仁100克、生菜50克、葱10克、姜10克、盐2克、料酒10克、胡椒粉2克、味精1克、素油500克、香油15克、糖10克、白醋5克、椒盐10克、蛋清50克、豆粉25克。');
INSERT INTO `recipe` VALUES (2095, 65, '炸珍珠虾', '1.把葱、姜切片。面包切成似绿豆大小的丁。生菜叶消毒洗净。将大虾洗净后去头，皮壳、去脊缝屎线，由脊背缝下刀切开成为一扇，并在一面浅剞十字花刀，用调料把切好的虾拌匀腌半小时入味。 \r\n2.将大虾两面贴上面粉再滚上打散的鸡蛋浆，之后贴好面包丁于虾的两面，并用手按实不使脱落。 \r\n3.炒勺将植物油烧到6成热后，将上述处理好的虾放入，炸成金黄色，至表面黄脆、内熟时捞出。然后，将每只虾改刀切成三块盛盘，围上青菜叶即成。', '鲜香脆嫩，色美金黄。', NULL, NULL, '主料：大虾370克，生菜叶70克，鸡蛋2个，面粉25克，面包50克。调料：植物油500克(实耗约50克)，净葱8克，姜5克，盐3克，胡椒粉1克，料酒13克，味精4克。');
INSERT INTO `recipe` VALUES (2096, 65, '樟茶鸭子', '1.鸭从背尾部横开7厘米长口，去内脏、肛门，料酒、醪糟汁、胡椒粉、盐、花椒拌匀抹鸭身腌8小时捞出，入沸水烫一下紧皮，沥干后放入熏炉，将花茶、稻草、松柏皮、樟树叶拌匀做熏料熏至鸭皮呈黄色取出，上笼蒸两小时，出笼晾凉。\r\n2.熟菜油烧至八成热时放入熏蒸后的鸭，炸至鸭皮酥香捞出，刷香油，鸭颈折成2厘米长段后放盘中间，鸭身斩成4厘米长、2厘米宽条，盖在鸭颈块上，配成鸭形另配荷叶软饼上席即可。', '以秋季上市肥嫩公鸭为主料，经腌、熏、蒸、炸而成，其中用樟树叶和花茶叶烟熏工序最为关键。成菜色泽红亮，外酥里嫩，带有浓厚的樟木和花茶香味，风味十分独特。', NULL, NULL, '肥公鸭1只2000克，花茶、樟树叶、醪糟汁各50克，稻草、松柏枝各500克，精盐、香油各10克，料酒25克，花椒1克，胡椒粉1.5克，熟菜油1000克。');
INSERT INTO `recipe` VALUES (2097, 65, '蒸江团', '1.鱼宰杀洗净，在鱼身两侧斜剞六至七刀（刀深0.3厘米左右），用精盐2克\\绍酒15克腌渍入味，香菇片成薄片，火腿切成片。\r\n2.将腌渍好的鱼氽去血水，放在蒸盘内，再将火腿、香菇片逐一插入剞刀处，加精盐2克、绍酒15克、葱、姜、清汤250克，盖上网油，上笼旺火蒸三十分钟，取出。捡去葱、姜片、网油，将鱼轻轻地滑入鱼盆内。\r\n3.炒锅加清汤1250克，再将蒸盘内的原汁滗入锅内，旺火烧开，加味精、胡椒粉，浇入汤盆内即成。同时与姜汁、味碟一起上桌。', '形状美观，肉质嫩肥美，汤清味鲜，营养十分丰富。', NULL, NULL, '岷江团鱼1条（约1250克左右）、水发香菇5个、火腿10片、姜片25克、水发虾米10克、网油1张、葱段15克、精盐4克、胡椒粉1克、绍酒15克、味精1克、清汤1500克、姜汁味2碟。');
INSERT INTO `recipe` VALUES (2098, 65, '芝麻肉丝', '猪肉洗净，切成长约10厘米的粗丝，用姜（拍破）、葱、盐、料酒拌匀静放约30分钟；搁置旺火上，下菜油烧热（约150度），下肉丝炸至呈浅黄色时捞出。滗去炸油，锅洗净，做肉丝，加鲜汤烧沸，去尽油沫，加盐、白糖、八角、糖色烧沸后，移小火守至汁干吐油时，放入味精、香油略收，起锅晾冷，装盘，撒上熟芝麻即成。', '酥香滋润，鲜香带甜。', NULL, NULL, '猪瘦肉500克、熟芝麻25克、姜、葱各10克、盐4克、料酒10克、菜油500克、白糖25克、八角2克、糖色10克、味精1克、香油10克、鲜汤350克。');
INSERT INTO `recipe` VALUES (2099, 65, '仲景羊肉汤', '当归、生姜用清水洗净，切成大片；羊肉去骨，剔去筋膜，入沸水汆去血水，捞出晾凉，切成5厘米长、2厘米宽、1厘米厚的条。砂锅中掺入清水适量，将切好的羊肉、当归、生姜放入锅内，旺火烧沸后，打去浮沫，改用炆火炖1小时，羊肉熟透即成。', '羊肉粑烂，汤汁清鲜，并有对病后虚寒，面黄憔悴有较好疗效。', NULL, NULL, '羊肉500克、生姜250克、当归150克、胡椒面2克、葱50克、料酒20克、盐3克。');
INSERT INTO `recipe` VALUES (2100, 84, '泰国蟹盒', '1.将原料及1个蛋用搅拌机或用手搅拌至有粘性，放入蟹壳内。\r\n2.蒸锅内加水烧开，将蟹盒蒸15分钟取出。\r\n3.炸油烧热，2个蛋打匀，蟹盒沾蛋蒸液以中火炸呈金黄色（3分钟）捞出。', NULL, NULL, NULL, '蟹肉、虾泥各1/4杯、猪绞肉4两、鱼露1大匙、胡椒1/4小匙、糖1/2小匙、蒜，芫荽茎（切碎）各1大匙、蛋3个、蟹壳2个、炸油适量。');
INSERT INTO `recipe` VALUES (2101, 84, '泰国炸海鲜饼', '1.将原料用搅拌机或用手搅拌至有粘性，手沾水做成6份虾饼，两面沾裹面包粉。\r\n2.炸油烧热，中火将虾饼炸约5分钟至熟。', NULL, NULL, NULL, '虾泥6两、鱿鱼（剁碎）1/4杯、白酱油3/4大匙、胡椒、糖、太白粉各1/2小匙、蛋1个、芫荽茎（切碎）1大匙、苏打粉1/8小匙、面包粉1杯、炸油适量。');
INSERT INTO `recipe` VALUES (2102, 84, '泰国炸鸡肉饼', '1.将原料用搅拌机或用手搅拌至有粘性，随入四季豆拌匀；手沾水做成6个肉饼。\r\n2.炸油烧热，中火将肉饼炸约5分钟至熟。', NULL, NULL, NULL, '鸡绞肉6两、鱿鱼（剁碎）1/4杯、红咖哩酱1/2大匙、盐1/4小匙、糖、太白粉各1小匙、蛋1个、泰国柠檬叶（切碎）2片、苏打粉1/8小匙、四季豆（切碎）3大匙、炸油适量。');
INSERT INTO `recipe` VALUES (2103, 84, '泰式蛋包什锦', '1.油2大匙烧热，炒香蒜末，随入肉炒熟，依序入B、C料拌炒后即铲出。\r\n2.在干净锅面涂少许油，用中火将蛋液煎成蛋皮。\r\n3.将炒好材料置蛋皮上，对折成半圆形即成。', NULL, NULL, NULL, 'A：绞肉（猪、牛或鸡肉）3两、蒜末1/2小匙。B：洋葱末、红萝卜丁各1大匙 、芹菜丁2大匙、青豆仁1大匙、小西红柿（切半）3粒、草菇（罐头）6个。C：鱼露3/4大匙、鲜味露，糖各1小匙、西红柿调味汁1大匙、蛋（打散）2个。');
INSERT INTO `recipe` VALUES (2104, 84, '泰式烤鸡', '1.鸡拌入所有配料腌3小时。\r\n2.鸡在炭火上边翻面边烤至略焦，肉熟；切块后食用。如用烤箱，先将烤箱烧热，鸡皮朝上，置烤盘上置，烤箱门略开，以500°F烤（火由上往下）烤至表皮呈金黄色，再以400°F烤至鸡熟共烤约50分钟。黄姜粉是用来加强菜色用。', NULL, NULL, NULL, '鸡半只1斤、鱼露2大匙、胡椒1/2小匙、黄姜粉或咖哩粉1/2小匙、椰奶或牛奶1/2杯、蒜、芫荽茎（切碎）各1大匙。');
INSERT INTO `recipe` VALUES (2105, 84, '咸肉鸡蛋烤豆及薯条', '1.将咸肉番茄排在扒板上扒至微黄，翻面取下放在纸巾上，沾干多余油脂，保湿。\r\n2.用煎锅加热油，并放入两只鸡蛋煎好后小心转到盘中。\r\n3.另外用一只热锅放入烤豆及番茄汁，根据要求调味。\r\n4.炸薯条至微金黄色，并抖掉多余油脂，撒入盐并搅匀放入盘中，小心沾干多余油脂。\r\n5.排好配料装饰料摆在盘中。', NULL, NULL, NULL, '瘦咸肉50克、煎蛋2个、烤豆80克、薯条1包、扒番茄1个、盐调至够味、白胡椒调至够味。');
INSERT INTO `recipe` VALUES (2106, 66, '拌肉皮丝', '1.将肉皮刮洗干净，放入锅内，加水煮熟（以能掐动为准），捞在凉开水内泡凉，片尽里面的肥膘，坡刀片成极薄的片，再切成极细的丝；葱白切成细丝。 \r\n2.将肉皮丝放入盘内，上面放葱白丝，加入精盐、味精、辣椒油、香油、花椒面，拌匀即可食用。', '麻辣脆香，酒饭皆宜。', NULL, NULL, '主料：猪肉皮（上肋部位）500克，辣椒油，香油，精盐，味精，花椒面，葱白各适量。');
INSERT INTO `recipe` VALUES (2107, 66, '红烧肘子', '1.选皮薄、毛孔细小的新鲜肘子，剔去骨头，修整成圆桃形状。 \r\n2.肘子皮朝上，放入五香料卤锅加热，煮至五成熟捞出。 \r\n3.将油锅烧热，把肘子皮朝下炸呈黄色，炸时不停地翻动，以防糊底。 \r\n4.炸好肘子捞出，放一碗内，加葱、姜，用卤汤对些水，浇入碗中，上屉蒸烂。 \r\n5.将蒸好的肘子扣入大盘内，汤沥在锅中，加入料酒、味精，调一下色味，开始勾芡，撒上青蒜段，淋入明油，浇在肘子上即成。', NULL, NULL, NULL, '肘子1000克，花生油2000克（实耗75克），酱油50克，料酒75克，盐1.5克，葱、姜、五香料少许，淀粉15克，糖10克，汤适量，青蒜少许。');
INSERT INTO `recipe` VALUES (2108, 66, '花椒嫩醉鸡', '1.光鸡洗净，去内脏，滴干水，备用。\r\n2.烧红锅，下两汤匙盐炒至烫手，再放入花椒同炒至花椒有香味变色，盛起。\r\n3.用炒过的花椒盐擦遍鸡身及内腔，用保鲜纸紧包，放在冰箱内腌一日。\r\n4.吃时取出解冻，用水冲去鸡内外腔的花椒盐，将姜片、干葱头放入鸡内腔，以酒擦匀鸡身。\r\n5.隔水蒸约十五分钟，待冷却后即可上碟。', NULL, NULL, NULL, '嫩光鸡一只，花椒50克，盐2汤匙，酒3汤匙，姜3片，干葱头3棵。');
INSERT INTO `recipe` VALUES (2109, 66, '熘肥肠', '1.把熟肥肠切成斜刀片（约一分半厚）。将水发玉兰片、胡萝卜切成片。油菜切成段。葱切成象眼块。姜、蒜切成末。香菜切成段。冬菇切两半。\r\n2.用酱油、醋、花椒水、绍酒、味精、湿淀粉、鸡汤对成汁水。 \r\n3.勺内放猪清油，油热八成时，将肥肠倒入勺内冲炸片刻，倒入漏勺控净油。 \r\n4.勺内放少量油烧热，用葱、姜、蒜炸锅，放入玉兰片、油菜、胡萝卜、冬菇略加煸炒，接着将冲炸好的肥肠倒入勺内，将对好的汁水烹上，再放入香菜段，颠炒几下出勺即成。', '滋味香浓，肠肥软烂。', NULL, NULL, '猪净熟肥肠250克、水发玉兰片10克、水烫油菜15克、水烫胡萝卜10克、水发冬菇10克、猪清油、酱油、醋、花椒水、绍酒、味精、葱、姜、蒜、香菜梗、鸡汤、湿淀粉。');
INSERT INTO `recipe` VALUES (2110, 66, '茄汁草鱼片', '1.选新鲜草鱼肉片切成1厘米厚的片，与绍酒、精盐拌匀，柠檬酸用适量清水溶化。 \r\n2.油锅烧六成热，下鱼肉炸至呈黄色时捞起。\r\n3.炒锅置中火上，下熟菜油50克烧至三成热，放入番茄酱炒香至油呈红色，又放入白糖、柠檬酸、鲜汤推匀，再放入鱼片裹匀茄汁，起锅盛盘。', '颜色红亮，肉细嫩，味甜酸。', NULL, NULL, '草鱼肉500克，番茄酱50克，柠檬酸微量，绍酒10克，精盐2.5克，白糖20克，味精0.5克，鲜汤50克，熟菜油750克。');
INSERT INTO `recipe` VALUES (2111, 66, '酸辣萝卜丝', '1.将萝卜洗净，去皮，切成5厘米长、0.3厘米见方的丝，加入少许精盐拌匀，腌5分钟，挤干水分；青蒜择洗干净，切成3厘米长的粗丝；姜去皮洗净，切成细丝；香菜洗净，切段。\r\n2.将萝卜丝、姜丝、青蒜丝、香菜放入盆内，加入酱油、白糖、醋、味精、香油、辣椒油拌匀，盛入盘内即成。', '脆嫩咸鲜，微辣回甜。', NULL, NULL, '白萝卜300克、香菜15克、辣椒油30克、香油4克、酱油10克，醋20克、白糖15克、味精1克、姜10克、青蒜25克、精盐少许。');
INSERT INTO `recipe` VALUES (2112, 66, '瑶柱鱼翅', '1.鱼翅用开水焯一下捞出，再用鸡汤入味，然后取出装入碗内，上置火腿、猪肘、鸡、鸭肉块，滗出原汁，翻扣在盘中央。 \r\n2.干贝下鸡汤汆一下捞出，配上红绿樱桃，码在鱼翅周围，原汁滤去杂质烧开，勾薄芡淋在鱼翅上即成。', NULL, NULL, NULL, '水发鱼翅300克、干贝100克、红、绿樱桃适量、火腿片适量。');
INSERT INTO `recipe` VALUES (2113, 66, '玉箸山珍', '1.猪里脊肉切丝，加盐、味精、料酒、胡椒粉入味，再加少许蛋清，下6成热油中滑熟捞出。 \r\n2.蕨菜洗净切段。 \r\n3.炒锅置中火上，用葱、姜呛锅后下蕨菜段煸炒至熟，再下入滑熟的肉丝，翻勺炒匀装盘即成。', NULL, NULL, NULL, '猪里脊肉100克、蕨菜500克、鸡蛋、料酒、盐、味精、胡椒粉、葱。');
INSERT INTO `recipe` VALUES (2114, 66, '蛋泡银鱼', '1.将银鱼去掉眼睛，前面的一块骨头及下水，用凉水洗净，用干白布吸干水分，撒上精盐、味精、绍酒卤三分钟，再蘸上一层面粉。 \r\n2.勺内放入清油，油烧至三、四成热时，将银鱼蘸上鸡蛋泡糊，一个一个地放油内炸。炸透时捞在盘内，盘边放上花椒盐即成。', '银鱼软嫩鲜香。', NULL, NULL, '银鱼150克、鸡蛋清3个、淀粉，面粉适量、精盐、味精、绍酒、猪清油、花椒盐。');
INSERT INTO `recipe` VALUES (2115, 66, '红血肠', '1.用铜箩筛过得的生猪血，放入盆里。把白肉汤烧热后，加入胡椒粉、花椒、盐、味精等搅拌均匀后，晾凉。\r\n2.用铜箩把汤滤入猪血内，加入香菜(洗净切成末)搅拌均匀后，灌入洗干净的肠皮内，用线绳绑扎好，放入清水锅内，用旺火烧开后，移入微火上再煮15分钟左右，取出后解除线绳，用凉水浸泡凉。\r\n3.把凉血肠切成5～6毫米厚的薄片，用开水煮热捞出后，在炒勺里加入适量的肉汤，在旺火上烧开以后，再放入血肠片稍微煮煮，连汤一起盛入碗内即成。', '颜色紫红，质地鲜嫩，汤醇鲜美，北国风味。', NULL, NULL, '生猪血1公斤，肠皮300克，肉汤500克，香菜20克，胡椒粉2克，花椒5克，盐30克，味精适量。');
INSERT INTO `recipe` VALUES (2116, 66, '家常饼', '1.将面粉和水均匀，分成小面团，再把面团擀成圆片。\r\n2.将圆片刷上油和盐，做成坯子，再擀成圆饼状。 \r\n3.刷上油，放锅内烙成饼。', '北方主食。', NULL, NULL, '面粉500克，精盐5克，油20克。');
INSERT INTO `recipe` VALUES (2117, 66, '熘花胗', '1.将鸡胗剔去里外的皮，逐个切成深而不透的花刀，胡萝卜、油菜、玉兰片均切成小片，葱切成葱花，姜切成末，蒜拍一下切成末。 \r\n2.勺内放入水，水开时放入鸡胗烫一下捞出控净水。 \r\n3.在碗内放入肉汤、酱油、味精、花椒水、绍酒、醋、水淀粉兑成混汁。 \r\n4.炒勺内放入油，烧至八、九成热时，放入烫过的鸡胗翻动一下，迅速倒出控净油。接着勺内再放入少量的油，用葱、姜蒜作锅，放入玉兰片、胡萝卜、油菜、花胗翻几次，再倒入兑好的汁水，翻几个出勺盛在盘内即成。', '鸡胗开花似菊，清鲜脆嫩，饮酒佳肴。', NULL, NULL, '净鸡胗200克、水发玉兰片15克、水烫胡萝卜10克、水烫油菜15克、酱油、醋、花椒水、味精、葱白、姜、蒜、混合油、鸡汤、水淀粉。');
INSERT INTO `recipe` VALUES (2118, 66, '芹菜鱼丝', '1.将芹菜心洗净，切寸段，青鱼肉顺长切成6厘米长的细丝，加蛋清20克，精盐1.5克，湿淀粉20克拌匀上好浆。\r\n2.浆锅置火上，烧热后放入猪油，烧至三成热时，放入鱼丝，用筷子轻轻滑散滑熟，变色后倒入勺中，沥油。 \r\n3.锅内留少量的油，下入葱姜丝炝锅，倒入芹菜翻炒五成热，加精盐，味精，高汤调味，再放入鱼丝，料酒，用湿淀粉勾芡，淋入香油，颠翻炒勺，出锅即可食用。', '鱼丝鲜香，芹菜脆嫩，清爽利口。', NULL, NULL, '嫩芹菜心150克，净青鱼肉200克，鸡蛋清20克，精盐3克，料酒15克，味精1克，油20克，姜丝5克，湿淀粉25克，葱丝10克，高汤80克，猪油300克。');
INSERT INTO `recipe` VALUES (2119, 66, '鸭腰烧口蘑', '1.将口蘑用开水泡十分钟捞出，去净口蘑根上的泥沙，用水洗净，切成厚片。冬笋、火腿切成片。将鸭腰煮熟，捞在凉水内泡二十分钟，用刀将鸭腰外皮划一道口，剥去外皮切成二半，放入开水内烫一下捞出。 \r\n2.锅内放猪油少许，油烧热时，用酱油炸锅，添汤，加精盐、绍酒、白糖少许，再放入鸭腰、口蘑、冬笋、火腿、豌豆。汤烧开后移在小火上煨三分钟，放入味精，用水淀粉勾芡，加点明油翻一个个，出久盛盘即成。', '鲜嫩不腻，香气扑鼻。', NULL, NULL, '鸭腰12个，水发口蘑100克，冬笋10克，熟火腿10克，豌豆15克、酱油、水淀粉、味精、绍酒、白糖、猪油、鸡汤。');
INSERT INTO `recipe` VALUES (2120, 66, '鱼腹藏羊肉', '1.将洗净鲤鱼整鱼剔骨后加调料稍微腌制。\r\n2.将羊肉、冬笋、香菇等切成米粒状，加调料放入锅中煸炒后填入鱼腹中。 \r\n3.将鱼用猪网油裹好后放置于烤炉内烤熟。 \r\n4.将红辣椒、黄瓜切成细丝，摆放在烤好的鱼腹上即可。', '在北方水产以鲤鱼为最鲜，肉以羊肉为最鲜，此菜两鲜并用，互相搭配，烤制而成。成菜色泽光润，外酥里嫩，鲜美异常。', NULL, NULL, '鲜鲤鱼700克、净羊肉200克、香菇50克、冬笋30克、黄瓜20克、红辣椒15克、精盐20克、酱油10克、料酒25克，醋，糖各20克。');
INSERT INTO `recipe` VALUES (2121, 84, '扒鸡', '1.将洋葱、蒜、姜香料及一半的调味品放在一盆中搅匀，倒入柠檬汁、醋及酸奶。\r\n2.将鸡涂上上述腌料，并腌制一晚（注意：鸡应去骨，并修净，半个鸡翅）。\r\n3.制作时将鸡的皮面放在扒炉上，将其放在烤盘上，入160度角，烤12－15分钟，用小刀扎鸡腿骨处，如无血溢出，肉即熟了。 \r\n4.配以白米饭、酸奶汁、黄瓜沙拉及其他装饰，摆放在盘中。', NULL, NULL, NULL, '鸡（1.2公斤）切为一半、去皮及骨1个、白酸奶225毫升、麦芽醋75毫升、洋葱细丁20克、大蒜酱30克、姜、鲜酱30克、印度混合香料5克、辣椒粉1.5克、红粉2克、红色素0.5毫升、黄色素0.5毫升、盐2.5毫升、黑胡椒粉2.5毫升、柠檬（浓缩汁）0.5个。');
INSERT INTO `recipe` VALUES (2122, 84, '扒酿馅小牛排', '1.用刀将牛排切为一个袋，大约深3厘米、长4厘米，填入少许调味品及蘑菇酱。\r\n2.在肉表面涂少许酱、胡椒、柠檬屑、香草、大蒜、油及芥末，浸泡20分钟。\r\n3.将其两面扒纹并制成所需火候。 \r\n4.用另外一只锅炒土豆，然后放在餐盘中央，小牛排放在土豆上，小心地用勺将汁淋上，同样地淋上香草黄油并用明火烤稍微上色。\r\n5.放上熏肉及洋紫苏，摆上装饰及配料。', NULL, NULL, NULL, '小牛柳排225克、野蘑菇酱30克、瘦的熏肉片（意大利）25克、干番茄及洋紫苏汁、热的50毫升、土豆饼（15厘米）1个、时令沙拉1个、沙拉汁40毫升、法香汁1个、香草黄油汁25毫升、面包2个、份装黄油20克、盐调至够味、黑胡椒粉调至够味、柠檬屑焯的2克、混合香草1克、橄榄油20毫升、英国芥末20克、柠檬汁20毫升、大蒜酱5克。');
INSERT INTO `recipe` VALUES (2123, 84, '花生素菜色拉', '1.将花生炒熟待冷却后，与虾糕、蒜瓣、辣椒（去籽）、砂糖、精盐、酸荚豆，月桂叶放在一起捣成粗末，用香油煸片刻装盆备用。\r\n2.另将刀豆、豆芽、蕹菜、圆白菜洗净切段，用沸水烫熟捞出控干备用。\r\n3.将老豆腐洗净切小块，用食油炸至金黄熟时盛盆备用。\r\n4.起菜时，将烫熟的素菜与炸好的豆腐放在一起，倒上花生末，淋上香油调匀即可食用。', NULL, NULL, NULL, '花生120克，刀豆50克，圆白菜50克，豆芽50克，蕹菜50克，老豆腐50克，虾糕1小块，蒜瓣2个，辣椒1个，月桂叶1片，砂糖、精盐、香油、酸荚豆少许。');
INSERT INTO `recipe` VALUES (2124, 84, '冷番茄荡配红花虾仁', '1.除装饰外，混合所有的配料，在汤锅中并冷藏一晚。\r\n2.打碎混溶，并过滤。\r\n3.放上装饰及配料。', NULL, NULL, NULL, '番茄籽1千克、黄瓜、去皮、去籽并切成丁285克、洋葱丁285克、红椒丁285克、白面包丁225克、橄榄油180毫升、红酒醋120毫升、盐调至够味、白胡椒粉调至够味、番茄细丁60克、去皮黄瓜细丁60克、红椒细丁60克、绿椒细丁60克、面包丁225克、红花虾仁500克。');
INSERT INTO `recipe` VALUES (2125, 84, '牛肉火锅面', '1.把调料放入白面里一起搅拌，加工出面条。\r\n2.把各种蔬菜和原料一起煮开，时间约几小时左右。把肉汤用过滤网过滤，再放入调料重新烧开。', NULL, NULL, NULL, '面条、牛肉、香菇、鲜蘑、金针菇、白菜、大葱、韭菜、鸡蛋、高蒿等。');
INSERT INTO `recipe` VALUES (2126, 84, '印尼炒饭', '1.把牛肉剁成末，蘑菇切片。\r\n2.在炒锅中加色拉油、牛肉末、蘑菇片炒熟后加入饭翻炒，饭热后加入咖喱粉、盐、胡椒粉、葡萄干再次翻炒匀后即可食用。', NULL, NULL, NULL, '饭360克、牛肉80克、咖喱粉10克、葡萄干5克、盐少许、胡椒粉少许、蘑菇10克、色拉油适量。');
INSERT INTO `recipe` VALUES (2127, 84, '炸海蜊子肉', '1.将海蜊子肉洗净，撒上精盐、胡椒粉、雪利酒、白醋。\r\n2.腌制20分钟后，蘸上一层面粉、再沾一层鸡蛋液，然后均匀撒上面包屑备用。\r\n3.把锅烧热后倒入食油，待油温至5成时逐个放海蜊子肉，炸至黄色熟香即可。', NULL, NULL, NULL, '海蜊子肉200克，鸡蛋50克，面粉少许，食油100克，雪利酒15克，面包屑15克，精盐、白醋、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (2128, 85, '白菜薄荷', '1.把圆白菜洗净，切细丝，放入水中泡5分钟，取出，控水；再把葱头横切成片，越薄越好，撒上盐揉搓一下，用冷水泡2分钟，取出，控水；然后把橙子去皮，横切成2毫米厚的薄片；芹菜洗净，择好，切碎末；杏仁去皮，切碎。 \r\n2.将白砂糖、白醋、肉豆蔻粉(中药房有售)、盐拌匀，与圆白菜丝、葱头片、橙子片放在一起拌匀后，放在盘一边。 \r\n3.把山楂糕改切成薄荷叶大小，每片5毫米厚，共15片，用两片薄荷叶(中药房有售)夹住l片山楂片，然后沾上发粉糊，下七成热的油锅中，炸至呈金黄色，捞出，沥油，在炸好的薄荷叶坯一面上，用筷子', NULL, '发粉糊：50克发酵粉用400毫升温水调开，加适量面粉拌匀而成。', NULL, '圆白菜300克，薄荷叶30片，葱头85克，橙子3个，芹菜末5克，杏仁10克，山楂糕60克，生菜油100克，白醋10克，白砂糖75克，发粉糊50克，肉豆蔻粉3克，盐适量。');
INSERT INTO `recipe` VALUES (2129, 84, '扒牛柳', '1.用盐及胡椒调味牛柳，并涂少量芥末、蒜及油，将其放在扒炉上，根据要求制成嫩、五成、全熟等，用扒制时间来控制达到要求。\r\n2.使肉有条纹痕迹（1分钟后），将肉转90度角，重复上述步骤，使肉有交叉条纹，翻过来另一面，重复同样步骤，上色。\r\n3.根据肉的软硬状况来判断内部温度（嫩、五成、全熟等），配以香草黄油或胡椒汁。\r\n4.排好牛排及其它装饰放在盘中。', NULL, NULL, NULL, '澳洲牛柳200克、香草黄油25克、胡椒汁50毫升、薯条或其它土豆1包、时令蔬菜1包、沙拉边1个、扒番茄一个90克、沙拉装饰1个、面包2个、份装黄油20克、盐调至够味、黑胡椒粉调至够味、英式或地壮芥末20克、蒜酱10克、植物油15毫升、沙拉汁40毫升。');
INSERT INTO `recipe` VALUES (2130, 84, '煎鱿鱼带子配黑豆汁', '1.在柠檬汁喔彻斯特汁，辣椒籽汁及盐胡椒的混合物中浸软带子及鱿鱼1－2分钟。\r\n2.将其取出沾上面粉（混入红粉的），少量但要均匀，放入盘中并根据需要调料。 \r\n3.在大的炒锅中加热油，然后煎带子及鱿鱼，保持脆及美观，并保温。\r\n4.然后放在排有生菜的盘中，用冷藏卷曲的红椒丝撒在上面装饰，然后小心地用勺浇上黑豆汁。', NULL, NULL, NULL, '小鱿鱼，鲜净或冻鱼（解冻）400克；带子，冻，鲜400克；混合生菜叶，净的300克；红椒（1只）70克；面粉过箩的500克；盐2克；黑胡椒粉2克；红粉，甜30克；柠檬汁30毫升；喔彻斯特汁5毫升；辣椒籽2毫升；橄榄油100毫升；黑豆汁250毫升。');
INSERT INTO `recipe` VALUES (2131, 84, '柠檬蟹肉汤', '1.将葱头洗净切成薄片，芹菜洗净切段备用。\r\n2.把锅烧热后放入黄油，待融化后放入葱头片、芹菜段炒至上色盛起。\r\n3.另起一锅，放入少许清水，撒入面粉调匀，加入砂糖、牛奶、红辣椒粉用微火煮沸。\r\n4.放入葱丝、芹菜、蟹肉煮熟。\r\n5.加精盐、胡椒粉、柠檬汁、雪利酒调好口味微沸即可。', NULL, NULL, NULL, '蟹肉250克，牛奶500克，葱头25克，芹菜25克，面粉15克，鲜奶油125克，黄油25克，砂糖15克，柠檬汁50克，红辣椒粉少许，雪利酒25克，精盐适量。');
INSERT INTO `recipe` VALUES (2132, 84, '石锅拌饭', '1.准备蒸好的白米饭，再准备与之相拌的蔬菜。\r\n2.只煎鸡蛋的一面，使蛋黄完整而表面凝结，使其脱离蛋清而不破裂。准备两个嫩蛋黄。\r\n3.米饭在放入石锅前，在石锅底部及边缘涂抹一层香油，其作用是不使米饭粘锅，同时添加油香味道。\r\n4.准备一小碗佐餐的淡汤，当你觉得饭干时可拌入；准备一小碟辣椒酱，适量拌入后非常提味。\r\n5.石锅放入米饭后，再在表面呈扇状铺上与之相拌的蔬菜、肉末与鸡蛋，加热后当香油兹兹作响时端下，此时，浓香四溢，你可以用筷子搅拌使它们混合，视觉效果五彩斑斓，在它们与石锅热接触中，增加你的味觉快', NULL, NULL, NULL, '米饭，香油，肉末，鸡蛋，凉拌的嫩豆芽和青菜，炒好的蕨菜、红萝卜丝，炒好的肉末。');
INSERT INTO `recipe` VALUES (2133, 84, '云呢拿忌廉布甸', '1.鱼胶粉用4倍水开匀。\r\n2.将忌廉、鲜奶及糖用慢火加热2分钟后离火，加入呢拿香油拌匀。\r\n3.加入鱼胶粉溶液，用慢火煮至完全溶解。熄火待凉后，加入甜酒。 \r\n4.将混合液倒入喱杯中，放入雪柜冻至凝固即成。', NULL, NULL, NULL, '忌廉1公升、鲜奶7汤匙、糖180克、呢拿香油数滴、鱼胶粉16克、杏仁甜酒4汤匙。');
INSERT INTO `recipe` VALUES (2134, 85, '巴黎卷心菜', '1.卷心菜切成4块，剥去外层老叶，焯6分钟，捞出，控去水分，再切成大块。\r\n2.黄油入平底锅，加入葱头片，旱芹块，翻炒5分钟，倒入卷心菜，改小火，加大蒜瓣、鸡清汤、香叶、香芹、百里香粉、用小火煮15分钟。 \r\n3.制出白色调味汁(参看牡蛎汤)，卷心菜绞成菜泥后，再加入汤里，倒入白色调味汁，加盐、胡椒粉。 \r\n4.火腿切成薄片后，放入汤内，放入香芹片，烤面包片单跟即可。 \r\n5.另一种食法：不用烤面包，用夹心巧克力球、加盐、奶油少许、胡椒粉，拌匀一起喝，风味独道。', NULL, NULL, NULL, '卷心菜1棵，葱头1个，香叶1片，香芹1棵，百里香粉5克，大蒜头2瓣，火腿150克，黄油50克，白色调味汁50克，鸡清汤150毫升，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2135, 85, '法式拌菜花', '1.将菜花洗净，拆成小朵，用盐水浸泡约10分钟，再洗净，用沸水煮熟，捞出控干。 \r\n2.将洋葱洗净，切成小丁。 \r\n3.将芹菜叶洗净，切成末。 \r\n4.将煮菜花、洋葱丁、芹菜叶末放入盆内，再加沙拉油、胡椒粉、盐、醋拌匀即可。', NULL, NULL, NULL, '菜花750克、洋葱50克、精盐5克、沙拉油50克、醋5克、胡椒粉少许、芹菜叶15克。');
INSERT INTO `recipe` VALUES (2136, 85, '槟榔排骨锅', '1.猪肠清洗干净，剪成3公分，排骨烫过，洗净槟榔。 \r\n2.排骨铺在砂锅底层，依此序排上猪肠、槟榔、高汤、盐、米酒，滚开小火焖30分钟。 \r\n3.放枸杞，焖煮5分钟即可。', NULL, NULL, NULL, '幼齿槟榔12粒、枸杞半两、猪肠半斤、排骨一斤、高汤6杯、盐少许、米酒2大匙。');
INSERT INTO `recipe` VALUES (2137, 85, '冰冻茄丁', '1.将茄子洗净，去蒂，切成小方丁。 \r\n2.炒勺上火，加花生油烧至五成热，放入茄丁炸熟成黄色，捞出，控油。 \r\n3.再把芹菜末、葱末、葱头末放入炒勺内，炒至牙黄色，放番茄汁和茄丁，用温火煨10分钟。 \r\n4.然后，放入白醋、蜂蜜、柠檬、胡椒粉等调味料，再煨10分钟，翻炒均匀，收浓汤汁，放盐拌匀，出勺后晾凉，放入冰箱冷藏。 \r\n5.食用时配面包、脆饼。', NULL, NULL, NULL, '茄子2个，番茄汁50克，葱头25克，芹菜末25克，柠檬片25克，大葱25克，花生油50克，蜂蜜、白醋、胡椒粉、盐各适量。');
INSERT INTO `recipe` VALUES (2138, 85, '菠菜乳酪焗生蚝', '1.生蚝去壳洗净，用干葱、白酒煮熟，蚝壳飞水沥干。\r\n2.菠菜洗净，用蒜蓉炒熟，放在蚝壳上，然后把生蚝放在上面。\r\n3.牛油炒面粉，加鲜奶及上汤，至加完为止，洒些芝士蓉兜匀，淋在生蚝上。\r\n4.面包糠与芝士粉拌匀，洒在白汁上，即可放入焗炉以250度，焗5分钟即成。', NULL, NULL, NULL, '生蚝8只、菠菜或苋菜4两（160克）、蒜茸1茶匙、牛油1安士、面粉3汤匙、鲜奶3汤匙、上汤1/2杯、面包糠适量、芝士粉适量、干葱1粒（切片）、白酒1汤匙。');
INSERT INTO `recipe` VALUES (2139, 85, '菠菜沙拉', '1.制出醋油沙司：将两个熟蛋黄搓碎，用箩过入盆内，放上10克白砂椒粉、20克芥末沙司、30克精盐、5克味精，用勺仔细地搅匀，使蛋黄成泥，再陆续倒入生菜油100克，随倒随搅，一点点搅开。然后倒入50克醋精，搅匀，调好味，即可过箩装入干净的瓶内，加盖保存。使用时，可把瓶子晃动一下再往外倒。醋油沙司，也称法国沙司。 \r\n2.菠菜择洗干净，用开水烫一下，切成碎段，堆在盘中央成山丘形。 \r\n3.煮鸡蛋去皮，用绳线割切成6瓣，码在菠菜上二分之一，其余围盘边，葱头末加醋油沙司搅匀，浇在菠菜上，即可。', NULL, NULL, NULL, '菠菜1.5分斤，煮鸡蛋500克，葱头末150克，醋油沙司。');
INSERT INTO `recipe` VALUES (2140, 85, '菠萝莴笋', '1.莴笋去皮、叶、根、洗净后，切成梳子背块，用开水烫熟，控干，再放精盐稍腌片刻，入凉开水中漂洗一次，沥净水分，盛入盘内。\r\n2.菠萝切成小丁盛碗内，放入糖水（白糖预先用少许凉开水化开）、白醋、味精拌匀，置冰箱内镇凉后，浇在莴笋块上即成。', '酸甜脆香，清爽可口。', NULL, NULL, '莴笋500克，菠萝（罐装）200克，白糖100克，白醋5克，精盐、味精、清水各适量。');
INSERT INTO `recipe` VALUES (2141, 85, '草莓黄瓜', '1.黄瓜用清水洗净，切去两头，再切成“梳子背”块形，放入小盆内，加精盐腌10分钟，捞出，凉水中稍漂洗，轻轻挤干水分，盛盘内。 \r\n2.将白糖用凉开水溶化，把草莓去蒂，洗净、控干、碾碎，淋入糖水、白醋，加味精拌匀，入冰箱冷冻后取出，浇在黄瓜块上即成。', NULL, NULL, NULL, '黄瓜500克，草莓200克，白糖100克，白醋5克，精盐、味精、清水各适量。');
INSERT INTO `recipe` VALUES (2142, 85, '洞房喜汤', '1.鸡块、牛肉块、胡萝卜块放入锅内，加清水，大火烧开，小火煨成汤，待用。\r\n2.葱头去皮，洗净，切成薄片；西红柿洗净，去蒂，一切4块。 \r\n3.厚平底锅烧热，放鹅油，烧五成热，放入葱头片，小火翻炒成牙黄色，再用大火煸炒番茄块，炒匀，放入已煮好的肉菜汤内，煮开lO分钟后，捞出葱头、番茄，绞成泥后放入锅，撒入胡椒粉，烤面包皮。 \r\n4.再把汤烧开，加入细面条，煮5分钟(把面条煮熟)，放盐调好口味。把烤面包皮分均在两个大碗里，再倒入汤，即可。', NULL, NULL, NULL, '葱头1个，鹅油(或鸡油)30克，鸡块150克，牛肉块150克，胡萝卜2根，西红柿4个，小面包皮8块，细面条50克，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2143, 85, '炖小牛肉', '1.剥好并剁碎洋葱。把小牛肉切成2英寸的小方块。磨好两个柠檬，然后把四个酸橙剥好并把它们弄成小块。 \r\n2.把黄油倒入焙盘并加入盐和胡椒粉。 \r\n3.加入小牛肉，撒上面粉搅拌并烹饪1-2分钟。 \r\n4.压挤柠檬和酸橙并把果汁倒入焙盘，倒入白葡萄酒高于牛肉。用低火烹饪大约30分钟。 \r\n5.打好鸡蛋，加入胡椒粉、食盐和奶酪，把它们混合。 \r\n6.加入混合物到小牛肉里，当沸腾时就拿下。', NULL, NULL, NULL, '2.4斤去骨的牛肩肉、1杯鲜乳酪、6个鸡蛋、3个大洋葱、3个柠檬、2勺面粉、50克黄油、4个酸橙、3杯干白葡萄酒、食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2144, 85, '多味鱼汤', '1.将绯鲤、无须鳕、海鳗、江鳕四种鱼去鳞、开膛、剥皮、剔去鱼骨，切成块。葱头、大蒜瓣和韭葱合在一起切碎。放油锅，烧热，倒入切碎的葱头、大蒜和韭葱，煽炒出香味。\r\n2.鱼块入锅，炸成两面黄色，沥出炸油。加清水，炒葱头、大蒜和韭，再放入西红柿块、茵香、香芹段、风轮菜、香叶和藏红花，加盐、胡椒粉，再倒入鱼头、鱼骨，盖盖，用旺火煮30分钟。\r\n3.制出红色调味汁：3瓣大蒜加去籽辣椒倒入研钵，再加入藏红、少许盐，一起研成膏；熟土豆去皮，压碎后，放入研钵，边加油边研磨，研成膏状。\r\n4.汤煮熟后，捞出风轮菜和香叶，锅', '鱼鲜宜人，口味浓郁，酸辣适中。', NULL, NULL, '海鱼(绯鲤、无须鳕、海鳗、江鳕)2000克，葱头2个，大蒜8瓣，韭葱1根，西红柿3个，茵香5克，香芹l根，香叶1片，风轮菜1片，干辣椒1个，面包1个，熟土豆1个，红色调味汁、藏红花、色拉油、盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2145, 85, '鹅肝酱煎鲜贝', '1.鲜贝略泡水，即泡冰水急速冷却，取出在侧边切一半深，加入葱、姜、盐、酒约10分钟，再煎至两面黄，上盘。 \r\n2.芦笋取花蕊部分，以盐水烫熟，作盘饰。 \r\n3.取出鹅肝酱，略煎，置于鲜贝上，淋上酱汁，芦笋装饰即可上桌。', NULL, NULL, NULL, '法国鹅肝酱3片、鲜干贝6粒、芦笋6支、洋葱100克、红萝卜30克、洋葱20克、盐、糖、太白粉');
INSERT INTO `recipe` VALUES (2146, 85, '鹅肝温沙拉', '1.用炒锅加热黄油，煎干表面，直至金黄，然后取出。\r\n2.向锅内加入洋葱，一半香草并炒至汁液开始蒸发，加入苹果及核桃，继续煮，使味道进入其中。\r\n3.倒入白兰地并燃烧，浓缩一半，将鹅肝放回锅内，然后将锅从火上取下。 \r\n4.在盘中放好混合的生菜及香草，将鸭肝放上，用勺将油、醋汁、及肝汁搅匀然后倒入黄油，撒上红黄椒丝。\r\n5.排好装饰及配料。', NULL, NULL, NULL, '鲜鹅肝，片状100克；洋葱，细碎30克；混合鲜香草3克；苹果，去皮、核，切成丁30克；黄油20克；苹果白兰地25毫升；黑香草醋汁20毫升；酸酱3克；核桃，烤碎，5克；黑香草醋10毫升；黑胡椒1克；盐1克；混合生菜60克；混合香草10克；红黄椒丝4克。');
INSERT INTO `recipe` VALUES (2147, 85, '鳄梨生鲑鱼', '1.剥好鳄梨并把它们切成小片。 \r\n2.把三个柠檬压挤出汁。剥好洋葱并把它们切成碎末。 \r\n3.在一个搅拌器里用黄油乳化鳄梨。 \r\n4.加入食盐和胡椒粉并注入橄榄油混合。 \r\n5.加入柠檬汁和大蒜，再次搅拌。把它们放在冰箱里。 \r\n6.在每个鲑鱼薄片上加入一匙鳄梨，摇晃并注入橄榄油和柠檬汁，最后再撒上胡椒粉和食盐。', NULL, NULL, NULL, '3个鳄梨、1盘新鲜鲑鱼、3个柠檬、10毫升橄榄油、3片大蒜、150克黄油、少许食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2148, 85, '法国白酒田螺', '1.锅内加热，将田螺肉炒香备用。备一锅热水烫熟面皮，排进盘内。另置一锅融解奶油，放大蒜碎炒香。 \r\n2.再加田螺肉，白葡萄酒，使酒精蒸发。加奶油及鲜奶油，置放于盘内，部分塞于面皮内，周围淋上剩余的白酒汁即可。', NULL, NULL, NULL, '田螺6-8个、千层面皮2张、白葡萄酒20CC、黄奶油15公克、大蒜碎少许、奶油40公克、鲜奶油10CC。');
INSERT INTO `recipe` VALUES (2149, 85, '法国鹅肝酱煎鲜贝', '1.鲜贝略泡冰水急速冷却，取出，在侧边切一半深，加入葱、姜、盐、酒约10分钟，再煎至两面变黄，上盘。 \r\n2.芦笋取花蕊部分，以盐水烫熟，作盘饰。 \r\n3.取出鹅肝酱略煎，置于鲜贝上，淋上酱汁，芦笋装饰即可上桌。', NULL, NULL, NULL, '法国鹅肝酱3片、鲜干贝6粒、芦笋6支、洋葱100克、红萝卜30克、洋葱20克。');
INSERT INTO `recipe` VALUES (2150, 85, '法国荞麦煎饼', '用荞麦（即黑麦）做的煎饼叫“ble noir”，颜色呈深啡，含丰富的蛋白质及维他命。基本上没味道的煎饼，加进两只半熟鸡蛋、火腿及芝士已滋味非常。', NULL, NULL, NULL, '鸡蛋、烟肉、芝士馅煎饼。');
INSERT INTO `recipe` VALUES (2151, 85, '法式薄牛排', '将肺利牛肉切成10块，放砧板上逐块用刀拍松拍薄，摊放在台子上，撒上精盐和胡椒粉调味后，稍稍蘸上一点面粉，放一边备用。将平底煎锅放火上烧热，加入少许生菜油后铺入薄牛排，用大火将两面迅速煎黄后，烹入黄酒和辣酱油后稍翻一下，装入盆子内，边上配放一些洋葱土豆或其他应时素菜后即可上台食用。', NULL, '牛肉质地要嫩，否则就不宜制此菜。', NULL, '肺利牛肉500克、精盐、胡椒粉和面粉各少许，黄酒25克，辣酱油30克，配菜用洋葱土豆或其他应时素菜适量。');
INSERT INTO `recipe` VALUES (2152, 85, '法式长棍', '将面粉、白糖、精盐、麦淇淋和奶粉一起放入搅拌盆内，把酵母粉溶解在水里放入盆内，搅打至起韧劲，揉制成匀滑的面团，醒发60分钟左右。预热烤箱至205度。将发好的面团取出，分割成所需大小，搓成长棍形，排列在烤盘内，再醒发至原来体积的一倍时，进入烤炉内烤约25分钟，至表面褐黄外壳硬脆时取出即可食用。', NULL, '搓长棍时一定要卷紧并不要将表皮搓毛，以免影响外观。', NULL, '精白面粉500克、白糖15克、细盐8克、麦淇淋50克、奶粉8克、酵母粉5克、清水300克。');
INSERT INTO `recipe` VALUES (2153, 85, '法式蛋白糖', '将蛋白放在一只搅拌盆内，用蛋扦猛烈急速地打成白雪泡沫状时，分几次将白糖加入并仍不断地搅打均匀，至白糖搅打到完全溶化后，筛入糖粉，仔细地拌和均匀即成法式蛋白糖。此蛋白糖多用于装饰表面和制作各种蛋白饼之用。', NULL, NULL, NULL, '蛋白5只、细白砂糖145克、糖粉125克。');
INSERT INTO `recipe` VALUES (2154, 85, '法式蛋煎鱼', '将鱼肉用刀斜批成形，平摊在台板上，撒上精盐和胡椒粉加以调味后烹上黄酒， 两面蘸上面粉，放在搅匀的鸡蛋液时浸蘸一下裹上蛋液后备用。取一只平底煎锅放火上，加入少许油烧热后，放入备用的鱼块，用小火煎至两面呈金黄色并熟透时，放入白脱油再两面煎翻一下，将鱼块分装入数只小盆中，然后将布朗沙司倒入平底煎锅内，再烧至沸滚时，浇在鱼块上面，趁时撒上切成碎末的香菜并饰以一片柠檬片后即可上台供食。', '煎鱼时火不宜太大，以免外焦里生。', NULL, NULL, '出骨鱼肉500克，精盐、胡椒粉、黄酒和面粉各少许，鸡蛋三只，白脱油30克，布朗沙司100克，供装饰和使用的香菜和新鲜柠檬各适量。');
INSERT INTO `recipe` VALUES (2155, 85, '法式烩土豆', '1.土豆用水洗净，去皮后切成丁，洋葱去皮切碎。蒜去皮拍碎。 \r\n2.用厚底铝锅，置于火上，烧融黄油，然后把葱和蒜下入，炒至洋葱呈透明，把土豆丁加入搅拌几分钟。土豆丁炒到全部挂上油后，加入蔬菜汤、香叶、少许盐、胡椒面、味精、搅拌均匀。如果水少，可以再加一些，但不要太多，约微沸45分钟，要不停地搅拌，勿使其糊底。到土豆熟时，再放些植物油和酒，混合好即可，装盘时撒上一些芹菜末。', NULL, NULL, NULL, '土豆500克、洋葱40克、黄油25克、蒜少许、浓蔬菜汤125克、香叶2片、盐胡椒面、植物油、白葡萄酒各少许、切碎的芹菜少许。');
INSERT INTO `recipe` VALUES (2156, 85, '法式火焰薄饼', '1.将一大型平底锅加热后，将火转小。 \r\n2.将白糖均匀撒入锅中，使融化，过程中可以将锅距离火约5公分，并不时转动，以避免糖变焦。 \r\n3.待所有白糖融化约2/3后，将奶油放入，并转动平底锅以确保融化后的奶油完全均匀的分布在锅底。 \r\n4.将柠檬丝和柠檬汁倒入。 \r\n5.倒入柳橙汁。 \r\n6.将半个柳橙面朝锅面涂抹一次，此步骤是为了避免尚有未融化的糖黏在锅底，并可以使锅中的所有材料均匀混合。 \r\n7.将薄饼对折两次，使成1/4之大小后放入。 \r\n8.将白兰地柑橘酒（GrandMarnier）倒入一汤瓢中，', NULL, NULL, NULL, '薄饼（crepe）2片、香草冰淇淋1球、香橙半个、柳橙皮（用刀将整个柳橙皮以环形方式削下备用）、柳橙汁4-6oz、柠檬丝约十条、柠檬汁0.5oz、白糖5茶匙、奶油（butter）1汤匙、白兰地柑橘酒（GrandMarnier）1oz。');
INSERT INTO `recipe` VALUES (2157, 85, '法式鸡肉色拉', '把土豆剥去外皮，苹果去外皮后剖开除去果核，然后将土豆、苹果和鸡肉一起切成差不多大小的小丁， 放入盛器内，加入切成碎末的青葱，放入各种调味品加以调味并拌入色拉油沙司，至完全拌匀并对好口味时即成鸡肉色拉。把装饰用的生菜叶铺在盆子底部，中央堆放上鸡肉色拉，把番茄和煮蛋用刀交叉剖成块，呈放射状地饰围在鸡肉色拉旁边即可上台食用。', '做色拉时，其主料的大小和形状应基本保持一致。', NULL, NULL, '煮熟土豆150克、青蕉或国光苹果1只约150克、煮熟鸡肉150克、青葱2根、精盐、味精、胡椒粉、辣酱油和鲜奶油各少许、色拉油沙司100克、装饰用生菜叶、番茄和煮蛋各适量。');
INSERT INTO `recipe` VALUES (2158, 85, '法式蘑菇蛋卷', '1.洋茹切片，洋葱切丝，以1匙奶油炒成金黄色，加盐、胡椒粉调味。\r\n2.蛋和牛奶加盐搅匀以成蛋汁。\r\n3.平底锅烧热后加入1匙奶油融解，再倒入蛋汁。', NULL, NULL, NULL, '洋茹约5-7朵，洋葱1/4个，奶油2大匙，盐、胡椒少许，蛋2个，牛奶1大匙。');
INSERT INTO `recipe` VALUES (2159, 85, '法式柠檬塔', '将开面放在台板上，撒上一些面粉后，用擀面杖将其擀压成厚约5毫米的面片，然后用直径为7厘米半的圆形刻模刻出10块面饼，铺放在塔盆内，并用手将其揿压一周成浅碗状，装入烤盘内备用。将白脱油和白糖一起放在一只小锅内，放火上慢慢熬制融化时移离火源，将蛋黄和柠檬及磨碎柠檬外皮一起放入一只小的搅拌盆内，搅打混合均匀，用汤匙装入塔盆内，并将剩余的面团再擀成薄片，用齿轮夹上的齿轮切割成6×1厘米的小长条，在每只塔盆面的边沿上，用软刷涂上一点蛋液，粘上三根小长条成交叉状，再用软刷蘸上蛋液在表面涂刷上一层后，进入预热至195度', NULL, '面皮一定要压成浅碗状，不然就装不多馅心，而且形状难看。', NULL, '开面350克、白脱油200克、白糖300克、蛋黄3只、柠檬汁及磨碎柠檬外皮2只。');
INSERT INTO `recipe` VALUES (2160, 85, '面壳肠子', '1.先制出巴代面：把90克黄油、150克面粉、8克盐及冰水100毫升都放入大瓷盆里，掺匀，和好成团，揉匀，表面撒些面粉，用塑料薄膜包上，放入冰箱冷却3小时，取出，用手掌把面团压成2.5厘米，撒上干面粉，成直径2.5厘米、厚3毫米的圆片，再抹上30克黄油，入烤箱190度温度烤成浅黄色，晾凉成巴代壳。待用。 \r\n2.再把生猪肉蒜肠用叉子扎5个眼，以防肠子破裂，然后把肠子放在锅里，加冷水（水要没过肠子）用中火烧开，改小火煨50分钟捞出，晾凉，去皮。\r\n3.在巴代壳上放肠子包好，接口处刷上蛋液，放在抹了油的烤盘上', NULL, NULL, NULL, '生猪肉蒜肠900克，鸡蛋1个，巴代面200克，芥末汁25克，生菜叶150克。');
INSERT INTO `recipe` VALUES (2161, 82, '八宝鹌鹑', '1.将鹌鹑去头后，脱骨，洗净，沥去水分后，用精盐、料酒、葱姜末、腌渍入味。\r\n2.再将猪肉、罐头竹笋片均切成丁，莲子、枸杞、芡实、丁香、花椒等用清水胀透，滗去水分。\r\n3.肉丁、笋丁、莲子、枸杞、芡实、葱末、姜末加入精盐、料酒，少许熟花生油搅拌均匀成馅，装入鹌鹑腹内，入瓷盆，再加上丁香、花椒、葱段、姜片上蒸锅蒸透后，晾凉，控干。拍上淀粉，待炸。\r\n4.炒锅烧热，倒入熟花生油烧至五成热时，投入龙虾片，炸脆，捞出，待油烧至六成热时，将鹌鹑逐只放入油锅中，炸至金黄色，捞出，分别装入小盘，以龙虾片镶边。\r\n5.食', '皮脆肉酥，滋味鲜美，香糯适口。', NULL, NULL, '净鹌鹑10只，芡实100克，莲子50克，枸杞10克，猪肉50克，淀粉15克，罐头竹笋25克，精盐2.5克，料酒15克，丁香8粒，葱末10克，花椒2克，熟花生油1500克（实耗60克），姜末10克，葱末15克，龙虾片50克，辣酱50克。');
INSERT INTO `recipe` VALUES (2162, 82, '扒莲蓉鹌鹑', '1.将鹌鹑洗净，整只脱去骨头（皮不要破损）；瘦肉粒放在碗内，加湿淀粉拌匀，下开水锅内氽一下，捞出，与各种粒末一起盛入碗内，加入精盐、味精、白糖、熟鸡油、淀粉拌匀，分成10份，分别酿入鹌鹑腹内，咸蛋黄一开四片，每只鹌鹑内放入一片，再将破口处扎牢，下开水锅中氽一下，捞出，抹上酱油，待用。\r\n2.炒锅净后烧热，倒入花生油烧五成热，下入鹌鹑炸至金黄色，捞出，控油，放入瓷盆内，加入鸡汤200毫升、料酒、酱油、葱姜、上蒸锅蒸烂后，捞出，头朝外放入圆盆内，油菜心用油加水炒熟后，围在鹌鹑四周。\r\n3.炒锅上火，倒入熟豆油', '色泽金黄，郁浓嫩滑，香辣爽口。', NULL, NULL, '净鹌鹑10只，花生油500克（实耗50克），莲子末400克，火腿粒50克，酱油25克，冬菇粒50克，辣椒油10克，咸蛋黄3只，精盐8克，油菜心100克，熟鸡油20克，淀粉40克，鸡汤500毫升，葱段10克，芝麻25克（焙好），姜片10克。');
INSERT INTO `recipe` VALUES (2163, 82, '葱爆牛肉', '1.牛腱子肉洗净，去筋膜，用清水浸泡2小时后，捞出，控干，切成小方块，再捣成薄片，把大葱白洗净，切成滚刀片。\r\n2.牛肉放入瓷碗内，放入芝麻、大蒜末、生姜末、酱油25克、辣椒面、黄酒、味精，搅拌均匀，腌渍，使牛肉渗进调味料。\r\n3.干蘑菇水发后，洗净，去蒂，切长丝。\r\n4.炒锅内倒入豆油，烧八成热，放入牛肉片、蘑菇丝、大葱白丝，爆炒熟，放入大蒜末、米醋、精盐、味精炒匀，淋芝麻油，出锅。\r\n5.食用时，同热大米饭一起同吃，即可。', '味浓肉嫩，焦香郁浓，葱味宜人。', NULL, NULL, '牛腱子肉750克，大葱白120克，芝麻50克（焙好），大蒜1瓣，生姜末3克，酱油50克，辣椒面1克，豆油40克，干蘑菇6朵，大米饭250克，黄酒25克，精盐5克，味精2克，米醋10克，芝麻油25克。');
INSERT INTO `recipe` VALUES (2164, 82, '脆皮鹌鹑', '1.鹌鹑闷死，去毛，从脊部剖开，取出内脏，洗净，用尖竹针将鹌鹑胸内扎几个小孔（但不要将鹌鹑皮扎破），入瓷盆，加酱油、精盐、胡椒粉、花椒水、葱块、姜块（拍松），腌渍半小时，入味。\r\n2.将大蒜去皮，剁成细末，装碗，加入芝麻油、姜末、酱油和发好的芥末粉、味精调成调味精汁，待用。\r\n3.再将腌渍好的鹌鹑用铁钩吊起，挂竹棍上晾干；淀粉装入瓷碗，加入温水150毫升，搅拌均匀，涂抹在晾干的鹑皮上；每隔3分钟抹一次，共抹三次，至鹌鹑体表呈现出一层微薄的粉霜。\r\n4.炒锅烧热，放入豆油，烧至五成热时，用漏勺托着鹌鹑，快速', '色泽红亮，皮脆肉嫩，咸鲜辣香。', NULL, NULL, '鹌鹑10只，香菜5克，淀粉50克，精盐1克，辣椒油15克，酱油25克，生姜50克，豆油1000克(实耗60克)，大蒜25克，味精1克，辣大酱50克。');
INSERT INTO `recipe` VALUES (2165, 82, '大白菜泡菜', '1.大葱去皮，大蒜去皮，生姜去皮，均洗净，切成碎末；红辣椒擦净，去蒂根，切成碎末。\r\n2.去掉大白菜根，对直开成两半，放在太阳下晾晒半天，然后，装入没有上釉的陶瓷坛里，撒上盐和胡椒面，照此一层一层地放，放完毕后，用木盖盖上，其大小与坛口一样大，使盖正好压在白菜上。\r\n3.然后，压上重石，放两三天后用冷水彻底冲洗白菜，并尽可能把菜汁挤出来。接下来，在白菜中间塞上葱末、大蒜末、辣椒末、生姜末，再把牛肉汤与酱油、白糖与味精和匀后，灌满坛子，用油纸封上，盖上盖，冷冻15天后，可把泡菜缸埋在地下半截，上面盖好，以防', '红白相间，酸辣可口，促进食欲。', NULL, NULL, '大白菜1棵（约200克），胡椒面50克，大葱80克，大蒜40克，红辣椒15克，生姜15克，牛肉汤500毫升，淡酱油15克，味精5克，白糖50克，盐125克。');
INSERT INTO `recipe` VALUES (2166, 82, '蛋白丸子', '1.将猪腰柳肉洗净，剔去筋，剁成八成细，打入2个鸡蛋清，加大葱末、生姜末、芝麻面，拌匀，再剁成极细的泥；罐头竹笋切成4厘米长，1厘米宽的条，用开水氽一下，捞在凉水中冲凉；把大葱去皮，洗净，切成段；生姜去皮，洗净，切成片。\r\n2.取3个鸡蛋清，加干淀粉调成稀糊，不能太稠。\r\n3.肉泥加盐、味精、料酒，搅拌均匀。大盘内放入极细的无粒干淀粉，将肉挤成40个丸子放入盘内滚满淀粉，搓圆，然后按成边薄中间厚的扁圆形，放在铺好屉布的笼屉上用大火蒸熟，取出晾凉。\r\n4.炒锅烧热，加入鸡油，油热时，将丸子裹上蛋糊下锅（油不', '色白细嫩，汤清味鲜，易于吸收。', NULL, NULL, '猪膘柳肉300克，水发木耳100克，罐头竹笋250克，鸡汤500毫升，花椒5克，胡椒面2.5克，鸡蛋清5克，味精3克，干淀粉15克，鸡汤500克（实耗50克），大葱15克，芝麻面25克（焙好），料酒25克。');
INSERT INTO `recipe` VALUES (2167, 85, '法式土司', '将大方包用刀削去两头，切成8片，修去四周的面包外皮后，用刀斜开成16块三角形备用。将鸡蛋、白糖和肉桂粉一起放入搅拌盆内，用蛋芡搅打均匀后，冲入牛奶继续慢慢地打至白糖溶化后，浸入切成三解形的面包片，至吸饱水分而不变形时捞出。将长柄平底煎锅放火上烧热，加入少许油，放入面包片，慢慢地煎至金黄色，用铲刀翻身后，继续煎至另一面也呈金黄色时，装入盆内撒上糖粉后立即上台食用。', NULL, '面包片一定要在牛奶混合物内浸透，使煎出后更加软糯适口。', NULL, '大方面包1只、鸡蛋2只、白糖50克、肉桂粉少许、牛奶450克、装饰用糖粉适量。');
INSERT INTO `recipe` VALUES (2168, 85, '法式洋葱汤', '1.把洋葱切片，并用色拉油炒熟至褐色。 \r\n2.在锅中放入洋葱、牛肉汤搅拌均匀并煮沸，加入盐、胡椒粉即可。 \r\n3.出汤时，在汤盆内加入面包片，并撒入沙司即可食用。', NULL, NULL, NULL, '牛肉汤2000克、洋葱500克、色拉油150克、盐20克、胡椒粉少许、面包片少许、沙司少许。');
INSERT INTO `recipe` VALUES (2169, 85, '翡翠肉汤', '1.葱头去皮，切成薄片，用黄油煸炒出香味，放入择洗干净的菠菜段，煽炒。猪五花肉切成长条；鸡肉、烤牛肉、烤猪肉均切成小块同菠菜一起煸炒后，放入鸡清汤，搅拌均匀，烧开。\r\n2.再取4个鸡蛋打成蛋液，放入漏勺过入滚开的鸡汤内，边放边搅，使蛋液入汤后成细长的面条状，改小火，煮5分钟。\r\n3.余下的6个鸡蛋，逐个打入加醋的开水锅里，煮成荷包蛋。\r\n4.芹菜切成碎末，撒入汤内。汤与荷包蛋同时上桌，喝汤时，配些硬面包。', '色泽喜人，肉菜软嫩，易于吸收。', NULL, NULL, '鸡肉100克，烤牛肉100克，烤猪肉100克，猪五花肉200克，菠菜300克，黄油50克，鸡清汤500毫升，鸡蛋10个，葱头1个，芹菜、精盐、胡椒粉、醋各适量。');
INSERT INTO `recipe` VALUES (2170, 85, '鲑鱼奶油冻', '1.把马铃薯放在盐水里烹调。在平底锅里油煎鲑鱼片。 \r\n2.把马铃薯和鲑鱼片放在一块捣碎，使它们成泥状。 \r\n3.在一个电搅拌器里，加入鲑鱼片、马铃薯、乳酪、柠檬汁、芥末、3茶匙鱼子酱、食盐和胡椒粉，搅拌两分钟，使它们充分混合。 \r\n4.注入水果蛋糕酶，放在电冰箱里冷冻至少3个小时。', NULL, NULL, NULL, '1磅不带骨的鲑鱼片、5匙酸奶油、1个柠檬汁、1个中等的土豆、 3匙鱼子酱、1茶匙芥末食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2171, 85, '海带芥末沙司', '1.水发海带上蒸锅干蒸半小时，取出，切片，待用。 \r\n2.芥末粉用凉开水调开，放柠檬汁、人造奶油、红辣椒面、白糖、白醋、牛奶搅拌均匀，用清水烧开片刻，成芥末沙司。 \r\n3.再将海带片放入芥末沙司中调匀即可。', NULL, NULL, NULL, '水发海带500克，人造奶油50克，红辣椒面10克，芥末面15克，柠檬汁10克，白醋25克，牛奶200毫升，白糖15克。');
INSERT INTO `recipe` VALUES (2172, 85, '海鳗汤', '1.海鳗最好是买鱼头后面的一大块、鱼尾前一块和一部分鱼头，洗干净，靠鱼头的前段鱼块留下备用。各种蔬菜去皮，洗净。 \r\n2.600毫升清水入锅，放入鱼头和靠近鱼尾的鱼块(多刺)，加入蒜瓣、葱头、1根胡萝卜及香叶，加盐和胡椒粉烧开，煮半小时，汤用漏勺过滤。\r\n3.胡萝卜、萝卜、韭葱均切成小块，取一半黄油入平底锅，倒入各种蔬菜，翻炒均匀出香味，再倒入过滤后的汤。小火煨煮烧开，放入鱼块，小火煨煮15分钟。 \r\n4.食用前，捞出色块，去皮，去鱼刺，压碎鱼肉，放入汤里。肥汤都倒入盆里，撒入切碎的香芹叶，加入余下的黄油', NULL, NULL, NULL, '海鳗150克，黄油75克，胡萝卜4根，萝卜2根，韭葱2根，葱头2个，香叶1片，芹菜1棵，香芹叶10克，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2173, 85, '韩氏鳀鱼炒香菇', '1.鳀鱼切碎，罐头中的油弃置不要；香菇去蒂，切成四块；蒜头切片。 \r\n2.锅中烧橄榄油至七分热，蒜片入锅，略煎香；但不可见焦黄。 \r\n3.鳀鱼入锅，炒至咸香味溢出，再将香菇入锅同炒；若嫌太干，可酌加油。 \r\n4.香菇炒至均沾到油与鳀鱼末之后，加白酒、盐和胡椒；此时仍须不时翻炒，至香菇出水软熟。 \r\n5.锅中菇汁与白酒炒至将干时，熄火，拌入九层塔丝，略微翻炒后，即可起锅。', NULL, NULL, NULL, '3大匙特级橄榄油（E');
INSERT INTO `recipe` VALUES (2174, 85, '核桃鸡汤', '1.鸡膀尖摘净细毛，放人煮锅内，入清水600毫升，烧开，去沫，汤去净沫后，放入去皮的胡萝卜、葱头块、丁香花蕾、芹菜、百里香粉、香叶、加盐、胡椒粉烧40分钟。\r\n2.砸开核桃壳，去掉核桃仁上的薄膜，切成小块。盛蛋黄的碗里加入鲜奶油，调匀，加入核桃块和细叶芹菜细末，搅拌均匀。\r\n3.鸡膀尖煮熟后，汤过筛再倒入锅内，保温。\r\n4.捞出鸡膀尖，剔去骨头，肉和胡萝卜绞成泥，再放入锅内煮开，倒入汤盆即可。', '味道鲜美，肉菜嫩软，口味清香。', NULL, NULL, '鸡膀尖250克，鲜核桃250克，胡萝L 2根，葱头1个，丁香花蕾1个，芹菜l棵，百里香粉(或五香粉)5克，香叶l片，鲜奶油25克，蛋黄1个，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2175, 85, '烘焙芦笋', '1.仔细清洗蘑菇。加入水、柠檬汁和食盐并用小火烹调。 \r\n2.准备好芦笋并把它们系成一束，放在盐水里煮一会使它们变成易碎的 。 \r\n3.打两个鸡蛋。把鸡蛋黄放入一个小罐里并加上少量水，搅拌之后，加入蘑菇，逐量加入黄油。 \r\n4.不断搅拌并加入8盎司新鲜乳酪。 \r\n5.混合乳酪加入到沙司，用芦笋装饰成玫瑰形。 \r\n6.在烤箱里烘烤几分钟即可。', NULL, NULL, NULL, '1束芦笋、7盎司黄油、8盎司新鲜的乳酪、8盎司栽植的蘑菇、2个鸡蛋、1个柠檬、少许食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2176, 85, '红烩肉杂拌', '1.将胡萝卜、葱头、姜去皮、芹菜去筋，洗净，均切成片。 \r\n2.往煎盘上放入200克生菜油以及香叶、胡椒粒、干辣椒，上火烧热，再把胡萝卜、葱头、芹菜和姜下入，煸炒至葱头成黄色时，下入咖喱粉，将咖喱粉炒出香味，下入番茄酱，待番茄酱中的水分炒去，倒入鸡清汤和5克精盐、味精，改小火上把汁去一半，撤火并过箩成咖喱油汁。 \r\n3.再将大虾去头、去尾、去屎线，洗净，从中间一切成两段，放上5克精盐和奶油拌匀。 \r\n4.把生菜油下锅，中火烧热，把大虾下锅炸成金黄色捞出，放入小焖锅，再把咖喱油放入装大虾的焖锅，上火焖3分钟', NULL, NULL, NULL, '大虾(约l公斤左右)16只，咖喱粉40克，葱头150克，生菜油750克（实耗200克），姜15克，胡椒粒5克，干辣椒1个，香叶2片，芹菜150克，胡萝卜150克，味精10克，番茄酱20克，鸡清汤500毫升，奶油20克，精盐10克。');
INSERT INTO `recipe` VALUES (2177, 85, '红汁黄瓜', '1.黄瓜洗净，去蒂，消毒，切丁；鲜西红柿去皮，切块；青蒜择洗干净，切末；小茴香炒熟、压碎。 \r\n2.将酸奶酪放碗内搅拌均匀，放酸奶油、小茴香末、黄瓜丁、西红柿块、青蒜末、胡椒粉、辣椒油、白糖、盐拌匀，调好口味，放入冰箱中保存。随吃随取。', NULL, NULL, NULL, '嫩黄瓜500克，鲜西红柿2个，酸奶油25克，酸奶酪25克，青蒜25克，辣酱油30克，小茴香5克，白糖、胡椒粉、盐各适量。');
INSERT INTO `recipe` VALUES (2178, 85, '黄瓜粉皮', '1.把黄瓜洗净、消毒，浸泡后，捞出，不去皮。纵向对切吴长条形、放盐，倒扣盘中控干；熟鸡蛋用细线割开；柠檬对半切开，一半切片，一半挤汁。 \r\n2.把熟鸡蛋丁、蛋黄酱及柠檬汁拌匀成馅，分别放入黄瓜瓤中，并把余下的蛋黄酱汁抹上，放盘一边。 \r\n3.盘中间放柠檬片。 \r\n4.粉皮洗净切长条；瘦猪肉切丝；葱、姜、蒜切末；水发海米切粒。待用。 \r\n5.炒勺放熟花生油少许，放葱姜蒜末，煽炒出香味后，放肉丝、精盐、味精、海米粒，当肉丝呈白色时，放入粉皮条，快速炒匀，出勺，入盘另一边，上面撒香菜末。', NULL, NULL, NULL, '黄瓜2条，熟鸡蛋2个，柠檬1个，蛋黄酱100克，粉皮l张，瘦猪100克，葱25克，姜15克，大蒜头25克，香菜末15克，精盐、味精、胡椒粉、水发海米各适量。');
INSERT INTO `recipe` VALUES (2179, 85, '鸡蛋番茄沙拉', '1.把葱头、青椒、红椒、芹菜叶择洗干净，均切成碎末，放入凉开水加精盐、砂糖、白醋、辣椒粉、生菜油、芥末酱、胡椒粉调匀制成蔬菜沙司；鸡蛋入开水锅内，煮熟，捞入凉水中。 \r\n2.再把鲜番茄洗净，消毒，切成片；熟鸡蛋去皮，切片，每两片鲜番茄间夹一片鸡蛋片，整齐地码在盘内，浇上蔬菜沙司，即可。', NULL, NULL, NULL, '鸡蛋500克，鲜番茄750克，葱头100克，芹菜叶10克，青椒25克，红椒25克，辣椒粉5克，芥末酱25克，生菜油50克，砂糖50克，白醋15克，精盐15克，凉开水200毫升，胡椒粉少许。');
INSERT INTO `recipe` VALUES (2180, 85, '鸡皇饭', '1.烧油2汤匙，炒香青、红椒、磨菇及鸡粒、薯仔等备用。\r\n2.热镬融牛油，加入面粉炒至微黄，逐步加入上汤、淡奶、蛋黄和调味等。\r\n3.将已炒好之材料加入奶油拌匀，与白饭同上。', NULL, NULL, NULL, '熟鸡肉6两(240克)切粒、熟薯仔1个(切粒)、青椒1两(40克)切粒、红椒1两(40克)切粒、磨菇6粒(切片)、牛油2两(80克)、白饭2碗、盐1/2茶匙、糖1/2茶匙、鸡粉1/2茶匙、奶油料、蛋黄2只(打散)、淡奶90克、面粉6汤匙、上汤2杯。');
INSERT INTO `recipe` VALUES (2181, 85, '鸡肉沙拉', '1.先制出素沙拉：将1.5公斤土豆、100克胡萝卜，去皮，切成小片；熟鸡蛋切小片；鲜黄瓜250克去皮，先一切两开，再切成小片。然后，将土豆、胡萝卜、鲜黄瓜、鸡蛋片、青豆、奶油、精盐、味精、白砂椒粉、辣酱油、马乃司沙司混合，用木铲轻轻地搅匀，装入瓦罐，入冰箱保存。 \r\n2.再将熟鸡脯肉片成小片，取一半拌入素沙拉内，把沙拉摆成高桩圆馒首形，然后，把余下一半鸡脯肉拼摆覆盖在沙拉上面。 \r\n3.把熟鸡蛋去皮与黄瓜均切成斜角块。再把红白菜、生菜叶一起围在沙拉周围，在鸡脯肉的上面用锥形纸卷挤上马乃司沙司即成。', NULL, NULL, NULL, '熟鸡脯肉200克，素沙拉750克，红白菜100克，鲜黄瓜100克，马乃司沙司50克，熟鸡蛋100克，生菜叶少许。');
INSERT INTO `recipe` VALUES (2182, 85, '煎龙虾肉', '1.切除长触须（在往后的操作中用于除去虾肠），切开虾头和虾身之间的薄膜，把虾头和虾身分成两部分，之后将虾头切成两半，摘除沙袋，再将虾头纵向切成大块，以便能把虾味充分煮出来。 \r\n2.用热沙津油把蒜炒香后，倒入龙虾头，用强火把龙虾头炒成红色。 \r\n3.把锅移离火炉，倒入苹果白兰地酒，然后把锅放回火炉，用强火加热并点燃酒，倒入调味蔬菜并添加牛油，用中火把蔬菜炒黄。 \r\n4.把苹果切成八等分，再将各块削成橄榄球形，用牛油把苹果煎黄然后连锅一起端进180度的烤炉中加热，待装盘。 \r\n5.把苹果碎略炒片刻，适时倒入', NULL, NULL, NULL, '龙虾400-500公克（2只）、蒜1瓣、红萝卜40克、洋葱40克、西芹20克、苹果2个、苹果白兰地酒50毫升、苹果酒（西打）300毫升、鱼汤200毫升、鲜忌廉200毫升、红胡椒适量、香叶芹适量、红胡椒适量、牛油面糊适量、盐、胡椒、牛油、橄榄油。');
INSERT INTO `recipe` VALUES (2183, 85, '茭白虾冻', '1.茭白去壳、皮，洗净后拍松，切成5厘米的筷条形，用七成热油炸缩，呈淡黄色时捞出，沥油；两个鸡蛋打入碗里，分别取出蛋白、蛋黄。 \r\n2.把猪肉皮洗净后放入水中，加胡椒粉、盐、香叶、葱头后将其煮，再打入两个蛋白、盐，搅拌均匀后，上火熬开，滤去杂质，冷却后，成青冻待用。 \r\n3.将葱头片、芹菜段、盐与大虾一起用水煮熟，去皮壳及头尾，切成大块，两个蛋黄用油煎成块；熟胡萝卜切花刀，待用。 \r\n4.再将青冻化开、倒入冰箱模子少许，把大虾块、胡萝卜块、熟豌豆、鸡蛋块倒入模子里，再把余下青冻倒上，放入冰箱冷冻后，切块放', NULL, NULL, NULL, '茭白300克，猪肉皮250克，大虾200克，熟豌豆25克，鸡蛋2个，熟胡萝卜25克，葱头25克，芹菜25克，白糖25克，香叶1片，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2184, 85, '芥末拌黄瓜', '1.将黄瓜洗净、消毒，切成上厚下薄滚刀小块，加精盐入味3分钟，再用凉开水漂洗一遍，挤干水分，盛盘内。\r\n2.将香油、白醋、芥末酱、胡椒粉同放碗内调匀，浇在黄瓜块上拌匀即成。', NULL, NULL, NULL, '嫩鲜黄瓜500克，香油25克，白醋5克，芥末酱10克，胡椒粉、精盐各适量。');
INSERT INTO `recipe` VALUES (2185, 85, '焗猪扒饭', '1.猪扒拍松，加调味拌匀，拍上少许面粉，以中火油炸至金黄备用。\r\n2.烧热适量油，将蛋及饭炒匀，用玻璃盘或深窝盛起，猪扒放在饭面。\r\n3.牛油起镬，炒香洋葱加入面粉、茄膏及上汤和汁调味拌匀。\r\n4.把所有材料倾下煮片刻，淋在猪扒上，转放炉，以高火250度，8分钟见表面金黄即成。', NULL, NULL, NULL, '猪扒1/2磅（切块）、蛋液2只、白饭3碗、番茄2个（去核切粒）、磨菇6粒（切片）、什菜粒1/2杯、洋葱1/2个（切粒）、茄膏1汤匙、上汤1/2杯、牛油2安士、面粉2汤匙、番茄2个(去核切粒)、味椒盐1/2茶匙、外国酱油1茶匙、急汁1/2茶匙、白兰地酒1茶匙、油1茶匙、盐1茶匙、糖1汤匙、外国酱油1/2茶匙、急汁2汤匙、胡椒粉少许、白兰地酒1茶匙、茄汁3汤匙。');
INSERT INTO `recipe` VALUES (2186, 85, '菊莴苣色拉', '1.把菊莴苣外面的老叶子去掉。同时分离别的叶子并把它们切成3-4厘米的小片。把切好的叶子连同菊莴苣装在色拉碗里。 \r\n2.把罗克福奶酪粉碎成半个胡桃大，每个菊莴苣里放两个块。 \r\n3.在一个碗里混合食盐、胡椒粉和芥末，慢慢地加入醋。随后加入胡桃油和向日葵油，并不断搅拌。 \r\n4.装盘时，在色拉里注入混合物，并均匀搅拌它们。', NULL, NULL, NULL, '6个中等的菊莴苣、1/2盎司胡桃、对半分、3匙向日葵油、1匙醋、7盎司罗克福奶酪、3匙胡桃油、1茶匙芥末、食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2187, 85, '卷心菜包肉', '1.卷心菜叶子用沸水氽一下，捞起后，冷透，去掉梗。 \r\n2.烧热平底锅，化开猪油后将葱头末放入锅，炒至呈金黄色时，加碎大蒜头略炒，再加面粉、番茄酱炒至香透并呈枣红色时，再加牛肉末，炒至牛肉末有九成熟时，加黄酒、辣酱油、盐、胡椒粉和150毫升牛肉清汤，使其略滚片刻，起锅倒入盘内，冷却后即成司刀粉肉，待用。\r\n3.卷心菜叶逐片摊平在桌上，分别将司刀粉肉放在卷心菜叶子上，并卷起来，包成长6厘米、直径3厘米的卷子(形似春卷)，共20只，放在厚底钢精锅里，浇上余下的牛肉清汤，用小火焖煮(煮时须把锅摇动，以防炯焦)约', NULL, NULL, NULL, '卷心菜大叶20片，牛肉末500克，面粉50克，葱头末100克，大蒜末25克，番茄酱50克，辣酱油25克，猪油100克，黄酒50克，牛肉清汤250毫升，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2188, 85, '咖喱油烟虾段', '1.将胡萝卜、葱头、姜去皮、芹菜去筋，洗净，均切成片。 \r\n2.往煎盘上放入200克生菜油以及香叶、胡椒粒、干辣椒，上火烧热。再把胡萝卜、葱头、芹菜和姜下入，煸炒至葱头成黄色时，下入咖喱粉，将咖喱粉炒出香味，下入番茄酱，待番茄酱中的水分炒去，倒入鸡清汤和5克精盐、味精，改小火上把汁去一半撤火，过箩成咖喱油汁。 \r\n3.再将大虾去头、去尾、去屎线，洗净，从中间一切成两段，放上5克精盐和奶油拌匀。 \r\n4.把生菜油下锅，中火烧热，把大虾下锅炸成金黄色捞出，放入小焖锅。再把咖喱油放入装大虾的焖锅，上火焖3分钟，', NULL, NULL, NULL, '大虾(约l公斤左右)16只，咖喱粉40克，葱头150克，生菜油750克（实耗200克），姜15克，胡椒粒5克，干辣椒1个，香叶2片，芹菜150克，胡萝卜150克，味精10克，番茄酱20克，鸡清汤500毫升，奶油20克，精盐10克。');
INSERT INTO `recipe` VALUES (2189, 85, '烤羊肉', '1.将羊肉上的薄膜白皮去掉，用水洗净，放上精盐用手搓匀，淋上少许白醋。 \r\n2.将胡萝卜、葱头去皮，芹菜去筋，洗净，切成片，与香叶、胡椒粒一起放在羊肉上。把蒜片嵌入羊肉内，腌2小时，然后将羊肉放入烤盘，浇生菜油，入烤箱。\r\n3.要随烤随翻过。烤半小时之后，往烤盘上加400毫米清水。烤1个半小时左右，待羊肉四面都是金黄色时，可用叉子叉一下，如羊汁是白色，证明肉已熟，即可取出，晾凉。 \r\n4.把烤熟的羊肉切成70片，按10客份计算，每客7片，约125克左右。食用时，羊肉片周围配酸白菜、红菜头丁和西红柿片，用生', NULL, NULL, NULL, '羊后腿净肉（当年生大羊羔）2公斤，葱头200克，芹菜150克，胡萝卜200克，生菜油50克，大蒜片50克，酸白菜250克，红菜头丁250克，西红柿250克，香叶3片，生菜叶150克，胡椒粒5克，精盐、白醋各适量。');
INSERT INTO `recipe` VALUES (2190, 85, '冷烤鸡', '1.把鸡去头、爪、内腔，洗净，浸泡，控干，放上精盐用手搓匀，将鸡腿别在尾部。 \r\n2.将炸油入锅烧热，把奶油抹在鸡上，然后将鸡下锅，炸成黄色捞出，控油，放入烤盘。 \r\n3.把胡萝卜、葱头去皮，芹菜去筋，洗净切片，与香叶、胡椒粒一同放在鸡上，浇上鸡油，放入烤箱，要随烤随翻过。烤15分钟后，将鸡清汤倒入烤盘，再烤40分钟左右取出。连鸡带汤放入焖锅，大火烧开，小火煨焖，使之入味，鸡熟透取出，晾凉。 \r\n4.装盘时，应将鸡脯和鸡腿一切两开，再将鸡腿切成三段，大腿根部那段竖切两刀，在第二段下关节处剁断，把最后一节的', NULL, NULL, NULL, '嫩母鸡(约1.25公斤左右)1只，胡萝卜100克，芹菜50克，葱头10O克，精盐10克，香叶半片，胡椒6粒，鸡清汤250毫升，生菜油500克，奶油15克，鸡油15克，红白菜200克，酸黄瓜100克，鲜西红柿100克，纸花2个，萝卜花2个，生菜叶10克。');
INSERT INTO `recipe` VALUES (2191, 85, '龙虾占米多', '1.龙虾放滚水中，煮15分钟取出。\r\n2.切开两边，将虾肉取出，切粒备用。\r\n3.用油炒香干葱、蒜茸、洋葱和烟肉，加入白菌、白兰地酒，倒下龙虾肉及调味兜匀盛起。\r\n4.牛油放镬中，加入面粉以慢火炒匀，边炒边加上汤及淡奶，至加完为止，最后加入芝士粉和龙虾料拌匀。\r\n5.将龙虾馅料置虾壳内。\r\n6.撒上少许芝士粉和面包糠，放炉中，以高火成金黄色即成。', NULL, NULL, NULL, '龙虾1只（约1磅）、干葱茸1茶匙、蒜茸1茶匙、洋葱粒1/4 杯、白菌1/4杯（切片）、烟肉1片（切碎）、芝士粉1汤匙、面包糠少许、牛油1安士、面粉1/4杯、淡奶1汤匙、上汤3/4杯、盐1/4茶匙、糖1/2茶匙、芥末粉1/2茶匙。');
INSERT INTO `recipe` VALUES (2192, 85, '芦笋浓汤', '1.芦笋去硬皮，洗净，煮锅内放清水500毫升，放芦笋煮15分钟，捞出。 \r\n2.芦笋煮软的上部嫩尖切下，剩下的部分切成段，再放入锅内；土豆去皮，洗净，入锅。再加鸡汤和煮芦笋的水，用温火煮25分钟。 \r\n3.捞出汤里的菜，经绞菜机绞成菜泥；蛋黄加鲜奶油，打成蛋液后，菜泥混合、搅拌，倒入汤里，搅打，放盐和胡椒粉，调好口味，再烧开，加入备用的嫩芦笋，入汤碗，上桌即可。', NULL, NULL, NULL, '芦笋1000克，鸡清汤400毫升，鲜奶油100克，蛋黄2个，土豆2个，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2193, 85, '马背上的魔鬼', '1.以灼熟的煙肉把生蠔捲起来，用竹签或烤叉串着烤熟。\r\n2.用辣粉作调味。\r\n3.烤熟放在搽了芥酱的多士上即可，热食。餐酒可配有气的干白酒。', NULL, NULL, NULL, '生蠔24双、煙肉(于中间切成半12条)、辣粉1/2茶匙、多士、1-1/2寸四方形或圆形24块、法国芥酱6茶匙。');
INSERT INTO `recipe` VALUES (2194, 85, '马乃司拌莴笋', '1.将莴笋去皮、根、留嫩叶，洗净后将莴笋切滚刀块，嫩叶切碎，分别用盐腌渍3分钟，再放凉开水中漂洗干净，放入盘内。\r\n2.白醋放碗内，用3倍的凉开水稀释调好，再放马乃司、胡椒粉拌匀，浇在笋块、笋叶上，再淋上麻油即成。', NULL, NULL, NULL, '嫩莴笋2500克，马乃司15克，麻油25克，白醋1克，胡椒粉、精盐各少许。');
INSERT INTO `recipe` VALUES (2195, 85, '奶油牛肉丁番茄汤', '1.先制出奶油番茄汤：烧热钢精锅，放150克白塔油，将500克蔬菜香料(胡萝卜、葱头、药芹菜)放入炒至呈牙黄色，加100克番茄酱炒透呈枣红色，加500克番茄酱、2公斤牛肉清汤，用大火烧开，再加10克盐、胡椒粉、香叶、油面酱搅匀，转小火熬两个小时。然后捞出蔬菜香料等杂物，或用洁净布滤清，配鲜奶油即成。 \r\n2.熟牛肉、土豆和葱头、胡萝卜都切成5毫米见方的块。 \r\n3.烧热钢精锅，加白塔油后，烧热，将葱头、胡萝卜下锅炒到八成熟时，将预先制好的2公斤番茄汤倒入，加牛肉丁、土豆丁、酒、香叶，改小火煮约30分钟，上', NULL, NULL, NULL, '奶油番茄汤2公斤，熟牛150克，胡萝卜IOO克，土豆150克，番茄酱50克，香叶1片，黄酒50克，白塔油50克。');
INSERT INTO `recipe` VALUES (2196, 85, '奶汁海带', '1.水发海带洗净、控干，切成长方形片，入锅煮软，捞出，控干。 \r\n2.将白糖、蜂蜜放进锅内，加牛奶、鲜奶油、白葡萄酒，烧开后放海带块，温火煨熬，待海带片附上奶浆，离火，晾凉，切成菱角形块，入盘，上放柠檬片即可。', NULL, NULL, NULL, '水发海带250克，蜂蜜100克，白糖20克，牛奶250毫升，鲜奶油10克，白葡萄酒25克，柠檬2片。');
INSERT INTO `recipe` VALUES (2197, 85, '柠檬牛油煎西冷', '1.牛油搅软，混和其余材料，以保鲜纸卷成一元宽之长条型，两端头尾拧紧，置雪柜雪好备用。\r\n2.西冷断筋(以防煎弯)，平底煎锅烧热，注入适量橄油，放入西冷扒，用猛火煎至两面金黄，下白兰地，（约2分钟）生熟随意。\r\n3.将煎好之西冷排放碟中，时蔬及薯条拌碟。\r\n4.将雪好之柠檬牛油切成约一厘米厚块，放在西冷上即成。', NULL, NULL, NULL, '西冷扒2件、时蔬适量、橄榄油适量、炸薯条适量、白兰地酒1汤匙、洋葱盐适量、蒜盐适量、急汁适量、牛油2安士、柠檬汁1/2茶匙、番荽碎1茶匙、黑椒碎少许。');
INSERT INTO `recipe` VALUES (2198, 85, '牛肉丸子汤', '1.将牛肉洗净，切成块，100克葱头去皮，和牛肉放在一起用绞刀绞两遍，再用刀剁一遍，放入盆内，加入5克精盐，50克鸡油、胡椒粉、味精、鸡蛋搅拌均匀，加水150毫升，随倒随搅，和成肉泥。 \r\n2.向汤锅内放水烧开，用手把肉馅挤成120个丸子，放入开水锅内，烧一个开，丸子漂起，把原汤滗出，将丸子放入冷水里洗去浮沫，调入漏勺内，控净水。 \r\n3.把胡萝卜、葱头去皮，洗净，切成小方丁；将土豆去皮，洗净，切成40块。 \r\n4.往锅内放入200克鸡油，烧至五成热。先把胡萝卜丁、葱头丁放入，随之放入香叶、胡椒粉、干辣椒', NULL, NULL, NULL, '牛腿肉750克，鸡蛋150克，葱头400克，胡萝卜300克，土豆500克，豌豆50克，鸡油400克，精盐15克，牛肉清汤2.5公斤，香叶半片，干辣椒1个，胡椒粉、味精各适量。');
INSERT INTO `recipe` VALUES (2199, 85, '茄子沙拉', '1.将茄子去皮，洗净，切丁，入清水中浸泡出黑液；熟鸭蛋剥壳，切丁，待用。 \r\n2.取不锈钢锅或搪瓷锅，加水煮开，倒入茄丁氽至熟透，捞出、沥干。 \r\n3.将茄丁、蒜苗段、芹菜段、柠檬丁、鸭蛋丁入盘，放盐、胡椒粉、白醋、色拉油拌匀，送入冰箱镇凉。食用时，取出即可。', NULL, NULL, NULL, '茄子1个，蒜苗段50克，芹菜25克，去皮柠檬25克，熟鸭蛋1个，清水300毫升，盐、胡椒粉、白醋、色拉油各适量。');
INSERT INTO `recipe` VALUES (2200, 85, '瓤馅鸡肉饼', '将鸡脯肉搅成馅，再放入牛奶、泡面包、蛋、盐、胡椒粉和成馅。将和好的馅做成小饼状，再将炒好的素菜丁馅包起来，蘸上面包粉入油炸熟。', '色金黄，口味鲜，外焦里嫩。', '在摆盘上有所讲究，用面条筐盛入。生加工制作完毕后，要立即进行热处理，不可放置时间过长，否则馅会塌下。', NULL, '鸡脯肉，牛奶，面包，鸡蛋，素菜丁。');
INSERT INTO `recipe` VALUES (2201, 85, '生耗鱼汤', '1.先以鱼骨及生蚝熬汤，过滤保存。 　　\r\n2.将梭子鱼做成的鱼丸子慕斯切片。 　　\r\n3.舌鲽鱼切成细条同样置于热汤内。 　　\r\n4.加入鱼板片，并趁热加入生蚝，烫过20秒即熄火。 　　\r\n5.洒上少许肉荳蔻香料及盐。', '这一道汤乃以鲜美的生蚝与舌鲽鱼肉清炖而成，点缀梭子鱼酱打成的鱼慕斯，非常爽口雅致。在奶油焗生蚝之后安排，非常恰当。', NULL, NULL, '三片新鲜生蚝、50g的梭子鱼慕斯、一个蛋、少许盐、1g的肉G蔻、200g的鲜奶油、20g的舌鲽鱼。');
INSERT INTO `recipe` VALUES (2202, 85, '束法鸡', '1.先制出奶油沙司：钢精锅中放入200克黄油，上火烧热，下入200克面粉在小火上炒，搅拌均匀，出香味时，下入牛奶1.25公斤、100克酸奶油，随下随搅拌均匀，然后将1.25公斤鸡清汤也陆续倒入，边倒边搅，随之下入味精10克、精盐10克、辣酱油25克，调好口味，过箩，过入瓦罐，上浇黄油，避免干皮，即成奶油沙司，可出成品2.5公斤。\r\n2.再将洗净的净膛光鸡，放入水锅中，加入盐、胡萝卜、葱头、芹菜煮熟，放凉，然后把鸡脯剔下来，切成斜薄片，去掉胸骨，把鸡仰放在盘中，在鸡膛里加入肝泥子，再把切好的鸡脯肉贴在肝泥子', '色泽乳白，口味鲜香，清凉适口。', NULL, NULL, '净膛光鸡1只（约1.5公斤左右），肝泥子400克，奶油沙司500克，香叶2片，胡萝卜25克，葱头25克，芹菜25克，生菜叶150克，西红柿150克，鲜黄瓜150克，精盐适量。');
INSERT INTO `recipe` VALUES (2203, 85, '酸辣肚丝', '1.肚皮煮熟，切粗丝，撒盐、胡椒粉拌匀；葱头切丝与肚皮丝一起拌匀后入盘。 \r\n2.炒锅烧热，化白塔油，放草莓酱、酸奶油、辣酱油、蘑菇片，煸炒均匀，晾凉后浇在猪肚丝上即可。', NULL, NULL, NULL, '猪肚皮（去掉肚头部分）500克，葱头50克，蘑菇片50克，白塔油100克，酸奶油50克，辣酱油25克，草莓酱50克，盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2204, 85, '酸甜莴笋', '1.莴笋去叶、削皮、去根，切丁后用开水氽一下；鲜西红柿去皮，切块；青蒜切末。\r\n2.将柠檬汁、砂糖、凉开水、精盐放入大瓷碗内搅匀，调好口味，再放莴笋丁、西红柿块、青蒜末拌匀，入冰箱贮存，随吃随取。', '颜色美观、甜酸脆嫩、清香爽口。', NULL, NULL, '嫩莴笋500克，鲜西红柿2个，青蒜25克，柠檬汁（或鲜橙汁）75克，砂糖30克，凉开水50毫升，精盐少许。');
INSERT INTO `recipe` VALUES (2205, 85, '西红柿百里香鳕鱼', '1.把鱼切成小片。 \r\n2.剥好白洋葱。把两杯水和三杯白葡萄酒倒入一个焙盘。加入百里香和橄榄油并在小火下烹调。 \r\n3.加入食盐、胡椒粉和洋葱继续烹调。 \r\n4.在长柄浅锅里加入黑鳕，并注入葡萄酒。 \r\n5.把西红柿切成小块并加到锅中。 \r\n6.用盐和胡椒粉调味并加入柠檬汁，继续用小火烹调5分钟即可。', NULL, NULL, NULL, '2磅黑鳕 、75毫升白葡萄酒、1束百里香、6个西红柿、1个柠檬、3盎司橄榄油、食盐、胡椒粉、洋葱。');
INSERT INTO `recipe` VALUES (2206, 85, '西红柿花双拼', '1.大小西红柿洗净、消毒，用净水果刀切成均匀西瓜瓣形。取大圆浅盘洗净，擦干，把“西红柿花”放于盘中心，周围交替码一圈松花蛋瓣和鸭蛋瓣、使其清白相间。 \r\n2.再将凉水泡挺的芹菜叶放在“西红柿花”旁，也可放在盘适当处点缀陪衬。 \r\n3.大蒜、葱头切剁成细茸，盛入瓷碗里，加芥末粉、精盐、胡椒粉、白醋各适量及色拉油200克，搅拌均匀成法国沙司，浇在松花瓣、鸭蛋瓣上即成。', NULL, NULL, NULL, '鲜西红柿2个(大小各1个)，松花蛋2个，熟鸭蛋2个，法国沙司250克，鲜芹菜叶15克，姜末15克，大蒜末25克，葱头15克，芥末粉5克，色拉油200克，精盐、胡椒粉、白醋各适量。');
INSERT INTO `recipe` VALUES (2207, 85, '咸蛋挞', '1.将烤箱调至375度。\r\n2.把鸡蛋及忌廉搅匀，加盐及白胡椒粉调味。\r\n3.餐酒可配BEAUJOLAIS。', NULL, NULL, NULL, '迷你挞皮24件、黑森林火腿，切粒4片、日式芝士碎或其他清淡芝士六安士、盐及白胡椒粉少许、忌廉1-1/2杯、鸡蛋3只。');
INSERT INTO `recipe` VALUES (2208, 85, '香脂醋风味烤鸡', '1.切除鸡翼、鸡脚和鸡颈，摘除鸡锁骨，把光鸡分割成4大块。 \r\n2.沿着鸡髀骨的走向，用刀把髀肉左右切开，摘除髀骨，切断关节，把鸡髀肉翻过来，摘除断骨。用刀背敲断鸡小腿腿骨，摘除肉中的小腿骨和软骨，成为备用的鸡髀肉。\r\n3.在鸡肉表面涂抹盐和胡椒。用炒锅、沙律油、牛油先煎好带皮的一面，翻过来再煎另一面，把鸡肉两面都煎出颜色，并使其表面形成一层硬膜。鸡皮面朝下，送入烤炉中烤制。 \r\n4.将炒锅油倒出，使用煎鸡肉的炒锅制作调味料。用香脂醋浇锅底，倒入香脂醋后把煮汁略微煮干。 \r\n5.倒入清炖牛仔肉汤并略微煮干', NULL, NULL, NULL, '（4人份量）鸡1只、盐、胡椒、牛油、沙律油调味汁、清炖牛仔肉汤200毫升、香脂醋200毫升、芜菁2个、小红萝卜4根、节瓜2个、绿花椰菜1个、苹果1个、柠檬汁适量、鸡汤少量');
INSERT INTO `recipe` VALUES (2209, 85, '蟹黄活虾', '1.把蟹肉去软骨，撕成碎片，莴笋冲洗净切成小段，熟鸡蛋去皮后，用细绳割切成薄圆片。 \r\n2.把莴笋叶和2/3的蟹肉用蛋黄沙司加精盐轻轻拌匀，入盘一边，再把余下的蟹肉和鸡蛋片放在表面，周围用芹菜叶点缀。 \r\n3.把小河虾剪去头部的刺、须、脯，洗净，沥干水，装盘另一边，用碗扣上，然后将花椒油、白醋、料酒、葱末、姜末、白糖等调味料勾兑成汁，打开碗后，浇上即成。', NULL, NULL, NULL, '罐头蟹肉100克，蛋黄沙司l00克，熟鸡蛋2个，小河虾250克，葱头末15克，姜末15克，精盐、味精、花椒油、白醋、料酒、芹菜叶、辣酱油、青蒜末、柠檬汁、胡椒粉、莴笋叶、奶油、白糖各适量。');
INSERT INTO `recipe` VALUES (2210, 85, '洋葱汤', '1.把烘箱预热，磨碎干酪。 \r\n2.在一个大的炖锅里融化黄油，并放入洋葱，用中火加热五分钟。 \r\n3.当它们变得柔软时，在上面撒上面粉并搅拌直到充分混合。接着加热五分钟，倒入葡萄酒和温水。最后再加入盐和胡椒粉并用小火加热15-20分钟。 \r\n4.把汤盛在有盖的汤盘里，在上面加上薄面包片，再在薄面包片上撒上磨好的干酪。 \r\n5.最后在烤箱里烘焙几分钟即可。', NULL, NULL, NULL, '1/2磅褐色的洋葱、1盎司黄油、1杯白葡萄酒、4薄片面包片、 3盎司瑞士干酪、3匙橄榄油、1匙面粉、食盐、胡椒粉。');
INSERT INTO `recipe` VALUES (2211, 85, '蒸鸡蛋肉卷', '1.将猪肉去筋膜，切小块，洗净，上绞肉器绞三遍，放入瓷盆内。加鸡蛋200克和精盐25克、胡椒粉、肉蔻末、味精，用力搅匀，再下入玉米粉继续搅打，然后陆续下入牛奶搅匀，使其成肉泥馅。\r\n2.用小煎盘放在火上，放入50克生菜油，烧热后再将油倒回。用叉子叉住猪肥膘肉在小煎盘上稍擦，把泻开的鸡蛋摊成4个薄蛋饼，摊鸡蛋饼动作要快、火不要大。\r\n3.再把4张鸡蛋饼放在案上，用刷子蘸余下的鸡蛋在饼上抹一层。再把肉馅分成4份，分摊在4张鸡蛋饼上，卷成卷，两头用手捏住。 \r\n4.将搪瓷盘抹上生菜油，上放鸡蛋卷，用油纸盖严，入', NULL, NULL, NULL, '瘦猪肉1公斤，鸡蛋600克，生菜油100克，肉蔻末10克，牛奶250毫升，精盐30克，胡椒粉5克，玉米粉100克，味精20克，油纸l/4张，猪肥膘肉50克，红白菜150克，生菜叶150克。');
INSERT INTO `recipe` VALUES (2212, 85, '中法双松', '1.虾头洗净，剁烂泥状，再用黄油炒黄，放葱头末、大葱末煸炒后，放牛肉清汤及红葡萄酒、西红柿粒、西红柿酱，煮开，用漏勺或小筛子滤去渣，再放入白兰地酒，拌匀后成法国汁。 \r\n2.鸡肝去筋络，洗净，用清水煮熟，切成细米粒状，入盘一边，浇上法国汁。 \r\n3.再把黄豆煎饼(豆制饭粢，市场有售)叠在一起，卷面扁圆形长条，切成细丝，抖松后放在大盘中。 \r\n4.炒锅放生菜油，滑锅均匀后，油烧四成热时，将勺离火，把黄豆煎饼丝撒入勺中，不停地翻动，待炸成金黄色时，捞出、控油。然后，放入盘另一边，迅速用筷子抖松，再撒入精盐、白', NULL, NULL, NULL, '鸡肝250克，黄豆煎饼4张(约75克左右)，大虾头2个，鲜西红柿25克，西红柿酱5克，葱头25克，白兰地酒25克，白糖40克，大葱25克，生菜油500克(实耗150克)，黄油50克，姜末15克，红葡萄酒15克，精盐、味精各适量');
INSERT INTO `recipe` VALUES (2213, 85, '猪肉冻子', '1.瘦猪肉洗净，去筋膜；葱头去皮，芹菜摘筋，洗净；把胡萝卜去皮，洗净。 \r\n2.鸡蛋煮熟去壳，切成圆形整片；把结力片用冷水浸泡40分钟，泡软，滗去水。 \r\n3.煮锅放清水，加入葱头、芹菜及整根的胡萝卜，煮开，放入瘦猪肉，待水再开时撇净浮沫，移至温火煨煮；胡萝卜煮熟后，立即捞出，晾后，切成花形片；瘦猪肉煮熟，切成片，待用。 \r\n4.再将肉汤过箩后入另一个锅内，放入泡软的结力片，加入鸡蛋清，用抽条稍搅一下，上微火煮约1小时（火候以保持似开不开的状态为好）待汤清澈时下入味精、精盐，用净纱布过滤一次，盛入洁净的容', NULL, NULL, NULL, '瘦猪肉1公斤，鸡蛋2个，结力片50克，鸡蛋清1个，西红柿250克，罐头豌豆15克，生菜叶100克，胡萝卜50克，辣根沙司125克，葱头50克，芹菜25克，嫩芹菜叶10克，精盐、味精、鸡清汤各适量。');
INSERT INTO `recipe` VALUES (2214, 79, '白斩文昌鸡', '1.将活鸡割颈放血，去毛除脏（不损坏整鸡原状），洗净。在鸡素囊口插进一瓷汤勺（以利汤水从鸡腔中对流），把鸡脚扭反插进鸡下腹洞内固定，将鸡头仰屈，用翅膀窝夹住（目的是使鸡头向后仰起、不用翅膀夹住亦可，但需在煮鸡时掌握好造型），放进烧滚的清汤中翻转烫氽，使鸡身四周受热膨胀定型。 \r\n2.改用慢火浸煮（以汤中冒气泡而不滚开为度）约5分钟，用铁勾将鸡提起，倒出腹腔汤水，再放入汤中浸煮，反复三四次至仅熟（不可大生也不可过熟），离火后另放凉汤中浸泡稍冷后取出，抹上一层香麻油，使皮色淡黄光亮。食时斩件装盘，砌成原鸡形上', '摆盘美观，色泽淡黄光亮，皮脆肉嫩味鲜，蘸佐料而吃，入口喷香，爽滑异常。', NULL, NULL, '文昌鸡（以将要下蛋的小母鸡最佳）1只（重约1000克）、姜丝、蒜泥、味精、白醋、白糖、精盐、老抽各适量。');
INSERT INTO `recipe` VALUES (2215, 79, '海南煎饼', '1.精面粉和发酵粉拌匀，开窝，加入鸡蛋液，用30度温水调开，反复揉搓成面团，用干净湿布盖住，静置15分钟，再搓一次，然后压簿，用长擀面杖擀成簿片（越簿越好，要均匀），然后用猪油涂面。\r\n2.把精盐、味精、香料、胡椒粉一起拌匀，均匀地撒在簿面片上，再把蒜茸、葱茸掺和一起，均匀地撒上一层，接着，将面片由外往里卷成圆长条，按25厘米长度切段，每段揉成圆团压簿，擀成1厘米厚度的圆饼。\r\n3.旺火烧锅，落油，至四成热度，放入生面饼，慢火煎炸，不断翻转，使两面均匀受热，起金黄色时捞起，挤出油分，按辐射状均等切块，装盘', '皮薄层多，外酥内软，咸淡适口，香味奇特，诱人食欲。', NULL, NULL, '精面粉500克、发酵粉10克、鸡蛋4个、精盐10克、味精5克、胡椒粉7克、蒜茸75克、葱茸50克、香料少许、精猪油适量。');
INSERT INTO `recipe` VALUES (2216, 79, '海南萝卜糕', '1.叉烧、虾米切成中粒，热油炒香待用，粘米淘净，用清水浸约30分钟后磨成米浆。 \r\n2.五花腩肉切粒，用微火煎出少量油后投入蒜茸爆香，再投入萝卜丝炒匀，加盖焖熟，然后徐徐倒入粘米浆，边倒边搅拌至六、七成熟，加入其余调料铲匀，成为萝卜糕浆。 \r\n3.将萝卜糕浆倒入已刷油的9寸钢方盘里，抹平，再均匀地洒上虾米粒和叉烧粒，上笼旺火蒸熟，取出待凉后切成30块方形块，滴上香油、蒜头油、熟芝麻屑及葱粒，重新加温即可。', '糕面红绿点缀，色泽鲜艳，萝卜味香浓，各味相济，入口鲜嫩。', NULL, NULL, '糯米600克、清水25千克、白萝卜丝1.75千克、新鲜虾米50克、叉烧100克、五花腩肉150克、味精30克、食盐25克、白糖75克，胡椒粉、五香粉、香油、蒜头油、炒熟芝麻碎、干净葱粒等适量。');
INSERT INTO `recipe` VALUES (2217, 79, '金华海鲜卷', '1.鲜虾解球，鲜带子解成条状，冬瓜（或白萝卜）切片，火腿切条，北菇、西芹、红萝卜切条待用。\r\n2.将冬瓜片用滚水烫软，将鲜虾球、带子、红萝卜、西芹、北菇等分别飞水后，落油及味料调味。\r\n3.用冬瓜片将虾球及多种条状物料按等量包卷成卷状，上笼蒸约3分钟至熟取出。\r\n4.将蒸好的海鲜卷摆入盘，调薄玻璃芡淋在面上，摆装饰物伴边即成。', '形状美观别致，色泽清新淡雅，质爽味鲜可口。', NULL, NULL, '鲜虾180克、鲜带子80克、火腿15克、北菇20克、西芹20克、红萝卜25克、白萝卜（或冬瓜）500克、香菜50克、棕油、精盐、味精、白糖、麻油、胡椒粉、生粉适量。');
INSERT INTO `recipe` VALUES (2218, 79, '九层油糕', '1.大米淘净，清水浸泡3小时后，细磨成含水量100％的米浆，加1/5生粉（木薯粉碎后除去渣滓沉淀出来的粉），分成两等份。\r\n2.白糖、红糖各加清水500克分别煮沸，滤去杂质，趁滚烫时分别冲入两份米浆里，搅拌均匀使之成为半熟糊浆，每份糊浆中加葱油200克调匀。\r\n3.薄铁皮平底圆盘抹油，倒进一层3毫米厚的白糖糊浆，上笼蒸三分钟取出，再倒进一层红糖糊浆，蒸3分钟取出，再倒进白糖糊浆，再蒸，如此反复蒸至9层，最后一层蒸的时间长些，使之熟透。\r\n4.将蒸熟的油糕切块上碟，抹油并撒上一薄层炒芝麻即成。', '用大米磨浆，配糖、油，蒸制而成。其黄白相间，层次分明，糕身光滑柔软，油香味浓，甜润爽口。', NULL, NULL, '大米1000克、生粉、白糖、红糖各400克、葱油200克、炒芝麻。');
INSERT INTO `recipe` VALUES (2219, 79, '群龙戏珠', '1.将中角虾洗净，切断虾头待用，虾身剥壳留尾；生鱼宰净，两面取肉，切出12块簿片，用味料腌制虾肉和鱼片，然后取一片鱼片包一条虾身，沾上面包渣，如此制作12件为止。\r\n2.虾胶挤成圆珠状，约20颗左右，分别沾上面包渣。 \r\n3.烧锅下油，至七成热度，先将虾珠炸熟，摆在圆盘中间。再用竹签将生鱼虾卷、虾头直串成串（共12串），炸至金黄色捞出，拔除竹签，虾头朝里，虾身接虾头，虾尾朝外，成辐射状围住虾丸，即为“群龙戏珠”。', '色泽金黄，造形美观，质酥爽，焦香浓，味鲜美。', NULL, NULL, '中角虾12只（重约600克）、鲜虾胶100克、活生鱼（即“乌鳢”）1尾（重约500克）、生油1500克（耗50克）、精盐、味精、面包渣适量。');
INSERT INTO `recipe` VALUES (2220, 79, '石山扣羊肉', '1.将羊肉皮毛刮净，整块放入烧沸的姜葱水中，滚至八成熟捞出，沥去水分。\r\n2.旺火烧锅，下棕榈油，烧至八成热度，将羊肉块放入油锅炸至略呈金黄色，倒入笊篱，再放清水中漂净油分。\r\n3.旺火烧锅，倾入芝麻油，下姜块（拍碎）、葱段、蒜子、八角、桂皮、陈皮、南（腐）乳、柱候酱等，推匀，爆香，接着下羊肉块，加入绍酒，再加清水、老抽、盐、糖、味精、胡椒粉，推拌均匀，加盖，慢火焖至皮熟入味，用疏壳沥出原汁待用。\r\n4.将熟羊肉连皮切成长方块，皮朝底、肉朝面，整齐排列碗中，加入原汁，上笼蒸至熟透，倒出原汁，将羊肉反扣入盘', '色泽金黄，装盘整齐，肉质酥烂，润滑适口，气味芳香，十分鲜美。', NULL, NULL, '带皮软骨羊肉（以羊腩肉最好）1000克、芝麻油250克、南（腐）乳15克、柱候酱2克、老抽5克、盐3克、糖3克、味精3克、胡椒粉2.5克、绍酒25克、姜50克、葱100克、蒜子30克、棕榈油1500克、二汤150克。');
INSERT INTO `recipe` VALUES (2221, 79, '文昌空心煎堆', '1.取100克糯米粉用清水调拌，搓成粉团，放沸水锅里煮熟，捞出放在案板上，混入余下的400克糕米粉并加入白糖，拌匀后用手掌反复搓至有韧性和粘性（如太干硬，可加进少许热水再搓），即成糯米粉团。 \r\n2.将糯米粉团分成两等份，分别揉圆压平，捏成空心圆球状，留一小洞，往里充气后快速封口，即成两个煎堆坯。 \r\n3.热锅落油，烧至120度时，将煎堆坯轻轻放进热油中，边炸边用长筷子翻动，使之均匀受热，炸至体积比原坯大一倍时捞起，待完全冷却后再用同样方法炸一遍。如此反复3-4次，使之膨胀至排球般大小即成。', '色泽金黄，体积膨大滚圆，皮簿酥脆，香甜可口。', NULL, NULL, '糯米粉500克、白糖100克、生油2000克。');
INSERT INTO `recipe` VALUES (2222, 82, '八宝全鸡', '1.将整笋母鸡洗净，由颈部脱皮，皮不要弄破，剔下少许肉，切成丁；水发冬菇洗净，与罐头竹笋一起切成3厘米见方的丁；白果、莲子、栗子均去皮，莲子尚需去心，洗净；再将江米用鸡汤煮五成熟，捞出，待用。\r\n2.炒锅烧热，放入鸡油，烧五成热，放入鸡丁、冬笋、冬菇。火腿、小枣、白果、栗子煸炒一下，再投入江米、辣大酱、料酒、糖，煸拌均匀成馅。\r\n3.将馅装在鸡腹内，开口处用线缝好，鸡脯向下放在盆内，加少许鸡汤，上笼蒸1小时候左右后，取出后，将汤滗入锅内，再将鸡脯向上扣在盘内。\r\n4.将原汤加入鸡油和调味料，用水淀粉勾成汁', '呈米黄色，味道鲜美，清嫩适口。', NULL, NULL, '笋母鸡1只（约1.75公斤），江米100克，小枣25克，莲子50克，栗子50克，火腿25克，冬菇25克，白果50克，罐头竹笋25克，盐10克，糖12.5克，料酒15克，辣大酱5克，鸡油50克，鸡汤250毫升，水淀粉15克。');
INSERT INTO `recipe` VALUES (2223, 82, '扒野兔肉', '1.将熟兔肉切成6厘米长，1.5厘米宽的长条。\r\n2.炒锅烧热，放入熟鸡油，烧六成热，投入葱片、蒜片，炒出香味，放入兔肉条，辣椒酱、花椒面、酱油、精盐、鸡汤，烧开后，撇去浮沫，用微火扒至肉烂汁快浓时，加味精、芝麻油，再用湿淀粉勾芡，撒匀熟芝麻，出锅，入盘，即可。', '咸辣利口，肉糯鲜香，易于消化。', NULL, NULL, '净熟兔肉300克，精盐1克，花椒面5克，酱油5克，辣椒酱15克，生姜末10克，大葱片10克，鸡汤150毫升，大蒜片10克，湿淀粉15克，芝麻油15克，熟鸡油35克，熟芝麻15克。');
INSERT INTO `recipe` VALUES (2224, 82, '白扒燕菜', '1.将燕菜水发好，火腿切成细丝，菜心撕去筋皮，用水洗净。\r\n2.取瓷碗一个，洗净，先把火腿丝在碗的内壁对角码成四角形，碗底不要码，然后，把燕菜一根一根地顺码在碗内，先码碗底，再码上面，剩下的比较碎的燕菜装入碗内。\r\n3.再取250毫升鸡汤，加少许盐、白糖、味精，倒入碗内，然后上笼蒸20分钟左右取出，滗出汤，翻扣在盘内，碗暂不要拿开。\r\n4.油菜心用开水氽透捞出，用凉水冲凉，理齐，控净水，然后下锅，锅内放鸡汤，上火烧开，加入盐、白糖、味精，烧烂入味后捞出，围在盘子边上，反滗出的汤倒入锅内，再加入适量的鸡汤，', '软滑洁白，色泽美观，味道鲜美。', NULL, NULL, '干燕菜50克，净火腿100克，油菜心10棵，鸡汤1公斤，盐10克，白糖10克，味精3克，料酒15克，芝麻油10克，淀粉5克。');
INSERT INTO `recipe` VALUES (2225, 82, '白果烧牛肉', '1.牛肉洗净，切成4厘米见方的块。\r\n2.白果去皮，用油将果仁稍过一下，捞出，放入开水里，用布搓去嫩皮。\r\n3.炒锅上火烧热，放少许油，投入白糖10克，炒成红色时，下入牛肉块，煸炒片刻，待糖色与肉块炒匀时，加进开水，以没过牛肉为好，同时下入白果仁、白糖10克、葱块和生姜块，用旺火烧开，小火煨焖烂软。\r\n4.捡去葱块和姜块，用水调好淀粉后，倒入炒锅中，勾薄芡，出锅，即可。', '汁浓味香，软嫩郁鲜，咸香微甜。', NULL, NULL, '牛肉750克、大葱50克、白果250克、生姜50克、酱油50克、白糖20克、淀粉10克、味精3克、精盐3克、豆油40克。');
INSERT INTO `recipe` VALUES (2226, 82, '白雪鸡', '1.将鸡脯肉、鸭肥膘肉剔净筋膜，洗净，分别置于干净的丰墩上斩成细茸，用刀分别把鸡茸、鸭茸一层层刮下来，靠砧墩面上带筋的不要，放入瓷盆中，加入少许葱姜水，用竹筷搅匀，然后，加入50克鸡蛋清、1克精盐、料酒和味精，用竹筷3根，向同一方向搅拌，如感觉太稠，仍可稍加些葱姜水，搅拌上劲。\r\n2.将熟鸡蛋黄糕切成小梅花瓣25瓣，水发冬菇洗净，去蒂，用刀片去内层的肉，用剪刀剪成梅花的枝干。\r\n3.再将鸡蛋清125克放入汤盆里，用竹筷3根不停的使劲向一个方向搅打，直打至竹筷插入鸡蛋清中不倒即好。然后加入少许细干淀粉搅拌均', '造型似梅，质地软嫩，清淡而鲜。', NULL, NULL, '鸡脯肉175克，鸡蛋清175克，鸭肥膘肉50克，火腿末15克，油菜叶末10克，熟鸡蛋黄糕75克，水发冬菇1个，料酒10克，精盐1.5克，味精1.5克，白鸡油20克，细干淀粉15克，水淀粉10克，白芝麻25克（焙好），葱姜水15克。');
INSERT INTO `recipe` VALUES (2227, 82, '百花鹌鹑', '1.虾仁洗净后剁成茸，放入碗内，先用牛奶调开后，加入精盐，拌至起粘时，再加入味精、芝麻油、熟鸡油10克、鸡蛋清、淀粉拌成虾胶。\r\n2.将鹌鹑开胸拆去骨头，肉厚处用刀捶匀，将鹌鹑皮朝下平整地摊放在箅上（竹箅衬平盆），撒上少许干淀粉抹匀，然后酿入虾胶，表皮抹光洁，将蟹黄粒、蟹肉、香菜叶、火腿末、粘贴在虾胶上面，随即上蒸锅，用旺火上蒸熟后，取出，切成长方块装盆，装上鹑头、尾，成鹌鹑形。\r\n3.芥菜洗涤净，切丝，炒熟后，围在鹑肉四周。再用熟鸡油起锅，烹入料酒、鸡汤、味精、胡椒粉、精盐、白糖、芝麻油烧开，用湿淀粉勾', '色泽洁白，嫩滑爽脆，饼软香美。', NULL, NULL, '净鹌鹑2只，虾仁400克，青蟹肉50克，牛奶100毫升，青蟹黄50克，精盐10克，火腿末15克，味精15克，鸡蛋清100克，料酒15克，香菜10克，白糖5克，芥菜200克，鸡汤200毫升，芝麻油5克，淀粉100克，胡椒粉1克，熟鸡油80克，熟芝麻15克。');
INSERT INTO `recipe` VALUES (2228, 82, '拌毛蟹肉', '1.将鲜螃蟹冲洗干净，用绳线捆住腿脚，腹向上放入蒸屉上，隔水蒸熟。\r\n2.晾凉后，揭去蟹盖，取出蟹黄，剁去蟹腿，挤出蟹肉，片开蟹身，取出蟹肉，装入净盘中，待用。\r\n3.再将醋、酱油、盐、白糖、葱、姜、蒜、辣椒面、味精和芝麻油勾兑成汁，均匀地浇在蟹肉上，即可。', '味道酸辣，蟹肉鲜美，佐酒上品。', NULL, NULL, '新鲜蟹肉150克，醋50克，酱油15克，精盐2克，白糖4克，大葱15克，生姜5克，大蒜3克，辣椒面1克，味精1.5克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2229, 82, '拌墨鱼', '1.将净墨鱼清洗干净，切成宽2毫米，深度为墨鱼2/3的井字形花纹，再切成1厘米的方块，放入开水锅中，烫至脆软。\r\n2.甜青椒洗净，去蒂根、籽、洗净后，切成1厘米大的方块；大葱去皮，洗净，切成1厘米的小段，分别用油将青椒块、大葱段用热油炸香。\r\n3.酱油加糖、味精，用小火烧成浓汁，食用时，将墨鱼块、葱段、青椒块、酱油浓汁放在一起搅拌均匀，倒入盘中，撒上芝麻，即可。', '色彩大方，香嫩适口，墨鱼腿软。', NULL, NULL, '净墨鱼500克，甜青椒250克，大葱250克，白糖50克，酱油100克，芝麻油75克，味精25克，芝麻5克（焙好）。');
INSERT INTO `recipe` VALUES (2230, 82, '拌牛舌', '1.将牛舌切成3厘米长的片。\r\n2.生姜洗净，去皮，拍碎末；大蒜去皮，去根，拍成碎末。\r\n3.菠菜洗净，摘去黄叶和根，切成段，用开水焯一下，再用凉水冲凉，控干，加入海米搅拌均匀，放在盘里。\r\n4.再将切好的熟牛舌片整齐地码放在菠菜拌海米上。\r\n5.然后，把生姜末、大蒜末、胡椒面、芝麻油、焙好的芝麻和味精调好，浇在牛舌上，吃时拌匀，即可。', '香味扑鼻，清爽适口，佐酒佳品。', NULL, NULL, '熟牛舌150克，水发海米25克，菠菜250克，生姜5克，大蒜5克，精盐5克，辣椒面2克，芝麻油10克，芝麻10克（焙好），味精2克。');
INSERT INTO `recipe` VALUES (2231, 82, '拌鲜贝', '1.将鲜贝去内鳃，洗净，用沸开水烫熟，过凉后控干，切成1厘米的原形片；大葱、生姜、大蒜去皮，洗净，均切成末。\r\n2.瓷碗中，放入大葱末、生姜末、大蒜末、酱油、糖、盐、味精、芝麻油、醋搅拌均匀成调味汁，放入鲜贝肉拌匀，腌渍半个小时，入味。\r\n3.鲜贝壳洗净，消毒，做为容器，把做好的鲜贝肉分装在鲜贝壳中，再码放一个小盘里。\r\n4.食用时，辣大酱分装在每个小碟里，一起上桌，用鲜贝肉蘸辣酱吃，即可。', '味道酸辣，贝肉鲜嫩，别具一格。', NULL, NULL, '鲜贝100克、酱油15克、醋50克、味精1.5克、芝麻油5克、精盐1.5克、辣椒酱2克、大葱10克、生姜10克、大蒜10克。');
INSERT INTO `recipe` VALUES (2232, 82, '鲍鱼鹌鹑', '1.将鲍鱼洗净，控干，切成宽片；蘑菇水发后，去蒂梗，洗净，再将鹌鹑掼死，去净毛，去内脏，洗净，去骨，将肉切成3厘米见方的块，装入盆内，加1.5克盐、蛋清、5克淀粉，拌匀；大葱去皮，洗净，切成长段；生姜去皮，大蒜去皮，洗净后切剁成碎；黄瓜洗净，切丝。\r\n2.炒锅烧热，放入花生油，烧至七成热时，投入鹌鹑肉，划散，翻炸2分钟后，捞出，控油，余油倒入油罐。\r\n3.炒锅留25克豆油，烧热，倒入葱段、青豆，翻炒几下，倒入过油后的鹌鹑，加酱油、糖、料酒和200毫升清水，盖上盖，用中火煨3分钟，加味精，用淀粉勾芡，淋上芝', '红中带绿，鲜香郁浓，肉质鲜嫩。', NULL, NULL, '鲜鲍鱼200克，鹌鹑2只，蘑菇25克，黄瓜200克，花生油750克（实耗75克），料酒10克，酱油5克，精盐4克，鸡骨汤250毫升，蛋清1个，大葱3克，胡椒粉2克，生姜2克，芝麻油5克，大蒜1克，淀粉10克。');
INSERT INTO `recipe` VALUES (2233, 82, '鲍鱼裙边', '裙边，又叫“鱼裙”，就是鼋鱼背盖周围的软边，是很珍贵的烹饪原料。有干、鲜之分。鲜者是直接从活宰后的鱼身上取得。干者则需先进行发制，方法是：坐一锅开水，将干裙边放入，水沸后改小火，将其焖软，取出，捞出，捞在凉水内，刮去表面黑皮和底面粗皮，再用水加葱、姜、料酒煮到能去骨时，捞出拆去骨，修去腐朽变质部分，用水冲泡，去除腥味。\r\n1.将鲍鱼撕去毛边，每个鲍鱼用坡刀片成3片（小的片2片，大的片4片），豌豆苗择好洗净。\r\n2.再将裙边改刀切成5厘米的段，用温水洗一下，捞出，放入盘内。\r\n3.把老母鸡用开水氽透，捞出，', '汤浓味鲜，软烂适口，柔软嫩糯。', NULL, NULL, '鲍鱼肉300克，裙边300克，净老母鸡2公斤，干贝25克，豌豆苗50克，盐10克，白糖10克，胡椒面3克，芝麻油15克，水淀粉5克，大葱15克，生姜10克，料酒15克。');
INSERT INTO `recipe` VALUES (2234, 82, '爆炒虾仁', '1.虾仁用清水冲洗干净，捞出，控干，放入瓷盆内，加入蛋清、5克料酒、2克盐、0.5克胡椒面、5克辣椒面、2.5克味精、10克淀粉、25克豆油，搅拌均匀，腌渍20分钟。\r\n2.大葱去皮，洗净，剁碎；鲜姜去皮，洗净，切片。\r\n3.将鸡汤、5克料酒、4克盐、糖、0.5克胡椒面、2.5克味精、5克淀粉搅拌均匀，配成粉芡汁。\r\n4.再将豆油倒入锅内，烧至五成热时，放入虾仁 ，炸熟后捞出，控油，待用。\r\n5.炒锅留底油25克，烧热，放入葱末、姜末、青豆、辣椒酱，煸炒均匀投入虾仁，倒入芡汁，翻炒几下，淋入芝麻油，出锅，', '红白相间，虾肉嫩辣，清爽利口。', NULL, NULL, '虾仁600克，青豆150克，豆油750克（实耗75克），芝麻油10克，辣椒面5克，辣椒酱10克，鸡汤10毫升，料酒10克，精盐6克，白糖5克，蛋清1个，胡椒面1克，大葱2克，味精5克，生姜2克，淀粉25克。');
INSERT INTO `recipe` VALUES (2235, 82, '冰糖狗腿', '1.将狗腿洗净，放入凉水中浸泡一天，捞出，控水；油菜心摘洗干净，生姜去皮，洗净，切片。\r\n2.狗腿用开水煮透，捞出，在狗腿的一面划成大方块形，深度划到肉的二分之一，在皮的一面抹上酱油和料酒。\r\n3.炸锅上火烧热，放入花生油，烧七成熟时，把狗腿皮朝下放入，炸上色后，捞出（不要把肉皮炸起泡，上色即可）。\r\n4.把狗腿放在大扣碗内，加入冰糖、料酒、盐、姜片、胡椒粉，上蒸锅，用大火蒸2个小时左右，待肉烂时，取出。\r\n5.菜心用开水氽透，捞出，用凉水冲凉，理齐整。\r\n6.狗腿蒸烂后，取出，滗去原汤，把狗腿翻扣在大盘', '肉烂味甜，甜中有咸，肥而不腻。', NULL, NULL, '狗腿半只（约1.5公斤左右）、油菜心10棵、冰糖400克、精盐7.5克、料酒10克、酱油7.5克、花生油1公斤（实耗75克）、生姜片10克、水淀粉25克、狗骨汤500毫升、胡椒粉2.5克、芝麻盐25克。');
INSERT INTO `recipe` VALUES (2236, 82, '菠菜豆芽汤', '1.菠菜择洗干净；绿豆芽去两头，择净洗好，若豆芽很长，可稍微剁几下；开水锅中分别放入菠菜段、绿豆芽焯一下，捞出，控干水分，待用。\r\n2.净狗肉去筋膜，切成3厘米长、1厘米宽的细丝，放入瓷碗中，加入酱油、大蒜茸、芝麻油、大葱丝、生姜丝、胡椒粉搅拌均匀，腌渍入味。\r\n3.炒锅旺火烧热，放入豆油，烧七成热时，放入狗肉条翻炒一下，倒入净清米汤，再把黄酱放入后加辣椒酱搅拌均匀，旺火烧开，放入绿豆芽稍煮后，放入菠菜，淋芝麻油，出锅，入碗，即可。', '酱香郁浓，狗肉鲜香，清爽利口。', NULL, NULL, '菠菜200克、绿豆芽100克、狗肉100克、黄酱50克、辣椒酱15克、清米汤500毫升、酱油10克、大蒜茸10克、芝麻油5克、葱丝10克、胡椒面2克、生姜丝5克、味精1克、豆油25克。');
INSERT INTO `recipe` VALUES (2237, 82, '菜心狮子狗肉头', '1.将狗腿肉浸泡，洗净，去筋膜，放在砧墩上，用双刀斩剁成碎茸泥，放入瓷盆里，加入鸡蛋、料酒、炖酱、精盐、水淀粉向一个方向拌和搅上劲，分成4份，用双手打光，做成大狗肉圆4个，稍揿扁形，油菜心择洗干净，切成6厘米长的段，待用。\r\n2.炒锅上火烧热，用油滑锅后，加入猪油，烧到六成热时，放入狗肉圆，炸到两面呈淡黄色，外层已结皮，随即用漏勺捞出，将油倒入油罐，再将炒锅上火。 \r\n3.把狗肉圆复入锅中，加入料酒、酱油、精盐、白糖、葱段、姜片和狗骨白汤，烧开后，盖上锅盖，端到小火上约焖20分钟。\r\n4.另用炒勺上火烧热', '色泽淡红，油而不腻，味鲜肉嫩。', NULL, NULL, '狗腿肉250克（瘦七肥三）、油菜心200克、鸡蛋40个、料酒15克、狗骨白汤200毫升、精盐10克、酱油20克、味精2克、白糖5克、水淀粉25克、大葱段10克、花生油750克（实耗60克）、生姜1片、炖酱15克。');
INSERT INTO `recipe` VALUES (2238, 82, '叉烧狗肉', '1.将狗脯方肉放入凉水中浸泡半天，捞出，控水，用净布擦净，放入开水锅中，用微火煮透，捞出，洗净，晾凉。\r\n2.再将方狗肉切成5厘米长、2.5厘米宽、0.4厘米（4毫米）厚的片；胡萝卜洗净，刮去皮，也切成与狗肉同样大的片；冬菇小个的整用，大个的一剖两半；大葱去皮，洗净，切段；生姜去皮，洗净，切片。\r\n3.冬菇用开水氽透，捞出，用凉水冲凉，挤净水。\r\n4.按下一片肉、一片胡萝卜、一片冬菇的顺序，相间码入深盘内（将整齐的一面朝下），把不够刀口的狗肉和剩下的配料放在上面（中间要凸起）；用料酒、酱油、狗骨汤、精盐、', '狗肉鲜嫩，味道鲜美，形如葵花。', NULL, NULL, '狗腿肉1方（约1公斤左右）、水发冬笋150克、胡萝卜250克、大葱25克、水淀粉25克、生姜15克、狗骨汤150毫升、酱油7.5克、料酒25克、白糖12.5克、胡椒粉2.5克、精盐10克、焙好芝麻15克。');
INSERT INTO `recipe` VALUES (2239, 82, '炒狗肝', '1.大葱去皮，洗净；生姜去皮，洗净，大蒜去皮，洗净，均切成米粒。\r\n2.将狗肝洗净，用清水浸泡4小时，捞出，冲洗三次，控干，切成薄片，放入瓷碗中，加入鸡蛋、淀粉、精盐，用手抓匀浆好；再把笋片、胡萝卜片放入开水中焯透，捞出，控水。\r\n3.再将炒勺上火，加入花生油烧热，下入浆好的狗肝滑开，炸透，捞出，倒去余油。\r\n4.原炒勺净后烧热，加入香油，烧五成热时，下入葱米、姜米、蒜米，煸炒一下，再投入狗肝片、笋片、胡萝卜片、黄瓜片、料酒、味精、酱油、醋、碱面、狗骨清汤，颠翻翻炒，用淀粉勾芡，淋入明油、翻匀，出勺，盛入', '狗肝滑嫩，味道鲜美，蒜香味浓。', NULL, NULL, '狗肝250克、鸡蛋25克、笋片50克（罐头竹笋）、黄瓜片50克、胡萝卜片50克、葱15克、生姜10克、大蒜头10克、狗骨汤150毫升、花生油500克（实耗40克）、香油20克、酱油15克、米醋10克、料酒10克、味精2.5克、碱面2克、精盐10克。');
INSERT INTO `recipe` VALUES (2240, 82, '炒蚶子', '1.将蚶肉取出，洗净，切成厚3毫米的片；竹笋和蘑菇切丝；水发木耳去蒂根，洗净，撕成小片；葱、姜、蒜去皮，洗净，均切末。\r\n2.再将蚶肉、竹笋、蘑菇、水发木耳用沸水焯一下，捞出，控干，待用。\r\n3.炒锅放入花生油，烧五成热时，入葱末，姜末、大蒜末煸炒出香味，倒入蚶子肉、竹笋、蘑菇、水发木耳，颠炒后加入料酒、味精、酱油、盐、鸡汤、翻炒几下，调好口味，放入少许水淀粉勾芡，烧开后，淋上芝麻油，出锅，入盘，即可。', '蚶肉郁浓，味鲜肉嫩，脆香爽口。', NULL, NULL, '鲜蚶肉250克、罐头竹笋15克、蘑菇15克、花生油40克、大葱15克、生姜10克、大蒜5克、料酒15克、味精1.5克、酱油10克、精盐3克、淀粉15克、鸡汤50克、芝麻油10克、水发木耳15克。');
INSERT INTO `recipe` VALUES (2241, 82, '炒牛肉片', '1.把牛肉剔净筋膜，洗净后，切成薄片，拍平。\r\n2.再把牛肉单层排在盘里，撒上大葱末、韭菜末和大蒜泥，腌渍一天，再把白糖、酱油、芝麻油、辣椒油搅拌均匀，浇在牛肉上，搅拌均匀，腌渍3小时，如放在冰箱里，则要适当延长时间，一定要做到腌渍入味。\r\n3.把豆油倒入炒锅内，烧七成热时，旺火沸油，把牛肉片放入，爆炒几分钟，捞出，稍微滗一下油，出锅，入盘。\r\n4.烤牛肉用的渍汁，取200克入碗，一同上桌，即可。', '口味香辣，牛肉细嫩，咸鲜微辣。', NULL, NULL, '牛臀尖250克，芝麻油16克，酱油45克，大葱10克，大蒜泥5克，韭菜5克，白糖6克，辣椒油1克，烤牛肉渍汁200克，豆油100克。');
INSERT INTO `recipe` VALUES (2242, 82, '炒什锦西红柿', '1.红辣椒择洗干净，与青甜椒去蒂籽，洗净，均切成1厘米见方的丁；大葱去皮，洗净，切成葱末，待用。\r\n2.西红柿洗净，用开水烫后，去皮，去蒂，切成1厘米见方的丁。\r\n3.瘦猪肉洗净，去筋膜，洗净，也切成1厘米见方的丁。\r\n4.小瓷碗中，放入精盐、白糖、味精、鸡汤、水淀粉，搅拌均匀，调拌成芡汁。\r\n5.炒锅旺火烧热，加豆油烧热，放入葱末煸炒出香味，投入瘦肉丁，变白，捞出。\r\n6.把鸡蛋磕开，分别放入两个小瓷碗中，加少许精盐、味精和少许清水搅拌均匀，上屉蒸熟，取出，晾凉后，切成1厘米见方的小丁。\r\n7.炒锅净后', '色相衬，鲜嫩酸辣，大酱郁浓。', NULL, NULL, '西红柿250克，瘦猪肉50克，红辣椒50克，青甜椒50克，鸡蛋2个，豆油80克，精盐3克，白糖15克，味精2克，大葱10克，芝麻油5克，鸡汤250毫升，水淀粉15克，辣大酱15克。');
INSERT INTO `recipe` VALUES (2243, 82, '炒贻贝', '1.贻贝去壳，洗净。\r\n2.竹笋、蘑菇和油菜切片；水发木耳撕成小片；葱、姜、蒜去皮，洗净，均切末。\r\n3.将贻贝、竹笋、木耳、蘑菇和油菜片分别用开水氽一下，捞出。\r\n4.炒锅烧热，放入花生油，烧五成热时，放入葱、姜、蒜和辣椒面煸炒出香味，再投入贻贝肉、竹笋、木耳、蘑菇和油菜，翻炒后，用酱油、料酒、味精调好口味，淋少许水淀粉勾芡，烧开即可。', '色彩鲜艳，味清可口，贻贝脆嫩。', NULL, NULL, '贻贝肉250克，罐头竹笋15克，蘑菇15克，水发木耳15克，油菜心15克，花生油35克，酱油15克，料酒10克，味精1.5克，淀粉15克，精盐1.5克，辣椒面0.5克，大葱5克，大蒜5克，生姜5克。');
INSERT INTO `recipe` VALUES (2244, 82, '炒杂烩', '1.大白菜择洗干净，胡萝卜洗净，去皮，罐头竹笋捞出，控干；洋葱头去皮，洗净，黄瓜洗净，均切成丝。\r\n2.菠菜准则洗干净，切成3厘米长的段。\r\n3.将牛肉切成纸一般薄片（先冷冻后切），再改切成3厘米长、厘米宽的丝，装入瓷碗里，待用。\r\n4.再取一小瓷碗，将白糖、酱油、葱、大蒜泥、芝麻面、黑胡椒面和芝麻油调成汁，搅拌均匀后，倒在牛肉丝上，抓匀，腌渍30分钟。\r\n5.将粉丝放在清水里泡10分钟，捞出，控干，切成10厘米长的小段。\r\n6.再将大白菜丝、胡萝卜丝、竹笋丝、洋葱丝、菠菜段，分别炒熟。\r\n7.取一大碗放', '色彩鲜明，味香可口，风味独特。', NULL, NULL, '牛肉片250克，白糖12克，酱油15克，大葱10克，大蒜5克，黑胡椒面1.5克，芝麻油8克，大白菜、胡萝卜、竹笋、洋葱头、黄瓜各125克，菠菜50克，鸡蛋1个，粉丝60克，酱油20克，白糖10克，精盐20克，胡椒粉2.5克，味精5克。');
INSERT INTO `recipe` VALUES (2245, 82, '炒章鱼', '1.将章鱼肉清洗干净，切成长3厘米的片；罐头竹笋，水发蘑菇均切丝；水发木耳撕成小片；大葱、生姜、大蒜去皮，洗净，剁成碎末。\r\n2.再将章鱼用开水烫好，控干。\r\n3.旺火烧热炒勺，放入豆油，烧至五成热时，放入葱末、姜末、蒜末煸炒出香味，再放入章鱼肉片、竹笋丝\\蘑菇丝、木耳片，翻炒几下，加料酒、味精、盐，再炒几下，调好口味，加入少许淀粉勾芡，盛入碗中，撒上芝麻，即可。', '口味鲜美，章鱼脆嫩，咸香可口。', NULL, NULL, '鲜章鱼肉250克，罐头竹笋15克，蘑菇25克，水发木耳15克，豆油35克，酱油25克，料酒15克，味精2克，淀粉25克，大葱5克，大蒜3克，生姜5克，精盐2克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2246, 82, '陈皮炸鹅', '1.炒勺放入少许油，烧热后，放葱段、姜片、陈皮块，煸炒出香味后，放鹅肉块300克，再放料酒、酱油、白糖、清水，加盖用旺火烧开，改小火煨烧20分钟，再用大火烧，小火慢煨，至熟。\r\n2.余下的300克鹅肉块，撒盐、胡椒粉，滚上面粉，蘸鸡蛋液，沾上面包渣，用豆油炸至呈金黄色，熟透，放盘一边，旁边放辣大酱。\r\n3.土豆切成片，炸黄撒盐成咸口，黄胡萝卜用开水氽一下，加点醋和白糖成酸甜口，豌豆煮熟后，均放在盘中间。\r\n4.小火煨熟的300克鹅块，淋上芝麻油，放在盘的另一边，即可。', '脆香郁浓，肥而不腻，促进食欲。', NULL, NULL, '鹅肉600克，鸡蛋2个，面粉50克，豆油500克（实耗50克），面包渣100克，土豆100克，黄胡萝卜50克，陈皮50克，辣大酱25克，豌豆50克，葱10克，姜5克，胡椒面2克，酱油25克，料酒15克，白糖15克，醋25克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2247, 82, '齿绿苦荬菜（苦苦菜）', '学名齿经苦荬菜，又叫苦菜、苦菜芽，多年生草本植物，株最高可达20厘米－50厘米，每棵开花6－8朵，瓣呈黄色，叶、茎断裂时立刻涌出苦味的白浆液，冬天冻不死，它的分布很广。食疗可解热、健胃、造血、消炎等功效，还可治消化不良、肺炎、肝炎、跌损伤、脓肿等症，作药用，可在春季菜集后，晒干、切碎，入药，作食用要放在盐水中久泡。可作凉拌菜，也可腌制泡菜。\r\n1.将盐放入泡菜坛中，加清水，搅拌均匀化开成盐水。\r\n2.再将苦荬菜根择洗干净，控干，装坛浸泡在盐水中，以驱苦味，装完后压上石头（或重物），过5天后，取出，倒去苦咸', '辣咸甜酸，苦鲜爽口，调理消化。', NULL, NULL, '苦荬菜2公斤（根茎），糯米面250克，红辣面200克，大蒜2头，鱼清汤150毫升，大葱8根，生姜15克，芝麻25克（焙好），精盐50克，白糖50克。');
INSERT INTO `recipe` VALUES (2248, 82, '葱辣鳜鱼条', '1.鳜鱼洗净，剔去脊骨，切成条，放入瓷盆中，加葱丝、姜片、酱油、料酒搅拌均匀，腌渍1小时，入味。\r\n2.大葱去皮，洗净，切6厘米长的丝；辣椒擦净，去把和籽，切段。\r\n3.再将腌渍入味的鳜鱼条，拣去葱丝、姜片，鳜鱼控干，放入八成热的花生油中，炸至八成熟，捞出，控油，余油倒出。\r\n4.炒锅净后放入50克芝麻油，烧热，放入25克葱丝煸炒至牙黄色，出香味后，再下入姜片、鸡汤，加干辣椒段、酱油、料酒、糖、鳜鱼条、味精，用中火煨至汁浓，撒上焙好的芝麻。\r\n5.食用时，拣出姜和辣椒。100克葱丝垫底，鱼摆在葱丝上，即可', '鳜鱼细嫩，口味香辣，葱香宜人。', NULL, NULL, '鳜鱼1条（约750克），大葱150克，鸡汤500毫升，花生油500克（实耗50克），生姜10克，精盐5克，酱油10克，料酒30克，味精2克，干辣椒2克，芝麻20克（焙好），白糖10克。');
INSERT INTO `recipe` VALUES (2249, 82, '脆皮嫩鸡', '1.将鸡用刀自尾尖沿背脊骨剖至颈部，掰开，用刀面拍一下，再在肉厚处剁几刀；葱去皮，切段；生姜去皮，洗净，拍碎。\r\n2.炒锅烧热，放入750毫升水烧开，把鸡放入锅内（皮朝下），加入酒、酱油、葱段、生姜末烧20分钟，至鸡呈红色时，捞出，控干。\r\n3.净锅，将豆油倒入锅中，在旺火上烧至八成热时，放入鸡，炸3分钟左右，至鸡皮黄亮时，捞出，控油，擦匀芝麻油。\r\n4.用刀先将鸡的腹部剖开，使鸡切成两片，斩下鸡颈及鸡翅膀，取下双腿。然后将鸡头斩下，对劈开，拼放在腰盘的一端；将鸡颈斩成小段，拼接在鸡头的下面，再把鸡翅膀和', '肉皮脆香，鸡肉质嫩，鲜美宜人。', NULL, NULL, '嫩鸡1只（约1250克），大葱20克，料酒25克，生姜15克，酱油50克，花椒盐15克，辣大酱50克，芝麻油15克，豆油1000克（实耗75克）。');
INSERT INTO `recipe` VALUES (2250, 82, '大酱豆腐', '1.将豆腐切成2厘米见方的丁，放入开水锅中稍氽一下后，捞出，控水。\r\n2.大葱、生姜去外皮，洗净，均切成碎末。\r\n3.炒勺旺火烧热，放入50克豆油，待五成热时，投入葱末、姜末、煸炒几下，加入大酱稍炒几下，再放入白糖、料酒、味精，搅拌均匀后加豆腐丁，翻炒几下，放入水淀粉勾芡，即可。', '色泽枣红，豆腐细嫩，味道醇香。', NULL, NULL, '豆腐500克，大酱50克，豆油50克，白糖30克，料酒20克，大葱10克，生姜10克，味精3克，淀粉10克。');
INSERT INTO `recipe` VALUES (2251, 82, '蛋清红鳟鱼条', '1.将红鳟鱼去鳞，从鳃部挖出鳃和内脏，洗净，斩去头，把鱼肉切成两片，剔去鱼皮，切成长5厘米、宽5毫米长的鱼条，放入碗中，用料酒、盐、味精腌渍1分钟，入味，待用。\r\n2.再将蛋清放入碗中，抽打起泡，拌入干淀粉，涂遍鱼条。\r\n3.炒锅烧热，放入豆油，烧热后，稍冷却，再将鱼条逐条入锅，炸2分钟，待结壳时，捞出，控油，入盘。\r\n4.食用时，随跟花椒盐、葱白段和辣椒酱各一碟，配食，即可。', '色泽淡黄，脆酥爽口，焦香微辣。', NULL, NULL, '鲜红鳟鱼1条（约750克），鸡蛋清4只，大葱白段50克，辣椒酱50克，料酒25克，精盐2.5克，味精2.5克，干淀粉75克，豆油1000克（实耗75克），花椒盐25克。');
INSERT INTO `recipe` VALUES (2252, 82, '党参炖鸡', '1.将鸡放血，褪毛，去内脏洗净，在开水锅中煮一下，去血污，捞出用清水冲洗干净，控干水。\r\n2.再将鸡放入，放入清水淹没鸡，加枸杞、陈皮、党参、大酱、姜片、精盐，拌匀，盖上盖，放入蒸锅屉上，蒸2.5小时，入味，出锅。', '鸡肉嫩鲜，汤美肉烂，活血补气。', NULL, NULL, '母鸡1只（约1.5公斤），党参50克，枸杞25克，大酱150克，陈皮10克，姜片4片，精盐10克。');
INSERT INTO `recipe` VALUES (2253, 82, '地枣狗肉条', '地枣，又名天蒜、野茨菰、剪刀草，在亚洲各地分布很广，根很似大蒜头，但直径仅有2－3厘米，属百合科，多年生草本植物，喜欢生在山地或原野的草丛中。根与茎可食，用亦可作药材，采集时，根和茎一起采，尽量不要伤根，时间选在4－5月花含苞欲放末放时最佳。地枣具有解毒和促进血液循环的作用，可治疗跌打损伤，筋骨痛，肠炎等症，并具有抗癌作用。\r\n1.把地枣洗净，根丸洗净切片，荭切成丝段。\r\n2.再把熟瘦狗肉切成薄长条。\r\n3.鸡蛋打入瓷碗中，加入面粉、精盐、酱油、胡椒面、清水，搅拌均匀，再将地枣和狗肉条放入，拌匀，使其挂匀', '呈金黄色，脆香爽口，佐酒上品。', NULL, NULL, '地枣400克，狗肉50克（熟瘦狗肉），豆油500克（实耗75克），鸡蛋1个，面粉150克，精盐3克，酱油10克，胡椒面2克，清水100毫升，酱油20克，食醋5克，白糖3克，葱末2克。');
INSERT INTO `recipe` VALUES (2254, 82, '炖牛掌', '1.牛蹄砸去蹄壳，在火上烧焦皮面，放在水内泡软，刮洗净，用水加整葱，碎姜，入煮锅将牛蹄煮到能拆骨时，捞在凉水内拆去骨，切下掌，水削去掌底粗皮，修去周围边沿，再用葱、姜、料酒，开水氽两遍，用凉水泡上。\r\n2.油菜择用嫩心；母鸡、肘子用开水氽熟，捞在凉水内刮洗干净，把鸡剁成4大块；干贝洗净；大葱去皮切段，姜去皮切成片；冰糖砸成碎粒。\r\n3.用大砂锅垫上竹箅子，将牛掌放在箅子上，放入鸡、肘子、干贝（用净布包上）、葱、姜、清水、盐、料酒250克、炖酱、胡椒粉，烧开撇尽沫子，用中等火力煨上。\r\n4.炒锅烧热，放入芝', '色泽透亮，质地糯烂，味浓汁鲜。', NULL, NULL, '牛蹄12个，净膛母鸡1千克，干贝15克，肘子500克，菜心500克，大葱50克，精盐10克，生姜25克，炖酱25克，料酒250克，冰糖15克，芝麻油75克，味精5克，豆油50克，胡椒面2.5克，鸡油15克，焙好芝麻25克。');
INSERT INTO `recipe` VALUES (2255, 82, '番茄黄焖鸡块', '1.将净鸡浸泡，洗净剁成6厘米长、3厘米宽的长方块，放入瓷盆内，加精盐、料酒、辣椒面腌渍入味，待用。\r\n2.鸡蛋打入小瓷碗，加入水淀粉，搅拌均匀成蛋糊粉，放入鸡块浆匀。\r\n3.西红柿去蒂根，洗净，切成厚片；大葱、生姜均洗净，切成碎末，待用。\r\n4.炒锅烧热，放入花生油，烧至四五成热时，将浆好的鸡块分散下入勺中，用筷子慢慢滑开，待鸡块变白时，滑透，倒入漏勺中。余油倒入油罐。\r\n5.炒锅净后，放入25克花生油，烧热后，投入葱末、姜末煸炒出香味，下入炸好的鸡块、辣椒油、料酒、精盐、白糖、鸡汤，烧开，盖好锅盖，改', '色泽红黄，甜酸咸辣，肥而不腻。', NULL, NULL, '净鸡块250克，西红柿250克，花生油500克（实耗50克），辣酱油15克，辣椒面5克，料酒15克，精盐2克，白糖20克，味精2克，鸡蛋1个，大葱5克，鸡汤250毫升，生姜5克，水淀粉10克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2256, 82, '番茄牛肉菠菜汤', '1.葱头去皮，洗净，切丝；熟牛肉切片；鸡蛋煮熟，去皮，竖切一半；菠菜择洗干净，切成1.5厘米的段；土豆去皮，切块；胡萝卜去皮，洗净，切斜花片。\r\n2.炒锅内，放入芝麻油，烧热，放入葱头丝煸炒出香味，放胡萝卜斜花片，加花椒粒焖半熟时，放上番茄酱，焖至油呈红色时，成汤码。\r\n3.煮汤放入鸡汤，投入土豆块，焖九成熟时，加上焖好的汤码，加盐、胡椒面、醋、辣椒油，调剂好口味。\r\n4.食用前5分钟放入牛肉片、菠菜段，拣去花椒粒，煮开，出锅，盛入碗内，每碗放半个煮老熟的鸡蛋，撒上芝麻，即可。', '咸酸利口，红黑透绿，口味独道。', NULL, NULL, '熟牛肉250克，鸡汤2500毫升，土豆500克，鸡蛋4个，芝麻油50克，盐15克，胡椒面2克，花椒粒2克，番茄酱150克，菠菜500克，醋20克，辣椒油1克，芝麻10克（焙好），葱头2个，胡萝卜2根。');
INSERT INTO `recipe` VALUES (2257, 82, '翡翠鸡茸豆腐汤', '1.将豆腐切成长3.5厘米较薄的象眼片；白菜去掉根部和叶子留下中间的梗，用刀顺白菜中间破开，取出菜心留用，再将菜心切成3.5厘米长的小一字条，放入盘内，待用。\r\n2.鸡胸肉去筋膜，洗净后，置菜墩上切成碎粒状，再用刀背反复捶砸成鸡泥，放入碗内，待用。\r\n3.菠菜叶放铝锅内，加少许盐，用擀面棍砸成烂泥状，然后，用纱布过滤出汁，待用。\r\n4.火腿切成象眼片，海米用温水泡软，冬菇洗净，去掉根部，片成两瓣；干贝放料酒、葱段、姜片，上火蒸至用手搓成丝时，下屉；菜心在开水锅中焯一下，控净水分，放碗内，撒上味精、盐，待用', '腐如玛瑙，菜心脆嫩，汤清味醇。', NULL, NULL, '豆腐100克，白菜心100克，鸡胸肉15克，鸡蛋清2个，菠菜叶100克，熟火腿50克，水发香菇50克，水发海米40克，干贝25克，淀粉75克，鸡汤1000毫升，料酒15克，盐3克，味精2克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2258, 82, '翡翠鲜贝球', '1.先制出鲜贝泥：鲜贝洗净，控净水分，放在干净的猪皮上砸成泥，加入毛姜水（生姜洗净骼刀拍碎，放温水中泡2个小时左右）调成稠糊状，搅拌均匀，上劲。加入盐、味精、料酒、3只蛋清调匀，再入熟白鸡油及少许干淀粉拌匀。\r\n2.菠菜叶择洗干净，用刀剁成泥，放入开水锅中，叶绿素就会浮上来，取出，放凉水中使用。\r\n3.鲜贝泥放入瓷盆中，加入叶绿素，搅拌均匀，挤成直径1.5厘米的小球，氽熟后，盛入盘内。\r\n4.净炒勺放入鸡清汤，加入料酒、盐、味精烧开，放湿淀粉，勾琉璃芡，淋芝麻油，出勺，烧在鲜贝球上。\r\n5.鲜西红柿洗净，', '色泽碧绿，鲜美滑嫩，易于消化。', NULL, NULL, '鲜贝250克，熟鸡油50克，菠菜叶300克，鸡蛋清3个，水淀粉15克，味精3克，生姜15克，料酒15克，盐5克，大葱10克，鲜西红柿1个，鸡清汤300毫升，芝麻油10克，干淀粉5克。');
INSERT INTO `recipe` VALUES (2259, 82, '粉丝煨牛肉丝', '1.牛肋条肉洗净，浸泡在清水中，4小时后，捞出，控干。\r\n2.把整块牛肉同盐和胡椒粉一起放进煮锅里，加清水，直至水把牛肉淹没为止，盖上盖，用微火煨，直到牛肉熟烂为止。\r\n3.捞出牛肉冷却后，用手把牛肉顺丝撒成长丝。\r\n4.再把粉丝放在热水里浸泡10分钟，捞出，沥干。\r\n5.煮锅里，放入牛肉丝，加进葱末、白糖，煨约10分钟。煨的时候，把粉丝放在热牛肉锅里，接下来，再把芝麻油、辣椒粉和精盐、黑胡椒粉等调料一起放进，待红色的油滚到上面时，把抽打好的鸡蛋液慢慢倒进锅里，搅拌均匀，使鸡蛋液和肉丝相互缠上。\r\n6.食', '牛肉香嫩，粉丝软糯，口味微辣。', NULL, NULL, '牛肋条肉1000克，大葱25克，粉丝125克，芝麻油40克，辣椒粉2克，鸡蛋100克，精盐20克，黑胡椒粉2.5克，白糖12克。');
INSERT INTO `recipe` VALUES (2260, 82, '凤凰里脊', '1.鸡虾泥加盐、味精、大葱末、姜末、豆腐茸一起搅拌，和上劲，在盆中摆成凤凰展翅的形状；鸡蛋分装在二个碗内，加入食品色素、水菱粉、盐打匀，上蒸锅蒸，分别蒸成红、黄二色蛋糕，冷却后，用小刀刻成羽毛形；将香菇切成细丝，白鸡切成羽毛状，青萝卜刻成凤凰头，分别将蛋糕、香菇、白鸡、翅针一层层摆在虾泥身子旁，成一完整的五色凤凰，上笼蒸熟。\r\n2.将猪里脊片洗净，剔净筋膜，切拍成金钱圆形厚片，放入盆内，加葱姜末，料酒、盐、味精，煨拌入味，沾上干面粉，蛋液。\r\n3.炒锅烧热，放入豆油，待烧至三成热时，将金钱里脊片拖上鸡蛋糊', '呈五彩色，造型漂亮，味鲜爽口。', NULL, NULL, '猪里脊300克，甜青椒100克，胡萝卜100克，鸡蛋6个，面粉25克，熟白鸡脯50克，水发香菇50克，水发翅针25克，青萝卜1个，鸡虾泥75克，豆腐25克，大葱末25克，料酒10克，生姜末15克，鸡汤150毫升，味精5克，番茄红油50克，樱桃2个，水淀粉25克，芝麻面15克，豆油250克。');
INSERT INTO `recipe` VALUES (2261, 82, '凤凰氽牡丹', '1.摘取正在盛开的金达莱花5朵，用清水掺入少许洗涤精，将花冲洗干净，然后，再用纯清水冲，控去水分，注意不能伤花瓣。\r\n2.鸡脯肉去筋膜，洗净，用刀片成9厘米长、3厘米标准的柳叶形薄片；鲜蘑菇洗净，片成圆形片；海蜇头洗去泥沙。\r\n3.将鸡片放入碗内，加入盐、味精、蛋清、水淀粉腌拌上浆。\r\n4.旺火炒热炒锅，放入500克熟鸡油，待油烧至五成热时，倒入鸡片，滑熟后倒入漏勺，沥去油。\r\n5.煮锅放入开水，烧滚开后，将海蜇头下锅烫五成熟，捞出，控水，同鸡片一起放入汤碗内。\r\n6.煮锅净后，加入鸡油汤、盐、料酒、味精', '汤汁清鲜，嫩香爽脆，原汤原味。', NULL, NULL, '水发海蜇头250克，鸡脯肉175克，鲜蘑菇25克，盐5克，金达花5朵，味精5克，水淀粉20克，鸡蛋清半只，鸡清汤2公斤，熟鸡油500克（实耗50克），芝麻油10克。');
INSERT INTO `recipe` VALUES (2262, 82, '芙蓉蹄筋', '1.将发好的蹄筋置于砧墩上，每根蹄筋用刀一切两段，火腿、罐头冬笋切成片。\r\n2.瓷碗中加入7.5克料酒、1克精盐、1克味精、20克水淀粉、100毫升清水，搅拌均匀，待用。\r\n3.炒锅上火烧热，用油滑锅后，加入鸡油，烧至五成热，倒入鸡蛋清，随即用手勺推几下，鸡蛋清翻上来时，倒入漏勺内，控干油，锅复上火，加入鸡汤，放入蹄筋，然后，加入7.5克料酒、1.5克精盐、1克味精，烧透后，倒入芙蓉蛋，再把冬笋片、火腿片放在芙蓉蛋上面，用20克水淀粉，淋入锅中勾芡，边淋边晃动锅，加入芝麻油，出锅，入盘即可。\r\n注：\r\n半', '洁白如芙蓉，肥糯嫩鲜香。', NULL, NULL, '发好的蹄筋300克，鸡蛋清100克，火腿25克，熟冬笋25克，鸡汤200毫升，料酒15克，精盐2.5克，味精2克，芝麻油5克，水淀粉40克，鸡油750克（实耗75克）。');
INSERT INTO `recipe` VALUES (2263, 82, '芙蓉虾仁', '1.虾仁洗净，沥干水分，放入瓷碗内，加盐、蛋清、干菱粉搅拌均匀上浆。\r\n2.另取一个小瓷碗，放入鸡蛋清、盐、味精、凉鸡汤、水菱粉、芝麻油拌匀，待用。\r\n3.炒锅烧热，加入豆油，待烧至五成热时，放入虾仁，滑至八成熟时，倒入漏勺，沥去油，即倒入蛋清糊，用微火推炒，见蛋清将凝固时，倒入虾仁，煸炒均匀，出锅，装盘。', '色泽美观，味鲜清爽，嫩滑可口。', NULL, NULL, '虾仁250克，鸡蛋清6只，豆油500克（实耗100克），精盐5克，味精5克，水菱粉25克，鸡汤150毫升，干菱粉5克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2264, 82, '盖浇饭', '1.豆芽（黄豆芽和绿豆芽均可）去根，洗净控干，放入开水锅焯熟，出锅，放入凉水中，控水，切成段，加熟牛肉丝、芝麻、50克芝麻油，拌匀成冷拌菜。\r\n2.黄瓜洗净，消毒，切成丝，凉拌。\r\n3.芹菜摘洗干净，用开水焯熟，再切细丝，做成凉拌菜。\r\n4.再将蛋白和蛋黄色开，分别摊成饼，一半切成菱形，一半切面细丝。\r\n5.紫菜在锅里烤脆，出锅后揉成粉末。\r\n6.胡萝卜洗净，去皮，切成细丝凉拌。\r\n7.把熟大米饭盛在碗里，中间稍高，再将各种菜料按颜色配好，按顺序整齐地摆出美妙的图案，放在饭上，撒上葱花，淋上芝麻油。\r\n8', '图案美妙，色泽艳丽，开胃爽口。', NULL, NULL, '大米饭2000克，豆芽300克，黄瓜300克，胡萝卜300克，芹菜200克，熟牛肉丝1000克，鸡蛋5个，芝麻100克（焙好），紫菜100克，大葱末50克，芝麻油100克。');
INSERT INTO `recipe` VALUES (2265, 82, '干贝海参', '1.水发海参去内脏，洗净，切成6厘米长，6毫米宽的丝，用冷水下锅，煮开，除去杂味，倒入漏勺，控干水。\r\n2.熟鸡肉、蘑菇洗净去蒂根，均切成海参同样大的丝。\r\n3.干贝去掉边角上的硬丁，洗净，装入瓷碗中，加入50毫升清水，入蒸锅蒸透，取出，晾凉，用手将干贝撕碎。\r\n4.炒锅放入10克花生油，烧至六成热时，放料酒、酱油、精盐，倒入海参，加入鸡汤煮一下，倒入漏勺，控干。\r\n5.净锅，放入50克花生油，将海参下锅，加入鸡汤500毫升、盐、酱油、味精，烧开，水淀粉勾芡。烧开，把其中三分之二盛入大碗内，三分之一留在锅', '干贝嫩鲜，海参味浓，汁浓适口。', NULL, NULL, '水发海参750克，干贝50克，熟鸡肉50克，酱油30克，味精1克，胡椒面0.5克，花生油60克，蘑菇25克，料酒50克，湿淀粉75克，芝麻油2.5克，精盐1.5克，鸡汤650毫升。');
INSERT INTO `recipe` VALUES (2266, 82, '干贝黄肉翅', '1.将干黄肉翅加开水下锅，用旺火煮1小时，再改小火煮1小时，离火后，在原锅中泡3小时，取出，出沙，再用冷清水洗净。\r\n2.把洗后的鱼翅温水下锅，用大火煮开，改小火煨4小时，取出，放在温水盆里，剔去翅骨及烂肉，然后，用凉水泡3小时。\r\n3.再将泡过的鱼翅换水上锅，煮沸开，先把嫩的取出，放凉水盆中，剔去翅脆骨，老的继续煮，直至煮软，去净脆骨为止，然后，再放入凉水中泡2小时，换凉水洗净。\r\n4.把洗净的鱼翅再下锅，用小火煮4小时，离火浸泡7小时，取出后洗一次，再用凉水泡3小时，用火煮开，去净异味、细沙、碎骨，再', '干贝鲜美，鱼翅柔软，汁浓醇厚。', NULL, NULL, '鲜干贝100克，干黄肉翅1公斤，老母鸡2.5公斤，填鸭1公斤，火腿150克，大葱25克，淀粉15克，生姜15克，味精5克，芝麻油10克，芝麻15克（焙好），干辣椒2个。');
INSERT INTO `recipe` VALUES (2267, 82, '干贝鱼肚', '水发鱼肚的方法是：将鱼肚放入开水锅中一沸开就离火，焖5、6个小时，取出放入温水，将鱼肚表面的脏物刮洗干净，再用凉水浸凉备用。临用前改刀成型，放入开水中略煮一下，用凉水冲凉即可，一般优质肚方可用水发。\r\n1.将干鱼肚放在5公斤开水锅内，煮开后，即离火泡13个小时，取出，用冷清水洗2次。\r\n2.再将鸡、鸭、葱、姜用小火煮出1公斤汤，鸡鸭作它用。\r\n3.把鱼肚切成3厘米见方的块，用500毫升的鸡鸭清汤氽一次。\r\n4.再把干贝去净筋，洗净，加入150毫升鸡鸭汤，上锅蒸2个小时，抓碎。\r\n5.将鸡鸭放入双耳锅，再放', '汤浓味鲜，软糯味美，鲜美醇厚。', NULL, NULL, '干贝100克，干鱼肚350克，老母鸡1500克，填鸭500克，糖10克，盐10克，料酒25克，大葱25克，淀粉5克，生姜15克，胡椒面5克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2268, 82, '干烧鳜鱼', '1.将鱼去鳞、鳃和内脏，洗涤净，在鱼身两侧斜剞数刀，抹匀料酒15克、酱油、大葱、云皮，洗净；生姜，洗净，均切开。\r\n2.炒锅烧热，倒入100克豆油，烧五成热时，将鱼放入锅内，煎黄后，翻煎另一面，煎好后捞出，锅内再加50克豆油，放进葱片、姜片煸炒一下，加上大酱、煸炒几下，烹入35克料酒，加入盐、糖、味精、胡椒面和50毫升清水，再将鱼下锅，沸后再烧25分钟，待汁收干时，用淀粉勾芡，加上醋、辣椒油即可。', '色泽金黄，口味鲜辣，鳜鱼细嫩。', NULL, NULL, '鳜鱼1条（约1000克），大酱100克，豆油150克，料酒50克，精盐2克，酱油1克，醋2克，白糖5克，胡椒面2克，辣椒面2克，辣椒油5克，葱5克，淀粉5克，姜5克。');
INSERT INTO `recipe` VALUES (2269, 82, '干烧鲫鱼', '1.将鲫鱼去鳞、鳃和内脏，洗净后鱼身两侧斜切数刀，用3克料酒、1克酱油、1克胡椒面腌15分钟。\r\n2.大葱、大蒜、生姜均切成碎末。\r\n3.炒锅烧热，下入花生油，烧热，将鱼投入锅中，煎炸，待一面煎黄时，再翻煎另一面，煎好后，捞出，控油。\r\n4.炒锅放入花生油，将姜末、蒜末、大酱、辣椒酱一起投入锅中，煸炒均匀，加入料酒、盐、酱油、糖、味精、胡椒面和400毫升清水，投入鲫鱼，盖上盖，用小火焖20分钟后，改大火收汁，放入葱末、芝麻油和醋，即可。', '色泽焦黄，口味香辣，鲫鱼肥嫩。', NULL, NULL, '活鲫鱼2条（约75克），花生油150克，精盐2克，醋6克，酱油2克，白糖8克，料酒8克，胡椒面1克，大酱50克，辣椒酱6克，大葱10克，大蒜15克，生姜35克，芝麻油5克，味精3克，淀粉5克。');
INSERT INTO `recipe` VALUES (2270, 82, '干烧鲤鱼', '1.将鲤鱼去鳞、鳃，剖腹去内脏，洗净，控干，鱼身两侧斜剞数刀，抹上酱油，将蘑菇、竹笋、葱、木耳、生姜、大蒜、红辣椒洗净，均切碎末，青豆煮熟，待用。\r\n2.炒锅倒入豆油，烧八成热时，放入鲤鱼，炸3分钟，捞出，控油，倒出余油。\r\n3.炒锅放25克豆油，烧热时，放入白糖，翻炒化开，倒进辣椒酱末、大葱末、姜末煸炒，再投入蘑菇、竹笋、木耳片末、鸡汤、盐、酱油、料酒，烧开，放入鱼，盖盖，用小火煨5分钟，把鲤鱼翻个儿，让另一面吸收料汁，待汁浓时，加味精和余下的料酒、青豆，再淋上芝麻油，撒上芝麻，即可。', '汤汁郁浓，辣香爽口，肉嫩软糯。', NULL, NULL, '鲤鱼1条（约500克），蘑菇15克，罐头竹笋15克，水发木耳5克，熟青豆15克，精盐3克，酱油10克，红干辣椒2个，大葱12克，生姜5克，大蒜5克，白糖25克，料酒15克，味精1克，豆油1000克（实耗50克），鸡汤300毫升，芝麻2克（焙好），芝麻油2克。');
INSERT INTO `recipe` VALUES (2271, 82, '干煨肉条', '1.猪方肉用刀刮去表皮毛污，放入开水锅内煮熟，去尽血沫，捞出，用冷水洗净，放入大酱卤汁腌渍上色，捞出，冷却后，切成7厘米长、3毫米厚的肉条，排列整齐，肉皮朝碗底扣入碗内。\r\n2.炒锅烧热放入香油、糖炒成金黄色，即烹入黄酒，加入盐、鸡汤搅拌均匀，倒入肉条碗内，上笼蒸半小时，取出。\r\n3.炒锅烧热，放入香油、冬笋丁、香菇丁、熟火腿丁、青豆、煸炒几下，把肉连汤一起推入锅内，用小火煨5分钟，待汤汁收浓后，下水淀粉勾芡，淋入辣椒油，端锅将肉条翻身，再浇上辣椒油，出锅，入盆，即可。', '金黄明亮，酥烂鲜香。', NULL, NULL, '猪方肉600克，熟火腿丁25克，水发香菇25克，罐头冬笋25克，青豆25克，酱油25克，料酒15克，白糖25克，盐5克，味精5克，香油30克，大葱块15克，鸡汤200毫升，生姜块10克，辣椒油25克，水淀粉20克，大酱卤汁1公斤。');
INSERT INTO `recipe` VALUES (2272, 82, '鸽蛋银耳汤', '1.将银耳用温水泡2小时左右，洗净杂质，削净黄根，加工成小朵，用开水烫焖2－3遍，待软透后，滗净开水，换用清汤泡上。\r\n2.再将豆苗择洗干净，鸽蛋外皮亦用凉水冲洗干净。\r\n3.模子用水洗净，再用净布擦干，在模子内面均匀地抹上薄薄一层鸡油，然后把鸽蛋打入模子里，不要把鸽蛋黄打碎，再摆上两叶洗净的豌豆苗，上笼蒸5分钟左右取出，放入凉开水内，将蒸好的鸽蛋由模子内倒出，用冷水泡上。\r\n4.走汤时，须先将鸽蛋放入清汤内烫透，然后将银耳盛入汤碗，注入清汤，再将鸽蛋放在碗中央。\r\n5.食用时，往汤碗中撒入芝麻，即可。', '鸽蛋鲜嫩，银耳适口，汤清味美。', NULL, NULL, '水发银耳20克，鸽蛋12个，豌豆苗5克，清鸡汤1.5公斤，盐7.5克，糖5克，熟鸡油25克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2273, 82, '狗肉汤', '1.韩国人宰狗方法，因地而异，主要的两种：（1）将狗放血后，整剥皮或煺毛（2）将狗吊起，灌一杯水入口，使狗窒息，再用快刀割开后腿动脉放血，再用热水脱毛。\r\n2.褪毛后，破腹去内脏，用凉水洗净，剁切成6大块，放入冷水中浸泡。再将泡好的带骨肉块，放入开水锅中，旺火烧开，改微火炖熟，撇去浮油，待用。\r\n3.炒锅烧热，倒入豆油，炒至5秒钟，热后，倒入打出的浮油和辣椒面，煸炒均匀出香味后，做成辣椒油盛入碗中，待用。\r\n4.取500克煮熟的狗肉，趁热拆骨，取肉，撕成细丝，调入焙好的芝麻、葱末、胡椒面、酱油，腌渍半个小', '肉香汤美，香辣可口，补气补血。', NULL, NULL, '鲜狗肉500克，豆油250克，酱油250克，精盐50克，味精10克，大蒜末200克，大葱末200克，香菜末200克，辣椒面50克，胡椒面20克，芝麻50克（焙好），鸡蛋500克。');
INSERT INTO `recipe` VALUES (2274, 82, '狗肉粥鲜桃仁', '1.将狗肉剔净筋膜，再剁成细泥茸，放入瓷盘中，加入狗骨汤调开，再加入适量的淀粉搅匀；把核桃仁放入开水中焯透，捞出，剥去皮，再用手掰成碎块。\r\n2.再将炒勺上火，加入鸡汤，烧开后撇去浮沫，下入搅匀的狗肉茸和核桃仁碎块、味精、碱面、料酒，烧开后下入牛奶，调成粥状，淋入姜汁，盛入碗中，即成。', '色泽银白，质地软嫩，味道鲜咸。', NULL, NULL, '狗脯肉200克，核桃仁100克，味精5克，淀粉15克，料酒15克，牛奶50毫升，狗骨汤150毫升，姜汁5克，碱面2克。');
INSERT INTO `recipe` VALUES (2275, 82, '罐焖鸡', '1.将鸡脯肉洗净，切成长2厘米，宽1厘米的块；蘑菇泡好去梗，洗净；猪肉切成长7厘米、宽1厘米、厚5毫米的片，待用。\r\n2.大葱去皮，洗净，切成2厘米长的段；生姜洗净，去皮切成片。\r\n3.炒锅烧热，倒入豆油，烧至八成热时，倒入鸡块，稍炸一下，捞出，控干，余油倒入油罐。\r\n4.炒锅放少许豆油，烧五成热时，放入葱段、姜片，煸炒几下，下25克料酒，捞出葱段和姜片，再投入鸡块、75克料酒，等汤汁干时放盐、酱油、白糖、胡椒面、鸡汤、味精，烧5分钟后，将鸡肉和汤分装在小罐内，放入蘑菇、猪肉。加盖，用小火焖2小时，撒上芝', '呈微红色，口味浓香，鸡肉细腻。', NULL, NULL, '鸡脯肉750克，蘑菇100克，猪肉100克，豆油750克（实耗75克），鸡骨汤750毫升，料酒100克，白糖25克，精盐4克，酱油4克，胡椒面1克，芝麻油10克，大葱25克，生姜25克，味精5克，芝麻面10克。');
INSERT INTO `recipe` VALUES (2276, 82, '辣炒鱿鱼丝', '1.取净鱿鱼块，去膜，洗净，切成6厘米长、2厘米宽的丝，泡入清水中。\r\n2.炒锅旺火烧热，放入25克花生油，烧热，烹入料酒，放盐和150毫升鸡汤，烧开，倒入鱿鱼丝，稍煮一下，捞出，待用。\r\n3.干红辣椒擦净，去蒂根，去籽，切成细丝；大葱去皮，洗净，切碎。\r\n4.净锅烧热，放入余下的75克花生油，旺火烧至五成热时，放入干红辣椒丝、酱油、盐、醋炒几下，倒入余下的鸡汤、鱿鱼，烧开，放入湿淀粉，勾芡，放入葱末，淋上芝麻油，出锅，入盘，即可。', '鱿鱼脆嫩，色白味辣，促进食欲。', NULL, NULL, '鱿鱼300克，醋10克，酱油25克，湿淀粉75克，花生油100克，大葱10克，干红辣椒3克，料酒25克，鸡汤300毫升，芝麻油5克，精盐1.5克。');
INSERT INTO `recipe` VALUES (2277, 82, '辣椒嫩鸡', '1.光笋鸡净膛，洗净，放入清水锅中煮熟，捞出，晾凉，去脊骨和脚，把肉切成3厘米长、1.5厘米宽的条，待用；红干辣椒擦净，去蒂，切碎；大葱要0.6厘米长的小段。\r\n2.炒锅烧热，放入芝麻油，烧至五成热，先放入干辣椒末，大葱末煸炒一下，再放入酱油、精盐、醋、味精、鸡汤50毫升，烧开兑成汁，一半盛入碗内，另一半淋在鸡条上，吃时放在盘中，即可。', '鸡肉鲜嫩，咸香辣鲜，促进食欲。', NULL, NULL, '光笋鸡500克，红干辣椒2个，大葱10克，味精0.5克，芝麻油25克，醋10克，酱油25克，鸡汤50克，精盐1克。');
INSERT INTO `recipe` VALUES (2278, 82, '辣烧鳜鱼', '1.鲜活鳜鱼放入百分之二左右的食盐水里养一个小时左右，去掉泥腥味，洗净，宰杀，去鳞、内脏，洗净；葱姜去皮，洗净，搅捣出葱姜汁，加料酒、2克精盐调匀，抹遍鱼身里外，放入大鱼盘中，腌渍半小时左右。\r\n2.鲜红辣椒，水发松蘑洗净，均切成3厘米长的丝；大蒜去皮，洗净，切片。\r\n3.将腌过的鱼入蒸锅蒸熟，取出，滗出盘中汤汁，用筷子将鱼面上的皮拨在盘边，保温。\r\n4.炒锅烧热，放花生油100克，五成热时放入鲜红辣椒丝、桦蘑丝、葱姜汁煸炒均匀，再放入酱油、醋、白糖合炒。另将100毫升鸡汤、湿淀粉、1克精盐、芝麻油勾兑成', '鱼肉鲜嫩，味浓汁香，咸辣利口。', NULL, NULL, '鲜活鳜鱼1条（约1公斤），鲜红辣椒25克，松蘑25克，鸡汤100毫升，花生油100克，芝麻50克（焙好），白糖50克，大蒜苗25克，芝麻油25克，醋25克，生姜10克，料酒50克，湿淀粉25克，酱油25克，大葱10克，精盐3克。');
INSERT INTO `recipe` VALUES (2279, 82, '辣油鲍鱼', '1.将老母鸡肉先用开水氽透，捞出，洗净血沫，再放入锅内，加入2.5－3公斤清水，上火煮成1.5公斤鸡汤。\r\n2.头道汤煮好，过滤出来之后，再加1.5-2公斤清水继续煮汤(此汤称为毛汤或普通汤)，约煮至剩下1公斤汤时，捞出鸡。将汤过箩，待用。\r\n3.鲍鱼取出，撕去毛边，用刀片成片，一般一个鲍鱼片成3片或4片。\r\n4.锅内放进毛汤，大火烧开后，放入鲍鱼片，氽透后捞出。\r\n5.炒锅净后烧热，放入100克鸡油烧热，下入葱丝、姜丝煸炒出香味，倒入鸡汤，汤烧开后，捞出葱丝、姜丝不要，下入鲍鱼，然后，投入盐、料酒、白糖', '汁浓醇厚，味鲜软嫩，辣香利口。', NULL, NULL, '罐头鲍鱼2桶（约500克重），净母鸡肉1.5公斤，辣椒油25克，盐15克，白糖7.5克，芝麻油10克，水淀粉15克，大葱25克，生姜5克，熟芝麻5克，鸡脯100克。');
INSERT INTO `recipe` VALUES (2280, 82, '桂花兔肉', '1.野兔肉放入清水中浸泡，捞出，控干，切成宽2厘米、厚3毫米、长5厘米的大片，放入瓷碗中，加清水用苏打粉浸泡30分钟，去掉血腥味，再用冷水漂洗两遍，取洁净的纱布包上野兔片，挤去浮水，加料酒、酱油腌渍入味。沾上面粉，即可桂花兔肉生坯。\r\n2.大葱、生姜均切成细丝；鸡蛋磕入碗里，加淀粉打匀；取小碗一只，加鲜汤10毫升、精盐、绍酒、白糖、桂花、味精、芝麻油拌匀，勾兑成咸甜味芡汁。\r\n3.炒锅烧热，加入熟花生油，烧至五成热时，将粘匀面粉的兔肉片逐片拖蛋糊，放油内炸至呈金黄色，倒入漏勺，沥油。\r\n4.原炒锅净后，放', '色泽金黄，兔肉鲜嫩，咸甜酸香。', NULL, NULL, '野兔肉150克，醋5克，熟花生油500克（实耗60克），苏打粉2克，精盐1克，鸡蛋50克，料酒10克，淀粉15克，味精1克，面粉5克，大葱5克，白糖5克，桂花5克，生姜10克，酱油5克，芝麻油15克，鲜汤10毫升。');
INSERT INTO `recipe` VALUES (2281, 82, '鳜鱼丝油菜', '1.油菜心择洗干净，放入开水锅中焯一下，捞出，用凉水冲凉后，泡上。\r\n2.鳜鱼肉洗净，去皮、刺，切成6厘米长，3厘米粗的长丝；大葱去皮，洗净，切末；生姜去皮，切片，待用。\r\n3.瓷碗内，打入鸡蛋清，用干淀粉调成糊，放盐、味精搅拌均匀，放入鱼丝拌匀上蛋糊，放入开水锅中滑熟，捞出，用原水泡上。\r\n4.炒锅烧热，倒入35克芝麻油，烧热，下入葱末、姜片，炒出香味，放入油菜心、盐、料酒、胡椒面、味精、鱼丝、烧开，去浮沫，调好口味，出锅。\r\n5.食用时，将油菜心用筷子夹出，码盘四周，鱼丝放入盘中心，撒上焙好的芝麻即可', '色彩鲜明，鱼肉鲜嫩，清爽可口。', NULL, NULL, '油菜心250克，鳜鱼肉150克，鸡汤500毫升，精盐20克，料酒15克，味精1.5克，胡椒面2克，干淀粉10克，鸡蛋1个，芝麻油35克，大葱10克，芝麻15克（焙好），生姜10克。');
INSERT INTO `recipe` VALUES (2282, 82, '锅烧全鸡', '1.光鸡去内脏，除净，投入开水锅里煮去血沫，捞出，洗净，从脊背处剖开，再放入酱卤里，用微火卤制入味，待鸡酥烂时，沥去汁水后，拆除全骨，注意形状完整。\r\n2.取一只大瓷盆，放入鸡蛋和水菱粉调匀成蛋糊，投入整鸡，周身抹上蛋糊。\r\n3.炸锅烧热，滑锅后，倒入豆油，烧至九成热时，将挂上糊的鸡平整地推入油锅内，炸至金黄色，捞出，控油，余油倒入油罐。\r\n4.炸全鸡捞出控油后，用净布擦去浮油，用刀斩成7厘米长、3厘米宽的长方块，码入盆中，镶上头膀，仍拼成原来鸡状，撒上花椒盐，即可。', '呈老黄色，外脆里酥，秋冬适宜。', NULL, NULL, '嫩光鸡1只（约1.5公斤），鸡蛋1只，大酱卤1公斤，水菱粉100克，豆油1公斤（实耗100克），花椒盐15克。');
INSERT INTO `recipe` VALUES (2283, 82, '锅烧肘子', '1.蹄膀刮去表皮细毛，放入开水锅内煮15分钟，捞出洗净血秽，用刀剖开，再放入炖酱卤汁内，腌渍20分钟，取出放入碗内，加入大葱块、生姜块、炖酱卤适量，上蒸锅，蒸烂取出，滗去汁水，待用。\r\n2.鸡蛋和水淀粉调成浆，放在汤盘内，蹄膀拆骨后，全只挂上蛋浆平摊好。\r\n3.炒锅烧热，放入豆油，待油烧至八成热时，将蹄膀推入油锅内，炸至表皮呈牙黄色时，捞出沥去油，斩成块装盆。\r\n4.食用时，芝麻盐入碟随上，即可。', '呈牙黄色，香酥脆松，肥而不腻。', NULL, NULL, '猪前蹄膀1只（约750克），水淀粉75克，炖酱卤汁1公斤，鸡蛋1只，豆油1公斤（实耗50克），大葱25克，生姜25克，芝麻盐50克。');
INSERT INTO `recipe` VALUES (2284, 82, '锅塌番茄', '1.把西红柿洗净，用开水烫约2分钟后，去皮，切开，去蒂，去籽，然后切成2厘米厚的片，撒上少许精盐，腌渍入味。\r\n2.大葱、生姜去皮，洗净，切成碎末，待用。\r\n3.再把鸡蛋打入碗内，抽打起泡。将炒锅净后，烧热，放入豆油，待油烧至四五成热时，把西红柿两面蘸上面粉，再蘸匀鸡蛋液，逐片下入油锅中，将两面均煎成金黄色。\r\n4.炒锅中，放入葱末、姜末、鸡汤、精盐、白糖盖上锅盖，用旺火将汤烧开，放入煎西红柿片，改小火煨塌片刻，加入料酒、味精，当汤汁收干时，淋入芝麻油，入盘，撒上焙好的芝麻，即可。', '色泽金黄，质地鲜嫩，味道酸甜。', NULL, NULL, '西红柿500克，鸡蛋2个，面粉50克，豆油100克，精盐2克，白糖10克，味精2克，芝麻油5克，大葱10克，生姜5克，料酒15克，鸡汤250毫升，芝麻（焙好）。');
INSERT INTO `recipe` VALUES (2285, 82, '锅塌里脊', '1.猪里脊洗净，剔去筋膜，用刀切成长10厘米、宽7厘米的大片，再用刀剞上排列十字刀口，放入瓷盆内，加姜葱末，料酒、芝麻油10克、盐、味精拌匀，腌渍5分钟后，打入鸡蛋液，拌匀。\r\n2.炒锅烧热，放入少许芝麻油，将里脊片整齐排列在锅内，然后，用小火煎一分钟，再翻身煎一分钟，倒入漏勺中，控油。\r\n3.炒锅烧热，放香油，五成热时，投入葱姜，炒出香味，即放入鸡蛋、味精、盐、料酒，放入煎过的里脊，煨2分钟，放上青蒜段，撒上芝麻面即可。', '色黄鲜艳，清口鲜嫩，咸香适口。', NULL, NULL, '猪里脊300克，鸡蛋2克，葱姜末各5克，料酒15克，盐5克，味精5克，面粉35克，芝麻油80克，鸡汤150毫升，青椒段15克，芝麻面25克（焙好）。');
INSERT INTO `recipe` VALUES (2286, 82, '锅塌明虾', '1.明虾洗净后，剥去盔壳，用刀从虾脊背剖开，剔去沙肠，肚皮处保持连结，再用刀轻轻拍平，排斩十字花型。\r\n2.再将明虾放入盆中，加入葱姜末、料酒、芝麻油、盐、味精搅拌均匀，腌渍5分钟后，两面沾上面粉，放入打散的鸡蛋碗内。\r\n3.炒锅烧热，放入熟鸡油，将沾满蛋糊的明虾放入，整齐排列在锅内，然后，用微火煎塌一分钟，再翻身煎一分钟，倒入漏勺，控油。\r\n4.热锅内，放入熟鸡油，烧热，放余下的葱姜末煸炒出香味，加入鸡汤、味精、盐、料酒，放入明虾煨2分钟，用旺火收汁，放上净青蒜段，浇上芝麻油，出锅，倒入盆中，即可。', '色泽鲜艳，清鲜适口，咸香嫩滑。', NULL, NULL, '对虾6只，鸡蛋2只，葱姜末10克，料酒15克，盐5克，味精5克，面粉35克，芝麻油15克，青蒜段10克，鸡汤150毫升，鸡油60克。');
INSERT INTO `recipe` VALUES (2287, 82, '海参鹌鹑蛋', '1.水发海参去内脏，洗净，用开水焯一下，再用凉水冲，反复二三次，以去腥味，控干，切成条，待用。\r\n2.鹌鹑蛋放入冷水锅中煮开，4分钟后取出，放入冷水中浸泡后，去皮，待用。\r\n3.炒锅烧热，倒入200毫升鸡汤，加上10克料酒、4克精盐、味精，汤沸后下入海参，煮二三分钟后捞出。\r\n4.净锅烧热，倒入花生油，烧热后下入葱、姜，煸炒几下，捞出葱、姜，烹入酱油、15克料酒、糖、6克盐、海参、鹌鹑蛋和400毫升鸡汤，用小火焖到汤汁稠浓时，用淀粉勾芡，淋上芝麻油装盘，撒上芝麻，即可。', '色彩鲜明，汁浓味香，风味独特。', NULL, NULL, '水发海参500克，鹌鹑蛋20个，鸡汤600毫升，精盐10克，花生油100克，酱油50克，料酒25克，白糖10克，大葱20克，生姜10克，芝麻油20克，淀粉40克，芝麻10克（焙好），味精3克。');
INSERT INTO `recipe` VALUES (2288, 82, '罐子狗肉', '1.先把带皮狗肉洗净，放入清水中浸泡4小时。\r\n2.再把带皮狗肉捞出，冲洗干净，切成两块，每块约750克左右，放入锅内，加入凉水，烧到断生时，捞出，用清水洗净。\r\n3.大葱去皮，切成3厘米长的段；生姜去皮，取15克切片、10克剁碎末；干辣椒洗净，去蒂，切丝，待用。\r\n4.将狗肉块放入锅内，加清水，水要淹没肉块，加入葱段、姜片、黄酒，盖上盖，用旺火烧开，再改用小火焖至八成熟，捞出，拆骨，再将骨头放回汤中继续熬。\r\n5.把狗肉控干，切长2厘米，宽5厘米，厚0.5厘米的片。\r\n6.取植物油倒入炒锅中烧热，下狗肉', '口味鲜香，狗肉嫩软，咸香微辣。', NULL, NULL, '带皮狗肉1500克，大葱25克，大蒜瓣25克，干辣椒1个，植物油15克，黄酒100克，胡椒粉6克，芝麻20克（焙好），精盐10克，味精2克，生姜25克。');
INSERT INTO `recipe` VALUES (2289, 82, '辣炒猪肉丝', '1.将猪肉洗净，剔净筋膜，切成3厘米长、宽5毫米、厚5毫米的丝，放入清水中，浸泡，捞出，控干。干辣椒擦净，切长丝。\r\n2.大葱去皮，洗净，切长丝；生姜去皮洗净，切细丝；大蒜去皮，洗净，切短丝，待用。\r\n3.炒锅烧热，下入豆油，烧四成热时，放入辣椒丝，待炸至变色时，取出。\r\n4.再将肉丝用盐和胡椒面拌匀，放入油锅中，煸到肉丝呈深黄色时，加入干辣椒丝、生姜丝、大蒜丝，稍煸，加酱油，放葱丝，淋上芝麻油，放味精，炒匀，即可。', '味道浓郁，肉香鲜嫩，香辣爽口。', NULL, NULL, '猪腿瘦肉300克，豆油75克，干辣椒3个，精盐20克，酱油15克，大葱20克，生姜10克，大蒜10克，料酒15克，芝麻油15克，味精1克，胡椒面1克。');
INSERT INTO `recipe` VALUES (2290, 82, '辣肉烧驼掌', '1.将老母鸡块、肥鸭块洗净加入大葱段、生姜块加清水，用小火煮出750毫升鸡鸭汤。\r\n2.再将发好的驼掌剞成方块花联刀，然后斜切成长条块，用开水氽一次，再用250毫升鸡鸭汤氽一次。\r\n3.把猪五花肉（带皮）用酱油、辣椒酱、料酒、盐、葱末、姜末腌渍一下，腌前先用铁叉子在肉上多叉几下，使其容易入味。然后挂在通风处晾3－4个小时，再上烤炉吊烤1个小时左右，取出后，切成5毫米厚、3厘米宽、4厘米长的方片，放入炒锅煸一下，使肉片两面颜色均匀，然后再加少许鸡鸭汤，上蒸锅蒸30分钟左右。\r\n4.将余下的500毫升鸡鸭汤放', '浓香软烂，美味可口，浓辣糯香。', NULL, NULL, '水发驼掌300克，猪五花肉500克，老母鸡250克，肥鸭250克，白糖10克，盐10克，料酒15克，淀粉5克，葱25克，姜25克，酱油10克，芝麻10克（焙好），辣椒酱10克 。');
INSERT INTO `recipe` VALUES (2291, 82, '辣子牛肉丁', '1.将牛肉洗净，剔净筋膜，切成1厘米见方的丁，用精盐、黑胡椒面腌渍入味。\r\n2.黄瓜洗净，去蒂，去籽，也切成与牛肉丁大小的方丁；大葱去皮，洗净，切成肉丁大的节；生姜去皮，洗净，切成方片；大蒜去皮，洗净，切片。\r\n3.再将腌渍入味的牛肉丁，放入碗内，加酱油、淀粉浆匀后，再加一些油搅拌一下，待用。\r\n4.小瓷碗内，放入牛肉汤、淀粉、酱油、白糖、味精、葱节、姜片、大蒜片勾兑成粉芡汁，待用。\r\n5.炒锅烧热，放入花生油，待烧至五成热时，下入腌渍入味的牛肉丁，翻炒煎匀后，待肉丁散开时，下入辣椒酱，炒出香味后，烹入黄', '味道香辣，牛肉细嫩，爽口开胃。', NULL, NULL, '牛肉400克，鲜黄瓜150克，花生油50克，精盐10克，淀粉20克，酱油40克，白糖15克，味精15克，大葱30克，生姜20克，大蒜10克，辣椒酱10克，牛肉汤250毫升，黑胡椒面2.5克，黄酒15克。');
INSERT INTO `recipe` VALUES (2292, 82, '熘鹌鹑', '1.将净鹌鹑洗净，剁成3厘米长，1.5厘米宽的块；玉兰片、生菜、水发香菇均切成片。鹌鹑块用湿淀粉抓匀，待用。\r\n2.用酱油、料酒、花椒水、香醋、味精、鸡汤、湿淀粉勾兑成调味芡汁。\r\n3.炒锅烧热，倒入豆油，烧至六成热时，将鹌鹑逐块投入锅中，炸成金黄色时，捞出，沥油。\r\n4.炒锅留底油30克，烧热，投入葱末、姜末炒香，放入玉兰片、香菇、生菜煸炒几下，倒入炸好的鹌鹑块，烹入兑好的调味芡汁，淋入芝麻油，撒匀焙好的芝麻，即可。', '色泽金黄，外脆里嫩，味咸鲜香。', NULL, NULL, '净鹌鹑200克，生菜15克，水发玉兰片10克，水发香菇5克，湿淀粉75克，花椒水5克，鸡汤25毫升，生姜末5克，芝麻油10克，大葱末10克，味精1克，料酒5克，香醋1克，酱油15克，豆油750克（实耗60克），芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2293, 82, '蘑菇炖鸡', '1.鸡脯肉去皮，剔净筋膜，去骨，放入清水中浸泡后，洗净，控干，剁成泥茸。\r\n2.取个儿均匀的鲜蘑菇，用净湿毛巾擦干水，切下顶盖完整无损，蘑菇蒂留下另用。\r\n3.去皮生虾去掉虾肠，虾肉剁碎末。\r\n4.取瓷盆，放入鸡泥茸，虾碎末，加大葱、生姜末、精盐、酱油、玉米粉、竹笋末搅拌均匀，入味。\r\n5.鲜蘑菇净后，放入入味的馅料，再用手稍微按紧，沾少许玉米粉，待用。\r\n6.铁盘放在火上，放入芝麻油，把入馅的鲜蘑菇放入，煎20分钟，至熟后，铲出，放入盘中，再用洗净、消毒的胡椒叶放入盘边装饰，即可。', '鸡虾质嫩，蘑菇香鲜，味美可口。', NULL, NULL, '鸡脯肉700克，鲜蘑菇250克，去皮生虾125克，罐头竹笋50克，大葱15克，玉米粉25克，酱油15克，精盐5克，生姜2.5克，胡椒叶3克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2294, 82, '茉莉银耳汤', '1.将银耳用温水泡2小时左右，去净黄根，洗去杂质，用开水烫一下，并用盖盖上，焖10分钟左右，滗去水，再倒入开水烫焖10分钟，挤干水分，用清鸡汤泡上，上笼蒸8分钟左右，取出，待用。\r\n2.再将鸡脯肉砸成泥，用水泡开，抓匀；鸡汤撇去油沫，大火烧开后，加入调料，将鸡泥下锅，改用中火烧，同时用手勺用中速，将鸡泥搅匀，待鸡泥将汤中杂物吸净，汤清见底时，停止搅动，待白沫泛上来时，将鸡泥捞出，此时，汤中浮油俱随鸡泥而出，汤清见底，用微火保持汤的温度。\r\n3.把茉莉花择洗干净，放入汤碗内，浇入清汤，盖盖焖一会，待茉莉花香', '汤味清鲜，茉莉芳香，银耳润滑。', NULL, NULL, '水发银耳20克，茉莉花15朵，鸡脯肉150克，清鸡汤1.5公斤，盐7.5克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2295, 82, '桂花狗肉', '1.将狗上脑肉放入清水中浸泡，洗净，置于砧墩上，片成2厘米厚的大片，然后，剞上十字刀，剞后切成5厘米宽、7厘米长的片，放入瓷盆内，加入料酒、精盐、味精拌匀入味，再加入鸡蛋液和清水100毫升，放入精白面粉，搅拌均匀，待用。\r\n2.炒锅上火烧热，加入植物油，烧至五成热时，一片一片地放入肉片，炸至嫩黄色时，倒入漏勺内，控干油，锅内留15克余油，放入葱末煸出香味，倒入桂花肉，加入香油，急翻几个身，出锅，入盘。\r\n3.食用时，随跟芝麻盐一碟，即可。', '色泽金黄，脆香松散，金黄似桂花。', NULL, NULL, '狗上脑200克，鸡蛋液100克，精白面粉150克，料酒10克，精盐2克，味精2克，植物油1.5公斤，葱末5克，香油10克，芝麻盐15克。');
INSERT INTO `recipe` VALUES (2296, 82, '烂鸡海参', '1.将生鸡腿、鸡脯肉洗净，去筋膜，放置于大瓷碗中，加入10克料酒、葱段、姜片、100毫升清水，上蒸锅用旺火蒸烂。\r\n2.再将海参剖开，去内脏，洗去内膜，再洗净，置于砧墩上，切成三片，用直刀法在内面剞过；罐头竹笋切成片，待用。\r\n3.炒锅上火，加入清水，烧开，下入海参氽过，用清水漂净，控干水。\r\n4.净锅烧热后，放入豆油，烧至六成热时，放入海参，逐片过油，除去腥味，随即倒入漏勺内，控干油。倒出余油。\r\n5.烧锅再上火，加入10克料酒、酱油、白糖、精盐、味精和白汤，放入蒸烂的鸡腿肉、鸡脯肉、海参、竹笋片、冬菇', '柔软酥糖烂，味鲜而浓，富于营养。', NULL, NULL, '水发海参200克，生鸡腿肉100克，罐头竹笋25克，鸡脯肉100克，水发冬菇25克，鸡汤100毫升，料酒20克，酱油个5克，精盐1克，白糖1.5克，味精1克，豆油750克（实耗75克），水淀粉25克，葱段15克，姜片1片，芝麻油15克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2297, 82, '鳓鱼冻', '1.将鲜鳓鱼去头、鳞、鳍和内脏，洗净，由腹部取出膜，再由脊背下刀，片下鱼肉，去掉小刺，切成长3厘米、宽1.5厘米的片。\r\n2.猪五花肉去皮，切大块，洗净。\r\n3.把大葱、生姜去皮，洗净，拍松；花椒、大料用净纱布缝成小袋。\r\n4.再把鳓鱼片放入瓷盆内，入蒸锅，用旺火蒸熟，取出分装在小碟里，待用。\r\n5.把猪五花肉块放入锅中，加清水1200毫升，再放料酒、味精、葱、姜、盐、糖及花椒大料包，用大火烧开，再用温火炖烂，捞出猪肉、葱、姜和大料包。猪肉块做它用。\r\n6.用少许水与蛋清一起搅拌均匀，再搅入猪肉汤中，烧开', '色泽洁白，味道清鲜，鳓鱼肉嫩。', NULL, NULL, '鲜鳓鱼1000克，猪五花肉300克，料酒50克，大葱10克，精盐10克，生姜5克，味精1.5克，白糖5克，大料5克，花椒2克，辣椒酱3克，鸡蛋清1个。');
INSERT INTO `recipe` VALUES (2298, 82, '栗子焖鸡', '1.将鸡净膛后，洗净，去头爪，剁成长3厘米、宽2厘米的块，放碗内，加少许酱油、料酒搅拌均匀，腌渍入味。\r\n2.栗子洗净，用开水煮片刻，捞出，去皮。\r\n3.炒锅放豆油，烧至七成热时，下入鸡炸一会儿，用漏勺捞起，控干油。余油倒出。\r\n4.炒锅留少许油，烧热，放入姜末煸炒，再下入鸡块，烹入料酒、加酱油、白糖炒匀，加入鸡汤，用旺火煮开，放入栗子肉同焖，焖至栗子和鸡肉块酥烂时，投入味精、熟芝麻、葱段、姜丝炒匀，用湿淀粉勾芡后，出锅入盘，淋上芝麻油，即可。', '黄亮油润，鸡酥栗糯，汁浓味香。', NULL, NULL, '鸡1.5公斤，栗子400克，鸡汤500毫升，酱油200克，白糖30克，味精2克，料酒50克，豆油600克（实耗75克），芝麻油20克，大葱段20克，生姜丝10克，淀粉40克，熟芝麻25克。');
INSERT INTO `recipe` VALUES (2299, 82, '海带冷菜汤', '1.把牛肉去筋膜，洗净，切成长6厘米、宽3厘米的丝，放入牛肉渍汁中腌渍入味，搅拌均匀，待用。\r\n2.取鲜海带中的中间部分，若没有，亦可用水发海带，水发后洗净，控干，切成6厘米长、3厘米宽的丝。\r\n3.净清水烧开，倒入盆中，晾凉，待用。\r\n4.炒锅旺火烧热，放入豆油，五成热时，放入牛肉条煸炒，再放入海带条翻炒均匀，炒熟透，出锅，晾凉。\r\n5.把淡口酱油倒入凉开水中，分装入4个小瓷碗中，加味精，然后，再分别盛匀炒熟晾凉的海带条、牛肉条，拌匀。\r\n6.食用前倒入适量醋，有条件的加洁净冰块，即可。', '海带嫩鲜，牛肉冷香，清凉爽口。', NULL, NULL, '鲜海带100克，豆油25克，牛肉75克，酱油25克，醋5克，味精2克，清水500毫升，豆油25克，酱油10克，芝麻油5克，大蒜茸5克，大葱丝10克，白糖10克，芝麻盐7克，冰块300克。');
INSERT INTO `recipe` VALUES (2300, 82, '海米炒蕨菜', '蕨菜学名蕨，又叫蕨菜芽、蕨根、如意菜、猫爪子菜、龙头菜、拳头菜、鸡爪爬、乌糯等，在亚洲分布很广。为多年生草本植物，株高约30厘米，喜欢生长在山地、原野，春天呈猫爪状，牙叶尖端呈卷曲状，是一种比较名贵、较有很高营养价值的医用野蔬，烹制菜品则质地鲜美、脆嫩爽口、清香柔滑，色香味营养俱佳。\r\n1.将蕨菜择去老茎，用开水焯过，切成6厘米长的段。\r\n2.海米水发后，洗净，放入瓷碗内，加料酒及清水上蒸锅蒸软。\r\n3.生姜去皮，洗净，切成细末，待用。\r\n4.炒锅烧热放入豆油，烧五成热时，投入姜末、辣椒面炸出香味，下入蕨', '鲜嫩柔滑，清香溢口，咸辣甜香。', NULL, NULL, '蕨菜400克，水发海米50克，精盐2克，料酒15克，白糖20克，豉克，味精2克，生姜10克，水淀粉10克，鸡汤50毫升，辣椒面5克，酱油10克，芝麻油5克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2301, 82, '韩国白煮肉', '将去骨猪肉横切成三、四条，再切成长块，刮洗干净，皮朝上放入锅内，倒入清水，盖上锅盖，在旺火上烧开，再转微火煮2小时，用筷子一穿即入，捞出晾凉，撕去肉皮，切成薄片，整齐地排在盘内；把酱油、蒜泥、韭菜花、酱豆腐汁和辣椒油等，一起放在小碗内，随同肉片一起上桌。', '肉质香烂、肥而不腻、味道醇厚、最宜卷着荷叶饼或烧饼食用。', NULL, NULL, '去骨猪五花肉、酱油、蒜泥、腌韭菜花、酱豆腐汁、辣椒油。');
INSERT INTO `recipe` VALUES (2302, 82, '旱蒸南瓜', '1.将南瓜洗净，去蒂，去皮，去籽，切成12厘米长、5厘米宽、4毫米厚的片条，用盐稍腌渍，滚上米粉、淀粉，放入豆油锅中煎炸呈黄色，捞出，放入蒸碗中加泡菜汁，封上油纸，放入蒸锅中，隔水蒸熟，取出，待用。\r\n2.青蒜苗摘洗干净，切成马耳形，姜蒜切成指甲片，待用。\r\n3.炒锅放豆油，烧热，放入辣大酱，稍炒，再下姜蒜片、白糖、味精、辣椒面以及蒸好的南瓜片，急速翻炒，出锅，入盘，淋入芝麻油，即可。', '香辣微甜，咸酸适口，易于消化。', NULL, NULL, '南瓜500克，米粉50克，淀粉50克，生姜5克，大蒜5克，辣大酱20克，白糖10克，辣椒面5克，青蒜苗25克，味精2克，豆油100克，油纸1张，精盐3克，芝麻油5克，泡菜汁5克。');
INSERT INTO `recipe` VALUES (2303, 82, '核桃酪', '1.将核桃仁用开水泡好，剥去皮，切碎；小枣去皮和核，大米洗净，一起加入500毫升清水磨成浆，越细越好。\r\n2.再将磨好的浆再加500克水，用细箩过一下，去掉渣子，把汁倒入锅中，用旺火烧开后，加入白糖，再开后，去掉沫，分别装在12个小碗里，即可。', '口味香甜，核桃郁浓，富于营养。', NULL, NULL, '核桃仁300克，大米100克，白糖300克，小枣100克，清水1公斤。');
INSERT INTO `recipe` VALUES (2304, 82, '荷叶鸭子', '1.光鸭净膛，去内脏，洗净控干后，从鸭颈处开刀拆去骨叉，切成8厘米长、3厘米宽抹刀块12块，放入瓷盆内，用糖色、料酒、盐、酱油、味精、糖、葱姜丝、辣大酱腌5分钟，加入米粉拌和，再加入麻油、鸡油搅拌均匀。\r\n2.鲜荷叶放入有碱的开水锅里，烫一下，捞出，浸在冷水中洗净后，切成14厘米长、7厘米宽的长方块，顺序将鸭块包好，整齐地扣在屉内，上蒸锅，用微火蒸一个半小时，待鸭块酥烂后，取出，倒入盆内，即可。', '鸭子醇香，有荷叶味，肥而不腻。', NULL, NULL, '光鸭1只（约2公斤），荷叶5张，酱油15克，糖色10克，黄酒15克，糖25克，盐6克，大葱15克，生姜10克，味精5克，芝麻油25克，鸡油25克，米粉150克，碱水3克，辣大酱15克。');
INSERT INTO `recipe` VALUES (2305, 82, '红炖狗肉', '1.将生鲜狗肉切成中块，浸泡在清水中4小时，捞出，洗净，控干水分。\r\n2.煮锅放入清水，投入狗肉块，烧开，去浮沫，煮熟透，捞出，放入清水中浸泡干净，控水，待用。\r\n3.大葱去皮，洗净，切成段；生姜去皮，洗净，切块；干辣椒去蒂，洗净切段；大蒜去皮，洗净，切片，待用。\r\n4.炒锅烧热，放入香油烧五成热时，下入大料、葱段、姜块、大蒜片、辣椒段、桂皮炒出香味，加入大酱、料酒、酱油、狗肉块及适量清水，大火烧开后，改用小火煮至八成熟，捡去大料、葱段、姜块、蒜片、辣椒段，再把狗肉捞出，放入瓷盆内，加入红汁，狗骨清汤、味', '狗肉软烂，味道鲜美，色泽明亮。', NULL, NULL, '狗肉500克，香油50克，大葱50克，生姜25克，大蒜50克，味精5克，料酒50克，淀粉15克，红汁50克，大料5克，白糖25克，桂皮5克，狗骨清汤250毫升，干辣椒2个，大酱25克（炖酱），酱油50克，精盐20克。');
INSERT INTO `recipe` VALUES (2306, 82, '红炖牛尾', '1.将生牛尾按骨节剁成段，用清水浸泡4小时。\r\n2.再将生牛尾捞出，控干，放入开水锅里煮透，捞出，去净毛，再用清水泡洗干净，控去水。\r\n3.炒锅上火，加入香油烧热，下入大料、葱段、姜块、大蒜，煸炒出香味，加入炖酱、料酒、酱油、牛尾段及适量的清水，旺火烧开后，改用小火煮至八成熟，捡去大料、葱段、姜块、大蒜，再把牛尾捞出，放入盆内，加入渍汁（用葱姜末、芝麻油、酱油、芝麻面、黑胡椒面、大蒜泥、精盐混合调成的汁）、鸡汤、味精、料酒，上屉蒸至软烂，取出，倒入汤勺中，上火烧开，收浓汁，用淀粉勾通芡，淋入芝麻油，撒上芝', '牛尾软烂，味道鲜美，银红明亮。', NULL, NULL, '生牛尾3公斤，大料50克，芝麻油100克，大葱段125克，生姜25克，大蒜50克，味精5克，料酒100克，淀粉75克，炖酱50克，渍汁200克，鸡汤500毫升，酱油75克，精盐25克，芝麻面50克（焙好），黑胡椒面5克。');
INSERT INTO `recipe` VALUES (2307, 82, '红花焖鹌鹑', '1.将鹌鹑剁去脚爪，从脊背处开膛，去内脏，洗净，入沸水锅中焯烫一下，捞出，沥干水分，抹上酱油30克，晾干。\r\n2.干辣椒擦净，去蒂柄，切成小段；罐头竹笋切成滚刀块，投入沸水内氽下，捞出；香菇去蒂，洗净；姜块、葱段洗净拍松；黄瓜洗净，切成水草花形，泡在清水中，待用。\r\n3.炒锅烧热，倒入豆油，烧至五成热时，将鹌鹑投入油锅中，炸呈至金黄色时，捞出，沥油，倒出余油。\r\n4.净锅，烧热，放入熟鸡油，化开烧热，放入辣椒段、葱段、姜块炸出香味，倒入鸡汤，烧开，捞出葱段、姜块、辣椒段，再下入藏红花、精盐、绍酒、味精、白', '色泽红亮，鹌鹑鲜嫩，咸鲜辣香。', NULL, NULL, '净鹌鹑12只，黄瓜100克，藏红花10克，干辣椒3个，豆油500克（实耗125克），料酒25克，精盐10克，罐头竹笋50克，味精1.5克，水发香菇25克，白糖5克，熟鸡油50克，酱油30克，湿淀粉25克，大葱段50克，鸡汤750毫升，姜块30克，芝麻面15克。');
INSERT INTO `recipe` VALUES (2308, 82, '红烧干贝', '1.罐头竹笋和蘑菇均切成小方丁。大葱、大蒜、生姜去皮，洗净，均切成碎末。\r\n2.炒锅烧热，放入花生油15克，烧至八成热时，把净干贝肉投入油中，滑动一下，立即倒出。\r\n3.再将余下的20克花生油倒入锅内，烧热时，放入葱、姜、蒜末煸炒出香味，加味精，翻炒均匀，倒入鸡汤，放入贝肉，汤烧开后，放入竹笋、蘑菇和青豆，炒匀，加入料酒、酱油、白糖，加入精盐，用水淀粉勾芡，即可。', '味道鲜美，贝肉嫩滑，易于吸收。', NULL, NULL, '干贝肉250克，罐头竹笋15克，蘑菇15克，青豆25克，花生油35克，料酒15克，酱油15克，淀粉25克，味精1.5克，鸡汤50毫升，大葱15克，生姜10克，大蒜10克，白糖10克。');
INSERT INTO `recipe` VALUES (2309, 82, '红烧牛肉狮子头', '1.先将牛肉洗净，去筋膜，用刀剁成泥茸；发糕切成米粒大小的丁；松蘑水发后洗净，去蒂，切成小丁；水发虾米亦剁碎末，待用。\r\n2.取一瓷盆，放入牛肉泥茸、发糕丁、松蘑丁和海米末，打两个鸡蛋（100克），再放入25克湿淀粉、50克面粉、1.5克盐、10克酱油，搅拌均匀，做成4个扁形的肉饼。\r\n3.炸锅烧热，放入豆油，待烧至八成热时，下入牛肉饼，炸到两成呈金黄色时出锅，用钵子扣好，倒出炸油。\r\n4.牛肉钵里放入100毫升牛肉汤料，加酱油10克，上笼蒸1小时。\r\n5.炒锅烧热，放入少许豆油、精盐，将菜心焯熟，装入盘', '色泽金黄，味香肉烂，开如狮头。', NULL, NULL, '牛肉500克，湿淀粉50克，味精0.5克，发糕100克，豆油1000克（实耗75克），菜心8个，酱油20克，虾米10克，松蘑25克，芝麻油1克，鸡蛋100克，面粉50克，辣椒面0.5克，精盐2克。');
INSERT INTO `recipe` VALUES (2310, 82, '红烧牛头', '1.先把牛角锯去，整个放在火上烧焦皮面，用刀刮去焦面，看是否烧到毛根，再继续烧刮，至烧尽毛根为止（烧时要先烧顶部，尤其要注意，不要把皮烧裂）。烧刮后放在水内泡软，再刮尽焦面，整个放在锅内煮到能去骨（拆时要保持牛头皮肉整齐，不可弄碎），拆骨后放在水内再清刮一遍，修去边沿不洁和碎料部位。\r\n2.牛舌剥去表皮，把头肉和舌部都切成长6厘米、宽3厘米的长方形，用开水加葱、姜、料酒，将牛头和牛舌煮两三遍，每煮一遍都用凉水冲洗，然后用凉水泡上。\r\n3.火腿用热碱水刷洗干净，改成块；干贝洗净泥沙；鲜蘑菇洗净，去蒂；油菜心', '色泽红亮，汁浓味厚，质糯不粘。', NULL, NULL, '牛头1个，母鸡1只，干贝50克，火腿500克，鲜蘑菇50克，油菜心500克，大葱50克，生姜25克，精盐30克，糖色25克，料酒25克，芝麻油25克，豆油50克，味精5克。');
INSERT INTO `recipe` VALUES (2311, 82, '红烧裙边', '1.新鲜鼋裙边改刀切成5厘米长的长方块，因常用的鼋鱼裙边都不宽，帮就原来的宽切成段，待用。\r\n2.将裙边用开水氽透，捞出，用温水洗净；火腿切成长方块；油菜心去筋去皮，用水洗净，大葱切段，生姜切厚片。\r\n3.油菜心用开水氽透，捞出，用凉水漂凉，捞出，控净水，切去叶的一部分，一般保留8－9厘米长的段。\r\n4.锅内加入鸡汤1公斤，裙边控净水，放入锅内，火腿片放在上面，再下入葱、姜、料酒、酱油、盐、白糖、胡椒面，先用大火烧开，然后用小火煨。待裙边烂后，挑出葱、姜，兑好口味，用水淀粉勾薄芡，浇上鸡油，盛入盘内。\r\n', '软滑味，汤浓味鲜，裙边嫩糯。', NULL, NULL, '鼋鱼裙边750克，将火腿50克，油菜心10棵，鸡汤1.5公斤，盐10克，白糖10克，酱油25克，胡椒面3克，鸡油15克，芝麻油15克，大葱15克，生姜10克，水淀粉10克，料酒15克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2312, 82, '栗子烧猪肉', '1.将猪肉洗净，切成1厘米见方的丁，将栗子稍煮一下，剥壳，去皮，洗净，待用。\r\n2.炒锅烧热放入豆油，烧五成热时，放入肉丁炒几下，加入盐、料酒炒匀，再加适量清水，用中火焖至五六成熟时，放入栗子、酱油和匀，再用小火焖，至快酥烂时，放入葱段、味精、炒匀，出锅入盆，即可。', '色泽红亮，肉香栗糯，咸香微甜。', NULL, NULL, '五花猪肉500克，栗子300克，料酒50克，酱油50克，精盐10克，白糖15克，葱段10克，味精2克，豆油25克。');
INSERT INTO `recipe` VALUES (2313, 82, '莲蓬鲜贝', '1.鲜贝去骨鳃，洗净，控净水，剁成泥茸状，加毛姜水，调成稠糊状，搅拌均匀上劲，加入盐、味精、料酒、3只蛋清调匀，加白鸡油及干淀粉，拌匀。\r\n2.将制好的鲜贝泥五分之四加虾脑搅拌均匀，放入瓷调羹中。\r\n3.余下的五分之一放入小瓷碗中，按中间稍高，四周略低，抹匀，里边用净青豆按成莲子形，跟瓷调羹里的一起入蒸锅，蒸7分钟即可取出。\r\n4.取出后，调羹里的摆放四周，形似叶片，莲子放中间，一大朵整个莲蓬就制好了。\r\n5.炒勺内放鸡汤、毛姜水、盐，烧开，放入湿淀粉，勾薄芡，浇在莲蓬上。\r\n6.食用时，沾辣酱油同吃，即', '咸鲜辣香，造型喜人，诱人食欲。', NULL, NULL, '鲜贝400克，白鸡油40克，料酒15克，盐5克，味精3克，淀粉15克，鸡蛋清3只，青豆50克，虾脑100克，鸡汤500毫升，辣酱油50克');
INSERT INTO `recipe` VALUES (2314, 82, '凉拌三丝', '1.海带洗净，切成细丝，放入开水锅中煮2分钟，捞出，用凉水焯一下，控干。\r\n2.把青椒和红椒洗净，去蒂根，去籽，切成丝，分别入开水锅中焯一下，捞出，用凉水冲一下，控干。\r\n3.取洁净瓷盆，将海带丝、青椒丝、红椒丝，放盐稍微腌一下，再加酱油、醋、糖、姜末、辣椒粉和芝麻油，搅拌均匀，白糖化开后，出锅，晾凉。\r\n4.熟芹菜段放入净瓷盆中，倒入酸辣汁，腌渍40分钟，入盘，撒入芝麻油，即可。', '甜酸咸辣，开胃醒酒，促进消化。', NULL, NULL, '水发海带200克，青椒50克，精盐1.5克，红椒50克，酱油40克，醋25克，白糖25克，姜末20克，芝麻15克（焙好），辣椒粉25克。');
INSERT INTO `recipe` VALUES (2315, 82, '溜鲈鱼', '1.将鲈鱼去鳞和内脏，洗净，去头和脊骨，在鱼身两侧斜剞数刀，放在盘里淋入料酒，放入姜片腌渍入味。\r\n2.再将瘦猪肉去筋膜，洗净；罐头竹笋、水发松蘑摘洗干净，均切成小丁。\r\n3.把腌渍入味的鲈鱼，放上葱段，用旺火蒸5分钟，取出葱段、姜片，滗去汤汁。\r\n4.炒锅烧热，放入豆油，烧热后稍凉，放入猪肉丁、竹笋丁、松蘑丁稍煸炒，加入酱油、糖、鸡汤煮开，用水淀粉勾芡，加醋拌匀。\r\n5.另取一炒勺放火上烧热，放入50克豆油，烧至八成热时，将前一炒锅中的肉菜芡汁倒入炒勺内，急翻几下，出锅，烧在蒸熟的鲈鱼上，撒上胡椒面，即', '味道醇香，鱼肉鲜嫩，配料可口。', NULL, NULL, '鲈鱼1条（约1000克），瘦猪肉50克，罐头竹笋50克，松蘑50克，大葱50克，鲜姜25克，胡椒粉1克，鸡汤100毫升，料酒25克，酱油25克，白糖35克，醋35克，湿淀粉15克，豆油75克。');
INSERT INTO `recipe` VALUES (2316, 82, '红烧兔肉', '1.将兔肉切成4厘米见方的中块，入盆，加清水浸泡半个小时，去其草腥和红浆，用开水焯烫一下，洗净，控干水，入碗，加入酱油，精盐、料酒，拌匀，腌渍入味。\r\n2.罐头竹笋切成菱角块；松蘑水发好，洗净泥沙，去蒂根，切成片；油菜摘洗干净，切成象眼块；生姜去皮，切片拍松；葱去皮，洗净，切段，待用。\r\n3.炒锅烧热，放入花生油，烧四成热，将腌渍好的兔肉分三次投入油锅中，炸呈红色，捞出，原锅油倒出。\r\n4.炒锅留少许底油，入花椒、大料、葱段、姜片略煸，倒入炒好的兔肉块，加大酱、料酒、白糖、精盐、酱油、香醋，放鸡汤，烧开后', '色泽酱红，味香肉烂，咸甜酸辣。', NULL, NULL, '野兔肉500克，罐头竹笋50克，松蘑25克，淀粉5克，油共50克，鸡汤750毫升，精盐2克，酱油15克，香醋25克，鲜姜15克，辣椒面0.5克，白糖15克，大葱15克，花椒5颗，料酒15克，花生油500克（实耗60克），大料5克，味精1.5克，芝麻油3克，大酱25克。');
INSERT INTO `recipe` VALUES (2317, 82, '红烧驼峰', '1.将驼峰切成4厘米厚块，用温水下锅，大火烧3小时，换水再煮2次，然后，再换凉水泡2个小时。\r\n2.再将老母鸡块洗净，放葱段、姜块用小火煮成500毫升汤，过滤成鸡清汤。\r\n3.把煮好的驼峰切成5毫米厚、2厘米宽、2厘米长的块，用250毫升鸡清汤，煮滚沸一次。\r\n4.将罐头竹笋、水发冬菇用开水氽过，捞出，控干，然后和驼峰一起，放入汤锅中，加入250毫升鸡清汤煨10分钟，加白糖、盐、料酒、味精、酱油、辣椒油、葱丝、姜末，再放入水淀粉勾兑成浓汁，淋入熟鸡油，出锅。', '丰腴肥美，汁浓味厚，富于营养。', NULL, NULL, '驼峰750克，老母鸡500克，罐头竹笋50克，水发冬菇50克，白糖10克，盐10克，料酒15克，味精3克，将油10克，辣椒油10克，葱5克，姜5克，淀粉15克，熟鸡油5克。');
INSERT INTO `recipe` VALUES (2318, 82, '红烧熊掌', '1.将鲜熊掌用凉水下锅，火煮沸开，小火2小时，取出，拔净毛，去掉其指甲和脚垫（注意不要弄破皮），然后，再用温水下锅，大火煮沸，小火煨3小时，取出用凉水泡洗4次，直到去尽腥膻味为止。\r\n2.再将加工好的熊掌用开水氽一次，然后放入搪瓷盆内，上面放鸡、鸭、葱、姜、火腿、干贝。\r\n3.把干松蘑丁洗净，用150毫升水泡10分钟，取出松蘑丁，汁亦留用。\r\n4.在双耳锅内垫上竹箅，放入蒸好的熊掌及汤和松蘑丁及其汁，用大火煮沸，小火煨，再加入调料，成浓汤。取出熊掌放入圆盘内，其汁加水淀粉搅拌均匀成棕红色，出锅前淋上少许芝', '味道浓厚，掌糯郁美，汤鲜利口。', NULL, NULL, '鲜熊掌1对（约1.5公斤），老母鸡1000克，填鸭1000克，干贝25克，松蘑丁25克，糖10克，盐10克，料酒15克，葱15克，酱油25克，姜5克，水淀粉15克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2319, 82, '红烧猪肉丸子', '1.猪肉洗净，去筋，剁成茸泥。\r\n2.将大葱去皮，洗净，分成两份；一份切成长3厘米的段；另一份切成大葱末。\r\n3.小白菜心洗净，切长丝。\r\n4.猪肉茸放入碗内，加入大葱末、生姜末搅拌均匀，再加入鸡蛋液、盐、5克酱油、料酒等拌匀，再将拌匀的猪肉茸挤成小丸子。\r\n5.将豆油放入炒锅内，烧至八成热时，将拌好的肉泥挤成的小丸子，放入油锅中炸，待丸子呈金黄色时，捞出，控油，将余油倒入油罐中。\r\n6.原炒锅留25克油，烧热，放入大葱段、小白菜心丝，炒几下，加鸡汤，10克酱油、味精、胡椒面，搅拌均匀，待丸子熟透后，放入', '色泽金黄，丸子软糯，口味鲜香。', NULL, NULL, '五花猪肉350克，鸡蛋1个，小白菜心150克，精盐2克，豆油500克（实耗75克），酱油15克，料酒15克，鲜姜15克，鸡汤400毫升，味精1克，淀粉125克，大葱50克。');
INSERT INTO `recipe` VALUES (2320, 82, '红煨牛肉', '1.将牛肉用清水洗净，浸泡2小时，捞出，切成4大块，每块约250克。\r\n2.煮锅内放入清水，投入牛肉块，煮至五成熟，除去血水和沫子，捞出，洗净，再切成1.5厘米见方的牛肉块。\r\n3.炒锅烧热，下入花生油，待油五成熟时，放入牛肉块煸炒断生，加精盐1克，煸炒入味。\r\n4.取蒸钵用箅子垫底，将牛肉放在箅子上，再放入清汤1000毫升及大料、葱白、姜片、酱油，上面盖一瓷盘，在旺火上烧开，转小火煨烂。\r\n5.将煨烂的牛肉去掉大料、姜、葱，倒入炒锅内，放大蒜、味精、白胡椒粉，勾芡，淋芝麻油，撒上白芝麻，即成。', '牛肉软糯，鲜香汁浓，细嫩可口。', NULL, NULL, '牛肋条肉1000克，大葱白10克，花生油75克，芝麻油1.5克，白胡椒0.5克，白芝麻5克（焙好），味精0.5克，大料1克，大蒜片10克，生姜片10克，酱油30克，湿淀粉25克，精盐1克。');
INSERT INTO `recipe` VALUES (2321, 82, '红煨笋鸡', '1.光笋鸡洗净，从肛门旁开口，取出内脏，从颈部开口取去食袋，再洗净入开水锅内焯一下，去掉血污，剁去脚爪、嘴尖，用料酒10克、酱油10克，抹遍笋鸡身内外，拌匀。\r\n2.炸锅内放入豆油，大火烧至七成热时，下入笋鸡，炸成金黄色，捞出，控油。\r\n3.大葱去皮，洗净，取125克打结，余下25克切成7厘米长的条，生姜去皮，洗净，切片。\r\n4.把炸过的鸡放入瓷盆内，加葱结、生姜片、料酒、酱油、0.5克盐、白糖及冷水250毫升，放入蒸锅，隔水蒸熟，切块。\r\n5.将蒸鸡放入盘中，炒锅烧热，放入25克油，五成热时，投入大葱条', '色泽红亮，鸡肉香嫩，鲜美郁浓。', NULL, NULL, '光笋鸡1只（约500克），大葱150克，生姜10克，料酒25克，酱油25克，白糖10克，湿淀粉25克，味精2.5克，豆油1000克（实耗50克），芝麻油10克，精盐1克。');
INSERT INTO `recipe` VALUES (2322, 82, '红油鹌鹑', '1.将净鹌鹑剁去两脚爪，剖开脊背，洗净，与肥瘦肉、肉皮、鸡骨一起下入滚水锅焯烫一下，捞出，洗净，控干。\r\n2.取1只砂锅，用竹箅垫底，把鹌鹑腹向下先放入砂锅内，再放入肥瘦肉、肉皮、鸡骨；另将菜心根部削成撖榄形，放入油锅中滑一下油，捞出，倒去热油，原锅放入高汤、精盐、味精，放入菜心，烧入味后，捞起待用。\r\n3.炒锅净后烧热，放花生油，投入姜块、葱段煸炒出香味时，将辣大酱入锅，炒至翻沙，烹入料酒、花椒、高汤、精盐、酱油、白糖、胡椒粉，待烧开后倒入砂锅内，随即放在小火上焖1个小时，取下，把原汤滗在锅内。同时将肥', '造型美观，鹑肉酥烂，味鲜香辣。', NULL, NULL, '净鹌鹑4只，菜心20只，肥瘦猪肉250克，肉皮150克，辣大酱50克，鸡骨250克，花生油75克，高汤2000毫升，胡椒面2克，精盐2克，酱油5克，白糖5克，料酒50克，味精5克，花椒10粒，辣椒油25克，葱段25克，姜块25克。');
INSERT INTO `recipe` VALUES (2323, 82, '红枣煨鸡', '1.笋嫩鸡宰杀，去毛，从肛门旁开口去内脏，从颈部开口取出食袋，洗净，放入清水中浸泡，捞出，洗净。再将锅中清水烧开，把净鸡放入锅内焯一下，去浮沫血水，剁去脚爪，嘴尖。\r\n2.炒锅烧热，放入豆油，烧至六成热，将鸡下锅过油，两色呈金黄色时，捞出，控油。\r\n3.蘑菇水泡后，洗净泥沙，去蒂，红枣煮熟，去皮；胡萝卜去皮，洗净，切成细丝；生姜去皮，洗净，切片；大葱15克洗净，打结；余下10克大葱切成1.5厘米长的段，待用。\r\n4.瓷盆放入鸡，加白糖、料酒、酱油35克，精盐1.5克、葱结、姜片、牛肉清汤，加盖，在旺火上烧', '色泽金黄，鸡肉香嫩，滑爽可口。', NULL, NULL, '笋嫩鸡1250克，蘑菇100克，红枣50克，胡萝卜25克，牛肉清汤750毫升，大葱25克，生姜15克，料酒50克，酱油50克，白糖15克，味精0.5克，芝麻油5克，湿淀粉50克，胡椒面0.5克，豆油425克（实耗100克），精盐2克。');
INSERT INTO `recipe` VALUES (2324, 82, '红鳟鱼丸', '1.将鱼去鳞、鳃，剖腹去内脏，洗净，用平刀从尾部沿背脊骨批片，取两片鱼肉，用刀剔下鱼皮，剥去红筋，取用350克鳟鱼肉。\r\n2.把鱼肉横切成2毫米厚的片，用清水洗净，放在砧板上，用双刀排剁成绿豆大碎粒；随即放入瓷盆内，加300毫升清水、盐，向同一方向搅拌，至鱼肉的粘性并见细泡时，即杨鱼泥茸，放在阴凉处，让它发胀。\r\n3.把胀好的鱼茸加入料酒、芝麻油、生姜末、味精搅拌均匀入味。\r\n4.炒锅放入半锅清水，用手抓起鱼茸，轻轻揉成鱼丸，放入锅中煮熟。\r\n5.取5个小瓷碗，放入盐、味精，将鱼丸连汤盛入每个小碗中，撒上', '鱼丸松散，味鲜汤清，清香适口。', NULL, NULL, '红鳟鱼1条（约1000克），水发香菇1朵，大葱段5克，料酒15克，大葱末5克，精盐5克，生姜末5克，味精2.5克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2325, 82, '花生米炒牛肉丁', '1.先将牛里脊肉先净，剔去筋膜，切成1厘米厚的大片，两面剞十字花刀，再切成1厘米见方的丁，用15克湿淀粉，1克精盐、鸡蛋搅拌均匀；鲜红辣椒洗净，去蒂和籽，切成1厘米长宽的方片；油炸花生米去皮，待用。\r\n2.大葱洗净，切段。\r\n3.炒锅烧热，放入豆油，烧五成热时，稍冷却，将牛肉丁入锅走油至七成熟，倒入漏勺，控干油，余油倒入油罐。\r\n4.炒锅净后放75克豆油，用旺火烧热，稍冷却，先将鲜红椒加精盐0.5克，下锅炒八成熟，再放入油炸花生米合炒，炒出香味时，倒入牛肉丁，依次放15克酱油，0.5克精盐、醋和葱段合炒，', '牛肉细嫩，花生香脆，香辣爽口。', NULL, NULL, '牛里脊肉300克，豆油500克，大葱10克，湿淀粉30克，酱油15克，精盐2克，油炸花生米50克（去皮），鲜红辣椒75克，鸡蛋50克，醋5克，焙好芝麻30克。');
INSERT INTO `recipe` VALUES (2326, 82, '花生米炒虾仁', '1.小虾洗净，挤出虾仁，浸泡在清水中；油炸花生米去皮，分成两份。\r\n2.大葱去皮，生姜去皮，洗净，均切成片；鸡蛋去黄取清，放入淀粉，搅拌均匀成蛋清糊。\r\n3.将虾仁放入瓷盆里，加5克盐、料酒、味精、鸡汤、15克湿淀粉、葱片、姜片、胡椒粉（面），辣椒面搅拌均匀，腌渍入味。\r\n4.捞出虾仁，将瓷盆中葱片、姜片拣出，加余下鸡汤，拌匀，勾兑成芡粉汁。\r\n5.炒锅烧热，放入豆油，烧热后稍冷却，将蘸匀鸡蛋清糊的虾仁投入，滑至白色至熟捞出，控油，余油倒入油罐。\r\n6.炒锅内留50克油，烧三成热入油炸花生仁米，复炸速炒几', '虾仁细嫩，花生米脆，香辣爽口。', NULL, NULL, '小虾1000克，豆油1000克（实耗75克），油炸花生米150克，精盐7.5克，料酒25克，淀粉40克，胡椒面1克，辣椒面1克，鸡汤50毫升，大葱10克，生姜5克，鸡蛋2个，味精7克。');
INSERT INTO `recipe` VALUES (2327, 82, '滑狗里脊', '1.将狗里脊肉剔去，切成长条，放入碗中，加入鸡蛋清、淀粉、精盐，抓匀浆，倒入温油中滑透，捞出，控油。\r\n2.将炒勺上火，加入少许花生油、葱末、姜末，稍加煸炒，烹入料酒、醋、酱油、精盐、白糖和少许清水，将白糖炒融化，下入滑透的狗里脊条，用淀粉勾芡，淋入明油，颠翻均匀，出勺，盛入盘中，即成。', '狗肉软嫩，甜酸略咸，滑爽适口。', NULL, NULL, '狗里脊肉250克，鸡蛋清50克，花生油500克（实耗50克），淀粉50克，白糖75克，米醋15克，酱油5克，酱油5克，料酒15克，大葱25克，生姜10克，精盐4克。');
INSERT INTO `recipe` VALUES (2328, 82, '滑溜猪里脊', '1.猪里脊去筋膜，切成片，放入清水中，将血沫漂净，挤去水分，加1克盐、12.5克淀粉、蛋清，将里脊片上匀浆芡，待用。\r\n2.大葱去皮，洗净，切片；大蒜去皮，洗净，切片；黄瓜洗净，切片。\r\n3.再将盐、味精、葱片、大蒜片，黄瓜片、淀粉和150毫升清水放入碗内，勾兑成粉芡汁。\r\n4.炒锅烧热，放入豆油，烧三成热，放入上好的里脊片，用筷子拔散，熟后，捞出，余油倒入油罐。\r\n5.炒锅内，倒入滑过油的里脊片，烧热，放入勾兑好的粉芡汁，炒匀，淋上芝麻油，即可。', '口味清香，肉片滑嫩，清爽适口。', NULL, NULL, '猪里脊150克，鸡蛋清25克，黄瓜25克，豆油300克（实耗50克），精盐3克，大葱1克，味精2克，芝麻油10克，淀粉25克。');
INSERT INTO `recipe` VALUES (2329, 82, '黄瓜炒牛肉', '1.先把牛肉洗净，剔净筋膜，放入冰箱冷冻一下，切得象纸一样薄，再切成约5厘米长、1.2厘米宽的小片，切均匀整齐。\r\n2.再把切好的牛肉片，放在瓷盆里，加上芝麻油、酱油、精盐、糖和辣椒粉，用手抓匀，使味渗进牛肉中。\r\n3.黄瓜洗净，去蒂，去皮，每隔3厘米的距离留一点薄薄的绿皮作为装饰，再纵切成两半，去籽后切成细条，厚薄要适中。\r\n4.炒锅烧热倒入豆油，热一下后，滑匀锅，把锅拿起转动几下，使油沾满锅面，倒进牛肉片，旺火快炒一分钟，倒入黄瓜条，再炒一会儿，直到黄瓜半熟为止，使黄瓜又嫩又脆，用芝麻面装饰，出锅，即', '肉嫩味香，黄瓜脆嫩，味鲜爽口。', NULL, NULL, '牛臀尖250克（或腱子肉），芝麻油16克，酱油15克，精盐5克，白糖6克，辣椒粉0.25克，黄瓜400克，豆油25克，芝麻面6克。');
INSERT INTO `recipe` VALUES (2330, 82, '黄瓜炒小虾', '1.将小虾用水洗净，剪去须和脚；黄瓜洗净，带皮切成3厘米长、1毫米厚的斜片。\r\n2.炒锅放入芝麻油，在旺火上烧热后，稍冷却，放入小虾翻两个身，加入黄瓜、料酒、糖、酱油、盐、再翻两个身，加清水50毫升，烧开，再炒，翻炒均匀，出锅，入盘，即可。', '红绿鲜明，虾嫩瓜脆，香嫩可口。', NULL, NULL, '小虾150克，白糖1.5克，黄瓜125克，精盐1克，酱油35克，熟猪肉50克，料酒2克，芝麻油50克。');
INSERT INTO `recipe` VALUES (2331, 82, '黄瓜炒猪肉片', '1.将猪肉洗净，去筋膜，切成2厘米宽的薄片，加1克盐、10克酱油、50克淀粉，搅拌均匀。\r\n2.再将黄瓜洗净，去皮、籽，切成薄片；葱切成2厘米长的丝，生姜、大蒜均切成片；干辣椒洗净，去蒂，切丝；水发木耳摘洗干净，撕成小片。\r\n3.取小碗，放入15克酱油、1克盐、50克淀粉，少许鸡汤勾兑成芡汁。\r\n4.炒锅净后烧热，放入芝麻油，烧至七成热时下入猪肉片，划散，加姜、蒜、葱、辣椒酱及黄瓜片，拌炒片刻，倒入芡汁，汁熟，出锅，入盘，即可。', '猪肉滑爽，黄瓜脆嫩，味道鲜香。', NULL, NULL, '五花猪肉150克，黄瓜250克，芝麻油75克，水发木耳5克，鸡汤50毫升，辣椒酱10克，精盐2克，酱油25克，大葱25克，生姜15克，大蒜15克，淀粉100克。');
INSERT INTO `recipe` VALUES (2332, 82, '黄瓜羹', '1.把4条老黄瓜洗净，去皮，把每条黄瓜在柄的一端切下少部分，挖去黄瓜籽瓤，洗净，用盐渍一下，待用。\r\n2.猪肉洗净，剁成泥茸，放入瓷碗中，加酱油、葱末、姜末、蒜茸、胡椒面、白糖、芝麻、芝麻油、精盐，搅拌均匀，入味。\r\n3.豆腐洗净，放入瓷盆中，碾碎，加盐、胡椒面、芝麻油、1个鸡蛋，拌上面粉，再倒入猪肉馅，搅拌成稀稠适宜粘合的馅心，然后，做成和黄瓜心同样大小的丸子。\r\n4.把丸子均匀地装入空黄瓜心内，盖上切成少部分黄瓜端头，用竹签逐条黄瓜通心串起来。\r\n5.煮锅放入鸡汤，加辣椒酱，放入平菇片、水发香菇和葱白', '瓜香馅嫩，汤鲜味美，香辣适口。', NULL, NULL, '老黄瓜500克，猪肉200克，豆腐150克，平菇15克，香菇5克，鸡蛋2个，葱白100克，辣椒酱15克，鸡汤1500毫升，竹签4根，酱油15克，葱末10克，蒜茸5克，姜末5克，胡椒粉2克，白糖15克，芝麻5克（焙好），芝麻油10克，精盐3克。');
INSERT INTO `recipe` VALUES (2333, 82, '黄瓜泡菜', '1.选小嫩黄瓜，用淡盐水搓洗干净，控净水，切约8厘米长的条，余下不足8厘米长的部分，去掉皮，剖两瓣，再切成2厘米长的小条垫铺在底层。\r\n2.再把8厘米长的段按十字线从两头相对剖口，但都不剖至底而保持整体，然后放10克盐腌渍入味。\r\n3.瓷碗内，放辣椒面、大葱末、大蒜泥、生姜末、白糖、10克盐、捣碎的虾酱糊搅拌均匀，成甜辣虾酱糊。\r\n4.再把腌渍过的黄瓜挤出水分，在刀口处涂抹匀辣虾酱糊。\r\n5.把绿色葱末放入罐底，撒少许盐（5克），再把涂沫好辣酱虾酱糊的黄瓜密实的码进去。\r\n6.把余下的甜辣虾酱糊和腌渍黄瓜', '甜辣酸香，清鲜爽口，虾酱郁浓。', NULL, NULL, '黄瓜1公斤，辣椒面50克，大葱末100克，大蒜末50克，生姜末50克，食盐25克，白糖15克，虾酱50克。');
INSERT INTO `recipe` VALUES (2334, 82, '黄焖狗肉', '1.先制出红汁：炒锅上火，放入香油，烧热，下入大料，稍炸后，再下入葱段、姜片稍炸，然后，下入大酱，稍加煸炒，即烹入料酒，下入酱油、狗骨汤、精盐，烧开后，小火煮至汤汁变浓时，将汤汁过箩，即成红汁。\r\n2.将熟狗肉片去皮，切成骨牌块，土豆去皮，洗净，切成滚刀块。\r\n3.再将炒锅烧热，放入香油，烧至七成热时，下入土豆块，炸至呈金黄色，捞出，控油，倒去余油。\r\n4.净锅烧热，加入红汁、狗骨汤、炸好的狗肉块、土豆块、味精、料酒、酱油，烧开后，小火煨入味，放入水淀粉勾芡，淋入明油，翻炒均匀，盛入碗中，即可。', '色泽杏黄，狗肉软烂，味道咸香。', NULL, NULL, '熟狗肉250克，土豆250克，狗骨汤150毫升，红汁50克，酱油15克，料酒15克，淀粉15克，香油500克（实耗50克）味精2.5克，精盐10克。');
INSERT INTO `recipe` VALUES (2335, 82, '鸡蛋包饭', '1.炒锅烧热化开20克熟鸡油，烧热，下入大葱末，炒成淡黄色，下入鸡丁炒几下，再放入大酱、辣椒酱炒去水分，再下入大米饭、鸡汤、味精、精盐、青豆，把米饭炒透。\r\n2.把鸡蛋打入碗内，放精盐搅匀。\r\n3.平底锅烧热，放入10克熟鸡油，烧热，把鸡蛋液倒入煎盘，用手一转，摊成大鸡蛋饼，把炒好的米饭放在鸡蛋饼的正当中，将鸡蛋饼的两头包起，往前一卷，一翻成卷形，放入少许底油，稍煎。\r\n4.装盘时，用铲子慢铲，使其完整。食用时蘸大酱吃，即可。', '饭香蛋鲜，色泽金黄，香辣适口。', NULL, NULL, '大米饭250克，鸡蛋250克，熟鸡丁50克，辣椒酱10克，味精2克，大酱25克，大葱末10克，青豆5克，鸡汤30毫升，精盐3克，熟鸡油30克。');
INSERT INTO `recipe` VALUES (2336, 82, '鸡丁鲜贝', '1.将鲜贝切好，切开，去掉内鳃；鸡肉去筋膜，洗净，切1厘米见方的丁；玉兰片、蘑菇均切成与鲜贝同样大小的方丁；大葱、生姜和大蒜，去皮，洗净，剁末。\r\n2.再将干贝放入开水锅中氽一下，捞出，捞干。\r\n3.鸡丁放入瓷碗内，加蛋清、淀粉抓均匀。\r\n4.炒锅烧热，放25克芝麻油，烧至五成热时，放入鸡丁，至八成熟时，捞出。再倒入35克芝麻油，烧热，投入大葱末、生姜末、大蒜末煸炒出香味，投入玉兰片、蘑菇、青豆和鲜贝。翻炒几下，加入料酒、味精、鸡汤和盐等，调好口味，放入鸡丁，翻炒均匀，放少许湿淀粉勾芡，入盘，撒匀焙好的芝', '色泽鲜艳，味道清香，贝肉嫩美。', NULL, NULL, '鲜贝125克，玉兰片15克，鸡肉125克，芝麻油60克，蘑菇15克，料酒15克，味精1.5克，鸡蛋清半个，淀粉50克，鸡汤50毫升，精盐10克，大葱15克，生姜10克，大蒜10克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2337, 82, '罗汉大虾', '1.将大虾洗净，剪去腿、头，去掉沙包和泥肠，把大虾从中间切成两段，头部一段待用。\r\n2.尾部一段去皮，留尾巴，从背上片开，不要片透，打上花刀，摆在盘中，淋上5克料酒，撒上少许盐、胡椒粉和拍碎的葱、姜，腌渍10分钟。\r\n3.将肥猪肉用汤煮5分钟，捞出，去皮与荸荠、虾肉切成碎粒，加入5克盐、2.5克料酒和蛋清一起拌匀成馅，镶在尾部有花刀的一面，使其成小凸肚状，然后，摆上几粒青豌豆，粘上黑芝麻。\r\n4.水发冬菇洗净，去蒂根，与竹笋、瘦猪肉均切成2厘米见方的小丁；葱、姜去皮，洗净，切丁。将肉丁、竹笋丁、冬菇丁煸炒', '美观大方，焦嫩鲜美，肉嫩鲜香。', NULL, NULL, '大虾1公斤，黑芝麻50克，罐头豌豆25克，罐头荸荠100克，虾肉125克，鸡蛋清2个，肥猪肉75克，罐头竹笋50克，水发冬菇10克，瘦猪肉50克，生姜25克，大葱60克，盐10克，糖10克，料酒15克，花生油100克，鸡汤100毫升，淀粉15克，辣大酱10克，生菜25克。');
INSERT INTO `recipe` VALUES (2338, 82, '萝卜沙拉', '1.萝卜洗净，去皮，去根，切成3厘米长的火柴棍大小细丝。\r\n2.脆苹果洗净，去皮，去蒂，去核籽，也切成火柴棍大小的细丝，泡在冷水中，以免锈黑变色。\r\n3.鲜红辣椒洗净，去蒂根，去籽，剁成碎末；大葱去皮，洗净，切成碎末。\r\n4.取沙拉盆，放入萝卜丝、苹果丝、柠檬汁、大葱末、酱油、沙拉油、芝麻油、醋、白糖、芝麻面、鲜红辣椒末，轻轻搅拌均匀。\r\n5.食用时，将制好的萝卜色拉，分装在每个小沙拉碟中，即可。', '色彩美观，咸酸甜辣，清爽利口。', NULL, NULL, '萝卜500克，脆苹果250克，柠檬汁10克，大葱30克，酱油45克，沙拉油15克，芝麻油16克，醋45克，白糖35克，精盐10克，芝麻面15克（焙好），鲜红辣椒5克。');
INSERT INTO `recipe` VALUES (2339, 82, '麻辣牛肉', '1.牛肉改成两个整齐的块；大葱去皮；洗净，剖开切成段；生姜切成片；干红辣椒洗净，去把、籽，切成1厘米长的节；芝麻焙好炒熟。\r\n2.牛肉用葱段、生姜片、精盐、料酒腌1小时，沸水旺火上笼蒸烂，取出晾凉，改切成4厘米长、1厘米宽的条。\r\n3.炸锅烧热，放入豆油，烧五成热，放入牛肉条，炸干水分，捞出，控油。\r\n4.锅内放豆油75克，烧热，放入花椒，炸糊后捞出，弃之。油稍凉一些，放入干红辣椒炸成紫黑色时，加入葱、姜炒一下，放入牛肉汤，加酱油、味精、牛肉条，用中等火力将汁收浓，汁尽时浇上辣椒油，撒上焙好的芝麻。\r\n5', '口味香辣，牛肉鲜嫩，酥香爽口。', NULL, NULL, '牛臀尖肉500克（黄瓜条肉），豆油500克（实耗75克），大葱50克，生姜25克，精盐20克，酱油20克，料酒15克，白糖25克，花椒5克，芝麻10克（焙好），辣椒油5克，干红辣椒5克，味精5克，牛肉汤750毫升，芝麻盐15克。');
INSERT INTO `recipe` VALUES (2340, 82, '鸡茸葵花汤', '1.将鸡里脊肉剔净筋膜，剁成细泥，鲜蘑洗净，去蒂根，竖起顶刀切成2厘米厚的片；鸡蛋煮熟，剥皮去掉蛋白，将蛋黄碾成碎末；火腿切成细末。\r\n2.鸡蛋清用筷子抽打成泡沫状。\r\n3.再将鸡泥用少许鸡汤调开，加入精盐、料酒、白兰地酒、胡椒面、蛋清泡、玉米粉，搅拌均匀，制成鸡茸汤料。\r\n4.取一个24厘米的平盘，盘上抹一层熟鸡油，将鸡茸汤料铺于盘中，用餐刀抹平，中间略凸起一些，上面撒匀蛋黄末，中心部位撒上火腿末，周围用鲜蘑片竖着插一圈，紧挨着鲜蘑根部平着贴成一圈，制成葵花形状，然后，上笼屉，隔水蒸熟。\r\n5.汤锅上火', '形态喜人，色泽鲜艳，入口即化。', NULL, NULL, '鸡里脊肉100克，鲜蘑50克，火腿25克，鸡蛋1个，鸡蛋清2个，鸡汤1千克，精盐7.5克，味精5克，料酒10克，胡椒面2克，玉米粉5克，辣椒油1克，熟鸡油15克，白兰地酒10克。');
INSERT INTO `recipe` VALUES (2341, 82, '麻辣兔肉', '1.取兔肉洗净，去筋膜，切成均匀的薄片，放入瓷盆中，加清水浸泡半小时，发白后，去其血污，沥干水分，加料酒、精盐、鸡蛋、淀粉上浆，拌匀。\r\n2.锅烧热，倒入花生油，烧至四成热时，下入兔肉片滑散至熟时，倒入漏勺沥油，余油倒入油罐。\r\n3.净锅烧热，放入25克花生油。五成热时，放入干红辣椒段，炸香，捞出，再下入花椒面，炒匀出香味，投入葱片、姜片、黄瓜片，倒入兔肉片，加精盐、料酒、酱油、辣椒油、味精、翻炒均匀，撒入芝麻油，出锅，入盘。\r\n4.食用时，配春饼同时吃，即可。', '色泽红黄，麻辣嫩鲜，饼薄如纱。', NULL, NULL, '野兔肉200克，鸡蛋50克，淀粉10克，黄瓜片25克，精盐2.5克，料酒10克，酱油5克，味精1克，生姜10克，大葱10克，辣椒油15克，花椒面1克，花生油500克（实耗50克），干红辣椒5克，芝麻油3克。');
INSERT INTO `recipe` VALUES (2342, 82, '马不停蹄', '1.猪前蹄用明火烧毛，刮洗干净，剔去猪肉成一张完整无损的蹄皮。再用线按人字形密缝蹄皮，使其形同原猪蹄。\r\n2.再将猪蹄瘦肉、水发香菇均切成颗粒状，虾仁切成末，盛入瓷盆内，加入鲤鱼末，焙好的芝麻面、白糖、精盐、味精、料酒、水淀粉搅拌均匀成馅料，腌渍2小时，入味。\r\n3.把馅料搅拌均匀后，灌入猪蹄内，填实，口部用线缝，然后，用净白布按原形裹紧，取10根竹筷子夹住，捆牢。\r\n4.大酱卤汤倒入砂锅内，用中火烧开，放入猪蹄，用微火煮1个半小时，取出，解去绳子、竹筷、白布，用香油涂匀皮面，晾凉后将缝线拆开，直切成两片', '柔嫩香甜，咸辣微甜，嚼劲甚佳。', NULL, NULL, '猪前蹄1只（约1000克左右），猪瘦肉1000克，猪肉皮100克，鲤鱼肉末50克，水发香菇50克，白糖15克，精盐5克，味精5克，料酒30克，香油5克，水淀粉50克，大酱卤1千克，芝麻面25克（焙好），辣椒酱50克。');
INSERT INTO `recipe` VALUES (2343, 82, '玛瑙狗', '1.将狗肉用凉水浸泡4小时后，剔去筋膜，切成象眼块；与竹笋片、胡萝卜片一起放入开水中，焯透后捞出，控去水；再将油皮切成象眼片。\r\n2.再将炒勺上火，加入香油烧热，下入葱丝、姜丝稍加煸炒，下入狗肉块、笋片、料酒、醋、酱油、白糖及适量的清水，烧开后下入黄瓜片，用淀粉勾成流芡，出勺，盛入汤碗中。\r\n3.将炒勺上火，加入花生油烧热，下入油皮片，炸至酥脆时捞出，放入一个汤盘内与汤碗一起上席，当着宾客的面，把碗中的流芡倒入汤盘内，发出响声，再撒上焙好的熟芝麻即可。', '颜色淡茶，质地酥烂，甜酸稍咸。', NULL, NULL, '熟白狗肉200克，油皮2张，罐头竹笋15克，黄瓜片15克，胡萝卜片15克，花生油500克（实耗75克），淀粉40克，料酒15克，酱油10克，醋10克，葱丝15克，蒜片5克，姜丝10克，咸面2克，白糖75克，香油15克，焙好芝麻15克。');
INSERT INTO `recipe` VALUES (2344, 82, '美味双耳', '1.将黄瓜洗净，去皮，切成小菱形片；香菜洗净，切成2厘米长的段；黑木耳择洗干净，撕开；再把黄瓜、黑木耳用开水焯透，捞出，用冷水过凉，控干水后，入盘一边。\r\n2.炒勺烧热放芝麻油，煸炒花椒粒，炸糊后捞出，放葱丝、姜丝、大蒜茸、辣椒酱炒匀，出勺浇在黑木耳上，扣碗稍焖片刻，打开，撒上香菜段，味精，拌匀，放盘一边。\r\n3.把山楂糕切成小块；把银耳去根，洗净，大朵的撕开，用开水烫一下，放盘另一边。\r\n4.炒勺放500毫升清水浇开，加白糖、冰糖溶化后，撇去浮沫，倒在银耳上，上面放山楂片，即可。', '黑白相映，甜辣爽口，佐酒上品。', NULL, NULL, '水发木耳150克，水发银耳100克，芝麻油25克，白糖150克，黄瓜50克，香菜25克，山楂糕150克，冰糖150克，辣椒酱25克，味精2克，花椒粒2克，葱丝5克，姜丝5克，大蒜茸5克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2345, 82, '鸡茸银耳汤', '1.将鸡里脊肉剔净筋膜，用刀背剁成细泥；银耳水发后，摘洗干净，撕成小朵，鸡蛋清用筷子抽打成泡沫状，加入玉米粉拌匀，待用。\r\n2.将鸡泥用少许鸡汤调开，加入精盐、料酒、胡椒面搅拌均匀，调成稠糊状，加入蛋清泡搅拌均匀，制成鸡茸汤料。\r\n3.取浅酒杯12个，酒杯内抹上一层熟鸡油，将鸡茸汤料逐个放入浅酒杯内，用餐刀将酒杯口抹平；再将银耳小朵插入鸡茸中，上笼屉蒸熟，取出，把鸡茸银耳从酒杯内起出，放入汤盆内，撒匀熟芝麻和芝麻油。\r\n4.汤锅上火，放入鸡汤，烧开后撇净浮沫，加入精盐、味精调好口味，倒入汤盆中，即可。', '美观素雅，洁白无瑕，汤清味鲜。', NULL, NULL, '鸡里脊肉100克，水发银耳50克，鸡蛋清2个，鸡汤1千克，精盐7.5克，味精5克，料酒10克，胡椒面3克，玉米粉15克，芝麻油5克，芝麻15克（焙好），熟鸡油10克。');
INSERT INTO `recipe` VALUES (2346, 82, '焖炒平菇', '1.把250克平菇洗净，用开水烫一下，挤水，撕成条状；核桃仁用热水浸泡一下，去掉外皮，再用开水氽一下，待用。\r\n2.再把狗肉入温水浸泡，洗净后放入开水锅中，氽至半熟，捞出，切片。\r\n3.余下的250克鲜平菇洗净，撕成片后，挤去余水。\r\n4.炒勺烧热放素油，烧至九成热时，将狗肉片放入油锅煸炒，炒至狗肉片出香味时，再下平菇片250克，煸至菇片发软后，加入200毫升鲜汤、陈皮和料酒，大火烧开，小火烧开，小火煨焖，焖熟后，放浓口酱油、味精、葱、姜、大蒜瓣、辣椒酱，翻炒均匀入味后，出勺入盘一边。\r\n5.净勺烧热，化', '双色双味，咸辣爽口，中韩合壁。', NULL, NULL, '鲜平菇500克，泡菜25克，嫩狗肉200克，核桃仁50克，鲜汤250毫升，鸡油25克，辣椒酱15克，陈皮5克，精盐5克，料酒15克，味精2克，酱油15克，葱10克，水淀粉15克，豆油25克，大蒜瓣25克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2347, 82, '焖烧狗肉', '1.将熟狗肉片去皮，切成小骨牌块，把鸡蛋磕入碗内，加入淀粉、面粉、精盐和适量清水，搅拌均匀。\r\n2.再将炒锅上火，放入红汁、狗骨清汤、狗肉块，把西红柿码放在肉块的周围，再加入味精、料酒、白糖、咸面、辣椒油，烧开，用小火煨，入味后收汁，淋入水淀粉勾芡，淋少许香油，翻个儿，出锅，盛入碗中即成。', '颜色淡茶，肉质软烂，什锦口味。', NULL, NULL, '熟狗肉250克，鸡蛋100克，淀粉75克，面粉25克，香油500克（实耗75克），白糖75克，米醋15克，料酒5克，大葱25克，生姜15克，狗骨汤100毫升，精盐20克，干辣椒1个。');
INSERT INTO `recipe` VALUES (2348, 82, '面包虾仁', '1.将虾仁洗净，控干水，用鸡蛋清、精盐、细干淀粉拌匀，浆好；面包及刀切去外皮，改成6厘米长，3厘米见方条，待用。\r\n2.炒锅上火，烧热，加入豆油，烧至六成热时，放入面包条，用手勺翻几下，随即将油锅改小火。\r\n3.再用一炒锅，烧热，用油滑锅后，加入豆油，烧至三成热时，倒入虾仁，用手勺轻轻滑散，同时，放入青豆，用手勺翻几个身，见虾仁变成乳白色时，倒入漏阿勺内，控干油。锅内留25克余油，加入番茄酱，炒透后，烹入料酒、精盐、味精、白糖和鸡汤，并倒入滑过的虾仁、青豆。同时用漏勺把面包条捞出，控油，入盘。\r\n4.然后', '酥香脆嫩，色泽红亮，甜酸咸鲜。', NULL, NULL, '净虾仁100克，熟鸡油25克，面包200克，罐头青豆15克，鸡蛋清10克，细干淀粉5克，鸡汤100毫升，料酒10克，精盐2.5克，味精1克，白糖20克，番茄酱60克，豆油750克（实耗75克），水淀粉15克，熟芝麻15克。');
INSERT INTO `recipe` VALUES (2349, 82, '明月红松鸡', '1.将出骨鸡腿肉皮朝下放在砧墩上，摊平，用刀在肉面轻轻剞成十字花刀，猪肉斩成细茸，用5克料酒，2.5克精盐，5克白糖，5克水淀粉及半个鸡蛋，拌匀。\r\n2.往鸡腿肉上撒上细干淀粉，把拌好的肉茸均匀地铺在鸡腿上，用刀轻轻排斩，使肉茸和鸡腿肉粘在一起（若不排斩，下锅后易脱干）；菠菜择洗干净，待用。\r\n3.炒锅上火烧热，用油滑锅后，加入250克豆油，烧热，下入鸡腿肉，先煎挂肉茸的一面，煎成金黄色时，翻身稍煎皮面，滗去余油，随后烹入10克料酒、酱油、精盐、辣大酱、辣酱油、味精、葱花、姜末、鸡清汤，用小火烧10分钟，', '酥烂清香，红绿相衬，鸡蛋似明月。', NULL, NULL, '生鸡腿肉150克（出骨），肥瘦猪腿肉150克，菠菜150克，火腿10克，鸡蛋2个，生菜叶25克，鸡清汤150毫升，料酒15克，酱油15克，精盐6克，辣酱油15克，辣大酱25克，白糖20克，细干淀粉15克，水淀粉15克，豆油250克（实耗75克），味精1.5克，葱花5克，姜末5克。');
INSERT INTO `recipe` VALUES (2350, 82, '蘑菇炒牛肉丝', '1.瘦牛肉洗净，切成大薄片，再横着肉纹切成细丝，加入大蒜泥，生姜末和精盐，抓匀。\r\n2.鲜蘑菇洗净泥沙，去蒂，切大块，待用。\r\n3.炒锅烧热，放入豆油，烧七成热，放入牛肉丝，用旺火爆炒，直炒至肉色变白，再放入蘑菇块，翻炒几下，倒入调料汁，改用小火，盖上锅盖，焖2分钟。\r\n4.玉米粉用凉水搅拌均匀，放入炒牛肉丝锅里，不断搅拌，直到变稠为止，最后把牛肉丝和玉米浆拌匀，出锅即可。', '口味清香，牛肉细嫩，蘑菇清香。', NULL, NULL, '瘦牛肉250克，大蒜泥3克，生姜片3克，精盐5克，鲜蘑菇250克，豆油40克，调料汁65克（或热水），玉米粉12克，凉水250毫升。');
INSERT INTO `recipe` VALUES (2351, 82, '鸡丝炒海螺', '1.将海螺从中间割开取肉，用刀拍扁，由上往下切为厚3毫米的片；鸡肉去筋膜，洗净，切成长3厘米，厚2毫米的丝；竹笋和蘑菇均切成长3厘米的丝，待用。\r\n2.再将海螺片放入开水锅内，焯一下。\r\n3.鸡肉丝放入瓷碗中，加蛋清抓拌均匀。\r\n4.炒锅烧热，放入花生油，烧至五成热时，放入鸡丝，用温油滑至鸡丝变白，待八成熟时，捞出。\r\n5.炒锅再加花生油，油热时，先放入辣椒面、大葱丝、生姜丝、大蒜末煸炒出香味，再放入竹笋丝、蘑菇丝、青豆和海螺丝，翻炒均匀后，加料酒、味精、鸡汤和盐，待汤烧开后，放入鸡丝同炒几下，调好口味，', '螺肉细嫩，鸡丝鲜香，味辣爽口。', NULL, NULL, '海螺肉125克，鸡肉100克，竹笋15克（罐头），蘑菇15克，青豆20克，花生油60克，料酒15克，味精1.5克，淀粉50克，蛋清半个，鸡汤50毫升，辣椒面2克，大葱5克，大蒜5克，生姜5克。');
INSERT INTO `recipe` VALUES (2352, 82, '鸡丝海螺汤', '1.将海螺肉由缺口处割开取肉，用刀拍一下，切成厚3厘米的片；罐头竹笋切成小片；蘑菇水发后，洗净，也切成小片；油菜心洗净，切片；水发木耳去蒂根，洗净泥沙，撕成小片。\r\n2.大葱、生姜、大蒜去皮，洗净，均切成碎末。\r\n3.再将海螺肉、竹笋、木耳、蘑菇和油菜片分别用开水氽一下，捞出，控水，盛入汤盆内，撒上胡椒面。\r\n4.把鸡汤倒入锅中，加上料酒、酱油、味精、盐、葱末、生姜末、大蒜末、汤烧开时，调好口味，撇去浮沫，冲入汤盆中，淋上辣椒油，即可。', '鸡丝滑嫩，海螺鲜美，口味香辣。', NULL, NULL, '海螺肉150克，鸡肉100克，罐头竹笋15克，水发木耳15克，水发蘑菇15克，油菜心15克，料酒10克，酱油10克，味精1.5克，鸡汤400毫升，胡椒面3克，精盐10克，大葱15克，辣椒油1克。');
INSERT INTO `recipe` VALUES (2353, 82, '鸡丝鸟窝', '1.将鸡脯肉去皮筋，用清水浸泡半小时，切成7厘米长和火柴棍一样粗细的丝；竹笋、青椒均切成与鸡丝同样粗细的丝；将芋头刮去皮，洗净，切成帘子棍似的丝，待用。\r\n2.将50克鸡蛋清、1克精盐、15克细干淀粉，放入碗内，调匀后，放入鸡丝，轻轻上浆，浆好后，再放入芝麻油拌匀。\r\n3.将芋头丝放入瓷盆中，加入50克鸡蛋清、1克精盐、拌匀后，加入35克细干淀粉和精白面粉搅拌均匀后，待用。\r\n4.炒锅烧热，放入豆油。烧至六成热时，将拌好的芋头丝，放入碗型带眼的模子中，抹至厚薄均匀后，将另一碗型带眼的模子按上，卡住后，放入', '形象逼真，鲜嫩清淡，美观大方。', NULL, NULL, '鸡脯肉300克，罐头竹笋75克，青椒25克，芋头200克，鸡蛋清100克，细干淀粉50克，精白面粉25克，鸡汤125毫升，花叶生菜25克，西红柿花1朵，料酒5克，精盐3.5克，味精2.5克，豆油750克（实耗100克），水淀粉15克，芝麻油15克，鸡油15克。');
INSERT INTO `recipe` VALUES (2354, 82, '吉姆鸡丝汤', '1.将鸡脯肉洗净，剔净筋膜，切成6厘米长的细丝，豌豆苗摘尖，洗净，消毒。\r\n2.再将鸡丝用精盐、料酒、白兰地酒、胡椒面、鸡蛋清、湿淀粉搅拌均匀，浸渍好。\r\n3.干紫菜擦净，放在大碗中，先用少许开水泡一下，滗净水分，再用开水泡3分钟，撕成小片，待用。\r\n4.汤锅上火，放入清水，烧开后，放入鸡丝氽熟而不老，捞出，沥净水，放入汤盆中。\r\n5.汤锅上火，放入鸡汤，烧开后，撇去浮沫，放入精盐、味精、紫菜块和汤汁，烧开，倒入汤盆中，撒上豌豆苗和熟芝麻，再淋上芝麻油，即可。', '色泽美观，汤汁清澈，紫菜浓香。', NULL, NULL, '吉姆150克（干紫菜），白兰地酒15克，鸡脯肉150克，蘑菇50克，鸡汤1千克，豌豆苗10克，精盐7.5克，味精5克，料酒15克，胡椒面3克，湿淀粉15克，鸡蛋清1个，熟芝麻15克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2355, 82, '家常豆腐', '1.将豆腐切成长4厘米、宽3厘米、厚5毫米的长方块；去皮猪肉去筋膜，切成薄片，放入少许盐和水淀粉入味拌匀，待用。\r\n2.炒锅置旺火上，放入豆油，烧热，中火，将豆腐分片、分批放入锅中爆炸，每次放8片为宜，炸至豆腐两面呈金黄色时，捞出，控油，待用。\r\n3.净锅放油，烧热放入肉片，没溜至熟，取出，待用。\r\n4.炒锅留少许油，五成热时，放入花椒炸糊，捞出，放入辣大酱，烧匀，投入肉片，然后加酱油、白糖、味精、鸡汤，最后，倒入豆腐块，中火焖烧10分钟左右，然后加入大蒜汁，用水淀粉勾芡，出锅，盛入盆中，再撒上辣椒油，即', '色彩美观，香味浓郁，味道鲜美。', NULL, NULL, '豆腐400克，鸡汤100毫升，豆油200克，肥瘦猪肉50克（去皮），辣大酱15克，酱油10克，大蒜10克，白糖10克，豆腐粉20克，辣椒油15克，盐5克，味精3克，生姜末10克，花椒2克。');
INSERT INTO `recipe` VALUES (2356, 82, '酱爆双脆', '1.猪肚头摘洗干净发好，剔去表面老皮，打成十字花刀，切成4厘米见方的三角块；鸡肫剔除茧皮，打开菊花形，分别放入开水锅里，五成熟，捞出，挤干水份，盛入碗内，用料酒腌拌一下。\r\n2.香菜除去老根，切成4厘米长的段。\r\n3.取瓷碗，放大蒜片、葱丝、盐、青蒜丝、芝麻、味精、料酒、醋，搅拌均匀，待用。\r\n4.旺火烧热炒锅，放入熟鸡油，烧至八成热时，倒入肚块、鸡肫，爆炸几秒钟，用漏勺捞出，控油。\r\n5.原炒锅留50克底油，烧热，放入辣大酱加少许清水，炒出红油，倒入肚块、鸡肫，快好后，放入香菜段和瓷碗中调好的料汁，炒匀', '色彩鲜明，脆嫩鲜美，口味辣香。', NULL, NULL, '猪肚头200克，鸡肫200克，香菜100克，辣大酱50克，大蒜片5克，大葱丝5克，料酒15克，盐5克，味精5克，熟鸡油150克，芝麻油10克，芝麻10克（焙好），青蒜丝5克。');
INSERT INTO `recipe` VALUES (2357, 82, '酱爆猪肉丁', '1.将猪肉洗净，去筋膜，切成5毫米见方的丁，放入盆内，加5克料酒，鸡蛋清、盐、胡椒粉、1.5克味精和淀粉拌入味。\r\n2.炸锅净后烧热，放入豆油，烧至六成热时，下肉丁，变白断生，捞出，控油。\r\n3.炒锅烧热，放少许豆油，五成热时，放入大酱，煸炒几下，放酱油、白糖、2.5克味精，再烹入5克料酒，放入肉丁，翻炒几下，淋上芝麻油，装盘，撒上大葱花，即成。', '色泽酱红，口味浓香，肉丁鲜嫩。', NULL, NULL, '猪腿瘦肉400克，豆油750克（实耗50克），料酒10克，精盐1克，酱油5克，白糖5克，胡椒面1克，大酱20克，蛋清2个，大葱10克，芝麻油5克，味精4克，淀粉15克。');
INSERT INTO `recipe` VALUES (2358, 82, '酱鸡', '1.将鸡宰杀，去毛，净腔，去内脏，洗净，控干水，用盐搓擦鸡的里外，擦匀后，置盆中，用重物压紧，腌一天一夜后取出，控干盐水。\r\n2.再将鸡放入锅中，放入清水，水略淹没鸡，烧开，捞出。鸡汤过滤成清汤，再把鸡用凉水冲洗干净，再放回原清汤中，并加入大葱段、生姜末、大蒜末、酱油、25克白糖、料酒，用盆把鸡压入汤中，盖好锅盖，大火烧开，用小火焖至八成熟，捞出，冷却。\r\n3.原汤过滤后，入锅，用旺火烧开，加25克白糖，使其熬成浓汁，抹在鸡皮上面。要抹匀，抹好。\r\n4.食用时，切成片后，码成原鸡形，入盘，蘸辣大酱吃，即可', '色泽金黄，口味咸辣，浓香扑鼻。', NULL, NULL, '嫩鸡1500克，酱油50克，精盐40克，大葱段10克，生姜末5克，大蒜末5克，料酒25克，辣大酱25克，白糖50克。');
INSERT INTO `recipe` VALUES (2359, 82, '酱焖加吉鱼', '1.加级鱼洗净，去鳞、内脏和鳃，洗净，在鱼身两侧斜剞数刀，放入瓷盆中，用盐、花椒面、葱末、姜末抹遍鱼身内外，腌渍2小时，去掉葱、姜、花椒面，控干，待用。\r\n2.炒锅上火，倒入250克豆油，烧至七成热时，放入加吉鱼，待油皮略硬时，捞出，控油，待用。\r\n3.炒锅留35克底油，烧热，放入肉丁、蘑菇，稍煸后，放大酱，再翻炒几下，放入葱、姜、蒜、料酒、酱油、醋、糖、鸡汤、辣椒面，待汤烧开时，放入加吉鱼，将锅改小火煨半小时左右。待汤汁稠浓时，放旺火收汁，淋上芝麻油，即成。', '味道鲜美，营养丰富，甜辣鲜香。', NULL, NULL, '加吉鱼1000克，肉丁50克，蘑菇25克，豆油250克（实耗60克），白糖100克，料酒25克，醋75克，酱油25克，味精5克，葱末20克，鸡汤500毫升，芝麻油10克，生姜末15克，大蒜片10克，芝麻油3克，精盐7.5克，花椒面1克，辣椒面2克，大酱10克。');
INSERT INTO `recipe` VALUES (2360, 82, '酱汁鲻鱼', '1.将鲻鱼洗净，去鳞、鳃、内脏，冲洗三次，在鱼身两侧每隔1.5厘米剞斜刀；大蒜去皮，大葱去皮，生姜去皮，均洗净，切片。\r\n2.再将鱼身抹上酱油，腌渍片刻，入味。\r\n3.平底锅烧热，倒入豆油，待五成热时，放入腌渍入味的鲻鱼煎炸，翻个儿，煎成两面呈金黄色时，铲出，控油。\r\n4.炒锅烧热，倒入25克熟豆油，烧热，放入葱、姜、蒜烹锅，投入大酱翻炒，炒出酱香味时放鱼，烹入料酒、酱油、盐、糖、味精、大料和鸡汤，旺火烧开小火煨炖，待汤汁余下很少时，拣去葱、姜、蒜和大料，调好口味，把鱼盛入鱼盘中。再将汁加热，加淀粉勾芡，', '色泽红艳，味鲜汁浓，鲻鱼鲜嫩。', NULL, NULL, '鲻鱼1条（约500克），料酒15克，豆油175克，酱油10克，大酱15克，白糖25克，味精2.5克，淀粉15克，鸡汤250毫升，葱10克，姜5克，大蒜7克，大料5克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2361, 82, '浇汁螃蟹', '1.将2只大螃蟹洗净，用绳线捆牢，腹朝上装盘，加少许花椒、葱、姜，入笼，用旺火蒸1小时左右，取出，解开绳线，剥掉肚脐、盖壳，挖出蟹腿，切去爪尖。\r\n2.再将蟹身由中间向左右二分之一处各斜一刀，即成3大块，每块再顺切成4小块，另一只蟹也是同样切法。\r\n3.装盘时，把蟹腿排在中间，大腿在前，小腿在后，蟹块摆在小腿上和两侧，再加上蟹黄。\r\n4.大葱、生姜、大蒜去皮，洗净，均切成碎末，放入瓷碗中，加醋、酱油、味精、白糖和芝麻油，调成汁，浇在蟹块上，即可。', '黄白相间，味香肉鲜，嫩美可口。', NULL, NULL, '毛蟹2只（约500克），大葱15克，生姜10克，醋50克，大蒜5克，酱油75克，花椒3克，精盐3克，白糖15克，味精2克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2362, 82, '金达莱花菜', '1.取熟透的五味子，除去疵点，洗净，放入凉开水中浸泡24小时。\r\n2.取洁净透明的玻璃盆，放入浸过五味子的水，放入蜂蜜，加入烧开放凉的白糖水，调好颜色和口味。\r\n3.摘取正在盛开的金达莱花30朵，用清水掺入少量洗涤精，将花冲洗干净，然后再用净清水冲洗多次，控净水分，但不能碰伤花瓣。\r\n4.除去金达莱花的并蒂，只留红色花瓣，蘸上绿豆淀粉，蘸匀，然后，在沸开水中焯一下，放入立即捞起，用净清水冲一下，然后将金达莱花瓣放入盛有五味子水和甜味调料的玻璃盆中，撒上松仁，即可。', '汤鲜花香，舒肚和胃，甜香适口。', NULL, NULL, '金达莱花30朵，五味子100克，白糖50克，凉开水1000毫升，蜂蜜15克，绿豆淀粉25克，松仁15克。');
INSERT INTO `recipe` VALUES (2363, 82, '金酿豆腐', '1.将老豆腐四面边皮去掉，切成长5厘米、宽3厘米的块12块，用45克干细淀粉滚过；再将猪肉洗净，用刀斩成泥茸，放入瓷碗中，加入5克料酒、0.75克精盐、0.75克味精、鸡蛋和葱末、姜末，用竹筷搅拌均匀并上劲。\r\n2.再把虾仁洗净，控干水，用鸡蛋清、0.5克精盐、5克细干淀拌匀上浆，待用。\r\n3.炒锅上火烧热，加入熟鸡油，烧至五成热时，逐块放入豆腐，炸至金黄色，用漏勺捞出稍凉，再用小刀从豆腐的一面切开，把豆腐挖成箱子形，填入肉茸，然后把浆好的虾仁放在肉茸上，即成“金酿豆腐”。\r\n4.炒锅上火烧热，加入熟鸡油', '鲜嫩香酥，美观大方，易于消化。', NULL, NULL, '老豆腐300克，猪后腿肉150克，虾仁100克，小菠菜100克，鸡蛋清15克，鸡蛋25克，细干淀粉50克，鸡汤250毫升，料酒15克，酱油20克，精盐2.5克，味精2.5克，白糖2克，水淀粉15克，熟鸡油750克（实耗75克），葱末10克，姜末5克。');
INSERT INTO `recipe` VALUES (2364, 82, '韭菜炒海螺', '1.将海螺肉由中间缺口处割开相连，用刀面拍一下，切成厚6厘米的原形片；韭菜择洗干净，切成4厘米长的段。\r\n2.再将海螺片放入开水锅中烫好，捞出，控干水。\r\n3.炒锅烧热放入花生油，烧热时，用姜丝、葱丝、大蒜末煸炒，再放入韭菜炒几下，再将海螺片倒入，翻炒后，加酱油、味精、料酒、盐，再炒几下，调好口味，出锅，入盘，撒上芝麻，即可。', '韭菜螺嫩，口味鲜美，咸香爽口。', NULL, NULL, '海螺肉150克，韭菜150克，花生油35克，酱油10克，精1.5克，料酒15克，大葱10克，生姜5克，大蒜5克，精盐25克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2365, 82, '桔形虾仁', '1.将虾仁漂洗干净，猪肥肉洗净，去筋膜，切成片后与虾仁250克排斩成茸，放入盆中，加入蛋清、淀粉、盐，用力搅拌均匀待用。\r\n2.再将罐头竹笋切成直径为2厘米、厚3毫米的金钱形片12片，放入盆内。\r\n3.另将虾肉茸挤成12个丸子，放在冬笋片上。\r\n4.把余下的250克虾仁，拌上蛋清、盐、淀粉上浆，放入温芝麻油中，划熟，控去油。\r\n5.再把划熟的虾仁围在虾丸上，在虾仁顶端上放上一粒鲜豌豆，即成桔子形，放入蒸锅蒸熟，取出。\r\n6.取炒锅烧热，放入芝麻油，烧热放入葱姜末煸炒出香味，加入鸡汤、盐、料酒、味精，烧开，', '呈桔黄色，造型美观，鲜嫩清香。', NULL, NULL, '虾仁500克，猪肥肉50克，罐头竹笋100克，鲜豌豆25克，蛋清1个，料酒10克，鸡汤150毫升，盐5克，淀粉25克，味精5克，鸡汤15克，芝麻油25克，葱姜末10克。');
INSERT INTO `recipe` VALUES (2366, 82, '菊花猪肉', '1.把猪里脊洗净，去筋膜，切成细丝，越细越好，罐头竹笋也切成同样的细丝；粉丝用剪刀剪成3寸长的段，用一细绳捆在粉丝的中间；淀粉用清水泡上；肉丝用蛋清、水淀粉（比例为1：1）、精盐浆好，待用。\r\n2.炒锅旺火烧热，倒入豆油，待油烧至6成热时，将猪肉丝滑入，用筷子划散，然后倒入漏勺里，余油倒入油罐。\r\n3.再将炒锅放在火上，加入鸡汤、酱油、辣大酱、味精、料酒、肉丝、笋丝，将25克淀粉加10毫升水拌匀，徐徐淋入，甩上芝麻油，将锅颠翻过来，出锅，放入盘的一边。\r\n4.净锅，烧热，倒入豆油，待油烧至6成熟时，用筷子', '色形美观，猪肉嫩软，味道鲜香。', NULL, NULL, '猪里脊肉200克，罐头竹笋25克，粉丝15克，鸡蛋清1只，豆油500克（实耗100克），酱油30克，精盐2.5克，味精2克，淀粉35克，料酒15克，辣大酱15克，芝麻油10克，鸡汤150毫升。');
INSERT INTO `recipe` VALUES (2367, 82, '卷筒狗肉', '1.将狗后腿肉放入清水中浸泡，洗净，置于砧墩上，切成7厘米长与竹帘子棍一般粗细的丝；大葱、生姜去皮，洗净，均切成细丝；狗网油用清水冲洗干净，晾干水，切成3块，置于砧墩上摊平，撒上精白面粉25克，将狗肉丝、葱丝、姜丝放入瓷盆内，加入7.5克料酒、精盐、味精、香油和胡椒粉，拌匀，分放在3块网油上，卷成3个圆筒形。\r\n2.鸡蛋打入瓷碗中，加入7.5克料酒、精盐、味精，用筷子打匀后，兑入清水50毫升，放入清白面粉50克，细干淀粉，用竹筷搅拌均匀上劲，见无粉块即好。\r\n3.炒锅上火烧热，加入植物油，烧至五成热时，将', '色泽金黄，外皮松脆，果肥嫩鲜。', NULL, NULL, '狗后腿肉200克，狗网油200克，鸡蛋100克，精白面粉75克，细干淀粉50克，料酒15克，精盐2克，植物油1.5公斤（实耗100克），大葱10克，生姜2.5克，香油15克，胡椒粉2. 5克，辣酱油25克，芝麻盐15克。');
INSERT INTO `recipe` VALUES (2368, 82, '咖喱鸡', '1.先制出咖喱油：炒锅烧热，放入200克豆油，烧至六成热时，放入100克葱头末，煸炒出香味时，再放入50克大蒜泥和250克咖喱粉，用小火炒匀，待炒透时，加入清水，用手勺调匀，如需咖喱油浓些，可用适量面粉加清水调匀后，加入咖喱油中，炒透，即可盛入容器中，待用。\r\n2.把肥嫩生鸡摘洗干净，斩去头颈、尾脚，用刀从鸡背上顺长剖开，切成2片，斩去脊背骨，再把刀从鸡的腿、脯中间切断，共切成4块。去皮山芋洗净，切成菱形块。\r\n3.炒锅上火烧热，放入豆油，烧至七成热时，放入鸡块过油，炸至鸡块外皮紧缩变色，随即用漏勺捞出，', '色泽黄亮，味香稍辣，酥烂不糊。', NULL, NULL, '肥嫩生鸡1.5公斤，去皮山芋750克，鸡汤3公斤，料酒60克，精盐30克，味精7克，白糖15克，豆油2公斤（实耗150克），咖喱油250克，葱段50克，生姜25克，水淀粉25克，芝麻油25克，芝麻面25克。');
INSERT INTO `recipe` VALUES (2369, 82, '烤牛排', '1.把牛排骨（连肉带骨）切成5厘米长的小块，骨朝下，用快刀在肉上剁十字花刀，以让调味汁渗进去。把各种调料都放在大瓷盆里，倒入排骨抓匀，盖上盖，入冰箱冷冻8小时。\r\n2.再把排骨放在炙子上，骨头朝下，烤至黄褐色，再烤另一面，直到烤熟为止，烤时要不断翻动，以使排骨各面的肉都熟透，香酥，可用手拿着吃。\r\n3.烤排骨也可用烤箱烤，每个烤盘只放一层排骨，用中火烤1小时，中间翻转一次，即可。', '色泽金黄，牛肉香嫩，甜咸微辣。', NULL, NULL, '牛排骨2000克，酱油125克，清水125毫升，葱末28克，大蒜泥5克，生姜末5克，白糖30克，黑胡椒面2.5克，芝麻面6克（焙好）。');
INSERT INTO `recipe` VALUES (2370, 82, '烤牛肉饼', '1.将净牛肉剔筋膜，和猪肥膘一起剁烂成泥茸或用绞肉机把两种肉绞两遍成碎泥茸。\r\n2.鲜面包用牛奶浸泡一下，取出，挤干牛奶汁与鸡蛋液，盐、胡椒粉混合后搅拌均匀制成直径约10厘米、厚约3毫米的圆形牛肉饼。\r\n3.煎锅内放植物油，烧热，放入圆形牛肉饼，用微火慢慢将两面煎黄，待外部基本煎上色后，放入烤盘，浇上辣酱油、西红柿汁、黄油和清汤，放入烤炉内烤熟。\r\n4.食用时，趁热装盘，浇上烤汁，旁边配熟土豆泥一同食用，即可。', '外脆里嫩，汁浓味香，松软嫩鲜。', NULL, NULL, '净牛肉500克，牛奶250毫升，鸡蛋2个，猪肥膘150克，胡椒粉2.5克，新鲜面包50克，植物油125克，土豆泥500克，辣酱油25克，西红柿汁50克，黄酒25克。');
INSERT INTO `recipe` VALUES (2371, 82, '烤牛肉', '1.将牛臀尖洗净，剔净筋膜，放入冰箱冷冻。\r\n2.取出冰箱内的牛臀尖，切成大块薄片，用刀背轻轻拍平后再切成中等方块。\r\n3.取一瓷盆，将酱油、清水、大葱末、大蒜泥、生姜末、白糖、黑胡椒面、焙好的芝麻面等调料放入，调好搅匀，放入牛肉腌渍4小时，如果放在冰箱里，时间要长一些。\r\n4.烤牛肉的工具有好几种，用铁丝编成的架子，可把肉片放在上面烤，用铁签把肉片串起来烤，烤时可在肉片中间加上蘑菇、青椒、洋葱、红萝卜或其它食品。\r\n5.渍入味的牛肉片，放在炭火上烤熟后，将渍汁分装在各人的小碗里，蘸汁。', '味道鲜香，牛肉软嫩，焦香扑鼻。', NULL, NULL, '牛臀尖1000克（或黄瓜肉），生姜末1克，大葱末15克，大蒜泥1克，白糖15克，黑胡椒面1克，焙好芝麻面，渍汁（制法附后），蘑菇100克，青椒100克，洋葱100克，红萝卜100克。');
INSERT INTO `recipe` VALUES (2372, 82, '口蘑烩全狗', '1.把熟狗肉脊髓切片，熟狗脑切块，狗舌切条，下水切片，再一起放入开水锅中焯透，捞出，控水。\r\n2.将口蘑放入瓷碗中，用开水沏泡后，捞出，碗中口蘑汤澄清，备用。然后再加上精盐，用手揉搓，去掉泥沙，用清水洗净，放入开水中焯透，捞出，控水。\r\n3.再将炒锅上火，加入狗骨清汤、口蘑汤。把鸡肉茸放入碗内，加入少许清水调开，倒入炒锅的汤中，待鸡肉茸受热浮起，捞出（另做它用）。\r\n4.把炒锅的汤倒出再过箩，澄清，再倒回炒锅内，加入味精、料酒、碱面、口蘑，控去水的各种全狗料，烧开，撇去浮沫，倒入辣椒油、姜汁，用淀粉勾成流', '料材丰盛，汤味醇厚，鲜咸清香。', NULL, NULL, '全狗料500克（熟狗脊髓、狗眼、狗脑、狗舌、下水等），口蘑25克，鸡肉茸50克，精盐5克，狗骨清汤1千克，淀粉15克，胡椒粉2.5克。');
INSERT INTO `recipe` VALUES (2373, 82, '蜡石烤牛肉', '1.把生牛臀尖去筋膜，洗净，放入冰箱冷冻半小时。\r\n2.取出牛臀尖，切成2毫米厚的片，要切得薄而整齐。\r\n3.瓷盆中放入酱油、清水、大葱末、大蒜泥、生姜、白糖、胡椒面和焙好的芝麻面等各种调料，调好，拌匀，放入切好的薄牛肉片腌4个小时，捞出，控干，用盘子装好，放在餐桌上待用；再将辣大酱分盛在每个人的碗里。\r\n4.把几块光滑的蜡石（约长15厘米，宽8厘米，厚5厘米），放在电炉或炭火上烧，待石头烧到赤热时，放在铁盘里，分放在客人面前，每人用不锈钢筷子夹牛肉片在石头上翻烤，烤熟后，蘸辣大酱吃。', '味香肉嫩，烤法别致。', NULL, NULL, '牛臀尖500克（或黄瓜），辣大酱20克，芝麻面2克（焙好），酱油35克，大葱10克，大蒜泥1克，生姜8克，白糖8克，胡椒面1克，清水30毫升。');
INSERT INTO `recipe` VALUES (2374, 82, '辣炒干贝', '1.去掉干贝内鳃，洗净，由上而下片为5毫米的圆形片；蘑菇水发后，洗净，去蒂根与罐头竹笋均匀切成片，大葱去皮，生姜去皮，大蒜去皮，洗净，均剁成碎末，待用。\r\n2.将干贝片、竹笋、蘑菇、青豆放入开水锅中，焯过，控干。\r\n3.炒锅烧热，放入花生油，烧至五成热，放入辣椒面、大葱末、生姜末、大蒜末烹炒，加入干贝片、竹笋、蘑菇、青豆，翻炒几下，加入酱油、味精、料酒，调好口味，加少许淀粉勾芡，即可。', '口味香辣，干贝鲜嫩，滑爽适口。', NULL, NULL, '干贝肉200克，罐头竹笋15克，蘑菇15克，青豆20粒，花生油40克，料酒15克，淀粉1.5克，鸡汤50毫升，大葱15克，生姜10克，大蒜10克，辣椒面2克，酱油15克，味精2克。');
INSERT INTO `recipe` VALUES (2375, 82, '牡丹虾仁', '1.将青鱼肉去皮骨，洗净，放在洁净的砧墩上，用双刀轻轻地剁成极为细腻的泥茸，放入瓷碗内，加入10克料酒、2.5克精盐、1克味精、25克鸡蛋清、少许葱姜水及少许清水，用3根竹筷用力向一个方向搅拌，一直搅拌至细腻上劲时为好，待用。\r\n2.再将鱼茸用手挤成大小均匀的鱼丸6个（约25克1个），待用。\r\n3.把竹笋顶刀切成圆形的薄片，用镊子夹信笋片，一片片地插入鱼圆中，做成牡丹花形，把火腿切成末，点缀在花的中心，上蒸锅，用旺火蒸5分钟，保温，待用。\r\n4.将明虾肉洗净，控干水，放在砧墩上，用刀片去外层虾肉（留作它用', '洁白光亮，美观大方，鲜香滑嫩。', NULL, NULL, '明虾肉400克，青鱼肉150克，罐头竹笋100克，火腿3克，鸡蛋清75克，料酒25克，细干淀粉35克，精盐6克，味精2.5克，豆油1.5公斤（实耗100克），芝麻油10克，葱姜水15克。');
INSERT INTO `recipe` VALUES (2376, 82, '奶卤虾仁', '1.将虾仁漂洗干净，控干水。\r\n2.再将鸡蛋清放入碗中，用竹筷稍打，加入虾仁用手向一个方向搅拌片刻，加入1克精盐，拌匀后，再加细干淀粉拌匀，上浆。\r\n3.把火腿、鸡脯肉、冬笋、冬菇置于砧墩上，切成同青豆大小的丁。\r\n4.炒锅上火烧热，用油滑锅后，加入花生油，烧至四成热时，倒入虾仁，用手勺轻轻滑散，随即放入火腿丁、鸡脯丁、竹笋丁、冬菇丁和青豆，用手勺翻几个身，滑至虾仁变成乳白色时，倒入漏勺内，控干油。\r\n5.净锅，烧热，加入牛奶、料酒、2克精盐、味精、白糖，倒入虾仁、火腿丁、鸡脯丁、竹笋丁、冬菇丁和青豆，烧', '乳白发亮，五色俱全，清鲜滑嫩。', NULL, NULL, '虾仁150克，火腿15克，熟鸡肺肉15克，罐头竹笋15克，水发冬菇15克，青豆15克，鸡蛋清30克，牛奶250毫升，细干淀粉15克，花生油750克（实耗60克），料酒15克，粗盐3克，味精1.5克，白糖0.5克，水淀粉15克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2377, 82, '蘑菇海参', '1.水发海参去内脏，洗净，切成5毫米宽的长斜条；罐头竹笋切丝；蘑菇洗净，去蒂根，一切两半；大葱去外皮，洗净，切成2厘米长的段；姜剁碎末；青豆洗净，待用。\r\n2.将海参放入开水锅，换水焯三次，去掉腥味；竹笋、蘑菇和青豆分别用开水焯一次。\r\n3.炒锅烧热，放入50克花生油，烧至五成热时，下入大葱段、生姜末煸炒出香味，烹入料酒、酱油和少许清水。待汤烧开后，撇去浮沫，加海参、青豆、蘑菇、竹笋、盐和味精，烧开后，用淀粉勾芡，淋上芝麻油，入盘，撒上芝麻，即可。', '海参滑润，口味香美，配料可口。', NULL, NULL, '水发海参250克，蘑菇150克，罐头竹笋50克，青豆25克，花生油50克，料酒20克，精盐5克，酱油25克，大葱15克，酱油25克，芝麻油10克，味精2克，淀粉10克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2378, 82, '南瓜酱汤', '1.鸡肉洗净去筋膜，切成6厘米长的丝，用酱油、葱丝、蒜茸、白糖、胡椒面、精盐腌渍入味，待用。\r\n2.将嫩南瓜一切4瓣，去皮，去籽，去蒂根，切成3厘米见方的块；小青椒、鲜辣椒洗净，去蒂根，均切成丝。\r\n3.炒勺烧热，放入花生油，烧五成热时，放入腌渍入味的鸡肉丝煸炒一下，放入嫩南瓜、小青椒、鲜辣椒、葱白丝，拌匀，烧开即成。', '口味香辣，南瓜嫩鲜，汤浓醇美。', NULL, NULL, '嫩南瓜500克，鸡肉100克，小青椒25克，大葱白100克，鲜辣椒25克，辣椒酱15克，黄豆酱10克，清米汤500毫升，酱油10克，大葱丝10克，大蒜茸5克，白糖15克，胡椒面3克，精盐2克，花生油25克。');
INSERT INTO `recipe` VALUES (2379, 82, '泥鳅穿豆腐', '1.活泥鳅放入淡盐水中养两天，使其吐尽污泥。\r\n2.牛肉洗净，去筋膜，切成6厘米长的细丝。\r\n3.大葱去皮洗净，切末；生姜去皮洗净，切片；大蒜去皮洗净，拍碎。\r\n4.把活泥鳅从淡水中捞出，洗净放入煮锅内，加上清水，再将豆腐洗净，放入锅内，大火烧锅，随着水温升高，泥鳅挣扎，钻入豆腐内，待汤烧开2分钟后将牛肉丝、料酒、大葱末、生姜片、大蒜末放入锅内，加盐、料酒、味精调好口味，加芝麻油和醋，烧开即成。', '泥鳅鲜美，豆腐嫩软，别具风味。', NULL, NULL, '活泥鳅200克，豆腐500克，牛肉150克，大葱20克，精盐10克，生姜10克，辣椒油5克，大蒜10克，料酒20克，醋20克，味精5克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2380, 82, '酿海盖', '1.将螃蟹洗净，上笼蒸15分钟左右，取出，晾凉，揭开蟹盖，取出蟹肉，待用。\r\n2.再将猪肉洗净，去筋膜，剁碎，再将冬菇、竹笋、面包均切成碎米丁；海米用水泡过，洗净，也切成碎米丁。\r\n3.将猪肉末与螃蟹肉放在一碗内，并加入科菇丁、竹笋丁搅拌均匀，然后加入盐、糖、胡椒面、料酒、蛋清、玉米粉、花生油一起，拌匀成馅。\r\n4.把拌匀的馅放入洗屈的蟹盖内，抹成圆形，再抹上蛋清玉米粉糊，撒上面包渣。\r\n5.炸锅放入花生油，烧二成热时，逐只放入蟹盖油浸炸透，待油五成热时，放入蟹盖再炸5分钟左右，炸成金黄色，捞出，控油。\r', '外焦里嫩，鲜香味美，蟹香郁浓。', NULL, NULL, '螃蟹1公斤，猪瘦肉425克，罐头笔笋150克，水发冬菇150克，水发海米25克，面包150克，鸡蛋3个，盐7.5克，糖7.5克，玉米粉50克，胡椒面2克，花生油250克，料酒10克，生姜10克，醋50克，鸡蛋清2个。');
INSERT INTO `recipe` VALUES (2381, 82, '牛肉丁豆腐', '1.将豆腐冲净，切成2厘米见方的丁，倒入开水锅中焯一下，捞出，控干。\r\n2.牛肉洗净，去筋膜，切成5毫米见方的丁，放入盆内，加蛋清、盐、味精和淀粉，抓匀。\r\n3.大葱去皮，洗净，切成2厘米长的段；生姜去皮，洗净，切碎末，待用。\r\n4.炒勺炒热，放入25克花生油，烧五成热时，放入牛肉丁，稍煸后倒出，待用。\r\n5.将余下25克花生油倒入炒勺内，烧热，放入葱段、姜末、大酱，煸炒几下，再放入豆腐丁、牛肉丁，炒匀后，放入水淀粉勾芡即可。', '色泽金黄，牛肉醇香，豆腐嫩软。', NULL, NULL, '豆腐250克，牛肉50克，花生油50克，鸡蛋清1个，料酒15克，白糖10克，酱油25克，盐2克，大酱10克，大葱10克，味精2克，生姜5克，淀粉10克。');
INSERT INTO `recipe` VALUES (2382, 82, '牛肉泡菜汤', '1.胡萝卜去皮，洗净，切丝；白菜泡菜切丝；大葱去皮，洗净，切丝；土豆去皮，洗净切角块；松蘑水发后，洗净，切丝；生姜去皮，洗净，切丝；煮牛肉切丝条。\r\n2.炒锅烧热化牛油，放入大葱丝、生姜丝炒出香味，放入胡萝卜丝、土豆块、煸炒后，放入牛肉清汤，加辣大酱，焖九成熟。\r\n3.再将泡菜丝、牛肉丝条放入，烧开，加盐、醋、味精、胡椒面、松蘑丝，调好口味，煮熟后，下入水淀粉勾琉璃芡，淋入芝麻油。\r\n4.食用时，汤盘内放2块煮牛肉丝条，泡菜丝及各种蔬菜与汤汁即可。', '鲜咸香辣，酸嫩爽口，美味宜人。', NULL, NULL, '白菜泡菜500克，土豆250克，松蘑100克，辣大酱100克，牛油150克，煮牛肉250克，牛肉清汤3000克，醋50克，盐20克，胡椒面5克，生姜10克，味精3克，大葱20克，芝麻油10克，淀粉15克。');
INSERT INTO `recipe` VALUES (2383, 82, '牛肉清汤', '1.将牛肉洗净，切成6厘米长，2厘米宽的条，把牛棒骨用砍刀砸断，洗净；将芹菜去根，洗净，把葱头、胡萝卜去皮，洗净，用刀切两半，放在炉板上，烤成深黄色，以便提色和保持汤正。\r\n2.往煮锅内入2500毫升清水，把牛肉和牛骨放入在旺火上煮开，把血沫撇去再稍煮片刻，改小火浸煮，同时，将葱头、胡萝卜、芹菜放入锅内，因牛肉、牛骨血污多，要边煮边打泡沫，否则会使汤色不正而发腥味。\r\n3.煮3个小时后，用叉子叉一下牛肉，能叉进去，即为熟透，将牛肉取出，将汤用箩或细布过滤，清除杂质。\r\n4.煮锅净后，放入牛肉汤、牛肉条、生', '汤清味郁，味鲜不腻，色泽淡黄。', NULL, NULL, '牛腱子肉500克，葱头1个，牛骨500克，胡萝卜50克，生姜10克，芹菜25克，酱油50克，精盐5克，味精3克，辣椒油2克，清水2500毫升，芝麻（焙好）。');
INSERT INTO `recipe` VALUES (2384, 82, '牛肉汤', '1.先制出牛肉汤汁：把45克酱油和16克芝麻油倒入瓷盆中，再加入8克大酱和4克辣椒油，接着放入10克大蒜泥茸，3克盐和24克，再加入1200毫升清水，搅拌均匀，制成牛肉汤汁。\r\n2.再把牛臀尖洗净，去筋膜，切小丁；大葱去皮，洗净，切成5厘米长的条，然后，再直着切；大蒜去皮，拍碎末，待用。\r\n3.旺火烧热炒锅，放入豆油，烧五成热时，放入牛肉丁，炒至黄褐色，加上大葱、生姜末、水发蘑菇末，翻炒2分钟，再加入辣大酱，制好的牛肉汤汁料，酱油以及精盐、料酒，烧开后搅拌均匀，改小火煨焖10分钟，调好口味，放黑胡椒粉。倒', '口味香辣，牛肉细腻，汤汁可口。', NULL, NULL, '牛肉汤水1500毫升，牛臀尖250克（或牛颈肉），大葱30克，大蒜2瓣，豆油40克，蘑菇4朵，辣大酱8克，酱油30克，精盐15克，料酒15克，黑胡椒粉2.5克，芝麻油8克。');
INSERT INTO `recipe` VALUES (2385, 82, '牛尾汤', '1.将松蘑水发后洗净，切成3毫米厚的伞形片；干辣椒洗净，去蒂根，去籽；葱头去头洗净，切成1.5厘米见方的丁；罐头竹笋也切成与葱头同样大小的丁。\r\n2.将牛尾去净污物，洗净，剁成段，放入开水锅内，烫出血沫，捞出，洗净，再放入锅内净水，入牛尾，烧开，撇去浮沫，加大料、干辣椒及胡椒粉，煮4个小时后，中间撇两次浮油和浮沫，待牛尾段熟后，捞出，洗净。\r\n3.煮锅净后放入热鸡汤，烧滚沸后，放入牛尾段，保温。\r\n4.焖锅内放花生油，烧五成热，投入葱头丁，炒至断生，放入桦蘑片、竹笋片，稍煸炒后，把煮牛尾汤过箩后，倒入焖锅', '配料鲜嫩，汤清透亮，醇美芳香。', NULL, NULL, '牛尾750克，罐头竹笋250克，松蘑75克，豌豆100克，热鸡汤1500毫升，花生油25克，精盐10克，胡椒面4克，味精2克，干辣椒2个，料酒50克，葱头50克，芝麻15克（焙好），大料3颗。');
INSERT INTO `recipe` VALUES (2386, 82, '糯米全鸡', '1.笋母鸡宰杀放血，褪毛，去内脏，由颈部脱皮，皮不要弄破，将鸡肉剔下，切成小丁，火腿、冬菇也切成小丁；糯米用鸡汤蒸至五成熟。整鸡洗净，浸泡清水中，捞出，控水。\r\n2.炒锅烧热，放入鸡油，烧五成热，放入鸡肉、冬菇丁、火腿丁煸炒一下，放入料酒、盐、糖、一半大酱，炒匀，然后放入糯米，拌匀，装入瓷碗中，待用。\r\n3 .将馅从开口处装入鸡腹，开品处用线缝好，将鸡身抹上余下的大酱，放在瓷盆内，蒸30分钟左右。\r\n4.将鸡取出，抹匀玉米粉，放入五成热的豆油锅中，炸成黄色，捞出，控干，鸡脯向上放在盘中，用生菜叶围边，放上', '色泽金黄，外脆里嫩，味道香美。', NULL, NULL, '笋母鸡1只（约1.5公斤），糯米150克，火腿25克，生菜25克，水发冬菇15克，萝卜花一朵，盐10克，料酒10克，糖12.5克，玉米粉12.5克，大酱7.5克，鸡油50克，豆油250克，芝麻盐25克，鸡汤500毫升，辣椒酱25克。');
INSERT INTO `recipe` VALUES (2387, 82, '枇杷狗肉', '1.狗腿肉洗净，浸泡后捞出，去筋膜，青鱼肉用清水洗净，置于砧墩上，用双刀轻轻斩成细茸，放入瓷盆内，加入鸡蛋、料酒、精盐、味精和葱末、生姜汁，用3根竹筷顺一个方向搅拌均匀，上劲即好。用手挤成24个圆子，滚上细干淀粉，竹笋切面4厘米长的小条24根，插入每个圆子作枇杷柄。\r\n2.鸡蛋黄放入瓷碗内，加入少许清水，放入富强粉，用竹筷搅拌成干稀适中的蛋糊；再把菠菜择洗干净，控干水，切段。\r\n3.炒锅上火烧热，加入花生油，烧至五成热，将锅端离火口，放稳，将枇杷肉蘸上蛋黄糊逐个下入油锅，再将锅端到小火上慢慢的炸，一定不要', '形似枇杷，色泽鲜艳，甜酸适口。', NULL, NULL, '狗腿肉150克（瘦七肥三），青鱼肉150克，罐头竹笋50克，菠菜200克，鸡蛋液25克，细干淀粉100克，鸡蛋黄50克，富强粉100克，狗骨白汤50毫升，料酒10，花生油1公斤（实耗75克），精盐3克，味精3克，白糖100克，番茄酱50克，米醋25克，水淀粉5克，葱末15克，姜汁5克。');
INSERT INTO `recipe` VALUES (2388, 82, '平菇鲫鱼汤', '【制作过程】   \r\n1.将平菇去根蒂洗净；鲫鱼宰杀，去鳃、鳞、内脏，冲洗干净。\r\n2.鸡肉去筋膜，洗净，切2厘米方丁。\r\n3.汤锅内放入鸡汤再加适量清水，将鲫鱼入锅，加葱末、姜末。用微火煮至八成熟时，再把平菇片放入汤中煮开，盛入盆中加酱油、精盐、味精、白糖、醋、芝麻油拌匀，撒上洗净的豌豆苗即可。', '平菇肥美，鲫鱼嫩鲜，宜于产妇。', NULL, NULL, '鲜平菇250克，鲫鱼2条（约300克左右），鸡肉50克，酱油5克，醋50克，白糖25克，精盐1克，味精1克，芝麻油3克，葱末5克，姜末5克，碗豆苗10克，鸡汤1000毫升。');
INSERT INTO `recipe` VALUES (2389, 82, '苹果炒牛肉片', '1.苹果洗净，去蒂，去皮，去核，切成薄片，浸泡在清水中，以免锈黑。\r\n2.大葱去皮，洗净，切3厘米的段。\r\n3.牛肉去筋膜，洗净，切成长4厘米、宽2厘米的薄片，放入瓷碗中，加入5克酱油、盐、糖、味精和酒拌和，然后分3次加入75毫升清水，顺一个方向搅拌，直到水全部被牛肉吸收，最后拌入5克干淀粉和小苏打粉，静置2小时。\r\n4.炒锅上火烧热，放入豆油500克，烧至五成热时，放入牛肉片，用筷子划散，捞出，控油，把余油倒入油罐。\r\n5.炒锅留少许底油，烧热，放入大葱段，待炒出香味后，放入酱油、糖、小苏打粉、料酒、精', '色泽淡红，牛肉绿嫩清爽，苹果清香脆嫩。', NULL, NULL, '牛腿肉300克，苹果2个，酱油20克，糖12克，小苏打粉2克，料酒15克，精盐5克，味精2克，淀粉15克，大葱20克，芝麻10克（焙好），鸡汤150毫升，豆油500克（实耗50克）。');
INSERT INTO `recipe` VALUES (2390, 82, '牛肉丸汤', '1.将牛里脊片去筋，用刀剁成细馅，放入瓷盆中，加入酱油及适量的清水、精盐，用筷子向一个方向搅至肉馅成粘稠状，再加入鸡蛋清、淀粉、葱末、姜末、芝麻油，搅拌均匀。\r\n2.将汤勺上火，加入清水，烧开后，把汤勺端离火口，用手将肉馅挤成小丸子，下入汤勺中，再将汤勺上火，用微火把小丸子煮熟，捞出后，倒掉汤。\r\n3.再将汤勺坐上火，加入牛肉清汤、料酒、味精、酱油、精盐和小丸子，烧开后撇去浮沫，撒入辣椒油，加入黄瓜片、豌豆苗叶，盛入汤碗，撒上焙好的芝麻即可。', '汤鲜味美，肉嫩醇香，咸辣适口。', NULL, NULL, '牛里脊肉250克，黄瓜片50克，鸡蛋清50克，豌豆苗叶5克，芝麻油25克，淀粉40克，料酒15克，味精5克，酱油10克，辣椒油1克，芝麻10克（焙好），牛肉清汤1公斤，大葱末10克，生姜末5克。');
INSERT INTO `recipe` VALUES (2391, 82, '牛杂碎汤', '1.将牛心、牛肝、年肺、牛肚去筋膜污物，用盐醋碱水洗净，浸泡半天后，捞出，冲洗多次，使其无杂质，无粪味。\r\n2.再将牛头肉、牛脚剔净小毛后，将净牛骨洗净，砸断。\r\n3.大煮锅内，放入牛下水、牛头肉、牛脚、牛骨头，加6倍的清水，用旺火烧开，改用小火焖，撇去沫子。\r\n4.待肉焖熟后，捞出，趁热从骨头上剔下肉，切成小块，将骨头仍放进锅内去熬煮6个小时，熬至成浓汤。\r\n5.大葱去皮，洗净，剁成碎末，与辣椒面、胡椒面、盐搅拌均匀，装在每个小碟内，待用。\r\n6.食用时，先将杂碎肉切碎后，放入碗中，再浇上热浓汤，根据个', '味道鲜美，杂碎烂软，汤浓郁美。', NULL, NULL, '牛下水1000克（心、肝、肺、肚），牛头肉1000克，牛脚1000克，大葱200克，牛骨头2000克，辣椒面10克，胡椒面4克，精盐20克。');
INSERT INTO `recipe` VALUES (2392, 82, '烹鹌鹑', '1.将鹌鹑煺毛、去头、爪和内脏，每只剁成4块，拌上面粉； 大葱去皮，洗净，切成长2厘米的段。\r\n2.再将油倒入锅内，烧至七成热时，放入鹌鹑块，炸2分钟，捞出，控干油，余油倒入油罐内。\r\n3.炒锅内留底油，烧热，投入葱段，煸炒几下，加青豆、鹌鹑块，加料酒、白糖、酱油、醋、味精、翻炒几下，淋上芝麻油，撒上芝麻即可。', '味甜汁浓，肉鲜嫩香，桂圆滑润。', NULL, NULL, '鹌鹑5只，青豆15克，面粉50克，豆油500克（实耗75克），芝麻油15克，料酒10克，酱油50克，醋15克，白糖20克，味精1.5克，芝麻10克。');
INSERT INTO `recipe` VALUES (2393, 82, '软溜狗肉片', '1.将狗肉放入凉水中浸泡4小时，捞出，控水，剔去筋膜，剞上浅花刀，再切成方丁，放入瓷碗中，加入鸡蛋清、淀粉、精盐，用手抓匀浆好。\r\n2.再将汤勺上火，加入清水烧开，把汤勺端离火口，再把浆好的狗肉丁下入汤勺中烫一下，倒入漏勺中，滗去水。\r\n3.原汤勺上火，加入白汁、狗骨汤、味精、料酒、咸面和狗肉丁，烧开后仍把汤勺端离火口，待狗肉丁入味后，倒入漏勺，控掉汤汁。\r\n4.净汤勺后上火，加入牛奶、味精、料酒、咸面及狗肉丁，烧开后用淀粉勾芡，淋入鸡油，颠翻几下，出勺，盛入盘中即成。', '色泽乳白，狗肉软嫩，味鲜咸香。', NULL, NULL, '狗脯肉250克，鸡蛋清25克，花生油500克（实耗40克），白糖50克，香油15克，料酒10克，淀粉50克，醋15克，大葱15克，生姜10克，精盐10克。');
INSERT INTO `recipe` VALUES (2394, 82, '软炸番茄', '1.把酱油、芝麻面、黑胡椒、大葱末和芝麻油放入瓷碗中，搅拌均匀成渍汁，待用。\r\n2.再把西红柿洗净，用开水烫后，去皮，去蒂，剖开去籽，切成2厘米厚的片，然后在西红片逐片挂上渍汁糊，下入炒锅内，炸至呈微黄色时，用漏勺捞出，控油。\r\n3.如拌匀的渍汁内打入鸡蛋清，加入余下的淀粉和面粉，搅拌均匀成渍汁糊。\r\n4.炒锅烧热放入豆油，旺火烧至三四成热时，将粘好面粉的西红柿片逐片挂上渍汁糊，下入炒锅内，炸至呈微黄色时，用漏勺捞出，控油。\r\n5.食用时，趁热上盘，配一碟辣椒酱同食，即可。', '松香饱满，色泽淡黄，酥脆软嫩。', NULL, NULL, '西红柿250克，鸡蛋3个，豆油750克（实耗50克），精盐2克，右粉25克，酱油30克，淀粉25克，芝麻面6克（焙好），黑胡椒粉5克，大葱15克，芝麻油16克。');
INSERT INTO `recipe` VALUES (2395, 82, '氽兔肉圆', '1.将野兔肉放水中浸泡4小时，去掉血沫和草腥味，洗净，去筋膜，用双刀剁成泥茸。\r\n2.再将兔泥茸放入瓷盆中，加入料酒、蛋清、精盐、5克芝麻油、淀粉搅拌均匀岩层馅心。\r\n3.调匀的馅心，挤成3厘米的丸子，入开水锅中，氽一下，捞出，过凉。\r\n4.把黄瓜、火腿均切成象眼片；大白菜限帮，洗净，切象眼片，用开水焯烫一下出，过凉；水发桦蘑择洗干净切薄片；香菜摘洗干净，切段；金针菜用水发透，择洗干净，挤去水，切段；大葱、生姜去皮，洗净，均切成丝；水发银耳择洗干净，切片，待用。\r\n5.砂锅洗净后，放入鸡汤、葱姜丝和各种配', '汤汁乳白，丸子嫩糯，味道鲜美。', NULL, NULL, '野兔肉500克，黄瓜片15克，水发海米15克，火腿10克，大白菜100克，水发银耳50克，松蘑50克，鸡汤1500毫升，香菜10克，金针菜25克，蛋清100克，生姜10克，芝麻油15克，大葱10克，味精2克，料酒15克，胡椒面2克，淀粉50克，醋5克。');
INSERT INTO `recipe` VALUES (2396, 82, '煨鸡块', '1.鸡肉洗净，控干，剁成3厘米见方的小块，以一口一块为宜。\r\n2.把鸡块放进大锅里，加上酱油、芝麻油、大蒜、辣椒面、大葱和盐，搅拌均匀，大火烧开，小火煨焖2个小时，直煨至鸡块焖烂为止。\r\n3.食用时，配热大米饭，泡菜一起吃。', '口味香辣，肉细嫩软，鲜味扑鼻。', NULL, NULL, '鸡肉1000克，酱油125克，芝麻油40克，大蒜15克，辣椒面0.5克，大葱15克，精盐2克，泡菜50克。');
INSERT INTO `recipe` VALUES (2397, 82, '虾仁豆腐', '1.将豆腐切成长3厘米，宽2厘米，厚1厘米的条；大葱去皮，洗净，切成2厘米长的段；生姜去皮，洗净，切末，待用。\r\n2.炒锅烧热，放入豆油，待七成热时，放入豆腐条，炸成两面呈金黄色时，捞出，控油，倒去余油。\r\n3.炒锅内放入25克豆油，旺火烧热，放入葱段、姜末、煸炒几下，下入虾仁，煸炒几下，加酱油、料酒、糖少许，烧开后，放入豆腐，改小火焖3分钟，加味精，用水淀粉勾芡，淋上辣椒油，即可。', '色泽鲜艳，口味辣鲜，促进食欲。', NULL, NULL, '豆腐500克，虾仁100克，豆油250克，料酒15克，酱油50克，精盐5克，糖10克，葱15克，蒜10克，辣椒油5克，味精3克，淀粉8克。');
INSERT INTO `recipe` VALUES (2398, 82, '苹果焖牛肉片', '1.牛腱子肉洗净，剔净筋膜，切成20片，每片约87.5克，拍薄，撒上盐、胡椒粉，沾匀面粉。\r\n2.苹果洗净，去皮、籽、蒂，切成小块，撒在盘内，洋葱去皮，切长丝。\r\n3.炒锅放入豆油，烧热，放入牛肉片，煎成两面呈金黄色时，捞出，控油。\r\n4.烤盘内放一层苹果块，上面码好煎过的牛肉片，再撒上苹果片。\r\n5.洋葱去皮，洗净，切成丝后，加油炒出香味，放入牛肉汤，加盐调好口味，浇在码好牛肉的烤盘内，送入烤炉内焖至牛肉熟软，撒上焙好的芝麻。\r\n6.食用时，入盘，盘边配上土豆泥，即可。', '果味浓郁，牛肉酥嫩，清香适口。', NULL, NULL, '牛腱子肉1750克，苹果1000克，洋葱250克，精盐15克，豆油250克，土豆泥1250克，面粉50克，黄油100克，胡椒粉3克，牛肉汤1000毫升，芝麻20克（焙好）。');
INSERT INTO `recipe` VALUES (2399, 82, '荠菜炒鸡片', '荠菜，又叫荠、大荠、地儿菜、香荠、菱角菜等，多年生草本，株长20-30厘米，4月开花，含有维生素A及其他多种维生素，无机物主要以钙和铁两种成分为最多，呈碱性，是可食用的含蛋白质最高的山菜之一，另外还含胆碱、乙酰基胆碱、番木鳖碱等成分，有利尿、止血、解毒的功效，可治高血压、便秘、肝硬化，并对癌病变有一定的抑制作用。荠菜的烹制方法较多，可拌，可炒，可烩，可汤，还可用为作馅，虽是野蔬，味道却极其鲜美。\r\n1.荠菜剪去根，摘去老叶，洗净，下入开水锅中焯一下，捞出，再放入冷水中投凉，挤去水分，切成细末，待用。\r\n2', '荠菜鲜美，鸡片嫩香，清爽适口。', NULL, NULL, '荠菜150克，鸡脯肉250克，罐头竹笋100克，鸡蛋清2个，豆油500克（实耗50克），精盐10克，白糖4克，味精2克，淀粉15克，鸡汤50毫升，芝麻油5克，葱花10克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2400, 82, '青酱泡菜', '1.大白菜去根，掰下菜帮，洗净，控干，切成4厘米见方的块，脆萝卜去头尾，洗净，切成比白菜块薄一点的块。\r\n2.先取100克酱油腌白菜和萝卜块，因萝卜比白菜容易被腌熟透，因此，应先放白菜，过一会儿再放萝卜块。\r\n3.把芥蓝菜和水芹菜择好洗净，只取茎，切成4厘米长的条状，把香菇用水发好后洗净，挤干，切成葱丝状。\r\n4.银耳用水发好，洗净，择好，切小片。\r\n5.板栗去皮，洗净，切成薄片；把大枣泡洗去核，切成4瓣；再把甜柿子与梨去皮，切成同萝卜一样的片块。\r\n6.大葱去皮叶，取葱白部分，切成长4厘米的丝；大蒜、生', '色泽鲜艳，味香咸甜，泡菜脆嫩。', NULL, NULL, '白菜500克，萝卜200克，芥蓝菜100克，水芹菜75克，水发香菇10克，水发银耳5克，板栗120克，大葱120克，大枣25克，甜柿子1个，梨1个，大蒜2头，生姜15克，松仁25克，酱油250克，蜂蜜50克。');
INSERT INTO `recipe` VALUES (2401, 82, '青椒炒猪肉', '1.将青椒去蒂、籽，洗净，切成3厘米长的细丝，放入盐腌渍片刻。\r\n2.再将猪肉切成长3厘米、宽5毫米、厚5毫米的丝，与盐、40克淀粉搅拌均匀，腌渍入味。\r\n3.把酱油、35克淀粉放入碗内，加鸡汤勾兑成芡汁。\r\n4.炒锅烧热，放入25克香油，烧至七成热时下青椒丝，煸炒几下，取出，再放入25克香油，烧热，放入肉丝，煸炒几下，倒入青椒丝，翻炒几下起锅，撒上焙好的芝麻即可。', '色泽美观，肉嫩椒鲜，味美爽口。', NULL, NULL, '猪瘦肉150克，青椒200克，酱油30克，香油50克，鸡汤75毫升，淀粉75克，芝麻5克（焙好），盐3克。');
INSERT INTO `recipe` VALUES (2402, 82, '清炒虾仁', '1.将虾仁洗净，用蛋清、淀粉、盐拌匀浆好。\r\n2.黄瓜洗净与罐头竹笋一起切成菱形片；胡萝卜去皮，洗净，切成小片，用开水烫一下。\r\n3.炒锅烧热，放入花生油，烧至三成热时，投入虾仁，滑透，捞出，控油，倒出余油入油罐。\r\n4.净锅，倒入芝麻油烧至五成热时，放入竹笋片、胡萝卜片、黄瓜片，滑透，捞出，控油。\r\n5.原炒锅烧热，倒芝麻油六成热时，投入葱姜末煸炒出香味，加入虾仁、配料、凋料，炒匀，出盘即可。', '虾肉细嫩，配料脆鲜，清爽可口。', NULL, NULL, '虾仁400克，蛋清2个，黄瓜50克，胡萝卜50克，罐头竹笋50克，花生油500克（实耗50克），芝麻油25克，味精1.5克，料酒50克，淀粉5克，大葱20克，生姜5克。');
INSERT INTO `recipe` VALUES (2403, 82, '清炖狗肉', '1.用凉水把狗肉洗净，放入清水中浸泡一天。\r\n2.把狗肉捞出，冲洗干净，控干，切成1厘米见方的小块丁，放入煮锅里，加入凉水，烧开，捞出，再用清水冲洗三次控干。\r\n3.大葱去皮，洗净，切成块；生姜去皮，洗净，切片，待用。\r\n4.将植物油放入锅里烧热，投入狗肉块丁，用旺火煸炒四分钟，烹入料酒，待水分干生，加入生姜片、葱块、盐和1000毫升左右的清水，烧开后，去沫，改用小火炖熟，加入味精、胡椒粉和熟芝麻面，即成。', '汤清味香，狗肉嫩鲜，原汁原味。', NULL, NULL, '鲜狗肉1000克，大葱40克，生姜15克，植物油75克，胡椒粉2.5克，料酒50克，熟芝麻面10克，味精2克，精盐20克。');
INSERT INTO `recipe` VALUES (2404, 82, '清炖哈士蟆', '1.将哈士蟆放入温水泡2小时左右，在净水中撕去黑丝和杂物，洗净后，放在碗里，待用。\r\n2.净雏母鸡洗净，剔骨，取肉，并将鸡肉切成3厘米见方的块，下锅煮1个钟头左右，在煮的过程中，要把血沫撇净，并加入四分之三的调料。\r\n3.再把哈士蟆放入，加入葱段、姜片，开锅后煮10分钟左右，再把其余的四分之一的调料放入，调好口味，盛入小碗，撒上洗净、消毒的豌豆苗即可。', '味道清鲜，滋养补身，软滑爽口。', NULL, NULL, '哈士蟆20克，净雏母鸡1公斤，盐7.5克，白糖7.5克，胡椒面2克，葱5克，豌豆苗5克，姜5克。');
INSERT INTO `recipe` VALUES (2405, 82, '清汤鲤鱼丸', '1.大葱去皮，一半切段，一半切末；生姜去皮，洗净，切成末；松蘑水发后，洗净，去净蒂根，用开水氽熟透。\r\n2.净鲤鱼肉洗净，剁成鱼泥，放入钵中，加清水300毫米、盐12.5克，顺同一个方向搅至有粘性时再加清水200毫升，拌匀后置放5分钟，然后加入葱末、姜末、味精1.5克，料酒、芝麻油，搅拌均匀成鱼茸，用手挤成核桃大的鱼丸，待用。\r\n3.做鱼丸后，放入开水锅中氽熟，原汤过滤后，倒入汤碗中，放盐2.5克、味精1克，拌匀，盛入松蘑，分成5个小碗，每碗1朵松蘑和汤，再用漏勺轻轻地将鱼丸盛入每个小碗中，再撒匀香菜末即', '鲤鱼细嫩，汤汁清鲜，香菜味浓。', NULL, NULL, '净鲤鱼肉500克，松蘑5朵，大葱段5克，大葱25克，精盐2.5克，生姜1.5克，味精2.5克，料酒5克，芝麻油25克，香菜末10克。');
INSERT INTO `recipe` VALUES (2406, 82, '清蒸八宝鸭', '1.将填鸭净膛后，冲洗干净，在头劲处用刀剖开，力断头骨，将鸭骨先除前肢骨，后除身骨，最后除后肢骨，这样，鸭骨已全部剔除，将皮肉翻转，用清水洗净，开水烫过。\r\n2.再将猪肉丁、鸡肫丁、火腿丁、栗子丁、冬菇丁、冬笋丁和莲子、青豆一起放入瓷盆中，加入料酒10克、精盐5克拌匀，从鸭颈部开口处填入，将鸭颈打一个结，不使原料流出。\r\n3.将填鸭腹向下放入陶瓷容器中，加入15克料酒、7.5克精盐、葱段、姜片、清水1公斤，上蒸锅用旺火蒸3小时，至鸭肉酥烂时，取出，捡去葱段、姜片。将汤滗入锅中上火，再加入鸭汤、7.5克精盐', '酥烂肥嫩，汤汁鲜香，肥而不腻。', NULL, NULL, '白光填鸭1只（约2公斤左右），猪肉丁100克，鸡肫丁100克，火腿丁75克，栗子丁150克，莲子100克，水发冬菇丁50克，罐头竹笋丁50克，青豆25克，火腿片25克，豆苗5根，鸡汤1公斤，料酒25克，葱15克，精盐20克，生姜2片，味精2.5克。');
INSERT INTO `recipe` VALUES (2407, 82, '清蒸甲鱼', '1.活甲鱼宰杀放血后，去苦胆，洗净，剁成块，用葱、姜、料酒、精水入锅氽透，捞在凉水内，洗净，控干。\r\n2.罐头竹笋和姜切成片，大葱切小段，鸡肉去筋膜，剁成泥，用500毫升鸡汤泡上。\r\n3.甲鱼同葱、姜、料酒上蒸锅蒸到能拆骨时，取出，拆去骨后，放入盆内，将水发松蘑洗净泥沙和竹笋整齐地摆上，再放葱、姜、盐、胡椒面、料酒、鸡汤，将盆放入锅内，上旺火蒸烂，取出。\r\n4.将蒸甲鱼汤汁滗入小锅内，上火，将鸡泥汤汁冲入，待鸡泥浮起凝结时，用小眼漏勺捞出，将汤倒入甲鱼盆内，即成。', '甲鱼肉嫩，鲜香味美，清爽可口。', NULL, NULL, '鲜活甲鱼1只（约1000克），水发松蘑10朵，罐头竹笋150克，鸡汤1500毫升，鸡肉150克，大葱20克，生姜10克，料酒30克，精盐2.5克，胡椒面1克，味精1.5克。');
INSERT INTO `recipe` VALUES (2408, 82, '清蒸鲻鱼', '1.松蘑水发后，洗净泥沙，去蒂根，生姜去皮，洗净，与罐头竹笋均切成长2.5厘米、宽1.5厘米、厚1毫米的片；大葱去皮，洗净，切成3厘米长的段。\r\n2.鲻鱼去鳞，洗净，剖腹，去鳃和内脏，在两侧斜剞数刀，放在汤盆里，把松蘑片、竹笋片、姜片放在鱼段上，加入料酒、冰糖、盐，入蒸锅，在旺火蒸约10分钟，取出，去掉姜、葱，将鱼入盘。\r\n3.原盆内汤汁过滤后，加热，再淋上芝麻油，烧开，浇在蒸鲻鱼的身上。', '色泽美观，鲻鱼嫩鲜，笋脆清香。', NULL, NULL, '鲻鱼1500克，松蘑15克，罐头竹笋15克，大葱25克，生姜10克，料酒25克，冰糖10克，精盐5克，芝麻油30克。');
INSERT INTO `recipe` VALUES (2409, 82, '全家福', '1.瘦猪肉洗净，去筋膜，切菱形片，虾仁洗净，控干水，放入碗中，用5克鸡蛋清、0.25克精盐、2.5克细干淀粉，拌匀上浆。\r\n2.猪腰子、猪肚、猪心、猪耳摘洗干净，均切成鱼鳃形片；生鸭脚用清水煮到六成酥，取出，拆骨；水发冬菇洗净，去根蒂；鱼片用5克鸡蛋清、2.5克细干淀粉、0.25克精盐，浆好，这样，全家福已经配好。 \r\n3.炒锅烧热，用油滑锅后，放入芝麻油，烧至五成热时，倒入鱼片、猪肉片、猪腰片、猪心片、猪耳片，用手勺轻轻滑散，见腰片变白，倒入漏勺内，控干油。\r\n4.锅内留50克油，放入鸭掌脚、冬菇、青豆', '料材俱全，味道清鲜，富于营养。', NULL, NULL, '瘦猪肉100克，大虾仁50克，猪腰子25克，猪心25克，猪肚25克，鱼片25克，猪耳25克，水发冬菇15克，青豆15克，生鸭脚4只，鸡蛋清10克，细干淀粉5克，鸡汤100毫升，料酒25克，精盐1克，酱油25克，白糖1.5克，味精1.5克，水淀粉15克，芝麻油275克，鸡油25克。');
INSERT INTO `recipe` VALUES (2410, 82, '肉沫海参', '1.将水发海参剖腹，去内脏，洗净，切成两半；五花猪肉去皮，去筋膜，洗净，剁成末；大葱去皮，洗净，一半切成3厘米长的段，另一半切末；生姜去皮，洗净，一半切片，一半切末；大蒜去皮，切末。 \r\n2.再将海参放入锅内，用冷水反复烧三次，去腥味，捞起，控干。\r\n3.油锅放入500克豆油，烧五成热时，放入海参，过油后，捞出，控油。\r\n4.炒锅内留25克油，和入葱段和姜末，煸炒一下，加入料酒、50毫升鸡汤，烧开，捞出葱段和姜片，倒进海参，烧十分钟左右，捞出，控干，待用。\r\n5.净锅，烧热，放25克豆油，五成热时，放入姜', '色泽红亮，味道鲜辣，细嫩适口。', NULL, NULL, '水发海参400克，五花猪肉100克，鸡汤150毫升，豆油500克（实耗75克），大酱50克，酱油5克，白糖2克，料酒5克，辣椒面0.5克，胡椒面1克，大葱25克，生姜25克，大蒜5克，味精3克，淀粉5克，芝麻8克（焙好）。');
INSERT INTO `recipe` VALUES (2411, 82, '如意蛋卷', '1.将鸡蛋打入碗中，加入5克料酒，0.75克精盐、5克水淀粉，用竹筷打散打均匀，把干净炒锅上火烧热，淋少许豆油滑锅，随即倒入一半鸡蛋，将锅转动，摊成一张圆形鸡蛋皮，依此法再摊一张。\r\n2.再将青鱼肉洗净，在鱼肉面用刀背，轻砸，然后将刀翻过来，用刀刃将鱼肉刮下来，则鱼肉与刺皮自然分开，放在干净的丰墩上，用刀斩成细茸，将鸡蛋清放在碗内，用竹筷打散，与鱼茸一起放入瓷盆里，加入10克料酒、2克精盐、1.5克味精和葱姜水，用3根竹筷使劲向一个方向搅打，若太稠，可加入适量的清水，直打至鱼糕发稠上劲、光亮即好。\r\n3.', '形似如意，美观大方，清鲜微辣。', NULL, NULL, '青鱼肉250克，辣大酱25克，鸡蛋2个，鸡蛋清50克，火腿30克，油菜叶20克，鸡油10克，鸡汤100毫升，料酒15克，精盐3克，味精2.5克，水淀粉15克，葱姜水10克。');
INSERT INTO `recipe` VALUES (2412, 82, '软炸豆角', '1.将豆角择去两头，洗净，用开水烫熟，入凉淡盐水中过凉，捞出，控干，放入瓷盆中，放少许盐、味精、料酒、大葱丝、生姜丝搅拌均匀，腌渍入味。\r\n2.再将鸡蛋磕入瓷碗内，加入少许盐，用筷子打散；面粉放入碗中，加适量清水，调匀成面糊，待用。\r\n3.炸锅烧热，放入花生油，烧五成热，将入味的豆角抖去葱姜丝，蛋液，拖匀面糊，一根一根下入油锅中，炸成黄色，捞出，待油温八成热时，放入豆角复炸一次，捞出，控油，入大圆盘中。\r\n4.炒锅旺火烧热，放入芝麻油，五成热时，放入葱白丁、猪肉丁、竹笋丁、松蘑丁下入锅中煸炒，放料酒、酱油', '色泽黄亮，外脆里嫩，咸鲜微酸。', NULL, NULL, '嫩豆角250克，白糖10克，鸡蛋3个，花生油1000克（实耗100克），精盐5克，味精2克，料酒15克，大葱丝5克，面粉50克，生姜丝5克，猪肉丁25克，罐头竹笋25克，松蘑丁25克，葱白丁25克，芝麻油25克，酱油15克，醋15克，水淀粉10克。');
INSERT INTO `recipe` VALUES (2413, 82, '软炸狗脑', '1.剥去狗脑的皮膜，把狗脑切成小方块，放入开水中焯透，捞出，下入炒锅中，加入白汁，狗骨汤、味精、料酒、咸面，上火烧开，用小火煨，入味后，捞出狗脑块，晾凉。\r\n2.将鸡蛋清放入盆内，用筷子顺一个方向抽打成泡沫状，加入淀粉搅匀，即成高丽糊。\r\n3.再将炒锅上火，放入花生油，烧至六成熟时，把狗脑块沾匀一层面粉，再沾匀一层高丽糊，逐块下入油中，炸至呈浅黄色，待酥透时捞出，放入盘中。\r\n4.食用时，沾芝麻盐即成。', '地酥软，味咸鲜香，风味独特。', NULL, NULL, '熟狗脑250克，鸡蛋清100克，花生油750克（实耗75克），淀粉25克，面粉25克，味精5克，料酒10克，白汁50克，鸡蛋150克，咸面2克，芝麻盐15克。');
INSERT INTO `recipe` VALUES (2414, 82, '软炸蟹油', '1.将海蟹洗净，上屉蒸熟，取出，掀开蟹盖，取出蟹油，再将蟹油周围的黑膜洗净，择好，放入开水中焯透，捞出，控水，待用。\r\n2.再将汤勺坐上火，加入鸡汤、味精、料酒、精盐、烧开后，放入焯透的蟹油，改小火煨，入味后，捞出晾凉。\r\n3.把鸡蛋清放入瓷碗中，用筷子向一个方向连续抽打成泡沫状（以泡沫中能立住筷子为准），再加入淀粉拌匀成蛋清糊。\r\n4.再把炒勺上火，加入花生油烧热。同时，把蟹油均匀沾一层面粉，再均匀沾一层蛋清糊，逐一下入油中，炸成浅黄色，捞出，放入盘中。\r\n5.然后，将汤勺上火，加入鸡汤、味精、料酒、精', '颜色浅黄，软嫩鲜香，味浓醇香。', NULL, NULL, '鲜海蟹4公斤（雄尖脐的），鸡蛋清125克，面粉25克，淀粉40克，花生油750克（实耗75克），味精5克，料酒5克，鸡汤250毫升，牛奶50毫升，花椒盐25克，熟芝麻25克，精盐10克。');
INSERT INTO `recipe` VALUES (2415, 82, '三鲜鲍鱼', '1.将水发紫鲍鱼洗净，用刀剞上花刀，翻过来再剞另一面，然后坡刀片成厚片，小的片成2片，大的片3－4片；油发鱼肚洗净，用坡刀片成片，薄的可以改成方块；水发乌参去内脏，洗净，也用坡刀片成厚片，待用。\r\n2.老母鸡和鸭子肉用开水氽透，捞出，洗净血污，放入锅内，再放入2.5－3公斤清水，大火煮开后，用小火煮成浓汤，将鸡、鸭煮烂，共出汤1.5公斤。\r\n3.鲍鱼、鱼肚、海参分别用开水氽透，再用鸡汤氽一遍捞出。\r\n4.锅内放入浓鸡汤，下入葱、姜将鲍鱼、鱼肚、海参下入锅内，汤开后，再烧3-5分钟，然后挑出葱、姜，再加入料', '汤浓味厚，清鲜适口，营养丰富。', NULL, NULL, '水发紫鲍500克，水发乌参500克，油发鱼肚500克，净老母鸡肉1000克，白糖7.5克，净鸭子肉500克，盐5克，芝麻油10克，酱油15克，料酒25克，大葱30克，生姜20克，水淀粉10克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2416, 82, '三鲜汤', '1.将鸡脯肉剔净筋膜，切成5厘米长、2厘米宽、5毫米厚的片；水发海参剖腹去内脏，洗净，切成秣刀片；火腿切成与鸡片相同的一字片，黄瓜洗净，斜刀切成段，再顶刀切成菱形片。\r\n2.再将鸡片用精盐、料酒、胡椒面、鸡蛋清、水淀粉拌匀，浸渍好，待用。\r\n3.汤锅上火，放入清水，待烧开后，放入鸡片氽透捞出，沥净水分，再将海参放入开水锅中氽透，捞出，将鸡片、海参、火腿放入汤盆中，淋入芝麻油，拌匀。\r\n4.汤锅上火，放入鸡汤，烧开后撇去浮沫，加入精盐，味精调好口味，倒入汤盆中，再放入黄瓜片，即可。', '鸡肉软嫩，火腿味美，海参滑软。', NULL, NULL, '鸡脯肉150克，火腿50克，水发海参100克，黄瓜25克，鸡汤1公斤，精盐7.5克，味精5克，料酒15克，胡椒面3克，鸡蛋清1个，葱末5克，生姜末5克，水淀粉10克，芝麻油5克。');
INSERT INTO `recipe` VALUES (2417, 82, '三鲜鱼唇', '1.将火腿、竹笋、冬菇都切成长方形片；水发鱼唇切成3厘米见方的块。\r\n2.鸡鸭整理洗净，用开水氽透，再用清水洗净血沫，放入锅内，加入3公斤清水。用旺火烧开，转小火煨煮。加葱姜，煮出1.5公斤的鸡鸭汤，过滤后成鸡鸭清汤，待用。\r\n3.鱼唇用开水氽透，捞出，再取部分鸡鸭清汤倒入锅内，将鱼唇再用鸡鸭汤氽一遍，捞出，汤倒掉不要。\r\n4.将锅再上火，放入鸡鸭汤，将鱼唇放入；竹笋片、冬菇片用开水氽透，捞出，放入锅内同鱼唇一齐烧，烧5分钟，加入火腿片、盐、白糖、料酒、味精，稍烧片刻，用水淀粉勾薄芡，淋上芝麻油，盛入盘内', '汁浓味鲜，鱼唇软滑，烂糯可口。', NULL, NULL, '水发鱼唇1.25公斤，罐头竹笋100克，水发冬菇50克，熟净火腿50克，净老母鸡1.5公斤，肥鸭500克，白糖10克，盐12克，味精3克，芝麻油10克，料酒15克，鲜姜10克，水淀粉10克，大葱10克。');
INSERT INTO `recipe` VALUES (2418, 82, '砂锅海鳗', '1.把鳗鱼内脏去掉，用水洗净骼刀切成5厘米长的段。\r\n2.大葱切段，生姜切片；猪肥肉切成片；香菜梗切成段，待用。\r\n3.炒锅内加入清水，烧开，将鳗鱼段放入开水中氽一下，捞出，控净水分。\r\n4.取砂锅1只，用清水洗净，把葱段、生姜片一半垫在砂锅底，然后将鳗鱼段放砂锅内，加上肥肉片、葱段、姜片、花椒、大料、鸡汤、桂皮、烧开，撒上芝麻油、精盐、料酒、醋、味精，旺火烧开，小火炖煨1小时，取下盖，拣去葱、姜、花椒、大料、桂皮，撒匀香菜梗、辣椒面、胡椒面即可。', '软嫩柔糯，鳗鱼咸辣，汤清醇美。', NULL, NULL, '海鳗鱼1000克，猪肥肉50克，大葱15克，生姜15克，花椒10克，大料10克，桂皮10克，香菜梗15克，精5克，料酒15克，鸡汤500毫升，味精5克，醋5克，芝麻油15克，胡椒面2克，辣椒油5克。');
INSERT INTO `recipe` VALUES (2419, 82, '烧猪肉条', '1.大葱去皮，切丝；生姜去皮，切丝；干辣椒去蒂，切丝；胡萝卜洗净，切小方丁。\r\n2.猪五花方肉刮净皮上毛污，洗净后放入煮锅，煮15分钟后，捞出，放入清水中浸泡，去血污、血秽，切成长12厘米、宽2厘米的条。\r\n3.炒锅烧热放豆油，烧五成热，放入大酱，加少许鸡汤，投入猪肉条，煨烧上色，捞出。\r\n4.净锅，烧热，放100克芝麻油，烧至九成热时，投入猪肉条，炸从皮起泡时，捞出，整齐排列在碗内，肉皮朝碗底，加少许大酱卤上蒸锅，蒸烂，取出。\r\n5.炒锅烧热，放50克香油，三成热时，放入辣椒丝、大葱丝、生姜丝、炒出香味', '肉味浓郁，软烂适口，易于吸收。', NULL, NULL, '猪五花方肉600克，青豆25克，胡萝卜15克，大酱175克，盐5克，糖15克，料酒25克，干红辣椒15克，芝麻面10克，味精2克，大葱25克，生姜15克，豆油60克，芝麻油150克，鸡汤250毫升，水淀粉20克。');
INSERT INTO `recipe` VALUES (2420, 82, '神仙炉', '1.制出牛肉汤料：先把酱油45克和芝麻油16克倒进小碗里，再加入8克大酱和4克辣椒油，先放入大蒜泥、盐和糖24克，再放入4克大葱末、1克芝麻面、30克料酒、30毫升水搅拌均匀为一份牛肉汤料，依法共制出8份牛肉汤料。\r\n2.把牛肉去筋膜，洗净，冷冻，切成似纸那样薄；洋葱头去皮，洗净，切丝。\r\n3.再把牛肉和葱头丝放入炉槽（双槽或单槽）。\r\n4.将白鱼和肝匀切成大小相同的小薄片，分别用胡椒粉和盐腌起来。\r\n5.取1个鸡蛋的蛋黄，抽打均匀，待用。\r\n6.把肝片先后在蛋黄和面粉里挂糊。\r\n7.蛋清放瓷碗中，打好，', '味道香醇，多色多味，诱人食欲。', NULL, NULL, '牛肉500克，洋葱头2个，白鱼片250克，小牛肝250克，鸡收3个，面粉10克，豆油50克，大葱40克，胡萝卜1个，牛肉汤料8份，松子100克，核桃100克，芝麻酱80克。');
INSERT INTO `recipe` VALUES (2421, 82, '生拌鲻鱼', '1.准备洗净、消毒的案板，菜刀、竹筷最好用酒精擦抹消毒冲净。\r\n2.鲜鲻鱼肉洗净，放在案板上，用菜刀切成长6厘米、宽2厘米的片，顺序码放在消毒煮过的净盘子上。\r\n3.黄瓜洗净，消毒，胡萝卜洗净，去皮，消毒，均切成5厘米长的细丝，撒在鱼片四周。\r\n4.大葱、生姜、大蒜均去皮，洗净，剁成碎末。\r\n5.取小瓷碗，放入醋、酱油、盐、白糖、味精搅拌均匀，浇在鱼片上。\r\n6.食用时，蘸辣大酱吃即可。', '香嫩脆嫩，咸酸微辣，风味独道。', NULL, NULL, '鲜鲻鱼片200克，黄瓜25克，胡萝卜25克，大葱15克，生姜5克，大蒜3克，醋50克，酱油15克，精盐1.5克，味精2.5克，白糖25克，辣大酱2克。');
INSERT INTO `recipe` VALUES (2422, 82, '生菜牛肉', '1.将生姜、大葱去皮，洗净，切成碎末，加入料酒、胡椒粉、0.5克精盐、味精调和均匀成渍汁。\r\n2.再将牛肉去筋膜，洗净，下入开水锅中焯一下，捞入瓷盆中，倒入渍汁，腌一个半小时后，捞出，入盆，放入蒸笼中蒸，熟后取出晾凉。\r\n3.再把熟牛肉切成长4厘米，宽1厘米，厚1厘米的片。\r\n4.将鸡蛋打入碗内，加入淀粉调成蛋粉浆，抹在牛肉片上。\r\n5.炸锅内倒入植物油，烧至八成热时，放入牛肉片，至肉片呈金黄色时，捞出，控干，码在盘子的左边。\r\n6.生菜摘洗干净，切成丝，加糖、姜、葱、醋、1.5克盐和芝麻油拌匀，装在牛肉', '牛肉酥嫩，生菜酸甜，荤素搭配。', NULL, NULL, '牛腿肉500克，生菜150克，鸡蛋2个，料酒10克，白糖40克，精盐6.5克，醋15克，胡椒粉0.5克，味精1.5克，芝麻油10克，植物油250克（实耗50克），大葱10克，生姜5克。');
INSERT INTO `recipe` VALUES (2423, 82, '生烤野兔', '1.野兔去皮，去内脏、头、脚、筋膜、洗净，凉水浸泡一天后，捞出，控干，擦去水分，用铁针将肉质较多处扎上几针。\r\n2.取大瓷盆，放酱油、大葱末、蒜泥、白糖、黑胡椒面、芝麻面、生姜末调拌均匀，放入野兔，擦秣兔身里外后，放入大瓷盆上腌渍半天，入味后，待用。\r\n3.再制出渍汁：先把90克酱油、30克芝麻油倒入瓷碗中，加入16克大酱和8克辣椒油，5克大蒜茸泥、6克精盐和50克白糖，搅拌倒进每个人的小碗里。\r\n4.将腌渍入味的野兔装入烤盘，用小刷刷匀芝麻油，入烤箱烤，隔半小时，刷一次芝麻油，翻转一次，至烤熟，出烤箱。', '色泽棕红，香味郁浓，口味辣香。', NULL, NULL, '野兔1只（约重1.5公斤），酱油125克，大葱末25克，白糖30克，蒜泥5克，黑胡她面2.5克，生姜末5克，芝麻面15克（焙好），渍汁250克，芝麻油30克。');
INSERT INTO `recipe` VALUES (2424, 82, '什锦豆腐', '1.将豆腐、去皮胡萝卜、牛肉均切成豌豆大丁；豌豆去皮；大葱、生姜去皮，均剁成碎末。\r\n2.炒锅旺火烧热，放入豆油，烧五成热，投入豆腐丁稍炒，捞出，控干；胡萝卜丁和豌豆分别放入开水锅中焯一下，捞出，控干。\r\n3.炒锅净后，放入25克豆油，烧热时，下入牛肉、葱末、姜末，煸炒几下，加少许水，再放酱油、盐、糖、料酒，汤沸后放入豆腐丁、胡萝卜丁、豌豆、味精，用淀粉勾芡，即可。', '味道鲜美，美观大方，富于营养。', NULL, NULL, '豆腐250克，胡萝卜50克，牛肉100克，鲜豆腐200克，蘑菇100克，豆油250克（实耗75克），料酒25克，酱油50克，精盐2克，糖10克，葱10克，姜5克，味精2克，淀粉15克。');
INSERT INTO `recipe` VALUES (2425, 82, '双味海带', '1.将250克水发海带洗净，控干，切长方形片，入锅煮软，捞出，控干。\r\n2.余下的250克海带上蒸锅，隔水干蒸半小时，切片待用。\r\n3.把白糖、蜂蜜放入锅内，加牛奶、熟鸡油10克、料酒烧开，放入长方形煮海带片，温火煨熬，待海带片附上奶浆，离火，晾凉，改切成菱角形。\r\n4.芥末粉用温开水调开，放醋、熟鸡油50克、红辣椒粉10克及奶粉，拌匀后放入锅中，用火烧开，搅拌均匀成辣椒芥末汁。\r\n5.蒸海带片拌匀辣椒芥末汁，放入盘的另一边，盘中间放鲜柠檬片分隔开，即可。', '呈乳白淡黄双色，甜辣双味，脆香爽口。', NULL, NULL, '水发海带500克，蜂蜜100克，白糖200克，牛奶250克，熟鸡油60克，料酒25克，红辣椒粉10克，奶粉50克，柠檬片10片，芥末粉15克，醋20克。');
INSERT INTO `recipe` VALUES (2426, 82, '四鲜丸子', '1.净猪腿肉、水发海参、水发香菇、水发冬笋洗净，控干，均切成碎末；海米水发后，斩成末，一起放入瓷盆中，加上黄酒、炖酱、麻油、盐、淀粉、芝麻，搅拌均匀，做成4只丸球。\r\n2.面粉和鸡蛋拌匀，然后将丸球表面全沾上蛋糊。\r\n3.炒锅烧热，放入豆油，烧至七成热时放入丸球，炸至表面呈牙黄色时捞出，放入大碗中，加少许鸡汤，上笼蒸烂，取出倒入4个汤碗中。\r\n4.炒锅烧热，加入鸡汤、盐、味精、酱油，烧开后，水淀粉勾芡，煨浓，出锅，倒入4个汤碗中即可。', '丸子酥烂，味鲜嫩香，原汁原味。', NULL, NULL, '净猪腿肉400克，水发海参100克，水发香菇50克，冬笋50克，海米25克，鸡蛋2个，料酒15克，盐5克，味精2克，酱油5克，干淀粉15克，面粉10克，豆油1000克（实耗100克），炖酱15克，芝麻25克（焙好），麻油15克，鸡汤500毫升。');
INSERT INTO `recipe` VALUES (2427, 82, '松蘑炖鹌鹑', '1.将鹌鹑用酒醉死，不放血，干拔，去净毛，剁去头和爪，剖开脊背，取出内脏，用清水洗净，放入开水锅中，用开水烫一下，捞出，冲洗净血沫。\r\n2.松子去皮；大葱去皮，洗净；生姜去皮，洗净；大蒜去皮，洗净，均切成沫。\r\n3.再将鹌鹑放入炖锅内，加入清水，大火煮开后，捞出，放入炖锅内，加入大葱末、生姜末、大蒜末，小火微煮。\r\n4.松蘑要选大小一样的，洗净，去蒂柄，与鸡汤、松子、盐、料酒、胡椒面、花椒和味精等一起加进炖鹌鹑锅内，盖好，上蒸锅隔水蒸3小时左右，取出，撒上焙好的芝麻，即可。', '呈微红色，鹑肉香嫩，鲜香适口。', NULL, NULL, '鹌鹑6只，松蘑200克，鸡汤1500毫升，大葱15克，大蒜5克，花椒5粒，料酒20克，精盐14克，味精10克，芝麻10克（焙好），松子50克，胡椒面5克。');
INSERT INTO `recipe` VALUES (2428, 82, '松仁炒鹌鹑丁', '1.将鹌鹑剔骨，剁去头颈、尾、脚、翅膀，洗涤净；将鹌鹑肉切成1.5厘米见方的丁；猪瘦肉也切1.5厘米见方的丁，与鹑丁一起，加入酱油5克、味精2.5克、胡椒分0.5克、蛋清、干淀粉上浆；面包去皮，切粗丁，竹笋与香菇、葱白、玉兰片、大蒜瓣均切成小丁。\r\n2.将鹌鹑头颈剖为两片，脚爪去尖，尾割开，去臊洗净，鹑肝、肫剔去筋膜，洗净后，切成粗丁。\r\n3.净锅烧热，放入豆油，烧五成热时，将面包丁下锅，烧至酥黄，捞出，控油，入盘；随即把鹑丁、瘦肉丁下锅滑散，炸八成熟时，捞出；再将鹌鹑头、脚爪、翅膀、肫、肝等下锅炸熟，倒', '清鲜淡爽，香郁多味，细嫩酥脆。', NULL, NULL, '净鹌鹑1只，松子仁50克，瘦猪肉100克，罐头竹笋50克，咸面包50克，蛋清50克，玉兰片50克，大葱白25克，水发香菇15克，大蒜瓣15克，酱油15克，熟青豆仁50克，胡椒粉1.5克，料酒10克，白糖15克，干淀粉15克，味精5克，芝麻油5克，鸡汤100毫升，豆油500克（实耗150克）。');
INSERT INTO `recipe` VALUES (2429, 82, '酥炸野兔', '1.将带骨兔后腿肉放入清水中浸泡，捞出，洗净，用刀将兔腿肉剖开，露出腿骨，用开水焯一下，取出，洗去血污。\r\n2.生菜叶洗净，消毒，待用。\r\n3.砂锅洗净，加入鸡汤旺火烧开，放精盐、白糖、料酒、醋，尝好口味，放入兔腿肉烧开，改用小火烧煮至酥烂，捞出，剔去骨头，改切成1.5厘米厚的大片，粘上干淀粉待用。\r\n4.炒锅烧热后放入熟豆油，待油温五成热时，投入兔肉炸黄，捞出，待油九成热时，投入复炸一次，至兔肉酥香时，立即捞出，切成一字条形，装入围生菜叶的盘子里。\r\n5.食用时，辣大酱小碟单跟即可。', '咸鲜辣香，外酥里烂，风味独道。', NULL, NULL, '野兔肉500克（带骨后腿肉），淀粉25克，生菜150克，熟豆油500克（实耗60克），料酒25克，精盐8克，酱油25克，白糖15克，辣大酱25克，醋5克。');
INSERT INTO `recipe` VALUES (2430, 82, '素炒什锦', '1.把鸡蛋的蛋黄和蛋清分别放入两个瓷碗中，抽打起泡，摊成薄鸡蛋饼，将蛋黄饼和蛋白饼分别切成5厘米长的细丝。\r\n2.菠菜择洗干净，切成5厘米长的段，放入开水锅中焯一下；绿豆芽摘去根、壳，冲洗干净，放入开水锅中焯一下；大葱去皮，洗净，切成5厘米长的段；粉丝水发，煮透。\r\n3.炒锅旺火烧热，放入豆油，烧六成热时，投入大葱段煸炒出香味，放入绿豆芽，翻炒几下，加入粉丝、菠菜丝、鸡蛋清丝、鸡蛋黄丝和盐，炒匀，淋上芝麻，入盘即可。', '色彩艳丽，清香爽口，口味喜人。', NULL, NULL, '绿豆芽200克，菠菜100克，粉丝50克，鸡蛋3个，豆油75克，芝麻油15克，精盐6克，大葱5克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2431, 82, '酸辣豆腐汤', '1.将豆腐洗净，切成长3厘米、宽2厘米、厚为1厘米的条，放入清水盆里，使其散开；牛肉切成2厘米长的细丝；蘑菇、木耳水发好后，去蒂梗，洗净，均切成细丝；香菜摘洗干净，切成碎末；葱、姜去皮，均切碎末。\r\n2.再将鸡汤倒入锅内，烧开后放入姜末、葱末、牛肉丝、木耳丝、蘑菇丝，加酱油、盐、醋、豆腐丝条，烧开后，用淀粉勾芡，再加味精、辣椒油、芝麻油，轻轻搅匀装碗，撒上香菜末和胡椒粉即可。', '味道香辣，开胃可口，易于吸收。', NULL, NULL, '豆腐200克，蘑菇50克，水发黑木耳25克，牛肉25克，鸡汤1000毫升，酱油25克，盐5克，醋10克，大葱10克，生姜5克，胡椒面2克，辣椒油2克，芝麻油5克，味精3克，香菜5克，淀粉15克。');
INSERT INTO `recipe` VALUES (2432, 82, '酸辣兔肉', '1.将兔肉洗净，去筋膜，节成8毫米的厚的大片，拍松后，放入冷水内浸泡半个小时，洗净，沥去水分，去其血浆，切成长3厘米、宽1.5厘米片；青椒洗净，去蒂籽，切小丁。\r\n2.再将兔肉片放入瓷碗内，加料酒10克、鸡蛋、淀粉、少许精盐上浆拌匀；大葱去皮，洗净，切段；生姜去皮，洗净，拍松。\r\n3.炒锅烧热放豆油，待五成热时，将上浆的兔肉分两次投入油中，炸至表面有硬壳时捞出，待油温九成热时，投入兔肉片复炸一次，然后，装入大瓷碗中，放入葱段、姜块、花椒粒，加少许鸡汤、精盐、香醋、味精、料酒调好口味，上笼蒸30分钟，捞出，', '质地酥烂，味鲜酸辣，诱人食欲。', NULL, NULL, '野兔肉300克，料酒20克，豆油500克（实耗75克），淀粉25克，酱油10克，鸡蛋50克，香醋25克，精盐2.5克，大葱10克，生姜10克，花椒粒10颗，味精1克，胡椒面1.5克，芝麻15克（焙好），表椒25克，鸡汤300毫升。');
INSERT INTO `recipe` VALUES (2433, 82, '酸甜海带', '1.将水发海带洗净，横切成细丝；大葱、生姜、大蒜去皮，均切成末。\r\n2.炒锅旺火烧热，放入25克豆油，待五成热时，投入葱末、姜末、蒜末煸炒几下，加精盐、酱油、料酒、白糖和适量的水，烧开后，放入海带丝，待汤浓时，烹入醋，烧开即成。', '味道酸甜，海带脆嫩，含碘丰富。', NULL, NULL, '水发海带500克，醋50克，白糖150克，豆油25克，料酒15克，精盐5克，酱油15克，大葱5克，生姜5克，大蒜5克，味精2克。');
INSERT INTO `recipe` VALUES (2434, 82, '糖醋里脊', '1.将猪里脊洗净，去筋膜，切成4厘米长、5毫米宽的条，放入瓷碗内，加入鸡蛋清、水淀粉、精盐搅拌均匀，上浆。\r\n2.取小瓷碗放入盐、糖、醋、黄酒、葱姜末、水淀粉调成糖醋汁。\r\n3.炒锅烧热，放入花生油，烧至八成热，逐个投入里脊条，炸成牙黄色时，倒入漏勺，沥去油，余油倒入油罐。\r\n4.原炒锅烧热，放入豆油，烧五成热，倒入糖醋汁，打成薄芡，投入里脊条，翻炒几下，淋入芝麻油，出锅，装盘，即可。', '呈金黄色，外脆时嫩，口味甜酸。', NULL, NULL, '猪里脊300克，鸡蛋清1只，水淀粉50克，豆油50克，花生油1000克（实耗50克），大葱末3克，生姜末2克，料酒15克，酱油5克，白糖100克，醋75克，芝麻油15克，味精2克。');
INSERT INTO `recipe` VALUES (2435, 82, '甜辣鸽丝', '1.将300克净鸽肉去皮，切成粗丝；竹笋切成丝；香菜梗洗净，切段；葱、姜去皮，洗净，切长丝，待用。\r\n2.再把鸽粗丝放入碗内用味精、蛋清、水淀粉抓匀；另一碗内放入料酒、味精、白糖、20克葱丝、姜丝、蒜茸拌匀成清汁，待用。\r\n3.粗粉皮洗净，用开水氽一下，切丝，控干，余下200克鸽肉也切丝，放盐、胡椒粉拌均匀，腌渍片刻，入味待用。\r\n4.炒勺内放豆油，在旺火上烧至六热，将鸽丝下勺，拨散变白滑透后，立即捞出。\r\n5.炒勺留少许油，倒入300克鸽肉丝、竹笋丝、香菜段，烹入甜清汁，颠翻数下，出勺，放入盘一边。\r\n', '双色双味，嫩鲜清香，中韩合壁。', NULL, NULL, '净鸽肉500克（去骨），粉皮300克，鸡油100克，大葱100克，罐头竹笋25克，香菜梗20克，蛋清1克，淀粉10克，番茄酱25克，精盐5克，白糖25克，料酒15克，辣椒油5克，胡椒面3克，醋15克，豆油250克（实耗25克），大葱15克，生姜10克，大蒜5克。');
INSERT INTO `recipe` VALUES (2436, 82, '铁排明虾', '1.将大虾洗净用剪刀剪去虾须、虾枪，虾枪处用剪刀头挑去脏物，再用剪刀从背部剪开，挑去沙筋，洗净；罐头竹笋切成片，待用。\r\n2.大葱去皮，洗净，切长丝。\r\n3.再将明虾从背部剖开，刀口深至虾肉腹部，但不剖断，批好后摊开，在剖面上用十字刀法轻轻排一下，然后撒上精盐、胡椒粉，抹上7.5克料酒。\r\n4.鸡蛋用竹筷打散后，放入明虾拖过，将明虾两面再拍上细干淀粉。\r\n5.炒锅上火烧热，放入花生油，烧至五成热时，逐只放入明虾，炸2分钟，至外皮呈金黄色，倒入漏勺内，控油。\r\n6.锅内留10克底油，烧热，放入大葱丝煸炒出香', '外皮松嫩，虾里鲜嫩，酸甜香辣。', NULL, NULL, '新鲜明虾3只（约重200克），罐头竹笋25克，鸡蛋40克，细干淀粉30克，大葱丝25克，鸡汤100毫升，料酒15克，辣大酱15克，酱油15克，精盐1克，味精1克，花生油750克（实耗60克），水淀粉10克，胡椒粉1克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2437, 82, '氽金银丝', '1.将狗上脑肉、鸡脯肉去筋膜，切6厘米长的细丝，放入瓷碗中，加入鸡蛋清、淀粉、精盐，用手抓匀浆好，再把冬笋也切成6厘米长的细丝，放入开水中焯透，捞出，控水；黄瓜洗净，去籽，切成6厘米长的细丝，待用。\r\n2.将炒勺上火烧热，放入花生油，待花生油烧到五至六成热时，将碗中的狗肉丝、鸡肉丝入油中，滑散后立即捞出。倒去油。\r\n3.再将原炒勺上火，加入香油、葱末、姜末，稍加煸炒，下入狗肉丝、鸡丝、冬笋丝、黄瓜丝、料酒、味精、酱油、精盐，煸炒均匀，淋入明油，出勺盛入盘中即成。', '色泽漂亮，质地清鲜，脆嫩爽口。', NULL, NULL, '狗上脑肉150克，鸡脯肉100克，冬笋50克，黄瓜25克，鸡蛋清50克，花生油500克（实耗50克），味精5克，料酒10克，淀粉15克，酱油10克，香油10克，大葱25克，生姜10克，精盐10克。');
INSERT INTO `recipe` VALUES (2438, 82, '豌豆炒虾仁', '1.将嫩豌豆洗净，放入开水锅中，用淡盐水氽一下，待用。\r\n2.炒锅中火烧热，放入豆油，待三成热时，将虾仁入锅，快速用竹筷划散，炸约10秒钟，倒入漏勺，控油（炸时要掌握热锅，冷油，快速，则不粘锅）。\r\n3.炒锅内留25克底油，烧热，投入辣椒面稍炒一下，放入豌豆，翻炒几下，再烹入料酒、鸡汤、盐、味精，随即放入虾仁，用湿淀粉勾薄芡，将炒锅颠翻几下，淋上芝麻油，出锅，入盘即可。', '虾仁玉白鲜嫩，豌豆翠绿清香。', NULL, NULL, '虾仁250克，嫩豌豆100克，鸡汤25毫升，料酒10克，精盐1克，味精1.5克，湿淀粉5克，豆油750克（实耗50克），芝麻油10克，辣椒面15克。');
INSERT INTO `recipe` VALUES (2439, 82, '虾仁扒海参', '1.水发海参去内脏，洗净，控干，切3厘米见方的丁，放入清水中浸泡。\r\n2.炒锅烧热，少许油滑锅后，放入花生油250克，烧三成热时，倒入虾仁，快速用手勺划散，约10秒钟，倒入漏勺，控油，保温。\r\n3.炒锅留25克底油，放入大葱段炒香，烹入料酒、鸡汤稍煮，捞出葱段，放入海参丁，加酱油、盐、白糖，烧3分钟，再放入味精，用湿淀粉勾薄芡，淋入芝麻油，出锅，倒入大盘内，把虾仁放在海参丁的两旁，油菜心丝和胡萝卜丝用开水氽熟，放在大盘的旁边即可。', '虾仁鲜美，海参滑嫩，咸香微甜。', NULL, NULL, '水发海参400克，虾仁100克，油菜心50克，胡萝卜100克，大葱50克，鸡汤250毫升，料酒15克，酱油20克，白糖1.5克，精盐5克，味精2.5克，湿淀粉20克，花生油250克（实耗30克），芝麻油15克。');
INSERT INTO `recipe` VALUES (2440, 82, '虾仁干贝', '1.将干贝洗净，浸涨，去掉硬丁，放入碗内，加入15克料酒和150毫升清水，以没过干贝为度，放入蒸锅，用旺火蒸烂待用。\r\n2.再将炒锅烧热，用油滑锅后，倒入花生油，烧至三成热后，稍凉，放入虾仁，用筷子拨散，炸至变白，立即倒入漏勺，控油。\r\n3.炒锅中留10克底油，放入大葱段、糖炒出香味时，烹入15克料酒、盐、鸡汤和蒸干贝原汁，待汤烧开后，捞出葱段，放入干贝、味精，用湿淀粉勾芡，倒入虾仁，待芡汁煮开时，放入生菜丝和胡萝卜丝，淋上芝麻油，出锅，装盘，即可。', '鲜艳美观，虾仁滑嫩，干贝清爽。', NULL, NULL, '虾仁100克，干贝50克，生菜2克，胡萝卜3克，大葱15克，鸡汤100毫升，料酒30克，精盐1克，味精2.5克，湿淀粉25克，芝麻油10克，花生油250克（实耗30克）。');
INSERT INTO `recipe` VALUES (2441, 82, '虾须牛肉', '1.牛肉（忌用水洗）剔去筋膜，顺着纹路片成6毫米厚的大片；盐炒到滚烫，晾到不烫手时待用。\r\n2.在牛肉的两面都搓上盐，平放（勿重叠）在竹筛内晾上，控去血水。\r\n3.把晾好的牛肉散铺在铁丝箅上，用木炭火烘烤，火要微小，每15分钟翻烤一次，约烤3小时。\r\n4.再把烤好的牛肉直接放在蒸屉上，沸水旺火蒸2小时，取出晾凉，撕成火架棍一般粗的丝。\r\n5.炒锅烧热，放入豆油，烧七成热时，放入牛肉丝，炸透，捞出，控油，余油倒入油罐里。\r\n6.炒锅放50克豆油，烧热，加入黄酒、辣椒面、花椒面、味精、糖、五香粉、焙好的芝麻，', '色泽红亮，质地酥脆，麻辣咸香。', NULL, NULL, '瘦牛肉500克，黄酒15克，精盐20克，白糖25克，味精2克，辣椒面3克，花椒面5克，五香粉5克，豆油500克（实耗50克），焙好芝麻15克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2442, 82, '虾子烧海参', '1.虾子用清水洗去泥沙，沥去水份。\r\n2.水发海参剖腹，去内脏，用清水洗净，捞出，控干。\r\n3.炒锅烧热，放入熟鸡油，待油八成热时，投入海参炸2分钟，捞出，除去腥味。\r\n4.净锅，放50克鸡油，放入葱段、姜片煸黄，加入鸡汤，推入海参煨炖，顺序将软烂海参取出，汤倒出，弃之。\r\n5.将锅烧热，放熟鸡油50克，放入葱姜末，煸炒出香味，随即倒入虾子煸炒，再加入鸡汤，汤烧开后，用细网筛沥去末子，加入盐、味精、酱油、酒、辣大酱、，倒入海参，要腹面朝上，改小火煨8分钟后，待汤汁浓醇时下入水淀粉勾成硬芡，沿锅边浇上香油、', '色泽金红，明亮剔透，香浓软滑。', NULL, NULL, '水发海参1000克，虾子25克，三合油50克（香油、酱油、醋），大葱段50克，生姜片25克，熟鸡油500克（实耗100克），葱姜末25克，料酒40克，精盐7克，味精10克，辣大酱10克，白糖20克，水淀粉40克，鸡汽500毫升，酱油20克。');
INSERT INTO `recipe` VALUES (2443, 82, '苋菜黄鱼羹', '苋菜，又称苋、雁来红、老少年、三色苋，是一种开绿白色小花的野菜，俗称“人青草”。味道鲜美，营养价值高，其维生素C的含量高于西红柿，而且有祛病之功，对心血管病、胃肠炎，便秘、黄胆病及癌症等疾病均有一定的疗效。作药用，可取鲜叶或汁用，也可取全株晒干长期备用。因其具有解热、解毒、消肿等功效，可治感冒、眼疾、齿疾、毒蛇咬伤、脓肿等病症，也有利尿、止泻、通经的效能。\r\n1.将苋菜择去老茎，黄叶，洗净后，沥干水分，黄花鱼去皮，去刺，切成小丁，放入碗内，加料酒搅拌均匀，腌渍片刻。\r\n2.大葱、生姜去皮，洗净，均切成碎末', '色美味鲜，清香嫩滑，爽嫩润口。', NULL, NULL, '苋菜100克，黄花鱼肉150克，熟火腿丁20克，熟鸡油50克，精盐3克，料酒5克，白糖4克，胡椒面2克，水淀粉25克，鸡汤600毫升，芝麻油10克，大葱10克，生姜5克，辣椒油5克。');
INSERT INTO `recipe` VALUES (2444, 82, '香辣牛肉', '1.将牛肉洗净，去筋膜，切成两块。\r\n2.再将牛肉放入清水锅中，氽3分钟，放入凉水中漂去血水。\r\n3.取一个砂锅，锅底放上蒸架，用大葱段、生姜块垫底，再把牛肉放在上面，加糖、酱油、辣椒面、大料、芝麻油，再倒入清水，淹没牛肉。将砂锅盖紧，放在旺火上烧开后，改为小火上焖5小时左右。\r\n4.待牛肉熟烂时，起锅盛在汤盆内，浇上牛肉汤汁，撒上焙好的芝麻面即可。', '牛肉酥烂，味道浓香，清嫩爽口。', NULL, NULL, '牛腿肉500克，大葱50克，生姜25克，辣椒面2.5克，大料2克，酱油50克，白糖50克，芝麻油25克，芝麻面3克（焙好），牛肉汤100毫升。');
INSERT INTO `recipe` VALUES (2445, 82, '香酥鹌鹑', '1.鹌鹑掼死，拔净毛，剖开脊背取出内脏，洗净，用开水焯烫一下，取出，用清水洗净。瓷盆中放入鹌鹑，并加酱油、精盐、绍酒、花椒、大料、白糖、醋，添上与鹌鹑相平的水，调好口味，拌匀；大葱去皮，切3厘米段；生姜去皮拍松，均放入瓷盆中。\r\n2.笼屉放上瓷盆，入蒸锅，盖严至断生，取出，去掉汤水和调配料，用湿淀粉抹匀鹑面表皮，晾凉，待炸。\r\n3.净锅烧热，放入豆油，烧至六成热时，放入鹌鹑炸黄，捞出，待油九成热时，放入复炸一次，使鹑皮起脆，捞出，控干。\r\n4.用净纱布包后拍松，再去掉纱布装盘，配上焯过的油菜叶，配芝麻盐一', '呈樱红色，味咸甜辣，香醇鲜嫩。', NULL, NULL, '鹌鹑5只，淀粉25克，油菜5克，花椒10粒，豆油1000克（实耗75克），大料2颗，白糖10克，酱油15克，醋10克，精盐2克，大葱10克，料酒25克，芝麻辣盐5克，生姜10克。');
INSERT INTO `recipe` VALUES (2446, 82, '香酥鸡腿', '1.将油鸡腿除尽茸毛，洗净后，用洁布吸干水分，放入瓷盆内，用盐、糖、糖色、酱油、芝麻油擦抹鸡腿，然后，放入黄酒、八角、小茴香、桂皮、大葱段、生姜片。入锅，隔水蒸烂后，取出，滗去汁水，沥干，待用。\r\n2.炸锅烧热，放入豆油，烧至油冒清烟时，放入鸡腿，用旺火炸至鸡腿呈金红色时，倒入漏勺，沥去油，逐只炸透，撒上味精后，装盆，花椒盐、辣椒酱小碟单跟。\r\n3.食用时，附上甲皮饼，即可。', '呈金黄色，香酥松脆，清香可口。\r\n注：甲皮饼一用大米面、小豆、白糖、精盐，制成的半月形豆馅饼。', NULL, NULL, '油鸡腿12只，八角10克（大料），小茴香15克，桂皮25克，酱油15克，白糖25克，糖色25克，黄酒25克，大葱段50克，生姜片25克，盐5克，味精5克，豆油1000克（实耗100克），淀粉50克，辣椒酱40克，花椒盐25克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2447, 82, '蟹黄排翅', '1.将水发玉洁翅放入瓷盆内，放入清水，加入25克葱段、5克姜片、25克料酒，上屉蒸至鱼翅八成烂的时候取出，捡出葱段、姜片，滗出原汤不要。\r\n2.砂锅内放入水，下入鱼翅，坐在火上，加入葱段12.5克、姜片2.5克、料酒12.5克，煨透，捞出，捡去葱段、姜片控干水。\r\n3.炒锅上火烧热，用油滑锅后，加入芝麻油100克，烧热，下入葱段12.5克、姜片2.5克、炸至出香味，放入蟹黄煸炒至出红油，加入料酒12.5克，把锅盖盖上焖一下，再加入酱油、精盐、鸡汤，捡出葱段、姜片，放入鱼翅后，烧透加入味精，用水淀粉勾琉璃芡', '色泽金黄，味带甘香，软糯肥鲜。', NULL, NULL, '水发玉洁翅750克，蟹黄200克，鸡汤600毫升，料酒75克，精盐6克，白糖3克，水淀粉40克，酱油25克，味精2克，葱段75克，姜片15克，芝麻油150克，芝麻15克（焙好）。');
INSERT INTO `recipe` VALUES (2448, 82, '蟹黄鱼翅', '1.将母鸡开膛，去净内脏，剁去爪，洗净后，剔下鸡肉，去筋膜，洗净。\r\n2.再把鸡肉用刀背砸成粗鸡肉茸，放入盆中，加入适量的凉水调开，拌匀。\r\n3.烧热炒锅，放入5公斤鸡汤，待烧至四至五成热时，把鸡肉茸倒入锅内，用手勺搅拌均匀。旺火烧开后，用微火煨煮一会儿，用漏勺捞出鸡肉茸，撇去锅内浮沫，再将汤过箩，澄清，即成鸡清汤。\r\n4.将发好的鱼翅用开水反复焯煮几次，捞出，控水，整齐地码入平盘内。\r\n5.再将汤勺坐上火，加入白鸡清汤、味精、料酒、酱油、白糖、咸面、糖色和盘内的鱼翅，旺火烧开后，改用小火煨至鱼翅软烂，待', '色泽淡红，鱼翅柔嫩软烂，鲜咸浓香。', NULL, NULL, '水发黄翅750克，净蟹黄250克（罐头），母鸡1只（约1.5公斤），鸡油100克，味精10克，料酒10克，淀粉15克，酱油10克，白糖10克，鸡汤5公斤，咸面1克，糖色5克，芝麻油10克。');
INSERT INTO `recipe` VALUES (2449, 82, '蟹里藏珠', '1.大青蟹洗净泥沙，去脐洗净后，入笼蒸熟，取出，揭去蟹盖，挖去蟹内沙包，蟹壳待用；蟹身拆去肉，蟹脚用剪刀修齐，涂上芝麻油，放入长盆内，两端摆成原蟹脚形状。\r\n2.鸽蛋洗净，放入冷水碗里，上笼蒸熟后，取出，剥去外壳。\r\n3.炒锅烧热，放入熟鸡油，五成热时，投入葱姜末煸炒出香味，加入盐、味精、高汤、鸽蛋，煨入味后，下入水菱粉，勾成琉璃芡，汁要少。\r\n4.净锅，把熟鸡油化开，用葱姜末煸炒出香味，将蟹内放入煸炒，即烹入料酒，加入糖、盐、鸡汤煨，待汁浓干时，取出，装入盆中两端蟹脚中间，再放入鸽蛋，扣上蟹盖，即成蟹原', '呈五彩色，味清爽口，形如活蟹。', NULL, NULL, '大青蟹2只，鸽蛋10只，油菜叶100克，净土豆100克，鸡蛋清3只，干菱粉25克，盐5克，料酒15克，味精5克，鸡汤150毫升，白糖5克，葱姜末20克，芝麻油10克，熟鸡油50克。');
INSERT INTO `recipe` VALUES (2450, 82, '杏仁炒猪肉丁', '1.把猪里脊肉去筋，洗净，切成1厘米厚的大片，两面剞十字花刀，再切成1厘米方的丁，用15克湿淀粉，1克精盐，鸡蛋液搅拌均匀，鲜红辣椒去把、籽，切成1厘米长宽的片。\r\n2.炒锅烧热放入豆油，五成熟时，放入杏仁，炸黄，捞出，控油，晾凉，去皮。\r\n3.原炒锅豆油凉一下，待三成热时，放入猪肉丁，要做到热锅凉油防粘锅，肉丁入锅后滑油至七成熟时，捞出，控油，倒出炸油。\r\n4.炒锅内留75克素油（豆油），旺火烧热，放入红辣椒片，加少许盐。炒出香味，放入去皮的杏仁，炒匀而不糊，倒入猪肉丁，放入15克酱油，少许精盐、米醋、', '肉丁细嫩，杏仁脆香，辣咸微酸。', NULL, NULL, '猪里脊肉400克，杏仁50克，豆油500克（实耗75克），大葱15克，湿淀粉25克，酱油15克，精盐2克，鲜红辣椒75克，鸡蛋1个，米醋5克，芝麻30克（焙好）。');
INSERT INTO `recipe` VALUES (2451, 82, '杏仁鹑脯', '1.将鹑脯肉洗净，改切成1.5厘米见方的块，剞上花刀。\r\n2.炒锅化熟鸡油，待五成热时，下入鹑肉丁，炸黄，捞出，沥油；再放入杏仁炸熟，捞出，控油，晾凉，去皮，余油倒入油罐。\r\n3.炒锅净后，烧热，放入25克熟鸡油，待五成热时，投入葱段、姜片煸炒出香味，再烹入酱油、精盐、料酒、白糖、味精、鸡汤，烧开，放入炸好的熟脯肉，撇去浮沫，改小火至肉烂，下杏仁，用旺火收汁，淋入芝麻油，入盘。\r\n4.食用时，配松饼同吃即可。', '色泽褐红，清香利口，鹑肉酥烂。', NULL, NULL, '鹑脯肉300克，醋5克，杏仁75克，酱油25克，净葱段25克，料酒15克，芝麻油25克，精盐0.5克，鸡汤500毫升，白糖10克，味精5克，姜片25克，熟鸡油500克（实耗100克）。');
INSERT INTO `recipe` VALUES (2452, 82, '杏仁豆腐', '1.将杏仁用开水泡开，剥去皮，切碎；再将大米洗净，与杏仁一起加400毫升清水磨成浆，磨得越细越好，过细箩，待用。\r\n2.再将冻粉洗净，加100毫升水，上笼蒸20分钟后，和磨好的浆一起放在一个干净的锅里，加上50克糖，烧开后，去掉沫，加上杏仁精搅拌均匀，分装在10个小碗里，晾凉后，放冰箱冰镇。\r\n3.再将750毫升清水加250克白糖，烧开后，晾凉，冰镇；金糕与洗净消毒的黄瓜均切成20块月牙片，待用。\r\n4.用小刀将杏仁冻斜划6道，再交叉打6道，注意不要粘碗边，每碗放入2块金糕片、2块黄瓜片，将冰镇的糖水分装', '色泽鲜明，质地细腻，清凉甜美。', NULL, NULL, '杏仁150克，大米50克，冻粉10克，白糖300克，杏仁精0.5克，金糕50克（山楂糕），黄瓜50克。');
INSERT INTO `recipe` VALUES (2453, 82, '绣球蒸菜', '1.油发肉皮放热碱水中泡软，改用清水漂洗干净，放沸水锅中焯过，捞出，切成小粒，用纱布包好，挤干水；瘦猪肉、肥猪肉洗净，去筋膜，去皮切成豆粒大的肉粒；火腿也切成小粒；鸡肉去皮筋，切成片；净鱼肉去皮，横切成片；将鸡肉片、鱼肉片一起放在净案上，剁成茸泥；去皮马蹄、葱白、生姜均去皮，洗净，剁成末；菜心的头削成橄榄形。\r\n2.炒锅放入25克豆油，烧五成热，放入瘦猪肉丝煸炒，再放盐、蘑菇丝、木耳丝、韭菜段翻炒一下，淋上少许淀粉，制作成馅，出锅，待用。\r\n3.将鸡蛋打入瓷碗中，放淀粉、盐，搅拌均匀。\r\n4.放少许油滑锅', '呈金黄色，皮香脆酥，馅鲜美嫩。', NULL, NULL, '油发肉皮75克，瘦猪肉150克，鸡肉50克，肥猪肉50克，鱼肉50克，火腿50克，菜心250克，大葱白5克，去皮马蹄50克，生姜5克，鸡汤250毫升，鸡油50克，淀粉45克，食粉2克，胡椒粉3克，精盐10克，味精5克，芝麻油15克，芝麻25克（焙好），水淀粉15克，辣椒油15克。');
INSERT INTO `recipe` VALUES (2454, 82, '雪花鸡腿', '1.将鸡腿去净毛，洗净，撕去表皮，在大小腿骨节中间切开，在一段节骨处，用刀将肉、骨剔开，腿骨长则去一段（留7厘米长即可）。\r\n2.将6只鸡腿，做成12只球棒状，再用刀排列斩十字窗格花，即放入瓷盆内，加入盐、味精、黄酒、胡椒粉、芝麻油、鸡油、大葱末、生姜末，腌渍半小时，入味后，放蒸锅内，隔水蒸熟，即可取出，滗去汁水，待用。\r\n3.将鸡蛋清放入盆内，用筷子迅速打起来，使筷子竖立不倒即好，再放入干淀粉、面粉、搅拌均匀，然后将鸡腿逐只放入蛋糊内，沾满蛋糊，撒上面包粉，待用。\r\n4.炸锅烧热，放入豆油，待油烧至五成', '呈艳黄色，味鲜松嫩，香酥爽口。', NULL, NULL, '嫩鸡腿6只，鲜鸡蛋6个，杏仁6粒，黑芝麻12粒，面包粉500克，鸡蛋清6个，盐8克，料酒15克，味精5克，大葱末15克，芝麻油25克，生姜末10克，鸡油10克，豆油1000克（实耗100克），干淀粉15克，面粉15克，胡椒面2.5克，辣大酱20克。');
INSERT INTO `recipe` VALUES (2455, 82, '鸭丝炒鲍鱼', '1.将鲍鱼洗净，切成长达3厘米，厚3毫米的片；鸭肉去皮，去筋膜，洗净，切成长3厘米、厚2毫米的片；竹笋、蘑菇均切成片。\r\n2.再将鲍鱼片用开水烫好，待用。\r\n3.炒锅烧热，放25克花生油，烧至五六成热时，加入鸭肉片，煸炒至八成熟，铲出，控净油。\r\n4.再将余下的35克花生油倒入炒锅内，烧热后加辣椒面、葱末、姜末、蒜末煸炒烹锅，再加竹笋、蘑菇、青豆翻炒几下，加酱油、盐、料酒、鸡汤、味精和鲍鱼片，调好口味，加入鸭肉片，翻炒几下，淋少许淀粉勾芡，盛入盘内，撒上芝麻即可。', '色泽洁白，味香可口，鲍鱼肉嫩。', NULL, NULL, '鲍鱼肉125克，鸭肉100克，罐头竹笋15克，蘑菇25克，青豆20克，花生油60克，酱油15克，料酒15克，味精1.5克，淀粉50克，鸡汤50毫升，辣椒面2克，生姜5克，大蒜5克，精盐1克，芝麻5克（焙好）。');
INSERT INTO `recipe` VALUES (2456, 82, '盐水毛蟹', '1.螃蟹洗净，放入煮锅中，加入清水，清水要淹没螃蟹，再加盐、大葱段、生姜块、花椒压上锅盖，用旺火煮沸后，改用小火稍煮。\r\n2.拣出螃蟹，沥干，装盘。\r\n3.辣椒酱分装两小碟，用螃蟹肉蘸辣椒酱吃。', '色泽金黄，蟹肉香且嫩，诱人食欲。', NULL, NULL, '大毛蟹1000克，精盐50克，大葱10克，生姜20克，花椒2克，辣椒酱20克。');
INSERT INTO `recipe` VALUES (2457, 82, '杨梅虾球', '1.将新鲜大河虾剥壳，出虾仁，漂洗干净，控净水；猪肥肉去皮，去筋膜，洗净，用刀切成细粒。\r\n2.大葱、生姜去皮，洗净，用刀拍碎，放入盆中，加少许清水，浸泡成葱姜水；火腿切成小粒，放入盘中，摊开，待用。\r\n3.挑选去净肥膘的后腿猪皮一块，皮面向砧墩，将虾仁和猪肥膘放在肉皮上，用双刀斩成细茸，放入碗中，加少许葱姜水搅拌均匀，再加入鸡蛋清、料酒、精盐拌匀搅上劲，然后，用手挤成杨梅大小的圆子，放入火腿粒盘里，滚匀火腿粒。\r\n4.炒锅上火烧热，加入花生油，烧至成五成热时，将粘满火腿粒的虾球，逐个下入油锅中，用手勺不', '形似杨梅，胀发饱满，鲜嫩而香。', NULL, NULL, '鲜大河虾600克（出虾仁200克），瘦火腿50克，猪肥肉50克，鸡蛋清50克，料酒15克，精盐2克，味精1克，花生油1000克（实耗60克），大葱50克，生姜25克，辣椒油15克，辣酱油25克。');
INSERT INTO `recipe` VALUES (2458, 82, '一品鲜贝', '1.鲜贝洗净，控水，剁成泥茸，加生姜水，调成稠糊状，搅上劲，加入盐、味精、料酒、3只蛋清调匀，再加入白鸡油及淀粉搅拌均匀，待用。\r\n2.将调制好的鲜贝泥放入大盘内摊平，盘四周用一张油纸抹上少许油，上蒸锅，蒸10分钟左右，取出。\r\n3.制出高丽糊：三个鸡蛋清打入盘内，用筷子抽打起泡，加干面粉调匀。\r\n4.再将蒸好的鲜贝换盘，抹蛋泡糊用火腿、生菜叶，组织成图案由蒸锅蒸1分钟后，取出。\r\n5.炒勺内倒入鸡汤，加入生姜水、盐，烧开，放湿淀粉，勾芡，浇在一品鲜贝上即可。', '造型漂亮，华贵大方，味美滑嫩。', NULL, NULL, '鲜贝500克，面粉5克，白鸡油100克，生姜水10克，料酒15克，盐5克，味精3克，湿淀粉10克，蛋清6个，火腿50克，生菜25克。');
INSERT INTO `recipe` VALUES (2459, 82, '樱桃肉', '1.猪五花方肉用刀刮去表皮毛污，浸入冷水浸泡后，洗净，捞出，放入开水锅里烧开，氽去血污，捞出，洗净，切8毫米见方的小块。\r\n2.烧热锅放水1公斤，并加入肉块和葱姜块，用小火炖至八成烂，倒入漏勺，沥去水分，待用。\r\n3.炒锅烧热，放入豆油，烧至八成热，将肉块投入炸成牙黄色时，连油一起倒入漏勺，沥去油。\r\n4.原炒锅烧热，放入芝麻油，四成热时，放入番茄酱，待油炒至呈红色时，放入肉块，加入鸡汤、料酒、葱姜末、白糖、醋、盐、味精、樱桃汁，用小火煨15分钟，再用旺火至汤汁浓厚，出锅，装盆。', '艳红色，酸中带甜，酥烂适口。', NULL, NULL, '五花猪肉500克，豆油500克，番茄酱100克，白糖75克，醋30克，盐5克，芝麻油50克，味精5克，樱桃汁50克，大葱25克，鸡汤500毫升，生姜15克，料酒15克。');
INSERT INTO `recipe` VALUES (2460, 82, '油爆大虾', '1.将虾剪去钳、须、脚，洗净，控干。\r\n2.炒锅内放入花生油，用旺火烧至八成热时，放入大虾，用勺不断推动，速炸5秒钟左右，用漏勺捞起，控油，待用。\r\n3.将炸油倒出，再将虾放回炒锅内，加入葱段、料酒、白糖、酱油，用旺火略炒，颠翻炒锅，烹入醋，出锅即可。', '虾肉鲜嫩，酸甜鲜美，咸鲜适口。', NULL, NULL, '大虾300克，葱段1克，料酒15克，酱油25克，白糖25克，醋15克，花生油500克(实耗50克)。');
INSERT INTO `recipe` VALUES (2461, 82, '油爆时脊丁', '1.将里脊肉洗净，去筋膜，切成3厘米见方的丁，用清水浸泡后，捞出，控干。\r\n2.把里脊丁放在碗内，加入蛋清、盐、水淀粉拌匀上浆，再另取小碗，放入青蒜末、葱段、大蒜片、料酒、盐、味精、醋、鸡汤、水淀粉，调成芡汁。\r\n3.炒锅烧热放入豆油，烧至六成热时，放入里脊丁，划至九成熟，倒入漏勺，沥去油，把余油倒入油罐。\r\n4.原炒锅烧热，放入芝麻油，将里脊倒入煸炒几下，随即倒入调匀的芡汁，迅速用旺火翻炒一分钟，出锅，装盆，撒上芝麻面即可。', '色白透亮，肉丁鲜嫩，味美可口。', NULL, NULL, '猪里脊丁400克，鸡蛋清1个，青蒜15克，大葱段15克，大蒜片10克，盐5克，味精5克，料酒15克，醋5克，鸡汤100克，水淀粉70克，豆油1000克（实耗100克），芝麻油25克，芝麻面25克（焙好）。');
INSERT INTO `recipe` VALUES (2462, 82, '油爆兔肉', '1.将兔肉切成1厘米见方的丁，放入瓷碗内，加蛋清、精盐、淀粉、5克芝麻油，搅拌均匀，上浆。\r\n2.花生米用温水泡好，去净皮，放入油锅中，炸成金黄色，捞出，入碗，撒少许盐，拌匀。\r\n3.小瓷碗内放入精盐、味精、香醋、淀粉、5克芝麻油，勾兑成调味芡汁。\r\n4.炒锅烧热，放入熟花生油，烧三成热时，投入兔肉丁，散开滑透捞出，沥油。原炒锅留底油50克，下入葱片、蒜片、姜末炒至起香，放入兔肉丁、花生仁，烹入兑好的调味粉芡汁，翻炒焙好的芝麻面，即可。', '花生米脆，兔丁嫩软，易于吸收。', NULL, NULL, '净兔子肉300克，熟花生油500克（实耗75克），花生仁75克，淀粉30克，蛋清50克，味精1克，精盐3克，蒜片10克，醋1克，芝麻油10克，葱片10克，姜末5克，芝麻面5克，鸡汤50毫升。');
INSERT INTO `recipe` VALUES (2463, 82, '柚子花菜', '1.选择熟透且鲜美，无伤痕的柚子，用刀尖水削去皮后，切成四等份，去掉核，然后，顺着纤维质方向切丝，放入糖腌渍。\r\n2.把梨削去皮，分两瓣，去核，切成薄片，即切成与柚子丝同样长短大小的梨丝。\r\n3.把柚子丝由碗心向碗沿口，即碗的半径上依次摆放成两个对顶圆心的扇状面；按同样方法把梨丝也装入该碗内，使黄白两色均称相同，开成色调对比和谐美。\r\n4.再把石榴粒和桦仁集中堆放在碗心，即柚、梨从丝对称的位置上，在中心衬映几点红。\r\n5.最后，将清水加白糖，烧开，去沫，晾凉后，倒入碗内，水平即可。', '造型美观，色泽漂亮，清凉爽口。', NULL, NULL, '柚子2个，梨1个，白糖175克，清水600毫升，石榴粒8粒，松仁25克。');
INSERT INTO `recipe` VALUES (2464, 82, '炸鲍蛋', '1.将鲍鱼洗净，撕去毛边，两面均直剞花刀，大个的坡刀剖成两半，小个的不剖。\r\n2.鸽蛋洗净，用清水煮熟，泡入凉水内，剥去外皮，放入盘内，沾匀盐入味。\r\n3.鸡蛋清加玉米调成蛋清糊。\r\n4.再将鸡汤倒入锅内，烧开后，放入鲍鱼，用小火略煮10分钟左右，把鲍鱼氽透，捞出，滗净汤，放于盘内，待炸。\r\n5.炸锅内放入花生油，烧至六、七成热时，把鲍鱼先撒上玉米粉搅匀，再沾匀蛋清糊，投入锅内炸呈至金黄色，捞出，控油，放在大圆盘内。\r\n6.炸锅烧至八成热，把鸽蛋沾上玉米粉，投入油锅内，炸成金黄色，捞出，控油，围在炸鲍鱼盘', '外焦里嫩，味道鲜美，脆香诱人。', NULL, NULL, '鲍鱼肉400克，鸽蛋15个，鸡汤1公斤，鸡蛋清4个，盐10克，料酒25克，玉米粉25克，花生油500克(实耗75克)，芝麻盐25克，辣椒酱10克。');
INSERT INTO `recipe` VALUES (2465, 82, '炸鹑排', '1.将鹌鹑肉洗净，剞上细的十字花刀，再用刀背轻拍，使其肌肉纤维松散，放入大瓷碗内，加精盐、甜酒酿、五香粉、辣酱油、葱段、姜片朱10克。\r\n2.咸面包切成片，在小火上烤脆，并用擀棍压成粉状。\r\n3.鸡蛋清磕入碗内，打散，将腌好的鹑脯肉在鸡蛋清中蘸均匀，再拍上面包粉，并用手按紧。\r\n4.炸锅放入豆油，五成热时，放入鹑排炸黄，捞出，待油九成热时，放入鹑排复炸一次至深黄色，而不能炸焦，捞出，控油，改刀，入盘。\r\n5.食用时，配辣椒酱蘸食，即可。', '呈深黄色，酥脆鲜嫩，味香醇厚。', NULL, NULL, '鹑脯肉250克，花生油500克（实耗60克），咸面包150克，甜酒酿20克，蛋清150克，五香粉1克，辣酱油15克，精盐1.5克，葱段15克，辣椒酱25克，姜片10克。');
INSERT INTO `recipe` VALUES (2466, 82, '炸鳜鱼', '1.将鳜鱼去鳞、鳃、内脏，洗净，控干，用刀两面打上细柳叶口，瘦猪肉、竹笋、松蘑、葱白均切成丁。\r\n2.用150克湿淀粉均匀地涂在鱼身上及刀口内，然后，手提着鱼尾，浸入六成热的豆油锅内拖炸，至淀粉结壳后，再全部投入锅中。\r\n3.炸约5分钟，捞出，待油九成热时，放入鳜鱼速炸速捞，控油，入盘，保温。\r\n4.原炒锅倒出余油，留25克底油，烧热，放入葱白丁、笋丁、肉丁、松蘑丁等下锅煸炒，烹入料酒、酱油、白糖、味精和鸡汤，烧开后，将醋、湿淀粉25克调匀，入锅勾成薄芡，淋上熟油25克，盛入碗内。\r\n5.食用时，将芡汁浇', '色泽黄亮，脆酥酸甜，咸香微酸。', NULL, NULL, '鳜鱼1条（约750克），熟瘦猪肉25克，罐头竹笋25克，葱白5克，松蘑25克，鸡汤200毫升，料酒25克，酱油50克，白糖75克，味精1.5克，醋75克，豆油1500克（实耗60克），湿淀粉175克。');
INSERT INTO `recipe` VALUES (2467, 82, '炸牛排', '1.将牛里脊肉去筋膜，切成长13厘米、宽8厘米、厚6毫米的块，用刀面拍一拍，每块相距6毫米剞一刀，放入瓷盆内，加入盐、味精稍腌渍，入味。\r\n2.再将50克鸡蛋打匀，把渍过的牛排沾上面粉，蘸上蛋液，放在盛有芝麻的盘内，使牛排两面均粘上芝麻，待炸。\r\n3.炸锅烧热，放入熟花生油，烧至六成熟时，逐块下入牛排，约炸2分钟，将牛排翻身。再炸1分钟左右，至呈金黄色时，用漏勺捞起，控干油，每块切成6小块。\r\n4.食用时，上桌入盘，随带一小碟酱油蘸食即可。', '牛肉嫩香，咸鲜微辣，味美适口。', NULL, NULL, '牛里脊肉800克，白芝麻20克，鸡蛋50克，辣酱油25克，精盐2.5克，味精2克，精白面粉15克，熟花油1000克（实耗75克）。');
INSERT INTO `recipe` VALUES (2468, 82, '炸牛肉串', '1.先将牛臀尖洗净，剔净筋膜，放入冰箱冰冻2小时。\r\n2.取出冻牛肉切成6毫米的厚肉块，再改成5毫米长的片。\r\n3.瓷碗中放芝麻油、酱油、芝麻面、大蒜泥、白糖、胡椒面和辣椒泥，调和拌匀，放入牛肉片，抓匀，腌渍入味渗入肉内。\r\n4.把蘑菇切成宽条，小的蘑菇可一切两半，大葱去皮、叶，洗净，取大葱白，切成5厘米长的条。\r\n5.再把牛肉片、大葱白、蘑菇条按顺序穿在竹签上，若竹签太长，可去掉一点，竹签的头要削光，穿起来才迅速方便。\r\n6.将做好的牛肉串沾上一层面粉，再放进打好的鸡蛋里挂糊。\r\n7.炸锅内放入植物油，', '味香辣鲜，外脆里嫩，色泽金黄。', NULL, NULL, '牛臀尖250克（或腱子肉），芝麻油40克，大葱120克，鸡蛋100克，面粉25克，酱油15克，芝麻面2克（焙好），大蒜泥2瓣，白糖6克，胡椒面0.5克，鲜辣椒泥3克，鲜蘑菇185克，大米饭100克，植物油500克（实耗50克）。');
INSERT INTO `recipe` VALUES (2469, 82, '炸烹肉段', '1.猪腿肉洗净，去筋，切成长5厘米、宽2厘米的长方的段，用刀划排列十字刀口，然后放入碗内，加盐、水淀粉、蛋清挂糊。\r\n2.炒锅烧热，放入豆油，待烧至八成热时，放入肉段，炸至牙黄色时，倒入漏勺，沥去油，随即在锅内加入芝麻油，倒进肉段一颠翻，即倒入用葱姜丝、盐、味精、料酒、鸡汤调成的小料汁，撒上焙好的芝麻，一烹翻炒几下，即可。', '呈牙黄色，外脆里嫩，味鲜微酸。', NULL, NULL, '猪腿肉300克，大葱丝15，水淀粉50克，生姜丝15克，鸡蛋清2只，盐8克，味精5克，醋10克，料酒15克，鸡汤75毫升，芝麻油25克，焙好芝麻25克，豆油1000克（实耗100克）。');
INSERT INTO `recipe` VALUES (2470, 82, '炸琵琶虾', '1.将明虾4只去头，剥去身壳，尾部保留，用剪刀从背部剪开，挑去沙筋，洗净。\r\n2.再将猪肥肉去皮，切细和虾仁一起置于砧墩上，用双刀斩成细泥茸。\r\n3.把精白面包去四周外皮，切成每片约3毫米的厚片；再把火腿蛋皮、净葱叶、水发冬菇切成和去头明虾身段一样长的各4根，待用。\r\n4.再将明虾肉用刀从背部剖开，刀口深至虾肉腹倍，但不剖断，剖好后摊开，在剖面用十字刀法轻划一下，置于碗中，加入10克料酒、1.5克精盐、0.5克味、葱段、姜片、胡椒粉，腌渍入味。\r\n5.将30克鸡蛋清放入碗中，用筷子稍打，加入细干粉，调成稀', '形似琵琶，松脆鲜嫩，美观大方。', NULL, NULL, '新鲜大明虾4只（约重250克），虾肉75克，猪肥肉25克，精白面包200克，火腿15克，鸡蛋清50克，鸡蛋皮25克，大葱叶1根，水发冬菇2个，细干淀粉15克，料酒15克，精盐3克，味精1克，豆油750克（实耗60克），生姜2片，胡椒粉1克，辣大酱25克，葱段5克，葱姜水10克。');
INSERT INTO `recipe` VALUES (2471, 82, '炸蒸狗肉', '1.将熟白狗肉片去皮，切成厚片，剞上浅花刀，再切成骨牌块，放入碗内，用酱油、料洒拌匀，把山药洗净，去皮，切成滚刀块，待用。\r\n2.大葱去皮，洗净，切小段；鲜姜洗净，去皮，切块；干辣椒洗净，去蒂；大料用水清洗一下，待用。\r\n3.炒锅上火，放入香油烧热，下入拌匀的肉块；炸至呈金黄色，捞出，控油，码在碗中的狗肉片上，加上味精、大酱、狗骨汤、葱段、姜块、干辣椒、大椒，上屉蒸熟，取出，捡去葱、姜、干辣椒、大料，扣入碗中即可。', '肉嫩汤鲜，醇香适口，咸鲜微辣。', NULL, NULL, '熟白狗肉250克，山药250克（或冬瓜），香油500克（实耗60克），干辣椒2个，酱油15克，大酱10克（韩国称炖酱），料酒15克，狗骨汤50毫升，生姜15克，大葱25克，大料5克，味精2克。');
INSERT INTO `recipe` VALUES (2472, 82, '炸大叶芹', '大叶芹，喜欢生长山地背阴处，多年生草本植物，株体光滑无植物毫毛，株高约50-80厘米，上部有分枝，散发香气，与芹菜形状相似，是春季有助食欲的山菜，并可提神，去春困症。大叶芹含有多种维生素，以维生素A、B、C为多，对高血压、中风有预防作用，也可治肝炎、神经痛、白带多等症，具有抗癌功能。\r\n1.摘取大叶芹叶子和芝麻株叶，洗净，控去水分。\r\n2.把蟹肉去软骨，撕碎，搅烂呈泥茸状，放入瓷盆内，加入面粉、鸡蛋液、清水、盐、酱油、胡椒粉、辣椒丝搅拌均匀，成粉衣料。\r\n3.大叶芹叶和芝麻叶放入拌匀的粉衣料中，搅拌均匀并', '外脆里嫩，味道清香，咸鲜甜香。', NULL, NULL, '大叶芹100克，芝麻叶200克，蟹肉50克，豆油500克（实耗50克），芝麻15克，酱油25克，食醋5克，白糖5克，葱末5克，鸡蛋1个，清水半杯，面粉150克，食盐3克，酱油3克，胡椒面2克，辣椒丝3克。');
INSERT INTO `recipe` VALUES (2473, 82, '炸海螺', '1.海螺去盖洗净，由中间割开取肉，用刀拍一下，两侧由里向外斜剞数刀，然后\r\n从中间切断，装入碗内，加入料酒，胡椒面、酱油、盐、味精，搅拌均匀，将海螺肉逐个沾上面粉，装在盘中，待用。\r\n2.炒锅烧热，放入花生油，烧至七八成热时，将海螺肉一个一个地投入油中，用手勺不断地翻动，炸至呈金黄色时，捞出，控油，放入盘内。\r\n3.食用时，随跟一小碟辣椒酱，蘸食即可。', '口味酸辣，以壳为碗，妙趣横生。', NULL, NULL, '海螺200克，花生油50克，面粉250克，料酒25克，胡椒面2克，辣椒酱2克，酱油15克，精盐2克，味精1.5克。');
INSERT INTO `recipe` VALUES (2474, 67, '八宝辣酱', '将鸭肫片，鸡丁，肉丁，肚丁，笋丁，水发海米，花生米放在碗里拌和。熟猪油烧至四成热，放虾仁滑熟后捞出。锅内炒红辣椒煸出红油，放入已拌好的鸡丁等，加味精、酱油、白糖、豆瓣酱、肉清汤烧开，小火烧3分钟转大火，湿淀粉勾芡淋入熟猪油，除锅装盘，将滑熟的虾仁和剩下的肉清汤烧开，湿淀粉勾芡加熟猪油，浇在鸡丁上即可。', '菜色泽艳丽光亮，鲜辣辛香。', NULL, NULL, '已浆虾仁，花生米，猪腿肉丁，鸭肫片，笋丁各50克，肉清汤、熟鸡肉丁各75克，熟肚丁，豆瓣酱，白糖各25克，熟猪油150克，水发海米10克，湿淀粉40克。');
INSERT INTO `recipe` VALUES (2475, 67, '白肚', '1.将肚子翻过来，用水洗净内壁污渍，剪去肥油，然后仍翻过去，用明矾擦透，洗净粘液，下开水锅氽一下捞起，刮去白衣，再用明矾、米醋擦透洗净，放入开水锅中，加入料酒、姜、葱烧至肚子八成酥时捞起，将肚子顺长一剖两半，平放在盆内，肚子上面压上重物（使肚子平整），待其自然冷却。 \r\n2.将冷却后的肚子修成6.6厘米宽的长方块（共3块），随即内壁向上，切成6.6厘米长、1厘米宽的条块，排齐作为刀面；将其余肚子切片，装入盆中，两旁放两个刀面并排齐，上面覆盖一个刀面，略带桥形，肚子面上放香菜。 \r\n3.酱油分装在两只小碟内', '嫩，滑，爽口。', NULL, NULL, '猪肚子1只（约1千克），香菜5克。调料：姜葱10克，料酒50克，酱油25克，麻油5克，明矾5克，米醋10克。');
INSERT INTO `recipe` VALUES (2476, 67, '火夹糟青鱼', '1.用整青鱼一条杀好，理净，洗清，一切两开，留600克重的中段一段，正面用斜刀均匀地披成10条左右的斜缝，每条斜缝不能披穿，再抹上盐放着腌半小时；另用香糟和酒拌和，将鱼放入糟约三小时左右，使它入味。然后取出，用冷水稍洗一下。\r\n2.将火腿片成10片2寸长的薄片，在鱼上披好的每条斜缝内嵌1片，放在长盘内，加葱、姜、盐和味精，上笼蒸约十至十五分钟，取去葱、姜。如喜吃葱、姜，可在上面另行放上少许葱丝和姜丝，再浇上少许熟猪油即好。', '味清香鲜嫩，适宜于春冬二季。', NULL, NULL, '青鱼中段1段、600克、火腿45克、香糟160克、整葱2根、姜片3片、黄酒75克、盐20克、味精少许');
INSERT INTO `recipe` VALUES (2477, 67, '鸡火干丝', '1.豆腐干每块批成15块左右的薄片，再切成约3.5厘米长的细丝，放入盛器，加少量盐，用沸水浸泡三次，每次间隔1小时，再和清水过净，捞出沥干水分。\r\n2.开洋加温水稍浸，去除外壳，放在碗内，加绍酒少许，上笼蒸透涨发。\r\n3.炒锅置旺火，放熟猪油40克烧热，加鸡汤和肉骨汤，将干丝、开洋下锅烧沸，加酒和盐，移小火烩煮15分钟，使干丝涨发吸足鲜汁。出锅前下豆苗，淋熟猪油10克，将干丝倒在汤碗里，鸡丝和火腿丝放在干丝面貌一新，豆苗放在干丝四周即成。', '“鸡火煮干丝”是上海扬州饭店名菜。扬州饭店的前身，是上海著名的“莫有财厨房”。创建人是过去长期在银行和私人公馆厨房负责烹饪的扬州著名厨师莫德峻、莫有赓、莫有财、莫有源父子四人。他们在上海工商界人士的建议下，于1950年6月开设了一个公馆式的厨房，设在宁波路上海银行大楼三楼上的上海纱厂工商界人士联合俱乐部内。是一间可容纳四五十人就餐的小餐厅，但设备齐全；因当时常由莫有财出面办事，大家便称它为“莫有财厨房”。每天供应一二十种扬州菜肴，并经常调换花色。1956年以后，该厨房扩大服务，成为上海各界人士聚餐、宴请的', '干丝切得薄而细，用淡盐水浸泡去除豆腥味，能使其更加洁白柔软。烩煮时必须用鸡汤和肉骨汤相配，口味鲜美，汤汁醇浓。', NULL, '豆腐干400克，味精1.5克，熟鸡丝50克，绍酒10克，熟火腿丝25克，熟猪油50克，豆苗25克，鸡汤250克，开洋25克，肉骨汤250克，精盐1.5克。');
INSERT INTO `recipe` VALUES (2478, 67, '鸡汁排翅', '1.鱼翅浸入锅内滚烧，取出抽入清水里浸漂，去除腥味，剔清杂质，更换清水，再行浸漂。如此反复得3次，除尽腥味、杂质。然后将其整齐地摆放在纱布上，包裹起来放在锅里，用鸡汤（250克）滚烧一遍。 \r\n2.钵中先放排骨，次放布包鱼翅，再摆上鸡腿2只、葱结25克、姜片10克、绍酒25克、猪肥膘、鸡汤250克，上笼蒸1.5小时，捞出鱼翅、姜片、绍酒、鸡汤，上笼蒸2.5小时，直至鱼翅软润，取出鱼翅。\r\n3.炒锅放熟猪油75克，加姜片5克、葱结5克，煸出香味，下绍酒25克、鸡汤500克，烧出葱姜香味，捞出葱姜，放入鱼翅，', '鱼翅，主要吃翅筋，状似粉丝，质地鲜嫩，清香爽口，而且蛋白质含量较高。此外，还有脂肪、糖类及其他矿物质。但鱼翅所含的蛋白质缺少一种必需的氨基酸（色氨酸），是一种不完全的蛋白质，本身少鲜味。因此，在烹调时，厨师们都注重配料，以取得营养上的互补和口味上的吊鲜助味。鸡汁排翅不仅主料名贵，而且配料精细多样。成品翅形完整，与浓汤调和，宛如玉块，衬以金黄鸡油，色泽宜人，翅糯汤鲜，特色显著。', '葱结：一般是以1-2根小葱打成一个结，在烹制时放入，菜肴成熟后拣出，取其香味，不作食用。鸡清汤：又称高级清汤、顶汤、上汤。将整只老母鸡放在汤桶里（也可将鸡斩成大块，在煮汤过程中使鸡肉的可溶性物质易于析出），加入原料量3-4倍的清水，用小火慢慢烧开（注意：在将要烧开时，要防止汤水沸腾，这是制鸡清汤的关键），立即改用微火，保持汤面没有波动，约煮3-4小时。在这期间，不要撇沫，任其血污上浮结块，防止搅散，使用鸡肉、鸡骨内的可溶性营养物质渗入汤内。煮好后，先将汤面上的浮沫轻轻撇净。然后将清汤滗出，再用纱布过滤即成', NULL, '水发排翅1000克，绍酒75克，干贝75克，鸡汤1250克，大排骨4块（约重400克），白胡椒粉0.25克，姜片25克，猪肥膘150克，精盐5克，母鸡腿4只，水淀粉35克，葱结50克，熟猪油100克，熟鸡油50克。');
INSERT INTO `recipe` VALUES (2479, 67, '上海锅贴', '1.面粉加开水和成面团，揉匀揉透后搓条摘成40只坯子，擀成圆形皮子待用。\r\n2.肉末放盘内，加盐、糖、味精、黄酒、葱姜汁和少许清水搅匀即成馅心。\r\n3.将馅心包入皮子内捏拢成月牙形生坯。\r\n4.平锅上旺火，加油，将锅贴下锅煎5分钟，加适量清水盖上锅盖，续煎10分钟左右，再将锅转动续煎数分钟即可。', NULL, NULL, NULL, '面粉500克，猪肉末300克，精盐10克，白糖10克，味精、黄酒、葱姜汁各适量，生油75克。');
INSERT INTO `recipe` VALUES (2480, 82, '炸指盖', '1.狗脯方肉洗净，放入清水中，浸泡一天后，捞出，控干，放入煮锅内，加清水、花椒、大料、桂皮大火烧开，去浮沫，煮半熟后，捞出，洗净。\r\n2.炖酱用清水调稀，倒入过滤煮肉汤，再放入狗脯方肉，炖烧至酥烂时，捞出，控干，待冷却后，切成长10厘米、宽2厘米的斜刀片。\r\n3.鸡蛋、水淀粉、精盐放入碗内，调成面糊，将肉片沾上面糊。\r\n4.炸锅放入工花生油，烧至八成热时，投入沾糊狗肉片，炸至表面呈牙黄色时，捞出，控油，用刀斩成手指块后，装盆上桌，附带小碟芝麻盐即可。', '呈牙黄色，外脆里嫩，油而不腻。', NULL, NULL, '狗脯方肉500克，鸡蛋1个，炖酱500克，水淀粉50克，精盐10克，花生油750克（实耗50克），芝麻盐25克，花椒5克，桂皮5克，大料5克。');
INSERT INTO `recipe` VALUES (2481, 82, '蒸牛肉卷', '1.牛肉冼净，去筋膜，剁成泥茸。\r\n2.蘑菇去蒂，洗净，水发木耳去蒂，洗净，大葱去皮，洗净，与竹笋均剁成碎末。\r\n3.炒锅放入花生油，烧五成热，放入牛肉末煸炒，再加入蘑菇末、水发木耳、竹笋末、大葱末翻炒几下，加味精，淋上芝麻油，用淀粉勾芡，与糯米拌匀，做成馅料，待用。\r\n4.将鸡蛋打入碗内，用筷子打散后放入淀粉，再打匀，加盐，拌匀。\r\n5.炒锅烧热，倒入少许油滑锅，将鸡蛋糊逐个摊成薄饼状，熟后扣于案板上，每张切成4片。\r\n6.用鸡蛋皮包馅料，卷成长条，切去两端毛料，整齐地竖放在盆里。把盆放入蒸笼中用急火蒸', '色泽鲜艳，口味鲜美，软糯适口。', NULL, NULL, '牛肉300克，蘑菇50克，水发木耳50克，罐头竹笋100克，糯米100克，大葱50克，鸡蛋12个，精盐10克，花生油40克，淀粉50克，味精1.5克，辣酱20克，芝麻油20克。');
INSERT INTO `recipe` VALUES (2482, 82, '蒸鸭卷鲜', '1.将猪脚爪用刀刮洗干净，斩成小块，用开水氽过，捞出，用清水洗净，放在瓷盆中，加入5克料酒、5克葱段、1片姜，少许清水，上蒸锅隔水蒸至酥烂，待用。\r\n2.油发肉皮用凉水浸泡后，片成片，加碱少许，用开水泡后，用手洗，换水漂清挤干水，竹笋切成条块。\r\n3.鸡蛋打入碗内，摊成1张蛋皮。\r\n4.猪肥夹心肉用清水洗净，置于砧墩上，用刀剁成泥茸，放入碗中，加入5克料酒、1克精盐、0.5克味精，搅拌上劲后，抹在蛋皮上，卷成蛋卷，上蒸锅用小火蒸熟。\r\n5.熟鸭肉斩成条块，整齐地码在扣碗中间，蛋卷斜切成块，码于鸭块两侧，放', '肉质软糯，鲜香可口，整齐美观。', NULL, NULL, '熟鸭肉100克，猪夹心肉150克，鸡蛋75克，鲜猪脚爪100克，油发肉皮75克，罐头竹笋75克，料酒15克，鸭汤650毫升，精盐7.5克，味精1.5克，葱段10克，生姜2片。');
INSERT INTO `recipe` VALUES (2483, 82, '芝麻虾球', '1.将虾仁浸泡，冲洗干净，剁成泥茸，放入瓷碗里，加入料酒、味精、芝麻、淀粉、蛋清、盐、搅拌均匀成虾糊，用手挤成虾球。\r\n2.炒锅烧热，放入花生油。烧至四成热时断火，一只只地放入虾球，至虾球全部入锅后，点旺火，用漏勺不断翻动虾球，炸至呈金黄色时，用漏勺捞出，控油。\r\n3.食用时，将芝麻虾球盛入盘里，随带小碟酱菜蘸食即可。', '油润清香，外松里嫩，味鲜爽口。', NULL, NULL, '虾仁200克，芝麻10克（焙好），淀粉25克，料酒5克，鸡蛋1个，精盐2克，辣椒酱3克，味精2克，花生油500克（实耗50克）。');
INSERT INTO `recipe` VALUES (2484, 82, '芝麻猪肉丁', '1.将瘦猪肉洗净，去筋膜，切成1厘米见方的丁；大葱去皮，生姜去皮，洗净，均切成末。\r\n2.鸡蛋打入碗内，抽打起泡，加入芝麻油、大葱末、生姜末搅拌均匀成渍汁，放入瘦猪肉丁，腌渍15分钟，取出，放在面粉里滚一下，再挂蛋液，然后，沾匀一层芝麻。\r\n3.炒锅烧热，倒入豆油，烧至七成热时，把沾好芝麻的猪肉丁放在肉锅内炸，炸至肉丁呈金黄色时，捞出，控油，入盘，即可。', '呈金黄色，香酥可口，芝麻味浓。', NULL, NULL, '瘦猪肉200克，鸡蛋1个，芝麻100克（焙好），豆油750克（实耗50克），面粉50克，料酒25克，精盐5克，大葱15克，生姜5克，味精2克，芝麻油20克。');
INSERT INTO `recipe` VALUES (2485, 82, '竹笋肝糕汤', '1.取新鲜猪肝洗净，去掉筋膜，放在案上，用刀背捶成极细的茸浆，装在碗中，取250毫升冷鸡汤掺在碗中调匀，再把肝浆倒在一块净细纱布上，滤去残渣，待用。\r\n2.再取鸡蛋3个，打破去蛋黄留蛋白，把蛋白放在浆碗中，适量放在些盐、味精、料酒和胡椒粉调匀，用一圆盘把碗口盖严，上笼蒸约15分钟。（注意：蒸肝糕时，用的气不要太大，否则肝糕要被气冲坏，时间不易过长，刚熟即取出，如时间过长，有蜂窝眼出现时，糕则变老）。\r\n3.干竹荪用温水浸泡半个小时，洗净泥沙，修去根部和杂质，再冲洗一次，控干，切成3厘米长的小节，用开水氽过', '肝质细嫩，竹荪味鲜，清汤爽口。', NULL, NULL, '干竹荪30克，鲜猪肝300克，鸡汤2公斤，鸡蛋3个，盐5克，料酒15克，味精2克，胡椒面5克，辣椒油1克，芝麻10克（焙好）。');
INSERT INTO `recipe` VALUES (2486, 82, '煮兔腿', '1.将兔后腿剔出骨头，切成长条，洗净，用酱油拌匀，腌渍4小时，使其入味。\r\n2.炒锅烧热，倒入熟豆油，烧三成热，把兔腿条投入锅中，炸成金黄色，倒入漏勺，沥油，余油倒出。\r\n3.原炒锅留余油15克，把葱段、姜片、大蒜头、枣、松籽仁、干辣椒炒出香味，倒入鸡汤，放入兔肉条、辣大酱、酱油、白糖、精盐、味精、料酒、盖上盖，用小火煮一个小时，拣出葱、姜、蒜、辣椒，再用旺火煮开，煨浓，淋上芝麻油，出锅。\r\n4.食用时，每碗盛匀兔肉、枣、松籽仁即可。', '枣红汁亮，松籽仁香，兔肉甜辣。', NULL, NULL, '兔后腿500克，松籽仁50克，干辣椒1个，大枣50克，酱油25克，熟豆油500克（实耗40克），料酒25克，葱段10克，芝麻油15克，姜片10克，白糖100克，大蒜头5克，味精1克，鸡汤750毫升，辣大酱20克。');
INSERT INTO `recipe` VALUES (2487, 82, '箸头春', '1.将鹌鹑煺毛，开膛取出内脏，剁去爪子，洗净，控干水，用净布擦净，用黄酒掺辣大酱拌匀，逐只擦抹净鹌鹑，里外抹匀，放入盘内，腌渍半小时，入味。\r\n2.炒锅烧热，倒入豆油，烧至六成热时，放入腌渍入味的鹌鹑，炸成酱红色，捞出，控油。\r\n3.原炒锅留30克豆油，烧热，放入葱花、姜末、大蒜泥炸香，添入鸡汤，烧开，加酱油、精盐、黄酒、番茄汁，放入鹌鹑，改小火焖酥。\r\n4.炸锅放入豆油，旺火烧八成热时，放入已焖酥的鹌鹑炸透，装盆。\r\n5.食用时，配花椒盐一小碟蘸食。', '肉质细嫩，酥香脆鲜，焦香扑鼻。', NULL, NULL, '鹌鹑10只，豆油500克（实耗50克），番茄汁35克，辣大酱10克，大葱10克，胡椒粉1克，生姜5克，酱油5克，大蒜5克，黄酒5克，精盐2.5克，香菜5克，鸡汤300毫升，花椒盐15克。');
INSERT INTO `recipe` VALUES (2488, 82, '鲻鱼汤', '1.将鱼去鳃和内脏，洗净，在鱼身两侧剞斜刀。\r\n2.猪肉去筋膜，洗净，与竹笋和蘑菇切丝；大葱去皮，洗净，切3厘米长的段；生姜、大蒜去皮，洗净，均切片。\r\n3.把鱼身两侧抹上酱油，腌渍入味。\r\n4.炒锅烧热放入35克花生油，烧至八九成热时，放入腌渍入味的鲻鱼，炸至两面呈金黄色，捞出，控油。\r\n5.再倒入炒锅内25克花生油，烧五成热，放入猪肉丝、翻炒几下，加入竹笋丝、蘑菇丝、料酒、酱油、味精、鸡汤、盐、葱段、生姜片、大蒜片。投入炸过的鱼，旺火烧开，改小火焖至汤剩下约200毫升时，拣去葱、姜、蒜，淋上几滴辣椒油', '汤汁鲜辣，鲻鱼鲜嫩，郁浓适口。', NULL, NULL, '蘑菇15克，猪肉50克，罐头竹笋15克，花生油60克，料酒15克，酱油20克，味精3克，鸡汤300毫升，精盐3克，大葱25克，生姜5克，大蒜3克，辣椒油0.5克。');
INSERT INTO `recipe` VALUES (2489, 82, '紫菜蛋卷', '1.韭菜摘洗干净，切末；大葱、生姜去皮，洗净，均切成末，待用。\r\n2.将猪肉馅放入瓷盆内，放10克盐，3个鸡蛋，20克水淀粉和味精、料酒、芝麻油、胡椒面、韭菜末、大葱末、生姜末搅拌均匀后，用力打上劲，待用。\r\n3.再将余下的9个鸡蛋，打入瓷盆内，加入5克盐、10克水淀粉，搅拌均匀后，待用。\r\n4.把铁锅净后，烧热，热的程度要热而不烫，擦少许油，烧热，抹掉油，试好热度，逐个加入已经打好的鸡蛋液，摊成8张圆形蛋皮，平放在净案板上。\r\n5.把搅拌均匀后的猪肉韭菜馅，放在蛋皮上抹平，上面再放一张紫菜，然后到放一层', '韭馅鲜美，紫菜郁浓，咸香微辣。', NULL, NULL, '紫菜8张，猪瘦肉馅1000克，鸡收12个，韭菜100克，盐15克，料酒10克，味精5克，芝麻油20克，大葱25克，生姜25克，胡椒面5克，水淀粉30克，辣大酱20克。');
INSERT INTO `recipe` VALUES (2490, 82, '走油鹑脯', '1.将鹑脯肉剞上交错十字花刀，剁成两块，罐头竹笋要切成滚刀块，用开水焯烫一下。\r\n2.将鹑脯放入瓷盆中，加入料酒、精盐、酱油腌渍片刻，然后，加入蛋清、淀粉搅拌均匀，上好浆。\r\n3.炒锅净后烧热，倒入熟花生油，烧至五成热时，投入鹑脯腿滑散，至六成热时，再放入竹笋片略滑片刻，一起倒入漏勺。\r\n4.原炒锅上火，放50克熟花生油，烧热，投入葱、姜稍煸，倒入鹑脯和冬笋，烹入绍酒，下鸡汤、酱油、白糖、精盐、味精、胡椒分、芝麻油，用小火略焖片刻；待鹑脯焖透入味，汤汁已余三分之二时，用湿淀粉勾薄芡，撒入焙好的芝麻，即可。', '鹑脯鲜嫩，滋味咸鲜，味鲜清口。', NULL, NULL, '嫩鹑脯肉500克，罐头竹笋200克，芝麻油10克，蛋清100克，酱油15克，鸡汤500毫升，精盐3.5克，白糖1克，料酒20克，味精1.5克，姜片10克，葱段10克，胡椒粉1克，熟花生油500克（实耗125克），湿淀粉15克，熟芝麻15克。');
INSERT INTO `recipe` VALUES (2491, 82, '走油狗肉', '1.将狗胸肉洗净，放入清水中浸泡半天，捞出，控水，放入煮锅中，加入75克料酒，75克大葱段，25克生姜片、50克大蒜头，大火烧开后，撇去浮沫，盖上锅盖，用小火烧至八成酥。\r\n2.炸锅上火，加入植物油，烧至八成热时，从煮锅内把狗胸肉捞出，控干，皮向上，趁热下入油锅内约炸5分钟，待肉炸成金黄色即可。捞出，控油，放入清水内浸泡30分钟，俟肉皮起皱纹即可取出，皮面向上放入锅中，加入酱油、75克料酒、150克白糖、桂皮、大料、100克葱段、15克姜片、原过滤煮汤，待烧至肉上色，即一块块起锅，肉卤盛入另一容器中。\r\n', '狗肉金黄，咸中带甜，肥而不腻。', NULL, NULL, '狗胸肉3公斤（约出走油肉10份），油菜心1.5公斤，料酒150克，酱油500克，白糖250克，精盐15克，味精15克，植物油3公斤（实耗300克），水淀粉75，大葱段200克，生姜片50克，桂皮50克，大料25克，大蒜头50克。');
INSERT INTO `recipe` VALUES (2492, 82, '走油田鸡', '1.将田鸡腿在连着的中间切断，在腿骨中间用刀剁断。\r\n2.再将田鸡腿放在瓷盆中，加入料酒、精盐，用手捏和，然后加入水淀粉拌匀，再将青椒去蒂，洗净，切成块；竹笋切成片。\r\n3.炒锅上火烧热，加入豆油，烧至五成热，逐只放入田鸡腿，炸至嫩黄色，用漏勺捞出，待油烧至七成热，再倒入田鸡腿，炸至金黄色时，放入青椒块、冬笋片，稍炸，倒入漏勺内。\r\n4.炒锅留15克余油，加入葱花，煸出香味，烹入料酒，加精盐、米醋、白糖、辣椒酱和鸡汤，加入水淀粉，溜透后，倒入炸好的田鸡腿，配料和蒜泥，翻几个身，使卤汁裹满田鸡腿，加入热油1', '色泽粽亮，甜辣适口，外脆里嫩。', NULL, NULL, '田鸡腿250克，青椒15克，罐头竹笋15克，大蒜泥10克，鸡汤100毫升，料酒15克，精盐1克，米醋50克，白糖60克，辣椒酱50克，豆油1000克（实耗75克），水淀粉150克，葱末10克。');
INSERT INTO `recipe` VALUES (2493, 67, '八宝鸭', '将肥壮嫩鸭宰杀治净，劈开背脊，剪去鸭脚，入沸水锅焯水后捞出洗净，沥干，在鸭身上抹上酱油；将笋丁，肉丁，火腿丁，栗子丁，鸡肫丁，冬菇丁，莲子，虾米，糯米饭放入碗内，加绍酒，酱油，白糖，味精，拌和成馅放入鸭肚内，背朝上放入盛器，上笼蒸三四小时，至鸭肉酥烂时取出翻扣在盘中；炒锅烧热，下猪油，将虾仁滑熟取出。锅内留油少许，放笋片，冬菇片，加酱油少许，蒸鸭原汁适量，烧沸后放虾仁和熟青豆，下湿淀粉少许勾芡，淋上猪油，出锅浇在鸭身上即成。', '成菜色泽红润，形状完整，鸭肉酥烂，腴香浓溢，汁浓味鲜。', NULL, NULL, '肥鸭，笋丁，肉丁，火腿丁，栗子丁，鸡肫丁，冬菇丁，莲子，虾米，糯米饭，绍酒，酱油，白糖，味精，虾仁，湿淀粉，熟青豆，猪油。');
INSERT INTO `recipe` VALUES (2494, 67, '白汁鮰鱼', '1.鮰鱼刮鳞、剖腹、去内脏、去鳃，清水洗净，放在砧板上，齐鳍斩下鱼头，在肛门处切下鱼尾。将钱中段剖成两爿，每爿各斩成4小块；鱼头一劈两片，再各斩成2块；鱼尾竖切成4块。将鱼块放入开水锅中略烫取出，用清水稍淋，漂洗干净。\r\n2.竹笋剥去壳，清水洗净，切成菱形。\r\n3.炒锅上旺火，放入猪油，烧至七八成热，下葱、姜煸出香味后捞出，再放入鮰鱼块稍煎，烹酒后加盖加重焖，以去其腥味，随即下笋片，加盐、糖、清水（以淹没鱼块为度），加盖烧开后用小火焖烧15分钟左右，再用旺火稠浓卤汁，放味精，出锅装盘。', '白汁鮰鱼是老半斋酒楼的春季名菜。色泽乳白，肉质细嫩，卤汁稠粘，滋味鲜美。鮰鱼又名江团，是一种珍贵的鱼类，它头呈现钦重型，尾长、嘴小、肚大，无鳞、少刺，肉质细嫩。明代著名的文学家杨慎曾称赞它说：“河豚有毒能药人，鲥鱼味美但刺多，鮰鱼兼有河豚、鲥鱼之美，而无两鱼之缺陷。”可见，在明代以前就把鮰鱼列为鱼中上品。', NULL, NULL, 'G鱼1条1250克，白糖5克，竹笋100克，味精2克，绍酒30克，葱结1只，盐3克，姜2片，猪油150克。');
INSERT INTO `recipe` VALUES (2495, 67, '爆鱿鱼卷', '鲜鱿鱼撕净套膜，摘下头脑、內脏，洗净后去角质內壳，在体內剞花刀后切片，下清汤中汆至入味捞出；用葱姜炝锅，下鱿鱼卷稍炒，调好味勾薄芡裝盘即可。', NULL, NULL, NULL, '鲜鱿鱼150克。');
INSERT INTO `recipe` VALUES (2496, 67, '冰糖甲鱼', '1.甲鱼仰放，待头伸出，迅速用手指掐住其颈，用力拉出，用刀齐背壳沿颈骨割开，排尽血后入90度热水中浸泡。当甲鱼壳上泛起白衣时捞出，在冷水中清除腹部、腿上和裙边的白膜，用洗帚刷掉背壳黑衣，开肚去骨脏，斩去头尾、爪尖，然后均匀地斩成8块，放入锅中焯水，捞出用清水洗净，并撕掉血筋、备块和黄油。\r\n2.炒锅烧热，用油滑锅，加入花生油，烧至八成热，放入葱结、姜片爆香，推入甲鱼块（肚朝下），烹入绍酒加盖稍焖，加入清水750克，烧开3分钟后，改用小火盖焖25分钟左右。待鱼块柔软无弹性时，加入精冰糖、熟猪油（35克），再', '每年四五月间，江南地区菜花盛开，正值甲鱼最为肥美，故有“菜花甲鱼”之称。此时是烹制冰糖甲鱼的最佳时期。冰糖容易使卤汁浓稠，加上甲鱼的胶汁，鲜美肥腴，有入口甜、收味咸的特点，是上海传统的火功名菜。', '大火烧开，小火焖熟，再加盐、酱油、糖火靠入味，改旺火收汁，中火溶芡。火功到家，入口甜，收味咸，风味别具。', NULL, '甲鱼750克，红酱油30克，葱结1个，冰糖75克，姜片3片，熟猪油65克，绍酒25克，花生油35克，精盐2克。');
INSERT INTO `recipe` VALUES (2497, 67, '冰糖元蹄', '1.将猪蹄膀放在清水里，刮洗干净，用刀在内侧软的一面顺长剖开，刀深见骨。再在大骨的两侧各划一刀，使其摊开，然后切去四面的肥肉成圆表。\r\n2.将蹄膀放入开水锅里煮10分钟左右，至外皮紧缩捞出，用清水洗净。\r\n3.炒锅内放一只竹箅子，将蹄膀皮朝下放在上面，下清水淹没，再加入绍酒、酱油、精盐、冰糖、葱结、姜片，用旺火烧开，加盖后端在小火上再烧半小时左右，将蹄膀翻身，烧至烂透，再用旺火烧到汤水如胶汁，泛出油光时，将蹄膀取出，放在汤碗里，皮朝下，拣去葱结、姜片，把卤汁浇在蹄膀上即成。', '此菜色泽红亮，猪蹄酥烂，卤汁如胶，味香浓，咸甜适口。', '猪蹄膀选重750克左右为宜。炒锅内一定要放竹箅子，否则易糊底，影响美观和口味。', NULL, '猪蹄膀1只，冰糖100克，绍酒50克，酱油50克，精盐1克，葱结5克，姜片2.5克。');
INSERT INTO `recipe` VALUES (2498, 67, '茶叶蛋', '1.将鸡蛋放入沙锅中煮至八成熟时捞出，用小面杖将鸡蛋敲破，将花椒、八角洗净，连同茶叶用洁净纱布裹好。\r\n2.鸡蛋放入沙锅内，加入香料包、绍酒、酱油及适量开水，用旺火约一个小时，蛋壳呈茶褐色时即可食用。', NULL, NULL, NULL, '鸡蛋20只，茶叶5克，八角克，花椒2克，绍酒5克，酱油100克。');
INSERT INTO `recipe` VALUES (2499, 67, '柴把鸽蛋汤', '1.将鸽蛋整个打在小酒盅里（不要打散，盅里先抹些猪油），再用香菜、火腿片在鸽蛋上面摆成图案，上笼蒸约二分钟取出。\r\n2.将冬笋、火腿、冬菇、鸡脯都切成1寸半长的条子12根（粗如筷子）；将扁尖笋撕成12条，与冬笋、火腿、冬菇、鸡脯扎成12个稻柴把，放在汤碗里，加盐、味精、清汤、酒、葱段、猪油、上笼蒸约一小时。\r\n3.上席时，将柴把扣在另一只汤碗内，鸽蛋放在四周，冲上热鸡汤，取去葱段即好。', NULL, NULL, NULL, '鸽蛋10只，熟鸡脯165克，火腿片80克，扁尖笋即嫩笋干头80克，葱段3、4段，冬菇50克，黄酒13克，时菜3条，味精6.5克，盐8.5克，鸡汤700克。');
INSERT INTO `recipe` VALUES (2500, 67, '炒毛蟹', '1.将活蟹用清水洗干净，顺长斩成2片，剁去爪尖和蟹脐，蟹肚刀斩处蘸满干面粉，不使蟹肚内蟹黄流出。 \r\n2.锅放旺火上，放油50克烧热，把蟹蘸面粉的一面朝下放入油内煎至淡黄色，然后将锅颠翻，使蟹壳脚都受热变红，烹酒，加酱油、白糖、姜末、毛豆肉、白汤，盖上锅盖烧至肉熟入味。最后加味精，待汤汁稠浓时下水淀粉推匀翻和，使卤汁滚粘在蟹壳上。淋油后盛出，整齐地装大圆盘内，盘边撒上香菜。', '此菜蟹壳鲜红，鸡蛋微黄，色悦目，味醇美鲜香。', NULL, NULL, '活毛蟹八两，黄酒三钱，鸡蛋二个，胡椒粉二分，葱末一钱，湿淀粉二钱，姜末二钱，食油八钱，精盐七分。');
INSERT INTO `recipe` VALUES (2501, 67, '炒虾腰', '1.猪腰洗净，揭去外皮，用刀冲开，去净腰臊，拉成条，再切成小象眼丁，泡净血水，搌干装入盘内。虾仁用清水淘净，搌干。香菇、玉兰片改成小象眼片。 \r\n2.蛋清、粉芡调成糊，将虾仁、腰丁下入叠匀。 \r\n3.锅放火上，热锅凉油，将叠好糊的腰丁、虾仁散开下锅，用勺蹚开，即出锅滗油。锅内留油少许，放火上，将香菇、玉兰片、青豆、姜米、盐水、头汤、粉芡、味精、料酒兑成的汁下入，汁沸下入虾仁、腰丁，翻二三次身，装盘即成。', NULL, NULL, NULL, '虾仁165克，猪腰250克，冬笋80克，水发冬菇45克，鸡蛋白1只，绿叶菜45克，葱段、白糖、菱粉、酱油、黄酒、味精、麻油适量。');
INSERT INTO `recipe` VALUES (2502, 67, '冻粉拌鸡丝', '1.冻粉切成5厘米长的段，放入碗内，加入冷开水450克，浸发约3分钟后捞出，沥干水分装入盆内，熟鸡肉撕成丝，铺放在冻粉上面。 \r\n2.芝麻酱用冷开水（50克）调稀后，加入盐、糖、味精拌和，淋上麻油，浇在鸡丝上面即成。', '鲜，脆，爽口。', NULL, NULL, '冻粉10克，熟鸡肉150克，芝麻酱25克，盐5克，糖2.5克，味精5克，麻油5克，冷开水500克。');
INSERT INTO `recipe` VALUES (2503, 67, '粉蒸肉', '1.以豆瓣酱、酒酿汁、酱油、酒、姜末、香油等将牛肉片腌渍，再拌上五香炒米粉。\r\n2.在蒸笼底铺上荷叶，摆上肉片蒸熟。另将香菜、蒜泥煸出香味，淋于肉上即可。', NULL, NULL, NULL, '牛肉，豆瓣酱，酒酿汁，酱油，酒，姜末，香油，五香炒米粉，葱花，香菜，蒜泥，辣椒粉，花椒面，荷叶。');
INSERT INTO `recipe` VALUES (2504, 67, '枫泾丁蹄', '猪蹄用温开水刮干净，抽掉管骨，放入开水锅中略焯去污血，修削外形然后放入汤锅，加清水，放丁香，桂皮，绍酒，葱，姜，焖烧至半熟，汤紧时，加优质酱油，冰糖；旺火烧开后，微火焖煮（俗称“三微三旺”，“以微为主”），使猪蹄外酥内熟，卤汁渗入猪蹄内层；如加隔年老卤汁，应滤去油膜和肉屑以保持汤味醇厚；出锅前用旺火烧煮，并放味精使卤汁稠浓，紧包猪蹄而入味；食用时，切片上桌。', '色泽红亮，外形完整，肉质细嫩，酥烂浓香，鲜美可口。', NULL, NULL, '猪后蹄，优质酱油，绍酒，冰糖，桂皮，丁香，味精，葱，姜。');
INSERT INTO `recipe` VALUES (2505, 67, '干煸四季豆', '1.猪肉洗净剁碎，暇米浸软切碎。\r\n2.四季豆撕去筋，洗净滴干水分。放入油镬中炸片刻盛起，滴去油份。\r\n3.烧热镬，下油两汤匙爆香姜茸，放入猪肉、暇米及炸菜炒片刻，加入四季豆、酒，再加入调味料，改中火至汁收干，撒上葱粒兜匀即可上碟。', '上海菜是以用料平、功夫多取胜，如干煸四季豆便是一款经济又惹味的小菜。干煸是上海菜其中一种烹调方法，特点是火候大、动作快，用料简单，煮出来的菜呈翠绿色，味道脆嫩。', NULL, NULL, '四季豆一斤、枚头猪肉三两、炸菜粒、暇米、葱粒各一汤匙、姜茸一茶匙、酒一汤匙、盐1/4茶匙、糖一茶匙、生抽半汤匙、麻油半茶匙、清水半杯。');
INSERT INTO `recipe` VALUES (2506, 67, '干切咸肉', '1.将猪后腿洗净，理清，用细竹签在腿肉上均匀地打上些小孔，两面抹上盐、硝各45克，放在瓦钵里，撒上花椒，上压洁净石块。一天后，泌去血水，再压好。三天后，抹上剩余的盐、硝、放四瓦钵，倒上黄酒，再压好。每隔二天翻转一次，约半个月后，肉色红透，香味很足，即已腌透，取出风干二三天。\r\n2.食用时，先将猪肉上的腌料洗去，再蒸熟冷却后切片即好。此菜色红，味香嫩不腻。', NULL, NULL, NULL, '猪后腿2只、盐700克、硝65克、花椒少许、黄酒200克。');
INSERT INTO `recipe` VALUES (2507, 67, '干烧虾', '1.炒锅上火，留底油油热放人大虾、肉丁、木耳丁、葱节煽炒。\r\n2.放入豆瓣酱、调料烧入味即可。', NULL, NULL, NULL, '大虾，木耳丁，葱节豆瓣酱，调料。');
INSERT INTO `recipe` VALUES (2508, 67, '蛤蜊汆鲫鱼', '1.鲫鱼洗净，从脊背出剖开，从背上剞几刀，下清水锅加调料、姜块、葱段、笋片烧开，用小火煮熟装入汤盆。\r\n2.蛤蜊下清水锅煮到外壳张开捞出，置于汤盆中。 \r\n3.滗出鱼汤，蛤蜊汤烧开，加香菇、熟火腿片调好料后倒入汤盆即成。', NULL, NULL, NULL, '活鲫鱼2尾，活蛤蜊250克，熟火腿3片，笋肉5片，水发香菇一只。');
INSERT INTO `recipe` VALUES (2509, 67, '枸杞肉丝', '1.枸杞只要叶，洗净滴干水份，葱洗净切段。\r\n2.瘦肉切丝，加入调味料配十分钟。\r\n3.烧热镬，下油一汤匙爆香姜片及葱段，加入肉丝、红萝卜丝炒片刻，放入枸杞拌匀，下芡汁料，再加少许熟油即可上碟。', '枸杞可炒食或放汤，有清心降火的功用，枸杞带有少许苦味，老的更苦，所以烹调时必须加糖。枸杞肉丝是一款经济有益的家常小菜。', NULL, NULL, '嫩枸杞六两、瘦肉四两、红萝卜丝半汤匙、葱一棵、姜一片、生抽一茶匙、生粉半茶匙、油半汤匙、盐1/4茶匙、糖、酒各半茶匙、生抽一茶匙、麻油少许、清水一汤匙。');
INSERT INTO `recipe` VALUES (2510, 67, '核桃肉卷', '将核桃肉放入碗，加入精盐，用开水淹没浸泡5分钟，取出，用小竹签挑去外皮，沥干后放入四成热的花生，油锅里氽熟。鸡蛋磕入碗内，加入面粉、精盐、绍酒和清水25克，调成蛋粉糊，猪肉切成约5厘米长、3厘米宽的片，摊在砧板上，涂一层蛋粉糊，将炸过的核桃肉放在每片的一端，卷成蚕茧形。花椒盐装入碟中待用。将炒锅置旺火上烧热，放入花生油，烧到三成热时，将肉卷拖上蛋粉糊，逐只放入油锅里，炸到蛋粉糊成熟时捞出。待油温回升至七成热时，全部一起复炸一次，炸至呈金黄色时，捞出装盘。配花椒盐碟一同上桌。', '色呈金黄，外皮松脆，猪肉鲜嫩，桃仁酥香，桃仁酥香，老少皆宜。', NULL, NULL, '猪里脊肉，核桃肉，鸡蛋，面粉，绍酒，精盐，花椒，盐，花生油。');
INSERT INTO `recipe` VALUES (2511, 67, '红烧划水', '1.草鱼尾洗净，剖成4小段，用酱油、糖腌3分钟待用；锅中放油烧热，放入葱、姜及腌过的鱼，快速将鱼翻面，用酒、糖入锅煮至鱼熟后，用淀粉加水。\r\n2.从锅周边淋下勾芡，然后再淋些香油便可起锅，盛盘后上面撒入青蒜丝即可。', '以青鱼尾巴（即划水）红烧而成。此菜工艺十分讲究，须经几次颠翻而鱼尾不断，其难度很高。', NULL, NULL, '草鱼尾500克，青蒜丝、糖、淀粉各5克，酱油50克，姜4克，香油、胡椒粉、酒各2克，精盐3克，油80克。');
INSERT INTO `recipe` VALUES (2512, 67, '红烧烤麸', '1.烧麸用水清洗后挤出水份，再用手撕成小块，油烧至八成热时，投入撕好的烧麸炸至呈金黄色，捞出沥油。\r\n2.笋去皮切片，香菇泡软对切，豆干每块斜切成3块，另外将金针、木耳泡软。\r\n3.锅内放油，将香菇、豆干、笋片、金针、木耳放入炒锅，最后加入炸好的烧麸，淋上酒后加入酱油，酒、糖、胡辣粉、味精、香油、姜片及八角，然后加水用中火煮至汁干，淋上香油食用。', NULL, '烤麸是由面粉加调料并且发酵后，上蒸笼旺火蒸熟成面筋，再经烤制而成。以其为原料加酱油、白糖等调料烧熟后，即成菜肴。', NULL, '烧麸500克，笋200克，香菇100克，豆干250克，金针25克，木耳30克，姜、香油各2克，八角3克，酱油85克，酒15克糖5克，胡椒粉、味精各1克，油80克。');
INSERT INTO `recipe` VALUES (2513, 67, '红烧牛肉', '1.牛肉切块，葱切长段，姜拍破。萝卜去皮，切块，过油至半熟捞出。\r\n2.用开水煮牛肉撇去泡沫，加入姜、料酒、盐、糖、花椒、味精调好色味，移小火炖烂，加盐和萝卜，至萝卜烂即可。', NULL, NULL, NULL, '牛肉500克，萝卜300克，葱、姜、料酒各25克，盐、糖少许，花椒、味精8克，花生油400克。');
INSERT INTO `recipe` VALUES (2514, 67, '红烧排骨', '1.冬笋切较厚的片，葱切段，姜切块。\r\n2.炒锅上火，放油将葱、姜和笋片放入，煸炒至上色加入料酒、酱油、盐、味精、白糖和清水，放入排骨坯料，用火烧开锅后，转用小火烧25分钟，待汁收浓且口味浓香时，加入水淀粉，把汁全部挂在原料表面即可。', NULL, NULL, NULL, '猪排骨500克，冬笋150克，酱油20克，料酒5克，盐4克，味精3克，白糖5克，葱8克，姜5克，淀粉5克，油50克。');
INSERT INTO `recipe` VALUES (2515, 67, '红烧圈子', '先将猪直肠里外翻洗干净，放入冷水锅中用旺火烧到水沸，见直肠外层发硬紧缩，捞出放在盛器内用盐或米醋搓揉，除去粘液，漂净。再放入汤锅内加葱结、姜块、料酒等，用旺火烧煮，边烧边换水，待汤锅内水色清澄无臊味时取出再洗，然后放入汤锅内加盖焖烧至直肠呈白色，捞出用冷水浸凉后，改切成段，放入烧锅里，加肉清汤少许和料酒、酱油、白糖，加盖用旺火烧沸，改用小火再焖二三分钟，待肠上色入味，再改旺火收汁，用少许湿淀粉勾芡，淋些熟猪油颠翻一下，出锅装盘。可缀上风根嫩韭芽。', '色泽金黄，卤汁稠浓，香糯肥腴，嫩如面筋，咸中微甜。', NULL, NULL, '猪直肠，姜块，米醋，料酒，酱油，白糖，湿淀粉，嫩韭芽。');
INSERT INTO `recipe` VALUES (2516, 67, '红烧狮子头', '1.冬笋切片，水发木耳择好洗净，淀粉溶于水中。\r\n2.炒锅上火，加油150克，烧至八成热将做好的狮子头放入炸至上色，取出滤干油分。\r\n3.炒锅上火，放油30克，将葱段，姜块放入，煸出香味下狮子头、酱油、料酒和水300克，再加盐、醋、味精，放入冬笋片和木耳烧约5分钟，放入水淀粉，将汁收浓即可装盘。', NULL, NULL, NULL, '冬笋50克，水发木耳20克，酱油20克，料酒15克，醋3克，盐8克，味精3克，白糖3克，油180克，葱10克，姜10克，淀粉3克。');
INSERT INTO `recipe` VALUES (2517, 67, '红烧丸子', '1.将柿子椒洗净去蒂，切成边长为1厘米的菱形块，葱、姜切成末，将肥三瘦七的猪肉剁成肉馅，加入鸡蛋、盐1克，酱油2克、料酒1克、淀粉3克，顺时针方向不停搅动，均匀后备用。\r\n2.将油放入锅中烧至五六成热，用手将肉馅挤成核桃大小的丸子，放入油锅中，炸至定型即可取出。\r\n3.炒锅内放底油20克，加入葱、姜末略煸炒，放入料酒、酱油、油、汤或水300克，再加入白糖，开锅后把丸子放入，用小火烧约10分钟，放入柿子椒块，并把剩余的淀粉溶于水中，然后再倒入锅中，把汁收浓，即可装盘。', NULL, NULL, NULL, '猪肥瘦肉300克，青椒50克，盐4克，酱油10克，料酒5克，味精3克，白糖5克，醋2克，葱5克，姜5克，油250克，淀粉5克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (2518, 67, '红烧鱼', '1.将鱼洗净、切块。\r\n2.放入热油锅内先煎，煎至鱼皮呈黄色见硬时，把鱼捞出。 \r\n3.锅内加熟猪油、酒葱、蒜、辣椒、调味料煸炒，再放入肉清汤烧开，将鱼放入。然后用小火焖烧十分钟左右，见鱼肉熟汁呈胶状后，加味精并晃动，使卤汁裹包鱼块即成。', NULL, NULL, NULL, '鱼、酒、葱、蒜、辣椒猪油、调味料各少许。');
INSERT INTO `recipe` VALUES (2519, 67, '红松嫩鸡', '1.将鸡脯去骨，得净肉400克，肉面上排十字花刀，拍上干菱粉。\r\n2.将猪腿肉去皮去筋，斩成线末，加上打散的鸡蛋、干菱粉、葱、姜、酒、酱油、味精拌和，贴在鸡脯上摊平。再用十字刀轻轻排一遍，撒上干菱粉折平。\r\n3.开小猪油锅烧五成熟，将鸡肉放入两面煎黄，滗去油，倒上300克汤，加上糖、酱油、酒、葱、姜、味精，烧到汤将收干，再加上番茄汁、辣酱油，随即提起锅转几转，取出鸡肉，切成梳子式，扣在盘内，在原汤汁内加上菱粉勾好琉璃芡，浇在鸡肉上。另将绿叶菜用热猪油加少许盐炒熟，围在鸡肉旁边即好。', '此菜制成后入口松嫩，同进因为番茄汁，呈红色，所以称为“红松嫩鸡”。', NULL, NULL, '鸡脯或鸡腿肉1块约400克、猪腿肉200克、绿叶菜130克、鸡蛋1只、姜母2片、葱末少许、白糖20克、黄酒20克、白酱油40克、盐2克、番茄汁75克、辣酱油156.5克、菱粉75克、味精少许、清汤300克。');
INSERT INTO `recipe` VALUES (2520, 67, '滑炒虾仁', '1.虾仁用精盐水抓洗后，再以清水冲洗沥干。\r\n2.虾仁沥干至无水份，调入精盐、酒、胡椒粉、蛋白、淀粉腌10分钟，另将葱切成段，姜切片待用。\r\n3.油温热后投入虾仁，一变色即可捞出，另用油入锅，依序下葱段、姜片及虾仁，快速调入精盐、味精、香油、胡椒粉，翻炒数下即可。', '成菜色泽素雅，肉质滑爽脆嫩，口味鲜香清淡，食后盘中无油、无汁、无芡，堪称一绝。', NULL, NULL, '虾仁6000克，葱20克，姜、胡椒粉各2克，精盐6克，酒5克，蛋白半个，淀粉10克，味精、香油各1克，油50克。');
INSERT INTO `recipe` VALUES (2521, 67, '鸡饭煲', '1.鸡洗净抹干水，斩成块，加老抽半茶匙，生粉一茶匙，捞匀泡油。\r\n2.下油二汤匙，爆香姜、蒜茸、磨豉下鸡兜匀。下酒一汤匙，加调味煮滚，慢火焖约十分，勾芡。取起八角，桂皮不要，熄火。\r\n3.生菜洗净切段放在煲仔内，把鸡放在生菜上面，放上葱，煲滚。\r\n4.用另一煲煲饭熟。', NULL, NULL, NULL, '鸡半只，姜数片葱三条切短段，蒜茸一茶匙，磨豉一汤匙半，生菜八两糖，老抽，盐八角，桂皮，果皮生粉，水适量。');
INSERT INTO `recipe` VALUES (2522, 67, '鸡丝炒面', '1.先将煎面底放在碟中，用油20克起锅将熟鸡肉切成细丝。\r\n2.注入二汤，用精盐、味精调味，放入鸡肉丝，撒上胡椒粉用湿淀粉打芡，加包尾油5克和匀，盖在面上便可。', NULL, NULL, NULL, '熟鸡肉120克，姜片1克，油25克，二汤250克，精盐5克，味精2克，胡椒粉0.1克湿淀粉20克，煎面底400克。');
INSERT INTO `recipe` VALUES (2523, 67, '家常黄鱼', '1.黄鱼刮鳞、去鳃、挖去内脏，洗净，在鱼身两侧每隔1.5厘米剞一斜刀纹。猪腿肉切片。 　　\r\n2.黄鱼用酱油（5克）浸渍，使其渗入咸味。炒锅置旺火烧热，用油滑锅后，下熟猪油烧至冒清烟时，放入黄鱼，煎至鱼身两面呈现金黄色，倒入漏勺沥油。\r\n3.锅里留底油（15克），放入葱段、姜片、蒜片煸出香味，再放肉片、笋片煸炒，接着放入黄鱼，放入绍酒、酱油（12.5克）、白糖略烧一下，加热水300克，烧开后，改用小火烧10分钟左右，再端回旺火上，用漏勺轻轻地将黄鱼捞出装盘。锅里留下卤汁，加入味精，淋上芝麻油推匀，收稠汤汁', '此菜色泽金红，卤汁不腻，鱼肉酥嫩，味道鲜美。', '加工黄鱼，不必剖腹，用筷子从口中搅出肠肚，冲洗干净即可烹制。煎鱼时，锅先烧热，用油滑锅，猪油烧至冒清烟，即八成油热以上，则不易粘锅。', NULL, '大黄鱼1条500克，春笋片50克，猪腿肉75克，葱段5克，姜片5克，味精1克，蒜片5克，芝麻油15克，绍酒25克，熟猪油150克，酱油15克（约耗40克），白糖5克。');
INSERT INTO `recipe` VALUES (2524, 67, '酱猪肉', '1.葱姜、药料，用竹箅子将肉压住，在旺火上烧，撇去浮沫，移至微火上焖煮，至肉熟时捞出，皮朝下盛在盘内晾凉。\r\n2.锅内汤烧沸，撇净浮油，晾凉后倒入肉盘内。吃时将肉捞出，切成薄片装盘即可。', NULL, NULL, NULL, '带皮猪肉5000克，酱油1000克，精盐200克，葱50克，姜50克，药料（丁香25克），砂仁5克，玉果5克，草果3克，白芷3克，八角10克，小茴香3克，肉桂25克。');
INSERT INTO `recipe` VALUES (2525, 67, '椒盐八宝鸡', '1.将鸡去毛，去头去脚，洗净，在颈部开一个1寸长的裂口，并割断翅膀与鸡身相加的筋络，再将大小骨头慢慢地拆下来，这叫做“全拆”。然后将鸡的内脏挖去，但注意不要将皮弄破。再将鸡放在开水锅里川一下后，用冷水漂净。\r\n2.将糯米淘净，加适量水蒸熟；火腿、鸡肫、冬笋、冬菇、猪肉、青豆，干贝分别切成小丁，一起放入猪油锅内，加上酒、盐、糖、酱油炒一下，再与蒸熟的糯米拌和，塞入鸡肚内，用线扎好。\r\n3.将鸡放入锅内，加上葱、姜、酒、盐和水1000克，用温火烧约一小时半，然后将鸡取出，全身抹上干菱粉。\r\n4.开大猪油锅，烧', '色金黄，味香而脆，四季皆宜。', NULL, NULL, '母鸡1只1400克、火腿50克、鸡肫50克、猪肉50克、冬笋50克、水发冬菇50克、干贝50克、青豆50克、糯米350克、整葱200克、姜片200克、黄酒200克、白糖9克、盐5克、椒盐少许、酱油9克、菱粉9克。');
INSERT INTO `recipe` VALUES (2526, 67, '菊花对蟹', '1.将熟蟹壳和蟹钳用开水洗净待用。把水发香菇先经开水煮熟，然后分别剪成对蟹的16只蟹脚、菊花（或用白萝卜、胡萝卜雕成菊花状），经食用水毒水过净。\r\n2.炒锅上火，放油烧至七成热，下葱结略煸取出，再下蟹肉稍肖煸后，烹入绍酒，加盖稍焖去腥，随即下姜末、精盐、味精、鲜汤，烧沸后略烩，下湿淀粉勾芡，淋上熟油少许，出锅盛在盘内两侧，套上蟹壳，装上蟹脚，放上菊花即成，随香醋一小碟上桌供蘸食。', '王宝和酒家创建于清朝乾隆九年（公元1744年）。当时，有一个名叫王楼臣的作坊主，先在绍兴开设了王宝和酒作坊，后来其子王仁山在上海南市咸瓜街开设了王宝和酒家。咸丰二年（公元1825年）迁至南京路、山西路一带，1936年搬到福州路现址营业，以酿造经营黄酒和烹制蟹宴而著称。 这个酒家专营绍兴陈年黄酒，历史悠久，驰名中外。据记载，清朝嘉庆县志就将绍兴黄酒列为十大名产品之一。在1910年南洋劝业会和1916年巴拿马赛会以及1925年西湖博览会等展览中，绍兴黄酒边疆被评为太雕、花雕、金波、玉液、善酿、香雪和陈加饭等绍', '宜选每只重200-300克以上的大闸蟹为原料制作。蟹肉宜旺火快炒，时间不能过长，保持肉质鲜嫩。', NULL, '净蟹肉150克，味精2克，大闸蟹蟹壳2只，湿淀粉50克，蟹钳4只，花生油35克，绍酒5克，水发香菇4只，葱结2克，鲜汤75克，姜末1克，菊花1-2朵，精盐2克，香醋25克。');
INSERT INTO `recipe` VALUES (2527, 67, '烤樱桃', '1.用活田鸡1100克1只只地在项背部下刀，将项颈斩断，但项颈后背的皮，必须连牢，不能斩断。然后用左手抓住田鸡的颈部，用右手抓住田鸡的头，向脊背那面往下一拉，皮即随之剥下，脚和其他部分都斩去不要，只取其两只后腿，约400克，再用刀将腿骨一只只轻轻敲断。\r\n2.用猪油、葱、姜开锅，将田鸡腿放入，加上糖、酒、酱油爆香，再加上清汤、糖色，用温火烤至腿肉卷缩，撒上五香粉，浇些麻油即好。色金黄，形如樱桃，鲜美非常，适宜于夏季。', '色金黄，形如樱桃，鲜美非常，适宜于夏季。', NULL, NULL, '田鸡腿400克、葱段13克、姜米6.5克、五香粉少许、黄酒、白糖、糖色少许、酱油、麻油13克、清汤175克。');
INSERT INTO `recipe` VALUES (2528, 67, '扣三丝汤', '1.鸡肉洗净，加入调味料拌匀，隔水蒸熟切成丝。\r\n2.火腿洗净，鸡蛋打散煎成蛋皮，一同切丝。冬菇浸软去蒂。\r\n3.冬菇重叠放在碗底，将鸡丝、火腿丝、蛋皮丝整齐排于碗底，空隙将剩余材料填满，注入上汤一杯隔水蒸三十分钟。\r\n4.汤倒出留用，材料扣入汤碗中，汤混合其余上汤煮滚，滚入汤碗中即成。', NULL, '扣的做法是将材料整齐地排放在碗中，然后再覆扣在另一只较大的碗或深碟中。扣三丝汤的材料可随意调配，如用冬菇丝、鸡丝、肉丝、火腿丝或蛋皮丝等均可。', NULL, '鸡胸肉六两、洋火腿两片、鸡蛋、冬菇各两只、上汤三杯、姜两片、葱一棵、酒半茶匙。');
INSERT INTO `recipe` VALUES (2529, 67, '栗子百果羹', '1.将栗子、红瓜、青梅都切成与白果一样大小，然后将栗子和白果上笼蒸约四十五分钟。\r\n2.将栗子和白果取出，与红瓜、青梅一起放入锅内，加入600克烧开后再加上糖的湿菱粉，调成羹（湿菱粉不要放得过多），然后将桂花糖放入，调匀后起锅。', '色白，味香甜，适宜于秋冬二季。', NULL, NULL, '栗子肉200克、白果肉200克、红瓜40克、青梅40克、白糖165克、桂花糖少许、菱粉40克。');
INSERT INTO `recipe` VALUES (2530, 67, '龙穿凤翼汤', '1.将鸡翅膀用水烧至六成熟取出，斩去两头骨节，抽去里面的骨。\r\n2.将火腿、笋切成与翅膀一样大小的长方块，塞入翅膀当中，扣在汤碗里，放上酒、盐、味精，上笼蒸烂（约1小时）。\r\n3.另将冬菇用水煮熟，放在翅膀上面，再翻覆在汤碗里，冲上热汤即好。', NULL, NULL, NULL, '鸡翅膀500克、火腿80克、笋125克、水发冬菇165克、黄酒8.5克、盐6.5克、味精少许、鸡汤500克。');
INSERT INTO `recipe` VALUES (2531, 67, '龙凤酸辣汤', '1.葱姜炝锅下熟鳝丝、熟鸡脯丝、香菇丝炒至入味； \r\n2.加鸡清汤烧开，撇去浮油，加米醋、味精、盐、白胡椒粉即成。', NULL, NULL, NULL, '熟鳝丝100克、熟鸡脯丝100克、香菇丝100克。');
INSERT INTO `recipe` VALUES (2532, 67, '卤鸭', '1.先将鸭宰杀干净放锅内，加水、肥膘、红米粉、盐、酱油、冰糖、桂皮、茄香、葱、姜各少许在旺火上烧沸，放黄酒加盖烧约1小时，再将鸭身翻转后移至小火连续烧半小时，取出待冷后斩成块； \r\n2.拣出锅中肥膘将锅中卤汁筛滤后放另一锅中，加酱油、白糖，在旺火上烧开，加水淀粉搅和，起锅倒入钵中，冷透后放麻油少许搅和，将其卤汁浇在鸭上即成。', NULL, NULL, NULL, '新肥鸭一只，肥膘、红米粉、盐、酱油、冰糖、桂皮、茄香、葱、姜、黄酒、白糖、麻油、水淀粉各适量。');
INSERT INTO `recipe` VALUES (2533, 67, '眉毛酥', '1.将红小豆洗净下锅，放入凉水和少许碱煮烂，用绷筛将豆皮擦去。把豆沙泥用布袋装起挤去水分。将豆沙泥和白糖放入碗内，先用植物油油滑一下，以免粘锅，然后用小火熬干水分，加入桂花炒匀，即可取出凉透待和。 \r\n2.取面粉300克放在案板上，扒一个小窝。放入70克熟大油、200克凉水拌和揉软，揉至不粘手即成水油面。将水油面揿成圆扁形，放入油酥包拢再揿扁，用擀面杖擀成0.3厘米厚的长形皮子，折叠成3层再擀开，按此法再擀一次，最后擀成0.3厘米厚，20厘米的长形皮子，然后卷成一个长筒，用刀切成28克即成皮胚。 \r\n3.', NULL, NULL, NULL, '精白面粉1.8公斤，豆沙500克，熟大油900克（实耗约300克），植物油少许。');
INSERT INTO `recipe` VALUES (2534, 67, '蜜枣核桃', '1.将蜜枣上笼蒸熟，使之回软，再去核。\r\n2.将核桃肉用热水泡一下，取出，去净皮，放入热猪油锅内略炸，取出冷却（可使更加松脆），然后用一只蜜枣裹一块核桃肉卷成一只只橄榄形。\r\n3.将蛋白打散，加糯米粉调和，然后将卷好的蜜枣核桃放下去滚一滚。\r\n4.打开猪油锅，烧至两成热时，将蜜枣核桃一只只地放下去，炸至色黄而发脆，炸好一只拿出一只，待全部炸好后，再一起放入油锅内回一回，取出装盘即好。', '色黄，又香又松脆。', NULL, NULL, '蜜枣325克、核桃肉125克、鸡蛋白5只、糯米粉125克。');
INSERT INTO `recipe` VALUES (2535, 67, '蜜汁火方', '将火煺上方的净精肉一整块洗净，入汤锅煮至八成酥取出，除去筒骨，用盆子压平，冷却后入冰箱冰一下取出，切成厚片，砂锅内下火腿肉厚片，鸡汤煨煮，拔去咸味，如味仍过咸，滗去原汤，另加清鸡汤再拔咸味，至火腿肉厚片咸味拔净取出，放入另一锅内，加清水，白糖，用小火煨1小时至卤汁稠浓，色泽光亮即成。', '色泽火红，卤汁透明，火腿酥烂，滋味鲜甜，深有回味。', NULL, NULL, '火腿，清水，白糖。');
INSERT INTO `recipe` VALUES (2536, 67, '荠菜山鸡片', '1.将山鸡肉切成片，放入用打散的蛋白、盐、菱粉调成的浆内浆一浆；冬笋也切成片；荠菜用开水泡一下，再用冷水洗净，挤去水分，切成细末。\r\n2.打开猪油锅，将冬笋片放入锅内溜一溜，倒出，滤去油，放好。\r\n3.另开猪油锅，烧至六成熟时，将山鸡片放入溜约一分钟，取出，与冬笋片放在一起。\r\n4.将荠菜放入油锅，加上葱、姜、酒、酱油、糖、味精和清汤烧开后，再将山鸡片和冬笋片倒入，翻转，浇些麻油，即起锅装盘。', NULL, NULL, NULL, '山鸡肉300克、荠菜50克、冬笋125克、鸡蛋白2只、葱末、姜米、黄酒、白糖、白酱油、盐、味精、菱粉、麻油、清汤75克。');
INSERT INTO `recipe` VALUES (2537, 67, '茄汁牛肉', '1.将牛肉横切成薄片，放入小苏打、酱油、胡椒粉、水淀粉、绍酒、花生油、姜末和清水100克，腌1小时。\r\n2.炒锅上火，油烧至六成热，放入牛肉片，待牛肉炒熟倒入漏勺沥油。\r\n3.炒锅内放少许油将番茄酱、精盐、黄酒、白糖、味精、少许汤，用水淀粉勾芡加麻油淋上即可。', NULL, NULL, NULL, '嫩牛肉300克，番茄酱50克，绍酒、水淀粉、酱油、葱姜片各20克，白糖10克，小苏打5克，胡椒粉1克，姜末2克，花生油500克，黄酒、味精少许。');
INSERT INTO `recipe` VALUES (2538, 67, '芹菜肉松', '1.芹菜去根、叶洗净用刀拍松，切成6厘米长的丝。\r\n2.锅烧热，加少量油煸炒肉松，待断生时盛起。锅内再加少许油烧热，放入芹菜煸炒至将熟时，放入肉松、精盐、味精淋入麻油即可。', NULL, NULL, NULL, '芹菜300克，猪肉松50克，熟猪油50克，精盐3克，味精1克，麻油5克。');
INSERT INTO `recipe` VALUES (2539, 67, '青鱼秃肺', '将鱼肝旁的两条黑线撕去洗净，沥干水，改切成大块；将炒锅置旺火上烧热，用油滑锅后倒出，再下熟猪油，烧到五成热时，下入葱段爆出香味，即下鱼肝，随即将锅晃转二次，把鱼肝摊在锅底煎二秒钟，端锅颠翻一下，烹入绍酒，加盖焖三四秒钟，加入姜末、酱油、白糖、米醋和清水，烧开后，用水火烧三分钟左右，见肝块已熟时再加味精，用水淀粉勾芡，淋入芝麻油，出锅装盘，撒上青蒜丝即成。', '色泽金黄，口味鲜美，肥而不腻。', NULL, NULL, '青鱼肝，青蒜丝，葱段，姜末，绍酒，酱油，白糖，味精，米醋，水淀粉，芝麻油，熟猪油。');
INSERT INTO `recipe` VALUES (2540, 67, '青鱼下巴甩水', '1.将青鱼甩水放在盘子的中间，两边各放一块鱼下巴，再把笋片放在上面。\r\n2.炒锅置旺火上烧热，用油滑锅后，再下熟猪油25克，放入葱段爆出香味，随将青鱼尾、下巴、笋片按原样倒入锅里，边煎边晃动炒锅四五秒钟，即烹入料酒，加盖焖下，再放入姜末、酱油、白糖、肉清汤 ，烧开后，加盖改用小火烧6分钟左右。待鱼下巴呈现青灰色，鱼眼珠发白凸出时，将炒锅端回旺火放猪油25克，再晃动炒锅，颠翻一下，接着淋入麻油出锅，按原样排在盘中，上面撒上青蒜丝即成。', '青鱼又名青鲩鱼，乌青鱼，是我国特有的一种淡水鱼，它具有丰富的蛋白质、脂肪、钙和维生素等各种营养成分，亦具有食的功效。据清代《随息居饮食谱》中记载：“青鱼甘平。补气、养胃，除烦满，化湿，治脚气。可鱼会、可脯、可醉。古人所谓有五候鲭即此。其头尾烹食极美，肠脏亦肥鲜可口。”青鱼下巴甩水是用青鱼头部两面的下巴和尾巴制做的，色泽浓油赤酱，形状美观，两片整块不碎的下巴，扒在几条鱼尾两旁，似活鱼浮水一样，醇厚入味，肉质鲜嫩肥糯，卤汁紧包，受人喜爱。用鱼尾作菜肴，以青鱼为佳。因青鱼尾肉脂丰腴，且含丰富磷质，味极鲜美，故民', '青鱼下巴：即青鱼下颚，包括脸颊部分和眼瞠，这些地方的肉都很肥美。青鱼甩水：是青鱼的尾巴，上面有油有肉，有肥有瘦，最适宜于冬季食用。用湿淀粉勾芡时，要边淋入湿淀粉边晃动炒锅，以防汤汁结块。', NULL, '青鱼下巴200克，青蒜丝0.5克，青鱼甩水100克，麻油5克，味精1.5克，绍酒15克，葱段1克，笋片25克，酱油30克，姜末1克，白糖15克，肉清汤200克，湿淀粉10克，熟猪油50克。');
INSERT INTO `recipe` VALUES (2541, 67, '清炒鳝糊', '1.将鳝鱼用开水烫死，再用牙刷骨柄的尖端将鳝背和肚膛肉一起划成鳝条，然后切成一寸半的长条。\r\n2.将猪油锅烧高热，把鳝条倒入，加上葱、姜、酒、酱油、糖、糖色、味精和清汤，烧约五分钟，下湿菱粉勾芡，再加上猪油、胡椒粉炒几下，翻转，起锅推入盘中，随即用铁勺在鳝条中央揿一个凹状，撒上少许葱花，同时将麻油烧滚倒上，周围放些香菜，当中撒上火腿末即好。', '这是不同于脆鳝的又一种做法，用料经济，鳝背与肚膛都可使用。制成后，呈金黄色，既鲜香又肥嫩，四季适应，冬季当令。', NULL, NULL, '鳝鱼1100克、熟火腿末6.5克、葱花少许、姜米少许、香菜少许、胡椒粉少许、黄酒13克、白糖20克、糖色少许、酱油27.5克、味精少许、麻油40克、清汤175克。');
INSERT INTO `recipe` VALUES (2542, 67, '清蒸大闸蟹', '1.将蟹逐只洗净，放入水中养半天，使它排净腹中污物，然后用细绳将蟹钳，蟹脚扎牢。 \r\n2.用葱花、姜末、醋、糖调和作蘸料，分装十只小碟。 \r\n3.将蟹上蒸笼蒸熟后取出，解去细绳，整齐地放入盘内，连同小碟蘸料，专用餐具上席，由食用者自己边掰边食用。', '九雌十雄：大闸蟹每只以250克左右为好，煮蟹时，应用细绳将蟹钳、蟹脚扎牢，以免在受热时蟹挣扎蟹脚脱落，蟹黄、蟹油流出，影响口味和美观。蟹的吃法有时令性，“九雌十雄”九月要食雌蟹，这时雌蟹黄满肉厚，十月要食雄蟹，这时雄蟹膏足肉坚。', NULL, NULL, '淡水蟹2500克，绵白糖150克，葱花、姜末各50克，香醋、酱油各100克，香油20克。');
INSERT INTO `recipe` VALUES (2543, 67, '乳腐肉', '1.烤：将猪肋条肉切成2厘米长、1.3厘米宽的长方块，平叉在铁叉上，皮朝下放在炉火上，烤至皮呈深黄焦色时为止，放入清水中浸一会儿，用刀刮去毛根、污秽，再用温水洗净，放入汤锅，用小火烧至六成熟时捞起冷却，在皮面上剞上花纹，刀深为肉的一半，接着在肉面上剞两个斜十字刀，刀深为肉的1/4。将绿叶菜洗净，沥干水分待用。 　　\r\n2.蒸：半红乳腐捏碎，用纱布过滤，去渣留卤，与红米曲米汁、绍酒调和后抹在猪肉上，放入扣碗，上笼蒸酥，约需两小时左右，撕去封口纸，取去葱、姜、桂皮、大茴香，滗出卤汁。\r\n3.烧：将锅放火上，下', '此菜系火功菜肴，色泽鲜红，酥烂不腻，表不变，卤肥浓，甜咸适口，老少咸宜。具体操作与红烧肉相似，只是红烧肉以黄酒、酱油代水，乳腐肉以红腐汁为主，经过烤、蒸、烧三道工序，便成富有地方特色的美味。如用荷叶夹夹食更觉爽口。荷叶夹制法：将发酵面50克，擀成圆莆片，刷上植物油，对折盛装，再在上面切成若干刀痕，为荷叶状，放入蒸笼，用旺火5-6分钟即成荷叶夹。乳腐肉原是用红乳腐作调料，后逐渐改用红米曲米汁，把肉烧成红乳腐大小，借用红乳腐的形与以故名。', '嫩绿叶菜可选青菜、菠菜或草头等。如没有桑皮纸可选用玻璃或丝棉纸。目的是以防水汽入碗。', NULL, '猪肋条酒750克，红乳腐75克，姜片5克，嫩绿叶菜250克，碎水糖75克，浓红米曲米水50克，精盐7.5克，绍酒25克，桑皮纸1张，桂皮1克，植物油25克，湿淀粉5克，葱结5克。');
INSERT INTO `recipe` VALUES (2544, 67, '砂锅大鱼头', '1.将鱼头（要选淡水的鱼）理净，洗清，在肉质厚的地方，用刀剞一剞，放入酱油内浸入味。\r\n2.用猪油锅，将鱼头放入，两面煎黄，加上葱、姜，盖上锅盖焖片刻。再加进酱油、糖和清汤，用旺火烧开，再转温火烧约半小时，使之熟透。最后加上水发冬菇、板油丁、粉皮、糖色一滚，并浇上麻油，加上味精起锅。', NULL, NULL, NULL, '大花鲢鱼头1只700克、冬笋45克、粉皮5张、水发冬菇20克、葱段少许、板油丁20克、黄酒60克、姜片2片、糖色少许、白糖20克、味精少许、酱油125克、清汤200克、麻油20克。');
INSERT INTO `recipe` VALUES (2545, 67, '上海春卷', '1.将肉丝入锅，用猪油在旺火上煸炒至七成熟，加酒、酱油、盐、味精和水，用湿淀粉勾厚芡，视其沸透后起锅，装盘摊凉。将韭菜切成三分长的段，撒入炒好的肉丝中拌匀成馅。\r\n2.将春饼皮子放在案板上，放上肉丝馅，卷拢，两头折拢，最后卷成约二寸六分长、八分宽的小长方形包，用面粉封口。菜籽油烧至七成熟，将春卷入锅炸，炸时用筷不断翻动，约两分钟后呈金黄色，捞起装盘，吃时可蘸甜面酱或醋。', NULL, NULL, NULL, '肉丝，猪油，酒酱油，盐，味精水，湿淀粉，韭菜春饼皮子，菜籽油，甜面酱或醋。');
INSERT INTO `recipe` VALUES (2546, 67, '上海蒸鱼', '1.将鱼刮鳞去腮，内服洗净。\r\n2.将鱼放入场盘里放葱、姜片、香菇、笋片、火腿片，再加黄酒、白糖、精盐、味精、胡椒粉，一起拌和放在桂鱼身上，上笼用旺火蒸约15分钟，出笼后，淋上麻油即可。', NULL, NULL, NULL, '鲜鱼1条，姜片25克，香菇25克，黄酒15克，白糖、精盐、味精、笋片、熟火腿片、麻油适量，胡椒粉少许。');
INSERT INTO `recipe` VALUES (2547, 67, '绍兴鸡', '将嫩母鸡洗净，置于冷水中浸泡1小时，锅中放水煮沸，将鸡放沸水中浸烫一下，使鸡烫匀烫遍，取出；在原锅内稍加冷水，将烫过的鸡放入加盖，水煮沸后，用小火焖煮20分钟，将鸡翻身焖煮10分钟再将鸡捞出，投入冷开水容器内浸冷，捞出沥干水分，在外皮搽上一层香油即可。', '著名上海菜，因上海小绍兴鸡粥点所创而得名。选用上海名产“三黄鸡”，不加任何调料，全凭白水煮成。', NULL, NULL, '活嫩母鸡2000克，白糖、味精各2克，酱油35克，精盐1克，葱结1只，姜末5克，香油10克。');
INSERT INTO `recipe` VALUES (2548, 67, '松江鲈鱼', '将鲈鱼肉去骨切成丁；笋丁用开水氽熟；炒锅上火，放熟猪油烧至五成热下葱、姜煸香捞出，倒入鱼丁稍炒，烹绍酒，加鸡汤、笋丁和盐，汤滚后用湿淀粉着芡，淋麻油少许出锅倒入汤盘，撒上熟鸡肉和熟火腿末即成。', '色泽洁白带红点，汤汁薄腻，肉嫩味鲜，回味无穷。', NULL, NULL, '鲈鱼肉，冬笋薄丁，熟鸡脯肉末，熟火腿末，麻油，绍酒，葱，姜，熟猪油，湿淀粉，精盐，鸡汤。');
INSERT INTO `recipe` VALUES (2549, 67, '松仁鱼米', '1.鳜鱼肉切成绿豆大小的粒，用鸡蛋清、味精、白胡椒粉、水淀烩上浆，加色拉油15克拌匀。青椒、红椒、葱白切成同鱼相仿的粒。\r\n2.将色拉油烧到四成热，下入松仁炸至金黄色，捞出沥油。鱼米亦用四成热的温油滑散，再下青、红椒粒，翻炒一下，随即倒入漏勺沥油。趁热锅余油，下葱白略煸，随即下绍酒、精盐、鱼米、松仁，用水淀粉勾芡，颠翻几下，淋入芝麻油成佳。', '“松仁鱼米”工艺精细，色彩悦目，鲜嫩爽滑，清淡不腻。', '用水淀粉勾薄芡，让淀粉充分糊化，做到明汁亮芡，出锅装盘，立即上桌，趁热食用，鱼肉嫩，松仁酥，风味尤佳。', NULL, '鳜鱼肉350克，红椒1只，松仁100克，鸡蛋清1个，青椒1只，绍酒25克，精盐4克，葱段4克，味精1.5克，色拉渍1500克，芝麻油15克（约耗150克），水淀粉75克，白胡椒粉1.5克。');
INSERT INTO `recipe` VALUES (2550, 67, '素炒鳝糊', '1.鳝鱼用热水焯一下，捞出过凉水取出肠和骨，然后切成6-8厘米长的段。\r\n2.取一只碗，放入葱末、姜末和蒜末的一半及酱油、料酒、白糖、味精、盐、高汤、湿淀粉调成味汁。\r\n3.炒锅上火，油烧至九成热，倒入鳝段，加胡椒粉煸炒熟，再把调好的味汁倒入，炒拌均匀起锅装盘。', NULL, NULL, NULL, '活鳝鱼500克，葱末、姜末、蒜末各5克，酱油20克，料酒10克，湿淀粉30克，白糖10克，味精、盐各适量，高汤100克，香菜段20克，姜丝10克，胡椒粉5克，香油10克，食油100克。');
INSERT INTO `recipe` VALUES (2551, 67, '酸甜豆腐', '1.将豆腐切成长约3厘米、宽2厘米、厚O.2厘米的片，用开水焯一下。\r\n2.取一个锅，锅内放入清水、香叶和丁香，放火上烧煮至飘出香味时，加入白糖搅溶并离火，放一边待凉透后加入醋精，成为甜酸适宜的卤汁。然后将豆腐浸在卤汁中即可。', NULL, NULL, NULL, '豆腐3OO克，香叶2片，丁香3只，白糖适量，醋精少许。');
INSERT INTO `recipe` VALUES (2552, 67, '汤圆', '1.将白糖、芝麻粉与板油丁拌匀揉透，再搓成1O克左右的小圆子，即成馅心。\r\n2.将水磨粉加水揉透，摘成小粉团，搓圆，捏成锅子形，包入馅心，捏拢收口，成生坯。 \r\n3.将生坯放入沸水锅内煮熟。', NULL, NULL, NULL, '黑芝麻粉5OO克，猪板油丁75O克，绵白糖1OOO克，水磨粉1OOO克。');
INSERT INTO `recipe` VALUES (2553, 67, '烧白桃', '1.用猪油75克、葱、姜开锅，将青鱼眼膛放入（眼珠必须朝上，否则便会落出），不要翻转，加上酒烹一烹，盖好锅盖焖一下。\r\n2.加酱油、糖、糖色和250克水，烧约七八分钟，待汤汁收到六成干，翻转，随即加上菱粉和猪油，再翻转，浇上醋起锅。', NULL, NULL, NULL, '青鱼眼膛12只、葱段6.5克、姜米6.5克、白糖20克、糖色少许、黄酒45克、酱油50克、醋少许、味精少许、菱粉25克。');
INSERT INTO `recipe` VALUES (2554, 67, '生煸草头', '将炒锅烧热，放入油，同时将草头放在漏勺内，加入盐，白糖，味精，再将酱油放入铁勺内，油待烧至冒青烟时，将草头、酱油同时入锅炒，至熟后烹入白酒炒匀，起锅装盆即成。', '脆嫩，爽口。', NULL, NULL, '取草头尖嫩绿叶，留草头茎。');
INSERT INTO `recipe` VALUES (2555, 67, '盐水鸭肫', '1.鸭肫洗净，剞芝麻花刀，入开水锅内略焯，再用清水洗净。 \r\n2.将鸭肫放入锅内，加清水2000克左右，下葱、姜、花椒、盐，旺火烧沸，改用小焖煮2小时左右，至鸭肫熟透，再加绍酒、味精，继续用小火烧至入味后取出。 \r\n3.待锅内卤汁冷却后，再将鸭肫浸入卤水中。食用时，将鸭肫捞出，切片装盘即成。', '清鲜无腻，爽脆可口。', NULL, NULL, '鸭肫500克，姜20克，盐15克，味精3克，葱结1只15克，绍酒15克，花椒1克。');
INSERT INTO `recipe` VALUES (2556, 67, '糟钵头', '1.将猪肺、猪肝、猪大肠、猪肚、猪心、冬菇、冬笋都洗净，分别切成厚小块，火腿切成厚片，猪脚斩成小块。\r\n2.将以上用料（除猪肝外）放入砂锅，加葱、姜、酒（20克）和清汤（1250克）、用温火烧至七成熟（约三小时）后，即加油豆腐，继续烧至全熟，放入猪肝，并将香糟加酒和清汤淘和过滤后倒入一滚，取去葱、姜即好。', '色乳白，有香糟味。适宜于春冬二季。', NULL, NULL, '猪肺半只、猪肝75克、猪大肠125克、猪肚、猪心、猪脚、火腿、冬菇、冬笋、油豆腐、香糟、葱结、姜片、黄酒、盐、清汤1400克。');
INSERT INTO `recipe` VALUES (2557, 67, '炸藕合', '1.将莲藕切成O.5厘米厚的圆形片24个，用清水漂3O分钟。将肉馅分成12份，放在藕片上，再压一块藕片粘成盒形。蛋白与湿淀粉调成蛋白稀浆。\r\n2.炒锅旺火烧油至六成沸，藕盒上蛋白浆后下油锅，炸至金黄色酥脆便可捞起，沥清油，排好便可上桌。', NULL, NULL, NULL, '洗净莲藕25O克，肉馅2OO克，蛋白1OO克，湿淀粉75克，生油1OOO克。');
INSERT INTO `recipe` VALUES (2558, 68, '三丝敲鱼', '1.将鲩鱼取净肉，切成片，然后在砧板上放上干淀粉，用小木槌敲成鱼片；\r\n2.清水烧沸，将鱼片落锅煮熟，捞出入冷水过凉后，切成条，熟火腿、香菇、熟鸡脯肉均切成细丝；\r\n3.把鱼片和青菜心放入沸水锅中汆一下，捞起沥去水，炒锅中倒入清汤，放进鱼片、青菜心、精盐、料酒，用中火烧沸，撇去浮沫，放入香菇丝、熟鸡脯丝、熟火腿丝、味精，淋上熟鸡油，起锅盛入汤盘即可。', '以新鲜去骨鲩鱼肉，撒上干淀粉，用木槌敲成鱼片。成菜后鱼片透明亮丽，光滑洁白，味道鲜美，别具一格。', NULL, NULL, '鲩鱼一条750克，熟火腿25克，水发香菇、熟鸡脯肉各50克，青菜心100克，料酒20克，精盐7克，味精2克，干淀粉10克，清汤500克，熟鸡油10克。');
INSERT INTO `recipe` VALUES (2559, 68, '砂锅豆腐', '1.豆腐切块焯水后捞出，粉丝、腐竹泡发，胡萝卜切块； \r\n2.砂锅内放粉丝、腐竹、胡萝卜，再放入豆腐，加鸡清汤，调好味后大火烧开，小火煨至入味，淋香油即成。', NULL, NULL, NULL, '嫩豆腐150克，胡萝卜50克，菜心100克，腐竹、水发粉丝100克。');
INSERT INTO `recipe` VALUES (2560, 68, '鲜豆腐皮炒青蟹肉', '1.先将青蟹的黄挖出，加少量的水，酒、菱粉调稀。再把青蟹带壳上笼蒸熟，挖出蟹肉。\r\n2.一面起炉火，下鸡油化开，一面将豆腐皮切碎，同蟹肉、生蟹黄、鸡汤、盐、酒道倒入一炒，接着调菱粉下锅勾薄芡即好。', NULL, NULL, NULL, '新鲜水豆腐皮2张、净蟹肉200克、鸡汤1小碗、精盐12.5克、菱粉2汤匙、鸡油2汤匙、黄酒少许。');
INSERT INTO `recipe` VALUES (2561, 67, '糖醋排骨', '1.将排骨洗净，用料酒、盐腌一下。 \r\n2.锅上火，放油烧至六成热，下入排骨炸至呈金黄色捞出。\r\n3.另坐锅底油，放入白糖、番茄酱、醋、盐，搅匀成糖醋汁，再把炸好的排骨放入，翻炒至均匀挂上汤汁即可。', NULL, NULL, NULL, '猪排骨25O克，番茄酱少许，白糖25克，醋2O克，料酒1O克，盐3克，油1OOO克。');
INSERT INTO `recipe` VALUES (2562, 67, '糖醋鱼', '1.将鱼剞刀，用精盐稍腌，并涂上淀粉糊。\r\n2.炒锅倒油，共炸8分钟，将鱼身炸呈弓形，至鱼身全部呈金黄色时，取出放入盘内。\r\n3.炒锅留油少许，放入配料，烧浓后勾芡，淋油出锅浇在鱼身上即可。', NULL, NULL, NULL, '鱼、姜末、葱末、蒜末备少许，醋、白糖、酱油、湿淀粉、精盐适量，清汤3OO克，花生油2OOO克。');
INSERT INTO `recipe` VALUES (2563, 67, '天下第一鲜', '1.先将文蛤壳劈开，用力将文蛤肉及衣裳膜从壳上一道刮下，洗后带水放在碗内，不要放在竹篮里，以免水分沥干。\r\n2.将文蛤肉、葱花、姜天、黄酒、盐等放在一起。\r\n3.炒锅上火，倒入生油，待油温至九成热时，将准备好的文蛤肉等一起倒入油锅，快速翻炒几下即成。', '“天下第一鲜”即文蛤，又名花蛤、黄蛤、圆蛤，其贝壳两片，坚厚有花纹，内则光白如立，扁平如舌状，形如古代兵器中的月斧，故又称月斧。文蛤味美，营养丰富，含有丰富的蛋白质、钙、磷、铁、维生素等营养元素。据《本草纲目》中记载，以治疮疖，解酒素养，消痈疽等有疗效。又据近代研究，对抑制肝癌、平哮喘、治老慢支、淋巴结核、甲状腺肿大等，有很好疗效。常食能滋补健身。从唐人开始，文蛤就已作为贡品进入御厨。文蛤味极鲜嫩，营养丰富，被誉为“天下第一鲜”，当之无愧。文蛤食法多样，可炒、爆、烩 、氽等。此菜成菜饱满含液，口感滑嫩，鲜', '葱结：一般是以1-2根小葱打成一个结，在烹制时放入，菜肴成熟后拣出，取其香味，不做食用。鸡清汤：又称高级清汤、顶汤、上汤。将整只老母鸡放在汤桶里（也可将鸡斩成大块，在煮汤过程中使鸡肉的可溶性物质易于析出），加入原料量3-4倍的清水，用小火慢慢烧开（注意：在将要烧开时，要防止汤水沸腾，这是制鸡清汤的关键），立即改用微火，保持汤面没有波动，约煮3-4小时。在这期间，不要撇沫，任其血污上浮结块，防止搅散，使鸡肉、鸡骨内的可溶性营养物质渗入汤内。煮好后，先将汤面上的浮沫轻轻撇净。然后将清汤滗出，再用纱布过滤即成。', NULL, '文蛤肉250克，葱花5克，姜末5克，黄酒15克，精盐5克，生油40克。');
INSERT INTO `recipe` VALUES (2564, 67, '网油清蒸鲥鱼', '1.鲥鱼开膛，除去内脏和鳃，保留鱼鳞，用清水洗净。火腿、冬笋切成约0.15厘米的片。香菇去蒂洗净。网油洗净后揩干。香醋、嫩姜末放在同一碟内。\r\n2.把猪网油摊平，中间先放冬菇，然后把火腿片、笋片间隔地排列好，两侧撒上猪油丁，把鲥鱼摆放在火腿、笋片之上，用网油轻轻包裹起来，保持排列原样，放入盘内。然后，加绍酒、白糖、熟猪油、精盐、洒酿、葱结、姜片，上笼用旺火急蒸10-15分钟，待网油基本融化时取出，放入保暖的鱼盘内（有香菇的一面朝上）。上菜时，带香醋、姜末碟，供蘸食。', NULL, '酒酿：也称甜酒酿，用糯米饭加甜酒药发酵而成。其配制方法：把糯米（1500克）淘净，浸入冷水中（夏季需3小时，冬季8-10小时，春秋季4-8小时），待米粒发胀后，沥干水，松散地放入蒸桶里蒸熟后取出，用冷水冲淋，冲至米饭粒不沾，倒入大盆里，将甜酒药（5克）碾成粉，均匀地撒入糯米饭里拌和。取用钵头1只，中间竖1只酒瓶，四周放入糯米饭揿平，然后取出瓶，中间呈圆形空洞，上盖，用棉絮裹紧保温，约24小时左右，即发酵成甜酒酿。蒸鱼时间不能过长，否则鱼肉发面，鱼刺不易离肉，风味大减。', NULL, '鲥鱼中面350克，猪网油150克，火腿20克，精盐10克，水发冬菇1只，白糖25克，冬笋肉25克，酒酿15克，糖猪权油丁10克，熟猪油25克，葱结1个，香醋25克，姜1片，嫩姜末5克，绍酒25克。');
INSERT INTO `recipe` VALUES (2565, 67, '虾籽大乌参', '1.炒锅置中火上，放入熟猪油，烧到五、六成热时，放入葱结炸出香味即成莸油。\r\n2.炒锅炉置旺火上烧热，入热豆油，烧到八成熟时，将大乌参皮朝上放在漏勺里，浸入油锅，并用漏勺轻轻抖动，炸到爆裂声减弱小时，捞出沥油。随后把锅内热油倒出，锅内留余油5克，放入大乌参，再加入绍酒、酱油、炒肉卤、白糖、干虾籽、肉清汤烧开，加盖，用小火烧四分钟左右，再改用大火，用漏勺捞出大乌参，皮朝上平放在长盆里，锅里的卤汁加入味精，用湿淀粉勾芡，接着边撒葱边搅拌，把葱油全部搅进卤汁后，撒入葱段，将卤汁浇在大乌参上即成。', '海参是我国的名贵特产。我国各海域均产，以南海参出产著名。海参分为光参和刺参两类，营养价值很高，含有较丰富的蛋白质、钙、磷、铁等营养素，并且有治疗溃疡和补肾、补血的医疗效用，是珍贵的海味之一。虾籽即虾卵的干制品，产于辽东半岛、山东半岛、河北沿海、舟山群岛等地。此菜形如发髻，乌光发亮，酥烂不碎，味鲜汁浓而香醇。', '大乌参涨发方法与其它海参不同，要先用火钳夹住，放在火上烧烤，使其外皮都烤到焦炭状时，离火用铲刀刮去硬壳，这时将海参取出，放在冷水中，剖肚去脏泥沙等，再用煎刀剪去四边形硬皮。锅里换上清水，放入乌参置旺火上烧开，端锅离火，泡到冷后取出清洗，这样反复三四次泡洗，直至发透为止。再放入冷水中漂浸待用。发海参的炊具不能有油、碱、盐，否则发不开或深化，泡洗次数要视涨发情况而定，已发好的先捞出，防止发得太酥软而破碎不成形。将乌参放在炒锅里烧的时候，要皮朝上，否则易破坏表皮完整，影响美观。如没有干虾籽，可将鲜虾籽洗净后，放', NULL, '水发大乌参250克，炒肉卤24克，葱结15克，湿淀粉25克，绍酒15克，豆油750克\r\n，白糖4克，干虾籽1.5克，葱段2克，肉清汤150克，味精2克，猪油60克，酱油10克。');
INSERT INTO `recipe` VALUES (2566, 67, '苋菜黄鱼羹', '1.将黄鱼的刺和骨拆净，肉切成小丁，放入温猪油锅内煸一下。\r\n2.将苋菜洗清，剔除老根败叶，也下油锅煸一下，随即将煸过的黄鱼丁放下去，加清汤、盐、酒、葱末、姜米、糖和笋丁、火腿丁，烧滚后，加湿菱粉勾芡，浇上少许猪油，撒些胡椒粉即好。', '色翠绿，味清鲜。', NULL, NULL, '黄鱼肉250克、苋菜75克、熟笋丁45克、熟火腿丁20克、葱末少许、姜米少许、胡椒粉少许、黄酒8.5克、白糖少许、盐6克、菱粉20克、清汤400克。');
INSERT INTO `recipe` VALUES (2567, 67, '香糟扣肉', '1.猪腿肉刮洗干净，修切成约10厘米见方的块，放入开水锅内白煮，约20分钟后捞出，趁热在肉皮上均匀地涂抹酒酿汁（5克）。\r\n2.炒锅置旺火，倒入花生油，烧热后放入涂抹过酒酿的猪肉，炸至肉皮发典时取出。冷却后，切成约8厘米长、0.6厘米厚的大片。\r\n3.将香糟、绍酒和清水75克放在同一碗内，拌成糊状，滤去糟渣，加入白糖、精盐、味精，一起和匀成糟汁，把肉片放入浸渍10分钟。\r\n4.将猪肉片肉皮朝下，整齐地放入碗内，浇上浸渍肉的糟汁，放入蒸笼里和旺火蒸至酥油，取出，翻扣在平盆里即成。', '香糟扣肉的烹制遵循了《随园食单》所指的“味太浓重者，只宜独用不同搭配”的须知条。它制法独特，把猪肋条肉改用猪后腿肉，以酒酿汁代替了酱油，肥少瘦多，色泽棕黄，肉质酥烂，入口不腻。更兼糟香四溢，咸里透甜。', '猪腿肉白水煮至五成熟，趁热在皮上涂抹酒酿，立即下锅油炸，皮色金黄，酥松厚发即可。旺火气足，蒸约2.5小时，酥烂为度。', NULL, '带皮猪腿肉500克，味精1克，绍酒25克，香糟50克，酒酿汁20克，花生翊1000克，精盐10克，白糖50克。');
INSERT INTO `recipe` VALUES (2568, 67, '蟹粉蹄筋', '1.将蹄筋去开成二段。\r\n2.打开热猪油锅，将葱、姜放入锅内爆一下就捞出来。然后将蟹粉放入略炒，加酒、盐、酱油、味精和水110克，随后将蹄筋放入锅内，烧约二分钟，下湿菱粉勾芡，撒上胡椒粉，浇些猪油即好。', NULL, NULL, NULL, '水发蹄筋300克、蟹粉160克、葱段8.5克、姜米8.5克、胡椒粉、黄酒、酱油12.5克、 盐、味精、菱粉60克。');
INSERT INTO `recipe` VALUES (2569, 67, '蟹黄扒翅', '1.将鱼翅整只扣在碗里，加上酒、葱、姜蒸约一小时，倒掉汤，取出翅，放入开水锅里川一下（要注意不要将翅弄散），再用冷水洗净。\r\n2.用猪油、葱、姜开锅后，捞去葱、姜，将蟹黄倒入略炒，随即下酒一烹，再加上白酱油、味精和清汤用旺火烧开，将鱼翅放进（正面朝下），用温火烤约五分钟，再转旺火，同时下湿菱粉，提锅转几转，以防黏锅。然后浇上鸡油，猪油转几转，翻转（正面朝上）、起锅装盘。', '金黄色，味鲜糯，适宜于秋冬二季。', NULL, NULL, '水发鱼翅最好用吕宋黄翅600克、蟹黄150克、姜片6克、葱段6克、白酱油35克、黄酒12.5克、菱粉12.5克、味精少许、清激发250克、鸡油少许。');
INSERT INTO `recipe` VALUES (2570, 67, '雪菜炒冬笋', '1.将冬笋切成小旋刀块；雪菜去掉败叶和老梗，切成末子。\r\n2.开温猪油（325克）锅（太热了，不能使笋里熟外白），将笋入下去拉约两分钟倒出，滤去油。\r\n3.再将雪菜放入锅内略炒，然后把笋放下去，加清汤、糖、盐、烧约一分钟，加湿菱粉勾芡，浇上少许麻油起锅。', '雪菜就是雪里蕻咸菜，要选用冬令腌制的，叶嫩茎肥，有鲜味，腌制时间约需半月，过短带辣味，过久会变黑，都将影响烹制的好坏。雪菜炒冬笋应白烧，油要重一些，味鲜嫩清口。', NULL, NULL, '净冬笋肉325克、雪菜75克、白糖20克、盐45克、菱粉8.5克、麻油少许、清汤175克。');
INSERT INTO `recipe` VALUES (2571, 67, '熏鱼', '1.将青鱼段洗清，片成三分厚的长块（太厚了不易入味）；用酱油、盐、酒调和，将鱼块放入约腌五分钟。\r\n2.将茴香、桂皮、葱、姜、盐、酱油、糖、味精放在锅里，加水约165克，起温火熬出汁，捞去渣滓，卤汁放好。\r\n3.开大生油锅，烧至七成熟后，将鱼块放入滚炸约六七分钟后捞起，再放在卤汁里浸泡一段时间（长短听便），捞出，切成2寸长、半寸宽的小块装盘，撒上五香粉即好。', '这种熏鱼不用烟熏，而用油熏，即将调制好的青鱼放入大生油锅内滚炸后，再用卤汁浸泡至色呈金黄为止。此菜有五香味而无烟味，这是它不同于一般烟熏的特点。青鱼必须新鲜，炸的时间要适当，太嫩了不香，太老了不鲜。', NULL, NULL, '青鱼段500克、葱段20克、姜片20克、五香粉少许、桂皮20克、茴香13克、黄酒26.5克、白糖46.5克、盐4克、酱油41.5克、味精少许。');
INSERT INTO `recipe` VALUES (2572, 67, '烟鲳鱼', '将鲳鱼刮鳞，去鳃，去内脏洗净，用斜刀片切成三段放在汤盘里，加葱花，姜片，曲酒，白糖，酱油，饴糖，味精，精盐，浸渍几十分钟；用铁丝网架一只在网上抹熟花生油，然后将浸过汁的鱼平放在铁丝网上，放进烤箱烤十分钟左右，再在炉里加茶叶和赤砂糖，关上炉门任其燃烧冒出浓烟，使鲳鱼在炉内边受热边烟熏，约六分钟后，鱼表面呈红棕色，鱼块已熟取出，用油刷涂上熟花生油拼摆成整鱼装盘，盘子两边放色拉酱，供蘸食。', '肉质嫩，味咸鲜，烟香浓郁。', NULL, NULL, '鲳鱼，葱花，姜片，曲酒，酱油，精盐，白糖，味精，饴糖，色拉酱，湿红茶叶，赤砂糖，熟花生油。');
INSERT INTO `recipe` VALUES (2573, 67, '盐擦鸡', '1.将鸡杀好，去内脏及脚并洗净，用干洁布揩干，用针在鸡腿上、鸡脯上均匀地刺上一些小孔，全身抹上盐和花椒，放在钵内压约十二小时。\r\n2.做卤汁：将鸡汤加酒、味精调好味，放入锅里烧开，倒在钵头里，等冷却后，将压过的鸡放入浸约五六小时，取出，放在蒸器内，上面放葱、姜，用温火蒸约二十五分钟，即可切长方条食用。', '味道鲜美。', NULL, NULL, '肥母鸡1只1900克、葱段42克、姜块42克、花椒13.5克、黄酒330克、盐85克、味精少许、鸡汤2100克。');
INSERT INTO `recipe` VALUES (2574, 67, '盐水鸭', '1.光鸭斩去小翅和鸡爪，在右翅窝下开约8厘米长的小口，取出内脏、气管、食管，放入清水中浸泡，去除污，洗净沥干水。和精盐、花椒、五香粉少许抖和腌制2小时左右，再放在盐水中浸1-2小时取出洗净。\r\n2.炒锅加清水，放入鸭子和香醋、葱结、姜块、八角，置旺火上烧开，加盖焖烧20分钟，将鸭翻身，再焖烧20分钟左右，至鸭肉酥烂时取出，冷却即成。', '鸭肉玉白，油润光亮，皮肥骨香，鲜嫩异常。', '鸭子必须洗净，去除肾臊，否则会有异味。', NULL, '光鸭2000克，葱结1只，精盐25克，姜2块，香醋25克，五香粉2克，花椒1克，八角2只。');
INSERT INTO `recipe` VALUES (2575, 67, '原笼粉蒸牛肉', '1.牛肉剔去筋膜，横切薄片，加甜面酱，郫县豆瓣料酒，酱油，白糖，花椒粉，辣椒粉和炒米粉拌匀。\r\n2.用青菜叶垫在笼底，倒入拌好的牛肉，用大火蒸熟。 \r\n3.用香油，葱花调成葱花油，淋在牛肉上，原笼上桌即成。', NULL, NULL, NULL, '净牛肉250克，炒米粉40克，青菜叶100克，葱花、姜末各3克，甜面酱50克，郫县豆瓣酱10克。');
INSERT INTO `recipe` VALUES (2576, 67, '炸玫瑰球', '1.将白膘、红瓜、青梅、桔饼、红枣、蜜萝卜、葡萄干、蜜枣、花生剁成末，与糖拌和，做成小丸子；将蛋白打在碗里，加菱粉、食用红水，打和成蛋白糊。\r\n2.开四成熟的猪油（1000克）锅，将小丸子一只只地滚上蛋白糊，随即放入油锅滚炸约四分钟，起锅装盘。', '肥而香甜。', NULL, NULL, '白膘250克、白糖75克、红瓜、青梅、桔饼、红枣、蜜萝卜、葡萄干、蜜枣、花生共200克、鸡蛋白3只、菱粉45克、食用红水。');
INSERT INTO `recipe` VALUES (2577, 67, '炸猪排', '1.将猪肉切成长13厘米、宽45厘米、厚O.4厘米左右的大片，平摊在砧板上，用刀背拍透，放盘内用葱结、姜块、绍酒、精盐、味精、胡椒粉浸渍数分钟。 \r\n2.鸡蛋磕入碗内打散、调匀，把浸渍后的肉片蘸上鸡蛋液，两面蘸上面粉，再蘸满面包屑，以手掌揿捺之，成猪排生坯。 \r\n3.炒锅上火，待油烧至七成热时放入猪排生坯，炸至淡黄色捞起。待油八成热时，将猪排再炸一次呈金黄色捞起沥油，用刀改成小块装盘，带辣酱油碟上桌即可。', NULL, NULL, NULL, '猪夹心肉25O克，咸面包屑1OO克，鸡蛋1只，面粉5O克，花生油1OOO克，绍酒5克，精盐2克，辣酱油5O克，味精1克，葱结1个，姜块1块，胡椒粉1克。');
INSERT INTO `recipe` VALUES (2578, 67, '猪肉烧卖', '1.取面粉，用开水烫，揉匀揉透，搓成条，摘成坯，擀皮待用。猪肉蒸熟，配料均切成细丁。\r\n2.锅内放油，烧热，葱姜爆锅后，放入肉末炒一下，随即下配料炒至半熟，加调味料，盛出。', NULL, NULL, NULL, '面粉5OO克，熟猪肉5OO克，猪肉末15O克，猪油5O克，冬笋 5O克，冬菇25克，酱油25克，白糖6O克，味精、黄酒、葱姜各适量。');
INSERT INTO `recipe` VALUES (2579, 68, '白烧四宝', '1.把鸡腰洗清，放入开水煮一下，取出撕去外皮，把鸭舌先用开水烫过，也撕去外皮，同鸭掌一道放入开水内煮熟，取出拆净软骨。 \r\n2.起热猪油锅，把鸡腰、鸭舌、鸭掌、熟鸡皮，菜心、蘑菇一道下锅，加浓汤、酒、盐烧。等汤将近收干，即调菱粉下锅勾薄芡，同时浇上鸡油起锅。', '味道鲜美可口。', NULL, NULL, '鸡腰200克、鸭舌15克、鸭掌15只、熟鸡皮15块、菜心10余棵、鲜蘑菇75克、浓汤1中碗、鸡油75克、黄酒75克、菱粉7.5克、精盐少许。');
INSERT INTO `recipe` VALUES (2580, 68, '脆皮卷', '1.把大肠里外洗净，加黄酒、葱、姜、八角炖熟（约两小时）。取出放在酱油内拌一拌，再下旺猪油锅炸脆。取出去掉头尾。切成1寸上下的小段，装在盘的中央。\r\n2.把豌豆苗放入油锅，加极少的盐炒好，镶在大肠的周围。另外在大肠上撒少许香醋、花椒盐即成。', '味香脆而嫩。', NULL, NULL, '猪大肠500克、豌豆苗300克、八角3只、精盐4克、酱油4克、葱姜125克、黄酒100克、花椒盐少许、香醋少许。');
INSERT INTO `recipe` VALUES (2581, 68, '淡菜炒笋尖', '1.把扁尖笋切成1寸长的条子，把淡菜放入开水内泡一泡；\r\n2.用碗两只，把扁尖笋、淡菜各装一碗，碗内加开水与材料平，上笼蒸松后，取出淡菜，剪除老块和中心的毛茸，再洗一次；\r\n3.起素油锅、把笋尖、淡菜分两边倒入，加原汤（即蒸淡菜的汤）、糖、酒、盐、鸡汤、分两边边滚边炒，直至汤收干，起锅装盘。一边放淡菜，一边放笋尖，冷却后上桌。', '色美观，味香脆。', NULL, NULL, '小淡菜200克、嫩扁尖笋200克、素油200克、白糖适量、鸡汤小半碗、黄酒65克、精盐少许。');
INSERT INTO `recipe` VALUES (2582, 68, '冻鸭', '1.把光鸭、猪肉、猪皮一道放入开水内稍煮一下，解除血水，取出放入砂锅，加适量的水（以能淹没材料为度）和酱油、盐、酒、糖、茴香、八角、葱、姜、用温火炖烂。\r\n2.将汤泌出，盛在大碗内，再把鸭取出，拆去骨，也放入大碗内，其他都不要，用快冻方法冻好后，倒出切成大块即好。', NULL, NULL, NULL, '大光鸭1只、猪肉500克、猪皮200克、酱油60克、精盐12.5克、黄油75克、白糖40克、茴香少许、八角少许、葱姜少许。');
INSERT INTO `recipe` VALUES (2583, 68, '和合腰子', '1.先将猪腰洗净，用清激发煮熟，切成32个2分厚的小块，每块拌上干菱粉。\r\n2.将鸡肉切成茸，将鲜蘑菇和火腿切成末子，加鸡蛋（打匀）、酒、盐调匀。\r\n3.然后将一片腰片放在上面，一片放在下面，中央夹鸡茸，成为和合腰子。\r\n4.取盘子，盘内搽麻油，把和合腰子放入，上笼蒸，约十分钟可蒸熟。\r\n5.再用清汤（约50克）、菱粉、盐调卤，浇了再吃。', NULL, NULL, NULL, '猪腰4只、鸡肉125克、鲜蘑菇40克、火腿20克、黄酒20克、精盐4克、鸡蛋1只、干菱粉40克、麻油少许。');
INSERT INTO `recipe` VALUES (2584, 68, '荷花集锦炖', '1.鱼肚放入砂锅，加鸡清汤，调料烧开，捞出沥干，放入砂锅内。 \r\n2.鸡肫切菊花刀纹，猪腰剞麦穗刀纹，鳜鱼肉片成片，香菇、虾仁用鸡清汤汆过。将火腿、冬笋、虾茸蛋卷，鸡脯肉切片，排成阶梯形，在鱼肚上摆成“十” 字形，四角摆鸡肫花、猪腰穗、鳜鱼片及香菇片，放在四角。 \r\n3.鸡蛋小头处雕成花瓣形，火腿、冬笋切花瓣形片，插入鸡蛋黄中，1个蛋放中间，9个放砂锅周围，蒸15分钟。 \r\n4.鸡清汤烧开倒入砂锅中即成。', NULL, NULL, NULL, '熟鸡脯肉，熟虾茸蛋卷，熟火腿，油发鱼肚，浆虾仁，熟鸡蛋，青菜心。');
INSERT INTO `recipe` VALUES (2585, 68, '红果拌梨丝', '1. 把红果放入开水略泡几分钟，取出去皮去核，把雅梨也去皮去核，切成1寸长的丝。\r\n2. 起干锅，将冰糖放入锅内化开，随即加红果炒成透明起锅，同梨丝拌和装盘。味酸甜清脆。', NULL, '梨丝容易变色，必须现吃现做。', NULL, '红果150克，雅梨500克，冰糖200克。');
INSERT INTO `recipe` VALUES (2586, 68, '黄油两吃', '1.先准备好蟹黄，再把葱、姜下热猪油锅，跟着再下蟹黄和酒、盐、糖炒熟，取出放在盘内。\r\n2.将蟹油放入用蛋白、盐、菱粉、面粉调的稀酱内拌一拌，再一个个放入旺火大油锅内炸熟，取出蟹黄放旁边，再在蟹黄上面撒少许香菜末，胡椒粉即好。', NULL, NULL, NULL, '蟹油200克、蟹黄200克、鸡蛋白200克、菱粉75克、葱姜少许、黄酒少许、精盐少许、白糖少许、香菜末少许、胡椒粉少许、面粉75克。');
INSERT INTO `recipe` VALUES (2587, 68, '酒风猪肝', '1.把猪肝洗净，切成宽长条，每条开一个长长的裂缝。\r\n2.把板油（或肥膘）也切成小宽条，塞入每条猪肝，泡入高梁酒内，并加密封，到次年取出，加糖、麻油、盐蒸熟切片吃。', '味道香美无比。', NULL, NULL, '黄色猪肝1400克、高梁酒500克、板油或肥膘500克、精盐少许、白糖75克、麻油少许。');
INSERT INTO `recipe` VALUES (2588, 68, '眉炸梅卷', '1.把猪里脊、鸡肝切细捶茸，加葱姜汁、鸡蛋（1只）、酒、糖、盐拌匀。\r\n2.把肉油摊开，将里脊、鸡肝茸放上全面铺开，卷成1寸径、粗6寸长的卷子，一切两段，排在盘中，上笼蒸约十分钟，取出稍凉一下。\r\n3.再用糯米粉和鸡蛋2只打匀，将卷子一段段放入拖一拖，再放入六成热油锅内炸酥，取出将每段再切成6小段，整齐地装在盘内，另外再撒上少许花椒盐即好。', '造型美观，香脆可口。', NULL, NULL, '猪里脊肉300克、鸡肝125克、网油500克、葱姜汁75克、黄酒40克、白糖2克、精盐6克、鸡蛋3只、糯米粉250克、花椒盐少许。');
INSERT INTO `recipe` VALUES (2589, 68, '全家富', '1.鱼片、虾仁过油滑熟； \r\n2.海参、鱼肚、鸡脯片、冬菇、口蘑、蹄筋焯水漂净； \r\n3.炒锅加熟猪肉25克，加葱姜汁、高汤、精盐、味精、白胡椒烧开，倒入各种原料烧烩入味后，勾芡装盘即成。', NULL, NULL, NULL, '水发海参150克，鲍鱼5克，油发鱼肚100克，水发鱼翅50克，鸡脯肉100克，厥鱼肉100克，水发冬菇、口蘑、鲜虾仁、水发蹄筋各50克。');
INSERT INTO `recipe` VALUES (2590, 68, '三丝燕菜', '1.燕窝洗净涨发好，放鸡汤内用小火蒸至软糯； \r\n2.鸡腿、火腿、冬菇切丝，加鸡清汤蒸15分钟捞出； \r\n3.鸡清汤烧开，倒入燕菜碗中连烫两次捞出； \r\n4.鸡腿丝、火腿丝、冬菇丝分置碗底，上覆燕菜丝，浇入鸡清汤即成。', NULL, NULL, NULL, '燕窝30克，熟鸡腿肉50克，熟火腿肉50克，水发冬菇25克。');
INSERT INTO `recipe` VALUES (2591, 68, '天下第一菜', '1.虾仁治净，加干淀粉、盐、鸡清汤搅至上劲。 \r\n2.鸡清汤烧开，下虾仁、熟鸡丝、料酒、白糖、味精、番茄酱烧开，用水淀粉勾芡，淋入香油成卤汁。 \r\n3.锅巴下7成热油中炸脆，捞出装盘，再淋一勺热油后迅即倒入卤汁即成。', NULL, NULL, NULL, '锅巴100克，大虾仁200克，熟鸡丝100克，番茄酱100克。');
INSERT INTO `recipe` VALUES (2592, 68, '扬州炒饭', '1.将鸡蛋打碎炒熟，切成块，火腿、黄瓜、虾仁切小丁。\r\n2.炒锅加油，将米饭、鸡蛋、火腿丁、黄瓜虾仁丁一同放入锅内炒，加味精。', NULL, NULL, NULL, '米饭250克，火腿30克，鸡蛋一个，虾仁20克，青碗豆20克，盐、味精适量，油20克。');
INSERT INTO `recipe` VALUES (2593, 68, '鱼香茄夹', '1.将茄子切成夹刀片，中间填上调好的猪肉蹄，调上鸡蛋糊备用。 \r\n2.炒锅中油烧五成热时，将挂好糊的茄子下锅炸定型，待油升至七成热下锅复炸至金黄色。 \r\n3.用葱姜末，蒜泥，辣椒末，酱油，糖、味精，醋调成鱼香卤汁，将茄子下锅颠翻匀。淋油出锅装盘。', '色泽红润，五味俱全。', NULL, NULL, '茄子、五花肉、西兰花、葱、姜、料酒、蒜、糖、盐、醋、味精、淀粉、樱桃、橙子、鸡蛋、辣椒。');
INSERT INTO `recipe` VALUES (2594, 68, '竹叶蒸鸡', '1.将鸡宰杀，洗净，去头脚，剁成块状，用葱，姜，盐腌渍。 \r\n2.竹叶用热水烫过将鸡块包裹起来，上面用香菇封口，入蒸笼旺火蒸30分钟左右取出装盘，用樱桃点在口上。', '竹叶清香，鲜嫩爽口。', NULL, NULL, '竹叶、仔鸡、酱油、糖、盐、味精、香菇、樱桃、番茄、菜叶。');
INSERT INTO `recipe` VALUES (2595, 69, '爆乌花', '1.鲜墨鱼洗净，切成荔枝花刀块焯水沥干；\r\n2.花生油烧至6成热，投入葱末、姜末、蒜末煸香；再放入乌花、玉兰片、木耳、青豆、香菜及调料勾芡，装盘后撒上胡椒粉即成。', NULL, NULL, NULL, '鲜墨鱼肉560克、香菜15克、水发玉兰笋片25克、木耳、青豆、葱末、姜末、蒜末各5克。');
INSERT INTO `recipe` VALUES (2596, 69, '寿桃豆腐', '1.豆腐削去边皮塌成泥，虾仁也剁成泥，同放碗内，加入鸡蛋清、葱姜汁、味精、精盐、胡椒粉搅拌，再加干淀粉和匀。 \r\n2.咸面包切成约0.3厘米厚的片，修成桃形，共10块，平摊在案板上，撒上一层干淀粉拍匀，每片抹上约1厘米厚的豆腐泥，涂上一层鸡蛋清，在桃尖上撒火腿末，底部撒上菜叶末，制成寿桃豆腐坯。 \r\n3.炒锅置旺火上，放入熟猪油，烧至四成热，下寿桃豆腐坯炸至面包片发硬、现出微黄色时捞出。待油温升至七成热后，再炸一次，捞起装盘，淋芝麻油少许即成。', '“寿桃”是安徽寿筵中不可缺少的豆腐为主料塑造成立体状寿桃的一种名菜。此菜面包酥脆，豆腐鲜嫩，一香一鲜，以花椒盐、葱、白段、甜面酱佐食，是淮南“豆腐宴”中的主要名菜。', '重油时火大油热，冲炸即成。', NULL, '八公山豆腐400克，精盐2.5克，虾仁50克，味精1克，熟火腿末25克，葱姜汁4克，青菜叶末25克，绍酒15克，咸面包150克，芝麻油25克，鸡蛋清1个，熟猪油500克（约耗100克），胡椒粉1克，干淀粉100克。');
INSERT INTO `recipe` VALUES (2597, 69, '无为熏鸭', '1.将鸭宰杀，去毛洗净，在右翅下划开一个6.5厘米长的刀口抽出食管和内脏。将鸭放入水里，从刀口灌进水，然后用一节6.5厘米长两端削尖的小竹筒（芦苇管亦可）插进肛门，放到水里浸泡一个半小时，拔掉小竹筒，出尽血水，再洗净沥干。\r\n2.将鸭由开口处放入盐25克，再灌进硝水15克，用右手食指插进肛门堵紧（防止硝和盐漏出），左手握住鸭颈，拾起上下晃动两下，再将鸭倒过来，晃动两下，然后把鸭背向下，置板上，一手拿着颈部，一手拿着尾部，平着提起掼两下，翻个身，脯向下，提起来也掼两下，使盐和硝水在肚中分布均匀。再用盐擦遍全', '此鸭经过浸、腌、烫、熏和恒温宽汤卤汁焖煮后，色泽金黄，皮脂厚润，肉质嫩鲜，烟熏芳香，别有滋味。', NULL, NULL, '活鸭1只（约2000克重）、酱油125克、醋25克、精盐50克、白糖15克、姜15克、小葱结10克、麻油25克。');
INSERT INTO `recipe` VALUES (2598, 68, '肴肉', '1.把蹄膀拆骨修齐洗净，用细木签在蹄膀的肉面上均匀地打上一些小孔。\r\n2.用硝6.5克、盐65克拌和，擦在肉上，再抓住肉来回揉透，使硝、 盐通过小孔渗透到肉的内部。做十斤肴肉，最多只能用硝6.5克，如肉增加，硝则要随之增加。揉好后放着腌，冬天约须腌三天，春秋天约须腌两天，夏天约须腌一天，腌过后，再把肉放冷水内泡两小时，取出用刀把肉上的硝末、杂质轻轻刮净，至皮肉呈现白色停止。\r\n3.刮净后，将肉放入锅内，倒上做肴肉老卤起火煮，煮前须用木板加石块将肉压紧，同时把锅盖扣紧，使肉煮成后不发松，煮的时间须根据肉的老', '色透明，味香嫩不腻，作小吃、冷盘、大菜无不适宜。', NULL, NULL, '蹄膀1只4000克、硝6.5克、粗盐65克。');
INSERT INTO `recipe` VALUES (2599, 68, '鸳鸯雪花卷', '1.将猪肥膘下开水锅煮到六成熟，取出片切成薄片，将鸡肉切成茸。将火腿、葱都切成细末。再将鸡茸、火腿末、葱末、酒、盐混和调匀。\r\n2.把肥膘摊开，表面撒一层薄薄的干菱粉，再把鸡茸、火腿末、葱末放上，卷成指头大小的圆形卷子4条，放入湿菱粉内拖一拖，再在外面滚满白芝麻，再滚黑芝麻，下油锅炸透取出，每条切成斜刀块四块，放在菜盘周围。\r\n3.取青菜心下油锅，加盐、味精炒透，放在菜盘中央即好。', '色美观，味香脆。', NULL, NULL, '猪肥膘300克、鸡肉75克、葱20克、火腿40克、鸡蛋2克、湿菱粉40克、干菱粉40克、黑芝麻65克、白芝麻75克、黄酒少许、精盐少许、青菜心12棵、味精少许。');
INSERT INTO `recipe` VALUES (2600, 69, '八公山豆腐', '1.豆腐切块，下冷水锅中烧开捞起沥干。 \r\n2.笋切片，淀粉加水调成糊状。豆腐滚浆，下5成热油中炸至黄金色。 \r\n3.虾籽、笋片、木耳下5成热油中煸炒，加入豆。', NULL, NULL, NULL, '八公山豆腐250克，熟笋25克，水发木耳50克，虾籽10克。');
INSERT INTO `recipe` VALUES (2601, 69, '茶叶熏鸡', '1.葱10克切成段，另5克葱和花椒、盐一起制成细末，做成葱椒盐备用。 \r\n2.鸡宰杀，放尽血水，烫去毛，从脊背开刀，掏去内脏和鸡嗉，洗净沥干水，用葱椒盐均匀撒在鸡身上，腌20分钟。将鸡身扒开，皮向下放在碗里，上放葱段、姜片，加酱油、烧酒，上笼蒸至八成熟，取出，拣去葱姜。 \r\n3.饭锅巴掰碎放入炒锅，撒上菜叶、红糖，架上篦子，将鸡皮向上摆在篦子上，盖严锅盖，先用中火熏出茶叶，稍顷片刻改旺火熏至浓烟四起时离火，烟散尽，掀开锅盖，取鸡刷上芝麻油。将鸡头、鸡尖、鸡腿爪剁下，鸡身切成5厘米长、3厘米宽的块，鸡骨、鸡', '瓜片茶叶产于安徽省六安地区，以齐山出产最佳，茶叶清香扑鼻。此菜鸡色金黄悦目，肉质鲜美，烟熏味中带有瓜片茶叶之清香，别具风味。', '要选用嫩鸡，一方面是易熟易入味，另一方面是熏制出来的鸡口感好，不老不柴。火候的掌握是鸡味好坏的关键，时间短，茶香不入；时间长，易出煳味。鸡皮向上，上色均匀。', NULL, '嫩鸡1只750克、姜片10克、瓜片茶叶15克、精盐5克、小葱15克、红糖25克、酱油25克、饭锅巴100克、绍酒20克、芝麻油15克、花椒3克。');
INSERT INTO `recipe` VALUES (2602, 69, '符离集烧鸡', '1.选用每只约重1000克的烽鸡10只，宰杀放尽血水，热水烫、去毛、洗净，在靠肩的颈部直开一小口，取出嗉囊，用水洗净。然后先用刀背敲断大腿骨，从肛门上边开口处把两只腿交叉插入鸡腹内；再将右翅膀从宰杀的刀口处穿入，使翅膀尖从鸡嘴露出。鸡头弯回别在鸡膀下边，左膀向里别在背上，与右膀成一直线。最后将鸡腹内两只鸡爪撑开，顶住鸡腹。用以上方法将10只鸡宰杀别好备用。 \r\n2.将别好的鸡挂在阴凉处，晾干水分，用毛刷蘸饴糖涂抹鸡身。涂匀后入大油锅中炸成金黄色时捞出。剩油留作别用。\r\n3.大锅内放足水，把所有香料装入一只', '此菜外观油润发亮，肉质雪白，味道鲜美，香气浓郁，肉烂脱骨，肥而不腻。嚼其骨，有余香。趁热提起鸡腿轻抖，鸡肉可全部脱落，为菜中上品，冷食热食均可。', '“别”鸡工序不可少，这是为了成批的卤煮方便，形体完整美观。当天宰的鸡，当天最好不用。因鸡的尸僵期是7小时左右，所以鸡刷匀饴糖后，即可挂起备用。第二天再进行下一步加工。炸鸡的油温要始终保持在七成热。油温低，鸡不变色。油温过高，则发黑。为了控制油温，可采取一次炸1-2只，炸完后捞出。卤汤一次加入的水量，以没过鸡为宜，中间不能再加水。卤汤用后晾凉，除浮油放容器中，存入冰箱，一个星期加热一次即可。无冰箱，可每天加热一次，冬天两天加热一次。', NULL, '活鸡10只，桂皮10克，白糖15克，陈皮10克，八角10克，辛萋2克，小茴香2克，精盐150克，姜20克，饴糖200克，肉蔻3克，山奈片3克，砂仁2克，丁香3克，白芷5克，草果3克，花椒5克，芝麻油1500克。');
INSERT INTO `recipe` VALUES (2603, 69, '干贝萝卜', '1.将干贝洗去浮灰，置碗内，加入绍酒和水50克，上笼蒸烂取出搓散，汤滤去陈渣留用。选生瘦火腿，切成2厘米宽、5厘米长的片。小片圆萝卜剥皮，削成圆柱打成齿轮形花，再切成0.5厘米厚齿轮形的片。\r\n2.炒锅放在旺火上，放入熟猪油，烧至四成热时，下萝卜片，炸至发软捞出沥油。 \r\n3.取碗1只，碗底中间放干贝，旁边围火腿，再将萝卜片排放汤碗中，加入鸡汤和蒸干贝的汤以及冰糖、精盐上笼用旺火蒸约45分钟取出，先将原汁滗入锅中，另取一汤盘在碗上翻身扣在盘中，锅中原汁加湿淀娄调稀勾芡，淋入熟猪油10克，撒上葱末，芡浇在上', '此菜清淡、干鲜、爽口、助消化，在酒席中有醒酒功效。', '蒸干贝时间要保证1.5小时以上。对质地较硬的干贝可先用冷水浸泡3至4小时。萝卜要选鲜嫩整齐的，去皮后将萝卜削成直径2厘米粗的圆柱。炸萝卜时注意不要时间过长。', NULL, '干贝12克，绍酒15克，白萝卜500克，湿淀粉10克，火腿10克，鸡汤300克，冰糖1.5克，小葱末5克。');
INSERT INTO `recipe` VALUES (2604, 69, '花菇田鸡', '1.将田鸡腿入开水锅中略烫捞出洗净沥干，用姜汁、精盐、甜酒、味精腌渍入味，水发花菇去蒂洗净； \r\n2.取碗一只，将田鸡腿排列在碗中，花菇放在田鸡腿上面，加入猪油、鸡汤，另用1只盘子盖好，上笼旺火蒸15分钟取出，拣出花菇；\r\n3.将蒸田鸡的原汤滗入锅中，田鸡腿翻扣在盘中。', '田鸡不仅肉质细嫩鲜美，而且富含蛋白质、钙。', NULL, NULL, '去皮田鸡腿400克，水发花菇150克，姜汁10克，味精1克，甜米酒、精盐、湿淀粉各5克，鸡汤250克，熟猪油100克。');
INSERT INTO `recipe` VALUES (2605, 69, '黄山炖鸽', '1.将山药削去外皮，洗净，切成0.1厘米的片，放开水锅中浸烫一下捞起。 \r\n2.将鸽子人腹部（靠近肛门附近）开一小口，掏出内脏（留肫、肝），洗净，下开水锅中浸烫一下，捞起再清洗一次，放在汤碗内，加入山药片、葱结、姜（拍松）、精盐、冰糖、绍酒和鸡汤，用1个大盘盖在汤碗上，上笼蒸2小时左右取出，淋入熟鸡油即成。', '黄山是世界闻名的旅游胜地，它位于皖南山区，方圆250公里，现代文学家、考古学家郭沫若推黄山为“天下第一山”。黄山不仅风景著名，其特产山珍野味也令人垂涎。黄山炖鸽是道名菜，山药滋阴补肾，鸽肉强身健体，被当地百姓视为滋补延年的佳品。此菜汤清味鲜，鸽肉酥烂，山药软糯，是徽菜中的传统名肴。', '汤碗用皮纸严，保持原汁原味，且能缩短蒸制时间，鸽肉口感更美。', NULL, '净菜鸽2只500克，姜块25克，冰糖2.5克，精盐5克，黄山山药25克，绍酒25克，小葱结25克，鸡汤750克，熟鸡油10克。');
INSERT INTO `recipe` VALUES (2606, 69, '徽州桃脂烧肉', '1.将桃脂洗净，用温热水泡软，取出沥干，剥去外皮。 　　\r\n2.将猪五花肉洗净，切成两厘米见方的肉丁备用。 　　\r\n3.锅放火上，下猪油烧至七成热，放入肉丁略炒，下酱油再炒上色，入水稍没过肉，用大火烧开，再加入葱、姜、盐、糖，改用小火烧至肉熟时，投入桃脂，改用大火继续烧一下，出锅盛入盘内即成。', '此菜品肉色红润，酥而不腻，桃脂绵软嚼有桃香。', '选用不带皮的五花肉，以肥瘦相间，层次分明为止。炒肉丁时，视其外表收缩，瘦肉变白时，再加入酱油炒上色。烧肉时，锅要加盖，水不可过多，待汁少肉熟时，再加入桃脂，敞开锅，大火烧一会儿，这样烧出的菜，桃脂不易化，肉味又佳，汁量正好，是此菜的制作关键。', NULL, '桃脂250克，猪五花肉500克，葱结15克，白糖5克，酱油10克，姜片10克，精盐2克，猪油15克。');
INSERT INTO `recipe` VALUES (2607, 69, '夹心虾糕', '1.虾仁洗净沥干水，菜叶剁成细泥。\r\n2.将虾仁和肥膘肉轻轻地剁成细泥，放在碗里加盐2.5克、味精0.5克、水50克、绍酒搅匀，再放鸡蛋清搅拌上劲，最后加干淀粉搅匀。菜叶泥放在碗里，加盐1克、虾泥100克搅拌均匀成绿色虾泥。\r\n3.取大盘1个，盘心抹一层薄薄的猪油。把肥膘虾泥分成相等的两份。放一份虾泥在盘中铺平，把绿色虾泥放在上面摊平，再将另一份肥膘虾泥放在绿色虾泥上摊平（三层共厚约2厘米）。然后上笼用旺火蒸制。将蒸好的虾糕切成3厘米宽的长条，再改刀切成菱形块。把切下来的虾糕边角料放在盘中心，再把整块的尖', '夹心虾糕是安徽巢湖地区喜庆筵席上的名菜，系选用我国五大淡水湖之一的巢湖特产白米虾，据其熟后不红的特点，制成洁白鲜嫩的虾泥，间以绿色菜汁配制的翡翠虾泥层，以旺火蒸制而成，工艺精湛，火候独到，成品白如玉，绿如翠，赏心悦目，咸鲜滑嫩，以香醋相佐，别具风味。', '虾仁洗净，先用牙签挑去沙线，然后将虾仁倒入清水盆里用筷子搅转半分钟倒出浑水，换清水再轻搅半分钟，洗净沥水，待用。蒸虾糕用旺火蒸6-7分钟即取出。时间短不熟，时间长则硬。', NULL, '虾仁200克，鸡蛋清2个，绿色叶100克，盐5克，肥猪膘肉100克，绍酒15克，味精0.5克，干淀粉5克，香醋50克，鸡汤200克，湿淀粉10克，熟猪油10克。');
INSERT INTO `recipe` VALUES (2608, 69, '李鸿章杂烩', '1.海参、鱼肚、鱿鱼、玉兰片、腐竹、熟火腿切片；\r\n2.猪肚、鸡肉、干贝加葱结、姜片、绍酒蒸透入味、切片；\r\n3.熟白鸡肉、熟火腿切丝；\r\n4.鱼肉制茸上味，粘上干贝丝蒸熟；\r\n5.将干贝球、各种原料和调料下入鸡汤烧烩入味，装碗蒸5分钟，滗出原汤勾芡，浇在杂烩上即成。', NULL, NULL, NULL, '鸡肉、海参、熟白鸡肉、鱼肉、熟火腿、油发鱼肚、鱿鱼、水发腐竹、干贝、冬菇、玉兰片适量。');
INSERT INTO `recipe` VALUES (2609, 69, '莲蓬鱼', '1.鱼肉、肥膘内剁成泥，放在碗内，加入精盐4克、葱姜汁、绍酒和清水50克，搅拌上劲，再加鸡蛋清、味精和绿菜汁拌匀成馅。\r\n2.取大酒杯12只，杯内抹匀熟猪油，分别放入鱼肉馅并抹平，每个馅上插入花生米5粒，逐一做好后，连酒杯上笼蒸约5分钟至熟取出，脱出酒杯，码放在盘内。\r\n3.锅置中火上，放入鸡汤、精盐1克烧开，用湿淀粉调稀勾芡，淋入熟猪油，浇在上面即成。', '以鱼肉为莲房，花生米为莲实，象形制成的莲蓬鱼，是徽州名菜，形色逼真，鱼肉鲜软，花生香脆，别具一格。', '清水徐徐加入馅中，边加边搅，顺一个方向，使之上劲。花生米摆成梅花状，露尖少许，形似莲实。', NULL, '青鱼肉200克，味精0.5克，炒花生米75克，精盐0.5克，猪肥膘肉50克，绍酒5克，鸡蛋清2个，绿色菜叶汁3克，湿淀粉10克，鸡汤150克，熟猪油15克。');
INSERT INTO `recipe` VALUES (2610, 69, '蜜汁红芋', '1.选出橘黃色红芋洗净去皮，削成雨头尖的块； \r\n2.砂锅底部放竹箅加水、放冰糖放入火上熬化，再放入红芋、蜂蜜，烧开后移小火1小时； \r\n3.汤汁浓缩后，将红芋装盘，浇上原汁即成。', NULL, NULL, NULL, '红芋1000克，蜂蜜200克，冰糖125克。');
INSERT INTO `recipe` VALUES (2611, 69, '明珠甲鱼', '1.甲鱼去内脏，用开水略烫，去掉膜衣，剁成块。再入清水锅内烧开，烫3分钟捞起。\r\n2.炒锅置旺火，放入熟猪油，烧热后投入甲鱼，炸3分钟捞起沥油。\r\n3.原锅留底油少许，放入甲鱼、鸽蛋，加生痿、蒜、酱油、精盐、味精、绍酒，烧5分钟，用湿淀粉勾芡，淋入芝麻油，起锅将鳖肉装在盘中，鸽蛋围边，撒上火腿丝、葱段即成。', '明珠甲针是徽州名菜，用“生烧法”烹制而成。小马蹄鳖经沸水烫、过油、红烧，5-6分钟快速成熟，即生烧法。颜色酱褐，肉质细嫩，配以白色鸽蛋，故称明珠甲鱼。', '加工甲鱼，注意保持裙边完整。用湿淀粉勾厚芡，顶开冒泡，让淀糊熟透，则明汁亮芡。', NULL, '嫩甲鱼1000克，葱段5根，鸽蛋12个，酱油10克，熟火腿丝20克，冰糖10克，大蒜100克，湿淀粉5克，生姜10克，芝麻油25克，精盐15克，熟猪油100克，绍酒25克。');
INSERT INTO `recipe` VALUES (2612, 69, '明珠酥鲍', '1.水发鲍鱼两面刀剞成兰花刀，深度为鲍鱼的2/3换水焖至鲍鱼膨胀软嫩，用热水浸泡后，切成1厘米宽的长条，放碗内。鸽蛋煮熟去壳放碗内，加鸡蛋清100克、精盐0.5克浸泡入味。\r\n2.锅置火上放猪油，投入一半葱、姜炸出香味，加入鸡清汤烧几滚，捞去葱姜不用，汤倒碗内待用。再将锅中放入开水和葱姜、绍酒15克，投入鲍鱼烧一会，倒入漏勺沥水，拣掉葱姜，再用肉清汤套煮一至二次，锅内放入鲍鱼、绍酒10克，倒入碗中汤，加精盐4.5克，烧几滚移向小火烧透，再用旺火收浓汤汁，放入味精1克，用湿淀粉10克调匀勾芡，撒上胡椒粉，淋', '鲍鱼是海产软体动物，并非鱼类，有紫鲍、灰鲍等品种。其贝壳即中药材“石块明”，其肉鲜美，是海产品中之佼佼者，自古以来视为海味珍品。明珠酥鲍是以肉汤、鸡汤多次烧煮鲍鱼使其肉质鲜酥，在鲍鱼四周围上熟鸽收，色白形圆如珠，故名“明珠酥鲍”。', '干鲍胀发：干鲍用浊水浸泡12小时，换水反复搓洗，放铝锅中煮一昼夜，待其发软，即鲍鱼的边缘容易撕下时，用温水一遍，放入原汤中续煮，熟透为止，凉后不用，可入冰箱保鲜。', NULL, '水发紫鲍250克，绍酒25克，鸽蛋20个，胡椒粉1克，姜5克，湿淀粉15克，葱10克，鸡清汤500克，精盐5克，熟鸡油10克，味精1.5克，湿淀粉10克，味精1克，鸡汤250克，熟猪油50克。');
INSERT INTO `recipe` VALUES (2613, 69, '莫家干丝', '1.豆腐干切成丝，用沸水烫过，虾仁上浆。炒锅烧热放猪油至四成热下虾仁滑油，倒出沥油。\r\n2.锅里留少量余油，加汤、绍酒，再放下干丝，烧沸加盐、味精、绿叶菜、立即装盆，撒上火腿丝即成。', '汤清味醇，干丝软嫩，口味咸鲜。', NULL, NULL, '黄豆豆腐10块，虾仁75克，熟火腿15克，绿菜叶少许，骨汤500克，料酒10克，味精2克，盐5克，熟猪油25克。');
INSERT INTO `recipe` VALUES (2614, 69, '奶汁肥王鱼', '1.将鱼去鳃，剖腹去内脏，洗净后，用刀在鱼身两边直剞小柳叶刀花。猪瘦肉切成一寸长、半寸宽、一分厚鸡冠形的片。\r\n2.炒锅放在旺火上烧热，放入熟猪油，烧至七成热时，下热鸡清汤，烧沸后，再放入鱼、肉和葱、姜，盖上锅盖，将汤熬成奶汁，加入盐和白胡椒，出锅倒入汤碗。上桌时，随带香菜一小碟佐食。', '汤浓似奶，鱼肉肥嫩细腻，味道极鲜。', NULL, NULL, '肥王鱼1条（100克左右）、猪瘦肉50克、大葱白段10克、姜片10克、香菜5克、精盐5克、白胡椒1.5克、鸡清汤1000克、熟猪油100克。');
INSERT INTO `recipe` VALUES (2615, 69, '葡萄鱼', '1.鱼肉治净修成梯形，剞花刀至皮，放调料腌20分钟，粘一层蛋糊，再粘一层面包屑，下7成油锅中炸至金黄色，呈葡萄粒状时捞出装盘；\r\n2.菜叶焯水修成葡萄叶状，贴在鱼肉旁，用白糖、白醋、盐、葡萄汁勾芡淋上即成。', NULL, NULL, NULL, '带皮青鱼肉350克，青菜叶4片，鸡蛋一个，咸面包屑75克，葡萄汁100克。');
INSERT INTO `recipe` VALUES (2616, 69, '炝芹菜', '将鲜芹菜摘去叶和根洗净，直刀切成八分长段（粗根可劈两半），放进开水锅中汆熟捞出，用凉水冲冷控干，再将精盐、味精、陈醋拌匀盛盘，放上姜末，倒上加热的椒油炝味即可。', '营养丰富，扑鼻喷香。', NULL, NULL, '鲜嫩芹菜一斤五两、姜末二钱、精盐五钱、味精十粒、椒油五钱、陈醋二钱。');
INSERT INTO `recipe` VALUES (2617, 69, '瓤豆腐', '1.将鸡腿肉洗净，去皮去骨，粗斩成肉末，加盐、味精、姜末、虾仁，拌匀成馅料。将豆腐切成5厘米风方的12小块，入开水锅焯一下，使豆腐硬功夫结，在每块豆腐中间切开一个小洞，塞入肉馅成坯。 \r\n2.取鸡蛋清打成泡沫状，加绿豆粉拌成飞糊。 \r\n3.炒锅上旺火，下油烧至四五成热，将豆腐夹肉生坯分别滚上飞糊，下油锅炸至豆腐外表起软壳、呈金黄色，捞出装盘。 \r\n4.炒锅内留油少许，加糖、醋、绍酒、鲜汤烧开，用湿淀粉勾薄芡，淋麻油，浇在豆腐上即成。', '形似樱桃，色泽金黄，外脆里嫩，酸甜适口。', NULL, NULL, '洁白嫩豆腐500克，鸡腿肉100克，虾仁、绍酒巴、白糖各50克，姜末5克，味精1.5克，精盐4克，醋15克，豆油1000克（约耗100克），麻油少许，鲜汤150克，湿淀粉15克，鸡蛋3个，绿豆粉10克。');
INSERT INTO `recipe` VALUES (2618, 69, '什锦肉丁', '1.里脊肉切丁，加料酒、葱姜、胡椒粉、味精入味，下八成热油中滑熟捞出；\r\n2.锅留底油，下辣椒块煸炒出辣味，下肉丁、青豆、香菇丁翻炒几下勾薄芡出锅即可。', NULL, NULL, NULL, '猪肉脊肉150克，鲜辣椒50克，青豆50克，香菇50克。');
INSERT INTO `recipe` VALUES (2619, 69, '问政山笋', '1.将笋切成滚刀块，冬菇，香肠切片。\r\n2.将笋放入沙锅中，加火腿骨和水，旺火烧开后转小火炖15分钟。\r\n3.另取一沙锅，将炖好的笋倒入锅中，上火烧开，加入香肠片，冬菇片，白糖，精盐翻炒几下，待汤汁收浓，取出火腿骨，再用湿淀粉勾芡，起锅装盘即可。', NULL, NULL, NULL, '山笋500克，香肠，冬菇各50克，火腿骨1根。');
INSERT INTO `recipe` VALUES (2620, 69, '虾籽管廷', '1.先将管廷外面的大油疙瘩择去洗净，以筷子顶住稍细的一头，从另一头把管廷翻过来洗净，放入汤锅煮至八成熟捞出。然后在管廷上段隔0.3厘米剞一斜刀，再切成4厘米长的段，冬菇切成片。 \r\n2.锅置旺火上，放入熟猪油，烧至四成热时下管延，炸至刀花张开后，倒入漏勺沥油。 \r\n3.在原锅余油中下葱末、姜末、虾籽煸炒，待葱、姜煸出香味时，先烹料酒，再放笋片、冬菇片、管廷炒几下，加入鸡汤和酱油、盐，再翻炒几下，用湿淀粉调稀勾薄芡、淋上猪油10克，将锅颠两下，出锅装盘即可。', '管延，又名黄香管，是猪食管后面的一截，色奶黄、质脆因管内附着很多脂肪，故味道很香。虾籽系选用江淮所产的淡水虾籽。二者同烹，虾籽受热吐鲜，管廷散发脂香，两者互取其长，味极鲜香，是江淮名菜。', '剞管廷时，刀口的深度应为2/3，经加热后舒展美观。', NULL, '管廷12根，鸡汤100克，虾籽5克，酱油15克，水发冬菇3个，绍酒15克，熟笋片15克，湿淀粉10克，小葱末10克，熟猪油250克（约耗75克），姜末10克，精盐25克。');
INSERT INTO `recipe` VALUES (2621, 69, '香炸琵琶虾', '1.虾仁、猪肥膘肉、鸡脯肉斩成茸，加蛋清（1只）、生粉适量、盐、味精、胡椒粉、绍酒，搅拌均匀，倒入笋丝、冬菇丝，搅匀制成虾馅。取汤匙20个，抹少许熟油，把凤尾虾拍一拍放在汤匙里，虾尾露出匙把外，装上虾馅抹平，用大火蒸5分钟取出晾凉，脱出汤匙，放在碟中。蛋清、面粉、生粉、香油调制成酥糊。将蒸好的琵琶虾蘸上糊，粘上芝麻仁。 \r\n2.烧热锅，下油，至七成热时，将琵琶虾放入炸至外皮酥脆捞起装碟即可。', NULL, NULL, NULL, '虾仁125克，凤尾虾20只，鸡脯肉75克，猪肥膘肉35克，熟笋丝50克，冬菇丝5克，芝麻仁50克，鸡蛋清3只，面粉、绍酒各2茶匙，生粉、香油各适量。');
INSERT INTO `recipe` VALUES (2622, 69, '蟹连鱼肚', '1.将蟹洗净，蒸熟，冲洗一下，剔取蟹肉（蟹腿、蟹黄另放）。香菜切碎。\r\n2.鳜鱼肉剁成泥，加入葱、姜末、盐3克和200克清水，与蟹肉一起搅拌上劲成馅。\r\n3.油发鱼肚用温水泡软，放入温水中加碱5克，边洗边挤去浮油，再换温水洗二三下，洗净碱味后挤去水分，每块片成两片，切成4厘米左右菱角形块，平放在案板上，撒匀干淀粉。\r\n4.取鱼肉馅1份，放在鱼肚上，蟹腿、蟹黄放在馅上，在菱形的左右两个尖端上，各撒1小撮香菜末，再轻轻地按一下。如此将鱼肚块逐一做好装盘，上笼用旺火蒸5分钟左右取出。\r\n5.锅放旺火上，将蒸鱼肚', '长江安徽河段产鲴鱼，肉味鲜美，其鳔肥厚，干制成鱼肚，色淡黄，胶厚，韧性大，为名贵水产原料。配以安徽名产大闸白蟹，集河鲜之珍，鱼肚上置蟹肉、蟹黄，并加配料组合成黄、白、桔红、黑、绿五色。鱼肚吸蟹鱼之味，非常鲜美，是沿江传统名菜。', '须选用鲴鱼鱼肚和安徽大闸蟹，方显徽味特色。俗云：“蟹肉上席百味淡”，此菜鲜冠天下。', NULL, '活蟹750克，酱油5克，鳜鱼肉200克，白胡椒粉5克，油发鲴鱼肚50克，食碱5克，小葱末5克，干淀粉10克，姜末5克，湿淀粉3克，香菜50克，鸡汤100克，精盐5克，熟猪油15克。');
INSERT INTO `recipe` VALUES (2623, 69, '油酥烧饼', '1.取面粉，精盐倒入盆中，和成面团，反复搓揉，揉至面团光滑柔韧，分成若干份，烙制成烧饼。\r\n2.将生油条放入滚烫的油锅中，炸至金黄色时，即可捞出，夹在烧饼中即可食用。', NULL, NULL, NULL, '面粉500克，精盐10克，凉水300毫升，油1000克。');
INSERT INTO `recipe` VALUES (2624, 69, '玉板蟹', '1.将鸡蛋下冷水锅煮熟捞起，放冷水中略浸剥壳，每个鸡蛋切成4块，去蛋黄放碟内摆好。青菜摘洗干净，沥水，将鱼肉剁成泥置碗中，加姜汁、盐、绍酒1茶匙和适量水拌匀成馅料。蟹肉放碗内，加盐和绍酒1茶匙调好味。 \r\n2.在蛋白上碟上干淀粉，每个放鱼肉馅1份，再放上蟹肉一份在碟内排放好。碟中间的1份放上一点蟹黄，其余的上面分别放葱叶段、香菇片、火腿片，连碟上笼蒸熟取出。 \r\n3.炒锅置中火上，放入熟猪油（35克）烧至六成热，下青菜煸炒，加适量盐炒熟放在碟内滗去汁水，将蒸好的蟹移至菜碟中。炒锅中放入上汤加适量盐，用生粉', NULL, NULL, NULL, '蟹肉50克，鸡蛋5只，鱼肉50克，青菜250克，水发香菇、熟火腿片、小葱叶段各5克，绍酒2茶匙，姜汁、生粉各适量，上汤100克，熟猪油50克。');
INSERT INTO `recipe` VALUES (2625, 69, '玉兔海参', '1.水发海参每根一剖为二洗净放碗中，加入鸡汤200克、鸡油15克、绍酒10克、精盐1克、葱结5克、姜片5克、上笼蒸20分钟，取出海参，汤汁留用。鸡脯肉、鱼肉、肥膘肉剁成茸泥，冬菇切成末，放入碗中，加精盐1克、味精0.2克、胡椒 粉、绍酒、葱姜汁、干淀粉5克，搅拌成三鲜馅心。 \r\n2.海参酿上三鲜馅心，鸡蛋清放入碗内用筷子搅打成泡沫状，加入味精0.3克、盐0.5克、干淀粉搅拌成蛋泡糊。取汤匙12把抹上冷猪油，将剩下的三钱馅心分别抹在汤匙里，再将蛋泡糊堆抹在上面成兔状，连骨上笼蒸5分钟取出。同时将白菜用开水烫', '此菜用三鲜馅心加蛋泡糊做成一个个小白兔，它们伏在青菜心上对着盘中的海参“出神”。选型优美，色调素雅，令人心旷神怡。海参柔糯鲜醇，并瓤有三鲜馅心，锦上添花。', '蛋泡糊，容器洁净，无油、盐、碱及生水，用抽子顺一个方向搅打，中途勿停，一气呵成。徽菜强调工艺象形，如花蕊海参、蝴蝶海参等，小兔要求生动活泼，形神皆似。', NULL, '水发海参400克，水发冬菇25克，生鸡脯肉100克，鸡蛋清5个，鱼肉50克，小白菜心12棵，猪肥膘肉25克，葱结15克，姜片15克，干淀粉10克， 精盐5克，湿淀粉10克，味精1克，鸡汤250克，绍酒20克，熟鸡油25克，胡椒粉0.5克，熟猪油25克。');
INSERT INTO `recipe` VALUES (2626, 69, '掌上明珠', '1.将鸭掌加温开水浸泡，剥去外衣，如鸭掌上有黑或黄的斑点、应用刀刮去洗净后入开水锅中略焯取出再用清水洗净，去除异味。再放入鸡汤锅煮酥取出。冷却后，拆去鸭掌骨。\r\n2.虾仁清水洗净沥干，放在干净的砧板上，用刀斩成虾茸，放入碗中加绍酒、味精、精盐、干淀粉拌和上劲成虾胶。鸽蛋入冷水锅中煮熟，取出经冷水浸一下，剥去蛋壳。将鸭掌放在盘里，涂上虾胶，然后嵌上一只鸽蛋，上笼旺火蒸5分钟左右，至虾胶成熟取出放在盘中。\r\n3.炒锅洗净，加鸡汤烧开后，加细盐、味精，用湿淀粉勾成薄芡，淋上熟猪油，起锅，均匀地浇在10只鸭掌上，', '鸽蛋色白嫩糯，虾胶色红肥鲜，鸭掌脆嫩爽口。', NULL, NULL, '大鸭掌10只、鸽蛋10只，虾仁100克、绍酒5克、细盐20克、味精1克、清鸡汤150克、熟猪油25克、干淀粉5克、湿淀粉15克。');
INSERT INTO `recipe` VALUES (2627, 69, '朱洪武豆腐', '1.将肥瘦猪肉发成绿豆大的末。虾仁沥干水，加精盐1克、湿淀粉5克浆匀后剁碎。炒锅置火上烧热，先用油将锅滑一下倒出，放入葱姜末各5克、肉末、虾仁下锅煸炒至松散，烹入绍酒，加肉清汤、味精、精盐炒和后，用湿淀粉5克调匀勾芡成馅，盛入盘内稍凉待用。\r\n2.将豆腐去皮用刀修整齐，切成4厘米长、2厘米宽的片，再片成0.1厘米厚的片，共24片，平放在盘内，将虾、肉馅心分成12份，放在豆腐片上拌匀，分别盖上一片豆腐制成的豆腐生坯。将鸡蛋清放碗内搅打成泡沫状，加入干淀粉调匀成糊。 \r\n3.锅置旺火上，放入熟猪油烧至五成热，', '此菜是豆腐夹馅，先炸后火靠，质地软嫩，颜色奶黄，咸鲜可口。', '此菜以炸、火靠两种不同的烹制方法加工制成。八公山所产豆腐，非常鲜嫩，烹制时动作要轻，切不可将豆腐碰碎。', NULL, '嫩豆腐500克（实用250克），醋5克，白糖3克，肥瘦猪肉100克，绍酒5克，虾仁25克，味精0.5克，鸡蛋4个，干淀粉25克，葱末10克，湿淀粉35克，精盐5克，熟猪油1000克，肉汤150克（约耗75克）。');
INSERT INTO `recipe` VALUES (2628, 70, '八宝饭', '1.将糯米蒸熟后，与白糖、猪油和开水和匀。\r\n2.蜜枣等放入碗内时，排列成图案，中间放豆沙及桂花，再铺上糯米饭，塌平，上笼蒸一小时，食时铲出。', NULL, NULL, NULL, '糯米、白糖、猪油、开水、豆沙馅、桂花、蜜枣。');
INSERT INTO `recipe` VALUES (2629, 70, '扒酿海参', '1.海参洗净，用开水焯后内粘面粉。鸡脯肉剁成茸状加入盐、葱末、姜、熟猪油、蛋清、料酒搅匀，分装在粘好面粉的海参内膛。\r\n2.将酿好的海参用锅稍蒸使之定型（时间不宜过长，以免鸡茸蒸老）。\r\n3.炒锅上火放油烧热，放葱末煸出香味，放酱油、料酒、姜汁、味精、鸡汤、白糖烧开，放入海参，微火煮熟，淋水淀粉勾芡，淋葱姜油即可出勺。', '色泽红润油亮，质地鲜嫩，口味鲜咸。', NULL, NULL, '水发海参500克，鸡脯肉150克，熟猪油25克，葱姜油50克，料酒15克，姜至15克，味精4克，盐3克，蛋清2个，葱末5克，酱油10克，白糖5克，及糖、水淀粉、面粉。');
INSERT INTO `recipe` VALUES (2630, 70, '拔丝鸡盒', '1.鸡脯肉洗净切成直径3厘米的圆片2片（每两片相连），粘上面粉。\r\n2.果脯洗净剁成馅约分16份，用手搓成小球，樱桃切丁。\r\n3.发面加水调匀，使用前要加适量碱调成发面糊。\r\n4.炒勺上火放油烧至5-6成热，将鸡片中间加入果脯球成鸡盒状，裹上发面糊，逐一投入油锅内，满火炸至鸡盒漂浮、成金黄色捞出控油。\r\n5.勺内放白糖、水，加热将糖熬至浅黄色时投入炸好的鸡盒颠炒，使糖汁均匀的裹在鸡盒上即可出勺，再撒上红、绿樱桃丁即成。', '色泽美观，香甜适口。', NULL, NULL, '鸡脯肉300克，油75克，北京果脯100克，白糖100克，红绿樱桃6克，面粉、发面、碱各适量。');
INSERT INTO `recipe` VALUES (2631, 70, '拔丝莲子', '1.将发好的莲子洗净，粘上干淀粉。\r\n2.炒勺放油，烧至5-6成热，将粘好淀粉的莲子放入锅内，炸至金黄色捞出、控油。\r\n3.炒勺刷洗干净放入白糖，注入温水加热，糖熬至浅黄色时放入炸好的莲子，颠翻几下将糖汁均匀裹在莲子上出勺装在抹过熟油的盘子里，再将切好的红绿樱桃丁洒在盘中即可。', '酥脆，香甜。', NULL, NULL, '水发莲子200克，油50克，白糖50克，红绿樱桃、面粉、干淀粉各适量。');
INSERT INTO `recipe` VALUES (2632, 70, '拔丝山药', '1.山药刮去皮切滚刀块；冰糖碾碎成面儿；菜盘中涂上少许油。 \r\n2.勺内注入油烧至五成热，放入山药至金黄皮脆，倒入漏勺内。勺内留油少许，放入冰糖面和一调匙清水，加桂花卤熬糖，待糖汁表面的大气泡变小，糖开始变微微有点浅红色时，马上将炸过的山药倒入勺中搅动，用糖汁将山药包匀，倒入涂油的盘中，迅速上席即可。另上凉开水一小碗。', NULL, '京人喜做俏活。冰糖葫芦手艺在于熬糖，糖脆而不粘牙，行家叫糖活。拔丝属于这熬糖的手艺。火小，不是拔不出丝来就是糖呈沙粒；火大，糊了，成糖色了，更甭提拔丝了。', NULL, '山药500克，桂花卤2克，冰糖75克，熟白芝麻5克，熟花生油1000克。');
INSERT INTO `recipe` VALUES (2633, 70, '爆炒虾仁', '1.虾仁里，加鸡蛋、淀粉、面，入味挂糊，黄瓜切丁。\r\n2.油五成熟时，将虾仁炒一下，倒出。原锅炒葱，烹料酒，加调料，倒入虾、黄瓜丁，淋香油，出锅。', NULL, NULL, NULL, '虾仁300克，油1000克，料酒20克，醋10克，盐、味精、葱花适量，鸡蛋、水淀粉25克，面少许，香油10克，黄瓜80克，汤200克。');
INSERT INTO `recipe` VALUES (2634, 70, '北京烤鸭', '1.鸭去双掌、鸭舌，把空气充入皮下。\r\n2.取出内脏，用高粱杆支撑于脊骨和胸脯三岔骨之间；\r\n3.由刀口处灌水，从肛门处放出，反复清洗干净为止；\r\n4.在颈上将铁钩斜穿于颈上，将鸭挂起；\r\n5.用开水浇烫皮，使皮肤光滑紧绷；\r\n6.用糖水淋鸭身；\r\n7.将鸭子吹干；\r\n8.在鸭肛门做堵塞。灌水八成满的开水，鸭入挂炉，烤至上色成熟时出炉；\r\n9.将鸭片成片，食用时同葱白段、甜面酱各少许卷入鸭饼即可。', NULL, NULL, NULL, '北京鸭1只，糖水，葱，甜面酱，鸭饼。');
INSERT INTO `recipe` VALUES (2635, 70, '北京烙饼', '1.面粉加开水250克揉合揉透后，摊开冷却，然后搓成长条，揿扁，擀成长方形薄皮坯子；\r\n2.在皮子上抹些麻油后卷起来，切成小坯子，再将小坯子揿扁，擀成圆饼待用；\r\n3.平锅上火，在饼上刷油，放入锅内用小火烤烙至两面呈金黄色时即可。', NULL, NULL, NULL, '面粉500克，麻油100克。');
INSERT INTO `recipe` VALUES (2636, 70, '北京泡菜', '将大白菜择洗干净，控去水分，剥下外层的菜帮，劈成两半。菜心从头部劈成四半。再改刀切成约5厘米长。将苹果、梨一起放入搪瓷盆内，下入葱、蒜、盐、味精、辣椒末搅拌均匀，倒入牛肉汤。在原料上面压上一块干净的大石头或一个盘子，然后放在温度较高的地方（约40度左右），五天即可食用。', '此菜为黄白色，清淡鲜香，酸辣咸甜适中，加入水果的香味，直切与众不同，是北京人冬季家家都制的一道家常菜。', '制作此菜时，所用的容器一定要清洁，以免泡制时间稍长，产生大量细菌，造成疾病。', NULL, '大白菜1000克，鸭梨50克，白萝卜50克，味精10克，胡萝卜50克，葱50克，辣椒末20克，蒜50克，牛肉汤50克，盐35克，苹果50克。');
INSERT INTO `recipe` VALUES (2637, 70, '炒包心菜', '1.将包心菜切成丝或块，加少许盐暴腌，姜、葱切末，青椒、红椒切丝；\r\n2.将锅烧热，加油，投入葱、姜末煸透，再下青、红椒丝稍炒，随即将包心菜挤掉水，入锅略煸后加白酱油、糖、醋，继续煸烧至熟，放少许味精、麻油即可。', NULL, NULL, NULL, '包心菜500克，姜、葱、盐、青椒、红椒、白酱油、糖、醋、味精、麻油各适量。');
INSERT INTO `recipe` VALUES (2638, 70, '炒四季豆', '1.先将四季豆去筋，切成段。葱、姜切末；\r\n2.先将肉馅煸炒，放入葱姜及料酒；倒入四季豆煸炒；加入味精、白糖、鸡汤、盐，大火烹炒，待汁收干，淋入香油，即完成。', NULL, NULL, NULL, '四季豆300克，肉馅80克，盐、味精、鸡汤、葱姜、料酒、油适量。');
INSERT INTO `recipe` VALUES (2639, 70, '春饼卷菜', '1.将圆白菜、洋葱、火腿、荸荠切片，并用沸水将圆白菜、洋葱烫一下捞出沥干水，然后放入火腿、香油、醋、盐拌匀。 \r\n2.将面粉用水调匀，烙制成薄面饼。 \r\n3.将拌匀的调料放在薄面饼上卷起即可食用。', '北京夏季家常主食。以面粉烙制的荷叶饼包卷家常炒菜，即可食用。其特点是：制法简单，风味独特，食用方便。可根据原料和炒制工艺的变化制成各种不同口味。', '黄花菜即称金针菜、金菜、南菜或西菜，是用黄花的花蕾干制而成，其味香、鲜、嫩、糯，维生素和矿物质含量丰富，一向被视为珍贵的素菜。黄花菜的产地很广，以湖南产量最多，江苏质量最好。', NULL, '面粉100克，水30克，圆白菜、火腿各50克，洋葱、荸荠25克，盐2克，醋1克，姜片15克，香油50克。');
INSERT INTO `recipe` VALUES (2640, 70, '葱扒鸭子', '1.将鸭去掉内脏，彻底清洗干净放入沸水煮至八成熟后去掉骨头；\r\n2.把大葱切成3厘米长段，放入沸猪油炸至棕色，将葱取出和鸭子一同放入大碗中，加入料酒、味精、精盐、汤，将鸭子蒸熟； 将一勺汤烧开后加入味精、料酒、精盐和酱油，用玉米淀粉调稠； \r\n3.将蒸好鸭子扣入大盘中，浇汁即可。', '以过油葱段与整鸭上锅蒸制而成，成菜金黄，鸭肉酥嫩，味道鲜美。', NULL, NULL, '填鸭1000克，大葱、料酒各10克，味精1克，玉米淀粉30克，味精5克，酱油20克，姜15克，油菜汤200克，猪油150克。');
INSERT INTO `recipe` VALUES (2641, 70, '葱爆鸡丁', '1.将鸡肉切成小丁，撒上精盐腌一下，再加鸡蛋白及干淀粉调拌均匀，连同笋丁放入旺火热猪油锅内炒10多分钟，沥去油。\r\n2.将葱切成丝，将酒、酱油、葱丝、姜、湿淀粉倒入锅内炒拌几下即可。', NULL, NULL, NULL, '鸡腿肉150克，笋50克，干淀粉6克，鸡蛋清20克，白糖、黄酒、精盐、葱、姜、湿淀粉、酱油适量。');
INSERT INTO `recipe` VALUES (2642, 70, '葱爆肉片', '1.将净肉切片，用面酱和少许熟油拌好，葱白切斜粗条。\r\n2.炒锅内加底油用旺火烧热，将肉片下锅炒散，待有六成熟时将葱条、姜丝入锅急炒几下，依次加入香醋、酱油、花椒面、味精、麻油，颠炒翻身，待葱香脆即可。', NULL, NULL, NULL, '猪肉200克，葱白50克，素油75克，麻油、面酱、味精、香醋、酱油、花椒面、白糖、姜丝适量。');
INSERT INTO `recipe` VALUES (2643, 70, '葱爆羊肉', '1.羊腿肉去筋，切成大薄片；京葱切成旋刀块，爆炒后就会变成片。\r\n2.将京葱块、生油（或猪油）、酱油、盐、酒、花椒粉、羊腿片拌和在碗里。\r\n3.用生油、麻油、大蒜头（折碎）炝锅烧至高热时，将放在碗里的羊腿片、京葱等材料倒入，用大武火很快地爆炸几下，再加少许麻油，醋起锅。', '香而又嫩，无羊膻气。', NULL, NULL, '羊腿肉200克、京葱200克、大蒜头1瓣、花椒粉少许、黄酒20克、酱油50克、盐少许、醋少许、生油少许、麻油50克。');
INSERT INTO `recipe` VALUES (2644, 70, '葱烧海参', '1.将水发嫩小海参洗净，整个放入凉水锅中，用旺火烧开，约煮5分钟捞出，沥净水。把大葱分别切成长5厘米的段（100克）和末（5克）。青蒜切成长3.3厘米的段。 \r\n2.将炒锅置于旺火上，倒入熟猪油，烧到八成热时下入葱段，炸成金黄色时炒锅离火，葱段端在碗中，加入鸡汤（50克）、绍酒（5克）、姜汁（2.5克）、酱油（2.5克）、白糖（2.5克）和味精（1克），上屉用旺火蒸1至2分钟取出，滗去汤汁，留下葱段备用。 \r\n3.将炒锅置于旺火上，倒入熟猪油（25克），烧到八成热时，下入白糖（25克），炒成金黄色，再下入', '大葱烧海参，原为山东名肴，葱香浓郁，风味独特。传入北京后，北京的厨师们根据本地人口味的嗜好，改成了小汁小芡，用油量减少，口味却更加清新、醇和。此菜北京丰泽园饭庄制得最好。海参入味，柔软滑嫩，清鲜可口，食后盘无余汁。', '糊葱油的制法：将熟猪油500克放入炒锅内，烧到八成热时下入葱段100克、姜片75克、蒜片50克，炸成金黄色，再下入香菜段100克，炸焦后，将以上原料捞出，余油即为糊葱油。芡汁下锅，不要马上搅动，稍等三四秒钟，再将其搅匀，淀粉糊化，使之明汁亮芡。', NULL, '水发小海参1000克，精盐2克，大葱105克，味精3.5克，青蒜15克，湿淀粉10克，姜末5克，鸡汤200克，姜汁27.5克，糊葱油50克，白糖27.5克，熟猪油150克，酱油12.5克（约耗75克），绍酒15克。');
INSERT INTO `recipe` VALUES (2645, 70, '醋椒鱼', '1.将活鳜鱼一尾（约重90克）去鳞、鳃、鳍，开膛去内脏，洗净后，用开水烫一下，再用凉水洗一遍，刮去鱼身外面的黑衣，然后，在鱼身的两面剞上花纹：一面剞成十字花刀（即先坡着刀在鱼体上每隔1.65厘米宽切入1刀，深及鱼骨，再直着刀在已切的刀口上交叉切，即成十字花刀）；另一面则剞成一字刀（即直着刀生隔1.65厘米宽横切1刀，深及鱼骨）。香菜洗净清毒，切成长2厘米的段。葱一半切成长3.3厘米的细丝，一半切成末。 \r\n2.将熟猪油倒入炒锅里，置于旺火上烧热，依次放入胡椒粉、葱末和姜末，煸出香味后，倒入鸡汤，下入姜汁、', NULL, '普通宴席可选用草鱼，制作方法同上，鲤鱼上腥味大，最好不用。', NULL, '活鳜鱼1尾，姜末5克，香菜10克，白胡椒粉2.5克，鸡汤1000克，熟猪油50克，葱10克，醋50克，绍酒10克，味精2.5克，姜汁5克，芝麻油10克，精盐3.5克。');
INSERT INTO `recipe` VALUES (2646, 70, '番茄牛肉', '1.牛肉切成6.5厘米长、4.5厘米宽、0.3厘米厚的片，放在碗内，加精盐、绍酒、姜片、葱段拌匀，腌渍约半小时，拣去葱、姜。干辣椒切成2.5厘米长的段。蕃茄改成2厘米的小片；\r\n2.炒锅上火，舀入菜油烧至七成熟，放入牛肉片炸至棕褐色，捞出沥油。锅内留油烧至四成热，下干辣椒、花椒炸香成棕红色，下蕃茄炒出香味，加入鲜汤，放入牛肉、精盐、酱油烧沸，用旺火收稠卤汁，加入酒酿、白糖、味精、麻油即可。', NULL, NULL, NULL, '牛腿肉500克，熟菜油500克，蕃茄15克，干辣椒20克，花椒3克，姜片15克，白糖10克，酱油20克，精盐7.5克，甜酒酿50克，鲜汤250克，葱段10克，麻油10克，绍酒5克。');
INSERT INTO `recipe` VALUES (2647, 70, '蛤蟆鲍鱼', '1.鳜鱼去骨制茸。 \r\n2.油菜切丝过油。 \r\n3.将鲍鱼在鸡汤鸭汤中煨5分钟后捞出，从底部肉边开口出向两边撕至一半，即“蛤蟆前腿”；在头部平片一口，填满鱼茸，即“蛤蟆嘴”；鱼茸上嵌两粒豌豆即“蛤蟆眼”。 旺火蒸5分钟取出装盘，将鸡鸭汤加调料，烧开勾芡，淋在蛤蟆鲍鱼上即成。', NULL, NULL, NULL, '罐头鲍鱼12个，鳜鱼肉100克，豌豆24粒，油菜叶250克，鸡蛋清4个，鸡鸭汤200克。');
INSERT INTO `recipe` VALUES (2648, 70, '锅烧鸡', '1.将鸡收拾干净，放锅中加水，加入肉料煮至7-8成熟捞出，拆去骨头装在盘内，加入姜汁、味精、料酒，上锅蒸熟，两面粘面粉。\r\n2.鸡蛋打碎加入盐、淀粉、料酒调成蛋糊。\r\n3.将滚粘好面粉的鸡肉挂蛋糊。\r\n4.炒勺放油上火烧至5-6成热时，将挂好蛋糊的鸡肉炸至金黄色，捞出控油装盘，将黄瓜、西红柿、香菜洗净改刀成不同的花形围在四周。跟花椒盐味碟上桌。', '色泽金黄，外焦里嫩，鲜香适口。', NULL, NULL, '嫩鸡1只（重约750克），油100克，鸡蛋4个，黄瓜25克，西红柿50克，香菜25克，花椒盐适量，料酒10克，姜汁8克，盐、味精、面粉、淀粉、肉料各适量。');
INSERT INTO `recipe` VALUES (2649, 70, '果料鱼骨', '1.将鱼骨用凉水浸泡6小时，用锅煮，开锅后改微火再煮一小时，捞出，换汤，加入葱、姜上锅蒸，蒸至用手能捏碎时捞出。\r\n2.鱼骨切成2厘米的丁，后用开水焯一下。果料除金糕外均用开水洗净切成丁。\r\n3.锅上火加清水，放适量的糖熬开，放入鱼骨熬两分钟，用水淀粉勾薄芡，撒上果料、麻仁即成。', '色泽洁白，式样美观，味道香甜。', NULL, NULL, '发好的鱼骨300克，北京果脯（蜜枣、金糕、青梅、瓜条、杏仁）150克，白糖75克，麻仁10克，葱、姜、水淀粉各适量。');
INSERT INTO `recipe` VALUES (2650, 70, '金丝海蟹', '1.海蟹洗净，去掉螯脚、盖等不可食用部位，切成4块，加料酒、葱、姜、味精、盐略腌后，挑出葱、姜，粘上淀粉。\r\n2.锅上火，放油，将搅匀的鸡蛋汁用温油炸至金丝状控油。\r\n3.锅留底油烧6-7成热，投入蟹块炸至金黄色捞出，码盘中。\r\n4.将炸好的金丝撒放在盘内即可。', '外形美观，色泽金黄，蟹肉酥香。', NULL, NULL, '海螃蟹1500克，油125克，鸡蛋100克，盐10克，料酒10克，味精4克，葱段、姜片各25克，姜末5克，淀粉、醋、香油少许');
INSERT INTO `recipe` VALUES (2651, 70, '金针鸡丝', '1.将鸡肉切丝，用蛋清抓匀，再加入团粉拌好，金针菇用热水氽一下；\r\n2.炒匀上旺火，下大油，烧四成熟时，下鸡丝，用筷子拨，滑透倒出，滗油；\r\n3.炒勺加旺火，下底油，先下葱丝，再下金针菇，放料酒、味精、盐、毛姜水少许，速将鸡丝投入，急炒，加青蒜段即可。', NULL, NULL, NULL, '生鸡脯肉150克，金针菇75克，大油500克，料酒10克，盐2克，味精2克，水团粉25克，葱丝、青蒜段、毛姜水少许，蛋清一个，胡萝卜丝适量。');
INSERT INTO `recipe` VALUES (2652, 70, '京酱肉丝', '1.将猪肉切成丝，放入碗内，加料酒、盐、鸡蛋、淀粉抓匀，即为上浆；\r\n2.将葱白斜切成丝放在盘中。姜切片略拍，取葱丝同放一碗内，加清水，泡成葱、姜水；\r\n3.炒锅上火，加油，烧热后将肉丝放入炒散，至熟时取出，放在盘中滤干油分；\r\n4.炒锅上火放油，加入甜面酱略炒，放入葱、姜水、料酒、味精、白糖，不停炒动甜面酱，待白糖全部熔化，且酱汁开始变粘时，将肉丝放入，不停地炒动，使甜面酱均匀地沾在肉丝上；\r\n5.肉丝放在盛有葱丝的盘中，将葱丝基本盖住，食用时拌匀即可。', NULL, NULL, NULL, '甜面酱80克，料酒5克，味精2克，白糖20克，盐1克，淀粉2克，鸡蛋1个，油150克，姜5克。');
INSERT INTO `recipe` VALUES (2653, 70, '京味荞麦面条', '1.先将浇汁中的高汤烧热，然后加酱油、甜料酒烧沸，撤火冷却； \r\n2.胡萝卜切成薄片，雕成花瓣形，加进少许汤汁、盐、糖，预先煮熟； \r\n3.鸡蛋打散，用糖和盐调味，煎锅中放入油，将蛋液摊成饼，卷成卷，切成圆片； \r\n4.萝卜擦成萝卜泥，轻轻挤去水分，与丛生口蘑拌成萝卜口蘑馅； \r\n5.豆苗用沸水焯一下捞出； \r\n6.荞麦面条煮熟后，用凉水过一遍，沥去水分，盛入器皿中，多盛一些萝卜口蘑馅撒在荞麦面条上面，再配上胡萝卜花瓣、鸡蛋卷和豆苗，最后浇上汁即可。', '适合糖尿病人食用。', NULL, NULL, '荞麦面条、鸡蛋、糖、盐、油、胡萝卜、丛生口蘑（罐头）、萝卜、豆苗、浇汁（高汤、酱油、甜料酒）。');
INSERT INTO `recipe` VALUES (2654, 70, '高力豆沙', '1.将豆沙炒好，搓成像小核桃那样的小圆球；鸡蛋白打成雪堆状（要能立而不塌），再将面粉、菱粉放入鸡蛋白里调成稠糊。\r\n2.以鸡蛋糊作为外皮，豆沙为馅心，包成一只只像算盘珠子那样的小丸子，放入清热油锅内炸成金黄色即好。上席时，另跟白糖，由食用者蘸着吃。', '外形如硬球，入口则柔软如绵，十分香嫩。', NULL, NULL, '豆沙150克、鸡蛋白5只、面粉50克、菱粉50克。');
INSERT INTO `recipe` VALUES (2655, 70, '锅烘菜合', '1.将猪肉去皮去筋后，与虾仁分别斩成泥，地栗去皮折碎，再斩成泥。然后将以上三种用料都放在碗里，加盐、味精和少许猪油调和。\r\n2.将菜叶洗净，用开水烫一下，平铺在砧板上，将调好的猪肉、虾仁、地栗泥均匀地铺在菜叶上面，揿成方形，肉泥上再盖菜叶，并轻轻地将四边揿平服，理清爽，切成16至20块的小方块，即称菜合。然后用刀将菜合托放在盘里，排成行。\r\n3.用鸡蛋一只打散在碗里，加面粉搅拌成稠糊，然后再打一只鸡蛋进去，继续搅拌成不厚不稀的黄糊。\r\n4.用净锅，下猪油500克烧热（不要烧太热，太热容易烘焦；不热则糊会黏', '色金黄，味清香，表面看不见有菜叶，吃起来却有菜味。', NULL, NULL, '猪肉175克、虾仁75克、地栗1只、菜叶4片、鸡蛋2只、黄酒12.5克、盐6克、面粉少许、味精少许、鸡油少许、清汤少许。');
INSERT INTO `recipe` VALUES (2656, 70, '锅溻桂鱼', '1.桂鱼肉2片(每片约120克左右)，用刀拍松，两面涂上酱油，沾上一层面粉。将鸡蛋打入碗内搅拌，把鱼片放入，使之沾满鸡蛋汁。 \r\n2.在炒勺内倒入熟大油50克，放入葱、姜、花椒，在文火上炸焦后捞出来，所剩下的油为煳葱油待用。 \r\n3.炒勺内倒入熟大油，在旺火上烧到8成热，放入鱼片，炸30秒钟左右，见显金黄色时捞出来。 \r\n4.把炒勺再放在旺火上，倒入鸡汤，加入料酒、盐、味精、酱油、姜汁和炸好的鱼片，盖上盖，汤汁烧开后，再移到文火上烤，把汤烤干后，满上已炸好的煳葱油即成。', '颜色金黄滑润，鱼片鲜嫩醇香。', NULL, NULL, '桂鱼肉250克，鸡蛋1个，熟大油300克（实耗约30-40克），鸡汤220克，面粉4克，料酒6克，姜汁8克，盐少许，味精3克，酱油8克，花椒2克，葱6克，姜5克。');
INSERT INTO `recipe` VALUES (2657, 70, '海红鱼唇', '1.水发鱼唇用凉水冼净，切成长10厘米、宽1.65厘米的条，放入开水锅里氽一下，初步去掉腥味。捞出后检查一下，把比较软的挑出来，放在碗中待用；把比较硬的放在另一碗中，加入绍酒5克、精盐1克、味精2.5克、葱段、姜块和鸡鸭汤300克。上屉用旺火蒸约10分钟，直蒸到鱼唇象豆腐一样柔软时取出。然后，将以上两碗鱼唇合在一起，滗去原汤待用。 \r\n2.将胡萝卜洗净削去皮，切成细丝。把熟猪油倒入炒锅中，用旺火烧至三四成热，放入胡萝卜丝，立即改用微火炸10分钟。待猪油变成红色后，捞出胡萝卜丝，留红油待用。 \r\n3.将鸡鸭', '鱼唇：用梨头鳐和鲨鱼等鱼的上唇加工而成。梨头鳐的唇最好，呈“△”，色近白，有光泽，透明度大，质量好。广东的湛江、汕头、福建宁德、山东沿海、海南岛、浙江舟山、温州等地均有产。此菜蟹黄调味，胡萝卜取色，汁浓味厚、色泽红润、柔软有劲，蟹味浓郁，适于秋季蟹肥时制作。', '鱼唇蒸不要过久，防止将鱼唇蒸化。煸炒胡萝卜丝要用小火，应避免胡萝卜丝炸糊，油色不能变红。淀粉勾芡后的浓度以能挂住手勺为度。', NULL, '水发鱼唇750克、精盐25克、蟹黄125克、葱段10克、红胡萝卜100克、姜块5克、湿淀粉15克、鸡鸭汤600克、绍酒20克、熟猪油25克、味精7.5克。');
INSERT INTO `recipe` VALUES (2658, 70, '合菜盖被', '1.猪肉切成3厘米长的帘子棍丝，豆芽菜去两头洗净，菠菜择好洗净切寸段，粉丝煮熟码在盘中。\r\n2.起锅放底油烧热，投入肉丝煸炒，放入葱丝炒出香味，放入豆芽菜旺火烹炒，倒入酱油、盐、料酒、姜汁、味精，放菠菜翻炒，淋少许明油出锅，盛在盘中的粉丝上。\r\n3.另起锅放油将鸡蛋煎熟，出锅盖在盘中菜上即可。', '造型美观，多料多味。', NULL, NULL, '鸡蛋汁150克，猪肉150克，豆芽菜150克，菠菜100克，泡好的粉丝100克，盐3克，料酒5克，姜汁5克，味精3克，葱丝5克，将油10克，油125克。');
INSERT INTO `recipe` VALUES (2659, 70, '核桃酪', '1.将核桃仁用开水浸泡后剥去外皮，再用凉水洗净。红枣放在开水锅中煮到膨胀时捞出，去皮、去核。粳米淘净，用温开水泡2小时。 \r\n2.将核桃仁和红枣一起剁成碎末，加入泡好的粳米和清水200克，搅成粥状，再用磨磨成极细的核桃浆。 \r\n3.将核桃浆放入铜锅里，加入白糖和清水500克搅匀，放在火口上，用小勺不断推搅，待浆烧开即成。', '此菜呈浆状，色浅灰而略红，细腻香甜，润滋不糊口。', '核桃浆放置时间不要长，否则变酸。忌用铁器，容易变黑。', NULL, '核桃仁200克，红枣50克，粳米50克，白糖200克。');
INSERT INTO `recipe` VALUES (2660, 70, '红烧丸子', '1.肉末加入料酒、姜（切末）、味精、酱油、盐、鸡蛋、淀粉搅拌均匀（可加入少量高汤）。\r\n2.炒勺放油烧至5-6成热时将肉末挤成扁圆形丸子（8个）投入锅中略炸至皮硬，捞出。\r\n3.锅底留油，放入大料炒出香味，加入酱油、料酒、高汤、桂皮，投入炸好的丸子烧滚，去浮沫，置微火炖熟，出锅装入盘中。锅内汤汁烧开，用水淀粉勾芡，淋明油，浇在丸子上即成。', '色泽浅红，质地松软，口味咸香。', NULL, NULL, '肥瘦猪肉末500克，料酒100克，姜50克，葱50克，盐2克，味精3克，酱油25克，鸡蛋2个，淀粉、大料、桂皮、高汤各适量，油75克。');
INSERT INTO `recipe` VALUES (2661, 70, '红血肠', '1.把用罗筛过的生猪血，放入盆里。把白肉汤烧热后，加入胡椒粉、花椒、盐、味精等搅拌均匀后，晾凉。 \r\n2.用罗把汤滤入猪血内加入香菜（洗净切成末）搅拌均匀后，灌入洗干净的肠皮内，用线绳绑扎好，放入清水锅内，用旺火烧开后，移入文火上再煮15分钟左右，取出后解除线绳，用凉水浸泡凉。 \r\n3.把凉血肠切成5-6毫米厚的薄片，用开水煮热捞出后，在炒勺里加入适量的肉汤，在旺火上烧开以后，再放入血肠片稍微煮煮，连汤一起盛入碗内即成。', '颜色紫红，质地鲜嫩，汤醇鲜美，北国风味。', NULL, NULL, '生猪血1公斤，肠皮300克，白肉汤500克，香菜20克，胡椒粉2克，花椒5克，盐30克，味精适量。');
INSERT INTO `recipe` VALUES (2662, 70, '虎皮肉', '1.将肉在火上烤黄，放在温水中泡软，刮去黄焦皮及脏物，擦干。而后在皮上划成虎纹花刀，再以清水洗净待用。 \r\n2.把姜拍疏，葱切3厘米长段。把腌雪里红梗除去根梢，切成2厘米长段以后，用凉水洗净，稍干后用油炸焦待用。 \r\n3.在锅里放些清水，旺火烧开后，加入酱油、料酒、大料、白糖、葱、姜、盐，再放肉（皮朝上）。待汤烧开以后，撇去浮沫，有文火煨至3-4成熟时，把肉翻过去再继续煨，当肉煨到8-9成熟时，捞出来把肉皮朝下，放在碗内，把汤汁倒在肉上，再加上待用的雪里红梗，上屉用旺火再蒸15-20分钟。而后皮朝上扣入盘', '油亮光滑，纹似虎皮，软烂醇香，煳在渗甜。', NULL, NULL, '带毛五花猪肉1公斤，腌雪里红梗50克，植物油1公斤（实耗约100克），料酒20克，白糖150克，盐50克，酱油20克，葱20克，姜20克，大料2枚。');
INSERT INTO `recipe` VALUES (2663, 70, '花卷', '1.面粉加水、面肥调成面团，发酵发足后加碱水揉匀；\r\n2.葱切成细末，加盐、油拌匀。面团搓成胚子，擀成长方形片，刷上油，撒上葱花，卷起；\r\n3.上笼蒸一刻钟即可。', NULL, NULL, NULL, '面粉500克，油15克，葱50克，盐适量。');
INSERT INTO `recipe` VALUES (2664, 70, '黄焖鱼翅', '1.将鱼翅整齐地码放在竹箅子上。 \r\n2.将干贝用温水泡开后，用小刀去掉边上的硬筋，洗去表面泥沙，放入碗中，加适量的水，上笼蒸透，取出待用。 \r\n3.将火腿肉5克切成细末，待用；将火腿肉45克切成薄片，待用。 \r\n4.将两只母鸡、一只鸭子宰后煺尽，由背部劈开，掏出内脏，用水洗净血污，待用。 \r\n5.将水发鱼翅连同竹箅子放入锅内，将洗净的鸡鸭放在另备的竹箅子，然后压在鱼翅上面，将葱段姜片也放在锅内，注入清水，用大火烧开后，滗掉水，去掉葱段姜片，以去掉血腥味。 \r\n6.注入锅内4000克清水，放入45克火腿肉', '此菜翅肉软烂，杏黄透亮，柔软糯滑，味极醇鲜，整翅多汁。', '选整只黄鱼翅，即吕宋翅。用小火焖煮，一般6至7小时。一是入味，二是鱼翅软烂不散。水要一次加足，中途不宜加水、加汤。由于焖焅时间较长，最后调味。', NULL, '水发黄鱼翅1750克、鸭子750克、老母鸡3000克、白糖15克、干贝245克、绍酒25克、熟火腿250克、葱段250克、精盐15克、姜块50克。');
INSERT INTO `recipe` VALUES (2665, 70, '烩乌鱼蛋', '1.先把乌鱼蛋用清水洗一洗，剥去脂皮，放在凉水锅里，在旺火上烧开后，端下锅浸泡6小时。然后把乌鱼蛋一片片地揭开，放进凉水锅里，在旺火上烧到八成开时，换成凉水再烧，如此，反复五六次，以去掉其咸腥味。 \r\n2.将汤勺置于旺火上，放入鸡汤、乌鱼蛋、酱油、绍酒、姜汁、精盐和味精。待汤烧开后，撇去浮沫，加入用水调好的湿淀粉，搅拌均匀，再放入醋和胡椒粉，翻搅两下，淋入熟鸡油，倒在碗内，撒上香菜末即成。', '汤汁清亮，呈微带黄色，滋味清鲜中微带酸辣，食之开胃解腻。', '发好的乌鱼蛋薄片，行话名“乌鱼钱”，如果当天不用，必须用清水浸泡起来，每天要换一次水。味精、醋和胡椒宜后下，拌匀即可，开锅易变质变味。', NULL, '乌鱼蛋100克，醋1.5克，香菜末1.5克，绍酒7.5克，胡椒粉0.5克，精盐1克，姜汁7.5克，味精3克，酱油1克，湿淀粉75克，鸡汤250克，熟鸡油5克。');
INSERT INTO `recipe` VALUES (2666, 70, '鸡蛋汤', '1.将鸡蛋磕入豌中，加黄酒、精盐少许搅匀；\r\n2.锅上火，倒入清汤，放入嫩菜叶烧沸，放入黄酒、精盐、酱油、味精，倒入蛋液，待其再沸时，淋入熟鸡油即可装碗食用。', NULL, NULL, NULL, '嫩菜叶25克，鸡蛋3只，黄酒15克，清汤适量，精盐适量，味精少许，酱油少许，熟鸡油10克。');
INSERT INTO `recipe` VALUES (2667, 70, '家常豆腐', '1.豆腐切成1寸半见方，1分半厚的片，青蒜剖开切段；\r\n2.烧热锅，注入油，油热后把豆腐煎成两面焦黄色取出，下入肉片炒熟，再加豆瓣酱炒酥，加入酱油、汤、料酒、豆腐，小火入味，再加味精、用水淀粉勾芡，放入青蒜即可。', NULL, NULL, NULL, '北豆腐4块，肥瘦猪肉150克，猪肉150克，豆瓣酱50克，青蒜100克，酱油50克，料酒25克，味精5克，淀粉25克，汤150克。');
INSERT INTO `recipe` VALUES (2668, 70, '煎牛排', '1.将牛排切成10克重的小块，逐块用刀拍成1寸半直径的圆薄片，随即用刀斩几下，再把肉修圆，把牛排逐片放入用葱头汁、黄酒等调成的卤汁内拖一拖，取出腌五分钟，再用蛋糊涂拖；\r\n2.下热油锅两面煎成金黄色，烹上辣酱油和蕃茄沙司，翻几下出锅装盆，配以黄瓜片、土豆条，或生菜和蕃茄片上席即可。', NULL, NULL, NULL, '牛排，葱头汁，黄酒，鸡蛋，辣酱油，蕃茄沙司，黄瓜片、土豆条或生菜和蕃茄片。');
INSERT INTO `recipe` VALUES (2669, 70, '煎猪排', '1.将猪通脊肉去筋，横切成厚片，用刀拍松拍薄，撒上盐、味精和胡椒粉，再拍上面粉，蘸上鸡蛋液，裹上面包渣；\r\n2.锅上火，放油烧至五、六成熟，下猪排炸至两面呈金黄色，捞出装盘。周边可以配上炸土豆条、泡菜、生菜、熟豌豆等。或淋上少许黄油即可。', NULL, NULL, NULL, '猪通脊肉300克，面包渣100克，面粉50克，味精2克，胡椒粉少许，食盐1000克。');
INSERT INTO `recipe` VALUES (2670, 70, '姜葱海参', '1.将皮薄肉厚质量好的水发刺参，投入八成熟的油锅内，略炸一下捞起；\r\n2.另烧热锅，用生油、葱姜炝锅，煸炒至葱姜呈金黄色时加入高汤、精盐、味精、糖色、糖、酱油，烧滚后用细网筛除净沫子，推入刺参、胡葱，煨约10分种，待汤汁浓醇后勾芡，浇上用猪油、鸡油、麻油和香味料熬制的三味油，出锅装盆。', NULL, NULL, NULL, '刺参，生油，葱，姜，味精，糖，酱油，猪油，鸡油，麻油，香味料。');
INSERT INTO `recipe` VALUES (2671, 70, '酱爆鸡丁', '1.将鸡脯肉用凉水泡1小时后，去掉其脂皮和白筋，切成0.8厘米见方的丁，加入鸡蛋清、湿淀粉和清水5克，拌匀浆好。 \r\n2.将熟猪油倒入炒锅内，用微火烧到四成热时，放入浆好的鸡丁，迅速用筷子拨散，滑到六成熟，倒在漏勺里沥去油。 \r\n3.将熟猪油15克和芝麻油倒入炒锅内，用旺火烧热，随即下入黄酱，炒干酱里水分（酱一下锅就发出哗哗的响彻云霄声，等响声变得极其微小时，水分就基本上炒干了），再加入白糖。待糖溶化后，加入绍酒和姜汁，炒成糊状时再倒入鸡丁，继续炒约5秒钟即成。', '“酱爆鸡丁”是北京地区的传统名菜。红润油亮，咸中带甜，肉嫩透鲜，酱香浓郁，堪称酱爆菜中的魁首。', '此菜特别注重火候，火大了酱易糊、发苦，火小了酱又挂不到肉上。做到食后盘内只有油而无酱，是这一名菜的特色。', NULL, '鸡脯肉150克，姜汁2.5克，鸡蛋清0.5个，绍酒7.5克，黄酱25克，熟猪油500克，白糖40克（约耗20克），湿淀粉7.5克，芝麻油15克。');
INSERT INTO `recipe` VALUES (2672, 70, '酱汁活鱼', '1.将活鲤鱼去鳃、鳍，刮去鳞，开膛去内脏洗净，在鱼身两面每隔0.83厘米距离横切1刀（切到鱼骨为止，不要切断鱼腹）。然后，手提鱼尾在开水锅中约烫2-3分钟，使刀口张开，除去腥味。 \r\n2.将炒锅放在旺火上，先下入熟猪油、白糖、甜面酱，用清水100克调均，再续入清水1150克。烧开后，放入烫好的鱼，汤再烧开时，改用微火约20分钟。待汤汁已剩2/3，再用旺火烧开。最后，将鱼捞出，放入盘内。 \r\n3.将有汤汁的炒锅继续放在旺火上，用手勺不断搅动（防止糊底），待汤汁焅浓后，烧在鱼上，再撒上姜末即成。', '“酱汁活鱼”是京味特色名品。烹制过程中，以水为加热体；烧焅酱汁的滋味全部浸渍到鱼中，使鱼有酱香，酱有鱼味，成菜为深酱红色，油亮润泽。因为没有用油煎炸，鱼肉水分失去的少，因此肉质十分鲜嫩。', '高档宴席，可用活桂鱼2尾。用草鱼较用鲤鱼为好，鱼腥味不重。 鱼不可油煎炸，以免失去北京正宗风味。不可勾芡，汤汁焅浓，所谓“自来芡”是也。', NULL, '活鲤鱼1尾、姜末15克、甜面酱125克、熟猪油125克、白糖125克。');
INSERT INTO `recipe` VALUES (2673, 71, '酿银瓜', '莲子上笼蒸透、红小豆洗净加水蒸烂，去外皮，绞成豆沙。银瓜去皮，切去瓜尾、从中间开4厘米的方口、挖去瓜瓤、用水洗净。将豆沙、莲子、熟猪油放入碗内，加白糖拌匀，填入瓜内盖好盖，上笼蒸烂，取出晾凉，再入冰箱凉透（不要结冰）。炒锅加入白糖，清水，熬成糖汁后晾凉，放入冰箱凉透，上桌时，瓜上面撒青红丝，浇入糖汁即成。', '味道清新，绵软，甜香，凉爽，是夏令甜菜佳品。', NULL, NULL, '银瓜20克、莲子20克、红小豆20克、熟猪油15克、青红丝15克、白糖50克。');
INSERT INTO `recipe` VALUES (2674, 70, '芥末白菜墩', '1.将大白菜择洗干净，去掉尾部，将根部切成4厘米长的墩，下入开水中焯一下，控净水待用。 \r\n2.将芥末放在碗中，用沸水冲开，并按一个方向搅动，同时加入醋、盐、糖、香油。 \r\n3.将调好的芥末汁，涂在白菜墩上，码在一个盘子里，上边再取一个盘扣上，置于屋内暖和的地方，第二天取出，码盘，即可食用。', '此菜脆嫩爽口，甜、酸、辣具全，具有解腻通气之效，为冬春两季的时令佳肴', '焯白菜墩时，为使其下锅不散，最好事先用线捆一下，以保持成菜的美观。', NULL, '大白菜500克、白糖10克、芥末100克、白醋5克、精盐20克、香油10克。');
INSERT INTO `recipe` VALUES (2675, 70, '金鱼戏莲', '1.龙虾片炸好后置盘中央呈莲花状，樱桃作莲心。\r\n2.蘑菇用高汤入味，码放在“莲花”外，再铺一层发菜，虾茸下6成热油中滑熟，用蛋皮包成“金鱼”状，码在盘周围即成。', NULL, NULL, NULL, '龙虾片适量，蘑菇适量，发菜适量，虾茸适量，蛋皮适量，红樱桃一颗。');
INSERT INTO `recipe` VALUES (2676, 70, '京都排骨', '1.猪排骨剁成2.8厘米长的小块，加入绍酒5克、酱油5克浸渍一下，再磕入鸡蛋1只拌匀，加入面粉浆起；\r\n2.炒锅上火，舀入花生油烧至六成熟，将排骨投入锅内，用铁勺拨散，炸透，用漏勺捞起，待油烧至八成热时，再投入排骨复炸至酥脆，倒入漏勺沥油。炒锅复上火，放少许油、葱姜末、绍酒、酱油、白糖，再加入清水少许烧沸，用水淀粉勾芡，加醋、麻油，倒入排骨，颠翻均匀，起锅装盘即成。', NULL, NULL, NULL, '猪排骨325克，鸡蛋1只，葱姜末10克，酱油20克，绍酒15克，白糖50克，醋25克，麻油15克，面粉50克，水淀粉10克，花生油500克。');
INSERT INTO `recipe` VALUES (2677, 70, '京味海米拌黄瓜', '1.用清水洗净黄瓜，切去两头，再切成“梳子背”块形；将海米洗净放一小碗内，用沸水发一下。 \r\n2.把切好的黄瓜放一碗内，用盐腌约1刻钟后，再用凉开水淘洗一次，轻轻挤干水分，放入盘内。 \r\n3.将发好的海米放在黄瓜上，再放入精盐、味精、香油及少许发海米的汁水，拌匀即可食用。', '色泽鲜艳，咸香脆嫩。', NULL, NULL, '黄瓜、海米、精盐、味精、香油。');
INSERT INTO `recipe` VALUES (2678, 70, '京味水饺', '1.将面粉掺200克温水，并加点盐，拌和后用力反复揉面，揉透揉匀，稍待片刻，才可搓条，摘胚，一般每50克摘成6-8个胚子；\r\n2.将胚子擀成皮，把三鲜馅包入皮子内，将边儿捏成花边。即可下开水锅中煮熟，捞出，添佐料吃。', NULL, NULL, NULL, '面粉500克，三鲜（肉、鱼、笋）馅适量。');
INSERT INTO `recipe` VALUES (2679, 70, '三鲜豆腐盒', '1.将豆腐切成5厘米长、3厘米宽、3厘米厚的长方形，抹上酱油，过油炸至黄金色捞出控油。\r\n2.虾仁、海参、香菇洗净均切成小丁，加入盐、料酒、姜末、葱末、味精绞成馅。\r\n3.将炸好的豆腐切小口挖空，添入三鲜馅，将掏出的豆腐盖上成盒状，依次做完置碗中，加入高汤、料酒、葱、姜、酱油、味精上锅蒸透入味，沥干水分。起锅将原汁烧开去浮沫，淋水淀粉勾芡浇在豆腐盒上即可。', '鲜嫩，咸香。', NULL, NULL, '豆腐250克，海参50克，虾仁50克，香菇50克，酱油15克，料酒15克，盐2克，味精4克，葱、姜各25克，油、高汤、淀粉各适量');
INSERT INTO `recipe` VALUES (2680, 70, '沙锅栗子鸡', '1.鸡斩块，滴干水；\r\n2.栗子肉放滚水中煲约五分钟，冷后去衣，滴干水；\r\n3.下油三汤匙，爆透栗子及姜，下鸡、蘑菇爆片刻，铲起放入煲内煮滚，慢火焖，约需四十分钟，勾芡，放上芫荽即可。', NULL, NULL, NULL, '鸡半只，栗子肉八两，姜数片，酒一汤匙，蘑菇半汤匙，芫荽二棵。盐、糖、生油、老油各一汤匙。麻油、胡椒粉适量。');
INSERT INTO `recipe` VALUES (2681, 70, '涮羊肉', '1.肉放在零下5度的冷库中冷藏12小时，待肉冻僵后，再行切片，切时将冰冻肉去掉边缘和肉头，去掉云片（即附在肉上的一层薄膜）、脆骨和未剔净的筋膜，剩下最精致的肉核。然后横放在砧板上，盖上白布，右边露出1厘米宽的肉块，左手五指并拢向前平放，手掌压紧肉块和盖布，防止滑动。右手持刀，紧贴着左手拇指关节下刀，如拉锯似的来回拉切。当每刀切到肉的厚度一半时，将已切下的上半片，用刀刃一拨，把肉片折下，再继续到底，使每片肉都成对折的两层，也可切成卷如刨花形的肉片。每250克肉可切出长20厘米、宽5厘米的肉片40至50片，但', NULL, '精选原料：羊选内蒙古集宁所产小尾巴绵羊，且要阉割过的公羊，这种羊没有膻味。1只羊以出肉20公斤左右最佳。其中能够涮食的肉只有7500克左右，是用羊的“上脑”、“小三岔”、“大三岔”、“蘑裆”、“黄瓜条”这五个部位。传统冻肉方法：冰池内埋肉，一层肉、一层冰，中隔油布，层层埋好。冰冻两天左右，使肉内外冻实，其目的，清除膻味，改善肉质。调料不少于七八种，如可用香油炸制的辣油，芝麻酱、卤虾油、酱豆腐、腌韭菜花和酱油等。辅料还可加酸菜、冻豆腐、热芝麻饼等。食用时一次夹的肉片不宜过多，一般2至3片，过多就会有生熟不均', NULL, '羊肉1000克，酱豆腐1块，白菜头250克，腌韭菜花50克，水发细粉丝250克，芝麻酱100克，糖蒜100克，酱油50克，绍酒50克，辣椒油50克，卤虾油50克，葱花50克，香菜末50克。');
INSERT INTO `recipe` VALUES (2682, 70, '酒香鸡', '1.将母鸡宰杀后，放开水内泡一泡，褪净毛后，把头、爪剁掉，从肛门下方横切一小口子，掏出内脏。再顺着脊背由肛门至脖子切开，洗干净。\r\n2.用料酒10克、酱油15克，搅和一下，均匀地涂抹在鸡肉上（内都外涂满）。\r\n3.炒勺内倒入植物油，放旺火上烧7-8成热，把鸡胸脯向下放入炸成金黄色后捞出来，放进开水里涮掉浮油，再放入沙锅里，把鸡汤倒进去。\r\n4.用炒勺将葱段炸成金黄色，捞出后放在盛鸡的沙锅里，加入料酒、酱油、盐、味精，放在文火上炖2小时左右，炖烂后，洒入葡萄酒即成。', '色泽金黄亮光，肉质软烂鲜美，葡萄酒香味浓，中外遐迩驰名。', NULL, NULL, '活母鸡1只（重约1-1.5公斤），植物油600克（实耗约70克），鸡汤1200克，葡萄酒60克，料酒20克，盐6克，酱油50克，味精4克，葱段30克。');
INSERT INTO `recipe` VALUES (2683, 70, '烤牛排', '1.牛排用刀拍，用盐、糖、胡椒等调味品腌半小时；\r\n2.牛排放在烤架上入箱烘烤，先烤25分钟，再翻转烤10分钟即可。', NULL, NULL, NULL, '净牛排500克，盐、糖、胡椒、酱油、酒、味精适量。');
INSERT INTO `recipe` VALUES (2684, 70, '莲蓬鸡糕', '1.油菜叶切丝，鸡脯肉砸成泥，蛋清掺入面粉和干淀粉搅均，菠菜挤汁，鸡泥加调料、熟猪油、菠菜汁、蛋清搅成糊。\r\n2.取14个小酒盅，分别抹上熟猪油，装入鸡泥，嵌上豌豆，缀以火腿末上笼蒸5分钟后取出放入大汤碗，倒入鸡汤，烧开勾芡即成。', NULL, NULL, NULL, '鸡脯肉100克，西红柿3个，鲜豌豆火腿末，油菜叶适量。');
INSERT INTO `recipe` VALUES (2685, 70, '凉拌海蜇', '1.将蜇皮洗净切丝，泡两天，去咸味。用五六成热的热水把海蜇丝烫一下，捞出过凉。把醋、香油、味精对在碗里成调料。\r\n2.把黄瓜丝先放盘里，再把海蜇挤干水分放在黄瓜丝上面，浇上调料，淋芥末油即可。', NULL, NULL, NULL, '海蜇皮300克，黄瓜丝50克，芥末油3克，醋40克，香油3克，味精3克。');
INSERT INTO `recipe` VALUES (2686, 70, '晾肉', '1.将瘦肉切成顺纹的长条。而后于肉条下边用刀平片（不断）。揭开上边的片翻过来，照样再次平片，反复把肉条片成条带形状，吊在通风阴凉处晾干，而后取下用温水洗干净。 \r\n2.用冷水把条面筋洗干净，挤出水分后，切成小段，同时把姜切成小片，把葱切成小段待用。 \r\n3.用旺火烧锅，倒入植物油，待冒烟时，放入面筋炸成金黄色后捞出来。  \r\n4.继续用旺火烧锅倒上开水（浸没肉10厘米左右），放入晾干的肉条，烧开后，改用文火煮1小时许，把肉条捞入冷水内洗净。 \r\n5.把红曲粉放在大容器内，用1500毫升开水冲解，稍晾一会待', '味香甜咸，红亮经咬，虽凉不冰，别有风味。', NULL, NULL, '瘦猪肉1公斤，条面筋500克，植物油1公斤（实耗约120克），香油20克，红曲粉30克，酱油180克，白糖180克，料酒10克，葱50克，姜50克，味精适量。');
INSERT INTO `recipe` VALUES (2687, 70, '溜松花', '1.将松花蛋剥掉外壳，切成西瓜形状6块，放在面粉中滚拌，使其外裹一层。 \r\n2.将少许葱丝、蒜片、姜水、酱油、醋、味精、盐、湿淀粉及适量鸡汤，调成芡汁。 \r\n3.炒勺放在旺火上，倒入植物油，烧到5-6成热，将拌上面粉的松花蛋瓣外面再裹上一层湿淀粉糊，下油炸成金黄色，使其挺实起来，倒入漏勺控净油。 \r\n4.炒勺留些底油再回旺火上，倒入芡汁，炒几下成稠卤汁后，再倒入炸好的松花蛋瓣，翻几下，使芡汁均匀地包裹外层，滴入几滴明油即成。', '颜色金黄油亮。蛋瓣柔软酥香，佐酒食之素雅，深受宾客欢迎。', NULL, NULL, '松花蛋4个，植物油600克（实耗约60克），面粉6克，湿淀粉10克，鸡汤适量，料酒12克，醋5克，盐3克，味精1克，葱丝5克，姜水5克，蒜片少许。');
INSERT INTO `recipe` VALUES (2688, 70, '罗汉大虾', '1.将对虾洗净，剪去足、须，再从脊背剪开，去掉头部沙包和背部沙线，从中腰切成两段。前半部分备用，后半部分剥去外壳，保留虾尾，用刀从脊背部横着片开（但不片断），使腹部相连成扇形。在片开的虾肉里侧轻轻剞上交叉花刀后，放在盘内，用精盐（3克）、绍酒（5克）、葱白段（25克）、姜片（25克）腌入味。 \r\n2.另一半净虾肉片去表面的红膜，同猪肥膘肉放在一起，用刀背砸成虾肉泥。荸荠用刀拍碎后剁成末。葱白段（125克）、姜片（25克）、切成细丝。黑芝麻洗净，沥净水。鸡蛋清打散，放入虾肉泥、荸荠末、精盐（2克）、绍酒（5', NULL, '选用每500克4至5头的大虾，新鲜头体紧密相连，外壳与虾肉紧贴成一体，头足完整，虾身硬挺，有一定弯曲度，皮壳发亮，呈青白色，肉质紧实细嫩。雌虾略呈青蓝色（渔民称青虾），一般雌虾比雄虾稍大，入馔则雌佳雄次。焅虾的前半部，用手勺轻压虾头，挤出虾脑，成菜色红油润；炸虾的后半部，外部呈嫩黄色捞出，不可重油，避免炸老。', NULL, '对虾1250克、番茄酱25克、净对虾肉150克、湿淀粉50克、猪肥膘肉75克、鸡汤150克、罐头荸荠75克、葱白段150克、黑芝麻25克、姜片50克、鸡蛋清2个、芝麻油75克、精盐7克、花生油1000克、白糠20克（约耗75克）、绍酒25克。');
INSERT INTO `recipe` VALUES (2689, 70, '麻花', '1.把糯米粉和粳米粉拌和，加适量清水搓揉成粉团，分成若干份胚子搓条拧花待用。白糖入锅内，加少许冷水煮成糖汁；\r\n2.把油放入净锅内至见烟时，将油锅端离火，兑入煮好的糖汁，再端回火口，然后将生胚逐只入油内粘满糖汁，浮出油面，呈金黄色时，捞出，滤尽油。', NULL, NULL, NULL, '糯米粉700克，粳米粉300克，白糖200克，油150克。');
INSERT INTO `recipe` VALUES (2690, 70, '马莲肉', '1.把干马莲草放入开水中泡软。 \r\n2.顺纹把肉切成6-7厘米宽的长条，再横纹切成3-4厘米的肉块。而后肥、瘦各一块搭配，用一根马莲草捆绑好。 \r\n3.用小布袋装入花椒、大料、小茴香，与猪肉皮、桂皮、葱、姜、酱油一同放入开水锅内，水将烧开时撇出浮沫，然后放入捆好的肉块（水要浸没肉面3厘米），再加上味精、料酒，水将开时再次撇去浮沫。 \r\n4.把锅移至文火上，炖烂后，先捞出肉再煮猪皮，煮烂后与小布袋一同捞出。\r\n5.撇出锅中的浮油，再添些净水，加入白矾翻搅溶化于水中。 \r\n6.把汤烧开撇掉浮沫，离火晾10分钟', '上等凉菜，味道清香，可作酒菜，四季皆宜。', NULL, NULL, '猪肉（最好是五花肉和腿上的肉等量）1公斤，猪皮300克，干马莲草30克，料酒25克，白矾10克，味精少许，酱油250克，花椒、大料、桂皮、小茴香各适量，大葱（切成段）25克，姜（切成片）20克。');
INSERT INTO `recipe` VALUES (2691, 70, '木樨肉', '1.将猪瘦肉切成长5厘米、宽0.3厘米、厚0.3厘米的丝。鸡蛋磕入碗中，用筷子打匀；\r\n2.干木耳加开水泡5分钟，去掉根部，撕成块。黄瓜斜刀切成长2厘米的段，放平后直刀切成片，片形状即为菱形。葱、姜切成丝；\r\n3.炒锅上火，加油，烧热后加入鸡蛋炒散，使其成为不规则小块，盛装在盘中，即为所说的木须；\r\n4.炒锅上火，加油烧热，将肉丝放入煸炒，肉色变白后，加入葱、姜丝同炒，至八成熟时，加入料酒、酱油、盐，炒匀后加入木耳、黄瓜和鸡蛋块同炒，熟后淋入香油即可。', NULL, NULL, NULL, '猪瘦肉150克，鸡蛋150克，干木耳5克，黄瓜10克，盐5克，酱油3克，料酒5克，油80克，香油少许。');
INSERT INTO `recipe` VALUES (2692, 70, '奶汁菜心', '1.先将白菜切去根，切下菜头，去老帮，切十字刀，锅内加水，把白菜焯一下捞出。\r\n2.锅内放油，放入葱、姜炒出香味，加入牛奶，把白菜轻轻推入锅中，略炒出锅，把余汤勾芡淋在白菜上即可。', '色泽乳黄，乳香四溢。', NULL, NULL, '白菜心400克，牛奶50克，料酒3克，盐、味精各2克，葱、姜少许。');
INSERT INTO `recipe` VALUES (2693, 70, '牛肉汤', '1.牛肉洗净，切成方块。\r\n2.锅内倒入开水，把牛肉整块入锅，烧开撇去浮沫，加入花椒大料袋、葱姜、黄酒，盖上锅盖，慢火加盐炖1小时后，再放其他调味炖2.5小时即酥烂。拣出葱、姜、花椒大料袋，加味精、麻油，出锅，撒上香菜末即可。', '胆固醇 9mg \r\n碳水化合物 21g \r\n蛋白质 18g \r\n脂肪 15g \r\n热量 150cal', NULL, NULL, '牛肉两斤，花椒、大料5克，制成调味袋，葱段、姜块25克，精盐、味精、麻油、黄酒、香菜末各适量。');
INSERT INTO `recipe` VALUES (2694, 70, '潘鱼', '制作过程：   \r\n将活鲤鱼宰杀，去鳞，去鳃，去内脏，洗净，斜切成头，尾和中间三段，入开水锅内氽一下取出，放大海碗中；香菇洗净，去蒂，连同海米、玉兰片、葱段、姜片一起放在鱼上；炒锅上火，倒入鸡汤，加酱油，精盐，味精，绍酒，烧沸后倒入盛鱼的大海碗中，盖上盖子，防止气水进入，上笼蒸约10分钟即成。', '鱼肉鲜嫩，汤汁味美可口。', NULL, NULL, '活鲤鱼一条，海米，玉米片，姜片，绍酒，酱油，精盐，味精，葱段，鸡汤，干香菇。');
INSERT INTO `recipe` VALUES (2695, 70, '芹菜牛肉', '1.将芹菜切成薄片。牛肉横切成2厘米长的薄片，放入碗内，加小苏打、酱油胡椒粉、水淀粉，绍酒、姜末和清水，浸10分钟后，加入花生油，再腌1小时；\r\n2.炒锅上火，花生油烧至六成热，放入牛肉片，用勺拌和，待牛肉色白时，倒入漏勺沥油。锅内留少许油复上火，放入葱姜片、白糖、酱油、味精、清水少许，烧沸后，用水淀粉勾芡，放入牛肉片、芹菜片，拌均匀即可。', NULL, NULL, NULL, '嫩牛肉300克，芹菜150克，绍酒40克，酱油20克，白糖10克，小苏打5克，水淀粉20克，胡椒粉1克，葱姜片20克，姜末2克，花生油500克，味精少许。');
INSERT INTO `recipe` VALUES (2696, 70, '全家福', '1.海参洗净切成抹刀片，用开水烫好。鱿鱼切成笔筒卷用开水烫好。猪肉剁茸浇淀粉、调味品炸成小丸子。鱼皮、鱼唇切抹刀片。虾仁上浆过油。荷兰豆择洗干净，用开水烫好。葱、姜切丝。\r\n2.锅上火放猪油烧热，放葱、姜丝煸炒出香味，放酱油、白糖、味精、海参、鱿鱼、鱼唇、虾仁、丸子、鱼皮、高汤，旺火烧开用微火炖入味，转旺火，加荷兰豆、香菇翻炒。用水淀粉勾芡，淋葱油出勺即可。（装盘也可以将各种原料分别码放，并加点缀。所用原料也可根据自己的爱好稍作调整。）', '色泽艳丽，多料多味，鲜味浓厚，是喜庆宴席的一道大菜。', NULL, NULL, '水发海参、水发鱿鱼、水发鱼皮、猪肉、水发鱼唇、虾仁、水发香菇各50克，荷兰豆100克，猪油40克，酱油15克，料酒10克，姜10克，白糖4克，味精、葱、水淀粉、高汤、葱油各适量。');
INSERT INTO `recipe` VALUES (2697, 70, '软炸鸡', '1.将鸡脯去皮，去筋，用力拍松，打大花刀，再切成长块。\r\n2.将酒、盐、味精、猪油调在小碗里，放鸡块浸一下；再放入打散的鸡蛋里抓一抓，最后放在湿菱粉内抓一抓（菱粉不要抓得太多或太少，太多了鸡块炸不入味，吃起来只有菱粉，没有鸡味；太少了鸡块容易炸老）。\r\n3.以大火烧热猪油，在油刚被烧滚时，即将鸡块一块块放进滚炸，并用筷子鸡鸡块拨动，勿使黏在一起。鸡块炸呈深黄色时，起锅装盘。另跟椒盐上席，外深黄内白色；软嫩香松。', '外深黄内白色，软嫩香松。', NULL, NULL, '鸡脯或鸡腿250克、鸡蛋1只、黄酒少许、盐少许、味精少许、菱粉75克。');
INSERT INTO `recipe` VALUES (2698, 70, '软炸里脊', '1.将里脊肉洗净，切成长4厘米、宽2厘米的薄片，放在碗内，加盐、味精、料酒拌匀，腌渍入味；\r\n2.将蛋清倒入碗内，用筷子顺一个方向连续抽打起泡沫，直到能立住筷子为止，再加干淀粉，乃顺同一方向搅拌均匀，制成蛋泡糊；\r\n3.炒锅上火，放入大油，烧至五成熟，将腌渍好的里脊肉分片粘上蛋泡糊后放入，用筷子轻轻翻动，大约5分钟炸熟捞出装盘。淋上香油即成。可蘸花椒盐食之。', NULL, NULL, NULL, '猪里脊肉200克，鸡蛋4个，味精2克，料酒30克，干淀粉30克，盐少许，香油10克，花椒盐1碟，大油1000克。');
INSERT INTO `recipe` VALUES (2699, 70, '软炸虾', '1.虾仁加少许食碱面腌制，然后用水冲去碱，搌干水分，加盐、胡椒、蛋清、干淀粉上浆，用油滑熟，控去油；\r\n2.锅内留底油，下葱姜末、虾仁稍炒，倒入事先备好的碗汁（用水淀粉、盐、胡椒、汤合成），翻炒装盘，炒豆苗围边即可。', NULL, NULL, NULL, '虾仁350克，豆苗50克，碱面、味精、胡椒、蛋清、干淀粉、葱、姜、油各适量。');
INSERT INTO `recipe` VALUES (2700, 70, '软炸鱼', '1.将鱼肉切成三成厚的大片，再切成二寸长、三分见方的条，放在碗里，加入葱末、姜末、绍酒、精盐、味精、香油，腌好；\r\n2.取老面肥放在碗里，用水调开，加干面粉、清水适量搅成稠糊状，放在30摄氏度处发酵。待糊发起时，下入少量碱，再放入花生油搅匀；\r\n3.炒锅内放入猪油，上微火烧至五成热，将腌好的鱼条逐条蘸上发面糊，下入油里的漏勺中翻炸，待鱼上的面糊遇热油胀起、呈金黄色时，捞出滴净油，去掉鱼条外部的糊渣不要，码于盘中即可上桌。', NULL, NULL, NULL, '净鱼肉150克，葱末、姜末各15克，绍酒10毫升，精盐2克，味精、香油各3克，老面肥50克，干面粉100克。');
INSERT INTO `recipe` VALUES (2701, 70, '萨其马', '1.制胚：将鸡蛋打碎，与面粉等原料拌匀，搓揉，稍加淀粉防粘，和成面团后，擀薄，切成面条；\r\n2.炸：将面条生胚入热油锅炸至完全松发为圆丝状、呈均匀的乳黄色时，即捞出待用；\r\n3.上糖：砂糖加水下锅煎熬，糖沸后约五分钟投入饴糖，再熬至具有一定粘度即可；\r\n4.成型：糖浆熬好后，将糖锅移离火炉，把半成品的胚料投入锅内，充分拌匀，然后倒入面积较大的木框内，压实，使其互相粘结，待冷却后，切成2寸正方形糕块即可。', NULL, NULL, NULL, '富强粉21斤，鸡蛋14斤，油20斤，白砂糖21斤，饴糖26斤，蜂蜜6斤，青梅1.5斤，瓜子仁0.3斤，金糕2斤，葡萄干1斤，桂花1斤，麻仁5斤，干面或淀粉4斤。');
INSERT INTO `recipe` VALUES (2702, 70, '三不粘', '将蛋黄打匀，加白糖、绿豆粉和清水搅匀；炒锅上火，加熟猪油，烧至五成热，将调好的蛋黄液倒入，边炒边用铁勺搅拌，并不断加油少许，以防粘锅，不停地搅炒十几分钟，直到蛋黄与猪油融为一体，出锅装盘即成。', '色泽金黄，鸡蛋软嫩，鲜美爽口，香甜不腻。', NULL, NULL, '鸡蛋黄、白糖、绿豆粉、熟猪油、麻油少许。');
INSERT INTO `recipe` VALUES (2703, 70, '沙锅对虾', '1.砂锅上旺火，用油滑锅，将虾下锅稍煎，烹入绍酒和姜汁，加盖稍焖，加糖、鲜汤，加盖焖；\r\n2.至八成熟，放入粉丝，味精，烧沸后，倒入大砂锅内，放在微火上煨5分钟，再移入中火烧约二三分钟，撇去浮沫，加青、红椒丝、味精，淋入熟猪油，原锅上桌即可。', NULL, NULL, NULL, '虾二对，青椒一个，红椒一个，粉丝二两，菠萝丁少许，绍酒25克，白糖20克，味精3.5克，熟猪油少许，鲜汤1000克。');
INSERT INTO `recipe` VALUES (2704, 70, '烧饼油条', '1.取面粉，精盐倒入盆中，合成面团，反复搓揉，至面团光滑柔韧，分成若干份，烙制成烧饼。\r\n2.将生油条放入滚汤的油锅中，炸至金黄色时，即可捞出，夹在烧饼中即可食用。', NULL, NULL, NULL, '面粉500克，精盐10克，凉水300毫升，油1000克。');
INSERT INTO `recipe` VALUES (2705, 70, '酥小鲫鱼', '1.将鲫鱼的鳃、鳞去掉，在鳃下顺着鱼腹部剖开2厘米长左右的小口掏出内脏（小心别弄破苦胆），用用清水灌洗干净。 \r\n2.葱白切成10厘米长的段。把酱油、陈醋、料酒拌在一起成调料水。\r\n3.在沙锅内辅上一屋猪肋骨，再铺上一层姜片，再撒上桂皮、花椒、大料、丁香、豆蔻等香料。然后把鱼头向锅边，离心方向逐条码成整齐的圆圈（中间留个孔，形成菊花状）在上面撒上五香粉。在中间再横码上一排鱼，盖住小孔，再撒一层五香粉把葱白段照样在鱼上码成菊花形。白糖、冰糖末倒入葱白段中间，把香油糖色均匀地浇泼在葱白段上面，加入调料水一大半', '颜色鲜美，鱼肉肥嫩，刺脆香酥，味道特殊。', NULL, NULL, '小活鲫鱼（最好10厘米左右长）1公斤，葱白600克。香油120克，糖色30克，冰糖末60克，白糖100克，五香粉3克，桂皮、花椒、大料、丁香、豆蔻共6克，料酒120克，陈醋120克，酱油120克，姜片12克。');
INSERT INTO `recipe` VALUES (2706, 70, '坛子肉', '1.将五花肉皮刮洗干净，切成3厘米左右见方的块，在开水锅中氽透，捞出洗去浮沫。\r\n2.炒勺放底油加入白糖，炒出糖色，投入氽透的肉，加入葱段、姜片、肉料包一起煸炒约10分钟左右，再加入酱油、料酒、高汤烧开打去浮沫，倒入专用的坛子里，用牛皮纸和纸绳将坛口封严，置微火炖熟烂。\r\n3.白菜心用开水焯一下，放入坛中垫底。', '色泽红润油亮，肉质软烂醇香，多为秋季应时菜。', NULL, NULL, '猪带皮五花肉1000克，白菜心150克，油25克，酱油50克，葱段100克，姜片50克，料酒30克，白糖15克，肉料包1个，高汤适量。');
INSERT INTO `recipe` VALUES (2707, 70, '汤泡肚尖', '1.把猪肚尖（猪肛贲门处）洗干净，用刀片去外皮、刮去油丝。把肚尖里面用刀每隔1-1.5毫米宽切一直刀，深约2/3厚，而后横着刀纹每隔2-3毫米宽切断，切成5-6毫米宽、2-3厘米长的坡刀片。 \r\n2.用开水泡发干口蘑，择洗干净后，片成1-1.5毫米厚的片，再洗两次，然后把油菜芯洗干净。 \r\n3.旺火上架上炒勺，倒入鸡汤，加入口蘑片、姜片和味精、盐4克。汤烧开后撇去浮沫，放入油菜芯，把汤倒入容器里，浇上鸡油。 \r\n4.把炒勺放在旺火上，倒进500克开水，再加入料酒、味精。 \r\n5.水烧开后，放进肚片氽一下，', '汤热淡黄，味美醇厚，肚尖脆嫩，口蘑特鲜，滋香清爽，可口宜人。', NULL, NULL, '猪肚尖250克，油菜芯30克，干口蘑15克，鸡油15克，鸡汤600克，料酒10克，胡椒粉1克，盐5克，味精2克，姜片5克。');
INSERT INTO `recipe` VALUES (2708, 70, '四喜丸子', '1.主料放碗里，加老油、味精、料酒、盐、面酱、葱、姜末、香油，拌匀，团成4个大丸子。鸡蛋加面和成糊；\r\n2.油烧至五成热，丸子下油炸，至发硬捞出，加全部配料，上屉蒸熟，汤倒勺里，丸子放大碗里；\r\n3.坐汤煮菠菜，开后除沫，倒在大碗里即可。', NULL, NULL, NULL, '肉末400克，肉丁100克，菠菜段、笋丁、虾仁、海参丁各25克，油800克，酱油25克，面酱20克，料酒10克，盐3克，味精5克，葱、姜末各2克，香油5克，鸡蛋150克，面45克，姜片、葱段、大料各3克，汤500克。');
INSERT INTO `recipe` VALUES (2709, 70, '炸香椿鱼', '1.将香椿洗净，用开水略烫、控水，粘匀面粉。\r\n2.鸡蛋液加盐、面粉、料酒调成鸡蛋糊。\r\n3.炒勺上火，放油烧至5-6成热，将粘好面粉的香椿芽裹上蛋糊投入锅内炸熟捞出即可。食用时沾花椒盐（炸香椿时要防止粘连，尽量做成小面鱼状，炸的时间不可太长，面糊一熟马上捞出）。', '鲜香适口，佐酒尤佳。', NULL, NULL, '嫩香椿芽250克，鸡蛋液100克，油100克，盐2克，料酒6克，面粉、花椒少许。');
INSERT INTO `recipe` VALUES (2710, 70, '炸羊尾', '1.鸡蛋清搅打泡沫状的糊，加入干淀粉搅拌均匀。豆沙馅分成10份，团成10个小圆球。 \r\n2.将花生油倒入炒锅中，在火上烧到五成热，端离火口放在灶旁。用筷子夹着豆沙球蘸上蛋清糊，逐个放入油内，把炒锅端到火口上。待到蛋清糊炸成金黄色，随即捞出沥去油，放在盘内，撒上白糖即成。', '“炸羊尾”是北京清真风味中的一道甜菜，名为“羊尾”，实则鸡蛋清糊和豆沙馅制成。此菜创始于、清朝末年，原是用羊尾作馅炸的，因其油味膻，没有什么人爱吃。后来厨师将羊尾换成豆沙馅，形状和颜色都还保持原来的样子，所以仍继袭了“羊尾”的名称。此菜色泽金黄，味道甜而不腻，外皮软而酥脆。', '豆沙馅制法：红小豆5公斤用清水洗净。烧开水，加入红小豆和碱水65克，水要超过小豆10厘米，大火烧开后，用小火把豆煮烂，取出过罗，边搅边加水，否则会粘到一块。过好罗装入布袋压干水分，即成豆沙。锅内放入花生油2公斤，砂糖7.5公斤、豆沙，上火炒1小时，然后加10克碱水，待炒透后，用手摸不粘手，取出放入盆内即成。打蛋泡糊时要先轻后重、先慢后快，一气呵成。因为蛋清中的浓厚蛋白和系带这两部分有较好的弹性和韧性，所以在抽糊刚刚开始的时候，动作要缓慢，并要轻一点儿，尽量使浓厚蛋白和系带附着在工具上，不然这两部分就抽打不', NULL, '鸡蛋清6个，白糖100克，豆沙馅150克，花生油100克，干淀粉75克。');
INSERT INTO `recipe` VALUES (2711, 70, '芝麻鸡', '1.将肉鸡洗净，放入锅中煮20至30分种，取出晾凉，葱切成末。芝麻炒熟晾凉；\r\n2.将酱油、醋、香油调和，再放入糖、花椒面、辣椒油、味精、葱末搅拌均匀；\r\n3.将熟鸡剁成便于食用的小块，码放盘中，浇汁后即可食用。', NULL, NULL, NULL, '肉鸡一只，葱15克，花椒面1克、酱油25克，香油25克，芝麻25克，糖15克，醋10克，辣椒油15克，味精3克。');
INSERT INTO `recipe` VALUES (2712, 71, '熬黄花鱼', '将活黄花鱼刮去鳞，掏净内脏及鳃，洗净。在鱼身两面剖上斜直刀，用精盐腌渍。猪肥瘦肉切丝、青菜切段。炒锅内加花生油、中火烧至六成热（约150度），用葱段、姜片煸炒几下，倒入肉丝煸至断血，放入绍酒、醋，加入酱油、清汤、精盐烧至沸，将鱼入锅内小火熬炖20分钟，撒上青菜、青蒜，淋上芝麻油盛汤盘内即成。', '鱼肉软绵，烂而不糜，汤汁醇厚。', NULL, NULL, '黄花鱼1000克。猪肥瘦肉、青蒜、青菜各100克。鲜姜10克、大葱15克、绍酒20克、醋15克、酱油10克、芝麻油10克、花生油250克、精盐7.5克。');
INSERT INTO `recipe` VALUES (2713, 71, '八仙过海闹罗汉', '1.取鸡脯肉150克斩成鸡泥，用其中一部分镶在碗底上，做成罗汉钱状。白鱼肉切成条，用刀划开夹入鱼骨。其余鸡脯肉切成长条。活青虾做成虾环。将鱼翅与剩下的鸡泥做成菊花鱼翅形。海参做成蝴蝶形。鲍鱼切成片。鱼肚切成片。芦笋发好后选取八根。 \r\n2.将上述食物用精盐、味精、绍酒调好口味，上笼蒸熟取出，分别放入磁罐，摆成八方，中间放罗汉鸡，上面撒火腿片、姜片及氽好的青菜叶，将烧开的鸡汤和少许熟猪油浇上即成。关键:海参、鱼翅、鲍鱼、鱼骨均必须浸透发软，洗净沙子。用鸡肉制成罗汉钱状的形象要均匀、完整。宜用鸡汤烹制。', '原料多样，汤汁浓鲜，色泽美观，形如八仙与罗汉。', NULL, NULL, '鸡脯肉300 克，水发鱼翅、海参、鲍鱼、鱼骨（明骨）、鱼肚、活青虾、火腿各100克，芦笋50克，白鱼肉250克，绍酒50克，鸡汤、精盐、姜片、味精各适量，青菜叶、熟猪油各少许。');
INSERT INTO `recipe` VALUES (2714, 70, '糖醋鱼', '1.鱼去磷，鳍，腮，内脏洗净，鱼身两侧每隔2厘米切一刀至鱼骨，然后顺骨切1.5厘米，使鱼肉翻起。金糕，青梅切小丁用开水略烫。 \r\n2.起锅放油烧7-8成热，投入挂淀粉的鱼微火炸透，放盘中。 \r\n3.锅留底油烧热，加入葱，姜末爆香，葱，姜末捞出，放入酱油，白糖，盐，料酒，醋，姜汁，烧开淋水淀粉制成糖醋汁，浇在炸好的鱼上，再撒青梅，金糕丁即可。', '色泽金黄，甜咸适口，外焦里嫩。', NULL, NULL, '黄鱼（也可用草鱼，鲤鱼等）1尾（重约750克），金糕，青梅各5克，葱10克，姜10克，白糖125克，醋50克，盐3克，料酒15克，酱油10克，油，淀粉，姜汁各适量。');
INSERT INTO `recipe` VALUES (2715, 70, '乌龙吐珠', '1.海参净膛，从膛内剞花刀，用开水焯好。鹌鹑蛋煮熟剥皮。\r\n2.炒勺上火将猪油烧热，放葱丝煸炒出香味，加调料烧开去浮沫，放入海参、鹌鹑蛋后一直微火炖入味，淋水淀粉勾芡，淋葱姜油出勺即可。', '海参油亮，菜式美观，鲜味浓厚。', NULL, NULL, '水发灰参500克，鹌鹑蛋10个，猪油10克，葱姜油15克，酱油25克，料酒10克，姜汁6克，味精6克，葱丝10克，白糖5克，高汤、水淀粉各适量。');
INSERT INTO `recipe` VALUES (2716, 70, '五彩鸳鸯蛋', '1.将鸡胸脯肉、猪肥膘肉同时洗干净剁成肉泥，虾仁切成末，同放在一碗内，加鸡汤调散，再加入葱、姜末、盐、味精（1克）、鸡蛋清、料酒（半两）、湿淀粉等，用筷子顺时针搅拌，使其发粘成茸状。 \r\n2.把松花蛋外壳剥掉，切成两半，分别在每块上涂抹和好的鸡肉泥，成为原来的样子。 \r\n3.在鸡肉泥上面，用5种小料码成5行，一齐摆在盘内，然后上笼屉蒸10分钟左右，熟后取出，即成五彩鸯鸳蛋。', '颜色五彩缤纷，外形很像鸯鸳，食之清淡爽口，宴客别有风味。', NULL, NULL, '松花蛋6个，鸡胸脯肉200克，猪肥膘肉60克，火腿丝10克，蛋皮丝10克，玉兰片丝20克，木耳丝10克，辣椒丝20克，虾仁20克，鸡蛋清60克，鸡汤适量，葱姜末10克，湿淀粉25克，料酒25克，盐适量，味精3克。');
INSERT INTO `recipe` VALUES (2717, 70, '五香鱼', '1.将鱼刮鳞，去头，去鳍，去尾取内脏，冲洗干净待用。 \r\n2.用刀顺鱼脊骨由头向尾破两片，再斜刀改成小块，用盐、葱姜、醋、酱油、料酒腌30分钟入味。\r\n3.锅中留油，油八九成热时下鱼，炸至金黄色时捞出待用。 \r\n4.锅中留底油，油热后先下大料、桂皮、花椒煸炒片刻，再加入葱姜，煸炒后依次加入适量酱油、料酒、醋、盐、糖、味精（稍后放），最后下鱼，加水，水量以稍低于鱼量为宜。先用大火烧开，然后改用小火焅，直至汁浓味厚。取出鱼放在净盘中。锅内浓汁打明油，淋在鱼上面即可。晾凉后入冰箱待上菜。', '口味咸鲜，五香浓郁。', '鱼块宜炸稍硬，煨焅时成形不碎。武火烧开，文火慢焅，味汁十之七八收入肉内，酥透为止。', NULL, '鲤鱼500克、盐20克、花椒10克、味精10克、大料10克、糖10克、桂皮10克、料酒100克、酱油50克、醋150克、葱姜各50克、香油50克。');
INSERT INTO `recipe` VALUES (2718, 70, '虾子烧鲨鱼皮', '1.发好的鱼皮洗净，切成长4厘米，宽2厘米的菱形片泡在清水内。 \r\n2.虾子洗净后加入高汤，葱丝，姜丝上锅蒸透，用纱布过滤。\r\n3.将鱼皮用开水焯后加高汤，料酒稍煮捞出控净水。 \r\n4.炒勺上火加油，油热后放入虾略炒，加入葱丝，高汤，料酒，姜汁，白糖，盐，味精烧开，打去浮沫，放入鱼皮微火煮入味，转旺火加水淀粉勾芡，淋入葱酱油即可出。', '质地脆软，鲜味浓郁。', NULL, NULL, '水发鲨鱼皮300克，虾子15克，葱姜油15克，盐3克，料酒12克，姜汁8克，味精4克，葱姜各8克，白糖2克，高汤、水淀粉、油各适量。');
INSERT INTO `recipe` VALUES (2719, 70, '香糟酒肉', '1.把选好的肉洗干净，切成12厘米见方块，放入开水锅里烧开，撇净浮沫后，用微火煮8-9成烂，捞出晾凉。 \r\n2.将晾好的肉去皮，横纹切成薄片，浇上香糟酒即可食用。', '味道香美甜滋滋，带有浓厚的香糟酒味，别具风格。', NULL, NULL, '猪肉（最好是蹄膀肉）1公斤，香糟酒200克。');
INSERT INTO `recipe` VALUES (2720, 70, '熏肝', '1.将猪肝放在冷水锅里煮熟，再用冷水洗净。\r\n2.将葱、姜、花椒、大茴香、桂皮、酱油和清汤放在锅里，再放进猪肝煮透，捞出猪肝，其他不要。\r\n3.另用干锅，放上木屑165克，上面放熏笼，熏笼里放猪肝。点燃木屑（不要烧起火）熏约十分钟，取出，抹上麻油润色，切成薄片装盘。', '味道鲜美，熏味浓厚。', NULL, NULL, '猪肝500克、京葱段40克、姜片2片、整花椒少许、大茴香少许、桂皮少许、酱油160克、麻油少许、清汤650克。');
INSERT INTO `recipe` VALUES (2721, 70, '熏鸭脑', '1.用鸭头20只放在开水锅里煮熟，一只只剖开，挖出鸭脑，加清汤、酱油、酒、葱段、姜片、五香粉，放在温火上煨一煨，倒出，滗去汤。\r\n2.用熏笼，笼内放菜叶，菜叶上放鸭脑，一只只排整齐。\r\n3.用干锅，锅底放少许米饭、白糖和木屑，上面放熏笼，盖紧，烧起烟略熏一下即取出盛入盘。\r\n4.用生油500克倒在锅内烧至高热，随即将豆苗（只用叶，不用梗）放入清水漂过，入油锅迅速一炸，取出，围在鸭脑四周即好。炸时要把握时间，过长豆苗发黄；过短水分炸不干，不脆不酥。', '味道鲜美，颜色美观，又脆又酥。', NULL, NULL, '鸭脑20克、豌豆苗125克、京葱段3段、姜2片、五香粉少许、黄酒12.5克、酱油6.5克、味精少许、清汤250克。');
INSERT INTO `recipe` VALUES (2722, 70, '芫爆百叶', '1.将百叶清理后，随冷水下锅，煮3小时7成烂时，离火泡冷。 2.把百叶平铺砧板上，剔去黑色外层切成长5厘米、宽1.3厘米块，再煮1分钟。香菜择洗干净，切成长3厘米段。 　　\r\n3.将熟鸡油倒入炒锅内，用旺火烧热，下入葱末、姜末，稍一冒出香味，迅速放入百叶块爆炒几下，随即加入清汤、精盐、胡椒粉、醋和香菜段，颠翻几下，淋上芝麻油即成。', '此菜脆嫩清鲜，香菜味型，清爽不腻，无汁无芡，诱人食欲。', '用新鲜香菜的三叉梗切段，此味最佳。煮至七成熟的百叶块，放入清水煮开掺碱漂洗干净。掺碱量按1000克百叶，放碱20克，掺碱是为了使百叶更加白洁，纤维体松懈，烹制易入味。此菜要求烹炒迅速，无汤汁，可将各种调料入碗，一同下锅，后入香菜。', NULL, '牛百叶250克，精盐3克，香菜75克，醋5克，葱末10克，清汤40克，姜末5克，熟鸡油25克，胡椒粉2克，芝麻油15克。');
INSERT INTO `recipe` VALUES (2723, 70, '油爆肚仁', '1.把羊肚仁去掉油脂，洗干净，切成4×2厘米的肚条。把葱和青蒜都切成0.6厘米长的小段，一同放入碗中，加入蒜片、湿淀粉、料酒、醋、盐、姜汁、味精和鸡汤等，调成芡汁。 \r\n2.用开水把肚条烫一下。 \r\n3.炒勺里倒入大油，放在旺火上烧到冒烟时，放入肚条，爆2秒种，倒在漏勺内沥去油。 \r\n4.炒勺内倒入花椒油烧热，放入爆好的肚条，翻炒几下，倒入芡汁后，再翻炒3-4秒钟即成。', '色泽乳白，肚质脆嫩，味道鲜美，清香不腻。', NULL, NULL, '羊肚仁200克，青蒜（去皮根）2克，蒜片2克，熟大油600克（实耗约22克），花椒油16克，鸡汤12克，湿淀粉8克，料酒6克，醋2克，盐2克，味精2克，葱2克，姜汁5克。');
INSERT INTO `recipe` VALUES (2724, 70, '油焖大虾', '1.将虾去净须脚、沙包，洗净。\r\n2.炒勺上火加油烧至5成热，放入虾煎至两面发硬，倒出。炒勺回火加油烧热放葱、姜丝煸炒出香味，放煎好的虾再稍煎至色红，放料酒、醋、高汤、盐、白糖、味精、大油烧开，后移至微火煸炒约3分钟，中火收汁后出勺码齐，余汁淋在虾上即可。', '色泽鲜亮，虾肉鲜嫩，甜咸适口。', NULL, NULL, '对虾750克，大油100克，葱、姜丝各25克，白糖75克，料酒15克，盐2克，米醋5克，高汤、味精各适量。');
INSERT INTO `recipe` VALUES (2725, 70, '油条', '1.取面粉，精盐倒入盆中，合成面团，反复搓揉，至面团光滑柔韧，试抻细长不断条为止。再把面团放案板上，揉搓成60厘米长的粗条，手握两端反复溜20至30次，放在案板上，对折起来，撒上干面粉，再伸溜、再对折、再撒干面；\r\n2.将面条放入滚汤的油锅中，炸至金黄色时，即可。', NULL, NULL, NULL, '面粉500克，精盐10克，凉水200毫升，油1000克。');
INSERT INTO `recipe` VALUES (2726, 70, '玉黍鳜鱼', '1.将鳜鱼去鳞、内脏、鳍洗干净，用刀切成两扇，去掉脊骨、胸刺，用刀截成长片，剞花刀后入盆，用姜、盐、料酒略泡。\r\n2.将腌好的鱼肉挤干水分粘上干淀粉。\r\n3.炒勺上火，放油烧至6-7成热，投鱼炸至金黄色捞出控油，码在盘内。\r\n4.锅留底油烧热，放姜末煸炒出香味，加高汤、料酒、盐、姜汁、白糖、醋、酱油烧开，去浮沫加水淀粉，成糖醋汁，淋葱油，浇在鱼上。\r\n5.油菜叶洗净后过油控净，切成玉米叶形码好，鸡蛋皮丝码鱼上部，形似玉米即成。', '形似玉米，色泽金黄，甜酸适口。', NULL, NULL, '鲜鳜鱼1条（也可用草鱼，重约1000克），鸡蛋皮丝、油菜叶各10克，葱油10克，姜汁15克，盐3克，料酒10克，白糖125克，醋40克，酱油10克，油150克，姜、淀粉、高汤各适量。');
INSERT INTO `recipe` VALUES (2727, 70, '糟鸭头炖鲫鱼', '1.将鲫鱼剖开，去内脏，去鳞，用水洗净，放入猪油锅内略炸后取出。\r\n2.用猪油、葱末、姜米炝锅，加上浓汤后，将鲫鱼和糟鸭头放入，再加盐、酒、味精，炖至鱼熟汤浓而成白色时即好。附注：糟鸭头是将鸭头（包括鸭颈和一小部分鸭脯）斩成小块，加香糟酒、葱段、姜块、盐，糖和少许鸡汤蒸烂而成。', '味醇厚。', NULL, NULL, '糟鸭头1只、鲫鱼1条约500克、京葱末少许、姜米少许、黄酒12.5克、盐6.5克、味精少许、浓汤1200克。');
INSERT INTO `recipe` VALUES (2728, 70, '炸酱面', '1.炒锅烧热，油烧至八成热，放葱末，炸出香味，再放入猪肉丁煸炒片刻，加黄酱、黄酒煸炒，炒至猪肉熟，待肉与酱分离时，加白糖、清汤少许，再续炒片刻，加味精，淋上麻油即成；\r\n2.用开水锅下面条，面条熟后盛入大汤碗内，加炸酱即可。', NULL, NULL, NULL, '面条250克，猪肉丁150克，黄酱40克，葱末、黄酒、麻油、猪油、白糖、酱油、味精各适量。');
INSERT INTO `recipe` VALUES (2729, 70, '炸响铃', '1.把豆腐皮切成方形。用绍酒、精盐、味精和鸡蛋黄与猪肉拌成馅；\r\n2.将肉馅在豆腐皮上，卷包成筒形，将圆筒切成段，竖在盘中；\r\n3.油烧至五成熟，将豆腐皮卷投入。当卷筒炸至金黄，捞出装盘，配椒盐即可食用。', NULL, NULL, NULL, '豆腐皮，猪里脊肉，绍酒，精盐，味精，鸡蛋黄，甜面酱或蕃茄酱，椒盐，熟菜油作调料。');
INSERT INTO `recipe` VALUES (2730, 70, '蒸蛋羹', '1.将鸡蛋打散搅匀，加入干贝丁、火腿丁、咸肉丁、味精、猪油加水拌匀，放入笼内蒸熟。约10分钟即可。\r\n2.最后撒上葱花。', '北方家常菜肴，色泽乳黄，松软骨嫩，清香爽口，老少均宜。', NULL, NULL, '鸡蛋3个，猪油20克，味精2克，葱花5克，干贝、火腿丁、咸肉丁各5克。');
INSERT INTO `recipe` VALUES (2731, 70, '芝麻肉片', '1.猪肉放清水中加盐、花椒、葱、姜块，煮熟切条；\r\n2.下入热油中把肉条炸干，再投入汤中加酱油、糖，烧两小时，至汁近干时，加香油、白糖，放入辣椒粉，熟芝麻即可。', NULL, NULL, NULL, '猪肉，盐，花椒，葱，姜块，酱油，糖，辣椒粉，熟芝麻，香油。');
INSERT INTO `recipe` VALUES (2732, 71, '八宝原壳鲜贝', '将鲜贝肉洗净，用沸水氽后备用。冬笋、冬菇、火腿、大虾肉、熟鸡肉、水发海参、海米、鲜鲍鱼肉，均切成小丁，即八宝丁。剩余的冬笋，冬菇切片。鸡蛋清打成雪丽糊，加上淀粉和适量水搅匀。取12只鲜贝壳刷洗净，放入沸水煮过，捞出沥水，均匀地围大盘摆一周，上笼蒸10分钟取出。净炒锅内加上少量花生油，旺火烧至六成热（约150度）时加葱、姜末略煸，随即加上八宝丁煸炒，再放入精盐、酱油、味精、绍酒、清汤稍煮一下，用湿淀粉勾芡，淋上芝麻油即成八宝馅料。盛入碗内，将八宝馅均匀地盛在贝壳内。抹上一层雪丽糊，撒上香菜、蛋糕末，入笼蒸2', '造型美观，色泽艳丽，口味清鲜。', NULL, NULL, '鲜贝150克、净冬笋、水发冬菇各30克、大虾肉、熟鸡肉、水发海参各50克、火腿15克、精盐15克、酱油25克、味精7.5克、绍酒30克、葱姜末各15克、清汤300克。');
INSERT INTO `recipe` VALUES (2733, 71, '八仙瑶池聚会', '山鸡肉洗净切成片，放入鸡蛋清、味精、精盐、湿淀粉上浆拌匀，倒入六成热（约150度）油锅中划油后捞出。将赤鳞鱼洗净。鹿角菜、松蛾、松香蛾、雷搌蛾、白菜心洗净切段，豆腐切片，与赤鳞鱼均放入沸水中一焯，捞出与山鸡片一起放入瓷盘中合为珍。鸡里脊肉去筋膜剁成泥，加入鸡蛋清、精盐、清汤、味精、葱椒油搅成鸡料子，与汆下的豆腐制成泥拌匀成稀糊状，倒入90度的水中，漂起成片，形似白云，捞出倒在“八珍”上，汤锅内放入清汤、绍酒、精盐、味精，旺火烧沸后打去浮沫，淋鸡油倒入瓷盘内即成。', '造型典雅，汤浮鸡茸宛如朵朵白云，轻飘其上，“云”下八珍，郁香四溢，鲜嫩柔脆，质感各异。', NULL, NULL, '山鸡肉100克、赤鳞鱼65克、鹿角菜50克、松蛾50克、松香蛾50克、雷搌蛾50克、白菜心50克、豆腐50克、 鸡蛋清50克、味精1克、精盐5克、清汤100克、葱椒油10克、绍酒15克、菜油250克（实耗油50克）、淀粉50克。');
INSERT INTO `recipe` VALUES (2734, 71, '豆豉肉', '将猪肉刮洗干净，放入锅内，用中火煮至六成熟时捞出，切成宽1厘米、长4厘米的片，放入盆内。加入豆豉、葱段、姜丝、酱油、绍酒、味精、白糖、清汤，入笼蒸30分钟取出，凉透成冻即成。食时根据需要码入盘中即成。', '“豆豉肉”是一品颇具浓郁古齐国遗风的传统美味，已有1500多年的历史，现今成为博山地区新春佳节户户心备的传统风味菜品。以猪五花肉为主料，配以豆豉蒸制而成。 豆豉的营养价值较高，其蛋白质和脂肪含量丰富；此外，还含有丰富的植物黑色素，可以乌发，对盗汗症有治疗作用，是一种优良的营养佳品，山东以临沂市出产最为著名。', NULL, NULL, '带皮猪五花肉2500克、豆豉150克、葱段100克，姜丝50克，酱油100克，绍酒50克，精盐50克，味精50克，白糖50克，清汤250克。');
INSERT INTO `recipe` VALUES (2735, 71, '炖口蘑龙凤珠', '1.将母鸡肉剁成三角块，放入烧至6成热的植物油炒勺中，稍炸后捞出沥油。虾肉切成厚片。口蘑泡后把泥沙洗去。 \r\n2.炒勺上旺火，把熟大油烧热，放入拍松的姜块煸炒，烹入料酒加入盐、奶汤，把鸡块和口蘑也放入汤内，而后改用小火炖烂。 \r\n3.然后将葱、姜拣出，再放入虾片，汤开后撇去浮沫，放入菠菜芯，淋入鸡油即成。', '汤色乳白，味道鲜美，肉质软嫩。', NULL, NULL, '母鸡肉1.3千克，口蘑25克，对虾肉500克，净菠菜芯10棵。植物油900克（实耗约70克），熟大油25克，味精、盐各10克，料酒、鸡油姜块各15克，葱段25克，奶汤1.5公斤。');
INSERT INTO `recipe` VALUES (2736, 71, '扒鲍鱼冬瓜球', '将鲍鱼先用直刀法每0.2厘米宽剞一刀（深度为2/3）然后转换一个角度劈成大片。冬瓜去皮切成1.5厘米见方的块，修成圆球形。冬菇、冬笋、火腿均切象眼片。炒锅加水，旺火烧沸放入冬瓜煮熟，捞出控净水分。炒锅内花生油、用中火烧至六成热时，放入葱姜蒜出香味后、加入清汤（捞出葱、姜、蒜不用）、鲍鱼、冬菇、火腿、青豆、冬瓜球，烧沸后用湿淀粉勾芡，用微火扒透，放味精、淋上鸡油出锅即成。', '质地柔软润滑，口味鲜嫩香醇。', NULL, NULL, '水发鲍鱼肉300克冬瓜50克、火腿20克、水发冬菇30克、冬笋20克、青豆10克。花生油50克、葱姜、蒜各15克、清汤20克、湿淀粉30克、味精5克、鸡油25克。');
INSERT INTO `recipe` VALUES (2737, 71, '扒栗子白菜', '1.白菜心顺刀切长条。罐头栗子用开水焯。如果用生栗子，切口煮熟，去皮，再切两半。\r\n2.白菜用蒸锅蒸软，或用开水焯软后，理顺码放盘中。\r\n3.锅放油烧温热，放葱姜末爆香，烹料酒，加酱油、盐、高汤、白糖、味精，放栗子、白糖，转微火稍煮，勾水淀粉，翻勺，淋香油既成。', '鲜咸软烂，有栗子的香甜味。', NULL, NULL, '白菜心400克，栗子罐头一听（或生栗子250克），清油40克，葱末、姜末各2克，料酒20克，酱油5克，白糖5克，盐2克，味精5克，水淀粉35克，香油5克，高汤适量。');
INSERT INTO `recipe` VALUES (2738, 71, '扒酿海参', '将虾泥、肥猪肉泥加精盐、绍酒、味精、清汤、芝麻油及虾脑搅匀为馅，再将海参用开水氽香捞出，炒锅内加入清汤、葱姜、精盐、绍酒、海参用中火烧沸后，再用小火烧透，捞出晾干。将虾馅分抹在海参内，摆入盘内入笼蒸熟取出，虾馅面朝下平放在案板上，逐个在海参上用刀斜切4/5深，刀距1厘米，然后馅面朝上排入盘内。炒锅内加入清汤、绍酒、葱姜水、精盐、味精烧开，去净浮沫，用湿淀粉勾溜芡，加鸡油淋在海参上即成。', '造型美观，鲜嫩滑爽、糯软清新，质地柔软滑润，鲜咸适口。', NULL, NULL, '整条水发海参12个，鲜虾泥30克、虾脑50克、肥猪肉泥100克。绍酒10克、精盐5克、味精1克、葱姜汁、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (2739, 71, '扒牛肉条', '将牛肉整块放入冷水内泡透，洗净血水放入沸水锅中焯水，捞出洗净血污，刷净锅，另加清水放入洗净的牛肉、葱段、姜片，置旺火烧沸后撇去浮沫，盖严锅盖，用小火焖煮2-3小时，熟透捞出晾凉。切去熟牛肉上的脂皮及不整齐的边，顶丝切成长8厘米、宽3厘米、厚0.4厘米的条，净面朝下整齐地码入碗内，加入酱油、精盐、绍酒、葱段、姜片、八角及煮牛肉的原汤，上笼用旺火蒸20分钟取出，去掉八角、葱姜，滗出汤汁，将牛肉扣入平盘内。将蒸牛肉汤倒入炒锅内，加入酱油烧沸，用湿淀粉勾芡，淋芝麻油，浇在牛肉上即成。', '色泽红润，牛肉酥烂，味鲜适口。', NULL, NULL, '牛肉500克。葱10克、姜10克、酱油15克、精盐20克、绍酒15克、八角5克、芝麻油6克、淀粉少许。');
INSERT INTO `recipe` VALUES (2740, 71, '拔丝珍珠苹果', '1.苹果去皮、核，用小刀削成直径为2厘米的圆球。酵母加干淀粉和水搅成糊状，再加熟猪油搅匀成为发酵糊，备用。 \r\n2.炒锅内放花生油，烧至八成热时，将苹果滚匀面粉，沾上发酵糊下锅炸至金黄色时，捞出控净油。 \r\n3.炒锅留油25克，用小火烧至四成热，加入白糖，炒至略呈红色时，迅速倒入炸好的苹果球，再撒匀青红丝，淋上香精颠翻沾匀，盛入盘内即成，外带一小碗凉开水上席。', '成菜拔丝细长，香甜软嫩，宜于热吃、快吃。', NULL, NULL, '烟台青香蕉苹果500克，白糖150克，青红丝5克，香精2滴，清粉100克，干淀粉25克，酵母100克，熟猪油50克，花生油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (2741, 71, '白扒鱼翅', '将母鸡肉、肥鸭肉、猪肘肉洗净剁成小块，放入开水锅内焯过捞出，洗净血沫待用。炒锅内加入清汤、精盐、绍酒、鱼翅，烧开后捞出沥净水分。再把菜心放入锅内氽过捞出。将鱼翅用清水洗净，排列整齐，码在大碗的底层，上面放上鸡块、鸭块、猪肘肉块和葱、姜片、精盐、绍酒、倒入清汤上笼蒸烂，取出捡去鸡块、鸭块。猪肘肉块、葱、姜它用。炒锅内放熟猪油，用中火烧至六成热（约132度）时，放入葱、姜片烧出香味，再加精盐、奶汤、绍酒，捞出葱姜不要，放入鱼翅、菜心，烧开后撇去浮沫，改用微火煨扒，待汤汁剩下1/3时，放湿淀粉勾芡，至汤成浓汁时', '汤汁浓郁，鱼翅软烂光亮，鲜香味醇。', NULL, NULL, '水发鱼翅200克。母鸡肉、肥鸭肉、猪肘肉各25克、菜心50克、熟猪油50克、鸡油25克、精盐5克、绍酒10克。葱10克、鲜姜10克、味精0.5克、淀粉30克。');
INSERT INTO `recipe` VALUES (2742, 71, '白汁酿鱼', '将黄鱼去鳃、内脏，刮去鱼鳞冲洗干净。在鱼的两面用拔刀法贴着脊骨劈成斜刀片（肉不离骨），每面约劈7片。将鱼头下颌，胸鳍中间割开（鱼腹不割开），两鳃壳往两边一掰，用刀在鱼头顶上拍一下，使鱼头扒下。猪肉剁成茸泥放入碗内，加葱末、姜末、鸡蛋清、精盐、芝麻油搅拌成馅，火腿、冬笋均切成长3厘米、厚0.1厘米的象眼片。将肉馅抹在鱼片上，逐片向劈进方向卷成卷，鱼腹朝下摆入大鱼盘内（两面的鱼卷紧贴在鱼脊骨两旁竖着），每个鱼卷的顶端按一粒青豆，入笼内用旺火约蒸15分钟即熟取出。将鱼的原汤滗入炒锅内。加清汤、绍酒、精盐、冬笋、', '造型新颖，鱼肉鲜嫩，馅香味美。', NULL, NULL, '黄鱼750克。猪肥瘦肉30克、净冬笋20克、火腿15克、青豆15克、姜末10克、鸡蛋清25克、精盐5克、芝麻油5克、清汤150克、绍酒15克、湿淀粉60克。');
INSERT INTO `recipe` VALUES (2743, 71, '百花大虾', '将大虾挑去脊筋泥沙，去掉虾米，剥成虾皮，去腿留尾，用清水洗净，从腹剖，用刀劈成2/3深、使脊背相连。在肉面剖上十字花刀，用精盐、绍酒腌渍入味。火腿、黄蛋糕，红辣椒皮均切成小象眼皮。鸡脯肉去净筋膜，剁成细泥，加入葱、姜末、精盐、绍酒、清汤、芝麻油搅匀成馅，均匀地分抹在虾肉上。鸡蛋清放入碗内搅打成蛋泡，加入干淀粉调和成蛋泡糊，将蛋泡糊分别抹在大虾肉馅的表面，再用火腿、红辣椒、蛋糕、香菜叶、发菜等点缀成花色图案，装盘内上笼，用火蒸熟取出，滗净水分，摆在盘内。炒锅内加清汤、精盐、旺火烧沸，撇去浮沫，用湿淀粉勾成流', '造型优雅，味道鲜美，营养丰富。', NULL, NULL, '新鲜对虾250克。鸡脯肉25克、鸡蛋清20克、火腿15克、黄蛋糕15克、发菜15克、红辣椒皮15克、香菜叶15克，精盐5克、绍酒15克、葱15克、姜末10克、清汤100克、芝麻油5克、淀粉50克。');
INSERT INTO `recipe` VALUES (2744, 71, '爆炒腰花', '将腰子洗净，一剖两片，批去腰臊，剖成麦穗花刀，切成宽2厘米、长5厘米的条，加酱油入味，用湿淀粉拌匀待用。笋片、木耳用沸水锅淖一下，酱油、精盐、味精、绍酒、清汤、湿淀粉调成芡汁。炒锅内加入花生油，置旺火上烧至九成热（约225度）时，将腰花入油滑至卷缩成麦穗状迅速捞出，炒锅内留少量油，烧至六成热（约150度）时，将蒜片、葱末放入煸炒，烹入醋、绍酒，加入冬笋片、木耳略炒，倒入芡汁，然后将腰花投入，迅速颠翻，淋上鸡油出锅即成。', '腰花鲜嫩，造形美观，味道醇厚，滑润不腻。', NULL, NULL, '猪腰子200克。冬笋片、水发木耳各50克、酱油10克、精盐3克、味精1克、绍酒20克、湿粉15克。');
INSERT INTO `recipe` VALUES (2745, 71, '爆两样', '1.肠斜切段，猪肝切片，用水淀粉、鸡蛋液挂糊。\r\n2.碗中放酱油、醋、料酒、盐、味精、葱、姜、蒜、水淀粉对成汁。\r\n3.锅放油烧六成热，将猪肝滑散，盛出。锅留底油，倒入猪肝、熟肠、黄瓜片、胡萝卜片、木耳炒匀，烹入味汁，颠炒，淋香油即可。', '鲜咸微酸，软嫩适口。', NULL, NULL, '熟猪肠100克，猪肝100克，黄瓜片30克，胡萝卜片30克，水发木耳10克，油100克，酱油25克，醋15克，料酒20克，葱、姜、蒜片各2克，盐2克，味精5克，鸡蛋液35克，水淀粉50克，香油5克。');
INSERT INTO `recipe` VALUES (2746, 71, '冰糟肘子', '将肘子用手叉架在火上烤至皮面发焦时，放入温水中泡透，用刀刮净焦皮，用刀顺骨劈开至露骨，放入汤锅中，煮至六成熟时捞出，趁热用净布擦干肘子皮面的浮油，抹上糖色，晾干，放入八成热（约200度）的油锅中炸至微红，肉皮起皱纹成小泡时捞出，用刀剔去骨头，从肉的内侧切成核桃形的块（深度为肉的2/3）取大碗一个，将肘皮朝下放入碗内，然后加入冰糖、酱油、绍酒、清汤、葱段、姜片上笼蒸至酥烂取出，扣入盘内，将汁入锅内，加入清汤，用湿粉勾芡，加入花椒油，淋在肘子上面即成。', '色泽红亮，肉香味浓，质地酥烂。', NULL, NULL, '猪肘（带皮骨）750克、酱油16克、冰糖50克、绍酒20克、葱段、姜片20克、花椒油15克、清汤适量、花生油2500克。');
INSERT INTO `recipe` VALUES (2747, 71, '炒鸡丝', '鸡脯肉剔去筋膜，片成厚0.2厘米的片，再切成长5厘米的丝。加蛋清、精盐、湿淀粉拌匀入味，水发玉兰片切成3.5厘米长的丝，用开水焯过。青蒜切成3.5厘米长的段。炒锅放猪油，烧至五成热，下入鸡丝划开，至八成熟，捞出，锅内留油，放玉兰片丝、青蒜段稍微一煸，倒进鸡丝，再放清汤、精盐、绍酒、味精，颠翻出锅即成。', '颜色洁白，质地细软滑爽，口味清淡鲜嫩。', NULL, NULL, '鸡750克 、鸡蛋一个、水发玉兰片25克、精盐3克、绍酒20克、味精1克、青蒜5克、湿淀粉25克、猪油100克。');
INSERT INTO `recipe` VALUES (2748, 71, '炒木樨肉', '1.将猪肉切成丝，木耳、黄花洗净泥沙、择净去根在开水中烫一下捞出。鸡蛋打入碗中搅散。 \r\n2.炒勺放入葱姜油烧至6成热。把鸡蛋倒入勺内炒熟取出。用原勺放入熟大油，烧至6成热倒入肉丝炒至6成熟时拨至勺边，放入甜面酱，炒熟后与肉丝再一起煸炒，然后加酱油炒匀，并加入炒好的鸡蛋再放入黄花、菠菜、木耳，颠翻几下即成。', '色泽艳丽，味道鲜美，质在脆嫩，四时咸宜。', NULL, NULL, '猪肥瘦肉150克，鸡蛋150克，水发木耳50克，水发黄花50克，菠菜100克，熟大油50克，葱姜油50克，酱油30克，甜面酱15克，盐3克。');
INSERT INTO `recipe` VALUES (2749, 71, '炒肉丝拉皮', '将猪瘦肉洗净切成细丝，葱头洗净切成丝，香菜梗择洗干净切成3.5厘米长的段，黄瓜、鸡蛋皮、木耳均切成丝。炒锅内放入花生油，中火烧至五成热，放入肉丝炒至半熟，加酱油、葱头翻炒，加精盐、香菜段炒熟，淋上芝麻油翻匀，分盛两小盘内，湿淀粉调成稀糊，加精盐溶化，入沸水锅拉成2张粉皮，用清水浸泡、捞出后切成1厘米宽、5厘米长的条，放入盘中摆正，将黄瓜丝、木耳丝、蛋皮丝顺序放在粉皮上摆成为花色图案。芝麻酱加入精盐，凉开水调成糊状，用酱油、醋、蒜泥、芝麻油对成汁，浇在粉皮上。同炒肉丝一同上桌即可。', '肉丝滑软脆嫩，香美醇厚，拉皮色白透明，凉爽柔软，调和酸香等口味，食之令入爽快，是盛夏之佳肴。', NULL, NULL, '猪瘦肉500克、香菜10克、鸡蛋1个、木耳10克、葱头15克、黄瓜20克、精盐10克、芝麻油20克、芝麻酱25克、醋15克、蒜15克、花生油25克、酱油10克。');
INSERT INTO `recipe` VALUES (2750, 71, '炒三泥', '1.将栗子洗净，在栗子顶端剞上十字刀纹，放入沸水锅中煮10分钟取出，剥去硬壳及软皮。将净栗子与水发莲子分别放入碗内，放入笼内旺火蒸约40分钟至烂取出，在砧板上用刀面压成细泥。 \r\n2.炒锅内放入熟猪油（40克）、白糖（75克），用中火烧至融化时，加入莲子泥，改用小火炒至起沙，即盛在盘内一边；炒锅内加猪油（40克）、白糖（75克）、栗子泥，小火炒至起沙时，盛入盘内的另一边；炒锅内放入猪油（20克）、白糖（50克）、京糕，用小火（以手勺底将京糕压碎）炒成稠泥时盛在盘子中间。 \r\n3.净炒锅内放入清水（50克）', '“炒三泥”是选用莲子、京糕、栗子为主料，将其分别制成细泥，然后分别与糖炒制而成。成菜红、黄、白三色分明，口味酸甜香糯适口，夏季以冷食为宜。', NULL, NULL, '水发莲子100克，栗子150克，京糕100克，青红丝2克，西瓜子仁3克，白糖250克，桂花酱2克，熟猪油100克。');
INSERT INTO `recipe` VALUES (2751, 71, '葱爆羊肉丁', '将羊肉切成1.2厘米厚的片，两面交叉打花刀（深度各为肉厚的1/3），切成1厘米的长条，再切成方丁，放入碗内加精盐、鸡蛋清、湿淀粉搅匀。将大葱一剖为二，改刀成1.2厘米的段备用，取一空碗放入精盐、酱油、绍酒、味精、湿淀粉搅匀成汁。炒锅内放入花生油，在旺火上烧至六成热时，放入羊肉丁，用手勺拨散，再放入葱段搅散迅速捞出。锅内留少量油，用旺火烧热后放入羊肉丁、葱段爆炒，接着倒入碗内芡汁翻炒，淋上芝麻油，颠翻几下，速出锅即成。', '羊肉滑嫩，鲜香不膻，汪油包汁，食后回味无穷。', NULL, NULL, '羊肉500克、鸡蛋1个（重约50克）、大葱100克、精盐20克、酱油10克、绍酒15克，味精15克、湿淀粉10克、花生油25克、芝麻油15克。');
INSERT INTO `recipe` VALUES (2752, 71, '葱油鱼', '1.草鱼宰杀后净膛，去磷，去鳃，洗净，两面剞一字刀，将鱼入锅加葱段，姜块及水煮约15分钟，至熟捞出，装鱼盘。\r\n2.鱼身上撒盐、味精、胡椒粉、加料酒、码葱丝、姜丝，倒酱油稍腌。\r\n3.锅放香油烧热，浇在鱼身上即可。', '形美味鲜，清淡素雅，咸香微辣。', NULL, NULL, '活草鱼1尾（重约750克），盐6克，酱油25克，料酒15克，味精3克，胡椒粉2克，姜丝15克，葱丝15克，葱丝15克，葱段、姜块各适量，香油50克。');
INSERT INTO `recipe` VALUES (2753, 71, '醋椒桂鱼', '桂鱼刮洗净鳞，挖去鳃，从鳃部取出内脏，冲洗干净，用开水略烫一下，再放入凉水中，刮净黑皮，用刀在鱼的两面每隔2厘米剖斜十字花刀，放入沸水中一会。炒锅内放入熟猪油、中火烧至五成热时，放入葱段、姜片、花椒、白胡椒，炸出香味后，放入清汤、绍酒、精盐，沸后放入桂鱼，旺火煮15分钟，刀口入醋，捞出葱、姜、花椒、胡椒不要。盛入大汤碗内，撒上香菜段、葱丝、胡椒粉即成。', '汤鲜肉嫩，咸香酸辣，清淡开胃。', NULL, NULL, '桂鱼1000克、香菜段25克、葱段25克、葱丝10克、花椒5克、白胡椒5克、清汤100克、绍酒20克、精盐4克、醋40克、姜10克、熟猪油100克。');
INSERT INTO `recipe` VALUES (2754, 71, '脆炸网油卷', '猪网油洗净，用洁净的纱布搌干水分，切成10厘米见方的块，共14块。将猪里脊肉、冬笋、鸡肉洗净切成3厘米长、2.5厘米宽0.3厘米厚的薄片，对虾、口蘑洗净切成0.6厘米厚的片，放入碗内，加上酱油、葱姜末、味精、芝麻油、绍酒拌匀成馅。把鸡蛋磕入碗内调散，加上面粉，搅匀成鸡蛋糊，把油铺案板上，先抹上鸡蛋糊再放上馅，卷成直径2厘米、长10厘米的卷，摆入盘内。上笼用旺火略蒸取出。炒锅内放入花生油，用中火烧至五成热时，把网油卷沾满鸡蛋糊，再沾上芝麻，逐个放入油内炸至呈金黄色，捞出沥油，盛入盘内即成。', '外皮金黄酥脆，里边肉嫩虾鲜。', NULL, NULL, '对虾100克、猪里脊肉50克、鸡肉50克、冬笋25克、口蘑10克、鸡蛋1个、面粉15克、酱油10克、葱姜末各10克、味精10克、芝麻油15克、绍酒10克。');
INSERT INTO `recipe` VALUES (2755, 71, '单县羊肉汤', '将羊肉洗净切成长10厘米、宽3.3厘米、厚3.3厘米的块，羊骨砸断铺在锅底，上面放上羊肉，加水至过肉，旺火烧沸，撇净血沫，将汤滗出不用。另加清水，用旺火烧沸，撇去浮沫。再加上适量清水，沸后再撇去浮沫，随后把羊油放入稍煮片刻，再撇去一次浮沫。将花椒、桂皮、陈皮、草果、良姜、白芒等用纱布包起成香料包，一同与姜片、葱段、精盐放入锅内，继续用旺火煮至羊肉八成熟时，加入红油。花椒水，煮约二小时左右即成。此时汤锅要始终保持滚沸，捞出煮熟的羊肉，切成薄片，放入碗内，撒上香菜末即成肉汤。可将辣椒油、大葱段装味碟，荷叶饼装', '投料考究，制作精细，调味丰富，汤汁乳白，不腥不膻，香醇不腻，味道鲜美，并有补虚壮阳，温中暖下之功能。', NULL, NULL, '羊肉500克、花椒5克、桂皮5克、陈皮5克、香菜50克、草果5克、良姜10克、白芷5克、葱10克、精盐15克、红油25克、花椒水15克、丁香面5克、桂子面5克、酱油5克、芝麻油25克。');
INSERT INTO `recipe` VALUES (2756, 71, '冬菇烧蹄筋', '1.蹄筋洗净截成3厘米的段，切成两片，两头划刀口，芸豆、蒲菜切成3厘米长的段。 \r\n2.炒锅内加清水，置中火烧开后下入蹄筋，加酱油（5克）推搅，煮开时捞出，挤净水分放入盘内。 \r\n3.炒锅放中火上，加入熟猪油，烧至六成热时，放入葱末爆锅，再倒入酱油、清汤、蹄筋、芸豆、蒲菜、水发冬菇、精盐。煨烧5分钟后撇去浮沫，用漏勺将原料捞在大盘内。装盘时，将蹄筋放在中央，冬菇围在四周。 \r\n4.炒锅内余汤重新烧开，加入湿淀粉勾薄芡，加上绍酒、味精、花椒油；然后浇在冬菇蹄筋上面，再放上刻好的蛋糕即成。', '冬菇清素淡雅，蹄筋鲜醇软糯，荤素兼备，各扬其长。蹄筋乳白，放置中央，冬菇深褐，围在四周，典雅美观、营养丰富，多为宴席菜品之一。', NULL, NULL, '水发冬菇50克，水发猪蹄筋250克，芸豆40克，蒲菜40克，雕刻好的蛋糕1片、清汤125克，酱油10克，葱末10克，姜末8克，绍酒4克，味精3克，精盐4克，湿淀粉5克，花椒油10克，熟猪油25克。');
INSERT INTO `recipe` VALUES (2757, 71, '炖加吉鱼', '将加吉鱼去头，取出内脏，刮净鳞，去净鳍、尾，洗净，剁成大骰牌块。猪肉冬笋切成片。鹿角菜泡开择净。鸡蛋打碗内搅匀。炒锅内加花生油烧至七成热时，将鱼块沾上面粉，裹上鸡蛋液，入油中炸至八成熟捞出。另起油锅放入猪油烧热，用葱段、姜片煸炒几下，加入肉片、冬笋、鹿角菜煸炒，用绍酒一烹，加入酱油、清汤、食醋、精盐、味精烧开撇去浮沫，将鱼块投入锅内用小火炖约10分钟，淋上芝麻油盛入汤盘中，撒上葱丝香菜段即成。', '鱼块香嫩，汤汁醇鲜，营养丰富。', NULL, NULL, '鲜加吉鱼800克 、猪肉100克、鹿角菜50克、冬笋75克、鸡蛋100克、香菜段50克、面粉50克、花生油150克、葱15克、姜15克、绍酒25克、酱油10克、清汤250克、食醋20克、精盐5克、味精1克、芝麻油10克。');
INSERT INTO `recipe` VALUES (2758, 71, '番茄松鼠鱼', '将黄鱼去鳞、鳃、内脏，用清水洗净。以胸腹鳍处下刀，将鱼头切下，然后再从下颌处下刀，将鱼头劈半刀，用刀略拍，剔下两面鱼肉，除净胸部细刺，鱼尾相连，在鱼肉面切上宽麦穗花刀，连同鱼头用绍酒、精盐腌渍入味。葱头、冬菇、玉兰片、胡萝卜均切成丁，放入沸水中汆过。炒锅内放入花生油，用中火烧至七八成热时，分别将鱼头、鱼肉沾匀精面粉，在鸡蛋黄液中拖过。做成松鼠形炸至金黄色；捞出装入鱼盘中。炒锅内留少量油，番茄酱下锅略炒，加葱头丁、冬菇丁、玉兰片丁、胡萝卜丁、青豆略炒，加绍酒、清汤、精盐、白糖、醋烧开，用湿淀粉勾成流芡，加入', '形象逼真，色泽鲜艳，鲜嫩酥香，酸甜适口。', NULL, NULL, '黄鱼750克、葱头20克、水发冬菇20克、水发玉兰片20克、胡萝卜15克、熟青豆15克、番茄酱100克、绍酒15克、精汤150克、清盐5克、白糖20克、醋20克、芝麻油2克、淀粉50克、鸡蛋黄15克、精面粉15克。');
INSERT INTO `recipe` VALUES (2759, 71, '粉蒸肉', '1.将肉切成长约10厘米，宽约5厘米、厚约0.4厘米的大片，共10片，然后放入葱姜丝、酱油、甜面酱、糖色、料酒拌匀，将肉腌约1小时。\r\n2.将大米中放入八角、桂皮、花椒，用小火炒至微黄时取，将米碾碎（不要太碎），然后把米放入盆中，倒入开水（水要没过大米），浸闷至水全部浸入米中备用。 \r\n3.把闷好的大米和腌过的肉再一起拌匀，腌约半小时，然后将肉皮朝下，放入大碗内（排列成鱼鳞形），把剩余的的米和汤汁浇在肉上面，上笼蒸约2小时（肉已料），取出扣入盘中即成。', '肉烂香醇，肥而不腻。', NULL, NULL, '带皮猪五花肉350克，大米50克，葱姜丝25克。甜面酱40克，酱油50克，料酒10克，糖色少许，八角、桂皮、花椒共15克。');
INSERT INTO `recipe` VALUES (2760, 71, '凤尾金鱼', '将黄鱼刮去鱼鳞，除去鱼鳃、内脏洗净剁下头尾，批下鱼身的两片，剥去鱼皮，再劈成24片长5厘米、宽33厘米、厚0.33厘米的鱼片。平铺在盘内，鱼头剁断，从里面砍一刀（不要砍断）用刀拍扁，将鱼头、尾分别摆在鱼盘前后两端。用精盐、味精、绍酒、葱椒水撒在鱼片，鱼头、鱼尾上稍腌。冬菇丝、冬笋丝、火腿丝掺在一起，加精盐、味精、绍酒拌匀入味，分别卷入鱼片内，三丝要露出1/3作成“凤尾”。然后把“凤尾”向着鱼尾部整齐地摆在鱼首、尾间，上笼旺火蒸30分钟至熟取出。炒锅内加清汤，放入精盐、味精、绍酒烧开撇去浮沫，放入湿淀粉勾稀', '造型新颖，制作精细，口味鲜咸，质地软嫩。', NULL, NULL, '黄鱼750克、冬菇20克、冬笋20克、火腿15克。 精盐5克、味精4克、绍酒15克、葱15克、花椒10克、湿淀粉50克。');
INSERT INTO `recipe` VALUES (2761, 71, '干炸丸子', '1.猪肉末加入葱姜末、料酒、鸡蛋液、盐、酱油、黄酱、水淀粉搅拌均匀。花椒稍炒擀末，制成椒盐。\r\n2.锅放油烧六成热，将肉馅挤成个头均匀的小丸子，入锅炸至金黄色捞出。用勺将丸子拍松，再入锅炸，反复炸几次，至焦脆捞出装盘，上桌时，随带椒盐味碟供蘸食。', '外焦里嫩，干香适口。', NULL, NULL, '肥瘦猪肉末250克，黄酱少许，酱油1克，葱末，姜末各2克，鸡蛋1个，水淀粉100克，盐2克，料酒10克，清油500克(实耗约50克)，花椒适量。');
INSERT INTO `recipe` VALUES (2762, 71, '锅烧肘子', '将肘子放入水锅内，用旺火焯水洗净，切成1.5厘米厚的大片，皮朝下摆入盛器内，加酱油、绍酒、葱段、姜片入笼蒸熟取出，沥净汤汁。加湿淀粉、鸡蛋、绍酒、酱油调成糊。将一半糊平摊在平盘内，肘子皮朝下摆在糊的上面，再将剩余的糊均匀地倒在肘子上面。炒锅置火上，下油烧至七成热（175 度）时，用小火将挂糊的肘子，推入油内炸至起壳时，捞出，再将油锅用旺火加热，待油温升至八成热（约200度）时，再移至小火炸至肘子表面呈金黄色，内熟外脆时，立即捞出沥净油，用刀切块装盘，上面撒上花椒面，另带3厘米长的葱段一碟，甜面酱，花椒盐各', '色泽金黄，外焦里嫩，干香无汁，肥而不腻。', NULL, NULL, '猪前肘（带皮去骨）500克、酱油10克、绍酒15克、姜片15克、鸡蛋2个（重约100克）、湿淀粉50克、花生油2500克。');
INSERT INTO `recipe` VALUES (2763, 71, '海米珍珠笋', '1.珍珠笋洗净，用刀切成3.5厘米长的滚刀块，入开水锅中焯过，捞出沥干水分。 \r\n2.将芝麻油放入炒锅内烧至九成热时，将花椒入油内炸至焦黑捞出，放入姜末炸出味，烹绍酒，放入珍珠笋块、海米、清汤、精盐，颠翻出锅装盘即成。', '珍珠笋清香鲜嫩，脆美殊爽，而且富于营养，齐鲁古地早就用此入馔为肴。因其季节性强，应市时间短，故尤为珍贵 “海米珍珠笋”，是以珍珠笋为主料，配以海米，用芝麻花椒油烹制而成。成菜清爽鲜嫩，味香诱人。', NULL, NULL, '珍珠笋350克、水发海米30克。花椒5克，姜末3克，绍酒5克，清汤25克，芝麻油35克。');
INSERT INTO `recipe` VALUES (2764, 71, '翡翠虾环', '1.青虾洗净、黄瓜洗净切成圆片、中间捅一小孔，穿在虾尾上，装盘。 \r\n2.将虾环入8成热油锅中一汆即倒出余油；香油烧至5成热，用花椒炝锅，放入姜末、料酒、高汤、精盐、虾环，翻炒装盘即成。', NULL, NULL, NULL, '大青虾500克、嫩黄瓜200克、花椒、姜末、料酒、高汤、精盐。');
INSERT INTO `recipe` VALUES (2765, 71, '凤凰鱼翅', '将雏鸡冲洗干净，从脊背劈开，胸部相连，拍断骨骼，鸡的表面抹上酱油，放入旺火热油中炸至皮金黄色时捞出，皮朝下，放入大碗内，加入葱段、姜片、八角、花椒、绍酒、清汤、白糖、酱油，放入笼内蒸烂，滗出原汤，去掉葱姜、八角，将鸡扣入鱼盘内，把鸡翅，腿伸开，把鸡头竖起，鸡嘴上嵌一朵香菜叶。冬笋切成标枪头型。再将鱼翅放入开水中氽一下，捞出沥净水，摆成蓑衣状放入平盘内。汤锅内加入蒸鸡原汤、清汤、酱油、精盐、绍酒。将码好的鱼翅原样推入锅内，加入冬笋，微火扒透，用湿淀粉勾流芡，淋葱椒油，起锅顺披在鸡身上。汤锅内放入清汤，精盐烧', '料讲究。制作精细，注重火候，成菜造型美观，酥烂鲜香，色泽红润，芡汁醇厚。', NULL, NULL, '冰发鱼翅200克、净雏鸡200克。净冬笋30克。葱段、姜片各10克、精盐7.5克、酱油15克、绍酒25克、湿淀粉25克、味精5克、清汤250克、葱椒油50克、白糖15克、八角1克、花椒1克、花生油1000克（实耗油75克）。');
INSERT INTO `recipe` VALUES (2766, 71, '干炸赤鳞鱼', '1.用利刀将鱼腹剖开，取出内脏，冲洗干净，放入盛器内加花椒、精盐、葱椒绍酒拌匀，腌渍10分钟取出，沾匀一薄层干细面粉。 \r\n2.炒锅内放入花生油，中火烧至七成热时，将鱼投入改微火炸至淡黄色捞出。吃时蘸花椒盐。', '鱼体呈弓形，颜色淡黄，外焦酥，里鲜嫩，蘸以花椒盐佐食，风味尤佳。', NULL, NULL, '活赤鳞鱼16条（重约250克），葱椒绍酒20克，花椒16粒，精盐3克，面粉750克，花椒盐5克，花生油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (2767, 71, '锅烧鸭', '将净雏鸭洗净，从脊背处割开，去鸭嘴，放锅内加水烧开，煮至八成熟捞出。剔去鸭骨（保留鸭掌），放大碗内，加酱油、清汤、葱段、姜片、精盐、绍酒，八角、桂皮、花椒，切下鸭头、鸭掌备用。将鸭蛋打入碗内，加面粉、淀、粉和适量清水调成浓糊，将一半倒在平盘上抹平，把鸭子皮面朝上放在盘内的糊上，再把碗内的蛋糊均匀地抹在鸭面上。炒锅加花生油烧至八成热（约200度）时，把鸭子从盘中慢慢拖进油内，炸两面均为金黄色时捞出沥油，然后改切长4厘米、宽25厘米的长条，再将鸭头剁两半，连同鸭掌一并摆成原形，装盘即成。', '外香脆，肉鲜嫩，造型美观，色味俱佳。', NULL, NULL, '净雏鸭750克 、肥肉丝30克、鸭蛋一个、酱油10克、葱段10克、姜10克、精盐4克、绍酒20克、八角5克、桂皮3克、花椒3克、面粉25克、淀粉25克、花生油1000克。');
INSERT INTO `recipe` VALUES (2768, 71, '椒盐肘子', '1.在开水锅中将猪肘子煮烫紧透后，捞出，用凉水泡上，把污毛刮洗干净。把糖色在皮上涂抹一层放在盆内。加进料酒、酱油、花椒、大料、盐，再上屉蒸约2小时，把肘子蒸烂取出，控去水分。 \r\n2.炒勺将植物油烧至8成热而后下入肘子，炸至皮酥脆时捞出沥油，切成一字条摆放在盘中。花椒盐装碟随肘子上桌。', '外酥里嫩，味香咸鲜。', NULL, NULL, '猪肘子1个（约1.5公斤左右），植物油2公斤（实耗约50克），料酒、酱油各15克，花椒，大料各5克，盐7克，花椒盐10克，糖少许，鸡汤2.5公斤。');
INSERT INTO `recipe` VALUES (2769, 71, '荠菜鱼卷', '黄花鱼肉、猪肥肉膘切成长3.5厘米、宽厚均0.3厘米的丝。荸荠去皮后切细丝，荠菜洗净，放入开水中一汆，再放入冷水中晾凉后，取出挤干水分切成末。将以上原料放入盛器中，加入葱末、姜末、鸡蛋清、绍酒、芝麻油、湿淀粉、精盐、用筷子搅成馅。鸡蛋清加湿淀粉调成蛋清糊待用。油皮去硬皮成两块，平铺在案板上，放入馅卷成直径1.6厘米的卷，用蛋清粘住油皮卷的边，然后将鱼切成3.3厘米的段。用面粉、发酵粉和清水调成糊。炒锅内放入花生油，中火烧至七成热（约175度）时，将鱼卷蘸匀面糊，逐个投入油内炸至金黄色即成。外带花椒盐碟上桌', '外焦里嫩，鱼肉鲜美，荸荠清香。', NULL, NULL, '黄鱼750克、荠菜20克、荸荠25克、猪肥肉丝30克、葱10克、姜15克、鸡蛋清15克、绍酒15克、芝麻油10克、淀粉50克、花生油200克、花椒10克、精盐5克。');
INSERT INTO `recipe` VALUES (2770, 71, '清汤柳叶燕菜', '将燕窝用温水洗净，放入九成热的水中扣焖3小时取出，用摄子摘去燕窝内的绒毛杂质，用清水洗净，再放入碱水中泡发1小时，待涨后，用温水漂洗碱液，撕成丝状，放入清水浸泡。火腿切成小象眼块，其余的切成细丝待用。取羹匙12个，里面抹上一层薄油，把鸽蛋分别打入羹匙内，两个分别摆入火腿片和香菜叶，上笼蒸熟取出，即成“柳叶鸽蛋”。捞出燕窝控净水分，炒锅内加入清汤。烧沸后，放入燕窝一焯即可，捞出放在汤碗内，上面摆上细火腿丝和香菜叶，再放上蒸好的柳叶鸽蛋，摆在燕窝的四周。另用净锅倒入清汤，加入精盐、绍酒，烧开后撇净浮沫，加入味', '燕窝色白如雪，鸽蛋形似柳叶，汤清澈晶莹，口味鲜醇。', NULL, NULL, '白燕窝100克、火腿50克、鸽蛋100克、香菜25克、精盐5克、绍酒10克、味精1克。');
INSERT INTO `recipe` VALUES (2771, 71, '荷花金鱼虾', '将虾尾剥净壳留尾，去尽泥肠洗净，从脊背处劈成合页形，在肉上剖十字花刀，用绍酒、精盐、腌渍入味，把鱼肉、猪肥肉一起剁成细泥，加精盐、绍酒、葱姜汁、清汤及鸡蛋清搅匀成馅，分别抹在虾肉面上。火腿、黄蛋糕、青菜、紫菜均切成鱼鳞状，连同豌豆在虾肉上点缀成金鱼形，西红柿割成荷花瓣形去掉瓤。将汆下的鱼肉馅堆放在盘中呈小扁圆形，顶部点缀上豌豆。成为荷花心，将西红柿瓣插在鱼泥上呈荷花状，连同金鱼虾上笼旺火蒸熟，取出滗净汁，金鱼虾整齐地围摆在荷花四周，锅内放入清汤、精盐绍酒、烧沸，撇净浮沫，用湿淀粉勾成溜芡，淋上芝麻油，浇在', '造型生动，色盛形美，口味鲜嫩。', NULL, NULL, '鲜对虾250克、净鱼肉200克。猪肥肉100克、火腿50克、黄蛋糕15克、青菜25克、豌豆15克、紫菜25克、红辣椒皮15克、西红柿25克。精盐3克、绍酒30克、葱姜汁15克。');
INSERT INTO `recipe` VALUES (2772, 71, '荷花鱼翅', '将鸡茸放入碗内，加上鸡蛋清、湿淀粉、精盐，拌匀成鸡料子。选汤匙12把（大号6把，中号6把）调匙内抹上熟猪油，再抹上鸡料子，至平为止，汤匙顶端撒上火腿末，成莲花瓣形。将剩余鸡料子加入菠菜汁，呈绿色，取酒盅一个，内抹油，再抹上绿色鸡料子，用海米均匀地插入，做成莲蓬，将莲蓬、莲花瓣入笼用微火蒸8一12分钟。熟后取出。锅内加入清水烧沸，将软鸡蛋用细针扎一小眼。将鸡蛋液轻轻挤入沸水内，成鸡蛋黄丝，待漂起后捞出，鸡蛋糕修成圆形，成一个蛋糕托，放入鱼盘的一端，把蒸熟的莲花瓣、莲蓬分别取出，放在蛋糕托上拼成荷花状，把鸡蛋', '造型美观，鱼翅软烂，“荷花”细嫩，汤鲜味美。', NULL, NULL, '水发鱼翅200克 、鸡茸30克、水发海米30克、火腿末20克、黄蛋糕50克、软皮鸡蛋1个、熟鸡、猪肉各20克、鸡蛋清20克、淀粉30克、精盐2.5克、熟猪油5克，菠菜汁10克、葱、姜各10克。');
INSERT INTO `recipe` VALUES (2773, 71, '荷叶肉', '1.将肉切成长约10厘米、宽约5厘米、厚约0.4厘米的大片，共10片。然后放入葱姜丝、酱油甜面酱、糖色、料酒拌匀，将肉腌约1小时。\r\n2.荷叶用开水烫过，把荷叶反面的大盘用刀片掉，修正后备用。大米放入药料（八角，桂皮，花椒），用小火炒至微黄时取出，将米碾碎（不要太碎），然后把米放盆中，倒入开水（水要没过大米），浸闷好备用。\r\n3.把闷好的大米和肉拌匀，再腌约半小时。取荷叶铺平，光面朝下，每张放上腌好的肉一块，包成小长包，共10包，放入碗内，上笼蒸至肉烂时取出，扣在大盘内即成。', '口味醇厚，风味独特，肉烂清香，肥而不腻。', NULL, NULL, '带皮猪五花肉350克，鲜嫩小荷叶10张，大米50克，葱姜丝25克，甜面酱40克，酱油50克，料酒5克，糖色少许，八角、桂皮、花椒共10克。');
INSERT INTO `recipe` VALUES (2774, 71, '红扒鱼翅', '将鱼翅用开水氽一遍，捞出用清水洗净；整齐地码在竹箅上呈扇面状，再盖上一个箅子，放入炒锅内，将母鸡，猪肘肉剁成大块，下锅用开水氽过，捞出洗净，火腿洗净与母鸡肉，猪肘肉同放锅内，加入清水，以浸过原料为宜，加姜片、葱段，用中火烧热，撇去浮沫，移至微火上焖2－3小时，将鱼翅取出。炒锅内放上花生油。用中火烧至七成热（约175 度），加姜片、葱段炸至金黄色捞出不用。烹入绍酒、酱、清汤、精盐。鱼翅烧沸，撇净浮沫，用小火煨10分钟，用湿淀粉勾成溜芡。加花椒油、味精、鸡油翻锅，盛入盘中即可。', '汤汁浅红发亮，鱼翅软烂糯滑，味道鲜美。', NULL, NULL, '水发鱼翅250克、净母鸡肉200克、猪肘肉、火腿各50克、姜片15克、葱段20克、精盐10克、绍酒35克、味精12克、清汤250克、湿淀粉25克、花椒油15克、花生油100克。');
INSERT INTO `recipe` VALUES (2775, 71, '红烧甲鱼', '活甲鱼宰杀洗净，放在沸水中烫过，刮去黑皮，斩去爪尖，洗净，再放入开水锅内稍煮、能揭开盖时捞出、放在凉水盆中，将盖和裙边一起撕下，去肠留肝和胆，然后将头、脖、四肢拆开、鸡腿肉，猪肉切成2.5厘米见方块，一同放在盆内，葱白切段，姜切片、待用。炒锅内加入熟猪油，中火烧至五成热（约110度），下入葱、姜炸至黄色时加入黄面酱炸透，下入花椒、八角、鸡肉和猪肉略炒，放入酱油。绍酒、清汤，沸后用中火烧10分钟撇去浮沫，倒入砂锅内，然后放入甲鱼及头、脖、四肢摆成整形。再放上甲鱼肝、胆、盖上砂锅盖，放入小火上烧至酥烂时，剔去', '汤鲜味醇，肉细质嫩，营养丰富。', NULL, NULL, '甲鱼750克 、肥嫩鸡腿肉50克、猪腱子肉25克、黄面酱25克、葱15克、姜片10克、花椒5克、八角5克、熟猪油100克、酱油10克、绍酒15克、清汤100克。');
INSERT INTO `recipe` VALUES (2776, 71, '红烧鱼唇', '将鱼唇改刀成2.5厘米长、宽1厘米的片，下入锅内用开水氽透，捞出用清水漂净。如此反复两遍。炒锅内加清汤、葱段、姜片、绍酒，加入鱼唇用微火烧开，撇尽浮沫，再用微火煨10分钟左右，捞出控净汤汁。炒锅内加入花生油。中火烧至六成热（约150度），放入葱段、姜片煸至金黄捞出不用，再放入绍酒烹后，加酱油、清汤，白糖、糖色、味精、精盐、鱼唇烧开，撇去浮沫，用慢火煨5分钟，再用淀粉勾成熘芡，淋上鸡汤、芝麻油，炒匀盛出装盘即成。', '质地糯软味美，滑润鲜香。', NULL, NULL, '水发鱼唇150克、精盐7.5克、酱油10克、葱段、姜片各15克、白糖12克、糖色5克、味精5克、绍酒25克、清汤250克、花生油50克、芝麻油10克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (2777, 71, '葫芦大吉翅子', '1.鸡脯肉与肥肉膘制泥，加蛋清、高汤搅匀成鸡料子，水发鱼翅洗净，加高汤、葱段、姜片、花椒蒸2小时取出。 \r\n2.将大平盘上抹油，把鸡料子在盘内制成12个葫芦形状，海米安在葫芦顶端，蒸10分钟取出。 \r\n3.炒勺内放入花椒油，三套汤、调料及鸡料子制成的葫芦，慢火烧5分钟后捞出装碗，再将鱼翅放勺内烧开，勾芡，盛入盘中央，葫芦摆四周即成。', NULL, NULL, NULL, '水发鱼翅500克，母鸡脯肉250克，猪肥肉膘50克，水发大金钩海米40克，鸡蛋清3个。');
INSERT INTO `recipe` VALUES (2778, 71, '蝴蝶海参', '将每个海参顺长劈成3片（共12片），在沸水中烫过，捞出用洁布搌干水分，修成蝴蝶形状，鱼肉在凉水中浸泡10分钟，捞出斩成细泥，放入碗内，加精盐、味精、清汤、鸡蛋清搅匀成鱼料。将海参皮面朝上放入盘内，海参中间撒上干淀粉，然后用鱼料做成蝴蝶身躯横放在海参中间，用黑芝麻在头端作蝴蝶“眼”，按上火腿丝作蝴蝶嘴，鱼翅作须。冬菇、鸡蛋皮、黄瓜皮均切成长2厘米、宽0.2厘米的丝，分色摆在蝴蝶身上，制成12只“蝴蝶”，上笼蒸熟取出。炒锅内放入清汤，置旺火烧沸，撇净浮沫，加绍酒、精盐、味精、酱油，调好口味，倒入汤碗中，将蒸熟', '形状美观，清澈见底，味道鲜醇，食之清新爽。', NULL, NULL, '水发海参250克 、鱼肉30克、水发鱼翅20克、水发冬菇25克、熟火腿、黑芝麻各适量 、精盐5克、酱油10克、味精0.5克、绍酒10克。');
INSERT INTO `recipe` VALUES (2779, 71, '换心乌贼', '乌贼去墨袋，软骨，入沸水中氽透，虾仁，里脊肉，肉皮冻切成 0.3厘米的丁，加盐，酱油，味精，料酒，葱姜末调匀。鱼肉制成茸加盐，料酒，味精调好味。乌贼肚内放入调好的料，用鱼馅封口，上笼蒸熟后摆盘。勺内加原汤勾芡淋明油浇在乌贼上。', '外形美观洁白，鲜香无比。', NULL, NULL, '乌贼，虾仁，里脊肉，肉皮冻，鱼肉，酱油，葱姜末，明油，盐，味精，料酒，湿淀粉。');
INSERT INTO `recipe` VALUES (2780, 71, '烩肥肠', '1.将肥肠斜刀切成片，用开水氽透后控去水分。 \r\n2.将葱姜油烧热，把蒜末炸出味时，烹入料酒、酱油、鸡汤、味精、盐，并将肥肠放入汤内用旺火把汤烧开，撇去浮沫，用调稀的湿淀粉勾成流芡，撒上香菜末即成。', '蒜香味浓，肥肠松软。', NULL, NULL, '猪熟肥肠500克，酱油6克，盐5克，葱姜油76克，味精5克，料酒15克，干淀粉75克，鸡汤1.5千克，香菜末50克，蒜末20克。');
INSERT INTO `recipe` VALUES (2781, 71, '烩乌龟蛋', '1.乌龟蛋用水煮开，换水再煮，煮几次后剥去壳再煮，捞出。\r\n2.将乌龟蛋、香菜放盘中，加盐、味精、料酒、葱丝、姜丝，拌和均匀。\r\n3.锅上火，加入高汤，烧开后勾玻璃芡，将拌好的乌龟蛋倒入芡汤锅中，烧开后，淋香油，撒胡椒粉即成。香菜、胡椒粉亦可不入锅，上桌时装味碟备用。', '酸咸辣鲜，适口不腻。', NULL, NULL, '乌龟蛋150克，香菜末2克，胡椒粉2克，葱末、姜末各2克，盐2克，料酒10克，味精10克，醋50克，大油50克，香油5克，水淀粉60克，高汤适量。');
INSERT INTO `recipe` VALUES (2782, 71, '火爆燎肉', '将猪肉洗净，切成长4.5厘米、宽2.5厘米，厚0.2厘米的片，放入碗内加入葱丝、姜丝、蒜片、酱油、甜面酱、绍酒一起拌匀浸渍一下，炒锅放花生油，用旺火烧至九成热（约225度）的热油、火苗沿锅边直上，引燃了锅内的油，迅速将浸渍的肉片倒入，用手勺拨匀，将炒锅颠翻几下，使肉片在热油中炒燎，使之成熟，装盘即成。', '色紫红，肉香嫩，味美而带焦味，用大葱甜面酱佐食，富有地方特色。', NULL, NULL, '猪臀尖肉400克 、大葱10克、生姜10克、蒜瓣5克、酱油10克、甜面酱15克、芝麻油6克、绍酒20克、花生油150克。');
INSERT INTO `recipe` VALUES (2783, 71, '鸡丝蜇皮', '1.将鸡脯肉切成麻线般粗的细丝。海蜇皮切成丝，反复用清水淘洗干净。香菜洗净，切成寸段。\r\n2.将鸡丝用少许盐、蛋清、水淀粉挂浆。小碗中放入清汤、料酒、盐、味精、胡椒粉，对成汁备用。海蜇丝用热水烫一下，沥去水分。\r\n3.炒勺内放入白油、烧至四成熟时，放入鸡丝，用铁筷子拨散，将油滗出，勺内留油少许，放入葱姜丝炝锅，烹入醋，放入海蜇丝、香菜段，快速倒入对好的汁，颠翻均匀出勺即成。', '蜇皮脆嫩，鸡丝滑软，清鲜爽，风味独特。', NULL, NULL, '鸡脯肉200克，水发海蜇皮300克香菜2棵，葱姜丝共25克清汤40克，料酒10克，味精1克，胡椒粉2克，精盐1.5克，醋10克，水淀粉20克，鸡蛋清1个，白油750克（约耗40克）。');
INSERT INTO `recipe` VALUES (2784, 71, '煎雏肉', '将猪里脊肉片切成0.3厘米厚的大片，剖上深度为肉片厚度1/2的十字花刀，再改切成象眼块，用鸡蛋清、精盐、湿淀粉腌渍待用，取炒锅，加入花生油，上中火烧至六成热（约150度）时，将肉片下锅滑熟倒入漏勺沥油原锅留有少量油，用中火烧热，加葱、姜丝、玉兰片丝煽炒几下，再加入清汤，白糖、酱油、精盐、味精、绍酒、豌豆烧开，用湿淀粉勾成芡，倒入肉片，加入花椒油翻锅盛出装盘即成。', '肉质鲜嫩，芡汁红亮，口感香醇而略带麻辣。', NULL, NULL, '猪里脊肉（当地俗称“雏肉”）200克、玉兰片50克、豌豆30克、葱姜丝15克、精盐4克、酱油2克、味精0.5克、绍酒15克、白糖20克、花椒油、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (2785, 71, '酱汁鸭方', '净雏鸭冲洗净，剁嘴留舌，去脚爪。砸断大骨，放开水锅内加葱段、姜片、花椒，煮至六、七成熟取出，放在菜墩上，脯朝底剁去头、脖、腿、拆去鸭骨，四周修整，顺鸭脯中间剁为两半，再横刀剁成约2.6厘米的长方块，摆成原样。毛芋头煮熟，剥去皮，削净黑点，切滚刀块。将鸭原样铲入锅垫上，再放入毛芋头、鸭脖、腿，骨，放入锅内置火上，加煮鸭汤（以没过鸭子为宜）、绍酒、酱油、白糖。另取炒锅加入花生油、白糖，炒至嫩色放甜面酱炒熟，加煮鸭原汤，溶化后倒入鸭锅内。净炒锅加花生油少许，放中火上烧六成热（约150度），加葱段、姜片、花椒炸至', '颜色油红光亮，酱香四溢，鲜嫩适口，咸中略甜。', NULL, NULL, '净雏鸭750克、毛芋30克、绍酒20克、酱油10克、白糖20克、花生油60克、葱10克、姜10克、花椒5克、甜面酱20克。');
INSERT INTO `recipe` VALUES (2786, 71, '京酱肉丝', '1.肉洗净切细丝，加入料酒、盐、鸡蛋液、淀粉上浆。\r\n2.葱少许切片，其余切细丝，码在盘中。姜片略拍松，连同2克葱片放碗内，加少量清水制成葱姜水。\r\n3.锅上火，放油烧热，倒入肉丝滑散至八成熟捞出控油。锅留底油，加入甜面酱略炒，加葱姜水、味精、白糖翻炒，待白糖炒化、酱汁变粘时放入肉丝，翻炒使酱均匀裹在肉丝上即可出勺。', '咸香略甜，菜色枣红。', NULL, NULL, '猪瘦肉250克，葱白250克，甜面酱80克，料酒、味精、盐、姜片、淀粉各适量，白糖20克，鸡蛋1个，清油150克。');
INSERT INTO `recipe` VALUES (2787, 71, '九转大肠', '1.将大肠切成2厘米的段，香菜切1厘米的小段。\r\n2.勺内加入清水，开后放入大肠氽透，捞出沥净水份。炒勺上火加熟猪油。\r\n3.将白糖炒至发红时，放大肠上色，下入葱姜蒜未，烹上醋、料酒，加入清汤，开后下盐、白糖、味精、三种药材的一半。\r\n4.然后移至小火，烧至汤汁发浓时，加入。', '色泽红亮，五味俱全，是山东名菜。', NULL, NULL, '熟大肠750克、熟猪油、花椒油、清汤、白糟、盐、味精、料酒、葱、姜、蒜、香菜、醋、胡椒面、砂仁、肉桂、豆蔻各适量。');
INSERT INTO `recipe` VALUES (2788, 71, '烤花揽桂鱼', '将桂鱼刮去鳞，剁去脊鳞，从口中取脏，冲洗干净，再入开水中一烫，速放进凉水里，刮去黑皮。用刀把鱼嘴、下巴划开，两面打坡刀，置于盘中，加绍酒，精盐、葱段、姜片、花椒液渍约15分钟，入味备用。将鸡里脊肉剔去筋，然后和猪肥肉膘一起剁成细泥，加鸡蛋清、绍酒、精盐搅成鸡料子备用。猪五花肉切成0.7厘米见方的丁，放入开水锅中汆熟捞出备用，海参、冬笋、冬菇均切成0.7厘米见方的丁，和干贝一起用毛汤汆过捞出，与肉丁混合加绍酒、精盐、腌渍三分钟，火腿切成6厘米长。2厘米宽、0.3厘米厚的片。猪花网油洗净劈去厚筋，面粉和清水调', '营养丰富，外香里嫩，自中泛红，味道特鲜，佐以姜末、香醋，味道更佳。', NULL, NULL, '鲜桂鱼750克、鸡里脊肉25克、猪肥膘肉50克、水发干贝20克、水发海参15克、冬笋15克、冬菇20克、火腿15克、猪五花肉20克、猪网油20克、绍酒20克、精盐5克、葱段15克，姜片10克、花椒10克、香醋20克。');
INSERT INTO `recipe` VALUES (2789, 71, '烤牌子', '1.将料酒、精盐加温水化开；蜂蜜加适量清水。 \r\n2.将硬肋肉去掉奶泡肉，放开水锅煮8分钟取出，抹上蜂蜜后放炭火上烤，烤一会儿刷一次料酒、盐水，烤2小时呈金黄色取下。 \r\n3.用刀将烤好的牌子贴排骨从中间片成两块；把带骨的部分剁成块，皮朝上整齐地排入盘内即成。', NULL, NULL, NULL, '猪硬肋带皮1块约2000克、蜂蜜、料酒、精盐。');
INSERT INTO `recipe` VALUES (2790, 71, '烤网油签', '将猪瘦肉切成长4.5厘米、宽0.2厘米、厚0.2厘米的丝，加葱、姜丝、酱油、鸡蛋清、清汤、绍酒、味精拌匀。猪网油切成长30厘米、宽15厘米，共切3块，把拌好的肉丝均匀地分成3份，分别放在网油上包成长20厘米、宽4厘米的“网油签”。用湿淀粉和蛋清调成糊，沾住“油签”封口处，放入烤箱内用旺火烤20分钟，烤至两面金黄色时取出。再改切成小长方块，整齐地摆在盘内即成。', '外皮酥脆，肉嫩味香，造型整齐。', NULL, NULL, '猪瘦肉200克、猪网油100克、酱油6克、味精0.5克、葱姜丝12克、鸡蛋清若干、绍酒10克、湿淀粉30克。');
INSERT INTO `recipe` VALUES (2791, 71, '烤小鸡', '将雏鸡从脊背中间劈开（腹部相连），再将鸡的大腿处割开一刀，翅膀用刀背拍断，去掉爪尖洗净。然后将鸡身沾匀酱油。入九成热（225度）的油锅中一触即出，放入烤盘内，加葱段、姜片，再倒入清汤、酱油、葱油等，入烤炉内烤10分钟左右至熟，呈金黄色时取出，剁成条状，按原鸡形状摆入盘内，浇入原汤。生菜洗净切段，放在鸡的两边佐食即成。', '颜色金黄，肉嫩淡香，用生菜佐食风味更佳。', NULL, NULL, '鸡1000克、生菜30克、精盐5克、酱油10克、葱油10克、姜10克、菜子油1500克（实耗油100克）。');
INSERT INTO `recipe` VALUES (2792, 71, '孔府一品锅', '1.海参片成抹刀片。鱼肚片成长5厘米、宽2厘米、厚0.3厘米的片。玉兰片片成长5 厘米、宽2厘米、厚0.2厘米的片。豌豆苗放入开水中烫过捞出，用冷水过凉。鱿鱼卷用鸡汤汆过备用。 \r\n2.取“一品锅”一只，将龙须粉、白菜墩、白煮山药放入锅内垫底，将白煮肘子、白煮鸡、白煮鸭分别摆在上面，再将海参、鱼肚、鱿鱼卷、玉兰片、鸡蛋荷包在各料间隔处摆成一定的图案，加入鸡汤、绍酒、精盐，用旺火蒸2小时左右取出，搭上豌豆苗上席即可。注意：鸡、鸭宰杀后清水洗净，要放入开水锅中略焯，去除血水。海参、鱼肚、鱿鱼均必须发透至软。要', '食物多样，用料珍贵，汤汁鲜美，白菜清口。', NULL, NULL, '水发海参50克，水发鱼肚150克，白煮肘子500克，白煮母鸡1 只500克，白煮鸭二只750克，水发鱿鱼卷10个150克，水发玉兰片25克，鸡蛋荷包10个，白煮山药段500克，水发龙须粉250克，蒸好的白菜墩150克，豌豆苗3根，精盐40克，绍酒150克，纯鸡汤1500克。');
INSERT INTO `recipe` VALUES (2793, 71, '栗子白菜', '1.将大白菜去根去帮，一破两半，切成7厘米长、1厘米宽的白菜条(菜根处要竖着切几刀使整个菜心相连)，每个栗子上用剪刀剪一个十字小口，放入锅内煮熟。捞出剥皮，栗仁一切两半。 \r\n2.将植物油放入锅内，烧热，投入白菜稍炸一下捞出，控净油。 \r\n3.猪油下锅，烧热，用葱花炝锅，下入高汤、酱油、料酒、味精、白糖、精盐、白菜、栗子，烧开，移微火上烧2-3分钟，使汤汁去掉一部分，栗子已焖烂，用水淀粉勾芡，淋上明油即成。', '味咸香，菜软烂，色金黄', NULL, NULL, '栗子100克，白菜300克 ，猪油25克，酱油10克，精盐2克，料酒5克，味精2克，白糖5克，水淀粉10克，葱花2克，姜水少许，植物油500克(实耗50克)。');
INSERT INTO `recipe` VALUES (2794, 71, '龙眼凤肝', '将肝洗净，在开水中氽过。切成1.7厘米宽的长条，用竹签在每一条鸭肝上划一小口，玉兰片切成长5厘米、厚0.7厘米的片，冬菇每个劈两半，均在沸水焯过。将大肠切成长2.5厘米的段，在沸水中汆过备用，将鸡茸入碗内加鸡蛋清。精盐、绍酒、湿淀粉、清汤、熟猪油，搅成茸泥，火腿切成0.1厘米立方粒；豌豆去外皮，用开水烫过备用。锅内放猪油少许，旺火烧至六成热（约132度）时入葱姜炸出香味，加入鸡肝，冬菇、玉兰片翻炒，随即放入酱油、清汤、绍酒、白糖待沸时移至微火上焖至熟透时，除去葱姜用湿淀粉勾芡，再加入葱椒油调匀，盛入腰盘内', '造型美观，鸭肝鲜嫩，猪肠软酥，一肴多味，富有营养。', NULL, NULL, '鸭肝150克、熟猪大肠75克、鸡里脊肉茸73克、水发冬菇、水发玉兰片各50克、火腿15克、青豌豆10克。精盐4克、鸡蛋清若干、绍酒10克、湿淀粉30克、酱油10克、葱椒油10克。');
INSERT INTO `recipe` VALUES (2795, 71, '落叶琵琶虾', '将河虾去壳留尾洗净。沥干水分，放入碗内，加精盐。绍酒腌渍入味，将腌渍好的虾放在干淀粉上，用擀面杖把虾逐个压成形似的琵琶的薄片，放入沸水中汆过，豌豆苗叶洗净用沸水焯过，炒锅内加入熟猪油，中火烧至六成热（约132度）时，放葱姜末炸出香味，加清汤绍酒、精盐、烧沸，放入虾片。烧1分钟后将虾片捞出盘内，锅内汤汁用湿淀粉勾芡烧沸放入姜汁、葱油搅匀，迅速倒在放虾片的盘中，再撒上豌豆苗叶即成。', '外型美观，鲜香味美。', NULL, NULL, '鲜大河虾250克、碗豆苗50克、熟猪油100克、精盐3克、绍酒20克、葱姜末15克。');
INSERT INTO `recipe` VALUES (2796, 71, '麻粉肘子', '将肘子肉刮洗干净，放入锅内煮至九成熟时，捞出切成长4.5厘米、宽2厘米、厚1厘米的块再放入锅内，加上葱、花椒、绍酒、精盐用旺火烧沸，用湿绿豆淀粉、芝麻酱慢慢倒入锅内，随倒随搅，煮沸后倒入碗内，晾凉后放入冰箱内冷却后即成麻粉，切成象眼块。再用炒锅，放入清汤、精盐烧沸勾芡，再放入葱椒、绍酒、姜汁、葱油调匀后即成“白玉卤”，晾凉待用，再用芝麻酱、清汤、精盐、醋蒜末搅匀成糊，均匀地抹在肘子上，再将肘子与制好的麻粉拌匀盛入盘内，核桃仁、毛豆、黄瓜丝摆在上面，鲜花椒用沸水烫一下，拍扁放在中间，浇上“白玉卤”即成。', '麻粉软滑，卤汁晶莹，肘肉透烂，肥而不腻，荤素搭配，清爽适口最宜夏令食用。', NULL, NULL, '猪肘子500克、核桃仁50克、白豆20克、黄瓜15克、葱10克、花椒5克、绍酒10克、精盐4克、姜2克、麻酱2克、醋3克、蒜5克。');
INSERT INTO `recipe` VALUES (2797, 71, '麻酱紫鲍', '将鲍鱼两面交叉剞直刀法，再切成宽1厘米的条，投入开水中一分捞出备用。炒锅内加花生油少许，用中火烧至七成热（约175度），放入葱姜末，炸出香味，随入鲍鱼、酱油、绍酒、白糖、精盐、清汤烧开，打去浮沫，加入芝麻酱、味精，用手勺搅匀，淋上鸡油即成。', '味鲜质嫩，具有浓郁的芝麻酱清香。', NULL, NULL, '水发鲍鱼肉200克、芝麻酱75克、酱油5克、绍酒20克、白糖、精盐、葱姜末各10克。');
INSERT INTO `recipe` VALUES (2798, 71, '玛瑙银杏', '白果砸碎，去外壳，煮约10分钟，搓去皮膜，上笼蒸制回软，放入干淀粉内，使其沾匀，放入七成热（约175度）油锅内，略炸捞出。待油温升高至八成热（约200度）时，再炸至微黄色时，捞出沥油，锅内放少许油，放入白糖炒至金黄色起泡时，迅速放入白果，颠翻匀后，撒上青红丝、芝麻仁、倒入抹好麻油的搪瓷盘内，用刀将糖液拉成片状，稍凉装盘即成。', '造型美观，外脆香甜，内韧软嫩，颜色晶莹剔透，别具一格。', NULL, NULL, '银杏250克、青红丝20克、芝麻仁15克、白糖20克、花生油250克、淀粉50克。');
INSERT INTO `recipe` VALUES (2799, 71, '梅花拼盘', '1.将猪肉松分为5份，在大圆盘中堆成5个花瓣的雏形。将腌胡萝卜、黄蛋糕、山楂糕、熟火腿、白蛋糕分别切成凤尾片，并依次由外向内在猪肉松上拼摆成5个花瓣，花瓣上端分别用半个红樱桃点缀。 \r\n2.将部分炝黄瓜切成椭圆片，沿花瓣中心拼摆成圆形，在黄瓜片中心用胡萝卜点缀成花蕊。 \r\n3.将炝黄瓜皮切成大菱形片，切上梳子花刀，用刀略压成凤尾形，共切5片，分别放在5个花瓣之间，成为花叶即成。', '色彩鲜艳，如盛开的梅花，形态美观大方。', NULL, NULL, '腌胡萝卜75克，黄蛋糕75克，山楂糕75克，熟火腿75克，白蛋糕75克，炝黄瓜100克，猪肉松400克，胡萝卜25克，红樱桃3个。');
INSERT INTO `recipe` VALUES (2800, 71, '焖大虾', '1.对虾洗净，去沙线、腿、须； \r\n2.葱段、姜片放入六成热油锅中炸，直至香味溢出，捞出葱和姜； \r\n3.下白糖、清汤、料酒和盐入锅，再放虾； \r\n4.大火烧开，小火煨至汤浓，淋香油上桌即成。', NULL, NULL, NULL, '大对虾12只约750克、葱段10克、姜片10克、白糖50克、清汤约100克、料酒、香油、花生油适量。');
INSERT INTO `recipe` VALUES (2801, 71, '蜜汁金枣', '山药洗净，上笼蒸熟去皮，用力压成细泥，加面粉拌匀，做成直径3厘米的圆饼，包入枣泥馅，做成枣状的丸子，在一端插上一根青梅条为枣蒂，外面滚上干面粉，放入六成热（约150度）油锅中炸成金黄色捞出沥油。炒锅加油，白糖，中火炒至呈红色，加水、白糖、蜂蜜、桂花酱炆成浓汁，倒入炸好的“金枣”，再炆半分钟，装盘即成。', '形似金枣，色泽金黄，甘香浓郁。', NULL, NULL, '枣泥150克，山药100克、青梅条20克，花生油50克、白糖20克、蜂蜜20克、桂花酱15克。');
INSERT INTO `recipe` VALUES (2802, 71, '蜜汁梨球', '1.梨去皮去核，切成丝； \r\n2.加鸡蛋清、淀粉、面粉调匀，制成丸子； \r\n3.将梨丸放入5成热油锅中炸至金黄色捞出，沥油； \r\n4.炒糖色，加清水、白糖、梨球慢火收汁，浓稠时放入蜂蜜； \r\n5.浇明油装盘即成。', NULL, NULL, NULL, '黄梨500克、蜂蜜100克、白糖200克。');
INSERT INTO `recipe` VALUES (2803, 71, '牡丹蝎托', '茶叶用开水泡透，待凉后将蝎子放入泡30分钟，取出沥净水后再放入姜，葱，盐，味精入味，将山楂糕放在面包片上，抹上蛋泡糊，再放上入味的蝎子。油五成熟时，放入蝎子炸至酥透，即可。', '造型美观，风味独特。', NULL, NULL, '蝎子，面包，山楂糕，鸡蛋，心里美萝卜，盐，味精，干淀粉，茶叶，姜，葱。');
INSERT INTO `recipe` VALUES (2804, 71, '奶汤棱桃仁', '将猪臀尖肉片成2.5厘米的大片，在两面每隔0.5厘米剂上交叉直刀纹，切成2.5厘米见方块，用鸡蛋清，湿淀粉、精盐上浆入味。火腿、菜心、冬菇、冬笋批成长3.5厘米、宽1.5厘米、厚0.3厘米的片。均放入沸水中焯一下备用。汤锅内放清水，烧沸后放入肉块，一沸即捞出，放入碗中加进葱段、姜汁、精盐、奶汤入笼蒸熟（这时肉呈核桃形）取出滗去汤汁，去掉葱段，扣入汤盘内。炒锅内放葱油，中火烧至七成热（约175度）时入奶白汤炒沸，放入菜心、香菇、冬笋、精盐、姜汁、葱椒、绍酒，用湿淀粉勾芡，浇在核桃肉上，将火腿片摆在上面即成。', '肉质鲜美，汤浓味醇。', NULL, NULL, '猪臀尖肉200克、菜心50克、水发冬菇50克、冬笋30克、火腿25克、葱段适量。精盐4克、姜汁7克、绍酒15克、鸡蛋清3个、湿淀粉50克。');
INSERT INTO `recipe` VALUES (2805, 71, '奶油扒菜芯', '1.先将白菜芯洗净，顺刀叶连着切成6瓣。 \r\n2.炒勺上火，放入鸡汤500克，加入盐、味精各2克，同时把白菜芯也放入汤内炖烂捞出，控去汤，顺着菜芯撕开，摆入盘中。 \r\n3.汤勺上火，将葱姜油烧热，烹入100克鸡汤和料酒，加入盐味精各2克，烧开后下入菜芯，小火煨5分钟后，再加入牛奶，汤开后用调好的稀淀粉勾芡，淋入少量葱姜油，大翻勺，再淋入鸡油即可。', '汤乳白鲜美，菜嫩烂可口。', NULL, NULL, '青口白菜芯3棵，牛奶100克，鸡油，料酒各15克，葱姜油25克，味精、盐各4克，湿淀粉25克，鸡汤600克。');
INSERT INTO `recipe` VALUES (2806, 71, '奶油桂鱼', '桂鱼刮鳞去鳃，挖去内脏，冲洗干净，鳍尖用刀切掉。将鱼放入八成开的水锅中稍烫，两面烫匀后，放入凉水中刮净黑皮，冲洗干净，沥于水分。剁开鱼的下唇骨，把鱼头向左，肚朝外平放在案板上。在鱼身上划成2厘米宽的坡刀口，翻过来划成象眼块，两面沾上干面粉，炒锅置中火上，加入熟猪油。烧至六成热（约132度）时，将鱼下锅炸至六成熟后，放入葱、姜炸至黄色时，加入奶汤、花椒、绍酒、八角、精盐，随后放入炸好的桂鱼，用旺火煮10分钟，至熟时捞出葱姜、花椒、八角不要。把鱼盛在大盘内，再倒入奶汤，鸡油，搭入冬菇、火腿、笋尖即成。', '汤如奶汁，鱼肉似玉，鲜香细嫩，增味提神。', NULL, NULL, '桂鱼l000克、盐笋尖50克、冬菇65克、火腿100克、干面粉30克、熟猪油250克、葱10克、姜15克、奶汤250克、花椒5克、绍酒20克、八角20克、精盐3克、鸡油10克。');
INSERT INTO `recipe` VALUES (2807, 71, '清汤鲍鱼', '1.将鲍鱼用刀切成斜片，鲜蘑切薄片，熟火腿切成小象眼片，豌豆苗留嫩叶、去根、洗净。 \r\n2.将鸡清汤300克倒入炒勺用旺火烧开，将熟火腿片、鲜蘑片、鲍鱼片和豌豆苗下勺煮透捞出，倒入10个汤碗中，勺中汤如不要可倒掉。 \r\n3.将余下的鸡清汤500克入勺上火，并加进盐、味精、料酒调好味，撇去浮沫，盛入10个小汤碗中即成。', '味鲜汤清，鲍鱼嫩软。', NULL, NULL, '罐头鲍鱼半桶，熟火腿15克，筒鲜蘑15克，豌豆苗15克、盐10克，料酒15克，味精3克，鸡清汤800克。');
INSERT INTO `recipe` VALUES (2808, 71, '清汤全家福', '将生鸡脯肉片切成薄片，用鸡蛋清、精盐、味精、湿淀粉拌匀，入沸水内氽过。水发海参片为长片。发好蹄筋一片两半，发好鱼肚、鸭肝均切成片。鸡、鸭腰煮熟撕去外薄膜，用刀片为两瓣。鸡腰切十字花刀。猪黄管划成蜈蚣状。水发鱼翅顺理整齐。均放入清汤内氽过。取一只大碗，把上面各种原料的剩汆碎料放入碗底，然后将猪黄管和鱼翅以外的各种原料均匀相间地摆入碗内，最后将火腿、水发玉兰片、冬菇、菜心相间地摆在上面封面，汤锅内放入清汤、精盐、绍酒烧开，将碗内摆好的形状原样推入漏勺内，将漏勺放入清汤内烫透。取出后，用手勺轻轻挤干水分，保持原', '选料精细，品种多样，集于一馔，色彩缤纷，软烂清香，汤鲜味美，营养丰富。', NULL, NULL, '鸡脯肉250克、鸡蛋清20克、水发海参20克、蹄筋25克、鱼肚25克、鸭肝20克、鸡腰20克、鸭腰25克、猪黄管20克、水发鱼翅20克、火腿20克、水发玉兰片20克、冬菇25克、精盐5克、味精3克、淀粉50克、清汤150克、绍酒15克。');
INSERT INTO `recipe` VALUES (2809, 71, '清蒸八宝甲鱼', '活甲鱼背部朝下，腹部朝上，待其伸出头，迅速用刀将其头砍下，提起控净血，放入开水锅内稍烫后，捞入冷水中，刮净黑皮，揭盖掏去内脏，剁去尖爪，用清水冲洗干净，放在大锅内。把海参、猪肉、蛋糕、冬笋、冬菇均切成0.6厘米见方的丁，连同干贝、莲子、糯米分别摆放在甲鱼胸腔内，再浇入用精盐、绍酒、清汤，甲鱼胆兑好的汁，把姜丝分上、中、下三层摆在八宝馅上，盖上甲鱼盖，对和猪肘肉放在甲鱼上面，姜片和花椒放在一起，连同葱段摆在甲鱼盖上。再将甲鱼碗入笼以沸水旺火蒸3-4小时取出，拿掉鸡、肘肉、拣去葱段和姜片。甲鱼的汤汁滗入锅里，', '形状完整，八宝配料，色调悦目，汤鲜醇正，味道多变。', NULL, NULL, '甲鱼450克、水发海参20克、干贝20克、莲子20克、水浸糯米15克、猪肥瘦肉30克、黄蛋糕20克，净冬笋15克、冬菇20克、净鸡肉30克、猪肘肉40克、精盐5克、绍酒15克、清汤150克、姜丝15克、花椒15克、葱段10克、湿淀粉60克。');
INSERT INTO `recipe` VALUES (2810, 71, '清蒸加吉鱼', '将加吉鱼刮去鳞，掏净鱼鳃、内脏，洗净。在鱼身打1.7厘米见方的柳叶花刀，放入开水中一烫即捞出，撒匀精盐，整齐地摆入盘中。猪肥肉膘打上花刀，切成33厘米长、1厘米宽的条，葱切小段，姜切片。冬菇、冬笋、火腿、油菜心都切成宽1厘米、长3.3厘米的片。将鱼放入鱼盘内，加入绍酒，花椒、清汤、再把猪肥肉膘、葱段、姜片、冬菇、火腿均匀地摆在鱼身上，入笼蒸20分钟熟后取出，将汤滗入炒锅内，去掉葱、姜、花椒，将油菜心入锅一烫，整齐地摆在鱼身上。将炒锅内放汤旺火烧开，打去浮沫，浇在鱼身上，淋上鸡油即可。', '原汁原叶，鲜嫩爽口，久食不腻，吃时外带姜末、醋碟用以蘸食，口味尤佳。', NULL, NULL, '加吉鱼750克、猪肥肉膘20克、冬菇20克、火腿15克、冬笋20克、油菜心25克、绍酒15克、花椒10克、清汤150克、葱段25克、姜片10克、鸡油3克。');
INSERT INTO `recipe` VALUES (2811, 71, '全虾三做', '将对虾去虾尖、腿、泥肠、沙袋、用清水洗净。从头部第二斑节处斜刀切下，再从尾部第三斑节处切为两段。将虾腰去皮，用刀从脊背处劈1/3深，放碗内加绍酒、精盐、味精、芝麻油、葱、姜汁略腌。虾尾去壳留尾，从脊背处劈为合页形，用刀轻拍打上十字花刀。用绍酒、精盐、味精、葱、姜汁、芝麻油腌渍，炒锅内放花生油，中火烧至四成热（约100度）时，将虾尾皮面先沾上干淀粉、鸡蛋黄、下锅煎至九成熟，捞出沥油，皮面朝下放入盘内，鸡蛋清打成蛋泡加干面粉搅匀，分别抹在虾内面上，用红辣椒皮，发菜、冬菇、嫩香菜叶，黄蛋糕摆成各色图案，做成芙蓉', '造型优美，一虾三吃，风味迥异。', NULL, NULL, '鲜对虾250克、红辣椒皮15克、水发冬菇25克、水发发菜50克、香菜叶15克、黄蛋糕50克、绍酒20克、精盐4克、味精0.5克、芝麻油200克、葱15克、姜汁15克。');
INSERT INTO `recipe` VALUES (2812, 71, '沙锅炖吊子', '1.熟猪心、肝、肺均切成大薄片，肥肠切成小段，玉兰片、口蘑均切成小片。 \r\n2.把熟大油放入炒勺内烧至5成热时，将葱段、蒜瓣、姜块(拍松)投入煸出香味，后放汤，再把油豆腐放入，随之再将盐、味精、奶汤、米醋、酱油和胡椒粉以及桂皮、大料等放入，调好味。汤开后倒入沙锅，用文火炖30分钟，然后将葱、姜拣出，撒上葱、姜丝、香菜段，淋入香油即成。', '汤鲜香，味醇厚。', NULL, NULL, '熟猪肚400克，熟猪肺、肝、心、肥肠各200克，油豆腐100克，水玉兰片、水口蘑、水海米各20克、熟大油50克，香油10克，盐、味精各5克，料酒、葱、姜丝各15克，香菜段15克，葱段、蒜瓣、姜块各15克，酱油25克，米醋50克，桂皮、大料、胡椒粉各少许，奶汤1.8千克。');
INSERT INTO `recipe` VALUES (2813, 71, '沙锅牛尾', '1.先用火燎去牛尾小毛，然后刷洗干净，去掉尾根大骨后剁成段，用开水将母鸡紧透，捞出，去血沫洗净。火腿切成片。干贝去筋洗净。 \r\n2.铁锅将熟大油烧热，放入花椒和50克葱、24克姜煸出香味，然后下入牛尾段，用旺火把牛尾煸出血水，烹入50克料酒后直炒至牛尾断生。取出牛尾洗净，把水分控干。\r\n3.把鸡汤放入沙锅内加入葱50克和拍松姜块25克、料酒、盐，同时把牛尾段、干贝、火腿片和母鸡块都放入沙锅中，用小火煨4小时，中间可加汤一次，待牛尾炖烂时，拣出葱、姜和母鸡块(另用)，加入味精撇去浮抹即可。', '营养丰富，牛尾软烂，汤味醇厚。', NULL, NULL, '牛尾(带皮)2千克，净母鸡半只，熟火腿100克，干贝50克，熟大油，料酒各100克，味精10克，盐14克，葱段100克，姜块50克，花椒5克，鸡汤3公斤。');
INSERT INTO `recipe` VALUES (2814, 71, '山东包子', '1.把白菜猪肉都切成小丁，放入盆中，加入鹿角菜(洗净后切成小段)、碎葱、姜末、香菜末、盐、香油、味精和黄酱拌匀，把味调好制成馅。 \r\n2.把发面对好碱分成30个剂子，用面杖擀成皮，把馅放在皮中捏20余个摺包好。把包好的包子先放10分钟醒一下，然后上屉用旺火蒸10分钟即可。', '鲜美适口，别有风味。', NULL, NULL, '发面500克，猪肉，香菜300克、水鹿角菜各50克，嫩白菜帮100克，香油25克，碎葱50克，味精3克，盐2克，黄酱35克，姜末15克。');
INSERT INTO `recipe` VALUES (2815, 71, '糖炒虾瓣', '1.香菇切成丝。将虾须、虾枪、虾爪剪去，挑出沙包除沙肠，从脊部片开成一大片。虾皮朝下，把筋扦断后，将每只虾剁成3段。用1克盐、10克料酒腌上，并撒上干淀粉。 \r\n2.将虾段下到烧至8成熟的植物油中，炸至金黄色，倒入漏勺中将油滤去。 \r\n3.另用炒勺放入100克植物油烧热，加入葱、姜丝和蒜片煸出味，烹入25克料酒，加入白糖、米醋、鸡汤和2克盐，再把豌豆和香菇丝放入，把炸好的虾段也放入，待把汤炒干，淋入香油即成。', '虾瓣鲜嫩，味甜美微酸。', NULL, NULL, '鲜大虾750克，罐头豌豆15克 ，植物油900克(实耗100克)，料酒50克，白糖50克，米醋25克，香油、葱丝、姜丝、蒜片各10克，盐3克，干淀粉3克，鸡汤500克。');
INSERT INTO `recipe` VALUES (2816, 71, '糖醋黄河鲤鱼', '鲤鱼去鳞，开膛取出内脏，挖去两鳃洗净，每隔25厘米，先直剖（1.5厘米深）再斜剖（2.5厘米深）成刀花。然后提起鱼尾使刀口张开，将精盐撒入刀口稍腌，再在鱼的周身及刀口处均匀地抹上湿淀粉。炒锅放花生油。中火烧至七成热（约175度）时，手提鱼尾放入锅内，使刀口张开。用锅铲将鱼托住以免粘锅底，入油炸2分钟，将鱼推锅边，鱼身即成方形，再将鱼背朝下炸2分钟，然后把鱼身放平，用铲将头按入油炸2分钟，待鱼全部炸至呈金黄色时，捞出摆在盘内。炒锅内留少量油，中火烧至六成热（约150度）时，放入葱、姜、蒜末、精盐、酱油、加清', '鱼尾翅起，色如琥珀，外焦里嫩，具有香酥，酸、甜、咸的独特风味。', NULL, NULL, '黄河鲤鱼1000克、姜10克、葱15克、蒜末10克、精盐5克、酱油10克、白糖40克、醋40克、清汤150克、湿淀粉60克、花生油100克。');
INSERT INTO `recipe` VALUES (2817, 71, '糖醋里脊', '1.将肉切成长约3.5厘米的一字条，然后放入细盐少许，加入鸡蛋、水淀粉挂糊备用。\r\n2.勺内放入花生油，烧至六成熟时，放入里脊肉，用旺火炸至肉呈微黄时捞出，把油再烧至八成熟时，将肉放入，炸至金黄时捞出。\r\n3.勺内留油少许，放入葱姜蒜米炝锅，烹入醋，加汤，放白糖、酱油，烧沸后用水淀粉勾芡，倒入炸好的里脊，快速颠翻均匀出勺即成。', '色泽金黄，外焦里嫩，甜酸适口。', NULL, NULL, '猪里脊肉250克，葱姜蒜米少许，花生油1000克（约耗40克），水淀粉100克，盐1克，鸡蛋1个，白糖75克，醋30克，酱油5克。');
INSERT INTO `recipe` VALUES (2818, 71, '铁板蚝油栗子鸡', '1.把鸡剁成4厘米见方的块，用酱油腌渍入味后过热油。\r\n2.熟栗子炸成黄色捞出与鸡块一同放入锅内加糖，酱油，蚝油，料酒，用小火扒至酥烂，汤剰1/4时用淀粉，勾芡，淋香油倒入烧热的铁板上即成。', '色泽红润，微甜，蚝油味浓。', NULL, NULL, '净鸡，去壳栗子，蚝油，酱油，葱，姜，料酒，白糖，味精等。');
INSERT INTO `recipe` VALUES (2819, 71, '沙锅散丹', '1.先用80度热水把羊散丹烫一下捞出，用手将散丹的黑膜搓去，洗净后放入开水锅中，加入葱段、姜块、花椒、用微火煮约3小时左右，煮烂捞出，将散丹上的油脂、杂物择去，再用温水洗干净，然后切成三角块，放入开水中煮1分钟，捞出控水。\r\n2.把散丹块放入沙锅里，加入鸡汤，用旺火烧开，把浮沫撇去。再加入葱丝、味精、料酒、盐。汤滚开后，再用微火5分钟，随后淋入鸡油，撒上葱丝、香菜段即成。卤虾油盛在碗中，随沙锅散丹一起上桌。', '鲜嫩清香，清淡爽口。', NULL, NULL, '羊散丹(即瓣胃)2.5千克，鸡油10克，盐、味精各5克，卤虾油、料酒各10克，葱段、姜块各15克，葱丝10克，姜丝5克，花椒10粒，鸡汤2.5公斤。');
INSERT INTO `recipe` VALUES (2820, 71, '山东菜丸', '将猪瘦肉洗净，剁成茸状，放入碗内，加鸡蛋搅匀，猪肥肉批成厚0.6厘米的片，肉片两面交叉打直刀，再改切成0.6厘米的见方丁，淳米洗尽泥沙，鹿角菜择洗干净，取30克，切成细末。取盛器一个，放入瘦肉茸，葱、姜末、海米末、鹿角菜米、大白菜末、香菜末，加入胡椒粉、味精、精盐，搅拌均匀，制成直径3.3厘米的丸子，平摆在盘内，入笼用旺火蒸约8分钟，至熟取出，放在大碗内，汤锅内放入清汤、精盐，蒸沸，放香菜段旺火烧开后加入味精，倒入大汤碗中，淋上芝麻油，食醋，撒上胡椒面即成。', '用料多样，质地疏松软烂，肥而不腻，汤浓味鲜，酸辣咸香俱全。', NULL, NULL, '猪肥瘦肉各半250克，香菜100克、淳米50克、鹿角菜50克、白菜，鸡蛋100克，精盐4克、葱姜末10克、味精0.5克、食醋、芝麻油适量。');
INSERT INTO `recipe` VALUES (2821, 71, '山东丸子', '1.用温水将鹿角菜泡开去根洗净。海米泡软剁成末。\r\n2.猪肉剁成末放入盆内，加进盐、味精、酱油各3克，再加入鹿角菜末、香菜末、海米末和适量水。并将鸡蛋打入盆内，搅拌成馅，将馅挤成直径3厘米的丸子，放入盆中上屉蒸15分钟左右取出放入碗内。 \r\n3.炒勺放入鸡汤，加入盐、味精、酱油、米醋、料酒等，汤开后将浮沫撇去，淋入香油，将汤浇在丸子上，把香菜段撒上即可。', '汤清淡鲜香，丸子味美可口。', NULL, NULL, '猪肥瘦肉350克，鹿角菜350克，海米25克，鸡蛋50克，香油50克，葱、姜末各10克，料酒15克，米醋15克，盐7克，味精5克，酱油15克，香菜段、末各10克，鸡汤500克。');
INSERT INTO `recipe` VALUES (2822, 71, '珊瑚金钩', '1.豆芽洗净去根，放入开水中煮熟，捞出控水，装盘。 \r\n2.香油烧至5成熟，放入花椒粒炸成金黄色捞出，将辣椒丝，葱姜丝放入，再依次放入木耳丝，酱油，料酒，醋，白糖，精盐浇开后，浇在豆芽上即成。', NULL, NULL, NULL, '嫩黄豆芽350克，红辣椒丝5克，葱姜丝各1克，木耳丝11克。');
INSERT INTO `recipe` VALUES (2823, 71, '烧罗汉面筋', '熟面筋去边，划上平行花刀，再切成片，炒锅内加入花生油，烧至六成热（约150度），将面筋炸成杏黄色，捞出沥油。再将炸好的面筋放入开水锅内煮至发软，捞在温水盆内，洗去油渍，挤净水分。炒锅再上火放猪油，烧至六成热（约150度），加入拍扁的葱姜、炸成黄色、加酱油、绍酒烧开，去葱姜、下入面筋煨烧15分钟，下入水发冬菇片、水发玉兰片、口蘑、精盐。锅开后端离火口，将玉兰片摆在碗底，面筋刀口朝下逐片摆在碗内，口蘑摆在四周，上笼用大火蒸10分钟，取出扣在大汤碗内，将汤滗在锅内，勾薄芡，淋上花椒油，浇在面筋上即成。', '造型优美，鲜香软嫩，制作精巧，营养丰富。', NULL, NULL, '熟面筋200克、水发冬菇片20克、水发玉兰片20克、口蘑50克、花生油150克、精盐5克、葱10克、姜15克、酱油10克、绍酒15克、猪油200克。');
INSERT INTO `recipe` VALUES (2824, 71, '生菜拌鸭掌', '1.将鸭掌煮至六成熟时捞出，趁温热从鸭掌背面顺骨骼用刀划破皮，将骨头剔出，每个鸭掌从中一切两半。把生菜洗净，切成3厘米的段。\r\n2.将酱油、香油、醋调成汁。生菜装入盘内，鸭掌用开水汆一遍冷凉后摆在生菜的上面，放上姜米，烧上汁即成。此菜食用时也可加入芥末糊、或蒜泥。', '鲜香脆嫩，清淡爽口。', NULL, NULL, '熟百鸭掌12个生菜150克，姜米5克，酱油25克，醋20克，香油10克。');
INSERT INTO `recipe` VALUES (2825, 71, '什锦蜂窝豆腐', '净鱼肉去净筋膜，砸成细泥，放碗内与鸡蛋清、绍酒、精盐搅成鱼料。再将豆腐搅成泥，同鱼料混合搅匀。将白煮鸡肉切成条，放入大盘底，涂抹上豆腐鱼料，入笼蒸10分钟取出，即成豆腐状。鸡蛋清放在碗内打成雪丽糊，均匀地抹在“豆腐”上，将海参、虾仁、火腿、冬菇、海米、青豆、佛筋、冬笋均切成1厘米见方的丁，各色相间的插在豆腐上面，入笼稍蒸片刻，改切成象眼块。汤锅内加清汤、酱油、绍酒、味精、精盐、旺火烧沸后撇去浮沫，倒入瓷碗内。将豆腐整齐地推入汤内即成。', '造型新颖，清香鲜嫩，味美适口，营养丰富。', NULL, NULL, '豆腐200克、鱼肉20克、鸡蛋清15克。鸡肉条50克、海参15克、虾仁20克、火腿15克、冬菇15克、海米20克、青豆15克、蹄筋15克、冬笋15克。精盐5克、味精1.5克、绍酒25克、酱油15克、清汤100克。');
INSERT INTO `recipe` VALUES (2826, 71, '什锦拼盘', '1.将火腿、黄蛋糕、白蛋糕、腌黄瓜分别切成8厘米长、1.5厘米宽的薄片，色彩相间地摆成中间空的彩色圆环。\r\n2.将海带蛋卷、海带午餐肉卷切成片，黄蛋糕切成凤尾片，芹菜切成小菱形块，然后由彩色圆环向盘中间将各种原料分别摆成环形，摆好后盘中央留10厘米直径的圆形空白。\r\n3.用大青萝卜雕刻成底座、花盆和树枝，然后组装在一起，并在树枝上点缀上用胡萝卜雕刻的梅花，即成为梅花盆景。将其放入什锦拼盘中央，在盘边用黄蛋糕片和红樱桃略点缀即可。', '色泽鲜艳，整齐美观，观赏性较强。', NULL, NULL, '熟火腿150克，黄蛋糕150克，白蛋糕150克，腌黄瓜150克，海带蛋卷150元，海带午餐肉卷150克，大青萝卜1个，胡萝卜50克，炝芹菜50克，红樱桃2个。');
INSERT INTO `recipe` VALUES (2827, 71, '什锦一品锅', '海参片成刀片形状，鱼肚、玉兰片均片成长条块。豌豆苗略烫冷水过凉，猪黄管、鱿鱼卷用毛汤氽过。取“一品锅”一只，龙须粉、白菜墩、自煮山药放入锅内垫底，将白煮肘子、白煮鸡、白煮鸭分摆在上面，再将海参、鱼肚、鱿鱼卷、玉兰片、荷包鸡蛋在间隔处摆成一定的图案，再把猪黄管放在顶端，加入三套汤、绍酒、精盐，用旺火入笼蒸2小时左右取出，放上豌豆苗上席即成。', '作功精细原料多样，风味清鲜，品味浓郁，营养丰富。', NULL, NULL, '鸡200克、海参20克、鱼肚15克、玉兰片15克、豌豆苗20克、猪黄管20克、鱿鱼卷20克、龙须粉25克、白菜墩20克、山药20克、白煮肘子25克、白煮鸭25克，荷包鸡蛋20克、三套汤20克、绍酒6克、精盐5克。');
INSERT INTO `recipe` VALUES (2828, 71, '双味蹄筋', '将发好的蹄筋取一半，顺长从中间切开，再切成2.5厘米长的段，大葱批成两半，切成2.5厘米长的段，玉兰片、火腿切成厚0.3厘米、长宽各2厘米的片，青菜心切成2.5厘米长的段，将鱼肉、肥肉膘一起剁成细泥，葱姜切成细末放入碗内，加入精盐、味精、绍酒、鸡蛋清和少量清水，搅匀成馅，将多余的玉兰片，火腿均切成细末。将粗细均匀的发好蹄筋切成4厘米长的段，从中间割上道口（不要割断），将鱼肉馅抹在蹄筋刀口里，馅要高出一点，上面以玉兰片末、火腿末、香菜末、红柿椒丝等配料点缀成不同颜色的花卉图案，做酿蹄的原料放在盘内。炒锅加入', '红烧蹄筋色泽红亮，汤汁浓郁，软韧咸香，酿蹄筋红、绿、黄、白相间，色彩悦目，鲜嫩适口。', NULL, NULL, '发好的蹄筋500克，偏口鱼肉100克、猪肥肉50克、火腿20克、小油菜心20克、红柿椒丝10克、香菜10克、青菜心10克、玉兰花5克、鸡蛋1个，大葱10克、精盐5克、味精5克、绍酒10克、姜末5克、酱油6克、淀粉少许。');
INSERT INTO `recipe` VALUES (2829, 71, '四喜丸子', '1.将猪肥肉切0.3厘米的丁，瘦肉剁成细茸，放入碗内，加入鸡蛋、葱姜末、湿淀粉、酱油、绍酒拌匀，做成四个大小一致的丸子备用。\r\n2.冬菇、冬笋洗净均匀切成片，炒锅放入花生油，上中火烧六成热（约150度）时放入肉丸，炸至金黄色捞出。放入碗内加入酱油、绍酒、花椒、八角、清汤入笼用旺火蒸30分钟取出，摆入大盘内。原汤滗入锅内，加冬笋片、冬菇片，用旺火烧开，放入味精、葱油拌匀，用湿淀粉勾芡，浇在丸子上即可。', '色泽红润，鲜香糯嫩，入口即散，老幼皆宜。', NULL, NULL, '猪肥、瘦肉（1：4）500克、 冬菇10克、冬笋10克、鸡蛋100克。葱10克、姜10克、酱油15克、绍酒20克、精盐15克、花椒5克、八角5克、味精15克、湿淀粉25克、花生油25克。');
INSERT INTO `recipe` VALUES (2830, 71, '四喜鸭子', '净雏鸭由脊背处劈开，鸭脯相连，用刀背把大骨斩断，去掉鸭嘴内鸭舌，猪肉切成1.5厘米见方的丁，连同鸭子用酱油腌渍10分钟，捞出沥干，放入九成热（约225度）油锅中，将鸭、猪肉块炸成棕红色捞出沥油，放砂锅内，把猪肉、葱段、姜块、花椒、八角、桂皮放在正面，清汤内加白糖、精盐、味精，绍酒搅匀倒入砂锅内，用慢火炖2小时至烂熟取出，皮卤朝上摆在大腰盘内，猪肉放在鸭子的四周。汆汤倒入锅内烧开，调好口味用湿淀粉勾成浓溜芡，加芝麻油浇在鸭子上即成。', '鸭肉酥烂，口味醇厚浓郁，入口软烂不腻，富含营养，尤宜老年人食用。', NULL, NULL, '净雏鸭750克、猪肉30克、葱10克、姜10克、花椒5克、八角5克、桂皮5克、白糖20克、精盐5克、味精1克、绍酒21克、湿淀粉15克、芝麻油100克、花生油1500克。');
INSERT INTO `recipe` VALUES (2831, 71, '四喜鱼卷', '将比目鱼肉洗净，劈成八条长10厘米、宽2.6厘米、厚0.5厘米的条。再由两端向里劈至中间（中间连接），撒上精盐、绍酒、浸渍入味。猪肉剁成细泥，加葱末、胡椒面、精盐、绍酒、鸡蛋、湿淀粉、芝麻油搅匀。搅好的馅分别卷在鱼肉片内（每条鱼片卷四个卷）。在四个小鱼卷内分别按上蛋糕末、冬菇末、火腿末、青菜末、然后放蒸笼内蒸熟取出，整齐地摆在盘内。锅内加清汤、精盐、绍酒、烧沸后撇去浮沫，勾流漓芡浇在鱼卷上，淋上熟鸡油即成。', '色泽艳丽，肉质细嫩，口味鲜香，造型美观。', NULL, NULL, '比目鱼750克、猪肥瘦肉30克、黄蛋糕末20克、水发冬菇末20克、火腿末15克、青菜末15克。精盐5克、绍酒15克、葱末15克、胡椒面3克、淀粉50克、芝麻油10克、清汤100克、熟鸡油5克。');
INSERT INTO `recipe` VALUES (2832, 71, '酥烂蹄筋', '将发好的猪蹄筋挤去水分，切成3.3厘米段，放入碗内加盐、味精、绍酒浸渍；另取一碗，放上面粉发酵，用少许清水调匀，加少量碱、鸡蛋黄调成糊，再加上花生油拌匀。取炒锅，倒入花生油，置中火烧至六成热（约150度），将蹄筋拖上糊，逐条炸至起壳捞出，待油加热九成热（约225度）时，重炸一次，炸至酥，呈金黄色时取出装盘即成。', '色泽金黄，外酥里香，下酒佳肴。', NULL, NULL, '油发猪蹄筋200克、精盐3克、味精1克、绍酒10克、鸡蛋黄若干、碱少许。');
INSERT INTO `recipe` VALUES (2833, 71, '酥炸春花肉', '将里脊丝切成长5厘米，厚0.3厘米待用，鸡蛋磕入碗内、加精盐湿淀粉，清汤搅匀，用锅制成3张蛋皮，每张蛋皮切成相等的七块，再用面粉、老酵面加水搅匀，然后加入熟猪油食用碱调成发酵糊。炒锅内加熟猪油，上中火烧至五成热（110  度）时，放入葱、姜丝、肉丝、冬笋丝、荠菜、冬菇丝、木耳丝、酱油略炒，随后加入清汤、精盐、味精烧开，用湿淀粉勾芡，加芝麻油盛出为馅。将炒好的熟馅分放在21张蛋皮上，逐个卷成食指粗的卷，用面糊粘住口。炒锅内加入熟猪油，烧至八成热（约176度）时，将蛋卷逐个粘上发酵糊，下锅炸至金黄捞出装盘，上', '外酥香，里鲜嫩，口感清爽带微辣，营养价值较高，常用于宴席菜肴。', NULL, NULL, '猪里脊肉500克、荠菜50克、冬笋15克、冬菇10克、木耳10克、鸡蛋5个、葱10克、姜10克、酱油15克、猪油50克、椒盐5克、精盐5克、味精3克。');
INSERT INTO `recipe` VALUES (2834, 71, '素锅烤鸭', '将面筋切成长条，加干淀粉，酱油浸泡，荸荠肉切小片，蒲菜头用刀轻拍切成段，蘑菇片、水发玉兰片切两半。四种原料一齐下入开水锅内，见开捞出沥干，挤净水分。将面筋下五成热（约125度）油锅煎炸，挺身后捞出沥油，与蒲菜、荸荠片、玉兰片、蘑菇片一起放盘内，加入葱椒汁、味精、绍酒、酱油调料浸渍，再加入蛋清糊拌匀，做成“素鸭”，锅内加花生油烧至四成热（约100度），将“素鸭’平摊在锅内，用小铲转动，挺身后折叠，用筷子在上面扎4-5个冒气孔，炸成枣瓤色捞出沥油，切成象眼块摆在盘中，外带葱白、甜面酱上席即成。', '颜色鲜艳，酷似鸭肉，鲜香不腻。', NULL, NULL, '熟面筋150克、蒲菜头25克、荠荸50克、玉兰片25克、蘑菇25克、蛋清50克、葱15克、花椒3克、味精1克、绍酒15克、酱油10克、花生油150克、淀粉50克。');
INSERT INTO `recipe` VALUES (2835, 71, '素火腿', '1.山药去皮入笼蒸熟，制成泥与湿淀粉一起放在盆内，加入鸡蛋清、精盐、芝麻油（80克），味精，白糖搅匀，即成山药料。 \r\n2.将1/10的山药料加入嫩糖色，掺匀放入抹油的铁盒，摊平入笼蒸挺身拿出，即为“肉皮”。将3/10的山药料放在“肉皮”上，摊平蒸10分钟，出笼即为“肉膘”。再将剩余的山药料加入砂仁面，建曲汁，将油搅匀，摊在“肥膘”上，再入笼蒸35分钟，晾凉，刷上芝麻油（20克），切片上席即可。', '素火腿以山药为主料制作，成菜红白相间，香甜细嫩，色型逼真，常作为宴席的行件登席。', NULL, NULL, '山药1500克，湿淀粉125克，嫩糖色0.5克，鸡蛋清2个，精盐50克，建曲汁4克，白糖100克，砂仁面10克，酱油3克，芝麻油100克。');
INSERT INTO `recipe` VALUES (2836, 71, '烤网油签', '将猪瘦肉切成长4.5厘米、宽0.2厘米、厚0.2厘米的丝，加葱、姜丝、酱油、鸡蛋清、清汤、绍酒、味精拌匀。猪网油切成长30厘米、宽15厘米，共切3块，把拌好的肉丝均匀地分成3份，分别放在网油上包成长20厘米、宽4厘米的“网油签”。用湿淀粉和蛋清调成糊，沾住“油签”封口处，放入烤箱内用旺火烤20分钟，烤至两面金黄色时取出。再改切成小长方块，整齐地摆在盘内即成。', '外皮酥脆，肉嫩味香，造型整齐。', NULL, NULL, '猪瘦肉200克、猪网油100克、酱油6克，味精0.5克、葱姜丝12克、鸡蛋清若干、绍酒10克、湿淀粉30克。');
INSERT INTO `recipe` VALUES (2837, 71, '泰安三美豆腐', '1.将豆腐上笼或放入锅里隔水蒸约十分钟取出，沥干水，切成3.5厘米长、2.5厘米厚的片，白菜心用手撕5厘米长的小条块，分别放入沸水锅中烫过。\r\n2.炒锅烧热，放猪油，烧至五成热时，加入葱、姜末炸出香味，放入鲜汤、盐、豆腐、白菜烧滚，撇去浮沫，加入味精，淋上鸡油即成。', '汤汁乳白而鲜，豆腐软滑，白菜鲜嫩，清淡爽口。', NULL, NULL, '泰安豆腐150克、白菜心100克、鲜汤500克、精盐4克、味精1克、葱末、姜末各1克、鸡油5克、熟猪油20克。');
INSERT INTO `recipe` VALUES (2838, 71, '泰山赤鳞鱼', '1.将鱼剖腹去内脏，洗净，放入开水锅中汆熟，捞出放在大汤碗内，撒上胡椒粉。 \r\n2.炒锅上火，加清汤、盐、酱油、花椒、绍酒、味精，烧开，撇净浮沫，倒入鱼碗内。醋加姜末拌和入碟上桌佐食。', '鱼肉细嫩，汤汁鲜美清口，无鱼腥味。', NULL, NULL, '活赤鳞鱼750克 、精盐、姜未、花椒、绍酒、酱油各10克、醋100克、味精1克、清汤1000克、胡椒粉0.5克。');
INSERT INTO `recipe` VALUES (2839, 71, '坛子肉', '1.将猪助条肉洗净，切成2厘米见方的块放入开水锅中焯五分钟捞出清水洗净。葱切成3.5厘米长的段、姜切成片。\r\n2.把肉块放入磁坛子内，加酱油、冰糖、肉桂、葱、姜、水（以浸没肉为度），用盘子将坛口盖好，在中火上烧开移至微火上煨约三小时，至汤浓肉烂即成。', '色泽深红，肉烂汤浓，肥而不腻，鲜美可口。', NULL, NULL, '猪硬肋肉500克、冰糖15克、肉桂5克、葱、姜各10克、酱油100克。');
INSERT INTO `recipe` VALUES (2840, 71, '汤爆双脆', '肚头、鸡肫头用刀切开，剥去外皮，在清水中洗净，去掉里面的筋杂、外面刻十字花刀（深为肚厚的2／3），清水洗净放入另一碗内备用。汤锅内放入清水，置旺火上烧至八成热时，先放鸡肫后放肚头末，立即捞出放入汤碗内，加葱椒、绍酒拌匀，撒入香菜末、胡椒粉。炒锅内放入清汤、酱油、精盐、葱椒、绍酒置火上加热烧沸；打去浮沫，加味精浇入汤碗内，迅速上桌，落桌后将主料推入汤内即成。', '质地脆嫩，汤清质淡，味道香醇。', NULL, NULL, '猪肚头150克、鸡肫100克、葱椒15克、绍酒15克、香菜末适量、酱油10克、精盐3克、味精1克。');
INSERT INTO `recipe` VALUES (2841, 71, '氽发菜三丝', '发菜洗净，用温开水泡2分钟，搌净水分。鸡脯肉切成细丝。炒锅放火上，加入开水，将蛋黄用针扎一小孔，用手轻轻捏入锅内，成为细丝时捞出搌净水分，鸡丝下锅，用竹筷划开即捞出。发菜、蛋黄丝、鸡脯肉丝分三堆放在大汤碗内，锅加入清汤烧开，撇去浮沫，加入口蘑汤、酱油、绍酒，调好口味，倒入三丝碗内即成。', '汤鲜味醇、色泽鲜艳，似琼浆玉液中漂浮的“灵芝”，令入赏心悦目。', NULL, NULL, '发菜200克、蛋黄丝50克、鸡脯肉丝100克、酱油10克、绍酒10克、清汤100克、口蘑汤100克。');
INSERT INTO `recipe` VALUES (2842, 71, '氽虾蘑海', '将虾去肠洗净、劈成片，口蘑海参均劈成小片，香菜切成长0.7厘米的段，炒锅内加水置旺火烧开，投入虾片，氽熟捞出，海参亦氽透放在汤盆内，撒上香菜、葱丝。炒锅内加上清汤、味精、绍酒、酱油、精盐、虾片、口蘑，旺火烧沸，撇去浮沫，浇在汤盘内入淋上鸡油、撒上胡椒面即成。', '汤清澈见底，味鲜香微辣。', NULL, NULL, '水发海参250克、大虾50克、水发口蘑120克、味精12克、绍酒30克、酱油20克、精盐15克、葱丝10克、胡椒面5克、鸡油25克、清汤500克、香菜15克。');
INSERT INTO `recipe` VALUES (2843, 71, '网油棒榻', '1.把香棒老梗择掉，洗净，切成末放入盆中，加25克鸡蛋清、2克盐、料酒、味精、20克淀淀粉、猪肉末、香油，充分搅拌成肉馅。用100克鸡蛋清、2克盐、100克湿淀粉一起放于碗内调成糊。 \r\n2.将网油辅平撒上干淀粉，放上肉馅，折成长15厘米、宽5厘米、厚1厘米的网油板。 \r\n3.炒勺将植物油烧至6成热，将网油板蘸上干淀粉，裹匀鸡蛋糊。放入油勺中炸至浮起，将勺离火浸一浸，然后继续炸至呈金黄色时捞出，沥油，剁成段即成。', '味鲜香可口，适于春季食用。', NULL, NULL, '猪肉末400克，鲜香棒100克，网油200克，鸡蛋清125克，植物油900克（实耗约80克），香油15克，料酒15克，味精4克，湿淀粉100克，干淀粉25克。');
INSERT INTO `recipe` VALUES (2844, 71, '五香酱肉', '1.准备工作：将猪肉刮洗净，切成10块，放入水锅中，用旺火烧沸后，即捞至冷水中洗净；葱切长3厘米的段；姜稍拍；药料用洁布包好。 \r\n2.烹调：锅内加清水（以漫过肉为度），放入肉、酱油、盐、葱、姜、药料，用铁箅子将肉压住，在旺火上烧沸，撇去浮沫，移至微火上焖煮，至肉熟时捞出，皮朝下盛在盆内晾凉。锅内汤烧沸，撇净浮油，晾凉倒入肉盆内即成。吃时将肉捞出，切薄片装盘。', NULL, '如无带皮猪肉，无皮猪肉亦可，但色、味较差。煮肉的原汤可留作下次煮肉时继续使用，肉的香味更加醇厚。按此法亦可酱制猪肝、心、肺、肚、肠、尾、头、蹄、排骨。', NULL, '带皮猪肉5000克，酱油1000克，精盐200克，葱、姜共200克，药料10克。');
INSERT INTO `recipe` VALUES (2845, 71, '雾熘桂鱼', '桂鱼刮鳞去内脏洗净，用开水烫后刮净黑皮清洗，一面间隔2厘米打坡刀（不要划断脊骨）成瓦垄块，另一面剖成边长2.5厘米的菱形块。火腿，玉兰片、香菇切成细丝。绍酒、精盐、胡椒粉、湿淀粉、酱油、清汤放入汤碗，搅成芡汁。将桂鱼放入开水中再汆一下，使花块卷起，放入大盘里，用精盐撒在身上腌渍片刻，放笼内以沸水旺火蒸10分钟取出，将鱼放入另一盘中。炒锅内加入花生油，用中火烧至六成热（约150度）时，加入葱姜丝，炸出香味后加入白糖、火腿丝、玉兰丝、香菇丝，把碗内芡汁倒入锅烧开后浇在鱼身上即成。', '鱼肉鲜嫩，味入其里，咸甜适口，略有辛辣。', NULL, NULL, '新鲜桂鱼1000克、火腿50克、水发玉兰片25克、水发香菇50克、绍酒20克、精盐4克、胡椒粉5克、湿淀粉65克、酱油10克、清汤150克、花生油100克、葱10克、姜10克、白糖20克。');
INSERT INTO `recipe` VALUES (2846, 71, '鲜贝冬瓜球', '将鲜贝洗净放入碗内，加入鸡蛋清、湿淀粉和适量水抓匀上浆。冬瓜用修成直径1厘米的圆球，放入沸高汤内煨熟入味待用。炒锅内放入花生油，中火烧至五成热（约125 度）时，放入鲜贝滑熟捞出。碗内加清汤、精盐、味精、湿淀粉调匀成汁。炒锅内留少量油，旺火烧至七成热（约175度）时，放入葱、姜末略煸，烹入绍酒，随即加入冬瓜球，鲜贝以及调好的芡汁，迅速颠炒均匀，淋上芝麻油装盘即成。', '造型别致，质地软嫩，口味鲜美。', NULL, NULL, '鲜贝150克、冬瓜350克、精盐13克、味精7.5克、湿淀粉50克、绍酒35克、葱姜各15克、花生油500克、清汤500克。');
INSERT INTO `recipe` VALUES (2847, 71, '蟹黄蹄筋', '将洗净的油发猪蹄筋切成长条，放入沸水锅中汆透捞出，沥去水分。炒锅加花生油置中火烧六成热（约150度）时，放入少量白糖炒至黄色，倒入猪蹄筋、葱段、姜炒至上色后，加入酱油、清汤、绍酒烧沸移至微火上烧制，待汤汁紧稠浓时，取出葱姜不要，用湿粉勾芡，加味精、葱油搅匀盛入盘中间。炒锅内放花生油，置中火烧至六成热（约150度），加葱姜蒜末炸出香味，随即倒入蟹黄搅拌炒几下，然后烹入酱油、绍酒、味精颠翻均匀，放在盘内蹄筋四围即成。', '色泽光润，口味软嫩，鲜咸适度，老少皆宜。', NULL, NULL, '油发蹄筋200克、蟹黄75克、酱油5克、绍酒15克、湿淀粉30克、葱段姜蒜25克、糖色适量。');
INSERT INTO `recipe` VALUES (2848, 71, '杏仁豆腐', '杏仁放入水中浸泡片刻，剥去皮，砸成细泥，然后用细纱布拧出汁，放入碗内。汤锅洗净放火上，加清水，浸泡的冻粉，用小火熬煮化后，加入淀粉、杏仁汁、白糖、煮沸后倒入盘内，冷却后原样切成象眼块（周边不要切断），将山楂糕、黄瓜皮、西红柿均匀成细丝，在杏仁豆腐上摆成图案。汤锅内加清水、白糖，烧沸后倒入汤盘内，待晾凉后，将制成的“杏仁豆腐”原样推入糖水盘内即成。', '造型典雅，甜酸适口，冷热食用皆宜。', NULL, NULL, '杏仁150克、冻粉100克、山植糕20克、黄瓜皮25克、西红柿25克、淀粉50克、白糖20克。');
INSERT INTO `recipe` VALUES (2849, 71, '绣球干贝', '干贝挤干水分，搓成细丝，火腿、冬笋、冬菇均切成2厘米长的细丝，用沸水焯一下，捞出控净水分与干贝丝拌和一起待用。将大虾肉、肥猪肉剁成细泥，加鸡蛋清、清汤、精盐、味精、绍酒。葱姜汁、芝麻油拌匀成馅，然后用手挤成直径为25厘米的丸子，放在拌和好的干贝丝上滚沾均匀，成绣球状，摆于盘内上笼用旺火蒸至嫩熟取出，滗净汤汁。炒锅加入清汤、精盐、绍酒。味精烧开后，撇净浮沫，用湿淀粉勾成溜芡，加芝麻油浇在干贝球上。菠菜心洗净，炒锅内放上花生油烧热后加入菠菜心煸炒，加精盐、味精炒熟，淋上芝麻油盛出摆在绣球干贝四周即可。', '形似绣球，鲜而不腻，嫩爽多汁，甘美清润。', NULL, NULL, '水发干贝200克、大虾肉100克、肥猪肉50克、净冬笋25克、菠菜心35克、精盐12克、味精7.5克、绍酒30克、葱姜汁20克、鸡蛋清25克、芝麻油15克、湿淀粉25克。');
INSERT INTO `recipe` VALUES (2850, 71, '绣球全鱼', '黄鱼刮去鱼鳞，去鳃和内脏冲洗干净，从鱼下巴剁开，用刀背一砸，让鱼头扒下，劈下鱼肉剥去鱼皮待用，将鱼脊骨及鱼头保持原形，放入鱼盘中。再将鱼肉、冬菇、冬笋、火腿、猪肥肉膘均切成长3.5厘米、宽厚各0.2厘米的丝，放入盛器中，加入精盐、白糖、绍酒、葱末、姜末、鸡蛋清、湿淀粉搅匀成馅，挤成直径2厘米的丸子12个放入盘中骨架的两侧，呈整鱼状，上笼旺火蒸熟取出。将原汤倒入炒锅内，加清汤、绍酒、精盐、青豆烧开，用湿淀粉勾芡，淋上鸡油浇在鱼身上即成。', '造型美观，无筋无刺，五彩缤纷，口味鲜美。', NULL, NULL, '黄鱼750克、猪肥肉膘100克、水发冬菇20克、净冬笋20克、火腿15克、青豆15克、精盐5克、白糖20克、绍酒15克、葱末15克、姜末10克、鸡蛋清15克、淀粉50克、清汤150克、鸡油3克。');
INSERT INTO `recipe` VALUES (2851, 71, '雪梨肘棒', '1.将肘棒表皮的毛污刮净，露在肉外面的骨头砸断，然后用开水紧透，捞出洗净。把雪梨剥皮、挖核，切成瓣块后泡入凉水中。 \r\n2.用炒勺把植物油烧热，放入白糖（25克）、味精、盐、料酒，同时把肘棒也放入，汤烧开后把浮沫撇去，用小火炖2小时，肘棒烂时放入梨块再炖3分钟，然后将肘棒和梨一起盛于盘中。 \r\n3.用旺火将原勺中汤汁收浓，浇入肘棒梨块上即可。', '味鲜香甜咸，肘棒不腻。', NULL, NULL, '肘棒2个（1.5千克左右），雪鸭梨（梨也可）500克，植物油100克，味精5克，白糖50克，盐7克，姜片10克，料酒15克，葱段3根。');
INSERT INTO `recipe` VALUES (2852, 71, '芫爆鱿鱼卷', '1.鱿鱼去头、去内膜和明刺，剞麦穗花刀，切菱形片，香菜切段。\r\n2.鱿鱼片用开水焯起卷。\r\n3.锅上火放油，烧至六七成热，投入鱿鱼卷稍滑油后捞出，控油。锅留底油，放葱姜蒜爆香，放入鱿鱼卷、香菜，加料酒、醋、盐、味精炒熟，出锅前淋香油即可。', '咸鲜微酸，脆嫩利口。', NULL, NULL, '水发鱿鱼400克，香菜梗150克，清油1000克（实耗约100克），料酒30克，醋20克，盐2克，味精5克，葱丝、姜丝、蒜片各2克，香油10克。');
INSERT INTO `recipe` VALUES (2853, 71, '一品寿桃', '长红枣洗净，煮烂，剥皮去核，制成枣泥，放入炒锅内加熟猪油、白糖，熬成枣泥馅。山药洗净，蒸烂，去皮、斑点，用刀抹成细泥。山楂糕切成长条。红丝、青梅均匀成细末。取大盘1只，中间放上枣泥，将山药泥覆盖其上制成桃形及两片叶子，抹尖滑，用青丝做叶子筋高轮廓，桃身上用山植糕条摆上“寿”字，分别在桃尖和下半部用红丝末点缀好，青梅入桃蒂上，入笼蒸5分钟取出，汤锅内加清水、白糖，中火化开，用湿淀粉勾芡制成薄芡，轻轻浇在寿桃上即成。', '寿桃形象逼真，食之沙甜爽口，沁人心脾。', NULL, NULL, '长红枣150克、山药50克、山楂糕30克、红丝20克、青梅20克、熟猪油20克、白糖20克、淀粉50克。');
INSERT INTO `recipe` VALUES (2854, 71, '油爆双脆', '1.准备工作：将肚头剥去脂皮硬筋洗净，用刀划上网状花刀，放碗内，加精盐1.5克，湿淀粉10克抓匀。鸡胗洗净，片去内外筋皮，用刀划上间隔1.5毫米的十字花刀，放另一碗内，加精盐1克，湿淀粉5克抓匀。清汤、绍酒、味精、精盐、湿淀粉对成芡汁。 \r\n2.烹调：炒勺放旺火上，加白油，烧至八成熟时，把鸡胗、肚头下入油内，用铁筷迅速拨散，倒入漏勺内；炒勺内留油25克，放入葱、姜、蒜末炸出香味，随即倒入鸡胗、肚头及对好的芡汁，颠翻两下，盛入盘内即成。', NULL, NULL, NULL, '猪肚头200克，精盐4.5克，绍酒10克，清汤50克，湿淀粉25克，鸡胗150克，味精1.5克，葱末1.5克，姜末0.5克，白油500克（约耗50克）。');
INSERT INTO `recipe` VALUES (2855, 71, '油爆鲜贝', '1.先将贝壳表面粘液洗掉，撬开贝壳取出贝肉，洗去污物去掉筋，将每个贝肉切成段，放入碗内，加入清汤、料酒、味精和盐、湿淀粉兑成汁。 \r\n2.炒勺上旺火，将植物油烧开后，将贝肉放入爆炒几分钟，即倒入漏勺内沥油。\r\n3.将原勺留底油烧热，再将贝肉倒入，立即烹入兑好的汁，颠翻几次，淋上香油即成。', '味鲜美，肉嫩脆。', NULL, NULL, '扇贝200克，蒜瓣15克，青蒜25克，植物油500克(实耗约50克)，料酒10克，姜5克，葱25克，盐2克，味精3克，湿淀粉40克，清汤100克。');
INSERT INTO `recipe` VALUES (2856, 71, '油焖凤尾鱼', '将黄荡苗鱼刮鳞去鳃去内脏，清洗干净。白菜心切四瓣，葱切成12厘米长的段，姜切片，花椒、八角、肉桂用纱布包好为香料袋备用。炒锅内加入花生油，中火烧至六成热（约150度），分别把鱼炸成金黄色，捞出控油。取炒锅一只刷洗干净，在锅底部放上锅箅，上放香料袋，将炸好的鱼头向外。尾朝里整齐地摆在锅内，将葱放在鱼面上，白菜心放在葱上，摆匀盖严。清汤、酱油、绍酒、白糖兑成汁，倒入锅内，盖严锅盖，中火烧开后移至小火焖煮，待汤汁耗至约剩2/5时加入芝麻油，改为微火焖煮使汤汁稠浓，耗至1/5时，将锅离火，晾凉后将白菜、葱捡放在一', '咸甜适口，甘香味醇，鱼骨适酥。', NULL, NULL, '活黄荡苗鱼（又名噘嘴鲢子）1000克、嫩白菜心100克、大葱50克、鲜姜20克、花椒10克、八角10克、肉桂10克、花生油250克、酱油10克、绍酒15克、白糖20克。');
INSERT INTO `recipe` VALUES (2857, 71, '油焖虾', '1.将大虾剪去须、爪，除去头部沙包和脊背纱线，洗净。 \r\n2.锅上火加油烧至五成热，下入虾，煎至两面呈金黄色，盛出。 \r\n3.锅留底油，放入葱姜丝、料酒、盐、白糖、醋、高汤，下入煎好的虾煮开后用小火焖约5分钟，加味精炒匀，先将虾出锅码盘，余汁收浓后淋在大虾上即成。', '色泽油亮，鲜嫩适口。', NULL, NULL, '大虾750克，味精2克，盐5克，白糖50克，醋10克，料酒20克，葱丝、姜丝、各5克，高汤150克，花生油50克。');
INSERT INTO `recipe` VALUES (2858, 71, '油泼青鱼', '1.香菇和红根切成小丁，在炒勺的开水中稍烫控去水分，和豌豆放在一起，注入鸡汤煨透后，捞出把汤控净。 \r\n2.将活青鱼宰杀放血，刮鳞去鳃和内脏，洗净后放入鱼盘，加入盐、料酒、姜块(拍松)、葱段，上屉蒸20分钟，取出将汤沥去，另把酱油、味精、胡椒粉放入碗内随鱼上屉。 \r\n3.把蒸好的酱油浇在鱼上，撒上豌豆、香菇丁、红根丁，再撒上豆瓣葱和姜末。\r\n4.再用炒勺将植物油烧开后浇在鱼身上，撒上香菜段即成。', '鱼肉鲜嫩，味美可口。', NULL, NULL, '活青鱼1条（约1.5公斤）红根、豆瓣葱15克，鲜豌豆25克，香菜段50克，加工好的水香菇10克，调料植物油100克，料酒15克，酱油50克，盐5克，味精8克，葱段、姜块各10克姜末5克，胡椒粉少许，鸡蛋适量。');
INSERT INTO `recipe` VALUES (2859, 71, '鱼腹藏羊肉', '将洗净鲤鱼整鱼剔骨后加调料稍微腌制；将羊肉、冬笋、香菇等切成米粒状，加调料放入锅中煸炒后填入鱼腹中；将鱼用猪网油裹好后放置于烤炉内烤熟；将红辣椒、黄瓜切成细丝，摆放在烤好的鱼腹上即可。', '山东名菜，相传有春秋时代齐国人易牙所创。在北方水产以鲤鱼为最鲜，肉以羊肉为最鲜，此菜两鲜并用，互相搭配，更显鲜香。', NULL, NULL, '鲜鲤鱼700克，净羊肉200克，香菇50克，冬笋30克，黄瓜20克，红辣椒15克，精盐、酱油各10克，料酒25克，醋、糖各20克。');
INSERT INTO `recipe` VALUES (2860, 71, '鸳鸳珍珠汤', '将偏口鱼肉去皮洗净，在冷水中浸泡10分钟，捞出用刀背砸成细泥放入碗内，加精盐、绍酒、鸡蛋清、熟猪油和水搅匀成鱼料子（以能在水中漂起为宜）。炒锅内放水烧至五成热时，将鱼料捏成直径1厘米的丸子下锅汆熟，捞出放汤碗内。将蛋清打成蛋泡糊，取羹匙2个，匙底抹少许熟猪油，将蛋泡做成两个鸳鸯形上笼旺火蒸熟取出。汤锅内加清汤旺火烧沸，加精盐、酱油、撇去浮沫，加香菜梗，淋上芝麻油浇在汤碗内，把“鸳鸯”轻轻推入汤碗内即成。', '汤清色艳，鱼丸白晶，形象生动。', NULL, NULL, '偏口鱼肉500克、鸡蛋清20克、香菜梗20克、精盐5克、绍酒15克、熟猪油20克、清汤150克、酱油10克。');
INSERT INTO `recipe` VALUES (2861, 71, '云片鹿角菜', '锅内放清水、绿豆，烧沸后捞出绿豆，将鹿角菜放绿豆汤中氽过，然后在清水中浸泡1小时，捞出沥干。将鹿角菜、火腿、玉兰片、黄瓜皮均片成抹刀片，鱼鳞状正面朝下间隔整齐地摆在碗中。清汤、味精、绍酒、食盐调匀浇入碗内，上笼用旺火蒸半小时取出，滗出汤汁扣在盘内。将滗出的原汤，勾上芡，淋入鸡油，浇在菜面上即成。', '香郁爽口，鲜艳美观。是一种风味独特的野菜佳肴。', NULL, NULL, '鹿角菜150克、火腿20克、玉兰片20克、黄瓜皮20克、绿豆15克、清汤100克、味精5克、绍酒15克、淀粉50克、鸡油5克、精盐10克。');
INSERT INTO `recipe` VALUES (2862, 71, '糟炒厚鱼片', '将桂鱼肉洗净，截成4厘米长的段，顺丝用斜刀法劈成2厘米宽、0.4厘米厚的厚鱼片，放在盘中，加湿淀粉、鸡蛋清调匀，再加精盐搅匀。木耳、冬笋切成片。青豆瓣放汤中煮熟入味待用。香糟汁、绍酒、精盐、湿淀粉同放碗内搅匀成汁。炒锅内放入熟猪油，中火烧至四成热（约88度）时，用筷子将鱼片逐片下入，漂起后用勺推搅，熟时倒出沥油。炒锅内留少量油，用中火烧至六成热（约132度）将兑好的汁倒入锅中，待稠后，把鱼片下入锅内，颠翻几次，出锅装盘，撒上青豆瓣即成。', '鱼片厚匀，肉质鲜嫩，糟香扑鼻。', NULL, NULL, '桂鱼肉750克、青豆瓣20克、水发木耳20克、净冬笋20克、淀粉30克、鸡蛋清20克、精盐5克、香糟汁250克、绍酒15克、熟猪油50克。');
INSERT INTO `recipe` VALUES (2863, 71, '糟煨冬笋', '1.先将冬笋的外皮剥去一破两半，再将笋里边的细皮撕去，切掉笋根清洗干净后，放入开水锅中氽透，捞出放于凉水中，再将笋块切成两瓣拍松撕成小条。 \r\n2.炒勺上火，将奶汤烧热，加入白糖、香糟酒、盐和味精，随将冬笋也放入汤内用旺火烧开再移至微火上煨2分钟左右，再转旺火，用湿淀粉勾芡，淋入鸡油即可。', '有糟香味，冬笋鲜脆。', NULL, NULL, '鲜冬笋400克，香糟酒400克，鸡油少许，味精5克，盐3克，白糖15克，奶汤500克。');
INSERT INTO `recipe` VALUES (2864, 71, '炸大扁', '猪肉洗净切成5×3×3厘米的丁，放在碗内加绍酒、味精、酱油、精盐、姜末、葱末调拌浸泡，海参、熟肉膘、熟荸荠、冬笋、薄菜头切成5×3×1厘米的扁丁，放在猪肉丁碗内，调成馅分成15份。炒锅放火上，加少量花生油，小火烧至三成热（约75度），用少量蛋糊放入锅内：取一份馅放在蛋糊上，用手压成扁形，再将蛋糊倒在馅上面，略凝固，推入油锅中，炸至成熟捞出沥油，即成大扁。依次做成15个“大扁”。炒锅移至中火上，油烧至七成热（约175度）时，将制好的大扁放入油锅，炸成杏黄色捞出，倒入盘内，撒上花椒盐；将葱段、甜面酱分别装入小', '外酥脆，里软嫩，疏松香醇，色泽杏黄，配以葱段，甜面酱佐食更佳。', NULL, NULL, '猪肥瘦肉200克、水发海参75克、熟荸荠50克、净冬笋50克、熟蒲芋头、蛋糊适量、精盐4克、绍酒10克、酱油10克、味精1克、葱姜末15克、花生油1500克（实耗油75克）。');
INSERT INTO `recipe` VALUES (2865, 71, '炸豆腐丸子', '豆腐入笼蒸15分钟取出，沥干水分，用刀面压成泥，放入大碗内，加鸡蛋、海米末、甜面酱、酱姜芽末、葱末、酱苤蓝末、香菜末、精盐、花椒面拌匀成馅，挤成直径3.5厘米的丸子，滚上干淀粉，放入八成热（约200度）油锅内炸1－2分钟捞出。待油温开至九成热（约225度）时，再将丸子放入油内，炸至金黄色捞出装盘。外带花椒盐或糖醋汁上席食用。', '丸子外焦里嫩，味道鲜美，食之软脆适口，外配调料，余味隽永。', NULL, NULL, '泰安豆腐200克、鸡蛋20克、海米末50克、甜面浆25克、酱姜末10克、葱末10克、酱苤蓝末10克、香菜末10克、精盐4克、花椒面5克、淀粉50克、花椒盐10克、糖醋汁20克、花生油750克（实耗油75克）。');
INSERT INTO `recipe` VALUES (2866, 71, '炸佛手通脊', '1.将通脊肉斜刀切成7厘米长的5个梯形段，再把每段一分为二切成10片(每片约重75克)。然后顺着肉片宽边切4刀(如手指缝)，窄边不切，用料酒、味精、盐、毛姜水稍腌。将肉片蘸上干淀粉，刷上用鸡蛋打散搅拌而成的鸡蛋液两面沾上面包渣拍牢，照此再反复一次，将中间3条肉向下变成“佛手伏”。 \r\n2.炒勺将植物油烧至7成热，把佛手肉卷置于漏勺内放入油中炸挺，这时将勺离火浸一浸，待肉卷在油内漂起，继续加热炸至金黄色，捞出沥油。将佛手肉卷顺切成两块，摆在盘中(码成原形)，浇上辣酱油，用洗净的生菜围边即成。', '造型美观，鲜香酥脆，别具一格。', NULL, NULL, '猪通脊肉750克，鸡蛋150克，生菜叶150克，细面包渣200克，植物油800克(实耗约100克)，味精、盐各5克，料酒、辣酱油各10克，干淀粉50克。');
INSERT INTO `recipe` VALUES (2867, 71, '炸灌汤丸子', '猪瘦肉洗净剁成肉泥，放入碗内，加入精盐、鸡蛋清、湿淀粉拌匀，高汤冻切1厘米见方的块，共12块待用。用肉泥做成直径约2.5厘米的丸子，肉丸中间包一块高汤冻，表面均匀地沾上一层面包末，彼此共做12个。炒锅上中火，倒入花生油，烧至五六成热（约125～1500度）时逐个放入肉丸，炸至棕黄色捞出装盘即可，以五香粉蘸食。', '肉丸外皮酥脆，内包汤汁。色泽棕黄，香鲜可口，食时蘸五香粉味更佳。', NULL, NULL, '猪瘦肉250克、高汤冻100克、面包末50克、花生油500克、淀粉50克、鸡蛋50克、精盐10克，五香粉5克。');
INSERT INTO `recipe` VALUES (2868, 71, '炸鸡扇', '1.将鸡脯翅骨上的肉刮净，修成扇形，在鸡脯上切十字花刀，加料酒、精盐腌渍3分钟。 \r\n2.倒入蛋清拌匀，再蘸上馍馍渣，将鸡扇入5成热油锅炸熟捞出，待油8成熟时，再炸至金黄色捞出沥油，装盘，撒上花椒面即成。', NULL, NULL, NULL, '雏鸡脯7个(带翅骨1节)、鸡蛋清2个、料酒、精盐。');
INSERT INTO `recipe` VALUES (2869, 71, '炸菊花虾包', '1.虾仁一切两段，海参、荸荠、冬菇、火腿切丁，焯水控干后再装盘，加调料腌5分钟，再加蛋清、淀粉调均制馅； \r\n2.将猪网油切成5张8厘米见方的小片，放上馅包好扎口，沾匀蛋清糊； \r\n3.油锅烧至5成热，将虾包逐个放入，炸至金黄色捞出，每个改成4瓣呈荷花形，装盘即成。', NULL, NULL, NULL, '虾仁250克、猪大油500克、鸡蛋清3个、水发海参、冬菇、荸荠、火腿、猪油网适量。');
INSERT INTO `recipe` VALUES (2870, 71, '炸龙肠', '对虾去泥肠、筋洗净，由脊背片开成片。鸡里脊肉、海参均片成0.2厘米厚的片，荸荠、火腿均切成片。海参片入开水锅中略烫捞起、把切好后的各种原料放入盛器内，加精盐、味精、酱油、绍酒、胡椒面、芝麻油调和成馅。鸡蛋清放入碗内，加湿淀粉调匀成蛋清糊备用。猪网油切成边长12厘米的正方形片，铺平，每块抹上少许蛋清糊，放一份馅，卷成直径l.5厘米、长3.5厘米的卷，上笼旺火蒸熟，取出滗去汤汁，周身蘸匀蛋清糊再粘上一层芝麻，放入五成热（约125度）的油锅中，炸至芝麻泛黄时捞出，整齐摆入盘内即成。', '外焦酥，里鲜嫩，鲜咸适口，芝麻清香。', NULL, NULL, '对虾250克、鸡里脊肉50克、海参25克、火腿5克、荸荠25克、鸡蛋清20克。精盐4克、味精1克、酱油10克、绍酒15克、胡椒面10克、芝麻油10克、淀粉50克、菜油150克。');
INSERT INTO `recipe` VALUES (2871, 71, '炸素鸡排', '熟山药去皮制成泥，冬菇、冬笋切成0.3厘米小方的丁，同放碗内加葱椒泥、味精、精盐、鸡蛋清调匀，取豆腐皮切成长7.5厘米、宽3厘米的椭圆形片，放入调好的山药泥摊平在豆腐片上，再分别覆盖一张豆腐片，然后插入前把，做成“鸡排”，馒头去皮切成0.2厘米见方的末。将鸡蛋清与淀粉、精盐一起拌成蛋清糊，将鸡排沾匀蛋清糊。再沾匀馒头末，放入四成热（约100度）油锅中炸成金黄色出锅，沥油后摆在放有香菜的盘内，外带葱白、甜面酱碟即可。', '造型美观，色泽杏黄。外酥香，里软嫩，清爽味鲜，增进食欲，并有健脾除湿，益精补气之功效。', NULL, NULL, '熟山药400克、冬菇25克、冬笋25克、鸡蛋清50克、豆腐皮50克、茄把25克、馒头25克、葱15克、花椒3克、味精1克、精盐5克、淀粉50克、花生油150克、香菜15克、葱白15克、甜面酱50克。');
INSERT INTO `recipe` VALUES (2872, 71, '炸鸳鸯嘎渣', '鸡蛋加入碗内，另加蛋黄、清水、湿淀粉。桂花酱、面粉，搅匀成汁，分盛两碗。将山楂糕研碎放入其中一碗，搅匀，炒锅滑油置小火上，分别先将其中一碗蛋液倒入锅内，用小铲不停地搅动，熟时出锅倒入摊平成嘎渣。另一碗亦用其法覆入其上摊平。即成鸳鸯嘎渣。两片厚度约1.5厘米。面粉铺在案上，晾凉的嘎渣放在上面，切成0.5厘米宽的长条，再切成象眼块，沾匀面粉，下入六成热（约150度）油锅中，端在小火上，翻动，炸成杏黄色时，捞出沥油，装盘撒上白糖即成。', '颜色鲜艳，黄红互映，质地软嫩，甘酸甜美，雅致可口。', NULL, NULL, '鸡蛋50克、桂花酱10克、山楂糕15克、鸡蛋黄20克、湿淀粉10克、面粉10克、白糖20克、花生油500克。');
INSERT INTO `recipe` VALUES (2873, 71, '炸芝麻里脊', '将里脊肉批成1厘米厚的片，两面均匀的剖上十字花刀，再改成长3.5厘米、宽1.5厘米的条放入盛器中，加精盐、味精、绍酒、酱油腌渍入味。另一只碗内放入蛋清，湿淀粉搅匀成糊备用。炒锅内放入花生油，用中火烧至六成热（约150度）时，将肉逐条粘上蛋糊、再蘸满一层芝麻仁，放入油内炸透捞出，待油温升至八成热时，再将肉投入油内一炸，炸至呈金黄色时捞出沥油，装盘即成。', '色泽金黄，外焦里嫩，香鲜可口，猪里脊肉鲜嫩。', NULL, NULL, '猪里脊肉400克、鸡蛋50克、芝麻25克、精盐15克、味精10克、绍酒15克、酱油10克、花生油500克（实耗油100克）、淀粉10克。');
INSERT INTO `recipe` VALUES (2874, 71, '炸脂盖', '将羊肉洗净放入冷水锅内，煮约5分钟使血水浸出，再捞出晾凉，切成长8厘米、宽2.5厘米、厚0.8厘米的片，平放在盘内，加入酱油、葱段、姜片、蒜片入笼蒸熟（约蒸90分钟）取出，滗净汤汁，去掉葱、姜、蒜片。鸡蛋打入碗内，加入湿淀粉搅匀成糊状待用。炒锅放入花生油，置中火上烧至八成热（约200度）时，将肉片逐一滚匀鸡蛋糊放入油内，炸至九成熟时捞出，待油温升至九成热（约225度）时，再投入肉片炸成金黄色捞出，将炸好的肉片切成斜块，装入盘内即成。将甜面酱与芝麻油一起拌匀装于味碟内，再将葱段、蒜瓣另盛一小碟，随菜上桌佐食', '色泽金黄，外酥里嫩。', NULL, NULL, '羊五花肉500克、鸡蛋50克、酱油10克、大葱15克、生姜15克、大蒜10克、湿淀粉10克、甜面酱15克、芝麻油15克。');
INSERT INTO `recipe` VALUES (2875, 71, '珍珠豆腐羹', '鸡脯肉切成0.5厘米见方的丁，加入鸡蛋清、精盐、湿淀粉拌匀。水发海参、黄蛋糕、玉兰片切成0.8厘米见方的丁。菠菜梗切成丁，入沸水中略焯，捞出沥水。炒锅内放入花生油至五成热（约150 度），放入鸡脯丁划油捞出。净锅内放入清汤，加入酱油、精盐、味精，倒入鸡脯肉、海参、豆腐、黄蛋糕、水发玉兰片，沸后撇去浮沫，用湿淀粉勾芡，淋鸡油出锅即成。', '色泽鲜艳夺目，黑白黄绿各色相间，配料为小丁，形如珍珠，汤鲜料美，风味清鲜，营养丰富。', NULL, NULL, '豆腐250克、鸡脯肉20克、鸡蛋清15克、水发、海参25克、黄蛋糕20克、玉兰片20克、菠菜梗15克、精盐5克、淀粉50克、花生油100克、酱油10克、味精2克、鸡油5克、清汤100克。');
INSERT INTO `recipe` VALUES (2876, 71, '蒸扒三丝干贝', '将干贝放入沸水中氽一下，捞出沥干水分，再整齐均匀地沿碗底直到碗边排列一层，冬笋、冬菇、火腿均切丝。炒锅内加入少量猪油，用旺火烧七成热（约154度），放入葱姜略煸，加入笋丝、冬菇丝、火腿丝，煸炒，再加精盐、绍酒、酱油、味精、清汤，待沸后勾芡，淋上芝麻油，盛入干贝碗内压紧、压平，放笼中蒸10分钟，取出扣入大圆盘内。炒锅内加入清汤、精盐、味精、绍酒，待沸后，用湿淀粉勾芡，淋上鸡油浇于干贝上即成。', '造型美观，色泽悦目，味清鲜软嫩，原汁原味。', NULL, NULL, '水发干贝200克、冬笋、水发冬菇、火腿各100克、精盐12克、绍酒30克、酱油10克、味精7.5克、葱姜各10克、清汤300克、麻油15克、湿淀粉25克、鸡油10克、猪油100克。');
INSERT INTO `recipe` VALUES (2877, 71, '炸肉骨碌烹', '1.先把五花肉刮洗净煮7成熟，捞出，皮朝上放在墩上，每隔0.6厘米深切一刀（深度为肉的一半），再隔0.6厘米切下一大片，把世下的肉片平放在墩上，再从肉皮面隔0.6厘米深切一刀（深度同上），用湿淀粉把肉骨棣 （肉条）浆好。 \r\n2.炒勺上旺火将植物油烧至7成熟时，把浆好的“肉骨碌”沾上干淀粉，散开十字花刀炸至金黄色时倒入漏勺沥油。原勺留底油倒入肉骨碌，烹入用葱丝、姜丝、姜片和青蒜段加鸡汤、盐、料酒、米醋和香油对好的汁，颠翻几下即成。', '外焦里嫩，肉味醇香。', NULL, NULL, '五花肉750克，葱丝25克，姜丝20克，蒜片15克，青蒜段25克，植物油900克（实耗约100克），酱油、醋各15克，料酒、香油各10克，盐6克，湿淀粉50克，干淀粉25克。');
INSERT INTO `recipe` VALUES (2878, 81, '炒鸡什件', '1.把鸡胗两面白色的皮去掉切成片，肝片成片。随之将难胗、肝用料油、酱油和盐腌好，浆些湿淀粉再拌些油。木耳用水发透择洗干净，青菜叶切段，茎切成片。葱、姜、蒜均切片。\r\n2.用酱油、料酒、味精、湿淀粉和葱姜蒜、汤在一起对成汁。\r\n3.用炒勺将油烧热后，随下胗、肝翻炒，至将要熟时，把木耳、青菜倒入再继续翻炒，并将对好的汁倾入勺中，并翻炒均匀，滴几滴醋，起锅将菜盛入盘中即成。', '味鲜醇香，胗肝脆嫩。', NULL, NULL, '鸡胗肝净350克，干木耳10克，青菜100克，大油80克，姜20克，葱25克，蒜20克，湿淀粉30克，料酒25克，酱油20克，盐5克，味精5克，醋3克，汤适量。');
INSERT INTO `recipe` VALUES (2879, 71, '整鱼两吃', '将妒子鱼去内脏、鳃及鳍，切下鱼头，劈两片。再剔下两面鱼肉，去净鱼腹部细刺。取一片鱼肉，皮朝下放案板上，从鱼身前部起，用劈刀法和直刀法将鱼改成松鼠花刀，连同鱼头用葱姜汁精盐腌渍。另一片鱼肉用斜刀法，以鱼尾劈起至前身部（鱼片厚0.2厘米，依肉皮相联）待用。虾仁、猪肥肉剁成泥，加清汤、精盐、鸡蛋清、葱姜汁、芝麻油，搅成虾馅，分别抹在批好的鱼片上，卷成直径约1厘米的鱼卷至尾部，即成合页鱼。然后用鸡蛋清、湿淀粉，调成稀汁，均匀抹在鱼卷和半个鱼头上，加葱段、姜片，上笼蒸7－8分钟，至熟取出，去掉葱姜，放鱼盘一边。炒锅', '一菜两色，一鱼两味，咸鲜、酸甜适口，造型美观。', NULL, NULL, '鲜妒子鱼750克、虾仁25克、胡萝卜15克、香菜15克、猪肥肉50克、青豆10克、精面粉15克、葱10克、姜10克、精盐2克、鸡蛋清25克、芝麻油10克、淀粉30克、花生油250克、红樱桃2个、番茄酱10克、白糖20克、醋20克、绍酒15克。');
INSERT INTO `recipe` VALUES (2880, 71, '芝麻鱼球', '将偏口鱼肉洗净，与肥肉膘一起剁成细泥，放碗中加精盐、绍酒、芝麻油、湿淀粉、鸡蛋清、葱、姜末、清水搅匀成鱼泥。芝麻放在锅中偏炒至熟，把鱼泥料挤成直径1.2厘米的丸子，周身上沾芝麻，炒锅内加花生油，中火烧至六成热（约150度）时，将鱼丸逐个放入油内炸熟，呈金黄色时捞出，摆入盘中即成。', '造型美观，鲜香四溢，色泽金。', NULL, NULL, '偏口鱼肉450克、猪肥肉膘50克、精盐3克、绍酒15克、芝麻油10克、湿淀粉50克、葱10克、姜末15克、鸡蛋清15克、芝麻20克、花生油100克。');
INSERT INTO `recipe` VALUES (2881, 71, '纸包三鲜', '将鲜虾肉从脊背片开，去虾肠，片成小坡刀片。海参、鸡脯肉均片成小坡刀片。冬菇、冬笋片薄，切成小象眼片。将上述原料放入碗内。加蛋清、葱姜末。精盐、味精、绍酒、芝麻油搅拌均匀浸渍入味。把玻璃纸平铺在菜墩上，均匀地放进调好的鲜料，包成长6厘米、宽3厘米的纸包，放入五成热（约125度）的油锅内，炸成金黄色熟后捞出沥油，整齐地摆入盘内。稍装饰美化即成。', '造型美观，鲜嫩爽口，原汁原味。', NULL, NULL, '鲜虾肉100克、海参50克、鸡脯肉100克、冬菇25克、冬笋20克、鸡蛋清20克、玻璃纸25克。葱15克、姜10克、精盐5克、味精1克、绍酒15克、芝麻油10克、菜油500克（实耗油50克）。');
INSERT INTO `recipe` VALUES (2882, 71, '抓炒鱼仁', '将偏口鱼肉洗净，劈成0.8厘米厚的大片，剖上十字花刀，切成长、宽各1.5厘米的鱼仁，用精盐、绍酒腌渍入味。鸡蛋黄放入碗内，加湿淀粉调成蛋黄糊，放入鱼仁抓匀，备用。另取一碗，放入酱油、醋、清汤、精盐、白糖、湿淀粉配成芡汁待用。炒锅内加花生油；用中火烧至六成热（150度）时，将鱼仁下入油内炸熟，呈金黄色时捞出沥油。炒锅内留少量油，用旺火烧至七成热（约175度）时，用葱、姜、蒜片、花椒面爆炒几下，加入绍酒、放入鱼块、青豆及配好的芡汁，迅速翻炒，淋上芝麻油装盘即成。', '鱼仁金黄，入口香脆，具有醇、麻、甜、咸等多种味道。', NULL, NULL, '偏口鱼肉750克、熟青豆100克、精盐5克、绍酒15克、鸡蛋黄20克、淀粉30克、酱油10克、醋10克、清汤150克、白糖20克、葱、姜各15克、花椒10克、芝麻油5克。');
INSERT INTO `recipe` VALUES (2883, 81, '白蹦鱼丁', '1.将目鱼肉用刀片去掉皮下的黑色肉和靠鱼骨的红色肉，切成长2.3厘米、宽1.6厘米的丁，洗净后用清水泡2分钟，使其白嫩，然后沥去水，先加入精盐1克拌匀，再加入湿淀粉35克和鸡蛋清搅匀浆好。蒜末加清水10克泡成蒜汁。\r\n2.炒勺置旺火，倒入醋和清水1500克。烧开后，把浆好的鱼丁逐个拨入，煮2分钟，捞出用凉水泡凉，滗去水待用。\r\n3.炒锅置于旺火，注入熟鸡蛋油40克，烧至六成热，下入葱段、姜片稍炸，随即加入绍酒7.5克和鸡鸭汤，煮1至2分钟。然后，捞出葱段姜片，放入煮过的鱼丁，同时加入味精1克、精盐0.5克', '“白蹦鱼丁”是清真名菜，以北京鸿宾楼制作最佳。白蹦，是清真独特的烹调方法，沿袭已久。其烹调过程是主料先煮后火靠，成菜色白如玉。用这种方法制出的“白蹦鱼丁”，色泽洁白，清雅素丽；鱼丁软嫩而不碎，清鲜味爽。', '制作此菜，必须选用活鱼，桂鱼、草鱼亦可，鱼丁用清水漂去污血，使其洁白，然后依法制作白蹦鱼丁。', NULL, '目鱼肉750克，葱段5克，鸡蛋清1个，姜片2.5克，牛奶100克，蒜末2.5克，湿淀粉50克，醋2.5克，鸡鸭汤250克，绍酒10克，精盐1.5克，熟鸡油60克，味精1.5克。');
INSERT INTO `recipe` VALUES (2884, 81, '白炒三七花田鸡', '1.将田鸡剥皮割下后腿剔去骨，洗净入碗。加葱10克、姜5克、料酒10克，腌渍15分钟捞出晾干水分。\r\n2.田鸡肉入碗，加盐5克、鸡蛋清、水淀粉20克上浆。\r\n3.三七花放入淡明矾水中浸泡30分钟，捞出漂洗干净。\r\n4.炒勺上火烧热，注入油，下菜芯过油，捞入漏勺内，锅上火，注入鸡汤200毫升，放盐，倒入菜芯，沸后，捞出摆在菜盘外沿。\r\n5.将熟萝卜雕刻成小花，摆放在菜芯里面一圈。\r\n6.炒勺上火，烧热注入油，放入葱20克、姜15克，下田鸡肉，滑散呈白色，倒入漏勺，捡出葱、姜。\r\n7.炒勺上火，舀入鸡汤200', '田鸡，学名虎纹蛙。生长于稻田、水沟、沼泽地。体长最在可达66毫米，头略扁平，前肢较为粗状，后肢长，体背及体侧有较显著的疣粒，腹面皮肤光滑，腹背为橄榄色或带黄色。肉细嫩，鲜美，有清热解毒、利尿消肿，补虚弱的功能。三七是云南出产的名贵药材，有止血强心，散瘀生肌，消肿定痛的显著功效。李时珍在《本草纲目》中誉之为“金不换”。《本草纲目拾遗》称道：“人参补气第一，三七补血第一味同而功亦等，故称人参三七，为中药中最珍贵者。”其根、茎、花、叶均可入药。白炒三七花田鸡，白绿相映，三七花清香浓郁，田鸡肉细嫩鲜美，是一款造型', '田鸡肉用调料腌渍15分钟，使其入底味，便于后面的烹调。', NULL, '活田鸡600克，料酒20克，鲜三七花10克，精盐15克，油菜芯200克，味精3克，熟红萝卜200克，麻油3克，鸡蛋清1个，胡椒粉2克，鸡汤400毫升，水淀粉30克，葱丝30克，猪油1000克，姜丝20克，明矾水10克。');
INSERT INTO `recipe` VALUES (2885, 81, '白鹅下蛋', '1.鲜鹅1只去毛、开膛、洗净，入盐水浸30分钟。　\r\n2.用羊肉馅、江米饭加葱姜蒜末打成丸子馅。　\r\n3.把丸子放入鹅膛（裹上蛋清）。\r\n4.在鹅的外皮上刷一层蜂蜜和油。\r\n5.放入微波炉大火烤10分钟，转文火烤10分钟。\r\n6.取出置椭圆形或方形盘中即可。吃时用筷子敲一下鹅屁股，每敲一下就会掉下一个丸子。', NULL, NULL, NULL, '鲜鹅、羊肉馅、江米饭、葱姜蒜末、蜂蜜。');
INSERT INTO `recipe` VALUES (2886, 81, '爆牛肉', '1.将牛里脊肉上筋膜剥除掉，切成3厘米长的薄片，加入酱油30克、湿淀粉、清水（30-35克）拌匀，把青蒜切成3厘米长的长段。\r\n2.炒勺内倒入香油，在旺火上烧到冒烟，倾入拌好的肉片，爆15-20秒钟后倒入漏勺里沥出油。\r\n3.炒勺再放回旺火上，加入香油（30克）烧热，再加上葱斜段、姜末、蒜末，急炒成黄色。\r\n4.再将爆好的肉片倒入，随即放入料酒、酱油、醋、水（少许），再炒15-20秒钟，撒上青蒜段即成。', '色泽鲜亮，肉片松散，软嫩清香，爽口不腻。', NULL, NULL, '牛里脊肉250克，香油500克（实耗约60克），湿淀粉25克，青蒜6克，蒜末3克，料酒少许，酱油60克，醋少许，葱斜段20克，姜末2克。');
INSERT INTO `recipe` VALUES (2887, 81, '北菇鹅掌', '1.将鹅脚洗净，用开水烫熟，取出脱去骨、筋、爪，每只切成3块，下开水锅氽泡一下捞起，用冷清水漂凉待用。\r\n2.烧热鼎倒入猪油，油温四、五成热时，将鹅脚下鼎熘炸2分钟，捞起。再把香菇洗净，笋花切片后一起下鼎炒香，投进鹅脚，加入上汤，用中火焖约20分钟，投入味精、精盐、胡椒粉、芝麻油、湿淀粉勾芡，放入火腿片，起鼎装盘即成。', '富有胶质，软滑、味美、醇香。', NULL, NULL, '鹅脚10只，湿香菇50克，熟火腿15克，笋花30克，猪油500克（耗75克），上汤250克，精盐、湿淀粉、味精、芝麻油、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2888, 81, '玻璃酥鸡', '1.将鸡洗净后，拆肉切成两半，抹精盐、绍酒后，放在盘里。膘肉、韭黄切成细丁，火腿切末盖在鸡肉上，再将面粉、鸡蛋清拌匀抹在上面。\r\n2.将鼎烧热，下油，烧至四、五成热，放入鸡肉，用热鼎软油炸至金黄色捞起，沥去油，用刀切块。原鼎加入精盐、味精、葱椒、芝麻油，用淀粉水勾薄糊淋在盘里，鸡肉块放在上面即成。', '色泽金黄，外酥内嫩，鲜香爽滑。', NULL, NULL, '光鸡1只（约750克），鸡蛋2只，面粉60克，绍酒20克，韭黄50克，膘肉25克，熟火腿10克，猪油1000克（耗75克），味精、葱椒、芝麻油、精盐、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (2889, 81, '菠萝鸡块', '1.鸡洗净，切块，用生抽、胡椒粉腌15分钟。菠萝切成小块备用。 \r\n2.鸡块倒入沸水中煮2分钟，捞出洗净烧红锅，下油，爆香葱，放入鸡块爆透，占酒，加入少量清水，加盖烧5分钟后加入菠萝，兜炒几下加盐、味精，用生粉勾芡即可上盘。', NULL, NULL, NULL, '嫩鸡半只，菠萝四大片，酒、葱少许。生粉、胡椒粉、生抽适量。');
INSERT INTO `recipe` VALUES (2890, 81, '叉烧炒蛋', '1.将鸡蛋打入碗内，用筷子搅打。\r\n2.将叉烧肉切成0.5厘米见方的小丁，放入鸡蛋碗内，加入胡椒粉、盐、味精调均匀。\r\n3.炒勺内倒入大油，上火烧至7-8成热，下入葱花，倒入调好的鸡蛋，用文火煎至两面呈金黄色，至熟即成。', '色泽金黄，味道鲜美。', NULL, NULL, '鸡蛋4个，叉烧肉100克，豆瓣葱30克，大油100克，胡椒粉少许，盐3克，味精3克。');
INSERT INTO `recipe` VALUES (2891, 81, '叉烧鸭', '1.将宰杀去毛的填鸭取来，左右边膀下切开3-4厘米长的口子，把右手的食指与中指伸进去，掏出内脏。从鸭腿和翅膀的拐弯处，分别剁去鸭掌和膀尖，然后洗干净。\r\n2.用温水把干荷叶浸泡软以后切碎，与葱、姜、花椒掺和一起，从开口处塞入鸭肚内。\r\n3.用特制的铁叉子由鸭腿内侧根部穿至鸭肩（不穿透），再把叉尖捏拢，经过鸭颈穿出，把鸭头横着穿在两股鸭叉上，让叉尖从头部穿出裸露18-20厘米长。\r\n4.用开水反复（5-6次）浇烫鸭身，使鸭皮膨胀，然后用5倍清水调稀饴糖，在鸭身上刷抹一层，放于通风处晾干。\r\n5.将鸭叉架在燃', '颜色枣红油亮，鸭皮酥脆醇香，肉质柔软鲜嫩，味美常吃不腻。一般都是将鸭肉与特制的甜面酱（甜面酱、白糖、香油搅拌均匀后蒸熟），再加上葱段，用荷叶饼卷着吃，则别有风味。', NULL, NULL, '北京填鸭1只（重约1.5-2公斤），干荷叶5-6片，花椒7-8粒，饴糖50克，葱段60克，姜片520克。');
INSERT INTO `recipe` VALUES (2892, 81, '炒葱椒鸡', '将鸡杀血去毛洗净，脱骨拆净肉加上花刀法，切成块状。然后抹上淀粉水，下鼎炸熟捞出，将川椒末和青葱末炒酥，再把炸好的鸡肉投入翻炒，加入味精、胡椒粉、精盐、芝麻油，用少许淀粉水勾薄糊，起鼎装盘即成。（盘的周围用真珠花菜叶炸酥拼盘）。', '色泽淡黄、肉滑鲜嫩、葱椒香味浓郁。', NULL, NULL, '鸡1只（约1250克），青葱末30克，川椒末5克，猪油750克（耗500克）芝麻油、味精、精盐、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (2893, 81, '炒生鸡丝', '1.把冬笋削去筋皮切成丝，在开水锅中氽透，捞入凉水中。鸡胸脯肉去筋皮切成丝，用2克盐、鸡蛋清和湿淀粉适量拌均匀浆好。\r\n2.炒勺上火烧热，放入植物油，烧到5-6成热，把浆好的鸡丝放入，拨散滑透，捞出把油滤去。\r\n3.炒勺再回到火上，倒入25克植物油烧热，把冬笋丝放入勺内煸炒，烹入料酒，加入味精、鸡汤和盐2克，再加入适量湿淀粉，然后把鸡丝倒入翻炒几下，将鸡油淋入即成。', '色泽洁白，鸡丝软嫩，味道鲜美。', NULL, NULL, '鸡胸脯肉400克，罐头冬笋150克，鸡蛋清50克，植物油800克(实耗约50克)，鸡油10克，料酒15克，味精5克，盐4克，湿淀粉25克，鸡汤50克。');
INSERT INTO `recipe` VALUES (2894, 65, '成都蛋汤', '1.将鸡蛋去壳，放入碗内用力调匀，木耳洗净。\r\n2.汤锅置火上，放入猪油烧热，鸡蛋入锅，煎至两面微黄，当蛋质松软时，用勺将鸡蛋捣散，加入汤，再下精盐、木耳、菜心、味精烧开，入味后淋上猪油即成。', NULL, '煎鸡蛋要注意控制火候，不要大火，以免蛋发焦变硬。此汤汁色奶白，味浓鲜香，蛋质酥软，木耳脆嫩，成都地区常用，故名。可作四季汤菜，酒饭均宜。', NULL, '鸡蛋4个，水发木耳50克，菜心100克，精盐4克，猪油75克，味精2克，浓白汤1000克。');
INSERT INTO `recipe` VALUES (2895, 81, '炊水晶鸡', '1.将鸡洗净拆去骨，然后剥出鸡皮（皮略带肉），再将鸡皮切成圆形（直径约3厘米）24件。白肉用刀片成24片圆片。\r\n2.将鸡肉、虾肉切碎剁烂，加入鸡蛋清、绍酒、味精、精盐和火腿末10克搅匀做成馅料。\r\n3.将馅料分成24粒，分别放在24件鸡皮上面，每件贴上1片白肉，白肉崐上面贴上火腿末，然后放入蒸笼炊约12分钟即熟，取出将原汤倒入鼎内，加入味精、精盐、芝麻油用淀粉水勾薄糊淋上即成。', '肉嫩清香，味美可口。', NULL, NULL, '光鸡1只（约750克），白肉100克，虾肉150克，火腿末25克，鸡蛋清1只，精盐、绍酒、味精、芝麻油、淀粉各适量。');
INSERT INTO `recipe` VALUES (2896, 81, '葱油白切鸡', '1.将鸡在滚开汤锅内浸烫熟（不宜过熟，一般15分钟左右即可），取出后切成块（保持原鸡形状），装上头翅。\r\n2.葱、姜切成细丝。\r\n3.炒勺内倒入油，在旺火上烧开，鸡身上撒上姜丝，然后以热油浇淋，而后再放葱丝。\r\n4.炒勺内下汤200克，在文火上烧开，再加入胡椒粉、盐、味精等熬成汁，浇淋于鸡上即成。', '色洁白带油黄，具有葱油香味，葱段打花镶边，食时带芥末酱。', NULL, NULL, '净肥嫩雏母鸡1只（重约0.8-1.2公斤为好），葱120克，姜40克，植物油120克，胡椒粉少许，盐15克，味精8克。');
INSERT INTO `recipe` VALUES (2897, 81, '担担鸡', '1.用50克鸡汤、2克盐、3克味精、5克料酒和适量湿淀粉对成汁。冬笋，青、红椒切成片。面包切成长1.2厘米，宽、厚各6厘米的长方形块。将鸡胸脯肉400克去皮筋后切成片，用鸡蛋清、盐2.5克、料酒5克加适量湿淀粉搅拌均匀并浆好备用。将余量鸡胸脯肉去皮筋后，剁成茸放盆内，加入葱、姜末、味精、盐各2.5克、料酒5克、猪肉末和适量湿淀粉拌匀做成馅。\r\n2.炒勺上旺火，把植物油烧至6成热，分别把浆好的肉片以及冬笋、青、红椒放入油内滑透，捞出沥油。待油烧至7成热，把用肉馅挤成的丸子放入，炸透后捞出沥油。再将面包炸成金', '摆形似鸡，美观别致，鸡片香嫩，丸子酥香。', NULL, NULL, '鸡胸脯肉600克，鸡膀1对，鸡头1个，笋鸡腿2个，猪肉末200克，净冬笋，青、红椒各25克，鸡蛋清50克，面包150克，植物油900克（实耗约100克），料酒、姜末各15克，葱末25克，味精8克，盐7克，湿淀粉100克，鸡汤50克。');
INSERT INTO `recipe` VALUES (2898, 81, '粉蒸牛肉', '1.大米磨成粗粉。葱切成葱花。豆豉剁细。姜捣烂后用少许泡之，香菜洗净切碎。\r\n2.牛肉切成薄片（长4厘米、宽2.5厘米）用油、酱油、姜水豆豉、豆瓣酱、胡椒粉、大米粉等拌匀，放入碗中上屉蒸熟，取出翻扣盘中撒上葱花。另用小碟盛香菜、花椒粉、胡椒粉上桌。', '鲜嫩醇香、麻辣适口。', NULL, NULL, '瘦牛肉370克，大米75克，植物油50克，酱油30克，花椒、胡椒粉各3克，辣椒粉2克，葱、姜各8克，料酒13克，豆瓣酱30克，四川豆豉5克，香菜少许。');
INSERT INTO `recipe` VALUES (2899, 81, '干煸扁豆', '1.将扁豆去筋、洗干净，滤干水分；葱、姜切成末；豆瓣酱用刀剁碎。\r\n2.将炒锅放在火上，放入油，烧到油面略起青烟、将肉馅放入煸炒，加入豆瓣酱，煸炒到肉馅中水分基本出净，再放入料酒、酱油、葱末、姜末、糖和盐。将扁豆下锅，不停地翻炒、待扁豆中水分基本出净、色泽嫩绿时加入味精、翻炒均匀即可出锅装盘。', '口味咸香辣、色泽嫩绿，口感脆嫩。', '本菜以牛肉馅为最佳，用猪肉馅也可以。扁豆须新鲜嫩绿，否则制作后口感过老、无法食用，煸炒扁豆时应选用较小的火力，以免火力过量将扁豆炒糊。', NULL, '扁豆200克、牛肉馅100克、盐4克、酱油5克、料酒2.5克、味精1.5克，豆瓣酱3.5克、葱5克、姜2.5克、油70克、糖1克。');
INSERT INTO `recipe` VALUES (2900, 81, '干煽牛肉', '1.将牛肉筋膜剥除掉，切成0.1-0.2厘米厚的薄片，横着肉纹切成5-6厘米长的细丝。把芹菜的根、筋、叶择去，洗干净后切成2-3厘米长的段（过粗的劈开）。将豆瓣辣椒酱剁成细泥。\r\n2.用旺火烧热炒勺，倒入植物油烧到6-7成热，放入牛肉丝快速煽炒几下，加入盐，再炒至酥脆，肉变成枣红地，再加入豆瓣辣椒酱泥和辣椒粉，再颠炒几下。\r\n3.后顺次加入白糖、料酒、酱油、味精，翻炒均匀，再加入芹菜、青蒜、姜丝、拌炒几下后，喷点醋颠翻几下盛出，在上面撒上花椒即可。', '肉丝色泽酱红酥香，芹菜嫩绿清脆，麻辣咸甜味美，促进食欲佳肴。', NULL, NULL, '瘦嫩牛肉600克，芹菜60克，植物油120克，豆瓣辣椒酱30克，辣椒粉6克，白糖8克，料酒15克，青蒜段30克，姜丝（去皮）6克，盐、酱油、醋、味精适量。');
INSERT INTO `recipe` VALUES (2901, 81, '干烧雁鹅', '1.将桂皮、八角、甘草放入小布袋里，扎口后，放入汤锅，加清水3000克，酱油、精盐、白糖、芫荽头、绍酒、南姜，用中火烧沸后，加入肥鹅，转用小火滚约10分钟后，倒出鹅腔内的汤水，再放入汤锅，边滚边转动，约40分钟熟。取出晾干后，片下两边鹅肉，脱出四柱骨，把鹅脊骨剁成方块，用湿淀粉10春涂上抹匀，再用湿淀粉20克涂抹鹅皮。\r\n2.用中火烧热油鼎，下油烧至五成热时，先放下鹅骨后放鹅肉（皮向上），端离火口炸浸，再端回炉上，继续炸至骨硬、皮脆、呈金黄色时捞起，把鹅骨放入餐盘，鹅肉用斜刀切成长6厘米、宽4厘米、厚5毫', '色泽紫红，皮脆肉嫩，甘香味浓，蘸甜酱吃，极具潮汕风味。', '雁是大型游禽，形似家鹅，属侯鸟一类，每年春分后北方，秋分后又南回，因季节难逢，遂改用家鹅代替。', NULL, '光鹅1只（约1750克），南姜50克，酱油250克，白糖50克，绍酒50克，精盐30克，芫荽25克，猪油1500克（耗100克），酸甜萝卜150克，胡椒油25克，淀粉25克，桂皮、八角、甘草各5克。');
INSERT INTO `recipe` VALUES (2902, 81, '蛤蟆鸡', '1.把活雏鸡宰杀后，放开水内泡好褪净毛，然后由脊背上大开膛，掏出内脏，用水内外洗干净。用刀把脊骨砍劈几下，并把大腿骨砸断；把锁骨砍断。如此，即可防止油炸时卷缩起来。\r\n2.把植物油倒入炒勺内，放旺火上烧热（未冒烟时），把全部蘸上酱油的鸡放入，先炸鸡胸脯的一面，当鸡皮已炸硬并呈金黄色时，翻过来炸另一面（要求同样），炸好后捞出待用。\r\n3.把炒勺放旺火上，倒上清水，加入干青笋。当水烧开后，移入文火上再煮30分钟左右，干青笋膨胀变绿，即可捞出来，把根剁掉，用手撕成0.3厘米宽的条，再用刀切成长3厘米、宽0.6厘', '汤汁滋味醇厚，鸡肉鲜嫩清香，面筋柔软味美，雏鸡炸后皮皱，上撒绿色毛豆，形似蛤蟆得名。', NULL, NULL, '活雏鸡1只（重约600克左右），干青笋50克，生面筋80克，净毛豆100克，植物油600克（实耗约30克），熟大油30克，鸡汤600克，白糖5克，料30克，盐2克，酱油50克，味精2克，葱段15克，姜片12克。');
INSERT INTO `recipe` VALUES (2903, 81, '广式烧填鸭', '1.将鸭放砧板上，从翅膀下开膛掏出内脏，洗干净后，再用开水烫一下使鸭皮收紧，然后把稀糖均匀地涂抹在鸭身上，吊起来晾干。\r\n2.将白糖、料酒、花椒粉、盐、味精、酱油、葱、姜、蒜末等调料放在碗中，混合均匀后，注入鸭腹中。\r\n3.将鸭上炉烤烧，至鸭皮坚脆，色泽均匀即熟。\r\n4.将烧烤好的鸭切成块，摆成原鸭形状，浇上鸭腹中的卤汁即成。', '用长鱼盘盛装，造型美观大方，色泽金黄悦目，食之皮脆肉香。', NULL, NULL, '光鸭1只（重约2.5公斤左右为宜），白糖15克，料酒50克，花椒粉10克，味精5克，稀糖10克，酱油100克，葱姜、蒜适量。');
INSERT INTO `recipe` VALUES (2904, 81, '锅贴鸡片', '1.把冬笋和生鸡胸脯肉切成宽2.5厘米、长4厘米的薄片。肥膘肉同样大小的片，在片上扎一些小眼。葱、姜均切片。火腿肉切细末。圆白菜洗净后切细丝。\r\n2.用蛋清和湿淀粉调成稀糊。再把葱、姜、料酒，味道、盐合在一起，将鸡片腌一下入味。用净布揩去肥膘肉上的油脂，继而将蛋糊抹在肥膘肉上，放上一片冬笋，再撒上火腿末。此时，把腌鸡片用的葱、姜挑去，把蛋糊与鸡片拌均匀后，放在笋片上。\r\n3.烧锅注油，将鸡肉、肥膘肉面朝下一片一片地放入热油锅底，加火煎至底部发黄、鸡肉已熟的程度，即可取出，盛盘放在一端。圆白菜丝用蕃茄酱、糖', '鲜香嫩脆，形态美观。', NULL, NULL, '鸡胸脯肉250克，猪肥膘肉200克，冬笋200克，熟火腿70克，圆白菜200克，鸡蛋1个，香油20克，蕃茄酱30克，醋10克，白糖20克，椒盐25克，湿淀粉30克。');
INSERT INTO `recipe` VALUES (2905, 81, '红烧牛肉萝卜', '1.将白萝卜和胡萝卜洗净，去皮，切成段和块。　\r\n2.将牛肉切成3-4厘米大小的块。\r\n3.将板栗的两层皮都剥去，葱，姜切成段和块。\r\n4.炒勺烧热、放油，将牛肉及葱，姜投入，用大火炒至肉色变白，盛出；用剩下的油炒白胡萝卜块，至略带烧焦状盛出。\r\n5.锅中放牛肉、白水（一大碗）、酱油、陈酒、冰糖，用旺火烧开后改微火炖煮1小时。\r\n6.在煮好的牛肉锅中放入白、胡萝卜块及板栗，至变软后再稍煮收汁即可。', '色泽红亮，软滑适口。', NULL, NULL, '白萝卜500克，牛腰打肉500克、胡萝卜2个，板栗15-20个，冰糖（或白糖）5克、葱、姜、酱油、陈酒、油各适量。');
INSERT INTO `recipe` VALUES (2906, 81, '虎皮蛋', '蘑菇切成小丁，瘦猪肉、青椒各切成米粒大小。肉末用鸡蛋（1个）、精盐、干淀粉拌匀；鸡蛋煮熟，去壳，放入酱油中上色。锅中加猪油，烧热后将鸡蛋放入油中炸，炸至虎皮色时捞出，沥净油。原锅净油倒入鸡蛋，加鲜汤、绍酒、酱油、味精，烧滚后改用小火煮5分钟，然后用旺火收浓卤汁。将鸡蛋取出切成两半，蛋皮朝上排在盘里，再把卤汁倒入盘中。 锅中留油少许，将肉末下锅，用勺搅散，至熟后沥干油。在锅中放入蘑菇青椒煸炒一下，加绍酒、味精、精盐、鲜汤、烧沸勾芡后，再放入肉末一起翻炒淋入麻油，起锅浇在鸡蛋上面即可。', NULL, NULL, NULL, '鸡蛋六个，水发蘑菇、青椒各50克，瘦猪肉75克，绍酒、精盐、味精、白糖、淀粉、芝麻油、鲜汤、酱油、猪油各适量。');
INSERT INTO `recipe` VALUES (2907, 81, '花束鸡', '1.剔去鸡胸脯肉筋皮，切长约5厘米的细丝，分成24分（每分约6根），均用料酒、盐、味精调味。将火腿、青椒、冬菇均分别切细丝，各分成24分。\r\n2.用湿粉、蛋清调成糊，取1分鸡丝，抹上蛋糊后，再将火腿丝、冬菇丝、青椒丝各1分并拢，横放在鸡丝顶头。将鸡丝卷起，一端露出三丝，一端是鸡丝卷成的圆形，成为“花束”。其余均照此法逐一做好。\r\n3.炒勺上旺火，将生菜油烧至4-5成热时将“花束”下勺温熟后倒出。\r\n4.原勺放入味精、糖、盐、料酒、鸡汤，烧开后用湿淀粉勾芡，把拉熟的花束鸡倒入勺内翻几下，淋少许油即可。', '色美红白绿棕，鲜嫩清香可口。', NULL, NULL, '鸡胸脯肉400克，方火腿80克，大青椒2个，水发大冬菇4个，鸡蛋清120克，生菜油800克（实耗约100克），料酒25克，盐5克，味精5克，湿淀粉10克，鸡汤40克。');
INSERT INTO `recipe` VALUES (2908, 81, '黄瓜炒鸡蛋', '1.将黄瓜洗净，去把，劈为两半，斜刀切成片。\r\n2.炒锅放在火上，加入猪油20克，热后先将鸡蛋打入碗内，打开后，倒入炒锅内炒熟，推至锅边，另加入猪油20克，下入葱末炝锅，投入黄瓜片，同鸡蛋一起炒匀，烹入料酒，加少许水，最后放入精盐、味精、勾芡、出锅装盘即成。注意炒此菜不能放酱油，熟后才能黄绿分明。', '色鲜味香。', NULL, NULL, '黄瓜200克，鸡蛋4个，猪油50克，精盐5克，味精3克，料酒10克，水淀粉10克，葱末少许。');
INSERT INTO `recipe` VALUES (2909, 81, '黄酒焖鸭', '1.将宰杀去毛后的填鸭开膛去内脏、足、舌、鸭臊及翅尖一段，清洗干净控去水分，用盐在鸭身上揉搓，再抹上酱油稍浸一会。用烧热的植物油将鸭子糗成金黄色。\r\n2.用一铝锅，底部垫上竹篦，把鸭子背朝上放在篦子上，加入拍破的葱、姜、料酒、胡椒粉、白糖和清水，用旺火烧开，随将浮沫撇去改用文火煨3小时左右。\r\n3.提起竹篦取下鸭子，去葱、姜，把已煮透的鸭子翻扣盘中，把汁收浓，用湿淀粉勾薄芡，放上味精，浇在鸭子上即成。', '色美红亮，酥烂味浓。', NULL, NULL, '填鸭1只（1.5-2公斤），植物油900克（实耗约120克），料酒300克，葱、姜各25克，盐12克，湿淀粉15克，酱油25克，花椒粉2克。味精5克，白糖少许，清水适量。');
INSERT INTO `recipe` VALUES (2910, 81, '烩鸭丁油皮', '1.先用温水把油皮泡软，切成象眼片。\r\n2.将鸭子洗净，放入锅内，煮到9成烂，捞出后剔骨，切成骰子丁。\r\n3.炒勺上旺火，放入鸡汤、毛姜水、味精、盐，同时把油皮片和鸭丁也放入汤内，汤烧开后把浮沫撇去，用湿淀粉勾芡，淋上鸡油便成。', '油皮滑爽，汤味鲜美，鸭肉醇香。', NULL, NULL, '净鸭子半只（约1公斤），油皮2张，鸡油、料酒、味精、毛姜水各10克，盐7克，湿淀粉适量，鸡汤750克。');
INSERT INTO `recipe` VALUES (2911, 81, '鸡蛋花汤', '1.将鸡柳肉去筋砸成泥，用凉汤做成糊状后，加入味精、湿淀粉搅匀，再倒入蛋清混合一起。火腿蒸熟剁成末。\r\n2.把汤烧至小开（大开易将糊冲散不成团），倾入调好的料，轻轻用勺推动不使沾锅，待已熟浮起时用细眼漏勺捞起入碗即成。', '汤清淡，味鲜美，为高级汤菜之一，北京的老豆腐四川称为豆花，此菜似豆花而得名。', NULL, NULL, '母鸡柳肉200克，火腿肉50克，鸡蛋清120克，盐6克，胡椒粉1克，料酒25克，味精5克，湿淀粉少许，凉汤适量。');
INSERT INTO `recipe` VALUES (2912, 81, '鸡蛋鲜虾豆松', '豆角洗净切成小粒，虾仁洗净，抹干水，亦切成粒，洋葱切米粒。鸡蛋敲开打匀。烧热锅，下适量油，先下洋葱、豆角兜炒几下。再加入虾仁，拌炒至熟，把蛋徐徐倒入锅中，炒熟即可上盘。', NULL, NULL, NULL, '鸡蛋3只，青豆角200克，虾仁100克，洋葱半个，盐少许。');
INSERT INTO `recipe` VALUES (2913, 81, '韭菜炒鸡蛋', '1.将韭菜摘洗干净，切成3厘米长的段；打入碗内搅匀待用。\r\n2.将炒锅烧热，加油烧至五、六成热，倒入蛋液，烧至小团块时倒出。\r\n3.炒锅放入猪油烧热后，放入韭菜，用旺火速炒，放盐。快熟时倒入鸡蛋，颠两下出锅即成。', '黄绿相间，色泽协调美观，鲜嫩浓香，诱人食欲。', NULL, NULL, '韭菜150克，鸡蛋3个，猪油15克，植物油20克，精盐5克。');
INSERT INTO `recipe` VALUES (2914, 81, '酒蒸全鸡汤', '1.将活母鸡宰后退毛，开膛取出内脏，再从背部剖开，用开水氽烫将血污除净。\r\n2.使鸡双腿朝天、腹部向上，放入汤斗内，加入花雕酒、葱、姜、味精、盐和适量清水，上屉蒸烂后取出，去掉葱、姜即成。', '色泽银白，味香汤清。', NULL, NULL, '活母鸡1只（约1.8公斤左右），花雕酒150克，葱、姜各15克，味精5克，盐克。');
INSERT INTO `recipe` VALUES (2915, 81, '酒蒸鸭子', '1.将冬笋去皮筋拍松，用手掰成块。把鸭子从脊背开膛掏出内脏，剁断脊背骨、小腿骨，剔除鸭臊，清洗干净后放入开水锅中紧透。捞出放入盆内加入拍松的姜和葱、盐、冬笋块和白酒，用油纸或其它物品将盆口封住，上屉蒸2小时左右。\r\n2.将蒸好的鸭子放入汤盆中。把蒸鸭子的原汤滤入炒勺内加入香菇、冬笋块和菠菜芯，用旺火烧开后连汤带菜一起浇在鸭子上即可。', '酒香味浓，肉肥嫩可口。', NULL, NULL, '肥鸭1只，香茹、净冬笋各50克，净菠菜芯4棵，白酒100克，盐20克，姜块15克，葱段25克，味精7克。');
INSERT INTO `recipe` VALUES (2916, 81, '烤全羊', '1.将羊宰杀，用80-90度的开水烧烫全身，趁热煺净毛，取出内脏，刮洗干净，然后在羊的腹腔内和后腿内侧肉厚的地方用刀割若干小口。\r\n2.羊腹内放入葱段、姜片、花椒、大料、小茴香末，并用精盐搓擦入味，羊腿内侧的刀口处，用调料和盐入味。\r\n3.将羊尾用铁签别腹内，胸部朝上，四肢用铁钩挂住皮面，刷上酱油、糖色略凉，再刷上香油。\r\n4.将全羊腹朝上挂入提前烧热的烤炉内，将炉口用铁锅盖严，并用黄泥封好，在炉的下面备一铁盒，用来装烘烤时流出的羊油，大约3-4小时，待羊皮烤至黄红酥脆，肉质嫩熟时取出。\r\n5.食用时先将', '“烤全羊”是蒙古族用来招待贵客的传统名菜。此菜历史悠久。据传，早在几千年前，生活在内蒙古河套地区的鄂尔多斯人就已经开始用火来烧食猎取整只野兽，大草原人民特定的游牧生活方式，使这种食用方法延续至今。', '烤全羊的制作要求严格，必须选用1-2岁的内蒙古白色大头羯羊，经过宰杀、烫皮、煺毛、腌渍、调味后，再挂入烤炉内，封住口用慢火烤成熟，成品色泽黄红、油亮，皮脆肉嫩，肥而不腻，酥香可口，别具风味。', NULL, '白色羯羊1只，葱段250克，姜片250克，精盐30克，花椒75克，酱油150克，大料75克，糖色150克，小茴香末75克，香油150克。');
INSERT INTO `recipe` VALUES (2917, 81, '烤鸭', '1.将填鸭用刀宰杀（割断食管、气管和颈部血管）去毛后，洗干净剁去双脚和翅膀尖端，再进行加工整理。\r\na.打气。从喉管开刀处拉出食管，用左手拇指，顺着食管外面向胸脯推入，使食管与周围的膜分开，再将食管放入喉管去，不要拉断。然后从喉管部刀口处的气管，用打气机（或打气筒）慢慢地打入气体，使鸭身各部充满气体，膨胀起来。鸭身打入气后，再操作时注意手指不能接触鸭身，只能拿翅膀、腿骨、头颈等以免留有指印影响质量。打气能使鸭子烤熟后，皮脆肉嫩，外形丰富美观。\r\nb.掏膛。使用锋利的尖刀，于鸭右腋下（先排出该处局部气体）开', '烤鸭皮质酥脆，色泽油光红艳，肉质柔软鲜嫩，味道醇香可口，食之肥而不腻，美名驰传中外。', NULL, NULL, '北京填鸭1只（重约1.5-2公斤），糖汁（用麦芽糖、白糖和水调和而成）。');
INSERT INTO `recipe` VALUES (2918, 81, '烤羊肉串', '1.将羊里脊肉洗净，切成直径2厘米、厚0.66厘米的圆切，共70块。取10把银扦，每把穿7块肉。穿肉的方法是：左手捏着肉块，将银扦从肉的背面穿到正面，再从正面穿入背面，如此反复穿2次。\r\n2.把肉串平排架在炭火上烤，随烤随用刷子少涂一些清油,并根据个人的口味将辣椒面、精盐面和孜然粉撒在肉串上。约烤3分钟，待肉呈酱黄色时，再用同样方法烤背面。最后，将两面都刷上清油，放在盘内即成。', '“烤羊肉串”系新疆维吾尔族的著名小吃，选用羊身上的最嫩的里脊肉，用银扦（一种特制如剑形的工具，尖扁圆，有把，长约33厘米）穿起，随烤随加调料。成熟后，色泽酱黄，肉质鲜嫩，麻、辣、咸、香，回味悠长。由于这个菜制作方法特殊，风味不俗，深受食者的欢迎，在北方各地广泛流传。', '要选羊里脊肉、后腿肉亦可，片去筋膜，切块大小一致，烤时勤翻，使两面受热均匀。', NULL, '净羊里脊肉750克，辣椒粉0.5克，孜然粉3克，精盐1克，芝麻油或清油50克。');
INSERT INTO `recipe` VALUES (2919, 81, '两吃菠菜', '1.将菠菜择洗净，放面粉裹匀待用；蛋清、蛋黄分别磕入两碗内，各加少许湿淀粉搅匀成糊。\r\n2.锅上火，放油，将裹匀面粉的菠菜一半挂匀蛋清糊，一半挂匀蛋黄糊，分批依次入锅炸至表皮酥脆后捞出，控净油装入盘内（蛋清菠菜和疏黄菠菜各占盘一半）。\r\n3.锅留少许底油烧热，加葱姜末，盐、味精、鲜汤，花椒水烧开，用少许淀粉勾芡，淋入香油、浇在盘子的一边；另起油少许，烧热放清汤，糖和醋，调酸甜烧开，勾芡浇在盘的另一边即成。', '金银相映，咸鲜酸甜。', NULL, NULL, '菠菜500克，鸡蛋3个、油150克、葱、姜、花椒水、盐、味精、糖醋、香油、清汤，干面粉，湿淀粉各适量。');
INSERT INTO `recipe` VALUES (2920, 81, '酿松茸', '1.鸡脯肉和猪肥膘切成小块，在肉墩上垫上猪皮，把鸡肉和肥膘放在猪皮上，用刀背捶成茸，入碗加上盐4克、味精1克、胡椒粉2克、鸡蛋清、葱姜汁搅打起劲，做成馅料。\r\n2.松茸菌盖洗净，将菌盖翻过来放在桌上，抹匀馅料，撒上火腿末，入碗上笼蒸6分钟取出，翻扣入盘。\r\n3.炒锅上火，注入鸡清汤，烧开后下盐4克、味精2克、胡椒粉2克，调好味，用湿淀粉勾芡，淋上明油，将汁浇在松茸上即成。', '松茸名贵，除菌体坚实肥厚，气味芳香外，还含有较高的营养价值和特殊的药用效果。据分析，鲜品含粗蛋白17%，纯蛋白8.7%，可溶性无氮化合物总量61.5%，还有丰富的维生素B1、B2、C及PP。具有益肠胃、止前、理气化痰、驱虫等功效。现代科学研究表明，松茸所含的多糖，对治疗糖尿病、抗癌等有一定的效用。经常食用，可调节人体新陈代谢，降低血压和减少胆固醇。纳西族聚居的丽江县，是云南松茸菌的主产地之一。酿松茸是纳西族婚筵的珍贵名菜，以咸鲜清香，形状美观而著称。', '鲜松茸只用菌盖，抹上一层馅料，扣在碗内，旺火气足，蒸15分钟即熟。', NULL, '鲜松茸300克，鸡脯肉200克，火腿末80克，葱10克，猪肥膘肉70克，鸡蛋清2个，精盐8克，湿淀粉2克，味精3克，熟猪油4克，胡椒粉4克，鸡清汤500毫升，姜8克。');
INSERT INTO `recipe` VALUES (2921, 81, '酿雪梨', '1.选大小一样的雪梨，削去皮，从梨把下切一片为盖子，挖去梨核，梨盖还原插上竹签，用清水漂着。\r\n2.冰糖碾成末。大枣去核切碎。桂元、火腿、柿饼切丁。松子去膜。白菜去筋，切丝。莲子去皮、去心，煮熟。糯米洗净，蒸成饭。将以上配料和调料入盆，上笼蒸熟，取出加洗沙拌匀成馅。\r\n3.炒锅上火，注入猪油，烧至五成热，下梨炸至色黄，取出沥净油。取下梨盖，装入馅，盖上盖，再插上竹签，上笼蒸透，取出梨，装入盘内。\r\n4.锅上火，将蒸梨的原汁滗入锅内，下白糖、水，至白糖溶化，用湿淀粉勾芡，芡汁倒在梨上即成。', '中国是梨属植物的起源中心。《诗经》中早已记载，有“百果之宗”的声誉。营养价值丰富，含有蛋白质、脂肪、矿物质和果糖、葡萄糖、蔗糖、苹果酸、柠檬酸、胡萝卜素及维生素B1、B2、C等成分。不仅是深受人们喜爱的佳果，也是治疗疾病的药物之一。《本草纲目》云：“梨品甚多，俱为上品，可润肺凉心，消痰降火，能解疮毒酒毒”。酿雪梨，是白族人民的传统甜菜，此菜雪白晶莹，细线路鲜甜，梨香浓郁。雪梨独产洱海之滨，洁白似雪，滑糯蜜香，用于做菜胜过鸭梨。', '雪梨去皮、心后，用清水当漂，可避免变色。蒸酿梨，大火气足，约蒸1小时左右。', NULL, '雪梨10个1500克，火腿80克，糯米50克，卷心白菜80克，松子仁50克，白糖100克，莲子80克，冰糖100克，大枣60克，湿淀粉40克，桂元肉60克，熟猪油1000克，洗沙60克，柿饼60克。');
INSERT INTO `recipe` VALUES (2922, 81, '牛肉炒芹菜', '1.将瘦嫩牛肉自横断面切成丝，用酱油，料酒，水淀粉浆匀。将芹菜去叶洗净。切成3厘米长的段，用开水烫一下，捞出用凉水过凉，控净水分。\r\n2.将炒锅置于火上、放入油，热后下入葱、姜末略炸，再下入牛肉丝用旺火炒散起出待用。再把芹菜下油锅煸炒，加入精盐。然后将炒过的牛肉丝倒入，并加入酱油、料酒、急炒几下后即可出锅。', '鲜香、味美。', NULL, NULL, '芹菜300克、瘦牛肉75克，花生油50克、酱油20克、精盐7克，料酒5克、葱、姜末各4克，水淀粉10克。');
INSERT INTO `recipe` VALUES (2923, 81, '牛肉黄瓜', '1.熟牛肉切片，黄瓜、葱头洗净，葱头削球状，黄瓜切块。\r\n2.炒锅上火，加素油烧热，下入熟牛肉片稍煸炒，放番茄酱、红葡萄酒、清汤、葱头球焖至入味，再倒入黄瓜块，盐、胡椒粉调好口味，稍滚沸后撒入少许芹菜末，出锅装盘即成。', '郁香适口，双色双味。', NULL, NULL, '黄瓜150克，熟牛肉250克，葱头25克，红葡萄酒75克，番茄酱50克，芹菜末、素油清汤、盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (2924, 81, '烹蹄筋', '1.熟蹄筋用刀改成5厘米长的条，放在碗内，抓上用淀粉、鸡蛋打的糊，备用。\r\n2.炒勺上火，放入食油，烧至七成熟，下入蹄筋，炸成金黄色，呈外焦里嫩后捞出。\r\n3.炒勺回旺火，放底油，用葱姜末炝勺，烹上酱油、料酒、 醋、白糖、高汤、糖色，搅匀勾芡，然后下入蹄筋，翻匀，淋明油，盛入盘内即成。', '烹蹄筋是全羊大菜席中一道名馔。“全羊大菜”是适应民族口味和风俗习惯的高级宴席，其特点是用整只羊身上所有的部位烹制成一百余种菜肴，其中羊的脑、眼、耳、脊髓，甚至羊蹄，无不包括在内。羊行走、奔跑都靠四蹄的运动，因此，蹄筋粗壮，富有极强的韧性及弹性，羊身上任何部份也无法与之相比，烹制成菜，当然别具风格。清末民初以来，天津清真饭庄日益增多，煮蹄筋、熘蹄筋、烩蹄筋盛大行于市。其口味多为咸鲜清香。由于天津地处沿海，南北交通发达，南方食客也甚多。原座落在南市繁华地区的鸿宾楼为了适应更多食客的口味，名厨师宋少山制作出一种', '鸡蛋糊的最佳比例：面粉150克、鸡蛋100克、淀粉克、清水25克。', NULL, '煮熟羊蹄筋200克，醋10克，鸡蛋糊100克，料酒10克，白糖75克，水淀粉25克，油750克，葱5克，糖色50克，酱油15克。');
INSERT INTO `recipe` VALUES (2925, 81, '青椒炒鸭片', '1.鸭脯肉批成鹅毛薄片，用清水漂洗干净沥去水，加精盐、蛋清、湿淀粉上浆。青椒去蒂、去籽切菱形片，入沸水锅汆一下，捞出沥去水。\r\n2.将锅置旺火上烧热，滑锅后，加油烧至四成热，投入鸭片滑至嫩熟沥出。锅内留油少许，下葱末、青椒炒透，烹绍酒，加精盐、白糖、味精、白汤、用湿淀粉勾芡，倒入鸭片，淋油炒匀，装盘即可。', NULL, NULL, NULL, '鸭脯肉200克，鸡蛋清1个，青椒150克，绍酒、精盐、味精、白糖、白汤、葱白末、湿淀粉、猪油。');
INSERT INTO `recipe` VALUES (2926, 81, '清炖牛肉', '1.牛肉切块后，用凉水泡半小时，移入开水锅内边炖边除浮沫，直到肉熟透无沫为止。\r\n2.此时，把葱（长段）、姜（拍破）、花椒、料酒加入锅中，用文火炖至9成烂时，再放入萝卜（切滚刀块，去皮）。\r\n3.萝卜炖烂时加盐、味精即成。', '汤清鲜适口，肉软烂味香。', NULL, NULL, '牛肉500克，白萝卜适量，料酒15克，花椒10粒，味精3克，葱、姜、盐适量。');
INSERT INTO `recipe` VALUES (2927, 81, '清蒸鸭子', '1.鸭子剖膛去内脏、足、舌、鸭臊、及翅尖的一段，用水洗净，控去水分。然后，在烧开的汤内把鸭子煮一下，将血水去掉，捞出后用水冲洗，并尽量把水分控干。\r\n2.用盐在鸭身上揉搓一遍，脊背朝上腌一会，并放上料酒、葱、姜、胡椒粉和清汤，上屉，用旺火开水蒸2-3小时，取出，将乳油撇去，加入味精并调好咸淡即成。', '肉质软烂，味道鲜美。', NULL, NULL, '净填鸭1只（2公斤左右），料酒50克，味精10克，盐30克，姜20克，葱30克，胡椒粉少许，汤适量。');
INSERT INTO `recipe` VALUES (2928, 81, '三不粘', '将鸡蛋黄、白糖、湿淀粉加清水35克，用筷子搅匀，再加清水125克搅匀。将锅置旺火上，烧热，下猪油，滑锅后倒出余油，随即倒入搅匀的蛋黄，用手勺不断的翻炒两分钟，再把25克猪油用手勺分3-4次陆续淋入锅中，同时不停翻炒，至金黄色不见油迹时，即可出锅装盘。', '此菜色金黄，味香甜，炒制时不粘锅、勺、盛时不粘碟、筷，吃时不粘牙，故名“三不粘”。', NULL, NULL, '鸡蛋黄5个，白糖125克，湿淀粉30克，猪油适量。');
INSERT INTO `recipe` VALUES (2929, 81, '三鲜鸽蛋', '1.用凉水、小火将鸽蛋煮熟后，剥去蛋壳。火腿、蘑菇、熟鸡肉均切成中等大小的斜角片。小菜芯洗净、氽烫后，捞出待用。\r\n2.热炒勺中放入浓汤、鸽蛋、火腿、鸡肉、蘑菇、小菜芯、料酒、盐、味精后，先用小火上味，后改用大火将汁收浓，用湿淀粉勾芡。然后淋上鸡油，盛入汤盆即可。', '色白，鲜嫩，清淡。', NULL, NULL, '鸽蛋16个，蘑菇、火腿各50克，小菜芯10棵，熟鸡肉50克，鸡油50克，味精、盐各6克，湿淀粉15克，料酒25克，浓汤250克。');
INSERT INTO `recipe` VALUES (2930, 81, '三鲜牛蹄筋', '1.将发好的蹄筋洗净，切成长5厘米的段，若较粗可从中间剖开。葱、姜用刀略 拍。 \r\n2.鸡肉切成片，火腿切成薄片。鲜蘑切成0.1厘米厚的片，菜心洗净。 \r\n3.炒锅上火，放入油50克、将葱、姜煸出香味后去掉，加入料酒、盐、味精和 鸡汤，放入蹄筋烧约10分钟，再将鸡肉、火腿、鲜蘑、菜心放入同煮，成熟后，用水淀粉将汁收浓，即可装盘。', NULL, '此菜为准扬风味，口味咸鲜，汤汁浓郁，色泽洁白。烹制器皿一定要干净。', NULL, '发好的牛蹄筋200克，熟鸡肉50克，火腿30克，鲜蘑50克，油菜心5棵，料酒10克，盐3克，味精2克，淀粉3克，油50克，葱、姜各3克，鸡汤300克。');
INSERT INTO `recipe` VALUES (2931, 81, '砂锅焖狗肉', '1.将狗肉切成4厘米方块，用清水漂洗两次，控干水分。薄荷洗净，摘取嫩尖。\r\n2.炒锅上火，注入菜籽油，烧至七成热，下草果、大料、花椒、白糖、盐、干辣椒段、葱、姜、酱油，下狗肉煸炒20分钟，注入肉汤，烧开后倒入砂锅。将砂锅上火，把白酒分两次加入，改用小火，焖约1小时，汤汁收稠时，放上薄荷，连同砂锅上桌。', '民谚：三伏天吃狗肉避暑，三九天吃狗肉驱寒。此话一点不假。在古代，狗肉不仅能上席，而且还是席上珍馐。狗肉不上席是在宋代之后。据《曲消旧闻》记载：“崇宁初，范致虚上方‘十二宫神，狗居戌位，为陛下本命。今京师有屠狗为业者，宜行禁止。’”宋徽宗对治国安邦的建议听不进去但对这种毫无根据的阿谀之词却非常重视，马上降旨禁止杀狗。从此，京师狗肉绝迹。到了清代，满族人传说狗和乌鸦救过努尔哈赤的命，因而不吃狗肉。但是，民间食狗肉仍代代相传。著名画家郑板桥，因贪吃狗肉，曾被盐商骗去自己的佳作。砂锅焖狗肉，是云南文山苗族的风味名', '狗肉用清水拔去血污，然后漂洗干净，熟后膻味大减，肉香四溢。砂锅炉焖狗肉，大火烧开，小火慢，以狗肉酥烂为度。薄荷后下，一烫即可，若经煮沸，香味尽失。', NULL, '狗后腿肉500克，薄荷100克，白糖10克，干辣椒25克，白酒10克，葱10克，姜10克，大料6克，草果10克，花椒粒2克，精盐20克，菜籽油120克，酱油20克，肉汤150克。');
INSERT INTO `recipe` VALUES (2932, 81, '蒜苔炒鸡蛋', '1.将蒜苔摘洗干净，切成3厘米长的段；鸡蛋打入碗内，加入精盐2克，用筷子搅打均匀；花椒放入碗内，用50克清水泡上。\r\n2.锅内放油35克烧热，把鸡蛋汁倒入锅内，用手勺轻轻推炒，炒至蛋汁定浆，似小花瓣时倒出。\r\n3.把余油倒入锅内烧热，放入蒜苔段翻炒几下，烹入花椒水，加入剩余的精盐和味精，放入炒好的鸡蛋花，翻炒均匀即成。', '滋味鲜美。', NULL, NULL, '蒜苔100克，鸡蛋3个，花生油50克，精盐6克，味精2克，花椒15粒。');
INSERT INTO `recipe` VALUES (2933, 81, '天麻扣岩鸽', '1.岩鸽宰杀后，烫毛去内脏，砍去头脚，脊骨，一只分为4块，漂洗干净。\r\n2.炒锅上火，加清水、葱、姜和白酒，沸时下鸽肉，氽后捞出，整齐码在大碗内。原汤用鸽血清汤，加盐调味，浇入鸽碗内，上笼蒸3小时。\r\n3.天麻用清水洗净，横刀切薄片，放在小碗内，上笼蒸1小时。将鸽肉扣入盘中，周围围上天麻片，再蒸10分钟即成。', '岩鸽又名野鸽。喜群居，常以杂粮、种籽为食，只重400克左右，居住山岩，故称岩鸽。肉质鲜美。岩鸽补中益气，配以天麻，更有强身之功。兼治眩晕头痛、小儿惊风等。此菜原汁原味汤鲜质嫩，是纳西族名菜。', '蒸鸽肉时，汤碗用绵纸封严，避免水气渗入，保持原汁原味。', NULL, '岩鸽4只，姜10克，天麻100克，盐10克，葱10克，白酒10克。');
INSERT INTO `recipe` VALUES (2934, 81, '五香牛肉', '1.牛肉洗净过水沥干。\r\n2.烧红锅，下油，爆香牛肉，加酒，五香桂皮及水，旺火烧滚再转文火烧至熟透。取出。\r\n3.洗净锅，烧热，放油，爆香放入牛肉，加酱油、糖、料酒，滚后转中火，其间不断翻动牛肉以免粘锅，直至卤汁收干。取出牛肉待凉后切成薄片便可供食用。', NULL, NULL, NULL, '牛肉一斤，五香桂皮少许，姜三片葱一棵酱油、酒糖适量。');
INSERT INTO `recipe` VALUES (2935, 81, '西红柿炒鸡蛋', '1.把西红柿洗净，去蒂，切成象眼块；鸡蛋打入碗内，加入精盐，少许搅匀，用热油炒散。\r\n2.将炒锅放入油，烧热后放入西红柿、鸡蛋，搅均匀加入白糖、精盐、再搅炒几下，开后勾芡即成。', '红黄相间，味道鲜美。', NULL, NULL, '西红柿500克，鸡蛋2个，植物油50克，白糖25克，精盐5克，水淀粉15克。');
INSERT INTO `recipe` VALUES (2936, 81, '西红柿炖牛肉', '1.先将牛肉切成长3.5厘米、宽3厘米左右的块，西红柿洗净、去蒂、切块。\r\n2.炒锅内放底油，将大料炸至枣红色，放葱、姜炝锅，炒面酱，加高汤、盐、放牛肉，火靠4分钟左右，再放西红柿，白糖，再火靠一会儿，用水淀粉勾芡，颠炒均匀后出锅。', '色泽鲜艳、甜酸咸口、肥烂不腻。', NULL, NULL, '西红柿250克、熟牛肉200克、面酱5克、猪油20克、大料少许、葱末、姜末各5克，酱油15克、白糖25克、料酒10克，水淀粉15克、高汤100克。');
INSERT INTO `recipe` VALUES (2937, 81, '香椿烘蛋', '1.香椿切末。湿淀粉用水再调稀。鸡蛋打散加入稀淀粉、香椿、味精盐搅匀。\r\n2.将炒勺烧热注油50克，油热时改用文火，将鸡蛋搅匀倒入，盖好盖，再将剩余的30克油沿勺分次倒入，约烘10分钟，将盖掀开取出，沥去油后将蛋翻扣盘中即成。', '色泽金黄，微咸鲜香，面酥里嫩，别有风味。', NULL, NULL, '鸡蛋500克，香椿25克，大油80克，湿淀粉25克，盐、味精各5克。');
INSERT INTO `recipe` VALUES (2938, 81, '香茅草烤牛肉', '1.将牛肉漂洗干净，切为长方块，用木棰捶松。加盐、辣椒面、花椒面、蒜泥、香乌末，用手揉匀入味，腌渍半小时。\r\n2.取出牛肉块用香茅草捆扎，用竹片夹住，放在栗炭火上慢慢烘烤，烤至肉色变黄，香味四溢，牛肉熟透即食。', '香乌末：又名香脑、香丁、香辣柳，学名香蓼草。产于亚热带山中，多年生草本，形似杨柳，择其尖或叶作香料。香味独特，对肠胃炎症有明显疗效。此菜鲜香酥嫩，麻辣味厚，是拉祜族的风味菜。', NULL, NULL, '净瘦牛肉1000克，精盐15克，辣椒面15克，香乌末5克，花椒面8克，香茅草100克，蒜泥100克。');
INSERT INTO `recipe` VALUES (2939, 81, '香片煎虾饼', '1.香片用热开水冲泡1分钟后滤茶汤，茶叶切碎备用；荸荠、板油、生葱各切成碎末备用。\r\n2.虾仁洗净切碎成泥，放入容器中，加1大匙茶汤、配料及调味料（A）拌匀。\r\n3.烧热炒锅，加1/2碗色拉油，将拌好的材料取出置于锅内，分别压制成直径约5厘米大小的圆饼，饼中间放少许沥干的碎茶士末，用五分火煎至呈金黄色即可。', NULL, NULL, NULL, '香片1克、虾仁300克、配料、荸荠80克、生葱2根、蛋清1份、板油（猪油）30克。调味料：（A）盐1茶匙、糖1/2茶匙、白胡椒粉少许、淀粉1茶匙。（B）色拉油1/2碗。');
INSERT INTO `recipe` VALUES (2940, 81, '芫爆散丹', '1.将牛散丹用八成开的水烫一下，搓去散丹上一层粒状的灰黑色的皮，洗净后放在开水锅内，用微火煮烂捞出。择去散丹上的油和杂质，用温水洗净，切成长5厘米、宽1.3的块，再用开水煮1分钟捞出，挤净水。香菜择洗干净，切成长3厘米的段。\r\n2.将熟鸡油倒入炒锅内，旺火烧至七成熟，下入葱末、姜末，稍一煸出香味，迅速放入散丹块爆炒几下，随即加入清汤、精盐、胡椒粉、醋和青菜段，颠翻几下，淋上芝麻油即成。', '“散丹”系牛、羊胃的一部分，质地脆嫩，有一种特殊的鲜味，食之容易消化。此为清真名菜，用牛散丹制成，脆嫩清鲜，香菜味浓郁，爽口不腻口，诱人食欲。北京鸿宾楼饭庄烹制此项，闻名遐迩。', '水煮散丹，宜用旺火，约需煮3小时以上。爆炒散丹，旺火热油，瞬间即成，延长加热时间，质韧不脆。', NULL, '牛散丹250克，精盐3克，香菜75克，醋5克，葱末10克，清汤75克，姜末5克，熟鸡油25克，胡椒粉2克，芝麻油15克。');
INSERT INTO `recipe` VALUES (2941, 81, '燕窝鸽蛋汤', '1.先将鸽蛋洗干净，用清水煮熟，捞出后放入凉水中紧一下，剥去壳。\r\n2.将燕窝洗干净后，用开水焖，再用清水洗一下。\r\n3.将鸽蛋、燕窝放在汤碗里，冲上清鸡汤，加入胡椒粉、盐、味精即成。', '色泽清亮，味道清淡，鲜美可口，别致高雅。', NULL, NULL, '鸽蛋10个，燕窝25克，胡椒粉少许，盐6克，味精5克，鸡汤800克。');
INSERT INTO `recipe` VALUES (2942, 81, '银粉牛肉丝', '1.将牛肉切成长4厘米、厚0.3厘米、宽0.3厘米的丝。葱切成丝。韭菜择好，洗 净，切成长3厘米的段。 \r\n2.将牛肉丝内加入盐1克、料酒2克、淀粉2克，搅拌均匀。 \r\n3.将葱丝、盐、酱油、白糖、味精、料酒、胡椒面、淀粉和汤50克，放入碗中调成汁。 \r\n4.炒锅上火，加油150克，加入牛肉丝煸炒，炒至八成熟时加入韭菜段，即刻倒 入碗汁，待汁收浓后，浇在粉丝上即可。', NULL, '此菜为准扬风味，口味咸鲜，色泽丰富。制作时注意不可将粉丝炸上色，且肉丝炒得不可过老，韭菜以脆绿为好，不可过烂。', NULL, '牛肉200克、粉丝100克、韭菜50克。调料：盐5克、白糖5克、味精2克、料酒5克、酱油10克、胡椒面0.5克、淀粉3克、油200克、葱5克、汤50克。');
INSERT INTO `recipe` VALUES (2943, 81, '鱼香荷包蛋', '1.用上述调料（除泡辣椒和油）对成汁，葱切葱花，姜、蒜均切末，辣椒剁碎。\r\n2.将炒勺烧热注油，待油热后将鸡蛋分次打入，两面煎成黄色，取出放入盘中。勺内留底油少许，把辣椒下入稍炒，倒入汁，汁开时把明油浇在鸡蛋上即成。', '形似荷包，色泽金黄，味道鲜浓，带有鱼香。', NULL, NULL, '鸡蛋500克，大油50克，净葱、湿淀粉各15克，净姜5克，蒜瓣5克，泡辣椒10克，酱油35克，白糖20克，料酒25克，味精5克，汤50克，醋15克。');
INSERT INTO `recipe` VALUES (2944, 81, '云腿鸽子汤', '1.将活仔鸽宰杀后，用热水烫透褪净毛，开膛掏去内脏，下入开水锅后捞出，将水沥干装盘。\r\n2.将葱、姜、料酒加进盘中，上屉蒸至7成烂时取出（原汁留用），拆去骨头（骨留用）将肉放在汤斗一边，另一边放上云腿片。\r\n3.把鸡肉剁成鸡茸，放在碗里加上葱、姜、250克清水，挤出血水后，将鸡汤倒入炒勺内，加入鸽骨鸡茸、血水、原汁、盐和味精，滤净汤渣，将清汤倒入盛鸽肉的汤斗内，再上屉蒸烂即成。', '色泽银红，原味原汤，清香适口。', NULL, NULL, '活仔鸽4只，云腿片100克，鸡肉150克，料酒50克，葱、姜各20克，盐5克，味精5克，鸡汤800克。');
INSERT INTO `recipe` VALUES (2945, 81, '竹荪鸽蛋汤', '1.将竹荪先用凉水清洗，再用温水泡胀去泥沙，剖成两半，把头尾两端的尖切去，改切3厘米长节。再用开水氽一下。然后将鸽蛋打入盛有凉水的大碗中。\r\n2.炒勺上旺火将水烧开后，离火，将鸽蛋倒入，用文火煮热捞出。\r\n3.另用锅将清汤烧开，放入盐、胡椒粉、味精、竹荪调好味。再把鸽蛋放入汤中，将汤倒进去即成。', '竹荪鲜脆，鸽蛋软嫩，汤清淡味美。', NULL, NULL, '鸽蛋20个，竹荪50克，盐10克，味精6克，胡椒粉1克，清汤适量。');
INSERT INTO `recipe` VALUES (2946, 77, '竹筒鸡', '1.将鸡宰杀去毛，剖腹除内脏，冲洗干净。鸡身、肝、肫、冬菇、玉兰片和火腿，加葱、姜、盐、味精、胡椒粉、白糖、甜咸酱油腌渍入味。\r\n2.选生长一年的青竹一节，约长50厘米、外径12厘米，一头留节，一头开口。用鸡身装入鸡肝、肫、冬菇、玉兰片和火腿，合拢成全鸡状，塞入竹筒，口用芭蕉叶塞紧，放在栗炭火上烧烤2小时左右即熟，取下去掉芭蕉叶，倒入盘内即成。', '利用竹筒烹饪，历史久远。最远的文字记载见于《齐民要术·炙法第八十》“筒炙”的菜，用肉馅敷在竹筒上用明火烘烤而成。清代朱彝尊《食宪鸿秘》的“蟹丸”，则是将蟹丸入竹筒煮熟而成。时至当代，云南和各少数民族仍保留竹筒烹饪的传统肴馔，这与“满山的翠竹根连根”，竹子取之不竭有关，又因竹筒菜清香秀气，故沿袭不衰，哈尼族的竹筒鸡，便是一例。竹筒鸡，是云南哈尼族招待贵客的一道名菜。制法独特，古老朴实。既有鸡肉之鲜甜，又有青竹之清香。', '必须用鲜竹，成菜清香四溢，北方无竹，用干竹筒为之，且反复使用，大失风味特点。', NULL, '仔公鸡1只1500克，白糖30克，精盐10克，火腿片100克，味精3克，水发冬菇50克，胡椒粉2克，水发玉兰片50克，葱段20克，甜酱油50克，姜片20克，咸酱油50克。');
INSERT INTO `recipe` VALUES (2947, 81, '醉鸡', '1.将鸡宰杀去毛，去食管、食袋、气管，开膛取出内脏，清洗干净，鸡身带有剁成小块，入清水中浸泡，除去血水。取出鸡杂、鸡块控干水分，入盆，加入红糖、粗米酒揉匀，腌渍半小时。\r\n2.将鸡块、鸡杂及米酒、红糖汁装入口小肝大的瓦罐中，用碗（口朝上）盖严，再用面糊密封罐口。\r\n3.锅上中火，放入盐1000克，再放上瓦罐，盖罐口的碗内注入半碗冷水，徐徐加热，待碗内水蒸发完即成。', '醉鸡，是普米族的传统甜菜，其制法特殊，用隔皿盐法导热，在传统的烹调方法中实属罕见。云南一些少数民族，不仅喜欢饮酒，而且还把酒当水用于做菜，普米族的醉鸡即是一例。菜色老红，酒香味浓，甜嫩适口。亦可酌加砂仁、三七或当归之类的药材，则能舒筋活血，提神补脑，适于老年人及产妇食用。', '以盐导热，徐徐加温，比“炖”法成菜更快，原汁源味，风格独特。', NULL, '嫩母鸡1只2000克，粗米酒500克，红糖末400克。');
INSERT INTO `recipe` VALUES (2948, 105, '珍珠奶茶', '1.温热红茶适量奶精粉搅拌匙调匀。 \r\n2.加入一盎斯糖浆，搅拌匙调匀，充分融于热奶茶中（若有珍珠此时加入）。 \r\n3.最后加入冰块，摇动杯子，到摇摇杯冰冷为止。 \r\n4.先打开上面的小孔，一部分出来。 \r\n5.再打开杯身，把冰块和其余的奶茶倒出。', NULL, '奶精粉和糖浆一定要在加入冰块之前加入，红茶最好是温热的，在于此时是最适合融解的温度，如此奶香和甜度才能充分地充满在整杯奶茶之中。若有一顺序颠倒，则奶茶将会大失其味！摇动杯子的次数一定要足够，杯奶茶的冰点才够！先由小孔中倒少许奶茶出来，旋开杯身倒出其余为使奶茶不易因旋开杯身而溢出。', NULL, '阿萨姆红茶一杯，糖浆一盎斯，奶精粉一匙，冰块适量。');
INSERT INTO `recipe` VALUES (2949, 105, '炸芋泥卷', '1.洋芋蒸熟压成泥拌入椰粉、糖、奶油及葡萄干成内馅。面粉一茶匙加水一茶匙成面糊，糊口用。 \r\n2.馄饨皮摊开，放入1料馅，包成糖果状，用面糊封口。 \r\n3.油三杯加热，至八分热时投入馄饨炸呈金黄色捞出即可。', NULL, '洋芋蒸透要趁热压成泥。如果冷了再压，就有颗粒状，压不松。另配料也要趁热拌入，才会溶化。', NULL, '馄饨皮廿张，洋芋泥一杯，椰粉一茶匙，葡萄干一茶匙，糖一茶匙，奶油一茶匙，面糊一茶匙。');
INSERT INTO `recipe` VALUES (2950, 105, '枣泥寿桃', '温水加糖加酵母粉，先溶化后，静待五分钟见酵母在活动时，倒入面粉中，慢慢揉成团，待发酵约四十分钟左右，面团比原来大一倍时取出再揉至光滑分切二十块，每块包入枣泥，收口后捏成桃型，再醒五分钟，入蒸笼蒸十二分钟，取出压纹，喷上红色色素水（红色素加水，放入喷的容器中）。', NULL, '发面比较能看得出来好坏，只要你多试几次一定会成功的。成本约一百元。为了您的健康，还是不要加红色色素比较好。', NULL, '中筋面粉一斤，温水1/2杯，酵母粉一匙，糖一匙，枣泥一斤，红色食用色素水。');
INSERT INTO `recipe` VALUES (2951, 105, '玉米浓汤', '1.用大火把一锅水煮开。\r\n2.水开时改小火，并把冷冻蔬菜包与素火腿放进去煮，此时水会再一次滚沸。 \r\n3.快滚时再倒进玉米罐头。 \r\n4.最后勾芡（太白粉加水）就行了喔！', NULL, '材料的量都是视情况而定，盐等调味料则可试味道后再斟酌加入。', NULL, '玉米粒罐头，冷冻蔬菜，包素火腿切块，太白粉。');
INSERT INTO `recipe` VALUES (2952, 105, '悠乐春卷', '1.先做沾酱。红辣椒洗净，去籽，切末。所有沾酱材料放入碗中，拌匀至糖溶化即可。 \r\n2.粉丝切成小段，先以冷水浸透，再放入滚水中煮一下，捞起沥干待用。 \r\n3.莴苣泡盐水10分钟，以去农药，再洗净切丝。豆干切丝，放入滚水中川烫一下。 \r\n4.红萝卜，豆薯、青木瓜，去皮刨成丝。 \r\n5.2－4之材料，全部放入大碗中拌匀成馅料，以少许盐、胡椒调味。 \r\n6.取一春卷皮，放些馅料，包卷成春卷。在尾端要涂些面糊封口，让春卷皮黏住，免得在炸时散开。 \r\n7.将春卷一一做好。 \r\n8.锅内热油，将春卷炸成金黄色即可。', NULL, '因馅料大多是生菜，在加盐后会出水，最好将水份榨后，再制作春卷较易处理，春卷皮也不会破。易处理，春卷皮也较不会破。沾酱若用柠檬汁来做，最好不要隔餐食用，以免不新鲜。', NULL, '春卷皮，红萝卜，豆薯，青木瓜，豆干，莴苣，粉丝，封口用面糊，（以面粉加些水调成）沾酱：红辣椒一只，柠檬汁或白醋3大匙，淡色酱油或酱油3大匙，冷开水半碗，糖1小匙。');
INSERT INTO `recipe` VALUES (2953, 105, '养颜果菜汁', '番茄和香蕉去皮，再和酵母乳一起放入果汁机，也可再加入少许的柠檬汁。', NULL, NULL, NULL, '酵母乳（养乐多或多多或亚当）60cc红番茄一个，小型香蕉一条。');
INSERT INTO `recipe` VALUES (2954, 105, '雪花糕', '1.鲜奶慢火煮滚。起司粉和玉米粉用水调匀，倒入鲜奶中搅拌均匀。 \r\n2.再倒入调味料，以慢火边煮边搅拌，避免烧焦。全部调匀，倒入水盘中待冷，即可放入冰箱冷藏。 \r\n3.食前切块沾椰子粉而食。', NULL, NULL, NULL, '鲜奶1公升，起司粉半碗，玉米粉半碗，椰浆半碗，炼乳1罐，椰子粉少许。');
INSERT INTO `recipe` VALUES (2955, 105, '新鲜桔茶', '1.将桔子对半切开将汁压出置于小锅中。 \r\n2.依序放入百香果浓缩汁、柠檬汁、糖及热水。 \r\n3.煮沸后再加入桔子。试试味道，喜欢甜的就糖多点。', NULL, '需要厨具：瓦斯或电磁炉；需要器材：小茶壶；烹调时间：准备10分钟，制作5分钟。', NULL, '绿色桔子3－4颗，百香果浓缩汁随自己喜好添加，柠檬汁半盎司（约1汤匙）果糖适量。');
INSERT INTO `recipe` VALUES (2956, 105, '味噌沙拉酱', '1.味噌加配料拌匀。 \r\n2.坚果泡软用坚果机打碎混合，细末状味噌适合调制沙拉酱，颗粒状味噌适合汤用。', NULL, NULL, NULL, '味噌2汤匙，坚果类（葵瓜子、南瓜子、杏仁任选其一）一汤匙，柠檬汁2汤匙糖蜜（或甘蔗汁）一汤匙。');
INSERT INTO `recipe` VALUES (2957, 105, '味噌焖笋', '1.将笋尖洗净后过水处理，可使其去笋尖之苦味。 \r\n2.锅中倒入2杯水，放入笋尖及所有材料（调味料），煮开后转至小火，盖上锅盖闷煮，直到汤汁收干（注意不要烧焦了）即可。', NULL, NULL, NULL, '笋尖300公克，味噌2匙，醋1匙，糖1匙，黄色芥末粉1小匙。');
INSERT INTO `recipe` VALUES (2958, 105, '泰式炸脆皮', '1.把豆干、火腿、素香肠、荸荠、香菜、豆腐、胡椒粉、盐、面粉、太白粉、少量水放进大碗里拌匀。 \r\n2.面粉加水调成黏糊状，再把一张春卷皮摊开，抹少许面粉糊，而后把作法1.的材料铺在春卷皮上，再把腰果铺在其上，最后再把另一张春卷皮抹上面粉糊盖上压平。 \r\n3.再脆饼上戳几个洞，热油至中温后，将脆饼炸成金黄色后再切数块，就可以享用啦！', '好吃而不油腻，料多实在，吃起来真是口感俱佳。', '食用时再沾些甜辣酱更顺口喔！喜好咖哩口味的，也可以依自己喜好撘配材料。炸脆饼时，如果怕馅料不熟，可以分成两次，炸第一次用低温炸，第二次再高温炸，但小心别炸焦了。记得戳些洞再下去炸，炸出来较平而且好看。', NULL, '沙拉油两杯，春卷皮2张，豆干2块，火腿两片，切丝素香肠半条，荸荠4颗，香菜两根，腰果适量（烤过），面粉1茶匙，太白粉1茶匙，胡椒粉适量，盐1:1。');
INSERT INTO `recipe` VALUES (2959, 105, '泰式凤梨饭', '1.凤梨直剖成两半，果皮保持完整，将果肉挖出切丁备用。凤梨之汤汁可留下来备用。 \r\n2.素火腿切丁。 \r\n3.起油锅，倒入素火腿丁炒热，再加入冷冻什锦蔬菜炒匀。 \r\n4.加入米饭一起炒， 再倒入椰浆及凤梨汁炒匀，最后以少许盐调味即可。 \r\n5.将炒好之凤梨饭，填装于凤梨壳内即可上桌供食。', NULL, '因炒饭还要加入椰浆及凤梨汁，故饭要煮得比平常的饭硬一点，就是在煮饭时水少一点，若饭太软炒得时候容易糊掉。在炒饭里加椰浆，可增添炒饭之香味及口感。凤梨可用凤梨罐头来代替。若买的凤梨较酸，炒饭时可加些糖来调味。椰浆可以用椰浆粉加水调和来代替。', NULL, '凤梨，素火腿，冷冻什锦蔬菜，煮好之米饭，椰浆适量。');
INSERT INTO `recipe` VALUES (2960, 105, '素蒸蛋', '1.生香菇切半，菠菜烫熟漂凉对折切成五公分长度，炸豆包切成四片。 \r\n2.豆腐烫过（约煮五分钟）放入果汁机中加入高汤打糊（高汤加到果汁机能转动即可）再加入适量淡酱油及盐再打一下。 \r\n3.每个蒸杯放进1/4的材料后再加进高汤（与材料一般高度）最上面再倒下打糊的豆腐，蒸20分钟即可。', NULL, '蒸锅的盖子会滴水下来，所以要以纱布（普通布亦可）先把盖子包起来。', NULL, '豆腐四块，生香菇二朵，菠菜二棵，炸豆包一片，红萝卜片（圆形）四片。淡酱油，盐，高汤。');
INSERT INTO `recipe` VALUES (2961, 105, '素蚵仔', '1.将发菜、红萝卜、香椿及香菇切碎备用。 \r\n2.将两碗半冷开水置于锅中，加入碱、1.料、盐、酱油、味素、黑胡椒、白胡椒调勺后，将蒟蒻粉徐徐放入（边放边搅），调拌匀，待凝结后，捏取小块放入沸水中煮约10分钟捞起（主要将碱煮出），捞起置凉，用水清洗表面碱。 \r\n3.将太白粉加入少许水、将酱油及九层塔调匀成浆以便勾芡。 \r\n4.将放凉之2.料捏取小块放入3.料中，稍后取出沾蕃粉，放入油锅炸至酥黄即可。 \r\n5.将炸酥之香酥软列在生菜上，再插上辣椒（切花）即成。', NULL, NULL, NULL, 'GG粉40g，硷2.5g，红萝卜、香椿或九层塔、香菇、太白粉、蕃薯粉、盐、酱油、味素、黑胡椒、白胡椒，生菜、辣椒。');
INSERT INTO `recipe` VALUES (2962, 105, '素霸丸', '1.水与来米粉同煮，在煮的过程中须不断搅拌，煮成浆状后放置室温中待凉。 \r\n2.放入4杯地瓜粉与2杯太白粉搅拌至糊状。 \r\n3.器皿先薄薄涂一层油，盛适量米浆糊至器皿内抹匀。 \r\n4.置入炒熟之馅料，再覆一层米浆糊在其上抹匀（馅不要露出来）。 \r\n5.将器皿放入蒸笼中蒸10－15分钟。', NULL, '需要厨具：瓦斯炉，器材：蒸锅，碗约20个，烹调时间：准备分15分钟，制作30－40分钟。', NULL, '馅料：自理（想吃什么就放什么）炒熟后备用，外皮：在来米粉1杯、地瓜粉4杯、太白粉2杯、水7杯。');
INSERT INTO `recipe` VALUES (2963, 105, '松饼', '1.把低筋面粉，发粉及苏打粉混合过筛。 \r\n2.把乳清蛋白放在盆子里，把优酪乳加入，充分混合均匀。 \r\n3.依次将沙拉油，砂糖加入，全体混合均匀。 \r\n4.把过筛的面粉分2－3次加入，并加入盐，全体搅拌均匀后，用保鲜膜盖起来（放置30分钟－1小时，在冰箱冷藏半天左右也可以，让面糊入味，变得更加滑润）。\r\n5.把平底锅烘热，倒入沙拉油，然后转成小火，用大汤勺舀起半勺的面糊倒入锅中，使成直径约12－13分分左右的圆形。 \r\n6.用小火煎，等到表面出现一个一个的气泡时，用锅铲翻面，漂亮地煎出正反面。 \r\n7.趁', NULL, NULL, NULL, '低筋面粉100公克，乳清蛋白25g，发粉1小茶匙（5cc），砂糖2大茶匙（30cc），苏打粉1/3小茶匙，沙拉油2大茶匙，优酪乳1杯（200cc）盐一撮。');
INSERT INTO `recipe` VALUES (2964, 105, '水蜜桃冰茶', '1.制作加州水蜜桃冰块：水蜜桃切片捣成泥状，加入一茶匙柠檬汁搅匀，再倒入制冰盒冰冻成冰块。 \r\n2.冲茶、取出茶袋待凉后冷藏，加入苏打水及水蜜桃冰块即可饮用。', NULL, NULL, NULL, '滚开水1杯，茶袋1个，苏打水（柑橘口味）80克，加州水蜜桃1个（切片）。');
INSERT INTO `recipe` VALUES (2965, 105, '水果三明治', '把水果都切薄片，夹在土司中，就完成了好吃的点心！', NULL, NULL, NULL, '土司，奇异果，苹果，水蜜桃（果酱）或其他你想吃的水果都可以加进来，但不要水份太多的。');
INSERT INTO `recipe` VALUES (2966, 105, '水果茶', '用水煮开后放入锡兰茶或大吉岭茶，煮两分钟后倒入壶中即可。水蜜桃、芒果（甜），凤梨、柑橘类的皮、苹果皮（香），爱酸的加百香果或柠檬。柑橘类(有皮的)要最后再放下去煮，不然会苦涩。', NULL, '需要厨具：瓦斯或电磁炉，需要器材：小茶壶，烹调时间：准备10分钟，制作10分钟。', NULL, '水蜜桃，桔子，苹果，凤梨，锡兰茶或大吉岭茶。');
INSERT INTO `recipe` VALUES (2967, 105, '蔬菜汤', '1.红萝卜、白萝卜、牛蒡洗净,泡盐水3分钟后再用水冲洗。 \r\n2.将所有材料入锅，加3－4倍的水用大火煮开，转小火续煮1小时。 \r\n3.将所有材料滤清，冷却后即可饮用，但不可加入任何调味料。', NULL, NULL, NULL, '红萝卜1/2根留皮切段，白萝卜1/4根留皮切段，白萝卜叶（或茎）1/4棵，牛蒡1/2根留皮切段，香菇1－2朵（必须是日晒过的干品,大朵1朵或小朵2朵）。');
INSERT INTO `recipe` VALUES (2968, 105, '蔬菜浓汤', '1.将黄耆、党参、枸杞加8碗水熬高汤，约剩6碗余时，去渣留汤。 \r\n2.黄豆芽洗净沥干；洋葱去老膜切丁，胡萝卜削皮也切丁。 \r\n3.高丽菜洗净、切丝，番茄、马铃薯去皮、切丁。 \r\n4.将作法2、3之材料加进高汤中煮沸后，以小火慢熬，熬至汤成浓稠状，加盐调味即成。', NULL, NULL, NULL, '番茄1个，黄豆芽3两，马铃薯3个，胡萝卜1条，高丽菜6两，洋葱1/2个，黄耆3钱，党参3钱，枸杞2钱，盐2小匙，黑胡椒粉1小匙。');
INSERT INTO `recipe` VALUES (2969, 105, '膳糊牛蒡', '1.牛蒡切丝先泡醋和冰糖。海带泡软切丝。 \r\n2.香菇切丝拌椒盐、太白粉和黑芝麻备用。 \r\n3.用麻油把2炒香后, 最后加入牛蒡和海带丝快炒1分钟起锅。淋上黑醋，摆上九层塔。', NULL, NULL, NULL, '牛蒡2根、新鲜香菇5朵、黑芝麻2茶匙。海带1条、黑醋2茶匙、冰糖2茶匙、麻油一汤匙、胡椒盐一茶匙、太白粉一茶匙、九层塔少许。');
INSERT INTO `recipe` VALUES (2970, 105, '沙茶素肉', '1.素肉片先加水泡软后沥干。小黄瓜、红萝卜、辣椒切成片。 \r\n2.放约2大匙油于锅中烧热后，放入所有的材料及盐、糖、沙茶拌炒3－4分钟。 \r\n3.太白粉加１碗水调匀后放入锅中勾芡，并淋上香油即可。', '这道菜是满下饭的一道菜，吃起来香辣中带点小黄瓜的清爽，而且经过勾芡之后，整个口感都会变得比较滑顺，相当好吃呦！这道菜的要领是素肉片最好先用太白粉抓揉一下，吃起来会更滑嫩喔！', NULL, NULL, '素肉片200公克，红萝卜80公克，小黄瓜1条，辣椒2根，素沙茶酱50公克，盐1茶匙，细砂糖1/2茶匙，香油少许，太白粉1大匙。');
INSERT INTO `recipe` VALUES (2971, 105, '全麦面饼', '1.燕麦、五谷米泡水二小时后，（泡过的水倒掉）以二碗水用绿汁机打成米浆。 \r\n2.全麦面粉加米浆加地瓜，搅拌成面团（软硬度可以放水量自行调整），搅拌时间稍长可增加韧度。 \r\n3.将2.放入电锅，外锅水二杯（热度以筷子插入无黏状即熟了）。', NULL, '吃时，增加口感，可增加少许味噌，待凉才切块。', NULL, '全麦面粉二杯，燕麦加滋五谷米一杯或纯燕麦一杯，地瓜、芋头等蒸熟捣成泥状一碗，水二碗。');
INSERT INTO `recipe` VALUES (2972, 105, '全麦馒头加地瓜', '1.地瓜先蒸熟不去皮捣成泥状。 \r\n2.将1.加入全麦面粉中搅拌均匀。 \r\n3.将酵母粉放入微温水（与人体温同）搅拌放至约五分钟成起泡状。 \r\n4.将（3）放入（2）再慢慢加水揉成面团，（可将面团摔一摔增加韧度），放置约四十分钟，待其发酵，（夏天温度高，面团较硬，发酵时间四半分钟，冬天温度低，面团较软发酵时间约一小时。）在等待发酵的时间面团要盖湿布。\r\n5.蒸时蒸笼下放湿纱布，蒸至馒头压下去反弹不黏手即是熟了。注意需水滚才放下去蒸。全麦包子材料与作法跟全麦馒头相同，包子的馅是依个人喜好（有重病或癌症患者不', NULL, NULL, NULL, '全麦面粉四杯，酵母发粉1/4匙至1/2匙，地瓜二中条（蒸熟后约二碗不去皮），面粉、地瓜的比例是2:1。');
INSERT INTO `recipe` VALUES (2973, 105, '奇异果汁', '将所有材料放入果汁机打匀。', NULL, '需要器材：果汁机，烹调时间：准备5分钟，制作3分钟。', NULL, '奇异果3个，凤梨250公克，原味优酪乳100cc，冰块半杯。');
INSERT INTO `recipe` VALUES (2974, 105, '葡萄柚茶', '1.葡萄柚切丁加入柳丁汁、柠檬汁，入锅以中火煮约2分钟。 \r\n2.放入红茶茶包煮约10秒即可。 \r\n3.倒入壶中，糖依个人喜好适量加入糖。', NULL, NULL, NULL, '柳橙汁1大匙，葡萄柚半个，柠檬汁1茶匙，蜂蜜1大匙（也可用果糖代替），红茶茶包3包，热开水500cc。');
INSERT INTO `recipe` VALUES (2975, 105, '苹果酥派', '1.苹果去皮及核、切成约1.5cm小丁。 \r\n2.将奶油热融，将苹果丁与糖、3大匙水置于汤锅中，以中火煮沸，转小火煮至苹果黏稠变软，加入葡萄干拌匀，洒肉桂粉。 \r\n3.将起酥皮室温稍解冻至方便包馅。 \r\n4.起酥皮，内包入苹果馅，包成三角形或长方型；面皮接缝处可沾少许水或牛奶压压黏合，入烤箱以200度烤约10分或至表皮金黄即可。', NULL, '酥皮买回来略加变化还可以做成许多其他点心，如“长条型沾满杏仁碎粒”的酥饼、可颂、包素火腿或热狗的可颂、果酱酥派、起酥条等。', NULL, '中型苹果2个，水3大匙，奶油1大匙，细糖（简单抓约煮软的苹果量1/3），肉桂粉1小匙，葡萄干2大匙，冷冻起酥皮10片，牛奶少许。');
INSERT INTO `recipe` VALUES (2976, 105, '奶油乳酪球', '1.起司＋糖粉搅匀，用汤匙做成球状。\r\n2.麦片碎饼干碎烤香辗散加1/3量的起司＋糖粉，外滚饼干屑。 \r\n3.巧克力豆加入1/3的起司＋糖粉，外滚烤香的杏仁片。　 \r\n4.蜜饯加入1/3的起司＋糖粉。', NULL, NULL, NULL, '奶油起司200g，糖粉6汤匙，麦片碎饼干碎1大匙，巧克力豆2大匙，蜜饯2大匙饼干屑1/2杯，杏仁片少许。');
INSERT INTO `recipe` VALUES (2977, 105, '奶油巧克力球', '1.Cheese加糖粉拌匀。　 \r\n2.巧克力隔水融化，加入1.拌匀。\r\n3.核桃炒香切碎，拌入2.中。　 \r\n4.取一小匙生料，揉成球状，裹上可可粉即可。', NULL, NULL, NULL, '奶油乳酪150g，苦甜巧克力150g，核桃100g，可可粉适量，糖粉三大匙。');
INSERT INTO `recipe` VALUES (2978, 105, '奶油果酱夹心饼', '1.低粉加泡打粉加奶油揉匀加牛奶揉成一面团，松驰5分钟擀开。 \r\n2.抹上草莓酱对折，以180度烤约30－40分钟。', NULL, NULL, NULL, '低筋面粉310克，泡打粉2小匙，奶油180克，冰牛奶小许，有果粒的莓果酱200克。');
INSERT INTO `recipe` VALUES (2979, 105, '木瓜牛奶', '所有材料以果汁机打匀。', NULL, '未用完的木瓜切块以保鲜盒装着, 随时可打来喝。打好的木瓜牛奶最好马上喝，否则会凝结。', NULL, '熟透木瓜去仔切块1C，鲜奶1C，黄砂糖1T。');
INSERT INTO `recipe` VALUES (2980, 105, '木瓜慕斯', '木瓜削皮去子切块，将木瓜块、果糖、优格混合搅拌，隔热水将洋菜粉加入柳丁汁中融解（或是放微波炉加热20秒），将柳丁汁（混了洋菜粉的！）和百香果加入木瓜“混合物”中，搅拌均匀，倒入四个玻璃杯中，放入冰箱冷藏就行了！', NULL, NULL, NULL, '一个中型木瓜，果糖2匙，低脂优格200克，洋菜粉1大匙，柳丁汁1/4杯，三个中型百香果。');
INSERT INTO `recipe` VALUES (2981, 105, '抹茶水果冰', '1.糯米粉加抹茶加砂糖加水揉成团，再搓成小汤圆。 \r\n2.另备一锅水煮开，丢下汤圆煮熟。 \r\n3.杯中放入水果罐头，再放汤圆，凉后可放入碎冰或冰淇淋。', NULL, '需要厨具：瓦斯炉或电磁炉，烹调时间：准备5分钟，制作15分钟。', NULL, '糯米粉100公克，抹茶粉二分之一大匙，砂糖40公克，水90cc，综合水果罐头1罐。');
INSERT INTO `recipe` VALUES (2982, 105, '另类沙拉', '这种不用油的沙拉吃法，做法简单！用果汁机将副材料打成泥状，就可当做沙拉的dressing，若为西洋芹则可直接将此苹果胡萝卜泥舀起来吃，甜甜酸酸很爽口！而且热量不高！', NULL, NULL, NULL, '西洋芹（剥去老茎切5－6cm长）或其它生菜约10－40kcal/100g，苹果一小颗（去皮子切小块）约100kcal/个，胡萝卜约50g（去皮切小块）20kcal，蜂蜜一大匙（可随意增减）或果糖60kcal，开水两大匙。');
INSERT INTO `recipe` VALUES (2983, 105, '可丽饼', '1.奶油隔水加热至半融化，加入糖、盐拌匀。 \r\n2.慢慢加入乳清蛋白，边搅边加，再加入鲜奶，过筛的面粉。 \r\n3.平底不沾锅烧热（洒水马上蒸发即热了），放入一大匙面糊摊平，边缘干了就可翻面。 \r\n4.可加果酱或玉米沙拉或素火腿＋高丽菜丝＋沙拉，自己变化。', NULL, '需要厨具：瓦斯炉，需要器材：平底不沾锅，烹调时间：准备10分钟，制作20分钟。', NULL, '面粉2.5杯，糖4大匙，奶油1条，盐1/2小匙，鲜奶3杯，乳清蛋白100g，香草精少许。');
INSERT INTO `recipe` VALUES (2984, 105, '咖啡雪泥', '1.在果汁机里放入口味较浓的咖啡。 \r\n2.加入可可粉、碎冰块、糖适量，搅拌中速打30秒即可。完成后，上面会有少许泡沫，是种味道不错的饮料！', NULL, '需要器材：咖啡壶；烹调时间：准备5分钟，制作1分钟。', NULL, '浓咖啡2杯，可可粉2T，糖适量，冰块适量。');
INSERT INTO `recipe` VALUES (2985, 105, '桔茶', '柠檬切小块，金桔切一半，用手挤压汁液在小锅内，连果皮一起丢入，并加入柳橙汁煮沸，沸腾后都跟水果茶的方法一样，不够甜一样也可加入果糖调味。（水果用量视口味而定）煮热要等到茶凉了才能冰，如果直接加入冰块就失去茶的美味，教大家急速将热饮冷却又不失风味的好方法，首先要有摇杯，而且要是铁制的，拿出你家的冰块二盒，将其中一盒到入家中较大的锅中，加点冷水，将热饮倒入摇杯中并将摇杯置入刚才倒冰的锅中，透过铁摇杯，将热茶急速冷却，泠却后，加入未用过的冰块入摇杯中，摇匀后倒出，如果是要请朋友的也可以在杯缘加点水果点缀一下，是', NULL, NULL, NULL, '柠檬，金桔柳，橙汁，红茶包。');
INSERT INTO `recipe` VALUES (2986, 105, '韭菜淋饼', '1.韭菜洗净切0.8公分长末。面粉入大碗中，加入水调成浓稀度刚好的面糊，加入韭菜及调味料，略醒一下。\r\n2.用平底锅，锅中抹少许油，淋入面糊，成薄薄一层，煎熟一面，翻另一面，再煎一下，即可起锅盛盘。', NULL, '煎饼时油少许抹一下即可，放太多油口味又不一样了。', NULL, '韭菜十支，面粉二杯，盐一茶匙，胡椒粉1/8茶匙。');
INSERT INTO `recipe` VALUES (2987, 105, '金玉满堂火锅', '1.香菇切片以麻油、姜丝爆香后倒入高汤，先煮菜心、包心菜，其余材料食前烫食。\r\n2.材料中蒟蒻、竹笋、红萝卜、雪里红，先切成2寸长条，以胡瓜干系绑再煮。', NULL, NULL, NULL, '菜心，包心菜，茼蒿菜，磨菇，金针菇，豆腐，素丸子，GG，竹笋，红萝卜，雪里红，胡瓜干高汤（以上材料量自行斟选）香菇5朵，姜丝1汤匙，麻油1汤匙。');
INSERT INTO `recipe` VALUES (2988, 105, '回春水', '1.小麦种子挑选洗净以后，泡水约8个小时，或过夜。\r\n2.将水过滤掉，沥干放入宽口瓶中，瓶口以纱布盖上。\r\n3.每日加水沥干约3次左右，等到长出约0.2公分的小白芽时（约需一日），以冷水洗净。\r\n4.倒入三倍的冷开水续泡24小时，即可倒出饮用了（可重覆再泡第二次）。', '回春水是麦芽浸泡过的水，富含酵素与维他命A、E，能改善体质增加抗体，养颜美容。', '回春水看似柠檬汁，微酸，若味道变酸变臭，不对味时，有可能小麦本身有问题，（未发芽的种子应挑出）也有可能是小麦浸泡过久！另外回春水不要重覆浸泡三次以上。', NULL, '小麦种子。');
INSERT INTO `recipe` VALUES (2989, 105, '蝴蝶素糕', '1.奶油打软＋糖＋乳清蛋白打发，再拌入低筋面粉和牛奶。 \r\n2.将上面的注入小杯中，180度烤10－15分钟。 \r\n3.乳清蛋白和玉米粉、鲜奶油拌匀，倒入煮热的牛奶＋糖后，煮至黏稠，放凉。 \r\n4.待凉后将蝴蝶素糕顶部挖出小片圆型素糕体。 \r\n5.注入至素糕中空位置，再将挖出的素糕切半摆回，中间放切成长片的奇异果，做成蝴蝶型即可。', NULL, NULL, NULL, '奶油60克，糖50克，乳清蛋白15g，牛奶3－6大匙，低筋面粉120克，乳清蛋白35g，玉米粉1大匙，糖40克，牛奶200克，鲜奶油100克，奇异果1颗。');
INSERT INTO `recipe` VALUES (2990, 105, '红烧汤', '1.将卤味包、芹菜与3瓶宝特瓶（1250cc）的水一起滚沸20分钟。之后将芹菜捞起。 \r\n2.将红、白萝卜去皮切成约2cm立方的方块。 \r\n3.另起炒锅将姜切片后用麻油爆香连同麻油一起放入汤中。 \r\n4.将所有的材料全部放入汤锅中熬煮30分钟即可。', '这道汤是属于汤头的一种，味道较浓些，比较不适合直接当作汤来喝。熬煮完毕之后，可以加到饭中成为红烧饭；或煮些面条亦可成为红烧面，随君喜好加以改变。在寒冬中来上一碗真是五脏六腑尽舒畅！', '由于汤经过熬煮时水份会蒸发流失，因此必要时要加些开水避免太咸。食用时可加烫些菠菜一起配用味道更美。此汤加到饭变成红烧饭；加面变成红烧面任君调配。', NULL, '素肉块1碗，素羊肉半碗，白萝卜1条（约10cm长），红萝卜1条（约10cm长），姜1块（约5cm长），卤味包1包，芹菜1小把（约2枝），酱油1大匙，细砂糖1大匙，麻油1大匙，豆办酱1/2大匙（好吃辣可用辣豆办酱）。');
INSERT INTO `recipe` VALUES (2991, 105, '海苔酱', '1.把紫菜和海带洗净，加进果汁机拌碎取出。\r\n2.用沙拉油爆一下姜末。\r\n3.把（1）加进来。\r\n4.加酱油及蕃茄酱。\r\n5.加冰糖。\r\n6.完成。', NULL, '偶对食材的多寡没有概念，自己斟酌吧。这道菜夏天凉拌满开胃的，可以放进冰箱久藏喔。加点香菇酱也不错，但这个成本高。要加一点醋才能消紫菜的味道了。', NULL, '紫菜很多，海带次多，姜末有点多，蕃茄酱有点多，酱油不多，冰糖不用多，沙拉油一点多。');
INSERT INTO `recipe` VALUES (2992, 105, '果酱酥派', '1.派皮解冻至软（约2－3分钟）。 \r\n2.将一块派皮分割成二等份长方型，一等份中间放上果酱，另一等份的中间用刀子切开一条的横纹，但不要切断，放在涂了果酱的派皮上，四边对齐沾水或牛奶并压紧，保持下派皮平坦，此时上派皮会成弧形鼓鼓的把切痕撑开。 \r\n3.烤箱预热3分钟，将酥皮放锡薄纸上，放进190度的烤箱烘烤10分钟，就是一个好吃的甜派了。', NULL, '若是用烤面包用的小烤箱，因派饼距电热管较近，需覆盖一张锡箔，以免太过焦黑。酥派会长大，因此需留约3cm间隔。正常烤好的酥派大概3公分厚上下。果酱可换成薄哈姆片，就是火腿咸派。派皮也可对切成三角形；或多叠几层来个豪华型也不错。', NULL, '果酱（口味随意），冷冻起酥派皮6');
INSERT INTO `recipe` VALUES (2993, 105, '宫保素鱿鱼', '将蒟蒻切成约0.5公分的薄片。将锅子烧热后倒入油与辣椒、花椒爆香。放入蒟蒻以及其它调味料用中火拌炒。等到水份收干后就完成了。', NULL, '这是一道口味较重的菜，肠胃不是很好的人，可以斟酌所要放入的佐料。', NULL, '鱿鱼型GG1小包(上面有刻花)，花椒少许，辣椒干3条（依个人口味增减），油1大匙，酱油1大匙，醋1茶匙，盐1/4茶匙，糖1茶匙，水1大匙。');
INSERT INTO `recipe` VALUES (2994, 105, '港式玉米球', '1.先将2个梨子削皮，并切薄片，与500cc的水中火熬煮半小时。 \r\n2.梨子捞出，后放入一匙半的奶油，起司片两片，和适量的糖及少许盐。 \r\n3.关掉火，再伴入面粉与太白粉，按3:1的比例，调成具黏性的面糊。\r\n4.紧接着，将苹果削皮切小丁，青椒、红椒也切小丁，并与玉米粒、玉米片和适量的肉桂粉拌匀。 \r\n5.这是个重要的步骤将面糊与适量的果料调好拌匀。 \r\n6.调好后用手搓成圆球状。 \r\n7.再用锡箔纸包住后，放入烤箱约200度烤40分钟就成了。', '健康的梨子、苹果加上色彩鲜艳的红椒、青椒，食欲大增。', '也可以放入蒸锅里蒸蒸看，完全不同的口感与滋味。肉桂粉应适量调入，调太多了，就吃不到其中的果香罗。面糊不宜太干或太湿，只须调到能与材料紧密结合，不会脱落的程度就可以了！锡箔纸底层先涂上奶油，避免玉米球黏在上面。', NULL, '玉米粒半罐， 玉米片适量，青椒丁适量，红椒丁适量，苹果丁半颗，锡箔纸，梨子约两颗，奶油一匙，起司片二片，面粉/太白粉3:1，肉桂粉少许，糖、盐少许。');
INSERT INTO `recipe` VALUES (2995, 105, '茯苓素什锦', '1.茯苓加三碗水熬煮10分钟，留1/2碗汤汁去渣备用。 \r\n2.所有材料分别入滚水中汆烫过。 \r\n3.起锅加3大匙油，爆炒所有材料1分钟后，加入茯苓汁液1/2碗煮开，再入调味料拌匀，以太白粉勾芡。', NULL, '适合食欲不振、小便不利、心神不宁、失眠多梦者、节食者。不适者：小便过多者。 季节时间：四季之午、晚餐。', NULL, '茯苓5钱，GG半斤，草菇5朵，甜豆8片，红萝卜片6片，黄甜椒1/5个，盐1/2小匙，香油1/2小匙，香菇粉1/2小匙，胡椒粉1/4小匙，太白粉1');
INSERT INTO `recipe` VALUES (2996, 105, '凤梨素肉汤', '1.先把凤梨切丁放入水中煮10分钟。 \r\n2.将素肉放入凤梨汤中，以大火煮滚。 \r\n3.水滚即可熄火。', NULL, '凤梨味酸甜，可以不用加盐；喜咸者则可加入少许盐。', NULL, '凤梨半斤，素肉4两，水3碗。');
INSERT INTO `recipe` VALUES (2997, 105, '法式薄饼', '1.将低筋面粉加鲜奶加素蛋粉加盐拌匀。 \r\n2.将平底锅加热后涂上奶油，再倒入面糊，煎至成金黄色即可。 \r\n3.可在薄饼上淋上巧克力酱或蜂蜜或鲜奶油，也可用奶油煎香蕉再加入薄饼内。', NULL, NULL, NULL, '低筋面粉120至150公克，鲜奶300c.c，素蛋粉1大匙或玉米粉、盐、奶油少许， 蜂蜜（或果糖）适量，巧克力酱适量，鲜奶油适量，香蕉1根，奶油少许。');
INSERT INTO `recipe` VALUES (2998, 105, '冬瓜枸杞当归薏仁汤', '1.将薏仁放在水中浸泡3、4个小时；枸杞半小时。 \r\n2.冬瓜不削皮切块。 \r\n3.先煮薏仁约2-3小时。 \r\n4.把冬瓜、枸杞、当归放入薏仁汤中再煮40分钟。', NULL, '冬瓜可连皮食用，吃的时候再把皮吐掉，上桌前可洒少许盐，增加口感。', NULL, '冬瓜半斤，枸杞1两，当归5钱，薏仁2两。');
INSERT INTO `recipe` VALUES (2999, 105, '炒饭', '1.在锅内倒入适量的油加热。 \r\n2.油热到一定程度后倒入冷冻蔬菜与香菇并搅动它们。 \r\n3.加入适量的水并调味（推荐加入素食豪油）。 \r\n4.加入糙米饭并翻搅（视个人口味可加沙茶酱）。 \r\n5. 好了，将它们盛起来。', '其实糙米饭也可以用白饭代替，也不一定要加豪油或沙茶酱，这个食谱的特色是炒饭的颜色很漂亮。', NULL, NULL, '冷冻蔬菜，香菇（软的），剩糙米饭（温的）。');
INSERT INTO `recipe` VALUES (3000, 105, '补血果菜汁', '将材料一起放入搅拌器，再加入柠檬汁即可。', NULL, NULL, NULL, '柠檬1/4个，苹果小型一个，香菜少许（约1两多），荷兰芹少许（比香菜稍多一点）。');
INSERT INTO `recipe` VALUES (3001, 105, '比司吉', '1.将低筋面粉和植物油以低速搅混45秒。 \r\n2.把奶粉置入冰水中搅拌均匀，奶油切块备用。 \r\n3.低筋面粉和奶油拌匀后加入调好的冰牛奶再以高速搅拌30秒。 \r\n4.搅拌好后，面团就大功告成。 \r\n5.洒少许中筋面粉，取出适量的面团，将面团压平为厚度五公分。 \r\n6.以直径八公分的圆形容器挖出一块一块的美味的Biscuit（饼干）雏形。 \r\n7.将烤箱加温到华氏395度以上，如果一次烤十二块以上要烤45分。 \r\n8.烤好后的美味Biscuit（饼干）在表面涂上带有咸味的奶油。 \r\n9.Biscuit（饼干', NULL, NULL, NULL, '低筋面粉1500g，奶粉150g，植物油647g，冰开水1500g，奶油一条。');
INSERT INTO `recipe` VALUES (3002, 105, '白菜豆腐汤', '1.豆腐切丁。 \r\n2.小白菜洗净后沥干水分，约四、五公分切成一段。 \r\n3.高汤与水和豆腐同煮。 \r\n4.水滚后放入小白菜再滚煮一次，熄火。 \r\n5.撒少许盐后即可食用。', NULL, NULL, NULL, '高汤1碗，水2碗，豆腐1块，小白菜4两，盐少许。');
INSERT INTO `recipe` VALUES (3003, 107, '滋肾双耳粥', '将银耳10克，黑木耳10克温水泡发，除杂质并洗净后放入碗内，加冰糖30克，大米适量，水半碗，再隔水蒸煮成粥即成。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3004, 107, '芝麻粥', '先将芝麻仁放热锅内炒熟，再将粳米加入水煮至八成熟时，放入芝麻仁、蜂蜜，拌匀后继续煮至成粥。每日2次，作早晚餐食用。', NULL, NULL, NULL, '黑芝麻10克，粳米250克，蜂蜜适量。');
INSERT INTO `recipe` VALUES (3005, 107, '榨菜肉丝', '1.将肉切成0.6厘米粗丝盛于碗中，加盐1克和水豆粉码匀。榨菜洗净，撕去老皮，切成约3厘米长的细丝，漂洗一下挤干。\r\n2.韭菜切成约2.5厘米长的小节。炒锅炙后置旺火上，下油烧到约六成热，下肉丝快速炒至散开发白时，烹料酒、加白糖、味精，下榨菜韭菜炒几下后，簸转起锅入盘即成。', NULL, NULL, NULL, '猪背脊肉200克，榨菜50克，韭菜50克，化猪油125克，盐1克，味精0.5克，白糖2.5克，水豆粉15克，料酒10克。');
INSERT INTO `recipe` VALUES (3006, 107, '雪花鸡淖', '1.选白皮母鸡脯肉，用刀背捶松，剔去大筋反复捶茸，再择去小筋，洒点清水，宰至粘刀时，即成“鸡茸”。轻轻在菜墩上刮下（忌沾污），置大汤碗内。\r\n2.加适量冷鲜汤和水豆粉调散成浓糊状，加入盐、味精、料酒、胡椒粉调匀待用。\r\n3.鸡蛋清装入碗内，用筷子调散掺泡。火腿或金钩宰成细末。\r\n4.炒锅炙后，置旺火上，熟油100克，烧至八成热，将鸡茸和蛋清混合调散，加鲜汤500克调转，倒入锅内，用炒瓢在锅内轻轻转动，炒熟至松散。呈白色时，即用热的熟植物油50克，从锅的四周倒下，片刻起锅，盛于大圆盘内，撒上火腿末或金钩末即', '“鸡淖”系用鸡脯肉捶茸，以精细的烹饪方法制成。洁白如雪，质极细嫩，味鲜清淡，油而不腻。', NULL, NULL, '鸡脯肉100克，鸡蛋清4个，猪油（可用植物油）150克，金钩或熟瘦火腿10克，盐2.5克，味精1.5克，料酒25克，豆粉100克，胡椒粉0.5克，鲜汤500克。');
INSERT INTO `recipe` VALUES (3007, 107, '小煎鸡', '1.鸡杀后去内脏洗净，剔去骨头，宰成约5厘米长的筷子条。\r\n2.冬笋也切成5厘米长的筷子条，芹黄切成节子，泡海椒剁碎。将鸡肉放在碗中，加入食盐及水豆粉拌和均匀。\r\n3.猪油在锅内烧辣后放下鸡肉微炒，随即加进冬笋、泡海椒、芹黄、姜、葱稍炒，同时将酱油、醋、白糖、味精、水豆粉连同少许清汤在一碗内调成汁，倾入锅中再炒几下起锅即成。', '此菜肉质细嫩，味香鲜美。', NULL, NULL, '嫩仔公鸡1只（约重1250克）、泡海椒3个、净冬笋100克、芹黄50克、葱数节、姜片数小片、酱油少许、猪油100克、食盐少许、白糖少许、醋少许、味精少许。');
INSERT INTO `recipe` VALUES (3008, 107, '鲜馏什件', '1.将鸡鸭肫、肝、心洗净；肫去壳，剔掉白筋切成花子，肝、心切成薄片，用蛋清勾芡加盐少许调匀；豌豆尖洗净，在汤中烫半分钟捞起加盐少许待用。\r\n2.把炒锅置旺火上，一次下较多的化猪油烧至五成热，将用蛋清芡调好的肫、肝、心片下锅，以筷子炒散，煎至变色，除去多余”的猪油，下盐、味精、鲜汤兑成滋汁。待肫、肝、心片收汁起锅，盖在盘子里的豌豆尖上即成。', '此菜色香味美，秋季时菜。', NULL, NULL, '鸭肫、肝、心；熟猪油、蛋清、味精、盐、豌豆尖，鸭肫、肝、心；熟猪油、蛋清、味精、盐、豌豆尖，各适量。');
INSERT INTO `recipe` VALUES (3009, 105, '鲜奶酪', '用作果冻四分之一的洋菜（洋菜浓度只要果冻的四分之一）加水煮熟，将洋菜煮至完全溶化，用奶粉调出高浓度的牛奶。将牛奶、砂糖、少许奶油加入洋菜中搅散，过滤后将液体倒入模型冷却后置入冰箱，记住一定要过滤，不然洋菜没溶化的部份吃且来口感不好，只要放附冰箱下层就好，千万不要冷冻。洋菜可以到超级市场买，三十块一大包。', NULL, '牛奶，砂糖用量视个人口味。', NULL, NULL);
INSERT INTO `recipe` VALUES (3010, 105, '素高汤', '以香菇、海带、黄豆芽、高丽菜、白萝卜、玉米为材料，来熬制高汤，大致上是半锅材料一锅水的比例，加上少许的盐，也可以再加一点糖（取代味精最好不放任何味精），熬煮四小时左右，一锅鲜美的素高汤就此完成了。接着，可以把熬制好的高汤适当地冷藏，随时加以取出使用，不管炒菜、煮汤都是最佳的调味圣品。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3011, 105, '山药', '1.白山药削皮后，再汆烫，就可以沾芥酱、酱油来吃。 \r\n2.紫山药削皮后，洗净放置电锅中蒸煮，加一点点糖，和干果：南瓜子、杏仁果、白葡萄干、腰果等，可热食，也可凉食，是补充钙质的圣品。 \r\n3.山药去皮后，放入电锅中蒸煮，再把龙眼干用开水烫开后，拌着煮好的山药一起吃，此项食品适合体质虚冷的人食用。', '山药是一种很好的蔬菜、食疗圣品，盛产于冬季，具有健脾胃、益肾气、润皮毛、养颜美容的功效，更能增加免疫功能，预防糖尿病、高血压等疾病，所以很受大众的欢迎。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3012, 105, '柠檬汁', '以温水150cc或300cc的温水，泡一颗柠檬与适量的果糖，再加一小撮盐，空腹时饮下。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3013, 105, '南瓜浓汤', '不需要任何香料和味精的南瓜浓汤时，首先去皮、去子，蒸二十分钟左右后，再加适当水量，以果汁机打成类似木瓜牛奶状。把事先以奶油炒好的面粉（如果欲煮成四碗份的浓汤，可用一大茶匙的奶油，一大茶匙的其它油，一大茶匙的面粉混着炒），再加洋菇、芦笋、青豆仁等，用小火煮熟，可直接食用，也可以成为拌通心面的最佳拌料。', '南瓜是一种性温的食物，近代更被证实具有抗癌的效益，所以食用南瓜既营养又健康。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3014, 107, '五仁健脑糕', '用枸杞子、枣仁、桃仁、核桃仁、大枣各10克，与糯米250克混合放入盆或大瓷碗中，加水适量，置笼屉中蒸熟，常食可健脑益寿。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3015, 107, '菟丝子粥', '将菟丝子洗净后，加水煎煮，去渣取汁，入米煮粥，粥将熟时，加入白糖，稍煮即可，日分2次服。', NULL, NULL, NULL, '菟丝子30克，粳米60克，白糖适量。');
INSERT INTO `recipe` VALUES (3016, 107, '甜椒肉丝', '1.红海椒2.5克，去蒂子，淘洗干净，切成粗丝。嫩仔姜30克，切成0.6厘米粗丝。蒜苗50克，切成6厘米长，瘦猪肉50克，切成0.6厘米粗丝，加盐0.5克及酱油10克调味，再加水豆粉25克拌匀。\r\n2.取小碗放精盐、酱油、味精鲜汤少许，及豆粉兑成汁加香油。\r\n3.菜油100克烧至冒烟时投入肉丝，炒至散籽发白后放甜酱5克搅匀，再下甜椒丝炒至断生，下姜丝、蒜苗炒转，放入兑好的滋汁，收汁亮油起锅即成。', '色泽鲜美，肉丝滑嫩，甜椒鲜脆，略带酱香，佐酒下饭均宜。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3017, 107, '桃仁粥', '先将桃仁捣烂如泥，同粳米煮成粥，分早、晚两次食用。', NULL, NULL, NULL, '桃仁18克，粳米100克。');
INSERT INTO `recipe` VALUES (3018, 107, '蒜泥白肉', '1.二刀坐臀猪肉去毛洗净，用开水煮到八成熟（手按肉实贴略有弹性，肉皮刚掐得动）捞起，原汤浸泡约10分钟。\r\n2.片肉时，取出汤内泡的肉，置墩上，抹干水气，按横筋，用平片法，先从肉皮起片，片得越薄越大越好。盛于盘中，加蒜泥、酱油、红辣椒油拌均匀即成。', '辣香鲜嫩，蒜泥味浓，爽口化渣，油而不腻，色泽美观。有下饭开胃之功效，无积脂肥胖之忧虑。', NULL, NULL, '猪肉250克（选膘厚皮薄的猪坐臀肉），蒜泥25克，酱油60克，红辣椒油25克。');
INSERT INTO `recipe` VALUES (3019, 107, '生烧鸡翅', '1.公鸡去毛洗净，用刀齐鸡身取下翅膀八对，燎去细毛，切去翅尖，切成两段，放入锅内煮3分钟捞出，除去血腥水。火腿、冬笋、口蘑等的长宽厚切成适度。\r\n2.猪油入锅置于旺火上，将拍松的姜及葱白、料酒、鸡翅倒入锅内稍煸，再加入酱油、盐和鸡汤等，烧开后撇去浮沫，用微火烧约90分钟，去掉姜、葱，再在炒锅内放入味精及水豆粉，起锅时淋下香油上盘即可。', '此菜选料为鸡全身活动部位，再经生烧久焖，入口更加稠烂香嫩，色鲜味浓，非常可口。', NULL, NULL, '公鸡翅八对约750克，水发口蘑90克，冬笋剥皮去根125克，葱白30克，料酒75克，酱油15克，味精0.75克，化猪油50克，火腿30克，水豆粉10克，姜7.5克，香油10克，鸡汤500克，盐0.75克。');
INSERT INTO `recipe` VALUES (3020, 107, '山楂粥', '先将山楂片放入砂锅内煎取浓汁去渣，然后加入粳米、冰糖及清水适量煮成粥，分早、晚两次食用。', '此粥有健脾胃、消食积、散瘀血的作用，适用于老年人食滞、高血压、冠心病、高血脂等。', NULL, NULL, '山楂50克（切片），粳米100克，冰糖15克。');
INSERT INTO `recipe` VALUES (3021, 107, '人参蒸鸡', '将鸡宰杀好，去头、翅、颈、出水。将人参用温水洗净泥沙，取汤盘一只，将人参及鸡放入，加清汤、精盐、味精、料酒、胡椒粉、盖上盖，上笼蒸1小时。', NULL, NULL, NULL, '小公鸡1只（约1斤半），人参30克。精盐、味精、料酒、清汤、胡椒粉适量。');
INSERT INTO `recipe` VALUES (3022, 107, '山药粥', '把干山药片100g，或鲜山药200g，洗净切片，粳米100g，同煮为粥。四季皆宜，温热服食。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3023, 107, '清炖牛尾汤', '1.将牛尾修去残余皮子，清洗干净。在每一骨节缝处切进2/3深，不切断，入清水内漂20分钟。\r\n2.用大锅盛开水3000克，放入牛尾，置旺火上烧开，打去泡沫。下老姜、花椒、料酒、母鸡（去头及爪），再烧开后移微火上炖，每隔1小时翻动一下，以免粘锅。\r\n3.炖至七成熟时，以干净稀白布滤去汤中花椒、老姜。重新放在旺火上烧开后，又置微火上炖，直到炖熟为止。前后共需炖7.5小时，母鸡只取汁，炖好后，鸡取出作别用。吃时加味精、食盐和猪油。', '汤浓鲜、香美，富营养。', NULL, NULL, '黄牛尾2500克，老姜（拍破）75克，料酒125克，味精5克，食盐（炒后磨细）3.5克，净鲜肥母鸡1500克，猪油80克，上等花椒3克。');
INSERT INTO `recipe` VALUES (3024, 107, '泡豇豆炒肉末', '1.选用脆嫩泡豇豆清洗后，切成粗颗粒。瘦猪肉切成细肉颗。干辣椒去蒂籽，切成0.4厘米的节子。\r\n2.灸锅后，用旺火下油50克，烧至五成热时放入肉颗，烂酥后，舀入碗中。成细肉颗、泡豇豆炒转，放蒜苗末簸转，起锅盛入盘内，淋上香油即成。', '豇豆脆，肉末酥，胡辣鲜香、鲜嫩、香脆。', NULL, NULL, '豇豆200克，瘦猪肉100克，菜油100克，干辣椒5克，味精0.5克，花椒0.5克，香油10克，蒜苗25克。');
INSERT INTO `recipe` VALUES (3025, 107, '牛骨髓油炒面', '牛骨髓50克，黑芝麻50克，面粉500克，置铁锅中，文火炒至淡黄色发出面粉固有的香味，取出晾凉后，每次取出油炒面40－50克，加红糖适量，用开水冲服。老年人可早晚或加餐食用。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3026, 107, '米汤蜜蛋花', '取热米汤一碗，蜂蜜20毫升，鸡蛋1个。先将鸡蛋打入碗中，加入蜂蜜后将鸡蛋搅匀成蛋浆，然后冲入热米汤，再将碗盖上15分钟即成，每日清晨煮早饭时冲服1次。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3027, 107, '萝卜连锅汤', '1.选猪肥瘦相间的肉，去毛刮洗干净。锅内的水以把肉淹过为准（一次加足，不要再添），在旺火上烧沸，再放入猪肉，至再沸时打去浮沫（在制作过程中要随时打去浮沫）。随即加花椒10粒，并把姜拍松放入，用盖子盖好。煮10分钟后，用削尖的竹筷从肉皮上往下插，略微用力，一穿即透便够火候。然后捞出晾一下，不烫手后按纹横切成两块。把肉放在菜墩上，片成10厘米长的薄片，愈薄愈好。\r\n2.萝卜去皮洗净，切成5厘米长、4厘米宽、0.6厘米厚的片，连同葱白放入煮肉汤中煮6－7分钟。至萝卜片刚熟，将肉片倒入锅中搅匀，再煮2－3分钟。待', '此菜汤浮白色，宜热吃，肉片肥瘦连皮不脱，入口肥而不腻，瘦而不绵，吃时醮调料，味麻辣鲜香，汤系生烧，肉味鲜甜。冬季火锅，四季皆宜。', NULL, NULL, '猪肉500克，白萝卜750克，姜10克，葱白15克，菜油75克，干辣椒25克，豆瓣75克，花椒20粒，酱油75克，味精0.5克。');
INSERT INTO `recipe` VALUES (3028, 107, '鹿角胶粥', '先将粳米煮粥，待水沸后，再加入鹿角胶、生姜同煮，熟后即可食用。', NULL, NULL, NULL, '鹿角胶20克，粳米100克，生姜3片。');
INSERT INTO `recipe` VALUES (3029, 107, '辣子鲜鱼', '1.活鲤鱼一尾重600克，去鳞挖鳃剖腹除内脏，用清水淘洗干净后，在鱼身两侧各竖割3－4刀（注意不要割穿鱼腹），投入油锅炸至进皮时捞出待用。\r\n2.猪肉100克（以瘦为主）剁细入锅焖干水气。泡红辣椒75克（去蒂籽）剁细，放热菜油少许待用。\r\n3.生姜15克（去皮）剁细，葱花50克（叶宜少）。锅洗净，放入菜油100克烧至刚冒烟时，投入姜粒及部分葱花，焖香时放入泡辣椒末、肉末、鲜汤750克。\r\n4.待汤沸，将炸过的鱼放入后，再依次加入料酒15克、盐1.5克、酱油40克、白糖10克、胡椒0.3克，用中火烧约8分钟', NULL, NULL, NULL, '活鲤鱼约600克，猪肉100克，泡辣椒75克，生姜15克，葱50克，菜油100克，料酒15克，盐1.5克，酱油40克，白糖10克，胡椒0.3克，味精、香油适量。');
INSERT INTO `recipe` VALUES (3030, 107, '决明子粥', '先取决明子置于锅内炒至微有香气取出，待冷却后加水适量煎取药汁与粳米同煮成粥，再加入冰糖稍煮即可，分早、晚两次食用。', NULL, NULL, NULL, '决明子18克，粳米100克，冰糖15克。');
INSERT INTO `recipe` VALUES (3031, 107, '鸡皮冬笋', '1.冬笋用刀剥壳、去篼，修去毛衣（嫩衣不修），修时用刀试笋，有顶刀部分，再切下留作别用。修好后切成长6厘米、宽3厘米的极薄片，越薄越好。鸡皮切成长3厘米、宽2厘米的片。\r\n2.猪油在旺火上烧至六成热时，将冬笋倒下，炒约一分钟，刚熟（保持白色，能炸黄）滗尽猪油，即下盐和料酒，将笋簸转，掺入奶汤及鸡皮，合烧3分钟，起锅时放鸡油，盛碗上席。', '色白、汤鲜、味浓。', NULL, NULL, '冬笋1000克，盐2克，味精2克，熟鸡脯皮75克，汤500克，鸡油5克，猪油300克，料酒5克。');
INSERT INTO `recipe` VALUES (3032, 107, '鸡豆花', '1.鸡脯肉要选用老白鸡的。必须用老鸡，煮熟后才能凝成豆花状。其次要选用白皮鸡，保证颜色雪白。雄鸡脯肉质粗，去筋后用刀背捶成茸，再用刀口剁数遍；剁后再捶，盛入碗内。鸡蛋清与豆粉混合后调匀。碗内的鸡茸先用清水以竹筷搅散，再逐次加入蛋清、豆粉(用刀压为细末)、盐、味精、特级清汤，每加一种佐料搅匀一次；分次加入，分次搅匀，最后搅为鸡茸糊。\r\n2.锅内揩净，加特级清汤，烧开时放味精、盐。随后将鸡肉糊以竹筷搅匀入锅，烧至微沸时把锅移于微火上烧10分钟，鸡茸凝聚在一块时即成豆花状。将鲜菜心入锅汆过，用清水漂透心，用刀修', '此菜形如豆花，鲜嫩、清爽，宜于夏季佐餐。', NULL, NULL, '老白鸡脯肉125克，鸡蛋清4个，干豆粉12克，特级清汤1000克，味精165克，鲜菜心50克，盐3克，火腿5克，胡椒0.5克。');
INSERT INTO `recipe` VALUES (3033, 107, '红薯蜜糖饮', '先将红薯洗净，削去外皮，并切碎，和大枣一起入锅，加水500毫升左右，用旺火熬煎至200毫升时，再将蜂蜜加入和匀，用文火煎10分钟，冷后即可服用。', NULL, NULL, NULL, '红薯200克，大枣30克。');
INSERT INTO `recipe` VALUES (3034, 107, '黑芝麻核桃糖', '将黑芝麻、核桃仁炒熟待用。赤砂糖放锅内，以小火煎熬至较稠厚时，加入黑芝麻、核桃仁调匀，即停火。趁热将糖倒在表面涂过食用油的搪瓷盘中，待稍冷，将糖压平，用刀划成小块，冷却后即成黑色砂板糖。可随意食用。', NULL, NULL, NULL, '赤砂糖500克，黑芝麻、核桃仁各250克。');
INSERT INTO `recipe` VALUES (3035, 107, '核桃鸡丁', '1.将核桃仁用开水泡涨剥去皮；枸杞子用温水洗净；生姜洗净切小片，葱切葱花，鸡蛋去黄留清，鸡肉洗净，切成1厘米见方的丁。\r\n2.鸡丁装碗中，用精盐（一半）、蛋清、湿豆粉拌匀浆好，另碗中放入味精、白糖、胡椒粉、鸡汤、湿豆粉兑成汁。净锅置火上，放入猪油，待7成熟时，下核桃仁炸至微黄，及时捞起待用。把浆好的鸡丁倒入锅中，快速滑透，翻炒几下，下姜、葱，倒入上汁快速翻炒，随即入核桃仁，枸杞子炒匀，淋入香油，装盘。佐餐食。', NULL, NULL, NULL, '鸡脯肉350克，核桃仁15克，枸杞子8克，鸡汤100克，猪油150克，鸡蛋2个，清盐5克，料酒25克，胡椒粉2克，湿豆粉35克，生姜、葱各10克，香油5克，白糖7克。');
INSERT INTO `recipe` VALUES (3036, 107, '枸杞子酒', NULL, NULL, NULL, NULL, '枸杞子60克，白酒500克，浸泡2周。每次饮5－10毫升，每日2次。');
INSERT INTO `recipe` VALUES (3037, 107, '枸杞粥', '洗净放锅内熬成粥。服时加少许精盐调味，可经常食之。', NULL, NULL, NULL, '枸杞子20克，小米100克，瘦猪肉30克。');
INSERT INTO `recipe` VALUES (3038, 107, '枸杞蒸鸡粥', '取从未产蛋的母鸡1只，杀后去毛及内脏，洗净，放入锅内稍离片刻，然后捞起放入凉水内冲洗干净并滤尽水分，再把枸杞（约15－20克）装入鸡腹内，放入砂锅中，鸡腹切口处朝上，把葱（切段），姜（切片）放入锅内，加清汤、食盐、料酒、胡椒粉各适量，用火蒸2小时后即可佐食米粥。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3039, 107, '番茄溜鸭肝', '1.鲜鸭肝200克，去掉苦胆筋，用清水淘洗后滤干水分，切成0.3厘米厚的片。\r\n2.番茄250克，用沸水冲淋，撕去表皮，用刀对剖，切成厚约0.4厘米的片（去掉籽粒为好）。小白菜菜心4-5朵，姜、蒜共15克，切成1厘米见方的薄片，葱2根（叶部不用）斜切成3厘米长的节。将盐1.5克、酱油4克、胡椒0.5克、味精1克、水豆粉和鲜汤适量，兑成滋汁待用。\r\n3.锅洗净，放化猪油，烧至刚冒烟时投入搅拌了盐和干水豆粉的鸭肝片滑散，烹入料酒5克，稍后依次放姜、蒜、葱节、番茄片入锅，快速炒至断生刚熟时烹入滋汁，搅匀起锅装盘', '此菜色泽美观，亮油流汁，咸鲜略带番茄酸味，鸭肝细嫩，富于营养，是老幼皆宜的佳肴。', NULL, NULL, '鲜鸭肝200克、番茄250克。小白菜、葱、姜、蒜、熟猪油、料酒、盐、酱油、胡椒、味精、水豆粉各适量。');
INSERT INTO `recipe` VALUES (3040, 107, '冬虫夏草炖鸡', '每次用冬虫夏草4枚，鸡肉120克，水适量，隔水炖熟后，加调味吃肉喝汤，每天或隔天一次。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3041, 107, '大枣粥', '大枣15个，粳米100g，同煮为粥。', NULL, '四季皆宜。但痰湿较重的肥胖老人应少食。', NULL, NULL);
INSERT INTO `recipe` VALUES (3042, 107, '大蒜炖鸭子', '1.将大蒜（最好是独蒜）剥皮洗净，生姜洗净拍破，鲜葱扎成小把备用。\r\n2.将肥鸭宰杀，扯毛，去内脏，洗净放于铝锅之中，掺水以刚淹过鸭子为好。置火上炖煮，煮到快开时，撇去泡沫。烧沸后下姜、胡椒。待鸭子炖熟后下大蒜、葱把，再炖半小时，捞出姜、葱，下精盐、味精即成。', '鸭子熟而不烂，味鲜香，富于营养，适宜老人食用。', NULL, NULL, '独蒜、生姜、葱、胡椒、精盐、味精、肥鸭1只。');
INSERT INTO `recipe` VALUES (3043, 107, '苁蓉羊肉粥', '先将肉苁蓉、羊肉洗净后切碎，再用砂锅煮肉苁蓉取汁、去渣，放入羊肉和适量水与大米同煮，待沸后加入盐、生姜、葱白即成。', NULL, NULL, NULL, '肉苁蓉15克，羊肉100克，大米150克，葱白2根，生姜3片，盐少许。');
INSERT INTO `recipe` VALUES (3044, 107, '虫草蒸鸭', '1.选用约重500克重的嫩肥鸭一只，煺毛后在腹尾部横开一口，去内脏，割去肛门。放置沸水中煮尽血水捞起。\r\n2.把姜、葱装入鸭子腹中；用醪糟汁和胡椒面抹在鸭子身上。鸭翅膀扭在背上盘好，腹胸部戳小孔，插入浸泡过的虫草，插完为止。再将浸泡虫草的温水同鲜汤一起掺在盆中，放入锅内蒸2－3小时，待蒸烂后下精盐、味精即成。', '虫草蒸鸭是适于老年人食用的滋补品。', NULL, NULL, '鸭子1只、虫草25克、生姜、葱、醪糟汁、胡椒面、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (3045, 107, '扁豆粥', '取炒扁豆60g，或鲜白扁豆120g，粳米100g，同煮为粥，但要注意煮到米熟，豆烂，防止中毒。夏秋皆宜。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3046, 107, '板栗烧肉', '1.选用五花猪肉去毛洗净，放入开水锅内煮一下，去掉血腥味，用刀切成2.5厘米见方的方块。板栗切去蒂，用开水烫后去壳。\r\n2.菜油下锅烧至七成热时下板栗，炸熟呈黄色时捞起待用。锅内放油、加糖，炒至深黄色时，放入切好的肉稍炒一下，加入酱油、盐、鲜汤，烧开打去泡沫，再把姜拍松，葱挽结，放入锅内搅匀，再加少许糖，用小火焖烧，肉熟时加入板栗，待板栗熟透、汤汁浓稠时即可食用。', NULL, NULL, NULL, '五花肉250克，板栗250克，菜油、葱、姜、蒜、精盐、酱油、白糖各适量。');
INSERT INTO `recipe` VALUES (3047, 107, '百合粥', '取百合250克，蜂蜜适量。将百合加适量清水煮成糊状后，加入蜂蜜拌匀，然后食用，每日1次。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3048, 107, '八仙米', '以上俱为细末，混合调匀，外加荞麦，炒黄熟，与前几味等分拌匀；胡桃仁、南枣、松子仁、白糖等，随意加入，瓷罐收贮。开水冲泡代茶饮。日3匙。', NULL, NULL, NULL, '粳米、黄粟米、黄豆、赤小豆、绿豆（上五味炒香）各75克。小茴香（洗净）150克，炮干生姜，炒白盐各30克。');
INSERT INTO `recipe` VALUES (3049, 106, '燕窝鸽蛋', '将燕窝泡发，去毛除杂；鸽蛋先煮熟，后去壳；枸杞子蒸烧。起油锅，加鸡汤、盐，烧开后，将燕窝放入煨二分钟，取出沥干放在锅中间，鸽蛋镶在四周，火腿肉丝、枸杞子放燕窝上面，加入奶液，稍煮片刻即可装盘。', NULL, NULL, NULL, '干燕窝15克，枸杞子10克，鸽蛋20只，熟瘦火腿肉丝20克。');
INSERT INTO `recipe` VALUES (3050, 106, '鲜枸杞汁', '鲜枸杞250克，洗净后用纱布包裹，榨取汁液。每次10－20毫升。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3051, 106, '虾米粥', '大虾米10个，小米100克，盐、味精、麻油、葱末适量。将虾米切成小丁，小米淘净，共煮粥，加调料即成。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3052, 106, '石块龙牡粥', '石块明30克，龙骨30克，牡蛎30克，加水300毫升，煎1小时去渣取汁，再加糯米100克和水100毫升煮成粥，加红糖食用。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3053, 106, '山药扁豆糕', '将上面的四个药和匀，加入淀粉少许，放在碗中蒸熟，佐早餐食用。', NULL, NULL, NULL, '鲜山药500克、扁豆100克、红枣肉500克、陈皮丝6克。');
INSERT INTO `recipe` VALUES (3054, 106, '桑菊茶', '用沸水冲泡或煎汤代茶饮。', NULL, NULL, NULL, '桑叶一撮，菊花5朵（或干菊花适量）。');
INSERT INTO `recipe` VALUES (3055, 106, '七宝粥', '先将红豆、黑豆、黄豆煮沸15分钟后再入莲子、核桃，再煮沸10分钟入红枣。', NULL, NULL, NULL, '红豆50粒，黑豆64粒，黄豆56粒，莲子21粒，红枣24枚，核桃仁8个。');
INSERT INTO `recipe` VALUES (3056, 106, '龙眼糯米粥', '龙眼肉25克、糯米40克，粥成入龙眼肉。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3057, 106, '鲤鱼粥', '取约500克的活鲤鱼1条，洗净切片煎汤；再取苎麻根20克，加水200克，煎成100克，去渣留汁入鲤鱼汤中，加入糯米50克，适量的葱、姜、油、盐等，煮成稀粥。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3058, 106, '老鸡大枣汤', '将红枣洗净去核纳入鸡腹内，文火煲5小时，肉烂后加盐调味，佐餐服食。', NULL, NULL, NULL, '老鸡一只，大枣12枚。');
INSERT INTO `recipe` VALUES (3059, 106, '韭菜汁', NULL, '温阳寒宫。适应形寒肢冷、面色酰白、精神萎靡、腰膝酸冷、经血量少多、色淡而清、夜尿多等。韭菜洗净，用干净纱布包好，榨取汁液，临服时加点白糖。每日2次，每次5－10毫升。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3060, 106, '红枣黑木耳汤', '锅中蒸1小时，吃枣、木耳，喝汤。', NULL, NULL, NULL, '红枣8枚，黑木耳20克，水和冰糖适量。');
INSERT INTO `recipe` VALUES (3061, 106, '黑豆大蒜红枣汤', '洗净，放入砂锅中，加水适量，同煮至黑豆熟透，再加入红糖适量，煮沸后即可食用。', NULL, NULL, NULL, '黑豆100克，大蒜30克，红枣30克。');
INSERT INTO `recipe` VALUES (3062, 106, '甘麦大枣汤', '水煎代茶饮。', NULL, NULL, NULL, '小麦30克，红枣10枚，甘草10克。');
INSERT INTO `recipe` VALUES (3063, 106, '茯苓红枣粥', '取茯苓粉15克，粳米50克，去核红枣5枚，加水煮粥，早晚服用。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3064, 106, '二仙百合龟肉汤', '把仙灵脾、仙茅洗净装入纱布袋，将药袋、龟肉及百合同时放入砂锅，加料酒、盐、姜、清水适量，微火煮至龟肉熟烂，捞出药袋，吃龟肉、百合，喝汤。', '适用于更年期出现的性欲减退、心烦易怒、健忘、失眠等症。', NULL, NULL, '仙灵脾10克，仙茅10克，百合20克，龟肉150克（龟肉洗净切小块）。');
INSERT INTO `recipe` VALUES (3065, 106, '冬瓜羊肉汤', '冬瓜50克，瘦羊肉50克切片，先将冬瓜煮熟，再将与葱花、香油拌匀的羊肉放入冬瓜中煮沸即成，可佐餐食用。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3066, 106, '冬瓜蚕豆饮', '加水适量共煮，豆烂后弃黄芪，加红糖适量食用。', NULL, NULL, NULL, '冬瓜50克，蚕豆瓣15克，生黄芪10克（纱布包）。');
INSERT INTO `recipe` VALUES (3067, 106, '地黄枣仁粥', NULL, NULL, NULL, NULL, '酸枣仁30克，生地黄30克，大米100克。');
INSERT INTO `recipe` VALUES (3068, 106, '赤小豆粥', '赤小豆150克，入锅先煮；取重约500克的活鲤鱼1条去鳞及肠杂后，与粳米100克、陈皮6克，以及先煮好赤小豆一同煮粥。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3069, 104, '珍珠三鲜', '1.将鸡胸肉剁成肉泥。\r\n2.将三分之一的团粉用牛奶调和成汁。\r\n3.把蛋清和鸡肉泥放在一起拌匀。\r\n4.把肉汤入锅中煮开，下豌豆、胡萝卜丁、蕃茄丁，待肉汤滚沸后离火，用筷子把鸡肉从碗边一点一点地拨进锅内，每个鸡泥要和碗豆大小一样，待拨完后将锅烧开，最后把团粉汁倒入锅中勾芡，放入味精、食盐、鸡油、料酒，煮开即可装碗食用。', NULL, NULL, NULL, '生鸡胸肉、胡萝卜丁各100g，鸡蛋清20g，嫩豌豆25g，蕃茄丁50g，料酒20g，牛奶、鸡油15g，肉汤300g，味精1g，团粉、食盐各5g。');
INSERT INTO `recipe` VALUES (3070, 104, '炸牛里脊', '1.将牛里脊肉切成3cm薄片。\r\n2.鸡蛋、干团粉、面粉及适量清水一起调成鸡蛋糊。\r\n3.把牛肉片放入用少许花椒、盐、葱花、姜末、味精、料酒和三分之二香油调好的佐料中腌30分钟，待入味后再放入已调好的蛋糊中，用手抓勾，一片片地放入旺火热油锅中，炸3分钟后，熄火稍等一会儿再炸，如此炸二、三次，避免油温过高，等肉炸至呈金黄色时即可，装入盘中，撒上剩下的花椒、盐，淋上剩下的香油即可食用。', NULL, NULL, NULL, '牛里脊100g，鸡蛋4个，姜末1.5g，味精0.5g，面粉40g，植物油60g，干团粉15g，香油10g，料酒4g，花椒1g，精盐、葱花各5g。');
INSERT INTO `recipe` VALUES (3071, 104, '糟鱼肉圆汤', '1.青鱼洗净，切成5cm长、2cm宽的长方块，盛在体内，加少量精盐拌匀，腌半小时，随即将香糟用料酒调稀后，倒入鱼块内拌和，腌2小时待用。\r\n2.冬笋切成5cm长，0.5cm厚的片状。\r\n3.冬菇去蒂，洗净泥沙。\r\n4.肥瘦肉剁成肉末，放在碗内，加入精盐、味精、葱姜汁、蛋清拌匀搅上劲，再加入干姜粉拌和待用。\r\n5.将砂锅置于炉上，倾入清水（约750g），随即将鱼块洗后，和笋片、冬菇一起下锅，加入精盐、味精，待烧开后，端锅离火，将拌好的肉泥做成肉圆，一面做一面放入锅内，制毕后起锅再置炉上，用小火滚烧5分钟左右', NULL, NULL, NULL, '青鱼中段400g，肥瘦猪肉200g，蛋清1份，冬笋、水发冬菇各25g，豆苗15g，精盐、味精、干姜粉各5g，料酒、葱汁、姜汁各50g，鸡油10g，香糟100g。');
INSERT INTO `recipe` VALUES (3072, 104, '月母鸡', '1.宰后去毛、内脏及骨，剁成3见方的鸡块，放入开汤锅内氽，去血水，捞出。\r\n2.炒锅洗净，烧热，下化猪油，烧至六分热时放入姜葱，炒出香味后，再下鸡块，炒一下，烹入料酒，随即掺汤3000g，下盐、胡椒粉，旺火烧至汤汁成白色时，拣去姜、葱，再移上小火烧。上菜时，盛入碗内上桌即可食用。', NULL, NULL, NULL, '小母鸡1只（约1000g），姜10g，精盐2g，葱段、料酒各50g，胡椒粉1g，化猪油25g，汤3000g。');
INSERT INTO `recipe` VALUES (3073, 104, '元宝肉', '1.将肉刮洗干净，切成5cm长，1.5厘米厚的片状，下油锅稍炒一下。\r\n2.鸡蛋放到开水中煮熟，剥去壳，用酱油浸泡几分钟，下油锅中炸一下，成片后取出，切成数瓣。\r\n3.一片肉，一瓣鸡蛋，排放碗内，上锅蒸烂。\r\n4.冬菇炒一下垫底，将蒸好的元宝肉扣在冬菇上即可。', NULL, NULL, NULL, '猪肉、冬菇100g，鸡蛋150g,酱油、料酒各5g，葱、姜各3g。');
INSERT INTO `recipe` VALUES (3074, 104, '鱼香肝片', '1.干团粉中加入6g的水调匀。\r\n2.猪肝切成薄片。\r\n3.料酒、精盐及水团粉（先用2/3）混合，将猪肝片放入浸泡，待入味后放入已烧热的油锅内，随即将姜末、蒜末及10g葱加入，急炒几下，待肝变色后将调好的白糖、酱油、香醋、肉汤及剩下的水团粉溶液倒入勾芡，待熟即可装盘食用。', NULL, NULL, NULL, '猪肝150g，精盐、姜末、酱油、蒜末、白糖各5g，香醋、料酒各2g，猪油50g，奶肉汤15g，干团粉3g。');
INSERT INTO `recipe` VALUES (3075, 104, '腰花木耳汤', '1.将腰子切成二半，除去腰臊，洗净，切成兰花片，清水泡一会儿。\r\n2.木耳用清水洗净泥沙待用。\r\n3.将腰花、木耳、笋片一起下水锅煮熟后捞出，放在汤碗内，加入葱段、味精、精盐、胡椒粉。再将烧沸的高汤倒入汤碗内便成。', NULL, NULL, NULL, '猪腰子150g，水发木耳15g，笋花片20g，葱段、精盐各5g，味精3g，胡椒粉0.5g，高汤500g。');
INSERT INTO `recipe` VALUES (3076, 104, '羊肉烧鱼', '1.将鲫鱼去鳞、鳃，剖腹去除内脏，洗净。\r\n2.羊肉切成3cm长，6cm宽的长方块，放入开水锅中略烫一下，捞出再洗一次，沥干水。\r\n3.锅放炉上，放入食油烧热，下羊肉炒几下，加水（约650g）、酱油40g、葱段5g、姜片6g、八角2.5g、白糖4g、精盐2.5g，烧至八分烂时，转放砂锅中。\r\n4.锅放炉火上，放入食油烧热，放入鲫鱼（鱼身两面抹点酱油），煎成两面浅金黄色时取出，放入羊肉锅，内加入剩下的葱、姜、八角、精盐、酱油、白糖及烧羊肉的原汤，改用小火烧约30分钟，待鱼酥肉烂时，撒上胡椒、芫荽即成。', NULL, NULL, NULL, '羊五花肉400g，鲫鱼500g，芫荽0.5g，白糖、精盐、八角各5g，酱油125g，胡椒粉2g，葱段、姜片10g，食油50g。');
INSERT INTO `recipe` VALUES (3077, 104, '羊肝排叉', '1.将花椒炒焦、碾细和精盐混合。\r\n2.羊肝切成6cm长，3cm宽的薄片。\r\n3.面粉用清水（15g）调成稠糊。\r\n4.豆腐皮切两半。\r\n5.将豆腐皮铺平，把面糊分别均匀地抹在上面，分别铺上羊肝片，铺完之后，把剩下的面糊分别抹在羊肝上压实，之后分别切成6cm长、3cm宽的长方形块，每块豆腐皮夹肝再顺着它的长边，取豆腐皮夹肝一头从中间的划口插进去，形成排叉样。\r\n6.将油放入锅内加热至六、七分热时，把羊肝排叉放进油锅内，炸时不要翻动，以免羊肝溢出，至豆腐皮呈金黄色时，捞出即可食用。', NULL, NULL, NULL, '羊肝200g，豆腐皮100g，花生油500g，面粉30g，精盐、花椒各3g。');
INSERT INTO `recipe` VALUES (3078, 104, '香酥凤卷', '1.鸡腿起肉，切薄片，拍松，加入腌料拌匀，腌15分钟。\r\n2.冬菇浸软去蒂，加入少许油、生抽蒸熟，切条。\r\n3.红萝卜去皮洗净，切长条，葱切段。\r\n4.西兰花洗净切小朵，以油、盐、水灼熟，放于碟中央。\r\n5.铺平鸡肉，放入冬菇、红萝卜、葱各1条，卷成1卷，拌匀蛋黄及生粉，涂匀鸡肉卷，拌匀鸡肉卷，放入中火油内炸至金黄色取出，切件排于碟上，煮开芡汁淋上即成。', '鸡肉含有蛋白质、脂肪及磷质，营养丰富。香酥凤卷鲜嫩美味，而且外形漂亮，不但适合孕妇食用，更是一家大小都适宜的佳肴。', NULL, NULL, '鸡腿3只，西兰花200克，冬菇2只，红萝卜1/4个，葱2棵，蛋黄1只，生粉半茶匙。盐半茶匙，酒、生粉各1茶匙，蛋黄1只，麻油、胡椒粉各少许，糖1/4茶匙，生抽1茶匙，清水4汤匙。');
INSERT INTO `recipe` VALUES (3079, 104, '鲜奶煮鸡蛋', '1.鲜奶用水浸暖，加入砂糖拌匀至糖溶。\r\n2.鸡蛋打散入碗内，拌匀，慢慢加入鲜奶，轻手搅匀（切勿猛力打发，引进气泡）。\r\n3.烧开水，将蛋放入，加盖或碟盖着碗面，猛火蒸8分钟即可取出进食。', '鲜奶含蛋白质、脂肪、钙质和维生素。鸡蛋含蛋白质、脂肪、矿物质及多种维生素。孕妇常吃鲜奶煮鸡蛋，不但能使皮肤光滑，将来小宝宝也会胖胖的。', NULL, NULL, '鲜奶300克，鲜鸡蛋2只，砂糖3汤匙。');
INSERT INTO `recipe` VALUES (3080, 104, '鲜滑鱼片粥', '1.猪骨洗净敲碎。\r\n2.腐竹用温水泡软。\r\n3.粳米淘洗干净。\r\n4.将猪骨、粳米、腐竹放入砂锅，加水（约1500g），先用大火烧开，改用小火慢熬1个半小时左右，放入盐（3g），味精（0.5g），调好味，拣出猪骨。\r\n5.草鱼（或鲩鱼）洗净，斜刀切成大片，厚以0.3cm为宜，用盐、太白粉、姜丝、麻油拌匀，倒入滚开的粥内轻轻拨散，待粥再滚起，端离火位，用碗盛起，撒上胡椒粉、麻油即可食用。', NULL, NULL, NULL, '优质粳米、草鱼净肉各100g，猪肉200g，腐竹40g，味精1g，精盐、姜丝、葱、太白粉各5g，香菜10g，胡椒粉0.5g，麻油20g。');
INSERT INTO `recipe` VALUES (3081, 104, '西芹鸡柳', '1.鸡肉切条，加入腌料拌匀，腌15分钟，泡嫩油待用。\r\n2.西芹去筋切条，以油、盐略炒盛起。\r\n3.烧热锅，下油1汤匙爆香姜片、蒜片、红萝卜、加入鸡柳，加酒，放入西芹及芡汁料兜匀上碟即成。', NULL, NULL, NULL, '西芹、鸡肉各300克，红萝卜、姜片各数片，蒜肉（切片）2粒，酒1茶匙。盐、糖1/4茶匙，蛋白半只，生粉、生抽各1茶匙，麻油、胡椒粉各少许，油1汤匙，清水2汤匙。');
INSERT INTO `recipe` VALUES (3082, 104, '乌鸡白凤汤', '1.鸡宰后去血，清水煮至90度左右，见四周冒水泡时，加入一匙盐离火，浸入鸡，见鸡毛淋湿，提出脱净毛及嘴尖、脚上硬皮，剪去尖，剪开鸡屁股，开膛取出内脏，用水冲洗干净。\r\n2.清水加姜片煮沸，放入鸡，加上黄酒、葱结，用小火焖煮至酥，放入白凤尾菇，调味后沸煮3分钟起锅食用。', NULL, NULL, NULL, '乌骨鸡1只（约1000g），白凤尾菇50g，黄酒10g，葱、姜、味精各5g。');
INSERT INTO `recipe` VALUES (3083, 104, '团鱼汤', '1.团鱼（鳖）放入沸水锅中烫死，剁去头爪，揭去鳖甲，掏出内脏洗净。羊肉洗净待用。\r\n2.将团鱼肉、羊肉切成2厘米见方的小块，放入铝锅内，加苹果、生姜及水适量，置大火上烧开，移至小火炖熬至熟。\r\n3.加入食盐、胡椒粉、味精即成。\r\n4.可佐餐，亦可单食。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3084, 104, '松花猪肉', '1.先将冬菇用开水泡开，摘去根蒂。\r\n2.香菜洗净待用。\r\n3.猪肉、冬笋、香菇、葱均切碎，加入酱油、白糖、味精、五香粉调和好，用少许猪油炒调好的肉馅。\r\n4.将鸡蛋打匀，加进面粉、蛋、葱、味精、精盐搅打均匀，将其一半倒入放有少许猪油的热锅内，随时摇转炒锅，将蛋汁煎成165cm左右直径的圆饼时，再把炒好的肉馅倒在圆饼的中心铺平，另把剩下的一半蛋汁倒在肉馅的上面，使之形成一圆盖，然后把洗好的香菜撒在蛋片上，将剩下的猪油用旺火烧开，用勺盛起热油，慢慢浇在蛋饼上，使肉馅上的蛋汁烫熟成淡色时，取出蛋饼，去余油，即', NULL, NULL, NULL, '瘦猪肉50g，鸡蛋150g，冬笋15g，干冬菇3g，香菜叶1g，五香粉0.25g，酱油5g，白糖、味精、精盐各2g，葱末6g，面粉30g，猪油500g。');
INSERT INTO `recipe` VALUES (3085, 104, '什锦豆腐', '1.将冬菇用水发好，和猪肉、鸡肉、笋尖、火腿一起均切成片。\r\n2.将豆腐蒸一下，取出后切成方块。\r\n3.将油放入锅中上火，待锅热，把姜末、虾子放入锅中炒一下，之后立即放入蒸好的豆腐和切好的肉片、鸡片、火腿片、笋片及虾米，略煮一会儿，倒入酱油、料酒略炒，加入肉汤待烧开后倒入砂锅内，放在小火上约煮十余分钟，再加入味精，即可上桌。', NULL, NULL, NULL, '豆腐200g，瘦猪肉、火腿、笋尖各25g，虾子265g，鸡肉50g，干冬菇5g，干虾米10g，猪油5g，葱花、姜末、料酒25g，酱油15g，肉汤100g，味精1g。');
INSERT INTO `recipe` VALUES (3086, 104, '烧牛蹄筋', '1.先把生牛蹄筋放在砂锅内，加750g清水，小火煮至八分熟时捞出，切成2cm长条块。\r\n2.干团粉加入20g清水调匀。\r\n3.烧热油锅，先炒青菜随即把牛蹄筋、料酒、生姜、酱油及煮蹄筋汤倒入，煮开后再加味精及调好的团粉汁勾芡，装盘即可上桌食用。', NULL, NULL, NULL, '牛蹄筋250g，青菜心25g，酱油、料酒各10g，生姜末、葱花各5g，干团粉3g，味精0.1g，植物油25g，煮牛蹄筋原汤50ml。');
INSERT INTO `recipe` VALUES (3087, 104, '三鲜烩鱼唇', '1.冬菇泡软去蒂，叉烧切片。\r\n2.鱼唇洗净，放入姜、葱、开水中煮5分钟取出，冲净切块。\r\n3.西兰花洗净摘小朵，放入油、盐、水中灼熟盛起。\r\n4.烧热锅，下油两汤匙爆香姜片、葱段，加入调味料煮至开，放入鱼唇烩至软，加入红萝卜花、叉烧、西兰花拌匀，下芡汁料兜匀即可上碟。', '怀孕后期胎儿的生长迅速，蛋白质的摄取量应比平常增加20克，若摄取不足够，会很容易生下体形短小的婴儿或有早产的现象发生，鱼唇是高蛋白质的食品，三鲜烩鱼唇最适宜此时期食用。', NULL, NULL, '发好鱼唇500克，（或干鱼唇）300克，叉烧、西兰花100克，冬菇6只，红萝卜花数片，姜3片，葱（切段）2棵。盐半茶匙，生抽1汤匙，糖1/4茶匙，酒2茶匙，上汤3杯，生粉3/4茶匙，麻油、胡椒粉各少许，清水2汤匙。');
INSERT INTO `recipe` VALUES (3088, 104, '三鲜豆腐', '1.将海参、冬笋拣好，洗净，切成蚕豆丁大，用开水氽过，放入碗中。\r\n2.将虾仁洗净，切丁，用适量蛋白、太白粉及适量精盐拌好，用温油滑透，捞出待用。\r\n3.将豆腐切成7cm长、3cm宽、1.5cm厚的长方块，用热油炸成金黄色后捞出，从其上面各取一薄片，下面的一片揪成桶状，然后将海参、冬笋及虾仁丁加入适量精盐、味精、姜末、香油拌匀，分别填入豆腐内，再用掺入蛋黄的少许面粉抹在切下的豆腐片上，将豆腐盖封好，上笼蒸10分钟后取出，滤出汤汁，排入盘中。\r\n4.在炒锅中加入鸡汤、调味料，煮沸加入太白粉勾成薄芡，淋在豆腐', NULL, NULL, NULL, '白豆腐500g，水发海参、鸡蛋清各20g，虾仁100g，冬笋50g，鸡蛋35g，香菜25g，面粉15g，葱花、姜丝、精盐各3g，味精、香油各5g，酱油15g，花生油50g，鸡汤200g，太白粉10g。');
INSERT INTO `recipe` VALUES (3089, 104, '三丝汤', '1.将肉、鸡、笋切成细丝，切得越细越好。\r\n2.肉丝放入碗中，加入冷水搅散，浸出血水待用。\r\n3.炒锅洗净，置旺火上，加入白汤500g，倒入血水和血丝，放入笋丝、冬菇丝、烧至将滚，用漏勺把浮上来的丝捞起，洒上冷水少许，待浮沫升至汤面，即撇净，然后加入黄酒、盐、味精略滚。\r\n4.把捞出的肉丝、笋丝、冬菇丝，装在勺子内覆于碗中，然后把汤浇在碗中，洒上火腿，滴几滴猪油即成。', NULL, NULL, NULL, '生肉丝、生笋丝各25g，熟鸡丝、冬菇丝各15g，熟火腿丝10g，白汤500g，黄酒15g，食盐5g，味精2g，清猪油几滴。');
INSERT INTO `recipe` VALUES (3090, 104, '三片汤', '1.将猪肉、猪腰、竹笋洗净，切成片，香菇水发后撕成条状待用。\r\n2.炒锅洗净，置旺火上，加入白汤500g，再加入含有血水的鸭片、肝片、肉片、腰片、虾仁、香菇和笋片，加入少许冷水，用勺子淘散，推匀使血水跑出溶于汤中，烧至微滚，撇净浮沫，待汤全清。\r\n3.待汤清后，加入食盐、味精、黄酒略滚，起锅装碗，滴上几滴清猪油。', NULL, NULL, NULL, '虾仁、腰片、肉片、鸭肫肝片各15g，熟笋片25g，白汤500g，水发香菇10g，黄酒15g，食盐5g，味精1.5g，清猪油几滴。');
INSERT INTO `recipe` VALUES (3091, 104, '肉末蒸蛋', '1.将鸡蛋打入碗内搅散，放入精盐、味精、清水（适量）搅匀，上笼蒸熟。\r\n2.选用三成肥、七成瘦的猪肉剁成末。\r\n3.锅放炉火上，放入食油烧热，放入肉末，炒至松散出油时，加入葱末、酱油、味精及水（适量），用太白粉用水调匀勾芡后，浇在蒸好的鸡蛋上面即成。', NULL, NULL, NULL, '鸡蛋3个，猪肉50g，葱末、太白粉各5g，酱油10g，精盐2g，味精0.5g，食油25g。');
INSERT INTO `recipe` VALUES (3092, 104, '清汤', '1.宰杀母鸡后去净毛及内脏，洗净，将鸡胸肉及鸡腿剔下。\r\n2.与翅膀一同放入锅中，加入清水，待烧开后撇去血沫，然后用小火煮4－5小时。\r\n3.将鸡胸肉及鸡腿肉去净油质后拍碎成鸡茸，加入清水调稀，放入精盐、料酒、葱、姜、味精等待用。\r\n4.将煮好的鸡汤滤净碎骨肉，并撇去浮油，烧开，将调好的鸡茸倒入汤内搅匀，待开后再撇净油沫等杂质，即可成清汤。', NULL, NULL, NULL, '母鸡200g，肘子500g，精盐、味精各7.5g，料酒、葱、姜各10g。');
INSERT INTO `recipe` VALUES (3093, 104, '清炖猪肚汤', '1.猪肚用碱灰和香油混合搓揉5分钟，搓揉出粘液后用清水洗涤3－4遍，洗净后放入沸水锅中煮半小时，捞出，再用清水冲洗。\r\n2.把洗净的猪肚切成3cm长，3cm厚的片状。\r\n3.葱白洗净，切段。\r\n4.姜洗净，拍破待用。\r\n5.将锅放在旺火上，倒入猪油烧热，先放葱、姜炒一下，接着放入肚片爆炒，加盐。再将炒片装入砂锅或砂罐内，放足清水，用中火煨至猪肚烂时放入味精起锅即成。', NULL, NULL, NULL, '猪肚150g，香油、碱灰各10g，熟猪油30g，味精2g，精盐3g，葱白5g。');
INSERT INTO `recipe` VALUES (3094, 104, '清炖鸡参汤', '1.将发好的海参洗净，下开水锅氽一下取出。\r\n2.鸡骨、小排骨斩成块，与童子鸡一起下开水锅氽一下取出，洗净血秽。\r\n3.冬菇去蒂，洗净泥沙待用。\r\n4.将海参、童子鸡放在汤锅内，将笋花片放在海参与童子鸡间的空隙两头，火腿片放在中央，加入料酒、味精、精盐、葱姜、鸡骨、小排骨、高汤，盖上盖子，上笼蒸烂取出，除去鸡骨、排骨，捞去葱姜即可食用。', NULL, NULL, NULL, '水发海参400g，童子鸡7500g，火腿片25g，水发冬菇、笋花片各50g，鸡凤500g，小排骨250g，精盐6g，料酒35g，葱、姜各10g，味精5g，高汤1000g。');
INSERT INTO `recipe` VALUES (3095, 104, '芪归炖鸡汤', '1.活鸡宰杀，去毛及内脏，剁去鸡爪及嘴壳，用清水洗净。\r\n2.黄芪去粗皮，与当归均洗净待用。\r\n3.砂罐洗净，放清水400g，放入全鸡。烧开后撇去浮沫，加黄芪、当归、胡椒，用小火炖2小时左右，鸡肉同时加入精盐，再炖2分钟即可食用。', NULL, NULL, NULL, '小母鸡1只（约1000g），黄芪50g，当归10g，精盐5g，胡椒0.5g。');
INSERT INTO `recipe` VALUES (3096, 104, '皮蛋瘦肉粥', '1.将瘦肉切成两块，用精盐10g分别在肉块上涂匀，放入冰箱腌渍一夜，成为瘦咸肉。\r\n2.腐竹洗净、切粒。\r\n3.皮蛋去壳洗净，切成数块。\r\n4.白米洗净，用精盐5g，生油20g拌匀，成为油盐米。\r\n5.将清水（2000g）放入锅内烧沸，倒入油盐米、腐竹粒，并稍加拌匀，煮15分钟，放入洗净的咸瘦肉、一个皮蛋、麦片及剩下的生油，继续煮10分钟后改用小火再煮30分钟，视粥呈乳糊状时即可离火，瘦肉捞起，撕成肉丝，并与剩下的皮蛋粒一起放入粥内，煮沸片刻，即可放入精盐及味精调味。', NULL, NULL, NULL, '白米150g，瘦猪肉250g，水发腐竹50g，皮蛋2g，麦片30g，生油40g，精盐15g，味精3g。');
INSERT INTO `recipe` VALUES (3097, 104, '牛肉粥', '1.洗净牛肉，剁成肉末。\r\n2.将粳米淘洗干净。\r\n3.将锅置火上，倒入开水烧沸，放入葱段、姜块（拍松）、牛肉末、黄酒、五香粉煮沸，捞出葱、姜，倒入粳米，煮成粥，用精盐、味精调成咸鲜味即成。', NULL, NULL, NULL, '粳米400g，牛肉200g，味精、五香粉各3g，黄酒8g，葱段、精盐各10g，姜块5g。');
INSERT INTO `recipe` VALUES (3098, 104, '奶油鲫鱼', '1.将鱼去鳃剖去内脏，洗净，用刀在鱼背上每1cm宽划出人字形刀纹。\r\n2.把鱼放入沸水锅中烫一下捞出，将皮、头上血水、粘液烫掉，以去腥。\r\n3.炒锅置旺火上烧热，用油滑锅加入清猪油25g，烧至七分热，放入葱姜爆出香味。推入鲫鱼略煎，翻身，洒入黄酒略焖，随即放入白汤500g，冷水150g（放冷水的目的是色光好，解腥），猪油25g，盖牢锅盖滚3分钟左右，使汤白浓，调至中火焖3分钟，焖出鱼眼凸出，白木色，放入笋片、食盐、味精、调回旺火又滚至汤呈乳白色，加入豆苗略滚，拣掉葱姜，起锅装碗。笋片、火腿齐放在鱼上面，豆', NULL, NULL, NULL, '鲫鱼1条（500g），熟火腿2片，豆苗15g，白汤500g，笋片25g，食盐2g，葱结1只，姜片2片，味精15g，黄酒15g，猪油50g。');
INSERT INTO `recipe` VALUES (3100, 104, '龙眼贵妃翅', '1.将鸡翅膀去毛洗净，用精盐、酱油腌好。\r\n2.将龙眼去皮核。\r\n3.将葱破开后切段。\r\n4.将鸡翅放入热油锅内炸至金黄色捞出，锅内留油少许，下100g葱，爆出香味，放入汤、红葡萄酒及鸡翅，调好色、味，将鸡翅烧熟后脱骨，整齐地排入盘中。将龙眼用汤烧热，围在鸡翅周围。将剩下的葱用油爆出香味，再把烧鸡翅的汁滤入，用太白粉勾芡，浇在鸡翅上即可。', NULL, NULL, NULL, '肉鸡翅膀24个（约600g），龙眼、葱各250g，味精、酱油各10g，料酒、太白粉各15g，花生油150g，白糖20g，汤1250g，红葡萄酒100g。');
INSERT INTO `recipe` VALUES (3101, 104, '栗子黄焖鸡', '1.将肉鸡骨头剔下，剁成4cm见方的块状，用少许太白粉、精盐拌匀。\r\n2.剥去栗子外皮，用油炸至皮紧。\r\n3.将鸡块下油锅炸至金黄色倒出，锅留底油下葱姜稍炒，再下鸡块及汤，调好颜色及口味，以中火煮25分钟（烧煮之中放入栗子），待鸡熟汁浓时起锅，挑去葱、姜，用太白粉勾芡，盛盘即可食用。', NULL, NULL, NULL, '肉鸡1750g，栗子250g，味精、料酒、白糖、葱段、姜片各10g，太白粉15g，汤1000g，花生油150g。');
INSERT INTO `recipe` VALUES (3102, 104, '酱烤猪肝', '1.将猪肝去血，切成薄片，倒上酒和酱油，稍晾一下，涂上面粉。\r\n2.将沙拉油倒入锅中烧热，然后将准备好的猪肝两面煎一下，穿成长串。\r\n3.豆酱中加入海带柴鱼汤汁、白糖、料酒后点火，搅匀后做成熟豆酱。\r\n4.将煎后穿成长串的猪肝放入盘中，浇上熟豆酱，如再撒上花椒，会更美味。', NULL, NULL, NULL, '猪肝100g，面粉10g，豆酱、沙拉油各10g，酱油、糖、料酒各5g，海带柴鱼汤汁少许。');
INSERT INTO `recipe` VALUES (3103, 104, '鲫鱼豆腐汤', '1.豆腐切5cm厚的薄片，用盐沸水烫5分钟后沥干待用。\r\n2.鲫鱼去鳞、肠杂，抹上酒，盐渍10分钟。\r\n3.锅放炉火上，放入食油，烧至5分钟，爆香姜片，将鱼两面煎黄，加水适量，用小火煮沸30分钟，放入豆腐片，调味后勾薄芡，并撒上葱花。', NULL, NULL, NULL, '鲫鱼1条（约250g），豆腐400g，黄酒5g，葱花、姜片各3g，精盐2g，味精1g，食油30g。');
INSERT INTO `recipe` VALUES (3104, 104, '鸡蛋黄花汤', '1.将海带泡好洗净后切丝。\r\n2.黄花拣择洗净后切段。\r\n3.木耳泡发、洗净。\r\n4.鸡蛋打入碗中搅拌均匀。\r\n5.锅内加高汤烧开，放入味精及海带、黄花、木耳、白菜心，烧开后再冲入鸡蛋，再烧片刻后勾芡即成。', NULL, NULL, NULL, '鸡蛋3个，黄花、白菜心各10g，海带、木耳各5g，酱油3g，精盐2g，味精1g，高汤350g。');
INSERT INTO `recipe` VALUES (3105, 104, '鸡蛋豆腐', '1.将鸡蛋放入碗内，搅打均匀，加入精盐、葱末及豆腐，再搅打均匀。\r\n2.锅放炉火上，放入食油烧热，加入调好的鸡蛋，炒至鸡蛋凝固即成。', NULL, NULL, NULL, '鸡蛋3个，嫩豆腐150g，精盐5g，葱末2.5g，食油75g。');
INSERT INTO `recipe` VALUES (3106, 104, '黄鱼参羹', '1.将火腿末蒸熟。\r\n2.将大黄鱼肉及水发海参切成小方厚片。\r\n3.将鸡蛋打入碗中，搅拌均匀。\r\n4.锅内放油，烧至5－6分热时，放入葱末爆香，随即加入料酒、肉汤、海参片及黄鱼片、胡椒粉，煮开后放入精盐、味精略煮，缓慢倒入鸡蛋，待全熟时倒入水团粉勾成薄芡离火，倒入碗中，淋上熟猪油，撒上火腿末即可食用。', NULL, NULL, NULL, '大黄鱼肉、水发海参各125g，火腿末10g，鸡蛋40g，肉汤280g，料酒、精盐各5g，熟猪油、植物油各15g，葱末3g，干团粉2g，味精1g，胡椒粉0.5g。');
INSERT INTO `recipe` VALUES (3107, 104, '红烧蹄膀', '1.将蹄膀刮干净，放在砧板上，用刀直划开（可见骨头），下沸水锅内煮约2分钟捞出。\r\n2.葱打结。\r\n3.取砂锅一个，内放竹片垫底，蹄膀皮向下放在竹片上，加入葱结、姜片、酱油、白糖及水（水与肉面平），用旺火烧开，撇去浮沫，盖上锅盖，改用小火焖约1小时，将蹄膀翻身（皮向上），蹄膀酥烂时，取出竹片即成。清炖蹄膀，去酱油、白糖，加盐和水。', NULL, NULL, NULL, '猪前蹄1只（约1000g），葱、姜各5g，酱油90g，白糖10g');
INSERT INTO `recipe` VALUES (3108, 104, '红烧甲鱼', '1.先将甲鱼仰放在砧板上，待其头伸出时，迅速用左手捏住甲鱼的颈部，将颈全部拉出，右手持刀，从颈根部下刀放血，然后去头洗净，放入开水中略烫一下，拿出用小刀刮去全身粗皮和裙边上的黑膜，再用水洗净，放回开水锅中煮至裙边能削下甲壳时再去除内脏、气管、喉管、爪甲，切成3cm见方的小块，裙边切成3cm见方的薄片。\r\n2.将香菇去根洗净，火腿蒸熟切成薄片。\r\n3.锅内加清水，烧开，将切好的甲鱼放入沸水中氽一下提出，洗净后放在大碗中，加入料酒、精盐、猪油拌匀，上面放上火腿、香菇、大蒜、葱段、姜片，置于蒸笼中蒸30分钟左右', NULL, NULL, NULL, '甲鱼500g，火腿25g，香菇10g，姜片、葱段、料酒各15g，大蒜片、太白粉、酱油、猪油各25g，味精1.5g，精盐1g，花生油40g，香油5g。');
INSERT INTO `recipe` VALUES (3109, 104, '红烧海参', '1.海参放入姜、葱、开水内煮5分钟，除去内脏洗净，滴干水分。\r\n2.瘦肉切丝，加入调味料拌匀，泡嫩油待用。\r\n3.白菜洗净，以油、盐、水灼熟围于碟边。\r\n4.烧热锅，下油两汤匙爆香姜、葱，加入煨料及海参煮至海参软烂，放入瘦肉，芡汁料兜匀上碟即成。', '海参的营养价值极高，含丰富的蛋白质、钙和碘，是滋补食品，具有补血调经的功用，更有安胎及利于生产的功能，最适宜怀孕后期食用。', NULL, NULL, '发好海参500克，瘦肉200克，白菜300克，姜2片，葱2棵，红萝卜数片。盐、糖各半茶匙，生抽、酒各1茶匙，上汤1杯，蚝油、生粉各1茶匙，油半汤匙，麻油、胡椒粉各少许，清水3汤匙。');
INSERT INTO `recipe` VALUES (3110, 104, '核桃明珠', '1.核桃肉放入开水中煮3分钟，取出滴干，放入暖油中炸至微黄色盛起。\r\n2.虾去壳，去肠，用盐擦洗干净，冲水吸干水份，加入调味料拌匀，泡嫩油盛起。\r\n3.烧热锅，下油一汤匙爆香蒜茸，加入芦笋、红萝卜略炒，放入虾，加酒，下芡汁料及核桃肉，兜匀上碟即成。', '核桃是滋养食品，有补血的功效，常吃能使皮肤光滑。虾含有大量蛋白质，可算是养生佳品，配合含丰富纤维的芦笋，最适宜怀孕后期食用。', NULL, NULL, '去皮核桃肉50克，虾400克，芦笋粒3汤匙，红萝卜粒2汤匙，蒜茸半茶匙，酒1茶匙，盐、糖、生粉各1/4茶匙，蛋白、油各1汤匙，麻油、胡椒粉各少许，蚝油1茶匙，清水2汤匙。');
INSERT INTO `recipe` VALUES (3111, 104, '海参羊肉汤', '1.海参以40度温水泡软后，剪开参体，除去内脏，洗净，再用开水煮10分钟左右，取出后连同水倒入碗内，泡2－3小时。\r\n2.羊肉洗净，去血水，切成小块，加水适量（约50g），小火炖煮，煮至将熟，将海参切成小块放入同煮，再煮沸15分钟左右，加入生姜末、葱段、胡椒末及精盐，即可。温食参肉，饮汤，或供餐用。', NULL, NULL, NULL, '海参50g，羊肉250g，生姜2片，葱5g，胡椒末0.5g，食盐3g。');
INSERT INTO `recipe` VALUES (3112, 104, '锅粑肉片', '1.将里脊肉横着肉纹切成长5cm、宽2cm、厚0.2cm的片状，放入5g太白粉、2g料酒、1g精盐的混合液中拌匀。\r\n2.水发玉兰片和水发香菇切成厚片。\r\n3.将精盐2g、料酒3g、太白粉水15g、白糖、醋、酱油、味精和肉汤放入另一碗内，调成汁液。\r\n4.选干透厚薄适宜的锅粑，用手掰成6cm见方的块状。\r\n5.将里脊肉片放入烧至七分热的炒锅内炒，再依次放入姜、葱、蒜、玉兰片、香菇片及辣椒，与肉末一起炒匀，倒入汁液，待收汁后盛入碗内。\r\n6.将锅粑放入八分热的油锅内炸酥，待浮面呈金黄色时捞到较深的大圆盘内，', NULL, NULL, NULL, '白米锅粑100g，里脊肉75g，水发香菇10g，水发玉兰片25g，料酒、红辣椒各5g，味精1g，太白粉水20g，肉汤250g，蒜片、精盐、姜片3g，醋10g，白糖、酱油各8g，葱10g，植物油500g（实耗75g）。');
INSERT INTO `recipe` VALUES (3113, 104, '贵妃牛腩', '1.红萝卜去皮洗净，切角形。\r\n2.牛腩洗净，放入开水煮5分钟，取出冲净，再放入开水中煮20分钟，取出切厚块，汤留用。\r\n3.烧热锅，下油2汤匙爆姜片、葱段、豆瓣酱、甜面酱等，加入牛腩爆炒片刻，加酒，放入调味料及八角等烧开，改慢火煮30分钟，加入红萝卜煮至熟，以少许生粉打芡，上碟时放上芫荽即成。', NULL, NULL, NULL, '牛腩500克，红萝卜250克，姜（切片）25克，葱（切段）2棵，辣豆瓣酱、番茄酱、酒各1汤匙，甜面酱半汤匙，八角1粒，芫荽少许，盐1/4茶匙，糖1汤匙，生抽3汤匙，牛脯汤两杯。');
INSERT INTO `recipe` VALUES (3114, 104, '龟肉汤', '1.将活龟头剁掉放血，剥开壳，去掉苦胆，取龟肉及内脏洗净，切约3cm长，2cm宽的肉块。\r\n2.炒锅置旺火上，放猪油烧热，先下葱段、姜块略爆香，再放龟肉、内脏、精盐、香油一起爆炒，起锅盛入砂锅，一次放足清水，置旺火上煨2小时，如有龟蛋则加入，继续煨至汤汁浓稠，发出香气时，加入味精，起锅装碗即成。', NULL, NULL, NULL, '乌龟500g，猪油、香油各100g，味精5g，精盐、葱段、姜块各10g。');
INSERT INTO `recipe` VALUES (3115, 104, '蛋奶炖布丁', '1.将牛奶分为两份，一份与白糖混合，放在小火上慢慢加热使白糖溶化。\r\n2.布丁模可用上大下小的瓷茶杯代替，洗净擦干，涂一层薄油备用。\r\n3.锅中加水15g，糖50g，小火慢熬至金黄色后，趁热倒入布丁模内，垫住布丁模的底层（约2cm厚）。\r\n4.鸡蛋打入碗内搅打均匀，先加冷牛奶搅拌，再倒入加糖溶化的热牛奶搅匀，然后用细筛（或干净纱布）过滤即成蛋奶。\r\n5.将蛋奶浆倒入布丁模内（装至八分满），入笼微火炖约20分钟，至蛋浆中心熟透即可出笼，冷却后覆于小玻璃（或小茶碟）上即可食用。', NULL, NULL, NULL, '鲜牛奶250g，白糖125g，鸡蛋135g。');
INSERT INTO `recipe` VALUES (3116, 104, '淡菜腰花', '1.洗净淡菜。\r\n2.将鸡肉切成小鸡块。\r\n3.将淡菜、鸡块同时放入碗中后加入少许温开水，用旺火蒸2小时取出，拔掉淡菜中的菜须，用纱布滤去渣，成为原汁。\r\n4.将猪腰放在清水中浸泡1小时以除去腥味，用刀剔去腰子中间的腰臊，每片切成几个小块。放入开水锅中煮沸30分钟，待煮熟后捞起冲净，再放入砂锅内，加入葱花、姜末、料酒、精盐、花椒，加入少许清水后，在微火上约蒸3小时取出，弃汤不要。\r\n5.把蒸好的淡菜和腰块与原汁放在一起，加上味精后，再煮20－30分钟，加上用水已调好的团粉、猪油搅拌均匀，即可装盘食用。', NULL, NULL, NULL, '淡菜、瘦猪肉各10g，猪腰250g，鸡肉15g，姜末、花椒、味精、精盐各0.5g，酱油、料酒各1g，干团粉、猪油各1.5g，葱花2g。');
INSERT INTO `recipe` VALUES (3117, 104, '大排蘑菇汤', '1.每块大排骨用刀背拍松，再敲断骨髓后加酒、盐腌15分钟。\r\n2.锅中加水适量，放炉火上烧煮，水沸放入大排骨，撇去浮沫，加酒，用小火煮30分钟，加入蘑菇片再煮10分钟，放入调味料后加入蕃茄片，煮沸即可食用。', NULL, NULL, NULL, '大排骨500g，鲜蘑菇、蕃茄各100g，黄酒10g，精盐5g，味精3g。');
INSERT INTO `recipe` VALUES (3118, 104, '荸荠鱼卷', '1.将加工后的肥肉、黄鱼肉、荸荠、荠菜都切成细丝，加入葱末、姜末、鸡蛋清、料酒、精盐、香油、味精调成肉馅。\r\n2.把油皮一张切成两半，各铺平撒上混合的肉馅，再卷成长卷，外面抹上细糊后，再切成3cm长的小段，蘸用面粉、小苏打和清水调成的面糊，放在油锅中炸成金黄色，起锅食用。', NULL, NULL, NULL, '黄鱼肉100g，肥猪肉、荸荠、荠菜各25g，鸡蛋清30g，油皮50g，小苏打、精盐各1.5g，味精1g，葱末2.5g，面粉、干团粉各60g，料酒、香油各5g，植物油1000g。');
INSERT INTO `recipe` VALUES (3119, 104, '白糖豆浆', '1.将黄豆择洗干净，浸泡7小时（夏季4小时），捞出用清水洗净，沥尽水，带水用石磨或用多功能食品加工机磨成细浆，灌入干净布袋内，向袋内加适量清水，将袋口扎紧。\r\n2.备一干净盆，将盆垫高，使盆向前倾斜，浆袋放入盆内，用手有节奏地轻揉，使浆液不停地流出，揉至袋内只剩渣滓为止。\r\n3.将钢锅洗净，倒入豆汁，放在中火上煮开，加入白糖搅动，稍煮一会儿即可食用。', NULL, NULL, NULL, '黄豆100g，白糖50g。');
INSERT INTO `recipe` VALUES (3120, 102, '猪肝汤', '1.将猪肝冼净，切成0.3厘米厚、2厘米宽、3厘米长的片，放入碗内，加入少许料酒、精盐及水50克腌片刻；菠菜择洗干净，切成小段，用开水烫一下，捞出，沥干水。\r\n2.锅置旺火上加入植物油烧热，下入葱姜末爆香，放入盐，加水100克，烧沸后将肝片下入锅内，烧开后撇去浮沫，放入菠菜，再烧开后，倒入碗内即成。', '肝鲜嫩，菜烂软，可口。', '猪肝下锅后，煮至断生即可，不要煮老，以免影响口味。', NULL, '猪肝50克，菠菜50克，植物油13克，精盐2克，葱姜末5克，料酒3克。');
INSERT INTO `recipe` VALUES (3121, 102, '猪肝菠菜汤', '把100克猪肝洗净，切成小薄片，菠菜切成两厘米的段。锅里放入清汤，汤烧开后，把锅里的猪肝、菠菜捞在大碗里。撇净汤里浮沫，加精盐、味精、香油调好味就行。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3122, 102, '蒸蛋', '1.将蛋放入碗中打散，放入高汤、肉末拌匀。  \r\n2.移入蒸锅中，待水沸，继续蒸20分钟。', NULL, '肉末可以清蒸鱈鱼、鱿鱼去刺代替；素食幼儿可以素火腿、豆皮或豆腐等代替。', NULL, '蛋1个，清高汤1/2杯，肉末1/2大匙。');
INSERT INTO `recipe` VALUES (3123, 102, '炸藕盒', '鲜藕洗净去皮，在开水中烫一下，切成两片相连的合页片。肉末加葱末、姜末、酱油、盐和一些淀粉，加点水搅拌匀。藕片加上肉馅挂上面糊，炸成金黄色就行了。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3124, 102, '玉米丸子', '1.把绞肉剁细，拌入鸡蛋、盐、淀粉调匀，顺时针方向搅拌。\r\n2.将绞肉分成一个个小丸子，每个丸子裹上一层玉米粉，放入盘内，入锅，以中火蒸八分钟即可食用。', '玉米丸子色泽明亮、鲜甜可口，可促进小儿食欲。适于一岁半以上小宝贝食用。', '玉米粉有天然甜味，质地甜嫩，很适合小宝贝食用。若用新鲜玉米粉，要先将玉米粉煮熟，否则生玉米粉短时间煮不熟，煮时水里加点盐，玉米粉会比较甜。', NULL, '绞肉6两、熟嫩玉米粉1杯、鸡蛋一个、盐、淀粉少许。');
INSERT INTO `recipe` VALUES (3125, 102, '鱼丝烩粟米', '1.黄鱼去皮去刺改刀成黄豆芽粗细的丝状，清水漂净，用盐、味精少许，蛋清、淀粉上浆待用。\r\n2.炒锅放油烧至二成热时，入鱼丝滑油至熟捞出，用开水焯去鱼丝的油，待用。\r\n3.锅洗净，放入鲜汤适量，烧开加盐、入粟米烧开，入鱼丝，放味精烧开，水淀粉勾芡，淋熟油即可。', NULL, NULL, NULL, '净黄鱼肉150克，粟米100克，蛋清1只，精制油少许，盐少许，味精少许，鲜汤适量，淀粉适量。');
INSERT INTO `recipe` VALUES (3126, 102, '油爆鳝片', '1.宰杀鳝鱼，清洗干净后去骨，改成菱形片，加盐、鸡蛋、味精、淀粉上浆待用；红椒切片、笋片焯水待用。\r\n2.锅置旺火上，下色拉油烧至四成热，放入鳝片过油，捞起沥干油。锅留底油，加姜粒、蒜茸煸香，依次放入豆瓣酱、红椒、笋片、高汤、盐翻炒，再加入鳝片、酱油、味精勾芡，淋上红油、撒上葱段即成。', '蒜香咸鲜微辣，鳝鱼中的胶原蛋白丰富，经常食用，有助健康发育。', '爆鳝片的关键是先上浆，后过油，并快速翻炒，鳝片才鲜嫩。', NULL, '鲜活鳝鱼500克。红椒100克，笋片50克，鸡蛋1个，盐、味精、酱油、料酒、淀粉、碎豆瓣酱、红油、蒜茸、姜粒、葱段各少许。');
INSERT INTO `recipe` VALUES (3127, 102, '香椿薯拌豆腐', '1.将香椿芽择洗干净，放入碗内，倒入沸水，用盘子扣上，焖5分钟，捞出挤去水，切成细未。 \r\n2.将豆腐切成小丁，放入锅内稍煮一下，捞出放在盘内，加入香椿芽末、精盐、香油拌匀即成。', '此菜软嫩可口，气味芳香。', '制作时，不要把豆腐直接与香椿拌食，应先将豆腐用开水煮一下，晾凉后再拌之。', NULL, '豆腐100克，嫩香椿芽25克，香油3克，精盐2克。');
INSERT INTO `recipe` VALUES (3128, 102, '苋菜鱼肉饭', '1.鱼蒸熟后（最好选鱼脯，因鱼脯无幼骨），拣出鱼肉弄碎。\r\n2.苋菜洗净，放入滚水中焯软捞起，滴干水剁细。\r\n3.米洗净，加入浸过米面的清水浸1小时。\r\n4.水1杯或适量，放入小煲内煲滚，放下米及浸米的水煲滚，慢火煲成浓糊状的烂饭，放下苋菜搅匀煮粘，下鱼肉及极少的盐搅匀，煲滚即可。待温度适合时，便可喂幼儿进食。', NULL, '苋菜含草酸较多，煮时最好先用滚水焯过，这样草酸可溶于水，能去掉涩味。真它适合幼儿吃的蔬菜都可以用此方法烹调饭。', NULL, '米3汤匙，蒸熟的鱼肉1汤匙或适量，苋菜1－2棵。');
INSERT INTO `recipe` VALUES (3129, 102, '咸蛋蒸肉饼', '\r\n将咸蛋的蛋黄取出，对半切开，瘦肉剁烂，咸蛋白与鸡蛋打匀，加入少量肉调匀，放少许盐，把咸蛋黄放在上面，搁水慢火蒸至瘦肉熟，加少许香油，便可食用了。', NULL, NULL, NULL, '2个咸蛋、1个鸡蛋、3两瘦肉。');
INSERT INTO `recipe` VALUES (3130, 102, '虾皮鸡蛋羹', '把虾皮洗净稍浸泡，鸡蛋去壳打匀，加少量温开水、盐，与虾皮拌匀，隔水慢火蒸熟。', NULL, NULL, NULL, '鸡蛋一只，虾皮少许。');
INSERT INTO `recipe` VALUES (3131, 102, '虾皮豆腐', '1.将豆腐放入开水锅内烫一下，捞出沥水后切成1厘米见方的小丁；虾皮择洗干净，剁成细未。 \r\n2.锅置火上，放入猪油烧热，下入葱姜末和虾皮，爆香后倒入豆腐，翻炒一下加入酱油、白糖、精盐及水100克，翻匀烧沸，转小火烧2分钟，用水淀粉勾芡，盛入盘中即成。', '此菜鲜嫩适口。', '虾皮择洗干净剁碎。菜不宜咸。', NULL, '豆腐100克，虾皮15克，熟猪油15克，酱油25克，白糖1.5克，葱姜末4克，水淀粉3克，精盐少许。');
INSERT INTO `recipe` VALUES (3132, 102, '西红柿荷包蛋', '1.将西红柿洗净，去皮去籽，切成小片；菠菜择洗干净，切成2厘米长的段。 \r\n2.锅置火上，加适量水烧开，磕入鸡蛋，煮熟即成荷包蛋。 \r\n3.另取一净锅，放入花生油，烧热，下入葱丝、姜丝炝锅，再下入西红柿，煸炒一会儿，将煮熟的荷包蛋及水一起倒入，加入精盐、白糖、菠菜段，开锅后，用水淀粉勾芡，盛入大碗内即成。', '色彩艳丽，味道酸甜，营养丰富。', '制作时，鸡蛋要往开水锅内磕，凉水下锅鸡蛋易煮碎。', NULL, '鸡蛋2个，西红柿25克，菠菜10克，花生油6克，精盐2克，白糖5克，淀粉3克，葱丝、姜丝各少许。');
INSERT INTO `recipe` VALUES (3133, 102, '温拌双泥', '1.把茄子、土豆分别洗净，茄子去皮，土豆上屉蒸烂，剥去土豆皮，分别捣成泥茸状，加入精盐、味精拌匀。 \r\n2.将熟鸡蛋剥去皮，蛋清、蛋黄分开，将蛋黄捣成泥，蛋清切成细未，各加少许精盐拌匀。 \r\n3.将拌好的茄泥、土豆泥对称放在盘内，再把蛋清蛋黄分别放在茄泥和土豆泥两侧，将西红柿酱堆放在中间，撒上香菜末，浇上香油即成。', '清香利口，富于营养，极宜幼儿食用。', '要注意厨具和制作间的卫生。', NULL, '茄子100克，土豆100克，熟鸡蛋一个，番茄酱5克，精盐、味精、香菜末、香油各少许。');
INSERT INTO `recipe` VALUES (3134, 102, '水果冻', '琼脂加水煮化，放入白糖与切成小丁的水果，倒入平盘内，冷却后切成小块即可食用。水果可选1－2种,并注意色彩搭配。', NULL, NULL, NULL, '琼脂、水果（可选用菠萝、猕猴桃、桔子、樱桃、苹果等）、白糖等');
INSERT INTO `recipe` VALUES (3135, 102, '双色萝卜', '白萝卜、胡萝卜切成小方块，在开水锅里煮一小会儿，用清水浸泡1小时，控去水分。炒锅油热后放精盐炝锅，加进肉汤、酱油、味精烧开后放入胡萝卜块。烧熟后将萝卜块捞出，余下的汤汁加入淀粉，淋些香油，将稠汁浇在萝卜块上。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3136, 102, '什锦面', '1.细面或河粉先以沸水煮至熟软，切1－2公分小段。  \r\n2.锅內放入所有材料和少许盐，以小火煮熟。', NULL, '清高汤是由排骨煮成汤，并去掉油脂部分。细绞肉可以蛋、鱈鱼末等代替，素食幼儿可以素火腿、豆皮或豆腐等代替。', NULL, '胡萝卜末1/2大匙，菠菜末1大匙，细绞肉1大匙，细面或河粉50公克，清高汤1/2杯。');
INSERT INTO `recipe` VALUES (3137, 102, '烧草鱼豆腐', '1.将草鱼肉洗净，顺长剖开，切成1厘米见方的丁；豆腐亦切成同样大小的丁；笋切0.3厘米厚的小方片。 \r\n2.锅置火上，放油烧至八成热时，下鱼丁煎黄，烹入料酒，加盖略焖，加入葱、姜、酱油、精盐，烧上色后，倒入鲜汤烧开，加盖转小火煨3分钟，下入豆腐、笋片，再焖3分钟，转旺火烧稠汤汁，加入味精，撒上蒜苗，盛盘内即可。', '此菜鱼肉鲜嫩，豆腐爽滑，汤汁鲜美。', '制作中，要用文火，防止把鱼肉和豆腐烧碎。', NULL, '净草鱼肉100克，豆腐100克，笋10克，蒜苗5克，熟猪50克，酱油、料酒、精盐、味精、葱末、姜末、鲜汤各适量。');
INSERT INTO `recipe` VALUES (3138, 102, '沙锅豆腐', '豆腐切成片，猪肉切成薄片，香菇、海米用水发好洗净，油菜心洗净切段。砂锅里放入水、鸡汤、葱段、姜片、料酒和盐，开锅后放入肉片，再放入海米、香菇和豆腐片，开锅后改用小火炖30分钟。然后放入油菜心，菜熟后加点味精就好了。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3139, 102, '三色烩饭', '1.将青花椰菜、小蕃茄放入锅中。  \r\n2.加入清高汤1杯，煮至熟、软。\r\n3.以适量太白粉水勾芡。  \r\n4.将蛋打散置锅中煮熟即成三色汤。  \r\n5.煮好的三色汤1/3碗拌入2/3碗白饭中。', NULL, '1－3岁幼儿，材料可加白饭1/3碗煮沸，小火烧煮20分钟（记得搅拌数次），置焖烧锅焖1－2小时即成三色烩饭。', NULL, '青花椰菜数朵，切成0.5cm细丁，小蕃茄1－2个，切成0.5cm细丁，蛋1个，清高汤1杯，太白粉1小匙。');
INSERT INTO `recipe` VALUES (3140, 102, '三色豆芽', '绿豆芽洗净；红和绿柿子椒各一个洗净切成丝；香菇发好后洗净切成丝。锅里加水烧开，放入豆芽翻一翻，捞出晾凉；炒锅加油烧热后，放入姜丝、青红椒丝和香菇丝炒，加盐、白糖、味精翻炒拌匀后出锅晾凉，与豆芽拌在一起。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3141, 102, '三明治', '1.吐司片切成三角形，将上述材料做成两个三明治。  \r\n2.置摄氏100度烤箱烤10分钟。', NULL, '煎蛋可以Cheese代替，素火腿可以鱿鱼酱（去油）、鱼松、肉松等代替，番茄可用烫过绿色蔬菜叶代替。1－3岁幼儿可将吐司四边切除。', NULL, '吐司2片，番茄切成2薄片，蛋1个，煎成2薄片，素火腿切成2薄片。');
INSERT INTO `recipe` VALUES (3142, 102, '软煎鸡肝', '1.将鸡肝洗净，摘去胆囊，切成圆片，撒上精盐、面粉，蘸满蛋清液。\r\n2.锅置火上，放油烧热，下入鸡肝，煎至两面呈金黄色即可。', '肝软鲜香，幼儿喜食。', '鸡肝切时要薄，以利煎透熟软。', NULL, '鸡肝100克，面粉少许，鸡蛋清、精盐、植物油各适量。');
INSERT INTO `recipe` VALUES (3143, 102, '肉松豆腐', '1.豆腐洗净用热开水烫过，沥干水份。\r\n2.幼儿肉松置于豆腐上即可拌食。', NULL, '此道菜肴只有蛋白质的来源，宜与米饭类及蔬菜一起食用（如蔬菜粥），方达均衡之效，适合1岁以上幼儿。', NULL, '幼儿食用肉松1汤匙，豆腐1/2块。');
INSERT INTO `recipe` VALUES (3144, 102, '清蒸肝糊', '1.将猪肝去掉筋膜，切成小片，和葱花一起炒熟，盛出剁成细末。\r\n2.将猪肝末放入碗内，加入鸡蛋液、清水、精盐、香油搅匀，上屉用旺火蒸熟即成。', '此糊鲜香，软嫩。', '制作时，先把猪肝炒熟后再切成细末。', NULL, '猪肝125克，鸡蛋半个，香油、精盐、葱花各适量，清水50克。');
INSERT INTO `recipe` VALUES (3145, 102, '牛奶鸡蛋羹', '可以把鸡蛋、牛奶、白糖共调匀，然后隔水慢火蒸熟。另外，也可以把牛奶放入锅内，加鸡蛋，慢火煮至鸡蛋熟，最后加白糖调味。', NULL, NULL, NULL, '1袋牛奶、1个鸡蛋。');
INSERT INTO `recipe` VALUES (3146, 102, '蘑菇蛋卷', '1.取一烤锅，以纸巾蘸沙拉油1大匙涂抹烤锅内底，先用微波炉以强微波3分钟预热后，倒入1个打好的蛋液，轻晃锅子使蛋液均匀铺在烤锅内部，然后再以强微波1分钟煎成蛋皮。 \r\n2.奶油软化后，与面粉、奶水搅拌均匀，以70%电力煮4分钟，取出搅拌，重复此动作数次，直至变成适当度之面糊。 \r\n3.取1大盘，先放上蛋皮，再铺上面糊、蘑菇及火片，撒上少许胡椒粉，卷起即可。', NULL, NULL, NULL, '鸡蛋2个、罐头蘑菇1OO克、火腿片8O克、奶油3大匙、面粉1/2杯、奶水1杯，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3147, 102, '绿豆西米露', '绿豆仁洗净，加水3/2杯煮沸，中火煮20分钟，西谷米加入，小火煮10分钟（记得搅拌数次），最后加椰浆粉、糖，置焖烧锅焖30分钟即成。', NULL, NULL, NULL, '绿豆仁1大匙，西谷米1/2大匙，水3/2杯，椰浆粉1/2大匙，糖1大匙。');
INSERT INTO `recipe` VALUES (3148, 102, '绿豆沙牛奶', '1.绿豆煮熟后取2汤匙。\r\n2.加牛奶一起入果汁机搅打。\r\n3.可以不加糖。', NULL, '适合1岁半幼儿。', NULL, '绿豆泥2汤匙，牛奶1杯');
INSERT INTO `recipe` VALUES (3149, 102, '烤马铃薯', '1.烤箱以摄氏200度预热。  \r\n2.将切丁的马铃薯分別放入3个涂上奶油（1小匙）的蛋糕烤杯中，上以铝箔纸覆盖。\r\n3.摄氏200度烤15－20分钟。', NULL, NULL, NULL, '马铃薯1个，奶油3小匙。');
INSERT INTO `recipe` VALUES (3150, 102, '烤点心/布丁', '1.烤箱以摄氏150度预热。  \r\n2.将蛋、蜂蜜打散，再加入牛奶拌匀，分別倒入数个涂上奶油的布丁杯。\r\n3.烤盘加入一杯水。  \r\n4.将布丁杯移入烤盘中。 \r\n5.摄氏150度烤15－20分钟。', NULL, '牛奶以幼儿平常所喝的幼儿奶粉配制。蛋1个，蜂蜜1/2小匙，牛奶1/2杯依上述作法，即成烤布丁。', NULL, '蛋1个，蜂蜜1小匙，牛奶1杯。');
INSERT INTO `recipe` VALUES (3151, 102, '桔味印糕', '将以上4种原料用温开水和成软硬适中的粉团。印糕模内撒上干粉（防止粘住），揿入粉团压紧后再敲出,上笼蒸熟，即为桔味印糕。如放入其他原料（如可可粉、番茄酱、青菜汁）,可做成不同颜色、风味各异的印糕。', NULL, NULL, NULL, '粳米粉、糯米粉各半，果珍、白糖适量。');
INSERT INTO `recipe` VALUES (3152, 102, '黄瓜色拉', '1.将葡萄干洗净，用温水泡软；黄瓜去皮，涂少许盐、切成小片；西红柿用开水烫一下，去皮。去籽，切成小片；橘子去皮。去核切碎。 \r\n2.将葡萄干、黄瓜、西红柿。橘子放入盘中，加入蛋黄酱拌匀即成。', '色泽鲜艳，爽口不腻，味美适口，营养丰富。', '各种原料一定要切碎，以适应幼儿的咀嚼与消化能力。', NULL, '黄瓜30克，橘子3瓣，西红柿10克，葡萄干10克，蛋黄酱、精盐各少许。');
INSERT INTO `recipe` VALUES (3153, 102, '花生鸡脚汤', '鸡爪10只，爪间洗净，加酒、姜片和水煮30分钟，加入花生，用小火煮1到2小时，调味后撒上一点葱花，淋点鸡油就好了。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3154, 102, '花色饭', '将西式火腿、胡萝卜切成丁，鸡蛋打散调味蒸熟后切成丁。大米煮成饭。胡萝卜丁、豌豆粒下油锅炒熟，加入西式火腿丁、鸡蛋丁略炒，最后再倒入米饭，放少许盐、味精调味拌匀即可食用。也可用洋葱、蘑菇、青豆、胡萝卜、西式火腿、鸡蛋等炒成西式炒饭；用虾仁、鲜贝、豌豆等炒成海鲜饭。', NULL, NULL, NULL, '大米、西式火腿、胡萝卜、鸡蛋、豌豆等。');
INSERT INTO `recipe` VALUES (3155, 102, '红根腐竹', '1.胡萝卜洗净去皮，切成5厘米长、0.5厘米宽方条。腐竹冷水浸透，洗净、切成胡萝卜同样大小的条。\r\n2.香油放炒锅烧热，下花椒炸焦黄捞出，投入葱、姜丝，加清水半碗，随即放精盐、白糖、味精、腐竹和胡萝卜，开锅后小火焖至汤汁收尽，离火晾凉即成。', '富含植物蛋白、铁、钙、及胡萝卜素等营养素，色香味美。', NULL, NULL, '水发腐竹50克，胡萝卜75克，香油15克，葱、姜各1克，花椒1克，白糖1克，味精0.5克，精盐2克。');
INSERT INTO `recipe` VALUES (3156, 102, '汉堡牛肉饼', '1.取1盛盘，放入奶油及洋葱未，用微波炉以强微波3分钟炒香后，拌入面包粉备用。 \r\n2.牛绞肉加入蛋拌匀，再加入炒香的洋葱末及调味料，充分搅拌均匀后，分制成6个肉饼。 \r\n3.烤锅入沙拉油3大匙，先以强微波预热5分钟，放入牛肉饼，用强微波煎2分钟后，取出翻面，再以强微波2分钟即可。', NULL, NULL, NULL, '牛绞肉4OO克、洋葱末1杯、奶油3大匙、面包粉1杯、蛋1个，盐1小匙、胡椒粉1/4小匙。');
INSERT INTO `recipe` VALUES (3157, 102, '枸杞猪肝汤', '鲜枸杞叶100克，猪肝100到200克，洗净后将猪肝切片煮汤，加姜末、盐、味精。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3158, 102, '翡翠白玉汤', '在鸡血里加些水淀粉，在平底锅上摊成薄片切成条；豆腐切成条，挂上蛋糊后在平底锅上烙一下，使蛋糊凝固在豆腐条上；炒勺里放入高汤，开锅后加入菠菜段和鸡血条、豆腐条，再开锅后加盐、味精调好味，就行了。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3159, 102, '番茄牛肉汤', '1.里脊边肉洗净，切小块，先氽烫过，再冲净泡沫，加水6杯及酒适量。姜2片先煮40分钟使其熟软，然后挑除。\r\n2.番茄用热水烫过，剥除外皮后切片，放入牛肉汤中，再煮十分钟，加入调料。\r\n3.最后加入烫熟过的青豆仁，再煮开即熄火，盛出食用。', NULL, '里脊边肉质地较嫩、油层较薄，较适合小宝宝咀嚼。番茄选红一点儿的比较不酸，去皮切片再煮比较易烂。青豆仁先煮熟，冲凉再用，可保持脆绿；若用超市的冷冻青豆仁则可直接加入。', NULL, '牛里脊边肉0.5斤、红番茄2个、姜2片、青豆仁2两、酒、盐、糖、淀粉各适量。');
INSERT INTO `recipe` VALUES (3160, 102, '豆浆', '将大豆洗净，用水浸泡7－20小时（根据气候冷暖确定时间）后，磨碎加水（豆、水比例为1:7－9），过滤煮沸即成。制作时可根据营养需要加入花生、芝麻等食品。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3161, 102, '豆腐鱼肉饭仔', '1.把已蒸熟的豆腐蒸鱼，拣去鱼骨，鱼肉及豆腐弄碎，加入少许蒸鱼的生抽、熟油，分量大约各1汤匙或视食量而定。\r\n2.米洗净，加入浸过米面的水浸1小时。\r\n3.把适量的水放入小煲内煲滚，放下米及浸米的水煲滚，慢火煲成浓糊状的烂饭，加入豆腐、鱼肉搅匀煲滚，即可熄火。待温度适合时，便可喂幼儿进食。', NULL, '煲烂饭时，当饭水渐干时要搅动，以免粘底。大人的菜若是豆腐蒸鱼，蒸熟后便可拣给幼儿煲饭仔。如果是鸡蛋蒸肉饼，也可以将肉饼弄碎，依照此法煲饭仔给幼儿进食，可免专为幼儿买菜的麻烦。', NULL, '米适量，豆腐蒸鱼酌量。');
INSERT INTO `recipe` VALUES (3162, 102, '冬瓜鲤鱼汤', '鲤鱼肉洗净，切成块，加上料酒和盐腌15分钟。待锅里的油温热后，将鱼块煎黄后加一些清水，用小火煮30分钟。加入冬瓜片煮10分钟后调味即成。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3163, 102, '蛋黄豆腐', '1.将鸡胸脯肉剁成泥，加入鲜汤调匀；熟咸鸭蛋研成细泥，豆腐用开水烫过后用刀抹成细泥，加入鲜汤调成糊。\r\n2.将鸡胸脯肉泥、咸鸭蛋泥、豆腐泥合在一起，加入精盐、味精、淀粉和少许鲜汤，搅成泥糊。\r\n3.锅置旺火上，放少许油烧热，下入葱花烧炸一下，随即放入一半泥糊，略炒一下。放入油和另一半泥糊，炒至熟，淋上香油即成。', '此菜色泽油亮，形为豆沙，味道鲜香。', '制作时要勤翻炒，防止炒糊，影响口味。', NULL, '豆腐100克，鸡胸脯肉35克，熟咸鸭蛋黄2个，熟油25克，湿淀粉5克，精盐2克，味精1克，葱花4克，鲜汤适量，香油少许。');
INSERT INTO `recipe` VALUES (3164, 102, '赤豆糕', '赤豆煮烂带适量的汤，拌入糯米粉、生粉、白糖、发酵粉，调成糊状。方盘内涂油，倒入面糊，刮平后上笼蒸熟，待冷却后切成小块。如果再放点糖桂花即成桂花赤豆糕。也可做成绿豆糕、枣泥糕等，这是适合夏季食用的美味点心。', NULL, NULL, NULL, '糯米粉、生粉（约为糯米粉的二分之一）、赤豆、白糖、发酵粉少许。');
INSERT INTO `recipe` VALUES (3165, 102, '比萨', '1.将番茄细丁加适量油、糖、水炒软，以适量太白粉水勾芡成番茄酱。  \r\n2.在吐司上依序放番茄酱、青豆及素火腿细丁、cheese丝做成两个比萨。\r\n3.置摄氏200度烤箱烤10分钟。', NULL, '素火腿可以煮熟鱿鱼等代替。1－3岁幼儿可将吐司四边切除。3岁以上幼儿可斟酌加青椒丝、新鲜洋菇及煮熟马铃薯细丁。', NULL, '吐司2片，番茄切成0.5公分细丁，青豆煮熟，切成细丁，cheese丝适量（比萨用），素火腿切成0.5公分细丁。');
INSERT INTO `recipe` VALUES (3166, 102, '拌三丝', '1.肉洗净，切细丝，放油锅内煸炒晾凉。\r\n2.绿豆芽去根洗净，豆腐干洗净切丝。\r\n3.锅内加水烧开，豆腐干丝放入煮开，捞出沥干放入碗中。\r\n4.将绿豆芽倒入煮过丝的锅中，待水开后迅速捞起，放入盛肉丝、干丝的碗中，加香油、酱油、精盐、白糖搅拌即成。', NULL, NULL, NULL, '绿豆芽125克，豆腐干75克，瘦肉丝50克。');
INSERT INTO `recipe` VALUES (3167, 102, '八宝粥', '1.所有材料洗净。\r\n2.除葡萄干外，放入锅里，加1－2碗水慢炖。', '适合1岁半幼儿。', NULL, NULL, '糯米1汤匙，红豆1汤匙，绿豆1汤匙，葡萄干10粒。');
INSERT INTO `recipe` VALUES (3168, 101, '煮挂面', '1.将肝、虾肉、菠菜分别切成碎片。\r\n2.将挂面煮软后切成短段，放入锅内，加入鸡汤、酱油一起煮，再将肝、虾肉、菠菜放入锅内，把鸡蛋磕开调好用1/4蛋液甩入锅内，煮熟即成。', '此面色艳、味美，含有丰富的蛋白质、碳水化合物、钙、磷、铁、锌及维生素A、D、E、C、B1、B2和尼克酸等多种婴儿发育所必需营养素。', '制作中，要将所用原料切碎，煮烂。', NULL, '挂面50克，肝10克，虾肉10克，鸡蛋1个，菠菜10克，酱油少许。');
INSERT INTO `recipe` VALUES (3169, 101, '猪肝丸子', '1.将猪肝剁成泥，葱头切碎同放一碗内，加入面包粉，鸡蛋液、淀粉拌匀成馅。\r\n2.将炒锅置于火上，放油烧热，把肝泥馅挤成丸子，下入锅内煎熟；将切碎的西红柿和番茄酱下入锅内炒至呈半糊状，倒在丸子上可即可喂食。', '此菜色泽红亮，软嫩可口。猪肝和鸡蛋除含丰富的蛋白质外，还含有丰富的铁，容易被婴幼儿吸收。经常让婴儿吃猪肝，鸡蛋，能保证婴幼儿血色素维持在正常标准。', '制作中，要把原料弄至极碎，否则做不成丸子。', NULL, '猪肝15克，面包粉15克，葱头15克，鸡蛋液15克，西红柿15克，色拉油15克，番茄酱少许，淀粉9克。');
INSERT INTO `recipe` VALUES (3170, 101, '猪肝泥', '1.将猪肝洗净，横剖开，去掉筋膜和脂肪，放在菜板上，用刀轻轻剁成泥状。\r\n2.将肝泥放入碗内，加入香油、酱油及精盐调匀，上笼蒸20－30分钟即成。', '此肝泥软烂，鲜香，含有丰富的铁和维生素A、B1、B2、B12等多种营养素。其中维生素A含量极为丰富，对防治婴儿维生素A缺乏所致的夜盲症，具有良好的作用。还含有大量的铁，能预防缺铁性贫血的发生。', '制作中，要去掉肝上的筋膜和脂肪。', NULL, '猪肝50克，香油1克，酱油、精盐各少许。');
INSERT INTO `recipe` VALUES (3171, 101, '蒸肉豆腐', '1.将豆腐洗净，放入锅内煮一下，沥去水分，研成泥摊入抹过香油的小盘内。\r\n2.将鸡肉剁成细泥，放入碗内，加入切碎的葱头、鸡蛋、酱油及淀粉，调至均匀有粘性，摊在豆腐上面，用中火蒸12分钟即成。', '此豆腐松软，稍咸，味美。豆腐含有丰富的植物蛋白质，与动物蛋白质相互补充，能对婴儿生长发育起到很好的作用。葱头能健胃杀菌，提高婴儿对疾病的防疫能力。', '要把鸡肉、葱头剁成细泥，搅至有粘性，再与豆腐同蒸。', NULL, '豆腐20克，鸡脯肉15克，葱头10克，鸡蛋8克，香油1克，酱油4克，淀粉5克。');
INSERT INTO `recipe` VALUES (3172, 101, '蒸蛋', '1.将蛋放入碗中打散，放入高汤、鸡肉末拌匀。 \r\n2.移入蒸锅中，待水沸，继续蒸20分钟。', NULL, '鸡肉末可以用清蒸鳕鱼、鲔鱼去刺代替；素食幼儿可以用素火腿、豆皮或豆腐等代替。', NULL, '蛋1个，清高汤1/2杯，鸡肉末1/2大匙。');
INSERT INTO `recipe` VALUES (3173, 101, '鱼肉糊', '1.将鱼肉切成2厘米大小的块，放入开水锅内，加入精盐煮熟。\r\n2.除去骨刺和皮，将鱼肉放入碗内研碎，再放入锅内加鱼汤煮，把淀粉用水调匀后倒入锅内，煮至糊状即成。', '此菜软烂、味鲜，含有丰富的维生素A、D和蛋白质，还含有较多的钙、磷、钾等矿物质。', '制作中，一定要把鱼刺剔净，鱼肉要煮熟，研碎。', NULL, NULL);
INSERT INTO `recipe` VALUES (3174, 101, '鱼蓉麦皮', '1.麦皮放入碗中，加入清水5汤匙，浸20分钟，用汤匙搅烂。\r\n2.若大人有蒸鱼的话，蒸熟后，选择鱼背或鱼脯的肉（因为这两部分的幼骨较少），小心拣去骨，把鱼肉放在碗中弄碎，只要一汤匙已够。\r\n3.水3/4杯或适量放入小煲内煲滚，放下麦皮煲滚，慢火煲成很烂的稀糊，放下鱼肉及极少的盐拌匀再煲片刻，即成。待温度适合时，便可喂婴儿进食。', NULL, '麦皮和鱼都含有蛋白质，麦皮更含有丰富的淀粉质及维生素B而鱼肉则有益消化。', NULL, '麦皮1汤匙半，蒸熟的鱼适量。');
INSERT INTO `recipe` VALUES (3175, 101, '鱼泥', '1.将净鱼肉放入碗内，加入适量的水、精盐、酱油，隔水蒸熟，去净鱼刺，再将鱼肉搅烂成泥。\r\n2.或将鱼洗净后，去刺，放在菜板上剁成泥状，放入碗内，加入少许精盐、酱油、香油，调匀后蒸熟即可喂食。', '鱼泥软烂、咸香，含有丰富的蛋白质、脂肪。其脂肪多为不饱和脂肪酸，具有较好的健脑作用，因此对婴脑的发育尤为有益。此外，鱼泥还含有较多的钙、磷、铁及维生素B1、B2和尼克酸等。', NULL, NULL, '净鲜鱼肉50克，香油2克，酱油、精盐各少许。');
INSERT INTO `recipe` VALUES (3176, 101, '营养蛋饼', '1.将葱头切成碎末；鱼肉煮熟，放入碗内研碎。\r\n2.将鸡蛋放入碗内，加入鱼泥，葱头未调拌匀成馅。\r\n3.把黄油放入平底锅内溶化，将馅肉团成小圆饼，放入油锅内煎炸，煎好后把番茄沙司浇在上面即成。', '此蛋饼色泽美观，软嫩鲜美，营养全面，适宜6－9个月婴儿食用。它含有婴儿生长发育需要的优质蛋白质、脂肪、钙、磷、铁、锌及维生素A、B1、B2、C、D、E等多种营养素。', '制作中，要将原料研碎，用微火将蛋饼煎熟；但要防止煎老，以免影响婴儿食用。', NULL, '鸡蛋半个，净鱼肉20克，净葱头10克，黄油6克，番茄沙司10克。');
INSERT INTO `recipe` VALUES (3177, 101, '鸭心鸡粥', '1.鸭心切开边，除去鸭心内的淤血并洗净。\r\n2.米洗净，加入浸过米面的清水浸1小时（米浸软能加速煲烂）。\r\n3.把3/4杯或适量的水放入小煲内煲滚。放入米及浸米的水，鸭心也放入煲内煲滚，慢火煲成稀糊，取出鸭心（不要），放下极少的盐调味。待温度适合时，便可喂婴儿。', NULL, '也可以把鸭心剁至极烂，待粥快煲成，然后将鸭心倒入粥内搅匀煲熟。鸡心燥热，所以煲粥多是用鸭心，而且鸭心不肥腻。婴儿的粥必须煲得很烂才可喂食。如米烂水己干，要加水烫。如需节省时间，可将米用搅拌机磨碎后煲，否则难消化。', NULL, '米2汤匙，鸭心3个。');
INSERT INTO `recipe` VALUES (3178, 101, '小鱼蛋花粥', '1.将小孩用的饭碗装1/2碗饭，与水1杯放入小锅煮至烂。 \r\n2.取小鱼10g，小煮一下，把打散的蛋撒在上面搅拌即可。', NULL, NULL, NULL, '饭1/2碗，水1杯，小鱼10g，蛋1/2个。');
INSERT INTO `recipe` VALUES (3179, 101, '香蕉泥', '1.将香蕉洗净，剥皮去白丝。\r\n2.把香蕉切成小块，放入搅拌机中，加入白糖，滴几滴柠檬汁，搅成均匀的香蕉泥，倒入小碗内，即可喂食。', '香蕉泥含有丰富的碳水化合物、蛋白质，还含有丰富的钾、钙、磷、铁及维生素A原、B1和C等。具有润肠、通便的作用，对便秘的婴儿有辅助治疗作用。', '制作中，要选用熟透的香蕉，洗干净；生香蕉有涩味，不能给婴儿喂食。', NULL, '熟透的香蕉1根，白糖、柠檬汁各少许。');
INSERT INTO `recipe` VALUES (3180, 101, '香蕉蜂蜜牛奶', '1.将香蕉、橙子去皮，与蜂蜜一起放入搅拌机里搅拌，待搅拌至稠粘状时，立即将热奶冲入，再搅和几秒钟。\r\n2.将奶汁倒入奶瓶内，温度合适后即可喂食。', '此奶香甜适口，含有丰富的蛋白质、碳水化合物和维生素A、C等多种营养素，对婴儿发育十分有益。', '制作中，香蕉、橙子一定要搅至稠粘糊状，不能带一点颗粒。', NULL, '牛奶200克，香蕉半根，橙子半个，蜂蜜10克。');
INSERT INTO `recipe` VALUES (3181, 101, '苋菜水', '1.将苋菜洗净，切丝。\r\n2.锅置火上。放水烧沸，倒入菜丝，加入精盐煮5－6分钟，离火后，再焖10分钟，滤去菜渣留汤即成。', '此水味鲜，略咸。苋菜水的铁、钙含量极为丰富，能供给婴儿较多的铁和钙，是缺铁性贫血患儿的良好辅助治疗食品。', '制作此水，菜要煮烂，菜渣要滤净。', NULL, '苋菜100克，精盐少许，水100克。');
INSERT INTO `recipe` VALUES (3182, 101, '鲜虾肉泥', '1.将鲜虾肉洗净，放入碗内，加水少许，上笼蒸熟。\r\n2.加入适量精盐、香油搅匀即成。', '虾泥软烂、鲜香，含有丰富的蛋白质、脂肪，其中含有多种人体必需的氨基酸及不饱和脂肪酸，是婴儿极佳的健脑食品。此外，还含有钙、磷、铁及维生素A、B1、B2和尼克酸营养素。', '制作中，要将虾皮剥净，虾肉要蒸熟、蒸烂。', NULL, '鲜虾肉（河虾、海虾均可）50克。香油1克，精盐适量。');
INSERT INTO `recipe` VALUES (3183, 101, '鲜柠檬汁', '1.将柠檬洗净去皮，压榨取汁，加入白糖即可饮用。\r\n2.亦可将柠檬洗净，在沸水中浸渍15分钟后，切成薄片，放入经煮消毒过的玻璃瓶内，放一层柠檬片，其上铺一层白糖，浸渍1周后即可用来泡开水喂婴儿。', '此汁酸甜适口，含有丰富的维生素A、B、C及柠檬酸，能增加食欲，帮助消化吸收，适宜于婴儿食用。', '制作此汁要选择新鲜的柠檬作原料，炊具要清洁。', NULL, '鲜柠檬100克，白糖65克。');
INSERT INTO `recipe` VALUES (3184, 101, '鲜橘汁', '将鲜橘子洗净，切开成两半，放在挤果汁器上压出橘汁，加入温开水和蔗糖即成。', '此汁色泽金黄，酸甜适口，含有丰富的葡萄糖、果糖、蔗糖、苹果酸、柠檬酸以及胡萝卜素、维生素B1和B2、尼克酸、维生素C等。特别是维生素C含量丰富，婴儿饮用对身体十分有益。', '制作时，要特别注意炊具的卫生。', NULL, '鲜橘子适量，白糖、温开水各适量。');
INSERT INTO `recipe` VALUES (3185, 101, '鲜红薯泥', '1.将红薯洗净，去皮。\r\n2.把去皮红薯切碎捣烂，放入锅内，盖上锅盖，煮15分钟左右，至烂熟，加入白糖少许，稍煮即可喂食。', '此薯泥软烂，香甜，含有丰富的碳水化合物及维生素C，还含有钙、磷、铁、锌和维生素B1、B2及尼克酸等多种营养素，适宜6个月婴儿食用。', '制作中，要去净红薯皮。', NULL, '鲜红薯50克，白糖少许。');
INSERT INTO `recipe` VALUES (3186, 101, '鲜果布甸', '1.苹果、雪梨去皮、去心，洗净切片。未煮前浸于清水中，以免变锈色。 \r\n2.米洗净，加入清水约1/4杯浸半小时，倒入煲内煲滚。慢火煲10分钟，加入水果煮至水果熟，约需五分钟，熄火待冷。\r\n3.把煲内的米、水及水果放入搅拌机内搅烂，倒回煲内煮成糊状，加入适量的蜜糖或葡萄糖，待温度适合时，便可喂食，也可以冰冻后进食。', '此食谱可作婴儿的辅助食品，也可作为幼儿和儿童的下午菜小点。煮成后盛于精致的小杯内冷藏，待他午睡醒后，母亲陪伴着他，让他自己拿着小匙进食，这样可以增加进食的兴趣，又可借此培养他的自信心和独立能力。若他午睡后情绪好的话，晚餐也会特别开胃哩！', NULL, NULL, '苹果1个，雪梨或啤梨（草莓）1个，米2汤匙半。蜜糖或葡萄糖适量。');
INSERT INTO `recipe` VALUES (3187, 101, '鲜菠菜水', '1.将菠菜洗净，切成丝。\r\n2.把水烧沸，放入菠菜，加入精盐煮5－6分钟，离水，再焖10分钟，倒出汤汁，即可喂食。', '此菜水颜色碧绿，略有咸味，含丰富的钙、磷、铁、胡萝卜素及维生素和补血的铁质。制作此菜水，菠菜要煮烂，口不宜咸。', NULL, NULL, '鲜菠菜叶100克，白糖、开水各适量。');
INSERT INTO `recipe` VALUES (3188, 101, '虾末菜花', '1.将菜花洗净，放入开水中煮软后切碎。\r\n2.把虾放入开水中煮后剥去皮，切碎，加入白酱油、精盐煮，使其具有淡咸味，倒在菜花上即可食用。', '此菜细嫩，味甘鲜美，食后容易消化。菜花营养丰富，含有蛋白质、脂肪、糖及较多的维生素A、B、C和较丰富的钙、磷、铁等矿物质。尤以维生素C含量最多，是同量大白菜含量的4倍，番茄含量的8倍，芹菜含量的15倍，苹果含量的20倍以上。人体摄入足量的维生素C后，不但能增强肝脏的解毒能力，促进生长发育，而且有提高机体免疫力的作用，能够防止感冒、坏血病等的发生。', '制作中，要把虾皮剥净，菜花、虾肉要切碎煮烂，以适应婴儿的咀嚼能力。', NULL, '菜花30克，虾10克，白酱油、精盐各少许。');
INSERT INTO `recipe` VALUES (3189, 101, '西红柿汁', '1.将西红柿洗净，用开水烫后剥去皮，用榨汁机或消毒纱布把汁挤出。\r\n2.将白糖放入汁中，用温开水冲调后即可饮用。', '此汁色泽鲜红，酸甜适口，含有丰富的胡萝卜素、维生素B1、B2、C，其各种维生素的含量比苹果、梨、香蕉等高2－4倍。它还含有占其总重量0.6%的各种矿物质，其中以钙、磷较多，锌、铁次之；此外，还有锰、铜、碘等重要微量元素。这些矿物质，对婴幼儿生长发育特别有益。', '制作此汁要注意炊具卫生，西红柿要去皮挤汁。', NULL, '新鲜西红柿适量，白糖、开水各适量。');
INSERT INTO `recipe` VALUES (3190, 101, '西红柿鱼', '1.将收拾好的鱼放入开水锅内煮后，除去骨刺和皮；西红柿用开水烫一下，剥去皮，切成碎末。\r\n2.将鸡汤倒入锅内，加入鱼肉同煮，稍煮后，加入西红柿末、精盐，用小火煮成糊状即成。', '此菜色泽红艳，叶鲜软烂，含有丰富的蛋白质、钙、磷、铁和维生素C、B1、B2及胡萝卜素等多种营养素，适宜5个月以上婴儿食用。', '要选用新鲜的鱼做原料，剔净骨刺，把鱼肉煮烂给婴儿喂食。', NULL, '净鱼肉100克，西红柿70克，精盐2克，鸡汤200克。');
INSERT INTO `recipe` VALUES (3191, 101, '西瓜汁', '1.将西瓜瓤放入碗内，用匙捣烂，再用纱布过滤。\r\n2.汁内加入白糖，调匀即成。', NULL, NULL, NULL, '西瓜瓤100克，白糖10克。');
INSERT INTO `recipe` VALUES (3192, 101, '糖水樱桃', '1.将樱桃洗净，切去把，掏去核，放入锅内，加入绵白糖及水50克，用小火煮15分钟左右，煮烂备用。\r\n2.将锅中樱桃搅烂，倒入小杯中，晾凉后喂食。', '此食品酸甜适口，色泽鲜红，含有丰富的铁、钙、胡萝卜素和维生素B、C等多种营养素。含铁量比苹果、橘子高20倍以上，居各种水果之首，含胡萝卜素也比苹果、橘子高4－5倍。因樱桃是一种净化水果，一般婴儿都爱吃。', '制作中，要将樱桃核洗净，樱桃要煮烂。', NULL, '熟透的樱桃100克，绵白糖15克。');
INSERT INTO `recipe` VALUES (3193, 101, '水果藕粉', '1.将藕粉加适量水调匀；苹果去皮，切成极细末。\r\n2.将锅置火上，加水烧开，倒入调匀的藕粉，用微火慢慢熬煮，边熬边搅动，熬至透明为止，最后加入切碎的苹果，稍煮即成。', '此羹味香甜，易于消化吸收，含有丰富的碳水化合物、钙、磷、铁和维生素，营养价值极高，是婴儿良好的健身食品。', '制作中，要把水果洗净切碎，最好用小勺刮成泥，以利婴儿消化吸收。', NULL, '藕粉50克，苹果（桃、杨梅、香蕉均可）75克，清水250克。');
INSERT INTO `recipe` VALUES (3194, 101, '水果麦片粥', '1.将干麦片用清水300克泡软；水果洗净切碎。\r\n2.将泡好的麦片连水倒入锅内，置火上烧开，煮2-3分钟后，加入牛奶，再煮5－6分钟，等麦片酥烂，稀稠适度，加入切碎的水果、白糖略煮一下，盛入碗内即成。', '此粥软烂适口，果香味浓，含有婴儿发育所需的蛋白质、脂肪、碳水化合物、钙、磷、铁、锌和维生素A、B1、B2、C及尼克酸等多种营养素。', '制作中，粥要煮稀稠适度，水果下入锅内稍煮一下，再给婴儿喂食。', NULL, '麦片100克，牛奶50克，水果50克，白糖少许。');
INSERT INTO `recipe` VALUES (3195, 101, '水果拌豆腐', '1.将豆腐加水煮后，沥去水分。\r\n2.把草莓用盐水洗净后切碎，并把橘瓣剥去皮、去核、研碎，再与蜂蜜和精盐混合，加入豆腐中均匀混合，即可喂食。', '此菜色泽美观，味美适口，含有丰富的蛋白质、碳水化合物、钙、铁、锌及维生素B1、B2、C等多种营养素.', '制作时要把橘子、草莓切碎，不能有较大的块。另外，还要使用含维生素C丰富的水果，作拌豆腐的辅料。', NULL, '嫩豆腐20克，草莓1个，橘子3瓣，蜂蜜、精盐各少许。');
INSERT INTO `recipe` VALUES (3196, 101, '薯茸', '1.马铃薯削去皮，洗净切薄片。\r\n2.当大人的饭煲至大滚时，把马铃薯放在饭面煮，饭熟马铃薯粘，将马铃薯搓成薯茸。\r\n3.大人的汤适合婴儿食用的，除去汤面的油及渣汤。\r\n4.薯蓉放入小煲内，加入适量的汤拌匀，可以放入极少量的盐煮成糊状。待温度适合时便可喂婴儿。', NULL, '婴儿初进食半流质或软质的食物，不能操之过急，每次由少量开始，逐渐增加分量，所以无需专为婴儿烹煮食物。依照以上方法煮，既方便又简单。如大人的汤不适合婴儿肠胃的话，可以用牛奶拌煮。马铃薯最好选购超级市场内小个的，因为这种马铃薯多数是不出芽的。大个的马铃薯多数己出了芽，出了芽的马铃薯有一种对人体有害的物质，即“龙葵素”，所以不能给婴儿吃。', NULL, '马铃薯1个，肉汤或鱼汤适量。');
INSERT INTO `recipe` VALUES (3197, 101, '蔬菜泥', '1.将绿色蔬菜嫩叶部分煮熟后，磨碎、过滤。 \r\n2.取（1）中10－20克及加少许水至锅中，边搅边煮。 \r\n3.快好时，加入牛奶以及由1/5－1/4小匙玉米粉用一倍水调好的玉米粉水，继续加热搅拌煮成泥状即可。', NULL, NULL, NULL, '绿色蔬菜（也可用土豆、南瓜、菠菜等代替）10g、牛奶两汤匙、玉米粉1/5－1/4小匙。');
INSERT INTO `recipe` VALUES (3198, 101, '什锦猪肉菜末', '1.将猪肉、西红柿、胡萝卜，葱头、柿子椒分别切成碎末。\r\n2.将猪肉末、胡萝卜末，葱头末、柿子椒末一起放入锅内，加肉汤煮软，再加入西红柿末略煮，加入少许精盐，使其具有淡淡的咸味。', '此菜色泽鲜艳，味美可口，无论在什么季节，都应尽可能给婴儿食用新鲜蔬菜。因为新鲜蔬菜最富有营养，味美且经济。罐装蔬菜已经被烹调过，只需要做成菜泥即可食用。如果可能的话，罐头里的菜汁也应食用，因为菜汁里含有很多营养物质。', '制作中，要将所用主料全部切碎、煮软，才能给婴儿喂食。', NULL, '猪肉15克，西红柿，胡萝卜，葱头、柿子椒各7.5克，精盐、肉汤各适量。');
INSERT INTO `recipe` VALUES (3199, 101, '什锦菜面', '1.将面条切段，依幼儿的能力来决定面条的细切程度。\r\n2.放一碗水于锅中，将青菜和蕃茄丢入煮，待煮烂时，将豆腐、鲔鱼、面条加入一起煮。', NULL, '面条的细切程度得依幼儿的能力，绝不可以把食物放到嘴里咀嚼后才喂。', NULL, '豆腐1/4块，鲔鱼罐头1汤匙，青菜切碎1/2汤匙，蕃茄1片，熟面条1/2碗');
INSERT INTO `recipe` VALUES (3200, 101, '山楂水', '1.将山楂片用凉水快速洗净，除去浮灰，放入杯内，浇入开水，加盖焖上。\r\n2.待水温下降到微温时，加入白糖，搅至溶化即可饮用。', '此汁色泽鲜红，酸甜适口，对增进婴儿食欲大有益处，而且具有很高的营养和医疗价值。每100克鲜山楂果肉中，含维生素C89毫克（在水果中仅次于鲜枣、猕猴桃而居第三位）；含钙85毫克，在鲜水果中名列前茅，因此特别适合婴儿、孕妇对钙质的需求。此外还含有胡萝卜素、碳水化合物及铁、尼克酸等多种营养素。还能促进消化液的分泌，增进食欲，帮助消化。', '制作的要把山楂片洗净，用开水沏。', NULL, '山楂片100克，白糖20克，开水300克。');
INSERT INTO `recipe` VALUES (3201, 101, '三色肝末', '1.将猪肝切碎；葱头剥去外皮切碎；胡萝卜洗净切碎；西红柿用开水烫一下，剥去皮切碎；菠菜清洗干净，用开水烫一下，切碎备用。\r\n2.将切碎的猪肝、葱头、胡萝卜放入锅内，加入肉汤煮熟，最后加入西红柿、菠菜、精盐煮片刻即成。', '此菜色泽美观，鲜软适口，含有丰富的蛋白质、钙、磷、铁、锌及维生素A、胡萝卜素B1、B2、B12、C和尼克酸等多种营养素。', '菜、肝下锅不要煸炒，必须加汤煮；西红柿，菠菜不宜上锅过早，此菜适宜7个月以上婴儿食用。', NULL, '猪肝25克，葱头、胡萝卜、西红柿、菠菜各10克，精盐2克，肉汤适量。');
INSERT INTO `recipe` VALUES (3202, 101, '肉末豆花', '1.土白菜洗净切细。 \r\n2.肉末加水煮到熟。 \r\n3.起锅前加入小白菜。 \r\n4.豆花置在碗里。 \r\n5.加入菜肉汤。', NULL, NULL, NULL, '豆花1/2碗，肉末1汤匙，土白菜2汤匙。');
INSERT INTO `recipe` VALUES (3203, 101, '肉末菜粥', '1.将米淘洗干净，放入锅内，加入水500克，用旺火烧开后，转微火熬成粥。\r\n2.将青菜切碎，然后将油倒入锅内，下入肉末炒散，加入葱、姜末、酱油、精盐炒匀，放入切碎的青菜炒几下，加入米粥内，尝好味，同熬煮一下即成。', '此粥粘稠，肉末香，咸淡适口，含有丰富的蛋白质、脂肪、碳水化合物、钙、磷、铁、锌及维生素B1、B2、E、C和尼克酸等多种营养素，适宜9个月婴儿食用。', '熬粥不要放碱，以免破坏营养。肉末煸炒一下再与粥同煮。', NULL, '大米（或小米）50克，肉末30克，青菜50克，植物油10克，酱油5克，精盐2克，葱姜未少许。');
INSERT INTO `recipe` VALUES (3204, 101, '清水煮荷包蛋', '1.将小锅内加入250克水，倒入醋，将水烧开。把鸡蛋磕入一个杯子内。\r\n2.待水开后，使开水保持微开而不太翻滚时，将鸡蛋徐徐倒入水内，煮至蛋清凝固、蛋黄呈溏黄时，捞入小盘内，稍晾即可喂食。', '此蛋软，易于消化，含有丰富的蛋白质，脂肪，并含有除维生素C以外的几乎所有其他维生素和矿物质。鸡蛋的蛋白质与人体蛋白质组成极为相近，是所有食物蛋白质中生理价值最高的。它是婴儿最为理想的蛋白质食品。', '制作中，不要加盐，食盐会妨碍蛋清凝固。', NULL, '鲜鸡蛋1个，醋少许。');
INSERT INTO `recipe` VALUES (3205, 101, '青菜粥', '1.将青菜（菠菜、油菜、小白菜的叶）洗净，放入开水锅内煮软，切碎备用。\r\n2.将大米洗净，用水泡1－2小时，放入锅内，煮30－40分钟，在停火之前加入精盐及切碎的青菜，再煮10分钟即成。', '此粥粘稠适口，含有婴儿发育需要的蛋白质、碳水化合物、钙、磷、铁和维生素C、E等多种营养素。', '制作中，粥要煮烂，菜要煮软切碎。', NULL, '大米300克，青菜30克，清水300克，精盐少许。');
INSERT INTO `recipe` VALUES (3206, 101, '葡萄干土豆泥', '1.将葡萄干用温水泡软切碎；土豆洗净，蒸熟去皮，趁热做成土豆泥。\r\n2.将炒锅置火上，加水少许，放入土豆泥及葡萄干，用微火煮，熟时加入蜂蜜调匀，即可喂食。', '此食品质软、稍甜。葡萄干含铁极为丰富，是婴幼儿和体弱贫血者的滋补佳品。', '制作时，土豆要蒸熟后再制成泥，葡萄干要用温水泡软切碎，然后再上火煮制调味。', NULL, '土豆50克，葡萄干8克，蜂蜜少许。');
INSERT INTO `recipe` VALUES (3207, 101, '苹果色拉', '1.将苹果洗净，去皮后切碎；橘瓣去皮、核、切碎；葡萄干温水泡软后切碎。\r\n2.将苹果、橘子、葡萄干放入小碗内，加入酸奶酪和蜂蜜，拌匀即可喂食。', '此色拉色美、酸甜，含有丰富的蛋白质、碳水化合物、维生素C、钙、磷；另外，维生素A、B1、B2和尼克酸、铁等的含量也较高。具有助消化，健脾胃之功效，尤适宜消化不良的婴儿食用。', '制作中，要把原料切碎，块不宜大，以适应婴儿的咀嚼能力。', NULL, '苹果20克，橘子2瓣，葡萄干5克，酸奶酪、蜂蜜各5克。');
INSERT INTO `recipe` VALUES (3208, 101, '苹果泥', '1.将苹果洗净、去皮，然后用刮子或匙慢慢刮成泥状，即可喂食。\r\n2.或者将苹果洗净、去皮，切成黄豆大小的碎丁，加入凉开水适量，上笼蒸20－30分钟，待稍后即可喂食。', '苹果泥含有丰富的矿物质和多种维生素。婴儿吃苹果可补充钙、磷，预防佝偻病，还具有健脾胃、补气血的功效，对婴儿的缺铁性贫血有较好的防治作用，对脾虚消化不良的婴儿也较为适宜。', '制作时要去掉皮，刮子或匙要洗净消毒，再给婴儿刮泥喂食。', NULL, '苹果100克，凉开水适量。');
INSERT INTO `recipe` VALUES (3209, 101, '苹果金团', '1.将红薯洗净、去皮、切碎煮软。\r\n2.把苹果去皮除籽后切碎，煮软，与红薯均匀混合即可喂食。', '此果团软烂、香甜，含有丰富的碳水化合物、蛋白质、钙、磷及多种维生素，尤以胡萝卜素含量极为丰富。它是一种生理碱性食品，能与肉、蛋、米、面所产生的酸性物质中和，调节人体的酸碱平稳，对维持婴幼儿身体健康十分有益。', '制作中，要把红薯、苹果切碎、煮软，再给婴儿喂食。', NULL, '红薯50克，苹果50克。');
INSERT INTO `recipe` VALUES (3210, 101, '苹果酱', '1.将苹果洗净，去皮除籽，切成极薄的片，放入锅内，加入糖及清水50克，煮成糊状。\r\n2.停火后，用勺子背面将煮烂的苹果研碎即可喂食。', '苹果酱细软、酸甜，营养极为丰富。婴幼儿吃苹果可补充钙、磷、预防佝偻病。', '制作中，要将苹果煮烂、研碎，再给婴儿喂食。', NULL, '苹果半个（约50克），白糖少许。');
INSERT INTO `recipe` VALUES (3211, 101, '牛奶粥', '1.将大米淘洗干净，用水泡1－2小时。\r\n2.将锅置火上，放水烧开，下入大米用微火煮30分钟，加入牛奶再煮片刻即成。', '此粥粘稠，味美可口，含有丰富的蛋白质，脂肪，碳水化合物、钙、磷、铁和维生素A、B1、B2及尼克酸等多种营养素。牛奶是良好的钙源，此外还含有磷、铁等矿物质。食用牛奶基本上能满足婴儿对各种营养素的需要。', '加牛奶以后，不要煮的时间太长。', NULL, '牛奶100克、大米50克，水400克。');
INSERT INTO `recipe` VALUES (3212, 101, '牛奶玉米粥', '1.将牛奶倒入锅内，加入精盐和碎肉豆蔻，用文火煮开，撒入玉米粉，用文火再煮3－5分钟，并用勺不停搅拌，直至变稠。\r\n2.将粥倒入碗内，加入黄油和鲜奶油，搅匀，晾凉后喂食。', '此粥粘稠，味美适口，含有丰富的优质蛋白质、脂肪、碳水化合物、钙、磷、铁及维生素A、B1、B2、D和尼克酸等。', '用牛奶加玉米粉熬粥，不宜用旺火熬。粥盛入碗内后，再加入黄油和奶油搅匀。此粥适于8个月以上的婴儿食用。', NULL, '牛奶250克，玉米粉50克，鲜奶油10克，黄油5克，精盐2克，肉豆蔻少许。');
INSERT INTO `recipe` VALUES (3213, 101, '牛奶香蕉糊', '1.将香蕉洗净，去皮后用勺子研碎。\r\n2.将牛奶倒入锅内，加入玉米面和白糖，边煮边搅均匀，煮好后倒入研碎的香蕉中调匀即可食用。', '此糊香甜适口，奶香味浓，含有丰富的蛋白质、碳水化合物、钙、磷、铁、锌及维生素C等多种营养素。碳水化合物的成分主要是淀粉和糖类，故味甜，极宜婴儿食用。', '制作中，要把牛奶、玉米面煮熟，然后倒入研碎的香蕉中搅匀。', NULL, '熟透的香蕉20克，牛奶30克，玉米面5克，白糖5克。');
INSERT INTO `recipe` VALUES (3214, 101, '牛奶蜂蜜饼干', '1.将面粉放入盆内，加入发酵粉混合，把黄油放入蜂蜜中搅开，加入牛奶，倒入面粉或粘面团。\r\n2.将面团擀成0.5厘米厚的片，切成正方形，用叉子扎成孔，刷上牛奶，放入烤盘内，在250度炉温下烤18分钟，呈焦黄色即成。', '此点心焦黄、酥脆。出牙期的孩子喜欢在各种硬的饼干或烤馒头片上磨牙，适当给孩子吃些磨牙食品，可以帮助婴儿练习咀嚼，利用牙齿的生长发育。', '制作中，要掌握好用料比例及炉温。', NULL, '普通面粉200克，黄油25克，牛奶25克，蜂蜜25克，发酵粉4克。');
INSERT INTO `recipe` VALUES (3215, 101, '牛奶蛋', '1.将鸡蛋的蛋白与蛋黄分开，把蛋白调至起泡待用。\r\n2.在锅内加入牛奶、蛋黄和白糖，混合均匀用微火煮一会儿，再用勺子一勺一勺把调好的蛋白放入牛奶蛋黄锅内稍煮即成。', '此品蓬松柔软，是婴儿最佳的午后食品，含有丰富的蛋白质、钙、磷、铁、及维生素A、B2、D等多种营养素。', '制作中，蛋黄、蛋白要分开，牛奶用微火煮一会儿，再下入蛋白同煮。', NULL, '鸡蛋1个，牛奶1杯，白糖10克。');
INSERT INTO `recipe` VALUES (3216, 101, '嫩肉丸子', '1.绞肉剁细，入葱末豆粉1/2小匙、盐1/8小匙料拌均，甩打至有弹性，再分搓成一口大小之丸状；以中火蒸1小时至肉软。 \r\n2.以水1小匙、豆粉1/2小匙、酱油少许勾芡即可。', NULL, NULL, NULL, '绞肉60g，葱末1小匙，豆粉1/2小匙、盐1/8小匙，水1小匙、豆粉1/2小匙、酱油少许。');
INSERT INTO `recipe` VALUES (3217, 101, '面糊糊汤', '1.将牛奶倒入锅内，用微火煮开，撒入面粉，调匀，加入少许精盐和碎肉豆蔻，再煮一下，并不停地搅和。\r\n2.加入黄油，装入大孔嘴的奶瓶中，晾凉后喂食。', '此汤味美适口，含有丰富的蛋白质、脂肪、碳水化合物、钙、磷、锌及维生素A、B、C、D等多种营养素。', '制作中，牛奶不等煮至滚开，就要撒入面粉，防止面粉调不匀，有小疙瘩。', NULL, '牛奶250克，面粉10克，黄油5克，精盐、肉豆蔻各少许。');
INSERT INTO `recipe` VALUES (3218, 101, '面包干', '1.将面包切成1厘米厚的薄片。\r\n2.将面包片放在烤盘上，入炉烘烤，烤呈金黄色，即可出炉，冷却后食用。', '此品易消化，耐嚼，可锻炼幼儿的咀嚼能力，促进唾液分泌，适合作幼儿磨牙食品。含有丰富的碳水化合物，能增加幼儿的热量供给。', '面包片可用烤箱烤，也可用平底锅烙烤，但炉温不宜过热，否则面包片烤不焦脆。', NULL, '面包（或馒头）适量。');
INSERT INTO `recipe` VALUES (3219, 101, '面包布丁', '1.将鸡蛋磕入碗内调匀；面包切成小块后与蜂蜜、牛奶均匀混合。\r\n2.在碗内涂上色拉油，再把上述混合物倒入碗内，放入蒸锅内，用中火蒸7－8分钟即成。', '此布丁软嫩滑爽，含有丰富的蛋白质、脂肪、碳水化合物及维生素A、B、D、E等多种维生素和钙、磷、锌等营养素，很适宜婴儿食用。', '制作中，要用中火蒸，火不宜过大。', NULL, '面包15克，鸡蛋半个，牛奶125克，蜂蜜5克，色拉油少许。');
INSERT INTO `recipe` VALUES (3220, 101, '蜜制胡萝卜', '1.将胡萝卜切成小碎片。\r\n2.将胡萝卜片、蜂蜜、黄油、姜末及少许开水放入锅内搅拌均匀，盖下盖，文火煮30分钟至胡萝卜变软。煮的过程中偶尔搅拌一下，出锅即可喂食。', '此菜红艳，甜软，营养丰富，婴儿食用极佳。含有丰富的维生素A，胡萝卜素含量相当于土豆的360倍，芹菜的36倍，苹果的45倍，柑橘的23倍。此外还含有较丰富的碳水化合物、蛋白质、钙铁及维生素B1、B2、C等。', '制作中，要把胡萝卜切碎煮烂。吃剩下的胡萝卜，可以装瓶入冰箱内，随吃随取。', NULL, '净胡萝卜200克，蜂蜜25克，黄油15克，姜末2克。');
INSERT INTO `recipe` VALUES (3221, 101, '米粉粥', '1.将牛奶、精盐放入一小钢精锅中，待牛奶刚要开时放入米粉，边放边搅。把火关小，盖上锅盖，用微火煮8－10分钟。\r\n2.吃时再放入黄油、水果及孩子喜欢吃的其它食品。', '此粥粘稠、香甜，含有婴儿所需的蛋白质、脂肪、碳水化合物、钙、磷、铁、锌及维生素A、D等多种营养素。', '米粉下锅时要边煮边搅拌，防止糊锅，把火关小后，要用微火煮。', NULL, '牛奶500克，米粉125克，精盐、黄油、水果各少许。');
INSERT INTO `recipe` VALUES (3222, 101, '两色蛋', '1.将煮熟的鸡蛋剥去壳，把蛋白与蛋黄分别研碎，用白糖和精盐分别拌匀。\r\n2.将蛋白放入小盘内，蛋黄放在蛋白上面，放入笼内，用中火蒸7-8分钟，浇入胡萝卜酱即可喂食。', '此蛋色泽美观，柔软可口。鸡蛋是婴儿发育不可缺少的主要副食品之一，蛋黄易消化，营养价值高，蛋白是高蛋白，还含有一定的铁质，配以胡萝卜酱，增加了维生素及胡萝卜素的供给。', '要将蛋黄、蛋白研碎，上火蒸一下，不可直接给婴儿喂食。', NULL, '鸡蛋1个，胡萝卜酱10克，白糖、精盐各少许。');
INSERT INTO `recipe` VALUES (3223, 101, '腊肠西红柿', '1.将西红柿用开水烫后剥去皮，去籽切碎；腊肠切碎。\r\n2.将锅置火上，放入肉汤，下入西红柿、腊肠，边煮边搅拌，并用勺子背将其研成糊状，加少许精盐使其具有淡淡的咸味，即可喂食。', '此菜软烂，含有丰富的蛋白质、脂肪、钙、磷、铁、锌及维生素C、B1、B2和胡萝卜素、尼克酸等多种营养素，对婴儿生长发育特别有益。', '制作中，要将西红柿皮去掉，腊肠切碎，口味不宜咸。', NULL, '西红柿20克，腊肠10克，肉汤、精盐各少许。');
INSERT INTO `recipe` VALUES (3224, 101, '节爪鸡肉粥仔', '1.节瓜去皮洗净。\r\n2.鸡胸肉剁至极烂。\r\n3.米洗净，加入浸过米面的清水浸1小时（米浸软能加速煲烂）。 \r\n4.把3/4杯或适量的水，放入小煲内煲滚。放入米及浸米的水，节瓜也放入煲内煲滚，慢火煲成稀糊。取起节瓜压成蓉放回粥内，鸡肉也放入，煲成稀糊，加入极少的盐调味。待温度适合时，便可喂婴儿。', NULL, '鸡肉蒸熟剁烂，放入粥内不会结成团（生剁的鸡肉则不易分散），喂给婴儿较安全。节瓜要削去青色皮层，只要白肉，目的是容易煲烂。', NULL, '米2汤匙，熟鸡胸肉1小块，节瓜2片。');
INSERT INTO `recipe` VALUES (3225, 101, '煎西红柿', '1.将面包粉放入平底锅内，烤成焦黄色；西红柿用开水烫一下，剥去皮，切成薄片。\r\n2.将色拉油放入平底锅内烧热，放入西红柿煎至两面焦黄，盛入小盘内，撒上面包粉、芹菜末即成。', '此菜色泽美观，十分可口，能诱发婴儿食欲。含有丰富的钙、磷、铁、锌、锰、铜、碘等重要微量元素，这些矿物质，对婴儿生长发育特别有益。此外，还含有丰富的维生素C、B族维生素、胡萝卜素等。', '制作中，把西红柿放入油锅内煎至两面焦黄时，再将面包粉和芹菜末撒在上面。', NULL, '西红柿1/4个（约25克），面包粉10克，熟芹菜末少许，色拉油8克。');
INSERT INTO `recipe` VALUES (3226, 101, '鸡肉粥', '1.将大米淘洗干净，放入锅内，加入清水用旺火煮开。\r\n2.将锅置火上，放入油，下入鸡肉末炒散，加入葱姜末、酱油搅匀，倒入米粥锅内，加入精盐尝好味，用微火煮几分钟即成。', '此粥粘稠、味香，含有丰富的蛋白质、碳水化合物、钙、磷、铁、B族维生素等多种营养素，适宜7－8个月婴儿食用。', '粥要熬至发粘，鸡肉末煸入味后，再与粥同煮。', NULL, '大米50克，鸡肉末30克，植物油10克，酱油6克，精盐2克，葱姜末少许，清水500克。');
INSERT INTO `recipe` VALUES (3227, 101, '鸡肝糊', '1.将鸡肝放入水中煮，除去血后再换水煮10分钟，取出剥去鸡肝外皮，将肝放入碗内研碎。\r\n2.将鸡架汤放入锅内，加入研碎的鸡肝，煮成糊状，加入少许酱油，搅匀即成。', '此菜呈糊状，含有丰富的蛋白质、钙、铁、锌及维生素A、B1、B2和尼克酸等多种营养素。尤以维生素A、铁含量较高，可防治贫血和维生素A缺乏症。', '鸡肝要研碎，煮成糊状给婴儿喂食。', NULL, '鸡肝15克，鸡架汤15克，酱油少许。');
INSERT INTO `recipe` VALUES (3228, 101, '鸡蛋面条', '1.将锅置火上，放入黄油熬至溶化，下入葱头略炒片刻，再放入面条、肉汤和精盐一起煮。\r\n2.将鸡蛋调匀后倒入锅内，与面条混合均匀盛入碗内，上笼蒸5分钟，把西红柿放在面条上即成。', '此面条色美、味好，含有丰富的蛋白质、脂肪、碳水化合物，还含有一定量的钙、磷、锌等矿物质及维生素A、D、E、C、B1、B2和尼克酸等，能为婴儿提供机体所需的充足的热量，是婴儿较佳的一种营养食品。', '制作中，要将面条、葱头、西红柿等切碎，煮烂。', NULL, '煮烂切碎的细面条50克，切碎的葱头10克，切碎的西红柿5克，鸡蛋半只，黄油、肉汤、精盐少许。');
INSERT INTO `recipe` VALUES (3229, 101, '火腿土豆泥', '1.将土豆去皮洗净，切成小块，放入锅内，加入适量的水煮烂，用汤匙捣成泥状。\r\n2.将火腿去皮，去肥肉，切碎。\r\n3.把土豆泥盛入小盘内，加入火腿末和黄油，搅拌均匀后用小勺喂食。', '此菜色美，味好，含有丰富的蛋白质、脂肪、碳水化合物、钙、磷、铁、钾及维生素A、B、C等多种营养素，适宜7－9月婴儿食用。', '土豆要煮熟煮透，去皮后再制成泥，火腿要切成极细的末，再给婴儿喂食。', NULL, '土豆泥100克，熟瘦火腿10克，黄油2克。');
INSERT INTO `recipe` VALUES (3230, 101, '黄豆茸粥', '1.当煮大人饭时，放了米及水在煲内，用汤匙在中心挖一洞，使中心的米多些水，煮成饭后，中心的米便成软饭。\r\n2.把四汤匙软饭或视婴儿食量而定搓烂（饭的分量，只是配合黄豆的分量，黄豆不宜过多）。 \r\n3.黄豆放在筛内，用汤匙搓成茸。筛放在小煲上，倒下约2/3杯的黄豆汤，将豆茸冲落煲内，在筛内的豆壳不要（即是隔茶叶那种筛，但筛眼要大些）。\r\n4.将烂饭也放入煲内搅匀煲滚，用慢火煲成稀糊，放入极少的盐调味。待温度适合时，便可喂婴儿。', NULL, '大人煲黄豆排骨汤时，可因利乘便，依法泡制黄豆蓉粥。', NULL, '软饭4汤匙，煲粘的黄豆1汤匙，黄豆排骨汤适量（除去汤面的油）。');
INSERT INTO `recipe` VALUES (3231, 101, '花色饭', '将西式火腿、胡萝卜切成丁，鸡蛋打散调味蒸熟后切成丁。大米煮成饭。胡萝卜丁、豌豆粒下油锅炒熟，加入西式火腿丁、鸡蛋丁略炒，最后再倒入米饭，放少许盐、味精调味拌匀即可食用。也可用洋葱、蘑菇、青豆、胡萝卜、西式火腿、鸡蛋等炒成西式炒饭；用虾仁、鲜贝、豌豆等炒成海鲜饭。', NULL, NULL, NULL, '大米、西式火腿、胡萝卜、鸡蛋、豌豆等。');
INSERT INTO `recipe` VALUES (3232, 101, '花豆腐', '1.将豆腐煮一下，放入碗内研碎；青菜叶洗净，用开水烫一下，切碎也放入碗内，加入淀粉、精盐、葱姜水搅拌均匀。\r\n2.将豆腐做成方块形，再把蛋黄研碎撒一层在豆腐表面，放入蒸锅中用中火蒸10分钟即可喂食。', '此豆腐形色美观，软嫩可口，含有丰富的蛋白质、脂肪、碳水化合物及维生素B1、B2、C和钙、磷、铁等矿物质。豆腐柔软，易被消化吸收，能参与人体组织构造，促进婴儿生长，是老少皆宜的高营养廉价食品。鸡蛋黄含丰富的铁质，对提高婴儿血色素极为有益。', '制作时，口味不宜过咸，以利婴儿食用。', NULL, '豆腐50克，青菜叶10克、熟鸡蛋黄1个，淀粉10克，精盐、葱姜水各少许。');
INSERT INTO `recipe` VALUES (3233, 101, '胡萝卜泥', '1.将胡萝卜擦碎，苹果去皮切碎。\r\n2.将胡萝卜放入开水中煮1分钟，研碎，然后放入锅内用微火煮，并加入切碎的苹果，煮烂后，调匀即可。', '胡萝卜泥含有丰富的胡萝卜素。其含量为土豆的360倍，芹菜的36倍，苹果的45倍。还含有碳水化合物、钙、铁及维生素C、B1、B2等多种营养素。', '制作时，胡萝卜、苹果要煮烂，方可给婴儿喂食。', NULL, '胡萝卜75克，苹果50克。');
INSERT INTO `recipe` VALUES (3234, 101, '红枣泥', '1.将红枣洗净，放入锅内，加入清水煮15－20分钟，至烂熟。\r\n2.去掉红枣皮、核，调匀即可喂食。', '红枣泥含有丰富的钙、磷、铁，还含有蛋白质、脂肪、碳水化合物及多种维生素，具有健脾胃、补气血的功效，对婴儿缺铁性贫血有较好的防治作用，对脾虚消化不良的婴儿也较为适宜。', '制作中，一定要把红枣煮烂，去净皮，核。', NULL, '红枣100克。');
INSERT INTO `recipe` VALUES (3235, 101, '红烧排骨', '1.葱切段；姜切片；排骨剁成4厘米长的块，洗净控干水分，加入少许酱油、水淀粉拌匀，用热油炸成金黄色捞出。\r\n2.将排骨放入锅内，加入水（以漫过排骨为度）、酱油、料酒、精盐、大料、葱段、姜片，尝好味，用大火烧开后，转微火焖至排骨肉烂即成。', '味道香咸，排骨酥烂，色泽金红。', '排骨挂上淀粉后，要用旺火热油炸，排骨要多分几次下锅，这样才炸得好。', NULL, '猪排骨1.5公斤，酱油200克，精盐10克，料酒8克，葱30克，姜15克，大料5克，水淀粉80克，植物油2公斤（实耗250克）。');
INSERT INTO `recipe` VALUES (3236, 101, '红萝卜水', '1.红萝卜去皮，洗净切片；蜜枣洗净。\r\n2.把适量的水煲滚，放入蜜枣、红萝卜，煲滚后慢火再煲1小时便出味，隔去渣。\r\n3.把红萝卜水装入暖水壶内保温，用以冲奶粉或米糊。红萝卜已有少许甜味，加糖或不加随意。', NULL, '婴儿4个月大，可尝试喝些稀米糊，让他学习吞咽半流质食物。母亲应注意抱婴儿的姿势，将婴儿放在膝上，手弯托着婴儿的颈，令其背部抬高些，这样在吞咽食物时会容易些。用小茶匙盛少许米糊放在婴儿舌上，让婴儿慢慢吞。初期婴儿只吃一两口，作为母亲的也应该很满足，不要强迫他吃。', NULL, '红萝卜1只或适量，蜜枣2粒。');
INSERT INTO `recipe` VALUES (3237, 101, '红萝卜牛肉粥仔', '1.米洗净，加入浸过米面的清水浸1小时（米浸软能加速煲烂）。\r\n2.将红萝卜压成茸。\r\n3.红萝卜牛肉汤除去汤面的油，放入小煲内煲滚。放下米及浸米的水煲滚，慢火煲成稀糊，加入红萝卜茸搅匀再煲片刻，加入极少的盐调味。待温度适合时，便可喂婴儿。', NULL, '大人煲红萝卜牛肉汤，可以留汤用来煲粥。虽然红萝卜煲过汤，但仍有营养的，同时也可让婴儿吸收纤维质', NULL, '米2汤匙，红萝卜牛肉汤约3/4杯，煲粘的红萝卜1－2片。');
INSERT INTO `recipe` VALUES (3238, 101, '红根腐竹', '1.胡萝卜洗净去皮，切成5厘米长、0.5厘米宽方条。腐竹冷水浸透，洗净、切成胡萝卜同样大小的条。\r\n2.香油放炒锅烧热，下花椒炸焦黄捞出，投入葱、姜丝，加清水半碗，随即放精盐、白糖、味精、腐竹和胡萝卜，开锅后小火焖至汤汁收尽，离火晾凉即成。', '富含植物蛋白、铁、钙、及胡萝卜素等营养素，色香味美。', NULL, NULL, '水发腐竹50克，胡萝卜75克，香油15克，葱、姜各1克，花椒1克，白糖1克，味精0.5克，精盐2克。');
INSERT INTO `recipe` VALUES (3239, 101, '汉堡牛肉饼', '1.取1深盘，放入奶油及洋葱未，用微波炉以强微波3分钟炒香后，拌入面包粉备用。 \r\n2.牛绞肉加入蛋拌匀，再加入炒香的洋葱未料及调味料，充分搅拌均匀后，分制成6个肉饼。 \r\n3.烤锅入沙拉油3大匙，先以强微波预热5分钟，放入牛肉饼，用强微波煎2分钟后，取出翻面，再以强微波2分钟即可。', NULL, NULL, NULL, '牛绞肉4OO克、洋葱末1杯、奶油3大匙、面包粉1杯、蛋1个，盐1小匙、胡椒粉1/4小匙。');
INSERT INTO `recipe` VALUES (3240, 101, '翡翠泥', '1.将鲜蚕豆剥去老、嫩皮，放入锅内煮烂、捞出，用冷水过凉，放菜板上，砸成泥状放入碗内。\r\n2.将京糕切成绿豆大小的丁。\r\n3.炒锅置火上，放入油，加入白糖、蚕豆泥、桂花，用中火推炒，炒透后盛入盘内，撒上京糕丁即成。', '鲜蚕豆砸成泥，加入白糖，与京糕一起呈红绿两色，鲜美清香，色香味兼备。具有增进食欲、帮助消化，清热利尿的作用，是婴儿春季较为适宜的食品之一。要选择鲜蚕豆作原料，并要煮熟、炒透，给婴儿喂食。', NULL, NULL, '鲜蚕豆50克，京糕25克，白糖15克，花生油5克，桂花少许。');
INSERT INTO `recipe` VALUES (3241, 101, '番茄牛肉汤', '1.里脊边肉洗净，切小块，先氽烫过，再冲净泡沫，加水6杯及酒适量，姜2片先煮40分钟使其熟软，然后挑除。\r\n2.番茄用热水烫过，剥除外皮后切片，放入牛肉汤中，再煮十分钟，加入调料。\r\n3.最后加入烫熟过的青豆仁，再煮开即熄火，盛出食用。', NULL, '里脊边肉质地较嫩、油层较薄，较适合小宝宝咀嚼。番茄选红一点儿的比较不酸，去皮切片再煮比较易烂。青豆仁先煮熟，冲凉再用，可保持脆绿；若用超市的冷冻青豆仁则可直接加入。两岁以上小宝宝食用。配色漂亮，可同时吸收肉类和蔬菜的营养，并且简单易做。', NULL, '牛里脊边肉0.5斤、红番茄2个、姜2片、青豆仁2两，酒、盐、糖、淀粉适量。');
INSERT INTO `recipe` VALUES (3242, 101, '豆腐泥', '1.取去皮红萝卜1/2条及豌豆5g分别烫过后切成几小块并取水1/2杯放入小锅。 \r\n2.嫩豆腐1/6个边捣碎边加进去，煮到汤汁变少，最后加1/2个打散的蛋放在锅里即可。', NULL, NULL, NULL, '去皮红萝卜1/2条、豌豆5g、水1/2杯、嫩豆腐1/6个、鸡蛋1/2个。');
INSERT INTO `recipe` VALUES (3243, 101, '豆腐糊', '将豆腐放水锅内，加入少量肉汤，边煮边用勺子研碎，煮好后放入碗内，研至光滑即可喂食。', '此菜味美可口，蛋白质含量丰富，质地优良，既易于消化吸收、参与人体组织的构造，又能促进婴儿生长。此菜还含较丰富的脂肪、碳水化合物及维生素C、B1、B2和钙、镁等矿物质。', '煮时要注意，蛋白质如果凝固就不好消化，所以煮的时间要适度。', NULL, '豆腐20克，肉汤适量。');
INSERT INTO `recipe` VALUES (3244, 101, '豆豉牛肉末', '1.将炒锅置火上，放入植物油，下入牛肉末煸炒片刻，再下入碎豆豉、鸡汤和酱油，搅拌均匀即成。\r\n2.此菜在给婴儿喂稠粥或烂面时添加。', '此菜味鲜香，略咸。牛肉含蛋白质较高，是婴儿生长发育所必需的营养品之一；豆豉含有丰富的钙、磷、铁、锌、维生素E，对婴儿发育十分有益。', '制作中，豆豉一定要切碎，菜要烧烂，咸味不宜太重，淡淡的就可以了。', NULL, '碎豆豉、牛肉末各15克，植物油5克，酱油3克，鸡汤10克。');
INSERT INTO `recipe` VALUES (3245, 101, '蛋黄粥仔', '1.当煮大人饭时，放了米及水在煲内，用汤匙在中心挖一洞，使中心的米多些水，煮成饭后，中心的米便成软饭，把适量的软饭搓成糊状。\r\n2.把适量的汤隔去渣，如鱼汤要特别小心以防有幼骨。除去汤面的油。\r\n3.把汤及饭糊放入小煲内煲滚，用慢火煲成稀糊状，放入极少量的盐调味，然后放下1/4只熟鸡蛋黄（要搓成茸）搅匀煮滚，盛入碗中。待温度适合时，便可喂婴儿。', NULL, '这个月龄的婴儿，只宜学习吞咽半流质的食物，让他知道奶以外的“味”，训练他接受食物的习惯。因此他不会吃得多，故用此方法煲粥仔较快捷方便。大人的汤不适宜婴儿食用的话，可以用牛奶或水煮成稀糊，必须煮成极烂的稀糊，否则，婴儿难消化。试验食物的热度方法：母亲可以将少许粥仔放在自己的手背。便可感受温度的程度了。', NULL, '米适量，肉汤（或鱼汤、菜汤）适量。');
INSERT INTO `recipe` VALUES (3246, 101, '蛋黄粥', '1.将大米淘洗干净，放入锅内，加入清水，用旺火煮开，转微火熬至粘稠。\r\n2.将蛋黄放入碗内，研碎后加入粥锅内，同煮几分钟即成。', '此粥粘稠，有浓醇的米香味，富含婴儿发育所必需的铁质，适宜6个月婴儿食用。', '制作中，米要煮烂，熬至糊稠，蛋黄研碎后再放入粥内同煮。', NULL, '大米50克，蛋黄1个，清水500克。');
INSERT INTO `recipe` VALUES (3247, 101, '蛋黄泥', '1.将鸡蛋洗净，放入锅内煮熟，剥去蛋壳，除去蛋白，取其蛋黄，加入精盐，开水少许，用匙搅拌即成。\r\n2.也可将蛋黄泥用牛奶、米汤、菜水等（略加一点盐或糖）调成糊状，即可喂食。', '此菜软烂适度，微咸，营养丰富，给婴儿喂食既可营养大脑，又可满足婴儿对铁质的需要。', '要选择鲜蛋做原料，煮时要凉水下锅，这样不易煮坏。煮好后要立即用凉水浸泡一下，这样易去皮。', NULL, '鸡蛋1个，精盐、开水各少许。');
INSERT INTO `recipe` VALUES (3248, 101, '大米粥', '1.将大米淘洗干净，加水及少许精盐煮成稠米粥。\r\n2.粥中加入黄油搅拌均匀，盛入碗内喂食。', '此粥粘稠，味咸香，含有丰富的优质蛋白质、脂肪、碳水化合物、钙、磷、铁、锌及维生A、D、E、B1、B2和尼克酸等。可以在米粥中加炼乳、鲜奶油、调味菜末、肉末、蛋黄、红糖等。适宜9个月婴儿食用。', NULL, NULL, '大米50克，黄油7.5克，精盐少许。');
INSERT INTO `recipe` VALUES (3249, 101, '粗粉糕', '1.将牛奶倒入锅内，用微火煮开，趁热撒入面粉，煮10－15分钟，并不停搅拌。\r\n2.熄火后，加入黄油，并使劲搅拌，趁热用小勺喂食。', '此糕软烂适口，含有婴儿必需的蛋白质、脂肪、碳水化合物和维生素A、D、B1、B2、C，还含有钙、磷、铁、锌等多种营养素。', '制作中，牛奶烧开撒入面粉后要不停搅拌，防止糊锅。', NULL, '牛奶250克，小麦粗粒粉（或玉米粗粒粉）40克，黄油5克。');
INSERT INTO `recipe` VALUES (3250, 101, '草莓麦片粥', '1.将水放入锅内烧开，下入麦片煮2-3分钟。\r\n2.把草莓用勺子背研碎，再加入少许蜂蜜均匀混合，然后放入麦片锅内，边煮边混合，煮片刻即成。', '此粥色美，酥烂，稀稠适度，含有丰富的蛋白质、碳水化合物、钙、磷、铁、及维生素B1、B2、C等多种营养素。草莓用勺子背研碎后，再与麦片混合煮。也可用牛奶代替麦片。', NULL, NULL, '麦片50克，草莓3克。蜂蜜少许。');
INSERT INTO `recipe` VALUES (3251, 101, '百玉金银汤', '1.清汤70g，煮开时，倒入小鸡丁块15g，1/3朵香菇丝煮热。\r\n2.豆腐25g，切丁。倒入1内，以盐，酱油各少许调味勾芡，煮成稠状。\r\n3.青花菜10g，烫熟，倒入2内。淋入蛋汁1/4个量，熄火。盖上锅盖闷到蛋熟。', NULL, NULL, NULL, '清汤70g、小鸡丁块15g、香菇1/3朵、豆腐25g、青花菜10g、蛋汁1/4个。');
INSERT INTO `recipe` VALUES (3252, 101, '白薯泥', '1.将白薯去皮，洗净，切成小块，放入锅内，加水及少许盐煮30分钟。\r\n2.沥去水分，放入搅拌机内（或用勺挤压碎），加入热牛奶，黄油和几滴柠檬汁，搅成泥，倒入盘内喂食。', '此薯泥软烂、甜食，含有丰富的碳水化合物、钙、磷、铁，还含有胡萝卜素和维生素A、D、B1、B2、C等。', '制作中，要把白薯去皮、煮烂，要选用新鲜的白薯作原料。由于白薯味甜，一般婴儿都爱吃。', NULL, '白薯100克，柠檬1/4个，牛奶100克，黄油5克，精盐少许。');
INSERT INTO `recipe` VALUES (3253, 63, '八宝饭', '瓜干、青梅、核桃仁切成小丁，山楂糕、猪板油切条，水500克烧开放白糖75克，糯米小火焖至米粒不夹生后加熟猪油拌匀，大碗底部抹猪油，板油条、山楂糕分八瓣摆放，瓣之间摆别的原料，摆图案，将糯米抹平，上笼蒸40分钟取出翻扣在盘里，水150克，放白糖100克化开湿淀粉勾芡浇在八宝饭上即可。', NULL, NULL, NULL, '糯米250克，核桃仁、瓜干、葡萄干、山楂糕、青梅各15克，糖玫瑰10克，白糖150克，甜杏仁、芝麻各5克，猪板油50克。');
INSERT INTO `recipe` VALUES (3254, 63, '白菜水饺', '1.猪肉剔净筋膜，洗净，绞成肉末。 \r\n2.白菜洗净，剁成白菜末再挤去水分。 \r\n3.猪肉末放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量），搅拌均匀，再加入白菜末，植物油、香油搅拌。搅拌均匀成馅料。 \r\n4.饺子粉加入精盐5克，再加清水20摄氏度（200克左右）拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克，6－8个小面剂，擀成中间稍厚的圆形面皮，打入馅料，对折捏成饺子生坯。 \r\n5.锅置旺火上，清水烧沸后下入饺子生坯，用手勺轻轻推动至坯饺全', NULL, NULL, NULL, '饺子粉500克，白菜500克，猪肉250克，葱花50克，鲜姜末10克，酱油30克，香油10克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3255, 63, '春饼卷菜', '将圆白菜、洋葱、火腿、荸荠切片，用沸水将圆白菜、洋葱焯一下捞出，然后放入火腿、香油、醋、盐拌匀，将面粉用水调匀烙成薄面饼。将拌匀的调料放在薄面饼上卷起即可。', NULL, NULL, NULL, '面粉100克，水30克，圆白菜、火腿各50克，荸荠25克，洋葱25克。');
INSERT INTO `recipe` VALUES (3256, 63, '春卷', '将肉、菜切成丝在油锅中煸炒一下，加调料勾芡即成馅心，将馅心包入春卷皮放在油锅中炸至金黄色即可。', NULL, NULL, NULL, '春卷皮60张，瘦猪肉250克，卷心菜1250克，花生油1500克。');
INSERT INTO `recipe` VALUES (3257, 63, '葱油饼', '将面粉、水拌匀，揉成面团，搓成长条擀成圆片，刷上油，将葱花、精盐、油、花椒粉拌和，均匀的撒在圆片上，卷好，擀圆上平底锅烙至金黄色即可。', NULL, NULL, NULL, '面粉500克，葱花100克，油300克，花椒粉2克，精盐5克。');
INSERT INTO `recipe` VALUES (3258, 63, '担担面', '将冬菜切成细末，分装5个碗，再把其他调料均匀的分装在每个碗中，将面条煮熟装碗即可。', NULL, NULL, NULL, '圆细面条500克，辣椒油、酱油、葱各50克，芝麻酱35克，川冬菜100克。');
INSERT INTO `recipe` VALUES (3259, 63, '蛋饼', '1.面粉加水揉成面团，饧约30分钟，切块，再擀成薄圆形。\r\n2.葱切末，蛋打散，加料及葱末拌匀。 　　\r\n3.平底锅热油一大匙，先将面皮煎成半熟盛起，倒入2的蛋汁至锅内，盖上面皮，再煎烤一下即可。', NULL, NULL, NULL, '面粉250克，蛋4个，葱2根，水半杯，盐半茶匙，鸡粉半小匙。');
INSERT INTO `recipe` VALUES (3260, 63, '地瓜粥', '1.地瓜去皮洗净切块。\r\n2.米洗净，浸泡30分钟，加入地瓜同煮即可。', NULL, NULL, NULL, '米1杯，地瓜2条，水6杯。');
INSERT INTO `recipe` VALUES (3261, 63, '干贝皮蛋粥', '1.米洗净，浸泡约30分钟，加高汤熬煮。\r\n2.小干贝洗净泡软，放入粥内同煮，皮蛋切碎，葱切末。\r\n3.皮蛋放入煮好的粥内，加料调味，并撒上葱花、香菜即可。', NULL, NULL, NULL, '米l杯，小干贝150克，皮蛋2个，葱1根，香菜少许，高汤7杯，盐、鸡粉各半茶匙，胡椒粉、麻油少许。');
INSERT INTO `recipe` VALUES (3262, 63, '荷叶饭', '将大米加猪油和水蒸熟，晾凉拌入精盐、胡椒粉。将猪肉切丁加油、精盐、料酒、胡椒粉炒熟，加淀粉拌成馅，然后将饭和馅拌在一起，荷叶包起来蒸熟即可。', NULL, NULL, NULL, '大米500克，猪油100克，猪瘦肉200克，虾仁150克，荷叶50克。');
INSERT INTO `recipe` VALUES (3263, 63, '红烧牛肉面', '1.肉洗净切块，烫去血水。\r\n2.起油锅，炒香蒜、姜、葱段、八角、辣椒、牛肉，加（2）料，用中火煮约1小时。\r\n3.水烧开，煮熟面条捞出，再烫熟小白菜。\r\n4.碗中放入（3）料、葱花、牛肉汤，加入面条、青菜、牛肉即可。', NULL, NULL, NULL, '牛肉500克，面条500克，小白菜250克，水10杯，葱4根，姜2片，大蒜8粒，八角4粒，辣椒1根；酱油1杯，水6杯，酒l汤匙，辣豆瓣酱1大匙；麻油少许。');
INSERT INTO `recipe` VALUES (3264, 63, '红油抄手', '将肉末与精盐、料酒拌匀用皮包成抄手。在碗里放好调料，将抄手煮熟装入碗里即可。', NULL, NULL, NULL, '抄手皮20张，肉末50克。');
INSERT INTO `recipe` VALUES (3265, 63, '红枣百合粥', '1.糯米洗净，浸泡30分钟，加水熬煮。　　 \r\n2.百合、红枣泡水后加入粥内同煮。\r\n3.待粥将烂熟时，加入冰糖即可。', NULL, NULL, NULL, '糯米1杯，红枣100克，百合50克，冰糖300克。');
INSERT INTO `recipe` VALUES (3266, 63, '滑蛋牛肉粥', '1.米洗净，浸泡约30分钟，加高烫熬煮。\r\n2.牛肉切片，拌a料腌l0分钟，葱切末。\r\n3.粥快煮好时，放入肉片烫熟，再淋入蛋汁，加b料调味后，即可享用。', NULL, NULL, NULL, '米l杯，牛肉3两，蛋2个，葱2根，高汤7杯，酱油半匙，酒、淀粉适量，盐l茶匙，鸡粉半茶匙，胡椒粉、香油各少许。');
INSERT INTO `recipe` VALUES (3267, 63, '火腿玉米粥', '1.米洗净，浸泡约30分钟，加入高汤熬煮。\r\n2.火腿切丁，芹菜切末。\r\n3.粥内倒入火腿丁、玉米粒同煮约10分钟，加a料调味，食用时加b料、芹菜末、香菜即可。', NULL, NULL, NULL, '米l杯、火腿150克、玉米粒l罐、芹菜l根、香菜少许、高汤6杯。a料：盐、鸡粉各半茶匙。b料：胡椒粉、麻油各少许。');
INSERT INTO `recipe` VALUES (3268, 63, '鸡茸馄饨', '1.将鸡胸肉剁成肉茸，海米浸泡10分钟后洗净剁成细末。\r\n2.将肉茸、海米、蛋、胡椒粉及少量盐放在一起搅匀，包成馄饨。', NULL, NULL, NULL, '鸡胸肉2两，馄饨皮4两，豆苗4两，海米1大勺，香油半勺，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3269, 63, '鸡丝粥', '1.米洗净，泡约30分钟，加入高汤熬煮。\r\n2.鸡胸肉切丝，以a料腌10分钟。胡萝卜切丝，葱切末。\r\n3.起油锅，炒熟鸡丝、胡萝卜丝，加b料后,倒入粥内食用时撒上葱末。', NULL, NULL, NULL, '米1杯、鸡胸肉半个、胡萝卜半根、葱2根、高汤7杯。A料：酱油1大匙，淀粉1茶匙，水少许。B料：盐1小匙，鸡粉半茶匙，胡椒粉、香油少许。');
INSERT INTO `recipe` VALUES (3270, 63, '家常饼', '将面粉揉好擀成薄皮，在薄皮上抹上油和盐，再卷起来擀成圆饼。在饼的两面刷油放入平底锅烙至两面金黄色即可。', NULL, NULL, NULL, '面粉500克，油60克，盐3克。');
INSERT INTO `recipe` VALUES (3271, 63, '赖汤圆', '用糯米粉包好汤圆，把茶放入杯中冲入100克开水泡2分钟，将茶叶滗掉，再加开水200克泡好。将汤圆煮熟，捞出放入碗中，取适量茶汁浇入即可。', NULL, NULL, NULL, '汤圆馅100克，糯米粉250克，浙江上等龙井茶25克。');
INSERT INTO `recipe` VALUES (3272, 63, '萝卜丝饼', '1.白萝卜切丝，加入开水中煮软捞出，沥干。虾米泡软，切碎，葱、姜切碎。\r\n2.锅中热3大匙油，放入白萝卜丝加盐、五香粉炒匀，沥干备用。\r\n3.面粉加猪油和水一起揉成面团，切成小块，每一小块面团中包入2制作的馅后再用手压平成饼状。\r\n4.平底锅热油2大匙，放入3煎成金黄色即可盛盘。', NULL, NULL, NULL, '白萝卜500克，面粉500克，虾米50克，猪油l杯，葱2根，姜2片，盐1小匙，五香粉l小匙。');
INSERT INTO `recipe` VALUES (3273, 63, '麻辣面', '1.辣椒、葱切末。 　　　　\r\n2.将a料、辣椒末加高汤拌匀即为酱料。 　　　\r\n3.面煮熟，捞出，置于盘中，加上酱料、葱末即可。', NULL, NULL, NULL, '面条250克，辣椒3根，葱2根，高汤半杯。A料：辣椒酱3大匙，辣油l大匙，盐少许。');
INSERT INTO `recipe` VALUES (3274, 63, '麻油米线', '1.米线用水浸洗，煮时再捞出。 \r\n2.用中火热麻油，煎荷包蛋1个盛起，接着把姜片放入锅内炒出香味后，倒进清汤、盐煮开，再放下米线和荷包蛋，续煮至锅开即可。', '麻油米线是福州地方名点，十分滋补活血。', NULL, NULL, '米线10厘米长1束，老姜数片，盐半勺，黑麻油1勺半，清水半碗，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3275, 63, '美味炒米粉', '1.米粉、香菇、海米用水泡软后，香菇切丝，米粉沥干水分。\r\n2.用热油爆香香菇、海米，加入肉丝翻炒，再加大白菜丝、胡萝卜丝同炒，倒蒜末、芹菜末及半杯水，汤汁稍滚放入米粉翻炒至熟。', NULL, NULL, NULL, '米粉、肉丝、香菇、海米、大白菜丝、胡萝卜丝、蒜末和芹菜末。　');
INSERT INTO `recipe` VALUES (3276, 63, '牛肉炒面', '1.牛肉切片，加a料腌10分钟。\r\n2.葱斜切片，姜切片，芥兰菜切段。\r\n3.面先煮熟、沥干。\r\n4.加热5大匙油，快炒牛肉片，盛起。\r\n5.起油锅，爆炒葱、姜、芥兰，再加入面条、少许水拌炒，接着加入b料及牛肉炒匀，起锅前淋上c料即可。', NULL, NULL, NULL, '面条250克，牛肉250克，芥兰菜3棵，葱2根，姜l小块。A料：酱油l大匙、淀粉l小匙、油1大匙、水少许。B料：酱油1茶匙、鸡粉、盐少许。C料：麻油半茶匙。');
INSERT INTO `recipe` VALUES (3277, 63, '排骨面', '1.排骨洗净去，两面拍松，用a料腌约15分钟后沾裹地瓜粉。\r\n2.油烧热，将排骨用中火炸至表面呈金黄后转大火捞出。\r\n3.面煮熟，青菜亦烫熟捞出，置于碗内。\r\n4.面碗内加入葱花、高汤，再加上排骨即可。', NULL, NULL, NULL, '面条500克，大排骨5片，青菜250克，水l0杯，葱2根，大蒜2瓣，淀粉1杯。A料：酱油1杯，糖、酒1大匙，盐1小匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3278, 63, '芹菜水饺', '1.猪肉剔净筋膜，洗净，绞成肉末。\r\n2.芹菜摘洗干净，去净叶片，取成菜茎。\r\n3.芹菜放入沸水略烫，捞入冷水过凉捞出，剁成碎末，挤出水分。猪肉末放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量），搅拌均匀，再加入芹菜末、植物油、香油搅拌。搅拌均匀成馅料。\r\n4.饺子粉加入精盐5g，再加清水200克左右拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克,6－8个小面剂，擀成中间稍厚的圆形面皮，打入馅料，对折捏成饺子生坯。\r\n5.锅置旺火上，清水烧沸后', NULL, NULL, NULL, '饺子粉500克，芹菜500克，猪肉250克（瘦肉125克，肥肉125克），葱花50克，鲜姜末10克，酱油30克，植物油50克，香油10克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3279, 63, '青椒水饺', '1.猪肉剔净筋膜，洗净，绞成肉末。\r\n2.青椒去净菜籽，洗净。\r\n3.青椒放入沸水略烫，捞入冷水中过凉捞出，剁成碎末，挤去水分。\r\n4.猪肉末放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量）搅拌均匀，再加入青椒末、植物油、香油馅料。搅拌均匀成馅料。\r\n5.饺子粉加入精盐5g，再加清水200克左右拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克,6－8个小面剂，擀成中间稍厚的圆形面皮，打入馅料，对折捏成饺子生坯。 \r\n6.锅置旺火上，清水烧沸后下入', NULL, NULL, NULL, '饺子粉500克，青椒500克，猪肉250克，葱花30克，鲜姜末10克，酱油50克，香油10克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3280, 63, '三色凉面', '1.蛋打散，煎成2片蛋皮。青椒、豆芽菜水烫备用。 　　　\r\n2.小黄瓜、蛋皮、洋火腿、青椒均切丝。 　　　\r\n3.芝麻酱先加高汤调匀，再加入（1）料拌匀，即成酱料。\r\n4.盘中放面，上面摆饰洋火腿、小黄瓜、蛋皮、青椒、豆芽、淋上酱料即可。', NULL, NULL, NULL, '油面250克，蛋1个，洋火腿3片，青椒半个，小黄瓜1条，豆芽菜150克，高汤半杯，芝麻酱3大匙。（1）料：糖、麻油2大匙，盐半小匙，酱油、乌醋3大匙，蒜泥1小匙。');
INSERT INTO `recipe` VALUES (3281, 63, '三鲜水饺', '1.猪肉剔净筋膜，洗净，绞成肉馅。\r\n2.虾仁（泡发的海米）挑去沙线，洗净，切成小丁。\r\n3.海参洗净，切成小丁。\r\n4.韭菜去掉老叶，洗净，切成菜末。\r\n5.猪肉放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量），搅拌均匀后加入虾仁（海米）、加入韭菜末、香油搅拌均匀后，再依据口味调好味，即制成三鲜馅料。\r\n6.饺子粉加入精盐5克，再加清水20升（200克左右）拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克,6－8个小面剂，擀成中间稍厚的圆形面皮', NULL, NULL, NULL, '饺子粉500克,猪肉300克（瘦肉200克，肥肉100克），虾仁（海米）100克，水发海参50克，韭菜100克，葱花30克，鲜姜末10克，酱油50克，香油50克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3282, 63, '烧烤乳酪煎饼', '1.牛乳加热至大约70度，倒入放有面粉的器皿中，加以均匀搅拌。\r\n2.把切碎的青菜、盐、酱油及打散的蛋，放入1中，使之均匀搅拌，然后分成4等分，搓成圆形。\r\n3.将奶油放入锅中加热，把2放入，用锅铲翻烤两面，直到两面变成棕色即可。', NULL, NULL, NULL, '面粉1/4杯，牛奶2/3杯，生乳酪1杯，蛋1只，青菜切碎末1大匙，盐1/4小匙，酱油少量，沙拉油及奶油各1大匙，红色小樱桃2个，生菜适量。');
INSERT INTO `recipe` VALUES (3283, 63, '烧卖', '面粉揉好，猪肉剁成茸，加菜馅和调料拌好，将面团擀成荷叶花边形，包上馅上笼蒸熟即可。', NULL, NULL, NULL, '面粉100克，菜馅800克，猪肉600克。');
INSERT INTO `recipe` VALUES (3284, 63, '什锦烩饭', '鸡肉等原料均切丁，油锅中爆炒胡萝卜，泡萝卜、四季豆后取出，爆炒鸡丁转色后放入炸红薯炒几下，与其它原料混炒，高汤烧开放酱油、精盐、料酒、胡椒粉，倒入炒饭即可。', NULL, NULL, NULL, '鸡腿肉，炸红薯150克，胡萝卜、高汤各100克，泡萝卜40克，四季豆30克，大米饭400克。');
INSERT INTO `recipe` VALUES (3285, 63, '四鲜水饺', '1.猪肉剔净筋膜，洗净，绞成肉馅。 \r\n2.虾仁（泡发的海米）挑去沙线，洗净，切成小丁。 \r\n3.海参洗净，切成小丁。 \r\n4.干贝丁洗净，切成小丁。 \r\n5.韭菜去掉老叶，洗净，切成菜末。 \r\n6.猪肉放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量），搅拌均匀后加入虾仁（海米）、干贝丁、韭菜末和香油，搅拌均匀后，再依据口味调好味，即制成四鲜馅料。 \r\n7.饺子粉加入精盐5g，再加清水20升（200克左右）拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每', NULL, NULL, NULL, '饺子粉500克，猪肉300克(瘦肉200克，肥肉100克），干贝丁50克，虾仁（海米）100克，水发海参50克，韭菜100克，葱花30克，鲜姜末10克，酱油50克，香油50克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3286, 63, '酸菜水饺', '1.猪肉剔净筋膜，洗净，绞成肉末。\r\n2.酸菜洗净，剁成末，挤出水分。\r\n3.猪肉末放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量）搅拌均匀，再加入酸菜末、植物油、香油搅拌。搅拌均匀成馅料。\r\n4.饺子粉加入精盐5g，再加清水20升（200克左右）拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克，6－8个小面剂，擀成中间稍厚的圆形面皮，打入馅料，对折捏成饺子生坯。\r\n5.锅置旺火上，清水烧沸后下入饺子生坯，用手勺轻轻推动至坯饺全部上浮水面。水沸腾', NULL, NULL, NULL, '饺子粉500克，酸菜350克，猪肉300克，葱花50克，鲜姜末10克，酱油30克，植物油50克，香油10克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3287, 63, '天津包子', '将猪肉绞成肉茸，加调料拌好，将面粉与酵母一起放入盆中，加清水和成面团使之发酵，然后兑碱面揉匀，擀成薄皮，将馅包入，上笼蒸熟即可。', NULL, NULL, NULL, '猪肉1000克，香油150克，面粉1500克，酵母15克，碱面12克，酱油200克。');
INSERT INTO `recipe` VALUES (3288, 63, '虾仁炒面', '面条煮熟捞出，将海参、鸡肉、虾仁切片下锅煸炒加葱末、姜末、酱油、花椒水、味精及鲜汤再把面条倒入拌炒，至水分干即可。', NULL, NULL, NULL, '面条500克，熟鸡肉、虾仁各50克，水发海参25克，鲜汤150克。');
INSERT INTO `recipe` VALUES (3289, 63, '鲜虾奶油意大利面', '1.虾仁去泥肠、大蒜、西洋芹切碎。 　　　　 \r\n2.锅烧热，放奶油、蒜末炒香， 加入虾仁炒至虾仁变色后，加入a料煮至汤汁收干。\r\n3.烧半锅水放少许盐，将意大利面煮熟后捞出。 \r\n4.煮熟的面条上淋上虾仁酱汁，并撒些起司粉、芹菜末即可。', NULL, NULL, NULL, '意大利面半包，虾仁200克，大蒜3瓣，奶油半杯，西洋芹半根。A料：白酒半杯，番茄酱半杯，盐半茶匙，胡椒粉少许。B料：起司粉3大匙。');
INSERT INTO `recipe` VALUES (3290, 63, '蟹黄灌汤包', '将肉馅加调料拌好，将面粉揉好擀成薄皮，将馅及蟹黄包入薄皮，再加入肉皮冻，捏成鲫鱼嘴状，上小笼蒸熟即可。', NULL, NULL, NULL, '面粉1000克，猪五花肉700克，肉皮冻280克，蟹肉160克，蟹黄40克。');
INSERT INTO `recipe` VALUES (3291, 63, '扬州炒饭', '将鸡蛋打碎炒熟切碎，火腿、黄瓜、虾切小丁，油锅中放入火腿、豌豆、虾拌炒，然后加米饭、鸡蛋、黄瓜、虾，豌豆、味精、精盐略炒，然后装盘即可。', NULL, NULL, NULL, '米饭250克，火腿30克，鸡蛋1个，豌豆20克，黄瓜、虾各25克，油30克。');
INSERT INTO `recipe` VALUES (3292, 63, '羊肉什菜水饺', '1.羊肉剔净筋膜，洗净，绞成肉末，猪肉洗净，剁成肉末。 \r\n2.时令蔬菜摘好洗净，剁成菜末，挤出水分。 \r\n3.肉末放入盆中，加入葱花、姜末、精盐、酱油、花椒粉、味精、清汤（少量），搅拌均匀，再加入菜末、植物油、香油搅拌，搅拌均匀成馅料。 \r\n4.饺子粉加入精盐5克，再加清水20升（200克左右）拌好，和成面团，搓匀揉造，盖上湿布饧面10分钟，在面板上再稍楺几下，搓成长条，揪成每个50克，6－8个小面剂，擀成中间稍厚的圆形面皮，打入馅料，对折捏成饺子生坯。 \r\n5.锅置旺火上，清水烧沸后下入饺子生坯，用手', NULL, NULL, NULL, '饺子粉500克，羊肉300克，时令蔬菜250克，五花猪肉100克，葱花30克，鲜姜末10克，酱油50克，植物油30克，香油10克，精盐、味精、花椒粉、清汤适量。');
INSERT INTO `recipe` VALUES (3293, 63, '榨菜肉丝面', '1.梅花肉切丝，以a料腌l0分钟。榨菜切丝，小白菜切段。\r\n2.起油锅，放入大蒜、肉丝炒香，放榨菜、辣椒拌炒盛出。\r\n3.面先煮熟，小白菜烫熟，置于碗内，加上b料盛出。\r\n4.高汤烧开加b料调味，淋入面碗内即可。', NULL, NULL, NULL, '阳春面250克，梅花肉200克，榨菜（淡）100克，小白菜150克，大蒜3瓣，辣椒1根，高汤8杯。A料：酱油1大匙，淀粉1茶匙，水少许。B料：盐1小匙，鸡粉半匙，胡椒粉、香油少许。');
INSERT INTO `recipe` VALUES (3294, 63, '珍珠圆子', '将汤圆面包甜陷，外面裹上浸泡过的江米，上笼蒸熟即可。', NULL, NULL, NULL, '汤圆面750克，甜陷500克，浸泡的江米150克。');
INSERT INTO `recipe` VALUES (3295, 63, '粽子', '将猪肉切丁，用糖、酱油、精盐、胡椒粉腌一夜，糯米浸泡一至二小时，滤水后用肉汁、酱油、精盐腌2小时，冬菇切碎，将馅包入粽叶，用草绳扎牢，下锅煮熟即可。', NULL, NULL, NULL, '糯米600克，猪肉400克，冬菇50克，笋80克，肉汁200克。');
INSERT INTO `recipe` VALUES (3296, 61, '八宝番茄', '1.将蜜枣、水发莲子、冬瓜糖、蜜瓜片、樱桃、桃脯、桃仁、橘饼八种馅料均匀切为绿豆大的粒，用白糖50克拌匀。\r\n2.番茄洗净，在顶端1.6厘米处切开成盖。逐个用小刀挖空番茄内瓢，填入八宝蜜馅。\r\n3.将鸡蛋清搅打成蛋泡糊，对入生粉30克调匀。将调好的蛋泡糊涂在番茄的接口处，封好番茄，盛入盘中。\r\n4.将放好馅的番茄放入蒸笼中，用旺火蒸约5分钟取出。', '此菜色泽红润，外形美观，质地鲜嫩，甜润适口。', NULL, NULL, '新鲜番茄12只，蜜枣20克，水发莲子20克，冬瓜糖20克，蜜瓜片20克，樱桃15克，桃脯15克，桃仁15克，橘饼15克，鸡蛋清4只，精盐1克，白糖100克，生粉40克，色拉油60克。');
INSERT INTO `recipe` VALUES (3297, 61, '板栗焖仔鸡', '1.母鸡宰杀洗净后，将整鸡剖成两半，把胸翅部位剁成6块，其他部位剁成3厘米见方的块。\r\n2.板栗砍成十字形，放入旺火沸水中煮5分钟，取出脱壳。\r\n3.炒锅置旺火上，下芝麻油烧至七成热，放入鸡块炸约3分钟，起锅沥油。\r\n4.原炒锅再置旺火上，倒入鸡块和板栗，加入鸡汤、精盐、酱油、白糖，盖上锅盖，焖约2分钟，移锅至小火上续焖10分钟，待鸡肉松软，板栗粉糯时，再加猪油、味精、蒜白炒匀。', '此菜色泽金黄，味道鲜美，鸡肉鲜嫩有韧性，板栗绵糯味粉甜，是中秋时节的节令佳肴。多用于民间的婚、寿、吉、庆宴席上。有补虚、健脾、强筋、活血、行乳、止带等功效，对年老体弱、久病体虚者及肺结核、胃下垂等患者有益。', NULL, NULL, '鲜活仔母鸡1只（约1000克），板栗200克，精盐3克，味精2克，酱油40克，白糖100克，鸡汤600克，湿淀粉20克，蒜白20克，猪油50克，芝麻油250克（约耗100克）。');
INSERT INTO `recipe` VALUES (3298, 61, '炒小白菜', '1.选用肥嫩的小白菜去掉边叶和根部，将菜心放入水中浸约15分钟，再洗净沥干。\r\n2.取净炒锅置旺火上，下熟猪油烧热，用姜片炝锅，下入小白菜边炒边翻锅。待小白菜八成熟时，加入精盐和白糖翻炒，最后加入味精，翻锅装盘即成。', '色泽碧绿油润，质地嫩脆适口，口味咸鲜微甜，工序简短且成本低廉。', NULL, NULL, '小白菜500克，精盐5克，味精2克，白糖4克，姜片5克，熟猪油50克。');
INSERT INTO `recipe` VALUES (3299, 61, '糍粑鱼', '1.净鲤鱼肉剁成5厘米长、3厘米宽的鱼块置小盆中，加精盐3克，姜片、葱白段5克拌匀腌制12小时取出晾干。\r\n2.炒锅置旺火上，放入芝麻油100克烧热，再将鱼块逐块下入锅内煎至呈金黄色，加精盐、酱油、味精、醋、糖、姜末、黄酒、干辣椒段、清水250克一起烹烧至卤汁浓稠时，淋入芝麻油，撒上葱白段装盘即成。', '此菜是具有浓厚的湖北乡土气息的地方风味名菜。其口味咸辣、香气扑鼻。佐饭能增量，佐酒可转换口味。在鄂东南地区人们食用“糍粑鱼”已是一种嗜好。', NULL, NULL, '净鲤鱼肉750克，精盐5克，酱油35克，醋10克，黄酒5克，葱白段10克，姜片5克，姜末5克，糖20克，干辣椒段5克，味精2克，芝麻油150克。');
INSERT INTO `recipe` VALUES (3300, 61, '葱烧武昌鱼', '1.烧热锅后,冷油划锅,下鱼煎两面金黄,到出 .\r\n2.小葱过油出香,下油,海鲜酱,姜米,花雕,小葱垫底,下糖,味精,老抽.\r\n3.下鱼,文火烧,大火收汁,用醋勾芡,(有小糖醋味)\r\n4.大翻锅,小葱在上,淋明油,\r\n5.出锅装盘.\r\n', '保持了传统鄂菜烧菜特点，鱼肉鲜嫩，汁稠味浓，突出了葱、醋、色香味俱全。', NULL, '色拉油200克(实用50克),小葱一把(15根),糖,醋,味精,姜米,盐,花雕,老抽,海鲜酱', '鲜活武昌鱼一条');
INSERT INTO `recipe` VALUES (3301, 61, '豆豉鱼', '1.将净青鱼肉剁成4厘米长、3厘米宽的鱼块，放入钵内1加放精盐2克，绍酒、葱段姜片一起拌匀，腌制2小时入味。\r\n2.干豆豉放入碗内加入温水，泡约半小时，拣去杂质洗净，放上干辣椒粒，入笼以旺火蒸1小时取出待用。\r\n3.炒锅置旺火上，下入花生油烧至七八成热时，投入腌制好的鱼块炸至金黄色捞出沥油。\r\n4.将葱段、姜片入锅中稍煸，投入鱼块，加鸡汤、白糖、精盐、酱油和豆豉一起烧沸后，改用小火。', '此菜以五香干豆豉作配料，以辣椒、黄酒、姜粉等调料烹制而成。香味浓厚、偏咸、偏辣，是湖北乡土名菜之一。在家庭里作下菜食用，在酒席上作调味菜和压桌菜食用，很为人们所喜爱。', NULL, NULL, '净青鱼肉750克，干豆豉50克，精盐4克，酱油25克，白糖20克，鸡汤200克，味精4克，花生油1000克（约耗75克），芝麻油25克，绍酒2克，姜片2克，葱段2克，干辣椒粒5克，醋10克。');
INSERT INTO `recipe` VALUES (3302, 61, '豆腐合', '1.将水豆腐逐一切成正方块。鸡蛋磕入碗中，加湿淀粉5O克、面粉、精盐2克、味精1克拌和成全蛋糊。\r\n2.炒锅置旺火上，下花生油烧至七成热，放入水豆腐炸约3分钟，至八成熟时捞出。\r\n3.将炸好的水豆腐用小刀在侧面的一方戳入，挖空其四分之三，然后均匀地填入肉茸馅，并用湿淀粉封口。\r\n4.原炒锅再置旺火上，待油烧至六成热时，将填料的豆腐块裹匀全蛋糊并逐块下锅，炸约3分钟至呈金黄色时捞出。', '此菜色泽金黄，形态美观，质地柔软松脆，口味鲜香醇和。', NULL, NULL, '水豆腐4块（约3O0克），肉茸馅150克，鸡蛋2只，酱油10克，白糖5克，精盐3克，味精2克，湿淀粉6O克，面粉40克，葱花5克，姜末5克，胡椒粉1克，鸡汤150克，花生油1500克（约耗1O0克）。');
INSERT INTO `recipe` VALUES (3303, 61, '风鱼烧肉', '1.将风干鱼用湿水泡洗，切成3厘米见方的块。\r\n2.将带皮的净猪五花肉切成2厘米见方的块，焯水待用。\r\n3.炒锅置旺火上，下少许油滑锅，再投入五花肉稍煸炒，放入葱结、风干鱼、高汤、精盐、味精、白糖、酱油和料酒烧靠1小时离火起锅装盘，淋上小麻油，撒上葱段即成。', '此菜是湖北传统风味名菜之一。是用风干鱼与鲜猪肉混烧而成，既有鱼香诱人的鲜味，又有肥瘦相间、味道香醇的肉香。鱼肉相得益彰，别有风味。', NULL, NULL, '带皮猪五花肉300克，风干鱼150克，精盐4克，味精2克，白糖3克，料酒25克，葱段2克，葱结50克，酱油10克，芝麻油10克，高汤500克。');
INSERT INTO `recipe` VALUES (3304, 61, '锅烧牛肉', '1.将牛肉洗净，置炒锅中加清水用旺火煮熟后捞出沥干，切成5厘米长、1厘米宽、0.3厘米厚的长条。\r\n2.炒锅置旺火上，下芝麻油50克烧热，放入豆瓣酱稍煸，加入牛肉条、精盐2克，姜末、牛肉汤150克，烧至透味时起锅。\r\n3.鸡蛋磕入碗内，放入面粉、干淀粉、芝麻油20克，味精加清水40克拌匀成糊，然后将烧好的牛肉倒入糊中拌匀。\r\n4.炒锅置旺火上，下芝麻油烧至五成热，将挂了糊的肉块下锅。', '此菜色泽黄亮，外酥松内嫩爽，香醇可口，有回味。若蘸以牛肉汁，其味更佳。', NULL, NULL, '牛软肋肉300克，鸡蛋2个，精盐3克，豆瓣酱5克，花椒粉1克，葱花5克，姜末5克，牛肉汤250克，面粉50克，干淀粉50克，湿淀粉10克，味精2克，芝麻油750克（约耗150克）。');
INSERT INTO `recipe` VALUES (3305, 61, '荷叶蒸鸡', '1.嫩荷叶用水洗净，放入七成热的水中烫一下，切成15厘米见方的小块（共20块）。\r\n2.五香米粉用温开水调和成浆。\r\n3.鸡肉、猪肉分别切成5厘米长、3厘米宽的厚片（共20块），各盛于碗内，用料酒、精盐、白糖、酱油、味精、姜末腌半小时，拌上五香米粉浆和熟猪油。\r\n4.把荷叶展开，一片荷叶内包一块鸡肉和一块猪肉，逐个放入笼内，用旺火蒸1小时取出，拆开荷叶，装入盘中即成。', '此菜色泽红亮，滋味鲜醇，鸡肉鲜嫩适口，融有荷叶的甘美清香。有补虚损、益五脏、实筋骨、活血脉、耐寒暑、清热法等功能。既可作孕妇、乳母、老人的滋补食品，又可充当贫血、肝炎、结核病等患者的廉价药膳。', NULL, NULL, '净鸡肉400克，猪五花肉150克，五香米粉100克，精盐6克，味精2克，酱油25克，白糖4克，姜末4克，料酒20克，嫩荷叶5张，熟猪油20克。');
INSERT INTO `recipe` VALUES (3306, 61, '红菱肉片', '1.将猪瘦肉洗净，切成3厘米长、2厘米宽、0.2厘米薄的柳叶片，放入碗内，加入精盐2克抓捏至发粘，加鸡蛋、湿淀粉10克，黄酒拌匀上浆。红菱去壳切成片，用清水漂洗干净。\r\n2.取炒锅置旺火上，下入熟猪油烧至四成热，下入浆好的肉片滑油，将肉片捞起沥干油。\r\n3.原炒锅留底油25克再置旺火上，下姜末、葱段、红菱片略煸，加鸡清汤、精盐、味精烧沸，加肉片翻锅，用湿淀粉勾芡，淋入熟猪油，撒上胡椒粉。', '肉片淡黄，软嫩鲜滑，红菱色白，油润脆爽。两物相配，相得益彰，盛夏食之，清爽可口。', NULL, NULL, '猪瘦肉250克，红菱15O克，鸡蛋2只，精盐5克，味精2克，黄酒3克，葱白段5克，姜末5克，湿淀粉20克，胡椒粉2克，鸡清汤5O克，熟猪油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (3307, 61, '红烧肥肠', '1.将猪肥肠用精盐、香醋等翻洗干净，放入沸水锅中烫约2分钟取出，用清水漂洗干净，水发香菇切成片，大蒜拍松。\r\n2.将猪肥肠再次放入沸水锅中煨至八成熟，取出稍晾，用洁布搌干水分，置大碗中加酱油20克，精盐1克腌入味。\r\n3.炒锅置旺火上，下芝麻油烧至七成热，下肥肠炸至呈金黄色时捞出，切成0.5厘米厚的象眼形肠圈。\r\n4.炒锅置旺火上，下熟油50克烧热，下姜末、蒜瓣稍煸，加入肥肠。', '猪肠形如象眼，色泽黄亮，味道香醇，柔嫩可口。', NULL, NULL, '猪肥肠500克，水发香菇50克，精盐8克，味精3克，酱油30克，白糖15克，大蒜砣50克，姜末10克，葱花5克，香醋20克，黄酒10克，猪肉汤100克，湿淀粉25克，熟猪油75克，芝麻油500克（约耗75克）胡椒粉2克。');
INSERT INTO `recipe` VALUES (3308, 61, '红烧野鸭', '1.野鸭宰杀褪毛，去掉鸭尾和内脏（保留胗、心、肝等），切为九块，洗净沥干，用硝水、姜片、精盐4克腌1小时。\r\n2.取炒锅置旺火上，下入色拉油烧热，放入腌好的鸭块煸炒，待血水炒干时，加料酒翻炒2分钟，至鸭肉刚熟时起锅。\r\n3.原炒锅洗净再置旺火上，下入猪油烧热，投入姜末、鸭块稍炒，加精盐、白糖、清水，盖上锅盖焖烧半小时。待鸭块入味，锅内尚有少量汤汁时，加蒜白、味精稍焖，起锅装盘。', '此菜色泽黄亮，野香扑鼻，鸭肉肥嫩香酥，滋味鲜美爽口，多用于节日家宴中。', NULL, NULL, '洪湖野鸭1只（约1000克），精盐7克，味精2克，料酒10克，硝水50克，白糖10克，胡椒粉1克，葱花2克，姜片5克，姜末5克，蒜白25克，湿淀粉10克，色拉油50克，熟猪油25克。');
INSERT INTO `recipe` VALUES (3309, 61, '黄焖牛肉', '1.将净牛肉置炒锅中加清水在旺火上煮至酥嫩，取出晾凉，切成5厘米长、2厘米宽、0.8厘米厚的长条，盛入碗内，加湿淀粉40克，鸡蛋液、味精1克，面粉、精盐3克一起搅匀挂糊。\r\n2.取炒锅置旺火上，下芝麻油烧至六成热，将牛肉逐块挂匀浆糊放入油锅氽炸，至呈黄色时捞出。\r\n3.原炒锅再置旺火上，留底油50克烧热，放入葱段、蒜片略煸，再下黄花、黑木耳、玉兰片、酱油、精盐、白糖、味精、牛肉。', '牛肉烂而不糜，嫩而不韧，醇香爽口，滋味鲜香。', NULL, NULL, '净牛肉700克，水发玉兰片50克，水发黄花菜25克，水发黑木耳25克，鸡蛋2个，精盐6克，味精2克，白糖5克，酱油20克，葱段10克，蒜片10克，牛肉汤200克，湿淀粉60克，面粉50克，胡椒粉2克，芝麻油1000克（约耗150克）。');
INSERT INTO `recipe` VALUES (3310, 61, '家常鳊鱼', '1.将鳊鱼治净，两面剞上十字花刀。\r\n2.炒锅置旺火上，下入熟猪油烧至七成热，放入鳊鱼煎成两面黄色，然后烹入黄酒、姜末、蒜末、辣椒末、白糖、鸡清汤、精盐烧沸，下入冬笋片、冬菇片。待汤沸后转入文火靠15分钟，用手勺略转动一下，待鱼眼凸出时，将锅移置旺火上加入味精。待汤汁浓稠时，淋入香醋、芝麻油，撒上胡椒粉、葱段即成。', '此菜是江汉平原农村饭桌上的家常菜。成菜鱼形完整，鱼香溢人，肉质外酥内嫩。', NULL, NULL, '鳊鱼1条（约750克），熟五花肉片50克，冬笋片25克，冬菇片25克，葱段2克，蒜末5克，精盐7克，香醋5克，酱油5克，姜末2克，黄酒2克，白糖2克，味精2克，辣椒末5克，鸡清汤250克，熟猪油100克，芝麻油10克，胡椒粉2克。');
INSERT INTO `recipe` VALUES (3311, 61, '京味水晶肘子', '猪肘子一个，用刀刮洗净，剔掉骨头后先用清水煮至无血水（约10分钟），捞出用水冲净，再换水煮肘子，内放葱姜段、盐适量，用小火煮到熟透。备一个大汤碗，把肘子皮朝下放好，汤撇浮沫，捞出葱姜，调少许味精，倒入碗内，待冷却后放入冰箱，两小时后即可切片码盘上桌。水晶肘片晶莹剔透，清香爽口，佐以蒜泥、米醋、香油调的汁，是夏日美味凉菜。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3312, 61, '辣椒蟹', '1.将红鲟洗净后切块，去除灰色腮部，沾上淀粉，用熟油炸过备用。\r\n2.起油锅，将芹菜末、番茄丁、咖喱粉、番红花爆炒出香味，放入。', '辣！', NULL, NULL, '红鲟1只，冬粉2把（约50毫克），芹菜末少许，蕃茄1/4个，咖喱粉3/2匙，番红花少许，酱油1/2小匙，味精1/4小匙，细糖1/3小匙，蕃茄酱1/2小匙，水1杯，蟹肉酱2小匙，越式辣椒酱2小匙。');
INSERT INTO `recipe` VALUES (3313, 61, '凉拌毛豆', '1.将毛豆洗净，剪去毛豆角，放入沸水锅中焯水至断生，捞起晾凉。\r\n2.葱花、姜末、蒜粒放入大碗中，将烧滚的芝麻油冲入碗中，加入酱油、白糖、精盐、味精、香醋、鲜汤、红油，调成综合汁。\r\n3.将毛豆倒入大碗中，与综合汁一起拌匀，放置约10分钟，装入小碟即成。', '此菜色泽淡绿，质地脆爽，口味咸微甜，佐酒特佳。', NULL, NULL, '毛豆600克，精盐6克，味精4克，酱油15克，白糖15克，香醋15克，葱花10克，蒜粒20克，姜末10克，红油20克，鲜汤40克，芝麻油40克。');
INSERT INTO `recipe` VALUES (3314, 61, '凉拌鱿鱼', '1.鱿鱼洗净，左右交叉斜切条纹，再切成片，用滚水烫至熟后捞起，洋葱切丝备用。\r\n2.将材料2混合，以小火煮3分钟，等到变凉以后，再用果汁机打成酱汁。\r\n3.将做好的酱汁与调味料拌匀，再跟鱿鱼、洋葱丝、葱花及薄荷叶末一起拌匀等入味后即可装盘上桌食用。', '一般而言，泰式凉拌菜，大都是用开水氽烫主材料后，和调味料拌匀即可。作法2可多做一些，放入冰箱冷藏，也可用在其他凉拌菜里。', NULL, NULL, '鱿鱼1只，洋葱1/4个，香菜末50克，葱花50克，材料2：薄荷叶切末10克，辣椒2个，蒜头6瓣，白醋1/2杯，糖2大匙，柠檬汁1大匙，鱼露2大匙。');
INSERT INTO `recipe` VALUES (3315, 61, '熘皮蛋', '1.鸡蛋磕入碗中，加精盐2克、面粉10克，干淀粉搅匀成全蛋糊。将酱油、白糖、香醋、味精、料酒、鸡汤、湿淀粉放小碗中拌匀，对成卤汁待用。\r\n2.将面粉10克、干淀粉20克铺撒在长腰盘中。皮蛋剥去外壳洗净，每只切为6瓣，逐一放入盘中裹匀面粉和淀粉。\r\n3.炒锅置旺火上，倒入植物油烧至六七成热，将皮蛋逐一粘上全蛋糊，入锅炸黄沥油。\r\n4.原炒锅留底油再置旺火上，倒进稠的卤汁烧沸。待起。', '此菜形如橘瓣，色泽金黄，质地软嫩适口，鲜咸略带酸甜。最宜佐酒，夏秋食用尤佳。', NULL, NULL, '新鲜鸡蛋9只，熟瘦火腿25克，荸荠50克，水发香菇20克，面粉100克，精盐4克，味精2克，酱油5克，白糖75克，香醋30克，料酒5克，葱段5克，蒜瓣10克，鸡蛋2只，面粉20克，湿淀粉25克，干淀粉30克，胡椒粉1克，鸡汤100克，芝麻油50克，植物油1000克。');
INSERT INTO `recipe` VALUES (3316, 61, '千张肉', '1.将猪五花肉刮洗干净，皮朝上放入炒锅内，加清水用旺火煮半小时捞出，用金酱涂匀猪皮。\r\n2.炒锅置旺火上，下植物油烧至六成热，将涂了金酱的肉块趁热下锅，约炸2分钟，待呈红色时捞出晾凉，切成5厘米长的薄片。\r\n3.取大碗一只，先放入花椒、葱段、姜片垫底，再将肉片皮朝下整齐地码入，正面码50块，两边各镶15块。然后将酱红、红方腐乳汁倒在肉块上，加五香豆豉、精盐、味精，连碗入笼，用旺火蒸4小时。', '此菜系湖北民间传统名菜，刀工精细，肉片菲薄如纸，数十片相叠，故名。成菜色泽嫣红，肉片软嫩不腻，味道溶烂不糜，咸甜馥香浓郁。', NULL, NULL, '带皮猪五花肉500克，精盐3克，酱油25克，胡椒粉2克，花椒6粒，葱段5克，姜片5克，金酱15克，红方腐乳汁20克，味精2克，葱花3克，植物油1000克（约耗50克），五香豆豉50克。');
INSERT INTO `recipe` VALUES (3317, 61, '芹菜干丝', '1.将豆腐干去掉外皮，切成细丝，用开水焯一下，芹菜去叶，去掉老茎，切成细丝。\r\n2.炒锅内放油，将葱放入煸出香味后，将芹菜丝放入煸炒。炒至八成熟时加入干丝同炒，加入盐、料酒、味精、白糖，翻炒均匀后，即可出锅。', '此菜咸鲜略带甜味，豆腐干丝白嫩，芹菜脆绿。', NULL, NULL, '豆腐干200克，芹菜150克，盐5克，味精1.5克，白糖2.5克，葱1克，料酒2克，油30克。');
INSERT INTO `recipe` VALUES (3318, 61, '清蒸鸭子', '1.填鸭宰杀去毛，剁去鸭蹼、漂洗干净，放入锅中煮至八成熟捞出。腹部向下置案上，分别将腿、翅及背划开，去净全脊。\r\n2.将鸭曲卷入蒸盆中铺平（腹部向下，刀口向上），抽出鸭脯撕成条放原处，鸭架小骨置鸭身上。添鸡汤，加精盐6克、料酒、葱段、姜片，将鸭扣入汤碗（腹部向上）。\r\n3.取炒锅置旺火上，淋入鸭汤加玉兰片、香菇、味精烧沸。撇去浮沫，将汤浇入汤碗内（汤与鸭持平），豌豆苗摆放在鸭身两旁。', '此菜色泽和谐、造型雅致，汤汁浓醇、滋味鲜香；鸭肉酥嫩、肥而不腻。是秋冬的进补佳品，亦可用于中高级宴席。', NULL, NULL, '填鸭1只（约1500克），玉兰片20克，水发香菇20克，豌豆苗15克，鸡蛋皮1张，鸡茸25克，鸡蛋清1只，精盐8克，味精2克，料酒5克，葱段20克，姜片20克，鸡汤1000克，湿淀粉20克。');
INSERT INTO `recipe` VALUES (3319, 61, '全家福', '1.炒锅置旺火上，下熟猪油烧至四成热，下入上浆才鱼片滑油，捞出沥油。\r\n2.将水发蹄筋、熟猪肚片、油发鱼肚、水发香菇、口蘑、嫩玉米笋、嫩白菜心分别置旺火沸水中焯水，再用清水冲凉。\r\n3.炒锅洗净置旺火上，放入熟猪油25克烧热，下入葱姜汁、高汤、精盐、味精、白胡椒粉烧沸，再倒入种种原料烧烩入味后勾芡，淋上熟猪油，起锅装入大窝盘中，撒上葱花即成。', '此菜汤汁清醇，香鲜可口，色彩和谐，质地各异，多料合烹，营养丰富，多用作家宴的头菜。', NULL, NULL, '水发蹄筋100克，熟猪肚片100克，油发鱼肚100克，上浆才鱼片100克，水发香菇50克，口蘑50克，嫩玉米笋50克，嫩白菜心50克，精盐7克，味精2克，葱姜汁8克，白胡椒粉2克，高汤600克，湿淀粉30克，熟猪油500克（约耗100克），葱花5克。');
INSERT INTO `recipe` VALUES (3320, 61, '山口羊肉', '1.将熟羊肉切成约5厘米长、3厘米宽的长方块。香菜洗净沥干。\r\n2.炒锅置旺火上，下色拉油烧至七成热，将羊肉块下入油锅中炸约3分钟,呈黄色时起锅沥油。\r\n3.原炒锅留底油25克烧热，将炸好的羊肉块倒入锅中，随即将葱花、蒜末、酱油、白糖、精盐、味精、料酒、鲜汤对成卤汁一起下锅，烹约5分钟。待卤汁收紧、羊肉透味时，淋上芝麻油，起锅装盘，缀以香菜即可。', '此菜色泽黄亮，块形一致，滋味鲜香，肉质酥嫩。', NULL, NULL, '熟羊肉500克，香菜25克，精盐4克，味精2克，酱油15克，白糖10克，香醋15，料酒10克，葱花5克，蒜末10克，鲜汤150克。芝麻油25克，色拉油1000克（约耗75克）。');
INSERT INTO `recipe` VALUES (3321, 61, '双黄鱼片', '1.将鳜鱼肉去皮剔去骨刺，切成7厘米长、5厘米宽、0.2厘米厚的片。鸡蛋磕入碗内，加精盐2克，放入鳜鱼片、湿淀粉15克拌匀。冬菇、冬笋切成片焯水等用。\r\n2.炒锅置旺火上，放入熟猪油，烧至五成热，将鱼片逐片下入锅内，边炸边用锅铲拨动。炸至呈淡黄色时，用漏勺捞出。\r\n3.原炒锅倒去余油后置旺火上，下入葱段略煸，加入姜末、绍酒、鸡汤稍烹，再倒入鱼片、冬笋、冬菇，加入精盐、味精、酱油、白糖。', NULL, NULL, NULL, '净鳜鱼肉500克，姜末3.5克，冬菇25克，冬笋25克，蟹黄5克，精盐6克，酱油20克，白糖5克，葱段25克，绍酒15克，鸡汤300克，鸡蛋3个，姜末4克，味精1克，湿淀粉25克，熟猪油500克（约耗100克）。');
INSERT INTO `recipe` VALUES (3322, 61, '酸辣莴笋', '1.将莴笋竖刀切开，成丝码入盘内，撒上精盐3克，白糖10克，腌30分钟，沥干水分，放另一盘内备用。\r\n2.净锅置中火上放入香油10克烧热，投入花椒炸出香味，加入香油5克，放入干辣椒炸至呈金黄色离火过箩。将油倒在莴笋片上，干辣椒摆在上面。\r\n3.另起锅置中火上，放入剩余的白糖、精盐、米醋，烧开浇在莴笋丝周围即成。', '此菜色泽碧绿，酸辣微甜，清鲜适口。', NULL, NULL, '皮莴笋500克，精盐5克，米醋30克，白糖20克，香油5克，花椒5克，干辣椒15克。');
INSERT INTO `recipe` VALUES (3323, 61, '蒜泥黄瓜', '1.将黄瓜用毛刷清洗干净，切去两端，再切成四瓣，片去瓜芯改刀切成筷子粗细、6厘米长的条放在盆中，撒上精盐腌渍15分钟左右。将腌好的黄瓜用清水冲洗一遍，捞出沥干水分，再用洁布沾干水分待用。\r\n2.将大蒜去皮洗净，放入钵捣成泥状，然后倒入容器中，放入精盐、味精、香油调拌均匀，即可装盘食用。', '此菜淡雅美观，清鲜爽脆，蒜泥香郁，咸鲜适口，酒饭皆宜。', NULL, NULL, '黄瓜300克，精盐8克，香油25克，大蒜50克，味精1克。');
INSERT INTO `recipe` VALUES (3324, 61, '蒜泥莴苣', '1.将莴苣去皮洗净，切成5厘米长、0.5厘米见方的条。将大蒜去皮，捣成蒜泥。\r\n2.将莴苣放入一盆内，撒上精盐腌一会沥去汤汁，放入蒜、香油、醋拌匀装盘即可。', '此菜色泽碧绿，莴苣脆嫩，蒜香味浓，清凉爽口，为夏季冷食之佳品。', NULL, NULL, '莴苣600克，精盐5克，香油10克，大蒜50克，醋25克。');
INSERT INTO `recipe` VALUES (3325, 61, '糖醋莲藕', '莲藕削去藕节，刮去皮，洗净。每节藕从中破两节，均匀地切片，整齐地放在盘中，撒上精盐腌约1小时后，沥尽渗出的水分。加入白糖、白醋，撒上姜丝，再腌1小时即可。食用时，整齐地叠片装盘。', '此菜脆爽酸甜，宜于佐酒。', NULL, NULL, '脆嫩白莲藕500克，白糖60克，白醋25克，精盐3克，姜丝10克。');
INSERT INTO `recipe` VALUES (3326, 61, '五香鹌鹑', '1.将鹌鹑宰杀，拔去羽毛，从背部剖开，摘去内脏，用水冲净，控干水分备用。\r\n2.鹌鹑蛋下入凉水锅内，用小火煮熟，剥去蛋壳备用。\r\n3.取一铁锅，置大火上倒入花生油烧至八成热，下入鹌鹑炸至呈金黄色，捞出，控净油。\r\n4.原锅内留底油，烧热后下入葱花、姜片、蕃茄酱、咖喱油煸炒，然后放入鹌鹑、柠檬、葡萄酒、辣椒油、精盐、白糖、味精、鸡汤烧沸，用小火煨至熟，后转大火收汁，待汤汁变浓。', '此菜鹌鹑鲜香味美，咸甜酸辣，造型优雅，色泽鲜艳，为佐酒下饭的佳肴。', NULL, NULL, '鹌鹑10只，鹌鹑蛋10个，辣椒油40克，葡萄酒3克，姜片2克，味精5克，香油10克，咖喱油10克，花生油1000克，香菜1克，白糖30克，蕃茄酱50克，精盐5克，花椒粉1克，柠檬1克，葱花5克，鸡汤适量。');
INSERT INTO `recipe` VALUES (3327, 61, '五香葱油鸭', '1.仔鸭宰杀去毛后，从肛门处切一小口，掏出内脏洗净，放入旺火沸水中烫约5分钟，除去腥臊味。猪板油洗净，切成方块。\r\n2.取沙钵一只，放入葱段垫底，再放入整鸭。加猪板油姜片、酱油、八角、陈皮、茴香、白糖、料酒和清水500克。先用旺火烧沸，再用微火慢慢炖2小时，至鸭皮呈酱红色时，翻过面来继续炖2小时。直至汤汁稠浓、鸭肉软烂透味、喷发浓郁香气时，起钵盛入碗内，加入精盐、味精、葱花和白胡椒粉。', '鸭形完整，色泽红亮，鸭肉软烂油滑，口味鲜香浓郁。', NULL, NULL, '仔鸭1只（约1250克），猪板油200克，精盐2克，味精2克，酱油200克，白糖10克，八角2只，陈皮3克，茴香3克，姜片15克，葱段400克，葱花10克，料酒100克，白胡椒粉2克，芝麻油20克。');
INSERT INTO `recipe` VALUES (3328, 61, '五香野兔', '1.将丁香、八角、桂皮、陈皮装入纱布袋内用绳扎紧口，放入锅内，加清水、黄酒、酱油、精盐和冰糖在旺火上煮成卤汁。\r\n2.将野兔肉切成五大块（前腿2块、后腿2块、中部1块）盛入盆中，用清水漂洗1小时后沥干。用硝水、葱结、姜块腌渍半小时，置沸水锅中煮5分钟除腥，再用清水洗净。\r\n3.将兔肉放入卤汁锅内，用旺火煮1小时至熟透后捞出，用洁布揩干卤汁，冷却后将兔肉表皮涂上一层芝麻油，切成小方块盛盘。', '兔肉肉质细嫩，味道鲜美，有馥郁的五香回味。', NULL, NULL, '野兔肉1000克，精盐6克，酱油30克，冰糖15克，丁香2.5克，八角3克，桂皮3克，陈皮3克，黄酒50克，葱结15克，姜块15克，硝水2.5克，蒜泥5克，芝麻油20克。');
INSERT INTO `recipe` VALUES (3329, 61, '西餐糖醋泡菜', '洋白菜、胡萝卜、白菜花、黄瓜、柿子椒、芹菜一共备1公斤，切成块或段。水开后快速焯一下，捞出后摊开晾凉，撒少许盐。再用干净锅熬糖水（糖化即可），放进香叶几片，干辣椒几段，醋精适量（尝出甜酸），晾凉后，将泡菜原料放进浸泡，换干净器皿加盖入冰箱，两小时后，甜酸脆爽，清凉可口的泡菜即可使用。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3330, 61, '鲜拌芫荽', '1.将芫荽的根部去掉，择去黄叶洗净，控干水分切成末。姜去皮切成末。蒜苗洗净也切成末备用。\r\n2.取一盆，将以上三种末加入豆豉、味精、精盐、香油调匀即成。', '此菜芫荽味浓、豆豉味香，口味咸鲜微辣，为佐酒佳品。', NULL, NULL, '鲜芫荽500克，鲜姜10克，香油5克，味精2克，焖酱豆豉25克，蒜苗25克，精盐3克。');
INSERT INTO `recipe` VALUES (3331, 61, '香油烤麸', '1.将烤麸用手撕成小块，放入温水中漂洗干净，取出挤干水分待用。\r\n2.净锅置旺火上烧热，用素油滑锅后加入花生油，烧至六成热，将烤麸块下入油锅中炸至外表略硬，捞出滤净油。\r\n3.原锅留底油置旺火上烧热后，用素油滑锅，下入烤麸、笋片、冬菇片、酱油、黄酒、白糖、精盐、鲜汤、味精，烧滚后端至小火加盖焖烧至卤浓、烤麸熟，端回旺火翻炒，收干汤汁，淋入香油出锅晾凉，即可食用。', '咸中带甜，香鲜软嫩。', NULL, NULL, '烤麸500克，冬菇片25克，笋片25克，黄酒10克，精盐1克，白糖20克，味精2克，酱油20克，麻油10克，鲜汤250克，花生油1000克。');
INSERT INTO `recipe` VALUES (3332, 61, '香糟鸡', '1.仔鸡洗净，放入开水锅中略烫，捞出后洗净血污。下汤锅用小火慢慢煮熟，捞出晾凉。\r\n2.熟鸡去头颈、大骨，剖六大块待用。\r\n3.将香糟、绍酒、白糖、精盐、糖桂花和花椒一同放容器中搅匀成糟汁，再将鸡块放入糟汁中浸泡4小时。\r\n4.食用时将鸡块切片装盘，淋入香糟即成。', '此菜肉质细嫩，口味咸鲜，糟香浓郁，佐酒最佳。', NULL, NULL, '净仔鸡1只（约750克），绍洒200克，白糖30克，精盐5克，糖桂花10克，花椒5克，姜片20克，香糟50克。');
INSERT INTO `recipe` VALUES (3333, 61, '香炸鹌鹑', '1.鹌鹑宰杀去毛后，剁去脚爪，从肋下开刀，取出内脏，冲洗干净。\r\n2.取鹌鹑置于大碗内，用精盐、味精、料酒、香醋、葱段、姜片将鹌鹑内外抹遍，腌渍约半小时。\r\n3.将炒锅置于旺火上，注入芝麻油。烧至成热，再下入鹌鹑重油复炸，然后端锅将油滗尽。\r\n4.炒锅再置旺火上，下入姜米和葱花，将鹌鹑颠翻均匀，撒上白胡椒粉，将鹌鹑摆入盘中，缀以香菜，随花椒盐味碟一同上桌。', '此菜色泽和谐，形态完整，肉质酥嫩，鲜香醇浓。多用于民间的喜庆宴席上，是理想的佐酒佳肴。', NULL, NULL, '鹌鹑10只，香菜50克，精盐5克，味精2克，香醋10克，料酒15克，葱段15克，姜片10克，葱花10克，姜米5克，花椒盐6克，白胡椒粉2克，芝麻油1500克（约耗100克）。');
INSERT INTO `recipe` VALUES (3334, 61, '炸藕夹', '1.将莲藕切去节、削去皮、洗净。先将细的竖切两半，粗的切成四半，再切成3－4毫米厚的片，每两片相连，成夹子形。\r\n2.将肉茸放入碗内，放入精盐2克、淀粉10克、酱油，搅拌均匀，再放葱末、姜末、麻油拌匀成馅待用。\r\n3.将鸡蛋打入另一大碗内，放入湿淀粉、面粉、精盐搅匀成糊。\r\n4.将肉馅均匀地夹入藕夹内，码放盘内成一串，再将糊浇在藕夹上。\r\n5.炒锅内倒入植物油，置旺火上烧。', '此菜脆嫩香咸，色泽金黄。', NULL, NULL, '莲藕250克，肉茸75克，面粉250克，鸡蛋2个，精盐5克，麻油25克，湿淀粉20克，酱油10克，椒盐适量，植物油500克，葱末、姜末各5克。');
INSERT INTO `recipe` VALUES (3335, 61, '炸酥鱼', '1.将青鱼肚当剁成6厘米长、3厘米宽的长条块，洗净沥干，放入盆中加精盐3克，绍酒腌渍15分钟。玉兰片切成片。黑木耳洗净待用。\r\n2.将鸡蛋磕入大碗内，加面粉、淀粉、精盐和水20克，搅拌成全蛋糊。将腌渍好的鱼块倒入全蛋糊上沾匀糊浆。\r\n3.取炒锅置旺火上，下植物油烧至七成热，将鱼块逐一下锅炸至外酥里嫩、色泽金黄时捞起，整齐地摆在扣碗中。', '此菜色泽黄亮、鱼肉酥嫩、汁浓芡亮、滋味咸香，多用于节日家宴上。', NULL, NULL, '青鱼肚当500克，鸡蛋2只，水发玉兰片25克，水发黑木耳25克，酱油15克，白糖8克，精盐5克，味精2克，面粉40克，淀粉30克，绍酒15克，葱花10克，姜片10克，胡椒粉2克，香醋6克，鸡汤100克，植物油1000克（耗约75克）。');
INSERT INTO `recipe` VALUES (3336, 58, '八宝香糯', '1.将红枣、黑枣放入小碗在蒸笼中以中小火蒸15分钟后去核切碎丁，百合去头尾逐瓣洗净，与枣丁一起备用。\r\n2.洗净后的黑糯米与长春酒、砂糖、清水一同加入电锅中蒸煮至熟软备用。\r\n3.蒸熟的黑糯米趁热顺同一方向用力搅拌5-6分钟，黑糯米呈黏糊状时撒入芒果干碎丁拌匀。\r\n4.平铺一张半月型豆皮，把混合好的八宝糯米薄摊在豆皮上卷成桶状，调面粉糊封粘开口处。\r\n5.锅中入油，以中火烧至6分热，将香糯卷炸至金黄，炉火转大火炸约半分钟逼出余油后捞出熄火。\r\n6.香糯卷沥干油分后切1.5厘米厚小段，沾取花生粉或芝麻盐食用', '糯米软粘，齿齿留香。', NULL, NULL, '材料黑糯米1杯半，红枣10颗，芒果干100克，面粉2大匙，鲜百合1棵，黑枣5颗， 糖酥松子仁2大匙，糖酥核桃仁7-8朵，豆皮2张（半月型）高味料长春酒1/2杯，清水1杯，花生粉、芝麻盐各适量，砂糖2大匙。');
INSERT INTO `recipe` VALUES (3337, 58, '串煎馄饨', '1.将平底锅预热放入少许油，转动油锅，让锅底平均布满沙拉油，再改小火。\r\n2.将馄饨平放入平底锅中，摆放整齐，将底部煎成金黄色，改大火，加入水煮沸后，盖上锅盖继续煮，听到油爆声后改小火，再将锅盖打开，慢慢煮至水分完全收干即可熄火，并用小铲将馄饨铲起。\r\n3.用竹签将馄饨串起，每串3个，上面撒上法国香菜末，再依个人口味，酌量撒上辣椒粉，串好后摆盘即可。如果口味想吃重一点的话，可在馄饨起锅前先涂上酱油，然后再撒上喜欢的调味粉或芝麻均可。', '外焦里嫩，有些微辣。', NULL, NULL, '馄饨1盒、法国香菜末2小匙、辣椒粉2小匙。');
INSERT INTO `recipe` VALUES (3338, 58, '大白菜水饺', '1.猪绞肉放入葱、姜末、虾米及盐、胡椒粉。\r\n2.倒入油及麻油，以同一方向搅拌均匀。（提示：向同一方向搅拌，才能让肉充分吸收油及调味料。）\r\n3.再加入3/2杯白菜汁以同一方向搅拌至黏稠状。（提示：让白菜汁及空气融入绞肉中，会使肉更滑嫩且有白菜汁的鲜甜滋味。）\r\n4.最后拌入白菜末。\r\n5.整个搅拌均匀就是道地的大白菜馅料。\r\n6.锅中倒入8分满的水，煮开大滚后，放入饺子，立刻用漏勺由上往锅底推动。待水再度煮开后，加入1杯冷水改中火煮，再重复加水步骤1—2次。至水饺浮起，颜色变白即熟，捞起后沥干水分，盛盘', '色泽鲜亮，齿颊留香。', NULL, NULL, '大白菜800克，猪绞肉300克，葱2支，姜3片，虾米20克，麻油1大匙，胡椒1小匙，盐、油各2大匙。');
INSERT INTO `recipe` VALUES (3339, 58, '番茄镶肉', '1.虾仁、荸荠剁碎，加肉糜、调味料拌匀。\r\n2.番茄自顶部切开，挖除籽与瓤并抹上少许生粉，将肉馅塞入。全部塞好后置于盘上，覆塑料膜，高功率微波4分钟即成。', NULL, NULL, NULL, '番茄6个，猪肉糜100克，虾仁70克，荸荠2个，酱油、酒各1汤匙，水2汤匙，糖1茶匙，胡椒粉少许，盐1/2茶匙，葱末2汤匙。');
INSERT INTO `recipe` VALUES (3340, 58, '黄梨焖鸡', '1.将鸡块放进一个22寸的陶瓷锅里，以混合的A料调味料腌约1小时。\r\n2.加进B料。\r\n3.加盖以中功率档微波煮约10分钟。\r\n4.拌入玉米粉浆。\r\n5.保持时间5分钟。\r\n6.以生葱拌饰。', NULL, NULL, NULL, '鸡块600克，玉米粉1汤匙（用清水2汤匙混合），生葱用于拌饰（切成细丝）5克。调味料A：胡椒粉1茶匙，嫩姜（剁细）10克，蕃茄酱2汤匙，濠油2汤匙，白酒2汤匙。B料：黄梨（切成1/2厘米楔状）200克，鲜辣椒（去核切丝）5克，盐1/2茶匙。');
INSERT INTO `recipe` VALUES (3341, 58, '馄饨千层饼', '1.葱洗净沥干，切成末后加入面粉、盐及所有调味料一起拌匀备用。\r\n2.将馄饨皮分成6组，每组5片。将馄饨皮5片铺平，将作法1的馅料涂在其中4片上，一一叠起，最后一片盖在上面；依序做好6份，每份周围再撒上少许面粉封住，避免馅料流出。\r\n3.将油锅热至160—170摄氏度，放入做好的千层饼，炸至外表呈金黄色即可捞起，油沥干后食用，整片食用或切成小片均可。', '风味独特，口感香脆润滑。', NULL, NULL, '馄饨皮30张，葱5根，面粉1/2杯，蛋1-3个，面粉2大匙，盐1/3大匙，黑芝麻1大匙，猪油（鸡油）3大匙，糖1/3大匙，水2大匙，肉桂粉2小匙。');
INSERT INTO `recipe` VALUES (3342, 58, '馄饨香蕉卷', '1.将香蕉去皮，先切一半，每段再横切6份，一共12条备用。面粉和水调成面糊。\r\n2.取一张馄饨皮铺平，将香蕉段放在对角线上面，卷起后，再涂上面糊固定封口，两边涂上少许面糊，将开口处压紧备用，调味料混合均匀备用。\r\n3.将烤箱预热至180—200摄氏度，将馄饨香蕉卷放入，烤约15分钟，取出即可装盘，蘸调味料食用。', '外脆里嫩，鲜咸适宜，有些味甜。', NULL, NULL, '馄饨皮12张，香蕉1根，面粉1大匙，水2大匙，调味料沙拉酱2大匙，蕃茄酱0.5大匙。');
INSERT INTO `recipe` VALUES (3343, 58, '家制密汁叉烧', '1.枚头肉洗净，在表面轻轻刻痕。\r\n2.蒜头、干葱去衣切碎，与调味料同混和，腌肉约45分钟。\r\n3.预热煎碟约2分钟，加入油2汤匙，放进枚颈肉，用高火煮约12分钟。\r\n4.打开炉门，取出枚头肉，涂上蜜汁料，再用高火煮约1-2分钟。\r\n5.取出叉烧，切片供食。', NULL, NULL, NULL, '枚头肉450克（约12两），蒜头1粒，干葱l粒，调味料：老抽1汤匙，生抽、淮盐、芝麻酱各1茶匙，糖、玫瑰露酒、姜汁各1汤匙，磨豉1平汤匙，海鲜酱2茶匙。蜜汁料：暖水2汤匙，麦芽糖1平汤匙。');
INSERT INTO `recipe` VALUES (3344, 58, '酱鸭', '1.将调料在微波炉中用中功率档加热发出香味。\r\n2.在鸭的表皮涂上红酱油，并让它干却。然后翻身淋上调料，浸1小时，中途翻转几次，使浸料。\r\n3.将鸭及卤汁置入盘中，用耐高温塑料膜或加盖以中功率焖30分钟。\r\n4.烹煮一半时间后，将鸭翻转，再包裹后继续焖煮其余的时间。', NULL, NULL, NULL, '鸭（约1.3公斤）1只，红酱油2茶匙，玉米淀粉1.5茶匙（用1汤匙清水调和）。调味料：嫩姜（切片）20克，老姜（切片）50克，丁香6粒，八角2粒，肉桂皮2厘米，濠油2.5汤匙，糖1茶匙，白酒2茶匙，盐1茶匙，清水1杯。');
INSERT INTO `recipe` VALUES (3345, 58, '酱汁香煎牛柳', '1.牛柳洗净，切出约1厘米厚的牛柳块，轻轻拍松，用调味料腌约1小时。\r\n2.用高火预热煎碟约3分钟。\r\n3.加油2汤匙，放进牛柳，高火煮约一分钟半。\r\n4.打开炉门，加入芡汁，用中高火煮约2分钟，即可上碟供食。', NULL, NULL, NULL, '牛柳220克（约6两）。调味料：糖、酒各1茶匙，姜汁、生抽、粟粉各1茶匙，老抽半汤匙，油、水各2汤匙，胡椒粉少许。芡汁料：茸辣酱半汤匙，糖2茶匙，粟粉1/4茶匙，水3汤匙。');
INSERT INTO `recipe` VALUES (3346, 58, '荔芋西米露', '1.西米洗净，芋头去皮切粒。\r\n2.冰糖放入清水，用深盅盛，盖上盖子，高火煮5分钟。\r\n3.加入西米和芋头粒，中火煮20分钟。\r\n4.取出，搅入鲜奶和椰汁，便可供食，晾凉后放冰箱冷冻亦可。', NULL, NULL, NULL, '西米、芋头各100克，清水1250毫升，鲜奶、椰汁各150毫升，冰糖125克。');
INSERT INTO `recipe` VALUES (3347, 58, '莲子红豆沙', '1.红豆洗净，用热水浸过夜。\r\n2.果皮洗净浸软。\r\n3.水和冰糖同放深碗内，盖上盖子，高火煮6分钟。\r\n4.加入红豆、果皮和莲子，中火煮50分钟，再用高火煮30分钟，即可供食。', NULL, NULL, NULL, '红豆100克（约2两半），莲子（已去衣）25克（约6钱半），冰糖110克（约3两），水600毫升，果皮1片。');
INSERT INTO `recipe` VALUES (3348, 58, '凉拌馄饨皮', '1.将馄饨皮切成宽约1公分的长条状，煮熟捞起后冲冷水，沥干水分备用。\r\n2.将小黄瓜、西红柿洗净，切片备用，将柠檬磨出一大匙柠檬皮末，然后柠檬榨汁备用。\r\n3.把调味料及柠檬汁拌匀，作为酱料。\r\n4.将馄饨皮、小黄瓜、西红柿片及酱料轻轻拌匀，最后撒上柠檬末，即可食用。', '鲜咸适宜，风味独特，口感润滑。', NULL, NULL, '馄饨皮20张，小黄瓜1条，西红柿1个，柠檬1个，调味料：白醋2大匙，橄榄油1大匙，糖1大匙。');
INSERT INTO `recipe` VALUES (3349, 58, '龙凤煲', '蛇宰杀、老母鸡的加工是本煲汤中的亮点。蛇三根分别去皮、去胆、去血、去头与老母鸡一同放入大砂锅中加水。', '药膳功效有行血去风湿，姜片去腥。汤汁中如有蛇毒，姜片将变黑色。能引起食客注意，以免中毒，蛇胆有瞑目清肺之作用，蛇胆有补血作用。', NULL, NULL, '老母鸡一只，草蛇三只，淮山、枸杞、沙参、红枣，盐、味精、姜片。');
INSERT INTO `recipe` VALUES (3350, 58, '龙珠芋香饭', '芋头挖空后，芋头切粒待用。芋头粒、虾米、腊肠和叉烧炒熟后，拌和香米饭炒熟倒入芋头内蒸上10分钟即可上桌。', '原味醇香，滋味鲜美。', NULL, NULL, '300克香米，煮熟待用，150克芋头，100克虾米，切粒，100克叉烧，切粒，50克腊肠，切粒，盐、胡椒粉、生抽少许。');
INSERT INTO `recipe` VALUES (3351, 58, '麻油素面', '1.在汤锅内煮水，水沸后将乌东素面放入，煮到自己喜欢的软硬程度时捞起装盘。\r\n2.将120毫升水加酱油煮开，放入豌豆仁，煮熟后熄火，将此汤汁全部淋在素面上。\r\n3.将紫菜切丝点缀在汤面上，滴数滴麻油即成。', '色鲜明，味清香、鲜嫩爽口。', NULL, NULL, '乌东素面半把（超市食品柜台有售），紫菜少许，豌豆仁5－6粒。水20毫升，酱油1汤匙，麻油数滴。');
INSERT INTO `recipe` VALUES (3352, 58, '密汁烤凤翼', '1.鸡翼洗净，抹干水分，用调味料腌约30分钟。\r\n2.干葱去衣，切碎。\r\n3.煎碟用高火预热约2分钟，加入油约2汤匙，再加干葱碎，用高火煮约1分钟。\r\n4.取出煎碟，上放鸡翼，用高火煮约3分钟。', NULL, NULL, NULL, '鸡翼300克（约8两），干葱2粒。调味料：海鲜酱、面豉酱、赤砂糖各半汤匙，五香粉1茶匙，玫瑰露酒、生抽、老抽各1茶匙，麻油数滴。');
INSERT INTO `recipe` VALUES (3353, 58, '墨西哥水饺片', '1.将水饺皮用波浪刀全部切成半圆形备用。\r\n2.热油锅，等油温在160摄氏度左右时，将切好的水饺皮入锅炸至呈金黄色，约需3—5分钟，再捞起将油沥干，摆盘后蘸调味料食用。', '风味独特，香脆爽口，有些酸辣。', NULL, NULL, '水饺皮10张，番茄丁3大匙，洋葱丁2大匙，葱末1大匙半，香菜末1/3大匙，辣椒末2小匙，白醋1大匙，糖1/2大匙，香油2小匙。');
INSERT INTO `recipe` VALUES (3354, 58, '排骨冬瓜汤', '冬瓜、排骨切成小块，姜切细丝。取一只大汤碗，盛4碗热开水，将冬瓜块、排骨块及姜丝放入，以微波高段火力煮15－20分钟，取出加盐及味精调味即可。', NULL, NULL, NULL, '排骨250克，冬瓜250克，姜4片，盐适量，味精少许，热开水4碗。');
INSERT INTO `recipe` VALUES (3355, 58, '啤酒蟹', '1.花蟹洗净，斩件。\r\n2.姜去皮切片。\r\n3.葱洗净切段。\r\n4.蒜头去衣切碎。\r\n5.把所有材料拌匀，加油1汤匙，同放在盛器上，盖上保鲜纸，留一开口处疏气，高火煮4分钟即成。', NULL, NULL, NULL, '花蟹600克（约1斤），姜15克（约4钱），葱30克（约8钱），蒜头4粒，花椒1/2茶匙，八角1粒，啤酒200毫升，盐1平茶匙。');
INSERT INTO `recipe` VALUES (3356, 58, '葡萄面排', '将葡萄干洗净，加糖，适量水煮沸后，放入玉米粉调匀，加柠檬汁、糖色调好口味煮至微沸，备用。先将猪油、面粉放在一起拌匀后，加盐、牛奶、泡打粉用力混和成面团，分成2块面团，擀成约2毫米厚的面片，将一块面片摊入排模内，切去多余边缘，放进烤箱烤至6成熟时取出，倒入葡萄干煮液，待冷后再盖上另一块面片，切齐边缘，涂上鸡蛋糊，用餐叉划花纹，再放进烤箱烤熟。食用时每份切一块装盘即可。', '色泽油亮，鲜嫩适口。', NULL, NULL, '葡萄干200克，面粉150克，玉米粉25克，泡打粉1.2克，猪油75克，砂糖60克，柠檬汁3克，牛奶50克，水250克，糖色适量，精盐2.5克。');
INSERT INTO `recipe` VALUES (3357, 58, '麒麟冬瓜', '1.冬瓜切出1厘米阔的厚片，每片中央刻一刀。\r\n2.火腿切片，大小与冬瓜夹相称。\r\n3.把火腿片嵌进瓜片中，用保鲜纸包着，留一开口处疏气，高火煮3分钟。\r\n4.芡汁料放碗内，盖上保鲜纸，留一开口处疏气，高火煮3分钟，淋瓜片上，便可供食。', NULL, NULL, NULL, '冬瓜200克（约5两半），火腿50克（约1两半）。芡汁料：蚝油1汤匙，糖1茶匙，粟粉1茶匙，水2汤匙，葱粒、蒜茸各1茶匙。');
INSERT INTO `recipe` VALUES (3358, 58, '三色蛋', '1.方形扁平容器内铺上玻璃纸，上面涂少许麻油。\r\n2.鸡蛋打散，加入调味料拌匀再加入切丁之咸鸭蛋、皮蛋拌匀，倒入容器内，加盖，以解冻档加热12分钟后取出待凉，切块、排盘。', NULL, NULL, NULL, '鸡蛋5个，咸鸭蛋3个（熟），蛋黄3个，皮蛋3个，水6汤匙，猪油1汤匙，酒1/2茶匙，盐1/2茶匙。');
INSERT INTO `recipe` VALUES (3359, 58, '什果马蹄冻糕', '1.大菜剪碎，冲洗净。\r\n2.马蹄去皮，切粒。\r\n3.水、大菜、马蹄同放深碗内，盖上盖子，高火煮8分钟。\r\n4.什果切碎。\r\n5.什果碎与蔗汁同拌入大菜溶液中。\r\n6.把做法5倒在模内，晾凉后冷冻约30分钟，便可供食。', NULL, NULL, NULL, '大菜15克（约4钱），马蹄150克（约4两），冰糖100克（约2两半），蔗汁250毫升，水350毫升，什果100克（约2两半）。');
INSERT INTO `recipe` VALUES (3360, 58, '什锦银针粉', '1.冬菇浸软切丝。\r\n2.红萝卜、珍珠笋、叉烧均切丝。\r\n3.虾仁去肠，洗净，加调味料腌片刻。\r\n4.蒜头拍碎。\r\n5.葱切段。\r\n6.把所有材料和芡汁料拌匀，置碟上，盖上保鲜纸，留一开口处疏气，高火煮3-4分钟即成。', NULL, NULL, NULL, '银针粉120克（约3两），冬菇2只，红萝卜、珍珠笋各25克（约6钱半），葱1条，叉烧50克（约1两半），虾仁100克（约2两），蒜头2粒。芡汁料：盐半茶匙，糖1茶匙，牛抽2茶匙，酒1茶匙，水2汤匙，胡椒粉少许，麻油数滴。');
INSERT INTO `recipe` VALUES (3361, 58, '水饺豆沙煎饼', '1.将豆沙及柠檬皮末搅拌均匀，分成10等份，每份揉成小圆球备用。\r\n2.取1张水饺皮，将一份豆沙放在中间，水饺皮周围涂上少许水，上面再盖上一张水饺皮。将豆沙馅稍微压平，水饺子皮四周捏紧，折出花边，依序做好10份。\r\n3.平底锅预热，加入少许色拉油，再将水饺豆沙饼放入，煎至两面呈金黄色即可盛入盘中食用。', '风味独特，香脆爽口，有些酸甜。', NULL, NULL, '水饺皮20张，豆沙1杯，柠檬皮末1大匙，水1大匙。');
INSERT INTO `recipe` VALUES (3362, 58, '酸梅排骨', '1.排骨洗净，抹干水分，在表面刻少许刻痕。\r\n2.用调味料和蒜茸腌排骨半小时。\r\n3.把排骨放在碟上，用保鲜纸包裹，留一开口处疏气，中火煮4分钟。\r\n4.除去保鲜纸，高火煮2分钟，即可供食。', NULL, NULL, NULL, '排骨400克（约10两半），蒜茸2汤匙。调味料：酸梅150克（约4两），黄糖3汤匙，柱侯酱2汤匙，老抽、生抽、玫瑰露酒、姜汁酒各1汤匙。');
INSERT INTO `recipe` VALUES (3363, 58, '酸梅乳鸽', '1.乳鸽洗净，抹干水分。\r\n2.预备调味料。\r\n3.用调味料腌乳鸽约30分钟。\r\n4.用保鲜纸盖著乳鸽，留一开口处疏气，放入微波炉用高火煮10分钟，转用中火煮3分钟，取出斩件上碟。', NULL, NULL, NULL, '乳鸽2只（350克，即约9两半），梅300克（约8两），黄糖4平汤匙，柱侯酱3汤匙，老抽2汤匙半，玫瑰露酒、姜汁、蒜茸各1汤匙。');
INSERT INTO `recipe` VALUES (3364, 58, '五香牛肉', '1.把卤水料（酒除外）放在一个深碗内，用高火煮15分钟。\r\n2.牛肉洗净，放进卤水中，并加入酒，再用中火煮约40分钟。\r\n3.牛肉取出，晾凉后切薄片。\r\n4.蘸汁料除麻油和浙醋外，放炉内用高火煮1分钟，取出加入余下的汁料，便可与牛肉同供食。', NULL, NULL, NULL, '牛肉100克（约10两半），卤汁料：花椒半茶匙，八角2粒，桂皮1块，沙姜5-6块，草果1个（碾碎），茴香半茶匙，干葱1条，盐1平茶匙，冰糖碎半汤匙（约25克，即6钱半），上汤400毫升，酒1汤匙。');
INSERT INTO `recipe` VALUES (3365, 58, '虾米茄子', '1.虾米洗净，浸软，切碎。\r\n2.茄子去皮，放在热水中，加姜片，盖上保鲜纸，高火煮15分钟，沥清水分，排在碟上。\r\n3.汁料、虾米碎、葱粒、榨菜粒及红椒丝同放碗内，盖上保鲜纸，留一开口处疏气。高火煮3分钟取出，淋在茄子上，便可供食。', NULL, NULL, NULL, '茄子300克（约8两），虾米40克（约1两），葱粒2汤匙，榨菜粒1汤匙，红椒丝1茶匙，姜2片，热水250毫升。芡汁料：油、生抽、糖各半汤匙，老抽1茶匙半，麻油1汤匙，水3汤匙。');
INSERT INTO `recipe` VALUES (3366, 58, '香酱鸡', '1.鸡洗净，把调味料和蒜茸混合，搽匀鸡皮，腌约1小时。\r\n2.葱切丝，与姜丝同塞进鸡腔内。\r\n3.鸡腿末端包上铝箔，放盛器中，加上保鲜纸，留一开口处疏气，高火煮8分钟。\r\n4.除去铝箔和保鲜纸，用高火煮2分钟。\r\n5.取出稍晾凉后，即可斩件上碟。', NULL, NULL, NULL, '光鸡1只（900克，即约1斤半），姜丝2汤匙，葱1条，蒜茸3汤匙。调味料：面豉2汤匙，蒜茸辣酱1平汤匙，沙姜粉半茶匙，五香粉1/4茶匙，酒1汤匙，糖1茶匙，生抽2茶匙。');
INSERT INTO `recipe` VALUES (3367, 58, '小蒸包', '1.瘦肉及肥肉同剁烂，加入调味料搅至起胶，分别做成小肉丸。\r\n2.面粉筛匀放入大碗中，慢慢加入滚水，迅速拌匀成软粉团，以少许面粉爽手，将粉团搓成长条形，再分切小圆粒，碾薄成圆形粉皮，放入肉丸做成小笼包形状。\r\n3.蒸笼涂油，铺上菜叶，再涂油在菜叶上，放入小笼包隔水蒸七至八分钟，拌匀沾汁一同上桌，趁热进食。', '齿齿留香。', NULL, NULL, '瘦肉四两，肥肉一两，面粉九两，滚水半杯，椰菜或绍菜叶数片，盐、酒各半茶匙，生抽、生粉各两茶匙，姜茸一茶匙，清水四汤匙，麻油、胡椒粉各少许，沾汁料：姜丝半汤匙，醋两汤匙。');
INSERT INTO `recipe` VALUES (3368, 58, '芝麻元宵', '1.将白糖、芝麻粉与板油丁拌匀揉透，再搓成l0克左右的小圆子，即成馅。\r\n2.将水磨粉加水揉透摘成小粉团，搓圆捏成锅子形，包入馅心，捏拢收成生坯。\r\n3.将生坯放入沸水锅内煮熟,配以各色水果食用。', '色泽油亮，鲜嫩适口。', NULL, NULL, '黑芝麻粉500克，板油丁750克，白糖l000克，水磨粉l000克。');
INSERT INTO `recipe` VALUES (3369, 58, '纸包鸡', '1.鸡肉切成薄片，冬菇浸软后切丝。\r\n2.将鸡肉及冬菇拌入调味料。\r\n3.玻璃纸剪成小张，每小张包入葱段、胡萝卜丝、冬菇及鸡件，折成小包。\r\n4.将以上纸包鸡排放碟上，以高功率加热3至4分钟即可食用。', NULL, NULL, NULL, '鸡肉300克，冬菇6个，葱段酌量，胡萝卜丝1汤匙，玻璃纸1大张，盐1/2茶匙，鲜酱油1.5汤匙，砂糖1茶匙，胡椒粉少许，麻油少许，酒1茶匙，油1匙。');
INSERT INTO `recipe` VALUES (3370, 59, '八仙长寿面', '用乌冬面来制作。用乌鸡，老龟熬制上汤，乌鸡具有滋补的作用，老龟代表长寿。取虾、鱿鱼、海参、黑鱼丸、鲜贝、玉兰片、香菇、蟹柳八种原料寓意八仙。八仙即人们常说的：铁拐李、汉钟离、张果老、何仙姑、兰采和、吕洞宾、韩湘子、曹国舅，八仙面取“八仙庆寿”之意。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3371, 59, '叉烧包', '蒸6分钟。', NULL, NULL, NULL, '叉烧、生抽、老抽、食油、蚝油、糖、香油、粟粉、生粉、色素、蒜肉、干冲肉、面粉。');
INSERT INTO `recipe` VALUES (3372, 59, '豉油捞面', '用蛋面来制作，将面捞热，用豉油鸭摆盘，绿菜心做点缀。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3373, 59, '担担面', '1.用香油将麻酱调稀，葱切成小葱花，芽菜（或用冬菜代替）洗净后用刀剁成细末，将全部调料分成5碗。\r\n2.用开水把面条煮熟，加入小白菜稍烫一下一起捞出也分成5份放入碗中即成。', '口味鲜香，稍微咸辣，面条滑软，别有风味。', NULL, NULL, '面条500克，小白菜（或菠菜）净重250克，香油25克，大油50克，麻酱50克，酱油100克，净葱25克，芽菜50克，辣椒油75克。');
INSERT INTO `recipe` VALUES (3374, 59, '高力豆沙', '1.将豆沙炒好，搓成像小核桃那样的小圆球；鸡蛋白打成雪堆状（要能立而不塌），再将面粉、菱粉放入鸡蛋白里调成稠糊。\r\n2.以鸡蛋糊作为外皮，豆沙为馅心，包成一只只像算盘珠子那样的小丸子，放入清热油锅内炸成金黄色即好。上席时，另放白糖，由食用者蘸着吃。', '外形如硬球，入口则柔软如绵，十分香嫩。', NULL, NULL, '豆沙150克，鸡蛋白5只，面粉50克，菱粉50克。');
INSERT INTO `recipe` VALUES (3375, 59, '过桥米线', '1.把肉料分别切薄片，有味的焯水后漂凉装盘；\r\n2.其余各料另锅焯水，漂凉后切段装盘；\r\n3.香菜、葱切碎和油辣椒及烫过的米线一同上桌；\r\n4.鸡油烧至7成热时装入碗中，倒入烧开的清汤，加调料上桌；\r\n5.食时先将肉片烫至白色，下绿菜稍烫，再下米线，撒少许葱花、香菜即成。', NULL, NULL, NULL, '鸡脯肉，猪肚头，猪腰子，乌鱼肉，水发鱿鱼，油发鱼肚，火腿、香菜、葱头、净鸡块各20克，水发豆皮、白菜心、豌豆尖、葱、豆芽菜、蘑菇各50克，米线200克。');
INSERT INTO `recipe` VALUES (3376, 59, '咖喱牛肉饺', '先煸馅，再烤。', '酥皮。', NULL, NULL, '牛肉，洋葱，咖喱粉，黄油，面粉。');
INSERT INTO `recipe` VALUES (3377, 59, '三色珍珠饺', '煮（火锅）。', '颜色红绿黑，有手指盖大小。', NULL, NULL, '鱼肉，干贝，淀粉，高汤，冬菇，豌豆， 西红柿。');
INSERT INTO `recipe` VALUES (3378, 59, '三鲜锅贴', '煎。', '外焦里嫩。', NULL, NULL, '三鲜馅（虾，海参、鸡蛋），烫面。');
INSERT INTO `recipe` VALUES (3379, 59, '烧麦', '1.将牛羊肉绞成肉末，西葫芦去皮、瓤，擦成丝挤去水分，与肉末一起放在盛器里，加上各种调料，拌成烧麦馅。\r\n2.面粉用开水或半开水和面团，揉匀备用。\r\n3.淀粉过箩备用。 \r\n4.将面团搓成条，揪成小剂（每50克3个），用面杖擀成圆皮，再蘸上干淀粉，用木棰压出花边。', '鲜嫩多汁，香醉适口。', NULL, NULL, '面粉1000克，牛羊肉500克，西葫芦500克，葱50克，黄酱100克，熟油75克，姜3克，盐10克，淀粉50克，味精4克。');
INSERT INTO `recipe` VALUES (3380, 59, '四喜蒸饺', '蒸！', '四方形上有四种不同的颜色。', NULL, NULL, '小麦淀粉，海鲜馅，火腿，蛋黄，木耳，菠菜叶。');
INSERT INTO `recipe` VALUES (3381, 59, '土豆饼', '1.土豆洗干净，蒸熟后去皮切成土豆条，越细越好（看自己的手艺了），与鲜奶、盐拌匀腌20分钟。香葱切成葱花。 \r\n2.平底锅旺火（最好是不沾锅）烧热加油转中火，将100克左右土豆条 倒入锅内，用平铲压成厚度5毫米左右的圆饼，要压实。一面煎黄后小心翻面，两面煎黄后双面撒匀葱花，一定要将葱花压入饼内将来才好吃，待两面焦黄色即可。', NULL, '上桌后可依从食客口味，配椒盐或番茄寿司食用味道都不错，但实际上什么都不沾更香。', NULL, '土豆一斤左右，花生油100克，香葱100克，盐5克，鲜奶少许。');
INSERT INTO `recipe` VALUES (3382, 59, '窝贴', '1.绍菜洗净，放在碗内，加盖，高火煮1分钟半，取出沥清水分，切碎，并压出水分。\r\n2.剁碎瘦肉，混和调味料，和切碎的菜拌匀，分为8等份，置冰箱内冷冻约30分钟。\r\n3.筛粉和盐，加猪油和热水，做成一软粉团，并分成8等份。\r\n4.每份压成约7厘米直径的圆块。\r\n5.在其中一边摺褶干，加入馅料，封口成一窝贴。\r\n6.高火2分钟预热煎碟，加油，再高火2分钟。\r\n7.排上窝贴，高火1分钟，取出加热水25毫升，盖上保鲜纸，留一开口处疏气，中火煮2分钟，便可食用。', NULL, NULL, NULL, '绍菜60克（约1两半），瘦肉60克（约1两半）。调味料：姜汁1茶匙，生抽1茶匙半，糖、粟粉各半茶匙，酒1茶匙，麻油半茶匙，菜汤水1茶匙，熟油1茶匙，胡椒粉少许。粉糯用料：面粉60克（约1两半），猪油1茶匙，盐少许，热水2-2汤匙半。点食料：姜丝、镇江醋各1汤匙。');
INSERT INTO `recipe` VALUES (3383, 59, '小豆粥', '1.红小豆去皮杂物，洗净，锅里放清水5000克，下入红小豆，加碱，先用大火，后用微火，煮至软烂，加入红糖搅匀。 \r\n2.淀粉用凉水调匀，在小豆粥熟时下，下入水淀粉勾芡，使粥富有粘性。最后放入糖桂花，搅匀即可食用。', '具有解毒排脓、利水消肿、清热去湿、健脾止泻的功效。', NULL, NULL, '红小豆1000克，红糖600克，糖桂花50克，淀粉200克，碱适量。');
INSERT INTO `recipe` VALUES (3384, 59, '羊肉杂面', '1.把绿豆面和黄豆面以8：2掺匀，加水和成硬面团，制成细面条。\r\n2.羊肉洗净，切成小块放开水中焯煮，将肉捞出，去掉锅中的原汤杂物。\r\n3.将酱油、盐、葱段、姜块、花椒、大料及焯过的肉放入锅内，加适量水用微火炖至肉烂为止。\r\n4.锅上火，放水烧开，下入杂面条。熟后捞出。', '汤肥肉香，面软利口，是冬季应时小吃，亦可作主食。', NULL, NULL, '羊肉1000克，绿豆面800克，黄豆面200克，酱油150克，盐10克，葱段10克，姜5克，花椒、大料各3克，香菜50克，酸菜50克。');
INSERT INTO `recipe` VALUES (3385, 59, '羊眼包子', '1.把面粉倒在盛器中，加发酵粉拌匀，对水和成发面团，揉均匀，稍饧。\r\n2.羊肉洗净，绞成末。海参切小丁。玉兰片切小丁。冬菇用水泡软去根切碎。干贝用水发透，加葱、姜、料酒蒸软，碾碎。将以上原料加盐、香油、葱花、姜汁、味精拌成馅。\r\n3.将发面揉成长条，揪成小剂。', '营养丰富，深受人们喜爱。', NULL, NULL, '面粉1000克，羊肉250克，海参50克，玉兰片50克，冬菇50克，干贝25克，盐6克，味精3克，香油50克，葱花25克，姜、发酵粉、料酒各适量。');
INSERT INTO `recipe` VALUES (3386, 59, '鱼翅蛋汤饺', '蒸！', NULL, NULL, NULL, '鸡蛋黄，鱼翅丝，葱丝，姜丝，胡萝卜，香菜叶。');
INSERT INTO `recipe` VALUES (3387, 59, '元宝饺', '先蒸后炸。', '元宝饺是清真小吃炸回头演变制成，象征全家人团聚。', NULL, NULL, '野菜，牛肉，半烫面。');
INSERT INTO `recipe` VALUES (3388, 59, '知了饺', '蒸！', '晶莹透明。', NULL, NULL, '虾肉，冬菇，澄面。');
INSERT INTO `recipe` VALUES (3389, 64, '八宝辣酱', '1.切丁的胡萝卜和毛豆分别放入袋中，高火煮3分钟。\r\n2.将调料拌1大匙色拉油用高火煮3分钟。\r\n3.其余的材料全部切丁，里脊肉丁拌淀粉备用。\r\n4.取出煮过的调料，再加入所有配料及2大匙色拉油，覆上微波薄膜，高火煮4分钟即可。', NULL, NULL, NULL, '毛豆2大匙，虾米半大匙，熟笋2大匙，胡萝卜2大匙，小黄瓜2大匙，生花生2大匙，里脊肉100克，黑豆腐干2块，白糖1匙，味精少许，甜面酱1大匙，辣豆瓣酱1匙，色拉油3大匙，淀粉少量。');
INSERT INTO `recipe` VALUES (3390, 64, '白切鸡', '1.鸡洗净，用精盐及味精抹遍全身，放葱、姜入鸡肚内，腌30分钟使其入味。\r\n2.鸡放入盘内，高火20分钟，取出凉后切块即可。', NULL, NULL, NULL, '光鸡1000克，葱6段，姜6片，味精1茶勺，精盐1汤勺。光鸡1000克，葱6段，姜6片，味精1茶勺，精盐1汤勺。');
INSERT INTO `recipe` VALUES (3391, 64, '炒花生', '1.花生洗净装盘，撒上盐。\r\n2.用高火加热4分30秒，炒熟后待凉即可。', NULL, NULL, NULL, '花生1杯，精盐少许。');
INSERT INTO `recipe` VALUES (3392, 64, '炒青菜', '1.青菜洗净切块，用调料腌渍10分钟。\r\n2.装入盘内，加盖高火4分钟 ，中途搅拌一次即可。', NULL, NULL, NULL, '青菜300克，精盐、白糖、味精各1/2小匙，熟色拉油1小匙。');
INSERT INTO `recipe` VALUES (3393, 64, '炒肉丁', '1.将肉丁用绍酒、湿淀粉、精盐少许调匀。\r\n2.将青椒切丁，茭白切丁，拌入肉丁内。\r\n3.装入器皿中，加入白糖、味精、精盐少许和熟油拌和，加盖高火4分钟，期间搅拌一次即可。', NULL, NULL, NULL, '生净猪肉丁100克，茭白150克，青椒150克，绍酒1中匙，精盐1小匙，白糖、味精各半小匙，湿淀粉1中匙，熟油1大匙。');
INSERT INTO `recipe` VALUES (3394, 64, '炒四季豆', '1.刀豆撕筋洗净，切成3厘米左右段，用盐、味精腌15分钟。\r\n2.放入盘内，淋上熟色拉油，加盖高火6分钟。中途搅拌一次即可。', NULL, NULL, NULL, '刀豆300克，精盐1小匙，味精1/2小匙，熟色拉油1大匙。');
INSERT INTO `recipe` VALUES (3395, 64, '东坡肉', '1.刮净皮上污物与余毛，放入沸水锅中焯一下，用冷水洗净，切成1.5厘米见方的肉块。\r\n2.在器皿先放上姜（拍裂〕25克，小葱40克，再放上切好的肉块，加上所有调料，放上余下的10克小葱，封上微波薄膜，先高火4分钟，再改低火25分钟，见肉已酥，再用高火收汁至浓稠即可。', NULL, NULL, NULL, '生净猪五花助肉（去骨带皮）450克，小葱50克，生姜25克，绍酒500克，酱油80克，白糖40克。');
INSERT INTO `recipe` VALUES (3396, 64, '炖鲫鱼', '1.鲫鱼去鳞、肚，洗净后用色拉油少许煎至微黄，放入绍酒、姜片、适量清水煮至滚起。\r\n2.大白菜洗净切块，豆腐切块，放入器皿中，将1连汤同放入器皿中。\r\n3.笋片、金华火腿、黑木耳放在鱼上面，中火20分钟，汤浓放入精盐、胡椒粉各少许调味，即可食用。', NULL, NULL, NULL, '鲫鱼重500克，大白菜250克，豆腐2块，笋、金华火腿片、水发黑木耳各少许，姜2片，色拉油、胡椒粉、精盐各少许，清水、绍酒各适量。');
INSERT INTO `recipe` VALUES (3397, 64, '番茄炒蛋', '1.番茄洗净，用沸水泡后去皮切成块。\r\n2.鸡蛋打散，加入精盐、味精、白糖拌和。\r\n3.盘内淋上熟色拉油，倒入鸡蛋和番茄，高火4分钟，中途搅拌1次即可。', NULL, NULL, NULL, '鸡蛋5只，番茄200克，熟色拉油1大匙，精盐、白糖、味精各1小匙。');
INSERT INTO `recipe` VALUES (3398, 64, '腐乳芥菜', '1.将芥菜洗净，摘短段，沥干水。\r\n2.腐乳搓烂，红辣椒切丝。\r\n3.将芥菜放入盘内，拌入腐乳、精盐、色拉油及红椒丝。加盖高火4分钟即可。', NULL, NULL, NULL, '芥菜250克，腐乳3块，精盐1/2小匙，色拉油2大匙，红辣椒1个。');
INSERT INTO `recipe` VALUES (3399, 64, '海鲜醋鱼', '1.鱼处理后，从腹下剖开，使鱼摊成背部相连的一大片，用刀斩断中间大骨数处，抹上葱段、姜片、绍酒、胡椒粉，置于盘中腌30分钟。\r\n2.嫩姜丝用冷水略泡，挤干水分备用。\r\n3.盘中的鱼覆上微波薄膜，高火12分钟，取出撒上嫩姜丝和葱丝。\r\n4.在一碗中将调料酱油3大匙，米醋2大匙，白糖1大匙，高汤近1杯，调匀后，高火3分钟，再加入调料淀粉2大匙，水1大匙拌匀，高火煮半分钟成芡汁，淋在鱼身上，上桌前撒上少许芝麻油即可。', NULL, NULL, NULL, '鲜鱼600克，葱5段，姜5片，姜丝、嫩姜丝各2大匙，绍酒、胡椒粉、芝麻油各少许，酱油3大匙，米醋2大匙，白糖1大匙，高汤近1杯，淀粉2大匙，水1大匙。');
INSERT INTO `recipe` VALUES (3400, 64, '蚝油茄子', '1.茄子洗净后，切段约5厘米长。\r\n2.将茄子并排于盘中。\r\n3.猪肉末、蒜末、辣椒末、蚝油混合一起，再倒于茄子中。\r\n4.罩上微薄膜，低火加热约5分钟即可。', NULL, NULL, NULL, '茄子2个，猪肉末适量，蒜末、辣椒末少许，蚝油适量。');
INSERT INTO `recipe` VALUES (3401, 64, '红烧带鱼', '1.带鱼洗净切成块，用调料拌和腌20分钟。\r\n2.带鱼放入盘子中，高火5分钟即可。', NULL, NULL, NULL, '带鱼300克，绍酒、酱油、葱段、姜末各2大匙，精盐少许，白糖1小匙，味精半小匙，熟色拉油1大匙。');
INSERT INTO `recipe` VALUES (3402, 64, '红烧凤翅', '1.鸡翅膀洗净，放入调料（a）中腌30分钟。\r\n2.色拉油2大匙高火2分钟，放入腌好的鸡翅膀，高火5分钟，撒上少许葱花即可。', NULL, NULL, NULL, '鸡翅膀8只，葱花少许，色拉油2大匙；（a）酱油、绍酒各1大匙，姜6片，白糖1小匙，芝麻油1中匙。');
INSERT INTO `recipe` VALUES (3403, 64, '红烧狮子头', '1.将猪肉末加调料（a）充分拌匀，做成4只丸子。\r\n2.调料（b）中加入高汤调匀，放入肉丸，覆上微波薄膜，高火7分钟后，倒出剩汁。\r\n3.将青菜洗净后对半切,再加适量的色拉油、精盐、味精。放于器皿内与剩汁高火2分钟，将剩汁用湿淀粉勾芡。\r\n4.将青菜切边，中间排入肉丸，淋上剩汁即可。', NULL, NULL, NULL, '夹心猪肉末300克，青椒250克。（a）精盐、味精、姜末各1匙，绍酒、葱末各1大匙，芝麻油半匙。（b）色拉油5大匙，味精1匙，绍酒1匙，白糖1匙。（c）湿淀粉1.5大匙，高汤半杯。');
INSERT INTO `recipe` VALUES (3404, 64, '红烧蹄膀', '1.将蹄膀去毛、洗净沥干，与香菇丝一起用冰糖、酱油、八角腌30分钟。\r\n2.将蹄膀放入有1大匙色拉油的器皿内高火8分钟后，放入绍酒、姜、葱末再高火10分钟。\r\n3.将菜心加水、色拉油高火2分钟后取出，排放于盘的四周。\r\n4.把淀粉和水1大匙与肉汁拌匀，高火1分钟后淋上即可。', NULL, NULL, NULL, '蹄膀1只，青菜心18棵，水发香菇5朵，冰糖3匙，酱油5大匙，八角（即大料）5粒，姜3片，绍酒、葱末、淀粉、水各1大匙，色拉油2大匙。');
INSERT INTO `recipe` VALUES (3405, 64, '红烧猪肉', '1.用叉子把肉穿五六个洞，淋上调味（a），再用棉绳绑紧。\r\n2.将肉浸在搅拌均匀的调味（b）里大半天，期间用筷子上下翻动。\r\n3.将肉放在微波炉专用的竹笼或网上，不罩微波薄膜，转1分钟。\r\n4.取出上下翻动，并用刷子涂上腌汁，再转6分钟，中途需多涂抹几次。\r\n5.取出切薄片，再装饰蔬菜即可。', NULL, NULL, NULL, '生净猪里脊肉400克，生菜适量，（a）精盐、胡椒各少许；（b）白糖半大匙，酱油半杯，酒2大匙，洋葱（泥）、葱白均适量。');
INSERT INTO `recipe` VALUES (3406, 64, '花生鲤鱼', '1.花生用清水浸40分钟，然后加入适量水，高火10分钟。\r\n2.鲤鱼洗净抹干水，加胡椒粉、精盐各少许。', NULL, NULL, NULL, '鲤鱼1条，细粒花生肉150克，姜数片，葱4根，香菜2棵，绍酒、生抽各1汤匙，色拉油7汤匙，水1.5杯，芝麻油、胡椒粉、精盐、果皮（浸软切丝）各少许。');
INSERT INTO `recipe` VALUES (3407, 64, '滑溜肉丝', '1.将肉切薄片，用蛋白、精盐腌10分钟，再泡在湿淀粉及1大匙水里，香菇去蒂切丝，小黄瓜切片。\r\n2.将色拉油2大匙高火2分钟后放入腌泡好的肉丝及香菇丝，高火4分钟。\r\n3.取出后放入酱油、米醋、绍酒及小黄瓜，高火1分钟，撒上葱段、辣椒段即可。', NULL, NULL, NULL, '猪里脊肉400克，小黄瓜1条，水发香菇5朵，鸡蛋清1个，精盐半小匙，淀粉、水、酱油、葱段、辣椒段各1大匙，色拉油2大匙，米醋1中匙，绍酒1匙。');
INSERT INTO `recipe` VALUES (3408, 64, '黄瓜塞肉', '1.将黄瓜去皮，挖去瓜心，切成3厘米厚的空心节。\r\n2.将猪肉剁成肉末，拌入冬菇末，精盐、胡椒粉、淀粉、绍酒，打入生鸡蛋拌匀。\r\n3.将黄瓜节排列在抹有熟油的器皿中，然后将肉末分别塞入黄瓜节的空心部分。\r\n4.淋上一半酱油汤汁，中火3分钟，取出后，将黄瓜翻面，中间的往边缘移，淋上剩余的酱油汤汁，将葱末均匀撒于肉末表面，再中火3分钟，保持片刻即可食用。', NULL, NULL, NULL, '老黄瓜3条，瘦猪肉120克，水发冬菇8只（切末），鸡蛋1个葱末少许，精盐、胡椒粉、淀粉、熟油、酱油、汤汁、绍酒各适量。');
INSERT INTO `recipe` VALUES (3409, 64, '火腿冬瓜', '1.冬瓜去皮，切6.5厘米长、3.5厘米宽、1厘米厚的冬瓜条，约切15片，每片中间切一刀但要连片。香菇泡软。\r\n2.火腿切成与冬瓜大小相同的薄片，葱切小段。\r\n3.火腿片夹入冬瓜内，放入深盘，香菇3朵放在中间，加入高汤、葱及调料（a），覆上微波薄膜，高火蒸18分钟呈透明状即可。', NULL, NULL, NULL, '冬瓜500克，火腿15片，香菇3朵。（a）绍酒半大匙，白糖半大匙，精盐1匙，葱1根，高汤2杯。');
INSERT INTO `recipe` VALUES (3410, 64, '煎鱼', '1.鱼洗净沥干后，鱼身划斜刀，抹上调料（a）腌30分钟后，再把淀粉抹于鱼身上。\r\n2.烤盘高火7分钟，铺上铝箔纸，加色拉油，放上鱼，高火3.5分钟，翻面再高火3.5分钟即可。', NULL, NULL, NULL, '鱼一条，铝箔纸1张，色拉油3大匙，淀粉2大匙；（a）姜5片，精盐1匙，绍酒、酱油各1大匙，胡椒粉适量。');
INSERT INTO `recipe` VALUES (3411, 64, '韭菜花鱼丸', '1.韭菜花洗净，去除头尾的老茎及花的部分，切成段，鱼丸切成块。\r\n2.红辣椒切成段，与葱段及色拉油放入1盘内，高火爆香2分钟。\r\n3.鱼丸、韭菜花、爆香的色拉油放入1盘内，加精盐、绍酒、胡椒粉拌匀，加盖高火6分钟即可。', NULL, NULL, NULL, '韭菜花250克，鱼丸200克，葱段4段，色拉油3大匙，精盐半小匙，绍酒1小匙，胡椒粉少许，红辣椒1只。');
INSERT INTO `recipe` VALUES (3412, 64, '酒炖甲鱼', '1.热水烫甲鱼，去净潺胶及外层薄膜，过冷水，切小块。\r\n2.水发冬菇去蒂，瘦肉切片，用少许生抽、淀粉拌匀。\r\n3.姜切片，葱切丝，蒜肉原粒用色拉油略炸至金黄色捞出，陈皮浸软切丝。\r\n4.器皿中放入色拉油2汤匙，略爆甲鱼、姜片、再加入肉片同爆匀，加入绍酒少许、上汤（或清水）2杯，加盖中火20分钟。\r\n5.加入蒜子、冬菇、陈皮丝，低火8分钟，放入适量生抽、精盐调味，加入白酒，高火5分钟，食时用长葱丝撒上即可。', NULL, NULL, NULL, '生猛甲鱼约500克，水发冬菇6朵，瘦肉200克，陈皮1块，蒜子肉100克，姜、葱各适量，白酒4-6汤匙，淀粉、精盐、生抽、绍酒各少许，色拉油2汤匙，上汤（或清水）2杯。');
INSERT INTO `recipe` VALUES (3413, 64, '咖喱鸡块', '1.嫩鸡与土豆切成块，用精盐腌15分钟。\r\n2.调料（a）与鸡2块拌匀。\r\n3.鸡块与土豆放入盘内，加盖高火8分钟，中途搅拌1次即可。', NULL, NULL, NULL, '嫩鸡400克，土豆150克，精盐1中匙；（a）咖喱粉半包，绍酒、熟色拉油各1大匙，葱段2大匙，味精半小匙，淀粉、姜丝、白糖各1小匙。');
INSERT INTO `recipe` VALUES (3414, 64, '开洋大白菜', '1.虾米用水泡软后，取出放于碗中，加色拉油，用高火爆香2分30秒。\r\n2.大白菜斜刀切成4厘米宽的小片，放在深盘中，加入虾米、调料色拉油、绍酒、精盐及姜片，覆上微波薄膜，高火煮3分钟后，再用中火焖4分钟。\r\n3 取出拌入调料淀粉1大匙，水2大匙，覆上微波薄膜，高火煮2分钟即可。', NULL, NULL, NULL, '大白菜300克，虾米2大匙，色拉油4大匙，绍酒1匙，精盐1匙，淀粉1大匙，水2大匙，姜3片，调料色拉油3大匙。');
INSERT INTO `recipe` VALUES (3415, 64, '烤山芋', '1.选择大小略同、较瘦长的红心山芋2个，洗净抹干，分别用纸巾包好。\r\n2.高火烘8分钟即可，中途翻一次。', NULL, NULL, NULL, '红心山芋300克，纸巾2张。');
INSERT INTO `recipe` VALUES (3416, 64, '辣子鸡丁', '1.鸡腿切丁放入大碗中，加入调料（a）搅拌均匀，小黄瓜切丁，红辣椒切片备用。\r\n2.调料（b）放入一锅内，高火爆香3分钟，加入鸡丁，再加盖高火3分钟，再加入小黄瓜、红辣椒及调料（c）拌匀，加盖后继续高火2.5分钟即可。', NULL, NULL, NULL, '鸡腿1只，小黄瓜3根，红辣椒1只，调料（a）鸡蛋清1只，精盐、绍酒、淀粉各1匙，胡椒粉少量；（b）蒜末、葱末1大匙，色拉油3大匙；（c）精盐半匙，白糖1匙。');
INSERT INTO `recipe` VALUES (3417, 64, '萝卜丝汤', '1.将萝卜洗净切成丝，放开水中焯一下，沥干水分。\r\n2.器皿放油烧热，下葱，即放萝卜丝，高火2分钟，加高汤、虾皮、精盐、味精，烧开即成。', NULL, NULL, NULL, '萝卜250克，虾皮（或小海米）15克，猪油20克，葱末2克，高汤适量，精盐、味精各少许。');
INSERT INTO `recipe` VALUES (3418, 64, '梅花肉片', '1.将五花肉切片，放入蚝油1大匙及水2大匙腌30分钟。\r\n2.器皿内放熟猪油2大匙，放入腌好的五花肉，高火3分钟后，取出翻面，再高火3分钟。取出排盘，添加香菜若干即可。', NULL, NULL, NULL, '生净猪五花肉400克，蚝油1大匙，水2大匙，香菜若干，熟猪油2大匙。');
INSERT INTO `recipe` VALUES (3419, 64, '泡菜猪肉', '1.将豆腐沥干水分，不罩微波薄膜，转2分钟。\r\n2.切成适当大小块。\r\n3.将猪肉切适当大小，放入容器中，加调味，罩上微波薄膜或盖子，转2分钟。\r\n4.把豆腐、泡菜加入，搅拌均匀，罩上微波薄膜或盖子，转3分钟。\r\n5.取出再搅拌一次，以同样的方法，转2分钟，盛盘撒上葱花即可。', NULL, NULL, NULL, '生净猪肉片200克，豆腐1块，泡菜200克，酱油1大匙，绍酒3大匙，芝麻油1小匙，葱花少许。');
INSERT INTO `recipe` VALUES (3420, 64, '皮蛋豆腐', '1.榨菜切片，泡入水中以去除盐分。\r\n2.皮蛋切适当大小，榨菜拧干水分切末，葱切末。\r\n3.豆腐放在微波炉专用网上，不罩微波薄膜，加热3分30秒。\r\n4.用汤匙把豆腐切成适当大小。\r\n5.把豆腐、榨菜和皮蛋一起搅拌均匀，再加调料。', NULL, NULL, NULL, '豆腐1块，皮蛋1个，榨菜200克，芝麻油1大匙，精盐少许，葱1根。');
INSERT INTO `recipe` VALUES (3421, 64, '茄汁肉片', '1.将肉片用调料拌和。\r\n2.装盘加盖高火4分钟即可，期间搅拌一次。', NULL, NULL, NULL, '生净猪肉片300克，精盐、白糖各1小匙，干淀粉、熟油各1中匙，味精半小匙，番茄沙司2大匙。');
INSERT INTO `recipe` VALUES (3422, 64, '青椒干丝', '1.用调料和香干丝、青椒丝拌和。\r\n2.装入盘中，高火3分钟，中途搅拌一次即可。', NULL, NULL, NULL, '香干丝300克，青椒丝50克，精盐1小匙，味精1/2小匙，熟色拉油1大匙。');
INSERT INTO `recipe` VALUES (3423, 64, '青椒肉丝', '1.将猪腿肉、青椒切成丝。\r\n2.用调料拌和。\r\n3.装入器皿内，高火4分钟即可，期间搅拌一次。', NULL, NULL, NULL, '生精猪腿肉100克，绍酒、精盐各1小匙，淀粉1中匙，味精半小匙，色拉油1大匙，青椒200克。');
INSERT INTO `recipe` VALUES (3424, 64, '清炒猪肝', '1.将猪肝洗净切成薄片，用绍酒、葱、姜汁腌10分钟。\r\n2.加入一器皿中，高火4分钟即可，期间翻拌2次。', NULL, NULL, NULL, '猪肝300克，绍酒、酱油、熟油、葱、姜汁各1大匙，干淀粉1中匙，味精半小匙。');
INSERT INTO `recipe` VALUES (3425, 64, '清蛋糕', '1.鸡蛋打散至起泡沫，和白糖、牛奶、香精一起拌和。\r\n2.将面粉和发酵粉倒入蛋中拌匀。\r\n3.将拌好的粉料分批装盘，高火5分钟，待面黄即可。', NULL, NULL, NULL, '鸡蛋2只，面粉200克，牛奶半瓶，发酵粉10克，白糖200克，香精少许。');
INSERT INTO `recipe` VALUES (3426, 64, '清蒸甲鱼', '1.甲鱼洗净，用刀在腹部划十字，绍酒、精盐、味精、胡椒粉抹于其身。\r\n2.葱洗净，切成2段，与姜丝摆放于甲鱼背上。\r\n3.放入汤碗，加入熟色拉油及金华火腿片，加盖高火15分钟即可。', NULL, NULL, NULL, '甲鱼600克，葱4根，姜丝适量，胡椒粉、绍酒、精盐各1小匙，味精半小匙，熟色拉油1中匙，金华火腿片50克。');
INSERT INTO `recipe` VALUES (3427, 64, '清蒸鱼', '1.白肉鱼撒上精盐、胡椒粉、白酒，腌一会儿，使味道渗透。\r\n2.番茄底部用力画个十字，用热水烫过，便可把皮撕开，然后切丁，洋葱切薄片。\r\n3.玉蕈去蒂撕开，柠檬切片。\r\n4.白肉鱼、番茄、洋葱、玉蕈、柠檬分成4等份，各自放入盘中。乳酪也分成4等份，放在上面，撒上白酒。\r\n5.罩上微波薄膜，高火2分钟，取出即可。', NULL, NULL, NULL, '白肉鱼4片，番茄1只，洋葱少许，玉蕈1把，柠檬约1个，白酒3大匙，比萨用乳酪80克，精盐、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (3428, 64, '肉泥豆腐', '将豆腐切成小块，倒入肉末、精盐、味精适量轻拌，使其均匀，覆上微波薄膜，中火8分钟即可。', NULL, NULL, NULL, '肉末250克，豆腐3方块，精盐适量，味精少许。');
INSERT INTO `recipe` VALUES (3429, 64, '丝瓜油条汤', '1.将新鲜油条切成段。丝瓜切去蒂和把，轻轻刮去皮，洗净，切成块。葱切段。\r\n2.器皿放油，放入葱爆香，即放入丝瓜迅速高火，随加3杯清水，高火10分钟，待滚片刻，以精盐、味精调味，加进油条段即成。', NULL, NULL, NULL, '丝瓜250克，油条2条，花生油20克，精盐3克，味精1克，葱2克。');
INSERT INTO `recipe` VALUES (3430, 64, '蒜泥白肉', '1.将肉洗净，加入葱姜。\r\n2.器皿中放1大匙色拉油，把加好葱姜的肉放入器皿中，高火4分钟后取出，切薄片排盘。\r\n3.淋上拌匀的蒜泥、白醋、酱油、辣椒油即可。', NULL, NULL, NULL, '洗净带皮猪后腿250克，葱2根，姜3片，蒜泥、色拉油各1大匙，白糖、米醋各1小匙，酱油2大匙，辣椒油少许。');
INSERT INTO `recipe` VALUES (3431, 64, '糖醋香鱼', '1.鱼身两面每隔1.5厘米用刀斜切（刀深需触及大骨），调料（a）挤成汁，均匀擦至鱼身（在每一切开之鱼肉缝擦匀），腌30分钟，烧时再涂上鸡蛋黄及淀粉于鱼身各处，需仔细涂上，紧压鱼使其呈游水的姿势，备在盘内。\r\n2.炸油装入长椭圆形容器中，高火10分钟，提起鱼尾，将鱼顺着游水姿势入油中，高火3分钟拿出。\r\n3.调料（b）高火爆香3分钟，倒入调料（c），再高火2分钟，取出，淋在鱼上，撒上调料（c），趁热吃。', NULL, NULL, NULL, '鱼一条，鸡蛋黄1只，炸油2.5杯，淀粉半匙；（a）绍酒2大匙，葱2根，姜2片，胡椒粉半匙，精盐1匙，淀粉适量；（b）色拉油3大匙，蒜末1大匙，洋葱丝少量；（c）青椒丝半只，白糖、米醋、番茄酱、水各6大匙，精盐半匙；（d）葱丝、香菜、姜丝各2大匙，红辣椒丝1大匙。');
INSERT INTO `recipe` VALUES (3432, 64, '五香豆干', '1.将猪肉整块抹上调料（a），覆上微波薄膜，转4分钟，取出待凉，切薄片，豆腐干切斜薄片，青、红辣椒切小块。\r\n2.器皿内放色拉油及调料（c），高火2分钟，再放入其它材料及调料（b），拌匀，再高火4分钟即可。', NULL, NULL, NULL, '猪腿肉500克，五香豆腐干2块，青辣椒1个，红辣椒2个，调料（a）绍酒1匙，食盐半匙，葱2根，姜3片；（b）甜面酱、白糖各2匙，辣豆瓣酱、酱油、绍酒、水各1匙；（c）蒜末1匙；（d）色拉油1.5匙。');
INSERT INTO `recipe` VALUES (3433, 64, '咸蛋蒸肉饼', '1.生的咸鸭蛋剥开，取蛋黄2份、鸡蛋清1份备用。\r\n2.肉末和葱末、生咸鸭蛋的蛋清搅拌均匀，揉成肉饼状，置于盘中，再将2只生咸鸭蛋的蛋黄分置于肉饼上面。\r\n3.覆上微波薄膜，高火15分钟即可。', NULL, NULL, NULL, '肉末600克，生咸鸭蛋2只，葱末1大匙。');
INSERT INTO `recipe` VALUES (3434, 64, '香菜榨菜肉', '1.将榨菜切薄片泡水10分钟后，再捞起拧干。\r\n2.将红辣椒切大块，木耳切适当大小，姜切末。\r\n3.把猪肉末、调料（a）一起放入器皿中，拌匀，加入榨菜、红辣椒和姜末完全拌匀。\r\n4.放3于抹色拉油的器皿中，把其压至1－2厘米的厚度，罩上微波薄膜，高火4分钟。\r\n5.撒上香菜，蘸米醋、酱油即可。', NULL, NULL, NULL, '猪肉末150克，榨菜20克，木耳4朵，调料色拉油少许，红辣椒1个，姜1片；（a）酱油、水、白糖各半大匙，胡椒粉、淀粉、米醋、香菜各少许。');
INSERT INTO `recipe` VALUES (3435, 64, '香葱鲤鱼', '1.鲤鱼两面各斜切3刀，抹上精盐、绍酒，使之入味后置于盘中备用。\r\n2.调料（a）放入一碗中，搅拌均匀，高火3分钟，取出后再加入调料（b），拌匀淋到鱼身上，覆上微波薄膜，高火8分钟后，倒出汤汁于另一碗中备用。\r\n3.汤汁及调料（c）混合，高火2分钟，取出淋于鱼身上，撒上调料（d）即可。', NULL, NULL, NULL, '鲤鱼1条，调料（a）葱末、姜末、蒜末各1大匙，辣椒末半大匙，辣豆瓣酱1.5匙，色拉油2大匙；（b）精盐、白糖各半匙，鸡精、米醋各1匙，酱油2.5匙，酒酿（或酒）1大匙，高汤半杯；（c）淀粉、水各1大匙；（d）葱花、辣椒（切花）各1大匙，芝麻油1匙。');
INSERT INTO `recipe` VALUES (3436, 64, '香芹肉丸', '1.将芹菜、葱切末。\r\n2.将芹菜末、葱末加入肉末内，加上调料，充分搅拌，做成椭圆形的丸子，约可做30只，排于器皿中，覆微波薄膜，高火4分30秒，趁热与香菜同食即可。', NULL, NULL, NULL, '五花肉末300克，芹菜1棵，精盐少许，味精半匙，白糖1匙，酱油、芝麻油、酒各1大匙，胡椒粉半匙，香菜适量，葱6根。');
INSERT INTO `recipe` VALUES (3437, 64, '洋葱肉丝', '1.将猪腿肉切成丝，用绍酒、湿淀粉拌匀。\r\n2.将洋葱切丝，与肉丝拌和，并加入精盐、白糖、味精、色拉油，调匀。\r\n3.装入器皿内加盖高火4分钟，期间搅拌一次。', NULL, NULL, NULL, '生猪腿肉150克，洋葱200克，绍酒、精盐、白糖、湿淀粉各1小匙，味精半小匙，色拉油1大匙。');
INSERT INTO `recipe` VALUES (3438, 64, '油焖茭白', '1.将茭白洗净，切1寸长条块，用酱油、精盐、白糖、味精拌匀，腌渍15分钟。\r\n2.装入盘内，淋上色拉油，加盖高火7分钟。中途搅拌一次即可。', NULL, NULL, NULL, '茭白（去壳）300克，酱油1大匙，精盐少许，白糖1小匙，味精1/2小匙，色拉油1大匙。');
INSERT INTO `recipe` VALUES (3439, 64, '鱼香肉丝', '1.把猪里脊丝用精盐、绍酒、湿淀粉拌匀。\r\n2.器皿中放入色拉油1匙（余下的1匙拌入肉丝中），并加入除红椒油外所有调料，覆上微波薄膜，高火5分钟后取出，淋上红椒油即可。', NULL, NULL, NULL, '生猪里脊丝300克，红椒末少许，葱、蒜末、郫县豆瓣酱、酱油、绍酒、湿淀粉各1中匙，色拉油2匙，白糖、精盐、米醋、红椒油1小匙。');
INSERT INTO `recipe` VALUES (3440, 64, '糟溜鱼片', '1.鱼去骨除皮得净肉（约500克），斜切大薄片，用调料（a）拌腌20分钟，再拌入调料（b）。\r\n2.调料（c）中加入色拉油，高火2分钟至滚后，拌入鱼片，再高火4分钟，烧熟后，淋上熟色拉油拌匀。', NULL, NULL, NULL, '草鱼750克，调料色拉油4大匙，熟色拉油4大匙；（a）绍酒、精盐各半匙；（b）鸡蛋清1只，淀粉半大匙；（c）葱末、姜末各1大匙，酒酿1.5大匙，精盐、白糖各1匙，淀粉3大匙，热高汤半杯，绍酒4大匙。');
INSERT INTO `recipe` VALUES (3441, 64, '榨菜肉末', '1.将榨菜切片，浸入水中，木耳泡软切成适当大小，红辣椒切小块，姜切末。\r\n2.把肉末、榨菜、木耳、红辣椒、姜及调料（a）倒入器皿中，搅拌均匀，使其产生粘性为止。\r\n3.放2于抹上色拉油的器皿中，把其压至1－2厘米的厚度。\r\n4.罩上微波薄膜或盖子，高火6分30秒。\r\n5.取出后，再撒上葱白、香菜即可。', NULL, NULL, NULL, '猪肉末300克，榨菜40克，木耳3朵，调料色拉油1小匙，葱白、香菜各适量；（a）酱油、水、绍酒各一大匙，淀粉2小匙，白糖1小匙，胡椒粉少许，姜1片，红辣椒1个。');
INSERT INTO `recipe` VALUES (3442, 64, '蒸糕', '1.充分筛析面粉，搁置一边备用。打鸡蛋3只，分别取其蛋清和蛋黄。\r\n2.取一较大的容器，搅蛋清至奶油状。\r\n3.逐渐加白糖于其中，搅匀。\r\n4.将蛋黄分别加入，搅拌至发泡状态。\r\n5.将筛好的面粉加入其中，与调好的蛋液充分搅混拌匀。 \r\n6.加入葡萄干，搅混拌匀。\r\n7.取容器，内侧薄抹色拉油或黄油。然后，灌入调好的面粉，抹平表面。覆盖一纸巾，高火5分钟。待容器凉后取出，切成薄片。', NULL, NULL, NULL, '丝瓜250克，油条2条，调料:白糖70克，色拉油或黄油（抹涂用）少许。');
INSERT INTO `recipe` VALUES (3443, 64, '醉鸡', '1.鸡对半对剁开后，放入器皿内加上调料（a）后，加盖高火20分钟后，趁热抹上精盐，凉后切块，汁液备用。\r\n2.汁液、白酒及鸡块一起放入一大碗中，浸泡24小时，使味道完全进入可食用。', NULL, NULL, NULL, '鸡1只，调料:精盐1大匙，白酒半瓶；（a）葱6段，姜4片，水2杯。');
INSERT INTO `recipe` VALUES (3444, 57, '白菜绿豆饮', '将白菜根茎洗净切片，与绿豆芽同煮热服，每日2－3次。', NULL, NULL, NULL, '白菜根茎1个，绿豆芽30克。');
INSERT INTO `recipe` VALUES (3445, 57, '北芪枸杞子炖乳鸽', '将上述原料一起放沙锅内加水适量，炖熟，饮汤吃鸽肉。', NULL, NULL, NULL, '北芪50克，枸杞子15克，乳鸽1只（去毛和内脏）。');
INSERT INTO `recipe` VALUES (3446, 57, '菠菜麻油拌芹菜', '将芹菜去根叶洗净切段，入沸水中焯3分钟捞出，菠菜洗净切几刀，入沸水中焯一下捞出，放入盆中，和入调料拌匀即成，可佐餐食用。', NULL, NULL, NULL, '菠菜250克，嫩芹菜250克，麻油、盐、味精、食醋适量。');
INSERT INTO `recipe` VALUES (3447, 57, '薄荷糖', '将红糖放入铝锅内，加水少许，用文火熬稠，加入薄荷粉，调匀，再继续熬至粘手起丝离火。并将其倒入涂有熟菜油的搪瓷盘内，待冷后取出，切成条状或小方块，随时食用。', NULL, NULL, NULL, '薄荷粉30克，红糖500克。');
INSERT INTO `recipe` VALUES (3448, 57, '炒鹌鹑', '1.将鹌鹑放在水中淹死，去毛和内脏，洗净血水，切成长宽各为1.5厘米的块；萝卜切成长3厘米、宽1.5厘米的块，备用。\r\n2.将锅置于武火上，放上菜油烧沸后将鹌鹑放入锅内，用锅铲反复翻炒到变色，再将萝卜放入混炒，然后放入葱、生姜末、料酒、醋，加水少许，煮数分钟，待鹌鹑肉熟即成。', NULL, NULL, NULL, '鹌鹑2只，萝卜200克，菜油、葱、生姜、食盐、料酒、味精适量。');
INSERT INTO `recipe` VALUES (3449, 57, '冬瓜粥', '冬瓜不去皮，洗净，切成小块，同粳米煮粥。', NULL, NULL, NULL, '冬瓜60克，粳米30－60克。');
INSERT INTO `recipe` VALUES (3450, 57, '高梁黑豆大枣饭', '将高梁用水泡软，黑豆打成粗粒亦用水泡软，大枣用开水洗净备用。将高梁、黑豆粒加少许白砂糖和匀。碗内先排放大枣，再将高梁、黑豆放入碗内蒸熟透即成。食用时用盘翻倒并将剩下之白砂糖撒于枣面上。', NULL, NULL, NULL, '红高梁50克，黑豆20克，大枣10枚，白曲2克，白砂糖30克。');
INSERT INTO `recipe` VALUES (3451, 57, '葛根胡萝卜粥', '将葛根洗净切片，加水磨成浆，取淀粉晒干备用。将胡萝卜切成颗粒，粳米洗净，一同放入铝锅中，加水适量，武火烧沸，文火熬至半熟，加入葛根粉，熬成粥，可佐餐食用。', NULL, NULL, NULL, '葛根粉50克，胡萝卜200克，粳米100克。');
INSERT INTO `recipe` VALUES (3452, 57, '枸杞桂圆蛋糖水', '先将枸杞子、桂圆一起加水煮，待蛋熟后去壳，再加入冰糖，稍煮片刻即可食用。', NULL, NULL, NULL, '枸杞子15克、桂圆肉10克、鸡蛋2个、冰糖30克。');
INSERT INTO `recipe` VALUES (3453, 57, '枸杞子炖羊脑', '1.将枸杞子拣选洗净，羊脑洗净（注意不要把羊脑碰破），放入铝锅内，加水适量，放入食盐、葱、生姜、料酒，隔水炖熟。\r\n2.食用时，加少许味精即可。', NULL, NULL, NULL, '枸杞子50克，羊脑1具，葱、生姜、食盐、料酒、味精适量。');
INSERT INTO `recipe` VALUES (3454, 57, '枸杞子黄精瘦肉汤', '将上述原料一起煮汤，去黄精后调味食用。', NULL, NULL, NULL, '枸杞子15克、黄精10克、猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3455, 57, '归地红烧羊肉', '1.将羊肉去筋去骨，切成小块，入沸水焯去血水后撇沫，漂净。2味药物洗净后装入纱布袋中，封口待用。干姜切片。\r\n2.将羊肉、纱布袋、干姜均放入锅中，加清水适量同煮，用文火煎1小时，取药袋弃之，再用武火烧沸，加酱油、黄酒、盐适量，移至文火煮至酥烂，然后移至武火，加糖略收汁即可。', NULL, '本膳份量为每人份。忌用铜器烹调。不宜与萝卜、葱白、韭白、薤白、南瓜同食。上感发热、便干便秘、高热大渴、痰黄稠、口腔咽喉肿痛、腹泻、上腹部胀者禁用。', NULL, '羊肉200克，当归、生地各15克，干姜10克，黄酒、盐、糖、酱油适量。');
INSERT INTO `recipe` VALUES (3456, 57, '桂元肉粥', '一并煮粥，早、晚服用（可当早餐）。', NULL, NULL, NULL, '桂元肉15克，红枣3一5枚，粳米100克。');
INSERT INTO `recipe` VALUES (3457, 57, '红枣糯米粥', '先将糯米加水煮至8成烂以后，再放红枣煮5分钟即可食用。', NULL, NULL, NULL, '红枣10枚，糯米100克。');
INSERT INTO `recipe` VALUES (3458, 57, '胡萝卜豆腐丸子', '将胡萝卜洗净，剁成菜泥，与等量的豆腐混合后拌匀，再加上盐、味精、姜葱末、湿淀粉适量，拌匀后制成小丸子，下油锅炸熟后便可食用。', NULL, NULL, NULL, '胡萝卜，豆腐。');
INSERT INTO `recipe` VALUES (3459, 57, '花生粥', '花生45克，洗净，连衣皮捣碎，加入粳米100克，同煮为粥，待熟时加入冰糖少许，即可食用，可当早餐，可长期食用。', NULL, NULL, NULL, '花生45克，粳米100克');
INSERT INTO `recipe` VALUES (3460, 57, '淮山药芝麻糊', '1.将粳米洗净，用清水浸泡1小时，捞出滤干；淮山药切成小颗粒，黑芝麻炒香。将以上三物放入盆中，加水和鲜牛奶拌匀，磨碎后滤出细茸待用。\r\n2.在锅中加入清水、冰糖，溶化过滤。将冰糖水放入锅中，继续烧开后，将芝麻水慢慢倒入锅内，加入玫瑰糖，不断搅拌成糊，熟后起锅即成。', NULL, NULL, NULL, '淮山药15克，黑芝麻120克，玫瑰糖6克，鲜牛奶200克，冰糖120克，粳米60克。');
INSERT INTO `recipe` VALUES (3461, 57, '黄精炖猪瘦肉', '1.将黄精、猪瘦肉洗净，分别切成长3厘米、宽1.5厘米的小块。\r\n2.将黄精和猪瘦肉块放入瓦锅（砂锅）内，加水适量，放入葱、生姜、食盐、料酒，隔水炖熟。\r\n3.食用时，加味精少许，吃肉喝汤。', NULL, NULL, NULL, '黄精50克，猪瘦肉200克。葱、生姜、料酒、食盐、味精适量。');
INSERT INTO `recipe` VALUES (3462, 57, '莲子百合煨瘦肉', '1.莲子去芯，用清水把莲子、百合洗净；猪瘦肉洗净，切成长3厘米、厚1.5厘米的块。\r\n2.将莲子、百合、猪瘦肉放入铝锅内，加水适量，再加入葱、生姜、食盐、料酒。用武火烧沸，文火煨炖1小时即成。\r\n3.食用时，加少量味精，吃莲子、百合、猪肉，喝汤。', NULL, NULL, NULL, '莲子50克，百合50克，猪瘦肉250克，葱、生姜、食盐、料酒、味精适量。');
INSERT INTO `recipe` VALUES (3463, 57, '绿豆洁肤粉', '绿豆干粉皮在热水中浸泡半小时，捞出切成1厘米宽的长条；香菇、木耳用水泡发；笋、叉烧肉、香菇、木耳切成5厘米长的细丝；蒜苔切成5厘米长的段。葱切成碎末。混合调料与鸡汤合并在一起。植物油下锅烧热，入葱末煸炒，再下叉烧肉、香菇、笋、木耳、蒜薹，然后下粉皮，最后倒入鸡汤。煮开后，淋上麻油、醋即成。', NULL, NULL, NULL, '绿豆干粉皮2张，香菇2枚，木耳20克，笋30克，叉烧肉150克，蒜苔50克，葱少许，鸡汤半杯。');
INSERT INTO `recipe` VALUES (3464, 57, '麦芽青皮饮', '共煎水服之。', NULL, NULL, NULL, '麦芽30克，青皮10克。麦芽30克，青皮10克。');
INSERT INTO `recipe` VALUES (3465, 57, '牛蒡粥', '将牛蒡子研煮取汁100毫升，再煮米成粥，兑入牛蒡汁温服之。', NULL, NULL, NULL, '粳米50克，牛蒡子30克。');
INSERT INTO `recipe` VALUES (3466, 57, '芡实煮老鸭', '1.芡实洗净：将老鸭宰杀后，去毛和内脏，洗净血水，将芡实放入鸭腹内。\r\n2.将鸭放入瓦锅内，加水适量。\r\n3.将锅置武火上烧沸，放入葱、生姜、料酒，改用文火炖熬，约2小时，至鸭肉炖烂即成。\r\n4.食用时，加食盐、味精，吃肉喝汤。', NULL, NULL, NULL, '芡实200克，老鸭1只。葱、生姜、食盐、料酒、味精适量。');
INSERT INTO `recipe` VALUES (3467, 57, '琼玉膏', ' 1.将人参、白茯苓（去黑皮）粉碎成细末；白沙蜜用生绢滤过，生地黄取自然汁（捣时不用铜铁器），然后将四味中药合并一处拌匀，装入瓷瓶或罐内，用净纸二三十层封闭。\r\n2.用大铝锅一口，盛装净水，再将装药瓷瓶放入铝锅内，隔水煮熬。先用武火，后用文火，经三天三夜炖熬后取出，用蜡纸数层包扎瓷瓶口，放入水中浸过，然后取出，再放入铝锅炖熬一天一夜即成。\r\n3.每日空腹服用，每次1汤匙。', NULL, NULL, NULL, '人参1200克，生地黄汁8000克，白茯苓2450克，白沙蜜5000克。');
INSERT INTO `recipe` VALUES (3468, 57, '山药茯苓包子', '将山药、茯苓打成细粉，与面粉和匀揉面团，加发酵粉发酵，白糖、猪油、果料做成馅。用已发酵的面团加少量碱，加馅做成包子，蒸熟食之。', NULL, NULL, NULL, '山药100克，茯苓100克，面粉300克，白糖、猪油、果料适量。');
INSERT INTO `recipe` VALUES (3469, 57, '山楂肉丁', '猪肉洗净切丁，油锅烧热，放入红花油炸，去渣留油入肉丁煸炒，加盐适量，入山楂炒熟，放味精适量即可。', NULL, NULL, NULL, '山楂30克，猪瘦肉250克，红花10克，油、盐、味精各适量。');
INSERT INTO `recipe` VALUES (3470, 57, '鲜肉黄花饼', '将肉绞成肉末，黄花菜切成细段，与盐、葱、姜、味精调成肉馅，再用和好的白面包成馅饼，油煎炖食。', NULL, NULL, NULL, '鲜瘦猪肉500克，水泡黄花菜250克，葱、姜、盐、味精适量。');
INSERT INTO `recipe` VALUES (3471, 57, '香椿拌豆腐', '将豆腐洗净，切成大块放锅中，加清水煮沸后捞出，沥干水晾凉，切成黄豆大的丁，装盘。再将香椿洗净，放沸水内焯一下，捞出切成细末，放入碗内，加适量精盐、味精、麻油，拌匀后撒在豆腐丁上，吃时用筷子拌匀。', NULL, NULL, NULL, '豆腐500克，嫩香椿50克，精盐、味精、麻油适量。');
INSERT INTO `recipe` VALUES (3472, 57, '消食茶膏糖', '熬取红茶汁1000毫升，加入白糖继续熬至起丝状离火，倒入涂有熟菜油的盘内摊平，待凉切成块随时可服。', NULL, NULL, NULL, '红茶50克，白糖500克。');
INSERT INTO `recipe` VALUES (3473, 57, '猪蹄豆腐汤', '将猪蹄洗净切块煮烂，再入豆腐煮开10分钟，加入盐、葱花、姜末、味精等调味，一日一剂吃完，共吃3天。', NULL, NULL, NULL, '猪蹄一只，豆腐200克，盐、葱、姜、味精少许。');
INSERT INTO `recipe` VALUES (3474, 57, '猪蹄豆花汤', '将以上三物入砂锅内加水清炖，熟烂后调味食用。', '适用于产后贫血', NULL, NULL, '猪蹄2只，黄豆60克，花生仁60克。');
INSERT INTO `recipe` VALUES (3475, 60, '拌卷心菜丝', '1.将卷心菜择洗干净，切成细丝，用开水烫一下，晾凉，挤去水分，黄瓜洗净，切成细丝。\r\n2.将卷心菜、黄瓜放入盐内，加入香油、精盐，拌匀即成。', '味咸香，脆嫩，制作简单。', '为保持卷心菜脆嫩，千万不能把卷心菜烫过火，为使卷心菜入味好吃，烫好的卷心菜须挤净水分再拌。', NULL, '卷心菜800克，黄瓜200克，香油20克，精盐15克。');
INSERT INTO `recipe` VALUES (3476, 60, '拌茄泥', '1.将茄子去皮、蒂洗净，切成1厘米厚的片，放入盆内，上笼旺火蒸料取出，晾凉后，去掉盆内茄汁，香菜切末。\r\n2.将肉末、蒜泥、精盐、味精、熟酱油、姜末、香油依次放到茄子上，拌匀成泥状，撒上香菜末即成。', '软烂，清淡，醇香。', '茄子一定要蒸烂，放什么调料可灵活掌握，依口味而定。', NULL, '茄子1公斤，熟肉末200克，蒜泥100克，香菜20克，香油20克，熟酱油40克，精盐12克，味精3克，姜末15克。');
INSERT INTO `recipe` VALUES (3477, 60, '拌芹菜腐竹', '1.将芹菜洗净，切成丝，腐竹切丝，用开水烫一下用凉开水过凉， 控干水分待用。\r\n2.将芹菜丝、腐竹丝放入小盆内，加入精盐、香油拌匀即成。', '白绿相间，色泽美观，营养丰富。', '腐竹一定要发透，凉拌前用开水烫一下，可以起到回软作用。', NULL, '净芹菜750克，水发腐竹500克，香油10克，精盐20克。');
INSERT INTO `recipe` VALUES (3478, 60, '拌肉丝菠菜', '1.将菠菜择洗干净，切成1.5厘米长的段，用开水烫一下捞出，用凉水过凉，控干水分放入盆内，猪瘦肉切丝待用。\r\n2.将植物油放入锅内，热后下入肉丝煸炒断生，加入少许酱油，炒好后倒入菠菜上边。\r\n3.将酱油、味精、精盐、香油放入碗内对成汁，浇入盆内菜上，拌匀即成。', '鲜香适口，营养丰富。', '菠菜中的维生素C容易流失，易溶水，要现吃现洗，先洗后切。菠菜放入开水锅内稍烫立刻捞出，防止烫过火而使菠菜变烂。', NULL, '菠菜750克，猪瘦肉250克，香油20克，植物油70克，酱油30克，精盐12克。');
INSERT INTO `recipe` VALUES (3479, 60, '拌土豆丝', '1.将土豆洗净、去皮，切成细丝；青红柿椒去蒂籽，洗净切成细丝待用。\r\n2.将水倒入锅内烧开后，投入土豆丝烫一下捞出，用凉开水过凉，控净水分；青红椒用开水烫一下，捞出用凉水过凉，控净水分。\r\n3.将土豆丝、青红椒丝放入盆内，加入花椒油、精盐，拌匀即成。', '色泽红绿白，味咸香，质脆嫩。', '土豆不要烫过火，下锅后稍烫一下即可捞出，这样才能保持土豆丝脆嫩爽口。土豆丝烫好后，要立即捞出用凉水过凉，否则土豆丝容易发黑，影响菜的美观。', NULL, '土豆800克，青红柿椒200克，花椒油20克，精盐15克。');
INSERT INTO `recipe` VALUES (3480, 60, '拌莴笋条', '1.将莴笋择洗干净，去皮切成小细条，用开水烫一下捞出，控净水分，晾凉；木耳放入盆内，加入凉水泡涨，择洗干净，切成小细条，用开水烫一下，捞出控净水分，晾凉。\r\n2.将莴笋条、木耳同放盆内，加入精盐、味精、香油、醋，拌匀即成。', '色泽黑绿，莴笋脆嫩，味道鲜美。', '莴笋入开水锅稍烫即可捞出，烫的时间不要太长。木耳须和冷水泡发，这样涨发丰满；如用热水泡发，既不出数，又发粘。', NULL, '净莴笋800克，木耳20克，香油20克，精盐15克，醋10克。');
INSERT INTO `recipe` VALUES (3481, 60, '孢子甘蓝芦荟色拉', '1.将土豆去皮，放入清水煮熟，加盐，取出切成丁块。 \r\n2.把孢子甘蓝剖成两瓣，红胡萝卜一小段切成丁块，入锅烧煮，捞出备用。 \r\n3.芦荟叶肉10克煮烧后取出，切成丁块，备用。 \r\n4.把土豆、孢子甘蓝、芦荟、红胡萝卜的丁块混合，放入色拉酱拌匀，装盘，中央放半颗红樱桃，四周放绿色芫荽叶。', '红、黄、白三色相配，爽口开胃，清热。', NULL, NULL, '土豆200克，孢子甘蓝100克，芦荟25克，红胡萝卜、芫荽、红樱桃果、色拉酱各100克。');
INSERT INTO `recipe` VALUES (3482, 60, '菠菜松', '1.将菠菜叶洗净、理齐，顺着菜纹切成细丝。\r\n2.将油倒入锅内，烧至八成热时放入菜丝，炸至酥脆时捞出，控净油，趁热拌上精盐、味精、香油即成。', '酥香，脆绿。', '此菜一定要切丝，炸时用热油，趁热加入精盐、味精、香油。', NULL, '菠菜500克，精盐10克，味精3克，香油10克，植物油1公斤（实耗60克）。');
INSERT INTO `recipe` VALUES (3483, 60, '炒酱黄瓜丝', '1.将猪肉切丝，用凉水泡出血水，挤干，酱黄瓜切细丝，用凉水洗到不太咸时挤干，葱、姜切丝。\r\n2.将油放入锅内，热后下入肉丝煸炒断生，再下入葱姜丝炒几下，投入黄瓜丝煸炒，最后加入白糖、精盐稍炒即成。', '有特殊的鲜香味。', '做好此菜关键有三，一是酱黄瓜用水泡一泡，以去掉一部分咸味。二要把用水泡过的肉丝、酱黄瓜丝挤干水分，这样才好入味。三是炒咸菜一类要多加点葱姜丝。', NULL, '猪瘦肉250克，酱黄瓜750克，葱白50克，鲜姜25克，白糖40克，精盐12克，植物油75克。');
INSERT INTO `recipe` VALUES (3484, 60, '炒酱芥丝', '1.将酱芥洗净，切成细丝，用凉水泡20分钟，挤干水分待用。\r\n2.将油放入锅内，热后下入葱丝，炒出香味后投入酱芥丝，煸炒几下即成。', '脆嫩，咸香。', '炒菜前一定要把酱芥丝用凉水泡一泡，以去咸味。还要把酱芥丝水分挤干，这样炒出菜来没汤。', NULL, '酱芥1公斤，葱丝100克，姜丝50克，植物油50克。');
INSERT INTO `recipe` VALUES (3485, 60, '炒什锦咸菜', '1.将水芥、酱黄瓜、胡萝卜、豆腐干，水面筋洗净，切成细丝；香菜切小段；姜分别切成丝和片待用。\r\n2.将水芥丝用凉水泡至不咸时挤干。\r\n3.将油放入锅内，烧至七成热时放入姜片，炸成金黄色捞出，再将油晾至六七成热时下入少许姜丝及胡萝卜丝，煸炒至稍软即盛出，放入盘内待用。\r\n4.再用原锅加油煸水芥丝及酱黄瓜丝，最后下入煸好的胡萝卜丝、豆腐干丝、面筋丝、香菜段、精盐、白糖，炒匀后撒入熟芝麻即成。', '甜咸适口，红绿黄白相间，极为美观。', '此菜分几道工序入锅炒，制作时要按先后顺序进行操作，千万不可一锅烩，否则做不出满意的味道。', NULL, '腌水芥200克，酱黄瓜100克，胡萝卜300克，豆腐干150克，面筋100克，香菜150克，鲜姜20克，熟芝麻30克，精盐12克，白糖40克，植物油100克。');
INSERT INTO `recipe` VALUES (3486, 60, '粉肠', '1.用绞刀把猪肉绞成细肉末，加入各种调料拌匀制成馅。\r\n2.用870克干淀粉加水后，搅成稠糊。然后将稠稀糊和食品红色全部倒入肉馅中，搅拌均匀，使肉馅成为粉红色。\r\n3.将猪小肠用米醋加花椒，反复揉搓，除去肠上粘液，用清水反复洗净后，将肉馅用漏斗徐徐灌入肠中，将肠两头系起来缠在一起，放在微开水锅里煮30分钟（不超过100度），取出晾凉，切片食用即可。', '五香味浓，爽口不腻，熏制之后，便于保存。', NULL, NULL, '猪肥瘦肉2.5公斤，猪小肠适量，干淀粉1公斤，香油250克，盐12克，酱油120克，蒜末、葱末各75克，米醋120克，花椒粒12克，肉料10克，食品红色适量。');
INSERT INTO `recipe` VALUES (3487, 60, '风活鲤鱼', '1.在鲤鱼两胸鳍间刺一刀放血，去鳃刮鳞，从脊部开膛取出内脏后洗净，然后用花椒、料酒、盐、味精、姜、葱腌两天。\r\n2.用铁丝串住鱼尾，把腌好的鱼挂在阴凉处风干。\r\n3.餐时将风干的鱼铲在盘里，加入香油上屉蒸熟后，剁成块，装在盘内晾凉上桌。', '味道鲜美，久食不腻。此菜在严冬制作，春天食用为宜。', NULL, NULL, '活鲤鱼1条，香油、料酒各50克，葱段25克，姜块、花椒各25克，盐16克，味精10克。');
INSERT INTO `recipe` VALUES (3488, 60, '佛手海蜇皮', '1.先用水将海蜇皮浸泡，而后洗净去掉泥沙，用刀切成3厘米宽的长条。然后沿长条纵向每切4刀连刀（切断第5刀，即成五指手掌形状的条），切好后用清水泡上。\r\n2.上桌前，把加工好的海蜇皮，放入开水锅中烫一下，即成佛手状，迅速去水分装盘，浇上用香油、酱油、味精、盐、香醋等调料调匀的卤汁即可。', '色泽淡黄，鲜脆爽口。', NULL, NULL, '海蜇皮250克，香油25克，味精3克，酱油10克，香醋5克，盐3克。');
INSERT INTO `recipe` VALUES (3489, 60, '干煽苦瓜青椒', '1.将苦瓜剖开挖去籽瓤，斜切成厚片。小青椒去柄，与苦瓜一并清洗干净。\r\n2.炒勺将苦瓜、小青椒干炒，去水分后倒出。再将油注入烧热，随之下苦瓜、小青椒煸炒，继下盐和味精调和即成。', '味咸辣苦香，清暑爽口，夏令良菜。', NULL, NULL, '苦瓜250克（要青色的不要发红熟透的），小青椒250克，香油50克，盐15克，味精5克。');
INSERT INTO `recipe` VALUES (3490, 60, '红根腐竹', '1.将胡萝卜洗净去皮，切成条，腐竹切成同样大小的条。\r\n2.将香油放入锅内，投入花椒炸成焦黄后捞出，投入葱姜丝，加清水500克，随即加入精盐、白糖、味精、腐竹及胡萝卜条，焖至汁收尽，即可出锅装盆，晾凉后食用。', '味鲜香，色泽美观，营养丰富。', '此菜要用香油炒，用花椒、葱、姜丝炝锅来增加菜的味道，要将菜汁收尽才能出锅，晾凉后食用。', NULL, '胡萝卜600克，水发腐竹400克，香油100克，精盐12克，白糖8克，味精3克，葱、姜丝各8克，花椒5克。');
INSERT INTO `recipe` VALUES (3491, 60, '花生仁拌金笋干', '1.将胡萝卜洗净，上笼蒸至断生，取出晾凉，穿于绳上，挂通风处晾至八成干，用温水浸泡回软，洗净，再切成丝；将炸花生米拍破斩碎；将白糖、精盐、醋、香油放碗内调匀成汁。\r\n2.将胡萝卜干、花生米同放盆内，浇入调料汁，吃时拌匀即成。', '软韧酥脆，咸香酸甜。', '胡萝卜干不要现吃现晾，可提前晾好，随吃随取。做菜时一定要将胡萝卜干洗干净，注意卫生。', NULL, '胡萝卜1公斤，炸花生米150克，精盐10克，白糖80克，醋40克，香油15克。');
INSERT INTO `recipe` VALUES (3492, 60, '黄瓜拌花生米', '1.将黄瓜洗净，切成1厘米见方的小丁；花生米放入小盆内，加入凉水泡涨后，煮熟晾凉待用。\r\n2.将黄瓜、花生米同放入盆内，精盐、香油拌匀即成。', '色泽美观，诱人食欲，味道咸香。', '要选择鲜嫩的黄瓜做原料。花生米用凉水泡涨后再煮，如不经泡发，直接入锅煮，花生米煮不透，不利咀嚼与消化。', NULL, '黄瓜750克，花生米250克，香油20克，精盐15克。');
INSERT INTO `recipe` VALUES (3493, 60, '酱牛肉', '1.将牛肉切成5厘米见方的块，放入凉水内浸泡1小时，用开水烫一下，再用清水洗净，葱切段，姜切片，花椒、大料、丁香、桂皮用纱布包成料包待用。\r\n2.将锅内放入清水，用旺火烧开，放入牛肉，汤沸后撇去浮沫，加入料包、葱段、姜片、酱油、精盐，转微火炖2-3小时，牛肉熟时捞出即成，食时切片、切末均可。', '色泽紫黑，味道醇香，富含蛋白质。', '牛肉在酱制前要先用开水烫透，这样可去掉腥膻味。酱制时要用微火慢慢炖熟。', NULL, '牛肉1公斤，酱油100克，精盐20克，大料5克，桂皮3克、丁香4克，花椒4克，大葱30克，鲜姜15克。');
INSERT INTO `recipe` VALUES (3494, 60, '辣白菜', '1.将大白菜去叶、老帮、剁根，洗净，用下半节，把菜剖成两半，顺着切成两半，顺着切成宽约1厘米的条，盛于盆中均匀地撒上盐，腌3-4小时，控去水。葱、姜、辣椒均切细丝。\r\n2.炒勺上旺火将油烧热，将辣椒炸成紫色，再加上葱、姜煽炒，加水和糖，糖溶后放上醋，烧开再倒出晾凉后，浇在腌好的白菜上，焖3-4小时。\r\n3.吃时改切成两段，再将原汁浇上，摆上葱、姜丝。', '味酸甜带辣，脆嫩爽口。', NULL, NULL, '大白菜500克，香油25克，干辣椒10克，净葱15克，净姜10克，盐7克，白糖70克，醋精3克，水50克。');
INSERT INTO `recipe` VALUES (3495, 60, '兰花干', '1.将熏干剞蓑衣花刀，葱切段，姜切片。\r\n2.将植物油放入锅内，烧至八成熟时，将熏干投入锅内，炸去水分，呈金黄色捞出，沥净油。\r\n3.将香油放入锅内，下入葱段、姜片炝锅，下入熏干加入水（以漫过熏干为度）、酱油、精盐、料酒、味精、白糖、花椒、大料，开后转中火，烤软，收浓汁，出锅即成。', '咸香，软柔。', '此菜关键是切花刀，在熏干的两侧交叉着切成花刀，深度为料厚的4/5，用手一拉呈手风琴形。初学者切时不要着急，一刀一刀地顺序往后切，不要把熏干切断。', NULL, '熏干1公斤，酱油100克，精盐10克，料酒20克，味精5克，白糖40克，香油60克，花椒30粒，大料4瓣，葱40克，姜20克，植物油2公斤（实耗150克）。');
INSERT INTO `recipe` VALUES (3496, 60, '麻辣榨菜丝', '先把菜头去掉皮，切成片后，再切成细丝。用精盐码好榨菜丝，后滴去水分，再加入调料，拌匀装盘上桌。', '麻辣、脆嫩。', NULL, NULL, '鲜青菜头250克，精盐、辣椒油、花椒油、白糖、香油、蒜泥、红酱油各适量。');
INSERT INTO `recipe` VALUES (3497, 60, '面酱拌牛舌', '1.把牛舌洗净，用开水稍烫后，刮去外皮，再放入开水锅中煮熟（筷子可以扎透为度），捞出后将舌骨剔除，切成3厘米的长条。而后将舌条放入烧至7成热的植物油炒勺中稍炸一下，捞出沥油。\r\n2.炒勺再上火，放入50克植物油烧热，加入葱、姜（拍松），煸出香味后，再加入味精、料酒、香油、面酱，继续煸炒，待面酱被炒散后加入鸡汤、盐，同时牛舌也放入汤内，用小火把汤收成浓汁，颠翻几下使汁浸住牛舌，拣出葱、姜，盛盘晾凉即可。', '色枣红鲜艳，味咸甜爽口。', NULL, NULL, '牛舌1公斤，面酱100克，植物油900克（实耗约100克），香油100克，葱段、姜块各25克，味精5克，料酒15克，白糖25克，盐3克，鸡汤250克。');
INSERT INTO `recipe` VALUES (3498, 60, '双色榨菜拼', '把青菜头去皮，切成两寸长宽、一寸厚的片，把锅放旺火上，烧上1000克清水，沸后加青菜头煮1分钟起锅，快速加精盐、香油凉后再加蒜泥水、姜汁、味精，泡菜头成红色的切成同青菜大小一样的片，加上味精、香油，取一张盘，各拼一边即成。', '造形美观，风格典雅，香、鲜、味美。', NULL, NULL, '菜头250克，胭脂萝卜水泡菜头，选青嫩青菜头、泡菜头成粉红色，精盐、香油、姜汁、蒜泥水、味精。');
INSERT INTO `recipe` VALUES (3499, 60, '四川泡菜', '1.将泡菜坛洗净擦干，把清水倒入坛内，放进盐、白酒、红糖、干辣椒（去柄洗净）、姜（去皮洗净）。\r\n2.把要泡的菜加工洗净，该削皮的削去外皮，晾去水分泡入坛内。以后续泡的也要洗净晾干再入坛，并要把坛盖好，沿盖边倒入清水，以免空气进入坛内。坛沿内的水每周换1次，以保持清洁。\r\n3.泡进味后即可食用，取菜要有清洁的专筷，不要把带油和不洁的东西带入坛内。要随泡、随取、随续，并注意加盐，以保持一定咸味，达到既保持有适当咸味又有酸味就好。', '色泽艳丽，味美酸甜，鲜香爽口，很受欢迎。', NULL, NULL, '黄瓜、青笋、盖菜、元白菜、芹菜、嫩蒜、扁豆、辣椒、苦瓜、茭白、蒜台等各适量，清水5公斤，红糖、干辣椒各100克，花椒15克，白酒50克，盐200克，姜150克。');
INSERT INTO `recipe` VALUES (3500, 60, '素鸡', '1.用50克清水和味精、料酒、盐兑成汁。把4张油皮叠在一起，放在汁内浸泡透，然后取出油皮，把香油撒上平叠在一起，放在汁内透泡，然后取出油皮，压扁约3厘米宽。\r\n2.把油皮卷放在搪瓷盘中，上面用干净的重物压上。然后上屉用旺火大汽蒸约15分钟，取出晾凉，刷上香油，用原汁泡上。食时以斜刀切成厚片即可。', '色泽金黄，味似鸡肉。', NULL, NULL, '油皮400克，香油、料酒各25克，味精、盐各5克。');
INSERT INTO `recipe` VALUES (3501, 60, '酸辣榨菜条', '先把菜头去皮，切成一字条，把锅放旺火上，烧清水1000克沸后下菜条，煮1分钟30秒，起锅，用温水凉后，整齐摆在盘中，把调料兑成汁，加上香菜即成。', '咸鲜酸辣，造型整齐。', NULL, NULL, '鲜青菜头500克，香菜两根，精盐、辣椒油醋、白糖、红酱油、蒜泥、味精、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (3502, 60, '糖醋酥黄豆', '1.将葱切末，蒜瓣拍碎，泡辣椒切碎，黄豆洗净，泡涨后沥水待用。\r\n2.将花生油倒入锅内，热后投入黄豆，炸熟后捞起、沥油，倒入盆内。\r\n3.用上述调料对成的味汁浇在黄豆上，拌匀后即可食用。', '甜酸味美，酥脆可口。', '此菜应现做现吃，放久了则不酥脆。味汁不可过多，以糊完豆不见汁为宜。', NULL, '干黄豆500克，香油50克，白糖200克，醋100克，葱白30克，蒜瓣20克，泡辣椒20克，花生油1公斤（实耗50克）。');
INSERT INTO `recipe` VALUES (3503, 60, '糖酥黄豆', '1.将黄豆择洗干净，用凉水泡涨，控净水分，磕入鸡蛋拌匀，加入干淀粉，用手搓匀。\r\n2.将花生油倒入锅内，烧至七成热时投入黄豆炸成金黄色，捞出沥油。\r\n3.将锅烧热，加入水200克，开后放入白糖，炒至溶化，视糖汁稍变黄，迅速倒入炸黄豆拌匀，再倒在案板上摊匀，冷却后即可食用。', '色泽金黄明亮，味道甜香酥脆，营养丰富。', '必须用凉水泡豆，否则不酥脆。', NULL, '干黄豆500克，鸡蛋2个，白糖300克，干淀粉200克，花生油1公斤（实耗150克）。');
INSERT INTO `recipe` VALUES (3504, 60, '土豆松', '1.将土豆去皮，切成火柴梗粗细的丝，放水中轻轻搓洗，去除粉质，捞起晾干。\r\n2.将油放入锅内，烧至八成热时，将土豆分3－4次下锅，炸至呈金黄色时捞出沥油，待全部炸完后，趁热撒上精盐和味精，拌匀即成。', '酥脆，咸香，色泽金黄。', '土豆丝要切得极细。炸时油温高一些，趁热撒精盐，味精。', NULL, '土豆500克，精盐10克，味精3克，植物油1公斤（实耗60克）。');
INSERT INTO `recipe` VALUES (3505, 60, '五香豆腐', '1.将豆腐顶刀切成0.8厘米厚的片，用热油逐片炸成金黄色，捞出待用。\r\n2.将炸豆腐片放入锅内，加入水（以漫过豆腐为度）、酱油、精盐、大料，开锅后转微火慢慢烤透入味，视汤汁尽干时，加入味精，撒入葱花，淋入花椒油即成。', '炸豆腐松软咸香，花椒油味浓厚。', '豆腐切好片后，炸时控净水。炸豆腐油温要高些，炸至外皮呈金黄色、豆腐发硬时捞出。豆腐一定要烤透入味。', NULL, '豆腐1公斤，酱油100克，精盐10克，味精6克，花椒油40克，大料4瓣，葱花60克，植物油2公斤（实耗150克）。');
INSERT INTO `recipe` VALUES (3506, 60, '五香花生米', '1.将花生米择洗干净，泡4小时待用。\r\n2.将花生米放入锅内，加入清水（以没过花生米为度）、花椒、大料、桂皮、精盐同煮，熟后马上端离火，用原汤卤泡，待凉后捞出食用。', '入口甘香脆嫩。', '首先要将花生米选择好，如有霉变的，往往含有大量黄曲霉素，一定要特别注意。煮制前，一定要用冷水泡涨，不能不经涨发直接下锅煮。', NULL, '花生米500克，精盐30克，花椒3克，大料3克，桂皮3克。');
INSERT INTO `recipe` VALUES (3507, 60, '五香肉', '1.将瘦猪肉切成5厘米长、4厘米宽、2厘米厚的块，葱切段，姜切片。\r\n2.将肉块放入盆内，加入葱20克、姜10克、酱油30克、料酒10克拌匀，腌20分钟，拣出葱姜，用热油炸成金红色待用。\r\n3.将香油放入锅内，热后投入余下葱、姜，炸一下，放肉、水（以漫过肉为度）、白糖、五香粉及余下酱油、料酒、精盐，待开锅后用微火靠，至八成烂时，再用旺火收干汁，出锅即成。', '味咸香，略甜。', '先用油炸一下再加调料靠，最后收干汁。炸肉时，要用热油分数次下锅炸，一次不宜炸得太多，否则肉炸不好。', NULL, '瘦猪肉1公斤，酱油100克，白糖80克，精盐17克，料酒25克，香油50克，五香粉6克，葱50克，姜25克，植物油2公斤（实耗80克）。');
INSERT INTO `recipe` VALUES (3508, 60, '五香鱼', '1.鱼去鳞、鳍、鳃和内脏后洗净，用刀由脊背劈成两半，再用斜刀改切成块后，用少量葱（切长段）、姜（切大片）、炸油、盐腌1小时左右。\r\n2.用炒勺将植物油烧热，把鱼炸熟捞出沥油（若油温低易粘在一块）。再把香油注入，油热后下葱、姜煽一下，即下入汤、酱油、白糖、料酒、醋、五香粉和鱼，文火收汁约15-20分钟，将汁收浓即成。', '肉质鲜嫩，香甜味咸。', NULL, NULL, '桂鱼（或鲤鱼、草鱼）500克，植物油800克（实耗约80克），净葱、料酒各50克，净姜、香油、白糖、醋各25克，酱油30克，盐5克，味精5克，五香粉2克，汤250克。');
INSERT INTO `recipe` VALUES (3509, 60, '香油菜头丝', '把浸好的菜头切成细丝，放入勺内加上调料，拌匀装盘。', '香、鲜、脆、爽，四季宜佳。', NULL, NULL, '浸制好的青菜头、味精、香油、酱油、白糖、小葱花。');
INSERT INTO `recipe` VALUES (3510, 60, '熏干拌芹菜', '1.将芹菜去掉叶、根、切成丝，用开水烫一下捞出，晾凉，控去水分，熏干洗净，切丝待用。\r\n2.将芹菜、熏干同放入盆内，加入花椒油、精盐、拌匀即成。', '色泽美观，味道咸香，营养丰富。', '要选择鲜嫩的芹菜做原料。待水开后再将芹菜投入锅内烫，且水要多，芹菜要少下，烫好的芹菜才绿。拌菜前要把水控净，否则不好吃。', NULL, '芹菜500克，熏干500克，花椒油20克，精盐15克。');
INSERT INTO `recipe` VALUES (3511, 60, '腌咸鸭蛋', '1.将鸭蛋洗净，晾干，码入坛内。\r\n2.将锅内放入水、精盐、花椒、大料、桂皮，烧开后，视盐溶化，盛入盆内晾凉，加入白酒，倒入坛内（水量以没过鸭蛋为度），封好坛口，腌30天左右，即可取出，蒸熟食用。', '味咸香，不腻。', '鸭蛋要精心挑选，裂口的不能用。精盐必须加水上火熬化再用。鸭蛋腌好后，要把坛口封严，而且不要腌得太咸。', NULL, '鸭蛋100个，精盐1.5公斤，花椒10克，大料10克，桂皮10克，白酒50克，水5公斤。');
INSERT INTO `recipe` VALUES (3512, 60, '盐水虾', '1.将虾去须、脚，洗净后下入开水锅内。在烧开之前，锅内先加入葱、姜、味精、料酒、盐、花椒等调料，烧开后将浮沫撇去，约3分钟左右虾即可煮熟。\r\n2.连汤一起离火装入碗中，冷却后盛入盆内即可。', '鲜红美观，鲜嫩清口。', NULL, NULL, '新鲜河虾500克，盐15克，花椒4粒，姜2片，葱3段，料酒50克，味精5克。');
INSERT INTO `recipe` VALUES (3513, 60, '盐水鸭', '1.将鸭子内脏去净，清洗后，在鸭子腹壁里外抹上盐，腌2小时左右。\r\n2.把锅烧热加入清水、花椒、盐、葱、姜，将鸭子下锅，烧开后转文火煮至四成熟时，再加入曲酒、味精，继续煮至鸭子全熟时取出。煮鸭子的卤汁，也同时离锅（下次再用），待鸭子冷却后，再浸入卤内，临吃时取出，切成长方块装盆，浇上少许卤汁即成。', '色白微黄，醇香鲜嫩。', NULL, NULL, '净肥鸭1只（2公斤左右），盐150克，葱、姜各50克，花椒100克，大曲酒25克，味精7克。');
INSERT INTO `recipe` VALUES (3514, 60, '油浸咸鱼', '1.将偏口鱼洗净，把鳞刮去，除去鳃和内脏用料酒、盐、葱和姜、花椒腌7天左右。然后挂在通风荫凉处风干透，剁成方块，浸在植物油盆中1个月左右，并将盆口用油纸封好。\r\n2.将油浸好的鱼块上屉蒸熟，取出晾凉即可。', '鱼味咸香，别具一格，冬季制作，不会腐败坏。', NULL, NULL, '偏口鱼2.5公斤，植物油2.5公斤（实耗约500克），料酒100克，盐50克，花椒13克，姜块、葱段各125克。');
INSERT INTO `recipe` VALUES (3515, 60, '榨菜拌鸭掌', '选择新鲜、洁白无异味的鸭掌，将鸭掌一分为二，榨菜切成丝备用，将鸭掌放入开水中煮熟捞出，凉后放入调料和榨菜拌匀，装盘即成。', '香嫩化渣。', NULL, NULL, '鸭掌、酒、榨菜一两五钱，白酱油一钱，香油二钱，食盐一份、味精一份、炒熟芝麻一钱。');
INSERT INTO `recipe` VALUES (3516, 60, '芝麻酱拌豇豆', '1.将豇豆择洗干净，用开水烫透捞出，沥干水分，切成1.6厘米长的段，放入盆内。\r\n2.将芝麻酱用凉开水解开，加入精盐、香油、大蒜末，浇在豇豆上拌匀即成。', '脆嫩清香，麻酱、大蒜味浓醇。', '豇豆要烫透，麻酱要用凉开水解，不可用生冷水解。', NULL, '豇豆1公斤，香油20克，芝麻酱油100克，精盐15克，大蒜末50克。');
INSERT INTO `recipe` VALUES (3517, 62, '柏林猪酸白菜', '1.将白菜切粗丝，用15克盐腌后挤干水分。取葱头100克切丝用油炒黄。放白菜丝一起炒至半熟，放入不锈钢锅内加适量清汤、香叶1片及茴香籽焖之。当白菜焖熟时放糖、醋精调剂口味，并擦上生土豆末，微沸调剂浓度，待用。\r\n2.将猪腿肉切方块（每块250克），放锅内加适量水，放盐15克、香叶4片、切碎的葱头250克、胡萝50克，煮至熟软改温火（必须加盖免得肉皮变色）。起菜时配以焖酸菜。', '量大实惠，味香不腻。', NULL, NULL, '带皮猪腿肉2500克，胡萝卜50克，葱头350克，芹菜100克，白菜500克，净土豆100克，香叶5片，糖25克，醋精10克，盐25克，茴香籽5克，油100克，清汤适量。');
INSERT INTO `recipe` VALUES (3518, 62, '比利时烩牛肉', '1.把胡萝卜片、葱头片（50克）、芹菜段、大蒜末放锅内用黄油炒出香味，放上面粉炒成黄色，放番茄酱炒至油呈红色，然后放汤搅匀，微沸放盐、糖调剂口味，滤净后即成红沙司，备用。\r\n2.将牛肉切成小片撒盐、胡椒粉，用热油炒上色，倒入红沙司内一起在文火上焖，待肉焖到八成熟时，将炒过的葱头丝、番茄沙司和去皮、去籽切成片的酸黄瓜加入焖熟为止。起菜时配煮土豆。', '色泽红润，郁香适口。', NULL, NULL, '牛肉1750克，煮土豆1250克，葱头300克，酸黄瓜250克，油200克，盐20克，胡椒粉少许，瓶装香茄沙司200克，猪油125克，面粉100克，番茄酱150克，胡萝卜、芹菜各50克，糖25克，香叶2片，大蒜25克，胡椒粒2克，汤75O克。');
INSERT INTO `recipe` VALUES (3519, 62, '法式煎猪排', '1.先用锅将黄油溶化，然后放入过箩的面粉，用木制搅板搅匀，放在温炉板上炒之，随炒随搅，以免糊底，炒到面粉微黄且呈翻砂颗粒状，并发出面香味时即可，备用。\r\n2.将猪排切成10段（带骨），用肉拍子拍成片，撒盐、胡椒粉。煎盘放油烧热，放入猪排两面煎上色，控出油，烹上白兰地酒。\r\n3.油炒面粉用滚沸的清汤冲开搅匀调好浓度，放酸黄瓜片，沸后放盐调剂口味制成沙司。起菜时浇于猪排上。', NULL, NULL, NULL, '猪排1750克，酸黄瓜250克，白兰地酒25克，盐10克，胡椒粉少许，油炒面粉75克。');
INSERT INTO `recipe` VALUES (3520, 62, '法式鱼卷', '1.将净鱼切成20片，用肉拍子拍成长方形薄片。\r\n2.葱头、芹菜切丝，用黄油炒至呈黄色，放盐、胡椒粉调好味，用白兰地酒烹之，拌上摊好的蛋饼切成的丝。凉后用鱼片卷成筒形，叠在器皿内蒸熟即可。', '咸鲜适口，清香不腻。', NULL, NULL, '净鱼肉1250克，葱头250克，芹菜15O克，鸡蛋100克，盐15克，黄油100克，胡椒粉少许，白兰地酒25克。');
INSERT INTO `recipe` VALUES (3521, 62, '罐搁鸭', '1.把胡萝卜、葱头各50克切片，芹菜50克切段与大蒜末一起放锅内用黄油炒出香味，放上面粉炒成黄色，加番茄酱炒至油呈红色，然后加水搅匀，微沸放盐、糖调剂口味，过滤后即制成红沙司，待用。\r\n2.鸭剁成每份四块，撒盐、胡椒粉用热油煎上色，放入锅内加香叶和适量鸭汤焖之，焖至八成熟时放上炒黄的葱头块、胡萝卜块及用开水烫过的青椒块、芹菜段及大蒜末搅匀，加上红沙司调剂浓度，放盐、红葡萄酒调剂口味。\r\n3.取烤罐放炉板上烧热，先在罐里放炸黄的土豆块，再按份装上烩好的鸭及原什，放上豌豆、鲜蘑菇、西红柿角和煮熟的菜花，加盖炉', '郁香适口，味浓不腻，能保持热度。', NULL, NULL, '净鸭2000克，土豆600克，胡萝卜250克，葱头250克，菜花500克，西红柿20O克 ，豌豆100克，青椒50克，芹菜75克，大蒜50克，油15O克，盐25克，胡椒粉少许，香叶片4片，红葡萄酒25克，猪油125克，面粉100克，蕃茄酱150克，糖25克，胡椒粒2克。');
INSERT INTO `recipe` VALUES (3522, 62, '黄瓜色拉', '1.将黄瓜去皮切片，洋葱切成末。\r\n2.在锅中加入色拉油、醋精、盐、胡椒粉拌匀，后加入黄瓜、洋葱拌匀即可食用。', '清丽可口，凉爽开胃。', NULL, NULL, '黄瓜2500克，色拉油50克，盐15克，醋精10克，胡椒粉少许，洋葱100克。');
INSERT INTO `recipe` VALUES (3523, 62, '鸡蛋火腿色拉', '1.把鸡蛋煮熟切片，黄瓜、火腿去皮切片，土豆煮熟切丁。\r\n2.将鸡蛋黄放入陶瓷器皿内，放入盐、芥末酱，用蛋抽子（器皿）搅匀，再放少量生菜油，搅拌，然后陆续加生菜油，随加随搅；当搅成单一体稠度较大时放入少量醋精、柠檬汁、凉开水搅之，随后再加生菜油，搅到再次稠度大时，复加入醋精、柠檬汁、凉开水搅之，再加生菜油搅成单一糊状体即制成“马乃司”备用。\r\n3.在锅中加入鸡蛋、黄瓜、火腿、土豆、盐、胡椒粉、马乃司拌匀即可食用。', '法式风味，味浓色艳。', NULL, NULL, '鸡蛋750克，黄瓜150克，火腿250克，土豆300克，盐10克，胡椒粉少许，马乃司15O克。');
INSERT INTO `recipe` VALUES (3524, 62, '煎扳鱼', '1.把板鱼去皮去骨，将鱼肉包成长方形，裹以打碎搅匀的鸡蛋浆和面粉，撒上盐、胡椒粉待用。\r\n2.取煎锅加入色拉油，用热油将裹好的鱼块煎至色黄后放入烤箱中烤熟，再取另一只煎锅把黄油溶化并着色，然后浇在鱼上面即可食用。', NULL, NULL, NULL, '板鱼l条，面粉50克，鸡蛋2只，盐、胡椒粉少许，黄油50克，色拉油少许。');
INSERT INTO `recipe` VALUES (3525, 62, '煎牛里脊扒', '1.将净牛里脊切为10段，每段均用肉拍子拍成1.2公分厚的圆形饼，把周围毛边收拢整齐，撒上盐、胡椒粉。\r\n2.煎盘内放油烧热，把里脊扒放入，两面煎至七成熟，将油沥去，再加黄油稍煎，放辣酱油烹之。\r\n3.起菜时最好将牛里脊扒放在配好的炸土豆条、煮胡萝卜条、煮扁豆的盘内，并浇以原汁。', '咸香适口，肉质软嫩。', NULL, NULL, '净牛里脊1750克，油150克，黄油100克，辣酱油25克，盐10克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3526, 62, '橘汁鸭肉', '1.把鸭去骨，肉切成块，待用。将橘子去皮，取瓤待用。\r\n2.在锅中加入鸭骨和水煮开至收浓，并有鸭汁，鸭汁待用。\r\n3.在煎锅中放入鸭骨肉煎上色，待用。\r\n4.在锅中加入面粉、黄油炒至起沙，再加入煮沸的鸭汁冲匀开煮开，并把鸭肉煮熟，然后捞出鸭肉，放在盛器中，并浇入沙司即可食用。', '酸甜香嫩，油而不腻。', NULL, NULL, '鸭1只，面粉70克，黄油70克，橘子汁300克，水1000克，盐、胡椒粉少许，橘子4只，色拉油少许。');
INSERT INTO `recipe` VALUES (3527, 62, '烤咖喱酿馅笋鸡', '1.大米洗净放入沸水锅内加黄油、盐煮沸搅匀加盖，用文火焖熟或入烤炉烤焖熟，待用。\r\n2.生菜油放煎盘内加热，放葱姜蒜末、辣椒粉一起炒黄，加咖喱粉炒出香味时放切碎的鸡肝一起炒透，再放米饭炒透，加盐调剂口味后放入少量鸡汤搅匀，然后酿进鸡膛内，外面撒盐，抹上干咖喱粉，鸡上面浇溶化的黄油，入烤箱烤之，边烤边浇油，熟后取出。将原汁调上鸡汤煮沸后过箩滤清，调好口味即可。食用时配以沙司。', '郁香适口，味浓不腻。', NULL, NULL, '净笋鸡（食用小而嫩的鸡）2500克，葱头50克，姜25克，大蒜25克，大米1000克，辣椒粉5克，鸡肝250克，盐10克，胡椒粉少许，糖5克，咖喱粉50克，生菜油150克，黄油100克。');
INSERT INTO `recipe` VALUES (3528, 62, '美式煮鱼', '1.把净鱼肉切成每份两块，锅内放水、芹菜段、胡萝卜片、葱头块、香叶、胡椒粒、柠檬、醋精、盐煮沸，放入鱼块煮熟。\r\n2.用黄油炒面粉，炒至微黄时用滚沸鱼汤冲之，搅匀微沸后过滤，放入用沸水烫过的芹菜末。用盐、白兰地酒调剂口味成沙司。\r\n3.土豆去皮，削成鼓圆形洗净，往钢精锅内加水没过土豆即可，放盐用旺火煮沸后改微火煮熟为止，控去水，浇上黄油。\r\n4.食用时在鱼块上浇沙司，配以煮土豆共食。', NULL, NULL, NULL, '净鱼1500克，胡萝卜、芹菜、葱头各50克，香叶1片，胡椒粒5粒，柠檬50克，醋精少许，盐15克，白兰地酒25克，芹菜末100克，水适量，面粉75克，黄油75克，煮土豆1250克。');
INSERT INTO `recipe` VALUES (3529, 62, '蘑菇烩鸡肉', '1.把鸡肉切成大块，蘑菇、洋葱切成小块。\r\n2.在锅中加黄油，将面粉炒至起沙并微黄，再加入热牛油冲匀开，待用。\r\n3.在锅中加入色拉油，把鸡块、蘑菇、洋葱炒熟后加入沙司200克，拌匀加入盐、胡椒粉适量，即可食用、配米饭。', '肉嫩味香，口味浓鲜。', NULL, NULL, '鸡肉100克，蘑菇100克，洋葱100克，黄油100克，牛油450克，面粉300克，盐15克，胡椒粉少许，米饭360克，蕃茄沙司200克，色拉油少许，鸡汤适量。');
INSERT INTO `recipe` VALUES (3530, 62, '南斯拉夫烤猪肉串', '将猪通脊切成60块，拍成厚片，用三分之一的葱头擦成泥，加盐、胡椒粉、生菜油、柠檬汁和肉片拌匀，腌12小时后，用细竹签串起来，每份6片，以炭火烤熟。食用时配葱头末即可。', '油而不腻，香嫩开胃。', NULL, NULL, '净猪通脊1800克，葱头500克，生菜油100克，柠檬50克，盐15克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3531, 62, '啤酒焖牛肉', '1.将牛肉（每块500克－750克）撒盐、胡椒粉腌约1小时，然后用热油煎上色，放入烟锅内加啤酒、鲜西红柿块、牛肉汤和用油炒黄的葱头丝、胡萝卜片、芹菜段，在旺火上煮沸，沸后改文火微沸，至牛肉熟软时将其取出。\r\n2.原汁用油炒面粉调剂浓度，放盐、糖调剂口味，过滤成沙司。\r\n3.食用时将牛肉每份切2－3片，浇沙司即可。', NULL, NULL, NULL, '牛后腿肉1750克，啤酒750克，牛肉汤750克，油100克，鲜西红柿500克，葱头150克，胡萝卜100克，芹菜50克，盐15克，油炒面粉50克，糖50克，胡椒粉少许 ，黄油50克。');
INSERT INTO `recipe` VALUES (3532, 62, '软煎鱼', '把净鱼去骨去皮切成每份两块，撒上盐、胡椒粉，沾面粉，裹鸡蛋糊用热油煎成黄色，放入烤炉内烤熟。起菜时配煮土豆，浇黄油。', '色黄味鲜，清淡适口。', NULL, NULL, '净鱼1500克，面粉50克，鸡蛋200克，油150克，黄油50克，煮土豆1500克，盐10克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3533, 62, '瑞士牛肉扒', '1.土豆去皮切成1厘米宽厚、4厘米长，用冷水洗净，沥干水分。\r\n2.炸锅放植物油，加热至七、八成时，将土豆条放入，以旺火炸至土豆发硬时，移微火，快熟透时再上旺火，使其上色，当完全熟透并呈黄色时，取出沥干余油，撒盐备用。\r\n3.将牛外脊切20片，用肉拍子拍成薄片，用刀断其筋，剁成圆形，撒盐、胡椒粉，沾面粉，用热油煎成黄色，抹在烤盘内。\r\n4.将葱头切丝、胡萝卜切片，和香叶一起用油炒黄，再放上番茄酱，炒至油呈红色时放牛肉汤、西红柿块，沸后放盐调剂口味，浇在肉扒上，入烤箱烤熟为止。起菜时配炸土豆条共食。', '红褐色，郁香适口。', NULL, NULL, '牛外脊1750克，土豆500克，植物油100克，胡萝卜150克，葱头150克，鲜西红柿200克，面粉50克，番茄酱15O克，香叶2片，盐20克，胡椒粉少许，油150克。');
INSERT INTO `recipe` VALUES (3534, 62, '桑给巴尔式鱼饼', '1.先把鱼肉放在锅内加适量水焖烧，当水将要收干时加上柠檬汁稍焖，凉后剔去刺、骨，搓碎成末。\r\n2.把净土豆煮熟，倒掉水，凉后用绞肉机绞过，与鱼肉揉在一起，放上桂皮粉、胡椒粉、盐、柠檬汁合成馅，做成每份4个的小圆饼，沾鸡蛋、面包粉，用热油炸至黄色即可。', '外焦内嫩，清香适口。', NULL, NULL, '净鱼肉1250克，净土豆1250克，桂皮、胡椒粉少许，柠檬10O克，鸡蛋200克，面包渣25O克，油25O克，盐15克。');
INSERT INTO `recipe` VALUES (3535, 62, '蔬菜末烤鱼', '1.将净鱼去骨去皮，切成20块，撒盐、胡椒粉，沾面粉用热油煎上色，放入烤斗内。\r\n2.胡萝卜、葱头、芹菜切末，放柠檬汁、盐、胡椒粉用生鸡蛋拌匀，浇在鱼的上面，再浇上黄油入烤炉，用文火烤上色，熟后即可食之。', '黄色光亮，肉嫩清香。', NULL, NULL, '桂鱼1250克，胡萝卜250克，葱头250克，芹菜150克，柠檬50克，鸡蛋150克，油150克，黄油100克，盐15克，胡椒粉少许，面粉50克。');
INSERT INTO `recipe` VALUES (3536, 62, '蔬菜色拉', '1.把土豆、胡萝卜分别去皮切片，并用水煮熟，将黄瓜、洋葱切片。\r\n2.把土豆片、黄瓜片、洋葱片、胡萝卜片放入盆中，再加入马乃司、盐拌匀，即可食用。', '清淡可口，鲜嫩美观。', NULL, NULL, '土豆1000克，黄瓜250克，洋葱50克，胡萝卜150克，马乃司150克，盐10克。');
INSERT INTO `recipe` VALUES (3537, 62, '蔬菜丝混合色拉', '1.把卷心菜、洋葱、芹菜切成丝，把胡萝卜去皮切丝，把青椒去籽切成丝。\r\n2.在锅中加入卷心菜、胡萝卜、青椒、洋葱、芹菜拌匀，加入色拉油、盐、胡椒粉、醋精，拌匀即可食用。', '清淡可口。', NULL, NULL, '卷心菜500克，胡萝卜500克，青椒250克，洋葱150克，芹菜50克，色拉油50克，盐15克，胡椒粉少许，醋精15克。');
INSERT INTO `recipe` VALUES (3538, 62, '西班牙式煎鱼', '1.将净鲜鱼肉切斜片，撒上盐、胡椒粉，沾面粉，用热油将两面煎黄控去油，烹白酸葡萄酒，放入牛奶以微火候之。\r\n2.大蒜切末，葱头切丝，用生菜油炒至微黄，放咖喱粉，炒出香味时加适量鸡汤调匀，青、红撇切丝放入，煮沸后加糖、盐调剂口味成沙司。\r\n3.将沙司浇在鱼上，在微火上煮至微沸。起菜时配煮土豆。', NULL, NULL, NULL, '净鲜鱼肉1000克，煮土豆1000克，青、红椒250克，葱头100克，大蒜25克，牛奶250克，面粉250克，盐15克，糖10克，胡椒粉少许，咖喱粉5克，白酸葡萄酒100克，生菜油150克，鸡汤1000克。');
INSERT INTO `recipe` VALUES (3539, 62, '西红柿色拉', '1.将西红柿去皮切角状，洋葱切成末。\r\n2.锅中加入色拉油、醋精、盐、胡椒粉拌匀做成沙司，再加入西红柿、洋葱拌匀即可。', '酸甜自然，清香可口。', NULL, NULL, '西红柿1500克，洋葱80克，色拉油200克，醋精60克，盐15克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3540, 62, '蟹肉色拉', '1.将鸡蛋黄放入陶瓷器皿内，放入盐、芥末酱，用蛋抽子（器皿）搅匀，再放少量生菜油，搅拌，然后陆续加生菜油，随加随搅；当搅成单一体稠度较大时放入少量醋精、柠檬汁、凉开水搅之，随后再加生菜油，搅到再次稠度大时，复加入醋精、柠檬汁、凉开水搅之，再加生菜油搅成单一糊状体即制成“马乃司”备用。\r\n2.土豆、胡萝卜、去皮、切片、煮熟，黄瓜、酸黄瓜去皮、去籽、切片，鸡蛋白煮、去皮、切片，葱头切片，一并放入消毒过的瓷盆内，加上豌豆、奶油、盐、辣酱及马乃司拌匀。此即通常的素沙拉，备用。\r\n3.把蟹肉撕碎、去壳骨，二分之一拌', '鲜味适口，美观大方。', NULL, NULL, '蟹肉400克，土豆1000克，黄瓜250克，酸黄瓜150克，豌豆100克，葱头50克，胡萝卜150克，鸡蛋100克，生菜油750克，鸡蛋黄100克，芥末酱5克，醋精5克，糖5克，盐20克，柠檬什25克，凉开水50克，奶油150克，辣酱油15克，柠檬150克，青菜（点缀用）250克，西红柿1只。');
INSERT INTO `recipe` VALUES (3541, 62, '英式鱼饼', '1.先用不锈钢锅将500克黄油溶化，然后放上过箩的面粉500克，用木制搅板搅匀，放在温炉板上随炒随搅，防止糊底，炒到面粉微黄、翻砂颗粒状并发出面香味时即可，此即西餐中常用作烩菜、汤之浓度的油炒面，备用。\r\n2.将鱼切成小丁，放盐、胡椒粉、柠檬汁拌匀，撒面粉用热油煎熟，再用油炒葱头末至微黄，加入油炒面粉，放清汤搅拌，微沸成糊状，放辣酱油、盐、胡椒粉和用绞肉机绞过的土豆泥以及煎熟的鱼丁搅拌均匀，做成每份两个圆饼，用热油煎至两面金黄。\r\n3.食用时将鱼饼放在盘内，单配番茄沙司。', NULL, NULL, NULL, '净鱼肉1000克，熟土豆1000克，柠檬50克，黄油500克，面粉550克，辣酱油50克 ，盐15克，胡椒粉少许，油200克，清汤适量，瓶装香茄沙司15O克。');
INSERT INTO `recipe` VALUES (3542, 62, '鱼冻', '1.净鱼肉放锅内加水、醋精，上火煮沸，捞出控去水，并用冷水冲洗干净，放入锅内加鸡清汤、香叶、胡椒粉、盐及切片的胡萝卜、葱头、芹菜、柠檬在温水煮沸，当鱼肉熟时移阴凉处冷后取出，剔去刺，切成长方形片。\r\n2.结力片用冷水浸软后控去水，放入煮鱼的汤内放盐调剂口味（汤要保持1500克—2000克）。放在温炉板上搅之，使结力片全部溶化。将蛋白用蛋抽子拍打，略有泡沫时注入锅内搅匀后加盖，放微火上使其微沸，当鱼汤的杂质与蛋白凝结为一体，完全清澈透明时，将其过滤，成胶冻汁。取花形模于洗净消毒后在底部浇上一层胶冻汁，冷冻凝', '美观大方，鲜味甚佳，清澈透明，有水晶体之感。', NULL, NULL, '净鱼肉1000克，结力片5O克，鸡清汤750克，醋精10克，胡萝卜、葱头、芹菜各50克，香叶2片，胡椒粒0.1克，盐15克，柠檬100克，老鸡蛋100克，豌豆50克 ，黄瓜100克，酸黄瓜100克，辣根沙司100克，蛋白150克。');
INSERT INTO `recipe` VALUES (3543, 62, '炸黄油鸡卷', '1.把带骨鸡胸肉撒上盐和胡椒粉后，用手揉成圆形，再裹上面粉、鸡蛋、面包粉待用。\r\n2.用旺火把色拉油加至五、六成热后，改用微火，把裹好的鸡肉放入锅中炸熟，然后改用旺火炸至色黄，取出沥干后放入盘中，浇上热黄油即可食用。', '鲜嫩清香，呈金黄色。', NULL, NULL, '带骨鸡胸肉200克，鸡蛋2只，盐、胡椒粉少许，面包粉100克，黄油30克，色拉油少许。');
INSERT INTO `recipe` VALUES (3544, 62, '炸黄油鸡排', '1.净胡萝卜切成5毫米厚、2公分长的方条，用冷水煮熟控去水，用黄油炒透放盐调剂口味。\r\n2.净菜花拆成小朵，用盐水泡l小时左右（杀死幼虫），然后用水煮熟，捞出用冷水浸泡待用。\r\n3.土豆削皮洗净切成1.5毫米粗的丝，用冷水冲洗去淀粉，然后控干水分。炸锅内放入植物油（油量多些为好），加热至七、八成时将土豆丝放入锅内，在旺火上炸之，发硬后改微火，并轻轻搅开（防止粘连），直到炸至黄而脆取出，将油控干。\r\n4.将鸡脯肉在翼关节上部剁断（留上节和鸡脯肉连着），剔净翼骨上节的腱肉成骨把，再取下鸡里脊抽去筋，然后用刀片', '肉质松软，外焦内嫩。', NULL, NULL, '鸡脯肉750克，黄油250克，炸土豆丝500克，龙须菜250克，煮菜花250克，煮胡萝卜条250克，面包渣250克，植物油200克，盐15克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (3545, 62, '炸鸡肉团', '1.净扁豆切成段或菱形，放入沸水中加碱煮沸，控去水，用冷水浸凉，以水泡之放入冷藏柜待用。\r\n2.把熟鸡肉切丁，通心粉切l厘米长的段，葱头切末，用黄油炒至微黄，放入鸡丁、通心粉段一起炒透。\r\n3.将锅内放黄油溶化后放入面粉炒至微黄，用滚沸牛奶冲匀，以木搅板搅之在文火上熬成稠面糊状，加入炒过的鸡丁、通心粉拌匀，放盐、胡椒粉调好口味，做成10个鸡蛋形的肉团，沾鸡蛋糊裹面包粉，用热油炸成黄色。装盘时将鸡肉团放在盘的一边配黄油扁豆，单配番茄沙司。', NULL, NULL, NULL, '净熟鸡肉350克，熟通心粉150克，葱头100克，面粉25克，黄油50克，牛奶125克，鸡蛋150克，面包渣150克，盐、胡椒粉各少许，油150克，煮扁豆750克，瓶装番茄沙司150克，碱适量。');
INSERT INTO `recipe` VALUES (3546, 60, '拌土豆丝', '1.将土豆洗净、去皮，切成细丝；青红柿椒去蒂籽，洗净切成细丝待用。\r\n2.将水倒入锅内烧开后，投入土豆丝烫一下捞出，用凉开水过凉，控净水分；青红椒用开水烫一下，捞出用凉水过凉，控净水分。\r\n3.将土豆丝、青红椒丝放入盆内，加入花椒油、精盐，拌匀即成。', '色泽红绿白，味咸香，质脆嫩。', '土豆不要烫过火，下锅后稍烫一下即可捞出，这样才能保持土豆丝脆嫩爽口。土豆丝烫好后，要立即捞出用凉水过凉，否则土豆丝容易发黑，影响菜的美观。', NULL, '土豆800克，青红柿椒200克，花椒油20克，精盐15克。');
INSERT INTO `recipe` VALUES (3547, 88, '何首乌山鸡', '何首乌用清水洗净、放入铝锅内煮二次，共收药液20毫升。山鸡去净毛，剖腹去内脏，洗净去骨，切成丁。冬笋，青椒切成丁。鸡蛋去黄留清。蛋清加入豆粉，调成蛋清豆粉，用一半入少许精盐将山鸡丁浆好，另一半同料酒、酱油、味精、首乌汁兑成液汁待用。净锅置火上，注入菜油，烧至六成热时下鸡丁过油滑熟，随即捞入勺内待用。锅留底油，加入鸡丁、冬笋、青椒、倒入液汁勾芡，起锅装盘即成。', '有补肝肾、乌须发、悦颜色、延寿命之效。', '佐餐食用。', NULL, '山鸡二只、炙何首乌10克、青椒100克、冬笋15克、酱油10克、料酒20毫升、味精1克、精盐2克、豆粉20克、鸡蛋1个、菜油1000克（实际消耗60克）。');
INSERT INTO `recipe` VALUES (3548, 88, '胡桃阿胶膏', '先将红枣、胡桃肉、桂圆肉、黑芝麻研成细末；阿胶浸于黄酒中10天，然后与酒一起置于陶瓷器中隔水蒸，使阿胶完全溶化，再加入红枣、胡桃、桂圆肉、黑芝麻末调匀，放入冰糖再蒸，至冰糖溶化，即成护肤美容珍品，制成后盛于干净容器装好封严。\r\n　', '补肾美血，润肤美容。', '每日清晨取1至2匙，用开水冲服。', NULL, '取红枣500克（去核），胡桃肉、黑芝麻（炒熟）、桂圆肉各150克，阿胶，冰糖各250克，黄酒500克。');
INSERT INTO `recipe` VALUES (3549, 88, '黄芪鲫鱼火锅', '1.将鲫鱼去鳃、鳞，剖去内脏，片成5厘米见方、0.3厘米厚的鱼片（鱼刺弃之不用）；猪瘦肉去筋膜，洗净沥水切片；豆腐切块；粉条水发后切段；莴笋叶洗净择好。以上各料全部装盘，围于火锅四周。\r\n2.用干净纱布包上黄芪、炒枳壳，入沙罐中，注入清水，熬2次，每次15分钟，收药液待用。\r\n3.锅置火上，下猪油烧至六成热，下姜片，煸出香味，放盐、胡椒粉、醋、料酒、白糖等，加入汤烧开，撇去浮沫，再下药液，烧开之后，倒入火锅中，烫食各种原料，饮汤。', '补气健胃，美容润颜之功效。主治脾虚所至的食欲不振、消化不良、便溏泄泻，以及气虚所致的气短乏力、胃下垂、脱肛等症。女人常食可美容润肤。', '直接食用或配餐均宜。', NULL, '黄芪15克，鲫鱼3条（约重500克），猪瘦肉200克，豆腐、粉丝各150克，莴笋叶100克，化猪油75克，生姜15克，葱10克，料酒30毫升，白糖、精盐各5克，炒枳壳、味精、胡椒粉各2克，醋3克，鲜汤2000毫升。');
INSERT INTO `recipe` VALUES (3550, 88, '减肥－－豆腐素蒸包', '皮面制法同一般包子。锅内加水烧开，将粉条放入烫软后斩成细末，加入油菜、豆腐、海米、葱、姜斩成的细末和盐、味精、植物油调拌均匀即成馅。将包子捏好，放入蒸笼内蒸20分钟即成。', '和脾胃、消胀满、宽中益气、下大肠浊气等功能。', '作为主食食用。', NULL, '面粉400克，面肥100克，蒸好的豆腐400克，水发海米10克，粉条20克，油菜30克，盐10克，味精2克，葱10克，姜10克，植物油20克，碱适量。');
INSERT INTO `recipe` VALUES (3551, 88, '莲子龙眼汤', '将上述五种原料加水大火煮开，再用小火煮一小时，加入蜂蜜即成。', '促进新陈代谢，改善粗糙、病态的皮肤；主治皮肤粗糙，面色无光泽者。', '莲汤渣一道服食。', NULL, '莲子30克、芡实30克、薏苡仁50克、龙眼肉8克、水500毫升、蜂蜜少许。');
INSERT INTO `recipe` VALUES (3552, 88, '罗汉果烧兔肉', '1.将罗汉果洗净，打破；兔肉洗净，切成3厘米见方的块；莴苣去皮，切成3厘米见方的块；姜切片，葱切段。\r\n2.将炒锅置火上烧热，加入素油，烧至六成热时，下入姜、葱爆香，再下入兔肉、罗汉果、莴苣、料酒、酱油、白糖、盐、味精、鲜汤烧熟即成。', '润肺，止咳，美容。适用于肺热干咳、肌肤不润、面色无华等症。', NULL, NULL, '罗汉果1个，兔肉300克，莴苣100克，料酒、姜、葱、酱油各10克，盐4克，味精3克，鲜汤300克，素油50克。');
INSERT INTO `recipe` VALUES (3553, 88, '美颜补血粥', '将前3味用米酒洗后，切成薄片，与红花共入布袋，加入鸡汤和清水，煎出药汁，去布袋后，入粳米，用旺火烧开，文火熬煮成粥。', '补血、理气、祛瘀、和色、祛斑功效。适用于血虚所致的面色苍白者，并可消除皮肤黑斑与黑眼圈。', '每日1剂，分次食用。', NULL, '当归10克，川芎3克，黄芪5克，红花5克，鸡汤1000毫升，粳米100克。');
INSERT INTO `recipe` VALUES (3554, 88, '女贞子酒', '冬季果实成熟时采收，将女贞子洗净，蒸后晒干，放入低度白酒中，加盖密封，每天振摇1次，1周后开始服用。', '补益肝肾，抗衰祛斑。主治老年脂褐质斑。', '每日1至2次，每次1小盅。', NULL, '女贞子200克，低度白酒500毫升。');
INSERT INTO `recipe` VALUES (3555, 88, '芪烧活鱼', '鲤鱼去鳞、鳃及内脏洗净，保留鱼子，在鲤鱼身上斜刀划成十字花，将油烧至六成热，下鲤鱼，煎至金黄色，捞出；洗净黄芪、党参,用纱布包好置砂锅中，加酱油、红糖、半熟鲤鱼、水发香菇、葱、蒜煮30分钟，加味精、姜末，水淀粉勾芡，即成。', '补中益气，健脾和胃，养血护颜，提高性欲。', '佐餐食用，吃肉喝汤。', NULL, '生黄芪30克，党参20克，活雌鲤鱼1条（约700克），香菇20克，精盐、料酒、酱油、红糖、葱、蒜、水淀粉、味精各适量。');
INSERT INTO `recipe` VALUES (3556, 88, '清炖猪蹄膀', '将猪蹄膀洗净后用刀剖开呈4至6瓣。锅烧开水，将蹄膀放入，烫透，煮沸数次，撇沫。坐大砂锅，放清水烧开，下蹄膀，放入泡发的红枣，煮沸，加料酒，转小火炖至蹄膀皮酥软，肉烂熟，下精盐、葱、胡椒面、姜、味精、白糖各适量，继续煨炖30分钟即成。', '和血脉，润肌肤，填肾精，健腰脚，强体增肥。主治老年单纯性消瘦。', '佐餐当菜，随意服食。', NULL, '猪蹄膀（带皮）1个，红枣5枚。');
INSERT INTO `recipe` VALUES (3557, 88, '人参蜂蜜粥', '将人参切片，置清水中浸泡1夜，连同泡参水与洗净的粳米一起放砂锅中，文火煨粥，粥将熟时放入蜂蜜、生姜汁、生韭菜汁调匀，再煮片刻即成。', '调中补气，润肠通便，丰肌泽肤功效。适用于因气虚而致的面色苍白无华者及有气血两虚表现的大便秘结的中老年人。', '每日1剂，分次食用。每6剂为1个疗程。间隔6-10日后可用下1个疗程。\r\n　　', NULL, '人参3克，蜂蜜50克，生姜汁5毫升，韭菜汁5毫升，粳米100克。');
INSERT INTO `recipe` VALUES (3558, 88, '双瓜菜窝头', '冬瓜去皮后斩成细末，甘薯斩成细泥，加葱、姜、盐、玉米粉调匀。将菜窝头捏好后，上笼用旺火蒸20分钟即成。', '此窝头具有清热生津、消肿止渴、宽中、润肤等功效。', '可作为主食食用。', NULL, '冬瓜300克，甘薯200克，玉米粉100克，盐5克，葱10克，姜10克。');
INSERT INTO `recipe` VALUES (3559, 88, '酸枣仁粥', '先以粳米加水煮粥至将熟，加入酸枣仁末再煮片刻即可。', '养心、安神、敛汗。适用于神经衰弱、心悸、失眠、多梦、黑眼圈。', '早晚温服。', NULL, '酸枣仁末15克、粳米100克。');
INSERT INTO `recipe` VALUES (3560, 88, '笋烧海参', '将水发海参切成长条，鲜笋切成片。将肉汤烧开，加入海参、竹笋，小火片刻，加入盐、糖、酱油、黄酒、淋入淀粉汁勾芡，汤汁明透即可。', '滋阴养血、养颜、润肤。', '佐餐食用。', NULL, '水发海参200克，鲜笋或水发竹笋100克，瘦猪肉汤500毫升。');
INSERT INTO `recipe` VALUES (3561, 88, '乌发汤', '1.将羊骨、羊头打破。羊肉洗净，入沸水中氽去血水，同羊骨、羊头一起放入锅内（羊骨垫底）。将以上药物用纱布袋装好扎紧口，放入锅内，并放入葱、生姜和白胡椒，加水适量。\r\n2.将锅置炉上，先用武火烧开，撇去浮沫，捞出羊肉切片再放入锅中，用文火炖1.5小时，待羊肉炖至熟透，将药包捞出不用。', '滋肝补肾，补血养气，乌须发。适用肝肾不足、血虚风燥的脱发，头发早白等症。', '服用时可加入味精、食盐等调料。吃肉喝汤，每日2次。', NULL, '怀山药、菟丝子、核桃仁各3 克、丹皮、泽泻、天麻各1.5克，枣皮2克，当归、红花、侧柏叶各1克，制首乌、黑芝麻、黑豆各5克，羊肉、羊骨各500克，羊头1个，葱、生姜、白胡椒、味精、食盐各适量。');
INSERT INTO `recipe` VALUES (3562, 88, '西洋参大枣粥', '先将西洋参洗净，置清水中浸泡一夜，切碎西洋参；洗净大枣；将西洋参、大枣、粟米及浸泡西洋参的清水一起倒入砂锅内，再加些清水，文火熬60分钟。', '久食本粥可使身体变得强壮，皮肤变得细腻红润。适用于四肢无力，气虚体弱，面色苍白无光泽者。', '每日1次，早晨食用。', NULL, '西洋参3克，大枣10枚，粟米100克。');
INSERT INTO `recipe` VALUES (3563, 88, '养容－红颜汤', '将水发木耳去杂质洗净切成小块。红枣去核，一同放入锅中加清水适量煮至红枣、木耳烂熟，放入白糖调味即可食用。', '常食能使面色红润、青春焕发，此汤尤益于女性。据本草记载，红枣具有润肺健脾、止咳、补五脏、疗虚损的作用，配以滋补强身的黑木耳，其补益、滋养、活血、养容的作用增强。', NULL, NULL, '红枣50克、水发黑木耳100克、白糖适量。');
INSERT INTO `recipe` VALUES (3564, 88, '养颜润肤－玉颜小米粥', NULL, '小米在我国民间自古就被认为是滋补营养的佳品，不仅补益脾肾，而且养颜润肤，因而有“小米养人”之说。补益脾肾，养颜润肤。辅以何首乌、鸡蛋、更有养益美容，延缓衰老之功效。', NULL, NULL, '小米50g、何首乌30g，洗净切片，用洁布包好，扎口，同入锅炉内加清水，小火煮约45分钟，米烂后取出药包，淋入少量鸡蛋汁，加白糖调味即成。');
INSERT INTO `recipe` VALUES (3565, 88, '薏米银耳羹', '将薏米用温水浸泡，洗净；水发银耳去杂质、洗净、撕成小朵。锅中加入冷水，放入银耳、薏米炖煮，待薏米熟透时，加入白糖烧沸，用湿淀粉勾成稀芡，加糖桂花搅匀即成。', '常食能润肤健美。', NULL, NULL, '薏米150克，水发银耳50克，白糖、糖桂花、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (3566, 89, '补肚益肾－杜仲牛膝猪脊骨汤', '猪脊骨洗净，去髓剁块，与杜仲、牛膝、红枣洗净，加水适量，文火煮1小时，调味，去药渣饮汤。', '补肚益肾，强筋健骨。适用于中风后遗症，属肝肾两虚者，症见偏瘫，肢体无力，或筋络拘挛等。', NULL, NULL, '杜仲15克，淮牛膝10克，红枣6枚，猪脊骨250克。');
INSERT INTO `recipe` VALUES (3567, 89, '补肝－何首乌粥', '以上同入砂锅煮粥。将熟时，加入红糖或冰糖少许调味，再煮一、二沸即可食用。', '可当早餐连续服用；也可每天1-2次，以10天为一疗程，间隔5天再服。补肝、益肾、养血。', NULL, NULL, '制首乌30克，煎取浓汁，去渣，加粳米100克，红枣3-5枚。');
INSERT INTO `recipe` VALUES (3568, 89, '补肝肾－旱莲草红枣汤', '将旱连草、红枣加清水2碗煎至1碗。', '补肝肾、滋阴补血、止血。对胃、十二指肠溃疡出血，失血性贫血有较好的辅助治疗效果。', '去渣饮服，每日2次。', NULL, '鲜旱连草50克、红枣8-10枚。');
INSERT INTO `recipe` VALUES (3569, 89, '补肝肾－何首乌煮鸡蛋', '将何首乌洗净，切成长3.3厘米、宽1.6厘米的块；把鸡蛋、何首乌放入铝锅内，加水适量，再放入葱、生姜、食盐、料酒等调料将铝锅置武火上烧沸，文火熬至蛋熟，将收取出用清水泡一下，将蛋壳剥去，再放入铝锅内煮2分钟。', '补肝肾，益精血，抗早衰。适用于血虚体弱、头晕眼花、须发早白、未老先衰、遗精、脱发以及血虚便秘等症。最适于虚不受补的患者。', NULL, NULL, '何首乌100克、鸡蛋2个、葱、生姜、食盐、料酒、味精、猪油各适量。');
INSERT INTO `recipe` VALUES (3570, 89, '补肝肾－黑芝麻粥', '黑芝麻30克洗净晒干，炒熟研碎，加大米60克煮成稀粥。', '可补肝肾，养阴血，润五脏，适用于阴虚肠燥者。', '早晚服用。', NULL, NULL);
INSERT INTO `recipe` VALUES (3571, 89, '补肝肾－鹿角胶粥', '先煮粳米和枸杞为粥后，加入鹿角胶，使其溶化，再煮二、三沸即可。', '补肝肾，益精血，主用于脑动脉硬化的防治。', '早晨空腹以粥代食，可稍加糖调味。半个月一个疗程。', NULL, '鹿角胶20克，枸杞30克，粳米100克。');
INSERT INTO `recipe` VALUES (3572, 89, '补肝肾－清脑羹', '将炙杜仲煎熬3次，收取药液4000毫升待用。干银耳用温热水发透择去杂质，揉碎，淘洗干净。冰糖用水溶化后，置文火上熬至色微黄时过滤待用。杜仲汁倒入锅中，下银耳，置武火上烧沸后，移文火上久熬至银耳熟烂，约3-4小时，再冲入冰糖水熬稠即成。', '补肝肾，降血压。适宜于肝肾两虚型高血压病。', NULL, NULL, '干银耳50克，炙杜仲50克，冰糖250克。');
INSERT INTO `recipe` VALUES (3573, 89, '补肝肾－桑椹酒', '将桑椹捣汁煮过，将米煮半熟沥干，与桑椹汁液拌合，蒸煮后下酒曲适量搅拌和匀，装入瓦坛内。', '补肝肾，明耳目，抗衰老。适用于肝肾不足之耳鸣耳聋、视物昏花等衰老征象。', '食用时，每次4汤匙，用开水冲服，或置锅中加水适量煮食。', NULL, '桑椹500克，大米3000克，酒曲适量。');
INSERT INTO `recipe` VALUES (3574, 89, '补肝肾－沙苑子白菊花茶', '上两味同入锅，加水煎煮成300毫升。', '平补肝肾，降低血脂，降压明目。主治高脂血症，高血压病出现头昏、目眩、腰痛、尿频等症，辨证属于肝肾不足类型者。', '分6次，当茶饮，温服。当日服食完。', NULL, '沙苑子30克，白菊花10克。');
INSERT INTO `recipe` VALUES (3575, 89, '补肝肾－山楂桑椹子煎', '山楂、桑椹子先用温开水浸泡，冲洗干净，入锅，加水适量，文火煎煮20分钟即成。', '补益肝肾，滋阴养血，消食降脂，软化血管。主治阴亏血虚型动脉硬化，症见头晕耳鸣，目暗昏花，须发早白，口干，便秘，失眠，遗精等。', '上下午分服，食果饮汤。', NULL, '鲜山楂30克（干品20克），桑椹子30克（干品20克）。');
INSERT INTO `recipe` VALUES (3576, 89, '补肝肾－松花淡菜粥', '皮蛋、淡菜、粳米共煮粥，加盐、味精调味即可。', '补益肝肾，益精血，除烦，降火。适用于高血压病。', '早晚温热服用。', NULL, '皮蛋1个、淡菜50克、粳米、盐、味精各适量。');
INSERT INTO `recipe` VALUES (3577, 89, '补肝肾－天麻酒', '将上述各味切碎，纳入纱布袋内，扎紧袋口，放入酒坛内，倒入白酒密封浸泡半个月以上，每天振摇1次，即成。', '补养肝肾，活血祛风。适用于冠心病、高血压、高脂血症及肥胖等症。', '每日2次，每次服10毫升。', NULL, '天麻72克，制首乌36克，丹参48克，黄芪12克，杜仲、淫羊藿16克，白酒2000克。');
INSERT INTO `recipe` VALUES (3578, 89, '补肝肾－益寿鸽蛋汤', '1.将枸杞子、龙眼肉、制黄精均洗净切碎，待用；冰糖砸碎装在碗内。\r\n2.锅置中火上注入清水约750克，加入以上3味同煮至沸后约15分钟，再把鸽蛋打破逐个下锅内，同时将冰糖屑下入锅中同煮至熟即成。', '补肝肾，益气血。适用于肺燥咳嗽、气血虚衰、智力减退等症。可作为老年体衰者之膳食。', '空腹服。每日服1次，连服7日。', NULL, '枸杞子10克、龙眼肉10克、制黄精10克、鸽蛋4个、冰糖50克。');
INSERT INTO `recipe` VALUES (3579, 89, '补肝肾－芝麻粥', '先将黑芝麻晒干后炒熟研碎。再与粳米同煮作粥。', '补肝肾，润五脏。适用于身体虚弱、头发早白、大便干燥、头晕目眩、贫血等症。', '可随意服食。', NULL, '黑芝麻30克，粳米100克。');
INSERT INTO `recipe` VALUES (3580, 89, '补肝益肾－牡瓦鸡肝汤', '先将牡蛎、瓦楞子研碎、煎煮30分钟；在将鸡肝切开，入锅中煮30分钟即成。', '补肝益肾、消积化痰。适宜于慢性咳嗽、低热。以及慢性肺结核的辅助治疗。', '食肉，饮汤。', NULL, '生牡蛎、瓦楞子各15克。公鸡肝1具。');
INSERT INTO `recipe` VALUES (3581, 89, '补肝益肾－强补猪肝', '将北五加皮、北五味子装入细纱布袋内扎紧口；香菇、枸杞子洗净；以上4味与猪肝共入砂锅内，加清水适量，盐少许，置文火上烧煮，待猪肝熟，捞出药袋，加入味精、酱油少许即可。', '补肝益肾，强身壮体，益寿延年。适用于久病体弱或年老体衰者。', '每日早晚各适量食之，每周2剂。', NULL, '猪肝250克，香菇30克，枸杞子30克，北五加皮10克，北五味子10克，盐、味精、酱油各适量。');
INSERT INTO `recipe` VALUES (3582, 89, '补肝益肾－银杞明目汤', NULL, '补肝益肾，明目美颜。适用于阴虚所致的视物模糊、两眼昏花、面色憔悴等。', '每日2次。佐餐食。', NULL, '银耳15克，枸杞15克，鸡肝100克，茉莉花24朵，水豆粉、料酒、姜汁、食盐各适量。');
INSERT INTO `recipe` VALUES (3583, 89, '补肝－芝麻粥', '先将黑芝麻晒干后炒熟研碎。再与粳米同煮作粥。', '补肝，润五脏。适用于身体虚弱、头发早白、大便干燥、头晕目眩、贫血等症。', '可随意服食。', NULL, '黑芝麻30克，粳米100克。');
INSERT INTO `recipe` VALUES (3584, 89, '补脾肾－红枣益脾糕', '把面粉发好放入盆中，红糖用玫瑰水化开，与小米面一起掺入面中，调成稀糊状。将方模子放入笼屉，把调好的面糊倒入一半用板刮平，放上小枣（去核），再将剩下的一半面糊倒上，在上面码上蜜枣，用旺火蒸20分钟即成。', '补脾肾，益气血。适宜于贫血、食欲不振，消化不良等症。', NULL, NULL, '面粉500克，小枣150克，蜜枣150克，红糖150克，小米面100克，玫瑰5克。');
INSERT INTO `recipe` VALUES (3585, 89, '补脾养肾－大枣黑豆丸', '先将黑豆放锅内炒出香味，凉后磨成细粉，再将大枣洗净，蒸熟去核，同黑豆共捣烂，捏成丸。', '此方具有补脾养肾之功效，适宜于脾胃虚弱，肾虚腰酸吞食用。', '每次15克，淡盐水或黄酒送服。', NULL, '大枣、黑豆备适量。');
INSERT INTO `recipe` VALUES (3586, 89, '补脾益肾－百合淮山猪胰汤', '将猪胰切块，洗净，以清水煮，同时放入百合、淮山药同煮，煮45分钟以后，取汤加少量盐饮用。', '补脾益肾。适宜于消渴症（即糖尿病）。', NULL, NULL, '百合20克，淮山药50克，猪胰脏100-150克。');
INSERT INTO `recipe` VALUES (3587, 89, '补肾健脑－养元鸡子', '鸡蛋打入碗中备用；小茴香、菟丝子、桑寄生、蜜炙黄芪入砂锅中，加水适量，煎煮2小时，趁沸时滤取药汁冲调蛋花，可依个人口味调以白糖或食盐。', '补肾健脑，强壮元阳。常服可治疗肾虚所致的早衰。', '每晚睡前服1次。', NULL, '鸡子2枚，小茴香6克，菟丝子15克，桑寄生15克，蜜炙黄芪15克。');
INSERT INTO `recipe` VALUES (3588, 89, '补肾健脾－山药羊乳羹', '1.将山药在锅中炒至微黄，轧碎为细末。\r\n2.将羊乳烧沸，加入山药末和白砂糖，搅匀即成。', '益气养阴、补肾健脾。适宜于慢性肾炎、慢性胃炎等病症及气阴不足之症。', '佐餐食用，每日1次。', NULL, '山药50克，新鲜羊乳500毫升，白砂糖或蜂蜜适量。');
INSERT INTO `recipe` VALUES (3589, 89, '补肾养肝－益智鳝段', '1.将菟丝子、干地黄煎两次，取汁过滤。\r\n2.水发木耳；调水淀粉。\r\n3.鳝鱼肉切成鱼片；笋切片；黄瓜切方片。\r\n4.将鳝鱼片放入碗内加水淀粉、蛋清、盐、药汁煨好，放温油中划开，待鱼片泛起，滗入笊篱。\r\n5.原勺留油，炸蒜末、姜末，下笋片、黄瓜片、木耳、鱼片，加盐、味精、白糖，烹料酒、高汤，淋香油出勺装盘，撒上胡椒面即成。', '菟丝子古人认为它是“补肾养肝，温脾助胃之药也”，具有益精髓、坚筋骨、止遗泄之作用。久服可明目轻身延年。菟丝子配合滋阴补血的地黄及益气健脾的鳝鱼制成此菜肴，确有益智增力之作用。', '佐餐食。', NULL, '干地黄12克，菟丝子12克，净鳝鱼肉250克，净笋10克，黄瓜10克，木耳3克，酱油、味精、盐、淀粉、料酒、胡椒面、姜末、蒜末、香油、白糖各适量，蛋清1个，高汤少许。');
INSERT INTO `recipe` VALUES (3590, 89, '补肾益精－玉竹海参火锅', '1.玉竹用水泡软，切片；水发海参淘尽涩水，沥干，切片；水发香菇去蒂，切为两半；水发玉兰片切片，沥水；午餐肉切片；毛肚片开，切块；猪舌头刮洗干净，入开水中煮至断生，去舌骨，切片；黄豆芽、小白菜择洗干净，沥水。以上各料分别装盘待用。\r\n2.高压锅置火上，下玉竹片、海参片、香菇片、汤、姜、葱、盐，煮开加阀压10分钟，停火降温后舀入火锅中，撇去浮沫，入味精、麻油，便可烫食其它原料。', '补肾益精、养血补气，适用于高血压、血管硬化、皮肤干燥、大便秘结，病后体虚等症。', '佐餐并饮汤。', NULL, '玉竹50克，水发海参500克，水发香菇、水发玉兰片、猪舌头各150克，午餐肉、牛毛肚各100克，黄豆芽、小白菜各200克，姜25克，葱15克，精盐6克，味精5克，麻油10克，鲜汤3000毫升。');
INSERT INTO `recipe` VALUES (3591, 89, '补益脾肾－玉颜小米粥', '洗净切片，用洁布包好，扎口，同入锅炉内加清水，小火煮约45分钟，米烂后取出药包，淋入少量鸡蛋汁，加白糖调味即成。', '小米在我国民间自古就被认为是滋补营养付佳品，不仅补益脾肾，而且养颜润肤，因而有“小米养人”之说。辅以何首乌、鸡蛋、更有养益美容，延缓衰老之功效。', NULL, NULL, '小米50g、何首乌30g。');
INSERT INTO `recipe` VALUES (3592, 89, '补阴血－芝麻白糖糊', '将芝麻拣净，放入铁锅内用文火炒香后晾凉，捣碎后，装入瓦罐内备用。', '补阴血，养肝肾，乌须发，长肌肉，填精髓。适用于平时调补，以抗早衰；肺燥咳嗽、皮肤干燥；肝肾阴虚的头发早白及老人便秘等症。', '食用时，每次2汤匙，放入碗中，再加白糖适量，用开水冲服。', NULL, '芝麻500克，白糖适量。');
INSERT INTO `recipe` VALUES (3593, 89, '肝肾亏损－枸杞子酒', '将枸杞子淘洗干净，剪碎，放入瓶中，再加入白烧酒，加盖密封，置阴凉干燥处，每日摇动1次，1周后即可饮用。边饮边添加白酒。', '益精气，抗早衰。适用于肝肾亏损和早衰。', '可根据自己的酒量，每晚餐前或临睡前饮用，通常每次服10-20克，不得过量。', NULL, '枸杞子200克，白烧酒500克。');
INSERT INTO `recipe` VALUES (3594, 89, '固肾调精－当归黄芪乌鸡汤', '乌鸡肉洗净，切块，当归、黄芪洗净，一齐置瓦锅内，加水适量，文火煮熟。调味服食。', '气血双补，固肾调精。适用于月经病气血不足、肾虚者，症见经期不准，经量少而色淡，神疲气短，多梦失眠，头昏腰酸，面色苍白等。', NULL, NULL, '乌鸡肉250克、当归15克、黄芪20克。');
INSERT INTO `recipe` VALUES (3595, 89, '健脾补肾－莲子鸡丁', '将鸡脯肉切丁，用蛋清、淀粉拌匀；香菇泡软，同火腿肉切成小菱形块；莲子去心，蒸熟备用。先将鸡丁在油锅中煸至七成熟，沥去油，加入莲子、香菇、火腿及适量调味品，翻炒几下出锅即成。', '健脾补肾，养心强身。适宜于食欲不振、消化不良、肢软无力、眩晕健忘、心烦失眠、遗尿、遗精者食用。健康人常食，能增强体质、益智延年。', '分数次佐餐食。', NULL, '净鸡脯肉250克，莲子60克，香菇10克，火腿肉10克，蛋清、淀粉、调料适量。');
INSERT INTO `recipe` VALUES (3596, 89, '健脾利湿－白茯苓粥', '将粳米淘洗干净，加茯苓粉，放铝锅内加水适量，置火上，先用武火烧开，后移文火上，煎熬至米烂，再放入味精、食盐、胡椒粉即成。', '健脾利湿。适用于老年性浮肿、肥胖症、小便不利、腹泻等症。', '当饭吃，吃饱，常服有效。', NULL, '白茯苓粉15克、粳米100克、味精、食盐胡椒粉各适量。');
INSERT INTO `recipe` VALUES (3597, 89, '健脾利湿－葫芦双皮汤', '将以上几味加水400克，煎至约150克，去渣即成。', '健脾利湿，消肿。适宜于慢性肾炎患者。', NULL, NULL, '葫芦壳50克，冬瓜皮、西瓜皮各30克，红枣10克。');
INSERT INTO `recipe` VALUES (3598, 89, '健脾利湿－土茯苓猪骨汤', '将猪脊骨同适量清水同放入锅中，武火烧沸，文火慢炖，炖至水成3碗量，去猪骨与上层浮油，加入土茯苓30-50克，煎至2碗，分2次饮，1日饮完。', '健脾利湿，补阴益髓。适宜于糖尿病患者。', NULL, NULL, '猪脊骨500克，土茯苓30-50克。');
INSERT INTO `recipe` VALUES (3599, 89, '酒精性脂肪肝－葛花荷叶茶', '先将荷叶切成丝状，与葛花同入锅中，加水适量，煮沸10分钟，去渣取汁即成。', '治疗酒精性脂肪肝。', '当茶频频饮用，当日服完。', NULL, '葛花15克，鲜荷叶60克（干荷叶30克）。');
INSERT INTO `recipe` VALUES (3600, 89, '开胃健脾－姜枣龙眼蜜膏', '先将龙眼肉、大枣肉洗净，放入锅内，加水适量，煎煮至熟烂时；加入姜汁、蜂蜜，文火煮沸，调匀；待冷后，装瓶即可。', '开胃健脾，益智养心。龙眼肉、大枣均有补益心脾、益智宁心之功效。蜂蜜有增强脑力、改善心肌功能作用。三物相合能益心脾、增智力，治心脾不足、心悸健忘等。适宜于思虑劳伤太过、心脾亏虚、纳呆、腹胀、健忘失眠者食用。', '每日2次，每次取1汤匙，开水化开，饭前食用。', NULL, '龙眼肉250克，大枣肉250克，蜂蜜250克，鲜姜汁2汤匙。');
INSERT INTO `recipe` VALUES (3601, 89, '利尿消肿－玉米须饮', '玉米须洗净，加水500克，小火煮30分钟，静置片刻，汁液滤过，加白糖适量。', '利尿消肿。适宜于水肿、慢性肾炎患者。', NULL, NULL, '玉米须100克。');
INSERT INTO `recipe` VALUES (3602, 89, '脾肾阳虚－三七粥', '水洗净，蒸软，切成薄片，用植物油煎黄（切勿煎枯焦），研粉（或用成药“熟田七粉”）。', NULL, '用鸡汤或牛奶或糖水、肉汤送服，每次服3—5克，每日3次。（或将三七、瘦猪肉适量，共蒸熟服食）。', NULL, '三七适量。');
INSERT INTO `recipe` VALUES (3603, 89, '脾肾阳虚－瘦猪肉', '将猪肉先炒后煮，熟后，放入河车粉（成药）6克，并调味，连汤服食，每周2—3次。', NULL, NULL, NULL, '瘦猪肉250克（切块），生姜20克（切丝）。');
INSERT INTO `recipe` VALUES (3604, 89, '平肝潜阳－天麻石决明猪脑汤', '将山3味同放锅中，加水适量，以文火烧1小时，炖成厚羹捞去天麻、石决明即可。', '平肝潜阳。适用于肝阳头痛、头晕胀痛、心烦易怒、睡眠不宁等症。', '食脑，喝汤。可分2-3次食完。宜常服。', NULL, '猪脑1个，天麻10克，石决明15克。');
INSERT INTO `recipe` VALUES (3605, 89, '平肝－天麻钩藤茶', '将天麻、钩藤洗净，加水适量煎煮2次，去渣；以其汁液冲泡绿茶盖严浸泡5-10分钟即可。', '平肝熄风镇静。用于肝阳上亢之高血压、头晕目眩、神经衰弱、四肢麻木等。', '每日1剂，代茶饮用。', NULL, '天麻5克，钩藤6克，绿茶10克。');
INSERT INTO `recipe` VALUES (3606, 89, '清肝泻火－夏枯草瘦肉汤', '将猪瘦肉洗净、切片，与夏枯草、石上柏洗净，一齐放置瓦锅内，加水适量，文火煮30分钟，调味服食用。', '清肝泻火，利胆退黄。适用于急性黄疸型肝炎属湿热者，症见面目皆黄，胁肋胀痛，胃腹胀满，不欲饮食，小便短黄等。', NULL, NULL, '猪瘦肉100克、夏枯草15克、石上柏20克。');
INSERT INTO `recipe` VALUES (3607, 89, '清热解毒－炒绿豆芽', '绿豆芽洗净，起油锅炒熟，调味食用。', '清热解毒，遍窍利湿。适用于泌尿系统感染属湿热者，症见尿频尿急，涩痛不畅等。', NULL, NULL, '绿豆芽250克。');
INSERT INTO `recipe` VALUES (3608, 89, '清热解毒－海带绿豆汤', '海带洗净，切丝，绿豆洗净，加水适量，文火煮至豆烂，加入白糖，食豆饮汤。', '清热解毒，利水通淋。适用于肾盂肾炎，肾结石属湿热蕴结者，症见尿频尿急，淋沥不畅，或溺时涩痛，尿中带血等。', NULL, NULL, '鲜海带30克，绿豆50克，白糖适量。');
INSERT INTO `recipe` VALUES (3609, 89, '清热解毒－蒲公英粥', NULL, '清热解毒，消肿散结适应症。肝炎、胆囊炎以及急性乳腺炎，急性扁桃体炎，疔疮热毒，尿路感染，急性结膜炎等。', NULL, NULL, '蒲公英40-60克（鲜品用量为60-90克），粳米50-100克。');
INSERT INTO `recipe` VALUES (3610, 89, '清热解毒－芹菜蜜汁', NULL, '清热解毒，养肝。适应症肝炎。', NULL, NULL, '鲜芹菜100-150克，蜂蜜适量。');
INSERT INTO `recipe` VALUES (3611, 89, '清热解毒－鱼腥草拌莴笋', '将鱼腥草择去杂质老根，淘洗干净，用沸水略焯后捞出，加1克食盐拌和腌渍待用。鲜莴笋去皮切成3-4厘米的节，再切成细丝，用盐少许腌渍沥水待用。莴笋丝放入盘内，加入鱼腥草，酱油、味精、麻油、醋、姜末、葱花、蒜米和匀入味即成。', '清热解毒，利湿排脓。适宜于尿路感染、肺脓疡患者。', NULL, NULL, '鲜鱼腥草100克，莴笋500克，葱、姜、蒜酱油、醋、味精等适量。');
INSERT INTO `recipe` VALUES (3612, 89, '清热解毒－栀子仁粥', '粳米洗净，加水适量，文火煮成稀粥。再放入栀子仁细末，煮2分钟，调味食用。', '清热解毒，利湿退黄。适用于急性黄疸型肝炎属湿热者，症见面目俱黄，胁肋胀痛，食欲不振，小便短黄等。', NULL, NULL, '栀子仁6克，粳米50克。');
INSERT INTO `recipe` VALUES (3613, 89, '清热平肝－罗布麻茶', '以上4味开水冲泡，代茶常饮。', '清热平肝，活血化瘀。适宜于高血压、高血脂症。', NULL, NULL, '罗布麻叶6克，山楂15克，五味子5克，冰糖适量（肥胖病人可不放糖）。');
INSERT INTO `recipe` VALUES (3614, 89, '肾虚腰痛－人参山药', '同煮熟食。', '肾虚腰痛症。', NULL, NULL, '人参6克，山药30克，大枣10枚，瘦猪肉适量。');
INSERT INTO `recipe` VALUES (3615, 89, '肾虚腰痛－首乌黑豆', '制首乌水煎取汁50毫升，黑豆泡胀，洗净，煮1小时后加入首乌汁再煮30分钟，得1小碗，加食盐猪油调味，一次食完。', '肾虚腰痛证，适用于肾阴虚腰痛。', '日服1剂。', NULL, '制首乌10克，黑豆30克。');
INSERT INTO `recipe` VALUES (3616, 89, '肾虚腰痛－猪腰杜仲', '杜仲开水浸泡后切丝，用花生油热锅后，放猪腰、杜仲炒熟，少加调料食之。', '适用于肾阳虚腰痛。', NULL, NULL, '猪腰子2枚，洗净切片，杜仲10克。');
INSERT INTO `recipe` VALUES (3617, 89, '通痹－芝麻粥', '先将芝麻炒干出香味，另煮米成粥，将熟时加入芝麻、砂糖即成。', '通痹、润肠、益五脏、坚筋骨。适宜于肝肾不足、风湿内侵引起的肢节疼痛，或因肺气不足以致大肠不利的便秘等症。', NULL, NULL, '芝麻仁6克，白米30克，白砂糖少许。');
INSERT INTO `recipe` VALUES (3618, 89, '温宫补肾－鹿茸炖乌鸡', '乌鸡洗净，切块，与鹿茸一齐置炖盅内。加开水适量，文火隔水炖熟，调味服食。', '温宫补肾，益精养血。适用于宫冷、肾虚精衰不孕者，症见婚久不孕，月经不调，经血色淡量少，小腹冷感，腰酸无力等。', NULL, NULL, '乌鸡250克、鹿茸10克。');
INSERT INTO `recipe` VALUES (3619, 89, '泄热平肝－冬瓜鲩鱼汤', '鲩鱼去鳞洗净，用植物油煎至黄色。加开水适量，再下冬瓜，文火煮30分钟。调味食用。', '泄热平肝，利水消痰。适用于高血压病属肝阳上亢型者，症见胸满烦热，头痛眼花等。', NULL, NULL, '冬瓜200克、鲩鱼150克（鱼尾部较好）。');
INSERT INTO `recipe` VALUES (3620, 89, '泻肝利水－苦瓜荠菜瘦肉汤', '猪瘦肉洗净切片，苦瓜去瓤切片，荠菜去根洗净。先将荠菜加水适量，文火煮30分钟，捞出，再入苦瓜、瘦肉煮熟，调味，食肉菜饮汤。', '清心祛暑，泻肝利水。适用于高血压病属肝阳上亢型者症见头晕头痛，心悸失眠，心烦易怒，口渴咽干，或目赤肿痛等。', NULL, NULL, '鲜苦瓜200克、荠菜50克、猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3621, 89, '养肝－芹菜蜜汁', '将芹菜洗净捣烂取汁，加蜂蜜适量炖服。', '清热解毒、养肝。适用于肝炎。', '每日1次，疗程不限。', NULL, '鲜芹菜150克、蜂蜜适量。');
INSERT INTO `recipe` VALUES (3622, 89, '养肝肾－鸡肝粥', '将鸡肝切细，与菟丝子、粟米同煮为粥，粥将熟，加入葱白、盐及胡椒粉调味，再煮一二沸即成。', '养肝肾，壮阳事。治肝肾不足、筋骨痿弱、阳痿、早泄、泄泻等。', NULL, NULL, '雄鸡肝1具，菟丝子末15克，粟米100克，葱白2段，食盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (3623, 89, '养肝肾－菊花酒', '1.将甘菊花、当归、生地黄、枸杞入锅中，加水煎汁，用纱布过滤待用。\r\n2.将大米煮半熟沥干，和药汁混匀蒸熟，再拌适量酒曲，装入瓦坛中，四周用棉花或稻草保温发酵，直到味甜即成。', '养肝肾，利头目，抗早衰。适用于肝肾不足的头痛、头晕、耳鸣目眩，手足震颤等症。', '每日2次，每次3汤匙，用开水冲服。', NULL, '甘菊花2000克，生地黄1000克，当归500克，枸杞500克，大米3000克，酒曲适量。');
INSERT INTO `recipe` VALUES (3624, 89, '养肝益脑－桑椹糯米', '煮粥服食。', '可养肝益脑，滋阴明目，润肠通便，适用于肝阴亏虚便秘者。', NULL, NULL, '桑椹30克，糯米60克，冰糖适量。');
INSERT INTO `recipe` VALUES (3625, 89, '益肾补脑－胡桃粳米粥', '同煮成粥。', '胡桃肉具有益肾补脑、止咳定喘的功效，与粳米同煮成粥，是冬季常见病哮喘病的食疗方，经常食用可防止喘咳旧病复发。', NULL, NULL, '胡桃仁50克，粳米100克。');
INSERT INTO `recipe` VALUES (3626, 89, '脂肪肝－虫草香菇炖豆腐', '先将冬虫夏草、香菇用冷水泡发，洗净，香菇切丝，与豆腐同入油锅，熘炒片刻，加精盐、味精、葱花、姜末等调料适量，加清汤少许，文火烧煮30分钟，即成。', '治疗脂肪肝。', '佐餐当菜，随量服食，当日吃完。', NULL, '冬虫夏草10克，香菇20克，豆腐200克。');
INSERT INTO `recipe` VALUES (3627, 89, '脂肪肝－党参茯苓扁豆粥', '将党参、茯苓洗净，切片，与白扁豆同日入锅中。加水煎煮30分钟，投入淘净的粳米，文火煮成稠粥即成。', '治疗脂肪肝。', '早晚各1次温服，党参、茯苓、白扁豆可同时嚼食。本食疗方连续服食3-4个月。', NULL, '党参10克，茯苓10克，白扁豆20克，粳米100克。');
INSERT INTO `recipe` VALUES (3628, 89, '脂肪肝合并肥胖症－橘皮苡仁粉', '将橘皮、苡仁洗净，晒干或烘干，共研成细粉，瓶装备用。', '治疗脂肪肝合并肥胖症。', '每日2次，每次15克，用温开水送服。', NULL, '橘皮250克，苡仁300克。');
INSERT INTO `recipe` VALUES (3629, 89, '脂肪肝－花生豆奶', '将黄豆和花生淘洗干净后，用冷水浸泡6小时。待黄豆、花生充分浸胀，加清水500毫升磨碎，用洁净纱布滤汁去渣。将滤液置锅中煮沸，加适量白糖调味，即可饮用。', '治疗脂肪肝。', '早晚各1次，温服。', NULL, '黄豆50克，花生10克。');
INSERT INTO `recipe` VALUES (3630, 89, '脂肪肝－山楂蜂蜜饮', '将山楂洗净，晾干，切成两半，入锅，加水煎煮30分钟，兑入蜂蜜即成。', '治疗脂肪肝。', '分2次，吃山楂饮汤，当日服完。', NULL, '生山楂40克，蜂蜜10克。');
INSERT INTO `recipe` VALUES (3631, 89, '滋补肝肾－四子粥', '诸药洗净，用纱布包，与大米洗净，一齐置瓦锅内，加水适量，文火煮成稀粥，调味食用。', '滋补肝肾。', NULL, NULL, '菟丝子20克、覆盆子20克、枸杞子15克、茺蔚子15克、大米50克。');
INSERT INTO `recipe` VALUES (3632, 89, '滋肝明目－兔肝鸡蛋汤', '先将油烧热加盐，加水适量，烧沸；兔肝洗净，去胆囊，切为数片放在锅中，至肝变色，放香菜，鸡蛋去壳搅匀，倒入锅中致熟即可。\r\n　　', '补阴养血、滋肝明目。适宜于肝血不足之夜盲症。也可用于肝经有目赤肿痛等症的治疗。', '食肉，饮汤。', NULL, '新鲜兔肝1具，红皮鸡蛋2只，植物油、精盐、香菜等各适量。');
INSERT INTO `recipe` VALUES (3633, 89, '滋养肝肾－蜂蜜桑椹膏', '将鲜红熟桑椹洗净，放入大碗内用擀面杖擂烂，倒入白纱布滤取汁液，然后将汁液放入瓦锅内熬至稍浓，加入蜂蜜，不停搅匀，煮成膏状，冷却后装瓶备用。', '滋养肝肾，补益气血。适用于须发早白、病后血虚、未老先衰等症。', '每日早晚各服1次，每次1-2汤匙，温开水送服。', NULL, '鲜红熟桑椹200克、蜂蜜50克。');
INSERT INTO `recipe` VALUES (3634, 89, '滋养肝肾－杞精炖鹌鹑', '将鹌鹑宰杀，去毛及内脏，冼净，枸杞、黄精装鹌鹑腹内，加水适量，文火炖酥，加盐、味精适量调味即成。', '滋养肝肾，补精益智。鹌鹑是良好的益智食品，含有丰富蛋白质、无机盐、维生素等，有助于小儿发育、增进食欲、提高记忆力。脑力劳动者常食，能消除眩晕健忘症状，能提高智力，有健脑养神之作用；枸杞子能补肾益精、养肝明目、抗疲劳、增强体力和智力；黄精能补脾润肺、养阴生津、强化筋骨、益智强身。几味同用更增加其滋补和益智作用。适宜于肝肾不足、精血亏虚而见神疲乏力、腰膝酸软、眩晕健忘者服食。', '弃药，吃肉喝汤，每日1次。', NULL, '鹌鹑1只，枸杞子、黄精各30克，盐、味精少许。');
INSERT INTO `recipe` VALUES (3635, 89, '滋阴补肾－银耳枸杞汤', '将银耳放入锅内加入煮一段时间后加入枸杞，用文火煎成稠汁状，加糖即成。', '滋阴补肾。', '隔日服食1次。', NULL, '水发银耳50克，枸杞25克。');
INSERT INTO `recipe` VALUES (3636, 90, '清肺补血－川明参烧雅鱼', '1.将川明参浸泡1夜，切成3厘米长的段；雅鱼宰杀后，去鳞、腮、肠杂；姜切片，葱切段。\r\n2.将雅鱼放入六成热的油锅内，炸3分钟，沥干油分，备用。\r\n3.将炒锅置武火上烧热，下入姜、葱爆香，再下雅鱼、料酒、川明参、盐、味精、白糖、鲜汤，烧熟即成。', '清肺、化痰、平肝、和胃、补气、补血。适用于痰火咳嗽。喘逆、头晕、呕吐、目赤、白带、疔毒疮疡、气血两亏等症。', '佐餐食用。', NULL, '川明参30克，雅鱼（或草鱼）1条，料酒、姜、葱各10克，盐4克，味精、白糖各3克，鲜汤300克，素油50克。');
INSERT INTO `recipe` VALUES (3637, 90, '补气血－桂圆童子鸡', '1.将鸡去内脏、洗净，放入沸水中氽一下，捞出，放入钵或汤锅；再加桂圆、料酒、葱、姜、盐和清水。\r\n2.上笼蒸1小时左右，取出葱、姜即可。', '补气血，安心神。适用于贫血、失眠、心悸。健康人食用能使精力更加充沛。', '佐餐食用。', NULL, '童子鸡1只（重约1000克），桂圆肉30克，葱、姜、料酒、盐各适量。');
INSERT INTO `recipe` VALUES (3638, 90, '补气血－红枣粳米粥', '红枣10-15枚与粳米100克同煮。', '治疗体弱，病后身虚，气血两亏，营养不良，脾胃虚弱、贫血等，并有保护肝脏的作用。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3639, 90, '补气养血－参砂蒸蛋', '1.先将苏条参或潞党参、山药研成细末，与朱砂拌匀备用。\r\n2.每次用6克混合药末，与鸡蛋在碗内搅均匀，蒸锅上蒸熟即成。', '补气养血，安神。适用于气血虚、心脾不足之心悸、失眠、食少纳呆等症。血脂高，肝有器质性病变者不宜服用。', '每日晨起一碗蒸蛋，连服半月以上。', NULL, '苏条参（或潞党参）、淮山药各30克，朱砂6克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3640, 90, '补血－美颜补血粥', '将前3味用米酒洗后，切成薄片，与红花共入布袋，加入鸡汤和清水，煎出药汁，去布袋后，入粳米，用旺火烧开，文火熬煮成粥。', '补血、理气、祛瘀、和色、祛斑功效。适用于血虚所致的面色苍白者，并可消除皮肤黑斑与黑眼圈。', '每日1剂，分次食用。', NULL, '当归10克，川芎3克，黄芪5克，红花5克，鸡汤1000毫升，粳米100克。');
INSERT INTO `recipe` VALUES (3641, 90, '补血益气－归参炖母鸡', '将母鸡宰杀后，去毛，去内脏，洗净。将洗净的当归、党参、葱、姜、料酒、食盐一起放入鸡腹中，再把鸡放入砂锅内，加适量水，把砂锅放在武火上烧沸，然后再用文火炖至鸡肉熟烂即成。', '补血益气，健脾补虚。适用于久病体衰、妇女月经不调。久服可强壮身体、轻身延年。', '可分餐食用，吃肉，喝汤。', NULL, '当归15克，党参20克，母鸡1只，葱、姜、料酒、食盐各适量。');
INSERT INTO `recipe` VALUES (3642, 90, '补益气血－乌鸡汤', '将鸡切块，与上述各味同煮，文火炖烂。吃肉，喝汤，每日2次。', '温中健胃，补益气血。适用于气血双亏的妇女痛经。', NULL, NULL, '雄乌骨鸡500克，陈皮3克，良姜3克，胡椒6克，草果2枚，葱、醋适量。');
INSERT INTO `recipe` VALUES (3643, 90, '补益肾阳－鹿茸酒', '将鹿茸、山药浸泡在酒中，封固7天后饮用。每次饮1小盅。', '补益肾阳，固摄膀胱。凡属肾阳不足，气化失摄而引起的小腹冷痛、阴囊湿凉、男子阳痿、女子白带清稀、夜尿频多等症，均可常饮此酒。', NULL, NULL, '鹿茸3-6克，山药30-60克，白酒500克。');
INSERT INTO `recipe` VALUES (3644, 90, '红枣炖兔肉－补中益气', '兔肉洗净切块，红枣（去核），洗净，一齐置炖盅内，加开水适量，调味，文火隔水炖熟，食肉饮汤。', '补中益气，养血强力。适用于贫血、病后身体虚弱者，症见疲乏怠倦，四肢无力，少气懒言，食少纳差，心悸健忘等。', NULL, NULL, '兔肉100克，红枣10枚。');
INSERT INTO `recipe` VALUES (3645, 90, '大补气血－双参肉', '1.将海参发好，切块；香菇洗净，切丝；瘦猪肉洗净，切小块；竹笋切片。\r\n2.将以上4味与人参、青豌豆一齐放砂锅内，加清水适量炖煮，以瘦猪肉熟烂止，加入味精、精盐、香油各少许即可。', '大补气血，强壮身体，消除疲劳。适用于久病体虚不复，或年老体衰、精神萎靡、身体疲倦者。', '每日1-2次，每次适量。每周2剂。', NULL, '鲜人参15克，海参150克，猪瘦肉250克，香菇30克，青豌豆60克，竹笋60克，味精，精盐、香油各适量。');
INSERT INTO `recipe` VALUES (3646, 90, '气血双补－当归黄芪乌鸡汤', '乌鸡肉洗净，切块，当归、黄芪洗净，一齐置瓦锅内，加水适量，文火煮熟。调味服食。', '气血双补，固肾调精。适用于月经病气血不足、肾虚者，症见经期不准，经量少而色淡，神疲气短，多梦失眠，头昏腰酸，面色苍白等。', NULL, NULL, '乌鸡肉250克、当归15克、黄芪20克。');
INSERT INTO `recipe` VALUES (3647, 90, '气血虚弱－当归生姜羊肉汤', '将羊肉去油膜切成方块，与当归、生姜同炖，取出当归、生姜，加入精盐调味即可。', '温养阴血止痛。适用于气血虚弱、血虚气寒引起的腹部隐隐作痛。', '食肉喝汤，早晚温热食用。', NULL, '当归10克，生姜20克，羊肉100克，精盐适量。');
INSERT INTO `recipe` VALUES (3648, 90, '养血补气－玉竹海参火锅', '1.玉竹用水泡软，切片；水发海参淘尽涩水，沥干，切片；水发香菇去蒂，切为两半；水发玉兰片切片，沥水；午餐肉切片；毛肚片开，切块；猪舌头刮洗干净，入开水中煮至断生，去舌骨，切片；黄豆芽、小白菜择洗干净，沥水。以上各料分别装盘待用。\r\n2.高压锅置火上，下玉竹片、海参片、香菇片、汤、姜、葱、盐，煮开加阀压10分钟，停火降温后舀入火锅中，撇去浮沫，入味精、麻油，便可烫食其它原料。', '补肾益精、养血补气，适用于高血压、血管硬化、皮肤干燥、大便秘结，病后体虚等症。', '佐餐并饮汤。', NULL, '玉竹50克，水发海参500克，水发香菇、水发玉兰片、猪舌头各150克，午餐肉、牛毛肚各100克，黄豆芽、小白菜各200克，姜25克，葱15克，精盐6克，味精5克，麻油10克，鲜汤3000毫升。');
INSERT INTO `recipe` VALUES (3649, 90, '养血止血－阿胶粥', '将阿胶捣碎。将糯米煮粥，待熟，入阿胶稍煮，搅令烊化即成。', '养血止血，滋阴润肺，安胎。适宜于咯血、吐血、便血、尿血等多种血症。', NULL, NULL, '阿胶15克，糯米100克。');
INSERT INTO `recipe` VALUES (3650, 90, '养血止血－菠菜粥', '先将菠菜洗净，放滚水中烫半熟，取出切碎。粳米成粥后，将菠菜放入，拌匀，煮沸即成。', '养血止血，敛阴润燥，通利肠胃。适宜于习惯性便秘、高血压等症。大便干结，痔疮出血患者宜经常服用。', NULL, NULL, '新鲜菠菜100克，粳米100克。');
INSERT INTO `recipe` VALUES (3651, 90, '益气补虚－黄精蒸鸡', '将鸡宰杀，去毛及内脏，洗净，剁成1寸见方的块，放入沸水锅烫3分钟捞出，洗净血沫，装入气锅内，加入葱、姜、食盐、川椒、味精，再加入黄精、党参、山药，盖好汽锅盖，上笼蒸3小时即成。', '益气补虚。适宜于体倦无力、精神疲惫、体力及智力下降者服食。湿热内盛者不宜食用；感冒时暂停。', '空腹分顿食用，吃鸡喝汤。', NULL, '黄精、党参、山药各30克，母鸡1只（重约1000克），生姜、川椒、食盐、味精各适量。');
INSERT INTO `recipe` VALUES (3652, 90, '益气健脾－参灵甲鱼', '将甲鱼切块，同以上各味药及调料同放大碗内，加水适量，放蒸锅内蒸至甲鱼熟烂即可。', '益气健脾，消除疲劳。', '吃肉喝汤。', NULL, '党参、浮小麦各15克，茯苓10克，灵芝、大枣各6克，甲鱼200克，火腿50克，葱、姜各20克，鸡汤、盐、味精各适量。');
INSERT INTO `recipe` VALUES (3653, 90, '补脾肾－红枣益脾糕', '把面粉发好放入盆中，红糖用玫瑰水化开，与小米面一起掺入面中，调成稀糊状。将方模子放入笼屉，把调好的面糊倒入一半用板刮平，放上小枣（去核），再将剩下的一半面糊倒上，在上面码上蜜枣，用旺火蒸20分钟即成。', '补脾肾，益气血。适宜于贫血、食欲不振，消化不良等症。', NULL, NULL, '面粉500克，小枣150克，蜜枣150克，红糖150克，小米面100克，玫瑰5克。');
INSERT INTO `recipe` VALUES (3654, 90, '滋阳益气－虫草红枣炖甲鱼', '1.将甲鱼宰杀，去内脏，洗净，剁成4大块，放锅中煮沸捞出，割开四肢，剥去腿油洗净。\r\n2.冬虫夏草洗净；红枣用开水浸泡。\r\n3.甲鱼放汤碗中，上放冬虫夏草、红枣，加料酒、盐、葱段、姜片、蒜瓣和鸡清汤，上笼隔水蒸2小时，取出，拣去葱、姜即成。 \r\n　　', '滋阳益气，补肾固精，抗疲劳。适用于腰膝酸软、月经不调、遗精、阳痿、早泄、乏力等症。健康人常食，可增强体力、防病延年、消除疲劳。', '佐餐食。', NULL, '冬虫夏草10克，活甲鱼1只，红枣20克，料酒、盐、葱、姜、蒜、鸡清汤各适量。');
INSERT INTO `recipe` VALUES (3655, 90, '滋阴润燥－白兰花猪瘦肉汤', '猪瘦肉洗净切片，白兰花洗净，一齐置瓦锅内加水适量，文火煮30分钟，调味食用。', '滋阴润燥，行气化浊。适用于妇女白带过多症。', NULL, NULL, '鲜白兰花30克（干品10克），猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3656, 91, '补阳－狗肉粳米', '将狗肉洗净，切成小块，与粳米、生姜同煮为粥。', '对年老体弱、阳气不足、遗精早泄、阳痿、营养不良、畏寒肢冷者均具有滋补和治疗作用。', '分次服下。', NULL, '狗肉250克，粳米450克，生姜适量。');
INSERT INTO `recipe` VALUES (3657, 91, '通阳散结－薤白粥', '薤白清洗干净，切成碎米粒状。粳米淘洗干净，放入锅内、加薤白、清水，上火烧开，换小火慢慢熬煮成粥。', '理气宽胸，通阳散结，止痛。适宜于冠心病胸闷不舒或心绞痛。', NULL, NULL, '薤白50克，粳米160克。');
INSERT INTO `recipe` VALUES (3658, 91, '胸阳痹阻－丹参茶', '开水泡，代茶饮，至味淡为止。', '适宜冠心病患者。', '每日1-2次。', NULL, '丹参6克。');
INSERT INTO `recipe` VALUES (3659, 91, '胸阳痹阻－山楂白扁豆', '将前二味加水煮熟，将韭菜用开水烫熟。', '适宜冠心病患者。', '再用红糖调味服食，每天1剂。', NULL, '山楂30克，白扁豆20克，韭菜30克，红糖40克。');
INSERT INTO `recipe` VALUES (3660, 91, '益气－参麦团鱼', '煮汤食。', '本品有滋阴、益气、补虚的作用。对于阳虚潮热、骨蒸盗汗、神疲短气等虚弱病人，有辅助治疗作用。', NULL, NULL, '人参5克、浮小麦20克、团鱼一只（约500-1000克）、生姜片10克、食盐6克、鸡汤500克、绍酒15克、味精0.5克。');
INSERT INTO `recipe` VALUES (3661, 91, '阴虚阳亢－白黑木耳', '用温水浸软并洗净，放入碗内，加水和冰糖适量，蒸1-2小时，一次或分次服用，每日1剂。或单用黑木耳3-5克，冰糖适量，按上法蒸制，睡前服。', '适宜高血压病患者。', NULL, NULL, '白木耳、黑木耳各10克。');
INSERT INTO `recipe` VALUES (3662, 91, '阴虚阳亢－黄精黄芪', '以上加水2碗煮汤饮用。', '适宜高血压病患者。', NULL, NULL, '黄精15克，黄芪10克，枸杞子5克，瘦猪肉100克。');
INSERT INTO `recipe` VALUES (3663, 91, '阴虚阳亢－制首乌', '以上两味水煎2次后去渣取汁，调入蜂蜜2汤匙。', '适宜高血压病患者。', '分2次服，每日1剂。', NULL, '制首乌、丹参各20克。');
INSERT INTO `recipe` VALUES (3664, 91, '阴阳两虚－海参冰糖', '以上煮烂。', '适宜高血压病患者。', '早晨空腹饮用，每日1次，常服。', NULL, '海参30克，冰糖适量。');
INSERT INTO `recipe` VALUES (3665, 91, '阴阳两虚－桃仁红糖', '前二味捣碎，与红糖混合。', '适宜冠心病患者。', '每次服30克，每日3次，温开水送下。', NULL, '核桃仁750克，桃仁250克，红糖1000克。');
INSERT INTO `recipe` VALUES (3666, 91, '阴阳两虚－鲜磨菇', '以上煮食。', NULL, '每日1次，常服。', NULL, '鲜磨菇或香菇30克（干品减半）。');
INSERT INTO `recipe` VALUES (3667, 91, '滋阴清热－萝卜煲鲍鱼', '将干鲍鱼泡发，鲜萝卜去皮，砂锅置火上，放入清水及鲍鱼、萝卜，共同煲汤服食。', '滋阴清热，宽中止渴。适宜于糖尿病患者。　', NULL, NULL, '干鲍鱼20-25克，鲜萝卜250-300克。');
INSERT INTO `recipe` VALUES (3668, 91, '滋阴润燥－白兰花猪瘦肉汤', '猪瘦肉洗净切片，白兰花洗净，一齐置瓦锅内加水适量，文火煮30分钟，调味食用。', '滋阴润燥，行气化浊。适用于妇女白带过多症。', NULL, NULL, '鲜白兰花30克（干品10克）、猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3669, 91, '滋阴润燥－鸡蛋甜豆浆', '豆浆置锅中煮沸，鸡蛋打匀加入。煮沸后饮用。', '滋阴润燥，清肺化痰。适用于肺结核久咳不愈者，症见咯痰不爽，低热，咽干，身体虚弱等。', NULL, NULL, '鸡蛋1个、甜豆浆350毫升。');
INSERT INTO `recipe` VALUES (3670, 92, '补肺益肾－羊蜜膏', '先熬羊脂令沸，次下羊髓又令沸，再放入蜜、地黄汁、生姜汁，不住搅拌，微火熬数沸成膏。每日空腹用温黄酒调1匙食之。', '补肺益肾，除骨蒸热。适宜于因虚劳而引起的腰痛、咳嗽、骨蒸潮热等症。', NULL, NULL, '熟羊脂150克，熟羊髓150克，白沙蜜150克（炼净），生姜汁适量，生地黄汁150克。');
INSERT INTO `recipe` VALUES (3671, 92, '补脾肺－猪肺粥', '将猪肺洗净加水适量，放入料酒，煮至七成熟，捞出切成丁，同淘净的大米、薏苡仁一起入锅内，并放入葱、姜、食盐、味精，先置急火上烧沸，然后改文火煨炖，米熟烂即可。', '补脾肺、止咳。适用于慢性支气管炎。', '可当饭吃。经常食用效果显著。', NULL, '猪肺500克、梗米100克、薏苡仁50克、料酒、葱、姜、食盐、味精各适量。');
INSERT INTO `recipe` VALUES (3672, 92, '补虚平喘－银杏蒸鸭', '银杏去壳，开水煮熟后去皮、芯，再用开水焯后混入杀好去骨的鸭肉中。加清汤，上笼蒸2小时至鸭肉熟烂后食用。', '补虚平喘、利水退肿。适宜于晚期肺癌喘息无力、全身虚弱、痰多者。', '佐餐，可经常食用。', NULL, '银杏200克、白鸭1只。');
INSERT INTO `recipe` VALUES (3673, 92, '肺癌血热－仙枣赤豆粥', '取生薏米、赤小豆加温水浸泡半日；仙鹤草用纱布包好；大枣去核备用；取上述诸药加水常法共煮成稀粥，加入白糖调味即可。', '清热解毒、活血止血。适用于肺癌血热症。', '佐餐食用，连服15日。', NULL, '仙鹤草60克、大枣20枚、赤小豆50克、生薏米100克、白糖适量。');
INSERT INTO `recipe` VALUES (3674, 92, '肺结核（肺阴不足症）－白芨鸡蛋', '早晨用开水冲成蛋花服。', '适宜肺结核患者。', NULL, NULL, '白芨粉5克，鸡蛋1个搅匀。');
INSERT INTO `recipe` VALUES (3675, 92, '肺结核（肺阴不足症）－白心萝卜', '萝卜加入300毫升水煎至100毫升时，除去残渣，加入明矾9克，蜂蜜120克，再煮沸即可。', '适宜肺结核患者。', '每次服50毫升，每日3次，早、午、晚空腹服。', NULL, '红皮白心大萝卜1000克。');
INSERT INTO `recipe` VALUES (3676, 92, '肺结核（肺阴不足症）－麦冬粳米', '先以麦冬30克煎汤取汁，用汁同粳米50克，沙参20克煮粥，加入冰糖适量，煮至粥熟。', '适宜肺结核患者。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3677, 92, '肺结核（气阴两虚证）－百合党参', '炖熟加入许食盐调味。', '适宜肺结核患者。', '饮汤食猪肺。', NULL, '百合30克，党参15克，猪肺250克。');
INSERT INTO `recipe` VALUES (3678, 92, '肺结核（气阴两虚证）－大枣冰糖', '以上同煮，煮沸后加入银耳15克，冰糖适量煮为粥。', '适宜肺结核患者。', '经常服用。', NULL, '粳米100克，大枣5枚。');
INSERT INTO `recipe` VALUES (3679, 92, '肺结核（气阴两虚证）－蛤蚧人参粉', '先煮稀粥，待粥熟时加入蛤蚧粉2克，人参粉3克。', '适宜肺结核患者。', '以上搅匀热服。', NULL, '糯米50-100克。');
INSERT INTO `recipe` VALUES (3680, 92, '肺结核－北沙参', '共煮至肉烂熟，去药渣服食。', '适宜肺结核患者。', NULL, NULL, '北沙参15克，百合12克，南杏仁9克，瘦猪肉100克。');
INSERT INTO `recipe` VALUES (3681, 92, '肺结核－银杏白芨', '水与冰糖适量，共煮为粥。', NULL, '早晚服用。', NULL, '银杏15克，白芨粉5克，粳米100克。');
INSERT INTO `recipe` VALUES (3682, 92, '肺虚咳嗽－红枣粥', '待粥烧煮至七成熟时，再放入红枣同煮成粥。', '具有养脾、润心肺、补五脏、治虚损的功用，对贫血、肺虚咳嗽、肝炎、高血压等也有一定疗效。', NULL, NULL, '红枣100-200克。');
INSERT INTO `recipe` VALUES (3683, 92, '肺燥咳嗽－松子胡桃仁', '研膏，和熟蜜25克收之。', '主治肺燥咳嗽。', '每服10克，食后沸汤点服。', NULL, '松子仁50克，胡桃仁100克。');
INSERT INTO `recipe` VALUES (3684, 92, '肺燥咳嗽－松子粥', '将松子仁研碎，同粳米煮粥。粥熟后冲入适量蜂蜜即可食用。', '补虚，养液，润肺，滑肠。适用于中老年及体弱早衰、产后体虚、头晕目眩、肺燥咳嗽咳血、慢性便秘等症。', NULL, NULL, '松子仁50克、粳米50克、蜂蜜适量。');
INSERT INTO `recipe` VALUES (3685, 92, '化痰止咳－北杏炖雪梨', '将北杏、雪梨、白砂糖同放炖盅内，加清水半碗，急火隔水炖1小时。', '化痰止咳、清热生津、润肺平喘。适用于咳嗽、慢性支气管炎及肠燥便秘。', '每日2次，食雪梨饮汤。', NULL, '北杏10个、雪梨1个、白砂糖50克。');
INSERT INTO `recipe` VALUES (3686, 92, '健脾补肺－龙猪枣汤', '将山3味共煮为汤。', '健脾补肺。现多用于消化不良、慢性支气管炎、肝硬变腹水等症。', '饮汤，食肉、枣。', NULL, '五爪龙，大枣，瘦猪肉各适量。');
INSERT INTO `recipe` VALUES (3687, 92, '清补脾肺－淡兔汤', '将兔子宰杀，剥皮、去爪，剖腹去内脏，洗净，切大块，放入砂锅内，加冷水至使肉块浸没，先用中火烧开，加入黄酒，再用小火炖煮4小时，至肉烂骨离即成。', '清补脾肺、养胃利肠、解热止渴。适用于糖尿病。', '饮汤，食肉。每日3-4次，每次1大碗。兔肉蘸少量酱油食用。', NULL, '兔子1只，黄酒2匙，酱油适量。');
INSERT INTO `recipe` VALUES (3688, 92, '清肺－川明参烧雅鱼', '1.将川明参浸泡1夜，切成3厘米长的段；雅鱼宰杀后，去鳞、腮、肠杂；姜切片，葱切段。\r\n2.将雅鱼放入六成热的油锅内，炸3分钟，沥干油分，备用。\r\n3.将炒锅置武火上烧热，下入姜、葱爆香，再下雅鱼、料酒、川明参、盐、味精、白糖、鲜汤，烧熟即成。', '清肺、化痰、平肝、和胃、补气、补血。适用于痰火咳嗽。喘逆、头晕、呕吐、目赤、白带、疔毒疮疡、气血两亏等症。', '佐餐食用。', NULL, '川明参30克，雅鱼（或草鱼）1条，料酒、姜、葱各10克，盐4克，味精、白糖各3克，鲜汤300克，素油50克。');
INSERT INTO `recipe` VALUES (3689, 92, '清肺化痰－杷叶粥', '将枇杷叶用纱布包好放入砂锅内，加水200克煎至100克，去渣加入粳米、冰糖，再加水600克，煮成稀粥。', '清肺化痰，止咳降气。适用于急性气管炎、大叶性肺炎、衄血以及胃热呕吐、呃逆等症。', '早晚温热服用。', NULL, '枇杷叶15克、粳米100克、冰糖适量。');
INSERT INTO `recipe` VALUES (3690, 92, '清肺－昙花炒鸡蛋', '1.将昙花（鲜品）洗净，切碎；鸡蛋（鹌鹑蛋）打碎，放入盐、味精，与昙花末共搅匀。\r\n2.将炒锅置火上烧热，加入素油，烧六成热时，下入蛋液，将一面煎至金黄后，再煎另一面，熟透即成。', '清肺，止咳，化痰。适用于心胃气痛、吐血、肺结核等症。', NULL, NULL, '昙花6朵，鸡蛋4个（或鹌鹑蛋8个），盐4克，味精3克，素油30克。');
INSERT INTO `recipe` VALUES (3691, 92, '清肺止咳－天花粉粥', '先取天花粉浸泡15分钟，文火煮20分钟，去渣取汁，再加入粳米煮粥。', '清肺止咳、生津止渴。适用于糖尿病及肺热咳嗽。', '分2次早晚温服。', NULL, '天花粉20克、粳米100克。');
INSERT INTO `recipe` VALUES (3692, 92, '清热化痰－枇杷罗汉果粥', '鲜枇杷叶洗净、切碎、纱布包袋，罗汉果洗净打烂，与粳米一起置入锅中，加水适量，煮成稀粥，再加冰糖调味服食。', '清热化痰，降气止咳。适用于急性支气管炎属痰热甚者，症见发热胸痛，咳嗽气喘，痰多黄稠，口渴，小便短黄等。', NULL, NULL, '鲜枇杷叶30克（干品15克），罗汉果1枚，粳米30克。');
INSERT INTO `recipe` VALUES (3693, 92, '清热化痰－笋菇肉丝', '水发香菇洗净切丝，香菇浸出液沉淀，滤清备用；芦笋切丝；猪肉切丝放入打碎的鸡蛋拌匀。肉丝过油捞出，余油加入葱、姜略炒，放入笋、菇、肉丝、盐、味精翻炒，加入香菇浸出液略煮，水淀粉匀芡，淋麻油出锅即可。', '健脾理气、清热化痰。常用于肺癌。', '早晚温热食用。', NULL, '芦笋300克、香菇50克、瘦猪肉200克、鸡蛋2个、葱、姜、油、盐、淀粉、味精、麻油各适量。');
INSERT INTO `recipe` VALUES (3694, 92, '清热化痰－鲜芦根竹茹粥', '先将粳米洗净加水适量煮成稀粥，待米将烂时，加入芦根、竹茹药液（提前煮好），文火煮15分钟左右，调味食用。', '清热化痰，止咳平喘。适用于急性支气管炎、肺炎，症见胸痛，咳嗽气喘，痰稠色黄，发热，烦渴引饮，小便少黄等。', NULL, NULL, '鲜芦根60克、竹茹15克、粳米50克。');
INSERT INTO `recipe` VALUES (3695, 92, '清热化痰－竹沥粥', '取鲜竹沥约长65厘米，劈开两端去节，以火烤中间，流出汁液即竹沥，用粳米加入竹沥煮稀粥。', '清热化痰。竹沥味甘性大寒，善治痰火，清肺胃之热，对各种原因引起的高热神昏皆宜。与粳米同用，兼顾胃气。', '每日服2-3次。', NULL, '粳米100克，竹沥水100-150毫升。');
INSERT INTO `recipe` VALUES (3696, 92, '清热利肺－丝瓜鱼片汤', '丝瓜去棱、生鱼刮鳞，洗净，切片，加油略炒，加开水适量，文火煮汤，调味食用。', '清热利肺，化痰止咳。适用于急性支气管炎属痰热者，症见发热，咳嗽，痰黄而稠，口渴，咽喉痛等。', NULL, NULL, '丝瓜150克、生鱼100克。');
INSERT INTO `recipe` VALUES (3697, 92, '清热润肺－川贝百合炖雪梨', '雪梨洗净、去皮、切块，贝母、百合洗净，一齐置炖盅内，加开水适量，文火隔水炖1小时，加冰糖适量。', '清热润肺，镇咳祛痰。适用于肺结核属虚热者，症见咳嗽，咯血，口咽干燥，痰黄稠等。', '吃梨饮汁。', NULL, '川贝母10克，百合15克（干品），雪梨1个。');
INSERT INTO `recipe` VALUES (3698, 92, '清热润肺－西洋菜瘦肉汤', '蜜枣洗净、去核，猪瘦肉洗净切片，加水适量，文火煮至猪肉熟后，下西洋菜煮10分钟，调味，食肉菜饮汤。', '清热润肺，利气止咳。适用于慢性支气管炎、肺结核属肺热、肺燥者，症见久咳痰少，咳引胸胁作痛，气喘，咽干等。', NULL, NULL, '鲜西洋菜250克，蜜枣6枚，猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3699, 92, '润肺和胃、补虚强身－鸡汤银耳', '先把银耳泡胀，再将鸡清肠倒处锅内，加入盐、料酒瑚椒末烧开，再加入银耳，用大火蒸，待银耳软入味，加味精即成。', '润肺和胃、补虚强身。', NULL, NULL, '银耳12克，鲜莲子30克，鸡清汤1.5千克，盐、味精、料酒、胡椒末各适量。');
INSERT INTO `recipe` VALUES (3700, 92, '润肺化痰－瓜蒌饼', '瓜蒌去籽，放在锅内，加水少许，加白糖100克，以小火煨熬，拌成馅。另取面粉750克，加水适量经发酵加面碱，揉成面片，把瓜蒌夹在面片中制成面饼，烙熟或蒸熟。', '润肺化痰，散结宽胸。适用于肺癌胸痛。瓜蒌主要含三萜皂甙、有机酸、树脂、糖类和色素等。在体外抗癌实验中，发现全瓜蒌煎剂（20%煎剂）有抗癌作用。实验研究还指出：瓜蒌皮抗癌效果尤胜于瓜蒌仁，种壳和脂肪油则无效。', '佐餐或随意服用。', NULL, '瓜蒌200克、面粉600克、白糖75克、清水适量。');
INSERT INTO `recipe` VALUES (3701, 92, '润肺生津－白木耳粥', '白木耳10克（或黑木耳30克）浸泡半天。粳米100克，红枣3-5枚煮沸后加木耳和适量冰糖同煮。', '润肺生津、益气止血和健脑强心的作用。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (3702, 92, '润肺止咳－百合粥', '大米、百合淘洗干净置锅内，先用大火烧沸，再改用小火煨熬，待百合与米熟烂时，加入白糖拌匀即成。', '润肺止咳，清心安神。适用于肺痨久咳，咳痰唾血，神志恍惚，虚烦惊悸等症。', NULL, NULL, '百合60克，大米250克，白糖100克。');
INSERT INTO `recipe` VALUES (3703, 92, '润肺止咳－大枣百合汤', '1.将大枣洗净，去核；百合洗净，撕散鳞片。\r\n2.将上2味共入砂锅煮烂，加水冰糖适量调匀即可。', '补中益气，润肺止咳、健脾养胃。主治慢性咳嗽或虚老咳嗽。', '饮汤，食大枣、百合。每日1次，5-7日为1个疗程。', NULL, '大枣10枚，百合60克，冰糖适量。');
INSERT INTO `recipe` VALUES (3704, 92, '润肺止咳－淮山百合炖白鳝', '白鳝去内脏、洗净，淮山、百合洗净一起置炖盅内，加开水适量，隔水炖熟，调味食用。', '适用于肺结核经久不愈，咳嗽，或痰中带血，低热，体虚纳差等。', NULL, NULL, '淮山15克，百合20克（干品），白鳝1条。');
INSERT INTO `recipe` VALUES (3705, 92, '润肺止咳－蜜饯百合', '将干百合洗净，放入大搪瓷碗内，加入蜂蜜，上笼蒸1小时，趁热调均匀，晾冷后，装入瓶内即成。', '润肺止咳，清心安神。适用于慢性支气管炎以及秋天肺燥或热邪伤及肺阴所致咳嗽等。', '每日早晚各服1汤匙。', NULL, '干百合100克、蜂蜜150克。');
INSERT INTO `recipe` VALUES (3706, 92, '润肺止咳－宁嗽定喘饮', '先将山药煎取清汤一大碗，再将两汁及生鸡子黄调入，分3次温饮之，不可过热。过热则鸡子黄熟而效大减。', '润肺止咳，收涩益阴。适宜于由于津亏液少而致肺燥的干咳不止，缠绵不愈，渐至咳喘并发，口干舌燥者。', NULL, NULL, '生怀山药50克，甘蔗自然汁30克，酸石榴自然汁18克，生鸡子黄4个。');
INSERT INTO `recipe` VALUES (3707, 92, '通肺止咳－薄荷杭菊糖茶', '将薄荷、杭菊用开水浸泡，或文火煮5-10分钟，去渣，加冰糖适量饮用。', '疏风清热，通肺气，止咳逆。适用于外感初起，症见风热头痛，发热，咳嗽等。', NULL, NULL, '薄荷6克，杭菊10克，冰糖或白糖适量。');
INSERT INTO `recipe` VALUES (3708, 92, '温肾补肺－熙春酒方', '1.将女贞子于冬至一日九蒸九晒；生地洗净晒干；仙灵牌去皮毛；绿豆洗净晒干。\r\n2.将上述药物装入布袋内，扎紧，备用。\r\n3.将瓷坛装烧酒，再放入药袋。严密封口，浸制一日即成。', '温肾补肺，泽肌肤，美毛发。适用于老年久咳。平时服用可使容颜少壮，毛发润泽。', '早晚各服1次，根据酒量酌饮，但每次不得超过30克。', NULL, '枸杞子、龙眼肉、女贞子、生地、仙灵牌、绿豆各100克，柿饼500克，烧酒10公斤。');
INSERT INTO `recipe` VALUES (3709, 92, '宣肺理气－生姜芥菜汤', '鲜芥菜洗净切断，生姜洗净切片，加入适量，文火煮15-20分钟，调味，食菜饮汤。', '宣肺理气，祛痰散寒。适用于风寒感冒，症见头痛鼻塞，咳嗽，痰白不利等。', NULL, NULL, '鲜芥菜250克，生姜10克。');
INSERT INTO `recipe` VALUES (3710, 92, '宣肺止咳清火－杭菊糖茶', '将杭菊花用开水浸泡，加白糖适量。', '宣肺止咳清火。适宜于风热感冒初起，头痛发热患者。', '代茶频饮。', NULL, '杭菊花30克，白糖适量。');
INSERT INTO `recipe` VALUES (3711, 92, '益心肺－黄精炖猪瘦肉', '将黄精、猪瘦肉洗净，分别切成长3.3厘米、宽1.6厘米的小块将黄精和猪瘦肉块入瓦锅（砂锅）同，加水适量，放入葱、生姜、食盐、料酒，隔水炖熟。', '养脾阴，益心肺。适用于阴虚体质的平时调养以及心脾阴血不足所臻的食少、失眠等症。', '食用时，加味精少许，吃肉喝汤。', NULL, '黄精50克、猪瘦肉200克、葱姜、料酒、食盐、味精各适量。');
INSERT INTO `recipe` VALUES (3712, 92, '益阴补髓－羊脊骨粥', '先将羊脊骨砸碎，煮沸后捞出羊骨，取汁；再将青小米洗净后，加入羊骨汁内煮粥。粥熟后加适量食盐即可服用。', '益阴补髓，润肺泽肤。适用于阴虚不足，虚劳瘦弱，肺劳咳嗽，皮肤、毛发憔悴等症。', '可于早晚佐餐服食。', NULL, '大羊脊骨1具，青小米100克，食盐适量。');
INSERT INTO `recipe` VALUES (3713, 92, '阴虚、肺燥－银耳鸭蛋羹', '将银耳倒入锅内加水适量，煮一段时间，再将鸭蛋打入碗内搅匀，倒入锅内煮沸，加入冰糖稍煮即成。', '治疗因阴虚、肺燥。', NULL, NULL, '鸭蛋1只，水发银耳50克，白糖适量。');
INSERT INTO `recipe` VALUES (3714, 92, '止咳定喘－百果玫瑰球', '将蛋清入浅汤盆中，用筷子打至起细浓泡沫，加入干淀粉30克，拌匀，再放入红米汁拌成红色。', '补脾和胃，止咳定喘。适用于哮喘、慢性支气管炎、肺结核等。健康人经常食用能防病延年。', '每日2次，随量食。', NULL, '核桃仁末、红枣（去皮核）、青梅末、橘饼、莲米末各15克，南瓜子仁末6克，猪板油30克，白糖45克、干淀粉40克、鸡蛋清4只、菜油3000克、玫瑰酱、红米汁各适量。');
INSERT INTO `recipe` VALUES (3715, 92, '止咳化痰－丝瓜花蜜饮', '丝瓜花用沸水冲泡5分钟，再调入蜂蜜即成。', '清热泻火，止咳化痰。主治小儿肺热、痰热咳嗽，喘急气促等症。', '每日3次，趁热饮用。', NULL, '丝瓜花10克，蜂蜜20克。');
INSERT INTO `recipe` VALUES (3716, 92, '滋补肝肾－枸杞粳米粥', '同煮成粥。', '枸杞具有滋补肝肾、润肺止咳、明目的作用，尤其适宜于老年人服食。枸杞粥还有较明显的护肝作用。', NULL, NULL, '枸杞50克，粳米100克。');
INSERT INTO `recipe` VALUES (3717, 92, '滋润五脏－松子抗衰膏', '将松子仁、黑芝麻、核桃仁同捣成膏状，入砂锅中，加入黄酒，文火煮沸约10分钟，倒入蜂蜜，搅拌均匀，继续熬煮收膏，冷却装瓶备用。', '滋润五脏，益气养血。适用于治疗肺肾亏虚、久咳不止、腰膝酸软、头晕目眩等症。中老年人经常服用，可滋补强壮、健脑益智、延缓衰老。脑力劳动者经常服用能使思维敏捷、记忆力增强，是抗老防衰的有效食品。', '每日2次，每次服食1汤匙，温开水送服。', NULL, '松子仁200克，黑芝麻100克，核桃仁100克，蜂蜜200克，黄酒500毫升。');
INSERT INTO `recipe` VALUES (3718, 92, '滋养肺胃－黄精玉竹猪胰汤', '猪胰剔去油膜，洗净，切两段，与黄精、玉竹洗净，一齐置瓦锅内，加水适量，文火煮熟，调味服食用。', '滋养肺胃，降糖止渴。适用于糖尿病属肺胃阴虚者，症见多食多饮，形体消瘦，口渴咽干，小便频多等。', NULL, NULL, '黄精12克、玉竹20克、猪胰1具。');
INSERT INTO `recipe` VALUES (3719, 92, '滋阴降火－川贝腐竹炖水鱼', '鱼用开水烫死，去鳃和内脏，洗净。腐竹用水浸软，与葱、生姜、花椒一齐置瓦锅内，加水适量，调味。文火煮熟，食用。', '滋阴降火，润燥除烦。适用于糖尿病属肺肾阴虚者，症见口渴咽干，食多易饥，身体消瘦，夜间尿多等。', NULL, NULL, '川贝母10克，水鱼100克，腐竹50克葱、生姜、花椒少许。');
INSERT INTO `recipe` VALUES (3720, 92, '滋阴清热－杏仁川贝水鱼汤', '先将水鱼洗净，去鳃和内脏，杏仁、川贝、知母洗净用纱布包，置入鱼腹内，加清水适量，武火煎沸后，文火煮1小时。食盐少许调味，饮汤食肉。', '滋阴清热，化痰止咳。适用于慢性支气管炎、肺气肿，症见胸痛久咳，或咳嗽咯血，午后潮热，颧红盗汗，口咽干燥等。', NULL, NULL, '南杏仁10克、川贝母10克、知母10克、水鱼1条（约400克）。');
INSERT INTO `recipe` VALUES (3721, 92, '滋阴润肺－阿胶粥', '将阿胶捣碎。将糯米煮粥，待熟，入阿胶稍煮，搅令烊化即成。', '养血止血，滋阴润肺，安胎。适宜于咯血、吐血、便血、尿血等多种血症。', NULL, NULL, '阿胶15克，糯米100克。');
INSERT INTO `recipe` VALUES (3722, 92, '滋阴润燥，清肺化痰－鸡蛋甜豆浆', '豆浆置锅中煮沸，鸡蛋打匀加入。煮沸后饮用。', '滋阴润燥，清肺化痰。适用于肺结核久咳不愈者，症见咯痰不爽，低热，咽干，身体虚弱等。', NULL, NULL, '鸡蛋1个、甜豆浆350毫升。');
INSERT INTO `recipe` VALUES (3723, 92, '滋阴生津－百合雪耳瘦肉汤', '猪瘦肉洗净切片，与百合、雪耳一起入锅。加水适量，共煎30分钟。调味，食肉饮汤。', '滋阴生津，润肺止咳。适用于慢性支气管炎、肺燥久咳者，症见干咳无痰，或痰少粘稠不利，甚则痰中带血，口咽干燥，舌红津少等。', NULL, NULL, '百合20克、雪耳15克、猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3724, 92, '滋阴生津－银耳粥', '干银耳用清水发泡洗净。糯米淘净，入锅加水烧开，放入银耳，熬煮成粥，调入冰糖即成。', '此粥滋阴生津、润肺养胃、益气和血，可治虚劳咳嗽、痰中带血、阴虚口渴', NULL, NULL, '干银耳6克，糯米100克。');
INSERT INTO `recipe` VALUES (3725, 100, '发汗解表－五神汤', '将荆芥、苏叶洗净，与茶叶、生姜一并放砂锅中，加水500克浸泡20分钟，文火上煎沸10分钟取汁，加红糖溶化即成。', '发汗解表。适用于风寒感冒，症见发热无汗、流清水鼻涕、痰少清稀者。', '随意服用。', NULL, '荆芥10克、苏叶10克、生姜15克、茶叶6克、红糖30克。');
INSERT INTO `recipe` VALUES (3726, 100, '风湿肢疼－芝麻粥', '先将芝麻炒干出香味，另煮米成粥，将熟时加入芝麻、砂糖即成。', '通痹、润肠、益五脏、坚筋骨。适宜于肝肾不足、风湿内侵引起的肢节疼痛，或因肺气不足以致大肠不利的便秘等症。', NULL, NULL, '芝麻仁6克，白米30克，白砂糖少许。');
INSERT INTO `recipe` VALUES (3727, 100, '解表和中－葱豉黄酒汤', '豆豉加水1小碗，煎煮10分钟，再加洗净的葱须，继续煎5分钟，最后加黄酒，出锅取汁。', '解表和中。适用于风寒感冒。', '每日2次，趁热顿服。', NULL, '豆豉15克、葱须20克、黄酒50克。');
INSERT INTO `recipe` VALUES (3728, 100, '解表散寒－葱白香菜汤', '上2味洗净，加水煎煮为汤。', '解表散寒，适用于外感风寒。', '趁热顿服。', NULL, '葱白，香菜各15克。');
INSERT INTO `recipe` VALUES (3729, 100, '解表散寒－苏叶姜葱橄榄汤', '将各用料洗净共置锅内。水适量。文火煮10-15分钟，加盐少许调味，去渣。', NULL, '趁热饮汤。', NULL, '紫苏10克，生姜10克，葱白15克，新鲜橄榄（连核）50克。');
INSERT INTO `recipe` VALUES (3730, 100, '解表散寒－苏叶生姜鸡蛋汤', '鸡蛋去壳卧入开水中，待成形后，加入紫苏叶、生姜片、文火煮15分钟，去渣，调味即成。', '解表散寒，温肺止咳。适用于风寒感冒，症见恶寒发热，头痛鼻塞，咳嗽痰稀，流清涕，打喷嚏等。', '食蛋饮汤。', NULL, '紫苏叶15克，生姜10克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3731, 100, '清热止渴－三稔煲荠菜', '将三稔切开；荠菜洗净，同煎汤，不加油，加食盐少许调味。', '清热，止渴，除烦，抗疲劳，利小便。适用于风热感冒见头痛发热、咳嗽、痰黄稠、口干舌燥、口鼻气热、大便秘结、小便短黄等症。日常体力劳动或体育活动后肌肉酸痛，服之可透汗解肌、祛除疲劳、恢复精力。民间亦用以解酒。', '1次饮服。', NULL, '三稔4-5枚，荠菜500克，盐适量。');
INSERT INTO `recipe` VALUES (3732, 100, '清热解毒－白菜绿豆饮', '将白菜根茎、绿豆芽洗净，白菜根茎切片，然后同置铝锅内加水适量煎煮15分钟，去滓取液。', '清热解毒。适宜于风热外感，头痛、身热、口干、鼻塞、无汗或少汗等。', '每日饮用2-3次。', NULL, '白菜根茎头1个，绿豆芽30克。');
INSERT INTO `recipe` VALUES (3733, 100, '清热解毒－臭草绿豆糖水', '先将绿豆洗净，加水适量煮汤，鲜臭草洗净用纱布包后下，文火煮20分钟左右，再加红糖适量饮用。', '清热解毒，消暑凉血。适用于感冒热重者，证见发热，咽喉红肿，鼻衄，牙痛等。', NULL, NULL, '鲜臭草30克，绿豆50克，红糖适量。');
INSERT INTO `recipe` VALUES (3734, 100, '清热解毒－鸡骨草红枣汤', '鸡骨草、红枣洗净，加水适量，文火煮20分钟，去渣饮用。', '清热解毒、舒肝利湿。适用于胆囊炎、胆结石等症见右胁疼痛，脘腹胀满，时有恶心呕吐，小便短黄等。', NULL, NULL, '鸡骨草30克，红枣6枚。');
INSERT INTO `recipe` VALUES (3735, 100, '清热解毒－苦瓜水', '苦瓜切丁，加水小火熬至苦瓜稀烂。', '清热解毒，解暑消痤，治疗痤疮，并可祛暑。', '每天当饮料喝，注意不能加糖、盐等调味品。', NULL, '苦瓜半个。');
INSERT INTO `recipe` VALUES (3736, 100, '清热解毒－马齿苋绿豆汤', '马齿苋、绿豆洗净，一齐置锅内，加水适量，文火煮豆烂后食用。', '清热解毒，止痢。适用于痢疾，症见腹痛，里急后重，脓血便等。', NULL, NULL, '鲜马齿苋120克（干品30克），绿豆50克。');
INSERT INTO `recipe` VALUES (3737, 100, '清热解毒－双花饮', '将金银花、大青叶放入锅内，加水煮沸，3分钟后将药液滤出，放入蜂蜜，搅拌均匀，即可饮用。', '清热解毒，解表退烧。适用于风热感冒，发热头痛，口渴等症。', NULL, NULL, '金银花15克，大青叶10克，蜂蜜50克。');
INSERT INTO `recipe` VALUES (3738, 100, '清热解毒－鱼腥草猪肺汤', '猪肺反复清洗干净，切块，放入锅中爆干水分，再放入水中漂洗，挤干水，加水适量，下红枣，文火煮至猪肺熟后，下鱼腥草，再煮20分钟左右，调味食用。', '清热解毒，止咳消痛。适用于急性支气管炎、肺脓疡、肺炎属热毒壅盛者，症见胸痛，发热，咳嗽，痰多黄稠等。', NULL, NULL, '鱼腥草30克，红枣4枚，猪肺250克。');
INSERT INTO `recipe` VALUES (3739, 100, '清热解毒－玉米须蚌肉汤', '玉米须、蚌肉洗净，加水适量，文火煮20-30分钟，饮汤。', '清热解毒、利湿退黄。适用于急性胆囊炎、胆结石、胆道感染属湿热者，症见右胁疼痛，胃脘不适，食少纳差，恶心呕吐，口苦咽干，小便短黄等。', NULL, NULL, '玉米须30克，蚌肉50克。');
INSERT INTO `recipe` VALUES (3740, 100, '温中祛寒－炖狗肉', '先以蒜头及花生油炝锅，放入狗肉块微炒，待皮色转黄，加水适量，用武火烧开后，放入熟附片及煨姜片，改用文火炖至狗肉熟烂，调味即可。', '温中祛寒，用于冻疮。', '佐餐食用。', NULL, '熟附片6克，生姜100克，狗肉150克，调料适量。');
INSERT INTO `recipe` VALUES (3741, 100, '温中散寒－丁香姜糖', '将糖放入锅中，加水少许，以文火煎熬至较稠厚时，加入姜末及丁香粉调匀；再继续煎熬至用铲挑起即成丝状而不粘手时，停火。将糖倒在涂过食油的大搪瓷盘中，稍冷切条块。', '温中散寒。主治冻疮。', '严冬季节常服。', NULL, '红糖200克，生姜碎末40克，丁香粉5克。');
INSERT INTO `recipe` VALUES (3742, 100, '消除疲劳－丁香火锅', '1.将蛤蜊肉、虾仁洗净备用；鱼圆切片；墨鱼除去腹内杂物洗净后，在开水锅里速烫一遍，然后切成2片。粉丝用热水泡软，切成几段；芹菜切成寸段；冻豆腐切成小块；葱切小段。\r\n2.将以上各料先各放一半入锅，汤也加入一半，并可加入适量葡萄酒，盐少量，旺火烧5-6分钟后，即可趁热吃，边吃边加。', '丁香具有强烈的芳香，有兴奋强身作用。当身体疲劳时，食丁香火锅能使人精神振奋，增强全身活力，消除疲劳。', '佐餐食。', NULL, '丁香6克，蛤蜊肉200克，鱼圆100克，墨鱼2条，虾仁100克，粉丝、芹菜、冻豆腐、葱、味精各适量，鸡汤4碗。');
INSERT INTO `recipe` VALUES (3743, 93, '安神－参砂蒸蛋', '1.先将苏条参或潞党参、山药研成细末，与朱砂拌匀备用。\r\n2.每次用6克混合药末，与鸡蛋在碗内搅均匀，蒸锅上蒸熟即成。 \r\n　　', '补气养血，安神。适用于气血虚、心脾不足之心悸、失眠、食少纳呆等症。宜忌：血脂高，肝有器质性病变者不宜服用。', '每日晨起一碗蒸蛋，连服半月以上。', NULL, '苏条参（或潞党参）、淮山药各30克，朱砂6克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3744, 93, '安神－人参鸡火锅', '1.将人参洗净，用少许水煮一下，捞出切片，再放原锅中煮15分钟，捞出放碗中仍以原汤泡好；将母鸡肉洗净，搌干水分，剁成4厘米见方的块，入开水锅中汆一下捞出；瘦猪肉去筋膜，入开水锅中汆一下，捞出切片；猪舌头刮洗干净，入开水锅中汆一下；海参抠洗干净；顺着切成片，用水泡上；牛喉撕去皮、筋，剞花刀，剖开，切成约8厘米长的条；胡萝卜、莴笋削去皮筋，切成片；豌豆苗洗净；葱拍破，切段。以上各料除母鸡块和人参片外，均分别装入盘中上桌备用。\r\n2.炒锅置火上，下猪油烧至五成热，先放姜、葱、花椒炸香，继下鸡块、盐、酱油、料酒、', '补虚扶正、抗衰老、具有气血双补、固脱生津、安神之效。', '佐餐食用。', NULL, '人参20克，母鸡肉1500克，水发海参、牛环喉、猪油各200克，猪瘦肉、猪舌头、胡萝卜、莴笋各150克，豌豆苗尖、醪糟汁各100克，料酒50毫升，花椒10克，酱油15毫升，精盐、冰糖、葱各15克，味精5克，姜20克，胡椒粉3克，鲜汤2500毫升。');
INSERT INTO `recipe` VALUES (3745, 93, '补虚养神－莲子奶糊', '1.将石莲子去硬壳，磨粉，用少量清水调成糊状；\r\n2.煮牛奶，放入白糖，将沸时慢慢倒入莲子水糊。', '健脾益胃，补虚养神。主治胃癌以及其他癌肿化疗期间和治疗后胃气虚弱者，或有心烦欲呕、头晕目眩等症者。', '并不断搅拌，煮熟即可，随量食用。', NULL, '石莲子60克，牛奶200毫升，白糖适量。');
INSERT INTO `recipe` VALUES (3746, 93, '补肝益智－地黄乌鸡', '将乌鸡宰杀，去毛、内脏，洗净，备用；生地黄洗净，切成条状，加饴糖拌匀，装入鸡腹内；将鸡仰置瓷盆中，隔水用文火蒸熟即成。', '填精添髓，补脏益智。适宜于用脑过度、脑髓不足而见头转耳鸣、记忆力减退、腰膝酸痛、神疲气短等症者食用。常食能收到填精补脑、益智健身功效。宜忌：感冒发热，或湿热内蕴而见食少、腹胀、便溏者，均不宜食用。', '分2日食用，吃肉喝汤。', NULL, '雌乌骨鸡1只（重约1000克），生地黄、饴糖各150克。');
INSERT INTO `recipe` VALUES (3747, 93, '记忆力衰退－拔丝蛋块', '先将鸡蛋搅开，放进面粉、香蕉精和少许水，和匀成蛋糊状；再在油锅中放进油少许，将调好的蛋糊倒出，摊成薄饼；将薄饼取出，切成菱形蛋块，然后再放在热油锅中炸，待炸成金黄包时，捞出。最后放油少许于锅中，将白糖倒入，炒至糖能拔丝时，再放入炸好的鸡块，并撒上青红丝即成。', '滋阴补血，养心安神益智，适用于记忆力衰退者；神经衰弱、失眠、心烦者，此外，对妇女崩漏、胎动不安者亦有效。', '佐餐食用。', NULL, '鸡蛋黄36克、白糖60克、面粉15克、植物油500克（耗油45克）、青红丝0.6克、香蕉精0.12克。\r\n　　');
INSERT INTO `recipe` VALUES (3748, 93, '宁心安神－小米枣仁粥', '将小米加水煮粥至将熟，加入酸枣仁末，搅匀煮开即可。', '补脾润燥，宁心安神。主治纳食不香、夜寐不宁、大便干燥。', '每日2次，加蜂蜜温服。', NULL, '小米100克、酸枣仁末15克、蜂蜜40克。');
INSERT INTO `recipe` VALUES (3749, 93, '宁心神－玫瑰枣仁心', '1.将猪心去脂膜，洗净。\r\n2.枣仁略炒与玫瑰花共研末，灌入猪心中。\r\n3.将灌药的猪心盛碗中，隔水蒸或上笼屉蒸至熟透。', '养心血，宁心神。适用于心血不足所致的心悸怔忡、失眠健忘等症。', '食用时去心内药末，切片，拌调料服。', NULL, '猪心1个，枣仁20克，玫瑰花10克。');
INSERT INTO `recipe` VALUES (3750, 93, '神经衰弱－干炸猪脑', '将猪脑洗净，撕去“红筋”，放进开水锅中，加入八角、茴香、姜、葱 、精盐等，煮10分钟。捞出晾凉，切成滚刀块、并将汤及调料弃掉；再将蛋清打好，放入淀粉，调成蛋糊。再把猪脑块放入，调拌均匀；待油锅到半热时，将猪脑放进油锅中炸，成草黄色时，捞出，沥去油，再入热油锅，重新再炸，直到呈杏黄色时，捞出，放在盘内即成，可备甜酱蘸着吃。', '清虚热、补脑益智、安神之功。主治神经衰弱、症见心悸、失眠、健忘、脑力不佳。', NULL, NULL, '猪脑60克、姜3克、鸡蛋清24克、精盐6克、甜酱3克、干淀粉15克、八角、茴香各0.2克、葱6克。猪油500克（耗30克）。');
INSERT INTO `recipe` VALUES (3751, 93, '神经衰弱－乌灵参炖鸡', '取乌灵参用温水浸泡6小时，洗净切片，放入鸡腹内。将鸡放入砂锅内，清水淹过鸡体，放入酒、姜、葱适量，旺火烧开后，改小火清炖，待鸡熟后，加盐少许即成。', '补气健脾，养心安神。适用于神经衰弱。', '每周2次，食鸡肉，饮汤。', NULL, '鸡1只、乌灵参100克、酒、姜、葱、盐各适量。');
INSERT INTO `recipe` VALUES (3752, 93, '生津安神－地黄乌鸡火锅', '1.将生地黄浸泡3至5小时，取出切成薄片；大枣洗净，沥干水；乌鸡宰杀，去毛、内脏及爪尖，入开水中汆去血水，切成5厘米见方的块；蘑菇、金针菇去蒂，洗尽泥沙；菜心洗净，沥干水；午餐肉切片；藕去皮，切片。以上各料用盘装好，围于火锅四周待用。\r\n2.将骨头汤装入高压锅中，放入乌鸡块、地黄片；大枣、姜、葱、盐、料酒、烧开加入味精调味，即可烫食各种原料。', '补虚损、益气血、生津安神。', '直接食用或作面汤、佐餐皆可。', NULL, '生地黄、午餐肉、菜心各100克，大枣10个，乌鸡1只（约重1500克），蘑菇、金针菇各150克，藕200克，姜20克，葱15克，精盐10克，味精5克，料酒25毫升，骨头汤2500毫升。');
INSERT INTO `recipe` VALUES (3753, 93, '失眠－罗布麻速溶饮', '加水适量煎煮3次，去渣留液后将3次药液合并，再以文火煎煮浓缩到将要干锅时，停火晾凉，然后拌入干燥的白糖粉500克将药液吸净，混合晒干压碎，装瓶备用。', '对高血压患者的烦躁失眠有良效。', '以沸水冲开代茶饮，每次10克。', NULL, '罗布麻叶500克。');
INSERT INTO `recipe` VALUES (3754, 93, '失眠－牛（羊）肝大枣汤', '将牛和羊肝切片与大枣共煮服用。', '补心气，养阴血。主治心悸乏力。', NULL, NULL, '牛或羊肝250克，大枣15枚。');
INSERT INTO `recipe` VALUES (3755, 93, '失眠－桑螵龙牡散', '以上共研细末。', '久治不愈的失眠患者可食之。', '每日3次，每次6克以盐开水送服。', NULL, '桑螵蛸、生龙骨、生牡蛎各等份。');
INSERT INTO `recipe` VALUES (3756, 93, '小麦甘枣汤', '每日一剂水煎服。', '宜用于妇女更年期失眠健忘者。', NULL, NULL, '浮小麦100克，炙甘草20克，大枣30克，百合50克。');
INSERT INTO `recipe` VALUES (3757, 93, '枣仁地黄泥', '以上共捣如泥置于瓷缸中，以沸开水冲之，焖泡20分钟即可食之。', '适用于肝胆火旺引起的彻底不眠者。', '每日2次。', NULL, '酸枣仁30克、生地黄100克、白砂糖50克。');
INSERT INTO `recipe` VALUES (3758, 93, '养心安神－酸枣仁粥', '先以粳米加水煮粥至将熟，加入酸枣仁末再煮片刻即可。', '养心、安神、敛汗。适用于神经衰弱、心悸、失眠、多梦、黑眼圈。', '早晚温服。', NULL, '酸枣仁末15克、粳米100克。');
INSERT INTO `recipe` VALUES (3759, 93, '安心养神－白醋鸡蛋', '1.将鸡蛋打入碗中，将白醋放入其中。\r\n2.将放有白醋鸡蛋的碗置笼屉上，蒸熟即成。', '养心安神。适用于心气虚、心血不足的心悸、失眠等症。', '趁热服食，可加少量蜂蜜调味，每日晨起1碗蒸蛋，连服半月以上。', NULL, '陈白醋1.5克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (3760, 93, '养心－荷花首乌肝片', '1.把荷花洗净，切成3厘米见方的片；姜切片，葱切段，蒜切片；小油菜切段待用。\r\n2.将炒锅至武火上烧热，放入素油烧至六成热时下入猪肝（用首乌粉、豆粉抓匀）滑炒，随后放入葱、姜、蒜、荷花，调味至熟即成。', '养心，益肾，乌须发。', '佐餐食用。', NULL, '荷花2朵，首乌粉20克，猪肝200克，豆粉20克，小油菜20克，盐3克，味精2克，葱5克，姜3克，蒜3克。');
INSERT INTO `recipe` VALUES (3761, 93, '益脑－杞子炖羊脑', '将羊脑洗净（注意不要碰破）放入器皿内，加水、盐、葱、姜、料酒，隔水炖熟，加入味精调味即可。', '补肝肾、益脑强身。适用于肝血虚所致的头痛头晕、眼涩眼花、癫痫等。', '佐餐食用，每日2次。', NULL, '枸杞子30克、羊脑1具、葱、姜、料酒、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (3762, 93, '益智增力－益智鳝段', '1.将菟丝子、干地黄煎两次，取汁过滤。\r\n2.水发木耳；调水淀粉。\r\n3.鳝鱼肉切成鱼片；笋切片；黄瓜切方片。\r\n4.将鳝鱼片放入碗内加水淀粉、蛋清、盐、药汁煨好，放温油中划开，待鱼片泛起，滗入笊篱。\r\n5.原勺留油，炸蒜末、姜末，下笋片、黄瓜片、木耳、鱼片，加盐、味精、白糖，烹料酒、高汤，淋香油出勺装盘，撒上胡椒面即成。', '菟丝子古人认为它是“补肾养肝，温脾助胃之药也”，具有益精髓、坚筋骨、止遗泄之作用。久服可明目轻身延年。菟丝子配合滋阴补血的地黄及益气健脾的鳝鱼制成此菜肴，确有益智增力之作用。', '佐餐食。', NULL, '干地黄12克，菟丝子12克，净鳝鱼肉250克，净笋10克，黄瓜10克，木耳3克，酱油、味精、盐、淀粉、料酒、胡椒面、姜末、蒜末、香油、白糖各适量，蛋清1个，高汤少许。');
INSERT INTO `recipe` VALUES (3763, 94, '鼻衄－白萝卜汁', '白萝卜洗净切碎，用洁净纱布绞取汁液，加冰糖溶化即可。', '宽中消食，清热凉血。主治鼻衄、咳嗽。', '每日3次，每次冷饮40克。', NULL, '白萝卜2000克，冰糖适量。');
INSERT INTO `recipe` VALUES (3764, 94, '鼻衄－大蒜泥', '将生大蒜捣烂如泥，摊在布上如硬币大小，贴在脚底涌泉穴，包扎固定。左鼻出血贴左足，右鼻出血贴右足，两鼻出血贴双足，至足底疼痛欲起泡即可。 \r\n　　', '大蒜辛温，解毒，以蒜泥敷足心，引肝火下行，为“上病下取”之法，能治鼻衄与吐血等。', NULL, NULL, '生大蒜3瓣。');
INSERT INTO `recipe` VALUES (3765, 94, '鼻衄－猪蹄汤', '将茜草用纱布包裹，猪蹄洗净剁成小块，与大枣共入锅中，加水煎煮，待猪蹄熟烂，除去茜草即可。', '滋阴养血，凉血止血。用于鼻衄、便血等。', '吃肉食枣饮汤，早晚餐佐食。', NULL, '猪蹄2个，茜草20克，大枣10枚。');
INSERT INTO `recipe` VALUES (3766, 94, '聪耳明目－桑椹蜜膏', '桑椹洗净加水适量煎煮30分钟取汁1次，加水再煎，共取煎液2次。合并两次煎液，再以小火煎熬浓缩至较粘稠时，加蜂蜜煮沸，起锅待冷装瓶。 　　', '滋补肝肾、聪耳明目。用于目暗、耳鸣、失眠、健忘、烦渴、便秘、须发早白等。', '每次1匙，热水冲服，每日2次。', NULL, '桑椹1000克、蜂蜜400克。');
INSERT INTO `recipe` VALUES (3767, 94, '耳鸣耳聋－狗肉黑豆汤', '将洗净、切块之狗肉、黑豆同放锅内，加适量水，再放入姜片、五香面及少量精盐，炖烂即可。', '补肾壮阳，可作为肾虚耳鸣、耳聋之膳食。', '佐餐食用。', NULL, '狗肉500克，黑豆100克，精盐适量。');
INSERT INTO `recipe` VALUES (3768, 94, '耳鸣耳聋－羊肾黑豆杜仲汤', '先将剖开洗净的羊肾用开水浸泡3分钟待用；煮黑豆、杜仲、菖蒲30分钟，然后加入羊肾，文火炖熟。', '益肾填精开窍。用于肾精亏虚之耳鸣、耳聋等。', '喝汤吃肉，分2次服。', NULL, '羊肾1对，黑豆50克，杜仲15克，生姜10克，菖蒲10克。');
INSERT INTO `recipe` VALUES (3769, 94, '急性咽喉炎－生地螃蟹汤', '每次用生地、鲜蟹、水适量，煎煮。', '清热凉血，解热散结。用于辅助治疗急性咽喉炎。本方四季可用。《日用本草》说蟹“不可与红柿共食”，《本草纲目》记载“不可与柿与荆芥食”。', '去渣饮汤，每天一次，连服3天。', NULL, '生地30克，螃蟹1只。');
INSERT INTO `recipe` VALUES (3770, 94, '慢性鼻炎－白扁豆粥', '取白扁豆、党参同煎30分钟，去滓取汁，加入粳米煮成稀粥。', '益气健脾。主治慢性鼻炎。', '早晚空腹食用。', NULL, '白扁豆30克，党参10克，粳米100克。');
INSERT INTO `recipe` VALUES (3771, 94, '明耳目－桑椹酒', '取桑椹捣汁煮沸；将米煮熟，沥干，与桑椹汁搅匀蒸煮，加入酒曲适量搅匀，装入瓦坛内；将瓦坛放入棉花或稻草中发酵，根据季节气温不同，至发酵到味甜可口时，即可取出。', '补肝肾、明耳目、抗衰老。适用于肝肾不足引起的耳鸣耳聋、视物昏花等。', '每次4匙，开水冲服。每日2次。', NULL, '桑椹5000克，大米3000克，酒曲适量。');
INSERT INTO `recipe` VALUES (3772, 94, '明目美颜－银杞明目汤', '将鸡肝洗净切成薄片，放入碗内，加水豆粉、料酒、姜汁、精盐拌匀备用；水发银耳洗净切成小片，用清水浸泡备用；茉莉花择去花蒂洗净，放入盘中备用。将锅置中火上，放入清汤，加入料酒，姜汁，食盐和味精，随即下入银耳、鸡肝、枸杞煮沸，撇去浮沫，待鸡肝煮熟，装入碗内，将茉莉花撒入碗内即可。', '补肝益肾，明目美颜。适用于视物模糊、两眼昏花、白内障、面色憔悴等。', '早晚佐餐食用。', NULL, '银耳20克，枸杞子10克，鸡肝100克，茉莉花24朵，水豆粉、料酒、姜汁、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (3773, 94, '目眩－沙苑子白菊花茶', '上两味，同入锅，加水煎煮成300毫升。', '平补肝肾，降低血脂，降压明目。主治高脂血症，高血压病出现头昏、目眩、腰痛、尿频等症，辨证属于肝肾不足类型者。', '分6次，当茶饮，温服。当日服食完。', NULL, '沙苑子30克，白菊花10克。');
INSERT INTO `recipe` VALUES (3774, 94, '清热明目－枸杞叶粥', '取鲜枸杞叶洗净加水300克，煮至200克时去叶取汁，加入糯米、白糖，再加水500克煮成稀粥。', '补虚益精、清热明目。用于虚劳发热、头晕目赤、夜盲症。也可用于糖尿病，但不要加糖。', '早晚温热食用。需长期服用，方可奏效。', NULL, '鲜枸杞叶100克、糯米100克、白糖适量。');
INSERT INTO `recipe` VALUES (3775, 94, '龋齿－花椒粥', '花椒水煎10分钟取汁，取粳米常法煮粥，粥将熟时加入花椒汁略煮即可。', '温通散寒止痛。适用于龋齿疼痛，怕冷恶风，牙痛连及半侧头痛者。', '空腹趁热服用。', NULL, '花椒5克，粳米50克。');
INSERT INTO `recipe` VALUES (3776, 94, '头晕目眩－杞菊决明子茶', '将枸杞子、菊花、决明子同时放入较大的有盖杯中，用沸水冲泡，加盖，焖15分钟后可开始饮用。', '清肝泻火，养阴明目，降压降脂。主治肝火阳亢型脑卒中后遗症，症见肢体麻木瘫痪，头晕目眩，头重脚轻，面部烘热，烦躁易怒，血压增高，舌质偏红，苔黄，脉弦。', '当茶，频频饮用，一般可冲泡3至5次。', NULL, '枸杞子10克，菊花3克，决明子20克。');
INSERT INTO `recipe` VALUES (3777, 94, '牙痛－地黄乌鸡火锅', '1.将生地黄浸泡3至5小时，取出切成薄片；大枣洗净，沥干水；乌鸡宰杀，去毛、内脏及爪尖，入开水中汆去血水，切成5厘米见方的块；蘑菇、金针菇去蒂，洗尽泥沙；菜心洗净，沥干水；午餐肉切片；藕去皮，切片。以上各料用盘装好，围于火锅四周待用。\r\n2.将骨头汤装入高压锅中，放入乌鸡块、地黄片；大枣、姜、葱、盐、料酒、烧开加入味精调味，即可烫食各种原料。', '补虚损、益气血、生津安神。主治气血虚损、血热伤津、心烦热躁、牙痛等症。', '直接食用或作面汤、佐餐皆可。', NULL, '生地黄、午餐肉、菜心各100克，大枣10个，乌鸡1只（约重1500克），蘑菇、金针菇各150克，藕200克，姜20克，葱15克，精盐10克，味精5克，料酒25毫升，骨头汤2500毫升。');
INSERT INTO `recipe` VALUES (3778, 94, '眼涩眼花－杞子炖羊脑', '将羊脑洗净（注意不要碰破）放入器皿内，加水、盐、葱、姜、料酒，隔水炖熟，加入味精调味即可。', '补肝肾、益脑强身。适用于肝血虚所致的头痛头晕、眼涩眼花、癫痫等。', '佐餐食用，每日2次。', NULL, '枸杞子30克、羊脑1具、葱、姜、料酒、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (3779, 94, '益目－猪肝炒胡萝卜', '将猪肝洗净切片，放于大碗内，放入碗内，加盐、酒、姜、菱粉适量拌匀待用。将胡萝卜洗净切片。炒锅置于旺火上，倒油于锅内，烧热，将胡萝卜放入锅内煸炒，然后倒入猪肝翻炒几下即可装盆。', '补肝、养血、益目，适用于夜盲、目涩、目难远视。', '佐餐食用。', NULL, '猪肝250克、胡萝卜100克。');
INSERT INTO `recipe` VALUES (3780, 95, '补气健胃－黄芪鲫鱼火锅', '1.将鲫鱼去鳃、鳞，剖去内脏，片成5厘米见方、0.3厘米厚的鱼片（鱼刺弃之不用）；猪瘦肉去筋膜，洗净沥水切片；豆腐切块；粉条水发后切段；莴笋叶洗净择好。以上各料全部装盘，围于火锅四周。\r\n2、用干净纱布包上黄芪、炒枳壳，入沙罐中，注入清水，熬2次，每次15分钟，收药液待用。\r\n3.锅置火上，下猪油烧至六成热，下姜片，煸出香味，放盐、胡椒粉、醋、料酒、白糖等，加入汤烧开，撇去浮沫，再下药液，烧开之后，倒入火锅中，烫食各种原料，饮汤。', '补气健胃，美容润颜之功效。主治脾虚所至的食欲不振、消化不良、便溏泄泻。以及气虚所致的气短乏力、胃下垂、脱肛等症。女人常食可美容润肤。', '直接食用或配餐均宜。', NULL, '黄芪15克，鲫鱼3条（约重500克），猪瘦肉200克，豆腐、粉丝各150克，莴笋叶100克，化猪油75克，生姜15克，葱10克，料酒30毫升，白糖、精盐各5克，炒枳壳、味精、胡椒粉各2克，醋3克，鲜汤2000毫升。');
INSERT INTO `recipe` VALUES (3781, 95, '补中益气－红糖炖豆腐', '豆腐洗净切厚片，与红糖共置炖盅内。加开水适量，文火隔水炖15分钟，趁热饮汤食豆腐。', '补中益气，和胃止血。适用于胃、十二指肠溃疡引起的出血，症见吐血，黑大便等。', NULL, NULL, '豆腐100克，红糖39克。');
INSERT INTO `recipe` VALUES (3782, 95, '滑肠－松子粥', '将松子仁研碎，同粳米煮粥。粥熟后冲入适量蜂蜜即可食用。', '补虚，养液，润肺，滑肠。适用于中老年及体弱早衰、产后体虚、头晕目眩、肺燥咳嗽咳血、慢性便秘等症。', NULL, NULL, '松子仁50克、粳米50克、蜂蜜适量。');
INSERT INTO `recipe` VALUES (3783, 95, '健脾开胃－淮山圆肉炖水鱼', '鱼烫死，去内脏和鳃，洗净，淮山、桂圆肉洗净一齐置炖盅内，加开水适量，调味，文火隔水炖熟，食肉饮汤。', '健脾开胃，益气补血。适用于贫血属脾肺肾三脏俱虚者，症见病后体虚，食欲不振，心悸怔忡，倦怠乏力等。', NULL, NULL, '淮山药15克、桂圆肉20克、水鱼200克。');
INSERT INTO `recipe` VALUES (3784, 95, '健脾开胃－莲藕粥', '将藕洗净切薄片，同粳米共入锅中加水煮粥，调入白糖即成。', '健脾开胃，止泻止渴。适用于食欲不佳、大便溏薄及糖尿病。', NULL, NULL, '藕250克，粳米100克，白糖60克。');
INSERT INTO `recipe` VALUES (3785, 95, '健脾开胃－麦芽山楂蛋羹', '将麦芽、山楂、淮山药洗净，放入药锅内，加清水适量，煮1小时左右，去药渣，取汤用。鸡蛋去壳搅拌均匀，淀粉用水调成糊状。将药汤煮沸，加入鸡蛋液及淀粉糊，边下边搅拌，加适量食盐调味。', '健脾开胃，消食导滞。适宜于小儿消化不良，症见脘腹痞胀，大便溏薄等。', NULL, NULL, '鸡蛋2个，麦芽15克，山楂20克，淮山药15克，淀粉适量。');
INSERT INTO `recipe` VALUES (3786, 95, '开胃消食－蜜饯山楂', '将生山楂洗净，去果柄、果核，放在铝锅内，加水适量，煎煮至七成熟、水将耗干时加入蜂蜜，再用小火煮熟煮透，收汁即可。待冷，放入瓶罐中贮存备用。', '开胃消食，活血化淤。适用于冠心病以及肉食不消腹泻。', '每次20克冲服，每日3次。', NULL, '生山楂500克、蜂蜜250克。');
INSERT INTO `recipe` VALUES (3787, 95, '补中益气－大枣粥', '将大枣、粳米淘洗干净后放火锅内，加水适量，煮至烂熟成粥，加入冰糖，搅拌均匀即可食。', '此方具有补中益气，养血安神之功效，适宜于脾胃虚弱，中气不足之倦怠无力、食少、泄泻、血虚脏燥、精神不安者食用。', NULL, NULL, '枣30克，粳米100克，冰糖适量。');
INSERT INTO `recipe` VALUES (3788, 95, '润肠通便－菊楂决明饮', '将决明子打碎，同菊花、生山楂片水煎。', '疏风散热平肝，润肠通便降压。适用于高血压兼有冠心病患者，对阴虚阳亢、大便秘结等症更有效。', '代茶饮，可酌加白糖。', NULL, '菊花10克、生山楂片15克、决明子15克。');
INSERT INTO `recipe` VALUES (3789, 95, '润肠通便－桑椹糯米', '煮粥服食。', '可养肝益脑，滋阴明目，润肠通便，适用于肝阴亏虚便秘者。', NULL, NULL, '桑椹30克，糯米60克，冰糖适量。');
INSERT INTO `recipe` VALUES (3790, 95, '润肠通便－山楂核桃饮', '将核桃仁和山楂用适量的水浸至软化，用搅拌机打碎。再加水至一公升，过滤去渣。将滤液煮沸，加入蔗糖调味即可。', '降血脂，降血压，润肠通便。预防冠心病，高血压，高脂血症及便秘。治血滞经量少、血块引致的行经腹痛。', '代茶饮。', NULL, '核桃150克，山楂50克，蔗糖200克。');
INSERT INTO `recipe` VALUES (3791, 95, '润肠通便－桃仁粥', '先将桃仁捣烂如泥加水研汁去渣，与粳米同煮为稀粥。', '活血祛瘀、润肠通便。适用于高血压、冠心病、心绞痛等。', '每日1次，7天为一疗程。', NULL, '桃仁10克、粳米100克。');
INSERT INTO `recipe` VALUES (3792, 95, '通痹－芝麻粥', '先将芝麻炒干出香味，另煮米成粥，将熟时加入芝麻、砂糖即成。', '通痹、润肠、益五脏、坚筋骨。适宜于肝肾不足、风湿内侵引起的肢节疼痛，或因肺气不足以致大肠不利的便秘等症。', NULL, NULL, '芝麻仁6克，白米30克，白砂糖少许。');
INSERT INTO `recipe` VALUES (3793, 95, '填精补肾－金髓煎', '1.将枸杞洗净，放白酒中浸泡。15天后取出，放入盆中研碎。\r\n2.将酒和枸杞浆汁倒入白布袋中，绞取汁液。\r\n3.将汁液放入铝锅中，先用武火烧开，后移文火上煎熬，浓缩至膏状时停火，稍凉，盛入瓷器内，封贮备用。', '填精补肾，延年益寿。适用于肾虚发白、肾精亏损等症。', '食用时，早晚各服1汤匙，用温酒冲服。', NULL, '枸杞250克、白酒500克。');
INSERT INTO `recipe` VALUES (3794, 95, '填精补肾－人参鹿肉汤', '煮汤食。', '填精补肾，大补元阳。适用于体虚羸瘦、面色萎黄、四肢厥冷、腰膝酸痛、阳痿、早泄等。', NULL, NULL, '人参、黄芪、芡实、枸杞各5克，白术、茯苓、熟地、肉苁蓉、肉桂、白芍、益智仁、仙茅、泽泻、枣仁、淮山药、远志、当归、菟丝子、怀牛膝、淫羊藿、生姜各3克，鹿肉250克，葱、胡椒面、食盐各适量。');
INSERT INTO `recipe` VALUES (3795, 95, '通利肠胃－菠菜粥', '先将菠菜洗净，放滚水中烫半熟，取出切碎。粳米成粥后，将菠菜放入，拌匀，煮沸即成。', '养血止血，敛阴润燥，通利肠胃。适宜于习惯性便秘、高血压等症。大便干结，痔疮出血患者宜经常服用。', NULL, NULL, '新鲜菠菜100克，粳米100克。');
INSERT INTO `recipe` VALUES (3796, 95, '温肾逐寒－附子鸡肉汤', '把全部用料洗净，一齐放入锅内，加清水适量，文火煮2-3小时，至汤水入口无麻辣感。', '温肾逐寒，祛湿止痛。适用于风湿性关节炎、良性膝关节痛、类风湿性关节炎等属寒湿痹阻者。', NULL, NULL, '鸡肉100克，熟附子10克，生姜15克，红枣适量。');
INSERT INTO `recipe` VALUES (3797, 96, '动脉硬化－绿豆粥', '先将绿豆洗净，后以温水浸泡2小时，然后与粳米同入砂锅内，加水1000克，煮至豆烂米开汤稠。', '清热解毒，解暑止渴，消肿，降脂。适用于中暑、暑热烦渴、疮毒疖肿、食物中毒等，还可预防动脉硬化。', '每日2-3次顿服，夏季可当冷饮频食之。', NULL, '绿豆50克、北粳米100克。');
INSERT INTO `recipe` VALUES (3798, 96, '心肌缺血－党参田七炖鸡', '先将田七研成细粉，备用。将党参切片，用纱布袋装后扎口，与鸡肉同入锅，加水适量，加葱、姜、精盐、料酒，用文火炖至肉烂，兑入田七粉，拌匀即成。', '补养心气，改善心肌缺血。主治气虚兼夹血瘀的冠心病心绞痛，症见胸痛，痛有定处，心悸气短，苔薄白，舌质紫，脉细。', '佐餐，当菜食用，每日1次或隔日1次，连续半个月以上。', NULL, '党参15克，田七10克，鸡肉100克。');
INSERT INTO `recipe` VALUES (3799, 96, '高血压－车前子粥', '将车前子用布包好后煎汁。再将粳米入车前子煎汁中同煮为粥。 　　　', '利水消肿，养肝明目，祛痰止咳。适用于老人慢性气管炎及高血压、尿道炎、膀胱炎等。', '每日早晚温热食用。', NULL, '车前子20克、粳米100克。');
INSERT INTO `recipe` VALUES (3800, 96, '高血压－松花淡菜粥', '皮蛋、淡菜、粳米共煮粥，加盐、味精调味即可。', '补益肝肾，益精血，除烦，降火。适用于高血压病。', '早晚温热服用。', NULL, '皮蛋1个、淡菜50克、粳米、盐、味精各适量。');
INSERT INTO `recipe` VALUES (3801, 96, '高血压－天麻钩藤茶', '将天麻、钩藤洗净，加水适量煎煮2次，去渣；以其汁液冲泡绿茶盖严浸泡5-10分钟即可。', '平肝熄风镇静。用于肝阳上亢之高血压、头晕目眩、神经衰弱、四肢麻木等。', '每日1剂，代茶饮用。', NULL, '天麻5克，钩藤6克，绿茶10克。');
INSERT INTO `recipe` VALUES (3802, 96, '高血压－玉竹海参火锅', '1.玉竹用水泡软，切片；水发海参淘尽涩水，沥干，切片；水发香菇去蒂，切为两半；水发玉兰片切片，沥水；午餐肉切片；毛肚片开，切块；猪舌头刮洗干净，入开水中煮至断生，去舌骨，切片；黄豆芽、小白菜择洗干净，沥水。以上各料分别装盘待用。\r\n2.高压锅置火上，下玉竹片、海参片、香菇片、汤、姜、葱、盐，煮开加阀压10分钟，停火降温后舀入火锅中，撇去浮沫，入味精、麻油，便可烫食其它原料。', '补肾益精、养血补气，适用于高血压、血管硬化、皮肤干燥、大便秘结，病后体虚等症。', '佐餐并饮汤。', NULL, '玉竹50克，水发海参500克，水发香菇、水发玉兰片、猪舌头各150克，午餐肉、牛毛肚各100克，黄豆芽、小白菜各200克，姜25克，葱15克，精盐6克，味精5克，麻油10克，鲜汤3000毫升。');
INSERT INTO `recipe` VALUES (3803, 96, '辛凉解表－米醋萝卜菜', '将萝卜洗净切成小的薄片，放花椒、食盐少许，加米醋浸4小时即可。食用时淋香油。', '辛凉解表，消食解毒。用于治疗便秘、高脂血症、脂肪肝、冠心病、动脉硬化等，也用于预防流行性感冒。', '佐餐食用，每日2次。', NULL, '生白萝卜250克、米醋适量。');
INSERT INTO `recipe` VALUES (3804, 96, '冠心病－蜜饯山楂', '将生山楂洗净，去果柄、果核，放在铝锅内，加水适量，煎煮至七成熟、水将耗干时加入蜂蜜，再用小火煮熟煮透，收汁即可。待冷，放入瓶罐中贮存备用。', '开胃消食，活血化淤。适用于冠心病以及肉食不消腹泻。', '每次20克冲服，每日3次。', NULL, '生山楂500克、蜂蜜250克。');
INSERT INTO `recipe` VALUES (3805, 96, '冠心病－玉竹猪心', '1.将玉竹洗净，切成节，用水稍润，煎熬2次，收取药液800克。\r\n2.将猪心剖开，洗净血水，与药液、生姜、葱、花椒同置锅内用中火煮到猪心六成熟时，将它捞出晾凉。\r\n3.将猪心放在卤汁锅内，用文火煮熟捞起，揩净浮沫。在锅内加卤汁适量，放入食盐、白糖、味精和香油，加热成浓汁，将其均匀地涂在猪心里外即成。\r\n　　', '安神宁心，养阴生津。主治冠心病、心律不齐以及热病伤阴的干咳烦渴。', '每日2次，佐餐食用。', NULL, '玉竹50克、猪心500克、生姜、葱、花椒、食盐、白糖、味精、香油适量。');
INSERT INTO `recipe` VALUES (3806, 96, '活血－山楂荷叶饮', '将山楂、荷叶水煎1000克。', '活血化淤，消导通滞、清暑除烦。适用于高血压兼有高脂血症患者，还可做夏季消暑饮料。', '随意代茶饮用。', NULL, '山楂15克、荷叶12克。');
INSERT INTO `recipe` VALUES (3807, 96, '活血－桃仁粥', '先将桃仁捣烂如泥加水研汁去渣，与粳米同煮为稀粥。', '活血祛瘀、润肠通便。适用于高血压、冠心病、心绞痛等。', '每日1次，7天为一疗程。', NULL, '桃仁10克、粳米100克。');
INSERT INTO `recipe` VALUES (3808, 96, '活血－天麻酒', '将上述各味切碎，纳入纱布袋内，扎紧袋口，放入酒坛内，倒入白酒密封浸泡半个月以上，每天振摇1次，即成。', '补养肝肾，活血祛风。适用于冠心病、高血压、高脂血症及肥胖等症。', '每日2次，每次服10毫升。', NULL, '天麻72克，制首乌36克，丹参48克，黄芪12克，杜仲、淫羊藿16克，白酒2000克。');
INSERT INTO `recipe` VALUES (3809, 96, '降血压－葛根粉粥', '粳米浸泡一宿，与葛根粉同入砂锅内，加水600克，用文火煮至米开粥稠即可。', '发表解肌，清热除烦，生津止渴，透疹止泻，降低血压。适用于高血压、冠心病、老年性糖尿病、慢性脾虚泻泄、夏令口渴多饮等。', '随意温热食用。', NULL, '葛根粉30克、粳米100克。');
INSERT INTO `recipe` VALUES (3810, 96, '降血压－荷叶粥', '取梗米煮粥，待粥熟后加适量冰糖搅匀，趁热将荷叶撕碎覆盖粥面上，待粥呈淡绿色取出荷叶即可食用。\r\n　　', '清暑利湿，升发清阳，止血，降血压，降血脂。适用于中暑、高血压、高脂血症、肥胖病以及夏天感受暑热致头昏脑胀、胸闷烦渴、小便短赤等。', '可作夏季清凉解暑饮料，或作点心供早晚餐，温热食用，也可凉饮。', NULL, '新鲜荷叶1张、粳米100克、冰糖适量。');
INSERT INTO `recipe` VALUES (3811, 96, '降血压－菊苗粥', '将菊苗洗净切细，煎水取汁，约100克，入北粳米、冰糖，再加水600克，煮成稀薄粥。', '清肝明目，降低血压。适用于高血压、高脂血症、目疾等。', '每日2次，温热食用。', NULL, '甘菊新鲜嫩芽或幼苗40克、北粳米100克、冰糖适量。');
INSERT INTO `recipe` VALUES (3812, 96, '降血压－菊楂决明饮', '将决明子打碎，同菊花、生山楂片水煎。', '疏风散热平肝，润肠通便降压。适用于高血压兼有冠心病患者，对阴虚阳亢、大便秘结等症更有效。', '代茶饮，可酌加白糖。', NULL, '菊花10克、生山楂片15克、决明子15克。');
INSERT INTO `recipe` VALUES (3813, 96, '降血脂－红花绿茶饮', '将红花、绿茶放入有盖杯中，用沸水冲泡。', '降低血脂，活血化瘀。主治血瘀痰浊型高脂血症，症见身体肥胖，胸闷刺痛，脘痞腹胀。', '当茶频频饮服，一般冲泡3至5次。', NULL, '红花5克，绿茶5克。');
INSERT INTO `recipe` VALUES (3814, 96, '降血脂－菊花山楂茶', '水煎或开水冲泡10分钟即可。', '健脾，消食，清热，降脂。适用于冠心病、高血压、高脂血症、肥胖。', '每日1剂，代茶饮用。', NULL, '菊花15克、生山楂各20克。');
INSERT INTO `recipe` VALUES (3815, 96, '降血脂－灵芝甜酒', '将灵芝洗净，晾干，切成片，加酒和蜂蜜，密封，冷浸30天，即可饮用。', '降低血脂，益气补脾，镇静安神。主治高脂血症出现头昏气短乏力、失眠多梦、心悸烦躁等症，辨证心脾两虚的病人。', '每日2次，每次2盅。', NULL, '灵芝50克，糖食酒1000克，蜂蜜20克。');
INSERT INTO `recipe` VALUES (3816, 96, '降血脂－罗布麻茶', '取上述三药加冰糖两三块，热开水泡茶饮，饮至味淡再换一杯。 　　', '清热平肝，活血化淤，生津止喝。此茶久饮可降低血脂，降低血压，并可防治冠心病。', '不拘量，代茶饮。', NULL, '罗布麻叶6克、山楂15克、五味子5克、冰糖适量。');
INSERT INTO `recipe` VALUES (3817, 96, '降血脂－沙苑子白菊花茶', '上两味，同入锅，加水煎煮成300毫升。', '平补肝肾，降低血脂，降压明目。主治高脂血症，高血压病出现头昏、目眩、腰痛、尿频等症，辨证属于肝肾不足类型者。', '分6次，当茶饮，温服。当日服食完。', NULL, '沙苑子30克，白菊花10克。');
INSERT INTO `recipe` VALUES (3818, 96, '降血脂－山楂桑椹子煎', '山楂、桑椹子先用温开水浸泡，冲洗干净，入锅，加水适量，文火煎煮20分钟即成。', '补益肝肾，滋阴养血，消食降脂，软化血管。主治阴亏血虚型动脉硬化，症见头晕耳鸣，目暗昏花，须发早白，口干，便秘，失眠，遗精等。', '上下午分服，食果饮汤。', NULL, '鲜山楂30克（干品20克），桑椹子30克（干品20克）。');
INSERT INTO `recipe` VALUES (3819, 96, '降血脂－山楂乌梅饮', '将山楂、乌梅和水煎一小时，浓缩至一公升，过滤去渣，将浓缩液装入阔口瓶中。', '降血脂，抗病毒，抗过敏，止泻。适用血管粥样硬化，血脂高。', '分四次饮用。\r\n　　', NULL, '用山楂一两（30g），乌梅五钱（15g），水1.5公升。');
INSERT INTO `recipe` VALUES (3820, 96, '降压降脂－杞菊决明子茶', '将枸杞子、菊花、决明子同时放入较大的有盖杯中，用沸水冲泡，加盖，焖15分钟后可开始饮用。', '清肝泻火，养阴明目，降压降脂。主治肝火阳亢型脑卒中后遗症，症见肢体麻木瘫痪，头晕目眩，头重脚轻，面部烘热，烦躁易怒，血压增高，舌质偏红，苔黄，脉弦。', '当茶，频频饮用，一般可冲泡3至5次。', NULL, '枸杞子10克，菊花3克，决明子20克。');
INSERT INTO `recipe` VALUES (3821, 96, '降压降脂－山楂核桃饮', '将核桃仁和山楂用适量的水浸至软化，用搅拌机打碎。再加水至一公升、过滤去渣。将滤液煮沸，加入蔗糖调味即可。', '降血脂，降血压，润肠通便。预防冠心病，高血压，高脂血症及便秘。治血滞经量少、血块引致的行经腹痛。', '代茶饮。', NULL, '核桃150克，山楂50克，蔗糖200克。');
INSERT INTO `recipe` VALUES (3822, 96, '降压降脂－小米粉粥', '将小米粉加适量冷水调和，将粳米粥煮沸后入小米粉糊同煮为粥。 　　', '降脂，降压。对动脉硬化、冠心病、心肌梗塞及血液循环障碍有一定的治疗作用；高脂血症病人常服也有效。', '早晚餐温热服用。', NULL, '小米粉、粳米各适量。');
INSERT INTO `recipe` VALUES (3823, 96, '脑动脉硬化－鹿角胶粥', '先煮粳米和枸杞为粥后，加入鹿角胶，使其溶化，再煮二、三沸即可。', '补肝肾，益精血，主用于脑动脉硬化的防治。', '早晨空腹以粥代食，可稍加糖调味。半个月一个疗程。', NULL, '鹿角胶20克，枸杞30克，粳米100克。');
INSERT INTO `recipe` VALUES (3824, 96, '气血虚弱－当归生姜羊肉汤', '将羊肉去油膜切成方块，与当归、生姜同炖，取出当归、生姜，加入精盐调味即可。', '温养阴血止痛。适用于气血虚弱、血虚气寒引起的腹部隐隐作痛。', '食肉喝汤，早晚温热食用。', NULL, '当归10克，生姜20克，羊肉100克，精盐适量。');
INSERT INTO `recipe` VALUES (3825, 96, '软化血管－槐花茶', '将槐花放入有盖杯中，用沸水冲泡。', '软化血管，降脂降压，凉血止血。主治各种类型的动脉硬化症，对动脉硬化合并高血压，肝火上炎，有脑血管破裂倾向者尤为适宜。', '当茶频频饮用，一般冲泡3-5次。', NULL, '干槐花10克（干品20克）。');
INSERT INTO `recipe` VALUES (3826, 96, '软化血管－女贞子蜂蜜饮', '先将女贞子放入锅中，加水适量，文火煎煮30分钟，去渣取汁，调入蜂蜜即可。', '滋补肝肾，软化血管。主治肝肾阴虚型动脉硬化，症见头晕目眩，腰酸耳鸣，须发早白，遗精，便秘等。', '上下午分服。', NULL, '女贞子20克  蜂蜜30克。');
INSERT INTO `recipe` VALUES (3827, 96, '养心－荷花首乌肝片', '1.把荷花洗净，切成3厘米见方的片；姜切片，葱切段，蒜切片；小油菜切段待用。\r\n2.将炒锅至武火上烧热，放入素油烧至六成热时下入猪肝（用首乌粉、豆粉抓匀）滑炒，随后放入葱、姜、蒜、荷花，调味至熟即成。 　　', '养心，益肾，乌须发。', '佐餐食用。', NULL, '荷花2朵，首乌粉20克，猪肝200克，豆粉20克，小油菜20克，盐3克，味精2克，葱5克，姜3克，蒜3克。');
INSERT INTO `recipe` VALUES (3828, 96, '养血益精－龟板胎盘膏', '胎盘用清水漂洗干净，切块；龟板捣碎，同放砂锅中加水适量，文火煎煮，过滤取汁。将3次滤取的药液用文火煎煮浓缩至200毫升左右，再投入捣碎的阿胶搅拌使其溶化，最后加入红糖收膏，阴凉处贮存备用。', '补气滋阴，养血益精充脑。用于脑动脉硬化症。', '每次2匙，日3次，空腹温开水冲服。以上为一疗程量。', NULL, '龟板300克，胎盘200克，红糖1斤。');
INSERT INTO `recipe` VALUES (3829, 96, '养血止血－花生衣', '将花生米用热水烫后取皮，晒干研碎备用。', '养血止血。适用于再生障碍性贫血和出血的患者。', '每次6克冲服，每日2次。', NULL, '花生衣末12克。');
INSERT INTO `recipe` VALUES (3830, 96, '中风－桑叶汤', NULL, '祛风，安神。主治摇头不止，言语不清等症。', '水煎服，日服2次。', NULL, '桑叶3-6克。');
INSERT INTO `recipe` VALUES (3831, 97, '百草脱骨扒鸡', '选用健康毛鸡，宰杀，热水烫，脱毛，开膛，去内脏及瘀血，选净，烹炸，然后在有老汤及中药汁的锅中煮制而成。中药汁是将特定配方的中药粉碎，布包煎煮，过滤去渣而得。煮成的百草脱骨扒鸡，出锅凉透，装袋抽空，经高压120度灭菌，凉透，卫生包装，即得真空包装的成品。', '滋养五脏，补益气血，消食化积，活血化瘀。应用：老少皆宜，尤适用于病后、产后身体虚弱及年老体衰者。感冒者慎用。', '作滋补营养菜肴，或作成汤，适量食用。', NULL, '茯苓、百合、龙眼肉、蜂蜜、芡实、枸杞子、山楂、白果、花椒等中药，鸡。中药与鸡的重量比为1.2：100。');
INSERT INTO `recipe` VALUES (3832, 97, '何首乌山鸡', '何首乌用清水洗净、放入铝锅内煮二次，共收药液20毫升。山鸡去净毛，剖腹去内脏，洗净去骨，切成丁。冬笋，青椒切成丁。鸡蛋去黄留清。蛋清加入豆粉，调成蛋清豆粉，用一半入少许精盐将山鸡丁浆好，另一半同料酒、酱油、味精、首乌汁兑成液汁待用。净锅置火上，注入菜油，烧至六成热时下鸡丁过油滑熟，随即捞入勺内待用。锅留底油，加入鸡丁、冬笋、青椒、倒入液汁勾芡，起锅装盘即成。', '补肝肾、乌须发，悦颜色，延寿命之效。', '佐餐食用。', NULL, '山鸡二只、炙何首乌10克、青椒100克、冬笋15克、酱油10克、料酒20毫升、味精1克、精盐2克、豆粉20克、鸡蛋1个、菜油1000克（实际消耗60克）。');
INSERT INTO `recipe` VALUES (3833, 97, '女贞子酒', '冬季果实成熟时采收，将女贞子洗净，蒸后晒干，放入低度白酒中，加盖密封，每天振摇1次，1周后开始服用。', '补益肝肾，抗衰祛斑。主治老年脂褐质斑。', '每日1至2次，每次1小盅。', NULL, '女贞子200克，低度白酒500毫升。');
INSERT INTO `recipe` VALUES (3834, 97, '芹菜炒香菇', '芹菜去叶、根，洗净，剖开切成2厘米长的段，用盐拌匀约10分钟，清水漂洗滤干待用。香菇切片，醋、味精、淀粉混合后装在碗里加水约50毫升兑成芡汁待用。炒锅烧热后，倒入菜油30克，油炼至无泡沫、冒青烟时，入芹菜煸炒2－3分钟，投入香菇片迅速炒匀，再加入酱油稍炒，淋入芡汁速炒起锅即成。', '平肝清热，益气和血。适用于肝阳上亢的头痛、眩晕。可作为高血压、高脂血症、动脉硬化、神经衰弱患者的保健膳食。', '作佐餐食之。', NULL, '芹菜400克，香菇（水发）50克，食盐、干淀粉、酱油、味精、菜油各适量。');
INSERT INTO `recipe` VALUES (3835, 97, '清炖猪蹄膀', '将猪蹄膀洗净后用刀剖开呈4至6瓣。锅烧开水，将蹄膀放入，烫透，煮沸数次，撇沫。坐大砂锅，放清水烧开，下蹄膀，放入泡发的红枣，煮沸，加料酒，转小火炖至蹄膀皮酥软，肉烂熟，下精盐、葱、胡椒面、姜、味精、白糖各适量，继续煨炖30分钟即成。', '和血脉，润肌肤，填肾精，健腰脚，强体增肥。主治老年单纯性消瘦。', '佐餐当菜，随意服食。', NULL, '猪蹄膀（带皮）1个，红枣5枚。');
INSERT INTO `recipe` VALUES (3836, 97, '琼玉膏', '将人参、茯苓粉碎成细末，生地黄捣取自然汁（捣时不用铜铁器），用绢过滤蜜，将四物合并搅匀，装入瓷罐内，用20－30层净纸封闭。取大铝锅一口，盛装净水，将药罐放入铝锅内，先用武火，后用文火，隔水煮熬，三天三夜后取出，用蜡封罐口，入水中浸去火毒10日，再入原锅内煮一天一夜即成。', '补气养血，填精生髓。应用：作为中老年人平时的保健食品。', '早晚各服1次，空腹服，每次1汤匙。', NULL, '人参120克，生地黄汁800克，白茯苓245克，蜂蜜500克。');
INSERT INTO `recipe` VALUES (3837, 97, '雀儿药粥', '先把菟丝子、覆盆子、枸杞子一同放入沙锅内煎取药汁，去掉药渣，再将麻雀去毛及肠杂，洗净用酒炒，然后与粳米、药汁加水适量一并煮粥，欲熟时，放入细盐、葱白、生姜，煮成稀粥。', '壮阳气，补精血，益肝肾。应用：肾气不足所致的阳萎，遗精，早泄，头晕眼花，视物不清，耳鸣耳聋，遗尿，妇女带下等。老年人经常服食可健身益寿。注意事项：以冬季服食为宜，3-5天为1疗程。发热及性机能亢进者忌服。', '空腹食之，早晚各1次。\r\n　　', NULL, '麻雀5只，菟丝子30-45克，覆盆子10-15克，枸杞子20-30克，粳米60克，细盐少许，葱白2茎，生姜3片。');
INSERT INTO `recipe` VALUES (3838, 97, '人参蜂蜜粥', '将人参切片，置清水中浸泡1夜，连同泡参水与洗净的粳米一起放砂锅中，文火煨粥，粥将熟时放入蜂蜜、生姜汁、生韭菜汁调匀，再煮片刻即成。', '调中补气，润肠通便，丰肌泽肤功效。适用于因气虚而致的面色苍白无华者及有气血两虚表现的大便秘结的中老年人。', '每日1剂，分次食用。每6剂为1个疗程。间隔6-10日后可用下1个疗程。', NULL, '人参3克，蜂蜜50克，生姜汁5毫升，韭菜汁5毫升，粳米100克。');
INSERT INTO `recipe` VALUES (3839, 97, '桑椹蜜膏', '桑椹洗净，加水适量煎煮30分钟取煎液后，加水再煎取二汁，合并煎液，以文火煎熬浓缩，至较稠粘时，加入蜂蜜，至沸停火，待冷装瓶备用。', '滋补肝肾，补益气血。适用于须发早白，耳鸣，耳聋，便秘，失眠，健忘，病后血虚，未老先衰等。', '早晚各1次，每次1汤匙，沸水冲饮。', NULL, '鲜桑椹1000克（或干品500克），蜂蜜300克。');
INSERT INTO `recipe` VALUES (3840, 97, '山药芝麻糊', '粳米洗净，清水浸泡1小时，捞出滤干，山药切成小颗粒，黑芝麻炒香后同放盆中，加水和鲜牛奶拌匀，磨碎后滤汁待用。锅中加入清水、冰糖，溶化过滤后烧开，将芝麻水慢慢倒入锅内，放入玫瑰酱，不断搅拌成糊，熟后起锅即成。', '滋阴补肾，益脾润肠。适用于肝肾不足，病后体弱，大便燥结，须发早白等。中老年人平时服用，可健体强身，延年益寿。', '适量服用。', NULL, '山药15克，黑芝麻120克，玫瑰酱6克，鲜牛奶200克，冰糖120克，粳米60克。\r\n　　');
INSERT INTO `recipe` VALUES (3841, 97, '山楂核桃饮', '核桃仁加水少许，用石磨磨成浆，装入容器中，再加适量凉开水调成稀浆汁。山楂去核，切片，加水500毫升煎煮半小时，滤出头汁，再煮取二汁，一、二汁合并，复置火上，加入白糖搅拌，待溶化后，再缓缓倒入核桃仁浆汁，边倒边搅匀，烧至微沸即可。', '补肺肾，润肠燥，消食积。用于肺虚咳嗽、气喘，腰痛，便干，食积，经少腹痛等。也可作为冠心病、高血压、高脂血症及老年便秘等患者的保健饮料。', '早晚各服1次，温服为宜。', NULL, '核桃仁150克，山楂50克，白糖200克。');
INSERT INTO `recipe` VALUES (3842, 97, '松子核桃膏', '松子仁、核桃仁用水泡过去皮；然后研成末，放入蜂蜜和匀即成。', '益精润燥、补脑安神。核桃含有丰富的蛋白质、脂肪、维生素A、维生素E、维生素B、烟酸及钙、磷、铁、锌、锰、铬等人体所需的营养物质，有抗衰老、健脑、强心等重要作用。松子仁是补五脏、补虚损、益智力佳品。蜂蜜也是润养补益之品，有明显的抗衰老和益智作用。适宜于腰膝酸软、健忘失眠、心神不宁、大便干燥者服。大便溏泻者慎食。', '每日2次，每次取1汤匙，用滚开水冲服。', NULL, '松子仁、核桃仁各30克，蜂蜜250克。');
INSERT INTO `recipe` VALUES (3843, 97, '松子抗衰膏', '将松子仁、黑芝麻、核桃仁同捣成膏状，入砂锅中，加入黄酒，文火煮沸约10分钟，倒入蜂蜜，搅拌均匀，继续熬煮收膏，冷却装瓶备用。 \r\n　　', '滋润五脏，益气养血。适用于治疗肺肾亏虚、久咳不止、腰膝酸软、头晕目眩等症。中老年人经常服用，可滋补强壮、健脑益智、延缓衰老。脑力劳动者经常服用能使思维敏捷、记忆力增强，是抗老防衰的有效食品。', '每日2次，每次服食1汤匙，温开水送服。', NULL, '松子仁200克，黑芝麻100克，核桃仁100克，蜂蜜200克，黄酒500毫升。');
INSERT INTO `recipe` VALUES (3844, 97, '乌发糖', '1.将红糖放入铝锅内，加水适量，用武火烧开，移文火上煎熬至稠厚时，加炒香的黑芝麻、核桃仁搅拌均匀停火即成乌发糖。\r\n2.将乌发糖倒入涂有熟菜油的搪瓷盘中摊平、晾凉，用刀划成小块，装糖盒内备用。', '健脑补肾，乌发生发。适用于头昏耳鸣、健忘、脱发、头发早白等症。久服有预防早衰作用。', '早、晚各食3块。', NULL, '核桃仁250克，黑芝麻250克，赤砂糖500克。');
INSERT INTO `recipe` VALUES (3845, 97, '熙春酒方', '女贞子（九蒸九晒），生地、淫羊藿、绿豆洗净晒干。共入绢袋内，扎紧，备用。瓷瓶内装烧酒2公斤，放入药袋，严密封口，浸制1个月即成。', '温肾补肺，泽肌肤，美毛发。平时服使容颜少壮，毛发润泽，并治老年人久嗽。可益气健脾。中老年人每天少饮此酒，可健身益寿。', '早晚各服1次，每次15毫升。', NULL, '枸杞子20克，龙眼肉20克，女贞子20克，生地20克，白酒2公斤，淫羊藿20克，猪油100克，绿豆20克。');
INSERT INTO `recipe` VALUES (3846, 97, '金髓煎', '1.将枸杞洗净，放白酒中浸泡。15天后取出，放入盆中研碎。\r\n2.将酒和枸杞浆汁倒入白布袋中，绞取汁液。\r\n3.将汁液放入铝锅中，先用武火烧开，后移文火上煎熬，浓缩至膏状时停火，稍凉，盛入瓷器内，封贮备用。', '填精补肾，延年益寿。适用于肾虚发白、肾精亏损等症。', '食用时，早晚各服1汤匙，用温酒冲服。', NULL, '枸杞250克、白酒500克。');
INSERT INTO `recipe` VALUES (3847, 97, '养元鸡子', '鸡蛋打入碗中备用；小茴香、菟丝子、桑寄生、蜜炙黄芪入砂锅中，加水适量，煎煮2小时，趁沸时滤取药汁冲调蛋花，可依个人口味调以白糖或食盐。', '补肾健脑，强壮元阳。常服可治疗肾虚所致的早衰。', '每晚睡前服1次。', NULL, '鸡子2枚，小茴香6克，菟丝子15克，桑寄生15克，蜜炙黄芪15克。');
INSERT INTO `recipe` VALUES (3848, 97, '益寿鸽蛋汤', '1、将枸杞子、龙眼肉、制黄精均洗净切碎，待用；冰糖砸碎装在碗内。2、锅置中火上注入清水约750克，加入以上3味同煮至沸后约15分钟，再把鸽蛋打破逐个下锅内，同时将冰糖屑下入锅中同煮至熟即成。', '补肝肾，益气血。适用于肺燥咳嗽、气血虚衰、智力减退等症。可作为老年体衰者之膳食。', '空腹服。每日服1次，连服7日。', NULL, '枸杞子10克、龙眼肉10克、制黄精10克、鸽蛋4个、冰糖50克。');
INSERT INTO `recipe` VALUES (3849, 97, '芝麻白糖糊', '将芝麻拣净，放入铁锅用文火炒香后晾凉、捣碎后，装入瓦罐内备用。', '补阴血，养肝肾，乌须发，长肌肉，填精髓。适用于平时调补，以抗早衰；肺燥咳嗽，皮肤干燥、肝肾阴虚的头发早白及老人便秘等症。', '每次2汤匙，放入碗中，再加白糖适量，用开水冲服。', NULL, '芝麻500克，白糖适量。');
INSERT INTO `recipe` VALUES (3850, 98, '癌肿－珍珠米糊', '1.将珍珠米洗净，捣烂（搅拌机搅拌）成浆；\r\n2.把珍珠米浆放入沸水锅内，并不断搅拌，放白糖适量，煮熟即可。\r\n　　', '健脾养胃，益阴润燥。适应各癌肿以及癌肿术后，放疗，化疗期间和治疗后脾肾不足者，症见虚弱无力，不思饮食，烦闷神疲，口渴咽干等。', '随量食用。', NULL, '鲜珍珠米250克，白糖适量。\r\n　　');
INSERT INTO `recipe` VALUES (3851, 98, '白血病甲状腺癌－猪骨汤', '将黄豆洗净，浸半小时，蚝豉洗净。猪脊骨洗净，斩件。把全部用料一齐放火锅内，加清水适量，武火煮2小时，调味即可。随量饮用。', '健脾养血，益阴除烦。主治甲状腺癌属阴虚烦热者，症见口渴心烦，烦热失眠，胃纳不佳等；白血病属阴虚，症见发热，大便干结等。', NULL, NULL, '黄豆90克，蚝豉60克，猪脊骨250克。');
INSERT INTO `recipe` VALUES (3852, 98, '肠癌－猪肠汤', '将木耳浸开洗净；水发海参洗净，切丝，猪大肠用粗盐一汤匙擦洗净，放入开水中稍烫，再用冷水冲洗后，切段。把全部用料一齐放入锅内，加清水适量，武火煮沸后，文火煮1至2小时，调味即可。随量饮汤食肉。', '滋阴养血，凉血止痢。主治肠癌属阴血亏虚、热伤肠络者，症见下痢脓血，腹中隐痛，口干欲饮等。', '滋阴养血，凉血止痢。', NULL, '猪大肠250克，木耳15克，水发海参120克。');
INSERT INTO `recipe` VALUES (3853, 98, '防癌－绞股蓝茶', '取绞股蓝加水1000克，煎15分钟。取汁即可。或取绞股蓝15克冲茶至味淡。', '益气养血、消瘀散结、扶正抗癌。用于防治癌症、糖尿病，治疗神衰疲劳、高血脂症等。', '分多次代茶饮用。', NULL, '绞股蓝30-50克。');
INSERT INTO `recipe` VALUES (3854, 98, '防治鼻咽癌－菊花茶', '取野菊花，沸水冲泡10分钟，入冰糖溶化即可。', '清热解毒、凉血明目。用于防治鼻咽癌。', '每日2剂，代茶饮用，冲至无味。', NULL, '野菊花15克（鲜品加倍）、冰糖20克。');
INSERT INTO `recipe` VALUES (3855, 98, '肺癌－瓜蒌饼', '瓜蒌去籽，放在锅内，加水少许，加白糖100克，以小火煨熬，拌成馅。另取面粉750克，加水适量经发酵加面碱，揉成面片，把瓜蒌夹在面片中制成面饼，烙熟或蒸熟。', '润肺化痰，散结宽胸。适用于肺癌胸痛。', '佐餐或随意服用。', NULL, '瓜蒌200克、面粉600克、白糖75克、清水适量。');
INSERT INTO `recipe` VALUES (3856, 98, '肺癌食道癌－灵芝粉蒸肉饼', '1.将灵芝洗净，晾干研末；猪瘦肉洗净剁成肉酱。\r\n2.把灵芝末与肉酱放入容器内，加酱油及生油少许，食盐适量拌匀，隔水蒸熟即可。', '补气益血，养心健脾。适用于肺癌、食道癌、其他癌肿属虚证者，或放疗、化疗。术后身体虚弱。白细胞减少者，症见神疲倦怠，心悸失眠，食少懒言等。', '随量食用，或佐膳。', NULL, '灵芝3克，猪瘦肉100克。');
INSERT INTO `recipe` VALUES (3857, 98, '肺癌－笋菇肉丝', '水发香菇洗净切丝，香菇浸出液沉淀，滤清备用；芦笋切丝；猪肉切丝放入打碎的鸡蛋拌匀。肉丝过油捞出，余油加入葱、姜略炒，放入笋、菇、肉丝、盐、味精翻炒，加入香菇浸出液略煮，水淀粉匀芡，淋麻油出锅即可。', '健脾理气、清热化痰。常用于肺癌。', '早晚温热食用。', NULL, '芦笋300克、香菇50克、瘦猪肉200克、鸡蛋2个、葱、姜、油、盐、淀粉、味精、麻油各适量。');
INSERT INTO `recipe` VALUES (3858, 98, '肺癌－无花果粥', '取粳米洗净常法煮粥，粥将成时加入去皮无花果略煮，加入冰糖即可。', '润肺解毒。用于防治早期肺癌，咽痛、咳嗽、泻痢、痔疮等。', '佐餐食用，连服20日。', NULL, '鲜无花果30克、粳米50克、冰糖适量。');
INSERT INTO `recipe` VALUES (3859, 98, '肺癌－仙枣赤豆粥', '取生薏米、赤小豆加温水浸泡半日；仙鹤草用纱布包好；大枣去核备用；取上述诸药加水常法共煮成稀粥，加入白糖调味即可。', '清热解毒、活血止血。适用于肺癌血热症。', '佐餐食用，连服15日。', NULL, '仙鹤草60克、大枣20枚、赤小豆50克、生薏米100克、白糖适量。');
INSERT INTO `recipe` VALUES (3860, 98, '肝癌－玫瑰花茶', '将花与茶同置大杯中，以沸水冲泡10分钟即可。', '理气解郁、舒肝健脾、扶正抗癌。宜于肝癌患者饮用。', '分多次饮用，连服4周。', NULL, '玫瑰花瓣10克、茉莉花5克、云南抗癌保健茶10克。');
INSERT INTO `recipe` VALUES (3861, 98, '肝癌－香菇薏米饭', '取生薏米洗净浸透心；温水发香菇，香菇浸出液沉淀滤清备用；香菇、油豆腐切成小块。将粳米、薏米、香菇、油豆腐、香菇浸出液等加入盆中混匀，加油盐调味，撒上清豆上笼蒸熟即可。', '健脾利湿、理气化痰。肝癌患者常用食物。', '用法：\r\n三餐做主食用，连服15天。', NULL, '粳米250克、生薏米50克、香菇50克、油豆腐3块、青豆半小碗、油、盐各适量。');
INSERT INTO `recipe` VALUES (3862, 98, '肝癌消化道癌症－黑木耳炒猪肝', '将木耳用冷水泡发，拣杂后撕朵，分开。猪肝洗净后切成薄片，以湿淀粉少许，抓芡均匀。锅内放植物油，旺火上烧至八成热，先把猪肝在热水中焯一下，控净水分下入油锅内滑炒数下，加料酒、葱末、姜丝、精盐，再煸炒至猪肝熟透，倒漏勺里。锅留底油，用旺炎翻炒木耳，炒至木耳亮滑透香时，把猪肝倒回炒锅，随即加入味精、芝麻油适量，拌和均匀即成。', '补益肝肾，强体抗癌。主治肝癌及消化道癌症等。', '佐餐当菜，随量服食，当日吃完。\r\n　　', NULL, '黑木耳25克，猪肝250克。');
INSERT INTO `recipe` VALUES (3863, 98, '肝炎癌症－泥鳅粉', '先把活泥鳅放清水中养1天，使其排净肠内废物，次日再把它放干燥箱内烘干或焙干研末装瓶。', '温中益气，解毒。适用于肝炎、癌症。', '每日3次，每次10克，温开水送服。15日为一疗程，连续食用最多不超过4个疗程。', NULL, '活泥鳅2000克。');
INSERT INTO `recipe` VALUES (3864, 98, '前列腺癌－蛇草薏苡仁粥', '将白花蛇舌草洗净加水1500毫升，急火煮开改文火煎15分钟，去渣取汁；加苡仁煮至苡仁裂开，再加菱粉煮熟即可。', '清热解毒、健脾利水。可用于前列腺癌。', '分数次温热食用。', NULL, '白花蛇舌草80克、菱粉50克、薏苡仁50克。');
INSERT INTO `recipe` VALUES (3865, 98, '食管癌－糟茄', '将紫茄子洗净切块，与酒糟、精盐共置于瓷罐中拌匀封好，根据气温，约1个月左右即可。', '清热解毒、散血消肿。对食管癌有一定疗效。健康人食用能防止癌变。', '佐餐食用，每日40克，连服3周。', NULL, '紫茄子2000克、精盐200克、酒糟250克。');
INSERT INTO `recipe` VALUES (3866, 98, '晚期肺癌－银杏蒸鸭', '银杏去壳，开水煮熟后去皮、芯，再用开水焯后混入杀好去骨的鸭肉中。加清汤，上笼蒸2小时至鸭肉熟烂后食用。', '补虚平喘、利水退肿。适宜于晚期肺癌喘息无力、全身虚弱、痰多者。', '佐餐，可经常食用。', NULL, '银杏200克、白鸭1只。');
INSERT INTO `recipe` VALUES (3867, 98, '胃癌－海带炖鲫鱼', '水发海带切成丝，将活鲫鱼去鳃和肠杂，留鳞，油锅煎至略黄，加入少许食盐、生姜、葱、花椒、料酒，加入海带丝炖煮40分钟，加味精调味即可。', '消痰软坚、健脾利水。可用于胃癌。', '分2次佐餐食用。', NULL, '海带20克、鲫鱼1尾（约300克）、生姜、葱、花椒、油、盐、料酒、味精各适量。');
INSERT INTO `recipe` VALUES (3868, 98, '胃癌－莲子奶糊', '1.将石莲子去硬壳，磨粉，用少量清水调成糊状；\r\n2.煮牛奶，放入白糖，将沸时慢慢倒入莲子水糊并不断搅拌，煮熟即可。', '健脾益胃，补虚养神。主治胃癌以及其他癌肿化疗期间和治疗后胃气虚弱者，或有心烦欲呕、头晕目眩等症者。', '随量食用。\r\n　　', NULL, '石莲子60克，牛奶200毫升，白糖适量。');
INSERT INTO `recipe` VALUES (3869, 98, '胃癌食道癌－韭汁牛乳饮', '将韭菜洗净捣烂用纱布绞汁；将牛乳烧开，每次用韭汁100克，冲入姜汁即可。', '温中行气、活血解毒。用于胃癌、食道癌。', '分数次温热缓缓咽下。\r\n　　', NULL, '韭菜800克、牛奶400克、生姜汁200克。');
INSERT INTO `recipe` VALUES (3870, 56, '西红柿烧豆腐', '(1)先把西红柿用清水洗净， 用开水烫泡， 去其皮，切成厚片，豆腐切成3cm长的块。\r\n(2)将炒勺上旺火，加底油，下西红柿炒一分钟，再加豆腐、酱油、盐、糖，烧透后用淀粉勾芡即可。', NULL, NULL, '食油、酱油、糖、盐、淀粉。', '西红柿300克，豆腐300克。');
INSERT INTO `recipe` VALUES (3871, 56, '四喜豆腐', '(1)将豆腐弄碎(如豆腐水多可用布挤出)，加少许团粉及咸味搅拌，油莱叶洗净用开水烫一遍，同其它料——起剁成末，加调料与豆腐合成馅。\r\n(2)将豆腐馅做成四个大丸子，放入盘中，上屉蒸熟，将汤汁泌入炒勺内上火勾芡，淋香油浇在丸子上即可。', NULL, NULL, '花生油、酱油、淀粉、葱姜末、精盐。', '豆腐400克，胡萝卜25克，油菜叶50克，冬蘑，木耳各20克。');
INSERT INTO `recipe` VALUES (3872, 56, '素炒蒿茼', '(1)将苘蒿去叶及老梗，剩茼蒿秆，洗净切成3cm长的段。\r\n(2)炒勺上旺火，加底油，葱姜丝炝锅，下茼蒿秆，炒至快熟时加盐，再加味精搅匀即可。', NULL, NULL, '盐、植物油、味精、葱姜丝。', '茼蒿600克。');
INSERT INTO `recipe` VALUES (3873, 56, '奶油冬瓜条', '(1)把冬瓜去皮和瓤，洗净，切成1.5cm见方的长条，过烈油炸至发软时捞出。\r\n(2)炒勺打底油，加高汤，下冬瓜条，加盖焖烧二分钟，即放盐、味精并倒入牛奶，用湿淀粉勾成浓芡即可。', NULL, NULL, '淀粉、鲜奶、高汤、盐、植物油、味精。', '冬瓜750克．');
INSERT INTO `recipe` VALUES (3874, 56, '油焖茄子', '(1)茄子洗净，切成菱形块，下热油锅中炸至茄子酥软时捞出。\r\n(2)炒勺上火，加底油，用葱姜炝锅，加高汤、茄子，料酒、酱油、白糖、糖色，开锅后加盖，移小火焖煮一分钟，待汤汁快干时，上旺火，加味精和明油，待汤汁浓稠时即可出锅。', NULL, NULL, '葱花、姜未、味精、白糖，酱油、料酒，高汤、植物油、糖色。', '茄子600克。');
INSERT INTO `recipe` VALUES (3875, 56, '辣味茄丝', '(1)把茄子洗干净，切成5cm长的丝。\r\n(2)炒勺上旺火，加底油，用姜末炝锅，下茄丝，用手勺翻搅几下，加酱油、白糖，辣酱、高汤，烧2分钟，放入味精，待汤汁快干时，淋上明油即可。', NULL, NULL, '姜末、辣椒酱，味精、酱油，高汤、植物油，白糖．', '茄子600克。');
INSERT INTO `recipe` VALUES (3876, 56, '芙蓉菜花', '(1)把菜花劈成小朵如蘑菇一样大小，洗净．\r\n(2)把蛋清加少量清水及料酒、盐搅匀，放在汤盘中上锅蒸成蛋白糕取出。\r\n(3)炒勺上旺火，加底油，烹料酒高汤，加适量的盐，将菜花倒入汆熟透后捞出，加味精拌匀，码在蒸好的芙蓉盘内，浇上原汤即可。', NULL, NULL, '料酒、盐、大油、味精．', '菜花500克，蛋清80克。');
INSERT INTO `recipe` VALUES (3877, 56, '开洋烧菜心', '(1)把青菜剥去老叶，用刀削去菜根，保持菜心叶根部相连，洗干净。笋肉切成4cm的厚片。虾米用水泡发。\r\n(2)炒勺上旺火，打底油，油热后把菜心倒入翻炒五分钟后，加笋、开洋(虾米)和盐，再炒五分钟，放入料酒和高汤，移小火烧至菜烂时，加味精，翻勺后出锅．', NULL, NULL, '料酒、盐、味精、食油、高汤。', '青菜750克，笋肉20克，虾米10克。');
INSERT INTO `recipe` VALUES (3878, 56, '珊瑚白菜', '(1)将青红椒、干辣椒、冬笋、香菇切丝，在开水中氽透，用冷水过凉，葱、姜切丝。将上述原料加糖、醋、盐下锅，炒几下盛出备用。\r\n(2)洋白菜去老中，一劈四瓣，洗净后用开水烫透，用冷水过凉，放入盐、糖，醋、红油拌匀，装入盘中，上面盖上原来炒好的冬笋、香菇等即可。', NULL, NULL, '葱、姜、干辣椒、盐、白糖、醋。', '洋白菜600克(五棵)，青红椒500克，冬笋500克，香菇50克。');
INSERT INTO `recipe` VALUES (3879, 56, '扒奶汁白菜', '(1)菜心洗净，切成5cm长，1cm宽的条，放清水锅中煮烂，捞出备用。\r\n(2)炒勺上旺火，加底油，葱花炝锅，倒入料酒、高汤、盐烧开，放入白菜，锅移微火上，烧至透味，放入牛奶、味精，略翻炒几下，用淀粉勾芡，淋上明油，大翻勺后盛入汤盘中即可。', NULL, NULL, '料酒、盐、食油、葱花、高汤、味精、淀粉。', '大白菜心500克，牛奶100克。');
INSERT INTO `recipe` VALUES (3880, 56, '清炖猴头蘑', '(1)先用热水将猴头蘑浸泡1小时，撕去边缘杂质，洗去泥沙，切成约1cm厚的大片，再切成长3cm，宽2cm的长块，放在砂锅中间，笋切成两半，用刀轻轻拍松，放在砂锅中猴头蘑周围。姜切成片．\r\n(2)炒勺放在中火上，放入清水、口蘑汤、精盐、冰糖、料酒烧开后，倒入砂锅内，上小火烧开，撒去浮沫，放入热豆油、姜片，盖上盖儿，用微火慢炖约1小时，端离炉火(在此之前，应先取茶杯一只，放上瓜片、茶叶，用开水冲入杯中，立即沥去水，再冲入新的开水泡约二分钟)，揭开盖子，取出姜片，放味精、茶叶汁，用手勺轻轻调匀，加上盖丁即可上桌', NULL, NULL, '味精、精盐、生姜、冰糖、清水、料酒，豆油。', '干猴头蘑一只，鞭笋尖100克。');
INSERT INTO `recipe` VALUES (3881, 56, '萝卜丸子', '(1)将萝卜、洗净，切成大片，下开水锅烫透捞出，用清水洗几遍，控净水，剁成末，加调料、面粉拌匀成馅。\r\n(2)油锅上旺火，烧至五成热时，将萝卜馅挤成丸子—厂油锅炸至里熟外焦、金黄色时捞出装盘即可。吃时可撒上椒盐，', NULL, NULL, '精盐、味精、葱姜末、五香粉。', '红皮萝卜500克，面粉50克。');
INSERT INTO `recipe` VALUES (3882, 56, '土豆松', '将土豆洗净去皮，切成细毛丝，过烈油炸至酥脆、色泽金黄时捞出，拌上白糖(或精盐)装盘即可。', NULL, NULL, '白糖(或精盐)，植物油。', '土豆500克。');
INSERT INTO `recipe` VALUES (3883, 56, '菠菜松', '菠菜叶洗净，切成细丝，下热油锅中炸至酥脆时捞出，拌上白糖(或精盐)装盘即可。', NULL, NULL, '白糖(或精盐)、植物油。', '菠菜叶500克。');
INSERT INTO `recipe` VALUES (3884, 56, '糖拌蕃茄', '将西红柿洗净，用开水烫一遍，剥去外皮(或用消毒水消毒，不去皮)切成片，码在盘中，上面撒上白糖即可。', NULL, NULL, '白糖100克。', '西红柿(红的)500克。');
INSERT INTO `recipe` VALUES (3885, 56, '凉拌粉丝', '(1)将粉丝放入盆中，用开水泡软，捞出切成段，蒜苗洗净切成丝。\r\n(2)将粉丝、蒜苗一起放在盘中，加调料拌匀即可。', NULL, NULL, '葱丝、姜丝、辣油、香醋、味精、精盐。', '龙口粉丝(或其它细粉丝)200克，蒜苗50克。');
INSERT INTO `recipe` VALUES (3886, 56, '拌金针茹', '(1)将金针菇切去根，择去杂质，用清水洗净，下开水锅烫至开锅捞出，用清水洗凉，切成3cm长的段。香莱洗净，切成2．5cm长的段。\r\n(2)把金针菇、香莱、葱丝及其它调料放在一起拌匀即可。', NULL, NULL, '葱丝，酱油、精盐、味精、香醋、香油。', '金针菇500克，香菜10克。');
INSERT INTO `recipe` VALUES (3887, 56, '翡翠豆腐', '(1)将豆腐上屉蒸一下，去掉水分，切成3cm长的丝，用凉冰过凉，控净水，菠菜梗切成3cm长的段，下开水锅中烫至变色时捞出，用冷水过凉。\r\n(2)将豆腐和菠菜放在盘中，加调料拌匀即可。', NULL, NULL, '花椒油、精盐、味精。', '豆腐400克，菠菜梗200克。');
INSERT INTO `recipe` VALUES (3888, 56, '虾油拌豆腐', '(1)将豆腐切成1cm见方的丁，下开水锅烫一遍捞出琼凉，控净水分。\r\n(2)将豆腐丁放在盘中，加L．调料拌匀即可。', NULL, NULL, '虾油25克，葱花、精盐、味精。', '豆腐500克。');
INSERT INTO `recipe` VALUES (3889, 56, '清汤菜心', '(1)将嫩油菜剥去外面的老帮，削去根(但要保持菜心根部相连)，洗净。棵大的可将每棵片成两半。下开水锅中烫一遍捞出，用凉水洗一遍。\r\n(2)炒勺上旺火，加底油，用葱姜：呛锅，加清汤，待开锅后撇去浮沫，下菜心，加少许白糖，加盐，待开锅后用，小火煨至菜心软烂时，加味精、胡椒粉，淋香油即可盛在深盘中上桌。', NULL, NULL, '葱花、姜末、精盐、味精、白糖、胡椒粉、香油，清汤。', '嫩油菜750克(要在10棵以上)。');
INSERT INTO `recipe` VALUES (3890, 56, '香酥南瓜条', '(1)南瓜去皮去瓤洗净，切成条，加精盐、味精、花椒水拌匀，再加上面粉拌匀，使南瓜条外面包上一层面糊。\r\n(2)油锅上旺火，烧至七成热时，将南瓜条分批撒在油锅中(不能粘在一起成块)，待表面炸硬时，用筷子轻轻翻搅，炸至南瓜条酥脆成金黄色时捞出装盘。吃时可撒上些花椒盐。', NULL, NULL, '精盐，味精，五香粉，植物油。', '南瓜600克，面粉50克。');
INSERT INTO `recipe` VALUES (3891, 56, '香酥茄子条', '(1)茄子去皮洗净，切成条，加精盐、味精、花椒水拌匀，再加上面粉拌匀，使茄子条外面包上一层面糊。\r\n(2)油锅上旺火，烧至七成热时，将茄子条分批撒在油锅中(不能粘在一起成块)，待表面炸硬时，用筷子轻轻翻搅，炸至茄子条酥脆成金黄色时捞出装盘。吃时可撒上些花椒盐。', NULL, NULL, '精盐、味精、五香粉、植物油。', '茄子500克，面粉50克。');
INSERT INTO `recipe` VALUES (3892, 56, '香酥土豆丝', '(1)土豆去皮洗净，切成丝，加精盐、味精、花椒水拌匀，再加上面粉拌匀，使土豆丝外面包上一层面糊。\r\n(2)油锅上旺火，烧至七成热时，将土豆丝分批撒在油锅中(不能粘在一起成块)，待表面炸硬时，用筷子轻轻翻搅，炸至土豆丝酥脆成金黄色时捞出装盘。吃时可撒上些花椒盐。', NULL, NULL, '精盐、味精、花椒水、植物油。', '土豆400克，面粉50克。');
INSERT INTO `recipe` VALUES (3893, 56, '海米烧冬瓜', '(1)将冬瓜去皮、去瓤，切成片，海米用热水泡开。\r\n(2)炒勺上旺火，加猪油少许，用葱姜炝锅，下冬瓜翻炒几下，加盐及海米，炒至出水时，将泡海米的水倒进去，再加少许鲜汤，待开锅后用小火把冬瓜烧烂，加味精，用淀粉勾芡即可。', NULL, NULL, '葱花、姜末、味精、白糖、盐、淀粉、猪油。', '冬瓜750克，海米20克。');
INSERT INTO `recipe` VALUES (3894, 56, '白菜炖冻豆腐', '(1)将冻豆腐用凉水化开，挤去水份，切成3cm长、2cm宽、1cm厚的块，白菜洗净切成4cm长、1cm宽的条，葱、姜切成末。\r\n(2)将白菜、冻豆腐放开水锅中氽烫透捞出，控净水。\r\n(3)炒勺放火上，倒入少许油，葱姜炝锅，加鲜汤，放白菜，豆腐，用大火慢慢炖至白菜烂时，加味精、香油即可．', NULL, NULL, '味精、香油、精盐、葱、姜、鲜汤、豆油。', '白菜500克，冻豆腐200克。');
INSERT INTO `recipe` VALUES (3895, 56, '砂锅豆腐', '(1)豆腐上屉蒸出蜂窝状时取出晾凉，然后切成3cm长，1cm宽的条，冬笋切成薄片，冬菇去蒂，洗干净，葱切段、姜拍松备用。\r\n(2)取砂锅一个，洗干净，放入豆腐条、葱段、姜块，将冬菇、冬笋放在豆腐条上，然后添入黄豆芽汤、盐、油，在大火上烧开，改小火慢炖15分钟，加味精、香油即可。', NULL, NULL, '味精、黄豆芽汤、精盐，葱，姜，香油、豆油。', '豆腐700克，水发冬菇20克，冬笋20克，豌豆10克。');
INSERT INTO `recipe` VALUES (3896, 56, '烧素鸡', '(1)将素鸡切成3cm长，1cm厚的象眼片，放开水锅中烫透捞出。葱姜切成末。\r\n(2)炒勺加油，烧热后用葱、姜末：呛锅，煸炒出香味后加酱汕、料酒、味精、糖、鲜汤，倒入烫好的素鸡，用小火煨五分针，再用大火收汁，用水淀粉勾芡，淋香油即可．', NULL, NULL, '味精、料酒、糖、酱油、淀粉、鲜汤、葱、姜、香油、豆油。', '素鸡500克。');
INSERT INTO `recipe` VALUES (3897, 56, '溜豆腐', '(1)将豆腐切成1.5cm见方的块，放入开水中汆透，捞出控净水。葱姜切成末。\r\n(2)勺中放油，烧热后放葱姜末、花椒面炝锅，随即加入酱油，烧开后倒入豆腐块，加味精、糖迅速轻轻颠翻，煨约三分钟后，用淀粉勾芡，淋香油即可。', NULL, NULL, '酱油、味精、白糖、淀粉、香油、花椒面、葱、姜、豆油。', '豆腐600克。');
INSERT INTO `recipe` VALUES (3898, 56, '密汁山药墩', '(1)山药洗干净，去皮，切成3cm高的墩，码在盘子里，上屉蒸烂取出，码在另一个盘子里。青红丝洗净备用。\r\n(2)炒勺放火上，加凉水烧开，下白糖熬一会儿，用水淀粉勾芡，淋入香精，将烧好的汁浇在山药墩上，撤上青红丝即可。', NULL, NULL, '白糖、香精、淀粉。', '山药500克，青红丝10克。');
INSERT INTO `recipe` VALUES (3899, 56, '煮千丝', '(1)炒勺上旺火，放入凉水烧开，倒入豆腐干煮一下，捞出晾凉，片成薄片，再改成细丝。冬菇洗净，除去根、杂质，切成细丝。冬笋洗净，切成3cm长的细丝。姜切丝。\r\n(2)把切好的豆腐丝放在盆里，加入热水浸泡一会儿，用筷子将豆腐丝抖开，待晾凉后滗出水分，再加入开水，这样如此三次，以除去豆腥味。\r\n(3)炒勺放在火上，加豆油烧至七成热时，随下精盐炸锅，再放入豆芽汤，烧开后放入干豆腐丝，盖上盖，煮十分钟，将冬菇，冬笋放入烧开后，加入姜丝，淋上香油即可装碗。', NULL, NULL, '精盐、味精、生姜、香油、豆油、黄豆芽汤。', '豆腐干500克，冬菇50克，冬笋50克。');
INSERT INTO `recipe` VALUES (3900, 56, '烧二冬', '(1)将冬菇摘去硬根，除去杂质洗干净，挤去水份备用，冬笋切成4cm长、2cm宽片，葱姜切末。\r\n(2)油锅上旺火，烧至六成热时放入冬笋氽炸一下捞出。冬菇用开水氽烫一下捞出。\r\n(3)炒勺上旺火，加底油，用葱姜炝锅，加酱油、料酒、味精、白糖，倒入冬菇、冬笋煸炒，再加入鲜汤，烧开后用水淀粉勾芡，淋香油即可。', NULL, NULL, '酱油、味精、料酒，白糖、淀粉、葱，姜、香油、鲜汤，豆油。', '水发冬菇，冬笋400克．');
INSERT INTO `recipe` VALUES (3901, 56, '醋溜白菜', '(1)白菜去叶洗净，切成3．5cm长、1．5cm宽的条，葱姜切末。\r\n(2)油锅上旺火，油烧热后，将白菜条下油锅炸一下捞出，炒勺上旺火，加底油，用葱姜炝锅，下白菜煸炒几下，加调料烧开，用湿淀粉勾芡，淋入香油即可．', NULL, NULL, '酱油、味精、白糖、香醋、精盐、香油、葱、姜，淀粉、豆油。', '白菜750克。');
INSERT INTO `recipe` VALUES (3902, 56, '脆皮萝卜', '(1)萝卜洗净去皮，切成细丝；下开水锅汆烫至开锅捞出，用凉水浸泡两小时，捞出后控净水，加味精、盐、香油拌匀备用。\r\n(2)面肥加水搅开，加入适量小苏打，再加面粉搅成糊，一半抹在刷过油的盘子里，上面撒上萝卜丝，另一半盖在萝卜丝上，推入热油锅中炸成两面金黄色时捞出，放在盘中改刀成块即可。', NULL, NULL, '味精、精盐、香油、豆油、小苏打。', '大红辣萝卜500克，面粉20克，面肥(酵面)100克。');
INSERT INTO `recipe` VALUES (3903, 56, '炝菜花', '(1)将菜花洗净，切成小块，下开水锅烫熟捞出，装在盘中。\r\n(2)炒勺上旺火，加花椒油，用葱花、蒜片炝锅浇在菜花上，加盐、味精，醋拌匀即可。', NULL, NULL, '味精、精盐、花椒油、香醋，，葱花、蒜片。', '嫩菜花500克。');
INSERT INTO `recipe` VALUES (3904, 56, '炝青椒', '(1)将青椒去把、去籽洗净，切成块或丝。\r\n(2)大勺上旺火，添入水烧开，将青椒下锅氽烫至变色捞出放入盆里，用花椒油、葱花炝锅后浇在青椒上，加上调料拌匀即可。', NULL, NULL, '味精、精盐、花椒油、葱姜丝。', '青椒500克。');
INSERT INTO `recipe` VALUES (3905, 56, '拌茄泥', '将茄子洗净，上屉蒸烂取出，放入盆中，加上香菜段及调料搅拌均匀即可。', NULL, NULL, '味精、蒜泥、精盐、香油。', '嫩茄子100克，香菜段10克。');
INSERT INTO `recipe` VALUES (3906, 56, '拔丝香蕉', '(1)将香蕉去皮，切成块，鸡蛋打在碗中，加面粉搅匀成蛋粉糊。\r\n(2)油锅上旺火，烧至5成热时，将香蕉逐个沾上干面粉，挂上蛋糊下油锅炸至金黄色捞出。\r\n(3)炒锅洗净上旺火，加少许底油，放入白糖快速搅炒，待糖快化时，滴几滴开水，待炒至白糖由翻大泡到翻小泡，由粘稠至突然稀软时(或颜色微呈黄色时)，将刚出锅的香蕉倒入撒上青红丝，炒勺离火，颠翻均匀即可。或先将香蕉摆盘，将炒好的糖浆浇上即可。', NULL, NULL, '白糖、植物油。', '香蕉500克，鸡蛋50克，面粉20克。');
INSERT INTO `recipe` VALUES (3907, 56, '拔丝山药', '(1)将山药去皮，切成西瓜块，鸡蛋打在碗中，加面粉搅匀成蛋粉糊。\r\n(2)油锅上旺火，烧至5成热时，将山药逐个沾上干面粉，挂上蛋糊下油锅炸至金黄色捞出。\r\n(3)炒锅洗净上旺火，加少许底油，放入白糖快速搅炒，待糖快化时，滴几滴开水，待炒至白糖由翻大泡到翻小泡，由粘稠至突然稀软时(或颜色微呈黄色时)，将刚出锅的山药倒入撒上青红丝，炒勺离火，颠翻均匀即可。或先将山药摆盘，将炒好的糖浆浇上即可。', NULL, NULL, '白糖100克，植物油，青红丝。', '山药(或土豆，地瓜等)300克。');
INSERT INTO `recipe` VALUES (3908, 56, '炝酸辣黄瓜', '(1)黄瓜洗净切成5cm长的段，用旋刀法片成卷，切成1.5cm宽的片。\r\n(2)炒勺上旺火，加辣油，将黄瓜上锅炝一下倒入盆内，加调料拌匀，放在冰箱里冷藏2—3小时即可装盘。', NULL, NULL, '精盐、味精、白糖、辣椒油、香醋。', '嫩黄瓜500克。');
INSERT INTO `recipe` VALUES (3909, 56, '拔丝苹果', '(1)将苹果去皮，切成西瓜块，鸡蛋打在碗中，加面粉搅匀成蛋粉糊。\r\n(2)油锅上旺火，烧至5成热时，将苹果逐个沾上干面粉，挂上蛋糊下油锅炸至金黄色捞出。\r\n(3)炒锅洗净上旺火，加少许底油，放入白糖快速搅炒，待糖快化时，滴几滴开水，待炒至白糖由翻大泡到翻小泡，由粘稠至突然稀软时(或颜色微呈黄色时)，将刚出锅的苹果倒入撒上青红丝，炒勺离火，颠翻均匀即可。或先将苹果摆盘，将炒好的糖浆浇上即可。\r\n注：苹果下糖锅前必须是刚出油锅。用此法可拔香蕉、桔子等。', NULL, NULL, '白糖150克，植物油。', '苹果400克，鸡蛋50克，面粉10克，青红丝少量。');
INSERT INTO `recipe` VALUES (3910, 56, '锅溻豆腐', '(1)将豆腐切成长3cm，宽2cm、厚1cm的块，撒上少许盐腌上，鸡蛋打在碗里搅开，绿菜叶洗净切块。\r\n(2)油锅上旺火，加上净猪油，烧至四成热时，将豆腐块逐个沾上千面粉，拖上鸡蛋糊，下油锅炸至金黄色捞出。炒锅上火，加底油，用葱姜炝锅，加上鲜汤及调料，下豆腐，加盖用小火焖至汤汁干时即可出锅摆入盘中。\r\n(3)青菜炒熟围在豆腐周围即可。', NULL, NULL, '葱末、姜末、精盐、料酒、味精、酱油、熟猪油、鲜汤。', '豆腐400克，鸡蛋50克，面粉10克，绿菜叶100克。');
INSERT INTO `recipe` VALUES (3911, 56, '素火腿', '(1)炒锅置火上，放入酱油、麻油、白糖、味精、料酒烧开，放入撕碎的豆腐皮后，再将锅端离火，稍冷却，用手搓揉豆腐皮，使汤汁全部吸入，并淋麻油拌和。\r\n(2)取洁净白布一块，将豆腐衣分别包入卷成圆筒状，用绳扎紧，上笼蒸透取出。\r\n(3)待豆腐衣卷冷却后，去掉包布顺长剖两半，切薄片，装盘、淋上麻油即成。', NULL, NULL, '酱油、味精、白糖、料酒，麻油。', '豆腐皮500克。');
INSERT INTO `recipe` VALUES (3912, 56, '油焖笋', '(1)将笋剥壳去老根洗净，切成粗条备用。\r\n(2)炒锅放油烧热，放入葱、姜、笋条一起煸炒后，再加酱油、桂皮、八角翻炒一下，然后焖烧至汤汁收干，完全是酱油和油时起锅装盆，淋上麻油即成。', NULL, NULL, '花生泊、精盐，酱油、味精、麻油、桂皮、八角、葱、姜。', '冬笋(或春笋)600克。');
INSERT INTO `recipe` VALUES (3913, 56, '五香豆腐', '(1)将豆腐切成方块，放入油锅中炸至金黄色捞出。\r\n(2)将八角、花椒、陈皮、葱，姜用纱布包好，放入锅，加适量水烧开，再放白糖、酱汕、料酒和炸好的豆腐卤制，待豆腐入味上色，收干汤汁，起锅装盆，淋上麻油即成。', NULL, NULL, '花生油、酱油、料酒、白糖、麻油、八角、花椒、陈皮、葱、姜。', '豆腐600克。');
INSERT INTO `recipe` VALUES (3914, 56, '炝冬笋', '鲜冬笋肉切片，放入开水中烫透捞出，酱油、味精、鲜汤烧\r\n成卤汁，浇在冬笋片上，撒上姜末、丁香萝卜末，淋上麻油即成。', NULL, NULL, '酱油、味精、麻油、鲜笋汤、丁香萝卜末、姜末。', '鲜冬笋肉500克。');
INSERT INTO `recipe` VALUES (3915, 56, '凉拌海带', '将海带用冷水泡发后，洗净切成细丝，放入开水中汆烫至开锅，捞出后沥干水分，放入盆中，加调料拌匀即可。', NULL, NULL, '酱油、味精、白糖、香醋、麻油、红辣椒丝、姜丝。', '水发海带500克。');
INSERT INTO `recipe` VALUES (3916, 56, '酸辣白菜', '(1)将嫩白菜剥去老叶，削去老根、洗净后切粗条，撒上精盐，略搓捏，待出水即腌入盆内，上面压一盆子，盖5—6小时后取出，绞干盐水，放入另一盆中。\r\n(2)炒锅置小火，放麻油，投入花椒、辣椒炸出香味后，捞出花椒、辣椒，将炸油浇在白菜上。\r\n(3)将白醋、白糖放在盆内调和后，倒入白菜上，加盖浸渍4小时即成。', NULL, NULL, '精盐、白糖、白菜、干辣椒、麻油、花椒、嫩姜丝。', '嫩白菜700克。');
INSERT INTO `recipe` VALUES (3917, 56, '萝卜卷', '(1)盆内放开水，加精盐调成咸水，冷却待用；泡辣椒去籽去蒂，洗净切丝。\r\n(2)将萝卜洗净去皮去蒂，加工成圆柱形状，用旋刀法，将萝卜片成薄薄的长条片，放入咸水中浸泡至发软。\r\n(3)取出泡制好的萝卜长片一条，放入两根辣椒丝，卷成拇指粗的萝卜卷，按此法逐个做好，整齐地分放在盆内，加入白醋、白糖腌制12小时(夏天调料偏多些，时间相对缩短，冬天相对延长)左右，即可食用。', NULL, NULL, '精盐、白醋、白糖、泡辣椒。', '辣萝卜600克。');
INSERT INTO `recipe` VALUES (3918, 56, '凉拌粉皮', '(1)将粉皮切成粗条，黄瓜去皮去籽，洗净切片，红辣椒去蒂去籽，洗净切丝，香菜洗净，葱、大蒜均切成末待用。\r\n(2)锅里放水烧开后，先放入粉皮烫过，捞出过凉，再放入盆内，加酱油，精盐，香醋、葱、蒜、红辣椒、芥末、味精，辣椒油、麻油拌匀即成。', NULL, NULL, '精盐、酱油、味精、香醋、麻油、辣椒油、芥末、葱、蒜。', '鲜粉皮500克，嫩黄瓜100克，红辣椒50克，海米、香菜各10克。');
INSERT INTO `recipe` VALUES (3919, 56, '八宝菠菜', '(1)菠菜去老叶洗净，切成段，放入开水中汆一下捞出，撒些精盐散热。\r\n(2)将鸡蛋的蛋黄和蛋清分磕在两个盆内，分别加入精盐，味精搅匀，上屉蒸成蛋白糕和蛋黄糕，取出晾凉，分别切成细条；熟火腿切成与蛋糕相同的条。海米用开水泡发后洗净待用，熟冬笋’叨细条状，用开水汆一下后，捞出置锅中，加精盐、水、味精、胡椒粉和葱段、姜片烧至入味，离火晾凉，胡萝I、去皮，洗净切细条，放入开水中，开锅后捞出，与其它原料加调料拌匀即可．', NULL, NULL, '精盐、味精、胡椒粉、麻油、花椒油、葱、姜。', '菠菜400克，熟火腿20克，鸡蛋50克，海米20克，熟冬笋50克，水发冬菇50克，胡萝卜50克。');
INSERT INTO `recipe` VALUES (3920, 56, '小葱拌豆腐', '豆腐切成小方丁，放入开水锅氽一下捞出，沥净水分，放入盆内，小葱洗净切细末，放入豆腐盆内，加精盐、味精、麻油，拌匀撒上胡椒粉即成。', NULL, NULL, '精盐、味精、麻油。', '豆腐500克，小葱50克。');
INSERT INTO `recipe` VALUES (3921, 56, '拌千丝', '(1)先将豆腐干批薄片，再切细丝。\r\n(2)将切好的千丝放入盆内，用开水浸泡抖散，待凉后，滗去原水，再用开水浸泡，如此三次以除黄苦味，并使千丝松软。(3)使用时再用开水烫过，滗去水分，用酱油、米醋、白糖、味精、麻油放入另一盆内，做成调料汁，浇在千丝上，放上姜丝、香菜末，京葱丝拌匀即成。', NULL, NULL, '精盐，味精，酱油、白糖，香醋，麻油、香菜末，京葱丝、姜丝。', '豆腐干400克，香菜50克。');
INSERT INTO `recipe` VALUES (3922, 56, '凉拌土豆丝', '(1)将土豆去皮洗净，切成细丝(越细越好)，浸入凉水内待用，青红椒，葱、姜均切细丝。\r\n(2)锅烧开水，放入配料丝，土豆丝汆一下捞出，用冷水过凉后，放入盆内，加味精，精盐，白醋，麻油拌匀即可。', NULL, NULL, '精盐，味精、麻油，白醋、青红柿子椒，葱、姜。', '土豆500克。');
INSERT INTO `recipe` VALUES (3923, 56, '凉拌茄子', '(1)将茄子去蒂洗净，上笼蒸烂，取出，放入盆内，冷却后，用’手撕成条后，再切段待用。\r\n(2)姜、蒜均剁成泥，放入盆内，加麻油，辣油、味精、香醋调成汁后，淋在茄子上即成。', NULL, NULL, '酱油，味精、香醋、辣油、麻油、姜末、大蒜头。', '嫩茄子600克。');
INSERT INTO `recipe` VALUES (3924, 56, '凉白菜', '白菜去根去老叶洗净切丝，放入开水中烫后捞出，加精盐拌匀，再放入酱油，味精，辣椒油，花椒面，白糖＼麻油拌合，撒上葱花即成。', NULL, NULL, '精盐。味精，酱油、白糖、花椒而，辣椒油，麻油，葱丝，姜丝。', '白菜心700克。');
INSERT INTO `recipe` VALUES (3925, 56, '蒜泥扁豆', '(1)将扁豆的两边筋和两头摘去洗净，姜切成细末待用。\r\n(2)锅放水烧热，放入扁豆烫熟后，捞出沥去水分，放在盆内，趁热加精盐，待晾凉后加味精、蒜泥、麻油拌匀即成。', NULL, NULL, '精盐、味精、麻油、蒜泥。', '嫩扁豆500克。');
INSERT INTO `recipe` VALUES (3926, 56, '蒜泥黄瓜', '(1)将黄瓜刨皮去籽洗净，切粗条，加精盐拌腌—下，捞出沥水，放入盆内。\r\n(2)将熟花生油、麻油、姜末、白糖、味精放入黄瓜盆中搅拌至入味，过10分钟后，即可食用。', NULL, NULL, '熟花生油、精盐、味精、香醋、麻油、蒜泥。', '鲜嫩黄瓜700克。');
INSERT INTO `recipe` VALUES (3927, 56, '海米拌芹莱', '将芹菜去根去老叶切成段，放入开水中烫透，捞出沥去水分，放入盆内。将海米泡开切末，撒在芹菜上，加精盐、味精、香醋、姜末拌匀，淋上麻油即可。', NULL, NULL, '精盐、味精、香醋、麻油，姜末。', '芹菜600克，海米25克。');
INSERT INTO `recipe` VALUES (3928, 56, '虾籽炝芹菜', '(1)芹菜去根、去叶洗净切成段，放入开水中烫透捞出，用冷水过凉后，放入盆内，放上姜末：虾籽用温水泡发后洗净，沥去水分待用。\r\n(2)炒锅放油烧热，放入花椒粒炸透捞出，再放入虾籽稍炸，浇在芹菜上，略焖一下，然后加精盐、味精拌匀即成。', NULL, NULL, '花生油、精盐、味精、花椒粒、姜末。', '芹菜600克，虾籽15克。');
INSERT INTO `recipe` VALUES (3929, 56, '三丝芹菜', '(1)芹菜去根和老叶，洗净切段，放入开水中烫一下，捞出过凉，豆腐干、水发香菇均切细丝待用。\r\n(2)将芹菜，香菇、豆腐干同放一盆内，加精盐、味精、白糖、料酒、香醋、姜丝一起拌匀，淋上麻油即成．', NULL, NULL, '精盐、味精、料酒、白糖、香醋、麻油、姜末。', '芹菜300克，五香豆腐干200克，水发香菇50克。');
INSERT INTO `recipe` VALUES (3930, 56, '芹菜拌香千', '(1)豆腐干切细丝，芹菜洗净切段。将它们分别放入开水中烫一下捞出，沥去水分。\r\n(2)将豆腐干和芹菜放入盆内，加精盐、味精、麻油、姜丝拌匀即成。', NULL, NULL, '精盐、味精、麻油、姜丝。', '豆腐干200克，芹菜300克。');
INSERT INTO `recipe` VALUES (3931, 56, '糖醋嫩藕', '将嫩藕洗净刨皮，切成薄片，放入盆内用开水烫至发软，然后沥净水分，再加白糖，香醋，精盐、姜末腌渍入味即可。', NULL, NULL, '白糖、香醋、精盐，姜末。', '嫩藕600克。');
INSERT INTO `recipe` VALUES (3932, 56, '青菜粉丝', '(1)青菜去根去老叶洗净，切成约3cm长的段，粉丝用开水泡软后切成段。\r\n(2)炒锅放底油，烧至七、八成热时，用葱、姜炝锅，下青莱翻炒几下，下入泡好的粉丝，加精盐，炒至青菜变色，达到八成熟时加味精翻炒几下即可出锅。', NULL, NULL, '花生油、精盐、味精、猪油、葱花、姜末。', '青菜500克，粉丝50克。');
INSERT INTO `recipe` VALUES (3933, 56, '瓢儿莱豆腐', '(1)瓢儿菜去根去老叶洗净，豆腐切片，放入开水中烫一下，捞出过凉备用。\r\n(2)炒锅放油烧至七成热时，放入瓢儿菜，加精盐翻炒几下，再放入豆腐烧透，加味精，淋上明油即可出锅装盆。', NULL, NULL, '猪油、精盐、味精。', '瓢儿菜400克，豆腐100克。');
INSERT INTO `recipe` VALUES (3934, 56, '糖酷藕丝', '(1)鲜藕刨皮，洗净切成藕丝待用，大葱洗净切段．\r\n(2)炒锅放油烧热，放入葱段煸出香味后，再放入藕丝翻炒至，八成熟，放入白糖、香醋再翻炒数次，见熟时用淀粉勾芡，淋上明油，出锅装盆即可。', NULL, NULL, '花生油、精盐、白糖，香醋，大葱，淀粉．', '藕700克。');
INSERT INTO `recipe` VALUES (3935, 56, '红烧芋头', '(1)将芋头去皮，用清水洗净，切成滚刀块，先在水里煮八成熟时捞出待用，葱切段、姜切末。\r\n(2)炒锅放油烧热，将葱段、姜末入锅煸炒出香味后，放入芋头块翻炒数下，再放酱油、精盐拌炒，见芋头变黄色时，注入肉汤焖烧，待芋头酥烂加味精，收干水分，淋上明油，．出锅装盆即成。', NULL, NULL, '花生油、酱油、精盐、味精、肉汤、葱、姜。', '芋头700克。');
INSERT INTO `recipe` VALUES (3936, 56, '素炒三丝', '(1)把千张切成丝后，用开水烫一下，捞出沥干水分，青椒、茭白切成细丝。\r\n(2)炒锅用旺火烧热放油，先将青椒丝和茭白丝炒一下，然后放入千张丝，翻炒数次，加酱油、料酒，鲜汤、精盐、味精，用湿淀粉勾薄芡出锅即成。', NULL, NULL, '花生油，酱油、精盐、料酒、味精、湿淀粉，鲜汤(或水)。', '千张(豆腐皮)150克，青椒50克，茭白300克．');
INSERT INTO `recipe` VALUES (3937, 56, '双冬焖菜心', '(1)将小油菜去老根，剥去老叶，剖成两片5冬菇去蒂头，洗净，切成两片，笋切成长片，分别将油菜心、笋片下开水锅烫一下捞出，沥干水分。\r\n(2)炒锅放油烧至六成热时，放入油菜心，冬菇，笋片煸炒几下后，再加入姜汁和鲜汤烧至汤汁收浓，放味精，勾薄芡，淋上鸡油即可出锅装盆，', NULL, NULL, '花生油、精盐，味精、淀粉、姜汁、鸡油。', '小油菜600克，水发冬菇50克，净冬笋100克。');
INSERT INTO `recipe` VALUES (3938, 56, '金鱼菜心', '(1)将油冬菜心洗净，切成金负形状，用开水烫一下捞出，沥干水分，加精盐，味精拌匀，把菜心一颗颗放在盘内，放时菜叶朝外，菜头朝里，互相间距—样。\r\n(2)将虾仁洗净，沥清水分，把虾仁剁成茸，放入盆内，鸡肉、荸荠斩成末和虾茸放一起，加料酒、精盐、味精、蛋清、淀粉拌匀后，分成两份，用其中一份挤成小丸子，放在菜头上，丁香萝卜洗净切粒，做金鱼的眼眼，再将冬菇切成丝，做金鱼身子，成金鱼形状。\r\n(3)将另一份虾茸放入酒盅里，豌豆一剖两半嵌在虾茸上，上笼蒸熟取出，摆在盆中央，将熟火腿切成荷花瓣，做成荷花，四周围上金鱼菜', NULL, NULL, '精盐、味精、料酒、淀粉、鸡汤、鸡油。', '小油菜心10棵，荸荠、蛋清、水发冬菇、熟火腿、虾肉、鸡脯肉、辣萝卜各适量。');
INSERT INTO `recipe` VALUES (3939, 56, '盐水大青豆', '将青豆去壳，用清水洗净，放入清水锅中，加上葱结、姜片、花椒，上火煮几分钟，尔后加精盐，煮至豆粒熟脆时加味精即可。装盘时浇上原汁即可．', NULL, NULL, '精盐、味精、姜片、葱结、花椒，', '新鲜大青豆600克。');
INSERT INTO `recipe` VALUES (3940, 56, '盐水毛豆', '(1)将毛豆夹剪去两尖，洗净。\r\n(2)煮锅加水置大火上，把毛豆放入锅内，煮至半熟，加精盐略翻拌几下，直到煮至豆粒酥烂时即可。吃时蘸麻油、酱油。', NULL, NULL, '精盐，麻油、酱油。', '鲜毛豆600克。');
INSERT INTO `recipe` VALUES (3941, 56, '烧四季豆', '(1)将四季豆洗净去筋，摘去蒂头，切成长约2—3cm的段。\r\n(2)把油锅烧热放油，待油烧至七、八成热时，倒入四季豆炒至青绿时，放入酱油、精盐，然后再放入适量的水，烧至半熟时，放味精翻炒几下盛起。', NULL, '炒时须先放盐，夏季四季豆不用烫，秋季下开水锅汆—下为宜，这样才能使四季豆碧绿而入味。', '花生油、精盐、酱油、味精、姜。', '四季豆600克。');
INSERT INTO `recipe` VALUES (3942, 56, '椒麻蚕豆', '(1)先将蚕豆去掉上面的黑嘴，洗净后，放进开水锅内煮熟捞起，拌上精盐。\r\n(2)炒锅放油烧至六成热时，放入蚕豆炒约1分钟，加入花椒末，葱末，姜末、泡辣椒末翻炒几下，出锅装盆，淋上麻油即成。\r\n此莱色绿，鲜香耐嚼，有麻香味，饮酒佐餐均宜，是时令莱。', NULL, NULL, '花生油、精盐、麻油、葱末、姜末、花椒末，泡辣椒末。', '鲜蚕豆500克。');
INSERT INTO `recipe` VALUES (3943, 56, '炒苋菜', '(1)将苋菜去根，去老叶、老茎，洗净沥水，切寸段。', NULL, NULL, '花生油、精盐、味精、葱丝、姜丝、蒜丝。', '苋菜700克。');
INSERT INTO `recipe` VALUES (3944, 56, '炒菠菜', '(1)将菠菜去老叶及根须(不要去根)，洗净，沥干水分，切成3cm的段。\r\n(2)炒锅放油旺火烧热，待葱煸出香味后，放菠菜翻炒，菜软近熟时放盐、味精翻炒均匀，淋上明油即可。', NULL, '大锅炒时，须旺火快炒，一般炒至八成熟即可，现炒现吃，炒时不要加水、加盖．', '花生油、精盐、味精，葱丝，', '菠菜700克。');
INSERT INTO `recipe` VALUES (3945, 56, '炒空心菜', '(1)空心菜去根去老叶，洗净切段，大蒜头去皮洗净拍碎切末待用。\r\n(2)烧锅放油烧至七至八成热时，放入蒜末和精盐煸出香味，再放入空心菜翻炒几下，加味精炒拌均匀即成。', NULL, NULL, '猪油、精盐、味精。', '空心菜(或叫“钢管菜’，学名为蕹菜)700克，大头蒜30克。');
INSERT INTO `recipe` VALUES (3946, 56, '拌菠菜泥', '(1)将菠菜去根，去黄叶洗净，放入开水锅内烫一下捞出，挤去水分，切成细末，再将菠菜末包入干净纱布挤净菜汁，放入盆内；五香豆腐干和熟笋也切成细末和菠菜放在一起，加入精盐、味精、姜末、麻油拌匀即成。\r\n此菜绿中带白，松中有脆，咸入口，回味甜鲜。', NULL, NULL, '精盐、味精、麻油、姜末。', '菠菜500克，五香豆腐干100克，熟笋10克。');
INSERT INTO `recipe` VALUES (3947, 56, '醋溜白菜', '(1)将白菜洗净切成丝，葱、姜均切成细丝。', NULL, NULL, '花生油，盐，白糖，香醋，味精，辣油，淀粉、花椒、葱、姜。', '白菜600克。');
INSERT INTO `recipe` VALUES (3948, 56, '白菜煎豆腐', '(1)白菜洗净切成片，用开水烫至七，八成熟，用凉水投一下，豆腐切成长3．3厘米的片，葱，姜均切成丝。\r\n(2)炒锅放油烧热，将豆腐煎成两面金黄捞出，原锅留适量油，放入葱姜丝，花椒面，白莱片翻炒几下，再放入酱油，精盐和煎好的豆腐烧透，然后放味精，勾薄芡出锅装盆即成。', NULL, NULL, '花生油、酱油、味精、精盐、淀粉、葱、姜、花椒面。', '白菜300克，豆腐200克。');
INSERT INTO `recipe` VALUES (3949, 56, '平菇白菜', '(1)白菜洗净切片，用开水烫至七、八成熟，用凉水投一下，鲜平菇洗净切成片。\r\n(2)炒锅放油烧热，放入姜丝、葱丝、花椒面煸出香味后，再放平菇、白菜翻炒，加精盐，菜熟后放味精、香醋，勾薄芡出锅，淋上明油即成。', NULL, '炒时须旺火快炒．', '猪油，精盐，味精、香醋、淀粉、花椒面、葱、姜。', '白菜400克，平菇200克．');
INSERT INTO `recipe` VALUES (3950, 56, '白菜油豆腐', '(1)白菜洗净切成片，油豆腐一剖两开，葱、姜切成丝。\r\n(2)炒锅放油烧热，放入姜丝、葱丝、白菜翻炒几下，加肉汤、精盐、油豆腐，待白菜炖熟，放味精出锅即成。', NULL, NULL, '猪油、精盐，味精、葱，姜、肉汤，', '大白菜600克，油豆腐100克。');
INSERT INTO `recipe` VALUES (3951, 56, '白菜粉条', '(1)粉丝用水泡软切段，白菜洗净切成丝，用开水烫七、八成熟，用凉水投一下。\r\n(2)炒锅放油烧热，放入葱姜丝、花椒面、酱油、精盐和粉丝，再加适量的水，待粉丝炒熟，水熬干放白菜翻炒几下，放味精、香醋出锅装盆即可。', NULL, NULL, '花生油、酱油、精盐、味精、香醋、花椒面、葱、姜。', '白菜500克，干粉丝100克。');
INSERT INTO `recipe` VALUES (3952, 56, '白菜虾米', '(1)白菜洗净切片，虾米泡发好备用，葱、姜均切丝。\r\n(2)炒锅放油烧热，放入葱姜丝、花椒面、白菜片翻炒至七\r\n成热时，再放精盐、香醋，莱熟后放味精，用淀粉勾芡出锅，淋上麻油即成。', NULL, '炒时要菜梗先下锅煽炒，再放莱叶，油锅要热，速度要快。也可不放花椒面制作。', '猪油、精盐、味精、香醋、花椒面、葱、姜。', '白菜700克，虾米15克。');
INSERT INTO `recipe` VALUES (3953, 56, '酸辣黄瓜', '(1)将黄瓜洗净去瓤，切成片，加盐腌1小时左右，再轻轻挤净水分盛盆内，辣椒洗净切丝放在黄瓜上。\r\n(2)锅内放油烧热，放入花椒炸出香味后，立即浇在辣椒上(浇时用笊篱将花椒滗出)，再加姜丝、味精、白糖、香醋、麻油拌匀，用盖子盖压十分钟后即可装盘。\r\n此菜黄瓜翠绿，酸、甜、麻辣味，吃时皮极爽口。', NULL, NULL, '花生油、精盐、白糖、香醋、味精、麻油、泡辣椒、花椒、姜丝。', '嫩黄瓜600克。');
INSERT INTO `recipe` VALUES (3954, 56, '炒黄瓜', '(1)韭菜拣去黄叶，洗净，沥干水分，切成段，黄瓜刨皮去瓤，切成薄片。\r\n(2)炒锅放油大火烧热后，将韭菜下锅炒至三成熟起锅；原锅放油烧到极热，放黄瓜片和姜末下锅急炒，至半熟加入韭菜、酱油、精盐、味精、香醋炒几下，勾薄芡，淋上明油出锅即成。', NULL, NULL, '花生油、酱油、精盐、味精、香醋、淀粉、姜末。', '黄瓜600克，韭菜30克。');
INSERT INTO `recipe` VALUES (3955, 56, '炒南瓜丝', '(1)嫩南瓜洗净后，去瓜瓤，切成50厘米左右长的细丝，用盐腌上，炒时洗净，沥去水分，红辣椒同样切成细丝。\r\n(2)炒锅放油烧热，先将葱姜丝、红辣椒丝煸出香味后，倒入南瓜丝再加酱油、精盐、味精、香醋翻炒均匀、淋上明油装盆即可。', NULL, NULL, '花生油，酱油，精盐、味精、香醋、葱姜丝、花椒。', '嫩南瓜500克，红辣椒25克。');
INSERT INTO `recipe` VALUES (3956, 56, '红乳冬瓜', '(1)冬瓜去皮，掏净瓤，洗净控干水分，切成大方块，再用刀剞成樱桃花纹，在每块冬瓜上都擦上酱油。\r\n(2)炒锅放油加热后，放冬瓜炸至金黄色捞出，原锅留适量的油，放入姜片，葱段、红腐乳煸炒出香味后，随即倒入冬瓜，加精盐、白糖，待冬瓜烧熟后勾薄芡，加味精出锅即可。', NULL, NULL, '花生油、精盐、酱油、红腐乳、味精、白糖、葱段，淀粉。', '冬瓜750克。');
INSERT INTO `recipe` VALUES (3957, 56, '炒冬瓜片', '(1)将冬瓜去皮，挖去瓤，洗净切成薄片，大葱、生姜、红辣椒分别切成丝。\r\n(2)冬瓜片入盆内用盐腌20分钟左右、洗净、沥去水分．\r\n(3)炒锅放油烧热，将辣椒和大葱、姜丝急炒数下，放入冬瓜片，翻炒至八成熟时，放点香醋、麻油即可出锅装盆。', NULL, NULL, '花生油、酱油、精盐、味精、香醋、淀粉、麻油、生姜、大葱。', '冬瓜700克，红辣椒5克。');
INSERT INTO `recipe` VALUES (3958, 56, '虾籽茄泥', '(1)将茄子去蒂，一剖四开，上笼蒸熟后出笼，捣成茄泥，将葱、姜、蒜切细末，放入盆内，下茄泥、精盐、酱油、味精拌匀。\r\n(2)炒锅放辣油烧热，下入虾籽煸炒一下，倒入茄泥，拌匀即成。', NULL, NULL, '精盐、酱油、味精、辣油、葱末、姜末、大蒜末．', '嫩茄子600克，虾籽5克。');
INSERT INTO `recipe` VALUES (3959, 56, '锅烧茄子', '(1)茄子洗净沥干水分，去皮切成厚片，用精盐腌制20分钟左右，洗净，沥水，将鸡蛋打入盆里，加淀粉、精盐、味精、面粉打’成蛋糊拌入茄子里。\r\n(2)炒锅放油烧至六成热时，茄子分批下油锅炸成金黄色，吃时再炸一次捞出装盆。\r\n(3)香菜洗净切细，围在茄子四周，吃时加甜面酱。', NULL, NULL, '花生油、精盐、味精、甜面酱、面粉、淀粉、京葱丝、花椒、香菜。', '鲜茄子600克，蛋清20克。');
INSERT INTO `recipe` VALUES (3960, 56, '烧萝卜块', '(1)萝卜洗净去皮切小块，用开水烫七、八成熟，用凉水投一下，沥干水分，用蛋清、淀粉、面粉抓糊，用油炸至金黄色。\r\n(2)炒锅放油烧热，放花椒面、葱末、姜末、蒜末、香醋、酱油、油、精盐和水，锅放味精，用淀粉勾薄芡，放萝卜翻炒几下出锅即可。', NULL, NULL, '花生油、酱油、精盐、味精、花椒面、面粉、淀粉、葱、姜、蒜。', '萝卜500克，蛋清20克。');
INSERT INTO `recipe` VALUES (3961, 56, '回锅萝卜', '(1)将萝卜洗净，切成箭头片、条片、方片均可，用开水煮熟，冷水浸凉，沥干水分；豆腐干切薄片，下开水锅烫一下捞起过凉，泡辣椒洗净叨小片，葱、姜，蒜均切成丝。\r\n(2)用精盐、面酱、酱油、白糖胡椒配成调料汁备用。\r\n(3)炒锅放油烧热，将葱、姜，蒜下锅炒出香味后，放入萝卜翻炒数次，再放入豆腐干，倒入调料汁，加味精，勾薄芡，淋点麻油即可出锅装盆。', NULL, NULL, '花生油、精盐、味精、面酱、酱油、白糖、泡辣椒，辣油、胡椒、淀粉、葱、姜、蒜。', '辣萝卜400克，豆腐干150克。');
INSERT INTO `recipe` VALUES (3962, 56, '翡翠土豆丝', '(1)将土豆洗净去皮，切成细丝，放清水盆内洗净沥千水分，再用精盐腌制半小时左右，挤干水分待用。\r\n(2)芹菜去根和老叶，洗净下开水锅烫至八成熟捞出沥干水分，用刀切成粗末，放盘内，加精盐、麻油、味精、姜末拌匀。\r\n(3)将油锅烧至七成热时，将土豆丝分批下油锅炸至金黄色时捞出，放入盘中间，然后将拌好的芹菜均匀地放在土豆丝的四周，吃时撒上花椒盐即可。', NULL, NULL, '花生油、精盐、姜末、麻油、味精、花椒盐。', '土豆400克，芹菜100克。');
INSERT INTO `recipe` VALUES (3963, 56, '油焖茭白', '(1)茭白去皮，削去老根，洗净切成小块；·香菇用温水泡开后，去蒂头洗净。\r\n(2)炒锅放油烧热，放入葱段、姜块、八角、桂皮煸炒出香味后，倒入茭白、香菇，再放料酒、酱油、白糖焖透，放味精，淋上麻油即可出锅装盆。', NULL, NULL, '花生油、精盐、酱油、白糖、味精、麻油、葱、姜、八角、桂皮。', '茭白600克，香菇15克．');
INSERT INTO `recipe` VALUES (3964, 56, '炒土豆片', '(1)将土豆去皮洗净，切成柳叶片，放开水锅烫—下，捞出沥干水分，红椒切成小片待用，\r\n(2)锅里放油，待油热后，将葱段、姜末、花椒面放入锅内煸炒出香味后，随即将土豆片、红椒片放入翻炒片刻，加上精盐、酱油、香醋、味精，待入味后，勾芡淋明油出锅装盆即可。', NULL, NULL, '花生油、酱油、精盐、味精、花椒面、红椒、淀粉、葱、姜、香醋。', '土豆500克，红柿椒50克。');
INSERT INTO `recipe` VALUES (3965, 56, '炒土豆丝', '(1)土豆削皮洗干净切成细丝；青椒去蒂，去籽洗净切成丝，葱切成丝。\r\n(2)炒锅放油烧热后，放入花椒，煽炒出香味后捞出，再倒入京葱丝、青椒丝煸炒几下，随即放入土豆丝，加精盐翻炒数次后，放入味精、香醋炒熟，淋上明油即可出锅装盆。', NULL, NULL, '花生油、精盐、味精、花椒、香醋、大葱。', '土豆400克，青椒100克。');
INSERT INTO `recipe` VALUES (3966, 56, '素炒莴笋', '(1)莴笋刨皮，去根洗净，切成长方片、箭头片、滚刀片均可，用盐腌上，炒时用清水淘洗，沥干水分。\r\n(2)炒锅放油烧热，放入葱段煸炒出香味后，再放入莴笋片翻炒数次，加精盐、味精，勾薄芡，淋上明油即可出锅装盆。', NULL, NULL, '花生油、精盐、淀粉、葱段。', '莴笋700克。');
INSERT INTO `recipe` VALUES (3967, 56, '油焖笋', '(1)将春笋剥壳削去老根洗净，用刀将笋直剖开，然后切成长方条待用。\r\n(2)炒锅放油烧热，将葱段、姜块、桂皮、八角下锅炒出香味后，把笋条倒入锅里急炒4~5分钟，再加入酱油，白糖、精盐，焖至锅里没有水煎声，完全是酱油和油时，起锅装盆即成。\r\n此莱色泽红亮，嫩鲜香脆。', NULL, NULL, '花生油、酱油、精盐、白糖、桂皮，八角、葱段、姜块、麻油。', '春笋(或冬笋)600克。');
INSERT INTO `recipe` VALUES (3968, 56, '干煸春笋', '(1)将春笋切成长方条，榨菜洗净切成细末。\r\n(2)炒锅放油烧至六成热时，放入笋块炸至呈浅金黄色，捞出，沥净油，原锅留适量的油，倒入笋块，加入酱油，精盐、白糖、姜汁、酒酿汁、鲜汤烧开后，用小火煸透端回中火上至汤汁将干时，把锅端离火。\r\n(3)另起炒锅放油置中火烧热，放入榨菜末煸炒出香味后，倒入笋块，加味精翻炒至卤汁收干，笋发出焦香时，淋上麻油，撒上小葱末，出锅装盆即成。\r\n此菜为春令应时菜肴。因用干煸、焖、三种方法，使笋块入味\r\n透，脆嫩香鲜，别具风味。\r\n注：可用萝卜或茭白制作“干萝卜”或“干茭白”。', NULL, NULL, '熟花生油、精盐、酱油、白糖、味精、麻油、鲜汤、酒酿汁、姜汁，小葱。', '净春笋400克，榨菜100克。');
INSERT INTO `recipe` VALUES (3969, 56, '荠菜冬笋', '(1)冬笋切薄片，荠菜去根，去老叶洗净，放入开水中烫一下捞出，用冷水过凉后挤干水分，切成细末。\r\n(2)炒锅放油烧至五成热时，放入笋片炒几下，再加鲜汤、精盐、姜汁、白糖烧开后，移置小火烧透，再端回到中火上，放入荠菜和味精，勾薄芡后翻炒几下，淋上麻油即成。此菜笋片嫩黄，点以绿色荠菜，色泽悦目，入口脆香，鲜嫩爽口。\r\n注：如没有笋，可用茭白或土豆。', NULL, NULL, '熟花生油、精盐、味精、白糖、淀粉、麻油、黄豆芽汤、姜汁。', '净冬笋350克，荠菜150克。');
INSERT INTO `recipe` VALUES (3970, 56, '莴笋', '(1)将熟冬笋尖切成块，芹菜入沸水锅烫至翠绿捞出，沥干水，切末，放入盆内，再加味精、麻油拌匀。', NULL, NULL, '精盐、酱油、味精、清汤、麻油、姜末。', '熟冬笋尖400克，芹菜100克。');
INSERT INTO `recipe` VALUES (3971, 56, '冬菇面筋', '(1)将油面筋用温水泡软，切成两片，冬菇洗净待用，毛豆下开水锅煮开捞出，用冷水过凉。\r\n(2)炒锅放汕烧热，放入冬菇、笋片、葱末炒几下，随即放入油面筋、毛豆、精盐、酱油、白糖、冬菇、姜末。', NULL, NULL, '花生油、精盐、酱油、味精、白糖、淀粉，高汤、麻油、姜末、葱末。', '干油面筋300克，水发冬菇50克，笋片50克，毛豆50克。');
INSERT INTO `recipe` VALUES (3972, 56, '油焖腐竹', '(1)腐竹切段，放入开水中汆一下，沥净水分，莴笋去皮去根洗净，切滚刀块，腐竹成段，木耳泡发后洗净待用。\r\n(2)炒锅放油烧热。放入腐竹翻炒，待腐竹吐尽水分，加入精盐、酱油、白糖，八角、桂皮、葱、姜、肉汤，小火焖至汤汁减少时，放入黑木耳、莴笋片烧至汤快干，再加味精，淋上麻油出锅即成。\r\n注：此莱也可用竹笋、萝卜作配料。', NULL, NULL, '花生油、精盐、酱油、白糖，麻油、肉汤、大料、桂皮、葱，姜。', '水发腐竹300克，莴笋200克，水发木耳20克。');
INSERT INTO `recipe` VALUES (3973, 56, '香椿拌豆腐', '(1)豆腐切成块，用开水烫透捞出，沥去水分，香椿去老叶，放在盆内，用开水烫一下捞出，凉透切成末待用。\r\n(2)豆腐放在盆内，撒上香椿末，加精盐，味精，最后淋上麻油略拌即成。', NULL, NULL, '精盐，味精、麻油(或花椒油)。', '豆腐500克，嫩香椿芽100克。');
INSERT INTO `recipe` VALUES (3974, 56, '笋片豆腐', '(1)豆腐切成块，放入开水中烫透捞出，沥去水分，木耳泡发后洗净待用。\r\n(2)炒锅放油烧热，放入葱丝煸香后，再放入豆腐、笋片、木耳，加酱油、精盐、高汤烧透，勾薄芡，待再沸时，加味精，淋上麻油即成。\r\n注：如用笋干、雪莱即可制作“笋干豆腐”，“雪莱豆腐”。', NULL, NULL, '花生油、精盐、酱油、淀粉、高汤、麻油、葱丝。', '豆腐500克，笋片50克，水发木耳20克。');
INSERT INTO `recipe` VALUES (3975, 56, '干烧油豆腐', '(1)豆腐在开水中浸泡片刻，捞出沥去水分，切成块，葱切末，蒜剁成泥，香莱洗净切末待用。\r\n(2)炒锅放油烧热，放入豆腐：炸至金黄色捞出，另起锅，加肉汤、酱油烧滚，放入炸好的豆腐，待汤再滚时，加葱末，蒜泥和味精，用小火煨至汤汁收干，放些辣椒丝，香莱末，淋明油即可。', NULL, NULL, '花生油、酱油、味精、肉汤、辣椒丝、葱，大蒜。', '豆腐600克，香菜20克。');
INSERT INTO `recipe` VALUES (3976, 56, '红烧豆腐', '(1)豆腐切块，用油炸至金黄色捞出，丁香萝卜洗净切片；蒜苗去老叶去根洗净切成段待用。段待用。\r\n(2)炒锅放油烧热后，放入葱、姜煽出香味，再放丁香萝卜，蒜苗翻炒几下，加酱油、花椒面、精盐，适量的水，用小火煨透，勾薄芡出锅即成。', NULL, NULL, '花生油、精盐、酱油、味精、淀粉、花椒面、葱，姜末。', '豆腐500克，辣萝卜50克，蒜苗50克。');
INSERT INTO `recipe` VALUES (3977, 56, '四季豆腐', '(1)将豆腐切成块，放在冷水锅里烧开后捞出，沥去水分，笋和香菇均切成小薄片，用淀粉加适量的清水调成糊浆待用。\r\n(2)炒锅放油烧至五成热，把豆腐上糊，放入油锅炸至豆腐呈金黄色捞出，原锅留适量油，放入葱段、香菇、笋片炒几下，加入酱油，白糖、姜末、精盐、肉汤和豆腐烧开，再放入味精，勾薄芡，淋上明油即可出锅装盆。', NULL, NULL, '花生油、精盐、酱油、味精、白糖、淀粉、肉汤、葱段。', '豆腐500克，净冬笋100克，水发香菇30克。');
INSERT INTO `recipe` VALUES (3978, 56, '砂锅素豆腐', '(1)海米用温水泡洗干净3笋、蘑菇切薄片，菜心洗净剖成两片。\r\n(2)豆腐切成方块，用开水烫一下捞出，放入砂锅中，加上海米，笋、蘑菇片、高汤、精盐，炖至豆腐起孔，放入莱心，猪油烧开，加味精即可。', NULL, NULL, '香油、精盐、味精、高汤、胡椒粉，', '豆腐600克，春笋，蘑菇、青菜心各少量，海米10克。');
INSERT INTO `recipe` VALUES (3979, 56, '烧老豆腐', '(1)豆腐切块，下开水锅煮至出小孔捞出，小香菇泡发后去蒂洗净待用，蒜苔洗净切段。\r\n(2)炒锅放油烧热，下葱末、姜末煸出香味后，再加汤，然后放入煮好的豆腐和香菇，加调料，旺火烧开后，改用文火炖透，至起小孔后，另取炒锅，放油烧热，将蒜苔炒至七成熟，倒入豆腐上，勾薄芡，淋上明油即成。', NULL, NULL, '花生油、精盐，酱油、味精、淀粉、葱末、姜末。', '豆腐500克，小香菇10克，蒜苔50克。');
INSERT INTO `recipe` VALUES (3980, 54, '腐竹烧肉', '(1)将腐竹用热水泡发至软，切成段，蒜苗择洗干净切成段，猪肉切成小条。', NULL, NULL, '葱段、姜片，料酒、味精、盐、酱油、淀粉、鲜汤、花生油。', '干腐竹200克，猪肉100克，蒜苗50克。');
INSERT INTO `recipe` VALUES (3981, 54, '苤蓝炖肉', '(1)猪肉切成小厚片；苤蓝去皮，切成薄象眼块，下开水锅熨透捞出洗净。\r\n(2)炒勺上旺火，加底油，葱姜、花椒炝锅，下肉片、烹料酒，翻炒几下，下苤蓝、盐翻炒几下，加汤烧开，用小火炖至苤蓝软烂时加味精，用淀粉勾少许芡汁即可。', NULL, NULL, '葱花、姜末、花椒、料酒、味精、盐、淀粉，花生油。', '苤蓝(球茎甘蓝)750克，猪肉120克。');
INSERT INTO `recipe` VALUES (3982, 54, '土豆烧牛肉', '(1)牛肉切小块，下开水锅烫开捞出，用清水洗净，土豆去皮切成滚刀块，下烈油锅炸至外皮金黄色捞出。\r\n(2)炒锅上旺火，加底油，用葱、姜、花椒、大料炝锅，下牛肉，烹料酒、酱油翻炒几下，加清水、白糖烧开，用微火炖至牛肉软烂时，下炸好的土豆，加盐烧至肉烂、土豆熟时加味精出锅。', NULL, NULL, '葱段、姜片、花椒、大料、酱油、白糖、料酒、味精、盐、花生油。', '土豆600克，牛肉120克。');
INSERT INTO `recipe` VALUES (3983, 54, '蘑菇炒肉片', '(1)将蘑菇去根洗净，改刀成小块，香菜择洗干净切段，猪肉切成小片。\r\n(2)炒勺上旺火，加底油，用葱姜炝锅，下肉片、烹料酒，翻炒至半熟时，下蘑菇，加盐，炒至蘑菇熟时，加味精、香莱段拌均匀，用湿淀粉勾芡即可。', NULL, NULL, '葱姜末、精盐、料酒、淀粉、味精、花生油。', '鲜蘑菇(平菇或凤尾菇)750克，猪肉120克，香菜20克。');
INSERT INTO `recipe` VALUES (3984, 54, '韭黄炒肉丝', '(1)韭黄择洗干净，切成3cm长的段。肉切成丝，加少许盐、淀粉、蛋清上浆。\r\n(2)炒勺上旺火，注半勺油，待烧至五成热时，将肉丝下油锅划出，炒勺上旺火，加少许底油，用葱姜丝炝锅，下肉丝、烹料酒，翻炒几下后下韭黄，加盐炒至韭黄熟时加味精拌匀即可。', NULL, NULL, '葱姜丝、料酒、盐、味精、花生油、淀粉。', '鲜韭黄500克，猪肉100克，蛋清20克。');
INSERT INTO `recipe` VALUES (3985, 54, '苦瓜炒肉片', '(1)将猪肉切成片，将苦瓜去瓤后切成片，加少许盐拌匀腌半小时，尔后挤去水分，用凉水洗净，’下开水锅烫开，捞出后用冷水洗几遍。\r\n(2)炒勺上旺火，加底油，用葱姜炝锅，下肉片、料酒炒至八成熟时，下苦瓜片，加盐、味精翻炒均匀后即可出锅。', NULL, NULL, '料酒、味精、盐、葱花、姜末、花生油。', '鲜苦瓜600克，猪肉100克。');
INSERT INTO `recipe` VALUES (3986, 54, '辣椒炒肉丝', '(1)将辣椒择去把，洗干净，切成丝，猪肉切成丝。\r\n(2)炒勺上旺火，加底油，用葱姜丝炝锅，下肉丝翻炒，烹料酒、酱油，炒至肉丝变色后下辣椒丝，加盐翻炒，待辣椒丝变成翠绿之后，加味精搅匀，即可出锅。', NULL, NULL, '葱丝、姜丝、料酒、酱油、味精、盐、花生油。', '尖辣椒(青)300克，猪肉100克。');
INSERT INTO `recipe` VALUES (3987, 54, '西法土豆饼', '(1)将土豆洗净，上屉蒸熟后去皮，擦成泥，加入少量菱粉拌匀。鸡蛋打在碗里搅匀。\r\n(2)将火腿、虾仁、玉兰片、切成小丁，加味精、葱姜蒜拌均匀，用土豆泥作皮包成扁圆形小饼，沾上菱粉，拖上鸡蛋液，表面滚上面包渣，下油锅炸至外表金黄色即可。吃时撒上椒盐．', NULL, NULL, '猪油、菱粉、味精、葱姜末、盐、椒盐。', '土豆500克，火腿100克，鲜虾仁50克，玉兰片50克，鸡蛋50克，面包渣50克。');
INSERT INTO `recipe` VALUES (3988, 54, '蚂蚁上树', '(1)油锅上火烧热，将粉条下热油锅快速炸发起来捞‘出．\r\n(2)锅内留下底油，下肉末、豆瓣酱、葱、姜、蒜、辣椒面炒几下，随即注入料酒、汤、酱油，加上粉条，炖至汤汁干时加味精炒匀即可。', NULL, NULL, '花生油、大葱丝、姜丝、蒜末、豆瓣酱、辣椒面、酱油、料酒、味精。', '细粉条250克，肉末100克。');
INSERT INTO `recipe` VALUES (3989, 54, '酸菜汆白肉', '(1)把五花肉切成长5cm的薄片。酸菜切成细丝洗净。\r\n(2)炒勺内放入肉汤，烧开后把肉片、酸菜、粉条放入汤内，开锅后移在小火上，撇净浮沫，炖10分钟，加上精盐、味精，花椒水即可。', NULL, NULL, '精盐、味精、花椒水、肉汤。', '酸菜600克，熟猪五花肉100克，水泡粉丝50克。');
INSERT INTO `recipe` VALUES (3990, 54, '白菜熬肉片', '(1)将白菜洗净，切成块；猪肉切成片，大葱切成黄豆大小的葱花。\r\n(2)炒勺加底油，葱花炝锅，下肉片，烹花椒水翻炒至变色，下白菜，加盐炒至白菜由脆变软时，加少许汤，用小火熬至白菜软烂时，加味精即可出锅。', NULL, NULL, '猪油、精盐、味精、葱、花椒水。', '大白菜750克，猪肉100克。');
INSERT INTO `recipe` VALUES (3991, 54, '肉丝拌豆腐皮', '(1)豆腐皮切成4cm长的丝，下开水锅快速烫开，捞出后用凉水过凉备用。\r\n(2)炒勺上旺火，加底油，用葱姜炝锅，下肉丝、酱油、料酒炒熟，倒在豆腐皮上，加上其余的调料拌匀即可。', NULL, NULL, '料酒、味精、盐、香油、酱油、葱姜丝。', '豆腐皮400克，肉丝100克。');
INSERT INTO `recipe` VALUES (3992, 54, '肉丝拌豆芽', '(1)将豆芽择洗干净，下开水锅快速烫开，捞出后用凉水过凉备用。\r\n(2)炒勺上旺火，加底油，用葱姜炝锅，下肉丝、酱油、料酒炒熟，倒在豆芽上，加上其余的调料拌匀即可。', NULL, NULL, '料酒、味精、盐、香油、醋、葱姜丝、酱油、料酒。', '绿豆芽(或黄豆芽)500克，肉丝100克。');
INSERT INTO `recipe` VALUES (3993, 54, '炝肉丝莴笋', '(1)精肉洗净切成丝，用精盐、淀粉、蛋清浆好，放入温油中迅速划散，见断生变色捞出，用冷水过凉后，放在盆内，莴笋去皮，去叶洗净切成丝，放入开水中烫透捞出，用冷水过凉，沥水，和肉丝放在—起，加精盐、葱丝、姜丝，浇上花椒油，稍焖一下，再加些味精、精盐拌匀即成。', NULL, NULL, '花生油、精盐、味精、花椒油、淀粉、葱丝、姜丝。', '猪瘦肉100克，莴笋500克，蛋清20克。');
INSERT INTO `recipe` VALUES (3994, 54, '凉拌彩皮', '把油发肉皮泡软，洗去油腻，用开水烫后捞出，沥千水分切丝，芹菜洗净切段，放入开水中氽透捞出沥水，放入盆内，再加酱油、味精、白糖、香醋、麻油，同皮肚、香菇丝拌匀即成。', NULL, NULL, '酱油、味精、白糖、香醋、麻油、辣酱。', '泡发皮肚100克，嫩芹菜400克，香菇丝50克，火腿丝50克。');
INSERT INTO `recipe` VALUES (3995, 54, '萝卜拌海蛰', '(1)海蛰皮浸后刮腻去沙，洗净切丝，再用烫水烫后，用清水反复洗净，沥干水分；萝卜刨皮洗净切细丝，用盐腌后去汁待用。\r\n(2)将海蛰皮、萝卜丝放入盆内，加白糖，香醋、麻油、花椒油、味精拌匀即可。', NULL, NULL, '精盐、味精、白糖、香醋、麻油、花椒油、葱丝．', '海蛰皮100克，水萝卜500克。');
INSERT INTO `recipe` VALUES (3996, 54, '酥炸蕃茄', '(1)蕃茄洗净，用开水烫一下捞出，撕去皮，从正中剖成两片，去籽、去瓤，再切成龙船块，放入盆内，撒上淀粉待用。\r\n(2）虾仁、条肉分别斩成茸，同放盆内，加蛋清、精盐、胡椒粉、料酒、葱、姜汁拌匀，再放入麻油搅拌成馅，分放于蕃茄片上，用手抹成椭圆形，成酥炸蕃茄生坯，干面粉加蛋清打成糊，加适量花生油拌匀。去皮，去叶洗净切成丝，放入开水中烫透捞出，用冷水过凉，沥水，和肉丝放在—起，加精盐、葱丝、姜丝，浇上花椒油，稍焖一下，再加些味精、精盐拌匀即成。\r\n(3)炒锅放油烧至—匕成热时，将蕃茄生坯挂糊下锅炸透捞出，待油升', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、面粉、麻油、花椒盐，葱，姜汁。', '鲜蕃茄500克，虾肉50克，五花肉100克，蛋清20克。');
INSERT INTO `recipe` VALUES (3997, 54, '肉丝炝芹菜', '(1)芹菜去老茎、老叶，洗净，切成寸段，猪肉切成丝，用精盐、淀粉拌匀，蛋皮切成丝，葱切段。\r\n(2)炒锅置大火，待水开放入芹菜烫一下捞出，用冷水过凉。\r\n(3)炒锅放油烧热，倒入肉丝，炒至熟铲起。\r\n(4)把芹菜、肉丝、蛋皮，精盐、米醋、味精、麻油放盆中拌匀即可。', NULL, NULL, '花生油，精盐、味精、料酒、香醋、麻油、淀粉、葱。', '嫩芹菜500克，猪肉100克，蛋清20克，蛋皮一张。');
INSERT INTO `recipe` VALUES (3998, 54, '青椒炒虾皮', '(1)柿子椒去蒂、籽，洗净沥干，用刀拍扁，切成长丝。\r\n(2)锅内放入花生油，用旺火烧热，放入葱丝稍炸，再放入柿子椒炒拌均匀。\r\n(3)待柿子椒炒成绿色起油壳时，加入虾皮、酱油、精盐、味精，再炒拌均匀，淋上明油，翻炒一下即可出锅。', NULL, '炒时要火旺快炒，现炒现吃。', '花生油、酱油、精盐、味精、葱丝。', '青柿子椒500克，虾皮100克。');
INSERT INTO `recipe` VALUES (3999, 54, '炒豌豆粒', '(1）将鲜豌豆粒淘洗干净待用，瘦肉切成丁，放入盆里用蛋清、精盐、淀粉浆好。\r\n(2)锅放油烧至七成热时，把浆好的肉丁下油锅划散捞出，锅内留适量的油，把豌豆米入锅爆炒，半熟放精盐及肉汤焖约五分钟，随后将肉丁倒入炒熟，勾薄芡，出锅即可。', NULL, '炒时要旺火快炒，才能使豌豆呈碧绿色，要现炒现吃。', '花生油、精盐、料酒、味精、淀粉、肉汤。', '鲜豌豆粒400克，猪肉100克，蛋清20克。');
INSERT INTO `recipe` VALUES (4000, 54, '扁豆角炒肉', '(1)嫩扁豆撕筋去头掰成两节洗净，把水沥干，瘦肉切片，大葱切段。\r\n(2)炒锅放油烧热，先放葱段煸炒出香味后，将肉片放入翻炒至七成熟起锅，原锅放油，待油热时，把扁豆倒入，大火炒搅至七成熟时，放入肉片和调料拌炒至熟，即可出锅装盆。', NULL, NULL, '猪油、精盐、味精、大葱。', '扁豆角400克，猪肉100克。');
INSERT INTO `recipe` VALUES (4001, 54, '翡翠虾球', '(1)将虾仁、肥膘分别洗净斩成茸，放入盆内，加蛋清、淀粉、料酒、葱末、姜末、味精、精盐拌匀，将蚕豆剁成泥拌入虾茸内，打成翡翠虾球生馅。\r\n(2)炒锅放油烧至五成热时，将生馅挤成丸子，放入锅内炸至浮起捞出，沥净油，炒锅复上火，放入虾球，加花椒盐翻炒出锅即成。', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、花椒盐、葱末、姜末。', '鲜蚕豆400克，猪肥膘40克，鲜虾仁200克，蛋清20克。');
INSERT INTO `recipe` VALUES (4002, 54, '包菜肉末', '(1)将包心菜切成丝，葱、姜、蒜均切成细末。\r\n(2)炒锅放油烧热，放入葱、姜、蒜末炒出香味后，再放肉末煸炒，随后倒入包心菜翻炒，把上述的调料兑到——块倒入锅内搅匀，勾薄芡淋上花椒油即成。', NULL, NULL, '花生油、精盐、酱油、味精、香醋、淀粉、辣酱、面酱、花椒油、葱、姜、蒜。', '包心菜1000克，猪肉末150克，');
INSERT INTO `recipe` VALUES (4003, 54, '酱焖丝瓜', '(1)嫩丝瓜刨皮，洗净切滚刀块；腊肉洗刮干净后，切成丁，葱、姜均切成丝。\r\n(2)炒锅放油烧热，放葱、姜丝煸出香味后，放入腊肉丁炒，然后加辣酱炒搅1—2分钟左右，再将丝瓜投。人焖烧，放料酒、精盐、味精、淀粉勾薄芡即成，淋上明油装盆。', NULL, NULL, '猪油、精盐、料酒、味精、淀粉、辣酱、葱段、姜。', '嫩丝瓜700克，腊肉100克。');
INSERT INTO `recipe` VALUES (4004, 54, '冬瓜烧排骨', '(1)将排骨洗净，放入开水锅内烫一下捞出，沥去水分备用，将冬瓜削皮，挖去瓤，切成块。\r\n(2)炒锅放油烧热后，把葱段、姜块、桂皮、八角下油锅煸炒出香味后，再放排骨翻炒几下，加酱油、白糖烧至七成熟时，放入冬瓜，加精盐、味精烧透，收干水分，即可出锅。', NULL, NULL, '花生油、精盐、白糖、味精、姜块、葱段、桂皮、八角。', '冬瓜700克，猪排骨200克。');
INSERT INTO `recipe` VALUES (4005, 54, '冬瓜烧肉', '(1)将猪肉洗净，切成3.3厘米见方的块，放入开水锅内烫一下捞出，沥去水分备用，将冬瓜削皮，挖去瓤，切成块。\r\n(2)炒锅放油烧热后，把葱段、姜块、桂皮、八角下油锅煸炒出香味后，再放猪肉翻炒几下，加酱油、白糖烧至七成熟时，放入冬瓜，加精盐、味精烧透，收干水分，即可出锅。', NULL, NULL, '花生油、精盐、白糖、味精、姜块、葱段、桂皮、八角。', '冬瓜750克，猪肉120克。');
INSERT INTO `recipe` VALUES (4006, 54, '红烧茄子', '(1)用鲜嫩的茄子，去柄洗净，切成两片，每片再切成两片，然后切成三角形状的小丁，猪肉洗净同样切成丁，大葱、姜、蒜切细末。\r\n(2)炒锅放油烧热，把茄子逐次倒入锅里炸至八成熟捞起，把油沥干，原锅留适量油，将葱、姜、蒜下锅煽炒出香味后，下肉丁急炒，炒至八成熟时随即加酱油、料酒、精盐、味精，放入茄子烧透，加入肉汁烧片刻起锅装盆，食时放些葱花。', NULL, '茄子不能用水浸泡，大锅炒时，不宜放水，要用中火煸透。', '花生油，精盐、酱油、味精、料酒、大葱、姜，蒜。', '茄子500克，猪肉100克。');
INSERT INTO `recipe` VALUES (4007, 54, '炒银丝', '(1)将萝卜洗净刨皮，切成细丝备用，银鱼丝泡软洗净，蒜苗切段待用。\r\n(2)炒锅放油烧热，将蒜苗下锅煸炒出香味后，放入萝卜丝炒至。八成熟，再放银鱼丝、料酒、精盐、肉汤稍煮片刻，放味精，勾薄芡，再翻炒几次，淋上麻油即可。', NULL, '萝卜要切细丝，炒时要旺火快炒，才能生脆。', '花生油、精盐、味精、料酒、蒜苗、淀粉、肉汤。', '萝卜400克，银鱼丝100克。');
INSERT INTO `recipe` VALUES (4008, 54, '炒三丁', '(1)萝卜洗净刨皮切丁，猪肉洗净切丁，葱，姜切成指甲片，用开水将萝卜烫至七、八成熟，用冷水过凉。\r\n(2)炒锅放油烧热，将葱、姜片下锅煸出香味后，放入猪肉丁翻炒几下，再放入毛豆、精盐，菜熟放味精，用淀粉勾芡，淋上明油出锅装盆即可。', NULL, NULL, '花生油、精盐、淀粉、葱、姜。', '萝卜200克，毛豆200克，猪肉100克。');
INSERT INTO `recipe` VALUES (4009, 54, '茭白里脊', '(1）茭白剥去皮，削去硬根用清水洗净，切成细丝，下开水锅烫后捞出冷却后备用，红辣椒同样切细丝。\r\n(2)将猪肉用刀披成片后，切成细丝，大葱也同样切丝；把切好的肉丝用冷水洗净血水，沥干水分后，加精盐、味精、蛋清，淀粉浆好备用。\r\n(3)炒锅放油烧至七成热时，放入浆好的肉丝划散捞出，原锅内留适量的油，放入葱丝，红辣椒煸炒出香味后，再放入肉丝、茭白丝翻炒数次，加料酒、精盐、味精翻炒均匀，勾薄芡，淋上明油即可出锅装盆。', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、大葱。', '茭白400克，瘦猪肉100克，红辣椒50克，蛋清20克．');
INSERT INTO `recipe` VALUES (4010, 54, '萝卜腊肉', '(1)腊肉用温水浸泡数分钟，然后刮洗干净切成薄片，萝卜洗净，切薄片，放入开水锅烫一下后捞出过凉，葱切段备用。\r\n(2)炒锅放油烧热，放入葱段、腊肉煸炒出香味后，再放萝卜片、料酒、精盐，味精翻炒数次后，勾薄芡，淋上明油装盆即可。', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、葱段。', '辣萝卜400克，腊肉100克。');
INSERT INTO `recipe` VALUES (4011, 54, '春笋腊肉', '(1)腊肉用温水浸泡数分钟，然后刮洗干净切成薄片，春笋剥皮，削去硬根洗净，切薄片，放入开水锅烫一下后捞出过凉，葱切段备用。\r\n(2)炒锅放油烧热，放入葱段、腊肉煸炒出香味后，再放春笋片、料酒、精盐，味精翻炒数次后，勾薄芡，淋上明油装盆即可。', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、葱段。', '春笋400克，腊肉100克。');
INSERT INTO `recipe` VALUES (4012, 54, '茭白腊肉', '(1)腊肉用温水浸泡数分钟，然后刮洗干净切成薄片，茭白剥皮，削去硬根洗净，切薄片，放入开水锅烫一下后捞出过凉，葱切段备用。\r\n(2)炒锅放油烧热，放入葱段、腊肉煸炒出香味后，再放茭白片、料酒、精盐，味精翻炒数次后，勾薄芡，淋上明油装盆即可。', NULL, NULL, '花生油、精盐、味精、料酒、淀粉、葱段。', '茭白400克，腊肉100克。');
INSERT INTO `recipe` VALUES (4013, 54, '咖喱土豆', '(1)把土豆洗净蒸熟，去皮切成块，下油锅炸成金黄色捞出，仔鸡洗干净后剁成块；葱、姜、蒜均切成末。\r\n(2)锅烧热放油，下葱、姜、蒜煸出香味后，加入咖喱粉炒几下，再倒入鸡块翻炒，加水漫过鸡肉，下入土豆块烧开，至鸡肉烂、土豆酥，加入精盐、味精、猪油出锅装盆。', NULL, NULL, '花生油、咖喱粉，精盐、味精、猪油、葱、姜、蒜头。', '土豆400克，嫩鸡100克。');
INSERT INTO `recipe` VALUES (4014, 54, '酱爆土豆', '(1)将土豆洗净切成1.5厘米左右的方丁，下开水锅中煮至变色，捞出晾凉，猪肉、黄瓜分别切丁，葱、姜切末，蒜头拍碎。\r\n(2)锅放油烧热，先把土豆下油锅炸至金黄色捞出；原锅留适量油，先下肉丁稍炒几下，后下葱姜煸炒出香味，再下土豆、黄瓜翻炒熟，随即下入面酱、味精，白糖翻炒入味，勾芡淋熟油，出锅装盘即可。', NULL, NULL, '甜面酱、花生油、精盐、味精、白糖、淀粉、葱，姜、蒜头。', '土豆400克，猪肉100克，黄瓜100克。');
INSERT INTO `recipe` VALUES (4015, 54, '干烧笋', '(1)将笋剥壳去根，洗净后切成一寸长的条，把虾米、猪肉、榨菜(用水洗净)、泡辣椒(去蒂和籽)均切成末。\r\n(2)把笋条放入油锅内略炸，即把笋条捞出，原锅内留适量油，把肉末倒入煽炒，随后将虾米、榨菜末、葱姜末、蒜末、辣椒末一同煸炒，再倒入炸好的笋条，加酱油及适量水，用文火焖，待焖透入味，收干水分后，浇上点辣麻油，即可出锅。', NULL, NULL, '花生油、酱油、白糖、味精、麻辣油、泡辣椒、酒酿汁，葱末、姜末、蒜末。', '春笋400克，猪肉末100克，榨菜40克，虾米10克。');
INSERT INTO `recipe` VALUES (4016, 54, '虾仁豆腐', '(1）先将虾仁洗净后，沥干水分，放入盆内，用蛋清、精盐、淀粉浆好，蕃茄用开水烫——下去皮，切成小丁，嫩豆腐切成小丁，下锅用清水煮开捞出沥水。\r\n(2)炒锅放油烧热，放入浆好的虾仁划散捞出，锅内留适量的油，放入葱、姜煸出香味后，再放入高汤、精盐、料酒、虾仁、蕃茄丁、豆腐烧透，加味精，勾薄芡出锅装盆即成。', NULL, NULL, '猪油、精盐、味精、料酒、高汤、淀粉、葱、姜。', '豆腐600克，鲜虾仁100克，蕃茄50克，蛋清20克。');
INSERT INTO `recipe` VALUES (4017, 54, '咸肉豆腐', '(1)豆腐切方块，放入开水锅烫一下捞出，沥去水分，咸肉切薄片待用。\r\n(2)将豆腐和咸肉放入锅中，加入咸肉汤，在旺火上烧沸后，用小火焖透，待豆腐浮起时，放入青蒜段，淋上明油即可出锅。', NULL, NULL, '猪油、精盐、味精、咸肉汤。', '豆腐600克，熟咸肉100克，青蒜20克。');
INSERT INTO `recipe` VALUES (4018, 54, '虾籽豆腐', '(1)将嫩豆腐切成丁，用水烧开，除去苦黄水，捞出沥干水分。\r\n(2)炒锅放油烧热，放入虾籽、葱末、姜末煸炒几下，随即倒入豆腐烧透，加精盐，味精，勾薄芡，淋上麻油即成。', NULL, NULL, '花生油、精盐、味精、淀粉、麻油、葱末、姜末。', '嫩豆腐600克，虾籽100克。');
INSERT INTO `recipe` VALUES (4019, 54, '豆腐脑', '(1)将豆浆熬开点成豆腐脑备用。榨菜剁成末。\r\n(2)炒勺上旺火，加底油，葱姜炝锅，加清汤、酱油、胡椒粉、味精、精盐，烧开后调成咸口，用淀粉勾芡，盛在海碗中成卤。\r\n(3)吃时将豆腐脑盛入碗中或深盘中，上边浇上打好的卤，淋上香油、辣油，撒上榨菜末、虾皮即可。', NULL, NULL, '精盐、味精、麻油，辣油，胡椒粉、葱末、姜末，淀粉、酱油。', '豆腐脑，虾皮，榨菜。');
INSERT INTO `recipe` VALUES (4020, 54, '烧冻豆腐', '(1)冻豆腐用沸水解冻，挤去黄水，切成块，猪肉洗净剁成末，大蒜洗净切斜片待用。\r\n(2)炒锅放油烧至八成热，放入猪肉末炒散，再放豆腐块、料酒、精盐，酱油轻轻翻炒，然后加肉汤或清水焖透，加蒜片炒几下，再放味精，勾薄芡，撒上胡蒜粉，淋上明油即成。', NULL, '豆腐要冻透，才能烧呈蜂窝状，入味可口。', '猪油、精盐、酱油、味精、料酒、淀粉、大蒜、胡椒', '冻豆腐500克，猪肉100克。');
INSERT INTO `recipe` VALUES (4021, 54, '红焖豆腐', '(1）豆腐切方块，猪肉洗净，切成片，木耳泡发后洗净待用。\r\n(2)炒锅放油烧热，放入豆腐炸至两面呈金黄色捞出，沥净油，原锅留适量的油，放入肉片炒散，加料酒、笋片、木耳、酱油、精盐、姜末、鲜汤或水，把炸好的豆腐焖透至有孔入味，再加蒜片、味精，勾薄芡，淋上明油，撒上胡椒粉即可出锅．', NULL, NULL, '猪油，精盐、酱油、味精、鲜汤、料酒、淀粉、胡椒粉、姜末、蒜片。', '豆腐600克，猪肉100克，笋片，水发木耳各20克。');
INSERT INTO `recipe` VALUES (4022, 54, '砂锅豆腐', '(1)豆腐切成粗条，用开水烫透捞出，沥去水分，青菜洗净切斜片，猪肉切片，海米用温水泡发待用。\r\n(2)砂锅内放鲜汤烧开，撇去浮沫，加入精盐、猪肉片、海米，花椒粉、料酒、青菜片、豆腐烧透，再加味精，淋上明油即成。', NULL, NULL, '精盐、味精、料酒、花椒粉、麻油，鲜汤。', '豆腐700克，猪五花肉120克，海米5克，油菜心(或白菜心)100克。');
INSERT INTO `recipe` VALUES (4023, 54, '豆腐烧肉片', '(1)猪肉洗净切成片，豆腐切成厚片，用开水烫透捞出，沥去水分。\r\n(2)炒锅放油烧热，放入葱段、姜片、蒜片煽出香味，再放猪肉片，下料酒炒至变色，加酱油，料酒，花椒粉、肉汤或水，然后放入豆腐片，用中火烧透，加味精精盐，勾薄芡，淋上明油即可出锅。', NULL, NULL, '花生油、精盐、酱油、味精、淀粉、料酒、肉汤、葱段、姜、蒜片。', '豆腐500克，猪肉100克，笋片50克。');
INSERT INTO `recipe` VALUES (4024, 54, '麻辣豆腐', '(1)豆腐切成小方丁，放入开水中烫透捞出，沥去水分。\r\n(2)炒锅放油烧热，放入花椒、干红椒炸至呈黑糊色时，捞出弃去不用。将锅内的麻辣油留出一部分备用，再下肉末炒，至肉末变色，下葱末、姜末、蒜末、酱油、精盐、高汤(或水)，再放入豆腐丁，用大火烧开，改用中火慢煮至豆腐入味时，加味精，勾薄芡，即可出锅装盆，撒上胡椒粉，浇上麻辣油即成。', NULL, NULL, '花生油、精盐、酱油、味精、高汤，花椒、干红辣椒、胡椒粉、葱末、姜末、蒜末。', '豆腐500克，猪肉末100克。');
INSERT INTO `recipe` VALUES (4025, 54, '锅煽豆腐', '(1）将肉末放盆里，加精盐、料酒、味精、葱末、姜末、麻油拌匀成馅，冬笋切片，香菇泡发后洗净待用。\r\n(2)鸡蛋打入碗中，用筷子搅均匀。\r\n(3)豆腐切成约3cm长、2cm宽、0．5cm厚的片；每两片中间抹一层肉馅，然后拍上面粉，拖上鸡蛋液，逐块下入七成热的油锅内煎至两面金黄色后，下葱丝、姜丝、冬笋片、香菇，加入鲜汤、精盐、料酒、白糖、味精，待入味后，勾薄芡，淋上麻油出锅摆入盘中即可。', NULL, NULL, '花生油、精盐、味精、白糖、淀粉、料酒、胡椒粉、葱、姜、面粉。', '豆腐500克，猪肉末100克，冬笋50克，香菇10克，鸡蛋50克。');
INSERT INTO `recipe` VALUES (4026, 54, '三丁豆腐', '(1)将豆豉加料酒，蒸10分钟，取出切细末：猪肉洗净切成丁，荸荠切细丁，豆腐切丁，用开水烫透捞出待用。、\r\n(2)炒锅放油烧热，将蒜末、姜末、肉末煽出香味后，放入豆豉、荸荠、豆腐，加料酒、味精、精盐一起炒，随同泡辣椒末、葱末翻炒几下即可出锅装盆。', NULL, NULL, '猪油、精盐、味精、料酒、泡辣椒、葱末、姜末、蒜末、豆豉。', '豆腐300克，罐头荸荠200克，猪肉100克。');
INSERT INTO `recipe` VALUES (4027, 54, '三丝豆腐', '(1)豆腐切粗条，放入开水中烫透捞出，沥去水分；榨菜洗净，冬笋去壳洗净切成细丝，猪肉洗净切细丝，放入盆内，用精盐，淀粉、蛋清浆好。\r\n(2)炒锅放油烧热，放入肉丝划散捞出，原锅留适量油，放入葱丝、姜丝炒出香味，加料酒、酱油、肉汤、豆腐丝、肉丝、榨菜丝、冬笋丝烧至汤汁将收干时，加味精，撒上花椒粉，勾薄芡出锅即成。', NULL, '烫豆腐时，在开水中加些精盐，这样，烧时不容易烧碎。', '猪油、精盐、酱油、料酒、淀粉。肉汤、花椒粉、葱', '豆腐400克，猪肉100克，冬笋100克，榨菜50克，蛋清20克。');
INSERT INTO `recipe` VALUES (4028, 54, '炒金银豆腐', '(1)豆腐切成片，放开水中烫透捞出，沥干水分。\r\n(2)鸭蛋打入盆内，加精盐、味精，蒸成蛋糕，切片待用。\r\n(3)炒锅放油烧热，先将葱丝、姜丝炒出香味，然后放入豆腐烧透、加入蛋糕、精盐高汤翻炒几下，最后放味精，勾薄芡，撒上胡椒粉即可出锅装盆。', NULL, NULL, '精盐、味精、淀粉，葱丝、姜丝、胡椒粉。', '豆腐700克，鸭蛋150克。');
INSERT INTO `recipe` VALUES (4029, 54, '家常豆腐', '(1)豆腐切成约3cm长、2cm宽、0。5cm厚的片，猪肉切．片，木耳泡发后择洗干净。\r\n(2)油锅上旺火，油热后将豆腐片下锅炸成表面金黄色时捞出。炒勺上火，加底油，油热后放肉片、葱姜丝、辣椒丝煸炒几下，加豆瓣酱、笋片、萝卜片、木耳、料酒、高汤及豆腐，用旺火烧开，用小火烧至豆腐片入味后，加味精，勾薄芡，淋上明油即可出锅。', NULL, NULL, '花生油，酱油、味精，淀粉，高汤，辣椒丝、葱丝，豆瓣酱。', '豆腐750克，猪肉100克，笋片，胡萝卜片，黑木耳各适量，');
INSERT INTO `recipe` VALUES (4030, 54, '黄瓜炒鸡蛋', '(1)将黄瓜洗净，切成小象眼片。鸡蛋打在碗里，加葱花、盐搅匀备用。\r\n(2)炒勺上旺火，加底油将鸡蛋炒熟盛入碗中待用。炒勺再上旺火，加底油，用葱花炝锅，下黄瓜，加盐炒至半熟时，加味精、鸡蛋搅匀即可出锅。', NULL, NULL, '葱花、精盐、花生油、味精，', '黄瓜500克，鸡蛋150克．');
INSERT INTO `recipe` VALUES (4031, 54, '蕃茄炒鸡蛋', '(1)将鸡蛋打入盆内，加精盐、味精搅匀；蕃茄洗净去蒂切块待用．\r\n(2)炒锅放油烧热，放入鸡蛋炒至半熟时，倒入蕃茄翻炒，熟后加葱末即可出锅装盆。', NULL, NULL, '花生油，精盐、味精、葱末。', '鸡蛋150克，蕃茄750克。');
INSERT INTO `recipe` VALUES (4032, 54, '韭菜炒鸡蛋', '(1)将韭菜择洗干净，切成约1.5cm长的段。鸡蛋打在盆里，加盐、葱末搅匀。\r\n(2)炒勺上旺火，勺热后加底油，油热后下鸡蛋炒至熟后夕盛在盆中，炒勺再上火，加少许底油，油热后下韭菜炒至半熟，将炒好的鸡蛋下锅，搅拌均匀后即可出锅。', NULL, NULL, '花生油、精盐、葱末。', '韭菜400克，鸡蛋150克。');
INSERT INTO `recipe` VALUES (4033, 54, '鸡丝拉皮', '(1)先将熟鸡脯肉，切成细丝，黄瓜洗净去瓤，切成粗丝；粉皮也切粗丝。\r\n(2)将粉皮丝放在开水里烫一下，沥去水，随即用麻油拌一拌，黄瓜丝用盐水泡一下，然后将粉皮丝放入盘内，上面再放鸡丝，鸡丝上面放黄瓜丝，再浇上适量的酱油，香醋、麻油、并撒上姜丝及芥末，最后加些味精调和即成。', NULL, NULL, '白酱油、精盐、味精、料酒、香醋、麻油、葱姜丝，芥末。', '熟鸡脯肉100克，鲜黄瓜200克，鲜粉皮300克。');
INSERT INTO `recipe` VALUES (4034, 54, '葱头炒猪肝', '1)猪肝洗净切片，放入盆中，用淀粉、精盐浆好葱头洗净去瓤切片，黑木耳泡发后洗净待用．\r\n(2)炒锅放油烧至六成熟时，放入浆好的猪肝划散捞出，沥去油，原锅留适量的油，放入葱，姜、蒜、葱头、木耳稍炒几下，即将猪肝倒入锅内，加入料酒、酱油、精盐、白糖和汤翻炒至熟，再放味精，勾薄芡，淋上明油即成．', NULL, NULL, '花生油、酱油、精盐、味精、料酒、淀粉、白糖、葱花、蒜片、姜片。', '猪肝100克，葱头400克，水发木耳20克。');
INSERT INTO `recipe` VALUES (4035, 54, '莴笋炒猪肝', '(1)莴笋要削皮，用开水烫一遍，猪肝洗净切片，放入盆中，用淀粉、精盐浆好，黑木耳泡发后洗净待用．\r\n(2)炒锅放油烧至六成熟时，放入浆好的猪肝划散捞出，沥去油，原锅留适量的油，放入葱，姜、蒜、黄瓜、木耳稍炒几下，即将猪肝倒入锅内，加入料酒、酱油、精盐、白糖和汤翻炒至熟，再放味精，勾薄芡，淋上明油即成．', NULL, NULL, '花生油、酱油，精盐、味精、料酒、淀粉、白糖、葱花、蒜片、姜片。', '猪肝100克，莴笋400克，水发木耳20克。');
INSERT INTO `recipe` VALUES (4036, 54, '黄瓜炒猪肝', '(1)猪肝洗净切片，放入盆中，用淀粉、精盐浆好，黄瓜洗净去瓤切片，黑木耳泡发后洗净待用．\r\n(2)炒锅放油烧至六成熟时，放入浆好的猪肝划散捞出，沥去油，原锅留适量的油，放入葱，姜、蒜、黄瓜、木耳稍炒几下，即将猪肝倒入锅内，加入料酒、酱油、精盐、白糖和汤翻炒至熟，再放味精，勾薄芡，淋上明油即成．', NULL, NULL, '花生油、酱油、精盐，味精、料酒、淀粉、白糖、葱花、姜片、蒜片．', '猪肝100克，黄瓜400克，水发木耳20克。');
INSERT INTO `recipe` VALUES (4037, 54, '白菜肚块', '(1)白菜去老叶洗净切块，京葱去老皮去根洗净切成丝；熟猪肚切成斜刀块。\r\n(2)炒锅放油烧热，放入京葱、蒜片、白菜炒几下，再加料酒，酱油、鲜汤、香醋、肚块翻炒至熟，勾薄芡，淋上花椒油即成。', NULL, '炒大白莱时要旺火快炒，才能生脆．', '花生油、精盐、酱油、味精、料酒、香醋、淀粉、花椒油、蒜片．', '熟猪肚100克，大白菜500克，大葱50克，辣椒50克。');
INSERT INTO `recipe` VALUES (4038, 54, '芹菜牛肉丝', '(1)牛肉切成丝用淀粉、料酒、精盐，蛋清和适量麻油抓拌均匀。\r\n(2)炒锅放油烧至五成热时，将肉丝下锅划散捞出，沥净油，原锅留适量油，放入豆瓣酱、酱油、白糖、料酒炒出香味后，下葱丝、姜丝、干红椒丝和芹菜段，快炒后加牛肉丝翻炒几下，加味精，麻油出锅装盆即可。', NULL, '划散牛肉丝的油温控制在五、六成，芹菜要旺火快炒，才能生脆。', '花生油、精盐、酱油、味精、料酒、面酱、麻油、淀粉，花椒粉、辣豆瓣酱、红椒丝、葱丝、姜丝。', '瘦牛肉150克，嫩芹菜段500克，蛋清20克。');
INSERT INTO `recipe` VALUES (4039, 54, '丝瓜肉丸', '(1)将丝瓜刨皮洗净，切成滚刀块，蕃茄洗净，去蒂切成块，木耳泡发后洗净待用。\r\n(2)猪肉洗净剁成末，放入盆内，加料酒、精盐、味精、淀粉、葱末、姜末拌匀制成肉馅。锅放水烧开，用手将肉馅挤成小肉丸，放入开水中，待肉丸浮起时，即可捞出。\r\n(3)炒锅放油烧热，放入丝瓜，蕃茄、木耳翻炒几下，随即加入适量煮肉丸的汤，待烧透后，再放精盐、肉丸、味精翻炒至熟，勾薄芡，出锅即成。', NULL, '肉丸必须水开后下锅，以免破碎，丝瓜下油锅炒时火要旺，动作要快，才能保持其叶绿素成份．', '猪油、精盐、味精，料酒、淀粉、葱末、姜末。', '丝瓜500克，猪肉100克，蕃茄50克，木耳10克。');
INSERT INTO `recipe` VALUES (4040, 54, '冬瓜炖猪爪', '(1)将冬瓜去皮，去瓤，洗净后切成块备用。\r\n(2)将猪爪择去毛根，刮干净，剁成小块，下开水锅烫至开锅，捞出后用清水漂洗干净。\r\n(3)炒锅上旺火，加底油，用花椒、葱姜炝锅，下猪爪块炒几下，加料酒、清汤，用旺火烧开，再用小火炖至猪爪快烂时，将冬瓜下锅，与猪爪一起炖，加上盐，待冬瓜炖熟时即可出锅。', NULL, '冬瓜下锅的时机要掌握好，要在猪爪快烂时再下，下早了会造成猪爪没有烂而冬瓜已太烂。', '葱段、姜片、料酒、盐、花椒、花生油。', '冬瓜700克，猪爪150克。');
INSERT INTO `recipe` VALUES (4041, 54, '冬瓜咸肉', '(1)冬瓜削皮去瓜瓤，洗净切成块。\r\n(2)咸肉用开水泡洗，刮去肉皮上污物，切成大块，放入开水中烧至七成熟时，捞出冷却后，再切成厚片，将冬瓜放入咸肉汤里，加精盐、咸肉片一起烧透入味，放入味精即可出锅装盆。', NULL, NULL, '精盐、味精、料酒、葱结、姜块．', '冬瓜750克，猪肉100克。');
INSERT INTO `recipe` VALUES (4042, 54, '萝卜烧肉', '(1)猪肉洗净切成块，萝卜削皮洗净，切成滚刀块。\r\n(2)炒锅放油烧热，放入萝卜块炸至金黄色捞出，沥净油；原锅留适量的油，先放入姜块、葱段、桂皮、八角炒出香味，随即放入肉块翻炒，待肉变色后，放酱油白糖翻炒数次，下入萝卜，移入小火烧至汤汁收千，出锅装盆即可。', NULL, '萝卜要预炸，肉块要煸炒后再烧制。', '花生油，酱油、精盐、料酒、味精、白糖，姜块、葱', '猪五花肉100克，辣萝卜400克。');
INSERT INTO `recipe` VALUES (4043, 54, '土豆烧肉', '(1)猪肉洗净切成块，土豆削皮洗净，切成滚刀块。\r\n(2)炒锅放油烧热，放入土豆块炸至金黄色捞出，沥净油；原锅留适量的油，先放入姜块、葱段、桂皮、八角炒出香味，随即放入肉块翻炒，待肉变色后，放酱油白糖翻炒数次，下入土豆，移入小火烧至汤汁收千，出锅装盆即可。', NULL, '土豆要预炸，肉块要煸炒后再烧制。', '花生油、酱油、精盐、料酒、味精、白糖、姜块、葱段、桂皮、八角。', '猪五花肉100克，土豆400克。');
INSERT INTO `recipe` VALUES (4044, 54, '炒五丁', '(1)将猪肉去皮切成筷头丁，藕削皮洗净切成筷头丁，用冷水浸泡，把豆腐干和榨菜也切成筷头丁，黄豆用水浸泡后，下热水锅煮熟。\r\n(2)炒锅放油烧热，先把鲜姜米、葱丝、青蒜苗煸出香味后，随即把上述五种主料放入，加入料酒味精翻炒几下，即可出锅装盆。', NULL, '五丁要切得大小—致，黄豆煮时要加少许香料。', '花生油、酱油、料酒、味精、青蒜苗、鲜姜米、葱丝。', '猪肉120克，鲜藕200克，干黄豆30克，豆腐干100克，榨菜50克。');
INSERT INTO `recipe` VALUES (4045, 54, '粉丝肉末', '(1)将干粉丝用开水泡开后，用刀切成长段，葱、姜切成末。\r\n(2)炒锅放油烧热后，放入葱、姜末煸出香味后，将肉末下入锅中翻炒几下，然后放入料酒、酱油、白糖、味精、精盐、肉汤，粉丝下锅烧透，待汤汁将干，放入少许辣油，出锅装盘即成。', NULL, NULL, '花生油，酱油、白糖，精盐、味精、葱、姜、辣油，肉汤。', '猪肉末100克，干粉丝150克。');
INSERT INTO `recipe` VALUES (4046, 54, '莴笋里脊片', '(1)莴笋去老根刨皮切薄片，猪肉洗净同样切薄片，放入盆内，用料酒、精盐、蛋清、淀粉浆好待用，黑木耳用冷水泡发后洗净，沥去水分。\r\n(2)炒锅放油烧至六成热，放入浆好的里脊片下锅划散，捞出沥油，原锅留适量的油，先放入莴笋片煸炒后，再放精盐、黑木耳、里脊片、料酒一起翻炒几下，加味精，勾薄芡，淋上明油即成。', NULL, '莴笋含有绿叶素，所以在炒时要旺火，快炒，才能保持翠绿。', '花生油、精盐、味精、料酒、淀粉．', '莴笋400克，猪里脊肉100克，水发黑木耳10克，蛋清20克。');
INSERT INTO `recipe` VALUES (4047, 54, '丝瓜里脊片', '(1)丝瓜刨去皮洗净，对剖开切成长3厘米的片，里脊肉洗净切片，放入盆内，加精盐、料酒、蛋清、淀粉拌匀浆好待用，韭菜洗净切段，木耳：泡发后洗净待用。\r\n(2)炒锅放油烧至六成热时，放入浆好的里脊片划散，捞出沥油，原锅留适量的油，先放入韭菜煸炒再放丝瓜、木耳，加料酒、精盐、翻炒几下，然后放入里脊片，加味精，勾薄芡，再翻炒几下，淋上明油即成。', NULL, '丝瓜不宜过早去皮，炒时火要旺，动作要快。', '猪油、精盐，味精、料酒，淀粉。', '丝瓜500克，猪里脊肉100克，韭菜50克，水发黑木耳20克，蛋清20克。');
INSERT INTO `recipe` VALUES (4048, 54, '茄子烧肉片', '(1)茄子去蒂洗净切滚刀块，猪肉洗净切薄片，葱、姜、蒜均切末。\r\n(2)炒锅放油烧热，放入葱末、姜末、蒜末、肉片炒出香味后，再倒入茄子，加酱油、精盐、料酒翻炒至熟，再放味精、勾薄芡，撒上蒜末即成。', NULL, NULL, '花生油、酱油、精盐、味精、淀粉、葱、姜、蒜。', '猪肉100克，茄子500克。');
INSERT INTO `recipe` VALUES (4049, 54, '四季豆炒肉片', '(1)将四季豆的两头摘去，撕掉两边的老筋，折成小段洗净，放入开水中烫至六成熟时捞出，沥去水分，将猪肉洗净，切成薄片(或切成丝)，用精盐、蛋清、湿淀粉浆好。\r\n(2)炒锅放油烧热，放入肉片划散后捞出，沥净油，原锅内留适量的油，放入烫好的四季豆炒至七成熟时，放入肉片、料酒、酱油、精盐、味精翻炒几下，淋上明油，出锅装盆即成。', NULL, '炒四季豆时火要旺，炒的要快，保持其绿色。', '猪油(或花生油)，酱油、精盐、料酒、味精、淀粉。', '四季豆400克，猪肉100克，蛋清20克。');
INSERT INTO `recipe` VALUES (4050, 54, '粉皮肉片', '(1)将猪肉洗净后，切成薄片，粉皮切成粗条，放入开水中烫一下捞出，沥去水分，晾凉，蒜苗去老叶和根须，洗净切段待用。\r\n(2)炒锅放油烧热，放入蒜苗炸出香味后，加入肉片翻炒几下，然后加料酒、酱油，味精，精盐，将粉皮下锅翻炒至熟，淋上麻油、辣椒酱，出锅装盆即成。', NULL, '粉皮放开水锅烫时，水要多，不要将粉皮烫过头。', '花生油、精盐、酱油、味精、料酒、辣椒酱、麻油。', '猪肉100克，粉皮(鲜)500克，蒜苗50克。');
INSERT INTO `recipe` VALUES (4051, 54, '土豆炒肉片', '(1)猪肉洗净切薄片，土豆洗净去皮切成片，放入开水锅中烫一下即捞出，用冷水过凉，沥去水分，葱，姜切成丝待用。\r\n(2)炒锅放油烧热，放入葱丝、姜丝出香味后，再放肉片炒至变色，倒入土豆片、酱油、花椒面，精盐、味精、炒熟，勾薄芡，出锅装盆即成。', NULL, '土豆要切成厚薄一致的片。', '花生油、酱油、精盐、味精，淀粉、花椒面、葱、姜。', '猪肉100克，土豆400克。');
INSERT INTO `recipe` VALUES (4052, 54, '菜花炒肉片', '(1)把花菜切去花柄，切成小块，洗净放，入开水锅里烫至六成熟捞出，沥去水分，猪肉洗净切薄片，用精盐、淀粉、料酒浆好，黑木耳泡开后洗净待用。\r\n(2)炒锅放油烧热，把浆好的肉片下锅划散捞出，沥净油，原锅留适量的油，放入姜丝、葱丝，蒜片、肉片急炒至八成熟时再放入花菜、木耳翻炒，加精盐、酱油炒匀至熟，再放入味精，勾薄芡，淋上明油即可出锅装盆。', NULL, '花菜炒至八成熟即可出锅。', '花生油、酱油、精盐、味精、淀粉、葱丝、姜丝、蒜片。', '菜花500克，猪肉100克，水发木耳50克。');
INSERT INTO `recipe` VALUES (4053, 54, '蕃茄炒肉片', '(1)蕃茄洗净切成片，猪肉切片用蛋清，淀粉，酱油，胡椒粉浆好待用。\r\n(2)炒锅放油烧热，放入浆好的肉片，划散捞出，沥净油，原锅留适量的油，放入葱段，蒜片煸炒后，再放入蕃茄，肉片翻炒几下，加精盐、味精、勾薄芡出锅即成。', NULL, '炒蕃茄时，火要旺，炒要快，炒至八成熟时即可。', '花生油，精盐、酱油、味精、淀粉、胡椒粉、葱段，蒜片。', '猪肉120克，蕃茄600克，蛋清20克。');
INSERT INTO `recipe` VALUES (4054, 54, '豆芽炒肉丝', '(1)猪肉洗净切丝，放入盆内，加精盐、味精拌匀；绿豆芽洗净待用。\r\n(2)炒锅放油烧热，放入肉丝翻炒几下捞出；锅内加适量油烧热，放入绿豆芽翻炒后，加精盐，酱油、味精、白糖、姜丝、肉丝，烹些香醋，翻炒几下即可出锅装盆。', NULL, '炒绿豆芽时，火要旺，炒得要快，才能生脆．', '花生油、盐、酱油，味精，白糖、香醋。', '猪肉120克，绿豆芽750克。');
INSERT INTO `recipe` VALUES (4055, 54, '青椒南瓜肉丝', '(1)南瓜刨皮去瓤，切成细丝，青椒去蒂去籽，洗净切细丝，猪肉洗净切成丝，\r\n(2)炒锅放油烧至七成热，放入肉丝炒，加酱油、料酒翻炒出锅，原锅再放适量的油烧热，放入青椒丝煽炒几下后，随即放入南瓜丝，加精盐、味精、肉丝翻炒至熟，勾薄芡，淋上明油即成。', NULL, '南瓜要切成粗细、长短一致，火要旺，炒得要快，才能使南瓜炒得生脆。', '花生油、精盐、酱油、味精、料酒、淀粉。', '猪肉100克，青椒200克，南瓜300克。');
INSERT INTO `recipe` VALUES (4056, 54, '土豆炝肉丝', '(1)将土豆洗净，去皮，切成细丝放入碗内，加精盐拌腌，猪肉洗净切细丝，放入开水中氽一下，速用漏勺捞出，沥干水分，放入盆内；\r\n(2)将拌腌过的土豆丝挤去水分后，放在肉丝盆内；嫩姜去皮洗净切细丝，放入土豆丝和肉丝内，加精盐、味精、香醋、芥末，白糖拌匀，淋上麻油即成。', NULL, NULL, '盐、味精、香醋、白糖、麻油，嫩姜，芥末。', '猪肉150克，土豆650克。');
INSERT INTO `recipe` VALUES (4057, 54, '萝卜炝肉丝', '(1)将萝卜洗净，去头、根、皮，切成细丝放入碗内，加精盐拌腌，猪肉洗净切细丝，放入开水中氽一下，速用漏勺捞出，沥干水分，放入盆内；\r\n(2)将拌腌过的萝卜丝挤去水分后，放在肉丝盆内；嫩姜去皮洗净切细丝，放入萝卜丝和肉丝内，加精盐、味精、香醋、芥末，白糖拌匀，淋上麻油即成。', NULL, NULL, '盐、味精、香醋、白糖、麻油，嫩姜，芥末。', '猪肉150克，鲜萝卜650克。');
INSERT INTO `recipe` VALUES (4058, 54, '雪冬肉丝', '(1)猪肉洗净切丝，冬笋去壳洗净切丝，雪莱切成末，挤去水分。\r\n(2)取锅烧热放油，放入姜丝、肉丝、冬笋丝、精盐翻炒几下，放入雪里蕻炒至熟，再加味精、白糖翻炒几下，出锅装盆即成。', NULL, '雪里蕻洗净切碎后，一定要挤干水分，炒时要多放些油。', '花生油，精盐、味精、白糖、香油、姜丝。', '猪肉120克，冬笋150克，雪里蕻350克。');
INSERT INTO `recipe` VALUES (4059, 54, '白菜炒肉丝', '(1)猪肉洗净，批成薄片后，再切成丝(不宜太细)，白菜对剖后，切成丝待用。\r\n(2)炒锅放油烧热后，放入肉丝急炒，加料酒、酱油，炒至七成熟起锅。原锅加入适量的油烧至七成熟时，放入白菜丝(先茎后叶)炒后，加上述调料炒匀，随后加入肉丝和适量的水，用文火焖烂，在起锅前，勾薄芡，放入白糖吊鲜，即可出锅装盆。', NULL, '白菜的茎要切细，叶要稍粗一些，炒时火要旺，要急炒。', '花生油、酱油、白糖、精盐、味精、淀粉、料酒．', '猪肉150克，大白菜750克。');
INSERT INTO `recipe` VALUES (4060, 54, '豇豆拌肉丝', '(1)红辣椒去蒂、去籽，切细丝；猪肉洗净切细丝；豇豆摘去蒂头，洗净切成段，锅放水烧开，放入红辣椒丝和豇豆烫熟，捞出冷却待用。\r\n(2)炒锅放油烧至七成热时，放入葱段出香味，再放肉丝，加料酒、精盐、味精、酱油炒熟出锅，放入豇豆内，加精盐、味精，麻油、蒜末拌匀即可。', NULL, '烫豇豆时火要旺，水要多，才能保持豇豆的绿色。', '花生油、精盐、酱油、味精、料酒、麻油、葱段、蒜末。', '豇豆500克，猪肉120克，红辣椒20克。');
INSERT INTO `recipe` VALUES (4061, 54, '青豆炒里脊丝', '(1)将里脊批成薄片，再顺纹切成细丝，用冷水洗净，沥干水分，放入盆内，加蛋清，精盐、淀粉浆好待用，把青豆下开水锅中烫·—下捞出，沥干水分，大葱切丝待用。\r\n(2)炒锅放油烧热后，把浆好的里脊丝下锅划散捞出，沥净油，原锅留适量的油，放入大葱煸炒出香味后，下青豆、料酒、味精翻炒数次，再放入里脊丝翻炒至熟，勾薄芡，淋上明油即可出锅装盆。', NULL, '青豆下旺火锅烫熟，里脊丝下四成油锅划散。', '猪油，精盐、味精、料酒、淀粉。', '猪里脊肉150克，青豆500克，大葱50克，蛋清25克。');
INSERT INTO `recipe` VALUES (4062, 54, '葱头炒肉丝', '(1)将猪肉洗净，切成丝，放入盆内，用精盐、蛋清、湿淀粉浆好，葱头去老皮切成细丝待用，\r\n(2)炒锅放油烧热，放入浆好的肉丝划散捞出，沥净油；原锅内留适量的油，放入甜面酱，酱油、精盐、姜末，随即再倒入葱头丝、肉丝炒拌均匀，再放花椒粉、味精、勾薄芡，淋上明油、出锅装盆即成。', NULL, '烹炒时要旺火、快速．', '花生油、酱油，精盐，淀粉、味精、甜面酱、花椒粉、姜末。', '猪肉150克，葱头600克，蛋清25克．');
INSERT INTO `recipe` VALUES (4063, 55, '炸藕盒', '(1)将猪肉剁碎，加葱姜末、料酒、味精、盐，酱油、香油调和成馅，鸡蛋打在盆中，加淀粉、少许面粉和成蛋粉糊。\r\n(2)将藕去皮，切成约0．5cm厚的夹刀片，下开水锅烫至变色后捞出，每片中夹上肉馅，表面蘸上千面，挂上蛋粉糊，下油锅炸至外表金黄色，内部馅熟时即可。上桌前撒上椒盐即可。', NULL, NULL, '葱姜末、料酒、味精、盐、淀粉、酱油、香油、花椒盐。', '鲜藕250克，猪肉200克，鸡蛋50克，面粉20克。');
INSERT INTO `recipe` VALUES (4064, 55, '蒸菜卷', '(1)白菜叶洗净，用开水烫软备用。猪肉剁碎，加调料调成肉馅。\r\n(2)将猪肉馅抹在菜叶上，卷成菜卷儿，封口处抹上淀粉，摆在盘中上屉蒸20分钟取出，将汤汁沥在炒勺中上火勾上薄芡，莱卷改刀成段摆入盘中，浇上芡汁即可。', '菜卷段长短粗细均匀，色泽明亮，口味咸香。', NULL, '葱姜末、料酒、味精、盐、酱油、香油、淀粉。', '白菜叶(或包莱)200克，猪肉300克。');
INSERT INTO `recipe` VALUES (4065, 55, '瓤青椒', '(1)将青椒去籽洗净，猪肉剁成末，加各种调料拌匀成肉馅。\r\n(2)将拌好的肉馅分装在每个青椒中，摆在盘内上屉蒸约25分钟取出，沥去汤汁，用旺火勾芡后浇在青椒上即可。', '形整不碎，口味咸香。', NULL, '葱姜末、料酒、酱油、味精、盐、香油。', '猪肉300克，青椒150克(3—10个)。');
INSERT INTO `recipe` VALUES (4066, 55, '干张卷', '(1)将猪肉剁成末，加各种调料调成馅。\r\n(2)将豆腐皮上抹上肉馅，卷成卷儿，摆在盘中上屉蒸20分钟取出，沥出汤汁上火勾成芡，千张卷改刀成小段后装在盘中，浇上芡汁即可。', '千张卷粗细、长短均匀，不碎不散，口味咸香', NULL, '葱姜末、酱油、料酒、味精、盐、香油。', '豆腐皮300克，猪肉300克。');
INSERT INTO `recipe` VALUES (4067, 55, '豆腐夹', '(1)将豆腐切成约0．5cm厚、5cm长、4cm宽的大片，逐个过油炸至外表硬时捞出，晾凉备用。\r\n(2)将猪肉剁碎，加葱姜末、酱油、料酒、味精、盐调成肉馅。将炸好的豆腐片用尖刀从一侧割成两端相连的夹刀片，尔后分别装上肉馅，摆在盘中，上屉蒸20分钟取出即可。', '豆腐夹大小形状整齐，色泽浅黄，口味咸香。', '装馅时要尽量装匀，摆放时要口朝上。此菜也可改蒸为烧，即把夹好馅的豆腐摆在炒锅中加汤烧熟。', '葱姜末、酱油、料酒、味精、盐，植物油．', '豆腐500克，猪肉250克。');
INSERT INTO `recipe` VALUES (4068, 55, '炸茄夹', '(1)将鸡蛋打在盆里，加淀粉和少许面粉调成蛋粉糊。茄子去皮后剖成两瓣，每瓣切成若干个约0。6cm厚的夹刀片，猪肉剁成末，加葱姜末、料酒、味精、盐调成馅，装在每个夹刀茄片中。(2)油锅上旺火，油热后将夹上肉的茄片分别蘸上千面粉，挂上蛋粉糊，下油锅中炸至外焦脆、金黄色，内部肉馅熟时捞出。\r\n(3)将炸好的茄夹择去毛边，码在盘中，撤上椒盐即可上桌。', '色泽金黄，外焦里嫩，口味咸香。', '茄夹挂糊前要蘸匀干面粉，否则不易挂上糊，炸制过程中要注意控制火候，防止外表已上色而内部不熟，并要及时捞出油锅中的碎碴，防止把油炸黑，变苦。', '料酒、味精、盐、葱末、姜末、湿淀粉，植物油、花椒盐。', '长茄子300克，猪肉200克，鸡蛋50克，面粉10克。');
INSERT INTO `recipe` VALUES (4069, 55, '银丝双脆', '(1)将海蛰洗干净，切成细丝，浸泡数小时，以去其泥沙，然后捞出放盆内。\r\n(2)将针金菇洗净切段，放入开水中烫透，捞出沥干水，待冷却后与海蛰放在一起，加葱丝，盐，味精，料酒、香油、醋拌匀即可。', '质地脆嫩，口味清香，凉爽可口，', NULL, '精盐、味精，料酒，香醋、麻油、葱丝。', '金针菇200克，海蛰300克．');
INSERT INTO `recipe` VALUES (4070, 55, '拌肚丝', '(1)将猪肚洗净，放入开水中煮熟，捞出过凉，再切‘成细丝，黄瓜刨皮去籽，洗净切成丝，蒜苗切段待用。\r\n(2)将肚丝，黄瓜丝，蒜苗放入同一盆内，加精盐，味精，酱油、香醋拌匀即成。', NULL, NULL, '精盐、酱油、味精、香醋、香油、蒜苗。', '猪肚500克，黄瓜200克。');
INSERT INTO `recipe` VALUES (4071, 55, '黄鱼豆腐', '(1)豆腐切厚片，放入开水中烫后捞出，沥去水分，黄鱼去鳞，去鳃和内脏，洗净，剞成花刀，抹上酱油，下油锅炸成两面黄，黑木耳泡发后洗净待用。\r\n(2)炒锅放油烧热，放入葱段、姜丝煸出香味，再放炸好的黄鱼，加高汤、料酒、酱油、味精，再加豆腐片、笋片、黑木耳烧至汤汁适量，勾薄芡，撒上胡椒粉即成。', '鱼肉形整不碎，豆腐软嫩可口，口味咸香。', NULL, '猪油，酱油、味精，料酒，淀粉，高汤、胡椒粉、葱段、姜丝．', '黄花鱼600克，豆腐100克，笋片、水发木耳各适量。');
INSERT INTO `recipe` VALUES (4072, 55, '油豆腐嵌肉', '(1)油豆腐用盐开水烫过，稍捏去其油气，使其柔软。\r\n(2)将肉末、香干味、开洋末和调料放入盆内搅匀，然后嵌入油豆腐内，分放在盆内，上笼蒸透即可。', '色泽金黄，形状整齐，口味咸香。', NULL, '精盐、酱油、味精、料酒、葱末、姜末。', '油豆腐300克，肉末300克，香干末、开洋末各适量。');
INSERT INTO `recipe` VALUES (4073, 55, '炒木樨肉', '(1)猪肉切成丝，用淀粉，盐、料酒上浆；鸡蛋打在盆中加少量盐搅匀；木耳择洗干净改刀成小块，黄花菜摘去硬柄洗净切成约3cm长的段，韭菜择洗干净切成2．5cm长的段。\r\n(2)炒锅上旺火加底油，油热后将鸡蛋炒熟装在盆中；原锅上火加适量油，将肉丝下锅划出；原锅留底油，葱姜炝锅，加黄花莱、木耳、高汤、盐翻炒几下，尔后下肉丝、韭菜、料酒、白酱油、味精颠炒几下，再下入炒好的鸡蛋拌匀即可。', '色泽紫、黄、绿相间，如开花的木樨，口味咸', NULL, '猪油、白酱油、料酒，精盐、味精、淀粉、葱姜末、高汤。', '鸡蛋150克，猪肉200克，水发木耳50克，水发黄花菜100克，韭菜50克。');
INSERT INTO `recipe` VALUES (4074, 55, '炒什件', '(1)将鸡肝、鸡心、鸡肫洗净切成小片，放．入盆内，用淀粉、精盐浆好。\r\n(2)炒锅放油烧热，放入浆好的鸡什件划散捞出，沥净油；原锅留适量的油，放入红葱、姜末、蒜片煸炒出香味后，再放入鸡肫、鸡肝、鸡心、料酒、酱油、适量鲜汤，味精翻炒至熟，勾薄芡，淋上明油出锅。', '菜肴色泽深红，质地脆嫩，口味咸香。', NULL, '花生油、酱油、味精、料酒、淀粉、姜末、蒜片．', '鸡肝、鸡心、鸡肫共350克，大葱200克。');
INSERT INTO `recipe` VALUES (4075, 55, '宫保鸡丁', '(1)将鸡肉拍松切成丁，红葱剥皮洗净切成小块，红辣椒、大蒜、生姜均切成指甲片，花生米去衣炸熟待用，把鸡丁放入盆内用蛋清、淀粉浆好待用，青椒切丁。\r\n(2)炒锅放油烧热，放入鸡丁划散捞出，原锅内留适量油，先把红葱、青椒、红辣椒、大蒜、生姜煽炒出香味后，再放入鸡丁，花生米，加料酒、酱油、白糖、辣油、味精翻炒数次，，勾薄芡出锅装盆即可。', '色泽浅酱红，口味咸辣香，鸡丁软嫩，花生米', NULL, '猪油、精盐、酱油、味精、白糖、料酒、淀粉、红辣椒、辣油、肉汤、姜片、大蒜。', '鸡脯肉300克，花生米50克，大葱50克，蛋清25克，青椒20克。');
INSERT INTO `recipe` VALUES (4076, 55, '炒大肠', '(1)将猪大肠用明矾擦几下，用清水冲洗干净，放入开水锅中烫透后捞出，再用清水洗净，放入锅中，加花椒、桂皮、八角、葱段、姜片、料酒和水煮约八成熟捞出，切成片待用；葱去老皮，切成片。\r\n(2)炒锅放油烧热，先放入姜片、葱段、蒜片、花椒炒出香味后，再放入葱翻炒几次，加大肠片、白酱油、料酒，肉汤、精盐翻炒至熟，放些味精，勾薄芡即可出锅装盘。', '色泽清淡，大肠软烂荤香，无邪味．', NULL, '花生油、白酱油、精盐、味精、料酒、淀粉、花椒、姜片、葱段、蒜片，桂皮、八角。', '猪大肠500克，大葱200克。');
INSERT INTO `recipe` VALUES (4077, 55, '炒腰花', '(1)将猪腰子剖成两半，除去腰臊，用冷水泡约1小时左右，里面划成花，成麦穗形，放入盆内，加精盐、料酒、蛋清、淀粉、胡椒粉拌匀，京葱洗净拍扁切成段，黑木耳泡发后洗净待用。\r\n(2)炒锅放油烧至七成熟，放入腰花划散捞出；炒锅留适量油，放入京葱段、姜片、蒜片煸炒出香味后，再放玉兰片、木耳和调料翻炒几下，倒入腰花再翻炒儿下，勾薄芡，淋上明油即成。\r\n注：“炒猪心”也可用此法。', '腰花形如麦穗，色泽深红，口味咸香略带辣。', NULL, '花生油：精盐、酱油、味精、料酒、淀粉，红辣椒、胡椒粉、姜片，蒜片、鲜汤。', '猪腰子400克，玉兰片50克，大葱100克，黑木耳(水发)20克，蛋清25克。');
INSERT INTO `recipe` VALUES (4078, 55, '鱼香腰片', '(1)将猪腰子洗净剖成两半，除去腰臊，批成薄片，放入盆里，用料酒、精盐、淀粉浆好，荸荠洗净切片，葱切段，姜蒜、泡辣椒切成丝待用。\r\n(2)炒锅放油烧至七成热时，放入浆好的腰片划散，捞出，沥去油，原锅留适量的油，放入葱段、姜丝，蒜丝炒出香味后，再放入荸荠片、腰片、料酒、酱油、白糖翻炒几次，烹上香醋，加点味精，勾薄芡，淋上明油即成。', NULL, NULL, '花生油、精盐、酱油、白糖、香醋、料酒、淀粉、泡辣椒、葱、姜、大蒜。', '猪腰子400克，荸荠200克。');
INSERT INTO `recipe` VALUES (4079, 55, '生炒猪肝', '(1)猪肝洗净，擦干水分，切成薄片，放入盆中，加精盐、蛋清、淀粉浆好，荸荠切片，葱切段。\r\n(2)炒锅放油烧热，将浆好的猪肝下油锅划散捞出待用；原锅留适量的油，放入葱段、葱末、姜末煸炒出香味，再放入荸荠、猪肝、料酒、酱油、精盐、白糖翻炒几下，勾薄芡，淋上明油即可出锅装盆。', NULL, NULL, '花生油、酱油、精盐、味精、淀粉、料酒、白糖、葱末、姜末。', '猪肝350克，罐头荸荠100克，大葱50克，蛋清20克。');
INSERT INTO `recipe` VALUES (4080, 55, '酸辣肚丝', '(1)将熟猪肚切丝，放入开水锅烫透捞出，沥去水份，冬笋去壳，去老根洗净切丝，葱、姜、蒜、香菜均切末待用。\r\n(2)炒锅放油烧热，放入葱、姜、蒜煸炒出香味后，再加酱油、鲜汤、精拾、料酒、味精、肚丝、笋丝烧透，烹香醋，勾薄芡，淋上麻油撒上胡椒粉、香菜末出锅装盘即成。', '肚丝软烂，笋丝脆嫩，酸辣可口。', NULL, '花生油、精盐、酱油、味精、料酒、淀粉、麻油、香', '熟猪肚300克，莴笋100克。');
INSERT INTO `recipe` VALUES (4081, 55, '烩肚片', '(1)将猪肚用醋和食盐搓擦几次，用清水洗净，然后下开水锅烫—下捞出，刮去白皮，再用开水，葱、姜块煮熟，捞出后切成片，木耳泡发后，洗净待用，黄瓜洗净去瓤切成片。\r\n(2)炒锅放油烧热，放入葱末、姜末煸炒，再放入木耳，玉兰片，黄瓜片和适量鲜汤、料酒、味精、白糖、肚皮、精盐、勾薄芡，撒上胡椒粉，淋上明油即成。', '汤白肉嫩，口味清香，汤汁宽厚，属于半汤莱', NULL, '花生油，精盐、味精、料酒、白糖、鲜汤、胡椒粉、葱、姜。', '猪肚500克，黄瓜100克，木耳(水发)20克，玉兰片50克。');
INSERT INTO `recipe` VALUES (4082, 55, '熟炒肚丝', '(1)将熟猪肚切成丝，葱去老叶，去根洗净切丝；红辣椒去蒂、去籽洗净同样切细丝。\r\n(2)炒锅放油烧热，放入葱丝、姜丝、红辣椒丝炒出香味后，再放入肚丝、料酒，酱油、味精翻炒几次，勾薄芡，淋上花椒油出锅即成。', '色泽红绿相间，口味咸香。', NULL, '花生油、酱油、味精、淀粉、料酒，姜丝、花椒油。', '熟猪肚300克，大葱100克，红辣椒50克。');
INSERT INTO `recipe` VALUES (4083, 55, '葱爆羊肉', '(1)将羊肉切片，放入盆内加料酒、精盐、蛋清、淀粉浆好后待用，京葱去根去老皮洗净切成马蹄块。', NULL, '制作过程中，火力要猛，动作要快。', '花生油、酱油、精盐、味精、料酒、香油、醋、蒜末，姜丝、淀粉。', '净羊肉400克，大葱100克，蛋清20克。');
INSERT INTO `recipe` VALUES (4084, 55, '牛肉粉丝', '(1)将牛肉洗净切成细丝(要横切)放入盆内，加精盐、蛋清、酱油、淀粉拌腌，粉丝切成长段，青蒜去皮去根洗净，切成细丝，泡辣椒去蒂去籽洗净切丝。\r\n(2)炒锅放油烧热约六成热时，将拌腌的牛肉丝下锅划散，捞出沥油，原锅留适量的油，下姜丝一炒，放入粉丝、泡辣椒、高汤、酱油、精盐、白糖、料酒翻炒几下，再放入牛肉丝，加香醋、味精、青蒜翻炒，勾薄芡，淋上麻油即可出锅装盆．', NULL, NULL, '花生油、酱油、’精盐、料酒，白糖、味精、香醋、淀粉，香油、高汤、姜丝、泡辣椒。', '牛肉350克，水发粉丝100克，青蒜50克，蛋清20克。');
INSERT INTO `recipe` VALUES (4085, 55, '猪肉烧海带', '(1)将猪肉刮洗干净，切成小方块，海带浸泡3～4小时后，用清水洗净切成片，葱切段，姜切块。\r\n(2)锅烧开水，将猪肉、海带分别下锅烫一下捞出，沥去水分，炒锅放油烧热，放入葱段、姜块、桂皮、八角煸出香味盾，再将猪肉下锅炒透，加料酒、酱油、白糖烧至七成熟时，再放入海带烧至酥烂，加精盐、味精、葱翻炒几下，待汤汁收干，即可出锅装盆。', '色泽紫红，口味咸香味带甜，质地酥烂．', NULL, '花生油、酱油、精盐、味精、料酒、白糖、桂皮、八角、葱、姜。', '猪肉400克，海带(水发)200克。');
INSERT INTO `recipe` VALUES (4086, 55, '酱爆三丁', '(1)将猪肉切成丁，用淀粉、料酒、蛋清、盐上浆，土豆去皮后切成丁，青椒去籽后切成丁。\r\n(2)油锅上旺火，待油烧至五成热时，将肉丁上锅划出，原油锅烧至八成热时，将土豆丁下锅炸至表面黄色时捞出。\r\n(3)炒锅上旺火，加底油，下青椒炒至变色后盛出，炒锅另加底油，油热后下面酱、葱姜末炒出香味，下肉丁炒几下，加料酒、酱油，土豆、青椒丁，精盐和少量高汤，待土豆入味后加味精，用淀粉勾芡即可出锅。', '肉丁酱红色，土豆、青椒黄绿相间，酱香味浓', NULL, '花生油、酱油、精盐、味精，淀粉、甜面酱、葱末，', '猪肉300克，土豆100克，青椒100克，蛋清25克。');
INSERT INTO `recipe` VALUES (4087, 55, '鲜肉莱包', '(1)将包心莱洗净，取嫩莱叶，用开水烫一下，捞出，用冷水过凉，沥干水份，把包心菜叶修整后，摊在案头上。\r\n(2)将荸荠剁成末，和肉末一起放在盆内，加料酒，精盐、味精、葱末、姜末拌成馅，将肉馅放在包心莱叶上，然后包成10只莱包，用蛋清，淀粉调和，糊在莱包口上，封住口，以防破裂。\r\n(3)煎锅放油烧热，把莱包煎成两面黄，煎好后加少许鸡汤和精盐，味精，待汤汁收干，装在盆内即可。', '色泽清淡，质地酥烂，口味鲜香。', NULL, '猪油，精盐、料酒、味精、淀粉、葱末、姜末。', '嫩包菜心(或大白菜叶)200克，猪肉末200克，罐头');
INSERT INTO `recipe` VALUES (4088, 55, '肉片炒蒜苔', '(1)猪肉洗净切成片，加适量的酱油、精盐、淀粉拌匀，蒜苔洗净切段待用。\r\n(2)炒锅放油烧热，放入肉片翻炒至熟起锅装盆，原锅内再加适量油烧热，放入蒜苔，加精盐翻炒至七、八成熟时，倒入肉片炒几下，再放入适量酱油，勾薄芡，加些味精即成。', '色泽翠绿，肉片软嫩，蒜苔脆嫩，口味咸香。', NULL, '花生油、精盐、酱油、味精、淀粉。', '猪肉300克，蒜苔200克。');
INSERT INTO `recipe` VALUES (4089, 55, '麻辣肉丝', '(1)将肉洗净切细丝，加酱油、湿淀粉浆好，莴笋去叶，去皮洗净，切成细丝，干红椒洗净去蒂切末；青蒜洗净去根切成末；花椒用刀压成细末。\r\n(2)炒锅放油烧热，放入浆好的肉丝划散，再放莴笋丝同炒，加高汤、酱油、干红椒末颠炒几下，出锅前撒些青蒜、花椒末、勾薄芡，出锅装盆，淋上明油即成。', '口味麻辣咸香。', NULL, '花生油、酱油、高汤、淀粉、干辣椒、大蒜、花椒。', '猪瘦肉300克，莴笋丝200克。');
INSERT INTO `recipe` VALUES (4090, 55, '肉丝炒韭菜', '(1)猪肉洗净切细丝；韭菜洗净切成段，下开水锅烫后捞出，沥干水分待用。\r\n(2)炒锅放油烧热，放入肉丝加料酒炒至七成熟时，再放入韭菜翻炒至熟，加酱油、精盐、味精再翻炒—下即可出锅装盆。', '色泽白绿相间，口味咸香，韭菜嫩而不生，无', NULL, '花生油、酱油、精盐、味精、料酒。', '猪肉300克，韭菜200克。');
INSERT INTO `recipe` VALUES (4091, 53, '红肠', '(1)将猪肉绞成泥装盆内，加精盐、蛋清、淀粉、胡椒粉，味精、硝拌匀，灌入泡好的肠衣内，扎好口，分扎成节。\r\n(2)汤锅加冷水，放上灌好的肠子，用旺火烧开后立即换微火煮至熟时捞出，待冷却后在表面滚上淡了的食红色，再涂上香油即可，拼盘上桌。\r\n莱肴质量：色泽浅红发亮，口味咸香，软硬适度，形整不破。', NULL, NULL, '淀粉40克，精盐10克，胡椒粉1克，味精1克，硝0.5克。', '猪肉500克，肠衣10克，蛋清50克。');
INSERT INTO `recipe` VALUES (4092, 53, '糖醋鱿鱼卷', '(1)将发好的鱿鱼剞上交叉花刀，然后改刀成象眼块。冬笋切象眼片，葱切指甲片，姜切末。\r\n(2)炒勺内加半锅油，烧热后放入切好的鱿鱼冲炸一下倒入漏勺，控净油备用。\r\n(3)炒勺上旺火，加底油，烧至油热后用葱姜炝锅，加鲜汤、糖醋、料酒、盐、味精，用淀粉勾芡，下鱿鱼卷搅匀即可出锅．\r\n菜名:两做大虾\r\n烹调方法：炸、烧\r\n原料：大虾500克，鸡脯肉50克，肥膘肉10克，芝麻5克，蛋清25克，生莱叶20克。\r\n调料．精盐、料酒、白糖、味精、葱、姜、淀粉。\r\n(1)将大虾收拾干净，从中间剁成两段，葱切段，姜切片。\r', NULL, NULL, '白糖、醋、料酒、盐、葱、姜、味精、鲜汤、淀粉。', '水发鱿鱼300克，冬笋20克。');
INSERT INTO `recipe` VALUES (4093, 53, '虎皮蛋', '(1)将鸡蛋煮熟，用冷水浸凉后剥去皮，滚上面粉，过烈油炸至外皮脆硬、形似虎皮时捞出。\r\n(2)炒锅打底油，上旺火用葱姜炝锅，烹料酒加高汤及其它调料，再加炸好的鸡蛋，用文火烧至鸡蛋入味、汤汁浓时即可．', '色如虎皮，外形整齐，咸香可口。', NULL, '葱段、姜片、花椒、大料、精盐、料酒、味精、酱油、白糖、面粉、高汤。', '鸡蛋或鸭蛋10个(约500克)。');
INSERT INTO `recipe` VALUES (4094, 53, '酥鲫鱼', '(1)油锅上旺火，将鲫鱼下锅炸至皮硬时捞出。\r\n(2)炒锅内撒上葱段、姜片、蒜瓣，码上一层负，再撒上一层葱、姜、蒜，尔后又码鱼，如此码几层把鱼码完，加上酱油、糖、醋、盐、香油，高汤，使汤能淹没鱼，锅上旺火，烧开后调好口味，用小火烧约二小时，至鱼骨酥烂时即可。', '骨酥软而鱼不碎，咸香可口．', NULL, '糖，醋，料酒、酱油、葱、姜、蒜，大料、盐、香油，高汤。', '鲫鱼750克。');
INSERT INTO `recipe` VALUES (4095, 53, '面托虾', '(1)将河虾剪去虾须，加盐、味精、料酒、葱姜块拌匀腌半小时，拣出葱姜块，加上小苏打、面粉、鸡蛋搅匀备用。\r\n(2)油锅上旺火，烧至六成热时，将虾撒入锅中9炸至虾变色，外焦脆而里熟时捞出装盘即可。', '色泽金黄，外焦里熟，咸香可口。', NULL, '盐、味精、料酒、葱姜块、油．', '鲜河虾250克，鸡蛋50克，面粉100克。');
INSERT INTO `recipe` VALUES (4096, 53, '姜汁螃蟹', '(1)将活蟹用清水泡几小时，并换几次水，让其吐出些腹中的污物。\r\n(2)酱油、醋、姜末、味精放在碗中调成姜汁备用。\r\n(3)将螃蟹洗净放入清水锅中，加盐，花椒、大料、葱段、姜块，上火煮约半小时后取出装盘，带姜汁一起上桌。', '螃蟹红亮，肉质鲜嫩，吃时蘸姜汁，口味鲜香', NULL, '生姜末，醋、酱油、味精、盐、花椒、大料、葱段，姜块。', '活河蟹1000克(八只)。');
INSERT INTO `recipe` VALUES (4097, 53, '蒸蛋卷', '(1)将肉剁成泥，加葱姜末、料酒等调料调和均匀备用。\r\n(2)将鸡蛋打在碗中，加少许盐和少量淀粉搅均匀。炒勺上中火，加几勺油，烧至六成热后将油倒出，用擦布(或肥肉)将锅擦一遍，打一小勺鸡蛋液(约一个鸡蛋的量)倒入炒勺，吊成蛋皮，如此共吊4张蛋皮。\r\n(3)将蛋皮平铺在菜墩上，放上肉馅，卷成蛋卷(共卷4个)，封口处抹上少许淀粉，摆入平盘上屉蒸约20分钟，取出晾凉，切成片拼盘即可。', '色黄、味咸香，造型美观。', NULL, '葱姜末、料酒、味精、酱油、盐、淀粉、香油。', '鸡蛋200克，猪肉300克。');
INSERT INTO `recipe` VALUES (4098, 53, '皮冻', '(1)将肉皮下开水锅烫至变色捞出，冷水洗一遍，捞出后剔去肥肉，刮净毛根，切成条，下清水锅，加葱、姜、料酒、盐、酱油和用纱布包好的花椒、大料，上旺火煮开，撇去浮沫，用小火煮至软烂为止。\r\n(2)将煮好的肉皮汤中的葱、姜、纱布包拣出，原汤倒在盆里，放在荫凉处(环境温度要在20℃以下)。或放在冰箱冷藏室(或冷库的冷藏间)，数小时后，肉皮和原汤凝结在一起即可。\r\n(3)将凝结后的肉皮冻切成片或块拼在盘中浇』二醋、酱油、香汕，撒上葱蒜末即可。', '色泽透亮，如不加酱油则如水晶一般，清凉脆', '制作时要掌握好加水量，一斤肉片的制成品一般不超过3斤。成品不能遇热，否则会化成水。', '葱段、姜片、蒜瓣、花椒、大料，料酒，味精，盐，酱油、香油，醋。', '鲜肉皮250克(一盘量)。');
INSERT INTO `recipe` VALUES (4099, 53, '红烧鳝段', '(1)用钉子把鱼头钉在菜案上，用尖刀划开肚皮，取出五脏，去头去尾，洗净，切成1．5寸的段，放入开水锅内烫去粘液，捞出放在碗内，用酱油腌一下，待用。\r\n(2)肥肉切成锯齿花刀片，冬菇切抹刀片，香菜切成段。\r\n(3)炒勺放入食油1斤左右，上火烧至八九成热，将鳝鱼段放入，烧至上色，捞出。\r\n(4)炒勺放入食油半两、上火，将肥肉片、葱段、姜片、蒜瓣放入煸一下，放酱油、料酒、味精、胡椒粉，糖、盐、高汤，下入鳝段，用文火烧至汤浓入味后，撒香菜即可出锅。', '色浅红，绿色点缀，味鲜美。', NULL, '食油、酱油、料酒、味精、糖、精盐、胡椒粉、湿团粉、葱、姜、蒜、高汤。', '鳝鱼500克，肥肉20克，玉兰片、水发冬菇各20克，香菜30克。');
INSERT INTO `recipe` VALUES (4100, 53, '四喜丸子', '(1)肥瘦肉剁成末放一小盆内，打入鸡蛋、加葱姜末、料酒、盐，酱油、香油，用手搅至起粘有劲时，把海参丁、笋丁也下入拌匀。要掌握好肉末的咸，鲜味，接着把合好的馅分成四个相等的量，用两手团成大丸子。\r\n(2)鸡蛋加浓度稠的水团粉，合成浓糊，同时将油锅烧开，立即把丸子拿在手里，涂抹上均匀鸡蛋糊，急速下油锅炸成金黄色立刻捞出。\r\n(3)将丸子装入蒸碗，浇点毛汤，料酒、味精、盐、酱油，尝好味，上屉蒸透，扣在碗内即成。', '色泽金黄，酥嫩鲜香，入口即散。', NULL, '团粉、酱油、料酒、味精，盐、葱、姜末、香油，毛汤、花生油。', '猪肥瘦肉300克，海参丁20克，冬笋丁20克，鸡蛋80克。');
INSERT INTO `recipe` VALUES (4101, 53, '黄焖鸡', '(1)鸡整理干净，加葱姜块、料酒、盐、酱油上屉蒸熟。\r\n(2)水香菇、玉兰片切薄片，和菜心分别用开水烫一下备用。\r\n(3)下屉后把汤滗在勺内，鸡扣在盘内，把菜心码在鸡上。另外，把香菇、玉兰片放入汤内，上旺火烧开，调一下色味，用水团粉勾薄芡，淋些五香油，浇在鸡身上即成。', '色泽黄亮，形整软烂，味咸香。', NULL, '葱、姜、盐、料酒、酱油、味精、香油、高汤。', '嫩母鸡750克，菜心50克(两个)，水发冬菇30克，玉兰片30克。');
INSERT INTO `recipe` VALUES (4102, 53, '红扒熊掌', '(1)冬菇、冬笋、火腿切成片。，\r\n(2)初步加工好的熊掌，放盆内加水，放到蒸笼中蒸八成熟取出，待凉后，由熊掌的掌背处开四道口，剔出骨，再将熊掌放在碗内(掌面朝上)加鸡汤、酱油、白糖、花椒水，味精、葱段、姜块，再放屉内蒸烂后，取出葱段，姜块。\r\n(3)锅内放猪油1两，油热时，添少许鸡汤、放入冬笋、冬菇、火腿片，再把蒸烂的熊掌拖入锅内，煨5分钟，勾芡淋明油翻个出勺即成。', '熊掌形状整齐、质地软烂，色泽红亮，口味咸', NULL, '酱油、猪油、鸡汤、味精、白糖、葱、姜块、花椒水。', '熊掌1000克(一只)，冬笋30克，冬菇30克。');
INSERT INTO `recipe` VALUES (4103, 53, '芙蓉海参', '(1)海参切一字刀，用开水氽过，鸡脯肉去净筋，用刀背剁成细泥，放入碗内，加少许凉清汤、姜汁解开，放入蛋清，用筷子搅匀，再加入精盐、味精、湿淀粉，用筷子再搅匀，火腿切小象眼片，葱切段，姜切片。\r\n(2)勺内放入少许油，上火烧热，下入葱姜，炸出香味，加入清汤、精盐、料酒、味精，把海参放入见开，在文火上煨2～3分钟，上旺火把海参捞出，挑出葱姜，控净水份，盛于大平盘中心。\r\n(3)勺内放入大油，上火烧至五六成热，把调好的鸡茸倒入勺内，用手勺轻轻地推一推，片刻，将油滗出，即成为滑好的芙蓉鸡片，再用开水汆—下，迅速把', '色泽黑白相映，质地软嫩，口味鲜香。', NULL, '精盐、料洒、味精、清汤、猪油、淀粉、鸡油、葱，姜汁。', '水发海参500克，鸡脯肉100克，蛋清250克，火腿50克。');
INSERT INTO `recipe` VALUES (4104, 53, '红烧干贝', '(1)干贝用温水洗干净，放于大碗内，加上开水，上屉蒸烂(约2小时左右)，下屉后将原汤滗出留用，拨出干贝硬肌，仍用原汤浸泡。\r\n(2)勺内放入食油，上火烧热，下入葱、姜米，炸出香味，继下清汤、酱油、料酒、味精、食糖和少许干贝原汤，另将玉兰片、水发香菇片(用开水汆过)和干贝一起下入，见开，撇去浮沫，用水团粉勾芡，加入葱，姜，油，盛于盘内即成．', NULL, NULL, '葱姜末、酱汕、料酒、味精、白糖、淀粉、清汤、食油、葱油．', '水发干贝300克，玉兰片30克，水发香菇30克。');
INSERT INTO `recipe` VALUES (4105, 53, '红烧海参', '发好的海参，坡刀切成—字条，用开水汆下下，勺内加入清汤、料酒、姜水、海参、上火煨‘煨，将汤滗出不用，另在勺内加入清汤、料酒、酱油、味精、食糖、糖色、盐少许，调好味，把海参下入，见开后，，撇去浮沫，用水团粉勾成浓汁，加入葱姜油，倒入盘内即成。', '海参软糯而不碎，口味鲜香。', NULL, '酱油，料酒、味精、食糖、糖色、清汤、姜水、精盐，葱油。', '水发海参500克。');
INSERT INTO `recipe` VALUES (4106, 53, '红烧甲鱼', '(1)将甲鱼宰杀后，放入开水锅中，烫过捞出，放在案板上刮掉裙边的绿苔(黑皮)，揭开盖子，去五脏，洗干净，剁成3cm见方的块，并把甲鱼的爪剁去，然后，放入开水锅中“飞水”捞出择去黄油(甲鱼油)，洗净待用。\r\n(2)猪肉切片，玉兰片切片，水发冬菇切抹刀片，蒜切段，姜切片、蒜切末。\r\n(3)汤勺放入底油，上火，把葱段、姜片放入煽过，将甲鱼放入，加料酒、精盐、毛汤，在温火上炖至熟。\r\n(4)炒勺放入底汕，上火烧至五六成热，把蒜米放入，煸出香味，把肉片放入煸炒，加酱油、料酒、味精、糖、胡椒粉，把炖好的熟甲鱼放入，再', '色泽微红，肉质软烂而不碎，口味咸鲜香．', NULL, '酱油、料酒、味精、精盐、胡椒粉、水团粉、糖、葱、姜、蒜、食油、毛汤。', '活甲鱼500克，肥肉30克，水发玉兰片20克，水发冬菇20克。');
INSERT INTO `recipe` VALUES (4107, 53, '焦溜丸子', '(1)肉末放一碗内，放入少许黄酱、盐、料酒、水团粉，用力搅至起粘有劲，然后挤成20个左右丸子。\r\n(2)用酱油、料酒、味精、水团粉，毛汤、少许醋、姜水，葱丝，青蒜对成碗欠。\r\n(3)炒勺上旺火，放入半勺炸油，烧八九成热时下入丸子，抽温高时，可端离片刻，边炸边用漏勺拍一拍，使丸子外表松散，易于传热，丸子一漂起即成，倒入碗欠炒熟，下丸子颠翻几下，淋入少许明油即成。', '色泽浅红发亮，口味咸香。', NULL, '花生油、黄酱、酱油、料酒、淀粉、味精、高汤、葱丝、姜水、青蒜、醋。', '肥瘦猪肉末300克，鸡蛋50克。');
INSERT INTO `recipe` VALUES (4108, 53, '炸荷包蛋', '(1)油锅内注入花生油，烧热，手勺内加底油也上火烧热，把鸡蛋打在手勺内(不要把蛋黄弄破)撒些细盐，放在油锅内煎，等下面煎到快定形时，用筷子在一边夹出一道印，好似荷包口，再加入一些热油，使上面也凝固。\r\n(2)倒入大锅中，再炸一会，成金黄色，即可捞出．\r\n(3)其它鸡蛋均照此办法做完，码入盘中即可。', '色泽金黄，外焦里熟。', NULL, '花生油、精盐。', '鸡蛋500克(8～10个)。');
INSERT INTO `recipe` VALUES (4109, 53, '砂锅鱼头', '(1)鱼头去鳃，劈开洗净；冬笋、火腿切成片，葱切段，姜切片水香菇洗净。\r\n(2)勺内放大油，油热，放入鱼头，煎成两面金黄色，再下入葱、姜稍煎，然后烹入料酒，放入高汤，下入盐，味精，调好口味，开锅后，盛入砂锅内，再放冬笋片、火腿、香菇、胡椒面，海米，旺火见开后，移至微火炖半个小时左右，鱼头烂、汤汁浓，再下青蒜段，以少许烈油淋在青蒜段上，即可上桌。', '汤色乳白，口味浓香。', NULL, '料酒、精盐，味精、大油，胡椒粉、青蒜段，葱、姜，', '胖头鱼鱼头1000克，冬笋25克，香菇25克，火腿25克，海米5克。');
INSERT INTO `recipe` VALUES (4110, 53, '砂锅鱼翅', '(1)冬菇、玉兰片、火腿等均切片，豆苗择洗：厂净。\r\n(2)勺内下入猪油，上火烧热，下入葱、姜，炸出香味，冲入清汤，下入味精、料酒，精盐，用旺火将汤烧开，捞出葱姜，接着下入冬菇、玉兰片、火腿，把汤倒入炒锅内，将鱼翅慢慢池推入砂锅中，移小火上煨20分钟左右，淋少许鸡油，撒上豆苗，随两碟香菜末一起上桌。', '汤清味鲜，咸香可口．', NULL, '清汤1000克，味精、料酒、精盐、大油、鸡油各适量。', '水发鱼翅750克，水发冬菇50克，玉兰片50克，火腿50克，豌豆苗10克，香菜末5克。');
INSERT INTO `recipe` VALUES (4111, 53, '炝腰花', '(1)腰子片成两半，去掉腰心(尿臊)，切成丁字花刀再切成块，用开水抄透投凉，控于水分。\r\n(2)冬笋、黄瓜切片、姜切细丝。\r\n(3)把腰花、冬笋、瓜片、姜丝放在一起，用精盐、味精、绍酒、花椒油拌匀装盘即成。', '质地脆嫩，味道鲜美。', NULL, '精盐，姜、料酒、花椒油、味精。', '猪腰子400克，冬笋30克，黄瓜30克。');
INSERT INTO `recipe` VALUES (4112, 53, '玉扇干贝', '(1)净鱼肉放菜墩上，用刀背砸成极细的茸泥，放盆中加入料酒、葱姜水，鸡蛋清、味精、水淀粉、熟猪油、顺向用力搅打成鸡酿子。\r\n(2)水海参、水鱿鱼、水香菇、水干贝(—半)均田成米粒丁，炒成三鲜馅料。\r\n(3)干贝壳洗净，用开水烫—下；青菜叶洗净用开水烫过，剪成和贝壳一样大的片，铺在贝壳里，给菜叶上放少许三鲜馅料，再放——团鱼酿子，抹平成抹子形状，将干贝揉散贴在上方做扇缨，两边各贴一条香菇丝做扇柄；中间用香菜和火腿片做成花草图案。\r\n(4)将做好的扇贝在盘中仍摆成扇形，连盘上笼蒸五分钟取出。\r\n(5)净锅置火', '形如玉扇，质地软嫩，清爽鲜香。', NULL, '精盐、料酒、味精、淀粉、熟鸡油、熟猪油、葱姜水。', '水发干贝75克，净鱼肉100克，熟火腿25克，水发海参25克，水发鱿鱼25克，水发香菇50克，蛋清20克，青菜叶50克，干贝壳12个。');
INSERT INTO `recipe` VALUES (4113, 53, '菊花干贝', '(1)鸡脯肉用刀背砸成泥，放盆中加入鸡蛋清、料酒、湿淀粉、味精、葱姜水、熟猪油搅打制成鸡酿子。\r\n(2)绿菜叶用开水烫过，和火腿、香菇分别切成末。\r\n(3)将发好的干贝用开水烫过，滗去水，揉散成长丝状(要散而刁<断)，竖直整齐地粘贴在抹过油的小酒盅沿周围成花瓣形，然后将鸡酿子挤成小丸子放入酒盅内，压住干贝的四分之一，使四分之三的干贝露在鸡酿子外面，中间撒上火腿末、香菇末、绿菜末做花蕊，共制成20个菊花干贝生坯待用。\r\n(4)将做好的菊花干贝生坯连酒盅一起上笼蒸五分钟取出，脱入盘内。\r\n(5)净锅置火。上，', '汤汁清亮而味纯，形状逼真而鲜嫩，随汤浮动', NULL, '味精、淀粉、清汤、精盐、料酒、熟猪油、葱姜末．', '水发干贝60克，鸡脯肉100克，香菇5克，熟火腿5克，绿菜叶5克，鸡蛋清20克，豌豆苗5克。');
INSERT INTO `recipe` VALUES (4114, 53, '松鼠桂鱼', '(1)将鱼去鳞、去鳃，剖腹去内脏、洗净。从胸鳍斜切下鱼头，在鱼头下巴处剖开，用刀面轻轻拍平。鱼身用刀沿脊骨两侧平片至尾部(不要片断鱼尾)，斩去脊骨和鱼刺肋骨。鱼皮朝下，在肉面削上荔枝花刀(刀距约1厘米)，深至鱼皮，用料酒，精盐在碗内调匀，抹在鱼头和鱼肉上，待入味再滚蘸上干淀粉，并用手提起鱼尾抖去余粉。\r\n(2)将蕃茄酱放入碗内，加入清水(100克)、绵白糖、香醋、料酒、湿淀粉、精盐搅拌成调味芡汁。冬笋、水香菇均切成丁。\r\n(3)净锅置旺火上，添入多量油，烧六成热，将两片鱼肉翻卷，翘起鱼尾成松鼠形，一手提起', '此菜外脆内松，甜中带酸，微咸且鲜，味香可', NULL, '料酒、精盐、香醋、绵白糖，蕃茄酱、蒜末、葱末、姜末、淀粉、植物油。', '鲜桂鱼一尾750克，虾仁20克，冬笋20克，水发香菇20克，青豆20克。');
INSERT INTO `recipe` VALUES (4115, 53, '糟溜鱼片', '(1)将黑鱼开膛去内脏，从脊背下刀，剔去脊梁骨，使鱼身成两大片，再用刀剔去鱼皮，最后用斜刀法将鱼肉片成薄片，用清水漂洗干净，加料酒、精盐、鸡蛋清、湿淀粉上浆。玉兰片切成片，木耳撕开。\r\n(2)用清汤、香糟汁、白糖、精盐、味精、湿淀粉在小碗中\r\n兑成芡汁。\r\n(3)油锅上火，加半锅油，待烧至四成热时，把浆好的鱼片撒入锅中划至熟时捞出。原锅留底油，投入葱姜末炝锅，加玉兰片、木耳煽炒几下，倒上兑好的芡汁，开锅后下鱼片，轻轻颠翻几下即可出锅。', '鱼片洁白，质地软嫩，滋味鲜美。', '上浆时和过油时要轻抓轻搅，以防鱼片碎烂。', '香糟汁、白糖、味精、熟猪油、湿淀粉、精盐、葱姜末、清汤。', '黑鱼500克，水发木耳20克，水发玉兰片20克，鸡蛋清20克。');
INSERT INTO `recipe` VALUES (4116, 53, '清炖甲鱼', '(1)将甲鱼宰杀后，砍去头和爪尖，用沸水烫一下，刮去背上的黑膜，下冷水锅上火煮到四成熟，取出去掉背壳和内脏，清洗干净，用刀剁成三大块。鸡腿用开水氽一遍，火腿、香菇均切成长方片。\r\n(2)煮锅上火，加入清水1.5千克，放入甲鱼、鸡腿、葱、姜、蒜、料酒，旺火烧开，打净浮沫，转用小火烧至八成熟时，加入火腿、香菇、青菜心再炖透。\r\n(3)将甲鱼块捞出盛入汤碗中，火腿，香菇、菜心摆在甲鱼上面，捞去汤中的鸡腿；葱、姜，加入精盐、胡椒粉、味精，倒入汤盆中，淋上鸡油即可。', '汤清味鲜，肉质软烂。', NULL, '葱、姜、精盐、味精、胡椒粉、料酒、蒜瓣、鸡油。', '甲鱼750克(一盘量)，鸡腿2个约100克，火腿15克，');
INSERT INTO `recipe` VALUES (4117, 53, '辣子鸡丁', '(1)鸡肉去骨，浅剞刀纹，再用刀尖轻轻地斩一下(不能斩透)，切成约1．5cm见方的丁，用盐、料酒拌一下，再用蛋清，湿淀粉浆上。\r\n(2)青辣椒(红、绿各半)去籽洗净，切成1cm见方的丁。葱切丁，姜、蒜去皮切成厚指甲片。用盐、酱油、料酒、味精、高汤、淀粉兑成芡汁。\r\n(3)炒锅加半锅油，上旺火烧至五、六成热时下鸡丁划至八成熟且散开后捞出。\r\n(4)炒锅留底油上旺火，烧至油热时下葱、姜、蒜炝锅，再下豆瓣辣酱，炒出红油后下青椒丁炒熟，倒入兑好的芡汁，开锅后下鸡丁，淋香油颠匀即可。', '色泽红亮，鲜嫩爽口，香辣味浓。', NULL, '精盐、料酒、葱、姜、蒜，酱油、味精、豆瓣辣酱、葱，湿淀粉、熟猪油。', '仔鸡肉400克，大青椒60克，鸡蛋清20克。');
INSERT INTO `recipe` VALUES (4118, 53, '芙蓉鸡片', '(1)鸡脯肉去净筋膜，用刀背砸成泥。葱、火腿、冬笋切成小象眼块。豌豆苗洗净。\r\n(2)鸡泥放大碗中加上用姜、葱泡成的水调成稀糊，随即加入鸡蛋清、盐、味精、湿淀粉，搅匀备用。\r\n(3)用盐、味精、料酒、鲜汤、湿淀粉在碗中对成调味芡汁。\r\n(4)炒锅上火，添入少半锅油，待油热后将油倒出，趁热将锅中加入凉猪油，待油烧至三成热时，改用小火，用手勺盛半勺鸡泥甩入油锅中，并不停地晃动油锅，使之慢慢结片成熟并浮出油面，立即倒入漏勺中。这样反复几次把鸡泥都划成鸡片。\r\n(5)锅留少许底油，烧热后下入姜末炝锅，随即下火腿、', '鸡片白亮，盘中红绿相映，质嫩味鲜，营养丰', '划鸡片时要保证热锅凉油，鸡泥下锅后不可搅动，让其自动浮出油面。', '熟猪油、葱、姜末、湿淀粉、精盐、味精、料酒、清汤。', '鸡脯肉150克，鸡蛋清80克，熟火腿20克，豌豆苗10克，冬笋15克。');
INSERT INTO `recipe` VALUES (4119, 53, '葱爆羊肉', '(1)将羊肉切成约4cm长、2cm宽的薄片，大葱切成3cm长的马蹄片，二者同放碗中，—用酱油、精盐拌匀。\r\n(2)炒锅上旺火，加底油，烧热后将拌好的羊肉、大葱投入，急速爆炒，并用手勺搅转颠翻，使其受热均匀。炒至葱软肉熟时烹料酒、加香油颠匀后出锅。', '羊肉软嫩，葱味浓郁，鲜香爽口。', '大锅大量分批制做时，不可提前大批量拌入调料，应随炒随拌。', '精盐、酱油、料酒、植物油、香油。', '羊后腿肉400克，大葱100克。');
INSERT INTO `recipe` VALUES (4120, 53, '油爆双脆', '(1)肚头洗净，剥去筋膜外皮成肚仁，先剞一遍蓑衣花刀，再切成3cm见方的块。鸡肫用刀片去硬皮，剞上荔枝花刀，然后一并放入5％的碱水中浸泡二小时，取出后用清水将碱水漂净，再放清水盆中加葱、姜、料酒浸泡约一小时，取出后沥干水分，用蛋清上浆。\r\n(2)将玉兰片切成小象眼片，木耳撕成指甲片。将葱，姜、蒜末。料酒、盐、味精、湿淀粉—起放在碗中，加少量鲜汤兑成芡汁。\r\n(3)炒锅上旺火，添少量底油，烧至八成热时，将肚头和鸡肫下油锅划熟捞出，撒上胡椒粉。\r\n(4)炒锅上旺火，加少许底油，下入玉兰片、木耳，倒入兑好的芡汁', '色泽银黄、质地脆嫩，口味鲜香。', NULL, '精盐、味精、料酒、葱末、姜末、蒜末、湿淀粉、胡椒粉、熟猪油。', '猪肚头200克，鸡肫200克，水发玉兰片5克，水发木耳5克，蛋清20克。');
INSERT INTO `recipe` VALUES (4121, 53, '珍珠丸子', '(1)将猪瘦肉剁成细茸，肥肉用开水烫熟后切成黄豆大小的丁，荸荠去皮切成小丁，同放在一盆中，加精盐、料、酒、味精、葱姜末、湿淀粉搅拌均匀。\r\n(2)江米提前淘洗干净，用温水浸泡约二小时捞出，沥干水。\r\n(3)将肉馅挤成杨梅大小的肉丸子(每50克肉约7～8个)，逐个放在江米中滚动一遍，使其周身粘满江米，摆在盘中，每个丸子周围要有一定的空隙，上屉蒸约十分钟后取出，整齐地摆在上菜盘中即可。', '色泽洁白如玉，肉丸软糯荤香，丸子上竖起的', '丸子滚糯米后，不能用手团搓，防止米粒陷入肉馅中而使蒸熟后的米粒竖不起来。要轻轻将滚上糯米的丸子摆在盘中丸子馅的口味要调得稍重些。', '精盐、味精、料酒、葱姜末、湿淀粉。', '猪瘦肉320克，猪肥肉80克，荸荠50克，海米15克，江米150克。');
INSERT INTO `recipe` VALUES (4122, 53, '生汆丸子', '(1)将猪肉剁成肉泥放在碗中，先加约50克水，用筷子顺着一个方向用力搅，搅至肉泥发粘时，加入蛋清、葱末、姜末、精盐、料酒、淀粉，用力搅至肉色发白、粘性较大时为止。\r\n(2)将木耳漂洗干净，个大的撕成小片。菠菜叶洗净切成大片。\r\n(3)炒锅上火，加清汤1000克，烧至快开锅时将肉馅挤成杨梅大小的丸子逐个下锅，并保持锅中的清汤似开未开的火候。待肉馅全部挤完，锅中的丸子全部浮于水面时，用漏勺撇净浮沫，下入木耳，菠菜叶，加适量的精盐，料酒，味精，待开锅后即可出锅盛于汤碗中。', '丸子色自质嫩，爽口不腻宁汤汁清，口味鲜香', '丸子馅要剁细，拌馅时要边加水边搅，使所加水都能吃进肉中。汆制过程中要注意火候，挤完丸子之前锅中之水不能沸腾，防止下锅早的丸子过老。丸子的大小要以每50克肉挤7～8个大小均匀的丸子为标准。若大锅大量制作时，应采取分批出锅的方法，即先挤一部分下锅，待其都浮起后全部捞在一盆中，尔后再挤第二批、第三批，待最后挤完后将捞出的丸子全部下锅，调好汤汁的口味，待开锅后一起出锅装盘。丸子肉的用料应是七成瘦肉，三成肥肉．', '葱末、姜末、精盐、料酒、味精、湿淀粉。', '猪肉400克，鸡蛋清20克，水发木耳10克，菠菜叶2片。');
INSERT INTO `recipe` VALUES (4123, 53, '鱼香肉片', '(1)将猪肉切成约4cm长、2cm宽、0．3cm厚的片，用少许盐，酱油、料酒、湿淀粉上浆。玉兰片切薄片，泡辣椒去籽后剁碎，\r\n(2)用酱油、醋、白糖、味精、葱、姜、蒜末、湿淀粉，高汤在小碗中兑成芡汁。\r\n(3)油锅上旺火，加底油，烧至六成热时将酱好的肉片下锅用勺慢慢炒散，尔后加入泡好的红辣椒炒出香味，再加上玉兰片、木耳炒几下，然后倒入兑好的芡汁，开锅后炒匀即可出锅。', '色泽红亮，肉质软嫩，菜周围红油发亮，口味', '炒肉片时要掌握好火候，防止炒老而肉不滑嫩。芡汁下锅前要先搅动几下，防止淀粉沉底而使芡汁不匀。', '葱花、姜末、蒜末，泡红辣椒，醋、精盐、酱油，白糖，淀粉、熟猪油。', '猪瘦肉350克，水发木耳30克，水发玉兰片25克。');
INSERT INTO `recipe` VALUES (4124, 53, '五香牛肉', '(1)将牛肉切寸块、放入沸水中烫透捞出，沥干水分。\r\n(2)炒锅放油烧热，放入葱、姜、花椒、丁香、大料＼桂皮煽炒出香味后，再放酱油，精盐、白糖、清水和牛肉烧开，撇去浮沫，移至小火慢炖，待汤汁呈酱红色，即可出锅冷却，改刀装盆，淋上麻油即成。\r\n注：驴肉、马肉、羊肉、狗肉均可照此做法(驴肉、马肉带\r\n酸性，要用冷水和明矾泡制)。', '色泽酱红，口味五香。', NULL, '花生油、精盐、酱油、味精、料酒、白糖、大料，花', '牛肉1000克。');
INSERT INTO `recipe` VALUES (4125, 53, '酱牛肉', '(1)牛肉洗净切大块，肉四周扎小口，均匀地撒上盐，用力揉搓，然后放入盆内腌制(春夏时间短，秋冬时间长一些，但最短不少于12小时，最长不超过48小时)，腌好后用清水洗净，放入开水中煮一下，捞出洗净浮沫。\r\n(2)花椒、八角、山奈、肉桂、陈皮、白芷、小茴香装入小布袋，将小布袋放入锅中，加酱油、精盐、葱、姜、白糖和适量的水，烧开后，放入牛肉，待再开时，撇去浮沫，移至小火慢煮，待酱汁浓稠，牛肉熟透，晾凉后，切片装盆。', '色泽酱红，内质软烂，口味咸香。', '切熟牛肉时不能顺着牛肉的纤维丝下刀，否则吃起来不易嚼碎。', '精盐、酱油、料酒。白糖、硝、姜、花椒、八角、山奈、肉桂、陈皮、白芷、小茴香。', '牛肉1000克');
INSERT INTO `recipe` VALUES (4126, 53, '酱肚花', '将猪肉切条，放精盐、酱油、料酒、白糖、淀粉，五香面，姜末拌匀；猪肚洗净，将拌好的猪肉装在猪肚里，拿针线缝住肚口，用绿豆绳捆紧，下卤锅煮熟，凉冷后取掉绳子，抽去缝线即成．吃时切片即可食用。', '肚脆嫩肉软烂。', '此菜用肚应为整个猪肚，故不能单盘制作．', '精盐、酱油、料酒、白糖、麻油，淀粉、五香面、姜末、绿豆绳10条。', '猪肚400克，猪五花肉200克(单盘用量)。');
INSERT INTO `recipe` VALUES (4127, 53, '盐水什件', '将鸡心、肝、肫洗净，放入开水中汆透捞出，放在盆内，再加精盐、料酒、花椒、葱段、姜片，添适量的水上笼蒸烂(水量以淹没原料为准)，取出凉冷即成。吃时切片。\r\n注：如鸡什件量多，也可放锅中加调料烧制。', '咸香可口', NULL, '精盐，料酒、花椒、葱段、姜片。', '鸡心100克，鸡肝200克，鸡肫300克。');
INSERT INTO `recipe` VALUES (4128, 53, '叉烧肉', '(1)将肉剔去皮和肥膘(肥膘不要剔净，但不能留得太多，这样能使肉香而光亮)，切成长条，放入盆内，加酱油、葱段、姜片、白酒、精盐、白糖、食用红色素腌制2～4小时。\r\n(2)烤炉烧热，把腌好的肉用钩子挂起，吊在烤炉内，烤制40分钟左右，然后刷层麻油即成。\r\n注：如没有电烤箱或烤炉，可将肉放入油中炸透捞出，切片，淋上麻油食用。', '色泽红，口味甜咸。', NULL, '精盐、酱油、白糖、白酒，葱段、姜片、食用红色素。', '猪前腿肉700克。');
INSERT INTO `recipe` VALUES (4129, 53, '卤肉', '(1)将猪肉洗净，切成大块：放入开水中烫后捞出洗净，放入锅内。\r\n(2)将红米、桂皮、茴香、葱、姜用洁净白布包好，放入猪肉锅内，加清汤，用旺火烧开，再用温火，加精盐、料酒、酱油、白糖卤，透捞出，冷却后切薄片，放入盆内，淋上麻油即成。', '肉外表红亮，质地软烂而不碎，口味荤香。', NULL, '精盐、酱油、料酒、白糖、红米、桂皮、茴香、麻油、葱段、姜片。', '猪肥瘦肉700克。');
INSERT INTO `recipe` VALUES (4130, 53, '白切肉', '(1)将猪肉切大条块，放入开水中汆一下捞出。\r\n(2)煮锅加水、料酒、葱结、姜块、大蒜头和肉块烧至八成熟时，捞出冷却，再切薄片装盆；用大蒜末、味精、精盐、麻油调成汁，浇在白切肉上即成。\r\n注：如吃辣时可加辣油、香醋：香菜调成汁食用。', '肉色淡白，肉质细腻，味清香。', NULL, '精盐，酱油、味精、麻油、料酒、葱结，姜块，大蒜头。', '猪肉700克。');
INSERT INTO `recipe` VALUES (4131, 53, '盐水鹅', '(1）将鹅洗干净去五脏、鹅翅、鹅掌，用清水泡1小时左右，捞出沥干水分，用精盐擦遍鹅全身，腌3～4小时(或放在老卤内浸泡1～2小时)然后洗净。\r\n(2)煮锅上火加适量清水、料酒、葱段、姜片、精盐，烧开后，撇去浮沫，再将腌制好的鹅下入煮锅，移至小火煮40分钟左右即熟，然后将鹅捞出冷却后，用刀斩成块，装盆即可。', '肉色黄白，肉质软烂，味咸香。', NULL, '精盐，料酒，姜片(或老卤)，花椒。', '光鹅2000克(一盘的量)。');
INSERT INTO `recipe` VALUES (4132, 53, '五香酱鸭', '(1)将鸭杀好洗净，用精盐、葱段、葱片腌制3～4小时，然后出水洗净。\r\n(2)锅置火上，加入老卤汤烧滚，放入鸭子，待再沸时，撇去浮沫，再放入葱段、姜片和料酒，移置小火煮至汤汁收浓后，捞出，改刀装盆，淋上麻油，即可食用。\r\n注：用老卤时，每次都要烧开后去浮沫，再加桂皮，八角，丁香，陈皮，这样时间越长越好。', '色泽浅红，肉质软烂。', NULL, '精盐、味精，料酒、老卤汤、麻油、葱段、姜片。', '鸭子1000克．');
INSERT INTO `recipe` VALUES (4133, 53, '卤猪肝', '(1)将猪肝冲洗干净，放入开水中加蒜煮至五成熟时捞出，再用清水洗净，沥去水分备用；用一块净布，包入大葱段、丁香，小茴香，桂皮等，制成药料袋。\r\n(2)煮锅放水，加精盐、酱油、药料袋，用旺火烧开，撇去浮沫，制成卤汁，放入猪肝烧开后，再用小火煮熟取出，待冷却，即可切片装盆。', NULL, '煮时要随时检查成熟程度，防止煮老。检查的方法是：用筷子插到肉厚的部位中间，拔出筷子后不见有血水冒出即可。', '精盐，酱油、蒜、大葱段、姜片，花椒、大料、丁香，小茴香，草果皮、砂仁、桂皮、豆寇、陈皮。', '猪肝1000克。');
INSERT INTO `recipe` VALUES (4134, 53, '卤鸡', '(1)将鸡杀好后，放入六至七成热的水烫，去毛污，从腋下开一小洞去内脏洗净，用洁布拭干水分，将酱油抹在鸡身上。\r\n(2)炒锅放油烧至匕成热时，放入仔鸡，炸至金黄色，捞出沥油；炒锅内留适量的油，放入葱，姜、八角，桂皮炸出香味，再加酱油、白糖和清水烧沸，撇去浮沫，放入仔鸡，用小火焖至六成熟，再置旺火上，加麻油，待汤汁收浓，取出仔鸡，冷却后改刀装盆，浇上卤汁即成。\r\n注：兔肉、狗肉、猪肉，猪肝、牛肉均可用此法制作。', '色泽酱红，肉质脆嫩，咸香可口。', '卤鸡时，开锅后要用小火焖制，以保证鸡肉形整、脆嫩，入味。', '花生油、酱油、料酒，白糖，麻油，桂皮，八角、姜片、葱结。', '仔鸡750克。');
INSERT INTO `recipe` VALUES (4135, 53, '白斩鸡', '(1)将鸡杀好，洗净后出水，出水后再洗净，煮锅置火上，加适量清水、葱段、料酒、姜片，沸后撇去浮沫，再放入洗净的鸡，用小火煮熟，离火后遂加精盐，使鸡浸泡入味，晾凉后，捞出鸡，沥去汁水，改刀装盆。\r\n(2)酱油放入另一盆内，加味精、麻油制成卤汁，吃时将卤汁浇在鸡上即成。', '色白质嫩，\r\n清香可口。', '煮鸡的火候要把握“断生”时，即在砍开后不见骨头上有血的前提下越嫩越好。', '酱油、精盐、味精，料酒、胡椒粉、麻油，葱段，姜片。', '嫩鸡750克。');
INSERT INTO `recipe` VALUES (4136, 53, '凉拌腰子', '(1)猪腰剖开，除去腰臊洗净，批成薄片，用葱姜汁浸泡2小时，再换清水浸泡至发白膨大；笋尖切成片，鸡肉切成斜方片，冬菇去蒂批成片，味精放入葱油内调匀。\r\n(2)炒锅放入开水，分别将猪腰片，冬菇片，笋尖片汆透捞出，沥去水分，放入盆内，加料酒、香醋、拌匀后，撒上胡椒粉、姜末，香菜叶，浇上酱油，淋上麻油即成。', '色浅红，质地软脆，味咸香。', NULL, '酱油、味精、料酒，香醋、麻油，胡椒粉，葱末、姜末。', '猪腰子400克，熟笋尖20克、熟鸡肉20克，水发香菇20克、香菜叶5克。');
INSERT INTO `recipe` VALUES (4137, 53, '油爆虾', '(1)河虾剪去虾须洗净，沥干水分。另取一盆，放入料酒、酱油、白糖、香醋、精盐、麻油，葱末、姜末搅匀成调料汁。\r\n(2)炒锅放油烧至八成热时，将河虾分批放入炸透，捞出，滗出锅中余油后，放炸好的虾，再加盆内的调料汁拌匀即可出锅装盆。', '色泽鲜红，外脆里嫩，咸香可口。', '操作过程中，火要旺。', '花生油、精盐、酱油、料酒、白糖、香醋、麻油、葱末、姜末。', '鲜河虾600克。');
INSERT INTO `recipe` VALUES (4138, 53, '干烧带鱼', '(1)带鱼杀好洗净切成段，放入盆内，加料酒拌腌好，榨菜洗净斩成细粒，泡辣椒切末待用。\r\n(2)炒锅放油烧至七成热时，放入带鱼炸至外焦里嫩捞出，原锅留适量的油，放入泡辣椒片，煽出辣香味，再放豆瓣酱、辣椒粉、葱末、姜末、蒜片、榨菜、肉末煸炒至油色变红，然后放入料酒，酱油，白糖、带鱼和适量的水，旺火烧开，再用小火烧至汤汁将干即可出锅。\r\n注：用其它鲜鱼也可制作。', '色泽红亮，口味咸辣香，无汤汁。', '加水要适量，要以在入味的情况下能把水煸干为度。', '花生油、酱油，味精，料酒，白糖，豆瓣辣酱，辣椒粉，泡辣椒、葱末，姜末、大蒜。', '鲜带鱼700克，肉末20克，榨菜10克。');
INSERT INTO `recipe` VALUES (4139, 53, '煎咸带鱼', '(1)咸带鱼杀好洗净后，置于风口晾干(如太咸者，须先用温水浸1小时)。\r\n(2)将带鱼的头尾剁去，切成段，正反面划成斜纹。\r\n(3)炒锅放油烧热，放入鱼段炸至金黄色捞出沥油，原锅留适量的油，放入葱、姜煸炒后，再放鱼块，加料酒、白糖、香醋，用文火焖至入味即成。', '色金黄，味咸香。', NULL, '花生油，料酒，白糖，香醋、葱丝、姜丝。', '带鱼700克。');
INSERT INTO `recipe` VALUES (4140, 53, '红烧带鱼', '(1)将带鱼杀好洗净、切成段。\r\n(2)炒锅放油烧至八成热时，放入带鱼炸至金黄色捞出沥油，原锅留适量油，放入葱，姜，蒜煸出香味后，把炸好的带鱼放入，加料酒、高汤、酱油，精盐、白糖，味精烧开后，再用中火焖至汤汁收浓，淋上明油即可出锅装盆。', '形整肉嫩，味咸香。', '炸鱼的锅要光滑，鱼下油锅后不能立即搅动，防止粘锅底后粘破鱼皮。', '花生油，精盐，酱油、味精，料酒、白糖、淀粉、高汤、葱末，姜末，蒜末。', '带鱼700克。');
INSERT INTO `recipe` VALUES (4141, 53, '酥炸带鱼', '(1)将带鱼杀好洗净切成块，放入盆内，用盐腌制好。\r\n(2)将腌制好的带鱼洗净，沥干水分，拌上鸡蛋、淀粉，面粉、五香粉，胡椒，待油烧至，七成热时，放入鱼块炸熟即可。', '色泽金黄，咸香酥脆。', NULL, '花生油、精盐，淀粉、面粉、五香粉、胡椒。', '带鱼700克，鸡蛋25克。');
INSERT INTO `recipe` VALUES (4142, 53, '葱焖鲫鱼', '(1)将鲫鱼杀好洗净，沥干水分，鱼身两面划斜刀，放适量的酱油、料酒腌制好待用。\r\n(2)炒锅放油烧至八成热时，放入鲫鱼炸至两面黄捞出沥油，原锅留适量油，放入葱、姜煸炒出香味，随即将鲫鱼放在葱、姜上，加料酒、酱油、白糖、味精和清汤烧，盖上锅盖，用温火烤至鱼肉入味，将鱼盛入盆中，将锅里鱼汤收浓，加麻油，香醋一起浇在鱼上即成。', '色浅红，味咸香，形状整齐。', NULL, '花生油、酱油，味精，料酒、白糖、葱段、姜丝。', '小鲫鱼750克。');
INSERT INTO `recipe` VALUES (4143, 53, '滑炒虾仁', '(1)将河虾去壳，去头尾，洗净，用白布吸干水分，再用精盐，淀粉、苏打粉、蛋清拌匀，装入盆内，放在冰箱里冰1小时后取出。\r\n(2)炒锅放油烧至六成热，放入虾仁，轻轻搅散(火不宜太大)，油不能太滚(否则会使虾老或成团粘连)，待虾仁爆至七、八成熟时捞出，原锅留适量的油，放入葱末和虾仁，随即加料酒，味精，勾薄芡即可出锅装盆。', '色白肉滑嫩，鲜香可口。', NULL, '猪油、精盐、味精、料酒，淀粉、苏打粉。', '鲜河虾500克，蛋清20克。');
INSERT INTO `recipe` VALUES (4144, 53, '炒虾仁', '(1)将虾仁发好后，反复冲洗干净，沥去水分，放入盆内，加料酒、精盐、味精、蛋清、淀粉拌匀浆好待用3豌豆米放入开水中烫后捞出，过凉，沥去水分。\r\n(2)炒锅放油烧至六成热，放入浆好的虾仁划散，捞出沥油，原锅留适量的油，放入豌豆米煸炒后，加料酒，精盐、味精和虾仁翻炒一下，淋上明油即可出锅。', '虾仁色白如玉，豌豆色碧绿，口味鲜香脆嫩。', NULL, '猪油、精盐，味精，淀粉。', '鲜河虾仁400克，青豌豆50克，蛋清20克。');
INSERT INTO `recipe` VALUES (4145, 53, '雪菜黄鱼', '(1)将鱼刮鳞、去鳃、去内脏洗净切成块，放入盆内；雪里蕻洗净切成末待用。\r\n(2)炒锅放汕烧热，放入葱、姜煸炒一下，随即放入鱼块煎透，再放精盐、料酒、味精、胡椒粉，高汤，用旺火烧开，改用小火炖至汤汁发白收浓时，放入雪里蕻烧开后出锅即成。', '鱼肉色白，口味咸香。', NULL, '猪油、精盐、味精、料酒、高汤、胡椒粉、葱段、姜块。', '黄花鱼750克，雪里蕻50克。');
INSERT INTO `recipe` VALUES (4146, 53, '红烧黄鱼', '(1)黄鱼杀好洗净，用刀在鱼两侧横剞三、四刀，放在酱油中浸渍片刻捞出。\r\n(2)炒锅放油烧至八成热，将鱼放入炸至两面黄，捞出沥油，原锅留适量的油，放入花椒炸出香味后，捞出不用，再放葱丝、姜丝、蒜片煸炒一下，将鱼放入，加料酒、酱油、白糖、味精和适量水烧透，待水份快收干，烹些香醋、勾芡、淋上麻油即成。', '色泽微红，口味咸香，鱼肉不碎。', NULL, '花生油，酱油、味精、料酒，麻油、白糖、香醋，花椒、葱丝、姜丝、蒜片、淀粉。', '黄花鱼750克。');
INSERT INTO `recipe` VALUES (4147, 53, '清蒸鲢鱼', '(1)将鱼杀好切成块，分放在盆中。\r\n(2)将肥肉切薄片，放在鱼块上，加入料酒、葱、姜、胡椒，味精、精盐，料酒、猪油上笼蒸15分钟左右取出即成。\r\n注：按此法也可制作“清蒸草鱼”，“清蒸桂鱼”，“清蒸\r\n黄鱼”等。如在鱼上放些香菇，笋片，火腿则味更美。', '色白肉嫩，鲜咸可口。', '蒸鱼时要根据鱼块大小和火力大小掌握好蒸制时间，防止过嫩而不熟或过老肉硬。', '猪油、精盐、味精、料酒、胡椒、葱段、姜片。', '大鲢鱼750克，肥肉20克。');
INSERT INTO `recipe` VALUES (4148, 53, '红烧鱼块', '(1)将鱼杀好洗净后，切成块。\r\n(2)炒锅放油烧热后，先放葱、姜、蒜、豆瓣酱煸炒出香味，再放入鱼块翻炒几下，加酱油、料酒、白糖、高汤烧至八成熟，勾薄芡，烹些醋，放味精即可出锅装盆。', '色微红，味咸香。', NULL, '花生油、酱油、味精、料酒、白糖、香醋、淀粉、高汤，豆瓣酱、胡椒、葱段、姜丝、蒜片。', '鲢鱼(或其它鲜鱼)750克。');
INSERT INTO `recipe` VALUES (4149, 53, '红烧鳝鱼', '(1)将鳝鱼杀好洗净切段，用温水烫后，捞出再用清水洗净待用．\r\n(2)炒锅放油烧至八成热，放入葱、姜、蒜、煸炒出香味，再放鳝鱼段，加酱油、精盐、料酒，白糖、水适量烧开，再用小火焖至酥烂，淋上麻油，撒上胡椒粉即可出锅装盆。', '肉质软嫩，口味咸甜。', NULL, '花生油、精盐、酱油、味精、料酒、白糖、麻油、胡椒粉、葱段、姜片、大蒜片。', '鳝鱼600克。');
INSERT INTO `recipe` VALUES (4150, 53, '红烧鳊鱼', '(1)将鱼杀好洗净，在鱼身上斜划十字刀纹；玉兰片切薄片待用。\r\n(2)炒锅放油烧热，将鱼逐个煎成两面黄，原锅加适量油烧热，放入葱段、姜丝、玉兰片，煸出香味，将煎好的鱼放上，加料酒、精盐、酱油、白糖和适量的水烧至汤沸后，改用中火烧至出鱼香味，再用大火烧至汤汁收浓时，放入味精，淋上麻油即成。', '鱼形完整，咸香微甜。', NULL, '花生油、精盐、酱油、味精、料酒、白糖、麻油、淀粉、葱段、姜丝。', '鳊鱼750克，猪肉20克，玉兰片10克。');
INSERT INTO `recipe` VALUES (4151, 53, '红烧鲤鱼', '(1)将鱼杀好洗净，用刀在鱼身剞成月牙状，抹上淀粉。\r\n(2)炒锅放油烧至七成热时，放入鱼炸至金黄色捞出锅留适量的油，放入葱、姜、蒜，炒出香味后，再放玉兰片和肉片翻炒，加各种调料添些高汤，将炸好的鱼放下移置小火焖熟，将鱼捞出，汤里勾薄芡，浇在鱼身上即成。', '色浅红，口味咸中微带甜。', NULL, '花生油、精盐、酱油，味精、白糖，料酒、淀粉、高汤、葱段、姜丝、蒜片。', '鲤鱼750克，猪肉20克，玉兰片10克。');
INSERT INTO `recipe` VALUES (4152, 53, '雪笋黑鱼', NULL, '鱼形整齐不碎，咸香入味。', NULL, '花生油、精盐、味精、白糖、淀粉、肉汤、葱白段，姜片。', '黑鱼750克，冬笋75克，雪里蕻75克。');
INSERT INTO `recipe` VALUES (4153, 53, '糖酷鲤鱼', '(1)将鱼杀好洗净切成块，放在盆内，用精盐、料酒腌渍30分钟左右后，用稠淀粉在鱼块上挂一层糊，下油锅炸至外焦里嫩，呈金黄色捞出沥油。\r\n(2)原锅留适量的油，放入葱末、姜末、蒜末，出香味后，再放青豆、丁香萝卜丁翻炒，加酱油、白糖、香醋，料酒、精盐、高汤、淀粉调成糖酷汁，放进鱼块稍加翻炒即可。\r\n要领：烧鱼时放水要得当，时间一般在10分钟左右，刀口大小要一致。', '色红发亮，口味甜酸，微带咸，外焦里嫩。', NULL, '花生油、精盐、酱油、料酒、白糖、香醋，淀粉、胡椒粉，高汤、葱末，姜末、蒜末。', '鲤鱼750克。');
INSERT INTO `recipe` VALUES (4154, 53, '葱油鱼块', '(1)将鱼刮鳞、挖鳃，剖腹去内脏，洗净后切成长方块，放入开水中汆透，捞出沥水。\r\n(2)炒锅放油烧热，放入葱丝，姜丝，炒出香味后，加适量的水，再放上鱼块，加精盐、酱油、料酒、胡椒面，用小火烧10分钟左右，最后加．些味精即可出锅装盆。\r\n要领，鱼块放入开水中不能汆得太老，一般在3分钟左右．\r\n注：如鱼多，也可上笼蒸熟。', NULL, NULL, '花生油、精盐、酱油、味精，料酒、胡椒面、葱丝、姜丝。', '草鱼(或用其它鲜鱼)750克。');
INSERT INTO `recipe` VALUES (4155, 53, '干烧青鱼', '(1)将鱼杀好洗净切成块，放入酱油中腌制一下捞出，猪肉洗净剁成末，笋、丁香萝卜、冬菇洗净切成小丁待用。\r\n(2)炒锅放油烧至八成热时，放入鱼块炸至金黄色捞出。\r\n(3)炒锅放猪油烧热，先放入豆瓣酱煸一下，再放入猪肉末、酒酿、丁香萝[、、笋丁，葱末、姜末、辣椒末，蒜末、料酒、精盐、白糖，倒入负块烧至大开时，放冬菇和味精，移向小火慢烧至汤汁收干，淋上明油即可。', '色浅红发亮，口味咸辣微甜，无芡汁。', '干烧鱼要烧透，卤汁要收干，火不要旺，各种配料要切成大小一致。', '花生油、精盐、酱油、猪油、味精、料酒、酒酿、白糖、豆瓣酱、泡辣椒、葱、姜、蒜。', '青鱼750克，猪肥肉25克。');
INSERT INTO `recipe` VALUES (4156, 53, '蒸蛋羹花', '(1)鸡蛋打入蒸钵内，加水、精盐、味精充分打匀。\r\n(2)上笼蒸至中间凝固为度(一般15～20分钟)，下笼浇上猪油，撒上葱花即成。', '蛋羹嫩而不生，完全凝固，熟而不老，断面无', NULL, '水、精盐、猪油、味精。', '鸡蛋300克，小葱30克。');
INSERT INTO `recipe` VALUES (4157, 53, '肉末蒸蛋', '(1)取鸡蛋打入盆里，打调均匀。\r\n(2)猪肉洗净，剁成细末，然后将肉末、葱、姜末、白酱油，味精及适量的清水和精盐放入盆内打搅均匀，逐碗盛满上笼蒸熟取出，蘸麻油食用。', NULL, NULL, '精盐、白酱油、味精、麻油、葱姜末。', '鸡蛋(或鸭、鹅蛋)250克，猪肉100克。');
INSERT INTO `recipe` VALUES (4158, 53, '肉饼蒸蛋', '(1)将猪肉末放入盆内，加葱、姜末、料酒、精盐、清水、味精打成肉馅，分成8盘，在肉馅中间挖个洞，将鸡蛋磕入其中，鸡蛋上面撒少许精盐。\r\n(2)锅中水烧开后，把肉饼蛋上笼蒸20分钟左右，出锅即可。', '肉馅不散，鸡蛋不外溢，造型独特，别有风味', NULL, '精盐、白糖，料酒，味精，葱、姜末。', '鸡蛋400克(8个)，猪肉200克。');
INSERT INTO `recipe` VALUES (4159, 53, '溜黄菜', '(1)鸡蛋打入盆中，加少许高汤、精盐、料酒，味精、湿淀粉打调均匀。\r\n(2)炒锅放油烧热，倒入蛋液搅炒成糊状，倒入盆内。\r\n(3)炒锅加油烧热，将姜末、笋丁、火腿丁、高汤、酱油、料酒、香醋、白糖下锅翻炒后，勾薄芡，倒入蛋糊一起翻炒几下后，即可出锅装盆。', '色浅黄，软嫩咸香。', NULL, '猪油、精盐、料酒、味精，酱油、白糖，香醋、姜末、淀粉、高汤。', '鸡蛋300克，冬笋50克，火腿50克。');
INSERT INTO `recipe` VALUES (4160, 53, '紫菜蛋卷', '(1)将鸡蛋打入盆中加精盐和少许猪油打成蛋液。炒锅烧热后，用少许猪油擦锅，逐个逐个摊成蛋皮备用。\r\n(2)将鱼肉剁成细泥，发好的虾仁同样刮成虾茸，将鱼和虾放入盆中，加料酒，蛋清，精盐、葱末，姜末、味精、胡椒粉打成鱼虾馅备用，紫菜卷紧，用刀背去掉砂子，再切成细末。\r\n(3)将蛋皮平放在墩头上，把鱼虾肉馅摊在蛋皮上抹平，把紫菜末撒在鱼虾馅上，用手把它逐条卷紧，上笼蒸15分钟左右，待冷却后，再切成厚片，装盘即成。\r\n注：此菜也可做冷菜，也可作热菜，做热菜食用时要勾薄芡。', '蛋卷切开后不散，肉质细腻，口味咸香。', '若做凉菜，在蛋卷冷却过程中应用平板压上，以使蛋卷结实不碎。', '猪油、精盐、味精、料酒、葱、姜未、胡椒粉．', '鸡蛋300克，鲢鱼200克，虾肉50克，紫菜20克．');
INSERT INTO `recipe` VALUES (4161, 53, '酷溜松花蛋', '(1)将松花蛋去泥去壳洗净，用干抹布擦去水分，每只切成八块放在碗内，挂上玉米粉，另取碗将鸡蛋去壳打散加面粉和少许高汤或水搅匀，倒入松花蛋碗内，使每块松花蛋都粘满面糊。\r\n(2)取锅烧热放油，待油烧至六成热时，将糊好面糊的松花蛋下锅炸约2分钟，见呈金黄色时，用漏勺捞出沥油，将油倒出留余油，放入葱、姜末煸炒出香味后，加白糖、酱油、香醋，高汤或水适量烧沸，淋上湿淀粉，将炸好的松蛋下锅颠炒几下出锅装盘。', '色泽金黄，口味酸甜，外焦里嫩。', NULL, '花生油、酱汕、香醋、白糖、湿淀粉、葱末、姜末，', '松花蛋3个，鸡蛋25克，面粉10克');
INSERT INTO `recipe` VALUES (4162, 53, '煎荷包蛋', '炒锅放油(最好是平底锅)，见油热时取蛋破壳，轻轻磕入油锅，按锅大小可放数个，但不要靠拢，在每个上面撒少许精盐，精盐不可放太多。待蛋垫锅底一面发黄，即用铲子翻身煎另—面，煎好装盆即成。\r\n注：此菜按桌制作计算。吃时可用青菜或其它蔬菜作配菜。', '色泽金黄，不破不损，不流黄，口味咸香。', '煎时锅要光滑，锅热后加油，火不宜太大，防止煎糊。', '花生油、精盐，味精。', '鸡蛋8个');
INSERT INTO `recipe` VALUES (4163, 53, '熏蛋', '(1)将鸡蛋放入锅里加冷水烧熟，捞出过冷水，浸透，削去蛋壳。\r\n(2)将鸡蛋放入锅里加酱油，葱、姜、清汤、白糖烧成卤蛋捞出。\r\n(3)另用—只炒锅，放入木屑、桂皮，八角、花椒、桔子皮，白糖、茶口十、大米等，再放上铁丝架，将整葱放在铁丝架上面，把蛋全部放上，盖严锅盖，熏7～8分钟后(熏时用旺火)，取出，在每只蛋上搽上麻油即可。', '色泽酱红，风味独特。', NULL, '整花椒、味精、清汤、白糖，桔子皮，木屑、整葱，酱油，麻油，茶叶、八角、桂皮、大米。', '鸡蛋400克。');
INSERT INTO `recipe` VALUES (4164, 53, '黄焖蛋', '(1)将鸡蛋打入盆中，加精盐，味精打成蛋液，熟酱肉切丁待用。\r\n(2)炒锅放油烧至七、八成热时，将打好的蛋液倒入，双手倾动，使蛋液淌成五、六寸盘大小的圆形，随将肉丁分布其上。\r\n(3)将炒锅移置小火，待底层凝结，用铲翻身，约3～4分钟，肉面与蛋相结合，再翻身，用面积相等的盘盖罩在蛋上，用文火焖至香气透出，即可翻铲于盘中。', '色金黄，形整如圆饼，味咸香。', NULL, '猪油，精盐、料酒、味精，', '鸡蛋(或鸭蛋鹅蛋)300克，猪肉50克。');
INSERT INTO `recipe` VALUES (4165, 53, '虾仁爆蛋', '(1)将虾仁用苏打粉发制20分钟左右，用冷水反复冲洗干净，沥去水分后，用精盐、味精、蛋清、淀粉浆好待用。\r\n(2)将鸡蛋打入盆内，加精盐、味精、料酒打成蛋液。\r\n(3)炒锅放入猪油烧热后，将虾仁下锅划散后捞出，滗去油，原锅内留适量的余油，把蛋液倒入，翻炒数次，再把虾仁覆盖在炒蛋上，盖上锅盖，移向小火焖片刻，使其涨发后出锅即可。', NULL, '爆蛋的关键是油要多，火要小，炒时要动作快。', '猪油，精盐、料酒、味精、淀粉。', '鸡蛋360克，鲜虾仁50克。');
INSERT INTO `recipe` VALUES (4166, 53, '蟹黄蛋', '(1)将鸡蛋打入盆中，加精盐、味精、姜末、白糖、香醋、胡椒粉打成蛋液。\r\n(2)炒锅上中火，加底油烧热，倒入蛋液，用手勺不断搅动，见蛋已基本炒成蟹黄色，翻身淋上明油出锅即可。', '口味酸、甜、咸，有姜味，形状和口味都有蟹', NULL, '猪油，精盐、白糖、香醋，味精、姜末、胡椒粉。', '鸡蛋400克。');
INSERT INTO `recipe` VALUES (4167, 53, '银鱼炒蛋', '(1)银鱼洗净，沥于水分，放在盆里，加蛋清、料酒、精盐和干淀粉浆拌好。\r\n(2)鸡蛋打入另—盆中，葱切成末，放在鸡蛋盆里，加精盐、料酒打匀。\r\n(3)炒锅放油烧热，下入浆好的银鱼，划散捞出；原锅留余油，下入鸡蛋搅炒到凝结时，再放入银鱼翻炒几下，然后在炒锅四周淋上点明油即可出锅。\r\n注：如用金针菇作配料，可制作“金菇炒蛋”。', NULL, NULL, '猪油、精盐、料酒、味精、干淀粉。', '鸡蛋300克，小葱50克，银鱼50克。');
INSERT INTO `recipe` VALUES (4168, 53, '清炒鸡蛋', '(1)小葱择好洗净切成末；鸡蛋打在盘里，加盐、胡椒粉和葱末打成蛋液。\r\n(2)炒锅上中火，锅热后加适量猪油，油热后倒上蛋液轻轻用勺(铲)沿锅底推炒，待蛋液完全凝固后即可出锅。', '色泽黄中透绿，咸香软嫩。', '炒锅必须光滑，做到锅热后加油，油热后加蛋液。炒时火太大或太小都会糊锅，火要适中。', '猪油、精盐、胡椒粉。', '鸡蛋(或鸭蛋)300克，小葱100克。');
INSERT INTO `recipe` VALUES (4169, 53, '虾米炒蛋', '(1)虾米用温水泡开，鸡蛋打入盆内，加精盐、味精、胡椒粉、葱末打匀。\r\n(2)炒锅加猪油坐中火，油热后下虾米稍炒，倒入鸡蛋，用锅铲不断炒动，见蛋已基本疑固，翻身浇上明油起锅即可。\r\n注：也可用鸭蛋或鹅蛋，但在炒鹅蛋时须加酱油。', '色泽浅黄，味鲜香。', NULL, '猪油、精盐、料酒、味精、葱末、胡椒粉。', '鸡蛋300克，虾米100克。');
INSERT INTO `recipe` VALUES (4170, 53, '软炸鸡肫', '(1)将鸡肫剥去老皮，洗净后，切成两瓣，用刀将鸡肫剞一剞，切成小块，放入盆内，加精盐、味精、料酒拌匀，另取盆，打入鸡蛋，放入面粉和淀粉打成蛋糊，然后放入鸡肫搅拌上糊。\r\n(2)炒锅放油烧至七成热时，放入鸡肫炸透捞出；临吃时，再炸呈金黄色捞出装盆，撒上花椒盐，放上京葱丝、甜面酱即成。', '色泽金黄，外焦里脆。', NULL, '花生油、精盐、味精、料酒、淀粉、干面粉、花椒盐、京葱丝、甜面酱。。', '鸡肫500克，鸡蛋50克。');
INSERT INTO `recipe` VALUES (4171, 53, '清炸鸡肫', '(1)将鸡肫洗净，除去肫的老皮，洗净后切成菊花形状，放盆内，用葱末、姜末、料酒、胡椒粉、酱油腌制好。\r\n(2)炒锅放油烧热，放入腌制好的鸡肫炸至断生捞出，放入盆内，加麻油、味精、葱末、胡椒粉拌匀即成。', '色泽紫红，香脆可口．', NULL, '花生油、精盐、酱油、味精、料酒、淀粉、胡椒粉、麻油、葱末、姜末。', '鸡肫600克。');
INSERT INTO `recipe` VALUES (4172, 53, '荷叶鹅块', '(1)将仔鹅洗干净后切成指条状；丁香萝卜洗净去皮切成滚刀块，下开水锅烫后捞出，米粉加大料上火炒黄。\r\n(2)用酱油、料酒、葱姜末、米粉把鹅块和丁香萝卜拌起，把荷叶切成3厘米见方块，包住鹅块，放在扣盘里，上笼蒸烂，扣在汤盘里即成。\r\n注：用此法可制作“荷叶鸭块”。', '鹅肉软烂，荷叶味香。', NULL, '花生油，料酒、酱油、大料，大葱末、大蒜末、姜末、味精。', '嫩鹅600克，萝卜100克，大米100克。花生油，料酒、酱油、大料，大葱末、大蒜末、姜末、味精。花生油，料酒、酱油、大料，大葱末、大蒜末、姜末、味精。花生油，料酒、酱油、大料，大葱末、大蒜末、姜末、味精。');
INSERT INTO `recipe` VALUES (4173, 53, '红烧鹅块', '(1)将光鹅开膛去内脏，洗干净后，用刀斩成小方块，炒锅放水烧开，把鹅块下锅烫—…下，撇去浮沫，捞出洗干净。\r\n(2)炒锅放油烧热后，先放葱、姜、桔皮、八角煸出香味后，随即将鹅块下锅翻炒数次，放入料酒、酱油、白糖、味精，精盐，上小火焖透后，再上大火收干水分，淋上麻油，出锅装盘即可。', '色褐红，味咸香微带甜。', NULL, '酱油，料酒、白糖、精盐、味精、麻油、葱段、姜块、桔皮、八角。', '光鹅600克。');
INSERT INTO `recipe` VALUES (4174, 53, '香酥鸭子', '(1)钢精桶置火上，加入适量的水烧沸，下入料酒、精盐、酱油和切好的葱段、姜片，再把所有的香料用布包起，也投入钢筋桶内，制成卤汤。鸭子洗净，出水后下入卤汤内煮烂。\r\n(2)煮烂的鸭子捞出，沥净汤水，全身沾匀干淀粉。\r\n(3)油锅置火上，放入素油，烧至大热时下入鸭子，炸至内透外酥呈褐红色时捞出，改刀装盘(仍要保持鸭的原形)，盘边撒点花椒盐即好。', '色泽褐红，质地酥脆，咸香可口。', NULL, '料酒、酱油、精盐、淀粉、花椒盐，丁香、桂皮、大料，花椒、小茴香、豆蔻，砂仁勺肉果、山楂、葱、姜、花椒、素油。', '鸭子750克。');
INSERT INTO `recipe` VALUES (4175, 53, '金葱扒鸭', '(1)将)匕鸭的绒毛去尽，洗净，抽出鸭舌(另作它用)；从背脊剖开光鸭，取出内脏，鸭肫肝洗净(去污物、苦胆)留用，除去鸭骚，鸭背脊、龙骨、腿骨，用刀拍一下，洗净，葱切成长段待用。\r\n(2)炒锅置火上，放入花生油，待油八成热时，投入葱段，炸成金黄色，用漏勺捞起，即成“金葱”，将鸭用酱油抹遍全身，投入油锅拉油上色捞起。\r\n(3)煮锅内放竹垫，放入肫、肝、鸭子(鸭脯朝下)，加清水淹没鸭身，放酱油、白糖、姜块、葱结、料酒，上盖盘，再盖。上锅盖，上火烧开，移小心焖2小时至烂离火，取出鸭子，拣去葱姜，捞出肫，肝切成片。', '此菜鸭肉酥烂脱骨而不失其形，口味浓厚而不', '煮炖鸭子时最好用砂锅，其效果较好。', '花生油、酱油、白糖、姜块(柏松)葱结、料酒、淀粉、麻油。', '光鸭600克，冬笋50克，大葱100克。');
INSERT INTO `recipe` VALUES (4176, 53, '怪味鸡', '(1)将净鸡肉放入锅里烧开后，撇去浮沫，加葱段、姜片、料酒、移到文火上焖至七、八成熟时，捞出，拆去大、小骨头，斩成小方块，(切片或切丝也可)装盆。\r\n(2)用一只碗放酱油、香醋、辣油、芝麻酱、花椒粉、白糖、麻油、葱、姜末、大蒜末、泡辣椒末调匀，浇在鸡肉上即可。', '口味别致，酸咸辣香麻诸味俱全，故称怪味。', NULL, '辣油、花椒粉、葱、姜末、白糖、大蒜泥、酱油、香醋、麻油、芝麻酱、泡辣椒、料酒、味精、精盐。', '鸡600克。');
INSERT INTO `recipe` VALUES (4177, 53, '生炒仔鸡', '(1)将鸡杀好，挖去内脏，洗净后，用刀拍松，剁成小块，冬笋切块，姜、葱头和泡辣椒分别切成指甲片。\r\n(2)炒锅置火上，加油烧热，放入红葱、姜、蒜、鸡块、面酱、酱油、将鸡块炒透，再加鲜汤和料酒、白糖、味精烧开，然后用小火煨透，见汤汁收浓时，用旺火收汁，勾薄芡，淋香油即可。', '鸡块熟而不老，嫩而刁<生，味咸香。', NULL, '花生油、酱油、味精、料酒、白糖、麻油、淀粉，面酱、泡辣椒、姜、蒜头。', '嫩鸡500克，冬笋100克，大葱100克。');
INSERT INTO `recipe` VALUES (4178, 53, '荷叶粉蒸鸡块', '(1)将仔鸡洗净斩成块，放在盆内，加葱丝、姜丝、料酒、酱油浸渍半小时，荷叶洗净后，除去背部老筋，入开水锅略烫，捞出，擦干水分，粳米淘洗干净待用。\r\n(2)炒锅置火上，放入粳米，八角、丁香、桂皮炒至粳米呈金黄色取出，拣去桂皮、八角、丁香，将粳米碾成粗粉，用粗筛筛去杂质，米粉放入盆中，加蛋清、酱油、白糖、麻油搅成糊状，将浸渍好的鸡块，放入糊中拌匀，分成十份，分别用荷叶包好，放入盆中，上笼蒸至酥烂取出即成。', '荷叶清香，鸡肉鲜嫩酥烂，米粉香糯，咸中带', NULL, '花生油、酱油、白糖、料酒、麻油、姜丝、葱丝，桂皮、八角、丁香。', '嫩鸡600克，粳米100克。');
INSERT INTO `recipe` VALUES (4179, 53, '粉蒸鸡', '(1)将鸡肉切成厚片，分放在盆内，加料酒、酱油、白糖、甜面酱、胡椒粉、酒酿汁、葱末、姜末，豆瓣酱调味拌匀。\r\n(2)将大米炒至黄色时，放入花椒一起炒几下，然后磨成末，再加猪油、麻油和鸡片一起调拌均匀，然后把熟豌豆拌上米粉和花椒粉，加适量精盐，放在鸡片上，上笼蒸熟即可。', '鸡肉软嫩，荤香即可。', NULL, '猪油、精盐、酱油，料酒、麻油，白糖、甜面酱、豆瓣酱、洒酿汁、大米、米粉、胡椒粉、整花椒，葱末、姜末。', '鸡肉500克，豌豆100克，大米100克。');
INSERT INTO `recipe` VALUES (4180, 53, '滑鸡', '将鸡杀好，去内脏，洗净斩块，放在盆内，加蒜末、葱末、姜末、精盐、料酒、白酱油、猪油调和，再放入蛋清、淀粉搅匀，分十份用透明纸包住(要封口，不要透气，以保持原味)，放入盆内，上笼蒸烂取出，吃时撒上胡椒粉，淋上熟鸡油即可。', '软滑鲜嫩，外表透明。', NULL, '猪油、精盐、白酱油、料酒，鸡油、淀粉、花椒粉、胡椒粉、葱末、姜末、蒜末，大透明纸10张。', '鸡600克，鸡蛋清25克。');
INSERT INTO `recipe` VALUES (4181, 53, '咖喱鸡块', '(1)将鸡肉剁成块，土豆去皮切成滚刀块。\r\n(2)油放锅里，上火加热后，先把土豆炸成金黄色捞出，锅内留适量油，放入姜末、大蒜末、葱末，煸炒出香味后，再将鸡肉、土豆放入，并加入咖喱粉、精盐、料酒，味精，上大火开锅后，放在小火上炖烂，再滴明油即可。', '肉软烂脱骨而不碎，口味独特。', '土豆下锅的时间要适当，防止碎烂成泥。', '花生油、咖喱粉、淀粉、精盐、姜末、大蒜末、葱末、料酒，味精。', '鸡600克，土豆100克。');
INSERT INTO `recipe` VALUES (4182, 53, '糖醋鸡块', '(1)将鸡脯肉洗净，切成小块，荸荠切成小块，加料酒，酱油拌匀，和蛋清、淀粉浆好，逐块下温油锅内稍炸，捞出后，再下热油锅炸成金黄色捞出，放在盆里。\r\n(2)锅里放油，上火烧热，放入葱段，姜片、蒜片、荸荠、鸡块一起下锅煸炒后，再加各种调料翻炒数次，加少许肉汤，勾薄芡，淋上明油即可出锅。', '色泽金黄，口味甜酸，外焦里嫩。', '炸好的鸡块出锅后到滚糖醋汁间隔时间要短，以保证火候适宜。', '花生油、白糖、淀粉、葱段、蒜片，姜片、酱油，香醋。', '鸡脯肉600克，荸荠50克，蛋清25克。');
INSERT INTO `recipe` VALUES (4183, 53, '油焖鸡', '(1)将仔鸡洗干净斩成核桃大的块，春笋剥壳削皮后洗净切成滚刀块。\r\n(2)锅上火加热放油，把红辣椒丝、姜丝、京葱丝依次放入，待煽出香味后，放入鸡肉，上旺火炒后加笋块，再加入适量肉汤，上小火焖烂，出锅时将猪油浇上面即成。', '色微黄，味咸香微辣。', '鸡块要大小一致，烧时加水要适量，不宜过多，用中火焖制。', '花生油、猪油，酱油，白糖，精盐，料酒，姜丝，京葱丝，红辣椒丝。', '嫩鸡750克，春笋25克。');
INSERT INTO `recipe` VALUES (4184, 53, '铁扒鸡', '(1)将仔鸡杀好，去内脏、气管、食管，洗干净，拆去大、小骨头，切成四大块(两块鸡腿，两块鸡脯肉)，用刀背轻轻地拍一拍，加料酒、味精、精盐、酱油、葱、姜等调和，擦在鸡肉上，腌半小时左右。\r\n(2)用煎锅，放入花生油烧热，将鸡块煎成黄色，盛放在盆中，再将炒锅烧热，放冬菇丝、葱姜丝，加料酒、白糖、酱油、清汤，把煎好的鸡块入锅烧烂，汤汁收干，切片码在盘中央，淋上少许香油即可．', '色泽近橙黄，质地软烂，口味咸香微带甜。', NULL, '花生油、料酒、精盐、味精、酱油、麻油、白糖、番茄、酱、葱丝、姜丝、蒜头丝。', '嫩鸡750克(一只)，冬菇丝15克。');
INSERT INTO `recipe` VALUES (4185, 53, '母子团圆', '(1)将鸡杀好，从背部剖开，去内脏，整理干净，放入开水中氽烫，捞出，用清水洗净，火腿、冬笋切成片待用。\r\n(2)将鸡放入盆中，加料酒、精盐、味精、鲜汤，放上火腿、冬笋、葱段、葱片，上笼蒸烂取出，将鸽蛋煮熟，剥去壳，在温油中稍炸后捞出。\r\n(3)炒锅置火上，放入鲜汤和鸡，待汤沸后，勾芡，加鸡油，出锅摆在盘中央，炒锅放油，将葱、姜炒一下，放鲜汤、鸽蛋、碗豆、冬菇，加精盐、，味精、料酒，待汤沸后，勾薄芡，淋上鸡油，拣出鸽蛋，豌豆和冬菇码在鸡的四周，将汤汁浇在鸡身上即可。', '鸡形完整，造型美观，咸香可口。', NULL, '花生油、鸡油、精盐、味精、料酒、鲜汤、淀粉、葱段、姜片。', '嫩鸡750克，鸽蛋200克。');
INSERT INTO `recipe` VALUES (4186, 53, '炸溜仔鸡', '(1)将鸡肉剞成方格花刀，轻轻拍松(刀距及下刀深浅要均匀)，然后切成小方块，加少许精盐、干淀粉、料酒调匀，放入油内，用旺火炸至外酥里嫩，捞出，滗去原油。\r\n(2)原锅放油烧热，放入葱、姜、番茄沙司、料酒、白糖煸出香味，倒入鸡块，烧成淡黄色，然后勾薄芡，烹点香醋，淋上明油即可装盆。', '色泽淡黄，甜酸可口。', NULL, '花生油、精盐、酱油、料酒、白糖、香醋、番茄沙司，淀粉、葱末，姜末。', '鸡肉750克，蛋清25克。');
INSERT INTO `recipe` VALUES (4187, 53, '干烹仔鸡', '(1)将葱姜汁、酱油、料酒、花椒粉、精盐合在一起调成卤，将仔鸡的大骨头拆除，切成小方块，放在卤内泡半小时。\r\n(2)炒锅放油烧至六成热时，放入鸡块炸熟，捞出，沥出余油，原锅内加适量麻油，倒入鸡块，烹些香醋，撒上葱末、蒜末翻炒几下即成。', '色金黄，味咸香。', NULL, '料酒、精盐、酱油、香醋、麻油，白糖、花椒粉、葱末，葱姜汁、蒜末。', '嫩鸡750克。');
INSERT INTO `recipe` VALUES (4188, 53, '生熏仔鸡', '(1)将鸡杀好整理干净，擦干水份，从脊背开刀成两片，去净大小骨头，用刀背把鸡肉轻轻捶松，再用料酒、酱油、白糖、葱姜汁、花椒等调成卤，把鸡腌1小时左右，用网油——只只包好，分放在铁丝架上。\r\n(2)熏锅内放大蒜头、花椒、桂皮、八角、桔皮、大米、茶叶、整葱、白糖，置旺火上烧2分钟左右，将铁丝架放在锅内，盖好锅盖，熏约半小时，取出鸡，表面涂上麻油，切成片装盘即可。', '色酱红发亮，风味独特。', '熏制时要注意火候，不能太大，防止起火或使鸡味发苦。', '料酒、酱油、精盐、味精、白糖、麻油、网油、花椒、整葱、生姜、大蒜头、大米、桂皮、茶叶、桔皮、八角。', '仔鸡750克。');
INSERT INTO `recipe` VALUES (4189, 53, '葱油香露鸡', '(1)将鸡杀好，整理干净，下开水锅汆—下捞出洗净，放老卤汤中加葱结姜块上文火焖透捞出，斩成块，在盘中摆成原鸡的形状，再浇上原卤汤，撒上葱姜丝。\r\n(2)炒勺下火，加底油、麻油烧开，浇在鸡身上即成。', '色泽金黄，味带五香。', NULL, '花生油、料酒、麻油、葱结、姜块、葱姜丝、老卤汤。', '嫩鸡750克。');
INSERT INTO `recipe` VALUES (4190, 53, '醉猪心', '(1)猪心洗净，割开后片成薄片。\r\n(2)用花椒盐水将猪心洗泡干净，再下开水锅中烧开烫透捞出，沥去水份，装入盆中，加料酒，精盐、酱油、胡椒粉、麻油、葱花拌匀，焖半小时左右即成。', '质地脆嫩，口味清香。', '烫猪心，—定要烫透，防止不熟。', '酱油、精盐、味精，料酒、麻油，花椒、胡椒粉、葱花。', '猪心500克。');
INSERT INTO `recipe` VALUES (4191, 53, '生爆三样', '(1)猪肝洗净去筋，切成片，猪肉洗净切薄片，猪腰洗净，除去腰臊，切成麦穗花块，把它们放入盆内，用味精、盐、料酒、淀粉、蛋清上浆。\r\n(2)炒锅上火加油，烧至七成热时，将肉，肝、腰下锅划开后捞出。原锅留底油，葱蒜炝锅，再放入三样，倒入用精盐、酱油、味精、料酒、香醋、白糖、淀粉、鲜汤兑成的汁，翻炒几下即可出锅。', '色泽浅红，嫩而不生。', '各种原料的刀工要精细，腰花块要适时下锅，防止欠火返生。大锅炒时要注意火候。', '花生油，精盐，味精，白糖、香醋，鲜汤，葱段、蒜末。', '猪肝200克，猪腰子60克，猪肉90克，蛋清25克。');
INSERT INTO `recipe` VALUES (4192, 53, '炒虾肚', '(1)将猪肝洗净切薄片，放入盆内，用料酒、精盐、淀粉、蛋清上浆。虾仁洗净，沥净水份，用盐、蛋清、淀粉上浆，葱切段。\r\n(2)炒锅放油，烧至六成热时，把浆好的猪肝、虾仁分别下油锅划出，沥净油，原锅留底油，葱姜炝锅，再放入荸荠、酱油、白糖翻炒几下，然后放猪肝，虾仁，料酒、味精、香醋，翻炒至熟，勾芡淋花椒油出锅即可。', '猪肝软嫩而不生，味咸香。', NULL, '花生油、精盐、酱油、味精，白糖、香醋、料酒、淀粉、花椒油、姜片。', '猪肚350克，虾仁(鲜)50克，大葱50克。');
INSERT INTO `recipe` VALUES (4193, 53, '干烧肚块', '(1)将榨菜、葱、辣椒、姜、蒜分别洗净切成细末；猪肚洗净煮熟切斜刀块。\r\n(2)炒锅放油烧至—七成热，将猪肚汆炸一下捞出，原锅留底油，下辣椒、葱、姜、蒜、榨菜煸炒出香味，再加入酱油、甜酒酿、鲜汤、白糖、料酒，味精和肚块，烹适量醋，用小火烧至汁浓时淋明油、撒花椒粉，即可出锅。', '色金黄，质地软脆。', NULL, '花生油、酱油、味精、料洒、香醋、白糖、甜酒酿、麻油、鲜汤、红葱、红辣椒、花椒粉、姜、蒜。', '猪肚500克，榨菜50克。');
INSERT INTO `recipe` VALUES (4194, 53, '清炖肚片', '(1)将猪肚内外用醋和盐搓擦几遍，再用清水洗干净．放入开水锅内烫—下捞出，刮去中间的一块白皮后，切成片。香菜洗净切成末，笋去壳和老根后切成片。\r\n(2)将肚月和笋片放入煮锅内，加调料和高汤炖至酥烂后放味精、胡椒粉、香菜即可。', '汤白肚烂，鲜香可口。', NULL, '精盐、味精、料酒、高汤、胡椒粉、葱段、姜片。', '猪肚500克，冬笋50克，香菜5克。');
INSERT INTO `recipe` VALUES (4195, 53, '糖醋羊肉丸', '(1)羊肉剁成末，葱、姜、荸荠均剁成末，和羊肉一起用鸡蛋、淀粉、味精、精盐、料酒、胡椒粉搅成肉馅。\r\n(2)炒锅加油，上旺火烧至六成热时，将羊肉挤成红枣大的丸子，下锅炸至里面熟、外面金黄色时捞出装盘。\r\n(3)炒锅上火，加底油，烧热后用葱姜蒜炝锅，加白糖、酱油，香醋、热水熬至糖化后，调好口，用湿淀粉勾芡后浇在已装盘的羊肉丸子上即可。', '色金黄，味甜酸。', '炒糖醋汁时，要先调好口后勾芡。口味要大甜大醋。', '花生油、酱油、白糖、香醋、湿淀粉、料洒、精盐、味精、胡椒粉、葱丝、姜丝、蒜片、青蒜段。', '羊肉400克，荸荠80克，鸡蛋20克。');
INSERT INTO `recipe` VALUES (4196, 53, '锅烧羊肉', '(1)将羊肉切成大片，用鸡蛋、淀粉、味精、面粉挂糊。\r\n(2)炒锅上火，加半锅油烧热，把羊肉下锅炸至金黄色捞出，上桌前再用热油炸一次，切成条装盘，撒上椒盐即可。', '色泽金黄，酥香可口。', NULL, '花生油、椒盐、味精、精盐、湿淀粉、干面粉、甜面酱、葱丝。', '熟肋板羊肉500克，鸡蛋20克。');
INSERT INTO `recipe` VALUES (4197, 53, '清蒸羊肉', '(1)将羊肉洗净用清水煮熟捞出。\r\n(2)将羊肉切成片码在盘中，加调料上屉蒸透，取出后拣去葱姜蒜，扣在盘中即可。', '软烂清香。', NULL, '精盐、料酒、味精、高汤、葱段、姜片，蒜片，大料。', '羊肉500克。');
INSERT INTO `recipe` VALUES (4198, 53, '红焖羊肉', '(1)将羊肉洗净煮熟，片成坡刀片，将葱洗净切小块。\r\n(2)炒锅上火，加半锅油烧热，将羊肉下锅炸成金黄色捞出，原锅留少许油，加葱、姜、蒜和甜面酱，稍炒几下后加入酱油、料酒、白糖，添适量的肉汤，再放入羊肉、味精，用微火焖透，勾薄芡，淋明油出锅。', '软烂咸香，不膻。', NULL, '油、酱油，料酒，味精，甜面酱，白糖、肉汤、湿淀粉，葱花、姜片、蒜片、大料。', '羊肉500克，大葱100克。');
INSERT INTO `recipe` VALUES (4199, 53, '咖喱牛肉', '(1)牛肉洗净切块，放入开水中烫一下捞出，再用清水洗净，萝卜洗净去皮、土豆洗净去皮切成滚刀块，用热油炸至金黄色捞出，葱头去皮，一半切丁，一半切成大块。\r\n(2)牛肉放入锅中，加水置旺火上烧透，放入葱头块、姜片、萝卜、香菜、胡椒，再加料酒、盐、味精，待烧透后移置小火烧至肉酥烂，然后捞出牛肉块，原汤待用。\r\n(3)炒锅置小火上，加入适量的油，放入蒜末、面粉和咖喱粉慢慢炒出香味，再徐徐倒入原汤，边倒边搅(防止出现疙瘩)，最后放入牛肉和土豆块，用小火稍炖片刻，再用大火收浓汤汁。另取炒锅上旺火，加少许油，烧热后将葱', '肉烂味浓，风味独特。', '牛肉要用小火炖烂，土豆下锅时间要适当，防止烧糊锅。', '花生油、精盐、味精、料酒、面粉，咖喱粉、香菜、胡椒、姜片、蒜末。', '牛肉500克、土豆150克、葱头50克。');
INSERT INTO `recipe` VALUES (4200, 53, '砂锅狮子头', '(1)将肉切成末，放入盆内，加葱末，姜末、料酒、味精、盐、淀粉拌匀，用手团成大丸子5个。鸡蛋打在碗里，加淀粉调成稠糊备用。\r\n(2)炒锅加半锅油，上旺火烧至七成热，将肉丸挂上蛋粉糊，下锅煎炸至金黄色捞出。油菜心用开水烫一下备用。\r\n(3)将狮子头放在砂锅中(每碗5个)加上海米、酱油，清汤、料酒、味精、盐，用旺火烧开，移到小火上慢慢炖透，再铺上油菜烧开，调好口即可。', '质地酥软，荤香可口。', '肉丸要挂糊，煎炸成黄色后再炖。防止炸老．', '花生油、酱油、精盐、味精、料酒、淀粉、清汤、麻油、葱姜末。', '猪肉500克，油菜心50克，鸡蛋50克，海米5克。');
INSERT INTO `recipe` VALUES (4201, 53, '椒雪肉片', '(1)将肉洗净切薄片，用刀背在肉片上轻轻捶—遍，泡入用葱姜汁、料酒、白糖、精盐调的卤汁1勺，使肉发松入味，大约半小时后取出。\r\n(2)炒锅上旺火，加半锅油，烧至六成热时，将腌好的肉片挂上蛋清、淀粉、花椒粉调成的浆，两现沾满芝麻，下油锅炸透捞出即可。', '外脆里嫩，咸香可口。', '炸肉时火候要先旺后小，', '白糖、花椒粉、芝麻、花生油、精盐、味精、料洒葱、姜汁等。', '猪肉300克，蛋清25克。');
INSERT INTO `recipe` VALUES (4202, 53, '干炸丸子', '将猪肉洗净、剁成肉泥，荸荠去皮剁成末和猪肉—起放在盒里加蛋清、淀粉、料酒、盐、味精搅成肉馅，用手挤成丸子放油锅中炸至里面热、外面：呈金黄色时捞出装盘，撒上花椒盐，随面酱、葱—起上桌。', '色泽金黄、外焦里嫩。', '炸时火候要先小后大。', '植物油、精盐、料酒、味精、椒盐葱丝。', '猪肉300克，荸荠25克，蛋清15克。');
INSERT INTO `recipe` VALUES (4203, 53, '红烧猪爪', '(1)先将猪爪刮洗干净，斩成小块，放入开水锅中烫一下，撇去浮沫，捞出洗净。\r\n(2)炒锅放底油烧热后，先将葱段，姜块、桂皮、八角下锅煸出香味，再放猪爪翻炒后，加入酱油、精盐、料酒、白糖和清水烧开，移小火烧至肉酥烂，再移至旺火将汤汁收浓即可出锅装盘。', '色泽酱红、口味咸甜。', NULL, '植物油、精盐、酱油，料酒，白糖、葱段、姜块，桂皮、八角。', '猪蹄1000克。');
INSERT INTO `recipe` VALUES (4204, 53, '熏肉', '(1)将肉刮洗干净，修成大方块，用竹签将肉周围扎一遍后用花椒、精盐、料酒腌渍3—4小时，然后放在盆内，加葱、姜、料酒上屉蒸熟取出备用。\r\n(2)锅底放木屑、白糖、桂皮、八角、桔皮、大米、茶叶等，上边架上铁丝架，架上摆上整葱和肉(皮朝下)锅盖严，锅上火烧熏，约15分钟左右即可出锅。\r\n(3)将熏好的方肉切成薄片装盘，配甜面酱、葱段上桌。', '色泽酱红、口味别致，肥而不腻。', NULL, '精盐、料酒、酱油、味精、白糖、大米、桔皮、茶叶，桂皮、八角、木屑、花椒、葱、姜。', '带皮五花肉500克。');
INSERT INTO `recipe` VALUES (4205, 53, '扣肉', '(1)将肉切成大块，和葱、姜、盐、花椒下锅煮至八成熟捞出。\r\n(2)将煮好的肉刮去表面皮上的汤油，抹上酱油或糖色，放白糖渍30分钟后，下烈油锅炸乍至肉皮变红时捞出，晾凉备用。\r\n(3)将晾凉的肉切成长约12cm、厚约0．3cm的大片，皮朝下一片片片辅码在碗中，(每碗10—16片)，上边放上切碎的雪里蕻(或炸别：豆块、油豆腐、碎肉等，加酱油、辣酱、胡椒、葱段、姜片，上屉燕约1小时，下笼后将汤汁滗入炒勺中，将肉扣在盘中。\r\n(4)将滗出的汤汁上火烧开，调好口，勾少许粉芡浇在肉上即可。', '肉皮色洋酱匀：发亮，肥而小腻，荤香可口，', '切扣肉时要皮朝下放肉，肉片要均匀，不宜太厚。扣肉碗中也可先不配其它菜，待蒸好后放上些炒好的青菜，吃起来荤素搭配，别有风味．', '花生油、精盐、酱油、白糖、辣酱、葱姜、花椒、胡椒，雪里蕻等。', '带皮五花肉750克。');
INSERT INTO `recipe` VALUES (4206, 53, '红腐烧肉', '(1)将肉洗净，放在锅里煮至四成熟(中间无血水)拆去骨头，在肉皮上川刀划上小方块(只切断肉皮，底部相连)。\r\n(2)将红米放锅里，加水熬成汁。\r\n(3)将肉放入锅中，加水和葱、姜、料酒、白糖、腐乳汁、匀：米汁、精盐，烧至酥烂时将肉捞出，扣在大碗里，皮朝下，将汤汁收浓浇在肉上，再上屉蒸一下。上桌时扣在盘中，皮朝上，再将肉汤汁收浓，浇在肉上即成。', '色泽红亮、肥嫩软滑，入口即化，口味咸甜。', NULL, '葱段、姜块、料酒、精盐、红腐乳汁，白糖、红米各适量。', '猪五花肉750克。');
INSERT INTO `recipe` VALUES (4207, 53, '红烧牛肉', '(1)牛肉洗净切成小方块，用冷水泡约30分钟，放开水锅中氽透，再用水洗净。\r\n(2)炒锅放油烧热后，放葱，蒜、姜、大料、桂皮、丁香炒出香味后，放牛肉，烹料酒、酱油、白糖、盐和水烧沸，撇去浮沫，用小火烧至酥烂即可。', '色红，肉烂。', '炖时火要小，一次烧成．', '花生油、精盐、酱油、料酒、葱段、姜块、蒜瓣、大料、桂皮、丁香、白糖。v', '花生油、精盐、酱油、料酒、葱段、姜块、蒜瓣、大料、桂皮、丁香、白糖。v');
INSERT INTO `recipe` VALUES (4208, 53, '酱爆肉片', '(1)将肉洗净切成薄片，放入盆中，加料酒、精盐、淀粉、蛋清上浆，蒜苗洗净切段。\r\n(2)炒锅上旺火，加半锅油，烧至六成热时，将肉片下锅滑开后捞出。炒勺上旺火，加少许底油，放蒜苗炒至出香味时，再放料酒、酱油、甜面酱、白糖、味精、花椒油，炒至有酱香味时下肉片翻炒几下即可。', '色泽酱红、口味甜咸。', NULL, '植物油、酱油，精盐，味精，料酒、淀粉，甜面酱、', '猪肉350克，蒜苗25克，蛋清25克。');
INSERT INTO `recipe` VALUES (4209, 53, '糖醋里脊', '(1)将猪肉片成约1cm厚的片，，用刀在其两面分别剞上交叉的花刀(不能切断)再改刀成长约3cm、宽约1．5cm的条，放入盆中，加少许盐、料酒拌匀、再加淀粉、鸡蛋清成蛋粉糊。\r\n(2)炒锅上旺火，加入半锅油，烧至五成热时，将挂好糊的肉段逐个下入油锅中，用中火炸至里脊里面已熟时捞出，待油至八成热时，再将炸好的里脊下锅氽一次，使其达到外焦里嫩，色泽金黄时捞出。\r\n(3)炒勺上旺火，加少许底油，用葱姜蒜爆锅之后加酱油、白糖、香醋、料酒，炒至糖溶化后，将口味调成甜酸味，用淀粉勾成浓芡，下入炸好的里脊翻滚几下，浇上少', '色微红发亮，口味甜酸，外焦里嫩。', '炸里脊时油温要掌握好，要先低，后中，最后汆炸时要高。炒糖醋汁和最后一次汆炸最好用两个锅同时进行，使刚出油锅的里脊直接进炒勺滚汁。此菜在放在桌上时仍在翻泡为最佳火候。', '植物油、酱油，料酒、精盐、白糖、香醋、淀粉、葱花、姜末、蒜泥各适量。', '猪里脊350克，蛋清25克。');
INSERT INTO `recipe` VALUES (4210, 53, '蜜汁子排', '(1)将小排骨斩成小方块，洗净后沥去水份，放入盆内，加料酒、酱油、盐，味精，葱姜蒜末，陈皮拌匀，再加蛋清、淀粉调匀，放入蒸盘内，上面加碗扣好，以保持其原味，上屉蒸烂取出。\r\n(2)将蒸好的排骨扣在另一盘中，撒上些胡椒粉即可。', '肉酥烂，略有桔香味。', NULL, '酱油、味精、精盐、料酒、白糖、淀粉、胡椒粉，陈皮、蒜末、葱末、姜末各适量。', '小排骨750克，蛋清25克。');
INSERT INTO `recipe` VALUES (4211, 53, '红烧大排', '(1)将大排洗净，切成约50克重的大片，用精盐、湿淀粉上浆。\r\n(2)炒锅加油上中火，待油热后放入排骨炸至黄色捞出。\r\n(3)炒锅内留少许底油，上中火，放大料、葱段、姜块煸炒一下，再放入大排，烹料酒、加水或高汤，其量以漫过排骨为宜，再加白糖，待烧沸后，改用小火烧至肉酥烂为止。用湿淀粉勾芡即可出锅。', '色酱红，肉质酥烂而不离骨，口味咸香微带甜', NULL, '植物油、酱油、精盐、白糖、料酒、淀粉、葱段、姜块，大料各适量。', '猪排骨750克。');
INSERT INTO `recipe` VALUES (4212, 53, '炸猪排', '(1)将猪瘦肉洗净，用刀片切成约1cm厚的大片，放入盆内，加精盐、味精、料酒、姜末拌匀，将鸡蛋打入碗中搅匀备用。\r\n(2)炒锅上火，加上半锅油，烧至七成热时，将肉片粘上千面粉，再拖上鸡蛋液，尔后再两面粘上面包粉，下油锅炸至金黄色，里边熟透后捞出，待油温达到八成热时，再将肉排下锅炸至外面焦脆时捞出，沥干油，改刀成条后装盘即可。吃时蘸辣酱油、甜面酱或番茄酱。', '外焦里嫩，色泽金黄，咸香可口。', '炸时油温要适当，下锅时要高，中间过程要低，出锅前油温要高，使其外焦而里不老。', '植物油，精盐，味精，料酒、面粉，辣酱油、姜末各适量，', '猪瘦肉1000克，鸡蛋清50克，面包粉50克，');
INSERT INTO `recipe` VALUES (4213, 53, '椒盐排骨', '(1)将鸡蛋打入盆内，搅均后放入淀粉。面粉搅拌成蛋粉糊，将排骨洗净剁成小块，放入蛋粉糊中，加料酒、精盐、胡椒粉拌均。\r\n(2)炒锅放油上火烧至七成热时，逐个放进浆好的排骨炸熟后捞出，待临上桌时再下油锅炸至外脆里嫩，呈金黄色时捞出，沥净油后装盘。吃时配上大葱丝和花椒盐，甜面酱。', '色泽金黄，外焦里嫩，口味咸香。', NULL, '植物油，精盐、味精，料酒、淀粉，面粉、胡椒粉，花椒盐、甜面酱，大葱各适量。', '猪排骨1000克，鸡蛋50克。');
INSERT INTO `recipe` VALUES (4214, 113, '白冰糖绿豆汤', '每次用白冰糖150克，绿豆100克，加水1500毫升，煮熟后待凉饮用。', '清香味甜，绿豆，性味甘、凉、入心、胃经。白冰糖，性味甘、平、入脾、肺经。', NULL, NULL, '白冰糖、绿豆。');
INSERT INTO `recipe` VALUES (4215, 113, '菠菜猪血汤', '用鲜菠菜500克，切断，猪血250克，切成块状，入锅加清水适量煮汤，调味后食用。', '清香可口。菠菜，性味甘、凉、入肠、胃经。猪血，性味咸平。', NULL, NULL, '菠菜、猪血。');
INSERT INTO `recipe` VALUES (4216, 113, '川贝杏仁饮', '将洗净的杏仁、川贝同置锅内，加水烧沸后，放入冰糖或蜂蜜，转用文火煮30分钟，每日临睡前服5－10毫升。', NULL, NULL, NULL, '川贝母6克，杏仁3克，冰糖或蜂蜜少许。');
INSERT INTO `recipe` VALUES (4217, 113, '聪耳酒', '将磁石捣碎，用纱布包裹；石菖蒲用米泔水浸2日后切碎，微火烤干。把3味药装入纱布袋里，与白酒同置入容器中，密封浸泡7天后即可服用。', NULL, NULL, NULL, '磁石30克，木通、石菖蒲各80克，白酒1700克。');
INSERT INTO `recipe` VALUES (4218, 113, '丹参蜜饮', '丹参、檀香、灸草加水煎煮后，去渣取汁，调入蜂蜜，再煎几沸，随饮。', NULL, NULL, NULL, '丹参15克，檀香9克，灸甘草3克，蜂蜜30克。');
INSERT INTO `recipe` VALUES (4219, 113, '当归独活酒', '将独活去芦头后，上3味药中，先将独活、当归捣碎细，置于净器中，以白酒浸泡。1宿后，将大豆炒至青烟出，投入酒中密封，候冷，去渣备用。', NULL, NULL, NULL, '独活60克，大豆500克，当归10克，白酒1000克。');
INSERT INTO `recipe` VALUES (4220, 113, '当归酒', '将当归切薄片，浸于白酒中3－5日即可。', NULL, NULL, NULL, '当归250克，白酒1000克。');
INSERT INTO `recipe` VALUES (4221, 113, '党参红枣茶', '党参30克，大枣10个，煎汤饮用。', '香甜可口。党参，性味甘，微温。红枣，性味甘温，入脾经。', NULL, NULL, '党参、红枣。');
INSERT INTO `recipe` VALUES (4222, 113, '党参酒', '选粗大之连须党参，将参拍出裂缝，置净器中，以酒500克浸泡，密封，经7日后开取。', NULL, '表症未解（即感冒发热），中满邪实者勿用。', NULL, '老条党参1条，白酒500克。');
INSERT INTO `recipe` VALUES (4223, 113, '地黄酒', '先将地黄洗净，切成薄片，倒入净坛内，注入白酒封固，浸7天以上即成。', NULL, '凡脾虚有湿，腹满便溏及阳虚怕冷诸症忌用。服用期间勿食贝母、芜荑、萝卜及莱菔子。', NULL, '地黄30克，白酒1000克。');
INSERT INTO `recipe` VALUES (4224, 113, '跌打药酒', '将上药捣碎，与白酒同置入容器中，密封浸泡45日以上，过滤后即可服用。', NULL, NULL, NULL, '赤芍13克，当归10克，生地黄、莪术、刘寄妈、三棱、泽兰、泽泻川芎、桃仁各8克，红花、苏木各6克，土鳖虫4克，田七1克，白酒1000克。');
INSERT INTO `recipe` VALUES (4225, 113, '丁香蜜米饮', '温水浸泡丁香、陈皮，以浸透为度，大火煮沸，小火煮15分钟后取汁，调入蜂蜜、米饮。每服5－10毫升，日服4－5次。', NULL, NULL, NULL, '丁香2克，陈皮3克，蜂蜜、米饮各适量。');
INSERT INTO `recipe` VALUES (4226, 113, '二红煎', '上述二料加水三碗，煮取一碗。日制一剂，随意饮用。', NULL, NULL, NULL, '红（胡）萝卜200克，红枣12克。');
INSERT INTO `recipe` VALUES (4227, 113, '二汁饮', '洗净，分别榨汁，混合后饮用。每服一盅，日服2－3次。', NULL, NULL, NULL, '鲜藕、白梨等量。');
INSERT INTO `recipe` VALUES (4228, 113, '蜂蜜银花水', '用蜂蜜30克、金银花15－30克，先将金银花煎水，去渣放凉，分次加入蜂蜜溶化后饮用。', '蜂蜜，性味甘、平，入肺、脾、大肠经。金银花，性味甘、寒，入肺、脾、胃、心经。', NULL, NULL, '蜂蜜，金银花。');
INSERT INTO `recipe` VALUES (4229, 113, '甘草麦枣汤', '用甘草10克、小麦30克、大枣5个，清水1000毫升煎至500毫升，去渣饮汤。', '香甜可口。甘草性味甘、平、入脾、胃、肺经。小麦，性味甘、平、入心、脾、肾经。大枣，性味甘、温、入脾、胃经。', NULL, NULL, '甘草、小麦、大枣。');
INSERT INTO `recipe` VALUES (4230, 113, '龟胶酒', '用酒将龟板胶煮化即成。', NULL, '脾胃虚寒，腹胀便溏者忌服。', NULL, '龟板胶10克，黄酒50克。');
INSERT INTO `recipe` VALUES (4231, 113, '寒凉咳嗽酒', '上药共捣碎装入细纱袋中，扎紧口，置入容器中，倒入白酒浸泡，密封，隔天振摇一次，12天以后开封，弃去药渣，过滤即成。', NULL, '咳嗽属阴虚，久咳痰少，痰中带血丝，口燥咽干者忌服。', NULL, '全紫苏60克，杏仁、瓜蒌皮、浙贝母、半夏、枳壳、百部、桔梗、桑白皮、枇杷叶、茯芩各15克，陈皮、干姜各30克，细辛、豆蔻仁、五味子各7.5克，甘草1.5克，白酒2500克。');
INSERT INTO `recipe` VALUES (4232, 113, '红薯糖水', '每次用红薯300克，削去外皮，切成小块，加适量清水烧煮，待红薯熟透后起锅，加入适量食糖、生姜片，再煮片时饮用。', '味甜而香，营养丰富。', NULL, NULL, '红薯、食糖。');
INSERT INTO `recipe` VALUES (4233, 113, '还童酒', '将上药加工碎，用绢布袋或细纱布盛贮，扎紧口，放入坛中，再倒入陈酒，加盖密封。经14天后启封，取去药袋，用纱布过滤一遍即成。', NULL, NULL, NULL, '熟地、秦艽、麦冬各45克，生地、当归、五加皮各60克，川萆、怀牛膝、苍术、陈皮、川断、枸杞子、丹皮、木瓜各30克，羌活、独活、小茄香、乌药各15克，桂皮7.5克，陈酒5000克。');
INSERT INTO `recipe` VALUES (4234, 113, '健脾酒', '将上药加工研为细末，加适量蜂蜜调匀为丸（每丸约9克重），每丸用500克酒浸泡。', NULL, NULL, NULL, '木香9克，丁香、檀香各6克，茜草60克，砂仁15克，红曲30克，白酒、蜂蜜适量。');
INSERT INTO `recipe` VALUES (4235, 113, '金针煮红糖', '每次可用金针菜60克，红糖适量，加水煮熟。每天早饭前服用，连服3－4天。', '金针菜，性味甘、凉。红糖，性味甘平。', NULL, NULL, '金针菜、红糖。');
INSERT INTO `recipe` VALUES (4236, 113, '菊花酒', '将上4味药物加水煎煮取浓汁，用纱布过滤。再将糯米煮半熟后沥干，与药汁混匀后再蒸熟，待凉后拌上酒曲，装入瓦坛中发酵，味甜后即成。', NULL, NULL, NULL, '甘菊花500克，生地黄300克，枸杞子、当归各100克，糯米3000克，酒曲适量。');
INSERT INTO `recipe` VALUES (4237, 113, '鹿茸山药酒', '将鹿茸、山药与白酒共置入容器中，密封浸泡7天以上便可服用。', NULL, NULL, NULL, '鹿茸15克，山药60克，白酒1000克。');
INSERT INTO `recipe` VALUES (4238, 113, '马齿苋绿豆汤', '用新鲜马齿苋120克（干者30克），绿豆60克，煎汤服用，每日一次，连服3天。', '清凉解毒。马齿苋，性味酸、寒、入大肠、肝、脾、心经。绿豆，性味甘、凉、入心、胃经。', NULL, NULL, '马齿苋、绿豆。');
INSERT INTO `recipe` VALUES (4239, 113, '美容酒', '将上药切成小片与黄酒一起置入容器中，密封浸泡7天即成。', NULL, NULL, NULL, '人参、当归、玉竹、黄精、制首乌、枸杞子各30克，黄酒1500克。');
INSERT INTO `recipe` VALUES (4240, 113, '木耳煮红枣', '每次可用黑木耳15－30克，红枣20－30个，煎汤食用，每日一次，连食数日。', '香甜可口，营养丰富。黑木耳，性味甘、平。红枣，性味甘、温、入脾经。', NULL, NULL, '黑木耳、红枣。');
INSERT INTO `recipe` VALUES (4241, 113, '牛膝复方酒', '上药共捣碎，置于净坛中，用白酒浸之，密封口，经7天后开取。', NULL, NULL, NULL, '石斛、杜仲、丹参、生地各60克，牛膝120克，白酒1500克。');
INSERT INTO `recipe` VALUES (4242, 113, '芹菜煮枣', '芹菜400克、红枣100克，煮汤分次服用。', '甜而香。芹菜，性味甘、凉、入肺、胃经。红枣，性味甘平。', NULL, NULL, '芹菜、枣。');
INSERT INTO `recipe` VALUES (4243, 113, '芹汁蜜汁', '芹菜洗净捣烂后取汁，与蜂蜜同炖温服，日服一次。', NULL, NULL, NULL, '鲜芹菜100-150克，蜂蜜适量。');
INSERT INTO `recipe` VALUES (4244, 113, '祛风活血酒', '将白芍炒过，黄柏盐炒，杜仲姜炒，上药捣碎入绢袋中，倒入白酒贮于瓮中，火上煮1小时，去渣备用。', NULL, '如久痛加虎胫骨（酥炙）25克，苍术（炒）30克。', NULL, '当归、白芍、生地、牛膝、秦艽、木瓜、黄柏、杜仲、防风、白芷、陈皮各30克，川芎、羌活、独活各25克，槟榔18克，肉桂、炙甘草各10克，油松节15克，白酒1500克。');
INSERT INTO `recipe` VALUES (4245, 113, '人参酒', '将人参切成段或薄片，浸入优质白酒中，加盖密封，置于阴凉处，每日振摇1次，浸泡7天后饮用。', NULL, '服用期间，不宜喝茶，忌食萝卜。反藜芦。', NULL, '白人参30克，白酒500克。');
INSERT INTO `recipe` VALUES (4246, 113, '三豆饮', '三豆洗净，同入砂锅内水煎，煮烂，调入白糖，作饮料频饮。', NULL, NULL, NULL, '赤小豆、绿豆、黑豆各100克，白糖适量。');
INSERT INTO `recipe` VALUES (4247, 113, '三汁饮', '三味洗净，后二味切片；麦冬，生地置一锅内，藕放另一锅内，分别加水，烧沸，文火煎；前者煎20分钟，后者煎30分钟，取汁混合，酌加白糖，代茶饮，不拘次数。', NULL, NULL, NULL, '麦门冬10克，生地15克，藕200克。');
INSERT INTO `recipe` VALUES (4248, 113, '生津滋胃饮', '鲜青果去核，橙子带皮切碎，与绿豆、竹叶同置锅内，加水750毫升，煎煮一小时，静置片刻，取上部清汁随意温服。', NULL, NULL, NULL, '绿豆15克，鲜青果20个，竹叶3克，橙子一个。');
INSERT INTO `recipe` VALUES (4249, 113, '首乌酒', '先将首乌焖软，切成1厘米见方小丁，生地黄洗净切片，同放入酒瓶中，密封浸泡15天即可服用。', NULL, NULL, NULL, '制首乌、生地黄各40克，白酒1000克。');
INSERT INTO `recipe` VALUES (4250, 113, '四补酒', '将上药加工切碎，入净器中，倒入白酒浸泡，封固。置阴凉处，每日摇晃数下，春夏10日，秋冬20日，澄清即得。', NULL, NULL, NULL, '柏子仁、何首乌、肉苁蓉、牛膝各30克，白酒1000克。');
INSERT INTO `recipe` VALUES (4251, 113, '天冬白糖水', '天冬15－30克，用清水1000克煎至400克，加入红糖适量再煮开，温服。', '香甜可口。天冬，性味甘、苦、寒，入肺肾经。红糖，性味甘平。', NULL, NULL, '天冬、红糖。');
INSERT INTO `recipe` VALUES (4252, 113, '无花果冰糖水', '每次用无花果30克，冰糖适量，加水煮沸后饮用。每天一次，连服3－5天即可见效。', '清香可口。无花果，性味甘、平。冰糖，性味甘、平，入脾、肺经。', NULL, NULL, '无花果、冰糖。');
INSERT INTO `recipe` VALUES (4253, 113, '五味酒', '将上药加工碎，倒入净坛里，注入白酒，以淹没药物为宜，封固，隔水加热至药片蒸透，取坛放凉，再浸数日，即可启用。', NULL, NULL, NULL, '淫羊藿125克，胡桃肉、怀生地各60克，枸杞子、五加皮各30克，白酒适量。');
INSERT INTO `recipe` VALUES (4254, 113, '西洋参酒', '将西洋参加工粗碎，置净瓶中，入白酒（或黄酒）浸泡，加盖密封，每日振摇数下，经14天后便可开取饮服。', NULL, '忌食萝卜。反藜芦。体质虚寒者不宜用，腹冷痛，寒性腹泻者忌用。', NULL, '西洋参30克，白酒500克。');
INSERT INTO `recipe` VALUES (4255, 113, '养心安神酒', '将前7味药装入纱布袋内，与白酒一起置入容器中，密封浸泡15天以上。密封浸泡期可加温2－3次，以利有效成分析出。', NULL, '脾胃虚弱，肠胃腹泻者慎服。', NULL, '茯苓、柏子仁（去油）、归身各30克，生地黄45克，酸枣仁15克，麦门冬、龙眼肉各60克，白酒3000克。');
INSERT INTO `recipe` VALUES (4256, 113, '益智健脑酒', '将上药加工粗碎，装入细纱布袋扎紧口，放入坛内，倒入白酒，加盖封固，置阴凉处，经常摇动。7天后开封过滤即可。', NULL, NULL, NULL, '远志、熟地、菟丝子、五味子各36克，石菖蒲、川芎各24克，地骨皮48克，白酒1200克。');
INSERT INTO `recipe` VALUES (4257, 113, '竹黄酒', '将竹黄置白酒中，密封浸泡1周。', NULL, NULL, NULL, '竹黄50克，白酒500克。');
INSERT INTO `recipe` VALUES (4258, 113, '壮阳酒', '将红参、鹿茸蒸软后，放入白酒中，加盖密封，浸泡15天即可饮用。', NULL, '夏日不宜饮用。易上火者慎服或禁服。', NULL, '红参20克，鹿茸6克，白酒1000克。');
INSERT INTO `recipe` VALUES (4259, 113, '紫菜猪肉汤', '用紫菜15克，猪瘦肉100克，清水适量煎汤，加油、盐等调味品食用。', '紫菜，性味甘、咸、寒。猪瘦肉性味甘、咸、平，入脾、胃、肾经。', NULL, NULL, '紫菜、瘦猪肉。');
INSERT INTO `recipe` VALUES (4260, 114, '豆浆晶', NULL, '豆浆晶速溶性好，无分层、无沉淀，清甜顺滑、豆香味浓，有良好速溶性和丰富口感。该品为淡黄色晶粒状，用温开水冲调即可饮用，放入冰箱冰冷后风味更佳。', NULL, NULL, '优质黄豆、白砂糖。');
INSERT INTO `recipe` VALUES (4261, 114, '豆奶粉', '豆奶粉采用超微细化处理工艺，使大豆子叶能够进行全利用，从而提高产品中食用纤维的含量，膳食纤维含量比同类产品高，对于防治成年人病有一定作用。在加工过程中，豆奶粉利用先进的生产工艺，去除了大豆中对人体不利的不良成分, 如：胰蛋白酶抑制素、凝血素等，使消费者喝的放心。另外经过多次的脱腥工艺，提高了产品的感观效果。', '豆奶粉综合大豆和牛奶的营养成份，具有口感细腻、香味浓郁、营养丰富、携带方便等特点。豆奶粉冲调简单，饮用时只需兑以热开水，稍加搅拌即可享用。', NULL, NULL, '大豆、牛奶。');
INSERT INTO `recipe` VALUES (4262, 114, '核桃粉', NULL, '核桃粉以核桃仁为原料，按照营养互补的科学原理，配以黄豆、芝麻等辅料，采用现代先进工艺精制而成，是一种纯天然营养滋补保健食品。富含优质蛋白质（包括人体必需的八种氨基酸）维生素、以及人体易缺的钙、铁、锌等矿物营养素，营养丰富、全面均衡，能满足不同消费者的需求，具有良好的滋补作用。', NULL, NULL, '核桃仁、黄豆、芝麻。');
INSERT INTO `recipe` VALUES (4263, 114, '黑芝麻糊', NULL, NULL, NULL, NULL, '黑芝麻、核桃仁、花生仁等。');
INSERT INTO `recipe` VALUES (4264, 114, '花生晶', '选用优质花生仁、白砂糖为原料经先进真空干燥技术精制而成固体饮料。', '花生晶为灰白色固体粉末状，用温开水冲调能迅速溶解，色泽乳白诱人，口感顺滑，有典型花生香味，常饮有益，是一种健康营养的风味饮品。', NULL, NULL, '花生仁、白砂糖。');
INSERT INTO `recipe` VALUES (4265, 114, '花生奶', '花生奶是以花生、牛奶为主要原料，经特殊脱脂工艺去除部分油脂，以先进的提取技术保留优质蛋白，采用真空干燥加工技术加工而成的具有浓郁花生乳香风味的速溶固体饮料。', '花生奶为乳白色粉粒状，用温开水即冲即溶即饮，色泽乳白诱人，口感顺滑，有典型的花生香味，其低脂特点符合保健食品的要求，满足特殊人群的需要，是一种理想的低脂饮品。', NULL, NULL, '花生、牛奶。');
INSERT INTO `recipe` VALUES (4266, 114, '麦片', NULL, '经膨化加工而成的速溶食品，无飘浮物，稠度可调，形态各异，有粒状、圆片状、扁状等。是学生课间加餐，上班族早、晚茶点的最佳选择。', NULL, NULL, '玉米、大米、大豆、芝麻、花生、燕麦、荞麦等谷物。');
INSERT INTO `recipe` VALUES (4267, 114, '麦乳精', '以优质奶粉、麦精、奶油、葡萄糖、蛋黄粉等为原料，经科学配方，真空干燥精制而成。', NULL, NULL, NULL, '奶粉、麦精、奶油、葡萄糖、蛋黄粉等。');
INSERT INTO `recipe` VALUES (4268, 114, '杏仁豆浆晶', NULL, '产品为淡黄色固体粉末状，用温开水冲调能迅速溶解，色泽洁白诱人，杏仁风味突出，常饮有益，是一种健康营养的风味饮品。', NULL, NULL, '黄豆、杏仁等。');
INSERT INTO `recipe` VALUES (4269, 115, '刺梨酒', '刺梨酒是采用优质野生刺梨果实，经清洗、压榨，低温发酵陈酿而成的低酒精度果酒。', '刺梨酒的感观为淡黄色或金黄色，澄清透明，具有清雅的刺梨原果香及协调的酒香，口味柔和、纯净、爽利，具有刺梨酒特有的典型风格。', '剌梨含有极丰富的维生素C，刺梨中天然VC含量平均高达2500mg/100g，而且易被人体吸收，有水果中“维生素C之王”的称号，所以被誉为“新山珍”。刺梨果可食部分含有蛋白质0.7%，脂肪0.1%，碳水化合物13%，粗纤维4.1%。刺梨果实含维生素C2400－2729mg/100g，胡罗卜素2.9mg/100g，维生B10.05mg/100g，维生素B2 0.03mg/100g，维生素pp0.75mg/100g，维生素E0.73mg/100g。刺梨中还含有较高的SOD，可防衰老、抗癌变。刺梨酒中含有18种人', NULL, '刺梨');
INSERT INTO `recipe` VALUES (4270, 115, '广柑酒', '广柑酒是以广柑果为主要原料，经分选，去皮，挤压发酵，补加香料酒，精心配制而成的低度果酒。', '广柑酒的感观为淡黄色或橙黄色，澄清透明，具有广柑浓郁的果香和优美的酒香，香气协调，口味纯正柔协，甘酸适口，丰满醇厚，有广柑酒典型风格。', NULL, NULL, '广柑果');
INSERT INTO `recipe` VALUES (4271, 115, '黑加仑酒', '黑加仑酒（学名黑穗栗）是选用东北地区特产黑豆为原料，采用发酵，浸泡，陈酿，调配而成的一种风格独特的野生果酒。', '黑加仑酒呈深宝石红色，澄清透明，有明显的黑豆果香，果香和酒香协调，酒味纯净，酸甜适口，后味绵长，具有独特风格。', NULL, NULL, '东北地区特产黑豆');
INSERT INTO `recipe` VALUES (4272, 115, '红豆酒', '红豆酒是采用大兴安岭林区寒带林中盛产的野生浆果－红豆果为原料，低温发酵酿造而成的营养健身酒。', '红豆酒色泽艳丽悦人，呈宝石红色，是果实本身的天然色素。红豆酒醇厚柔顺，芳香浓郁，风味独特。', NULL, NULL, '野生浆果－红豆果。');
INSERT INTO `recipe` VALUES (4273, 115, '红桔酒', '桔子酒是将桔子皮去除，经压榨，发酵，陈酿，精心调配而成的低度果酒。', '红桔酒的感观性质：黄色，澄清透明，桔香明显，桔香酒香协调，口味醇厚，酒体丰满，甜酸适度，具有桔子酒独特的典型风格。', NULL, NULL, '桔子');
INSERT INTO `recipe` VALUES (4274, 115, '沙棘酒', '沙棘酒是采用野生沙棘果为原料，经发酵或半发酵酿制而成的低度果酒。', '沙棘酒感观为金黄色，清亮透明，具有沙棘果香和醇正的酒香，酸甜爽口，甘润醇厚，其有独特的典型沙棘果酒风格。', NULL, NULL, '野生沙棘果。');
INSERT INTO `recipe` VALUES (4275, 115, '山楂酒', '山楂酒是以山楂果为原料，经脱臭酒精浸泡和发酵工艺结合酿制而成的一种低度果酒。', '山楂酒为宝石红色，清亮透明，有浓郁的山楂果香和陈酿酒香，香气协调，酸甜适口，醇厚丰满，余味绵长，具有山楂酒的典型风格。', NULL, NULL, '山楂果。');
INSERT INTO `recipe` VALUES (4276, 115, '五味子酒', '五味子酒是以五味子果实为原料，经过精选，发酵，陈酿而成的一种风格独特的果酒。', '五味子酒呈浅宝石红色，透明晶亮，具有五味子果浓郁悦人的果香及酒香，酒体丰满，酸甜适口，柔协绵长，具有独特风格。', NULL, NULL, '五味子果实。');
INSERT INTO `recipe` VALUES (4277, 115, '香梅酒', '香梅酒是以树梅果为原料，经分选，破碎，发酵与浸泡相结合的工艺，精制而成的果酒。', '香梅酒为宝石红色，澄清透明，具有浓郁悦人的果香及酒香，香气协调，酸甜适口，醇和柔美，酒体丰满，典型性强。', NULL, NULL, '树梅果。');
INSERT INTO `recipe` VALUES (4278, 115, '杨梅酒', '杨梅酒是以鲜杨梅为原料，经破碎、榨汁、发酵、陈酿精制而成的果酒。', '杨梅酒的感官性质为淡橙红色，清亮透明，有杨梅果独特的香气，果香酒香协调，酸甜适口，口味醇和舒顺，具有杨梅果酒独特风格。', NULL, NULL, '鲜杨梅');
INSERT INTO `recipe` VALUES (4279, 115, '越桔酒', '越桔酒是采用大兴安岭地区寒带山林中特有的野生浆果为原料，加入人工培养酵母，低温发酵酿制而成的野生果酒。', '越桔酒色泽深宝石红，光泽艳丽悦人，香气浓郁，酒体丰满，结构感很强，具有野生浆果独特风格。', NULL, NULL, '野生浆果');
INSERT INTO `recipe` VALUES (4280, 115, '中华猕猴桃酒', '中华猕猴桃酒，是由野生猕猴桃果为原料，酿制调配而成的低度果酒。', '中华猕猴桃酒的感观性质为微黄，浅黄或微黄带绿色，澄清透明，具有猕猴桃清雅的果香及和谐的酒香，口味纯正柔顺，酸甜适度，有该酒的典型风格。', NULL, NULL, '野生猕猴桃果。');
INSERT INTO `recipe` VALUES (4281, 115, '紫梅酒', '紫梅酒是以黑穗状醋栗为原料，经挑选破碎，发酵与浸泡相结合的工艺精制而成的果酒。', '紫梅酒呈紫红色或深宝石红色，澄清透明，有黑豆果香及陈酿酒香，香气协调，口感醇和，酸甜适中，酒体丰满，具有本品典型风格。', NULL, NULL, '黑穗状醋栗（黑加仑子、黑豆、斯马劳金）。');
INSERT INTO `recipe` VALUES (4282, 116, '草莓汁', '将草莓洗净，除去花托，用粉碎机打碎，用清洁的纱布筛过滤得清液。 将草莓渣加水浸没，煮沸，过滤得汁，与第一次汁液合并。 草莓汁与白糖混合、搅匀，将汁煮沸。晾凉，装入消毒瓶内，放入冰箱冷藏待用。草莓必须成熟、新鲜、无腐烂。可用人工将草莓捣烂，用纱布筛过滤。', NULL, NULL, NULL, '草莓250克、白糖55克。');
INSERT INTO `recipe` VALUES (4283, 116, '纯净水', '纯净水制造工艺中不但采用了矿泉水必需的过滤、软化等工艺，更关键的是利用孔径为1/10000m的R.O膜（相当于大肠杆菌的1/6000，病毒的1/3000），将现代社会工业污染、重金属、细菌、病毒等大量混入水中的杂质全部清除，从而得到品质纯净、口味甘甜的太空水。这种采用高分子材料制成的逆渗透膜，在高压情况下可以截留水分子以外的所有杂质，同时却使水中对人体健康有益的溶解氧等气体透过膜加以利用，水中的氧化性物质符合美国药用纯水水质要求，它品质纯净，无毒无菌，是一种可直接入口的含氧活性水。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4284, 116, '多情苹果汁', '将苹果洗净，削皮除果核后切成块状与养乐多及碎冰一起放入果汁机中，打成汁即可饮用。', NULL, NULL, NULL, '苹果一个，养乐多一瓶，碎冰少许。');
INSERT INTO `recipe` VALUES (4285, 116, '凤梨西瓜汁', '1.将凤梨与西瓜去皮切块后放入果汁机内打成汁；\r\n2.加入碎冰与蜂蜜搅拌一下即可饮用。', NULL, NULL, NULL, '凤梨一片，西瓜一片，苹果一个，蜂蜜一大匙，碎冰少许。');
INSERT INTO `recipe` VALUES (4286, 116, '矿泉水', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4287, 116, '酪梨牛奶汁', '将酪梨、草莓洗净削皮去蒂后切成块状，放入果汁机内加入鲜奶打成汁即可饮用。', NULL, NULL, NULL, '酪梨一个，草莓2粒，鲜奶约350cc。');
INSERT INTO `recipe` VALUES (4288, 116, '梨子汁', '1.将苹果、梨子、胡萝卜洗净，削皮除果核后切成块状与冷开水一同放入果汁机内打成汁；\r\n2.加入蜂蜜，果糖拌匀即可饮用。', NULL, NULL, NULL, '梨子半个，苹果半个，胡萝卜四分之一条。蜂蜜少许，果糖，冷开水约100cc。');
INSERT INTO `recipe` VALUES (4289, 116, '凉开水', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4290, 116, '柳橙汁', '柳橙洗净对切成半，用研磨器旋转取出果肉后，再用滤网过滤，即成柳橙汁。', NULL, NULL, NULL, '新鲜柳橙一个，副食品研磨器一G。');
INSERT INTO `recipe` VALUES (4291, 116, '柠檬蜂蜜汁', '1.将柠檬洗净切成两半，用榨汁器将汁压出备用；\r\n2.将冷开水与碎冰一同放入果汁机内搅拌一下；\r\n3.加入蜂蜜及柠檬汁再搅拌一下倒入杯中即可饮用。', NULL, NULL, NULL, '柠檬一个，蜂蜜一大匙，冷开水约200cc，碎冰少许。');
INSERT INTO `recipe` VALUES (4292, 116, '柠檬牛奶汁', '将鲜奶倒入杯中加入柠檬汁，蜂蜜调匀即可饮用。', NULL, NULL, NULL, '柠檬汁少许，蜂蜜少许，鲜奶约350cc。');
INSERT INTO `recipe` VALUES (4293, 116, '苹果蕃茄汁', '1.将苹果洗净，削皮除果核后切成块状放入果汁机内打成汁；\r\n2.将打好的汁加入蕃茄汁，柠檬汁，蜂蜜搅拌一下即可饮用。', NULL, NULL, NULL, '苹果一个，蕃茄汁一小瓶，柠檬汁少许，蜂蜜少许。');
INSERT INTO `recipe` VALUES (4294, 116, '苹果牛奶汁', '1.将苹果洗净，削皮去蒂后切成块状；\r\n2.将碎冰与苹果一同放入果汁机内打成汁；\r\n3.将鲜奶放入果汁机中搅拌一下即可饮用。', NULL, NULL, NULL, '苹果一个，鲜奶约350cc，碎冰少许。');
INSERT INTO `recipe` VALUES (4295, 116, '葡萄柚汁', '葡萄柚先用压汁器榨汁，再加入其他材料，充分搅拌均匀即可饮用。', NULL, NULL, NULL, '葡萄柚1个，蜂蜜3小匙，苏打水200cc，柠檬片1片，冰块2－3块。');
INSERT INTO `recipe` VALUES (4296, 116, '西瓜汁', '1.将西瓜去皮切成块状；\r\n2.将碎冰与西瓜放入果汁机内打成汁；\r\n3.加入蜂蜜拌匀即可饮用。', NULL, NULL, NULL, '西瓜一片，蜂蜜少许，碎冰少许。');
INSERT INTO `recipe` VALUES (4297, 116, '西红柿汁', '西红柿洗净，去皮。把西红柿切成小块，加少量水，捣烂或粉碎，过滤得汁。在汁内加入糖，边搅边煮沸，晾凉。装入消毒瓶，放入冰箱内冷藏待用。', NULL, NULL, NULL, '西红柿250克，白糖45克。');
INSERT INTO `recipe` VALUES (4298, 116, '香蕉苹果汁', '香蕉剥去外皮切块，苹果削皮去核，和冷开水、冰块一起放入果汁机中打，最后再加入柠檬汁搅拌均匀即可。', NULL, NULL, NULL, '香蕉1根，苹果1个，柠檬汁1大匙，冷开水150cc，冰块2－3块。');
INSERT INTO `recipe` VALUES (4299, 116, '香蜜桑椹汁', '1.将桑椹洗净后放入果汁机内打成汁；\r\n2.加入蜂蜜搅拌一下即可饮用。', NULL, NULL, NULL, '桑椹约300克，蜂蜜一大匙。');
INSERT INTO `recipe` VALUES (4300, 116, '综合果汁', '将苹果、草莓、哈密瓜、西瓜和冷开水放入果汁机内打成汁，加入柠檬汁和蜂蜜拌匀即可饮用。', NULL, NULL, NULL, '苹果四分之一个，草莓数粒，哈密瓜一块，西瓜一片；冷开水约100cc；柠檬汁少许，蜂蜜一大匙。');
INSERT INTO `recipe` VALUES (4301, 117, '菠萝红茶', '1.按红茶泡制法制取红茶汁200毫升。\r\n2.在红茶汁中倒入菠萝汁，糖，柠檬汁，调匀。倒入高直桶玻璃杯中。\r\n3.把菠萝和草莓用塑料牙签穿起来，放入杯口装饰。', '爽口，助消化，适合餐后饮用。', NULL, NULL, '红茶5克，菠萝汁100毫升，白砂糖10克，柠檬汁10毫升，菠萝1小块，草莓一颗，冰块2块。');
INSERT INTO `recipe` VALUES (4302, 117, '草莓果茶', '1.按红茶泡制法制取红茶汁70毫升。在红茶中投入30克冰块，使其迅速冷却。\r\n2.在玻璃杯中，加入柠檬糖浆、草莓糖浆，注入红茶汁，插上小匙，调匀即可饮用。', '茶汁红橙，清凉提神。', NULL, NULL, '红茶2克，草莓汁100毫升，冰块100克，柠檬糖浆15毫升，草莓糖浆10毫升，热开水80毫升。');
INSERT INTO `recipe` VALUES (4303, 117, '多味红茶', '1.按红茶泡制法制取红茶汁140毫升。\r\n2.将红茶汁、果汁、白砂糖放入容器中，搅拌匀后，再加入冷开水和白兰地。\r\n3.在两个杯中分别加入冰块，然后注入红茶，各放入薄荷叶3片，橘瓣3片，即可饮用。', '味甜、酸、微辛，解渴提神。', NULL, NULL, '红茶4克，热开水150毫升，橘汁15毫升，白砂糖90克，橘瓣6片，柠檬汁15毫升，冰块6块，白兰地15毫升，薄荷叶6片，凉开水60毫升。');
INSERT INTO `recipe` VALUES (4304, 117, '俄罗斯红茶', '1.将红茶放入壶中，冲入沸水200毫升，和上盖闷3分钟。\r\n2.将热红茶倒入杯中，舀入草莓酱调匀即可。草莓酱视个人口味而定。', '消除疲劳，振奋精神。', NULL, NULL, '红茶2.5克，草莓酱适量。');
INSERT INTO `recipe` VALUES (4305, 117, '蜂蜜红茶', '1.按热红茶泡制法制取红茶汁200毫升。\r\n2.在红茶汁中加入蜂蜜、白砂糖调匀即可。', '茶汁红褐澄清，有利尿、提神的功效。', NULL, NULL, '红茶4克，蜂蜜3毫升，白砂糖8克，水210毫升。');
INSERT INTO `recipe` VALUES (4306, 117, '果味凉红茶', '1.取直筒式大玻璃杯5只，把红茶卤平均倒入5只杯中，把柠檬汁、桔子汁、白砂糖分入五只杯中，用长柄勺搅拌后，放入冰箱冷藏。\r\n2.把凉开水另装放入冰箱冷藏。\r\n3.30分钟以后，取出五杯配料和凉开水，把凉开水兑入五只杯中，把白兰地和薄荷酒平分入杯中。\r\n4.在每只杯中放入一片桔肉，配上长柄匙，饮管即成。', '色泽红亮，香味浓郁，甜中带酸，酒香轻微，清醇凉爽。', NULL, NULL, '（五人份）红茶卤200克，柠檬汁20克，桔子汁20克，白砂糖125克，白兰地5克，桔子肉5瓣，凉开水750克。');
INSERT INTO `recipe` VALUES (4307, 117, '荷兰热红茶', '1.按热红茶泡制法制取红茶汁170毫升。\r\n2.在红茶汁中加入白砂糖和薄荷酒搅匀，放入薄荷绿叶浮在上面，以增加凉爽感。', '红色液面上浮着绿叶，散发出爽快的薄荷香味。', NULL, NULL, '红茶2克，白砂糖15克，薄荷酒9毫升，热开水180毫升，薄荷绿叶2片。');
INSERT INTO `recipe` VALUES (4308, 117, '皇家奶茶', '1.按热红茶泡制法制取红茶汁115毫升。\r\n2.将牛奶热至40度，倒入红茶汁中，调匀即可饮用。', '乳红色液体，是英国正统饮料。营养丰富，提神醒脑。', NULL, NULL, '红茶2克，牛奶85毫升，热开水120毫升。');
INSERT INTO `recipe` VALUES (4309, 117, '皇家热红茶', '1.按红茶泡制法制取红茶汁160毫升。\r\n2.将白兰地倒入红茶汁中，搅匀即可饮用。', '醇香浓郁，风味独特。', NULL, NULL, '红茶2克，热开水170毫升，白兰地9毫升。');
INSERT INTO `recipe` VALUES (4310, 117, '莫斯科热红茶', '1.按红茶泡制法制取红茶汁130毫升。\r\n2.将玻璃杯用热开水烫一下，放入果酱、蜂蜜和酒，注入热的红茶汁，用长匙搅匀，即可饮用。', '醇香浓郁，饮之令人精神振奋。', NULL, NULL, '红茶2克，热开水140毫升，果酱5克，蜂蜜30毫升，伏特加酒14毫升。');
INSERT INTO `recipe` VALUES (4311, 117, '奶绿茶', '1.将绿茶置杯中，注入热开水250毫升加盖，浸泡5分钟，茶汁220毫升注入另一杯中。\r\n2.在热茶汁中加入甜炼乳，搅匀即可。', '茶汁奶黄可口。', NULL, NULL, '绿茶2.5克，甜炼乳15毫升，沸水250毫升。');
INSERT INTO `recipe` VALUES (4312, 117, '柠檬蜂蜜冷红茶', '1.按热红茶泡制法制取红茶汁190毫升。\r\n2.将蛋黄和蛋清分开，在蛋黄中加入蜂蜜，把蛋搅匀，再加入红茶汁，一起搅打均匀，然后滴入柠檬汁混合，冷后倒入两只桶形玻璃杯。\r\n3.蛋清和白砂糖一起搅打成奶油状，注入红茶混合液中。插上吸管供饮用。', NULL, NULL, NULL, '红茶4克，清水200毫升，鸡蛋1个，蜂蜜20克，白砂糖40克，柠檬汁20毫升。');
INSERT INTO `recipe` VALUES (4313, 117, '柠檬红茶', '1.按热红茶泡制法制取红茶汁200毫升。\r\n2.在红茶汁中放入白砂糖、柠檬片1片搅匀。柠檬片1片挂杯口，即可。', '茶汁微红，澄清，甜而微酸，十分爽口，鲜柠檬香味飘逸。', NULL, NULL, '红茶2.5克，白砂糖15克，鲜柠檬片2片，沸水220毫升。');
INSERT INTO `recipe` VALUES (4314, 117, '牛奶红茶', '1.在红茶卤汁中加入白砂糖，调匀得甜红茶汁。\r\n2.把甜红茶汁倒入玻璃杯，倒入加热到40度的牛奶，搅拌均匀，即可饮用。', '清凉，解渴，味甜，具有典型的奶茶味。', NULL, NULL, '红茶卤汁25毫升，鲜牛奶250毫升，白砂糖50克。');
INSERT INTO `recipe` VALUES (4315, 117, '潘趣茶', '1.将红茶卤倒入大玻璃杯中。\r\n2.加入白砂糖，柠檬汁，橘汁，菠萝汁和草莓汁使白砂糖溶解。\r\n3.旋切黄瓜圈一个，放入杯中。\r\n4.最后加入姜汁啤酒或白兰地酒调匀即成。', '色泽艳丽，清凉可口。', NULL, NULL, '红茶卤50毫升，鲜柠檬汁10毫升，橘子汁、白砂糖、草莓汁各25克，菠萝汁35克，黄瓜圈一个，姜汁啤酒或白兰地少许。');
INSERT INTO `recipe` VALUES (4316, 117, '苹果茶', '1.将苹果削皮擦碎，加柠檬汁与水一起放入锅中。\r\n2.将锅置于火上，沸腾后再煮3分钟。\r\n3.熄火后，倒入玫瑰香酒混合，倒入茶杯即可。', '香糯可口，营养丰富。', NULL, NULL, '苹果一个，柠檬汁、砂糖各20克，玉米淀粉、玫瑰香酒各10克，开水100克。');
INSERT INTO `recipe` VALUES (4317, 117, '苹果红茶', '1.苹果去皮，切成0.5到1厘米厚的苹果片，晒干。\r\n2.将晒干后的苹果片干用食品袋装好放入冰箱内贮藏。\r\n3.苹果干放入茶杯中，倒入红热茶。\r\n4.苹果干一泡胀即取出，放入适量砂糖，即可饮用。', '红茶中融入苹果香味，有助消化。', NULL, NULL, '苹果一个，红茶10克，砂糖适量。');
INSERT INTO `recipe` VALUES (4318, 117, '肉桂热红茶', '1.按热红茶汁法制取红茶汁190毫升。\r\n2.在红茶汁中加入白砂糖和肉桂粉搅匀即可。', '红色液体，肉桂香味浓郁。', NULL, NULL, '红茶2克，肉桂粉1.5克，白砂糖15克，热开水200毫升。');
INSERT INTO `recipe` VALUES (4319, 117, '山楂茶', '1.按红茶泡制法制取红茶汁1000毫升。\r\n2.烧沸1000毫升水，将山楂酱、白砂糖倒入，充分搅匀后熄火，滤去沉淀物。\r\n3.将红茶汁、山楂汁加入柠檬汁充分调匀后即可饮用。', '酸酸甜甜，清凉解渴，健胃消食。', NULL, NULL, '红茶5克，山楂酱70克，鲜柠檬汁30毫升，白砂糖150克。');
INSERT INTO `recipe` VALUES (4320, 117, '什锦果茶', '1.将苹果削皮，去芯，切成薄片。将橘子、柠檬切片。\r\n2.按红茶泡制法制红茶汁600毫升。加入水果片，待果香果味渗入红茶汁中，即可注入杯中。按个人口味加入方糖即可。', '味道清爽适口。', NULL, NULL, '红茶10克，苹果1只，橘子1只，柠檬1只，方糖适量。');
INSERT INTO `recipe` VALUES (4321, 117, '水晶红茶', '1.红茶50克，注入开水1400克，在火上烧开4－5分钟，改用小火煮约1小时，滤去茶叶，得红茶卤1000克；加入盐，放入冰箱中冷藏。\r\n2.用冰模制得小方块冰一桶约2000克。\r\n3.先在玻璃杯中放入半杯小方块冰，冲入红茶卤，饮多少冲多少。', '水晶红茶最大的特点是冰多茶少，加入的红茶卤应该控制在只淹及冰的一半高度。在灯光的影射下，红茶中的冰块如水晶般折射出五彩光晕。', NULL, NULL, '红茶50克，盐2.5克。');
INSERT INTO `recipe` VALUES (4322, 117, '杏仁奶茶', '1.将杏仁放在热水中浸泡五分钟，去皮，将杏仁连水一起绞成浆汁，用纱布过滤。\r\n2.将杏仁汁倒入锅中，加入白糖，加入水500毫升，烧沸。\r\n3.取热杏仁汁250毫升，加入适量热牛奶即可饮用。', '果香与奶香浓郁，营养丰富。', NULL, NULL, '牛奶、白糖各250克，杏仁100克。');
INSERT INTO `recipe` VALUES (4323, 118, '奧林匹克', '将冰块和材料倒入调酒壶中摇匀即可。', '这种鸡尾酒诞生在巴黎有名的高级饭店－“丽晶饭店”。橙色柑香酒独特的微苦味道不但将柳橙汁的甜味衬托得恰道好处，同时也使白兰地的香味更加浓郁、香醇。这种鸡尾酒是为了纪念1900年在巴黎举行的奥林匹克运动会所调制的。', NULL, NULL, '白兰地：1/3盎司，橙色柑香酒：1/3盎司，柳橙汁：1/3盎司，调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4324, 118, '白兰地亚历山大', '将上述材料加冰块充分摇匀，滤入鸡尾酒杯后用一颗樱桃进行装饰并在酒面撒上少许豆蔻粉。', NULL, NULL, NULL, '白兰地2/3盎司，棕色可可甜酒2/3盎司，鲜奶油2/3盎司。');
INSERT INTO `recipe` VALUES (4325, 118, '百家地', '将冰块置于调酒壶内，注入酒、石榴糖浆和柠檬汁，充分摇匀，滤入鸡尾酒杯，以红樱桃一颗点缀。', NULL, NULL, NULL, '百家地朗姆酒1/5盎司，鲜柠檬汁1/4盎司，石榴糖浆3/4盎司。');
INSERT INTO `recipe` VALUES (4326, 118, '贝利尼', '1.将冰冷的桃子酒和石榴糖浆倒入杯中搅匀。 \r\n2.倒入冰冷的葡萄酒，轻轻搅匀后即可。', '发泡性葡萄酒口味清爽，加上桃子酒典雅的甜味，就能调制出这种容易入口的鸡尾酒。它是由意大利贝利尼一家有名的餐厅酒吧经营者在1948年发明的，目的是纪念在当地举行的文艺复兴初期画家“贝利尼”画展。', NULL, NULL, '发泡性葡萄酒：2/3盎司，桃子酒(Peach nektar)：1/3盎司，石榴糖：微量。\r\n搅拌长匙、香槟杯。');
INSERT INTO `recipe` VALUES (4327, 118, '边车', '将上述材料摇匀后注入鸡尾酒杯，饰以红樱桃。', '这款鸡尾酒带有酸甜味，口味非常清爽，能消除疲劳，所以适合餐后饮用。这种酒乃是“鸡尾酒十杰”之一，凡饮用过的人无不交口称赞。', NULL, NULL, '白兰地1.5盎司，橙皮香甜酒1/4盎司，柠檬汁1/4盎司。');
INSERT INTO `recipe` VALUES (4328, 118, '冰冻蓝色玛格丽特', '1.用盐将杯子做成雪糖杯型。 \r\n2.将冰块和材料倒入果汁机内，摇匀倒 入杯中即可。', '玛格丽特让人连想起加勒比海深蓝色的海洋，适合在夏日的海边或饭店的游泳池畔品酩。', NULL, NULL, '龙舌兰：30ml，蓝色柑香酒：15ml，砂糖：1茶匙，细碎冰：3/4杯，盐：适量。 果汁机、吸管、葡萄酒杯。');
INSERT INTO `recipe` VALUES (4329, 118, '薄荷鸡尾酒', '先把粉碎的冰粒放入杯内，这些冰粒必须尽量的压碎，随后再倒入苏打水，用汤匙搅打一会儿，使酒、水、冰均匀地分布，就可以饮用了。', NULL, NULL, NULL, '绿色的薄荷酒1/4杯、冰冻的苏打水1/4杯、碎冰粒半杯。');
INSERT INTO `recipe` VALUES (4330, 118, '薄荷茱莉普', '1.把威士忌以外的材料倒入杯中。 \r\n2.一面压碎薄荷叶一面溶解砂糖之后再倒入威士忌。\r\n3.用薄荷做装饰，最后附上吸管。', '薄荷的刺激香味能增添威士忌的味道，让这种鸡尾酒喝起来倍觉清凉，是一种消除口中苦味的甘甜饮料，它诞生在美国南方，属于夏季饮用的鸡尾酒。要使薄荷茱莉普喝起来更可口，可将酒杯放在冰箱中冰镇至杯子泛白。这层白霜遇上清爽的薄荷茱莉普，自然而然散发出一种无法言语的爽快感。', NULL, NULL, '威士忌：60ml，矿泉水：30ml，薄荷叶：3片，砂糖：2茶匙。\r\n高脚玻璃杯、搅拌长匙、吸管。');
INSERT INTO `recipe` VALUES (4331, 118, '布希球', '1.将安摩拉多和果汁倒入装有冰块的杯中。 \r\n2.加满冰冷的苏打水，轻轻搅匀即可。', '安摩拉多(Amaretto)利口酒含有浓厚的杏仁味，以柳橙汁及苏打水调淡后，就是一种口感极佳的鸡尾酒。它虽然诞生在美国，可是安摩拉多利口酒的原产地意大利人也很喜欢这种口味；当地人称它为“Splash”，配方也稍有不同，柳橙汁的份量比安摩拉多稍多。安摩拉多利口酒的酒精浓度在24－28%之间。', NULL, NULL, '安摩拉多利口酒：30ml，柳橙汁：30ml，苏打水：适量。 用具：搅拌长匙、平底杯。');
INSERT INTO `recipe` VALUES (4332, 118, '彩虹酒', '依序将配方慢慢倒入杯中。', '这种鸡尾酒是利用利口酒间的比重差异，调出色彩丰富的鸡尾酒。调制彩虹酒时最需注意的一点是，同一种利口酒或烈酒会因制造商的不同而使酒精度数或浓缩度不同，只要能掌握各种酒的比重数据，就能调出各种不同而漂亮的彩虹酒。', NULL, NULL, '山多利石榴糖浆：1/6盎司，汉密士瓜类利口酒：1/6盎司，汉密士紫罗兰酒：1/6盎司，汉密士白色薄荷酒：1/6盎司，汉密士蓝色薄荷酒：1/6盎司，山多利白兰地：1/6。 用具：利口杯一只。');
INSERT INTO `recipe` VALUES (4333, 118, '草帽', '1.将冰块放入杯中，倒入龙舌兰。 \r\n2.加满2－3倍的冰冷西红柿汁，轻轻搅匀。\r\n3.用柠檬和芹菜做装饰即可。', NULL, NULL, NULL, '龙舌兰：30－40ml，西红柿汁：适量，芹菜根：1根，柠檬片：1片。搅拌长匙、酒杯。');
INSERT INTO `recipe` VALUES (4334, 118, '长岛冰茶', '1.将材料倒入装满细碎冰的杯中搅匀。 \r\n2.用柠檬做装饰，最后附上吸管。', '虽然取名冰茶，却在没有使用半滴红茶的情况下就调制出具有红茶色泽与口味的美味鸡尾酒来。它的酒精成份相当高，千万别被柑香酒、柠檬汁与可乐的甜味蒙骗了。', NULL, NULL, '辛辣琴酒：15ml，伏特加：15ml，无色兰姆酒：15ml，龙舌兰：15ml，无色柑香酒：10ml，柠檬汁：30ml，糖浆：1茶匙，可乐：40ml，柠檬片：1片。搅拌长匙、吸管、大果汁杯。');
INSERT INTO `recipe` VALUES (4335, 118, '得其利', '将上述材料加冰摇匀后滤入糖圈口的鸡尾酒杯，或加有冰块的老式杯内，必要时可多加点糖，用一块鲜柠檬皮装饰。这是一种带有淡蓝灰色彩的鸡尾酒，日本人习惯上称它为“百家地”。这种酒宜餐前饮用或佐餐用，可助消化，增进食欲。', NULL, NULL, NULL, '淡朗姆1.5盎司，柠檬汁1/2盎司，砂糖1/2茶匙。');
INSERT INTO `recipe` VALUES (4336, 118, '反舌鸟', '1.将冰块和材料倒入调酒壶中摇匀。 \r\n2.倒入冰冷的杯中即可。', NULL, NULL, NULL, '龙舌兰：1/2盎司，绿色薄荷酒：1/4盎司，柠檬汁：1/4盎司。调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4337, 118, '公牛弹丸', '1.将冰块和材料倒入调酒壶中摇匀。 \r\n2.倒入加有冰块的杯中。', '这是一种将酒与汤结合在一起的特殊鸡尾酒。这种鸡尾酒的问世，让人更相信只要口味兼容，任何材料都可以拿来调制鸡尾酒。这种鸡尾酒的可口与否，关键全在肉汤的好坏。它是底特律一家名叫科卡斯的俱乐部主人所发明的。', NULL, NULL, '伏特加：30ml，牛肉汤：60ml 。调酒壶、酒杯。');
INSERT INTO `recipe` VALUES (4338, 118, '古典鸡尾酒', '在老式杯中先放入苦精、方糖和苏打水，将糖搅拌后加大冰块、威士忌搅凉后拧大一片柠檬皮，并饰以橘皮和樱桃。这是著名的鸡尾酒品种，酸甜适中，很受欢迎。', NULL, NULL, NULL, '威士忌1.5盎司，方糖1块，苦精1滴，苏打水2匙。');
INSERT INTO `recipe` VALUES (4339, 118, '含羞草', '将果汁倒入杯中，加满香槟即可。也可用柳橙做装饰。', '这种以香槟为基酒的鸡尾酒，被喻为世上最美味、最豪华的柳橙汁。上流社会的人们原本称它为兰休香槟，但因为它的色泽和鲜黄色的含羞草非常相似，所以取名为含羞草。在意大利，它的别名叫BacksFizz。葡萄酒瓶的底部都呈山型状，这不是为了使容量看起来更多，而是有它的缘故在。葡萄酒储存的时间越长，酒气越香，可是在储存的过程中会产生酒石和丹宁酸。当酒瓶站立时，这些沉淀物会沉淀到底部，如果瓶底平坦，沉淀物容易随酒一起注入杯中，要是酒瓶呈山型状，沉淀物会沉淀在凹陷处，就不会发生这种问题了。', NULL, NULL, '香槟(或发泡性葡萄酒)：1/2盎司，柳橙汁：1/2盎司。 葡萄酒杯。');
INSERT INTO `recipe` VALUES (4340, 118, '黑色俄罗斯', '1.将伏特加倒入加有冰块的杯中。 \r\n2.倒入利口酒，轻轻搅匀。', '这种鸡尾酒的特征是，它所散发出的高雅香气，酒精浓度虽高，但却容易入口。这种鸡尾酒以产自俄罗斯的伏特加为基酒，加上它的色泽因而得名，而另一种鸡尾酒-俄罗斯，则有别名为红色俄罗斯以示区别。', NULL, NULL, '伏特加：40ml，咖啡利口酒：20ml。搅拌长匙、岩石杯。');
INSERT INTO `recipe` VALUES (4341, 118, '黑色天鹅绒', '将啤酒与葡萄酒同时从两侧倒入酒杯中即可。', '喝过这种鸡尾酒，你会非常惊讶口味浓厚的啤酒跟清爽的发泡型葡萄酒，竟然这么协调。杯中冉冉上升的细泡宛如丝质天鹅绒般的细致，是一种口感滑溜顺口且十分美丽的鸡尾酒。相信大部分的人都喝过啤酒，但是您知道啤酒瓶盖上有几个锯齿吗？答案是21个，各家都一样，因为少于这个数目则容易脱落，多于这个数目不容易打开，发明者是美国的威廉亚姆-宾塔，从1892年起，这种瓶盖才开始流行，在此之前，啤酒和葡萄酒一样是用软木塞做瓶盖，再用带子加以固定，开瓶时非常麻烦。', NULL, NULL, '黑啤酒：1/2盎司，发泡性葡萄酒：1/2盎司。高脚玻璃杯。');
INSERT INTO `recipe` VALUES (4342, 118, '红粉佳人', '将酒料加冰摇匀至起泡沫，滤入鸡尾酒杯，以红樱桃点缀。', '这是颇负盛名的鸡尾酒，就如同粉红色的佳人一样，很受女士们的欢迎。这种酒颜色鲜红美艳，酒味芳香，入口润滑，适宜四季饮用。', NULL, NULL, '金酒1.5盎司，柠檬汁1/2盎司，石榴糖浆2茶匙，蛋白1个。');
INSERT INTO `recipe` VALUES (4343, 118, '红眼睛', '1.将冰冷的西红柿汁倒入酒杯中。 \r\n2.加满冰啤酒后，仔细搅匀即可。', '这种酒最适合解宿醉，爱喝酒的人通常都会喜欢这种鸡尾酒，它之所以取名为红眼睛，是因为宿醉的人眼睛会发红。喝起来有点像略带苦味的西红柿汁。下次在酒吧看见朋友喝醉时，别忘了帮他点一杯唷！', NULL, NULL, '啤酒：1/2盎司，西红柿汁：1/2盎司。 搅拌长匙、酒杯。');
INSERT INTO `recipe` VALUES (4344, 118, '皇家基尔', '1.将材料倒入冰过的杯中。 \r\n2.轻轻搅匀后即可。', 'Creme de Cassis是法语，在日本及英国，这种鸡尾酒被称为黑醋栗利口酒，这是由虎耳科的落叶灌木果实酿成的，呈暗红色，酒精成份在15－20%左右，其中以法国雷琼市的产品最有名。它的做法是将红醋栗的果汁和渣分开，仅蒸馏渣渣部份，最后才把果汁甜份及香精等放入蒸馏过的渣渣中混匀。', NULL, NULL, '发泡性葡萄酒：4/5盎司，红醋栗利口酒：1/5盎司。 搅拌长匙、漏斗型香槟酒杯。');
INSERT INTO `recipe` VALUES (4345, 118, '吉普森', '将上述材料加冰摇匀后滤入鸡尾酒杯，然后放入大一颗小洋葱。它的别名为“无苦汁的马提尼”，饮用时可放入柠檬皮，口味更加清爽。', NULL, NULL, NULL, '金酒1盎司，干味美思2/3盎司。');
INSERT INTO `recipe` VALUES (4346, 118, '吉普逊', '1.将冰块和材料倒入调酒杯内，搅匀后以隔冰器倒入杯中。 \r\n2.用珍珠洋葱做装饰。', '这种鸡尾酒杯底躺着一粒像珍珠般色泽的洋葱，看起来非常优美、典雅。据说，1890年美国有名的画家查尔斯－D－吉普逊喜欢在纽约“祈祷者俱乐部”喝这种鸡尾酒而得名。也有人说吉普逊是美国大使，他本身不喝酒，但由于大使的身份经常要参加各种酒会；那时候人们认为不喝酒的人不善交际，于是他要侍者把白开水倒入杯中在杯底丢一粒洋葱，假装跟别人一样喝着鸡尾酒；您相信哪一种说法？', NULL, NULL, '辛辣琴酒：5/6盎司，辛辣苦艾酒：1/6盎司，珍珠洋葱：1粒。 调酒杯、隔冰器、搅拌长匙、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4347, 118, '加州柠檬汁', '1.把冰块和材料放入调酒壶中摇匀。\r\n2.加入冰块且倒满冰冷的苏打水。', '这是一种以苏打水调制的琥珀色鸡尾酒。它喝起来的口感舒畅，最适合在空气干燥的加州饮用。', NULL, NULL, '威士忌：45ml，柠檬汁：20ml，莱姆汁：10ml，石榴糖浆：1茶匙，砂糖：1茶匙，苏打水：适量。平底杯、调酒壶。');
INSERT INTO `recipe` VALUES (4348, 118, '教父', '把冰块放入杯中倒入材料轻搅即可。', '安摩拉多酒味甜，散发出一般芳香的杏仁味道，配上浓厚的威士忌酒香，就是美味可口的教父。', NULL, NULL, '威士忌：3/4盎司，安摩拉多(Amaretto)：1/4盎司。 岩石杯、搅拌长匙。');
INSERT INTO `recipe` VALUES (4349, 118, '金菲士', '将碎冰放入调酒壶，注入酒料，摇匀至起泡沫，倒人高脚杯中，并在杯中注满苏打水。', '这种鸡尾酒酒香味甜，入口润滑，常饮可消除疲劳，振奋精神，尤其适宜夏季饮用。', NULL, NULL, '金酒2盎司，君度酒2滴，鲜柠檬汁2/3盎司，蛋白1个，糖粉2茶匙，苏打水适量。');
INSERT INTO `recipe` VALUES (4350, 118, '肯巴利苏打', '1.将肯巴利倒入装有冰块的杯中，加满冰冷的苏打水。 \r\n2.轻轻搅匀后，用柳橙装饰即可。', '苏打水在淡红色的液体中缓缓上升，给人一种轻快的感觉。这种鸡尾酒的特色是肯巴利独特的甘甜及微苦的味道，慢慢在口中散开，给人一种爽快的感觉。这种酒除了深受当地人士（意大利）喜爱外，也广受欢迎。', NULL, NULL, '肯巴利：45ml，苏打水：适量，切片柳橙：1片。 搅拌长匙、平底杯。');
INSERT INTO `recipe` VALUES (4351, 118, '绿色蚱蜢', '将冰块和材料放入调酒壶中摇匀倒入杯中即可。', '这是一种香味很浓的鸡尾酒，杯中散发着薄荷清爽的香味及可可酒的芳香。配方中加了鲜奶油，入喉香浓、滑溜，非常可口。GRASSHOPPER是指蚱蜢，因其酒色呈淡绿色，故名为绿色蚱蜢。此酒口味很甜，可以当甜点饮用。', NULL, NULL, '白色可可酒：1/3盎司，绿色薄荷酒：1/3盎司，鲜奶油：1/3盎司。调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4352, 118, '罗伯罗伊', '将上述材料搅拌均匀后滤入鸡尾酒杯，放入一块柠檬皮。', '这是一种带辛辣味的曼哈顿式鸡尾酒。', NULL, NULL, '苏格兰威士忌2盎司，甜味美思1/2盎司，苦精1滴。');
INSERT INTO `recipe` VALUES (4353, 118, '螺丝起子', '1.将伏特加倒入加有冰块的杯中。 \r\n2.把多于伏特加2－3倍的冰果汁倒入杯中搅匀即可。', '这是一种杯中洋溢着柳橙汁香味的鸡尾酒。在伊朗油田工作的美国工人以螺丝起子将伏特加及柳橙汁搅匀后饮用，故而取名为螺丝起子。', NULL, NULL, '伏特加：40ml，柳橙汁：适量。 平底杯、搅拌长匙。');
INSERT INTO `recipe` VALUES (4354, 118, '螺丝锥子', '将冰块和材料倒入调酒壶内，摇匀倒入杯中即可。', NULL, NULL, NULL, '辛辣琴酒：3/4盎司，柠檬汁：1/4盎司。 调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4355, 118, '螺丝钻', '将碎冰置于阔口矮型杯中，注入酒和橙汁，搅匀，以鲜橙点缀之。', '这是一款世界著名的鸡尾酒，四季均宜饮用，酒性温和，气味芬芳，能提神健胃，颇受各界人士欢迎。', NULL, NULL, '伏特加1.5盎司，鲜橙汁4盎司。');
INSERT INTO `recipe` VALUES (4356, 118, '马丁尼', '1.将冰块和材料倒入调酒杯内，搅匀倒入杯中。 \r\n2.用橄榄做装饰。', '在所有鸡尾酒中，就数马丁尼的调法最多。人们称它为鸡尾酒中的杰作、鸡尾酒之王。虽然它只是由琴酒和辛辣苦艾酒搅拌调制而成，但是口感却非常锐利、深奥。有人说光是马丁尼的配方就有268种之多。据说丘吉尔非常喜欢喝超辛辣口味，所以喝这种酒的时候是一边纯饮琴酒，一边看着苦艾酒瓶。', NULL, NULL, '辛辣琴酒：4/5盎司，辛辣苦艾酒：1/5盎司，橄榄：1粒。调酒杯、隔冰器、搅拌长匙、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4357, 118, '曼哈顿', '1.将冰块和材料倒入调酒杯中，搅匀倒入杯中即可。 \r\n2.用红樱桃做装饰。', NULL, NULL, NULL, '威士忌：2/3盎司，甜味苦艾酒：1/3盎司，香味苦汁：微量，红樱桃：1粒。调酒杯、隔冰器、搅拌长匙、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4358, 118, '美伦鲍尔', '1.将材料倒入装有冰块的杯中。 \r\n2.轻轻搅匀即可。', '这种鸡尾酒，色泽漂亮，味道甘美。瓜类鸡尾酒的甜味配上柳橙汁甜中带苦的味道，别有一番风味。', NULL, NULL, '瓜类利口酒：40ml，伏特加：20ml，柳橙汁：80ml。用具： \r\n搅拌长匙、高脚玻璃杯。');
INSERT INTO `recipe` VALUES (4359, 118, '梦幻勒曼湖', '1.将清酒、冰块、白色柑香酒、樱桃酒与柠檬汁倒入调酒壶中。 \r\n2.摇荡后倒入杯中，加满汤尼汽水。 \r\n3.将蓝色柑香酒慢慢倒入杯底。 \r\n4.附上一根调酒棒即可。', '这是一种杯中呈现蓝色浓淡层次的美丽鸡尾酒，是由上田和男先生发明的，在各种鸡尾酒大赛中获得很多的优胜，也曾获得世界鸡尾酒表演会的银牌。它是以瑞士的梦幻之湖“勒曼湖”为主题所调制而成的鸡尾酒。', NULL, NULL, '清酒：5/10盎司，樱桃酒：1/10盎司，柠檬汁：1/10盎司，汤尼汽水：适量，蓝色柑香酒：微量，白色柑香酒：3/10盎司。调酒壶、调酒棒、高脚玻璃杯。');
INSERT INTO `recipe` VALUES (4360, 118, '尼克拉斯加', '1.倒入九分满的白兰地。 \r\n2.把堆有砂糖的柠檬片放在酒杯上。', NULL, NULL, NULL, '白兰地：1杯，柠檬片：1片，糖浆：1茶匙。 利口杯一只。');
INSERT INTO `recipe` VALUES (4361, 118, '纽约', '1.将冰块和材料放入调酒杯中摇匀倒入杯中。 \r\n2.拧几滴柳橙皮汁即可。', NULL, NULL, NULL, '威士忌：3/4盎司，莱姆汁：1/4盎司，石榴糖浆：1/2匙，砂糖：1匙。调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4362, 118, '青草蜢', '将上述材料充分摇匀，使利口酒和鲜奶油充分混合，滤入鸡尾酒杯，用一棵樱桃进行装饰。', NULL, NULL, NULL, '白可可甜酒2/3盎司，绿薄荷甜酒2/3盎司，鲜奶油（或炼乳）2/3盎司。');
INSERT INTO `recipe` VALUES (4363, 118, '热威士忌托地', '1.把方糖放入温热的平底杯中，倒入少量热开水让它溶化。 \r\n2.倒入威士忌，加点热开水轻轻搅匀。 \r\n3.用柠檬做装饰，最后附上吸管。', NULL, NULL, NULL, '威士忌：45ml，热开水：适量，柠檬片：1片，方糖：1粒。平底杯、搅拌长匙、吸管。');
INSERT INTO `recipe` VALUES (4364, 118, '上海鸡尾酒', '1.将冰块和材料倒入调酒壶中摇匀。 \r\n2.倒入杯中即可。', '黑色兰姆独特的焦味配上茴香利口酒的甜味，调制出口味复杂的上海鸡尾酒，它是以革命前的上海为形象命名的。', NULL, NULL, '黑色兰姆：1/2盎司，茴香酒：1/6盎司，石榴糖浆：1/2盎司，柠檬汁：1/3盎司。 调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4365, 118, '生锈钉', '将碎冰放进老式杯中，注入上述材料慢慢搅匀即成。', '这是著名的鸡尾酒之一，四季皆宜，酒味芳醇，且有活血养颜之功效。', NULL, NULL, '苏格兰威士忌1盎司，杜林标甜酒1盎司。');
INSERT INTO `recipe` VALUES (4366, 118, '特基拉日出', '在高脚杯中加适量冰块，量入特基拉酒，兑满橙汁，然后沿杯壁放入石榴糖浆，使其沉入杯底，并使其自然升起呈太阳喷薄欲出状。', NULL, NULL, NULL, '特基拉酒1盎司，橙汁适量，石榴糖浆1/2盎司。');
INSERT INTO `recipe` VALUES (4367, 118, '天蝎宫', '1.将冰块和材料依序倒入调酒壶内摇匀。 \r\n2.倒入装满细碎冰的杯中。 \r\n3.用柠檬汁、莱姆汁、红樱桃做装饰。 \r\n4.附上一根吸管。', '这种鸡尾酒正如其名，是一种非常危险的鸡尾酒，因为它喝起来的口感很好，等到发现不对的时候，已经相当醉了。喝的时候你一定要小心啊！', NULL, NULL, '白兰地：30ml，无色兰姆酒：45ml，柠檬汁：20ml，柳橙汁：20ml，莱姆汁：15ml，柠檬片：1片，莱姆片：1片，红樱桃：1粒。调酒壶、高脚玻璃杯、吸管。');
INSERT INTO `recipe` VALUES (4368, 118, '威士忌密斯特', '1.把细碎冰放入杯中倒入威士忌。 \r\n2.绞几滴柠檬皮汁后将它丢入杯中附上吸管。', NULL, NULL, NULL, '威士忌：60ml，细碎冰：1杯。岩石杯、吸管。');
INSERT INTO `recipe` VALUES (4369, 118, '威士忌苏打', '1.将冰块放入杯中倒入威士忌。\r\n2.加满冰冷的苏打水轻轻搅拌。', NULL, NULL, NULL, '威士忌：45ml，苏打水：适量。搅拌长匙、平底杯。');
INSERT INTO `recipe` VALUES (4370, 118, '威士忌酸', '将上述材料加冰摇匀后滤入高球杯中，并加满冰苏打水，将一块柠檬皮拧在酒面，再用一片柠檬片和一颗红樱桃装饰。', NULL, NULL, NULL, '威士忌1.5盎司，柠檬汁1/2盎司，砂糖1匙。');
INSERT INTO `recipe` VALUES (4371, 118, '武士岩', '杯中放2、3个冰块，倒入上述材料轻轻搅拌即可。', '不习惯清酒口味的人不妨喝这种鸡尾酒试试看，莱姆汁青涩的香味可以抑制清酒独特曲味，使它较容易入口，相当受欢迎。', NULL, NULL, '清酒：45ml，莱姆汁：15ml。 搅拌长匙、岩石杯。');
INSERT INTO `recipe` VALUES (4372, 118, '新加坡司令', '1.将冰块和材料倒入调酒壶内，搅匀后倒入杯中。 \r\n2.加些冰块、苏打水、再倒入白兰地、以水果装饰。', '它诞生在新加坡波拉普鲁饭店。口感清爽的琴酒配上热情的樱桃白兰地，喝起来口味更加舒畅。夏日午后，这种酒能使人疲劳顿消。', NULL, NULL, '辛辣琴酒：45ml，柠檬汁：20ml，砂糖或糖浆：2匙，樱桃白兰地：15ml，苏打水：适量，红樱桃、柳橙各一。 调酒壶、搅拌长匙、平底杯。');
INSERT INTO `recipe` VALUES (4373, 118, '悬浮式威士忌', '1.将冰块放入杯中倒入矿泉水。 \r\n2.慢慢在上面浮一层威士忌。', '这是一种由矿泉水及威士忌构成的二层式鸡尾酒，看起来非常漂亮。它利用水与威士忌间的比重差，将威士忌悬浮在矿泉水上面。', NULL, NULL, '威士忌：45ml，矿泉水：适量。平底杯一只。');
INSERT INTO `recipe` VALUES (4374, 118, '血玛丽', '在老式杯中放入两块冰块，按顺序在杯中加入伏特加和番茄汁，然后再撒上辣酱油、精细盐、黑胡椒等，最后放大一片柠檬片，用芹菜杆搅匀即可。', '这是一款世界流行的鸡尾酒，甜、酸、苦、辣四味俱全，富有刺激性，夏季饮用可增进食欲。', NULL, NULL, '伏特加1.5盎司，番茄汁4盎司，辣酱油1/2茶匙，精盐1/2茶匙，黑胡椒1/2茶匙。');
INSERT INTO `recipe` VALUES (4375, 118, '血腥玛丽', '1.将冰块倒入杯中，倒入伏特加。 \r\n2.把多于伏特加2－3倍的冰冷果汁倒入杯中，轻轻搅匀。 \r\n3.以柠檬做装饰，附上一根芹菜。', 'BLOODY有血腥之意，鲜红的蕃茄汁看起来很像鲜血，故而以此命名。以带叶的芹菜根代替吸管，像极了一般的健康饮料。据说血腥玛丽的名字是源自英格兰女王玛丽都驿这个人。她是一个可怕的女王，因为迫害新教徒，所以被冠以血腥玛丽的称号。在美国禁酒法期间，这种鸡尾酒在地下酒吧非常流行，称为“喝不醉的蕃茄汁”。', NULL, NULL, '伏特加：45ml，蕃茄汁：20ml，半月形柠檬片：1片，芹菜根：1根。 \r\n拌长匙、平底杯。');
INSERT INTO `recipe` VALUES (4376, 118, '亚历山大', '把冰块和材料放入调酒壶中摇匀。', '19世纪中叶，为了纪念英国国王爱德华七世与皇后亚历山大的婚礼，所以调制了这种鸡尾酒作为对皇后的献礼。由于酒中加入咖啡利口酒和鲜奶油，所以喝起来口感很好，适合女性饮用。', NULL, NULL, '白兰地：1/2盎司，可可利口酒：1/4盎司，鲜奶油：1/4盎司。鸡尾酒杯、调酒壶。');
INSERT INTO `recipe` VALUES (4377, 118, '野莓龙舌兰', '1.将材料放入调酒壶中摇匀，倒入装有碎冰的岩石杯中。 \r\n2.附上吸管与小黄瓜即可。', NULL, NULL, NULL, '龙舌兰：30ml，野莓琴酒：15ml，小黄瓜条：1条，柠檬汁：15ml。调酒壶、岩石杯、吸管。');
INSERT INTO `recipe` VALUES (4378, 118, '一杆进洞', '将冰块和材料依序倒入调酒壶内，摇匀倒入杯中即可。', '虽然取名冰茶，却在没有使用半滴红茶的情况下就调制出具有红茶色泽。每个打高尔夫球的人都希望能有机会一杆进洞。据说这种酒的诞生在最盛行打高尔夫球的美国。它的口味比辛辣曼哈顿还复杂些。', NULL, NULL, '威士忌：2/3盎司，辛辣苦艾酒：1/3盎司，柠檬汁：微量，柳橙汁：微量。调酒壶、鸡尾酒杯。');
INSERT INTO `recipe` VALUES (4379, 118, '约翰柯林', '将上述材料充分摇匀后倒入柯林杯中，并在杯中加满冰苏打水，以柠檬片和樱桃装饰。这是一种甜带酸的消暑饮品。', NULL, NULL, NULL, '威士忌1.5盎司，樱桃白兰地2/3盎司，砂糖3匙，苏打水适量。');
INSERT INTO `recipe` VALUES (4380, 118, '竹子', '1.将材料倒入调酒杯中搅匀。 \r\n2.以隔冰器，倒入鸡尾酒杯中即可。', NULL, NULL, NULL, '辛辣雪莉酒：2/3盎司，辛辣苦艾酒：1/3盎司，香味苦汁：微量。 搅拌长匙、鸡尾酒杯、调酒杯、隔冰器。');
INSERT INTO `recipe` VALUES (4381, 119, '艾迪古巴冰咖啡', '杯中先放入8分满的碎冰，再倒入已加糖之冰咖啡，1/2oz棕兰姆酒，上面再旋转加入一层鲜奶油、并挤上巧克力糖浆。', '酒味浓烈，后劲足，适合喜爱重口味者饮用。香浓烈。适合餐后饮用。', '巧克力糖浆勿加太多，酒量则可随意增减。', NULL, '冰咖啡1杯、冰块适量、棕兰姆酒（Rum）1/2oz、鲜奶油适量、巧克力糖浆适量。');
INSERT INTO `recipe` VALUES (4382, 119, '爱迪咖啡', '杯中先放入已加糖之冰咖啡，再加入1/2—1oz加利安诺香甜酒，最后加满冰块即可。', '香浓醇美的香甜酒蕴含无比魅力，能将冰咖啡的清凉表露无遗。香浓、醇。适合餐后饮用。', '冰咖啡的份量勿太多，否则会掩盖加利安诺香甜酒的风采。', NULL, '冰咖啡1杯、加利安诺香甜酒（Galliano）1/2—1oz、冰块适量。');
INSERT INTO `recipe` VALUES (4383, 119, '爱尔兰咖啡', '1.爱尔兰咖啡高脚杯中先加1/2—1oz爱尔兰威士忌，加入方糖（或2小匙砂糖），上面再放专用架，用酒精灯烧热使其溶化；\r\n2.将冲调好的咖啡倒入杯中，约8分满；\r\n3.注入适量的鲜奶油；\r\n4.也可加入适量的肉桂粉于鲜奶油上；\r\n5.也可将一只鲜樱桃放在鲜奶油正中的表面即成。', '香浓醇烈，是喝咖啡的另一境界。加上爱尔兰威士忌的“Irish Coffee”酒香浓烈，成本较一般咖啡高，但颇能提升用咖啡的乐趣与浪漫境界。专人服务、非常高级尊贵的咖啡。很适合男性晚上饮用。', NULL, NULL, '爱尔兰威士忌（Irish Whiskey）1/2—1oz、方糖、热咖啡1杯、鲜奶油适量。');
INSERT INTO `recipe` VALUES (4384, 119, '爱列斯咖啡', '热咖啡1杯，少许糖，1/2oz白兰地，最后旋转加入一层鲜奶油即可。', '冰凉的鲜奶油之后，是滚烫香醇的热咖啡，白兰地的酒香扑鼻，闻之令人有轻松幸福之感。采用高级白兰地，更香醇浓烈。香醇美妙，适合餐后饮用。', '白兰地份量须适中，不可过量。', NULL, NULL);
INSERT INTO `recipe` VALUES (4385, 119, '爱情咖啡', '卡布基诺咖啡中加入咖啡酒1/4oz、棕可可酒1/4oz、伏特加1/4oz、附糖包上桌即可。', '混合烈酒与香甜酒，将咖啡的风味充分激发，浓烈的劲道，适合喜爱冒险刺激的人饮用，保证终生难忘。浓苦、香醇。适合午后、晚餐后饮用。', '可依各人口味浓淡，增减配酒份量，变化随意，自成一格。', NULL, '卡布基诺咖啡1杯、咖啡酒(Kahlua)1/4oz、棕可可酒（Brown  Creme Decacao）1/4oz、伏特加（Vodka）1/4oz、糖包。');
INSERT INTO `recipe` VALUES (4386, 119, '爱因斯坦咖啡', '意大利浓苦咖啡1杯，上面旋转加入一层鲜奶油，再洒上削片的巧克力屑，附糖包上桌。', '味道浓苦带甜，香气扑鼻，浓苦中别具甜美的感受……具有巧克力的甜美及可可的香味，再与咖啡香融合在一起，别具风味，适合餐后、下午饮用。', '巧克力削薄片，洒在咖啡上，可软化意大利咖啡的浓苦，别具甜美滋味。', NULL, '意大利浓苦咖啡1杯、鲜奶油适量、削片巧克力适量、糖包。');
INSERT INTO `recipe` VALUES (4387, 119, '巴巴利安咖啡', '杯中先加入1包冰糖，再倒入热咖啡约8分满，再旋加入一层鲜奶油、巧克力糖浆1/2oz，最后洒上少许肉桂粉，巧克力削片少许。', '适合女性饮用，十分香甜，但勿喝太多。适合餐后、夜晚饮用。', '巧克力糖浆勿加太多，肉桂粉要选用气味温和芳香者，加一点即可。', NULL, '冰糖包、热咖啡1杯、鲜奶油适量、巧克力糖浆1/2oz、肉桂粉少许、巧克力削片少许。');
INSERT INTO `recipe` VALUES (4388, 119, '巴西利亚咖啡', '倒入1/2杯蜂蜜再加入热咖啡1杯8分满，最后加入鲜奶。', '喝来香甜可口，很适合大众口味，适合餐后饮用。', '不能加糖。制作时要依序倒入，先倒蜂蜜。', NULL, NULL);
INSERT INTO `recipe` VALUES (4389, 119, '百合安娜冰咖啡', '杯中先放入碎冰约8分满，再倒入已加糖之冰咖啡、1/2oz绿薄荷酒，上面再旋转加入一层鲜奶油，最后挤上适量巧克力糖浆及少许七彩米即可。', '彩色多样的巧克力米，使造型更富变化。搅拌均匀后呈现美丽的翡翠色，喝时更可品尝透心凉的迷人滋味。香甜冰凉。适合餐后饮用。', '绿薄荷酒也可在最后加在鲜奶油上。', NULL, '碎冰适量、冰咖啡1杯、绿薄荷酒（Green Creme De Menthe）1/2oz、鲜奶油适量、巧克力糖浆适量、七彩米少许。');
INSERT INTO `recipe` VALUES (4390, 119, '布鲁诺咖啡', '热咖啡1杯约7分满，加1/2oz加利安诺香甜酒、1oz白兰地，再将柠檬皮切成螺旋状，插入丁香6个，插入肉桂棒一支，附上糖包即可上桌。', '味道香、酸、醇。材料丰富、香气卓越的特调咖啡，香酸醇美，风格独一无二。柠檬皮切成螺旋状挂在杯口，柠檬皮上插丁香，加上酒香与肉桂香，混合成独一无二的特调咖啡。适合餐后、晚上饮用。', '也可先将柠檬皮、丁香、酒放进手提锅中，点火后再倒入杯中，亦可依顺序将材料倒入杯中。', NULL, '热咖啡1杯、加利安诺香甜酒（Galliano）1/2oz、白兰地（Brandy）1oz、柠檬皮适量、丁香6个、肉桂棒1支、糖包。');
INSERT INTO `recipe` VALUES (4391, 119, '彩虹冰咖啡', '依序加入1/2oz蜂密，1/2oz红石榴汁，碎冰8分满，冰咖啡慢慢倒入约8分满，再加鲜奶油一层、1球草莓冰淇淋，最上面放一个红樱桃。', '创新的多层次表现，更显其迷人风采。甘甜醇。', '每一层的份量都要适当，勿多加或减少，动作要迅速，以防各层混合，失去美感。', NULL, '蜂密1/2oz、红石榴汁1/2oz、碎冰适量、冰咖啡1杯、鲜奶油适量、草莓冰淇淋1球、红樱桃1个。');
INSERT INTO `recipe` VALUES (4392, 119, '彩虹冰淇淋冰咖啡', '杯中先放入1/3oz蜂蜜（或红石榴汁），再加碎冰至7分满，再倒入已加糖之冰咖啡，上加一球香草冰淇淋，挤入一层鲜奶油，再加一球草莓冰淇淋即可。', '不同口味的冰淇淋可分层享用，可满足喜欢吃冰淇淋的人。香甜。适合全天饮用。', '依顺序倒入杯中，香草冰淇淋须比上层草莓冰淇淋大一些，分层才会明显。加碎冰效果明显。', NULL, '蜂蜜1/3oz、碎冰适量、冰咖啡1杯、香草及草莓冰淇淋各1球、鲜奶油适量。');
INSERT INTO `recipe` VALUES (4393, 119, '淡红色咖啡', '热咖啡1杯约8分满，加入1/3oz金万利红橙酒，上面再旋转加入一层鲜奶油，洒入豆蔻粉及切成细丁的柳橙皮。', '味道香醇。金万利红橙皮酒风味，使咖啡品味出众。豆蔻粉的芬芳扑鼻，混合鲜奶油入口，甜美滑顺。适合餐后、夜晚饮用。', '各种材料的份量正确。', NULL, '热咖啡1杯、金万利红橙皮酒（Grand Marnier Triple Orange）1/3oz、鲜奶油适量、豆蔻粉少许柳橙皮少许。');
INSERT INTO `recipe` VALUES (4394, 119, '法兰西斯冰咖啡', '杯中先放入已加糖之冰咖啡，再加1大匙奶精粉（或2oz奶精水）、1/2oz白兰地，最后加满冰块搅拌均匀即可。', '白兰地的香醇，提升了此冰咖啡尊贵与高级的品质。香浓烈醇。适合晚上饮用。', '选用高级白兰地，味道会更好。奶精粉需趁热时融解。', NULL, '冰咖啡1杯、奶精粉1大匙、白兰地(Brandy)1/2oz、冰块。');
INSERT INTO `recipe` VALUES (4395, 119, '蜂王咖啡', '热咖啡1杯约8分满，加11/2oz蜂蜜，2－3滴白兰地，最上面再旋转加入鲜奶油。', '味道香醇。采用高级白兰地制作，气味更香醇。适合餐后、晚上饮用。', '依咖啡、蜂蜜、白兰地的顺序倒入。', NULL, '热咖啡1杯、蜂蜜11/2oz、白兰地（Brandy）2－3滴、鲜奶油适量。');
INSERT INTO `recipe` VALUES (4396, 119, '凤梨咖啡', '热咖啡1杯约8分满，倒入1oz凤梨汁，杯口插上1小块凤梨角，附上糖包即可。', '味道酸、甜。凤梨与咖啡同属味道相近的材料，用在一起更可突显美味。适合全天、餐后饮用。', '可选用新鲜凤梨或凤梨罐头片，凤梨汁亦可改用1小匙凤梨粉代替。', NULL, '凤梨汁1oz（或凤梨粉1小匙）、热咖啡1杯、凤梨角1小块、糖包。');
INSERT INTO `recipe` VALUES (4397, 119, '古拉索咖啡', '意大利咖啡1杯，加入1/3－1/2oz白橙皮酒，附糖包上桌即可。', '味道香甜、醇美。香甜好喝的白橙皮酒，使咖啡更加甘醇顺口。适合餐后饮用。', NULL, NULL, '意大利咖啡1杯、白橙皮酒（Triple Sec）1/3－1/2oz、糖包。');
INSERT INTO `recipe` VALUES (4398, 119, '贵夫人咖啡', '半杯热咖啡、半杯温鲜奶同时倒入杯中，上面再旋转加入一层鲜奶油即可，附糖包即可。', '味道香纯、清淡。既可享受咖啡的醇厚刺激，又可享受鲜奶的营养温和。适合晨间饮用。', '咖啡与温热鲜奶同时倒入杯中，上面再加上一层鲜奶油。', NULL, '热咖啡半杯、鲜奶半杯、鲜奶油适量、糖包。');
INSERT INTO `recipe` VALUES (4399, 119, '果酱咖啡', '热咖啡1杯8分满，加入1大匙自己喜爱的果酱，附上糖包即可。', '味道香甜。可以自己添加喜爱的果酱，草莓、葡萄、桔子……均可，甜浓好喝。适合餐后饮用。', '草莓果酱也可改用新鲜草莓粒搅拌，效果更佳。', NULL, '热咖啡1杯、果酱（任何种类均可）、糖包。');
INSERT INTO `recipe` VALUES (4400, 119, '豪博咖啡', '热咖啡1杯约8分满，加入1/3oz君度橙酒，上面旋转加入一层鲜奶油，再洒上切成细丁的柳橙皮。附糖包上桌即可。', '香甜的君度橙酒，含有柳橙的香甜，饮用时口甘如饴，浓香醇美。香甜浓。适合夜晚饮用。', '君度橙酒（Cointreau）切勿加过量。', NULL, '糖包、君度橙酒（Cointreau）1/3oz、热咖啡1杯、鲜奶油适量、柳橙皮少许。');
INSERT INTO `recipe` VALUES (4401, 119, '豪华咖啡', '杯内先放入方糖，再倒入热咖啡约8分满，上面旋转放入一层鲜奶油，再将切细丁的红樱桃洒在上面即可。', '鲜红的樱桃丁，风味不凡又赏心悦目。樱桃的芬芳弥漫整杯咖啡，相当美味。香醇。适合午后饮用。', '红樱桃切细丁，铺放在鲜奶油上，赏心悦目又美味。', NULL, '方糖、热咖啡1杯、鲜奶油适量、红樱桃。');
INSERT INTO `recipe` VALUES (4402, 119, '黑玫瑰咖啡', '杯中先放入8分满的冰块，再倒入已加糖之冰咖啡、1/2oz樱桃白兰地，上面再旋转加入一层鲜奶油即可。', '芬芳的迷人气息，鲜艳的深红色樱桃白兰地，散发着令人无法捉摸的魅力。香醇浓。适合餐后饮用。', '装饰物是其重要表征，冰咖啡勿太浓黑，才不会掩盖樱桃白兰地的色泽。', NULL, '冰咖啡1杯、冰块适量、樱桃白兰地（Cherry Brandy）1/2oz、鲜奶油适量。');
INSERT INTO `recipe` VALUES (4403, 119, '皇家咖啡', '1.先冲泡出90ml热蓝山咖啡，倒入预热过的瓷杯中，约8分满；\r\n2.将皇家汤匙（汤匙两端皆有精美花样的边或以稍浅的汤匙替代）横放在杯上，上放方糖；\r\n3.以白兰地淋湿方糖后点火即可饮用。', '甘、醇、具有白兰地醇美的酒香。适合夜晚饮用。具有高贵而浪漫的情调，白兰地醇醇的酒香四溢，十分迷人。', NULL, NULL, '综合热咖啡（或蓝山咖啡）1杯、方糖适量、白兰地（Brandy）1/3oz。');
INSERT INTO `recipe` VALUES (4404, 119, '黄金冰咖啡', '杯中先放入已加糖之冰咖啡，再加1大匙奶精粉（或奶精水）、1/2oz白兰地，最后加满冰块搅拌均匀即可。', '味道香浓烈醇。白兰地的香醇，提升了本冰咖啡尊贵与高级的品质。', '选用高级白兰地，味道会更好，奶精粉需趁热时融解。', NULL, '冰咖啡1杯、奶精粉1大匙、白兰地（Brandy）1/2oz、冰块。');
INSERT INTO `recipe` VALUES (4405, 119, '茴香起泡奶油咖啡', '1杯热咖啡中加1个糖包（或2小匙砂糖），再加入1/3—2/3oz茴香利口酒，最后旋转加入一层鲜奶油即可。', '芬芳强烈的茴香酒味，喝后令人回味无穷，口齿留香。香醇。适合夜晚饮用。不同凡响的风味，令人口齿留香！', '茴香利口酒份量要确实，不可多加。', NULL, '热咖啡1杯、糖包、茴香利口酒（Sambuca或Anisette）1/3—2/3oz、鲜奶油适量。');
INSERT INTO `recipe` VALUES (4406, 119, '假日恋情', '意大利咖啡1杯约7分满，加入1/4oz杏仁酒，1/4oz茴香酒，上面再旋转加入一层鲜奶油，附糖包上桌。', '添加口味特殊的水果酒，酒香扑鼻。香浓。适合夜晚饮用。', '酒的份量要精准，不要添加过量。', NULL, '意大利咖啡1杯、杏仁酒（Amaretto）1/4oz、茴香酒（Sambuca或Anisette）1/4oz、鲜奶油适量、糖包。');
INSERT INTO `recipe` VALUES (4407, 119, '教皇冰咖啡', '杯中先放入已加糖之意大利冰咖啡，再倒入1/2oz茴香酒、3oz鲜奶，最后加满冰块即可。', '味道香、浓、醇。茴香酒独特的浓烈香味，正是本冰咖啡独一无二的招牌。适合餐后饮用。', '茴香酒气味浓烈，切勿多加。3oz鲜奶可改为1oz鲜奶油，效果更佳。', NULL, '意大利冰咖啡1杯、茴香酒（Sambuca）1/2oz、鲜奶3oz、冰块适量。');
INSERT INTO `recipe` VALUES (4408, 119, '卡布基诺冰咖啡', '杯中先放入已加糖之意大利冰咖啡，上面再旋转加入一层鲜奶油、1/2oz巧克力膏、再洒入少许肉桂粉。', '冰凉的风味，别有不同口感。香浓醇苦。适合餐后饮用。', '肉桂粉要选用等级高、味道温和者较好。咖啡的份量可少一些。', NULL, '意大利冰咖啡1杯、冰块适量、鲜奶油适量、巧克力糖浆适量、肉桂粉少许。');
INSERT INTO `recipe` VALUES (4409, 119, '卡尔亚冰咖啡', '杯中先放入1/4oz白橙皮酒（TripleSec），加满冰块，再倒入已加糖之冰咖啡，随后倒入7-UP汽水至8分满，上面再旋转加入一层鲜奶油，再撒上少许削薄的巧克力片即可。', '味道甘甜。可爱的分层效果，风味清新柔顺。适合餐后饮用。', '加7－UP汽水前，杯中要先加碎冰块，且要慢慢倒入。加鲜奶油时动作也要慢，效果才佳，并须依顺序倒入。', NULL, '白橙皮酒1/4oz、冰咖啡1杯、7－UP汽水适量、鲜奶油适量、巧克力削薄片。');
INSERT INTO `recipe` VALUES (4410, 119, '卡普基诺咖啡', '卡普基诺咖啡（Cappuccino）：当浓烈强劲的Espresso风吹过意大利，浮泛丰盈奶香泡沫的Cappuccino,却同时散播着一种宁静温柔的空气。在温过的杯中，依序加入1/2Espresso，1/3热牛奶，和些许牛奶泡沫，再撒上少许肉桂粉，趁热享用。略微辛辣的肉桂粉，让温柔的牛奶咖啡沾染些许个性魅力。', '奶油发泡并均匀铺满在橙黄的咖啡上，轻柔的奶香泡沫结合了浓郁的咖啡香，层次分明的口感，从第一口到最后一口，感受截然不同。卡普基诺咖啡通常较理想是不要搅和，首先要先用视觉欣赏卡普基诺咖啡的图案或牛奶泡沫（细度），再慢慢的吸喝，如此可感觉到朵细的牛奶泡沫及热热美味的咖啡。如果先将牛奶泡沫搅和再喝较不妥当。因为许多人喝咖啡喜欢放糖，为了溶解糖才去搅和。只要小心点喝就不会喝得一口白胡子了。\r\n香、甜、浓、苦。充分表现了意大利式的热情与浪漫，值得你在慵懒的午后，舒舒服服的来上一杯！充分洋溢南欧风情的浪漫咖啡，适合下午', '可加少许白兰地（Brandy）或加利安诺香甜酒（Galliano）以增加香味。玉桂粉要选用等级高、气味温和者。特浓咖啡的质量在牛奶和泡沫下不太会看出来，但它仍然是决定卡布奇诺口味的重要因素。把经过部分脱脂的牛奶倒入一只壶中，然后用起沫器以高温104度的蒸汽让牛奶起沫、冲气，并且让牛奶不经过燃烧就可以象掼奶油一样均匀。盛卡布奇诺的咖啡杯应该是温热的不然倒入的牛奶泡沫会散开。平时可以将这些杯子放在咖啡机的顶部保温。将牛奶和泡沫倒在特浓咖啡上面，自然形成了一层，就好象把下面的咖啡包了起来。注意倒入冲泡好的意大利', NULL, '热咖啡1/2杯、鲜牛奶适量、肉桂粉适量。');
INSERT INTO `recipe` VALUES (4411, 119, '绿茶咖啡', '1.将龙井茶5克用50ml约九十度的开水浸软，1分钟后倒出约30毫升茶汁，再向茶中加入100ml九十度的开水，制取龙井茶汁80ml；\r\n2.冲煮意大利特浓咖啡一杯约40ml；\r\n3.根据个人的口味，在咖啡中加入龙井茶汁，加入方糖调匀即可。', NULL, '咖啡和绿茶可以选用不同的搭配。也可将绿茶汁和咖啡倒入调酒壶内摇匀后饮用。', NULL, NULL);
INSERT INTO `recipe` VALUES (4412, 119, '罗马冰咖啡', '杯中先放入已加糖之意大利冰咖啡，再倒入1/2oz杏仁酒、3oz鲜奶，最后加满冰块即成。', '杏仁酒独特的香味，让喜爱者深深陶醉。香浓醇。适合餐后饮用。', '3oz鲜奶也可改为1oz鲜奶油，味道更加香浓。', NULL, '意大利冰咖啡1杯、杏仁酒（Amaretto）1/2oz、鲜奶3oz、冰块适量。');
INSERT INTO `recipe` VALUES (4413, 119, '罗马咖啡', '1.将冲调好的咖啡倒满于杯中；\r\n2.加入二汤匙的砂糖溶于咖啡中；\r\n3.注入适量鲜奶油；\r\n4.滴入适量的朗姆酒即成。', NULL, NULL, NULL, '热咖啡一杯、砂糖二汤匙、鲜奶油适量、朗姆酒适量。');
INSERT INTO `recipe` VALUES (4414, 119, '玛查格兰咖啡', '2.5oz红葡萄酒（Red Wine）先温热，再倒入半杯热咖啡中，随即放入一片柠檬片、一支肉桂棒，即可附糖包上桌。', '适合口味特殊或失恋的人喝。酸、甜、苦，诸味俱陈。适合饭前饮用。', '柠檬片要尽快拿出来，勿在杯中浸泡太久。红葡萄酒只须温热即可。', NULL, '热咖啡半杯、红葡萄酒（Red Wine）2.5oz、柠檬片、肉桂棒、糖包。');
INSERT INTO `recipe` VALUES (4415, 119, '玛克兰咖啡', '1.将冲调好的咖啡倒满于杯中，约7分满；\r\n2.把1oz白兰地倒入咖啡中；\r\n3.置入柠檬一片；\r\n4.再将肉桂棒加入，附糖包上桌即可。', '选用细长的肉桂棒，可做加味及搅拌用；白兰地香浓强烈、后劲强。咖啡中洋溢着白兰地的香醇和肉桂的芬芳，令人未饮先醉、饮后精神百倍！香醇、酸、浓。适合夜晚饮用。', '柠檬片要切薄，上桌后尽快将柠檬片取出，勿浸泡太久。白兰地份量要确实，不可多加。', NULL, '热咖啡、白兰地（Brandy）、柠檬片、肉桂棒（顶端包锡箔纸）、糖包。');
INSERT INTO `recipe` VALUES (4416, 119, '玫瑰浪漫咖啡', '蓝山咖啡一杯7－8分满，在杯口横置一支专用的皇家咖啡钩匙，上放方糖，并淋上少许白兰地，点火即可。', '味道香醇、浓烈。鲜红的玫瑰悬浮杯中，宛如雪里红，美丽动人，让人引发无限遐想。适合餐后、恋爱时饮用。香醇。别致的创意，优美的造型，适合情人对饮，或一个人享受自恋之美。', '选用鲜红的玫瑰花，才能产生对比之美。亦可在杯中放入一朵鲜奶油玫瑰花。', NULL, '蓝山咖啡一杯，白兰地少许，玫瑰花一朵，糖袋。');
INSERT INTO `recipe` VALUES (4417, 119, '密思梅咖啡', '热咖啡一杯、糖包1包（或砂糖二汤匙）、蜂蜜1/2oz、鲜奶油适量、话梅粉1/5汤匙。', '甘香甜蜜在心头，话梅粉具有甘甜留香的效果。加上了蜂蜜的甜美与话梅粉的甘香，使这种咖啡别具风味，是很受女性欢迎的花式咖啡，餐后饮用颇有去腻消油的快感。甜美甘香。适合餐后饮用。', '材料依顺序倒入杯中，蜂蜜最先倒入。', NULL, '热咖啡一杯、糖包1包（或砂糖二汤匙）、蜂蜜1/2oz、鲜奶油适量、话梅粉1/5汤匙。');
INSERT INTO `recipe` VALUES (4418, 119, '墨西哥冰咖啡', '杯中先放入7分满的碎冰，再倒入已加糖的冰咖啡至8分满，上面再旋转加入一层鲜奶油（或1大球香草冰淇淋），再从旁倒入1个蛋黄，最后加上1/3－1oz绿薄荷酒即成。', '清凉香醇，蛋黄新鲜浓润，易于肠胃吸收且美味可口。香、凉、浓。适合餐后饮用。', '杯中必须先加碎冰，才可撑住蛋黄不会沉下去，打蛋黄时须注意要完整，勿打破。', NULL, '碎冰适量、冰咖啡1杯、鲜奶油适量、蛋黄1个、绿薄荷酒1/3－1oz。');
INSERT INTO `recipe` VALUES (4419, 119, '柠檬咖啡', '1.调制热咖啡一杯；\r\n2.向咖啡杯内滴二至三滴白兰地；\r\n3.将一片薄切的柠檬浮在其上。', NULL, NULL, NULL, '浅烘焙咖啡粉10克、水量120cc、白兰地适量、鲜柠檬一片。');
INSERT INTO `recipe` VALUES (4420, 119, '浓情冰咖啡', '先在Shake壶中倒入已加糖的意大利冰咖啡，再倒入1/2oz香草香甜酒、3oz鲜奶、加满冰块Shake后倒入杯中，上面再旋转加入一层鲜奶油，再洒上少许肉桂粉即成。', '香醇的香草香甜酒，使本冰咖啡更显浓厚醇烈。香浓苦醇。适合餐后饮用。', '冲煮意大利咖啡要注重香浓，肉桂粉要温和适量。', NULL, '意大利冰咖啡1杯、香草香甜酒（D.O.M,Benedictine）1/2oz、鲜奶3oz、冰块适量、鲜奶油适量、肉桂粉少许。');
INSERT INTO `recipe` VALUES (4421, 119, '飘浮冰咖啡', '杯中先放入8分满的碎冰，再倒入已加糖之冰咖啡至8分满，最后放入1大球香草或巧克力冰淇淋，其上先挤上一点鲜奶油，再放入1颗红樱桃。', '做法简单，流行、好喝、香甜。香甜。适合全天饮用。', '杯中要先放碎冰，香草冰淇淋才会圆且完整。', NULL, '碎冰适量、冰咖啡1杯、香草或巧克力冰淇淋1大球。');
INSERT INTO `recipe` VALUES (4422, 119, '热情咖啡', '热咖啡1杯约7分满，上放1片柠檬片（宜品质新鲜且切得轻薄），再淋下1/2－2/3OZ白兰姆酒（Rum），点火送桌并附上糖包。', '浪漫迷人的品饮风情，非常具有享受生活的快感。苦、醇、烈、酸。', '柠檬片要切得很薄，柠檬片不可浸泡太久，须尽快取出。', NULL, '热咖啡1杯、柠檬片、白兰姆酒（Rum）1/2－1/3OZ、糖包。');
INSERT INTO `recipe` VALUES (4423, 119, '苏格兰爱丽丝咖啡', '热咖啡1杯约8分满，上放皇家钩匙及方糖1块，淋下苏格兰威士忌（Scotch Whisky）适量，点火上桌，并附上奶油球1粒。', '含有苏格兰威士忌（Scotch Whisky）的醇烈，适合男性饮用。香醇、浓烈。适合夜晚饮用。', '杯口横放皇家钩匙，上置方糖一块，再淋下苏格兰威士忌适量，即可点火送桌。', NULL, '热咖啡1杯、方糖、苏格兰威士忌（Scotch Whisky）适量、奶油球1颗。');
INSERT INTO `recipe` VALUES (4424, 119, '维也纳咖啡', '1.将冲调好的咖啡倒于杯中，约8分满；\r\n2.在咖啡上面以旋转方式加入鲜奶油；\r\n3.淋上适量巧克力糖浆；\r\n4.最后洒上七彩米，附糖包上桌。', '源于奥地利的欧风咖啡；具有三段式变化口味，适合喜欢甜食的人。香甜可口。适合早晨或下午饮用。', '巧克力糖浆与七彩米勿多加否则会太甜。', NULL, '热咖啡1杯、鲜奶油适量、巧克力糖浆适量、七彩米少许、糖包。');
INSERT INTO `recipe` VALUES (4425, 119, '香浓泡沫冰咖啡', 'Shake壶中先放入已加糖之冰咖啡，再放入1大球香草冰淇淋、2oz鲜奶油，加冰块shake。杯中先加冰块后，将shake好之冰咖啡倒入杯中，上加少许杏仁片即可。', '香浓、泡沫多，完全融化的冰淇淋，直接饮用香甜美味。香甜浓。适合全天饮用。', '冰淇淋勿太硬，否则不易摇融。加碎冰最好。', NULL, '冰咖啡1杯、香草冰淇淋1大球、鲜奶油适量、冰块、杏仁片少许。');
INSERT INTO `recipe` VALUES (4426, 119, '椰香咖啡', '杯中倒入已加糖之冰咖啡至8分满，再倒入1/2oz椰奶、2oz鲜奶油、1/3oz白兰地（Brandy），加满碎冰，搅拌均匀即可。', '充满椰香的冰咖啡，颇具热带浪漫风情。香甜柔顺。适饮餐后饮用。', '采用椰奶糖浆最顺口好喝。', NULL, '冰咖啡1杯、椰奶1/2oz、鲜奶油2oz、白兰地（Brandy）1/3oz、冰块。');
INSERT INTO `recipe` VALUES (4427, 119, '意大利浓缩咖啡', '1.50毫升意大利特浓咖啡约须6－8克咖啡粉。\r\n2.采用细咖啡粉。\r\n3.Espresso咖啡豆属混合咖啡，选购要特别注意调配烘焙品质是否良好。', '浓郁香醇、刺激性高，能令人神清气爽。但味道非常浓苦、具有强烈刺激性，令人神清气爽！酒足饭饱之余来一杯，确有“夫复何求”的满足感！', '要掌握冲煮的秘诀，就如它的原意一样：快速，煮的快，喝的也快，是不变的笺言。为煮出一杯真正的Espresso，最好选择为Espresso所特别调制的Espresso咖啡豆。记得咖啡粉的份量要足够，才够浓够味。亦可采用虹吸式双冲法来煮。如果是单品咖啡，焙炒程度上应是一种深度焙炒，豆表油脂大量冒出且呈近似黑色的暗褐色。而如果是混合咖啡豆，因每一种混合咖啡皆由不同原豆所组合，各种风味各有其最适合使用的特定焙炒法，Espresso也指专为在意大利咖啡机上制作意大利特浓咖啡而特调的混合咖啡豆。采用意大利咖啡粉，研磨要', NULL, NULL);
INSERT INTO `recipe` VALUES (4428, 119, '鸳鸯冰咖啡', '1.将冰红茶与冰咖啡各半混合置于一杯；\r\n2.饮用时以炼乳调节甜度；\r\n3.喜好热饮的朋友,将红茶和咖啡改为热的即可。', NULL, NULL, NULL, '冰红茶半杯、冰咖啡半杯、炼乳适量。');
INSERT INTO `recipe` VALUES (4429, 119, '爪哇摩卡咖啡', '1.将冲调好的咖啡倒满于杯中；\r\n2.加入二汤匙的砂糖溶于咖啡中；\r\n3.将巧克力沙司加入咖啡中；\r\n4.再把液体鲜奶油加入，充分搅拌均匀即成。', NULL, NULL, NULL, '热咖啡一杯、砂糖二汤匙、巧克力沙司适量、液体鲜奶适量。');
INSERT INTO `recipe` VALUES (4430, 120, '豆浆', '1.湿制法：大豆250g，水加到2000ml，浸泡8小时后用石磨或豆浆机磨碎，约可得豆浆1500ml。每1000ml中加入食盐1g，乳酸钙2g，淀粉20g，蔗糖60g，煮沸20分钟。\r\n2.干制法：黄豆洗净后晾干，与热沙同炒至微棕色，再用粉碎机磨成粉状，经细筛（筛孔直径0.5mm）筛过，每100g黄豆粉中加食盐1g，乳酸钙3g，淀粉20g，糖40g，临用时将水加到1000ml即成。', NULL, NULL, NULL, '黄豆。');
INSERT INTO `recipe` VALUES (4431, 120, '鳄梨牛奶汁', '鳄梨削皮，切半去核，切块，和其他材料一起放入果汁机中搅打即可。', NULL, NULL, NULL, '鳄梨1个，柠檬汁2大匙，鲜奶200cc，砂糖1大匙，冰块2－3块。');
INSERT INTO `recipe` VALUES (4432, 120, '木瓜牛奶椰子汁', '木瓜去皮对剖、去籽、切块，将所有材料放入果汁机搅拌约30秒，即可倒出饮用。', NULL, NULL, NULL, '木瓜1/2个，鲜奶250cc，蜂蜜1大匙，椰子汁50cc，碎冰块1/2杯。');
INSERT INTO `recipe` VALUES (4433, 120, '巧克力奶', '1.先用少许的凉牛奶把巧克力粉搅匀； \r\n2.然后再用热牛奶冲开即可； \r\n3.也可以把巧克力粉用凉牛奶搅匀后一起加热。', '香甜醇厚。', NULL, NULL, '纯巧克力粉（超市有售）、纯牛奶、白砂糖。');
INSERT INTO `recipe` VALUES (4434, 120, '酸奶', '选用不加抗菌素和防腐剂的新鲜牛奶，要求脂肪含量不高于3%，干物质高于8.5%，酸度小于0.16。按鲜奶5%－10%的比例加白糖，煮沸3分钟后过滤。再将过滤的牛奶迅速降温至38度-42度，在无菌下接种乳酸菌种，使菌种分布均匀。接种后的牛奶分装在已洗净、灭菌的乳瓶内，及时封口，在37度恒温下发酵4－6小时，发酵后要随时抽样检查酸度和凝固情况。当酸度为0.58、凝固也达到要求时，停止发酵，小心地取出在室温下冷却，再移到2－6度冰箱中冷却。', NULL, NULL, NULL, '鲜牛奶、白糖。');
INSERT INTO `recipe` VALUES (4435, 121, '薄荷莲子羹', '1.将薄荷梗洗净后放在锅内，加清水（约2升）用大火烧开后改用小火煮15分钟，离火去渣取澄清后的汤汁待用。\r\n2.选用去皮去心的干莲子，放在高压锅内，加入煮薄荷梗的澄清汁，用大火煮开后改用小火焖至莲子极酥烂时，加入白糖煮至糖全部溶化，倒入水淀粉迅速搅匀，开锅后即可停火，分盛入小碗即可。', '此羹香甜美味，凉爽适口。', NULL, NULL, '薄荷梗25克，干莲子100克，白糖200克，水淀粉少许。');
INSERT INTO `recipe` VALUES (4436, 121, '草莓酒', '1.选新鲜草莓，洗净，去其蒂，逐个拭干水。\r\n2.把柚子剥去外皮切成圆片。\r\n3.将草莓放入容器，加糖、酒、柚片（做容器内盖之用）密封放在冷暗处或冰箱。\r\n4.2周左右后即得鲜艳馥郁的草莓酒。', NULL, NULL, NULL, '草莓1000克，柚子1个，白糖200克，白酒1800克。');
INSERT INTO `recipe` VALUES (4437, 121, '茶汤', '1.将秫米淘洗干净，磨成潮湿的细粉，放入碗内，用小量温开水调成糊状。\r\n2.将装满沸滚开水的特制长嘴大铜壳提起（家庭可用钢精壶或铁壶）向碗内冲入开水，使秫米糊烫熟，再分别撒上红糖、桂花酱即成。', '本品质地细腻，甜美润口。', NULL, NULL, '秫米500克，红糖300克，桂花酱少许。');
INSERT INTO `recipe` VALUES (4438, 121, '炒羊油茶', '1.将羊油洗净、切碎，放入锅中，用小火炼成油，然后捞出油渣，再下入花椒、葱、姜、蒜炸一下捞出去净羊油的膻味。备用。\r\n2.先将花生仁放入羊油内炸黄捞出。然后盆内加水1碗，将羊油倒在水盆内，使油冷却后倒出，翻过面将下面的杂质刮净，待用。\r\n3.将面粉放锅内，置文火上煸炒，见面粉至七成熟，呈嫩黄色时，倒在案板上，再用小擀面杖擀碎过箩。将花生米剁成小粒。备用。\r\n4.将羊油放在小火上化开，再晾至二成热，随即将面粉和花生仁下锅炒，同羊油掺和均匀，并用手端住锅耳晃一会儿，使面、油凝固成块，用小铲摊平倒出。吃时用刀刮', '本品油香浓，味鲜美，冬令补品，最宜作早茶食用。如佐馓子同食，风味更佳。', NULL, NULL, '面粉2500克，羊腰窝油1000克，去皮花生仁50克，花椒5克，葱25克，姜5克，蒜25克。');
INSERT INTO `recipe` VALUES (4439, 121, '川味油茶', '1.把两种米淘洗干净，用水1500克混合后磨成稀米浆，然后慢慢倒入锅中烧开，放入葱、姜之后再烧开20分钟；接着放入盐，打去泡沫，用大火烧开，5分钟后再用小火撑至全熟，制成稀糊备用。\r\n2.碗中放入适量的米糊，再放入适量的红油、川盐、花椒油、姜末、葱花、大头菜、酥黄豆、芝麻面、馓子等，即成。', '本品味咸鲜香美，酥脆，热烫微辣，川味风味小吃，作早茶甚为适宜，更可作冬季佳品。', NULL, NULL, '大米1000克，酥黄豆50克，糯米50克，菜油、大头菜、花椒油、川盐、葱、红油、芝麻、姜各适量，馓子50把。');
INSERT INTO `recipe` VALUES (4440, 121, '蛋黄酒', '1.取蛋黄放入容器中。\r\n2.加入蜂蜜，一边搅拌一边再加入酒，待均匀后，用文火煮成糊状即可。', NULL, NULL, NULL, '鸡蛋1个，酒200克，蜂蜜20克。');
INSERT INTO `recipe` VALUES (4441, 121, '观音面茶', '1.将黑芝麻、粳米、山药分别炒熟，研细末，过筛，然后取细粉与藕粉、白糖共同混合均匀，用瓷罐收藏。可作早点和中间加餐服食。\r\n2.食用时，每取30克，用白开水冲服即可。', '本品能黑须发，益气血，抗衰老，强体质。', NULL, NULL, '黑芝麻、藕粉、粳米、白砂糖、淮山药各500克。');
INSERT INTO `recipe` VALUES (4442, 121, '果蔬冰淇淋饮品', '将香菜捆成捆，和胡萝卜、苹果一起推入漏斗榨汁，加入冰块，并放入冰淇淋，点缀以桔子片。', NULL, NULL, NULL, '1把香菜、5个去缨胡萝卜、1/2个去核苹果、冰块若干、冰淇淋1匙、桔子片2－3片。');
INSERT INTO `recipe` VALUES (4443, 121, '红宝石酒', '1.煮得4%咖啡液，过滤后放凉，备用。\r\n2.凉开水溶化小苏打制取0.6%或1%的溶液，备用。\r\n3.将白糖柠檬酸入大杯，加入40克的备用咖啡液和80克山楂汁（或桔子汁），搅拌均匀，加入全部白兰地。\r\n4.最后调配：加入备用小苏打液，加至使整个饮料达500克时为止，搅匀，即成。', NULL, NULL, NULL, '咖啡适量，小苏打适量，白糖30克，柠檬酸2克，山楂汁或桔子汁80克，金奖白兰地50克。');
INSERT INTO `recipe` VALUES (4444, 121, '金银乌梅茶', '1.将乌梅用开水浸泡30分钟，然后倒入锅内，加水1000毫升，用旺火烧开后，用小火煮30分钟后停火，晾凉，过滤去渣，约得乌梅液500毫升。\r\n2.金银花用1000毫升开水浸泡30分钟，滤出金银花液。\r\n3.将金银花液和乌梅液混合，加入白砂糖调匀。\r\n4.将金银乌梅液放入冰箱冰镇，即饮即取。', '清凉爽口，去热解毒，消暑生津，夏天饮用可预防中暑。', NULL, NULL, '金银花、乌梅各15克，白砂糖100克。');
INSERT INTO `recipe` VALUES (4445, 121, '扣碗茶汤', '1.先将糜子米淘洗干净，用凉水浸泡2－3小时，碾成面，然后过细箩，即成糜子面。\r\n2.将茶汤壶内的水烧开。\r\n3.取小碗1个，先倒入50克开水和10克凉水搅和一下，再放入1/10的糜子面调成面糊，然后左手端着盛面糊的碗紧对着茶壶嘴，右手握着壶把子将壶倾斜，使壶内的开水顺着壶嘴冲入碗内。这时速将碗与壶嘴由近而远地拉大距离，以加大水的冲力，以使面糊冲得均匀；随后又由远至近，面糊即冲成杏黄色的茶汤。\r\n4.冲好一碗后，即在茶汤上撒上红糖20克、白糖10克和糖桂花1克。也可再撒些金糕丁、青梅丁之类的食品。本配料用', '本品质地细腻，味道甜香，系北京茶食小吃，回民风味。因将碗翻扣过来而茶汤不洒，故有此“扣碗茶汤”之称。茶汤有两个特色：一是烹调方法特殊，用开水冲的方法将茶汤冲熟；二是用具特殊，使有一把铜制大茶壶，壶高60厘米，直径50厘米，壶嘴细长，壶内四周贮水，中间空如炉膛，将燃煤放入膛内，把水烧开，即可用以冲熟茶汤。', NULL, NULL, '糜子米500克，红糖200克，白糖100克，糖桂花10克，金糕丁、青梅丁各适量。');
INSERT INTO `recipe` VALUES (4446, 121, '麦茶', '1.将麦粒淘洗干净，然后入锅焙炒半焦，捞出入石臼内捣碎，待用。\r\n2.取陶罐一个，放入适量麦面、食盐以及其他配料，加适量水，经煮熬即成。', '本品味浓似咖啡，香味可口，系青、甘等省的一些少数民族族的饮茶习俗。', NULL, NULL, '麦米、食盐、佐料各适量。');
INSERT INTO `recipe` VALUES (4447, 121, '面茶', '1.将芝麻拣去杂质，用微火炒至呈黄色，放在案板上研碎。精盐、花椒分别用微火焙干，把花椒轧成末，连同精盐12克一起拌入芝麻椒盐，备用。\r\n2.将芝麻油烧至八成热，倒入芝麻酱中调匀，备用。\r\n3.将凉水2250克倒入锅内，放进碱面和余下的精盐8克，在旺火上烧至八成沸时，将糜子面用750克凉水调匀，倒和锅内不断推搅，以防止糊底。待锅开后，立即转用微火，再熬15分钟左右，将糜子面熬成黄色的稠粥。\r\n4.食用时将粥盛入碗内，淋入芝麻椒盐和姜粉，即成。', '本品色泽鲜黄，质地浓稠，香味扑鼻，咸淡适口。', NULL, NULL, '糜子面50克，芝麻酱150克，芝麻油50克，芝麻25克，碱面5克，姜粉5克，花椒5克，精盐20克。');
INSERT INTO `recipe` VALUES (4448, 121, '奶米茶', '1.将少量稷子米炒至有轻微煳味后，待用。\r\n2.在炒稷子米中加牛奶和水适量烧煮即成。', '本品味香、润口，系达斡尔族的茶饮习俗。', NULL, NULL, '稷子米、牛奶、清水各适量。');
INSERT INTO `recipe` VALUES (4449, 121, '奶啤酒', '1.将酒、奶、糖、蛋均放容器中，均匀搅打充分。\r\n2.倒入高脚大杯中，即可饮用。', NULL, NULL, NULL, '啤酒50克，牛奶150克，糖40克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (4450, 121, '柠檬葡萄酒', '1.先挤压柠檬取汁，再把柠檬皮、糖放一起研磨。\r\n2.最后将柠檬汁、研磨混合物、白葡萄酒、开水共放一容器里，搅拌均匀后过滤，冷却。\r\n3.倒入大高脚杯，即可饮用。', NULL, NULL, NULL, '白葡萄酒和开水各250克，白糖100克，调料柠檬皮适量，柠檬半个。');
INSERT INTO `recipe` VALUES (4451, 121, '柠檬威士忌酒', '1.先挤压柠檬取汁，将汁、糖混合。\r\n2.加威士忌酒，搅均匀即成。\r\n3.饮时可随意加入冰块，效果更好。', NULL, NULL, NULL, '威士忌酒300克，糖2食匙，柠檬2个，冰块适量。');
INSERT INTO `recipe` VALUES (4452, 121, '泡沫啤酒', '1.先把蛋、酒混合均匀。\r\n2.再把糖、啤酒混合均匀。\r\n3.然后将两种混合物在文火上加热，直至呈现一层厚泡沫为止。\r\n4.盛于高脚大酒杯中，即成质感诱人的泡沫啤酒。', NULL, NULL, NULL, '酒200克，啤酒750克，糖50克，鸡蛋3个。');
INSERT INTO `recipe` VALUES (4453, 121, '苹果柠檬冰淇淋饮品', '苹果、柠檬一起推入榨汁机漏斗，将果汁直接榨入一个装有冰块的小罐内。然后，放入冰淇淋（最好使之尽量的圆），并以樱桃为点缀。一杯冰润可口的苹果柠檬冰淇淋就大功告成了。', NULL, NULL, NULL, '2个去核青苹果、1个小柠檬、一大杯冰水、冰块若干、两棵樱桃（也可不用）、两大匙冰淇淋（口味可任选）。');
INSERT INTO `recipe` VALUES (4454, 121, '青果甘草茶', '1.将青果敲碎。\r\n2.将青果、绿茶、甘草同放入带滤网的玻璃茶壶内。\r\n3.用沸水冲泡，随冲随饮。', '清热、解渴、化痰。', NULL, NULL, '青果10克，绿茶5克，甘草2克。');
INSERT INTO `recipe` VALUES (4455, 121, '苏格兰之晨', '摇动法。在摇酒器中加入3块冰块，分别将蛋黄、甜红葡萄酒和威士忌酒依次倒入，摇动10－15秒钟，使酒液充分混合，滤入鸡尾酒杯中，上面撒一些肉豆蔻粉即可。', '提神醒脑。', NULL, NULL, '1个鸡蛋的蛋黄，甜红葡萄酒10毫升，威士忌酒30毫升。');
INSERT INTO `recipe` VALUES (4456, 121, '甜菜苹果冰淇淋饮品', '将甜菜和苹果推入漏斗榨汁，加入冰块，放入冰淇淋，点缀以奇异果片。', NULL, NULL, NULL, '1小块甜菜、2－3个去核苹果、冰块若干、冰淇淋1－2匙、奇异果片2－3片。');
INSERT INTO `recipe` VALUES (4457, 121, '甜杏仁茶', '1.将甜杏仁用开水略泡片刻，剥去外衣，洗净，剁成碎粒，用冷水浸泡。粳米、绿豆也洗净用冷水浸泡。\r\n2.将杏仁、粳米、绿豆放在一起磨成浆，然后加白糖及适量清水煮熟后饮用，当天吃完。', '本品清甜、滑软、润肤、防治痱子的功效。', NULL, NULL, '甜杏仁10克，绿豆50克，粳米50克，白糖。');
INSERT INTO `recipe` VALUES (4458, 121, '武陟油茶', '1.将芝麻仁炒成黄色；油炸花生米、胡桃仁分别研碎；各种香味料一起磨成粉。\r\n2.将干面粉、玉米淀粉上笼蒸40分钟取出，晾凉后擀散，过箩，用香油炒成浅黄色时，放入研碎的芝麻仁、花生米、胡桃仁以及香料粉、盐面与炒面粉拌匀，即成油茶面。', '本品质地稠浓，香味馥郁，颇具特色。此系河南武陟小吃，常作早茶、夜宵的流质茶食。', NULL, NULL, '芝麻仁500克，油炸花生米（脱皮）250克胡桃仁250克，八角、花椒、小茴香、丁香、肉桂、草果、陈皮、良姜等味料共计50克，干面粉2500克，玉米淀粉500克，香油300克，盐面250克。');
INSERT INTO `recipe` VALUES (4459, 121, '西瓜酒', '1.挑选好西瓜，洗净擦干，把蒂部切下一块，当盖子用。\r\n2.将若干葡萄干放进，将盖封得越严越好。\r\n3.放在阴凉处10多天，西瓜酒即成。', NULL, NULL, NULL, '西瓜1个，葡萄干若干。');
INSERT INTO `recipe` VALUES (4460, 121, '西瓜汁冰淇淋饮品', '将西瓜榨汁，并将榨好的果汁倒入玻璃杯，冰淇淋放入果汁，并用桔片装饰，此方法可用于西瓜皮太厚或不太熟的西瓜。', NULL, NULL, NULL, '1CM左右厚带白皮西瓜片、装饰用桔片或柠檬片、冰淇淋适量。');
INSERT INTO `recipe` VALUES (4461, 121, '香蕉冰淇淋饮品', '将香蕉和1/4杯橙汁放进加工器，搅成糊状，然后放入一大杯容器；将菠萝块和1/4杯橙汁放进加工器，搅成糊状，然后放入同一容器；将水、柠檬汁、1/2杯橙汁混合在一起放在一起搅拌、冰镇，加上冰淇淋，并以草莓做装饰。', NULL, NULL, NULL, '四根香蕉，去皮，切成片，两杯冰橙汁，1/2新鲜的菠萝，去皮，去核，切成块，一杯冰水，1勺新鲜柠檬汁，6个草莓，1至2匙冰淇淋。');
INSERT INTO `recipe` VALUES (4462, 121, '杏奶茶', '1.将粳米洗净，加清水2000克浸透；杏仁用热水浸泡后去衣，投入米中拌和，然后再带水磨成米浆，备用。\r\n2.锅内放清水2500克，加入白糖烧沸，再倒入米浆，边倒边用勺子搅动，至形成稀浆状时，加入鲜牛奶拌匀，继续煮片刻即成。', '本品味道清甜，细腻爽口，具有润肺止咳，消食散气的功效，明朝就有文载：此茶可益寿。', NULL, NULL, '粳米300克，杏仁200克，白糖1000克，鲜羊奶50克，清水2500克。');
INSERT INTO `recipe` VALUES (4463, 121, '杏仁茶', '1.将大米面倒入盆内，用温水澥开，再用开水冲浓，倒入锅内，用小火熬开后，加入杏仁精搅匀即成杏仁茶。\r\n2.将杏仁茶盛入碗内，加入白糖、桂花酱即可食用。', '本品甜爽适口，清香扑鼻，杏仁味浓。', NULL, NULL, '细大米面250克，白糖125克，桂花酱少许，杏仁精1滴。');
INSERT INTO `recipe` VALUES (4464, 121, '羊油茶', '1.锅内置火上，放入精面粉、羊化油，用小火翻炒，逐渐增至中火，炒至翻沙时，即成为羊油茶。将其盛入碗内备用。\r\n2.将碗内加入2碗清水烧开，再舀入羊油茶搅成稀糊状，煮沸装碗，然后撒上椒盐、辣椒面、香菜末或葱花，即成。', '本品麻辣酥烫，鲜香可口，油而不腻，是冬季早茶食用之佳品。', NULL, NULL, '精面粉200克，羊化油200克，椒盐、辣椒面、香菜末或葱花各适量。');
INSERT INTO `recipe` VALUES (4465, 121, '油茶', '1.将牛骨髓油放入锅内，上火溶化，捞出油渣，加入面粉，用微火炒约30分钟，待面粉呈金黄色时，出锅晾凉，过箩，或用擀面杖压细。然后拌入碾碎的熟芝麻，即成油茶。\r\n2.取油茶50克放入碗内，用开水冲成浓淡适宜的稠糊状，再拌入白糖，撒上桂花酱即成。', '本品香甜润口，营养丰富，填髓健脑，补益强身。', NULL, NULL, '面粉500克，牛骨髓油100克，熟芝麻、桂花酱各少许，白糖250克。');
INSERT INTO `recipe` VALUES (4466, 121, '油茶面', '1.在锅内放入面粉，用微火搅炒约10余分钟，待面粉呈现麦黄色时即熟，取出过细箩，仍放回原锅中。\r\n2.另起锅放入牛骨髓油，用旺火烧到油将冒烟时，立即倒入熟炒面里搅拌均匀。待用。\r\n3.将黑、白芝麻用微火炒出香味，核桃仁炒熟去皮，剁成细末，连同瓜子仁一起倒入炒面中，即成油炒面。', '本品为北京小吃，回民风味，系由熟炒面拌牛骨髓油用沸水冲制而成，呈稠糊状，质地细腻，甜润中带有浓郁的酥油香气，宜于老人食用。本品又名油炒面。除用牛骨髓油外，还可用奶油、芝麻油。若成咸味的也很好吃，风味不逊。', '吃时将油炒面分盛10个碗内，用沸水冲搅成稠糊状，然后各放上适量的白糖和糖桂花汁搅匀，即成。', NULL, '面粉500克，牛骨髓油150克，黑芝麻20克，白芝麻20克，核桃仁20克，瓜子仁10，白糖和糖桂花汁各适量。');
INSERT INTO `recipe` VALUES (4467, 121, '玉米面茶', '1.将芝麻炒至焦黄，研碎。芝麻酱用熟油调稀糊状，和芝麻末、精盐一起搅匀，备用。\r\n2.将玉米面和小米面放在一起加水调成稀糊状。\r\n3.锅内加水1000克，烧开后倒入面糊，边倒边搅动；待再烧开后，用小火熬成粥状，分盛碗内，浇入芝麻酱，即成。', '本品清香味美，营养丰富，制作简单。', NULL, NULL, '玉米面100克，小米面50克，芝麻酱40克，芝麻10克，精盐10克，熟油15克。');
INSERT INTO `recipe` VALUES (4468, 121, '郁金桔酒', '1.制取苏打水：用凉开水溶化小苏打，制取0.6%的溶液，备用。\r\n2.制取基本酒液：将白糖放入大杯加进桔汁、红葡萄酒、味美思酒和黄酒。\r\n3.最后调制：将备用的苏打水倒入大杯中，倒至整个饮料为500克为止，搅拌均匀即可。', NULL, NULL, NULL, '桔汁40克，白糖30克，红葡萄酒60克，味美思酒20克，黄酒数滴，小苏打适量。');
INSERT INTO `recipe` VALUES (4469, 121, '自调大富豪', '摇动法。在摇酒器中放入2－3块冰块，再依次倒入上述材料，用力摇动10秒种，然后将酒液连同冰块一并倒入海波杯中。', NULL, NULL, NULL, '糖粉1茶匙，玫瑰露酒5毫升，红粮大曲30毫升，鲜牛奶50毫升。');
INSERT INTO `recipe` VALUES (4470, 121, '自制饮品－丽人', '摇动法。在调酒器中放入3块冰块，将上述材料依次倒入，摇妥，滤入鸡尾酒杯中，在杯口点缀1颗鲜草莓或樱桃。', '颜色绯红，干中有甜，烈中有柔。', NULL, NULL, '一个鸡蛋的蛋白，红味美思酒2茶匙，尖庄酒30毫升。');
INSERT INTO `recipe` VALUES (4471, 122, '阿夸维特酒', '马铃薯煮熟后，以裸麦或大麦芽糖化，发酵，然后使用连续蒸馏法制出纯度高达百分之九十五的蒸馏液，这种蒸馏液用蒸馏水稀释后，加上各种草根、树皮等香料，就其制法来说类似于金酒，至于使用的植物的种类与分量多寡，各蒸馏所都有所不同，因此，各种牌子的阿夸维特酒之间，风味差异十分微妙。稀释后上市销售的阿夸维特一般酒精含量在40至45度之间。', NULL, NULL, NULL, '马铃薯、裸麦或大麦芽。');
INSERT INTO `recipe` VALUES (4472, 122, '白兰地', '白兰地的生产过程包括发酵、蒸馏、陈酿、勾兑等。', '白兰地的用途\r\n白兰地除了已公认为有医疗作用外，所有的都是由水果酒（WIN，主要指有葡萄酒）蒸馏来的，因此都可作为饭后酒。此外加了梳打水，可作任何休闲时的饮料。在咖啡里加些白兰地更好。因为白兰地有浓郁的香味，在作甜食、布丁、糕饼和冰淇淋时，加上一点就可增加美味，所有白兰地都可以调为各种混合饮料。', NULL, NULL, '葡萄、苹果、梨、桃子、草莓、杏、李子、野草莓和樱桃等水果。');
INSERT INTO `recipe` VALUES (4473, 122, '伏特加', '以马铃薯、小麦等谷物为原料，通过重复蒸馏，精心过滤的方法，除去酒精中所含的毒素和其它异物的一种纯净的高酒精浓度的饮料。', '俄罗斯的国酒伏特加酒液透明，非常纯净，除酒香外，几乎没有其他香味，但口感较烈，是北欧寒冷国家十分流行的烈性饮料。其名品有：波士伏特加、苏联红牌、苏联绿牌、柠檬那亚、斯大卡、朱波罗夫卡等。', NULL, NULL, '马铃薯、小麦');
INSERT INTO `recipe` VALUES (4474, 122, '金酒', '荷兰金酒是以大麦芽与裸麦等为原料，经发酵后蒸馏三次获得谷物原酒，然后加入杜松子香料再蒸馏。最后将精馏而得的酒贮存于玻璃槽中待其成熟，包装时稀释至酒度量40度左右；英国金酒的生产过程比荷兰金酒简单，它用食用酒精和杜松子以及其它香料共同蒸馏而得干金酒。', '荷兰金酒色泽透明清亮，香味突出，风格独特，适宜于单饮，不宜作鸡尾酒的基酒；英国金酒酒液无色透明，气味奇异清香，口感醇美爽适，既可单饮，也可与其它酒混合配制或作鸡尾酒的基酒。', NULL, NULL, '玉米、大麦、杜松子、芫荽、菖蒲根、小豆蔻、当归、茴香、杏仁、柠檬皮等。');
INSERT INTO `recipe` VALUES (4475, 122, '朗姆酒', '朗姆酒是制糖业的一种副产品，它以蔗糖作原料，先制成糖蜜，然后再经发酵、发馏，在橡木桶中储存3年以上而成。', '朗姆酒含酒精42%－50%，酒液有琥珀色、棕色，也有无色的。根据风味特征，可将朗姆酒分为：浓香型、轻香型。淡色和棕色朗姆略有糖蜜味，略甜，较醇。深色朗姆颜色较深，具有刺激性芳香，风味十分独特。', NULL, NULL, '蔗糖。');
INSERT INTO `recipe` VALUES (4476, 122, '力娇酒', '水果力娇酒多用浸泡法。比如首先将白兰地放在木桶里，再放鲜杏或杏干进行浸泡，直到把杏的颜色及香味泡出为止，大约要6－8个月，然后再把白兰地吸出来过滤，加进适量糖浆制成。植物力娇酒是使用过滤和蒸馏方法制成的。过滤器把增加味道的物质如：植物叶、籽等放在过滤器的上部，下面放白兰地或其它酒类，使下面的酒从下至上循环，以便吸收到植物的味道。这样不断地上下循环，直到把全部味道吸收为止。最后，把上方的物质再经蒸馏后，掺入下方的酒中过滤，加糖浆后立即装瓶。', NULL, NULL, NULL, '它的基酒包括：白兰地、金酒、朗姆酒、威士忌以及其它的蒸馏酒和香料；另外，还有2.5%以上的糖浆。');
INSERT INTO `recipe` VALUES (4477, 122, '利口酒', NULL, NULL, NULL, NULL, '基酒、加味材料、糖或蜂蜜。');
INSERT INTO `recipe` VALUES (4478, 122, '啤酒', NULL, NULL, NULL, NULL, '啤酒的生产原料是谷物，包括大麦、小麦、玉米、黑麦、稻谷等谷类作物。');
INSERT INTO `recipe` VALUES (4479, 122, '葡萄酒', '1.葡萄采摘。每年的九、十月份是葡萄的成熟期，葡农们统一进园开始采摘葡萄。\r\n2.破碎榨汁。葡萄采摘下来后必须在二十四小时内送入酿酒厂进行破碎和榨汁处理。\r\n3.发酵。将新鲜的葡萄汁放入发酵桶中进行发酵，在发酵过程中，葡萄中的酶菌和酵母与葡萄糖作用后产生酒精和二氧化碳，当酒精含量达到13%－15%时，发酵自动停止。\r\n4.陈酿。发酵完成后的葡萄酒是不能立即饮用的，需要将它进行陈酿，使葡萄酒的清香浓郁、甘醇丰润的独特品质逐渐形成。\r\n5.倒桶。陈酿阶段，葡萄酒处于相对静止状态，发酵中剩下的微粒会慢慢地沉入酒', NULL, NULL, NULL, '葡萄。');
INSERT INTO `recipe` VALUES (4480, 122, '特基拉', '玛圭龙舌兰从栽培到收割要8至10年时间，收割后的龙舌兰首先要摘抽叶子，然后将其根部七八十厘米处切割成块，用蒸气锅加热，使其淀粉质变成糖分，经过榨汁后就可以得到一种甜味的汁液，这种汁液经过发酵，并采用连续蒸馏法进行蒸馏，即生产出酒精度达到45度左右，具有龙舌兰天然风味的特基拉酒。', '粗犷豪爽的特基拉酒由于采用独特的原材料制成，深受墨西哥印第安人的喜爱。特基拉是一种酒体很重的烈性酒，目前在北美特别是在各大学校园内十分流行，它十分适合于调配鸡尾酒，近年来有很多鸡尾酒都是由特基拉加上果汁、香甜酒调配而成。', NULL, NULL, '玛圭（Maguey）龙舌兰。');
INSERT INTO `recipe` VALUES (4481, 122, '威士忌', '1.大麦发芽。\r\n2.烘烤麦芽。\r\n3.制浆。\r\n4.蒸馏。\r\n5.陈酿。威士忌经过发酵以后必须注入橡木桶进行陈酿，使酒品不断成熟，品质提高。\r\n6.勾兑。勾兑是威士忌生产过程中十分重要的一环，它关系到产品的质量和品质。', '大都呈棕红色，清澈透亮，清香优雅，口感醇厚，绵柔，回味悠长。', NULL, NULL, '小麦、谷物等。');
INSERT INTO `recipe` VALUES (4482, 122, '味美思', '味美思的生产工艺，要比一般的红、白葡萄酒复杂。它首先要生产出干白葡萄酒作原料。优质、高档的味美思，要选用酒体醇厚、口味浓郁的陈年干白葡萄酒才行。然后选取苦艾、龙胆、甘菊、薄荷等二十多种芳香植物或者把这些芳香植物直接放到干白葡萄酒中浸泡，或者把这些芳香植物的浸液调配到干白葡萄酒中去，再经过多次过滤和热处理，冷处理，经过半年左右的贮存，才能生产出质量优良的味美思。', '味美思的饮用方法在我国不拘形式，在国外习惯上要加冰块或杜松子酒。味美思还有一大功用，就是调配鸡尾酒。因为味美思除了具有加香的特点，还具有加浓的特点，它含糖量高达15度，所含固形物较多，比重大，酒体醇浓，是调配鸡尾酒不可缺少的酒种。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4483, 122, '香槟酒', '为了酿造质量优异的香槟酒，多采用传统的瓶式发酵法，即“香巴尼方法”，就是一瓶一瓶地单独发酵。把在低温条件下储存两年多的“基础酒”中加入砂糖溶液和人工酵母，然后将酒、糖、酵母的混合原料一瓶瓶地装入香槟酒瓶中。封好口的香槟酒瓶被放入地窖里，头尾相间，水平放置。窖内保持恒温12至14摄氏度，进行缓慢低温发酵。经过80天左右，瓶内生成大量二氧化碳，每平方厘米有六个大气压力。香槟酒的发泡过程完成了。发酵好的香槟酒还要在贮藏室里贮存二年乃至更长时间，让瓶内的二氧化碳慢慢地、完全地溶解在酒中。完全发酵好的香槟酒又被移置', '香槟酒色泽明丽。启塞时响声清脆、悦耳。酒倒入杯中后应有洁白的泡沫，而且泡沫不断地从杯底向上翻涌，可持续几十分钟不止。有葡萄酒业的行话说，叫做“起泡持久”。香槟酒还具有醇正清雅、优美和谐的果香，并具有清新、愉快、爽怡的口感。', NULL, NULL, '葡萄。');
INSERT INTO `recipe` VALUES (4484, 122, '雪利酒', '雪利酒的生产过程十分复杂，通常葡萄经过破碎和压榨后，葡萄汁便被送入酒窖发酵，发酵时间为3个月左右，一旦发酵停止便立即倒入新桶，然后兑大白兰地进行强化。雪利酒的老熟采取生物老熟法，这是雪利酒独特的生产工艺，利用这种生物老熟法酿出的酒具有一种特有的酒香。', '雪利酒以干型为主，主要分菲奴（Fino）和奥鲁罗索（Oloroso）两大类。菲奴色泽黑淡，气味干烈。奥鲁罗索雪利酒呈深棕色，体重色深，在销售前都要进行糖化处理，菲奴酒可以在喝汤时饮用，也可用作开胃酒，奥鲁罗索酒是最好的餐后甜酒，不过雪利酒随时都可以饮用。喝雪利酒之前一定要把它冷却，特别是菲奴酒，这样才能显示出它的香味。', NULL, NULL, '葡萄。');
INSERT INTO `recipe` VALUES (4485, 123, '巴山雀舌', NULL, '成品外形扁平匀直，色泽绿润略显毫；香气栗香高长，滋味鲜爽回甘，汤色黄绿明亮；叶底嫩匀成朵。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4486, 123, '白毫银针', NULL, '银针白毫芽头肥壮，遍披白毫，挺直的针，色白似银，福鼎所产茶芽茸毛厚，色白富有光泽，汤色黄，味清鲜爽口，政和所产，汤味醇厚，香气清芬。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4487, 123, '白兰花茶', NULL, '白兰花香浓郁持久，是窨制烘青绿茶的主要原料，白兰花茶的特征是，外形条索紧结重实，色泽墨绿尚润，香气鲜浓持久，滋味浓厚尚醇，汤色黄绿明亮，叶底嫩匀明亮。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4488, 123, '白琳工夫', NULL, '白琳工夫系小叶种红茶，条索细长弯曲，茸毫多呈颗粒绒状，色泽黄黑、内质汤色浅亮，香气鲜醇有毫香，味清鲜甜和，叶底鲜红带黄。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4489, 123, '白牡丹', NULL, '白牡丹属白茶类，它以绿叶夹银色白毫芽形似花朵，冲泡之后绿叶落归根托着嫩芽，宛若蓓蕾初开，故名白牡丹。白牡丹开两叶包一芽，叶态自然，色泽深灰绿茶或暗青苔色，叶张肥嫩，呈波纹隆起，叶背遍布洁白茸毛，叶缘向叶兹有微卷，芽叶连枝，汤色杏黄或橙黄，叶底浅灰，叶脉微红，汤味鲜醇。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4490, 123, '川红工夫', NULL, '川红工夫外表条索肥壮圆紧，显金毫，色泽乌黑油润，内质香气清鲜，滋味醇厚鲜爽，汤色浓亮，叶底厚软红匀。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4491, 123, '冻顶乌龙', NULL, '冻顶茶的鲜叶，采自青心乌龙品种的茶树上，故又名“冻顶乌龙”，属于轻度半发酵茶，制法则与包种茶相似，应归属于包种茶类。文山包种和冻顶乌花是姐妹茶，外形非常相似，都有强劲的芳味道，不过文山包种茶叶要稍粗长一些。冲泡后，冻顶茶汤色呈橙黄色，有桂花香，汤味醇厚甘润，茶过咽喉，香气自然涌出，这种茶百喝不厌。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4492, 123, '洞庭碧螺春', '碧螺春是高级传统工艺茶，完全保持手工制作，揉中带炒，炒中带揉。细嫩的芽叶，巧夺天工的高超技艺，使碧螺春形成了色香味形俱美的独特风格。洞庭碧螺春，茶香百里最。', '有花香果味的天然品质，香鲜浓，味鲜醇，色鲜艳，被人们誉为名茶珍品。碧螺春成茶外形卷曲呈螺，白毫显露，银绿隐翠，叶芽幼嫩，鲜爽生津，冲泡后白云翻滚，雪花飞舞，清香袭人。用温开水冲泡，仍能沉于杯底，如先冲了水再放茶叶，也照样下沉，放香展叶，因此碧螺春驰名国内外。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4493, 123, '洞庭春', '鲜叶于清明后采摘，以一芽一叶初燕尾服为标准。经摊放、杀青、清风、初揉、 初炒、摊凉、烘焙等工序加工而成。', '成品外形条索赔紧结微曲，芽叶肥硕匀齐，白毫满披隐翠，内质香气高鲜持久，滋味醇厚鲜爽，汤色嫩绿清澈；叶底嫩绿明亮。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4494, 123, '凤凰水仙', NULL, '凤凰单丛有“形美、色翠、香郁，味甘”之誉，茶条挺直肥大，色泽呈鳝鱼皮色，油润有光。茶汤橙黄清澈， 碗壁显金黄色彩圈，叶底肥厚柔团软，边缘朱红，叶腹黄亮，味醇爽回味甘甜，具天然花香，香味持久，耐泡。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4495, 123, '高峰云雾', '鲜叶以一芽一、二叶初为标准，于清明前后采摘。经摊放、杀青、青风、 初揉、初干、做形、提毫、摊凉、烘焙等八道工序而成。', '成品细紧弯曲显毫，色泽深绿尚润；香气高而持久，汤色嫩绿明亮，滋味鲜醇；芽叶完整成朵、嫩绿明亮。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4496, 123, '贡眉（寿眉）', NULL, '优质贡眉毫心显而多，色泽翠绿，汤色橙黄或深黄，叶底匀整、柔软、鲜亮、叶张主脉迎光透视呈红色，味醇爽，香鲜纯。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4497, 123, '狗牯脑', '鲜叶采自当地群体小叶种，每年清明前后开采，标准为一芽一叶。经拣青、杀青、初揉、二青、复揉、整形提毫、炒干等工序加工而成。', '成品外形紧结秀丽，白毫显露，芽端微勾；香气高雅，略有花香，泡后速沉，汤色清明，滋味醇厚；叶底黄绿。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4498, 123, '广东大叶青', '制法是先萎凋后东青，再揉捻闷堆。这与其它黄茶不同。产品品质具有黄茶的一般特点。所以也归属同茶类。', '广东大叶青的品质特点是，外形条索肥壮、紧结、重实，老嫩均匀，叶张完整，显毫，色泽青润显黄，香气纯正，滋味浓醇回甘，汤色橙黄明亮，叶底淡黄。产品分1－5级。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4499, 123, '海马宫茶', NULL, '海马宫茶属黄茶类名茶。具有条索紧结卷曲，茸毛显露，青高味醇，回味甘甜，汤色黄绿明亮，叶底嫩黄匀整明亮的特点。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4500, 123, '湖红工夫', NULL, '湖红工夫以安化工夫为代表，外形条索肥实，香气高，滋味醇厚，汤色浓，叶底红稍暗，平江工夫香高，但欠匀净，长寿街及浏阳大山一带产香高味厚（靠近江西修水，归入宁红工夫）淅化、桃源工夫外形条索紧细，毫较多，锋苗好，但叶肉较薄，香气较低，涟源工夫新发展的茶，条索紧细，香味较淡。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4501, 123, '湖南黑茶', NULL, '黑志茶分为4个级，高档茶较细嫩，低档茶较粗老。一级茶条索紧卷、圆直，叶质较嫩，色泽黑润。二级茶条索尚紧，色泽黑褐尚润。三级茶条索欠紧，呈泥鳅色，色泽纯净呈竹叶青带紫油色或柳青色。四级茶吉张宽大粗老，条松扁皱折，色黄褐，湖南黑毛茶内质要求香，无粗涩味。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4502, 123, '黄山毛峰', NULL, '黄山毛峰成品茶，外形细扁稍卷曲，状似雀舌，白毫显露，色如像牙，黄绿油润，带金黄色鱼叶（俗称茶简）冲泡后，雾气凝顶，滋味浓醇和，茶汤清澈，叶底明亮，嫩匀成朵，冲泡五、六次，香味犹存。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4503, 123, '霍山黄芽', NULL, '霍山黄芽的品质特点是，外形似雀舌，芽叶嫩细多毫，叶色嫩黄、汤色黄绿清明，香气鲜爽，有熟栗子香，滋味醇厚回甜，叶底黄亮，嫩匀厚实。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4504, 123, '君山银针', NULL, '君山银针属芽茶，因茶花树品种优良，树壮枝衡，芽头肥壮重实，每斤茶约2.5万个芽头。君山银针风格独特，岁产不多，质量超群，为我国名优茶之佼佼者，其芽头肥壮，紧实挺直，芽身金黄，满披银毫，汤色橙黄明净，香气清纯，滋味甜爽，叶底嫩黄匀亮。根据芽头肥壮程度，君山银针产品分特号、一号、二号三个档次。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4505, 123, '老青茶', NULL, '用以压制青砖茶的老青茶分面茶与里茶两种，面茶较精细，晨茶较粗放。老青茶一般分为三个级别，鲜叶采割标准通常按茎梗皮色划分。一级茶发青梗为主，基部稍带红梗。二级茶（二面茶）以红梗为主，顶部稍带青梗。三级茶（里茶）为当年生红梗，不带麻梗。老青茶的品质的要求，一级别茶叶子成条，红梗为主，叶色乌绿微黄。三级茶（里茶）叶面卷皱，红梗，叶色乌绿带花，茶梗以当年新梢为度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4506, 123, '六堡散茶', NULL, '六堡用茶的品质特点是，条索长整尚紧，色泽黑褐光润，汤色红浓。香气醇陈，滋味甘醇爽口，叶底呈桐褐色，并带有松烟味和槟榔味。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4507, 123, '鹿苑毛尖', NULL, '鹿苑毛尖的品质特点是，外形条索环状（环子脚），白毫显露，色泽金黄（略带鱼子泡），香郁高长，滋味醇厚回甘，汤色黄净明亮，叶底嫩黄匀整。鹿苑茶品质独具风格，芬芳馥郁，滋味醇厚，被誉为湖北茶中之佳品。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4508, 123, '茉莉花茶', '茉莉花茶用经加工干燥的茶叶,与含苞待放的茉莉鲜花混合窨制而成的再加工茶,其色、香、味、形与茶坯的种类、质量及鲜花的品质有密切的关系。大宗茉莉花茶以烘青绿茶为主要原料,统称茉莉烘青。', '茉莉花茶既是香味芬芳的饮料，又是高雅的艺术品。茉莉鲜花洁白高贵，香气清幽，近暑吐蕾，入夜放香，花开香尽。茶能饱吸花香，以增茶味。只要泡上一杯茉莉花茶，便可领略茉莉的芬芳。外形条索紧细匀整，色泽黑褐油润，香气鲜灵持久，滋味醇厚鲜爽，汤色黄绿明亮，叶底嫩匀柔软。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4509, 123, '宁红工夫', NULL, '宁红工夫锋苗挺拔，色乌略红，光润；内质香高持久似祁红，滋味醇厚甜和，汤色红亮，叶底红匀。宁红工夫具有独特的艺术风格和特异的色、香、味、形、多次被评为江西省的优质名茶。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4510, 123, '普洱茶', NULL, '普洱茶外形条索粗壮肥大，色泽乌润或褐红（俗称猪肝色），滋味醇厚回甘，并具有独特的陈香。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4511, 123, '祁红', NULL, '祁红，是祁门红茶的简称，为工夫红茶中的珍品，它的最大的特征就是充满兰花香味，茶叶上有细毛，形状细长、结实，且第一片茶叶的长度都几乎相同。祁红具独特的清鲜持久的香味，被国内外茶师称为砂糖香或苹果香，并蕴藏有兰花香，清高而长，独树一帜，国际市场上称之为“祁门香”。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4512, 123, '祁门工夫', '在制茶方法上，实行机械制茶，着重抓外形紧结苗秀和内质香味，保持并发扬祁门香的特点，使祁红工夫品质经久不衰，盛誉常在', '祁门工夫茶条索紧秀，锋苗好，色泽乌黑泛灰光，俗称“宝光”；内质香气浓郁高长，似蜜糖香，又蕴藏有兰花香，汤色红艳，滋味醇厚，叶底嫩软红亮。并以外形苗秀，色有“宝光”和青气浓郁而著称，在国内外享有盛誉。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4513, 123, '西湖龙井', '龙井茶优异的品质是精细的采制工艺所形成的。采摘1芽1叶和1芽2叶初展的芽叶为原料，经过摊放、炒青锅、回潮、分筛、回锅、筛分整理（去黄片和茶末）、收灰贮存数道工序而制成。龙井茶炒制手法复杂，依据不同鲜叶原料不同炒制阶段分别采取“抖、搭、捺、拓、甩、扣、挺、抓、压、磨”等十大手法。凡观看过炒制龙井茶全过程的，都会认为龙井茶确实是精工细作的手工艺品。', '其品质特点是：外形扁平挺秀，色泽绿翠，内质清香味醇，泡在杯中，芽叶色绿，好比出水芙蓉，栩栩如生。西湖龙井茶素以“色绿、香郁、味甘、形美”四绝著称。品饮龙井茶，宜用玻璃杯冲泡，3克茶叶加200毫升80度左右的热水，冲泡3－5分钟后，就可闻香、观色、品味了。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4514, 123, '小种红茶', NULL, '小种红茶外形条索肥实，色泽乌润，泡水后汤色红浓，香气高长，带松烟香，滋味醇厚，带有桂圆汤味，加入牛奶，茶香味不减，形成糖浆状奶茶，液色更为绚丽。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4515, 123, '宣红工夫', NULL, '宜红工夫以条索紧细有金毫，内质香味鲜醇，汤色红亮有“冷后浑”为其主要特点。外形条索紧细有金毫，色泽乌润，香甜纯高长，味醇厚鲜爽，汤色红亮，叶底红亮柔软，茶汤稍冷即有“冷后浑”现象产生，系我国工夫红茶之一。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4516, 123, '雨花茶', NULL, '“雨花茶”的特点是：肉质叶底匀嫩，滋味鲜凉，气香色清。 　 　　', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4517, 123, '越红工夫', NULL, '越红工夫以条索紧结挺直，得实匀齐，锋、苗、显，净度高的优美外形称著。越红工夫色泽乌润，外形优美，内质香味纯正，汤色红亮较浅，叶底稍暗。越红毫色银白或灰白。浦江一带所产红茶，茶索尚紧结壮实，香气较高，滋味亦较浓，镇海红茶较细嫩。总的来说，越红条索虽美观，但叶张较薄，香味较次。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4518, 123, '珠兰花茶', '珠兰花茶以清香幽雅、鲜爽持久的珠兰和米兰为原料，选用高级黄山毛峰、徽州烘青、老竹大方等优质绿茶作茶坯，混合窨制而成。由于珠兰花香隽永持久，在窨制后，花香气分子的挥发与茶叶对香气完全吸咐达到平衡需要在一段时间，即窨后的熟成作用需持续100天左右。', '珠兰花茶的品制特征是，外形条索紧细，锋苗挺秀，白毫显露，色泽深绿油润，花干整枝成串，一经冲泡，茶叶徐徐沉入杯底，花如珠帘，水中悬挂，妙趣横生，细细品啜既有兰花特有的幽雅芳香，又兼高档绿茶鲜爽甘美的滋味，一杯在手，实为一种高尚的精神享受，尤为高尚女士所爱不释手。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4519, 124, '白沙液', '此酒选用整粒高粱浸泡蒸煮，上箱堆积，二次投料，六个月发酵（连续发酵5次），使用高、中温曲，分层蒸馏，贮存后再精心勾兑而成。', '白沙液，无色透明，香气浓郁，醇正柔和，后味回甜，兼有茅台和泸州大曲之香型，亦茅亦泸，有茅有泸，茅泸兼而有之，属兼香型，形成自己的独特风格。酒度为54度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4520, 124, '宝丰酒', '该酒选用本地优质高粱为原料，用小麦、大麦、豌豆制成大曲为糖化发酵剂，采用传统清蒸二次清蒸工艺，经续渣操作，水泥池地窖发酵，甑桶蒸馏，量质摘酒，长贮陈酿，精心勾兑等工序而酿成。', '宝丰酒无色透明，清香芬芳，甘润爽口，纯净绵柔，回味悠长。酒度分54度、63度二种，属清香型大曲白酒。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4521, 124, '宝莲大曲', '宝莲大曲以优质高粱为原料，以上麦特制成的。包包曲为糖化发酵剂，采用固态续糟混蒸工艺，人工培养窖泥，新窖老熟，低温入窖，双轮底分层封泥，回沙回酒发酵，缓火蒸馏，分级摘酒，地窖陶器贮存，勾兑品尝，合格后包装出厂。', '宝莲大曲属浓香型曲酒，是蜀中新秀，其酒液无色透明，无悬浮物，窖香浓郁，醇和净爽，诸味协调，余香悠长，后味干净。酒度为38度、54度二种。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4522, 124, '陈曲酒', '此酒于以高粱为原料，用河内白曲制成的麸曲为糖化剂，5种产酯酵母为发酵剂，经人工老窖发酵，量质摘酒，陈贮老熟，精心勾兑，品评检验等工艺，要求酒的色、香、味达到高度谐调，方可出厂。', '陈曲酒具有色清、香浓、醇甜、余香的特点，评酒专家评为：“清亮透明、窖香浓郁、绵甜纯厚、尾净余长”。该酒属于浓香型麸曲白酒，酒度为58度、55度。 谢德萍诗曰：“名存草原四千里，位居北疆第一家”。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4523, 124, '豉味玉冰烧', '该酒选用优质大米为原料，以小曲为糖化发酵剂，采用传统工艺酿制成米香型白酒，放入陈年肥肉缸浸酿，所用肥肉需经加工及陈藏处理，肉、酒比例合理，浸肉时间达数十天，再经澄清、勾兑等工序酿成。', '豉味玉冰烧呈玉洁冰清状，晶莹悦人，豉香纯正，诸味协调，入口醇和，余味甘爽，低而不淡，风格独特。酒度30度，是其它香型的豉香小曲白酒典型代表。饮后有通血脉、散湿气，舒筋活血，消除疲劳等功效。远销香港、澳门地区及日本、丹麦、美国、加拿大、东南亚各国。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4524, 124, '丛台酒', '此酒以红高粱为原料，用小麦制曲。采取高温制曲，清蒸辅料，回醅发酵，回酒发酵，分批蒸烧，缓慢蒸馏，分级摘酒，分质贮存，精心勾兑等工艺酿成。', '酒质无色透明，芳香浓郁，入口绵软，落口甜净，回味悠长，属浓香型酒。酒度为53度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4525, 124, '董酒', '该酒选用优质高粱为原料，引水口寺甘洌泉水，以大米加入95味中草药制成的小曲和小麦加入40味中草药制成的大曲为糖化发酵剂，以石灰、白泥和洋桃藤泡汁拌合而成的窖泥筑成偏碱性地窖为发酵池，采用两小两大，双醅串蒸工艺，即是小曲由小窖制成的酒醅和大曲由大窖制成的香醅，两醅一次串蒸而成原酒，经分级陈贮一年以上，精心勾兑等工序酿成。', '董酒无色，清澈透明，香气幽雅舒适，既有大曲酒的浓郁芳香，又有小曲酒的柔绵、醇和、回甜，还有淡雅舒适的药香和爽口的微酸，入口醇和浓郁，饮后甘爽味长。由于酒质芳香奇特，被人们誉为其它香型白酒中独树一枝的“药香型”或“董香型”典型代表。酒度58度，低度酒38度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4526, 124, '杜康酒（汝阳）', '杜康酒选优质高粱为原料，用纯小麦制成的大曲为糖化发酵剂，以水质味甜纯净、硬度低的天然杜康泉水为酿造用水，酿造工艺采用高温制曲，混蒸混烧，老窖续渣，低温入池，长期发酵，多轮取醅，分层回酒，量质接酒，分类贮存，精心勾兑而酿成。', '杜康酒色清透明，芳香浓郁，柔绵醇净，味长回甜，独具风格，属浓香大曲白酒。酒度为52度，畅销全国，远销港、澳地区及日本、马来西亚、新加坡、美国等52个国家。曹操诗云：何以解忧，惟有杜康。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4527, 124, '汾酒', '汾酒以晋中平原所产的“一把抓”高粱为原料，用大麦、豌豆制成的“青茬曲”为糖化发酵剂，取古井和深井的优质水为酿造用水。这口有优美传说的古井之水，与汾酒的品质有很大关系。汾酒发酵仍沿用传统的古老“地缸”发酵法。酿造工艺为独特的“清蒸二次清”。操作特点则采用二次发酵法，即先将蒸透的原料加曲埋入土中的缸内发酵，然后取出蒸馏，蒸馏后的酒醅再加曲发酵，将两次蒸馏的酒配合后方为成品。', '汾酒，酒液无色透明，清香雅郁，入口醇厚绵柔而甘洌，余味清爽，回味悠长，酒度高（65度、53度）而无强烈刺激之感。其汾特佳酒（低度汾酒）酒度为38度。汾酒纯净、雅郁之清香为我国清香型白酒之典型代表，故人们又将这一香型俗称“汾香型”。专家称誉“其色、香、味”被实为酒中“三绝”，历来为消费者所称道。除销往全国各地及香港、澳门地区外，远销新加坡、日本、澳大利亚、英国、法国、波兰、美国等五大洲四十余个国家。1960年谢觉哉题诗曰：逢人便说杏花村，汾酒名牌天下闻。草长莺飞春已暮，我来乃是雨纷纷。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4528, 124, '古井贡酒', '古井贡酒以本地优质高粱作原料，以大麦、小麦、豌豆制曲，沿用陈年老发酵池，继承了混蒸、连续发酵工艺，并运用现代酿酒方法，加以改进，博采众长，形成自己的独特工艺，酿出了风格独特的古井贡酒。', '古井贡酒酒液清澈如水晶，香纯如幽兰，酒味醇和，浓郁甘润，粘稠挂杯，余香悠长，经久不绝。酒度分为38度、55度、60度三种。书法家启功题词：“佳酿千年传魏井，浓香万里发汤都。”有诗为证：“琼浆始汉家，献帝龙颜夸。煮酒论英雄，曹刘说天下。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4529, 124, '桂林三花酒', '此酒以桂北优质大米为原料，用特制药曲，用漓江上游的优良江水，陈贮于条件优越的象山岩洞内，加之工艺精湛，使酿得的酒香醇无比。', '桂林三花酒清亮透明，有浓郁幽雅的蜜香，入口香醇甘爽，清洌回甜，饮后留香，为米香型白酒，酒度为56度。桂林三花酒早年就是广西名酒，远销港澳地区、东南亚和日本等国。名人有诗曰：三花香飘云天外，八仙醉卧烟霞中。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4530, 124, '剑南春', '此酒以高粱、大米、糯米、玉米、小麦为原料，小麦制大曲为糖化发酵剂。其工艺有：红糟盖顶，回沙发酵，去头斩尾，清蒸熟糠，低温发酵，双轮底发酵等，配料合理，操作精细而酿成。', '剑南春酒质无色，清澈透明，芳香浓郁，酒味醇厚，醇和回甜，酒体丰满，香味协调，恰到好处，清洌净爽，余香悠长。酒度分28度、38度、52度、60度，属浓香型大曲酒。著名书法家启功赋诗赞：美酒中山逐旧尘，何如今酿剑南春。海棠十万红生颊，却是西川醉前人。作家刘心武赋诗：人间有酒香满杯，难得剑南春滋味。艰幸独留自己尝，幸福赠予天下醉。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4531, 124, '孔府家酒', '该酒选用优质高粱为原料，以小麦制成大曲为糖化发酵剂，引老龙头泉水为酿造用水，采用传统浓香型工艺，经清蒸续渣，人工老窖发酵，低温入池，双轮底增酯，分层蒸馏，按质取酒，分级陈贮，降度除浊，精心勾兑等工序而酿成。', '孔府家酒无色透明，晶莹悦人，窖香馥郁，酒质醇绵，甜柔爽口，尾子干净，余味香长。专家评语为“香浓、绵甜、爽净”，特点鲜明，具有浓香型白酒的典型风格。酒度为39度。诗曰：孔府佳酿，储醇激义。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4532, 124, '口子酒', '口子酒是国家优质酒，采用优质高粱为原料，以小麦、大麦、豌豆制成大曲为糖化发酵剂，继承传统工艺，老窖发酵，长期陈酿，精心勾兑而成。', '本酒属浓香型估级大曲，无色透明，芳香浓郁，入口柔绵，清洌甘爽，余香回甜。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4533, 124, '郎酒', '郎酒以高粱和小麦为原料，用纯小麦制成高温曲为糖化发酵剂，引郎泉之水，其酿造工艺与茅台酒大同小异。两次投料，七次取酒，周期为九个月，按质分贮于天然溶洞的“天宝洞”和“地宝洞”，三年后再勾兑出厂。人称“山泉酿酒，深洞贮藏；泉甘酒洌，洞出奇香”。', '郎酒呈微黄色，清澈透明，酱香突出，酒体丰满，空杯留香长，以“酱香浓郁、醇厚净爽，幽雅细腻，回甜味长”的独特风格著称。系酱香型大曲酒。酒度分39度、53度二种。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4534, 124, '林河特曲', '此酒以红高粱为原料，用小麦、大麦、豌豆制曲为糖化发酵剂，取古井之水，泥池发酵，缓慢蒸馏，分级陈化贮存，然后精心勾兑而酿成。', '林河特曲酒清亮透明，窖香浓郁，味绵甘洌，尾净而有余香，入喉净爽，回香悠长。苹果香味醇和是该酒的特殊风味，属浓香型白酒。酒度为54度。远销新加坡、泰国、马来西亚等国家。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4535, 124, '凌川白酒', '凌川白酒以高粱为原料，用麸曲为糖化剂，添加生香酵母，发酵21天，经缓慢蒸馏、贮存、勾兑而成。', '酒液澄清透明，酱香突出，醇厚绵甜，入口柔和，回味悠长，酒度为55度，具有酱香型白酒典型风格，其色、香、味俱佳，富有自然感。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4536, 124, '六曲香', '以高粱为原料，引进汾酒大曲和酒醅中优良的六种霉菌制造麸曲，采用一清到底工艺酿成，故取名为六曲香。', '六曲香酒无色，清亮透明，清香纯正，醇和爽口，绵软回甜，饮后余香。酒度分为65度、62度、53度、38度四种。属清香型麸曲酒。有诗为证：“酿造风格独一枝，清香蝉联状元榜。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4537, 124, '泸州老窖特曲', '泸州曲酒的主要原料是当地的优质糯高粱，用小麦制曲，大曲有特殊的质量标准，酿造用水为龙泉井水和沱江水，酿造工艺是传统的混蒸连续发酵法。蒸馏得酒后，再用“麻坛”贮存一、二年，最后通过细致的评尝和勾兑，达到固定的标准，方能出厂，保证了老窖特曲的品质和独特风格。', '此酒无色透明，窖香浓郁，清洌甘爽，饮后尤香，回味悠长。具有浓香、醇和、味甜、回味长的四大特色，酒度有38度、52度、60度三种。华罗庚题诗：“何以解忧，唯有杜康；而今无忧，特曲是尝；产自泸州，甘洌芬芳。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4538, 124, '茅台酒', '茅台酒系以优质高粱为原料，用小麦制成高温曲，而用曲量多于原料。用曲多，发酵期长，多次发酵，多次取酒等独特工艺，这是茅台酒风格独特、品质优异的重要原因。酿制茅台酒要经过两次加生沙（生粮）、八次发酵、九次蒸馏，生产周期长达八九个月，再陈贮三年以上，勾兑调配，然后再贮存一年，使酒质更加和谐醇香，绵软柔和，方准装瓶出厂，全部生产过程近五年之久。', '茅台酒是风格最完美的酱香型大曲酒之典型，故“酱香型”又称“茅香型”。其酒质晶亮透明，微有黄色，酱香突出，令人陶醉，敞杯不饮，香气扑鼻，开怀畅饮，满口生香，饮后空杯，留香更大，持久不散。口味幽雅细腻，酒体丰满醇厚，回味悠长，茅香不绝。茅台酒液纯净透明、醇馥幽郁的特点，是由酱香、窖底香、醇甜三大特殊风味融合而成，现已知香气组成成分多达300余种。酒度53度。陈毅有诗：“金陵重逢饮茅台，万里长征洗脚来。深谢诗章传韵事，雪压江南饮一杯。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4539, 124, '湄窖酒', '该酒选用优质高粱为原料，以中温大糖化酵剂，采用传统工艺，经人工老窖，混蒸混烧，续糟发酵，蒸馏取酒，陈贮勾兑酿成。', '湄窖酒无色透明，芳香浓郁，醇甜甘爽，口味协调，尾净余长。酒度55度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4540, 124, '宁城老窖', '该酒选用本地优质高粱为原料，以河内白曲制成麸曲、多种生香酵母为糖化发酵剂，引隆盛泉、景泰泉、天聚泉三个古泉水为酿造用水。采用人工泥窖发酵，经量质摘酒，分级陈贮，合理勾兑，精心调配等工序酿制而成。', '宁城老窖酒清澈透明，窖香浓郁，绵甜甘洌，酒体醇厚，回味悠长，尾子爽净，具有麸曲浓香型酒的特点。酒度为55度。蒙族诗人安柯沁夫诗云：“纵横天下饮美酒，李白无缘到宁城；今日有人饮老窖，定会醉舞八仙亭。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4541, 124, '黔春酒', '该酒选用优质高粱、小麦为原料，选择从茅台酒醅分离的菌株和麸皮制成麸曲及酵母为糖化发酵剂，采用酱香型工艺，经清蒸续渣，加曲堆积，石窖发酵，蒸馏摘酒，贮存陈酿，勾兑调味等工序酿成。', '黔春酒呈微黄色，清澈透明，酱香明显，幽雅细腻，酒体醇厚、绵甜，入口醇和，回味悠长。酒度54度，属酱香型麸曲白酒。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4542, 124, '全兴大曲', '该酒选用优质高粱为原料，以小麦制成中温大曲，采用传统老窖分层堆糟法工艺，经陈年老窖发酵，窖熟糟香，酯化充分，续糟润粮，翻沙发酵，混蒸混入，掐头去尾，中温流酒，量质摘酒，分坛贮存，精心勾兑等工序酿成。', '全兴大曲酒质呈无色透明，清澈晶莹，窖香浓郁，醇和协调，绵甜甘洌，落口净爽。系浓香型大曲酒。酒度分38度、52度、60度三种。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4543, 124, '绍兴黄酒', '以稻米、黍米、黑米、玉米、小麦等为原料，经过蒸料，拌以麦曲、米曲或酒药，进行糖化和发酵酿制而成的各类黄酒。', '绍兴酒具有独特的色香味格，堪称中华黄酒第一味。色，呈琥珀色，即橙色。透明澄澈，纯净可爱，使人赏心悦目；香，有诱人的馥郁芳香，贮藏时间越久越香；味，醇厚甘鲜，回味无穷，甜、酸、苦、辛、鲜、涩与味和谐融合于一体；格，指由色香味综合形成的一种不同寻常的风格和格调。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4544, 124, '双沟大曲', '此酒以优质高粱为原料，以大麦、小麦、豌豆制成的高温曲为糖化发酵剂。采用人工老窖，低温慢发酵，回沙发酵，缓火蒸馏，分段取酒，分级贮存，合理勾兑等。', '此酒清澈透明，以芳香扑鼻、风味纯正，入口醇厚甘美、回香悠长、醇而不烈的独特风格著称于世。为浓香大曲酒，酒度为46度、54度，低度酒为双沟特液，其中39度为国家名酒，33度为国家优质酒。陈昊苏题诗：古泗州，醉猿洲，特液酿成淮水头，浓香品质优。争上游，再上游，不愧中华第一流，举世赞双沟。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4545, 124, '四特酒', '该酒选用肥硕大米或糯米、高粱为原料，以优质小麦制成大曲为糖化发酵剂，采用伏曲，经地窖发酵，醅香蒸酒，摘取酒身，贮存陈酿，老酒为底，精心勾兑，调味装瓶等工序而成。', '四特酒其香闻之清雅，饮后浓郁，回香独特，其味入口醇甜绵软，味感绵长，酒体香味物质协调，饮之不干口、不上头，且香气喷发，溢满于室，回味恰到好处，嗝香愉快，有心旷神怡之快感。属于其他香型大曲酒，酒度为54度。周恒刚诗曰：酒绵恰是观音柳，味洌犹如罗汉松。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4546, 124, '宋河粮液', '宋河粮液选用当地高粱为原料，以小麦大曲为糖化发酵剂，采取老窖泥池发酵，定期贮存酿成。', '宋河粮液无色透明，芳香浓郁，醇和清洌，绵甜适口，回香悠长，属浓香型酒。酒度为38度、54度两种。书法家欧阳中石诗曰：“老聃鹿邑家乡水，孔子师徒不敢强；天赐名泉封祭酒，至今醉柏不成行。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4547, 124, '汤沟特曲', '此酒选用优质高粱为原料，以高温大曲为糖化发酵剂，采用传统老五甑工艺，人工老窖，双轮底发酵，回沙发酵，分级贮存，精心勾兑等工序酿制而成。', '汤沟特曲酒色清透明，窖香浓郁，醇香甘洌，入口绵柔，回味较长，尾子干净，具有饮后头不晕、口不渴等特点。该酒为53度，其低度酒38度名为汤沟特液。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4548, 124, '沱牌曲酒', '沱牌曲酒以优质高粱、糯米为原料，以优质小麦、大麦制成大曲为糖化发酵剂，老窖作发酵池，采用高、中温曲，续糟混蒸混烧，贮存勾兑等工艺酿制而成。', '沱牌曲酒具有窖香浓郁、清洌甘爽、绵软醇厚、尾净余长，尤以甜净著称的独特风格，属浓香型大曲酒。酒度为38度、54度。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4549, 124, '五粮液', '五粮液的酿造原料为红高粱、糯米、大米、小麦和玉米五种粮食。糖化发酵剂则以纯小麦制曲，有一套特殊制曲法，制成“包包曲”，酿造时，须用陈曲。用水取自岷江江心，水质清洌优良。发酵窖是陈年老窖，有的窖为明代遗留下来的。发酵期在70天以上，并用老熟的陈泥封窖。在分层蒸馏、量窖摘酒、高温量水、低温入窖、滴窖降酸、回酒发酵、双轮底发酵、勾兑调味等一系列工序上，五粮液酒厂都有一套丰富而独到的经验，充分保证了五粮液品质优异，长期稳定，在中外消费者中博得了美名。', '五粮液酒无色，清澈透明，香气悠久，味醇厚，入口甘绵，入喉净爽，各味谐调，恰至好处。酒度分39度、52度、60度三种。饮后无刺激感，不上头。开瓶时，喷香扑鼻；入口后，满口溢香；饮用时，四座飘香；饮用后，余香不尽。属浓香型大曲酒中出类拔萃之佳品。著名科学家华罗庚赋诗曰：名酒五粮液，优选味更醇；省粮五百担，产量增五成。 豪饮李太白，雅酌陶渊明；深恨生太早，只能享老春。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4550, 124, '西凤酒', '西凤酒以当地特产高粱为原料，用大麦、豌豆制曲。工艺采用续渣发酵法，发酵窖分为明窖与暗窖两种。工艺流程分为立窖、破窖、顶窖、圆窖、插窖和挑窖等工序，自有一套操作方法。蒸馏得酒后，再经3年以上的贮存，然后进行精心勾兑方出厂。', '西凤酒无色清亮透明，醇香芬芳，清而不淡，浓而不艳，集清香、浓香之优点融于一体，幽雅、诸味谐调，回味舒畅，风格独特。被誉为“酸、甜、苦、辣、香五味俱全而各不出头”。即酸而不涩，苦而不黏，香不刺鼻，辣不呛喉，饮后回甘、味久而弥芳之妙。属凤香型大曲酒，被人们赞为它是“凤型”白酒的典型代表。酒度分39度、55度、65度三种。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4551, 124, '习酒', '该酒选用本地优质高粱为原料，以小麦制成高温大曲，采用传统酱香型工艺，经两次投料，露地糖化，石窖发酵，清蒸回烧，多次发酵，多次蒸馏，按质装坛，陈贮二年，精心勾兑等工序酿成。', '习酒无色透明，清澈晶亮；酱香突出，幽雅细腻，协调丰满，回味悠长，空杯留香不息。酒度53度，属酱香型大曲酒。远销日本、俄国、东南亚各国。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4552, 124, '仙潭大曲', '该酒选用优质高粱为原料，以大曲为糖化剂，采用沪州老窖大曲工艺，经续糟配料，老窖发酵，混蒸混烧，分段取酒，陈贮老熟，降度除浊，精心勾兑等工序酿成。', '仙潭大曲酒无色，清亮透明，窖香浓郁，醇甜净爽，幽雅细腻，回味悠长。酒度39度，系浓香型大曲酒。远销港、澳地区及美国、马里、日本、东南亚等国。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4553, 124, '洋河大曲', '洋河大曲以粘高粱为原料，用当地有名的“美人泉”水酿造，用高温大曲为糖化发酵剂，老窖长期发酵酿成。', '此酒清澈透明，芳香浓郁，入口柔绵，鲜爽甘甜，酒质醇厚，余香悠长。其突出特点是：“甜、绵软、净、香。”其酒有38度、48度和55度三种。洋河酒厂进行科学酿造，合理降低酒度，连续酿成28、18度低度白酒，形成系列化。该厂28度洋河大曲于1988年在全国第五届评酒会上荣获国家优质酒称号及银质奖。书法家启功诗曰：早闻佳酿出洋河， 一饮琼浆发浩歌；添得少陵诗料富，仙人第九席中多。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4554, 124, '伊川杜康酒', '此酒以优质高粱为原料，用小麦制成的混合曲（高、中、低温曲）为糖化发酵剂，采用酒厂畔的虎泉为酿造用水，以香醅泥坯老窖发酵，缓慢蒸馏，按质接酒，分级贮存，然后再精心勾兑而成。', '伊川杜康酒，清澈透明，柔润芳香，甘绵清洌，回味悠长，属浓香型大曲白酒。酒度为55度。此酒销全国各地，受到称赞，远销欧、亚、美洲50多个国家和地区。 诗人艾青题诗曰：“早闻杜康名，今闻杜康香。”', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4555, 124, '张弓酒', '该酒选用当地优质高粱为原料，以小麦、大麦制成大曲，采用传统老五甑工艺与新技术相结合精酿而成。', '张弓酒无色透明，窖香浓郁，绵甜爽净，诸味协调，不暴不烈，余香悠长，饮后怡畅，具有独特风格，属浓香型大曲白酒。酒度分28度、38度、54度三种。张爱萍题词：“香飘万里，名扬四海。”作家李英儒题曰：为饮一杯张弓酒,舍得宝马和貂裘。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4556, 124, '珍酒', '该酒选用优质高粱为原料，以小麦制成高温曲，采用茅台酒工艺，经两次投料、八次发酵、七次烤酒、陈贮勾兑等工序酿成。', '珍酒微黄透明，色泽清亮，酱香突出，酒体醇厚，幽雅细腻，入口酱香明显，后味较长，空杯留香持久。酒度54度，系酱香型大曲酒。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4557, 78, '薄荷蔬菜沙拉', '1.先把薄荷页切碎，与优格、盐、胡椒一同拌匀。 \r\n2.用生菜页排放于盘底，放少许玉米粒；再加入小黄瓜、番茄、三椒丝。 \r\n3.把鸡肉丁炒熟拌入，淋上做法1的薄荷优格酱汁即可。', '清凉适口，内含大量的维生素。', NULL, NULL, '薄荷叶20克、优格（小）1瓶、盐少许、胡椒少许、生菜叶100克、玉米粒15克、小黄瓜5克、番茄2个、三椒丝少许、鸡肉丁10克。');
INSERT INTO `recipe` VALUES (4558, 78, '朝阳鸡蛋豆腐', '1.软豆腐用中火蒸10分钟，沥干，保暖。 \r\n2.蒸鸡蛋切半，取出蛋黄压碎后待用。蛋白切粒，加入瘦肉，虾肉和腌料拌匀。\r\n3.用2汤匙油爆炒蛋白、瘦肉及虾肉至熟透，加入芡汁，煮至热透，淋上豆腐面，洒上压碎蛋黄及葱粒即成。', '软嫩鲜甜，口感润滑。', NULL, NULL, '软豆腐400克（切片），瘦肉75克（切粒），虾75克（去壳，去肠及沥干，切粒），大熟鸡蛋2只，葱粒适量。腌料：特级蚝油2汤匙，芝麻油1茶匙，胡椒粉适量。');
INSERT INTO `recipe` VALUES (4559, 78, '醋溜藕片', '1.将藕去节、削皮洗净，顺长一剖两半，再顶刀切成薄片，放入开水锅中略烫，捞出沥干水分待用。 \r\n2.炒锅置旺火上，倒入油3汤匙，烧至温热，下葱末、姜末，马上烹入醋，加入酱油、盐和清汤4汤匙，加入藕片略炒，用水淀粉勾芡，淋入花椒油，翻炒均匀后出锅即可。', '味道鲜美，酸甜适口。', NULL, NULL, '嫩藕400克，酱油半茶匙，醋2汤匙，盐、味精各适量，水淀粉1茶匙，花椒油半茶匙，葱末、姜末各适量。');
INSERT INTO `recipe` VALUES (4560, 78, '番茄焖明虾', '1.将明虾洗净煮熟，剥壳去肠杂切段。\r\n2.葱头、芹菜、青椒，番茄、蒜瓣洗净切末，胡椒研末，干辣椒洗净切段，备用。\r\n3.把锅烧热后倒入食油，待油温6成时，放入葱头、蒜末炒至微黄，放入芹菜、青椒、番茄炒至5成熟时，放入胡椒末，干辣椒炒透，倒入适量清汤煮沸，加入精盐调好口味，放入明虾段用文火焖数分即可食用。', '鲜辣味浓。', NULL, NULL, '明虾750克，葱头125克，芹菜50克，青椒75克，番茄750克。调料：食油75克，蒜瓣15克，干辣椒5克，精盐适量，胡椒粉6粒。');
INSERT INTO `recipe` VALUES (4561, 78, '干煸茭白', '1.将茭白削去外皮，切去老跟，切成5厘米长的大粗条。 \r\n2.炒锅上火，放油烧至六成热，放入茭白炸至棱角微呈黄色、皱皮时，加入酱油、盐煸炒入味，放入芽菜，烹入绍酒炒匀，淋香油即可起锅。', '鲜咸适宜，清淡爽口。', NULL, NULL, '嫩茭白500克，芽菜末30克，酱油2汤匙，绍酒、盐、香油适量。');
INSERT INTO `recipe` VALUES (4562, 78, '海米烧萝卜', '1.将萝卜洗净，去皮，适当切块待用；将海米洗净，用温水泡发。 \r\n2.炒锅上火，加适量油烧热，投入萝卜煸炒；加适量盐炒匀，再放海米及少量水，烧至萝卜熟透入味，加味精即可出锅。', '味道香嫩可口。', NULL, NULL, '萝卜250克，海米25克，盐、味精。');
INSERT INTO `recipe` VALUES (4563, 78, '红油茄泥', '1.将茄子洗净削皮，切成块，放入蒸锅蒸大约15分钟，挤净水分用勺捣成泥，放盘中待用。 \r\n2.将辣椒油、酱油、香油、糖、盐和味精放入碗中调匀，浇在茄子上，拌匀即可。', '味道鲜美，口感润滑。', NULL, NULL, '嫩茄子750克，辣椒油2汤匙，酱油半茶匙，香油1茶匙，糖半茶匙，葱末1汤匙或适量，盐、味精各适量。');
INSERT INTO `recipe` VALUES (4564, 78, '家常茄子', '1.将茄子削皮洗净，切成长4公分，粗1公分的条；把青蒜洗净切成段；将青辣椒切成小块。 \r\n2.炒锅上火烧热，放入油4汤匙，倒入茄子条翻炒数下，加盐，焙至茄子条的水分基本收干时，捞出将油沥干待用。 \r\n3.炒锅再上火，放入油3汤匙，炒香豆瓣酱，下青蒜段、木耳和青辣椒块炒香，倒入茄子条，再用酱油调味，用水淀粉勾芡，淋入辣椒油炒匀即可。', '柔软香润，味鲜咸适宜。', NULL, NULL, '茄子500克，青蒜2根，青辣椒3个，水发木耳50克，豆瓣酱1汤匙，酱油1汤匙，盐、味精各适量，水淀粉1汤匙，辣椒油1汤匙。');
INSERT INTO `recipe` VALUES (4565, 78, '姜拌藕', '1.将藕去节、削皮洗净，顶刀切成圆片，放在清水中浸泡待用。 \r\n2.取一容器，放入姜末、白醋、盐和香油，勾成调味汁待用。 \r\n3.锅中放清水烧开，投入藕片，烫透后捞出，沥干水分，趁热放入盛有调味汁的容器中，加味精，给容器盖上盖子，把藕片焖上，等藕片晾凉了以后拌匀，装盘即可。', '味道鲜美，口感润滑。', NULL, NULL, '嫩藕500克，嫩姜末2汤匙，白醋2汤匙，香油1茶匙，盐、味精各适量。');
INSERT INTO `recipe` VALUES (4566, 78, '酱腌紫茄', '1.茄子去蒂洗净，放入容器内，放入盐腌渍，加盖腌6小时，取出用凉开水洗净盐液，然后切成长条状备用。 \r\n2.将精盐、酱油、白糖、姜汁、切碎辣椒用开水调拌，加入茄段中浸泡24小时， 即可淋香油食用。', '茄香味浓，口感绵软。', NULL, NULL, '嫩茄子500克，红辣椒3克，精盐10克，酱油5克，白糖5克，姜汁7克，香油5克。');
INSERT INTO `recipe` VALUES (4567, 78, '溜藕片', '1.将藕去节、削皮洗净，顺长一剖两半，再顶刀切成片待用。 \r\n2.炒锅上火烧热，放油烧热，下姜末煸炒几下，放入藕片继续煸炒至熟，加酱油、白糖和少许开水，待白糖溶化后，淋入香醋，炒匀即可。', '味道鲜美，酸甜适口。', NULL, NULL, '嫩藕400克，白糖1汤匙半，香醋1汤匙，酱油1汤匙，姜末适量。');
INSERT INTO `recipe` VALUES (4568, 78, '平菇肉片', '1.把平菇洗净，撕成大片，投入沸水中烫透，取出挤干水分。葱姜切片。 \r\n2.将肉片加适量酱油、盐、鸡粉、绍酒、淀粉拌匀备用。 \r\n3.锅上火，放油烧热，放入葱姜爆香，再将肉片放入煸炒，当肉片变色后，加入水、酱油、盐、鸡粉、胡椒粉烧开。 \r\n4.将平菇放入，用微火烧5分钟，再转用大火，将汁收浓，加入水淀粉，使汁均匀地挂在肉片和平菇上即可。', '味道香美，内含丰富营养。', NULL, NULL, '新鲜的平菇250克，猪肉片100克，葱、姜、酱油，绍酒，胡椒粉、盐、淀粉、鸡粉适量。');
INSERT INTO `recipe` VALUES (4569, 78, '葡萄干焖鸡块', '1.将鸡洗净切成20块，抹上少许盐、胡椒粉腌片刻；\r\n2.番茄、土豆、青椒洗净切块；葱头洗净切丁；大蒜，芹菜洗净切末；备用。\r\n3.把锅烧热后倒入植物油，待油温6成时，放入大蒜、葱头炒至微黄后，放入鸡块一起炒至黄色后，加入番茄、芹菜炒透后，倒入适量鸡清汤用文火焖至8成熟时，放入葡萄干、土豆、青椒、豌豆拌匀用小火焖熟后，加入精盐、胡椒粉、醋精调好口味即可食用。', '咸酸香浓。', NULL, NULL, '鸡肉1000克，葡萄干100克，番茄125克，土豆750克，青椒150克，新鲜豌豆125克，芹菜50克，葱头50克，植物油100克，大蒜5克，醋精2.5克，精盐适量，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4570, 78, '青椒茄子', '1.将茄子削皮洗净，切成半公分厚的片，把青椒去籽、去蒂洗净切成粗丝待用。 \r\n2.将酱油、糖、淀粉、醋、鸡粉和2汤匙水勾成调味汁待用。 \r\n3.炒锅上火烧热，放入油4汤匙，倒入茄子片翻炒数下，调成中火，盖上锅盖焖上，待茄子片被煎软了，下青椒丝，迅速翻炒后倒入调味汁，炒匀后出锅即可。', '柔软香润，鲜咸适宜。', NULL, NULL, '茄子500克，随意几个青辣椒，酱油2汤匙，糖2茶匙，淀粉1茶匙，醋少许，鸡粉1茶匙。');
INSERT INTO `recipe` VALUES (4571, 78, '青椒豇豆', '1.将豇豆洗净，切成3厘米左右的段；青椒去蒂、去籽洗净后切成粗丝。 \r\n2.炒锅置旺火上，将油烧至七成热，放入青椒丝炒出香味，加少许盐炒匀，再倒入豇豆同炒；加入小半杯水，加鸡粉，焖一会儿，用水淀粉勾芡起锅即可。', '色泽翠绿，鲜咸爽口。', NULL, NULL, '豇豆400克，青椒4个，盐、鸡粉、水淀粉适量。');
INSERT INTO `recipe` VALUES (4572, 78, '青蔬烩肉粽', '1.将肉粽2颗打开，放入大碗中，铺平压紧，再放入电锅蒸20分钟：取出后倒扣在盘中（只要在烩前打开即可）。 \r\n2.将材料2准备好，笋丁、胡萝卜丁氽烫熟备用。 \r\n3.将锅预热，放入少许油及蒜末爆香，再放入材料2及调味料1调味，水煮滚后，再将调味料2拌匀，淋入匀芡，即完成烩料；将烩料淋到作法1的肉粽上，即可食用。', '肉鲜能滑口，色泽鲜艳。', NULL, NULL, '肉粽2颗、笋丁1/2杯、胡萝卜1/3杯、山菜适量，调味料蒜末1/2大匙、盐1.5小匙、糖1/3大匙、香油2小匙、水1/3杯、淀粉1大匙、水3大匙。');
INSERT INTO `recipe` VALUES (4573, 78, '薯条手卷', '1.将生菜洗净沥干。 \r\n2.薯条用烤箱烤12-15分钟，至外表略呈金黄色即可，放凉备用；海苔片对角剪开成三角形。 \r\n3.取1张三角形海苔片铺平，先将生菜叶平铺于中间，再依序放入薯条、芦笋、沙拉酱、白芝麻等。包法是从左边卷至右边，要注意必须将中间材料卷紧，海苔边塞入尾端后，再把另一片的海苔从右卷至左，然后放入盘中，全部做好即可食用。手卷内的材料及份量可以自行调整，找出自己最喜欢的手卷口味。', '外脆里嫩，还有营养丰富的海苔，芦笋等。', NULL, NULL, '薯条100克、生菜3片、海苔2片、芦笋15根调味料沙拉酱2匙、白芝麻1大匙。');
INSERT INTO `recipe` VALUES (4574, 78, '桃梨焖牛肉丁', '1.将牛肉洗净切方丁；桃子、生梨除皮去核剖成两爿，番茄洗净切丁，土豆洗净去皮切滚刀块；葱头洗净切末；葡萄干洗净，备用。\r\n2.把锅烧热后倒入食油，待油温6成时，放入葱头未炒至微黄，放入牛肉丁煎至上色，放入番茄丁稍炒，倒入适量牛肉清汤（汤液不宜过多），用大火煮沸后，改用温火焖1小时，放入桃子、生梨、土豆焖熟后，加入精盐、胡椒粉、葡萄干调好口味即可。\r\n3.食用时将牛肉丁盛盘，放上桃子，生梨即可。', '清香适口，别具风味。', NULL, NULL, '牛腿肉750克，桃子3个（约400克），生梨3个（约400克），葡萄干50克，土豆120克，葱头100克，番茄250克。调料：食油100克，精盐、胡椒粉适量。');
INSERT INTO `recipe` VALUES (4575, 78, '鲜菇海鲜粥', '1.粘米和糯米混合后洗净，用热水浸1小时，沥清水分，加调味料腌约15分钟。\r\n2.果皮浸软，洗净。\r\n3.虾仁洗净，用调味料腌片刻。\r\n4.泥艋洗净，用少许胡椒粉调味。\r\n5.米放入一个煲或一个深容器内，加热水1000毫升及果皮，盖上盖，留一开口处疏气，高火煮10分钟，再用中火煮20分钟。\r\n6.冬菇或草菇洗净切粒。\r\n7.把冬菇粒、草菇粒、泥艋和虾仁加入粥内，继续用中火煮10分钟，便可供食。', NULL, NULL, NULL, '粘米2汤匙半，糯米1汤匙半，果皮（小）1片，泥艋150克（约4两），新鲜冬菇或草菇50克（约1两半），虾仁100克（约2两半）。米的调味料：盐半茶匙，油半汤匙。虾的调味料：盐1/8茶匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4576, 78, '香肠烧菜花', '1.把菜花洗净，掰成小块；香肠切片。 \r\n2.炒锅置上火，放油烧热，下葱姜爆香，随后烹入绍酒，加入香肠、菜花、半杯水、鸡粉、盐烧至入味，用水淀粉勾芡，出锅即成。', '味道鲜美，口感润滑。', NULL, NULL, '新鲜菜花400克，熟香肠2根，葱花、姜末、水淀粉、绍酒、盐、鸡粉各适量。');
INSERT INTO `recipe` VALUES (4577, 78, '香菇西兰花', '1.把西兰花洗净，适当切块；用热水把香菇泡软，洗净挤干水分。将西兰花、香菇同时放入沸水中烫一下，马上捞出待用。 \r\n2.锅中再放油烧热，依次放入香菇、西兰花、盐、味精和胡椒粉炒匀，出锅即成。', '味道香美，内含丰富营养。', NULL, NULL, '鲜嫩西兰花500克，香菇8枚，盐、味精、胡椒粉适量。');
INSERT INTO `recipe` VALUES (4578, 78, '蟹粉西兰花', '1.把螃蟹洗净，蒸熟，剔出蟹肉、蟹黄、蟹膏待用。 \r\n2.把西兰花洗净，掰成一朵一朵的，沥干水分；炒锅里倒入3汤匙油，烧热后爆炒西兰花，烹入绍酒，加入适量盐和少许糖，装盘待用。 \r\n3.炒锅放入2汤匙油，烧热后倒入剔出的蟹粉略炒，烹入绍酒，加入盐、糖、姜末、鸡粉和水，用水淀粉勾芡，炒匀后倒在西兰花上即可。', '味道鲜美，口感润滑。', NULL, NULL, '大个螃蟹两只，西兰花400克，姜末2茶匙，盐、绍酒各适量，糖、胡椒粉各少许，鸡粉半茶匙，水150克，水淀粉2汤匙。');
INSERT INTO `recipe` VALUES (4579, 78, '油拌酥瓜条', '1.将南瓜洗净，去皮及籽瓤，切成5厘米长、1厘米宽的条，放在碗内，放入精盐、味精、葱姜末腌制约10分钟。 \r\n2.鸡蛋磕入碗内，加入面粉、淀粉、精盐、味精，搅拌成厚糊。 \r\n3.炒锅上火，舀入花生油烧至五成热，把腌好的南瓜条逐条沾上蛋糊，放入油锅内炸成淡黄色，捞起。待油温烧至七成热时，放入南瓜条炸成金黄色，捞出沥油。炒锅内留少许油上火烧热，投入葱姜未炸出香味，倒入南瓜条拌和均匀，撒进胡椒粉、五香粉装盘即成。', NULL, NULL, NULL, '净南瓜300克，鸡蛋1只，面粉30克，淀粉10克，盐、葱姜、味精、胡椒粉、五香粉各适量。');
INSERT INTO `recipe` VALUES (4580, 78, '油吃麻辣茭白', '1.将茭白削去外皮，切去老跟，洗净后纵切成两半，用刀背稍拍一下，使其质地变松软，切成窄片，放入开水锅中烫一下捞出，拌上盐和味精待用；干辣椒去蒂去籽。 \r\n2.炒锅上火烧热，放入香油，下花椒炸出香味，捞出花椒粒不要，再放入干辣椒炸成棕色，立即离火，加酱油即成调味油。将此调味油倒在茭白上拌匀即可。', '鲜咸适宜，清淡爽口，油而不腻。', NULL, NULL, '嫩茭白500克，干辣椒、花椒粒、盐、酱油，香油、味精适量。');
INSERT INTO `recipe` VALUES (4581, 78, '鱼香苦瓜', '1.将苦瓜去瓜蒂，平剖成两瓣，去瓤后切成细丝，放入开水锅中焯一下，捞出用凉开水浸凉，红辣椒去蒂及籽，切成细丝，也焯一下，浸凉待用。\r\n2.炒锅上火，加入约2汤匙油烧至五成热，下葱丝、姜丝、蒜末炒出香味，再下豆瓣酱煸出红油后加入酱油、白糖、醋炒匀，盛出晾凉制成调味汁。 \r\n3.苦瓜丝、辣椒丝放入盘内，淋上调味汁、香油，拌匀即可。', '香脆清口，鱼香味比较浓。', NULL, NULL, '苦瓜500克，豆瓣酱1汤匙，葱丝、姜丝、蒜末共30克，红辣椒2只，香油、酱油、白糖、醋各适量。');
INSERT INTO `recipe` VALUES (4582, 78, '玉米色拉', '1.将玉米、大米洗净，用淡盐水煮熟冷却，红、青椒洗净切丁。\r\n2.番茄洗净用沸水烫后剥皮切丁。\r\n3.香蕉剥皮切片；熟花生捣碎；芹菜叶洗净切末；把橄榄油、柠檬汁放在一起拌匀成色拉液，备用。\r\n4.将玉米、大米、红、青椒丁、番茄丁、香蕉切片、花生、精盐、胡椒粉放入盆内浇上色拉液拌匀，撒上芹菜末，放进冰箱冷藏1-2小时即可食用。', '酸辣味浓！', NULL, NULL, '新鲜嫩玉米600克，大米250克，新鲜红、青椒各25克，新鲜番茄100克，香蕉100克，熟虾仁200克，熟花生100克，芹菜叶20克。调料：橄榄油75克，柠檬汁30克，精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (4583, 78, '炝油菜', '将油菜去叶根洗净，直刀切成八分长的抹刀片，放在开水中焯熟，捞出控干，拌上精盐盛盘，撒上葱丝、姜末，把椒油加热炝入即可。', '鲜绿脆嫩，宜佐面饭。', NULL, NULL, '鲜油菜二斤，精盐五钱，椒油五钱，姜三片葱一钱。');
INSERT INTO `recipe` VALUES (4584, 78, '蚝油炒草菇', '1.烧热油1汤匙，炒香草菇至熟透，拌入调味料，炒匀。\r\n2.饰以葱丝及红椒即可。', '色泽诱人，口感更为佳。', NULL, NULL, '草菇1罐(约1杯)，葱丝及红椒适量。调味料：李锦记旧庄特级蚝油1汤匙。');
INSERT INTO `recipe` VALUES (4585, 78, '蚝油茭白', '1.将茭白削去外皮，切去老跟，洗净后剖开，斜切成片。 \r\n2.旺火烧约500克油至五成热，放入茭白过油片刻，捞出沥干油待用。 \r\n3.炒锅洗净，置小火上，放入蚝油加热，倒入茭白同炒，烹入绍酒，加入大半杯水，放鸡粉、盐、胡椒粉和少量糖，加盖焖约3分钟，加入水淀粉勾芡，淋香油即可起锅。', '鲜咸适宜，清淡爽口。', NULL, NULL, '嫩茭白500克，蚝油2汤匙，绍酒、盐、糖、胡椒粉、水淀粉、鸡粉各适量。');
INSERT INTO `recipe` VALUES (4586, 50, '鸡蛋花汤', '1.将鸡柳肉去筋砸成泥，用凉汤成糊状后，加入味精、湿淀粉搅匀，再倒入蛋清混合一起。火腿蒸熟剁成末。\r\n2.把汤烧至小开(大开易将糊冲散不成团)，倾入调好的料，轻轻用勺推动不使沾锅，待已熟浮起时用细眼漏勺捞起入碗即成。', '汤清淡，味鲜美，为高级汤菜之一。', NULL, NULL, '母鸡柳肉200克，火腿肉50克，鸡蛋清120克。调料：盐6克，胡椒粉1克，料酒25克，味精5克，湿淀粉少许，凉汤适量。');
INSERT INTO `recipe` VALUES (4587, 50, '家常豆腐汤', '1.将豆腐切成0.5厘米厚、3.5厘米长的片。用沸水焯去生味。 \r\n2.炒锅放旺火上，加入菜油，放入豆腐、笋片、香菇和味精，煮5分种，加鲜汤，煮沸后，加入青蒜，味精，淋入猪油，装盘上桌即可。', NULL, NULL, NULL, '熟笋片75克，豆腐500克，水发香菇、豆腐酱、青蒜、绍酒各少许，姜末1.5克，酱油100克，味精4克，菜油250克，猪油少许，鲜汤1100克。');
INSERT INTO `recipe` VALUES (4588, 50, '肉片粉丝汤', '1.牛肉切薄片，加淀粉、黄酒、精盐和味精拌匀； \r\n2.锅里水滚后，先放牛肉片，盖上锅盖略滚即加入用开水发好的粉丝，盖上锅盖煮5分钟左右，开盖加精盐、味精后再烧沸，盛入汤碗，淋上麻油即可。', NULL, NULL, NULL, '牛肉100克，粉丝80克，虾米25克，精盐、黄酒、淀粉、味精、麻油备适量。');
INSERT INTO `recipe` VALUES (4589, 109, '酱渍萝卜', '    将小萝卜去根须，用清水洗净，晾干，撒上盐拌匀，装入坛内腌制，两天倒翻一次坛，约腌制一星期。\r\n	将腌制好萝卜取出，放在太阳下晒到6－7成干，用清水洗去萝卜上的污物，晾干表面水。\r\n	取锅上火，将腌制萝卜的盐卤倒入锅中烧至8成热时随即倒入萝卜，烧开后离火冷却，用沙布袋把萝卜装在袋中投入酱缸内，每天翻动两次，约一月后即可取出食用。', '酱香浓郁，咸甜爽口，滋味鲜美。', NULL, NULL, '鲜小萝，5000克；食盐，350克；甜面酱，250克；');
INSERT INTO `recipe` VALUES (4590, 109, '酱什锦菜', '    将各种原料洗净，切均匀(花生米、杏仁去皮)，放入盐水缸内浸泡，每天翻动一次，放置阴凉处，约3-4天。\r\n	酱前用清水泡下，约泡3天，每天换两次水，泡好后捞出晾干水分。\r\n    取纱布袋一只，将晾好的原料装入袋中，尔后用黄酱酱第一次(已酱两次黄酱)，一层菜一层酱，10天换一次酱，共3次酱，30天后即可食用。', '色泽鲜艳，清香爽口，营养丰富。', NULL, NULL, '苤蓝，1500克；黄瓜，50克；竹笋，1000克；豆角，500克；豇豆，500克；胡萝卜，500克；花生米，200克；生姜，100克；杏仁，200克；盐，800克；黄酱，4000克；');
INSERT INTO `recipe` VALUES (4591, 109, '酱芹菜', '    选用茎粗质嫩、品质优良的大杆芹菜。削去根须，摘去菜叶，用清水洗净，沥干水分，切成长约12-15厘米的段，放入盆内，倒入沸水略浸烫一下，然后控尽水，晾干。\r\n    取净坛一只，放入面酱、酱油及食盐搅匀(其中酱油须上火烧沸晾凉)，再放入芹菜段，浸入酱汤内，约40-45天后即成。', '色泽橙黄透明，脆嫩无渣，鲜美而回甜，清香宜人，为佐餐之佳品。', NULL, NULL, '鲜芹菜，2000克；稀甜面酱，500克；酱油，500克；精盐，100克；');
INSERT INTO `recipe` VALUES (4592, 109, '面酱嫩黄瓜', '    将嫩黄瓜用清水洗净，沥尽水分，放在一盆内，撒上精盐搅拌均匀，腌半小时后取出沥干盐水待用。\r\n    将锅上火，加入2500克水烧开离火，冷透后与甜面酱一起拌匀，反拌匀的酱汁倒入坛中，再将腌好的黄瓜投入坛中，酱要没过黄瓜，盖好坛盖，封好口，一个月后即可取出食用。', '脆甜鲜嫩，酱香浓郁。', NULL, NULL, '嫩黄瓜，5000克；精盐，500克；甜面酱，1500克；味精，60克；');
INSERT INTO `recipe` VALUES (4593, 109, '酱萝卜条', '　　将大头菜去皮，用清水洗净，沥干水分，改刀切成片待用。\r\n　　反大酱、精盐、糖、花椒、大料、辣椒丝放入锅中烧开，倒入盆中冷透。再把切好的大头菜片倒入坛中，封好口，约2－4天即可取出食用。', '鲜脆质嫩，制作简便，为佐餐之佳品。', NULL, NULL, '白萝卜，5000克；精盐，100克；大酱，1000克；糖，125克；花椒、大料，少许；辣椒丝，25克；');
INSERT INTO `recipe` VALUES (4594, 109, '酱生姜', '　　将嫩生姜刮去外皮，用清水洗净，沥干水分，用刀轻轻拍一下，放入盆中，撒上盐搓一下，腌半天，去掉盐水待用。\r\n　　把酱油、盐、糖、花椒、大料一起放在锅中煮开倒入盆内晾凉。\r\n　　取坛一只，将腌好的洋姜和冷透的汁液加入坛中，封好坛口，时间约一星期后即可取出食用。', '甜脆适口，辣中带咸，可做调味品使用。', NULL, NULL, '嫩生姜，5000克；酱油，1000克；盐，125克；糖，200克；花椒，50克；大料，50克；');
INSERT INTO `recipe` VALUES (4595, 109, '酱八宝菜', '    将嫩黄瓜用清水洗净，沥尽水分，放在一盆内，撒上精盐搅拌均匀，腌半小时后取出沥干盐水待用。\r\n    将锅上火，加入2500克水烧开离火，冷透后与甜面酱一起拌匀，反拌匀的酱汁倒入坛中，再将腌好的黄瓜投入坛中，酱要没过黄瓜，盖好坛盖，封好口，一个月后即可取出食用。', '色泽鲜艳，嫩中有韧，口味多样。', NULL, NULL, '大头菜，1000克；黄瓜，200克；藕，1000克；豆角，100克；萝卜，100克；青椒，80克；花生米，100克；核桃仁，50克；酱油、面酱、精盐各适量；');
INSERT INTO `recipe` VALUES (4596, 109, '美味丝', '　　将大头菜去皮，用清水洗净，沥干水分，改刀切成片待用。\r\n　　反大酱、精盐、糖、花椒、大料、辣椒丝放入锅中烧开，倒入盆中冷透。再把切好的大头菜片倒入坛中，封好口，约2－4天即可取出食用。', '质脆，味鲜，色泽褐红。', NULL, NULL, '白萝卜，5000克；精盐，100克；大酱，1000克；糖，125克；花椒、大料，少许；辣椒丝，25克；');
INSERT INTO `recipe` VALUES (4597, 109, '人参果', '将香菜根摘去细毛、洗净后用开水烫一下待用；姜切成细丝，花椒、精盐、酱油加水烧八成开，晾凉后拌入备好的原料一起，入缸后封好口，10天左右即可食用。', '脆中带有辣味', NULL, NULL, '香菜根，5000克；酱油，2000克；花椒，100克；鲜姜，少许；精盐，1000克');
INSERT INTO `recipe` VALUES (4598, 109, '糖酱洋葱', '（1）将洋葱去根和外层壳皮，用清水洗净，沥尽水分，改刀成滚刀块，放入盆内待用。\r\n（2）把酱油、红塘、盐、花椒、大料等倒入锅中，上火烧开后晾凉；\r\n（3）取净坛一只，将洋葱和兑好的汁液一起搅拌均匀即可装入坛中，封好口，约3-4天即可食用。', '色微红，清脆香甜，开胃增食。', NULL, NULL, '洋葱头，5000克；红塘，300克；姜哟，150克；盐，75克；花椒，少许；大料，少许；');
INSERT INTO `recipe` VALUES (4599, 109, '酱大头菜', '（1）将大头菜去皮，洗净，沥干水分，改刀切成片。\r\n（2）把大酱、精盐、糖、花椒、大料、辣椒丝放入锅中烧开，倒入盆中冷透；再把切好的大头菜片倒入盆中拌和。\r\n（3）取坛一只，将拌和的大头菜片倒入坛中，封好口，约2-4天即可食用。', '清香脆嫩，辣中有甜。', NULL, NULL, '鲜大头菜，5000克；大酱，1000克；精盐，125克；糖，125克；花椒、大料，少许；辣椒丝，25克；');
INSERT INTO `recipe` VALUES (4600, 109, '辣椒酱', '（1）将辣椒去蒂、籽，洗净，放在竹筐中控干水分，辣椒用磨碾碎，加盐拌匀后晒1-2天，花生米、芝麻也一起碾碎，大蒜碾成碎末，再将辣椒、花生、芝麻、大蒜等放在一起拌和，略晒一下加少许香油待用。\r\n（2）取坛一只，将调好的原料装入坛内封好口，10天即可食用，吃时用油炸一下味更佳。', '色泽鲜红，香辣开胃，也是调味品。', NULL, NULL, '鲜红辣椒，5000克；精盐，1200克；花生米，750克；大蒜，500克；熟芝麻，250克；');
INSERT INTO `recipe` VALUES (4601, 109, '酱黄瓜', '（1）将黄瓜洗净，沥干水分，不需改刀，放入盆内，撒上盐拌匀，腌约2小时，取出沥去盐水待用。\r\n（2）锅中加少许水，并把酱油、花椒、大料、糖、味精一起倒入锅中烧开离火晾透。\r\n（3）将腌好的瓜坯和冷透的汁同时加入坛中，盖好盖，24小时即可食用。', '脆嫩香甜，制作简单，是佐餐佳品。', NULL, NULL, '嫩黄瓜，5000克；酱油，250克；精盐，500克；味精，少许；花椒、大料，少许；糖，50克；');
INSERT INTO `recipe` VALUES (4602, 112, '辣黄瓜丝', '（1）将黄瓜去蒂，用水洗净，切成片，再改刀切成丝，放入盆内。\r\n（2）红辣椒去蒂、籽，用水洗净，切成丝，放入黄瓜盆内，撒上盐腌1小时，然后挤去盐水放回盆内，加姜丝、辣油、味精和原料一起拌匀，即可装盘食用。', '色鲜味辣，脆嫩可口，佐酒小菜/', NULL, NULL, '嫩黄瓜，300克；鲜红辣椒，50克；姜丝，10克；辣油，5克；精盐，8克；味精，少许；');
INSERT INTO `recipe` VALUES (4603, 112, '青椒冬瓜片', '将青椒去蒂、籽、瓤，用水洗净，把青椒、冬瓜分别起成片；锅上火加水烧开，投入切好的原料略烫一下随即捞出，用凉开水过后，沥尽水分。将烫好的原料倒入盆中加盐、香油、味精拌匀即可食用。', '色鲜味辣，脆嫩可口。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4604, 112, '双色辣丝', '将青椒去蒂、籽，改刀切成丝，锅上火加水烧沸，把切好的辣椒丝投入沸水中略烫一下随即捞出，放入凉水中过后，沥尽水分。将烫好的辣椒丝倒入盆内，加精盐、味精、香油、蒜泥和辣椒一起拌匀即可食用。', '色鲜味辣，脆嫩可口。', NULL, NULL, '红辣椒，200克；青辣椒，200地；经验，10克；味精，5克；香油，5克；蒜泥，少许；');
INSERT INTO `recipe` VALUES (4605, 112, '辣黄豆', '（1）将黄豆洗净，锅上火加水投入黄豆，加盐，把八角、桂皮、花椒用纱布扎好放入锅中，旺火烧开后用小火慢慢焖煮。\r\n（2）黄豆煮好后，弃取料袋，把豆装入坛内，辣椒剁碎，撒在上面，注入红酒，封好口，腌制10--15天即可食用。', '味鲜辣糯，可为调味料。', NULL, NULL, '黄豆，2000克；精盐，175克；八角，30克；桂皮，25克；辣椒，30克；红酒，125克；');
INSERT INTO `recipe` VALUES (4606, 112, '蛋松', '将鸡蛋打在盆内，用竹筷打匀；取锅上火，倒入植物油烧至8成热，用一漏勺将鸡蛋分数次慢慢淋入油锅中，烧至鸡蛋上浮，成金黄色，捞出沥油，倒入盘中，撒上精盐、味精即可食用。也可用蛋松拼摆冷盘。', '色泽金黄，脆香适口。', NULL, NULL, '鸡蛋，300克；精盐，10克；味精，少许；植物油，250克（约耗35克）；');
INSERT INTO `recipe` VALUES (4607, 112, '芹菜腐竹', '（1）将芹菜去根、叶，用水洗净，放在沸水锅中烫一下，捞出用凉开水过后控干水分，然后切成丝；腐竹也切成丝放入盆内。\r\n（2）将味精、盐、酱油、香油、米醋用碗调好，浇在切好的丝上，拌匀后即可食用。', '味道鲜美，清淡爽口，佐酒小菜。', NULL, NULL, '青菜，300克；水发腐竹，200克；精盐，5克；酱油，10克；香油，15克；味精，少许；米醋，8克；');
INSERT INTO `recipe` VALUES (4608, 112, '拌茭白丝', '（1）将茭白去根，用水洗净，切成片，用开水烫一下，捞出用冷水过凉，将茭白沥干水分，倒入盆内待用。\r\n（2）将锅上火，加入香油烧热，投入花椒炸出香味，把花椒捞出热浇在茭白片上，盖好盖焖一下，然后加精盐、味精搅拌匀，即可食用。', '味鲜清香，质脆色白。', NULL, NULL, '鲜嫩茭白，300克；盐，7克；辣油，10克；味精，3克；');
INSERT INTO `recipe` VALUES (4609, 112, '酸辣土豆丝', '土豆，500克；精盐，10克；辣油，10克；白醋，15克；葱丝、姜丝各少许', '色白质脆，辣中有醋，佐酒小菜。', NULL, NULL, '土豆，500克；精盐，10克；辣油，10克；白醋，15克；葱丝、姜丝各少许');
INSERT INTO `recipe` VALUES (4610, 112, '麻辣包菜', '先将包菜洗净，改刀成丝，沥干水分，放入盆内加盐拌匀，稍放一段时间，再加入花椒粉、辣椒粉拌均匀，入坛24小时左右，即可食用，如装坛前加入适量白酒，味道更佳。', '红色、味麻、脆辣适应一日三餐的开胃小菜。', NULL, NULL, '包菜，5000克；花椒粉，100克；盐，175克；辣椒粉，150克；');
INSERT INTO `recipe` VALUES (4611, 112, '五香蚕豆', '（1）将蚕豆放入清水浸泡2-3小时，捞出用刀在蚕豆上轻轻划一下待用。\r\n（2）锅上火加适量的水，投入蚕豆，将花椒，八角，桂皮，丁香，姜片，盐，味精等加入锅内同蚕豆一起煮烂，捞出放在烤盆中，入烤箱烤至5-6成干去出即可。', '五香味浓郁', NULL, NULL, '蚕豆，500克；精盐，10克；花椒，八角，桂皮，丁香，姜片，味精各少许。');
INSERT INTO `recipe` VALUES (4612, 112, '麻辣茄子', '将茄子洗净去掉根蒂，切成条，丝均可，腌约2小时，挤干水分，加入各种调料拌匀，即可食用。', '酸，辣，甜，麻。', NULL, NULL, '新鲜嫩茄子，5000克；精盐，500克；辣椒面，100克；白糖，150；姜丝，100克；葱，100克；姜丝，100克；');
INSERT INTO `recipe` VALUES (4613, 112, '芥末豆角', '（1）将扁豆摘去两端时，顺势撕下边丝，洗净后沥干水分，放入沸水锅中（沸水中加入少许盐，以保持它的颜色），捞出后控干水分，切成丝，放入盆内。\r\n（2）将生姜去皮上火烧热，倒入香油，待油热后倒入姜丝，炸出香味，捞出姜丝，油倒入丝上，加精盐，辣酱油，白糖，味精拌匀后即可。', '扁豆翠绿，清香爽口，味脆嫩。', NULL, NULL, '嫩豆角，300克；鲜姜，25克；精盐，10克；香油5克；辣酱油，10克；白糖，10克；味精5克。');
INSERT INTO `recipe` VALUES (4614, 112, '拌卷心菜', '（1）将卷心菜去老叶，根，用水洗净，切3厘米的丝长，1.5厘米宽，用开水烫一下，冷水中过凉，捞出控尽水分。\r\n（2）把酱油，精盐，白糖，香油放入碗中调匀，倒入卷心菜中，拌匀，一小时后即可', '脆嫩香甜，营养丰富。', NULL, NULL, '卷心菜，300克；精盐5克；酱油20克；白糖，5克。');
INSERT INTO `recipe` VALUES (4615, 112, '美味豆芽', '（1）将绿豆捏去两头，用清水洗净，放入沸水锅中，取出用凉水过一下，把水沥干水分。\r\n（2）青红辣椒去蒂，籽，洗净，放沸水锅中烫一下，再用凉水过一下，沥干水分。\r\n（3）将豆芽，青红辣椒丝倒入盆中，加精盐，大蒜，姜丝，香菇丝，香油等拌匀即可。', '色泽鲜艳，脆嫩爽口。', NULL, NULL, '绿豆芽500克；青椒，100克；红辣椒80克；精盐，10克；大蒜末，姜丝适量；香菇，10克；');
INSERT INTO `recipe` VALUES (4616, 112, '拌苦瓜', '（1）将苦瓜切成两半，去籽，用水洗净，切成0.3厘米宽的条，在沸水中烫一下，捞出再沥干。\r\n（2）将烫好的苦瓜倒入盆中，撒上盐腌1小时，然后用清水过干，捞出沥干水分，加辣椒油，酱油，豆瓣酱，味精一起拌合均匀即可。', '清凉香辣，微带苦味，防暑加品。', NULL, NULL, '苦瓜，500克；辣椒油，10克；酱油，10克；豆瓣酱15克；精盐5克；味精5克；');
INSERT INTO `recipe` VALUES (4617, 112, '辣拌空心菜', '（1）将空心菜去根、叶留杆，洗净控干水分，改刀成1.5厘米长的段，用5克盐腌约2小时后，沥去水分。\r\n（2）将红辣椒去蒂、籽、洗净，改刀切成粗丝，用沸水略烫捞出，放在冷水中过一下，捞出控干水分。\r\n（3）取盆一只把空心菜杆、红辣椒丝、精盐、葱、姜丝、花椒油、味精一起搅拌均匀即可食用。', '色泽鲜艳、脆嫩适口、微带辣味、营养丰富。', NULL, NULL, '嫩空心菜，300克；鲜红辣椒，150克；精盐，10克；葱，5克；生姜丝，10克；味精，5克；');
INSERT INTO `recipe` VALUES (4618, 112, '麻辣金珍蘑', '（1）将金珍蘑去根洗净，放在沸水中烫一下，放在盆内摊开晾凉，待冷却后挤去水分，待用。\r\n（2）取炒锅上火，加入花椒油烧热，放辣椒酱炒出辣香味离火，倒在金珍蘑上，加精盐、糖、味精、即可食用。', '麻辣味浓郁、开胃、能用于高档宴席。', NULL, NULL, '新鲜金珍蘑，500克；花椒油，10克；辣椒酱，10克；糖，5克；精盐，少许；味精，5克；');
INSERT INTO `recipe` VALUES (4619, 112, '五香海带丝', '（1）将海带用冷水涨发好，洗净，去根，改刀成丝待用。\r\n（2）锅上火加水烧开，投入海带丝煮约半小时捞出，再用清水洗净后晒干，将晒干的海带丝和调料拌匀，取坛装入海带丝，封好口，食用时用开水浸泡几分钟即成汤菜，也可凉拌。', '美味可口，营养丰富。', NULL, NULL, '海带，5000克；精盐，450克；酱油，800克；五香粉，150克；白糖，200克；酒，50克；生姜丝，20克；味精，40克；');
INSERT INTO `recipe` VALUES (4620, 112, '麻辣海白菜', '（1）将海白菜洗净，改刀切成丝，放在沸水中略烫一下，捞出控干水分倒入盆内，加精盐、糖、醋、生姜丝待用。\r\n（2）锅上火倒入香油，烧到6-7成热时投入花椒炸出香味离火，将花椒捞出，趁热将油浇在海白菜上，加味精调拌均匀即可食用。', '味道鲜香脆嫩，麻辣味浓郁。', NULL, NULL, '海白菜，500克；精盐，10克；糖，5克；醋，5克；辣香油，50克；花椒，10克；生姜丝，5克；味精，少许；');
INSERT INTO `recipe` VALUES (4621, 112, '姜汁菠菜', '（1）将菠菜去根、黄叶，用清水洗净，放在沸水锅中略烫一下，放在冷水中过凉，捞出控干水分，切成两段，放在盆中待用。\r\n（2）将生姜去皮，用清水洗净，切成丝后再剁成细末，放在菠菜上。\r\n（3）将精盐、酱油、醋、香油和菠菜一起搅拌均匀即可食用。', '鲜香清淡、姜味浓厚、营养丰富、制作简便。', NULL, NULL, '鲜嫩菠菜，500克；生姜，50克；精盐，5克；酱油，10克；醋，25克；味精，少许；香油，10克；');
INSERT INTO `recipe` VALUES (4622, 110, '泡酸白菜', '（1）去半心白菜，太阳下晒3-4天，去掉表层叶，洗净，沥干水分。\r\n（2）将大白菜剖开，沸水中略烫一下，而后在冷水中过凉，沥干水分。\r\n（3）将白菜一层层码在缸中，每层加少许盐，码好后上放重物，然后注入开水，淹没白菜，放置阴凉处，约20天待发酸时即可。', '清脆爽口，略带酸味，吃时可调味。', NULL, NULL, '大白菜，5000克；精盐50克；白醋，200克；干辣椒丝，10克。');
INSERT INTO `recipe` VALUES (4623, 110, '泡豆角', '（1）将豆角洗净，去两头；大蒜去皮切片；辣椒切成丝；生姜去皮切成片，后改刀成丝。\r\n（2）将豆角放入坛中，把盐，大蒜，辣椒丝，生姜丝放在豆角上，然后锅上加500克左右的水烧开，倒入盆中晾凉，在加在坛中，盖好坛口，5天后即可。', '色泽鲜绿，芳香味浓，具有四川风味。', NULL, NULL, '豆角，500克；食盐，50克；大蒜头，75克；辣椒，25克；白酒，40克；生姜；');
INSERT INTO `recipe` VALUES (4624, 110, '泡豇豆', '（1）将豇豆去尖，洗净，放在通风出晒干表面水分。\r\n（2）将盐水烧开，加入食盐，干红辣椒丝，白酒红塘等搅拌均匀，倒入盆内晾凉。\r\n（3）将豇豆角放入坛内，注入晾凉的盐水汁液和香料包，盖上坛口，5天后即可。', '色泽清黄透亮，鲜香咸辣爽口。', NULL, NULL, '豇豆，5000克；食盐，300克；干红辣椒，100克；白酒，50克；红塘，50克；新老混合盐水，500克；香料包一个。');
INSERT INTO `recipe` VALUES (4625, 110, '泡菜瓜', '将菜瓜去蒂，用凉水洗净剖开，去籽，放在太阳下晾至5成干待用。\r\n（2）将红辣椒切成丝；大蒜去皮切片；生姜去皮切成丝；葱切段。将菜瓜放入坛内。将盐水烧开冷透加入辣椒丝，大蒜等调料后注入坛内，盖好坛口，一个月后即可。', '色泽透明，脆香爽口，别有风味。', NULL, NULL, '菜瓜，5000克；盐水，5000克；红辣椒，75克；大蒜，50克；生姜，50克；葱白，25克；花椒，10克；八角10克；');
INSERT INTO `recipe` VALUES (4626, 110, '辣椒什锦菜', '将辣椒洗净切成片，去籽，萝卜，红萝卜，芥兰头去皮，切成薄片，一起用盐4小时，放入白醋，装入缸，7天后可食用。密封存放。', '味咸酸，快胃。', NULL, NULL, '辣椒，5000克；萝卜，8000克；红萝卜，8000克；芥兰头，8000克；盐，5000克；白醋，3000克。');
INSERT INTO `recipe` VALUES (4627, 110, '水咸芥菜', '芥菜洗净，晒至半干，装入缸泡7天即可，密封存放。', '味咸，清脆。', NULL, NULL, '芥菜，5000克；粗盐，750克；水，2000克；生姜，100克；');
INSERT INTO `recipe` VALUES (4628, 110, '木瓜什锦菜', '成熟木瓜，大嫩萝卜，红萝卜洗净去皮，切成丝拌糖精容于冷开水中，装缸浸泡7天即可。密封存放。', '清甜。', NULL, NULL, '木瓜，5000克；糖，750克；白糖，1500克；水，500克；');
INSERT INTO `recipe` VALUES (4629, 110, '盐水玉米笋', '将鲜嫩玉米笋去壳，须，用开水烫熟，捞出晾冷，不需改刀。\r\n（2）取盆一只，加入精盐，倒入冷开水，用竹筷在盆中搅动，待盐融化，把玉米笋倒入泡约2小时取出，加味精拌匀即可。', '嫩鲜脆，色泽浩白，清爽可口。', NULL, NULL, '鲜嫩玉米笋，3000克；冷开水，5000克；精盐，750克；味精，50克。');
INSERT INTO `recipe` VALUES (4630, 110, '蒜瓜', '（1）小黄瓜洗净沥干。去少量明矾石灰，加水融化后取其澄清液，入锅上火烧沸，投入小黄瓜略焯即倒出，控干水分，防一盆内，加盐15克，腌一天。\r\n（2）第二天，加入15克盐，蒜瓣捣烂再加入瓜内拌匀，连菜带卤水一同倒入坛内。\r\n（3）醋入锅内熬开，加入白酒，然后晾凉倒入瓜坛，浸没黄瓜。放置阴凉处，2-3天即可', '脆嫩爽口，香酒味蒜香味。', NULL, NULL, '秋季小黄瓜，500克；精盐，30克；打算瓣，90克；白酒，40克；醋，500克；明矾石灰少许。');
INSERT INTO `recipe` VALUES (4631, 110, '朝鲜泡菜', '（1）将白菜去根和老帮后，洗净，沥干水分，改刀切成四瓣，放入盆内，撒上盐腌4-5小时；萝卜去根、须、皮，切成薄片，用盐腌一下；苹果去皮、切成片；葱切碎；蒜捣成泥。\r\n（2）将腌渍好的白菜、萝卜沥去腌水，装入坛内，再把苹果、梨、牛肉汤等所有调料总在一起浇在白菜上，卤汁要淹没白菜，用一干净重物压紧，使菜下沉。时间可根据季节而定，夏天一般1-2天；冬天一般为3-4天即可食用。', '色泽鲜艳，酸辣脆嫩，清爽可口，具有浓厚的地方特色。', NULL, NULL, '大白菜，5000克；苹果，250克；梨，250克；白萝卜，500克；牛肉清汤，1500克；葱，250克；大蒜，250克；精盐，150克；辣椒面，150克；味精，50克；');
INSERT INTO `recipe` VALUES (4632, 111, '糖醋三丝', '（1）将白菜心洗净，切成丝，用精盐拌匀一下；鸭梨去皮核，切成和白菜相似的丝；山楂糕切成粗丝待用。\r\n（2）用手轻轻挤去白菜心的水分，放入盘内将利丝码在白菜丝上，最上边放上山楂糕丝。\r\n（3）白糖和醋加清水少许上火熬化，晾凉后浇在三丝上即成。', '白泽美观 甜酸可口 清爽开胃。', NULL, NULL, '白菜心，200克；鸭梨，300克；山楂糕，50克； 白糖， 75克  米醋，   40克； 精盐, 5克。');
INSERT INTO `recipe` VALUES (4633, 111, '糖醋白菜', '对大白菜进行顺丝改刀，改过后的大白菜先用盐脱水，20分钟后挤干待用；红辣椒丝作配色之用，放油锅一下一起放进盘中，再放入糖，醋等调料。一小时后就可是用。\r\n。', '酸甜爽口。', NULL, NULL, '大白菜 ，5000克；糖，750克；醋，400克；精盐 250克。');
INSERT INTO `recipe` VALUES (4634, 111, '糖醋黄瓜', '将黄瓜洗净沥干，然后改成兰花刀，再用盐淹一下，挤去水。葱，姜改刀成丝和改刀后的黄瓜一起放进盆中，放入调料，2小时后即可食用。', '脆爽酸甜，增加食欲', NULL, NULL, '黄瓜，5000克；盐，100克；糖 250克；醋 200克。');
INSERT INTO `recipe` VALUES (4635, 111, '蜜酱胡萝卜', '（1）将胡萝卜去根须，洗净，切成小块待用。\r\n（2）用锅上火加水，酱油，醋烧开，放胡萝卜煮5-10分，待冷后倒入容器内。\r\n（3）将红塘，蜂蜜分别倒入容器内，搅拌均匀，封口，放置阴凉处，一月后即可食用。', '色泽鲜艳，香甜适口，属维族风味。', NULL, NULL, '胡萝卜，5000克；红塘，500克；蜂蜜，250克；酱油，300克；醋，50克；水，4000克。');
INSERT INTO `recipe` VALUES (4636, 111, '蜜汁西红柿', '将西红柿洗净，放入盆内，倒入沸水烫2分钟，捞出用凉水过凉，撕去皮切开两半，去把，每半改成三份的瓜楞块，去籽待用。\r\n（2）蜂蜜放入碗内，冲入沸水调稀，放入西红柿块轻巧拌匀，取出沥去多余的蜜汁。将蜜玫瑰撒于面上，即可上桌食用。', '色泽红艳，清香细腻，甜蜜可口。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4637, 111, '蜜汁罗卜头', '将大蒜去掉表面老皮，洗净，取盆一只。用盐将大蒜拌和，淹约2-3天，淹后的盐水倒掉，再加，醋拌和，取坛一只将拌和好的大蒜入坛，封好口，1个月左右即可使用。', '酸，甜，脆，增加食欲。', NULL, NULL, '鲜罗卜头，5000克；白糖，750克；甜面酱，4000克；精盐，500克');
INSERT INTO `recipe` VALUES (4638, 111, '糖醋芹菜', '将芹菜叶去掉，洗净控干水分，切成3厘米的段，取坛一只注入2500克冷水分，\r\n然后加入白糖，醋和盐加入芹菜段拌匀封口，半月后使用。', '色呈淡黄，甜酸适口，略带咸味。', NULL, NULL, '芹菜，5000克；白糖，500克；醋，500，克；精盐，350克。');
INSERT INTO `recipe` VALUES (4639, 111, '香辣白菜', '（1）取白菜帮，洗净控干水分，改刀成条，葱姜切成丝。\r\n（2）将切好的白菜放入盆内撒上盐拌匀，腌约3小时，取出沥去水分放入盆内。\r\n（3）将香油放入锅内，上火烧热后加入辣椒丝，炸出辣味即放入葱姜炒一下，加入醋糖和少许水，开锅后倒入盆中冷透，而后把汁浇在白菜上，腌4-6小时即可食用。', '香 辣 脆 甜 清爽适口。', NULL, NULL, '原料：大白菜，1000克；香油，15克；干辣椒，10克；精盐，10克；白糖，75克；醋，25克；葱10克；姜，10克。');
INSERT INTO `recipe` VALUES (4640, 111, '木瓜银丝', '（1）将木瓜，胡萝卜分别用清水洗净，晾干表面后，装入坛内，用30%的盐水腌，约3个月。\r\n（2）将腌泡好的木瓜，胡罗卜捞出，改刀成丝，把切好的丝放入冷水冲泡一下，再捞出压干水分，拌上糖，带糖融化后即可食用。', '咸中带甜，脆嫩爽口，营养丰富。', NULL, NULL, '木瓜，2500克；胡萝卜，2500克；食盐，300克；白糖，1000克。');
INSERT INTO `recipe` VALUES (4641, 111, '香辣白菜', '制作方法：将大白菜的老帮，根去掉，洗净，改刀成两半，再切成1.5厘米宽的条。\r\n（2）把切好的白菜放入盆内，撒上盐腌2-3小时，在将白菜中的水分挤掉，摆入盆内。\r\n（3）将干辣椒，葱白，生姜分别切成细丝。去火倒入香油烧热，投入辣椒，炸出辣香味放入葱姜炒出香味，倒入醋，糖，晾凉，把凉好的汁浇在白菜上，腌4-5小时即可。', '色泽鲜艳，清爽适口。', NULL, NULL, '原料：大白菜，5000克；精盐，50克；白糖，500克；醋，150克；香油，100克；干辣椒，100克；葱白50克；姜50克；');
INSERT INTO `recipe` VALUES (4642, 111, '糖醋三片', '制作方法：（1）将茄子洗净去皮，切成厚片，放锅内蒸熟。\r\n（2）将土豆去皮，放入锅内煮熟，切成片。\r\n（3）将苹果去皮，核洗净，切成薄片。\r\n（4）将茄子，土豆片，苹果片放入盆内，加入精盐，白糖，白醋拌匀，淋上香油即可。', '酸甜适口，口味鲜甜', NULL, NULL, '原料：茄子，2000克；苹果，1000克；土豆，1500克；精盐250克；白醋1000克；白糖1000克；香油味精少许。');
INSERT INTO `recipe` VALUES (4643, 111, '糖醋大蒜头', '制作方法：将大蒜去皮，洗净，去盆一只。用盐将大蒜拌匀，腌2-3天，在倒掉水，加糖，醋拌匀，将蒜放入坛内，封好口，一个月左右即可。', '酸，甜，脆，增加食欲。', NULL, NULL, '原料：新鲜大蒜，5000克；精盐，500克；白糖，500克；醋，500克；');
INSERT INTO `recipe` VALUES (4644, 111, '蜜姜黄瓜', '将姜片去皮，洗净，改刀切成丝，黄瓜去蒂洗净切成半寸长的段，然后改刀切成条，把切好的姜丝黄瓜条拌匀，入坛，注入蜂蜜，盖好坛盖，2天后即可。', '舔中带辣，脆嫩爽口。', NULL, NULL, '嫩姜，5000克；嫩黄瓜，5000克；蜂蜜，4000克。');
INSERT INTO `recipe` VALUES (4645, 111, '蜜汁冬瓜', '将冬瓜去皮，籽，先切成1.5厘米的段，再改刀成条；黄瓜去蒂，籽，切成与冬瓜相同的条；冬菇去根也切成条。加水烧沸，把切好的原料投入沸水锅中略烫一下捞出，沥干。把原料投入坛中，然后注入蜂蜜，盖好，3-5天即可。', '色泽脆嫩，蜜香味浓。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4646, 111, '糖醋藕丝', '藕洗净，去皮，切成薄片改刀成粗丝。取半盆清水，加少许醋，调匀，把藕丝放入泡30分钟，捞出放沸水中略烫，再放凉水中过凉，沥干水分。将藕丝放入盆内，加盐，糖，醋等拌匀即可。\r\n特点：色泽洁白，脆嫩，甜酸爽口。', '色泽洁白，脆嫩，甜酸爽口。', NULL, NULL, '鲜藕，500克；白糖，100克；白醋，100克；精盐，15克；');
INSERT INTO `recipe` VALUES (4647, 108, '腌五香大头菜', '　　将大头菜去根须洗净，切成佛手形状，用盐腌7天，取出晒6－7成干，用手搓一下，搓出水来，再下缸腌3天后，取出晒到6成干，加五香粉拌匀，装入坛内封好口，一月后即可食用。\r\n　　注意：存放时间以较久为好。', '五香味浓，咸甜爽口，是早餐的理想佳品。', NULL, NULL, '大头菜，5000克；精盐，750克；五香粉，100克；');
INSERT INTO `recipe` VALUES (4648, 108, '腌香椿', '　　取香椿树嫩尖，整小船坞干净，沥干水分。然后用精盐搓揉，装入干净的坛中压紧。几天以后可取出改刀食用。\r\n　　食用时可拌上麻油、辣椒、味精、味道别致。\r\n　　注意：此菜食用于清明前后。', '鲜香适口，时令小菜，若配炒鸡蛋，辣椒风味独特。', NULL, NULL, '香椿，5000克；精盐，250克；');
INSERT INTO `recipe` VALUES (4649, 108, '腌酱黄瓜', '　　将黄瓜洗净晾干，用盐腌2－3天取出，倒去盐水。将黄瓜再放入盆内，投入各种调料拌和，盆口压紧，约在15天后即可食用。', '香脆爽口，开胃。', NULL, NULL, '黄瓜，5000克；大料，少许；大蒜，200克；姜，100克；精盐，700克；酱油，3000克；');
INSERT INTO `recipe` VALUES (4650, 108, '多味萝卜块', '　　将萝卜去缨蒂、根须，用水洗净，改切成小块，放入盆中，撒上盐拌匀，腌约一星期后，去掉腌水，拌上姜粉、五香粉、辣椒粉和哑，取坛一只，将拌好的萝卜块装入坛内压紧，也可注入些酱油，封好口约一星期后即可取出食用。', '脆嫩适口，香辣味浓。', NULL, NULL, '白萝卜，5000克；盐，750克；姜粉，50克；五香粉，50克；辣椒粉，30克；味精，10克；');
INSERT INTO `recipe` VALUES (4651, 108, '五香什锦丁', '　　将萝卜去掉缨和根须，用水洗净，控干水分，切成丁；豆腐干切成同世界上相似的丁。\r\n　　黄豆、花生米用水泡开，放在锅中煮烂，捞出控干水分。\r\n　　用盆将各种原料混合拌匀，再投入各种调料拌均匀后，装入坛内密封好，约１月左右即可取出食用。', '色鲜味香，别具风味。', NULL, NULL, '胡萝卜，3000克；白萝卜，3000克；花生米，1000克；黄豆，2000克；香干，1000克；盐，450克；八角，50克；酱油，150克；生姜，50克；');
INSERT INTO `recipe` VALUES (4652, 108, '八味菜', '　　将青椒蒂和籽去除，大头菜去根须洗净，控干水分，分别切成丁，黄豆放在锅中炒熟；然后将原料放入盆内，加糖、醋、盐、酱油、香油等拌匀，装入坛中封好口，约半月后即可食用。', '脆香爽口，略带辣味。', NULL, NULL, '青椒，1000克；黄豆，1000克；大头菜，3000克；糖，250克；醋，250克；盐，250克；酱油，250克；香油，50克；');
INSERT INTO `recipe` VALUES (4653, 108, '腌苤蓝', '    选用中等苤蓝，去皮，用水洗净，晾干表面水分，然后一层苤蓝一层盐装入缸内，装好后注入水，每天倒缸一次，连续一星期，一周后每隔两天倒一次，一月后即可封好缸口贮存。', '色泽黄白透亮，味咸脆嫩。', NULL, NULL, '苤蓝，5000克；盐，900克；水，1000克；');
INSERT INTO `recipe` VALUES (4654, 108, '腌黄瓜', '    将黄瓜去蒂、籽，用水洗净，放在竹席上晾2天，倒入缸内撒上盐拌匀腌7-10天，每天倒缸一次，10天之后换去原水，然后加入各种调料，装入坛内注入酱油，约15天左右即可食用。', '清脆爽口，北方风味。', NULL, NULL, '黄瓜，5000克；酱油，250克；水，2500克；八角，50克；盐，1000克；花椒，50克；');
INSERT INTO `recipe` VALUES (4655, 108, '风味白菜', '    将大白菜叶稍和老帮去除，用水洗净，控干水分，切成菱形状，倒入盆内，撒上少许盐腌渍一下后沥去腌水。然后将各种调味料放置一容器内捣烂后和白菜拌匀，再将拌匀的白菜装入缸内，压紧封好口，约一星期后即可取出食用。', '香味浓郁，味美可口，质脆，适口性好。', NULL, NULL, '大白菜，5000克；盐，250克；糖，250克；苹果，250克；梨，250克；蒜，50克；葱，100克；花椒，25克；味精，10克；');
INSERT INTO `recipe` VALUES (4656, 108, '腌高根白菜', '　　1.将白菜去掉老叶、黄叶洗净，晾晒到7-8成干装坛，一层菜加少许盐码好，顶上用干净的石块压紧，倒入凉开水淹没菜即可，封口腌一个月即可食用。\r\n　　2.食用时挤去水可进行改刀，切成丝，同干辣椒或肉丝炒后食用。　', '味香爽口，是一种既是小菜又是佳肴的春季食品。', NULL, NULL, '高根白菜，5000克；精盐，750克；干辣椒，少许；');
INSERT INTO `recipe` VALUES (4657, 108, '腌芫荽', '（1）将黄豆用热水泡发，入沸水煮熟，捞出控干。\r\n（2）芫荽去掉根，摘洗干净，控干水分，切成1.5厘米长的小段，然后与黄豆、盐搅拌均匀，放入缸内按实。腌制2天后即可食用。', '此菜绿中带黄，香味浓，食时拌以香油，非常适口。', NULL, NULL, '芫荽（香菜），1000克；黄豆，150克；精盐，20；');
INSERT INTO `recipe` VALUES (4658, 108, '腌山药', '将山药洗净放入坛中用盐腌渍，并将花椒、大料用纱布包好放在山药中间。一个月后取出，切成树心花，再放入干净坛中注入酱油和冷开水，泡制2天即可食用。', '香、脆可口。', NULL, NULL, '山药，5000克；大料，25克；花椒，10克；盐，500克；酱油，1000克；水，500克；');
INSERT INTO `recipe` VALUES (4659, 108, '佛手青椒', '将青椒蒂、籽去掉，先切成两瓣，再改刀成佛手状，用盐搓匀装在盆中腌约3小时即可食用，食用时可加糖、醋、香油口味更佳。', '香、脆、辣。', NULL, NULL, '青龙灯椒，5000克；精盐，500；白糖，1500克；');
INSERT INTO `recipe` VALUES (4660, 108, '腌韭菜', '（1）选用霜降前收获的宽韭菜，去黄稍，摘拣后洗净，控干水分，切成约5-6厘米长的段。\r\n（2）取一净瓷盆，铺一层韭菜，撒一层精盐，将韭菜和盐装完。腌1-2日后，（此期间可翻倒数次）装入一干净的坛。将剩余盐卤加少许香油倒入坛内腌制，数日后即可食用。\r\n（3）坛内亦可加腌小黄瓜、小茄子，用原盐卤拌匀收贮即可。', '韭菜食之辛甘鲜咸香，具有温中运气、开胃之功效。', NULL, NULL, '鲜韭菜，2500克；精盐，250克；香油，少许；');
INSERT INTO `recipe` VALUES (4661, 108, '腌雪里蕻', '将雪里蕻的黄叶除去，洗净晾干表面水分，一层菜加一层盐放入小盆内，隔两天翻倒一次，5天后用重物压紧，封罐贮存即可。', '脆、咸清香。', NULL, NULL, '雪里蕻，5000克；食盐，500克；水，1000-1200克；');
INSERT INTO `recipe` VALUES (4662, 109, '酱醋蒜', '将鲜蒜头去掉外层表皮和根须，放入盆内撒上盐拌匀腌1小时后加入清水浸泡2－3天，每天换一次水，泡好后取出沥干水分。\r\n将泡制好的蒜头和酱油、醋拌匀（酱油需烧沸冷透）。\r\n将拌匀的蒜头一起投入坛中，每天翻动一次，一星期后封好坛口，一月后即可取出食用', '酸甜脆辣，是具有独特的北方风味小菜。', NULL, NULL, '鲜嫩蒜头5000克；酱油2500克；醋1200克；盐500克；');
INSERT INTO `recipe` VALUES (4663, 109, '酱莴笋', '将莴笋去皮，用清水洗净，改兰花刀，放入一盆中，撒上盐拌匀腌半小时，将腌水挤掉待用。\r\n把原料倒回盆中，上面加酱油、精盐、醋、糖、辣椒丝（酱油要烧开凉透）和原料一起拌匀，1小时后可食用，此菜存放时间不宜过长。', '甜醋微辣，香脆适口。', NULL, NULL, '鲜莴笋5000克；\r\n酱油500克；\r\n精盐175克；\r\n醋175克；\r\n糖175克；\r\n辣椒丝少许；');
INSERT INTO `recipe` VALUES (4664, 451, '四川泡菜', '将调料放入水中烧开，倒入盆内冷透，再装入泡坛中浸泡5－7天左右即可食用。', '脆、辣、酸、制作简便。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4665, 451, '广东泡菜', '将原料及配料用精盐拌和均匀，腌渍约1个半小时后用清水漂，盛在盆内，加入白糖、醋、水（水需烧开冷透后使用）浇在主料中搅拌均匀。然后装入泡菜坛内，约一天后即可取出食用。\r\n注：使用调料的数量，要根据原料的性质和数量而定。', '酸、甜、脆、略带姜辣味，色泽鲜艳，营养丰富。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4666, 451, '太原泡菜', '将5000克水烧开，加入精盐250克（比例为5%），待盐完全溶化、晾凉，再倒入坛内。\r\n白菜需去根和老叶，洗净，沥干水分；萝卜去根须，切条、丁、丝、片均可；黄瓜去籽；辣椒去蒂、籽洗净沥干水分。\r\n将泡料投入泡坛中，加酒拌匀封好口，浸泡一星期左右即可食用。', '脆嫩爽口，制作简便，使用方便。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4667, 451, '北京泡菜', '将配料投入适量的水烧开，倒入盆中冷透。泡菜坛要用开水洗涮，晾干，切忌沾上生水。将晾好的配料及水倒入坛中，盖好坛盖，约2天后即可食用。\r\n吃时可放少许香油、香菜进行调制。各种蔬菜均可采用此法泡制。\r\n麻香味浓，鲜辣脆嫩，取料广泛。', '麻香味浓，鲜辣脆嫩，取料广泛。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4668, 451, '西式泡菜', '将泡料洗净，沥干水分，切成大小不等的薄片，放入沸水锅中略烫捞出用冷水过凉，放置竹筐内沥干水分。锅上火加水和各种调味品一起烧开冷透；再将原料和汁液一并入坛泡制7天左右即可食用。', '脆、辣、香、甜、酸，清爽可口。', NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4669, 112, '果味瓜条', '    将冬瓜切成条，用开水氽一下，再用凉开水凉透。\r\n    采果汁、澄汁各2两，白糖3两，白醋3两，食用色素少许。\r\n    用凉开水1斤，放入配料，透泡3小时即可食用。', '清凉爽口。', NULL, NULL, '主料：冬瓜\r\n配料：果汁、澄汁\r\n调料：绵白糖、白醋、食用色素');
INSERT INTO `recipe` VALUES (4670, 50, '开水白菜', '肥母鸡洗净，和猪排，火腿下砂锅，加水烧开后除去浮沫，用文火熬成清汤；白菜心去帮留心，在根部切十字刀，用开水略烫；置于碗中，注入清汤上笼蒸透即成。', NULL, NULL, NULL, '白菜心750克、肥母鸡一只、猪排1000克、火腿适量');
INSERT INTO `recipe` VALUES (4671, 50, '箩粉鱼头豆腐汤', '先将鱼头炸一下；再加少许豆瓣、葱姜、蒜末，加汤烧开后捞去渣，放香菇、冬笋片、豆腐、酱油、盐、味精、胡椒、料酒、鱼头熟后放粉皮，汤开后勾芡撒青蒜即成。', NULL, NULL, NULL, '鱼头2个、香菇冬笋各50克、豆腐100克');
INSERT INTO `recipe` VALUES (4672, 50, '清汤萝卜燕', '将萝卜洗净去皮,切成长片,再切5刀相连的连刀片,放水中漂透,沥水后拍干淀粉抖散,上笼蒸熟,放水中漂透,再放清水中泡.素清汤加盐烧开,下泡好萝卜片略煮,捞出入汤盆中.素清汤烧开加盐,胡椒粉,味精,打去浮沫,浇在汤盆中撒香菜即成.', NULL, NULL, NULL, '象牙白萝卜约1000克、香菜叶少许');
INSERT INTO `recipe` VALUES (4673, 50, '清蒸冬瓜盅', '冬菇、蘑菇洗净，冬笋去皮，各切碎末；下6成热油中煸炒，再加料酒、酱油、白糖、味精、冬菇汤，烧开后勾厚芡，冷后成馅；将冬瓜选肉厚处用圆槽刀捅出14个圆柱形，皮不去掉，刻上花纹及文字后片下瓜皮，焯水后抹香油待用；冬瓜柱掏空填上馅，放盘中，上笼蒸10分钟取出装盘，并饰以刻好的瓜皮，盘中汤汁烧开调好味后勾芡，浇在冬瓜盅上即可。', NULL, NULL, NULL, '绿皮冬瓜500克、熟冬笋100克、水发冬菇100克、蘑菇100克');
INSERT INTO `recipe` VALUES (4674, 50, '吉祥干贝', '水发干贝用毛汤汆后撕成丝;鸡里脊去筋制茸,加蛋清,三套汤,湿淀粉,精盐,料酒调成鸡汁;猪大油在炒勺内烧至4成热时,慢慢倒入鸡汁,边倒边搅,用慢火炒熟;一半盛入汤盘,一半在勺内与干贝搅匀后盛入汤盘,撒上眉毛蒜即成.', NULL, NULL, NULL, '水发干贝100克、鸡里脊50克、眉毛蒜1克、三套汤100克、鸡蛋清4个');
INSERT INTO `recipe` VALUES (4675, 50, '砂锅豆腐', '豆腐切块焯水后捞出，粉丝、腐竹泡发，胡萝卜切块；砂锅内放粉丝、腐竹、胡萝卜，在放入豆腐，加鸡清汤，调好味后大火烧开，小火煨至入味，淋香油即成。', NULL, NULL, NULL, '嫩豆腐150克、胡萝卜50克、菜心100克、腐竹、水发粉丝100克');
INSERT INTO `recipe` VALUES (4676, 50, '家常豆腐汤', '1 将豆腐切成0.5厘米厚、3.5厘米长的片。用沸水焯去生味； \r\n\r\n　　2 炒锅放旺火上，加入菜油，放入豆腐、笋片、香菇和味精，煮５分种，加鲜汤， 煮沸后，加入青蒜，味精，淋入猪油，装盘上桌即可', NULL, NULL, NULL, '熟笋片75克 豆腐500克 水发香菇、豆腐酱、青蒜、绍酒各少许 姜末1.5克 酱油100克 味精4克 菜油250克,猪油少许 鲜汤1100克。');
INSERT INTO `recipe` VALUES (4677, 50, '砂锅鱼翅', '火腿、玉兰片切片，香菇撕小块。鱼翅放凉水中用微火烧开捞入碗中。砂锅内油8成热时下葱丝、姜末，再放鸡汤及调料 \r\n\r\n　　，烧开后倒入盛鱼翅的碗中，用旺火蒸至8成烂。用葱丝、姜末呛锅，放清汤、调料和鱼翅，烧开后 倒入砂锅，移微火炖20分钟，放油菜心上即可。', NULL, NULL, NULL, '水发鱼翅500克、火腿25克、水发玉兰片20克、水发香菇20克、油菜心15克、鸡汤1000克、清汤150克');
INSERT INTO `recipe` VALUES (4678, 50, '砂锅鱼头', '①将鲢鱼头除鳃洗净，剞刀，涂上豆瓣酱，用酱油腌入味。 \r\n②在八成热的油中，将鱼头煎黄 入调味料，焖烧至将熟，放入笋片、香菇和味精，开锅后倒入砂锅内煨煮几分钟加入青蒜段及猪油，即可。', NULL, NULL, NULL, '鲜鲢鱼头1个，熟竹笋片50克，水发香菇、豆瓣酱、青蒜、绍酒各20克酱油B0克，白糖适量，味精、姜末少许，菜油200克，熟猪油 l0克，鲜汤l000克左右。');
INSERT INTO `recipe` VALUES (4679, 50, '茄汁牛肉汤', '牛腱入滚水中烫后切成大块，番茄入滚水中川烫过取出剥去外皮切块。 \r\n锅中注入水五碗及牛腱块、番茄块、葱段、姜片，大火煮滚后改小火焖煮约一小时后，加胡萝卜、盐、味精、料酒继续焖煮约半个钟头，撒上葱末即可。', NULL, NULL, NULL, '牛腱八两，胡萝卜六两，番茄一个，葱末一勺，姜三片，盐、味精、料酒适量。');
INSERT INTO `recipe` VALUES (4680, 50, '西湖牛肉羹', '将牛肉剁烂后飞水，鸡蛋拌匀备用。武火烧镬下油，加酒落汤，放入味料，并放入牛肉，以湿淀粉勾芡后将鸡蛋放入拌匀即可。', NULL, NULL, NULL, '鸡蛋80克，牛肉200克。');
INSERT INTO `recipe` VALUES (4681, 50, '龙凤酸辣汤', '    葱姜呛锅下熟鳝丝、熟鸡脯丝、香菇丝炒至入味； \r\n\r\n　　加鸡清汤烧开，撇去浮油，加米醋、味精、盐、白胡椒粉即成。', NULL, NULL, NULL, '熟鳝丝100克 熟鸡脯丝100克 香菇丝100克');
INSERT INTO `recipe` VALUES (4682, 50, '蛤蜊汆鲫鱼', '1:鲫鱼治净，从脊背出剖开，从背上剞几刀，下清水锅加调料、姜块、葱段、笋片烧开，用小火煮熟装入汤盆。 \r\n\r\n2:蛤蜊下清水锅煮到外壳张开捞出，置于汤盆中。 \r\n3:滗出鱼汤，蛤蜊汤烧开，加香菇、熟火腿片调好料后倒入汤盆即成。', NULL, NULL, NULL, '活鲫鱼2尾 活蛤蜊250克 熟火腿3片 笋肉5片 水发香菇一只');
INSERT INTO `recipe` VALUES (4683, 50, '奶油虾仁汤', '　1、在法兰盘中放少许油，烧热。在虾仁渗入适量的酒、盐和胡椒，倒入法兰盘中煸炒，虾仁炒熟后，取出备用。 \r\n\r\n　　2、将洋葱切成碎末，在法兰盘里放入少许油，然后把洋葱末倒入法兰盘里翻炒，炒香以后，放入蕃茄酱，再加些水炒匀，多炒一些时间可以去掉蕃茄酱中的酸味，取出备用。 \r\n\r\n　　3、将鸡汤倒入法兰盘中，放入牛奶、鲜奶油和炒好的洋葱蕃茄油面酱，用打蛋器刘匀起稠，放入少许盐，调好口味，将汤汁煮沸。 \r\n\r\n　　4、将炒熟的虾仁放入汤盆里，将烧沸的汤倒入盆中即可。', NULL, NULL, NULL, '虾仁30克，鲜奶油20克，牛奶40克，油面酱10克，洋葱10克，蕃茄酱20克，鸡汤1汤盆。');
INSERT INTO `recipe` VALUES (4684, 50, '精力汤', '1.坚果泡软切碎和西瓜，以果汁机打成汁，水份不足再加入甘蔗汁。 \r\n\r\n2.先打较硬的水果，再加入绿叶其余配料，最后放芽菜，以瞬间开关打均匀。 \r\n\r\n　　精力汤是综合蔬菜、芽菜、海菜、坚果再加上回春水的酵素，可随季节增加酪朵或芝麻等油脂较多的热性食物。这是生食疗法中最完整的营养汤，不但能使病者恢复体力、改善体质、对于十二指肠溃疡或皮肤过敏、癌症、中风等等慢性病深具疗效。', NULL, NULL, NULL, '水果类:西瓜、木瓜、苹果共3杯 野菜类:地瓜叶、龙葵1杯 芽菜类:各种芽菜(苜蓿芽最佳)1杯 坚果类:杏仁、核核1汤匙 （以上每类材料可任选一种使用） 甘蔗汁2杯、回春水1杯（酵母粉1/2汤匙也可）');
INSERT INTO `recipe` VALUES (4685, 50, '鱼头浓汤', '1、将花鲢鱼头洗净，在肉厚处适当剞刀，用水洗净；菜心适当切开。 \r\n2、炒锅上旺火烧热，下猪油烧至四成热，将鱼头先用沸水烫一下，剖面朝上放入锅内略煎，烹入绍酒，将鱼头翻转，加葱、姜，加入沸水淹没鱼头，盖上锅盖，旺火烧约5分钟，放入菜心，再烧1分钟。 \r\n3、将鱼头捞出，', NULL, NULL, NULL, '花鲢头半个约750克，熟火腿肉数片，菜心4棵。葱段、姜块、绍酒、盐、猪油、鸡粉各适量。');
INSERT INTO `recipe` VALUES (4686, 50, '火腿冬瓜汤', '1、火腿批成薄片10片。冬瓜削皮去籽，洗净，切成5厘米长0.3厘米厚的片。 \r\n\r\n　　2、炒锅放入清汤，置旺火上烧沸，放入火腿骨和皮，沸滚5分钟左右，加入冬瓜，烧至呈玉白色时，把火腿和皮捞出，撇撇去泡沫，加精盐、味精，出锅盛入荷叶碗，整齐地放上火腿片即成。', NULL, NULL, NULL, '净冬瓜 500克 精盐 3克熟火腿 50克 味精 2.5克 火腿骨和皮 100克 清汤 750克');
INSERT INTO `recipe` VALUES (4687, 50, '杏仁豆腐羹', '1．先将杏仁在水中浸过液，取出剥皮捣碎并磨细，放适量水和匀；将杏仁浆盛入纱布袋，挤压出浓汁；杏仁汁加水煮开，放入生粉拌匀（切勿至沸），即可盛入深盘中；待冷却凝固后，切成数方块。 2．锅中放适量水煮沸，倒入蘑菇、木耳再煮开；放下杏仁豆腐，加盐调味煮透；再放糖、味精、麻油拌匀即成。', NULL, NULL, NULL, '甜杏仁200克，蘑菇10只，木耳10朵。生粉1小碗，生抽3大匙，麻油1大匙，糖和味精少许，盐适量。');
INSERT INTO `recipe` VALUES (4688, 50, '排骨冬瓜汤', '冬瓜、排骨切成小块，姜切细丝。取一只大汤碗，盛4碗热开水，将冬瓜块、排骨块及姜丝放入，以微波高段火力煮15-20分钟，取出加盐及味精调味即可。', NULL, NULL, NULL, '排骨250克，冬瓜250克，姜4片，盐适量，味精少许，热开水4碗。');
INSERT INTO `recipe` VALUES (4689, 50, '仙人掌百合羹', '　　1.仙人掌切成丁，百合掰瓣。 \r\n\r\n　　2.取适量的水，把仙人掌、百合、白糖放在一起，用旺火烧沸改用小火烧10分钟，倒入碗中晾凉即可食用。 \r\n\r\n　　', '特点：润肺止咳，降血脂，降身压', NULL, NULL, '仙人掌200克，鲜百合200克,白糖。');
INSERT INTO `recipe` VALUES (4690, 50, '芙蓉海底松', '　　1、将海蜇撕去外衣膜洗净后，放入沸水锅中烧约2分钟，取 \r\n\r\n　　出放入清水中漂清，再放入80度的热水中浸约20分钟，至海蜇涨大 散开时取出，撕成长约5厘米的块，洗净，挤去水，放入汤碗中。将紫菜放入沸水锅中烫一下，捞出洗净挤干。 \r\n\r\n　　2、将鸡蛋清放入碗中打散，舀入鸡清汤(150克)，加精盐 \r\n\r\n　　(l克)调匀后，盖上圆盘上笼蒸熟，取出即成芙蓉蛋。 \r\n\r\n　　3、将锅置旺火上，舀入鸡清汤(650克)烧沸后，将汤舀入海蜇碗中浸烫一下把汤滗入锅中，加精盐(3克)，味精，烧沸后倒入海蜇碗中，', NULL, NULL, NULL, '海蜇750克，鸡蛋清6个，熟火腿片100克，紫菜30克，精盐4克，味精1克，鸡清汤800克。');
INSERT INTO `recipe` VALUES (4691, 50, '鲜鱼生菜汤', '　　1、将草鱼肉顶刀切夹刀片，轻轻排松。 \r\n\r\n　　2、生菜平垫在汤碗底，将鱼片码在生菜上面。 \r\n\r\n　　3、起锅下入高汤烧开，加鸡粉、盐随即倒入汤碗即可。', NULL, NULL, NULL, '草鱼尾部肉100克，生菜100克，鸡粉5克，盐5克，高汤适量。');
INSERT INTO `recipe` VALUES (4692, 50, '砂锅山海', '　　1.大白菜洗净，叶片用手抓开、叶梗用手撕开(比较脆)、笋切片、冬菇发好切块。 \r\n　　2.以上三样一起拉油，用高汤焖煮备用。 \r\n　　3.火腿切片，土鸡切块，海叁、广肚发好洗净、鱼翅煨好。 \r\n　　4. 将所有材料摆入砂锅内，加高汤小火煨煮，味透，勾芡即可上桌。', NULL, NULL, NULL, '大白菜， 笋， 冬菇， 海叁，腿，鸡，广肚 ，排翅，汤， 盐、酒、胡椒粉、太白粉 少许');
INSERT INTO `recipe` VALUES (4693, 50, '清汤柴把鸭', '　　1、将鲜鸭肉煮熟，剔去粗细骨，切成5厘米长、0.7厘米见方的条，水发大香茹去蒂洗净。与熟火腿、玉兰片均切成5厘米长、0.3厘米见方的丝。水发青笋切成粗丝。 \r\n\r\n　　2、 取鸭条4根、火腿、玉兰片、香茹丝各2根，共计10根，用青笋丝从中间缚紧，捆成小柴把形状，共24把，整齐码入瓦钵内，加入熟猪油、精盐1.5克、鸡清汤250克，再加入剔出的鸭骨，入笼蒸40分钟取出，去掉鸭骨，原汤滗入炒锅，鸭子翻扣在大汤碗里。 \r\n\r\n　　3、 在盛鸭原汤的炒锅内，再加入鸡清汤250克烧开，撇去泡沫，放入精盐0.5克、', NULL, NULL, NULL, '鲜鸭肉1000克，葱段5克，熟火腿75克，胡椒粉0.5克，水发玉兰片75克，味精1克，水发大香茹75克，精盐2克，水发青笋50克， 鸡油5克， 鸡清汤500克， 熟猪油25克');
INSERT INTO `recipe` VALUES (4694, 50, '冰糖湘莲', '　　1、将莲子去皮去芯，放入碗内加温水150克，上笼蒸至软烂，桂元肉温水洗净，泡5分钟，滗去水，鲜菠萝去皮，切成1厘米见方的叮 \r\n\r\n　　2、 炒锅置中火，放入清水500克，再放入普溏烧沸，待冰糖完全溶化，端锅离火。用筛子滤去糖渣，再将冰糖水倒回锅内，加青豆、樱桃、桂元肉、菠萝，上火煮开。 \r\n\r\n　　3、 将蒸熟的莲子滗去水，盛入大汤碗内，再将煮开的冰糖及配料一起倒入汤碗，莲子浮在上面即成。', NULL, NULL, NULL, '湘白莲200克，罐头青豆25克，鲜菠萝50克，罐头樱桃25克，桂元肉25克，冰糖300克，水650克');
INSERT INTO `recipe` VALUES (4695, 50, '醋椒鱼', '　　1.将活鳜鱼一尾(约重90克)去鳞、鳃、鳍，开膛去内脏，洗净后，用开水烫一下，再用凉水洗一遍，刮去鱼身外面的黑衣，然后，在鱼身的两面剞上花纹：一面剞成十字花刀(即先坡着刀在鱼体上每隔1.65厘米宽切入1刀，深及鱼骨，再直着刀在已切的刀口上交叉切，即成十字花刀)；另一面则剞成一字刀(即直着刀生隔1.65厘米宽横切1刀，深及鱼骨)。香菜洗净清毒，切成长2厘米的段。葱一半切成长3.3厘米的细丝，一半切成末。 \r\n\r\n　　2. 将熟猪油倒入炒锅里，置于旺火上烧热，依次放入胡椒粉、葱末和姜末，煸出香味后，倒入鸡', NULL, NULL, NULL, '活鳜鱼1尾，姜末5克，香菜10克，白胡椒粉2.5克，鸡汤1000克，熟猪油50克，葱10克，醋50克，绍酒10克，味精2.5克，姜汁5克，芝麻油10克，精盐3.5克');
INSERT INTO `recipe` VALUES (4696, 50, '菜薪菇蕃薯粉煲', '　1.先将蕃薯粉用温开水浸透，起锅注入开水将菜薪菇放入， \r\n  2.煸熟，倾在笊篱里，滤去水份，注入汤200克将上述原料放锅， \r\n　3.用精盐、鸡粉调味。', NULL, NULL, NULL, '菜薪菇150克，蕃薯粉150克');
INSERT INTO `recipe` VALUES (4697, 50, '星鱼丸汤', '　　1.海鳗鱼杀好，和小尖刀刮取鱼绒，盛入盆中，清水500克加精盐4克，分5次添入鱼绒中，每加一次，用手往一个方向命名劲搅动，使鱼肉泡涨起劲，加入淀粉调匀，至鱼绒捏成小丸放入水中能浮起即成。 \r\n\r\n　　2. 虾仁洗净利制泥，猪肉剁成茸，同放一碗中，加精盐2克、味精1.5克搅匀，分捏成一个个1厘米的馅丸。 \r\n\r\n　　3. 炒锅放中火上，盛入清水，用左手抓起鱼绒摊在掌心，右手挑入一个馅心，随后用左手的拇指和食指捏压，鱼绒包裹馅心，从指缝中压出包心鱼丸，用小匙舀入锅中，这样制成一个个浮于水面的鱼丸。锅改用旺', NULL, NULL, NULL, '净海鳗鱼肉600克，香油10克，鲜虾仁30克，精盐10克，干淀粉60克，高汤800克，五花猪肉60克，胡椒粉2克，味精5克，芥菜末5克');
INSERT INTO `recipe` VALUES (4698, 50, '鸡汤氽海蚌', '　　1.将母鸡宰杀洗净后，割下鸡脯肉待用。其余鸡肉切四块，牛肉、猪里脊内切大牍一料同装一盆中，加清水1500克，上笼屉用旺火蒸3小时取出，去肉取汤，撇浮沫，滤去杂质。鸡脯肉剁成茸，加适量清水与精盐抓匀，捏成几个小圆球，与鸡汤下锅煮5分钟，捞出鸡茸球，鸡汤用净纱纱布过滤成精美鸡汤，取900克待用。 \r\n\r\n　　2. 海蚌尖每只片成2片，洗耳恭听净后盛入漏勺内，放入沸水锅中氽至六成熟取出，剔净蚌膜，装在碗中，加绍酒抓匀腌渍后沥干酒汁，盛入汤碗，加入鸡汤150克稍浸，再将汤汁滗净。 \r\n\r\n　　3. 将鸡汤下锅', NULL, NULL, NULL, '鲜海蚌尖400克，绍酒25克，老母鸡1只，白酱油25克，鲜牛肉750克，精盐1.5克，猪里脊肉750克，味精5克');
INSERT INTO `recipe` VALUES (4699, 50, '清炖全鸡', '　　鸡宰杀煺毛，从背部剖开，掏出内脏，洗净，在沸水锅中烫过，鸡腹部向上，头盘向身旁，脚剁去爪尖，屈于内侧，放入炖钵内，背上放香菇，加入精盐、味精、清水500克与少许绍酒，用棉纸或牛皮纸将炖盅封严，上蒸笼用旺火烧20分钟后改中火蒸2小时取出，移入汤碗上席。', NULL, NULL, NULL, '肥嫩母鸡1只1000克，精盐6克，味精3克，水发香菇15克，姜片2克，绍酒20克');
INSERT INTO `recipe` VALUES (4700, 50, '秋葵滑汤', '    1.高汤和调味料煮开后熄火，待凉。 \r\n\r\n　　2.黄秋葵去蒂，用大火氽烫后，漂凉沥干。 \r\n\r\n　　3.将沥干的黄秋葵切成3段，用果汁机打碎倒出，再与作法1料一起拌匀。 \r\n\r\n　　放进冰箱待凉即可取出食用。', NULL, NULL, NULL, '黄秋葵300公克，高汤5杯，盐1小匙，淡酱油1大匙，糖1小匙');
INSERT INTO `recipe` VALUES (4701, 50, '牛肉蔬菜浓汤', '　　牛肉洗净切块氽烫，捞起冲净。 \r\n\r\n　　洋葱去老膜蒂须，洗净，十字对切；马铃薯削皮洗净、平剖对半再十字对切大块；胡萝卜削皮洗净、滚刀切大块；蕃茄洗净去蒂，切大片。 \r\n\r\n　　青花菜剥瓣去老皮，分成小朵，以盐水泡洗、沥干。 \r\n\r\n　　牛肉放进炖锅内加6碗水以武火烧开，改文火慢炖1小时后，加入作法2之材料续炖30分钟。 \r\n\r\n　　青花菜氽烫捞起泡冷水，加入锅内，加盐调味即成。食用前洒上胡椒粉。', NULL, NULL, NULL, '牛肉1斤,洋葱1个,马铃薯2个,蕃茄1个,青花菜1颗,胡萝卜1条,胡椒粉1小匙');
INSERT INTO `recipe` VALUES (4702, 50, '苏比兹汤', '　　１．洋葱切细末，把鸡肉汤加热； \r\n\r\n　　２．在锅内把黄油溶化，加进洋葱和盐，炒成黄色，把米撒入锅中，把热好的汤倒进去，煮２５分钟，然后用萝过滤； \r\n\r\n　　３．把做好的奶油汤加热倒在鸡汤中，搅拌均匀，用盐，胡椒粉，豆蔻粉撒匀。', NULL, NULL, NULL, '洋葱９００克，鸡汤５００毫升，黄油６０克，西米５０克，做好的奶油汤２５０毫升，豆蔻，盐，胡椒粉各少许。');
INSERT INTO `recipe` VALUES (4703, 50, '菊花鱼丸', '　　将净鱼肉放在一大张新鲜肉皮上，把鱼肉斩成细腻的鱼茸，加细盐，味精，白胡椒粉，清水100克，蛋清，微量猪油，顺着同一个方向用力搅和成“鱼胶子”。净锅内加多量冷水，用左手抓一把鱼胶子，挤出一只一只的鱼丸，放入锅内然后上火煮至将沸，端离火待用。净锅烧热，加少许油，放入葱花姜末煸香即加鲜汤，黄酒，细盐，味精，白胡椒粉等，烧沸后下水生粉勾米汤芡，使汤汁略有粘性，再把鱼丸，熟火腿（切丝），蘑菇（切丁），菊花推匀，淋上少许油增光即成。', NULL, NULL, NULL, '白菊花100克、鲜肉250克、熟火腿、鲜蘑菇、烫熟的小碗豆各50克、鸡蛋2只。黄酒2匙、细盐、味精各0.3厘米、白胡椒少许、葱花姜片各适量、葱');
INSERT INTO `recipe` VALUES (4704, 50, '鸡茸竹荪汤', '　　1、虾剪须，洗净，吸干水分。 \r\n\r\n　　2、连壳煎熟待用。 \r\n\r\n　　3、芋丝拆去包装，倾去水分，飞水。 \r\n\r\n　　4、青椒洗净，切丝，烧热油，炒熟青椒，上碟，待用。 \r\n\r\n　　5、烧热油，爆香干葱，倾入粟米茸、调味和献汁，待滚。 \r\n\r\n　　6、下蛋液，拌匀，下芋丝、青椒、虾和甘笋花，待滚便可食用。', NULL, NULL, NULL, '　　活中虾1/2斤、干葱2粒、鸡蛋1只、青椒1只、芋丝1包、粟米茸1罐、甘笋花数片。 \r\n，盐3/4茶匙，鸡粉1茶匙，胡椒粉、麻油各少许，生粉2茶匙，清水2汤匙，麻油或油1茶匙。');
INSERT INTO `recipe` VALUES (4705, 50, '八宝斋汤', '白果去壳去衣，雪耳浸透剪去梗蒂。冬菇浸透去蒂，栗子去衣、陈皮浸软后刮去瓤。放适量水入煲，放陈皮煲滚，把各料入煲10分钟，改慢火煲2小时后，加适量盐调味。', NULL, NULL, NULL, '白果、莲子、芡实各50克，栗子肉200克，雪耳30克，无花果、蜜枣、冬菇各8个，陈皮大半个，水、盐适量。');
INSERT INTO `recipe` VALUES (4706, 50, '清补凉汤', '　　1、将用料洗净，猪肉切成细块。 \r\n\r\n　　2、用清水6碗，材料一起放进煲内，煮约2小时，加少许盐调味便可。', NULL, NULL, NULL, '猪肉6两，淮山3钱，薏米3钱，玉竹2钱，芡实3钱，百合2钱，莲子3钱');
INSERT INTO `recipe` VALUES (4707, 50, '百合汤', '加水200毫升，煮烂，放白糖适量，每日下午食用。', NULL, NULL, NULL, '鲜野百合150克。');
INSERT INTO `recipe` VALUES (4708, 50, '米茸芋丝虾煲', '　　1、虾剪须，洗净，吸干水分。 \r\n\r\n　　2、连壳煎熟待用。 \r\n\r\n　　3、芋丝拆去包装，倾去水分，飞水。 \r\n\r\n　　4、青椒洗净，切丝，烧热油，炒熟青椒，上碟，待用。 \r\n\r\n　　5、烧热油，爆香干葱，倾入粟米茸、调味和献汁，待滚。 \r\n\r\n　　6、下蛋液，拌匀，下芋丝、青椒、虾和甘笋花，待滚便可食用。', NULL, NULL, NULL, '　活中虾1/2斤、干葱2粒、鸡蛋1只、青椒1只、芋丝1包、粟米茸1罐、甘笋花数片，盐3/4茶匙，鸡粉1茶匙，胡椒粉、麻油各少许，生粉2茶匙，清水2汤匙，麻油或油1茶匙。');
INSERT INTO `recipe` VALUES (4709, 50, '金针鸡丝', '　　1、鸡胸肉去皮去骨切成丝，用腌料抓匀腌20分钟。 \r\n\r\n　　2、煮1锅滚水，将腌好的鸡丝投入烫煮1分钟后捞起，再浸入清水中以免粘成一团。 \r\n\r\n　　3、金针将硬梗部分切掉，每条打成一结扣，用清水浸泡30分钟后捞起挤干水分。 \r\n\r\n　　4、煮锅放入清汤煮滚，同时放下鸡丝、金针和调味料一起续滚3分钟即可供食。', NULL, NULL, NULL, '4人份鸡胸肉1付、清汤3饭碗、金针80克。姜酒汁1/2大匙、白胡椒粉1/2茶匙、味精1/3茶匙、淀粉2茶匙、麻油2茶匙。盐1茶匙、味精1/2茶匙。');
INSERT INTO `recipe` VALUES (4710, 50, '鲜虾毛豆煲', '　　1.用盐水洗虾，去除虾仁的泥肠和外壳，粘上酱油、酒及淀粉。将大葱、生姜和大蒜切成碎末。煮毛豆，然后剥出。 \r\n\r\n　　煮毛豆前要撒些盐，轻揉并放置一会儿，煮的时间不能过长。煮好的毛豆放在帘子上，使之尽快凉下来，可保持颜色鲜艳。 \r\n\r\n　　2.用热油炒大葱、姜、大蒜，待香味出来后，放入豆瓣酱，再放虾，炒至是变色后，倒入混合调料，然后用淀粉勾芡，最后放入毛豆。起锅时沿锅边浇些香油。', NULL, NULL, NULL, '鲜虾、酱油、 \r\n\r\n　　酒、淀粉毛豆 \r\n\r\n　　（去皮）大葱生姜大蒜豆瓣酱沙律油混合调料麻油 \r\n\r\n　　200克各1茶匙1杯10厘米1块1瓣1茶匙3汤匙详见下文*1茶匙 \r\n　　*混合调料：砂糖1/2茶匙，酱油4茶匙，酒2汤匙，蕃茄酱1汤匙，中式汤料少许，水半杯），淀粉汁（淀粉、水各1茶匙');
INSERT INTO `recipe` VALUES (4711, 50, '鲜鱿鲛鱼茄汁煲', '　　（1）鲜鱿鱼去头除内脏，除去紫膜，洗净，横切大件，下调味料腌片刻。 \r\n\r\n　　（2）大头菜切片，洋葱去衣切片，番茄切件。 \r\n\r\n　　（3）中火烧热沙律油，炒香蒜茸，加入洋葱炒匀，下鲛鱼、鱿鱼炒匀，最后加入大头菜、番茄、茄汁和上汤，拌匀上盖煮至熟透，以盐、胡椒粉调味即成。 \r\n\r\n　　（4）上桌前撒上姜丝，拌匀供食。 \r\n\r\n　　[特点]： \r\n\r\n　　味道鲜浓，营养丰富。', NULL, NULL, NULL, '鲜鱿鱼1只，鲛鱼480克，大头菜、洋葱各1个，鲜番茄2个，葱头1瓣（切茸），茄汁2汤匙，沙律油3汤匙，盐1/4茶匙，胡椒粉少许，姜丝1汤匙，上汤（或清水）1/2杯。 盐1/4茶匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4712, 50, '白果鸭煲', '　　①白果去壳，放滚水中煮五分钟，洗净滴干水。鸭洗净，下滚水中煮熟，取出滴干水，切块； \r\n\r\n　　②下油二汤匙，爆透白果，然后下鸭爆片刻，加调味，白果焖熟，大约二十分钟，勾芡熄火； \r\n\r\n　　③芽白洗净，切短段。煮熟放在煲仔内，把鸭放在黄芽白上煲滚，放上芫絮。原煲上台即可。', NULL, NULL, NULL, '光鸭一只，白果4两，黄芽白八两，芫絮二棵，胡罗卜，磨豉鼓，蚝油，糖，鸡粉，生粉，酒，盐，老抽，果皮各适量。');
INSERT INTO `recipe` VALUES (4713, 50, '粤式酸辣汤', '　　(一)豆腐、鸡血、冬菇、笋肉、瘦肉及海参分别成丝，榨菜切细末，芫茜切碎待用。 \r\n\r\n　　(二)将笋丝及海参用沸水出水待用，肉丝用少许生粉及水拌匀。 \r\n\r\n　　(三)烧热１汤匙油，蘸洒，然后加入上汤及调味料煮沸，将所有材料放入，待再沸时，用生粉水打芡，再用小火放入鸡蛋液，拌匀，洒上芫茜 \r\n\r\n　　碎即成。', NULL, NULL, NULL, '鸡血１块，实豆腐２块，瘦肉２两，笋肉约１两半，冬菇４只，湿海参约４两，榨菜１小块。鸡蛋２只，芫茜１棵，上汤４杯。');
INSERT INTO `recipe` VALUES (4714, 50, '铁观音粟子排骨汤', '　　1.铁观音茶叶以热开水500克泡1分钟后滤去茶叶，取茶汤备用。 \r\n\r\n　　2.小排切成小段（宽2厘米），用开水氽烫后洗净。 \r\n\r\n　　3.把全部材料装入圆锅炖1个半小时。上桌前另盐调味即可。', NULL, NULL, NULL, '铁观音茶叶3克小排300克配料：新鲜粟子120克调味料：盐1茶匙');
INSERT INTO `recipe` VALUES (4715, 50, '蕃茄鸡煲', '　　①鸡洗净，抹干水，斩块，加老抽一茶匙捞匀。泡油；笋虾用清水洗数次，抹干水，放入滚水中煮十分后捞起，再用清水洗一洗，揸干水，切成适当的长短或大小； \r\n\r\n　　②下油四汤匙，爆葱、蒜、蕃茄酱，下笋虾炒片刻，加入调味煮滚，慢火焖二十分钟，加入鸡兜匀，再焖十五分钟，试味勾芡，铲起放入煲内煮滚，放上葱，原煲上台。', NULL, NULL, NULL, '鸡半只，笋虾3两，蒜茸2茶匙，姜数片，葱三条切段，蕃茄1个，水一杯，酒，生抽，鸡粉，糖，八角，生粉，盐各适量。');
INSERT INTO `recipe` VALUES (4716, 50, '肉片粉丝汤', '①牛肉切薄片，加淀粉、黄酒、精盐和味精拌匀； \r\n②锅里水滚后，先放牛肉片，盖上锅盖略滚即加入用开水发好的粉丝，盖上锅盖煮5分钟左右开盖加精盐、味精后再烧沸，盛入汤碗，淋上麻油即可。', NULL, NULL, NULL, '牛肉100克，粉丝80克，虾米25克，精盐、黄酒、淀粉、味精、麻油各适量');
INSERT INTO `recipe` VALUES (4717, 50, '成都蛋汤', '　　1.将鸡蛋去壳，放入碗内用力调匀。木耳洗净。 \r\n\r\n　　2.汤锅置火上，放入猪油烧热，鸡蛋入锅，煎至两面微黄，当蛋质松软时，用手勺将鸡蛋捣散，加入汤，再下精盐、木耳、菜心、味精烧开，入味后淋上猪油即成。', NULL, NULL, NULL, '鸡蛋4个，水发木耳50克，菜心100克，精盐4克，猪油75克，味精2克，浓白汤1000克。');
INSERT INTO `recipe` VALUES (4718, 50, '鲜花豆腐', '　　豆腐揭去表皮后用箩筛制茸，用纱布搌干水分后置盆中。猪肥膘肉和鸡脯肉去筋膜捶茸，与豆腐同置一盆中，加葱姜水搅散后。再加盐和鸡蛋清制成掺。在扇形与蝶形模具上抹一层猪化油，分别制出10个扇形、2个蝴蝶形的豆腐掺，将胡萝卜、大甜椒、香菌等片刻成不同花卉图案，分别嵌于豆腐掺上，上笼蒸熟备用。炒锅置旺火上，加清汤烧沸，加胡椒粉，放入豌豆苗烫熟，舀入汤盆内，将蒸好的豆腐惨滑入汤内即成。', NULL, NULL, NULL, '嫩豆腐500克。肥膘肉50克、鸡脯肉50克、胡萝卜30克、大甜椒20克、香菌15克、豌豆苗50克。葱姜水20克、盐3克、鸡蛋清25克、清汤500克、胡椒粉2克。猪化油30克。');
INSERT INTO `recipe` VALUES (4719, 50, '复元汤', '　　将羊脊骨砍成数块洗净，羊肉洗净，一起入沸水锅内氽去血水，再洗净。将淮山药等药物用纱布装好扎口，姜、葱拍破，羊肉切成条块。将以上食物和药物同时下入砂锅内，加清水适量，置武火上烧沸，打去浮沫，放入花椒、八角、料酒，移炆火上炖肥。将肉汤装碗，加胡椒粉、盐调味即成。', NULL, NULL, NULL, '瘦羊肉500克、粳米100克。淮山药50克、肉灰蓉20克，菟丝子10克、羊脊骨一具，胡桃仁2个。葱白3根、姜10克、料酒10克、八角3克、花椒2克、盐3克、胡椒粉2克。');
INSERT INTO `recipe` VALUES (4720, 50, '仲景羊肉汤', '　　当归，生姜用清水洗净，切成大片。羊肉去骨，剔去筋膜，入沸水汆去血水，捞出晾凉，切成5厘米长、2厘米宽、1厘米厚的条。砂锅中掺入清水适量，将切好的羊肉、当归、生姜放入锅内，旺火烧沸后，打去浮沫，改用炆火炖1小时，羊肉熟透即成。', NULL, NULL, NULL, '羊肉500克。生姜250克、当归150克、胡椒面二克、葱50克、料酒20克、盐3克。');
INSERT INTO `recipe` VALUES (4721, 50, '荷包鱿鱼', '　　碱发犹鱼片用开水泡去碱味沥干入锅，加汤、料酒、胡椒粉来透，捞出晾凉后，用桃形模将就鱼片按成桃形，搌干水。鱼肉（去皮和带血肉）、猪肥膘分别用刀背捶成细茸，去净茸肉细刺和筋膜，再用刀口排剁一遍，分别入碗。葱、姜拍破用清水泡上。将鱼茸用泡葱、姜的水调成糊状，下盐、胡椒粉。料酒。猪肉茸用力向一个方向搅动，再加入蛋清、水豆粉搅至发亮成为鱼掺。将蛋黄搅匀摊成蛋皮后，切成细丝。樱桃切成细牙。菜帮氽后，一半切成薄的小菱形块，另一半切成短丝。薄海带切成长约1厘米的细丝。用蛋清加干豆粉调成蛋清豆粉。将桃形就鱼片抹上蛋清豆', NULL, NULL, NULL, '水发鳅鱼300克。鱼肉50克、猪肥膘肉50克、蛋黄10克、樱桃10克、绿色菜帮25克、海带10克、发菜10克。料酒25克、胡椒粉3克、盐4克、蛋清30克、豆粉20克、味精0.5克、葱、姜各15克、清汤750克。');
INSERT INTO `recipe` VALUES (4722, 50, '沙参心肺汤', '　　将沙参、玉竹择净后用清水漂洗，再用纱布包好备用。心肺用清水冲洗干净，挤尽血水，同沙参、玉竹一起下入砂锅，葱洗净入锅，加清水适量，先用武火烧沸，移炆火炖1个半小时。待心肺熟透，加味精、盐调味即成。', NULL, NULL, NULL, '猪心肺1具。沙参15克、玉竹15克。盐3克、味精1克、葱25克。');
INSERT INTO `recipe` VALUES (4723, 50, '竹笋香菇汤', '　将香菇切丝，姜切丝，金针打结，竹笋切丝。将竹笋，姜丝放在汤锅中加适量清水煮15分钟，再放香菇，金针煮5分钟放精盐，味精即可。', NULL, NULL, NULL, '香菇25克，竹笋15克，金针110克，清汤300克。');
INSERT INTO `recipe` VALUES (4724, 50, '一品豆腐汤', '　　一、将豆腐搅烂成茸，用细筛滤过；鸡肉用刀背捶成鸡茸，放少许水使之化开，捞去鸡筋，并入豆腐内；将鸡蛋除去蛋白打散。二、将以上用料拌和，加盐，味精，胡椒粉，倒入盆内铺平（盆上先抹些油，以免豆腐与鸡茸粘住），面上用些有色彩的荤素用料（可自行酌定），做些花草,树木或其他图案加以点缀.然后在食用前七分钟上笼蒸.三、在热的鸡汤内加上竹荪,绿叶菜,再将蒸好的豆腐倒入其中即好', NULL, NULL, NULL, '嫩豆腐（２５０克）、绿叶菜（少许）、鸡肉（６０克）、味精（少许）、鸡蛋（３只）、精盐（少许）、水发竹荪（４０克）、胡椒粉（少许）、鸡汤（１碗）');
INSERT INTO `recipe` VALUES (4725, 50, '粉丝香菇蛋汤', '：①香菇泡软去梗切块，粉丝泡软，鸡蛋液打散，青蒜斜切片备用。 \r\n\r\n　　②将香菇和鸡蛋液混在一起搅，加少许盐，沙锅放些油将混合蛋液倒 \r\n\r\n　　入煎。 \r\n\r\n　　③加半锅水及调', NULL, NULL, NULL, '粉丝，香菇，鸡蛋，青菜（由自己口味定），盐，鸡精。');
INSERT INTO `recipe` VALUES (4726, 50, '黄芪汽锅鸡', '1.将子母鸡宰杀后，去毛和内脏，剁成3.3厘米见方的块，放入沸水锅内烫3分钟捞出，洗净血沫，装入汽锅内；加入葱、姜、食盐、味精、绍酒、花椒水等。 \r\n\r\n　　2.将黄芪片洗净，也放入汽锅内，盖上盖，上笼蒸3小时取出，拣去葱、生姜、黄芪即成。', NULL, NULL, NULL, '黄芪片20克，子母鸡1只，葱、生姜、食盐料酒、味精、花椒水各适量。');
INSERT INTO `recipe` VALUES (4727, 50, '煲仔鱼丸', '　　将虾粒，腊肠粒，发菜段，香菜，葱和鱼肉拌匀。鸡清汤下锅加热，鱼肉挤成鱼丸下锅，煮熟后捞出。将生菜，粉丝，放入汤锅加酱油等调料稍煮，将鱼丸排在上面勾芡浇上即可。', NULL, NULL, NULL, '绞碎鲮鱼肉225克，腊肠1条，发菜20克，粉丝50克，鸡清汤100克，生菜180克，切碎的香菜，葱，虾各10克。');
INSERT INTO `recipe` VALUES (4728, 50, '清汤全家福', '鸡脯肉切成薄片放入碗中，用鸡蛋清，精盐，湿淀粉浆好，海参切片，蹄筋切两半，鱼肚切大片。海参，鱼肚，蹄筋，鱼翅用清汤汆一下，鸡片，鱼片，虾片用沸水汆熟，把各种主料放在一个大汤碗内，将火腿，冬笋，冬菇，菜心相间摆在上面，汤锅内放入清汤，精盐，料酒旺火烧开，加味精后将清汤倒在汤碗中即可', NULL, NULL, NULL, '水发海参，鱼翅，蹄筋，虾片，鸡脯肉，鱼片料酒各50克， \r\n　　菜心，熟火腿各15克，鸡蛋清，冬笋各25克，味精2克， \r\n　精盐4克，水发鱼肚100克，清汤150克，水发冬菇，湿淀粉各10克');
INSERT INTO `recipe` VALUES (4729, 50, '百合栗子羹', '1、百合剥片后清洗干净，与板栗同放碗内，加水少许上笼蒸30分钟。 \r\n\r\n2、锅内放清水400克，烧开后放入蒸好的百合、板栗，加糖150克，待糖溶化后勾芡即可装碗。', NULL, NULL, NULL, '　鲜百合100克，鲜板栗肉100克，白糖150克，湿淀粉50克');
INSERT INTO `recipe` VALUES (4730, 50, '板栗粥', '板栗100克去皮，切碎粒与淘好的糯米100克，加拍碎的生姜10克煮米烂汤稠，加少许盐，温热服食。', NULL, NULL, NULL, '板栗100克,糯米100克,生姜10克,少许盐');
INSERT INTO `recipe` VALUES (4731, 103, '水果沙拉堡', '通心粉先用开水煮软、沥干备用。\r\n苹果、水蜜桃、蜜世界均切成 1公分见方、0.5公分厚的薄片，与通心粉、色拉酱拌匀，包入长条型面包。\r\n在水果色拉上撒下 少许葡萄干即可。\r\n \r\n备注：\r\n水果色拉不宜久放，故一次不要做太多。多切出来的水果，可以打成果汁和色拉堡一块吃。', NULL, NULL, NULL, '\r\n面包 ... 4个(长条型) \r\n苹果 ... 1/2个 \r\n水蜜桃 ... 1片 \r\n蜜世界瓜 ... 1片(或绿香瓜、奇异果) \r\n通心粉 ... 4匙(不加亦可) \r\n葡萄干 ... 少许\r\n调味料\r\n 色拉酱 ... 100公克');
INSERT INTO `recipe` VALUES (4732, 103, '熏肝面包', '土司去边切成四等分，吉士片亦切成四等分备用。\r\n酸黄瓜和熏肝切薄片、菠萝切小块备用。\r\n土司片涂上一层色拉酱，依序放上 1小片吉士、酸黄瓜 1片、熏肝 1片、菠萝 1小块，用牙签固定放入烤箱烤 2分 30 秒', NULL, NULL, NULL, '\r\n熏肝 ... 100公克 \r\n吉士片 ... 3片 \r\n酸黄瓜 ... 1条 \r\n土司 ... 3片 \r\n菠萝片 ... 1片(圆形)\r\n调味料\r\n 色拉酱 ... 少许');
INSERT INTO `recipe` VALUES (4733, 103, '土司披萨', '罐头洋菇切薄片备用。\r\n厚土司先涂上蕃茄酱，再撒上薄薄一层吉士丝。\r\n吉士丝上再铺上鲔鱼酱、洋菇片、撒上玉米粒，最后再撒上薄﹞@层吉士丝，放入烤箱中烤约 5至 6分钟即可。\r\n \r\n备注：\r\n1.鲔鱼酱可用意大利腊肠代替。\r\n2.口味可依个人喜好调配。\r\n3.搭配其它饮品亦可。', NULL, NULL, NULL, '\r\n厚土司 ... 2片(3cm厚) \r\n吉士丝 ... 1/3杯 \r\n洋菇 ... 4粒(罐头) \r\n玉米粒 ... 1大匙 \r\n鲔鱼酱 ... 50公克\r\n调味料\r\n 蕃茄酱 ... 1大匙(或辣椒酱)');
INSERT INTO `recipe` VALUES (4734, 103, '鲔鱼色拉饼、麦片粥', '冷冻马铃薯切小丁、蛋打成汁备用。\r\n鲔鱼罐头加马铃薯丁、红萝卜丁、玉米粒、火腿丁、盐及色拉酱搅拌均匀，分成六等分，做邑窭y状。\r\n将色拉球滚上一层面包粉，沾一层蛋汁再滚一层面包粉，然后压成饼型。\r\n起油锅，入油 2大匙，用小火煎至两面金黄即可。', NULL, NULL, NULL, '\r\n鲔鱼罐头 ... 100公克 \r\n马铃薯 ... 100公克 \r\n色拉酱 ... 100公克 \r\n红萝卜丁 ... 2大匙 \r\n玉米粒 ... 2大匙 \r\n洋火腿丁 ... 2大匙 \r\n面包粉 ... 1包 \r\n蛋 ... 1个\r\n调味料\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4735, 103, '法式薄饼、麦芽乳', '薄饼粉加水搅拌成均匀的糊状。\r\n起油锅，入油 1大匙，将薄饼糊分四分，舀一分下锅用小火煎至表面金黄，翻面力煎另@面，煎成 4片备用。\r\n可将薄饼包入水蜜桃丁包卷起来，在两头挤上奶油慕丝，并装饰水果片即可。\r\n亦可用玉米粉加水及糖煮开勾成浓汁，加入含果粒的草莓果酱拌匀，淋在薄饼上。\r\n \r\n备注：\r\n淋汁或配料可依个人喜好更改。', NULL, NULL, NULL, '\r\n薄饼粉 ... 250公克(或松饼粉) \r\n水蜜桃 ... 1罐 \r\n草莓果酱 ... 3大匙 \r\n玉米粉 ... 少许 \r\n装饰水果 ... 少许 \r\n葡萄干 ... 少许\r\n调味料\r\n 砂糖 ... 2大匙');
INSERT INTO `recipe` VALUES (4736, 103, '美式全餐', '作法步骤：\r\n起油锅，入油 1/2杯，将薯条炸至微黄，撒上细盐 少许。\r\n平底锅中加奶油 1小块，融化后，将蛋放入锅中，用小火煎至蛋白凝结、蛋黄半熟起锅。\r\n用剩下的奶油将培根煎至微黄即可。\r\n \r\n备注：\r\n全餐是一种配合性的餐点，所以量少样多，需要较多时间准备，可以在周日做。', NULL, NULL, NULL, '\r\n土司 ... 4片 \r\n蛋 ... 4个 \r\n培根 ... 8条 \r\n薯条 ... 少许 \r\n葡萄汁 ... 2杯 \r\n咖啡 ... 2杯(或红茶)\r\n调味料\r\n 奶油 ... 适量\r\n 果酱 ... 适量');
INSERT INTO `recipe` VALUES (4737, 103, '热狗大蒜面包、果菜汁', '法国面包切薄片、大蒜磨成蒜泥。\r\n面包先抹一层奶油，再抹一层蒜泥，放入烤箱烤 4分钟。\r\n大芹菜切片，热狗先斜切成两段，再在斜切片切一刀。\r\n起油锅，入油 1大匙，先将大芹菜略炒，加入热狗及蕃茄酱、黑胡椒粉，中火翻炒约 1分钟起锅。', NULL, NULL, NULL, '\r\n芹菜 ... 1支 \r\n热狗 ... 4条 \r\n法国面包 ... 6片 \r\n大蒜 ... 1粒\r\n调味料\r\n 蕃茄酱 ... 2大匙\r\n 奶油 ... 2大匙\r\n 粗黑胡椒粉 ... 少许');
INSERT INTO `recipe` VALUES (4738, 103, '鳕鱼粥', '鳕鱼去皮切丁，约 1公分见方大小。\r\n用平底锅起油锅，入油 1大匙，蛋煎成蛋饼，切成 1公分见方大小的小丁。\r\n白粥煮滚，加入盐及鲜鸡晶、鳕鱼丁、蛋丁搅拌均匀，改用小火煮 2分钟，撒下葱花即可熄火。\r\n \r\n备注：\r\n1.鳕鱼丁很容易熟，下锅时间要控制好。\r\n2.葱花太早加入会变色，若粥盛入碗里再撒会有生葱气味，所以必须撒在锅里，略加热再起锅才好。', NULL, NULL, NULL, '\r\n鳕鱼 ... 200公克 \r\n蛋 ... 1个 \r\n葱花 ... 1大匙 \r\n白粥 ... 2碗\r\n调味料\r\n 盐 ... 1小匙\r\n 鲜鸡晶 ... 1小匙');
INSERT INTO `recipe` VALUES (4739, 103, '油豆腐细粉', '粉丝用热水泡软应用。\r\n绞肉加太白粉及盐 少许，拌匀后，塞进三角型油豆腐里。\r\n高汤煮滚加入油豆腐煮 5分钟，再下粉丝及盐煮 3分钟，最后加入香菇丝、蛋丝、榨菜丝及芹菜末，煮 3分钟即可。', NULL, NULL, NULL, '\r\n油豆腐 ... 10块(三角型) \r\n绞肉 ... 100公克 \r\n香菇丝 ... 少许 \r\n蛋丝 ... 少许 \r\n榨菜丝 ... 少许 \r\n芹菜末 ... 少许 \r\n粉丝 ... 1把\r\n调味料\r\n 盐 ... 1小匙\r\n 太白粉 ... 1/2大匙\r\n 高汤 ... 2杯');
INSERT INTO `recipe` VALUES (4740, 103, '酒酿汤圆', '蛋打成汁备用。\r\n水 2杯煮沸放入汤圆，煮至汤圆浮起，再加入甜酒酿及冰糖。\r\n至水再沸时，缓缓加入蛋汁，用汤匙拨成漂亮的蛋花。', NULL, NULL, NULL, '\r\n甜酒酿 ... 4大匙 \r\n汤圆 ... 10粒 \r\n蛋 ... 2个\r\n调味料\r\n 冰糖 ... 5大匙');
INSERT INTO `recipe` VALUES (4741, 103, '金瓜米粉', '米粉用热水泡软，切约 5公分长段，沥干备用。\r\n金瓜去皮去籽刨成粗丝备用。\r\n肉丝用太白粉及盐 少许抓匀，腌 10分钟。\r\n起油锅，入油 3大匙，先下肉丝炒至肉丝变色，再下金瓜丝及盐，炒至金瓜丝变软，改用小火加入米粉及酱油，用筷子翻炒均匀即可。\r\n \r\n备注：\r\n炒米粉要用筷子翻炒，不要用锅铲，以免米粉被切断。', NULL, NULL, NULL, '\r\n南瓜 ... 1/2个(200公克) \r\n猪肉丝 ... 200公克 \r\n葱花 ... 1/2杯 \r\n米粉 ... 1片\r\n调味料\r\n 盐 ... 1小匙\r\n 酱油 ... 1/2匙\r\n 太白粉 ... 1/2大匙');
INSERT INTO `recipe` VALUES (4742, 103, '翡翠炒饭', '青刚菜洗净切碎，加盐 1小匙腌 10分钟，挤去水分再剁碎，愈细愈好。\r\n起油锅，入油 2大匙，放入青刚菜用大火爆炒 30 秒起锅。\r\n用剩余的油爆香葱花，再放入白饭炒散，然后加入刚菜、火腿丁、玉米粒及盐 1小匙一起拌炒，用小火炒至干松即可。', NULL, NULL, NULL, '\r\n青刚菜 ... 1/2斤 \r\n洋火腿丁 ... 3大匙 \r\n玉米粒 ... 3大匙 \r\n葱花 ... 2大匙 \r\n冷白饭 ... 2碗\r\n调味料\r\n 盐 ... 2小匙');
INSERT INTO `recipe` VALUES (4743, 103, '蕃茄蛋花汤', '蕃茄切块、蛋打成蛋汁备用。\r\n水 2杯煮沸，放入盐、鸡晶及蕃茄块，至水再沸时淋下蛋汁即可。', NULL, NULL, NULL, '\r\n蕃茄 ... 1个 \r\n蛋 ... 1个\r\n调味料\r\n盐 ... 1小匙\r\n鸡精 ... 1小匙');
INSERT INTO `recipe` VALUES (4744, 103, '鲜味翠玉片', '蛋打成汁，用平底锅起油锅，入油 1大匙，用微火烘成蛋饼，烘至两面焦黄，切成菱形块。\r\n起油锅，入油 1大匙，先爆香鱼香料，再将所有调味料及水 1/2杯拌匀，加入锅中勾成薄芡淋在蛋上即可。编号h037_034_b\r\n将火腿、洋葱、青椒均切成大小相似的薄片。\r\n火腿片加水及冰糖用小火焖煮 30 分钟。\r\n然后将火腿片、洋葱片、青椒片间隔排列，淋上火腿汁，再用隔水蒸 8 分钟或放进微波炉强微波 2分钟即可。', NULL, NULL, NULL, '\r\n蛋 ... 3个 \r\n葱末 ... 3大匙 \r\n姜末 ... 3大匙 \r\n蒜末 ... 3大匙 \r\n红椒末 ... 1大匙 \r\n火腿片 ... 10片 \r\n洋葱 ... 1/2个 \r\n青椒 ... 1/2个\r\n调味料\r\n 酱油 ... 1大匙\r\n 黑醋 ... 1大匙\r\n 白醋 ... 1大匙\r\n 糖 ... 1大匙\r\n 太白粉 ... 1大匙\r\n 冰糖 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4745, 103, '金钩萝卜汤', '虾米泡发、白萝卜刨丝备用。\r\n锅中入水 2杯，将虾米、白萝卜丝及盐、鸡晶，用小火煮至萝卜丝入口即化最好。', NULL, NULL, NULL, '\r\n虾米 ... 2大匙 \r\n白萝卜 ... 1/3根\r\n调味料\r\n盐 ... 1小匙\r\n鸡精 ... 1小匙');
INSERT INTO `recipe` VALUES (4746, 103, '香菇酿肉', '香菇泡发备用。\r\n绞肉加酱油、太白粉搅拌均匀备用。\r\n每朵香菇抹上 1大匙绞肉放在深盘中，浇上香菇汁及盐。\r\n隔水蒸 12分钟即可，亦可用电饭锅和饭一起煮。\r\n \r\n备注：\r\n若用微波炉则需强微波 5分钟。', NULL, NULL, NULL, '\r\n香菇 ... 6朵 \r\n绞肉 ... 6大匙(60公克)\r\n调味料\r\n 太白粉 ... 2大匙\r\n 酱油 ... 1大匙\r\n 盐 ... 少许');
INSERT INTO `recipe` VALUES (4747, 103, '金瓜起士', '西洋菜切细末备用。\r\n南瓜削皮去籽，切成 0.3公分厚的薄片，撒上盐拌匀，放入微波餐盒内。\r\n然后放入微波炉强微波 3分 30 秒，待南瓜变软，趁热拌入奶油及西洋菜末、撒上吉士丝。\r\n再放入微波炉中微波加热 2分钟即可。', NULL, NULL, NULL, '\r\n南瓜 ... 1/2个(350公克) \r\n西洋菜 ... 少许 \r\n奶油 ... 1大匙 \r\n吉士丝 ... 3大匙\r\n调味料\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4748, 103, '紫恋浓汤', '紫色高丽菜切细丝备用。\r\n用奶油起油锅，将面粉炒香，加入 2杯水稀释，放入紫色高丽菜、火腿末、盐煮滚即可。', NULL, NULL, NULL, '\r\n紫高丽菜 ... 少许 \r\n洋火腿末 ... 少许 \r\n面粉 ... 1大匙 \r\n奶油 ... 1大匙\r\n调味料\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4749, 103, '豆豉翠玉管', '空心菜去叶留茎切段、红辣椒切细末备用。\r\n起油锅，加入油葱，先将豆鼓爆香，再加入空心菜茎及红辣椒末，大火爆炒 1分 30 秒即可。\r\n \r\n备注：\r\n空心菜叶因不能久放，所以可将空心菜的叶、稆懦}，先吃叶的部份，改天再炒空心菜茎，又是个新菜色。', NULL, NULL, NULL, '\r\n空心菜 ... 1/2把 \r\n豆豉 ... 少许 \r\n红辣椒 ... 少许\r\n调味料\r\n 油葱 ... 1大匙');
INSERT INTO `recipe` VALUES (4750, 103, '蠔油青花菜', '青花菜切小块、洗净，用沸水烫 2分钟，捞起。\r\n将所有调味料加水 1/2杯搅匀，倒入锅中用小火煮开，煮时要不停搅动，到调味汁勾成薄芡时，淋在青花菜上即可。', NULL, NULL, NULL, '\r\n青花菜 ... 1小个\r\n调味料\r\n 蚝油 ... 1大匙\r\n 酱油 ... 1大匙\r\n 太白粉 ... 1大匙');
INSERT INTO `recipe` VALUES (4751, 103, '清蒸鳕鱼', '鳕鱼洗净放入盘内，上铺葱丝、姜丝、红辣椒丝，再撒上豆豉，放入锅中隔水大火蒸 8 分钟(如用微波炉可用强微波 3分钟)。\r\n将蒸鱼汁及酒淋在蒸好的鳕鱼上，再用大火蒸 4分钟即可(如用微波炉可用强微波 1分钟)。\r\n \r\n备注：\r\n1.不用蒸鱼汁，加盐亦可。\r\n2.最后再加蒸鱼汁（或盐），可防鱼肉水分流失使肉质变老。', NULL, NULL, NULL, '\r\n鳕鱼 ... 1片 \r\n葱丝 ... 少许 \r\n姜丝 ... 少许 \r\n豆豉 ... 少许 \r\n红辣椒 ... 少许\r\n调味料\r\n 蒸鱼汁 ... 1包\r\n 酒 ... 1大匙');
INSERT INTO `recipe` VALUES (4752, 103, '贡丸汤', '在碗中放芹菜末及白胡椒粉备用。\r\n锅中加水 2杯，放入贡丸及盐煮至水沸，盛入碗中淋上麻油即可。', NULL, NULL, NULL, '\r\n贡丸 ... 6粒 \r\n芹菜末 ... 1大匙\r\n调味料\r\n盐 ... 1小匙\r\n白胡椒粉 ... 少许\r\n 麻油 ... 少许');
INSERT INTO `recipe` VALUES (4753, 103, '蕃茄炒蛋', '蛋加盐 1/2小匙打成蛋汁、蕃茄切小块备用。\r\n起油锅，入油 2大匙将油烧热，倒入蛋汁快速搅散，等到蛋汁半凝结时，倒下蕃茄块及盐 1/2小匙、糖改为小火略为拌炒，至蕃茄变软撒下葱花，盖锅盖焖 30 秒即成。\r\n \r\n备注：\r\n这是一道人人会做的菜，但其中有些诀窍必须注意：不可先下蕃茄再下蛋汁，否则外观很难看；亦不可将蛋汁煎成蛋饼，再下蕃茄，如此口味就差了。', NULL, NULL, NULL, '\r\n蕃茄 ... 1个 \r\n蛋 ... 2个 \r\n葱花 ... 少许\r\n调味料\r\n 盐 ... 1小匙\r\n 糖 ... 1小匙');
INSERT INTO `recipe` VALUES (4754, 103, '酱瓜肉', '酱瓜切成细末倒入绞肉内，并加入酱瓜汁、盐、酒及水 1/2杯搅拌均匀。\r\n将拌好的料倒入浅盘内，并把咸蛋黄排在肉酱上，放入微波炉用强微i加热 7 分钟即可。\r\n \r\n备注：\r\n用电饭锅或隔水蒸要蒸 15分钟左右。', NULL, NULL, NULL, '\r\n绞肉 ... 200公克 \r\n酱瓜 ... 2大匙 \r\n咸蛋黄 ... 2个\r\n调味料\r\n 酱瓜汁 ... 3大匙\r\n 酒 ... 1大匙\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4755, 103, '豌豆肉末', '绞肉加酱油及太白粉拌匀。\r\n起油锅，入油 1大匙，大火爆炒绞肉至肉末变色，加入豌豆、盐及水 2大匙，改用中火快速翻炒均匀即可。', NULL, NULL, NULL, '\r\n豌豆 ... 1/2杯 \r\n绞肉 ... 50公克\r\n调味料\r\n 酱油 ... 1/2大匙\r\n 太白粉 ... 1/2大匙\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4756, 103, '生炒花枝', '花枝切斜十字花纹再切块，芹菜切段、红萝卜切片备用。\r\n盐、太白粉、糖、醋、加水 5大匙调成料汁。\r\n起油锅，入油 2大匙，先下芹菜、红萝卜片及盐略炒，再下花枝块约炒 30 秒，待花枝卷起十字花纹明显时，倒入料汁勾芡，至汤汁略为浓稠，立刻熄火。\r\n \r\n备注：\r\n这是一道火候菜，时间控制非常重要，花枝肉质易老，不可炒太久。', NULL, NULL, NULL, '\r\n花枝 ... 1支 \r\n芹菜 ... 少许 \r\n红萝卜 ... 少许\r\n调味料\r\n 盐 ... 1/2小匙\r\n 太白粉 ... 1大匙\r\n 糖 ... 1小匙\r\n 白醋 ... 少许');
INSERT INTO `recipe` VALUES (4757, 103, '烤鸡腿', '将所有调味料拌匀，把鸡块放入腌 20 分钟。\r\n腌好的鸡块放在烤盘上，送入烤箱烤 5分钟，翻面再烤 5分钟即可。\r\n \r\n备注：\r\n1.腌整支鸡腿或厚鸡排要多点时间才会入味。\r\n2.烤大块的鸡肉每面要 7 至 8 分钟，才会熟透。', NULL, NULL, NULL, '\r\n鸡腿 ... 2只(或棒棒腿切块)\r\n调味料\r\n 酱油 ... 3大匙\r\n 糖 ... 1大匙\r\n 五香粉 ... 少许\r\n 八角粉 ... 少许');
INSERT INTO `recipe` VALUES (4758, 103, '香菜肉末', '香菜洗净、荸荠去皮皆切细末备用。\r\n绞肉加酱油、太白粉搅拌均匀。\r\n起油锅，入油 1大匙，先将绞蛌ㄣ瓷A下荸荠末翻炒 30 秒，再下香菜末、盐、红辣椒末，用大火爆炒 30 秒。', NULL, NULL, NULL, '\r\n香菜 ... 1把 \r\n荸荠 ... 5粒 \r\n绞肉 ... 100公克 \r\n红辣椒末 ... 适量\r\n调味料\r\n 盐 ... 1/2小匙\r\n 酱油 ... 1/2大匙\r\n 太白粉 ... 1/2大匙');
INSERT INTO `recipe` VALUES (4759, 103, '洋葱牛肉汤', '洋葱切片、马铃薯切滚刀块、蕃茄一分为四备用。\r\n牛肋条切小块，用沸水烫煮一滚，去血水换清水 5杯。\r\n先放入洋葱、姜片，用中火煮 30 分钟，再下马铃薯块、蕃茄块、盐煮 20 分钟，改用小火加酒焖 10分钟。\r\n \r\n备注：\r\n如用电饭锅煮可一次将所有材料放入外锅放 3杯水。', NULL, NULL, NULL, '\r\n牛肋条 ... 300公克 \r\n姜 ... 5片 \r\n洋葱 ... 1个 \r\n马铃薯 ... 1个 \r\n蕃茄 ... 1个\r\n调味料\r\n 盐 ... 1小匙\r\n 酒 ... 2小匙');
INSERT INTO `recipe` VALUES (4760, 103, '蛤仔汤', '蛤仔洗净，放在清水内吐沙。\r\n锅中加水 2杯煮沸，放入蛤仔及姜丝、盐、酒，煮至蛤仔的壳张开，立即熄火。\r\n盛入碗内，淋少许麻油即成。', NULL, NULL, NULL, '\r\n蛤仔 ... 200公克 \r\n姜丝 ... 1大匙\r\n调味料\r\n盐 ... 1小匙\r\n酒 ... 1大匙\r\n 麻油 ... 少许');
INSERT INTO `recipe` VALUES (4761, 103, '皇宮菜', '皇宫菜洗净切段，放入微波餐盒(或耐热胶袋)内，用微波炉强微波 2分钟。\r\n起油锅，加入油葱及盐，至油八分热，倒入皇宫菜，大火爆炒 1分钟即可。\r\n \r\n备注：\r\n亦可将皇宫菜及所有调味料一次拌匀，用微波炉强微波 3分钟即可。', NULL, NULL, NULL, '\r\n皇宫菜 ... 1/2把\r\n调味料\r\n 盐 ... 1/3小匙\r\n 油葱 ... 2大匙');
INSERT INTO `recipe` VALUES (4762, 103, '茶碗蒸', '香菇泡发备用。蛋加盐及水 1杯打成稀蛋汁，分盛在两只小碗(或一个大的深盘)内，放进微波炉内，用中微波加热 5分钟，至蛋汁凝固。若用隔水蒸的方式，须用小火] 12分钟。\r\n再将其它材料放在蒸蛋上，用中微波加热 2分钟，淋上麻油即可。若用隔水蒸则需 5分钟。\r\n \r\n备注：\r\n蛋汁先加热至凝固，再放入其它材料，可避免所有材料都沉在蛋汁内，比较美观。', NULL, NULL, NULL, '\r\n蛋 ... 2个 \r\n香菇 ... 2朵 \r\n虾仁 ... 4只 \r\n蛤仔 ... 2个 \r\n豌豆 ... 少许\r\n调味料\r\n 盐 ... 1/2小匙\r\n 麻油 ... 少许');
INSERT INTO `recipe` VALUES (4763, 103, '虾仁炒蛋', '虾仁去肠泥、洗净，用毛巾吸干多余水分，用盐 1/2小匙、太白粉拌腌 10分钟。\r\n蛋打成蛋汁加盐 1/2小匙搅匀。\r\n起油锅，入油 2大匙，用大火将虾仁炒至变色，淋下蛋汁，快速搅拌至蛋汁凝固即可。\r\n \r\n备注：\r\n用搅散的方法，蛋的质感较嫩、颜色亦美，不要做成蛋饼或等到焦黄才起锅。', NULL, NULL, NULL, '\r\n虾仁 ... 100公克 \r\n蛋 ... 2个\r\n调味料\r\n 盐 ... 1小匙\r\n 太白粉 ... 1大匙');
INSERT INTO `recipe` VALUES (4764, 103, '烤牛小排', '将所有调味料拌匀，放入牛小排腌 20 分钟。\r\n放烤盘上烤 5分钟，翻面再烤 5分钟即可。', NULL, NULL, NULL, '\r\n牛小排 ... 2~3块\r\n调味料\r\n 酱油 ... 2大匙\r\n 迷迭香粉 ... 少许\r\n 糖 ... 1小匙\r\n 姜汁 ... 1大匙');
INSERT INTO `recipe` VALUES (4765, 103, '甜豆贡丸', '甜豆拓洗干净、贡丸切细长条、红辣椒切细丝备用。\r\n起油锅，入油 1大匙，爆甜豆至八分熟，放入盐、贡丸条、红辣椒及水 2大匙，用小火焖煮 2分钟拌匀起锅', NULL, NULL, NULL, '\r\n大贡丸 ... 3个 \r\n甜豌豆 ... 150公克 \r\n红辣椒 ... 1支\r\n调味料\r\n 盐 ... 1/2小匙');
INSERT INTO `recipe` VALUES (4766, 103, '沙茶牛蒡', '取一水盆装满水，牛蒡去外皮后，用刨刀刨成细丝，且要刨在水盆里，至水变铁绣色时，就必须再换清水，如此才能保持牛蒡原色。\r\n起油锅，入油 1大匙，将沥干的牛蒡倒入锅内用大火爆炒 2分钟，再加入盐、沙茶酱、葱花下锅拌炒均匀即成。', NULL, NULL, NULL, NULL);
INSERT INTO `recipe` VALUES (4767, 103, '干煸胡萝卜丝', '胡萝卜刨丝备用。\r\n绞肉加酱油及太白粉拌匀。\r\n起油锅，入油 2大匙，将绞肉下锅炒至变色，再下胡萝卜丝及盐，用小火煸炒至胡萝卜丝变n，放出红油即可。', NULL, NULL, NULL, '\r\n红萝卜 ... 1根 \r\n绞肉 ... 100公克\r\n调味料\r\n 盐 ... 1/2小匙\r\n 太白粉 ... 1/2大匙\r\n 酱油 ... 1/2大匙');
INSERT INTO `recipe` VALUES (4768, 103, '咕咾肉', '小里肌肉切成 2公分见方的肉丁，用盐 1/2小匙腌 15分锺，洋葱、青椒切小块备用。\r\n蕃茄酱加太白粉 1/2大匙及水 3大匙做为酱汁备用。\r\n起油锅，入油 2大匙，将肉丁沾太白粉放入油中炸至外表金黄捞出。\r\n用锅中剩余的油，放入洋葱、青椒、盐 1/2小匙略炒，倒入肉丁狰炒均匀，再加入酱汁勾薄芡即成', NULL, NULL, NULL, '\r\n里脊肉 ... 250公克 \r\n洋葱 ... 1/2个 \r\n青椒 ... 1/2个\r\n调味料\r\n 盐 ... 1小匙\r\n 蕃茄酱 ... 5大匙\r\n 太白粉 ... 2大匙');
INSERT INTO `recipe` VALUES (4769, 103, '奶油蘑菇浓汤', '蘑菇切片、洋火腿切细末备用。\r\n用奶油起油锅，用小火融化奶油，加入面粉炒香，再加入热水 2杯、蘑菇片、豌豆、盐煮沸后熄火，撒上少许火腿末即可。', NULL, NULL, NULL, '\r\n奶油 ... 2大匙 \r\n面粉 ... 2大匙 \r\n蘑菇 ... 6个 \r\n豌豆 ... 1大匙 \r\n洋火腿 ... 少许\r\n调味料\r\n 盐 ... 1小匙');
INSERT INTO `recipe` VALUES (4770, 103, '干炒牛河', '牛肉片加入酱油及太白粉腌 10分钟。\r\n高丽菜、红萝卜、葱切丝备用。\r\n河粉蝼荓瓥&#63157;峞C\r\n起油锅，入油 2大匙，将牛肉片大火炒至变色，加入高丽菜丝、红萝卜丝、葱丝拌炒均匀，再加入河粉，改用小火略炒至河粉变软，加少许麻油即可。\r\n \r\n备注：\r\n1.牛肉片改成猪肉片亦可。\r\n2.河粉汤的做法与上述步骤相同，只是在加入河粉的同时注入煮滚的高汤，大火煮一滚，即可起锅。', NULL, NULL, NULL, '\r\n河粉 ... 200公克 \r\n牛后腿肉片 ... 100公克 \r\n葱 ... 2支 \r\n高丽菜 ... 少许 \r\n红萝卜 ... 少许\r\n调味料\r\n 盐 ... 1/2小匙\r\n 酱油 ... 1/2大匙\r\n 太白粉 ... 1/2大匙\r\n 麻油 ... 少许');
INSERT INTO `recipe` VALUES (4771, 103, '杏子梅干巧克力蛋糕', '1. 提前将杏子、梅干及核桃在白兰地中浸泡一夜\r\n2. 将黄油与糖一起搅拌并一点点加入鸡蛋中搅拌\r\n3. 将杏仁粉、盐、丁香、肉桂及蛋糕烘烤45分钟\r\n4. 加入香草油于刚才的混合物中搅拌均匀\r\n5. 用180摄氏度火力将蛋糕烘烤45分钟\r\n6. 用杏子酱和一碟杏仁糖覆盖在蛋糕上并用黑巧克力装饰\r\n7. 用杏仁糖制成圣诞糖果，最后撒上冰糖', NULL, NULL, NULL, '\r\n干果几种：杏子、梅子、烤核桃、白兰地\r\n杏仁粉、杏子酱、杏仁糖、黄油、糖、鸡蛋、盐、丁香末、香草油、面包粉、鸡蛋清、黑巧克力、奶油与巧克力的混合物');
INSERT INTO `recipe` VALUES (4772, 103, 'Cassis圆木型蛋糕', '1. 将蛋糕模具用瑞士圈装点\r\n2. 将鸡蛋黄、鸡蛋、糖和肉桂加入少量沸水搅拌\r\n3. 将加热的鸡蛋混合物放入机器中并搅拌至冷却\r\n4. 将食用胶浸入水中并加入Cassis油\r\n5. 将深化了的食用胶与冷却了的起泡鸡蛋混合物充分搅拌\r\n6. 放些小型白瑞士圈在项部并浸入糖浆\r\n7. 再次加上另一些Cassis油在顶部配上瑞士圈并重复3次\r\n8. 将巧克力覆盖在最后一层上，放入冰箱直至凝固', NULL, NULL, NULL, '\r\n少量红色瑞士装饰卷，及少量折色瑞士圈，分别切成：5厘米*52厘米，7厘米*52厘米，9厘米*52厘米，10.5厘米*52厘米\r\n鸡蛋黄8个，鸡蛋2个，糖、肉桂泥，食用胶、Cassis油、起泡奶油、糖浆，黑巧克力。');
INSERT INTO `recipe` VALUES (4773, 103, '提拉米苏', '1、将蛋黄、糖放入盒中，用电动打蛋器以高速打到略微发白。 \r\n2、加入起司、柠檬皮屑。 \r\n3、将蛋白放在干净的盒中打发，加一些糖后继续搅拌后，再拌入已拌好的起司料中。 \r\n4、浓缩咖啡加酒混合，将淑女手指饼干浸湿后排放于杯底。 \r\n5、铺上一层起司料后，再放一层用咖啡沾湿的淑女手指饼干，以做为底部倒扣盘中时的底用，放入冰箱约一小时后取用。 \r\n6、倒扣盘中撒上可可粉即可食用，亦可加上芒果，口感更加浓郁，让人回味无穷。 \r\n\r\n具体操作： \r\na.持续搅拌蛋黄至微白为止 b.加入马士可邦起司于蛋黄之中 \r', NULL, NULL, NULL, '\r\n1、蛋黄3-4个 \r\n2、糖3大匙 \r\n3、马士可邦起司（Mascarpare Chese)一盒约500g \r\n4、柠檬皮屑1茶匙 \r\n5、蛋白1-2个 \r\n6、意大利浓缩咖啡2/3杯 \r\n7、酒2大匙（可用白兰地、白酒、莱姆酒、意大利玛撒拉甜酒） \r\n8、淑女手指饼干（Lady Fingers)约14片');
INSERT INTO `recipe` VALUES (4774, 103, '香草冰淇淋', '1.蛋黄倒入盆内用搅拌器打散；将砂糖与玉米粉混匀，再倒入盆内与蛋黄混合，搅拌至呈乳黄色。\r\n2.牛奶以平底锅加热后，慢慢倒入（1）中，并保持搅拌。\r\n3.将（2）倒回平底锅，以中火煮，边煮边用勺子搅拌，呈浓稠状时，即可离火，冷却冷藏。\r\n4.当（3）冰冻后，倒入香草精、奶油拌匀。\r\n5.将（4）倒入宽浅的模具内，送入冰箱冷冻。当液面结层冰时，即可取出用搅拌器拌碎再放入冷冻室。如此反复数次，直到呈雪泥状即可。', NULL, NULL, NULL, '\r\n蛋黄5个、砂糖125克、玉米粉10克、牛奶500克、香草精少许、奶油150克。');
INSERT INTO `recipe` VALUES (4775, 103, '夏威夷小炒', '1. 用2汤匙油炒熟鸡肉，加入甘笋及西芹，炒约1分钟。\r\n2. 拌入芡汁、菠萝及夏威夷果仁，炒匀至热透。', NULL, NULL, NULL, '鸡肉 300 克 [10 安士/8 两] [切粒], 甘笋 50 克 [2 安士/1.5 两] [切粒], 西芹 1 条 [切粒], 罐装菠萝 2 片 [切块], 夏威夷果仁 3 汤匙芡汁 : 水 5 汤匙, 粟粉 1/2 茶匙\r\n旧庄特级蚝油 1 包 [45 克/1.6 安士]');
INSERT INTO `recipe` VALUES (4776, 103, '豆腐芝士饼', '(1)鱼胶粉用水开匀，放于热水座溶；豆腐隔去多余水分，然后压成蓉。\r\n(2)迷你蛋糕切薄片，排放在已垫纸的糕盆内。\r\n(3)忌廉芝士打滑，加入豆腐蓉、酸忌廉及柠檬汁，加入鱼胶粉水拌匀。\r\n(4)将芝士料倒入糕盆内，放进雪柜雪至凝固，最后加上柠檬片作装饰即可。', NULL, NULL, NULL, '忌廉芝士250克,盒装豆腐2/3件,酸忌廉半杯,鱼胶粉2汤匙,冷开水1/4杯,柠檬汁1汤匙,迷你柠檬蛋糕卷2件,青柠及柠檬片数块（装饰用）');
INSERT INTO `recipe` VALUES (4777, 103, '草莓雪糕', '、在盆中打入鸡蛋，加30克砂糖搅拌，鸡蛋液汁泛白时加入玉米粉精心搅拌； \r\n2、在锅中将其余的砂糖和鲜奶油、牛奶混合，用木勺搅拌加热直到将近煮开；\r\n3、将锅从为上移离，一边搅拌一边一点一点地加入鸡蛋液，搅匀后将锅移至文火上加热直至粘稠；\r\n\r\n冷却后放入草莓\r\n4、将锅置于冰水中搅拌冷却，加入云呢拿香油搅匀，倒入冰盒中放入冰箱冷冻格冷冻1-2小时；\r\n5、在固化过程中反复搅拌冷冻3-4次，在最后一次搅拌时加入切碎的草莓，搅匀冷冻。', NULL, NULL, NULL, '\r\n鸡蛋:1个　　　　白砂糖:80克　　　 玉米粉:1汤匙　　　　 \r\n鲜奶油:3/4杯　　云呢拿香油:少许　 草莓:150克\r\n牛奶:1杯　　　　装饰物:起泡奶油、薄荷叶、草莓　');
INSERT INTO `recipe` VALUES (4778, 103, '橘子果冻', '1. 明胶加 4杯凉（32克）的水泡涨。\r\n2. 将砂糖加入 200 c.c.的水内煮溶至沸腾。\r\n3. 将橘子的蒂切掉三分之一，挖掉果肉但切忌破坏外皮的形状。将果肉炸成果汁，橘子皮要保留。\r\n4. 将（1）的明胶加入（2）的糖水内混匀，但不要搅拌至起泡。\r\n5. 待（4）凉到微温时，加入（3）的橘子汁和少许柠檬汁混匀。\r\n6. 待（5）完全冷却时倒入（3）的已挖掉果肉的橘子皮里，再置於冰箱里 4小时，切成半月形盛入盘，放上薄荷叶 作装饰（随个人喜好）。', NULL, NULL, NULL, '橘子 3--4个，明胶（凝胶）8克，砂糖50克，水200c.c.柠檬汁少许，薄荷叶（妆饰用）少许');
INSERT INTO `recipe` VALUES (4779, 103, '吉列生蚝', '（1）生蚝洗净去壳，用盐及粟粉拌匀，稍腌，再冲洗干净，放滚水中焯至变色，即捞出沥干。 \r\n（2）鸡蛋打散，下酒再搅拌。 \r\n（3）生蚝依次蘸上淀粉、鸡蛋、面包糠，放入中温滚油中炸至金黄色，沥干油上碟。 \r\n（4）生菜叶撕开、洗净、修剪成供食大小，与柠檬块拌食。汁料调匀蘸食。柠檬片、樱桃饰碟。', NULL, NULL, NULL, '材料：中等生蚝600克，鸡蛋1个，面粉、面包糠各2汤匙，酒、盐、粟粉各1茶匙，生菜1棵，柠檬1个，柠檬片1片，樱桃2片。 汁料：蚝油、番茄酱各1汤匙，生抽1/2汤匙，柠檬汁1茶匙');
INSERT INTO `recipe` VALUES (4780, 65, '干蒸黄鱼', '黄鱼洗净，两侧剞一字花刀，用料酒、盐、葱姜、胡椒粉腌半小时；另锅下油，煸炒肉丝，下泡辣椒丝、葱姜丝煸炒，再放入香菇丝、冬笋丝、榨菜丝，加酱油、胡椒粉、料酒、味精炒匀，出锅后浇在鱼上；上笼蒸熟，取出后在表面撒葱丝，浇些热油即成。', NULL, NULL, NULL, '黄鱼2尾约1000克、肉丝100克、泡辣椒丝25克、葱姜丝、香菇丝、冬笋丝、榨菜丝25克');
INSERT INTO `recipe` VALUES (4781, 65, '碧绿虾仁', '虾仁加少许碱面腌制，然后用水冲去碱，用毛巾沾干水分；加盐、胡椒、蛋清、干淀粉上浆，用油滑熟，控去水；锅内留底油，下葱姜末、虾仁、鲜豌豆稍炒，倒入事先用水淀粉、盐、胡椒、清汤兑成的汁；翻炒装盘，用炒豆苗围边即成。', NULL, NULL, NULL, '鲜碗豆50克、虾仁150克、豌豆苗200克、蛋清1个');
INSERT INTO `recipe` VALUES (4782, 65, '小笼粉蒸牛肉', '牛肉去筋切片；加豆瓣、醪糟汁、酱油、花椒粉，辣椒粉，蒜泥及米粉拌均；上笼蒸至软烂；加调料，香菜上桌即成。', NULL, NULL, NULL, '牛肉约500克、五香米粉75克、香菜50克');
INSERT INTO `recipe` VALUES (4783, 65, '酸辣汤', '1.将豆腐、冬菇、海参、鱿鱼分别切成细丝，同熟肉丝、熟鸡丝放入锅内，加鸡汤、精盐、味精、酱油，用旺火烧至沸滚，再放湿淀粉勾芡后，改小火加打散的鸡蛋；\r\n\r\n2.将胡椒粉、醋、葱花及少许猪油放入汤碗内。在锅内蛋花浮起时即改旺火，至肉丝滚起，冲入汤碗内即可。', NULL, NULL, NULL, '豆腐30克，熟鸡肉（或火腿）、冬菇、熟瘦猪肉丝、水发海参、水发鱿鱼各15克，鸡蛋1个，淀粉25克，葱花3克，酱油10克，猪油30克，味精、胡椒粉各1克，香醋6克，精盐5克，鸡汤750克。');
INSERT INTO `recipe` VALUES (4784, 65, '龙须牛肉', '1.牛肉洗净切成较大薄片，\r\n2.加盐、曲酒、姜、葱码味6小时，入笼内蒸熟，出笼晾凉，撕成细丝。 \r\n3.将牛肉丝放入油锅内炸酥捞出，加入白糖、味精、红油辣椒、花椒油、香油拌匀，装盘即成。', NULL, NULL, NULL, '鲜牛肉、\r\n盐、曲酒、姜、葱、\r\n红油辣椒、花椒油、白糖、\r\n味精、香油、熟菜油。');
INSERT INTO `recipe` VALUES (4785, 65, '三杯鸡', '1.将嫩子鸡洗净剁成5公分见方的块连同鸡脏、肝全部装入砂钵内,同时用容量80克左右的杯盏,量入酱油、食油、米酒,放入姜块、葱白段少许,不放水； \r\n2.用炭火炉子微火炖开,每隔十分钟左右翻动一次,以防烧焦。盖子不宜多开,约炖半小时至卤汁收浓即成。拣去葱、姜,加入少量小麻油即可。', NULL, NULL, NULL, '嫩子鸡一只, \r\n酱油、食油、米酒各一杯, \r\n姜块,葱,小麻油');
INSERT INTO `recipe` VALUES (4786, 65, '炒豌豆夹', '1.将豌豆夹洗净，加少许盐爆腌；姜、葱切末；青椒、红椒切丝。\r\n2.将锅烧热，加油，投入葱姜末煸透，再下青、红椒丝，随即将豌豆夹挤掉水，如果略煸后加白酱油、糖、醋，继续煸炒至熟，放少许味精、麻油即可。', NULL, NULL, NULL, '豌豆夹500克，姜、葱、盐、青椒、红椒、白酱油、糖、醋、味精、麻油各适量。');
INSERT INTO `recipe` VALUES (4787, 65, '辣椒鱼', '1.将鱼收拾洗净加腌料腌半小时泡油；\r\n2.下油二汤匙,爆香葱,辣椒粉,下鱼,下酒半汤匙炒匀勾芡,再炒数下上碟；\r\n3.珍珠叶洗净,把叶摘下,抹干,放落滚油中，炸脆放入盘中即可。', NULL, NULL, NULL, '鲜鱼一条,珍珠叶六两,葱二条切碎,辣椒粉半汤匙糖一茶匙,生抽二汤匙,盐半茶匙姜汁半汤匙,老抽一汤匙,蛋白一汤匙半,生粉一汤匙,油一汤匙。');
INSERT INTO `recipe` VALUES (4788, 65, '烟熏鸭子', '1.排骨3根相连横斩成大块，加盐、姜片、葱段、五香粉、料酒、花椒拌匀码味20分钟，入笼蒸至刚熟取出，入卤水锅中加热至肉能离骨时取出，再放入热油锅中炸至色泽金黄、肉质干香时起锅。\r\n2.炸好的排骨放入熏炉中，点燃烟熏料，熏至排骨色暗红、烟香入味时取出，刷上香油，斩成小块装盘。', NULL, NULL, NULL, '猪排骨、五香粉、盐、料酒、姜、葱、醪糟汁、卤水、烟熏料、香油、花椒、熟菜油。');
INSERT INTO `recipe` VALUES (4789, 65, '香辣炒蟹', '1.活肉蟹从腹脐处取壳，去净内脏及鳃叶，宰去腿尖及壳沿，洗净后，将蟹斩成八块，加入适量精盐，料酒拌匀。\r\n2.锅置旺火上，烧精炼油至五成油温，然后将蟹块斩口处粘裹上干细淀粉，入油锅内浸炸至熟(蟹壳同时成熟)。\r\n3.锅内另加油，烧至四成油温，投入干辣椒节，花椒炒香，掺入鲜汤，略烧片刻，再下姜、葱蒜、海蟹，最后放入精盐、料酒、海鲜酱、鸡精烧约2分钟后，用水淀粉收薄芡，最后加入香油、花椒油、辣椒油、胡椒粉翻匀即可装盘。 \r\n\r\n特 点：\r\n　　色泽红亮，鲜香麻辣，味浓厚。', NULL, NULL, NULL, '活肉蟹、干辣椒节、花椒、姜片、蒜片、葱节、精盐、胡椒粉、料酒、干细淀粉、海鲜酱、水淀汾、鸡精、香油、花椒油、辣椒油、精炼油、鲜汤。');
INSERT INTO `recipe` VALUES (4790, 65, '春蚕吐丝', '1.鸡脯洗净去筋、皮，捶茸后，排筋、剁细，放入盆内，依次加入鲜汤、蛋清、精盐、料酒、胡椒粉、味精、冻化猪油顺一方向搅打成“鸡糁”。\r\n2.虫草、竹荪洗净，用清汤涨发入味；将胡萝卜、菜花及竹荪菌盖制成如图所示的形状，入沸水中氽断生，摆入圆盘中备用。\r\n3.将鸡糁灌入竹荪中，然后用绳打结，制成蚕茧形状，上笼用小火蒸制成熟，用刀修成形即可备用。\r\n4.锅置中火上，掺入鲜汤，放入精盐、料酒、味清调味后，投入虫草，蚕茧同烧片刻，用水淀粉收薄芡，淋入鸡化油，装入盘中即成。', NULL, NULL, NULL, '鸡脯、虫草、竹荪、胡萝卜、菜花、精盐、料酒、蛋清、胡椒粉、水淀粉、味精、冻化猪油、鲜汤、精炼油、鸡化油。');
INSERT INTO `recipe` VALUES (4791, 65, '烟熏排骨', '1.排骨3根相连横斩成大块，加盐、姜片、葱段、五香粉、料酒、花椒拌匀码味20分钟，入笼蒸至刚熟取出，入卤水锅中加热至肉能离骨时取出，再放入热油锅中炸至色泽金黄、肉质干香时起锅。\r\n2.炸好的排骨放入熏炉中，点燃烟熏料，熏至排骨色暗红、烟香入味时取出，刷上香油，斩成小块装盘。', NULL, NULL, NULL, '猪排骨、五香粉、盐、料酒、姜、葱、醪糟汁、卤水、烟熏料、香油、花椒、熟菜油。');
INSERT INTO `recipe` VALUES (4792, 65, '八宝黑米粥', '桃仁去皮切丁，瓜元切丁，红枣去核，莲米、苡仁、芡实、花仁、百合用水涨发待用；黑米用清水洗净，加少量紫糯米放入锅中，加清水烧沸，将八宝料放入，移小火上煮约二小时，煮时注意不时用勺搅动，以免煳锅，待质浓糯软时放入压碎的冰糖，糖溶化后装碗即成。 \r\n　 \r\n特 点：\r\n八宝黑米粥色紫黑，质软糯，味甜香。', NULL, NULL, NULL, '莲米、苡仁、芡实、花仁、桃仁、百合、蜜樱桃、瓜元、红枣等');
INSERT INTO `recipe` VALUES (4793, 65, '甜水面', '用上等面粉加盐、水揉匀静置半小时，面杖擀成0.6厘米厚的面皮，切成0.6厘米宽的面条，然后两手抓住面条两头(每次5-6根)，用力扯长，待面条变为0.4厘米粗细时放入沸水锅中煮熟，捞出抖散晾冷，洒熟菜油少许拌匀；辣椒油、复制甜红酱油、蒜泥、芝麻油、酱油调成味汁。吃的时候将面条放沸水中烫热，捞入碗中，淋味汁即成。\r\n　 \r\n特 点：\r\n甜水面的面条较粗，具有筋力，风味独特。', NULL, NULL, NULL, '面粉、盐');
INSERT INTO `recipe` VALUES (4794, 65, '青菠面', '上等面粉加菠菜汁和鸡蛋清调成极硬的浅绿色水调面团，揉熟后用手工擀成面条，对叠多次，切成柳叶形或细面条。青菠面有多种吃法，一是煮熟后置清汤中成为清汤青菠面，其特点是汤清面绿，清淡爽口；二是加奶汤制之，其特点是汤白面绿，配色自然，营养丰富；其三，施以各种面臊，如三鲜、海参、鲍鱼、酸菜鱿鱼等等，其中以三鲜和酸菜鱿鱼面臊最为常用。酸菜鱿鱼面臊用四川泡青菜同鱿鱼丝、肉丝同烹，味咸中带酸，鲜美爽滑，为夏季佳品。\r\n　 \r\n特 点：\r\n色泽碧绿，面条筋而韧，绵而滑，汤鲜味美，流行于川西地区。', NULL, NULL, NULL, '面粉、菠菜汁、鸡蛋清');
INSERT INTO `recipe` VALUES (4795, 65, '鸡丝凉面', '面条入沸水中煮熟。煮时不能煮得太软，捞出放案板上趁热,洒熟菜油少许，抖散快速降温，到互不粘连且己冷却为止，成为凉面。\r\n凉面吃法多样。(1)绿豆芽凉面，绿豆芽在沸水中煮断生，保持脆嫩，放碗中，面上盖凉面，淋上用辣椒面、花椒油、醋、味精、酱油、蒜泥和麻油调成的味汁；(2)鸡丝凉面，基本制法同绿豆芽凉面，不同的是在面上加煮熟后撕成的鸡丝；(3)三丝凉面，面上加熟鸡丝、火腿丝、猪肉丝。 \r\n　 \r\n特 点：\r\n多味调合，清爽利口，面条有筋力，为夏季佳品。', NULL, NULL, NULL, '黄豆、红苕淀粉、石膏水、酱油、醋、辣椒面、味精、芽菜末、油酥黄豆、大头菜末、葱花');
INSERT INTO `recipe` VALUES (4796, 65, '火锅牛肉粉', '制作火锅牛肉粉首先要制好臊子，其方法是：黄牛肉切成小块，放热油中炒香，放入豆瓣，炒至油红，加大蒜、鲜汤、竹笋丁、盐、胡椒、香料和姜葱，用中火烧至牛肉粑软即成臊子。其次是调好用于煮烫粉丝的火锅汤料，其制法是：用牛油和菜籽油各一半，烧热，放豆瓣、豆豉末、姜片、蒜粒炒香，加醪糟汁、鲜汤、香料和用温油炒过的干辣椒节，煮至香味四溢即成。最后，将干红薯粉丝(最好用宽粉条)用清水涨发后淘洗干净，用竹制面篼装上，放入沸腾的火锅汤料内烫热装碗内，浇上牛肉臊子即为成品。\r\n　 \r\n特 点：\r\n因用火锅汤料煮烫粉丝而产生浓烈的', NULL, NULL, NULL, '黄牛肉、豆瓣、大蒜、鲜汤、竹笋丁、盐、胡椒、香料、姜葱、醪糟汁');
INSERT INTO `recipe` VALUES (4797, 65, '五柳鲜鱼', '1、 熟笋冲净，香菇泡软，胡萝卜去皮、洗净，嫩姜洗净，红辣椒去蒂及籽、洗净，均切丝；葱洗净，切段；香菜洗净，切末备用。鱼去鳞片及内脏，洗净，用刀在鱼身上轻划数刀，放入盘中均匀涂沫A料腌15分钟备用。\r\n2、 锅中倒入3大匙油烧热，爆香葱、姜、红辣椒及香茹，放入熟笋、胡萝卜及B料煮开，加入鱼略煮，捞出，盛入盘中；锅中汤汁继续加热，放入C料勾芡，淋在鱼上身上，即可端出。 \r\n\r\n[特点] \r\n肉鲜嫩,鲜咸适宜', NULL, NULL, NULL, '鲜鱼1条 熟笋1支 香茹3朵 胡萝卜1/2根 嫩姜30克 葱、红辣椒各1支香菜10克\r\n调味料\r\nA料：盐1小匙\r\nB料：盐1小匙 糖1大匙 白醋2大匙 清水1/2杯\r\nC料：淀粉水1小匙');
INSERT INTO `recipe` VALUES (4798, 65, '豆腐鲫鱼', '1、将活鲫鱼刮鳞、抠鳃、剖腹除去内脏，洗净血污；豆腐改条后放入盐开水中氽透捞起。\r\n2、锅上火，下化猪油烧至四成热，放入豆腐、泡红椒、干辣椒粉、姜蒜米等,出色出味后，掺入鲜汤，沸后稍熬，打去渣料，将鱼入锅，再下料酒、糖、醋、味精、盐等，改用小火3-5分钟，把豆腐下锅，烧制入味且鱼已离骨时起锅，将鱼装盘，摆成放射形，下边装入豆腐。锅中原汁置火口上，下生粉放汁，待汁稠发亮后，下葱颗，推匀起锅，挂淋于鱼和豆腐上即成。\r\n营养特点：\r\n豆腐含蛋白质、脂肪、糖类、钙、铁、磷、维生素B1、维生素B2、维生素C等营养素', NULL, NULL, NULL, '鲜活鲫鱼、石膏豆腐、郫县豆瓣、姜蒜米、泡红椒、干辣椒粉、料酒、葱颗、盐、味精、白糖、醋、鲜汤、生粉、化猪油各适量。');
INSERT INTO `recipe` VALUES (4799, 65, '巴国玉米糕', '将嫩玉米粒淘洗干净，放入盆内上笼蒸熟，取出后微冷即加入面粉、精米粉、精玉米粉、吉士粉、泡打粉、白糖和匀，稍饧发片刻，用菊花盏装上（或做成象生状），再上笼用旺火蒸制而成。\r\n\r\n营养特点：\r\n玉米含蛋白质、脂肪、碳水化合物、胡萝卜素、维生素E等成分，与其他谷物比较，玉米维生素的含量最高。中医认为，玉米味甘，性平，具有调中开胃、益肺宁心之功。', NULL, NULL, NULL, '嫩玉米、精米粉、精玉米粉、面粉、吉士粉、泡打粉、奶粉、水白糖各适量。');
INSERT INTO `recipe` VALUES (4800, 65, '蓉城鸳鸯卷', '1. 猪肥膘肉洗净、去皮、切成8cm方块，然后平片成3mm厚薄片10张。\r\n2. 鸭蛋去黄沥净蛋清、用刀拍成薄片待用。\r\n3. 芦笋去皮用开水汆过、漂冷。\r\n4. 取片好之薄片猪肉，两面粘满白糖放入蛋黄、芦笋，裹成条下入蛋液中沾匀，裹上面包糠，下入热油中炸成金黄色起锅改刀，装盘即成。\r\n色泽金黄、外酥内嫩、咸甜兼具。动物蛋白、脂肪含量丰实', NULL, NULL, NULL, '主料 肥膘肉250(g)、鸭蛋黄5(个)\r\n配  料 芦笋5(根)、面包糠200（g）、鸡蛋2(个)、面粉50(g)\r\n调  料 白糖100(g)、精练油1000(g)、（耗50g）');
INSERT INTO `recipe` VALUES (4801, 65, '南荠烧鸭丁', '1、鸭肉切成均匀的丁(1cm见方)，用姜片、葱段、绍酒、盐、胡椒码上味，腌20－30分钟，马蹄切成四瓣，香料洗净用纱布包好，面粉、黄油烤制酥合10个待用。\r\n\r\n2、鸭丁去渣下油中略炸，豆瓣炒香下汤熬五分钟去渣留汁，下入姜、葱、绍酒、香料包，少许酱油，烧开下入鸭丁开后，用中火焖，去掉姜葱、香料包，下入马蹄丁烧一会，下入味精，勾少许水豆粉，用小火收亮，装入酥合，装盘即成。', NULL, NULL, NULL, '主料 仔鸭肉500克\r\n配  料 马蹄150克、面粉250克、黄油200克\r\n调  料 姜15克、葱25克、豆瓣50克、绍酒15克、白糖3克、胡椒1克、 豆粉10克、 酱油5克、味精1克、盐1克、精炼油100克、五香粉2克');
INSERT INTO `recipe` VALUES (4802, 65, '鹿茸牛肉卷', '1、肉洗净切块，片成0.2×6×10公分的大片，用盐、胡椒粉、味精、五香粉、辣椒仔、料酒、姜（切片）、葱（拍破）、松肉粉码味三十分钟备用。\r\n\r\n2、花生米用开水泡后去皮，铡成二粗颗粒，蛋清加豆粉调成浓糊。\r\n\r\n3、蕃茄切成荷叶花瓣，黄瓜加工成绿叶形，鲜藕切成细丝。\r\n\r\n4、条盘一端藕丝蕃茄拼成大荷花，中间放少许生菜丝，另一端摆绿叶。\r\n\r\n5、将肉片上铺鹿茸片裹成元柱形，粘蛋清糊放入碎花仁中均匀粘上一层，待用。\r\n\r\n6、油烧6成热，下入肉卷，炸至花仁酥黄时捞出，装盘即成。\r\n外酥肉嫩，色泽浅黄，香', NULL, NULL, NULL, '主料 精牛肉500克\r\n配  料 鹿茸（片）5克、花仁200克、松肉粉1克、藕100克、鸡蛋清1个、蕃茄2个300克、黄瓜150克\r\n调  料 盐2克、胡椒粉1克、味精1克、五香粉1克、姜10克葱20克、料酒10克、辣椒仔15克、生菜丝75克、干细粉丝20克、精炼油耗100克');
INSERT INTO `recipe` VALUES (4803, 65, '连理双味鱼', '1、桂鱼去内脏、去骨，一半切鱼花10朵每块2公分见方，一半切鱼片（7×4×0.3公分)10张。然后分别将鱼花、鱼片加盐，料酒、姜葱码味待用;将嫩葱叶剁细备用,芝士切成3×3公分方形。\r\n\r\n2、将鱼头去腮，从下巴处切开按平，洗净。鱼的龙骨去肚腹部分修齐，鱼尾修齐备用。\r\n\r\n3、取码味后的鱼片，包入芝士成鱼包，然后裹一层细豆粉，放入全蛋液中裹匀，再捞入细面包糠中粘匀待用。\r\n\r\n4、将码味后的鱼花去姜葱后上笼蒸5分钟，同时取锅放入多量油，待油热后下入裹好的鱼包，炸至内部芝士溶化，表面金黄起锅，倒去多余油，', NULL, NULL, NULL, '主料 桂鱼750克\r\n配  料 芝士50克、鸡蛋清1个、姜15克葱30克、花椒油5克\r\n调  料 盐3克、高汤75克、料酒10克、五香盐3克、豆粉50克、精炼油耗100克');
INSERT INTO `recipe` VALUES (4804, 65, '鲜花白玉鸡片', '1、鸡脯肉洗净制茸，加高汤，蛋清、盐、豆粉调成浆待用。\r\n2、姜、蒜切片，葱切马耳型，菊花折散留花瓣洗净。\r\n3、取平锅小火下鸡浆摊成鸡片，起锅放入鲜汤中（摊完为止），然后上火烧开捞出鸡片，凉冷切成大小均匀的棱形片待用。\r\n4、炒锅上火下精炼油，下姜、蒜片，葱炒香加高汤放盐、料酒、下入鸡片烩入味，再放入一半鲜菊花瓣翻均匀，勾少许豆粉起锅，装入条盘\r\n的一端，另一端装饰花叶，将另一半菊花撒在鸡片即成。', NULL, NULL, NULL, '主料 鸡脯肉250克、鲜菊花100克\r\n配  料 鸡蛋清8个、豆粉60克、姜2克、葱7克、蒜4克\r\n调  料 盐3克、高汤450克、料酒5克、精炼油50克');
INSERT INTO `recipe` VALUES (4805, 65, '白汁鲤鱼', '1.鲤鱼治净，入沸水锅中略烫一下，捞起漂入温水中，去净鱼皮和杂质，在鱼身两面各剞数刀，然后用部分姜片、葱节、精盐、胡椒粉、料酒腌渍20分钟；熟火腿切片；冬笋、香菇、黄瓜均切片，并入沸水锅中焯熟后捞起。\r\n2.将腌渍好的鲤鱼去掉姜葱，盖上猪网油，入笼用旺火蒸熟后取出，去掉猪网油，将鱼放入一盘中，保温待用。\r\n3.炒锅置火上，放入化猪油烧热，先下姜片、葱节炒香，再掺入鲜汤，略煮后捞出姜葱不用，下入熟火腿、冬笋、香菇、黄瓜，调入精盐、胡椒粉、料酒、味精，烧入味后用水豆粉勾薄芡，起锅浇在盘中鲤鱼上即成。\r\n特点：', NULL, NULL, NULL, '鲜活鲤鱼1尾约450克 熟火腿50克 冬笋50克 香菇50克 黄瓜50克 姜片15克 葱节35克 精盐5克 料酒15克 胡椒粉3克 味精3克 水豆粉15克 化猪油50克 猪网油100克 鲜汤适量');
INSERT INTO `recipe` VALUES (4806, 65, '香菇鱼块', '1.净鱼肉切成约3-5厘米大小的块后，用部分姜、葱、精盐、胡椒粉、料酒腌渍20分钟，然后去掉姜葱，用鸡蛋加干淀粉调成全蛋糊，将鱼块拌匀；香菇洗净，入笼蒸熟后取出待用。\r\n2.净锅置火上，放入精炼油烧至六七成热，将鱼块放入锅中炸熟后捞出。\r\n3.锅留底油，先下姜片、蒜片、葱节炒香，再掺入鲜汤，下入鱼块、香菇，调入精盐、胡椒粉、料酒、酱油，烧入味后拣出香菇，摆在圆盘四周，锅内调入味精，用水豆粉勾薄芡，淋入香油，起锅盛入盘中即成。\r\n特点：鱼块酥香，香菇滑嫩，汤味鲜美。', NULL, NULL, NULL, '净鱼肉300克 香菇100克 姜片15克 蒜片15克 葱节30克 精盐4克 酱油5克 胡椒粉2克 料酒30克 味精2克 鸡蛋1个 干淀粉30克 水淀粉10克 香油5克 鲜汤适量 精炼油750克约耗75克');
INSERT INTO `recipe` VALUES (4807, 65, '特味麻辣鱼', '1.乌鱼宰杀后治净，去头、尾及骨刺，取两扇净鱼肉切成蝴蝶片，用精盐、胡椒粉、黄酒、干淀粉抓匀，再淋入少许色拉油，装入盘中待用；鱼头对剖成两半，再斩成块；鱼骨斩成节；胡萝卜、西芹均切细粒。 \r\n\r\n2.炒锅置火上，放入少许色拉油烧热，下入姜片、蒜片、葱节爆香，再下入干辣椒切节、花椒、郫县豆瓣剁细、芜湖百年辣酱炒出红油，接着下入鱼头、鱼尾、鱼骨略炒，烹入黄酒，掺入鲜汤，下入陈皮、胡萝卜粒、西芹粒，调入酱油、胡椒粉、鸡精、味精、五香粉、香醋，煮约１５分钟至出味时，打去料渣，留下的汤汁即为蘸汁，起锅盛入味', NULL, NULL, NULL, '乌鱼1条约600克 胡萝卜100克 西芹100克 姜片10克 蒜片20克 葱节30克 干辣椒10克 花椒5克 郫县豆瓣25克 芜湖百年辣酱50克 精盐、酱油、黄酒、胡椒粉、味精、鸡精、五香粉、陈皮、香醋、干淀粉、鲜汤、色拉油各适量 辣椒油750克');
INSERT INTO `recipe` VALUES (4808, 65, '清蒸江团', '1、将鱼宰杀治净，在鱼身两侧斜剞6～7刀（刀深0．3厘米左右），用精盐2克、绍酒15克腌渍入味。香菇片成薄片。\r\n\r\n2、将腌渍好的鱼沥去血水，放在蒸盘内，把火腿片、香菇片逐一嵌入剖刀处，放上虾米，加精盐2克、绍酒15克、葱、姜、清汤250克，盖上网油，上笼用旺火蒸30分钟，取出捡去葱、姜、网油，将鱼轻轻地滑入汤盘内。\r\n\r\n3、炒锅加清汤1250克，再滗入蒸盘内的原汁，旺火烧开，加味精、胡椒粉，浇入鱼盘内，与姜汁味碟一起上桌。\r\n \r\n特点: 形状美观，肉质细嫩肥美，汤清味鲜，营养丰富。\r\n关键: 鱼必', NULL, NULL, NULL, '岷江江团1条（重约1250克），水发香菇5个，火腿10片，姜片25克，水发虾米10克，猪网油1张，葱段15克，精盐4克，胡椒粉、味精各 1克，绍酒30克，清汤1500克，姜汁二碟。');
INSERT INTO `recipe` VALUES (4809, 65, '东坡墨鱼', '1、墨头鱼治净，顺剖为两爿，头相连，两边各留尾巴一半，剔去脊骨，在鱼身的两面直刀下、平刀进剞六七道刀纹（以深入鱼肉2/3为度），然后用精盐、绍酒抹遍全身。将葱白先切成7厘米长的段，再切成丝，漂入清水中。香油豆瓣切细。\r\n\r\n2、炒锅上火，下熟菜油烧至八成热，将鱼全身粘满干淀粉，提起鱼尾，用炒勺舀油淋于刀口处，待刀口翻起定形后，将鱼腹贴锅放入油里，炸至呈金黄色时，捞出装盘。\r\n\r\n3、炒锅留油50克，加猪油50克，下葱、姜、蒜、香油豆瓣炒熟后，下肉汤、白糖、酱油，用湿淀粉勾薄芡，撒上葱花，烹醋，放麻油，快速', NULL, NULL, NULL, '鲜墨头鱼1条（重约750克），麻油、香油豆瓣、湿淀粉、熟猪油各50克，葱花15克，葱白1根，姜未和蒜未各10克，醋40克，绍酒15克，干淀粉7克，精盐1．5克、酱油25克，熟菜油1500克（约耗15O克），肉汤、白糖各适量。');
INSERT INTO `recipe` VALUES (4810, 65, '金银蛋花', '1.鸡蛋磕入碗内，加精盐5克、味精5克搅匀；另把蛋清放入一碗内，加精盐5克、味精5克搅匀。\r\n2.将肠衣从中间截断，用绳子扎紧一头，分别灌入调好的鸡蛋液和蛋清液，然后用绳子扎紧肠衣的口。\r\n3.将鲜汤、料酒、精盐、味精、葱姜汁和湿淀粉调成芡汁。\r\n4.将灌好的两根蛋肠上屉蒸约20分钟至熟，随即取出晾凉。\r\n5.将两根蛋肠分别从中间片开，剞上十字花刀，再切成方块，入沸水锅汆至卷缩成花后整齐地摆入盘内。\r\n6.炒锅上旺火，将猪油烧热后倒入调好的芡汁，用手勺推匀后，淋入香油，起锅浇在盘中蛋花上，即成。\r\n操作关', NULL, NULL, NULL, '鸡蛋250克 鸡蛋清250克 肠衣50厘米 味精25克 精盐3克 葱姜汁50克 熟猪油50克 鲜汤150克 香油20克 料酒10克 湿淀粉10克');
INSERT INTO `recipe` VALUES (4811, 65, '飘香浮水鱼', '先在一容器内用辣椒油、香油将芝麻酱散，再加入鲜汤及其他调料，经充分搅拌后，用保鲜膜将容器密封，然后上笼蒸热，保温备用。\r\n\r\n二、葱油的制作\r\n  原料：大葱50克 生姜5克 洋葱头30克 大蒜10克 色拉油适量。\r\n  制法：\r\n  大葱、生姜、洋葱头、大蒜择洗净后拍破。锅上火放入色拉油，烧热后下入上述原料，用小火慢慢熬出味，打去料渣即得葱油。\r\n用这种特制的葱油于菜肴上桌前淋在菜肴上，可以增加菜肴的清香味。 \r\n\r\n三、鱼的加工及烹制\r\n  此菜主料可选择各种淡水鱼或咸水鱼，尤其以本味鲜浓的鱼为佳。', NULL, NULL, NULL, '芝麻酱30克 姜末3克 蒜茸5克 味精3克 鸡精3克 白糖2克 生抽王15克 香油5克 胡椒粉1克 辣椒油50克 花椒油5克 鲜汤75克');
INSERT INTO `recipe` VALUES (4812, 65, '干煸神仙蟹', '1.肉蟹宰杀后治净，剁去脚尖，斩成小块，再拍上干淀粉，入热油锅中炸熟后捞出；将猪肉末加精盐、湿淀粉抓匀，下入放有少许油的热锅中至酥香后铲出。\r\n2.炒锅置火上，放入精炼烧热，投入干辣椒节炸香，再下入碎米芽菜粒、香菇粒炒断生，接着下入蟹块和猪肉末，烹入料酒，调入精盐、味精，淋入香油，撒入熟碎花生米和小葱花，翻炒均匀即起锅装盘，最后用香菜稍加点缀即成。\r\n特点：酥香可口，芽菜味浓。', NULL, NULL, NULL, '肉蟹2只约750克 猪肉末50克 碎米芽菜30克 青椒粒、洋葱粒、冬笋粒、芹菜粒、香菇粒各10克 熟碎花生米、小葱花、香菜各15克 干辣椒节150克 精盐、料酒、味精、香油、干湿淀粉各适量 精炼油1500克约耗75克');
INSERT INTO `recipe` VALUES (4813, 65, '回锅肉炒蟹', '1.肉蟹宰杀后治净，剁去脚尖，斩成小块；猪二刀肉入清水锅中煮至断生后捞出，切成片；大青椒去蒂去籽，切成块；洋葱切块；蒜苗洗净切斜节；郫县豆瓣剁细。\r\n2.炒锅置火上，放入精炼油烧热，将蟹块拍上干淀粉后，下入锅中炸熟捞出。\r\n3.锅留底油少许，下入猪肉片，炒至起灯盏窝时，烹入料酒，加入姜片、蒜片、郫县豆瓣、甜面酱炒香，接着下入大青椒、洋葱和蒜苗炒断生，再下入蟹块，调入精盐、白糖、酱油、味精等，待炒匀后，起锅装盘即成。\r\n特点：咸鲜微辣，家常味浓。', NULL, NULL, NULL, '肉蟹2只约750克 猪二刀肉150克 大青椒50克 洋葱50克 蒜苗50克 郫县豆瓣30克 甜面酱25克 精盐、料酒、白糖、味精、酱油、干淀粉各适量 精炼油1500克约耗75克');
INSERT INTO `recipe` VALUES (4814, 65, '锅巴肉蟹', '1.肉蟹宰杀后治净，剁去脚尖，斩成小块；鲜虾仁洗净，用精盐、胡椒粉、料酒、水淀粉抓匀上浆；鱼肚洗净，切成菱形片；火腿、香菇均切片；青豆洗净；用精盐、胡椒粉、料酒、味精、鲜汤、湿淀粉等对成滋汁。均备用。\r\n2.炒锅置火上，放入精炼油烧热，先下入泡辣椒节、姜片、蒜片和马耳朵葱爆香，再下入虾仁滑散，接着放入鱼肚片、火腿片、香菇片和青豆炒熟，烹入滋汁，待芡熟后起锅装入大碗内。\r\n3.净锅重上火，放入精炼油烧热，将蟹块拍匀干淀粉，下入锅中炸熟，捞起摆入盘中，再将锅巴掰成小块，投入油锅中炸至金黄酥脆时捞起摆入盘中，随', NULL, NULL, NULL, '肉蟹2只约750克 大米锅巴150克 鲜虾仁30克 发好的鱼肚30克 火腿30克 香菇30克 青豆30克 泡辣椒节10克 姜片5克 蒜片10克 马耳朵葱20克 精盐、胡椒粉、料酒、味精、鲜汤、干湿淀粉各适量 精炼油1500克约耗100克');
INSERT INTO `recipe` VALUES (4815, 65, '纹露美鲍', '鲍鱼切成薄片，冬菇去蒂，用清水和少许油煮10分钟，锅中油加热，放入鲍鱼片略炒，加高汤、冬菇、红萝卜及味精、酱油、鱼露、蚝油、酒、太白粉水，煮4分钟后盛起，将冬菇片铺在盘中，即可上桌。\r\n\r\n主要营养成分： \r\n蛋白质108.5g，脂肪15.7g，醣类85.4g，钠780mg，胆固醇400.8mg，总热量915.7cal。', NULL, NULL, NULL, '鲍鱼220克、冬菇8朵、红萝卜1片、味精半匙、鱼露1小匙、太白粉水1小匙、高汤半杯、酱油1小匙、蚝油1大匙、酒半匙、油1大匙。');
INSERT INTO `recipe` VALUES (4816, 65, '鸡蒙四蔬', '1、鸡脯肉分别制成鸡茸、1.5公分见方鸡丁(用姜片、葱、料酒码上味)备用。\r\n\r\n2、花菇、冬笋、红椒分别切成骨排片。\r\n\r\n3、鸡茸加蛋清、高汤、盐、豆粉调成鸡浆，分别下入白菜心、花菇、冬笋、红椒片，拌匀后再分别下入沸水中，待表皮鸡浆成熟，捞起放入凉水漂冷待用。\r\n\r\n4、炒锅下入适量高汤，放盐、料酒，下入蒙好的四蔬，烧入味捞起，装入园盘四周，再将锅内滋水加入豆粉勾成二蔬汁淋于四蔬上。另取锅放适量油，下入去掉姜葱、码上15克豆粉的鸡丁炸成浅黄色起锅，另下油将盐蛋黄茸炒香，下入鸡丁，翻炒均匀，下入葱花起锅', NULL, NULL, NULL, '鸡脯400克、小白菜心10棵；花菇、冬笋尖、大红椒各100克 。\r\n配  料 鸡蛋清4个、姜10克、葱15克、盐蛋黄 4个（制茸）。\r\n调  料 盐3克、高汤500克、料酒15克、豆粉60克、精炼油75克。');
INSERT INTO `recipe` VALUES (4817, 65, '红酒醉凤翅', '１鸡翅剔去大骨至翅尖不要破皮，用清水漂去血水，捞出沥干水后，纳盆，加盐、味精、胡椒粉、食粉、松肉粉及玫瑰露酒拌匀，腌渍约２个小时。\r\n２净锅上火，入色拉油烧至五六成热时，逐个下入腌好的鸡翅，炸至色呈金黄时捞出。\r\n３锅留底油少许，下入葱段、姜片、蒜茸炝锅，再下入甜面酱炒香，倒入红葡萄酒，掺入鲜汤，放入鸡翅，调入盐、味精、白糖，烧沸后转小火烧至鸡翅酥烂时，捞出摆在盘中，锅内留汤汁，勾入水淀粉，淋入葱油，起锅浇在盘中鸡翅上即成。\r\n  特点：色泽红亮，鸡翅滑嫩，咸鲜微甜，酒香浓郁。', NULL, NULL, NULL, '鸡翅５００克 红葡萄酒１５０克 玫瑰露酒８克 食粉２克 松肉粉３克 甜面酱２５克 盐３克 味精３克 白糖６克 胡椒粉２克 葱段、姜片、蒜茸、水淀粉、鲜汤、葱油各适量 色拉油１５００克约耗１００克');
INSERT INTO `recipe` VALUES (4818, 65, '太白鸭', '1、将鸭子开膛，洗净，入开水锅中略焯取出，洗净血水，挤干水分，斩去脚爪。用绍酒、精盐、胡椒粉将鸭身内外抹匀，置盛器内，加葱结、姜片、绍酒、鲜汤少量，放入猪肉、枸杞子、三七片，用皮纸封严。\r\n\r\n2、将鸭放入笼屉，用旺火蒸约3小时，至肉质酥烂取出，揭去皮纸，拣去葱结、姜片，将鸭连汤盛入汤盘内即成。\r\n \r\n特点: 色白肉烂，汤味鲜醇，具有滋补功效。\r\n关键: 鸭子必须内外洗净，去除肚内血粕污物，否则会有异味。在放入盛器蒸制前必须用皮纸封严或加盖，以保持原汁原味。', NULL, NULL, NULL, '光肥鸭1只（重1500克左右），枸杞子25克，三七片15克，瘦猪肉100克，陈年绍酒200克，葱结1只（约50克），姜片15克，精盐5克，胡椒粉1克，鲜汤少量。');
INSERT INTO `recipe` VALUES (4819, 65, '酥盒回锅肉', '1、将猪腿肉（二刀肉）洗净，下入清水中，加少量姜、葱、花椒，煮至八成熟捞起，晾冷，横筋切0.2×4×7公分厚肉片，切完待用。\r\n    \r\n2、干咸菜、青椒、葱洗净切成1.2寸的段，洋葱切二粗丝，白面锅魁切成二层，分别再切成小棱形片，入热油中炸成色黄，酥脆时捞出，备用。\r\n    \r\n3、取面粉200克，加适当酵母、水、做面合一个涨发180分钟进烤箱烤至表皮金黄取出，在1/5处切开掏空、合心、待用。\r\n    \r\n4、炒锅上火加入适量油下入切好的肉片爆炒，待肉片开始吐油，形似灯盏时下入豆瓣、甜酱、豆豉继续', NULL, NULL, NULL, '主料 猪腿肉250克\r\n配  料 干咸、菜25克、青椒25克、葱15克、洋葱1个、白面锅魁 1个、面粉200克\r\n调  料 盐1克、料酒10克、酱油5克、豆豉10克、甜酱10克、白糖5克、豆瓣20克、黄油25克、花椒5克、精炼油50克。');
INSERT INTO `recipe` VALUES (4820, 65, '毛肚火锅', '1、将毛肚上的杂物抖尽，摊在案板上，把肚叶层层理伸，再用清水反复清洗至无黑膜和草味，切去肚门的边沿，撕去底部无肚叶一面的油皮，以一张大叶和一张小叶为一联，顺纹路切断，再将每联叶子理顺摊平，切成约1．5厘米宽的片，用凉水漂起。肝、腰、肉均分别片成又薄又大的片。葱和蒜苗均切成7厘米长的段。鲜菜清水洗净，撕成长片。\r\n\r\n2、炒锅上火，下牛油50克烧至六成热，放入豆瓣酱炒酥，加入姜未、辣椒粉、花椒炒香，加入部分牛肉汤烧沸，盛入火锅内，放旺火上，加精盐、绍酒、豆鼓、醪糟汁，烧沸出味，撇去浮沫。\r\n\r\n3、食用时，', NULL, NULL, NULL, '牛毛肚250克、牛肝、牛脊髓、牛腰各100克，黄牛背柳肉150克，味精1．5克，葱25克，青蒜苗250克，鲜菜（芹菜、卷心菜均可）1000克，麻油250克，辣椒粉40克，绍酒15克，姜未50克，花椒5克，精盐10克，豆鼓40克，醪糟汁100克，郫县豆瓣酱125克，牛肉汤1250克，熟牛油200克。');
INSERT INTO `recipe` VALUES (4821, 65, '夫妻肺片', '1、将牛肉、牛杂洗净。牛肉切成重约500克的大块，与牛杂一起放人锅内，加入清水（以淹过牛肉为度），用旺火烧沸，并不断撇去浮沫，见肉呈白红色，滗去汤水，牛肉、牛杂仍放锅内，倒人老卤水，放人香料包（将花椒、肉桂、八角用布包扎好）、白酒和精盐，再加清水400克左右，旺火烧沸约30分钟后，改用小火继续烧1．5小时，煮至牛肉、牛杂酥而不烂，捞出晾凉。\r\n\r\n2、卤汁用旺火烧沸，约10分钟后，取碗一只，舀入卤水250克，加入味精、辣椒油、酱油、花椒面调成味汁。\r\n\r\n3、将晾凉的牛肉、牛杂分别切成4厘米长、2厘米宽、', NULL, NULL, NULL, '鲜牛肉、牛杂（肚、心、舌、头皮等）、老卤水各2500克，辣椒油、油酥花生未、酱油各150克，芝麻面100克，花椒面25克，八角4克，味精、花椒、肉桂各5克，精盐125克，白酒50克。');
INSERT INTO `recipe` VALUES (4822, 65, '醉心土豆饼', '1.土豆去皮洗净，上笼蒸后压成泥；红葡萄酒倒入一碗内，再加入琼脂，入笼蒸化后取出，再入冰柜中冷藏约15分钟，成冻后切成1厘米见方的丁，即成酒冻馅心；香菇、马蹄均剁成末；将1个鸡蛋磕入碗内，搅匀成蛋液。\r\n2.土豆泥纳盆，加入香菇末、马蹄末、精盐、味精、胡椒粉、干淀粉，倒入花雕酒，再磕入另一个鸡蛋，然后朝一个方向搅打上劲，挤成约2厘米大小的丸子，包入酒冻馅心后，再压成饼状，然后均匀地沾上一层干淀粉。\r\n3.炒锅上火，入色拉油烧至六七成热时，将沾匀干淀粉的土豆饼先拖匀鸡蛋液，再沾匀面包糠，稍加按实后，下入锅', NULL, NULL, NULL, '土豆400克 红葡萄酒100克 琼脂15克 鸡蛋2个 香菇25克 马蹄25克 盐3克 味精2克 花雕酒6克 胡椒粉2克 干淀粉、面包糠各适量 色拉油1500克约耗100克');
INSERT INTO `recipe` VALUES (4823, 65, '荸荠圆子', '1.荸荠去皮洗净，拍碎后剁成细末，加入干淀粉、鸡蛋清搅拌均匀，即成荸荠泥；猪板油、桔饼、青红丝均剁细，加入白糖100克和匀，即成馅心。\r\n2.取一份荸荠泥，包入一份馅心，再搓成直径约2厘米的圆子，依法逐一制完待用。\r\n3.炒锅置火上，放入少许花生油烧热，将圆子下入锅中，进行煎炸，并用手勺将圆子逐个按成扁圆形，待煎至两面金黄时，再倒入剩余的花生油，用小火将圆子炸熟后捞出。\r\n4.净锅重上火，放入少许油烧热，下入白糖25克熬至能拔出丝来时，再加入蜂蜜及少许水，下入炸好的圆子裹入味，最后用湿淀粉勾浓芡，滴入', NULL, NULL, NULL, '荸荠750克 猪板油75克 桔饼25克 青红丝25克 干淀粉100克 湿淀粉25克 鸡蛋清1个 白糖125克 蜂蜜50克 香精少许 花生油500克约耗50克');
INSERT INTO `recipe` VALUES (4824, 65, '榆钱香椿丸子', '1.面粉入盆内，加面肥、清水和匀成面团，发酵后揉入适量碱水；把椿芽洗干净，汆水后剁碎；榆钱择干净，焯水后晾干、剁碎。\r\n2.把香椿末揉入面团中，分别揉成直径为２厘米的丸子，粘上榆钱末，摆入盘中，入笼蒸15分钟取出。\r\n3.油锅上火，烧至五成热时，把蒸好的丸子放入锅中炸制，视其呈金黄色时捞出食用。\r\n成菜特点：酥香可口，味道鲜美。', NULL, NULL, NULL, '面粉500克 榆钱150克 香椿芽250克 面肥、碱水各适量 精盐少许 食用油1000克');
INSERT INTO `recipe` VALUES (4825, 65, '榆钱酥', '1.把榆钱择洗净，入沸水中略汆，取出晾干，放入精盐2克和味精2克拌匀，腌渍10分钟。\r\n2.鸡蛋磕入大碗内，放入精盐、味精搅匀，将榆钱挂上蛋液，放入面粉中滚匀备用。\r\n3.油锅上火，烧至五成热时，把榆钱上多余的粉粒抖去，放入油中，炸至金黄色时捞出控油，装入盘中，撒上椒盐即可。\r\n成菜特点：酥香可口。', NULL, NULL, NULL, '榆钱250克 鸡蛋2个 面粉150克 精盐5克 味精4克 椒盐5克 食用油1000克');
INSERT INTO `recipe` VALUES (4826, 65, '煎香椿饼', '1.将香椿芽择洗干净，入开水中焯后切碎，放入一碗内，磕入鸡蛋，放入精盐、味精搅匀待用。\r\n2.油锅上火烧热，下入葱花炸香，倒入香椿蛋，炒熟铲碎，淋入香油搅匀成馅。\r\n3.面粉入盆内，加适量清水和成稍软一点的面团，揉匀，分成10等份。将鸡蛋香椿馅分成10等份，分别包入面剂子中，按扁成0.5厘米厚的圆饼状。\r\n4.平底锅上火，涮一层底油，放上饼坯，煎至一面呈金黄色后，再翻转煎另一面，直至煎熟，逐个煎完即成。\r\n成品特点：色泽金黄，外焦里嫩，风味独特。', NULL, NULL, NULL, '面粉500克 香椿芽250克 鸡蛋4个 葱花10克 精盐5克 味精2克 香油5克 化猪油100克');
INSERT INTO `recipe` VALUES (4827, 65, '绣球烩竹荪', '1.干竹荪用凉水泡发，待涨发完成，用温水洗净，切长骨排片。放入盆中加高汤、盐、少量姜葱及料酒上笼蒸入味，取出待用。\r\n2.干贝洗净，加高汤上笼蒸，取出沥干，青红椒切细丝，黄瓜雕成菊花座，蕃茄切片，菜心洗净备用。\r\n3.鸡脯制糁，加入干贝丝，做成球形，再粘上少许青红椒丝，上笼蒸3分钟，取出放于黄瓜雕成的菊花座上。\r\n4.锅上火放少许精炼油，下姜葱略炒，下入高汤煮2分钟，去掉姜葱，放入盐、料酒，下入竹荪、蕃茄、香菇、菜心，烧入味，勾芡起锅装入盘中，再围上绣球即成。\r\n色泽淡雅,竹荪香醇,配以干贝绣球,造型独', NULL, NULL, NULL, '主料 竹荪300克、干贝100克\r\n配  料 黄瓜250克、蕃茄100克、香菇50克、青菜心5棵、鸡糁50克\r\n调  料 盐3克、高汤400克、料酒10克、豆粉5克、胡椒1克、姜5克、葱10克  青椒10克、红椒10克、精炼油50克');
INSERT INTO `recipe` VALUES (4828, 65, '八珍豆腐盒', '1.豆腐改成5公分长，3.5公分宽，3.5公分高的长方块，片下面上一层（0.3公分厚)做为盒盖,将豆腐块中间掏空，成为豆腐盒，再在豆腐盒表面装饰百花。\r\n2.带子、鲜鱿、刺参、鲍丁、鸡脯、冬笋、香菇，分别切成细丁，泡辣椒剁茸，姜蒜切米，葱切花，大白菜只要嫩心12棵备用。\r\n3.将上述细丁码味上浆,炒锅上火,放少量油,下入细丁，滑炒散籽,下入泡辣椒,姜蒜米烹入酱油、高汤、料酒、糖醋、豆粉滋汁、炒转撒入葱花,起锅装入豆腐盒中。\r\n4.将装好的豆腐盒上笼蒸5分钟，同时将大白菜心氽水，用高汤烧起锅，另取高汤放入', NULL, NULL, NULL, '主料 豆腐500克\r\n配  料 带子20克、鲜鱿20克、刺参20克、鸡脯20克、冬笋20克、鲍丁20克、  香菇20克、青元20克、大白菜心12棵、泡辣椒15克、姜5克、葱15克、蒜10克\r\n调  料 酱油2克、白糖10克、高汤150克、料酒5克、豆粉7克、麸醋5克\r\n精炼油75克');
INSERT INTO `recipe` VALUES (4829, 65, '玫瑰醉兔腿', '1.兔腿洗净，剁成小块，用清水漂净血水，捞出沥干后纳盆，加入盐、味精、２５克玫瑰露酒、食粉、松肉粉等抓匀，腌渍约２小时；青尖椒、红尖椒、洋葱均切成小菱形片，焯一水。\r\n2.净锅上火，入色拉油烧至六七成热时，下入兔块，炸至色呈金黄且断生时捞出。\r\n3.锅留底油，下入葱段、姜片炒香，投入青尖椒片、红尖椒片、洋葱片和野山椒，倒入玫瑰露酒，放入兔块，调入盐、味精、白糖，勾入水淀粉，淋入红油，推匀，起锅装盘即成。\r\n  特点：色泽金黄，微辣鲜香，酒香浓郁。', NULL, NULL, NULL, '净兔腿450克 玫瑰露酒150克 野山椒30克 青尖椒25克 红尖椒25克 洋葱25克 食粉2克 松肉粉3克 盐5克 酱油6克 味精3克 白糖2克 红油15克 葱段、姜片、水淀粉各适量 色拉油1000克约耗100克');
INSERT INTO `recipe` VALUES (4830, 65, '泡菜肉末', '1、将干辣椒放入猪油锅内炒辣后，即将肉末、葱、姜、加入炒酥。\r\n\r\n2、再把蒜末、黄酒、油、糖、麻油加入炒几下，最后加入泡菜（泡菜切成末，水分要挤干）炒两分钟即好。\r\n\r\n特点\r\n此系道地川味，辣味甚浓，泡菜带酸。色美观，又红又白。', NULL, NULL, NULL, '肥瘦猪肉末（65克），泡菜（200克），葱姜（65克），白糖（3克），黄酒（少许），麻油（4克），蒜末（少许），干辣椒（少许）。');
INSERT INTO `recipe` VALUES (4831, 65, '酸菜鱼', '将鱼两面各切３分，酸菜搌干水分，切成细丝，泡红辣椒剁碎，泡姜切成粒； 炒锅置中火上，下熟菜油烧至六成热，放入鱼炸呈黄色时捞出； 锅内留油，放入泡红辣椒、姜、葱花，再掺入肉汤，将鱼放入汤内。汤沸后移至小火上，放入泡酸菜，烧约１０分钟，盛入盘。锅内加入醋。\r\n\r\n特点\r\n四川家常菜。以鲜草鱼为主料，配以四川泡菜煮制而成。此菜虽为四川民间家常菜，但流传甚广。成菜肉质细嫩。', NULL, NULL, NULL, '草鱼600克，泡酸菜100克 泡红辣椒25克，泡仔姜、葱花各15克，花椒3克，蒜5克，精盐4克，料酒10克 肉汤500克，熟菜油500克。');
INSERT INTO `recipe` VALUES (4832, 65, '苦瓜酿肉', '1、豆腐去皮切成6厘米长，2厘米见方条，共30条，冬笋切成片；\r\n2、苦瓜切4厘米段去瓤，冷水煮熟去苦味后攥干水；\r\n3、猪肉剁成，香菇、虾切碎放入碗中，加鸡蛋、面粉、湿淀粉25克、精盐调匀成馅，塞入黄瓜段，用湿淀粉50克封两端；\r\n4、熟猪油烧至六成热，放蒜瓣炸一下捞出，苦瓜入锅，待苦瓜表面炸至淡黄色捞出，竖放碗内，撤上蒜瓣，加酱油入笼蒸熟；\r\n5、熟猪油50克烧至七成熟，将蒸苦瓜的原汁入锅中烧开，加味精、湿淀粉25克勾芡，苦瓜翻扣盘中浇汁，撤胡椒粉，淋香油即可。', NULL, NULL, NULL, '鲜苦瓜750克，去皮猪肉300克，金钩虾、酱油各15克，水发香菇、面粉各25克，鸡蛋1个，湿淀粉100克，蒜瓣50克，胡椒粉、味精各1克，精盐、香油各2克，熟猪油1000克。');
INSERT INTO `recipe` VALUES (4833, 65, '龙井鲍鱼', '1、将鲍鱼用料酒、精盐、胡椒面码味，将蛋清、干豆粉调成糊，拌匀；\r\n\r\n2、用开水冲泡茶叶，头道茶水不用，再冲第二次，留75克茶水待用；将茶叶带水，翻扣在深圆盘正中，并将75克茶水倒入盘内；\r\n\r\n3、把锅内清汤烧至刚开，将鲍鱼、菜心、味精放入，装盘即可。', NULL, NULL, NULL, '鲍鱼200克，龙井茶叶、料酒各15克，菜心50克，清汤300克，精盐5克，味精2克，胡椒面1克，水豆粉30克，鸡蛋2个，干豆粉10克。');
INSERT INTO `recipe` VALUES (4834, 65, '干煸鳝鱼丝', '1、将牛肉切成8厘米细丝，芹菜切成4厘米段；\r\n\r\n2、锅中加油烧热，牛肉丝下锅炒散，加盐、姜丝继续煸炒，在牛肉水分将干，呈深红色时，加豆瓣酱炒散炒香，肉丝煸酥时加酱油、香油、芹菜炒熟，撒上花椒即可', NULL, NULL, NULL, '牛里脊肉250克，芹菜100克，姜丝15克，郫县豆瓣25克，花辣粉、川盐各1克，酱油、香油各10克，油150克。');
INSERT INTO `recipe` VALUES (4835, 65, '川辣黄瓜', '1、黄瓜洗净，切成条，稍去心，干辣椒切成短段；\r\n\r\n2、碗内放盐、白糖、醋，加少量清汤，兑成汁；\r\n\r\n3、炒锅加油置火上，放入花椒，炸出香味后捞出，下干辣椒，炸呈棕红色，将锅离火，再放入黄瓜条，翻炒均匀，加香油，起锅装盘晾凉，浇上调好的味汁即可。', NULL, NULL, NULL, '黄瓜500克，干辣椒、油各20克，花椒5克，白糖、醋各10克，香油15克，盐7克。');
INSERT INTO `recipe` VALUES (4836, 65, '豆瓣海参', '1、用凉水将海参中的泥沙杂质洗掉先泡2小时，再上屉蒸2小时，然后用清水多漂洗几次。用蒸海参的原汤澄去泥沙晾晾后，再将海参漂上。同时，将水海参剖开去肠抠净腹内壁膜洗净，顺斜片成长片(刺参片3～4片)，用开水氽一遍。芹菜芯抽筋洗净切成芹菜花。 \r\n\r\n2、炒勺烧热注入大油，油热后先下豆瓣酱煽炒，待油变红色后倒入鸡汤再煮一会，捞去豆瓣酱如进海参、酱油、胡椒粉、糖、料酒用中火炖入味时加味精，并用湿淀粉勾芡，烧上鸡油，把芹菜花撒上即成。', NULL, NULL, NULL, '水海参750克，鸡汤200克，芹菜芯30克，豆瓣酱30克,大油60克，鸡油、酱油、料酒、湿淀粉各15克，白糖2克，味精3克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4837, 65, '白汁鱼肚', '1、将鱼肚整块先用油浸软，捞出切小块再下入油中稍加温继续浸泡，待鱼肚出现气泡，继续提高油温，使鱼肚全部鼓起，为使之充分鼓足，稍加点水则彻底发透。 \r\n\r\n2、使用时，将油发鱼肚用水泡上，待皮发软时挤去水分斜切成较大的片。为去鱼肚用水泡上，待皮发软时挤去水分斜切成较大的片。为去鱼肚油腻可用温碱水洗，再用温水洗去碱味，最后用凉水洗净，挤去水分。 \r\n\r\n3、把炒勺注油烧热，加入奶汤、调料和鱼肚，以中火炖至汁浓时加味精，用湿淀粉勾芡，淋上鸡油即成。', NULL, NULL, NULL, '水发鱼肚250克，奶汤250克,在油100克，鸡油、料酒、湿淀粉各25克，盐8克，味精5克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4838, 65, '虾仁冬瓜汤', '1、将虾去壳,挤出虾洗净,沥干水分放入碗内；\r\n\r\n2、冬瓜洗净去皮去心切成小骨牌块。虾仁随冷水入锅煮至酥烂再加冬瓜同煮至冬瓜熟,人盐调味后盛入场碗, 淋上麻油即可。', NULL, NULL, NULL, '虾5克，冬瓜300克，香油、精盐各适量。');
INSERT INTO `recipe` VALUES (4839, 65, '仔牛赛熊掌', '将带皮的小牛肉下开水锅烫透捞出，放入鲜汤锅里加调料煮熟捞出，装入熊掌模具，用白纱布包好，压紧，成形后边同熊掌模具放入汤盆。将猪骨肉、老母鸡肉去血水，放入汤盆内加入鲜汤和各种调料，上屉蒸３小时。将小牛肉下屉后，除去纱布，扣在摆好菜心的大盘子中央，即呈熊掌状。再用原汤勾芡，打入明油，淋入芝麻油浇在熊掌状的小肉上即成。', NULL, NULL, NULL, '小牛后腿肉900g　猪骨肉100g　老母鸡150g　胡萝卜　熟油　绍酒　酱油　味精　白糖　精盐　桂皮　八角　花椒　葱　姜　鲜汤　芝麻油');
INSERT INTO `recipe` VALUES (4840, 65, '渔舟唱晚', '将草鱼一尾开片，硬扇做船身，剞刀码味，将鱼肉和另一尾鱼（取净肉）片成薄鱼片做船帆。将鱼身用番茄汁烧入味，勾芡浇汁码入盘中，鱼片码成帆船形在屉中蒸熟，取出摆在船身上蒙白汁即可。', NULL, NULL, NULL, '草鱼2尾（约1500g）、精盐、高汤、味精、水淀粉、色拉油、葱、姜块、番茄酱');
INSERT INTO `recipe` VALUES (4841, 65, '豆苗鸡丝', NULL, NULL, NULL, NULL, '豆苗400克，鸡丝135克，鸡蛋清1个。调料：绍酒、湿淀粉各3茶匙，胡椒粉、麻油、味精各1茶匙，植物油0.5汤匙，精盐、植物油各3茶匙，上汤1汤匙。');
INSERT INTO `recipe` VALUES (4842, 65, '牛腩煲', '1.牛脯切片,用开水煮5分钟捞起漓干水份。生菜洗净,切块； \r\n\r\n2.锅内放油二勺爆姜蒜末,加入牛脯及生菜、腐竹搅匀,铲起放入煲内加水四杯,煲滚后,慢火烧至牛腩熟,约三十分钟放调味,再放上葱段即可。', NULL, NULL, NULL, '牛脯300克,生菜250克,红枣l0颗,腐竹25克,姜数片，蒜末一茶匙,桂皮一块,蚝油、生抽、糖、酒、盐、葱各适量。');
INSERT INTO `recipe` VALUES (4843, 65, '五花肉炒豆腐泡菜', '将五花肉切片、豆腐用水煮透。炒锅放油，煸香蒜片，加入五花肉，八成熟时，放入红泡菜，加盐、味精炒熟，豆腐煮切成片放入盘中　即可。', NULL, NULL, NULL, '猪五花肉、红泡菜、豆腐');
INSERT INTO `recipe` VALUES (4844, 65, '麻油火鸡肾', '1.榨菜先用冻开水冲净，吸干水分，切成丁方薄块，加入砂糖1汤匙拌匀，使榨菜吸收糖之甜味，加入少许麻油拌匀，待用。\r\n2.火鸡肾解冻后，以姜片、葱段出水，洗净，抹干，加入腌料拌匀，腌至过夜使入味，取出略冲净，上碟，以大火隔水蒸至熟透，备用。\r\n3.置火鸡肾块于深碗内加入调味料拌匀后，放入榨菜块再拌至均匀，即可挟上碟供食。', NULL, NULL, NULL, '急冻火鸡肾3－4个，榨菜1小块，姜2片，葱段1条。腌料：花椒盐（或粗盐）1汤匙，绍酒2汤匙，姜汁1／2汤匙，胡椒粉少许。调味料：麻油、糖各少许。');
INSERT INTO `recipe` VALUES (4845, 65, '网油包烧鸡', '1.鸡洗净剁去爪、翅尖，掏除内脏，剔除腿骨下段。芽菜剁碎。猪肉切成丝。姜一半切片另半切丝。泡辣椒切丝。网油洗净控水。鸡用料酒、味精和盐揉擦一遍，再放上葱、姜片腌上入味。用盐与鸡蛋清、湿淀粉调成糊。\r\n2.将肉丝倒入热油炒勺中炒散后随下芽菜、泡辣椒丝、葱、姜丝，再调入味精、酱油、料酒等炒好装入鸡腹中。把鸡用网油(擦去水分，抹上蛋糊)包上。\r\n3. 把包好的鸡上香油。把包在外面的网油皮剥下，切为长方块放在盘四周，再把鸡腹中的馅掏出垫入盘底，将鸡肉剁成条形放于馅上边。椒盐另装碟随鸡上桌即成。', NULL, NULL, NULL, '母鸡1只，网油250克，四种芽菜100克，猪肉200克，泡辣椒2个，鸡蛋2个。调料 花生油800克，香油10克，葱30克，姜20克，盐10克，料酒30克。湿淀粉30克，味精5克，椒盐3克，酱油15克。');
INSERT INTO `recipe` VALUES (4846, 65, '鱼香油菜苔', '油菜苔去根去筋洗净，切成 5厘米长的段，姜，葱，蒜切成细末；用一小碗放入酱油，料酒，味精，白糖，醋，精盐，姜，葱，蒜和水淀粉兑成芡汁；锅置火上，放入花生油烧至八成热投入菜苔煸炒数下，控去水份，另起锅放入泡辣椒炒出红油，放入煸炒好的菜苔，烹入芡汁，翻炒即成', NULL, NULL, NULL, '油菜苔，生油，酱油，醋，白糖，泡辣椒，精盐，姜，葱，蒜，淀粉，味精和料酒');
INSERT INTO `recipe` VALUES (4847, 65, '红杞活鱼', '鲫鱼去鳍、鳃、鳞，剖腹去内脏，用沸水略汆一下。用凉水洗净。在鱼身上剞成十字花刀。芜宴洗净，切成段。葱一部分切丝，少许切花；将锅烧热，依次下猪油、清汤、奶汤、姜米、葱花、胡椒粉、味精、料酒、盐，熬成汤汁。同时用另一锅注入清水烧沸，放入鲫鱼煮约4分钟（以去腥味）捞出，放入汤锅内，然后将拘相洗净入锅，先用旺火烧沸，后移炆火炖20分钟，加入葱丝、芫荽、醋、芝麻油调味即成', NULL, NULL, NULL, '活鲫鱼3尾（约750克）。 枸杞15克、芫荽10克。葱15克、醋10克、芝麻油10克、料酒10克、白胡椒粉3克、姜10克、盐3克、味精1克、奶汤50克、清汤500克、猪油50克。');
INSERT INTO `recipe` VALUES (4848, 65, '杞鞭壮阳汤', '将牛鞭用热水发胀，顺剖成两块，刮洗干净，用凉水漂30分钟。枸杞拣去杂质。肉苁蓉洗净，用适量酒润透，蒸2小时取出，漂洗干净，切片备用。用砂锅加入清水，放入牛鞭烧开，打去浮沫，放入姜、花椒、料酒和鸡肉，用旺火烧沸，移小火炖，每小时翻动一次，炖至六成熟时，用干净纱布滤去汤中的姜、花椒，再置武火上烧沸，加入用纱布袋装好的枸杞、肉灰蓉，移炆火上炖至牛鞭八成熟时，取出牛鞭，切成3厘米的条块，放入砂锅炖火巴。鸡肉取出作它用，取出药包，加味精、盐、猪油等调味即成', NULL, NULL, NULL, '黄牛鞭1000克。 肥母鸡肉500克、枸杞15克、肉灰蓉50克。花椒3克、猪油20克、料酒20克、味精1克、食盐3克寸生姜10克。');
INSERT INTO `recipe` VALUES (4849, 65, '冬瓜燕', '冬瓜去皮、籽后，片成薄片，再切成长约10厘米的银针细丝。扑上干细淀粉。熟火腿切成细丝。炒锅置火上掺清水烧沸。放入冬瓜丝氽至色白发亮，捞入冷开水中漂凉后，捞出整理好放入汤碗内，加川盐、火腿丝、清汤、味精，置笼中蒸至瓜丝入味即成。', NULL, NULL, NULL, '冬瓜500克。 熟瘦火腿50克。于细淀粉15克、川盐2克、清汤500克、味精1克。');
INSERT INTO `recipe` VALUES (4850, 65, '贵妃鸡翅', '1. 将鸡翅一一去掉翅尖，砍为两节共24节，洗净后用川盐 （2克），胡椒粉拌匀，浸渍30分钟，然后投入沸水中除去污沫捞出1 锅，胡萝卜削成“青果形”，用沸水炟熟。 \r\n2. 炒锅置旺火上，下熟菜油、加姜、葱白略炒，加清汤、冰糖汁、红葡萄酒、花椒，投入鸡翅，急火烧沸后，用文火慢煨约1小时。 待汁浓肉耙时，拣去姜、葱、花椒，加入胡萝卜、味精炒匀，齐锅装盘，胡罗卜镶边，淋芝麻油即成', NULL, NULL, NULL, '鸡翅12只；胡萝卜200克；川盐4克，味精1克，胡椒粉1克，冰糖汁50克，红葡萄酒150克，姜15克，葱白50克，花椒10粒，清汤750克，芝麻油5克，熟苹油100克。');
INSERT INTO `recipe` VALUES (4851, 65, '菠饺鱼肚', '1. 鱼肚下猪油锅炸透，用水泡软，去尽油脂；切成3.5厘米见方，厚0.8厘米的片，下锅用肉汤、绍酒煨一次捞起。火腿、鸡肉均片成3.5厘米长、1.6厘米宽的薄片。\r\n2. 猪肉洗净剁茸，加酱油及味精（0.5克）、姜（2克）取汁，葱粒（1克）拌匀成饺馅。菠菜叶取汁入面粉内，揉面做成24张饺子皮，包馅煮熟待用。\r\n3. 炒锅置中火上，下煮油，放姜、葱炒香加入奶汤烧沸，打去姜、葱，下味精、胡椒、川盐后即下鱼肚、菠饺、鸡肉、火腿煮约１分钟，舀入大圆盘，菠饺镶边，淋上鸡油即成。', NULL, NULL, NULL, '鱼肚100克。配料：熟鸡肉50克，火腿25克，味精1克，姜10克，葱10克，酱油1克，绍酒25克，肉汤500克，奶汤500克，白面粉100克，鸡油20克，猪油1000克（约号50克）');
INSERT INTO `recipe` VALUES (4852, 65, '三菌炖鸡', '开膛嫩鸡洗净后，连骨剁成约2.5厘米大的块。三菌去根脚洗净，改小，用清水漂起。姜洗净拍破：葱、蒜洗净。炒锅置旺火上，下猪油烧热，放入鸡块，葱。姜炒出香味，加鲜汤、蒜烧开，舀入砂锅，用微火煨40分钟左右。炒锅置旺火上，下猪油少许烧热（约150℃）。将三菌沥干水入锅煸炒约3分钟，倒入砂锅内，加精盐，再用微火煨约20分钟至鸡块火巴，舀出盛于汤碗中即成。', NULL, NULL, NULL, '开膛嫩母鸡一只（约500克）。 三菌50克、独蒜25克。猪油75克、葱10克、姜10克、盐3克、料酒10克、鲜汤750克。');
INSERT INTO `recipe` VALUES (4853, 65, '番茄豆腐炒肉片', '1. 番茄切块。豆腐切大块。猪肉切片。\r\n2. 将猪肉在油中炒透，加番茄、豆腐炒匀，加调料煮开，用粟粉水勾芡 即成。', NULL, NULL, NULL, '番茄2个，豆腐1块，猪肉80克，姜丝1匙，油1汤 匙，水溶粟粉1汤匙。 调料:高汤1／2杯，沙糖、酱油各1汤匙，酒2汤匙，盐1匙。');
INSERT INTO `recipe` VALUES (4854, 65, '炝莲白卷', '将整张的莲花白叶去茎洗净沥干水。干辣椒去蒂、籽。用盐、味精、醋、酱油、启糖、水豆粉、鲜汤兑成芡汁。炒锅置旺火上，放油烧至五成热（约110℃），下于辣椒、花椒炸呈棕褐色捞起，莲白放入锅中炒至断生，烹入芡汁、炒匀起锅，入盘晾凉。干辣椒、花椒剁细，撒莲花白上，将莲花白裹成直径1厘米的卷，再切成3厘米长的节，整齐入盘，将原汁放入香油淋上即成。', NULL, NULL, NULL, '莲花白500克。 干辣椒m克、花辣3克、盐3克、白糖15克、味精1克、醋5克、酱油10克、香油10克、菜油50克、鲜汤50克、豆粉10克。');
INSERT INTO `recipe` VALUES (4855, 65, '宫保尤鱼', '1.汤锅上火将尤鱼卷略焯； \r\n2.炒锅上火留底油加花椒、辣椒略炸放入尤鱼卷、糖、醋、盐、味精,加花生米翻炒即可。', NULL, NULL, NULL, '尤鱼卷,花生米,花椒,辣椒,糖,醋盐,味精。');
INSERT INTO `recipe` VALUES (4856, 65, '海鲜拼盘', '1.鲜花入杯中倒扣在盘中间,尤鱼、海参、虾肉、鲜贝、熟鸡块、鱼片、冬笋用沸水焯一下捞出,码放在鲜花周围；\r\n2.汤勺内放入高汤、盐、昧精、胡椒粉、姜末、料酒烧开淋入水淀粉勾芡浇在菜上即可。', NULL, NULL, NULL, '鲜花一朵,尤鱼、海参、虾肉、鲜贝、熟鸡块、鱼片、冬笋、盐、味精、胡椒粉、姜末、料酒、水淀粉等调料');
INSERT INTO `recipe` VALUES (4857, 65, '鼓椒鸡片', '1.锅上火，下猪油烧热，放入火腿片略炸，倒入鸡汤、下鸡片、黄酒、豆鼓、花椒烧沸片刻； 2.加入精盐、菠菜心烧入味，用水淀粉勾薄芡，起锅装盘即可。', NULL, NULL, NULL, '熟鸡片50克，火腿片25克，菠菜心25克，黄酒25克，精盐3克，猪油50克，豆鼓20克，花椒5克，淀粉10 克，鸡汤250克');
INSERT INTO `recipe` VALUES (4858, 65, '人参全鸡煲', '1.下油二汤匙,炒透姜,下鸡,磨鼓爆片刻,铲起放入煲内煮滚；\r\n2.放人人参、构杞慢火焖煮,需40分钟至鸡熟透,勾芡,放上香菜、麻油,原煲上桌即可', NULL, NULL, NULL, '嫩鸡一只,人参1两柯肌杞少许,姜数片磨鼓半汤匙,香菜二棵,盐、麻油各适');
INSERT INTO `recipe` VALUES (4859, 65, '水煮肉片', '1．猪通脊肉切成片后用少许酱油和水淀粉浆一下； \r\n2．芹菜洗净切成段,青蒜拍松斜切成小段,莴笋叶洗净切成段； \r\n3．葱、姜、蒜切丝，豆瓣辣酱用刀剁碎； \r\n4．坐锅，放少量油，待油烧热后放入豆瓣辣酱，炒出红油后放入葱、姜、蒜翻炒几下，放入少量水，开锅放入少许盐、味精，然后放入青菜，断生后捞出装入碗内，将浆好的肉一片一片滑入锅内，待肉变色熟透后连汤一起装入碗内； \r\n5．将锅洗净烧热，将花椒粒、干红辣椒倒入锅内翻炒焙脆后，倒在案板上碾碎，撒在煮好的肉片上； \r\n6．坐锅，倒入少量油，烧热后淋在肉片上即可', NULL, NULL, NULL, '主料：猪通脊肉250克辅料：芹菜、莴笋叶、青蒜调料：姜、葱、蒜、豆瓣辣酱、酱油、淀粉、盐、味精、花椒粒、干红辣椒、食用油');
INSERT INTO `recipe` VALUES (4860, 65, '干煸冬笋', '将冬笋切成厚片拍松，再切成4厘米长、0·8厘米宽的片。肥瘦猪肉剁成绿豆大小的细粒。炒锅置火上，下化猪油烧至六成热（约132℃）时，下冬笋炸至浅黄色捞起，洋去油，锅内留油50克，下肉粒炒至散籽酥香，放入冬笋偏炒至起皱时，再烹入料酒，依次下盐、酱油：白糖、味精，每下一样煸炒几下，最后将芽菜入锅炒出香味，放入芝麻油，炒转起锅即成。', NULL, NULL, NULL, '冬笋500克。 肥瘦猪肉50克、芽菜50克。料酒10克、盐3克、酱油10克、白糖10克、味精卫克、芝麻油10克、化猪油500克。');
INSERT INTO `recipe` VALUES (4861, 65, '火爆双脆', '猪肚头漂洗净，去油筋，从正面剞十字刀纹（约2／3深），然后再切成边长约2厘米的菱形块。鸡肫去内金，洗净去底板和边筋，每个平剖成4块，剞十字刀纹（约2／3深）。姜蒜切片，泡辣椒、葱白切成“马耳朵”形，豌豆苗洗净。肚头、肫用盐、料酒、水豆粉拌匀肮渍码味。炒锅置旺火上，放猪油烧热（约180℃），下肚头。鸡腕爆散籽后，下泡辣椒、姜片、蒜片、葱白、豌豆苗炒匀，烹入以盐、料酒、味精、胡椒粉、水豆粉、香油、鲜汤兑成的滋汁，炒匀迅速起锅装盘即成。', NULL, NULL, NULL, '猪肚头150克、鸡肫151克。 豌豆苗30克。泡辣椒15克、葱白20克、蒜10克、姜10克、盐3克，料酒10克、猪油50克、味精0.5克、胡椒粉1克、香油5克、豆粉25克。');
INSERT INTO `recipe` VALUES (4862, 65, '鸡丝海蜇', '海蜇皮洗净去血筋，切成粗约3毫米、长7～10厘米的丝，用沸水烫一下，再用冷水泡起；熟鸡肉切成与海蜇皮相同的丝。用酱油、白糖、 醋、味精、芝麻油调成味汁。将葱白切成细丝 ，盛盘中垫底，鸡丝、海蜇丝合匀，盖在葱丝上，浇上味汁、淋辣椒油即成', NULL, NULL, NULL, '海蜇皮100克、熟鸡肉300克，酱油、白糖、醋、味精、芝麻油、葱白、味汁、辣椒油。');
INSERT INTO `recipe` VALUES (4863, 65, '锅贴鱼片', '鱼肉片成5厘米长、3厘米宽、0.3厘米厚的片。猪肥膘肉片成5厘米长、3厘米宽、0.5厘米厚的片。这两种均片成24片；鱼片用盐、料酒腌渍码味。鲜笋切成4厘米长、1.7厘米宽的薄片。火腿剁成细粒。猪肥膘肉铺于盘中，用净布（入热水透后拧干）搌去油质一至二次，然后均匀地抹上蛋清豆粉糊，在一片肥膘肉上贴一笋片，其汆的肥膘肉上贴火腿细粒。将鱼片用其汆的蛋清豆粉糊拌匀，一片片放在铺好笋片和火腿的肥膘肉上。将炒锅在火上把锅周围炕热，下油浪匀后，将锅提离火口滗去油，将肥膘鱼片逐一贴在锅上（先锅边，后锅底，肥膘一面贴在锅上）', NULL, NULL, NULL, '鲜鱼肉400克、熟猪肥膘肉400克、鲜笋10克、火腿50克、生菜25克。 盐4克、料酒20克、蛋清50克、豆粉50克、白糖5克、香油10克、素油250克、醋5克。');
INSERT INTO `recipe` VALUES (4864, 65, '砂仁肚条', '将猪肚洗净，下沸水锅末透捞出，刮去内膜。另锅中掺入清汤，放入猪肚，再下生姜、葱白、花椒。煮至熟，打去浮沫，起锅切成条状。再用原汤500克烧沸，放入切好的肚条及砂仁米搅拌，加入猪油和味精，用水豆粉勾芡，炒匀即成。', NULL, NULL, NULL, '猪肚100克。 砂仁米10克。猪油100克、胡椒粉3克、花椒2克、姜10克、葱白10克、盐3克、料酒15克、味精1克、豆粉15克、清汤500克。');
INSERT INTO `recipe` VALUES (4865, 65, '首乌肝片', '将首乌洗净置锅内，加水适量煎熬，取药汁三次，合并药液待用。将猪肝洗净剔去筋膜，切成薄片。姜、蒜、葱洗清，葱切丝、姜切粒、蒜切片，青菜叶淘洗干净。将肝片加入一些首乌药液、盐、水豆粉拌匀。另把首乌汁、酱油、料酒、醋、水豆粉加汤兑成汁。炒锅置武火上，加入油烧至五成热（约100℃）放入拌好的猪肝片，滑透，用漏勺沥去油。锅内剩底油50克、放姜米、蒜片炒香后，将肝片、青菜叶放入翻炒，勾汁炒匀，淋少许明油起锅即成，', NULL, NULL, NULL, '何首乌15克、水发木耳25克、青菜叶50克。 料酒10克、醋5克、盐3克、蒜10克、豆粉25克、酱油10克、葱10克、姜10克、汤50克、混合油500克。');
INSERT INTO `recipe` VALUES (4866, 65, '枸杞海参鸽蛋', '将水发海参内壁洗净，用汤氽两遍，再用刀尖在腔壁切棱形花刀（不要切透）。鸽蛋下入凉水锅，用炆火煮熟，捞出，入凉水浸过，剥去壳。葱切段，姜拍破。把花生油烧沸，将鸽蛋滚上干豆粉，放入油锅内炸呈黄色捞出。锅烧热注入100克猪油，油沸下葱、姜煸炒后，倒入鸡汤，煮3分钟，捞出葱、姜不用；再加入酱油、料酒，胡椒粉、构杞、海参、烧沸撇去浮沫，移炆火炆约10分钟，把海参、拘粑取出入盘，鸽蛋放在海参周围。汁内加味精，用水豆粉勾芡后，再淋50克沸猪油，把汁淋在海参和鸽蛋上即成。', NULL, NULL, NULL, '水发海参2只。 枸杞15克、鸽蛋12。花生油1500克、食盐3克、料酒10克、味精1克、酱油10克、胡椒面2克、猪油150克、鸡汤500克、姜10克、葱1克、豆粉30克、汤500克。');
INSERT INTO `recipe` VALUES (4867, 65, '荷叶粉蒸鸡', '仔鸡宰杀洗净后，去骨剁成长约5.5厘米、宽2厘米的条块。大米加花椒入锅炒呈浅黄色，磨成粗粉。泡梦椒去蒂、籽，切成斜刀节。荷叶洗净烫软，切成约12厘米长的等边三角形。鸡肉加盐、胡椒粉、酱油、味精、白糖、郸县豆瓣（剁细）、姜末、豆腐乳汁拌匀成金红腌入味，加汤、米粉、猪油拌匀。取荷叶一块（叶背向上，尖端向外），放鸡肉两块、泡辣椒一节、豌豆数粒，裹成卷装在蒸碗内，上笼蒸火巴，取出翻扣于盘中即成。', NULL, NULL, NULL, '鲜嫩仔鸡一只（约1000克）。 大米100克、豌豆25克。荷叶10块。花椒3克、泡辣椒10克、盐3克、酱油10克、味精1克、白糖15克、胡椒粉2克、郸县豆瓣15克）姜末10克、豆腐乳汁10克。汤56克。猪油20克。');
INSERT INTO `recipe` VALUES (4868, 65, '虫草鹌鹑', '将冬虫夏草择去灰屑，用温水洗净。鹌鹑宰杀后去毛、内脏和头爪，洗净沥干水分，放入沸水锅内氽一下捞出晾凉。生姜、葱洗净，姜切片，葱切段。将每只鹌鹑的腹内放入冬虫夏草1—4只，然后逐只用绳缠紧，放入盆内，放入葱姜、胡椒粉和盐，注入鸡汤，用温棉纸封口。上笼蒸约40分钟取出，揭去棉纸即成。', NULL, NULL, NULL, '鹌鹑8只。 冬虫夏草8克。生姜10克、葱10克、胡椒粉3克、盐3克、鸡汤300克。');
INSERT INTO `recipe` VALUES (4869, 65, '碎末鸡丁', '鸡脯肉轻拍后，切成0.7厘米见方的丁，入碗加盐，料酒、味精拌匀。泡红辣椒去蒂、籽，剁细。花生仁剁成粗粒。葱切细花，蒜切细粒。另取小碗，用盐，酱油、白糖、醋、料酒、味精、水豆粉、鲜汤兑成滋汁。炒锅置旺火上，下猪油烧热（约150℃），将鸡丁用蛋清豆粉上浆后入锅，滑透，倒入漏勺沥干油。锅内加油少许，烧热，下泡红辣椒，炒至油呈红色时，下葱、蒜炒香，下鸡丁炒匀，烹入滋汁，推炒至散籽亮油，撒上花生仁，推匀起锅装盘即成。', NULL, NULL, NULL, '鸡脯肉200克。 花生仁25克、泡红辣椒10克。猪油150克、盐4克、料酒20克。味精1克、葱10克、蒜10克、酱油10克、白糖5克、醋5克、豆粉20克、鲜汤50克、蛋清10克。');
INSERT INTO `recipe` VALUES (4870, 65, '肥肠豆花', '1.肥肠头洗净，锅内烧水加料酒、姜、葱、肥肠头，煮熟后，捞出切成长条待用。\r\n2.郫县豆瓣、泡辣椒剁细。\r\n3.锅内下油少许把肠头放进去煸炒，同时加花椒、姜、葱至干香时起锅待用。\r\n4.锅内烧油下豆瓣、泡椒、炒出香味颜色后加入高汤烧沸后去掉豆瓣泡椒渣，放入豆花进味，肥肠头烧至肠软入味时收汁起锅装盘，上面撒上香葱花即成。注意：煮肥肠时一是要洗干净去掉膻味，二是要煮烂又不能过火，煸炒时要把水分煸干才香，去掉豆瓣渣后，成菜更加干净，烧制时一定要烧入味，味才厚重。', NULL, NULL, NULL, '猪肠头、嫩豆花,郫县豆瓣、泡辣椒、花椒、盐、酱油、味精、姜、蒜末、香葱、食用油、大葱、料酒、水豆粉');
INSERT INTO `recipe` VALUES (4871, 65, '石斛花生米', '将石斜洗净。切成约1厘米长的节。花失米除去霉烂颗粒，洗净沥干。锅内注入适量清水，放入盐、香料，待盐溶化后，倒入花生米，同时将石斜入锅，置武火上烧沸后，移至炆火煮约1小时，待花生米熟透装盘即成。', NULL, NULL, NULL, '花生米250克、石斛25克。 盐3克、八角1.5克、山奈1.5克）');
INSERT INTO `recipe` VALUES (4872, 65, '炸蒸肉', '猪肉刮洗干净。切成长约6厘米、宽4厘米、厚0.4厘米的片，装碗中，加酱油、五香粉、花椒，豆腐乳汁、醪糟汁、白糖、姜米、葱花、料酒、盐拌匀。腌渍入味后，加米粉、豌豆拌匀。将肉片摆于碗底（成一本书形），上放豌豆，入笼蒸火巴，取出翻扣于碗中，拈出肉片，豌豆入笼保温。鸡蛋去壳搅匀，抹于肉片上，再沾上面包粉，入油锅炸至呈金黄色捞起装于条盘中，两端镶上蒸好的豌豆，并配椒盐碟上桌。', NULL, NULL, NULL, '猪肉500克。 鲜豌豆100克、米粉75克、鸡蛋50克、面包粉50克。酱油10克、五香粉之克、花椒2克、豆腐乳汁10克、醪糟汁10克、白糖30克、姜10克、葱10克、料酒15克、盐2克、素油500克、椒盐10克。');
INSERT INTO `recipe` VALUES (4873, 65, '雪魔芋鸡翅', '鸡翅去尖，一断为二，洗净，出水，葱挽结，姜拍破。雪魔芋切成长约5厘米、宽约3厘米的条块。炒锅置旺火上，下猪化油烧热（约180℃），下鸡翅、料酒、姜、葱炒出香味，再加鸡汤、酱油、盐烧沸，去尽浮沫，用小火慢烧至鸡翅将熟时，加雪魔芋继续烧至鸡翅离骨时，拣去姜、葱，勾芡放味精。胡椒粉和匀起锅即成', NULL, NULL, NULL, '鸡翅500克。 水发魔芋15克。猪油50克、料酒15克、鸡汤500克、酱油10克、盐2克、姜、葱各15克、豆粉10克。味精0.5克、胡椒粉1克');
INSERT INTO `recipe` VALUES (4874, 65, '软炸鸡肝', '将鸡肝去筋膜，大的鸡肝改成两块。葱切段，少许葱切花。姜拍破，鸡蛋打散，加水豆粉、山药粉，调成糊状。将鸡肝拌入葱、姜、胡椒粉、食盐、味精、料酒略腌后，用蛋清糊上浆。油锅烧至六成热（150℃）时，将鸡肝逐块放入锅内炸，用漏勺捞起、将锅烧热，注入芝麻油，下入鸡肝，放葱花、花椒，翻炸几下盛盘即成。', NULL, NULL, NULL, '鸡肝400克。 山药粉100克、鸡蛋4个。菜脑500克、花椒3克、生姜10克。葱10克、胡椒粉2克、食盐3克、味精1克、料酒10克、芝麻油20克。');
INSERT INTO `recipe` VALUES (4875, 65, '当归炖鸡', '1.鸡宰杀后去毛，剖腹洗净，剁去爪，用开水分透，再放入凉水中洗净，沥干水分。 \r\n2.当归洗净，按块质大小，顺切几刀。姜葱洗净，姜拍破、葱切段待用。\r\n3.将当归、姜、葱装入鸡腹内，肚腹朝上放入砂锅内，注入清水适量，加入盐、料酒，胃武火上烧开，再改用炆火炖至鸡肉酥烂时即成。', NULL, NULL, NULL, '母鸡一只（约1000克）。 当归刀克。姜10克、葱10克、盐3克、料酒10克、味精1克。');
INSERT INTO `recipe` VALUES (4876, 65, '黄焖鸡块', '将鸡肉切成１寸见方块，先入温猪油锅煎半分钟，即加笋块或冬菇及调味（葱、姜、黄酒、酱油、湿菱粉、盐、糖、味精、鸡汤），用文火焖十分钟，随即用湿菱粉下锅勾芡即好．', NULL, NULL, NULL, '鸡肉（200克）、笋块（或冬菇）（65克）、黄酒（6.5克）、 酱油（3克）、味精、姜、湿菱粉（8克）、葱花、精盐、白糖、鸡汤（130克）。');
INSERT INTO `recipe` VALUES (4877, 65, '糊涂鸡', '1.三黄鸡洗净血水，锅内加清水、姜、葱、盐、料酒，烧沸后，把鸡出水后放入锅内，用小火浸煮15分钟左右（水不能沸），捞起鸡放入冷鸡汤内浸1小时后取出，切条装盘。\r\n2.泡辣椒去籽剁细，加入糖、醋、味精、蒜末、花椒面、辣椒油等调制成味汁，淋在鸡条上面，撒上芝麻、香葱花即成。注意：选用2.5至3斤的当年仔鸡（太小则无肉、太大则肥，影响成菜质量），浸煮鸡时一定要注意火候大小及时间，过火就柴了，反之则未熟。', NULL, NULL, NULL, '三黄鸡（2.5至3斤）泡辣椒、花椒面、姜、葱、料酒、糖、醋、盐、味精、蒜末、辣椒油、芝麻');
INSERT INTO `recipe` VALUES (4878, 65, '泡菜鱼', '①将鲫鱼剖开，洗净，鱼身两面各立划4刀。泡青菜挤干盐水，切成长1．2厘米的短节细丝。泡辣椒剁细。葱切成葱花。 \r\n②烧热锅，下油至八成熟，将鱼身抹上料酒，入锅内炸2分钟，炸时翻面。至鱼身出现裂纹时，将油泌去滤干。 \r\n③锅内留适量油，放入泡辣椒、姜、蒜茸、葱粒(一部分)、醪糟汁等炒出香味，再依次放入料酒、酱油、红酱油、上汤等，将汤汁搅匀淹至鱼身，改用中火烧至汤汁滚后，放泡青菜丝、翻面，烧约10分钟，待鱼入味后装碟，再放醋、葱花于锅内搅匀，随即下生粉水勾芡，淋于鱼身上面即可', NULL, NULL, NULL, '鲜活鲫鱼三条,泡青菜50克。泡辣椒15克、姜、蒜各10克、葱15克、菜油5侧克、醒糟汁10克、料酒10克、酱油10克、鲜汤150克、香油10克、淀粉15克。');
INSERT INTO `recipe` VALUES (4879, 65, '姜汁热窝鸡', '1.将鸡开膛去内脏，洗净后用白水煮透捞出，然后去腿骨剁成条状，撒上少许盐盛入碗中，注入汤，上屉用旺火蒸10分钟左右取出。姜捣成汁，蒜切片。 \r\n2.炒勺烧热后注入香油，下葱稍炒，随将蒸鸡的原汁倒入(鸡扣入盘)再加入酱油、味精、姜汁、醋，用湿淀粉勾芡，浇上辣椒油,吃时揭开扣碗将热汁浇在鸡上即可', NULL, NULL, NULL, '笋鸡1只(约750克)调料 香油25克，酱油、醋各15克，葱10克，姜25克，盐5克，味精5克，汤250克，湿淀粉、辣椒油适量。');
INSERT INTO `recipe` VALUES (4880, 65, '筒子肉', '1.先将葱、姜(去皮)、花椒切制成细末，猪肉剁成肉泥，一起盛入碗里，拌入盐、料酒、味精、湿淀粉(10克)、香油、白肉汤，拌成馅。\r\n2.把鸡蛋打在碗里，加入白面、湿淀粉(余量)，搅拌成糊浆。 \r\n3.将猪网油洗干净后，切成20厘米见方的块。在各块的一头，把肉馅摊成1厘米粗的馅条。在未摊肉馅的网油上抹上一些鸡蛋糊浆，而后卷成2厘米粗、20厘米长的卷，上屉蒸10分钟左右，取出晾凉，切成10厘米长一根。 \r\n4.在旺火上架上炒勺倒入植物油，烧热，将网油卷沾上鸡蛋糊浆，慢慢放入油里炸1～2分钟，至金黄色即可取出，', NULL, NULL, NULL, '主料 去皮猪肉(肥瘦各半)200克，猪网油200克，鸡蛋2个，白面50克，淀粉50克。调料 植物油500克(实耗约50克)，香油10克，盐10克，料酒10克，葱10克，姜10克，花椒、味精各适量，白肉汤50克。');
INSERT INTO `recipe` VALUES (4881, 65, '西芹百合草莓炒腊肉', '1、腊肉切成片，西芹去筋切成片，百合瓣开洗净，草莓切成片。 2、锅中放水烧开，加西芹、百合过一下油水，腊肉也过一下开水。 3、锅中放2勺油，烧至温油，腊肉放同锅中过一下。 4、炒锅中留底油，放入蒜茸、姜片起锅，把主配料同放锅中一起翻炒，加盐、味精、糖、水淀粉淋芡即可。', NULL, NULL, NULL, '四川腊肉150克,西芹、百合、草莓各100克。');
INSERT INTO `recipe` VALUES (4882, 65, '重庆毛血旺', '1.将鸭血旺切成一字条形，将鸡胸肉，猪肚，猪心火腿肠切成片，将香菇切成片，海白菜，芹菜切成节待用。\r\n2.炒锅放置旺火上，加入少许油将海白菜，芹菜，大葱节，黄豆芽炒熟，放入味精，加少许香油，起锅装碗内做碗内做底用。\r\n3.将炒锅放置旺火上，将鸡胸肉，猪肚，猪心，火腿肠放炒锅内加泡辣椒，干辣椒会炒至香，加入少许鲜汤，然后放入牛油，香油，味精，鸡精起锅后盛入炒好做底用的辅料上即成', NULL, NULL, NULL, '鸭胸肉，猪心，猪肚，火腿肠，香菇，海白菜，黄豆芽，大葱，泡辣椒，干辣椒，牛油，香油，花椒，味精，鸡料　');
INSERT INTO `recipe` VALUES (4883, 65, '麻辣猪肝', '1.肝、葱、姜切成片，干辣椒切节，葱切段。将肝用盐和料酒拌匀，用湿淀粉浆好再拌些油。 \r\n2.用料酒、湿淀粉、葱、姜、蒜、糖、酱油、味精和汤对成汁。 \r\n3.炒勺烧热往油，油热后先下辣椒、花椒，炸至黑紫色，再下猪肝片，待肝熟透即速注汁入勺，汁开后稍翻炒，滴醋，并加入炸花生米即成。', NULL, NULL, NULL, '猪肝200克，炸花生米75克,混合油75克，花椒10粒，干辣椒8克，盐3克，料酒13克，味精3克，酱油、湿淀粉各20克，葱、姜、蒜、糖各8克，汤适量，醋少许。');
INSERT INTO `recipe` VALUES (4884, 65, '重庆麻辣火锅', '毛肝用清水漂净漂白，片成之厘米宽的长薄片，用凉水漂起。肝、腰和牛肉均片成又薄又大的片。葱和蒜苗均切成7一10厘米长的段。鲜菜（莲花白、芹菜、）卷心菜、豌豆苗均可），用清水洗净，撕成长片。豆鼓、豆瓣剁碎。炒锅置中火上，下牛油75克、烧至八成熟（约180℃）时，放入豆瓣炒酥，加入姜末、辣椒粉、花椒炒香，再入牛肉汤烧沸，移至旺火上，放入料酒、豆鼓、醪糟汁，烧沸出味，撇尽浮沫，即为火锅卤汁。将芝麻油和味精分成4份，调成4个味碟，供蘸食用。临吃时）将卤汁烧沸上桌，各种肉菜原料分别每入盘中，与精盐、牛油125克同时上', NULL, NULL, NULL, '毛肚250克、牛肝100克、牛腰100克、黄牛瘦肉（背柳肉）150克、牛脊髓100克。 鲜菜1000克、大葱50克，青蒜苗50克，芝麻油40克、味精4克、辣椒粉40克，姜末50克、花椒4克、精盐10克、豆鼓40克、醒糟汁100克、郸县豆瓣125克、料酒15克、熟牛油200克、牛肉汤2250克。');
INSERT INTO `recipe` VALUES (4885, 65, '果仁徘骨', '将炒果仁、苡仁捣碎，用水煎煮两次，收取汁液500毫升。将猪排骨洗净、拍破，同花椒一起下锅，煮至排骨达六成熟时，打净浮沫，捞出排骨放入卤锅中卤至熟透。取适量卤汁倒入锅中，加冰糖、味精、盐在炆火上收成浓汁，烹入料酒后，均匀抹在排骨上，再抹上香油即成', NULL, NULL, NULL, '排骨2500克。 炒果仁10克、炒苡仁50克、姜50克、葱50克、冰糖50克。花椒3克、料酒20克，香油15克、味精1克、盐3克、卤汁500克。');
INSERT INTO `recipe` VALUES (4886, 65, '鸡蹄花', '1.鸡爪洗净去老皮，置于冷水锅中，用中火煮沸后，改微火浸煮约20分钟，将锅端离火口，待冷后捞出鸡爪。 \r\n2.用小刀在主骨及小爪的背面顺开一刀，将鸡爪整骨取出，即成鸡蹄花。剔好的鸡蹄花用干净湿布盖好，以免水分散失。食时，依各入口味可分别配上红油、蒜尼、椒麻味碟蘸食。', NULL, NULL, NULL, '公鸡脚爪500克。 红油20克、蒜泥10克、椒盐10克。');
INSERT INTO `recipe` VALUES (4887, 65, '五柳鱼丝', '将鲜鲤鱼去鳞。去鳃，剖腹去内脏洗净，剞去骨刺后将净肉切成粗丝，放入钵内加精盐、料酒腌渍码味。熟火腿、熟冬笋、香菌、丝瓜皮（淖熟）、泡红辣椒（去蒂、籽）均切成细丝。炒锅置火上，下熟猪油烧热（约80℃），将腌渍入味的鱼丝用蛋清豆粉糊上浆后入名滑散。滗去汆油。将鱼丝拨一边，下火腿等丝略炒，烹滋汁翻簸，淋上少许鸡油起锅装盘即成。', NULL, NULL, NULL, '鲜活鲤鱼一条（约750克）。 熟火腿15克、冬笋刀克、香菌10克、丝瓜15克、泡红辣椒10克。大葱5克、精盐10克、料酒30克、熟猪油150克、湿淀粉30克、蛋清25克、鸡油10克。');
INSERT INTO `recipe` VALUES (4888, 65, '怪味白肉', '(1) 把熟芝麻用刀碾碎。葱切葱花。香菜切段。把猪肉煮熟晾凉。 \r\n(2) 将蒜泥、葱花、香菜段放入碗中，加入酱油、甜面酱、米醋、白糖、芝麻酱、辣椒油、熟芝麻和椒麻调拌均匀，并对成汁待用。 \r\n(3) 把煮好的肉切成长约3厘米、厚0.6厘米的肉条，放入盘内，浇上对好的汁即可。', NULL, NULL, NULL, '主料 猪肥瘦肉1公斤调料 芝麻酱、白糖、葱、香菜各25克，甜面酱10克，酱油15克，辣椒油、米醋少许，蒜泥5克，熟芝麻、椒麻各少许。');
INSERT INTO `recipe` VALUES (4889, 65, '萝卜炖羊肉', '将萝卜洗净，去皮切成块状。羊肉洗净切成条或块。陈皮洗净，姜洗净拍破，葱洗净切成节。把羊肉、陈皮放入锅内用武火烧开，打去浮沫，改用炆火煮半小时，再加入萝卜、姜、葱、料酒、盐，炖至萝卜熟透，加味精，装碗即成', NULL, NULL, NULL, '羊肉500克、萝卜1000克，陈皮10克。 料酒15克、葱20克、姜6克、盐3克、味精1克。');
INSERT INTO `recipe` VALUES (4890, 65, '乾坤蒸狗', '将狗肉皮在火上燎黄，用水泡软，刮洗后用凉水浸泡，待肉质发胀时，用木棒在肉上轻轻反复捶敲。边捶边洗至血水排尽肉质松软，将狗肉切条，用凉水泡上，将大冬、生地、枸杞、甘莫、柠檬洗净。鸡剔下胸脯肉，汆下的鸡肉和肘子各改成八块，姜切片，葱切节，鸡肉和肘子用姜、葱水泡上。把瘦猪肉和鸡脯肉分别用刀背剁茸，分别用姜、葱水泡上。炒锅置火上，放入清水，烧沸后放入一段柏木去臊气，再放狗肉、葱姜，料酒，用中火煮透，将狗肉捞出冲洗漂凉，鸡肉、时于用沸水氽透。锅内掺入清水，放鸡。肉，肘子、葱姜，用武火烧沸，打净浮沫，移炆火煮至肉熟时', NULL, NULL, NULL, '去骨连皮狗肉2000克，母鸡一只（约1000克）、猪瘦肉500克、肘子500克。 天冬10克、生地10克、枸杞15克、甘草3克、柠檬25克。葱25克，料酒20克、胡椒面3克、味精1克、香油20克、豆瓣50克。');
INSERT INTO `recipe` VALUES (4891, 65, '牛膝蹄筋', '将牛膝洗净，润后切成斜口片。蹄筋放入钵中，加水上笼蒸4小时，至蹄筋酥软时取出。再用凉水浸漂2小时，剥去外层筋膜，洗净，火腿洗净后切丝。蘑菇水发后切成丝，姜葱洗净后，姜切片，葱切段。把发涨的蹄筋切成长节，鸡肉剁成小方块，取蒸碗将蹄筋、鸡肉放入碗内，再把牛膝片放在鸡肉上面，火腿丝和蘑菇丝调合均匀，撒在周围。姜片、葱段放入碗中，上笼蒸约3小时，待蹄筋酥烂后出笼，拣去姜葱，加胡椒、料酒、盐、味精等调味即成', NULL, NULL, NULL, '猪蹄筋100克、鸡肉500克。 牛膝10克、火腿50克、蘑菇25克。胡相3克、料酒10克、味精1克、姜10克，葱10克、盐3克。');
INSERT INTO `recipe` VALUES (4892, 65, '水八块', '开膛仔公鸡清洗干净，入沸水锅中煮至刚熟捞出，晾凉。砍去鸡头、翅、腿（作它用），从腹中线剖开，分成背、胸、腹各一块，然后砍成八块，每块都应带骨，且成厚薄均匀的斜片。置入盆中，加盐、酱油、白糖等拌匀，再加入熟辣椒油拌匀，撒上花椒粉即成。', NULL, NULL, NULL, '开膛嫩仔公鸡一只（约500克）。 盐3克、酱油10克、白糖10克、熟辣椒油10克、花椒粉10克。');
INSERT INTO `recipe` VALUES (4893, 65, '凉粉鲫鱼', '活鲫鱼拍昏后，去鳞、鳃、内脏，洗净，在鱼身两侧各划几刀，抹上料酒、盐，用猪网油包好，放入蒸碗、上笼蒸至熟（约15分钟）。凉粉切成约1.3厘米见方的小块，入清水锅煮开，捞起滤干，加上由红油、豆鼓、蒜泥、芽菜末，葱花、花椒油等配合好的调料和匀。将蒸好的鱼取出，去掉网油。拈鱼入盘，倒上和好的凉粉即成。', NULL, NULL, NULL, '鲜活鲫鱼一条（约750克）。 白凉粉250克。料酒15克、猪网油200克、盐5克、红油15克、豆鼓10克、蒜泥5克、芽菜末10克、葱花5克、花椒油5克。');
INSERT INTO `recipe` VALUES (4894, 65, '葱辣鱼', '鲜鱼肉洗净，切成长约6厘米、宽约2厘米的条形，用盐、料酒、姜葱、胡椒粉拌匀，腌渍码味后，去尽汁水和姜葱。锅置旺火上，下素油烧热至200℃左右，下鱼条炸至呈黄色时捞起。倒去锅内油，另放素油入锅烧热，下葱段偏炒出香味，再下姜片、辣椒节稍偏，力口入鲜汤、盐、酱油、料酒和少许白糖、醋，待沸下鱼条，用中火烧至汁浓将干时，加入香油、辣椒油，起锅入盘晾凉。食用时以葱垫盘底，上放鱼条，去掉姜片和辣椒节，原汁淋于鱼条上即成。', NULL, NULL, NULL, '鲜鱼肉400克。 葱50克、盐5克、料酒20克姜15克、素油500克、泡辣椒15克、鲜汤50克、酱油10克、白糖15克、醋5克、香油、辣椒油各5克、胡椒粉2克。');
INSERT INTO `recipe` VALUES (4895, 65, '豆苗炒虾片', '(1)将虾剥壳去头，由脊背拉一口，将屎线挑出，清洗干净。把每只虾肉片成44片。姜切片。葱剖开切2厘米长的节段。把豆苗洗净，摘去尖。 (2)用25克湿淀粉和鸡蛋清调成糊.另用盐3克,味精3克,料酒10克把虾片拌匀,吃味,并浆上蛋糊. (3)再用所余的料酒,味精,盐,湿淀粉和汤50克对成汁备用.将灼勺烧热后注油,将虾片下进热油勺中滑熟后捞出.', NULL, NULL, NULL, '主料 大虾700克，豆苗500克，鸡蛋2个。调料 大油800克(实耗约100克)，料酒、净葱各25克，姜10克，盐7克，胡椒粉2克，湿淀粉40克，汤50克，味精5克。');
INSERT INTO `recipe` VALUES (4896, 65, '瓤甜椒', '甜椒在旺火上烧至周身起皱后，去掉皮、蒂柄，沿蒂柄处削下作盖，去净籽。猪肉剁细，加蛋清豆粉、川盐、味精、姜葱汁拌匀，瓤入甜椒腹内，盖上盖，封口处抹上蛋清豆粉粘牢后，逐个摆放于蒸碗内，用皮纸封住碗口，上笼用旺火蒸约20分钟（肉馅熟）取出。去掉皮纸，入盘摆成梅花型（盖不用）。炒锅内加清汤、料酒、味精烧沸，用水豆粉勾清二流芡，淋入鸡化油。浇于甜椒上即成。', NULL, NULL, NULL, '甜椒500克。 猪肥瘦肉100克。川盐2克、味精2克、姜葱汁10克、蛋清45克、豆粉30克、清汤200克、鸡化油10克、料酒10克。');
INSERT INTO `recipe` VALUES (4897, 65, '米熏鱼', '(1)鲜鱼去鳞、鳃，剖腹洗净，去掉牙骨，斩成斧头块，用盐、料酒、酱油、姜片、葱段腌渍15分钟取出。 \r\n(2)锅置旺火上，下素油烧热（约200℃），下鱼炸至呈金黄色捞起。 \r\n(3)滗去锅内一部分油（留油约75克），下葱、姜，火南至变色出味后，去掉葱、姜，加料酒、白糖、酱油、鲜汤搅匀。 \r\n(4)将锅移中火上，下鱼块，至滋汁收干起锅。土钵装烧红的木炭，放入大末（约25克）烧至起烟，将鱼盛入笛箕或特制的熏笼内，烟熏几分钟取出。吃时切成条形装盘即成。', NULL, NULL, NULL, '鲜鱼一条（约1000克）。盐5克、料酒30克、酱油20克、姜10克、葱10克、素油1000克、白糖20克、鲜畅1克。');
INSERT INTO `recipe` VALUES (4898, 65, '姜汁热窝鸡蛋', '母鸡宰杀洗净后，入锅煮熟，捞出晾凉后，去掉鸡腿骨，剁成均匀的块。姜成姜末，葱切成葱花。锅内放素油烧热（约15℃），下鸡块、姜末、葱花入锅炒出香味，加鲜汤、盐、酱油烧入味，加水豆粉和醋搅匀，淋上辣椒油即成。（此菜还有另一种烹法：鸡整治后，入锅稍煮，捞出斩成均匀的块，平放于碗中成三叠水，加调料，上笼蒸火巴，出笼拣去调料渣，翻扣于圆盘中。再将蒸鸡的原汁入锅，加姜汁及调料烧沸，勾二流芡收汁，起锅加香油，淋于鸡上即成）。', NULL, NULL, NULL, '鲜嫩母鸡上只（约1000克）。 姜10克、葱10克、素油50克、盐2克、酱油10克、豆粉15克、醋10克、辣椒油10克、鲜汤100克。');
INSERT INTO `recipe` VALUES (4899, 65, '陈皮鸡', '嫩鸡开膛洗净后，去掉头、颈、脚爪，剔去骨头，鸡肉切成约2厘米见方的块，盛入碗，加姜、葱、料酒、盐、酱油拌匀）腌渍码味。干辣椒擦净，去蒂、籽，切成短节。陈皮洗净撕成块。锅置旺火上，下菜油烧热（约150℃），去掉鸡块中的姜。葱。滗去什水后，鸡块下锅炸至呈金黄色（约5分钟），捞出，去掉炸油，另下菜油约150克，烧热，放入辣椒节、花椒、陈皮稍炸，随即放入鸡块，加少量鲜汤，烹入糖醋汁。用中火收至汤汁吐油时起锅，淋上少许香油，晾冷装盘即成。', NULL, NULL, NULL, '鲜嫩开膛鸡一只、（约500克）。 干辣椒4克、陈皮4克。姜、葱各10克、料酒10克、盐3克、酱油10克、菜油500克、鲜汤50克、糖20克、醋10克、香油5克。');
INSERT INTO `recipe` VALUES (4900, 65, '炒香舌片', '将酸枣仁（生熟均可）捣碎，用水煮两次，收取药汁700毫升。将猪舌洗净。切成两段，放入药汁中。把姜葱洗净拍破，与花椒一起下锅同猪舌同煮。煮至舌头七成熟时，打去浮沫，取出猪舌稍晾。将卤汁倒入锅中，炆火烧沸，再将猪舌放入卤锅中卤至熟透捞起。取适量卤汁倒入锅中，加冰糖、味精、盐在炆火上收成浓汁，加料酒，均匀地抹在猪舌上，再抹上香油，切片装盘即成。', NULL, NULL, NULL, '猪舌500克。 酸枣仁50克。姜10克、葱15克、花椒3克、料酒15克、卤汁450克、香油10克、味精1克、盐3克、冰糖25克。');
INSERT INTO `recipe` VALUES (4901, 65, '葱辣鱼条', '1.鱼去鳞、鳍、鳃和内脏，洗净，再将脊骨去掉，世成宽1.5厘米、长4厘米的条。葱剖开切6厘米长段。姜切片。干辣椒去籽。好收拾好的鱼用7克姜、10克葱、5克盐、15克酱油、20克料酒拌匀后，腌60分钟左右，然后将汁控去。 \r\n2.用炒勺将植物油烧到8成热，把鱼条下入，炸成8成熟后捞出控油。 \r\n3.炒勺上旺火烧热，注入植物油25克，将葱、姜稍煽后倒入汤加干辣椒、白糖和余下的酱油、料洒把炸好的鱼条也放进去，用火煽待汁浓浇入香油即成。吃时挑去葱、姜。', NULL, NULL, NULL, '鲤鱼(草鱼、桂鱼、鲢鱼均可)600克，净葱50克，干辣椒10个。调料 植物油800克(实耗约120克)，香油12克，净姜15克，酱油40克，盐5克，料酒30克，味精5克，汤260克，白糖少许。');
INSERT INTO `recipe` VALUES (4902, 65, '灯影苕片', '将红心苕洗净，切成长方片，再顶刀切成1毫米厚的片，放入明矾3克，清水90克的水中浸渍30分钟捞出，再放入盐3克，清水225克盐水中浸渍半小时捞出晾干水份；炒锅置火上放油烧至六成熟，将红苕片下油锅炸至棕黄色水份干时捞出，控干油盛入盘中；取碗一只，放入红油，精盐，味精，白糖兑成汁，浇在盘内的红苕片上，即可供食', NULL, NULL, NULL, '红心苕（红薯），生油（耗油），精盐，白糖，明矾，红油');
INSERT INTO `recipe` VALUES (4903, 65, '鱼香茄饼', '鸡蛋与干豆粉调成蛋糊。猪肉剁细，入碗加川盐、水豆粉、少量清水拌匀成肉馅。泡辣椒去籽剁细。茄子去外皮，切成连夹片（两刀一断，每片厚约3毫米），将肉馅逐一填入前片中。盐、酱油、白糖、醋、味精、水豆粉入碗，加少量鲜汤兑成鱼香味芡汁待用。炒锅置旺火上，放菜油烧热（约“150℃），将夹好馅的前饼，入蛋糊中粘裹均匀后，放入油锅炸至进皮捞起。待油温升高，再下前饼炸至呈金黄色时捞出。另取炒锅置火上，下菜油烧热（约80℃）。下姜、蒜末、泡辣椒炒香至油呈红色时，烹入芡汁、葱花，待收汁淋入香油，起锅浇在前饼上即成。', NULL, NULL, NULL, '鲜嫩茄子500克。 猪肥瘦肉75克、鸡蛋40克。豆粉35克、川盐3克、泡辣椒10克、酱油10克、白糖15克、醋5克、味精卫克、鲜汤50克、菜油500克、姜、蒜末各10克、葱10克、香油10克。');
INSERT INTO `recipe` VALUES (4904, 65, '夫妻肺片', '1.将牛肉、牛杂洗净。牛肉切成重约500克的大块，与牛杂一起放人锅内，加入清水（以淹过牛肉为度），用旺火烧沸，并不断撇去浮沫，见肉呈白红色，滗去汤水，牛肉、牛杂仍放锅内，倒人老卤水，放人香料包（将花椒、肉桂、八角用布包扎好）、白酒和精盐，再加清水400克左右，旺火烧沸约30分钟后，改用小火继续烧1．5小时，煮至牛肉、牛杂酥而不烂，捞出晾凉。\r\n2.卤汁用旺火烧沸，约10分钟后，取碗一只，舀入卤水250克，加入味精、辣椒油、酱油、花椒面调成味汁。\r\n3.将晾凉的牛肉、牛杂分别切成4厘米长、2厘米宽、0．2厘', NULL, NULL, NULL, '鲜牛肉、牛杂（肚、心、舌、头皮等）、老卤水各2500克，辣椒油、油酥花生未、酱油各150克，芝麻面100克，花椒面25克，八角4克，味精、花椒、肉桂各5克，精盐125克，白酒50克。');
INSERT INTO `recipe` VALUES (4905, 65, '芹黄烧鱼条', '1.将鱼去鳍、鳞、鳃和内脏，清洗干净，切成长6厘米、宽3厘米的长条。姜、蒜切末。葱切大葱花，留部分葱、姜拍破腌用。芹菜洗净去叶抽筋，切4厘米长段。用盐、料酒和拍破的葱、姜拌鱼，腌2小时入味。\r\n2.将鱼放入烧开的油中炸成黄色捞出，炒勺留50～100克油，将豆瓣酱炸酥出味后，入汤稍煮，即加入鱼、料酒、葱、妆、蒜，酱油、糖、胡椒粉，先旺火将汤烧开，再用文火煨熟。而后再加入芹菜稍氽,滴醋少许，用湿淀粉勾芡，并淋少量热油即成。', NULL, NULL, NULL, '鲜鱼1公手，芹菜芯250克。调料 植物油800克(实耗约100克)。豆瓣酱100克，料酒100克，酱油25克，白糖10克，葱、姜、蒜各10克，湿淀粉、醋少许，味精5克，胡椒粉少许，汤适量。');
INSERT INTO `recipe` VALUES (4906, 65, '豆鼓鱼', '鲜鱼肉洗净。切成长5厘米、宽1.2厘米的条形，用姜片、葱节、盐、料酒、胡椒粉拌匀腌渍码味。豆鼓剁细粒，葱切成长约3厘米的节，蒜切成圆片。锅置火上，下素油烧至约200℃，放入鱼条，炸至呈金黄色时捞起，倒去锅内油，另放少量干净素油烧热，下豆鼓炒干水分，下葱节、蒜片稍炒，倒入鱼条；加料酒、盐、酱油、鲜汤、胡椒粉炒匀略烧，改用小火收至汁浓将干时，加入香油，起锅装盘晾凉。食用时，将鱼条摆在盘内，去掉葱、蒜，原汁淋在鱼上即成。', NULL, NULL, NULL, '鲜鱼肉450克。 潼川豆鼓50克。素油500克、葱15克、蒜15克、料酒10克，盐5克、酱油10克、鲜汤50克、胡椒2克、香油10克、姜10克。');
INSERT INTO `recipe` VALUES (4907, 65, '干烧明虾球', '①大明虾肉用刀片开背部，使虾体成排状，抽取中间肠泥后，放入水中洗净并用布吸干水分。再以蛋白半个、玉米粉1茶匙、盐1/4匙交互拌匀备用。\r\n②姜、葱、蒜各切成碎末状备用。\r\n③把腌好虾肉入油锅，用16O℃中温油过油30秒钟捞出。锅中留1大匙油，先炒香辣豆酱，待油色鲜红，豆瓣生香后，再放入姜、蒜末和清水3大匙、甜酒酿1茶匙，将虾球肉微焖至熟，随即下凋味料拌炒，使汤汁变浓稠，最后洒人葱花提鲜即可起锅。', NULL, NULL, NULL, '大明虾6只、姜、葱、蒜各30g、甜酒酿1茶匙、辣豆瓣1茶匙、蛋1个．玉米粉1茶匙。味精1/4匙、糖1/2匙、白醋1茶匙、酱油1茶匙、日本太白粉l/2匙、麻油、红油各1/2匙、蕃茄酱1茶匙。');
INSERT INTO `recipe` VALUES (4908, 65, '酥皮龙虾', '1． 龙虾洗净，剔出虾肉，剁成绿豆大小的粒；荸荠洗净去皮剁成细粒；姜、葱捣成汁；西兰花入沸水锅中焯断出捞出，用川盐、味精、麻油拌上味；蒜薹焯断生拌味后编成辫形花待用。2． 龙虾肉粒、荸荠粒入盆，加姜、葱汁、川盐、味精、鸡蛋清、湿淀粉、胡椒粉搅匀呈干糊状备用。 　　\r\n3． 取大盘一个，先撒上一层面粉，再将虾茸挤成24个圆子，放在面包粉上，滚粘上一层面包粉，待用。 　　\r\n4． 锅置火上，放精炼油烧至七成热时，将龙虾壳整理成形，入锅炸至定型成熟后，捞入盘中摆好，再将虾茸圆子入锅炸至呈金黄色且熟时，捞出入虾壳', NULL, NULL, NULL, '龙虾1只（约1 500克），荸荠100克，蒜薹250克，西兰花100克，面包粉250克，川盐、 鸡蛋清、胡椒粉、味精、姜、葱、湿淀粉、椒盐味料、精炼油各适量');
INSERT INTO `recipe` VALUES (4909, 65, '鹰扬虎视', '1.先将猪舌、蛋黄糕、蛋白糕分别切成雀翅片；皮蛋切成羽毛片；张飞牛肉切成羽毛片和瓜子片；大红辣椒切成瓜子片；蛋 卷 、香肠分别切成片；西兰花切成小朵，蛋糕刻成鹰嘴和鹰爪；黄瓜切刻成松针和花的枝杆；香菇切成松肢。 　　\r\n2.取大白盘1个，把拌好的盐水鸡丝放入盘子上端，堆摆成雄鹰雏型，再依次用张飞牛肉、猪舌 、蛋黄糕、蛋白糕、皮蛋等片料，拼摆出鹰翅、鹰身，嵌入刻好的鹰嘴和鹰爪，鹰即拼摆完成。 　　\r\n3.香莴笋切片摆成荷叶，置雄鹰左下方，红辣左下方，红辣椒摆成荷花；蛋卷 、大虾、香肠、西兰花堆摆成荷塘小景；', NULL, NULL, NULL, '盐水大是250克　腌腊猪舌205克　盐水鸡丝150克，张飞牛肉100克，香肠100克，蛋卷150克，蛋黄糕100克，蛋白糕50克，皮黄80克，大红辣椒2个，花椒籽1粒，西兰花100克，麻油莴笋50克');
INSERT INTO `recipe` VALUES (4910, 65, '北菇火腿炆大芥菜', '1、先将炆菜的汤料加水煎浓汤。\r\n2、将大芥菜洗净，摘去老叶后开边，以滚水略煮以去掉苦味，然后用油炒香。\r\n3、用浓汤将大芥菜炆至蔫，切件后加火腿、北菇再蒸热，最后用浓汤调蚝油打芡即成。', NULL, NULL, NULL, '主料： 包心大芥菜2斤（约1.2公斤），金华火腿12片，北菇12朵。 \r\n配 料： 汤料：五花腩肉4两（约160克），甘草2片，姜4片，虾米1两（约40克），北菇脚适量。');
INSERT INTO `recipe` VALUES (4911, 65, '乡村嫩鸡', '1、仔公鸡宰杀小开洗净，尖青椒去蒂，花椒去籽，大蒜去皮成瓣，大葱去筋洗净。 \r\n2、炒锅内水烧沸，下老姜（拍破），放入仔鸡煮至断生（约10分钟）起锅，立即放入冷开水中浸泡待用。 \r\n3、尖青椒、花椒、蒜瓣、大葱、盐适量置于菜墩，用刀慢慢铡细，一面铡一面加入麻油，铡至调料呈细粒状，盛于碗中待用。另外，将一小碗煮鸡汤、精盐、味精对成味汁。 \r\n4、将鸡斩成块装盘，淋上味汁，放上青椒调料，炒锅将菜油烧至七成热，淋于青椒调料及鸡块上即成。', NULL, NULL, NULL, '仔公鸡1只（约1500克），尖青椒25克，菜油50克，老姜10克，大葱10克，大蒜15克，花椒10克，味精5克，麻油5克，精盐适量。');
INSERT INTO `recipe` VALUES (4912, 65, '酸菜肚片汤', '先将猪肚清洗干净，放入热水中略烫一下，再切成片状，把酸菜叶梗切部分成小段，如叶片过厚，再切成2片；笋洗净去壳，切成薄片。高汤放入锅中煮开，再放入肚片、酸菜笋片同煮，等高汤在开时加入盐、味精调味，改用小火继续煮10-15分钟，见肚片酥软，即可熄火，盛盘食用，淋上麻油，味道更香。', NULL, NULL, NULL, '猪肚1个、酸菜140克、高汤5碗、盐1小匙、味精半匙、麻油少许、笋1支。');
INSERT INTO `recipe` VALUES (4913, 65, '丁香鸭', '将鸭宰杀后，去毛和内脏，洗净。丁香、肉桂，豆寇用水煎熬两次。每次水沸后20分钟即可淫去药汁，取两次药汁合并倒入锅内，姜葱洗净拍破，同鸭子一起放锅中，鸭子淹没在药汁中，月炆火煮至六成熟，捞起稍凉，再放入卤汁锅内，用炆火卤熟后捞出。取适量卤汁放入锅内，加盐、冰糖、味精拌匀，放入鸭子，在炆火上边滚边浇卤汁，直到卤汁均匀地粘在鸭子上，色泽红亮时取出，抹上香油，切块装盘即成。', NULL, NULL, NULL, '鸭子一只（约1000克）。 丁香5克、肉棒皮5克、豆寇5克。生姜15克、葱20克、盐3克、卤汁500克、冰糖30克、味精1克、香油25克。');
INSERT INTO `recipe` VALUES (4914, 65, '魔芋烧鸭', '将净鸭去鸭头，颈，翅尖，脚掌，剔去大骨，斩成条。水魔芋切成条，放沸水锅内氽两次，去掉石灰味，再漂在温水内；炒锅置旺火上，下猪化油烧至七成热时，放入鸭条煸炒至浅黄色起锅，再将锅洗净加入肉汤烧沸，捞出花椒和豆瓣渣，放入鸭条，魔芋条，姜，蒜，绍酒，川盐，酱油烧至汁浓鸭软，魔芋入味时，加入青蒜苗，味精，用温淀粉勾薄芡起锅装盘即成。', NULL, NULL, NULL, '嫩肥鸭，水魔芋，青蒜苗段，绍酒，川盐，酱油，味精，郫县豆瓣，蒜片，湿淀粉，花椒，肉汤，猪化油');
INSERT INTO `recipe` VALUES (4915, 65, '烟熏排骨', '排骨3根相连横斩成大块，加盐、姜片、葱段、五香粉、料酒、花椒拌匀码味20分钟，入笼蒸至刚熟取出，入卤水锅中加热至肉能离骨时取出，再放入热油锅中炸至色泽金黄、肉质干香时起锅。 2.炸好的排骨放入熏炉中，点燃烟熏料，熏至排骨色暗红、烟香入味时取出，刷上香油，斩成小块装盘。', NULL, NULL, NULL, '猪排骨、五香粉、盐、料酒、姜、葱、醪糟汁、卤水、烟熏料、香油、花椒、熟菜油。');
INSERT INTO `recipe` VALUES (4916, 65, '葱辣鱼', '鲜鱼肉洗净，切成长约6厘米、宽约2厘米的条形，用盐、料酒、姜葱、胡椒粉拌匀，腌渍码味后，去尽汁水和姜葱。锅置旺火上，下素油烧热至200℃左右，下鱼条炸至呈黄色时捞起。倒去锅内油，另放素油入锅烧热，下葱段偏炒出香味，再下姜片、辣椒节稍偏，力口入鲜汤、盐、酱油、料酒和少许白糖、醋，待沸下鱼条，用中火烧至汁浓将干时，加入香油、辣椒油，起锅入盘晾凉。食用时以葱垫盘底，上放鱼条，去掉姜片和辣椒节，原汁淋于鱼条上即成。 　', NULL, NULL, NULL, '鲜鱼肉400克。 葱50克、盐5克、料酒20克姜15克、素油500克、泡辣椒15克、鲜汤50克、酱油10克、白糖15克、醋5克、香油、辣椒油各5克、胡椒粉2克。');
INSERT INTO `recipe` VALUES (4917, 65, '糖醋红柿椒', '1.将红柿椒用清水洗净，去柄和瓢，切成长块。 \r\n2.将炒勺烧热注油，先下红柿椒炒熟，再加盐和糖翻炒，随后加醋炒匀即成', NULL, NULL, NULL, '红柿椒500克。调料:香油40克，醋40克，盐5克，白糖60克。');
INSERT INTO `recipe` VALUES (4918, 65, '清蒸青鳝', '青鳝杀后，放尽血，用热水烫去皮面的涎液（可用小刀轻刮鱼皮），剪去鱼翅。将鱼切成2厘米长的节（或先用刀将骨刺切断，把鳝身断为两截，抽去内脏）。在蒸碗中铺上网油，将鳝段立放在网油上。料酒加胡椒面、盐、花椒拌匀，淋于鳝段上。猪肉切成3.3厘米长（约0.5厘米厚的片，放在鳝段上，用草纸封上碗口，上笼蒸熟取出。另用蒸碗，放入蛋清和凉清汤搅匀，上笼蒸成白色芙蓉蛋，取出切块。竹荪切成2厘米长的段。在汤中末熟。青鳝蒸碗取出后，加清汤过两次，滗干，翻扣于大圆盘中，揭去网油，周围镶上芙蓉蛋和竹荪。特级清汤烧沸，加盐、胡椒粉、', NULL, NULL, NULL, '鲜活青鳝750克。 猪网油59克、猪肥瘦肉100克、蛋清50克、竹苏50克、清汤750克。料酒15克、胡椒面3克、盐5克、花椒2克、味精1克、姜1克、醋20克。');
INSERT INTO `recipe` VALUES (4919, 65, '泡菜鱼', '鲫鱼去鳞、鳃，剖腹去内脏后洗净，在鱼身两面各立划几刀。泡青菜（只用菜帮）切成1.5厘米长的细丝。泡辣椒、姜、蒜剁细末。葱切成细花。炒锅置旺火上，倒菜、。油烧热至约200℃，放入鲫鱼炸约3分钟至两面略黄，滗去部分油，锅内留油约100克，将鱼推至锅边，下泡辣椒、姜、蒜、醒糟汁、葱煵出香味。再依次放入料酒、酱油、汤等，鱼推回锅中，用中火烧沸，放入泡青菜烧约10分钟（烧的过程中翻面），待鱼入味后捞起装入盘内，锅内放葱花、香油，勾薄芡后，将汁浇在鱼上即成。', NULL, NULL, NULL, '鲜活鲫鱼三条（每条约150克）。 泡青菜50克。泡辣椒15克、姜、蒜各10克、葱15克、菜油5侧克、醒糟汁10克、料酒10克、酱油10克、鲜汤150克、香油10克、淀粉15克。');
INSERT INTO `recipe` VALUES (4920, 65, '牛蛙火锅', '1.牛蛙宰杀去头的内脏，暂成4厘米见方的块，入汤锅汆水捞起。\r\n2.青笋，茭白，白萝卜去外皮，改成小块洗净装入火锅盆待用。\r\n3.姜切成指甲片，葱切成“马耳朵”形。\r\n4.炒锅置炉上，下油加热，放豆瓣，姜片，葱，花椒，干辣椒，炒香并呈红色，放牛蛙肉，整蒜子，冰糖，炒酥，掺红汤，下味精，鸡精，胡椒粉，料酒，烧沸，出尽浮沫，倒入盛有青笋，茭白，白萝卜的火锅盆，上台即可。', NULL, NULL, NULL, '牛蛙1500克，青笋200克，茭白200克，白萝卜200克。调料：精制油100克，姜5克，蒜5克，葱5克，干辣椒5克，花椒3克，冰糖3克，郫县豆瓣100克，味精10克，鸡精20克，料酒20克，胡椒粉5克，红汤2500克。');
INSERT INTO `recipe` VALUES (4921, 65, '鲫鱼砂锅', '1.玉兰片切成菱形，盒装豆腐，一分为七，鲜蘑菇一分为二，洗净，装入砂锅待用。\r\n2.姜蒜切片，葱，泡红椒切成“马耳朵”形。\r\n3.河鲫鱼去鳞，鳃和内脏，入油锅炸至金黄色取出。\r\n4.炒锅置上火，下油加热，放姜蒜片，葱，泡红椒，炒香。掺白汤，放河鲫鱼，味精，鸡精，料酒，胡椒粉，烧沸，去尽浮沫，倒入锅内，上台即可。', NULL, NULL, NULL, '河鲫鱼3条，玉兰片200克，盒装豆腐2盒，鲜蘑菇200克。调料：精制油50克，姜5克，蒜5克，葱5克，泡红椒3克，味精15克，鸡精20克，胡椒粉3克，料酒20克。');
INSERT INTO `recipe` VALUES (4922, 65, '肥肠砂锅', '1.草菇、香菇一分为二，鲜笋切棱形。洗净、装入砂锅内待用。\r\n2.姜蒜切片、葱、泡红椒切成“马耳朵”形。\r\n3.肥肠洗净上笼，蒸至8成熟取出，切成4厘米大小的块。\r\n4.炒锅置火上，下油加热，放姜蒜片、葱、泡红椒、肥汤，炒香。掺白汤，放味精，鸡精、料酒、胡椒粉，烧沸，去尽浮沫，倒入砂锅内，上台即可。', NULL, NULL, NULL, '肥肠800克、草菇200克、香菇200克、鲜笋200克。调料：精制油50克、姜5克、蒜5克、葱5克、泡红椒5克、胡椒粉3克、料酒15克、味精15克、鸡精20克、白汤2500克。');
INSERT INTO `recipe` VALUES (4923, 65, '墨鱼辣酱拌幼面', '1.墨鱼切好洗净，切薄片。红萝卜及洋葱切丝。 \r\n2.用油炒香蒜茸，加入洋葱丝、红萝卜丝和墨鱼片炒匀、倾入调味料、慢火煮二十分钟、煮成有浓烈香辣味的酱。 \r\n3.韩式幼面放入滚水内煮五分钟，捞起沥干水分上碟，拌上墨鱼辣酱供食。', NULL, NULL, NULL, '材料：韩式幼面六两，墨鱼一只（十六两），红萝卜四两，洋葱一个，蒜茸半汤匙。 \r\n调味料：韩式辣椒膏三汤匙，辣椒粉一茶匙，盐半茶匙，水二杯');
INSERT INTO `recipe` VALUES (4924, 65, '贝茸燕菜', '1、将干燕窝用温水泡上，泡2个小时之后滗掉水，用尖头镊子挑净燕毛，然后用冷水洗2次，再控净水 2、干贝剔净筋皮，洗净，用刀背砸成细泥茸状；火腿切成细丝 \r\n3、把砸好的干贝泥茸加入少许凉鸡汤调匀，用小眼漏勺把调好的干贝泥过滤一下，然后，加入3个鸡蛋清，再加入少许料酒、盐，用手朝一个方向搅上劲，即成鲜贝茸 \r\n4、锅内放入250毫升的鸡汤，稍许加点料酒、盐、味精，把洗净的燕菜投入锅内，用小火煮4-5分钟，待燕菜煮透入味后，捞出，轻轻挤净汤汁 \r\n5、锅内放入清水，上火烧开，火要小一点，把燕菜放在搅好的贝茸里', NULL, NULL, NULL, '干燕窝25克，鸡汤1.5公斤，干贝100克，鸡蛋清3个，净火腿15克，白糖10克，盐10克，水淀粉15克，料酒15克，味精2克，芝麻油10克，芝麻15克（焙好） 。');
INSERT INTO `recipe` VALUES (4925, 65, '苦瓜烘蛋', '1、鸡蛋磕入碗中，加精盐、味精、面粉、干淀粉及少许清水搅打均匀；苦瓜切成细粒，用精盐略腌渍后挤干水分。\r\n2、炒锅置上火，放入精炼油烧热，先倒入搅匀的鸡蛋液摊开成蛋饼，再将苦瓜粒撒在蛋饼上，然后加盖烘至蛋饼两面酥黄且熟（烘时需洒入少许清水），起锅装盘，将蛋饼切为条状待用。\r\n3、净锅上火，放入少许精炼油烧热，下入番茄酱炒香，掺入少许清水，调入少许精盐、白糖和白醋，起锅浇在盘中蛋饼上，即成。\r\n特点：蛋饼酥香，酸甜微苦。', NULL, NULL, NULL, '鸡蛋5个 苦瓜150克 面粉30克  干淀粉70克  番茄酱30克  精盐、味精、白糖、精炼油各适量。');
INSERT INTO `recipe` VALUES (4926, 65, '大蒜鲶鱼', '在鲶鱼背上割4—5刀；备好蒜头，油入锅烧热，先将蒜头、蒜末炒香，再加入姜末、辣豆瓣酱、盐、糖、味精、酱油匀后，放入鲶鱼、高汤同煮，汤滚起后改用小火焖煮10——15分钟。鱼身要翻面，见熟透夹出，在锅中放入葱花、辣油拌炒数下，再用太白粉勾芡，起锅前加点醋，淋浇在鱼身上。', NULL, NULL, NULL, '鲶鱼3条、蒜头1碗、蒜末2小匙、姜末1匙、葱花1匙、辣豆瓣酱2大匙、盐2小匙、糖1匙、味精1小匙、酱油2匙、醋3匙、高汤2碗、太白粉水1大匙、油6匙');
INSERT INTO `recipe` VALUES (4927, 65, '辣味过江鸡', '1.鸡开膛洗净，在汤锅内贴煮后捞出，用温水再洗一遍，然后用料酒、盐、味精、胡椒面，将鸡全身抹匀，装盆，把姜、葱节、花椒放在鸡上，腌1小时。 2.鸡上笼蒸至熟软，取出晾凉，从鸡背上划一刀，去尽鸡么，在鸡膛内的一面划成长方块（注意保持鸡皮完整），胸皮朝下装碗内，将其余鸡肉切长块摆在上面，加汤上笼蒸透，取出翻扣圆盘中，将蒸鸡原汁浇在鸡上。 3.番茄各切4瓣，去皮、去籽浆，烫热，摆在盘边周围。 4.将红油辣椒、油酥豆瓣、酱油、白糖、醋、蒜泥、葱花、芝麻酱、熟芝麻、精盐加汤，对成汁，装4个小碗，同鸡一起上桌即成。', NULL, NULL, NULL, '鲜肉鸡1只(重约2000克)，蕃茄2个，红油辣椒20克，油酥郫县豆瓣20克，酱油25克，精盐5克，味精2克，胡椒面1克，料酒25克，白糖15克，醋15克，蒜泥15克，葱花15克，芝麻酱25克，熟芝麻15克，姜片25克，葱节25克，花椒20粒，清汤适量');
INSERT INTO `recipe` VALUES (4928, 65, '花椒鱼片', '1、草鱼宰杀去鳞、去鳃，剖腹去内脏洗净，然后骨去头片成鱼片。葱切节，姜切片。金针菇洗净入沸水略煮，捞出盛入钵内打底。鱼片加料酒少许，码蛋清豆粉待用。 2、炒锅置旺火上，加入色拉油50克烧至六成热，下姜片、葱节爆炒出香味，掺入清汤加料酒、盐、胡椒粉、鸡精烧沸。将码好味的鱼片放入煮至九成熟起锅装入钵内，放入味精。 3、另锅置旺火上，放色拉油50克烧至七成热，下花椒炸香起锅淋在鱼片上面即成', NULL, NULL, NULL, '草鱼1条（约1000克），金针菇200克，大葱50克，花椒30克，精盐3克，味精5克，鸡精3克，料酒25克，胡椒粉2克，色拉油100克，鸡蛋1个，豆粉20克，清汤200克，老姜20克');
INSERT INTO `recipe` VALUES (4929, 65, '泡酸菜烧鸡', '1、仔公鸡宰杀后治净，取出鸡杂另作它用，将鸡肉斩成3厘米大小的块；泡酸菜切细。 2、炒锅置火上，放入精炼油烧热，下入姜片、葱节爆香，再下入鸡块煸炒并烹入料酒，炒至鸡块水气收干且吐油时，下入家常豆瓣和泡酸菜炒匀，最后掺入鲜汤，调入胡椒粉和白糖，烧至鸡块入味且粑软时，调入味精，起锅装盘，撒上葱花即成', NULL, NULL, NULL, '仔公鸡1只（约1500克），泡酸菜250克，家常豆瓣50克，姜片、葱节、葱花、胡椒粉、料酒、味精、白糖、鲜汤、精炼油各适量');
INSERT INTO `recipe` VALUES (4930, 65, '剁椒贵妃贝', '1、将贝壳弄开冲洗净，用直刀法在贝肉上划几刀，将贝肉还原置于贝壳内，摆在盘内。老姜切成米。小葱切成葱花。 2、剁椒用水、姜米、干豆粉、味精、盐拌均，淋于贝壳肉上，上笼蒸几分钟。 3、在将出笼的贵妃贝上撒入小葱花，将葱油烧至七成热，炸于贝肉上即成。', NULL, NULL, NULL, '贵妃贝500克，剁椒50克，味精2克，盐2克，老姜10克，小葱10克，干豆粉5克，葱油10克。');
INSERT INTO `recipe` VALUES (4931, 65, '小米蒸排骨', '1、排骨洗净，斩成4厘米长的节。豆瓣剁细，姜切米，葱切花，小米淘洗干净后用水浸泡待用。 2、排骨加豆瓣、甜酱、冰糖、料酒、料酒、精盐、味精、姜米、菜籽油抖匀，装入蒸碗内，然后在排骨上面装上小米，上笼锅用旺火蒸熟，取出扣入圆盘内，撒上葱花。 3、锅置旺火上，放入麻油烧至七成热起锅淋于葱花上面即成', NULL, NULL, NULL, '猪签排500克，小米150克，红豆瓣15克，菜籽油15克，料酒10克，冰糖8克，甜酱5克，精盐2克，味精10克，大葱10克，老姜5克，麻油10克');
INSERT INTO `recipe` VALUES (4932, 65, '萍乡驴肉', '1、驴肉冲血洗净，加卤料煮40分钟煮熟。 2、煮熟凉干后切片备用。 3、净锅烧油4成熟放入姜蒜、干辣椒编炒，放入驴肉，加入料酒，放入原汁驴肉汤，然后放精盐、味精、白糖、胡椒粉、酱油、烧2-3分钟即可，起锅装盘，下面垫一点香菜，浇上自己调好的秘酱，浇上干辣椒角即成。', NULL, NULL, NULL, '新鲜驴肉、香菜、干辣椒');
INSERT INTO `recipe` VALUES (4933, 65, '怪味鸡丝', '1、鸡去内脏洗净，入锅煮，汤烧开撇尽浮沫，煮至刚熟，捞入凉开水中，漂凉后去骨，切成约9厘米长、0.3厘米粗的二粗丝，盖在葱丝上。 2、取一只碗，放入酱油、白糖、盐、醋、花椒面、芝麻酱、熟芝麻、味精、香油、红油辣椒、姜末、蒜泥、油酥郫县豆瓣、葱花，加适量鸡汤，对成“怪味汁”，浇在鸡丝上即成。此法也可用于兔肉、猪肚、口条、鸭条等，做成各种“怪味”食品', NULL, NULL, NULL, '嫩公鸡1250克，酱油60克，白糖25克，醋20克，盐、花椒面各5克，红油辣椒50克，芝麻酱20克，熟芝麻20克，香油20克，味精5克，姜末10克，蒜泥10克，油酥郫县豆瓣30克，葱花10克，葱丝、鸡汤各适量。');
INSERT INTO `recipe` VALUES (4934, 65, '辣豆瓣鱼', '鲤鱼洗净，两边鱼身各剖三刀。油烧至8成熟，将鱼滑下煎至两边金黄，盛起。烧红锅，爆香葱、姜、蒜蓉放入鱼，加豆瓣酱、酒及调味煮五分钟后用生粉上欠上盘即可。', NULL, NULL, NULL, '鲤鱼一条，姜4片，葱、蒜、红辣椒适量，豆瓣酱2勺，酒、糖、生粉、番茄汁、适量');
INSERT INTO `recipe` VALUES (4935, 65, '姜汁热味鸡', '将鸡肉斩成块，炒锅置旺火上，下菜油烧至七成热，放入鸡块，姜，煸炒约2分钟，加川盐，葱花，稍煸炒后，加入酱油，肉汤，烧约5分钟至入味后，再加入剩下的葱花，用湿淀粉勾芡，放醋，待收汁这油后和匀即成', NULL, NULL, NULL, '净熟公鸡，姜末，川盐，葱花，酱油，醋，湿淀粉，肉汤，熟菜油');
INSERT INTO `recipe` VALUES (4936, 78, '兰花春笋', '虾泥鱼茸加鸡蛋清，顺一个方向搅成馅，加调料拌入味；笋尖取上段，从顶部剖十字切成兰 花形，用凉水漂净后上笼蒸熟； \r\n将馅料挤成24只丸子，蒸熟后置于“兰 花”中间，撤上火腿末； \r\n青椒切丝汆熟围边，勾薄芡即成。', NULL, NULL, NULL, '带尖春笋24只,火腿末15克,青椒50克,鸡蛋清1克,虾仁泥、草鱼茸适量');
INSERT INTO `recipe` VALUES (4937, 78, '炒豆腐脑', '1.雪里蕻洗净，挤干切末，海米切末； \r\n2.青蒜苗切段，嫩豆腐片去皮制成泥； \r\n3.油烧至6成热，放葱姜末呛锅，再放入豆腐泥，海米，雪里蕻，精盐，料酒，炒3分钟装盘，撒上葱段即成。', NULL, NULL, NULL, '嫩豆腐500克、水发海米50克、咸雪里蕻25克。');
INSERT INTO `recipe` VALUES (4938, 78, '桂花鱼条', '将鱼肉批成长方条，加精盐1.5克、味精1克和绍酒、胡椒粉、姜汁水等拌渍待用。鸡蛋磕入碗中，加精盐0.5克、味精0.5克打散，加湿淀粉搅匀，然后加入面粉40克、清水适量调成蛋糊，炒锅置中火上，下猪油至五成热（约1l0℃）时，将鱼条沾面粉拖蛋糊下锅炸至结壳捞出，捡去碎末，待油温回升成六成热（132℃）时，将鱼条全部倒入复炸至金黄色捞出，装盘即成。上桌随带甜面酱、椒盐碟。', NULL, NULL, NULL, '鲈鱼肉200克。 鸡蛋2个。湿淀粉15克、面粉50克、椒盐适量、精盐2克、绍酒5克、姜汁水10克、胡椒粉1克、甜面酱25克、猪油1000克（实耗油110克）。');
INSERT INTO `recipe` VALUES (4939, 78, '炸鸡肉串', '(1) 把鸡胸脯肉的脂皮、白筋去掉、坡着刀切成长2厘米、宽1.5厘米厚0.6厘米的块。放入碗中加入清水(40克)、盐(半量)，浸渍待用。 \r\n(2)把西红柿洗干净后，再用开水烫一下，去掉皮和籽，剁碎待用。 \r\n(3) 将盐(余量)、料酒、姜汁、味精、胡椒粉等放入盛鸡肉的碗内，先使其入味，然后再加入葱头末、西红柿、面粉、芝麻粉、鸡蛋清等，搅拌均匀，放入冰箱内冷藏1小时左右取出来。 (4) 用银扦10～12根，分别把鸡肉块穿成串(每串5～6块)，摆入盘中。 \r\n(5) 炒勺内倒入香油，在旺火上烧到7～8成热，', NULL, NULL, NULL, '鸡胸脯肉600克，西红柿80克，葱头末60克，鸡蛋清60克。调料：香油800克(实耗约110克)，芝麻粉40克，面粉60克，胡椒粉2克，姜汁1克，盐2克，料酒6克，味精4克。');
INSERT INTO `recipe` VALUES (4940, 78, '玛瑙野鸭片', '将野鸭肉顺丝撕成片条，加干淀粉拌匀。豆腐皮润潮后撕去边筋，每张切成6份。荸养去皮切片，栗子切片。炒锅置中火上烧热，下菜油至六成热（约150℃）时，将鸭肉片放入油锅划散，炸至结壳，用漏勺捞出（锅中的油用作炸豆腐皮用）。另取炒锅，置中火上烧热，下猪油25克，放入葱段煸至有香味，即加入绍酒和清汤，捞出葱段放入酱油、精盐、荸荠片、栗子片、绿蔬菜，烧沸后加入味精，用湿淀粉勾薄芡，倒入鸭肉片，淋上芝麻油，起锅盛入碗中。将原菜油置中火上烧热，把豆腐皮抖松入锅，炸到金黄时，即捞出装在另一碗中，和鸭肉片同时上桌。临食时把鸭', NULL, NULL, NULL, '熟野鸭胸脯肉100克。 豆腐皮6张、栗子肉25克、荸荠25克、绿蔬菜10克。酱油10克、精盐2克、味精2.5克、葱段3克、绍酒10克、清汤350克、干淀粉15克、湿淀粉25克、熟菜油1000克（约耗100克）、熟猪油25克、芝麻油5克。');
INSERT INTO `recipe` VALUES (4941, 78, '炒四宝', '将肚尖，鸡脓均剖成木梳片，用精盐0.5克、绍酒1克搅匀，加湿淀粉5克上浆，火腿切马鞍片待用。炒锅置火上烧热，下猪油，至四成热（约88℃）时，将肚尖、鸡片、鸡肫、虾仁一起入锅，划散至熟，倒入漏勺沥去油，原锅留油少许，放入葱白炝锅，下鸭掌、火腿、香菇、笋片，放入绍酒5克及清汤，加精盐1克，烧沸后加味精，用湿淀粉15克勾薄芡，立即倒入肚尖、鸡肫、鸡片、虾仁等料及蔬菜，翻颠炒锅，淋猪油25克，推匀出锅装盘即成。', NULL, NULL, NULL, '鸡脯肉400克、净鸡肫50克、剔骨鸭掌50克、净肚尖50克。 辅料：熟火腿25克、浆虾仁50克、熟笋片25克、水发香菇15克、绿菜叶15克、蛋清1／4个。湿淀粉25克、清汤750克、熟猪油500克（耗油60克）、大葱白2克、精盐3克、味精2.5克、绍酒25克。');
INSERT INTO `recipe` VALUES (4942, 78, '酸豆角炒肉末', '1.酸豆角洗净，用温水稍泡片刻，捞出切成0.5厘米的小段；猪肉切成肉末待用。 \r\n2.炒锅置旺火上，先将酸豆角下锅炒干水分起锅。锅内放猪油烧热，倒入肉末，加盐稍炒，再放入酸豆角合炒，接着放蒜泥、干椒末、酱油拌炒，加清水50克焖熟，收干汤汁，放味精起锅即可。', NULL, NULL, NULL, '酸豆角250克，猪肉（肥瘦各半200克），蒜泥10克，干椒末2克，熟猪油、精盐、味精、酱油各适量。');
INSERT INTO `recipe` VALUES (4943, 78, '肉茸菠菜', '1.将菠菜择洗净，裹匀面粉待用；能脊肉与肥膘肉同用刀斩成茸，加蛋清和适量水，将淀粉周匀成糊。 \r\n2.锅内放水1000克，上为烧以徽开，菠菜放入里脊糊中拖匀，下锅中氽熟捞出。 \r\n3.锅内放油烧热，下入蘸上蛋液的整棵菠菜，油煎两面呈金黄色时，加适量清汤调烧开，下菠菜，待汤再开后撒味精、盐，少许淀粉勾芡，淋香油，翻勺出锅装盘。', NULL, NULL, NULL, '菠菜400克，猪通脊肉200克，蛋清3个、清汤200克，猪油50克，肥膘肉 50克、葱、姜，花椒水，香油，盐味精，面粉，湿淀粉各适量。');
INSERT INTO `recipe` VALUES (4944, 78, '五香肥鸭', '将鸭子宰杀，煺毛洗净。在离尾端约5厘米的背部横开一刀口、取出内脏，去掉鸭臊，用水洗净，敲断脚骨，背部用尖刀直划一刀，放入沸水中氽约3分钟。取出洗净血污，全身涂上酱油待用。香干批三角片（每块批六片），笋尖切5厘米长、0.3厘米厚的片。姜块拍松。炒锅置中火上，加熟猪油至八成热（约200℃）时，将鸭子腹部朝下入锅油炸，然后翻身，炸至两面呈金黄色时捞出。将香于入油锅一下即捞起。炒锅内留油15克，将葱结、姜块、桂皮、鸭子放入，加料酒、白糖和酱油，再加水1000克左右，旺火烧沸后，移至小火炆熟，捞出葱姜。桂皮，放入笋', NULL, NULL, NULL, '肥鸭一只（重约2000克）、香干2块、水发金钱香菇50克、笋尖50克。 葱结1个、姜5克、葱段2克、湿淀粉15克、酱油75克、料酒50克，白糖30克、味精25克、桂皮3克、芝麻油15克、熟猪油1500克（实耗油75克。');
INSERT INTO `recipe` VALUES (4945, 78, '莲香脱骨鸡', '将嫩母鸡（不剖肚）斩去爪，整鸡出骨后，将鸡身翻转，恢复原状，再洗净。香菇、火腿切丁、莲子50克洗净蒸酥待用，另100克与香菇、火腿丁合在一起，加精盐2克，味精1克，拌匀从鸡颈部刀口处灌入鸡腹内，用线缝合，然后把鸡颈皮打个结放入沸水锅中煮3分钟取出，用水洗净，放在品锅中加入姜块（去皮拍松）、葱结和绍酒5克，上蒸笼用旺火蒸2小时，取出滗出汁汤待用，去掉姜块，葱结，拆去缝合线，鸡腹朝上，放在盘中。将原汁汤倒入炒锅，加精盐1克、味精1克、绍酒5克及蒸酥的莲子，烧沸后用湿淀粉勾薄芡，淋入鸡油，起锅浇在鸡身上即成。', NULL, NULL, NULL, '母鸡一只（约重1250克）、莲子150克。 火腿25克、水发香菇25克、通心菜150克。生姜块5克、湿淀粉15克、熟猪油15克、葱结10克、绍酒10克、精盐3克、味精2克。');
INSERT INTO `recipe` VALUES (4946, 78, '玻璃明虾', '（1）虾去壳除汤，从背部划开成双飞状。\r\n（2）在木圆棒上抹上粟粉，轻轻把虾逐一碾薄。\r\n（3）把虾放入滚水中氽熟，即取出过冷开水，沥干后上碟。\r\n（4）青瓜片、生菜丝伴碟，两款酱汁分别调匀，供蘸食。', NULL, NULL, NULL, '大虾6-8只，粟粉1汤匙，青瓜片数片，生菜丝少许。酱汁料（一）：砂糖、醋、葱粒各1汤匙，芝麻1/2茶匙，胡椒粉、麻油各少许。酱汁料（二）：蛋黄酱、蚝油、醋、番茄酱各1/2汤匙，盐1/8茶匙，胡椒粉少许。');
INSERT INTO `recipe` VALUES (4947, 78, '冬茸白兰', '冬瓜去皮，去酿，切成6厘米长、2厘米宽的条，再批削成白兰花形状，共约75朵。将虾仁剁成泥，分嵌于“白兰花”中心，火腿25克批成薄片逐个下锅，养至酥熟后倒入漏勺沥去油，然后卷包成花朵形，中间放几朵蛋松作花心。热锅下猪油，烧至二成热（约44℃）时，将“白兰花”逐个下锅，养至酥熟后倒入漏勺沥去油，然后复倒入锅中，加清汤，精盐1.5克、味精1克略烧，先将“白兰花”起锅分三层排放于盘中，将卷包好的火腿花放在冬瓜白兰的中间，锅内清汤加湿淀粉，勾成玻璃芡汁，浇在白兰花上，撤火腿末即成。', NULL, NULL, NULL, '净冬瓜1000克。 浆虾仁75克、熟火腿25克、蛋松1克。湿淀粉25克，熟猪油1000克（约耗50克）、精盐1.5克、味精1克、清汤150克。');
INSERT INTO `recipe` VALUES (4948, 78, '炸鸡椒', '(1) 剔净鸡胸脯翅骨上的肉，剔除筋皮，在鸡胸脯肉上均匀在用刀尖扦些眼再砸松，然后用盐稍腌一下。鸡蛋打入碗中控出蛋清25克备用，其余作筷子拨散。生菜洗净、消毒待用。 \r\n(2) 把青虾肉和肥膘肉一起剁成茸，加入鸡蛋清，搅入25克清水，再加葱、姜末、料酒、盐、海米末和香油搅成馅，分成12分。 \r\n(3) 把鸡胸脯肉顺切一刀分成两片，但带骨一头不要切断，在每片上放一分馅，卷成纺锤形成为“鸡椒”。 \r\n(4) 将鸡椒蘸上一层面粉，而后再裹上一层鸡蛋液，粘上一层面包渣待用。 (5) 提起鸡翅骨逐个放入烧至5成热的', NULL, NULL, NULL, '笋胸脯肉(带翅骨)6个。青虾肉200克，水发海米10克，猪肥膘肉50克，鸡蛋150克，炸粉丝50克，细面包渣、生菜叶各100克。调料：植物油900克(实耗约90克)，香油5克，料酒、面粉各10克，葱、姜末各15克盐4克。');
INSERT INTO `recipe` VALUES (4949, 78, '麒麟桂鱼', '将桂鱼剖洗净，沿颌部取下鱼头，劈开，不要劈断，尾部以臂鳍为界斜刀斩下，剞十字花刀，在椎骨内侧片开3厘米。鱼身对剖开，取下脊椎骨。然后将头、椎骨放尾部放入品锅，加陈皮、八角、绍酒、白糖、酱油、姜片、葱段、味精腌渍15至20分钟，待用。将两片鱼的中段，去肚裆、鱼皮及刺，片成0.3厘米厚的片，然后切细丝，用精盐、鸡蛋清味精、湿淀粉，拌匀捏上劲待用。把绿色蔬菜，胡萝卜、姜片、葱段均切成细丝、鸡蛋清（1个）打成蛋泡糊，蒸熟待用。将炒锅置旺火上，下熟菜油，烧至八成热（约200℃）。把腋渍过的鱼头尾，脊椎落锅炸至熟脆，', NULL, NULL, NULL, '桂鱼一条（重约1250克）。 鸡蛋清3个、番茄酱100克、罐樱桃1颗、胡萝卜25克、绿色蔬菜25克。精盐10克、味精4克、绍酒50克、芝麻油25克、酱油25克、白糖25克、姜片25克、熟菜油1000克、葱段15克、陈皮10克、八角10克、熟猪油2000克、湿淀粉40克。');
INSERT INTO `recipe` VALUES (4950, 78, '红烧素什锦', '1、 香茹、金针分别泡软，香菇切小块，金针打结；胡萝卜去皮、洗净，和熟竹笋一起均切滚刀块；黑木耳洗净，切片备用。 \r\n2、 麦面饼、面肠均冲净，沥干水分，以手撕成小块，分别放入热油锅中略炸一下，捞出，沥干油分备用。 \r\n3、 锅炉中倒入2大匙油烧热，放入香茹香，加入麦面饼、面肠、炸豆腐皮、黑木耳、金针、竹笋、胡萝卜及A料煮至汤汁快收干时，淋上B料，即可盛出。', NULL, NULL, NULL, '麦面饼6个 面肠2条 炸豆腐皮100克 香茹3朵 黑木耳30克 金针20克 熟竹笋1支 胡萝卜1/2根调味料 A料：盐、糖各1小匙 酱油3大匙 清水1杯 B料：香油1小匙');
INSERT INTO `recipe` VALUES (4951, 78, '素脆鳝', '1、水发香菇摘去梗（浸泡水滤清备用），挤去水，沿着边缘剪成鳝鱼样的长条。嫩生姜削去皮，切成棉纱线粗的细丝，加米醋、白糖（8克）浸泡备用。 \r\n2、炒锅放旺火上，下熟清油烧至5成热时，把香菇条挤干水分，放干淀粉里滚满干粉，再抖去未粘住的粉粒，投入热油里炸（油量少可分二次炸）不断翻身，炸1分钟左右至硬脆倒出沥干油，锅里放浸香姑水（60克左右）、酱油、白糖炒至稠粘后，将炸脆的香菇条回锅翻拌，均匀的滚满卤汁，撒上胡椒粉、淋香麻油盛出装盘。再在香菇条上撒上糖醋姜丝。操作关键：干淀粉要在下锅炸时才能拌上，菇条既要炸脆', NULL, NULL, NULL, '水发香菇250克，白糖100克左右，干淀粉60克，酱油少许，熟清油300克（耗60克左右），嫩姜15克，胡椒粉2克，香麻油5克，米醋1小碟');
INSERT INTO `recipe` VALUES (4952, 78, '蟾宫折桂', '桂鱼肉100克剁成泥，加水100克、蛋清3个、猪油25克、姜汁水5克、精盐3克、味精1.5克，搅打成鱼茸。熟干贝、火腿、鸡肉各25克斩成米丁，用小调羹12只抹上猪油，将鱼茸分两次装入调羹。中间夹放干贝、火腿、鸡肉丁，再用火腿10克、豌豆、青椒做赡蜍的眼和嘴，去壳留尾的小河虾做腿，制成赡蜍形，上笼蒸熟待用。汆下的鱼肉批成片，切成5厘米长的丝，加精盐3克、绍酒15克、姜汁水5克、味精1.5克、湿淀粉15克搅匀，再加鸡蛋1个、蛋黄3个，桂花拌匀，将绍酒5克、精盐0.5克、味精0.5克、湿淀粉10克拌兑成汁。锅洗', NULL, NULL, NULL, '桂鱼肉350克、熟火腿35克、熟鸡肉25克。 辅料：桂花25克、熟干贝25克、豌豆12粒、鸡蛋4个、青椒1个，小河虾36只。姜汁水10克、桂花酒20克、味精4克、精盐7.5克、湿淀粉30克、芝麻油5克、熟猪油25克、熟菜油750克（实耗油75克）。');
INSERT INTO `recipe` VALUES (4953, 78, '拌鸭掌', '1、鸭掌用碱搓匀洗净，去掉掌垫（鸭掌底部一块褐黄色硬质），然后煮熟，捞出晾凉，逐个退净骨，切成斜条，堆放在盘内。黄瓜洗净消毒后，1、切成小斜片，放在鸭掌上面。 \r\n2、酱油、米醋、香油对成三合油加味精，浇在鸭掌上面即可。', NULL, NULL, NULL, '鸭掌20只。辅料 黄瓜50克。调料 酱油20克，香油10克，米醋2.5克，味精少许。');
INSERT INTO `recipe` VALUES (4954, 78, '莲篷豆腐', '青菜叶切丝，拌入精盐1克，用纱布包拢放入净盆内，挤出菜汁，取用25克。豆腐捣成细泥，放入碗，加菜汁拌匀待用。鸡茸用绍酒和盐2克、味精3克搅匀，加蛋清搅上劲，倒入豆腐，加湿淀粉，再加熟猪油搅匀上劲。取直径8厘米碟子一只和小酒盅10只，抹上冻猪油，把豆腐茸装入碟、盅（约装盅的1／2），各用碗豆7粒嵌在豆腐上作“莲子”，上笼用中火蒸约5分钟（蒸时笼盖略开）。出笼取出即成“莲蓬”。花菜用沸水汆一下，捞出沥干水，放在汤碗里。锅中另放清汤和精盐2克，沸后加味精2克，倒在装有花菜的碗里，放入“莲蓬”，淋上鸡油即成。', NULL, NULL, NULL, '嫩豆腐150克。 鸡茸100克、花菜250克、豌豆25克、青菜叶250克、蛋清3个，湿淀粉5克。盐5克、味精5克、绍酒2克、清汤250克、熟鸡油15克、冻猪油25克、熟猪油25克。');
INSERT INTO `recipe` VALUES (4955, 78, '干炸黄雀', '将豆腐皮润潮去边筋，切成宽5厘米的长条，分成20份。面粉放水120克，加精盐搅拌成面糊，再加入酵母粉拌匀。将笋、香菇、香干均切成细丝，放上花椒盐3克、姜丝、味精拌匀，各分成20份，分别放在切好的豆膝皮上，逐只包成直径2厘米的腐皮卷，香菇、笋丝等可稍外露，卷口处用面粉糊粘住。把炒锅置旺火上烧热。下熟菜油烧至五成热（约125℃）时，把包成的腐皮卷，用筷子夹在3／5处，使两端翘起拆成“V”字形，两头蘸上面糊逐只放入锅内炸至面糊结壳成雀形时，再放松筷于，用漏勺捞出。全部炸好捞起后（炸时油温要始终保持在五成左右），', NULL, NULL, NULL, '豆腐皮10张。 熟笋肉100克、水发香菇60克、香豆腐干4块、上白面粉120克。姜丝5克、精盐15克、花椒盐6克，味精3克、酵母粉2克、甜面酱50克、熟菜油750克（约耗75克）。');
INSERT INTO `recipe` VALUES (4956, 78, '丰化芋艿头', '芋头切成莲花瓣形上笼蒸熟在盘中摆形.海参,鱼肚,冬菇,火腿,鸡脯肉,胡萝卜均切丁,加精盐,料酒下锅烧沸,勾芡倒在盘中即可', NULL, NULL, NULL, '芋头600克,水发海参300克,鱼肚,冬菇各100克,熟火腿,熟鸡脯肉各50克,青豆20克,胡萝卜10克.');
INSERT INTO `recipe` VALUES (4957, 78, '潮汕鱼丸', '1、将刮好鱼青（鱼肉不可用刀切片，以免鱼的细骨和近鱼皮处的红肉渗入）用刀剁幼，用盆盛起，加入蛋白、味精、幼盐，另用一碗清水加入幼盐开匀。 \r\n2、将盆中的鱼肉用手猛力搅打，加入盐水，再打至鱼胶放入冷水中能浮起为准。 \r\n3、将鱼胶挤成珠形排落在已抹过油的竹盘裹（每粒约4钱），上笼蒸5分钟取出。 \r\n4、起锅下上汤，投入冬菇、鱼丸、笋花、大地鱼（骨皮撕净，用油炸香）、紫菜（可用油炸，也可用火焙）、味精、鱼露、麻油、胡椒粉。生菜先放在大汤碗裹，鱼丸调味完毕淋落在大汤碗。', NULL, NULL, NULL, '主 料： 刮好的鱼青（肉）约500克，生菜300克，湿冬菇100克，蛋白100克，笋花100克，大地鱼50克，紫菜50克。 \r\n配 料： 调味料：上汤1250克，幼盐、胡椒粉适量，鱼露、味精、麻油适量。');
INSERT INTO `recipe` VALUES (4958, 78, '茶香鸡汁鱼片汤', '1、桂花鱼肉切片，以盐、油、胡椒粉拌匀。 \r\n2、火腿切丝，蘑菇洗净切片。 \r\n3、锅中注入上汤，煮沸，下桂花鱼片、火腿丝，煮沸约5分钟，倒入大汤盆中，以盐调味。 \r\n4、把龙井茶叶注入玻璃杯中冲开，置盆中央，食用前将茶倒入汤中即成。', NULL, NULL, NULL, '桂花鱼肉5两（约200克），火腿1两（约40克），冬笋片、蘑菇各2两（约80克），上汤8杯，龙井茶叶适量，盐、油、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (4959, 78, '半煎煮鱼', '1、将黄花鱼刮鳞、去腮及内脏后洗净，葱切段，姜切丝待用。2、烧热鼎，放入花生油，再放入黄花鱼，煎至半熟后取出。在鼎中投入肉片、冬菇、笋花、姜丝，略炒，放入黄花鱼，溅入绍酒，放入上汤、酱油、味精、白糖、精盐、姜上鼎盖，用文火加热10分钟后将鱼装上盘，把鼎中汤汁烧浓后放下葱段，然后把汤汁淋到鱼上即成。', NULL, NULL, NULL, '黄花鱼一条鱼约600克，肉片100克，湿冬菇50克，笋花75克，葱5克，姜10克，绍酒10克，味精5克，酱油10克，白糖3.5克，精盐5克，花生油100克。');
INSERT INTO `recipe` VALUES (4960, 78, 'XO酱炒乌冬', '1、猪肉丝用油炒熟，加入其他菜丝炒匀。2、即食乌冬用筷子弄松，蛋打匀，把蛋放入猪肉丝内同炒熟，放入XO酱及生抽再炒匀即成。', NULL, NULL, NULL, '即食乌冬2包，猪肉丝4两（约160克），洋葱丝、西芹丝、红萝卜丝共1/2杯，蛋1只，XO酱4汤匙，生抽1汤匙。');
INSERT INTO `recipe` VALUES (4961, 78, '蒜香鲶鱼', '1、将鲶鱼洗净在背上割4—5刀； \r\n2、备好蒜头，油入锅烧热，先将蒜头、蒜末炒香，再加入姜末、辣豆瓣酱、盐、糖、味精、酱油匀后，放入鲶鱼、高汤同煮； \r\n3、汤滚起后改用小火焖煮10——15分钟。鱼身要翻面，见熟透夹出，在锅中放入葱花、辣油拌炒数下，再用太白粉勾芡，起锅前加点醋，淋浇在鱼身上。', NULL, NULL, NULL, '鲶鱼3条、蒜头1碗、蒜末2小匙、姜末1匙、葱花1匙、辣豆瓣酱2大匙、盐2小匙、糖1匙、味精1小匙、酱油2匙、醋3匙、高汤2碗、太白粉水1大匙、油6匙.');
INSERT INTO `recipe` VALUES (4962, 78, '姜葱螃蟹', '①螃蟹去壳洗净，改刀成块； \r\n②取一碗放清汤、盐、味精、胡椒粉、葱、姜、蒜、淀粉、香油、蚝油，调成汁待用； \r\n③将螃蟹下入旺油锅，稍炸出锅。锅留底油，下葱白段爆香，加入螃蟹烹酒，速盖一下锅盖，倒入调好的汁，翻匀出锅即可。', NULL, NULL, NULL, '螃蟹1000克，盐2.5克，味精2.5克，胡椒粉0.1克，料酒15克，花生油750克，葱2.5克，姜末2.5克，淀粉2.5克，香醋2.5克，蚝油25克。');
INSERT INTO `recipe` VALUES (4963, 78, '法式牛油炸鱼', '（1）薯仔去皮切薄片，放冷水内浸片刻，沥干水分。（2）烧热6汤匙牛油，排入薯片煎透，上盖满火煮20分钟。（3）鱼切大件，洗净抹干，加盐和胡椒粉拌匀，扑上干面粉。（4）烧热沙律油，排入鱼块，煎至两面金黄熟透，最后下2汤匙牛油，以增加香味。（5）柠檬1个切片，半个切角。（6）薯片、柠檬片围边，炸鱼上碟，加柠檬块，撒上番芫荽即成。进食时挤下柠檬汁。', NULL, NULL, NULL, '白身鱼480克，薯仔3-4个，柠檬一个半，番芫荽茸1茶匙，盐、胡椒粉各1/4茶匙，面粉2汤匙，牛油8汤匙，沙律油1汤匙。');
INSERT INTO `recipe` VALUES (4964, 78, '豉椒蒸腊肉', '(l）腊肉用热水洗净，蒸约1小时，取出切厚片，排入扣碗内。 (2）豆鼓洗净，捣烂，红辣椒去籽，切碎。 (3) 烧热油，炒豆鼓红辣椒，全部放在腊肉上，隔水蒸片刻，取出上碟，完美装饰。', NULL, NULL, NULL, '豉椒蒸腊肉 腊肉12两（约480克），原粒豆鼓2汤匙，红辣椒1 ～2只，油1汤匙，芫蔡少许。');
INSERT INTO `recipe` VALUES (4965, 78, '鲜虾蟹柳沙拉', '将虾煮熟，去皮；将蟹柳，黄瓜，紫甘蓝叶片切成丝，用蛋黄酱拌好；将煮熟的虾从中间剥开，放在拌好的沙拉上。', NULL, NULL, NULL, '虾5只，蟹柳2个，黄瓜1个，蛋黄酱，紫甘兰叶片少许');
INSERT INTO `recipe` VALUES (4966, 78, '三鲜苦瓜汤', '1、将苦瓜去瓜蒂、去瓤，切成厚片；冬笋切薄片，香菇去蒂，切薄片。 2、锅中加清水适量，烧开，下苦瓜片汆一下，沥干水分。 3、汤锅洗净置旺火上，放油烧至七成热，放苦瓜微炒，倒入鲜汤，烧开后下冬笋片、香菇片，煮至熟软，加盐调味即可。', NULL, NULL, NULL, '苦瓜500克，水发香菇100克，冬笋100克，鲜汤1000克，盐适量。');
INSERT INTO `recipe` VALUES (4967, 78, '炖橄榄螺头汤', '1)将海螺头洗去黑斑及杂物，洗净。将橄榄用刀拍破待用。(2)将螺头和橄榄装入炖盅内(每人一份)，各注入鸡汤、姜片、烫熟瘦肉和绍酒，加盖，用湿宣纸将盖子密封，然后上笼蒸90分钟左右，配上精盐、胡椒粉调味即成。', NULL, NULL, NULL, '净海螺头400克、橄榄150克、姜5小片、鸡汤2000克、瘦肉150克，精盐、味精、胡椒粉、绍酒各适量。');
INSERT INTO `recipe` VALUES (4968, 78, '歙味笋丝汤', '冬笋片薄片切成细丝，开水煮２次，用冷水漂洗后再用高汤３００克煮１０分钟，虾仁上浆，菜心修好洗净，火腿切成细丝。热锅凉油将虾仁滑熟，倒人漏勺。锅回火放入汤、盐、糖、笋丝煮５分钟，再将虾仁、菜心，火腿丝下锅煮沸，出锅装人深口高脚盘中，虾仁、菜心围人四周，火腿丝放人顶部即可。', NULL, NULL, NULL, '净冬笋６００克，虾仁７５克，熟瘦皮火腿１５克，小菜心６棵，精盐５克，高汤７５０克，蛋清少许，玉米粉少许，冰糖４克。');
INSERT INTO `recipe` VALUES (4969, 78, '浪花天香鱼', '1．鱼剖好洗净，正面剞波浪花刀，反面剞小牡丹花刀，然后用调料腌渍一下。把虾仁洗净，上浆待用。将青豆等辅料分别用水焯熟。 \r\n2．起油锅，烧至三成热时，放入鱼头。保持三成热的油温，将鱼头慢慢浸熟后捞出，鱼身也按同样的方法慢慢浸熟。 \r\n3．另起一口油锅，放入虾仁，划熟后装盘。将浸熟的鱼也放入盘中。锅中留少许油，放入上汤、盐、味精、白糖、黄酒、虾油卤、生抽等调料，勾薄芡，淋入明油。最后把芡汁浇在鱼身上，姜丝、葱丝、青红椒丝等辅料围放两边，青豆放中间。', NULL, NULL, NULL, '基本材料 红东星斑鱼、河虾仁、青豆、柠檬片、姜丝、葱丝、青椒丝、红椒丝。 \r\n调料：食用油、盐、味精、黄酒、淀粉、上汤、虾油卤、生抽、白糖。');
INSERT INTO `recipe` VALUES (4970, 78, '咖喱海鲜锅', '①中虾去壳去肠，洗净吸干水分，切双飞。 \r\n②带子洗净，鱼肉洗净切片。 \r\n③中虾、带子及鱼肉加腌料拌匀，放入烧水中略烫一下后放置一旁备用。 \r\n④羊角豆洗净，番茄去皮切件。 \r\n⑤下2汤匙油爆香干葱茸及咖喱汁，加入清水煮滚后，放入全部材料及调味料，煮至再滚即成', NULL, NULL, NULL, '基本材料 中虾、带子各150克、去骨鱼肉150克、羊角豆115克、番茄2个、干葱（磨茸）4粒、咖喱汁1杯、清水2杯 \r\n调味料：盐、酱青各半茶匙、糖1/4茶匙、 \r\n腌料：盐半茶匙、玉蜀黍粉2茶匙');
INSERT INTO `recipe` VALUES (4971, 78, '树花炖土鸡', '1．先将洗净的本鸡在开水锅中焯一下水，然后放入砂锅，加清水、黄酒、生姜块、葱结、火腿片，用旺火烧开，然后用小火炖2个小时。　 \r\n2．炖熟后加入鸡精、味精、盐和水发灰树花，再炖15分钟。　 \r\n3．最后加入胡椒粉即可。', NULL, NULL, NULL, '本鸡、水发灰树花、火腿片、生姜、葱。调料：食用油、盐、味精、鸡精、黄酒、胡椒粉。');
INSERT INTO `recipe` VALUES (4972, 78, '凉冻金钟鸡', '一、先用白盐２.５克、绍酒２５克，涂抹鸡颓人外，上面放姜、 葱末 \r\n、五分钟后，上笼蒸约十五分钟，取出待冷，然后将鸡拆肉切成小块，加入味粉５克，白盐２.５克，猪油１５克拌匀，将鸡肉腌过待用。 \r\n二、把鱼胶、大菜洗净，用汤盅盛起，加入上汤５００克、哧粉５克、白盐５克，放进蒸笼，蒸至大菜溶化为上，取出待用。 \r\n三、鸡蛋２只蒸熟，将蛋白切片，火腿切片，香菜叶，莨笋切片，熟鸡粒等准备好，然后用１２只茶杯，杯内涂少许猪油，把以上的火腿片、香菜叶、蛋白片、芦笋片，鸡粒等都排在杯内。 \r\n四、在大菜汤约有七成冷时', NULL, NULL, NULL, '光鸡（1000克）、鱼胶（5克）、猪油（15克）、大菜（10克）、鸡蛋（2只）、火腿（25克）、香菜（12叶）、芦笋（50克）、白盐（10克）、味粉（10克）、上汤（500克）、姜、葱、（50克）、绍酒（25克）。');
INSERT INTO `recipe` VALUES (4973, 78, '鲤鱼苦瓜汤', '1、将净鲤鱼肉用餐巾纸搌干水分，切成片；苦瓜洗净，一切两半，去瓤、籽，用开水烫一下，捞出切片待用。 2、汤锅放清水用旺火烧开后，放入鱼片及苦瓜片，加醋、糖、盐调味后，用文火煮5分钟，加味精即可起锅。', NULL, NULL, NULL, '净鲤鱼肉400克，苦瓜250克，醋、糖、盐、味精各适量。');
INSERT INTO `recipe` VALUES (4974, 78, '蒜子瑶柱豆苗', '豆苗摘去硬茎，洗净隔干水，瑶柱用水盖面浸软，捞出撕碎（浸水留用），蒸大约10分钟至软，蒜头去衣，整粒留用。 2、烧热半杯油，将蒜头用中火炸至金黄色，取出，留6汤匙油，用猛火炒豆苗至软身，下调味料兜匀，取出隔净水上碟。 3、用1汤匙油爆香姜片，加芡汁，瑶柱丝，浸瑶柱水及蒜头煮成汁，淋于豆苗上。心得：豆苗要用猛火快手兜炒才能避免出水及变黄。', NULL, NULL, NULL, '豆苗 12两（480克），瑶柱 3粒，蒜头 8粒，姜花 4片量。调味料：盐1/2茶匙，鸡粉，糖各1茶匙。芡汁料：水1/2杯，生抽，蚝油各1汤匙，糖1茶匙，麻油，胡椒粉少许，生粉3/4汤匙。');
INSERT INTO `recipe` VALUES (4975, 78, '八珍鲜汤鱼燕', '1、将发好的鱼燕飞水备用。 \r\n2、将香菇、蟹柳、金华火腿、文蛤、虾仁一并飞水备用。 \r\n3、将老鸡汤烧开，下入鱼燕、香菇、蟹柳、金华火腿、文蛤、虾仁，调味后起锅。 \r\n建议最好是用明炉出品，因为鱼燕是愈煮愈鲜。', NULL, NULL, NULL, '发好鱼燕6两。香菇、蟹柳、金华火腿、文蛤、虾仁盐、味精、白胡椒');
INSERT INTO `recipe` VALUES (4976, 78, '千岛汁鸡球', '1 鸡腿肉切片； 2 加盐、味精、生粉、食粉，水腌20分钟； 3 在4成热油中滑熟成球； 4 锅留底油，放少许蒜茸和千岛汁，然后把鸡球放入，再加芡汤、盐、鸡粉、香油、胡椒粉炒均，勾芡点明油出锅，放在盘中; 5 芥菜心焯水后，放锅中留少许底油，放芥菜心烹料酒加芡汤略炒； 6 起锅码在鸡球边即可。', NULL, NULL, NULL, '鸡腿肉450克、芥菜心11条、千岛汁10克、蒜茸2克、鸡粉5克、胡椒粉1克、盐5克、香油1克。');
INSERT INTO `recipe` VALUES (4977, 78, '炒绵羊丝', '1.绵羊肉丝放入盆内，加入鸡蛋白、一半湿淀拌匀浆好。 2.将湿淀粉放碗中，放入芝麻油、胡椒粉、深色酱油、一半湿淀粉，调成汁待用。 3.炒锅置旺火上，烧热下入植物油，烧至五成热时，放入粉丝炸透，倒在漏勺里，沥去油，盛在盘中。 4.锅内放油0.5汤匙烧至五成热时，将绵羊丝放入油中浸至熟，倒入漏勺中。 5.炒锅内放油少许烧四成热下入姜丝、葱丝、青红辣椒丝、笋丝、香菇丝炒透，加入绵羊丝，烹绍酒，调入芡汁，搅拌匀后，加熟油少许匀装盘，炸粉丝伴边即成。', NULL, NULL, NULL, '绵羊肉丝200克，笋丝250克、湿香菇25克、青红辣椒红25克、鸡蛋白15克、粉丝15克。调料：姜丝1.5茶匙，深色酱油、葱丝各3茶匙，胡椒粉、麻油各1茶匙，绍酒、湿淀粉各0.5汤匙，植物油500克');
INSERT INTO `recipe` VALUES (4978, 78, '金牛鸭子', '将鸭宰杀，褪毛，进行整鸭取骨。鸭翅第二节斩断，作“牛角”造型。鸭腿骨剔拆上半截，下半截保留作“牛腿、造型用。然后将整鸭用精盐、味精擦渍。牛肉、紫菜及巴膘肉切丝，加葱入锅炒熟。晾凉，然后灌入鸭腹中，在颈处打个结。将鸭作“卧牛”造型，头回伸，翅腿摆成牛腿卧煀状，用麻绳捆牢，放入漏勺置沸水中煮一下促使成型。然后放入大盆，加葱结丫姜块、绍酒，加水至浸没，上笼屉旺火蒸酥，倒入漏勺，拣去葱姜，沥干水，抹上酱油。锅中下菜油，至八成热（约200℃）时，将鸭入锅炸成金黄色起锅，沥油。按造型要求装入盘中，抹上芝麻油。将斩下的', NULL, NULL, NULL, '肥鸭一只（约重2250克）。 牛肉300克、菜叶500克、紫菜25克、猪肥膘肉50克。盐5克，酱油10克、味精1.5克、绍酒10克、姜1克、葱25克、葱结1个、芝麻油50克、熟菜油100克。');
INSERT INTO `recipe` VALUES (4979, 78, '鲜蘑蒸鸡', '1、把鸡洗净，剁成块；将鲜蘑洗净，切成厚片待用。 2、用盐、糖和绍酒把鸡块腌上，再加入水淀粉和少许油拌匀，盛入碗中，将鲜蘑放在鸡块上，最后铺上葱段、姜片，入蒸锅旺火蒸30分钟，取出后挑出葱段、姜片即可。', NULL, NULL, NULL, '鸡一只（约两斤），鲜蘑50克，葱段、姜片各适量，绍酒1茶匙，糖半茶匙，水淀粉1汤匙，盐适量。');
INSERT INTO `recipe` VALUES (4980, 78, '豉汁鱼云', '大头鱼洗净后去鳃斩件，用少许胡椒粉、生粉及油拌匀待用。豆豉、蒜肉剁碎，姜、葱、红椒切丝。将剁碎的豆豉及蒜肉加入调味料内拌匀。将鱼云排放碟上，淋上拌匀的豆豉及蒜肉，包上微波保鲜纸用高火煮3分钟取出。将姜丝、葱丝及椒丝洒在上面，淋少许油，放入炉内，再用高火煮约1分30秒钟即可食用。', NULL, NULL, NULL, '大头鱼一个，豆豉两汤匙，蒜肉两粒，姜、葱、红椒各少许。生抽1汤匙，油1汤匙、糖1茶匙，生粉1茶匙，胡椒粉，麻油各少许，水1/3杯。');
INSERT INTO `recipe` VALUES (4981, 78, '肉烧海带', '1、将五花肉洗净，切成2-3公分的块；将海带洗净用开水煮15分钟左右，切成菱形片待用（用海带丝，则挽成结）。 \r\n2、炒锅中放入3汤匙油，加热，放入糖炒成金黄色，投入五花肉块、大料、葱段及姜块煸炒，等五花肉块的表面上好糖色时，加入煮好的海带，放入酱油、绍酒和盐，再翻炒片刻，加清水烧开，转用小火，烧至肉和海带熟烂入味即可。', NULL, NULL, NULL, '带皮五花肉500克，水发海带200克，酱油1茶匙，绍酒1茶匙，糖1茶匙，盐3茶匙，葱段、姜块若干，大料3-4粒。');
INSERT INTO `recipe` VALUES (4982, 99, '药粥', '(1)菠菜粥：菠菜100～150克，粳米50克，煮粥食用。适用于糖尿病阴虚化热型。便溏腹泻者禁服。\r\n(2)芹菜粥：鲜芹菜60～100克，切碎，粳米50克，煮粥食用。适用于糖尿病合并高血压者。\r\n(3)木耳粥：黑木耳30克，粳米50克，大枣3枚，先浸包木耳，将粳米、大枣煮熟后加木耳共煮粥食。适用于糖尿病血管病变者。美国明尼索达大学医学院汉穆希密特教授认为，常食木耳可以减少和预防心脏病的发作。\r\n(4)萝卜粥：新鲜白萝卜适量，粳米50克，煮粥服用，适用于糖尿病痰气互结者。\r\n(5)山药粥：生山药60克，大米', NULL, NULL, NULL, '详见制作方法');
INSERT INTO `recipe` VALUES (4983, 99, '菜肴', '(1)苦瓜：清热解毒，除烦止渴，临床与动物实验证明苦瓜具有明显降低血糖作用。糖尿病人常食有一定降低血糖作用，可用鲜苦瓜作菜食用或红烧苦瓜，每次100克。糖尿病脾胃虚寒者不宜服用。\r\n(2)南瓜：具有降低血糖、血脂作用。国内外临床研究表明，南瓜粉对轻型糖尿病确有疗效。可将南瓜烘干研粉，每次5克，每日3次，也可用鲜南瓜250克煮熟食用，既充饥又可降低血糖。\r\n(3)洋葱：味淡性平，具有降低血糖作用，可用洋葱50～100克水煎服，也可作菜食用。\r\n(4)蚌肉苦瓜汤：苦瓜250克，蚌肉100克，共煮汤，加油盐调味', NULL, NULL, NULL, '详见制作方法');
INSERT INTO `recipe` VALUES (4984, 99, '汤类、饮料', '(1)双耳汤：白木耳、黑木耳各10克，洗净加清水蒸笼蒸至木耳熟烂，食木耳饮汤。适用于糖尿病人眼底出血症。\r\n(2)猪胰汤：猪胰1个，黄芪60克，山药60克，水煎汤，食猪胰，饮汤。适用于各型糖尿病。\r\n(3)糯米桑皮汤：爆糯米花30克，桑白皮30克，水煎服。适用于糖尿病口渴多饮者。\r\n(4)菠菜银耳汤：鲜菠菜根150～200克，银耳20克，共煎汤，饮汤食银耳。适用于糖尿病大便秘结者。\r\n(5)鸽肉山药玉竹汤：白鸽1只，山药30克，玉竹20克，共煮熟食肉饮汤，适用于阴虚型糖尿病。\r\n(6)菊槐绿茶饮：菊花、槐', NULL, NULL, NULL, '详见制作方法');
INSERT INTO `recipe` VALUES (4985, 69, '樱橘蛤士蟆', '海参、鱼肚、鱿鱼、玉兰片、腐竹、熟火腿切片；猪肚、鸡肉、干贝加葱结、姜片、绍酒蒸透入味、切片；熟白鸡肉、熟火腿切丝；鱼肉制茸上味，粘上干贝丝蒸熟；将干贝球、各种原料和调料下入鸡汤烧烩入味，装碗蒸5分钟，滗出原汤勾芡，浇在杂烩上即成', NULL, NULL, NULL, '鸡肉、海参、熟白鸡肉、鱼肉、熟火腿、油发鱼肚、鱿鱼、水发腐竹、干贝、冬菇、玉兰片适量');
INSERT INTO `recipe` VALUES (4986, 69, '凤尾虾排', '将青虾去壳留尾，以精盐，料酒，葱姜汁，花椒稍腌入味后，以牙签每4只串为一排，制成虾排。将虾排入油锅中炸至金黄色即可', NULL, NULL, NULL, '青虾，精盐，料酒，葱姜汁，花椒');
INSERT INTO `recipe` VALUES (4987, 67, '蟹粉排鸡腰', '河蟹治净蒸熟，取蟹肉、蟹黄和蟹油，鸡腰汆熟去皮。\r\n用葱姜呛锅，下蟹肉、蟹黄、蟹油、鸡腰略炒，加鸡汤、调料，炒至入味勾芡淋香油装盘即可。', NULL, NULL, NULL, '河蟹350克鸡腰150克鸡汤750克10克大葱5克姜1克胡椒粉');
INSERT INTO `recipe` VALUES (4988, 67, '上海醉蟹', '清水加盐、糖、花椒烧开，冷却后加花雕酒、大曲酒调匀制成醉卤。活河蟹洗净，沥干水分。取大小适中的干净容器1只，底部先放葱结、生姜和陈皮，放入活河蟹，上面再放上葱、姜、陈皮，压上重物加入醉卤封口，放冷藏室4天后即可食用。食用时改切成块堆装，浇入少许醉卤。', NULL, NULL, NULL, '活河蟹500克　调料：花雕酒，大曲酒，盐，白砂糖，葱结，生姜，花椒，陈皮适量');
INSERT INTO `recipe` VALUES (4989, 67, '菊花黄鱼羹', '1、用葱段、姜块、料酒、精盐腌制鱼10分钟后，上笼蒸6分钟取出；\r\n2、将熟火腿、冬菇切成丝细，鸡蛋黄打散，待用；\r\n3、将炒锅加油置旺火上，投入葱段煸出香味，舀入鸡汤煮沸，加入料酒、笋丝、冬菇丝、再煮沸后，将鱼肉同蛋黄、豆腐原汁落锅，加酱油、精盐、醋搅匀，起锅装盘，撒上菊花、熟火腿丝、姜丝和胡椒粉即可.', NULL, NULL, NULL, '黄鱼中段250克，姜2克， 葱20克，鸡清汤1000克，冬菇、笋丝各50克，金华火腿丝20克，软豆腐160克，鸡蛋2个，生粉15克，油、香菜各5克，大白菊、香油、精盐各3克，胡椒粉1克，酱油6克，料酒3克');
INSERT INTO `recipe` VALUES (4990, 67, '虾子大乌参', '将原只大乌参用水反复洗净，放入漏勺中沥去水份，将炒锅放在旺火上烧热舀入猪油，待油烧到八成热时，将乌参皮朝上放进油锅中炸，约十秒钟后将乌参连油倒出沥去油份，用原锅放入葱油，将大乌参皮朝上放入锅中加进料酒，酱油，白糖，酱色，高汤，把虾子均匀地撒在大乌参上面，用旺火烧开后，即盛入瓷碗内，上笼蒸半小时左右，待大乌参酥软时取出，推入热炒锅中，加盖后用微火煨4分钟后，即将大乌参盛起皮朝上平放于盆中，锅内原汁用水菱粉勾芡，放入葱段，淋入葱油，浇在乌参的面上即成。', NULL, NULL, NULL, '水发大乌参，干虾子');
INSERT INTO `recipe` VALUES (4991, 67, '干煎大虾', '将大虾去须去足，将虾身切为两段，用盐腌制10分钟；煎锅内加油烧热，放入虾段，反复煎炸至熟，放入料酒、糖、酱油拌匀移出即可。', NULL, NULL, NULL, '大对虾450克 精盐7克，料酒20克，酱油8克，糖10克 油200克');
INSERT INTO `recipe` VALUES (4992, 67, '糖醋鲤鱼', '在鱼背上切两条3厘米的口，将精盐料酒撒入其中稍腌。将上述调料对成芡汁。在刀口处撒上湿淀粉后在七成热的油中炸至外皮变硬，移微火浸炸三分钟，再上旺火炸至金黄色捞出摆盘，用手将鱼捏松。将葱姜蒜放入锅中炸出香味后倒入对好的芡汁，起泡时用炸鱼的沸油冲入汁中，略炒后迅速浇到鱼上即可。', NULL, NULL, NULL, '鲤鱼一条，酱油，精盐，味精，料酒，醋，白糖，湿淀粉各适量');
INSERT INTO `recipe` VALUES (4993, 67, '干贝莴笋', '干贝蒸熟撕成丝.莴笋用半锅加精盐的沸水烫软捞出.旺火炒香葱段,倒入高汤,精盐及烫好的莴笋和干贝丝,煮开后用湿淀粉勾芡即可', NULL, NULL, NULL, '干贝50克,莴笋12支,高汤100克');
INSERT INTO `recipe` VALUES (4994, 67, '冬瓜球', '将冬瓜加工成小球镂空,球内填入开洋,用鸡茸封口上笼蒸熟.在油锅中煸香葱姜,加入鲜汤,精盐,湿淀粉勾芡浇在冬瓜球上即可', NULL, NULL, NULL, '冬瓜800克,开洋250克,鸡茸200克,鲜汤100克');
INSERT INTO `recipe` VALUES (4995, 67, '哈蜊汆鲫鱼', '鲫鱼去鳞,内脏洗净,沿脊骨从头至尾两侧各片一刀;熟猪油热至四成,先将鱼在沸水烫一下,随即放入油锅,加料酒,葱结,姜快和清水克,旺火烧5分种,然后拣去葱结,姜块,加入精盐,味精,用漏勺把鱼装如品锅;将哈蜊用开水烫至外壳咯开,瓣开哈蜊壳,去掉筋,用冷水洗净,放在鱼的两边,衬以色绿菜,鲮上熟鸡油你，随带姜末一碟和醋一道上席即可.', NULL, NULL, NULL, '火鲫鱼,哈蜊各500克,绿蔬菜,葱结各25克姜末3克醋5克精盐.\r\n味精各2克,料酒,熟鸡油各10克,熟猪油哥50克.');
INSERT INTO `recipe` VALUES (4996, 67, '黑胡椒牛柳', '牛肉切成长条厚片，用酱油，酒蛋白，苏打粉，淀粉腌泡1小时。将牛肉在温油中泡熟捞出，洋葱用油炒软加入精盐，味精待用。另用油爆香辣椒末及大蒜末，倒入牛肉丝，洋葱，黑胡椒粉，蚝油，辣酱油，糖，番茄酱，水，香油，淀粉快速翻炒即可。', NULL, NULL, NULL, '牛里脊肉500克，洋葱200可克，蛋白1个，苏打粉4克，淀粉15克，黑胡椒粉8克，番茄酱20克，油500克');
INSERT INTO `recipe` VALUES (4997, 67, '凉拌海蜇头', '1.选用隔年腌制的海蜇头，洗去泥沙，放清水里浸泡5-6小时，再冲洗干净泥沙，顺着蜇瓣切成小片，用沙滤水冲数小时备用。\r\n2.葱末放小碗内，精制油入锅烧热，冲入葱末碗内，使葱末发出香味，即成葱油。\r\n3.将海蜇头滤去水，放大碗内，注入80℃的沸水烫一下,立即将沸水滗干,趁热加酱油、白糖、味精拌匀，再淋入香麻油、葱油装盘。\r\n制作关键：\r\n1.陈年海蜇质地嫩脆，当年海蜇皮老质韧。\r\n2.海蜇头用沸水烫制时，水温不宜过高。水温越高，海蜇头收缩越大、排水越多而质地变老韧。', NULL, NULL, NULL, '海蜇头150克，香葱末25克，酱油15克，白糖适量，味精2克，香麻油10克，精制油15克');
INSERT INTO `recipe` VALUES (4998, 67, '蟹粉排鸡腰', '1、河蟹治净蒸熟，取蟹肉、蟹黄和蟹油，鸡腰汆熟去皮。 \r\n2、用葱姜呛锅，下蟹肉、蟹黄、蟹油、鸡腰略炒，加鸡汤、调料，炒至入味勾芡淋香油装盘即可。', NULL, NULL, NULL, '河蟹350克、鸡腰150克、鸡汤750克、10克大葱、5克姜、1克胡椒粉');
INSERT INTO `recipe` VALUES (4999, 67, '宁式鳝鱼', '1、将鳝鱼丝切成5厘米长的段，韭芽切成4厘米长的段；\r\n2、把炒锅放置中火上，倒入熟菜油，烧至八成熟，投入葱白焖香，然后加入酱油翻锅，放入笋丝和白汤稍烧，加入韭芽、味精、葱段，用湿淀粉勾芡，随即淋上香油，将锅内鳝丝颠翻几下，撒上胡椒粉即可。', NULL, NULL, NULL, '熟鳝鱼丝300克，熟笋丝100克，净韭芽50克，葱段2克，葱白段5克，姜汁水各10克，酱油30克，姜丝3克，胡椒粉、味精各1克，湿淀粉、香油、料酒各25克，白汤、熟菜油各75克');
INSERT INTO `recipe` VALUES (5000, 67, '奉化摇坩', '1、将蚶子放在清水中，用竹帚淡刷，中途换水三至五次，淘洗至蚶壳发白为方；\r\n2、将炒锅至旺火上，舀入清水烧沸，放入蚶子略烫，并翻动几下，见蚶壳欲开时迅即捞起；\r\n3、将烫好的蚶子剥去半边壳，摞放在盘中，撒上姜末、葱末、胡椒粉，淋入酱油、沼酒、芝麻油即可。', NULL, NULL, NULL, '蚶子750克，葱末、绍酒各10克，姜末、酱油各15克，胡椒粉0.5克，香油20克。');
INSERT INTO `recipe` VALUES (5001, 67, '如意鸭卷鲜', '1、鸭肉切块皮朝下码在碗里； \r\n2、鸡蛋摊成蛋皮， 抹上虾茸， 卷成“如意”卷， 蒸熟后切片， 码在鸭 块四周；\r\n3、加调料蒸透后扣在大汤盘中； \r\n4、鸡清汤烧开调好味后倒入大汤盘中即成。', NULL, NULL, NULL, '去头熟白鸭200克，鸡蛋3个，鸡清汤750克，虾茸适量。');
INSERT INTO `recipe` VALUES (5002, 67, '朱洪武豆付', '1、猪肉切末，虾仁剁碎加精盐、湿淀粉拌匀，和葱、姜、肉末煸至松散，烹入料酒、肉清汤、味精、精盐炒和后，用湿淀粉调匀勾芡成馅；\r\n2、豆腐切厚片，将馅分成12份，分别放在豆腐片上拌匀，盖一片豆腐制成豆腐生坯，鸡蛋清拌成泡沫状，加干淀粉调匀成糊；\r\n3、旺火热油至五成熟，将豆腐坯沾匀蛋泡糊，逐个炸至变色捞出，油温升至七成热时，重炸至金黄色捞出沥油，肉清汤中加入豆腐、精盐、白糖以小火烧开加醋勾芡起锅即可', NULL, NULL, NULL, '嫩豆腐500克，肥瘦猪肉100克，虾仁、干淀粉各25克，鸡蛋4个，葱末10克，姜末4克，精盐、醋、料酒各8克，白糖3克，味精1克，湿淀粉35克，肉汤150克，熟猪油1000克。');
INSERT INTO `recipe` VALUES (5003, 66, '清炸里脊', '1、把里脊切成滚刀块，用酱油、绍酒、味精卤一下。\r\n\r\n2、勺内放入油，烧至九成热时把里光彩块放入炸一下捞出，待油温升高时再放入油内，炸熟呈火红色捞出装盘即成。（吃时蘸花椒盐）', NULL, NULL, NULL, '猪里脊300克。油、酱油、味精、绍酒、花椒盐。');
INSERT INTO `recipe` VALUES (5004, 66, '酸菜鱼', '1、将鲤鱼去鳞、鱼鳃、剖腹、去内脏洗净，用刀取下两扇鱼肉，把鱼头劈开，钱骨制成块。泡青酸菜洗后切段。\r\n2、将炒锅置火上，放少许油烧热，下入花椒料、姜片、蒜瓣炸出香味后，倒入泡青酸菜煸炒出味，加汤烧沸，下鱼头、鱼骨，用大火熬煮。撇去汤面浮沫，滴入料酒去腥。再加入精盐、胡相通面备用。\r\n3、将鱼肉斜刀片成百姓0.3厘米的连刀鱼片，加入精盐、料酒、味精、鸡蛋清拌匀，使鱼片均匀地裹上一层蛋浆。\r\n4、将锅内汤汁熬出味后，把鱼片抖散入锅。用另一锅入油烧热，把泡癞椒末炒出味后，倒入汤锅内煮1至2分钟。待鱼片断生至熟', NULL, NULL, NULL, '鲤鱼1条（1000克左右），陈年泡青酸菜250克。辅料 鸡蛋清1个，混合油40克，汤1250克，精盐4克，味精3克，胡椒面4克，料酒15克，泡辣椒末25克，花椒 10粒，姜片3克，蒜瓣7克。');
INSERT INTO `recipe` VALUES (5005, 66, '炸田鸡腿', '1、将活田鸡剁下大腿扒去皮，剁去爪再从骨节处剁开，放盆内，加点精盐、绍酒，味精，花椒水抓匀，卤十分钟。\r\n2、勺内放入清油，油烧至七、八成热时，将田鸡蘸上面粉，一个一个地放入油内炸熟捞出控净油装盘即可。', NULL, NULL, NULL, '田鸡腿4只。 辅料 面粉。精盐，味精，清油，绍酒，花椒水，花椒盐。');
INSERT INTO `recipe` VALUES (5006, 66, '辣鱼粉皮', '把干粉皮掰成小方块，用温水洗净、泡软。干辣椒切段。葱切成丝。青鱼切成3厘米长、0.5厘米宽的条，放入热熟大油勺中稍炸，用漏勺捞出控去油。\r\n起勺起熟大油50克，烧热后投入葱丝和干辣椒段稍炒，即烹入料酒和鸡汤，加入味精、甜面酱、盐、酱油，再用糖色调成浅红色汤汁把鱼条倒入汤中。\r\n汤开后，将浮沫撇去，用中火煮10分钟后，加入粉皮再煮10分钟，淋入香油即成。', NULL, NULL, NULL, '带皮青鱼肉200克，干粉皮2张。调料 熟大油500克(实耗80克)，料酒、酱油各10克，甜面酱7克，干辣椒10克，葱段5克，鸡汤750克。香油3克，味精3克，盐2克，糖色少许');
INSERT INTO `recipe` VALUES (5007, 66, '韭菜干丝', '菜洗净，切段，干丝洗净切成段。炒锅入油，放入高汤、干丝和适量的盐、糖、酱油、味精用小火慢慢翻炒5~7分钟，使干丝完全吸收汤的味道，再放入韭菜继续炒半分钟即可。', NULL, NULL, NULL, '韭菜75克、干丝150克、素高汤半碗、色拉油4匙。盐、糖、酱油、味精适量。');
INSERT INTO `recipe` VALUES (5008, 66, '爆煎鲤鱼', '1、鱼洗净，劈为两半，再横切成数块，放在碗中，加盐，胡椒粉拌匀，腌2小时。 \r\n2、花生油下锅烧沸，先将鱼爆煎至熟，将葱花、姜末、酱油、醋调入，翻煎2分钟，盛盘上桌。', NULL, NULL, NULL, '主料：鲜鲤鱼（约75克）1条。\r\n辅料：葱花、姜末少许，胡椒粉少许，酱油2大匙，花生油8大匙，醋1大匙，盐1大匙。');
INSERT INTO `recipe` VALUES (5009, 66, '鲫鱼过河', '1、将鱼初加工后，改在一字刀，用开水一烫，控净水，腹面朝天于盘中整好形，撒上食盐、味精、肥肉片、葱段、姜片、冬菇、花椒、大料、料酒、上屉蒸熟取出，去掉配料。 \r\n2、将原汁加清汤烧开，去掉浮沫，加食盐、料酒、味精浇在鱼盘内，加少许鸡油即成。', NULL, NULL, NULL, '主料：鲫鱼一尾约750克。\r\n辅料：清汤、肥肉片、葱段、姜片、冬菇、花椒、大料、食盐、料酒、味精、鸡油各适量。');
INSERT INTO `recipe` VALUES (5010, 66, '炸芝麻虾', '1、芝麻用水洗净晒半干，放在石臼里捣去外皮。大虾去头须，剥去皮，除掉脊背上的沙线。将虾由脊背片开，腹部相连，再由里面剞成交叉的花刀，用刀拍一下，撒上少许精盐、味精、绍酒，蘸上面粉、鸡蛋湖（鸡蛋打在碗内用筷子调匀），再沾上芝麻。 \r\n2、勺内放入油500克左右，油烧到五成热时，放入已沾好的芝麻虾，炸熟出勺控净油，切成长条码在盘内即成（吃时蘸花椒盐）。注：炸时油不能过热，以免芝麻被炸糊而里面不熟。', NULL, NULL, NULL, '主料：大虾5个，芝麻少许。\r\n辅料：鸡蛋1个，面粉25克，精盐、味精、绍酒、清油、花椒盐。');
INSERT INTO `recipe` VALUES (5011, 66, '烩酸辣干丝', '1、将豆腐干洗净，先批成薄片，再切成细丝，越细越好；将木耳、笋片也切成细丝，与葱、姜丝放在一起。 \r\n2、将鲜汤入锅烧开，加入豆腐干丝、木耳丝 、笋丝、葱姜丝和盐、酱油、胡椒粉均匀后再烧沸滚，撇去浮沫，调水菱粉入锅勾大流水芡后，加上醋，起锅盛在汤碗内，浇上麻油，撒上芫荽即成。', NULL, NULL, NULL, '主料：白豆腐干200克。\r\n辅料：水发木耳25克，笋片（或玉兰片）25克，葱丝10克，姜丝10克，芫荽（香菜）少许，水菱粉20克，精盐适量，胡椒粉少许，酱油25克，醋50克，麻油10克，鲜汤600克。');
INSERT INTO `recipe` VALUES (5012, 66, '羊肉冬瓜汤', '1、把羊肉切成小薄片。冬瓜去皮去籽切成长方薄片，用开水烫一下，香菜切成八分长的段。 \r\n2、勺内放入肉汤，冬瓜，加精盐，花椒水，绍酒，味精，胡椒粉，汤开时把羊肉，葱丝，姜丝，香菜放入勺内，再烧开，加猪清油少许，立即出勺盛在碗内即成。', NULL, NULL, NULL, '主料：羊肉150克，净冬瓜150克。\r\n辅料：精盐、味精、花椒水，绍酒，胡椒粉，葱丝，姜丝，香菜，猪清油，内汤。');
INSERT INTO `recipe` VALUES (5013, 66, '肉末四季豆', '1、猪肉洗净剁碎。\r\n2、四季豆撕去筋，洗净滴干水份。放入油锅中煸炒两分钟左右，盛起，滴去油份。\r\n3、烧热锅下油，将肉末葱粒榨菜炒片刻，加入四季豆，再加清水，用大火至汁收干，加盐加味精，拌上蒜末即可。（为了保持四季豆的色泽，放盐的时间不亦过早。）', NULL, NULL, NULL, '四季豆1斤，猪肉三两，葱粒、榨菜粒、蒜末少许。');
INSERT INTO `recipe` VALUES (5014, 66, '排骨炖白菜', '1、把排骨剁成一寸五分长的段，白菜头切成长方块，香菜梗切成小段，葱，姜切成块，姜块用刀拍一下。 \r\n2、勺内放水，水烧开后放入排骨烫一下取出，再用水冲洗净血沫。 \r\n3、勺内放入少量清油，油烧热时放入葱，姜块炸锅，再放入白菜煸炒至半熟，添肉汤，加排骨，精盐，花椒水。烧开后，移在小火上炖烂，取出葱，姜块，加上味精，香菜梗，出勺盛在碗内即成。', NULL, NULL, NULL, '主料：猪排骨250克，白菜头250克。\r\n辅料：香菜梗10克，精盐、味精、花椒水、葱姜，肉汤，清油。');
INSERT INTO `recipe` VALUES (5015, 66, '蒜瓣烧田鸡腿', '1、田鸡腿洗净去爪剁成两块。蒜瓣剥皮冼净。葱、姜切成末。田鸡用酱油抓匀喂好。 \r\n2、勺内放猪油，把田鸡腿煸炒一下，加入绍酒、白糖、酱油、味精、蒜、葱、姜、花椒水、肉汤，烧开后用小火焖透，移中火上对好口味，用水淀粉勾芡，淋香油出勺即成。', NULL, NULL, NULL, '主料：剥皮的田鸡后腿250克。\r\n辅料：蒜瓣25克。葱、姜、酱油、白糖、味精、绍酒、湿淀粉、猪油、花椒水、肉汤。');
INSERT INTO `recipe` VALUES (5016, 70, '叫花鸡', '１、将光鸡去脚爪肋下取脏，用刀背敲断腿，翅，颈骨入坛，加酱油，绍酒，精盐腌渍一小时，\r\n２、将鸡取出，将丁香，八角碾末，加山奈末遍抹鸡身，\r\n３、炒锅入熟猪油，炸葱，姜起香后捞去，再将虾仁，鸡肫丁，香菇丁，猪肉下火腿丁，虾米入炒锅颠炒几下，加绍酒，酱油，绵白糖炒至断生，待凉后塞入鸡腹，鸡头塞入刀口，腋下放丁香用猪网油包紧鸡身，外用鲜荷叶包裹数层用细麻绳扎紧，把酒谭泥碾成粉加清水拌和起粘平摊湿布上，捆难置泥中间用湿布兜起，使泥紧粘揭去湿布，用包装纸包裹，在戳一小孔，\r\n３、将鸡装入烤箱，旺火烤约四十分钟取出', NULL, NULL, NULL, '母鸡一只，虾仁，鸡肫丁，猪瘦肉，熟火腿丁，水发香菇丁，大虾米，猪网油，鲜荷叶，包装纸，酒坛泥，绍酒，精盐，酱油，葱段，姜末，丁香，八角，山奈末，芝麻油，熟猪油');
INSERT INTO `recipe` VALUES (5017, 70, '烤方', '(1)选用皮约1厘米厚、带有正中7根肋骨的肉l块，用刀将肋骨从中间斩断(不能斩断肋肉)。把肉皮朝下放在砧板上，用刀把四边修齐，制成长约30厘米、宽约20厘米的长方块，再用削尖的竹筷在肉面上戳许多小眼(深至肉皮)，以便在烘烤时让热辐射深部，并使气体排出。\r\n(2)用铁叉双齿从肉块第二根与第六根肋骨之间，顺骨缝叉入。叉到7厘米处，翘起叉尖，使之走出肉面，隔7厘米再叉人，最后叉尖从另一边叉出，再用2根两头削尖的竹筷横叉在肋条肉的两边，别在叉齿上，使肉块平整地固定在铁叉上，这样在烘烤时，不致肉熟烂后下垂。\r\n(3', NULL, NULL, NULL, '猪肋条肉1长方块(约重3000克)。甜面酱100克，葱白段50克，花椒盐100克');
INSERT INTO `recipe` VALUES (5018, 70, '凉冻绿豆肘', '(1)把猪肘刮洗干净。把绿豆淘洗干净。(2)用沙锅盛上清水2公斤放旺火上烧开，加入白矾、绿豆和肘子，用文火慢慢煮，待肘子快熟时捞出。(3)把将熟的肘子(皮向下)放在碗里，把葱、姜、盐等放在上面，再倒入捞出绿豆的原汤，上笼屉用旺火蒸烂，拣去葱、姜，滗去原汤。(4)撇掉原汤上的浮油，过箩后，再倒回盛肘子的碗里，然后把它晾凉。(5)把凉肘子连汤放入电冰箱，待凝冻后取出，切成薄片，摆在盘内即可上桌吃。\r\n特点肉烂赛豆腐，稍带绿色，味道醇香，清凉爽口，含有绿豆清香味。可据个人口味不同，再调入蒜泥、虾油、辣椒油、花椒', NULL, NULL, NULL, '猪肘子1个(1～2公斤)，绿豆0.5～1公斤。');
INSERT INTO `recipe` VALUES (5019, 70, '炸卷果', '(1)把牛肉的筋膜剥除以后，剁成末，加入葱花和姜末。把格炸捏碎后连同湿淀粉(120克)、花椒粉、盐一起加入，搅拌均匀。分次加入清水180克左右，不断搅拌成均匀的肉馅。(2)将油皮摊平，均匀的洒上湿淀粉(最好搅成汁)。把肉馅做成两个圆条(约20厘米长)，分别放在油皮上卷成卷，用旺火蒸熟，取出后即成卷果。(3)把做好的卷果，放在干净布上，卷起来捏实后，去掉布，晾凉后再切成5～6毫米厚的片。(4)把炒勺倒入植物油，放在旺火上烧到刚冒烟时(约7成热)，放入卷果片，炸2～3分钟，见馅已呈红色即可捞出盛入盘里。', NULL, NULL, NULL, '瘦牛肉600克，格炸160克，油皮2张');
INSERT INTO `recipe` VALUES (5020, 70, '酸辣腰卷', '(1)将猪腰子去掉脂皮洗干净，切除腰臊后，顺腰子片成1～2毫米厚的薄片，加入盐、料酒、葱段(5克)、姜片(3克)，浸渍4～5分钟。(2)把香茹去掉蒂后洗干净，玉兰片，剩余的葱、姜切成细丝，干辣椒、泡菜\r\n　　　切成碎末。(3)把干淀粉(12克)撒在鸡蛋清中搅拌成糊浆。把腰片用干净布展干放平，用约一半鸡蛋清糊浆抹在腰片上。(4)把香菇、玉兰片、葱姜丝均匀地放于腰片的下边，一个一个的卷成卷。(5)把炒勺放在旺火上、倒入熟大油，烧至4～5成热，用腰卷蘸上蛋清糊浆，逐个入油炸成黄白色后捞出。(6)把炒勺放旺火上，', NULL, NULL, NULL, '猪腰子4个，水发香茹25克，水发玉兰片25克，泡菜60克，干辣椒6克');
INSERT INTO `recipe` VALUES (5021, 70, '炸卷肝', '(1)把猪肝洗净后，切成15×3×0.3厘米的长薄片。把猪网洗净后撕成厚薄均匀作卷肝用。(2)网油摆平成长方形，铺上猪肝片子一端约离边缘1/3处，然后撒上盐、葱、姜末于肝片上，将它卷成卷，再用马莲草每隔2～3厘米绑一道。(3)把卷放入开水锅内，用文火煮25～30分钟捞出来。(4)把植物油倒入炒勺内，用旺火烧热，放进卷肝炸成金黄色捞出，解开马莲草，随即切成圆片后撒上盐即可。', NULL, NULL, NULL, '猪肝250克，猪网油180克');
INSERT INTO `recipe` VALUES (5022, 70, '它似蜜', '羊里脊肉斜刀切成长3厘米、宽2厘米极薄片，用甜面酱、湿淀粉15克抓匀； 姜汁、糖色、酱油、醋、料酒、白糖、湿淀粉10克调成芡汁； 花生油烧至七成热，放入浆好的羊里脊片迅速拨散， 待里脊片变成白色捞出沥油； 将炒锅置旺火上，放入香油烧热，倒入滑好的里脊片。', NULL, NULL, NULL, '净羊里脊肉150克 白糖40克，甜面酱5克，酱油10克，醋、料酒各3克，湿淀粉25克，姜汁、糖色各1克，香油60克 花生油500克。');
INSERT INTO `recipe` VALUES (5023, 70, '鸡丝汤', '１、将鸡脯肉切成细丝，放入用蛋清、干淀粉调的蛋糊内搅拌一下，然后放到滚水内稍烫一下，取出放在汤碗内；\r\n２、将火腿、冬菇分别切成细丝，连同清汤倒入锅内，加盐、胡椒粉，用旺火烧至汤沸后，冲入装肉丝的汤碗内即可。', NULL, NULL, NULL, '鸡脯肉７５克，火腿１５克，冬菇１５克，鸡蛋一个（只用蛋清）、淀粉２０克，精盐３克，胡椒粉５克、清汤３７５克');
INSERT INTO `recipe` VALUES (5024, 70, '北京烤肉', '1、将肉剔除肉筋等，放在冷库或冰柜内冷冻，然后将肉切成薄片；\r\n2、将烤肉炙子烧热后，用生尾油擦一擦，然后将酱油、料酒、姜汁、白糖、味精、香油一起放在碗中调匀，把切好的肉片放入调料中稍浸一下。随即将切好的葱丝放在烤肉炙子上，再把浸好的肉片放在葱丝上，边烤边用筷子翻动\r\n3、葱丝烤软后，将肉和葱摊开，放上香菜段继续翻动，待肉呈粉白色或牛肉紫色时，\r\n盛入盘中，就着烧饼和溏蒜或嫩黄瓜吃', NULL, NULL, NULL, '羊肉或牛肉500克、大葱150克、香菜50克、料酒10克、酱油75克、姜汁40克、味精5克、白糖25克、香油30克');
INSERT INTO `recipe` VALUES (5025, 70, '鹿茸三珍', '1、将鹿茸、干贝洗净，加调料上锅烹制；\r\n2、将海参、鱼翅用开水氽透，将鸡脯肉切成肉末，拌入鸡蛋清和调料；\r\n3、将鱼翅、海参、干贝、鸡肉丸和鹿茸一次码入汽锅内，加清汤调料蒸至一小时即可。', NULL, NULL, NULL, '鹿茸20克，水发鱼翅、水发海参、干贝、鸡脯肉各250克，盐5克，料酒10克。');
INSERT INTO `recipe` VALUES (5026, 70, '银耳素烩', '1、银耳泡开去黄根，加清汤煮1小时；发菜加清汤煮10分钟；胡萝卜、莴笋去皮，削成鲜蘑状，放入沸水煮沸取出；\r\n2、清汤加精盐、白糖煮沸后放胡萝卜、莴笋、鲜蘑煮2分钟捞出，凉开水浸泡，原汤留下待用；\r\n3、将蒸好的银耳扣在盘中，发菜球、莴笋、胡萝卜、鲜蘑码放在银耳周围。将原汤加入精盐、白糖、鸡油、水淀粉勾芡，将芡汁均匀地浇上即可。', NULL, NULL, NULL, '银耳20克，发菜15克，胡萝卜、莴笋各200克，清汤600克，精盐5克，鸡油10克，白糖20克。');
INSERT INTO `recipe` VALUES (5027, 70, '蜜汁葫芦', '1、猪板油切成条，沾上干淀粉，搓成圆条。\r\n2、面粉用温水和成面团，放开水烫一下后倒出，搅拌成团。\r\n3、再放开水烫一下后倒出，如此反复3次后磕入鸡蛋调成糊。\r\n4、板油条蘸匀鸡蛋面糊入花生油锅中炸，呈葫芦状时捞出。\r\n5、蜂蜜熬至色深，放入“葫芦”，挂匀蜂蜜，装盘，撒上白糖和青，红丝即成 。', NULL, NULL, NULL, '猪板油300 克,蜂蜜约200克,青、红丝各15克,鸡蛋2个。');
INSERT INTO `recipe` VALUES (5028, 70, '生菜扒鸡腿', '1、将鸡腿用刀背拍松，放酱油上锅炸呈浅红色；\r\n2、炒勺内放入底油，加葱、姜煸出味，加料酒、酱油、汤、白糖、味精、精盐，把鸡腿放入，水干时即可出锅，改刀码置盘内生菜之上即可。', NULL, NULL, NULL, '鸡腿500克，生菜250克，葱、姜各5克，料酒15克，味精2克，酱油、精盐、白糖各5克，汤100克，油80克');
INSERT INTO `recipe` VALUES (5029, 70, '一品肉', '1、猪五花肉用沸水煮10分钟，再用凉水冲泡20分钟；\r\n2、五花肉切成2.5厘米见方的块，肉连在肉皮上入沸水中煮10分钟，捞出洗净，冰糖用热水化开后倒入锅中；\r\n3、五花肉用竹算子托住放入锅中，加清盐、料酒、葱段、姜块，用微火煨至七八成熟；\r\n4、用另一炒锅将白糖炒至起泡发红，倒入煨至的五花肉锅中，再用微火煨至酥烂后把五花肉拖入盘中，将原汁收浓，浇在五花肉上即可。', NULL, NULL, NULL, '带皮猪五花肉750克，冰糖250克，葱段15克，姜块25克，白糖10克，精盐3克，味精3克，\r\n料酒20克。');
INSERT INTO `recipe` VALUES (5030, 70, '筒子肉', '1、先将葱、姜(去皮)、花椒切制成细末，猪肉剁成肉泥，一起盛入碗里，拌入盐、料酒、味精、湿淀粉(10克)、香油、白肉汤，拌成馅。 \r\n2、把鸡蛋打在碗里，加入白面、湿淀粉(余量)，搅拌成糊浆。 \r\n3、将猪网油洗干净后，切成20厘米见方的块。在各块的一头，把肉馅摊成1厘米粗的馅条。未摊肉馅的网油上抹上一些鸡蛋糊浆，而后卷成2厘米粗、20厘米长的卷，上屉蒸10分钟左右，取出晾凉，切成10厘米长一根。\r\n4、在旺火上架上炒勺倒入植物油，烧热，将网油卷沾上鸡蛋糊浆，慢慢放入油里炸1～2分钟，至金黄色即可取出，切', NULL, NULL, NULL, '主料：去皮猪肉(肥瘦各半)200克，猪网油200克，鸡蛋2个，白面50克，淀粉50克。\r\n调料：植物油500克(实耗约50克)，香油10克，盐10克，料酒10克，葱10克，姜10克，花椒、味精各适量，白肉汤50克。');
INSERT INTO `recipe` VALUES (5031, 70, '藕丝羹', '1、将藕洗净削皮，切成15厘米长的段，放入沸水中焯1分钟后捞出，把琼脂、蜜枣、银杏切成与藕大小相等的块；\r\n2、将鸡蛋打碎放入碗中，装满水搅拌，上蒸锅蒸5分钟后取出，将藕、琼脂、蜜枣、银杏排在蛋羹上；\r\n3、把200克水放在火上煮沸，加入淀粉和糖调汁，浇在装藕丝的碗中即可', NULL, NULL, NULL, '鲜藕500克，鸡蛋3个，琼脂100克，蜜饯银杏80克，蜜枣60克，玉米粉150克，糖30克。');
INSERT INTO `recipe` VALUES (5032, 70, '北京鸡', '鸡腿去骨取肉切成块状，用精盐、黑胡椒粉、味精、鸡蛋、太白粉腌１０分钟，然后将油烧热，投入鸡块炸至呈金黄色时捞出； 青花菜一朵一朵剖开，洗净后，用精盐水汆烫冲凉铺入盘中； 锅热后倒入油，加糖、醋、辣酱油、精盐、味精、太白粉和水，煮稠后倒入鸡块拌匀盛在花菜盘。', NULL, NULL, NULL, '鸡腿150克，青花菜80克，黑胡椒粉、味精各1克，淀粉、糖各30克，精盐、醋、辣酱油各10克，鸡蛋1个，水100克。');
INSERT INTO `recipe` VALUES (5033, 70, '冰糖肘子', '1、将猪蹄膀刮洗干净，用刀在内侧；软的一面顺长剖开至刀深见大骨，再在大骨的两侧各划一刀，使其摊开，然后切去四面的肥肉成圆形； \r\n2、将蹄膀放入开水锅里，煮十分钟左右至外皮紧缩；\r\n3、炒锅内放一只竹算子，蹄膀皮朝下放在上面，加水淹没，再加入料酒、酱油、精盐、冰糖、葱结、姜片，旺火烧开，加盖后小火再烧半小时，将蹄膀翻身，烧至烂透，再改用旺火烧到汤水如胶汁，将蹄膀取出，皮朝下放入汤碗，拣去葱结、姜片，把卤汁浇在蹄膀上即可。', NULL, NULL, NULL, '去骨猪前蹄膀500克，酱油、料酒各50克，葱、蒜各5克，姜片10克，冰糖100克');
INSERT INTO `recipe` VALUES (5034, 70, '青椒鸭丁', '将鸭脯肉切丁加蛋清、淀粉浆好；青椒切成丁； 将鸭丁在温油中滑透，炒勺内加入底油后放青椒丁、葱、糯米、料酒、味精、精盐、白糖、酱油、鸭丁， 加少许炊芡炒匀，出勺时淋上辣椒油即可。', NULL, NULL, NULL, '鸭脯肉300克，青椒100克 鸡蛋1个，淀粉15克，糯米20克，料酒25克，味精2克，白糖15克，酱油、葱各10克，精盐5克，油50克。');
INSERT INTO `recipe` VALUES (5035, 70, '抓草鱼片', '把鳜鱼肉去净皮和刺，片成长3.3厘米，宽2.6厘米，厚0.5厘米的片，用湿淀粉抓匀浆好，将花生油倒入炒锅中，置于旺火上烧到冒青烟时，将浆好的鱼片逐片放入炒锅内炸，待外皮焦黄，鱼片已熟时捞出把酱油，醋，白糖，味精和湿淀粉，一起调成芡汁，在炒锅内倒入猪油，置于旺火上烧热，加入葱末，姜末稍炒一下，再倒入调好的芡汁，待炒成稠糊状后，放入炸好的鱼片翻炒几下，使汁挂要鱼片上，再淋上猪油即成。', NULL, NULL, NULL, '鳜鱼肉，白糖，湿淀粉，酱油，绍酒，醋，味精，葱末，姜末，熟猪油，花生油。抓匀浆好。');
INSERT INTO `recipe` VALUES (5036, 70, '鸡蛋玉米羹', '1、鲜豌豆放入热碱水中泡一下，捞入凉水中泡凉；\r\n2、炒锅烧热，加油用葱、姜、料酒煸锅；\r\n3、倒入豌豆、蘑菇、冬笋，稍烩后，加水，倒入玉米、鸡蛋、牛奶和盐，开锅后加入淀粉勾芡即可', NULL, NULL, NULL, '罐头玉米160克，鸡蛋2个，罐头蘑菇40克，淀粉5克，牛奶100克，净冬菇、料酒各25克，鲜豌豆粒20克，精盐4克，葱、姜各1克。');
INSERT INTO `recipe` VALUES (5037, 70, '葱扒整鸡', '1、鸡由背部开口，内外洗净，用干搌干水分，均匀的抹一层酱油。\r\n2、起锅放油烧至７成热，将鸡炸至金黄色捞出。\r\n3、将炸好的鸡鸡脯朝下放在大碗中，加入高汤、料酒、白糖、酱油、姜片、盐、味精和葱段，调好颜色和口味，放入锅内蒸熟（可根据鸡的老嫩程度、火力大小把握时间，一般蒸两小时为宜）。将鸡蒸好后拣出葱、姜，沥出汁水，扣入盘中。将沥出的汤汁倒入汤勺内稍滚，去浮沫，用水淀粉勾芡，淋入葱油，浇在蒸好的鸡上即可。（洗鸡时要剁去尾尖、去掉鸡嫂。。炸时要挑破鸡眼，防备溅烫。）', NULL, NULL, NULL, '净膛鸡1只（重约1000克），葱250克，姜25克，盐2克，料酒15克，味精4克，白糖5克，将油50克，高汤、水淀粉个适量，油5克，葱油10克。');
INSERT INTO `recipe` VALUES (5038, 70, '烂糊白菜', '1、将清汤烧开，加入白菜，煮熟后捞出；\r\n2、炒锅至旺火上，下油，加入火腿茸、糖、盐、料酒、香油、胡椒粉、味精、淀粉拌匀，收汁勾芡，淋入菜上即可。', NULL, NULL, NULL, '白菜1000克，金华火腿茸60克，鸡清汤250克，精盐10克，生粉、鸡粉各20克，料酒、香油各10克，糖3克，胡椒粉5克，油50克。');
INSERT INTO `recipe` VALUES (5039, 70, '莲枣肉方', '1、火腿肉方洗净，切成小方块（皮不能切断），加冰糖上笼蒸1小时后取出，滗去原汁，\r\n加冰糖、去芯莲子、枣再蒸1小时至软烂。 \r\n2、水发银耳洗后撕成小朵，煨至入味后围在火腿肉方四周即成。', NULL, NULL, NULL, '火腿肉方150克、莲子、枣、水发银耳');
INSERT INTO `recipe` VALUES (5040, 71, '糖醋鲤鱼', '１、鲤鱼去麟、内脏、两腮，于身两侧每2.5厘米直剞后斜剞成翻刀，提起鱼尾使刀口张开，料酒、精盐撒入刀口稍腌； 　　\r\n２、清汤、酱油、料酒、醋、白糖、精盐、湿淀粉兑成芡汁； 　　\r\n３、在刀口处撒上湿淀粉后放在七成热的油中炸至外皮变硬，以微火浸炸3分钟，再上旺火炸至金黄色，捞出摆盘，用手将鱼捏松； 　　\r\n４、将葱、酱、蒜放入锅中炸出香味后倒入兑好的芡汁，起泡时用炸鱼的沸油冲入汁内，加以略炒迅速浇到鱼上即可。 \r\n风味特点： \r\n　　传统山东名菜。此菜源于山东济南黄河码头洛口镇，用黄河鲤鱼，经炸、熘而成。', NULL, NULL, NULL, '鲤鱼750克，白糖200克，酱油、料酒各10克，葱、姜各2克，醋120克，蒜茸、精盐各3克，试淀粉100克，清汤300克，花生油1500克');
INSERT INTO `recipe` VALUES (5041, 71, '炒豆腐脑', '1.雪里蕻洗净，挤干切末，海米切末； \r\n2.青蒜苗切段，嫩豆腐片去皮制成泥； \r\n3.油烧至6成热，放葱姜末呛锅，再放入豆腐泥，海米，雪里蕻，精盐，料酒，炒3分钟装盘，撒上葱段即成。', NULL, NULL, NULL, '嫩豆腐500克、水发海米50克、咸雪里蕻25克。');
INSERT INTO `recipe` VALUES (5042, 71, '砂锅三味', '猪肘肉、带骨雏鸡肉均剁成。3.3厘米见方的块，放入开水锅中氽过，取出放入砂锅内。鸡蛋煮熟剥去壳，沾匀酱油。放入油锅中炸成金黄色捞出，摆入砂锅的四周，火腿、青菜心均切成小象眼片。砂锅内放入清汤、绍酒、葱段、姜片、精盐，旺火烧开后，移至小火炖至酥烂，取出葱段、姜片、放入火腿片、青菜心略炖。撇去浮油，放入味精，淋上鸡油即成。', NULL, NULL, NULL, '猪肘肉100克、带骨雏鸡肉100克。 鸡蛋60克，火腿25克、青菜心25克。酱油20克、清汤100克、葱段15克、姜片10克、精盐5克、味精1克、菜油100克。');
INSERT INTO `recipe` VALUES (5043, 71, '豆苗虾仁', '1.虾仁抽去肠泥、用盐抓洗，洗挣、拭干水分，腌上少许蛋白、盐、胡椒粉、太白粉。 \r\n2.起油锅，放入2杯油烧至七分热，投入虾仁快速过油，取出沥干。另留油4大匙，高温急炒豆苗，加盐、味精调味后盛出，置于盘内，再将虾仁放在上面即成。 \r\n烹调指南： \r\n1.虾擦得愈干，炒起来愈脆。 \r\n2.腌蛋白主要是为增加脆度，但不可过量，否则吸收不了，会造成相反效果。', NULL, NULL, NULL, '材料：*虾仁12两*豆苗1/2斤*蛋白1/4个,\r\n调味料：*盐、味精、胡椒粉、太白粉各少许');
INSERT INTO `recipe` VALUES (5044, 71, '生汁香蕉卷', '1.咸鸭蛋蒸熟取蛋黄，再将每个蛋黄一分两半； \r\n2.把每根香蕉一分为四，切成约5厘米长的段； \r\n3.生鸡蛋两个取黄搅匀； \r\n4.每两段香蕉夹半个咸蛋黄，用威化纸包紧，用生蛋黄封口； \r\n5.把香蕉卷均匀蘸上蛋黄液，再滚上一层面包粉； \r\n6.锅内放油（以没过香蕉卷为准）烧热，投入香蕉卷，以热油炸7～8分钟； \r\n7.将香蕉卷捞出装盘，另配一小碟沙拉酱。 \r\nTIPS:择香蕉要注意色、香、味三方面，表皮有许多黑色斑点，且色泽深黄的香蕉最为可口；若表皮呈青色，毫无斑点，虽然新鲜但未成熟。', NULL, NULL, NULL, '大香蕉2根、咸鸭蛋5个、生鸡蛋2个；辅料：威化纸10张、面包粉、沙拉酱适量。');
INSERT INTO `recipe` VALUES (5045, 71, '小米蒸排骨', '1、排骨洗净，斩成4厘米长的节。豆瓣剁细，姜切米，葱切花，小米淘洗干净后用水浸泡待用。 2、排骨加豆瓣、甜酱、冰糖、料酒、料酒、精盐、味精、姜米、菜籽油抖匀，装入蒸碗内，然后在排骨上面装上小米，上笼锅用旺火蒸熟，取出扣入圆盘内，撒上葱花。 3、锅置旺火上，放入麻油烧至七成热起锅淋于葱花上面即成。', NULL, NULL, NULL, '猪签排500克，小米150克，红豆瓣15克，菜籽油15克，料酒10克，冰糖8克，甜酱5克，精盐2克，味精10克，大葱10克，老姜5克，麻油10克');
INSERT INTO `recipe` VALUES (5046, 71, '共和凉菜卷', '1、芹菜、胡萝卜、山东白菜梗洗净，切丝；绿豆芽洗净，摘除头尾；香菇泡软，切丝；粉丝泡软，切6厘米小段备用。 2、1/2锅水大火烧开，放入步骤1的材料飞烫40秒，捞起沥干水分后加芥末酱及调味料拌匀。 3、将春卷皮摊开，放上步骤2的材料，边缘涂蛋黄后卷好，切段装盘即可', NULL, NULL, NULL, '芹菜120克,胡萝卜120克,绿豆芽180克,山东白菜（取梗）180克,香菇120克,粉丝1又1/2把,春卷皮12张,芥末酱2大匙,蛋黄1个,盐1/2小匙，糖1/2小匙');
INSERT INTO `recipe` VALUES (5047, 71, '香酥萝卜丸子', '将白萝卜去皮切粒备用。草鱼宰杀后去皮去骨取净肉加姜葱水泡后用搅拌器打成鱼茸备用。将白萝卜粒用少许盐抓匀后用纱布包住挤干水分后加入鱼茸、味精、鸡粉、白胡椒粉、料酒、蛋白、少许生粉，充分搅拌起劲，然后挤成大小均匀的萝卜园子，外面均匀的沾上白面馒头粒，依次做好后备用。锅内烧干净的色拉油至三成油温，放入备用的萝卜园子，慢火炸至全部浮起后捞出装盘（如图）。制作关键：白面馒头一定要用放干后切小粒备用。另外炸萝卜园子的时候油温不能够太高，应该是慢火浸炸至熟，否则馒头粒会炸成黄色，从而影响出品色泽', NULL, NULL, NULL, '白萝卜、草鱼（做成鱼茸）、白面馒头切粒 调料：盐、味精、鸡粉、白胡椒粉、料酒');
INSERT INTO `recipe` VALUES (5048, 71, '拔丝金枣', '1、红枣洗净，煮至8成熟捞出，去皮，捅掉核； 2、将山楂糕切短条插入枣肉，蘸均面粉； 3、红枣下8成热油锅中炸至金黄色，捞出沥油； 4、油锅烧至4成热时，加白糖炒至金黄色，将红枣迅速放入颠翻挂匀，倒在抹油的盘内即成。', NULL, NULL, NULL, '红枣400克、山楂糕75克、白糖150克');
INSERT INTO `recipe` VALUES (5049, 71, '炒豆腐脑', '1、雪里蕻洗净，挤干切末，海米切末 2、青蒜苗切段，嫩豆腐片去皮制成泥 3、油烧至6成热，放葱姜末呛锅，再放入豆腐泥，海米，雪里蕻，精盐，料酒，炒3分钟装盘，撒上葱段即成。', NULL, NULL, NULL, '嫩豆腐500克、水发海米50克、咸雪里蕻25克');
INSERT INTO `recipe` VALUES (5050, 71, '锅塌豆腐', '豆腐切成5厘米长,3厘米宽1厘米厚片,两片中间夹肉茸,上笼蒸过.鸡蛋打匀加料酒,味精,精盐,面粉,湿淀粉搅成糊,将豆腐排成两排放在糊上,再在豆腐上抹一层蛋糊.油烧至五成热把豆腐推入锅中翻煎至浅黄色后放入葱,姜丝,倒入清汤,酱油,料酒,味精调成的汁,加盖收干即可', NULL, NULL, NULL, '豆腐750克,肉茸150克,鸡蛋1个,清汤100克');
INSERT INTO `recipe` VALUES (5051, 71, '蒜苗火鸡排', '1、将蒜苗清洗后，切成5厘米的长段。去皮的洋葱，切成圈。把清洗过的番茄，去除蒂梗后，切块。在整块火鸡肉上抹油，放在不沾锅中用大火煎2分钟，然后换中火继续煎8分钟。加盐和胡椒后，包上铝薄纸保温。 \r\n2、将洋葱圈放入热锅中，炒2分钟。加蒜苗和番茄块，略炒一下后，将水、即溶高汤粉倒入锅内，均匀搅拌，小火煮5分钟。接着放入法式鲜乳酪和芥末，再煮3－5分钟，直到汤汁呈黏稠状。加入盐和胡椒调味。 \r\n3、把肉块切成厚片，肉汁倒入蒜苗中。肉片铺在盘中，然后蒜苗摆在肉片旁。配上法国面包，即可食用。', NULL, NULL, NULL, '蒜苗1小根·洋葱1个·番茄1个·火鸡肉150克（大腿部分的厚肉块）·油数滴·盐·粗磨的胡椒·即溶高汤粉1/4茶匙·水140毫升·法式鲜乳酪1汤匙·芥末1茶匙·法国面包1块（或是马铃薯、米或面一份）');
INSERT INTO `recipe` VALUES (5052, 71, '酒香椒盐肘子', '1)在开水锅中将猪肘子煮烫紧透后，捞出，用凉水泡上，把污毛刮洗干净； \r\n2)把糖在皮上涂抹一层放在盆内； \r\n3)加进料酒、酱油、花椒、大料、盐，再上屉蒸约2小时，把肘子蒸烂取出，控去水分； \r\n4)炒勺将植物油烧至8成热而后下入肘子，炸至皮酥脆时捞出沥油，切成一字条摆放在盘中，花椒盐装碟随肘子上桌。', NULL, NULL, NULL, '辅料：植物油2公斤(实耗约50克)，料酒、酱油各15克，花椒、大料各5克，盐7克，花椒盐10克，糖少许，鸡汤2.5公斤');
INSERT INTO `recipe` VALUES (5053, 71, '八宝梨罐', '在梨削去外皮；在梨头切下1／4（按梨的高度）作盖，去梨把，用小刀挖去梨核，梨肉壁厚1厘米成为罐形，用开水稍烫。桔饼、桂元肉、冬瓜条、红枣去核、山楂糕、青梅均匀切成小方丁，用沸水焯过，与蒸过的糯米饭、白糖、桂花酱、瓜子仁、熟猪油搅拌成馅。将馅装入罐内，盖上盖，青梅切条作梨把，装入盘内，上笼旺火蒸20分钟取出，撒上青红丝，炒锅放入清水、白糖、桂花酱，旺火烧沸成汁，浇在梨上即成。', NULL, NULL, NULL, '茌梨150克。 桔饼20克、桂元肉15克、冬瓜条20克、红枣15克、山植糕15克、青梅15克，糯米饭15克、瓜子仁15克、青红丝15克、白糖20克、桂花酱20克、熟猪油20克。');
INSERT INTO `recipe` VALUES (5054, 71, '扒龙须鲍鱼', '(1) 将鲍鱼沙去掉剞上一字花刀，顶刀切成3厘米厚的片，放在盘中。将龙须菜剥去老皮。 (2) 炒勺上旺火，放入200克鸡汤，同时加入料酒7克、盐2克白糖和味精，并把龙须菜也放入汤内，把汤烧开后用湿淀粉勾芡，淋上鸡油，盛入盘内码成两排。 (3) 另用炒勺放入鸡汤200克，加入料酒8克，盐1克，把鲍鱼也放入汤内用水火烧开后改旺火，用适量湿淀粉勾芡，淋上鸡油，拖入盘中龙须菜上即成', NULL, NULL, NULL, '罐头鲍鱼250克，罐头龙须菜300克。调料　鸡油25克，料酒15克，味精7克，盐3克，湿淀粉50克，白糖少许，鸡汤400克');
INSERT INTO `recipe` VALUES (5055, 71, '酿茄斗', '茄子去皮、蒂，切成3厘米见方、2厘米厚的块，中间挖去2厘米见方，成“斗”形，洗净。猪肉切成绿豆大小的丁，水发海米切碎。鸡里脊肉剁成泥状，加鸡蛋清。湿淀粉、精盐搅匀成鸡料子，樱桃每个切两半，水发木耳、玉兰片均切成末。炒锅放油烧至七成热（约175℃），加入葱、姜末爆炒。加肉丁、木耳、海米，玉兰片炒熟，加芝麻油、味精拌匀成馅料，盛入碗内。装上前斗，上面抹上鸡料，再沾上一片樱桃，入笼蒸透装入盘内。炒锅内加清汤、味精、绍酒、精盐，中火烧沸，用湿淀粉勾稀芡，淋在茄汁上即成', NULL, NULL, NULL, '茄子250克。 猪肉20克、水发海米20克、鸡里脊肉15克、鸡蛋清15克、樱桃15克、水发木耳15克、玉兰片15克。花生油50克、葱15克、姜10克、味精1克、芝麻油10克、清汤100克、绍酒15克、精盐5克、淀粉50克');
INSERT INTO `recipe` VALUES (5056, 71, '黄焖全鸡', '鸡宰杀好后，剁掉嘴尖、爪尖，再整鸡脱骨、洗净，酱油、黄酱调匀抹在鸡身上，放入七成热（约175℃）的油锅中，皮朝下炸至金黄色捞出沥油。炒锅上火放少许油，加葱段、姜片、八角，加入黄酱、酱油、清汤、精盐，放入鸡浇开，移至小火煨炖10分钟将鸡翻身，至炖熟时出锅沥去汤。将鸡皮朝下放入大碗内，倒入炖鸡的原汤，入笼旺火蒸20分钟，熟透出笼滗出原汤。再把鸡扣入大碗内，将原汤上火用湿淀粉勾薄芡，加绍酒、花椒油、浇在鸡上即成。', NULL, NULL, NULL, '鸡1000克。 葱10克、姜10克。八角5克、黄酱10克、酱油10克、精盐10克、湿淀粉25克。绍酒20克、花椒油200克、花生油1500克（实耗油150克）');
INSERT INTO `recipe` VALUES (5057, 71, '三彩大虾', '将大虾去掉外壳留虾尾，由脊背抽去泥肠，用刀从背部批开，腹部相连，剖上十字花刀，加盐、味精、绍酒腌渍入味。面包切成上宽5厘米、下宽3.5厘米、长10厘米、厚0.6厘米的片共10片。比目鱼肉和猪肥肉膘分别剁成细泥、同放碗内加葱、姜末、鸡蛋清、绍酒、精盐、湿淀粉调匀成馅。冬菇、火腿、香菜梗分别切成末。在面包片上抹一层蛋黄，虾肉片沾一层薄面粉，再挂上蛋清（虾尾不挂糊）。虾肉皮朝下贴在面包托上，再将鱼肉馅在虾片上抹平，随后在鱼馅上放一行香菜末，中间放火腿末，第三行放冬菇末，略按一下，即制成“三彩大虾”，炒锅内加花生', NULL, NULL, NULL, '鲜对虾250克。 比目鱼肉25克、猪肥肉膘20克、咸面包20克、火腿15克、香菜15克、水发冬菇20克。精盐5克、味精1克、绍酒15克、葱10克、姜15克。淀粉50克、花生油100克、花椒3克、辣椒油20克。');
INSERT INTO `recipe` VALUES (5058, 71, '油泼鸡', '雏鸡宰杀好去嘴尖、爪尖洗净，用刀从背脊自脖颈至后尾劈为两半，剔去筋骨，剁断鸡颈，但颈皮不要割断。用刀背砸断鸡翅，抽出大、小腿骨，脊骨剁成两段，将鸡放入碗内。加绍酒、酱油、姜汁、均匀沾在鸡肉上，腌渍20分钟，取出搌开水分，放入八成热（约200℃）的油锅中炸至八成熟，呈淡红色时捞出。待油温升至九成热（约225℃）时，再将鸡放入炸至呈枣红色取出，剁成条状，按原鸡形摆入盘内。炒锅内放清汤和腌鸡的汆汁，烧开后放入香菜、味精、淋上芝麻油泼在鸡身上即成。食用时用辣酱油和花椒面小碟上桌蘸食，其味更美。', NULL, NULL, NULL, '鸡1000克。 香菜5克。酱油10克、绍酒20克、姜10克、味精1克、芝麻油100克、花生油1000克（实耗油100克）。');
INSERT INTO `recipe` VALUES (5059, 71, '龙凤双腿', '鸡脯肉去筋膜，洗净，片成薄片成长2厘米、宽1厘米、厚0.1厘米，净大虾肉顶刀片成厚0.3厘米的片，放碗内磕入鸡蛋、湿淀粉搅匀成馅。再取一空碗，磕入鸡蛋，加剩汆湿淀粉调成蛋糊。猪网油洗净，切成长14厘米、宽10厘米的长方形片10张。取一张铺平，取1／10馅放在油片角上，将网油片四周抹上蛋糊，取熟鸡腿骨一根，放在网油片的一边，使馅包住鸡腿骨，然后用网油把馅和鸡腿骨包好（鸡腿骨露出1／2），像鸡腿形状，摆在盘子里，入笼旺火蒸透取出，稍晾，沾匀鸡蛋糊，逐个放入六成热（约150℃）油锅内（手提鸡腿骨），炸至金黄色捞', NULL, NULL, NULL, '鸡脯肉300克、净大虾肉300克。 鸡蛋一个（约50克）。湿淀粉15克、猪网油25克、辣椒油100克，花生油1500克（实耗油100克）。');
INSERT INTO `recipe` VALUES (5060, 71, '鱼包三经', '将偏口鱼肉洗净，劈成长5厘米、宽3厘米、厚0.3厘米的片，共12片。每片平铺案板上撒上精盐、绍酒腌渍。冬菇、冬笋、火腿、熟鸡肉切成长3.5厘米的细丝放入碗内，加精盐、绍酒、芝麻油拌匀成馅料，待用。鸡蛋打入碗内加面粉、发酵粉、清水，调成鸡蛋糊。把馅料分别均匀地包在鱼片肉内，卷成12个长3厘米、直径1厘米的鱼卷。炒锅内加花生油，中火烧至六成热（约150℃）时，将鱼卷沾匀鸡蛋糊，入油炸至熟透成金黄色时捞出，摆在盘中即成。外带花椒盐碟一并上席。', NULL, NULL, NULL, '偏口鱼750克。 火腿10克、水发冬菇20克、净冬笋20克、熟鸡肉25克。精盐5克、绍酒15克、芝麻油10克、面粉10克、发酵粉5克、花生油250克、鸡蛋黄25克。');
INSERT INTO `recipe` VALUES (5061, 71, '辣味过江鸡', '1.鸡开膛洗净，在汤锅内贴煮后捞出，用温水再洗一遍，然后用料酒、盐、味精、胡椒面，将鸡全身抹匀，装盆，把姜、葱节、花椒放在鸡上，腌1小时。 2.鸡上笼蒸至熟软，取出晾凉，从鸡背上划一刀，去尽鸡么，在鸡膛内的一面划成长方块（注意保持鸡皮完整），胸皮朝下装碗内，将其余鸡肉切长块摆在上面，加汤上笼蒸透，取出翻扣圆盘中，将蒸鸡原汁浇在鸡上。 3.番茄各切4瓣，去皮、去籽浆，烫热，摆在盘边周围。 4.将红油辣椒、油酥豆瓣、酱油、白糖、醋、蒜泥、葱花、芝麻酱、熟芝麻、精盐加汤，对成汁，装4个小碗，同鸡一起上桌即成', NULL, NULL, NULL, '鲜肉鸡1只(重约2000克)，蕃茄2个，红油辣椒20克，油酥郫县豆瓣20克，酱油25克，精盐5克，味精2克，胡椒面1克，料酒25克，白糖15克，醋15克，蒜泥15克，葱花15克，芝麻酱25克，熟芝麻15克，姜片25克，葱节25克，花椒20粒，清汤适量。');
INSERT INTO `recipe` VALUES (5062, 71, '绍子烘蛋', '1、蛋打散，加入盐、玉米粉后打松；再与绞肉、木耳末、榨菜末各2大匙混合均匀。 2、把油放入锅中加热，把蛋汁到入锅中，先用大火烘，再用小火烘3分钟，此时蛋皮两边会渐渐膨高，如果中间没有膨起，可以另放一勺油淋在中间，盖上锅盖，使蛋全部膨高后，小心用锅铲翻面，待两面呈金黄色，即可放在盘中，再切成2公分块状。 3、锅中再留一点余油，将绞肉放入锅中炒散，再把木耳末、榨菜末、葱花一起炒香，加入高汤煮滚，加盐、味精调味后，盛起淋在烘蛋上，即可食用。', NULL, NULL, NULL, '蛋5个、盐半匙、玉米粉半匙、绞肉3大匙、木耳末3大匙、榨菜末3大匙、葱花少许、高汤1饭碗、味精半匙、油600公克。');
INSERT INTO `recipe` VALUES (5063, 71, '奶汤八宝布袋鸡', '将整鸡冲洗净，从鸡嗉处入口，将鸡骨完整地剔出。将海参、干贝、猪瘦肉、火腿、鱼肚，莲子、口蘑、玉兰片、均切成1厘米见方的丁，用开水焯一下，放入葱姜、酒等调料调拌成“八宝馅”，装入鸡腹内，将鸡头从脖子处剔过，把刀口别住，放入沸水内稍烫，放进汤盛内，加入清汤、葱段、姜片、绍酒、精盐，入笼蒸熟取出，滗出汤汁，去葱姜，再将火腿、香菇、菜心、玉兰片切成片，烫后整齐摆在鸡身上。锅内放猪油，烧至六成热，放葱段、姜片稍炸后，加入奶汤、绍酒、姜汁、精盐，烧沸后捞出葱、姜，将汤倒入汤盛内，麻鸡油即成。', NULL, NULL, NULL, '鸡1000克。 海参10克、干贝10克、猪瘦肉10克、火腿10克、鱼肚10克、莲子5克、口蘑5克、玉兰片10克。绍酒20克、精盐5克、葱10克、姜10克、香菇5克、菜心5克、奶汤100克。');
INSERT INTO `recipe` VALUES (5064, 71, '胡椒海参汤', '把发好的海参放于清水中，逐个细心抠去腹内黑膜，洗净泥沙、片成大片，在开水中氽透控出水分。香菜择好洗净切成3厘米长的段。 (2) 炒勺上旺火，将熟大油烧热放入葱丝稍炒，烹入料酒加入鸡汤、味精、毛姜水、酱油盐和胡椒粉，将海参片也放入汤内，汤开后将浮抹撇去调好味，淋入香油盛入大汤碗中，撒上葱丝和香菜段即可。', NULL, NULL, NULL, '水发海参750克，胡椒粉3克。调料　熟大油、葱各25克，料酒15克，盐4克，味精5克，生姜水10克，香油、酱油少许，鸡汤750克。');
INSERT INTO `recipe` VALUES (5065, 71, '鱼包三经', '将偏口鱼肉洗净，劈成长5厘米、宽3厘米、厚0.3厘米的片，共12片。每片平铺案板上撒上精盐、绍酒腌渍。冬菇、冬笋、火腿、熟鸡肉切成长3.5厘米的细丝放入碗内，加精盐、绍酒、芝麻油拌匀成馅料，待用。鸡蛋打入碗内加面粉、发酵粉、清水，调成鸡蛋糊。把馅料分别均匀地包在鱼片肉内，卷成12个长3厘米、直径1厘米的鱼卷。炒锅内加花生油，中火烧至六成热（约150℃）时，将鱼卷沾匀鸡蛋糊，入油炸至熟透成金黄色时捞出，摆在盘中即成。外带花椒盐碟一并上席。', NULL, NULL, NULL, '偏口鱼750克。 火腿10克、水发冬菇20克、净冬笋20克、熟鸡肉25克。精盐5克、绍酒15克、芝麻油10克、面粉10克、发酵粉5克、花生油250克、鸡蛋黄25克。');
INSERT INTO `recipe` VALUES (5066, 71, '五花肉烧冻豆腐', '1、五花肉切块；辣椒切段；笋切小块；葱切寸段备用。 2、锅烧热先入辣椒、蒜、姜爆香、续入五花肉炒至焦黄。 3、最后加入调味料（1）与所有材料再稍加拌炒后，加入高汤，以小火煮40分钟至汤汁收干即可。', NULL, NULL, NULL, '蛋冻豆腐1包，五花肉300克，笋120克，葱2根，姜1小块，辣椒1根，蒜头6粒。调味料： 1、 酱油3大匙，米酒1大匙，糖1大匙，胡椒粉少许。 2、 高汤1杯。');
INSERT INTO `recipe` VALUES (5067, 71, '萝卜鱼', '将比目鱼肉洗净，劈成长5厘米、厚0.3厘米，一端宽3厘米）一端宽1.5厘米的片，共12片。平摆在盘内，撒上精盐、胡椒面、绍酒腌渍入味。再将剩下的鱼肉同猪肥肉膘一起剁成细泥放入碗内，加鸡蛋清、绍酒、葱末、姜末、胡椒面、清水、精盐搅匀成馅，均匀地卷入鱼片内，制成一头粗，一头细如萝卜状的鱼卷，鸡蛋打入碗内搅匀，将鱼卷周身沾匀干面粉，挂匀鸡蛋液，沾匀细包渣，每只鱼卷的粗端插入一根竹签。炒锅内放花生油用中火烧至七成热（约175℃）时，将鱼卷投入炸至金黄色取出。抽出竹签，在竹签的小孔内放入一叶消毒过的香菜叶，整齐摆入', NULL, NULL, NULL, '比目鱼450克。 猪肥肉膘25克、面包渣30克。精盐3克、胡椒面5克、绍酒15克、葱6克、姜末10克、花生油200克、香菜叶10克、鸡蛋清15克。');
INSERT INTO `recipe` VALUES (5068, 71, '蜜汁肥桃', '肥桃去皮切成滚刀块，沾匀干面粉，放入六成热（约150℃）油锅中炸透，呈金黄色捞出。炒锅加油、白糖，中火炒至呈红色时，加清水、白糖、蜂蜜炆2分钟，然后将桃倒入再放入桂花酱炆至汁浓时、盛入盘内即成。', NULL, NULL, NULL, '肥桃200克。 蜂蜜20克。白糖20克、桂花酱20克、花生油50克。');
INSERT INTO `recipe` VALUES (5069, 71, '紫桂焖大排', '(1) 将外脊肉除去皮和肥膘，顶刀把肉切成130克的大片，筋用刀扦三刀并将肉拍松，随即肉片放入烧至成熟的植物油炒勺中，炸至金黄色时捞出，沥油。葱切段。姜切片。紫肉桂砸成碎快。(2) 炒勺上旺火，将熟大油烧热后，把葱、姜煽出香味，放入紫肉桂稍加煽炒，烹入料酒，加入鸡汤、白糖、盐，再用糖色把汤调成浅红色，烧开后，将浮沫撇去，再放入炸好的外脊肉片，再烧开后改用微火40分钟，将肉再翻过来直至烂，拣去紫肉桂和葱姜，将肉夹入盘中。用旺火将勺内汤汁收浓，分别淋入湿淀粉和52克熟大油便成。', NULL, NULL, NULL, '主料　猪带骨外脊肉1.5公斤，紫肉桂5克。 \r\n调料　植物油900克(实耗约70克)，熟大油50克，盐8克，白糖5克，料酒15克， \r\n　　　糖色烽冼，姜25克，葱50克，鸡汤1.2公斤。');
INSERT INTO `recipe` VALUES (5070, 71, '双烤肉', '(1) 把肉用铁制的杵子叉起来，将肉皮在旺火上燎煳后，放进80℃热水中浸烫刮去煳皮。之后用开水煮至8成熟捞出，在肉上抹以蛋清糊(用蛋清和适量淀粉搅拌而成)，放入200℃烤箱内烤至红色，再翻烤30分钟取出，将煳皮刮去。(2) 将烤好的瘦肉、肥肉、肉皮分别切成长6厘米宽12厘米的块，排骨剁成6厘米的段，分别摆在盘中。(3) 葱白、花椒盐、萝卜条、甜面酱分装在小碟内，与烤肉一起上桌即成。用春饼、荷叶饼卷食', NULL, NULL, NULL, '主料 带骨硬肋猪肉2.5公斤，鸡蛋清50克，葱白50克，萝卜条50克。 \r\n调料 湿淀粉200克，花椒盐10克，甜面酱25克。');
INSERT INTO `recipe` VALUES (5071, 71, '煎烹肉丝', '⑴将肉切成长5厘米、宽0．3厘米、厚0．3厘米的丝，加入盐0．5克、鸡蛋1个、料酒2克、淀粉2克上浆，抓匀。 \r\n⑵葱、姜切成丝。香菜洗净去根，切成长3厘米的段。 \r\n⑶炒锅上火，加油50克，将肉丝放入，炒散，并继续煎炒至肉丝呈金黄色，加入料酒、盐、味精、醋、白糖、酱油略炒，放入葱丝、姜丝、香菜一同翻炒，淋入香油，即可装盘。', NULL, NULL, NULL, '主 料：猪瘦肉200克。 \r\n配 料：香菜100克。 \r\n调 料：料酒5克、盐5克、味精4克、醋4克、白糖2克、酱油3克、淀粉2克、葱、姜各 \r\n5克、油50克、鸡蛋1个、香油5克。');
INSERT INTO `recipe` VALUES (5072, 71, '怪味鸡丝', '1、鸡胸肉煮熟晾凉,撕成细丝,放在盘中； \r\n2、芝麻酱加水,酱油稀释拌匀,放盐、白糖、醋、鸡精、胡椒粉、辣椒末、花椒面、香油、拌匀成怪味酱； \r\n3、将怪味酱浇在鸡丝上，再撒上白芝麻和红油即可。', NULL, NULL, NULL, '鸡胸肉300克,芝麻酱100克,白糖10克,盐15克,鸡精5克,胡椒粉3克,醋20克,香油5克,白芝麻2克,酱油5克,花椒面2克,辣椒末2克,红油5克,水少许。');
INSERT INTO `recipe` VALUES (5073, 71, '八宝西红柿', '（1）把西红柿洗净，由蒂根处开一个方口，连蒂根拿下来，把瓤挖出三分之一；将猪肉剁成肉泥，加鸡汤、料酒、味精、精盐、花椒水调匀。 \r\n（2）将海参、香菇、冬笋、熟鸡肉、干贝、虾都切成小丁，放入肉馅，加少许葱、姜末、豌豆搅匀、瓤在西红柿内，再将西红柿蒂根盖严，摆在碗内，上笼蒸熟取出。 \r\n（3）锅内放鸡汤、料酒、精盐、味精、花椒水烧沸，用湿淀粉勾芡，浇在西红柿由即成。', NULL, NULL, NULL, '西红柿500克，猪肉25克，水发海参20克，大虾25克，干贝、冬笋、香菇、嫩豌豆各15克，熟鸡肉20克，料酒、味精、葱花、姜末、花椒水、鸡汤、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (5074, 71, '酱炒鸡翼球', '1、鸡翅中节去净骨，青、红椒切片，姜切粒，去骨的鸡翅用少许盐、味精腌约30分钟待用。 2、烧锅下油，放入生姜、青、红椒、鸡翅中节、豆瓣酱煸炒至八成熟。 3、调入剩下的盐、味精、白糖炒至入味，用湿生粉勾芡，淋入麻油出锅入碟即成。', NULL, NULL, NULL, '鸡翅中节200克、青、红椒各1只，姜15克,花生油20克、盐8克、味精8克、白糖3克、豆瓣酱20克、胡椒粉3克、湿生粉适量。');
INSERT INTO `recipe` VALUES (5075, 71, '烧五丝', '鸡丝、猪肉丝用鸡蛋清、湿淀粉、精盐抓拌上浆。海参丝、蛋糕丝、鱼肚丝用开水一氽，捞出沥于水分。将猪腰丝放漏勺内用开水一氽，捞出沥干，放入八成热（约200℃）油锅里一冲沥净油。炒锅内放入花生油，烧至五成热（约125℃），将鸡丝、猪肉丝下锅滑炒捞出。炒锅底留汆油，用葱姜丝爆炒，烹绍酒，加海参丝、鱼肚丝、玉兰片丝、鸡丝、腰丝。蛋糕丝、酱油、精盐、清汤，烧开后撇净浮沫，用湿淀粉勾芡，淋芝麻油，盛入汤碗内，撤上火腿丝，装汤碗内即成。', NULL, NULL, NULL, '鸡丝50克、猪肉丝50克、海参丝50克、蛋糕丝50克、血肚丝45克。 鸡蛋清30克，玉兰片丝25克、火腿丝25克、猪腰丝35克。淀粉50克、精盐5克、花生油50克、葱丝15克、姜丝10克、绍酒15克、酱油10克、清汤100克、芝麻油10克。');
INSERT INTO `recipe` VALUES (5076, 71, '烤加吉鱼', '将加吉鱼刮去鳞，掏净鳃和内脏，用清水洗净，搌净水分，周身蘸上酱油。炒锅置火上，加入花生油，烧至八成热（约200℃）后，放入加吉鱼一炸即捞，然后放入铁制烤盘内。碗内加热清汤，葱油、肥肉片、酱油、绍酒、白糖搅匀，浇在鱼身上、放入烤炉内。待烤至10分钟时打开炉门，把盘内的汁往鱼身上浇几次，关上炉门再烤10分钟取出，去掉肉片、花椒，将鱼摆在鱼盘内。炒锅内加花生油，放入菠菜段，煽炒至熟倒入漏勺内，沥净油，汤放在鱼腹的一边，将烤盘内的鱼汤浇在鱼和菠菜叶上即成', NULL, NULL, NULL, '加吉鱼750克。 菠菜50克、肥猪肉片100克。酱油10克、花生油250克、葱油15克、绍酒25克、白糖20克、花椒5克。');
INSERT INTO `recipe` VALUES (5077, 71, '酱渍鳎目鱼', '将鳎目鱼刮去鳞，洗净背上黑皮，在脊背部位打上斜刀，抹上甜面酱肮渍入味。冬菇切丝，葱、姜、蒜切成末，待用。炒锅内加花生油，中火烧至六成热（约150℃），将鱼段放入锅里煎，待两面呈金黄色时取出。锅内留少量油烧至六成热（约150℃），用葱、姜、蒜一烹，加入肥瘦肉丝偏炒，随即加入清汤、醋、绍酒、酱油、冬菇，再放入鱼段一起烧，待汤汁约剩1／3时，将鱼捞出摆在盘内，随后将锅内原汤用湿淀粉勾芡，加上葱油搅匀，浇在鱼身上即成。', NULL, NULL, NULL, '鳎目鱼750克。 肥瘦猪肉各50克、水发冬菇25克。甜面酱150克、葱15克、鲜姜15克、蒜15克、花生油300克、清汤250克、醋20克、绍酒10克、酱油10克、淀粉10克。');
INSERT INTO `recipe` VALUES (5078, 72, '香露全鸡', '将鸡治净，从背部剖开，再横切3刀，鸡腹向上放入炖钵，铺上火腿片、香菇，加入味精，精盐，绍酒，生姜，葱白段等调料、鸡汤。 钵内放入盛有高粱酒、丁香 的小杯，加盖封严，蒸2小时后取出钵内小杯即成。', NULL, NULL, NULL, '肥嫩母鸡1只，水发香菇2朵，火腿肉2片，高粱酒50克，鸡汤750克，丁香子5粒，味精，精盐，绍酒，生姜，葱等。');
INSERT INTO `recipe` VALUES (5079, 72, '白烧鱼翅', '一、将鱼翅整只放入清汤锅里，加葱、姜、酒煮三次，取出鱼翅，挤干水分，以拔除海腥味。 \r\n二、将母鸡剖开，去内脏，洗净；将蹄膀也洗净理清，一起放入 开水锅内川一下，以拔除血水。 \r\n三、用砂锅，锅底放竹垫，竹垫上放鱼翅，鱼翅上覆鸡和蹄膀，再加清汤， 然后用温火焖酥。', NULL, NULL, NULL, '水发整鱼翅（最好用吕宋黄翅）（５００克）、光母鸡（１只）、鸡油９少许）、猪蹄膀（１只）、姜片、葱、盐、黄酒、菱粉、清汤（１２００克）');
INSERT INTO `recipe` VALUES (5080, 72, '菊花鲈鱼', '1． 将鲈鱼去鳞，剁掉头尾，掏去内脏，洗净后剖成两片，剔去脊骨、肋骨。在鱼肉面用直刀剞上1厘米宽距、0.33厘米深的花刀，再用斜刀横剞1厘米宽距、3成切断1块菊花鲈鱼坯，按此法共切10块。　　\r\n2． 芥蓝菜叶洗净，剪成菊花叶状，下沸水锅氽熟取出。肉清汤、精盐、香醋、白糖、番茄酱、味精、湿淀粉兑成卤汁。 　　\r\n3． 炒锅置旺火，下入花生油，烧至七成热，将鲈鱼生坯用干淀粉抓匀后下锅，炸2分钟至鱼块卷成菊花形时，用漏勺轻轻捞起，沥干油后盛入盘中，再饰配菊花叶形芥蓝菜叶。 　　\r\n4． 锅中留底油约50克，放', NULL, NULL, NULL, '鲈鱼1条……1000克，花生油…500克 （约耗150克），干淀粉……100克，番茄酱……50克，芥蓝菜叶…2叶，香醋……15克，湿淀粉……10克，味精……2.5克，白糖……50克，精盐……1.5克，肉清汤……75克');
INSERT INTO `recipe` VALUES (5081, 72, '灵芝恋玉蝉', '1． 净虾肉75克、猪肥膘肉50克分别剁成泥，一并加上精盐0.5克、味精1拌匀，捏成10个菇盒馅料。 　　　　\r\n2． 冬菇捏去水分，放在碗里，放入少许上汤，上笼屉用旺火蒸30分钟取出，挤干水分，摆在盘上，使去蒂的一面向上，撒匀干淀粉，在其中10朵冬菇上各放1个馅料，用其余10朵冬菇分别覆盖之，稍压粘成冬菇盒，呈灵芝状。 　　　　\r\n3． 将余虾肉、猪肥膘肉分别剁成泥，一并加上精盐1克、味精2克拌匀，捏成20个玉蝉馅丸。 　　　　\r\n4． 鸭蛋清盛于碗内，打散，取铁勺一放在火上烧热取出。另取猪肥膘肉1小块', NULL, NULL, NULL, '水发冬菇…20朵， 熟火腿肉…10克， 鲜虾肉…200克， 猪肥膘肉…75克， 鸭蛋清…6个， 干淀粉…2.5克， 精盐…4克， 上汤…500克， 味精…5.5克');
INSERT INTO `recipe` VALUES (5082, 72, '炒鲎片', '1． 将鲎尾肉软骨剔净，切成薄片，用精盐1在、味精0.5克加少量清水调成汁，把鲎肉片浆匀稍腌，鸭蛋清盛于碗，打散后，加入干淀粉拌成蛋粉糊。 　　\r\n2． 番茹片、嫩姜、辣椒片、蒜末、湿淀粉、绍酒、精盐、味精、骨汤一并调成卤汁，冬笋切成片。　　\r\n3． 炒锅置旺火上，下熟猪油烧至六成热时，将腌好的鲎肉片挂匀蛋粉糊，下锅炸至九成熟捞起。而后，放入冬笋片炸熟，起锅滗去油，取过油笋片装盘垫底。　　\r\n4． 炒锅置旺火上，下猪油50克烧热，先将香菇下锅略几下，随即倒入卤汁煮沸勾芡，然后放入过没鲎肉片，并加入熟猪油1', NULL, NULL, NULL, '净鲎尾肉…400克，干淀粉…10克，水发香菇…25克，湿淀粉…25克，净冬笋…100克，绍酒…10克，番茄片…25克，精盐…3克，鸭蛋清…2个，味精…2克， 辣椒片…1.5克，猪骨汤…100克，蒜末…25个，猪骨汤…100克，嫩姜片…1.5克');
INSERT INTO `recipe` VALUES (5083, 72, '红焖通心河鳗', '1． 将活河鳗放养于清水中2天，以去掉泥腥味，宰杀时，用卫生纸擦掉鱼体上的粘液，剁掉头尾，去内脏，切成4.5厘米长的段，洗净后放钵内，用清水250克与精盐和成的盐水腌浆10分钟，冬笋下沸水锅氽熟捞出。与猪五花肉均切成长3厘米、宽1.5厘米、厚0.6厘米的块。 　　2． 锅置旺火上，下熟猪油烧至七成热，将鳗鱼段沥干腌汁，下锅炸3分钟，鱼体呈赤黄色时捞起。　　\r\n3． 锅置旺火上，下熟猪油10克烧热，放入白糖熬至色赤时，将五花肉片、冬菇及冬笋下锅煸炒几下，放进过油鳗鱼段，加姜块、葱条、酱油、味精10克、肉清汤', NULL, NULL, NULL, '活乌耳鳗……3条 精盐……1.5克 水发香菇……25克 肉清汤……600克 葱条……3克 猪五花肉……150克 湿淀粉……10克 净冬笋……50克 白糖……25克 姜块……5克 绍酒……50克 味精……15克 酱油……30克 熟猪油……750克');
INSERT INTO `recipe` VALUES (5084, 72, '扳指干贝', '1． 将白萝卜去皮洗净，切成1.65厘米长的段，横切面用1.65厘米直径的圆形薄铁筒套取萝卜柱24个，每个萝卜柱分别用0.8厘米直径的圆形薄铁筒扎透，去掉萝卜心呈\"扳指\"形，每个\"扳指\"均填入干贝1粒，装完摆入扣碗，浇上干贝汁150克，上，上笼屉用旺火蒸烂取出，先将蒸汁滗下待用，再翻扣于汤盘中。　　\r\n2． 炒锅置中火，倒入干贝汁100克及蒸汁煮沸，加精盐、味精调匀，起锅徐徐淋于\"扳指干贝\"上即成。  \r\n注意： \r\n　　干贝须先水发，将干边上的老肉去掉，用清水洗净，去沙土、霉味，上笼屉用中火蒸2小时取出', NULL, NULL, NULL, '干贝…24粒，精盐…1克，白萝卜…1000克，味精…1克，干贝汁…250克。');
INSERT INTO `recipe` VALUES (5085, 72, '红焖通心河鳗', '1. 将活河鳗放养于清水中2天，以去掉泥腥味，宰杀时，用卫生纸擦掉鱼体上的粘液，剁掉头尾，去内脏，切成4.5厘米长的段，洗净后放钵内，用清水250克与精盐和成的盐水腌浆10分钟，冬笋下沸水锅氽熟捞出。与猪五花肉均切成长3厘米、宽1.5厘米、厚0.6厘米的块。 2. 锅置旺火上，下熟猪油烧至七成热，将鳗鱼段沥干腌汁，下锅炸3分钟，鱼体呈赤黄色时捞起。 3. 锅置旺火上，下熟猪油10克烧热，放入白糖熬至色赤时，将五花肉片、冬菇及冬笋下锅煸炒几下，放进过油鳗鱼段，加姜块、葱条、酱油、味精10克、肉清汤500克，', NULL, NULL, NULL, '活乌耳鳗……3条、精盐……1.5克、水发香菇……25克、肉清汤……600克、葱条……3克、猪五花肉……150克、湿淀粉……10克、净冬笋……50克、白糖……25克、姜块……5克、绍酒……50克、味精……15克、酱油……30克、熟猪油……750克');
INSERT INTO `recipe` VALUES (5086, 72, '淡糟香螺片', '1.将香螺肉尾部切除，用竹刷去污，洗净后，片成大小均匀的薄片，放入60℃的热水锅中氽一下捞起沥干，用绍酒搅匀稍腌。冬笋、花菇均切成与螺片相称的片。葱白切马蹄片，与上汤、味精、白糖、白酱油、芝麻油、湿淀粉一并调成卤汁。 2. 炒锅置旺火上，下花生油烧至七成热时，将冬笋片下锅炸约1分钟，倒进漏勺沥去油。炒锅留余油约15克，放回旺火上烧热，先将蒜末下锅煸出香味，再放入红糟略煸，随即放入花菇及过油的冬笋片，到入卤汁烧沸芡匀，迅即放入氽好的螺片，颠炒均匀，装盘即成。注意：洗净的螺肉片为薄片，厚薄要划一适中，太厚不易', NULL, NULL, NULL, '净香螺肉………400克、净冬笋……75克、水发花菇……10克、葱白…2根、蒜末……15克、白酱油……15克、姜末……1克、味精……3克、湿淀粉……10克、上汤……50克、红糟……20克、芝麻油…5克、绍酒……10克、花生油………250克、白糠………10克');
INSERT INTO `recipe` VALUES (5087, 73, '爆氽熏鱼', '将青鱼段斜片成1.5厘米的鱼块。用绍酒、酱油浸渍5分钟。炒锅置旺火上，放入豆油，烧至八成热(约200℃）时，放入鱼块炸至深黄色时捞出。另取锅上火，加入鸡清汤，投入爆鱼块、笋片、水发冬姑、绍酒、葱姜，烧5分钟，再放入精盐、味精即成。', NULL, NULL, NULL, '净青鱼肉500克，笋片25克、水发冬菇15克。 鸡清汤450克，绍酒25克、酱油15克、豆油450克（实耗油75克）、葱姜各10克、精盐2克、味精1.5克。');
INSERT INTO `recipe` VALUES (5088, 73, '霸王别姬', '将甲鱼宰杀洗净，入沸水锅中焯水，去除血水，捞出洗净，用洁布揩干，盖上壳，嫩母鸡去内脏洗净，斩去爪子，将翅膀交叉塞在鸡嘴里焯水，去除血污，清水洗净，将甲鱼和鸡放入锅中加鲜汤，酒，精盐，葱，姜，火腿，香菇，冬笋加盖上笼蒸至汤浓鸡鱼肉烂时，捞出葱姜，加味精，青菜心稍蒸即成。', NULL, NULL, NULL, '甲鱼，鲜汤，酒，精盐，葱，姜，火腿，香菇，冬笋。');
INSERT INTO `recipe` VALUES (5089, 73, '料烧鸭', '将鸭肉顺丝片切成3.5厘米长，2厘米宽、0.4厘米厚的薄片。笋切成片待用。将锅置旺火上烧热，舀入花生油，烧至六成热（约150℃）时，放入冬笋片过油，然后倒入漏勺沥油。原锅仍置旺火上，放入笋片，加精盐、白糖、味精、酱油、鸡清汤，汤烧沸，将鸭片倒入，放入香醋，用湿淀粉勾芡，淋入熟鸭油，起锅装盘即成。', NULL, NULL, NULL, '肥烤鸭肉350克、冬笋75克。 精盐5克、白糖30克、味精3克，酱油25克、鸡清汤100克、香醋15克、湿淀粉25克、花生油500克（实耗50克）、熟鸭油25克。');
INSERT INTO `recipe` VALUES (5090, 73, '富春鸡', '将鸡洗净，从腋下开口，取出内脏，洗净，连同炖肝一起放入水锅中，煮熟捞出。将锅置火上，舀入花生油，烧至八成热（约200℃）时，将鸡投入炸至金黄色捞出，把鸡蛋放入炸至表面起皱纹，葱炸成金黄色。将鸡、肫肝、鸡蛋放入内有竹箅垫底的砂锅内，加入酱油、绍酒、白糖、葱姜，舀入清水淹没鸡身，上旺火烧沸。撇去浮沫，压上平盘加盖，移微火焖1小时30分钟离火，去掉竹箅、葱姜，捞出肫肝切片。把香菇、笋片相间地放在鸡身上，放入精盐，再将砂锅上火烧沸，淋芝麻油即成。', NULL, NULL, NULL, '活仔母鸡一只（重约750克）。 熟鸡蛋100克、笋片25克、水发香菇15克、鸡肫肝50克。酱油25克、绍酒35克、白糖15克、葱25克、姜25克、精盐10克、芝麻油25克、花生油1000克（实耗油125克）。');
INSERT INTO `recipe` VALUES (5091, 73, '大煮干丝', '（1）将豆腐干子先批成薄片，再切成细丝，放入沸水钵中浸烫，沥去水，再用沸水浸烫二次，捞出沥水。\r\n（2） 锅置火上，舀入熟猪油，放入虾仁炒至乳白色时，倒入碗中。\r\n（3） 锅中舀入鸡清汤，放干丝入锅中，再将鸡丝、肫、肝、笋放入锅内一边，加虾子、熟猪油，置旺火烧15分钟，待汤浓厚时，加酱油、精盐。加盖再煮5分钟，后离火，将干丝装入凹盘中，腕、肝、笋、豌豆苗分放在干丝四周，上放火腿丝，撒上虾仁即成。', NULL, NULL, NULL, '方豆腐干100克。 熟鸡丝50克、虾仁20克、熟鸡腕20克、熟鸡肝20克、熟火腿15克、冬笋25克、豌豆苗15克、虾子5克。熟猪油25克、酱油15克、盐10克、鸡清汤200克。');
INSERT INTO `recipe` VALUES (5092, 73, '豆苗山鸡片', '将野鸡脯肉筋膜剔滁，批在柳叶形片，放入清水、中浸漂，捞出沥干，加精盐、绍酒、鸡蛋清、水淀粉搅匀，再加入芝麻油拌和。炒锅上火烧热，放入花生油烧至四成热（约lD0℃）时，投入野鸡片划油，至鸡片呈乳白色对倒入漏勺沥油。炒锅再次上火，放油，投入冬笋片，豌豆苗煸炒，加绍酒、酱油、白糖、味精，用水淀粉勾芡，随即倒入野鸡片，烹入香醋，淋入芝麻油，翻锅装成。', NULL, NULL, NULL, '野鸡脯肉350克。 豌豆苗15克、熟冬笋片25克、鸡蛋清15克。精盐5克、绍酒20克、水淀粉15克、芝麻油10克、酱油15克、白糖5克、味精2克、香醋5克花生油克。');
INSERT INTO `recipe` VALUES (5093, 73, '水晶虾饼', '(1) 先将净白虾彻底洗干净，加入肥膘肉剁碎，砸成细泥状。 \r\n(2) 把去皮荸荠洗干净，用刀拍成碎末，合入虾仁细泥，加入斜酒、盐、味精，用葱、姜泡的冷汤蛋清一同搅拌成虾泥。 (3) 用手把虾泥挤成丸子，再压成小圆饼形。 \r\n(4) 炒勺内倒入熟大油，放文火上烧到3～4成热，把虾圆饼放入，使它煎透，不要上色，注意勤翻，不使它老。 \r\n(5) 煎好后放在盘中，镶生菜边，吃时另带姜丝白糖、醋一小盘。', NULL, NULL, NULL, '净白虾400克，猪肥膘肉100克，去皮荸荠50克，生菜200克，鸡蛋3个。调料 熟大油60克，料酒30克，白糖50克，湿淀粉30克，醋50克，盐 30克，味精10克，葱25克，姜20克。');
INSERT INTO `recipe` VALUES (5094, 73, '银丝长鱼', '鳝鱼活杀，去骨，铲去皮，洗净，放清水中漂泡至肉色发白时捞出，切成细丝，然后用清水漂洗一次，捞出吸干水分，倒入碗中，放进蛋清、精盐、绍酒、水淀粉上浆。冬笋、葱白、蒜瓣分别切成细丝。锅置旺火烧热，放入熟猪油烧至四成热（约88℃）时将鱼丝下锅划油，待鱼丝发白时倒出沥油，原锅留少许底油，投入葱、蒜丝略炸，随后投入笋丝煸炒，再下鱼丝，加鸡清汤、绍酒、精盐、白糖烧沸，用水淀粉勾芡，轻轻颠锅，装入盘中，撒上白胡椒粉即成。', NULL, NULL, NULL, '粗活长鱼750克。 鸡蛋清25克、冬笋40克。盐2.5克、绍酒20克、淀粉15克、葱白10克，蒜瓣20克、白糖5克、鸡清汤50克、白胡椒粉0.5克。');
INSERT INTO `recipe` VALUES (5095, 73, '拌鸡冠肚皮', '(1) 把肚头去皮，洗净去杂质，在肚肉上竖剞数力，然后顶头斜着批成鸡冠型，放进清水内反复漂洗几次。 \r\n(2) 将肚皮下入开水锅内，氽热后捞出，沥去水分，放容器中，加入姜汁、料酒、味精、盐、胡椒粉、香油，拌匀装盆即成。', NULL, NULL, NULL, '猪肚头2个。调料 香油15克，姜汁、味精各3克，料酒2克，盐2克，胡椒粉1克。');
INSERT INTO `recipe` VALUES (5096, 73, '白汤鲫鱼', '（1）将鲫鱼治净，在鱼脊背两侧剞斜十字刀纹。\r\n（2）将锅置旺火上烧热，舀入熟猪油，烧至四成热时，将鱼放入，两面略煎后，加绍酒、葱结、姜片和清水7 50克，烧沸后撇去浮沫，盖上锅盖，移至小火上煮到汤色乳白时（约8分钟），再移至旺火上，加精盐、味精、火腿片、笋片、香菇，烧2分钟后端离火口，拣去葱、姜，盛入大汤碗，将火腿片、香菇片放在鱼身上，淋入熟鸡油即成。', NULL, NULL, NULL, '活鲫鱼2条（约重 500克）。配料：熟笋片 50克，熟火腿片2 5克，水发香菇2 5克。 调料：绍酒 50克，精盐7克，味精2. 5克，葱结10克，姜片 5克，熟猪油7 5克，熟鸡油10 克。');
INSERT INTO `recipe` VALUES (5097, 73, '水晶肴肉', '将猪蹄刮洗干净，用刀平剖开，剔去骨，皮朝下平放在案板上，用竹签在瘦肉上戳几个小孔，均匀地洒上硝水，再用粗盐揉匀擦透，猪蹄入缸腌制后取出，放入冷水内浸泡一小时，取出刮除皮上污物，用温水漂净，猪蹄皮朝上入锅，加葱结，姜片，绍酒，水焖煮至肉酥取出，皮朝下放入平盆中，盖上空盆，压平后，将锅内汤卤烧沸，去浮油，倒入平盆中，稍加一些鲜肉皮冻凝结，即成水晶肴蹄。', NULL, NULL, NULL, '猪蹄，粗盐，葱结，姜片，绍酒。');
INSERT INTO `recipe` VALUES (5098, 73, '炝虎尾', '一、将黄鳝放入开水锅中焯熟。捞出划成鳝丝，各取尾背一段共400克为原料（每500克黄鳝只能取尾背50克左右），其余鳝背及鳝肚另作它用。\r\n二、将鳝尾洗净后，随冷水入锅烧沸，加绍酒，移小火上烩一二分钟即用漏勺捞出，沥干水分，放入碗内，加熬熟的酱油、味精、姜末、麻油、胡椒粉少许拌和。\r\n三、炒锅洗净上火，放猪油25克，下蒜泥煸炒至颜色变黄，将蒜泥连油一起，浇在拌好的鳝尾上即成。\r\n\r\n关键\r\n ①选用中等条子的鳝鱼，其肉肥嫩。开水焯熟时匆过烂，以保持其不碎不断。 ②加调味前，必须先将鳝尾沥干水，否则会影响菜肴', NULL, NULL, NULL, '中等条子的黄鳝5000克，姜末1．5克，蒜泥l克，酱油25克，麻油15克，绍酒5克，味精1．5克， 熟猪油25克，胡椒粉少许。');
INSERT INTO `recipe` VALUES (5099, 73, '白汁回鱼', '将鱼段切成块，放入沸水锅中，加酒、精盐烧沸，捞出洗净。锅置旺火上烧热、舀入熟猪油，烧至六成热（约132℃）时，投入葱白、姜片炸香，放入鱼块，加入绍酒、酒酿，盖上锅盖稍焖,加入精盐、春笋块和高汤，待锅、烧沸后移至小火，焖烧15分钟，再移大火收汤，使汤汁浓稠，淋入熟猪油。起锅装入盘中，撒上白胡椒粉即成。', NULL, NULL, NULL, '回鱼一尾（约重750克）。 春笋35克。绍酒20克、熟猪油75克、酒酿15克、高汤250克、盐2克、葱10克、姜7.5克、白胡椒粉2克。');
INSERT INTO `recipe` VALUES (5100, 73, '灌蟹鱼圆', '锅置火上烧热，放入熟猪油烧至五成热（约110℃）时，放入葱姜末煸香，再投入蟹粉，加绍酒煸炒，加精盐炒和，起锅装入盘中，凉后，做成莲子大小的丸子作馅心。将鱼肉、肥膘分别斩成茸，同放碗中，加入鸡蛋清、葱姜汁水、凉鸡清汤搅匀，放入精盐、绍酒上劲搅拌后，再加入熟猪油，搅匀成鱼茸。用手抓起鱼茸，塞入蟹粉馅心，挤成鱼圆，下入冷水锅中。做完后，将锅置火上烧至鱼圆变熟，捞入清水中待用。将锅置火上，放入鸡清汤,加入精盐、火腿片、木耳、笋片、菜心，烧沸后放入鱼圆，再沸后，起锅装入汤碗中即成。', NULL, NULL, NULL, '净青鱼肉300克，蟹粉150克、熟猪肥膘50克、熟火腿15克、熟春笋25克、水发木耳15克、熟菜心50克、鸡蛋50克。 熟猪油75克、绍酒25克、盐1.5克、葱10克、姜10克、鸡清汤250克。');
INSERT INTO `recipe` VALUES (5101, 73, '鏊锅油鸡', '将鸡治净，下沸水锅中煮5分钟，捞出洗净。将锅置火上，投入玉果。桂皮、八角、小茴香、山柰、姜黄，炒3分钟取出，装入布袋扎紧，放入深砂锅中，加鸡清汤、葱结、姜块，将锅置火上烧热，放入菜油烧至六成热（约150℃）时，离火凉透倒入砂锅中。将砂锅上火，加绍酒煮沸，放入鸡，加精盐，用盆压住鸡身，加盖置中火烧沸，移至微火，将鸡焖熟，取出鸡，凉后斩块装盆，浇上油卤、酱油即成。', NULL, NULL, NULL, '当年活母鸡一只（重约1750克）。 玉果3克、桂皮3克、八角3克、小茴香3克、山奈3克、姜黄3克、葱25克、姜25克、绍酒30克、精盐10克、酱油25克、菜油50克。');
INSERT INTO `recipe` VALUES (5102, 73, '黄泥煨鸡', '一、将活鸡宰杀，煺尽毛，在翅膀腋下开一个长约3厘米的小口，挖去内脏，切除肛门，洗净晾干，再用刀背拍断鸡脚、翅膀、腿骨、颈骨，放入钵里，加酱油50克、绍酒25克、精盐5克、葱末和姜汁各少许，腌30分钟。\r\n二、将鸡肫肝切成片。炒锅上火，放猪油50克，烧至五成热，先下葱段、姜片炒香，再将肫片、虾仁、冬菜末、猪肉丁、火腿丁、香菇丁、大虾米等配料倒入炒和，加酒、酱油各25克，放味精1 克、白糖适量，至配料刚熟时起锅倒入碗内。将配料从鸡翅下刀口放入腹内，然后将鸡头塞进开口处。再将葱切成细茸，拌上少许猪油，涂遍鸡身，', NULL, NULL, NULL, '活新嫩三黄（黄嘴、黄脚、黄毛）母鸡1只（重约1250克），虾仁、京冬菜、白糖各50克，猪肉丁150克，火腿丁、香菇丁、葱、姜各25克，鸡肫、绍酒、酱油各100克，大虾米16克，味精1克，盐200克，猪网油250克，鲜荷叶4张，熟猪油80克，芝麻油、芝麻酱、葱白段各少许。');
INSERT INTO `recipe` VALUES (5103, 73, '蜜饯捶藕', '糯米淘洗干净，放入60℃的温水中浸20分钟。藕洗净，削去两头，一头不见藕孔，从另一头的藕孔中将糯米灌入（不可灌得太实），用刀背将藕孔敲合，然后上笼蒸至酥烂取出，削去皮，顺长切成宽6厘米、厚1.5厘米的块。将鸡蛋加入干淀粉调成蛋糊，将藕块裹上蛋糊，再粘匀干淀粉，用面杖反复捶扁至藕片酥松。锅上火，放入熟猪油，烧至六成热（约132℃）时，逐一放入藕片炸至淡黄色捞起，横切成长条。取碗一个，抹熟猪油，将糖青梅丝、金桔饼丝、蜜枣丝、莲子排入碗底，再排入藕条，放入白糖、熟猪油，上笼蒸15分钟取出。复扣入盘中。炒锅上火，', NULL, NULL, NULL, '老白藕400克。 糯米75克、糖青梅丝5克、金桔饼5克、蜜枣25克、清水莲子50克、鸡蛋25克。精盐1克。淀粉15克、白糖50克、熟猪油500克（实耗油30克）、蜂蜜20克。');
INSERT INTO `recipe` VALUES (5104, 73, '五柳青鱼', '活青鱼去鳞鳃，剖腹去内脏，洗净腹内黑膜。锅内加清水、绍酒、葱姜烧沸，将鱼倒入锅中，加盖移微火焐焖10分钟。同时取炒锅上火烧热、放油、烧至五成热（约125℃）时，投入红辣椒丝略煸，再放入酱生姜丝、酱乳瓜丝、冬笋丝及冬菇丝煸炒，加绍酒、酱油、精盐、白糖、清汤烧沸，烹入香醋，用水淀粉勾芡，将鱼从锅中捞出，装入长腰盘内，浇上卤汁即成。', NULL, NULL, NULL, '乌背青鱼1250克（一条）。 酱生姜丝25克、酱乳瓜丝25克、红辣椒丝5克、冬笋丝25克、水发冬菇6克，葱10克、姜5克、绍酒25克、酱油15克、香醋25克、盐1.5克、白糖50克、淀粉25克、花生油50克。');
INSERT INTO `recipe` VALUES (5105, 73, '平桥豆腐', '将整块豆腐放入冷水锅中煮至微沸，以去除豆腥黄浆水，捞出后片成雀舌形，放入热鸡汤中。反复套过两次，鸡脯肉、蘑菇、海参均切成豆腐大小的片。虾米洗净，用温水泡透。干贝洗净，去除老筋，入碗内，加葱姜、绍酒、水，上笼蒸透取出。炒锅上火烧热，放油，投入配料、高汤、干贝汁、烧沸后将豆腐捞入锅中，加精盐、绍酒、味、精，沸后用水淀粉勾芡，淋入麻油，盛入碗中，撤上青蒜末即成。', NULL, NULL, NULL, '嫩豆腐300克。 水发海参50克、虾米25克、熟鸡脯肉50克、蘑菇25克、干贝25克。鸡汤200克、葱15克、姜10克、绍酒20克、盐10克、味精3克、淀粉25克、麻油15克、青蒜15克、高汤100克。');
INSERT INTO `recipe` VALUES (5106, 73, '常熟叫化鸡', '将鸡宰杀治净，丛右腋下开口取出内脏，治净，放入盘内，加精盐、绍酒、酱油、葱姜腌制1小时。将丁香、八角一并碾成末，加玉果末和匀，擦抹鸡身。将配料均切成豆粒大小的丁下锅加调料炒成馅。荷叶用水泡洗，酒坛泥砸碎，加水、盐拌和，摊在一块湿布上。将熟馅塞入鸡肚内，包上网油后再用荷叶包裹，外面用玻璃纸包好，细麻绳扎紧，放在泥布上，四角拎起包紧，然后揭去湿布，放入烤箱内烘烤4小时。将鸡取出，砸去泥，拆去荷叶、网油，后装入盘中，浇上原汁，食时佐以葱白、甜酱。', NULL, NULL, NULL, '嫩活母鸡一只（重约750克）。 火腿25克、冬菇15克、冬笋25克、鸡肫25克、开洋15克。盐12克、绍酒30克、酱油25克、葱25克、姜25克、八角5克、玉果3克、丁香3克。');
INSERT INTO `recipe` VALUES (5107, 73, '扬州蛋炒饭', '将海参、火腿等配料均切成小方丁。鸡蛋加精盐、葱末搅打均匀。炒锅上火放油，投入虾仁划熟倒出，放入配料丁煸炒，加入绍酒、精盐、鸡清汤烧沸，盛入碗中作什锦浇头／锅置火上烧热，放油烧至五成热（约l10℃）时，倒入鸡蛋炒散，加入米饭同炒，倒入一半浇头炒匀，将饭的2／3装入碗内，再将汆下的浇头、虾仁、青豆及葱末放入锅内炒匀，盛放在碗内盖面即成。', NULL, NULL, NULL, '白灿米饭300克、鸡蛋50克。 冰发海参25克、水发干贝25克、熟火腿25克、熟鸡脯肉35克、猪肉35克、上浆虾仁20克、熟鸭肫20克、水发冬菇15克、熟笋35克、青豆15克。盐10克、葱25克、绍酒25克、鸡清汤75克、猪油50克、');
INSERT INTO `recipe` VALUES (5108, 73, '白汁芦筋', '（１）将蹄筋洗精，放入锅中，加清水烧沸，用小火焖透，捞入清水中，撕去叉筋，剔除余肉，漂洗干净。\r\n（２）将生姜、葱、鸡脯、火腿、冬菇分别改刀成片。将蹄筋从叉头处切断。\r\n（３）炒锅上火，舀入清水烧沸，放入蹄筋，加绍酒１０克及葱、姜片，烫５分钟，倒出沥干。\r\n（４）炒锅复置火上，舀入熟猪油烧热，倒入鸡清汤，将蹄筋、火腿、鸡脯、冬菇、青豆放入，烧沸，加精盐、绍酒５克，放入味精略靠，用湿淀粉勾前，淋入鸡油，起锅装盘即成。', NULL, NULL, NULL, '猪后腿蹄筋３５根，熟鸡脯１００克，熟火腿１００克，水发冬菇５０克，青豆３０粒，精盐１.５客，绍酒１５克，味精１.５客，生姜１０克，葱１０克，湿淀粉４０克，鸡清汤３００克，鸡油１０克，熟猪油３０克。');
INSERT INTO `recipe` VALUES (5109, 73, '杏仁葛粉包', '熟猪肥膘肉斩细，熟花生米碾成末，与白糖一起拌匀，搓成黄豆大小的圆粒，边搓边放入内有葛粉的筐中，然后将筐转动，让圆粒沾上一层葛粉，取出放入漏勺中。炒锅上火，放清水烧沸，将盛有圆粒的漏勺放入沸水中略烫，取出倒入葛粉筐中，迅速转动，使圆粒再粘上一层葛粉，按上法再烫一下，再滚上一层葛粉，放入沸水锅中，烧沸后移至小火上。另取锅上火，放清水，加白糖烧沸。将牛奶、杏仁霜和水淀粉一起调和均均，徐徐倒入糖水锅中，沸时撇去浮沫，将糖乳汁倒入汤碗中，再用漏勺将葛粉包捞出，轻轻倒入汤碗中即成。', NULL, NULL, NULL, '葛粉400克、去皮熟花生25克。 熟猪肥膘40克、杏仁霜25克、鲜牛奶75克、白糖75克、淀粉15克。');
INSERT INTO `recipe` VALUES (5110, 73, '冬瓜鸡方', '将冬瓜切成15厘米、10厘米宽的长方块。在皮面上雕刻花纹图案，肉面挖一长方形槽，深度为1／2。将鸡脯切成细丝，加调料上浆，冬笋切成丝。炒锅上火烧热。放熟猪油，下冬瓜块过油，至皮面呈翠绿色捞出沥袖。炒锅复置火上，放油上鸡丝划油，鸡丝呈白色时倒出沥油。锅上火放油少许，下笋丝略偏，加清汤、精盐、味精、绍酒、咖喱油烧沸，匀芡，倒入鸡丝。搅拌均匀离火，倒入冬瓜槽内摊平，上笼蒸烂取出，扣入盘内。将蒸冬瓜原汁上火，加鸡汤、精盐、味精，勾芡，倒入牛奶，烧呈乳白色时起锅浇在冬瓜上。绿叶菜加调料炒熟围衬在冬瓜两头即成。', NULL, NULL, NULL, '鸡脯肉200克。 冬瓜300克、净笋肉30克、绿叶菜25克、牛奶50克、咖哩油25克。精盐7.5克、味精3克、绍酒25克、猪油500克（实耗油50克）。');
INSERT INTO `recipe` VALUES (5111, 73, '糖醋活鲤鱼', '将虾仁放碗内加调料上浆。锅置旺火烧热，舀入花生油烧至四成热（约100℃）时，放入虾仁划油，捞出沥油。原锅留底油，仍置火上。放蒜泥煸一下，加入青豆、番茄酱、白醋、精盐、白糖、绍酒，烧沸后勾芡，再倒入梨片。将活鲤鱼刮去鳞，剖腹取出内脏，用毛巾抹试干净，用刀在鱼两面剞牡丹花刀，拍上干淀粉，再用湿毛巾将鱼头扎紧，然后手持鱼头，将鱼身放入七成热（约175℃）的油锅中炸1分钟，取出放入盘中，除去毛巾，烧上糖醋汁，撒上熟虾仁，上桌即成。', NULL, NULL, NULL, '活鲤鱼一尾（重约1000克）。 大虾仁、15克、青豆15克、梨片35克。精盐7.5克、蒜25克、番茄酱30克、白醋15克、淀粉25克、白糖50克、绍酒25克、花生油750克（实耗油100克）。');
INSERT INTO `recipe` VALUES (5112, 73, '苏州酱鸭', '1.原料选择。采用娄门大鸭或太湖鸭为宜，活鸭在1.5公斤以上的一级品加工。 \r\n2.宰杀。将活鸭宰杀，放血要尽，去毛务净(不留小毛)，然后洗净，在清水中浸泡半小时，再刮肚、乱肚开口最多不超过4厘米长度，把内脏挖去，揩净内腔血迹。(鸭肺必须拿净)。\r\n3.腌制。将无内脏的光鸭放入圆桶中，洒些盐水或盐硝水(用硝按国家标准)，每只鸭擦上少许盐，肚内亦洒少许盐，后即抖出。根据不同季节掌握盐渍时间，在夏季以1～2小时，冬季可腌2～3天左右。 \r\n4.烧煮。在烧煮酱鸭前。先将老汤烧开，同时将上述香料加入锅内，每只鸭肚', NULL, NULL, NULL, '一级鸭100只酱油5公斤 盐7.5公斤 白糖5公斤 桂皮0.3公斤 大茴香0.3公斤 丁香0.03公斤 砂仁0.02公斤 红曲米0.75公斤 葱3公斤 生姜0.3公斤 绍酒5公斤 硝0.06公斤 (溶化制卤水2公斤)');
INSERT INTO `recipe` VALUES (5113, 76, '麒麟鲈鱼', '1.鲈鱼切下头、尾，从中段背部剖开成2大片，每片斜切成6小片，然后用精盐、酒、胡椒粉、味精腌一下，香菇泡软对切成12片； 鱼头、尾排开置于盘中， \r\n2.鱼肉、香菇、火腿、笋片各取1片组成一组，共12组，排好后放入蒸笼中蒸8分钟，精盐、味精、水、香油、淀粉勾芡淋在 。', NULL, NULL, NULL, '鲈鱼100克 香菇6个，火腿300克，笋片150克 姜片20克，精盐8克，酒25克，胡椒粉、味精各2克，葱段、香油各15克');
INSERT INTO `recipe` VALUES (5114, 76, '粟米香菇排骨汤', '1.排骨烫去血水，玉米切段，香菇泡软去蒂。\r\n2.水煮排骨、玉米、香菇，大火转文火，慢慢煨炖约1小时，撒盐即可  \r\n风味特点： \r\n此汤具有明目，解热之效。', NULL, NULL, NULL, '排骨1斤，玉米2个，香菇5个，盐。　');
INSERT INTO `recipe` VALUES (5115, 76, '海南椰子盅', '椰子去皮洗净，穿透椰眼，控出椰汁后从蹄部锯开作成椰盅；冰糖置碗内稍蒸后倒入椰盅，上笼蒸1小时后再倒入发好的银耳和椰奶，蒸 熟即成。', NULL, NULL, NULL, '大椰子1个水发银耳100克冰糖100克');
INSERT INTO `recipe` VALUES (5116, 76, '甘菊猪肚', '猪肚洗净后在滚水中煮3分钟，捞出后切成大薄片。在煮锅中加精盐，老姜，料酒后再放入猪肚片煮滚后小火焖一个半小时即可。 \r\n风味特点： \r\n此菜具有强壮肠胃之功，又有明目之效。', NULL, NULL, NULL, '猪肚1个，清汤6大碗，甘菊3克，黄芪3克，老姜1大片。');
INSERT INTO `recipe` VALUES (5117, 76, '佛手排骨', '1.先将排骨拆枝脱肉，排骨枝用刀剁成每枝5厘米长，再把脱出来的排骨肉、瘦肉及肥肉、虾仁肉、荸荠、方鱼、生葱、红辣椒，分别用刀改切后拌在一起，放在砧板用刀剁成茸后，加入精盐、味精、麻油、川椒末拌匀，用手把肉茸分别镶在排骨枝上捏成20枝佛手状，沾一干面粉，再将面粉压实。 \r\n2.将鸭蛋磕开，打成蛋液，然后把佛手状的排骨一枝一枝用鸭蛋液蘸过，再放入油鼎中用温油炸至熟透即成。配甜酱2碟上席。  \r\n风味特点： \r\n形似佛手，外香里嫩，鲜美可口。', NULL, NULL, NULL, '排骨400克，瘦猪肉300克，虾肉50克，鸭蛋2个，肥猪肉25克，生葱50克，荸荠50克，方鱼15克，精盐10克，麻油5克，味精6克，面粉100克，红辣椒1粒，川椒末少许，生油1000克（耗100克）。');
INSERT INTO `recipe` VALUES (5118, 76, '干贝发菜', '干贝用开水泡软，移入蒸笼内蒸1小时，整齐的排入碗中； 大白菜切成细丝，加油、精盐、味精炒软，沥干水分放入排好干贝的碗中，蒸20分钟取出扣在盘中； 发菜泡软放高汤中煮，等入味后捞出排在干贝的四周，放精盐、胡椒粉、高汤、淀粉勾芡，淋上即可 \r\n注意： \r\n　　营养成分：胆固醇：60毫克碳水化合物：6克 蛋白质：171克 脂肪：3克热量：738大卡', NULL, NULL, NULL, '干贝300克 大白菜200克，发菜50克 味精1克，精盐、胡椒粉各5克，淀粉10克 高汤100克 油50克');
INSERT INTO `recipe` VALUES (5119, 76, '皮片乳猪', '(1) 将乳猪放于案析上(胸脯向上)，从嘴巴开始经颈部至脊背骨尾部止，沿胸骨中线劈开(注意不要破损表皮)，掏出内脏(留下腰子不取出来)。\r\n(2) 将猪体内外冲洗干净沥水，使猪壳成平板形。挖出猪脑，将两边牙关节各劈一刀(注意不要破损老皮)，使上下分离。取出第三条肋骨、划开扇形骨关节取出扇形骨，并将附近的厚肉以及臀部厚肉轻轻划上几刀。 \r\n(3) 将五香盐均匀地涂抹在猪腔内，然后用铁钩把肉挂起，腌30分钟左右，凉干水分。 \r\n(4) 把豆酱、芝麻酱、腐乳、汾酒、蒜泥、白糖(25克)等，均匀地涂抹猪腔内，腌2', NULL, NULL, NULL, '宰杀净乳猪1只(重约15公斤左右)，千层饼130克，葱球150克，酸甜菜150克。植物油25克，木炭7～8公斤，白糖65克，豆酱100克，芝麻酱25克，甜酱100克，汾酒7克，烤乳猪糖醋150克，红腐乳25克，五香盐65克，蒜泥5克。');
INSERT INTO `recipe` VALUES (5120, 76, '东江盐焗鸡', '1、炒锅上小水，下精盐4克烧热，放入沙姜末拌匀取出，分3小碟，每碟加入猪油15克供佐食。将猪油75克、精盐5克和芝麻油、味精调成味汁。反纱纸一张刷上花生油待用。 \r\n2、将活鸡宰杀，褪毛去内脏洗净，吊起晾干，去掉趾尖和嘴上的硬壳，在翼膊两边各划一刀，在颈骨上剁一刀，然后用精盐3.5克擦匀鸡腔，并放入姜、葱、八角末，先用未刷油的纱纸裹好，再包上已刷油的纱纸。 \r\n3、用旺火烧热炒锅，下粗盐炒至高温，取出1/4放入沙锅，把鸡放在沙锅内，将余下的盐覆盖在鸡上，盖严锅盖，用小火焗约20分钟至熟。 4、把鸡取出，揭', NULL, NULL, NULL, '重1500克左右的肥嫩项鸡1只（毛黄、嘴黄、脚黄、下过蛋的母鸡称项鸡），姜片、葱条各10克，香菜25克，粗盐2500克，精盐13 克，味精7史，八角末、沙姜末各2.5克，芝麻油1克，熟猪油120克，花生油15克沙纸2张。');
INSERT INTO `recipe` VALUES (5121, 76, '什锦冬瓜帽', '1.先将冬瓜削去皮、抠去籽瓤，用清水把里外都洗干净，用开水烫一下，再用大扣碗放好。\r\n2.将猪肉洗干净切成小丁，用粉浆擦匀。 \r\n3.用凉水把薏米浸透后洗干净，然后把肉丁、熟火腿、冬菇、干贝、白果等放入炒勺内用开水氽5分钟左右，取出，沥干水分后放入冬瓜中。\r\n4.加入胡椒粉料酒、盐、味精等调料，放满鸡汤后，上笼屉用中火蒸1小时左右，不宜过烂，以瓜内的肉丁熟透为准，而后取出。 \r\n5.将冬瓜放入汤窝中，扣成帽形，而后加清鸡汤即成。', NULL, NULL, NULL, '冬瓜1个(重约2～2.5公斤为宜)，猪肉250克，熟火腿25克，冬菇15克，白果100克，薏米150克。调料:胡椒粉少许，料酒15克，湿淀粉6克，盐15克，味精10克，鸡汤适量。');
INSERT INTO `recipe` VALUES (5122, 76, '清风送爽', '1.取豆腐横刀切成簿片，制成扇形，底面2层，待用。 \r\n2.将瘦肉、虾肉、蛋清、荸荠制成肉，配上精盐、味精搅匀后抹在底层豆腐上面，再将面层豆腐覆盖上去，成夹心状。 \r\n3.用茄子做成扇柄骨架，西红柿切成半圆状，置于扇形顶端，白萝卜丝做扇坠，成型后放入蒸笼蒸5分钟即熟。 \r\n4.取出，用其余铺料在扇面点缀图案，洒上香油，即可上席。', NULL, NULL, NULL, '豆腐500克、瘦肉、虾肉、蛋清、荸荠、葱白、西红柿、香菇、茄子、香油适量。');
INSERT INTO `recipe` VALUES (5123, 76, '炖柠檬鸭', '将光鸭剖腹取出内脏，用开水烫过，洗去血水污物，再用清水漂凉，捞起，装入炖盅，加入精盐，上汤，放入蒸笼隔水炖50分钟后，再加入柠檬（要去掉内核），再炖10分钟，校对汤水咸淡，加入味精，淋下芝麻油即成。', NULL, NULL, NULL, '光鸭1只（约750克），上汤750克。柠檬1粒,精盐、味精、芝麻油各适量');
INSERT INTO `recipe` VALUES (5124, 76, '香煎萍果薯饼', '1．薯仔去皮洗净切成丝，萍果去核，连皮切丝，与薯丝拌匀。\r\n2．牛油煮溶，将烟肉碎炒至香脆，加入洋葱炒数分钟。\r\n3．加入萍果薯丝及葱碎拌匀，以盐及胡椒粉调味。\r\n4．将以上材料铺满锅底，用小火煎至两面金黄。\r\n5．将芝士铺于薯饼上，趁热食用', NULL, NULL, NULL, '薯仔600克 华盛顿萍果1个 洋葱(切碎)1个 烟肉(切粒)1片 葱段(切碎)少许 牛油2汤匙 芝士3片');
INSERT INTO `recipe` VALUES (5125, 76, '蜜瓜脆鳝', '1．黄鳝去头尾洗净，用盐、生粉略拌去滑过水放干，切成片，加入腌料拌腌片刻，留用。\r\n2．蜜瓜挖球，红椒切丝。\r\n3．将鳝鱼沥干洒上生粉，放滚油内炸脆盛起。\r\n4．烧油爆姜、蒜，下调味煮溶，下蜜瓜球，鳝鱼快手炒匀，洒下红椒丝即可。', NULL, NULL, NULL, '黄鳝320克、红辣椒2只、蜜瓜(挖球)约240克、姜2片、蒜头2粒、生粉半杯 镇江醋2汤匙、糖3/4汤匙、盐1/4茶匙、生粉1茶匙、水4汤匙 酱油1茶匙、酒1茶匙、胡椒粉少许');
INSERT INTO `recipe` VALUES (5126, 76, '马铃薯笋焖鸡', '1.鸡肉、马铃薯、笋分别洗净并分别切块，葱洗净切段，姜洗净切片，备用；\r\n2.将切好的鸡肉放入大碗内，拌入腌料并拌匀，腌约30分钟左右；\r\n3.用锅烧开水，放入马铃薯和笋并略煮一下，捞起沥干水分；\r\n4.锅烧热注入2匙油，爆香姜和葱，放入鸡肉、马铃薯和笋并炒匀；\r\n5.加入调味料，加盖，焖煮至熟透并汁浓即可供食。', NULL, NULL, NULL, '主料：鸡肉400克，马铃薯100克，笋100克，葱2条，姜适量，油适量。腌料：酱油2匙，淀粉半匙。调味料：生抽3匙，水适量。');
INSERT INTO `recipe` VALUES (5127, 76, '肉酿生麸圆', '（1）将猪腿肉剁成末，放入碗中，加葱末、绍酒50克、酱油、白糖，拌成肉馅。将生面筋拉成长条，切成50块，搓成圆形，放入清水（冷天放入温水，热天放入冷水）浸约10分钟后，取出拉成厚薄均匀的圆薄皮，每个包入肉馅17克，收好口，成生麸肉圆。\r\n（2）将锅置旺火上，舀入清水1500克烧沸，加入精盐，再放入生麸肉圆，烧至锅边水起小泡，移至小火再烧5分钟，撇去浮沫，加绍酒20克、味精、芝麻油，起锅盛入大汤碗中即成。', NULL, NULL, NULL, '净猪腿肉600克，生面筋500克，酱油50克，精盐15克，白糖30克，绍酒70克，味精2克，葱末10克，芝麻油2克。');
INSERT INTO `recipe` VALUES (5128, 76, '酸汤龙骨面', '面粉加鸡蛋、水和匀，揉至光滑成蛋面团，用湿布盖上，饧10分钟，然后擀成1厘米厚的片、用刀切条后改成小丁、撒上面粉防粘。再将面丁在梳子上压出一面有花纹的面片，入开水锅中煮至7分熟，再用漏勺捞出，放入烧开的酸汤中煮熟，连同酸汤一起舀入碗中，撒上葱花、滴几滴木姜油即成。  \r\n    技术要领：\r\n　　面片只压不搓才成排骨块状。，酸汤用番茄酱制成。', NULL, NULL, NULL, '精制面粉150克，鸡蛋1个，红酸汤250克，木姜油、葱花等适量。');
INSERT INTO `recipe` VALUES (5129, 76, '绍子烘蛋', '1、蛋打散，加入盐、玉米粉后打松；再与绞肉、木耳末、榨菜末各2大匙混合均匀。\r\n2、把油放入锅中加热，把蛋汁到入锅中，先用大火烘，再用小火烘3分钟，此时蛋皮两边会渐渐膨高，如果中间没有膨起，可以另放一勺油淋在中间，盖上锅盖，使蛋全部膨高后，小心用锅铲翻面，待两面呈金黄色，即可放在盘中，再切成2公分块状。\r\n3、锅中再留一点余油，将绞肉放入锅中炒散，再把木耳末、榨菜末、葱花一起炒香，加入高汤煮滚，加盐、味精调味后，盛起淋在烘蛋上，即可食用。', NULL, NULL, NULL, '蛋5个、盐半匙、玉米粉半匙、绞肉3大匙、木耳末3大匙、榨菜末3大匙、葱花少许、高汤1饭碗、味精半匙、油600公克。');
INSERT INTO `recipe` VALUES (5130, 76, '西柠蜜糖乳鸽', '1.乳鸽洗净抹干身，以醃料醃一小时。\r\n2.将乳鸽泡油至金黄。\r\n3.烧热锅，下葱、蒜蓉及姜至香及金黄。\r\n4.放回乳鸽、柠檬及调味料煮滚，慢火炆煮约三十分钟至腍滑。\r\n5.乳鸽斩件，将汁料以生粉水埋獻，淋回乳鸽表面即成。', NULL, NULL, NULL, ' 乳鸽：一只（十两）蜜糖 ：两湯匙');
INSERT INTO `recipe` VALUES (5131, 76, '荷包水鱼', '将水鱼宰杀后,用60℃水泡后擦去外衣，再用刀从水鱼胸部割开,取出肠肚洗净待用。薏米用开水泡洗。香菇、赤肉、火腿１０克切粒，放在碗内，加入薏米、味精、绍酒、精盐拌匀，瓤入水鱼腹内，用咸草扎紧，背朝下放入炖盅，把鸡壳斩碎，焯水生放在水鱼上面，加入疡、精盐、蒜头（用竹支串成１排）、姜、葱头、先用旺火烧沸，后用中火炖约１个半小时，取出去掉姜、葱、蒜头，倒翻落汤碗，上面放火腿片，校对汤水和咸淡，加入味精、胡椒粉、芝麻油即成。', NULL, NULL, NULL, '鱼1只(约600克),薏米25克,湿香菇25克, 笋花6片, 赤肉100克,熟火腿30克，上汤600克。蒜头肉几粒，鸡壳、味精、绍酒、姜、葱、精盐、胡椒粉、芝麻油各适量。');
INSERT INTO `recipe` VALUES (5132, 76, '红斑二吃', '1.红斑一边起肉，切成骨牌形，加调味料腌过。\r\n2.另一边红斑加菇丝、姜花、葱花蒸约五分钟至熟。\r\n3.把1)的斑肉泡嫩油，下姜花、红萝卜花炒过，埋芡，取出放2)之碟中的另一边，周边围放葱花、红椒', NULL, NULL, NULL, '红斑鱼1条（草鱼、鲤鱼也可）。');
INSERT INTO `recipe` VALUES (5133, 76, '宫庭献鱼', '1.鱼丢鳞刮净在鱼身切下数刀，抹上绍酒和盐少许腌匀，待用。\r\n2.将上项鱼扑上干粉，放入滚油中炸至外呈金黄色，鱼熟捞起，滤干油份。上碟。\r\n3.用1汤匙油爆香蒜茸、姜米，倒下茨汁料煮开，打茨。\r\n4.将奇异果、草莓、菠萝同切成小块放人茨汁中煮匀，淋上鱼面即成。', NULL, NULL, NULL, '鲩鱼或黄花鱼1条约重l斤左右，奇异果、草莓、菠萝各少许，蒜肉2粒剁碎，姜米1茶匙。生抽1/2汤匙，盐1/3茶匙，镇江醋3汤匙，糖1 1/4汤匙，水1/2杯，麻油少许。(生粉水少许打茨用。)');
INSERT INTO `recipe` VALUES (5134, 76, '炊麒麟鱼', '１、将鲈鱼起肉去皮，鱼头开两片，鱼尾留用。把鱼肉片成“双飞片”（２片连在一起），白肉、火腿、香菇各切成薄片，加入鸡蛋清、绍洒、精盐、味精、胡椒粉腌渍几分钟。\r\n２、将鱼盘的盘底抹上薄猪油，然后将火腿、香菇、白肉夹在鱼片中间，逐件摆进盘里，然后摆入头尾，放进蒸茏用旺火蒸约１０分钟取出。把原汁下鼎加上汤、精盐、味精、用淀粉水打芡，淋入芝麻油、猪油即成。', NULL, NULL, NULL, '鲈鱼１条（７５０克），湿香菇３０克，火腿２５克，白肉５０克，鸡蛋清１只，味精、精盐、绍洒、胡辣粉、芝麻油、淀粉、上汤、熟猪油各适量。');
INSERT INTO `recipe` VALUES (5135, 76, '炒麦穗花鱿', '1、将鱿鱼洗净，用竖刀从头部右上方斜着向下至尾刻斜纹（刀距要密），把鱿鱼调转，再由尾部右上方斜着刀向下刻斜纹，每距3厘米铲出一块。将香菇、辣椒切块。笋肉刻花后切成片。\r\n2、将上汤、味精、鱼露、胡椒粉、芝麻油和淀粉水调成芡汁。鱿鱼和湿淀粉拌匀待用。\r\n3、炒鼎烧热后，倒入猪油，烧至五成热时，放入鱿鱼过油，捞起沥去猪油。炒鼎下猪油，放入葱、笋、香菇、辣椒略炒，加入鱿鱼，烹入绍酒，用芡汁勾芡，最后加猪油25克拌匀即成。 \r\n特 点： 爽脆鲜美，味馥，经过细致刀工，形似麦穗，故名。', NULL, NULL, NULL, '主料： 浸发鱿鱼400克、熟笋50克、湿香菇25克、猪油500克（耗75克）。 \r\n配 料： 红辣椒、葱段、味精、鱼露、胡椒粉、芝麻油、绍酒、上汤、淀粉各少许。');
INSERT INTO `recipe` VALUES (5136, 76, '白汁鲳鱼', '1.将鲳鱼去鳞、剖腹、去鳃、肠、洗干净，用布抹干内外水分，然后用刀在鱼身的两面剞上花刀，抹上精盐2.5克、绍洒，加入姜、葱放进蒸笼12分钟，取出滗去原汁。\r\n2.将上汤下鼎，加入味精、精盐2.5克和淀粉水一起搅匀，即将鼎离炉，加入猪油、牛奶拌匀再煮一下，淋于鱼上面，把火腿末撒在鲳鱼上面布成２条直线即成。', NULL, NULL, NULL, '鲳鱼1条（约750克），牛奶100克，熟火腿末25克，猪油50克，上汤100克，味精、精盐、绍洒、姜、葱、淀粉各适量。');
INSERT INTO `recipe` VALUES (5137, 76, '柠檬乳酪饼', '①压碎小麦饼，加入砂糖、牛油溶液拌匀，置饼模内压实，然后放入冰箱急冻成硬块，备用。\r\n②低脂软乳酪、低脂乳酪置深碗内，用打蛋器搅至乳酪幼滑，然后加入炼奶、柠檬汁拌匀，冷藏。\r\n③把奶油注入深碗内，用打蛋器搅拌至奶油呈固态。\r\n④把乳酪浆倒入奶油内，轻轻拌匀，然后倒入饼模内，放冰箱急冻一夜，待乳酪凝固即成。', NULL, NULL, NULL, '低脂软乳酪100克、低脂乳酪100克、奶油230毫升、炼奶250毫升、柠檬汁2汤匙、活动饼模1个、小麦饼10块、牛油溶液4汤匙、砂糖2汤匙');
INSERT INTO `recipe` VALUES (5138, 76, '腊味油鸭饭', '1、煲仔烧红下少量油，放入米及2碗水，猛火煲5分钟至干水。\r\n2、倒去多余水分，再放入腊味封盖，慢火煲7分钟即可。\r\n3、局3分钟，香气更浓郁，饭味更香。\r\n贴士：1、先用温水将腊味略洗净，因腊味是风干食品，这样做可洗去腊味上的灰尘及“益味”，\r\n2、煲仔要预先烧红，下少量油的目的是令米粒沾上油而不易吸收水分，煲起饭来分外软绵绵。\r\n3、想有饭焦靓效果，在饭收水时可转用火力平均的电热饭煲热，这样饭会更易起焦。', NULL, NULL, NULL, '腊鸭5件，腊肠2条，米1碗');
INSERT INTO `recipe` VALUES (5139, 76, '七彩什锦煲', '1、将鸭珍肝切井字深花纹。发土鱿鱼（或鲜鱿鱼），斜切剞刻花纹，再斜切长三角块，分别将其飞水，漂冷。料菇切日字件，浮皮切日字件，笋切象眼片，飞水漂冷。瘦肉用湿粉五分拌匀，略飞水。\r\n2、白菜胆洗净改好（小棵按原棵，大棵应开边成件），放入沸水锅内滚水至八成熟，捞起漂冷。\r\n3、取大沙锅（或大煲仔）一个，坐气炉上火。待热落油四钱，溅入绍酒，排入白菜垫底，放入二汤，加入腐皮卷、鱼腐、牛肉丸、料菇、浮皮、鲜笋、烧沸约五分钟。加入珍肝、鱿鱼、肉片、鱼丸、白果，调入精盐、味粉、鸡精粉，待沸后放入麻油、胡椒粉，淋入一钱油', NULL, NULL, NULL, '炸腐皮卷二两（六件），炸鱼腐一两（约六只），鱼青丸一两五钱（约八只），浸发鱿鱼一两（或鲜鱿件），瘦枚肉片一两，鸭珍肝（或鹅）二两，牛肉丸一两五钱（约六只），白菜胆四两，湿料菇五钱，发浮皮件四片，熟白果仁五钱，鲜笋三钱。\r\n味料： 精盐二钱，味粉一钱、鸡精粉一钱，猪油五钱，麻油、胡椒粉少许，湿粉一钱、绍酒一钱，二汤一斤，香茜叶少许。');
INSERT INTO `recipe` VALUES (5140, 76, '沙拉蛋', '1、马铃薯、胡萝卜去皮后洗净、切片，入蒸锅蒸熟取出，趁熟分别捣成泥状。\r\n2、小黄瓜以盐搓洗，烧锅水将小黄瓜氽烫一下即捞出，浸泡冷开水待凉，取出切片状，备用。\r\n3、火腿切丁；鸡蛋与冷水共煮滚后，改小火再煮10分钟，用筷子夹起（易夹起则代表熟了），入冷水浸泡一下，剥去外壳切片。\r\n4、马铃薯、胡萝卜、火腿、小黄瓜共同拌匀，置于盘面，排上切好的蛋，食用时挤上沙拉酱。', NULL, NULL, NULL, '鸡蛋4个，马铃薯2个，胡萝卜1条，火腿250克，小黄瓜2条。调味料：盐1／2小匙，沙拉酱适量。');
INSERT INTO `recipe` VALUES (5141, 76, '虾胶龙凤卷', '虾仁去沙线制茸，加料酒、盐、味精、小苏打、肥肉末顺一个方向搅上劲。\r\n入冰箱冷藏2小时成虾胶，用蛋糊封口，沾上干粉下6成热油炸熟，取出改刀装盘即成。', NULL, NULL, NULL, '鲜虾仁300克 鸡蛋2个');
INSERT INTO `recipe` VALUES (5142, 76, '菘菜油鸭煲', '油鸭焯水切块，白菜切菜。\r\n北菇在顶部剞十字花刀。\r\n锅中留底油，放蒜茸、葱段、姜花、油鸭、白菜块，加汤、盐、味精、蚝油、香油、胡椒粉、水淀粉，点明油放在煲中，褒至白菜透明入味即可。', NULL, NULL, NULL, '鲜虾仁300克，油鸭半只，白菜约300克，北菇2只，蒜茸3克，姜花3克，葱段5克');
INSERT INTO `recipe` VALUES (5143, 76, '罗汉扒大鸭', '1.将制好的红鸭扣 ,去骨,放在碟上.\r\n2.用油起镬,放入料头及主,配料,赞酒,调味,埋芡,亮尾油,扒在大鸭上即成.', NULL, NULL, NULL, '鸭子830克,玉兰片40克,玉米笋50克,胡萝卜30克,冬菇40克,鲜菇50克,草菇60克,马蹄30克,老抽15毫升,盐1克, 蚝油20克, 味精4.5克,胡椒粉0.2克,料酒10毫升,生粉12克,糖1.4克,植物油40克. 蒸鸭：味精1.5克,盐4.5克,大料3克,葱25克,姜6克,老抽3.5毫升炸鸭：植物油25克.');
INSERT INTO `recipe` VALUES (5144, 76, '香酱炒卤肉', '1、卤水用锅烧开，五花肉洗净，切成大条，放到卤水里，卤至熟透，捞起切片，姜、蒜子切末，葱切花。 　　2、烧锅下油，放入姜、蒜子，煸锅，同卤肉煸炒至金黄。 　　3、卤肉炒至金黄时，再加入XO酱、葱花翻炒几次，淋入麻油即成。', NULL, NULL, NULL, '五花肉200克、姜、蒜子、葱各10克。卤水2瓶、XO酱10克、麻油5克');
INSERT INTO `recipe` VALUES (5145, 76, '柴鱼贵妃粥', '1、米用清水浸片刻，去水，加入腌米料拌匀，待米发涨并呈乳白色时，用匙压碎。\r\n2、柴鱼用清水浸透，沥干，起白镬烘至微香。\r\n3、贵妃骨斩件，氽水洗净，花生用煮水浸1小时以上，去水，再用煮水浸火局半小时，冲菜洗净，切细，腐竹抹净，剪碎。\r\n4、清水15杯注入锅内，先放入腐竹、花生，柴鱼和贵妃骨同煲1小时以上，添加适量煮水，才下米用大火煲20分钟，然后改慢火煲成粥。\r\n5、最后下冲菜和调味料搅匀，上碗时撒上葱粒和切片油条便成。\r\n备注：花生先用煮水浸火局才下锅煮，既可除去花生衣的苦涩味，也较易煮软。', NULL, NULL, NULL, '米3两（约120克），柴鱼1两（约40克），细粒花生仁2两（约80克），贵妃骨（有肉猪扇骨）1斤（约600克），腐竹1/2两（约20克），葱粒2汤匙，冲菜2片，切片油条适量。\r\n腌米料：油、盐各1/2茶匙。\r\n调味料：胡椒粉、麻油、熟油各少许，盐适量');
INSERT INTO `recipe` VALUES (5146, 76, '咖喱海鲜锅', '① 中虾去壳去肠，洗净吸干水分，切双飞。 \r\n② 带子洗净，鱼肉洗净切片。 \r\n③ 中虾、带子及鱼肉加腌料拌匀，放入烧水中略烫一下后放置一旁备用。 \r\n④ 羊角豆洗净，番茄去皮切件。 \r\n⑤ 下2汤匙油爆香干葱茸及咖喱汁，加入清水煮滚后，放入全部材料及调味料，煮至再滚即成。', NULL, NULL, NULL, '中虾、带子各150克,去骨鱼肉150克,羊角豆115克,番茄2个,干葱（磨茸）4粒 \r\n咖喱汁1杯,水2杯 \r\n调味料：\r\n盐、酱青各半茶匙 \r\n糖1/4茶匙 \r\n腌料： \r\n盐半茶匙　　　\r\n玉蜀黍粉2茶匙');
INSERT INTO `recipe` VALUES (5147, 76, '荔枝炒鸡丁', '1、荔枝去外壳及籽；葱切小段；青红椒切菱形片备用； 　　2、鸡胸肉去骨切为1.5厘米的小丁，放入1/4茶匙的盐及1个蛋清，太白粉适量； 　　3、锅内加入适量的油烧至四分热，入鸡丁滑开，待肉变白后，再加入荔枝、青、红椒略翻一下即可捞出沥干。 　　4、锅中留油1茶匙，爆香葱段后，依序加入葡萄酒、鸡丁、荔枝肉及适量的盐和太白粉拌炒均匀即可。', NULL, NULL, NULL, '荔枝300克、鸡胸肉200克、葱2根、青、红椒各1/2个调味料：盐1匙、蛋清1个、太白粉1大匙、白葡萄酒1茶匙、油适量');
INSERT INTO `recipe` VALUES (5148, 76, '富贵黄金卷', '将蛋典开萡、蚧柳、西芹卷成卷，上鸡旦汁，面包糠用中火炸至金黄色', NULL, NULL, NULL, '咸蛋黄、鸡旦汁、日本蟹柳、西芹、面包糠');
INSERT INTO `recipe` VALUES (5149, 76, '韭菜花咸猪肉', '锅内烧净色拉油至7成热，逐片放入腌制好的咸猪肉片后离火，炸成略脆状后倒出。\r\n将韭菜花飞水后，加入少许味水略炒后倒出。\r\n锅内放少许油，倒入料头炒香，加入炸好咸猪肉片，韭菜花，料酒，味水芡汁（带少许蚝油），快速炒匀后亮油起锅。\r\n注意：咸猪肉片腌制不能够太咸，炸的过程中要注意把握油温，既要炸熟又不能够炸干水分，还要保持脆度。', NULL, NULL, NULL, '咸猪肉片（咸猪肉的腌制方法：选用新鲜的带皮猪五花肉切成如图中大小的片，加入：南乳酱，玫瑰露酒，姜葱汁（用搅拌器将生姜和新鲜干葱头搅碎，用纱布沥出汁水），烧肉汁，美极鲜酱油，调和色素（大红和橙红），生粉，充分拌匀后防置冰箱冷藏后取出备用。）\r\n配料：韭菜花（拦刀切成段--如图中）\r\n料头：新鲜干葱头甲片，红萝卜花，炸蒜片，小葱度');
INSERT INTO `recipe` VALUES (5150, 64, '鸡香饼', '1、鸡肉加调味搅拌至有黏性，加入烟肉及葱拌匀，做成小薄饼，沾上鸡蛋，再沾上面包糠。\r\n2、把油约一杯放入深的盛器中，无须加盖，用Hi（高热）煮五分钟至滚。把鸡饼放入油中，盖上盖，留一洞让蒸汽排出，用Hi（高热）炸一分半钟至熟。如油不能浸过鸡饼面，把鸡饼翻转，用Hi（高热）再炸二十秒钟。取起上碟，蘸茄汁或沙律酱吃。\r\n注：盛油之器皿要深，以免炸时油满泻。', NULL, NULL, NULL, '鸡胸肉四两剁幼丁二汤匙，葱二条切碎，面包糠2/3杯，鸡蛋一只打散，\r\n茄汁或沙律酱一小碟。盐1/4茶匙，椒粉少许，鸡蛋白一汤匙，柠檬皮磨蓉半茶匙，生粉二茶匙。');
INSERT INTO `recipe` VALUES (5151, 64, '蒸水蛋', '1、鸡蛋加调味及冷开水打散，除去浮在面层的泡（可以用密孔筛隔去）。\r\n2、把蛋液倒在一个直径约六寸的盛器中，盖上盖，用Roast蒸四分半钟至熟。取出，洒下葱花，盖上盖一分钟，待葱半熟，淋下少许熟油及生抽。\r\n注：蛋液的厚薄影响烹调的时间，故把盛器的直径写出，让大家作为参考。', NULL, NULL, NULL, '大只鸡蛋二只，冷开水一杯，葱二条切碎（不吃葱免用），胡椒粉少计，\r\n生抽一茶匙，盐1/3茶匙');
INSERT INTO `recipe` VALUES (5152, 64, '素酿南瓜', '1、南瓜洗净，上部近蒂处横切去1/4作盖，用匙羹挖去南瓜下半部的核，放在盛器中，盛器放下一汤匙水。\r\n2、冬菇浸软去梗，挤干水切丝。\r\n3、粉丝浸软，切小段。\r\n4、榨菜洗净，切丝。\r\n5、冬菇、蘑菇、笋、榨菜、豆腐干、红萝卜、粉丝加入调味拌匀，放入盛器中，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）煮五分钟。取出，放入南瓜内，盖上南瓜盖。\r\n6、南瓜盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）煮七分钟，取出即成。', NULL, NULL, NULL, '南瓜一个（重约一斤），冬菇半两，蘑菇半罐切片，榨菜一两半，笋肉（罐装）三两切丝，五香豆腐干一件切丝，红萝卜切丝三汤匙，粉丝半两。\r\n蚝油一汤匙，生抽半汤匙，姜汁一茶匙，糖一茶匙，盐1/4茶匙，麻油、胡椒粉少许，上汤或水半杯，油二汤匙');
INSERT INTO `recipe` VALUES (5153, 64, '蘑菇豆腐', '1、青豆加入约半杯水，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）煮二分钟。取出，用清水浸冷，捞起沥干水。\r\n2、豆腐洗净，平均洒下1/3茶匙盐，腌十五分钟。盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）煮11/2分钟，取出。\r\n3、豆腐切件，排在深碟上，青豆、蘑菇、姜放在豆腐上。\r\n4、调味放在盛器中，放入微波炉，用Hi（高热）煮二分钟至滚。取出，下芡拌匀，加油二汤匙又拌匀，倒在豆腐、蘑菇上，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）煮四分半钟，取出即成。', NULL, NULL, NULL, '实板豆腐二件，蘑菇切片2/3杯（新鲜或罐装均可），青豆三汤匙，姜一片，老抽半汤匙，生抽半汤匙，糖一茶匙，麻油、胡椒粉少许，姜一片，水1/3杯，鸡粉半茶匙（不喜欢免用，可用上汤1/3杯取代水及鸡粉）。\r\n芡：生粉11/3茶匙，水二汤匙。');
INSERT INTO `recipe` VALUES (5154, 64, '酸忌廉鸡扒', '1、鸡上腿洗净，抹干水，去皮去骨，把肉厚的部分稍为片薄，然后拍松，加入腌料腌十分钟。\r\n2、鸡肉排在碟上，不可重叠，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用Roast煮二分半钟，取出。\r\n3、把鸡扒翻转另一面，排在盛器中（为方便进食，可以把鸡扒切成小块，然后排在盛器中）。\r\n4、把调味及蘑菇拌匀，倒在鸡扒上，盖上盖或微波炉用保鲜纸，留一孔让蒸汽排出，用Hi（高热）再煮五分钟至鸡肉熟。取出待三分钟，可配上饭或意粉吃。', NULL, NULL, NULL, '鸡上腿一磅半（或用鸡胸取代），蘑菇半罐切，胡椒粉少许，盐碱1/3茶匙，生粉半汤匙，油二汤匙。忌廉鸡汤一罐，酸忌廉1/3杯，芥辣粉二茶匙半');
INSERT INTO `recipe` VALUES (5155, 64, '家常咖喱鸡', '1、鸡腿洗净，抹干水剁块，加腌料拌匀。\r\n2、马铃薯、鸡、咖喱粉、蒜蓉、干葱蓉加调味搅匀，放入深的器皿中，盖上盖，用Hi（高热）煮二十分钟至马铃薯焾，取出待三分钟即可。', NULL, NULL, NULL, '鸡腿一磅半，马铃薯一磅（去皮切厚块），蒜蓉半汤匙，干葱蓉半汤匙，\r\n咖喱粉或没咖喱一汤匙，面粉一汤匙半，油二汤匙。热水一杯半，椰汁半杯，盐2/3茶匙，糖半茶匙，胡椒粉少许，花生酱二汤匙，鸡粉半茶匙');
INSERT INTO `recipe` VALUES (5156, 64, '苹果鸡', '1、鸡洗净，抹干水，用二茶匙半盐涂匀鸡肚及鸡皮，腌半小时。\r\n2、苹果加入半茶匙糖拌匀\r\n3、用一茶匙酒涂匀鸡肚，把八角、葱、苹果放入鸡肚内；用锡纸工包着鸡翼中段及鸡翼尖。\r\n4、鸡肚插在微波炉用烧鸡盘上或放入盛器中，盖上微波炉用保鲜纸或套上微波炉用的胶袋子，要留一孔让蒸汽水排出，用Hi（高热）煮十五分钟至熟（如用烧鸡盘，只需烹煮十三分半钟）。取出待稍冷，把肚内之苹果盛碟上，鸡剁块放在苹果上，汁淋在鸡上。', NULL, NULL, NULL, '光鸡一只（重约二斤半），青苹果一个（切件去核），葱二条，八角二粒');
INSERT INTO `recipe` VALUES (5157, 64, '火腿蛋', '1、鸡蛋打开放入杯内（或较深而面积小的器皿中），加入火腿、葱及调味打散。\r\n2、把蛋原杯放入微波炉，无须盖上保鲜纸，用Bake煮一分钟。取出，用筷子拌匀，再煮一分半钟至热，盛碟上。\r\n麻油=香油', NULL, NULL, NULL, '洋火腿一片切丁（夹面包那种火腿）， 鸡蛋两只，葱一条切碎，盐1/4茶匙，油半汤匙，麻油、胡椒粉少许');
INSERT INTO `recipe` VALUES (5158, 64, '牛肉窜烧', '1、将牛肉，青、红、黄椒及洋葱各切成正方形（大小相近）。\r\n2、牛肉用腌料腌约１０分钟，然后加入其他材料，再一起腌５分钟。\r\n3、用竹签将材料按次序重复串好，平放在碟上。\r\n4、先用［薄块烘烤］预热７分钟，然后放入串铙，用［薄块烘烤］烤焗７分钟，取出反转，再烤焗７分钟，即可供食。', NULL, NULL, NULL, '牛肉250g，洋葱半个，青、红、黄椒各半个，竹签4支。牛油1汤匙， 白酒适量， 蒜末２茶匙， 黑胡椒１茶匙， 姜末１茶匙，盐１茶匙， 混合香草１茶匙， 柠檬汁1/2茶匙。');
INSERT INTO `recipe` VALUES (5159, 64, '韩国烤肉', '1. 把牛肉切成适当大小，加A，放1小时左右，使调料浸入 \r\n2. 把疏菜洗净，控水。把蒜切成薄片。把大葱切成长7厘米、厚3毫米大小，泡凉水，控水，加A盛在碗里\r\n3. 牛肉1加B拌好。用手拌调料更容易浸入\r\n4. 先用［薄块烘烤］预热７分钟，然后放入肉串，用［薄块烘烤］烤焗７分钟，取出反转，再烤焗７分钟，取出。\r\n5. 在爱吃的菜叶上，放烤肉、葱花、蒜片和调料酱吃。同时吃肉汤', NULL, NULL, NULL, '牛肉300克， A（白糖1大勺、清酒2大勺、梨四分之一个） B（朝鲜酱油2大勺、葱花1大勺、蒜泥2小勺、香油1小勺、芝麻1勺），疏菜（生菜、芝麻叶、青辣椒、齐克里等）适量。 大葱半根 C（辣椒粉半小勺、香油半小勺、食盐五分之一勺），蒜3个。大酱2大勺、 辣椒酱半大勺、 蒜泥半小勺、 白糖半小勺、 葱花半小勺、 香油1小勺、 芝麻面1小勺， 把这些搅拌即成');
INSERT INTO `recipe` VALUES (5160, 64, '柠檬白肉鱼', '①在白肉鱼上撒上精盐、胡椒粉、白酒，腌一会儿，使味道渗透。\r\n②在番茄底部用力画个“十”字，用热水烫过，便可把皮撕开，然后切丁；洋葱切薄片。\r\n③将玉蕈去蒂撕开，柠檬切片。\r\n④将白肉鱼、番茄、洋葱、玉蕈、柠檬各分成四等份放入盘中，乳酪也分成四等份，放在上面，撒上白酒。\r\n⑤罩上微波薄膜，高火2分钟后取出即可。', NULL, NULL, NULL, '白肉鱼四片，番茄一只。洋葱少许， 玉蕈一把， 柠檬约一个， 白酒三大匙， 比萨用乳酪80克， 精盐、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (5161, 64, '清蒸鱿鱼', '①鱿鱼斜刀刻花，装盘，覆上微波薄膜，高火6分钟。\r\n②将调料拌匀后，鱿鱼蘸着食用即可。', NULL, NULL, NULL, '水发鱿鱼300克。红辣椒半只， 姜末、米醋各两大匙， 蒜末、芝麻油各一匙， 白糖一大匙半， 酱油四大匙。');
INSERT INTO `recipe` VALUES (5162, 64, '镶苦瓜', '①将苦瓜洗净，去头尾及籽，切成1.5～2厘米厚小段，撒上少许精盐，内抹少许淀粉。\r\n②将肉糜与芝麻油、淀粉、蒜末、酱油、味精、精盐各一小匙调成肉酱。\r\n③将肉酱镶入苦瓜段内，排放在预先放色拉油的器皿中，再撒上白糖、红辣椒末、豆豉，淋上色拉油一中匙、水一大匙，高火12分钟即可。', NULL, NULL, NULL, '苦瓜一条， 生猪肉糜150克。酱油、色拉油各一中匙， 味精、芝麻油、淀粉、蒜末、精盐、白糖各一小匙， 红辣椒末、水、豆豉各一大匙， 胡椒粒少许。');
INSERT INTO `recipe` VALUES (5163, 64, '羊肉汤', '①将羊肉切成块，葱头切丝，放入猪油炒至微黄，加入羊肉、大蒜末、辣椒粉、香叶、茴香籽，高火5分钟，加水中火15分钟煮沸。\r\n②土豆洗净切丁，扁豆洗净切条，高火8分钟，放入汤内，再把面粉加适量水和成稀糊，一同下汤内煮沸，加淡炼乳搅匀，用盐调好口味，高火1分钟即可。', NULL, NULL, NULL, '净羊肉750克，\r\n 葱头250克，\r\n 土豆500克，\r\n 扁豆500克。\r\n化猪油150克，\r\n 大蒜25克，\r\n 香叶一片，\r\n 茴香籽0.5克，\r\n 辣椒粉少许，\r\n 面粉25克，\r\n 淡炼乳100克，\r\n 精盐15克，\r\n 清水3000克。');
INSERT INTO `recipe` VALUES (5164, 64, '什菜鸡饭', '1. 青豆加入浸过之水（约半杯），盖上微波炉用保鲜纸，留一孔让蒸汽排出，用HI（高热）煮二分钟取出，用清水浸冷，捞起沥干水。\r\n2 榨菜用清水浸五分钟，减去咸味及辣味。\r\n3 鸡肉加腌料拌匀。\r\n4 米洗净，放入盛器中，加上汤及油一汤匙拌匀，依次序把鸡肉、榨菜、蘑菇、红萝卜丝放在米面上，盖上盖\r\n留一孔让蒸汽排出，用HI（高热）煮十五分钟。取出，把菜及饭拌匀，盖上盖，用用HI（高热）煮五分钟至饭熟，加入青豆又拌匀，盖上盖，用ROAST再煮三分钟。取出，放置五分钟即可。\r\n青豆=豌豆；生抽=浅色酱油', NULL, NULL, NULL, '米一杯（用黏性米更好）， 红罗卜切丝半杯， 蘑菇切片半杯， 榨菜切小薄片二汤匙， 鸡肉六两切丁， 青豆1/3杯， 上汤一杯半（或用鸡精取代）。生抽半汤匙， 胡椒粉少许， 油一汤匙， 酒一茶匙。');
INSERT INTO `recipe` VALUES (5165, 64, '风味炒饭', '1. 什菜放在盛器中，加入3/4杯水，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用HI（高热）煮三分钟至滚。取出，倒在筲箕内，沥干水。\r\n2 虾米用清水泡软，沥干水。\r\n3 虾米放入盛器中，加油三汤匙拌匀，盖上微波炉用保鲜纸，留一孔让蒸汽排出，用HI（高热）煮三分钟取出\r\n4 饭加调味拌匀，加入虾米、什菜、葱又拌匀，放入稍深的盛器中，不要加盖，用HI（高热）煮五分钟，取出\r\n用匙羹搅动，使饭松散，盛盘上。\r\n粟米=玉米；青豆=豌豆；蒜蓉=蒜泥；筲箕=漏勺', NULL, NULL, NULL, '冷饭二碗（新鲜饭亦可）， 什菜3/4杯（包括熟红萝卜、粟米、青豆，\r\n 冻肉公司有售）， 葱二条切吹碎， 虾米一两。蒜蓉辣椒一汤匙或适量， 鲜酱油一汤匙， 盐1/4茶匙。');
INSERT INTO `recipe` VALUES (5166, 64, '格兰仕食花总烩', '白糖80克，\r\n 花椒18克，\r\n 蒜苗（斜切）八片，\r\n 蒜粒两颗（切泥），\r\n 精盐两小匙，\r\n 味精少量，\r\n 酱油、芝麻油、香油、葱花各少许。', NULL, NULL, NULL, '桂花鱼800克， 花生仁88克， 花菜80克， 鸡蛋80克。白糖80克， 花椒18克， 蒜苗（斜切）八片， 蒜粒两颗（切泥）， 精盐两小匙， 味精少量， 酱油、芝麻油、香油、葱花各少许。');
INSERT INTO `recipe` VALUES (5167, 64, '白粥', '将大米淘净后放入容器中，加入热水，高火20分钟即可。', NULL, NULL, NULL, '大米约300克。\r\n热水七杯。');
INSERT INTO `recipe` VALUES (5168, 64, '玉米粉粥', '将玉米粉加适量冷水调和，待大米粥煮沸后加入玉米粉同煮，高火15分钟即可。', NULL, NULL, NULL, '玉米粉100克，\r\n 大米粥适量。');
INSERT INTO `recipe` VALUES (5169, 64, '糯米麦粥', '①将麦粒、花生仁洗净，一起放在器皿内，高火8分钟。\r\n②待麦粒煮到快开花时，加入糯米同煮，高火12分钟即可，用冰糖调味。', NULL, NULL, NULL, '糯米150克，\r\n 麦粒100克，\r\n 花生仁100克。\r\n冰糖适量。');
INSERT INTO `recipe` VALUES (5170, 64, '小麦粥', '将小麦洗净后放入容器中，加水高火8分钟煮熟，捞去小麦取汁，再加入大米、大枣同煮，高火12分钟熬至软烂即可。', NULL, NULL, NULL, '小麦约60克，\r\n 大米100克，\r\n 大枣5只。');
INSERT INTO `recipe` VALUES (5171, 64, '糯米粥', '将糯米洗净后放入容器，加水高火12分钟煮成粥，食时加白糖。', NULL, NULL, NULL, '糯米50克\r\n白糖适量');
INSERT INTO `recipe` VALUES (5172, 64, '香粳米粥', '将香粳米洗净放入容器，加水，高火12分钟熬成粥，加白糖即可。', NULL, NULL, NULL, '香粳米约60克。\r\n白糖适量');
INSERT INTO `recipe` VALUES (5173, 64, '荠菜粥', '①将荠菜洗干净，姜切成细末。\r\n②将大米、姜末放进器皿中加适量水，高火20分钟，烧沸后放入荠菜，改为小火煮熟，加入芝麻油、精盐、味精即可。', NULL, NULL, NULL, '荠菜1000克，\r\n 大米500克，\r\n 生姜10克。\r\n芝麻油、精盐、味精各少许');
INSERT INTO `recipe` VALUES (5174, 64, '韭菜粥', '①将新鲜韭菜洗净切细。\r\n②先煮大米粥（高火12分钟），待粥煮沸后，加入已切细的韭菜及精盐同煮。', NULL, NULL, NULL, '新鲜韭菜60克，\r\n 大米100克。  \r\n精盐少许。');
INSERT INTO `recipe` VALUES (5175, 64, '萝卜大米粥', '①将萝卜煮熟，绞取汁液。\r\n②将萝卜汁加入淘净的大米中，与适量水高火同煮20分钟即可，食用时可加少许白糖。', NULL, NULL, NULL, '萝卜2500克，\r\n 大米150克。 \r\n 白糖少许。');
INSERT INTO `recipe` VALUES (5176, 64, '桂花白薯粥', '①将白薯隔水蒸熟，去皮，碾成白薯泥。\r\n②把白薯泥调入大米粥中，加白糖后高火5分钟，撒上糖桂花即成。', NULL, NULL, NULL, '白薯50克， 大米粥25克。  糖桂花数粒， 白糖适量。');
INSERT INTO `recipe` VALUES (5177, 64, '番薯粥', '将番薯去皮切成小块，和小米高火15分钟煮成粥即可。', NULL, NULL, NULL, '番薯（即红薯）50克，\r\n 小米30克。');
INSERT INTO `recipe` VALUES (5178, 64, '红枣山药粥', '①将红枣用沸水胀发后去核切丁，山药去皮切丁，红枣丁和山药丁都加白糖腌30分钟。\r\n②将大米高火12分钟熬成粥后，加入红枣丁、山药丁，再低火10分钟即可。', NULL, NULL, NULL, '红枣15只，\r\n 山药250克，\r\n 大米100克。  \r\n白糖适量。');
INSERT INTO `recipe` VALUES (5179, 64, '白果粥', '①洗净大米，用少许精盐拌匀。白果去壳切开，去掉果中白心。腐竹浸软切细。\r\n②将清水注入器皿内，用高火煮12分钟至沸后，下大米、白果、腐竹，高火10分钟。\r\n③用纱布包一小包麦片，用绳系好，放入器皿中，高火10分钟，取出麦片即可。', NULL, NULL, NULL, '大米250克，\r\n 白果150克，\r\n 腐竹50克。  精盐半汤匙，\r\n 麦片一小包。');
INSERT INTO `recipe` VALUES (5180, 64, '山莲葡萄粥', '将山药片、莲子肉、葡萄干高火同煮20分钟熬成粥，加白糖即可（也可将上述三物同蒸，蒸烂成泥时加白糖食用）。', NULL, NULL, NULL, '山药片50克，\r\n 莲子肉50克，\r\n 葡萄干50克。  白糖少许。');
INSERT INTO `recipe` VALUES (5181, 64, '胡桃粥', '将胡桃肉与大米一起高火30分钟煮成粥，粥熟后加白糖即可。', NULL, NULL, NULL, '胡桃肉50克，\r\n 大米100克。  白糖少许');
INSERT INTO `recipe` VALUES (5182, 64, '龙眼枸杞粥', '①将优质龙眼肉、枸杞、红枣、大米分别洗净。\r\n②在器皿中加清水、大米，高火8分钟后下龙眼肉、枸杞、红枣，高火20分钟煮成稀粥。', NULL, NULL, NULL, '龙眼肉15克，\r\n 枸杞10个，\r\n 红枣四只，\r\n 大米100克。');
INSERT INTO `recipe` VALUES (5183, 64, '山药桂圆粥', '①将生山药去皮切成薄片。\r\n②将山药片、桂圆肉、荔枝肉（鲜肉更佳）、五味子、水同煮，高火30分钟，煮好后加白糖搅匀即可。', NULL, NULL, NULL, '生山药100克，\r\n 桂圆肉15克，\r\n 荔枝肉三个，\r\n 五味子3克。  白糖、水适量。');
INSERT INTO `recipe` VALUES (5184, 64, '龙眼粥', '用水煮大米，高火12分钟成粥，然后放入龙眼肉煮沸，加白糖即成。', NULL, NULL, NULL, '龙眼肉30克，\r\n 大米50克。  白糖适量');
INSERT INTO `recipe` VALUES (5185, 64, '干荔枝煲粥', '①将大米洗净，浸30分钟。\r\n②干荔枝去壳除核。\r\n③将大米和干荔枝置于一深容器内，加入沸水，加盖高火20分钟，然后取出自热焖调5分钟即可。', NULL, NULL, NULL, '干荔枝10只，\r\n 大米150克。  沸水两杯半。');
INSERT INTO `recipe` VALUES (5186, 64, '百合糖粥', '①将糯米洗净，浸30分钟。\r\n②将百合剥成瓣。\r\n③将糯米、百合放入一深容器内，加水加盖高火10分钟。\r\n④加入红糖拌匀，加盖低火20分钟后取出，自热焖调10分钟即可。', NULL, NULL, NULL, '糯米150克，\r\n 新鲜百合100克。  红糖一大匙，\r\n 沸水两杯半。');
INSERT INTO `recipe` VALUES (5187, 64, '八卦粥', '①用斧头击打活龟两侧硬骨，揭开龟壳，取下底板，将龟肉、内脏理净（龟壳、底板不用）。葱切段，姜拍烂。\r\n②容器中加入猪油、花椒、葱、姜，炸香，放入龟肉和内脏以及核桃仁，淋上香油，高火5分钟后加盐，加水1500克，水沸后加大米，低火30分钟至米烂粥浓即成。\r\n③食用时，可加少许味精、葱花提味。', NULL, NULL, NULL, '活龟一只，\r\n 大米200克，\r\n 核桃仁50克。  猪油50克，\r\n 香油5克，\r\n 葱白15克，\r\n 花椒10粒，\r\n 姜10克，精盐、味精各少许。');
INSERT INTO `recipe` VALUES (5188, 64, '虾球粥', '①将大米洗净，待器皿内的水烧开后连同瑶柱放入，高火20分钟。\r\n②将虾去壳，取出肠泥，然后加两匙白糖、半匙盐，腌拌20分钟，洗净沥干水，用少许湿淀粉、色拉油、酱油、精盐拌匀。\r\n③粥快煮好时放入虾肉再煮开，调好味即成。食用时，可撒上香菜末和葱花。', NULL, NULL, NULL, '鲜虾500克，\r\n 大米250克，\r\n 瑶柱25克。  白糖两匙，\r\n 香菜末3克，\r\n 葱花5克，\r\n 精盐、干淀粉、色拉油、酱油各少许。');
INSERT INTO `recipe` VALUES (5189, 64, '鲤鱼汁粥', '将鱼去鳞，并除内脏洗净，加水高火20分钟，煮至剩下一半水时取出鱼，加入糯米及葱白、豆豉，再高火15分钟即可。', NULL, NULL, NULL, '鲤鱼一条，\r\n 糯米约50克。  葱白、豆豉、水各适量。');
INSERT INTO `recipe` VALUES (5190, 64, '泥鳅鱼茸粥', '①将大米洗净，用精盐稍腌，用器皿将水烧滚后下米，高火12分钟煮成粥。\r\n②剪去泥鳅的刺及鳍，去掉内脏，洗净，沥干水，放在器皿内煎香，随即加入一大汤碗清水，将泥鳅烩熟。\r\n③取出泥鳅拆肉，泥鳅骨放回器皿内熬汤，熬成的泥鳅汤滗入粥锅内同煮。\r\n④泥鳅肉用少许熟色拉油、生抽酱油拌匀，粥快好时下精盐、胡椒粉调味，放入泥鳅肉，再沸滚时即成。食用时撒冲菜粒、香菜末和葱花即可。', NULL, NULL, NULL, '鲜活泥鳅1250克，\r\n 大米300克，\r\n 冲菜（切粒）一片。  香菜末3克，\r\n 葱花5克，\r\n 熟色拉油、生抽酱油各少许，\r\n 精盐、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (5191, 64, '鲤鱼阿胶粥', '用上述原料，高火20分钟，煮成粥后加精盐调味即可。', NULL, NULL, NULL, '鲤鱼500克，\r\n 糯米100克，\r\n 阿胶50克。  葱5克，\r\n 姜3克，\r\n 橘皮、精盐各少许。');
INSERT INTO `recipe` VALUES (5192, 64, '黄花鱼粥', '①将大米洗净，用精盐腌拌，器皿内加水烧滚后下米，高火12分钟，先煮成粥。\r\n②黄花鱼去鳞和内脏洗净，用精盐腌拌，放在器皿内煎至两面焦黄时，加入一汤碗清水，将鱼熬熟取出，拆肉，将鱼骨放回鱼汤内继续熬，熬好鱼汤后去掉鱼骨，将鱼汤倒入粥内加熟色拉油同煮，高火5分钟。\r\n③食用时加姜丝、香菜末、葱粒、酱油即可。', NULL, NULL, NULL, '鲜黄花鱼一条，\r\n 大米300克。  姜丝3克，\r\n 香菜末5克，\r\n 葱粒5克，\r\n 熟色拉油50克，\r\n 精盐、酱油各适量。');
INSERT INTO `recipe` VALUES (5193, 64, '海参粥', '将发好的海参洗净切成小块，与大米、适量水一起高火15分钟煮成粥即可。', NULL, NULL, NULL, '大米50克，\r\n 海参20克。  无');
INSERT INTO `recipe` VALUES (5194, 64, '海鲜粥', '①虾去壳及肠泥，蛤蜊用盐水浸泡吐沙，茭白由内侧切十字刀纹，再切成3×2厘米方片，鱼肉横切成薄片，蒜苗切斜段备用。\r\n②取一器皿，把白饭加上调料(A)放入，加盖高火10分钟；再加入虾、蛤蜊、茭白、鱼肉及调料(B)，加盖再高火3分钟；取出后撒上蒜苗和调料?即可。', NULL, NULL, NULL, '白饭适量，\r\n 虾10只，\r\n 蛤蜊10只，\r\n 茭白半条，\r\n 鱼肉一块。  (A)热水五杯，高汤三杯，蒜苗半根；\r\n (B)精盐一匙，绍酒少许，姜四片；\r\n ?芝麻油少许，芹菜末一大匙。');
INSERT INTO `recipe` VALUES (5195, 64, '虾米芋头粥', '①芋头去皮切滚刀块，虾米用热水泡软。\r\n②在一锅内放入肉糜、虾米和调料(A)，加盖高火3分钟；取出放入芋头，加盖高火10分钟；再加入白饭和调料(B)拌匀，加盖高火10分钟，撒上调料?即可。', NULL, NULL, NULL, '芋头一只，\r\n 肉糜75克，\r\n 虾米三大匙，\r\n 白饭三杯。  (A)油葱酥两大匙，色拉油三大匙；\r\n (B)热水六杯，精盐两匙，白胡椒粉半匙；\r\n ?葱花两大匙。');
INSERT INTO `recipe` VALUES (5196, 64, '鱼粥', '①草鱼擦干水分，切成连刀片（一刀断、一刀不断），拌入调料(A)，腌10分钟备用。\r\n②在一器皿内放入米饭和调料(B)，加盖高火10分钟，放入鱼片及调料?，加盖高火3分钟，取出加入调料(D)即可。', NULL, NULL, NULL, '草鱼（中段）300克，\r\n 米饭三碗。  (A)酱油三大匙，色拉油四大匙；\r\n (B)热水五杯；\r\n ?盐一大匙，姜末一匙；\r\n (D)油条（切小块）一根，芝麻油一匙，米酒一大匙，白胡椒粉少许，葱花两大匙。');
INSERT INTO `recipe` VALUES (5197, 64, '猪红粥', '①将大米洗净，沥干水，用少许精盐腌拌，放入滚开的水中，同时放入腐竹及瑶柱，高火20分钟。\r\n②将猪红切块，放在清水中浸泡。\r\n③粥快煮好时放入猪红，再煮开即成。\r\n④吃时浇上酱油，撒上葱花及胡椒粉调味。另外还可放入油条（切小段）或咸蛋（切碎）一起食用。', NULL, NULL, NULL, '猪红（即凝成块的猪血）1000克，\r\n 大米250克，\r\n 瑶柱15克，\r\n 腐竹100克。  酱油、葱花、胡椒粉各适量。');
INSERT INTO `recipe` VALUES (5198, 64, '肝黄粥', '①将猪肝去筋洗净刮成茸，加入适量绍酒、精盐腌10分钟。\r\n②鸡蛋煮熟除去鸡蛋白，将蛋黄碾成泥，与肝茸同时加入煮烂的大米粥中，加精盐和味精调味，再用低火煮8分钟即可。', NULL, NULL, NULL, '猪肝25克，\r\n 鸡蛋一个，\r\n 大米粥25克。  绍酒、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (5199, 64, '猪腰粥', '将猪腰与米高火20分钟合煮成粥，快熟时加入葱、姜、精盐及五香粉调味。', NULL, NULL, NULL, '猪腰两个（去膜切细），\r\n 粳米50克。  葱白10克，\r\n 五香粉、生姜各少许，\r\n 精盐适量。');
INSERT INTO `recipe` VALUES (5200, 64, '羊肝萝卜粥', '①将羊肝和胡萝卜洗净均切成0.5厘米见方的小丁，肝丁用绍酒、姜汁腌10分钟。\r\n②用器皿爆香蒜茸后倒入肝丁，高火略炒盛起。\r\n③将大米高火20分钟熬成粥后加入胡萝卜，焖15～20分钟，再加入肝丁，并下精盐、味精和葱花调味即可。', NULL, NULL, NULL, '羊肝150克，\r\n 胡萝卜100克，\r\n 大米100克。  蒜数瓣，\r\n 色拉油50克，\r\n 绍酒5克，\r\n 葱5克，\r\n 姜5克，\r\n 精盐5克，\r\n 味精少量。');
INSERT INTO `recipe` VALUES (5201, 64, '猪肝粥', '①猪肝切片，用热水略烫，去血水备用。\r\n②在一器皿内放入白饭和热开水，加盖高火8分钟取出，再放入猪肝、姜丝及调料(A)，加盖高火5分钟即可。', NULL, NULL, NULL, '白饭三碗，\r\n 猪肝300克。  热开水适量，\r\n 嫩姜丝三大匙。\r\n (A)精盐一匙，\r\n 绍酒两匙。');
INSERT INTO `recipe` VALUES (5202, 64, '羊骨大枣粥', '将羊骨（最好是腿骨）洗净敲成两段，入器皿加水，高火20分钟后捞起羊骨，剔出骨髓放在汤中，加入米和大枣，高火15分钟熬成粥。', NULL, NULL, NULL, '羊骨300克，\r\n 大米200克，\r\n 大枣15只。  无');
INSERT INTO `recipe` VALUES (5203, 64, '羊骨粥', '①取新鲜羊骨，洗净敲碎，加水煎汤，高火约20分钟。\r\n②取汤代水，下米煮粥。高火10分钟，待粥快好时，加入葱白、生姜、细盐，稍煮即可。', NULL, NULL, NULL, '羊骨1000克左右，\r\n 粳米或糯米100克。  葱白两根，\r\n 生姜三片，\r\n 细盐少许。');
INSERT INTO `recipe` VALUES (5204, 64, '鸡汁粥', '①将母鸡剖洗干净，煎鸡汁。黄芪煎汁。\r\n②器皿中放入粳米，加入鸡汁、黄芪汁，高火12分钟即可。', NULL, NULL, NULL, '母鸡一只，\r\n 黄芪15克，\r\n 粳米100克。  无');
INSERT INTO `recipe` VALUES (5205, 64, '金笋牛肉粥', '①将牛肉糜加上绍酒、精盐腌渍5分钟。胡萝卜蒸熟后除去皮碾成泥。\r\n②将牛肉糜、胡萝卜泥同时拌入大米粥，加精盐、味精，调味后再高火15分钟，淋数滴香油即可。', NULL, NULL, NULL, '胡萝卜20克，\r\n 牛肉糜30克，\r\n 大米粥25克。  绍酒、香油、精盐、味精各适量。');
INSERT INTO `recipe` VALUES (5206, 64, '生滚牛肉粥', '①洗净白米，用精盐稍加腌拌。器皿中加水，烧开时下米和陈皮。\r\n②将牛肉洗净切成薄片，用半茶匙苏打粉及适量精盐、白糖、干淀粉、酱油和少许清水调开，拌匀腌牛肉。\r\n③高火20分钟后，粥快煮好时放入牛肉片搅散，高火5分钟，再滚起。\r\n④上桌时，加熟色拉油、炸香的米粉、冲菜粒、香菜、葱花、姜丝等即可。', NULL, NULL, NULL, '牛肉500克，\r\n 白米300克，\r\n 干米粉100克，\r\n 冲菜两片（切粒）。  精盐5克，\r\n 白糖10克，\r\n 酱油15克，\r\n 干淀粉少许，\r\n 苏打粉半茶匙，\r\n 香菜、葱、姜各适量，\r\n 陈皮若干，\r\n 熟色拉油少许。');
INSERT INTO `recipe` VALUES (5207, 64, '牛肉滑蛋粥', '①香菇去蒂泡软切丝，牛肉用蛋清、精盐一小匙、味精一小匙腌20分钟，蛋黄两个放置碗中。\r\n②色拉油一大匙，高火2分钟，放入腌好的牛肉丝及香菇丝，高火3分钟。\r\n③把精盐一小匙、味精一小匙、酱油一小匙、热高汤加入，高火5分钟至沸，加入饭、牛肉丝和香菇，高火10分钟后放入蛋黄两个，再高火1分钟，撒上胡椒粉、芹菜末即可。', NULL, NULL, NULL, '牛肉丝150克，\r\n 白饭一碗，\r\n 香菇两朵。  精盐两小匙，\r\n 味精两小匙，\r\n 酱油一小匙，\r\n 热高汤两大杯，\r\n 胡椒粉一小匙，\r\n 芹菜末一大匙，\r\n 蛋清少许，\r\n 蛋黄两个，\r\n 色拉油一大匙。');
INSERT INTO `recipe` VALUES (5208, 64, '干贝瘦肉粥', '①将干贝洗净，用水浸1小时，取起撕成丝，浸干贝的水留用。\r\n②将猪肉洗净抹干，切成粗粒，加干淀粉拌匀，再加色拉油拌匀。\r\n③把两种米一起洗净，放入器皿内，再将干贝、猪肉、姜放入器皿内，加入三杯热水搅匀（包括浸干贝之水），加盖高火10分钟。煲盖要有小孔，以免粥水溢出。\r\n④改用低火15分钟，加入适量精盐调味即可。', NULL, NULL, NULL, '糯米两大匙，\r\n 大米两大匙，\r\n 干贝两粒，\r\n 瘦生净猪肉100克。  姜一片，\r\n 热水三杯，\r\n 干淀粉两中匙，\r\n 色拉油半大匙，\r\n 精盐适量。');
INSERT INTO `recipe` VALUES (5209, 64, '鹌鹑粥', '①将大米洗净后浸30分钟。\r\n②鹌鹑去毛和内脏，洗净切成块，用绍酒腌渍10分钟。\r\n③将鹌鹑和大米置于一深容器内，加入沸水，加盖高火10分钟。\r\n④加入精盐和芝麻油搅拌，再加盖低火10分钟，取出后自热焖调10分钟即可。', NULL, NULL, NULL, '鹌鹑一只，\r\n 大米150克。  绍酒一中匙，\r\n 芝麻油一中匙，\r\n 精盐半小匙，\r\n 沸水两杯半。');
INSERT INTO `recipe` VALUES (5210, 64, '广东粥', '①将猪肝切片，用热水烫去血水备用。\r\n②在一器皿内放入白饭和热开水，加盖高火8分钟，取出后再加猪肝、生净猪肉丝、虾仁及调料(A)，加盖高火3分钟，取出撒上葱花及油条块即可。', NULL, NULL, NULL, '白饭三杯，\r\n 油条（切小块）一根，\r\n 猪肝150克，\r\n 生净猪肉丝100克，\r\n 虾仁150克。  热开水六杯，\r\n 葱花一大匙；\r\n (A)精盐两匙，白胡椒粉半匙。');
INSERT INTO `recipe` VALUES (5211, 64, '蒜茸火腿粉', '①将细条实心面放入沸水中，加盖高火3分钟，取出焖5分钟，沥去水分，用少许黄油拌匀。\r\n②色拉油放入盘内，高火2分钟后将蒜泥、黑胡椒粉倒入稍爆，然后加入细条实心面、碎香菜、火腿末、黄油、精盐及胡椒粉拌匀，加盖高火3分钟，中途搅拌一次即可。', NULL, NULL, NULL, '细条实心面200克，\r\n 金华火腿末两大匙。  蒜泥两大匙，\r\n 黑胡椒粉一中匙，\r\n 黄油30克，\r\n 色拉油两大匙，\r\n 沸水五杯，\r\n 精盐一小匙，\r\n 胡椒粉半小匙，\r\n 碎香菜两大匙。');
INSERT INTO `recipe` VALUES (5212, 64, '熏肉蛋通粉', '①将通粉放入沸水中，加盐一小匙，低火3分钟，取出焖5分钟，沥去水分。\r\n②油加入盘内，高火2分钟，加入熏猪肉、洋葱爆至金黄色，然后倒入通粉拌匀，再加入干酪粉、茄汁、红椒粉拌匀，高火2分钟。\r\n③将蛋汁、精盐半小匙及胡椒粉加入通粉内拌匀，中火1分钟即可。', NULL, NULL, NULL, '通粉200克，\r\n 熏猪肉四条（切片），\r\n 洋葱一个（切碎），\r\n 蛋四只（打碎）。  茄汁一大匙，\r\n 干酪粉两大匙，\r\n 红椒粉半小匙，\r\n 精盐一小匙半，\r\n 胡椒粉半小匙，\r\n 沸水五杯，\r\n 色拉油两大匙。');
INSERT INTO `recipe` VALUES (5213, 64, '肉丝米粉', '①将米粉用温水浸软，松散后盛筲箕内沥干水（不可用滚水浸粉，以免太软）。\r\n②虾米用清水浸20分钟，捞起沥干水。\r\n③冬菇浸软切丝，红辣椒去籽切丝。洋葱、葱切丝。\r\n④米粉加入调料(A)、洋葱、红椒、葱丝拌匀。\r\n⑤瘦生净猪肉洗净沥干，切丝后加调料(B)搅匀，再加入冬菇、虾米、咖喱粉拌匀，加盖高火3分钟，取出倒入米粉内搅匀，再加盖高火3分钟，盛于盘上，用筷子兜匀，再加盖高火3分钟即可。', NULL, NULL, NULL, '米粉150克，\r\n 冬菇五朵，\r\n 洋葱半个，\r\n 虾米20克，\r\n 瘦生净猪肉75克。  咖喱粉一中匙，\r\n 红辣椒一个，\r\n 葱两根；\r\n (A)酱油一大匙，精盐半小匙，水一大匙，芝麻油半小匙，胡椒粉1/3小匙；\r\n (B)干淀粉一小匙，水一大匙，色拉油一大匙。');
INSERT INTO `recipe` VALUES (5214, 64, '肉酱面', '①将细条实心面放入沸水中，加盖高火3分钟，取出搁置5分钟，沥去水分。\r\n②色拉油倒入盘内，高火2分钟，放入洋葱、芹菜、胡萝卜、蒜泥爆香，再加入粉酱、牛肉、绍酒、香菜、黄油、精盐及胡椒粉拌匀，高火1分钟，然后倒在面上，高火1分钟即可。', NULL, NULL, NULL, '细条实心面200克，\r\n 牛肉糜400克，\r\n 洋葱粒1/3杯，\r\n 芹菜粒1/2杯，\r\n 胡萝卜粒1/2杯。  蒜泥两中匙，\r\n 粉酱一杯，\r\n 绍酒一大匙，\r\n 香菜半小匙，\r\n 黄油60克，\r\n 色拉油三大匙，\r\n 沸水五杯，\r\n 精盐一小匙，\r\n 胡椒粉一小匙。');
INSERT INTO `recipe` VALUES (5215, 64, '法国通心面', '①把通心面和热水放入一器皿内，加盖高火6分钟，捞出沥干，拌色拉油一大匙。\r\n②金华火腿切丝，洋菇切片，洋葱、红萝卜去皮切丝，小黄瓜洗净切丝。\r\n③在一器皿内放入三大匙色拉油和金华火腿、洋葱、红萝卜及洋菇，高火爆香5分钟后取出，加入通心面、青豆、小黄瓜丝和调料(A)拌匀，加盖高火3分钟即可。', NULL, NULL, NULL, '通心面半包，\r\n 金华火腿110克，\r\n 洋葱半只，\r\n 红萝卜110克，\r\n 洋菇115克，\r\n 小黄瓜一根，\r\n 青豆（冷冻的）75克。  色拉油四大匙，\r\n 热水六杯；\r\n (A)精盐一匙，鸡精半匙，白胡椒粉少量，番茄酱四大匙，高汤半杯。');
INSERT INTO `recipe` VALUES (5216, 64, '和尚面', '①取汤碗一只，倒入沸水500克，将切面投入，高火5分钟。\r\n②将面放入碗中，再放入调料即可。', NULL, NULL, NULL, '切面150克。  葱花一中匙，\r\n 精盐半小匙，\r\n 味精半小匙，\r\n 熟色拉油一中匙。');
INSERT INTO `recipe` VALUES (5217, 64, '麻酱面', '①取一汤碗，倒入沸水，将切面投入，高火5分钟。\r\n②将面放入有调料的碗中拌和即可。', NULL, NULL, NULL, '切面150克。  麻酱一大匙，\r\n 葱花一中匙，\r\n 精盐半小匙，\r\n 味精半小匙，\r\n 熟色拉油一中匙，\r\n 沸水500克。');
INSERT INTO `recipe` VALUES (5218, 64, '龙须面', '①将墨鱼、蛋、草虾、蛤蜊、香菇、鱼片、豌豆荚放入一碗中，倒入1/4调料(A)，加盖中高火4分钟。\r\n②将3/4调料(A)放入大汤碗中，加盖高火4分钟至沸。\r\n③将面放入汤中，把其他材料整齐地排列于面上，汤汁一并倒入，加盖中高火2分钟。\r\n④撒上葱花、辣椒粉即可。', NULL, NULL, NULL, '龙须面150克，\r\n 墨鱼两小片，\r\n 蛋一个，\r\n 草虾一只，\r\n 蛤蜊一个，\r\n 香菇一朵，\r\n 鱼片两片，\r\n 豌豆荚三片。  葱花一大匙，\r\n 辣椒粉半小匙；\r\n (A)热鱼汤两杯，精盐一小匙，姜酒汁一小匙，糖一小匙。');
INSERT INTO `recipe` VALUES (5219, 64, '三鲜面', '①虾仁去肠泥，墨鱼切花，海参斜切片，胡萝卜、竹笋切花片。加三大匙色拉油，高火炒3分钟。\r\n②中等器皿中加入高汤四杯及调料(A)，用高火煮至沸滚，再加入油面条及炒过的配料，中火煮3分钟，煮好后上面撒少许香菜即可。', NULL, NULL, NULL, '油面条250克，\r\n 虾仁150克，\r\n 墨鱼200克，\r\n 海参100克，\r\n 胡萝卜100克，\r\n 竹笋半根。  色拉油三大匙，\r\n 高汤四杯，\r\n 香菜少许；\r\n (A)精盐两匙，味精一匙，白酱油一大匙。');
INSERT INTO `recipe` VALUES (5220, 64, '什菜面', '①将切面放入沸水中，高火1分钟，取出焖调5分钟，沥去水。\r\n②将色拉油倒入盘内，高火4分钟，加入蒜泥、各色蔬菜、金华火腿、香肠、香菜、切面、精盐及胡椒粉拌匀，高火2分钟。中途搅拌一次。', NULL, NULL, NULL, '切面100克，\r\n 各色蔬菜一杯，\r\n 香肠两条（切片），\r\n 金华火腿末一大匙。  蒜泥一中匙，\r\n 沸水四杯，\r\n 精盐、胡椒粉各少许，\r\n 色拉油三大匙，\r\n 碎香菜一大匙。');
INSERT INTO `recipe` VALUES (5221, 64, '海鲜宽条面', '①宽条面放入沸水中，高火2分钟，5分钟后沥去水分。\r\n②黄油放入较深盘内，加盖高火5分钟后，放入扇贝肉、虾、奶及奶白汁拌匀。再高火5分钟，取出后加入精盐、胡椒粉。\r\n③将扇贝混合物淋在宽条面上，加盖高火2分钟即可。', NULL, NULL, NULL, '宽条面250克，\r\n 扇贝肉100克，\r\n 中虾250克，\r\n 奶一杯，\r\n 奶白汁一杯。  精盐一小匙，\r\n 胡椒粉少许，\r\n 黄油30克，\r\n 沸水五杯。');
INSERT INTO `recipe` VALUES (5222, 64, '鸡汤面条', '把熟鸡肉切成块，放入鸡汤，中火8分钟。食用前把面条煮熟，加清汤调好味，低火2分钟，每碗放上一块鸡肉，盛上即可。', NULL, NULL, NULL, '熟鸡肉500克，\r\n 鸡清汤2500克，\r\n 细切面250克。  精盐20克。');
INSERT INTO `recipe` VALUES (5223, 64, '炒面', '①将猪肉切成丝和调料(A)拌匀，腌10分钟备用。\r\n②香菇泡软切丝，鲜菇洗净切丝，豆芽洗净去头尾，韭黄洗净切段备用。\r\n③将腌好的肉丝与调料(B)拌匀，加盖高火5分钟；再加入面条和调料?拌匀，加盖高火5分钟；最后撒上胡椒粉即可。', NULL, NULL, NULL, '切面300克，\r\n 猪肉75克。  胡椒粉少许；\r\n (A)酱油一汤勺，淀粉一茶勺，色拉油一茶勺；\r\n (B)香菇三朵，鲜菇两朵；\r\n ?酱油两汤勺，白糖一茶勺，豆芽75克，韭黄50克，水半杯。');
INSERT INTO `recipe` VALUES (5224, 64, '黄油粟米', '①将玉米洗净，放入盘内，加水加盖高火7分钟取出。\r\n②玉米去衣（在玉米头尾各插上一枝竹签，可免烫手），搽上黄油即可。', NULL, NULL, NULL, '新鲜玉米两根（包有外衣）。  黄油适量，\r\n 清水半杯。');
INSERT INTO `recipe` VALUES (5225, 64, '珍珠米饭', '①淘净米，放于较深容器内，加入规定水量，然后浸泡1小时。\r\n②加盖高火7分钟，再加盖中低火13分钟即可。\r\n③煮好后，用饭勺轻轻扒松，盖上厨房用干布巾，然后，加盖让其自热焖调10分钟即可。', NULL, NULL, NULL, '大米约180克。  水一杯半。');
INSERT INTO `recipe` VALUES (5226, 64, '咸味饭', '将材料与调料拌合均匀后加入容器内，加盖高火12分钟。之后在室温下自热焖调3～4分钟。食用时，若用饭勺扒松，则味更美。', NULL, NULL, NULL, '热水一杯半，\r\n 大米200克。  黄油一大匙，\r\n 精盐一中匙。');
INSERT INTO `recipe` VALUES (5227, 64, '釜竹饭', '①将大米洗净，浸泡10分钟。\r\n②桂鱼去刺、皮，切成小丁。\r\n③将米沥干水，加上鱼丁、色拉油及水，加盖高火10分钟即可。', NULL, NULL, NULL, '大米一杯，\r\n 咸桂鱼60克。  色拉油一小匙，\r\n 水1.2杯。');
INSERT INTO `recipe` VALUES (5228, 64, '家常炒饭', '①将里脊肉切丝拌入调料(A)；将洋葱、冬菇、笋、胡萝卜切丝，青豆洗净和肉丝拌在一起，加入调料(B)及色拉油拌匀后，以高火加热8分钟至沸腾；再加入调匀的调料?拌匀，以高火加热2分钟取出，成为饭的浇头。\r\n②将一小盘抹色拉油后打入一个鸡蛋，刺破蛋黄，以高火煎1分钟做成荷包蛋，并依次将六个鸡蛋做完.\r\n③将浇头淋在六碗饭上拌好，再各放一个荷包蛋即成。', NULL, NULL, NULL, '里脊肉200克，\r\n 青豆150克，\r\n 洋葱一个，\r\n 冬菇六朵，\r\n 笋一根，\r\n 胡萝卜半条，\r\n 鸡蛋六个，\r\n 热饭六碗。  色拉油半碗；\r\n (A)酱油半汤勺，绍酒半茶勺，热水一汤勺，淀粉一茶勺；\r\n (B)热水三杯，白糖一茶勺，精盐一茶勺，胡椒粉少许，黑醋一汤勺，味精一茶勺；\r\n ?淀粉三汤勺，水三汤勺。');
INSERT INTO `recipe` VALUES (5229, 64, '香菇炒饭', '①将香菇去蒂泡软后切丁，洋葱、金华火腿、猪肉切丁，鸡蛋打散。\r\n②猪油放入盘内高火2分钟，再放入香菇、洋葱、青豆、金华火腿、猪肉高火2分钟。\r\n③加入番茄酱、酱油、味精、精盐拌匀，高火2分钟。\r\n④将白饭、蛋拌匀后再加进盘内，高火4分钟后取出，撒上胡椒粉即可。', NULL, NULL, NULL, '白饭100克，\r\n 香菇两朵，\r\n 洋葱1/4个，\r\n 青豆100克，\r\n 金华火腿两片，\r\n 鸡蛋一个，\r\n 猪肉100克。  番茄酱一中匙，\r\n 酱油一中匙，\r\n 味精、精盐各一小匙，\r\n 猪油两大匙，\r\n 胡椒粉少许。');
INSERT INTO `recipe` VALUES (5230, 64, '紫菜饭卷', '①将热白饭趁热与调料(A)混匀晾凉。\r\n②紫菜放盘中，以高火烤1分钟备用。\r\n③蛋打散，烤盘以高火预热3分钟，加色拉油，倒入蛋液，以中高火1分30秒使之呈蛋皮状，即可取出，切成1厘米宽的长条；小黄瓜抹盐，腌10分钟，直切成六长条；腌瓢瓜切成1厘米宽的长条。\r\n④白饭铺在紫菜上，将蛋条、黄瓜条、瓢瓜条和红甜菜丝放中间，卷成圆筒，切成1厘米宽的厚片，装盘即可。', NULL, NULL, NULL, '热白饭三杯，\r\n 紫菜三张，\r\n 鸡蛋两个，\r\n 腌瓢瓜一大块，\r\n 红甜菜丝110克，\r\n 小黄瓜一根。  精盐一茶勺，\r\n 色拉油一汤勺；\r\n (A)白醋三汤勺，白糖四汤勺，绍酒一汤勺。');
INSERT INTO `recipe` VALUES (5231, 64, '青菜饭', '①将青菜洗净，沥干水，切成1.5厘米长段。\r\n②青菜放入盘内，加色拉油一大匙，加盖高火4分钟。\r\n③米洗净，放入器皿内，加入盐、高汤，加盖高火10分钟。再加入青菜及色拉油两大匙拌匀，加盖中火5分钟取出，自热焖调3分钟即可。', NULL, NULL, NULL, '大米一杯，\r\n 青菜300克。  色拉油三大匙，\r\n 精盐一小匙，\r\n 高汤一杯半。');
INSERT INTO `recipe` VALUES (5232, 64, '章鱼干贝饭', '①将干贝用水浸1小时，撕碎，浸干贝之水留用。\r\n②章鱼用清水浸软（约浸40分钟），洗净后切成小粒，葱切碎。\r\n③将章鱼、干贝与姜汁拌匀。\r\n④将米洗净，放入器皿内，加入干贝、章鱼搅匀，再加入浸干贝之水，加盖高火15分钟。取出翻拌一下，再加盖中火2分钟，取出待5分钟。\r\n⑤调料(A)盛在盛器中，加盖高火30秒钟。\r\n⑥把煲盖掀开，淋上调料及葱，拌匀即可。', NULL, NULL, NULL, '大米一杯，\r\n 干贝15克，\r\n 章鱼50克。  姜汁半大匙，\r\n 葱两根，\r\n 水一杯半；\r\n (A)淡酱油一大匙半，黑酱油一大匙半，白糖一中匙，色拉油两大匙。');
INSERT INTO `recipe` VALUES (5233, 64, '炒油饭', '①将糯米洗净，用清水浸泡2小时后滤水，再加约一杯水，覆上微波薄膜，加上盖。\r\n②香菇、猪肉切丝，与虾米、油葱和调料(B)拌合，覆上微波薄膜，与糯米一起放入炉内，高火11分钟取出。\r\n③糯米饭静置3分钟。\r\n④将煮熟的所有材料及调料(A)拌入熟糯米中，加盖，再用高火煮透3分钟即可。', NULL, NULL, NULL, '糯米一杯，\r\n 香菇三朵，\r\n 生净猪肉75克，\r\n 虾米两大匙，\r\n 油葱三大匙。  (A)酱油两大匙，味精两匙；\r\n (B)猪油三大匙。');
INSERT INTO `recipe` VALUES (5234, 64, '凤梨饭', '①先将凤梨对半切开，挖出果肉，切丁。\r\n②把金华火腿片切丁，洋葱切丁，与豌豆仁、果肉丁、精盐、味精、酱油、葱末、胡椒粉、色拉油拌匀入炉高火2分钟，取出与饭拌匀，放入凤梨内再高火2分钟即可。', NULL, NULL, NULL, '凤梨（即菠萝）一个，\r\n 饭150克，\r\n 金华火腿两片，\r\n 豌豆仁100克，\r\n 洋葱半个。  精盐、味精各一小匙，\r\n 酱油一小匙，\r\n 葱末一中匙，\r\n 胡椒粉一小匙，\r\n 色拉油一大匙。');
INSERT INTO `recipe` VALUES (5235, 64, '西班牙炒饭', '①将米和水置于器皿中，以高火煮12分钟后，再以低火焖煮3分钟。\r\n②将其余的材料分别切好置于另一器皿中，放入色拉油，并加盖高火5分钟。\r\n③饭煮熟后，加入所有的材料和调料(A)，拌匀加盖，以高火3分钟即可。', NULL, NULL, NULL, '米三杯，\r\n 里脊肉110克，\r\n 红番茄两只，\r\n 甜红椒一只，\r\n 洋葱一只。  水四杯，\r\n 色拉油三大匙；\r\n (A)精盐一匙半，白胡椒粉少量。');
INSERT INTO `recipe` VALUES (5236, 64, '意大利香饭', '①将白蘑菇、洋葱切片，胡萝卜、黄瓜切粒，番茄去核切粒。\r\n②黄油50克放入饭煲内，高火6分钟后加入所有什菜拌匀，加盖高火2分钟。\r\n③再加入米、鸡汤、茄汁、红椒粉拌匀，高火16分钟。中途搅拌一次。\r\n④取出5分钟后，加入干酪粉、精盐、胡椒粉及黄油50克拌匀即可。', NULL, NULL, NULL, '白蘑菇1/3杯，\r\n 洋葱1/3杯，\r\n 胡萝卜1/3杯，\r\n 番茄1/3杯，\r\n 黄瓜1/3杯，\r\n 米两杯。  黄油100克，\r\n 鸡汤两杯半，\r\n 茄汁一大匙，\r\n 红椒粉一中匙，\r\n 干酪粉一大匙，\r\n 精盐、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (5237, 64, '糯米粽子饭', '①淘净糯米，浸泡1小时。捞起沥干后，搁置30分钟。\r\n②用水将香菇泡软后，切成1厘米见方的丁块，浸泡香菇用的水留用。\r\n③用水将干虾仁泡软。\r\n④将大葱切碎。\r\n⑤将糯米、其他原料和调料盛入盘内搅拌，加盖高火15分钟，中途搅拌一次。烹饪中，观察色味生熟程度。\r\n⑥炒拌后分作8等份，用微波薄膜分别包裹，并捏成三角形。\r\n⑦将三角饭团排于浅盘上，高火加热8分钟。\r\n⑧自热蒸焖10分钟即可。', NULL, NULL, NULL, '糯米两杯，\r\n 香菇两片，\r\n 干虾仁两大匙，\r\n 大葱20厘米，\r\n 色拉油一大匙，\r\n 芝麻油一大匙，\r\n 猪肉糜120克。  绍酒两大匙，\r\n 酱油五小匙，\r\n 精盐少量，\r\n 白糖少量，\r\n 胡椒粉少量，\r\n 浸泡香菇的水加高汤一杯半，\r\n 大葱10厘米。');
INSERT INTO `recipe` VALUES (5238, 64, '虾仁炒饭', '①将洋葱切碎，放入大的容器中，放入一大匙淡炼乳，高火1分钟。\r\n②把白饭、虾仁、蘑菇、绿豌豆、淡炼乳一大匙加入上述的容器中搅匀，高火4分钟，然后用精盐、胡椒粉调味即可。', NULL, NULL, NULL, '白饭300克，\r\n 洋葱20克，\r\n 虾仁40克，\r\n 蘑菇20克，\r\n 绿豌豆10克。  淡炼乳两大匙，\r\n 精盐、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (5239, 64, '田鸡炒饭', '①将田鸡去皮和内脏，洗净后一切两，用绍酒、精盐和花生油拌匀。\r\n②将米洗净，放入深盘内，加水加盖高火6分钟后将田鸡、葱白放入，再加盖低火12分钟。\r\n③取出自热焖调5分钟后食用。', NULL, NULL, NULL, '田鸡两只，\r\n 大米150克。  绍酒一中匙，\r\n 精盐半小匙，\r\n 葱白两支，\r\n 花生油一大匙，\r\n 沸水一杯半。');
INSERT INTO `recipe` VALUES (5240, 64, '什锦炒饭', '①蘑菇、金华火腿切丁，香菇泡发切丁备用。\r\n②材料(B)与调料拌匀后，覆上微波薄膜，高火5分钟。\r\n③去薄膜加入白饭拌匀，再用高火炒6分钟即可。', NULL, NULL, NULL, '(A)白饭六碗；\r\n (B)金华火腿100克，蘑菇适量，豌豆适量，香菇3～4朵，虾仁100克，猪油五大匙。  酱油一大匙，\r\n 精盐两匙，\r\n 味精半匙，\r\n 黑胡椒粉半匙。');
INSERT INTO `recipe` VALUES (5241, 64, '南海鸡饭', '①把调料(A)搅匀，盛入瓶内，浸6小时便可。\r\n②鸡洗净，抹干水，用一中匙精盐搽匀鸡肚，姜、葱放入鸡肚内。\r\n③鸡放入深盘内，鸡胸向上，加盖高火15分钟，再改用中火5分钟，取出待冷。搽色拉油半大匙于鸡皮上，斩成块排盘，鸡蘸调料汁吃。\r\n④大米洗净，放入盛器中，加入水（包括蒸鸡的汤）及精盐1/3中匙。\r\n⑤色拉油两大匙与蒜泥放入盘内，高火1分钟，取出倒入米内搅匀。将盛米的器皿加盖，高火15分钟，取出加入椰汁，再加盖中火5分钟。自热焖调3分钟即可。可配合鸡同吃。', NULL, NULL, NULL, '光鸡1200克，\r\n 大米一杯。  姜两片，\r\n 葱两根，\r\n 蒜泥一中匙，\r\n 椰汁1/4杯，\r\n 精盐约一中匙半，\r\n 色拉油两大匙半，\r\n 水一杯半；\r\n (A)红辣椒粒一大匙，蒜泥一大匙，姜末一大匙，鲜柠檬汁半大匙，精盐一小匙，白糖半大匙，白醋1/3杯，冷开水2/3杯。');
INSERT INTO `recipe` VALUES (5242, 64, '豆沙芋泥', '①将芋头去皮洗净，切成1厘米厚薄片，放入耐热塑料袋中，加水三大匙，以高火蒸12分钟，取出趁热压成泥，并去除硬块。\r\n②将碎冰糖加热开水，中火煮6分钟，至冰糖完全溶化。\r\n③将白芝麻倒入小碗内，高火炒5分钟，待冷却后压碎备用。\r\n④将糖浆及七大匙色拉油倒入芋泥中拌匀备用。\r\n⑤取一中碗，抹上一大匙色拉油，先将一半芋泥放入，铺上豆沙，再覆上一半芋泥，高火2分钟后倒扣在圆盘上，再撒上芝麻即可。', NULL, NULL, NULL, '芋头750克，\r\n 豆沙200克，\r\n 白芝麻一大匙。  色拉油八大匙，\r\n 碎冰糖150克，\r\n 热开水半杯，\r\n 水三大匙。');
INSERT INTO `recipe` VALUES (5243, 64, '八宝芋泥', '①将芋头削皮切片，水洗后用微波薄膜包起（勿滴干），以高火加热8分钟至筷子能穿透，取出后趁热用刀背压成泥状，加入调料(A)拌匀。\r\n②取一中碗，内侧抹上一层色拉油，然后将装饰用的蜜饯排成圆形，填入一半芋泥，将豆沙放在其中央，再用剩余之芋泥填满压平，覆上微波薄膜，高火加热3分钟，取出倒扣在盘子上。\r\n③将调料(B)放入容器中，高火加热2.5分钟，加入调料?拌匀，继续加热20秒取出，趁热淋在芋泥上即可。', NULL, NULL, NULL, '芋头500克，\r\n 豆沙100克，\r\n 装饰用蜜饯（糖莲子、红绿丝、葡萄干、青梅等）适量。  (A)白糖两匙，色拉油两匙；\r\n (B)白糖三匙，热水一杯；\r\n ?干淀粉、水各半匙。');
INSERT INTO `recipe` VALUES (5244, 64, '起士条', '①将面皮●平，涂上白脱淡炼乳。\r\n②将鲜起士片、红椒粉覆盖于半面面皮上。\r\n③将铺好的面皮对折压平。\r\n④将生料切成条形，卷成螺丝状置于盘内，放进微波炉烤箱以中火烤约20分钟。\r\n⑤待冷却后，把起士条对切即成。', NULL, NULL, NULL, '面皮一块，\r\n 鲜起士七片，\r\n 白脱淡炼乳适量。  红椒粉少许。');
INSERT INTO `recipe` VALUES (5245, 64, '牛油酥饼', '①把面粉、干淀粉拌匀。\r\n②把黄油与冰糖拌匀，再加入柠檬肉、柠檬汁拌匀，倒入拌匀的面粉、干淀粉内，搓成粉团，放入烤盆内压平，用叉刺孔，放入冰箱冷冻。\r\n③冷冻后取出，用中火烤4分钟，取出切成八块（过饼中心切）。\r\n④将草莓放在每块饼上，草莓四周镶上鲜淡炼乳即可。', NULL, NULL, NULL, '面粉120克，\r\n 干淀粉50克，\r\n 柠檬肉一中匙，\r\n 草莓250克，\r\n 鲜淡炼乳225克。  冰糖50克，\r\n 黄油100克，\r\n 柠檬汁两中匙。');
INSERT INTO `recipe` VALUES (5246, 64, '炸土豆饼', '①将马铃薯去皮切成薄片，泡水10分钟取出沥干，装入袋中，袋口不要结得太紧，高火15分钟，取出加上调料(A)，趁热直接揉成泥，取出置于大碗中。\r\n②蛋打散置于碗中。洋葱切碎丁放于器皿中，加入牛肉糜、三大匙色拉油，拌匀后以高火爆香3分钟，取出加入调料(B)和马铃薯泥拌匀，作六等份，逐个做成圆饼状。\r\n③肉饼依次蘸上面粉、蛋液、面包粉后置于盘上，在烤盘中加色拉油四大匙，用高火预热2分钟，再将肉饼全部放入，用高火炸2分钟，翻面再炸2分钟即可。', NULL, NULL, NULL, '马铃薯500克，\r\n 牛肉糜100克，\r\n 洋葱150克，\r\n 面粉半杯，\r\n 鸡蛋两个，\r\n 面包粉半杯。  色拉油七大匙；\r\n (A)精盐两匙，白胡椒粉少量；\r\n (B)盐一匙，白胡椒粉少量。');
INSERT INTO `recipe` VALUES (5247, 64, '香酥薄饼', '①将鸡蛋打散至起泡沫，用小苏打、香精、猪油、白糖拌和。\r\n②将拌和的蛋液与面粉揉和，捏成油酥面团后制成薄饼数只。\r\n③将油以高火4～5分钟加热，投放薄饼炸至金黄色即可。', NULL, NULL, NULL, '鸡蛋两个，\r\n 面粉300克，\r\n 小苏打5克。  白糖150克，\r\n 色拉油300克，\r\n 猪油80克，\r\n 香精少许。');
INSERT INTO `recipe` VALUES (5248, 64, '咖啡蛋糕', '①将鸡蛋清与蛋黄分开。\r\n②将泡打粉、色拉油徐徐注入蛋黄内混合。\r\n③将咖啡和奶水先调匀，倒入蛋黄内。\r\n④将面粉筛入蛋黄内。\r\n⑤将鸡蛋清加白糖打发，再倒入蛋黄搅拌均匀。\r\n⑥将调理好的生料倒在模型内，放进微波炉烤箱用中火烤约25分钟。\r\n⑦将蛋糕从模型中取出，对切成两份，涂上鲜淡炼乳，上面挤上花样即可。', NULL, NULL, NULL, '鸡蛋800克，\r\n 泡打粉少许，\r\n 色拉油2/3杯，\r\n 奶水一杯，\r\n 鲜淡炼乳适量，\r\n 面粉320克。  白糖360克，\r\n 咖啡两匙。');
INSERT INTO `recipe` VALUES (5249, 64, '水果卡士达', '①将白糖、白脱淡炼乳、面粉、干淀粉、蛋混合搅拌均匀。\r\n②将冷水和鲜奶高火5分钟煮沸，倒入混合好的生料，高火1分钟，再煮至沸腾，待冷却后备用。\r\n③完全冷却后的材料置于淡炼乳袋，再挤入脆皮模内，表面摆饰各种不同的水果，于冰箱内冷藏即可。', NULL, NULL, NULL, '鲜奶半杯，\r\n 面粉50克，\r\n 干淀粉50克，\r\n 鸡蛋两个，\r\n 白脱淡炼乳200克。  冷水一碗，\r\n 白糖400克。');
INSERT INTO `recipe` VALUES (5250, 64, '核桃松饼', '①将燕麦粉、面粉、发酵粉、精盐一起拌匀，再加入白糖拌匀。\r\n②将鸡蛋打匀，与鲜奶一起加入面粉内拌匀，再加入色拉油、核桃肉拌匀。\r\n③把蛋糕纸杯放在烤盘内，将面粉混合物倒入每个纸杯内（约两大匙），用中火加热3分钟即可。', NULL, NULL, NULL, '燕麦粉半杯，\r\n 面粉1/3杯，\r\n 核桃肉50克（切粒），\r\n 鸡蛋一个，\r\n 鲜奶1/3杯，\r\n 蛋糕纸杯数个。  白糖1/3杯，\r\n 发酵粉一大匙，\r\n 色拉油25克，\r\n 精盐一小匙。');
INSERT INTO `recipe` VALUES (5251, 64, '寿司', '①将大米洗净，装入器皿中，用两杯水浸泡1小时。\r\n②瓠瓜丝用热水浸泡30分钟捞出，另加糖一大匙以及酱油半杯，覆上微波薄膜，与大米一起放入炉内，用高火煮10分钟，再用中火加热12分钟，把饭取出，静放3分钟后趁热拌入白糖、醋。瓠瓜丝切成段，姜及黄萝卜也切成长条丝状备用。\r\n③取一张紫菜皮摆平，铺上饭，再铺适量的肉松、姜丝、瓠瓜丝、黄萝卜丝、蛋皮，卷成圆筒形，再切块即可。', NULL, NULL, NULL, '大米两杯，\r\n 肉松125克，\r\n 腌汁姜一块，\r\n 瓠瓜丝一条，\r\n 黄萝卜半条，\r\n 紫菜皮六张，\r\n 蛋皮适量。  白糖四大匙，\r\n 白醋四大匙。');
INSERT INTO `recipe` VALUES (5252, 64, '紫菜色拉卷', '①将金华火腿切成与土司同长的长条，土司去周围黄皮，马铃薯去皮后切成与土司同长的1厘米宽的粗条，四季豆与马铃薯分别放入袋中，袋口不要结得太紧，高火7分钟，取出待凉备用。\r\n②紫菜放在盘中，高火1分30秒，取出后切成比土司略长（约3厘米）的长条，备用。\r\n③取一块紫菜，放上土司，将金华火腿、马铃薯、四季豆并排放上，挤上色拉酱后卷起，接口处以色拉酱粘紧，食用时对角切半即可。', NULL, NULL, NULL, '金华火腿一片，\r\n 马铃薯一只，\r\n 四季豆12根，\r\n 紫菜一包，\r\n 土司（约12片）半条。  色拉酱一包。');
INSERT INTO `recipe` VALUES (5253, 64, '菜肉馄饨', '①将菜切碎，拌入肉糜，加入调料(A)拌匀，用皮子包成馄饨。\r\n②取一汤碗，倒上沸水，放入馄饨20只左右，高火3分钟，放至有调料(B)的碗中即可食用。', NULL, NULL, NULL, '鲜肉糜300克，\r\n 菜1000克，\r\n 馄饨皮500克。  (A)绍酒一小匙，精盐半小匙，姜末少许，葱花一大匙，味精半小匙，芝麻油一中匙；\r\n (B)精盐一小匙，葱花一大匙，味精半小匙，芝麻油一中匙，汤汁适量。');
INSERT INTO `recipe` VALUES (5254, 64, '北方馄饨', '①将大白菜洗净切碎沥干，用调料和肉糜拌和。\r\n②用清水和面，擀成饺子皮，白菜肉糜为馅，包成饺子。\r\n③取一汤碗，加入沸水，投入饺子，高火3分钟即可。', NULL, NULL, NULL, '肉糜100克，\r\n 大白菜50克，\r\n 面粉120克。  绍酒一小匙，\r\n 精盐半小匙，\r\n 芝麻油一中匙，\r\n 味精半小匙，\r\n 胡椒粉一小匙，\r\n 葱姜末一大匙。');
INSERT INTO `recipe` VALUES (5255, 64, '丝瓜炒面筋', '①取一碗，放入调料(A)，以高火爆香2分30秒备用。\r\n②丝瓜洗净去皮然后切成块置于器皿中，加入爆香过的调料(A)、面筋、面筋汁液和精盐，加盖中高火6分钟即可。', NULL, NULL, NULL, '丝瓜两条，\r\n 面筋罐头一罐。  精盐一小匙；\r\n (A)姜末一大匙半，色拉油三大匙。');
INSERT INTO `recipe` VALUES (5256, 64, '甜酒青梅', '①将蜂蜜、水、葡萄酒、橙肉放在容器内，加盖高火5分钟。\r\n②取出放入青梅，过2分钟后倒入鸡尾酒杯中，撒上松子即可。', NULL, NULL, NULL, '青梅一盒约250克，\r\n 松子1/3杯，\r\n 橙肉一中匙，\r\n 蜂蜜两大匙。  葡萄酒一杯，\r\n 水半杯。');
INSERT INTO `recipe` VALUES (5257, 64, '清凉一夏', '①芋艿高火2分钟后去皮。\r\n②将水、冰糖放在一起入炉高火5分钟，取出加入大西米后高火3分钟。\r\n③再放入芋艿、熟花生仁高火7分钟。\r\n④取出后淋上炼乳，待凉后放入冰箱（冷藏格）3小时即可。', NULL, NULL, NULL, '熟花生仁150克，\r\n 小芋艿150克，\r\n 大西米少许。  炼乳三大匙，\r\n 水五杯，\r\n 冰糖一中匙。');
INSERT INTO `recipe` VALUES (5258, 64, '草莓摩斯杯', '①将吉利丁粉加少量冷水调匀，高火6分钟煮沸，加入白糖充分搅拌至溶化，待冷却后备用。\r\n②草莓和鲜奶用果汁机打成汁后过滤，再把鲜奶打至糊状，再倒入打成汁的材料内。\r\n③煮好的吉利丁倒入材料内混合均匀。\r\n④将成品倒入塑胶模内，待冷却后放入冰箱使其凝固。\r\n⑤凝固后的成品挤上鲜淡炼乳并装饰草莓即可。', NULL, NULL, NULL, '鲜奶2000克，\r\n 吉利丁粉两大匙，\r\n 草莓500克，\r\n 鲜淡炼乳一杯半。  冷水一杯，\r\n 白糖一杯。');
INSERT INTO `recipe` VALUES (5259, 64, '朱古力杯', '①将核桃肉放在煎盘上，中火2分钟。\r\n②将巧克力、黄油放入深盘中，中火2分钟，取出拌匀，再加入核桃肉拌匀，用匙将适量混和物放入蛋糕杯中，冷却（或放入冰箱中）至凝结即可。', NULL, NULL, NULL, '巧克力120克（切碎），\r\n 黄油25克，\r\n 核桃肉120克，\r\n 纸蛋糕杯子适量。  无');
INSERT INTO `recipe` VALUES (5260, 64, '银耳什锦', '①将白木耳洗净，用开水烫泡1分钟，沥干。\r\n②在一中碗中放入热水，加入沥干的白木耳，加盖，以高火煮7分钟至滚后加白糖，最后用高火煮3分钟至开即可。待凉后拌入什锦水果丁，冰后即是一道清凉的夏日甜点。', NULL, NULL, NULL, '白木耳10克，\r\n 什锦水果丁一杯半。  白糖约一杯，\r\n 热水四杯。');
INSERT INTO `recipe` VALUES (5261, 64, '南瓜冻', '①将巧克力消化饼压碎，加入黄油搓匀成粉团，然后压在烤盘上，中火1分钟，取出待冻。\r\n②南瓜去皮切片，放入碗内，加盖高火2分钟，加入豆蔻粉，搓烂成瓜酱。\r\n③淡咖喱粉用水拌匀。将鲜奶及白糖煮至微滚，倒入淡咖喱粉拌匀，待半凝结，加入南瓜酱及掼淡炼乳，然后倒入盘内，放入冰箱冷冻后取出，镶上鲜淡炼乳即可。', NULL, NULL, NULL, '巧克力消化饼一包，\r\n 南瓜15克，\r\n 掼淡炼乳1/3杯，\r\n 鲜奶一杯（冰冻）。  黄油50克，\r\n 白糖50克，\r\n 淡咖喱粉两大匙，\r\n 水半杯，\r\n 豆蔻粉一小匙。');
INSERT INTO `recipe` VALUES (5262, 64, '橘红李子冻', '①将吉利丁粉加少量冷水调匀，高火6分钟，煮沸后加入白糖充分搅拌至溶化，待冷却后备用。\r\n②将煮好的吉利丁过滤，倒入橘子汁和洋酒，充分搅拌均匀。\r\n③调好的材料倒于杯内，杯中放一李子干，待冷却后放入冰箱，使其凝固成型即可。', NULL, NULL, NULL, '李子干20个，\r\n 橘子汁1600克，\r\n 吉利丁粉两大匙。  冷水400克，\r\n 白糖2/3杯，\r\n 洋酒少许。');
INSERT INTO `recipe` VALUES (5263, 64, '葡萄果冻', '①将葡萄洗净，加水，高火5分钟，煮至果肉和皮分离，再过滤。\r\n②将滤过的汁煮沸后，加入吉利丁粉、白糖，充分搅拌至溶化。\r\n③把成品倒于杯内，待冷却后放入冰箱使其凝固成型。\r\n④凝固后的成品，挤上鲜奶油，杯口装饰一个樱桃即可。', NULL, NULL, NULL, '新鲜葡萄1200克，\r\n 洋酒少许，\r\n 吉利丁粉三大匙，\r\n 鲜奶油适量，\r\n 樱桃一个。  冷水1200克，\r\n 白糖一杯。');
INSERT INTO `recipe` VALUES (5264, 64, '姜汁炖奶', '①白糖加1/4杯水高火5分钟，待冷。\r\n②蛋清加鲜奶拌匀，加入姜汁及糖水再拌匀，隔去泡，盛碗内，用一碟盖密碗口，低火6分钟即可。', NULL, NULL, NULL, '三只大鸡蛋的蛋清，\r\n 鲜奶一杯。  白糖1/3杯，\r\n 姜汁一汤匙半或适量。');
INSERT INTO `recipe` VALUES (5265, 64, '蜜汁炖银杏', '①将白果去壳后，放入滚水中高火3分钟，去衣洗净。\r\n②冰糖加四杯半水，低火12分钟至滚。\r\n③把白果、茨实、红枣、龙眼肉、冰糖水放入器皿内，低火1小时即可。', NULL, NULL, NULL, '白果200克，\r\n 茨实100克，\r\n 红枣八只去核，\r\n 龙眼肉一汤匙，\r\n 冰糖250克。');
INSERT INTO `recipe` VALUES (5266, 64, '鸽蛋雪蛤膏', '①将雪蛤膏用清水浸透，约需5小时，拣去污物洗净，放入滚水中高火3分钟，捞起滴干水。\r\n②冰糖加一杯半水，慢火煲滚。\r\n③鸽蛋或鹌鹑蛋蒸熟，约需15分钟。用清水浸冷，去壳洗净。\r\n④把雪蛤膏、鸽蛋或鹌鹑蛋、糖水放入器皿内，低火炖1小时，加入椰汁炖2分钟，最后加入鲜奶搅匀再炖2分钟即可。', NULL, NULL, NULL, '雪蛤膏15克，\r\n 冰糖200克，\r\n 椰汁半杯，\r\n 鲜奶一杯，\r\n 白鸽蛋或鹌鹑蛋10个。  无');
INSERT INTO `recipe` VALUES (5267, 64, '柠檬草莓酒', '①选择中等形状、大小一致的草莓，去蒂。用盐水清洗后，拭干水分。\r\n②柠檬去皮，横切成柠檬片。\r\n③将柠檬片、白葡萄酒、冰糖装入一大碗内，加盖高火7分钟。等冷却后倒入密封瓶内，再将草莓加入密封。\r\n④3日之后，将柠檬去除再密封，再经2～3日后可饮用。', NULL, NULL, NULL, '新鲜草莓400克，\r\n 柠檬两个。  碎冰糖225克，\r\n 白葡萄酒两杯半。');
INSERT INTO `recipe` VALUES (5268, 64, '首乌红枣茶', '①将鸡蛋蒸熟，冷后去壳洗一洗。\r\n②桑寄生洗净滴干水。\r\n③把首乌、桑寄生、红枣、鸡蛋放入器皿内，加入滚水3～4杯，低火1小时即可。如喜欢甜味，可酌量加入冰糖同炖。', NULL, NULL, NULL, '首乌25克，\r\n 桑寄生25克，\r\n 红枣100克去核，\r\n 鸡蛋四个。  无');
INSERT INTO `recipe` VALUES (5269, 64, '冰糖红枣茶', '①将桂圆干、红枣、碎冰糖和所有调料一起放入大汤碗中。\r\n②加盖高火6分钟后，改用中低火6分钟，其香味溢出即可。', NULL, NULL, NULL, '桂圆干100克，\r\n 红枣60克，\r\n 碎冰糖60克。  酒一大匙，\r\n 热开水八杯。');
INSERT INTO `recipe` VALUES (5270, 64, '香瓜西米露', '①将热水、冰糖放入碗内高火2分钟。\r\n②再放入大西米高火5分钟。\r\n③加入炼乳、香瓜汁拌匀即可。', NULL, NULL, NULL, '大西米100克，\r\n 炼乳三大匙，\r\n 香瓜汁一杯。  冰糖一小匙，\r\n 热水一杯。');
INSERT INTO `recipe` VALUES (5271, 64, '水果银耳羹', '①将白木耳用清水浸泡，发好后去根洗净。\r\n②再装入大汤碗，加上沸水，加盖高火4分钟。\r\n③加入白糖，再加盖中火25分钟。\r\n④取出后，加入罐装菠萝、橘子即可。', NULL, NULL, NULL, '白木耳30克，\r\n 罐装橘子一罐，\r\n 罐装菠萝1/4罐。  白糖250克，\r\n 沸水五杯。');
INSERT INTO `recipe` VALUES (5272, 64, '银耳冰糖', '①将冰糖加四杯水低火12分钟至滚。\r\n②白木耳用清水浸泡约1小时，撕成小朵，放入滚水中高火4分钟，取出洗净，滴干水。\r\n③木瓜去皮去核，洗净，切件。\r\n④把木瓜、白木耳、糖水放入器皿内，低火1小时即可。', NULL, NULL, NULL, '木瓜730克，\r\n 白木耳25克，\r\n 冰糖200克。  无');
INSERT INTO `recipe` VALUES (5273, 64, '杏仁燕窝', '①将杏仁放入滚水中高火3分钟，冷后去衣。\r\n②冰糖加水三杯半，低火10分钟至滚。\r\n③燕窝球放在大汤碗内，加入滚水盖密●4小时，除去水分，拣去毛。\r\n④燕窝放入滚水中低火10分钟，捞起用清水浸一浸，滴干水。\r\n⑤把燕窝、杏仁、糖水放入器皿内，低火1小时即可。', NULL, NULL, NULL, '燕窝球两个，\r\n 南杏50克，\r\n 冰糖200克，\r\n 北杏六粒。  无');
INSERT INTO `recipe` VALUES (5274, 64, '百合炖雪梨', '①冰糖加水三杯半，低火10分钟至滚。\r\n②百合用清水浸30分钟，放到滚水中煮3分钟，取出滴干水。\r\n③雪梨切去梨心，洗净，连皮切件。\r\n④把雪梨、百合、冰糖水放入器皿内，低火1小时即可。', NULL, NULL, NULL, '雪梨两个，\r\n 百合100克，\r\n 冰糖150克。  无');
INSERT INTO `recipe` VALUES (5275, 64, '川贝母炖蜜糖', '把川贝母、蜜糖加滚水3/4杯搅匀，中火炖20分钟即可。', NULL, NULL, NULL, '川贝母10克。  蜜糖两汤匙。');
INSERT INTO `recipe` VALUES (5276, 64, '川贝炖雪梨', '①将雪梨切去蒂部，挖出雪梨心。\r\n②把川贝粉、冰糖嵌入雪梨内，盖回蒂部，用牙签穿连，放入炖器皿内，中火炖45分钟即可。', NULL, NULL, NULL, '雪梨一个，\r\n 川贝粉5克，\r\n 拇指般大冰糖一粒（碾碎）。  无');
INSERT INTO `recipe` VALUES (5277, 64, '花生芝麻糊糕', '①盘中放色拉油，高火炸花生仁4分钟，待花生仁呈金黄色时取出晾凉，去皮备用。\r\n②将黑芝麻置于搅碎机中打碎，放入锅中，加热水、白糖、牛奶，加盖，高火8分钟，取出后加入湿淀粉调匀，加盖，再高火2分钟，撒上花生仁即可。', NULL, NULL, NULL, '花生仁250克，\r\n 黑芝麻（熟）110克。  色拉油两小匙，\r\n 热水六杯，\r\n 白糖三大匙，\r\n 牛奶三小匙，\r\n 湿淀粉五小匙。');
INSERT INTO `recipe` VALUES (5278, 64, '花生太妃糖', '①将花生仁洗净晾干，置于浅而宽且放入色拉油的盘中，高火10分钟（每2分钟翻动一次），取出，晾凉后去皮备用。\r\n②拌匀调料(A)，用中高火煮12分钟成糖液，再拌入花生仁，直至糖液成丝。\r\n③取一浅模具，内壁涂油，填入花生糖压扁，晾凉后切块装盘即可。', NULL, NULL, NULL, '花生仁300克。  色拉油一汤勺；\r\n (A)麦芽糖两汤勺，白糖200克。');
INSERT INTO `recipe` VALUES (5279, 64, '三丝河粉卷', '①将河粉皮泡水，取出沥干，每张裁成8厘米宽的长条；香菇用水泡软，去蒂切丝；笋连皮洗净，用微波薄膜包起置盘中（厚的一端朝外），高火7分钟，取出待凉，剥皮并切去粗硬部分，切丝；豆芽、香菜洗净备用。\r\n②肉丝与调料(A)拌匀，虾仁去肠泥洗净后，加入调料(B)搅拌备用。\r\n③取一锅，将肉丝、虾仁、香菇、笋、豆芽、香菜与调料?调匀，分成24等份，每一份馅料放入河粉皮中，逐一卷起置盘中，覆微波薄膜，高火5分钟。\r\n④取一碗，放入高汤，高火3分钟，拌入调料(D)，再高火30秒钟，取出加上芝麻油，淋在河粉卷上即可。', NULL, NULL, NULL, '河粉皮三张，\r\n 肉丝75克，\r\n 虾仁75克，\r\n 香菇三只，\r\n 笋半根，\r\n 豆芽75克。  高汤半杯多，\r\n 香菜少许，\r\n 芝麻油一匙；\r\n (A)酱油半大匙，干淀粉一匙；\r\n (B)蛋白半只，绍酒一匙，白胡椒粉少许；\r\n ?绍酒一匙，精盐一匙，酱油半大匙，白胡椒粉少许，芝麻油半匙；\r\n (D)干淀粉一大匙，水一大匙。');
INSERT INTO `recipe` VALUES (5280, 64, '甜味粉丝', '①将粉丝洗净，用热开水浸泡5分钟，沥干。豌豆荚摘去两头。\r\n②大白菜洗净切丝，胡萝卜去皮切丝，香菇泡软切丝，五花肉切片，虾米泡软。\r\n③将色拉油倒入盘内，加入葱段、大白菜、胡萝卜、香菇、五花肉、虾米，加盖高火5分钟后拌入其他调料。\r\n④将粉丝、豌豆荚倒入其中拌匀，加盖高火7分钟即可。', NULL, NULL, NULL, '将粉丝三小把，\r\n 大白菜200克，\r\n 胡萝卜100克，\r\n 豌豆荚50克，\r\n 五花肉150克，\r\n 香菇三朵，\r\n 虾米两大匙。  色拉油1/3杯，\r\n 葱段六段，\r\n 精盐半小匙，\r\n 白糖两小匙，\r\n 酱油一大匙，\r\n 胡椒粉半小匙，\r\n 水五大匙，\r\n 味精半小匙。');
INSERT INTO `recipe` VALUES (5281, 64, '咸汤圆', '①糯米粉加入冷水后，揉成面团，从中取出200克放入三杯热水中，高火2分钟后捞出，放入原来搓好的面团中，再用力揉搓，使其产生粘性后，将其分成40份。\r\n②虾米泡软剁碎，与干大葱一大匙、肉糜、调料(A)拌匀，摔打成肉馅。\r\n③一张皮包入半大匙肉馅，揉搓成咸汤圆。\r\n④取10个汤圆（其余冷冻，需要时再煮），放入热高汤中，并加入莴苣、腌芥末叶、干大葱一大匙及调料(B)，高火6分钟后至汤开即可（冷冻的汤圆，煮时多加1分钟）。', NULL, NULL, NULL, '糯米粉500克，\r\n 肉糜200克，\r\n 虾米一大匙，\r\n 莴苣150克，\r\n 腌芥末叶一大匙，\r\n 干大葱两大匙。  热高汤三杯，\r\n 冷水两杯；\r\n (A)酱油一大匙，精盐半小匙，胡椒粉1/4小匙，白糖一小匙；\r\n (B)精盐半小匙，味精半小匙，胡椒粉半小匙。');
INSERT INTO `recipe` VALUES (5282, 64, '小笼汤包', '①用上述调料与鲜肉糜、摇碎的肉皮冻拌和。\r\n②面粉用水揉成面团，放15分钟后擀皮包馅(约10只左右）。\r\n③取一盘，垫上湿纱布，摆上汤包，再用湿纱布盖上，高火7分钟即可。', NULL, NULL, NULL, '鲜肉糜100克，\r\n 面粉150克，\r\n 肉皮冻25克。  绍酒一小匙，\r\n 精盐半小匙，\r\n 白糖少许，\r\n 味精半小匙，\r\n 葱姜末一中匙。');
INSERT INTO `recipe` VALUES (5283, 64, '花生糯米球', '①将精粉置汤碗内，迅速冲入沸水半杯，立即搅拌，使精粉凝结而呈透明状。\r\n②将精粉加入糯米粉中，倒入适量的冷水、白糖、猪油，拌成面团后用力揉搓，使其产生粘性，再捏成24份皮。\r\n③豆沙分成24粒，一张皮包入一粒豆沙馅，搓成糯米球。先将一半的糯米球放入剩下的五杯沸水中，加盖高火5分钟，煮沸后捞出，立即滚上花生粉，再将剩下的一半糯米球放入，以同样的办法处理即可。', NULL, NULL, NULL, '糯米粉300克，\r\n 精粉三大匙，\r\n 花生粉3/4杯，\r\n 红豆沙200克。  白糖两大匙，\r\n 猪油两大匙，\r\n 沸水5杯半，\r\n 冷水适量。');
INSERT INTO `recipe` VALUES (5284, 64, '香菇菜包', '①青菜洗净，沸水中烫一下捞起，切碎沥干；香菇、黄花菜沸水泡开后洗净切碎；香干切碎。\r\n②将调料与青菜、香菇、香干、黄花菜一起拌和。\r\n③发酵粉和面粉用水揉成面团，放15分钟。\r\n④包成包子，放30分钟。\r\n⑤用大盘放上纱布一块，摆上包子，再盖上一块湿纱布，高火9分钟即可。', NULL, NULL, NULL, '青菜400克，\r\n 香菇30克，\r\n 香干两块，\r\n 黄花菜20克，\r\n 发酵粉8克，\r\n 面粉350克。  精盐一小匙，\r\n 味精一小匙，\r\n 葱末一中匙，\r\n 芝麻油一大匙。');
INSERT INTO `recipe` VALUES (5285, 64, '炒年糕', '①年糕用温水浸泡30分钟，取出后切为0.3厘米厚的斜片。\r\n②大白菜洗净切丝，五花肉切片，虾米泡软，香菇泡软后切丝，葱及红辣椒切成小段。\r\n③取一盘，倒入色拉油，放入除年糕以外的所有材料，高火3分钟爆香。\r\n④将精盐、白酱油、白糖、白胡椒、水加入爆好的大白菜中拌匀，再倒入年糕内拌匀，中高火8分钟，中途搅拌两次。', NULL, NULL, NULL, '宁波年糕250克，\r\n 大白菜200克，\r\n 五花肉100克，\r\n 香菇三朵，\r\n 虾米一大匙。  精盐半小匙，\r\n 白酱油两大匙，\r\n 白糖一小匙，\r\n 白胡椒1/4小匙，\r\n 水四大匙，\r\n 色拉油四大匙，\r\n 葱两根，\r\n 红辣椒一个。');
INSERT INTO `recipe` VALUES (5286, 64, '虾仁烧卖', '①虾仁去肠泥洗净剁碎，然后连同肉糜一起放入大碗中搅拌，加入调料后再搅拌均匀成馅。\r\n②用烧卖皮将馅子包入，并在上面放一粒蘸有干淀粉的豌豆，然后放在涂过油的盘中。\r\n③覆上微波薄膜，用高火加热2分30秒即可。', NULL, NULL, NULL, '虾仁150克，\r\n 肉糜80克，\r\n 绿豌豆10粒，\r\n 烧卖皮10张。  绍酒一大匙，\r\n 精盐少许，\r\n 酱油一匙，\r\n 干淀粉少许。');
INSERT INTO `recipe` VALUES (5287, 64, '鲜烧卖', '①将糯米饭和鲜肉糜加入调料拌匀成馅。\r\n②用馄饨皮包馅成烧卖。\r\n③取一盘，加入沸水，用蒸笼铺上湿布一块，摆上烧卖，喷洒一些水后，高火4分钟即可。', NULL, NULL, NULL, '大馄饨皮100克，\r\n 鲜肉糜150克，\r\n 糯米饭100克。  绍酒一小匙，\r\n 精盐半小匙，\r\n 白糖半小匙，\r\n 葱姜末一中匙，\r\n 味精半小匙，\r\n 香油一中匙，\r\n 酱油一中匙。');
INSERT INTO `recipe` VALUES (5288, 64, '水晶饺', '①荸荠去皮，以刀背拍碎后去生水；香菇放碗中，加水，高火3分钟，取出剁碎。\r\n②把调料(A)及肉糜、荸荠、香菇和葱放入器皿中，加盖高火5分钟，再加入面粉三大匙拌匀。然后将其包入饺子皮内，逐个包好放于盘中。\r\n③在隔板涂色拉油的蒸锅上，排上水晶饺，覆上纱布，热水沿锅边倒入，加盖高火6分钟即可。', NULL, NULL, NULL, '饺子皮24张，\r\n 肉糜75克，\r\n 荸荠225克，\r\n 香菇三朵，\r\n 湿纱布一块。  面粉三大匙，\r\n 沸水一杯，\r\n 热水一杯，\r\n 水半杯，\r\n 色拉油少许；\r\n (A)酱油两大匙，五香粉适量，精盐一匙，白糖一匙，葱两根。');
INSERT INTO `recipe` VALUES (5289, 64, '水晶虾饺', '①精粉加入干淀粉拌匀后，冲入沸水，烫成半熟状，加入猪油一小匙，搓成面团后，盖上塑料袋。\r\n②虾仁洗净，以纸巾吸干水分。肥肉切成与笋丁同样大小的丁状。\r\n③将虾仁、肥肉、笋丁与调料(A)拌匀，使其产生粘性后，加入油两小匙再拌，即成馅。\r\n④将面团切成直径2厘米的圆块，以沾上油的刀面将其压成薄圆片，再将馅包入，捏成饺子形。\r\n⑤取一深盘，放入一块湿布，再将12个虾饺放上，喷一点水，高火4分钟即可。', NULL, NULL, NULL, '精粉200克，\r\n 干淀粉一大匙，\r\n 虾仁200克，\r\n 肥肉30克，\r\n 熟笋丁半杯。  滚水2/3杯，\r\n 猪油一大匙；\r\n (A)精盐一小匙，白糖半小匙，芝麻油1/3小匙，胡椒粉1/4小匙。');
INSERT INTO `recipe` VALUES (5290, 64, '白脱淡炼乳', '①将白脱淡炼乳搅打至乳白状。\r\n②糖粉和奶水混合。\r\n③在调理好的白脱油中加入绍酒及香草粉。\r\n④再把②中的材料加入，充分搅拌均匀，低火2分钟即可。', NULL, NULL, NULL, '白脱淡炼乳500克，\r\n 奶水一杯。  糖粉250克，\r\n 绍酒少许，\r\n 香草粉一匙。');
INSERT INTO `recipe` VALUES (5291, 64, '通心粉色拉', '①通心粉用大碗装盛，倒入热水，加盖。\r\n②鸡胸肉用小碗装盛，加入少许葱、姜、绍酒，覆上微波薄膜。\r\n③胡萝卜切丁，用中碗装盛，覆上微波薄膜。\r\n④调料（B)覆上微波薄膜（蛋煮熟后去壳切丁）。\r\n⑤以上四项材料一起前后上下地放入炉内，用高火煮21分钟至熟。\r\n⑥通心粉煮熟后，需静置10分钟。\r\n⑦煮好的材料加入火腿丁及调料(A)后混合均匀。取一盘子，用生菜垫底，倒入通心粉色拉，撒上乳酪屑即可。', NULL, NULL, NULL, '通心粉一杯，\r\n 鸡胸肉200克，\r\n 乳酪1～2片，\r\n 胡萝卜200克，\r\n 豌豆仁一罐，\r\n 金华火腿丁一杯。  热水四杯，\r\n 葱、姜、绍酒少许；\r\n (A)色拉酱两包，精盐一匙，白糖两匙，黑胡椒粉一大匙;\r\n (B)鸡蛋两个，水一杯半，精盐少许。');
INSERT INTO `recipe` VALUES (5292, 64, '西米布丁', '①将四杯水用高火煮开，放入西米搅匀，高火1分钟，取出焖2分钟，滤去水分备用。\r\n②用半杯水搅匀干淀粉、乳蛋粉、蛋、炼乳。\r\n③将鲜奶、白糖、黄油用高火煮开（约12分钟），倒入由干淀粉拌成的混合液中不停搅动，再加入西米搅匀，高火1分钟。\r\n④放入冰箱冷冻即可。', NULL, NULL, NULL, '西米150克（浸透），\r\n 炼乳1/3杯，\r\n 鸡蛋三个，\r\n 干淀粉50克，\r\n 乳蛋粉100克，\r\n 鲜奶三杯。  水四杯半，\r\n 黄油100克，\r\n 白糖250克。');
INSERT INTO `recipe` VALUES (5293, 64, '葡萄布丁', '①将面包去黄边，撕成块状，用清水泡软，葡萄干也泡软。\r\n②蛋打散，边加调料(A)边打匀。\r\n③将面包挤去水，与葡萄干一同加入上料中，并搅拌，使其充分混合。\r\n④模型抹油，倒入汁液，覆上微波薄膜，用高火蒸4分钟，取出扣在盘上。\r\n⑤将调料(B)拌匀，用高火煮1分钟至滚后，迅速调入干淀粉勾芡，淋于布丁上即可。', NULL, NULL, NULL, '面包一只，\r\n 鸡蛋一个，\r\n 土司四片，\r\n 葡萄干两大匙，\r\n 布丁模型四只。  (A)白糖两大匙，奶水适量，香草片一片，猪油一大匙；\r\n (B)奶水半杯，白糖一大匙；\r\n ?干淀粉一匙半，水一匙。');
INSERT INTO `recipe` VALUES (5294, 64, '鸡蛋布丁', '①先高火5分钟，将水煮沸，加入白糖充分搅拌成糖水。\r\n②将糖水加蛋和鲜奶搅拌均匀。\r\n③将一杯白糖混合半杯开水煮至金褐色，熬成焦糖。\r\n④将煮好的焦糖倒入少许于模型内。\r\n⑤再把调匀后的布丁材料倒于模型内。\r\n⑥烤盘内加约1/3的温水，然后放进微波炉烤箱，用中火烤15分钟即可。', NULL, NULL, NULL, '鲜奶400克，\r\n 蛋500克。  冷水700克，\r\n 白糖150克，');
INSERT INTO `recipe` VALUES (5295, 64, '沙拉布丁', '①将调料(A)放入一碗中调匀，以高火煮5分钟，使之成浓稠的糖液备用。\r\n②布丁模型内部涂油，逐个加入半大匙的糖液。\r\n③将调料(B)拌匀，打入蛋和奶水，调匀过滤后，倒入模型至八分满。\r\n④在一大器皿中，注入热水，放入布丁，加盖中火蒸18分钟。\r\n⑤取出，置冷水中待凉，倒扣于盘上即可。', NULL, NULL, NULL, '布丁模型六只，\r\n 色拉油一大匙，\r\n 鸡蛋六个，\r\n 奶水1/4杯，\r\n 热水一杯。  (A)白糖120克，热水1/4杯；\r\n (B)白糖半杯，热水两杯。');
INSERT INTO `recipe` VALUES (5296, 64, '汉堡', '①将洋葱去皮剁碎，红萝卜去皮剁碎，小黄瓜抹精盐去水切斜片备用。\r\n②在一大碗中放入肉糜、洋葱末、红萝卜末和调料(B)，调匀成肉馅，再作六等份，压成圆饼状。\r\n③一器皿内放入色拉油一大匙半，高火3分钟，再放入肉饼三个，用高火煎1分30秒，翻面再煎1分30秒。第二次煎时不需要预热，功率与时间与第一次相同。\r\n④取汉堡面包，铺上调料(A)及肉饼，加上番茄酱和色拉酱即可。', NULL, NULL, NULL, '肉糜375克，\r\n 红萝卜40克，\r\n 洋葱75克，\r\n 汉堡面包六只。  精盐一匙，\r\n 色拉酱适量，\r\n 番茄酱适量，\r\n 色拉油一大匙半；\r\n (A)起士六片，金华火腿六片，红番茄（轮切）六片，生菜六片，洋葱（轮切）六片，小黄瓜一条；\r\n (B)酱油两大匙，黑胡椒粉少量，白胡椒粉少量，五香粉少量，白糖一匙。');
INSERT INTO `recipe` VALUES (5297, 64, '草莓果酱', '①草莓选择中等形状（大小一致），去蒂，用盐水洗净，再擦干水分。\r\n②果冻加入冷开水，搅匀备用。\r\n③将柠檬挤汁与草莓、白糖及果冻一起放入大碗中，中火20分钟，中途需要搅拌多次，视凝成浓稠状时即可取出，待冷却后装入瓶内。', NULL, NULL, NULL, '新鲜草莓500克，\r\n 柠檬两个，\r\n 果冻两小匙。  白糖350克，\r\n 冷水两大匙。');
INSERT INTO `recipe` VALUES (5298, 64, '情人卷', '①将蛋黄和白糖搅拌至微白。\r\n②将白脱淡炼乳加热熔化后倒在蛋黄内。\r\n③将面粉和泡打粉筛入蛋黄材料内混合均匀。\r\n④将调理好的生料倒于模型内，铺成薄状，模内垫纸，放进微波炉烤箱以高火烤8分钟。\r\n⑤待冷却后，将蛋糕从模型中取出，涂上白脱淡炼乳，再将草莓海绵蛋糕包卷起即可。', NULL, NULL, NULL, '草莓海绵蛋糕一盘，\r\n 蛋黄100克，\r\n 白脱淡炼乳80克，\r\n 面粉800克，\r\n 泡打粉少许。  白糖320克。');
INSERT INTO `recipe` VALUES (5299, 64, '葵花', '①将小黄瓜以波浪刀切斜片置盘中，加上调料(A)，腌10分钟。\r\n②将金华火腿切成0.5厘米的厚片，再对切成三角形，围在盘子的外围，打入三个蛋于盘的中央，再用筷子将蛋黄弄破，覆上微波薄膜，以高火煮2分30秒。\r\n③取出，边饰小黄瓜片，挤上色拉酱，再撒上白胡椒粉即可。', NULL, NULL, NULL, '金华火腿半块，\r\n 未冰过的鸡蛋三个，\r\n 小黄瓜一根。  白胡椒粉适量，\r\n 色拉酱适量；\r\n (A)精盐半匙，白糖一匙。');
INSERT INTO `recipe` VALUES (5300, 64, '香甜脆皮', '①将细白糖、白脱淡炼乳混合打发。\r\n②蛋分两次加入，充分搅拌。\r\n③面粉、奶粉筛入材料内混合搅拌。\r\n④取出调好的面团，用擀面棍擀。将擀平的生料放于模型内，修饰边缘所剩的生料。\r\n⑤用叉子将模型中的生料戳洞使其透气（以免膨胀），放进微波炉烤箱中以中火烤约20分钟即可。', NULL, NULL, NULL, '白脱淡炼乳50克，\r\n 鸡蛋两个，\r\n 面粉1000克，\r\n 奶粉400克。  细白糖200克。');
INSERT INTO `recipe` VALUES (5301, 64, '核桃片', '①将白脱淡炼乳、白糖混合打发后，加蛋搅拌均匀。\r\n②面粉筛入材料内，再加核桃粒搅拌均匀。\r\n③将生料放在盘中压平，置入冰箱，等冰硬后取出切片，放进微波炉烤箱中以中火烤约15分钟即可。', NULL, NULL, NULL, '白脱淡炼乳800克，\r\n 鸡蛋一个，\r\n 核桃600克，\r\n 面粉1800克。  白糖800克。');
INSERT INTO `recipe` VALUES (5302, 64, '香甜山芋片', '①山芋去皮，切厚片。\r\n②将白糖、水放在深盘内，加入黄油、山芋片，放入炉内高火5分钟，取出，淋上汁液即可。', NULL, NULL, NULL, '山芋250克。  水一大匙，\r\n 白糖120克，\r\n 黄油25克。');
INSERT INTO `recipe` VALUES (5303, 64, '桂花糖藕', '①莲藕洗净去皮，依节切开，并切下顶部约2厘米，作为盖子备用。\r\n②糯米洗净，沥干，灌入莲藕的洞内，轻轻敲打，使糯米填满洞中，盖上盖子，并以三根牙签固定，逐个做好备用。\r\n③取一微波炉用压力锅，放入莲藕，加水没过莲藕再加盖，以高火煮30分钟，取出，将汁液倒去，待凉，再加入调料(A)，加盖，以高火煮10分钟后，再以中火煮30分钟，取出切成0.5厘米厚的圆片装盘。\r\n④倒出汁液，加入调料(B)拌匀，以高火煮3分钟，淋在莲藕上即可', NULL, NULL, NULL, '生莲藕600克，\r\n 圆糯米适量，\r\n 牙签数根。  (A)桂花酱一大匙，白糖一杯，水一杯；\r\n (B)干淀粉两大匙，水一大匙。');
INSERT INTO `recipe` VALUES (5304, 64, '什锦椰子球', '①将蛋打散，加入白糖拌匀，再加入面粉、椰子粉与淡炼乳拌匀即成椰蓉。\r\n②樱桃、葡萄干切碎后，加到椰蓉中拌匀。\r\n③全部椰蓉分作约35团，将每一团用力捏紧，呈球状。\r\n④烤盘用高火预热5分钟，将椰子球排入12个后，从第一个开始翻面，翻到最后一个，再以中高火烤4分钟，中途再翻面1～2次即可。\r\n⑤余下的椰子球也依此顺序烘烤。', NULL, NULL, NULL, '椰子粉两杯，\r\n 鸡蛋一个，\r\n 面粉一大匙，\r\n 淡炼乳一大匙半，\r\n 葡萄干两大匙，\r\n 樱桃两粒。  白糖半杯。');
INSERT INTO `recipe` VALUES (5305, 64, '西欧土司', '①土司每片对切成三角形。\r\n②将蛋打散，调入奶水、白糖及盐拌匀。\r\n③小烤盘先用高火预热4分钟后滴入色拉油，并将四片三角形土司蘸上蛋液，放入烤盘后立刻翻面，再用高火烤1分钟即可。', NULL, NULL, NULL, '土司四片，\r\n 鸡蛋一个。  白糖一大匙，\r\n 精盐少许，\r\n 色拉油两大匙，\r\n 奶水少量。');
INSERT INTO `recipe` VALUES (5306, 64, '巧克力摩斯', '①将吉利丁粉加少量的冷水调匀，高火6分钟煮沸后，加入白糖充分搅拌至溶化，待冷却后备用。\r\n②巧克力沙司加鲜奶搅匀后备用。\r\n③鲜淡炼乳打至糊状，再倒入巧克力沙司和煮好的吉利丁混合均匀。\r\n④将成品倒于模型内，待冷却后放于冰箱内凝固成型，然后再置入盘中。\r\n⑤将成品挤上鲜淡炼乳，再装饰巧克力片即可。', NULL, NULL, NULL, '鲜奶1400克，\r\n 吉利丁粉两大匙，\r\n 巧克力沙司600克，\r\n 鲜淡炼乳一杯半。  冷水一杯，\r\n 白糖一杯半。');
INSERT INTO `recipe` VALUES (5307, 64, '熏肉玉米饼', '①将熏肉放在煎盘上，高火3分钟，取出放在盘中。\r\n②将蛋打匀，加入鲜奶、白糖、精盐拌匀，倒入拌了发酵粉的面粉内，拌匀成糊状，然后将玉米粒、黄油溶液倒入，待发酵30分钟。\r\n③煎盘中加入色拉油，高火4分钟，取出后放入一大匙发好的粉糊，高火1分钟，翻面再高火1分钟，玉米饼即做成。这样一大匙一个饼，直至做完，做好后放盘子的一边，另一边放上熏肉即可。', NULL, NULL, NULL, '熏猪肉两条，\r\n 面粉150克（加入发酵粉拌匀），\r\n 鸡蛋一个，\r\n 玉米粒1/3杯，鲜奶100克。  白糖50克，\r\n 精盐半小匙，\r\n 黄油溶液一大匙，\r\n 色拉油三大匙。');
INSERT INTO `recipe` VALUES (5308, 64, '法式煎薄饼', '①将面粉、蛋、奶水、白脱淡炼乳置于容器内，充分搅拌成糊状，成为生料。\r\n②色拉油于器皿内加热，舀入一匙生料，高火约6分钟煎至金黄色即翻面，再煎另一面，连续反复煎四片薄饼备用。\r\n③将两片薄煎饼挤上白脱淡炼乳，然后包卷起，摆放奇异果、草莓、水蜜桃。\r\n④将水蜜桃丁块和水煮沸后，加入少许干淀粉勾芡备用。\r\n⑤然后淋于另两块薄饼上，装饰葡萄干即可。', NULL, NULL, NULL, '面粉160克，\r\n 鸡蛋两个，\r\n 奶水少许，\r\n 白脱淡炼乳80克，\r\n 白糖60克，\r\n 奇异果、草莓、水蜜桃、葡萄干少许，\r\n 干淀粉少许。  精盐、色拉油少许。');
INSERT INTO `recipe` VALUES (5309, 64, '镶西式脆饼', '①洋葱切碎，芹菜与番茄切粒。\r\n②取一盘，加色拉油三大匙，高火4分钟，放入牛肉糜、洋葱、芹菜、番茄、黄油及调料拌匀，高火2分钟，中途搅拌一次。\r\n③将脆饼每面高火30秒，取出后放入适量牛肉糜混合物，面铺上生菜丝和胡萝卜丝，可与汁拌吃。', NULL, NULL, NULL, '牛肉糜400克，\r\n 洋葱半个，\r\n 芹菜一棵，\r\n 番茄一只，\r\n 生菜丝半杯，\r\n 胡萝卜丝半杯，\r\n 墨西哥脆饼六只。  番茄酱两大匙，\r\n 精盐一中匙，\r\n 胡椒粉少许，\r\n 辣酱油一小匙，\r\n 色拉油三大匙，\r\n 牛肉30克，\r\n 黄油适量。');
INSERT INTO `recipe` VALUES (5310, 64, '腰果小西饼', '①调料打发至绒毛状，蛋打散分数次加入拌匀，然后慢慢加入面粉，拌匀（不得揉搓），最后加入腰果拌匀。\r\n②将拌好的面团分成两块，每块压成直径为3厘米的圆柱条，放入冰箱冷冻，变硬后切成0.5厘米厚的薄片。\r\n③烤盘用高火预热3分钟，盘中放12个饼，用中火烤3分钟，翻面再烤2分钟即可。', NULL, NULL, NULL, '鸡蛋一个，\r\n 面粉200克，\r\n 炸熟腰果130克。  猪油45克，\r\n 淡炼乳70克，\r\n 精盐1克，\r\n 奶粉15克，\r\n 白糖100克。');
INSERT INTO `recipe` VALUES (5311, 64, '干果蛋糕', '①将白脱淡炼乳和白糖搅拌至呈乳白色。\r\n②将全部蛋分三次慢慢加入，调打均匀。\r\n③筛入面粉、泡打粉、奶粉。\r\n④将核桃、葡萄干、蜜饯、洋酒加入生料内，搅拌至糊状。\r\n⑤调理好的生料倒于模型内，表面洒些核桃，模内垫纸，放进微波炉烤箱中以中火烤20分钟，待冷却后切片即可。', NULL, NULL, NULL, '将白脱淡炼乳300克，\r\n 蛋400克，\r\n 泡打粉少许，\r\n 奶粉40克，\r\n 面粉300克，\r\n 核桃、葡萄干、蜜饯少许。  白糖300克，\r\n 洋酒少许。');
INSERT INTO `recipe` VALUES (5312, 64, '海绵蛋糕', '①将白脱淡炼乳加热熔化备用。\r\n②蛋和白糖一起搅打至发泡。\r\n③熔化过的白脱淡炼乳和奶水倒入混合。\r\n④面粉和香草粉筛入材料内，搅拌均匀。\r\n⑤调理好的生料倒入模型内，模内垫纸，放进微波炉烤箱以中低火烤约25分钟。\r\n⑥将蛋糕从模型中取出，切块并排于盘内即可。', NULL, NULL, NULL, '蛋1500克，\r\n 奶水一杯，\r\n 面粉500克，\r\n 干淀粉100克，\r\n 香草粉一匙，\r\n 白脱淡炼乳300克。  白糖600克，\r\n 香草粉一匙。');
INSERT INTO `recipe` VALUES (5313, 64, '生日蛋糕', '①将鲜淡炼乳打至糊状备用。\r\n②将蛋糕分割成两片，涂上鲜淡炼乳。\r\n③把各种花样（颜色）挤在蛋糕上，低火2分钟。\r\n④将水果摆放于蛋糕上即可。', NULL, NULL, NULL, '中型海绵蛋糕一个，\r\n 鲜淡炼乳一罐，\r\n 各式水果适量。  无');
INSERT INTO `recipe` VALUES (5314, 64, '什锦蛋糕', '①将鸡蛋清和白糖混合打发备用。\r\n②将面粉过筛，在调理好的鸡蛋清内加入核桃碎、椰子粉。\r\n③将烤盘擦上油，把完成的生料倒于模型内，抹薄，放进微波炉烤箱高火烤约5分钟。\r\n④待冷却后，取出蛋糕铺底，将三色蛋糕放于上面，涂上白脱淡炼乳，最后再将蛋糕包卷起即可。', NULL, NULL, NULL, '三色海绵蛋糕适量，\r\n 鸡蛋清1000克，\r\n 核桃碎200克，\r\n 面粉300克，\r\n 椰子粉200克。  白糖500克。');
INSERT INTO `recipe` VALUES (5315, 64, '巧克力核桃糕', '①核桃剥碎，用高火烤2分30秒，待凉备用。\r\n②蛋黄、鸡蛋清分开，鸡蛋清加盐打至起泡，将白糖分多次加入，边加边打至白色乳沫状，再拌入蛋黄。\r\n③将调料(A)搅匀，和奶水交替加入蛋糊中，轻轻拌匀。\r\n④将淡炼乳置入小碗中，用高火加热30秒使其软化后，再拌入面糊中，最后加入核桃，倒于模型中，覆上微波薄膜，用中火加热4分钟，再用高火烤3分钟至熟，取出扣在盘上，待凉即可。', NULL, NULL, NULL, '鸡蛋四个，\r\n 核桃50克。  精盐半匙，\r\n 白糖一杯，\r\n 淡炼乳四大匙，\r\n 奶水半杯；\r\n （A)低筋面粉一杯，可可粉两匙半，发粉一匙，小苏打少量，香草片一片。');
INSERT INTO `recipe` VALUES (5316, 64, '蒸糕', '①充分筛析面粉，搁置一边备用；打鸡蛋三个，分别取其蛋清和蛋黄。\r\n②取一较大的容器，搅蛋清至奶油状。\r\n③逐渐加白糖于其中，搅匀。\r\n④将蛋黄分别加入，搅拌至发泡状态。\r\n⑤将筛好的面粉加入其中，与调好的蛋液充分搅混拌匀。\r\n⑥加入葡萄干，搅混拌匀。\r\n⑦取容器，内侧薄抹色拉油或黄油，然后灌入调好的面粉，抹平表面；覆盖一纸巾，高火5分钟，待容器凉后取出，切成薄片。', NULL, NULL, NULL, '面粉90克，\r\n 鸡蛋三个，\r\n 葡萄干50克。  白糖70克，\r\n 色拉油或黄油（抹涂用）少许。');
INSERT INTO `recipe` VALUES (5317, 64, '拿破仑派', '①将面皮擀薄放于烤盘内，用叉子在面皮上戳洞使其透气，放进微波炉烤箱，用中低火约烤20分钟，待冷却后备用。\r\n②鸡蛋清和白糖混合打成糊状。\r\n③干淀粉、高筋面粉、核桃碎倒入鸡蛋清内搅拌均匀。\r\n④调理好的生料倒于烤盘内，放进微波炉烤箱以中火烤约15分钟。\r\n⑤待冷却后，分成三等份，将两层涂上白脱淡炼乳，另外一层再摆放于上面，撒上细糖粉即可。', NULL, NULL, NULL, '面皮两片，\r\n 鸡蛋清800克，\r\n 核桃碎500克，\r\n 干淀粉100克，\r\n 高筋面粉50克，\r\n 白脱淡炼乳适量。  白糖700克。');
INSERT INTO `recipe` VALUES (5318, 64, '苹果派', '①苹果洗净，去皮、籽，切丁块备用。\r\n②将白脱奶油、苹果、白糖、精盐、肉桂粉放入器皿中高火3分钟至熟。\r\n③加少量水调匀，倒入材料内勾芡。\r\n④勾好芡的材料倒入脆皮馍内。\r\n⑤生脆皮切成条状，覆盖于苹果派上，擦上蛋黄浆，放进微波炉烤箱以高火烤约10分钟即可。', NULL, NULL, NULL, '烤好的脆皮馍一个，\r\n 苹果三个，\r\n 肉桂粉50克，\r\n 白脱奶油100克，\r\n 干淀粉50克，\r\n 生脆皮一块。  白糖140克，\r\n 精盐少许，\r\n 柠檬半个。');
INSERT INTO `recipe` VALUES (5319, 64, '甜心', '①鸡蛋清与蛋黄分开备用。\r\n②白糖200克倒于蛋黄盆内打至发泡。\r\n③再倒200克白糖于鸡蛋清盆内打至发泡。\r\n④将鸡蛋清和蛋黄及筛过的面粉、干淀粉、淡炼乳混合均匀。\r\n⑤调理好的生料置入器皿内，放进微波炉中，中火烤20分钟。\r\n⑥烤好的成品淋上巧克力酱，装饰杏仁片即可。', NULL, NULL, NULL, '蛋800克，\r\n 面粉200克，\r\n 淡炼乳200克，\r\n 干淀粉20克。  白糖400克，\r\n 巧克力酱、杏仁片少许。');
INSERT INTO `recipe` VALUES (5320, 64, '双色鸡丸汤', '①将番茄用开水烫一下，去皮、籽、蒂，切成片。绿菜叶洗净，用开水烫一下然后漂凉。\r\n②器皿加清水1500克，取一半鸡糁保持本色，挤成直径2厘米长的丸子，用高火将丸子煮熟。另一半鸡糁中加入蛋黄粉搅匀，也用高火煮熟。\r\n③器皿内掺清汤，加精盐、胡椒粉，高火6分钟，烧开待用；番茄、绿菜叶放入另一沸器皿内，烫熟捞入碗内垫底，把鸡丸盛在上面，汤内加入味精，然后舀在鸡丸碗内即可。', NULL, NULL, NULL, '鸡糁300克，\r\n 蛋黄粉20克，\r\n 番茄一只，\r\n 绿菜叶50克。  精盐2.5克，\r\n 味精0.5克，\r\n 胡椒粉0.5克，\r\n 清汤约1000克。');
INSERT INTO `recipe` VALUES (5321, 64, '三鲜鸡糕汤', '①将鸡糁掺适量清汤搅匀，装入抹有猪油的方盘内，刮平成厚约1厘米的正方形，中火蒸8分钟左右，熟后均切成3.3厘米见方的块，起菜时再快速加热。\r\n②火腿、蘑菇、鸡皮切成4厘米长、2厘米宽的薄片。\r\n③器皿内注入清汤1000克，下火腿、蘑菇、鸡皮、胡椒粉、精盐，高火10分钟烧开入味，放入绿菜叶，去掉所有汤汁，取出鸡糕，投入碗内，汤内放味精搅匀，再把清汤灌入碗内即可。', NULL, NULL, NULL, '鸡糁300克，\r\n 熟火腿50克，\r\n 熟鸡皮25克，\r\n 鲜蘑菇100克，\r\n 绿菜叶50克。  精盐5克，\r\n 味精0.5克，\r\n 胡椒粉0.5克，\r\n 清汤1000克，\r\n 猪油少许。');
INSERT INTO `recipe` VALUES (5322, 64, '鸡肉蛋花汤', '①紫菜撕碎，鸡胸肉剁成泥，加入少许蛋白拌匀，蛋打散备用。\r\n②热水与紫菜一起入器皿，加盖高火煮6分钟，依次加入蛋液、鸡泥（先加一些热汤调开），搅拌，再高火3分钟。食用时撒上葱花、胡椒粉，淋上芝麻油即可。', NULL, NULL, NULL, '紫菜三张，\r\n 鸡胸肉100克，\r\n 鸡蛋两个。  精盐两小匙，\r\n 味精一小匙，\r\n 胡椒粉适量，\r\n 芝麻油少许，\r\n 热水五杯，\r\n 葱末一大匙。');
INSERT INTO `recipe` VALUES (5323, 64, '牛肉米汤', '①把葱头切成丝，胡萝卜切片，放下黄油、香叶、胡椒粉、干辣椒，高火8分钟焖熟。\r\n②土豆切块，圆白菜切块，同放器皿内加牛肉汤煮沸，放入焖好的材料，高火2分钟煮沸，撒入米渣，高火2分钟煮沸，加盐调好口味，起汤时在盘内放上熟牛肉片即可。', NULL, NULL, NULL, '熟牛肉500克，\r\n 牛肉汤2500克，\r\n 葱头15克，\r\n 胡萝卜150克，\r\n 圆白菜250克，\r\n 土豆200克，\r\n 米渣100克。  黄油150克，\r\n 香叶两片，\r\n 干辣椒1克，\r\n 胡椒粉2.5克，\r\n 精盐20克。');
INSERT INTO `recipe` VALUES (5324, 64, '竹节汤', '①将鸡腿肉与梅花肉同绞成肉糜，再用刀背敲剁到粘稠。\r\n②干贝用一杯热水浸泡5分钟后，加盖中高火5分钟，压丝剁碎。\r\n③荸●拍碎，挤去水分，与干贝末一起加入肉糜中，并放入所有调料，顺一方向搅拌，产生粘性后，慢慢加入温高汤拌匀，使其呈稀汤状。\r\n④将肉糜与汤灌入竹节内，至8分满，盖上微波薄膜。\r\n⑤六个竹筒圆形放入炉内，高火7分钟即可。', NULL, NULL, NULL, '鸡腿肉250克，\r\n 梅花肉200克，\r\n 干贝15克，\r\n 荸●10克，\r\n 竹节筒六个。  精盐一小匙，\r\n 白酱油一大匙，\r\n 白糖一小匙，\r\n 胡椒粉1/4小匙，\r\n 淀粉两小匙，\r\n 温高汤六杯。');
INSERT INTO `recipe` VALUES (5325, 64, '竹节鸡汤', '①竹筒先泡水2小时后备用。\r\n②香菇用水泡软，取出，汁留用。\r\n③将鸡肉、香菇、荸●剁碎置于锅中，加上调料(A)及香菇汁，用筷子不停地顺一方向拌至粘稠状。\r\n④取竹筒，分别放入肉馅至九分满，覆上微波薄膜，并用胶带固定，高火蒸18分钟即可。', NULL, NULL, NULL, '鸡胸肉400克，\r\n 香菇七朵，\r\n 荸●75克，\r\n 竹筒六个。  水两杯半；\r\n (A)精盐两匙，鸡精半匙，绍酒一大匙。');
INSERT INTO `recipe` VALUES (5326, 64, '鸡丝豌豆汤', '熟鸡去骨切成丝，豌豆用鸡汤高火10分钟。在盘内放鸡丝和豌豆，盛上调好口味的汤即可。', NULL, NULL, NULL, '熟鸡500克，\r\n 豌豆100克，\r\n 鸡清汤2500克。  精盐20克。');
INSERT INTO `recipe` VALUES (5327, 64, '榨菜鸡蛋汤', '①榨菜稍洗一洗，切成丝，放冷水中稍泡，除去咸味，鸡蛋打匀。\r\n②器皿中加入色拉油、榨菜丝，高火40秒，加入肉汤、味精，淋入蛋液，再加入猪油，高火1分钟即可。', NULL, NULL, NULL, '榨菜75克，\r\n 鸡蛋两个。  味精1.5克，\r\n 猪油5克，\r\n 色拉油20克，\r\n 肉汤适量。');
INSERT INTO `recipe` VALUES (5328, 64, '牛肉酸菜汤', '①将胡萝卜去皮洗净切丝，葱头切丝，土豆去皮切角块，器皿内放黄油炒葱头、香叶，炒出香味后放入胡萝卜，焖一会儿下番茄酱。\r\n②待出红油时，放入酸白菜丝，中火20分钟，倒入清汤，下土豆块，烧沸后下盐和味精调好口味。③上汤时汤内放煮牛肉一块，酸白菜上浇酸奶油一汤勺，撒点胡椒粉即可。', NULL, NULL, NULL, '腌酸白菜1000克，\r\n 土豆250克，\r\n 胡萝卜250克，\r\n 葱头250克。  番茄酱250克，\r\n 黄油250克，\r\n 清汤3000克，\r\n 煮牛肉250克，\r\n 酸奶油100克，\r\n 精盐10克，\r\n 味精5克，\r\n 香叶两片，\r\n 胡椒粉少许。');
INSERT INTO `recipe` VALUES (5329, 64, '馄饨汤', '①肉糜加入调料(A)，拌打均匀后包入馄饨皮内备用。\r\n②热水高火煮7分钟至滚后，加入包好的馄饨、所有材料及调料(B)，加盖高火煮4分30秒至开即可。', NULL, NULL, NULL, '小白菜100克，\r\n 芹菜末两大匙，\r\n 馄饨皮150克，\r\n 肉糜100克。  (A)精盐半匙，白糖半匙，味精少量，白胡椒粉少量，芝麻油一匙，葱屑少许；\r\n (B)精盐两匙，味精半匙，黑胡椒粉少许，芝麻油少许，葱花一大匙，葱油酥两大匙。');
INSERT INTO `recipe` VALUES (5330, 64, '苦瓜排骨汤', '①苦瓜洗净去籽切片，排骨切块。\r\n②碗内放入五杯热水及排骨，高火20分钟。\r\n③再放入苦瓜及所有调料，高火5分钟即可。', NULL, NULL, NULL, '苦瓜半条，\r\n 排骨400克。  姜丝一中匙，\r\n 盐一中匙，\r\n 味精1/2小匙，\r\n 热水五杯。');
INSERT INTO `recipe` VALUES (5331, 64, '蔬菜云吞汤', '①菠菜洗净切短，盛筲箕内沥干水；腌菜剁碎。\r\n②猪肉剁碎后，加调料(A)拌匀成馅，用馄饨皮包入适量馅，包成馄饨，用鸡蛋粘口。\r\n③热高汤倒入大汤碗中，高火5分钟，加入调料(B)、馄饨、菠菜及姜片，加盖高火5分钟，取出，放入腌菜，搅匀即可。', NULL, NULL, NULL, '馄饨皮100克，\r\n 猪肉100克，\r\n 腌菜一大匙，\r\n 菠菜100克，\r\n 鸡蛋一个。  姜一片，热高汤四杯；\r\n (A)葱末一大匙，鲜酱油1/2大匙，白糖1/3小匙，湿淀粉一中匙，芝麻油1/2小匙；\r\n (B)芝麻油一小匙，胡椒粉1/2小匙，白糖1/3小匙，精盐适量，色拉油1/2大匙。');
INSERT INTO `recipe` VALUES (5332, 64, '豆芽猪血汤', '①黄豆芽去根洗净；猪血划成小方块，用清水漂净；蒜剁成茸。\r\n②器皿内放入色拉油、蒜茸、葱、姜末，高火1分钟，加入猪血、绍酒，高火煮沸，放入豆芽，高火1分钟，调味即可。', NULL, NULL, NULL, '黄豆芽250克，\r\n 猪血250克。  黄豆芽250克，\r\n 蒜头两瓣，\r\n 色拉油20克，\r\n 绍酒20克，\r\n 葱末10克，\r\n 姜末5克，\r\n 精盐3克，\r\n 味精2克。');
INSERT INTO `recipe` VALUES (5333, 64, '猪爪发菜汤', '①猪爪洗刮净，斩件，加水高火10分钟，取出过冷河。\r\n②蚝豉用清水浸开。\r\n③发菜浸软，滴少许生油搓擦，用清水洗净。\r\n④把适量清水高火6分钟至滚，放入猪爪高火40分钟，加入发菜、蚝豉，中火30分钟，下盐调味即可。', NULL, NULL, NULL, '猪爪700克，\r\n 中等蚝豉50克，\r\n 发菜10克。  精盐少许。');
INSERT INTO `recipe` VALUES (5334, 64, '金银菜猪肺汤', '①洗净猪肺，切件。\r\n②洗净杏仁、蜜枣。\r\n③白菜拣好，洗净；菜干浸开，洗净切段。\r\n④把适量清水高火6分钟至滚，放入全部材料，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '猪肺一个，\r\n 杏仁50克，\r\n 蜜枣六只，\r\n 白菜400克，\r\n 菜干50克。  精盐少许。');
INSERT INTO `recipe` VALUES (5335, 64, '老黄瓜瘦肉汤', '①选用皮色黄澄的老黄瓜，洗净切边去瓜瓤，切成大件。\r\n②瘦肉原块洗净，赤小豆也洗净。\r\n③陈皮浸软，洗净刮去瓤。\r\n④把适量清水高火6分钟至滚，放入老黄瓜、瘦肉、赤小豆、蜜枣、陈皮，中火35分钟，下盐调味即可。', NULL, NULL, NULL, '老黄瓜1500克，\r\n 瘦肉400克，\r\n 赤小豆100克，\r\n 蜜枣四只，\r\n 陈皮一小块。  精盐少许。');
INSERT INTO `recipe` VALUES (5336, 64, '竹芋排骨汤', '①竹芋去外壳，洗净，斜刀切厚片待用。\r\n②芡实、蜜枣洗净。\r\n③排骨斩件，放落滚水中高火3分钟，取出洗净。\r\n④陈皮浸软，刮去瓤，洗净。\r\n⑤把适量清水高火6分钟至滚，放入全部材料及调料，中火40分钟即可。', NULL, NULL, NULL, '竹芋1000克，\r\n 芡实50克，\r\n 排骨500克，\r\n 蜜枣六只，\r\n 陈皮一块。  精盐、味精各少许。');
INSERT INTO `recipe` VALUES (5337, 64, '新藕肉汤', '①莲藕洗净，去皮，削净藕蒂。\r\n②猪肉原块洗净。\r\n③蚝豉用清水浸开；红枣去核；陈皮浸开刮去瓤。\r\n④把适量清水高火6分钟至滚，放入莲藕，中火20分钟，然后加入猪爪、蚝豉、红枣、陈皮，高火20分钟，下盐调味即可。', NULL, NULL, NULL, '莲藕750克，\r\n 猪爪400克，\r\n 中等蚝豉50克，\r\n 红枣六只，\r\n 陈皮一小块。  精盐少许。');
INSERT INTO `recipe` VALUES (5338, 64, '竹蔗猪骨汤', '①竹蔗去皮洗净，斩段。\r\n②红萝卜去皮洗净，切块。\r\n③陈皮浸软，刮去瓤，洗净。\r\n④猪骨放入滚水中高火3分钟，取出洗净。\r\n⑤把适量清水高火6分钟至滚，放入全部材料，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '竹蔗250克，\r\n 红萝卜500克，\r\n 陈皮一小块，\r\n 猪骨500克。  精盐少许。');
INSERT INTO `recipe` VALUES (5339, 64, '参果瘦肉汤', '①将太子参稍洗；无花果洗净切片。\r\n②瘦肉放入滚水中，高火3分钟，取起过冷河。\r\n③把适量清水高火6分钟至滚，放入太子参、无花果、瘦肉、蜜枣，中火40分钟，汤成下盐调味即可。', NULL, NULL, NULL, '太子参50克，\r\n 无花果75克，\r\n 瘦肉250克，\r\n 蜜枣五只。  精盐少许。');
INSERT INTO `recipe` VALUES (5340, 64, '沙虫干瘦肉汤', '①沙虫干用温水浸开，软身时，用剪刀将它剪开，洗净内裹之沙。\r\n②瘦肉放入滚水中高火3分钟，取出洗净。\r\n③洗净淮山、杞子、圆肉。\r\n④把适量清水高火6分钟至滚，放入全部材料及姜片，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '沙虫干150克，\r\n 瘦肉250克，\r\n 淮山、杞子、圆肉各25克。  姜两片，\r\n 精盐少许。');
INSERT INTO `recipe` VALUES (5341, 64, '荸荠蕹菜汤', '①将荸荠削去皮，切片；空心菜切成两段。\r\n②将沸水倒入碗内，放入空心菜、荸荠和调料，高火5分钟即可。', NULL, NULL, NULL, '荸荠10只，\r\n 空心菜（蕹菜）250克。');
INSERT INTO `recipe` VALUES (5342, 64, '南瓜汤', '①将南瓜、洋葱、鸡汤放在器皿内，加上盖，高火10分钟至南瓜软，取出洋葱，将南瓜连汤用搅拌器打烂成奶状。\r\n②黄油放入钵内高火2分钟，倒入面粉拌匀，然后把南瓜汤加入，搅拌至匀，加入鲜奶、豆蔻粉，加上盖，放入炉内高火煮5分钟或至沸，加入精盐、胡椒粉及蛋黄拌匀（加入前先用两大匙南瓜汤调稀）即可。', NULL, NULL, NULL, '南瓜150克（切片），\r\n 洋葱一个（切半），\r\n 鲜奶一杯，\r\n 鸡蛋两个（只取蛋黄），\r\n 面粉一大匙。  鸡汤两杯，\r\n 豆蔻粉1/2小匙，\r\n 黄油25克，\r\n 精盐、胡椒粉少许。');
INSERT INTO `recipe` VALUES (5343, 64, '莲子甜汤', '①白木耳泡水至软。\r\n②把熟花生仁、红枣、黑枣、莲子、熟红豆及蜂蜜两大匙、水一大匙放入炉中，高火5分钟。\r\n③再倒入冰糖、蜂蜜一大匙、水三杯，高火20分钟。\r\n④再加入一大匙蜂蜜，高火6分钟即可。', NULL, NULL, NULL, '白木耳50克，\r\n 熟花生仁100克，\r\n 熟红豆25克，\r\n 芋艿50克（切丁），\r\n 红枣10只，\r\n 黑枣10只，\r\n 莲子10粒。  冰糖一中匙，\r\n 蜂蜜四大匙，\r\n 水三杯加一大匙。');
INSERT INTO `recipe` VALUES (5344, 64, '鸡汤燕窝', '①燕窝放入容器内，用50℃的温水浸泡松软，用镊子拣去绒毛杂质，捞出用清水洗净，沥干水分，盛到器皿内，加入沸水，待其胀发。\r\n②将发好的燕窝加入鸡汤、精盐、味精，高火30分钟，取出即可。', NULL, NULL, NULL, '燕窝75克，\r\n 鸡汤1000克。  精盐5克，\r\n 味精2克。');
INSERT INTO `recipe` VALUES (5345, 64, '莼菜蟹汤', '①将河蟹洗净，中火蒸熟，掀下蟹盖，剔出蟹黄，再横向切开蟹身，用小刀剔出肉；蟹脚顺关节剪开，用木棍碾压挤出肉。\r\n②器皿中加油高火1分钟，爆香葱花、姜片，下蟹黄及肉，烹上绍酒，加盐，高火10分钟炒匀，加清水煮沸，再下莼菜，放味精调味，煮沸勾玻璃芡即可。', NULL, NULL, NULL, '活河蟹两只共约300克，\r\n 莼菜150克。  绍酒、葱、姜、精盐、味精、化猪油、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (5346, 64, '粉葛鲫鱼汤', '①鲫鱼去鳞，去内脏，洗净，抹干水，加入少许油煎至黄色，以去腥味。\r\n②排骨放入滚水中高火3分钟，取出洗净。\r\n③粉葛去皮后洗净切厚块；果皮浸软，刮去瓤洗净。\r\n④把适量清水高火6分钟至滚，放下鱼、排骨、蜜枣、果皮、粉葛，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '鲫鱼400克，\r\n 粉葛500克，\r\n 排骨250克，\r\n 蜜枣三只，\r\n 果皮1/6个。  色拉油、精盐各少许。');
INSERT INTO `recipe` VALUES (5347, 64, '山斑炖瘦肉', '①将山斑鱼刮去鱼鳞，剖好洗净，然后放入器皿中煎透待用。\r\n②瘦肉放入滚水中高火3分钟，取出洗净。\r\n③豆腐用清水略洗。\r\n④把适量清水高火6分钟至滚，放入瘦肉、豆腐、姜，低火20分钟，加入山斑鱼再中火40分钟，用盐调味即可。', NULL, NULL, NULL, '山斑鱼250克，\r\n 瘦肉500克，\r\n 豆腐两件。  姜两片，\r\n 精盐少量。');
INSERT INTO `recipe` VALUES (5348, 64, '蛤蜊鸡汤', '①香菇泡开切丝，鸡肉剁小块备用。\r\n②在一汤碗中装入所有的材料，加盖，高火煮15分钟即可', NULL, NULL, NULL, ' 蛤蜊10只，\r\n 鸡肉200克，\r\n 香菇一朵。  嫩姜丝、精盐适量，\r\n 热水三杯。');
INSERT INTO `recipe` VALUES (5349, 64, '清汤鸡块', '将熟鸡块放在器皿内，加入清汤，低火30分钟，食用时把鸡块放在盘内，盛上汤即可。', NULL, NULL, NULL, '清汤350克，\r\n 熟鸡块75克。');
INSERT INTO `recipe` VALUES (5350, 64, '鹅肝清汤', '将鹅肝（高火8分钟煮熟后取出冷却）及蛋黄格司切成1厘米长的菱形块，分别装在汤盆里。临吃时，浇上滚沸的牛肉汤（事先加入红葡萄酒，调好口味）即可。', NULL, NULL, NULL, '鹅肝250克，\r\n 牛肉清汤2000克，\r\n 蛋黄格司150克。  精盐、胡椒粉、红葡萄酒各适量。');
INSERT INTO `recipe` VALUES (5351, 64, '银耳鹌鹑汤', '①将鹌鹑去净余毛，剖腹，去内脏，洗净后抹绍酒、盐，腌渍20分钟，加水高火6分钟煮沸，撇浮沫，并加绍酒、姜片、葱段，低火25分钟。\r\n②留汤去肉（另外烹饪），汤内放入白木耳、蘑菇及整只煮熟去壳的蛋，调好味再高火6分钟，最后加入味精、番茄片即可。', NULL, NULL, NULL, '鹌鹑一只，\r\n 水发白木耳150克，\r\n 蘑菇50克，\r\n 鹌鹑蛋六个，\r\n 番茄50克。  绍酒12克，\r\n 葱5克，\r\n 姜2克，\r\n 精盐2克，\r\n 味精1克。');
INSERT INTO `recipe` VALUES (5352, 64, '鹌鹑蛋汤', '①虾仁洗净，加少许酒、盐、湿淀粉拌和。\r\n②鹌鹑蛋磕入碗内，加少许盐打匀。\r\n③色拉油高火4分钟将蛋炒熟，加水高火8分钟，再加入虾仁，加上绍酒、味精、葱与姜末，并淋上熟油即可。', NULL, NULL, NULL, '鹌鹑蛋六个，\r\n 虾仁50克。  色拉油20克，\r\n 绍酒5克，\r\n 葱、姜5克，\r\n 熟色拉油、精盐、味精、湿淀粉各适量。');
INSERT INTO `recipe` VALUES (5353, 64, '葱花蛋汤', '①鸡蛋打在碗内搅散，葱切成末。\r\n②器皿中放入油高火1分钟，放汤，加盐，高火5分钟，待汤开时将搅散的鸡蛋液淋入，用手勺抄底轻轻地推动，加酱油、味精，烧开，撒上葱花、胡椒粉即可。', NULL, NULL, NULL, '鸡蛋两个。  葱100克，\r\n 猪油1.5克，\r\n 酱油10克，\r\n 精盐1克，\r\n 胡椒粉1克，\r\n 味精0.5克，\r\n 清汤适量。');
INSERT INTO `recipe` VALUES (5354, 64, '番茄鸡蛋汤', '①番茄洗净后，切成厚片。\r\n②将鸡蛋抽打成鸡蛋糊，略加些盐。\r\n③将番茄片放入器皿中，加入色拉油高火10分钟，炒半熟后加入一碗半清水，高火5分钟煮开后倒入鸡蛋糊，高火4分钟，加入盐、味精和猪油即可。', NULL, NULL, NULL, '鸡蛋1～2个，\r\n 番茄250克。  素油10克，\r\n 精盐1.5克，\r\n 味精0.5克，\r\n 色拉油适量，\r\n 猪油15克。');
INSERT INTO `recipe` VALUES (5355, 64, '补血养颜汤', '①牛肉放入滚水中高火3分钟，取出洗净。\r\n②花生仁用热水烫过，去衣。\r\n③红枣去核洗净。\r\n④陈皮浸软，刮去瓤，洗净。\r\n⑤把适量清水高火6分钟至滚，放入牛肉、花生仁、红枣、陈皮、姜，中火45分钟，加淡奶，用盐调味即可。', NULL, NULL, NULL, '牛肉650克，\r\n 花生仁250克，\r\n 淡奶一小罐，\r\n 红枣12只，\r\n 陈皮一小块。  精盐少许，\r\n 姜两片。');
INSERT INTO `recipe` VALUES (5356, 64, '咸菜排骨汤', '①香菜洗净切短，咸酸菜洗净，切小薄片。\r\n②排骨放入滚水煮5分钟，取出洗净。\r\n③将姜及适量清水放入器皿内高火6分钟至滚，下咸酸菜、排骨，低火20分钟至排骨熟。', NULL, NULL, NULL, '咸酸菜200克，\r\n 贵妃排骨300克（斩件），\r\n 香菜两棵。  姜一片，\r\n 精盐、熟色拉油、酱油各少许。');
INSERT INTO `recipe` VALUES (5357, 64, '粉葛猪爪汤', '①猪爪烧净余毛，洗刮净，斩件，放入滚水中煮10分钟，取出过冷河。\r\n②粉葛撕去皮，洗净横切厚片。\r\n③八爪鱼干、蚝豉分别浸开，洗净。\r\n④把适量清水高火6分钟至滚，放入粉葛、八爪鱼干、陈皮，高火20分钟，加入猪爪、蚝豉，中火20分钟，下盐调味即可。', NULL, NULL, NULL, ' 新鲜猪爪两只，\r\n 粉葛1000克，\r\n 八爪鱼干50克，\r\n 蚝豉30克，\r\n 陈皮一块。  精盐适量。');
INSERT INTO `recipe` VALUES (5358, 64, '八爪鱼粉葛汤', '①粉葛去皮洗净，切件。\r\n②果皮浸软，刮去瓤洗净；蜜枣洗净。\r\n③八爪鱼用清水浸30分钟，洗净。\r\n④猪腿肉放入滚水中，高火3分钟，取起洗净。\r\n⑤把适量清水高火6分钟至滚，放下粉葛、八爪鱼、猪腿肉、蜜枣、果皮，中火30分钟，下盐调味即可。', NULL, NULL, NULL, '粉葛500克，\r\n 八爪鱼50克，\r\n 猪腿肉400克，\r\n 蜜枣四只，\r\n 果皮1/6个。  精盐少许。');
INSERT INTO `recipe` VALUES (5359, 64, '排骨蘑菇汤', '①将每块大排骨用刀背拍松，再敲断骨髓后加绍酒、盐腌渍15分钟。\r\n②器皿中加入沸水，放入大排骨，撇去浮沫，加酒，低火25分钟，加入蘑菇汤，再高火8分钟，放味精调味并投入番茄片，煮沸即可。', NULL, NULL, NULL, '大排骨500克，\r\n 鲜蘑菇汤100克，\r\n 番茄100克。  绍酒15克，\r\n 精盐5克，\r\n 味精3克。');
INSERT INTO `recipe` VALUES (5360, 64, '横舌赤豆葛汤', '①粉葛去皮，切片。\r\n②洗净赤小豆、蜜枣。\r\n③猪横舌切去油脂，与瘦肉同放入滚水中高火3分钟，取出洗净。\r\n④把适量清水高火6分钟至滚，放入赤小豆、粉葛、蜜枣，中火20分钟，加猪横舌、瘦肉，中火20分钟，下盐调味即可。', NULL, NULL, NULL, '猪横舌两条，\r\n 赤小豆50克，\r\n 粉葛750克，\r\n 瘦肉250克，\r\n 蜜枣四只。  精盐少许。');
INSERT INTO `recipe` VALUES (5361, 64, '肉丸豆腐汤', '①猪腿肉洗净剁成末，加上用猪油炒过的洋葱末，加绍酒、盐、胡椒粉、蛋液、干淀粉搅拌成肉馅，挤成小丸子，下温油煎黄。\r\n②油爆香蒜茸，下豆腐丁，加水煮沸，加入丸子、味精，高火3分钟即可。', NULL, NULL, NULL, '猪腿肉150克，\r\n 嫩豆腐400克，\r\n 鸡蛋两个。  色拉油两大匙，\r\n 洋葱50克，\r\n 蒜一瓣，\r\n 化猪油25克，\r\n 绍酒、胡椒粉、精盐、味精、干淀粉各少许。');
INSERT INTO `recipe` VALUES (5362, 64, '清补瘦肉汤', '①把瘦肉放入滚水中高火3分钟，取出洗净。\r\n②洗净全部配料。\r\n③把适量清水高火6分钟至滚，放入全部材料，中火40分钟，放入盐、生抽调味即可。', NULL, NULL, NULL, '瘦肉250克，\r\n 薏米10克，\r\n 莲子5克，\r\n 百合5克，\r\n 淮山10克，\r\n 玉竹5克，\r\n 芡实5克。  精盐、生抽各少许。');
INSERT INTO `recipe` VALUES (5363, 64, '西施排骨汤', '①将淮山、蜜枣洗净。\r\n②把排骨放入滚水中高火3分钟，取起洗净。\r\n③把适量清水高火6分钟至滚，放下排骨、蜜枣、淮山，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '排骨400克（斩件），\r\n 蜜枣六只，\r\n 淮山50克。  精盐少许。');
INSERT INTO `recipe` VALUES (5364, 64, '鞭打圆汤', '①把肉洗净，剁成泥，加入蛋清和少许盐、味精搅匀，氽成小丸子备用。\r\n②鲜汤入器皿烧开，放入细粉条、海米、酱油、盐，然后倒入丸子煮片刻，再放姜末、葱末、味精即可。', NULL, NULL, NULL, '瘦猪肉50克，\r\n 鸡蛋两个（只用蛋清），\r\n 细粉条25克，\r\n 鲜汤500克。  葱10克，\r\n 姜5克，\r\n 海米20克，\r\n 酱油10克，\r\n 精盐、味精各少许。　');
INSERT INTO `recipe` VALUES (5365, 64, '金银菜排骨汤', '①排骨洗净，出水过冷河；杏仁放落滚水中煮5分钟去衣洗净；菜干浸软切短段；白菜洗净。\r\n②把适量清水高火6分钟至滚，放下各料，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '排骨400克（斩件），\r\n 白菜250克，\r\n 白菜干50克，\r\n 杏仁50克，\r\n 果皮1/6个。  精盐少许。');
INSERT INTO `recipe` VALUES (5366, 64, '银耳排骨汤', '①排骨出水过冷河，银耳浸至发大，撕成小朵。\r\n②木瓜去皮去核，洗净切厚件。\r\n③把适量清水高火6分钟至滚，放下各料及姜片，中火40分钟，下盐调味即可。', NULL, NULL, NULL, '半生熟木瓜500克，\r\n 银耳20克，\r\n 排骨400克（斩件）。  姜一片，\r\n 精盐少许。');
INSERT INTO `recipe` VALUES (5367, 64, '金银菜汤', '①猪肝洗净，切成薄片。\r\n②粉肠用去衣蒜肉通过，洗净。\r\n③瘦肉放入滚水中，高火3分钟，取出洗净。\r\n④猪腰纵剖开两边，切去白筋，洗净，切薄片。\r\n⑤白菜摘好，洗净；菜干浸软，洗净切段。\r\n⑥把适量清水高火6分钟至滚，放入粉肠、瘦肉、白菜、菜干、蜜枣，中火40分钟，加入猪肝、猪腰高火烧熟，调适味即可。', NULL, NULL, NULL, '猪肝100克，\r\n 猪粉肠150克，\r\n 瘦肉250克，\r\n 猪腰一只，\r\n 白菜300克，\r\n 菜干100克，\r\n 蜜枣四只。  精盐少许。');
INSERT INTO `recipe` VALUES (5368, 64, '雪耳红萝卜汤', '①红萝卜去皮洗净，切件。\r\n②雪耳用水浸过，洗净。\r\n③瘦肉放入滚水中，高火3分钟，取出过冷河。\r\n④把适量清水高火6分钟至滚，放入红萝卜、瘦肉、雪耳，中火40分钟，汤成下盐调味即可。', NULL, NULL, NULL, '红萝卜500克，\r\n 瘦肉250克，\r\n 雪耳100克。  精盐少许。');
INSERT INTO `recipe` VALUES (5369, 64, '木瓜银耳汤', '①银耳用热水浸软，除去硬蒂，洗净沥干。\r\n②木瓜去皮、核，切成小块后备用，杏仁洗净待用。\r\n③将所有材料及调料放入容器中，加盖高火25分钟即可。', NULL, NULL, NULL, '银耳20克，\r\n 木瓜450克，\r\n 杏仁少许。  冰糖100克，\r\n 热水两杯。');
INSERT INTO `recipe` VALUES (5370, 101, '雪耳鸽蛋汤', '雪耳浸发去蒂，洗净摘小朵。百合、猪瘦肉（切为2件）洗净，鸽蛋煮熟去壳。\r\n先将猪瘦肉、百合入锅，文火再煲约1小时，然后下雪耳继续煲约40分钟，后下鸽蛋再煮片刻，调味食用。\r\n功用：益肺润燥，适宜小儿饮用。', NULL, NULL, NULL, '猪瘦肉100克，雪耳50克，百合60克，白鸽蛋10 只。');
INSERT INTO `recipe` VALUES (5371, 101, '胡萝卜炒肉丝', '①将胡萝卜少净，切成细丝，香菜洗净，切段待用。\r\n②将瘦猪肉剔去筋，切成细丝，放入盆内，加入淀粉50克，精盐5克上浆，用热锅温油滑开捞出。\r\n③将炒菜油放入锅内，热后下入葱姜末炝锅，投入胡萝卜丝煸炒断生，加入肉丝搅拌均匀，再加入酱油、精盐、醋、料酒，炒熟后加入味精、香油、香菜，搅匀出锅即成。 \r\n特 点： 甜香适口，营养丰富，口感软嫩，油而不腻。 \r\n制作关键： 因是幼儿菜肴，在刀工上切得要细，丝切得不要过长。炒菜不能太脆，口不宜过重。', NULL, NULL, NULL, '瘦猪肉500克，胡萝卜1公斤，香菜50克。 \r\n配 料： 炒菜用油80克，滑肉用油1公斤（实耗45克），香油10克，酱油50克，料酒5克，醋10克，味精15克，水淀粉50克。');
INSERT INTO `recipe` VALUES (5372, 101, '西红柿肝末', '1、 将猪肝洗净切碎；西红柿用开水烫一下，剥去皮切碎；葱头剥去皮洗净，切碎待用。\r\n2、 将猪肝、葱头同时放火锅内，加入水或肉汤煮，最后加入西红柿、精盐，使之有淡淡的咸味即成。\r\n3、 注意猪肝、葱头下锅不要煸炒，要立即加肉汤或水煮，味不应太咸，略有咸味即成。\r\n\r\n特点：\r\n色泽美观，味道鲜美。', NULL, NULL, NULL, '猪肝20克，西红柿20克，葱头10克，精盐0.7克。');
INSERT INTO `recipe` VALUES (5373, 101, '西红柿鱼糊', '1、 将净鱼肉放入开水锅内煮后，除去骨刺和皮；西红柿用开水烫一下，剥去皮，切成碎末。\r\n2、 将鸡汤倒入锅内，加入鱼肉同煮，稍煮后，加入西红柿末、精盐，用小火煮成糊状即成。\r\n3、 注意制作时一定要用新鲜鱼肉，同时必须将鱼刺剔净。\r\n\r\n特点：\r\n色泽红艳，味鲜软烂。', NULL, NULL, NULL, '净鱼肉100克，西红柿70克，精盐2克，鸡汤200克。');
INSERT INTO `recipe` VALUES (5374, 101, '蜂蜜橘糊', '1、将橘子洗干净，剥去皮，再把内皮剥去，然后放入容器内研碎。\r\n2、食用时加入蜂蜜搅拌均匀，使其具有一点柔和的酸味。\r\n3、注意，橘子可用广柑代替，最好用无核蜜橘，一定要将橘子研碎。', NULL, NULL, NULL, '橘子，蜂蜜适量');
INSERT INTO `recipe` VALUES (5375, 101, '丝瓜面筋', '丝瓜削皮切片，香菇泡软切片。下油1汤匙放入切碎的葱头文火爆香，再将切片的丝瓜放下，加入上汤及香菇片，文火焖熟后倒入面筋略煮，放入上述调味料炒匀即可。', NULL, NULL, NULL, '丝瓜1条，花生油、上汤各1汤匙，面筋250克，香菇3朵，葱头粒、芫荽少许。盐、胡椒、味精、麻油适量。');
INSERT INTO `recipe` VALUES (5376, 101, '芹菜炒猪肝', '把猪肝切成3厘米左右厚后开水烫一下，放入由生抽、酒、生粉、糖、生姜汁的混合料中腌一下。芹菜洗净切成5厘米长，开水烫一下即捞起沥干水，烧热锅下油4汤匙以猛火炒猪肝，加入芹菜快炒，用盐、胡椒粉生姜汁调味，炒匀即可。', NULL, NULL, NULL, '芹菜300克，猪肝300克，生抽、酒各1汤匙，生粉2茶匙，糖1茶匙，生姜汁1茶匙，油4汤匙，盐、胡椒粉各少许。');
INSERT INTO `recipe` VALUES (5377, 101, '豆角肉松', '青豆角去头尾洗净切碎，板豆腐洗净剁成细粒，加生抽、砂糖少许腌10分钟，瘦肉剁成肉碎，葱切碎。下油2汤匙，放青豆角炒透，溅水炒熟铲起；再放入生油3汤匙，放蒜茸、肉碎爆香，加入沙爹酱炒几下，再加其它调味品炒片刻加青豆角至汁干，加葱花炒匀即可。', NULL, NULL, NULL, '青豆角300克，板豆腐300克，瘦肉150克，葱、蒜茸少许，沙爹汤2汤匙，生抽2汤匙，砂糖1茶匙，生油4汤匙，盐、味精少许。');
INSERT INTO `recipe` VALUES (5378, 101, '涂抹面包片', '1、将面包切成片。\r\n2、根据婴儿的口味，分别在面包片上抹上红果酱、草莓酱、苹果酱、黄油、蜂蜜、香草糖、巧克力丝、碎果仁、调味菜末等，即可给婴儿食用。', NULL, NULL, NULL, '面包适量。辅料 红果酱、草莓酱、苹果酱、黄油、蜂蜜、香草糖、巧克力丝、碎果仁、调味菜末等各适量。');
INSERT INTO `recipe` VALUES (5379, 101, '三色肉丸', '1.将猪肉切碎，剁成泥，分成均匀的3份，放入碗内。 \r\n2.将1份肉泥内加入红曲粉、精盐、葱姜水、鸡蛋半个、鲜汤60克及水淀粉少许搅打上劲，调成红色肉馅。 \r\n3.将1份肉泥内加入菠菜汁、精盐、鸡蛋半个、鲜汤60克及水淀粉少许搅打上劲，调成绿色肉馅。 \r\n4.将另1份肉泥加入鸡蛋清、精盐、葱姜水、鲜汤60克及水淀粉搅打上劲，调成白色肉馅。 \r\n5.将上述3种肉馅分别挤成小丸子，下入温水锅内，等熟捞出。 \r\n6.把炒锅置火上，放入余下的鲜汤，下入三色丸子，加入精盐，味精、料酒，烧开后用水淀粉勾芡即成', NULL, NULL, NULL, '净猪瘦肉300克，鸡蛋1.5个。辅料 菠菜汁15克，红曲粉6克，精盐5克，味精1克，料酒5克，淀粉45克，葱、姜水30克，鲜汤500克。');
INSERT INTO `recipe` VALUES (5380, 101, '鸡汤煮饺子', '1、将鸡肉末放入碗内，加入少许酱油拌匀，再加入洋白菜末、鸡蛋末拌匀制成馅，包成饺子。\r\n2、将锅置火上，放入鸡汤，下入小饺子煮熟后，撒入芹菜末，加入少许酱油，使其具有淡淡的咸味即成。', NULL, NULL, NULL, '小饺子皮5个，鸡肉末15克，切碎的洋白菜15克，芹菜末5克，炒熟搅碎的鸡蛋10克。辅料 鸡汤、酱油各适量。');
INSERT INTO `recipe` VALUES (5381, 101, '红小豆粥', '（1）将红小豆与大米分别淘洗干净。\r\n（2）将红小豆放入锅内，加入适量清水，烧开并煮至烂熟，再加入水与大米一起煮。用大火烧沸后，转用小火，煮至粘稠为止。\r\n（3）将粥内加入适量红糖，烧开盛入碗内，撒上少许糖桂花即成。', NULL, NULL, NULL, '大米50克，红小豆15克辅料：红糖适量，糖桂花少许。');
INSERT INTO `recipe` VALUES (5382, 101, '什锦果盅', '各种时令鲜果洗净去皮，切成半寸大小的棱子块，草蓉可一破两半儿或四半儿，葡萄免切。将切好的果块儿混放在透明玻璃容器中，根据自己及家人的喜好倒入酸奶，以酸奶没过果块为宜，盖好盖子，放在冰箱冷藏室内，一小时后即可食用。', NULL, NULL, NULL, '各种时令鲜果(如苹果、草蓉、菠萝、葡萄、西瓜等)，酸奶。');
INSERT INTO `recipe` VALUES (5383, 101, '珍珠醪糟', '将汤圆粉加少量凉搅拌成类似做疙瘩汤的小碎状，醒糟放在锅里加热，开镑将搅拌好的汤圆疙瘩一把一把到锅里，边撤边搅拌，用文火2——3分钟，待小疙瘩完全漂来，打上碎鸡蛋花，蛋花漂起后关火，撤上桂花、白糖。', NULL, NULL, NULL, '醪糟、汤圆粉、鸡蛋。调料：白糖、桂花。');
INSERT INTO `recipe` VALUES (5384, 101, '果香藕片', '嫩藕洗净刮皮，切成片，在开水中婶一下去生味，立放到凉水中彻底冲凉，捞出后沥余水，放到自己喜欢的果汁中浸，4——5小时，以果汁没过藕片宜，盖好盖子，放在冰箱冷藏室 P做一次可多做一些，随吃随取。', NULL, NULL, NULL, '嫩藕、果汁。');
INSERT INTO `recipe` VALUES (5385, 101, '蛋黄羹', '（1）将熟蛋黄放入碗内研碎，并加入肉汤研磨至均匀光滑为止。 \r\n（2）将研磨好的蛋黄放入锅内，加入精盐，边煮边搅拌混合。 \r\n[制作关键] 蛋黄要研碎、研匀，不能有小疙瘩。此菜适宜3个月以上的婴儿食用。', NULL, NULL, NULL, '煮鸡蛋黄5个，肉汤200克。 [辅料] 精盐3克。');
INSERT INTO `recipe` VALUES (5386, 101, '干烧茄子', '1、茄子洗净，切成长条，用稀盐水浸透（免变黄黑色）备用。 \r\n2、烧红锅，将茄子走油，盛出，备用。倾出多余油份，用来爆香蒜蓉弃去，加入葱花略爆后，放调料及将茄子回锅，埋芡，兜匀便可上碟。', NULL, NULL, NULL, '嫩茄子一斤半，蒜肉数粒，葱花少许。 调味料：老抽半汤匙，盐四分一茶匙，糖一茶匙芡汁调味料：生抽一茶匙，绍酒一茶匙，糖、生粉、清水适量');
INSERT INTO `recipe` VALUES (5387, 101, '西红柿海带汤', '1.将水发海带洗净，切成丝，放入高汤中煮5分钟。 \r\n2.再在高汤中放入奶油、酱油、精盐、鲜柠檬汁、西红柿汁，煮开，倒入汤碗内即可。', NULL, NULL, NULL, '西红柿汁50克，水发海带250克。鲜柠檬2个挤汁备用，奶油50克，酱油、精盐少许，高汤适量。');
INSERT INTO `recipe` VALUES (5388, 101, '肉末', '（1）将将瘦肉片去筋，剁成细未。\r\n（2）将炒锅上火、放入植物油，热后放入肉未不断煸炒，炒至八成熟时，放人葱、姜末和酱油，炒至全熟时，放入味精即成。', NULL, NULL, NULL, '瘦猪肉或瘦牛肉300克，植物油50克、酱油30克，味精少许，葱、姜未各适量。');
INSERT INTO `recipe` VALUES (5389, 101, '腐乳排骨', '(1)葱切段；姜切片；排骨剁成4厘米长的段，洗净，控干水分，放入盆内，加入少许酱油、水淀粉拌匀，用热油炸成金红色捞出（分3次炸）。\r\n(2)将排骨放入锅内，加入水（以漫过排骨为度）、酱油、精盐、白糖、料酒、葱段、姜片、腐乳汤，用大火烧开后，转微火焖至排骨酥烂即成。', NULL, NULL, NULL, '猪排骨1.5公斤。【辅料】酱油130克，精盐5克，白糖50克，腐乳汤80克，料酒10克，葱50克，姜15克，水淀粉80克，植物油2公斤（实耗250克）。');
INSERT INTO `recipe` VALUES (5390, 101, '栗子凉糕', '1.将栗子壳上切成十字口，煮熟捞出，剥去外皮，搓成栗子面。\r\n2.将冻粉洗净，放入铝锅内，加入清水、白糖，熬至溶化，将栗子面倒入搅匀，倒入搪瓷盘内晾凉，切块装盘。\r\n制作关键 在栗子壳上切一十字口，煮熟后好剥。冻粉一定是按比例加水。', NULL, NULL, NULL, '栗子500克。辅料 白糖250克，冻粉25克，清水750克。');
INSERT INTO `recipe` VALUES (5391, 101, '蒸樱桃丸子', '1.将猪肉去筋、洗净，剁成茸状，放入碗内，打入鸡蛋液。再放入精盐、牛奶搅拌均匀成糊状。 \r\n2.把啤酒与白糖熬成咖啡色的糖汁，备用。 \r\n3.把糊状的肉茸均匀地挤成樱桃般大小的丸子，放在瓷碗里，将糖汁浇在丸子上。然后，放入蒸锅内蒸熟。 \r\n4.将金糕切成小方丁备用。', NULL, NULL, NULL, '肥瘦猪肉300克，鸡蛋1个，牛奶250克。啤酒50毫升，金糕25克，白糖50克，生菜叶、精盐各适量。');
INSERT INTO `recipe` VALUES (5392, 101, '三鲜馅包子(1－3岁)', '1)将面粉放入盆内，加入面肥、温水200克和成面团，待酵面发起，加入碱液揉镒，稍饧。\r\n2)将海米切成细末放入盆内，加入猪肉末，酱油、精盐、味精和少许清不搅匀，最后加入香油、熟豆油，切碎的韭菜，拌匀，成为三鲜馅。\r\n3)钭饧过的面团搓成条，揪成50克4个的面剂，擀成中间稍厚、边缘较薄的面皮，包入三鲜馅，捏成包子，码入屉内，上笼用旺火蒸12分钟即熟。 \r\n\r\n特点： 馅鲜香味美，适口。 \r\n制作关键： 海米一定要用水泡涨后再切末拌馅。拌这样馅，酱油不宜放得过多，否则影响鲜味。', NULL, NULL, NULL, '面粉 400克，猪肉末200克，海米100克(水发)，韭菜100克。 \r\n配  料： 香油10克，熟豆油50克，酱油75克，精盐3克，味精4克，姜末10克，碱面3克。');
INSERT INTO `recipe` VALUES (5393, 101, '干菜包(1－3岁)', '1)将猪肉和鸡分别用开水烫一下捞出，用凉水洗净。然后放入锅内，加入酱油、白糖、料酒、清水，开后，转微火煮至肉酥烂时将肉取出，稍凉后，切成豆粒大小的丁。\r\n2)将霉干菜用温水洗净，放入屉内蒸2小时取出，用凉水洗净，切成碎末，放入肉丁内，加入姜末、香油、熟猪油、味精搅匀。冬笋先用水煮熟，切成细末，也加入肉丁内，搅拌成馅。\r\n3)将面粉放入盆内，加入面肥、温水200克和成面团，待酵面发起，加入碱液揉匀，稍饧。\r\n4)将面团揉搓成长条，揪成50克4个的小剂，擀成中间略厚、边缘较苤的面皮。码入屉内用旺火蒸12分钟即', NULL, NULL, NULL, '面粉400克，面肥100克，肥瘦猪肉300克，霉干菜100克，冬笋50克，鸡架1个。 \r\n配  料： 香油20克，熟猪油20克，酱油75克，白糖15克，料酒20克，精盐3克，味精5克，姜末10克，碱面3克。');
INSERT INTO `recipe` VALUES (5394, 101, '小笼包子(1－3岁)', '1)将肉剁或绞成茸，放入盆内，加入酱油、精盐、白糖、料酒、味精、姜末，顺着一个方向搅上劲，使其入味。然后分几次加入清水175-200克，搅打上劲，最后加入香油拌匀待用。\r\n2)将富强粉放入盆内，加入面肥、水200克，和成面团，待酵面发起，加入碱水揉匀揉透，稍饧，揪成50克4个的小剂子，擀成周围薄、中间厚的片，分别包入馅心，捏成18-21个褶的小包子、皮馅比例是：皮15克，馅20克。码入小笼内，上屉，用旺火蒸10分钟即熟。 \r\n特  点： 鲜嫩，味美。 \r\n制作关键： 蒸时用大火。要掌握好蒸包子的时间，时间', NULL, NULL, NULL, ' 富强粉400克，面肥100克，猪肉250克(肥3瘦7)。 \r\n配  料： 香油60克，酱油20克，精盐8克，白糖2克，料酒7克，味精3个，姜末7克，碱面3克。');
INSERT INTO `recipe` VALUES (5395, 101, '肉菜包(1－3岁)', '1) 将面粉放入盆内，加入面肥、温水200克和成面团，待酵在发起，加入碱液，揉匀，稍饧。2)将猪肉剁成绞成茸，放入盆内，加入酱油、精盐、味精、姜末，拌匀后，加水搅城糊状，最后放入葱花、香油、菜馅，拌匀成馅。\r\n3)将面团揉成长条，按每50克2个揪成面剂，将剂按扁，擀成圆皮。然后左手托皮，左手打馅，再用右手拇指和食指边包边捏褶，收严剂口即成。4)将包子码入屉内，用旺火蒸15分钟即熟。 \r\n特  点： 松软，口味鲜香。 \r\n制作关键： 拌馅时要将菜馅挤净水，不然拌好馅后容易出汤。包包子进，不管捏褶多少，一定要', NULL, NULL, NULL, '面粉400克，面肥100克，猪肉300克，青菜馅(白菜、西葫芦、豆角、匣香等均可)250克。 \r\n配  料： 酱油75克，精盐8克，香油25克，味精 3 克，葱花25克，姜末4克，碱面3克。');
INSERT INTO `recipe` VALUES (5396, 101, '天津包(1－3岁)', '1)将面粉调成两种面团，夏天调一半加面肥，发成酵面，一半用水面(冬天则调七成酵面、三成水面)，两样面团再揉在一起，对碱，揉至光润，成为嫩酵面团。\r\n2)将猪肉剁成小碎丁(或用大眼绞肉机绞碎)，放入姜末、精盐搅匀，加入酱油再搅，略有粘性，加水打馅，水量根据肉质(新鲜程度)而定，最多加水175克左右，要分几次加入，边加边搅，每次加水不要太多，顺着一个方向搅，搅至水加完，呈粘稠状，临包时再将香油、味精、葱末倒入拌匀。\r\n3)将面团放在案板上，搓揉成条，揪50克4个的小剂，按扁，擀成圆皮，包入馅，收口捏褶16-1', NULL, NULL, NULL, '富强粉400克，面肥100克，猪肉250克(肥3瘦7)。 \r\n配  料： 香油25克，酱油75克，葱末25克，姜末3克，味精3克，精盐3爽，碱面3克。');
INSERT INTO `recipe` VALUES (5397, 101, '什锦糖包(1－3岁)', '1)将面粉放入盆内，加入面肥、温水和成面团，视酵面发起，加入碱液，揉匀，稍饧。 \r\n2）将芝麻洗净，炒熟放入盆内，核桃仁、瓜籽仁切碎放入盆内，果脯、葡萄干、蜜枣、瓜条、京糕、青梅切成小丁，与白糖、桂花地起投入，拌匀成什锦糖馅。\r\n3)将面团揉搓成条，揪成25克1个的面剂，将剂按成中间稍厚、边缘薄的片。然后左手托皮，右手打馅，包成糖包。4)将生坯码入屉内，用旺火蒸15分钟即熟。 \r\n特  点： 暄软香甜，十分可口，营养丰富。 \r\n制作关键： 面不宜发得太老，以刚发就使碱为度。所用辅料必须配够10样，但具体放', NULL, NULL, NULL, '面粉400克，面肥100克，白糖85克。 \r\n配  料： 碱面3克，核桃仁、花生仁瓜籽佳期、芝麻、果脯、葡萄干、蜜枣、瓜条、京糕、青梅各5克，桂花少许，温水200克。');
INSERT INTO `recipe` VALUES (5398, 101, '芝麻包(1－3岁)', '1)将白糖、果酱放入小盆内拌匀，加入熟芝麻、熟面粉轻搓成馅。\r\n2)将面粉放入盆内加入面肥、温水，和成面团，视酵面发起，加入碱液，揉匀，搓成3百米粗细的长条，按25克1个揪剂，将剂按成中间稍厚、边缘稍薄的小圆皮。\r\n3)左手托皮，左手打馅，捏成月牙形，在剂口处锁上花边，再将两角捏合在一起，呈半圆形，并在顶部沾少许青红丝。把生坯码入屉内，用旺火蒸15分钟即熟。 \r\n特  点： 暄软，香甜，造型美观。 \r\n制作关键： 面团使多少碱，视面团发的老嫩随时增减。芝麻包成形时，花边要锁均匀，而且要捏紧，防止漏馅。', NULL, NULL, NULL, '面粉 400克，面肥100克，白糖150克，熟芝麻150克，果酱25克。 \r\n配  料： 熟面粉 25克，青红丝少许，碱面3克，温水200克。');
INSERT INTO `recipe` VALUES (5399, 101, '红果包(1－3岁)', '1)将京糕切厉细泥，放入小盆内，加入白糖、青梅末、桂花少许，拌匀成馅。\r\n2) 将面粉放放盆内，加入面肥，温和成面团，视酵面发起，加入碱液，揉匀后搓成长条，揪成25克1个的面剂，将剂按成中间稍厚、边缘稍薄的圆片。\r\n3)然后左手托皮，左手打馅，收晋升剂口，把生坯整理成塔形，再用剪刀从生坯底部逐层剪蓐三角形(一层5个、二层4个、三层3个、四层2个、五层1个)，塔顶处再用剪刀剪出小花嘴形，整个生坯呈石榴形。\r\n4)把生坯码入屉内，用旺火蒸15分钟即熟。 \r\n特  点： 酸甜适口，造型美观。 \r\n制作关键： 面', NULL, NULL, NULL, '面粉400克，面肥100克，白糖100克，京糕60克。 \r\n配  料： 青梅5克，桂花少许，碱面3克，温水200克。');
INSERT INTO `recipe` VALUES (5400, 101, '豆沙包(1－3岁)', '1)将面粉放入盆内，加入面肥、温水和成面团，待酵面发起，加入碱液，揉匀，稍饧。\r\n2)把面团搓成条，按25克1个揪成面剂，将剂按扁，擀成中间稍厚、边缘稍薄的小圆皮。然后左手托皮，右手打馅，收紧剂口呈馒头状，最后用脾轻轻压成椭圆形。\r\n3)把生坯摆入屉内，用旺火蒸15分钟即熟。 \r\n特  点： 暄软，香甜。 \r\n制作关键： 面团使好碱后，稍饧一下，蒸出的豆包色白、暄软。豆包包好后要滚压成鸭蛋型，圆豆包不美观。', NULL, NULL, NULL, '面粉400克，面肥100克，豆沙馅300克。 配  料： 碱面3克，温水200克。');
INSERT INTO `recipe` VALUES (5401, 101, '盘转糕(1－3岁)', '1)将面粉放入面盆内，加入面肥、温水和成面团，待酵面发起，加入碱液，揉匀，稍饧。\r\n2)把面团搓成粗条，按100克1个揪成面剂，将剂按扁，擀成厚0.5厘米、宽8厘米的长方形面片，再将豆沙馅均匀地抹在面片上，然后从上至下，卷成筒状的长条，把两压晋升，从一头向另一头盘起，成圆锥形，将后边剂头压在宛若部边缘即成。\r\n3）视蒸锅上气时，将生坯码入屉内，撒上青红丝，用旺火蒸25分钟即熟，取下切十字刀，25克1块即可。 \r\n特  点： 暄软甜香，美观大方。 \r\n制作关键： 面团使好碱后，稍饧一下，蒸出来面白、暄软。用', NULL, NULL, NULL, '面粉400克，面肥100克，豆沙馅250克。 配  料： 青红丝少许，碱面3克，温水200克。');
INSERT INTO `recipe` VALUES (5402, 101, '千层糕(1－3岁)', '1)将精粉放入盆内，加入面肥、温水和成面团，使其发酵，至面半发起时对入碱水，揉匀稍饧待用。\r\n2)将面团擀成0.3厘米厚的薄片，抹上一层油，撒上精盐，卷成卷，用手按成宽10厘米的条，用旺火蒸25分钟取出，稍晾一下，从中间顺长切开，然后切成方块即在。 \r\n特  点： 层次分明，筋软适口。 \r\n制作关键： 面要发得嫩些，片要擀得薄些，这样可使糕层多，筋软。', NULL, NULL, NULL, '精粉400克，面肥100克。 \r\n配  料： 植物油50克，精盐10克，碱面3克，清水200克。');
INSERT INTO `recipe` VALUES (5403, 101, '鸳鸯糕(1－3岁)', '1)将面粉放入盆内，加入面肥、水和成在团，发酵待用。\r\n2)将发面对好碱揉匀，分成两块同样大的剂子，一块揉白糖，一块揉红糖，同时各加入少许桂花揉匀，稍饧。\r\n3)将两块面均擀成1厘米厚的方片，两片摞在一起，然后将青梅、葡萄干摆在上边，上屉用旺火，沸水蒸25分钟即熟。晾凉，切成菱形块即成。 \r\n特  点： 暄软适口，色泽美观。 \r\n制作关键： 两色糕的面剂要均匀，擀的薄厚要一致，用旺火、沸火蒸。此面点所用的发面，要掌握在刚发即可使用。', NULL, NULL, NULL, '面粉400克，面肥100克。 配  料： 白糖50克，桂花少许，青梅、葡萄干各10克，碱3克，清水200克。');
INSERT INTO `recipe` VALUES (5404, 101, '碗蜂糕(1－3岁)', '1)将面粉放入盆内，加入面肥水调成软面团，发酵，要发老一些，然后加碱水、白糖拌匀。\r\n2)准备5个小碗，洗净后抹点油，把软面团分到5个碗内，上面撒上京糕丁、青梅丁。上屉，用旺火蒸30分钟即成。吃时一切两半即可。 \r\n特  点： 松软适口，香甜味美。 \r\n制作关键： 面要和软些，发大酵，用旺火蒸。', NULL, NULL, NULL, '面粉400克，面肥100克。 \r\n配  料： 白糖100克，京糕75克，青梅25克，碱面7.5克，油少许，水300克。');
INSERT INTO `recipe` VALUES (5405, 101, '西红柿汁(4－5个月)', '(1)将成熟的西红柿洗净，用开水烫软去皮，然后切碎，用清洁的双层纱布包好，把西红柿汁挤入小盆内。\r\n(2)将白糖放入汁中，用温开水冲调后即可饮用。 \r\n特 点： 酸甜适口，营养丰富. \r\n制作关键： 要选用新鲜、成熟的西红柿做原料。可用纱布挤汁，也可用汤匙压出汁。可用白糖，也可用蜂蜜调口。此菜汁适宜3-4个月的婴儿食用。', NULL, NULL, NULL, '西红柿500克，白糖100克。 \r\n配 料： 温开水350克。');
INSERT INTO `recipe` VALUES (5406, 101, '胡萝卜汤(4－5个月)', '(1)将胡萝卜洗净，切碎，放入钢精锅内，加入水，上火煮沸约20分钟。\r\n(2)用纱布过滤去渣，加入白糖，调匀，即可饮用。 \r\n特 点： 味略甜，营养丰富。 \r\n制作关键： 要选用新鲜的胡萝卜作原料。操作时，要切碎、煮烂，去净胡萝卜渣。此菜汁适宜4-5个月的婴儿食用', NULL, NULL, NULL, '胡萝卜500克，白糖50克。 配 料： 清水500克。');
INSERT INTO `recipe` VALUES (5407, 101, '菜水(4－5个月)', '(1)将菠菜、油菜、白菜任选一种，洗净，切碎。 \r\n(2)将钢精锅放在火上，加入清水、碎菜，盖好锅盖烧开，稍煮，将锅离火，用汤匙压菜取汁，加入精盐少许，即可食用。 \r\n特 点： 用上述蔬菜制成的菜汁，含有丰富的钙、铁和维生素。 \r\n制作关键： 要选用新鲜的蔬菜做原料。菜要切碎、煮烂。只取菜汁，不要带菜渣。此菜汁适宜4-5个月的婴儿食用。', NULL, NULL, NULL, '菠菜(油菜、白菜均可)500克。配 料： 精盐8克，清水500克。');
INSERT INTO `recipe` VALUES (5408, 101, '山楂水(4－5个月)', '(1)将山楂片用凉水快速洗净，除去浮灰，放入盆内。\r\n(2)将开水沏入盆内，盖上盖闷上，至水温下降到微温时，把山楂水盛入杯中，加入白糖，搅至白糖溶解，即可饮用。 \r\n特 点： 生津止渴，酸甜适口，消食健胃。对增添婴儿的食欲大有益处。 \r\n制作关键： 山楂片要迅速用水冲净，否则，冲慢了山楂片一变软，就洗不干净了。沏山楂片的水要用水，晾温后饮用。此水适宜5个月以上的婴儿食用。', NULL, NULL, NULL, '山楂片500克，白糖100克。配 料： 开水1.5公斤。');
INSERT INTO `recipe` VALUES (5409, 101, '鱼松(4－5个月)', '(1)将鱼去鳞，去内脏，洗净，放在锅内蒸熟，去骨、去皮待用。\r\n(2)将锅放在小火上，加花生油，把鱼肉放入锅内边烘边炒，至鱼肉香酥时，加入精盐、料酒、酱油、白糖，再翻炒数下，即成鱼松。 \r\n特 点： 味咸甜，松香，入口即化。 \r\n制作关键： 炒鱼松时火不宜太旺，要边烘边炒。此菜适宜10个月以上的婴儿食用。', NULL, NULL, NULL, '鲜鱼1条(750克左右)。配 料： 花生油40克，酱油15克，精盐3克，白糖4克，料酒3克。');
INSERT INTO `recipe` VALUES (5410, 101, '蜂蜜桔糊(4－5个月)', '(1)将桔子洗干净，剥去皮，再把内皮剥去，然后放入容器内研碎。\r\n(2)食用时加入蜂蜜搅拌均匀，使其具有一点柔和的酸味。 \r\n特 点： 酸甜适口，是补充婴儿维生素C最好的食物。 \r\n制作关键： 桔子可以用广柑、招柑和葡萄代替，最好用无核蜜桔做原料。操作时要把桔子研碎，不能太 整。', NULL, NULL, NULL, '桔子300克。蜂蜜30克。');
INSERT INTO `recipe` VALUES (5411, 101, '什锦蛋羹(4－5个月)', '(1)将鸡蛋磕入盆内，加盐4克和1公斤温开水搅匀待用。\r\n(2)锅内加水，放在旺火上烧开，把鸡蛋盆放入屉内，上锅蒸15分钟，成豆腐脑状待用。\r\n(3)炒锅内放入1.5公斤清水，水开后放入海米末、菠菜末、番茄酱或西红柿末、精盐4克，勾芡淋入香油即成。 \r\n特 点： 色泽鲜艳，质软嫩，味鲜美，营养丰富。 \r\n制作关键： 蛋液内要加凉开水或温水，不能加凉水。蒸时用旺火，防止蒸过火、蒸老。勾什锦汁时不能放酱油，芡不要勾得太稠。吃时茨浇蛋羹上。此菜适宜8个月以上的婴儿食用。', NULL, NULL, NULL, '鸡蛋10个，海米25克，番茄酱(或鲜番茄)125克，菠菜末125克。香油3克，水淀粉15克，精盐8克。');
INSERT INTO `recipe` VALUES (5412, 101, '碎菜(4－5个月)', '(1)将菜洗净，切碎待用。\r\n(2)锅内加入油，热后，下入花椒面、酱油炸锅，随即放入碎菜，用旺火急炒，待菜烂时，放入精盐、味精即成。 \r\n特 点： 色艳味美，营养丰富。 \r\n制作关键： 此菜以绿叶菜为主，取其一种，也可几种合烹。操作时要先洗后切，旺火急炒。此菜适宜7个月以上的婴儿食用。', NULL, NULL, NULL, '油菜(菠菜、白菜也可)300克。植物油40克，花椒面少许，酱油10克，味精3克，精盐6克。');
INSERT INTO `recipe` VALUES (5413, 101, '鸡肝末(4－5个月)', '(1)将鸡肝洗净，放入锅内稍煮一下，除去血后再换水煮10分钟，然后把鸡肝外的薄皮剥去，切成末待用。\r\n(2)将鸡肝末放入锅内，加入鸡汤稍煮一会儿，加入酱油和蜂蜜使之入味即成。 \r\n特 点： 味甜咸，营养极为丰富。 \r\n制作关键： 鸡肝放入锅内加水煮，必须换水，去掉血。待鸡肝煮熟后再切成碎末。做好后不能太甜或太咸，应使其具有淡淡的甜、咸味道。此菜适宜5个月以上的婴儿食用。', NULL, NULL, NULL, '鸡肝150克。鸡汤200克(无盐)，酱油10克，蜂蜜10克。');
INSERT INTO `recipe` VALUES (5414, 101, '三色肝末(4－5个月)', '(1)将猪肝洗净切碎；葱头剥去外皮切碎；胡萝卜洗净切碎；西红柿用开水烫一下，剥去皮切碎;菠菜择洗干净，切碎待用。\r\n(2)把切碎的猪肝、葱头、胡萝卜放入锅内加肉汤煮熟，最后加入西红柿、菠菜、精盐，继续煮片刻即成。 \r\n特 点： 此菜有红、白、绿、黑等颜色，颇为美观，营养全面。不能下锅过早。此菜适宜5个月以上的婴儿食用。. \r\n制作关键： 菜、肝下锅不要煸炒，必须加汤煮。西红柿、菠菜不能下锅过早。此菜适宜5个月以上婴儿食用。', NULL, NULL, NULL, '猪肝50克，葱头100克，胡萝卜50克，西红柿50克，菠菜25克。精盐5克，肉汤适量。');
INSERT INTO `recipe` VALUES (5415, 101, '鱼肉末(4－5个月)', '((1)将鱼肉去皮、去骨刺，放入盘内，上锅蒸熟。\r\n(2)将鱼肉取出捣烂，加少许盐拌匀即可。\r\n(3)同烂米粥或面片一起食用 \r\n特 点： 软嫩，味鲜，营养丰富。 \r\n制作关键： 做此菜要用新鲜的鱼，必须把鱼刺剔净。', NULL, NULL, NULL, '净鱼肉100克(鲤鱼、草鱼均可)。 精盐1克。');
INSERT INTO `recipe` VALUES (5416, 101, '浓米汤(4－5个月)', '将大米、小米、高梁米取其一种，淘洗干净，放入锅内，添入水，煮成烂粥，撇取米汤饮用(注：在上午10时的喂奶时间添用，每天一次，每次一个婴儿两汤匙，以后逐渐增加到4汤匙)。 \r\n特  点： 有浓厚的米香味，流质，适宜5个月的婴儿食用。 \r\n制作关键： 开锅后用微火熬到米开花、米汤发粘。', NULL, NULL, NULL, '大米粉(小米、高梁米均可)250克。清水2.5公斤。');
INSERT INTO `recipe` VALUES (5417, 101, '烂米粥(4－5个月)', '将米淘洗干净，放入锅内，添入水，用旺火烧开后，转微火煮透，熬至熟烂成糊状时即成(注：每日可喂1-2次，每次一个婴儿1-2汤匙，逐渐增加到每次4汤匙，并可在粥内加蛋黄泥、肝末、鱼肉末等)。 \r\n特  点： 有浓厚的米香味，半流质，适宜6个月的婴儿食用。 \r\n制作关键： 开锅后用微火熬烂，汤要发粘。', NULL, NULL, NULL, '大米(小米也可)250克。清水2.5公斤。');
INSERT INTO `recipe` VALUES (5418, 101, '肉末菜粥(4－5个月)', '1)将米淘洗干净，放入锅内，加入水，用旺火烧开后，转微火煮透，熬成粥。\r\n2)将绿叶蔬菜切碎，然后将油倒入锅内，下入肉末炒散，葱姜末、酱油炒匀，投入青菜炒几下，放入米粥内，加入精盐尝好味，同熬煮一下即成。 \r\n特  点： 粥稠粘，肉末香，咸淡适口，适宜10个月的婴儿食用。 \r\n制作关键： 熬粥不要放碱，以免破坏营养。粥要熬至稠粘。肉末煸炒一下再与粥同熬。', NULL, NULL, NULL, '大米或小米250克，肉末150克，青菜200克。 \r\n配  料： 植物油50克，酱油25克，精盐10克，葱、姜末少许，水2.5公斤。');
INSERT INTO `recipe` VALUES (5419, 101, '蛋黄粥(4－5个月)', '1)将大米淘洗干净，放入锅内，加水用旺火煮开后，转微火熬成粘粥。\r\n2)把蛋黄放入碗内，研碎后加入粥锅内，煮几分钟即成。 \r\n特  点： 粥粘，有浓醇的米香味，富含铁质，适宜6个月以上的婴儿食用。 \r\n制作关键： 粥要熬至粘稠，米要煮烂，蛋黄研碎后再放入锅内煮。', NULL, NULL, NULL, '大米250克，蛋黄3个。清水 2.5公斤');
INSERT INTO `recipe` VALUES (5420, 101, '鸡肉粥(4－5个月)', '1)将米淘洗干净，放入锅内，倒入清水用旺火煮开，转微火熬至粘稠待用。\r\n2)将油倒入锅内，下入鸡肉末炒散，加入葱姜末、酱油搅匀，倒入米粥锅内，加入精盐，尝好口，用微火煮几分钟即成。 \r\n特  点： 粘稠，味香，适宜7-8个月的婴儿食用。 \r\n制作关键： 粥一定要熬烂、发粘，鸡肉末煸炒入味后再与粥同熬。', NULL, NULL, NULL, '大米250克，鸡肉末150克。植物油50克，酱油30克，精盐10克，葱、姜末少许，清水2.5公斤。');
INSERT INTO `recipe` VALUES (5421, 101, '鱼肉松粥(4－5个月)', '1)将大米淘洗干净，放入锅内，倒入清水用旺火煮开，转微火熬至粘稠待用。\r\n2)将菠菜用开水烫一下，切成碎末，放入粥内，加入鱼肉松、精盐、尝好口，用微火熬几分钟即成。 \r\n特  点： 粥粘，味鲜而香，营养丰富。适宜7-8个月的婴儿食用。 \r\n制作关键： 粥要熬烂、熬粘，鱼肉松，菠菜放入粥内一熬即可，不要熬的时间过长。', NULL, NULL, NULL, '大米250克，鱼150克，菠菜100克。精盐6克，清水2.5公斤。');
INSERT INTO `recipe` VALUES (5422, 101, '肉末软饭(4－5个月)', '1)将米淘洗干净，放入小盆内，加入清水，上笼蒸成软饭待用。\r\n2)将茄子、葱头、芹菜择洗干净，均切成末。3)将油倒入锅内，下入肉末炒散，加入葱姜末、酱油搅炒均匀，加入茄子末、葱头末，芹菜末煸炒断生，加少许水、精盐，放入软米饭，混合后，尝好口，稍焖一下出锅即成。 \r\n特  点： 味道咸香，饭软烂，营养丰富，适宜9个月以上的婴幼儿食用。 \r\n制作关键： 饭要蒸成软饭，菜、肉要切末，饭菜混凝土合后要烧透、煮烂。这样做法所用的菜可千变万化，具体放哪种菜适幼儿口味、营养素需要，可根据时令菜变化灵活掌握。肉末可改为鸡', NULL, NULL, NULL, '大米400克，茄子500克，葱头100克，芹菜50克，瘦猪肉末150克。植物油50克，酱油60克，精盐10克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5423, 101, '豆腐软饭(4－5个月)', '1)将大米淘洗干净，放入小盆内加入清水，上笼蒸成软饭待用。\r\n2)将青菜择洗干净切成末；豆腐放入开水中煮一下，切成末。\r\n3)将米饭放入锅内，加入 肉汤一起煮，煮软后加入豆腐、青菜末稍煮即成。 \r\n特  点： 饭软烂，味鲜香，营养丰富，适宜9-11个月的婴儿食用。 \r\n制作关键： 饭要软烂，菜要切碎。主要靠各种肉汤提味。青菜是指菠菜、油菜、芹菜等绿叶蔬菜。', NULL, NULL, NULL, '大米400克，豆腐250克，青菜250克。炖肉汤(炖鱼汤、炖鸡汤、炖排骨汤均可)适量。');
INSERT INTO `recipe` VALUES (5424, 101, '挂面汤(4－5个月)', '1)将猪肝切末；菠菜择洗干净切末；挂面切成短段儿。\r\n2)将鸡汤倒入锅内，加入 挂面、酱油、精盐一丐煮，视挂面煮软后，加入肝末、菠菜稍煮，再将鸡蛋调散后淋和锅内，滴和香油出锅即成。 \r\n特  点： 面条软烂，汤味鲜香，营养丰富，适宜6个月以上的婴儿食用。 \r\n制作关键： 面条一定要切短段再煮，而且要煮烂。挂面做好后，最后不用带很多汤。', NULL, NULL, NULL, '挂面(龙须面)400克，熟猪肝150克，鸡蛋150克，菠菜150克。白鸡汤(骨头汤) 1公斤，香油10克，酱油40克，精盐10克。');
INSERT INTO `recipe` VALUES (5425, 101, '肉末面条(4－5个月)', '1)将面粉加冷水和成硬面团，擀成薄片，再切成极细的条；瘦肉剁志末；菠菜择行干净切末。2)肉末放入碗内，加入葱姜末、酱油、香油调匀待用；将清水倒入锅内，开后下入面条，并加入喂好的肉末搅匀，再加入精盐、菠菜，稍煮即成。 \r\n特  点： 面条细软，汤味鲜美。 \r\n制作关键： 面条要切匀切细，最后汤不要带得太多，但也不能太干，味要香，口不要过重。', NULL, NULL, NULL, '面粉400克，瘦肉200克，菠菜200克。香油15克，酱油50克，精盐10克，味精3克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5426, 101, '鸡蛋面片汤(6－9个月)', '1)将面粉放入盆内，加入鸡蛋液，各成面团，揉好擀成薄片，切成小象眼块待用；菠菜择洗干净切末。\r\n2)将锅内倒入适量水，放在火上烧开，然后把面片下入，煮好后，加入菠菜末、酱油、精盐、味精，滴入香油即成。 \r\n特  点： 面条柔软、滑润，汤味鲜美，适宜6个月以上的婴儿食用。 \r\n制作关键： 此面片是用鸡蛋和面，如鸡蛋少，可加少许水调匀。面擀得要薄，片切得要小，要煮烂。', NULL, NULL, NULL, '面粉400克，鸡蛋4个，青菜200克。香油15克，酱油20克，精盐10克，味精3克。');
INSERT INTO `recipe` VALUES (5427, 101, '高汤水饺(9－11个月)', '1)将菜剁成碎末，挤去水分；猪肉剁成茸，加入酱油、精盐、味精、葱姜末拌匀，再加入适量的水调成糊状，最后放入猪、香油、菜末拦成馅待用。\r\n2)将面粉放入盆内，加冷水250克和成面团，揉匀，搓成细条，按生50克(干面)10个剂下剂，用面杖擀成小圆皮，加入馅，包成小饺子待用。\r\n3)先用开水将饺子煮至八成熟捞出，放入鸡汤内煮，加入精盐、味精、紫菜即成。 \r\n特  点： 汁多不腻，滋味鲜美，适宜9-11个月的婴儿食用。 \r\n制作关键： 饺子皮要薄，个要小，制成小巧玲珑的饺子，上火要多煮一煮。', NULL, NULL, NULL, '面粉500克，猪肉350克，青菜150克。 \r\n配  料： 猪油50克，香油25克，酱油50克，精盐15克，味精5克，葱、姜末少许，紫菜10克，鸡汤1公斤。');
INSERT INTO `recipe` VALUES (5428, 101, '疙瘩汤(9－11个月)', '1)将鸡蛋清与面粉和成稍硬的面团，揉匀，擀成薄片，切成黄豆粒大小的丁，撒入少许面粉，搓成小球。\r\n2)虾仁切成小片，香菜切末，菠菜切末待用。\r\n3)将高汤倒入锅内，放入虾仁、精盐、味精，开后下入面疙瘩，煮熟，淋入鸡蛋黄，加入香菜末、菠菜末，滴入香油，盛入盆内即成。 \r\n特  点： 滑润，汤鲜味美，营养丰富，适宜9-11个月婴儿食用。 \r\n制作关键： 面疙瘩搓好后，要将面粉抖净，这样煮熟后汤清，不乱汤。因是婴幼儿食用，面疙瘩下锅后要多煮一煮。', NULL, NULL, NULL, '面粉400克，鸡蛋清8个，鸡蛋黄4个，虾仁100克，菜叶200克，香菜100克。高汤1公斤，精盐15克，味精5克，香油10克。');
INSERT INTO `recipe` VALUES (5429, 101, '片儿汤(9－11个月)', '1)将面粉放入盆内，加入水200克调成面团，稍饧，擀成大薄片，切成小象眼块待用。\r\n2)将青菜择洗干净，切成末；鸡蛋磕入碗内调散。\r\n3)将植物油倒入锅内，下入葱姜末炝锅，投入肉末煸炒玛变色，加入酱油、水，开后下入面片，加入精盐、味精、青菜末，再开起时，撇去浮沫，淋入蛋液，视蛋片浮起，滴入香油，盛入盆内即成。 \r\n特  点： 滑软，汤鲜味香。 \r\n制作关键： 面片要小一点，要煮烂。蛋液要往开锅的地点淋，这样淋出的蛋片整齐、美观。', NULL, NULL, NULL, '面粉400克，猪肉末150克，鸡蛋3个，青菜200克，葱、姜末少许。 \r\n配  料： 植物油40克，香油10克，酱油30克，精盐10克，味精3克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5430, 101, '红小豆泥(9－11个月)', '1)将红小豆择洗干净，放入锅内，加入水，用旺火烧开后，加盖转小火焖烂待用。\r\n2)煮豆越烂越好，这样才可除去豆爰气味，并容易搅烂。\r\n3)炒沙时要不停地擦着锅底搅炒，并随着豆沙趋于成熟时，把火改小，以免炒焦而生苦味。 \r\n特  点： 香甜细软，含有丰富的B族维生素及铁质，适宜10个月以上的婴儿食用。可同粥一起食之。 \r\n制作关键： 1)煮焖豆时，必须凉水下锅，旺火烧开，小火焖煮。否则，容易把豆烧僵，影响起沙。2)煮豆越烂越好，这样才可除去平腥气味，并容易搅烂。3)炒沙时要不停地擦着锅底搅炒，并随着豆沙趋于', NULL, NULL, NULL, '红小豆500克，红糖500克。清水1.5公斤。');
INSERT INTO `recipe` VALUES (5431, 101, '水果藕粉(5个月以上)', '1)将藕粉和水调匀；水果切成极细的末待用。\r\n2)将藕粉倒入锅内，用微火慢慢熬煮，边熬边搅拌，直到熬至透明为止，最后加入切碎的水果，稍煮即成。 \r\n特  点： 味香甜，易于消化吸收，营养丰富，适宜5个月以上的婴儿食用。 \r\n制作关键： 水果一定要洗净，切碎，最好用小勺刮成水果泥。另外，在熬藕粉时注意不要巴锅。', NULL, NULL, NULL, '藕粉200克，苹果(桃、杨梅罐头)300克。清水800克。');
INSERT INTO `recipe` VALUES (5432, 101, '蜂蜜藕粉(5个月以上)', '1)将藕粉研细，不要有小疙瘩，然后将藕粉和水调匀待用。\r\n2)将调好的藕粉倒入锅内，用微火慢慢熬煮，注意不要巴锅，边煮边搅拌，直至呈透明糊状为止。停火后加入蜂蜜。 \r\n特  点： 香甜味美，易于水消化吸收，营养丰富，适宜5个月以上的婴儿食用。 \r\n制作关键： 藕粉要研细，不要有小疙瘩。边熬边搅拌，防止巴锅。', NULL, NULL, NULL, '藕粉200克，蜂蜜50克。清水800克。');
INSERT INTO `recipe` VALUES (5433, 101, '煮红薯(6个月以上)', '1)将红薯洗净、去皮，切成小碎丁；苹果洗净、去皮、去籽，亦切成小丁待用。\r\n2)将红薯、苹果放入锅内，加少许水用微火慢煮，煮熟烂后加入蜂蜜即成。 \r\n特  点： 香甜鲜美，营养丰富，适宜7个月以上的婴儿食用。 \r\n制作关键： 要将红薯，苹果切碎、煮烂，用微火煮。', NULL, NULL, NULL, '红薯300克，苹果300克。蜂蜜50克。');
INSERT INTO `recipe` VALUES (5434, 101, '黄油煎红薯', '1)将红薯洗净去皮，放开水中煮软捞出，控去水分，切成圆片待用。\r\n2)在平底锅内放和黄油，熔化后，下入切好的红薯片，煎至两面发黄为止，盛出后放入小盘骨，浇上蜂蜜，撒上熟芝麻即成。 \r\n特  点： 味香甜，软烂，营养丰富。 \r\n制作关键： 掌握煎红薯的火候，火不要太大，也砂能过小，使红薯煎至两面焦黄，防止煎糊。', NULL, NULL, NULL, '红薯500克，黄油50克。蜂蜜50克，熟芝麻15克。');
INSERT INTO `recipe` VALUES (5435, 101, '海带丝炒肉丝(1－3岁)', '①将海带洗净，切成细丝，放入锅中蒸15分钟，视海带软烂后，取出待用。\r\n②将肥瘦适度的猪肉用清水洗掐，切成肉丝。\r\n③将油放入锅内，热后下入肉丝，用猛火煸炒1-2分钟，加入葱姜末、酱油搅拌均匀，投入海带丝、清水（以漫过海带为度）、精盐，再以猛火炒1-2分钟，勾芡出锅即成。 \r\n特 点： 质厚肉嫩，味道鲜美，营养丰富。 \r\n制作关键： 海带要发透，蒸烂，海带丝不可切得过长。', NULL, NULL, NULL, '肥瘦猪肉500克，水发海带1公斤。炒菜油60克，酱油50克，精盐8克，白糖5克，葱，姜末各克，水淀粉75克。');
INSERT INTO `recipe` VALUES (5436, 101, '韭菜梗炒肉丝(1－3岁)', '①将猪肉洗净，切成细丝，韭菜梗切成2厘米长的段待用。\r\n②将油放入锅内，热后，下入肉丝煸炒变色，加入葱姜末、酱油、料酒、精盐，搅拌韭菜梗同炒几下，淋入花椒油、味精即成。 \r\n特 点： 味道鲜香，诱人食欲。 \r\n制作关键： 此菜适宜春季食用，因为这季节的韭菜鲜嫩味美。韭菜梗下锅后翻炒几下立即出锅，不要炒过火。', NULL, NULL, NULL, '肥瘦猪肉500克，韭菜梗1公斤。配 料： 炒菜油100克，花椒油30克，酱油120克，精盐15克，料酒5克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5437, 101, '扁豆炒肉丝(1－3岁)', '①将扁豆择去两头，清洗干净，切丝，用开水， 烫透，捞出控净水。\r\n②将瘦猪肉洗净，切成肉丝，放入盆内，用50克水淀粉、5克精盐上浆，用热锅温油滑散捞出。\r\n③将油放入锅内，热后下入葱姜丝炝锅，再投入肉丝、扁豆丝煸炒一下，加入高汤、精盐、料酒、味精，尝好味，待开时勾芡即成。 \r\n特 点： 白绿相间，色泽美观，味道鲜嫩。 \r\n制作关键： 扁豆要烧熟，煮透，防止食物中毒。此菜不宜放酱油。', NULL, NULL, NULL, '瘦猪肉500克，扁豆1公斤。炒菜油80克，滑肉用油1公斤（实耗45克），精盐20克，料酒5克，水淀粉100 克，葱、姜丝各克，高汤600克。');
INSERT INTO `recipe` VALUES (5438, 101, '蒜苔炒肉丝(1－3岁)', '①将扁豆择去两头，清洗干净，切丝，用开水， 烫透，捞出控净水。\r\n②将瘦猪肉洗净，切成肉丝，放入盆内，用50克水淀粉、5克精盐上浆，用热锅温油滑散捞出。\r\n③将油放入锅内，热后下入葱姜丝炝锅，再投入肉丝、扁豆丝煸炒一下，加入高汤、精盐、料酒、味精，尝好味，待开时勾芡即成。 \r\n特 点： 白绿相间，色泽美观，味道鲜嫩。', NULL, NULL, NULL, '瘦猪肉500克，扁豆1公斤。炒菜油80克，滑肉用油1公斤（实耗45克），精盐20克，料酒5克，水淀粉100 克，葱、姜丝各克，高汤600克。');
INSERT INTO `recipe` VALUES (5439, 101, '芹菜炒肉丝(1－3岁)', '①将青椒择洗干净，切成4厘米长的丝，瘦猪肉洗净，切成4厘米长帘子棍粗细的丝，放入盆内，加入50克水淀粉，5克精盐上浆，用热锅温油滑散捞出待用。\r\n②将炒菜油放入锅内，热后下入葱姜丝炝锅，投入青椒丝煸炒断生，放入肉丝搅拌匀，加入酱油，精盐、水少许，开后勾芡，淋入香油出锅即成。 \r\n特 点： 色泽美观，味道鲜美，维生素C含量极为丰富。 \r\n制作关键： 要选用新鲜、没有辣味的青椒', NULL, NULL, NULL, '瘦猪肉500克，青椒1公斤。炒菜用油80克，滑肉用油1公斤（实耗45克），香油5克，酱油40克，精盐15克，水淀粉80克，葱、姜丝各5克。');
INSERT INTO `recipe` VALUES (5440, 101, '莴笋炒肉丝(1－3岁)', '①将莴笋择去叶、削去皮，洗净切成细丝；瘦猪肉洗净，切成细丝，放入盆内，加入水淀粉50克，精盐5克上浆，用热锅温油滑散捞出。\r\n②将炒菜用油放入锅内，热后下入葱姜末炝锅，投入莴笋煸后，加入肉丝搅拌均匀，再加入酱油、精盐、水少许，开后勾芡，放味精搅匀即成。 \r\n特 点： 色泽白绿，味道清香，爽口。  \r\n制作关键： 肉丝、莴笋丝要切得短些，酱油不宜放得太多，以免影响菜的美观与味道。', NULL, NULL, NULL, '瘦猪肉500克，净莴笋1公斤。 炒菜用油80克，滑肉用油1公斤，酱油20克，精盐18克，味精5克，水淀粉80克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5441, 101, '青椒炒肉丝(1－3岁)', '①将青椒择洗干净，切成4厘米长的丝；瘦猪肉洗净，切成4厘米长帘子棍粗细的丝，放；入盆内，加入50克水淀粉、5克精盐上浆，用热锅温油滑散捞出待用。\r\n②将炒菜油放入锅内，热后下入葱姜丝炝锅，投入青椒丝煸炒后，放入肉丝搅拌匀，加入酱油、精盐、水少许，开后勾芡，淋入香油出锅即成。 \r\n特 点： 色泽美观，味道鲜美，维生素C含量极为丰富。 \r\n制作关键： 要选用新鲜、没有辣味的青椒做原料。青椒丝不要切得太长，以适合幼儿的咀嚼与消化能力。菜要炒得软些。', NULL, NULL, NULL, '瘦猪肉500克，青椒1公斤。 炒菜用油80克，滑肉用油1公斤(实耗45克），香油5克，酱油40克，精盐15克，水淀粉80克，葱、姜丝各5克。');
INSERT INTO `recipe` VALUES (5442, 101, '绿豆芽炒肉丝(1－3岁)', '①将绿豆芽择洗干净，控净水分毛衣和；瘦猪肉洗净，切成细丝，放入盆内，加入水淀粉，精盐5克上浆，用热锅温油滑散捞出待用。\r\n②将油放放锅内，热后下入葱、姜丝炝锅，投入绿豆芽煸炒断生，加入肉丝翻炒均匀，加入精盐，醋，翻炒几下即可出锅。 \r\n特 点： 酸咸脆嫩，清香利口。 \r\n制作关键： 要选择鲜嫩、短粗，无根的绿豆芽做原料。炒菜时火要旺，要大火急炒。不能放酱油。', NULL, NULL, NULL, '瘦猪肉500克，绿豆芽1公斤。炒菜用油80克，滑肉用油1公斤（实耗45克），精盐20克，醋10克，葱、姜丝各5克，水淀粉50克。');
INSERT INTO `recipe` VALUES (5443, 101, '狮子头(1－3岁)', '①将马蹄（罐头）切成碎末，同肉馅一起放入盆内，加入鸡蛋、葱姜末，精盐、料酒水淀粉，用力搅匀成馅，待有粘性时，做成20个鸭蛋形丸子，用热油炸成金红色捞出。\r\n②将狮子头放入锅内，加入水（以漫过狮子头为度），放入酱油、大料、葱段、姜片，用猛火烧开后，转微火焖20分钟即成。 \r\n特 点： 鲜香，松软，入口即化。形似狮子头，故名。 \r\n制作关键： 要用瘦肉做原料，不能太肥或太瘦，太肥无营养，太瘦则丸子发皮，不松软。因肉馅内有葱末，在炸时油不能太热，否则炸出的狮子头表面有黑点影响美观。', NULL, NULL, NULL, '肥瘦猪肉馅500克，鸡蛋1个约50克，精盐10克，料酒5克，水淀粉150克，大料2瓣，葱、姜各少许。');
INSERT INTO `recipe` VALUES (5444, 101, '四喜丸子(1－3岁)', '①将莴笋择去叶、削去皮，洗净切成细丝；瘦猪肉洗净，切成细丝，放入盆内，加入水淀粉50克，精盐5克上浆，用热锅温油滑散捞出。\r\n②将炒菜用油放入锅内，热后下入葱姜末炝锅，投入莴笋煸后，加入肉丝搅拌均匀，再加入酱油、精盐、水少许，开后勾芡，放味精搅匀即成。 \r\n特 点： 色泽白绿，味道清香，爽口。  \r\n制作关键： 肉丝、莴笋丝要切得短些，酱油不宜放得太多，以免影响菜的美观与味道。', NULL, NULL, NULL, '瘦猪肉500克，净莴笋1公斤。炒菜用油80克，滑肉用油1公斤，酱油20克，精盐18克，味精5克，水淀粉80克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5445, 101, '刺猬丸子(1－3岁)', '①将江米用凉水泡40分钟，控净水待用。\r\n②将肉馅放入盆内，加入鸡蛋，葱姜末、精盐、味精、料酒、香油、水淀粉、清水、用力搅拌，待有粘性时，用手挤成40个大小相等的丸子。\r\n③最后将丸子逐个沾一层江米，放入盘内，上笼用旺火蒸25分钟即成。 \r\n特 点： 米粘、肉香、色白，形似刺猬，故名。 \r\n制作关键： 江米必须用水先泡一下再用，不然，将肉馅直接沾米蒸不透。丸子的个大小要均匀。', NULL, NULL, NULL, '猪肉馅500克，鸡蛋3个约150克。香油15克，精盐10克，料酒3克，味精3克，水淀粉100克，水150克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5446, 101, '炖丸子(1－3岁)', '①将肉馅放入代舅内，加入鸡蛋、葱姜末、五香粉、料酒、精盐、水少许，搅拌均匀成馅。\r\n②将油放入锅内，七成热时，把内馅挤成直径2厘米的丸子40个，投入锅内，炸成金黄色捞出。\r\n③将丸子了放入锅内，加入水（以漫过丸子为度），放入酱油、花椒、葱段、姜片，开后转微火炖15分钟即成。 \r\n特 点： 酥嫩咸香，色泽金红。 \r\n制作关键： 搅肉馅时，按500克肉放1个鸡蛋，可使丸子松软。选择肥瘦肉馅做丸仓料。炸丸子火不宜太旺，油不宜过热，以免将丸子炸糊。丸子下锅后不要立即用勺推搅丸子。', NULL, NULL, NULL, '猪肉馅500克，鸡蛋1个约50克。植物油1公斤（实耗25克），酱油30克，精盐10克，料酒8克，五香粉2克，花椒10粒，葱15克，姜8克，水淀粉200克。');
INSERT INTO `recipe` VALUES (5447, 101, '氽丸子(1－3岁)', '①将叶菜类蔬菜切成2厘米大小的小块，瓜类蔬菜切成2厘米见方的小薄片，待用。\r\n②将肉馅放入盆内，加入鸡蛋清和水，用力搅动，至有粘性时加入葱姜末、精盐8克、味精2克，香油8克及水淀粉拌匀，用手挤成直径1.5厘米的60个小丸子,下入凉水锅内,待开锅时撇去浮沫,将丸子捞入盆内,浇上原汤待用。\r\n③将菜放入锅内（每次选其一种），加入水（以漫过菜为度），开锅后，加入盛入袋子舅内。食用是盛入盆内。食用时，菜盛入碗底，上盖6个小丸子即可。 \r\n特 点： 丸子松嫩，汤味鲜香，营养丰富。 \r\n制作关键： 氽丸最好选用瘦肉做', NULL, NULL, NULL, '瘦猪肉馅500克，鸡蛋清2个，白菜叶（菠菜叶、冬瓜、黄瓜等蔬菜均可）500克。 \r\n配 料： 香油15克，精盐15克，味精5克，水淀粉40克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5448, 101, '番茄丸子(1－3岁)', '①将肉馅放入盆内，加入葱姜末、精盐8克、水淀粉，搅匀后加番茄酱，再用力朝一个方向搅上劲待用。\r\n②锅内放入清水，开后，馅泥挤成1.5厘米大小的丸子氽入锅内，再加青菜、精盐略煮几分钟即成。 \r\n特 点： 色彩鲜艳，香而不腻。 \r\n制作关键： 肉馅加入调料后，要朝一个方面用力搅打，要打至发粘、上劲；要等水开下锅。上述两点，如有一点做不到，都容易把丸子氽碎。', NULL, NULL, NULL, '肥瘦肉馅500克，青菜250克。番茄酱100克，葱末10克，姜末5克，精盐18克，水淀粉15克。');
INSERT INTO `recipe` VALUES (5449, 101, '甜酸丸子(1－3岁)', '①将肉馅放入盆内，磕入鸡蛋，加入料酒3克，精盐8大象，水淀粉150克、面包屑，番茄酱10克和姜末拌匀，挤成1.5厘米大小的丸子,放入温油锅内炸成金黄色后捞出。\r\n②炒锅内留底油少许下入番茄酱炒一下，待丸子上沾满芡汁后即可淋入香油食用。 \r\n特 点： 色泽红亮，味道鲜美。 \r\n制作关键： 番茄酱下锅时，油不能热，以免把番茄酱炸糊。', NULL, NULL, NULL, '肥瘦猪肉馅500克，鸡蛋2个，面包屑50克。 \r\n配 料： 植物油1公斤（实耗50克），香油10克，精盐15克，姜末5克，料酒6克，番茄酱50克，醋10克，水淀粉180克。');
INSERT INTO `recipe` VALUES (5450, 101, '招远蒸丸(1－3岁)', '①将瘦肉剁碎成泥，放入小盆内，加入鸡蛋搅匀。\r\n②肥膘肉切成厚0.3厘米见方的丁。\r\n③海米、白菜心、水发冬菇、姜均切成碎末。葱30克切末、30克切丝，香菜一半切段、一半切末。\r\n④将肥肉丁与配料末、葱姜末、味精，精盐、胡椒面放入瘦肉泥盆内搅匀，做成直径1.5厘米大小的丸子，分别装入10个小汤盘内，上笼蒸熟取出。\r\n⑤将鸡汤倒入锅内，加精盐、醋、烧开撇去浮沫，淋上香油，撒葱丝、香菜段，倒入盛丸子的盘内即成。\r\n \r\n特 点： 丸子喧软鲜嫩，别有风味。 \r\n制作关键： 丸子上笼蒸时，注意不要蒸老，以保持丸子', NULL, NULL, NULL, '瘦猪肉300克，海米8克，水发冬菇8克，白菜心150克，香菜75克，鸡蛋3个。 \r\n配 料： 香油10克，胡椒面1克，精盐13克，味精3克，醋4克，葱60克，姜20克，鸡汤450克。');
INSERT INTO `recipe` VALUES (5451, 101, '丸子烧白菜(1－3岁)', '①将白菜切成2厘米长、0.5厘米宽的条：肉馅放入盆内，加入姜末、酱油、料酒、精盐、水淀、植物油10克，香油10克，酱油25克，精盐、水淀粉及水，搅拌均匀成馅。\r\n②将10个小盘抹一层植物油，把肉馅均匀地放入10个小盘内，摊开呈中间低四周高开头然后将鸡蛋分别磕入盘内肉馅上面，上笼用旺火蒸15分钟即成。\r\n \r\n特 点： 制法简单，造型美观，肥而不腻。 \r\n制作关键： 肉馅要斩或绞细，不能太粗，肉馅 加入调料后要朝一个方面搅打，打至有粘性为止，这样蒸熟的才成形美观', NULL, NULL, NULL, '肥瘦猪肉馅500克，净白菜1公斤。 \r\n配 料： 植物油1公斤（实耗50克），酱油50克，精盐13克，味精3克，水淀粉200克，葱、姜末少许，高汤（水）600克。');
INSERT INTO `recipe` VALUES (5452, 101, '太阳肉(1－3岁)', '①将肉馅放入盆内，加入葱姜末、酱油、精盐、水淀粉及水，搅拌均匀成馅。\r\n②将10个小盘抹一层植物油，把肉馅均匀地放入10个盘内，摊开呈中间低四周高形状，然后将鸡蛋分别磕入盘内网馅上面，上笼用旺火蒸15分钟即成。\r\n \r\n特 点： 制法简单，造型美观，肥而不腻。 \r\n制作关键： 肉馅要斩或绞细，不能太粗。肉馅加入调料后要朝一个方向用力搅打，打至有粘性为止，这样蒸熟的太阳肉才成形美观。', NULL, NULL, NULL, '猪肉馅250克，鸡蛋10个。 \r\n配 料： 植物油10克，香油10克，酱油25克,精盐5克，葱、姜末，水淀粉75克，清水100克。');
INSERT INTO `recipe` VALUES (5453, 101, '煎白菜肉卷(1－3岁)', '①将白菜叶用沸水烫软；肉馅放入盆内，加入精盐、味精、葱姜末、香油，搅匀待用。\r\n②将烫过的白菜叶，切成长10厘米、宽7厘米后，在菜板上铺平，抹上肉馅，卷成直径3厘米的卷。\r\n③疳花生油放入炒勺内，烧至七八成热，将白菜卷依次蘸上面粉、鸡蛋，入油煎至微黄色捞出。\r\n④勺内留底油少许，投入葱姜丝炝锅，烹入高汤225克，放入煎好的白菜卷，加入精盐、味精，待汤汁将干时淋入香油即可。\r\n \r\n特 点： 味道鲜美，易于消化，营养丰富，形态美观。 \r\n制作关键： 菜不能太咸，煎时火不宜太大，否则鸡蛋容易炸糊，影响味道，又', NULL, NULL, NULL, '白菜叶750克，肉馅500克，鸡蛋200dq 。 \r\n配 料： 花生油200克，香油25克，面粉75克，精盐10克，味精8克，葱、姜少许。');
INSERT INTO `recipe` VALUES (5454, 101, '如意卷(1－3岁)', '①将猪肉切成薄片，放凉水中拔一下，用洁布搌干水分，剁成泥，放入蛋清、水淀粉，精盐、料酒、味精，用手抓匀，搅上劲。\r\n②将鸡蛋打入碗内，放水淀粉及精盐调匀，摊成四张蛋皮，铺于案上，将调好的肉馅均分到四张蛋皮上，再撒野上虾子和菠菜叶，将蛋皮先从抹馅的一头卷起，卷至中间时，再从另一头向中间卷，至两卷并对，稍按平，用洁净的湿纱布将如意卷包上，上屉蒸熟，取出，用木板压一下，将布揭去，切成片即可食用。', NULL, NULL, NULL, '猪通脊肉500克，鸡蛋清4个，鸡蛋150克，虾子50克，菠菜叶50克。 \r\n配 料： 水淀粉150克，精盐10克，味精3克，料酒5克。');
INSERT INTO `recipe` VALUES (5455, 101, '丸子烧油菜(1－3岁)', '①将油菜择洗干净，切成2厘米长、0.5厘米宽的条,菜梗分开.\r\n②将内馅放入盆内,加入葱姜末、精盐、料酒、水淀粉、搅拌均匀，挤成直径1.5厘米大小的丸子,下入七八热的油内,炸成黄色捞出待用。\r\n③将植物油50克放入锅内，投入菜梗煸至六七成熟，加盐，再下菜叶合煸，然后将肉丸子放入，并加酱油和少量水，一同烧至味透菜烂即成。\r\n \r\n特 点： 丸子松嫩，油菜软烂漫，味道鲜香，色泽美观，营养丰富。 \r\n制作关键： 切菜时，油菜梗、叶要分开，不能同时下锅，菜要烧烂，酱油不能放得过多，以免影响色泽和味道。', NULL, NULL, NULL, '肥瘦猪肉馅500克，油菜1公斤。植物油1公斤（实耗50克），酱油40克，精盐13克，料酒6克，水淀粉150克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5456, 101, '小丸子卷心菜(1－3岁)', ' ①将油菜择洗干净，切成2厘米长、0.5厘米宽的条,菜梗分开.\r\n②将内馅放入盆内,加入葱姜末、精盐、料酒、水淀粉、搅拌均匀，挤成直径1.5厘米大小的丸子,下入七八热的油内,炸成黄色捞出待用。\r\n③将植物油50克放入锅内，投入菜梗煸至六七成熟，加盐，再下菜叶合煸，然后将肉丸子放入，并加酱油和少量水，一同烧至味透菜烂即成。\r\n \r\n特 点： 丸子松嫩，油菜软烂漫，味道鲜香，色泽美观，营养丰富。 \r\n制作关键： 切菜时，油菜梗、叶要分开，不能同时下锅，菜要烧烂，酱油不能放得过多，以免影响色泽和味道。', NULL, NULL, NULL, '肥瘦猪肉馅500克，油菜1公斤。植物油1公斤（实耗50克），酱油40克，精盐13克，料酒6克，水淀粉150克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5457, 101, '丸子烧冬瓜(1－3岁)', '①将冬瓜洗净，去皮、瓤，切成1.5厘米大小的片。\r\n②将肉馅放入盆内，加入葱姜末、精盐、料酒、水淀粉搅匀，下入七八成热的油内，炸成金黄色，捞出待用。\r\n③将余油倒出，锅内留少许油，下入冬瓜煸炒，加入酱油及精盐拌和，然后将炸好的丸子放入同烧，至冬瓜酥烂入味，即可出锅。\r\n \r\n特 点： 丸子松嫩味美，冬瓜软烂。 \r\n制作关键： 冬瓜片切得要小。冬瓜、丸子要烧透入味，口味不能咸。', NULL, NULL, NULL, '肥瘦猪肉馅500克，冬瓜1.5公斤。植物油1公斤（实耗50克），精盐13克，');
INSERT INTO `recipe` VALUES (5458, 101, '肉末炒芹菜(1－3岁)', '①将猪肉剁碎成末；芹菜择洗干净，切碎，用开水烫一下。\r\n②将油放入锅内，热后先煸葱姜末，继放入肉末，炒散，加入酱油，料酒炒几下，再将芹菜放入，一同炒几下即成。\r\n \r\n特 点： 清香爽口，含铁丰富。 \r\n制作关键： 煸肉末时油不能热，以免肉末炒不散。炒此菜不要勾芡，酱油要少放。', NULL, NULL, NULL, '瘦猪肉250克，芹菜1公斤。植物油75克，酱油60克，精盐10克，料酒8克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5459, 101, '肉末烧菜花(1－3岁)', '①将猪肉剁碎成末；菜花择洗干净，切成小碎花瓣，用开水烫一下，去其味。\r\n②将油放入锅内，热后先煸葱姜末，继而放入肉末炒散，加入酱油、料酒炒几下，再将菜花放入同炒，最后加入精盐炒透即成。\r\n \r\n特 点： 咸香味美，营养丰富。 \r\n制作关键： 切菜花时要从头往下顺切。用开水烫一下能去掉菜花中的怪味。肉末下锅时油不要太热，以免肉炒不散。', NULL, NULL, NULL, '瘦猪肉250克，菜花1公斤。植物油75克，酱油50克，精盐10克，料酒10克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5460, 101, '肉末炒西红柿(1－3岁)', '①将猪肉洗净剁成碎末；西红柿洗净，用开水烫一下，去皮去籽切成小片；\r\n②将油放入锅内，热后下入葱姜末炝锅，再将肉末放入炒散，加入酱油、精盐略炒，制投入西红柿炒几下，再投入粉皮，用旺火快炒几下即成。\r\n \r\n特 点： 色泽美观，味道鲜美，营养丰富。 \r\n制作关键： 肉末下锅时没不要太热，以免肉末结块，炒不散。西红柿、粉皮下锅后要用旺火快炒。', NULL, NULL, NULL, '瘦猪250克，西红柿400克，粉皮600克。植物油75克，酱油50克，精盐10大象，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5461, 101, '肉末炒茄子(1－3岁)', '①将猪肉洗净剁成碎末；西红柿洗净，用开水烫一下，去皮去籽切成小片；\r\n②将油放入锅内，热后下入葱姜末炝锅，再将肉末放入炒散，加入酱油、精盐略炒，投入西红柿炒几下，再投入粉皮，用旺火快炒几下即成。\r\n \r\n制作关键： 肉末下锅时油不要太热，以免肉末结块，炒不散。西红柿、粉皮下锅后要用旺火快炒。', NULL, NULL, NULL, '瘦猪250克，西红柿400克，粉皮600克。植物油75克，酱油50克，精盐10克，葱、姜末少许。');
INSERT INTO `recipe` VALUES (5462, 101, '肉末烩小水萝卜(1－3岁)', '①将猪肉剁成碎末；小水萝卜洗净，切成1厘米见方的丁，用开水烫一下。\r\n②将油放入锅内，热后先煸葱及肉末，放入酱油略炒，投入小萝卜炒匀，加水同烧，待将熟放入味精及味精、青蒜，用淀粉勾芡即成。\r\n \r\n特 点： 汁浓味美。 \r\n制作关键： 水萝卜要先烫一下再炒，这样能去看萝卜气味，炒出的萝卜味好。出锅前勾点芡，使汁裹在萝卜上，以增加味道。', NULL, NULL, NULL, '瘦猪肉250克，小水萝卜1公斤。植物油75克，酱油50克，精盐10克，味精5克，水淀粉20克，葱、青蒜各12克。');
INSERT INTO `recipe` VALUES (5463, 101, '肉末炒菠菜(1－3岁)', '①将猪肉洗干净，剁成碎末；菠菜择洗干净，切成1厘米长的段待用。\r\n②将油放入锅内，热后先煸葱姜，然后将肉末放入，煸至变色，加入酱油、精盐翻炒均匀，投入菠菜，用旺火急炒几下即成。\r\n \r\n特 点： 色泽碧绿，味道鲜香，含铁丰富。 \r\n制作关键： 菠菜要先洗后切。炒菜时，火要旺，油要热，快速操作。', NULL, NULL, NULL, '瘦猪肉250克，菠菜1公斤。植物油250克，菠菜1公斤。');
INSERT INTO `recipe` VALUES (5464, 101, '肉末炒冬瓜(1－3岁)', '①将猪肉洗净，剁成碎末；冬瓜洗净，削去皮，挖净瓤，切成1.5厘米大小的薄片。\r\n②将油放入锅内，热后，先煸葱姜末，继而下入肉末煸炒变色，加入酱油、精盐，搅炒均匀，投入冬瓜煸炒、视冬瓜软熟，用水淀粉勾芡，出锅即成。\r\n \r\n特 点： 软烂，味鲜。 \r\n制作关键： 炒此菜酱油不宜放得过多，口不能重。', NULL, NULL, NULL, '瘦猪肉250克，冬瓜1公斤。植物油75克，酱油40克，精盐10克，水淀 粉20克，葱、姜末各10克。');
INSERT INTO `recipe` VALUES (5465, 101, '芝麻肝(1－3岁)', '①将猪肉洗净，剁成碎末；冬瓜洗净，削去皮，挖净瓤，切成1.5厘米大小的薄片。\r\n②将油放入锅内，热后，先煸葱姜末，继而下入肉末煸炒变色，加入酱油、精盐，搅炒均匀，投入冬瓜煸炒、视冬瓜软熟，用水淀粉勾芡，出锅即成。\r\n \r\n制作关键： 炒此菜酱油不宜放得过多，口不能重。', NULL, NULL, NULL, '瘦猪肉250克，冬瓜1公斤。植物油75克，酱油40克，精盐10克，水淀 粉20克，葱、姜末各10克。');
INSERT INTO `recipe` VALUES (5466, 101, '炒猪肝(1－3岁)', '①将猪肝洗净后剔去筋，切成2厘米长、1厘米宽、0.2厘米厚的片；葱、姜、蒜均切成末；木耳洗净撕成小碎块。\r\n②将猪肝放入盆内，加入水淀粉25克，精盐7克搅拌均匀。\r\n③将葱、姜、蒜、木耳、酱油、白糖、醋、料酒、盐和余下的水淀粉合在一起，加入少许水，搅匀成调料汁。\r\n④炒锅内放入植物油，置旺火上烧至八成热时，放入拌好的猪肝片滑炸散捞出。\r\n⑤倒去锅中余油，将花椒油放入炒锅，烧热后倒入猪肝片，炒几下后倒入调味汁，炒匀即可出锅。\r\n \r\n特 点： 肝嫩而鲜，味美。 \r\n制作关键： 肝片要切小些，过油时火要旺，油', NULL, NULL, NULL, '猪肝500克，水发木耳200克。植物油1.5公斤（实耗150克），花椒油80克，酱油50克，料酒15克，精盐12克，醋15克，白糖15克，水淀粉50克，葱、姜、蒜各少许。');
INSERT INTO `recipe` VALUES (5467, 101, '黄瓜炒猪肝(1－3岁)', '①将锗肝洗净切成2厘米长、1厘米宽、0.2厘米厚的片，用水淀粉25克，精盐7克上浆，用八成热的油滑散捞出待用。\r\n②将黄瓜洗净，切成小象眼片；葱、姜、蒜切末；木耳洗净撕成小碎块待用。\r\n③将油150克放入锅内，烧至七成热时，放入葱、姜、蒜、黄瓜、木耳稍炒几下，即将滑油的猪肝倒入锅内，迅速淋入料酒，再加酱油、精盐、白糖、味精、高汤或水少许，开后用水淀粉勾芡，出锅即成。\r\n \r\n特 点： 滑嫩脆香。 \r\n制作关键： 因猪肝稀软，可用干淀粉面上浆。过油时火要旺，油要热，操作要迅速，防止炸老。猪肝下锅后，要立即淋', NULL, NULL, NULL, '猪肉1公斤，黄瓜400克，水发木耳100克。植物油1.5公斤（实耗150克），酱油20克，料酒20克，白糖15克，精盐12克，味精8克，水淀粉50克，葱、姜、蒜各少许。');
INSERT INTO `recipe` VALUES (5468, 101, '炸熘肝尖(1－3岁)', '①将猪肝切成2厘米、宽1厘米、厚0.2厘米的片，用水淀粉35克、精盐7克抓匀上浆；荸荠切片，青菜心切成1.5厘米大小的象眼片，木耳洗净撕成小块，均用开水烫一下；将清汤、酱油、水淀粉、料酒、精盐放入一个碗内，对成汁待用。\r\n②将油放入锅内，中火烧至七成热，将猪肝逐片下入油内炸，至呈红色漂起在油面时捞出；原锅内留少量油，放入葱姜蒜末，炸出香味时烹入醋，随即将配料和炸好的猪肝倒入翻炒，再倒入对好的汁翻炒均匀，使其与汁混合，盛入盆内即成。\r\n \r\n特 点： 色泽枣红，鲜嫩滑润，口味酸中带咸。 \r\n制作关键： 此种', NULL, NULL, NULL, '猪肝1公斤，玉兰片50克，水发木耳50克，荸荠（马蹄）30克，青菜心50克。 \r\n配 料： 植物油1.5公斤（实耗150克），酱油30克，精盐12克，料酒40克，醋20克，水淀粉60克，葱、姜、蒜末10克。');
INSERT INTO `recipe` VALUES (5469, 101, '金银蛋饺(1－3岁)', '加入水淀粉、精盐、用筷子打散搅匀；瘦肉剁成末，调味成馅。②将炒菜勺在火在烧热，用生肥肉在手勺内擦一下，用小匙取蛋清一匙倒入手勺内，推成小圆蛋皮，加上肉馅包成蛋饺，共做成15个；用同样方法再做15个蛋黄蛋饺。③二色蛋饺各放碗内一边蒸10分钟取出，摆在另一盘中，然后勾成白色卤汁，浇在蛋饺上，即成。 \r\n特 点： 色彩美观，黄白相映，味鲜质嫩，营养丰富。 \r\n制作关键： 摊蛋皮要用铁勺摊，不能用铝勺摊。蛋皮摊好后立刻加馅包蛋饺，蛋凉了，就包不成蛋饺了。', NULL, NULL, NULL, '鸡蛋10个，瘦肉300克，肥肉100克。精盐4克，水淀粉30克，味精5克，葱、姜末各10克');
INSERT INTO `recipe` VALUES (5480, 53, '可乐鸡翅', '(1)先把买来的鸡翅洗干净，然后将其切成小块（以方便入味）\r\n(2)调好酱汁，其主要原料为酱油，盐，糠，大蒜粒。\r\n(3)把鸡翅浸泡在酱汁里面十五分钟左右，将其捞起，放入到烧热的平锅里煎，（记得要慢火煎且锅里先入有少量生油）反复将鸡翅煎至金黄色为止\r\n(4)然后把刚才浸泡用的酱汁倒入，另加150ML的可乐一起，把鸡翅慢火闷熟。（大概25分钟）即可。', NULL, '煎鸡翅步骤要细心，不要烧焦了，', '生姜、生油，酱油、大蒜、盐、糖', '鸡翅 可乐');
INSERT INTO `recipe` VALUES (5485, 89, '滋阴润燥－－双耳粥', '将黑百木耳放入温水中,泡发,洗净,撕成瓣状,大枣洗净,粳米淘净,将粳米,木耳大枣一同放入沙锅内,加水适量,武火烧开,改成文火炖熟,至林耳极烂后,加入冰糖稍煮即可', '适用于治疗肝病伴口干咽燥.', NULL, '冰糖', '木耳(黑,白)各10克,大枣10枚,粳米100克');
INSERT INTO `recipe` VALUES (5486, 89, '健脾益胃－－－－氽山药球', '将山药削皮,洗净,上锅蒸烂,捣成山药泥,用洁净的纱布包好,挤去水,将面粉放入碗内,加水适量,调成浆糊,拌入山药泥内,调和均匀,搓条,揪成小剂子,按平,将豆沙拌入桂花,制成馅.将馅包入小剂子中,团成山药球,止笼蒸10分钟左右出笼,将白糖入锅,加水适量烧开,撇去浮沫,下入山药球烧开即成.', '适用于治疗肝硬化,脾虚腹泻.', NULL, '桂花', '山药250克,豆沙150克,白糖100克,面粉50克,桂花5克');
INSERT INTO `recipe` VALUES (5487, 89, '脂肪肝－－－－－绿豆藕片', '将绿豆洗净,浸泡半小时,研碎.胡萝卜洗净切丁,研成泥,加入绿豆末,做成馅,藕去皮洗净,从一端切开作盖,藕洞中塞满馅,盖上盖,置蒸笼中,隔水蒸熟,食用时切片即可.', '适用于治疗脂肪肝,高脂血症,高血压和动脉硬化.', NULL, '白糖', '大藕300克,绿豆200克,胡萝卜125克');
INSERT INTO `recipe` VALUES (5488, 89, '益肝补脾－－－－龙眼鸡片', '鸡脯肉洗净,切片,将龙眼肉洗净,生姜切薄片,葱切葱花,鸡蛋取清,小白菜洗净,鸡肉片用蛋清,盐,胡椒粉,白糖,鸡汤怼成汁,炒锅烧热,入香油,五成时放入鸡片,其将熟时出锅,沥油,锅内留底油少许,,入葱,姜出香味,随即倒入龙眼肉,鸡肉,小白菜再倒入调味汁翻炒几下,起锅装盘,即可,', '适用于治疗肝硬化脾亢引起的贫血.', NULL, '生姜,葱,盐,白糖,香油,鸡汤,胡椒各适量', '鸡脯肉400,龙眼肉30克,鸡蛋120克(两只左右)小白菜40克,');
INSERT INTO `recipe` VALUES (5489, 54, '蒜蓉烤茄子', '1。把茄子洗干净，用切在中间划一刀，不要划穿，然后放进微波炉里转两分钟这样（为了缩短烤的时间），压压，有点变软了； \r\n2。接着，用烤箱烤10分钟左右（我没有烤箱，用了微波炉代替，微波炉也有烧烤功能嘛！时间也根据茄子的大小做了适当调整）； \r\n3。取出烤盘，将茄子扒开，将肉末+蒜泥+姜蓉+盐+糖+鸡精+生抽+油+少许水拌均匀抹进茄子里，再入微波炉烤10分钟，烤到茄肉软软烂烂时取出来，撒点葱花、滴几滴香油，我烤了2条，一条加剁椒，一条不加，ok！开吃！ \r\n', NULL, NULL, '蒜蓉,姜蓉,盐,糖,鸡精,生抽,油', '茄子,肉末');
INSERT INTO `recipe` VALUES (5490, 56, '枸杞银芽', '1、枸杞泡水洗净，放入开水中泡软；绿豆芽洗净、去除头尾；胡萝卜洗净、去皮，切成丝。\r\n\r\n2、锅中放入2小匙油烧热，放入胡萝卜、绿豆芽略炒，再加入A料拌匀，最后加入枸杞快炒，即可盛出。\r\n\r\n', '豆芽菜爽口不油腻，即使不添加调味料，一样鲜美可口，多吃也不会增加身体负担，搭配有明目功效的枸杞，是一道简单又健康的营养佳肴。', NULL, '低钠盐、糖各1/4小匙', '绿豆芽200克，枸杞10钱，胡萝卜50克。\r\n');
INSERT INTO `recipe` VALUES (5491, 56, '醋熘白菜', '1.白菜帮切4厘米长，2厘米宽的一字条形块，青椒切白菜块小一点的一字条块。 \r\n2.锅内放油，加热至五成热时放入花椒粒，炸成紫红色（不要糊）时，捞出花椒粒不要，然后把白菜块放久内翻炒几下，再放姜丝，蒜片，海米，再炒几下，速加醋，糖，味精，精盐，鲜汤（50克），加盖燜1分钟后去盖，白菜断生加青椒块，翻炒几下，调好口味，用湿淀粉勾芡，点香油出锅装盘。 \r\n', '口味酸，甜，咸，嫩富有清香味。 \r\n', NULL, '水发海米2克，鲜青椒50克，湿淀粉15克，猪油50克，花椒5料，香油10克，醋，味精，蒜片，酱油各适量。', '嫩白菜帮300克 \r\n鲜青椒50克，');
INSERT INTO `recipe` VALUES (5492, 56, '软炸豌豆苗', '1.豌豆苗洗净控干水分。 \r\n\r\n2.面糊加调料搅拌均匀。 \r\n\r\n3.将豌豆苗挂面糊入油锅炸至金黄出锅装盘即可。 \r\n\r\n', '特点：金黄透绿，清脆可口。备料时间：5分钟 烹调时间：1分钟', '油温不宜过高。', '盐、胡椒粉。 ', '豌豆苗300克、面粉50克');
INSERT INTO `recipe` VALUES (5494, 53, '肉片豆腐卷', '1.把一块豆腐切成几毫米厚的薄片———豆腐片的长度跟涮肉片的宽度一致，宽度大概跟一片口香糖差不多。\r\n　　2.把一片涮肉用的羊肉片平铺在案板上，把豆腐片放在肉片的一端，然后，用肉片卷起豆腐，卷成一个豆腐卷。因为豆腐片的长度跟肉片的宽度一致，所以可以很整齐地卷起来。用同样的方法，一个一个地做好所有的豆腐卷。\r\n　　3.把所有卷好的豆腐卷都摆到一个浅盘子上，在上面浇上用生抽酱油、料酒、少许淀粉和少许白糖调好的汁，腌10—15分钟。汁不必调太多，可以在腌的时候翻一下面，让豆腐卷的两面都能沾上汁。\r\n　　4.煎锅里热少许油，把腌好的豆腐卷摆进去，小火慢慢煎至豆腐卷的两面都变成漂亮的金黄色，盛出摆盘即可。\r\n　　如果有人吃不惯羊肉，嫌它的膻味太重，也可以将肉片换成牛肉或猪肉，我都尝试过，它们和豆腐的味道也很相配。如果家里有客人，这道好吃又好看的“肉片豆腐卷”更是个不错的选择。\r\n\r\n', NULL, NULL, '生抽酱油、料酒、少许淀粉和少许白糖', '肉片200克、豆腐 300克');
INSERT INTO `recipe` VALUES (5495, 56, '海米炒洋葱', '1． 洋葱去皮、洗净、切成丝放盘中，水发海米洗净，放碗中待用。 \r\n\r\n2． 将料酒、味精、酱油、食盐、姜丝放另一碗中调成汁。\r\n\r\n3． 炒锅上火，倒入色拉油烧热，加入洋葱、海米，烹入调味汁炒熟，淋入香油即可。   ', '美味可口，减肥功效不错', NULL, '食盐、姜丝 ,酱油,各适量，色拉油25克，料酒 ,香油,味精 ', '水发海米 30克 ，洋葱200克');
INSERT INTO `recipe` VALUES (5496, 76, '秘制国药中华鳖', '做法：把鳖仰放在盆中，然后用80~85°热水烫其全身半分钟，再取起后用手褪去其身上表面的薄膜，这样就能去掉鳖身上的全部污秽。特别须注意褪净其颈部、腋窝和四趾有皱褶地方的薄膜，并剥去脚趾甲。处理好后，与药材同煮。时间依鳖的大小而定。\r\n', NULL, NULL, '盐。姜片', ' 中华鳖350克、党参50克、红枣30克、枸杞20克、沙参20克、玉竹20克、姜片少许。');
INSERT INTO `recipe` VALUES (5497, 76, '客家酿豆腐', '做法：\r\n\r\n　　◆瘦肉、鱼肉分别剁烂，虾米浸软切幼，咸鱼切幼，葱切粒。将各材料放大碗中，加入调味料搅至起胶待用；\r\n\r\n　　◆白菜摘好洗净，放入油、盐、滚水中灼熟盛起；\r\n\r\n　　◆豆腐冲洗滴干，一开四件，用匙羹挖去少量豆腐，洒上少许生粉，酿入鱼肉，放入油镬中煎至微黄色盛起；\r\n\r\n　　◆烧热瓦锅，下油一汤匙爆香姜片，加入上汤煮滚，放入白菜、豆腐滚片刻，下芡汁炒煮，滚即可原煲上桌。\r\n', '千年古城梅州，有“客都”之称，中原人“衣冠南迁”到这里，不但带来了读书皆上品的风气，也形成了自己的饮食文化客家菜。火柴盒大小的水豆腐炸成金黄色，把猪肉、鱼肉做成的馅“酿”入其中，放进葱花、香油，盛在鸡汤瓦煲内焖着，直到香气四溢。想来到了梅州的客家人一时无麦可包饺子，才创出如此美味。', NULL, '咸鱼肉、白菜、葱、姜、麻油、胡椒粉等', ' 板豆腐300克、瘦肉100克、鱼肉50克、虾米30克、咸鱼肉少许，白菜50克、');
INSERT INTO `recipe` VALUES (5498, 76, '水晶蟹粉卷', '制作方法：\r\n　　将白萝卜用旋片法切成薄片，用盐稍腌至软并用凉水冲洗后待用。将胡萝卜、青瓜切丝与蟹粉同炒，制成蟹粉馅。将上述蟹粉馅包入白萝卜片中，制成萝卜卷。将萝卜卷浸入用白醋、糖、青柠汁调成的酸甜汁中2小时。改刀造型即成。\r\n', ' 清脆爽口，香味扑鼻，形似水晶。', NULL, '白醋、糖、青柠汁', '梭子蟹蟹肉30克、白萝卜200克、胡萝卜50克、黄瓜30克');
INSERT INTO `recipe` VALUES (5499, 55, '家庭小炒皇', '1、鲜鱿鱼、鳝鱼肉、香干、红椒、生姜全部切丝，韭菜花切段。为食街\r\n\r\n　　2、烧锅下油，放入生姜丝、鲜鱿鱼、鳝鱼肉、香干、红椒丝、盐炒至出味时。\r\n\r\n　　3、然后调入味精、蚝油，用大火爆炒，再用湿生粉勾芡，淋入麻油，翻炒几下出锅即成。\r\n', NULL, NULL, '韭菜花10克、姜一块花生油20克、盐10克、味精8克、蚝油5克、麻油5克、湿生粉适量、胡椒粉少许', '鲜鱿鱼50克、鳝鱼肉50克、香干30克、红椒一只');
INSERT INTO `recipe` VALUES (5500, 424, '瘦身乌发－素炒黄豆芽', '制法： \r\n1、将黄豆芽去皮，去根毛，洗净。 \r\n2、将锅烧热，加入花生油，将油烧热，倒入黄豆芽，煸炒至半熟，加酱油、精盐、姜片及适量水，继续编炒，加入白糖再烧段时间即可出锅装盘。 \r\n', ' 食之清脆，不腻爽口。 \r\n黄豆芽性味甘寒，内含有蛋白质、脂肪和较高的维生素C、胡萝卜素、矿物质等物质。豆芽含有较多的纤维素，有利减肥。 \r\n    因此，黄豆芽能改善头发组织，使人保持头发乌黑发亮。并可润肤、减肥。女性多吃黄豆芽可以得到乌发、润肤、减肥三个方面的全面美容效果。 \r\n', NULL, '精盐、酱油、白糖、姜片、花生油各适量。 ', '黄豆芽500克，\r\n');
INSERT INTO `recipe` VALUES (5501, 424, '自然美胸－黄豆排骨汤', '(1)将猪排骨洗净，剁成块；黄豆、大枣、生姜洗净；黄芪、通草洗净用纱布包好，成药包。 \r\n\r\n2)锅内加水，用中火烧开，放入排骨、黄豆、大枣、生姜和药包，用文火煮2小时，拣去药包，加盐调味即成。 \r\n', '肉香汤鲜，可喝汤、食肉及黄豆、大枣。 \r\n此菜益气养血通络，适用于气血虚弱所致乳房干瘪之女性', NULL, '生姜片、盐各适量', '猪排骨500克，黄豆50克，大枣10枚，黄芪20克，通草20克');
INSERT INTO `recipe` VALUES (5502, 424, '产后塑身－鲜鲤鱼汤', '1. 鲤鱼洗净，去鳃及内脏，滚水汆烫。 \r\n2. 水烧开，放入鱼、姜及其它调味料，转小火煮15分钟(视鱼大小而定)至鱼熟。 \r\n', '鲤鱼含丰富蛋白质、铁质、钙质，以及各类维生素。鲤鱼汤热量低，且可帮助促进乳汁分泌，想喂哺母乳又怕猪脚花生热量过高的产妇，不妨以这道汤品取代。\r\n', NULL, ' 酒、盐、生姜 ', '鲤鱼 500克');
INSERT INTO `recipe` VALUES (5503, 424, '产后减肥－青木瓜肋排汤', '１．肋排切小块，用滚水汆烫。 \r\n２．青木瓜切小块备用。 \r\n３．水烧开，放入肋小排、青木瓜、嫩姜片、盐，以大火烧开后转小火煮约25分钟，洒上葱花即可盛出。 \r\n', ' 青木瓜是一种高纤食品，又可促进乳汁分泌；肋骨排富含钙质、铁质及蛋白质。这是一道很不错的产后汤品', NULL, ' 盐、嫩姜、葱花、酒少许 ', ' 肋排350克、青木瓜 300克\r\n');
INSERT INTO `recipe` VALUES (5504, 424, '纤体活肤－苹果雪梨煲排骨', '   1 将苹果、雪梨洗净后切薄片。  \r\n　 2 将排骨洗净后切块备用。  \r\n　 3 将南北杏、红枣泡水清洗。  \r\n　 4 将排骨氽烫后捞起备用。  \r\n　 5 将姜、排骨、苹果、雪梨、南北杏、红枣放入水中，以大火煮20分钟后，再以中火煮1个小时，起锅前加入盐调味即可。', ' 滋润养颜、生津健脾、祛寒解郁  　　\r\n 活化肌肤，窈窕纤体  \r\n', NULL, '姜2片、水3800毫升', '排骨250克、苹果3个、雪梨3个、南北杏20克、红枣20克、水3800毫升');
INSERT INTO `recipe` VALUES (5505, 424, '纤体降脂－萝卜甘蔗马蹄汤', '  1 将甘蔗切成10厘米（1厘米＝0.01米）一段，再从中间切成4块（对切再对切）。  \r\n　 2 将马蹄洗净，直接拍碎。  \r\n　 3 将胡萝卜洗净、去皮，切成现状。  \r\n　 4 接着将所有材料放入锅中，加水煮1小时，最后加入适量的冰糖调味即可。  \r\n', ' 活络气血、清热降火、治便秘、纤体  \r\n', '甘蔗以刀背拍开，这样可增加汤的甘甜度，甘蔗去皮或不去皆可。甘蔗含有铁质，多喝此汤可以补充人体的铁质。', ' 冰糖适量  ', '马蹄300克、甘蔗400克、胡萝卜500克  \r\n　　\r\n');
INSERT INTO `recipe` VALUES (5506, 424, '纤体健脾－椰子银耳煲老鸡', '1 将椰子去皮，取其椰子水与新鲜椰子肉。  \r\n2 将鸡氽烫后备用。  \r\n3 将银耳先泡水15分钟，洗净去带备用。  \r\n4 将鸡放入锅中，加热水淹过鸡肉，以大火煮沸，转中火续煮45分钟。再放入银耳、红枣、姜片，一起煮45分钟，然后加盐调味即可。  \r\n', '消暑、降火、健脾胃、纤体  \r\n', '椰肉中的纤维质具有纤体的功效。整个的椰子自己家取椰汁及椰肉较为困难，购买时请卖主切开，帮忙取出椰子水与新鲜椰肉，这样可以方便又省力。 ', '红枣12粒、姜3片  ', '土鸡1只（约500克）、椰子1个、干银耳40克、红枣12粒、姜3片  ');
INSERT INTO `recipe` VALUES (5507, 424, '美丽塑身核桃蚝油生菜', '将结球莴苣洗净后滤干水分，然后将核桃在小火上干炒，炒熟后压碎。而后大火快炒结球莴苣约1分钟，淋下蚝油，改小火煮约3分钟。将生菜盛入碗，撒下核桃屑即可。\r\n', NULL, NULL, '蚝油1汤匙半，核桃2两，油3匙。', '结球莴苣1个');
INSERT INTO `recipe` VALUES (5508, 424, '莲子红枣汤', '将莲子泡水2至3小时，红枣泡水10分钟。用一锅水将莲子以小火炖煮约3小时，将莲子煮烂，然后放入红枣及冰糖再煮10分钟即可。这道饮品不但可冷藏后食用，也可热食，一年四季男女老少皆适宜唷！ \r\n\r\n', NULL, NULL, NULL, '干莲子100克、红枣30克、冰糖100克  ');
INSERT INTO `recipe` VALUES (5509, 424, '生菜沙拉简单丰胸', '将莴苣与甜椒洗净后，用冷开水浸泡３分钟。滤干水分盛入盘中，食用时    \r\n拌入色拉酱即可。  \r\n', '所有叶菜类食物中，莴苣是最具丰胸效果的蔬菜唷，不妨多吃莴苣，不但能丰胸，也能使身体健康', NULL, '甜椒2个，色拉酱1包。', '结球莴苣350克');
INSERT INTO `recipe` VALUES (5510, 424, '腰果虾仁', '1腰果用油炸熟备用，然后将虾仁洗净去肠泥，沥干水分。将酱油、葱末、蛋白混合拌匀，再将虾仁浸泡于酱油、葱末与蛋白混合的汁液内，放置冰箱冷藏约1小时。  \r\n\r\n2油烧热熄火后，将虾仁中多余的调味料沥掉，放入油中搅开泡熟，再将油滤掉，腰果与虾仁拌匀后即可食用。\r\n', NULL, NULL, '、盐、麻油、葱末少许、蛋白1个、油3匙。  ', '腰果100克、虾仁100克');
INSERT INTO `recipe` VALUES (5511, 424, '丰胸美容－木瓜炖鱼', ' 1、先将木瓜洗净并切块，再放入水中熬汤，先以大火煮滚，再转小火炖约半小时。  \r\n\r\n 2、再将鱼切块，放入一起煮至熟，并加少许盐即可。  \r\n', NULL, '木瓜含有丰富的木瓜酉每，对胸部发育有很大的帮助，另也可依自己的喜好，搭配肉类等。 \r\n', '水4碗、盐少许', '青木瓜300克、鲜鱼500克 \r\n');
INSERT INTO `recipe` VALUES (5512, 424, '猪肾木耳汤', '将猪肾一切两半，除去臊腺，洗净，切成兰花片，清水泡一会。木耳用清水洗净泥沙，待用。将猪肾、木耳、笋片一起下水锅煮熟后捞出，入汤碗内，加入葱段、食盐、胡椒粉，再将烧沸的上汤倒入汤碗内即成。 \r\n', '健脾生血，补中益气，养肝明目，补肾益精。适用于青年妇女乳房瘦小、面色不华、腰膝无力等。对于分娩后母体康复及乳汁分泌均有促进作用。\r\n', NULL, '大葱段10克，上汤2 000毫升，胡椒粉、食盐各适量。 ', '猪肾、木耳（用水浸发）各200克，笋片30克，\r\n');
INSERT INTO `recipe` VALUES (5513, 424, '养血丰乳－石头鱼豆腐汤', '锅内加植物油，烧至五成热，放入石头鱼煎透，放生姜、绍酒，加入食盐、豆腐和适量水，炖至汤为白色时，加生菜、胡椒粉即成', '补益脑髓，祛风定脑。养血丰乳。适用于肾阴不足、阴血亏虚致脱发或乳房发育不良、扁平不丰或产后乳少等。\r\n', NULL, '生姜3片，生菜100克，植物油、食盐、胡椒粉、绍酒各适量。 ', '石头鱼（剥皮）500克，豆腐200克，\r\n\r\n');
INSERT INTO `recipe` VALUES (5514, 424, '桂圆红枣汤', '（1）将水煮沸后，变成小火。 \r\n（2）将桂圆放进沸水中煮20分钟后，放入红枣，再焖煮20分钟即可。\r\n', '红枣对促进血液循环，畅通乳腺很有帮助，而且它和桂圆还具有极佳的补血养气效果！ \r\n', NULL, NULL, '红枣100克、桂圆80克、水适量');
INSERT INTO `recipe` VALUES (5515, 424, '绿色减肥汤(1)萝卜汤', '红白萝卜各(洗净切块)，姜加水煮熟，食盐调味，饮汤吃萝卜。 \r\n', NULL, NULL, '姜10克，加水煮熟，食盐调味，饮汤吃萝卜。 ', '红白萝卜各250克');
INSERT INTO `recipe` VALUES (5516, 424, '绿色减肥汤(2)冬瓜皮蚕豆汤', '冬瓜皮50克，蚕豆60克，清水三碗煎至一碗，去渣饮用。 \r\n', NULL, NULL, NULL, '冬瓜皮50克,蚕豆60克');
INSERT INTO `recipe` VALUES (5517, 424, '绿色减肥汤(3)赤小豆鲤鱼汤', '赤小豆150克，鲤鱼一块(去内脏)，加水适量煲汤，食盐调味服用。 ', NULL, NULL, NULL, '赤小豆150克,鲤鱼一条');
INSERT INTO `recipe` VALUES (5518, 424, '绿色减肥汤(4)泥鳅豆腐羹', '将泥鳅放盆中养1-2天后，取出与玉米须、豆腐共入沙锅，加水适量煎煮，待烂熟后调味服用，每天一次', NULL, NULL, NULL, '鲜豆腐100克，活泥鳅250克，玉米须(布包)30克。');
INSERT INTO `recipe` VALUES (5519, 424, '减肥佳品－豆腐白果栗子汤', '  1.白果去壳，放入滚水浸片刻，取出，去衣和去心。 \r\n   2.栗子肉放入滚水内浸片刻，取出去衣。 \r\n   3.瘦肉洗干净，汆烫后再冲洗干净。 \r\n   4.煲滚适量水，下豆腐、白果、栗子肉、冬菇、瘦肉、姜片，煲滚后改慢火煲2小时，下盐调味即成。 \r\n', '   此汤生津润燥，对节食人士有补充营养和去除脂肪之作用。\r\n', NULL, ' 姜 2片  盐 适量 ', '豆腐 2块 \r\n    白果 38克 \r\n    栗子肉 225克 \r\n    冬菇（浸软） 6朵 \r\n    瘦肉 150克 \r\n   \r\n');
INSERT INTO `recipe` VALUES (5520, 424, '减肥菜谱1_双菇凉瓜丝', ' 制作方法：(1)将凉瓜顺丝切成细丝，姜片切成细丝；(2)香菇浸软切丝，金针菇切去尾端洗净；(3)油爆姜丝后，加入凉瓜丝、冬菇丝及盐，同炒至凉瓜丝变软；(4)将金针菇加入同炒，加入调味料炒匀即可食用。 \r\n', ' 功效：香菇、金针菇能降低胆固醇；凉瓜富含纤维素，可减少脂肪吸收。 \r\n', NULL, '姜、酱油、糖、香油适量。 ', '原料：凉瓜(即苦瓜)150克，香菇100克，金针菇100克，');
INSERT INTO `recipe` VALUES (5521, 424, '减肥菜谱2_香菇豆腐', ' 制作方法：(1)将豆腐切成四方小块，中心挖空；(2)将洗净泡软的香菇剁碎，榨菜剁碎，加入调味料及淀粉拌匀即为馅料；(3)将馅料酿入豆腐中心，摆在碟上蒸熟，淋上香油、酱油即可食用。\r\n   \r\n', ' 功效：香菇可降低胆固醇，豆腐有利减肥。 ', NULL, '榨菜、酱油、糖、香油、淀粉适量', '豆腐300克，香菇3只');
INSERT INTO `recipe` VALUES (5522, 424, '减肥菜谱3_枸杞烧鲫鱼', ' 制作方法：(1)将鲫鱼去内脏、去鳞，洗净，葱切丝，姜切末；(2)将油锅烧热，鲫鱼下锅炸至微焦黄，加入葱、姜、盐、胡椒面及水，稍焖片刻；(3)投入枸杞子再焖烧10分钟，加入味精即可食。\r\n    \r\n', '功效：枸杞可防治动脉硬化，鲫鱼含脂肪少，有利减肥。 ', NULL, '豆油、葱、姜、胡椒面、盐、味精适量。  ', '原料：鲫鱼1条，枸杞15克');
INSERT INTO `recipe` VALUES (5523, 424, '减肥菜谱4_木耳豆腐汤', ' 制作方法：(1)先将黑木耳泡发后洗净，豆腐切成片；(2)将豆腐与黑木耳加入鸡汤及盐同炖10分钟，即可食用。\r\n   \r\n', '功效：黑木耳及豆腐均为健康食品，可降低胆固醇 \r\n\r\n　\r\n', NULL, '盐少许，鸡汤1碗', '黑木耳25克，豆腐200克，');
INSERT INTO `recipe` VALUES (5524, 424, '健康减重_鳕鱼沙拉', '1、将马铃薯放入加盐的水中煮20分钟，去皮后，切成片。鱼排切成丁，抹上柠檬汁、盐和胡椒调味后，腌30分钟。将洋葱剥去皮后，切为8块。清洗西洋丝瓜，纵切成两半后，再切为半圆形的薄片。\r\n 2、在不沾锅中热油，放入洋葱和鱼，用大火煎5分钟，并且小心翻面。将切片的西洋丝瓜和马铃薯放进锅中，用中火继续加热5分钟，至熟。\r\n3、将水、法式鲜乳酪、芥末和龙蒿一起搅拌，倒入煮鱼的锅中，小心翻动，并以小火再煮上2分钟，即可', NULL, NULL, '盐、柠檬汁数滴、胡椒、洋葱2个、小的西葫芦1条（可用小黄瓜替代）、油1汤匙、水75毫升、法式鲜乳酪1汤匙、芥末2汤匙、干的龙蒿（又称香艾菊）1茶匙', '马铃薯3个、鱼排（鳕鱼、鲈鱼、鲑鱼或是鲽鱼）150克、');
INSERT INTO `recipe` VALUES (5525, 424, '滋阴养血_桑蒲熘蛋泥', '1)将桑寄生、香蒲去净尘埃，烘干研成粉末；鸡肝、鸡心洗净，剁成茸泥；鸡蛋黄搅散。鸡肝、鸡心泥加入绍酒、味精、精盐拌匀5分钟后，加入鸡蛋黄、中药末(桑寄生、香蒲末)拌匀。 \r\n(2)净锅置火上，加入熟猪油，烧至五成热时，放入蛋、肝茸泥，馏炒成熟，划散后起锅入盘。', '此菜质地松散，鲜香爽口。', NULL, '味精1克，精盐2克，绍酒5克。', '桑寄生10克，番蒲10克，鸡肝30克，鸡心1个，鸡蛋黄4个，熟猪油50克');
INSERT INTO `recipe` VALUES (5526, 424, '安神美容_桂圆炖蛋', '1.桂圆肉干用温水洗净。\r\n2.取4个饭碗，每碗放6－7分满的水及蛋1个，1/4的桂圆肉干，再放入蒸锅中。;蒸约5－8分钟即可食用，如喜食较甜者的，可事先多加些冰糖。', '补气血，益心气，安神', NULL, NULL, '蛋4个，清汤3饭碗，去壳纯桂圆肉干2大匙');
INSERT INTO `recipe` VALUES (5527, 424, '去脂汤水_西芹生菜豆腐鱼尾汤', '1.西芹洗干净，切段。\r\n2.生菜洗干净。 \r\n3.冬菇、木耳洗干净。\r\n4.豆腐冲净（小心避免冲散）。 \r\n5.鱼尾去鳞洗干净。 \r\n6.下油把鱼尾略煎，盛起。 \r\n7.煲滚适量水，下西芹、生菜、豆腐、冬菇、木耳、鱼尾、姜片，煲滚后以慢火煲90分钟，下盐调味即成。', '此汤材料丰富，有清热解毒、去脂及补充', NULL, '姜;2片 盐适量 ', '西芹225克 ,生菜300克 ,豆腐100克,冬菇20克,木耳(水发)20克,鱼尾1条 ');
INSERT INTO `recipe` VALUES (5528, 426, '什锦煎饼', '1。面粉加鸡蛋液，盐，生抽，味精，葱粒，姜末红椒末，水混合调成稍厚的面糊\r\n2。各料倒在一起氽水后捞起倒在面糊中拌和成什锦面糊。\r\n3。平底锅置火上，加色拉油烧到七成热，把面糊倒入，用中火烤煎，待煎到两面金黄色，即捞起沥油装盘。上桌时用小刀子切开。', NULL, NULL, '黑木耳丝，咸菜粒，鸡蛋液，鸡肉片，红椒末，色拉油，盐，味精，生抽，葱粒，姜末各适量', '面粉300克，');
INSERT INTO `recipe` VALUES (5529, 426, '开口笑', '1鸡蛋磕入碗中打散，加苏打，发酵粉，饴糖，白糖，油，水放在一起搅拌均匀，倒进面粉揉和成面料，并做成25克重的小剂。\r\n2。将面剂搓圆，轻轻滚上白芝麻，分批放入温油锅中炸，炸透开口即可。', NULL, NULL, '苏打，发酵粉，油适量。', '面粉500克，饴糖200克，鸡蛋5只，白糖200克，白芝麻100克');
INSERT INTO `recipe` VALUES (5530, 426, '蔬菜煎饼', '1。全部蔬菜氽水后切碎，面粉加温水充分搅拌，放入蔬菜，鸡蛋液，麻油搓揉均匀。\r\n2烧热麻油，倒入拌好的饼料，压成薄片以中火将其两面煎至略焦，边煎边将面饼压薄煎透。\r\n3。饼煎好后取出切块，用酱油，醋，葱末，芝麻拌成的酱汁蘸食。', NULL, NULL, '鸡蛋液，盐味精，麻油，酱油，熟芝麻，葱，醋各适量。', '面粉200克，芹菜，韭菜，青椒，红萝卜，青菜心各100克，');
INSERT INTO `recipe` VALUES (5531, 426, '蛋黄豆沙粽', '1.糯米浸2小时沥干，豆沙搓成长条，掐成10段，板油切成10小片，放入碗中，用白糖腌渍\r\n2。青粽叶洗净，用2张平叠折好，先放糯米，再放板油，豆沙，蛋黄，盖上糯米，包紧，用线扎实。\r\n3。大锅烧开水，放入粽子，大火煮开，改小火煮2小时后，再焖1小时即可。', NULL, NULL, '盐适量', '糯米700克,赤小豆300克,猪板油100克,白糖100克,熟咸蛋黄10只,青粽叶.麻线各适量');
INSERT INTO `recipe` VALUES (5532, 426, '羊肉粉团烧卖', '1.米粉加水，和成略干的粉团，干笋浸泡后取嫩段切片。\r\n2。羊肉切碎斩末，加酒，盐，生抽，味精，葱，姜末，淀粉，拌成馅，腌10份钟。\r\n3。烧卖皮一张，包上羊肉馅，封住口子，上面放米粉团，红椒末和一片干笋，上笼隔水蒸20分钟即可。', NULL, NULL, '红椒末，干笋，酒，盐，生抽，味精，葱末，姜末，淀粉各适量。', '烧卖面皮20张，羊肉200克，粳米粉100克');
INSERT INTO `recipe` VALUES (5533, 426, '小笼包', '1.发面搓长条,掐成小面团\r\n2.肉泥,葱末,姜末,盐,酱油味精,麻油拌和成馅.\r\n3.小面团擀圆薄皮,包入肉馅,捏成包子状,放入铺白布的蒸笼内,用大火蒸15分钟即可.', NULL, NULL, '盐酱油,味精,麻油适量', '发面600克,肉泥250克,葱末,姜末各50克');
INSERT INTO `recipe` VALUES (5534, 426, '虾肉蒸饺', '1.备好的全部用料\r\n2.虾仁洗净对切和肉末,笋末一起,加盐,糖,味精,麻油,胡椒粉,拌成馅料,面团分小剂,擀成饺子皮.\r\n3.平摊饺子皮,加馅料,捏成饺子状,放入蒸笼大火蒸10分钟即可.', NULL, NULL, '盐,糖,味精,麻油,胡椒粉,猪油各适量', '面团1份,虾仁250克,熟笋末100克,肥肉末50克,');
INSERT INTO `recipe` VALUES (5535, 426, '炸芝麻球', '1.面粉,糖,小苏打,番薯泥一起放入碗中,冲入温水,用筷子搅拌,待凉后用手揉匀,搓成长条切成20小块.\r\n2.每小块面压扁，包入1小团豆沙，搓圆粘上白芝麻，投入6成热油锅炸，待浮起后开大火炸透即可。', NULL, NULL, '小苏打,白芝麻,豆沙,温水各适量', '面粉500克,番薯泥200克');
INSERT INTO `recipe` VALUES (5536, 426, '干蒸菜肉饺', '1。菜，韭黄，肉茸，盐，酒，味精，麻油，白酱油拌匀成馅，用饺子皮一包成饺子，排在面板上，撒上少量干面粉。\r\n2。烧滚水，饺子入笼蒸约20分钟，上桌时伴醋一小碟蘸食。', NULL, NULL, '盐，酒，味精，醋，麻油，白酱油各适量', '饺子皮500克，肉茸250克，菜末150克，韭黄末50克');
INSERT INTO `recipe` VALUES (5537, 426, '炸素春卷', '烧热油锅，先炒三丝，后加豆芽及盐，味精，胡椒粉，快炒数下盛出，待凉后加麻油拌匀。\r\n2。春卷皮平摊，取炒好的材料1/2份放在春卷皮上，包卷成长形，粘上水封实。放入油锅内炸至金黄色即可。', NULL, NULL, '盐，麻油，味精，胡椒粉各适量。', '春卷皮12张，豆腐干丝，香菇丝，红萝卜丝，豆芽各50克');
INSERT INTO `recipe` VALUES (5538, 426, '脆皮肉松卷', '1。烧热油锅，倒入肉泥，加酒炒散，倒入香菇末，红萝卜末，芹菜末，调味料，拌炒均匀成馅。\r\n2。盘中撒少量干淀粉，取一张糯米纸平铺盘面，放入馅料，卷成长条形，再撒上干淀粉。\r\n3。面粉加蛋液打成糊，烧热油锅，投入沾上蛋糊的糯米卷，炸至金黄色时捞出，食时蘸椒盐即可。', NULL, NULL, '盐，面粉，胡椒粉，味精，糖，酒，蛋液，色拉油各自适量', '肉泥200克，香菇末，芹菜末，红萝卜末各50克，糯米纸。');
INSERT INTO `recipe` VALUES (5539, 426, '番薯糕', '1.薯粉加糖水搅拌均匀,揉搓成面团,压扁，撒上碎板栗肉压平，上笼蒸熟。\r\n2.柠檬切片垫盘底,上盖薯片即可.', NULL, NULL, NULL, '番薯粉,板栗肉,糖,柠檬果各适量');
INSERT INTO `recipe` VALUES (5540, 426, '萝卜丝饼', '1.萝卜丝加盐拌匀后捏干水分,加入葱末,虾米,芹菜末及调味料拌成馅料.\r\n2.面剂擀成圆薄片,包入馅料擀薄,下锅煎至两面金黄色即可.', NULL, NULL, '盐,胡椒粉,麻油各适量', '烫面面团小剂10份,萝卜丝,虾米,葱末,芹菜末');
INSERT INTO `recipe` VALUES (5541, 426, '芝麻葱饼', '1.在揉面团时加糖,使面团带软,蛋打散.\r\n2.面团擀成方形,撒上盐,葱末,对折成长形,刷上蛋液.撒上白芝麻.\r\n3.切成10厘米左右的长面块.排入烤盘,以大火200摄氏度烤12分钟即可.', NULL, NULL, '盐,的芝麻,鸡蛋各适量.', '发面面团1份,糖100克,葱末100克');
INSERT INTO `recipe` VALUES (5542, 426, '芋头角', '1.芋头去皮,蒸熟刨丝.\r\n2.趁热加粉类及盐捣烂.\r\n3.用力揉乱皮料\r\n4.虾仁,肉茸,鲜菇,调味料拌匀成馅.\r\n5.芋头皮料分小剂,压扁。\r\n6包入馅料，合口处沾点水，调整形状。\r\n7。油烧到7成热，放入粘上蛋液的芋头角，炸4分钟，略呈焦色即可。', NULL, NULL, '盐,酱油,麻油,姜,糖,味精各适量.', '芋头500克,虾仁200克,肉茸100克,鲜菇粒50克,蛋白2份,面粉,淀粉');
INSERT INTO `recipe` VALUES (5543, 426, '焖煎鸡肉饼', '1。鸡肉泥，葱末，蒜泥，面粉及调味料均匀倒入大碗内，用手搅拌成稠状，加麻油再搅拌成肉团。\r\n2。锡纸1张，里层抹麻油，把肉团放入中间，用手压成1厘米厚的饼，撒上白芝麻。\r\n3。色拉油入锅烧热，鸡肉饼入锅，锡纸当锅盖用小火煎约3分钟。\r\n4。反面同样焖煎约3分钟，到两面焦黄时起锅切块。莴笋叶作点缀。', NULL, NULL, '酒，酱油，味精，胡椒粉，麻油，色拉油，莴笋叶，锡纸各适量。', '鸡肉泥300克，葱末100克，蒜泥，面粉，白芝麻各50克');
INSERT INTO `recipe` VALUES (5544, 426, '豆沙包', '1。豆沙，糖，芝麻粉拌匀，分小剂，每剂裹一片猪油，搓圆成馅。\r\n2。面团分小剂，擀薄，包入几粒豆沙捏成包子状。\r\n3。用模具在面皮表面压一下成形，放入蒸笼蒸约有18分钟即可。', NULL, NULL, NULL, '发面面团1份，豆沙500克，芝麻粉100克，猪板油片200克，糖200克。');
INSERT INTO `recipe` VALUES (5545, 426, '薯粉蟹肉饼', '1。番薯粉加温水揉搓成面皮，分小剂擀薄。\r\n2。烧热油锅，爆香蒜，姜，葱，炒散猪肉泥，蟹肉，加调味料炒匀成馅。\r\n3。薯粉皮薄片，包入馅料压扁，入油锅炸成焦黄色即可。', NULL, NULL, '蒜茸，姜末，葱末，酱油，盐，味精各适量 ', '番薯粉400克，猪肉泥150克，蟹肉150克');
INSERT INTO `recipe` VALUES (5546, 426, '四味烧卖', '1。首先制作好全部馅料。\r\n2。每张烧卖皮取4个角向中心捏住成花形。花瓣部分分别塞入青菜，虾仁，香菇，蛋黄液。\r\n3。烧卖放进蒸笼，以大火蒸15分钟左右，出笼淋上麻油即可。', NULL, NULL, '蛋黄2只，姜末，葱末，盐，酱油，麻油，酒味精各适量', '烧卖皮20张，青菜心200克，猪肉末200克，香菇50克，虾仁50克');
INSERT INTO `recipe` VALUES (5549, 57, '芡实薏米粥', '以上原料共煮粥.', '可治疗白带过多', NULL, '油,盐', '薏米60克,芡实90克,大米100');
INSERT INTO `recipe` VALUES (5550, 104, '猪脚生姜甜醋汤', '将猪蹄,生姜,甜醋,一起放入锅里同煮,煮沸后,小火慢炖至猪蹄熟烂.', '补气血,通乳汁,散瘀血作用', NULL, '盐,', '猪蹄2只约2000克.生姜500克,甜醋1000克');
INSERT INTO `recipe` VALUES (5551, 428, '白扒四宝', '1.将鸡脯肉片去筋、皮，然后片成大薄片，放入锅中，加入少许精盐和少许玉米粉以及鸡蛋清拌匀上浆。将龙须菜整齐地放入小圆盘中，加入少许鸡油、少许精盐，上屉蒸10分钟取出。锅中注入200克清汤，加入料酒、精盐各少许，再放入鲍鱼，在微火上kao2分钟，倒入漏勺。坐煸锅，注入熟猪油，烧至五成热，下入浆好的鸡片滑熟，倒入漏勺控油。坐锅上火，注入汤，加1克精盐、少许绍酒，下入广肚氽透。\r\n2.将熟广肚、熟鲍鱼、熟鸡脯肉、熟龙须菜依次整齐码盘。锅中注入300克清汤，加入剩余的料酒、精盐，上火烧开，撇去浮沫，加入用水调稀的玉米粉勾芡淋上鸡油，浇在菜上即可', NULL, NULL, '料酒20克，精盐2.5克，鸡油15克，熟猪油500克(约耗40克)，湿玉米粉、白糖少许，清汤500克，奶汤30克，鸡蛋清1个。', '水发广肚250克，炖鲍鱼200克，鸡脯肉250克，桶龙须菜1桶。');
INSERT INTO `recipe` VALUES (5552, 429, '八宝膳粥', '1.将红小豆、绿豆洗净，分别放入两个小铝盆中，加清水，没过豆面4厘米，上火煮1小时，豆皮绽开，豆汤快熬干即成；将莲子加入食碱，用清水刷过，去外皮，捅出莲心，洗净；剥去栗子内外两层皮，洗净；剥去桃仁外皮，掰成两半；将花生米放入沸水盆中浸泡10分钟，将皮捏去；将小枣洗净去核；用清水将江米洗净。 \r\n\r\n2.将清水注入铜锅，旺火烧沸，下入江米、红小豆、绿豆、莲子、栗子、桃仁、花生仁、小枣，再煮沸，然后用微火熬30分钟左右，熬至米烂汁稠即成。', NULL, NULL, NULL, '江米150克，红小豆75克，绿豆75克，小枣50克，莲子50克，桃仁50克，花生仁50克，栗子50克，食碱0.5克。');
INSERT INTO `recipe` VALUES (5553, 430, '白梨凤脯', '1.将鸡脯肉剔去筋、皮，洗净，用刀片成长1寸5分、宽6分的 薄片，放入锅中，加入鸡蛋清和料酒、精盐各少许拌匀，再 加入少许玉米粉，搅匀上浆。 \r\n\r\n2.用清水将京白梨洗净，削去外皮，切成两瓣，剔去梨核， 切成长1寸5分、宽6分的薄片，放入碗中，注入清水浸泡，防 止发锈。 \r\n\r\n3.坐煸锅，注入熟猪油，烧至六成热，下入鸡片，用铁筷子 划开，倒入漏勺中。煸锅留底油，放入葱姜末炝锅，加入料酒、 精盐、清汤和京白梨片，烧开后，放入鸡片，用水调稀玉米粉， 倒入锅中勾成芡汁，翻炒均匀，淋上鸡油即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，葱姜末少许，清汤50克，鸡蛋清1个， 湿玉米粉15克熟猪油500克(约耗25克)，鸡油10克。', '鸡脯肉150克，京白梨3个。');
INSERT INTO `recipe` VALUES (5554, 431, '百寿桃', '1.将发面对好碱，加入白糖揉匀，饧10分钟后揪成 15个面剂，用手揉成扁圆形，再擀成圆皮。 \r\n\r\n2.将枣泥馅分成15份，分别包入圆皮中，用手揉成 圆锥形，上端做出一个桃尖，再用竹刮板在桃身上 竖着印出一条印痕，把两片香菜叶贴在寿桃的下部， 饧10分钟后上屉蒸8分钟，取出后在桃尖上稍抹一点 红色即可。', NULL, NULL, NULL, '发面225克，白糖25克，枣泥馅100克，香菜叶少许。');
INSERT INTO `recipe` VALUES (5555, 432, '百鸟还巢', '1.用手将活鹌鹑剥去皮毛，朋腹下掏去五脏，洗净，放大碗中， 加酱油、料酒、精盐、五香料、葱姜段拌匀，腌30分钟，放入 清水锅中，上火煮熟，捞出后去皮，洗净。将香菇、玉兰片、 火腿均切成长1寸的细丝，用开水氽一遍。 \r\n\r\n2.将土豆洗干净后去皮，切成长1寸的细丝(粗细均匀)。坐煸锅， 注入花生油烧至七成热，将土豆丝放入漏勺中，一同下油锅， 将土豆炸成金黄色，并成为鸟巢，取出，轻轻地放入盘中。 \r\n\r\n3.把2个鸡蛋ke在碗中打散，加入精盐、玉米粉拌匀。煸锅坐火 上烧热，将锅内刷上一层油，倒入鸡蛋液，吊成鸡蛋皮，取出， 用小刀在鸡蛋皮上刻成花边，轻轻铺在土豆丝鸟巢内。 \r\n\r\n4.锅中注入清水，下鹌鹑蛋煮熟剥去皮。 \r\n\r\n5.锅中注入清汤，加入精盐、料酒，放入香菇丝、玉兰片丝、 火丝，上火烧开后放入鸽蛋，在微火上炖5分钟，再用水调稀玉 米粉，倒入锅中勺成汁，倒入巢中间。 \r\n\r\n6.坐煸锅，注入花生油，烧至七成热，将鹌鹑下入油锅炸熟捞 出。再将油烧热，再下入鹌鹑炸成金红颜色，捞出控净油，码 在鹌鹑蛋上即可。', NULL, NULL, '精盐3克，料酒20克，湿玉米粉10克，花生油1公斤(约耗50克)， 五香料少许酱油25克，鸡油10克，葱、姜段各少许，清汤50克。', '鹌鹑12只，鹌鹑蛋20个。 配料：土豆1.5公斤，鸡蛋2个，水发香菇50克，水发玉兰片25克，熟 火腿25克。');
INSERT INTO `recipe` VALUES (5556, 433, '拌银耳', '1.将银耳控水后加调料拌匀。\r\n\r\n2.将胡萝卜切片，用开水略烫，放 清水中过凉，再拌入银耳，即可装盘。 \r\n\r\n3.将洗净的油菜心放入开水锅中氽一遍捞出。另起锅，注入250克 清汤，放入银耳、油菜心，加料酒、精盐各少许，火上煨3分钟， 挑出菜心，将银耳到入漏勺中控净汤。将银耳放入鱼丸子中间， 银耳上放两棵油菜心，在每个油菜叶上放一粒珍珠鱼丸。 \r\n\r\n4.锅中注入250克清汤，上火烧开，撇去浮沫，加入剩余的料酒、 精盐，用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在 珍珠雪耳上即成。', NULL, NULL, '精盐、香油、香醋各少许。', '水银耳150克。 配料：胡萝卜少许。');
INSERT INTO `recipe` VALUES (5557, 428, '仿膳饽饽', '1.将50克水发海参、50克虾肉用水洗净，与冬笋、冬菇一起剁成末，与猪肉末一起放入盆中，加入精盐、料酒、酱油、葱姜末，搅拌均匀，即成饽饽馅。 \r\n\r\n2.将60克清水放入面粉中和匀搓透，饧十分钟，用手揪成60个面剂，分别擀成圆皮，包上馅心，即成仿膳饽饽。 \r\n\r\n3.锅中注入清水，上火煮沸，放入饽饽，煮熟后捞出，盛入汤gu子中。 \r\n\r\n4.将100克海参和100克虾肉片成抹刀片，洗净氽透；将豆苗洗净。将以上三种原料放入gu肉子中。另将奶汤烧开后，调好口味，冲入gu子中即可。', NULL, NULL, NULL, '面粉150克，净虾肉150克，水发海参150克，冬笋25克，冬菇10克，猪肉末50克，奶汤1750克，精盐适量，料酒20克，酱油15克，香油15克，葱姜末5克，豆苗少许。');
INSERT INTO `recipe` VALUES (5558, 428, '凤凰趴窝', '1.从母鸡背部开膛，去五脏，洗净，由鸡腹内撤去大腿骨，用开水氽几遍。把鸡放入沙锅，注入清汤，加入15克料酒、少许精盐、葱姜段，在微火上焖两小时左右，然后再倒入大碗内，原汤上屉蒸烂。 \r\n\r\n2.用水将油菜叶洗净，切成丝。坐煸锅，注入花生油，烧至七成热时，下入油菜丝炸成松，捞出控净油。 \r\n\r\n3.将水发香菇、水发玉兰片、水发鱼肚均切成丝，放入开水锅中氽一遍捞出将100克清汤注入锅中，放入以上氽过的三种丝，加入料酒、精盐各少许，在微火上煨2分钟捞出，控净水，撒在盘中，周围撒上油菜松。 \r\n\r\n4.锅中注入清水，放入鹌鹑蛋，上火煮熟后捞出，剥去皮，摆在盘内两侧，半埋在丝中。把蒸好的母鸡控净汤汁，脯朝上，放在盘中。 \r\n\r\n5.将250克蒸鸡的原汤注入锅中，加入酱油、料酒各少许，对好口味，用水淀粉勾芡，淋上鸡油，浇于菜上即可。', NULL, NULL, '料酒20克，精盐2克，酱油10克，清汤600克，葱段50克，姜25克，湿淀粉15克，花生油500克(约耗20克)，鸡油15克。', '主料：母鸡1只，鹌鹑蛋15个。 \r\n配料：水发香菇50克，水发玉兰片50克，水发鱼肚50克，油菜叶100克。');
INSERT INTO `recipe` VALUES (5559, 61, '清蒸武昌鱼', '将鱼去鳞、鳃、内脏，洗净，鱼身两面划几个花，撒上精盐，盛入盘中。 \r\n香菇去蒂洗净，和熟火腿切成薄片，互相间隔在鱼面上。冬笋切成柏叶形的薄片，镶在鱼的两边，加葱，姜（拍松）和绍酒。 \r\n锅置在旺火上，下清水烧沸，将鱼蒸十分钟，蒸至鱼眼突出时出笼。拣出姜块、葱结。炒锅置在旺火上，下猪油烧热，泌入蒸鱼的原汁，下鸡汤烧沸，加入味精、鸡油起锅，浇在鱼面上，撒上胡椒粉，即成。', '此菜在白色的全鱼上面缀以红、黄、褐绿的各种配料，五彩缤纷。鱼肉肥美细腻，汤汁鲜浓清香，保持原汁原味。', NULL, NULL, '鲜团头鲂一尾（约重两斤），熟火腿半两，净冬笋一两，猪油一两半，味精三分，精盐半钱，葱结一钱半，水发香菇一两，鸡油二钱，鸡汤三两，料酒二钱，胡椒粉二分，姜末一钱半。');
INSERT INTO `recipe` VALUES (5560, 61, '红烧武昌鱼', '将鱼去鳞、鳃、内脏，洗净，在鱼身上划斜十字刀纹五个。玉兰片切成薄片。 \r\n炒锅置旺火上，下芝麻油烧热，把鱼下锅两面煎黄，加入料酒、姜末、酱油、精盐、葱段、玉兰片、清水等，一起烹调，待汤汁烧沸后，移锅置中火上烧十分钟，再端锅置旺火上继续烧三分钟，直到汤汁稠浓，即将鱼起锅盛入盘内。将原汁置旺火上，下味精、白糖，用湿淀粉勾芡，放入熟猪油，起锅浇在鱼面上即可。', '此菜色泽金黄，脂肪肥厚，肉质细糯，油润爽滑，味道异常鲜美。', NULL, NULL, '鲜团头鲂一尾（约重二斤），水发玉兰片一两，芝麻油一两，白糖一钱，料酒三钱，葱花一钱，熟猪油一两，味精半钱，湿淀粉六钱，酱油一两，姜末二钱。');
INSERT INTO `recipe` VALUES (5561, 61, '油焖武昌鱼', '将鱼去鳞、鳃、内脏，洗净。 \r\n在鱼身上两面划斜十字纹数个。用酱油半两涂抹鱼身，腌渍五分钟。肥膘猪肉、红辣椒、小葱、玉兰片，都切成一寸长的粗丝。 \r\n炒锅置旺火上，下麻油烧至八成熟，将鱼下锅，两面炸成淡黄色时捞出。原炒锅倒去余油后置旺火上，放入肥膘猪肉、红辣椒、小葱、玉兰片，炒二分钟，至葱散发出香味时，再将鱼下锅，加入料酒，姜末、酱油（半两）、白糖、味精、精盐、清水（八两）、焖烧三分钟。 \r\n待鱼汁渐浓，即移锅置微火上，加盖焖八分钟，再置旺火上，下猪油继续焖二分钟，起锅盛入盘即可。', '此菜黄色有光泽，皮酥滑，肉肥嫩，汁稠浓，味鲜美，咸甜中略带辣味。', NULL, NULL, '鲜团头鲂一尾（约重一斤），肥膘猪肉一两，猪油一两半，白糖半两，酱油一两，精盐半钱，姜末三钱，水发玉兰片一两，味精半钱，料酒三钱，红辣椒半两，小葱半两，芝麻油二斤（约耗一两）。');
INSERT INTO `recipe` VALUES (5562, 61, '葱油鳊鱼', '1、鳊鱼洗净后放入清水中，加入盐、料酒、姜等配料入锅同煮。 \r\n2、取出煮熟的蝙鱼，盛盘待用。香葱切末同味精等调料撒在鱼身上。 \r\n3、锅内倒入色拉油加热，将热油倒在鳊鱼身上，即成。', NULL, NULL, NULL, '新鲜鳊鱼一条，香葱若干，油盐味精料酒姜等调料备用。');
INSERT INTO `recipe` VALUES (5563, 61, '金沙武昌鱼', '1武昌鱼宰杀治净，改刀成段，用姜末、蒜末、精盐、料酒、胡椒粉、味精、白糖、辣椒面、生粉腌渍2小时。 \r\n2炒锅上火，注入色拉油烧至六成热，下鱼段炸熟，捞出沥油，在盘中摆成鱼形，撒上金沙料，即成。', '将蒜香骨的腌渍方法和金沙菜的制作方法相结合，创出了一道味道特别的“金沙武昌鱼”。', '金沙料制法：在250克炸面包糠和250克炸蒜茸中，加入适量熟芝麻、油酥花仁碎、精盐、鸡粉、辣椒面、红油，然后拌匀即成。', NULL, '武昌鱼1条(约500克) 姜末5克 蒜末10克 金沙料〔注〕80克 精盐、料酒、胡椒粉、白糖、辣椒面、生粉、色拉油各适量');
INSERT INTO `recipe` VALUES (5564, 61, '家乡武昌鱼', '1武昌鱼宰杀后，从背部剖开去内脏治净，再在鱼身两面剞上十字花刀，用姜片、葱段、精盐、胡椒粉腌渍6小时，中间需翻动2次，然后取出用毛巾搌干黏液，放在太阳下晾晒2～3天。 \r\n2锅置火上，放入少许色拉油烧热，下武昌鱼煎至两面呈金黄色，捞出摆在条盘上，浇上用老干妈辣酱、精盐、味精、料酒、蒜茸调成的酱料，上笼蒸5分钟，最后取出，撒上青红椒细丝、葱丝，淋上热油，即成。', '此菜借鉴了湖北梁子岛渔家独特的腌鱼法，这便是先腌后晒、先煎后蒸', NULL, NULL, '武昌鱼1条(约500克) 青红椒细丝、葱丝、姜片、葱段、蒜茸、精盐、料酒、味精、胡椒粉、老干妈辣酱、色拉油各适量.');
INSERT INTO `recipe` VALUES (5565, 61, '葱烤鳊鱼', '成型：将鳊鱼去鳞去内脏,剞斜刀,用葱、姜、酱油腌制4分钟。 \r\n烹调：将腌制好的鳊鱼下油锅,煎炸至皮脆骨酥,出锅滤油,锅内留油少许,加葱、姜、酱油、糖、黄酒、味精、醋炒出味,然后下炸好的鳊鱼用中火烧烤入味,猛火勾芡收汁,亮油出锅装盘。', '风味特色：酱色明亮,骨酥肉嫩,回味香甜,为佐酒佳肴。', '要领：烧烤时火要小。', '姜、酱油、糖、黄酒、味精、醋、水芡粉、色拉油等适量。', '半斤鳊鱼2条,葱100克。');
INSERT INTO `recipe` VALUES (5566, 428, '凤凰展翅', '1.把鸡蛋黄、鸡蛋清分别ke入两个碗中并打散，再加入料酒、精盐、清水各少许并拌匀，再分别倒入两个放油纸的平底四方铁模子中，上屉蒸熟取出，除去铁模子和油纸，晾凉后即成蛋黄糕和蛋白糕。 \r\n\r\n2.用水将3条黄瓜冲洗干净，用刀切成2寸段，然后片下黄瓜皮。将黄瓜皮放入盆中，加少许精盐拌腌5分钟，滗出汤汁，再加少许香油拌匀。 \r\n\r\n3.将胡萝卜刮去外皮，用清水冲洗干净，顺长切成两半，放入盆中，加入少许精盐拌腌10分钟，滗出汤汁，加少许香油拌匀。 \r\n\r\n4.用清水将1条黄瓜洗净，顺长切成两半，放入盆中，加少许精盐拌腌5分钟滗净汤汁，加少许香油拌匀。 \r\n\r\n5.将填鸭从尾部开膛，掏出五脏，剁去鸭掌，用水冲去血污。锅中注入清水放入填鸭，煮五成烂时捞出。另取锅，注入卤汤和葱姜段，上火烧开后放入填鸭，在微火上卤30分钟左右捞出，在鸭皮上涂抹一层香油，即成酱鸭子。 \r\n\r\n6.用刀从酱鸭子背部竖劈开，拆去鸭骨，留出一部分鸭皮，其余的鸭皮、鸭肉均切成长1寸2分的丝。 \r\n\r\n7.取12个羹匙，分别抹上一层香油，并分别ke入一个鸽蛋，上屉蒸熟取出，用小刀从羹匙边上划开，取出熟鸽蛋。 \r\n\r\n8.将蛋黄糕、蛋白糕、炝黄瓜皮、酱鸭皮均切成长2寸5分、一头宽3分，一头尖的长条。用小刀将炝黄条刻成凤凰嘴、爪子，将金糕刻成凤冠形，樱桃刻成眼睛。将剩余的黄瓜条、胡萝卜条分别切成宽5分的梳子块。将剩余的黄瓜皮切成四个1寸长的细丝。 \r\n\r\n9.将面包切成两个椭圆形片。用少许开水将黄油化开后，放入白糖，搅拌上劲，分别抹在两个面包托上。又分别在黄油上面放半个樱核，再分别在樱桃两侧码上两条黄瓜皮细条。 \r\n\r\n10.在大鱼盘中间放酱鸭丝，码成凤凰展翅的造型，在用五种颜色的条交错码成羽毛形，然后再码上嘴、爪子、眼睛和凤冠。用黄瓜和胡萝卜切成的梳子块码成两条尾巴，上面顺长摆上熟鸽蛋，在尾尖处各放上一个黄油面托即可。', NULL, NULL, '料酒5克，精盐1克，卤汤1500克，葱50克，姜25克，桂皮15克，大料10克，香油15克，白糖5克。', '主料：北京填鸭一只。 \r\n配料：鸡蛋8个，黄瓜500克(约4条)，金糕50克，胡萝卜250克，鸽蛋12个，黄油40克，面包50克，樱桃两粒。 \r\n');
INSERT INTO `recipe` VALUES (5567, 428, '凤尾群翅', '1.汤锅上火，注入清水烧开，放入水发鱼翅氽两遍后捞出，挑出杂物，洗净从母鸡腹部开膛，掏出五脏，从鸡背部劈开成两半，用水洗净。 \r\n\r\n2.锅中注入清水，放入半只母鸡、100克火腿末、350克猪肉，上火烧开后撇去浮沫。用纱布将鱼翅包起，放入锅中，在微火上炖5个小时左右，挑出鱼翅包，其余配料不用。 \r\n\r\n3.将700克清汤注入锅中，放入半只母鸡、150克火腿、400克猪肉、葱姜段，上火烧开后撇去浮沫，加入20克料酒、适量精盐，放入鱼翅包，在微火上再炖4至5小时。 \r\n\r\n4.取12个羹匙，抹匀鸡油，再在每个羹匙内ke入1个鹌鹑蛋，上屉蒸熟取出。 \r\n\r\n5.取出鱼翅包，打开纱布，将鱼翅在长鱼盘中摆成凤尾形状，上面码上鹌鹑蛋，点缀少许豆苗。 \r\n\r\n6.将300克清汤注入锅中，加入20克料酒和剩余的精盐、酱油，调好口味，用湿淀粉勾芡，淋上鸡油，浇于鱼翅上即成。', NULL, NULL, '料酒40克，精盐5克，葱50克，姜50克，湿淀粉20克，清汤1公斤，酱油少许鸡油15克。', '主料：水发群翅1公斤，鹌鹑蛋12个。 \r\n配料：母鸡1只，猪肉750克，金华火腿250克，豆苗少许。');
INSERT INTO `recipe` VALUES (5568, 428, '芙蓉大虾', '1.将鲜大虾去头尾，剥去外壳，挑净沙线，用刀片开成两片(如虾大可片四片)用清水洗干净，放入碗中，加入少许精盐、料酒、鸡蛋清1个和玉米粉浆好。 \r\n\r\n2.将鸡茸放在碗中，加入料酒、精盐、玉米粉拌匀，再ke入3个鸡蛋清，拌成稀糊。 \r\n\r\n3.坐油锅，注入熟猪油，烧至五成热时下入浆虾片，滑熟后捞出，控净油，倒入鸡茸稀糊中拌匀。油锅继续坐火上，烧至五成热时下入裹糊虾片，滑熟后倒入漏勺中，控净油。(注意，切不可上色)。 \r\n\r\n4.锅中留底油少许，倒入虾片，加入料酒、精盐、清汤翻炒一下。用湿淀粉勾芡，淋上鸡油，出锅装盘，撒上火腿末和油菜末即可。', NULL, NULL, '料酒10克，精盐1.5克，湿玉米粉20克，鸡油10克，熟猪油500克(约耗20克)清汤200克，火腿末、油菜末少许。', '主料：鲜大虾400克。 \r\n配料：鸡茸50克，鸡蛋清4个。');
INSERT INTO `recipe` VALUES (5569, 428, '宫保兔肉', '1.用水将兔肉洗净，切成边长4分的方丁，放入碗内，加入料酒、精盐、葱姜末、湿玉米粉各少许以及1个鸡蛋清，拌匀浆好。 \r\n\r\n2.将花生米放入碗中，注入开水浸泡5分钟，捞出剥去皮。坐煸锅，注入100克花生油，下入花生米，用温油炸熟呈黄色捞出。 \r\n\r\n3.坐煸锅，注入花生油，烧至五成热，下入兔肉丁滑熟，倒入漏勺中。 \r\n\r\n4.锅中留底油，下入豆瓣酱、料酒、精盐、白糖和葱姜末煸炒至汁浓时，倒入兔肉丁和花生米，翻炒均匀，即可出锅。', NULL, NULL, '料酒10克，精盐1克，豆瓣辣酱15克，白糖5克，葱、姜末各少许，鸡蛋清1个湿玉米粉15克，花生油500克(约耗25克)。', '主料：净兔肉150克。 \r\n配料：花生米50克。');
INSERT INTO `recipe` VALUES (5570, 428, '桂花干贝', '1.将干贝除去筋，用水洗净，放入盆中，注入温水浸泡1小时，然后上屉蒸3小时取出，滗净汤，用手将干贝撕碎。 \r\n\r\n2.将鸡蛋ke入碗中并打散，再加入料酒、精盐和干贝，搅拌均匀。 \r\n\r\n3.将15克花生油注入油锅，上火烧至六成热时放入葱姜末炝锅，随即倒入鸡蛋和干贝煸炒，并用铁筷子将鸡蛋、干贝划散，边炒边注入花生油，以免粘锅。待炒至鸡蛋熟时烹入少许料酒，出锅后倒入盘中，散熟火腿末、黄瓜皮末即可。', NULL, NULL, '料酒5克，精盐1克，葱姜末各10克，花生油25克。', '主料：干贝100克。 \r\n配料：鸡蛋2个，熟火腿末、黄瓜皮末各少许。');
INSERT INTO `recipe` VALUES (5571, 428, '虎皮兔肉', '1.将清水注入锅中，放入洗净的兔肉，在火上煮20分钟左右捞出。将葱、姜切成段。用水将水发香节奏、水发玉兰片洗净。 \r\n\r\n2.将清汤注入锅中，加入料酒、精盐各少许和葱姜段、酱油，放入兔肉，在火上烧至七成烂时捞出。 \r\n\r\n3.将熟兔肉、水发香菇、水发玉兰片均切成筷子头大小的小丁。坐煸锅，注入少许花生油，下入兔肉丁、香菇丁、玉兰片丁和料酒、精盐，煸炒1至2分钟，倒入漏勺中控净汤汁，放入盘中，分成四份馅。 \r\n\r\n4.将三个鸡蛋ke入碗中，加15克玉米粉、少许精盐，搅拌均匀。坐油锅，烧热，用油刷子将锅内刷一层油，然后将鸡蛋液倒在锅内，吊成两张圆形薄鸡蛋皮将面粉倒入碗中，用清水调成稀糊。 \r\n\r\n5.将鸡蛋皮改刀成四个边长4寸的正方形片。将一份馅放在一张蛋皮中间，四周抹上面粉糊，并从四面包起成长3寸5分、宽1寸5分的长方形条。按以上方法共包四个。 \r\n\r\n6.将一个鸡蛋ke入碗中，加入玉米粉、炒酒、精盐、拌成糊。 \r\n\r\n7.坐煸锅，注入花生油，烧至六成热，将兔肉沾满鸡蛋湖，入油锅中炸至金黄色时捞出控净油，码在盘中即成。', NULL, NULL, '料酒15克，精盐1.5克，玉米粉30克，面粉少许，葱25克，姜25克，花生油500克(约耗60克)，清汤350克，酱油5克。', '主料：净兔肉200克。 \r\n配料：水发香菇25克，水发玉兰片25克，鸡蛋4个。');
INSERT INTO `recipe` VALUES (5572, 428, '鸡沾口蘑', '1.将口蘑放碗中，注入凉水浸泡1小时左右，洗净泥沙。锅中注入150克清汤加入少许料酒、少许精盐，放入口蘑，在火上煨2分钟捞出，控净水。将口蘑顶朝上放盘中，顶面撒玉米粉。 \r\n\r\n2.将鸡茸放碗中，加料酒、精盐、玉米粉、鸡油各少许，搅拌上劲。将鸡蛋清倒入盘中，用筷子抽起雪白泡沫状后，倒入鸡茸搅成糊。 \r\n\r\n3.用手将鸡茸糊挤成同口蘑一样的小丸子，放在口蘑顶上，在鸡茸糊上撒些火腿末、清菜末，上屉蒸熟后取出，另换盘将蒸好的鸡沾口蘑码在盘中。 \r\n\r\n4.将200克清汤注入汤锅中，上火烧开，撇去浮沫，加入料酒、精盐，用水将玉米粉调稀勾芡，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒10克，精盐1.5克，湿玉米粉10克，面粉少许，清汤350克，鸡油5克。', '主料：口蘑50克，鸡茸50克。 \r\n配料：熟火腿末、油菜末各少许，鸡蛋清2个。');
INSERT INTO `recipe` VALUES (5573, 428, '金糕', '1.用清水将红果洗净，放入铜锅中，注入清水，上火煮10分钟后，捞出红果擦过细罗，去掉皮和籽，和桂花酱一起放入盆中备用。 \r\n\r\n2.将150克清水注入铜锅中，加入白糖，上火烧开，熬15分钟即可。同时将白矾放入另一铜锅中，注入50克清水，上火烧开至白矾溶化。 \r\n\r\n3.将熬好的糖水倒入盛有红果的盆中，趁热急速朝一个方向搅拌均匀，再倒入化开的白矾水，再搅拌均匀。然后倒入一个7分高的长方形铁模中摊平，盖上一张薄油纸。晾凉后切成5分见方的块，码在盘中即成。 \r\n', NULL, NULL, NULL, '红果500克，白糖350克，桂花酱5克，白矾7.5克。');
INSERT INTO `recipe` VALUES (5574, 428, '金钱吐丝', '1.将鲜虾去头尾及外壳，挑去沙线，用水洗净；将马蹄拍碎，用刀剁成末；将鲜虾肉及猪肥肉用刀背砸成茸；将面包切成直径1寸分、厚1分的圆形片，其余剁成面包粉。 \r\n\r\n2.将虾肉茸和猪肥肉茸放入碗中，加入精盐、料酒、玉米粉搅拌上劲，再放入马蹄末、鸡蛋清搅拌成糊，用手挤成直径1寸的丸子，放在面包片上，四周用小刀抹齐。将切好的发菜旋转式地码在虾丸子上。将面包粉过细罗，然后将细面包末撒在虾托上面，用手压实。 \r\n\r\n3.坐煸锅，注入1公斤花生油，烧至六成热时下入虾托，炸至金黄色时捞出，控净油，放在盘中即成，连同花椒盐一起上桌。', NULL, NULL, '料酒10克，精盐1.5克，干玉米粉15克，花生油1公斤(约耗40克)，花椒盐2克。', '主料：鲜虾350克。 \r\n配料：面包100克，面包少许，水发菜50克，猪肥肉50克，马蹄50克，鸡黄清1个。');
INSERT INTO `recipe` VALUES (5575, 428, '金丝烧麦', '1.用细罗将150克面粉筛过，浇入开水，拌匀搓透，再揪成45个圆剂，压遍，上下两面均铺上厚厚的面粉，用烧麦槌擀成裙折状的薄皮，弹去面粉。 \r\n\r\n2.将烧麦馅分成45份，放入擀好的薄皮上，用手将皮捏拢，将皮的裙折依次压好，然后将其开口向上，码在屉上，裙折略下垂，即成烧麦。再将蛋皮切成细丝，放在烧麦开口处的肉馅上，蒸熟即可。', NULL, NULL, NULL, '面粉150克，开水100克，烧麦馅300克，鸡蛋皮一张。');
INSERT INTO `recipe` VALUES (5576, 428, '金鱼鸭掌', '1.锅中注入清水，放入鸭掌，上火煮15分钟左右，五成熟时捞出，放入清水中过凉，取出，从鸭掌背面用手剔去骨头，然后用剪刀剪去掌心硬茧。然后，鸭掌心朝上，放在平盘中，在掌跟部撒些面粉。 \r\n\r\n2.锅中注入清水，分别放入水发香菇、水发玉兰片、水发鱼肚，在火上氽一遍捞出，控净水，切成丝。将鸡蛋清倒入盘中，用筷子抽起，呈雪白泡沫状。将黄瓜皮切成4分长的细丝。 \r\n\r\n3.将鸡茸放入盆中，加入料酒、精盐、玉米粉、鸡油、清汤各少许，搅拌上劲，再放入鸡蛋清，拌匀成糊。用手将糊挤成长1寸左右的金鱼形，放在鸭掌跟上，把两粒豌豆安在鱼头两侧作眼睛，鱼背中央撒上一条发菜。发菜两侧用黄瓜皮丝码成鱼鳞状，即成金鱼鸭掌。按此方法共做12个，上屉蒸6至8分钟取出。 \r\n\r\n4.将200克清汤注入锅中，加入料酒、精盐各少许，放入香菇丝、玉兰片丝、鱼肚丝，在微火上烧两分钟，然后倒入漏勺中控干水，撒在小鱼盘内，将金鱼鸭掌码在上面。 \r\n\r\n5.将200克清汤注入锅中，上火烧开，撇去浮沫，加入料酒、精盐各少许，对好口味，用水将玉米粉调稀，倒入锅中勾芡，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒15克，精盐2克，湿玉米粉20克，面粉5克，鸡蛋清两个，清汤400克，鸡油10克。', '主料：鸭掌12个。 \r\n配料：鸡茸100克，水发香菇30克，水发玉兰片15克，水发鱼肚30克，鲜豌豆24粒，发菜少许，黄瓜皮25克。');
INSERT INTO `recipe` VALUES (5577, 428, '咖喱菜花', '1.用小刀削去菜花根上的皮，改刀成小块，用清水洗干净。 \r\n\r\n2.锅中注入清水，上火烧开，放入洗净的菜花，氽熟捞出，放入盆中，用清水冲凉，滗去水，放入咖喱粉拌匀，腌5分钟，再用清水冲去多余的咖喱粉，滗净水。 \r\n\r\n3.将咖喱菜花放小盆中，加入精盐、香油搅拌均匀。将菜花在圆盘中码成一朵花，上面码上用燕窝做成的“意”字。 \r\n\r\n说明：以上麻辣牛肉、炝玉龙片、油焖鲜蘑、咖喱菜花四个菜中的四个字恰好组成“吉祥如意”。', NULL, NULL, '精盐1克，咖喱粉5克，香油10克。', '菜花500克。 \r\n');
INSERT INTO `recipe` VALUES (5578, 428, '栗子糕', '1.用刀将生栗子表面剞上十字，放入铜锅中，注入清水，上火煮透(约煮十分钟)，捞出。待晾温后，剥去内外两层皮，用清水冲洗干净。 \r\n\r\n2.将清水注入铜锅，放入去皮栗子，上火煮30分钟左右，捞出控净水。 \r\n\r\n3.将煮透的栗子擦过细罗，然后将栗子搓成泥状，晾凉后放在干净的布中，加入50克白糖、桂花酱隔着布搓成栗子面放在案上，用刀压抹成四分厚的长方形片，在表面撒上一层白糖压平将四边切齐，再切成四分见方的块码在盘中即成。', NULL, NULL, NULL, '生栗子250克，白糖150克，桂花酱5克。');
INSERT INTO `recipe` VALUES (5579, 428, '莲子膳粥', '1.将莲子加入清水、食碱，刷去外皮，捅出莲心，用温水洗净。用清水将小站米洗净。 \r\n\r\n2.将2.5公斤清水注入铜锅中，上火烧开，放入小站米、莲子，再烧开，然后用微火熬30分钟左右，熬至米烂汁稠即成。', NULL, NULL, NULL, '小站米150克，莲子150克，食碱1.5克。');
INSERT INTO `recipe` VALUES (5580, 428, '琉璃珠玑', '1.将一块猪肉皮面朝下平铺在菜墩上，把桂鱼肉洗净后放在猪肉皮上，用刀背砸成茸。如鱼肉发粘，可加少量清水，以使鱼肉不沾刀。待鱼肉砸成茸后挑出筋和刺。 \r\n\r\n2.将猪肉末、虾茸分别放在两个碗中，分别加入料酒、精盐各少许，搅拌上劲，分别用手挤成直径约4分的小丸子，分放两个碗中。将猪肉汤注入碗中，放入冰箱内约4小时，使其结冻，取出，切成边长约4分的小方丁，再用刀削圆，放在碗中。 \r\n\r\n3.用水将菠菜叶洗净，用刀剁成末，放入纱布中包起，挤出绿汁，倒入锅中上火烧开后撇去浮沫，控净水，即成绿泥。用水将姜洗净，切成末，放入小碗中，注入少许开水，浸泡10分钟。 \r\n\r\n4.将鱼茸放入大海碗中，加清水xie开，放入姜汁3克，搅拌上劲，再加入鸡蛋清和鸡油拌匀。然后将其分成三份，分放在三个碗中。其中的一份鱼泥糊加入绿泥拌成绿鱼茸糊；另一份加入红曲粉拌成红鱼茸糊；剩下一份呈白色。 \r\n\r\n5.汤锅中注入凉水。用绿鱼茸糊包起猪肉末馅心，挤成直径约6分的丸子，放入清水锅中；用红鱼茸糊包起虾茸馅心，挤成直径约6分的丸子，放入清水锅中；用白色鱼茸糊包起猪肉汤冻馅心，也放入清水锅中。然后将锅上火，烧开后略煮一会儿，捞出三种鱼丸，轻轻放入海碗中。 \r\n\r\n6.锅中注入清汤，上火烧开，撇去浮沫，加入料酒、精盐，对好口味，浇入海碗中即成。', NULL, NULL, '料酒15克，精盐4克，鸡蛋清1个，红曲粉0.5克，姜10克，清汤750克，鸡油5克。', '主料：将桂鱼肉250克。 \r\n\r\n配料：猪肉末50克，虾茸50克，浓猪肉汤50克，菠菜叶100克。');
INSERT INTO `recipe` VALUES (5581, 428, '龙凤柔情', '1.将鱼肉剔去皮，将鸡脯肉剔去皮和筋。将以上两种原料均切成长1寸5分的细丝，分别放入两个碗中，分别加入料酒、精盐各少许拌匀，再分别放入1个鸡蛋清、5克玉米粉，把鱼肉丝和鸡肉丝浆好。将豆苗切去根，洗净，切成寸段。 \r\n\r\n2.坐煸锅，注入花生油，烧至五成热时，将鱼肉丝和鸡肉丝分别下入锅中滑熟，分别倒入两个漏勺中，控净油。 \r\n\r\n3.煸锅中留底油，放入滑熟的鱼肉丝和料酒、精盐各少许，注入75克清汤，略加煸炒，再用湿淀粉勾芡，然后倒入圆盘的一边，呈半圆形。 \r\n\r\n4.煸锅中留底油，放入滑熟的鸡肉丝和料酒、精盐、酱油各少许，注入75克清汤，略加煸炒，加入用水调稀的玉米粉，勾芡，然后倒入圆盘的另一边，呈半圆形。 \r\n\r\n5.锅中留底油，放入豆苗煸炒，同时加入料酒、精盐各少许，翻炒均匀，倒在鱼肉丝和鸡肉丝的中间即成。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清2个，湿淀粉20克，清汤150克，酱油少许，花生油500克(约耗40克)，鸡油10克。', '主料：桂鱼肉150克，鸡脯肉150克。 \r\n\r\n配料：豆苗150克。');
INSERT INTO `recipe` VALUES (5582, 428, '龙井竹荪', '1.将竹荪放入盆中，注入温水浸泡30分钟，用清水冲洗数遍。如竹荪颜色过黑，可加入少许精盐揉搓几下，用清水冲洗干净。 \r\n\r\n2.将约2寸长的上半部竹荪(其余不用)放在平盘中，在茎部根上撒些干玉米粉。 \r\n\r\n3.将鸡蛋清倒入平盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放入碗中，加入炒酒、精盐、熟猪油各少许，搅拌上劲后再放入鸡蛋清和玉米粉，搅拌成糊。 \r\n\r\n4.将鱼茸糊挤成长约8分的龙井鱼身子，放在竹荪的撒有玉米粉的部位，用两粒豌豆安在鱼头两侧做眼睛，背上撒些发菜、火腿末、油菜末，即成龙井竹荪。按以上方法做16个，上屉蒸熟，取出，放在海碗内。 \r\n\r\n5.另坐锅，注入清汤，烧开后撇去浮沫，加入料酒、精盐，浇在海碗内即成。', NULL, NULL, '料酒10克，精盐2克，干玉米粉10克，清汤1250克，熟猪油5克。', '主料：干竹荪16个。 \r\n配料：鱼茸100克，豌豆32粒，水发发菜少许，熟火腿末、油菜末各少许，豆苗少许，鸡蛋清2个。');
INSERT INTO `recipe` VALUES (5583, 428, '麻辣牛肉', '\r\n1.用刀将净牛肉切成长1寸5分、宽1寸、厚2分的片。将葱、姜切成段。使牛肉片放在盆中，用葱姜、绍酒腌1小时。 \r\n\r\n2.将花生油注入油锅，上火烧至七成热时下入牛肉片，炸熟，捞出，控净油。 \r\n\r\n3.将香油注入锅中，上火烧至七成热，下入干辣椒炸焦，再放入葱段、姜段桂皮、花椒略炸一下，随即注入清汤，加入料酒、精盐、白糖、酱油，再倒入炸好的牛肉片，在微火上将汁kao浓。待汁快kao完时，倒入盆中晾凉，将牛肉挑出码在圆盘内，上面码上用燕窝做成的“吉”字即成。', NULL, NULL, '料酒5克，精盐0.5克，酱油10克，花椒5克，干辣椒5克，桂皮1克，香油20克，白糖5克，葱25克，姜25克，清汤250克，花生油500克(约耗20克)。', '净牛肉250克。');
INSERT INTO `recipe` VALUES (5584, 428, '炝玉龙片', '1.用刀将净草鱼切成长宽各1寸、厚1分的片。姜用水洗干净，切成细丝。 \r\n\r\n2.锅中注入清水，上火烧开，放入切好的鱼片，氽熟捞出，放入盆中，加入精盐、料酒、香油和姜丝，搅拌均匀晾凉。 \r\n\r\n3.将拌匀的炝鱼片整齐地码入圆盘中，上面码上用燕窝做成的“祥”字即可。', NULL, NULL, '精盐1克，料酒10克，香油10克，姜少许。', '净草鱼肉250克。');
INSERT INTO `recipe` VALUES (5585, 428, '肉末烧饼', '1.将发面对好碱，加入白糖揉匀，揪成15个剂，用手掌将面剂压成圆片。另揪一个小面球蘸上一点香油，放在圆片中心，把小面球包起来，揪去捏拢后收口处的面头，再用手把圆球按成直径1寸5分的扁圆饼。按此方法做出15个。 \r\n2.将扁圆饼正面沾上糖水，再沾上芝麻，芝麻面朝上，放进烤盘里按平，入烤炉烤熟即可。 \r\n\r\n------------------------------\r\n附：炒肉末的制作方法 \r\n\r\n原料：肥瘦猪肉末500克，水发玉兰片50克，料酒15克，精盐1.5克，酱油20克，葱姜末5克，香油10克，糖10克。 \r\n做法：将玉兰片洗净，剁成末。坐煸锅，注入50克清水，放入肉末进行煸炒，待肉末变成白色时，用漏勺将汤滗净，加入玉兰片末和料酒、精盐、糖、酱油，待味吃进肉末后，再加入葱姜末、香油，炒均匀即可出锅。', NULL, NULL, NULL, '发面500克，白糖50克，芝麻25克，香油50克。');
INSERT INTO `recipe` VALUES (5586, 428, '三鲜瑶柱', '1.将干贝除去筋，用水洗干净，放入盆中，注入温水浸泡1小时，然后上屉蒸3小时取出，滗净汤。 \r\n\r\n2.将水发玉兰片、水发香菇、熟火腿和黄瓜皮均切成小菱形薄片，用开水氽透。 \r\n\r\n3.坐油锅，注入花生油，放入葱姜末炝锅，随即放入干贝，加入料酒、精盐酱油和清汤，在微火上浇2至3分钟，然后将湿淀粉勾成芡汁，淋上鸡油，出锅装盘，撒上玉兰片、香菇片、火腿片和黄瓜皮片即可。', NULL, NULL, '料酒10克，精盐1克，酱油5克，清汤150克，湿淀粉10克，鸡油5克，葱姜末各10克，花生油10克。', '主料：干贝100克。 \r\n配料：水发香菇25克，熟火腿25克，水发玉兰片25克，黄瓜皮25克。');
INSERT INTO `recipe` VALUES (5587, 428, '酥卷佛手', '1.用细罗将75克面粉筛过，加入35克熟猪油搓匀擦透，即成油酥面。用细罗将75克面粉筛过，加入15克熟猪油、50克清水，搓至纯滑有筋，即成水油皮面。 \r\n\r\n2.将油酥洒和水油皮面各分成15份，用水油皮面包上油酥面，再用擀面棍开长，用手从长的一端卷起成筒状，轻轻压遍，卷成圆球，再压遍，然后擀成圆皮15个。 \r\n\r\n3.将豆沙馅分成15份，放入圆面皮中包起，制成长圆形，用刀背顺长将其一半压遍，在压遍的部位上竖着每隔2分宽切一刀，共切四刀，分成顶部相连的五小条。用手将中间的三条向下折过一半，形如三指拳起；边上两条形如伸出的手指。 \r\n\r\n4.将金糕切成小颗粒，在每个佛手卷上放一粒(放在没压遍的部位的中央)，然后装进烤盘，入烤箱烤熟即可。', NULL, NULL, NULL, '面粉150克，熟猪油50克，豆沙馅130克，金糕25克。');
INSERT INTO `recipe` VALUES (5588, 428, '糖醋鱼卷', '1.将桂鱼刮去鳞，开膛掏去肠肚和鱼鳃，用水洗净，剁下头、尾，剔出骨刺去皮，用刀片成1寸5分长、1寸宽、1分厚的长方形片，共片20片，放入碗中，加入料酒、精盐各少许，腌10分钟。 \r\n\r\n2.用水将黄瓜洗净，切成1寸5分长的段，再将黄瓜片皮去心，切成直径2分的条，共切20根，放在碗中，加入精盐少许，腌5分钟。 \r\n\r\n3.将腌好的鱼片平放在菜墩上。每片鱼片中间放上一根黄瓜条，再将皇片卷起，共做20个。将鸡蛋清放入碗中，加入90克湿玉米粉拌匀成鸡蛋清糊。 \r\n\r\n4.坐煸锅，注入花生油，烧至七成热时，将鱼卷和鱼头、鱼尾裹满鸡蛋清糊分别下入油锅中炸熟，捞出。待油烧至八成热时，再下入鱼卷、鱼头、鱼尾冲炸一下，至金黄时捞出，控净油，将鱼头、鱼尾分别码在鱼盘两侧，中间码上鱼卷，成整鱼形。 \r\n\r\n5.煸锅中留少许底油，放入葱姜末略炸一下，随即注入清汤，加入料酒、精盐、白糖、蕃茄酱和醋，上火烧开后，用水调稀玉米粉，倒入锅中勾芡，淋上热花生油，浇在鱼身、鱼头和鱼尾上即成。', NULL, NULL, '料酒20克，精盐1.5克，白糖100克，醋70克，蕃茄酱10克，葱、姜末各10克湿玉米粉10克，鸡蛋清2个，花生油1公斤(约耗50克)，清汤250克。', '主料：桂鱼一尾(约1.5公斤)。 \r\n配料：黄瓜1条(约150克)。');
INSERT INTO `recipe` VALUES (5589, 428, '桃仁鸡丁', '1.将鸡肺肉剞上花刀，切成3分大小的方丁，放入碗中，加入料酒、精盐、湿玉米粉各少许和鸡蛋清，搅匀浆好。 \r\n\r\n2.坐煸锅，注入150克花生油，烧至五成热时放入桃仁，用温油炸至淡黄色时倒入漏勺，控净油。将水发香菇、水发玉兰片、熟火腿均切成边长5分的菱形薄片。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，下入鸡丁，滑至八成熟，下入玉兰片香菇片、火腿片，滑透，倒入漏勺控净油。 \r\n\r\n4.锅中留少许底油，放入葱姜末炝锅，随即倒入鸡丁、玉兰片、香菇片、火腿片，加入料酒、精盐、清汤翻炒一下。用水将玉米粉调稀，倒入锅中勾芡再倒入桃仁，翻炒均匀，淋上鸡油即成。', NULL, NULL, '料酒10克，精盐1.5克，鸡蛋清1个，清汤100克，湿玉米粉15克，鸡油5克，花生油500克(约耗40克)，葱姜末各少许。', '主料：鸡脯肉200克。 \r\n配料：桃仁50克，水发香菇、水发玉兰片、熟火腿各20克。');
INSERT INTO `recipe` VALUES (5590, 428, '网油鱼卷', '1.用刀将鱼肉片成长2寸、宽1寸、厚1分的长方形片，放入碗中，用料酒、精盐各少许，腌2至3分钟。 \r\n\r\n2.将火腿、玉兰片、香菇均切成长2寸的细丝，放入开水锅中氽一遍捞出。将鱼片横摆，放入三种丝卷起。 \r\n\r\n3.用水将网油洗净，控干水，平摊在菜墩上，用刀背将厚的地方砸几下，然后改刀成长2寸5分、宽1寸5分的长方形片，然后将鱼卷放入，四边包起，卷成卷。 \r\n\r\n4.将鸡蛋在碗中，打散，放入料酒、精盐、花生油各少许和湿玉米粉搅拌成糊。 \r\n\r\n5.坐煸锅，注入花生油，烧至六成热时，将网油鱼卷裹满鸡蛋糊放入油锅中炸至金黄时捞出即成。', NULL, NULL, '料酒10克，精盐1.5克，玉米粉20克，鸡蛋2个，面粉10克，花生油500克(约耗40克)。', '主料：鱼肉150克，猪肉油250克。 \r\n配料：熟火腿100克，水发香菇50克，水发玉兰片50克。');
INSERT INTO `recipe` VALUES (5591, 428, '香桃鸽蛋', '1.将清水注入锅中，放入鸽蛋，上火煮熟后捞出，剥去外皮，洗净，用刀切去鸽蛋大头的一小部分，以能放平为准。 \r\n\r\n2.将面包切成长1寸5分、宽1寸、厚1分的长方形片。将核桃仁放碗中，注入开水，浸泡5分钟，捞出剥皮。 \r\n\r\n3.将鸡茸放碗中，加入料酒、精盐、鸡蛋清、玉米粉和鸡油，搅匀成糊。用小刀将鸡茸糊挂在面包片上，鸽蛋放在鸡茸糊中间，两边摆上两个核桃仁。用手将鸽蛋、核桃仁按实，再用小刀将挤出的鸡茸糊抹齐，四周点缀些熟火腿末和油菜末，即成香桃鸽蛋。 \r\n\r\n4.坐煸锅，注入花生油，烧至六成热时，下入香桃鸽蛋，用温油炸成金黄时捞出，码在盘中即成。', NULL, NULL, '料酒10克，精盐1.5克，玉米粉10克，鸡蛋清1个，花生油500克(约耗30克)，鸡油少许。', '主料：鸽蛋12个。 \r\n配料：面包150克，鸡茸50克，核桃仁50克，熟火腿末、油菜末各少许。');
INSERT INTO `recipe` VALUES (5592, 428, '熊猫品竹', '1.将面粉分成四份。一份中加入30克黄油搓匀即成酥面；另一份中加入10克黄油、白糖、25克清水，揉成皮面。 \r\n\r\n2.将皮面和酥面各揪成12个剂，用皮面包上酥面，用擀面棍擀成长条，卷成卷，用手掌按扁，再叠成三折，擀成圆皮，包入豆沙馅，用手捏成熊猫形状。按此方法做出12个熊猫。 \r\n\r\n3.用毛笔将熊猫生胚的耳朵、眼睛和腰围分别刷上鸡蛋清、粘上黑芝麻，放入烤炉烤12分钟，取出，放盘中，再放几枝用烫面捏成的竹子即可。', NULL, NULL, NULL, '面粉100克，黄油40克，白糖10克，豆沙馅100克，黑芝麻少许，鸡蛋清1个。');
INSERT INTO `recipe` VALUES (5593, 428, '鸭丝掐菜', '1.将鸭脯肉切成1寸半长、1分粗的细丝。将掐菜洗净，漂透。 \r\n\r\n2.将炒锅上火烧热，打底油，下鸭丝、掐菜迅速烹炒，并烹醋，炒七成热时倒出控净水，然后回锅再烹炒，加入精盐，出锅时洒点绍酒即可。', NULL, NULL, '精盐、绍酒、醋各少许，油少许。', '主料：熟鸭脯肉200克。 \r\n配料：掐菜150克。');
INSERT INTO `recipe` VALUES (5594, 428, '燕窝四字菜', '1.用刀将洗净的菠菜叶剁成末，挤出绿汁于锅中，上火烧开，撇去浮沫，备用。 \r\n\r\n2.用刀片去鸡脯肉的皮和筋，砸成鸡茸，放入碗中，加料酒、精盐、玉米粉面粉、鸡蛋清和1克鸡油，搅拌成糊，然后分成两份分别放置，一份加入菜汁一份加入生虾油，并分别搅拌均匀。 \r\n\r\n3.将干燕菜放入大碗中，注入温水浸泡30钟，捞出后用镊子择去燕毛和变质部分，用清水冲洗数遍后将燕菜撕成丝，放入碗中，注入500克开水，加入食碱搅匀，浸泡15分钟，滗去碱水，再注入开水冲洗三遍，倒入漏勺控干水。 \r\n\r\n4.在四个7寸盘中分别抹上一层鸡油，用绿鸡茸糊在每个盘子中制成直径约4寸的圆形薄片，上面撒满一层发好的燕菜。 \r\n\r\n5.将粉红色鸡茸糊和剩余的绿鸡茸糊分别放入油纸中卷起。用粉红色鸡茸糊在燕菜上挤出“吉祥如意”四个字，再用绿色鸡茸糊在字的下方挤成花边，然后上屉蒸3至5分钟取出，用小刀将燕窝四字菜起出圆盘，待凉后放在冷菜上即成。 \r\n\r\n说明： \r\n\r\n燕窝四字菜是明清皇宫内举行大典，四季佳节，皇帝、皇后生日时所设筵宴中的一道著名冷菜。由于选料不同，具体做法也不一样，每个菜中都有燕窝表示封建帝王的显赫与高贵。所拼写的字，常有“庆贺新年”“万寿无疆”“蟾宫折桂”、“福如东海”、“吉祥如意”等祝福庆贺语汇。', NULL, NULL, '料酒15克，精盐1克，鸡蛋清6个，玉米粉15克，面粉5克，鸡油2克，食碱2.5克。', '主料：干燕20克。 \r\n配料：鸡脯肉200克，菠菜叶200克，生虾油25克。');
INSERT INTO `recipe` VALUES (5595, 428, '油焖鲜蘑', '1.将鲜蘑放入盆中，用清水冲洗数遍。 \r\n\r\n2.汤锅上火，注入香油烧热，放入葱姜煽炒一下，随即放入料酒、精盐、清汤，在火上煨15分钟左右，倒入碗中，晾凉后将鲜蘑挑出，整齐地码在圆盘中，构成圆的造型，上面放上用燕窝做成的“如”字。', NULL, NULL, '料酒4克，精盐0.5克，清汤150克，香油5克，葱5克，姜5克。', '鲜蘑100克。');
INSERT INTO `recipe` VALUES (5596, 428, '御扇豆黄', '1.将杏仁去皮，磨成浆，放入盆中。 \r\n\r\n2.将冻粉加清水200克上火化开，加入白糖再化开，晾温后倒入杏仁浆中搅匀，再倒入一尺二寸大盘中晾凉。 \r\n\r\n3.将晾好的杏仁冻按扇面形状用刀从中间取出七条(条与条之间留一点间隔)将豌豆黄切成与杏仁条同样大小的条，放进盘中，扇形的底端刻一块圆形豌豆黄。再用豌豆黄切成7条长丝当扇骨。将金糕切成丝当穗。再将盘子两边点缀一下即成。', NULL, NULL, NULL, '杏仁200克，冻粉15克，白糖300克，豌豆黄200克，金糕75克。');
INSERT INTO `recipe` VALUES (5597, 428, '炸鸡葫芦', '1.用清水将鸡大腿洗净。将葱切成寸段。用刀将姜拍碎。 \r\n\r\n2.锅中注入少许花生油，上火烧热，放入葱段、碎姜、大料、桂皮略炸一下然后注入400克清汤，放入鸡大腿，加入料酒、精盐、酱油，开锅后撇去浮沫，移至微火上炖40分钟左右，至八成烂时捞出，控净油。 \r\n\r\n3.坐煸锅，注入花生油，烧至八成熟，用手轻轻拿起鸡大腿，沾满一层玉米粉，下油锅，用热油炸至枣红色时捞出，控净油，摆在盘中，带花椒盐上桌即可。', NULL, NULL, '料酒15克，精盐1.5克，酱油5克，葱50克，姜25克，玉米粉15克，大料、桂皮各少许，花生油500克(约耗40克)，清汤400克，花椒盐1.5克。', '鸡腿12个。');
INSERT INTO `recipe` VALUES (5598, 428, '芝麻巻', '1.用清水将芝麻洗净，控净水，放入锅中，用文火炒成金黄色，用走槌将芝麻碾碎，加入白糖搅拌均匀。用25克温水将桂花酱解开，倒入拌糖的芝麻中，即成芝麻糖馅。 \r\n\r\n2.将芸豆用小磨磨碎，放在盆中，用开水泡一小时，再用温水把豆皮淘掉，放入开水锅里煮，加入碱面，煮一小时左右，其火候应以略一用劲，即能搓成粉状为度，然后捞入细布中包好，上屉蒸20分钟，取出过罗，即成芸豆卷面。 \r\n\r\n3.将芸豆面晾凉后，倒在洗净的湿布上，隔着布揉搓透。取2尺长、1尺宽的湿布(拧干)一块，平铺在案板边上，再将揉好的芸豆面切成1寸粗细的条，放在湿布中间，用手掌按扁，用刀反复地抹，将芸豆抹成1分厚、5寸长、2寸宽的长方形薄片。然后撒上一层芝麻糖馅，用手按实。用白布从两边分别向中间卷起，再折成圆柱形，然后用双手隔着布捏实捋直。 \r\n\r\n4.将布打开，用刀切成6分长的段，断面朝上码在盘中即成。', NULL, NULL, NULL, '白芸豆200克，芝麻50克，白糖100克，桂花酱25克，碱面3克。 \r\n');
INSERT INTO `recipe` VALUES (5599, 428, '抓炒鱼片', '1.将鱼肉剔净皮、刺，用刀切成长1寸5分、宽8分、厚2分的长方形片，放入碗中，加入料酒、精盐各少许，玉米粉60克，把鱼片拌匀挂糊。 \r\n\r\n2.坐煸锅，注入花生油，烧至七成热时，将鱼片逐次下入锅中，炸至金黄色捞出。 \r\n\r\n3.锅中留底油，放入葱姜略炸一下，随即加入料酒、精盐、酱油、白糖、醋清汤，上火烧开。用水调稀玉米粉，倒入锅中勾汁成糊状时，将炸好的鱼片倒入，翻炒均匀，淋上少许热花生油即成。', NULL, NULL, '料酒10克，精盐1.5克，白糖30克，醋15克，酱油10克，葱姜末各少许，湿玉米粉75克，花生油500克(约耗50克)，清汤100克。', '鱼肉200克。');
INSERT INTO `recipe` VALUES (5600, 429, '炒黄瓜酱', '1.用清水将黄瓜洗净，顺长切成4条，片去黄瓜子，切成3分见方的丁，放入碗中，加入少许精盐，拌匀，腌3分钟，滗去水。 \r\n\r\n2.用刀将猪肉片成3分厚的大片，剞上十字花刀，切成3分见方的丁。 \r\n\r\n3.坐煸锅，注入熟猪油，烧至六成热，放入肉丁煸炒；待肉丁内的水分炒出来时，锅内响声加大，随即放入葱姜末、黄酱继续煸炒；待黄酱裹匀肉丁并散发出酱香味时，加入料酒、精盐继续煸炒均匀，再加入黄瓜丁，淋上香油翻炒均匀，即可出锅。', NULL, NULL, '料酒10克，精盐1克，黄酱10克，葱姜末各少许，熟猪油15克，香油5克。', '主料：瘦猪肉150克。 \r\n配料：嫩黄瓜1条(约100克)。');
INSERT INTO `recipe` VALUES (5601, 429, '炒榛子酱', '1.用刀将瘦猪肉片成3分厚的大片，剞上十字花刀，切成3分见方的丁；马蹄也切成同样大小的丁。 \r\n\r\n2.盆中注入开水，放入生榛仁，加入食碱，用竹刷子将榛仁外皮刷净，用开水冲洗两遍捞出，晾干。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，放入生榛片，用温油炸至金黄色时捞出，控净油。 \r\n\r\n4.坐煸锅，注入熟猪油，烧至六成热，放入肉丁进行煸炒；待肉丁内的水分炒出来时，锅中响声加大，随即放入葱姜末、黄酱继续煸炒；待黄酱裹匀肉丁并散发出香味时，加入料酒、精盐煸炒均匀，放入马蹄和榛仁，淋上香油翻炒均匀即可出锅。', NULL, NULL, '料酒10克，精盐1克，黄酱10克，葱、姜末各少许，花生油250克(约耗15克)熟猪油10克，香油5克，食碱2克。', '主料：瘦猪肉150克。 \r\n配料：生榛仁100克，马蹄4个。');
INSERT INTO `recipe` VALUES (5602, 429, '翠玉豆糕', '将油菜汁洗净，加入少量水，用粉碎机搅烂，用布挤出菜汁，在炒豌豆溶液时加入菜汁炒好即可。 \r\n', NULL, NULL, NULL, '白豌豆溶液500克，白糖250克，冻粉10克，油菜叶750克。');
INSERT INTO `recipe` VALUES (5603, 429, '豆沙卷', '1.用小磨将白芸豆破碎去皮，放在盆中，注入开水浸泡12小时，把未能磨掉的豆皮泡开，用清水冲去豆皮。 \r\n\r\n2.将芸豆碎瓣放入铜锅，加入食碱，注入开水，上火煮1小时左右，捞出少许芸豆，用手搓一下，如能搓成粉，即可全部捞出，然后控干水，放入白布中包起，上笼蒸20分钟，取出过细罗，将芸豆瓣擦成泥，晾凉。 \r\n\r\n3.用清水将红小豆洗净，放入铜锅中，加入清水，上火煮1小时左右，待红小豆煮烂成稀粥状时，将锅离火，把红小豆带原汤过罗，放入铜锅中，加50克白糖，上火炒20分钟左右，成稠糊状时即可倒入碗中，晾凉成豆沙馅。 \r\n\r\n4.将芸豆泥倒在湿布上，隔着面揉和均匀。取一块2尺长、1尺宽的湿布，平铺案板边上，再将芸豆泥搓成1寸粗的条，放在湿布中间，用刀把芸豆条压成长1尺、宽2寸、厚1分的薄片，用刀反复抹平。用小刀在芸豆片的两个长边上抹上两条豆沙，在中间空白处撒上一条白糖，用手按实，用白布顺着芸豆片的长边将两边同时卷起，捏实，使豆卷略微粘住。 \r\n\r\n5.将卷豆卷的白布打开，用刀切齐两头，然后切成6分长的段，将断面朝上，码在盘中即可。', NULL, NULL, NULL, '白芸豆250克，红小豆100克，食碱1.5克，白糖100克。');
INSERT INTO `recipe` VALUES (5604, 429, '二龙戏珠', '1.把自制小香肠放入平盘，上屉蒸熟，取出晾凉，放在熟菜墩上，顶刀斜切成圆薄片。 \r\n\r\n2.锅中注入清水，上火烧开，下入洗净的掐菜氽一遍捞出，倒入清水中过凉然后捞出，滗净水，放入盆中，加入精盐、香油拌匀。 \r\n\r\n3.用水将黄瓜、胡萝卜、水萝卜洗净泥沙，放入消毒水中浸泡5分钟，再用清水冲洗两遍。将黄瓜顺长切成3条，用中间的1条，用小刀在上面刻出锯齿状的龙脊形，共刻两个。用小刀将胡萝卜刻成两个龙头、龙爪和龙尾。将干莲子削去根部，刮去外皮，用水洗净，镶在两个龙头上做眼睛。用刀将水萝卜削去皮，刻成一个绣球。 \r\n\r\n4.用炝好的掐菜在长鱼盘中码成两条相等飞腾的龙身形胎，在每条龙胎的两头各放上用胡萝卜刻成的龙头、龙尾。用香肠片由龙头部开始，交错地码至龙尾，呈龙鳞状。在龙身上面镶上刻好的黄瓜龙脊，然后再镶上龙爪。在两条龙中间放上绣球即可。', NULL, NULL, '精盐1克，香油10克。', '主料：自制小香肠500克。 \r\n配料：掐菜400克，粗胡萝卜两根，黄瓜3条，小水萝卜1个，干莲子4个。');
INSERT INTO `recipe` VALUES (5605, 429, '发菜黄花', '1.将发菜、黄花分别放入两个碗中，分别注入温水泡20分钟，捞出后分别放入清水中冲洗干净。将黄花根部、尖部切去不用。 \r\n\r\n2.坐煸锅，注入花生油，烧至七成热，下入发菜略炸一下捞出，控净油，放入热水盆中洗去油质。 \r\n\r\n3.锅中注入150克清汤，加入料酒、精盐各少许，分别放入发菜、黄花在微火上煨5分钟捞出。将黄花摆在圆盘中间成堆形；将发菜切成长5分的段，围在黄花四周。 \r\n\r\n4.汤锅中注入清汤150克，加入剩余的料酒、精盐，上火烧开，撇去浮沫；将湿淀粉倒入锅中勾芡，淋上鸡油，浇在菜上即可。', NULL, NULL, NULL, '主料：干发菜25克，黄花50克。 \r\n调料：料酒10克，精盐1.5克，清汤300克，湿淀粉10克，鸡油5克，花生油250克(约耗15克)。');
INSERT INTO `recipe` VALUES (5606, 429, '凤穿金衣', '1.将鸭肉切成长一寸半的粗丝；将葱姜切细丝；将香菜择净切段。将以上原料装入容器，调入精盐、绍酒、胡椒粉、香油和匀成鸭馅。 \r\n\r\n2.将150克鸡蛋去皮，调成蛋液，吊成10张直径三寸半的圆皮。 \r\n\r\n3.交需粉调糊，抹于蛋皮上，包鸭馅成圆柱形，共包10个鸭包。 \r\n\r\n4.将50克鸡蛋液调面粉、淀粉、香油成稠糊。将鸭包裹满稠糊后再滋上面包粉，用热油炸成金黄色即可上席。', NULL, NULL, '葱、姜、精盐、绍酒、胡椒粉、香菜、面粉、淀粉适量，香油少许，面包粉50克。', '主料：酱鸭肉250克。 \r\n配料：冬笋尖50克，水冬菇50克，鸡蛋200克。');
INSERT INTO `recipe` VALUES (5607, 429, '核桃酪', '将桃仁去皮。将江米用水泡发，分别加入清水，磨成浆，再加入白糖，上火熬20分钟即成。', NULL, NULL, NULL, '桃仁200克，白糖200克，水750克，江米100克。');
INSERT INTO `recipe` VALUES (5608, 429, '琥珀鸽蛋', '1.锅中注入清水，放入鸽蛋，上火煮熟捞出，剥去蛋皮，用净水洗净。 \r\n\r\n2.锅中注入开水，将水发香菇、水发玉兰片氽一遍捞出，同火腿肉均切成小菱形片。 \r\n\r\n3.坐煸锅，注入花生油，烧至七成热，将鸽沾上少许酱油，下入油锅，炸成金黄色时捞出。 \r\n\r\n4.锅中注入清汤，放入鸽蛋，加入香菇片、玉兰片、火腿片和料酒、精盐、酱油，在微火上烧3分钟。用湿淀粉勾成芡汁，淋上鸡油，即可出锅。', NULL, NULL, '料酒10克，精盐1克，酱油10克，清汤150克，湿淀粉10克，鸡油5克，花生油250克(约耗15克)。', '主料：鲜鸽蛋15个。 \r\n配料：水发香菇、水发玉兰片、熟火腿各25克。');
INSERT INTO `recipe` VALUES (5609, 429, '滑溜鹌鹑', '1.用清水将鹌鹑脯洗净，用刀片成薄片，放入碗中，加入鸡蛋清和料酒、精盐各少许，以及5克湿玉米粉，搅拌浆好。 \r\n\r\n2.用刀将水发香菇、水发玉兰片切成同鹌鹑片一样大小的片，下入开水锅中氽一遍捞出。 \r\n\r\n3.坐煸锅，注入花生油，烧至五成热，放入鹌鹑片滑熟，倒入漏勺控净油。煸锅中留下少许底油，下入香菇片、玉兰片，随即加入料酒、精盐、清汤，上火烧开，撇去浮沫，用水调稀玉米粉勾成汁芡，放入鹌鹑片，淋上鸡油，翻炒均匀即成。', NULL, NULL, '料酒15克，精盐1.5克，湿玉米粉10克，清汤100克，鸡蛋清1个，鸡油10克，熟猪油500(约耗20克)。', '鹌鹑脯6个。');
INSERT INTO `recipe` VALUES (5610, 429, '金糕卷', '1.用小磨将白芸豆破碎去皮，放在盆中，注入开水浸泡12小时，把未能磨掉的豆皮泡开，用清水冲去豆皮。 \r\n\r\n2.将芸豆碎瓣放入铜锅，加入食碱，注入开水，上火煮1小时左右，捞出少许芸豆，用手搓一下，如能搓成粉，即可全部捞出，然后控干水，放入白布中包起，上笼蒸20分钟，取出过细罗，将芸豆瓣擦成泥，晾凉。 \r\n\r\n3.用清水将红小豆洗净，放入铜锅中，加入清水，上火煮1小时左右，待红小豆煮烂成稀粥状时，将锅离火，把红小豆带原汤过罗，放入铜锅中，加50克白糖，上火炒20分钟左右，成稠糊状时即可倒入碗中，晾凉成豆沙馅。 \r\n\r\n4.将芸豆泥倒在湿布上，隔着面揉和均匀。取一块2尺长、1尺宽的湿布，平铺案板边上，再将芸豆泥搓成1寸粗的条，放在湿布中间，用刀把芸豆条压成长1尺、宽2寸、厚1分的薄片，用刀反复抹平。用小刀在芸豆片的两个长边上抹上两条金糕，在中间空白处撒上一条白糖，用手按实，用白布顺着芸豆片的长边将两边同时卷起，捏实，使豆卷略微粘住。 \r\n\r\n5.将卷豆卷的白布打开，用刀切齐两头，然后切成6分长的段，将断面朝上，码在盘中即可。 \r\n', NULL, NULL, NULL, '芸豆面(熟)250克，金糕100克。');
INSERT INTO `recipe` VALUES (5611, 429, '菊花里脊', '1.将猪里脊肉切成8分厚的大片，剞成十字花刀(深约5分多)，再改成5分见方的块，上浆。 \r\n\r\n2.将葱、姜、干辣椒切丝。 \r\n\r\n3.炒锅加油上火，将猪肉块滑熟，沥油。然后下葱姜、干辣椒煸炒出香味后再下入肉块，烹酒，下精盐、糖，炒匀，淋红油，出锅即可。', NULL, NULL, '干辣椒5克，葱、姜各5克，鸡蛋1个，精盐、酒、糖、水淀粉适量，红油25克。', '猪里脊肉250克。');
INSERT INTO `recipe` VALUES (5612, 429, '烤羊腿', '1.将带骨羊肉后腿放入容器，加入料酒、精盐、酱油、五香料(是砂仁、豆蔻山奈、白芷、肉桂碾末拌匀而成)、胡椒粉、桂皮、大料、葱段、姜段，腌制24小时。 \r\n\r\n2.将腌好的羊腿放烤盘上入炉烤熟，出炉，盛盘上桌，带花椒盐。', NULL, NULL, '料酒75克，精盐5克，酱油25克，五香料10克，胡椒粉1.5克，葱段100克，姜段100克，桂皮25克，大料25克，花椒盐5克。', '带骨羊后腿1只(约2.5公斤)。');
INSERT INTO `recipe` VALUES (5613, 429, '侉炖羊肉', '1.将香菜切去根，用水洗净，切成8分长的段。将25克葱切段、25克葱切成细丝。将15克姜切成片、10克姜切成细丝。将鸡蛋在碗内打散。 \r\n\r\n2.锅中注入清水，放入洗净的羊肉，上火烧开后将羊肉捞出。另换锅，注入300克清汤，加入10克料酒、1克精盐和葱段、姜片，放入羊肉，上火炖20分钟左右捞出，用刀切成长1寸5分、宽4分的长方条块。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，将熟羊肉条沾一层玉米粉，裹满鸡蛋液，下锅炸成淡黄色捞出，控净油。 \r\n\r\n4.锅中注入250克清汤，放入羊肉条，加入10克料酒、1克精盐和胡椒粉，在微火上炖5至10分钟，出锅时放入香菜、葱丝、姜丝、醋和香油即可。', NULL, NULL, '料酒20克，精盐2克，葱50克，姜25克，醋2.5克，胡椒粉1克，玉米粉5克，清汤550克，花生油(约耗20克)，香油少许。', '主料：净羊后腿肉300克。 \r\n配料：香菜25克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (5614, 429, '兰花豆干', '1.先将豆腐干温油炸一下，再将豆腐干开1公宽的蓑衣花刀。葱切段，姜拍破。 \r\n\r\n2.坐炒锅，加油，炸豆腐干成金黄色，捞出控油。 \r\n\r\n3.炒锅留底油，煸炒葱姜、大料出香味，下汤、精盐、糖、绍酒和豆腐干，大火烧开后小火煨透，收干汤汁，淋上香油，出锅晾凉，改刀装盘。', NULL, NULL, '葱姜、大料、糖、绍酒、精盐各适量，上汤1公斤。', '白豆腐干250克。');
INSERT INTO `recipe` VALUES (5615, 429, '鲤跃龙门', '1.将活鱼刮去鳞，剖腹取出内脏，除去鱼鳍、鱼腮，用水洗净，控净水水，用刀在鱼身两侧划一字形花刀。用水将肥猪肉、玉兰片、香菇洗净，切成筷子头大小的方丁。用水将青蒜洗净，切成1寸长的段。 \r\n\r\n2.坐煸锅，注入花生油，烧至七成热，将鱼放入，炸成浅黄色时捞出，控净油。 \r\n\r\n3.坐煸锅，注入熟猪油，把豆瓣辣酱放入锅中煸炒，待油炒成红色时，注入清汤，开锅后略煮片刻，把豆瓣酱捞出。另坐一煸锅，注入25克花生油，烧至五成热时，下入肥猪丁、玉兰片丁、香菇丁煸炒片刻，再加入料酒、精盐酱油、白糖、醋、葱段、姜段。随即将炸好的鱼放入锅中，再将炒豆瓣酱的原汤倒入锅中，上旺火烧开后，移小火靠30分钟，再用大火收至汤汁将尽时将鱼起出，放入鱼盘中。锅中放入青蒜段，在火上煸炒 一下，倒在鱼身上即成。', NULL, NULL, '熟猪油100克，精盐2克，酱油10克，白糖50克，豆瓣辣酱50克，醋5克，清汤500克，料酒25克，葱段、姜段各50克，花生油1公斤(约耗30克)。', '主料：活鲤鱼1尾(约重1250克)。 \r\n配料：肥猪肉50克，水发玉兰片100克，水发香菇100克，青蒜25克。');
INSERT INTO `recipe` VALUES (5616, 429, '龙井金鱼', '1.将起酥面折叠好，擀成长方片，用刀切成16个长条，每条长7厘米、宽2厘米。在每条的一端均匀地切四刀。剩下的面再擀薄，刻出36个小圆片作鱼眼眶。 \r\n\r\n2.将酥皮面卷成卷，揪成16个小剂，用手按成圆皮，包入豆沙馅，揉成一头圆一头尖的圆锥形，作龙井金鱼的胎形。 \r\n\r\n3.将金鱼胎形的圆头部位刷上鸡蛋清，粘上两个起酥小圆片，用筷子头在圆片上捅上圆洞，即成鱼眼眶。将胎形中间一段用花镊子捏起一道花边，两边刷上鸡蛋清，粘上芝麻。将樱桃切成小圆片放在鱼眼眶中。 \r\n\r\n4.将金鱼胎形的尖头部位刷上鸡蛋清，裹上切好的起酥条，放入烤盘中，用手码成鱼尾形，放进烤炉中，烤熟即可。', NULL, NULL, NULL, '酥皮面160克，起酥面200克，豆沙馅90克，芝麻10克，樱桃4个，鸡蛋清适量。');
INSERT INTO `recipe` VALUES (5617, 429, '龙衔海棠', '1.用铁制小勺顺丝轻轻地将鱼肉一层一层地刮下(如有鱼刺可挑出)，然后用一块肉皮，面朝下放在菜墩上，将刮下的鱼肉放在肉皮上，用刀脊砸成茸(如鱼茸发粘，可加入少量清水，以保证鱼茸不沾刀为准)，挑去筋和细刺，放碗中，加料酒、精盐、玉米粉各少许，鸡蛋清1个，搅拌上劲，制成鱼茸糊。 \r\n\r\n2.用清水将虾仁、水发香菇、马蹄洗净，切成末。坐煸锅注入25克鸡油，烧至五成热，下入以上三种末煸炒，同时加入料酒、精盐各少许，将三种末炒熟成馅，倒入漏勺中，控净汤汁，分成十二份。将火腿切成十二根长1寸的细丝。 \r\n\r\n3.用手将鱼茸糊挤成十二个小圆薄饼，外面沾上少许干玉米粉(以不粘手为宜)中间放入一份熟馅，包起，成海棠形，上面竖插入一根火腿丝，两边贴上两片香菜叶，放在盆中，上屉蒸5至6分钟后取出，放另一圆盘中。 \r\n\r\n4.将清汤注入汤锅中，加入料酒、精盐，上火烧开后撇去浮沫，加入用水调稀的玉米粉勾成汁，淋上少许鸡油，浇在菜上即成。', NULL, NULL, '料酒10克，精盐1.5克，清汤200克，干玉米粉10克，湿玉米粉10克，鸡油30克鸡蛋清1个。', '主料：鱼肉250克。 \r\n配料：虾仁50克，熟火腿40克，马蹄25克，水发香菇25克，香菜叶少许。');
INSERT INTO `recipe` VALUES (5618, 429, '萝卜桂鱼', '1.将净桂鱼肉切成长3寸、一头宽4分、一头宽1寸、厚1分的薄片，共片十二片，然后放入碗中，加入葱姜末和料酒、精盐各少许拌匀，腌10分钟。用水将香菇、玉兰片洗净，用虾肉一起切成细末。 \r\n\r\n2.将虾肉末、香菇末、玉兰片末放入碗中，加入料酒、精盐搅拌成馅。将鸡蛋液ke在碗中，打散。切去香菜根，取用上半部(约2寸长)，用水洗净，放入消毒水中浸泡10分钟，捞出，用清水冲洗一遍，控净水。 \r\n\r\n3.将鱼片平放在菜墩上，放入馅料，从窄的一方斜着卷起成萝卜形。坐煸锅注入花生油，烧至六成热，将鱼卷沾一层干玉米粉，再沾一层鸡蛋液，然后放入面包渣中裹满按实，放入油锅炸成金黄色时捞出。用牙签将每个鱼卷的精头竖戳一个小眼，插入一根香菜叶(留在外部约1寸)，然后摆在盘中即可。', NULL, NULL, '料酒10克，精盐1.5克，葱、姜末各少许，面包渣50克，玉米粉15克，花生油500克(约耗30克)。', '主料：净桂鱼肉250克。 \r\n配料：水发香菇50克，水发玉兰片50克，净虾肉50克，鸡蛋两个，香菜50克。');
INSERT INTO `recipe` VALUES (5619, 429, '炝黄瓜衣', '1.用清水将黄瓜洗净，切成2寸长的段，片下黄瓜皮，卷成卷，放入盆中，撒些精盐，腌10分钟，捞出，挤出水，码在盆中。 \r\n\r\n2.用刀将姜刮去皮，洗净，切成细丝，放在黄瓜皮上。 \r\n\r\n3.锅中注入适量清水，加入醋精、白糖，上火熬化后晾凉，倒在黄瓜皮上，再放入桂花酱，用盘子盖上腌十小时。 \r\n\r\n4.将黄瓜皮卷取出，切成小段，断面朝上，码在盘中即可食用。', NULL, NULL, '精盐1克，醋精20克，白糖50克，桂花酱10克，姜15克。', '黄瓜750克(约6条)。');
INSERT INTO `recipe` VALUES (5620, 429, '清炸鹌鹑', '1.用一只手揪住鹌鹑翅膀，另一只手从脯处撕下皮和羽毛，剪去头、翅膀和爪子，从腹部开膛，掏出五脏，撕下鹌鹑腿。 \r\n\r\n2.用清水洗去鹌鹑腿的血污和杂物，放碗中，加入料酒、精盐、酱油、葱姜段，搅拌均匀，腌制10分钟，然后放入玉米粉拌匀。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，将鹌鹑腿逐个下入，略炸，捞出。将油锅上旺火，烧至七成热，下入鹌鹑腿冲炸两次，呈金黄色时捞出，放入盘中即成。', NULL, NULL, '料酒15克，精盐1克，酱油5克，玉米粉5克，花生油500克(约耗20克)，葱、姜段各25克。', '活鹌鹑8克。');
INSERT INTO `recipe` VALUES (5621, 429, '秋菊傲霜', '1.将桂鱼刮去鱼鳞，开膛掏去肠肚和鱼鳃，用清水洗净，剁去头、尾，剔去骨刺，成两片带皮的净鱼肉；锲菊花刀，然后切成六个三角形的大块。 \r\n\r\n2.将桂鱼块放碗中，加入料酒、精盐各少许，拌匀，腌10分钟左右。用水将90克湿玉米粉调成稠糊状，放入碗中，将鱼块裹满糊，再沾一层干玉米粉，使鱼条互不粘连。 \r\n\r\n3.坐煸锅，注入花生油，烧至七成热，将鱼块放入锅中，炸至金黄色时捞出摆在盘中，四周用洗净的芹菜叶围边。 \r\n\r\n4.在炸鱼块的同时，另用一煸锅，注入25克花生油，烧至五成热，放入葱姜末炝锅，随时放入番茄酱煸炒，待炒熟时，加入剩余的料酒、精盐、白糖、醋、清汤、炒至汤汁发浓时，再加入用水调稀的10克玉米粉，勾成芡汁，淋上热花生油，浇在鱼上即可。', NULL, NULL, '主料：桂鱼一尾(约1公斤)。 \r\n\r\n配料：芹菜叶50克。', '主料：桂鱼一尾(约1公斤)。 \r\n配料：芹菜叶50克。');
INSERT INTO `recipe` VALUES (5622, 429, '如意卷', '1.将小枣用水洗净，掏去核，每个枣从中间横着切成两半。 \r\n\r\n2.将发面对好碱，放入白糖揉匀，饧10分钟后揪成八个面剂，分别用擀面棍擀成长2寸5分、宽1寸5分的长方形薄片，上面刷一层香油，顺长边卷成圆卷用手轻轻压扁；再顺长切成两半，每半将断面朝上，两头各放上半个小枣(断面朝下)，由两端分别向中间卷起，呈如意形，然后饧10分钟，上屉蒸约8分钟，取出放入盘中即可。', NULL, NULL, NULL, '发面250克，小枣16个，白糖25克，香油5克。 \r\n');
INSERT INTO `recipe` VALUES (5623, 429, '如意竹荪', '1.将竹荪放入盆中，注入凉水，泡20分钟，用清水洗净捞出，放在菜墩上，切去顶部的网状菌伞和根部，用剪刀从筒中间竖着剪开，改刀成长3寸5分、宽2寸的片，挤干水，放在菜墩上，竹荪上面撒些干玉米粉。 \r\n\r\n2.将鸡蛋清倒入平盘中，用筷子抽起呈雪白泡沫状。将鸡茸放碗中，加入精盐、料酒、熟猪油各少许，搅拌上劲，加入鸡蛋清，搅拌成糊。用刀将马蹄剁成细末，加入糊中拌匀。 \r\n\r\n3.用小刀将鸡茸糊抹在竹荪上，抹平后将火腿末顺着竹荪的长边撒成一条线将油菜末对称地撒在竹荪的另一边。然后两边对着卷起，用干净纱布包好，放盘中，上屉蒸熟取出，打开纱布，将如意竹荪切成4分宽的段，断面朝上，整齐地码在盘中。 \r\n\r\n4.汤锅中注入清汤，加入料酒、精盐，上火烧开，撇去浮沫，用湿淀粉勾成芡汁，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒10克，精盐1.5克，清汤200克，鸡蛋清1个，湿淀粉15克，鸡油10克，熟猪油少许，干玉米粉少许。', '主料：竹荪25克。 \r\n配料：鸡茸100克，马蹄5个，熟火腿末、油菜末各少许。');
INSERT INTO `recipe` VALUES (5624, 429, '双色豆糕', '先将炒好的小豆糕溶液倒入长9寸、宽6寸、高7分的平底铁模子中摊平，晾凉待用。将豌豆黄溶液晾温，倒在小豆糕表面上摊平，盖上一层薄油纸，晾凉后放入冰箱。食用时将双色豆糕取出，扣在案板上，切成5分见方的块，码在盘中即成。', NULL, NULL, NULL, '豌豆黄溶液500克，小豆糕溶液500克。');
INSERT INTO `recipe` VALUES (5625, 429, '四喜饺', '1.用细罗将面粉筛过，浇入120克开水拌匀搓透，揪成15个圆 剂，压扁，用擀面棍开薄成圆皮。 \r\n\r\n2.用开水将扁豆烫过，切成碎末。将紫菜头切成碎末。将鸡 蛋放入锅中，注入清水，上火煮熟，剥皮，将蛋白、蛋黄分开， 分别剁成碎末。 \r\n\r\n3.将虾肉馅分成15份，分别放在圆皮上，将面皮分四等份向上 分别对捏在一起，呈四个孔洞，用手捏牢，顺着一个方向卷成 四个喇叭口状。将蛋白末、蛋黄末、紫菜头末、扁豆末分别填 满喇叭口内，共做成15个四喜饺，上屉蒸8分钟即成。', NULL, NULL, NULL, '面粉150克，虾肉馅200克，鸡蛋1个，紫菜头100克，扁豆3根。');
INSERT INTO `recipe` VALUES (5626, 429, '太极发财燕', '1.将燕菜放入大碗中，加入温水泡发约一小时，捞出，择去燕毛、杂质及燕菜两头的根，在净水中洗两遍，撕成细 条。 \r\n\r\n2.将食碱放入大碗中，用开水冲化，放入燕菜，用筷子慢 拌匀。待燕菜涨起后滗去碱水，用开水冲洗两三遍，再用 烧开有300克清汤烫一遍，放在大盘的一边。 \r\n\r\n3.将水发菜放碗中，加汤、葱、姜、鸡油，上笼蒸1小时，滗出汤，放在大盘的另一边，与燕菜构成太极图形，各点 缀红绿樱桃一颗。 \r\n\r\n4.起两锅，分别注入汤：一汤调味后勾芡，淋鸡油，浇于 燕窝上；另一汤加蚝油，勾芡，浇于发菜上，即可上席。', NULL, NULL, '料酒10克，精盐1.5克，食碱5克，蚝油10克，水淀粉15克， 清汤1.5公斤。', '干燕菜40克。');
INSERT INTO `recipe` VALUES (5627, 429, '檀扇鸭掌', '1.用水将鸭掌洗净泥沙，剪去脚垫。将清水注入锅中，放入鸭掌，在火上煮15分钟左右，捞出，放入凉水盆中过凉，用手将鸭掌的筋骨从背面拆净，剪去毛边，放入盆中，用沸水烫一下捞出。先将一个鸭掌放在圆盘边上(掌面朝上)，再将其余鸭掌的跟部同第一个鸭掌的跟部叠起，呈打开的扇面形。 \r\n\r\n2.将芥末放入碗中，注入适量开水，冲熟，用盘子盖严，放入冰箱中。待凉后打开盘子，再加入醋、精盐、香油，调和均匀，浇在鸭掌上即可。', NULL, NULL, '芥末粉10克，精盐0.5克，醋5克，香油5克。', '鸭掌25个。');
INSERT INTO `recipe` VALUES (5628, 429, '熊猫蟹肉', '1.将蟹肉中的皮骨挑出，用手搓成丝。加上精盐上锅蒸10分 钟。 \r\n\r\n2.将发菜放碗中，注入温水，浸泡10分钟，捞出，洗净，去 杂质，控净水。 坐煸锅，注入花生油，烧至六成热，放入发菜略炸一下，捞出，控净油。 \r\n\r\n3.将清汤注入汤锅，放入发菜，加少许精盐，在火上煨5分钟， 捞出，控净水，撕成小段，放入碗中，加入香油、精盐拌均 匀。 \r\n\r\n4.将蟹肉放入圆盘中，码成熊猫胎形，再用发菜码在熊猫身上成黑毛，将蛋白糕做成小圆片，码在熊猫头部成眼睛。用刀片下黄瓜皮，刻成竹叶形，码在熊猫周围即可。', NULL, NULL, '精盐1克，香油5克，清汤100克，花生油250克(约耗15克)。', '上等蟹肉250克，发菜15克，蛋白糕2.5克，黄瓜50克。');
INSERT INTO `recipe` VALUES (5629, 429, '绣球全鱼', '1.将鲤鱼刮去鳞，开膛掏去五脏及鳃，用清水洗净，控净水，放在菜墩上。用刀从划水后将鱼头剁下来，再将鱼头的嘴从下唇剁开，但顶部要连着，鱼尾也剁下来，用刀剔净鱼身的骨、刺和鱼皮，约得净鱼肉300克。用开水将鱼的头、尾烫一下，刮去皮上的黑膜，用清水洗净，摆在盘中。 \r\n\r\n2.将225克鱼肉和洗干净的香菇、玉兰片及火腿均切成1寸5分长的细丝。用水将胡萝卜洗净，刮去外皮，切成豌豆大小的丁。取一块干净的猪肉皮，面朝下，放在菜墩上，将剩下的75克鱼肉放在肉皮上，用刀背砸成茸，然后挑净筋及刺。 \r\n\r\n3.将鱼茸放碗中，加入料酒、精盐、玉米粉各少许，再加入1个鸡蛋清及葱姜末搅拌均匀，再放入鱼肉丝、香菇丝、玉兰片丝搅拌均匀，用手挤成十二个直径约1寸的丸子，即成绣球，放在盛有鱼头、尾的盘内，上屉蒸5至8分钟后取出。将绣球鱼放在另一盘盘中间，鱼头、尾分别放在两侧。 \r\n\r\n4.将清汤注入汤锅，加入料酒、精盐、青豆和胡萝卜丁，上火烧开，撇去浮沫，加入用水调稀的玉米粉勾成汁，淋上鸡油，浇在绣球鱼丸和鱼头、鱼尾上即可。', NULL, NULL, '料酒15克，精盐1克，葱姜各少许，鸡蛋清1个，清汤200克，湿玉米粉50克，鸡油10克。', '主料：鲤鱼一尾(约1公斤)。 \r\n配料：水发香菇50克，水发玉兰片50克，熟火腿50克，青豆25克，鱼茸50克，胡萝卜50克。');
INSERT INTO `recipe` VALUES (5630, 429, '雪月羊肉', '1.用水将羊肉洗净，放入锅中，注入清水，上火烧开，煮10分钟左右，捞出用水将油菜叶洗净，切成细丝。将鸡蛋清放入盘中，用筷子抽起，呈雪白泡沫状，然后加入20克干玉米熔，拌匀成糊。 \r\n\r\n2.坐煸锅，注入25克花生油，下入葱段、姜段、桂皮、大料，炸出香味，注入850克清汤，加入15克料酒、1克精盐、酱油，放入羊肉，开锅后撇去浮沫，移至微火炖50分钟左右，八成烂时捞出晾凉。 \r\n\r\n3.将羊肉的边角切齐，然后切成长1寸2分、宽5分、厚2分的长方形片12片。 \r\n\r\n4.取四个碗形小酒盅，内壁涂抹一层花生油，在每个小酒盅内磕入一个鸽蛋(不要将蛋黄磕破)，上屉蒸5至7分钟取出，用小刀从酒盅边上划开，取出鸽蛋。 \r\n\r\n5.坐煸锅，注入花生油，烧至七成热，下入切好的油菜丝，炸成松，捞出控净油。 \r\n\r\n6.坐煸锅，注入熟猪油，烧至四成热，将熟羊肉条沾一层干玉米粉，再裹满蛋清糊，下入锅中，用温油炸熟(不可上色)，捞出后控净油，整齐地码在盘中央，四周撒一些油菜松，在油菜松上对称地放上四个蒸熟的鸽蛋。 \r\n\r\n7.锅中注入150克清汤，加入10克料酒、1克精盐和白糖，上火烧开后撇去浮沫，放入用水调稀的湿玉米粉勾芡，淋上鸡油，浇在羊肉上即成。', NULL, NULL, '料酒25克，精盐2克，白糖5克，酱油50克，鸡蛋清3个，干玉米粉25克，湿玉米分10克，清汤1公斤，花生油500克(约耗40克)，熟猪油750克(约耗25克)，葱段50克，姜段25克，桂皮、大料各少许，鸡油5克。', '主料：羊肋条肉400克。 \r\n配料：鸽蛋4个，油菜叶100克。');
INSERT INTO `recipe` VALUES (5631, 429, '雨后春笋', '1.将干贝去筋，用凉水洗净，放大碗中，用清水浸泡1小时，上屉蒸3小时左右，取出大碗，使干贝在原汤中泡1小时，捞出，冲洗两遍，再放入原汤(过细罗除去沙子)中浸泡。 \r\n\r\n2.用清水将鲜笋尖洗干净，一破两开，放入开水锅中氽一遍捞出。坐煸锅，注入15克鸡油，下入笋尖煸炒一下，随即注入300克清汤，加入精盐、料酒各少许，再放入发好的干贝，用微火靠5分钟左右，捞出，控净水；将干贝(不要弄散)平码在圆盘四周，将竹笋整齐地码在中央。 \r\n\r\n3.锅中注入200克清汤，上火烧开，对入少许蒸干贝的原汤，加入剩余的精盐，撇去浮沫，用水将玉米粉调稀，勾成芡汁，淋上鸡油，浇在菜上即成。', NULL, NULL, '料酒15克，精盐1.5克，鸡油25克，清汤500克，湿玉米粉10克。', '主料：鲜嫩笋尖750克。 \r\n配料：干贝100克。');
INSERT INTO `recipe` VALUES (5632, 429, '玉掌献寿', '1.锅中注入清水，放入熊掌，在微火上煮五六小时，捞出，趁热用手撕下毛及掌皮，再用镊子择去细毛，洗净。从母鸡腹部开膛，掏出五脏，洗净。将葱切段，姜拍碎。 \r\n\r\n2.锅中注入清水，放入熊掌，加入葱、姜各50克，上火煮5分钟后捞出。锅中注入清水，放入母、猪肉、火腿、葱、姜和熊掌，加入20克料酒，在微火上炖两三小时，直至熊掌能脱骨时取出，从背面剔去大小骨头。 \r\n\r\n3.将鱼茸调味后做成桃形，上笼蒸熟后，码在大盘四周。将熊掌放大碗中加150克清汤、料酒、精盐各少许，把苹果切两瓣，放熊掌上，上屉蒸30分钟取出，滗净汤汁，捡去苹果，扣在大盘中间。 \r\n\r\n4.锅中注入350克清汤，加入料酒、精盐，上火烧开，撇去浮沫，加入用水调稀的玉米粉勾成浓汁，淋上鸡油，浇在熊掌和玉桃上即成。', NULL, NULL, '料酒40克，精盐2克，姜、葱各100克，湿玉米粉20克，清汤500克，鸡油15克。', '主料：鲜熊掌1只(约1750克)。 \r\n配料：熟火腿250克，猪后腿瘦肉1公斤，母鸡1只，苹果1个，鱼茸150克。');
INSERT INTO `recipe` VALUES (5633, 429, '云河段霄', '1.将香蕉剥去外皮，切成长1寸5分、宽5分、厚1分的长方形片。 \r\n\r\n2.将金糕、蜜枣均切成与香蕉片同样大小的片(蜜枣如不够大，可用两片拼起来)。 \r\n\r\n3.取两片香蕉，中间夹一片金糕、一片蜜枣，即成段霄，共做15个。 \r\n\r\n4.将鸡蛋清放入大盘中，用筷子抽起呈雪白泡沫状，加入玉米粉拌匀。 \r\n\r\n5.坐煸锅，注入花生油，烧至五成热，把段霄的六面沾一层面粉，再裹满蛋清糊，放入油锅中炸熟(不要上色)，捞出控净油，码在圆盘中，撒上白糖即可。', NULL, NULL, '白糖50克，鸡蛋清2个，玉米粉20克，面粉10克，花生油500克(约耗30克)。', '主料：香蕉500克。 \r\n配料：金糕200克，蜜枣100克。');
INSERT INTO `recipe` VALUES (5634, 429, '炸春卷', '1.将面粉放入碗中，加清水调成稀糊。 \r\n\r\n2.将春卷馅分成十五份，分别放入春卷皮中间，四面包起，成长2寸、宽5分的长条，在收口处抹上面粉糊粘牢，即成生春卷。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，下入生春卷，炸至金黄色时捞出，控净油，码入盘中即成。将面粉加清水和匀，再加精盐，用手抽打滋润后饧半小时。将饼铛烧热擦净把和好的水面用手抓起，一次次地放入饼', NULL, NULL, NULL, '春卷皮15张，春卷馅150克，面粉25克，花生油500(约耗40克)。');
INSERT INTO `recipe` VALUES (5635, 430, '白银如意', '1.将冬笋片成3寸长、2寸宽、1分厚的片，出水后下汤锅滚透。 将鸡蛋清抽起泡，调入鸡泥中，加精盐、绍酒、鸡油，搅拌成 茸。 \r\n\r\n2.将冬笋片的一面拍粉，并抹上鸡茸，两边各放少许发菜和火 腿末，由两边卷向中央呈如意状，上笼蒸3分钟，取出装盘。 \r\n\r\n3.锅中注入150克上汤，调入精盐、绍酒，上火烧开，下淀粉 勾二流芡，淋鸡油，浇于冬笋上即可。', NULL, NULL, '精盐、绍酒、上汤、鸡油各适量。', '冬笋150克。 配料：鸡泥100克，鸡蛋清50克，火腿末25克，水发发菜10克，上汤 150克。');
INSERT INTO `recipe` VALUES (5636, 430, '百子冬瓜', '1.将两个冬瓜刮去表皮。用小刀从距根部约1寸的地方开刀，将 其中的一个冬瓜根部刻成齿轮状，要刻透冬瓜；然后将冬瓜根 部打开，掏净冬瓜瓤；将冬瓜外皮刻成艺术图案。汤锅注入开 水，将刻好的冬瓜放入锅中氽一遍捞出，在清水中过凉，放在 大海碗中待用。将玉兰片、香菇、火腿均切成4分见方的小方叮 \r\n\r\n2.用刀将第二个冬瓜片成直径为4分的圆球。汤锅注入少许熟猪 油，上火烧热，放入冬瓜球、玉兰片盯香菇盯火腿丁煸炒 一下，加入精盐、料酒各少许，100克清汤，在火上煨两分钟， 然后连汤倒入刻好图案的冬瓜内，盖严冬瓜根部的盖，放入一 个海碗中，加入400克清汤，上屉蒸20分钟左右即可取出，放入 另一个海碗内。 \r\n\r\n3.汤锅注入原汤，加入料酒、精盐，对好口味，上火烧开，撇 去浮沫，淋上鸡油，倒入放冬瓜的海碗内即成。', NULL, NULL, '精盐2克，料酒25克，清汤500克，熟猪油25克，鸡油少许。', '小冬瓜两上(每个约重500克左右)。 配料：水发玉兰片50克，水发香菇50克，熟火腿100克。');
INSERT INTO `recipe` VALUES (5637, 430, '冰花雪莲', '1.将莲子去皮，捅去莲心，用温水洗净。 \r\n\r\n2.用热水将哈士蟆浸泡4小时，用水洗净。 \r\n\r\n3.将莲子和哈士蟆分别放入两个容器中，加入清水(水没过食物 表面)，放入蒸箱中蒸20分钟左右，取出，滗净水，放汤碗中。 \r\n\r\n4.锅中注入500克清水，加入白糖，上火烧开，倒在盛有莲子和 哈士蟆的汤碗中即可。', NULL, NULL, NULL, '莲子100克，哈士蟆25克，白糖200克。');
INSERT INTO `recipe` VALUES (5638, 430, '参婆千子', '1.将梅花参放入清水中冲洗干净。净水发玉兰片洗净，切成 成长2寸、宽5分厚1分的片。将火腿切成长2寸、宽5分、厚1 分的片。用水将菜心洗净。将葱切段、姜拍碎。 \r\n\r\n2.锅中注入清水，上火烧开，放入梅花参氽两遍捞出，放入 盆中。锅中注入熟猪油，放入葱段煸炒，待发黄时，倒出30 克葱油，然后注入400克清汤，加入20克酱油、25克料酒、 1.5克精盐和糖色、姜段，用旺火烧开，撇去浮沫，倒入放 有梅花参的盆中，上屉蒸40分钟左右取出，将梅花参带汤倒 入汤锅中在微火上到汤汁发浓时将梅花参捞出，放长鱼盘中。 \r\n\r\n3.锅中注入开水，将玉兰片、香菇、油菜心放入锅中，氽一 遍捞出。用清水将是籽洗净，添上汤，上笼蒸30分钟。另用 一锅，注入150克清汤，放入香菇玉兰片、油菜心和火腿片， 加入精盐、料酒各少许，在微火上煨二三分钟后捞出，整齐 地码放在梅花参上。 \r\n\r\n4.锅中注入250克清汤，加入是籽及原汤，再加入剩余的料 酒、精盐、酱油上火烧开后撇去浮沫，放入用水调稀的玉米 粉勾成芡汁，淋上葱油，浇在梅花参上即成。', NULL, NULL, '料酒40克，精盐2.5克，酱油40克，湿玉米粉20克，葱100克， 姜50克，清汤900克，熟猪油150克，糖色40克。', '水发梅花参1个(约1公斤)。 配料：水发香菇50克，水发玉兰片50克，熟火腿50克，油菜心两棵， 虾籽25克。');
INSERT INTO `recipe` VALUES (5639, 430, '翠柳凤丝', '1.用水将鸡脯肉洗净，剔去筋、皮，切成细丝，放入碗中，加 入鸡蛋清及少许精盐、料酒，搅拌均匀，放入5克玉米粉，拌 匀上浆。 \r\n\r\n2.将豆苗切去根部，用清水洗净，放在菜墩上，切成1寸长的 段。 \r\n\r\n3.坐煸锅，注入熟猪油，烧至六成热，下入鸡丝，用铁筷子划 开，倒入漏勺中。煸锅中留底油，下入葱姜末炝锅，随即放入 豆苗、鸡丝及调料，翻炒均匀，淋上鸡油，即可出锅。', NULL, NULL, '料酒10克，精盐1.5克，葱姜末各5克，湿玉米粉10克，鸡蛋清 1个，熟猪油400克(约耗25克)，鸡油10克。', '鸡脯肉150克，豆苗150克。');
INSERT INTO `recipe` VALUES (5640, 430, '翡翠玉扇', '1.用清水将菜心冲洗干净，用小刀在根部剞上十字花刀。用清 水将水发玉兰片洗净，切成长1寸5分、宽4分、厚1分的花刀形 薄片，共切4片。 \r\n\r\n2.锅中注入熟猪油，烧至五成热，下入油菜心煸炒一下，注入 奶汤，加入料酒、精盐、白糖各少许，放入玉兰片，炖3分钟， 倒入漏勺控净汤，整齐地码在盘中。将冬菇放容器中，加汤， 上笼蒸透后码于菜上。 \r\n\r\n3.锅中注入清汤，加入料酒、精盐，对好口味，上火烧开，撇 去浮沫，用水将玉米粉调稀，倒入锅中勾芡，淋鸡油，浇在菜 心上即成。', NULL, NULL, '料酒10克，精盐1.5克，白糖少许，清汤150克，奶汤250克，湿 玉米粉10克，熟猪油25克，鸡油10克。', '主料：油菜心15棵。\r\n配料：水发玉兰片40克，水冬菇50克。');
INSERT INTO `recipe` VALUES (5641, 430, '凤凰鱼肚', '1.用刀将鱼肚加工成宽7分、长1寸2分、厚1分的长方形片，共 切14片。将3个鸡蛋ke在大盘中，挑出蛋黄不用，用筷子将蛋 清抽起成雪白泡沫状；将另一个鸡蛋ke在碗中，加入少许精盐、 2.5克玉米粉，搅拌均匀。 \r\n\r\n2.将鱼茸放入碗中，加入料酒、精盐、鸡油、清汤各少许，搅 拌上劲，对入蛋清、1 2.5克玉米粉，拌匀成鱼茸糊。将洗净的 香菇切成长1寸5分、宽1分的细条。将发菜挑净杂质，用水冲 洗数遍后捞出。掐下豆苗顶部红1寸的叶，其余不用。 \r\n\r\n3.锅中注入清水，下入鱼肚片，上火烧开，氽两遍，再用100 克清汤煨二三分钟，捞出控净水。坐煸锅，烧热，将锅内刷一 层油，将锅离火，倒入鸡蛋液转动煸锅，吊成一张鸡蛋皮，切 成长1寸、宽1分的细条28条。 \r\n\r\n4.将鱼肚横摆盘中，竖抹鱼茸在中间，上面撒些火腿末和油菜 末。将香菇条插入鱼茸的前端，周围点缀些发菜，在香菇条顶 端抹上少许鱼茸做成凤凰头上面点上少许火腿末。将豆苗竖插 入鱼茸末端做尾巴，然后在豆苗两边各插入一根鸡蛋皮条做腿， 上屉蒸5分钟左右，取出放入大海碗中。 \r\n\r\n5.锅中注入清汤，加入剩余的料酒、精盐，上火烧开后撇去浮 沫，顺碗边浇入碗中即可。', NULL, NULL, '料酒15克，精盐2克，清汤1公斤，干玉米粉15克，面粉少许。', '\r\n \r\n主料：水发鱼肚300克。 \r\n配料：鱼茸100克，水发香菇75克，豌豆苗25克，水发发菜25克，熟火腿末、油菜末各5克，鸡蛋4个。\r\n \r\n');
INSERT INTO `recipe` VALUES (5642, 430, '佛手广肚', '1.用开水将鱼肚冲洗两遍，再用100克清汤上火煨2分钟左右，切 成长1寸8分宽7分、厚1分的片，然后顺片的长边每隔2分竖切一 刀，共切四刀(刀深至半分)，放入盘中，撒些面粉。 \r\n\r\n2.将鸡蛋清倒入盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放入 碗中，加入料酒、精盐、鸡油、玉米粉各少许，拌匀上劲后，放 入鸡蛋清，搅拌成糊。 \r\n\r\n3.用小刀将鱼茸糊抹在鱼肚片上没用刀切过的地方，将鱼肚折过 一半，盖在抹有鱼茸糊的鱼肚上，呈佛手状。在鱼肚的接口处抹 上一条鱼茸糊，在糊上点缀些火腿末和发菜，上屉蒸5分钟左右， 取出，码在盘中。 \r\n\r\n4.锅中注入200克清汤，加入剩余的料酒、精盐，上火烧开，撇 去浮沫，加入用水调稀的玉米粉，勾成芡汁，淋上鸡油，烧在佛 手鱼肚上即成。', NULL, NULL, '料酒15克，精盐1.5克，清汤300克，鸡蛋清1个，湿玉米粉10克， 面粉少许，鸡油10克。', '水发广肚150克。 配料：鱼茸50克，熟火腿末、油菜末各5克。');
INSERT INTO `recipe` VALUES (5643, 430, '佛手金卷', '1.将猪肉末放入碗中，加入料酒、1.5克精盐、葱姜末、香油搅 拌上劲后，加入20克玉米粉，拌匀成馅。将马蹄剁成碎末，放入 肉馅中拌匀，分成四份。将面粉加入适量水，调成稀糊。 \r\n\r\n2.将鸡蛋入碗内，加入0.5克精盐、15克玉米粉搅拌均匀。坐煸 锅，烧热，用油刷子擦一下，倒入一半鸡蛋液，转动煸锅，吊 成鸡蛋皮。照此方法将另一半鸡蛋液也吊成鸡蛋皮。 \r\n\r\n3.将两张鸡蛋皮分别切开，成为4个半张，在每半张鸡蛋皮的边 沿上抹上面糊，中间放一份肉馅，卷起，成为宽约8分的长条， 然后每隔2分宽切一刀(顶头留下1分的距离不要切断)，如此四 刀，第五刀时切断，即成佛手卷，共做四个。 \r\n\r\n4.坐煸锅，注入花生油，烧至六成热，下入佛手卷，炸成金黄 色时捞出，码入盘中，带花椒盐一同上桌。 \r\n', NULL, NULL, '料酒10克，精盐2克，湿玉米粉35克，面粉15克，葱姜末共5克， 花生油500克(约耗40克)，香油5克，花椒盐1克。', '瘦猪肉末200克。 配料：马蹄五个，鸡蛋3个。');
INSERT INTO `recipe` VALUES (5644, 430, '芙蓉鱼骨', '1.用清水将鱼骨洗净，放入盆中，注入开水浸泡两小时，原盆 上屉蒸30分钟后取出，挑出涨发好的鱼骨，放入清水盆中(鱼 骨发好时质地松软，用手稍一使劲捏，即成两半)；如有没涨 发好的，可继续上屉蒸，直至涨发好为止。然后用清水将发好 的鱼骨冲洗一遍，捞出，放入小盆中，注入清水(以没过鱼骨 为准)，入冰箱10小时左右，待涨发已满时取出，滗去水，用 刀切成小块。 \r\n\r\n2.将火腿、水发玉兰片、黄瓜皮均切成长8分、宽4分、厚1分 的片。将鸡蛋清倒入大碗中，注入100克清水，加入料酒、精 盐各少许拌匀，上屉蒸熟，即成芙蓉羹，取出后用小勺舀入 汤盘中。 \r\n\r\n3.锅中注入清汤，放入鱼骨、火腿片、玉兰片，加入料酒、 精盐各少许，调好口味，在火上烧两分钟左右，然后放入黄 瓜皮片。将玉米粉用水调稀，倒入锅中勾成芡汁，淋上鸡油， 倒在芙蓉羹上即成芙蓉鱼骨。', NULL, NULL, '料酒15克，精盐1.5克，清汤100克，鸡蛋清3个，湿玉米粉15 克，鸡油5克。', '主料：鱼骨100克。\r\n配料：水发玉兰片25克，熟火腿25克，黄瓜皮25克。 \r\n');
INSERT INTO `recipe` VALUES (5645, 430, '宫廷排翅', '1.锅中注入开水，放入水发鱼翅，上火氽两遍捞出，放入凉水 中洗净捞出，用干净纱布包好。锅中注入清水，放入母鸡、瘦 猪肉、火腿和鱼翅包，加入葱姜段和20克料酒，上火烧开，撇 去浮沫，移至微火上炖6小时左右，然后挑出鱼翅包，打开晾 凉，分成12份(其余配料不用)。 \r\n\r\n2.将鱼茸放入碗中，加入料酒、精盐、玉米粉、鸡油各少许， 搅拌上劲。将鸡蛋清倒入盘中，用筷子抽起，呈雪白泡沫状， 倒入鱼茸中，拌匀成鱼茸糊。 \r\n\r\n3.将每份鱼翅按顺丝长3寸、宽1寸的标准整齐地放入盘中， 上面抹上一层鱼茸糊；然后从中间部位将鱼翅对折过来，使折 过的一半鱼翅扣在另一半鱼翅上；再用小刀在鱼翅上顺丝抹上 3条鱼茸糊(间距为1分)，上面点缀火腿末和黄瓜皮末，然后上 屉蒸3至5分钟取出，放入另一个圆盘中码齐。 \r\n\r\n4.汤锅中注入清汤，加入料酒、精盐各少许，调好口味，上火 烧开，撇去浮沫；用水将玉米粉调稀，倒入锅中勾芡，淋上鸡 油，浇在宫廷排翅上即可。', NULL, NULL, '料酒30克，精盐1.5克，葱段50克，姜段25克，湿玉米粉15克， 鸡蛋清1个，清汤250克，鸡油10克。', '主料：水发鱼翅250克。 \r\n配料：母鸡半只，火腿100克，瘦猪肉250克，鱼茸50克，熟火腿末、黄瓜皮末各5克。');
INSERT INTO `recipe` VALUES (5646, 430, '海红鱼翅', '1.鱼翅的加工方法与宫廷排翅中的鱼翅加工方法相同。将螃蟹 放入盆中，上屉蒸熟取出，剥去外壳，挑出蟹肉和蟹黄。将胡 萝卜削去皮，用清水洗净，放在菜墩上，用刀切成丝。 \r\n\r\n2.坐煸锅，注入50克熟猪肉，烧至五成热，下入胡萝卜丝，用 温油炸成泥状(不可炸糊)，油即成为红色，捞出胡萝卜丝不用， 将油倒入碗中。 \r\n\r\n3.锅中注入20克熟猪油，下入蟹肉、蟹黄煸炒一下，然后注入 清汤，加入料酒、精盐、葱姜，调好口味，放入鱼翅，在微火 上kao至汤汁发浓时，倒入用水调稀的玉米粉，勾成芡汁，淋上 红萝卜油即可出锅。', NULL, NULL, '料酒30克，精盐1.5克，葱段50克，姜25克，湿玉米粉10克， 清汤200克，熟猪油700克。', '主料：水发鱼翅300克。\r\n配料：河螃蟹500克，母鸡半只，火腿100克，瘦猪肉250克，红色胡萝卜100克。');
INSERT INTO `recipe` VALUES (5647, 430, '荷包蟹肉', '1.将蟹肉挑去皮，同香菇、玉兰片一起剁成细末，放碗中，加 入料酒、精盐搅拌成馅，挤成20个小球。将鸡蛋ke入碗中打散， 加入湿玉米粉、精盐各少许，搅拌均匀。将少许油菜叶切成末， 其余的油菜叶都切成细丝。 \r\n\r\n2.将手勺放在火上烤热，用猪肥膘将勺内擦匀，将鸡蛋液倒入 手勺中(约有小羹匙)，摊成鸡蛋皮。在鸡蛋液还没有完全凝固 时，中间放上一份蟹肉馅，然后将蛋皮折过一半，用筷子轻轻 按一下，再用筷子竖着将馅夹在中间，待鸡蛋液完全凝固后取 出，放在盘中，即成荷包蟹肉。 \r\n\r\n3.将面粉放入碗中，加入少许清水搅拌成糊，用筷子沾上面糊， 抹在荷包蟹肉的馅肚上，上面撒一些火腿末和油菜末。坐煸锅， 注入花生油，烧至六成热，下入油菜丝，炸成深绿色时捞出， 控净油，撒在圆盘中。油锅继续坐火上，烧至七成热，放入荷 包蟹肉，炸成金黄色捞出，摆在油菜松上即成。', NULL, NULL, '料酒5克，精盐1.5克，猪肥膘肉25克，湿玉米粉少许，面粉15 克，花生油500克(约耗20克)。', '主料：罐桶蟹肉50克，鸡蛋3个。\r\n配料：水发香菇、水发玉兰片各25克，熟火腿末2.5克，油菜叶100克。');
INSERT INTO `recipe` VALUES (5648, 430, '荷花酥', '1.将面粉75克加入熟猪油45克，搓透，即成酥面。将剩下的 面粉加入5克熟猪油、清水50克，和匀搓透，即成皮面。将皮 面、酥面各揪成15个剂，用皮面包入酥面，用擀面棍擀成长 条，卷成面卷，用手再将面卷按扁，叠成三折，擀成圆皮。 \r\n\r\n2.将枣泥馅分成15份，分别放入圆皮中包起，用手揉成长圆 形，用刀在顶部剞五刀，切成5等分，刀口深至圆身的一半， 即成生荷花酥。 \r\n\r\n3.坐煸锅，注入花生油，烧至四成热，放入生荷花酥，用微 火温油炸熟(不要上色)，酥皮层层翻出，如盛开的花瓣，将 白糖撒在花心上即成。', NULL, NULL, NULL, '面粉150克，熟猪油50克，枣泥馅125克，白糖25克，花生油 500克(约耗35克)。');
INSERT INTO `recipe` VALUES (5649, 430, '红烧鱼唇', '1.用水将鱼唇洗净，去泥沙，剪去杂物，切成长1寸、宽8分的 长方形块，放入开水锅中氽两遍捞出。 \r\n\r\n2.锅中注入开水，把洗净的菜心放入氽熟，捞出，放入凉水中 过凉，然后控净水。将火腿切成两片长2寸、宽4分、厚1分的 大薄片。 \r\n\r\n3.炒锅下入少许葱油，下入鱼唇炒均匀后注入清汤，加入料酒、 精盐、酱油在微火上烧2分钟，再放入油菜心和火腿片，翻锅后 加入用水调稀的玉米粉勾成芡汁，淋上葱油，再翻锅倒入盘中 即成。', NULL, NULL, '料酒10克，精盐0.5克，酱油10克，清汤150克，湿玉米粉10克， 葱油25克。', '主料：水发鱼唇200克。 \r\n配料：油菜心两棵，熟火腿50克。');
INSERT INTO `recipe` VALUES (5650, 430, '黄袍加身', '1.将蟹肉择去皮、筋，放入碗中，加入少许料酒拌匀。将猪 肥肉切成直径1寸2分、厚1分的圆片，共切12片。用水将菜叶 洗净，切成与猪肉片同样大小的片。将鸡蛋清倒入碗中。加 入玉米粉和面粉，拌匀成稀糊。 \r\n\r\n2.将蟹肉挤成直径约9分的丸子，放在肥肉片上，用油菜叶圆 片盖严。坐煸锅，注入花生油，烧至六成热，将蟹肉裹满鸡 蛋清糊，下入油锅中炸熟(不要上色)，捞出，控净油，码入 盘中即可。', NULL, NULL, '料酒15克，精盐1克，鸡蛋清2个，干玉米粉10克，面粉10克， 花生油500克(约耗20克)。', '主料：罐头蟹肉200克。 \r\n配料：猪肥肉100克，油菜叶50克。');
INSERT INTO `recipe` VALUES (5651, 430, '姜汁扁豆', '1.将扁豆掐去两头及筋，用清水冲洗干净。将姜刮去外皮，用 水洗净，切成细末。 \r\n\r\n2.锅中注入清水，上火烧开，放入扁豆氽熟，捞出，放入凉开 水中过凉，捞出，滗净水。 \r\n\r\n3.将氽熟的扁豆放入容器，加入精盐、香油、姜末搅拌均匀， 腌30分钟，切成长2寸的段，码在盘中，再用剩余的扁豆切成丝， 在盘边码出花边即可。', NULL, NULL, '精盐1克，花椒油10克。', '鲜嫩扁豆250克，姜50克。');
INSERT INTO `recipe` VALUES (5652, 430, '金钱鱼肚', '1.将鱼肚改刀成圆片14片(直径为1寸2分)，用开水氽两遍，用 200克清汤煨5至6分钟。 \r\n\r\n2.将鸡蛋清放入盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放 入锅中，加入料酒、精盐、熟猪油、湿玉米粉各少许，搅拌上 劲，加入鸡蛋清，搅匀成稠糊。将马蹄剁成末，放入鱼茸糊中 拌匀。 \r\n\r\n3.将香菇剪成1寸2分长、1分宽的长条28根，将火腿切成边长6 分的菱形片56片，其余剁成火腿末。将鱼茸糊挤成直径约8分 的鱼丸放在鱼肚上。在鱼丸上对称地摆上两条香菇(呈半圆形) 和4片火腿片，做成古钱形，中间放一粒豌豆在香菇条外侧点缀 少许火腿末、油菜末，在火腿片外侧点缀少许发菜。按此方法 共做14个金钱。然后上屉蒸熟，取出后码放在另一盘中。 \r\n\r\n4.锅中注入200克清汤，加入料酒、精盐，上火烧开，撇去浮 沫，用水调稀玉米粉，倒入锅中勾芡，淋上鸡油，浇在金钱 鱼肚上即成。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清2个，清汤400克，湿玉米粉20克， 面粉2.5克， 熟猪油少许，鸡油10克。', '水发鱼肚200克。 配料：鱼茸100克，水发香菇3个，马蹄25克，熟火腿75克，豌豆14粒， 水发发菜、熟火腿末，油菜末各5克。');
INSERT INTO `recipe` VALUES (5653, 430, '金鱼角', '1.用65克沸水将面粉烫成面团，加入黄油搓透，揪成16个面剂。 \r\n\r\n2.将面剂擀成圆皮，将皮的四分之一叠在下面，包入虾馅，向 上捏成三角形将叠边的角捏上花边，将叠起的面皮翻出，捏上 花纹，形成鱼尾。将另外两角分别由外向里捏成两只鱼眼，放 上两粒青豆。按此方法共做16个，然后上屉蒸5分钟即可。', NULL, NULL, NULL, '面粉100克，黄油5克，虾馅100克，青豆32个。');
INSERT INTO `recipe` VALUES (5654, 430, '葵花麻鱼', '1.用刀将草鱼肉片成2寸5分、宽2寸、厚2分的大薄片，放入碗 中，加入葱姜段、料酒、精盐，腌10分钟，取出控净水，两面 沾上一层玉米粉。 \r\n\r\n2.将鸡蛋清倒入盘中，用筷子抽起呈泡沫状。将鱼片裹满鸡蛋 清，然后在鱼片的一面蘸满芝麻。 \r\n\r\n3.坐煸锅，注入花生油，上火烧至五成热，下入芝麻鱼，在温 油中炸熟(不要上色)，捞出，控净油，晾凉，再改刀切成长1 寸、宽5分的片，码在盘中成葵花形即可。', NULL, NULL, '料酒10克，精盐0.5克，鸡蛋清3个，干玉米粉15克，葱、姜段 25克，花生油500克(约耗20克)。', '净草鱼肉200克。 配料：芝麻50克。');
INSERT INTO `recipe` VALUES (5655, 430, '莲子糕', '1.将1公斤开水注入铜锅中，加入食碱化开，放入干莲子，用 竹刷子刷去莲子外皮，见亮光为止，然后用清水冲洗数遍以免 上色。用刀切去莲子两头，用牙签捅出莲子心，再用温水冲洗 两遍。 \r\n\r\n2.将莲子放入碗中，注入开水(以漫过莲子为宜)，上屉蒸20分 钟后取出滗净汤，过细罗，擦成泥状，晾凉后倒入净湿布中， 隔着布揉成面团状，分成两块，用刀分别压成2分厚的大片。 \r\n\r\n3.用刀将金糕切成1分厚的薄片。将金糕片码放在莲子片上， 再把另一片莲子盖在金糕片上，使金糕片夹在莲子片中部，然 后切齐四边，再切成方块，码在盘中即右。', NULL, NULL, NULL, '干莲子350克，白糖150克，金糕50克，食碱4克。');
INSERT INTO `recipe` VALUES (5656, 430, '明珠豆腐', '1.用清水将豆腐漂洗净，控水后碾成细泥。将虾肉剁粒。将马 蹄切碎末。将姜切细末。 \r\n\r\n2.将豆腐泥、虾粒、马蹄末、姜末放入钵中，调入绍酒、精盐、 胡椒粉及1个鸡蛋清，调匀后加入鸡油。将1个鸡蛋清加面粉、 水淀粉调稠糊。 \r\n\r\n3.将调好的豆腐挤成8个大丸子，拖糊滚面包渣后炸熟，呈金黄 色，出锅装盘即可。', NULL, NULL, '精盐、姜、绍酒、胡椒粉适量，鸡蛋2个，面粉、水淀粉适量， 鸡油适量。', '南豆腐200克。 配料：净虾肉100克，马蹄50克，面包渣50克。');
INSERT INTO `recipe` VALUES (5657, 430, '母子相会', '1.用刀从母鸡背劈开，掏去五脏，剁下鸡爪，用水洗净。将 鹌鹑蛋放入凉水锅中，上火煮熟，剥去外皮，放入卤锅中卤 10分钟左右捞出。 \r\n\r\n2.将一个鸡蛋ke入碗中打散。将卤好的鹌鹑蛋先沾一层面粉， 再沾上鸡蛋液裹满面包渣。用半粒花生仁竖着插入鹌鹑蛋小端 做嘴，用两粒黑芝麻做眼睛用此方法共做12个。 \r\n\r\n3.用清水将土豆洗净，削去外皮，切成细丝，放入清水盆中浸 泡10分钟。用清水将油菜叶洗净，切成细丝。坐煸锅，注入花 生油，烧至七成热，放入土豆丝，炸成金黄色时捞出，即成土 豆松；再将油菜丝下锅，炸成深绿色时捞出，即成油菜松。 \r\n\r\n4.将料酒、酱油、五香料、葱、姜等调料涂抹在鸡身内外，放 入盆中腌4小时左右，原盆上屉蒸两小时左右，取出，滗净汤， 挑出葱姜不用。 \r\n\r\n5.坐煸锅，注入花生油，烧至六成热，将鹌鹑蛋下入油锅炸成 淡黄色捞出，控净油。待锅中油烧至八成热时，放入母鸡，炸 至金红色捞出，控净油。将土豆松放入盘的中央堆起，油菜松 撒在周围，把炸好的母鸡放在土豆松上面将炸好的鹌鹑蛋码在 油菜松上即成母子相会造型。', NULL, NULL, '料酒40克，精盐2.5克，酱油20克，五香料15克，葱段50克， 姜50克，花生油1250克(约耗75克)，面粉少许。', '母鸡1只(约1.5公斤)，鹌鹑蛋12个。 配料：土豆500克，油菜叶300克，花生仁50克，黑芝麻5克，细面包 渣20克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (5658, 430, '千层糕', '1.用水将猪板油洗净，切成碎叮将面粉上屉蒸熟，取出过细 罗，掺入猪板油丁和150克白糖、桂花酱、料酒、香油搓匀成 板油糖馅。 \r\n\r\n2.将发面对好碱，加入50克白糖揉匀，饧10分钟后用擀面棍将 面团擀成长方形薄往。将三分之一的板油糖馅铺在三分之二的 面皮上，对叠成三层(中间一层为没有糖馅的面皮)，用擀面棍 在两头开缝处各压一下，再擀成长方形薄片用南样的方法分两 次将剩下的板油糖馅用完，最后呈厚约3分的长方形薄片，用油 刷子在面上刷一层香油，叠成三层，擀成厚6分的长方形千层 糕胚。 \r\n\r\n3.将青梅、金糕、蜜枣、杏脯切成长1寸、宽1分的细条，间隔 地码在千层糕胚的面上，上屉蒸20分钟左右，取出晾凉，切成 长1寸、宽七分的块，码在盘中即可。', NULL, NULL, NULL, '发面500克，面粉100克，白糖200克，生猪板油75克，桂花酱 5克，料酒5克，青梅50克，金糕50克，蜜枣100克，杏脯50克。');
INSERT INTO `recipe` VALUES (5659, 430, '日月生辉', '1.将鲍鱼撕去毛病，用刀从鲍鱼肚下片平，使鲍鱼大孝薄厚 一致。将两个鸡蛋ke入盘中，挑出蛋黄放在碗中，用筷子将蛋 清抽起，呈雪白泡沫状；将两个鸡蛋ke在盛有鸡蛋黄的碗中打 散。用水将油菜叶洗净，切成细丝。 \r\n\r\n2.将鱼茸放碗中，加入料酒、精盐、玉米粉各少许和5克熟猪 油，搅拌上劲后再加入蛋清，拌匀成鱼茸糊。将虾茸放入碗中， 加入料酒、精盐、玉米粉各少许和5克熟猪油，搅拌上劲，ke 入一个鸡蛋，拌匀成虾茸糊。 \r\n\r\n3.将鸽蛋放入凉水锅中，上火煮熟，捞出，放凉水中冲凉，剥 去皮。将其中的12个鸽蛋用刀切齐大头，然后从上半部把蛋白 横着划开，并剥去蛋白，露出蛋黄。将另外12个鸽蛋的大头切 去约3分厚，使其能立祝 \r\n\r\n4.用12个鲍鱼片当托，在每个鲍鱼片上抹鱼茸糊，再放上一个 露蛋黄的鸽蛋(蛋黄朝上)，按实，用小勺将鸽蛋周围的鱼茸糊 抹齐，按此方法做成12个，放入盘中。另将12个鲍鱼片当托， 在每个鲍鱼片上抹虾茸糊，再放上一个切去大头的鸽蛋(断面朝 下)，按实，用小刀将鸽蛋周围的虾茸糊抹齐，按此方法做12个， 放盘中。 \r\n\r\n5.坐煸锅，注入花生油，烧至七成热时，分别下入鸡蛋液和油菜 丝，炸成松捞出控净油。在长鱼盘的中间横着码一条鸡蛋松，在 鸡蛋松上面码油菜松。将两种鲍鱼片上屉蒸熟后分别码在鸡蛋松 的两边，即成日月生辉造型。 \r\n\r\n6.锅中注入350克清汤，加入料酒、精盐，上火烧开，撇去浮沫， 放入用水调稀的玉米粉，勾成芡汁，淋上鸡油。将其中的一半芡 汁浇在露蛋黄的鲍鱼上将另一半芡汁加入酱油，上火勾成红芡汁， 浇在另一半鲍鱼上即可。', NULL, NULL, '料酒15克，精盐2克，熟猪油10克，鸡油10克，湿玉米粉25克， 清汤350克，花生油500克(约耗40克)。', '罐头鲍鱼两桶(挑选24个)，鸽蛋24个。 配料：鱼茸50克，虾茸50克，鸡蛋五个，油菜150克。');
INSERT INTO `recipe` VALUES (5660, 430, '松鹤延年', '1 将蟹肉做鹤身底，将我鲍鱼改刀成羽毛片覆盖在鹤身底即成鹤身， 顺势将蟹肉堆码成脖子和头，共做两只鹤。\r\n\r\n2 将冬茹剪成尾羽，嵌入鲤鱼片下；用红樱桃做眼睛及头饰；用黄瓜 做嘴及腿，爪。冬笋改刀成松树干状，在盘内成型，用黄瓜切成松枝， 装点在树干上。将鸭肉改刀后码于盘的下部；将虾去皮后破开码在鸭 肉旁。鹤延年拼盘遂告完成。', NULL, NULL, NULL, '蟹肉100克，炖鲍鱼150克，红曲鸭子100克盐水虾150克。 配料： 醉冬笋150克，卤冬菇2粒，酱牛肉150克，黄瓜3条。');
INSERT INTO `recipe` VALUES (5661, 430, '豌豆黄', '1.用小磨将豌豆破碎去皮，用凉水洗三遍。铜锅坐火，开锅 后将豌豆下入锅内，加入碱，然后将豌豆煮烂成稀粥状，并 带原汤过罗。 \r\n\r\n2.将边了罗的豌豆放入锅内，加入白糖和溶化的冻粉水，上 火炒30分钟左右 \r\n\r\n3.将炒好的豌豆泥倒入1尺1寸长、5分宽、7分高的白铁模子 内，然后放在通风处晾三四个小时，晾透即成豌豆黄，改刀 装盘即成。', NULL, NULL, NULL, '豌豆黄');
INSERT INTO `recipe` VALUES (5662, 430, '五丝菜卷', '1.将圆火腿、水发玉兰片、水发香菇、胡萝卜(削去皮)、莴笋 (去皮、筋)洗净，均切成长1寸5分的丝。锅中注入1公斤清水， 上火烧开，放入玉兰片丝、香菇丝、胡萝卜丝、莴笋丝氽一遍， 捞出，放入凉开水中过凉。 \r\n\r\n2.用清水将圆白菜叶洗净。锅中注入1公斤清水，上火烧开，将 圆白菜叶放入锅中氽一遍捞出，用凉开水过凉，捞出，控净水， 切成长1寸5分、宽1寸的长方形片，放入盆中，加入精盐搅拌均 匀，腌5分钟捞出，然后将圆白菜片平码在菜墩上，上面顺宽度 码上五种丝(两头各露出一小部分)，用白菜卷起放入盆中，加 入白糖、桂花酱、醋精，对入适量水，用盘子盖严，腌4小时后， 将菜卷翻过来，再腌两小时，取出码在盘中即可。', NULL, NULL, '精盐0.5克，白糖40克，醋精5克，桂花酱5克。', '圆白菜叶500克。 配料：圆火腿、水发玉兰片、水发香菇、胡萝卜、莴笋各30克。 \r\n');
INSERT INTO `recipe` VALUES (5663, 430, '仙鹤鲍鱼', '1.将鲜鲍鱼改刀成片，加精盐拌匀，装钵中。 \r\n\r\n2.将香油注入锅中，上火烧热，下入花椒，炸出香味，滗出 花椒，将油倾入鲍鱼片上，立即加盖，炮制15分钟后，拌匀， 即可装盘成仙鹤造型。', NULL, NULL, '花椒、精盐、香油适量。', '鲜鲍鱼200克。');
INSERT INTO `recipe` VALUES (5664, 430, '香露苹果', '1.将鱼茸放入碗中，加入少许料酒、少许精盐和5克鸡油，搅 拌上劲。鸡蛋清倒入盘中，用筷子抽起，呈白泡沫状，倒入 鱼茸中，再加少许玉米粉，拌匀成稀鱼茸糊。 \r\n\r\n2.在20个碗形小酒盅内，分别抹上一层鸡油，倒满鱼茸糊。 用水将豆苗洗净掐下嫩叶，放在鱼茸糊上，周围撒些火腿末， 即成香露苹果，上屉蒸3至5分钟取出。 \r\n\r\n3.用小刀在油盅边上划开，取出香露苹果，码在圆盘中。锅 中注入清汤，加入料酒、精盐，上火烧开，撇去浮沫，用水 调稀玉米粉，倒入锅中勾成芡，淋上5克鸡油，浇在菜上即可。', NULL, NULL, '料酒5克，精盐1.5克，湿玉米粉10克，清汤100克，鸡油15克。', '鱼茸50克。 配料：豆苗50克，熟火腿末2.5克。鸡蛋清3个。');
INSERT INTO `recipe` VALUES (5665, 430, '燕影金蔬', '1.将胡萝卜削成直径4分的圆球，共削12个。将火腿改刀成燕 羽毛片，共12片将12粒冬菇入汤锅，加精盐炖透。将菜心 洗净，头部开十字刀，用温油炸过后添汤加盐炖九成熟， 捞出。燕窝涨发后，用滚汤氽透。 \r\n\r\n2.将菜心入盘码成燕身头；将火腿片、冬菇摆成燕翅；将胡 萝卜球在燕头下部围成圆圈，中间放入燕窝，点缀上燕嘴 及眼睛。将上汤上火调好味，勾二流芡浇于菜上即可。', NULL, NULL, '精盐、绍酒、淀粉各适量，上汤1公斤。', '水燕窝50克，菜心12棵。 配料：胡萝卜250克，熟火腿150克，水冬菇150克。');
INSERT INTO `recipe` VALUES (5666, 430, '薏米膳继', '将薏米洗净，放锅中，加1公斤清水，上火烧开，撇去沫子，改用微火熬40分钟即成。', NULL, NULL, NULL, '薏米150克。');
INSERT INTO `recipe` VALUES (5667, 430, '玉兔白菜', '1.将酥皮面卷成卷，揪起12个小剂。用手将面剂按成面皮，包入豆沙馅，揉成一头圆、一头细长的生胚，用刀在细长的一头 切开一段，窝上去，即成两只兔耳朵；在生胚圆头部位切一个小尾巴；用面头掺上一点食红，揉成面团，做成眼睛，安在生胚上，即成一只小白兔。按此方法共做12个。 \r\n\r\n2.将小白兔放入烤炉中烤10分钟取出，将每只的后半部涮上糖水，粘上椰茸，再放入烤炉用微火烤5分钟即成，然后放入盘中，再放几棵烫面做成的小白菜，即成玉兔白菜造型。', NULL, NULL, NULL, '酥皮面160克，豆沙馅100克，椰茸少许。');
INSERT INTO `recipe` VALUES (5668, 430, '枣泥糕', '1.用水将小枣洗净，放入铜锅中，注入清水，上火煮40分钟后捞出，去掉枣核，擦过细罗，再放入铜锅中，加入白糖和溶化的冻粉水和用100克温水开的玫瑰酱水，上火炒20分钟左右。 \r\n\r\n2.将炒好的枣泥倒入1尺1寸长、5寸宽、7分高的白铁模子内， 用一张光滑的薄纸盖在上面，放在通风处晾三、四小时，晾透即成枣泥糕，放入冰箱待用。', NULL, NULL, NULL, '小枣750克，白糖175克，玫瑰酱100克，冻粉10克。');
INSERT INTO `recipe` VALUES (5669, 431, '长春羹', '1.用水将鹿冲、牛鞭洗净，用刀顺长破开，片去里面的杂物，削去尿道膜，放入盆中，用开水烫一下，然后撕去表皮，放入开水锅中煮1小时左右，捞出，用清水再冲洗几遍，以去异味。 \r\n\r\n2.将干贝、大海米、母鸡、猪肉、葱姜段放入锅中，注入清水， 加入鹿冲、牛鞭，上火烧开，撇去浮沫，移至微火炖2小时左右， 至八成烂时捞出。将牛鞭切成长1寸的斜菱形，将鹿冲改成寸长 的车轮花刀片。 \r\n\r\n3.汤锅注入清汤，加入鲜蘑、料酒、精盐、酱油和胡椒粉，上火 烧开，撇去浮沫，用水将玉米粉调稀，倒入锅中勾成芡汁，倒入鹿冲片和牛鞭片，淋上鸡油倒入海碗中即成。', NULL, NULL, '料酒25克，精盐2克，酱油15克，胡椒粉1.5克，湿玉米粉25克， 葱25克，姜15克，清汤500克，鸡油10克。', '主料：鲜鹿冲1个，牛鞭1个。\r\n配料：水发干贝50克，水发大海米50克，水发香菇50克，母鸡半只，瘦猪肉500克，罐头鲜蘑150克。');
INSERT INTO `recipe` VALUES (5670, 431, '二龙戏珠', '先切二块大一些的豆沙芸豆卷作龙头，然后将余的豆沙芸豆 卷切成薄片，用手将其中一部分捏成三角形，按龙身的弯曲 程度依次码好成龙脊，将其余薄片码在两侧。用以上方法将 芝麻芸豆卷也做成一条龙。两条龙的中间的红球是用红樱桃 水加冻粉淀成的。龙尾、龙爪和眼睛则是选用冻粉淀成的不 同颜色的冻而分别刻成的。 \r\n', NULL, NULL, NULL, '豆沙芸豆卷300克，芝麻芸豆卷300克。');
INSERT INTO `recipe` VALUES (5671, 431, '翡翠银耳', '1.将银耳放在无油的瓷器内，注入凉水浸泡1小时左右，捞 出，择去根部和变色部分，用清水洗净，放入开水锅中， 在火上氽一遍捞出。 \r\n\r\n2.用清水将油菜叶洗净，剁成细末，用纱布包起，挤出绿 汁，放入碗中。将鸡茸放另一碗内，加入料酒、精盐、 熟猪油、玉米粉各少许，搅拌上劲，再放入绿汁拌匀。 将蛋清放入盘中，用筷子抽起，呈雪白泡沫状，再倒入 绿鸡中，搅拌成糊。 \r\n\r\n3.取羹匙12个，在每个羹匙中抹少许鸡油，倒入绿鸡茸糊， 用小刀抹平，上面放银耳，上屉蒸熟取出。 \r\n\r\n4.锅中注入100克清汤，加入料酒、精盐各少许，放入剩余 的银耳，在火上煨2分钟，倒入漏勺中，滗净汤，放在圆 盘中。用小刀将翡翠银耳从羹匙边上划开取出，整齐地码 在银耳上。 \r\n\r\n5.汤锅中注入100克清汤，加入炒酒、精盐各少许，对好口 味，上火烧开，撇去浮沫，用水将玉米粉调稀，倒入锅中 勾成芡汁，淋上鸡油，浇在翡翠银耳上即可。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清2个，清汤250克，湿玉米粉 10克，熟猪油10克鸡油10克。', '主料：干银耳15克。 \r\n配料：鸡茸100克，油菜叶150克。');
INSERT INTO `recipe` VALUES (5672, 431, '芙蓉鹿尾', '1.用清水将鹿尾洗净，除去血污和杂物，撕去毛，放入开水 锅中氽一遍捞出将葱切段，姜拍碎；将母鸡从腹部开膛， 掏去内脏，用清水洗净。 \r\n\r\n2.锅中注入 2.5公斤清水，放母鸡、猪肉、火腿和鹿尾，上 火烧开，撇去浮沫移至微火炖1小时左右，待鹿尾煮至七 成烂时，捞出鹿尾(将尾骨折出)，放入碗中，加清汤、料 酒、精盐、酱油、葱姜，上屉蒸20分钟，取出，滗净原汁。 \r\n\r\n3.将鸡蛋清加150克清汤调匀，盛入瓷钵，上笼蒸熟后，铲 入深海碗中，将煮好的鹿尾去骨改刀成段，放在蛋清上。 起锅，注入200克清汤，加精盐、料酒、酱油和水淀粉， 勾二流芡，倒入深海碗中即可上席。', NULL, NULL, '料酒25克，精盐1.5克，酱油10克，葱50克，姜25克，清汤 350克，鸡油少许干玉米粉15克，面粉少许。', '主料：鲜鹿尾1个。 \r\n配料：母鸡1只，猪肉500克，熟火腿100克，鸡蛋清150克。');
INSERT INTO `recipe` VALUES (5673, 431, '干煸牛肉丝', '1.将牛肉切2寸长丝，用淀粉、酱油浆过。将笋尖切寸半长丝。 将干辣椒、葱姜均切丝。 \r\n\r\n2.炒锅上火加油，下牛肉丝划透，捞出，再炸至枣红色，待 水分大部出尽时倒出。 \r\n\r\n3.锅留油，放干辣椒煸炒出香味，下葱姜丝煸炒，再放笋丝 炒透后，下牛肉丝翻匀，烹酒，加酱油、白糖，炒至吐油时， 撒入胡椒粉，略拌出锅', NULL, NULL, '干辣椒5克，葱、姜各5克，精盐、绍酒、白糖、淀粉、酱油、 胡椒粉、油各适量。', '主料：牛肉250克。 \r\n配料：冬笋尖100克。');
INSERT INTO `recipe` VALUES (5674, 431, '黑米膳粥', '将黑米洗净，放入锅中，加入清水，上火烧开，撇去沫子， 改用微火熬两个小时即成。', NULL, NULL, NULL, '黑米');
INSERT INTO `recipe` VALUES (5675, 431, '口蘑鹿肉', '1.将鹿肉改刀成1寸2分见方的块，用热油炸成枣红色。 \r\n\r\n2.锅中放入母鸡、火腿，注入清水，上火烧开，撇净浮沫，下 鹿肉、葱姜、精盐、绍酒炖熟，去鸡、火腿不用，鹿肉装盘。 \r\n\r\n3.起锅，注入上汤，上火烧开，放口蘑煨透，加精盐、绍酒、 胡椒粉、糖色在水粉勾稠芡，淋油，浇于鹿肉上即可。', NULL, NULL, '葱段、姜片、精盐、绍酒、胡椒粉适量，葱油、水粉、糖色各 少许，上汤150克。', '主料：鲜鹿肉1公斤。 \r\n配料：水发口蘑150克，母鸡1公斤，火腿100克。');
INSERT INTO `recipe` VALUES (5676, 431, '红烧鹿筋', '1.用水将鹿蹄筋洗净，放入开水锅中氽一遍捞出。将干贝、 大海米放碗中，注入开水浸泡20分钟。将香菇、玉兰片、 火腿均切成长8分的象眼薄片。将油菜心洗干净。 \r\n\r\n2.锅中注入2公斤清水，放入鹿筋，加入干贝、大海米及原 汤，以及母鸡、猪肉，上火烧开，撇尽浮沫，移至微火上 焖两小时左右，至七成烂时捞出鹿筋，切成2寸长、2分宽 的长条片。 \r\n\r\n3.汤锅注入200克清汤，加入料酒、精盐，上火烧开，撇去 浮沫，放入香菇片玉兰片、火腿片、油菜心和鹿筋片，在 火上烧2分钟，放入用水调稀的玉米粉勾成芡汁，淋上鸡油， 即可出锅。', NULL, NULL, '料酒20克，精盐1.5克，酱油15克，清汤200克，湿玉米粉10 克，鸡油10克。', '主料：鲜鹿蹄筋4根。 \r\n配料：干贝50克，大海米50克，母鸡1只，熟火腿25克，猪肉500克，水发香菇25克水发玉兰片25克，油菜心1根。');
INSERT INTO `recipe` VALUES (5677, 431, '蝴蝶海参', '1.锅中注入清水，上火烧开，放入海参，氽两遍捞出， 片去内外表皮，做成蝴蝶翅膀，放在盘中，向翅膀中 间撒一些干玉米粉，共做12个。 \r\n\r\n2.将鱼肉洗净，砸成细鱼茸，挑净筋、刺，放入碗中， 加入料酒、精盐、清汤、玉米粉各少许，加入鸡油， 搅拌上劲。将鸡蛋清汤倒入大盘中，在筷子抽起成雪 白泡沫状，倒入鱼茸碗中，拌匀成鱼茸糊。 \r\n\r\n3.用手将鱼茸糊挤成小长条形丸子，放在海参上撒玉米 粉的部位，成蝴蝶身子。用两根翅针插入头部成须子， 真豆做眼睛，黄瓜皮、火腿切长4分的细丝，码在蝴 蝶身上成花纹。共做成12个蝴蝶海参，上屉蒸4分钟 取出，放入大海碗中。汤锅上火，注入1公斤清汤， 开锅后撇去浮沫，放入料酒、精盐对好口味，倒中大海碗中即成。', NULL, NULL, '料酒15克，精盐2克，干玉米粉10克，鸡蛋清3个，鸡油 10克，清汤1公斤。', '主料：水发海参750克，鱼肉200克。 \r\n配料：干翅针24根，青豆24粒，黄瓜皮、熟火腿各2.5克。');
INSERT INTO `recipe` VALUES (5678, 431, '鸡油香菇', '1.将香菇放入盆中，注入清水，浸泡1小时左右，剪去根部， 用清水冲洗数遍把泥沙洗干净。用刀将玉兰片刻成长1寸5 分、宽4分厚1分的麦穗形花刀片共刻4片。 \r\n\r\n2.坐煸锅，注入熟猪油，随即注入150克清汤和精盐、料酒各少许，放入香菇在微火上煨3分钟左右，待香菇糯软时，倒入漏勺中，滗净汤汁。 \r\n\r\n3.锅中注入150克清汤，加入料酒、精盐，放入玉兰片和香菇， 上火烧开。用水调稀玉米粉，倒入锅中勾芡汁，淋上鸡油， 即可出锅。', NULL, NULL, '料酒10克，精盐1.5克，湿玉米粉10克，葱姜末共2.5克，清 汤300克，熟猪油25克，鸡油10克。', '主料：香菇125克。 \r\n配料：水发玉兰片25克。');
INSERT INTO `recipe` VALUES (5679, 431, '金屋藏娇', '1.将燕窝放入瓷体，添上汤，上笼蒸20分钟。起锅上火，加入上汤，放入洗净的水竹荪，加精盐、炒酒煨3分钟。 \r\n\r\n2.将燕窝滗去汤后瓤入竹荪身中，上笼蒸3分钟后装盘。 \r\n\r\n3.起锅上火，加入上汤，调入精盐、绍酒，勾明芡，浇于竹荪上即可。', NULL, NULL, '上汤500克，精盐、绍酒、水淀粉、鸡油各少许。', '主料：水燕窝50克。 \r\n配料：水竹荪身10条。');
INSERT INTO `recipe` VALUES (5680, 431, '金银鸽肉', '1.从脖下开刀，将鸽子宰杀，放净血后放入盆中，注入热水 (温度适宜)，浸泡4分钟，将羽毛煺下，洗净，从腹部开膛， 掏出五脏，剁去爪子，用水洗净。用水将土豆洗净，削去 皮，切成丝。 \r\n\r\n2.坐煸锅，注入25克花生油，放入25克葱、13克姜、大料略 炒一下，随即注入1公斤清汤，加入15克料酒、少许精盐、 酱油、五香料，入放一只命子，开锅后撇去浮沫，在微火 上炖至九成烂时捞出。 \r\n\r\n3.坐煸锅，注入500克花生油，烧至七成热时，下入土豆丝， 炸成黄松时捞出控净油，横码在盘中成一条。 \r\n\r\n4.将另一只鸽子下入汤锅中煮熟捞出，剔出骨，切成长2寸、 宽4分的长条，皮朝下摆在碗中，肉、翅膀放肉上，加150 克清汤、料酒、精盐、葱段、姜，上屉蒸烂取出，滗净汤 汁，挑出葱姜，摆放在土豆松一侧，头、翅膀摆在肉的旁 边。 \r\n\r\n5.坐煸锅，注入花生油，烧至七成热，下入炖烂的鸽子，炸 成枣红色，捞出控净油，切成长2寸、宽4分的条，摆放在 土豆丝的另一侧，头、翅膀摆在肉的旁边。 \r\n\r\n6.锅中注入蒸鸽肉的原汤汁，放入料酒、精盐，调好口味， 用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在 蒸烂的鸽肉上，即成金银鸽肉造型。', NULL, NULL, '料酒30克，精盐2克，酱油15克，葱段50克，姜25克，清汤 1.5公斤，鸡油15克，花生油500克(约耗50克)，桂皮、大料、五香料各少许。', '主料：鸽子两只。 \r\n配料：土豆250克。');
INSERT INTO `recipe` VALUES (5681, 431, '口蘑鹿肉', '1.将鹿肉改刀成1寸2分见方的块，用热油炸成枣红色。 \r\n\r\n2.锅中放入母鸡、火腿，注入清水，上火烧开，撇净浮沫，下 鹿肉、葱姜、精盐、绍酒炖熟，去鸡、火腿不用，鹿肉装盘。 \r\n\r\n3.起锅，注入上汤，上火烧开，放口蘑煨透，加精盐、绍酒、 胡椒粉、糖色在水粉勾稠芡，淋油，浇于鹿肉上即可。', NULL, NULL, '葱段、姜片、精盐、绍酒、胡椒粉适量，葱油、水粉、糖色各 少许，上汤150克。', '主料：鲜鹿肉1公斤。 \r\n配料：水发口蘑150克，母鸡1公斤，火腿100克。');
INSERT INTO `recipe` VALUES (5682, 431, '莲花卷', '将发面对好碱，放入白糖揉匀，饧10分钟后揪成15个面 剂，分别擀成厚2分的圆片，在面片上刷一层香油， 然后对折叠成直角扇面形，切成宽度相等的3块(其中最小的三角块为等腰三角形)，并按大小依次摞在一起码齐， 最小的一块放在最上面。用一根筷子在上面压成一个十 字，使其重叠的边角向上翻，呈莲花形状，饧10分钟， 上屉蒸8分钟即成。', NULL, NULL, NULL, '发面300克，白糖30克，香油5克。');
INSERT INTO `recipe` VALUES (5683, 431, '罗汉大虾', '1.将对虾分为两段。将头部去沙包，并去肠肚，用熟猪油煸 一下，至虾变为红色时，加入料酒、精盐、白糖、清汤、 葱姜丝，在微火上浇3至5分钟，待汤汁发浓时即可出锅。 \r\n\r\n2.用蛋清将鱼茸搅匀，加入料酒、精盐、玉米粉拌成鱼茸糊。 \r\n\r\n3.将对虾尾部去皮，留下尾巴。把虾片开，剁断虾筋，把鱼 茸糊抹在虾片上点些火腿末、油菜末及1棵豆苗，上屉蒸 10分钟后取出。 \r\n\r\n4.用花生油将油菜叶炸成松，摆在鱼盘中间，一面放虾头， 一面放虾尾。另用清汤、料酒、玉米粉，精盐勾稀汁，淋 上鸡油，浇在虾尾上。将烧虾头的汁上火熬至浓稠时，放 入蒜段，浇在虾头上即可。', NULL, NULL, '料酒30克，精盐2.5克，白糖20克，湿玉米粉25克，面粉5克， 花生油500克(约耗25克)，鸡油10克，熟猪油50克，葱、姜各25克。', '主料：对虾12个。 \r\n配料：鱼茸100克，鸡蛋清1个，豆苗20克，油菜末5克，火腿末5克， 油菜叶250克，青蒜段10克，清汤250克。');
INSERT INTO `recipe` VALUES (5684, 431, '麦穗虾卷', '1.将对虾剥去皮，去头和尾，挑出沙线，用清水洗净。将一 块猪肉皮(面朝下)放在菜墩上，将虾肉放在肉皮上，用刀 背砸成虾茸，放入碗中，加入1克精盐、料酒、玉米粉搅拌 上劲。将2个鸡蛋ke在盘中，挑出蛋黄，用筷子将蛋清抽起呈雪白泡沫状，倒入虾茸中搅拌成虾茸糊，再分成四等分。 \r\n\r\n2.将2个鸡蛋ke入碗中打散，加入0.5克精盐和少许玉米粉拌匀。坐油锅，用油刷子将锅内刷一层油，烧至五成热，倒入鸡蛋液，吊成两张大薄片的鸡蛋皮，用刀将每张蛋皮切两半。将面粉放入碗中，加入适量清水，调成稀 糊。 \r\n\r\n3.将半张蛋皮的边上抹少许面粉糊，放上一份虾茸糊，做成细长条形，然后用蛋皮卷起，呈宽6分、厚3分的长条蛋卷， 共做成4卷，放盘中，上屉蒸熟取出晾凉，切成长1寸的小 斜片，码在圆圆盘中呈麦穗形即可。', NULL, NULL, '料酒10克，精盐1.5克，鸡蛋4个，干玉米粉10克，面粉25克。', '对虾6个。');
INSERT INTO `recipe` VALUES (5685, 431, '琵琶大虾', '1.将对虾去头、皮肠子，留下尾巴，片开，剁断虾筋，挤干水分， 撒些精盐先两面蘸干玉米粉，再放在鸡蛋液中蘸一下，最后将背面蘸上面包渣，码在盘子里。 \r\n\r\n2.用蛋清、玉米粉、精盐、花生油将虾茸拌成糊，抹在虾上，在糊面中间放一根火腿丝，两旁各放一根黄瓜皮丝，外面再各放一根火腿丝，然后用筷子按一遍，即成琵琶大虾。 \r\n\r\n3.坐煸锅，注入花生油，烧至六成热，放入大虾，用温油炸熟。盘中放好生菜叶，把大虾剁成两段，对齐码成圆圈即可。', NULL, NULL, '料酒10克，干玉米粉50克，精盐少许，花生油1公斤(约耗75克)。', '主料：对虾12个。 \r\n配料：虾茸100克，鸡蛋4个，火腿150克，黄瓜皮100克，生菜叶100克， 面包渣100克。');
INSERT INTO `recipe` VALUES (5686, 431, '茸鸡待哺', '1.将鸡蛋黄放在容器中，用尺板打散，起油锅置中火上，待油温三成热时，徐徐倒入鸡蛋液，同时用长筷子顺着一个方向快速搅拌炸约5分钟，即可捞出，挤出炸油，即成鸡蛋茸。 \r\n\r\n2.将酥皮面卷成卷，用手揪成12个面剂，压成面皮，包入豆沙馅， 用手捏成葫芦形，将上头捏出鸡嘴，嘴两边粘上一粒黑芝麻， 放进烤炉烤熟，在鸡身上刷上糖水，裹上鸡蛋茸，即成小茸鸡。', NULL, NULL, NULL, '酥皮面160克，豆沙馅100克，鸡蛋黄150克，黑芝麻、糖水少许。');
INSERT INTO `recipe` VALUES (5687, 431, '三色糕', '1.将芸豆面搓匀，分成两份，分别用刀拍成同样大小的长方块， 中间抹上豆沙馅，摞在一起。 \r\n\r\n2.将金糕切成同芸豆长方块一样大小的长方块，放在芸豆块上面，切成小块码在盘中即可。', NULL, NULL, NULL, '芸豆面(熟)300克，豆沙馅100克，金糕150克。');
INSERT INTO `recipe` VALUES (5688, 431, '三鲜鸭舌', '1.用清水将鸭舌洗净，放入锅中，注入清水，上火煮熟，捞出后放入清水中过凉，除去软骨，用刀切成两半。 \r\n\r\n2.汤锅中注入开水，放入香菇、玉兰片氽一遍捞出，同火腿一起切成小菱形片。 \r\n\r\n3.锅中注入熟猪油，下入葱姜末略炸一下，随即注入清汤，加入料酒、精盐酱油、放入鸭舌、香菇片、玉兰片、火腿片，上火烧2分钟左右。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上 鸡油，即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，酱油10克，葱、姜末各少许，清汤150克， 湿玉米粉10克，鸡油10克，熟猪油25克。', '主料：鸭舌150克。 \r\n配料：水发玉兰片25克，水发香菇15克，熟火腿25克。');
INSERT INTO `recipe` VALUES (5689, 431, '双色马蹄糕', '1.用2250克热水将奶粉冲开，晾温后放入450克马蹄粉和900克白糖xie开，过罗去掉杂质。 \r\n\r\n2.用2250克热水将可可粉冲开，晾温后放入450克马蹄粉900克白糖xie开，过罗去掉杂质。 \r\n\r\n3.将奶粉汤放入铝盆中，将铝盆放热水中，用抽子搅拌至稠，倒入方模中，放进蒸箱，蒸至八成熟取出；再将可可粉汤用以上方法调稠，倒入奶粉糕上，放入蒸箱，蒸熟即成双色马蹄糕。', NULL, NULL, NULL, '马蹄粉900克，白糖1800克，可可粉50克，奶粉100克。');
INSERT INTO `recipe` VALUES (5690, 431, '随滑飞龙', '1.将飞龙皮、毛撕掉，从腹部开膛，掏去五脏，用清水洗净，取下飞龙脯片成薄片，加入炒酒、精盐、玉米粉各少许，倒入鸡蛋清，拌匀浆好，放入开水锅中滑熟捞出。 \r\n\r\n2.用清水将水发香菇、水发玉兰片洗净，切成长6分的梭形薄片，放入开水锅中氽一遍捞出。 \r\n\r\n3.煸锅留底油，放入香菇、玉兰片略加煸炒，再放入清汤、料酒、精盐、胡椒粉，开锅后撇去浮沫。用水将玉米粉调稀，倒入锅中勾成汁，再倒入飞龙片，翻炒均匀，淋上鸡油即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清1个，清汤100克，湿玉米粉15克， 胡椒粉1克鸡油10克，清汤50克。', '主料：飞龙两只。 \r\n配料：水发香菇40克，水发玉兰片40克。');
INSERT INTO `recipe` VALUES (5691, 431, '糖醋荷藕', '将藕去皮切片，开水氽透，凉水过凉后滤干水，装入瓷钵中。将糖、醋精、桂花酱调匀，浇淋于藕上拌匀后，腌制24小时即可装盘食用。', NULL, NULL, '白糖100克，醋精5克，桂花酱25克。', '鲜藕500克。');
INSERT INTO `recipe` VALUES (5692, 431, '芜爆山鸡', '1.将山鸡脯去筋，片成凤羽片，加蛋清、精盐、淀粉上浆。 将香菜洗净去根切寸段。将葱、姜切细丝。 \r\n\r\n2.炒锅加油，上火烧热，下山鸡片略爆，倒入漏勺控油。将精盐、绍酒、胡椒粉、淀粉、上汤对成汁待用。 \r\n\r\n3.起锅上火，下入山鸡片，加葱姜丝煸炒，再烹入对好的汁翻炒，下入香菜拌匀出锅。', NULL, NULL, '葱、姜、绍酒、淀粉、胡椒粉各适量，鸡蛋清25克。', '主料：净山鸡脯肉250克。 \r\n配料：香菜25克。');
INSERT INTO `recipe` VALUES (5693, 431, '芜爆鲜贝', '1.将青瓜切3分方叮将香菇去根、洗净、切寸段。将葱姜切细丝。 \r\n\r\n2.将鲜贝放容器内，加蛋清、绍酒、精盐、淀粉搅拌上浆。 \r\n\r\n3.将精盐、绍酒、胡椒粉、上汤、淀粉对成汁。 \r\n\r\n4.炒锅上火，加油烧热，下鲜贝略爆一下，再下入青瓜丁略滑，倒入漏勺腔油。再将鲜贝倒入锅中，倒入对成的汁速炒，放香菜翻炒即可出锅。', NULL, NULL, '精盐、绍酒、胡椒粉、淀粉、葱、姜各适量。', '主料：鲜贝250克。 \r\n配料：青瓜50克，香菜25克，鸡蛋清25克。');
INSERT INTO `recipe` VALUES (5694, 431, '喜鹊登枝', '1.用清水将牛肉洗去血污，放入锅中，注入清水，上火烧开，撇 去浮沫，煮1小时左右至五成烂时捞出，滗净水，晾凉后切成 宽3分、厚1分的小长条片。 \r\n\r\n2.将发菜放入碗中，注入温水浸泡10分钟，用清水洗净。油锅上火，注入花生油，烧至七成热，下入发菜炸一遍，倒入漏勺控 净油，放在盆中，注入开水洗净油质，捞出后，放碗中，加入 精盐、香油拌匀。用水将胡萝卜洗净，用小刀刻成喜鹊的嘴和 爪。用小刀将蛋黄糕刻成一个直径2分的小薄圆片；将松花蛋清 刻成一个直径3分的小薄圆片；将金糕刻成25个圆薄片，每片直径约为2分。 \r\n\r\n3.将蟹肉挑出皮，放在盆里上火蒸10分钟后晾凉，放在大圆盘中央码成喜鹊身子；用发菜码在往上翘的四条尾翅和身上的翅膀， 摆嘴和爪子。拼摆喜鹊眼睛时，先放上蛋黄糕圆片，再把松花蛋清圆片摞在上面，做成眼睛。在喜鹊的下面用牛肉片码成树枝形状，用刻好的金糕圆片在树枝上码成五朵梅花即成。', NULL, NULL, '精盐1克，香油10克，卤汤1公斤，花生油500克(约耗10克)。', '主料：上等蟹肉400克。 \r\n配料：发菜25克，牛肉30克，胡萝卜一根，金糕50克，蛋黄糕、松花蛋的蛋清各10克。');
INSERT INTO `recipe` VALUES (5695, 431, '杏仁豆腐', '1.将杏仁去皮，加入250克清水，磨成浆。 \r\n\r\n2.将冻粉放容器内，用清水泡开洗净，再放入锅中，加入500克清水，上火化开，晾到不烫手的时候，倒入杏仁浆中一起搅匀， 分别盛在小碗里晾凉。 \r\n\r\n3.将白糖放锅中，加入1公斤清水，上火烧开，晾凉后，倒入盛杏仁冻的小碗里，上面放上半粒樱桃即可。', NULL, NULL, NULL, '杏仁100克，冻粉15克，白糖750克，樱桃10个。');
INSERT INTO `recipe` VALUES (5696, 431, '燕尾桃花', '1.将虾顺背开三刀，去虾线，用蛋清、水淀粉上浆。 \r\n\r\n2.炒锅上火，加油，下虾划透，沥净油。 \r\n\r\n3.锅留底油，下干辣椒丝、葱姜丝煸炒出香味，再下虾，烹酒， 加精盐、水淀粉少许，淋辣椒油，即可出锅。', NULL, NULL, NULL, '主料：带尾去皮虾250克。\r\n 配料：鸡蛋清1个，葱姜丝各5克，干辣椒丝5克，绍酒、水淀粉、精 \\盐、辣椒油各少许。');
INSERT INTO `recipe` VALUES (5697, 431, '鹦鹉莴笋', '1.用小刀将黄瓜皮刻成鹦鹉的嘴和爪子；用剪刀将香菇剪成直径3分的圆片，将蛋黄糕剪成直径2分的圆片，将蛋黄糕片放在香菇片上做鹦鹉眼睛；将金糕切成长2寸、厚1分、一头尖， 一头宽3分的片做鹦鹉尾巴。 \r\n\r\n2.将莴笋剥去叶，削去外皮、筋和老根，顺长切成宽4分的长条，用开水煮熟放入凉水中过凉，再放入盆中，加精盐、香油拌匀，腌10分钟后，切成长1寸的片，码在圆盘中做鹦鹉身子， 用桔子瓣错开码成鹦鹉脖子，然后镶上嘴爪、眼用金糕交错地码成尾巴即成。', NULL, NULL, '精盐0.5克，花椒油10克。', '主料：莴笋750克。 \r\n配料：金糕50克，油焖香菇2个，蛋黄糕5克，罐头桔子50克，黄瓜皮50克。');
INSERT INTO `recipe` VALUES (5698, 431, '油攒大虾', '1.将对虾去皮、头、肠，留下尾巴，片开，剁断虾筋，加少许料酒。精盐稍煨，两面蘸些面粉。把蛋清抽起，放入玉米粉 和面粉拌匀。将虾蘸满蛋清糊，用微火温油炸熟(不要上色)，切成两半，码在盘中。 \r\n\r\n2.将玉兰片、香菇、火腿切成三角块。锅中注入清汤，加入剩余的料酒、精盐、白糖，对好口味，上火烧开，撇去浮沫， 放入香菇、玉兰片、火腿，加入用水调稀的玉米粉勾成芡汁，淋上鸡油，浇在大虾上即可。', NULL, NULL, '白糖15克，料酒15克，鸡油10克，熟猪油150克，精盐少许， 面粉25克，干玉米粉25克。', '主料：对虾12个。 \r\n配料：清汤250克，蛋清4个，水发香菇25克，水发玉兰片25克，火腿50克，豆苗两棵。');
INSERT INTO `recipe` VALUES (5699, 431, '御龙火锅', '1.用水将带皮五花肉洗净，刮去细毛。用水将香菜洗净，切去根， 切成2分长的段。用刀将水发香菇、水发黄蘑菇切成长1寸5分、 宽6分、厚1分的片。将粉丝放入盆中，注入开水，浸泡10分钟， 涨发好后捞出，剪成长约4寸的丝。用水将酸菜洗净，切去根， 切成长1寸5分、宽8分的片。将大海米放碗中，注入开水浸泡 20分钟。 \r\n\r\n2.锅中注入清水，放入猪肉，上火烧开，撇去浮沫，在火上煮至六成烂时捞出，控净水，稍凉后切成长2寸、厚1分的大薄片。 \r\n\r\n3.火锅中先放入粉丝和酸菜片，然后将猪肉片、干贝、大海米、黄蘑菇片、火腿片、香菇片间隔顺序地码在酸菜粉丝上，注入清汤，加入料酒、精盐对好口味，用炭火烧开，烧10分钟。 \r\n\r\n4.将酱豆腐放入小碗内，用凉开水调成卤状。将香菜末放入小盘内。将韭菜花、卤虾油、芝麻油、辣椒油分别放入小碗中， 同白肉火锅一起上桌。', NULL, NULL, '料酒40克，精盐', '主料：带皮五花猪肉1公斤。 \r\n配料：大海米150克，水发干贝150克，粉丝150克，酸菜400克，水发黄蘑菇150克，熟火腿150克，水发香菇150克。');
INSERT INTO `recipe` VALUES (5700, 431, '鸳鸯酥盒', '1.将面粉用细罗筛过，分别制成水油皮面和油酥面。将两种面各分成15份，用水油皮面包上油酥面，揉圆后再擀长，卷成筒状， 轻轻压扁，再擀长。然后从长的一端卷起至剩下8分时，将未卷上的一端擀成薄片，用刀从中间切开，再分向左右拉长，贴在 圆卷的两端，再用刀从圆圈中间切两半，然后用手压成两个圆剂将有层次的一面扣在下面，擀成圆皮，共做30个。 \r\n\r\n2.将豆沙馅和桂花糖馅各分成15份，分别包入擀好的圆皮中呈饺状。将一个豆沙馅饺和一个桂花糖馅饺的馅肚对在一起，馅边相搭，用手捏牢，呈带有裂缝的圆盒状，再将圆边捏成花边，即成生酥盒，用温花生油炸至金黄色捞出即可。', NULL, NULL, NULL, '面粉150克，熟猪油50克，豆沙馅75克，桂花糖馅75克，花生油 750克(红耗40克)。');
INSERT INTO `recipe` VALUES (5701, 431, '芝麻锅炸', '1.将鸡蛋ke在小盆中，放入面粉、75克玉米粉、200克清水，搅拌成鸡蛋稀糊将25克玉米粉撒在大圆盘中。坐汤锅，注入50克清水，烧开，下入鸡蛋稀糊，用水炒熟，倒在撒有玉米粉的圆盘上，用刀拍成厚2分的大片，切成边长1寸的菱形片。 \r\n\r\n2.坐煸锅，注入花生油，烧至七成热，下入熟鸡蛋糊片，炸至金黄色时捞出控净油，摆在小圆盘中即成锅炸。 \r\n\r\n3.坐炒锅，烧热，放入芝麻炒熟，倒在案板上擀成末，放入碗中，加入白糖拌匀，撒在锅炸上即可。', NULL, NULL, '白糖50克，干玉米粉100克，面粉50克，花生油500克(约耗 30克)。', '鸡蛋3个，生芝麻50克。');
INSERT INTO `recipe` VALUES (5702, 431, '抓炒大虾', '1.将对虾仁改刀成片，用湿玉米粉糊裹匀，下入油锅炸二三分钟，呈焦黄时即可。 \r\n\r\n2.把酱油、料酒、糖、醋、葱姜末放在碗中调匀。锅上旺火，倒入熟猪油，油热后将调好的汁倒入锅内，炒到汁成糊状后，即将炸好的虾倒入翻炒即可。', NULL, NULL, '湿玉米粉100克，料酒10克，酱油10克，醋15克，糖25克，熟猪 油25克，花生油100克，葱、姜、精盐各少许。', '对虾仁200克。');
INSERT INTO `recipe` VALUES (5703, 432, '拌鱼肚', '将水鱼肚漂净洗透，片成大抹刀片。将香菇片成片。将鱼肚、香菇加调料拌匀即可装盘。', NULL, NULL, '花椒油、精盐、绍酒各少许。', '主料：水鱼肚150克。 \r\n配料：水冬菇25克。');
INSERT INTO `recipe` VALUES (5704, 432, '鲍王闹府', '1.用水将水发紫鲍洗净，撕去毛边，用刀将紫鲍上下打成斜、直花刀，再顺长切成两半。锅中注入清水，上火烧开，将紫鲍下入，氽一遍捞出，控干水 \r\n\r\n2.坐煸锅，注入熟猪油，烧至五成热，放入葱姜末炝锅，再下入麻酱煸炒，随即放入料酒、精盐、清汤、白糖，略炒片刻，炒至成浆糊状时，下入紫鲍翻炒均匀，淋上鸡油，即可出锅。', NULL, NULL, '麻酱40克，料酒10克，精盐1.5克，白糖少许，熟猪油25克， 清汤40克，香油5克，葱姜末5克', '水发紫鲍200克。');
INSERT INTO `recipe` VALUES (5705, 432, '豆沙糕', '将芸豆面(熟)分成两份，分别用刀拍成同样大小的面片，中间抹上豆沙馅，摞在一起，上面放上用绿色素拌成的白糖， 铺匀压平，用刀切成小块，码在盘中即可。', NULL, NULL, NULL, '芸豆面(熟)300克，豆沙馅150克。');
INSERT INTO `recipe` VALUES (5706, 432, '豆沙凉糕', '1.将江米洗净，加入250克清水，放蒸箱中蒸20分钟，取出晾凉。 \r\n\r\n2.将晾好的江米用湿布揉匀，分成两份，摊成同样大小的长方块，中间抹上豆沙馅，摞在一起，上面码上果料即成。', NULL, NULL, NULL, '江米150克，豆沙100克。');
INSERT INTO `recipe` VALUES (5707, 432, '凤脯珍珠', '1.用水将鸡脯肉洗净，片去皮筋，放在猪肉皮上，用刀背砸 成茸，挑去细筋放入碗中，加料酒、精盐、清汤各少许，加 入10克玉米粉，将鸡茸xie开，再放入鸡蛋清搅拌均匀，调成 稀鸡茸糊。 \r\n\r\n2.锅中注入开水，放入洗净的鲜豌豆，上火氽一遍，捞出后放入清水中过凉将干贝洗净，用手搓碎，同豌豆一起放入碗 中。 \r\n\r\n3.坐煸锅，注入熟猪油，烧至五成热，迅速将鸡茸糊透过漏勺下入油内，炸成豌豆大小的白色圆球(如有块大的，可用手勺敲碎)，捞出，控净油。 \r\n\r\n4.锅中注入500克清汤，加剩余的料酒、精盐，对好口味，开锅后撇去浮沫水淀粉勾成稀芡，放入鸡茸球、干贝、鲜豌豆， 搅拌均匀，淋上鸡油，倒入小海碗中即成。', NULL, NULL, '料酒15克，精盐2克，清汤500克，湿玉米粉35克，熟猪油500克(约耗40克)，鸡油10克。', '主料：鸡脯肉750克，水发干贝50克。 \r\n配料：鲜豌豆50克，鸡蛋清5个。');
INSERT INTO `recipe` VALUES (5708, 432, '凤凰出世', '1.用刀将嫩冬瓜刮去表皮，洗净，在距冬瓜根部1寸左右的 地方，用小刀刻成齿轮形花刀，并将根部顺着花刀切下， 掏出冬瓜瓤。锅中注入清水，上火烧开，放入冬瓜氽一遍 捞出，放清水中过凉，再放入大海碗中。 \r\n\r\n2.将鸽子宰杀，放净血，放入盆中，注入热水(温度适宜)， 浸泡5分钟后将羽毛煺下，再从腹部开膛，掏出五脏，洗净， 剁下鸽子脖子(连头)。 \r\n\r\n3.将鸡脯肉、香菇、干贝、火腿均剁成细末，与拍碎的中 蹄一起放入碗中，加料酒、精盐、玉米粉、葱姜末、香油拌 匀成馅，填入鸽肚中。把鸽子放入碗中注入250克清汤，加 入料酒、精盐，上屉蒸八成烂取出。 \r\n\r\n4.将鸽子头放入碗中，注入100克清汤，加料酒、精盐、鸡 油，上屉蒸15分钟取出。 \r\n\r\n5.将蒸熟的鸽子(包括头、脖子)，尾部朝下，放入嫩冬瓜中，盖严冬瓜盖，冬瓜外面注入原汤，上屉蒸15分钟取出。将蒸好的冬瓜，盖朝上，换入另一个海碗中，打开盖，将鸽子头和脖子朝上弄成弓形，插入鸽子身内一点，再扣上盖。 \r\n\r\n6.锅中注入原汤，上火烧开，撇去浮沫，加入料酒、精盐各少许，对好口味顺碗边倒入海碗中，点上几滴鸡油即可。 \r\n\r\n4.锅中注入500克清汤，加剩余的料酒、精盐，对好口味，开锅后撇去浮沫水淀粉勾成稀芡，放入鸡茸球、干贝、鲜豌豆，搅拌均匀，淋上鸡油，倒入小海碗中即成。', NULL, NULL, '料酒20克，精盐1.5克，湿玉米粉10克，葱姜末少许，清汤500克，香油少许鸡油10克。', '主料：小嫩冬瓜1个(约重750克)，乳鸽1只(约重250克)。 \r\n配料：水发香菇25克，水发干贝50克，鸡脯肉50克，熟火腿25克， 马蹄4个。');
INSERT INTO `recipe` VALUES (5709, 432, '凤戏牡丹', '1.用水将鸡脯肉洗干净，片去皮、筋，再片成长条片，放入碗 中，加入精盐料酒各少许，拌匀后，放入鸡蛋清、玉米粉搅拌 浆好。 \r\n\r\n2.用清水将海蜇头洗净，切成10块。用花形刀将黄瓜皮、香菇、火腿切成厚1分的花形圆片。 \r\n\r\n3.坐锅上火，注入开水，将海蜇头块氽熟捞出，再将鸡片滑开氽熟捞出，然后再下入香菇片、火腿片氽一下捞出。将鸡片放入海碗中，将海蜇头块放在鸡片中间成一堆，四周撒上 香菇片、火腿片和黄瓜片。 \r\n\r\n4.锅中注入清汤，加入料酒、精盐，对好口味，上火烧开，撇去浮沫，顺海碗边倒入碗中即可。', NULL, NULL, '料酒10克，精盐1.5克，鸡蛋清1个，湿玉米粉5克，清汤300克。', '主料：鸡脯肉150克，海蜇头1个。 \r\n配料：黄瓜皮、水发香菇、熟火腿各50克。');
INSERT INTO `recipe` VALUES (5710, 432, '凤眼秋波', '1.锅中注入清水，放入鸽蛋，在火上煮熟捞出，剥去皮，用水洗净。先将鸽蛋的大头片去一点，使其能立住，再将鸽蛋上半部切下，使蛋黄露出。 \r\n\r\n2.将鸡茸放入碗中，加入料酒、精盐、玉米粉、鸡油，搅拌上劲，再ke入鸡蛋清搅拌成糊。 \r\n\r\n3.将面包切成长1寸2分、宽6分、厚1分的菱形片，共切12片。在每片面包上抹满鸡茸糊，再将一个鸽蛋按上，四围用刀抹齐。 \r\n\r\n4.坐煸锅，注入500克花生油，烧至六成热，放入鸽蛋，炸熟成金黄色即成。', NULL, NULL, '料酒10克，精盐1.5克，玉米粉5克，鸡油10克，花生油500克(约耗40克)。', '主料：鸽蛋12个，面包100克。 \r\n配料：鱼茸50克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (5711, 432, '干烧冬笋', '1.将冬笋去根部，片成厚4分的大片，锲蓑衣形花刀，再改刀 成长1寸，宽4分的条。将胡萝卜洗净去皮，同香菇、马蹄一起切成筷子头大小方丁，即成“三侗。锅中注入清水，上火烧开，下入三丁，用开水氽一遍捞出。 \r\n\r\n2.坐煸锅，注入花生油，烧至六成热，下入冬笋，炸熟捞出。锅中留下少许底油，放入葱姜末略炸，下入三丁煸炒，再下入冬笋，加入料酒、豆瓣酱、精盐白糖、酱油、清汤，在微火烧至清汤快完时，淋上鸡油即成。', NULL, NULL, '料酒10克，精盐1.5克，豆瓣酱5克，白糖10克，酱油少许，清汤200克，鸡油10克，花生油250克(约耗20克)，葱姜末少许。', '主料：冬笋200克。 \r\n配料：水发香菇50克，马蹄4个，胡萝卜25克。');
INSERT INTO `recipe` VALUES (5712, 432, '桂花海蜇', '1.将海蜇皮放清水中洗干净，刷净泥沙，切成长3寸、宽1寸的长方块，然后叠起一半，成为长1寸5分的长方块，在叠起的块上，每隔2分宽竖切一刀，顶端留2分的距离不要切断， 如此连切四刀，成佛手形。 \r\n\r\n2.锅中注入清水，上火烧开，放入海蜇氽熟，捞出，放上咸水泡1小时，放入清水中冲凉，滗净水，整齐地码在盆中，加入桂花酱、白糖、醋精、香油，腌24小时左右即可食用。', NULL, NULL, '桂花酱15克，白糖75克，醋精20克，香油25克。', '海蜇皮250克。');
INSERT INTO `recipe` VALUES (5713, 432, '荷塘莲香', '1.将桂花加入清水化开，成桂花水。将玉米面、豆腐粉、白糖放在一起拌匀加入桂花水，和成面团。 \r\n\r\n2.将面团揪成小剂(每两10个)，放在手掌心，点一点桂花水，做成窝头形状然后放蒸箱蒸15分钟即成。 \r\n\r\n3.用冻粉、鸡蛋丝等原料做成莲蓬图案，将小窝头当做莲子，放在莲蓬中，即成荷塘莲香造型。', NULL, NULL, NULL, '玉米面175克，豆腐粉75克，白糖100克，熟蛋黄50克，桂花2.5克， 苏打少许。');
INSERT INTO `recipe` VALUES (5714, 432, '红豆膳粥', '将红小豆用清水熬烂。将铜锅注入清水，上火烧开，放入洗净 的小站米和熬好的红小豆，烧开，在微火上熬30分钟左右，熬至米烂汁稠即成。', NULL, NULL, NULL, '红小豆、小站米');
INSERT INTO `recipe` VALUES (5715, 432, '红烧鱼骨', '1.用水将鱼骨洗净泥沙，放入盆中，注入开水，浸泡2小时左右，原盆上屉蒸30分钟取出。将涨发好的鱼骨捞出，没有涨发好的，可继续上屉蒸，至完全涨发好后，再放入盆中，加满清水，入冰箱10小时左右，使其继续涨发，然后取出，改刀成长1寸、宽6分的长方块。 \r\n\r\n2.用水将油菜心洗净，抽去老筋。锅中注入清水，上火烧开，将油菜心氽熟捞出。 \r\n\r\n3.锅中注入清汤，加入料酒、精盐、酱油，上火烧开，撇去浮沫，放入鱼骨在微火上烧两三分钟，放入油菜心。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，翻炒均匀，即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，酱油10克，清汤150克，湿玉米粉10克，鸡油10克。', '主料：鱼骨150克。 \r\n配料：油菜心两棵。');
INSERT INTO `recipe` VALUES (5716, 432, '花篮白菜', '1.将洋白菜剥去外皮、老帮，切去根部，再切成四瓣。将姜刮去皮，用水洗净，切成细丝。将干辣椒放温水中泡10分钟，捞出备用。 \r\n\r\n2.锅中注入清水，上火烧开，下入洋白菜叶氽一下，捞出控净水，放入盒中将姜丝、泡辣椒(留下两个)放在洋白菜上。另起锅，注入适量清水，加入醋精白糖熬化后倒在洋白菜上。 \r\n\r\n3.锅中注入香油，上火烧至七成热，下入两个泡辣椒炸焦捞出，将热油倒在盆中的泡辣椒上，用盘子盖严，腌10小时左右，取出，改刀切成1寸5分长的条，放在圆盘中，码成花篮形状。将泡辣椒切成小圆圈，点缀在花篮上即成。', NULL, NULL, '白糖100克，醋精20克，干辣椒15克，姜15克，香油50克。', '洋白菜1棵(约500克)。');
INSERT INTO `recipe` VALUES (5717, 432, '豢蝶大虾', '1.用剪刀剪去对虾的须、爪，挑去背上的沙线，用清水冲洗干净。 \r\n\r\n2.锅中注入750克清水，放入料酒、精盐、葱姜段、小茴香，上火烧开，放入对虾煮10分钟左右，然后连汤倒入盆中晾凉。 \r\n\r\n3.将晾凉的对虾捞出，录去头、皮，片成两半，码在圆盘中成蝴蝶翅膀。用小刀将一部分黄瓜条刻成花纹做蝴蝶身纹，放在翅膀中间；另将剩余的黄瓜条切出两根长3寸、宽1分的细长条做须子，码在蝴蝶头部。将樱桃切成两半放在须子前面做眼睛即可。', NULL, NULL, '料酒10克，精盐1克，黄瓜条50克，葱、姜段各25克，小茴香 2.5克，樱桃1粒。', '对虾6个。');
INSERT INTO `recipe` VALUES (5718, 432, '黄金解', '1.用沸水100克，将面粉烫成面团，放入蒸箱中蒸15分钟取出。 \r\n\r\n2.将熟蛋黄和黄油加入面团中揉匀搓透，揪成24个面剂。 \r\n\r\n3.用手掌将面剂按成面片，包入鸡粒熟馅，放入油锅，炸成金黄色即可。', NULL, NULL, NULL, '面粉150克，蛋黄(熟)225克，黄油15克，鸡粒熟馅150克。');
INSERT INTO `recipe` VALUES (5719, 432, '鸡丝汤面', '1.在面粉中加入鸡蛋液、50克清水和匀，盖上湿布饧10分钟，用擀面棍擀成厚1分的长方形薄片，叠起，顶刀切成面条。将熟鸡脯肉切成细丝。用水将豆苗叶洗净。 \r\n\r\n2.锅中注入清水，上火烧开，放入面条，煮熟捞出，分别放入小碗中，上面放入鸡丝和豆苗。另起锅，注入清汤，上火烧开，撇去浮沫，加入料酒、精盐，对好口味，倒入面条碗中即可。', NULL, NULL, '精盐1.5克,料酒5克。', '面粉250克，鸡蛋2个，熟鸡脯肉250克，豆苗汁少许，清汤750克。');
INSERT INTO `recipe` VALUES (5720, 432, '金蟾拜月', '1.将鲍鱼的毛边从其缺口处各撕开一半(切不可撕断)，用刀在上下壳接合处片开三分之二。 \r\n\r\n2.锅中注入清水，上火烧开，将香菇、玉兰片、鱼肚分别氽一遍，捞出，用清水冲凉，然后同火腿一起切成长1寸的丝，即成“四丝”。 \r\n\r\n3.把鸡蛋清倒入盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放入碗中，加入料酒、精盐、清汤、熟猪油、玉米粉各少许，搅拌上劲，再放入蛋清，拌匀成糊 \r\n\r\n4.用小刀将鱼茸糊抹入鲍鱼开口处内(周围要抹齐)，用豌豆按在鱼茸糊的两侧做眼睛，中间点缀些发菜，放入盘中，将撕开的毛边放在前面做爪，即成蛤蟆(金蟾)，按此方法做12个，上屉蒸熟取出。 \r\n\r\n5.将150克清汤注入汤锅中，放入四丝、料酒、精盐，在微火上煨2分钟捞出控净水，撒在盘中，把蒸好的蛤蟆鲍鱼嘴朝外码在丝上呈圆形。 \r\n\r\n6.锅中注入250克清汤，加入料酒、精盐，上火烧开，撇去浮沫。用水将玉米粉调稀，倒入锅中，勾成芡汁，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清1个，清汤300克，湿玉米粉25克，鸡油10克，熟猪油10克。', '主料：原汁桶鲍鱼12个。 \r\n配料：鱼茸50克，水发香菇30克，水发玉兰片25克，熟火腿25克，水 发鱼肚40克，鲜豌豆24粒，发菜5克。');
INSERT INTO `recipe` VALUES (5721, 432, '金鸡独立', '1.将鸡蛋黄和鸡蛋清分别ke入两个碗中打各少许和50克清水拌匀，分别倒入有油纸的平底方铁模中，上屉蒸熟，取出，扣在盘中，撒去油纸，晾凉，即蛋黄糕和蛋白糕。 \r\n\r\n2.将海带放入大盆中，注入温水，浸泡2小时，待涨起时用清水洗净泥沙，放入锅内，注入清水，上火煮30分钟，待软烂时捞出滗出水，切成五条一头宽6分、一头尖、长1尺的弯形片，再切5条长8寸、一头宽4分，一头尖的片。将切好的海带片放入盆中，加入料酒、精盐、香油各少许拌匀，腌十分钟。 \r\n\r\n3.用清水将黄瓜洗净，切成2寸长的段，片下黄瓜皮，切成一头宽3分、一头尖的片，放入盆中，加入精盐，腌10分钟，控净水，加入香油搅拌均匀。 \r\n\r\n4.将蛋黄糕、蛋白糕、金糕均切成一头宽2分，一头尖、长2寸的片；用剩余的金糕刻成一个直径3分的圆薄片和一个鸡冠；用剩余的蛋黄糕刻成一个直径2分的圆片，摞在金糕片上做“鸡”的眼睛。 \r\n\r\n5.从酱鸭子背部劈开，折去骨头。将一只酱鸭皮分别切成三片一头宽4分、一头尖、长3寸的片和若干片一头宽3分、一头尖、长2寸片。共余的鸭皮、鸭肉均切成长1寸5分的丝。用刀将一些蛋白糕、黄瓜皮、海带、酱鸭肉的碎料剁成末。将胡萝卜刮去表皮，洗净，刻成“鸡嘴”、“鸡爪”。 \r\n\r\n6.取一个1尺5寸的大圆盘，中间放入酱鸭丝码成的“鸡身”和“鸡脖子”，用剁好的碎末码成鸡头，用弯形大海带条码成鸡尾；再用其余各种各色的小片从鸡的尾部开始交错地码 在鸡身和脖子上，成五彩公鸡造型；用长3寸的海带条和长3寸的鸭皮片码成鸡翅膀，放在已码好的鸡身上；在鸡头上码好嘴、冠和眼睛，在鸡身下码上鸡爪即成。', NULL, NULL, '料酒10克，精盐2.5克，香油15克，湿玉米粉10克。', '主料：酱鸭子一只半(约重2.5公斤)。 \r\n配料：金糕50克，海带200克，黄瓜500克，鸡蛋12个，胡萝卜1根(粗的)。');
INSERT INTO `recipe` VALUES (5722, 432, '金狮绣球', '1.用清水将干贝洗净，放入碗中，注入开水浸泡2小时，然后上屉蒸两小时左右取出，洗去干贝内细沙粒，手将干贝搓成丝，放在盘内。用水将菜心洗净在根部剞上花刀。 \r\n\r\n2.将鸡蛋清放盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放碗中，加入料酒精盐、鸡油各少许，搅拌上劲，倒入蛋清和5克玉米粉，搅拌成鱼茸糊。用剪刀将香菇剪成长1寸2分、宽1分的长条，共32条。\r\n\r\n3.用手将鱼茸糊挤成直径5分的丸子，共挤16个，放在干贝丝上，使鱼丸子粘满干贝丝，放另一盘中，用香菇条在上面码成十字形，在十字形交叉处和四端抹上点鱼茸糊，再点缀些火腿末和发菜即成金狮绣球，上屉蒸10分钟。 \r\n\r\n4.锅中注入少许鸡油，上火烧热，放入菜心煸炒一下，注入200克清汤，加入料酒、精盐各少许，火上煨2分钟捞出，滗净汤，根部朝中，码入盘中，把金狮绣球码在上面。 \r\n\r\n5.锅中注入清汤，加入剩余的料酒、精盐，上火烧开，撇去浮沫。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清1个，清汤350克，湿玉米粉15克，鸡油40克。', '主料：干贝150克，鱼茸50克。 \r\n配料：水发香菇100克，油菜心8棵，熟火腿末、发菜各25克。');
INSERT INTO `recipe` VALUES (5723, 432, '龙凤双锤', '1.用刀剔去鸡脯肉、净草鱼肉的筋、皮，分别剁成细末，放入两个碗中，分别加料酒、精盐各少许，搅拌上劲，再各加入1个鸡蛋清，拌成鸡肉馅和鱼肉馅。马蹄和水发玉兰片分别剁成末；将马蹄末加入鸡肉馅拌匀分成六份；将玉兰片末加入鱼肉馅拌匀， 分成六份。将鸡小腿骨入锅，注入清水，上火煮10分钟捞出，用小刀刮净骨上的肉。 \r\n\r\n2.将网油放在菜墩上，用刀背将厚的地方砸平，切成长3寸的正方形片6个，在每片猪网油上放一份鸡肉馅，做成长1寸2分、一 头大、一头小形状，在小头放上一根鸡小腿骨，然后四面包起， 成鸡腿形(鸡小腿骨露出网油外一半) \r\n\r\n3.将油皮切成长3寸的正方形片，共切6片。每片油皮上放入一份鱼肉馅，做成长1寸2分、一头大，一头小的形状，在小头上放上一根鸡小腿骨，然后四面包起，做成鸡腿形(鸡小腿骨露出油皮一半)。 \r\n\r\n4.将1个鸡蛋磕入碗中打散，放入玉米粉、料酒、精盐各少许，拌匀成鸡蛋糊将两个鸡蛋ke入盘中，挑出蛋黄，用筷子将蛋清抽起，呈雪白泡沫，然后加入少许玉米粉，拌成蛋泡糊。 \r\n\r\n5.坐煸锅，注入花生油，烧至五成热，将猪网油鸡腿先沾一层面粉，再裹满蛋泡糊，下锅，用温油炸熟(不要上色)，捞出控净油。待油烧至六成熟时，将油皮鸡腿裹上一层鸡蛋糊，下锅炸成金黄色捞出，控净油。炸好龙凤双锤整齐地码在圆盘中即可。', NULL, NULL, '料酒20克，精盐1.5克，玉米粉30克，面粉10克，花生油1公斤(约耗40克)。', '主料：鸡脯肉100克，净鱼肉100克。 \r\n配料：猪网油1张，油皮3张，鸡小腿骨12个，鸡蛋5个，马蹄3个，水发玉兰片40克');
INSERT INTO `recipe` VALUES (5724, 432, '奶油菠萝冻', '1.将冻粉加250克清水，上火化开，加入白糖，熬至拉粘。 \r\n\r\n2.用蛋抽子将鸡蛋清打起，慢慢冲入冻粉液，加入黄油，用蛋抽子不停地搅动，待稍凉后倒入盏盒中，上面放入切碎的菠萝块，晾凉取出即可。', NULL, NULL, NULL, '菠萝150克，冻粉12.5克，白糖250克，鸡蛋清2个，黄油40克。');
INSERT INTO `recipe` VALUES (5725, 432, '清炒鳝丝', '将鳝鱼肉切成丝，加精盐、绍酒煨制。将葱姜切细丝。炒锅加油上火，将鳝丝倒入，滑至将熟时倒出。原锅留底油，加葱姜丝，煸炒出香味，下鳝丝，烹绍酒，调入精盐、胡椒粉，翻炒出锅即成。', NULL, NULL, '葱、姜、精盐、绍酒、胡椒粉各适量。', '净鳝鱼肉250克。');
INSERT INTO `recipe` VALUES (5726, 432, '如意乌龙', '1.锅中注入清水，上火烧开，放入海参，氽一遍捞出，放入清水中洗净。将火腿切成和海参一样长短的、宽1分、厚1分的长条， 共切4条。 \r\n\r\n2.将鱼茸放入碗中，加入料酒、精盐、鸡油、玉米粉各少许，搅拌上劲。将鸡蛋清放入盘中，用筷子抽起呈雪白泡沫状，放入鱼茸中，搅拌成鱼茸糊。 \r\n\r\n3.坐煸锅，注入30克葱油，放入海参煸炒一下，加入料酒、精盐、酱油、清汤，微火上煨3分钟捞出，控净汤汁，用干布将海参腹中擦干撒些玉米粉，放入盘中。将火腿条放入海参腹中，将鱼茸糊填满海参腹内，将海参放入纱布包起，上屉蒸20分钟左右取出， 即成如意海参。打开纱布，横切成长6分的段，断面朝上，码在盘中即成。 \r\n\r\n4.锅中注入150克清汤，加入料酒、精盐，对好口味，开锅后撇去浮沫。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上葱油，浇在菜上即成。', NULL, NULL, '料酒15克，精盐2克，酱油10克，鸡蛋清2个，清汤250克，湿玉米粉15克，面粉少许，葱油35克。', '主料：水发海参4个(约500克)。 \r\n配料：鱼茸75克，熟火腿50克。');
INSERT INTO `recipe` VALUES (5727, 432, '烧瓤菜花', '1.将鸡茸放碗中，加入料酒、精盐、玉米粉、清汤、鸡油各少许拌匀。将鸡蛋清放在盘内，用筷子抽起，呈雪白泡沫状，然后放入鸡茸中，拌匀成糊。 \r\n\r\n2.用小刀将菜花削去根、皮，用水洗净。锅内注入清水，上火烧开，将菜花放入锅内氽一遍捞出，用凉水过凉后捞出。锅中放熟猪油，下菜花煸炒一下随即放入精盐、料酒、清汤，在微火上煨2分钟后捞出。 \r\n\r\n3.在一平盘内抹少许油，用手将鸡茸挤成直径8分的小丸子放在盘中。在菜花根部沾上一点面粉插入鸡茸丸子中，四周点缀些火腿末、油菜末上屉蒸5分钟取出后放另一圆盘中。 \r\n\r\n4.锅中注入150克清汤，加入料酒、精盐、白糖，上火烧开，撇去浮沫。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在菜花上即成。', NULL, NULL, '料酒10克，精盐2克，白糖少许，鸡蛋清1个，清汤300克，湿玉 米粉10克，面粉少许，鸡油10克，熟火腿末、油菜末各少许， 熟猪油30克。', '主料：菜花250克。 \r\n配料：鸡茸50克。');
INSERT INTO `recipe` VALUES (5728, 432, '酥页层层', '1.在250克面粉中加入鸡蛋、白糖和清水，揉匀搓透，成皮面盖上湿布，饧一下。用手将搅好的猪板油搓透，加250克面粉搓匀放入冰箱中冻到油面挺实，用手还能按得动时，即可拿出。 \r\n\r\n2.将冻好的油面擀成长方片，擀成油面长方片的一半夹在油面的里面。皮面用擀杖在叠好的面片上挨着压紧，擀成长方形叠成三折。按此方法，再叠两遍，擀好后入冰箱冷冻。冻好后取出擀成3毫米厚长方片，用刀切成4方块(共切60块)中间夹上豆沙馅，叠成三角形刷上蛋液，入烤炉烤熟即可。', NULL, NULL, NULL, '面粉500克，猪板油325克，鸡蛋50克，豆沙馅450克，白糖50克。');
INSERT INTO `recipe` VALUES (5729, 432, '万年长青', '1.将油菜去帮，选用带有两三个叶的菜心，斜刀削去菜心的根，剞上十字花刀，用水洗净。把鸡蛋清放入大盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放入碗中，加入清汤、料酒、精盐、玉米粉各少许和鸡油，搅拌上劲，对入鸡蛋清，拌匀 成糊。 \r\n\r\n2.锅中注入清水，上火烧开，用手掐住菜叶尖，将菜帮和根先氽一下，然后将油菜放入锅中氽一下，捞出，放入清水中过凉，捞出，控净水，摆在盘中油菜叶上撒一点玉米粉。用手将鱼茸糊挤成小鱼丸子码在油菜叶上。在鱼丸子上撒些火 腿末，点上少许发菜，上屉蒸3分钟后取出，放入大海碗中。 \r\n\r\n3.汤锅注入清汤，加入料酒、精盐，上火烧开，撇去浮沫，对好口味，将汤顺碗边浇入大海碗中即可。', NULL, NULL, '料酒15克，精盐2.5克，清汤1公斤，鸡油5克，玉米粉2.5克。', '主料：油菜16棵。 \r\n配料：鱼茸50克，熟火腿末5克，发菜2.5克，鸡蛋清2个。');
INSERT INTO `recipe` VALUES (5730, 432, '卧龙戏珠', '1.用水将海参洗净泥沙，竖着切成两半。葱切寸段。锅中注入清水，上火烧开，放入海参，氽一遍捞出。 \r\n\r\n2.锅中注入清水，放入鸽蛋，上火煮熟捞出，剥去外皮，用水洗净。火上坐煸锅，注入花生油，烧至七成热，下鸽蛋，炸成金黄色，倒漏勺中，控净油 \r\n\r\n3.煸锅注入鸡油，坐火上，放入葱段，炸成深黄色时捞出葱段，即成葱油。将一半葱油倒入小碗，煸锅中留一半葱油，起锅上火，下入海参，煸炒一下再加料酒、精盐、酱油、清汤和鸽蛋，在微火上烤3分钟左右，待汁浓，用水将玉米粉调稀，倒入锅中钗成芡汁，淋入小碗中的葱油，即可出锅。', NULL, NULL, '料酒20克，精盐1.5克，酱油15克，清汤150克，葱50克，湿玉 米粉15克，花生油150克(约耗20克)，鸡油40克。', '水发海参250克，鸽蛋6个。');
INSERT INTO `recipe` VALUES (5731, 432, '绣球蛋糕', '1.将鸡蛋磕入小铜锅中，加入白糖，用小打蛋抽子将鸡蛋 液抽起，使体积胀约三倍，然后加入面粉拌匀，即成蛋糕浆。 \r\n\r\n2.将笼屉铺上纸，用小勺将蛋糕浆摊在纸上成直径约2寸的圆片，做15个，上屉蒸5分钟取出，将蒸熟的蛋糕片从纸上 取出。 \r\n\r\n3.将豆沙馅分成15份，分别包入蛋糕皮中，收口处用手捏牢，放在盘中(收口处朝下)呈蓬松多裂纹的球形。将樱桃切成15个小粒，分别放在蛋糕上即可。', NULL, NULL, NULL, '熟原粉70克，白糖75克，鸡蛋2个，豆沙馅120克，樱桃2个。');
INSERT INTO `recipe` VALUES (5732, 432, '一品官燕', '1.将燕菜放入碗中，加入温水浸泡约50分钟，轻轻捞出。用镊子择去燕毛和两头的根，用清水冲两三遍(不要揉搓)，滗净水，将燕菜撕成细条。将食碱放入大碗中，加入适量的 开水冲化，放入燕菜，用筷子慢慢地搅拌均匀，浸发15分钟 左右。待燕菜涨起后，滗去碱水，再用开水冲两三遍以去碱 味，并用干布挤去燕菜的水分。 \r\n\r\n2.将发好的燕菜放在漏勺内。锅中注入350克清汤，上火烧开，放入盛有燕菜的漏勺将燕菜氽一下捞出，控净水，摆在盘内，成圆堆形。将火腿切成长8分的细丝与洗干净的豆苗一起撒在燕菜上面。 \r\n\r\n3.锅中注入250克清汤，上火烧开，撇去浮沫，加入料酒、精盐，对好口味，用水将玉米粉调稀，倒入锅中勾成汁，淋上鸡油，浇在燕菜上即成。', NULL, NULL, '清汤600克，料酒15克，精盐2克，食碱5克，鸡油10克，湿玉米粉15克。', '主料：干燕菜40克。 \r\n配料：熟火腿25克，豆苗叶5克。');
INSERT INTO `recipe` VALUES (5733, 432, '玉板翠带', '1.坐煸锅，注入500克花生油，烧至四成热，将锅离火，放入广肚，用温油浸泡1小时左右捞出，用剪刀剪成长1寸、宽6分长方形片。火上坐大煸锅，注入2公斤花生油，烧至微热，放入广肚片，用手勺翻动，待鱼肚膨胀、出现气泡时，加高油温，继续 浸泡。待鱼肚全部鼓起，将锅离火，加少量凉水，使鱼肚更好地膨胀。如此反复几次，用手勺敲打鱼肚片，响声松脆时即已发透捞出控净油。 \r\n\r\n2.将鱼肚放入大盆中，加入开水，泡软后放一些食碱化开，用以去鱼肚中的油分；如若不净，可用碱水反复洗数次，以洗净为止，再用热水冲洗三四遍，以去碱味。 \r\n\r\n3.将豆苗切去根，用水洗净，掐下叶(梗不用)。在火腿中选用最红的肉，切成长约2寸、宽2分、厚1分的细长条，共切6根。 \r\n\r\n4.将广肚切成长2寸、宽1寸、厚3分的长方形片，放入锅中，用开水氽两遍捞出，控净水。锅中放300克清汤，加入鱼肚片、料酒、精盐各少许，在微火上煨5分钟捞出，控净油，在大圆盘中码成长4寸、宽3寸长方形堆，中间豆苗横码一条6分宽的长条，长条中间放一粒樱桃，豆苗两边用火腿条码齐。 \r\n\r\n5.汤锅中注入400克清汤，加入剩余的料酒、精盐，开锅后撇去乳沫。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在菜上即成。', NULL, NULL, '料酒20克，精盐 2.5克，清汤750克，湿玉米粉15克，鸡油10克， 食碱30克，花生油2公斤(约耗50克)。', '主料：广肚200克，豆苗150克。 \r\n配料：熟火腿50克，樱桃1粒');
INSERT INTO `recipe` VALUES (5734, 432, '鸳鸯哺乳', '1.将5个鸡蛋和5个鸽蛋分别放入锅中，注入清水，上火煮熟，捞出后剥去蛋皮，用刀竖着切成两半，掏出蛋黄，蛋白放入盘中，用干玉米粉少许撒在蛋黄坑内。用刀将马蹄拍碎，再剁成末。 \r\n\r\n2.将瘦猪肉末、虾茸放在盆中，加入马蹄末和料酒、精盐、湿玉米粉、面粉清汤、葱姜末各少许，再加入1个鸡蛋，搅拌上劲成馅。 \r\n\r\n3.用手将馅挤成小丸子，放在鸡蛋、鸽蛋的坑内，用小刀抹平。再将1个鸡蛋入碗中打散，将鸡蛋液抹在馅上。在鸡蛋馅上点两粒青豆做眼睛；在鸽蛋馅上点上两瓣青豆做眼睛。将水发发菜、油菜末、火腿末撒上面，上屉蒸熟取出码在盘中。 \r\n\r\n4.锅中注入200克清汤，加入精盐、料酒、酱油各少许，上火烧开，撇去浮沫用水调稀玉米粉，倒入锅中勾成汁，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒15克，精盐1.5克，酱油10克，葱姜末少许，清汤200克， 鸡油10克，面粉少许，湿玉米粉25克。', '主料：鸡蛋7个，鸽蛋5个。 \r\n配料：瘦猪肉末150克，虾茸100克，马蹄5个，青豆20粒，水发发菜5克，熟火腿末油菜末各 2.5克。');
INSERT INTO `recipe` VALUES (5735, 432, '云片鸽蛋', '1.将水发香菇、水发玉兰片洗净，同火腿一起均切成细丝，即成为“三丝”。 \r\n\r\n2.锅中注入清水，上火烧开，放入三丝氽一遍捞出。另起锅，注入100克清汤加料酒、精盐各少许，放入三丝，在火上煨两分钟捞出，控净汤，撒在圆盘中。 \r\n\r\n3.取12个碗形小酒盅，盅内抹一层鸡油，将鸽蛋分别磕入小酒盅内(不要将蛋黄弄散)将豆苗洗净，放在鸽蛋上，再撒些火腿末上屉蒸5分钟左右取出，用小刀从边上划开，取出鸽蛋，整齐地码在盘中的三丝上。 \r\n\r\n4.锅中注入150克清汤，加入剩余的料酒、精盐，对好口味，用 水将玉米粉调稀，倒入锅中勾成汁，淋上鸡油，浇在菜上即可。', NULL, NULL, '料酒10克，精盐1.5克，湿玉米粉10克，清汤250克，鸡油10克', '主料：鸽蛋12个。 \r\n配料：水发香菇、水发玉兰片、水发鱼肚各50克，豆苗叶25克，熟火腿末2.5克。');
INSERT INTO `recipe` VALUES (5736, 432, '芸豆金鱼', '1.将20克冻粉放入锅中，加250克清水，上火化开，加入50克 白糖和少量绿色素，熬10分钟左右，倒在一个1尺2寸的大盘中晾凉。 \r\n\r\n2.将杏仁(去皮)放入粉碎机中，加入150克清水，磨成杏仁浆。将15克冻粉放入锅中。加入150克清水，上火化开，加入50克 白糖，再煮开，晾温后，倒入杏仁浆，搅拌均匀，倒在平盘中，摊成2厘米厚的杏仁冻备用。 \r\n\r\n3.将切好的芸豆卷在摊好的绿色冻粉冻的大盘中，堆出鱼头和鱼肚的琪状。将金糕切成上短下长的十字形，盖在鱼头和鱼肚 上面。在告近鱼肚末端的金糕条上，用刀刻一个三角口，将三角片揭起，插入一块三角形的杏仁冻做鱼鳍。把杏仁冻切成四瓣鱼尾形，码在鱼肚下端。鱼尾上面码几条金糕。用两粒樱桃做鱼眼码在鱼头上面即成。', NULL, NULL, NULL, '豆沙芸豆卷250克，金糕150克，白糖100克，冻粉25克，杏仁 200克。');
INSERT INTO `recipe` VALUES (5737, 432, '珍珠雪耳', '1.将银耳放入无油的瓷器内，注入温水，浸泡30分钟左右，用清水冲洗数遍去杂物，然后将银耳捞出，用镊子择去根和变色部分。 将姜末放入碗中，注入适量开水，浸泡5分钟，即成姜末水(不用姜末)。 \r\n\r\n2.将鱼茸放碗中，注入75克清水化开，加2克精盐，用手将鱼茸朝一个方向搅拌上劲，加入鸡蛋清和姜末水，朝一个方向搅均匀，即成鱼茸稀糊。用手将鱼茸糊挤成指甲大小的丸子，放入清水锅，上火烧开，待丸子变白时捞出，即成珍珠鱼丸。除留下四五个外， 其余均放入圆盘中，码成一个大圆圈。 \r\n\r\n3.将洗净的油菜心放入开水锅中氽一遍捞出。另起锅，注入250克清汤，放入银耳、油菜心，加料酒、精盐各少许，火上煨3分钟，挑出菜心，将银耳到入漏勺中控净汤。将银耳放入鱼丸子中间，银耳上放两棵油菜心，在每个油菜叶上放一粒珍珠鱼丸。 \r\n\r\n4.锅中注入250克清汤，上火烧开，撇去浮沫，加入剩余的料酒、精盐，用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，浇在珍珠雪耳上即成。', NULL, NULL, '料酒15克，精盐4克，鸡蛋清1个，清汤500克，姜末10克，湿玉米粉15克，鸡油10克。', '主料：干银耳25克，桂鱼茸100克。 \r\n配料：小油菜2棵。');
INSERT INTO `recipe` VALUES (5738, 433, '棒渣膳粥', '将铜锅注入清水，上火烧开，放入细棒渣，烧开，在微火上熬30分钟左右，熬至渣烂汁稠即可。', NULL, NULL, NULL, '细棒渣');
INSERT INTO `recipe` VALUES (5739, 433, '叉烧猪肉', '1.将瘦猪肉切成长约2寸5分、宽2寸、厚1寸5分的块，两面剞上一字花刀，放入葱、姜、绍兴酒、盐腌1小时后，坐 煸锅，注入花生油，烧至七成热，下猪肉块冲炸一下捞出， 控净油。锅中留下少许底油，烧至五成热，下入桂皮、大料、姜段、葱段略炸一下，注入清水，加入料酒、精盐、 酱油、猪肉，上火煮至六成烂时将猪肉捞出。 \r\n\r\n2.坐锅，注入少许清水，放入红曲，在火上煮15分钟左右。将煮好的红曲水滗入另一锅中，加入白糖，放入煮熟的猪 肉，在火上将汁kao浓，淋上香油，即可出锅，倒入盘中晾 凉，切成薄片，放入另一圆盘中，码成圆堆形即可。', NULL, NULL, '料酒10克，精盐2克，酱油25克，白糖50克，葱、姜段各25 克，桂皮2.5克，大料2.5克，红曲10克，香油10克，花生油500克(约耗15克)。', '瘦猪肉350克。');
INSERT INTO `recipe` VALUES (5740, 433, '炒豌豆酱', '1.将猪后腿瘦肉放在菜墩上，用刀片成3分厚的大片，两面剞上花刀，然后切成3分见方的叮 \r\n\r\n2.锅中注入清水，上火烧开，放入鲜豌豆，煮熟捞出，放入清水中过凉。 \r\n\r\n3.坐煸锅，注入熟猪油，烧至五成热，放入猪肉丁煸炒。待肉丁内的水分炒出来时，锅内响声加大随即加入葱 姜末黄酱继续煸炒，至黄酱裹匀肉丁发出酱香味时，加入料酒精盐煸炒均匀，再放入鲜豌豆淋上得油，翻炒均匀，即可出锅。', NULL, NULL, '料酒10克，精盐1克，黄酱10克，葱姜末少许，熟猪油 25克，香油10克。', '主料：猪后腿瘦肉150克。 \r\n配料：鲜青豆100克。');
INSERT INTO `recipe` VALUES (5741, 433, '蛋挞', '1.将白糖放锅中，加入清水，上火化开，晾凉后加入鸡蛋液，搅匀，过细罗即成蛋挞水。 \r\n\r\n2.将起酥面折叠好，擀成面片，用花戳子刻出20个圆片。 \r\n\r\n3.将圆片放在盏中，用手沿着盏盒圆边，捏成碗状，倒入蛋挞水，放水烤炉中烤熟即成。', NULL, NULL, NULL, '起酥面200克，鸡蛋200克，白糖200克，清水250克。');
INSERT INTO `recipe` VALUES (5742, 433, '翡翠鱼丁', '1.用刀将净草鱼肉、马蹄均切成3分见方的叮用清水将菠菜洗净，剁成末用纱布包起，挤出绿汁注入干净的锅中上火烧开，捞出晾凉，即成叶绿素泥。 \r\n\r\n2.将鱼丁放入碗中，加入料酒、精盐各少许，再加入叶绿素泥拌匀，放入鸡蛋清、玉米粉，将鱼丁上浆，成绿色鱼叮 \r\n\r\n3.坐煸锅，注入熟猪油，烧至六成热，下入鱼丁和马蹄丁滑熟，倒入漏勺中控净油。 \r\n\r\n4.锅中留下少许底油，放入葱姜末略炸一下，随即注入清汤，加入料酒、精盐，对好口味用水淀粉勾成芡汁倒入滑熟的鱼丁马蹄丁，翻炒均匀，淋上鸡油，即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，鸡蛋清1个，湿淀粉15克，清汤100克， 熟猪油500克(约耗25克)，鸡油10克，葱姜末少许。', '主料：净鱼肉150克。 \r\n配料：马蹄50克，菠菜100克。');
INSERT INTO `recipe` VALUES (5743, 433, '芙蓉糕', '1.将芸豆面分成两份，分别用手拍成同样大小的长方形片，中间抹上豆沙馅摞在一起。 \r\n\r\n2.将白糖掺点食红，铺在芸豆面片上，铺匀压平，切成小块，码在盘中即可', NULL, NULL, NULL, '芸豆面(熟)300克，豆沙馅100克，白糖100克。');
INSERT INTO `recipe` VALUES (5744, 433, '芙蓉鱼角', '1.将净桂鱼肉片成1寸见方、厚1分的片，共片18片，放入碗中，加入料酒、精盐各少许，腌5分钟。 \r\n\r\n2.将水发香菇、马蹄、干贝、火腿均剁成末，放入碗中，加入料酒、精盐各少许，搅拌成馅分成18份。将豆苗掐下尖 用水洗干净。将大白菜叶洗净 \r\n\r\n3.将鸡蛋清放入大碗中，注入100克清水，加入料酒、精盐各少许，拌匀，上屉蒸熟取出，即成芙蓉羹。 \r\n\r\n4.将鱼片放入盘中，每片中间放上一份馅，然后对角折过一半，即成三角形鱼角，用两个大白菜叶盖上以防鱼角变形， 上屉蒸5分钟取出。将芙蓉羹用小勺舀入碗中，上面放上鱼角，再撒上豆苗。 \r\n\r\n5.锅中注入清汤，加入乘余的料酒、精盐，对好口味，上火烧开，加湿定粉勾成芡汁，淋上鸡油，浇在芙蓉鱼角上即成。', NULL, NULL, '料酒15克，精盐2克，清汤200克，湿玉米粉15克，鸡油10克。', '主料：净桂鱼肉200克。 \r\n配料：水发香菇25克，马蹄三个，水发干贝30克，熟火腿30，豆苗少许，大白菜叶两张，鸡蛋清3个。');
INSERT INTO `recipe` VALUES (5745, 433, '宫保鹌鹑', '1.将鹌鹑脯洗净，控净水，两面剞上花刀，切成3分见方的小方丁，放在碗中加入料酒、精盐、玉米粉、鸡蛋清拌匀上浆。 \r\n\r\n2.坐煸锅，注入花生油，烧至六成热，下入鹌鹑丁，用铁筷子划开，倒入漏勺中，控净油。煸锅中留底油，放入葱姜末豆瓣辣酱、白糖精盐料酒、酱油煸炒至金红色时，倒入鹌鹑丁， 煸炒均匀放入花生米翻炒均匀，即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，白糖15克，豆瓣辣酱15克，鸡蛋清1个， 葱、姜末各少许，酱油5克，玉米粉10克，花生油500克(约耗 40克)。', '主料：鹌鹑脯8个。 \r\n配料：油炸花生米100克。');
INSERT INTO `recipe` VALUES (5746, 433, '桂花鱼条', '1.将净草鱼肉切成长1寸、宽4分、厚4分的长方条，放入碗中，加入料酒、精盐各少许，腌5分钟。 \r\n\r\n2.另用一个碗，放入鸡蛋清、料酒、精盐、玉米粉、面粉各少许，搅拌成稀糊。 \r\n\r\n3.坐煸锅，注入花生油，烧至五成热，将鱼条裹满鸡蛋清糊，下入锅中，用温油炸至浅黄时捞出，控净油。 \r\n\r\n4.锅中注入25克花生油，下入葱姜末略炸一下，随即注入清汤，加入剩余的料酒、精盐和白糖开锅后撇去浮沫， 放入鱼条和桂花酱在微火上煮两三分钟。用水倒入淀粉锅中勾成稀芡淋上鸡油翻炒均匀倒入盘中，再撒上少许 火腿末、黄瓜皮末即成。', NULL, NULL, '料酒10克，精盐1.5克，白糖20克，桂花酱5克，鸡蛋清1个，清汤100克，湿玉米粉15克，面粉少许，葱姜末各少许，鸡油10克，花生油500克(约耗25克)。', '主料：净鱼肉150克。 \r\n配料：熟火腿末、黄瓜皮末各少许。');
INSERT INTO `recipe` VALUES (5747, 433, '胡萝卜酱', '1.将胡萝卜洗净，削去外皮，同豆腐干一起切成3分见方的 叮用温水将海米浸泡20分钟然后洗净泥沙。将猪后腿肉片成3分厚的大片上面剞上十字花刀切成3分见方的叮 \r\n\r\n2.坐煸锅，注入花生油，烧至六成热时，下入胡萝卜丁，炸熟捞出。另坐煸锅，注入熟猪油烧至五成热下入肉丁煸炒。待肉丁内水分炒出来，锅内响声加大即放入葱姜末和黄酱 继续煸炒待黄酱裹匀肉丁并发出酱香味时，加入料酒精盐、豆腐干、海米、胡萝卜丁，翻炒均匀，淋上香油，即可出锅。', NULL, NULL, '料酒10克，精盐1克，黄酱10克，葱姜末少许，熟猪油25克，香油5克，花生油250克(约耗15克)。', '主料：猪后腿肉150克。 \r\n配料：胡萝卜50克，豆腐干1块，海米10克。');
INSERT INTO `recipe` VALUES (5748, 433, '怀胎桂鱼', '1.从鱼鳃处取出五脏，刮去鱼鳞，用开水氽一遍，从头至尾用刀轻轻刮去鱼青(切不可刮破鱼皮)，在鱼身两侧斜剞花刀。 \r\n\r\n2.将3个香菇、4片玉兰片留下，其余同荸荠一起切成丝。将海参切丁，与鱼茸、蛋清料酒精盐、熟猪油拌成糊，从鱼鳃处放入鱼肚中。把少许料酒精盐抹在鱼身上，加上葱姜段，上屉蒸熟。然后滗去鱼汤，葱姜不要。 \r\n\r\n3.把火腿氽一下，码在鱼身上，用清汤加水淀粉、料酒、精盐勾稀汁，浇在鱼上面，再淋上鸡油。将菜心码在鱼两旁，一棵向前，一棵向后即可。', NULL, NULL, '料酒15克，湿淀粉15克，鸡油10克，熟猪油25克，葱、姜各50克，精盐少许。', '主料： 桂鱼1尾(约1公斤)。 \r\n配料：清汤205克，水发香菇、水发玉兰片、水发海参、火腿、鱼茸、荸荠、蛋清各50克，油菜心两棵。');
INSERT INTO `recipe` VALUES (5749, 433, '火炼金身', '1.用尖刀从猪脖子下直捅心脏，放尽猪血，从奶猪一条后腿的外侧用刀划一个小口把火通条插入皮下捅在前腿、腹下和脊背处然后抽出通条，将气打入猪皮内，再用绳捆紧气口，防止漏气。 \r\n\r\n2.坐一大锅，注入清水，烧至80℃左右时，将奶猪下入锅中烫一下捞出，用刀刮去猪毛用清水洗净。然后从腹部开膛掏出内脏再将奶猪腹内洗净，抹上葱段、碎姜、料酒、精盐、五香料、酱油，放大盘中腌一天左右。 \r\n\r\n3.将馒头塞入猪膛内填满，用四根竹条将猪腿捆住，在猪皮上抹匀糖色，放入大烤盘中，入烤炉烤熟。(注意温度适宜，大烤盘内应放少量清水) \r\n\r\n4.将葱白切成2寸长的段，与糖蒜、荷叶饼、甜面酱配成4个小碟，同奶猪一起上桌。客人看过奶猪后再取回用刀将猪 皮片下切成长1寸5分宽1寸的片放在4个圆盘中再上桌即成。', NULL, NULL, '料酒50克，精盐 2.5克，酱油50克，糖色少许，葱200克，姜 50克，糖蒜200克，葱白100克，甜面酱100克，荷叶饼250克， 馒头1公斤，五香料少许。', '活奶猪1只(约5公斤)。');
INSERT INTO `recipe` VALUES (5750, 433, '芥末鸭膀', '1.用水将鸭膀洗净，放入锅中，注入清水，上火烧开，撇去浮沫，在火上煮15分钟左右捞出，放入清水中冲凉后拆去膀骨，切成丝，码入圆盘中。 \r\n\r\n2.将芥末放入碗中，注入适量开水，将芥末沏开，用盘子盖严，放入冰箱，凉后取出，加入精盐、醋、香油，调成芥末汁，浇在鸭膀丝上即可。', NULL, NULL, '精盐1.5克，醋15克，芥末粉25克，香油10克。', '鸭膀20个。');
INSERT INTO `recipe` VALUES (5751, 433, '金钱香菇', '1.将香菇放入盆中，注入温水，浸泡1小时左右，然后换水洗净泥沙，用剪刀剪去香菇根，挑选出12个直径约1寸的香菇圆片。锅中注入清水上火烧开，放入挑出的香菇，氽一遍捞出挤干水分，面朝下放入盘中上面撒些玉米粉 \r\n\r\n2.将鱼茸放入碗中，加入料酒、精盐、玉米粉、鸡油各少许，搅拌上劲。将鸡蛋清放入盘中用筷子抽起呈雪白泡沫状，然后倒入鱼茸中搅拌成糊。将马蹄剁成细末，放入鱼茸糊 中拌匀。 \r\n\r\n3.用剪刀将剩余的香菇剪成长1寸、宽1分的长条，共剪24根；将火腿切成长5分、宽2分、厚1分的薄片，共切48片。将剩余的火腿剁成末。 \r\n\r\n4.用手将鱼茸糊挤成直径约8分的丸子，放在香菇上，将火腿片、香菇条对称地摆在鱼茸丸子上做成古钱形中间放一粒鲜豌豆。在香菇条外侧撒一些火腿末在火腿片的外侧点缀 些发菜即成金钱香菇，然后将做好金钱香菇上屉蒸熟取出，平码在圆盘中。 \r\n\r\n5.锅中注入清汤，加入剩余的料酒、精盐，上火烧开，撇去浮沫，倒入湿淀粉勾成芡汁，淋上鸡油，浇在金钱香菇上即成。', NULL, NULL, '料酒15克，精盐1.5克，清汤200克，湿玉米粉20克，面粉少许，鸡油10克。', '主料：香菇75克。 \r\n配料：鱼茸50克，马蹄3个，熟火腿75克，鲜豌豆12粒，水发发菜少许，鸡蛋清1个。');
INSERT INTO `recipe` VALUES (5752, 433, '金镶玉板', '1.将豆腐切成长1寸2分、宽8分、厚2分的片。将肥瘦猪肉切成长1寸、宽6分厚1分的片。将香菇、玉兰片切成小三角片。用水将菜心洗净。 \r\n\r\n2.坐煸锅，注入花生油，烧至七成热时，下入豆腐片(下锅时，豆腐片不可粘连)，炸成黄色捞出。 \r\n\r\n3.锅内留少许底油，下入猪肉片煸炒一下，放入香菇片、玉兰片煸炒，同时放入料酒、精盐酱油清汤豆腐片、油菜心，在火上煨烧五六分钟，然后加入用水调稀的玉米粉勾成芡汁翻炒均匀淋上鸡油，倒入盘中。将油菜心挑出，放在豆腐上面即成。', NULL, NULL, '清汤250克，料酒5克，精盐1.5克，白糖15克，酱油10克， 花生油500克(约耗40克)，湿玉米粉10克，鸡油10克。', '主料：豆腐1块(约100克)。 \r\n配料：肥瘦猪肉50克，水发香菇25克，水发玉兰片25克，油菜心1棵。');
INSERT INTO `recipe` VALUES (5753, 433, '晶玉海棠', '1.将大虾去皮，去虾屎，洗净，用刀将每个大虾切成两段，再从中间将每段虾片开，粘上玉米粉，用擀面棍头砸成虾皮。 \r\n\r\n2.用虾皮包入虾馅，捏成圆球形状。在每个虾球上面插一根细火腿丝，再贴上两片豆苗，放入蒸箱中，蒸5分钟即成。', NULL, NULL, NULL, '大虾10个，虾馅150克，火腿25克，豆苗少许。');
INSERT INTO `recipe` VALUES (5754, 433, '桔子盏', '1.用清水将冻粉泡发，洗净，放入锅中，加入半桶桔子水，上火化开，加入白糖，再化开，倒入盏盒中。 \r\n\r\n2.每个盏盒中放入半瓣桔子，晾凉后用牙签取出即可。', NULL, NULL, NULL, '冻粉10克，白糖200克，罐桶桔子半桶。');
INSERT INTO `recipe` VALUES (5755, 433, '龙凤呈祥', '1.将口条改刀成龙脊花刀片；将胡萝卜刻成龙头、凤头；将红菜头萝卜刻爪将口条改刀成凤膀羽毛片；将黄蛋糕改刀成羽毛片和龙尾。 \r\n\r\n2.将红曲鸭子去骨拆整肉。将半只鸭子改刀成羽毛片，在盘中码成凤身，用口条、蛋糕、鸭肉羽毛片码成翅膀；将另半只鸭子开条后改刀码成龙身。 \r\n\r\n3.将叉烧肉切片；将大虾去皮片成片。将以上两种片码成凤尾。摆上龙头、凤头、龙爪、龙尾即可。', NULL, NULL, NULL, '主料：红曲鸭子1只，盐水虾4只，叉烧肉100克。 \r\n配料：酱口条、胡萝卜、红菜头适量，黄蛋糕少许。');
INSERT INTO `recipe` VALUES (5756, 433, '梅竹山石', '1.将鸡蛋清倒入大盘中，用筷子抽起，呈雪白泡沫状。将鱼茸放入碗中，加入料酒精盐清汤各少许，搅拌上劲再加入鸡蛋清、玉米粉和少许鸡油，搅拌均匀成鱼茸糊。 \r\n\r\n2.在一个8寸平盘中抹上一层鸡油，将鱼茸糊倒入盘中，摊成圆薄片。用黄瓜皮刻青竹码在薄片上；用香菇码成两个螃蟹；用发菜码成山石；用干贝、火腿做成一棵干枝梅；用竹荪、木耳、青豆、火腿、黄瓜皮做成一只蜻蜓，即成梅竹山石，上屉蒸五分钟，取出，用小刀从边上划开，轻轻放入碗中。 \r\n\r\n3.锅中注入1公斤清汤，上火烧开，撇去浮沫，加入剩余的料酒、精盐，对好口味，顺碗边倒入盛有梅竹山石的碗中。', NULL, NULL, '料酒10克，精盐2.5克，鸡蛋清4个，鸡油15克，干玉米粉10克，清汤1公斤。', '主料：桂鱼茸100克。 \r\n配料：水发香菇、熟火腿、黄瓜皮、水发干贝、水发发菜、水发竹荪、水发木耳、鲜青豆各25克。');
INSERT INTO `recipe` VALUES (5757, 433, '蜜汁山药', '1.将山药刮去外皮，用清水洗净。将鸭梨、苹果剥去皮，切开，除去核，然后切成小叮将金糕瓜仁瓜条青梅均切成2分见方的小叮用水将葡萄干洗净，横切成两半。 \r\n\r\n2.锅中注入清水，上火烧开，放入山药，氽一下捞出，切成长3寸的段。沙锅中放入白糖，注入300克清水，上火烧开放入山药段鸭梨丁苹果丁，移在微火上煨约20分钟当山药糯烂时捞出山药段整齐地码入圆盘中，上面撒上各种果料叮 \r\n\r\n3.将煨山药的糖汁过细罗，注入另一锅中，加入桂花酱，将汁熬浓，浇在山药上即成。', NULL, NULL, '桂花酱10克，白糖150克。', '主料：山药500克。 \r\n配料：鸭梨1个，苹果1个，金糕10克，瓜仁10克，葡萄干10克，瓜条10克，青梅10克。');
INSERT INTO `recipe` VALUES (5758, 433, '藕丝羹', '1.将嫩鲜藕洗净泥土，削去皮，切成1寸5分长的细丝，放入开 水锅中氽一下捞出。将金糕、蜜枣、青梅均切成与藕丝相同的细丝。 \r\n\r\n2.将鸡蛋清倒入碗中，加入相当于蛋清一半份量的水，用筷子打匀，倒在大汤碗中入蒸箱蒸熟，即成白色固体蛋羹。把藕放在蛋羹的两边，中间放金糕、蜜枣、青梅丝。 \r\n\r\n3.锅中注入300克清水，加入白糖，上火烧开，加入湿玉米粉，勾成白色甜汁倒在大汤碗中即成。', NULL, NULL, NULL, '嫩鲜藕500克，鸡蛋清3个，金糕100克，蜜枣100克，青梅100克，白糖200克湿玉米粉25克。');
INSERT INTO `recipe` VALUES (5759, 433, '清蒸鹌鹑', '1.将鹌鹑撕去毛、皮，从腹部掏出五脏，剪去头、翅膀和爪子，再将每只鹌鹑剁成4块，洗净血污和杂物。 \r\n\r\n2.锅中注入清水，上火烧开，将鹌鹑块放入锅中氽一遍捞出，洗净，放入大碗中，注入300克清汤，加入料酒、精盐、鸡油和葱姜段，上屉蒸烂。 \r\n\r\n3.从笼屉取出鹌鹑，滗出汤汁待用。挑去葱姜段，将鹌鹑倒入小海碗中，锅中注入200克清汤和蒸鹌鹑的原汤，加入胡椒粉和剩余的料酒精盐对好口味上火烧开，撇去浮沫，倒入小海碗中即成。', NULL, NULL, '料酒15克，精盐2克，清汤500克，胡椒粉1.5克，葱段25克，姜片25克，鸡油10克。', '鹌鹑6只。');
INSERT INTO `recipe` VALUES (5760, 433, '群虾戏荷', '1.将酥皮面卷成卷，揪成16个面剂。用擀面棍将面剂擀成长条形 面片，包入豆沙馅用手搓成一头尖的长棍形生胚并捏出虾尾。用刀片生胚上切三四道刀口用酥皮面做细长钳粘在生胚上。用面头掺上可可粉揉匀揪两小撮做眼睛粘在生胚上。将一个面剂 擀成长条片用刀切成1公分宽长条在长条两端各切上一刀，中间部分涮上鸡蛋，粘在虾头靠下部位，即成两只虾钳。 \r\n\r\n2.将生胚刷上鸡蛋液，放进烤炉中烤熟，码在盘中，用糖做成荷花、荷叶，放在盘中作陪衬即可。', NULL, NULL, NULL, '酥皮面160克，豆沙馅100克，可可粉2.5克。');
INSERT INTO `recipe` VALUES (5761, 433, '三丝驼峰', '1.将驼峰用温水冲洗干净。锅中注入清水，放入驼峰，上火煮30分钟左右捞出晾凉，切成长2寸、宽2分的丝。 \r\n\r\n2.将葱、姜、香菜洗净，均切成长8分的细丝。将鲜青椒去核，水发玉兰片、水发香菇一起均切成长1寸5分的细丝即成“三丝”。锅中注清水上火烧开放入香菇丝、玉兰片丝氽一遍， 捞出控净水。 \r\n\r\n3.坐煸锅，注入香油，烧热时，下入驼峰丝、葱姜丝煸炒，随即加入胡椒粉料酒、精盐，煸炒约1分钟左右，再放入“三丝”翻炒均匀撒上香菜丝，即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，胡椒粉2克，葱、姜各15克，香油25克。', '主料：驼峰200克。 \r\n配料：水发香菇50克，水发玉兰片25克，鲜青椒50克，香菜10克。');
INSERT INTO `recipe` VALUES (5762, 433, '沙舟踏翠', '1.用小刀在油菜心根部剞上十字花刀，再用清水冲洗干净。水发玉兰片洗净切成长2寸宽8分厚1分的片。将葱、姜洗净。用100克葱切成丝、50克葱切成段。姜拍碎。 \r\n\r\n2.用清水将驼掌洗净泥土，放入清水锅中，上火煮约3小时，捞出，撕去皮、毛，放入温水中洗净。另起锅注入清水，放入净母鸡猪肉火腿，加入葱段姜和20克料酒，放入驼掌上火煮开，撇去浮沫，移至微火，炖至能脱骨时，将驼掌捞出，从掌背处脱去骨头，稍凉后切成大片待用。 \r\n\r\n3.坐煸锅，注入50克鸡油，烧至五成热，下入葱丝煸炒一下，再下入驼掌片炒至呈金黄色时，加入10克料酒精盐酱油各少许和胡椒粉，移中火上煸2分钟左右倒入大盘中晾温后挑出大葱不用。将玉兰片驼掌片按间隔顺序竖码在碗里，加入100克清汤，精盐、料酒、酱油各少许，上屉蒸烂。 \r\n\r\n4.坐煸锅，注入25克鸡油，烧至五成热放入油菜心煸炒一下，再加入200克清汤和少许精盐，烧5分钟左右捞出，控净汤根部朝里码在圆盘中呈圆圈形。取出驼掌，滗净汤汁，扣在油菜上。 \r\n\r\n5.锅中注入200克清汤，加入剩余的料酒、精盐，上火烧开，用湿淀粉勾成芡汁，淋上鸡油，浇在驼掌上即成。', NULL, NULL, '料酒40克，精盐3克，酱油25克，胡椒粉10克，葱150克，姜50克， 鸡油80克湿淀粉10克，清汤500克。', '主料：驼掌2人，油菜心12棵。 \r\n配料：净母鸡1只，肥瘦猪肉1.5公斤，熟火腿250克，水发玉兰片100克。');
INSERT INTO `recipe` VALUES (5763, 433, '松树猴头', '1.将猴头蘑放入盆中，注入温水，浸泡1小时左右，然后换水 洗净泥沙，再注入开水浸泡1小时捞出，用刀削去根部和谱 质部分，用纱布将猴头蘑包起。 \r\n\r\n2.锅中放入净母鸡、瘦猪肉、熟火腿，注入清水(以没过原料 为准)，上火烧开，撇去浮沫再放入包起的猴头蘑，加入30 克料酒葱段姜，在火上炖3小时左右。 \r\n\r\n3.用水将黄瓜洗净，顺长切成二半，然后切成长6分的梳子块。 \r\n\r\n4.将炖好的猴头蘑挑出(不用配料)切成块，放入碗中，注入150克清汤，加入料酒、精盐各少许，上屉蒸20分钟左右取出。 \r\n\r\n5.将酱牛肉切成厚1分的长条薄片，在大圆盘边上码成松树枝干形，把黄瓜梳子块码成树叶形，将猴头蘑滗净汤汁，扣在圆盘中间。 \r\n\r\n6.锅中注入250克清汤，加入剩余的料酒、精盐，对好口味，用水淀粉勾成芡汁，淋上鸡油，浇在猴头蘑上即成。', NULL, NULL, '料酒15克，精盐2.5克，葱段100克，姜50克，清汤400克，湿淀粉10克，鸡油15克。', '主料：干猴头蘑300克。 \r\n配料：净母鸡1只，瘦猪肉500克，熟火腿250克，酱牛肉250克，黄瓜3条(约500克)。');
INSERT INTO `recipe` VALUES (5764, 433, '棠花吐蕊', '1.将酥皮面卷成卷，揪成18个面剂，用擀面棍擀成面皮，包入豆沙馅，上面捏成五角形。用剪刀分别将每个角从上到下挨着剪两刀形成两条,在酥皮中心沾鸡蛋。然后将每个角上边第一条卷向酥皮中心用鸡蛋粘牢。在酥皮中心再放上一点红色面心。 \r\n\r\n2.将酥皮面生胚放入油锅中，用微火慢慢炸熟即成。', NULL, NULL, NULL, '酥皮面250克，豆沙馅150克，鸡蛋1个。');
INSERT INTO `recipe` VALUES (5765, 433, '五丝洋粉', '1.将洋粉放入盆中，注入清水，浸泡10分钟左右，捞出，用清水洗净，与火腿一起切成2寸长的细丝。 \r\n\r\n2.用水将玉兰片、香菇、胡萝卜洗净。用小刀刮去胡萝卜表皮，同玉兰片、香菇均切成2寸长的细丝。 \r\n\r\n3.锅中注入清水，上火烧开，放入玉兰片丝、香菇丝、胡萝卜丝氽一遍，捞出，放入清水中过凉，再捞出，控净水。 \r\n\r\n4.将以上五种丝放入盆中，加入精盐、香油拌匀，码入圆盘中成堆形即成。', NULL, NULL, '精盐1.5克，香油5克。', '主料：洋粉50克。 \r\n配料：圆火腿50克，水发玉兰片50克，水发香菇50克，胡萝卜1根 (约50克)。');
INSERT INTO `recipe` VALUES (5766, 433, '香爆螺盏', '1.将螺肉择净杂物，洗净，片成1分厚的大片，加蛋清、淀粉、精盐上浆。 \r\n\r\n2.将青蒜切寸段、葱姜切细丝，均放入小碗中，加精盐、胡椒粉、水淀粉及少许清汤，调匀成芡汁。 \r\n\r\n3.炒锅上火，加油，烧温后下螺肉片划散倒出，控油后回锅，烹绍酒，淋入碗内芡汁，翻炒均匀出锅。', NULL, NULL, '精盐、绍酒、胡椒粉适量，鸡蛋清1个，淀粉、葱、姜各适量。', '主料：鲜螺肉500克。 \r\n配料：青蒜25克。');
INSERT INTO `recipe` VALUES (5767, 433, '象眼鸽蛋', '1.用刀将面包切成长2寸、宽7分、厚1分的斜象眼片。将虾茸放入碗中，加入料酒、精盐鸡蛋清玉米粉面粉，搅拌均匀成糊。锅中注入清水，放入鸽蛋上火煮熟捞出，剥去外皮，用小刀竖切一刀，分成两半。 \r\n\r\n2.用小刀在面包片上抹满虾茸糊，中间放上半个鸽蛋(蛋黄朝下)按实，再用小刀将多余的虾茸糊抹齐，即成象眼鸽象。 \r\n\r\n3.坐煸锅注入花生油，烧至七成热，下入象眼鸽蛋，炸成金黄色时捞出，控净油，码在盘中即成。', NULL, NULL, '料酒10克，精盐1.5克，鸡蛋清1个，玉米粉、面粉各少许，花生油500克(约耗30克)。', '主料：鸽蛋10个。\r\n配料：面包100克，虾茸50克。');
INSERT INTO `recipe` VALUES (5768, 433, '雪里藏珍', '1.用水将驼掌洗净，放入锅中，注入清水，上火煮红3小时，捞出，撕去皮、毛，再用温水洗数遍。锅中注入清水放入母鸡瘦猪肉火腿和驼掌，上火烧开，撇去浮沫，加葱姜片，料酒10克，移至微火上炖至能脱骨时，将蛇掌捞出从掌背处脱去骨头，去掉驼掌内两个球状筋，切成厚1分的薄片。将葱切成细丝将水发香菇、水发玉兰片洗净，切成长8分、宽4分、厚1分的片。 \r\n\r\n2.坐煸锅，注入鸡油，下入葱丝，炸黄后捞出不用。再下入砣掌片，在微火上煸至两面发黄时，加入料酒、精盐、酱油、胡椒粉、玉兰片和香菇片，煸炒一下，随即注入清汤。用水将玉米粉调稀，倒入锅中勾成芡汁，淋上鸡油，翻炒均匀，倒入盘中。迅速将抽起的蛋清雪糊覆于驼掌上，撒上火腿、油菜末，上笼足汽蒸1分钟即可。', NULL, NULL, '料酒25克，精盐2克，酱油10克，清汤100克，葱100克，姜片25克，鸡蛋清2个，湿淀粉10克，胡椒粉0.5克，鸡油10克。', '主料：驼掌1个。 \r\n配料：净母鸡半只，瘦猪肉250克，熟火腿100克，水发香菇30克，水发玉兰片25克熟火腿末、油菜末各2.5克。');
INSERT INTO `recipe` VALUES (5769, 433, '玉盏龙眼', '1.用清水将冻粉泡发，洗净，放入锅中，加入200克清水，上火化开，加入白糖，再化开，倒入盏盒中。 \r\n\r\n2.每个盏盒中放入一粒龙眼，晾凉后取出即可。', NULL, NULL, NULL, '冻粉10克，白糖200克，罐桶龙眼半桶。');
INSERT INTO `recipe` VALUES (5770, 433, '鸳鸯鱼枣', '1.将净鱼肉片成厚3分的片，切成长1寸、宽3分的长方条，放入碗中，加入料酒、精盐各少许，腌5分钟左右。用水将油菜叶洗净，切成细丝。 \r\n\r\n2.将鸡蛋清放入盘中，用筷子抽起，呈雪白泡沫状，然后加入玉米粉，拌匀成高丽粉，分放在两个碗中，其中一个碗内加入红曲粉，拌匀呈红色。 \r\n\r\n3.坐煸锅，注入250克花生油，烧至七成热，下入油菜丝，炸成深红色时捞出控净油，撒在圆盘中。 \r\n\r\n4.坐煸锅，注入500克花生油，烧至四成热，将鱼条先沾一层面粉，再分成两份：一份裹满高丽糊入煸锅中用温油炸熟捞出，控净油；将另一份鱼条裹满含有红曲粉的高丽糊入 曲锅中用温油炸熟，捞出控净油，在圆盘的油菜松上面分两边码上红、白两色鱼条，即成鸳鸯鱼枣造型。', NULL, NULL, '料酒10克，精盐1克，鸡蛋清3个，干玉米粉20克，面粉少许， 红曲粉少许，花生油500克(约耗30克)。', '主料：净鱼肉200克。 \r\n配料：油菜叶150克。');
INSERT INTO `recipe` VALUES (5771, 433, '芸豆卷', '1.用小磨将芸豆破碎去皮，放在盆里，用开水泡一夜，把未磨掉的豆皮泡起来，再用温水把豆皮泡掉。将芸豆碎瓣放在开水锅里煮，加少许碱，煮熟后用漏勺捞出，用布包好，上屉蒸20分钟，取出过罗，将瓣擦成泥，泥通过罗而形成小细丝。 \r\n\r\n2.将芸豆丝晾凉后，倒在湿布上，隔着布揉和成泥。取1尺5寸 见方的湿白布平铺在案板边上，将芸豆泥搓成1寸粗的条，放湿布中间，用刀面抹成1分厚、5寸长、2寸宽的长方形薄片，然后抹上一层豆沙，顺着湿白布从长的边缘两面卷起，各一半后，合并为一个圆柱形，用双手隔着布轻轻捏一捏，压一压。最后将布拉起，使卷慢慢地滚在案板上，先切去两端不齐的边，再切成六七分长的段，芸豆卷即做成。', NULL, NULL, NULL, '白芸豆500克，豆沙250克，碱少许。');
INSERT INTO `recipe` VALUES (5772, 433, '抓炒里脊', '1.用刀将猪里脊肉片成3分厚的大片，上面剞上十字花刀，切成长1寸的滚刀块放入碗中，加入料酒精盐各少许腌5分钟， 放入30克湿淀粉，将猪里脊肉块裹满糊。 \r\n\r\n2.碗中放入料酒、精盐、白糖、醋、清汤和湿淀粉，调匀即成抓炒汁。 \r\n\r\n3.坐煸锅，注入花生油，浇至七成热，逐块下入猪里脊肉块，炸至金黄色捞出，控净油。 \r\n\r\n4.煸锅留底油，放入葱姜末略炸，随即注入抓炒汁，炒至汁发稠时，放入猪里脊肉块，翻炒均匀，淋上少许热花生油， 即可出锅。', NULL, NULL, '料酒15克，精盐1.5克，白糖25克，醋15克，酱油10克，葱姜 末少许，湿淀粉40克，花生油500克(约耗40克)，清汤100克。', '猪里脊肉150克。');
INSERT INTO `recipe` VALUES (5773, 434, '扣三丝', '1、金针菇洗净，用高汤入味，蛋皮、冬笋切肉丝。 \r\n\r\n2、冬笋丝加盐、料酒、味精、白糖入味，各丝分置碗底，余料填入碗中，然后扣入盘中，淋上味汁即成。', NULL, NULL, NULL, '蛋皮100克、冬笋100克、金针菇100克');
INSERT INTO `recipe` VALUES (5774, 434, '八宝海参', '1、海参、蹄筋治净，切条；\r\n\r\n2、冬笋、火腿、鸡肉切片，荸荠切丁，莲子去芯；\r\n\r\n3、炒锅烧热用葱姜呛锅，下海参稍炒，烹入鸡汤，装碗； 4.将鸡汤、香菇、火腿等原料烩熟，倒在海参上蒸1小时候滗汁码盘； 5.原汁加调料烧开勾芡，浇在海参上即成。\r\n', NULL, NULL, NULL, '水发海参400克，熟火腿、冬笋、熟鸡肉、熟莲子各25克，水发蹄筋50克虾米15克，荸荠50克，水发香菇5克。');
INSERT INTO `recipe` VALUES (5775, 434, '豆皮肉卷', '1、猪肉制茸，山药拍碎，冬菇切丁，再加调料、鸡蛋搅匀制成馅心。 \r\n\r\n2、豆皮发开切成6块，摊上肉茸，再卷成筒状，逐个沾上蛋糊下7成热油锅炸4分钟捞出，再蒸20分钟。 \r\n\r\n3、葱姜呛锅，放入焯过的木耳、腌青菜煸炒装盘，蛋卷也放入菜盘上即成。\r\n', NULL, NULL, NULL, '豆皮2张、猪肥瘦肉、水发冬菇、山药、腌青菜');
INSERT INTO `recipe` VALUES (5776, 434, '橘瓣鱼丸', '1、鱼肉制茸，加调料、荸荠末、鸡蛋清，顺一个方向边搅拌边加水调成糊状； \r\n\r\n2、锅内加清水置小火上，用手将鱼茸挤成橘瓣形，逐个入锅后，移旺火汆１分钟捞出； \r\n\r\n3、鸡汤加香菇片、调料置旺火上，下鱼丸汆诱起锅装碗，撒上葱花、白胡椒粉即可。\r\n', NULL, NULL, NULL, '净鱼肉400克、荸荠末5克、水发香菇50克、鸡汤500克、鸡蛋4个');
INSERT INTO `recipe` VALUES (5777, 434, '文山里脊丁', '猪里脊肉切丁后上浆划油， 下辅料编炒后加调料、 里脊丁， 勾芡炒匀即可。', NULL, NULL, '鲜红椒、葱、精盐、料酒、味精。', '猪里脊肉');
INSERT INTO `recipe` VALUES (5778, 434, '干贝丸子', '1、鱿鱼制茸，加调料搅匀；\r\n\r\n2、冬菇，海米荸荠切成细末放入鱼茸制成鱼茸糊；\r\n\r\n3、干贝泡30分钟沥干，加料酒，葱姜蒸30分钟晾凉装盘；\r\n\r\n4、鱼茸糊挤成圆球, 逐个放在干贝上滚上干贝丝蒸10分钟后装盘；\r\n\r\n5、鸡清汤加味精盐，烧开勾芡，浇在丸子上即成。', NULL, NULL, NULL, '干贝100克，净鱿鱼，水发冬菇，海米，去皮荸荠适量。');
INSERT INTO `recipe` VALUES (5779, 434, '玉带财鱼卷', '1、鱼肉片成30片，火腿、冬菇、玉兰片切丝；\r\n\r\n2、鱼片码盐，两面拍上碘粉，放上火腿丝、冬菇丝、玉兰片丝、姜丝、葱丝卷成卷装盘，上笼蒸5分钟取出；\r\n\r\n3、葱花、姜末呛锅，放鸡汤和调料烧开勾芡，淋在垫有粽叶的鱼卷上，撒上胡椒粉即成。', NULL, NULL, NULL, '带皮净财鱼肉300 克、水发冬菇、水发玉兰片各50克、鸡清汤250克、火腿100克');
INSERT INTO `recipe` VALUES (5780, 434, '针菇鸡丝', '1、鸡肉切丝，加蛋清，盐，淀粉拌匀，金针菇切段下清汤汆后沥干，将调 料调成卤汁。\r\n\r\n2、鸡丝过油。\r\n\r\n3、姜丝，葱段呛锅，放入鸡丝，金针菇煸炒，倒卤汁烧开后起锅装盘即成。', NULL, NULL, NULL, '鸡脯肉250克，金针菇150克，鸡蛋清1个。');
INSERT INTO `recipe` VALUES (5781, 434, '八宝桔盅', '1、桔子洗净，用花刀从蒂部雕开作盖，取出桔瓣成桔盅； \r\n\r\n2、香菇、冬笋、马蹄、火腿、鸡脯肉切丁，莲自去蕊上笼蒸熟，桃仁用开水焯熟去皮掰成细丁，青豆用高汤煮至入味后下6成热油中滑熟； \r\n\r\n3、各料拌匀装入桔盅中，上笼蒸热即成。\r\n', NULL, NULL, NULL, '香菇、马蹄、火腿、鸡脯肉、冬笋、莲子、桃仁、青豆各50克');
INSERT INTO `recipe` VALUES (5782, 434, '茄汁鳜鱼', '1、鱼治净后片成长条剞花刀，加葱结、姜汁、料酒腌30分钟，用干淀粉拍匀，下8成熟热油中炸至金黄，鱼肉呈葡萄粒状时装盘。 \r\n\r\n2、青菜叶焯水后，切成葡萄叶、梗衬在鱼肉两旁。 \r\n\r\n3、番茄汁、白糖下热油锅推匀，浇在鱼上即成。\r\n', NULL, NULL, NULL, '鲜鳜鱼1尾约250克、白糖100克、番茄酱100克、葱结5克、姜片10克');
INSERT INTO `recipe` VALUES (5783, 434, '麒麟鳜鱼', '1、金针菇洗净，用高汤入味，蛋皮、冬笋切肉丝。 \r\n\r\n2、冬笋丝加盐、料酒、味精、白糖入味，各丝分置碗底，余料填入碗中，然后扣入盘中，淋上味汁即成。', NULL, NULL, NULL, '鲜鳜鱼1尾约1000克、香菇片50克、蛋皮片各50克');
INSERT INTO `recipe` VALUES (5784, 54, '青椒豆干牛肉', '1、青椒、红辣椒分别去籽，姜和豆干均洗净、切丝；鸡蛋打入碗中搅匀；牛肉丝放入碗中，加入A料及1/3蛋汁掩拌15分钟。\r\n\r\n2、锅中倒入2杯油烧热，分别放入牛肉丝及豆干丝略烫、捞出、沥油。\r\n\r\n3、锅中放入姜丝及红辣椒丝以小火炒香，加入青椒丝炒匀，再加入牛肉丝、豆干丝及B料，炒至水分快收干时，最后加入C料勾芡，淋上D料即可。\r\n', NULL, NULL, '木瓜粉、米酒各1小匙,淀粉1大匙,水1大匙,鸡精、酱油各1小匙,水淀粉1大匙,香油1/2小匙', '牛肉丝110克,青椒、鸡蛋各1个,豆干2片,红辣椒1个,姜3片');
INSERT INTO `recipe` VALUES (5785, 439, '凉拌墨鱼丝', '1.将墨斗鱼去骨、内脏、眼、墨皮后洗净；\r\n2.葱洗净切段；鲜姜洗净，切成薄片；青、红辣椒切丝备用；\r\n3.往锅里加适量清水，烧开，然后将洗净的墨斗鱼放入沸水中(同时加入料酒、姜片、葱段)。煮透后将墨斗鱼捞出、沥水、晾凉；\r\n4.将晾凉的墨斗鱼切成细丝，放入盘内，加入精盐、白糖、酱油腌10 分钟，最后加入味精、香油以及辣椒丝，拌匀即可食用。', '肉质鲜美，爽嫩可口。', '水煮墨斗鱼时速度要快，以免肉质变硬。', '香油2小匙(6克)；酱油2小匙(6克)；料酒1大匙(30克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '新鲜墨斗鱼500克；红辣椒1个；青辣椒1个；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5786, 439, '毛蛤拌菠菜', '1.将毛蛤煮熟取肉，姜、蒜洗净后切末；\r\n2.菠菜择洗干净，焯水过凉，沥干水后切成段；\r\n3.将毛蛤、菠菜、盐、醋、味精、香油、姜末、蒜末拌匀，装盘即可。', '味道鲜美，清淡爽口。', '调拌时可先拌毛蛤再拌菠菜，然后再一起调拌。', '香油2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '毛蛤250克；菠菜250克；生姜1小块；大蒜4瓣');
INSERT INTO `recipe` VALUES (5787, 439, '油泼螺片', '1.将海螺敲破取肉，用醋洗去黏液，用刀将海螺片成薄片，放入沸水中焯过，装入盘中。香菜洗净切段；\r\n2.先将部分葱、姜和蒜洗净后切成末，用蚝油、酱油调成汁；再将剩余葱和姜切丝备用；\r\n3.将调好的汁倒在螺片上，撒上香菜段和葱、姜丝，浇热油即可。', '肉质鲜嫩，香味浓郁。', '这道菜热吃凉吃都行。海螺肉焯水时一定要快，否则肉质会变老。', '花生油10克；蚝油2小匙(6克)；酱油2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '海螺500克；大蒜3瓣；香葱1棵；生姜1小块；香菜1棵');
INSERT INTO `recipe` VALUES (5788, 439, '爽口鱼片', '1.将泡萝卜、泡椒、辣椒洗净后分别切成丁，均匀地垫在盘底；\r\n2.姜洗净切末后加盐、醋浸泡片刻，与高汤、味精、香油调成姜汁；香菜切段备用；\r\n3.将鱼宰杀洗净，把鱼肉切成片，放入沸水中氽熟，捞出码在已垫底料的盘上，淋上姜汁拌匀，撒上香菜即可。\r\n\r\n注：鲩鱼即草鱼。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '姜汁浓郁，淡雅爽口。', '鱼肉切片后用凉水浸泡一会，口感会更好。', '香油2小匙(6克)；高汤1/2大匙(15克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲩鱼500克；红辣椒1个；泡椒适量；泡萝卜适量；生姜1小块；香菜1棵');
INSERT INTO `recipe` VALUES (5789, 439, '三丝鱼皮', '1.将鱼皮、辣椒洗净切丝；蒜洗净切片；香菜洗净切段；\r\n2.碗内放入鱼皮丝、蒜片、辣椒丝，加入所有调料拌匀，装盘即可。', '色泽美观，口感爽脆。', '购买时要注意辨别，不要买用碱发的鱼皮。碱发的鱼皮颜色光亮，含水量大，弹性小，烧制时容易出水，并且口感不好，有股碱味。', '香油2小匙(6克)；芥末油适量；料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '水发鱼皮400克；红辣椒1个；青辣椒1个；黄辣椒1个；蒜1瓣');
INSERT INTO `recipe` VALUES (5790, 439, '老醋蜇头', '醋香蜇脆，爽口开胃。', '海蜇头拌好后，放进冰箱冷藏后再吃，口味更好。', '厨房小窍门：', '香油2小匙(6克)；料酒1大匙(30克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精；1/2小匙(1.5克)', '海蜇头400克；大蒜3瓣');
INSERT INTO `recipe` VALUES (5791, 439, '虾皮拌豇豆', '1.把豇豆洗净切段，用沸水焯透，沥干水分装盘；葱、姜洗净切末；红、黄辣椒切丝；\r\n2.虾皮洗净，沥干水分，放在豇豆上，再放上葱、姜、调料拌匀，淋香油即成。', '色泽碧绿，鲜香爽口。', '虾皮沥干水后，用油炸一下再拌也可以。', '香油2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '嫩豇豆200克；虾皮50克；红辣椒1个；黄辣椒1个；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5792, 439, '凉拌三鲜', '1.将虾仁洗净切片；辣椒洗净切块；水发海参、熟鸡肉分别切成各种形状的片；\r\n2.把虾片、海参片分别用沸水焯一下，控干水，和鸡肉片及辣椒片一起放在盘中；\r\n3.将所有调料混合调成味汁，浇在虾片、海参片、辣椒片和鸡片上，拌匀即可。', '味道鲜美，清淡爽口。', '采购时不要买用碱发的海参。碱发的海参比水发的海参更油亮，含水量大，弹性小，易碎，烧熟后有股碱味。', '香油2小匙(6克)；酱油2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '水发海参100克；虾仁100克；熟鸡脯肉100克；红辣椒1个；黄辣椒1个');
INSERT INTO `recipe` VALUES (5793, 439, '酱牛肉', '1.将葱洗净后切段，姜去皮拍松；红辣椒洗净切丝；香菜洗净切段；\r\n2.将肉洗净后放入凉水锅中，用大火煮去血水，捞出，沥干水；\r\n3.将肉放入锅中，加热水至没过肉面，放入酱油、黄酱、盐、糖、料酒、葱段、姜块、花椒、大料、桂皮，用大火煮半小时，然后改用小火炖2小时以上；\r\n4.捞出牛腱肉，沥水晾凉后切成薄片装盘，最后撒上辣椒和香菜即可。', '酱香浓郁，味道醇厚。', '切牛腱子肉时应逆着肉丝纤维的方向，这样切出来的肉片吃起来会更嫩。', '酱油1/2大匙(15克)；料酒11/2小匙(16.5克)；黄酱3大匙(90克)；精盐4小匙(12克)；白糖1/2大匙(15克)', '牛腱子肉1000克；红辣椒1个；大葱1根；生姜1块；香菜1棵；花椒适量；大料适量；桂皮适量');
INSERT INTO `recipe` VALUES (5794, 439, '夫妻肺片', '1.香葱洗净切末；将牛肉、牛杂洗净放入沸水中煮净血水后捞起；\r\n2.洗净锅，放入牛肉、牛杂，加入花椒、肉桂、大料、辣椒、精盐、料酒，再加清水，用大火烧开,约30分钟后改用小火煮；\r\n3.煮到牛杂熟而不烂时，先熟的先捞出，晾凉待用；将味精、辣椒油、酱油、花椒粉放入碗中调成味汁；将熟花生米拍碎待用；\r\n4.再将晾凉的牛肉、牛杂切成薄片混合在一起，淋上调好的味汁拌匀装盘，撒上香葱末、芝麻粉、花生仁末即可。', '质嫩鲜美，麻辣香浓。', '调味汁时，调料要一次性放足，以免压味。如果在调味料中加上卤水，味道会更地道。 ', '辣椒油1小匙(3克)；酱油1小匙(3克)；料酒1/2大匙(15克)；花椒粉适量；芝麻粉适量；精盐4小匙(12克)；味精1/2小匙(1.5克)', '牛肉250克；牛杂250克；盐酥花生仁10克；香葱1棵；花椒适量；肉桂适量；大料适量；干辣椒适量');
INSERT INTO `recipe` VALUES (5795, 439, '凉爽肉片', '1.姜洗净，一半切片，另一半去皮剁成末；\r\n2.将猪肉洗净，切成小方块，放入锅中加水，用大火煮开，撇去浮沫，放入姜片、料酒和精盐，改用小火煮，煮至用筷子能戳入肉块即可，捞出，晾凉；\r\n3.将大蒜洗净，捣成蒜泥，放入碗内；\r\n4.将韭菜花洗净，沥水后捣烂，放入装蒜泥的碗内，再加入酱油、酱豆腐汁、豆豉、白糖、辣椒油、香油、味精拌匀待用；\r\n5.将晾凉的肉块上的过多的肥肉去掉，只留2分厚的肥肉;将肉块切成薄片，肥肉朝上码在盘内，淋上调料即可。', '肉片鲜嫩，爽口不腻。', '猪肉可以不用完全煮熟，八成熟最佳。', '香油1/2小匙(1.5克)；辣椒油1/2小匙(1.5克)；酱油2小匙(6克)；料酒1/2大匙(15克)；酱豆腐汁1小匙(3克)；豆豉1小匙(3克)；精盐4小匙(12克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '猪肉(瘦多肥少)500克；韭菜花50克；生姜1小块；大蒜5瓣');
INSERT INTO `recipe` VALUES (5796, 439, '麻辣耳丝', '1.先将猪耳朵刮洗干净，抹上盐，腌3 小时，再放入锅中，大火煮至六七成熟，捞出晾凉；\r\n2.把猪耳朵切成丝，加入盐和味精，用手拌成蓬松状，放入盘内；香葱洗净切丝；干辣椒切碎；\r\n3.把香油倒入锅内，烧至七成热，将花椒和辣椒末倒入，用小火炸3 分钟，再转用中火，待花椒炸出香味时，捞出花椒、辣椒，把香油淋在猪耳丝上，撒上葱丝即可。', '鲜香可口，麻辣开胃。', '如果将猪耳中的脆骨取出，单独凉拌，口感更佳。', '香油2小匙(6克)；精盐4小匙(12克)；味精1/2小匙(1.5克)', '猪耳朵400克；香葱1棵；花椒适量；干辣椒适量');
INSERT INTO `recipe` VALUES (5797, 439, '叉烧肉', '1.葱、姜洗净切末；肉洗净切成长条，放上酱油、盐、料酒、葱、姜腌2 小时；\r\n2.将腌好的肉沥干后，放入油锅炸成枣红色捞出；\r\n3.锅内留少许油，再放入炸好的肉，加入腌制时的汤汁和半碗水，用小火烧半小时左右，加入糖，改用大火，待汁浓时(约小半碗的汁)，将肉盛入盘内，晾凉后，切成薄片装盘，将锅里剩下的汁浇在肉上即可。', '色泽枣红，甜咸醇香。', '切叉烧肉时，一定要逆着肉的纤维方向切，否则肉片不易嚼烂。', '食用油500克；（实耗50克）；酱油2小匙(6克)；料酒3小匙(9克)；精盐1小匙(3克)；白糖1小匙(3克)', '里脊肉500克；香葱1棵；生姜1块');
INSERT INTO `recipe` VALUES (5798, 439, '香椿拌鸡丝', '1.将鸡肉切成细丝，放入沸水中氽熟，捞出备用；\r\n2.将椿芽洗净，放入沸水中烫一下，迅速捞出沥水，切成段；\r\n3.在鸡丝上撒匀椿芽段，再加入精盐、酱油、醋、芝麻酱、香油、味精拌匀即可。', '色泽淡雅，清香味美。', '椿芽焯水后必须马上拨开晾凉，否则椿芽容易变黄。', '香油2小匙(6克)；酱油1小匙(3克)；芝麻酱1小匙(3克)；香醋1/2小匙(1.5克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸡脯肉200克；椿芽100克');
INSERT INTO `recipe` VALUES (5799, 439, '卤香鸡杂', '1.将鸡肫、鸡肝分别用开水烫一下，去除血水；葱洗净切段，姜去皮切片；\r\n2.把鸡肫、大料、葱段、姜片、盐、酱油、白糖放入锅内，加适量清水烧开，煮1 小时，再放入鸡肝煮半小时，改小火，然后浇入香油，烧浓卤汁，约烧10分钟，把鸡杂、卤汁一同倒入盆内晾凉即可。', '卤汁浓香，色泽红亮。', '鸡杂一定要洗干净，否则腥味太重。', '香油2小匙(6克)；酱油2小匙(6克)；精盐4小匙(12克)；白糖1小匙(3克)', '鸡肫200克；鸡肝200克；香葱1棵；生姜1小块；大料适量');
INSERT INTO `recipe` VALUES (5800, 439, '凉拌蛋皮丝', '1.将鸡蛋打入碗中搅拌，加入盐、胡椒粉、酒及淀粉拌匀，放入热油锅中煎成蛋皮，盛起放凉后切丝；\r\n2.蒜苗去头尾，洗净，放沸水中氽烫2 秒钟，迅速捞出，用凉开水冲洗，切细丝；\r\n3.香菇洗净后放入沸水中氽烫至熟，捞出，用凉开水冲凉，沥干水后切丝；榨菜洗净，切丝；\r\n4.最后将蛋皮丝、香菇丝、榨菜丝、蒜苗丝放入盘内，加入适量醋、糖、香油、盐搅拌均匀即可。', '色泽金黄，香嫩可口。', '用料酒将淀粉调匀后再加入蛋汁中，可以避免结块，也不会有蛋腥味。', '食用油30克；香油2小匙(6克)；料酒1小匙(3克)；胡椒粉适量；香醋1/2小匙(1.5克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '鸡蛋2个；榨菜50克；新鲜香菇50克；蒜苗25克；淀粉适量');
INSERT INTO `recipe` VALUES (5801, 439, '三色蒸蛋', '1.将鸡蛋打入碗中搅拌均匀，加入猪油和高汤，充分调匀备用；\r\n2.将皮蛋、熟鸭蛋去壳后切小块，放入打匀的蛋汁中略拌一下，放入蒸锅，用中火蒸20 分钟，取出；\r\n3.待蒸蛋冷却后倒扣出来切薄片，盛在盘中，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '颜色分明，口感极佳。', '蒸蛋时要掌握好火候，以免蒸蛋变硬。', '猪油2小匙(6克)；香油2小匙(6克)；高汤1/2大匙(15克)', '鸡蛋3个；皮蛋3个；熟鸭蛋3个');
INSERT INTO `recipe` VALUES (5802, 439, '珊瑚藕片', '1.将藕洗净削皮，一剖两半，再切成薄片，在凉水中浸泡一会；葱洗净切丝；\r\n2.把藕片放入沸水中烫一下，捞出，用冷水浸凉，沥干水后放入盘内；\r\n3.将2 个干辣椒切丝后与葱丝一并放入藕盘内，再将醋、白糖调匀，待糖溶化后浇在藕片上；\r\n4.往锅里加香油烧热，把剩下的干辣椒切段投入锅内，炸出辣味后浇在藕上，加盖略焖即可。', '酸甜味佳，红白相间。', '最好用鲜藕，不要用袋装藕或罐装藕；藕片用热水反复泡洗后口感更佳。', '香油2/3大匙(20克)；香醋2/3大匙(20克)；白糖11/2大匙(165克)', '莲藕500克；香葱1棵；干辣椒4个');
INSERT INTO `recipe` VALUES (5803, 439, '桂香糯米藕', '1.将糯米洗净后用温水浸泡1小时，沥干备用；红枣洗净；\r\n2.将莲藕刮去外皮，一头连蒂切掉2.5厘米，留做盖子，将糯米填入莲藕内，用筷子捅结实，加蒂盖，用几根牙签固定封口；\r\n3.将酿好的糯米藕放入锅中，加红枣和红糖，注入没过莲藕的清水，大火煮沸后改小火煮30 分钟，捞出糯米藕晾凉；\r\n4.将糯米藕切成片，摆入碟中，浇上桂花蜜，撒上白糖即可。', '补血润肺，香糯适口。', '选购莲藕时，应选藕节肥大粗短、表面鲜嫩、不烂不伤不带尾的。藕有池藕与田藕之分。池藕质白嫩，汁多；田藕品质次之。', '红糖11/2大匙(165克)；桂花蜂蜜1大匙(30克)；白糖3小匙(9克)', '莲藕1节；糯米200克；红枣25克');
INSERT INTO `recipe` VALUES (5804, 439, '素拌什锦', '1.将魔芋去皮洗净后切丝；黄豆芽洗净，沥干水；芹菜洗净切段；竹笋、木耳、胡萝卜分别洗净切丝；金针菇洗净，切去尾端并撕开；干辣椒洗净；\r\n2.将处理好的材料放沸水中烫熟，一起放入盘中，再加入所有的调味料拌匀即可。', '口味清淡，色泽鲜明。', '可以根据个人口味选用不同的素菜调拌。', '香油2小匙(6克)；辣椒油1小匙(3克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '魔芋300克；黄豆芽50克；芹菜25克；竹笋25克；木耳10克；胡萝卜10克；金针菇10克；干辣椒1个');
INSERT INTO `recipe` VALUES (5805, 439, '鲜香紫茄', '1.将茄子洗净，带皮切成块状，放入沸水中氽一下，捞出沥干水；\r\n2.把大蒜洗净捣成茸，放入茄子中，另放入精盐、甜面酱、味精、香油拌匀即可食用。', '鲜嫩爽口，脆咸生香。', '茄子焯水时可在水里加入少量盐、糖，这样既可保持茄子的本色，又可以使茄子更入味。', '香油2小匙(6克)；甜面酱1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '紫皮茄子300克；大蒜4瓣');
INSERT INTO `recipe` VALUES (5806, 439, '蓑衣黄瓜', '1.将黄瓜洗净，切成蓑衣花刀，用盐腌10 分钟，再用清水冲洗后沥干水分装盘；\r\n2.将香菇、胡萝卜、冬笋、葱、姜洗净切丝；\r\n3.锅内放油，油烧至六成热时放入葱丝、姜丝，炒出香味后再倒入香菇丝、胡萝卜丝、冬笋丝翻炒，加入白糖、醋、精盐、味精，烧开；\r\n4.将糖醋汁放凉后倒入装黄瓜的盘中，浸泡几小时后即可食用。', '清淡爽口，酸甜稍辣。', '必须等糖醋汁凉透后再浸泡黄瓜，这样黄瓜会更脆爽。', '食用油30克；香醋1小匙(3克)；精盐3小匙(9克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '黄瓜250克；香菇25克；胡萝卜25克；冬笋25克；大葱1根；生姜1小块');
INSERT INTO `recipe` VALUES (5807, 439, '金钩黄瓜条', '1.将黄瓜洗净去皮，切成条状（可根据个人喜好切成其他形状），加入精盐少许，腌5分钟；将海米洗净，用沸水泡透，捞出沥干水分；香葱洗净切成末；\r\n2.将黄瓜沥干，加入味精、香油拌匀，再放入海米；\r\n3.将炒锅置火上，倒入食用油烧热，下入葱末，炸出香味后捞出不用，把油淋在黄瓜条上，拌匀即可。', '色泽鲜艳，咸香爽口。', '腌黄瓜时不能加太多的盐，否则黄瓜不脆嫩。', '食用油25克；香油1小匙(3克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '黄瓜300克；海米10克；香葱1棵');
INSERT INTO `recipe` VALUES (5808, 439, '冰爽苦瓜', '1.将苦瓜洗净去皮，切条；枸杞洗净后备用；\r\n2.在碗内放入冰糖、雪碧、矿泉水，冰糖溶化后放入苦瓜片、枸杞，放入冰箱冰镇；\r\n3.将冰好的苦瓜装盘，食用时佐以蜂蜜、橙汁。', '脆嫩爽口，清凉去火。', '生苦瓜切片冰镇后，也可以直接蘸东北大酱食用。', '雪碧1/2大匙(15克)；矿泉水1/2大匙(15克)；橙汁1/2大匙(15克)；冰糖适量；蜂蜜1小匙(3克)', '苦瓜500克；枸杞25克');
INSERT INTO `recipe` VALUES (5809, 439, '鲜辣松花蛋', '1.香菇洗净、去蒂，每朵对切成4 等份后沥干水分，再放锅内干炒2 分钟后盛出；蒜洗净切末；辣椒洗净切成环状；\r\n2.再往锅里加入适量食用油，烧热后下蒜末、辣椒煸炒几下，然后放入香菇，先用大火炒，再改小火慢慢煨，直到香菇不再出水，微呈干缩状时关火，盛盘；\r\n3.将皮蛋剥壳、切片，放入盛香菇的盘内，放入酱油、糖、香油调匀即可。 ', '味美汁鲜，开胃下酒。', '香菇的水分是否已去除，可根据油中是否仍有水珠冒出来判断，但火不能大，以免把香菇炸得太干。', '食用油30克；香油2小匙(6克)；酱油1小匙(3克)；白糖1/2小匙(1.5克)', '新鲜香菇300克；皮蛋4个；红辣椒1个；青辣椒1个；大蒜3瓣');
INSERT INTO `recipe` VALUES (5810, 439, '多味扁豆', '1.大蒜切末；扁豆洗净后切丝，在沸水中焯熟，以除去毒素和豆腥味；\r\n2.扁豆丝捞出后立即投入冷水中浸泡一下，可以保持其碧绿的颜色，口感也更脆嫩；\r\n3.然后加入麻酱、酱油、蒜末、辣椒油、精盐、白糖、味精、醋、香油拌匀装盘即可。', '鲜咸酸甜，香辣可口。', '扁豆丝焯水的时间不能过长，否则不脆嫩。', '香油1小匙(3克)；辣椒油1小匙(3克)；酱油1小匙(3克)；麻酱1小匙(3克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '扁豆350克；大蒜5瓣');
INSERT INTO `recipe` VALUES (5811, 439, '花果荠菜', '1.将荠菜择洗干净，用开水焯后沥干水分、切成末；红、黄辣椒洗净切末；\r\n2.将花生米炸熟，捞出去皮拍成末；\r\n3.将荠菜、花生米、辣椒末放入碗内，加盐、味精、蚝油、香油拌匀装盘即可。 ', '色泽清新，口味清淡。', '如爱吃酸一点的，可加入少许香醋，味道也非常好。', '香油2小匙(6克)；蚝油1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '荠菜500克；花生米20克；红辣椒1个；黄辣椒1个');
INSERT INTO `recipe` VALUES (5812, 439, '糖醋三丝', '1.将白菜心洗净，切成丝，用精盐拌匀腌一下；\r\n2.鸭梨去皮核，切丝；山楂糕切成粗丝；\r\n3.沥去白菜丝的水分，放入盘内，将梨丝码在白菜丝上，最上面放山楂丝；\r\n4.白糖和醋加上少许清水后倒入热锅内熬化，倒出晾凉后浇在三丝上，淋上香油即可。 ', '甜酸清爽，解腻开胃。', '三丝不要切得太细，否则会影响口感。', '香油2小匙(6克)；精盐1小匙(3克)；白糖2小匙(6克)；香醋2小匙(6克)', '白菜心200克；鸭梨300克；山楂糕50克');
INSERT INTO `recipe` VALUES (5813, 439, '清凉梨丝', '1.将梨洗净削皮、去籽，切成丝；\r\n2.将山楂糕切成菱形片，摆在盘底；\r\n3.将梨丝加盐、白糖、醋拌匀，放在山楂片上即可。', '酸甜脆嫩，清凉消暑。', '为了防止梨丝氧化，可用调料将梨丝拌匀后再装盘。', '香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)', '鸭梨300克；山楂糕50克');
INSERT INTO `recipe` VALUES (5814, 439, '酱香瓜皮', '1.将西瓜外面的翠衣和靠近瓜瓤的白色软层去掉，只留靠近外皮的浅绿色的那层，放太阳下晒至一成干；\r\n2 .将晒干的瓜皮洗净，放入沸水中煮一下后捞出晾凉，然后挤干水，切成块状；黄瓜洗净切片；水萝卜洗净切丝；\r\n3.锅内倒入食用油，油热后倒入西瓜皮、黄瓜片和水萝卜丝，煸炒几下，加料酒、酱油、白糖、精盐和小半碗水，炒匀后加盖烧几分钟，汤汁将收干时加入味精，淋上香油炒匀即可。 ', '咸中带甜，鲜香适口。', '此菜凉食、热食均可。', '食用油30克；香油2小匙(6克)；酱油2小匙(6克)；料酒2小匙(6克)；精盐2小匙(6克)；白糖1小匙(3克)；味精1小匙(3克)', '西瓜皮1000克；水萝卜50克；黄瓜50克；蒜5瓣');
INSERT INTO `recipe` VALUES (5815, 439, '丁香花生', '1.将新鲜丁香鱼裹上适量淀粉，放入热油锅中炸约2分钟，至八成酥时捞起沥干油；\r\n2.将花生炸熟；葱、蒜、辣椒洗净切末；辣椒洗净切末；\r\n3.锅底留少量油，爆香葱末、辣椒末、蒜末，加入炸好的小鱼干、花生、调料，用大火拌炒均匀即可。', '香辣诱人，酥脆爽口。', '此菜凉食、热食均可。丁香鱼或其他鱼类裹上淀粉后应立即放入油锅炸，如果时间过长，淀粉会在鱼类表面结成颗粒，影响口感。', '食用油500克；（实耗50克）；酱油1小匙(3克)；料酒1小匙(3克)；鸡粉1小匙(3克)；胡椒粉适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '新鲜丁香鱼200克；花生200克；红辣椒1个；青辣椒1个；大葱1根；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5816, 439, '香油海带丝', '1.将海带洗净，切成细丝，放入沸水中焯过，捞出沥水装盘；葱、姜洗净切丝；\r\n2.将酱油、精盐、白糖、醋、胡椒粉、香油、味精调匀后浇在海带丝上，撒上葱姜丝；\r\n3.把食用油烧热，浇在海带丝上拌匀即可。', '鲜咸适口，清香脆嫩。', '如果海带丝有点硬，可用肉汤多煨一会。', '食用油10克；香油1小匙(3克)；酱油1小匙(3克)；胡椒粉适量；香醋1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '水发海带300克；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (5817, 440, '姜葱炒蟹', '1.生姜洗净切片；葱洗净切段；螃蟹洗净，每只切成6 块，蟹壳氽烫后留用；\r\n2.锅内热油后爆香姜片，加入螃蟹拌炒至蟹肉变白，加入盐、糖、料酒，转小火加盖焖煮；\r\n3.加入葱段，转大火翻炒至汁收干，盛入盘中，盖上蟹壳即可。', '葱香开胃，蟹肉鲜美。', '炒蟹的时间不宜过长，以免蟹肉变老影响口感。', '食用油30克；料酒1/2大匙(15克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '螃蟹2只；大葱4根；生姜1块');
INSERT INTO `recipe` VALUES (5818, 440, '麻辣小龙虾', '1.将小龙虾洗净，皮厚处剪口；葱白切段；姜、蒜洗净切末；\r\n2.将葱叶和花椒洗净，剁成末，加少许开水、精盐、味精、香油、香醋调成味汁；\r\n3.锅内注入适量开水，放入葱段、姜末、蒜末、辣椒、料酒，烧开后，放入小龙虾，煮熟后蘸调味汁食用即可。', '麻辣味浓，味道鲜美。', '也可以先炒后煨再收汁，口味也很棒。', '香油1小匙(3克)；料酒1大匙(30克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '新鲜小龙虾500克；香葱3棵；生姜1块；大蒜6瓣；花椒1大匙(30克)；干辣椒6个');
INSERT INTO `recipe` VALUES (5819, 440, '油爆虾', '1.河虾剪净须足，洗净备用；干辣椒洗净切段；葱洗净切丝；姜洗净切末；\r\n2.热锅加油烧至高温，倒入虾，翻动数下迅速捞起，稍稍沥干油后，再放入锅中，如此反复三次；\r\n3.锅内留少许油，加辣椒、葱丝、姜末煸香，倒入盐、味精、糖、香醋、酱油后小炒数下，加料酒搅匀，待汁液变稠时，倒入河虾，翻炒数下，淋上香油即可。', '味道鲜美，香脆可口。', '连炸三次，可使虾外脆内嫩。', '食用油500克；（实耗30克）；香油1小匙(3克)；酱油1小匙(3克)；料酒2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '河虾300克；大葱1根；生姜1小块；干辣椒５个');
INSERT INTO `recipe` VALUES (5820, 440, '辣炒河虾', '1.河虾剪净须足，洗净后擦干水分，拌入适量淀粉，用热油炸酥后捞出；\r\n2.葱洗净切葱花，辣椒、蒜、姜洗净切末；\r\n3.锅内留适量油，煸炒蒜末、辣椒末、姜末、辣椒酱，倒入河虾，加入料酒、酱油、胡椒粉、盐、糖、味精烧入味；\r\n4.最后撒下葱花，烧至汁液收干即可。', '香辣可口，美味下酒。', '1.虾擦干水后，拌淀粉才不易脱落，炸的时候也不会爆油。\r\n2.河虾肉质甜嫩，不要炸太久。', '食用油500克；（实耗50克）；酱油2小匙(6克)；料酒1/2大匙(15克)；辣椒酱适量；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '河虾400克；红辣椒1/2个；香葱1棵；生姜1小块；大蒜6瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5821, 440, '椒盐虾', '1.活基围虾用清水洗净，投入沸水中烫至颜色变红后捞出；\r\n2.逐只剪去虾须、虾枪和虾脚，控干水分备用；青辣椒洗净切成环状；红辣椒洗净切末；蒜切末；香葱切末；\r\n3.锅内倒入油，烧至六成热，倒入虾，大火快炸2 分钟后捞出沥油；\r\n4.洗净锅，烧热，倒入海盐干炒，炒至盐粒烫手后加入花椒再炒几分钟；\r\n5.椒盐炒好后倒入炸好的虾、辣椒、蒜末、葱末，炒拌均匀后熄火，盖上锅盖，利用盐粒的热度将虾焖熟。', '香辣酥麻，软嫩鲜美。', '这道菜适合用中等个头的虾，如基围虾、青虾、草虾等，个头太大的虾不容易入味。', '食用油300克；（实耗30克）；海盐8大匙(240克)', '活基围虾500克；红辣椒1个；青辣椒3个；花椒适量；香葱1棵；大蒜2瓣');
INSERT INTO `recipe` VALUES (5822, 440, '三鲜豆腐脑', '1.虾仁洗净，拌入少许蛋清、淀粉；葱、姜、香菜洗净切末；白果洗净；\r\n2.海参洗净，用葱姜末、料酒加适量清水，煮10分钟去腥，然后捞出、冲凉、切片；\r\n3.干贝先泡半小时，再加少许料酒蒸熟，取出撕成细丝；\r\n4.将高汤烧开，加入干贝丝、海参、白果和虾仁煮沸，改小火，加入盐、胡椒粉调味；\r\n5.慢慢滑入豆腐脑，略煮即关火盛出，撒下香菜末即可。 \r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '嫩滑爽口，清淡鲜香。', '瘦肉、鸡肉和任意一种海鲜搭配在一起都可称之为三鲜。', '高汤8大匙(240克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)', '虾仁100克；水发海参100克；干贝25克；白果25克；豆腐脑250克；鸡蛋1个；香葱1棵；生姜1小块；香菜2棵；淀粉适量');
INSERT INTO `recipe` VALUES (5823, 440, '青豆虾仁', '1.虾仁洗净沥干，放入蛋清、盐拌匀，再倒入淀粉搅匀；红辣椒洗净切成环状；\r\n2.锅内加油，烧至六成热，倒入虾仁小炒，捞起；\r\n3.锅内重新注入少许油，烧热，倒入青豆翻炒，再倒入虾仁、红辣椒、味精、料酒翻炒，起锅前淋上香油即可。', '虾肉爽滑，口味清淡。', '给虾仁上浆（即做法1）时浆液不要过厚，薄薄一层即可，否则会影响口感。', '食用油30克；香油1小匙(3克)；料酒2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '虾仁400克；青豆75克；鸡蛋1个；红辣椒1个；淀粉适量');
INSERT INTO `recipe` VALUES (5824, 440, '香酥虾球', '1.将虾仁、荸荠、白板油块剁碎，再加入糖、盐、玉米粉、淀粉、味精，搅拌均匀后捏成球状；\r\n2.吐司去四边后切成小丁，与面包糠拌匀，再将虾球放入并沾裹均匀；\r\n3.锅内注入油，烧热后放入虾球，先用小火炸几分钟，改大火再炸，炸至虾球表面呈金黄色，最后用余油轻炸粉条，放到虾球上即可。 ', '色泽金黄，口感香嫩。', '也可以用家庭榨汁机将虾仁和荸荠搅碎，这样能节省时间。', '食用油500克；（实耗60克）；白板油块50克；精盐2小匙(6克)；白糖小匙；味精小匙', '虾仁400克；荸荠100克；吐司5片；粉条5克；面包糠300克；玉米粉50克；淀粉适量');
INSERT INTO `recipe` VALUES (5825, 440, '松鼠黄鱼', '1.将黄花鱼剖开，收拾干净，割下鱼头另放；葱、姜、蒜洗净切末；\r\n2.用刀顺着鱼脊骨把鱼切成仅剩鱼尾相连的两半，把脊柱骨和小刺全部剔除掉；\r\n3.用刀把两半鱼片靠内脏的一面划成麦穗形花纹，撒上一层盐，滴上一层料酒，抹上一层湿淀粉；\r\n4.在炒锅内倒入油，大火烧热，先把鱼头炸一下，捞出后用刀劈开、拍扁。接着把鱼身放入炸成焦黄色，捞出，与鱼头拼在一起平放于盘中；\r\n5.把料酒、酱油、高汤、糖、香醋、味精加在一起，调成芡汁；\r\n6.锅内留少许油，烧热，下入松籽、青豆，炒熟后加入葱、姜、蒜末继续煸炒，然后注入芡汁，滴入适量熟食用油，煮开后浇在炸好的鱼上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '皮酥肉嫩，甜酸醇鲜。', '要掌握好炸鱼的火候及油温。', '食用油800克；（实耗100克）；酱油1/2大匙(15克)；高汤2大匙(60克)；料酒1大匙(30克)；香醋1大匙(30克)；精盐1小匙(3克)；白糖2大匙(60克)；味精1/2小匙(1.5克)', '黄花鱼1条（约600克）；青豆5克；松籽5克；香葱1棵；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5826, 440, '香酥小黄鱼', '1.将小黄鱼去除内脏和鳃，洗净；葱白洗净切段，葱叶切丝；姜洗净，部分切片，部分切末；\r\n2.在盛小黄鱼的大碗中放入盐、味精、料酒、葱段、姜片和花椒，腌制两小时；\r\n3.捡去花椒、葱段、姜片，在盆中撒入干面粉，将鱼身裹匀；\r\n4.往锅内倒油，烧热，调中火，逐条下入小黄鱼，炸至金黄色即可捞出；\r\n5.调大火，将油烧热，把鱼再次倒入，复炸一遍，使外皮焦脆即可捞出；\r\n6.将小黄鱼装盘，撒上葱丝、姜末、椒盐和辣椒粉即可。 ', '色泽金黄，外酥里嫩。', '给小黄鱼裹面粉时一定要压紧，以防油炸时面粉脱落。', '食用油500克；（实耗60克）；料酒1/2大匙(15克)；椒盐少许；辣椒粉少许；精盐3小匙(9克)；味精1/2小匙(1.5克)', '小黄鱼500克；香葱2棵；生姜1小块；花椒适量；面粉250克');
INSERT INTO `recipe` VALUES (5827, 440, '玉带黑鱼卷', '1.黑鱼宰杀后取净鱼肉，切块，然后片成片，用精盐抹匀上味，拍上干淀粉；葱白洗净切段；\r\n2.把鸡蛋清、水和适量淀粉调拌成糊，将火腿、鸡脯肉、香菇、玉兰片、生姜全切成细丝，整齐地卷在鱼片内，再在外面抹上蛋清糊收口，做成鱼卷；\r\n3.把葱叶系在鱼卷中间，装盘，蒸5 分钟后取出；\r\n4.炒锅上大火，加入高汤，放入蒸鱼卷的汁水，投入葱段，加入盐、味精，用湿淀粉勾薄芡，淋在鱼卷上，撒上白胡椒粉，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '质嫩洁白，咸鲜爽滑。', '蒸鱼的时间不易过长，否则鱼肉会变老。用葱叶系鱼卷时一定要系紧，否则鱼卷容易散开。', '香油1小匙(3克)；高汤1/2大匙(15克)；白胡椒粉1小匙(3克)；精盐3小匙(9克)；味精1小匙(3克)', '黑鱼1条（约600克）；火腿50克；鸡脯肉10克；香菇10克；玉兰片10克；鸡蛋1个；香葱2棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5828, 440, '西湖醋鱼', '1.鲩鱼去内脏、鱼鳞后洗净，加入料酒、精盐、姜汁，蒸20 分钟后取出；姜洗净切末；\r\n2.将蒸鱼的汤汁滤入炒锅内，加适量高汤、酱油、盐、胡椒粉、料酒、糖、香醋调成味汁；\r\n3.味汁烧开后用水淀粉增稠，撒上姜末浇在鱼上即可。 \r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '酸甜清香，口感软嫩。', '这道菜也可以这样做：先烧开水，关火，将收拾好的生鱼放入水中烫熟后淋味汁，鱼肉也很鲜嫩。', '酱油1小匙(3克)；高汤2大匙(60克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；姜汁1小匙(3克)；香醋1大匙(30克)；精盐4小匙(12克)；白糖4小匙(12克)', '鲩鱼1条（约750克）；生姜1小块；淀粉适量；');
INSERT INTO `recipe` VALUES (5829, 440, '泡椒辣鱼丁', '1.生姜洗净切片，蒜、葱、泡椒切末;\r\n2.鱼肉洗净切丁后加胡椒粉、盐、料酒、淀粉拌匀腌制;\r\n3.油温升至六成热时，放入鱼肉丁，炸成金黄色捞起;\r\n4.锅内留少许油，加入泡椒、生姜、蒜、葱段炒香，加入高汤烧开，将炸酥的鱼肉丁倒入锅内，再加入胡椒粉焖5 分钟，最后加料酒、味精、酱油、香油翻炒片刻，盛盘即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤汁鲜美，肉质鲜嫩。', '此菜也可做成麻辣味或其他口味。', '食用油500克；（实耗30克）；香油1小匙(3克)；酱油1小匙(3克)；高汤1/2大匙(15克)；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲩鱼肉300克；泡椒50克；香葱1棵；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5830, 440, '豆花鲩鱼', '1.鲩鱼宰杀后取净鱼肉，片成厚片；将鱼头劈开；葱洗净切葱花；青椒去蒂、籽切段；姜洗净切片；\r\n2.锅内放油烧热，下葱、姜爆香，烹入料酒，放入鱼头、鱼骨，加水用大火熬成鱼汤；\r\n3.锅内注少许油烧热，下入青椒段、豆瓣酱、蒜瓣炒香，放入鱼汤、料酒、糖、香醋、胡椒粉、味精、盐煮沸；\r\n4.加入豆腐脑煮入味，捞出倒入盆内，再将鱼片放入汤汁中煮熟，捞出盖在豆腐脑上，中间撒上黄豆和葱；\r\n5.锅内注入少许食用油，烧热，浇在鱼片上，淋上香油即可。', '麻辣鲜香，滑爽细嫩。', '煮豆腐脑的时间不易过长，以免影响口感。豆花即豆腐脑。', '食用油30克；香油1小匙(3克)；料酒1小匙(3克)；胡椒粉1小匙(3克)；豆瓣酱1小匙(3克)；香醋1小匙(3克)；精盐2小匙(6克)；白糖1小匙(3克)；味精1小匙(3克)', '鲩鱼1条（约750克）；豆腐脑100克；熟黄豆10克；青辣椒1个；香葱2棵；生姜1小块；大蒜8瓣；');
INSERT INTO `recipe` VALUES (5831, 440, '水煮鱼', '1.鲩鱼宰杀后将鱼头剁下，劈成两半;将鱼肉、鱼骨分开，鱼肉片成片，鱼骨剁成段;\r\n2.在鱼片内放蛋清，调入盐、淀粉和料酒，拌匀;\r\n3.蒜瓣洗净拍散，生姜洗净切片;黄豆芽入沸水焯几分钟;辣椒洗净备用;\r\n4.炒锅中加入全部的油，烧热后放花椒、辣椒，改小火慢炸。辣椒变色后，捞出花椒、辣椒和一半的油;\r\n5.调大火，放入蒜瓣、姜片，出香味后，将鱼头鱼骨入锅，翻炒两下，调入料酒、盐，加沸水适量，沸腾出味后将鱼片一片一片平放到汤中；\r\n6.鱼片煮熟后，放入味精、白胡椒粉和椒盐粉；在盆中放好黄豆芽和榨菜，将鱼片和汤汁一起倒入；\r\n7.最后将捞出的花椒、辣椒和油淋在鱼片上即可。', '香辣诱人，开胃下酒。', '鱼片要厚薄均匀，煮时断生即可，时间长了不够鲜嫩。', '食用油250克；料酒1大匙(30克)；白胡椒粉1小匙(3克)；椒盐粉1小匙(3克)；精盐3小匙(9克)；味精1/2小匙(1.5克)', '鲩鱼1条(约1000克)；黄豆芽300克；鸡蛋1个；榨菜条30克；生姜1块；大蒜10瓣；花椒1/2大匙(15克)；淀粉适量；干辣椒20个');
INSERT INTO `recipe` VALUES (5832, 440, '重庆酸菜鱼', '1.将鲩鱼宰杀，洗净，片出两片大的无刺鱼肉，再斜切成厚片；将鱼片用蛋清、盐拌匀；泡青酸菜洗后切段；红辣椒洗净切碎；葱切成葱花，姜切片，蒜瓣拍松；\r\n2.锅内放油烧热，下入花椒、姜片、蒜瓣炸出香味后，倒入泡青酸菜煸炒出味，加高汤烧沸，下鱼头、鱼骨、料酒、盐、胡椒粉煮沸；\r\n3.将锅内汤汁熬出味后，把鱼片逐片抖散入锅；\r\n4.把另一干净炒锅烧热，倒入油，把泡椒末煸炒出味后，倒入煮酸菜鱼的锅中，煮熟鱼片后加入味精，撒上葱花即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '酸味适口，辣而不燥。', '鱼片吃完后，剩下的汤汁可做成美味的鱼汤火锅。', '食用油45克；高汤10大匙(300克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1小匙(3克)', '鲩鱼1条(约1000克)；泡青酸菜250克；鸡蛋1个；红辣椒1个；香葱1棵；生姜1小块；大蒜5瓣；花椒适量；泡椒末1大匙(30克)');
INSERT INTO `recipe` VALUES (5833, 440, '蒜香鲶鱼', '1.鲶鱼宰杀后洗净，剁成块；辣椒洗净切段；葱洗净切段，生姜洗净切片，豆瓣剁细；\r\n2.大火烧热炒锅，倒入油烧热，放郫县豆瓣炒至油色红亮，加入葱、姜、高汤，再依次加入酱油、盐、料酒、鱼块、大蒜、辣椒，烧沸后转小火慢烧10分钟；\r\n3.待鱼熟透入味，先将鱼铲起盛入盘内；\r\n4 .再次调大火烧热锅中剩余的汤汁，加入味精和香醋，待汁浓稠，盛起浇在鱼块上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '浓香诱人，微辣适口。', '收汁要用大火。出锅时再放香醋，效果更好。', '食用油50克；酱油1/2大匙(15克)；高汤10大匙(300克)；料酒1大匙(30克)；郫县豆瓣3大匙(90克)；香醋1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲶鱼1条（约750克）；红辣椒2个；香葱1棵；生姜1小块；大蒜15瓣');
INSERT INTO `recipe` VALUES (5834, 440, '豉汁平鱼', '1.葱、姜、蒜、辣椒均洗净，切碎；\r\n2.鱼洗净，在鱼的两侧各划两刀，加料酒腌10 分钟；\r\n3.再在鱼中加入豆豉、葱、姜、蒜、盐、味精及辣椒，放入蒸锅中蒸熟即可。', '鱼肉嫩滑，豉香味浓。', '用料酒腌鱼可去腥味，如加入少量胡椒粉也可去腥。', '料酒1/2大匙(15克)；豆豉1/2大匙(15克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '平鱼1条；红辣椒1/2个；青辣椒1/2个；香葱1棵；生姜1块；大蒜2瓣');
INSERT INTO `recipe` VALUES (5835, 440, '雪笋鱼头', '1.鱼头洗净，拌入料酒和酱油略腌；葱、姜洗净切末；\r\n2.雪里蕻洗净，切碎；笋去壳，切片；豆腐切成方块；\r\n3.炒锅内放适量油，先爆香葱、姜，再将鱼头两面煎黄后盛出，放入砂锅内，将豆腐煎黄后一并放入；\r\n4.另用适量油炒肉馅和雪里蕻，加入料酒、酱油、盐、胡椒粉、水，然后放入砂锅内；\r\n5.同时加入笋片、辣椒，小火烧30分钟后加味精搅匀即可。 ', '汤鲜味美，香辣软嫩。', '1.选购鱼头时，以头型圆者为佳，先煎过再烧，可减少腥味。\r\n2.一定要用鲜笋烧，罐装笋或熟笋鲜味较差；夏天可用绿竹笋，冬天用冬笋。', '食用油50克；酱油2小匙(6克)；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲢鱼头1/2个（约250克）；雪里蕻110克；肉馅75克；冬笋75克；豆腐100克；香葱2棵；生姜1小块；干辣椒2个');
INSERT INTO `recipe` VALUES (5836, 440, '豉香鲳鱼', '1.红、黄辣椒洗净切成环状；葱洗净，切花；大蒜洗净切末；把豆豉、精盐、味精和料酒放入碗中调匀成酱料；\r\n2.鲳鱼去除内脏，洗净，两面各划两刀，并均匀地抹上盐，放入盘中，淋上酱料，撒上辣椒、蒜末后放入蒸锅中，用大火蒸熟，取出，撒上葱花即可。', '豉香味浓，鱼肉滑嫩。', '新鲜鲳鱼的肉质细嫩鲜美，适合清蒸或盐烤；冷冻鲳鱼的腥味较重，适合油煎，可减少鱼腥味。', '料酒2小匙(6克)；豆豉1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲳鱼1条(约600克)；红辣椒1/2个；黄辣椒1/2个；香葱1棵；大蒜3瓣');
INSERT INTO `recipe` VALUES (5837, 440, '红烧罗非鱼', '1.罗非鱼处理干净，在鱼身两侧斜划3 刀；葱洗净切段，姜、蒜、红辣椒洗净切片；\r\n2.锅内加油，烧热，将鱼煎至两面呈金黄色盛出；\r\n3.锅内留少量油，将葱、姜、蒜、辣椒爆香后加入酱油、料酒、糖、胡椒粉、盐、味精、水煮沸；\r\n4.放入煎好的罗非鱼，用小火烧至入味后捞出装盘，剩余汤汁煮至稠浓，淋上香油后浇在鱼身上即可。', '口味醇厚，色泽红润。', '新鲜罗非鱼无论是用来煎、炸、红烧还是煮汤，味道都很鲜美。', '食用油30克；香油1小匙(3克)；酱油1小匙(3克)；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '罗非鱼1条(约600克)；红辣椒1/2个；香葱3棵；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5838, 440, '豉香秋刀鱼', '1.秋刀鱼取出内脏后切段，洗净放入盘中；\r\n2.葱洗净后切成葱花；姜、蒜洗净切末；辣椒洗净切块；豆豉洗净；\r\n3.锅内放少量油，将葱、姜、蒜、辣椒、青豆、豆豉入锅爆香后淋在鱼上；\r\n4.再放上胡椒粉、酱油、料酒、盐、味精蒸约6 分钟至熟即可。', '豉香味浓，滑嫩清淡。', '如果不怕味道苦，不取出鱼的内脏也可以。', '食用油30克；酱油2小匙(6克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；豆豉1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '秋刀鱼3条(约600克)；红辣椒1/2个；黄辣椒1/2个；青豆5克；香葱1棵；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5839, 440, '锅贴青鱼', '1.把青鱼肉和肥膘肉切成同样大小的4大片；葱、姜、香菜洗净切末；用香油、料酒、葱末、姜末、盐、味精、大半个蛋清分别腌好鱼肉和肥肉；\r\n2.用肥膘肉铺底，上面撒玉米淀粉，把剩余的蛋清、淀粉、水调成稀芡糊，在鱼片上挂上一层，然后放在肥肉上，中间撒上火腿末，两边撒上香菜末；\r\n3.往锅内倒少量油，用小火烧热，把肥膘肉那面朝下，放入锅内煎；调大火，往锅内加油，将鱼炸成金黄色，捞出后切成小片装盘即可。', '双味混合，鲜香浓郁。', '锅贴技法只煎一面，不用翻面。此菜先用小火煎制，后加入另一半油，呈半煎半炸状态。', '食用油500克；（实耗50克）；香油1小匙(3克)；料酒1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '青鱼肉200克；猪肥膘肉100克；鸡蛋1个；火腿末50克；香葱1棵；生姜1小块；香菜1棵；玉米淀粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (5840, 440, '香辣小干鱼', '1.小干鱼洗净沥干；辣椒洗净，切段；蒜洗净切末；\r\n2.炒锅内放油煸炒小干鱼，然后放入蒜末和辣椒炒香；\r\n3.加入料酒、酱油、糖、味精拌炒入味后即可。', '干香微辣，开胃下饭。', '干爽一点的小干鱼比较新鲜，如果受潮或有霉味，炒好后会很腥。', '食用油30克；酱油2小匙(6克)；料酒2小匙(6克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '小干鱼300克；青辣椒6个；红辣椒6个；大蒜5瓣');
INSERT INTO `recipe` VALUES (5841, 440, '清蒸武昌鱼', '1.将鱼宰杀后洗净沥水，在鱼体两面划上花刀，抹少许盐，放在抹过油的盘子上；葱洗净切段，姜洗净切片；\r\n2.把熟火腿切成薄片；香菇去蒂、洗净，与熟火腿相间放在鱼体上；\r\n3.冬笋去壳后切成薄片，码放在鱼体两侧；上面放葱段、姜片，再倒入料酒；将盘放入蒸锅内大火蒸15分钟，取出，拣去葱段、姜片；\r\n4.将炒锅置大火上加热，倒入猪油烧至七成热时，再倒入蒸鱼的汤汁和少许高汤，烧开后加入精盐、味精，淋入鸡油后浇在鱼上，最后撒上胡椒粉即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肥腴细嫩，清香味鲜。', '鱼蒸好后直接加入酱油、葱末、姜末，淋上热油，味道也很鲜。', '食用油30克；猪油2小匙(6克)；鸡油1小匙(3克)；高汤适量；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/1小匙(3克)', '武昌鱼1条(约600克)；熟火腿25克；水发香菇50克；冬笋50克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5842, 440, '红烧带鱼', '1.将葱、姜、蒜洗净切片；将竹笋洗净切片；将带鱼处理干净，切段；\r\n2.将炒锅置火上，倒油烧热，把鱼段炸至两面呈浅黄色时捞出；\r\n3.锅内留少许油，放入葱、姜、蒜、竹笋稍炒，烹入料酒、酱油，加适量水，放入带鱼、醋、盐、味精，烧沸后转小火慢烧；\r\n4.待鱼烧透后捞出放入盘内，大火收汁，把稠汁浇在鱼上即可。', '鱼肉细嫩，咸鲜适口。', '如果带鱼很新鲜，也可以直接用沸水把带鱼氽烫后煨烧，这样做鱼肉会更加鲜嫩。', '食用油500克；（实耗50克）；酱油3小匙(9克)；料酒1/2大匙(15克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '带鱼400克；竹笋25克；香葱1棵；生姜1小块；大蒜6瓣');
INSERT INTO `recipe` VALUES (5843, 440, '清蒸鲫鱼', '1.将鱼宰杀洗净，在鱼体两面划上花刀，放在抹过油的盘子上；辣椒洗净切丝；姜洗净切片；葱洗净后1 棵切丝，另2 棵打成葱结；\r\n2.把料酒、鱼露、猪油、胡椒粉淋入盘中，放入葱结、姜，蒸10 分钟；\r\n3.鱼蒸好后取出，拣去葱、姜，把辣椒丝、葱丝撒在鱼身上；\r\n4.在炒锅内烧热香油和食用油，淋在鱼上即可。', '口味清淡，鱼味鲜嫩。', '因为鱼露的咸、鲜度高，所以不用放盐和味精。', '食用油20克；猪油1/2大匙(15克)；香油2小匙(6克)；料酒2小匙(6克)；鱼露1大匙(30克)；胡椒粉1小匙(3克)', '鲫鱼1条(约250克)；红辣椒1个；黄辣椒1个；香葱3棵；生姜1小块');
INSERT INTO `recipe` VALUES (5844, 440, '葱烧鲫鱼', '1.将鲫鱼宰杀洗净，在鱼身两侧划上花刀，抹一层料酒、精盐、酱油；葱、姜、蒜、辣椒洗净切末；\r\n2.将炒锅置大火上烧热，放入油，烧热后下鲫鱼煎至两面略黄，盛出；\r\n3.锅内留少量油，加入辣椒末、肉末、姜末、蒜末略煸，放入鲫鱼，加料酒、酱油、精盐、白糖、高汤烧沸，转小火将鱼烧熟，盛入盘内；\r\n4.炒锅置大火上，汤汁用水淀粉勾芡，淋入醋、香油，撒上葱花，浇在鱼身上即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鱼肉鲜嫩，红亮酥香。', '改小火后炖的时间要稍长一些，以便使鲫鱼入味。', '食用油30克；香油1/2大匙(15克)；酱油3小匙(9克)；高汤10大匙(300克)；料酒1大匙(30克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖2小匙(6克)', '鲫鱼2条(约400克)；猪五花肉末50克；香葱1棵；生姜1小块；大蒜6瓣；干辣椒3个；淀粉适量');
INSERT INTO `recipe` VALUES (5845, 440, '清蒸甲鱼', '1.甲鱼宰杀后用开水焯至外层变色起皱时捞出，用小刀刮净甲鱼身上的黑质及老皮，去掉爪尖、尾巴，掏净内脏、黄油，洗净剁成小块；\r\n2.将鸡肉洗净剁成肉茸；香菇洗净撕成大块；冬笋、火腿切成片；葱、姜洗净后分别切成段和片；\r\n3.取一汤碗加入甲鱼肉、葱段、姜片、高汤，入蒸锅蒸50 分钟取出；\r\n4.在汤碗里加火腿末、冬笋片、香菇块、油菜、葱、姜、味精、料酒、精盐、胡椒粉，用油纸封好，放入蒸锅中蒸15 分钟取出，打开油纸拣出葱、姜；\r\n5.最后把鸡茸倒入甲鱼汤内，烧开后即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肉烂菇滑，口味鲜香。', '甲鱼一定要洗干净，以免有腥味。适量加一点胡椒粒味道会更好。', '高汤10大匙(300克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '甲鱼1只；香菇50克；鸡肉100克；火腿肉50克；冬笋50克；油菜20克；香葱3棵；生姜1小块');
INSERT INTO `recipe` VALUES (5846, 440, '泥鳅钻豆腐', '1.葱洗净切段，姜洗净拍松；泥鳅静养几天后洗净；\r\n2.锅置火上，开小火放入冷水，将豆腐放在锅中间，把泥鳅放在周围，待锅中的水慢慢加热，泥鳅就会往豆腐里钻；\r\n3.在泥鳅全部钻进豆腐后，将豆腐取出，放在汤碗中，灌上高汤，放入精盐、胡椒粉、姜、葱、味精，蒸15 分钟即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜嫩爽滑，咸鲜适口。', '把活泥鳅先放在水盆中静养几日，往水中滴几滴菜油，让泥鳅吐尽肠内的脏物。', '高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '小泥鳅200克；豆腐500克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5847, 440, '麻辣泥鳅', '1.把泥鳅放入清水中养几天，排尽肠内杂物后剪去头部，清理内脏，洗净沥干后用酱油、料酒涂抹其表面和腹腔；葱、姜、蒜、香菜洗净切末；干辣椒切段；\r\n2.炒锅内放油，油温升至六七成热时放入泥鳅，炸至外表起脆壳，捞出沥油；\r\n3.锅内留底油，放入葱、姜、干辣椒煸炒出香味，倒入泥鳅，加酱油、糖、盐、水烧开入味，再加入醋、味精收干汁，撒上花椒粉，出锅后顺势撕成条，装盘撒上香菜末，淋上红油即可。', '麻辣味浓，香酥可口。', '此菜冷食、热食均可。', '食用油500克；（实耗60克）；红油1小匙(3克)；酱油2小匙(6克)；料酒1大匙(30克)；花椒粉1小匙(3克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '泥鳅300克；香葱1棵；生姜1小块；大蒜6瓣；香菜1棵；干辣椒3个；');
INSERT INTO `recipe` VALUES (5848, 440, '豉汁蟠龙鳗', '1.将河鳗用盐搓洗干净，每隔2 厘米在鱼背上切1 刀(脊骨断肚皮连)，从刀口处取出内脏，洗净；\r\n2.将豆豉洗净剁烂，红辣椒、葱、姜、蒜切末，加酱油、酒、白糖、精盐、胡椒粉拌成调料；\r\n3.把河鳗放入调料内腌入味后，再放湿淀粉调匀；\r\n4.将入味的河鳗摆在盘中呈蟠龙状，淋上香油，用大火蒸10 分钟即可。', '肉质滑嫩，豉香鲜美。', '掌握好刀工及蒸制的火候。', '香油1大匙(30克)；酱油2小匙(6克)；料酒1/2大匙(15克)；豆豉1大匙(30克)；胡椒粉1小匙(3克)；精盐3小匙(9克)；白糖1小匙(3克)', '河鳗600克；红辣椒1个；香葱3棵；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5849, 440, '银鱼炒蛋', '1.将鸡蛋打在碗中，搅匀，加入精盐待用；香葱切花；\r\n2.银鱼焯水烫透，捞出沥净水分；\r\n3.烧热油，加入搅好的鸡蛋，炒熟后加入葱花、银鱼，调入盐、酒，勾薄芡，淋香油出锅即可。', '鱼蛋滑嫩，诱人食欲。', '锅烧热后再加凉油，以免炒制时粘锅。', '食用油30克；香油1小匙(3克)；料酒1小匙(3克)；精盐1小匙(3克)', '银鱼150克；鸡蛋6个；香葱1棵；淀粉适量');
INSERT INTO `recipe` VALUES (5850, 440, '孜然鱿鱼卷', '1.将鱿鱼处理干净，切成圆圈状，放入碗中加精盐、料酒、孜然、辣椒粉腌制半小时；香葱洗净切段；\r\n2.在电磁炉专用烤盘中抹一层底油，放入鱿鱼及香葱，按下定温可调键，调至最大功率翻烤3 分钟左右，即可食用。', '孜然味浓，香嫩可口。', '也可以先把鱿鱼卷油炸一遍，然后加入孜然炒制。', '食用油20克；料酒1/2大匙(15克)；孜然2小匙(6克)；辣椒粉1小匙(3克)；精盐1小匙(3克)', '新鲜鱿鱼300克；香葱5棵');
INSERT INTO `recipe` VALUES (5851, 440, '爆炒双鱿', '1.蒜、姜、胡萝卜、竹笋、鲜香菇、荠菜洗净切片；葱切段；将胡萝卜、竹笋、香菇氽烫，捞出沥干；\r\n2.干鱿鱼泡软洗净，去除内膜；新鲜鱿鱼去除乌汁及内脏，洗净，内面划交叉刀纹，两种鱿鱼均放入沸水中氽烫，捞出沥干；\r\n3.锅内热油，爆香蒜、姜、葱，放入鱿鱼、笋、香菇、胡萝卜、荠菜及白胡椒粉、沙茶酱、蚝酱、盐、味精、料酒、糖、水翻炒入味，勾芡出锅即可。', '肉质鲜美，脆嫩可口。', '如果用沸水将水发鱿鱼烫过再炒，吃起来会更脆嫩。', '食用油30克；料酒1/2大匙(15克)；白胡椒粉1小匙(3克)；沙茶酱2小匙(6克)；蚝酱2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '干鱿鱼200克；新鲜鱿鱼200克；竹笋25克；胡萝卜25克；新鲜香菇25克；荠菜25克；香葱1棵；生姜1小块；大蒜2瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5852, 440, '酥炸牡蛎', '1.将淀粉、面粉倒入盘中，均匀混合成粉料；牡蛎洗净，沥干水分，均匀沾裹粉料；\r\n2.锅中倒入油烧热，放入牡蛎，炸至金黄色，捞出，食用前沾裹胡椒盐即可。', '色泽金黄，酥香鲜嫩。', '牡蛎沾裹粉料时需均匀，要避免粘连，这样吃起来才不会有黏糊的感觉。', '食用油500克；（实耗60克）；胡椒盐适量', '牡蛎600克；面粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (5853, 440, '家常海参', '1.将水发海参洗净切片；猪肉剁成末；黄豆芽择洗干净；蒜苗洗净切段；香菜洗净切段；\r\n2.锅内加高汤、料酒、盐和海参煨烧入味待用；\r\n3.锅内放少许油烧热，放入黄豆芽煸炒，加盐、味精炒熟，盛入盘内垫底；\r\n4.锅内注油烧热，放入蒜苗、辣豆瓣、高汤、酱油、猪肉末、海参煮沸，用水淀粉勾薄芡，加香油、味精后浇在豆芽上，撒上香菜即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '味浓香辣，鲜美软糯。', '这道菜是烩菜，所以芡汁不宜过稠。', '食用油50克；香油1小匙(3克)；酱油2小匙(6克)；高汤8大匙(240克)；料酒1大匙(30克)；辣豆瓣酱1小匙(3克)；精盐3小匙(9克)；味精1/2小匙(1.5克)', '水发海参450克；猪五花肉50克；黄豆芽50克；蒜苗25克；香菜1棵；淀粉适量');
INSERT INTO `recipe` VALUES (5854, 440, '香蒜鲜贝', '1.大蒜洗净切片，蘑菇去蒂，洗净切片；鲜贝洗净，沥干水分，放入碗中加入食用油拌匀，再均匀裹上面粉；\r\n2.锅中倒入奶油烧热，放入鲜贝，煎至两面呈金黄色，盛出；\r\n3.锅中再倒入剩余奶油烧热，爆香大蒜，放入蘑菇、青豆炒香，加入料酒、盐、辣椒酱炒匀，淋在鲜贝上即可。', '中西结合，鲜嫩可口。', '煎制鲜贝时火力不宜过旺，时间不宜太长，否则不易嚼烂。', '食用油20克；料酒2小匙(6克)；辣椒酱1/2大匙(15克)；奶油2大匙(60克)；精盐1小匙(3克)', '鲜贝300克；蘑菇20克；青豆20克；大蒜5瓣；面粉适量');
INSERT INTO `recipe` VALUES (5855, 440, '香辣花蛤', '1.姜、蒜洗净切末；辣椒洗净切段；\r\n2.炒锅加油烧热，先爆香姜、蒜、辣椒，再加入辣椒油、酱油、料酒、番茄酱、醋、盐、糖，把花蛤倒入，快速拌炒均匀至花蛤全部张开口即可。', '甜酸可口，鲜嫩微辣。', '如果将花蛤调好味后蒸熟，口感味道也会很好。', '食用油30克；辣椒油1大匙(30克)；酱油2小匙(6克)；料酒1/2大匙(15克)；番茄酱2小匙(6克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)', '花蛤600克；生姜1小块；大蒜6瓣；干辣椒3个');
INSERT INTO `recipe` VALUES (5856, 440, '多味九孔鲍', '1.葱、姜、蒜、辣椒均洗净切末，放入碗中加入番茄酱、酱油调匀成调味汁；\r\n2.九孔鲍洗净，放入碗中，加入盐及1 碗清水，浸泡10 分钟，捞出，再放入沸水中煮熟，捞出，盛入盘中，淋上调味汁即可。', '鲜嫩清香，酸辣可口。', '烹煮前，可先将九孔鲍冷藏一下，让肉块收缩，口感会更脆；煮九孔鲍的时间不宜过长，以免肉质变老。', '番茄酱2大匙(60克)；酱油1小匙(3克)；精盐1小匙(3克)', '九孔鲍鱼400克；红辣椒1个；青辣椒1个；香葱1棵；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5857, 440, '豉香夏威夷贝', '1.用开水将夏威夷贝烫开，摆入碟中；\r\n2.豆豉洗净后加盐、味精、淀粉、花生油、白糖拌匀，撒在夏威夷贝上；\r\n3.把蒸锅中的水烧开，放入碟子，用大火蒸6分钟拿出即可。', '豉香味浓，鲜嫩可口。', '蒸制时间不宜过长，以免影响肉质。', '花生油30克；豆豉1/2大匙(15克)；精盐2/3小匙(2克)；白糖1/3小匙(1克)；味精1/2小匙(1.5克)', '夏威夷贝200克；淀粉适量');
INSERT INTO `recipe` VALUES (5858, 440, '啤酒海螺', '1.将海螺壳敲碎，取肉洗净，片成薄片，入沸水中焯至七八成熟，捞出倒入料酒中浸泡几分钟，取出，用冷水洗净；葱、姜、蒜洗净切末；红辣椒洗净切片；笋洗净切片；火腿切片；\r\n2.锅内加少量油烧热，下葱姜末煸香，再将笋片、螺片、红辣椒片、火腿片投入，加精盐、啤酒、醋煸炒均匀，放入少量水淀粉勾芡即可。', '鲜嫩爽口，酒香扑鼻。', '选用活海螺制作时，一定要将螺肉上的污物去掉。', '食用油30克；料酒1/2大匙(15克)；啤酒2大匙(60克)；香醋1小匙(3克)；精盐2/3小匙(2克)', '活海螺8个；笋尖50克；火腿25克；红辣椒1/2个；香葱1棵；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5859, 441, '东坡肉', '1.用刀刮去猪肉皮上的细毛，去其肋骨，修去边皮，洗净后切成正方形的3 块，放入清水锅内，用大火煮5 分钟，捞出用清水漂洗干净；姜块去皮拍松；葱洗净打结；\r\n2.锅内放清水，加进肉块，再加入葱结、姜块、料酒，用小火烧煮1 小时，将肉煮熟；\r\n3.再加入酱油、白糖用小火焖煮30分钟，至肉质酥糯（煮时应不断将浮油撇去）；\r\n4.将肉捞起盛装在砂锅内，皮朝上，再加入一些煮肉的汁水，加盖密封，上笼用大火蒸30 分钟即可。', '肥而不腻，酥而不碎。', '宜选用肥瘦各半的优质猪肋肉。烧制此菜的重点是掌握好火候，火候到了，肉质自然就会美味可口。', '酱油3大匙(90克)；料酒7大匙(210克)；白糖2大匙(60克)', '带皮五花肋条肉1000克；香葱3棵；生姜1大块');
INSERT INTO `recipe` VALUES (5860, 441, '红烧肉', '1.猪肉洗净切成大块，保证每块都有皮和肥瘦肉；葱洗净切段，姜洗净切片；\r\n2.炒锅烧热，码入肉块用中火煎炸，肉中的油脂会逐渐渗出，需不断翻动，肉块煎至每个面都成金黄色时，盛出备用；\r\n3.锅内留底油，将冰糖倒入，小火烧至溶解呈黄色，关火，将煎好的猪肉入锅拌均匀，使每块肉均匀沾满糖色;\r\n4.葱、姜、大料、桂皮、干红辣椒入锅，加入料酒、酱油和没过肉面的温水，盖上锅盖开火煮沸，调小火炖40 分钟，肉熟烂、汤汁收浓即可。', '酱红油亮，肥而不腻。', '肉豆蔻、丁香、花椒、草果、香叶等等都是红烧肉的好搭配，但也不宜多放，以免抢了肉味。', '酱油1大匙(30克)；料酒1大匙(30克)；冰糖1大匙(30克)', '五花肉1000克；大葱2根；生姜1块；大料2粒；桂皮1片；干辣椒3个；注：可酌情配料');
INSERT INTO `recipe` VALUES (5861, 441, '回锅肉', '1.肉洗净，放入冷水锅内约煮20 分钟，用筷子插试，如无血水渗出即可捞出，切成薄片；\r\n2.青椒洗净，去蒂及籽，切成方块；蒜苗洗净切段；\r\n3.炒锅放油，先下肉片爆炒，肥肉部分收缩后再下青椒拌炒数下，盛出；\r\n4.锅中留少量油，将甜面酱、辣豆瓣酱炒香，加料酒、糖、味精炒匀，再倒回肉片、青椒一起翻炒，起锅前加蒜苗同炒，出香味后即可盛盘。', '色泽红亮，柔软醇香。', '这道菜一定要选用肥瘦相间的肉。', '食用油50克；料酒1/2大匙(15克)；甜面酱1大匙(30克)；辣豆瓣酱1大匙(30克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '五花肉200克；青辣椒2个；蒜苗50克；注：可酌情配料');
INSERT INTO `recipe` VALUES (5862, 441, '香菇烧肉', '1.肉洗净切块；香菇泡软、去蒂，对半切开；葱洗净，切段；姜洗净，切片；\r\n2.汤锅内放入五花肉块、香菇、葱段、姜片及大料，加入酱油、料酒、糖及适量水，用小火焖煮1 小时即可。', '色泽鲜艳，香味醇厚。', '烧五花肉之前可先把肉炸一会，让肉出点油，吃起来才不会太过油腻。', '酱油3大匙(90克)；料酒1大匙(30克)；白糖1小匙(3克)', '五花肉500克；香菇20克；香葱1棵；生姜1小块；大料2粒');
INSERT INTO `recipe` VALUES (5863, 441, '猪肉炖粉条', '1.五花肉洗净切小块；白菜洗净切条；粉条用温水泡软备用；\r\n2.把五花肉放入高压锅内炖约30 分钟；\r\n3.白菜内加酱油、盐腌制，把锅烧热，加入少许油，将腌制好的白菜炒香；\r\n4.将高汤、粉条、花椒、大料、桂皮放入高压锅内和五花肉一起炖香，加味精即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '咸鲜味美，汤香肉醇。', '用高压锅炖煮的时间不要过长。', '食用油50克；酱油1大匙(30克)；高汤适量；精盐1小匙(3克)；味精2/3小匙(2克)', '五花肉200克；白菜200克；粉条100克；花椒10粒；大料2粒；桂皮1片；注：可酌情配料');
INSERT INTO `recipe` VALUES (5864, 441, '海带烧肉', '1.将五花肉洗净，切成块；将海带洗净，用开水煮15分钟左右，切成菱形片待用（如果用海带丝，则挽成结）；葱洗净切段；姜洗净拍松；\r\n2.炒锅中放入食用油，加热，放入糖炒成金黄色，投入五花肉块、大料、葱段及姜块煸炒，等五花肉块的表面上好糖色时，加入煮好的海带，放入酱油、料酒和盐，再翻炒片刻，加清水烧开，转用小火，烧至肉和海带熟烂入味即可。', '香甜可口，营养丰富。', '炒糖色时，不宜用大火，应用中火使糖在油中均匀地溶化，糖液冒泡并呈金黄色时，下肉块最好；加入清水的量，以没过肉块为宜。', '食用油40克；酱油1/2大匙(15克)；料酒1大匙(30克)；精盐3小匙(9克)；白糖1大匙(30克)', '五花肉500克；水发海带200克；香葱1棵；生姜1小块；大料3粒');
INSERT INTO `recipe` VALUES (5865, 441, '荷叶粉蒸肉', '1.将肉洗净切成方块，中间直切一刀，表皮相连；鲜荷叶用热水烫软备用；葱、姜洗净切丝；\r\n2.将酱油、甜面酱、白糖、料酒、葱丝、姜丝、五香粉、香油放入装肉块的盆内，拌匀腌30 分钟；再加炒米粉拌匀，用鲜荷叶逐块包起，上笼用大火蒸2小时左右即可。', '酥烂不腻，清香可口。', '炒米粉分为辣的和不辣的两种，可根据个人爱好自选。', '香油1小匙(3克)；酱油1大匙(30克)；料酒1/2大匙(15克)；甜面酱2大匙(60克)；五香粉适量；白糖1/2大匙(15克)', '五花肉300克；炒米粉140克；香葱1棵；生姜1小块；荷叶2张');
INSERT INTO `recipe` VALUES (5866, 441, '陈皮卤肉', '1.五花肉洗净切块，拌少许酱油上色，放入油锅用中火略炸，捞出放入水中，洗去多余的油脂；葱洗净切段，姜洗净切片；\r\n2.锅中留少许油，将葱段、姜片爆香，加入五花肉、陈皮及酱油、料酒、冰糖，加水，煮沸后转小火焖煮约40 分钟即可。', '色泽红润，肥而不腻。', '用冷水冲洗油炸过的肉块可使得肉质酥嫩，更有弹性。', '食用油500克；（实耗25克）；酱油2大匙(60克)；料酒1大匙(30克)；冰糖1小匙(3克)', '五花肉600克；陈皮2克；香葱1棵；生姜1小块；');
INSERT INTO `recipe` VALUES (5867, 441, '栗子烧肉', '1.在板栗底端切一刀，放沸水中稍煮后捞出，去壳；葱洗净切段，姜洗净切片；\r\n2.将猪肉刮洗干净，切成小方块，放入锅内，加酱油、料酒、葱段、姜片，大火烧煮片刻，使肉上色；\r\n3.然后加水（以淹过肉面为宜），烧开后转用小火烧煮。肉块微酥时加入板栗；\r\n4.肉、板栗都烧酥时再加入精盐、白糖，略煮即可。', '栗糯肉酥，色红味美。', '板栗去壳后也可以先用油炸一下再烧。', '酱油1/2大匙(15克)；料酒1大匙(30克)；精盐4小匙(12克)；白糖3小匙(9克)', '猪五花肉500克；板栗500克；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (5868, 441, '八宝辣酱', '1.锅内放油，烧至二成热时，放入虾仁划散至熟，捞出沥油；\r\n2.锅内留底油，烧至四成热时投入鸡丁、肉丁、鲜贝划散至熟，放入栗子丁、白果仁滑一下，立即捞出沥油；\r\n3.锅内留适量油，先下辣椒酱、豆瓣酱炒透、炒香，加酱油、白糖、味精、料酒、高汤，将鸡丁等原料（除虾仁、青豌豆外）回锅，使卤汁稠浓，下水淀粉推匀，盛出装盘；\r\n4.将锅洗净后置火上，放入虾仁、青豌豆、味精、高汤，最后用水淀粉勾芡，盛出浇在炒好的菜的顶部即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽光亮，鲜嫩爽滑。', '可以根据自己的饮食喜好，随意选择8 种原料。', '食用油500克；（实耗100克）；酱油2小匙(6克)；高汤5大匙(150克)；料酒1大匙(30克)；豆瓣酱1大匙(30克)；辣椒酱1大匙(30克)；白糖1/2大匙(15克)；味精1/2小匙(1.5克)', '上浆虾仁50克；上浆鸡丁50克；上浆肉丁50克；上浆鲜贝50克；盐水鸭肫丁50克；熟栗子丁25克；白果仁25克；豌豆10克；淀粉适量');
INSERT INTO `recipe` VALUES (5869, 441, '水煮血旺', '1.将油菜洗净后备用；猪血切成厚片；葱、芹菜洗净切成段；姜、蒜洗净切末；\r\n2.锅烧热后，将花椒、干辣椒入锅炒香后剁成细末；\r\n3.锅内放油烧热后，加入大半匙豆瓣酱、姜末、蒜末爆香，再放入油菜炒至断生，起锅装入汤碗；\r\n4.锅中留底油，再加大半匙豆瓣酱炒香，加入少许清汤，放入猪血煮透，调入盐、味精，再盛入装有油菜的碗中，撒上辣椒末、花椒末，烧热油淋于其上即可。', '麻辣诱人，鲜嫩味美。', '烧热油淋菜时，油温一定要高。', '食用油30克；豆瓣酱1大匙(30克)；精盐1小匙(3克)；味精2/3小匙(2克)', '熟猪血300克；油菜50克；芹菜50克；香葱2棵；生姜1块；大蒜5瓣；花椒适量；干辣椒15个');
INSERT INTO `recipe` VALUES (5870, 441, '水煮肉片', '1.将瘦肉洗净后切成薄片；生菜洗净；姜、蒜洗净切片；葱切成花；干辣椒切碎备用；\r\n2.锅中放油烧热，放入姜片、蒜片爆香，加少许盐、味精，把生菜炒至断生盛入碗中；\r\n3.鸡蛋打入碗中，留蛋清，加淀粉打匀，将肉片放入蛋液中拌匀；\r\n4.锅中留少许油，爆香干辣椒、花椒、豆瓣酱，再加入水和少许淀粉勾芡，接着放入肉片煮透，盛出倒在生菜上，撒上葱花即可。', '麻辣爽口，鲜香诱人。', '肉片一定要用鸡蛋清和淀粉拌匀后才能下锅，煮的时间不宜太长。', '食用油50克；豆瓣酱1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '瘦肉200克；鸡蛋1个；生菜1棵；香葱3棵；生姜1小块；大蒜5瓣；花椒1/2小匙(1.5克)；淀粉适量；干辣椒15个；注：可酌情配料');
INSERT INTO `recipe` VALUES (5871, 441, '木樨肉', '1.将瘦肉洗净切片；鸡蛋磕入碗中，用筷子打匀；\r\n2.干木耳、干黄花菜用温水泡10分钟，充分发涨，剪去根部；黄瓜洗净斜刀切成段，放平后直刀切成菱形片；葱、姜洗净后切丝；\r\n3.锅内加油，烧热后加入鸡蛋炒散，盛装在盘中，即为所说的木樨；\r\n4.炒锅内加油烧热，将肉片放入煸炒，肉色变白后，加入葱丝、姜丝同炒，至八成熟时，加入料酒、酱油、盐，炒匀后加入木耳、黄花菜、黄瓜和鸡蛋块同炒，完全炒熟后淋入香油即可。', '色泽亮丽，味道鲜美。', '这道菜中的肉片不适宜用油滑，采用煸炒的方法肉味更香。', '食用油60克；香油1小匙(3克)；酱油1小匙(3克)；料酒2小匙(6克)；精盐1小匙(3克)', '瘦肉150克；鸡蛋2个；黄瓜50克；干木耳5克；干黄花菜5克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5872, 441, '腌笋千层肉', '1.腌笋用水泡软后，洗净切丝；把一小块五花肉切成肉丁；红辣椒洗净切丝；葱洗净切段；\r\n2.锅内注水，水开后，放入五花肉丁、腌笋，调入酱油、料酒、少量的糖及味精，用小火炖煮约3 个小时后，捞出沥干水分，取出五花肉，将腌笋装入碗中；\r\n3.锅中放入少许水，将剩下的五花肉下入锅中，加入酱油、料酒、生姜、葱和少许糖，用小火煮2 小时后，取出切成薄片，摆在腌笋上，剩余的汤汁加淀粉勾芡后淋在腌笋上即可。', '肥而不腻，肉软飘香。', '煮腌笋时加入五花肉，腌笋才会味美可口。', '酱油1大匙(30克)；料酒1大匙(30克)；白糖2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '五花肉500克；腌笋200克；红辣椒1个；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5873, 441, '干煸肉丝', '1.将瘦肉洗净切成细丝；干辣椒洗净后切段；生姜洗净切丝；葱洗净切段；\r\n2.将肉丝装入碗中，加入料酒、盐、胡椒粉腌制约5分钟；\r\n3.锅中放油，烧至五成热后，下入肉丝炸至金黄色后，捞起沥油；\r\n4.锅内留底油，放入干辣椒末、花椒、姜丝、葱段爆香，再加入肉丝、盐和味精煸炒入味即可。', '干辣下饭，香酥可口。', '肉丝一定要炸干后才能煸炒入味。', '食用油200克；（实耗30克）；料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '瘦肉400克；香葱2棵；生姜1块；干辣椒15个；花椒1小匙(3克)');
INSERT INTO `recipe` VALUES (5874, 441, '京酱肉丝', '1.里脊肉洗净，切丝，将蛋清、酱油、淀粉、料酒加入肉丝中拌匀；大葱洗净切成细丝；\r\n3.锅内加油，加热后放入肉丝迅速滑开，待肉色变白后立即捞出；\r\n4.锅中留适量的油，大火烧热，放入番茄酱、甜面酱、白糖，改小火用锅铲翻炒，炒至酱香扑鼻，并冒出小泡，改大火加入肉丝快炒至入味，淋上香油出锅；\r\n5.将炒好的肉丝码在盘上，撒上葱丝即可。', '咸香微甜，可口开胃。', '炒酱时要用小火温油慢慢炒，并不停地搅拌，否则酱很容易糊。', '食用油250克；(实耗30克)；香油1小匙(3克)；酱油2小匙(6克)；料酒1/2小匙(1.5克)；番茄酱2小匙(6克)；甜面酱3小匙(9克)；白糖1小匙(3克)', '里脊肉200克；鸡蛋1个；大葱4根；淀粉适量');
INSERT INTO `recipe` VALUES (5875, 441, '珍珠丸子', '1.把糯米洗净，放入水中浸泡12小时，沥干备用；鸡蛋打入碗内，滤去蛋清;\r\n2.将青芦叶放入开水中焯一下，洗净，铺在小蒸笼内;\r\n3.将猪肉洗净剁成茸，放入碗内，加料酒、精盐、味精、蛋黄、虾子、淀粉搅拌均匀成馅;\r\n4.然后把肉馅挤成核桃大小的丸子，每个丸子上滚上一层糯米，然后放在蒸笼内;\r\n5.把蒸笼放在沸水锅上，大火蒸20 分钟即可。', '清香细嫩，鲜咸适口。', '糯米要用清水浸泡12 小时，使其米粒松软便于蒸透。芦叶有特殊的清香，也可用菜叶代替。', '料酒1大匙(30克)；精盐2/3小匙(2克)；味精1/2小匙(1.5克)', '前腿夹心肉300克；糯米150克；青芦叶50克；鸡蛋1个；虾子适量；淀粉适量');
INSERT INTO `recipe` VALUES (5876, 441, '菜心扒肉饼', '1.瘦肉剁成泥;油菜心洗净;荸荠去皮洗净切末;姜洗净切末;\r\n2.瘦肉泥中加入一部分盐、味精、姜末、淀粉拌匀，打至起胶，做成肉饼;\r\n3.油菜心用开水烫熟，摆入碟内;\r\n4.锅内放油，油温升至六成热时下入肉饼，炸至外脆里嫩时捞起，摆在菜心上;\r\n5.锅内留底油，注入高汤，调入剩下的盐、味精、白糖、蚝油，用淀粉勾芡，淋香油后浇到肉饼上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '荤素搭配，诱人食欲。', '应选择带少许肥肉的肉，炸肉饼时火不宜太大。', '食用油500克；(实耗80克)；蚝油2小匙(6克)；香油1小匙(3克)；高汤2大匙(60克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '瘦肉200克；油菜心100克；荸荠10克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5877, 441, '蚂蚁上树', '1.先用温水将粉丝泡软洗净;瘦肉洗净剁成肉末;葱洗净切花；\r\n2.锅内放油，烧热后加入肉末，放入少许豆瓣酱炒干肉末，再加入粉丝炒匀，调入料酒、酱油、白糖、盐和味精炒匀后即可。', '口味清淡，爽滑美味。', '此菜要速炒，时间长了粉丝容易粘连，影响菜肴口感。', '食用油30克；酱油1大匙(30克)；料酒1大匙(30克)；豆瓣酱2/3大匙(20克)；白糖1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '瘦肉100克；粉丝350克；香葱1棵；注：可酌情配料');
INSERT INTO `recipe` VALUES (5878, 441, '腐皮肉卷', '1.葱洗净切段；姜洗净切末;猪肉洗净，剁成茸，盛入碗内，加适量的清水，放入精盐、味精、料酒和姜末，搅拌成肉馅。将面粉加适量水调成稀面糊;\r\n2.将豆腐皮铺在菜板上，撕去毛边，放入肉馅成粗条形，将豆腐皮由里向外卷起，边缘用稀面糊封口;\r\n3.锅内放油，烧至七成热，将肉卷放入炸至熟透，捞出沥油，切好后装盘;\r\n4.锅内留底油，放入葱段、姜末、酱油、糖，用小火烧至汤汁收浓，再加入香油后起锅，淋在腐皮肉卷上即可。', '色泽金黄，皮酥肉鲜。', '炸肉卷时油温不要过高，淋汁时速度要快。', '食用油500克；（实耗60克）；香油1小匙(3克)；酱油2小匙(6克)；料酒1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆腐皮3张；瘦肉300克；香葱2棵；生姜1小块；面粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5879, 441, '水浒肉', '1.将里脊肉洗净切成薄片，香葱洗净切段；里脊片用水氽一下，再用蛋清拌匀；\r\n2.锅内放油，烧热，把豌豆苗放入锅内，加盐、味精炒熟，盛在盘底；\r\n3.锅内放油，将干辣椒、花椒爆香，取出剁碎，放在另一碗中；\r\n4.利用原油锅，加高汤、酱油、味精、糖、盐及香葱爆一下，马上将里脊片入锅，炒至断生时，连汁盛出，倒在豌豆苗上，将爆好的干辣椒、花椒倒在里脊片上；\r\n5.再烧一点热油浇在里脊上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肉质嫩滑，汤汁鲜美。\r\n注：“水浒肉”原名“家乡肉”，相传是梁山好汉在江州城（今江西省九江市）的著名酒楼浔阳楼聚会时必点的一道菜。后人为了纪念他们，将“家乡肉”改名为“水浒肉”。', '辣椒、花椒一定要炸透，否则不香。', '食用油30克；酱油1大匙(30克)；高汤1大匙(30克)；白糖1小匙(3克)；精盐1小匙(3克)；味精1小匙(3克)', '里脊肉200克；豌豆苗80克；鸡蛋1个；香葱25克；花椒适量；干辣椒13个');
INSERT INTO `recipe` VALUES (5880, 441, '椒盐里脊', '1.里脊肉洗净切厚片，用刀将肉片拍松；\r\n2.把鸡蛋打入里脊肉片内，加入淀粉拌匀；\r\n3.烧锅下油，油烧至六成热时，下里脊肉炸至色泽金黄时捞起，把锅内余油倒入盆内；\r\n4.锅内留少许油，下炸好的里脊翻炒几次，再调入椒盐、味精，淋入香油炒匀即可。', '色泽金黄，外焦里嫩。', '一定要用刀拍松里脊肉，以使肉不易卷起，容易入味；炸肉时注意要用中火。', '食用油500克；(实耗油50克)；香油1小匙(3克)；椒盐1小匙(3克)；味精1小匙(3克)', '里脊肉100克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (5881, 441, '糖醋里脊', '1.鸡蛋打入碗内；肉洗净切片，放入鸡蛋液中，加水淀粉、面粉抓匀；葱、姜洗净切末；\r\n2.碗内放料酒、糖、香醋、盐、葱、姜、淀粉、高汤兑成芡汁；\r\n3.锅内放油，烧至五成热，下入肉片，炸至焦脆，捞出沥油；\r\n4.锅内留底油，烹入芡汁，倒入肉片，炒匀，淋香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '甜酸可口，外焦里嫩。', '里脊要多炸几遍，否则达不到外焦里嫩的效果。', '食用油500克；（实耗50克）；香油1小匙(3克)；高汤1/2大匙(15克)；料酒1/2大匙(15克)；香醋2大匙(60克)；白糖2大匙(60克)；精盐1小匙(3克)', '里脊肉250克；鸡蛋1个；香葱1棵；生姜1小块；面粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (5882, 441, '麻酥里脊', '1.将肉洗净切条，放入碗中加精盐、料酒、胡椒粉、味精、酱油拌匀腌制30 分钟；\r\n2.将鸡蛋打入另一碗中搅匀；将肉条先蘸上面粉、鸡蛋液，再蘸匀面包糠；\r\n3.锅内放油，油温升至五成热时，将肉条放入锅中，炸至金黄色后捞出装盘，食用时可蘸椒盐。', '色泽金黄，外酥里嫩。', '炸里脊时油温不能过低，防止面包糠脱落。', '食用油500克；（实耗80克）；酱油2小匙(6克)；料酒1大匙(30克)；胡椒粉1/3小匙(1克)；椒盐适量；精盐1小匙(3克)；味精1/2小匙(1.5克)', '里脊肉200克；鸡蛋2个；面粉100克；面包糠100克；');
INSERT INTO `recipe` VALUES (5883, 441, '京都排骨', '1.排骨洗净切小段，加入料酒、盐、糖拌均，腌至入味后，再拌入淀粉;\r\n2.锅内放油，油温升至七成热时，放入排骨，用小火炸几分钟，再改用大火炸约1 分钟后捞起沥油;\r\n3.另起一锅，在锅内加入少许油，将酱油、番茄酱、糖调匀倒入锅中煮开，再放入炸好的排骨拌炒至汤汁收干即可。', '色泽红润，咸鲜微甜。', '排骨要稍微炸干点，这样才可以多吸收些汤汁。', '食用油500克；（实耗50克）；酱油1大匙(30克)；料酒1大匙(30克)；番茄酱2大匙(60克)；精盐1小匙(3克)；白糖1小匙(3克)', '肋排500克；淀粉适量；');
INSERT INTO `recipe` VALUES (5884, 441, '粉蒸排骨藕', '1.葱洗净打结，姜洗净切片；将排骨洗去血水，沥干，切成小块，用酱油、料酒、白糖、葱、姜和少许五香米粉拌匀，腌至入味；\r\n2.洗净藕段，刮去老皮，竖剖为二，再横切成与排骨大小相似的片，用少许盐腌半小时；\r\n3.将排骨、藕片放在碗里，倒入五香米粉搅拌，使排骨、藕片都裹上一层米粉，然后将排骨、藕片交错分层码在大碗内，剩余的米粉也全部拌入，入蒸锅蒸15 分钟至熟即可。', '鲜嫩可口，醇香味美。', '蒸排骨前，在米粉上洒少量水，可使米粉柔软，蒸出来后口味会更好。', '酱油4小匙(12克)；料酒1大匙(30克)；精盐2小匙(6克)；白糖1/2大匙(15克)', '排骨500克；莲藕250克；五香米粉250克；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (5885, 441, '葱香排骨', '1.姜洗净切末；蒜洗净切片；排骨洗净，加入姜、蒜、酱油、酒、淀粉拌匀腌至入味；葱切长段备用；\r\n2.锅内放油，烧至七成热时，放入排骨，用中火稍炸至肉变色后即捞起沥油；\r\n3.锅内留底油，将香油、酱油、酒、糖、胡椒粉、盐、水调匀倒入锅中煮开，再放入排骨及葱段，改小火慢卤至汤汁剩一半，且排骨熟软入味即可。', '葱香味浓，骨香四溢。', '这道菜也可以蒸熟食用。', '食用油500克；（实耗30克）；香油1小匙(3克)；酱油1大匙(30克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；白糖1小匙(3克)；精盐1小匙(3克)', '排骨1000克；大葱3根；生姜1小块；大蒜3瓣；淀粉适量；注：可酌情配料；');
INSERT INTO `recipe` VALUES (5886, 441, '啤酒排骨', '1.姜、蒜洗净切末;葱洗净切段；鸡蛋打入碗中，滤去蛋黄；口蘑洗净切成十字花刀;春笋洗净切段，放入沸水中煮至断生，捞出用冷水浸泡;\r\n2.排骨洗净，切段，放入碗中，将盐、料酒、蛋清、淀粉、食用油、葱、姜、蒜、水拌匀，入冰箱冷藏2 小时；\r\n3.锅内放油，烧至七成热，放入排骨，炸至断生捞出;\r\n4.锅内留底油，下姜、蒜、葱、花椒炒香，下入高汤，熬出香味，捞去料渣，倒入排骨、口蘑、春笋、啤酒、盐、酱油、胡椒粉烧至入味;\r\n5.再加入味精、香油、辣椒油拌匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽红亮，麻辣清香。', '排骨段要大小一致，烧制的时间要充足，以便入味。', '食用油500克；(实耗30克)；辣椒油1小匙(3克)；香油1小匙(3克)；酱油2小匙(6克)；高汤3大匙(90克)；啤酒9大匙(270克)；料酒1/2大匙(15克)；豆瓣2/3大匙(20克)；胡椒粉2/3小匙(2克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '排骨500克；口蘑50克；春笋75克；鸡蛋1个；香葱1棵；生姜1小块；大蒜5瓣；花椒适量；淀粉适量');
INSERT INTO `recipe` VALUES (5887, 441, '无锡排骨', '1.排骨洗净后剁大块，用酱油抹匀;辣椒洗净切段;洋葱洗净切大块;大葱洗净切段;蒜洗净;姜洗净拍松;\r\n2.锅内放油，烧至七成热时，放入辣椒、洋葱、姜块、葱段、蒜头炸香后捞出;\r\n3.将排骨放入油锅内，炸成金黄色后捞起沥油;\r\n4.锅内留底油，放入辣椒、洋葱、葱段、蒜头、排骨，再加入大料、桂皮、酱油、蚝油、酒、冰糖、番茄酱及水，小火焖煮至肋排肉熟软后取出摆盘，淋上锅中余汁即可。', '咸香微辣，酥烂脱骨。', '用排骨熬的高汤取代水，味道会更好。', '食用油500克；（实耗50克）；蚝油1大匙(30克)；酱油1小匙(3克)；料酒1大匙(30克)；番茄酱1/2大匙(15克)；冰糖1/2大匙(15克)', '排骨1000克；洋葱60克；青辣椒1个；大葱1根；生姜1小块；大蒜10瓣；大料适量；桂皮适量');
INSERT INTO `recipe` VALUES (5888, 441, '炸猪排', '1.排骨洗净，用刀背拍松；姜洗净切末；\r\n2.将酱油、盐、姜、酒、牛奶、糖、淀粉、水拌匀，放入猪排腌至入味；\r\n3.锅内放油，烧至七成热，将猪排炸至金黄，捞起剁块即可。', '焦香诱人，滑嫩可口。', '排骨要反复炸几次，才能外焦里嫩。', '食用油500克；（实耗30克）；酱油1大匙(30克)；料酒1大匙(30克)；牛奶1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '排骨500克；生姜1小块；淀粉1大匙(30克)');
INSERT INTO `recipe` VALUES (5889, 441, '椒麻口条', '1.将花椒、蒜、香葱切碎待用；干辣椒洗净切段；\r\n2.将猪舌放入开水中焯2 分钟，捞出放温水中刮去舌苔，剔去舌骨根，洗净切片；\r\n3.锅内放油，烧热，爆香花椒、蒜、香葱、辣椒段，倒入猪舌片、盐、酱油，爆炒出香味，放入味精、香油即可。', '鲜香麻辣，脆嫩爽口。', '舌骨根可用青椒拌炒，味道也很好。', '食用油30克；香油1小匙(3克)；酱油1/3大匙(10克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '猪舌400克；香葱1棵；大蒜2瓣；花椒1大匙(30克)；干辣椒5个');
INSERT INTO `recipe` VALUES (5890, 441, '香芹爆腰花', '1.西芹洗净切片；红椒洗净切段；生姜洗净切片；香葱、蒜洗净切末；\r\n2.猪腰从侧面一切为二，去掉内白，用刀先横切三刀，再斜切五刀，切断成凤尾形；\r\n3.锅内放油，烧热，放入姜片、葱末、蒜末、酒、猪腰花，快速炒至八成熟，倒入小碗内；\r\n4.洗净锅，烧热下油，放入西芹、红椒炒至断生，加入猪腰，调入盐、味精、白糖翻炒，用水淀粉勾芡，淋入香油，出锅装盘即可。', '口感爽脆，开胃下酒。', '爆腰花时速度要快，才能保持腰花的脆嫩。', '食用油50克；香油1小匙(3克)；料酒1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精2/3小匙(2克)', '猪腰2个；西芹100克；红辣椒1个；香葱1棵；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5891, 441, '酒香腰丝', '1.猪腰从侧面一切为二，去掉内白，洗净，切成细条，放入沸水中氽烫透，捞出；\r\n2.葱洗净切末；姜洗净切丝；红辣椒洗净切丝；将花椒、葱末、姜丝、辣椒、精盐、味精放入大碗里，把高汤烧沸后冲入，调成味汁；\r\n3.锅内放油，烧热，下腰丝爆炒，加料酒、味汁烧开即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '酒香扑鼻，鲜咸适口。', '也可以把氽水后的腰丝放到味汁里腌入味，做成凉菜。', '食用油50克；料酒1大匙(30克)；高汤2大匙(60克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '猪腰2个；红辣椒1个；香葱1棵；生姜1小块；花椒10粒');
INSERT INTO `recipe` VALUES (5892, 441, '熘肝尖', '1.将猪肝洗净切片;辣椒、胡萝卜洗净切片;\r\n2.锅内放油，烧热，下猪肝滑熟后捞出;\r\n3.锅内留底油，下辣椒、胡萝卜、猪肝，放入酱油、盐、料酒、味精、白糖炒匀，用水淀粉勾芡即可。', '咸鲜可口，味美下酒。', '熘炒肝片的速度要快，火要大，否则影响肝片的嫩度。', '食用油500克；（实耗30克）；酱油1大匙(30克)；料酒1/2大匙(15克)；精盐1/3小匙(1克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '猪肝250克；青辣椒1个；胡萝卜1个；淀粉适量');
INSERT INTO `recipe` VALUES (5893, 441, '酱爆猪头肉', '1.熟猪头肉切片；蒜洗净切片；辣椒洗净去籽切块；郫县豆瓣剁细；\r\n2.锅内放油，烧热，加蒜片、郫县豆瓣爆香，放入猪头肉、笋片、辣椒、青豆、酱油煸炒，加糖、味精后淋上红油即可。', '颜色红亮，香辣不腻。', '猪头肉不要炒得过烂，否则影响口感。', '食用油30克；红油1小匙(3克)；酱油1/2大匙(15克)；郫县豆瓣1大匙(30克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '熟猪头肉300克；红辣椒2个；青豆10克；笋片30克；大蒜5瓣');
INSERT INTO `recipe` VALUES (5894, 441, '佛跳墙', '1.葱、姜洗净切末；鱼翅、蹄筋、猪肚、猪脚加入葱、姜、料酒略腌后放入沸水中汆烫；\r\n2.干贝、红枣、莲子分别浸软；油菜入水氽烫；芋头切块后用油炸一下；\r\n3.将处理好的材料加葱、姜、酒、盐、糖、酱油一起放入锅内煮开后续煮五分钟，再焖2 小时即可。', '佛跳墙是福建名菜，材料丰富，味道甘醇鲜美，香气相当浓烈，菜名有“连僧人都忍不住要跳墙过来吃”的寓意，好吃程度可想而知。', '焖汤的时间如长一点，口感和汤味更佳。', '食用油300克；（实耗20克）；酱油2小匙(6克)；料酒2大匙(60克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '鱼翅（或细粉丝）100克；蹄筋200克；干贝5克；猪肚50克；猪脚50克；芋头50克；油菜25克；红枣10克；莲子5克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5895, 441, '蒜香肠片', '1.猪大肠洗净；生姜、红辣椒洗净切片；大蒜洗净拍散；蒜苗洗净切段；\r\n2.把猪大肠放入沸水中，用中火煮至八成熟，捞起切厚片；\r\n3.锅内放油，烧热，放入姜、大蒜、大肠爆香，烹入料酒，注入高汤煮开，调入盐、味精、蚝油、酱油；\r\n4.用小火煨至大肠酥烂时下红椒片、蒜苗段煮片刻，撒入胡椒粉，淋入香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜香可口，开胃下酒。', '煨大肠时先用小火，出锅时用大火收汁。', '食用油30克；香油1小匙(3克)；蚝油1/2大匙(15克)；酱油1/3大匙(10克)；高汤3大匙(90克)；料酒1大匙(30克)；胡椒粉少许；精盐1小匙(3克)；味精2/3小匙(2克)', '大肠250克；红辣椒1个；蒜苗25克；生姜1小块；大蒜12瓣');
INSERT INTO `recipe` VALUES (5896, 441, '鲜辣肠旺', '1.大肠洗净后用水煮软，切段；酸菜心切薄片；猪血切小块；蒜苗切斜段；蒜、姜洗净切片；\r\n2.锅内放油烧热，先爆蒜片、姜片、花椒，再加入高汤、大肠、酸菜心、猪血，用小火煮沸后，加入蚝油、酱油、冰糖、蒜苗，再煮5 分钟后，用少许水淀粉勾芡即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜香可口，麻辣开胃。', '猪血可晚点放进去，以免煮的时间过长变老。', '食用油40克；高汤8大匙(240克)；蚝油1/2大匙(15克)；酱油1/2大匙(15克)；冰糖1/2大匙(15克)', '大肠300克；酸菜心150克；猪血500克；蒜苗25克；生姜1小块；大蒜3瓣；花椒1/2大匙(15克)；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5897, 441, '尖椒肚片', '1.猪肚洗净后从中间剖开，放入姜块、香醋、水，再放入高压锅内压约15 分钟后取出沥水;\r\n2.辣椒去蒂、去籽后洗净切片；蒜、姜洗净切片；葱洗净切段；猪肚切片;\r\n3.锅内放油，烧热，下姜、蒜片爆香，加入猪肚炒熟;\r\n4.再放辣椒块、葱段、胡椒粉、香油、盐和味精调匀，拌炒均匀即可。', '色泽鲜艳，微辣爽口。', '一定要先用高压锅将猪肚压透，否则不易炒烂。', '食用油30克；香油1小匙(3克)；胡椒粉1/2小匙(1.5克)；香醋1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '猪肚500克；红辣椒2个；青辣椒2个；香葱1棵；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5898, 441, '蒜爆猪心', '1.把猪心洗净;蒜苗洗净切段;葱、姜洗净后分别切成段、片;辣椒洗净切片；\r\n2.把猪心放入水中，煮沸，撇去浮沫;\r\n3.放入一部分葱、姜、蒜和花椒、大料、桂皮、丁香、酱油，与猪心同煮20 分钟后捞出猪心切片;\r\n4.锅内放油，烧热，放入辣椒和剩余的葱、姜、蒜煸香，放入猪心片煸炒后起锅盛盘;\r\n5.锅内放油，烧热，放入辣椒和蒜苗煸炒片刻，再倒入猪心，加辣椒粉、盐、味精即可。', '蒜香味浓，开胃下酒。', '一定要洗净猪心里面的血块。', '食用油30克；酱油1大匙(30克)；辣椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '猪心500克；蒜苗25克；红辣椒1个；香葱1棵；生姜1小块；大蒜3瓣；花椒适量；大料适量；桂皮适量；丁香适量');
INSERT INTO `recipe` VALUES (5899, 441, '鱼香蹄花', '1.蹄膀洗净后连皮切成大块，用酱油拌匀泡10分钟；葱、姜洗净分别切成段、片；\r\n2.锅内放油，烧热，将蹄膀炸成淡黄色，捞出浸入冷水中；\r\n3.锅洗净重置火上，加入浸泡时剩下的酱油和蹄膀、葱、姜、大料、酒、水，用小火煮至够烂且汁变稠为止；\r\n4.锅洗净，放少量油烧热，爆香豆瓣酱、蒜、姜末，并倒入煮蹄膀的汤汁，加入糖、香醋、盐、味精煮沸后，淋入淀粉水勾芡，撒下葱花、香油，浇到蹄膀上即可。', '香辣入味，微甜略酸。', '煮蹄膀时，大火烧开后关小火慢煮，这样不易破坏蹄膀的表皮。', '食用油500克；（实耗50克）；香油1小匙(3克)；酱油1大匙(30克)；料酒2大匙(60克)；香醋1/3大匙(10克)；辣豆瓣酱3小匙(9克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '猪蹄膀1个(约500克)；香葱3棵；生姜1小块；大蒜3瓣；大料适量；淀粉适量');
INSERT INTO `recipe` VALUES (5900, 441, '东坡肘子', '1.将蹄膀刮洗干净，顺骨缝划一切，放入水中煮透后捞出，剔去肘骨；葱、姜洗净后分别切成段、片；\r\n2 .将肘骨和蹄膀一起放入砂锅，将煮蹄膀的原汤倒入，要一次加足，放入葱、姜、料酒，大火烧开；\r\n3.将大豆洗净，放入砂锅，盖严，用小火炖3小时，直至用筷子轻轻一戳肉皮即烂为止，食用时放盐、味精即可。', '原汁原味，香气四溢。', '蹄膀炖烂后，可放在汤中多浸泡一段时间，这样更容易入味。\r\n注：蹄膀即肘子。', '料酒1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '猪蹄膀1个（约500克）；雪山大豆50克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (5901, 441, '豉椒蒸腊肉', '1.用热水将腊肉洗净，蒸约1小时，取出切厚片，排入碗内；\r\n2.将豆豉洗净，捣烂；红辣椒去籽，切碎；香葱洗净切碎；\r\n3.锅内倒油烧热，爆香豆豉、红辣椒和香葱，加味精后全部淋在腊肉上，再将腊肉隔水蒸片刻即可。', '咸香微辣，鲜而不腻。', '因腊肉偏咸，蒸制前需先用温水浸泡一会。', '食用油30克；豆豉1大匙(30克)；味精1/2大匙(15克)', '腊肉400克；红辣椒2个；香葱1棵');
INSERT INTO `recipe` VALUES (5902, 441, '腊肉炒芥蓝', '1.芥蓝洗净斜切成片，放沸水中焯一下捞出；辣椒洗净切片；腊肉洗净放沸水中稍煮片刻捞出，斜切成片；\r\n2.锅内放油，烧热，倒入芥蓝、辣椒和腊肉翻炒至出香味，放盐、糖、味精炒匀即可。', '色泽清新，咸香爽口。', '芥蓝焯水时在水中加少许盐，可使得芥蓝保持碧绿。', '食用油30克；精盐1/2小匙(1.5克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '腊肉50克；芥蓝梗300克；红辣椒1/2个');
INSERT INTO `recipe` VALUES (5903, 442, '啤酒烧牛肉', '1.将胡萝卜洗净，切成滚刀块；洋葱洗净切块；姜洗净切片；蒜洗净拍松；\r\n2.牛肉洗净切成小块，用沸水焯后捞出，放入凉水中洗去浮沫；\r\n3.往锅内倒油，烧热后放入姜片、蒜瓣、洋葱块翻炒，再加入番茄酱、牛肉块、酱油、白糖、啤酒，烧开后放入胡萝卜、胡椒粉、精盐、味精，最后倒入砂锅，用小火炖30 分钟即可。', '清香四溢，肉嫩可口。', '炖牛肉时，用啤酒代替水，牛肉会更加鲜嫩。', '食用油30克；酱油2小匙(6克)；啤酒8大匙(240克)；胡椒粉1小匙(3克)；番茄酱1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肉300克；胡萝卜50克；洋葱25克；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5904, 442, '果仁炸牛扒', '1.牛肉洗净后切成大块，用刀脊拍松肉块两面，撒上盐、味精；花生米去皮压成小粒；\r\n2.把鸡蛋打入碗内，加盐、味精、胡椒粉、淀粉、水调成鸡蛋糊；\r\n3.把牛肉挂上鸡蛋糊，再粘上花生米粒待用；\r\n4.往锅里放油，烧热后投入挂好糊的牛肉，炸至外脆里嫩，捞起装盘即可。', '外脆里嫩，酥香可口。', '如果去掉牛肉的筋络，肉块的口感会更好。', '食用油500克；(实耗50克)；胡椒粉少许；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛肉150克；熟花生米50克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (5905, 442, '姬菇牛肉', '1.姬菇去根洗净，牛肉去筋切片，生姜去皮切片；牛肉里加入盐、味精、淀粉、水，腌制5 分钟；\r\n2.往锅里倒油，烧热，下牛肉片滑炒至八成熟，倒出待用；\r\n3.锅内留底油，下入姜片、姬菇、盐炒至八成熟，加入牛肉片，调入味精、蚝油，翻炒数次，用水淀粉勾芡，淋入香油即成。', '味道鲜美，香嫩可口。', '切牛肉时要去掉牛肉筋；烹制前，最好用料酒、食用油腌一会牛肉，可使牛肉更入味、更鲜嫩。', '食用油30克；香油1小匙(3克)；蚝油1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛肉200克；姬菇200克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5906, 442, '兰椒炒牛肉', '1.牛肉洗净切片，荷兰豆洗净去老筋，青、红辣椒洗净去籽切片，姜洗净切片；\r\n2.牛肉加盐、味精、淀粉、水、料酒腌约5 分钟；烧热油锅，下牛肉炒至八成熟时倒出；\r\n3.往锅里重新注油，烧热，放入姜片、荷兰豆、辣椒片爆炒片刻，调入盐、味精、白糖炒几下，加入炒过的牛肉炒匀，用水淀粉勾芡，淋入香油即可。', '色泽鲜艳，清爽可口。', '在做法3 中，最后放牛肉是为了避免炒制时间过长，牛肉片肉质老化。', '食用油30克；香油1小匙(3克)；料酒2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肉150克；荷兰豆30克；红辣椒3个；青辣椒3个；生姜10克；淀粉适量');
INSERT INTO `recipe` VALUES (5907, 442, '虾香牛肉片', '1.把牛肉洗净切片，加料酒、盐、酱油、水、淀粉、少量食用油腌制一会；洋葱、姜、蒜洗净切末；\r\n2 .往锅内倒油，油温升至六成热时放入虾片炸至开花，捞出摆入盘中；再把牛肉片倒入锅内划散，捞出沥油；\r\n3.锅内留底油，烧热后倒入洋葱、姜、蒜、咖喱粉、沙茶酱、料酒、白糖、盐、高汤、味精、牛肉片，用水淀粉勾薄芡，待汁稠时盛出倒入装有虾片的盘中即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '浓香诱人，酥脆可口。', '切牛肉不要顺着肉的纹理切，应该横着切，用这种方法切出来的牛肉片口感更嫩。', '食用油500克；（实耗50克）；酱油1小匙(3克)；高汤2小匙(6克)；料酒1小匙(3克)；沙茶酱适量；咖喱粉适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肉300克；虾片50克；洋葱10克；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5908, 442, '西红柿烧牛肉', '1.牛肉洗净切片后加酱油、油、水、淀粉拌匀，腌置半小时；西红柿洗净去皮后切成块状；葱洗净切末；姜、蒜洗净切末；\r\n2.往炒锅里放油，烧至八成热时，把牛肉倒进去，炸至七成熟时捞起沥油；\r\n3.锅内留底油，放入葱、姜、蒜、西红柿，翻炒约2 分钟，再加适量清水及盐、番茄酱、糖、味精；\r\n4.西红柿煮烂后加入牛肉略炒，用水淀粉勾芡，炒匀后即可装盘。', '口味独特，味道鲜美。', '可以用番茄酱代替西红柿，菜的味道、色泽都很好。', '食用油500克；（实耗30克）；酱油1小匙(3克)；番茄酱1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肉250克；西红柿400克；香葱2棵；生姜1小块；大蒜2瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5909, 442, '孜然牛肉', '1.葱、姜洗净后切成末；牛肉洗净去筋，漂净血水后片成薄片，用盐、料酒、姜、葱腌15 分钟；\r\n2.往锅内注入食用油，烧至五成热时倒入牛肉片，炸至酥香捞出；\r\n3.锅内留底油，放入干辣椒、花椒翻炒出香味，然后下牛肉片炒匀，加高汤、酒、五香粉、糖，烧开后放入辣椒粉、孜然炒香，放入红油、味精、香油，翻炒均匀，装盘，撒上芝麻即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽棕红，麻辣鲜香。', '在煸炒时不宜放太多液体状调料。', '食用油500克；（实耗30克）；香油1小匙(3克)；红油1小匙(3克)；高汤1小匙(3克)；料酒2小匙(6克)；辣椒粉2小匙(6克)；五香粉1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '牛肉250克；香葱2棵；生姜1小块；熟芝麻1小匙(3克)；花椒适量；孜然3小匙(9克)；干辣椒8个');
INSERT INTO `recipe` VALUES (5910, 442, '冬笋烧牛肉', '1.把牛肉放在清水锅里，加入洗净的部分葱、姜，大火烧开，除尽血水后捞出切丝；用温水把冬笋泡软、洗净，切丝；辣椒洗净，切丝；\r\n2.往锅里放油，烧热后下入豆瓣酱、辣椒丝、花椒，炒出香味时加入剩余的姜、葱，烹入高汤烧制出香味后去掉料渣；\r\n3.接着把牛肉丝投入锅里，放酒、香油、胡椒粉、盐、味精，改小火，牛肉焖至七成熟时加入冬笋，烧入味后即可装盘。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '香味浓郁，肉质鲜嫩。', '切牛肉丝时要细心点，刀工不好可以多练习几次，这样才能做出更完美的菜肴。', '食用油30克；香油1小匙(3克)；料酒2小匙(6克)；高汤；10大匙(300克)；豆瓣酱1小匙(3克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛肉250克；冬笋250克；红辣椒1/2个；青辣椒1/2个；香葱1棵；生姜1小块；花椒适量');
INSERT INTO `recipe` VALUES (5911, 442, '东坡牛肉', '1.牛肉洗净后放入清水锅中煮至七成熟，捞出切成方块；胡萝卜、白萝卜洗净切块；葱、姜、蒜洗净；\r\n2.往锅里倒油，烧热，放入番茄酱、辣椒酱、排骨酱、花生酱、干辣椒炒香，加入高汤、葱、姜、蒜、草果、大料、香叶、糖、味精、盐煮开，再放入牛肉、胡萝卜、白萝卜，焖烧至入味软烂即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '香辣软糯，味厚滑爽。', '炖牛肉时，可以放几个山楂进去，这样牛肉会烂得快些，而且有股山楂的清香。', '食用油30克；高汤3大匙(90克)；番茄酱1小匙(3克)；辣椒酱1小匙(3克)；排骨酱1小匙(3克)；花生酱1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '牛肉400克；胡萝卜50克；白萝卜50克；香葱1棵；生姜1小块；大蒜3瓣；干辣椒5个；草果适量；大料适量；香叶适量');
INSERT INTO `recipe` VALUES (5912, 442, '桃仁牛肉', '1.把牛肉洗净切成2 厘米长、1 厘米厚的片；桃仁用温水泡一下，去掉皮；葱洗净切段；辣椒洗净切段；\r\n2.把食用油倒入炒锅内烧热，放入葱爆香，再把桃仁、牛肉下锅煸炒，烹入酱油，加糖、盐、水、味精，烧入味后用水淀粉勾芡，淋入香油即可。', '牛肉酥烂，桃仁脆嫩。', '可先把桃仁炸透，等牛肉快出锅时再加入，这样烹制出的桃仁更酥脆，口感更佳。', '食用油30克；香油1小匙(3克)；酱油3小匙(9克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '熟牛肉200克；核桃仁50克；红辣椒1/2个；青辣椒1/2个；大葱1根；淀粉适量');
INSERT INTO `recipe` VALUES (5913, 442, '冬菜蒸牛肉', '1.牛肉洗净切成薄片，冬菜洗净切成粒，洋葱洗净切末，香葱洗净切花；\r\n2.往牛肉片里加盐、味精、糖、蚝油、香油、洋葱、冬菜、胡椒粉、湿淀粉拌匀，装入碗内；\r\n3.蒸锅里的水烧开后，放入装牛肉的碗，隔水用大火蒸7分钟后取出，撒上葱花，淋上烧开的花生油即可。', '香气四溢，开胃下饭。', '蒸的时间不要太长，以防肉质变老。', '花生油20克；香油1小匙(3克)；蚝油3小匙(9克)；胡椒粉适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肉200克；冬菜15克；洋葱10克；香葱1棵；淀粉适量');
INSERT INTO `recipe` VALUES (5914, 442, '清炖牛肉', '1.牛肉洗净，切块，放沸水中氽烫，以去除血水，捞出后用冷水洗净；\r\n2.葱、姜洗净后拍松，与牛肉、大料、桂皮、料酒、冰糖、盐一并放入炖锅，大火煮开后改小火炖1小时，至牛肉松软即可。', '汤汁鲜美，肉质酥烂。', '做清炖牛肉可选用牛腩，牛腩有肥有瘦，口感更佳。', '料酒2大匙(60克)；精盐2小匙(6克)；冰糖适量', '牛肉1000克；大葱3根；生姜1块；大料适量；桂皮适量');
INSERT INTO `recipe` VALUES (5915, 442, '麻辣牛肉', '1.豆腐去硬皮、硬边，切成丁；牛肉洗净切成丁；葱、蒜洗净切末；豌豆洗净；\r\n2.豆豉碾碎后与花椒粉混合；把豆腐丁放入沸水中氽烫后捞出；\r\n3.往锅里放油，烧热，先将葱、蒜爆香，再倒入牛肉炒至半熟，最后加入豆腐、豌豆、辣椒粉、花椒粉、盐、豆豉、味精翻炒均匀；最后用水淀粉勾芡即可。', '麻辣鲜香，开胃下酒。', '用盐、糖将漂净血水的牛肉丁腌2 个小时后再烹制，牛肉吃起来非常软嫩。', '食用油30克；辣椒粉1大匙(30克)；花椒粉1大匙(30克)；豆豉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛肉200克；豆腐200克；豌豆50克；香葱2棵；大蒜6瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5916, 442, '美味牛肉丸', '1.先把所有材料洗净，再把西红柿切成小块，大蒜切成末；\r\n2.把番茄汁倒入锅内煮沸，再放入西红柿、蒜末、大料末煮10分钟，汁稠时再加入盐、醋即可调成多味汁；\r\n3.洋葱去皮切末；尖椒去蒂切成末；奶酪切成末；\r\n4.把牛肉馅、洋葱末、尖椒末、牛奶、鸡蛋液、奶酪、精盐、料酒、胡椒粉、面包糠放入碗里搅匀，挤成小丸子待用；\r\n5.往锅里倒油，油温升至四成热时将丸子放入锅中，炸至两面呈棕色，熟透后装盘，再淋入调制好的多味汁即可。', '色泽红润，咸酸微辣。', '拌牛肉馅时可稍微加一点肥肉，这样做出来的牛肉丸会更鲜嫩。', '食用油500克；（实耗50克）；料酒1小匙(3克)；胡椒粉1小匙(3克)；大料末适量；番茄汁1大匙(30克)；牛奶2大匙(60克)；香醋1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛肉馅300克；洋葱50克；西红柿100克；尖椒1个；鸡蛋1个；奶酪100克；大蒜8瓣；面包糠适量');
INSERT INTO `recipe` VALUES (5917, 442, '香干牛肉丝', '1.五香干洗净切丝；青椒、红椒分别去蒂、洗净，切丝备用；牛肉洗净切丝；\r\n2.把牛肉丝放入碗中，加入酱油、料酒、淀粉、食用油拌匀并腌10 分钟，再放入油锅中炒至七成熟，盛出；\r\n3.将原锅内的剩油烧热，放入豆干、青椒略炒，加入红椒、盐、酱油、糖炒至入味，最后加入牛肉丝炒匀即可。', '咸鲜微甜，香嫩可口。', '炒牛肉的时间不能长，否则肉丝不鲜嫩。', '食用油30克；酱油1大匙(30克)；料酒1大匙(30克)；精盐1小匙(3克)；白糖1/4小匙(0.75克)', '五香豆干200克；牛肉400克；青辣椒1个；红辣椒2个；淀粉适量');
INSERT INTO `recipe` VALUES (5918, 442, '银丝牛肉', '1.把牛里脊洗净切丝，加入鸡蛋、酱油、淀粉抓匀上浆；葱洗净切末；姜洗净切丝；\r\n2.烧热油锅，将粉丝盘成圆形下锅炸至粉丝涨发成白色，捞出放入盘中；\r\n3.将腌好的牛肉丝下锅，滑开后捞出沥油；\r\n4.锅内留底油，烧热，爆香葱、姜，倒入牛肉丝，同时加盐、料酒、香油、水淀粉、酱油、味精、白胡椒粉、水拌炒均匀，盛出放在炸好的粉丝中间即可。', '红白相间，软嫩酥香。', '炸粉丝时油温不能太低，否则炸出的粉丝不酥。', '食用油500克；（实耗50克）；香油1小匙(3克)；酱油1小匙(3克)；料酒2小匙(6克)；白胡椒粉适量；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛里脊肉200克；细粉丝30克；鸡蛋1/2个；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5919, 442, '萝卜焖牛腩', '1.牛腩洗净切块，白萝卜、胡萝卜洗净后去皮切块，生姜洗净切片，蒜苗洗净切段；\r\n2.把牛腩放在沸水中，用中火煮约5分钟，去净血水，用清水冲洗干净；\r\n3.往锅内放油，烧热，放入姜、料酒、大料、桂皮，加入高汤、水，用大火烧开，调入盐、味精、蚝油、酱油、胡椒粉，改用小火焖至牛腩快熟透时，再加入红、白萝卜块焖5 分钟，加入蒜苗，用水淀粉勾芡，淋入香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '营养丰富，清淡鲜美。', '用萝卜焖牛腩，既可以去除牛腩的膻味，又可以使萝卜更鲜美。', '食用油30克；香油1小匙(3克)；蚝油3小匙(9克)；酱油1小匙(3克)；高汤2小匙(6克)；料酒2小匙(6克)；胡椒粉少许；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛腩200克；白萝卜100克；胡萝卜20克；蒜苗10克；生姜1小块；大料适量；桂皮适量；淀粉适量');
INSERT INTO `recipe` VALUES (5920, 442, '黄豆焖牛腩', '1.牛腩洗净切块，干黄豆泡透洗净，生姜洗净切末，葱叶洗净切丝，胡萝卜洗净后去皮切块，枸杞泡洗干净；\r\n2.往锅里倒油，烧热，放入姜、牛腩爆炒干水分，倒入酒、高汤，用小火焖20 分钟；\r\n3.然后加入胡萝卜、黄豆、枸杞，焖烂后加盐、味精、胡椒粉、葱丝，焖透入味后装盘即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '营养丰富，豆香肉软。', '放二锅头酒有助于加快牛腩熟烂的速度。', '食用油30克；二锅头酒1/2大匙(15克)；高汤3大匙(90克)；胡椒粉少许；精盐2小匙(6克)；味精1小匙(3克)', '牛腩400克；干黄豆30克；胡萝卜30克；枸杞10克；大葱1根；生姜1小块');
INSERT INTO `recipe` VALUES (5921, 442, '西兰花牛柳', '1.牛肉洗净切成薄片，西兰花洗净切成小颗，胡萝卜洗净去皮切片，生姜洗净切片；\r\n2.往牛肉里加盐、味精、水淀粉，腌5 分钟，烧热油锅，把牛肉片炒至八成熟时盛出；\r\n3.往锅里倒油，烧热，放入姜、胡萝卜、西兰花，调入盐、味精、白糖、蚝油炒至断生时，加入牛肉，撒上胡椒粉，用大火爆炒出香味，用水淀粉勾芡，淋入香油即可。', '鲜嫩香浓，色泽清新。', '牛肉炒断生后食用，肉质最嫩、味道最醇。', '食用油30克；香油1小匙(3克)；蚝油3小匙(9克)；胡椒粉少许；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '牛里脊肉100克；西兰花150克；胡萝卜10克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5922, 442, '麻仁牛柳', '1.牛肉洗净切成粗条，用盐、味精、白糖腌5 分钟；\r\n2.把鸡蛋打入碗内，加入淀粉、水调成蛋糊，然后给牛肉条挂糊，再沾上白芝麻；\r\n3.往锅时倒油，烧热，放入沾有芝麻的牛肉，用小火炸至外脆里嫩，捞起装盘即可。', '外脆里嫩，色泽美观。', '炸牛肉时最好炸两遍，第一遍把牛肉炸熟，第二遍把牛肉炸脆。', '食用油500克；(实耗50克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '牛里脊肉150克；白芝麻50克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (5923, 442, '湘辣牛筋', '1.把牛筋倒入沸水中稍烫后捞出，切块；葱洗净切长段，和辣椒、大蒜一起略拍；\r\n2.往锅里倒油，烧热，放葱、辣椒、大蒜爆香，再放入牛筋、味精、糖、酒、番茄酱、盐、水，用小火焖煮40 分钟，然后夹出葱、辣椒、蒜，用水淀粉勾芡，淋上香油炒匀即可。', '色泽红润，辣爽可口。', '把牛筋放到沸水中稍烫是为了去除牛筋的腥味。', '食用油30克；香油1小匙(3克)；料酒1小匙(3克)；番茄酱2大匙(60克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛筋600克；红辣椒1个；大葱2根；大蒜2瓣；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5924, 442, '椒爆牛心顶', '1.牛心顶去净油后洗净切厚片，辣椒、姜洗净切片；\r\n2.往锅内加水，水烧开后，下入切好的牛心顶，烫去异味倒出，沥水；\r\n3.往锅里放油，放入姜、豆瓣酱、辣椒爆香，再投入牛心顶片，用大火爆炒片刻，调入盐、味精炒至入味，用水淀粉勾芡，淋入香油即可。', '鲜辣脆爽，美味下酒。', '用大火炒可使得牛心顶更加脆嫩爽口。', '食用油30克；香油1小匙(3克)；豆瓣酱3小匙(9克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛心顶250克；红辣椒1个；青辣椒1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5925, 442, '葱烧牛小排', '1.芹菜洗净切末；洋葱洗净切成条状；牛小排洗净切块，放入盐、糖、酒、酱油拌匀，腌2 小时；\r\n2.往锅里放油，烧热，将牛小排煎至两面微黄，再放入芹菜、洋葱同煎，至牛小排两面焦黄、洋葱软烂时倒入腌牛小排的汁液，煮至汁浓即可。', '肉鲜味美，色泽红润。', '牛小排煎至断生即可食用，这时的牛小排最为鲜嫩。', '食用油50克；酱油1大匙(30克)；料酒1大匙(30克)；精盐1小匙(3克)；白糖1小匙(3克)', '牛小排500克；洋葱20克；芹菜10克');
INSERT INTO `recipe` VALUES (5926, 442, '鲜香牛肝', '1.荸荠去皮后洗净切片；泡椒去蒂、去籽，切片；牛肝撕去表皮，洗净切片；木耳洗净；姜、蒜洗净切末；\r\n2.把牛肝放在碗里，加盐、糖、水、淀粉、高汤拌匀上浆，再把泡椒、姜、蒜同放入牛肝中拌匀腌制；\r\n3.把酱油、醋、味精、水、淀粉同盛于碗内，加少许高汤兑成芡汁；\r\n4.往锅里倒油，烧至七成热时下入牛肝、泡椒、姜、蒜，炒至牛肝散开发白时，烹入料酒，放入荸荠、木耳煸炒，倒入芡汁，炒匀后盛入碗内，淋入香油，撒上花椒粉即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜嫩微辣，味浓可口。', '牛肝比较粗糙，炒制的时间不要过长，以防牛肝变老、变硬。', '食用油30克；香油2小匙(6克)；酱油1/3大匙(10克)；高汤适量；料酒1/2大匙(15克)；花椒粉1/2小匙(1.5克)；香醋1/2小匙(1.5克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛肝200克；水发木耳15克；荸荠50克；泡椒50克；生姜1小块；大蒜8瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5927, 442, '芝香羊肉串', '1.羊肉去皮洗净切片，姜、蒜洗净切末，葱洗净切花；\r\n2.往羊肉里加盐、味精、料酒、胡椒粉、水、淀粉腌制，然后分别用竹签串好待用；\r\n3.往锅里倒油，烧热起烟时放入羊肉串，炸至外黄里嫩时捞起沥油；\r\n4.锅内留底油，把姜、蒜爆香，放入羊肉串，撒上白芝麻、葱花，淋入香油炒匀即可。', '肉嫩味美，焦香可口。', '可在羊肉串中加上一点肥羊肉，味道会更香。', '食用油500克；(实耗80克)；香油1/2小匙(1.5克)；料酒1/3大匙(10克)；胡椒粉少许；精盐2小匙(6克)；味精1小匙(3克)', '羊肉300克；炒熟的白芝麻10克；香葱2棵；生姜1小块；大蒜8瓣；淀粉适量；竹签8根；注：可酌情配料');
INSERT INTO `recipe` VALUES (5928, 442, '扒羊肉条', '1.把羊肉放在清水中浸泡，浸出血水后洗干净，放入开水锅中煮熟，然后取出晾凉；大葱和香葱洗净切段，姜洗净切片；\r\n2.将羊肉上的皮剥去，横着肉纹切成肉条，整齐地摆放在碗里；\r\n3.往锅里放3 小匙香油，烧热，加入大料、大葱、姜片爆香，再加入料酒、酱油及煮肉的原汤；汤汁烧开后，倒入盛肉条的碗中，放入蒸锅用大火蒸25分钟，然后夹去大料、大葱和姜片；\r\n4.把肉条和蒸肉的原汤倒入炒锅内(不要把肉条弄散)，用大火烧开，加入酱油、味精、香葱和姜，用水淀粉勾芡，颠翻两下，淋入香油即可。', '肉色金黄，味美香浓。', '翻勺时要注意，不要将肉条弄散。', '香油1大匙(30克)；酱油1大匙(30克)；料酒1小匙(3克)；味精1/2小匙(1.5克)', '羊肉300克；大葱1根；香葱1棵；生姜1小块；大料适量；淀粉；注：可酌情配料适量');
INSERT INTO `recipe` VALUES (5929, 442, '豆瓣焖羊肉', '1.把羊肉洗净切块，用开水焯过后捞出控水；葱、姜洗净分别切成段、片；\r\n2.锅中放油烧热，放入豆瓣酱炒出香味，加入高汤，烧开后稍煮，把豆瓣酱的渣子捞净，再把羊肉、料酒、盐、味精、葱、姜一起下锅，烧开，转小火慢烧，待肉软烂，收浓汤汁即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肉质细嫩，咸鲜微辣。', '羊肉的肉质细嫩，烧制时可以用小火慢煮，熟烂后口感极佳。', '食用油30克；高汤5大匙(150克)；料酒3小匙(9克)；豆瓣酱2大匙(60克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '羊肉500克；大葱2根；生姜1小块');
INSERT INTO `recipe` VALUES (5930, 442, '萝卜炖羊肉', '1.羊肉洗净切块，白萝卜洗净去皮切块，生姜洗净拍松，蒜苗洗净切段；\r\n2.往锅里放油，烧热，放入姜、大料、桂皮、豆瓣酱、羊肉爆炒出香味，注入料酒、高汤，用中火烧；\r\n3.然后加入白萝卜、盐、味精、胡椒粉、酱油烧透至入味，放入蒜苗、红油稍烧片刻即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '营养丰富，汤汁鲜美。', '白萝卜入锅前，可先用沸水焯一下，以去掉萝卜的辛辣味。', '食用油30克；红油3小匙(9克)；酱油3小匙(9克)；高汤3大匙(90克)；料酒1/3大匙(10克)；胡椒粉少许；豆瓣酱3小匙(9克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '羊肉400克；白萝卜50克；蒜苗15克；生姜1小块；大料适量；桂皮适量');
INSERT INTO `recipe` VALUES (5931, 442, '榨菜羊肉末', '1.羊肉洗净剁成碎末；榨菜剁成碎末，用温水泡出咸味，然后捞出沥水；红辣椒洗净切末；葱、姜、蒜洗净切末；\r\n2.将炒锅放在大火上，放入食用油，待油烧热时，放入肉末煸炒，待肉末煸散后，放葱、姜、料酒、酱油、蒜、辣椒、榨菜、味精，加入香油，炒匀即可。', '味道醇香，开胃下饭。', '煸炒羊肉时，油温一定要足够热，否则羊肉容易粘锅。', '食用油30克；香油3小匙(9克)；酱油3小匙(9克)；料酒2小匙(6克)；味精1/2小匙(1.5克)', '瘦羊肉100克；榨菜100克；红辣椒1个；大葱1根；生姜1小块；大蒜9瓣');
INSERT INTO `recipe` VALUES (5932, 442, '粉蒸羊排', '1.将羊排洗净剁成块；大葱、姜洗净切成末；\r\n2.把羊排、食用油、盐、腐乳、料酒、豆瓣酱、甜面酱、酱油、糖、大葱、姜同放在碗内，腌制25 分钟后加入米粉，使羊排均匀地裹上米粉，再滴入香油拌匀；\r\n3.把蒸锅放在火上，倒入清水，水开后将羊排放入，隔水蒸50 分钟出锅即可。', '色泽红润，咸香微辣。', '蘸裹米粉时可稍微洒点水，这样蒸出来的米粉软嫩适口。', '食用油10克；香油1小匙(3克)；酱油2小匙(6克)；料酒3小匙(9克)；腐乳适量；甜面酱1小匙(3克)；豆瓣酱1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '羊排300克；米粉100克；大葱1根；生姜1小块');
INSERT INTO `recipe` VALUES (5933, 442, '酸菜炖羊肚', '1.将羊肚切丝，放入沸水中稍氽，捞出控水；酸菜切丝；葱、蒜洗净切末；姜洗净切片；\r\n2.将食用油烧热，放入葱、姜、蒜、羊肚和酸菜，煸炒出香味，烹入料酒，加一大碗水，烧开后，加入味精、盐、胡椒粉稍煮，淋入香油即可。', '汁浓味厚，香而不腻。', '洗羊肚时加点醋，可以去除异味。', '食用油30克；香油2小匙(6克)；料酒1/2大匙(15克)；胡椒粉2小匙(6克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '熟羊肚400克；酸菜300克；大葱1根；生姜1小块；大蒜8瓣');
INSERT INTO `recipe` VALUES (5934, 442, '冬笋炒羊肝', '1.将羊肝洗好，切片，用水淀粉拌匀上浆；冬笋洗净切片；葱洗净切丝，蒜洗净切末；\r\n2.将料酒、酱油、味精、淀粉、精盐、葱丝、蒜末同放入碗内，搅匀，调成芡汁；\r\n3.往锅里倒油，烧热，将羊肝片放入锅内滑透，再放入笋片稍炸一会，盛出沥油；\r\n4.洗净锅，再将肝片、笋片重新倒入锅内，开火，倒入芡汁，炒匀，淋入香油即可。', '软嫩滑口，鲜香诱人。', '羊肝质地柔软、细嫩，炒制时间不要过长，以防止羊肝变老、变硬。', '食用油500克；(实耗50克)；香油1/2大匙(15克)；酱油1大匙(30克)；料酒2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '羊肝175克；冬笋25克；大葱2根；大蒜8瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5935, 443, '辣子鸡丁', '1.鸡脯肉去掉筋膜，洗净后切丁，加盐、酱油、料酒、味精拌匀腌制；\r\n2.荸荠去皮，洗净后切丁；泡椒去蒂、籽剁碎；葱、姜、蒜洗净切末；把蛋清、淀粉、水调成稀糊；\r\n3.炒锅置大火上，下猪油烧热，鸡丁用稀糊上浆后，下油锅滑散至熟；\r\n4.下入泡椒，急速翻炒至鸡丁全部呈辣椒红色时，下入荸荠、姜、葱、蒜炒出香味，烹入用盐、酱油、料酒、糖、味精、淀粉、高汤兑成的汁，迅速翻炒，并滴入少许醋炒匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '质嫩滑爽，鲜香带辣。', '做这道菜还可以用陴县豆瓣代替泡椒，用鲜笋代替荸荠。', '猪油50克；酱油1/2大匙(15克)；高汤1大匙(30克)；料酒2/3大匙(20克)；香醋适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1小匙(3克)', '鸡脯肉250克；荸荠50克；泡椒15克；鸡蛋1个；香葱2棵；生姜1小块；大蒜6瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5936, 443, '核桃爆鸡丁', '1.将鸡脯肉洗净切丁，用盐、料酒、水、淀粉拌匀；红辣椒洗净切片；葱、姜洗净切末；用温油将核桃仁炸熟后捞出；\r\n2.用料酒、盐、胡椒粉、味精、水、淀粉兑成芡汁；\r\n3.炒锅上火，烧油至六成热，下鸡丁滑熟捞出沥干；\r\n4.原锅留油，爆香葱姜，倒入鸡丁、辣椒、核桃及兑好的芡汁，炒匀即成。', '桃仁酥脆，鸡丁滑嫩。', '炸核桃仁时油温要低，否则核桃仁炸不透、不酥脆。', '食用油500克；（实耗40克）；料酒2小匙(6克)；胡椒粉2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸡脯肉300克；核桃仁100克；红辣椒1个；香葱1根；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5937, 443, '宫保鸡丁', '1.鸡肉洗净切丁，用蛋清、盐、淀粉腌拌均匀；蒜洗净切末；\r\n2.食用油入锅烧热，鸡丁下锅炸熟，捞起沥油；\r\n3.锅中留油少许，爆香干辣椒、蒜，再下入鸡丁翻炒；\r\n4.最后放酱油、料酒、味精、糖、醋、水、淀粉、香油炒匀并勾芡，最后加入花生米炒匀即可。', '酸甜微辣，开胃下酒。', '花生米一定要在菜起锅前下锅，以免长时间在锅内翻炒，影响花生米的酥脆感。', '食用油500克；（实耗50克）；香油1小匙(3克)；酱油1/2大匙(15克)；料酒1大匙(30克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '鸡脯肉400克；炸花生米50克；鸡蛋1个；大蒜5瓣；淀粉适量；干辣椒10个；注：可酌情配料');
INSERT INTO `recipe` VALUES (5938, 443, '雪菜毛豆鸡丁', '1.鸡肉洗净，切丁，放入碗中加淀粉、酱油拌匀，腌15 分钟；\r\n2.雪里蕻洗净，切末；毛豆仁去皮，洗净；红辣椒去蒂、洗净，切丁；\r\n3.锅中倒入食用油，依次放入辣椒片、毛豆仁炒香；\r\n4.再加入鸡肉丁及雪里蕻炒熟，调入味精、料酒炒匀即可。', '清淡爽脆，微辣爽口。', '毛豆仁可以先煮熟再去皮，这样处理后不用长时间煸炒。', '食用油30克；酱油3小匙(9克)；料酒1小匙(3克)；味精1/2小匙(1.5克)', '鸡肉400克；雪里蕻40克；毛豆仁15克；红辣椒2个；淀粉适量');
INSERT INTO `recipe` VALUES (5939, 443, '菠萝鸡丁', '1.葱洗净切段，姜洗净切片，红椒洗净切丝，青椒洗净切片；鸡腿去骨，用刀背把肉拍松后切丁，用酱油、盐、酒、糖腌5 分钟；\r\n2.锅里倒入食用油，烧热，将鸡肉过油捞出备用；\r\n3.锅内留底油，爆香葱、姜，加入菠萝块及辣椒，再将鸡丁倒入拌炒，淋上水淀粉即可。', '鲜香甜酸，美味爽口。', '可以先用沸水将罐装菠萝烫一下，再加入菜中，味道更好。', '食用油500克；（实耗30克）；酱油1小匙(3克)；料酒1小匙(3克)；精盐1/2小匙(1.5克)；白糖1/2小匙(1.5克)', '鸡腿300克；罐装菠萝3片；红辣椒1个；青辣椒1个；大葱1根；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5940, 443, '东安子鸡', '1.鸡洗净抹干，放入蒸锅蒸15分钟至七成熟，取出放凉后切块，加盐、糖腌10分钟；葱、姜、洗净切末；\r\n2.下油烧热，放入大料、花椒慢火炒香，下姜、葱爆香，再下鸡块炒匀，淋料酒后用大火炒数下；\r\n3.加入高汤、香醋、胡椒粉、盐、糖煮开，用水淀粉勾芡即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜辣可口，香嫩诱人。', '此菜最好选用仔鸡，仔鸡的肉质更滑嫩。', '食用油30克；高汤2大匙(60克)；料酒2小匙(6克)；胡椒粉少许；香醋1/3小匙(1克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '鸡1只(约800克)；大葱4根；生姜1小块；花椒适量；大料适量；淀粉适量');
INSERT INTO `recipe` VALUES (5941, 443, '三杯鸡', '1.鸡腿洗净剁成块状，生姜、蒜切片，辣椒斜切成环状；香菜洗净备用；\r\n2.将鸡肉块放入烧热的油锅中，先用小火炸约5分钟，再调大火炸约3 分钟，至表面呈金黄色即可捞起沥油；\r\n3.锅内留底油，爆香姜片、蒜片、辣椒，加入炸好的鸡肉块拌炒，再将香油、蚝油、料酒、豆瓣酱、冰糖放入，用小火煮至汤汁呈浓稠状，最后加入香菜，迅速拌炒均匀即可。', '色泽红润，肉嫩适口。', '鸡腿要切成小块，否则不容易入味。', '食用油500克；（实耗30克）；香油1大匙(30克)；蚝油1大匙(30克)；料酒1大匙(30克)；豆瓣酱1大匙(30克)；冰糖适量', '鸡腿600克；红辣椒1个；青辣椒1个；生姜1块；大蒜10瓣；香菜2棵');
INSERT INTO `recipe` VALUES (5942, 443, '砂锅土鸡', '1.鸡腿洗净切块，放入油锅炸至五成熟，捞起沥干；姜、蒜洗净切片；辣椒洗净；香菜洗净切小段；\r\n2.锅内留底油，爆香姜、蒜，再放入辣椒、鸡块煸炒，加入料酒、酱油、糖及适量清水，烧约15 分钟至鸡肉入味；\r\n3.把砂锅烧热，将烧好的鸡放进去，撒上香菜即可。', '鲜香诱人，味道醇厚。', '如果先把鸡放到油锅里爆一遍，再放入砂锅慢慢炖熟，鸡肉的香味更浓。', '食用油500克；（实耗50克）；酱油2大匙(60克)；料酒2大匙(60克)；白糖1/2大匙(15克)', '土鸡腿450克；生姜1块；大蒜8瓣；香菜1棵；干辣椒1个');
INSERT INTO `recipe` VALUES (5943, 443, '多味鸡块', '1.将鸡宰杀洗净；香葱洗净切段；姜洗净切片；\r\n2.往锅里放适量水，烧开，把鸡放进去，同时加入葱段、姜片、盐，盖上锅盖用大火煮10 分钟，将鸡捞出，切成小块，放在盘内；\r\n3.把芝麻酱放在小碗里，加入盐和煮鸡的汤，调成糊状；把辣椒粉放在小碗内加少许盐调和，把食用油烧热，浇在辣椒粉上，拌匀；\r\n4.把花椒粉均匀地撒在鸡块上，再撒上糖、盐，倒入酱油、醋、味精、辣椒粉和芝麻酱，拌匀后即可。', '色泽美观，酸咸麻辣。', '此菜口味多样，可根据自己喜好增减调味料。', '食用油10克；酱油1小匙(3克)；芝麻酱1小匙(3克)；花椒粉适量；辣椒粉适量；香醋1小匙(3克)；精盐3小匙(9克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '童子鸡1只（约500克）；香葱3棵；生姜1小块');
INSERT INTO `recipe` VALUES (5944, 443, '葱油鸡块', '1.把鸡洗净剁块，用酱油、料酒拌匀腌20 分钟；葱切末，姜切片；\r\n2.将食用油倒入炒锅内烧至八成热，把鸡块倒入炸透，捞出沥油；\r\n3.将葱油放入锅内，烧热后放入葱花、姜片爆香，烹入料酒、酱油，加入清水、桂皮、盐和糖；\r\n4.烧开后下入鸡块，改小火慢烧至熟，加入味精，用水淀粉勾芡即可。', '葱香浓郁，鸡块入味。', '烧热葱油时要用小火，这样才可慢慢炸出所有的葱香味。', '食用油500克；葱油（实耗50克）；酱油50克；料酒1/2大匙(15克)；精盐3小匙(9克)；白糖1小匙(3克)；味精2小匙(6克)；1/2小匙(1.5克)', '鸡肉500克；香葱3棵；生姜1小块；桂皮适量；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5945, 443, '栗子鸡', '1.把栗子放入水中泡2小时，捞出、沥干；鸡腿洗净、切块；葱洗净、切段，姜去皮、切片；\r\n2.把鸡块放入碗中加盐及适量的水腌拌一下，再放入蒸锅蒸熟，取出鸡块，鸡汁留着备用；\r\n3.锅中倒油加热，放入鸡块快炒数下，加入栗子及鸡汁，再加入酱油、糖，盖上锅盖用小火焖一个半小时，最后加水淀粉勾芡即可。', '栗香鸡嫩，口味极佳。', '焖鸡时火一定要小，防止栗子碎成粉状。', '食用油30克；酱油1/2大匙(15克)；精盐1小匙(3克)；白糖1小匙(3克)', '鸡腿300克；栗子100克；香葱2棵；生姜1小块；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5946, 443, '咖哩鸡块', '1.鸡肉洗净、切块；葱洗净，切末；一起放入碗中加酱油、料酒抓拌，腌15 分钟；姜洗净切片；\r\n2.土豆、胡萝卜去皮洗净，切小块，放入沸水中，煮熟后捞出、沥干；\r\n3.把食用油烧热，爆香姜片，放入鸡块翻炒均匀，再加入胡萝卜、土豆及适量水和盐，大火烧开后改小火煮至鸡肉入味，最后加咖喱粉、水淀粉勾芡即可。', '色泽淡黄，咖喱味浓。', '也可以先把咖喱粉放入锅内，再放入胡萝卜、土豆、鸡块慢慢炖。', '食用油30克；酱油1小匙(3克)；料酒1小匙(3克)；咖喱粉1大匙(30克)；精盐1小匙(3克)', '鸡脯肉300克；土豆100克；胡萝卜100克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5947, 443, '卤香鸡块', '1.将鸡宰杀，洗净；葱洗净切段，姜洗净切片；干辣椒洗净切段；\r\n2.把水烧开，放入鸡、花椒、辣椒、大料、盐、姜块、葱段、味精，烧开，撇去浮沫，改小火炖20 分钟，将鸡取出沥干；\r\n3.将鸡剁成块，装盘上桌即可。', '香气四溢，鲜嫩可口。', '关火后把鸡放在卤汁里浸泡10 分钟后再取出，香味更为醇厚。', '精盐2小匙(6克)；味精1/2小匙(1.5克)', '童子鸡1只(约500克)；香葱2棵；生姜1小块；大料适量；花椒适量；干辣椒5个');
INSERT INTO `recipe` VALUES (5948, 443, '豉香鸡块', '1.将鸡宰杀洗净，剁块；葱、姜洗净切末；蒜洗净切片；\r\n2.把油烧热，放入鸡块，炸成浅红色捞出；\r\n3.锅内留底油，放入辣椒粉、豆豉、葱、姜、蒜煸炒出香味，烹入料酒、酱油，加入一大碗开水，把鸡块、盐、味精、糖依次下锅，烧开，转小火慢烧，收浓汤汁出锅即可。', '香辣味美，骨酥肉烂。', '有些豆豉较咸，烹制时要先掌握好豆豉的咸度再放盐，以免菜味过咸。', '食用油500克；（实耗60克）；酱油3小匙(9克)；料酒3小匙(9克)；豆豉1大匙(30克)；辣椒粉1大匙(30克)；精盐2小匙(6克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '鸡1只(约750克)；香葱1棵；生姜1小块；大蒜8瓣');
INSERT INTO `recipe` VALUES (5949, 443, '椒香鸡块', '1.姜、蒜洗净切末，红椒洗净去籽切块；青椒洗净去籽切粗丝；\r\n2.鸡脯肉洗净，切小块；\r\n3.把食用油倒入锅内烧热，爆香蒜、姜，放入鸡块炒至七分熟后放入料酒、盐、糖炒匀即可。', '鲜嫩可口，色泽艳丽。', '如果用鸡腿肉做这道菜，口感也会很好。', '食用油30克；料酒1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '鸡脯肉500克；青辣椒1个；红辣椒2个；生姜1小块；大蒜2瓣');
INSERT INTO `recipe` VALUES (5950, 443, '银芽鸡丝', '1.鸡肉洗净切丝，先用盐、胡椒粉、料酒、淀粉拌匀，再放入烧热的油锅中，稍微过一下油，捞起后沥干备用；\r\n2.将绿豆芽头尾摘除洗净，大蒜洗净切末；红辣椒洗净切丝；\r\n3.锅内放香油，烧热，先爆香蒜，再加入高汤，煮开后，加入银芽、鸡丝、辣椒丝、盐拌匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '颜色淡雅，鲜美爽口。', '拌炒前先把豆芽加醋煸炒一下，豆芽的口感会更好。', '香油1/2大匙(15克)；料酒1大匙(30克)；高汤1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)', '鸡脯肉300克；绿豆芽300克；红辣椒1个；大蒜1瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5951, 443, '金菇鸡丝', '1.鸡脯肉和姜洗净后切丝，放入碗中加料酒、淀粉抓拌，并腌10 分钟；\r\n2.葱洗净、切丝，红辣椒去蒂，切块；金针菇切除根部，洗净备用；\r\n3.往锅里放油加热，放入鸡丝、金针菇及少许水炒熟，最后加盐调匀，盛起时撒上葱丝和红辣椒，淋上少许香油即可。', '味道鲜美，清淡适口。', '用料酒稍微腌一下，炒出来的鸡丝会更嫩。', '食用油30克；香油少许；料酒2小匙(6克)；精盐1小匙(3克)', '鸡脯肉250克；金针菇50克；红辣椒2个；香葱2棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5952, 443, '芙蓉鸡片', '1.鸡肉切成薄片，把鸡蛋清滤入深盘内，用筷子打成泡沫状；火腿切末；\r\n2.用蛋清和淀粉把鸡肉片浆好；\r\n3.炒锅上火烧热，加入食用油，烧至五成热时，下入浆好的鸡片，用筷子拨散滑熟，捞出沥油；\r\n4.锅内留底油，加鸡汤、盐、味精，烧开后用水淀粉勾芡，推炒几下，再加入滑好的鸡片翻炒均匀即可盛入盘内，撒上火腿末、热鸡油即成。', '颜色鲜艳，肉嫩可口。', '打蛋时最好不要间断，直到打成泡沫状为止。', '食用油500克；(实耗50克)；鸡油2小匙(6克)；鸡汤150克；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸡脯肉300克；火腿25克；鸡蛋2个；淀粉适量');
INSERT INTO `recipe` VALUES (5953, 443, '豆荚炒鸡柳', '1.甜豆荚洗净，去头尾及老茎；姜及胡萝卜均去皮、洗净、切片；\r\n2.鸡蛋打入碗中，滤除蛋黄，留蛋清备用；\r\n3.鸡脯肉洗净，切条，放入碗中加入蛋清、盐和淀粉抓拌均匀，腌15 分钟；\r\n4.锅中倒油烧热，放入鸡脯肉滑至变色，捞出沥油；\r\n5.锅中留底油烧热，爆香姜片，放入甜豆荚、胡萝卜、盐、味精用大火炒熟，加入鸡肉炒匀即可。', '口味清淡，爽滑可口。', '去除甜豆荚的老茎时，应从蒂头开始，沿着豆荚两边撕除老茎，这样才能保持豆荚头尾完整，避免翻炒时豆荚裂开。', '食用油200克；（实耗30克）；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸡脯肉200克；甜豆荚300克；胡萝卜50克；鸡蛋1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5954, 443, '干烧鸡翅', '1.将鸡翅用盐、料酒、胡椒粉、鸡蛋清、淀粉拌匀腌制；葱、姜、蒜洗净切末；\r\n2.锅内放油烧至六成热，将鸡翅逐条放入炸成金黄色时捞出；\r\n3.锅中留底油，将豆瓣酱炒出红色，加高汤稍煮，捞去豆瓣渣，放入鸡翅、姜、蒜、葱、辣椒、酱油、料酒、糖、盐，烧透入味，加味精，随即将汁收浓，装盘即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽深红，味咸鲜辣。', '油炸后的鸡翅容易吸收水分，所以收汁时要防止烧干。', '食用油50克；酱油1小匙(3克)；高汤2大匙(60克)；料酒1大匙(30克)；胡椒粉1/2小匙(1.5克)；豆瓣酱1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '鸡翅400克；鸡蛋2个；香葱2棵；生姜1小块；大蒜8瓣；淀粉适量；干辣椒5个');
INSERT INTO `recipe` VALUES (5955, 443, '贵妃鸡翅', '1.鸡翅取中段部分，摘光细毛，洗净，用热水氽过备用；葱切长段；姜切片；冬笋切片；\r\n2.炒锅内放入油，先下冰糖炒溶，待糖液成金黄色时，再放入鸡翅同炒，同时加入葱、姜炒香，调入盐、味精、酱油调味，待翻炒均匀后，倒入酒、高汤，用小火煮20 分钟；\r\n3.最后放冬笋片，改用大火烧30 秒，汤汁快干时，加水淀粉勾芡，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽淡红，口味极佳。', '糖液温度很高，搅动时注意安全，不要溅到手上，以免烫伤。', '食用油30克；香油1小匙(3克)；酱油3小匙(9克)；高汤2大匙(60克)；料酒3小匙(9克)；精盐1小匙(3克)；冰糖适量；味精1/2小匙(1.5克)', '鸡翅400克；冬笋10克；香葱2棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5956, 443, '五香凤爪', '1.把鸡爪洗净，切去爪尖，纵切两半；炒锅放大火上，放入食用油，烧至八成热时，将鸡爪下锅，炸至皮酥、呈金色时捞出沥油；\r\n2.把高汤、葱、姜、料酒、大料、花椒、丁香、肉桂、陈皮、酱油、盐、白糖、鸡爪放入砂锅，调中火，烧开后转用小火，盖上砂锅煮2小时，至肉质酥烂，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鸡爪酥烂，香味可口。', '关火后，让凤爪在汤汁中多浸泡一会，凤爪会更入味。', '食用油300克；（实耗30克）；香油1小匙(3克)；酱油1小匙(3克)；高汤适量；料酒2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '鸡爪500克；香葱2棵；生姜1小块；大料适量；花椒适量；肉桂适量；陈皮适量；丁香适量');
INSERT INTO `recipe` VALUES (5957, 443, '川味小煎鸡', '1.鸡腿肉去骨，用刀拍松，斩成长约5厘米、宽约1 厘米的一字条形，放入碗中，加盐、料酒、淀粉和匀；\r\n2.青笋切成条状，用少许盐腌一下，洗净；泡椒、芹菜、葱切成小段；姜、蒜洗净切末；\r\n3.把酱油、醋、白糖、盐、料酒、味精、淀粉、高汤兑成芡汁；\r\n4.往锅内倒油，烧热，下鸡肉炒散，加泡椒、姜、蒜炒出香味，再下青笋、芹菜、葱炒匀，汁收浓后即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '质嫩爽口，香辣微甜。', '炒鸡肉时，油温不要太高，否则鸡肉不容易划散。', '食用油50克；酱油3小匙(9克)；料酒2/3大匙(20克)；高汤2大匙(60克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '鸡腿300克；青笋50克；泡椒15克；芹菜25克；香葱1棵；生姜1小块；大蒜6瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5958, 443, '香芋焖鸡腿', '1.芋头去皮洗净，放入蒸锅中蒸40 分钟；葱洗净切段，姜洗净切片；鸡腿洗净切块；\r\n2.往锅内放油，烧热，爆香葱、姜，加入鸡块及所有调味料，用小火煮30 分钟，再放入芋头，开大火收干汤汁即可。', '鲜香诱人，味道醇厚。', '做菜时加入一点糖，可使得味道更加鲜美。', '冰糖适量；酱油1大匙(30克)；料酒1大匙(30克)', '芋头200克；鸡腿200克；大葱1根；生姜1小块；注：可酌情配料');
INSERT INTO `recipe` VALUES (5959, 443, '鲜蘑鸡腿', '1.鸡腿洗净，切成两段，拌入酱油腌10 分钟，再用热油炸上色，捞出；\r\n2 把蘑菇洗净后放到烧开的盐水中氽烫，捞出冲凉后切片；\r\n3.洋葱洗净切块，用适量油炒香后，把蘑菇倒入同炒，再加料酒、番茄酱、蚝油、糖、胡椒粉；\r\n4.放入鸡腿，改小火烧入味，待鸡腿熟软时，翻炒至汤汁稍收干即可盛出。', '营养丰富，鲜香可口。', '用盐水氽烫蘑菇可以去除蘑菇上的黏液并防止蘑菇变色，使其口感更清爽。', '食用油500克；（实耗50克）；蚝油1/2大匙(15克)；酱油2小匙(6克)；料酒1大匙(30克)；番茄酱1大匙(30克)；胡椒粉少许；白糖1/2大匙(15克)', '鸡腿300克；蘑菇75克；洋葱20克');
INSERT INTO `recipe` VALUES (5960, 443, '香酥炸鸡腿', '1.鸡腿洗净后用酱油、料酒、胡椒粉、白糖腌20分钟，再用蛋清拌匀，沾上淀粉；\r\n2.烧热食用油，把腌好的鸡腿炸至金黄即可。', '色泽金黄，口感香脆。', '炸鸡腿时油温不要过高，否则不够香酥。', '食用油500克；（实耗50克）；酱油2小匙(6克)；料酒1小匙(3克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2大匙(15克)', '鸡腿300克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (5961, 443, '酸豆角炒鸡胗', '1.酸豆角汆水后待用；鸡胗洗净切小块，放入油锅中滑熟；姜、蒜洗净切末；\r\n2.把锅中余油烧热，放入姜、蒜炒香，加入酸豆角、鸡胗翻炒，加入酱油、料酒、盐、糖、味精炒匀即可。', '脆嫩爽口，微酸开胃。', '把鸡胗倒入油锅滑炒时动作要快，防止鸡胗变硬变老。', '食用油30克；酱油1/2大匙(15克)；料酒1/2大匙(15克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2大匙(15克)', '鸡胗300克；酸豆角100克；生姜1小块；大蒜4瓣');
INSERT INTO `recipe` VALUES (5962, 443, '草菇鸡心', '1.鸡心剥除外层薄膜及血管后洗净，在心尖部分划米字形刀口，全部切好后放入沸水中汆烫，并加酒腌10 分钟，捞出洗净；\r\n2.草菇剥去外膜、洗净，用沸水氽烫时往水中加盐，快速烫过即捞出，冲凉；\r\n3.红椒剖开，去籽后洗净，切块；青椒洗净切段；葱、姜洗净切末；\r\n4.烧热油锅，爆香葱、姜后放入鸡心和辣椒同炒；\r\n5.再倒入草菇，加入酒、蚝油、胡椒粉、糖、盐、水淀粉、味精，炒匀即可盛出。', '色泽红亮，鲜咸适中。', '一定要用清水把鸡心浸泡1 小时，以去掉血污，否则炒制时鸡心容易变硬。', '食用油25克；蚝油1大匙(30克)；料酒1大匙(30克)；胡椒粉少许；精盐1大匙(30克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '鸡心225克；草菇150克；红辣椒1个；青辣椒1个；大葱2根；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5963, 443, '丝瓜炒鸡肝', '1.丝瓜洗净去皮去籽，切片；鸡肝洗净切薄片，胡萝卜洗净切片，生姜洗净去皮切片；\r\n2.用少许盐、料酒、水、淀粉将鸡肝腌5 分钟；把鸡肝放入沸水中烫至八成熟时捞起沥水；\r\n3.往锅里放油，烧热，下入姜、丝瓜、胡萝卜片翻炒片刻，加入鸡肝、胡椒粉、盐、糖、味精炒透，用水淀粉勾芡，淋入香油，出锅即成。', '清淡香浓，味道鲜美。', '一定要把鸡肝洗干净，否则会有苦味，影响菜肴的味道。', '食用油25克；香油1小匙(3克)；料酒1小匙(3克)；胡椒粉少许；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '丝瓜200克；鸡肝200克；胡萝卜50克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5964, 443, '酸菜炒鸭块', '1.鸭肉洗净、剁块；姜洗净、切片；把鸭、姜片一起放入沸水中煮30 分钟，捞起；\r\n2.大蒜洗净切末；红辣椒去蒂、籽后切段；干辣椒洗净切段；\r\n3.锅中倒油烧热，爆香姜片和蒜末，放入鸭块拌炒均匀，加入酸菜、红辣椒、干辣椒、料酒和适量水，用小火烧15 分钟，再加酱油、盐、味精炒熟即可。', '浓香味美，开胃下酒。', '煨制的时间要稍长一些，才能使酸菜和鸭肉相互入味。', '食用油40克；酱油1小匙(3克)；料酒2大匙(60克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '鸭肉600克；酸菜150克；红辣椒2个；生姜1小块；大蒜2瓣；干辣椒2个');
INSERT INTO `recipe` VALUES (5965, 443, '麻辣鸭块', '1.大葱洗净后纵向剖开，一半切末，一半切段；姜洗净后一半切片，另一半切末；甜豆荚洗净备用；\r\n2.将鸭子宰杀洗净，剁成均匀的小长方块放入盆内，加入姜片、葱段、料酒，放蒸锅内用大火蒸熟后取出，放入盘内；\r\n3.往锅内放油烧热，投入葱、姜末、干辣椒、甜豆荚，炒出香味后加入花椒粉、酱油、白糖、精盐、料酒、醋；\r\n4.烧开后加入味精，盛入小碗中，浇在鸭块上，最后淋上香油即可。', '肉质酥烂，麻辣爽口。', '此菜可根据自己喜好控制调味料的分量。', '食用油30克；香油1小匙(3克)；酱油1大匙(30克)；料酒2大匙(60克)；花椒粉1大匙(30克)；香醋1/2大匙(15克)；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '鸭子1只（约650克）；甜豆荚10克；大葱1根；生姜1块；干辣椒6个');
INSERT INTO `recipe` VALUES (5966, 443, '红煨鸭块', '1.鸭洗净切块，葱洗净切段，姜、蒜洗净切片；辣椒洗净切片；\r\n2.锅内放油，烧热，把带油的和较肥的鸭块放入锅内，煸炒出油；冬笋洗净切片；\r\n3.接着放入葱、姜、蒜、辣椒、大料和其余的鸭块煸炒，至肉色变白时，烹入料酒，放入高汤、酱油、白糖、冬笋，改小火煨；\r\n4.约煨1 小时左右，再开大火，加味精收汁，并把煨出来的鸭油撇掉，淋入香油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肉质软烂，味道浓香。', '因为煨鸭块的时间较长，所以要经常查看，以免汤汁烧干。', '食用油30克；香油2小匙(6克)；酱油3大匙(90克)；高汤8大匙(240克)；料酒1大匙(30克)；白糖1大匙(30克)；味精1/2小匙(1.5克)', '鸭子1只（约650克）；冬笋100克；红辣椒1个；大葱2根；生姜1块；大蒜15瓣；大料适量');
INSERT INTO `recipe` VALUES (5967, 443, '姜汁清香鸭', '1.姜洗净，一大半切丝，其余磨成碎屑，用纱布挤出姜汁；\r\n2.鸭洗净，切块，放入沸水中汆烫，捞出；\r\n3.锅内倒油烧热，放入姜丝爆炒出香味，加入鸭块一起煸炒，加盐，再加入料酒和少量水煮开，关火；\r\n4.将鸭块盛在碗中移入蒸锅，加入姜汁，用大火蒸约2 小时，最后撒上姜丝即可。', '汤鲜肉嫩，味道鲜美。', '如果将鸭皮上的脂肪除去，鸭子汤会更加清新可口。', '食用油30克；料酒1/2大匙(15克)；精盐2小匙(6克)', '鸭子1/2只（约300克）；生姜1大块');
INSERT INTO `recipe` VALUES (5968, 443, '盐水鸭块', '1.将花椒与盐放炒锅内置火上炒热，即成花椒盐；\r\n2 .鸭子洗净后抹干水分，将花椒盐趁热擦遍鸭子内外，腌1 天后挂在通风处晾至半干；\r\n3.将腌好的鸭子剁成鸭块，放入盆内；\r\n4.葱切去根，洗净，斜切成薄片；姜洗净，切成片。把葱片和姜片放在鸭块上，加入桂皮、大料、料酒、白糖，并加适量水，放在蒸锅内蒸熟，取出鸭块装盘即可。', '清香味鲜，爽口下酒。', '要撇净蒸鸭时的血沫，以免影响鸭肉的味道。', '料酒2大匙(60克)；精盐3小匙(9克)；白糖1小匙(3克)', '鸭子1/2只（约300克）；生姜1块；大葱1根；花椒适量；桂皮适量；大料适量');
INSERT INTO `recipe` VALUES (5969, 443, '冬菜扣鸭', '1.葱、姜洗净切末；冬菜洗净切小块；鸭子宰杀后洗净，用盐、料酒、姜、葱、五香粉抹遍鸭身内外，腌制约40 分钟取出；\r\n2.把鸭子放入蒸锅内蒸熟后，取出擦干水分，放入热油锅中，炸至金黄色时捞出，切成长5 厘米、宽2 厘米的鸭条，皮朝下摆入蒸碗中，调入盐、味精、酱油及料酒；\r\n3.最后放入冬菜，放入蒸锅蒸约30分钟取出，翻扣入盘，淋上香油即成。', '香味浓厚，口感极佳。', '蒸鸭时的火候要稳，这样才能蒸出好吃的鸭子。', '食用油500克；（实耗50克）；香油1小匙(3克)；酱油1大匙(30克)；料酒1大匙(30克)；五香粉1小匙(3克)；精盐4小匙(12克)；味精1/2小匙(1.5克)', '鸭子1只（约650克）；冬菜100克；大葱2根；生姜1小块');
INSERT INTO `recipe` VALUES (5970, 443, '红糟鸭煲', '1.鸭洗净剁块，放沸水中汆烫5分钟后捞出沥干；姜、葱洗净切末；\r\n2.锅内放油，烧热，爆香姜、葱，加入红糟及鸭炒香，淋入料酒，盛起放入砂锅内；\r\n3.往砂锅内加入盐和水，煮沸，改小火煲约1小时，下糖调匀，再煲约10 分钟，取出葱不要，下淀粉勾芡即可。', '色泽美观，鸭烂味浓。', '煲鸭肉时要经常翻动，以防砂锅糊底。', '料酒2大匙(60克)；红糟3大匙(90克)；精盐2小匙(6克)；白糖2小匙(6克)', '鸭子1只（约650克）；大葱8根；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5971, 443, '姜芽炒鸭片', '1.将宰杀好的鸭去掉骨头，切成薄片，用鸡蛋清、湿淀粉、料酒、盐搅拌均匀，把鸭片浆好后放沸水中氽熟；姜芽洗净切片，用少许盐腌渍，挤去水分；丝瓜洗净切块；蒜洗净剁成茸；\r\n2.把番茄酱、糖、醋、料酒、淀粉和清水放入锅内烧开，冷却后把姜芽放进去腌3 小时左右取出；\r\n3.再往腌姜芽的糖醋汁里加入味精、蒜茸和湿淀粉，调制成稀芡；\r\n4.锅内放油，烧热，放入鸭片炒约1 分钟左右，再将姜芽、丝瓜等放入炒锅内，用糖醋汁勾芡后翻炒2分钟即成。', '酸甜微辣，清脆淡爽。', '为使鸭肉滑嫩，炒鸭片时动作要快。', '食用油50克；料酒2/3大匙(20克)；番茄酱1大匙(30克)；香醋1/2大匙(15克)；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '鸭子1/2只（约300克）；姜芽300克；丝瓜50克；鸡蛋2个；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5972, 443, '酸辣鸭肠', '1.把鸭肠上的白油除净，去掉盲肠、直肠，剖开洗干净后慢慢理顺，用一根细绳把肠子从中间系紧，放入盆里，加上适量盐、醋浸泡一会，用手慢慢揉搓，待揉出白泡沫时，马上用水洗净；辣椒、葱洗净切丝；\r\n2.把肠子放沸水里烫至稍微有点卷缩、颜色变白时，尽快捞出放进凉水中，解开绳子；\r\n3.等肠子泡凉后，捞出切小段，然后再放入沸水中烫一下，沥净水分待用；\r\n4.把辣椒和葱放在一起，加入料酒、酱油、盐、味精、醋调成味汁；\r\n5.烧热炒锅，倒入葱油，加入调好的味汁，下入鸭肠炒10秒钟左右，再加入辣椒油搅拌均匀，最后淋入香油即成。', '清淡脆嫩，酸辣美味。', '烹制鸭肠时速度要快，以免鸭肠变老，变硬。', '葱油60克；香油2小匙(6克)；辣椒油3小匙(9克)；酱油3小匙(9克)；料酒1/2大匙(15克)；精盐1小匙(3克)；香醋2小匙(6克)；味精1/2小匙(1.5克)', '鸭肠400克；红辣椒2个；青辣椒2个；黄辣椒2个；大葱1根');
INSERT INTO `recipe` VALUES (5973, 443, '冬笋烧鸭肝', '1.去掉鸭肝上靠近胆的那部分，洗净切成薄片，放入沸水中稍烫，用冷水冲凉，整齐地码放在盘内；冬菇洗净、冬笋切成片；葱切段；姜、蒜切片；\r\n2.往锅里倒入油，烧热，投入大料、葱、姜、蒜，炸出香味后加入料酒、酱油、高汤烧开，捞出作料，放入鸭肝、冬笋、冬菇，投入味精、盐，用水淀粉勾芡，淋入香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽金红，醇香适口。', '鸭肝汆水的时间不要过长，以免影响鸭肝的口感。', '食用油30克；香油1小匙(3克)；酱油3小匙(9克)；高汤2小匙(6克)；料酒3小匙(9克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸭肝400克；冬笋25克；冬菇25克；大葱1根；生姜1小块；大蒜8瓣；大料适量；淀粉适量');
INSERT INTO `recipe` VALUES (5974, 443, '黄焖鸭肝', '1.将鸭肝放入沸水中焯过，洗净，切成长条；冬笋洗净切片；冬菇洗净分别对切成两半；冬笋、冬菇均放入沸水中焯过；葱、姜洗净切末；\r\n2.将20克猪油放入炒锅内，烧至七成热时，放入白糖，炒成酱红色时，放入酱油、料酒、味精、高汤、葱、姜、冬笋、冬菇、鸭肝， 焖至入味后倒入碗内。再把另一半猪油烧至八成热，放入甜面酱稍稍煸炒后淋在鸭肝上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '营养丰富，酥香美味。', '一定要把鸭肝洗干净，以去除苦味。', '猪油40克；酱油1/2大匙(15克)；高汤10大匙(300克)；料酒1大匙(30克)；甜面酱1大匙(30克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '鸭肝500克；冬菇20克；冬笋30克；大葱1根；生姜1小块');
INSERT INTO `recipe` VALUES (5975, 443, '萝卜烧板鸭', '1.将板鸭剁块，加入生姜煮熟；将萝卜洗净切块，汆水后捞出；蒜洗净拍松；香菜洗净切段；\r\n2.锅中下油，加入板鸭、萝卜翻炒几下，加入高汤、料酒、味精、蒜后用小火烧，汁浓后放入香菜炒匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '咸香适口，开胃下酒。', '板鸭本身有盐，烧制时要控制好咸度。', '酱油1小匙(3克)；高汤2大匙(60克)；料酒2小匙(6克)；味精1小匙(3克)', '板鸭1只（约400克）；白萝卜50克；香菜1棵；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (5976, 443, '麻辣狗肉', '1.狗肉洗净后用清水煮至八成熟，晾凉后切块；蒜洗净拍松；\r\n2.锅内放油，烧热，加入蒜、干辣椒、花椒爆香，投入狗肉，加入料酒、橘皮、高汤、酱油、盐、白芝麻、味精后用小火炖烂，改大火收汁即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽油亮，麻辣软烂。', '烹制前，先把狗肉放在清水中浸泡1 小时，可去除狗肉的膻味。', '食用油50克；酱油1大匙(30克)；高汤适量；料酒1/2大匙(15克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '狗肉500克；大蒜10瓣；白芝麻10克；花椒1/2大匙(15克)；新鲜橘皮10克；干辣椒10个');
INSERT INTO `recipe` VALUES (5977, 443, '罐子狗肉', '1.把狗肉洗净，放入清水中浸泡1 小时，然后捞出洗净，切成两块，放入锅内，加入凉水，煮至狗肉断生时，捞出，洗净；葱洗净切丝；蒜、姜洗净切片；\r\n2.将狗肉块放入锅内，加清水，水要淹没肉块，再加入葱、姜、料酒，盖上盖，用大火烧开，再改用小火焖至八成熟，捞出，拆骨，再将骨头放回汤中继续煮；狗肉沥水后切片；\r\n3.往炒锅内倒油，下入狗肉煸炒几下，烹入料酒、狗骨汤（以淹没狗肉为宜）、干辣椒、盐、味精，盖好盖，焖5分钟，再将焖好的狗肉和汤装在瓦罐内，上笼蒸30 分钟，上桌时，撒上芝麻和胡椒粉即可。', '味道鲜美，软嫩咸香。', '用清水浸泡狗肉时，可加点大蒜，放几个朝天椒，以去除狗肉的膻味。', '食用油30克；料酒3大匙(90克)；胡椒粉2小匙(6克)；精盐3小匙(9克)；味精1/2小匙(1.5克)', '带皮狗肉500克；熟芝麻20克；大葱2根；生姜1块；大蒜10瓣；干辣椒10个');
INSERT INTO `recipe` VALUES (5978, 443, '荷香乳鸽', '1.将木耳洗净切成丝；黄花菜、冬菇洗净切成段；葱洗净切末；姜洗净切片；荷叶洗净，用开水煮透晾凉；\r\n2.鸽子宰杀后洗净，放入酱油、料酒、白糖、味精、葱、姜腌制10～15 分钟；里脊肉洗净切丝，加入盐、料酒、淀粉、酱油、香油腌制入味；\r\n3.往锅里放油，油温升至四成热时，倒入葱姜炒出香味，放入肉丝、料酒、木耳丝、黄花、冬菇炒匀盛入盘中，晾凉后填入鸽子腹中；将荷叶铺平，把鸽子包好，放到蒸锅中蒸30 分钟即可。', '荷香鸽嫩，味道鲜美。', '这道菜借助于荷叶，使鸽肉更加清香。', '食用油20克；香油1小匙(3克)；酱油1小匙(3克)；料酒1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '乳鸽1只；猪里脊肉50克；木耳25克；黄花菜25克；冬菇25克；香葱2棵；生姜1小块；荷叶1张；淀粉适量');
INSERT INTO `recipe` VALUES (5979, 443, '扒兔肉', '1.将兔肉切成长条；葱、蒜、姜洗净切片；\r\n2.把锅烧热，放入鸡油，烧至六成热时投入葱、蒜、姜，炒出香味，放入兔肉条、辣椒酱、花椒粉、酱油、精盐、高汤；\r\n3.烧开后，撇去浮沫，用小火烧至肉烂汁浓时，加味精、香油，再用水淀粉勾芡即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '咸辣适口，肉糯鲜香。', '新鲜兔肉有点腥，所以在烹制前可将兔肉放入清水中浸泡1 小时。', '鸡油1大匙(30克)；香油1小匙(3克)；酱油2小匙(6克)；高汤5大匙(150克)；花椒粉2小匙(6克)；辣椒酱1/2大匙(15克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '兔肉300克；大葱2根；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5980, 443, '五香驴肉', '1.驴肉洗净备用；香料洗净沥水；\r\n2.往汤锅里加水、盐、味精、酱油、冰糖，煮开后即成酱汤，再加入陈皮、草果等香料煮30 分钟左右；\r\n3.把驴肉投入酱汤中煮熟后捞出，可以切片直接食用，也可以用新鲜辣椒拌炒。', '色泽红润，肉质鲜嫩。', '酱汤中不要放入过多的香料，否则容易抢了驴肉的本味。', '酱油1大匙(30克)；精盐2小匙(6克)；冰糖适量；味精1/2小匙(1.5克)', '驴肉350克；陈皮2克；草果2克；香叶1克；桂皮2克；大料2克；丁香1克');
INSERT INTO `recipe` VALUES (5981, 443, '玉楼姜味蛇', '1.将活蛇宰杀洗净，剁成5 厘长的段，焯水；葱洗净切花；\r\n2.锅内放油烧至七成热，把蛇放入过油，沥出；\r\n3.锅内留底油，放入高汤、味精、蛇段，煨烧入味后，用水淀粉勾芡，撒上葱花，淋入香油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '肉质鲜美，细腻爽口。', '蛇也可以用来清炖，味道非常鲜美。', '食用油800克；（实耗80克）；香油1小匙(3克)；高汤1/2大匙(15克)；味精1/2小匙(1.5克)', '活蛇1条（约750克）；香葱2棵；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5982, 443, '家常鹅肉', '1.将鹅肉洗净切块；青笋切滚刀块；葱、姜切片；\r\n2.将油烧热，把豆瓣酱、葱片、姜片下锅煸炒，待出香味后，把鹅肉放入稍炒，烹入料酒、酱油，放入精盐、味精后继续煸炒；\r\n3.待肉上色时，加入适量开水，烧开后，转小火慢烧，待肉将熟时放入辣椒、青笋，青笋烧熟后即可。', '咸鲜适口，鹅肉香浓。', '鹅肉的肉质较粗，烹制时要多炖一会，烧至鹅肉完全酥烂后口感更佳。', '食用油50克；酱油3小匙(9克)；料酒3小匙(9克)；豆瓣酱1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鹅肉300克；青笋100克；红辣椒2个；大葱1根；生姜1小块');
INSERT INTO `recipe` VALUES (5983, 443, '清炒牛蛙', '1.芥蓝洗净切斜片，牛蛙洗净剁成块，胡萝卜去皮切菱形片，生姜去皮切片，大蒜切片；\r\n2.在牛蛙中加入少许鸡蛋清，调入少许盐、味精、料酒、水、淀粉腌好；\r\n3.往锅里放油，烧至八成热时投入牛蛙，把牛蛙浸炸至八成熟倒出；\r\n4.锅内留底油，放入姜、蒜爆香，下入芥蓝片、胡萝卜片，炒至断生，再加入牛蛙，调入盐、味精、胡椒粉炒透入味，然后用水淀粉勾芡，淋入香油即可。', '味道鲜美，清香滑嫩。', '牛蛙肉质滑嫩，下油锅炸时不要炸老了。', '食用油500克；(实耗30克)；香油1小匙(3克)；料酒1/2大匙(15克)；胡椒粉少许；精盐1小匙(3克)；味精1/2小匙(1.5克)', '牛蛙300克；芥蓝100克；胡萝卜10克；鸡蛋1个；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5984, 443, '蒜香牛蛙', '1.将牛蛙宰杀洗净，每只切成8 块，放入碗中，加入糖、橄榄油、酱油、盐、料酒拌匀并腌30分钟备用；\r\n2.大蒜去皮，放入热油中炸至淡黄色，盛出；姜洗净，切片；葱洗净切段；\r\n3.往锅内倒油，烧热，放入姜片爆香，加入牛蛙炒匀，再加入大蒜、胡椒粉、水、味精，用大火煮开，转小火加盖焖煮25 分钟，撒上葱即可。', '肉质细嫩，味道鲜美。', '如果喜欢吃辣的，可以多放点干辣椒。', '食用油30克；橄榄油1小匙(3克)；酱油1/2大匙(15克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '牛蛙500克；香葱1棵；生姜1小块；大蒜10瓣');
INSERT INTO `recipe` VALUES (5985, 444, '干烧冬笋', '1.冬笋选用较嫩的尖部，切成大片，然后顺其纤维方向用刀略切小口，用手撕成长条；榨菜切末；\r\n2.锅烧热入油，放入笋块，用大火煸炒至其表面成深黄色时加入精盐、酱油、白糖、料酒、胡椒粉和高汤；\r\n3.用小火烧10～20分钟，待汁将收净时，加入榨菜末、味精，翻炒均匀即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '咸中带甜，香味浓郁。', '如果加点五花肉肉末，味道更鲜香。', '食用油30克；酱油1/3大匙(10克)；高汤2大匙(60克)；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '冬笋500克；榨菜15克');
INSERT INTO `recipe` VALUES (5986, 444, '清炒玉兰片', '1.用淘米水或清水将玉兰片泡软后洗净，切小片，放入沸水中焯一下；将熟面筋切成薄片；\r\n2.把锅烧热，倒入油烧至六成热，加精盐，倒入玉兰片翻炒；\r\n3.再倒入熟面筋，加味精、精盐、胡椒粉，用水淀粉勾芡，翻炒均匀即成。', '脆嫩清香，咸鲜可口。', '玉兰片是用鲜嫩的冬笋或春笋加工而成的干制品，由于形状和色泽很像玉兰花的花瓣，故称“玉兰片”。', '食用油50克；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '玉兰片250克；熟面筋100克；淀粉适量；注：可酌情配料');
INSERT INTO `recipe` VALUES (5987, 444, '鲜蘑冬笋', '1.平菇、笋洗净，切片；葱、姜洗净切段、切片；\r\n2.将葱、姜、高汤、精盐、味精、淀粉放入碗中调成调味汁；\r\n3.锅内放油烧热后，将笋放入，略煸炒后，再放入平菇煸炒，然后放入调味汁，翻炒即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '口味咸鲜，色泽淡雅。', '新鲜平菇和冬笋放在一起炒，味道更加清香鲜美。', '食用油30克；高汤2大匙(60克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '新鲜冬笋400克；平菇50克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (5988, 444, '上汤芦笋', '1.将芦笋切寸段，咸鸭蛋、皮蛋切块，青红椒切圈；\r\n2.将芦笋放入沸水中汆烫后待用，把咸鸭蛋、皮蛋、辣椒圈、高汤放入锅内，加入所有调料，烧开，淋在芦笋上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '口味清淡，汤鲜笋脆。', '芦笋汆水的时间不要过长，以免影响芦笋的脆感。', '高汤1大匙(30克)；料酒1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '芦笋400克；咸鸭蛋1个；皮蛋1个；青辣椒1个；红辣椒1个；大蒜1瓣');
INSERT INTO `recipe` VALUES (5989, 444, '咸酥苦瓜', '1.将苦瓜切片，放入沸水中氽烫约3分钟后捞起备用；\r\n2.把氽烫过的苦瓜片与盐、淀粉搅拌均匀，放入油锅中，炸约2 分钟后捞起沥油；蒜、葱切末；\r\n3.锅内留底油，烧热，先爆香蒜、葱，再放入苦瓜片，快速拌炒均匀即可。', '风味独特，清脆可口。', '炸苦瓜的时间不要过长，否则苦瓜不够脆嫩。', '食用油300克；（实耗30克）；精盐1小匙(3克)', '苦瓜300克；大蒜3瓣；香葱1棵；淀粉适量');
INSERT INTO `recipe` VALUES (5990, 444, '苦瓜炒咸蛋', '1.苦瓜洗净，对半切后去籽，切成薄片；葱切花备用；\r\n2.锅中入油，烧热，放入苦瓜片炒至断生，盛出；\r\n3.将咸鸭蛋去壳切碎，用锅中余油炒至冒泡，再加入葱花及苦瓜，拌炒至入味，加味精即可。', '香脆可口，清爽去火。', '苦瓜不能炒得太软，要保持苦瓜的清脆感。', '食用油30克；味精1/2小匙(1.5克)', '苦瓜300克；熟咸鸭蛋1个；香葱2棵');
INSERT INTO `recipe` VALUES (5991, 444, '豉香苦瓜', '1.先将苦瓜洗净后剖成两片，去籽后切成丝，放入碗中，加盐腌制片刻，漂洗干净后再将豆豉剁成末；葱洗净切末；姜、蒜洗净切片；辣椒洗净切片；\r\n2.将锅置火上，烧热后加入食用油，下苦瓜煸炒至水分减少时，盛出备用；\r\n3.锅内加入少许食用油，烧至五成热时，放入辣椒、豆豉翻炒几下，加入葱、姜、蒜、酱油、盐、白糖和味精煸炒均匀即可。', '豉香瓜脆，清淡适口。', '炒制苦瓜的时间不要过长，否则苦瓜不脆。', '食用油30克；酱油1小匙(3克)；豆豉1大匙(30克)；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '苦瓜300克；红辣椒6个；大蒜2瓣；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (5992, 444, '松仁玉米', '1.辣椒切小丁，香葱切末；将玉米粒放入沸水中煮4分钟至八成熟，捞出沥干；\r\n2.用中火将炒锅烧至温热，放入松仁干炒，至略变金黄出香味。要经常晃动锅或用锅铲翻炒，避免颜色不均匀；\r\n3.将炒好的松仁盛出，平铺在大盘中晾凉；\r\n4.炒锅中倒入油，用中火烧热，先把香葱煸出香味，再依次放入玉米粒、辣椒丁和松仁煸炒2 分钟，调入精盐和白糖；\r\n5.盖上锅盖稍煮，若觉得菜太干，可以沿锅边加少量水，最后撒上味精炒匀即可。', '清新爽口，香酥甜嫩。', '这道菜最好用嫩的袋装甜玉米粒，青椒一定要不辣的那种，否则会破坏菜的味道。', '食用油15克；精盐1小匙(3克)；白糖3小匙(9克)；味精1小匙(3克)', '玉米粒400克；剥壳松子仁100克；红辣椒1个；青辣椒1个；香葱2棵');
INSERT INTO `recipe` VALUES (5993, 444, '油菜扒金针菇', '1.油菜、金针菇洗净备用；蒜捣成泥；\r\n2.将锅中水烧开后，加少量精盐，倒入油菜氽烫后捞起沥干水分，装盘；\r\n3.将锅洗净后，加少许油，倒入金针菇，加少许水，放入各种调味料，勾薄芡，炒匀盛在油菜上即可。', '口味清淡，色泽美观。', '炒金针菇时芡汁不宜过多，否则不清爽。', '食用油25克；蚝油3小匙(9克)；胡椒粉2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '油菜心250克；金针菇200克；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (5994, 444, '蚝油草菇', '1.将草菇的蒂去掉后洗净，葱切成段，蒜切成片；\r\n2.锅内放水烧开，放入草菇焯水；\r\n3.往锅内放油烧热，放入葱、蒜炒香，下入草菇翻炒1 分钟至熟；\r\n4.将精盐、味精放入锅内炒匀，淋入蚝油即可。', '味道清淡，咸鲜可口。', '蚝油带点酱味，烧菜时如果放了蚝油就要少放点盐。', '食用油20克；蚝油3小匙(9克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '草菇300克；香葱2棵；大蒜5瓣');
INSERT INTO `recipe` VALUES (5995, 444, '玉兰片烩猴头菇', '1.将猴头菇放入沸水中焯透后捞出用凉水冲凉，沥干水分切片，和洗净的葱、姜一起装入碗内，用盐、味精、高汤调味，上蒸锅蒸熟，取出扣入盘中；\r\n2.玉兰片洗净，切成0.2 厘米厚的片，入锅煮透，捞出，摆于圆盘四周，淋上蒸猴头菇的汤汁即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜咸适口，诱人食欲。', '因为猴头菇中水分含量过多，需将水分沥干，才能吸收调味汁，增加菜品的鲜醇度。', '食用油25克；高汤2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '水发玉兰片150克；猴头菇250克；生姜1小块；香葱2棵');
INSERT INTO `recipe` VALUES (5996, 444, '红烧猴头菇', '1.豆腐切块；胡萝卜切片；姜切末备用；猴头菇洗净，用冷水泡软，以手撕成块状后，挤干水分，加入蛋、咖喱粉、油、精盐、白糖抓拌均匀后，先放入玉米粉拌匀，再沾裹一层地瓜粉；\r\n2.最后，将裹好的猴头菇放入热油锅中，以大火炸至表面呈金黄色时，捞出沥油；\r\n3.锅内留底油，放入姜末爆香，放入豆腐、胡萝卜、水、猴头菇、蚝油、沙茶酱，用小火烧至入味即可。', '口味醇正，鲜香味美。', '猴头菇上有些小茸毛，很容易藏细沙，所以一定要多洗几遍。', '食用油500克；（实耗30克）；咖喱粉1/2小匙(1.5克)；蚝油1大匙(30克)；沙茶酱1大匙(30克)；精盐1/2小匙(1.5克)；白糖1/2小匙(1.5克)', '猴头菇200克；豆腐200克；胡萝卜10克；鸡蛋1个；生姜1小块；玉米粉1大匙(30克)；地瓜粉2大匙(60克)');
INSERT INTO `recipe` VALUES (5997, 444, '炒素鳝糊', '1.韭黄洗净，切段；姜切丝备用；\r\n2.香菇泡软，挤干水分，去蒂头，再用剪刀顺着香菇外缘，以螺旋状剪至中心成为香菇条后，均匀沾裹一层地瓜粉；\r\n3.将裹好的香菇放入热油锅中，炸至表面酥黄熟透时捞出沥油，再置于厨房纸巾上吸干多余的油脂；\r\n4.锅内留底油，爆香姜丝，放入沙茶酱、蚝油拌匀，再放入香菇条、韭黄炒香，起锅前加入香油及胡椒粉拌匀即可。', '皮脆菇香，清淡适口。', '炸香菇时最好炸两次，这样才能将香菇的表面炸脆。\r\n注：炸好的香菇看上去像鳝丝，故称“炒素鳝糊”。', '食用油500克；（实耗40克）；香油1小匙(3克)；蚝油1大匙(30克)；沙茶酱1大匙(30克)；胡椒粉1/2小匙(1.5克)', '香菇100克；韭黄100克；生姜1小块；地瓜粉适量');
INSERT INTO `recipe` VALUES (5998, 444, '油醋香菇', '1.新鲜香菇洗净，每朵切成4小块；大蒜去皮，切末；芹菜洗净，切末；\r\n2.烧热油锅，加入橄榄油、蒜末、黑胡椒粉爆香后，放入新鲜香菇块，用大火快炒至熟，再加入红酒醋、精盐、水拌炒均匀至入味，起锅前撒上芹菜末即可。', '微酸适口，菇香味浓。', '在大型超市可以买到红酒醋。', '食用油15克；橄榄油2小匙(6克)；黑胡椒粉1/2小匙(1.5克)；红酒醋2小匙(6克)；精盐1/2小匙(1.5克)', '新鲜香菇400克；芹菜30克；大蒜2瓣');
INSERT INTO `recipe` VALUES (5999, 444, '山药烩香菇', '1.胡萝卜洗净，去皮，切成薄片；香菇洗净，切薄片；红枣洗净，泡水；葱洗净，切段；山药洗净、去皮，切成薄片，放入水中加精盐浸泡；\r\n2.锅中倒入油烧热，爆香葱段，放入山药、香菇及胡萝卜炒匀，加入红枣及酱油，用中火焖煮10分钟至山药、红枣熟软，再加入精盐和胡椒粉调匀，即可盛出。', '口味清淡，咸鲜味美。', '山药切片后需立即泡入盐水中，以防止氧化变黑。', '食用油30克；酱油1/2小匙(1.5克)；胡椒粉1/2小匙(1.5克)；精盐4小匙(12克)', '山药300克；新鲜香菇100克；胡萝卜100克；红枣10克；香葱1棵');
INSERT INTO `recipe` VALUES (6000, 444, '平菇炒翠笋', '1.胡萝卜去皮，煮熟，再切片；芦笋洗净，切小段；平菇洗净，斜切片；\r\n2.锅内放油，烧热，放入芦笋滑炒，再放入胡萝卜片和平菇，并放入酒、精盐、白糖、胡椒粉、水淀粉，炒匀即可盛出。', '色泽清新，味淡爽口。', '可依据个人口味加少量肉丝，这样味道更鲜美。', '食用油25克；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '平菇250克；芦笋150克；胡萝卜30克；淀粉适量');
INSERT INTO `recipe` VALUES (6001, 444, '口蘑烧香芋', '1.魔芋每条对半剖开，切斜块，放入沸水氽烫去味，捞出，洗净、沥干水分；红辣椒洗净切块；姜洗净切末；\r\n2.面筋切小片；葱洗净、切段；玉米笋对半切开，与口蘑、扁豆一起放入沸水中氽烫，捞出，沥干；\r\n3.锅中倒油烧热，放入姜爆香，加入以上全部材料炒匀，再加入高汤、精盐、胡椒粉、水淀粉调味、勾芡，最后淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '糯香可口，清淡美味。', '烧制此菜时稍加一点鸡油，味道会更加鲜美。', '食用油30克；香油1小匙(3克)；高汤1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)', '魔芋200克；口蘑100克；扁豆10克；面筋50克；玉米笋10克；红辣椒1个；香葱2棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6002, 444, '糖醋南瓜丸', '1.将南瓜洗干净，去皮、去瓤、切成块，蒸熟后沥水，加入面粉、糖、盐，揉成面团状；\r\n2.锅内放油，烧至七成热时，用手将南瓜团挤成鹌鹑蛋大小的丸子，放入油锅中炸至金黄色捞出；\r\n3.锅内留底油，放适量清水、白糖、盐，然后勾芡、淋入香醋，倒入丸子拌炒均匀即可。', '甜酸软嫩，清香味美。', '蒸南瓜时要用保鲜纸把装南瓜的碗封好，以防蒸馏水冲淡南瓜的味道。', '食用油250克；(实耗50克)；香醋12/3大匙(120克)；精盐2小匙(6克)；白糖12/3大匙(120克)', '南瓜500克；面粉50克；淀粉适量');
INSERT INTO `recipe` VALUES (6003, 444, '咸蛋黄炒金瓜', '1.将南瓜洗净，去皮、去瓤，切成小片；咸鸭蛋去皮后去掉蛋白，把蛋黄切成小丁；葱切成葱花，姜切成小片；\r\n2.往锅里放油，烧热，放入葱、姜、蛋黄丁，烹入料酒，放入南瓜片、精盐、味精翻炒均匀，待南瓜熟透后淋入香油即可。', '咸鲜甘甜，口感绵软。', '炒蛋黄时要多放点油，将蛋黄炒香、炒透，否则有蛋腥味。', '食用油30克；香油1小匙(3克)；料酒1小匙(3克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '南瓜300克；熟咸鸭蛋4个；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (6004, 444, '脆香藕丸', '1.将藕洗净削去皮、切除节，用刀斩成茸后放入碗内，加入鸡蛋、精盐、味精、面粉、葱姜末，拌匀成馅；\r\n2.锅内放油，烧至四成热时，用手把藕馅挤成鹌鹑蛋大小的丸子，再放入锅内炸成金黄色，捞出沥油；\r\n3.倒出锅中余油，加入少许开水，放入藕丸，烧开后，加入酱油、白糖，盖上锅盖烧约5分钟，起锅装盘即成。', '外香内脆，鲜美可口。', '切藕时速度要快，以防藕氧化变黑。', '食用油500克；（实耗40克）；酱油1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '莲藕400克；鸡蛋1个；香葱2棵；生姜1小块；面粉适量');
INSERT INTO `recipe` VALUES (6005, 444, '蒜香茄泥', '1.将茄子削去蒂托，去皮，切成0.3 厘米厚的片，放入碗中，笼25 分钟后取出放凉；韭菜、香菜洗净切末；蒜洗净捣成茸；\r\n2.将蒸过的茄子去掉水，加入香油、精盐、芝麻酱、香菜、韭菜、蒜泥拌匀即成。', '软烂适口，蒜香诱人。', '蒸茄子时要一次性蒸熟，不要中途打开锅盖，否则不容易熟。', '香油1小匙(3克)；芝麻酱3小匙(9克)；精盐1小匙(3克)', '茄子350克；韭菜20克；香菜1棵；大蒜8瓣');
INSERT INTO `recipe` VALUES (6006, 444, '酱烧茄子', '1.将茄子削去皮，切成2 厘米见方的块，表面切十字花刀，葱、姜、蒜切片待用；\r\n2.将油下锅烧热后，下入茄子炸成金黄色捞出；\r\n3.锅中留底油，把葱、姜、蒜和甜面酱一同下锅煸炒，待出香味时放入适量清水；\r\n4.随即把茄子、白糖、精盐、味精、酱油一同放入烧开，改小火，待茄子烧透，勾入水淀粉烧开即成。', '咸鲜微甜，酱香浓郁。', '炸茄子时为了防止茄子吸太多油，要把油温烧至九成热。', '食用油500克；(实耗75克)；甜面酱1大匙(30克)；酱油3小匙(9克)；精盐1/2小匙(1.5克)；白糖3小匙(9克)；味精1/2小匙(1.5克)', '茄子500克；香葱2棵；生姜1小块；大蒜10瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6007, 444, '鱼香茄子煲', '1.将茄子切成滚刀块；锅中放入油，待油温升至九成热时放入茄子，炸至茄子由硬变软时取出，沥干；葱、姜、蒜洗净切末；\r\n2.锅内留底油，先爆香葱末、姜末、蒜末、豆瓣酱；\r\n3.然后放入茄子，加料酒、酱油、盐后炒匀，再加入一碗水，用大火煮开后改用小火煮至茄子入味，加入白糖、醋调味，最后用水淀粉勾芡即成。', '柔软润香，鲜咸适口。', '可用小火多焖一会，以使茄子更软、更入味。', '食用油500克；（实耗75克）；酱油2小匙(6克)；料酒3小匙(9克)；香醋1小匙(3克)；豆瓣酱1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '茄子500克；香葱3棵；生姜1小块；大蒜8瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6008, 444, '虎皮辣椒', '1.将辣椒洗净，去蒂及籽，用刀平剖成两半；\r\n2.把醋、白糖、酱油、料酒同放入碗内，调成糖醋汁；\r\n3.锅内放油烧热，投入辣椒，用小火煎至表皮出现斑点时，烹入糖醋汁，搅拌均匀即成。', '酸中带甜，香脆爽口。', '最好选用刚上市的嫩辣椒，口感更为鲜嫩。', '食用油50克；酱油3小匙(9克)；料酒少许；香醋1/2大匙(15克)；白糖1/2大匙(15克)', '红辣椒100克；青辣椒200克');
INSERT INTO `recipe` VALUES (6009, 444, '豆豉辣椒', '1.豆豉洗净；辣椒洗净，切丁；蒜切末；\r\n2.锅内放油，烧热，爆香蒜末和豆豉，放入辣椒煸炒，加入酱油、酒、糖、盐炒匀即可。', '咸香微辣，豉香诱人。', '如果加点肉末，这道菜会更鲜美。', '食用油30克；酱油1/2大匙(15克)；料酒1大匙(30克)；豆豉1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '红辣椒450克；青辣椒2个；大蒜6瓣');
INSERT INTO `recipe` VALUES (6010, 444, '香酥薯条', '1.将味精和精盐掺和均匀成味精盐；\r\n2.将土豆去皮，切成宽1 厘米、长10 厘米的薯条；\r\n3.锅内放油，烧至三成热，放入薯条炸熟，当油温升至六成热时，把薯条炸成金黄色，捞出沥油；\r\n4.在薯条上撒上味精、盐，拌匀，整齐地码放在盘中即成。', '色泽金黄，酥香诱人。', '火不能太大，否则容易炸焦。', '食用油500克；(实耗80克)；精盐1/2大匙(15克)；味精1小匙(3克)', '土豆500克');
INSERT INTO `recipe` VALUES (6011, 444, '地三鲜', '1.先将土豆削皮，再把土豆、茄子和辣椒洗净，均切成滚刀块，大小要一致，淀粉放入碗中加适量水调匀；姜切末；蒜剁成茸；\r\n2.往锅内放油，烧热，把茄子、土豆炸熟至金黄色后捞起，沥油；\r\n3.锅内留少许底油，放入辣椒、姜末和蒜茸拌炒，再往锅中倒入茄子、土豆和其他调味料，淋入淀粉，勾芡盛起即可。', '咸鲜味美，老幼皆宜。', '土豆、茄子可以同时放入油锅炸。', '食用油500克；（实耗30克）；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '茄子100克；红辣椒1/2个；青辣椒3个；土豆150克；生姜1小块；大蒜5瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6012, 444, '麻辣土豆条', '1.土豆洗净去皮切成长条，用水洗一下，捞出沥净水，拌入辣椒粉、花椒粉、精盐、味精和淀粉备用；\r\n2.锅内放油烧至八成热时，放入土豆条，炸熟后捞出，等油温回升至八成热时，放入土豆条再炸一次捞出装盘即可。', '麻辣咸香，外脆里嫩。', '土豆含有淀粉，用清水洗净后再炸，可防止土豆入油时粘在一起。', '食用油500克；（实耗50克）；辣椒粉1大匙(30克)；花椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '土豆500克；淀粉适量');
INSERT INTO `recipe` VALUES (6013, 444, '酸辣土豆', '1.将土豆去皮切成片；蒜苗切成2 厘米长的段；\r\n2.将油放入锅内，烧热后投入土豆炸至焦黄色捞出，沥油；\r\n3.锅内留底油，烧热后加入蒜苗、辣椒粉、糖、醋、酱油、盐、水，烧开后用水淀粉勾芡，投入土豆片，搅拌均匀，出锅即可。', '甜酸辣咸，色泽金红。', '土豆片要切得厚一些，炸熟炒好后口感更佳。', '食用油500克；(实耗75克)；酱油3小匙(9克)；辣椒粉1大匙(30克)；香醋2/3大匙(20克)；精盐1小匙(3克)；白糖1大匙(30克)', '土豆500克；蒜苗25克；淀粉适量');
INSERT INTO `recipe` VALUES (6014, 444, '香素咕老肉', '1.土豆去皮切块，菠萝肉切块；\r\n2.鸡蛋打入碗内，放入土豆、水淀粉拌匀成糊，烧热油锅，逐块下入土豆块，炸至金黄色时捞起，沥油；\r\n3.锅内留底油，注入少许水、番茄酱、盐、糖、醋烧开，放入菠萝和炸好的土豆，用水淀粉勾芡，出锅即成。', '色泽美观，酸甜适口。', '这道菜只需勾薄芡。', '食用油500克；(实耗80克)；番茄酱1/2大匙(15克)；香醋1/3大匙(10克)；精盐1小匙(3克)；白糖1大匙(30克)', '土豆250克；菠萝20克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (6015, 444, '炒辣三丝', '1.土豆去皮，洗净切丝；辣椒洗净切丝；葱切丝；\r\n2.将土豆和豆皮丝分别用开水焯一下，捞出控干水分；\r\n3.锅内倒入油，烧热，把辣椒丝滑一下捞出；\r\n4.用余油煸炒葱丝、辣椒酱，出香味时烹入料酒，放入土豆丝、豆皮丝、辣椒丝、酱油、精盐、味精，用水淀粉勾芡，翻炒均匀，出锅即可。', '色泽鲜亮，口味浓厚。', '此菜不勾芡口感也很好。', '食用油50克；酱油1/2大匙(15克)；料酒2小匙(6克)；辣椒酱1/2大匙(15克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '土豆200克；豆皮丝80克；红辣椒2个；青辣椒2个；黄辣椒2个；大葱2根；淀粉适量');
INSERT INTO `recipe` VALUES (6016, 444, '干煸茭白', '1.将茭白削去外皮，切去老根，切成大粗条；\r\n2.锅内放油，烧至六成热，放入茭白煸炒至微呈黄色、表面起皱时，加入酱油、精盐、味精煸炒入味，放入芽菜，烹入料酒炒匀，淋香油即可起锅。', '鲜咸适宜，清淡爽口。', '多放点油，将茭白煸炒透才能出香味。', '食用油30克；香油1小匙(3克)；酱油3小匙(9克)；料酒2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '茭白500克；芽菜末30克');
INSERT INTO `recipe` VALUES (6017, 444, '清炒芥蓝', '1.摘取芥蓝上的嫩叶，洗净后切段；将蟹肉棒剥成丝；蒜切片；\r\n2.锅内放油，烧热，爆香蒜片，放入芥蓝用大火快炒；\r\n3.把蟹肉丝加进去，放入料酒、盐调味即成。', '素雅鲜嫩，清脆淡香。', '可以先把芥蓝放入沸水中焯一下再炒，口感也很好。', '食用油20克；料酒1/2大匙(15克)；精盐1/2小匙(1.5克)', '芥蓝250克；蟹肉棒50克；大蒜3瓣');
INSERT INTO `recipe` VALUES (6018, 444, '口蘑烧冬瓜', '1.将冬瓜的外皮及瓤去掉，切片，火腿切片；把冬瓜放入沸水中煮透，捞出用凉水冲凉；\r\n2.将火腿片与冬瓜片码入碗中，放入精盐、味精、猪油、高汤，上蒸锅蒸透，取出，把冬瓜翻扣在盘内；\r\n3.锅内注入剩余猪油，烧热，烹入料酒，倒入高汤、口蘑、煮冬瓜的原汤，加糖、盐、味精、胡椒粉，烧开后用水淀粉勾芡，淋入鸡油，将汁浇在冬瓜夹上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '口味清淡，汤汁鲜美。', '蒸冬瓜的时间不能太长，否则冬瓜太软，口感不好。', '猪油30克；鸡油1小匙(3克)；料酒2小匙(6克)；高汤8大匙(240克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '冬瓜500克；口蘑罐头1听；火腿100克；淀粉适量');
INSERT INTO `recipe` VALUES (6019, 444, '海米冬瓜', '1.将冬瓜削去外皮，去瓤，洗净切成片，用少许精盐腌10 分钟左右，沥干水分待用；用温水将海米泡软；葱、姜洗净切末；\r\n2.锅内放油，烧至六成热，倒入冬瓜片，待冬瓜皮色翠绿时捞出沥油；\r\n3.锅内留底油，烧热，爆香葱末、姜末，加入适量水、味精、料酒、精盐和海米，烧开后放入冬瓜片，再用大火烧开，转用小火焖烧，冬瓜熟透且入味后，下水淀粉勾芡，炒匀即可。', '汁浓味鲜，瓜嫩爽滑。', '冬瓜片不要切得太薄，否则口感不好。', '食用油500克；（实耗30克）；料酒1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '冬瓜500克；海米50克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6020, 444, '素回锅肉', '1.冬瓜去皮去籽切成长片，红椒切片，蒜苗切段，生姜去皮切片；\r\n2.往冬瓜里加入淀粉拌匀，烧热油锅，将冬瓜片炸至金黄，捞起沥油；\r\n3.锅内留底油，放入姜、豆瓣酱、红椒、蒜苗翻炒至断生，加入冬瓜片，调入盐、味精、糖、酱油，调中火炒透，用水淀粉勾芡，倒入盘中即可。', '香辣可口，脆嫩鲜香。', '炸好的冬瓜不要在锅里翻炒太久，否则汤汁会浸软冬瓜表层脆皮，从而影响口感。', '食用油500克；（实耗50克）；酱油2小匙(6克)；豆瓣酱2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '冬瓜250克；红辣椒1个；蒜苗10克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6021, 444, '开洋白菜', '1.白菜剥开叶片，洗净、切长块；虾米泡软；胡萝卜去皮切片；香菇泡软，撕成块状；\r\n2.锅中倒入油，烧热，爆香香菇和虾米，放入大白菜和胡萝卜炒至熟软，加精盐、高汤焖煮入味，再加水淀粉勾芡即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汁浓味鲜，清淡适口。', '不要把大白菜煮得太烂，否则影响菜肴的口感。', '食用油30克；高汤2大匙(60克)；精盐1小匙(3克)', '白菜300克；香菇30克；胡萝卜50克；虾米30克；淀粉适量');
INSERT INTO `recipe` VALUES (6022, 444, '海米炒蕨菜', '1.将蕨菜择去老茎，用开水焯过，切成长段；海米水发后，洗净，放入瓷碗内，加料酒及清水上蒸锅蒸软；生姜去皮，洗净，切成细末，待用；\r\n2.锅内放油，烧至五成热时，投入姜末、辣椒粉、豆豉炸出香味，下入蕨菜稍微煸炒后，将蒸好的海米及其汤汁倒入；\r\n3.同时，加入精盐、糖、酱油、料酒炒匀，放入高汤烧开，待蕨菜稍微变软时（不宜过于炒熟），加味精，用水淀粉勾薄芡，淋入香油，装盘即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜嫩柔滑，清香四溢。', '蕨菜有很高的营养价值，但未经开水焯过的蕨菜易致癌。', '食用油30克；香油1小匙(3克)；酱油3小匙(9克)；高汤2大匙(60克)；料酒1/2大匙(15克)；豆豉1小匙(3克)；辣椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '蕨菜400克；海米50克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6023, 444, '鱼香扁豆丝', '1.将扁豆掐去两尖头，去筋洗净，切成细丝；蒜、姜切末；泡椒切末；\r\n2.锅烧热，先放入扁豆干炒片刻，然后放入蒜、姜、泡椒、油、精盐炒至断生，再调入其余各种调料，用水淀粉勾芡即可。', '鲜香嫩爽，香辣可口。', '一定要将扁豆炒熟，否则不够入味。', '食用油30克；辣椒油1小匙(3克)；酱油1小匙(3克)；豆瓣酱1大匙(30克)；花椒粉1小匙(3克)；胡椒粉1小匙(3克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1小匙(3克)', '扁豆500克；泡椒20克；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6024, 444, '油焖豇豆', '1.将豇豆掐去两头，再切段，用开水烫透，捞出用冷水冲凉，沥干水分；葱切丝，姜、蒜切片；\r\n2.锅内放油，烧热，放入葱、姜、蒜爆香，投入豇豆，煸炒几下，放入酱油、精盐和水，用大火烧开后改小火焖烧，直至汤汁将收干；\r\n3.豇豆接近酥软时，加少许糖，再焖上2～3分钟，汤汁稠浓即可。', '豇豆酥软，味道浓厚。', '出锅时滴上一点香油味道会更好。', '食用油30克；酱油1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '豇豆300克；大葱1根；生姜1小块；大蒜3瓣');
INSERT INTO `recipe` VALUES (6025, 444, '海米豇豆', '1.豇豆洗净，切成5 厘米长的段；海米洗净，加温水泡软，捞出沥干，剁碎；葱切末；\r\n2.锅内放油，烧至六成热，下入豇豆炸至表皮起皱，捞出沥油；\r\n3.锅内留底油，烧热，下葱末、海米略煸，倒入豇豆拌炒，加料酒、盐、味精、高汤，用大火将汁收干，翻炒几下，淋入香油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '咸鲜味美，清香适口。', '炒豇豆时不放或少放点酱油，菜的颜色会更加青翠。', '食用油500克；（实耗30克）；香油1小匙(3克)；高汤2小匙(6克)；料酒2小匙(6克)；精盐1小匙(3克)；味精1小匙(3克)', '豇豆500克；海米20克；大葱1根');
INSERT INTO `recipe` VALUES (6026, 444, '鲜香豆末', '1.将豇豆洗净去筋络，切末，放沸水中焯熟捞出；\r\n2.鸡蛋打散，加入虾酱拌匀；\r\n3.锅内注油烧热，下入鸡蛋、虾酱翻炒，再下入豇豆，加香油、盐、糖、味精炒匀即可。', '虾酱香浓，口味独特。', '一定要将豇豆煮透，否则不易入味。', '食用油30克；香油1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)；虾酱适量', '豇豆400克；鸡蛋2个');
INSERT INTO `recipe` VALUES (6027, 444, '豆豉鲮鱼油麦菜', '1.把油麦菜切成10 厘米长的段，用开水焯熟，装盘；葱、蒜、姜切末；\r\n2.锅中下油，炒香葱、蒜、姜、豆豉，加入高汤及其他调味料，烧开后放入鲮鱼，用水淀粉勾芡，盛出放在油麦菜上，淋入香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽美观，咸鲜微甜。', '油麦菜焯水的时间不能长，断生即可。', '食用油30克；香油1小匙(3克)；高汤3小匙(9克)；料酒2小匙(6克)；豆豉1大匙(30克)；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '油麦菜400克；罐装鲮鱼50克；大葱1根；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6028, 444, '多味木耳菜', '1.调制怪味汁：将芝麻酱用香油泡开，再将精盐、酱油、糖、醋、熟芝麻、红油、花椒油、味精放入调匀即成；\r\n2.木耳菜择洗干净，去掉老筋切段；海带丝洗净，放入沸水中焯至断生，然后沥干，再将粉丝放沸水中焯水沥干；红辣椒洗净切丝；\r\n3.锅内放油，烧热，倒入木耳菜翻炒，再倒入海带丝、粉丝和辣椒丝，淋上怪味汁，拌匀即成。', '口感爽滑，味道特别。', '要用大火炒木耳菜。', '食用油15克；香油1小匙(3克)；红油1小匙(3克)；花椒油1小匙(3克)；酱油1小匙(3克)；芝麻酱1小匙(3克)；香醋1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '木耳菜200克；海带丝50克；水发粉丝50克；红辣椒1个；熟芝麻适量');
INSERT INTO `recipe` VALUES (6029, 444, '三鲜素海参', '1.先将水发木耳洗净，沥水，切末，与玉米粉同放在大碗内，加精盐、味精、水拌成木耳糊，用刀把糊划成手指形，逐条从刀上下到六成热的油锅中，汆成海参形，即为素海参；\r\n2.再将冬菇洗净去蒂，切成梳子状；熟冬笋切块；姜切末；\r\n3.锅内放油，烧热，下入全部材料煸炒，然后再下姜末、料酒、酱油、糖、盐、高汤，烧约半分钟后，加入素海参、味精，烧开后，用水淀粉勾芡，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '菜形美观，嫩滑鲜香。', '素海参下锅后不要翻炒太频繁，以防将菜炒碎。', '食用油250克；（实耗80克）；香油1小匙(3克)；酱油2小匙(6克)；高汤3大匙(90克)；料酒2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '水发木耳100克；水发冬菇50克；熟冬笋50克；生姜1小块；玉米粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (6030, 444, '西芹百合', '1.西芹择去筋，洗净，切成较薄的段；\r\n2.百合去蒂洗净，掰成片；\r\n3.锅内放油，烧热，下西芹炒至五成熟；\r\n4.加百合、精盐、白糖炒熟，勾薄芡即可。', '色泽美观，清淡爽口。', '西芹和百合焯水后再炒，成菜的速度更快。', '食用油20克；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '西芹200克；新鲜百合150克；淀粉适量');
INSERT INTO `recipe` VALUES (6031, 444, '椒盐花菜', '1.花菜切朵洗净，沥干水，生姜、蒜、红椒切末，葱切段；\r\n2.鸡蛋打入碗内，放入花菜、淀粉搅匀，烧热油锅，逐块放入花菜，炸至金黄时捞起；\r\n3.锅内留油，放入姜、红椒、炸好的花菜，调入椒盐、味精、葱炒透，淋入香油即成。', '清淡脆鲜，美味爽口。', '先用盐、味精把花菜腌一下再炸，味道会更好。', '食用油500克；(实耗50克)；香油1小匙(3克)；椒盐2小匙(6克)；味精1/2小匙(1.5克)', '花菜300克；鸡蛋1个；红辣椒1个；香葱3棵；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6032, 444, '糖醋素排骨', '1.将油条分别剪成段，中间用筷子挖空；芋头切成条状；洋葱去外皮，切成块状；姜切末；菠萝切块；\r\n2.将切好的芋头塞入油条中，再放入热油锅中炸至酥脆时，捞出沥干油即为素排骨；\r\n3.锅内放油，用中火爆香姜末，放入蚕豆、菠萝块及洋葱块拌炒后，放入素排骨、水及所有调味料一起煮开即可。', '排骨逼真，甜酸适口。', '要把芋头切细一些，这样更容易炒熟。', '食用油500克；（实耗30克）；番茄酱1/2大匙(15克)；香醋2小匙(6克)；白糖2小匙(6克)', '蚕豆100克；油条2根；芋头10克；菠萝20克；洋葱10克；生姜1小块');
INSERT INTO `recipe` VALUES (6033, 444, '丝瓜炖粉丝', '1.丝瓜刮皮洗净，切开，剔除瓤籽，切长段；\r\n2.海米用温水泡发后洗净；香菜择洗干净，粉丝泡发，分别切成段；\r\n3.砂锅放火上，放入丝瓜、粉丝、海米和高汤，用大火烧开后改小火炖透，撒入精盐、味精、胡椒粉和香菜段，烧开，淋上香油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤汁鲜美，清淡适口。', '萝卜炖粉丝的做法和这道菜一样，味道也很鲜美。', '香油1小匙(3克)；高汤3大匙(90克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '丝瓜600克；粉丝100克；海米适量；香菜1棵');
INSERT INTO `recipe` VALUES (6034, 444, '金银蒜蒸丝瓜', '1.丝瓜去皮切厚圆圈，蒜切成粒，红辣椒切小丁，把切好的丝瓜按顺序摆入盘内；\r\n2.烧锅下油，把一半蒜炸成黄色，捞起与另一半没炸的蒜和精盐、味精、白糖、淀粉拌匀撒到丝瓜上；\r\n3.蒸锅烧开水，放入摆好丝瓜的盘子，用大火蒸6 分钟后拿出，撒上红椒，烧开香油淋到丝瓜上即可。', '汤汁鲜美，蒜香味浓。', '可以按照这种做法蒸萝卜丝、芥蓝等菜。', '食用油500克；（实耗50克）；香油2小匙(6克)；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '丝瓜200克；红辣椒1个；大蒜15瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6035, 444, '双菇西兰花', '1.西兰花洗净，切小朵；香菇泡软、去蒂；口蘑洗净，对切；\r\n2.锅中倒入油烧热，放入香菇炒数下，加入西兰花、口蘑及高汤、料酒、精盐、白糖拌炒均匀，改小火焖10分钟即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '清香诱人，味道鲜美。', '最后放西兰花是为了防止炒制时间过长，西兰花变黄。', '食用油30克；高汤2大匙(60克)；料酒1/2大匙(15克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '西兰花250克；香菇50克；口蘑50克');
INSERT INTO `recipe` VALUES (6036, 444, '炒什锦蚕豆', '1.将蚕豆洗净；胡萝卜、荸荠、黄瓜、土豆、豆腐干切丁；木耳撕片，放入沸水中略焯，然后用凉水冲一下；葱、姜切末；\r\n2.锅内倒油，油热后放葱姜末煸炒出香味，再放入蚕豆及各种原料同炒，然后加料酒、精盐、味精、白糖及高汤，烧开后用水淀粉勾芡即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽鲜艳，清淡适口。', '可根据自己的喜好选择其他素菜拌炒。', '食用油30克；高汤适量；料酒2小匙(6克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '蚕豆200克；胡萝卜25克；荸荠25克；黄瓜25克；土豆25克；水发木耳25克；豆腐干25克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6037, 444, '虾皮卷心菜', '1.卷心菜剥下叶片洗净，用手撕成小片；虾皮洗净，沥干水分；蒜、姜切末；\r\n2.锅内倒入油烧热，爆香姜、蒜及虾皮，放入卷心菜炒软，加入水和精盐续煮3 分钟，即可盛出。', '菜鲜味美，清爽适口。', '卷心菜要用大火爆炒。', '食用油30克；精盐1小匙(3克)', '卷心菜400克；虾皮50克；大蒜2瓣；生姜1小块');
INSERT INTO `recipe` VALUES (6038, 444, '麻辣蚕豆', '1.将嫩蚕豆洗净沥干；葱、姜切成末；\r\n2.锅内放油，烧热，投入葱、姜末煸出香味时，加入蚕豆翻炒匀，再加精盐、白糖、水、辣椒粉，继续炒1 分钟左右，加味精、花椒炒匀即成。', '咸甜适口，鲜麻味美。', '用清水将蚕豆泡开后再炒，吃起来更有韧性。', '食用油30克；辣椒粉1/2大匙(15克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '蚕豆500克；香葱2棵；生姜1小块；花椒1小匙(3克)');
INSERT INTO `recipe` VALUES (6039, 444, '雪菜豆板酥', '1.雪菜洗净，用清水浸泡10 分钟，挤干水分，切丁；蚕豆洗净沥干水分；\r\n2.锅内放油，烧至七成热时放入雪菜，烹炸2 分钟至颜色呈鲜亮的深绿色，捞出沥油；\r\n3.锅内留底油，烧热后放入鲜蚕豆板煸炒熟，倒入高汤，改中火煮10分钟，用锅铲将已经酥软的蚕豆压成泥状；\r\n4.往蚕豆泥中加少许高汤，拌匀，放入雪菜，调入白糖，改大火煮沸，略收汁即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '酥软香甜，翠绿清新。', '雪菜即雪里蕻。', '食用油250克；（实耗40克）；高汤9大匙(270克)；白糖2小匙(6克)', '蚕豆300克；雪菜70克');
INSERT INTO `recipe` VALUES (6040, 444, '锅塌菠菜', '1.菠菜择洗干净，放入沸水中氽烫片刻，用凉水冲凉；葱、姜切丝，蛋磕入碗中打匀；\r\n2.菠菜挤净水，加精盐、味精、料酒、葱、姜拌匀，腌渍入味，然后洒上少许面粉，放入蛋液中调匀；\r\n3.锅内放油烧热，下入蘸上蛋液的整棵菠菜，煎成金黄色时，加适量高汤调味，收尽汤汁，淋入香油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '滑爽软嫩，色美味鲜。', '因菠菜容易出水，将菠菜下锅时要小心溅油烫伤皮肤。', '食用油50克；香油1小匙(3克)；高汤适量；料酒2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '菠菜200克；鸡蛋2个；香葱2棵；生姜1小块；面粉适量');
INSERT INTO `recipe` VALUES (6041, 444, '蛋香萝卜丝', '1.将萝卜洗净，去皮切成丝，加少许味精、精盐腌制；鸡蛋磕入碗内，打散，冲入少许开水成蛋花；葱去叶切末；\r\n2.锅内倒油，烧至七成热，将萝卜丝放入炒匀，加入葱末并立即撒入蛋花，最后放入味精即成。', '色彩淡雅，清香脆嫩。', '萝卜丝腌制后要沥干水再炒，否则有生萝卜味。', '食用油30克；精盐1小匙(3克)；味精1/2小匙(1.5克)', '白萝卜350克；鸡蛋4个；大葱1根');
INSERT INTO `recipe` VALUES (6042, 444, '甜辣萝卜干', '1.干辣椒洗净，切段；蒜切末；萝卜干洗净，沥干后切成细条；\r\n2.锅内倒油，爆香辣椒及蒜末，放入萝卜干拌炒；\r\n3 .加入白糖，用中火炒至萝卜干表面呈白色或微焦时，加入酱油、辣椒油、料酒拌炒均匀，转大火快炒至萝卜干入味即可。', '脆嫩爽口，甜辣开胃。', '烧这道菜时可加点醋，这样可使得萝卜干更脆。', '食用油30克；辣椒油1小匙(3克)；酱油1大匙(30克)；料酒2小匙(6克)；白糖适量', '萝卜干300克；大蒜3瓣；干辣椒2个');
INSERT INTO `recipe` VALUES (6043, 445, '香酥蛋松', '1.将鸡蛋打散，加入白糖、精盐搅拌均匀；\r\n2.热油，将打好的蛋液慢慢倒入滤勺中，轻轻摇动，让蛋液均匀入锅油炸；\r\n3.炸约1 分钟后，即可捞起浮出油面的蛋松，淋在白饭上即可食用。', '色泽金黄，外酥内软。', '油温要稍高一些，否则做不出蛋松的效果。', '食用油300克；（实耗30克）；精盐1/2小匙(1.5克)；白糖1/2小匙(1.5克)', '鸡蛋3个');
INSERT INTO `recipe` VALUES (6044, 445, '韭菜花炒皮蛋', '1.将皮蛋放入水中，煮沸之后再煮5 分钟熄火，捞出浸泡到冷水中，待凉后剥壳，切片备用；\r\n2.豆豉用料酒泡开，韭菜花洗净，去头尾，切小段备用；干辣椒切段；\r\n3.锅内放油，把猪肉馅炒出油，再加入干辣椒段、豆豉、精盐、味精炒至香味四溢；\r\n4.再将皮蛋片及韭菜花倒入锅中，拌炒至熟即可盛盘。', '咸鲜可口，风味独特。', '炒肉馅时先把锅烧热，再倒入油，这样可以避免肉馅粘锅。', '食用油30克；料酒1大匙(30克)；豆豉1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '皮蛋3个；猪肉馅50克；韭菜花30克；干辣椒2个');
INSERT INTO `recipe` VALUES (6045, 445, '芙蓉香酥蛋', '1.鸡蛋清内加少许精盐，打至起泡备用；\r\n2.锅内放油，烧热，把打好的鸡蛋清炒成棉花状，盛盘备用；\r\n3.海米泡软，取出切碎；火腿切成末；大蒜切末；香葱切成葱花备用；\r\n4.热少许油，爆香大蒜末与海米，再加入香葱花及火腿末，加精盐调味，最后淋上辣椒油即可铲起铺在做好的蛋清上。', '色彩鲜明，酥香可口。', '海米和火腿都有咸味，调味时要少放点精盐。', '食用油25克；辣椒油1小匙(3克)；精盐1/2小匙(1.5克)', '鸡蛋4个；海米20克；火腿10克；香葱1棵；大蒜3瓣');
INSERT INTO `recipe` VALUES (6046, 445, '香菇蒸蛋', '1.先将香菇洗净，切成末状；\r\n2.将鸡蛋打入碗内搅成蛋汁，加入适量水，再加入香菇末一起打匀；\r\n3.最后将精盐、味精和胡椒粉倒入碗内一起蒸15 分钟，熟后即可。', '质嫩爽滑，味道鲜美。', '最好用高汤把香菇煨透后再蒸蛋，这样味道会更鲜美。', '胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鸡蛋4个；香菇20克');
INSERT INTO `recipe` VALUES (6047, 445, '黄金脆皮蛋', '1.将鸡蛋放入水中，加少许精盐，用中火煮约15分钟至熟，捞起后浸泡冷水，待凉后剥壳，再泡入酱油中上色;\r\n2.胡萝卜、青辣椒切薄片，一起汆熟备用；\r\n3.烧热油，将上好色的鸡蛋放入热油中，炸成金黄色后捞出备用；\r\n4.洋葱切末，另烧热少量油，将洋葱末炒至呈透明状，再加入辣椒酱、白糖及淀粉、水，调成浓稠状；\r\n5.将炸好的鸡蛋与胡萝卜片、青辣椒片放入调好汁的锅中拌炒均匀，让鸡蛋表面完全沾裹酱汁即可。', '咸鲜微辣，外酥内香。', '最好用深色酱油给鸡蛋上色。', '食用油300克；（实耗30克）；酱油1大匙(30克)；辣椒酱1大匙(30克)；精盐1/2小匙(1.5克)；白糖1小匙(3克)', '鸡蛋5个；洋葱20克；胡萝卜20克；青辣椒1/2个；淀粉适量');
INSERT INTO `recipe` VALUES (6048, 445, '金丝虾皮蛋', '1.将萝卜干洗净，捞起沥干后，放入锅内炒干水;\r\n2.在平底锅内热好油，爆香虾皮和萝卜干，盛出备用;\r\n3.把鸡蛋打散，蒜苗切成末，与胡椒粉、精盐及炒好的虾皮、萝卜干混合搅拌匀;\r\n4.锅内放少许油，将拌好的料全部倒入锅里，盖上锅盖，用中火煎5 分钟左右，蛋液微呈凝固状时翻面煎熟即可。', '味道独特，色泽美观。', '翻鸡蛋时要细心一点，否则会破坏菜的形状。', '食用油15克；胡椒粉1小匙(3克)；精盐小匙', '鸡蛋4个；萝卜干80克；虾皮20克；蒜苗10克');
INSERT INTO `recipe` VALUES (6049, 445, '茴香炒蛋', '1.茴香洗净，去根及老茎，切末备用；\r\n2.鸡蛋打入碗中，用筷子拌打至均匀，加入精盐搅匀成蛋汁备用；\r\n3.锅中倒油烧热，倒入蛋汁炒至半凝固，加入茴香末快速炒至熟透，盛入盘中即可。', '清香诱人，滑嫩可口。', '如果把茴香切成碎末，和蛋液搅匀后再倒入锅中煎制，味道也很好。', '食用油30克；精盐1小匙(3克)', '鸡蛋4个；茴香180克');
INSERT INTO `recipe` VALUES (6050, 445, '咸蛋黄蒸肉饼', '1.将咸鸭蛋蛋黄切半，铺在碗底;香葱、大蒜洗净切末;\r\n2.往猪肉馅里加入精盐、酱油、水和大蒜末并搅拌均匀，将拌好的肉馅铺在咸蛋黄上，上蒸锅蒸20分钟;\r\n3.将蒸好的肉饼取出，倒扣在盘子上，撒上香葱末即可。', '香咸适口，诱人食欲。', '也可以把蛋黄夹在肉馅中间，蒸熟后再对切一下。', '酱油1小匙(3克)；精盐1小匙(3克)', '咸鸭蛋蛋黄2个；猪肉馅200克；香葱1棵；大蒜4瓣');
INSERT INTO `recipe` VALUES (6051, 445, '咸蛋虾仁', '1.将虾仁用精盐与少许料酒抓拌，然后洗净，用纸巾擦干，切成粒状备用；\r\n2.将咸鸭蛋去壳，与木耳一起切成小丁状备用；\r\n3.锅中倒油，稍微烧热就放入虾仁粒炒散至变色，盛出备用；\r\n4.转小火，放入咸鸭蛋丁，用锅中余油炒至冒泡，加入少许水、胡椒粉、精盐后，续炒木耳丁及青豆，再放入虾仁粒；\r\n5.把生菜洗净沥干水，与炒好的咸蛋虾仁一起食用。', '清淡爽口，咸鲜味美。', '一定要将咸鸭蛋丁炒透炒香。', '食用油30克；料酒1小匙(3克)；胡椒粉1小匙(3克)；精盐1小匙(3克)', '咸鸭蛋1个；虾仁100克；木耳5克；青豆10克；生菜10克');
INSERT INTO `recipe` VALUES (6052, 445, '鲜橙鸡蛋饼', '1.鲜橙去皮切成丁，用牛奶泡好；\r\n2.鸡蛋打散，加入精盐、味精、白糖、鲜橙丁、淀粉拌匀；\r\n3.煎锅内下油，把拌匀的鲜橙丁倒入，用小火煎香铲起切块即可。', '嫩滑可口，橙香味浓。', '淀粉不要放多了，否则蛋会变硬。', '食用油30克；牛奶1大匙(30克)；精盐3小匙(9克)；白糖3小匙(9克)；味精1小匙(3克)', '鸡蛋3个；鲜橙100克；淀粉适量');
INSERT INTO `recipe` VALUES (6053, 445, '鲜香豆腐', '1.豆腐切成厚片，瘦猪肉剁成末，生姜去皮切成末;\r\n2.把豆腐摆入深点的盘内，上面撒上瘦猪肉末、生姜末、青豆、精盐、味精;\r\n3.蒸锅烧开水，放入摆好的豆腐，用大火蒸5 分钟后打开盖，加入猪油，再蒸1 分钟，拿出即成。', '色泽淡雅，滑嫩爽口。', '蒸豆腐的时间不要过长，否则口感不好。', '猪油10克；精盐1小匙(3克)；味精1小匙(3克)', '豆腐200克；瘦猪肉10克；青豆10克；生姜1小块');
INSERT INTO `recipe` VALUES (6054, 445, '蟹肉豆腐', '1.豆腐切长条状；蟹肉解冻后入沸水中氽烫，捞出沥干;把面粉、淀粉、食用油、清水一起调成面糊，将蟹肉放入面糊内沾裹一层后，放入热油中炸酥捞出;\r\n2.蟹肉捞出后，以余油炸豆腐条，炸黄即捞出;\r\n3.香葱切丁，先用适量油炒香葱白，再放入炸好的蟹肉，将之与豆腐条、料酒、蚝油、白糖、胡椒粉、水淀粉炒匀，最后撒入香葱叶即可。', '蟹香味浓，质嫩爽滑。', '一定要将蟹肉烫透，否则用油炸时容易溅油。', '食用油500克；（实耗40克）；蚝油3小匙(9克)；料酒2小匙(6克)；胡椒粉少许；白糖1小匙(3克)', '豆腐300克；蟹肉100克；香葱2棵；面粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (6055, 445, '家乡豆腐', '1.把豆腐切成厚片，猪五花肉切成肉片，香葱切段，木耳用温水发开，豆瓣酱剁成细末；\r\n2.炒锅中放油烧热，放入豆腐片，煎成金黄色；\r\n3.锅中留少许油，将猪肉片炒香，加豆瓣酱末炒出红色，调入酱油、料酒、水，随即放入豆腐片、木耳、精盐和味精；\r\n4.烧开后调小火，将豆腐炖透，加入香葱段，用水淀粉勾芡，将汤汁收浓即可。', '咸鲜适口，味美下酒。', '煎豆腐时豆腐容易碎，也可以把豆腐放入油锅里炸好再用。', '食用油30克；酱油1/2大匙(15克)；料酒1/2大匙(15克)；豆瓣酱1大匙(30克)；精盐1小匙(3克)；味精1小匙(3克)', '豆腐300克；猪五花肉125克；木耳5克；香葱2棵；淀粉1大匙(30克)');
INSERT INTO `recipe` VALUES (6056, 445, '麻婆豆腐', '1.豆腐切丁，香葱、生姜、大蒜、干辣椒切细末备用；\r\n2.锅内放入油烧热，先爆香葱末、生姜末、大蒜末、干辣椒末和豆瓣酱，再放入猪肉馅炒熟，加入适量水，煮开后加入豆腐丁、酱油、白糖煮3 分钟，再用水淀粉勾芡，即可盛入盘中；\r\n3.烧热香油，爆香花椒，将花椒油淋在豆腐上即可。', '麻辣味浓，爽滑开胃。', '也可以用牛肉馅代替猪肉馅。', '食用油30克；香油1大匙(30克)；酱油1/2大匙(15克)；豆瓣酱1大匙(30克)；白糖2小匙(6克)', '豆腐300克；猪肉馅100克；香葱1棵；生姜1小块；大蒜6瓣；花椒1大匙(30克)；淀粉适量；干辣椒2个');
INSERT INTO `recipe` VALUES (6057, 445, '虾茸豆腐盒', '1.将豆腐切成长方片；虾仁剁成细茸，调入精盐和鸡蛋清搅匀；香葱、生姜切末；\r\n2.取一只大平盘，摆入一半量的豆腐，抹上虾茸，将剩余豆腐盖在虾茸上，然后撒上淀粉，沾匀蛋液待用；\r\n3 .平底煎锅中加少许油烧热，下入夹好虾茸馅的豆腐，煎至两面呈金黄色；\r\n4.加入香葱末、生姜末、高汤、精盐，焖至汁收干，加味精，淋香油出锅即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽金黄，味浓鲜嫩。', '这道菜应用有弹性的北豆腐做。', '食用油30克；香油1小匙(3克)；高汤7大匙(210克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐500克；虾仁150克；鸡蛋2个；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6058, 445, '糖醋豆腐', '1.将豆腐切成方块；把鸡蛋清打成泡沫，加入豆腐块和干豆粉；\r\n2.往锅中倒入油烧至五成热时，逐步放入豆腐块，待炸至金黄色后，捞起备用；\r\n3.锅中留少许油，倒入白糖、蕃茄酱、红醋、白醋，加入水淀粉勾芡，淋于豆腐上即可。', '色彩鲜艳，酸甜爽口。', '如果豆腐易碎，油炸前可先用沸水焯一下。', '食用油500克；（实耗50克）；番茄酱1大匙(30克)；白糖1大匙(30克)；红醋1/3大匙(10克)；白醋1/3大匙(10克)', '豆腐300克；鸡蛋3个；干豆粉适量；淀粉适量');
INSERT INTO `recipe` VALUES (6059, 445, '红烧豆腐', '1.豆腐切片后，用平底锅煎至双面呈金黄色;胡萝卜、木耳切片，蒜苗切斜段，红辣椒切段，大蒜、生姜切片备用;\r\n2.锅内放入油烧热，先爆香大蒜片、生姜片、红辣椒段，再加入高汤、蚝油、白糖，煮开后加入豆腐片、木耳片、胡萝卜片、甜豆荚、蒜苗段、精盐、味精一起煮3 分钟，再用水淀粉勾芡，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '脆嫩爽口，营养丰富。', '用卤水豆腐制作此菜，效果会更好。', '食用油30克；香油1小匙(3克)；蚝油1小匙(3克)；高汤2大匙(60克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '豆腐300克；胡萝卜30克；木耳30克；甜豆荚30克；蒜苗10克；红辣椒1个；生姜1小块；大蒜1瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6060, 445, '咸肉豆腐', '1.咸猪肉放清水中煮熟后捞出，汤汁留用;将香葱切段;\r\n2.将咸猪肉和豆腐都切成1.5 厘米见方的块，放入炒锅中，加入咸肉汤，用大火烧开;\r\n3.烧开后用小火焖烧，待豆腐浮起，加入香葱段和猪油即成。', '色亮形美，清香味鲜。', '因咸肉本身有咸味，所以可以不加盐。', '猪油少许', '豆腐250克；咸猪肉(带皮)100克；香葱2棵');
INSERT INTO `recipe` VALUES (6061, 445, '椒盐豆腐', '1.豆腐洗净后切成小方块;青、红椒去蒂和籽，切成环状;香葱切末;大蒜切片;\r\n2.锅内倒入油，烧至八成热时，将豆腐块入锅，至金黄色后捞出沥干油;\r\n3.锅内留少许油，大火爆香香葱末、大蒜片，再放入辣椒圈、精盐、味精、黑胡椒粉、花椒盐搅匀即可盛出;\r\n4.将所有调料与炸豆腐拌匀即可。', '外脆里嫩，风味独特。', '可先用盐、味精将豆腐略腌一会再炸，味道更佳。', '食用油250克；（实耗40克）；黑胡椒粉1/2小匙(1.5克)；花椒盐1/2小匙(1.5克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆腐250克；红辣椒1个；青辣椒1个；香葱1棵；大蒜6瓣');
INSERT INTO `recipe` VALUES (6062, 445, '西湖嫩豆腐', '1.豆腐切成小块，牛肉剁成末，生姜切成末，香菜切末；\r\n2.用锅烧开水，放入精盐、豆腐块、玉米粒，煮约2分钟，倒出待用；\r\n3.烧锅下油，放生姜末、牛肉末、高汤、精盐、味精、豆腐块、玉米粒煮透，用水淀粉勾芡，撒上香菜末即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '清淡爽滑，鲜嫩可口。', '可先用热水烫一下豆腐块，这样豆腐入锅时不易碎。', '食用油20克；高汤3大匙(90克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐150克；牛肉20克；玉米粒20克；生姜1小块；香菜1棵；淀粉适量');
INSERT INTO `recipe` VALUES (6063, 445, '口袋豆腐', '1.炸好的油豆腐从一方挖一个洞，再把内部的豆腐取出，然后轻轻地从底部翻卷过来；\r\n2.猪瘦肉剁成肉泥，荸荠去皮切成末，冬菇切成末，生姜切成末，青、红辣椒去籽切成环状，把肉泥、荸荠末、冬菇末、生姜末、部分味精、精盐、干淀粉搅打至起胶，然后填入挖好的油豆腐内；\r\n3.锅内放油，烧热，放入油豆腐，泡炸至内熟外脆，呈金黄色捞起；\r\n4.锅内留底油，下入青、红辣椒、炸好的油豆腐，注入高汤烧开，调入剩下的精盐、味精，煮至入味，淋入香油、红油即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '荤素搭配，汁浓味厚。', '豆腐炸好后也可以不放入锅内拌炒，直接把料汁淋在豆腐上即可。', '食用油500克；(实耗油50克)；香油1小匙(3克)；红油1小匙(3克)；高汤2大匙(60克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '油豆腐200克；猪瘦肉60克；荸荠20克；冬菇20克；青辣椒1个；红辣椒1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6064, 445, '咸蛋黄蒸豆腐', '1.豆腐切成大点的丁，咸鸭蛋蛋黄切成粒，虾仁从背部切一刀；\r\n2.烧开水，把豆腐放入煮片刻，捞起用凉水泡透；\r\n3.把泡凉的豆腐沥干水，拌入虾仁，调入精盐、味精、香油、猪油，上面撒上咸蛋黄粒，上蒸锅蒸5 分钟，拿出即成。', '清淡鲜美，风味独特。', '豆腐蒸制的时间不要过长，否则口感不好。', '猪油20克；香油1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐200克；咸鸭蛋蛋黄3个；虾仁10克');
INSERT INTO `recipe` VALUES (6065, 445, '红煨冻豆腐', '1.冻豆腐切块，香菇泡软，去蒂后对切;金针菇切除根部，用盐水氽烫后捞出;甜豆荚撕除两侧老筋，洗净备用;\r\n2.锅内倒入油，烧热，先炒香菇，然后放入金针菇和冻豆腐片，并加入蚝油、胡椒粉、白糖烧开，改小火煨入味;\r\n3.起锅前加入甜豆荚同烧，接着用水淀粉勾芡，汤汁黏稠即关火盛出。', '汁浓味鲜，美味可口。', '冻豆腐容易吸汁，所以要勾薄芡，以免汤汁过干。', '食用油25克；蚝油1/2大匙(15克)；胡椒粉1小匙(3克)；白糖1/2小匙(1.5克)', '冻豆腐350克；香菇10克；金针菇75克；甜豆荚75克；淀粉适量');
INSERT INTO `recipe` VALUES (6066, 445, '碧玉水晶豆腐', '1.将豆腐捣成泥;鸡脯肉剁成茸;香葱、生姜洗净切成末;青豆洗净，用开水焯一下捞出，沥干水分;\r\n2.取一大碗放入豆腐泥、鸡茸、香葱末、生姜末、精盐、料酒、白胡椒粉、味精、鸡蛋清、水淀粉，搅拌均匀做成玉米粒大小的丸子待用;\r\n3.锅内倒油，油温升至五成热时，放入丸子炸至变色，捞出沥干油分;\r\n4.锅内留底油，放入高汤、精盐、白胡椒粉，烧开后放入豆腐丸子、青豆，用水淀粉勾薄芡，淋入鸡油，汁浓时出锅即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '清新淡雅，鲜滑爽口。', '烧煨此菜的时间不要过长，以免豆腐丸散开。', '食用油500克；（实耗30克）；鸡油1小匙(3克)；高汤1大匙(30克)；料酒2小匙(6克)；白胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐300克；鸡蛋1个；青豆50克；鸡脯肉75克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6067, 445, '湘辣豆腐', '1.豆腐切成四方小块，放入热油中炸黄捞出;红辣椒去籽、切丁;香葱切花;大蒜切末;干辣椒切段；\r\n2.用适量油炒香大蒜末、红辣椒丁、干辣椒和豆豉后，加入酱油、白糖、精盐、味精炒匀，关火;\r\n3.倒入炸过的豆腐拌匀，装盘内，入蒸锅用中火蒸5分钟，取出时撒上香葱花即成。', '香辣可口，开胃下酒。', '炸过的豆腐容易吸汁，炒制时可加一点高汤或清水，以免菜肴过干。', '食用油500克；（实耗40克）；酱油1/2大匙(15克)；豆豉1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆腐300克；红辣椒2个；香葱1棵；大蒜8瓣；干辣椒2个');
INSERT INTO `recipe` VALUES (6068, 445, '双色豆腐', '1.豆腐切成长形厚片，放煎锅内将两面煎黄盛出；\r\n2.鸭血先切成3 等份长块，再横切片，放入沸水中汆烫后捞出；\r\n3.香葱洗净，斜切小段，锅内放油，先炒香葱，再放入豆腐片和鸭血块及所有调味料同烧入味即可。', '滑嫩爽口，红白相间。', '烧煨的时间不宜过长，否则口感不好。', '食用油30克；香油1小匙(3克)；酱油1/2大匙(15克)；黑胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆腐200克；鸭血200克；香葱4棵；淀粉适量');
INSERT INTO `recipe` VALUES (6069, 445, '鲜香鱼丸豆腐', '1.豆腐切成厚片，中间挖去一部分，西兰花切成朵；\r\n2.鲩鱼肉去皮，用刀剁成泥，加入部分味精和淀粉，打至起胶成馅，将馅填入豆腐中抹平，装盘；\r\n3.蒸锅烧开水，放入装好盘的豆腐，再用大火蒸6 分钟拿出，西兰花用开水烫熟，摆入豆腐旁边；\r\n4.锅内下油，烧热，注入高汤，调入剩下的精盐、味精、白糖、蚝油，烧开，用水淀粉勾芡，淋入香油，浇到豆腐上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '嫩滑可口，清淡鲜美。', '蒸制的时间不要过长，以免豆腐、鱼肉馅变老。', '食用油15克；香油1小匙(3克)；蚝油3小匙(9克)；高汤1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆腐200克；鲩鱼肉50克；西兰花30克；淀粉适量');
INSERT INTO `recipe` VALUES (6070, 445, '挂糊豆腐', '1.豆腐切成厚点的块，生姜去皮切成末；\r\n2.鸡蛋打碗内，加入干淀粉、清水、少许精盐调成糊；\r\n3.锅内下油烧热，把每片豆腐挂上糊，放入油锅炸至外脆里嫩捞起；\r\n4.锅内留底油，烧热，下生姜末，注入高汤烧开，调入精盐、味精，用水淀粉勾芡，淋到摆好的炸豆腐上即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽素淡，外脆里嫩。', '可把豆腐放入开水中烫一下，稍微拍点面粉后再挂糊。', '食用油500克；(实耗50克)；高汤1大匙(30克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐200克；鸡蛋1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6071, 445, '鱼子酱豆腐', '1.豆腐切成厚片，猪瘦肉、生姜切片，蒜苗切段;\r\n2.锅内下油，烧热，下入豆腐片，炸至金黄时捞起待用;\r\n3.锅内留底油，放入生姜片、肉片煸炒，下入豆腐，注入高汤，调入鱼子酱、精盐、味精、蚝油煮至入味，下蒜苗煮片刻，用水淀粉勾芡，淋上香油，出锅入盘即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜嫩可口，味道醇厚。', '炸好的豆腐容易吸汁，所以要多加点高汤，以免糊锅。', '食用油300克；(实耗油50克)；香油1小匙(3克)；蚝油3小匙(9克)；高汤2大匙(60克)；鱼子酱2/3大匙(20克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '豆腐200克；猪瘦肉20克；蒜苗10克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6072, 445, '麻辣臭豆腐', '1.将臭豆腐切成小块备用；洋葱、大蒜切末；干辣椒切段；\r\n2.将猪肉馅、肉酱以大火快速拌炒约1 分钟后起锅备用；\r\n3.锅内放油烧热，先爆香洋葱末、大蒜末、花椒、干辣椒，再加入料酒、蚝油、辣椒油、精盐、味精、臭豆腐以及炒好的肉酱、猪肉馅，最后加入水，以小火煮约5～8 分钟即可。', '酱香味浓，香飘四溢。', '如果先把臭豆腐放入油锅中炸一下，烧煨时就更容易入味。', '食用油1小匙(3克)；蚝油1大匙(30克)；辣椒油2大匙(60克)；料酒2大匙(60克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '臭豆腐300克；猪肉馅10克；肉酱罐头1/2罐；洋葱10克；大蒜6瓣；花椒1小匙(3克)；干辣椒2个');
INSERT INTO `recipe` VALUES (6073, 445, '金针菇日本豆腐', '1.金针菇洗净去根，日本豆腐均切成段，香葱切末，大蒜切片；\r\n2.锅内入油烧热后，放入日本豆腐，待炸成金黄色后，捞起；\r\n3.锅内留少许油，放入香葱、大蒜炒香，再加入少许水，倒入炸好的日本豆腐、金针菇翻炒几下；\r\n4.加入精盐、味精、酱油、蚝油翻炒均匀，淋入香油，起锅即可。', '质地酥嫩，口感顺滑。', '烧制前，先把金针菇放入开水中焯一下，以去除异味。', '食用油500克；（实耗30克）；香油1小匙(3克)；蚝油2小匙(6克)；酱油2小匙(6克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '日本豆腐300克；金针菇100克；香葱3棵；大蒜3瓣');
INSERT INTO `recipe` VALUES (6074, 445, '鱼香豆干', '1.豆干洗净，横剖成2 片再切成丝，放入热油锅中煎黄，盛出；\r\n2.银鱼洗净，沥干水分，放入碗中，加入料酒去除腥味备用；\r\n3.生姜、大蒜、香葱洗净，红辣椒洗净、去蒂及籽，均切末备用；\r\n4.锅中倒油烧热，放入银鱼炒香，加入豆豉、酱油、白糖、精盐、味精及其余所有材料炒熟即可。', '鱼香味浓，可口美味。', '如果觉得银鱼有鱼腥味，往锅内滴几滴醋即可去腥。', '食用油30克；酱油1/2大匙(15克)；料酒1大匙(30克)；豆豉1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '豆干300克；银鱼120克；红辣椒2个；香葱1棵；生姜1小块；大蒜5瓣');
INSERT INTO `recipe` VALUES (6075, 445, '椒爆香干', '1.香干切成片，青、红辣椒切成圈，大蒜切成片备用;\r\n2.锅内水烧开后，放入香干片氽烫，捞起，沥干水分;\r\n3.锅内放入少许油，下入香干、辣椒、大蒜煸炒，再下入所有调味料炒匀后，勾芡后淋上香油即可。', '咸鲜微辣，口感极佳。', '如果先用高汤煨一下香干，烧制出的味道更佳。', '食用油10克；香油1小匙(3克)；蚝油2小匙(6克)；酱油1小匙(3克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '香干250克；红辣椒4个；青辣椒2个；大蒜2瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6076, 445, '五彩豆皮', '1.芦笋洗净，去老皮，切小段；胡萝卜洗净，去皮，切条；香菇泡软，切片；韭菜洗净，放入沸水中略烫，捞出，沥干；干豆皮用水泡好，切成方形备用；\r\n2.豆皮摊平，分别适量包入已做好的配料，卷好，用韭菜绑牢，做成豆皮卷备用；\r\n3.锅中倒入食用油、酱油、白糖、精盐、香油、水烧开，放入豆皮卷，小火煮至汤汁收干即可。', '软烂清香，咸鲜适口。', '一定要用韭菜将豆皮绑牢，否则烧制时容易裂开。', '食用油20克；香油1小匙(3克)；酱油1大匙(30克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '干豆皮50克；韭菜20克；芦笋20克；胡萝卜20克；香菇20克');
INSERT INTO `recipe` VALUES (6077, 445, '八宝豆皮', '1.猪肉馅放入碗中，加入酱油拌匀；虾米放入碗中，加入料酒泡出香味，放入锅中蒸熟，取出沥干备用；\r\n2.白芝麻放入锅中炒香，盛出；胡萝卜去皮，切末；香菜洗净，切段；香菇泡软，切丝；\r\n3.把上述所有配料放入碗中，加入酱油、精盐、胡椒粉拌匀做成馅料备用；\r\n4.豆皮对半切开，分别包入适量的糯米饭及馅料，放入热油锅中煎熟，盛出，切块，放入盘中即可。', '味道特别，鲜香可口。', '可往糯米饭里加少量盐，这样味道会更好。', '食用油50克；酱油1大匙(30克)；料酒1大匙(30克)；胡椒粉1/2小匙(1.5克)；精盐1小匙(3克)', '豆皮6张；白芝麻25克；猪肉馅120克；香菇10克；胡萝卜50克；虾米20克；糯米饭60克；香菜2棵');
INSERT INTO `recipe` VALUES (6078, 445, '紫菜豆皮卷', '1.干豆皮用水泡好，切成方形备用；紫菜泡透洗净沙，沥干水分，生姜切末，香葱切花，然后把紫菜加入精盐、味精、白糖、生姜末、香葱花、淀粉、水拌匀；\r\n2.把豆皮铺平，放入拌好的紫菜，卷成卷，用湿淀粉封好口；\r\n3.锅内放油，烧热，下入素蛋卷，炸至内熟外金黄，捞起沥干油，切好即可。', '色泽金黄，外酥里嫩。', '如果将豆皮卷炸两遍，表皮会更脆。', '食用油500克；(实耗80克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '干豆皮50克；紫菜30克；香葱1棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6079, 445, '韭黄炒素肚丝', '1.素肚洗净、沥干，对半切开，再切成粗条；红辣椒去蒂及籽，洗净、切成丝；生姜洗净，切丝，泡入凉水中待用；蒜苗叶洗净、切丝；韭黄去黄叶和尾部，洗净、沥干，切段；\r\n2.锅中倒入油烧热，爆香红辣椒丝、素肚丝、生姜丝，加入料酒、韭黄段炒匀再加入酱油、白糖、精盐、胡椒粉、味精，拌炒均匀，淋上香油即可。', '清淡可口，味道鲜美。', '韭黄一定要炒透，否则有辛辣味。\r\n注：素肚是一种豆制品。', '食用油30克；香油1小匙(3克)；酱油1/2大匙(15克)；料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1/2小匙(1.5克)；味精1/2小匙(1.5克)', '素肚300克；韭黄150克；蒜苗叶适量；红辣椒1个；生姜1小块');
INSERT INTO `recipe` VALUES (6080, 445, '卤香面筋', '1.将面筋撕块，挤干水分，放入热油锅中炸至深黄色、表皮发脆时捞出沥油；\r\n2.锅内留底油，加入生姜、大料、桂皮炸出香味，放入面筋、酱油、白糖、味精，加高汤用大火烧开后，改小火焖约1 小时，待汤汁收干，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '软糯可口，香味浓郁。', '面筋必须炸透，否则口感不好。', '食用油500克；(实耗100克)；香油1小匙(3克)；酱油1大匙(30克)；高汤适量；白糖1小匙(3克)；味精1小匙(3克)', '面筋500克；生姜1小块；桂皮适量；大料适量');
INSERT INTO `recipe` VALUES (6081, 445, '红烧素牛肉', '1.面筋洗净，沥干水分，分别切4 等份，放入碗中加酱油拌匀，放入油锅中，以温油炸成金黄色，捞出沥干油；香葱、生姜洗净切末；胡萝卜去皮，洗净，切滚刀块；洋葱去皮、切块；\r\n2.锅内倒入油，爆香葱末、生姜末，加入洋葱块及料酒、高汤、热水煮匀，再加入面筋、胡萝卜块、大料及白糖、酱油煮开，捞除葱及姜末，改小火慢煮至汤汁收干，最后加入水淀粉勾芡，淋上香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '柔软浓香，独具风味。', '炸面筋时，油不要烧得过热，大约六成熟就可以。', '食用油500克；（实耗30克）；香油1小匙(3克)；酱油2大匙(60克)；高汤2大匙(60克)；料酒1/2大匙(15克)；白糖1/2小匙(1.5克)', '面筋100克；胡萝卜20克；洋葱20克；香葱2棵；生姜1小块；大料适量；淀粉适量');
INSERT INTO `recipe` VALUES (6082, 445, '香菇烧面筋', '1.将水发香菇剪去根蒂，洗净；水发玉兰片切成片；面筋切成两半；香葱洗净切末；\r\n2.将食用油放入炒锅内烧热，放入香菇、玉兰片稍煸，加入料酒、酱油、精盐、白糖、味精、高汤、面筋；\r\n3.烧开后，转小火烧约2 分钟，面筋变软后，转大火，用水淀粉勾芡，淋入香油，撒上葱花即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽金黄，柔软鲜香。', '不要将面筋烧得过于软烂，否则口感不好。', '食用油40克；香油1小匙(3克)；酱油1大匙(30克)；高汤8大匙(240克)；料酒2小匙(6克)；精盐1小匙(3克)；白糖1/2大匙(15克)；味精1/2小匙(1.5克)', '水发香菇75克；水发玉兰片30克；面筋100克；香葱1棵；淀粉适量');
INSERT INTO `recipe` VALUES (6083, 445, '滑炒腐竹木耳', '1.把腐竹、木耳泡软、洗净；腐竹切成段，投入沸水中烫熟透捞出，沥干水分；\r\n2.木耳洗净，撕成片，投入沸水内略烫，沥去水分。冬笋、青辣椒切片；红辣椒切成环状；\r\n3.锅内倒入油，烧热，投入腐竹发散滑透，盛出沥干油;\r\n4.锅内留底油，烧热，投入腐竹、木耳片、冬笋片、青辣椒片、红辣椒略煸，加酱油、白糖、精盐、味精、高汤，煮开，用水淀粉勾芡，炒匀装盘即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色彩淡雅，口味清鲜。', '应用冷水或温水泡发木耳。', '食用油400克；(实耗50克)；酱油1小匙(3克)；高汤1大匙(30克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1/2小匙(1.5克)', '腐竹100克；木耳100克；红辣椒1个；青辣椒2个；冬笋40克；淀粉适量');
INSERT INTO `recipe` VALUES (6084, 445, '海米烧腐竹', '1.将腐竹泡软后切成3 厘米长的小段；海米泡软；\r\n2.锅内放油，烧热，下海米、腐竹，加高汤，加入精盐、味精烧开，把腐竹烧透，将汁收稠即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '葱香浓郁，咸鲜细嫩。', '如果把干腐竹放入油锅中炸一下再用温水泡发，吃起来会更有韧性。', '食用油20克；高汤2大匙(60克)；精盐1小匙(3克)；味精1/2小匙(1.5克)', '海米20克；腐竹200克');
INSERT INTO `recipe` VALUES (6085, 446, '花生煲猪蹄', '补血养颜，汤鲜味浓。', '猪蹄汆水时一定要撇净血水，否则汤汁不白。', '厨房小窍门：', '料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '猪蹄150克；花生仁50克；香葱2棵；生姜1块');
INSERT INTO `recipe` VALUES (6086, 446, '黄豆炖猪蹄', '1.猪蹄洗净剁成块，生姜洗净切片，芹菜洗净去叶切段；\r\n2.锅内加水烧开，放入猪蹄块、料酒，用大火煮去猪蹄的血水，捞起待用；\r\n3.锅内下油，待油热时下生姜片、猪蹄爆炒，盛入砂锅内，加入水、黄豆，用小火炖约30分钟至汤汁白，放入精盐、味精、芹菜，撒入胡椒粉，出锅即可。', '美颜养肤，汤浓味香。', '如果先将黄豆煮熟，再同煮好的猪蹄一起炖，这样可以去掉豆腥味。', '食用油15克；料酒2小匙(6克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '猪蹄200克；水发黄豆100克；芹菜10克；生姜1块');
INSERT INTO `recipe` VALUES (6087, 446, '桂圆炖猪心', '1.将猪心剪开，切掉白色部分，洗净，切成薄片；将桂圆剥皮，洗净；\r\n2.将猪心片与桂圆一同放入砂锅内，先用大火煮沸，再用小火炖至猪心熟烂，放入调料即可。', '咸鲜微甜，营养丰富。', '猪心切开洗净后，最好先放在清水中浸泡1 小时，否则会有腥味。', '精盐2小匙(6克)；味精1小匙(3克)', '猪心250克；桂圆30克；注：可酌情配料');
INSERT INTO `recipe` VALUES (6088, 446, '酸菜肚片汤', '1.猪肚洗净，放入沸水中汆烫，捞起冲凉后切片；酸菜洗净、切丝；胡萝卜去皮、洗净，再切片；香葱洗净、切段；\r\n2.砂锅中倒适量水煮开，放入猪肚片、香葱段和料酒，炖2 小时，加入酸菜丝及胡萝卜片再炖20 分钟，最后加精盐、味精调味即可。', '汤鲜味美，风味独特。', '一定要将猪肚洗干净，否则汤汁不鲜美。', '料酒1大匙(30克)；精盐2小匙(6克)；味精1小匙(3克)', '猪肚600克；酸菜150克；胡萝卜50克；香葱1棵');
INSERT INTO `recipe` VALUES (6089, 446, '墨鱼肚片汤', '1.猪肚洗净，放入沸水中汆烫，捞起冲凉后切片；墨鱼干用温水泡软，去掉墨鱼的骨头，洗净后切丝；胡萝卜去皮、洗净，再切片；\r\n2.砂锅中倒适量水煮开，放入猪肚片、墨鱼丝和胡萝卜片，炖3 个小时，加入精盐、味精调匀即可。', '鲜咸适口，香味诱人。', '炖此汤时可加少许白酒和生姜，能有效去除腥味。', '精盐2小匙(6克)；味精1小匙(3克)', '猪肚600克；墨鱼干100克；胡萝卜50克');
INSERT INTO `recipe` VALUES (6090, 446, '莲藕排骨汤', '1.排骨洗净，砍成3 厘米长的节；刮尽莲藕表面的粗皮，切成块，洗净；生姜洗净切成两半；\r\n2.锅内放适量水，放入半块生姜、香葱、料酒，烧沸后，下入排骨，汆水后捞出待用；\r\n3.炒锅置火上，加水，下入排骨、半块生姜、香葱，用大火烧沸，去尽浮沫后改用小火，炖约20 分钟；\r\n4.把莲藕、排骨及汤汁一起倒进砂锅，再炖30 分钟，拣出生姜、香葱不用，放精盐、胡椒、味精即可。', '汤汁鲜美，藕香软糯。', '炖藕时不宜用铁锅，否则汤色会变黑。', '料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '排骨300克；莲藕300克；香葱2棵；生姜1块');
INSERT INTO `recipe` VALUES (6091, 446, '苦瓜排骨汤', '1.排骨洗净后砍成块，撒上五香粉，再在排骨两面拍上干面粉，鸡蛋打匀涂在排骨上面;苦瓜去籽洗净，切成厚片;香葱洗净切段，生姜洗净切片;香菇泡软洗净;\r\n2.把排骨放在砂锅底部，两边放上香菇;加料酒、水、香葱段、生姜片，用小火焖40 分钟;\r\n3.加精盐和苦瓜再炖15 分钟，撒上味精即可。', '清热解毒，风味独特。', '苦瓜不能煮太久，否则会变黄，而且没有清香味。', '料酒1大匙(30克)；五香粉2小匙(6克)；精盐2小匙(6克)；味精1小匙(3克)', '排骨400克；苦瓜200克；鸡蛋1只；香菇20克；香葱2棵；生姜1块；面粉适量');
INSERT INTO `recipe` VALUES (6092, 446, '墨鱼排骨汤', '1.排骨洗净砍成块，干墨鱼泡软、去骨，洗净切片，生姜洗净拍松；\r\n2.锅内加水，待水开时放入排骨块、墨鱼片，用中火煮至断生，捞起待用；\r\n3.在砂锅内投入排骨块、墨鱼片、生姜，注入清水，用小火煲2 小时，加精盐和味精即可。', '汤汁鲜美，香味浓郁。', '煲汤时一定撇净白沫，否则汤汁不白。', '精盐2小匙(6克)；味精1小匙(3克)', '排骨400克；墨鱼干50克；生姜1小块');
INSERT INTO `recipe` VALUES (6093, 446, '海带排骨汤', '1.排骨洗净，放入沸水中汆烫去血水，捞出，沥干水分；海带结洗净备用；生姜洗净拍松；\r\n2.砂锅内倒入水，放入排骨、生姜，用大火煮开，加入海带结，改小火继续煮1小时，最后加入精盐、味精调味即可。', '汤香味鲜，营养丰富。', '把海带结放入开水中烫一下，去除腥味后再煲，排骨汤的味道会更好。', '精盐2小匙(6克)；味精1小匙(3克)', '排骨200克；海带结120克；生姜1块');
INSERT INTO `recipe` VALUES (6094, 446, '冬瓜排骨汤', '1.排骨洗净，放入沸水中汆烫去血水，捞出，沥干水分；生姜洗净拍松；冬瓜切厚片；\r\n2.砂锅中放入清水，加入排骨、生姜，用大火烧开后，用小火煲40 分钟，待排骨熟透后加入冬瓜片；\r\n3.冬瓜煮熟后，加入精盐、味精、香油即可。', '淡香可口，清热解暑。', '冬瓜切成厚片后才不容易炖糊，口感也会更好。', '香油1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '冬瓜400克；排骨200克；生姜1块');
INSERT INTO `recipe` VALUES (6095, 446, '滑菇肉丸汤', '1.猪瘦肉洗净剁成肉泥，生姜洗净切丝，荸荠去皮洗净后切米；\r\n2.把肉泥、荸荠、精盐、味精、淀粉打成肉馅，烧热油锅，把肉馅做成丸子，炸成金黄色后捞起沥油；\r\n3.锅内留底油，放入生姜后加水烧开，下入肉丸煮至浮起，放入滑菇、精盐、味精、白糖煮透，淋入香油即可。', '菇滑肉香，汤鲜味美。', '下入肉丸后，不宜用大火，否则会把肉丸煮散。', '食用油500克；（实耗40克）；香油1小匙(3克)；精盐2小匙(6克)；白糖1小匙(3克)；味精1小匙(3克)', '猪瘦肉100克；滑菇100克；荸荠15克；生姜1块；淀粉适量');
INSERT INTO `recipe` VALUES (6096, 446, '豆腐肉丸汤', '1.豆腐洗净切块，西红柿洗净切片，生姜洗净切片，香葱洗净切花；\r\n2.猪瘦肉洗净剁成肉泥，加入精盐、味精、淀粉打至起胶，做成小肉丸待用；\r\n3.锅内放油，烧热，放入生姜片、水烧开，轻轻放入做好的肉丸，用小火煮至快熟时，加入豆腐块、西红柿片、精盐、味精，撒上香葱花，淋入香油即可。', '鲜嫩可口，汤鲜味美。', '拌肉馅时，可稍微加一点葱姜水，这样肉丸更嫩，味道更好。', '食用油10克；香油1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '猪瘦肉100克；豆腐150克；西红柿20克；香葱2棵；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6097, 446, '马齿苋肉丝汤', '1.猪瘦肉洗净，切丝，用酱油、淀粉腌渍；\r\n2.马齿苋择洗干净，掐断；大蒜洗净捣成茸；\r\n3.锅内放油，烧热，爆香大蒜茸，加适量清水，下马齿苋煮至六成熟；\r\n4.加肉丝煮熟，用精盐调味即可。', '味道独特，咸鲜味美。', '最好先把肉丝放入沸水中汆熟，再放入汤内，这样汤汁会更清爽。', '食用油25克；酱油2小匙(6克)；精盐2小匙(6克)', '猪瘦肉150克；马齿苋250克；大蒜2瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6098, 446, '莼菜鱼柳汤', '1.鱼肉洗净，剁末，加入肥肉馅同剁至匀，拌入鸡蛋清、精盐、淀粉，搅拌成有弹性的鱼浆；\r\n2.高汤烧开后关火，将搅匀的鱼浆装入塑料袋内，剪开一角，将鱼浆挤成条状，滑入汤中，全部挤完再开火煮沸，并轻轻搅动使它成小段；\r\n3.将莼菜倒入汤内同煮，并加精盐调味；\r\n4.淋入水淀粉勾芡，汤汁稍稠时关火，食用时撒入胡椒粉即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '脆爽可口，清淡鲜美。', '可用家用榨汁机搅拌鱼肉馅。', '高汤15大匙(450克)；胡椒粉少许；精盐1小匙(3克)', '罐装莼菜1听；鱼肉300克；肥肉馅75克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (6099, 446, '菜花鲫鱼汤', '1.鲫鱼宰杀洗净，再用盐水浸泡5 分钟后洗净；\r\n2.菜花去粗梗，洗净，切成朵；生姜洗净切片；\r\n3.锅内放油，烧热，把生姜爆香，下鲫鱼稍煎；\r\n4.加适量水，煮30 分钟，下香油、菜花煮熟；\r\n5.撒入胡椒粉、精盐、味精调味即可。', '咸鲜味美，口味清淡。', '如果把鲫鱼煎黄后放入砂锅慢慢煲，味道会更纯正。', '食用油25克；香油1小匙(3克)；胡椒粉少许；精盐1小匙(3克)；味精1/2小匙(1.5克)', '鲫鱼1条(约250克)；菜花120克；生姜1小块；注意：可酌情配料');
INSERT INTO `recipe` VALUES (6100, 446, '奶汤鲢鱼头', '1.将鲢鱼头挖鳃洗净，用刀劈成两片，放入沸水中烫一下，捞出沥水；火腿切片；豌豆苗洗净；香葱洗净后切段；生姜洗净拍松；\r\n2.锅内放油，烧热，放入鲢鱼头煎至两面呈黄色时，加入料酒、香葱段、生姜、水，大火烧开后，加盖再煮10分钟，烧至鱼头酥熟，汤汁乳白时将鱼头捞入汤碗中，放上豌豆苗；\r\n3.把原锅内的汤烧开，捞出葱段和姜，加入味精和精盐，倒入汤碗内，放上火腿片，淋上鸡油即可。', '汤汁浓白，肉质鲜嫩。', '一定要等汤汁变白后再放盐，否则汤味不醇。', '食用油30克；鸡油3小匙(9克)；料酒1大匙(30克)；精盐2小匙(6克)；味精1小匙(3克)', '鲢鱼头500克；火腿25克；豌豆苗10克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (6101, 446, '鱼头炖豆腐', '1.将胖头鱼头收拾干净，从中间切开；香葱、生姜、大蒜洗净切片；豆腐切长条；\r\n2.锅内放油，烧热，放入胖头鱼头稍炸，捞出沥油；余油爆香香葱片、生姜片、大蒜片，烹入料酒、酱油，加入适量开水，再放入胖头鱼头、白糖、精盐、味精和胡椒粉；\r\n3.汤烧开后，将豆腐下锅，小火慢炖，待烧透后，取出胖头鱼头放入盘中；汤汁烧开后，用水淀粉勾芡，烧透，浇入盘中即可。', '肉质滑润，入口醇香。', '炖汤时可加几滴醋或酒，味道会更加鲜美。', '食用油30克；酱油2小匙(6克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；白糖1小匙(3克)；味精1小匙(3克)', '胖头鱼头1个（约500克）；豆腐200克；香葱2棵；生姜1小块；大蒜3瓣；淀粉适量');
INSERT INTO `recipe` VALUES (6102, 446, '鸡丝银鱼汤', '1.干银鱼泡软洗净；鸡脯肉去筋，洗净切细丝，用鸡蛋清、淀粉、精盐、酱油腌渍；生姜洗净切末；\r\n2.炒锅内放入高汤、料酒、精盐，下银鱼烧开，捞出沥干装入砂锅内；\r\n3.炒锅内添加高汤，下入鸡丝，煮至鸡肉成白色时取出倒入砂锅中；\r\n4.砂锅内加精盐烧开，撇去浮沫，撒上生姜末、胡椒粉即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤汁清澈，味道鲜美。', '一定要把银鱼泡透、泡软，否则口感不好。', '酱油1小匙(3克)；高汤15大匙(450克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '干银鱼50克；鸡脯肉200克；鸡蛋1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6103, 446, '银鱼蛋汤', '1.干银鱼泡软洗净沥干，榨菜洗净切细丝；韭黄洗净切段；鸡蛋打散；生姜洗净切丝；\r\n2.锅内放猪油，烧热，稍煎一下银鱼，放入榨菜丝、韭黄段、生姜丝、高汤，煮10 分钟，加精盐，再缓缓倒入鸡蛋；\r\n3.用味精、胡椒粉调味即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '味淡鲜美，色泽清新。', '一定要烧开汤汁后再倒入鸡蛋，否则会有蛋腥味。', '猪油20克；高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '干银鱼50克；榨菜15克；鸡蛋2个；韭黄80克；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6104, 446, '金菇田螺汤', '1.金针菇切除根部后洗净，先用盐水汆烫，然后捞出；豆腐切成条状，香葱洗净切段；\r\n2.锅内加水烧开，倒入半罐田螺肉汤，并放入田螺肉和豆腐同煮；\r\n3.加入金针菇、精盐，煮开后放入香葱，关火，最后撒上胡椒粉即可食用。', '汤汁鲜美，清淡适口。', '做此汤也可以用新鲜田螺肉。', '胡椒粉1小匙(3克)；精盐1小匙(3克)', '金针菇50克；罐装田螺肉1听；豆腐300克；香葱2棵');
INSERT INTO `recipe` VALUES (6105, 446, '冬瓜文蛤汤', '1.冬瓜去皮去籽，切成2 厘米见方的块;生姜、香葱洗净;\r\n2.文蛤用清水清洗干净，放入沸水中加精盐、生姜、香葱、料酒汆去腥味；\r\n3.待文蛤张开后，除去内脏及泥沙；\r\n4.往汤碗内倒水，放入文蛤，上蒸锅蒸制半小时，出鲜味后取出；\r\n5.往汤内加冬瓜块，用精盐、胡椒粉、味精调味，加盖再蒸10 分钟即可。', '汤色清澈，清香鲜美。', '刚买回的新鲜文蛤要放入清水中浸泡8 小时，等文蛤吐净泥沙后再做汤。', '料酒1大匙(30克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '冬瓜300克；文蛤300克；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (6106, 446, '丝瓜鲜虾汤', '1.将鲜虾去皮洗净，挤干水分，用精盐、料酒、味精、鸡蛋清、淀粉上浆，放入冰箱冰镇10分钟后取出待用；香菜洗净切段；\r\n2.丝瓜洗净去皮，切成厚圆片，用小刀挖去中间的籽，制成瓜环片，再将鲜虾逐个穿入每个瓜环中；\r\n3.往锅内倒入高汤，烧开后放入虾环，加入精盐、料酒、胡椒粉、味精，待虾环断生后捞出放入碗中，再往锅里加香油、香菜煮开，倒在虾环上即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '味道鲜美，清淡可口。', '虾仁如果不上浆，味道也很鲜美。', '香油1大匙(30克)；高汤15大匙(450克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1/2小匙(1.5克)', '丝瓜200克；鲜虾100克；鸡蛋1个；香菜1棵；淀粉适量');
INSERT INTO `recipe` VALUES (6107, 446, '菠菜干贝汤', '1.干贝洗净，放碗内加料酒、清水浸泡30分钟；菠菜洗净，撕开；鸡蛋只取蛋清；\r\n2.干贝上蒸锅蒸30 分钟后取出，揉散，汤汁留用；\r\n3.锅内倒入油，烧热，慢慢倒入蛋清，煎成蛋花盛出；\r\n4.烧沸蒸干贝的汤，下入干贝、精盐、胡椒粉、蛋花，淋入鸡油，倒入菠菜煮熟，用水淀粉勾芡即可。', '色泽清新，味淡鲜美。', '蒸干贝时如果放入少许葱姜，干贝的味道会更加鲜美。', '食用油20克；鸡油1小匙(3克)；料酒1/4大匙(7.5克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '干贝10克；菠菜250克；鸡蛋2个；淀粉适量');
INSERT INTO `recipe` VALUES (6108, 446, '海鲜丝瓜汤', '1.豆腐洗净，切片；海参、鱿鱼泡发后洗净切片；火腿切薄片；\r\n2.丝瓜洗净，斜切成小片；香葱、生姜洗净切末；\r\n3.锅内放油，烧热，爆香香葱末、生姜末，放入海参片、鱿鱼片、火腿片略炒，加高汤煮沸；\r\n4.加豆腐片及胡椒粉、精盐、味精煮5 分钟，下丝瓜片煮熟即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '清淡可口，汤味鲜美。', '如果先把豆腐放入开水中焯一下再做汤，豆腐就不容易破碎了。', '食用油20克；高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '丝瓜150克；豆腐100克；海参15克；鱿鱼15克；火腿50克；香葱1棵；生姜1小块；注：可酌情配料');
INSERT INTO `recipe` VALUES (6109, 446, '鲜香河蚌汤', '1.河蚌肉洗净，黄豆泡透，枸杞泡好，生姜洗净切丝，香葱洗净切花；\r\n2.锅内放油，待油热时放入生姜丝、河蚌肉炒片刻，倒入料酒，加入黄豆，用中火煮；\r\n3.待煮至汤汁奶白时，放入枸杞，调入精盐、味精、胡椒粉烧开，撒入香葱花，淋入香油即可；', '味道清淡，汤汁鲜美。', '河蚌肉炒制的时间不要过长，否则肉质会变硬。', '食用油10克；香油1小匙(3克)；料酒1小匙(3克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '鲜河蚌肉200克；黄豆10克；枸杞5克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (6110, 446, '金菇鲜蛤汤', '1.金针菇切除根部后洗净，用盐水汆烫后捞出备用；生姜洗净切片；\r\n2.把高汤倒入锅内，蛤蜊吐净沙后放入，豆腐切块，放入同煮，随后加入金针菇及生姜片；\r\n3.蛤蜊壳张开后，加精盐调味，关火，并撒入胡椒粉即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤汁鲜美，菇滑肉嫩。', '金针菇氽水后应立即用冷水冲凉，以防止金针菇氧化变黑。', '高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '金针菇150克；蛤蜊300克；豆腐100克；生姜1小块');
INSERT INTO `recipe` VALUES (6111, 446, '蛤蜊山药汤', '1.蛤蜊肉用温盐水浸泡，去杂洗净，沥干，倒入碗中放入蒸锅中蒸1 个小时;\r\n2.山药洗净去皮切片，玉竹洗净切片，百合洗净，生姜洗净切片;\r\n3.锅内放猪油，烧热，爆香生姜片，烹入料酒，加适量清水;\r\n4.倒入蛤蜊肉及蒸蛤蜊的汤，放入山药、玉竹、百合和精盐;\r\n5.用大火烧开，再改用小火炖至肉熟味香即可。', '营养丰富，味道鲜美。', '山药不要切得太薄，否则容易炖糊。', '猪油10克；料酒2小匙(6克)；精盐2小匙(6克)', '蛤蜊肉250克；山药100克；百合50克；玉竹50克；生姜1小块');
INSERT INTO `recipe` VALUES (6112, 446, '虾仁雪菜汤', '1.虾仁洗净，雪里蕻洗净切段；\r\n2.豆腐切块，香葱洗净切段，生姜洗净切片；\r\n3.锅内放猪油，烧热，爆香生姜片，下虾仁略炒；\r\n4.再放入适量清水煮开，加入雪里蕻段、豆腐块和香葱；\r\n5.再次煮开后放入精盐、味精、香油调味即可。', '风味独特，清淡可口。', '如果把雪里蕻放入锅内煸炒一下再做汤，味道也很好。', '猪油20克；香油1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '虾仁50克；雪里蕻150克；豆腐100克；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (6113, 446, '香菇鸡汤', '1.土鸡腿洗净剁小块，汆烫后捞出；香菇泡软、去蒂，红枣泡软，生姜洗净拍松；\r\n2.把所有材料放入砂锅，倒入料酒，再加适量清水，烧开后用小火炖1 个小时；\r\n3.最后放精盐、味精调味，拌匀后即可。', '汤鲜味美，营养丰富。', '汤未煮好前，不要加精盐，这样鸡肉更嫩、汤汁更鲜美。', '料酒1大匙(30克)；精盐2小匙(6克)；味精1小匙(3克)', '土鸡腿300克；香菇30克；红枣10克；生姜1小块');
INSERT INTO `recipe` VALUES (6114, 446, '清炖乌鸡汤', '1.乌鸡宰杀洗净，放沸水中焯水，除去血水；\r\n2.把乌鸡、料酒、香葱、生姜放入砂锅内，用大火烧开后，改小火炖2 小时，加入精盐、味精即可。', '汤汁鲜美，鸡肉香嫩。', '乌鸡营养丰富，尤其适合女性食用。', '料酒2小匙(6克)；精盐2小匙(6克)；味精1小匙(3克)', '乌鸡1只（约800克）；香葱2棵；生姜1小块');
INSERT INTO `recipe` VALUES (6115, 446, '三鲜汤', '1.将鸡脯肉剔净筋膜，切成片；水发海参剖腹去内脏，洗净，切成片；火腿切片，黄瓜洗净，切成菱形片；生姜洗净切片；\r\n2.再往鸡片中加精盐、料酒、胡椒粉、鸡蛋清、水、淀粉拌匀，腌制片刻；\r\n3.把鸡片、海参分别放沸水中氽透捞出，再把鸡片、海参片、火腿片放入汤碗中，淋入香油拌匀；\r\n4.往锅内放入高汤、生姜，烧开后撇去浮沫，加入精盐、味精后倒入汤盆中，再放入黄瓜片即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '软嫩味美，爽滑适口。', '如果多加点胡椒粉和醋，这道汤就可以做成酸辣三鲜汤。', '香油1小匙(3克)；高汤20大匙(600克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '鸡脯肉150克；水发海参100克；火腿50克；黄瓜25克；鸡蛋1个；生姜1小块；淀粉适量');
INSERT INTO `recipe` VALUES (6116, 446, '鸡肝豆苗汤', '1.摘去附在鸡肝上的苦胆，略洗;\r\n2.将鸡肝切成薄片，加料酒和适量清水浸泡2分钟。豌豆苗洗净;\r\n3.锅内放入高汤烧开，保持微沸状态，撒入鸡肝片，用小火汆至嫩熟捞出，放入汤碗内，上面放上豌豆苗;\r\n4.撇去锅内汤面上的浮沫，下精盐、味精、胡椒粉调好味，烧沸起锅，倒入汤碗内即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽鲜亮，汤汁鲜美。', '做鸡肝汤时放入一点豌豆苗，可使得汤汁更加清爽。', '高汤10大匙(300克)；料酒1/2大匙(15克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '鸡肝25克；豌豆苗50克');
INSERT INTO `recipe` VALUES (6117, 446, '老鸭肚片汤', '1.将老鸭宰杀后切块煮熟，猪肚洗净切片煮熟；香葱洗净切段，生姜洗净拍松；\r\n2.锅中倒入油，爆香香葱段、生姜，倒入煮鸭块的汤汁，再放入鸭块、猪肚慢炖40 分钟；\r\n3.最后加入调味料即可。', '汤鲜味美，鸭肉软烂。', '一定要把猪肚洗净，否则会有异味。', '食用油20克；料酒2小匙(6克)；胡椒粉少许；精盐2小匙(6克)；味精1小匙(3克)', '老鸭半只（约800克）；猪肚200克；香葱3棵；生姜1块');
INSERT INTO `recipe` VALUES (6118, 446, '老鸭香芋汤', '1 .老鸭肉洗净后投入沸水中焯一下，漂洗干净切成块；火腿切片，香葱洗净打结，生姜洗净拍松；油菜心洗净掰开；\r\n2.芋头去皮，切片，投入油锅中滑炒一下；\r\n3.把鸭肉块、火腿片、高汤、生姜、香葱结、料酒和精盐放入砂锅，用大火烧沸后改中火煮2 小时，把鸭肉烧至六成熟时加入芋头片；\r\n4.煨至鸭肉熟烂、汤汁乳白时下入油菜心，撒入味精、淋入香油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜香味美，芋头软糯。', '芋头和鸭子不能同时放入锅内炖，否则会把芋头炖成糊状。', '食用油20克；香油2小匙(6克)；料酒1/2大匙(15克)；高汤1大匙(30克)；精盐3小匙(9克)；味精1小匙(3克)', '净老鸭肉500克；芋头200克；油菜心150克；火腿50克；香葱3棵；生姜1块');
INSERT INTO `recipe` VALUES (6119, 446, '西红柿牛肉汤', '1.豆皮洗净切成条状，西红柿洗净对切成两半；\r\n2.牛肉洗净切块，放入烧开的高汤中煮至八成熟，去掉浮油，再放入西红柿、豆皮，煮至汤色变红；\r\n3.最后放入香油、精盐、味精即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '营养丰富，汤汁鲜美。', '如果加点番茄酱，汤汁会更加醇厚。', '香油2小匙(6克)；高汤30大匙(900克)；精盐3小匙(9克)；味精1小匙(3克)', '牛肉1000克；西红柿200克；豆皮100克');
INSERT INTO `recipe` VALUES (6120, 446, '南瓜牛肉汤', '1.南瓜削去硬皮，洗净，切成约3 厘米大小的方块；生姜洗净拍松，香葱洗净打结；\r\n2.牛肉剔去筋膜，洗净切成约2 厘米见方的块，先放入沸水中略焯一下，再放入锅内，加入高汤，待牛肉煮熟后，加入南瓜块、生姜、香葱结同煮，待牛肉熟透，用胡椒粉、精盐调味即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '味道鲜美，营养丰富。', '把牛肉煮熟后再加入南瓜同煮，因为南瓜易熟。', '高汤20大匙(600克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '牛肉250克；南瓜500克；香葱2棵；生姜1块；注：可酌情配料');
INSERT INTO `recipe` VALUES (6121, 446, '滋补羊肉汤', '1.羊肉洗净剁成块，党参切段洗净，红枣、枸杞泡透，生姜洗净切片；\r\n2.锅内加水，待水开时放入羊肉块，用中火煮去血水，捞起冲净待用；\r\n3.在汤碗内放入羊肉块、生姜片、党参段、红枣、枸杞、精盐、味精、白糖、胡椒粉、料酒，注入清水，放入蒸锅蒸2 小时拿出即可。', '滋补祛寒，汤鲜肉烂。', '煲羊肉时滴入一滴白酒，即可去膻味，也可使肉酥烂得更快。', '料酒1/2大匙(15克)；胡椒粉少许；精盐2小匙(6克)；白糖1/2小匙(1.5克)；味精1小匙(3克)', '羊肉500克；党参10克；红枣10克；枸杞10克；生姜1块');
INSERT INTO `recipe` VALUES (6122, 446, '浓汤驴肉煲', '1.驴肉和驴骨头用清水洗净;香葱洗净打结，生姜洗净拍松，香菜洗净切末;\r\n2.将驴肉、驴骨头放入大锅中加香葱结、生姜、大料同煮，驴肉至肉烂时捞出，切片;\r\n3.待汤汁呈乳白时，再放入驴肉片烧开，加精盐、味精、胡椒粉、料酒、香油即可。', '汤汁醇厚，驴肉鲜嫩。', '煮驴肉时可稍加一点白酒，驴肉会熟烂得更快。', '香油2小匙(6克)；料酒1大匙(30克)；胡椒粉2小匙(6克)；精盐2小匙(6克)；味精1小匙(3克)', '驴肉300克；驴骨头200克；香葱2棵；生姜1块；大料适量');
INSERT INTO `recipe` VALUES (6123, 446, '酸辣汤', '1.豆腐切成条状，猪血切成薄片；水发黑木耳泡好，洗净切丝；竹笋、胡萝卜洗净切丝；金针菇洗净，切去根部剥开；\r\n2.往锅内倒入一大碗水，煮开，加入切好的材料(豆腐除外)、精盐、辣椒粉续煮至再开时，用水淀粉勾芡拌匀，熄火前放入豆腐条、猪血片稍煮，再加入胡椒粉、香醋、香油即可；', '酸辣开胃，诱人食欲。', '酸辣汤内还可以放入豆芽，这样口感更鲜美。', '香油2小匙(6克)；胡椒粉1小匙(3克)；辣椒粉1/2大匙(15克)；香醋1大匙(30克)；精盐2小匙(6克)', '豆腐300克；猪血50克；竹笋15克；胡萝卜15克；金针菇15克；水发黑木耳15克；淀粉适量');
INSERT INTO `recipe` VALUES (6124, 446, '香菇丝瓜汤', '1.先将丝瓜去皮、切成片。香菇泡软后洗净，切成小块；\r\n2.炒锅内放入食用油，烧热后倒入丝瓜煸炒片刻，放精盐，然后将香菇和清水倒入锅中同煮至熟，加入味精、香油即可。', '清爽鲜美，味淡可口。', '也可以用新鲜香菇做这道汤。', '食用油10克；香油2小匙(6克)；精盐2小匙(6克)；味精1小匙(3克)', '丝瓜200克；香菇5克');
INSERT INTO `recipe` VALUES (6125, 446, '鲜菇番茄汤', '1.平菇洗净撕成条；草菇剥去外膜，洗净后用盐开水快速汆烫后捞出冲凉；\r\n2.猪瘦肉洗净切片，拌入料酒、酱油、淀粉腌10 分钟；西红柿洗净，切成片；黄瓜洗净切斜片；\r\n3.高汤放锅内，烧开后先关火，放入猪肉片后再开火，并加入平菇、草菇和番茄片同煮，然后加精盐、味精调味；\r\n4.最后放入黄瓜片，一煮熟即关火盛出。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽美观，鲜嫩滑爽。', '千万别放多了酱油，否则汤汁的颜色不好看。', '酱油1小匙(3克)；高汤15大匙(450克)；料酒2小匙(6克)；精盐2小匙(6克)；味精1小匙(3克)', '平菇150克；草菇150克；猪瘦肉150克；西红柿100克；黄瓜50克；淀粉适量');
INSERT INTO `recipe` VALUES (6126, 446, '鲍菇蚕豆汤', '1.银耳用清水泡发，摘掉硬黄部分，撕碎；\r\n2.鲍鱼菇洗净切片，蚕豆洗净；\r\n3.把高汤与鲍鱼菇片同放入锅内，煮开；\r\n4.下蚕豆、精盐煮熟，加银耳略煮；\r\n5.用胡椒粉调味即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '滑爽可口，汤汁鲜美。', '银耳不要煮太长时间，否则口感不脆。', '高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '鲍鱼菇250克；蚕豆50克；银耳10克');
INSERT INTO `recipe` VALUES (6127, 446, '蛋皮豆腐汤', '1.豆腐切成丁，猪瘦肉剁成末，生姜切末，香菜切段；\r\n2.鸡蛋打散，将平底锅烧热，下少许油，倒入鸡蛋摊成蛋皮，倒出切成丁状；\r\n3.烧锅放油，放生姜，注入高汤煮开，下豆腐丁、瘦肉末，调入精盐、味精、白糖，用水淀粉勾芡，加入鸡蛋，淋入香油，放入香菜即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '蛋香宜人，爽滑可口。', '摊鸡蛋皮时一定要厚薄均匀。', '食用油30克；香油1小匙(3克)；高汤3大匙(90克)；精盐5克；白糖3克；味精5克', '豆腐150克；猪瘦肉10克；鸡蛋1个；生姜1小块；香菜1棵；淀粉适量');
INSERT INTO `recipe` VALUES (6128, 446, '苦瓜皮蛋汤', '1.苦瓜洗净去籽、瓤后切条，皮蛋去壳后切块；\r\n2.往锅中倒入高汤、水，烧开后加入苦瓜条、皮蛋块，加入调味料烧开即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '清淡爽口，去火开胃。', '做汤前，先用盐将苦瓜腌几分钟，然后洗净，可以去掉部分苦味。', '高汤1大匙(30克)；胡椒粉少许；精盐2小匙(6克)；味精1小匙(3克)', '苦瓜200克；皮蛋2个');
INSERT INTO `recipe` VALUES (6129, 446, '火腿冬瓜汤', '1.西红柿用开水微烫，去皮后切成月牙块；\r\n2.冬瓜去皮、去籽，洗净切成长片，火腿切成长片；\r\n3.把高汤、冬瓜片、火腿片放入锅内，烧开，至冬瓜熟透时，用精盐、胡椒粉、味精调味，起锅装入汤碗中；\r\n4.把西红柿块放入汤内即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤鲜味美，颜色鲜明。', '冬瓜要切成厚片，这样熟透后口感更好。', '高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '火腿50克；冬瓜250克；西红柿50克');
INSERT INTO `recipe` VALUES (6130, 446, '山药鸡茸羹', '1.山药洗净，去皮，捣成泥；芹菜洗净，切末；\r\n2.鸡脯肉洗净，剁成茸，放入碗中，加入精盐和淀粉拌匀备用；\r\n3 .往锅里倒适量水，加入山药泥及鸡茸，用中火煮10～15 分钟，再加入芹菜末与精盐、胡椒粉煮匀，最后加入水淀粉勾芡即可。', '汤汁鲜美，营养丰富。', '往锅里加鸡茸时要调成小火，以免鸡茸结成一团。', '胡椒粉1小匙(3克)；精盐2小匙(6克)', '山药300克；鸡脯肉150克；芹菜10克；淀粉适量');
INSERT INTO `recipe` VALUES (6131, 446, '银鱼翡翠羹', '1.干银鱼泡软洗净；菠菜洗净，用沸水汆烫后再用冷水泡凉，挤干水分后切成细末；\r\n2.高汤煮开后，加入银鱼、菠菜末，再次煮开时放入精盐，再用水淀粉勾稠芡，淋上鸡蛋清及香油、胡椒粉、味精即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽清新，鲜美爽口。', '一定要将银鱼泡透、泡软，否则口感不好。', '香油3小匙(9克)；高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '干银鱼25克；菠菜300克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (6132, 446, '西湖牛肉羹', '1.把瘦牛肉洗净剁成茸，放入沸水中汆熟，捞出；豆腐切成丁，香菜洗净切末；\r\n2.往锅中倒清水，放入牛肉茸、豆腐丁烧开，调入精盐、味精，倒入鸡蛋清、香菜末、胡椒粉即可。', '口味清淡，爽口开胃。', '先把牛肉氽熟，可使得汤汁更清澈。', '胡椒粉1小匙(3克)；精盐2小匙(6克)；味精1小匙(3克)', '瘦牛肉100克；豆腐100克；鸡蛋1个；香菜1棵');
INSERT INTO `recipe` VALUES (6133, 446, '宋嫂鱼羹', '1.香葱、生姜、熟竹笋、水发香菇切丝，鸡蛋黄打散;鳜鱼切成两片，加香葱丝、生姜丝、料酒、精盐，放入蒸锅用大火蒸6分钟左右，取出;锅内放猪油，下香葱丝煸至有香味，加入高汤煮开，放入料酒、熟竹笋丝、香菇丝，煮开后，把鳜鱼倒入锅内，加酱油、精盐、味精煮开，用水淀粉勾薄芡;\r\n2.然后将蛋黄液倒入锅内搅匀，待汤再开时加香醋，浇上六成热猪油即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜嫩润滑，味似蟹羹。', '鳜鱼肉蒸制的时间要稍长一些，才可以使鱼肉更入味。', '猪油50克；酱油1大匙(30克)；高汤10大匙(300克)；料酒1大匙(30克)；香醋1大匙(30克)；精盐2小匙(6克)；味精1小匙(3克)', '鳜鱼1条（约600克）；熟竹笋15克；水发香菇15克；鸡蛋2个；香葱2棵；生姜1块；淀粉适量');
INSERT INTO `recipe` VALUES (6134, 446, '芥菜豆腐羹', '1.芥菜菜切除老叶及粗梗，洗净，放入开水中汆烫后捞出，再用冷水冲凉，然后切碎；\r\n2.把高汤烧开，加入精盐及水淀粉勾芡成羹状，然后放入切成丁的豆腐煮开；\r\n3.放入切碎的芥菜，再度煮开即关火盛出，淋香油、撒胡椒粉后即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽清新，淡雅鲜美。', '此菜只能勾薄芡，否则汤汁容易凝固，影响口感。', '香油2小匙(6克)；高汤15大匙(450克)；胡椒粉1小匙(3克)；精盐2小匙(6克)', '芥菜150克；豆腐200克；淀粉适量');
INSERT INTO `recipe` VALUES (6135, 446, '青豆玉米羹', '1.将玉米粒洗净，放入适量开水，上笼蒸1小时取出；菠萝切成小颗粒；\r\n2.锅内放入适量水和冰糖，烧开，待糖溶化后，放入玉米粒、菠萝粒、青豆烧开，用水淀粉勾芡，装入汤碗内即可。', '汁浓香甜，营养丰富。', '如果用罐装玉米羹做这道菜，味道同样鲜美，而且省时省力。', '冰糖250克', '新鲜嫩玉米400克；罐装菠萝25克；青豆25克；淀粉适量');
INSERT INTO `recipe` VALUES (6136, 447, '香甜南瓜饼', '1.南瓜去皮，洗净后刨成丝，放大碗内，再加入切好的香葱花、油、面粉、精盐和适量清水调成面糊；\r\n2.平底锅烧热，加油，舀少许面糊，用小火煎成饼，两面微黄时即可盛出。', '香甜可口，色泽金黄。', '可先把南瓜丝放入开水中焯一下，这样南瓜饼更容易熟。', '食用油50克；精盐1小匙(3克)', '南瓜600克；香葱2棵；面粉适量');
INSERT INTO `recipe` VALUES (6137, 447, '黄金粟米饼', '1.南瓜去皮去籽上蒸茏蒸熟，用刀压烂，沥干水分；\r\n2.粟米粒倒出，加入蒸烂的南瓜、精盐、白糖、干淀粉拌匀，做成小饼；\r\n3 平底锅内放油，摆入粟米饼，用小火煎至两面金黄即可。', '色泽金黄，香甜味美。', '也可用家用榨汁机将南瓜打碎。', '食用油20克；精盐10克；白糖5克', '罐装粟米粒200克；南瓜50克；淀粉适量');
INSERT INTO `recipe` VALUES (6138, 447, '腰果雪梨', '1.雪梨去皮去核、切厚片后立即用清水浸泡，腰果切成粒；\r\n2.雪梨逐片拍上少许干淀粉，将鸡蛋打散，加入干淀粉、精盐、白糖和适量清水调成蛋糊，下入雪梨片拌匀，再在每片雪梨上撒上腰果粒；\r\n3.锅内倒入油，烧热后下入雪梨，炸成金黄色时捞起沥油，摆入盘内即可。', '外酥内润，风味独特。', '为防止雪梨变黑，可在浸泡雪梨的清水中加少许盐。', '食用油500克；(实耗油80克)；精盐1小匙(3克)；白糖3小匙(9克)', '雪梨120克；腰果100克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (6139, 447, '脆炸火龙果', '风味独特，外脆里嫩。', '一定要用大火炸。', '厨房小窍门：', '食用油500克；（实耗50克）；精盐2小匙(6克)', '火龙果1个；面粉50克；淀粉80克；泡打粉3克');
INSERT INTO `recipe` VALUES (6140, 447, '麻仁香蕉', '1.香蕉去皮切片，拍上干淀粉少许；\r\n2.把干淀粉、鸡蛋液、精盐、白糖、水调成糊，然后下入香蕉片拌匀，再逐片粘上白芝麻；\r\n3.锅内倒入油，烧热，放入香蕉，炸至表皮变脆时捞起沥油，摆入盘内即可。', '外酥内滑，香甜可口。', '一定要用大火，以防止香蕉拖糊。', '食用油500克；（实耗80克）；精盐1小匙(3克)；白糖2小匙(6克)', '香蕉120克；白芝麻50克；鸡蛋1个；淀粉适量');
INSERT INTO `recipe` VALUES (6141, 447, '拔丝山药', '1.山药去皮切块，用水泡洗，沥干水分，拍上少许干淀粉；把鸡蛋打入碗内，加入面粉、干淀粉、少许清水、少许油调成糊，把山药一一放入挂糊；\r\n2.锅内倒入油，烧热，放入山药，炸至金黄脆硬时捞起沥油；\r\n3.将锅洗净，加入清水、白糖，用小火炒至两面金黄起小泡时，放入炸好的山药炒匀，盛入抹好油的盘内，配上一碗凉水即可。', '外甜内滑，营养丰富。', '炒糖浆时用小火慢慢炒，才能拔出丝。', '食用油500克；（实耗80克）；白糖5大匙(150克)', '山药200克；鸡蛋1个；面粉10克；淀粉50克');
INSERT INTO `recipe` VALUES (6142, 447, '松仁土豆', '1.土豆去皮切厚片，用清水冲洗干净；\r\n2.鸡蛋打散，加入精盐、味精、白糖、淀粉调成糊，下入土豆片拌匀，再粘上松仁；\r\n3.锅内倒入油，烧热，放入土豆片，炸至香脆时捞起入盘即可。', '色泽美观，香酥可口。', '土豆片挂糊前应先拍上一点干面粉，以防止松仁脱落。', '食用油500克；（实耗油50克）；精盐3小匙(9克)；白糖2小匙(6克)；味精1小匙(3克)', '土豆200克；松仁50克；鸡蛋2个；淀粉适量');
INSERT INTO `recipe` VALUES (6143, 447, '糯米煎红枣', '1.糯米洗净后蒸熟，调入精盐、白糖；把红枣洗净煮透；青豆洗净；\r\n2.平底锅内倒入油，放入糯米饭，用小火煎至表皮香脆；\r\n3.然后把红枣、青豆摆到糯米上，再用小火煎一下，铲起切成小块即可。', '黏香脆甜，口感软糯。', '此菜制成后应速食，以免凉后变硬。', '食用油50克；精盐3小匙(9克)；白糖7小匙(21克)', '糯米100克；红枣50克；青豆20克；淀粉30克');
INSERT INTO `recipe` VALUES (6144, 447, '春卷', '1.五香豆干洗净；卷心菜剥开叶片、洗净；胡萝卜洗净，去皮；均切丝备用；\r\n2.猪肉洗净，切丝，放入碗中，加入酱油、淀粉拌匀并腌制10 分钟；\r\n3.锅中倒入适量油烧热，放入猪肉丝炒熟，盛出；\r\n4.用余油把其余馅料炒熟，再加入猪肉丝及精盐炒匀，最后加入水淀粉勾薄芡即为春卷馅；\r\n5.把春卷皮摊平，分别包入适量的馅卷好。放入热油锅中炸至金黄色，捞出沥油即可。', '外酥里鲜，口感极佳。', '可用蛋清把包好的春卷封好口。', '食用油500克；（实耗50克）；酱油1/2大匙(15克)；精盐2小匙(6克)', '春卷皮12张；五香豆干200克；猪肉150克；卷心菜100克；胡萝卜80克；淀粉适量');
INSERT INTO `recipe` VALUES (6145, 447, '杏仁桂圆炖银耳', '1.甜杏仁放入热水中浸泡，剥去种膜；\r\n2.桂圆肉放入凉开水中略泡；银耳去杂洗净；\r\n3.把水、冰糖同上述各种材料一同放入砂锅内，用大火烧开后改小火，炖至银耳软糯即可。', '甜蜜软烂，清香可口。', '银耳汤应当天炖好当天吃完，因为时间长了容易滋生毒素。', '冰糖100克', '水发银耳200克；甜杏仁25克；桂圆肉25克');
INSERT INTO `recipe` VALUES (6146, 448, '咖喱海鲜饭', '1.把虾仁、鲜贝、胡萝卜、洋葱、辣椒切丁;\r\n2.把上述材料放入沸水中，汆烫后随即捞起沥干备用;\r\n3.烧热油锅，把洋葱丁炒香，再把鸡蛋打散炒熟后盛起;\r\n4.烧热油锅，把白米饭炒至米粒呈松散状时，放入准备好的配料及所有调味料拌炒均匀入味即可。', '鲜香味美，异域风味。', '此饭突出的主要是咖喱味和海鲜味。', '食用油20克；咖喱粉1小匙(3克)；精盐1/4小匙(0.75克)；白糖1/4小匙(0.75克)', '白米饭300克；虾仁50克；鲜贝50克；鸡蛋1个；胡萝卜12克；洋葱12克；红辣椒1个；青辣椒1个');
INSERT INTO `recipe` VALUES (6147, 448, '虾仁豆豉炒饭', '1.虾仁去泥肠、洗净；火腿切丁；鸡蛋打入碗中，搅成蛋液；青、红辣椒和生姜切末；\r\n2.锅内倒入油，烧热，放入鸡蛋液，炒至成形，再下虾仁、豆豉、生姜末、火腿丁，煸炒片刻，加入白米饭、辣椒末、青豆及调料，拌炒均匀即可。', '虾鲜豉香，诱人食欲。', '豆豉本身有咸味，炒饭时注意不要放多了盐。', '食用油20克；豆豉2小匙(6克)；胡椒粉1小匙(3克)；精盐1/4小匙(0.75克)；味精1/4小匙(0.75克)', '白米饭300克；虾仁100克；火腿50克；鸡蛋1个；红辣椒1个；青辣椒1个；青豆少许；生姜少许');
INSERT INTO `recipe` VALUES (6148, 448, '蛋丝虾酱饭', '1.虾仁洗净，挑去泥肠；大蒜、洋葱、红辣椒分别切碎；四季豆切丝备用；\r\n2.烧热油锅，爆香大蒜、洋葱，加入1个鸡蛋打散，用中火炒熟，倒入白米饭，再加入虾酱和精盐拌匀，起锅前撒入自制虾酱粉，盛盘；\r\n3.虾仁放入热油锅中用大火快炒，加入红辣椒和柠檬汁、鱼露炒熟；\r\n4.烧热油锅，将另一个鸡蛋打散，煎成蛋皮，切丝；四季豆入沸水汆烫，捞起沥干水分后，加精盐调味；\r\n5.将蛋皮丝、四季豆放在饭上，最后铺上虾仁即可。', '鲜香可口，异国风味。', '把买来的虾酱放入烤箱，用120℃高温烤至干酥，即为自制虾酱粉。', '食用油20克；虾酱20克；自制虾酱粉1/3大匙(10克)；柠檬汁1/3大匙(10克)；鱼露1/2大匙(15克)；精盐1/4小匙(0.75克)', '白米饭300克；虾仁80克；鸡蛋2个；洋葱10克；红辣椒2个；四季豆20克；大蒜3瓣');
INSERT INTO `recipe` VALUES (6149, 448, '金黄蛋炒饭', '1.鸡蛋打散，滤去蛋清，把蛋黄打散成蛋液；香葱洗净切花；\r\n2.将白米饭放在大碗中，加入蛋液一起搅拌至米粒皆呈金黄色止；\r\n3.烧热油锅，将搅拌好的饭炒至米粒呈松散状时，加入精盐调味，撒上葱花即可。', '色泽金黄，香软可口。', '把锅烧热后再倒入油，以免炒饭时粘锅。', '食用油20克；精盐1/4大匙(7.5克)', '白米饭300克；鸡蛋2个；香葱1棵');
INSERT INTO `recipe` VALUES (6150, 448, '肉松小米饭', '1.锅内放油烧热，加入米饭翻炒，再加入鸡肉松、精盐、高汤、味精炒匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '风味独特，松香可口。', '也可以用鱼肉松或牛肉松。', '食用油20克；高汤2小匙(6克)；精盐1小匙(3克)；味精1/4小匙(0.75克)', '白米饭300克；鸡肉松1袋');
INSERT INTO `recipe` VALUES (6151, 448, '椒香牛柳炒饭', '1.将牛柳与酱油、淀粉搅拌均匀腌入味后，在锅中放入少许油烧热，放入牛柳略微拌炒，过油即可盛起备用;青、红辣椒洗净切丝;香葱洗净切花;\r\n2.鸡蛋打散成蛋液备用;锅内倒入少许油烧热，将蛋液下锅轻轻拌炒成蛋花;\r\n3.把白米饭倒入与蛋花一起炒匀，再加入牛柳及青椒丝、红椒丝、香葱花及其余调味料继续拌炒均匀即可。', '鲜嫩可口，香味诱人。', '如用蒜茸辣酱佐饭，味道更佳。', '食用油20克；酱油1小匙(3克)；黑胡椒粉适量；白糖1/4小匙(0.75克)；味精1/4小匙(0.75克)', '白米饭300克；牛柳100克；鸡蛋1个；红辣椒1个；青辣椒1个；香葱1棵；淀粉适量');
INSERT INTO `recipe` VALUES (6152, 448, '素炒饭', '1.香菇、草菇、胡萝卜、芦笋洗净切丁，放入沸水汆烫后，捞起沥干；\r\n2.烧热油锅，先将白米饭下锅翻炒至米粒呈松散状，再将汆好的材料及精盐、白糖下锅拌炒均匀即可。', '清淡爽口，软香诱人。', '汆烫好的材料要沥干水分后再炒，否则会影响米饭的口感。', '食用油20克；精盐1小匙(3克)；白糖1/4小匙(0.75克)', '白米饭300克；香菇50克；草菇50克；胡萝卜25克；芦笋25克');
INSERT INTO `recipe` VALUES (6153, 448, '雪笋饭', '1.干笋泡软后切丝；虾仁洗净，挑去泥肠；雪里蕻洗净，切碎备用；鲩鱼肉切片；香葱洗净切花，生姜切末；\r\n2.烧热油锅，放入虾仁、鲩鱼片一起用大火炒熟盛起备用；\r\n3.烧热油锅，爆香香葱花、生姜末，再放入雪里蕻、笋丝以大火炒香，加入其余所有调味料后转小火，煮至即将收汁时，将虾仁、鲩鱼片、白米饭下锅拌炒均匀即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '风味独特，清淡鲜美。', '拌炒时动作要轻，以免把鲩鱼片炒碎。', '食用油20克；香油适量；高汤2大匙(60克)；白胡椒粉1大匙(30克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '白米饭300克；虾仁10克；鲩鱼肉40克；雪里蕻50克；干笋20克；香葱1棵；生姜少许');
INSERT INTO `recipe` VALUES (6154, 448, '土豆香煎饭', '1.土豆去皮切丝；卷心菜洗净切丝；香葱洗净切花；\r\n2.先把土豆丝与白米饭拌匀，再拌入卷心菜丝和香葱花，以精盐、胡椒粉调味后，搓圆再压扁成厚圆片；\r\n2.锅中放入橄榄油加热，以大火将压好的米饭圆饼煎至凝固再翻面，煎至两面呈金黄色；\r\n3.四季豆切段，用沸水烫熟，与精盐、辣椒粉、橄榄油拌匀，再与菜饭煎饼一起盛盘即可。', '创新独特，菜饭同食。', '先把土豆丝放入沸水中焯水后再烧制，可去除生土豆味。', '橄榄油40克；胡椒粉2小匙(6克)；辣椒粉2小匙(6克)；精盐3小匙(9克)', '白米饭80克；土豆60克；卷心菜40克；四季豆100克；香葱2棵');
INSERT INTO `recipe` VALUES (6155, 448, '爽口鲜果饭', '1.将大米和黑米洗净浸泡，哈密瓜去皮切丁，猕猴桃、苹果去皮均切丁，腰果炸熟；\r\n2.将泡好的米放入焖罐中，焖25～30分钟至熟。将水果丁、柠檬汁和米饭一起拌匀即可。', '颜色鲜艳，香甜可口。', '稍微放一点柠檬汁即可，因为柠檬酸容易刺激肠胃。', '柠檬汁适量', '大米250克；黑米50克；哈密瓜1个；猕猴桃2个；腰果50克；苹果1个');
INSERT INTO `recipe` VALUES (6156, 448, '杂粮炒饭', '1.胡萝卜、芦笋切丁；玉米笋切块；虾仁、鲜贝、花枝切丁，再以沸水汆烫后捞起沥干；把鸡蛋煎成蛋皮，切丝；海苔切丝；\r\n2.用冷水将杂粮米浸泡约1小时，将浸泡的水倒掉，重注入适量水量，放入蒸锅中蒸约50 分钟；\r\n3.烧热油锅，将虾仁丁、花枝丁、鲜贝丁下锅以大火快炒，再加入少许精盐调味即可起锅备用；\r\n4.烧热油锅，将杂粮米饭下锅炒至米粒呈松散状时加入胡萝卜丁、芦笋丁、玉米笋、味精拌匀即可装盘，再放上炒好的海鲜与蛋皮丝、海苔丝即可。', '鲜香可口，营养健胃。', '摊蛋皮时注意厚薄要均匀。', '食用油20克；精盐1/3小匙(1克)；味精1/4小匙(0.75克)', '杂粮米800克；虾仁50克；鲜贝50克；花枝50克；胡萝卜12克；芦笋12克；玉米笋12克；鸡蛋1个；海苔适量');
INSERT INTO `recipe` VALUES (6157, 448, '生菜鱼球粥', '1.大米洗净，用少量油拌匀，放入粥锅，加水开火煮，转中火煮约30 分钟；\r\n2.生姜洗净切丝；陈皮、荸荠与海米洗净切末；\r\n3.鲮鱼绞碎，加入陈皮、荸荠与海米，并放入精盐、白糖、胡椒粉、水淀粉拌匀，摔打成有弹性的鱼球；生菜切丝，洗净后沥干；\r\n4.用汤匙将鱼球拨入快煮好的粥锅内煮熟，加入生菜丝，拌匀即可关火；\r\n5.最后撒上生姜丝和精盐、胡椒粉调味即可盛出食用。', '味道鲜美，营养丰富。', '鱼球最好用沸水汆烫后再放入粥锅，以去除鱼腥味。', '食用油5克；胡椒粉适量；精盐1/4小匙(0.75克)；白糖1/4小匙(0.75克)', '大米150克；鲮鱼肉300克；荸荠10克；海米10克；生菜100克；生姜少许；陈皮1小片；淀粉适量');
INSERT INTO `recipe` VALUES (6158, 448, '皮蛋鱼片粥', '1.大米洗净，用少量油拌匀；把1个皮蛋剥皮搓碎，加少许油与米粒拌匀，另一个皮蛋剥皮后切小块；生姜洗净切丝；香菜洗净切段；\r\n2.鲩鱼去骨后切薄片，加入生姜丝与精盐、白糖、胡椒粉拌匀；\r\n3.将米放入粥锅，加水煮开，再转中火煮约30 分钟；\r\n4.待粥熬好后放入鲩鱼片、皮蛋块略煮；\r\n5.最后加入精盐、胡椒粉调味，撒上生姜丝与香菜即可。', '风味独特，咸鲜味美。', '鲩鱼肉煮制的时间不要过长，以防将鱼片煮碎。', '食用油5克；胡椒粉适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '大米150克；鲩鱼肉300克；皮蛋2个；生姜少许；香菜1棵');
INSERT INTO `recipe` VALUES (6159, 448, '鲜香鱼头粥', '1.大米洗净后用少量油拌匀;生姜洗净切丝；香葱洗净切成葱花;\r\n2.卷心菜洗净后切丝，鲢鱼头洗净后切小块，加生姜丝和精盐、白糖、胡椒粉拌匀;\r\n3.将米放入粥锅，加水煮开，再转中火煮约30 分钟；\r\n4.将鲢鱼头、卷心菜丝加入熬好的粥内煮熟;\r\n5.最后加入精盐、胡椒粉调味，撒上生姜丝与香葱花即可食用。', '味道鲜美，营养丰富。', '先把鲢鱼头放入沸水中汆烫一下，去净杂质后再与米粥同煮。', '食用油5克；胡椒粉适量；精盐1小匙(3克)；白糖1/2小匙(1.5克)', '大米150克；鲢鱼头1/2个(约600克)；卷心菜20克；香葱1棵；生姜少许');
INSERT INTO `recipe` VALUES (6160, 448, '鲜贝粥', '1.大米洗净后用少量油拌匀;生姜洗净切丝，香葱洗净切成葱花;\r\n2.鲜贝洗净，加入生姜丝、精盐和胡椒粉略腌;\r\n3.将米放入粥锅，加水煮开，转中火煮约30 分钟;\r\n4.将鲜贝加入粥内煮熟;\r\n5.加精盐与胡椒粉调味，撒上香葱花与生姜丝即可盛出食用。', '味道鲜美，营养丰富。', '市场上袋装冷冻鲜贝较多，最好能用新鲜的活贝。', '食用油5克；精盐1小匙(3克)；胡椒粉少许', '大米150克；鲜贝300克；香葱1棵；生姜少许');
INSERT INTO `recipe` VALUES (6161, 448, '蛋花牛肉粥', '1.大米洗净加少许油拌匀；牛肉洗净，切片，再与少许精盐、胡椒粉、淀粉拌匀，腌10 分钟；鸡蛋打散成蛋液备用；\r\n2.将米放入粥锅，加水煮开，放入牛肉片，煮开后再煮2 分钟，最后将蛋液淋在粥上，顺同一个方向搅开即可。', '咸鲜味美，清爽可口。', '为防止产生蛋腥味，一定要在米粥烧开时淋入蛋液。', '食用油5克；胡椒粉少许；精盐1小匙(3克)；味精1/2小匙(1.5克)', '大米150克；牛肉225克；鸡蛋2个；淀粉适量');
INSERT INTO `recipe` VALUES (6162, 448, '苦瓜羊腩粥', '1.羊腩洗净切块，放沸水中氽烫，除去血污；苦瓜洗净、去瓤、切片，焯水后捞出；\r\n2.大米洗净，用少量油拌匀；燕麦洗净浸泡8 小时；生姜洗净切片；\r\n3.锅中加入清水、大米、燕麦，大火烧沸，下入羊腩、姜片及其余所有调料，搅拌均匀，转小火煮1小时，再下入苦瓜片，煮10 分钟即可。', '荤素搭配，清香可口。', '如果稍微放一点绿豆，味道会更好。', '食用油5克；料酒1小匙(3克)；胡椒粉适量；精盐1小匙(3克)；味精1小匙(3克)', '大米150克；羊腩150克；苦瓜100克；燕麦60克；生姜少许');
INSERT INTO `recipe` VALUES (6163, 448, '芥菜咸蛋鸭肉粥', '1.大米洗净，用少量油拌匀；生姜洗净切丝；\r\n2.芥菜洗净后切段，鸭脯肉洗净切片；\r\n3.将咸鸭蛋内的蛋黄取出，切小块；\r\n4.将米放入粥锅，加入水、芥菜段与鸭肉片煮开，转中火煮约30 分钟，最后放咸蛋黄块再煮片刻；\r\n5.加精盐与胡椒粉调味，撒上生姜丝即可盛出食用。', '清香诱人，咸鲜可口。', '可放入少量绿豆同煮，味道很不错。', '食用油5克；精盐1小匙(3克)；胡椒粉少许', '大米150克；鸭脯肉300克；咸鸭蛋1个；芥菜150克；生姜少许');
INSERT INTO `recipe` VALUES (6164, 448, '香芋排骨粥', '1.排骨洗净，切块，用沸水汆烫一下即捞起备用;大米洗净后拌少许油;\r\n2.芋头削去外皮，切块后洗净，并放入热油锅中以小火炸至外皮呈黄色时，捞起沥油;\r\n3.将米放入粥锅，加水煮开，转小火一边搅一边煮约20 分钟，再放入排骨继续煮30 分钟;\r\n4.最后放入芋头一起煮10分钟，加入其余所有调味料拌匀即可。', '香鲜味美，风味独特。', '如不爱吃油炸的芋头，也可以用蒸熟的芋头。', '食用油500克；（实耗40克）；精盐1小匙(3克)；味精1小匙(3克)', '大米150克；排骨300克；芋头200克');
INSERT INTO `recipe` VALUES (6165, 448, '皮蛋瘦肉粥', '1.皮蛋剥壳，每个切成等量的8 瓣备用;大米洗净拌入少量油;生姜洗净切丝，香葱洗净切成葱花，香菜切末;\r\n2.猪瘦肉洗净沥干水，用3小匙精盐腌3小时至入味，再放入蒸锅蒸20 分钟取出切片;\r\n3.将油条切小段，放入热油锅中，以小火炸约30秒至酥脆后，捞起沥油;\r\n4.将米放入粥锅，加水煮开，转中火煮约30分钟；放入皮蛋和瘦肉片、生姜丝及其余调味料一起煮开后，再继续煮几分钟即熄火，食用前加入油条及香菜、葱花即可。', '肉质软烂，鲜香味美。', '一定要先用盐把瘦肉腌制一会。', '食用油200克；（实耗10克）；香油少许；胡椒粉少许；精盐4小匙(12克)；味精1小匙(3克)', '大米150克；皮蛋2个；猪瘦肉225克；油条1根；香葱1棵；生姜少许；香菜1棵');
INSERT INTO `recipe` VALUES (6166, 448, '状元及第粥', '1.大米洗净，用少量油拌匀；生姜洗净切丝，香菜洗净切末;\r\n2.猪粉肠、猪肚用开水汆烫后放入锅内煮1小时后切开，备用;\r\n3.猪腰去除臊筋，用开水汆烫后切小块;猪肝、猪心与猪瘦肉切小块，洗净，加入生姜丝和精盐、白糖、料酒略腌;\r\n4.将米放入粥锅，加水煮开，转中火煮约30 分钟;\r\n5.将所有材料放入熬好的白粥内，煮熟后用精盐、胡椒粉调味，并撒上生姜丝与香菜末即可。', '香浓可口，味道醇厚。', '猪肚的处理方法可参照本书上卷。\r\n注：相传清朝年间，有位新科状元返乡途中经过广州，在一粥店点了此粥，这时一位御史恰巧经过，状元便招呼他也来一碗粥。御史好奇地问是什么粥，新科状元为炫耀自己的身份，便说是“状元及第粥”，更巧的是御史的儿子正准备要参加科举，刚好得了个好兆头，“状元及第粥”因此而声名大噪。', '食用油5克；料酒1/2大匙(15克)；胡椒粉少许；精盐3小匙(9克)；白糖1/2小匙(1.5克)', '大米150克；猪腰75克；猪肝75克；猪粉肠75克；猪瘦肉75克；猪心60克；猪肚75克；生姜少许；香菜1棵');
INSERT INTO `recipe` VALUES (6167, 448, '白果肚片粥', '1.大米洗净，用少量油拌匀;生姜洗净切丝，香菜洗净切段;\r\n2.猪肚用盐水反复搓洗，再用开水氽烫5 分钟，放入锅内煮烂，取出切片;豆腐皮切丝;\r\n3.将米放入粥锅，加入所有材料煮开，转中火煮约30分钟;\r\n4.最后加精盐与胡椒粉调味，撒上生姜丝与香菜即可盛出食用。', '软烂鲜香，爽稠可口。', '一定要洗掉猪肚的腥臊味。', '食用油5克；精盐2大匙(60克)；胡椒粉少许', '大米150克；白果120克；猪肚200克；豆腐皮80克；荸荠20克；生姜少许；香菜1棵');
INSERT INTO `recipe` VALUES (6168, 448, '川贝雪梨粥', '1.川贝用冷水浸泡1 小时后取出；圆糯米用冷水浸泡1 小时后沥干水备用；\r\n2.雪梨洗净，削去外皮剖开去心，切片备用；\r\n3.粥锅内加清水，用大火煮开，加入川贝及圆糯米转小火煮开后继续煮40 分钟，再加入雪梨片煮20 分钟，最后用冰糖调味即可。', '甜爽可口，清凉润肺。', '煲制此粥时可用不锈钢锅，因为此粥易粘砂锅、铁锅。', '冰糖75克', '圆糯米100克；川贝40克；雪梨3个(约500克)');
INSERT INTO `recipe` VALUES (6169, 448, '山药蛋黄粥', '1.山药刮皮洗净切片；香葱洗净切花；大米洗净加少量油拌匀；\r\n2.将大米与山药片一同下锅，先用大火烧开，再改用小火熬煮至熟；\r\n3.鸡蛋滤去蛋清，留蛋黄，倒入粥中搅拌均匀，加入其余调味料煮开，撒上葱花即可。', '滑爽味美，营养丰富。', '要将山药的黏液洗净后再煲粥。', '食用油5克；精盐1小匙(3克)；味精1小匙(3克)', '大米150克；山药50克；鸡蛋2个；香葱1棵');
INSERT INTO `recipe` VALUES (6170, 448, '营养八宝粥', '1.将山楂糕、冬瓜条分别切丁；将糯米淘洗干净；\r\n2.将糯米及其他各料放入锅中，加水烧沸后改用小火熬成稠粥，加入桂花酱和白糖拌匀即可。', '香甜可口，营养丰富。', '蜜饯本身有甜味，所以加糖前应先尝一下米粥。', '桂花酱适量；白糖适量', '糯米150克；桃脯15克；蜜枣15克；糖莲子25克；杏脯25克；冬瓜条25克；核桃仁25克；山楂糕25克');
INSERT INTO `recipe` VALUES (6171, 448, '碧荷莲子粥', '1.大米冼净，用少量油拌匀；其他材料洗净；\r\n2.芡实洗净后先放水中浸泡半小时，然后放入蒸锅内蒸熟取出；\r\n3 .将米放入粥锅，加水煮开后放入所有的材料再煮开，续煮约30 分钟；\r\n4.将粥盛入放有荷叶的碗中，加精盐与胡椒粉调味即可。', '荷香四溢，口味清淡。', '此粥有清热解暑的功效，夏季可经常食用。', '精盐1小匙(3克)；胡椒粉少许', '大米150克；莲子40克；芡实20克；薏仁20克；鲜荷叶1小张');
INSERT INTO `recipe` VALUES (6172, 448, '薏仁美颜粥', '1.薏仁米洗净放冷水中浸泡2小时后沥干；山药洗净，削去外皮，切成丁状；猪瘦肉洗净切丁；\r\n2.粥锅内注水，用大火煮开后转小火，加入大米和薏仁米煮50 分钟，再加入山药丁、瘦肉丁续煮10 分钟，最后加入精盐、味精拌匀即可。', '美容养颜，软烂鲜香。', '往米粥里加入瘦肉后，要将浮沫撇净。', '精盐1小匙(3克)；味精1小匙(3克)', '大米75克；薏仁米115克；山药225克；猪瘦肉75克');
INSERT INTO `recipe` VALUES (6173, 448, '山楂消食粥', '1.圆糯米洗净放冷水中浸泡2 小时后沥干水分；山楂洗净备用；\r\n2.粥锅内注水，用大火煮开后转小火，放入山楂边搅边煮，15 分钟后捞起山楂，再加入圆糯米继续煮1小时，最后加入冰糖调味即可。', '甜香微酸，开胃消食。', '山楂有助于消食，常食油腻者可经常吃此粥。', '冰糖75克', '圆糯米150克；山楂150克');
INSERT INTO `recipe` VALUES (6174, 448, '香菇玉米粥', '1.猪瘦肉洗净切粒，拌入少许淀粉；胡萝卜洗净切粒，玉米粒洗净；大米洗净拌入少许油；\r\n2.香菇用冷水泡软，去蒂，用淀粉抓洗后，洗净，挤干水切片，再拌入少许油备用；\r\n3.粥锅内注水，用大火煮开后转小火，将所有材料放入一起煮20 分钟，最后加入调味料拌匀即可。', '软烂鲜香，营养丰富。', '香菇用高汤煨入味后再煲，米粥的味道会更好。', '食用油8克；精盐1/2小匙(1.5克)；味精1小匙(3克)', '大米100克；猪瘦肉75克；胡萝卜75克；玉米粒75克；香菇25克；淀粉适量');
INSERT INTO `recipe` VALUES (6175, 448, '菊香清凉粥', '1.粥锅内注水，用大火煮开后转小火，放入黄菊花煮15 分钟后，捞掉黄菊花；\r\n2.将冼净的圆糯米倒入煮黄菊花的水中，继续煮50分钟后，加冰糖调味即可。', '清香四溢，甜爽可口。', '煮黄菊花时最好盖上锅盖，以防菊花的香味散发掉。', '冰糖75克', '圆糯米150克；干燥黄菊花40克');
INSERT INTO `recipe` VALUES (6176, 448, '腊八粥', '1.先将所有材料用水泡软，洗净；\r\n2.粥锅内注水，加入所有材料煮开，转中火煮约30分钟；\r\n3.放入冰糖调味即可食用。', '甜爽可口，营养丰富。', '此粥凉食、热食均可。', '冰糖75克', '圆糯米150克；绿豆25克；红豆25克；腰果25克；花生25克；桂圆25克；红枣25克；陈皮1小片');
INSERT INTO `recipe` VALUES (6177, 448, '香辣牛肉面', '1.牛腩洗净切块，放入沸水中氽烫约10 分钟，捞出洗净；酸菜洗净切丝放入油锅中炒，加入少许白糖;生姜洗净切丝；大蒜洗净切末；红辣椒、洋葱洗净切丝;\r\n2.烧热油锅，爆香大蒜、生姜以及红辣椒后，加入辣豆瓣酱、酱油略炒，放入牛腩炒至豆瓣酱有香味溢出，加一大碗清水，放入卤包及其他调味料，烧开后改小火煮1 小时;\r\n3.把面条煮熟，油菜洗净放沸水中烫熟，一起放入碗中，加入牛腩肉汤，放入酸菜丝即可。', '香辣可口，爽滑鲜嫩。', '煮牛腩时为了让牛腩烂得更快，可放一个山楂进去同煮。', '卤包1个(大料、桂皮)；酱油1大匙(30克)；辣豆瓣酱2大匙(60克)；白糖2小匙(6克)；味精1小匙(3克)', '面条200克；牛腩200克；油菜50克；酸菜10克；红辣椒1个；洋葱35克；生姜1小块；大蒜6瓣');
INSERT INTO `recipe` VALUES (6178, 448, '文蛤海鲜面', '1.草虾去除泥肠，洗净；文蛤放入清水中吐净泥沙；芦笋切段，其他材料洗净备用；\r\n2.把面条煮熟，捞出装碗；香葱、生姜洗净切末；\r\n3.锅内倒入油，放入香葱、生姜爆香，烹入料酒，加入高汤，烧沸后放入文蛤煮至微开口，加入草虾、夏威夷贝、带子、海带、芦笋及调料；\r\n4.再煮3 分钟后关火，将所有材料倒入面碗中即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤鲜味醇，清淡可口。', '为突出海鲜的鲜味，最好不要加酱油。', '食用油20克；高汤15大匙(450克)；料酒3小匙(9克)；胡椒粉1小匙(3克)；精盐1小匙(3克)；味精1小匙(3克)', '面条200克；草虾30克；文蛤100克；夏威夷贝50克；带子50克；海带20克；芦笋适量；香葱2棵；生姜少许');
INSERT INTO `recipe` VALUES (6179, 448, '雪菜肉丝面', '1.猪瘦肉洗净切成丝，雪里蕻洗净切成长段；红辣椒洗净切丝；\r\n2.锅内倒入油，烧热，下猪肉丝翻炒，把肉丝炒至变色时，放入雪里蕻、红辣椒、酱油、白糖、味精、香油炒匀盛出，即为面料；\r\n3.把面条煮熟，捞入碗内，将炒好的面料倒在上面，加上清汤即可。', '清淡爽口，味道鲜美。', '把雪里蕻煮烂后再食用，味道会更好。', '食用油20克；香油1小匙(3克)；酱油1小匙(3克)；白糖1小匙(3克)；味精1小匙(3克)', '面条200克；猪瘦肉100克；雪里蕻50克；红辣椒1个');
INSERT INTO `recipe` VALUES (6180, 448, '翡翠面片汤', '1.面粉放盆内，另将菠菜洗净，切碎，加适量水后打成泥，再将菜汁沥出，拌入面粉内，并将鸡蛋打散加入，加精盐调味后和成面团，然后盖上湿布醒10分钟；香葱洗净切花；\r\n2.锅内倒入半锅水，烧开，将揉好的面团用手撕成小片，放入开水中煮熟；\r\n3.汤碗内盛上烧开了的高汤，并加精盐调味后，把煮好的面片放进去，撒上香葱花即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '色泽淡雅，菜香浓郁。', '为使面片吃起来更有弹性，可以多揉一会儿面团，使各种调味品分布得更均匀。', '高汤15大匙(450克)；精盐2小匙(6克)', '面粉300克；菠菜150克；鸡蛋1个；香葱1棵');
INSERT INTO `recipe` VALUES (6181, 448, '海鲜酸汤揪片', '1.把面粉、胡萝卜汁、精盐混合均匀，揉成光滑的面团，醒30 分钟后擀成薄面皮，先切成宽条，再切成小片，倒入开水中煮熟，捞出；\r\n2.芹菜洗净切斜片，虾仁洗净，蟹腿肉洗净，墨斗鱼洗净切片；西红柿洗净切块；\r\n3.洋葱洗净切块，放热油锅中爆香，再放入西红柿块，把汤色炒红后加入高汤煮开；\r\n4.加入芹菜片、海鲜及煮熟的面片，把海鲜煮熟后放入精盐即可。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '汤味鲜美，面片爽口。', '出锅后即食，因为面片在汤汁中泡久了会失去韧性。', '食用油20克；高汤15大匙(450克)；精盐1/2小匙(1.5克)', '面粉300克；胡萝卜汁150克；洋葱20克；芹菜10克；虾仁20克；新鲜墨斗鱼20克；蟹腿肉20克；西红柿250克');
INSERT INTO `recipe` VALUES (6182, 448, '碧丝拌面', '1.豆角择洗干净，切成丝，香葱、生姜洗净切丝；猪瘦肉洗净切丝；\r\n2.锅内倒入油，烧热后放入肉丝炒至变色，再加香葱丝、生姜丝、酱油、豆角丝煸炒，稍加一点水，放味精、精盐炒匀盛出；\r\n3.把面条煮熟，捞出，用凉开水冲凉，沥干后装入碗内，放上炒熟的凉拌料拌匀即可。', '色泽清新，味道鲜美。', '一定要将豆角炒熟，去除掉生豆角内的毒素。', '食用油20克；酱油3小匙(9克)；精盐1小匙(3克)；味精1小匙(3克)', '面条200克；猪瘦肉200克；豆角100克；香葱1棵；生姜1小块');
INSERT INTO `recipe` VALUES (6183, 448, '麻酱凉拌面', '1.黄瓜洗净，胡萝卜去皮洗净，均切丝，放入沸水中烫熟；\r\n2.芝麻酱放入碗中，加精盐、香油、凉开水拌成酱汁；\r\n3.把面条煮熟，捞出用凉开水冲凉，沥干；\r\n4.把面条装入装有酱料的碗中，加胡萝卜丝、黄瓜丝拌匀即成。', '香气扑鼻，清凉爽口。', '食用凉面时，加入1 小匙醋，面条会更加爽口。', '香油1小匙(3克)；芝麻酱2大匙(60克)；精盐1小匙(3克)', '面条200克；黄瓜30克；胡萝卜30克');
INSERT INTO `recipe` VALUES (6184, 448, '泡菜凉拌面', '1.把面条煮熟，捞起用凉开水冲凉，沥干，装入碗中，上面撒上泡菜；\r\n2.把香醋、精盐、味精、香油放入小碗内调匀，倒入面碗中拌匀即可。', '爽口清淡，风味独特。', '做泡菜凉拌面可以用泡椒，也可以用其他种类的泡菜，比如花菜、卷心菜、胡萝卜等，如果把多种不同的泡菜放在一起，味道更好。', '香油2小匙(6克)；香醋适量；精盐1小匙(3克)；味精1小匙(3克)', '宽面条200克；泡菜100克');
INSERT INTO `recipe` VALUES (6185, 448, '牛肉通心粉', '1.牛肉洗净，切块，洋葱洗净切片；大蒜拍碎；胡萝卜洗净，切片；芹菜洗净，切末；\r\n2.锅内倒入油，烧热，下牛肉块、洋葱片、大蒜翻炒，牛肉炒熟后盛出；\r\n3.锅内放入一半高汤，将月桂叶、百里香扎成一束放入锅内，盖上盖炖30 分钟；\r\n4.把余下的高汤和番茄酱、胡萝卜片一起放入锅里，加黑胡椒拌匀；\r\n5.盖上盖，继续炖30 分钟，煮开后加入通心粉，盖上盖煮10 分钟；\r\n6.通心粉煮软后，取出香草束，在牛肉和通心粉上撒上芹菜末即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '鲜香诱人，粉滑爽口。', '通心粉煮制的时间不要过长，否则太软了口感不好。', '食用油20克；高汤20大匙(600克)；番茄酱1大匙(30克)；黑胡椒1小匙(3克)', '通心粉125克；牛肉150克；洋葱50克；胡萝卜75克；芹菜40克；大蒜1瓣；月桂叶1片；百里香1枝');
INSERT INTO `recipe` VALUES (6186, 448, '重庆酸辣粉', '1.红薯粉洗净，用清水泡发；香菜洗净切段；香葱洗净切花；榨菜切末；\r\n2.锅内倒入油，烧热，爆香花椒、干辣椒、花椒粉、辣椒粉、白芝麻，加入高汤烧沸，盛入碗内；\r\n3.锅中烧开半锅水，下红薯粉烫热，放入汤碗中；\r\n4.加味精、香醋、精盐、五香花生米、榨菜、香油，撒上香菜段、葱花即成。\r\n(高汤做法：瘦猪肉300克、排骨300克、火腿150克、虾米40克、姜1大片、大料1粒、陈皮1片、清水适量。用小火熬煮成1大碗汤汁，用纱布滤去渣滓即可。)', '润滑爽口，酸辣开胃。', '可根据自己的口味调制麻辣味。', '食用油30克；香油1小匙(3克)；高汤18大匙(540克)；香醋1小匙(3克)；花椒粉1/3小匙(1克)；辣椒粉1/2小匙(1.5克)；精盐1/2小匙(1.5克)；味精1/2小匙(1.5克)', '红薯粉100克；五香花生米30克；涪陵榨菜20克；白芝麻10克；香葱1棵；香菜2棵；花椒适量；干辣椒2个');
INSERT INTO `recipe` VALUES (6195, 441, '香橙烧猪肉条', '1.超市卖的新鲜精廋猪肉一块，切成小指粗的长条。 \r\n\r\n2.把猪扒放些许盐、味精、豆粉腌好，约10分钟。 \r\n\r\n3.把橙汁、柠檬榨汁，糖、盐、味精调成糖醋汁。 \r\n\r\n4.再把猪肉条放在热油中炸至七、八成熟，取出，沥干。 \r\n\r\n5.锅热放油，再加蒜茸，爆香后再倒入糖醋汁、猪肉条。收汁取出即可！\r\n', '香甜可口，色泽亮丽', NULL, '盐、味精、豆粉、橙汁、香醋、花生油', '鲜橙两个，蒜茸随意，柠檬两个，猪排');
